INSERT INTO [b13846] VALUES (335,'(ر) البكري [.. - ١٦٩ هـ .. - ٧٥٦ م]

<span data-type="title" id=toc-474>الربيع بن أنس البكري البصري ثم الخراساني:</span>
محدث، مفسر، من أهل البصرة، هرب منها إلى مرو خوفا من الحجاج بن يوسف. روى عن أنس بن مالك والحسن البصري وغيرهما. قال ابن سعد: "مات في خلافة أبي جعفر المنصور". ذكر الذهبي أنه توفي سنة ١٣٩ هـ أو سنة ١٤٠ هـ. له "تفسير القرآن" قال صاحب تاريخ التراث العريى: "يرجع أكثر هذا التفسير إلى أبي العالية (المتوفي سنة ٩٠ هـ) وقد استخدمه الطبري بالرواية الآتية: "حدثت عن عمار بن الحسن، قال: حدثنا عبد الله بن أبي جعفر عن أبيه الربيع بن أنس". ويبدو أن أجزاء كثيرة من هذا التفسير قد دخلت في تفسير الطبري، وذلك بواسطة النقول في التفاسير','1','189',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (336,'الأخرى. وأما الثعلبي في كتابه "الكشف والبيان" فيأخذ منه على أنه: تفسير أبي العالية والربيع" (١).

القطان [٢٨٨ - ٣٣٤ هـ / ٩٠١ - ٩٤٥ م]

<span data-type="title" id=toc-475>ربيع بن سليمان بن عطا الله القطان، أبو سليمان:</span>
محدث، عالم بالقرآن قراءة وتفسيرا ومعنى، من فقهاء المالكية، من أهل إفريقية، حج، وسمع بمصر وبمكة. قال المالكي: "كان يؤلف الخطب والرسائل ويقول الشعر، وكان لسان إفريقية في وقته في الزهد والرقائق، له
_________
(١) التهذيب لابن حجر ٣: ٢٣٨ وتاريخ التراث العربي ١: ١٩٣ وتقريب التهذيب ١: ٢٤٣ والجرح والتعديل ١/ ٢: ٤٥٤ والطبقات الكبرى ٧: ٣٨٠ والمعارف ٢٣٦ وطبقات المفسرين ١: ١٧٢ ترجمة رفيع بن مهران.','1','189',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (337,'بجامع القيروان حلقة ... ". قتل شهيدا في وادي المالح، في حصار المهدية (١).

البرسي [.. - حيا ٣١٨ هـ / .. - حيا ١٤١٠ م]

<span data-type="title" id=toc-476>رجب بن حمد بن رجب البرسي، الحلي:</span>
محدث، مفسر، شاعر، من غلاة فقهاء الشيعة الإمامية. نسبته إلى قرية "برس" بين الحلة والكوفة. من كتبه "تفسير سورة الإخلاص" (٢).

البغدادي [٤٠٠ - ٤٨٨ هـ / ١٠٠٩ - ١٠٩٥ م]

<span data-type="title" id=toc-477>رزق الله بن عبد الوهاب بن عبد العزيز التميمي البغدادي، أبو محمد:</span>
شيخ أهل العراق في زمانه، من كبار الحنابلة. قال الذهبي: "كان إماما مقرئا فقيها محدثا، واعظا أصوليا مفسرا لغويا، فرضيا كبير الشأن وافر الحرمة". وقال ابن ناصر: كان مكرما وذا قدر رفيع عند الخلفاء منذ زمن القادر ومن بعده من الخلفاء إلى خلافة المستظهر" (٣).
_________
(١) طبقات المفسرين ١: ١٧٠ وترتيب المدارك ٣: ٣٢٣ وطبقات علماء افريقية ١٧٩.
(٢) أعيان الشيعة ٣١: ١٩٣ وروضات الجنات ٢٨٤ وهدية العارفين ١: ٣٦٥ وشعراء الحلة ٢: ٣٨٦ وإيضاح المكنون ١: ٣٠٥.
(٣) طبقات المفسرين ١: ١٧١ وطبقات القراء للذهبي ١: ٣٥٦ والعبر ٣: ٣٢٠ والشذرات ٣: ٣٨٤ والمنتظم ٩: ٨٨ وتذكرة الحفاظ ١٢٠٨ والذيل =','1','190',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (338,'القنوجي [.. - ١١٧٨ هـ / .. - ١٧٦٤ م]

<span data-type="title" id=toc-478>رستم علي بن علي أصغر القنوجي:</span>
مفسر، من فقهاء الحنفية، من أهل الهند. له "الجامع الصغير" في تفسير القرآن الكريم (١).

الهمذاني [.. - ١٢٤٧ هـ / .. - ١٨٣١ م]

<span data-type="title" id=toc-479>رضا بن محمد أمين الهمذاني:</span>
مفسر، متكلم، من فقهاء الشيعة الإمامية. له كتب منها " الدر النظيم في تفسير القرآن العظيم" (٢).

الشيرازي [.. - ١١١٢ هـ / .. - ١٧٠٠ م]

<span data-type="title" id=toc-480>رضي الدين بن محمد الحسينى الشيرازي:</span>
محدث، مفسر، من فقهاء الشيعة الإمامية، أقام بأصفهان وحدث ودرس بها. له "تفسير القرآن" كبير (٣).
_________
= على طبقات الحنابلة ١: ٧٧ والمنهج الأحمد ٢: ١٦٤ وهدية العارفين ١: ٣٦٧.
(١) هدية العارفين ١: ٣٦٧ وإيضاح المكنون ١: ٣٥٤ ومعجم المؤلفين ٤: ١٥٦،
(٢) أعيان الشيعة ٣٢: ٤٦ وإيضاح المكنون ١: ٤٥٤ ومعجم المؤلفين ٤: ١٦٣.
(٣) أعيان الشيعة ٣٢: ١٠٩ ومعجم المؤلفين ٤: ١٦٧.','1','190',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (339,'الرياحي [.. - ٩٣ هـ / .. - ٧١٢ م]

<span data-type="title" id=toc-481>رفيع بن مهران الرياحي بالولاء، البصري، أبو العالية:</span>
محدث، مقرىء، مفسر، من كبار التابعين. أدرك الجاهلية وأسلم بعد وفاة النبي - صلى الله عليه وسلم - بسنتين، ودخل على أبي بكر وصلى خلف عمر. روى عن علي وابن مسعود وأبي موسى وغيرهم. قال أبو بكر بن أبي داود: "ليس أحد بعد الصحابة أعلم بالقرآن منه، وبعده سعيد ابن جبير .. ". وقال الداودي: "له "تفسير" رواه عنه الربيع بن أنس البكري، خرج حديثه الجماعة" (١).

روح بن عبادة [.. - ٢٠٥ هـ / .. - ٨٢٠ م]

<span data-type="title" id=toc-482>روح بن عبادة بن العلاء بن حسان القيسي البصري، أبو محمد:</span>
محدث، مفسر، من الحفاظ الثقات، من أهل البصرة. سمع شعبة وابن جريج وابن أبي عروبة وطبقتهم، وروى عنه أئمة كبار منهم أحمد بن حنبل. وكان كثير الحديث، قال
_________
(١) التاريخ الكبير للبخاري ق ١ ج ٢: ٣٢٦ والعبر ١: ١٠٨ وتذكرة الحفاظ ٦١ وشذرات الذهب ١: ١٠٢ وطبقات المفسرين للداودي ١: ٢٧٢ وطبقات القراء للذهبي ١: ٤٩ وخلاصة تهذيب الكمال ١٠١ وتهذيب التهذيب ٣: ٢٨٤ وغاية النهاية ١: ٢٨٤ واللباب ٢: ٤٦.','1','191',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (340,'ابن المديني: "نظرت لروح في أكثر من مائة ألف حديث كتبت منها عشرة الآلاف". وقال الخطيب البغدادي: "صنف الكتب في السنن والأحكام، وجمع التفسير، وكان ثقة". من آثاره "تفسير" "استخدمه الثعلبي في رواية أبي الأزهر أحمد بن الأزهر بن منيع العبدي النيسابوري، المتوفي سنة ٢٦٣ هـ " (١).

الشيرازي [.. - ٦٠٦ هـ / .. - ١٢٠٩ م]

<span data-type="title" id=toc-483>روزبهان بن أبي نصر البقلي، الفسوي، ثم الشيرازي، أبو محمد:</span>
محدث، مفسر، فقيه، أصولي، متكلم، أصله من فسا، وسكن شيراز فنسب إليها. له "عرائس البيان في حقائق القرآن" (كما في كشف الظنون وهدية العارفين) أو "لطائف البيان في تفسير القرآن" (كما في شد الإزار)، قال حاجي خليفة: وهو تفسير
_________
(١) طبقات المفسرين للداودي ١: ١٧٤ والتاريخ الكبير للبخاري ق ١ ج ٢: ٣٠٩ وتاريخ بغداد ٨: ٤٠١ وتاريخ التراث العربي ١: ٢٥٤ وتهذيب التهذيب ٣: ٢٩٣ وتذكير الحفاظ ٣٤٩ وخلاصة تهذيب الكمال ١٠١ وميزان الاعتدال ٢: ٥٨ والطبقات الكبرى لابن سعد ٧: ٢٩٦ والنجوم الزاهرة ٢: ١٧٩ والجرح والتعديل ١ ق ٢: ٤٩٨ والعبر ١: ٣٤٧ وابن النديم ٢٨٣ وشذرات الذهب ٢: ١٣ وهدية العارفين ١: ٢٧١ والأعلام ٣: ٦٣ ومعجم المؤلفين ٤: ١٧٣ وكشف الظنون ٤٤٨.','1','191',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (341,'على طريقة أهل التصوف. قال مصنفه: "صنفته موجزا مخففا لا إطالة فيه ولا إملال، وذكرت ما سنح لي من حقيقة القرآن ولطائف البيان بألفاظ لطيفة وعبارات شريفة، وربما ذكرت تفسير آية لم يفسرها المشايخ ثم أردفت بعد قولي أقوال مشايخي بما عبارتها ألطف وإشارتها أظرف وتركت كثيرا منها ليكون أخف عملا وأحسن تفصيلا" مخطوط (١).

رويم البغدادي [.. - ٣٠٣ هـ / .. - ٩١٥ م]

<span data-type="title" id=toc-484>رويم بن أحمد بن يزيد البغدادي، أبو محمد:</span>
صوفي، من جلة مشايخ بغداد، مقرىء، مفسر، فقيه على مذهب داود الظاهري. قال ابن كثير: تصوف أربعين سنة، ثم لما ولي إسماعيل بن إسحاق القضاء ببغداد جعله وكيلا في بابه، فترك التصوف ولبس الخز والقصب وركب الخيل وأكل الطيبات وبنى الدور" (٢).
_________
(١) هدية العارفين ١: ٣٧١ وكشف الظنون ١١٣١ وشد الإزار ٢٤٣ والمكبة البلدية تفسير ٢٩.
(٢) حلية الأولاء ١٠: ٢٦ وصفة الصفحة ٢: ٢٤٩ وتاريخ بغداد ٨: ٤٣٠ والبداية والنهاية ١١: ١٢٥ والرسالة القشيرية ٢٧ وطبقات الشعراني ١: ٨٨ والمنتظم ٦: ١٣٦ وطبقات الصوفية ١٨٠.','1','192',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (342,'<span data-type="title" id=toc-485>حرف الزاي</span>
