INSERT INTO [b13846] VALUES (344,'(ز)

الثقفي [.. - ١٦١ هـ / .. - ٧٧٨ م]

<span data-type="title" id=toc-486>زائدة بن قدامة، أبو الصلت الثقفي النكري الكوفي:</span>
محدث، مقرىء، مفسر، من أهل الكوفة. روى عن زياد الثعلبي وطبقته. قال الذهبي: "كان من نظراء شعبة في الاتقان، لكن ما علمت له عن غير أهل بلده" وأثنى عليه أبو حاتم الرازي والإمام أحمد. مات مرابطا بأرض الروم في أوائل سنة ١٦١ هـ وأخرخ له الجماعة. له "تفسير القرآن" (١).
_________
(١) غاية النهاية ١: ٢٨٨ والتاريخ الكبير للبخاري ٢/ ١: ٤٣٢ وشذرات الذهب ١: ٢٥١ والنجوم الزاهرة ٢: ٣٩ وأعيان الشيعة ٣٢: ١٦٣ وتذكرة الحفاظ ١: ٢١٥ وطبقات المفسرين للداودي ١: ١٧٤ وتهذيب التهذيب ٣: ٣٠٦ وابن النديم ٢٨٢ وخلاصة تهذيب الكمال ١٠٢ والعبر ١: ٢٣٦ وهدية العارفين ١: ٣٧٢. وابن عساكر ٥: ٣٤٦.','1','195',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (345,'الزبيري [.. - ٣١٧ هـ / .. - ٩٢٩ م]

<span data-type="title" id=toc-487>الزبير بن أحمد بن سليمان بن عبد الله بن عاصم الزبيري، أبو عبد الله:</span>
فقيه شافعي ثقة، محدث، عارف بالأدب، خبير بالأنساب. كان إمام أهل البصرة في عصره ومدرسها. حدث ببغداد عن داود المؤدب وغيره. وهو من أحفاد الزبير بن العوام. وكان أعمى. له مصنفات، منها "ناسخ القرآن ومنسوخه" (١).
_________
(١) تاريخ بغداد ٨: ٤٧١ ونكت الهميان ١٥٣ وابن خلكان ٢: ٣١٣ والسبكي ٢: ٢٢٤ ومرآة الجنان ٢: ٢٧٨ وغاية النهاية ٢٩٢ والنسخ في القرآن الكريم ١: ٣٢٤ وطبقات المفسرين ١: ١٧٥ وابن النديم ٤٠ و ٢٦٦ وهدية العارفين ١: ٣٧٣.','1','195',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (346,'الأنقروي [٩٢٠ - ١٠٠١ هـ / ١٥١٤ - ١٥٩٣ م]

<span data-type="title" id=toc-488>زكريا بن بيرم الأنقروي الرومي:</span>
فقيه حنفي، مفسر، باحث، تركي مستعرب، من أهل أنقرة، وبها نشأ وتعلم، ثم استوطن القسطنطينية. من كتبه "حاشية" على تفسير سورة الأعراف من أنوار التنزيل للبيضاوي (١). الخفاف [.. - ٢٨٦ هـ ... - ٨٩٩ م]

<span data-type="title" id=toc-489>زكريا بن داود بن بكر النيسابوري، أبو يحيى الخفاف:</span>
حافظ للحديث، مفسر، من أهل نيسابور مولدا ووفاة. قال الحاكم: هو المقدم في عصره، صاحب "التفسير الكبير". وقال الخطيب البغدادي: قدم بغداد وحدث بها (٢).

زكريا الأنصاري [٨٢٣ - ٩٢٦ هـ / ١٤٢٠ - ١٥٢٠ م]

<span data-type="title" id=toc-490>زكريا بن محمد بن أحمد بن زكريا الأنصاري السنيكي المصري، زين الدين، أبو يحيى:</span>
شيخ الإسلام.
_________
(١) كشف الظنون ١٩٢ وهدية العارفين ١: ٣٧٤ وخلاصة الأثر ٢: ١٧٣ ومعجم المؤلفين ٤: ١٨١.
(٢) تاريخ بغداد ٨: ٤٦٢ وهدية العارفين ١: ٣٧٣ وتذكرة الحفاظ ٦٧٦ وطبقات المفسرين ١: ١٧٥ ومعجم المؤلفين ٤: ١٨٢ والأعلام ٣: ٨٠.','1','196',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (347,'قاض، حافظ للحديث، مفسر، من فقهاء الشافعية. ولد في سنيكة (بشرقية مصر) ونشأ بها. ثم تحول إلى القاهرة سنة ٨٤١ هـ وقطن الأزهر، وأخذ عن علمائه، وكان فقير الحال، يجوع في الجامع، فيخرج ليلا ويجمع قشور البطيخ فيغسلها ويأكلها. واشتهر، فولاه السلطان قايتباي المحمودي (٨٧٢ - ٩٥١ هـ) قضاء القضاة سنة ٨٨٦ هـ، فلم يقبله إلا بعد مراجعة وإلحاح، ثم عزل سنة ٩٠٦ هـ. وفي هذه السنة كشف بصره، فعاد إلى اشتغاله بالعلم إلى أن توفي. من كتبه "فتح الرحمن بكشف ما يلتبس في القرآن"، في التفسير، طبع، قال صاحب "كشف الظنون": "وهو مختصر في ذكر الآيات المتشابهات المختلفة وغير المختلفة، وفيه أنموذج من أسئلة القرآن وأجوبتها، مأخذه كتاب الرازي، وله فيه بعض إلحاق". و "فتح الجليل ببيان خفي أنوار التنزيل" حاشية على تفسير البيضاوي، قال في كشف الظنون: نبه فيها على الأحاديث الموضوعة التي في أواخر السور" وقال الشعراني في "المتن"، أن الأنصاري "علق هذه الحاشية إملاء بعد أن كشف بصره، وغالبها بخطي وخط ولده جمال الدين" (١).
_________
(١) الضوء اللامع: ٣: ٢٣٤ والكواكب السائرة ١: ١٩٦ ومعجم المطبوعات ٤٨٣ والنور السافر ١٢٠ وهدية العارفين ١: ٣٧٤ والبدر الطالع ١: ٢٥٢ =','1','196',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (348,'زيب النساء [١٠٤٨ - ١١١٣ هـ / ١٦٣٨ - ١٧٠٠ م]

<span data-type="title" id=toc-491>زيب النساء بنت الشاه محي الدين أورنك زيب عالمكير سادس أباطرة المغل في الهند</span>
[١٦٥٨ - ١٧٠٧ م). أديبة، شاعرة، من آثارها "زيب التفاسير" في تفسير القرآن (١).

العدوي [.. ١٣٦ هـ / .. - ٧٥٣ م]

<span data-type="title" id=toc-492>زيد بن أسلم العدوي العمري، مولاهم، أبو أسامة، أو أبو عبد الله:</span>
فقيه، مفسر، محدث ثقة، كثير الحديث، من أهل المدينة. روى عن ابن عمر وأنس بن مالك وغيرهما. ومن رواته ابنه عبد الرحمن ومالك بن أنس. كان مع عمر بن عبد العزيز أيام خلافته، وهو أحد الفقهاء الذين دعاهم الخليفة الوليد بن يزيد إلى دمشق ليسمع آراءهم في قضايا ففهية. له "تفسير" رواه عنه ولده عبد الرحمن، قال صاحب "تاريخ التراث العربي": نجد كثيرا من آرائه الفقهية في "تفسيره" وكان هذا التفسير الأساسي الذي بنى عليه ابنه
_________
= والشذرات ٨: ١٣٤ ونظم العقيان ١١٣ والمجددون في الإسلام ٣٤١ وفهرس الفهارس ١: ٣٤٣ وكشف الظنون ١٨٨ و ١٢٣٢ والأعلام ٣: ٨٠ وفي بعض هذه المصادر خلاف في تاريخ مولده ووفاته.
(١) هدية العارفين ١: ٣٧٥.','1','197',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (349,'تفسيره، ويبدو أنه قد بقي كله أو بعضه في تفسير الطبرى" (١).

زيد بن ثابت [١١ ق هـ - ٤٥ هـ / ٦١١ - ٦٦٥ م]

<span data-type="title" id=toc-493>زيد بن ثابت بن الضحاك بن زيد بن لوذان الأنصارى الخزرجي، أبو خارجة، وأبو سعيد:</span>
صحابي، من أكابرهم، مقرىء، كان كاتب النبي - صلى الله عليه وسلم - وأمينه على الوحي وأحد الذين جمعوا القرآن على عهده - صلى الله عليه وسلم - من الأنصار، وعرضه عليه. وهو الذي كتبه في المصحف لأبي بكر، ثم لعثمان حين جهز المصاحف إلى الأمصار. ولد في المدينة ونشأ بمكة. قتل أبوه يوم بغاث وهو ابن ست سنين، وهاجر مع النبي - صلى الله عليه وسلم - وهو ابن ١١ سنة. واجتمع له شرف العلم والصحبة، وأول مشاهده الخندق. وكان عمر وعثمان يستخلفانه على المدينة. وكان ابن عباس - انظر ترجمته - يأتيه إلى بيته للعلم ويقول: العلم يؤتى ولا
_________
(١) التاريخ الكبير للبخاري ٢/ ١: ٣٨٥ وتهذيب الأسماء ١: ٢٠٠ وتهذيب التهذيب ٣: ٣٩٥ وخلاصة تهذيب الكمال ١٠٨ وتذكرة الحفاظ ١: ١٣٢ وشذرات الذهب ١: ١٩٤ وابن الأثير ٥: ٤٥٩ والمعارف لابن قتيبة ٩٥ وهدية العارفين ١: ٣٧٦ وكشف الظنون ٤٤٨ وأعيان الشيعة ٣٢: ٣٧٢ وطبقات المفسرين للداودي ١: ١٧٦ والجرح والتعديل ١: ٥٥١ وتاريخ التراث العرابي ٢: ٢٤ والأعلام ٣: ٩٥ ومعجم المؤلفين ٤: ١٨٩.','1','197',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (350,'يأتي، وأخذ ابن عباس بركاب زيد، فنهاه زيد، فقال ابن عباس: هكذا أمرنا أن نفعل بعلمائنا، فأخذ زيد كفه وقبلها وقال: هكذا أمرنا أن نفعل بآل بيت نبينا. عده الباحثون من مفسري الصحابة. ولما توفي قال أبو هريرة: اليوم مات حبر هذه الأمة وعسى الله أن يجعل في ابن عباس منه خلفا. له في كتب الحديث ٩٢ حديثا (١).

الفايشي [٤٥٨ - ٥٢٨ هـ / ١٠٦٦ - ١١٢٩ م]

<span data-type="title" id=toc-494>زيد بن الحسن بن محمد بن أحمد اليماني الفايشي:</span>
شيخ الشافعية وشيخ الفقهاء ببلاد اليمن في عصره. قال السبكي: جمع علوما في التفسير والقرآن والحديث واللغة والنحو والكلام والفقه والخلاف والحساب، ودرس ب "الجعامي" مدة حياته، وبها توفي" (٢).

زيد بن علي [٧٩ - ١٢٢ هـ - / ٦٩٨ - ٧٤٠ م]

<span data-type="title" id=toc-495>زيد بن علي بن الحسين بن علي بن أبي طالب:</span>
الإمام، أبو الحسين العلوي
_________
(١) شذرات الذهب ١: ٥٤ وغاية النهاية ١: ٢٩٦ وصفة الصفوة ١: ٢٩٤ وكشف الظنون ١: ٤٢٨ وعده من مفسري الصحابة الذين ليس لم تصانيف. وطبقات ابن سعد ٢: ٣٥٠ وانظر فهرسته. وتهذيب التهذيب ٣: ٣٩٩ والكامل في التاريخ. وطبقات الحفاظ ٣١.
(٢) طبقات الداودي ١: ١٧٧ وطبقات فقهاء اليمن ١٥٥ وطبقات السبكي ٧: ٨٥ وهدية العارفين ١: ٣٧٦.','1','198',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (351,'الهاشمي القرشي. فقيه، مفسر، شاعر، خطيب، عاش في الكوفة. قرأ على واصل بن عطاء (رأس المعتزلة)، وكانت له آراء في العقيدة تأثر فيها به. ثار على الأمويين محاولا الاستيلاء على الحكم (سنة ١٢٢ هـ) وقتل أثناء ذلك. تنسب إليه الطوائف الزيدية. من آثاره "تفسير غريب القرآن" رواه عنه أبو خالد عمرو بن خالد الواسطي. قال صاحب "تاريخ التراث العربيي" وهذا التفسير يناهض القدرية، وفيلف رأي المعتزلة في خلق الأفعال مخالفة واضحة، بيد أن زيدا يشارك المعتزلة في كثير من آرائهم ... . وفي هذا التفسير شروح لمواضع ليست ذات دلالة عقيدية، وقد نقلت هذه الشروح الكثيرة في تفاسير القرآن الكبيرة المشهورة بنصها .. الخ (١).

زيد بن محمد [أوائل القرقن ٥ هـ / أوائل القرن ١١ م]

<span data-type="title" id=toc-496>زيد بن محمد بن فضل:</span>
من الفقهاء
_________
(١) الطبقات الكبرى ٥: ٣٢٥ ومقاتل الطالبين ١٢٧ وتهذيب التهذيب ٣: ٤١٩ وفيات الوفيات ٣٥ والفرق بين الفرق ٣٠ ومختصره ٣٣ وطبقات المعتزلة ١٧ وتهذيب ابن عساكر ٦: ١٧ ومروج الذهب ٣: ٢٠٦ والتاريخ الكبير للبخاري ٢/ ١: ٤٠٣ والذريعة ١: ٣٣١ والأخبار الطوال ٣٤٤ والكامل لابن الأثير ٥: ٢٢٩ وأعيان الشيعة ٣٣: ٦٤٨ والأعلام ٣: ٩٨ وفي الحاشية أسماء مصادر أخرى وتاريخ التراث العربي ٢: ٣٢٨.','1','198',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (352,'والمفسرين في عهد الحاكم بأمر الله الفاطمي (٣٨٦ - ٤١١ هـ). ألف "المكنونة" في مجلدين. وتضم تأويلا لآيات القرآن.

زيدان السعدي [.. - ١٠٣٧ هـ / .. - ١٦٢٧ م]

<span data-type="title" id=toc-497>زيدان بن أحمد (المنصور بالله) بن محمد الشيخ المهدي بن عبد الله (القائم بأمر الله) من آل زيدان، أبو المعالي السعدي:</span>
من ملوك دولة الأشراف السعديين بمراكش. كان عالما بالتفسير والفقه، عارفا بالأدب، بويع بفاس. بعد وفاة والده - سنة ١٠١٢ هـ (١٦٠٣ م) وتوفي بمراكش. من آثاره "تفسير القرآن" (١).
_________
(١) اليواقيت الثمينة ٩: ١٥٤ وإتحاف أعلام الناس ٣: ٦٧ والاستقصا ٣: ٩٨ وهدية العارفين ١: ٣٧٦.','1','199',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (353,'الخوانساري [١١٨٨ - ١٢٤٥ هـ / ١٧٧٤ - ١٨٢٩ م]

<span data-type="title" id=toc-498>زين الدين بن محمد باقر الخونساري:</span>
فقيه إمامي، أصولي، مشارك في بعض العلوم. نسبته إلى خوانسار (بإيران). ولد وتعلم بالنجف، وتوفي بقرية قميسة. له "البدر الباهر في تفسير بعض الآيات المتعلقة بالقصص والنوادر" (١).
_________
(١) هدية العارفين ١: ٣٨٠ وإيضاح المكنون ١: ١٧٠.','1','199',NULL,NULL,'0');
