السيروزي = أحمد بن حسام الدين ... ١٠٣٣ هـ
السيغي = محمد بن عمر ...؟
ابن سينا = الحسين بن عبد الله ... ٤٢٨ هـ
السينوبي = إلياس بن إبراهيم ... ٨٩١ هـ
السيواسي = أحمد بن محمد ١٠٠٦ هـ
السيواسي = أحمد بن محمود ... ٨٦٠ هـ
السيواسي = عبد المجيد بن محرم ... ١٠٤٩ هـ
السيوطي (الجلال) = عبد الرحمن بن أبي بكر ... ٩١١ هـ','1','204',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (358,'(س)

المروزي [.. - ٢٣٥ هـ / .. - ٨٤٩ م]

<span data-type="title" id=toc-500>سريج بن يونس بن إبراهيم المروزي، البغدادي، أبو الحارث:</span>
محدث، مفسر، مقرىء، فقيه، أصله من مرو، سكن بغداد وحدث بها. ذكره ابن حبان في "الثقات"، وروى عنه البخاري ومسلم والنسائي. له "تفسير القرآن" و "الناسخ والمنسوخ" (١).

القمي [.. - ٣٠١ هـ / .. - ٩١٤ م]

<span data-type="title" id=toc-501>سعد بن عبد الله الأشعري القمي، أبو (القاسم)</span>
_________
(١) تاريخ بغداد ٩: ٢١٩ وابن النديم ٢٨٧ وطبقات المفسرين للداودي ١: ١٧٨ وتهذيب التهذيب ٣: ٤٨٥ وخلاصة تهذيب الكمال ١١٣ والعبر ١: ٤٢١ والنجوم الزاهرة ٢: ٢٨٢ والشذرات ٢: ٨٤ وهدية العارفين ١: ٣٨٢ والنسخ في القرآن الكريم ١: ٣١٥.','1','205',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (359,'القاسم: فقيه إمامي، محدث، مفسر، من أهل "قم". رحل كثيرا في طلب الحديث. من كتبه "ناسخ القرآن ومنسوخه ومحكمه ومتشابهه" (١).

المشاط [.. - ٥٤٦ هـ / .. - ١١٥١ م]

<span data-type="title" id=toc-502>سعد بن محمد بن محمود بن محمد بن أحمد، أبو الفضائل المشاط:</span>
مفسر، متكلم، واعظ، من فقهاء الشافعية، من أهل الري. سمع القاضي أبا المحاسن الروياني وأبا الفرج محمد القزويني الطبري وغيرهما. أثنى عليه
_________
(١) هدية العارفين ١: ٣٨٤ وهو فيه سعد بن إبراهيم، ومثله في الفهرست للنديم ٢٧٨. وانظر الرجال للنجاشي ١٢٦ وأعياد الشيعة ٣٤: ١٨٨ وتنقيح المقال ٢: ١٦ وفهرست الطوسي ٧٥ وإيضاح المكنون ٢: ٦١٥ والأعلام ٣: ١٣٥ ومعجم المؤلفين ٤: ٢١١. وقيل في وفاته سنة ٢٩٩ وقيل سنة ٣٠٠ هـ.','1','205',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (360,'السبكي في طبقاته وقال: "وكان عارفا بالمذهب والخلاف" وقال الصفدي: "له يد باسطة في علم الكلام، وكان يذب عن الأشعري" (١).

ابن الديري [٧٦٨ - ٨٦٧ هـ / ١٣٦٧ - ١٤٦٣ م]

<span data-type="title" id=toc-503>سعد بن محمد بن عبد الله بن سعد بن أبي بكر بن مصلح، أبو السعادات، المكنى سعد الدين، النابلسي الأصل، المقدسي، نزيل القاهرة، المعروف بابن الديري:</span>
مفسر، متكلم، قاض، من فقهاء الحنفية. ولد في القدس، ونسبته إلى قرية الدير بجبل نابلس. وانتقل إلى القاهرة فدرس وأفتى وفسر القرآن، ثم ولي قضاء الحنفية سنة ٨٤٢ هـ واستمر ٢٥ سنة. وضعف بصره، فاعتزل القضاء. قال السخاوي: "كان إماما علامة، شديد الرغبة في المباحثة في العلم والمذاكرة به، ذا عناية تامة بالتفسير لا سيما معاني التنزيل ... ، توفي بالقاهرة (٢).

سعدي جلبي [.. - ٩٥٤ هـ / .. - ١٥٣٩ م]

<span data-type="title" id=toc-504>سعد الله بن عيسى بن أميرخان، الشهير (بسعدي جلبي، أو سعدي أفندي)</span>
_________
(١) طبقات المفسرين ١: ١٧٨ وطبقات السبكي ٧: ٩٠ والوافي ١٥: ١٨١.
(٢) الضوء اللامع ٣: ٢٤٩ ونظم العقيان ١١٥ وهدية العارفين ١: ٣٨٥ والفوائد البهية ٧٨ والبدر الطالع ١: ٢٦٤ وحسن المحاضرة ١: ٢٧٠ والشذرات ٧: ٣٠٦.','1','206',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (361,'بسعدي جلبي، أو سعدي أفندي: قاض، مفسر، من فقهاء الحنفية، أصله من ولاية قسطموني. نشأ وتعلم في الآستانة، ودرس بها وببروسة، وولي قضاء الآستانة فحمدت سيرته، ثم تولى الإفتاء بها إلى آخر حياته. قال في "الشقائق النعمانية": "صرف جميع أوقاته في الاشتغال بالعلم، وملك كتبا كثيرة واطلع على عجائب منها وحفظ فوائدها، وقد بنى دار القراء بقرب داره بمدينة القسطنطينية". من آثاره "الفوائد البهية" حاشية على تفسير البيضاوي، منها نسخ في الأزهرية ودمشق وبغداد. ويستفاد مما ذكره صاحب الشقائق أن له أكثر من حاشية واحدة، فهو يقول: "وكتب "حواشي" مفيدة على تفسير البيضاوي". وقال صاحب "كشف الظنون": "له حاشية على تفسير البيضاوي، وهي من أول سورة هود إلى آخر القرآن، وأما التي وقعت على الأوائل فجمعها ولده بير محمد من الهوامش فألحقها إلى ما علقه، وفيها تحقيقات لطيفة ومباحث شريفة لخصها من حواشي الكشاف وضم إليها ما عنده من تصرفاته المسلمة، فوقع اعتماد المدرسين عليها، ورجوعهم عند البحث والمذاكرة إليها، وقد علقوا عليها رسائل لا تحصى" (١).
_________
(١) الشقائق النعمانية ٢٦٥ وهدية العارفين ١: ٣٨٦ والكواكب السائرة ٢: ٢٣٦ والشذرات ٨: ٢٦٢ وكشف الظنون ١٩١ والفوائد البهية ٧٨ وعثمانلي مؤلفلري ١: =','1','206',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (362,'القندهاري [.. - ١٣٠٦ هـ / .. - ١٨٨٩ م]

<span data-type="title" id=toc-505>سعد الله بن غلام حضرت القندهاري، الأفغاني:</span>
فاضل، له اشتغال بالتفسير، من أهل قندهار - في أفغانستان الجنوبية -، من آثاره "كشف المحجوبين عن خدي تفسير الجلالين" مخطوط، في المكتبة التيمورية (١). الديار بكري [.. - ١٢٤٧ هـ .. - ١٨٣١ م]

<span data-type="title" id=toc-506>سعيد بن إسماعيل الماويلي السهراني، الديار بكري، سعد الله:</span>
باحث، عارف بالتفسير والفقه، من فقهاء الحنفية. من أهل ديار بكر (مدينة في تركيا علن شاطىء دجلة الأيسر) تعلم بها، وقدم القسطنطينية فأخذ عن جماعة من العلماء، ثم سافر إلى مصر، وتولى رئاسة التصحيح في مطبعة بولاق التي جاء بها نابوليون إبان حملته على مصر سنة ١٧٩٨ م. من آثاره "الرسالة الفتحية المحميدية في بيان المراتب الجليلة العلية" في تفسير سورة النصر. ألفها برسم السلطان محمود خان الثاني
_________
= ٣٢٣ وعلم القرآن ٢٧٢ ومعجم المؤلفين ٤: ٢١٦ و ٨: ٢١ ودار الكتب الشعبية ٧٤ والتيمورية ٣: ١٣٤ والأزهرية ١: ٢٥٤ و ٢: ١٣٨، ٢٢٧ و ٣: ٦١١ والقادرية ١: ٨٥، ٨١ والأعلام ٣: ٨٨. وقد ورد اسمه في بعض هذه المصادر: عيسى بن اميرخان، أو سعد الدين عيسى بن أميرخان، وقد أخذت بما ذكر صاحب الشقائق النعمانية.
(١) التيمورية ١: ٥٣ و ٢٢٨ و ٢: ٢٤٧.','1','207',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (363,'(١٨٠٨ - ١٨٣٩ م). مخطوطة في المكتبة الخديوية (دار الكتب) (١).

أبر زيد الأنصاري [١١٩ - ٢١٥ هـ / ٧٣٧ - ٨٣٠ م]

<span data-type="title" id=toc-507>سعيد بن أوس بن ثابت الأنصاري، أبو زيد:</span>
لغوي، أديب، نحوي، من أهل البصرة. روى عن أبي عمرو بن العلاء وغيره، وروى له أبو داود، والترمذي. قال ابن خلكان: "كان من أئمة الأدب، وغلب عليه اللغات والنوادر والغريب، وكان يرى رأي القدرية، وهو ثقة في روايته". له تصانيف منها "لغات القرآن" (٢).

الأزدي [٩٨ - ١٦٨ هـ / ٧١٧ - ٧٨٤ م]

<span data-type="title" id=toc-508>سعيد بن بشير الأزلي، بالولاء، أبو عبد الرحمن:</span>
من رجال الحديث، مفسر، أصله
_________
(١) هدية العارفين ١: ٣٨٦ والخديوية ٧/ ٢: ٥٨٥.
(٢) طبقات المفسرين للداودي ١: ١٧٩ ووفيات الأعيان ٢: ٣٧٩ وتاريخ بغداد ٩: ٧٧ وأنباء الرواة ٢: ٣٠ وتهذيب التهذيب ٤: ٣ والعبر ١: ٣٦٧ ومعجم الأدباء١١: ٢١٢ وغاية النهاية ١: ٣٠٥ وبغية الوعاة ١: ٥٨٢ وهدية العارفين ١: ٣٨٧ ومرآة الجنان ٢: ٥٨ والبداية والنهاية ١٠: ٢٦٩ وميزان الاعتدال ٢: ١٢٦ ونزهة الألباء ١٢٥ وخلاصة تهذيب الكمال ١١٥ وابن النديم ٥٤ والشذرات ٢: ٣٤ والنجوم الزاهرة ٢: ٢١٠ ونور القبس ١٠٤ وروضات الجنات ٣١٣ وأخبار النحويين البصريين ٥٢ وتهذيب الأسماء واللغات ٢: ٢٣٥ والأعلام ٣: ١٤٤ وفي تاريخ وفاته خلاف، قيل سنة ٢١٤ وقيل ٢١٥ وقيل ٢١٦ هـ. والوافي ١٥: ٢٠٠.','1','207',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (364,'من البصرة أو واسط، مولده ووفاته بدمشق. وتعلم في البصرة. يروي عن قتادة والزهري، وعنه ابن مهدي، وأبو مسهر. قال البخاري: يتكلمون في حفظه، وقال أبو مسهر: "لم يكن في بلدنا أحفظ منه". وقال ابن سعد: كان قدريا". له كتاب في "التفسير" (١).

سعيد بن جبير [٤٥ - ٩٥ هـ / ٦٦٥ - ٧١٤ م]

<span data-type="title" id=toc-509>سعيد بن جبير بن هشام الأسدي، بالولاء، الكوفي، أبو عبد الله:</span>
من سادات التابعين علما، وفضلا، وصدقا، وعبادة، وواحد من أقدم مفسري القرآن. حبشي الأصل، من موالي بني والبة بن الحارث، من بني أسد. أخذ العلم عن ابن عباس وابن عمر، وسمع منه عمرو بن دينار، وجعفر بن إياس، والأعمش. له "تفسير" مفقود، لم يصل إلينا، يعتبر من أهم المراجع، يظهر ذلك في النصوص المنقولة عنه في الكتب المتأخرة، وكذلك في الروايات التي ذكرها الطبري في تفسيره. أخرج له أصحاب الكتب الستة. قتله الحجاج صبرا لخروجه عليه (٢).
_________
(١) طبقات المفسرين للداودي ١: ١٨٠ وخلاصة تهذيب الكمال ١١٦ وميزان الاعتدال ٢: ١٢٨ وتهذيب التهذيب ٤: ٨ والعبر ١: ٢٠٣ والنجوم الزاهرة ٢: ٥٦ والشذرات ١: ٢٦٥ والتاريخ الكبير للبخاري ق ١ ج ٢: ٤٦ وابن عساكر ٦: ١٢١. والوافي ١٠: ٢٠٠.
(٢) تهذيب التهذيب ٤: ١١ والجرح والتعديل ٢ ق ١: ٩ وحلية الأولياء ٤: ٢٧٢ ووفيات الأعيان ٢: =','1','208',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (365,'القطب الراوندي [.. - ٥٧٣ هـ / .. - ١١٨٧ م]

<span data-type="title" id=toc-510>سعيد بن عبد الله بن الحسين بن هبة الله بن الحسن، أبو الحسين، ويقال أبو الحسن، قطب الدين، الراوندي:</span>
عالم، أديب، مفسر، من فقهاء الشيعة الإمامية، نسبته إلى قرية راوند من قرى كاشان، بينها وبين أصفهان. وتوفي ببلدة قم وقبره بها. ذكر العاملي له ٥٦ مؤلفا، منها "خلاصة التفاسير" في عشر مجلدات، و "شرح آيات الأحكام" و "اللباب في فضل آية الكرسي من الكتاب". وفي "هدية العارفين" أن له "تفسير القرآن" بالإضافة إلى "الخلاصة" (١).
_________
= ٣٧١ وذكر أخبار أصبهان ٩: ٣٢٤ والنسخ في القرآن الكريم ١: ٣٥٩ وتاريخ البخاري ق ١ ج ٢: ٤٦١ وتذكرة الحفاظ ١: ٧٦ وغاية النهاية ١: ٣٠٥ والشذرات ١: ١٠٨ وطبقات ابن سعد ٦: ٢٥٦ والمعارف ٢٢٧ وتاريخ التراث العربي ١: ١٨٤ وطبقات المفسرين للداودي ١: ١٨١ والعقد الثمين ٤: ٧٥٤٩ والوافي ١٥: ٢٠٦.
(١) هديه العارفين ١: ٣٩٢، وهو فيه: سعيد بن هبة الله، وعنه أخذنا تاريخ وفاته. وأعيان الشيعة ٣٥: ١٦ وإيضاح المكنون ٤٣٤. ومعجم المؤلفين ٤: ٢٢٥ و ٢٣٣ ولسان الميزان ٣: ٤٨ وسفينة البحار للقمي ٢ والذريعة ٧: ١٤٥ وروضات الجنات ٣٠١ وتنقيح المقال ٢: ٢١ ومجلة المجمع العلمي العربي ٢٤: ٩٩ و ٢٥: ٣٠٦ وهو في بعض هذه المراجع سعيد بن هبة الله.','1','208',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (366,'ابن الدهان [٤٩٤ - ٥٦٩ هـ / ١١٠١ - ١١٧٤ م]

<span data-type="title" id=toc-511>سعيد بن المبارك بن علي بن عبد الله الأنصاري، ناصح الدين، ويقال تاج الدين، أبو محمد، المعروف بابن الدهان:</span>
نحوي مشهور، مفسر، أديب، ناظم. مولده ومنشأه ببغداد. سمع الحديث، وأخذ عنه الخطيب التبريزي وغيره. انتقل إلى الموصل، فأكرمه الوزير جمال الدين الأصبهاني المعروف بالجواد، وأقام في كنفه مدة، وكان قد ترك كتبه في بغداد، فطغى عليها سيل، فسير من يحضرها إليه، فحملت إليه وقد أصابها الماء، فأشير عليه أن يبخرها ببخور، فأحرق لها أكثر من ثلاثين رطلا، أثر دخانه في عينيه، فكف بصره. قال ابن خلكان: انتفع عليه خلق كثير، رأيت الناس يشتغلون في تصانيفه المذكورة بالموصل وتلك الديار اشتغالا كثيرا". من كتبه "تفسير القرآن" أربع مجلدات، و "تفسير سورة الفاتحة" و "تفسير سورة الإخلاص" (١).
_________
(١) طبقات المفسرين للداودي ١: ١٨٣ وروضات الجنات ٣١٤ وإنباه الرواة ٢: ٤٧ ووفيات الأعيان ٢: ٣٨٢ ونكت الهميان ١٥٨ وبغية الوعاة ١: ٥٨٧ وهدية العارفين ١: ٣٩١ ومعجم الأدباء ١١: ٢١٩ وابن قاضي شهبة ١: ٣٥٢ وشذرات الذهب ٤: ٢٣٣ ومرآة الجنان ٣: ٣٩٠ وكشف الظنون ٤٣٨ و ٤٤٩ والنجوم الزاهرة ٦: ٧٢ والوافي ١٥: ٢٥٠.','1','209',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (367,'الأنصاري [.. - حدود ٤٢٠ هـ / .. - حدود ١٠٢٨ م]

<span data-type="title" id=toc-512>سعيد بن محمد بن شعيب بن أحمد بن نصر الله الأنصاري، أبو عثمان:</span>
حافظ، خطيب، أديب، عالم، من أهل جزيرة "قبثور" بالأندلس. قال ابن بشكوال: "كان شفي صالحا من أئمة القرآن، عالما بمعانيه وقراءاته، وعالما بفنون العربية، متقدما في ذلك كله، حافظا ثبتا، وكان ظريف الحكايات والأخبار (١).

النيسابوري [.. - نحو ٤٤٠ هـ / .. - نحو ١٠٤٨ م]

<span data-type="title" id=toc-513>سعيد بن محمد بن حسن بن حاتم، أبو رشيد النيسابوري:</span>
من كبار المعتزلة، من أهل نيسابور. أخذ عن قاضي القضاة "عبد الجبار بن أحمد" (انظر ترجمته) وانتهت إليه الرياسة بعده. وكانت له حلقة في بلده، ثم انتقل إلى الري وأقام بها إلى أن مات. من آثاره "اعجاز القرآن" مخطوط، غير كامل، في الطائف (٢).

العقباني [٧٢٠ - ٨١١ هـ / ١٣٦٠ - ١٤٠٨ م]

<span data-type="title" id=toc-514>سعيد بن محمد بن محمد بن محمد (العقباني، التلمساني، أبو عثمان)</span>
_________
(١) طبقات المفسرين للداودي ١: ١٨٢ والصلة ١: ٢١٢ ومعجم البلدان ٤: ٣٠٤.
(٢) طبقات المعتزلة ١١٦ ولسان الميزان ٣: ٤٢ وفضل الاعتزال ٣٨٢ والأعلام ٣: ١٠١.','1','209',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (368,'العقباني، التلمساني، أبو عثمان: من أكابر فقهاء المالكية، قاض، مفسر، من أهل تلمسان، وكان إمامها وعلامتها في عصره، ومولده ووفاته بها. ولي قضاء مدينة "بجاية" في أيام السلطان أبي عنان المريني "والعلماء يومئذ متوافرون". كما ولي قضاء بلده تلمسان ووهران ومراكش وسلا، ومدة ولايته للقضاء نيف وأربعين سنة. قال صاحب نيل الابتهاج: "والعقباني نسبة إلى "عقبان" قرية بالأندلس، أصله منها، تجيبي النسب". من آثاره "تفسير سورة الأنعام" و "تفسير سورة الفتح" (١).

الخادمي [.. - ١٢١٣ هـ / .. - ١٧٩٨ م]

<span data-type="title" id=toc-515>سعيد بن محمد بن مصطفى بن عثمان الخادمي الرومي:</span>
فقيه حنفي، له اشتغال بالتفسير، مشارك في بعض العلوم، تركي مستعرب، انتقل إلى الحجاز واستوطن مكة إلى أن توفي. له "حاشية" على أنوار التنزيل في التفسير، للبيضاوي (٢).
_________
(١) معجم أعلام الجزائر (للمؤلف) - ط ٢ - ٢٣٧ وتعريف الخلف ٢: ١٥٣ ونيل الابتهاج ١٢٥ والديباج ١٢٤ ونفح الطيب ٥: ٤٢٨ والبستان ١٠٦ وطبقات المفسرين للداودي ١: ١٨٣ والأعلام ٣: ١٠١ ومعجم المؤلفين ٤: ٢٣٥ وتاريخ الجزائر العام ٢: ١٦٣ والمكتبة الصادقية، الرابع من الزيتونة ٤٠٢.
(٢) هدية العارفين ١: ٣٩٣.','1','210',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (369,'الأخفش الأوسط [... - ٢١٥ هـ / .. - ٨٣٠ م]

<span data-type="title" id=toc-516>سعيد بن مسعدة المجاشعي بالولاء، البلخي ثم البصري، أبو الحسن، المعروف بالأخفش الأوسط:</span>
نحوي، لغوي، أديب، من أهل بلخ، سكن البصرة، ودخل بغداد وأقام بها مدة، وروى وصنف بها. وكان معتزليا، وقال أبو حاتم السجستاني: "كان الأخفش رجل سوء قدريا، وكتابه في المعاني صويلح إلا أن فيه أشياء في القدر". قرأ النحو على سيبوبه، وكان أسن منه، ولم يأخذ عن الخليل بن أحمد. له كتب، منها "تفسير معاني القرآن" مخطوط (١).

الخراساني [.. - ٢٢٧ هـ / .. - ٨٤٢ م]

<span data-type="title" id=toc-517>سعيد بن منصور بن شعبة الخراساني، المروزي، الطالقاني، البلخي، أبو عثمان:</span>
من حفاظ الحديث الثقات،
_________
(١) طبقات المفسرين ١: ١٨٥ وبغية الوعاة ١: ٥٩ والشذرات ٢: ٣٦ ونور القبس ٩٧ ومعجم الأدباء ١١: ٢٢٤ ومرآة الجنان ٢: ٦١ ونزهة الألباء ١٣٣ وابن النديم ٥٢ وإنباه الرواة ٢: ٣٦ ووفيات الأعيان ٢: ٣٨٥ وكشف الظنون ١٧٣٠ والبداية والنهاية ١: ٢٩٣ ومفتاح السعادة ١: ١٣٢ وروضات الجنات ٣١٣ وأخبار النحويين البصريين ٥٠ وأعيان الشيعة ٣٥: ٦٠ وطبقات الزبيدي ٧٤ وهدية العارفين ١: ٣٨٨ ومراتب النحويين ٨ وتلخيص ابن مكتوم ٢٧ وتاريخ التراث العربي ١: ٢٢٢. والوافي ١٥: ٢٥٨.','1','210',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (370,'مفسر، أبواه من مرو، وولد هو في جوزجان، وشب في بلخ، ثم استوطن مكة وتوفي بها. روى عن حجر بن الحارث وسفيان بن عيينة وشريك وطبقتهم. وروى عنه مسلم وأبو داود. من آثاره "تفسير القرآن"، استخدمه الثعلبي مصدرا في كتابه "الكشف والبيان" (١).

ابن أبي عروبة = نحو [٧٠ - ١٥٦ هـ / ٦٨٩ - ٧٧٣ م] أ

<span data-type="title" id=toc-518>سعيد بن (أبي عروبة) مهران العدوي بالولاء، البصري، أبو النضر:</span>
من أهم محدثي عصره في البصرة، ومن أوائل من صنفوا كتبا في الحديث مرتبة ترتيبا منهجيا. روى عن قتادة، والحسن البصري، وابن سيرين، وخرج له ابن عدي، ووثقه ابن معين وأحمد. قال الذهبي: "شيخ البصرة وعالمها وأول من دون العلم بها، تغير حفظه قبل موته بعشر سنين". وعده ابن قتيبة في القدرية. قال سزجين: له " تفسير" اقتبسه ابن حجر في الإصابة (٣: ٨١٥) وقد أخذ التفسير عن قتادة (طبقات ابن
_________
(١) تهذيب التهذيب ٤: ٨٩ وميزان الاعتدال ١: ٣٩١ وتذكرة الحفاظ ٤١٦ وشذرات الذهب ٢: ٦٢ وهدية العارفين ١: ٣٨٨ وطبقات ابن سعد ٥: ٥٥٢ والتاريخ الكبير للبخاري ج ٢ ق ١: ٥١٦ وكشف الظنون ٤٤٩ والبداية والنهاية ١٠: ٢٩٩ والجرح والتعديل ٢ ق ١: ٦٨ والرجال للقيسراني ١٧٠ وتاريخ التراث العربي ١: ٢٨٦. والوافي ١٥: ٢٦٣.','1','211',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (371,'سعد (٧: ٢٧٣) وهو ما اقتبسه الطبري بهذا الإسناد: حدثنا بشر قال: حدثنا يزيد بن زريع عن سعيد عن قتادة" (١).

الثوري [٩٧ - ١٦١ هـ / ٧١٦ - ٧٧٨ م]

<span data-type="title" id=toc-519>سفيان بن سعيد بن مسروق الثوري، أبو عبد الله:</span>
أمير المؤمنين في الحديث، مفسر، كان عالم هذه الأمة، وعابدها وزاهدها. مولده ومنشأه في الكوفة. روى عن عمرو بن مرة وسماك بن حرب وغيرهما. وروى عنه وكيع ويحيى القطان وأبو نجيم وطائفة. وعاصر اثنين من خلفاء بني العباس، أبا جعفر المنصور (ت: ١٣٦ هـ) والمهدي (ت: ١٥٨ هـ) وكان لا يتودد إليهما، ولا يحب لقاءهما، فخرج من الكوفة وسكن مكة والمدينة، وهم المنصور بقتله في مكة فما أمهله الله، ثم طلبه المهدي، فتوارى، ودخل اليمن، ثم البصرة، فمات فيها مستخفيا. له "تفسير القرآن الكريم" رواية أبي جعفر محمد عن أبي حذيفة النهدي، عنه. صححه، ورتبه،
_________
(١) الشذرات ١: ٢٣٩ وتهذيب التهذيب ٤: ٦٣ والتاريخ الكبير ٢ ق ١: ٥٠٤ والمشاهير لابن حبان ١٥٨ وميزان الاعتدال ١: ٣٨٧ والفهرست ٢٢٧ وتذكرة الحفاظ ١٧٧ وطبقات ابن سعد ٧: ٢٧٣ وهدية العارفين ١: ٢٨٧ وتاريخ التراث العربي ١: ٢٦٤ والوافي ١٥: ٢٦٣.','1','211',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (372,'وعلق عليه، وقام بنشره امتياز علي عرشي مدير مكتبة رضا، رامبور، الهند (١).

ابن عيينة [١٠٧ - ١٩٨ هـ / ٧٢٥ - ٨١٤ م]

<span data-type="title" id=toc-520>سفيان بن عيينة بن ميمون الهلالي، الكوفي، أبو محمد:</span>
محدث الحرم المكي، مفسر، فقيه، ولد بالكوفة، واستوطن مكة وتوفي بها. سمع عمرو بن دينار، والزهري، ومنصور بن المعتمر وغيرهم. وروى عنه الأعمش وابن جريج وشعبة - وهم من شيوخه - والشافعي وأحمد بن حنبل. قال الشافعي: لولا مالك وسفيان لذهب علم الحجاز". وقال ابن حنبل: "ما رأيت أعلم بالسنن منه". وكان أعور، وحج سبعين سنة. له "تفسير القرآن" استخدمه ابن حجر في الإصابة (٢:
_________
(١) تاريخ التراث العربي ٢: ٢٢٣ والجرح والتعديل ٢: ٢٢٢ وطبقات المفسرين للداودي ١: ١٨٦ وهدية العرفين ١: ٣٨٧ وتهذيب التهذيب ٤: ١١١ وشذرات الذهب ١: ٢٥٠ والجواهر المضية ١: ٢٥٥ وحلية الأولياء ٦: ٣٥٦ و ٧: ٣ وتاريخ بغداد ٩: ١٥١ والفهرست ٢٢٥ والطبقات الكبرى لابن سعد ٦: ٣٧١ وتذكرة الحفاظ ٢٠٣ والتاريخ الكبير للبخاري ٢ ق ٢: ٩٢ وأعيان الشيعة ٣٥: ١٣٧ وتنقيح المقال ٢: ٣٦ والمعارف ٤٩٧ والمشاهير لابن حبان ١٦٩ ومنهج المقال ١٦٤ وروضات الجنات ٣١٦ ووفيات الأعيان ٢: ٣٨٦ والرسالة المستطرفة ٤١ وصيد الخاطر ١٧٥ وخلاصة تهذيب الكمال ١٢٣ والعبر ١: ٢٣٥ وذيل المذيل ١٠٥ ومنتهى المقال ١٤٨ واللباب ١: ١٩٨ والوافي ٢٧٨: ١٥','1','212',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (373,'٥١٥) والثعلبي في "الكشف والبيان" برواية سعيد بن عبد الرحمن المخزومي (١).

الفتى النهرواني [.. - ٤٩٣ هـ / .. - ١١٠٠ م]

<span data-type="title" id=toc-521>سلمان بن أبي طالب عبد الله بن محمد الفتى، الحلواني، النهرواني، أبو عبد الله:</span>
من العلماء باللغة والنحو والأدب، مفسر، من أهل النهروان - قرب بغداد - قدم بغداد وقرأ بها النحو واللغة، وسمع الحديث، وجال في العراق ونشر بها النحو، ثم استوطن أصبهان من كتبه "التفسير على القراءات" (٢).
_________
(١) تاريخ بغداد ٩: ١٧٤ وأعيان الشيعة ٣٥: ١٥١ والفهرست لابن النديم ٢٢٦ وتذكرة الحفاظ ٢٦٢ وطبقات المفسرين للداودي ١: ١٩٠ والعبر ١: ٣٢٦ وميزان الاعتدال ٢: ١٧٠ والتاريخ الكبير للبخاري ٢ ق ٢: ٩٤ والمشاهير لابن حبان ١٤٩ وحلية الأولياء ٧: ٢٧٥ وتهذيب التهذيب ٤: ١١٧ والمعارف لابن قتيبة ٢٥٤ والجرح والتعديل ٢ ق ١: ٢٢٥ والرجال للنجاشي ١٣٥ وشذرات الذهب ١: ٣٥٤ وهدية العارفين ١: ٣٨٧ والرسالة المستطرفة ٤١ وصفة الصفوة ٢: ١٣٠ والعقد الثمين ٤: ٥٩١ وطبقات ابن سعد ٥: ٤٩٧ وتاريخ التراث العربي ١: ٢٧٢ والأعلام ٣: ١٥٩ ومعجم المؤلفين ٤: ٢٣٥ والوافي ١٥: ٢٨١.
(٢) طبقات المفسرين للسيوطي ١٣ وطبقات المفسرين للداودي ١: ١٩٢ والوافي ١٥: ٣١١ وبغية الوعاة ١: ٥٩٥ ومعجم الأدباء ١١٤: ٢٣٤ وكشف الظنون ٤٤٦ وروضات الجنات ٣٢٢ ونزهة الألباء ٣٦٩ واسمه فيه: سليمان، ومثله في شذرات الذهب ٣: ٣٣٩ وإنباه الرواة ٢: ٢٦ وعلق محقق الإنباه كذا في الأصل، والذي في "الإكمال، ١: ٢١٨ وسائر المراجع الأخرى سلمان".','1','212',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (374,'النيسابوري [.. - ٥١٢ هـ / .. - ١١١٨ م]

<span data-type="title" id=toc-522>سلمان بن ناصر بن عمران بن محمد بن إسماعيل الأنصاري النيسابوري، أبو القاسم:</span>
فقيه شافعي، من الأئمة في علم الكلام والتفسير، صوفي. مولده ووفاته في نيسابور. سمع من عبد الغافر الفارسي وأبي القاسم القشيري وغيرهما. صحب القشيري مدة، ثم سافر إلى الحجاز، وعاد إلى بغداد، ثم قدم الشام، فصحب المشايخ، ثم عاد إلى نيسابور واستأنف تحصيل الأصول على إمام الحرمين. قال عبد الغافر الفارسي: "كان صوفيا، من بيت صلاح، وتصوف وزهد، وأصابه في آخر عمره ضعف في بصره وسمعه". له تصانيف في الفقه وعلم الكلام، ويقال أنه صنف في "التفسير" (١).

سلمة بن عاصم [.. - ٣١٠ هـ / .. - ٩٢٢ م]

<span data-type="title" id=toc-523>سلمة بن عاصم الكوفي، أبو محمد:</span>
نحوي، لغوي، من أهل الكوفة. أخذ عن يحيى بن زياد الفراء، وروى عنه كتبه كلها،
_________
(١) طبقات المفسرين للداودي ١: ١٩٣ وطبقات المفسرين ١٣ وتهذيب ابن عساكر ٦: ٢١١ وطبقات السبكي ٧: ٩٦ والعبر ٤: ٢٧ وشذرات الذهب ٤: ٣٤ وطبقات الشافعية لابن هداية (بتحقيقنا) ١٩٩ وهو فيه: سليمان، ومثله في مرآة الجنان ٣: ٢٠٣ والوافي ١٥: ٣١٤.','1','213',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (375,'وأخذ عنه ثعلب. من كتبه "معاني القرآن" قال ابن الأنباري: "هو أجود الكتب، لأن سلمة كان إماما عالما، وكان يراجع الفراء، فيما عليه، ويرجع عنه" (١).

الرازي [٣٦٥ - ٤٤٧ هـ / ٩٧٦ - ١٠٥٥ م]

<span data-type="title" id=toc-524>سليم بن أيوب بن سليم الرازي، أبو الفتح:</span>
فقيه شافعي، أصولي، محدث، مفسر. أصله من الري. دخل بغداد حدثا، فاشتغل باللغة والنحو والتفسير والمعاني والحديث، ثم تفقه على الشيخ أبي حامد الغزالي، ثم درس مكانه، وانتقل إلى بلاد الشام ورابط بثغر صور ينشر العلم، فتخرج عليه أئمة، منهم نصر المقدسي. وروى عنه أبو بكر الخطيب وغيره. وحج، فغرق في بحر القلزم عند ساحل جدة. من كتبه "ضياء القلوب" في التفسير. قال حاجي خليفة: "اختصره عبد الغني بن قاسم بن حسن بن أبي القاسم الشافعي المقرىء الحجازي، المتوفي بمصر سنة ٥٨٢ هـ، اختصارا حسنا" (٢).
_________
(١) طبقات المفسرين للداودي ١: ١٩٥ و ٢٠٨ والفهرست ٦٧ ومعجم الأدباء١١: ٢٤٢ وبغية الوعاة ١: ٥٩٦ ونزهة الألباء ١٤٦ وغاية النهاية ١: ٣١١ وفيه: توفي بعد السبعين ومائتين فيما أحسب". وهدية العارفين ١: ٣٩٥ ومراتب النحويين ٩٤ وتلخيص ابن مكتوم ٧٩ والمقتبس ٣٢١ والوافي ١٥: ٣٢٤.
(٢) كشف الظنون ١٠٩١ وطبقات السبكي ٤: ٣٨٨ =','1','213',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (376,'البلوي [.. - ٤٣٥ هـ / .. - ١٠٤٢ م]

<span data-type="title" id=toc-525>سليمان بن إبراهيم بن حمزة، أبو أيوب، البلوي:</span>
فقيه مالكي، محدث، عالم بعلوم القرآن، من أهل مالقة بالأندلس، روى عن شيوخها. قال ابن بشكوال: "كان مجودا للقرآن، عالما بكثير من معانيه، متصرفا في فنون من العربية، روى عن أبي عمر الطلمنكي كثيرا من روايته وتآليفه". مات بقرطبة (١).

القيسي [.. - قبل ٥٧٨ هـ / .. - قبل ١١٨٢ م]

<span data-type="title" id=toc-526>سليمان بن إبراهيم بن هلال، أبو الربيع، القيسي:</span>
عالم، له مشاركة في التفسير والحديث، من أهل طليطلة بالأندلس. قال ابن بشكوال: "كان رجلا صالحا زاهدا عالما بأمور دينه، تاليا للقرآن، مشاركا في التفسير والحديث، ورعا، فرق جميع ماله وانقطع لله تعالى، ولزم الثغور.
_________
= وطبقات المفسرين ١: ١٩٦ وطبقات ابن هداية (بتحقيقنا) ١٤٧ وتهذيب الأسماء واللغات ١: ٢٣١ والعبر ٣: ٢١٣ وشذرات الذهب ٣: ٢٧٥ وتبيين كذب المفتري ٢٦٢ وإنباه الرواة ٢: ٦٩ وطبقات الفقهاء ١١١ ووفيات الأعيان ٢: ٣٩٧ وهدية العارفين ١: ٤٠٩ ومرآة الجنان ٣: ٦٤ والأعلام ٣: ١٧٦ ومعجم المؤلفين ٤: ٢٤٣. والوافي ١٥: ٣٣٤.
(١) طبقات المفسرين للداودي ١: ١٩٧ والصلة ١: ١٩٦.','1','214',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (377,'توفي بحصن غرماج. وذكر أن النصارى يقصدونه ويتبركون بقبره، (١).

الطبراني [٢٦٠ - ٣٦٠ هـ / ٨٧٣ - ٩٧١ م]

<span data-type="title" id=toc-527>سليمان بن أحمد بن أيوب بن مطير اللخمي الشامي الطبراني، أبو القاسم:</span>
من كبار رجال الحديث في زمانه، مفسر، أصله من طبرية (بفلسطين) وإليها نسبته، ولد بعكا، وبدأ سنة ٢٧٣ هـ دراسة الحديث، فرحل إلى الشام والحرمين واليمن ومصر وبغداد والكوفة والبصرة وأصبهان والجزيرة وغيرها، وسمع إلى أكثر من ألف شيخ أثناء هذه الرحلة التي استغرقت ثلاثين عاما، ثم حدث عنهم. قال أبو نعيم: دخل الطبراني أصبهان سنة تسعين (٢٩٠ هـ) فسمع وسافر، ثم قدمها فاستوطنها ستين سنة". وتوفي بها. ترجم له الذهي وقال: "الحافظ الإمام العلامة الحجة بقية الحفاظ، مسند الدنيا ... وكان من فرسان هذا الشأن (أي الحديث) مع الصدق والأمانة .. " من تصانيفه الكثيرة "التفسير الكبير". (٢)
_________
(١) طبقات المفسرين للداودي ١: ١٩٨ والصلة ١: ١٩٦.
(٢) طبقات المفسرين للداودي ١: ١٩٨ وتاريخ التراث العربي ١: ٤٨٤ وأخبار أصبهان ١: ٣٣٥ ولسان الميزان ٣: ٧٣ وميزان الاعتدال ٢: ١٩٥ والمنتظم ٧: ٥٤ والعبر ٢: ٣١٥ ومرآة الجنان ٢: ٣٧٢ وابن عساكر','1','214',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (378,'أبوداود [٢٠٢ - ٢٧٥ هـ / ٨١٧ - ٨٨٩ م]

<span data-type="title" id=toc-528>سليمان بن الأشعث بن إسحاق بن بشير الأزدي السجستاني، أبو داود:</span>
إمام أهل الحديث في عصره، أصله من سجستان. بدأ رحلته العلمية في وقت مبكر، وسمع من مشايخ العراق وخراسان والجزيرة والشام ومصر والحجاز والثغر، وكان تلميذا لأحمد ابن حنبل. وحدث عنه الترمذي والنسائي وغيرهما. قيل: بلغ عدد شيوخه نحو ثلاثمائة شيخ. استقر في البصرة استجابة للخليفة العباسي الواثق بالله، وتوفي بها. له "ناسخ القرآن ومنسوخه" كان موجودا حتى أوائل القرن العاشر الهجري، نقل عنه السيوطي في الدر المنثور (١).
_________
= ٦: ٢٤٥ وتذكرة الحفاظ ٩١٢ والشذرات ٣: ٣٠ وطبقات الحنابلة ٢: ٤٩ والبداية والنهاية ١١: ٢٧٠ وغاية النهاية ١: ٣١١ والرسالة المستطرفة ٣٨ والنجوم الزاهرة ٤: ٥٩ ومناقب الإمام أحمد ٥١٣ وروضات الجنات ٣٢٢ وابن خلكان ٢: ٤٠٧ والأعلام ٣: ١٨١ والمختصر في أخبار البشر ٢: ١١٨ ومعجم المؤلفين ٤: ٢٥٣ والكامل لابن الأثير ٨: ٦١٧ واللباب ٢: ٢٧٣ ومعجم أعلام فلسطين مخطوط، للمؤلف، والوافي ١٥: ٣٤٤ وطبقات الحنابلة ٢: ٤٩.
(١) تهذيب التهذيب ٤: ١٦٩ وتذكرة الحفاظ ٢: ٥٩١ وطبقات المفسرين ١: ٢٠١ وتاريخ بغداد ٩: ٥٥ والجرح والتعديل ٢/ ١: ١٠١ ومفتاح السعادة ٢: ١٣٥ وابن عساكر ٦: ٢٤٤ والبداية والنهاية ١١: ٥٤ ومرآة الجنان ٢: ١٨٩ واللباب ٢: ١٠٥ والسبكي ٢: ٢٩٣ وطبقات الحنابلة ١: ١٥٩ والنسخ في القرآن الكريم ١: =','1','215',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (379,'أبو الوليد الباجي [٤٠٣ - ٤٧٤ هـ / ١٠١٢ - ١٠٨١ م]

<span data-type="title" id=toc-529>سليمان بن خلف بن سعد بن أيوب بن وارث التجيبي القرطبي، أبوالوليد الباجي:</span>
فقيه مالكي، من كبارهم، حافظ للحديث، مفسر، متكلم، أصولي، أديب، ولد في (باجة) قرب أشبيلية بالأندلس، وأصله من بطليوس. ورحل إلى المشرق سنة ٤٢٦ هـ فحج وجاور ثلاثة أعوام، ثم انتقل إلى بغداد فأقام بها ثلاثة أعوام أخذ خلالها عن أبي إسحاق الشيرازي والصيمري وغيرهما، وأقام بالموصل سنة، وبدمشق وحلب مدة، ثم عاد إلى الأندلس بعد ثلاثة عشر عاما، فولي القضاء في بعض أنحائها، قال القاضي عياض: "ولي قضاء أماكن تصغر عن قدره، فكان يبعث إليها خلفاءه" وربما أتاها المرة ونحوها. توفي بالمرية. من كتبه "تفسير القرآن" لم يتمه، و "الناسخ والمنسوخ" لم يتمه أيضا، وغير ذلك (١).
_________
= ٣١٨ وتهذيب الأسماء واللغات ٢: ٢٢٥ والمنتظم ٥/ ٢: ٩٧ والعبر ٢: ٥٤ والشذرات ٢: ١٦٧ وابن خلكان ٢: ٤٠٤ وهدية العارفين ١: ٣٩٥ وتاريخ التراث العربي ١: ٣٨٢ وطبقات الحنابلة لابن أبي يعلى ١: ١٥٩ والمختصر في أخبار البشر ٢: ٥٧ والأعلام ٣: ١٨٢ ومختصر دول الإسلام ١: ١٣١ وهدية الأحباب للقمي ١٥ ومعجم المطبوعات ٣٠٩ ومعجم المؤلفين ٤: ٢٥٥ والذريعة ١: ٣١٦ وتنقيح المقال ٢: ٥٥ وكشف الظنون ١٩٢١ والكامل لابن الأثير ٧: ٤٢٥. ودائرة المعارف الإسلامية.
(١) طبقات المفسرين ١: ٢٠٢ والنسخ في القرآن الكريم ١: ٣٣٠ وبغية الملتمس ٢٨٩ وتذكرة الحفاظ ١١٧٨ =','1','215',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (380,'مستقيم زاده [١١٣١ - ١٢٠٢ هـ / ١٧١٩ - ١٧٨٨ م]

<span data-type="title" id=toc-530>سليمان بن عبد الرحمن (أمن الله) بن محمد الرومي الحنفي، سعد الدين، الشهير بمستقيم زاده:</span>
أديب، صوفي، مفسر، مشارك في أنواع من العلوم، من علماء الدولة العثمانية، من أهل اسطنبول. له أكثر من ٥٠ كتابا ورسالة، منها "تفسير الفاتحة" (١).

الصرصري [٦٥٧ - ٧١٦ هـ / ١٢٥٩ - ١٣١٦ م]

<span data-type="title" id=toc-531>سليمان بن عبد القوي بن عبد الكريم بن سعيد الطوفي الصرصري، أبو الربيع، نجم الدين:</span>
فقيه حنبلي، أصولي، مشارك في أنواع من العلوم. كان قوي الحافظة، شديد الذكاء. ولد بقرية طوف - أو طوفا - من أعمال صرصر، في العراق، وبها نشأ وتعلم، ثم
_________
= والديباج المذهب ١٢٠ وطبقات المفسرين للسيوطي ١٤ ومعجم الأدباء ١١: ٢٤٦ والصلة ١: ١٩٩ وروضات الجنات ٣٢٢ وترتيب المدارك ٤: ٨٠٢ والقلائد ١٨٨ وتهذيب ابن عساكر ٦: ٢٤٨ وتاريخ قضاة الأندلس ٩٥ (والذخيرة، ق ٢: ٣٨) ونفح الطيب ٢: ٦٧ وشذرات الذهب ٣: ٣٣٤ والرسالة المستطرفة ٢٠٧ والبداية والنهاية ١٢: ١٢٢ ووفيات الأعيان ٢: ٤٠٨ وإيضاح المكنون ١: ٣٠٣ وهدية العارفين ١: ٣٩٧ والمغرب ١: ٤٠٤ وصفة جزيرة الأندلس ٣٦ ومرآة الجنان ٣: ١٠٨ والنجوم الزاهرة ٥: ١١٤ واللباب ١: ٨٢ وفوات الوفيات ٢: ٦٤ والزركشي ١٢٥.
(١) هدية العارفين ١: ٤٠٥ وعثمانلي مولفلري ١: ١٦٨ ومعجم المؤلفين ٤: ٢٦٦.','1','216',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (381,'دخل بغداد سنة ٦٩١ هـ وأخذ عن شيوخها. ورحل إلى دمشق سنة ٧٠٤ وأقام بها سنة، ثم انتقل إلى مصر وقرأ بها على أبي حيان وغيره. وتولى الإعادة في بعض مدارس الحنابلة. وقال الصفدي: "كان شيعيا يتظاهر بذلك، وجد بخطه هجو في الشيخن، ففوض أمره إلى بعض القضاة، وشهد عليه بالرفض، فضرب ونفي إلى قوص، فلم ير منه بعد ذلك ما يشين". ثم حج في أواخر سنة ٧١٤ هـ وجاور ودخل فلسطين، فأدركه أجله في بلد الخليل. من تصانيفه الكثيرة "الإكسير في قواعد التفسير" مخطوط، والإشارات الإلهية والمباحث الأصولية" في التفسير، مخطوط (١).

آل الشيخ [.. - ١٢٣٣ هـ / .. - ١٨١٨ م]

<span data-type="title" id=toc-532>سليمان بن عبد الله بن محمد بن عبد الوهاب، من آل الشيخ:</span>
فقيه، بارع في التفسير والحديث، من أهل نجد، من حفدة الشيخ محمد بن عبد الوهاب. وشى به بعض المنافقن إلى إبراهيم باشا ابن محمد علي باشا بعد استيلائه على الدرعية، فأمر بقتله، فقتل رميا بالرصاص (٢).
_________
(١) ابن رجب ٢: ٣٦٦ وكشف الظنون ١٤٣ وهدية العارفين ١: ٤٠٠ وإيضاح المكنون ١: ٨٣ والدرر الكامنة ٢: ٢٤٩ وبغية الوعاة ١: ٥٩٩ وأعيان الشيعة ٣٥: ٣٣ وجلاء العينين ٢٣ وروضات الجنات ٣٢٣ والشذرات ٦: ٣٩ والأنس الجليل ٢: ٥٩٣ ومرآة الجنان ٤: ٢٥٥ والأعلام
(٢) عنوان المجد ١: ٢١٠ وهدية العارفين ١: ٤٠٨ =','1','216',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (382,'الإبشيطي [.. - ٨١١ هـ / .. - ١٤٠٨ م]

<span data-type="title" id=toc-533>سليمان بن عبد الناصر بن إبراهيم بن محمد، صدر الدين، الإبشيطي:</span>
فقيه شافعي، مفسر، مشارك في بعض العلوم، من أهل مصر، أخذ عنه شيخ الإسلام ابن حجر العسقلاني. ودرس وأفتى. وناب في الحكم بالقاهرة وغيرها. قال ابن حجر: "كان ماهرا في أصول الفقه، والعربية، والآداب، والفقه، والخط، توفي وقد جاوز الثمانين" من كتبه "ناسخ القرآن ومنسوخه" (١).

الجمل [.. - ١٢٠٤ هـ / .. - ١٧٩٠ م]

<span data-type="title" id=toc-534>سليمان بن عمر بن منصور العجيلي الأزهري، المعروف بالجمل:</span>
مفسر، من فقهاء الشافعية، مشارك في بعض العلوم، من أهل "منية عجيل" - إحدى قرى الغربية بمصر - وانتقل إلى القاهرة، وتفقه على شيوخ وقته. ودرس الفقه والحديث والتفسير بالمدرسة الأشرفية والمشهد الحسيني. توفي بالقاهرة من كتبه "الفتوحات الإلهية بتوضيح تفسير
_________
= والأعلام ٣: ١٩٠ ومعجم المؤلفين ٤: ٢٦٨.
(١) إنباء الغمر ٢: ٤٠٩ والضوء اللامع ٣: ٢٦٥ وهدية العارفين ١: ٤٠٢ ومعجم المؤلفين ٤: ٢٦٩ ومولده فيه سنة ٨٠٧ ووفاته سنة ٨٨٧ هـ. ولم يذكر مصدره. وبغية الوعاة ١: ٦٠٠ ووفاته فيه سنة ٨٠١ هـ.','1','217',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (383,'الجلالين بالدقائق الخفية" حاشية على تفسير الجلالين، طبعت، في أربع مجلدات (١).

أبو داود [٤١٣ - ٤٩٦ هـ / ١٠٢٢ - ١١٠٣ م]

<span data-type="title" id=toc-535>سليمان بن نجاح الأموي القرطبي الأندلسي، أبو داود:</span>
شيخ القراء وإمام الإقراء في وقته. عالم بالتفسير، كان أبوه مولى لصاحب الأندلس المؤيد بالله هشام بن الحكم (٤٠٣ - ٣٥٥ هـ) وولد هو في قرطبة، ونزل دانية، وأخذ القراءات عن أبي عمرو الداني، وهو أجل أصحابه. قرأ عليه أبو علي الصدفي وغيره. وتنقل بين دانية وبلنسية. قال ابن بشكوال: "كان من جلة المقرئين وفضلائهم وأخيارهم، عالما بالقراءات وطرقها، حسن الضبط ثقة دينا" مات ببلنسية. من كتبه "البيان الجامع لعلوم القرآن" ثلاثمائة جزء. و "التبيين لهجاء التنزيل" ست مجلدات (٢).

العطار [٢٩٩ - ٣٨٧ هـ / ٩١١ - ٩٩٧ م]

<span data-type="title" id=toc-536>سهل بن إبراهيم بن سهل بن نوح بن عبد الله (بن جماز، ويعرف بالعطار)</span>
_________
(١) معجم المطبوعات ٧١٠ وهدية العارفين ١: ٤٠٦ والجبرتي، ٢: ١٨٣ وخطط مبارك ١٦: ٦٩ وإيضاح المكنون ١٧٧ ومعجم المؤلفين ٤: ٢٧١ والأعلام ٣: ١٩٤.
(٢) بغية الملتمس ٢٨٩ والصلة ١: ٢٥٠ وطبقات المفسرين للداودي ١: ٢٠٧ وطبقات القراء للذهبي ١: ٣٦٤ وغاية النهاية ١: ٣١٦ وشذرات الذهب ٣: ٤٠٣ والأعلام ٤٣: ٢٠٥ وهدية العارفين ١: ٣٩٨ والوافي ١٥: ٤٣٧.','1','217',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (384,'بن جماز، ويعرف بالعطار: نحوي، فقيه مالكي، عالم بمعاني القرآن والحديث، من أهل استجه، قال في تاريخ علماء الأندلس: "نسبه في البربر ويوالي بنى أمية، وكان فاضلا زاهدا، عاقلا ذكيا، عالما بمعاني القرآن، بصيرا بالمذاهب، حافظا للإعراب والحساب، مع الحديث ولزوم العبادة وإلانقباض" سمع بقرطبة، ودخل البيرة سنة ٣١٩ وسمع بها من محمد بن فطيس وغيره" ومات بها (١).

التستري [٢٠٠ - ٢٨٣ هـ / ٨١٥ - ٨٩٦ م]

<span data-type="title" id=toc-537>سهل بن عبد الله بن يونس بن عيسى بن عبد الله التستري، أبو محمد:</span>
أحد أئمة الصوفية وعلمائهم والمتكلمين في علوم الرياضيات والإخلاص وعيوب الأفعال. ولد في تستر - بالأهواز - وسكن البصرة وتوفي بها. صحب ذا النون المصري بمكة سنة خروجه إلى الحج. "اشتهر بأقواله الألف التي أدت إلى مذهب السالمية، وهو مراقبة الباطن من خلال أعمال العبادة. واصطناع ألفاظ أشبه بألفاظ أرباب العرفان تسلم إلى التوحيد ... " وكان دائم الترديد: الله معي، الله ناظر إلي، الله شاهدي". قال ابن خلكان: "الصالح المشهور، لم يكن له في وقته نظير في المعاملات
_________
(١) طبقات المفسرين ١: ٢٠٨ وتاريخ علماء الأندلس ١٩١ وهو فيه ابن خمار. وبغية الملتمس ٣٠٢ وبغية الوعاة ١: ٦٠٥.','1','218',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (385,'والورع .. ". من تصانيفه "تفسير القرآن العظيم" مختصر، طبع بمصر سنة ١٣٢٦ هـ - ١٩٠٨ م (١).

السجستاني [.. - ٢٤٨ هـ / .. - ٨٦٢ م]

<span data-type="title" id=toc-538>سهل بن محمد بن عثمان بن يزيد الجشمي السجستاني، أبو حاتم:</span>
من كبار العلماء في علوم القرآن واللغة والشعر. من أهل البصرة. روى عن أبي عبيدة والأصمعي وغيرها. وعنه ابن دريد وأبو العباس المبرد وكان يلازم القراءة عليه. ودخل بغداد، ولم يقم بها، ولم يأخذ عنه أهلها. قال السيوطي: "كان أعلم الناس بالعروض واستخراج المعمى، يعد من الشعراء المتوسطين. وكان يعنى باللغة، ترك النحو بعد اعتنائه به، حتى كأنه نسيه، ولم يكن حاذقا فيه. وكان جماعا للكتب يتجر فيها، ذكره ابن حبان في الثقات، وروى له النسائي في سننه والبزار في مسنده" له
_________
(١) حلية الأولياء١٠: ١٨٩ وطبقات المفسرين ١: ٢١٠ والعبر ٢: ١٠ وتذكرة الحفاظ ٦٨٥ واللباب ١: ٢١٦ وصفة الصوة ٤: ٤٦ والرسالة القشيرية ١٨ وطبقات الشعراني ١: ٩٠ وروضات الجنات ٣٢٤ والمنتظم ٥: ١٦٢ ونتائج الأفكار القدسية ١: ١٠٩ والكواكب الدرية ١: ٢٣٧ وابن النديم ١٨٦ ومرآة الجنان ٢: ١٤٨ وشذرات الذهب ٢: ١٨٢ وطبقات الصوفية ٢٠٦ ووفيات الأعيان ٢: ٤٢٩ وتاريخ التراث العربي ٢: ٤٥٢ ومعجم مصطلحات الصوفية ٤٤ والموسوعة العربية الميسرة ٥١٢ وهدية العارفين ١: ٤١٢ والأعلام ٣: ٣١٠.','1','218',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (386,'نيف وثلاثون كتابا، منها "إعراب القرآن" (١).
سيار بن عبد الرحمن = شيبان بن عبد الرحمن

سيد قطب [١٤٢٤ - ١٣٨٧ هـ / ١٩٠٦ - ١٩٦٦ م]

<span data-type="title" id=toc-539>سيد بن قطب بن إبراهيم:</span>
كاتب، عالم بالتفسير، من كبار المفكرين الإسلامين والأدباء في مصر في الثلث الثاني من القرن العشرين، ومن شهداء النهضة الإسلامية الحديثة. ولد في قرية "موشا" في أسيوط. تعلم بالقاهرة، وتخرج بكلية دار العلوم سنة ١٣٥٣ هـ (١٩٣٤ م). وعمل في جريدة "الأهرام"، وكتب في مجلتي "الرسالة" و "الثقافة" واشتغل
_________
(١) وفيات الأعيان ٢: ٤٣٠ وبغية الوعاة ١: ٦٠٦ ومعجم الأدباء١١: ٢٦٣ وطبقات المفسرين ١: ٢١٠ وإنباه الرواة ٢: ٥٨ وغاية النهاية ١: ٣٢٠ ونزهة الألباء ١٨٩ ومرآة الجنان ٢: ١٥٦ وابن النديم ٥٨ والعبر ١: ٤٥٥ والنجوم الزاهرة ٢: ٣٣٢ وتهذيب التهذيب ٤: ٢٥٧ وشذرات الذهب ٢: ١٢١ والبداية والنهاية ١١: ٢ وطبقات النحاة لابن قاضي شهبة ١: ٣٦١ وأخبار النحويين البصريين ٩٣ وهدية العارفين ١: ٤١١ وكشف الظنون ١٢٤ ومختصر دول الإسلام ١: ١١٨ والأعلام ٣: ١٤٣. ووفاته في بعض هذه المصادر والمراجع سنة ٢٤٨، أو ٢٥٠، أو ٢٥٤ أو ٢٥٥ وهو التاريخ الذي رجحه (جولد سيهر) في دائرة المعارف الإسلامية، ولعله نقله عن سير أعلام النبلاء للذهبي. وقد أخذت برواية ابن خلكان.','1','219',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (387,'بالتدريس، ثم عين موظفا في ديوان وزارة المعارف، فمراقبا فنيا للوزارة. وفي سنة ١٩٤٨ أوفد في بعثة رسمية لدراسة برامج التعليم في الولايات المتحدة، وعاد (سنة ١٩٥١ م) فانتقد البرامج المصرية التي تنتهجها الوزارة، وكان يراها من وضع الانجليز لتنفيذ مخططاتهم الاستعمارية وطالب الدولة بوضع برامج جديدة تتمشى والفكرة الإسلامية. وقامت الثورة (سنة ١٩٥٢) فاستقال من عمله وانضم إلى جماعة الإخوان المسلمين، فكان من أشد أنصارهم، وترأس قسم نشر الدعوة، وتولى تحرير جريدة "المسلمون" (١٩٥٣ - ١٩٥٤) الناطقة باسمهم. وجرت محاولة لاغتيال الرئيس جمال عبد الناصر (١٩٥٤) فكان سيد قطب من بين قادة جماعة الإخوان الذين اعتقلتهم السلطات وزجت بهم في غياهب السجون، فعكف على التأليف وهو في سجنه. وأطلق سراحه سنة ١٩٥٧، ثم أعيد إلى السجن سنة ١٩٦٤، فاستمر فيه إلى أن صدر الأمر بإعدامه، فأعدم. ولما وصل خبر استشهاده إلى بلدان العالمين العربي والإسلامي أقيمت على روحه الطاهرة صلاة الغائب. ولما كانت "النكسة" عام ١٩٦٧، قال علال الفاسي زعيم حزب الاستقلال المغربي: "ما كان الله لينصر حربا يقودها قاتل سيد قطب ... ويقصد','1','219',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (388,'به جمال عبد الناصر. له كتب كثيرة في الدفاع عن الإسلام والتعريف به، إنما أشهر مؤلفاته المتداولة تفسيره لكتاب الله العزيز "في ظلال القرآن"، طبع مرات، وطبعته الأخيرة الصادرة عن "دار الشروق" تتضمن إضافات وتنقيحات تركها المؤلف الشهيد وتنشر للمرة الأولى. والكتاب في ست مجلدات كبار (١).
_________
(١) سيد قطب وتراثه الأدبي والفكري، لإبراهيم البليهي، والشعر بعد شوقي، لمحمد مندور، الحلقة ٣: ٤٥ ومصادر الدراسة الأدبية ٢: ١٠٣٣ ومجلة العرب (السعوية) ٨: ١٥٩ ومجلة الشهاب (اللبنانبة) العدد ٢٤ في ١٠ جمادي الأولى ١٣٩٤ هـ والأعلام ٣: ١٤٧.','1','220',NULL,NULL,'0');
