INSERT INTO [b13846] VALUES (393,'(ش)

شاه شجاع [.. - ٧٨٧ هـ / .. - ١٣٨٥ م]

<span data-type="title" id=toc-541>شاه شجاع بن محمد بن مظفر اليزدي، جلال الدين المظفر:</span>
من ملوك فارس وكرمان وكردستان. خلف والده مبارز الدين محمد على الإمارة بعد أن ألقى به في السجن حيث مات سنة ٧٦٥ هـ. وقاتل أخاه شاه محمود، الذي أقر بسيادته، واستولى على أصفهان وتبريز. قال ابن حجر: "اشتغل بالعلم واشتهر بحسن الفهم ومحبة العلماء، وكان ينظم الشعر ويحب الأدباء ويجيز على المدائح. وكان يقرىء "الكشاف" وكتب منه نسخة بخطه الفائق ... " (١).
_________
(١) شذرات الذهب ٦: والدرر الكامنة ومعجم الاسرات الحاكمة ٣٧٩.','1','225',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (394,'المكي [٧٠ - نحو ١٦٠ هـ / ٦٨٩ - نحو ٧٧٦ م]

<span data-type="title" id=toc-542>شبل بن عباد المكي، أبو داود:</span>
مفسر، مقرىء، محدث، من أهل مكة. قال ابن الجوزي: مقرىء مكة، ثقة ضابط، هو أجل أصحاب عبد الله بن كثير، قيل أنه مات سنة ١٤٨ هـ قال الذهبي: وأظنه وهما، فإن أبا حذيفة موسى بن مسعود إنما سمع منه سنة نيف وخمسين، ثم قال: بقي إلى قريب سنة ستين ومائة بلا ريب،. من آثاره "تفسير القرآن" قال صاحب "تاريخ التراث العربي": هو من الكتب التي حصل على إجازة روايتها الخطيب البغدادي في دمشق. ولقد استخدمه الطبري في تفسيره وفي تاريخه بالرواية الآتية: حدثني المثنى بن إبراهيم، قال: حدثنا أبو حذيفة موسى بن مسعود، قال: حدثنا شبل"،','1','225',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (395,'وهذا التفسير أحد مراجع الثعلبي في كتابه الكشف والبيان" (١).

الفلاس [١٥٥ - ٢٣٥ هـ / ٧٧٢ - ٨٤٩ م]

<span data-type="title" id=toc-543>شجاع بن مخلد الفلاس، أبو الفضل البغوي:</span>
محدث ثقة، مفسر، سكن بغداد وحدث وتوفي بها. أثنى عليه أحمد بن حنبل ويحيى بن معين وغيرهما. وعده ابن حبان في الثقات. له كتاب في "التفسير" ذكره الخطيب البغدادي (٢).

ابن حبيب الغزي [.. - ١٠٠٥ هـ / .. - ١٥٩٦ م]

<span data-type="title" id=toc-544>شرف الدين بن عبد القادر بن بركات بن إبراهيم، المعروف بابن حبيب الغزي:</span>
عارف بالتفسير والعربية، من فقهاء الحنفية، من أهل غزة (بفلسطين). له (محاسن الفضائل بجمل الرسائل"، ضمنه تفسير بعض الآيات القرآنية الكريمة، كان قد سئل عنها (٣).
_________
(١) غاية النهاية ١: ٣٢٣ وتهذيب التهذيب ٤: ٣٠٥ والجرح والتعديل ٢/ ١: ٣٨٠ وتاريخ التراث العربي ١: ١٩٦.
(٢) تاريخ بغداد ٩: ٢٥١ ولسان الميزان ٢: ٢٦٥ وتهذيب التهذيب ٤: ٣١٢.
(٣) خلاصة الأثر ٢: ٢٢٣ ولم يذكر وفاته. وهدية العارفين ١: ٤١٦ وفيه توفي سنة ١٠٣٥ هـ. وصفحة ٥٩٩ وهو فيه شرف الدين عبد القادر ووفاته سنة ١٠٠٥ هـ. وصفحة ٦٠٣ وهو فيه عبد القادر بن عبد الله، ووفاته سنة =','1','226',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (396,'مؤذن زاده [.. - ١٠٩٩ هـ / .. - ١٦٨٨ م]

<span data-type="title" id=toc-545>شعبان بن أيوب الرومي، الشهير بمؤذن زاده:</span>
مفسر، قاض، من فقهاء الحنفية، تركي مستعرب. من محلة أبي أيوب الأنصاري بالآستانة. ولي قضاء بغداد. له "تفسير القرآن" ألفه باسم الوزير أحمد فاضل باشا المتوفي سنة ١٠٨٧ هـ (١).

شعبة بن الحجاج [٨٢ - ١٦٠ هـ / ٧٠١ - ٧٧٧ م]

<span data-type="title" id=toc-546>شعبة بن الحجاج بن الورد العتكي الأزدي، مولاهم، الواسطي، ثم البصرى، أبو بسطام:</span>
من تابعي التابعين وأعلام المحدثين وكبار المحققين. ولد ونشأ بواسط، وسكن البصرة إلى أن توفي. وهو من أوائل من صنفوا الحديث في البصرة تصنيفا منهجيا، وأول من بحث أحوال حياة المحدثين وأفرد لهذا علما مستقلا. روى عن كبار الأئمة وأجمعوا على إمامته في الحديث وجلالته وتحريه واحتياطه وإتقانه. قال الشافعي: "لولا شعبة ما عرف الحديث بالعراق". وقال أحمد
_________
= ١١٣٢. و (إيضاح المكنون ١: ٥٧ ولم يذكر تاريخ وفاته، وصفحة ١٣٣ أنه كان حيا ١٠٠٥ هـ و ٢: ٤٤ وفيه وفاته سنة ١٠٠٥ هـ. ومعجم المؤلفين ٥: ٢٩٨ وفيه أنه كان حيا سنة ١٠٣٢ والأعلام ٣: ١٦١.
(١) هدية العارفين ١: ٤١٧ ومعجم المؤلفين ٤: ٣٠٠.','1','226',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (397,'بن حنبل: "كان شعبة أمة وحده في هذا الشأن" (يعني علم الحديث وأحوال الرواة). وعن سفيان الثوري أنه قال: (شعبة أمير المؤمنين في الحديث". وكان عالما بالشعر، حتى أن الأصمعي قال: "لم نر أحدا قط أعلم بالشعر من شعبة". من آثاره "تفسير القرآن الكريم" (١).

العمادي [١٠٠٧ - ١٠٧٨ هـ /١٥٩٨ - ١٦٦٧ م]

<span data-type="title" id=toc-547>شهاب الدين بن عبد الرحمن بن محمد العمادي، الدمشقي:</span>
فقيه حنفي، مفسر، له نظم، من أهل دمشق مولدا ووفاة. اشتغل بالتدريس، وولي قضاء الركب الشامي، وحج. له "تعليقات" في التفسير والفقه (٢). الإسفراييني [٤٧١ هـ ... - ١٠٧٨ م]

<span data-type="title" id=toc-548>شهفور (ويقال: شاهفور) بن طاهر بن محمد الإسفراييني، أبو المظفر:</span>
مفسر، أصولي، من فقهاء الشافعية. ذكره عبد الغافر
_________
(١) تاريخ بغداد ٩: ٢٥٥ والتاريخ الكبير ٢/ ٢: ٤٤ وتهذيب الأسماء واللغات ١: ٢٤٤ وتهذيب التهذيب ٤: ٣٣٨ وتاريخ التراث العربي ١: ٢٦٥ وتذكرة الحفاظ ١٩٣ وحلية الأولياء ٧: ١٤٤ وأعيان الشيعة ٣٦: ١١٣ والجرح والتعديل ٢/ ١: ٣٦٩ والشذرات ١: ٢٤٧ وذيل المذيل ١٠٤ وابن سعد ٧: ٢٨٠ والأعلام ٣: ٢٤١ وهدية العارفين ١: ٤١٧ ومعجم المؤلفين ٤: ٣٥١ وكشف الظنون ٤٥١. والرسالة المستطرفة ٨٥.
(٢) خلاصة الأثر ٢: ٢٣١.','1','227',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (398,'الفارسي في تاريخه وقال: سافر في طلب العلم، وحصل الكثير، وارتبطه نظام الملك بطوس فأقام بها سنين، ودرس بها، وأفاد الكثير واستفاد الناس منه، ومات بطوس". له تفسير كبير مشهور سماه "تاج التراجم في تفسير القرآن للأعاجم" (١).

النحوي [.. - قبل ٣٨٠ هـ / .. - قبل ٩٩١ م]

<span data-type="title" id=toc-549>شيبان بن عبد الرحمن النحوي:</span>
له تفسير. ذكره ابن النديم ولم يزد على ذلك (٢)
_________
(١) طبقات الشافعية ٥: ١١ وطبقات المفسرين للداودي ١: ٢١٢ وبروكلمان ١: ٣٨٧ والأعلام ٣: ٢٦٥ ومعجم المؤلفين ٤: ٣١٠ وهو فيه شهفور بن طاهر، ثم سماه (٥: ٣٨) طاهر بن محمد الشهير بشاهفور. ومثله في كشف الظنون ٢٦٨ و ٣٤٠ و ٤٤٢. وهدية العارفين ٢: ٤٣٠.
(٢) الفهرست ٣٦ (طبعة طهران) وجاء اسمه في الطبعة القديمة سيار. وعنها نقل محقق طبقات المفسرين للداودي ١: ٢١٢.','1','227',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (399,'<span data-type="title" id=toc-550>حرف الصاد</span>
ابن الضائغ = أبو بكر بن أحمد ... ٧١٤ هـ
ابن الضائغ = محمد بن إبراهيم ... ١٠٦٦ هـ
ابن الضائغ = محمد بن عبد الرحمن ... ٧٧٦ هـ
الصابوني = إسماعيل بن عبد الرحمن ... ٤٤٩ هـ
الصادقي = محمود بن الحسن ... ٩٧٠ هـ
الصاوي = أحمد بن محمد ١٢٤١ هـ
الضباغ = عبد الله بن حنين ... ٣١٨ هـ
ابن الصباغ = صالح بن عبد الله ... ٧٢٧ هـ
ابن صبر = محمد بن عبد الله ... ٣٨٠ هـ
الصدر = إسماعيل الصدر ... ١٣٨٩ هـ
الصدر الشيراني = محمد بن إبراهيم ... ١٠٥٥ هـ
صدر الدين الكبير = محمد بن إبراهيم ... ٩٠٣ هـ
الصدفي = أحمد بن مغيث ... ٤٥٩ هـ
صديق حسن خان = محمد صديق خان ... ١٣٠٧ هـ
الصرصري = سليمان بن عبد القوي ... ٧١٦ هـ
الصعدي = علي بن صلاح ... نحو ١٠٧٠ هـ
الصعلوكي = محمد بن سليمان ... ٣٦٩ هـ
الصفار = محمد بن الحسن ... ٢٩٠ هـ
صفر شاه الحنفي ... حيا ٩١٩ هـ
الصفواني = محمد بن أحمد ... بعد ٣٤٦ هـ
الصفوي = عيسى بن محمد ٩٥٣ هـ
صفين المولوي = موسى بن علي ... ١١٥٧ هـ
ابن الصلاح = عثمان بن عبد الرحمن ... ٦٤٣ هـ
صلاح الدين العلائي = خليل بن كيكلدي ... ٧٦١ هـ','1','229',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (400,'الصناري = علي بن عراق ... ٥٣٩ هـ
صندقلي = عبد الحليم بن ببر قدم ... ٥٣٩ هـ
الصندلي = علي بن الحسن ... ٤٨٤ هـ
صنع الله بن جعفر ... ١٠٢١ هـ
الصنعاني = عبد الرزاق بن همام ... ٢١١ هـ
الصنعاني = محمد بن علي ... قبل ٧٠٩ هـ
الصنعاني = وهب بن منبه ... ١١٤ هـ
الصهري = عبد الرحمن بن إبراهيم ... ١٠٦٤ هـ
الصولي (أبو بكر) = محمد بن يحيى ... ٣٣٥ هـ
ابن الصيرفي = أحمد بن صدقة ... ٩٠٥ هـ','1','230',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (401,'(ص)

الشرواني [١٠٣١ - ١٠٨٣ هـ / ١٦٢٢ - ١٦٧٢ م]

<span data-type="title" id=toc-551>صالح بن إسحاق الشرواني الأصل، القسطنطيني، القره باغي الرومي، المعروف ب "ظهوري" وإسحاق زاده:</span>
قاض، مفسر، عارف بالتاريخ والأنساب، شاعر، تولى القضاء ببيروت، ثم بمصر، وتوفي بها وهو قاض. من كتبه "حاشية على تفسير البيضاوي" (١).

ابن الصباغ [٦٣٩ - ٧٢٧ هـ / ١٢٤١ - ١٣٢٧ م]

<span data-type="title" id=toc-552>صالح بن عبد الله بن جعفر بن علي بن صالح الأسدي، محي الدين، ابن الصباغ الكوفي:</span>
_________
(١) خلاصة الأثر ٢: ٢٣٧ وعنه أخذنا تاريخ وفاته. وهدية العارفين ١: ٤٢٣ ووفاته فيه سنة ١٠٧٣ هـ، ومثله في إيضاح المكنون ١: ١٤١.','1','231',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (402,'عالم بالتفسير والفقه والفرائض والأدب، من أهل الكوفة. انتدب لرياسة الحنفية بالمستنصرية، فامتنع. قال في "الدرر الكامنة": "كان نادرة العراق في علوم التفسير والفقه .. الخ" (١).

البلقيني [٧٩١ - ٨٦٨ هـ / ١٣٨٩ - ١٤٦٤ م]

<span data-type="title" id=toc-553>صالح بن عمر بن رسلان بن نصير بن صالح الكناني العسقلاني البلقيني، علم الدين، أبو البقاء:</span>
عالم بالحديث والفقه والتفسير، قاض، متكلم، له نظم، من كبار فقهاء الشافعية في وقته. ولد بالقاهرة، وتفقه بوالده وأخيه عبد الرحمن، وأخذ عن كبار العلماء. حج سنة ٨١٤ هـ. وناب عن أخيه في الحكم
_________
(١) طبقات المفسرين ١: ٢١٣ والدرر الكامنة ٢: ٢٩٩ وتاريخ علماء بغداد ٦٢ وبغية الوعاة ٢: ١٠.','1','231',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (403,'بدمنهور وغيرها، ودرس الفقه (قبل ٨٢٠ هـ) بالمدرسة الملكية، والتفسير (٨٢١ هـ) بالبرقوقية. ثم استقر بعد وفاة أخيه (٨٢٤ هـ) في تدريس الفقه بالخشابية والنظر عليها بجامع عمرو، وحضر عنده فيه الكبار من شيوخه وغيرهم. ثم ولى قضاء الشافعية بالديار المصرية في ذي الحجة سنة ٨٢٦ هـ، واستمر إلى أن عزل بعد سنة وشهر، ثم تكرر عزله وإعادته ست مرات. ومدة ولايته القضاء في المرار السبعة ثلاث عشرة سنة ونصف سنة. ولشعراء عصره فيه عدة قصائد. قال السخاوي: "كان إماما فقيها عالما قوي الحافظة، سريع الإدراك، طلق العبارة فصيحا، يتحاشى عدم الإعراب في مخاطباته، بحيث لا نضبط عليه في ذلك شاذة ولا فاذة، شهما مقداما لا يهاب ملكا ولا أميرا. تصدى لنشر العلم قديما، وكذا للوعظ والإفتاء، وحضر مجلس وعظه السادة من الشيوخ والرفاق، وطارت فتاويه في الآفاق، واشتهر اسمه وبعد صيته .. ". توفي بالقاهرة وهو على القضاء. من كتبه "تفسير القرآن" في ثلاثة عشر مجلدا، قال السخاوي: "شرع فيه لما عقد الميعاد بالمدرسة التي لوالده بعد موت أخيه، وانتهى منه في سنة ثلاث وستين، استمد فيه من "ابن كثير" و "البغوي" و "القرطبي" وتعاليق أبيه وأخيه في ذلك ونحوها". وله أيضا "تعليق على الكشاف" للزمخشري، قال السخاوي: "بنى فيه على كتابة والده، وذلك','1','232',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (404,'من قوله في سورة آل عمران (يستبشرون). شرع فيه حين استقر في التفسير بالبرقوقية، واستمر فيه حتى وصل إلى الأنعام في عدة مجلدات رأيتها بخطه ملقبة بالكشاف على الكشاف" (١).

الأسدي [٢٠٥ - ٢٩٤ هـ / ٨٢٠ - ٩٠٦ م]

<span data-type="title" id=toc-554>صالح بن محمد بن عمرو بن حبيب الأسدي، البغدادي، أبو علي، ويقال: أبو جعفر، الملقب ب "جزرة </span>":
محدث ما وراء النهر، حافظ، مفسر، ولد بالكوفة، وسكن بغداد. روى عن علي بن الجعد وسعدويه الواسطي ورحل إلى الشام ومصر وخراسان، في طلب الحديث. ونزل بخاري سنة ٢٦٦ هـ وليس معه كتاب فروى بها الكثير من حفظه، واستقر فيها إلى أن توفي. قال الذهبي: "كان صاحب نوادر ومزاح، وحدث عنه مسلم خارج صحيحه، وغيره، وهو ثقة
_________
(١) رفع الإصر عن قضاة مصر ٢: ٢٥٦ والذيل على رفع الإصر ١٥٥ - ١٨٤ والضوء اللامع ٣: ٣١٢ وطبقات المفسرين ١: ٢١٤ وحسن المحاضرة ١: ٤٤٤ وهدية العارفين ١: ٤٢٢ والبدر الطالع ١: ٢٨٦ ونظم العقيان ١١٩ وشذرات الذهب ٧: ٣٠٧ وكشف الظنون ٤٤٤ ودائرة المعارف الإسلامية ٤: ١١١ وحوادث الدهور ٣: ٥٧٣ وبروكلمان ٢: ٩٦ والذيل ٢: ١١٤ وفهرس الأزهرية ٢: ٤٣١ والأعلام ٣: ٢٧٩ ومعجم المؤلفين ٥: ٩.','1','232',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (405,'ثبت". ولقب بجزرة لأنه صحف في حديث "كانت لهم خرزة" فقال: "جزرة". له "تفسير القرآن" وغيره (١).

التوقادي [.. ١٣١٧ هـ / .. - ١٨٩٩ م]

<span data-type="title" id=toc-555>صالح محمد الخدائي التوقادي، صلاح الدين، الرومي:</span>
فقيه حنفي، واعظ، مفسر، درس ووعظ بجامع الفاتح بإستنبول من آثاره "أسهل المناهج في تفسير سورة المعارج" (٢).

المقبلي [١٠٤٧ - ١١٠٨ هـ / ١٦٣٧ - ١٦٩٦ م]

<span data-type="title" id=toc-556>صالح بن مهدي بن علي بن عبد الله بن سليمان المقبلي:</span>
من أعيان الفقهاء، له مشاركة في التفسير وعلوم القرآن والحديث واللغة والتصوف والفقه، ولد في قرية "مقبل" من بلاد كوكبان، باليمن. ونشأ في "ثلا" وتعلم فيها وفي شبام وكوكبان. وكان على مذهب الإمام زيد، ولكنه نبذ التقليد، وجرت بينه وبين علماء صنعاء مناظرات، فأدت إلى المنافرة، "لما فيه من الحدة، والتصميم على ما
_________
(١) هدية العارفين ١: ٤٢٢ وشذرات الذهب ٢: ٢١٦ والكامل لابن الأثير ٧: ٥٥٣ وتاريخ بغداد ٩: ٣٢٢ ومرآة الجنان ٢: ٢٢٢ وتهذيب ابن عساكر ٦: ٣٨١ والبداية والنهاية ١١: ١٠٢ وفيه: كان مولده بالرقة". ووفاته في بعض هذه المراجع سنة ٢٩٣ هـ.
(٢) هدية العارفين ١: ٤٢٠ ومعجم المؤلفين ٥: ١٠.','1','233',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (406,'تقتضيه الأدلة، وعدم الالتفات إلى التقليد، فرحل بأهله إلى مكة سنة ١٠٨٠ هـ. قال الشوكاني: "وقد أكثر الحط على المعتزلة، في بعض المسائل الكلامية، وعلى الأشعرية في بعض آخر، وعلى الصوفية في غالب مسائلهم، وعلى المحدثين في بعض غلوهم، ولا يبالي إذا تمسك بالدليل، بمن فيلفه، كائنا من كان". من كتبه "الاتحاف لطلبة الكشاف" انتقد فيه الكشاف للزمخشري، في التفسير، مخطوط (١).

الحيدري [.. - ١١٨٧ هـ / .. - ١٧٧٣ م]

<span data-type="title" id=toc-557>صبغة الله بن إبراهيم بن حيدر الحيدري:</span>
شيخ مشايخ بغداد في عصره. ولد في قرية "ماوران" واستوطن بغداد إلى أن مات فيها بالطاعون. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي. قال صاحب كشف الظنون: "وهي كبرى وصغرى جمعها من ثماني عشرة حاشية" (٢).
_________
(١) البدر الطالع ١: ٢٨٨ وهدية العارفين ١: ٤٢٤ ونشر العرف ١: ٧٨١ والدر الفريد ٣٧ والمجددون في الإسلام ٤١٢ ونبلاء اليمن ١: ٧٨١ وبروكلمان ٢: ٣٦٥ و ٤٤٧ والذيل ٢: ٦٦٦ ومعجم المطبوعات ١٧٧٢.
(٢) أصفى الموارد ٩١ وكشف الظنون ١٨٩ والأعلام ٣: ٢٨٦ ومعجم المؤلفين ٥: ١٦ وحلية البشر وفيه ولادته سنة ١١٦١ هـ ووفاته سنة ألف ومائتين ونيف وعشرين. ومجلة لغة العرب ٣: ٦٣٥.','1','233',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (407,'البروجي [.. - ١٠١٥ هـ / .. - ١٦٠٦ م]

<span data-type="title" id=toc-558>صبغة الله بن روح الله بن جمال الله البروجي الحسيني النقشبندي:</span>
فقيه، متصوف، مفسر، أصله من أصفهان، ولد في مدينة بروج بالهند، وتعلم بها، ثم رحل إلى الحجاز وحج في سنة ١٠٠٥ هـ، وأقام بالمدينة يدرس إلى أن مات. من آثاره "حاشية" على أنوار التنزيل في التفسير للبيضاوي. قال المحبي: وهي حاشية مشهورة في بلاد الروم" (١).

الواسطي [.. - ٥٥٧ هـ / .. - ١١٦٢ م]

<span data-type="title" id=toc-559>صدقة بن الحسين بن أحمد بن محمد بن وزير، أبو الحسن، الواسطي:</span>
عارف بالتفسير والفقه والأدب، وله شعر جيد، من أهل واسط، كان والده من المتقدمين فيها، فترك هو ما كان عليه أهله وطلب العلم وتزهد إلى أن مات (٢).

صفر شاه [.. - حيا ٩١٩ هـ / .. - ١٥١٣ م]

<span data-type="title" id=toc-560>صفر شاه بن ... . الحنفي:</span>
من آثاره "تفسير سورة التكاثر" قال صاحب كشف
_________
(١) خلاصة الأثر ٢: ٢٤٣ وهدية العارفين ١: ٤٢٥ وإيضاح المكنون ١: ٥٣ والأعلام ٣: ٢٨٧.
(٢) طبقات المفسرين للداودي ١: ٢١٥ والمنتظم ١٠: ٢٠٤ والسبكي ٧: ١١٢.','1','234',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (408,'الظنون: فرغ منها سنة ٩١٩ هـ ". وفي هدية العارفين أنه مات سنة ٨٣٤ هـ (١).

صنع الله [.. - ١٠٢١ هـ / .. - ١٦١٢ م]

<span data-type="title" id=toc-561>صنع الله بن جعفر:</span>
شيخ الإسلام ومفتي التخت العثماني في عهد السلطان محمد وولده السلطان أحمد. درس بمدارس استنابول، ثم ولي قضاءها سنة ١٠٠٠ هـ، فقضاء العسكر بأناطولي في نفس السنة، فقضاء الروم إيلي سنة ١٠٠١ هـ. وفي سنة ١٠٠٣ ولي افتاء التخت العثماني. من آثاره "حاشية" على أوائل الكشاف، في التفسير (٢).
_________
(١) كشف الظنون ٤٥٠ وهدية العارفين ١: ٤٢٧.
(٢) كشف الظنون ١٤٨١ وخلاصة الأثر ٢: ٢٥٦.','1','234',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (409,'<span data-type="title" id=toc-562>حرف الضاد</span>
الضبي = ضرار بن عمرو ... نحو ١٩٠ هـ
الضبي = يونس بن حبيب ... ١٨٠ هـ
الضحاك = بن مزاحم ... ١٠٥ هـ
ابن الضريس = محمد بن أيوب ... ٢٩٤ هـ
