INSERT INTO [b13846] VALUES (415,'(ط)

الجزائري [١٢٦٨ - ١٣٣٨ هـ / ١٨٥٢ - ١٩٢٠ م]

<span data-type="title" id=toc-567>طاهر بن صالح بن أحمد بن موهوب، السمعوني الجزائري، ثم الدمشقي:</span>
عالم، لغوي، أديب، باحث، من عمد الإصلاح اللغوي والديني بسورية، كان له تأثير كبير في نشر العلم، ووضع مناهج التعليم وإصلاح أساليبه، كما كان محسنا لأكثر اللغات الشرقية كالعبرية والسريانية والحبشية والزواوية والتركية والفارسية، واسع العلم بالمكتبة العربية ومخطوطاتها. أصله من "وغليس" بالجزائر، هاجر أبوه إلى سورية سنة ١٢٦٤ هـ، وولد هو بدمشق، وبها نشأ وتتلمذ على كبار أشياخها. مارس التعليم زمنا، ثم عين مفتشا للمدارس الجديدة التي أنشئت في عهد مدحت باشا. ساعد على إنشاء "دار الكتب الظاهرية" وجمع فيها ما تفرق من مخطوطات في الخزائن العامة، كما ساعد على','1','241',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (416,'إنشاء "المكتبة الخالدية" بالقدس. وانتقل إلى القاهرة حيث أقام بضع عشرة سنة (١٣٢٥ - ١٣٣٨ هـ) في أثناء الحكم التركي في الشام. وعاد، فانتخب عضوا في "المجمع العلمي العربي" سنة ١٩١٩ وسمي مديرا لدار الكتب الظاهرية. وتوفي بعد ثلاثة أشهر في ١٤ ربيع الثاني. من آثاره "تفسير القرآن" في أربعة مجلدات، و "التبيان لبعض المباحث المتعلقة بالقرآن" (١).
_________
(١) معجم أعلام الجزائر ١٥١ وتراجم أعيان دمشق ١٢٠ والسابقون ١٧ وتنوير البصائر بسيرة الشيخ طاهر. ومنتخبات التواريخ ٢: ٧٣٨ وكنوز الأجداد ٥ ومذكرات محمد كرد علي ٢: ٦٤٣ و ٣: ٧١٩ والأعلام الشرقية ١١٤ ومعجم المطبوعات ٦٨٨ والموسوعة العربية الميسرة ٦٣٠ و ١١٤٨ ومجلة الشرق ١٨: ١٤٤ ومجلة الزهراء٣: ٤٦٣ ومجلة لغة العرب ١: ٢٩٧ مجلة المجمع العلمي العربي ٨: ٥٧٧ ومصادر الدراسة الأدبية ٢: ٢٦٤.','1','241',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (417,'طاووس بن كيسان [٣٣ - ١٠٦ هـ / ٦٥٣ - ٧٢٤ م]

<span data-type="title" id=toc-568>طاووس بن كيسان الخولاني الهمداني، بالولاء، أبو عبد الرحمن:</span>
شيخ أهل اليمن ومفتيهم، تابعي، من أكابر المحدثين، ومن مشاهير مفسري مدرسة التفسير بمكة، أصله من الفرس، ولد ونشأ باليمن، وكان كثير الحج فاتفق موته بمكة، وكان هشام بن عبد الملك حاجا، فصلى عليه (١).

العلثي [.. - ٥٩٣ هـ / .. - ١١٩٧ م]

<span data-type="title" id=toc-569>طلحة بن مظفر بن غانم، تقي الدين، أبو محمد العلثي:</span>
فقيه حنبلي، محدث، مفسر، فرضي، زاهد، من أهل علث (قرية على دجلة بين عكبرا وسامراء). تفقه ببغداد، ولازم أبا الفرج ابن الجوزي، وكان موصوفا بحسن الخط (٢).

طنطاوي جوهري [١٢٨٧ - ١٣٥٨ هـ / ١٨٧٠ - ١٩٤٠ م]

<span data-type="title" id=toc-570>طنطاوي بن جوهري المصري:</span>
باحث، له اشتغال بالتفسير والعلوم الحديثة، ولد في قرية عوض الله حجازي - من قرى مديرية
_________
(١) تهذيب التهذيب ٥: ٨ وتذكرة الحفاظ ٩٠ وتاريخ البخاري ٤: ٣٦٥ والشذرات ١: ١٣٣.
(٢) طبقات المفسرين ١: ٢١٩ والشذرات ٤: ٣١٣ والذيل علي طبقات الحنابلة ١: ٣٩٠.','1','242',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (418,'الشرقية بمصر، وتعلم في الأزهر، وتخرج بدار العلوم، ودرس بها وبغيرها، وتعلم الانجليزية، وألقى محاضرات في الجامعة المصرية، وناصر الحركة الوطنية. توفي بالقاهرة. من كتبه "الجواهر في تفسير القرآن الكريم" طبع، في ٢٦ جزءا نحا فيه منحا خاصا، ابتعد في أكثره عن معنى التفسير وأعرق في سرد أقاصيص وفنون عصرية وأساطير (١).

طه الراوي [١٣٠٧ - ١٣٦٥ هـ / ١٨٩٠ - ١٩٤٦ م]

<span data-type="title" id=toc-571>طه بن صالح الفضيل الراوي:</span>
باحث عراقي، ولد في عنة على الفرات. تتلمذ على محمود شكري الآلوسي، ودرس الحقوق ببغداد، وعين مديرا للمطبوعات، فأستاذا في دار المعلمين العالية، واسندت إليه مديرية المعارف العامة، ورئاسه لجنة التأليف والترجمة والنشر، وانتخب عضوا بالمجمع اللمي العربي بدمشق، وتوفي ببغداد. من آثاره "تفسير بعض آيات القرآن الكريم" مخطوط (٢).
_________
(١) مجلة المجمع العلمي العربي ٢٤: ١٣٦ و ٤٣: ٦٣٤ وكتب وشخصيات ٣٤٦ والدليل العراقي لسنة ١٩٣٦ ص ٨٩٦ ومجلة الرسالة ١٤: ١٢٣٢ ومجلة الحديث ٢٢: ١٨٠ والموسوعة العربية الميسرة ١١٦٤.
(٢) مرآة العصر ٢: ٢٥٥ والإسلام والتجديد في مصر ٢٣٩ واتجاهات التفسير في العصر الحديث ٢٧٢ ومعجم المطبوعات ١٢٤٣ الأعلام الشرقية ٢: ٢١٦ والأعلام ٣: ٣٣٣.','1','242',NULL,NULL,'0');
