فقيه شافعي، أصولي، متكلم،
_________
(١) المجمعيون ١٠٧ والآدب العربي المعاصر ١٣٧ وأعلام الأدب والفن ٢: ١٢٠ وجامع التصانيف الحديثة ٨٥ والموسوعة العربية الميسرة ١٧٢٤ والأعلام الألف ٢: ١٢٥ وعالمنا العربي ٥٨٢ وقدماء ومعاصرون ٢٧٣ ومحاضرات عنه لمحمد أسعد طلس، ومجلة المجمع العلمي العربي ٣١: ٤٩٩ و ٣٦: ٣٣٢ ومجلة مجمع اللغة العربية ١٣: ١٧٧ ومجلة العرفان ٤٣: ١١٢١ و ٤٤: ٤٨٢ و ٤٩٢ ومجلة هدى الإسلام (عمان) س ١ عدد ٤: ٥ و ١٠: ٦٧ ومجلة الضاد (حلب) ٩: ١٧ ومجلة الإصلاح (الأرجنتين) ٥: ٢٤٩ ومجلة الأديب ١٥ ع ٧: ٧٨، ع ٩: ٧٨، ١٦ ع ١١: ٧٧، ع ١٢: ٧٥ وعدد مايو ١٩٦٧ وجريدة الحياة (بيروت) عدد ٣١١٠ تاريخ ٢٢/ ٦/ ١٩٥٦ ومجلة الحكمة (بيروت) ٦: ٢٢ ومجلة الرائد العربي (حماه) مجلد ٢ عدد ٨: ٣ ومجلة المواكب (القامشلي) س ٦ ع ٦: ٢٠ ومجلة المجمع العلمي العراقي ٧: ٣٠١ وجريدة الأهرام ١٤/ ٦/ ٥٦ والأعلام ٤: ٤٧ ومعجم المؤلفين ٥: ٣٠٦ ومجلة الهلال (القاهرة) عدد حزيران ١٩٥٩: ٥٧ ومصادر الدراسة الأدبية ٢: ١٢٦٤. =','1','294',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (513,'مفسر، كان صدر الإسلام في عصره. ولد ونشأ في بغداد، ورحل مع أبيه إلى خراسان فاستقر في نيسابور. ودرس في سبعة عشر علما. ثم فارقها في أيام التركمان وفتنتهم إلى اسفرائين. قال الصابوني: "ومن خراب نيسابور اضطرار مثله إلى مفارقتها". وقال عبد الغافر الفارسي: "كان ذا مال وثروة، أنفقه على أهل العلم والحديث، حتى افتقر". مات بأسفرائين. من كتبه "الناسخ والمنسوخ" مخطوط، و "تفسير القرآن" (١).

الجزجاني [.. - ٤٧١ هـ / .. - ١٠٧٨ م]

<span data-type="title" id=toc-718>عبد القاهر بن عبد الرحمن بن محمد الجرجاني، أبو بكر:</span>
من أئمة اللغة، وواضع أصول البلاغة، متكلم، فقيه، عارف بالتفسير، من أهل جرجان مولدا ووفاة. تقوم شهرته على كتابيه "دلائل
_________
(١) وفيات الأعيان ٣: ٢٠٣ وتبيين كذب المفتري ٢٥٣ وإنباه الرواة ٢: ١٨٥ وطبقات المفسرين للداودي ١: ٣٢٧ وطبقات الشافعية للسبكي ٥: ١٣٦ وبغية الوعاة ٢: ١٠٥ والزركشي ١٩٧ والنسخ في القرآن الكريم ١: ٣٢٨ وطبقات ابن قاضي شهبة ٩٤ ومفتاح السعادة ٢: ٣٢٥ ومرآة الجنان ٣: ٥٢ والبداية والنهاية ١٢: ٤٤ وفوات الوفيات ٢: ٣٧٠ وهدية العارفين ١: ٦٠٦ والأسنوي ١: ١٩٤ وطبقات الشافعية لابن هداية ١٣٩.','1','295',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (514,'الإعجاز" طبع، و "أسرار البلاغة" طبع، الأول في علم المعاني، والثاني في علم البيان. من كتبه الأخرى "إعجاز القرآن" طبع، و "تفسير الفاتحة" في مجلد. وذكر له صاحب هدية العارفين كتاب "درج الدرر في تفسير الآي والسور" قال صاحب كشف الظنون: "مختصر، له ظنا" (١).

السهروردي [٤٩٠ - ٥٦٣ هـ / ١٠٩٧ - ١١٦٨ م]

<span data-type="title" id=toc-719>عبد القاهر بن عبد الله بن محمد بن عمويه بن سعد السهروردي، القرشي، الصديقي، البكري، أبو النجيب:</span>
فقيه شافعي، واعظ، من أئمة المتصوفين. ولد بسهرورد، وسكن بغداد، وولي المدرسة النظامية، ودخل دمشق سنة ٥٥٧ هـ، فأكرمه الملك العادل. وعاد إلى بغداد فتولى بها، قال ابن قاضي شهبة: "كانت له
_________
(١) هدية العارفين ١: ٦٠٦ وإنباه الرواة ٢: ١٨٨ ونزهة الألباء ٣٦٣ وبغية الوعاة ٢: ١٠٦ وطبقات الداودي ١: ٣٣٠ وطبقات النحاة لابن قاضي شهبة ٢: ٩٤ ومفتع السعادة ١: ١٧٧ وفوات الوفيات ٢: ٣٦٩ ومرآة الجنان ٣: ١٠١ والنجوم الزاهرة ٥: ١٠٨ والشذرات ٣: ٣٤٠ وطبقات السبكي ٥: ١٤٩ والاسنوي ٢: ٤٩١ والزركشي ١٩٧ وكنوز الأجداد ٢٦٠ وكشف الظنون ٧٤٥.','1','295',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (515,'محافيظ جيدة في التفسير وفي الفقه وأصوله وأصول الدين" (١).

الغافقي [٥٣٦ - ٦١٧ هـ / ١١٤٢ - ١٢٢٠ م]

<span data-type="title" id=toc-720>عبد الكبير بن محمد بن عيسى بن محمد بن بقي الغافقي، أبو محمد:</span>
من أعيان فقهاء الأندلس، محدث، حافظ، مفسر، قاض، من أهل مرسية، سكن إشبيلية، وولي قضاء رنده، ونيابة القضاء بقرطبة. وحدث وروى عنه جماعة. أثنى عليه ابن الأبار. له "تفسير القرآن" جمع فيه بين تفسير ابن عطية، وتفسير الزمخشري (٢).

الجيلي [٧٦٧ - ٨٣٢ هـ / ١٣٦٥ - ١٤٢٨ م]

<span data-type="title" id=toc-721>عبد الكريم بن إبراهيم بن عبد الكريم الجيلي، القادري، قطب الدين، ابن سبط الشيخ عبد القادر الجيلاني:</span>
من علماء المتصوفين. من كتبه "تفسير القرآن" و "الكهف والرقيم في شرح بسم الله الرحمن
_________
(١) العبر ٤: ١٨١ والشذرات ٤: ٢٠٨ والسبكي ٤: ٢٥٦ ووفيات الأعيان ٣: ٢٠٤ ومرآة الجنان ٣: ٣٧٢ وهدية العارفين ١: ٦٠٦ واللباب ٢: ١٥٧ والأعلام ٣١١ ومعجم المؤلفين ٥: ٣١١.
(٢) طبقات المفسرين للسيوطي ٢٠ وطبقات المفسرين للداودي ١: ٣٣١ والصلة ٤٤ والتكملة ٦٥٤.','1','296',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (516,'الرحيم" طبع، و "الغايات في معرفة معاني الآيات والأحاديث المتثابهات" (١).

الكوراني [.. - ١٠٥٠ هـ / .. - ١٦٤٠ م]

<span data-type="title" id=toc-722>عبد الكريم بن أبي بكر بن هداية الله الحسينى المريواني، الكوراني، نزيل المدينة المنورة:</span>
مفسر، واعظ، شافعي المذهب، وهو ابن صاحب "طبقات الشافعية". له "تفسير القرآن الكريم" وصل فيه إلى سورة النحل، في ثلاث مجلدات (٢).

التككي [٤٥٧ - ٥٢٥ هـ / ١٠٦٥ - ١١٣١ م]

<span data-type="title" id=toc-723>عبد الكريم بن الحسن بن المحسن بن سوار، أبو علي المصرى التككي:</span>
مقرىء مشهور، مفسر، كانت له حلقة إقراء بمصر. روى عنه السلفي. والتككي نسبة إلى التكك، وهي جمع تكة. قال صاحب
_________
(١) معجم المطبوعات ٧٢٨ وهدية العارفين ١: ٦١٠ وكشف الظنون ١٥٢٥ ومعجم المؤلفين ٥: ٣١٣ والأعلام ٤: ١٧٥.
(٢) خلاصة الأثر ٢: ٤٧٤ وهدية العارفين ١: ٦١٢ وإيضاح المكنون ١: ٣٠٨، ومقدمة طبقات الشافعية لابن هداية مقدمة المحقق.','1','296',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (517,'طبقات المفسرين. كان عارفا بالقراءات والتفسير ..) (١).

التبريزي [٧٥٧ - حيا ٨٣١ هـ / ١٣٥٦ - حيا ١٤٢٨ م]

<span data-type="title" id=toc-724>عبد الكريم بن عبد الجبار بن إبراهيم بن كرشان التبريزي:</span>
مفسر، من آثاره "المحاكمات" حاشية على الكشاف أوله١: "الحمد لله الذي أخرج العباد من ظلمة العدم إلى نور الوجود ... الخ" ذكر فيها أن "شرح الكشاف" لقطب الدين الرازي، كتاب جليل الشأن، لكن جمال الدين الأقسرائي اعترض عليه اعتراضات، فكتب هو الأجوبة وسماها "المحاكمات". وله أيضا "حاشية" علي الكشاف وصل فيها إلى آخر الزهراوين، وأشار فيها إلى أجوبته عن اعتراضات الأقسرائي والقطب الرازي، أوله١: "الحمد لله المنعم المبدع المنان .. " فرغ منها في جمادي الآخرة سنة ٨٢٥ هـ .. " "وقال ابن فهد في معجم أبيه، أنه ذكر في ذي الحجة سنة إحدى وثلاثين وثمانمائة أنه
_________
(١) طبقات المفسرين للسيوطي ٢١ وطبقات المفسرين للداودي ١: ٣٣٢ وطبقات القراء للذهبي ١: ٣٨٩ وغاية النهاية ١: ٤٠٠ وطبقات المفسرين للأدنهوي ٢٩ ب (مخطوط) وحسن المحاضرة ١: ٤٩٥.','1','297',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (518,'(صاحب الترجمة) ابن أربع وسبعين سنة، قال: وله "تفسير، قرأت عليه منه" (١).

الرومي [.. - ٨٧٤ هـ / .. - ١٤٦٩ م]

<span data-type="title" id=toc-725>عبد الكريم بن عبد الله الرومي:</span>
فقيه حنفي، قاض، له اشتغال بالتفسير. من موالي أحد أمراء السلطان بايزيد خان. ولي قضاء العسكر بالروم إيلي. من آثاره "حاشية" على شرح السعد للكشاف في التفسير (٢).

أبو معشر القطان [.. - ٤٧٨ هـ / .. - ١٠٨٥ م]

<span data-type="title" id=toc-726>عبد الكريم بن عبد الصمد بن محمد بن علي القطان الطبري، أبو معشر:</span>
عالم بالقراءات، مؤرخ لرجالها، مفسر، من فقهاء الشافعية، كان مقرىء أهل مكة. سمع ببغداد وحران وتنيس ومكة وغيرها. وروى كتاب "تفسير النقاش، عن شيخه علي بن محمد الزيدي بحران، و "تفسير الثعلبي" عن مؤلفه. قال ابن الجزري: "إمام عارف محقق، أستاذ كامل، ثقة، صالح،. توفي بمكة. من كتبه (الدرر"
_________
(١) الضوء اللامع ٤: ٣١٠ وكشف الظنون ١٤٧٨ و ١٤٧٩.
(٢) هدية العارفين ١: ٦١٠.','1','297',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (519,'في تفسير القرآن، و "عيون المسائل" في التفسير أيضا (١).

الروضي [١٢٢٤ - ١٣٠٩ هـ / ١٨١٠ - ١٨٩١ م]

<span data-type="title" id=toc-727>عبد الكريم بن عبد الله بن محمد، أبو طالب الحسني اليمني الروضي، من نسل المنصور بالله القاسم بن محمد:</span>
محدث زيدي، مفسر، مولده ووفاته في مدينة الروضة من أعمال صنعاء، باليمن. من كتبه "التحفة، في تفسير القرآن" أربع مجلدات (٢).

ابن بنت العراقي [٦٢٣ - ٧٠٤ هـ / ١٢٢٦ - ١٣٠٤ م]

<span data-type="title" id=toc-728>عبد الكريم بن علي بن عمر الأنصاري، علم الدين ابن بنت العراقي:</span>
_________
(١) طبقات المفسرين للداودي ١: ٣٣٢ وميزان الاعتدال ٢: ٦٤٤ ولسان الميزان ٤: ٤٩ وطبقات الشافعية للسبكي ٥: ١٥٢ وشذرات الذهب ٣: ٣٥٨ وغاية النهاية ١: ٤٠١ والعبر ٣: ٢٩٠ ومرآة الجنان ٣: ١٢٢ وطبقات القراء للذهبي ١: ٣٥١ وهدية العارفين ١: ٦٥٨ ومجلة معهد المخطوطات ٤: ١٧ وكشف الظنون ٧٥٢ و ١٢٨٧ ومعجم المؤلفين ٥:
(٢) الروض النضير ٦٣ وأئمة اليمن، سيرة المنصور ٨٩.','1','298',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (520,'فقيه شافعي، مفسر، ضرير. أصله من وادي آش بالأندلس، قدم أبوه إلى مصر، فولد هو بها، وقيل له العراقي نسبة إلى جده لأمه، وهو العراقي شارح "المهذهب". أخذ الفقه عن ابن عبد السلام، والحديث عن المنذري. وتصدر بجامع مصر، ودرس بمشهد الحسين، ودرس الفقه بالقبة المنصورية وغيرها. مات بالقاهرة. له تصانيف، منها في التفسير "الإنصاف في مسائل الخلاف بين الزمخشري وابن المنير، مخطوط نبه فيه على مواضع الاعتزال في "الكشاف". ومختصر في"تفسير القرآن" قال فيه الصفدي. احتوى على فوائد (١).

الرافعي [٥٥٧ - ٦٢٣ هـ / ١١٦٢ - ١٢٢٦ م]

<span data-type="title" id=toc-729>عبد الكريم بن محمد بن عبد الكريم بن الفضل، أمام الدين، أبو القاسم الرافعي القزويني:</span>
من كبار فقهاء الشافعية، مفسر، محدث، مؤرخ، كان له مجلس بقزوين للتفسير ولتسميع الحديث. نسبته
_________
(١) الدرر الكامنة ٣: ١٣ وطبقات المفسرين للداودي ١: ٣٣٤ وحسن المحاضرة ١: ٤٢١ والسبكي ٦: ١٢٩ وطبقات الشافعية للأسنوي ١٩٤ ونكت الهميان ١٩٥ ومفتاح السعادة ٢: ٢٢١ وهدية العارفين ١: ٦١٠ وكشف الظنون ٤٥٣.','1','298',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (521,'إلى رافع بن خديج الصحابي مات بقزوين. من كتبه "الأمالي الشارحة لمفردات الفاتحة" وهو ثلاثون مجلسا، أملاها أحاديث بأسانيد عن أشياخه على سورة الفاتحة، وتكلم عليها (١).

السمعاني [٥٠٦ - ٥٦٢ هـ / ١١١٣ - ١١٦٧ م]

<span data-type="title" id=toc-730>عبد الكريم بن محمد بن منصور بن محمد التميمي السمعاني المروزي، تاج الدين، أبو سعد:</span>
محدث حافظ، مؤرخ، نسابة، رحالة. ولد بمرو، ورحل إلى بلاد الجبال والعراق والحجاز والجزيرة والشام والري وأصبهان وهمذان وسائر بلاد خراسان، ولقي العلماء والمحدثين، وأخذ عنهم وأخذوا عنه. توفي بمرو. له كتب كثيرة منها "تبيين معادن المعاني، لمن إلى تبيينها دعاني - خ" قال حاجي خليفة: مختصر في معاني القرآن الكريم على مقدمة ومقاصد وخاتمة، أوله:
_________
(١) طبقات المفسربن للداودي ١: ٣٣٥ وتهذيبب الأسماء واللغات ٢: ٢٦٤ ومفتاح السعادة ٢: ١١٤ و ٣٥٤ والنجوم الزاهرة ٦: ٢٦٦ ومرآة الجنان ٤: ٥٦ وطبقات السبكي ٨: ٢٨١ وشذرات الذهب ٥: ١٠٨ وطبقات الشافعية للأسنوي ١٠٨ والعبر ٥: ٩٤ وفوات الوفيات ٢: ٣٧٦ وطبقات المفسرين للسيوطي ٢١ وطبقات ابن هداية من تحقيقنا ٢١٨ وهدية العارفين ١: ٦٠٩.','1','299',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (522,'"الحمد لله مبشر من صدق بالحسنى .. " (١).

الموصلي [٦٣٢ - ؟ هـ / ١٢٣٥ - ؟ م]

<span data-type="title" id=toc-731>عبد الكريم بن محمود بن مودود بن محمود بن بلدجي الموصلي، أبو الفضل:</span>
فقيه حنفي، عالم بالتفسير. من أهل الموصل. ذكره القرشي في طبقاته ولم يذكر تاريخ وفاته (٢).

القشيري [٣٧٦ - ٤٦٥ هـ / ٩٨٦ - ١٠٧٣ م]

<span data-type="title" id=toc-732>عبد الكريم بن هوازن بن عبد الملك بن طلحة بن محمد النيسابوري القشيري، أبو القاسم، زين الإسلام:</span>
عالم بالفقه والتفسير والحديث والأصول والأدب والشعر والكتابة وعلم التصوف، تعلم بنيسابور، وأصله من ناحية "أستوا" من العرب الذين
_________
(١) وفيات الأعيان ٣: ٢٠٩ والعبر ٤: ١٧٨ والشذرات ٤: ٢٠٥ وتذكرة الحفاظ ١٣١٦ وطبقات السبكي ٥: ١٥٧ والنجوم الزاهرة ٥: ٥٦٣ واللباب ١: ٩ وهدية العارفين ١: ٦٠٨ والمنتظم ١٠: ٢٢٥ وروضات الجنات ٤٤٦ وكشف الظنون ٣٤٢.
(٢) طبقات المفسرين للداودي ١: ٣٣٨ والجواهر المضية ١: ٣٢٧ والطبقات السنية ١٢٩٢ وطبقات المفسرين للأدنهوي ٥٢ ب.','1','299',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (523,'قدموا خراسان. وحج في رفقة فيها جماعة من المشاهير، فسمع معهم الحديث ببغداد والحجاز. وأخرج من نيسابور في عهد السلطان السلجوقي طغرل بك بن ميكائيل، فدخل بغداد، وأكرمه الخليفة العباسي القائم بأمر الله. قال الخطيب البغدادي: "قدم بغداد سنة ٤٤٨ هـ، وحدث بها، وكتبنا عنه، وكان يعرف الأصول على مذهب الأشعري، والفروع على مذهب الشافعي". وعاد إلى نيسابور فتوفي بها. قال الدكتور إبراهيم بسيوني: "ما زال قبره قائما حتى وقتنا الحاضر يزوره الناس للتبرك". من آثاره "التيسير في التفسير" وهو تفسير كبير، انتهى منه قبل عام ٤١٠ هـ. قال صاحب كشف الظنون: "وهو من أجود التفاسير". و "لطائف الإشارات" وهو تفسير صوفي كامل للقرآن الكريم، فرغ منه سنة ٤٣٤ هـ، طبع في ستة أجزاء، حققه وقدم له الدكتور إبراهيم بسيوني (١)
_________
(١) طبقات المفسرين للسيوطي ٢١ وطبقات المفسرين للداودي ١: ٣٣٨ وطبقات الشافعية ٥: ١٥٣ والكامل في التاريخ ١٠: ٨٨ ولطائف الإشارات (مقدمة الدكتور بسيوني) ١: ١٥ - ٤٩ وتبيين كذب المفتري ٢٧١ وتاريخ بغداد ١١: ٨٣ والعبر ٣: ٢٥٩ والنجوم الزاهرة ٥: ٩١ والمنتظم ٨: ٢٨٠ وإنباء الرواة ٢: ١٩٣ وكشف الظنون ٥٢٠ و ١٥٥١ ومفتاح السعادة ٢: ١٠٧ وشذرات الذهب ٣: ٣١٩ ووفيات الأعيان ٣: ٢٠٥ وهدية العارفين ١: ٦٠٣ ودمية القصر ٢: ٩٩٣ =','1','300',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (524,'الوارداري [.. - ١٠٠٣ هـ / .. - ١٥٩٥ م]

<span data-type="title" id=toc-733>عبد الكريم الوارداري الرومي:</span>
فقيه شافعي، مفسر، تركي مستعرب، قدم دمشق صحبة سنان باشا، بعد عزل هذا الأخير عن الصدارة العظمى في ٢٠ ذي الحجة سنة ٩٩٠ هـ، وتعيينه واليا على دمشق. وولي صاحب الترجمة إفتاء الحنفية بالشام، والتدريس بالسليمانية بدمشق. وحج منها ثم عاد إليها. وعزل عن فتوى الشام، فرحل إلى القسطنطينية، واشتغل بالتدريس إلى أن مات. له "فصل الخطاب في تفسير أم الكتاب" مخطوط (١).

ابن ولي الدين [.. - حوالي ١١٠٠ هـ / .. - حوالي ١٦٨٩ م]

<span data-type="title" id=toc-734>عبد الكريم بن ولي الدين بن يوسف بن ولي الدين:</span>
محدث، فقيه مفسر، مشارك في بعض العلوم، من كتبه "تفسير سورة يوسف" (٢).
_________
= رقم ٧١ واللباب ٣: ٣٨ مادة "قشيري" والمختصر في أخبار البشر ٢: ١٩٩ والبداية والنهاية ١٢: ١٠٧ وروضات الجنات ٤٤٤ ومعجم المؤلفين ٦: ٦ والأعلام ٤: ٥٧ وبروكلمان ١: ٤٣٢ والذيل ١: ٧٧٠.
(١) خلاصة الأثر ٣: ١٣ ومعجم المؤلفين ٦: ٧ والتيمورية ١: ١٠٣ و ٢٢٣ و ٣/ ٣١٣.
(٢) هدية العارفين ١: ٦١٣ ومعجم المؤلفين ٦: ٧.','1','300',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (525,'الفاسي [٧٧٨ - ٨٤٣ هـ / ١٣٧٦ - ١٤٣٩ م]

<span data-type="title" id=toc-735>عبد اللطيف بن أحمد بن علي بن محمد ابن محمد بن عبد الرحمن الحسني الفاسي المكي، أبو الثناء، نجم الدين:</span>
فقيه شافعي، أصولي، مفسر، مشارك في بعض العلوم، أصله من فاس، ولد بمكة، سمع بها وبالمدينة والقاهرة واليمن، ودخل تونس وحدث بها. وعاد إلى القاهرة واستوطنهما إلى أن توفي بها شهيدا بالطاعون (١).

ابن سلطان العلماء [.. - ٦٩٧ هـ / .. - ١٢٩٨ م]

<span data-type="title" id=toc-736>عبد اللطيف بن عبد العزيز بن عبد السلام بن أبي القاسم بن الحسن السلمي:</span>
فقيه شافعي، مفسر، أصله من دمشق، إستوطن أبوه (الملقب بسلطان العلماء) القاهرة، فنشأ هو بها. من آثاره "تفسير القرآن" (٢).
_________
(١) طبقات المفسرين للداودي ١: ٣٤٦ والضوء اللامع ٤: ٣٢٢.
(٢) هدية العارفين ١: ٦١٦ وكشف الظنون ٤٥٣ ومعجم المؤلفين ٦: ١١.','1','301',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (526,'العامري [٦٤٩ - ٧١٠ هـ / ١٢٥١١٣١٠ م]

<span data-type="title" id=toc-737>عبد اللطيف بن محمد بن الحسين بن رزين بن عيسى بن موسى العامري، الحموي، المصري، بدر الدين، أبو البركات:</span>
فقيه شافعي، قاض، مفسر، ولد بدمشق، وتعلم بها وبالقاهرة، وناب في الحكم بقليوب، وولي قضاء العسكر أكثر من ثلاثين سنة، ودرس بالظاهرية وغيرها، وخطب بالجامع الأزهر. قال ابن كثير: درس الحديث والتفسير والفقه وأصوله .. الخ. ومات بالقاهرة (١).

المحبي [٩٦٦ - ١٠٢٣ هـ / ١٥٥٩ - ١٦١٤ م]

<span data-type="title" id=toc-738>عبد اللطيف بن محمد بن أبي بكر المحبي:</span>
فقيه، قاض، مفسر، من أهل دمشق، أقام مدة في إستنبول، وجاور بمكة، وولي قضاء حماة (بسورية) ودرس بالظاهرية وغيرها. ومات بدمشق. له "تفسير سورة الفتح" (٢).
_________
(١) الدرر الكامنة ٣: ٢٣ والشذرات ٦: ٢٦ والبداية والنهاية.
(٢) خلاصة الأثر ٣: ١٩ وهدية العارفين ١: ٦١٧ وإيضاح المكنون ١: ٣٠٧.','1','301',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (527,'غزي زاده [.. - ١٢٤٧ هـ .. - ١٨٣١ م]

<span data-type="title" id=toc-739>عبد اللطيف بن محمد بن أحمد البرسوي، المعروف بابن الغزي، وبغزي زاده:</span>
فقيه حنفي، صوفي، مفسر، متأدب، من أهل بروسة. من تصانيفه الكثيرة "زبدة البيان في تفسير بعض سور القرآن" (١).

عبد اللطيف البغدادي [٥٥٧ - ٦٢٩ هـ / ١١٦٢ - ١٢٣١ م]

<span data-type="title" id=toc-740>عبد اللطيف بن يوسف بن محمد بن علي البغدادي، موفق الدين، ويعرف بابن اللباد، وبابن نقطة:</span>
عالم اشتهر بعلم الكلام والفلسفة والطب وغيرها. مولده ووفاته ببغداد. زار حلب ودمشق وحران والقدس وبلاد الروم وملطية والحجاز ومصر، وأقام عشرة أعوام بالقاهرة في بلاط السلطان صلاح الدين الأيوب، وحظي عند الملوك والأمراه. وترجم له القفطي في "إنباه الرواة" فأزرى به وتحامل عليه، وقد تداركه محقق "الإنباه" الأستاذ محمد أبو الفضل إبراهيم فأنصفه بما علق به، فقال بعد أن أورد قول ابن النجار في البغدادي:
_________
(١) هدية العارفين ١: ٦١٨ والمستدرك على الكشاف ٨٢ ومعجم المؤلفين ٦: ١٤.','1','302',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (528,'"وظهر به تحامل القفطي عليه بما ذكره، وهذه عادته في هضم العصريين وحط مراتبهم وإيهام أنه عارف بمنازل العلماء وتمييز طبقاتهم، ولم يكن هناك ولا قريبا. عفا الله عنه، ولقد عرفه من نال منه ... ". من آثاره "الواضحة في إعراب الفاتحة" قال صاحب كشف الظنون: "نحو عشرين كراسة. و "رسالة" في قوله تعالى: {إذا أخرج يده لم يكد يراها ..} (سورة النور - ٤٠) و "الرد على فخر الدين الرازي في تفسير سورة الإخلاص" (١).

الجتهجي [١١١٥ - ١١٧٤ هـ / ١٧٠٣ - ١٧٦١ م]

<span data-type="title" id=toc-741>عبد الله بن إبراهيم الحسيني الجرمكي، المعروف بالجته جي:</span>
من وزراء الدولة العثمانية، ولد في "جرمك" من أعمال ديار بكر، وولي طرابلس وحلب ودمشق
_________
(١) إنباه الرواة ٢: ١٩٣ وفوات الوفيات ٢: ٣٨٥ وطبقات الشافعية للسبكي ٥: ١٣٢ والشذرات ٥: ١٣٢ وبغية الوعاة ٢: ١٠٦ والعبر ٥: ١١٥ وعيون الأنباء ٦٨٣ وهدية العارفين ١: ٦١٤ والأعلام ٤: ١٨٣ وحسن المحاضرة ١: ٥٤١ وتاريخ آداب اللغة العربية ٣: ٩٨ والترجمة الشخصية لشوقي ضيف ٣٢ وكنوز الأحداد ٣٢٥ ومرآة الجنان ٤: ٦٨ وطبقات ابن قاضي شهبة ١: ٩٨ وخطط مبارك ١٥: ٧٩ وخزائن الكتب ٨٩ وطبقات الأسنوي ١: ٢٧٣ ومعجم المؤلفين ٦: ١٥ والموسوعة العربية الميسرة ٣٨٤ وكشف الظنون ١٩٩٦.','1','302',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (529,'وديار بكر، وتوفي بها. من كتبه "أنهار الجنان في ينابيع آيات القرآن" طبع، في أوليات الآيات القرآنية (١).

الكعبي [.. - ٣١٩ هـ / .. - ٩٣١ م]

<span data-type="title" id=toc-742>عبد الله بن أحمد بن عمود الكعبي، من بني كعب، البلخي الخراساني، أبو القاسم:</span>
من أئمة المعتزلة، تنسب إليه الطائفة "الكعبية". من أهل بلخ. أقام ببغداد مدة طويلة، واشتهرت بها كتبه، ثم عاد إلى بلخ وتوفي بها. قال الذهبي: "وهو صاحب مقالات، وله اختيارات في علم الكلام" من كتبه "تفسير القرآن" قال حاجي خليفة في وصفه: "وهو كبير في اثني عشر مجلدا لم يسبق إليه": وقال آغا بزرك: "كان هذا التفسير عند السيد ابن طاووس وينقل عنه إلى الجزء الثاني والثلاثين منه في "سعد السعود" الذي ألفه في ٦٥١ هـ (٢).
_________
(١) سلك الدرر ٣: ٨١ وهدية العارفين ١: ٤٨٣ وإيضاح المكنون ١: ١٤٨ ومعجم المؤلفين ٦: ١٧.
(٢) طبقات المفسرين للداودي ١: ٢٢٢ وتاريخ بغداد ٩: ٣٨٤ الجواهر المضيئة ١: ٢٧١ وابن النديم ٣٤ وهدية العارفين ١: ٤٤٤ ولسان الميزان ٣: ٢٥٥ والمنتظم ٦: ٢٣٨ وتاج التراجم ٣١ وشذرات الذهب ٢: ٢٨١ وكشف الظنون ٤٤١ وخطط المقريزي ٢: ٣٤٨ والملل والنحل ١: ٧٦ والعبر ٢: ١٧٦ وطبقات =','1','303',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (530,'ابن المغلس [.. - ٣٢٤ هـ / .. - ٩٣٦ م]

<span data-type="title" id=toc-743>عبد الله بن أحمد بن محمد بن المغلس، أبو الحسن:</span>
فقيه ظاهري، انتهت إليه رياسة الظاهريين في وقته. من أهل بغداد. قال في "طبقات المفسرين": "لم ير مثله فيما بعد، وكان فاضلا عالما نبيلا صادقا ثقة، مقدما عند جميع الناس، يقصده العالم من سائر البلدان". توفي ببغداد. من كتبه "أحكام القرآن" (١).

ابن الخشاب [٤٩٢ - ٥٦٧ هـ / ١٠٩٩ - ١١٧٢ م]

<span data-type="title" id=toc-744>عبد الله بن أحمد بن أحمد بن أحمد ابن عبد الله بن نصر، المعروف بابن الخشاب، أبو محمد:</span>
عالم في النحو والأدب والتفسير والحديث والنسب والفرائض والحساب، مطلع على شيء من الفلسفة والهندسة. من أهل بغداد مولدا ووفاة. قال السيوطي: "ما من علم من العلوم إلا وكانت له فيه يد حسنة" وقال ابن النجار:
_________
= المعتزلة ٨٨ والفصل ٤: ٢٠٣ والفرق بين الفرق ١٦٥ ووفيات الأعيان ٣: ٤٥.
(١) طبقات المفسرين ١: ٢٢١ وتاريخ بغداد ٩: ٣٨٥ وتذكرة الحفاظ ٨٢١ والعبر ٢: ٢٠١ والشذرات ٢: ٣٠٢ وابن النديم ٢١٨ والمنتظم ٦: ٢٨٦ والنجوم الزاهرة ٣: ٢٥٩ والشيرازي ١٥٠.','1','303',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (531,'"لم يمت أحد من أهل العلم وأصحاب الحديث إلا وكان يشتري كتبه كلها، فحصلت أصول المشايخ عنده، وذكر عنه أنه وقف كتبه على أهل العلم قبيل وفاته. وقيل أنه كان مستهترا في حياته، مبتذلا في لباسه ومطعمه ومشربه، ظريفا مزاحا. يلعب الشطرنج مع العوام على قارعة الطريق، ويمازح السفهاء، ويتعمم بالعمامة حتى تتسخ أطرافها من عرقه فتسود وتتقطع" وقد خلط بعض مترجميه بينه وبين محمد بن أحمد (ابن الخشاب) المتوفي سنة ٦٥٠ هـ (انظر ترجمته) (١).

ابن قدامة [٥٤١ - ٦٢٠ هـ / ١١٤٦ - ١٢٢٣ م]

<span data-type="title" id=toc-745>عبد الله بن أحمد بن محمد بن قدامة الجماعيلي المقدسي، ثم الدمشقي الصالحي، أبو محمد، موفق الدين:</span>
عالم بالحديث والتفسير والأصول والنحو والفرائض، من أكابر الحنابلة. ولد في جماعيل - من قرى نابلس بفلسطين - وسمع بدمشق وبغداد والموصل ومكة المكرمة.
_________
(١) بغية الوعاة ٢: ٢٩ وإنباه الرواة ٢: ٩٩ ووفيات الأعيان ٣: ١٠٢ ومرآة الجنان ٣: ٣٨١ والنجوم الزاهرة ٦: ٦٥ ومعجم الأدباء ١٢: ٤٧ وذيل طبقات الحنابلة ١: ٣١٦ والمنتظم ١: ٢٣٨ وهدية العارفين ١: ٤٥٦ وروضات الجنات ٤٥١ والأعلام ٤: ١٩١ والشذرات ٤: ٢٢٠.','1','304',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (532,'وأقام ببغداد مدة، وعاد إلى دمشق فتوفي بها. قال الضياء المقدسي: "كان إماما في القرآن وتفسيره، إماما في علم الحديث ومشكلاته، إماما في علم الخلاف وأصول الفقه والنحو والحساب والنجوم السيارة والمنازل .. ". له تصانيف منها "البرهان في مسائل القرآن" منه مخطوطة باسم "البرهان في بيان حقيقة القرآن" (١).

النسفي [.. - ٧١٠ هـ / .. - ١٣١٠ م]

<span data-type="title" id=toc-746>عبد الله بن أحمد بن محمود النسفي، إبو البركات، حافظ الدين:</span>
مفسر، متكلم، أصولي، من فقهاء الحنفية. من أهل إيذج - كورة وبلدة بين خوزستان وأصبهان - ونسبته إلى "نسف" من بلاد السند، بين جيحون وسمرقند، تتلمذ لشيوخ كثيرين، ورحل إلى بغداد. قال في "الفوائد البهية": "كان إماما عديم النظير في زمانه، رأسا في الفقه والأصول، بارعا في الحديث ومعانيه ... ". من كتبه "مدارك التنزيل وحقائق التأويل" في التفسير،
_________
(١) ذيل طبقات الحنابلة ٢: ١٣٣ ومرآة الزمان ٨: ٦٢٧ والذيل على الروضتين ١٣٩ ومرآة الجنان ٤: ٤٧ وفوات الوفيات ٢: ١٠٨ والعبر ٥: ٧٩ والبداية والنهاية ١٣: ٩٩ والأعلام ٤: ١٩١ ومعجم المؤلفين ٦: ٣٠ والنجوم الزاهرة ٦: ٢٥٦ والشذرات ٥: ٨٨ وهدية العارفين ١: ٤٠٩ ومعجم البلدان مادة جماعيل.','1','304',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (533,'طبع، "وهو كتاب وسط يعرض لوجوه الإعراب والقراءات ولشيء من البيان والبديع، معولا فيه على آراء أهل السنة والجماعة" (١). اليافعي ٦٩٨ - ٧٦٨ هـ / ١٢٩٨ - ١٣٦٧ م]

<span data-type="title" id=toc-747>عبد الله بن أسعد بن علي اليافعي، عفيف الدين:</span>
مؤرخ، باحث، متصوف، من فقهاء الشافعية. من أهل عدن، ونسبته إلى يافع من حمير. حج سنة ٧١٢ هـ، وعاد إلى اليمن. ثم رجع إلى مكة (سنة ٧١٨ هـ) وجماور إلى أن توفي. من تصانيفه الكثيرة "الأنوار اللائحة في أسرار الفاتحة" (٢)
_________
(١) تاج التراجم ٢١ وتاريخ علماء بغداد ٦٥ والجواهر المضية ١: ٢٧٠ ومباحث في علوم القرآن ٢٩٠ ومفتاح السعادة ٢: ٥٧ واالتفسير والمفسرون للذهبي، والفوائد البهية ١٠١ وتاريخ التفسير للقيسي ١٤١ وهدية العارفين ١: ٤٦٤ ودراسات في التفسير ورجالله ١١٤ والموسوعة العربية الميسرة ١٨٣٣ والدرر الكامنة ٢: ٢٥٣ والأعلام ٤: ١٩٢ ومعجم المؤلفين ٦: ٣٢ وكشف الظنون ١٦٤٠ وفهرس المخطوطات المصورة ١: ٢٧٦ وعمدة عقيدة أهل السنة والجماعة، مقدته. وفي تاريخ وفاته خلاف، قيل سنة ٧٠١ وقيل: بعد ٧١٠ هـ.
(٢) البدر الطالع ١: ٣٧٨ وطبقات السبكي ٦: ١٠٣ وفيه: وفاته سنة ٧٦٧ ومثله في مفتاح السعادة ١: ٢١٧ والفوائد البهية ٣٣ والنجوم الزاهرة ١١: ٩٣ =','1','305',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (534,'العيدروس [٨١١ - ٨٦٥ هـ / ٤٠٨ - ١٤٦١ م]

<span data-type="title" id=toc-748>عبد الله بن أبي بكر بن عبد الرحمن السقاف، المعروف بالعيدروس:</span>
متصوف، عارف بالتفسير والفقه والحديث، من أهل تريم (بحضرموت) مولدا ووفاة وكان نقيب العلويين فيها. أخباره كثيرة. قال في المشرع الروي: "برع في التفسير والحديث والفقه والنحو واللغة والهيئة وفنون التصوف، وانتهت إليه الرئاسة الدينية والإصلاحية بحضرموت كلها (١).

درستويه [٢٥٨ - ٣٤٧ هـ / ٨٧١ - ٩٥٨ م]

<span data-type="title" id=toc-749>عبد الله بن جعفر بن درستويه بن المرزبان، أبو محمد:</span>
عالم باللغة، فارسي الأصل، قدم من مدينة "فسا" في صباه إلى بغداد واستوطنها واستقر بها. أخذ عن ابن قتيبة والمبرد، وكان شديد الانتصار لمذهب البصريين في اللغة والنحو. مات ببغداد.
_________
= وهدية العارفين ١: ٤٦٥ ومعجم المطبوعات ١٩٥٢ وروضات الجنات ٤٥٧ والدرر الكامنة ٢: ٣٥٢ وشذرات الذهب ٦: ٢١٠ والأعلام ٤: ١٩٨.
(١) تاريخ حضرموت ٢: ٧٧٥ وفيه أسماء المراجع المخطوطة التي ترجمت له.','1','305',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (535,'من تصانيفه الكثيرة "التوسط بين الأخفش وثعلب في تفسير القرآن" و "المعاني في القرآن". ودرستويه ضبطه السمعاني بضم الدال والراء وسكون السن وضم التاء وسكون الواو وفتح الياء. وذكره ابن ماكولا بفتح الدال والراء والواو (١).

الكازروني [.. - بعد ١١٠٢ هـ / .. - بعد ١٦٩٠ م]

<span data-type="title" id=toc-750>عبد الله بن حسن، العفيف الكازروني:</span>
فقيه حنفي، مفسر، مشارك في عدة علوم. من أهل مكة، ونسبته إلى كازرون من بلاد فارس. كان حيا سنة ١١٠٢ هـ. من آثاره "حاشية على تفسير البيضاوي" (٢).

الحسني [حيا قبل ٣٠٠ هـ / حيا قبل ٩١٣ م]

<span data-type="title" id=toc-751>عبد الله بن الحسين بن القاسم بن إبراهيم الحسني العلوي الرسي:</span>
مفسر،
_________
(١) تاريخ بغداد ٩: ٤٢٨ وإنجاه الرواة ٢: ١١٣ ووفيات الأعيان ٣: ٤٤ ونزهة الألباء ٣٥٦ كشف الظنون ٥٠٦ ومعجم المطبوعات ١٠١ وبغية الوعاة ٢: ٣٦ والفهرست لابن النديم ٦٨ والبداية والنهاية ١١: ٢٣٣ وهدية العارفين ١: ٤٤٦ والمثتبه ١: ٢٨٥ وتبصير المنتبه ٥٥٩ ومعجم المؤلفين ٦: ٤٠.
(٢) فهرس جامعة الرياض ٥: ٨ وفهرس دار الكتب المصرية ١: ٣٩٩ و ٤٥٠.','1','306',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (536,'زيدي المذهب، ولد بالمدينة، وكان يسكن "الفرع" من أرض الحجاز مع أخيه يحي وأبيه وأعمامه. ونشأ فقيها عالما ورعا. وراسل أبو العتاهية (وكان من ملوك اليمن) يحيى ودعاه إلى بلاده، فقصدها مع أخيه عبد الله ونزلا بصعدة (سنة ٢٨٣ هـ). وبويع يحيى، وخوطب بأمير المؤمنين وتلقب بالهادي إلى الحق، واستولى على نجران، وملك صنعاء، وخطب له بمكة، واستمر إلى أن توفي سنة ٢٩٨ هـ (٩١١ م). أما عبد الله (صاحب الترجمة) فلم أعثر على تاريخ وفاته. له "الناسخ والمنسوخ" منه مخطوطة في برلين تحت رقم ١٠٢٢٦ (الأوراق من ٥ - ٤٥) (١).

ابن حسنون [٢٩٥ - ٣٨٦ هـ / ٩٠٨ - ٩٩٦ م]

<span data-type="title" id=toc-752>عبد الله بن الحسن بن حسنون السامري، أبو أحمد:</span>
عالم باللغة، من كبار القراء، من أهل سامراء، نشأ ببغداد، وتعلم على أبي بكر بن مجاهد، وأبي بكر بن مقسم وغيرهما. ونزل
_________
(١) مجلة الدراسات الشرقية  R.S.O  إيطالية، ١٦٤ - ١٦٥، تاريخ التراث العربي ١: ٢٠٩ والحور العين ١٩٦ والمقتطف من تاريخ اليمن ١٠٤ والإكليل ١٠: ١١٨ و ١٨١ و ٢٢١ وتاريخ اليمن للواسعي ٢١.','1','306',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (537,'مصر، وتوفي بها. له "اللغات في القرآن" تناول فيه بالدراسة التفسيرات التي ذكرها ابن عباس. وقد نشره الدكتور صلاح الدين المنجد في القاهرة سنة ١٩٤٦ (١).

العكبري [٥٣٨ - ٦١٦ هـ / ١١٤٣ - ١٢١٩ م]

<span data-type="title" id=toc-753>عبد الله بن الحسن بن عبد الله بن الحسين العكبري البغدادي، أبو البقاء، محب الدين:</span>
عالم بالأدب واللغة والقراءات والتفسير والفرائض والحديث والحساب. أصله من عكبرة (بلدة على دجلة). ومولده ووفاته ببغداد. تفقه على مذهب ابن حنبل، وتأدب على ابن الخشاب، وروى عن شيوخ عصره. أصيب في صباه بالجدري، فعمي. له تصانيف منها "التبيان في إعراب القرآن" طبع، ويسمى إملاء ما من به الرحمن من وجوه الإعراب والقراءات في جميع القرآن، و "تفسير القرآن" (٢).
_________
(١) تاريخ بغداد ٩: ٤٤٢ وغاية النهاية ١: ٤١٥ وشذرات الذهب ٣: ١١٩ وتاريخ التراث العربي ١: ٢١٥ ومجلة المجمع العلمي العربي ٢٢: ١٦٤ والأعلام ٤: ٢٠٨.
(٢) نكت الهميان ١٧٨ وويفات الأعيان ٢: ١٠٠ وطبقات المفسرين للداودي ١: ٢٢٤ وبغية الوعاة ٢: ٣٨ والعبر للذهبي ٥: ٦١ والذيل على طبقات =','1','307',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (538,'الصباغ [.. - ٣١٨ هـ / .. - ٩٣٠ م]

<span data-type="title" id=toc-754>عبد الله بن حنين بن عبد الله بن عبد الملك الكلابي، أبو محمد، ويعرف بابن أخي ربيع الصباغ:</span>
محدث، أصولي، مفسر، من فقهاء المالكية، من أهل قرطبة بالأندلس. سمع بها وبمصر. وثقه أبو محمد الباجي وأثنى عليه. وقال في الديباج: "كان معتنيا بالحديث، إماما فيه، بصيرا بعلله، حسن التأليف فيه .. " له "مختصر تفسير بقي بن مخلد" (١). عبد الله بن الزبير ١ - ٧٣ هـ / ٦٢٢ - ٦٩٢ م]

<span data-type="title" id=toc-755>عبد الله بن الزبير بن العوام بن خويلد الأسدي القرشي، أبو بكر، ويقال أبو خبيب:</span>
قائد، من أبطال قريش في زمنه، وأول مولود في الإسلام بالمدينة بعد الهجرة. روى عن النبي - صلى الله عليه وسلم - وعن أبيه وجده أبي بكر الصديق وخالته عائشة وعمر وعثمان وعلي، ويعد من مشاهير مفسري القرآن الكريم من الصحابة.
_________
= الحنابلة ٢: ١٠٩ والنجوم الزاهرة ٦: ٢٤٦ وإنباه الرواة ٢: ١١٦ وشذرات الذهب ٥: ٦٧.
(١) طبقات المفسرين للداودي ١: ٢٢٧ والديباج المذهب ١: ٤٢٦ وبغية الملتمس ٣١٧ وجذوة المقتبس ٣٣٣ ومعجم المؤلفين ٦: ٥١.','1','307',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (539,'اشترك في غزوة القسطنطينية التي جهزها معاوية بن أبي سفيان شة ٦٧٠ م. وغزا مع عبد الله بن سعد إفريفية. ولما قتل الحسن بن علي بن أبي طالب ثار عبد الله بالحجاز، ثم بويع له بالخلافة (سنة ٦٤ هـ) عقيب موت يزيد بن معاوية، فحكم مصر والحجاز واليمن وخراسان والعراق وأكثر الشام، وجعل قاعدة ملكه المدينة. ثارت ضده الفتن وبخاصة بالكوفة، واشتغل بقتال الأمويين فكانت له معهم وقائع هائلة، ثم حاصره الحجاج بن يوسف الثقفي بمكة ستة أشهر، انتهت بمقتل ابن الزبير بعد أن خذله عامة أصحابه وقاتل قتال الأبطال. ومدة خلافته تسع سنين. له في كتب الحديث ٣٣ حديثا (١).
_________
(١) التفسير والمفسرون ١: ٦٣ وتهذيب التهذيب ٥: ٢١٣ وحلية الأولياء ١: ٣٢٩ وتاريخ الخميس ٢: ٣٠١ والعقد الثمين ٥: ١٤١ وطبقات ابن سعد ٥: ٢٥ وانظر فهرسته. وغاية النهاية ١: ٤١٩ ووفيات الأعيان ٣: ٧١ وفوات الوفيات ٢: ١٧١ وتهذيب تاريخ ابن عساكر ٧: ٣٩٨ والشذرات ١: ٧٩ وتاريخ الطبري ٤: ٤٥٢ وانظر فهرسته، وكشف الظنون ١: ٢٨، والموسوعة العربية الميسرة ١١٧٨ والكامل لابن الأثير ٤: ١٤. وانظر فهرسته وصفة الصفوة ١: ٣٢٢.','1','308',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (540,'ابن ابي جمرة [.. - ٦٩٥ هـ / .. - ١٢٩٦ م]

<span data-type="title" id=toc-756>عبد الله بن سعد بن سعيد بن أبي جمرة الأزدي الأندلسي، أبو محمد:</span>
عالم بالحديث، مفسر، من فقهاء المالكية. أصله من الأندلس ووفاته بمصر. من كتبه "تفسير القرآن" ويعرف بتفسير ابن أبي جمرة (١).

الأشج [.. - ٢٥٧ هـ / .. - ٨٧١ م]

<span data-type="title" id=toc-757>عبد الله بن سعيد بن حصين الكندي الكوفي، أبو سعد، المعروف بالأشج:</span>
محدث ثقة، مفسر، كان محدث الكوفة في عصره. حدث عنه الأئمة الستة وابن خزيمة وأبو يعلى وغيرهم. من كتبه "تفسير القرآن" (٢).
_________
(١) البداية والنهاية ١٣: ٣٤٦ وفيه وفاته سنة ٦٩٥ هـ. ونيل الإبتهاج ١٤٠ وفيه وفاته ٦٩٩ هـ وكشف الظنون ١: ٤٣٦ وفيه وفاته سنة ٥٢٥، وصفحة ١٠٤٠ سة ٦٧٥ هـ والأعلام ٤: ٣٢١ ومعجم المؤلفين ٦: ٤٠ و ٥٧ وهدية العارفين
(٢) تذكرة الحفاظ ٥٠١ والعبر ٢: ١٥ والأعلام ٤: ٢٢٢ وشذرات الذهب ٢: ١٣٧ وطبقات المفسرين ١: ٢٢٨ ومختصر دول الإسلام ١: ١٢٢ وكشف الظنون ٤٤٢.','1','308',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (541,'الشقاق [٣٤٦ - ٤٢٦ هـ / ٩٥٧ - ١٠٣٥ م]

<span data-type="title" id=toc-758>عبد الله بن سعيد بن محمد، أبو محمد الشقاق القرطبي:</span>
إمام في القراءات والتفسير، حافظ للحديث، مشارك في علوم العربية والحساب والفرائض، من فقهاء المالكية. من أهل قرطبة، وأقرأ بها مدة (١).

ابن أبي داود [٢٣٠ - ٣١٦ هـ / ٨٤٤ - ٩٢٩ م]

<span data-type="title" id=toc-759>عبد الله بن سليمان بن الأشعث ابن إسحاق الأزدي السجستاني، أبو بكر ابن أبي داود:</span>
من كبار رجال الحديث، مفسر، فقيه، مقرىء، كان إمام أهل العراق في زمنه. ولد بسجستان، ودرس على والده، ورافقه في رحلات علمية طويلة، فسمع بخراسان والجبال وأصبهان وفارس والبصرة وبغداد والكوفة والمدينة ومكة والشام والجزيرة ومصر والثغور. ثم استقر ببغداد وتوفي بها.
_________
(١) الصلة ١: ٢٥٨ وغاية النهاية ١: ٤٢٠ والديباج ١: ٤٣٧ وشجرة النور ١: ١١٣ وطبقات المفسرين للداودي ١: ٢٢٩ وبغية الملتمس ٣٣٢ وترتيب المدارك ٤: ٧٢٩.','1','309',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (542,'ويروى أنه كان يعرف من الأحاديث عددا أكبر مما عرف والده. من كتبه "المصاحف" طبع، و "نظم القرآن" و "فضائل القرآن" و "شريعة التفسير" و "الناسخ والمنسوخ" و "تفسير القرآن" قال ابن النديم: "عمله لما عمل أبو جعفر كتابه" (١).

الجوهري [.. - ١٢٠١ هـ / .. - ١٧٨٧ م]

<span data-type="title" id=toc-760>عبد الله بن سليمان اليمني، المعروف بالجوهري:</span>
فقيه شافعي، محدث، عارف بالتفسير. قال في "أبجد العلوم". إن تصانيفه تزيد على خمسين مؤلفا، منها "الرسالة" في بيان دلالة قوله تعالى: {ولقد زينا الدنيا بمصابيح ..} (سورة فصلت، الآية ١٢) (٢).
_________
(١) ابن النديم ٢٨٨ وتاريخ التراث العربي ١: ٤٣٨ وتاريخ بغداد ٩: ٤٦٤ ولسان الميزان ٣: ٣٩٣ وميزان الاعتدال ٢: ٤٣ وتذكرة الحفاظ ٧٦٧ وابن عساكر ٧: ٤٣٩ وغاية النهاية ١: ٤٢٠ وطبقات الحنابلة ٢: ٥١ والشذرات ٢: ٢٧٣ والنجوم الزاهرة ٣: ٢٢١ والمنتظم ٦: ٢١٨ وهدية العارفين ١: ٤٤٤ وتاريخ أصبهان ٢: ٦٦ والرسالة المستطرفة ٤٦ والسبكي ٣: ٣٠٧ والعبر ٢: ٦٤ ومرآة الجنان ٢: ٢٦٩ وطبقات المفسرين ١: ٢٢٩ وطبقات العبادي ٦٠ والأعلام ٤: ٤٢٤.
(٢) هدية العارفين ١: ٤٨٦ ومعجم المؤلفين ٦: ٦٢.','1','309',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (543,'الجهني [١٣٧ - ٢٢٣ هـ / ٧٥٤ - ٨٣٨ م]

<span data-type="title" id=toc-761>عبد الله بن صالح بن محمد الجهني المصري، أبو صالح:</span>
كاتب الليث بن سعد وراويته. يعتبر بصفة عامة محدثا ثقة، رغم ما يقال عن وجود أخطاء في مروياته إسنادا ومتنا. وكان راوية تفسير القرآن الذي ألفه ابن عباس. قال صاحب تاريخ التراث العربي: "وقد ظل هذا التفسير مستخدما لدى كثير من المحدثين والمفسرين في القرن الثالث الهجري". وكان عنده صحيفة من هذا التفسير رواها علي بن أبي طلحة عن ابن عباس، قال الإمام أحمد رضي الله عنه: لو رحل رجل فيها إلى مصر قاصدا ما كان كثيرا .. " (١).

اليابري [.. - ٥١٨ هـ / .. - ١١٢٣ م]

<span data-type="title" id=toc-762>عبد الله بن طلحة بن محمد بن عبد الله اليابري، أبو بكر، ويقال: أبو محمد:</span>
نحوي، أصولي، مفسر، من فقهاء المالكية. أصله من يابرة، ونزل اشبيلية،
_________
(١) تذكة الحفاظ ٣٨٨ وميزان الاعتدال ٢: ٤٤٠ والطبقات الكبرى ٧: ٥١٨ والتهذيب ٥: ٢٥٦ والتاريخ الكبير للبخاري ٣/ ١: ١٢١ والإتقان ٢: ١٨٨ وتاريخ التراث العربي ١: ٢٨٦.','1','310',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (544,'ورحل إلى المشرق، ودخل المهدية سنة ٥١٤ هـ، وأقام بالقاهرة وقنا، ثم رحل إلى مكة وتوفي بها. قال ابن الأبار: كان ذا معرفة بالنحو والأصول والفقه، وحفظ التفسير والقيام عليه وحلق به مدة باشبيلية وغيرها، وهو كان الغالب عليه مع القصص .. " (١).

ابن عباس [٣ ق هـ - ٦٨ هـ / ٦١٩ - ٦٨٧ م]

<span data-type="title" id=toc-763>عبد الله بن عباس بن عبد المطلب القرشي الهاشمي، أبو العباس:</span>
صحابي جليل، من أكابر العلماء بالفقه والحديث والتفسير في صدر الإسلام. وهو ابن عم النبي - صلى الله عليه وسلم -. ولد بمكة، ونشأ بها في بدء عصر النبوة، فلازم النبي - صلى الله عليه وسلم - وروى عنه الأحاديث الصحيحة. لم يكن له دور كبير في السياسة أو الحرب، ولكنه صحب الجيش الإسلامي في بعض فتوحاته. وأنابه الخليفة عثمان عنه في رياسة بعثة الحج سنة ٣٥ هـ. ثم شهد مع علي الجمل وصفين. وولاه علي البصرة سنة ٣٩ هـ. ولكنه تركها بعد سنة واحدة ورجع إلى الطائف. ولما نزل الحسن بن علي بن أبي طالب عن
_________
(١) طبقات المفسرين للداودي ١: ٢٣٢ وبغية الوعاة ٢: ٤٦ ونفح الطيب ٢: ٦٤٨ والتكملة لابن الأبار ٢: ٨١٥ ونيل الابتهاج ١٣١.','1','310',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (545,'الخلافة وسلم الأمر لمعاوية في بيت المقدس (سنة ٤١ هـ) اعترف ابن عباس ببيعة الأمويين، فضمنوا له حياة خالية من المتاعب، فقضى بقية عمره باحثا عن العلم، بعيدا عن الحياة السياسية. وكف بصره في آخر عمره وتوفي بالطائف. قال ابن مسعود: نعم ترجمان القرآن ابن عباس،. وكان عمر بن الخطاب إذا أعضلت عليه قضية دعا ابن عباس وقال له: أنت لها ولأمثالها، ثم يأخذ بقوله ولا يدعو لذلك أحدا سواه. وكان آية في الحفظ، أنشده عمر بن أبي ربيعة إحدى قصائده فحفظها في مرة واحدة، وهي ثمانون بيتا. وكان إذا سمع النوادب سد أذنيه بأصابعه مخافة أن يحفظ أقوالهن. من آثاره "تفسير القرآن" ويعد أول محاولة لتفسير كتاب الله تفسيرا لغويا محضا، وهو كذلك أول محاولة لتفسير القرآن في أوائل العصر العباسي. وهذا التفسير بقي كله عند الطبري، أما الكتب التي بقيت والمعنونة بتفسير ابن عباس، فأكثرها بتنقيح محمد بن السائب الكلبي والتي ترجع روايتها عن علي بن أبي طلحة إلى ابن عباس. وهناك جزء من تفسير ابن عباس جمعه العلامة الفيروزابادي (٧٢٩ - ٨١٧ هـ) وسماه "تنوير المقباس من تفسير ابن عباس" طبع في مصر. ولابن عباس أيضا "غريب القرآن" بتنقيح عطاء بن أبي رباح','1','311',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (546,'المتوفي سنة ١١٤ هـ. و "لغة القرآن" و "مسائل نافع بن الأزرق" المتوفى سنة ٦٥ هـ، وهي عبارة عن أسئلة زعيم الخوارج عن معنى أكثر من مائتي كلمة صعبة في القرآن، وجهها إلى ابن عباس، فأجاب عنها ووضحها بشواهد من الشعر القديم (١).

الدارمي [١٨١ - ٢٥٥ هـ / ٧٩٧ - ٨٦٩ م]

<span data-type="title" id=toc-764>عبد الله بن عبد الرحمن بن الفضل بن بهرام بن عبد الصمد التميمي الدارمي السمرقندي، أبو محمد:</span>
محدث، حافظ، مفسر، فقيه. سمع بالحجاز والشام ومصر والعراق وخراسان، وحدث عن يزيد بن هارون وخلق كثير، وحدث عنه مسلم والترمذي وأبو داود وبقي بن
_________
(١) نكت الهميان ١٨٠ وحلية الأولياء ١: ٣١٤ والمحبر ٣٨٩ وذيل المذيل ٢١ وتذكرة الحفاظ ٤٠ وخلاصة تهذيب الكمال ١٧٢ والعبر ١: ٧٦ وصفة الصفوة ١: ٣١٤ والنجوم الزاهرة ١: ١٨٢ والإصابة ترجمة ٤٧٧٢ وتاريخ بغداد ١: ١٧٣ وطبقات القراء للذهبي ١: ٤١ وأعيان الشيعة ٤١: ٣ وشذرات الذهب ١: ٥٧ وطبقات المفسرين للداودي ١: ٢٣٢ والأعلام ٤: ٢٢٩ وتاريخ التراث العربي ١: ١٧٩ والطبقات الكبرى ١١: ٣٦٥ وتاريخ الخميس ١: ١٦٧ وطبقات الشيرازي ١٨ وتنقيح المقال ٢: ١٩١ ومنهج المقال ٢٠٦ ومنتهى المقال ١٨٦ وتهذيب التهذيب ٥: ٢٧٦ والتفسير والمفسرون ١: ٦٥.','1','311',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (547,'مخلد وأبو زرعة وغيرهم. واستقضي على سمرقند، وألح عليه السلطان حتى ولي، فقضى قضية واحدة، واستعفى فأعفي. قال الخطيب البغدادي: "كان أحد الحفاظ والرحالين، موصوفا بالثقة والزهد والورع، يضرب به المثل في الديانة والحفظ والرزانة، والإجتهاد والعبادة، والزهادة والتقلل". وقال محمد بن إبراهيم الشيرازي: "أظهر علم الآثار بسمرقند، وكان مصنفا كاملا، وفقيها عالما". من كتبه "تفسير القرآن" وغير ذلك (١).

المسمعي [؟ -؟ هـ /؟ -؟ م]

<span data-type="title" id=toc-765>عبد الله بن عبد الرحمن الأصم، المعروف بالمسمعي:</span>
فقيه إمامي، عارف بالتفسير، من أهل البصرة. من آثاره "الناسخ والمنسوخ من القرآن". ذكره
_________
(١) تذكرة الحفاظ ٢: ٥٣٤ وخلاصة تهذيب الكمال ١٧٣ وتهذيب التهذيب ٥: ٢٩٤ والنجوم الزاهرة ٣: ٢٢ والعبر ٢: ٨ وطباقات المفسرين للداودي ١: ٢٣٥ وشذرات الذهب ٢: ١٣٠ والرسالة المستطرفة ٣٢ وهدية العارفين ١: ٤٤١ والأعلام ٤: ٢٣٠ وتاريخ بغداد ١٠: ٢٩ والجرح والتعديل ٢/ ٢/: ٩٩ ودائرة المعارف الإسلامية ٢: ١٥٩ ومعجم المؤلفين ٦: ٧١ وتاريخ التراث العربي ١: ٣٠٥ والمختصر في أخبار البشر ٢: ٤٩ ومفتاح السعادة ٢: ١٦.','1','312',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (548,'صاحب هدية العارفين ولم يؤرخ وفاته. وفي لسان الميزان ترجمة لعبد الله بن عبد الرحمن المسمعي البصري، ولكن ابن حجر لم يؤرخ وفائه أيضا (١).

ابن أبي زيد [٣١٠ - ٣٨٦ هـ / ٩٢٢ - ٩٩٦ م]

<span data-type="title" id=toc-766>عبد الله بن عبد الرحمن (أبي زيد) النفزي القيرواني، أبو محمد:</span>
إمام المالكية في وقته، يلقب بقطب المذهب وبمالك الأصغر. من أعيان القيروان، مولده ومنشأه ووفاته فيها. رحل وحج، وسمع من ابن الأعرابي وغيره. قال صاحب الديباج: "حاز رئاسة الدين والدنيا، وإليه كانت الرحلة من الأقطار، وهو الذي لخص مذهب مالك وضم نشره، وذب عنه، وملأت البلاد تآليفه .. " وقال الذهبي: "كان على أصول السلف في الأصول، لا يدري الكلام، ولا يتأول .. ". من آثارها "البيان عن إعجاز القرآن" (٢).
_________
(١) هدية العارفين ١: ٤٤١ ولسان الميزان ٣:
(٢) هدية العارفين ١: ٤٤٧ والديباج ١: ٤٢٧ وشجرة النور ١: ٩٦ ومعالم الأعيان ٣: ١٣٥ وشذرات الذهب ٣: ١٣١ ومرآة الجنان ٢: ٤٤١ وترتيب المدارك ٤٩٢ ودائرة المعارف الإسلامية (ط ١) ١: ٨٠ والنجوم الزاهرة ٤: ٢٠٠ وتذكرة الحفاظ ١٠٢١ ومعجم المؤلفين ٦: ٧٣ والأعلام ٤: ٢٣٠','1','312',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (549,'الانصاري [.. - ٦٣٤ هـ / .. - ١٢٣٧ م]

<span data-type="title" id=toc-767>عبد الله بن عبد الرحمن بن محمد الأنصاري الأندلسي، أبو محمد:</span>
لغوي، من أهل بسطة - مدينة بالقرب من وادي آش، بالأندلس - قال السيوطي: "شيخ فاضل، الغالب عليه معرفة اللغة، قرأها على أبي محمد بن زيدان المكي اللغوي". من آثاره "ري الظمآن في متشابه القرآن" (١).

ابن عقيل [٦٩٤ - ٧٦٩ هـ / ١٢٩٤ - ١٣٦٧ م]

<span data-type="title" id=toc-768>عبد الله بن عبد الرحمن بن عبد الله بن محمد القرشي الهاشمي، أبو محمد، بهاء الدين، المشهور بابن عقيل:</span>
من أئمة النحاة، مفسر، فقيه، من نسل عقيل بن أبي طالب. ولد بالقاهرة، وأخذ عن أبي حيان والعلاء القونوي وغيرهما. ودرس التفسير بالجامع الطولوني، وناب في الحكم بالقاهرة، وولي قضاء الديار المصرية مدة قصيرة. قال مترجموه: "كان مهيبا، مترفعا عن غشيان الناس ولا يخلو مجلسه من
_________
(١) بغية الوعاة ٢: ٤٨ وهدية العارفين ١: ٤٦٠ وإيضاح المكنون ١: ٦٠٤ ومعجم المؤلفين ٦: ٧٢.','1','313',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (550,'المترددين إليه، كريما، كثير العطاء لتلاميذه، في لسانه لثغة". وقال ابن قاضي شهبة: "درس التفسير بالجامع الطولوني، وختم به القرآن تفسيرا في مدة ثلاث وعشرين سنة، ثم شرع في أول القرآن بعد ذلك فمات في أثناء ذلك" أما نسبته إلى همذان أو آمد أو بالس كما في بعض المراجع فلأن بعض أسلافه كانوا يقيمون في همذان أو آمد، ولعلهم انتقلوا من إحداهما إلى الأخرى، واستقرت ذرية منهم في بالس (بين حلب والرقة) وقدم أحدهم إلى مصر، فولد بها عبد الله، فعرفه مترجموه بالهمذاني أو الآمدي البالسي ثم المصري. من كتبه "تفسير القرآن" وصل فيه إلى آخر سورة آل عمران، وتفسير آخر مختصر لم يكمله سماه "التعليق الوجيز على الكتاب العزيز" (١).
_________
(١) الدرر الكامنة ٢: ٣٧٢ وحسن المحاضرة ١: ٥٣٧ والبدر الطالع ١: ٣٨٦ وغاية النهاية ١: ٤٢٨ ومفتاح السعادة ٢: ١٠٩ وبغية الوعاة ٢: ٤٧ وشذرات الذهب ٦: ٢١٤ والأسنوي ١٩٨ وطبقات النحاة لابن قاضي شهبة ٣٣٤ والنجوم الزاهرة ١١: ١٠٠ وكشف الظنون ٤٣٩ وطبقات الداودي ١: ٢٣٣ ومفتاح السعادة ٢: ١٠٩ وروضات الجنات ٤٥٨ وهدية العارفين ١: ٤٦٧ والأعلام ٤: ٢٣١.','1','313',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (551,'الدنوشري [.. - ١٠٢٥ هـ / .. - ١٦٢٦ م]

<span data-type="title" id=toc-769>عبد الله بن عبد الرحمن بن علي بن محمد الدنوشري:</span>
فقيه شافعي، عارف باللغة والنحو والتفسير. نسبته إلى "دنوشر" غربي المحلة الكبرى بمصر. تعلم بالقاهرة، درس بالجامع الأزهر، ورحل إلى بلاد الشام، ومات بالقاهرة. من آثاره " هدية الأحباب في تفسير أعظم آيات الكتاب" (آية الكرسي) ورسالة في قوله تعالى {وبالآخرة هم يوقنون} (١).

الكليسي [١٢٤١ - ١٣٠٣ هـ / ١٨٢٦ - ١٨٨٦ م]

<span data-type="title" id=toc-770>عبد الله بن عبد الرحمن الكليسي الحلبي، المعروف بأنوري:</span>
فقيه حنفي، مفسر، أصولي، نحوي، مقرىء. له تصانيف منها "حاشية" على "أنوار التنزيل، في التفسير للبيضاوي (٢).

الأدكاوي [١١٠٤ - ١٦٩٢ هـ / ١١٨٤ - ١٧٧٠ م]

<span data-type="title" id=toc-771>عبد الله بن عبد الله بن سلامة الأدكاوي، الشافعي، ويعرف بالمؤذن:</span>
_________
(١) خلاصة الأثر ٣: ٥٣ وهدية العارفينن ١: ٣٧٤ وخطط مبارك ١١: ٦٥ ومعجم المؤلفين ٦: ٧٠ وكشف الظنون ١: ٩٠ و ٢٠٤٢.
(٢) هدية العارفين ١: ٤٩٢.','1','314',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (552,'أديب، ناظم، ناثر، ولد بقرية "أدكو" قرب رشيد بمصر. وتعلم وتوفي بالقاهرة. من تصانيفه "المنح الربانية في تفسير آيات الحكم الفرقانية" (١).

الورد [.. - قبل ١١٧٣ هـ / .. - قبل ١٧٦٠ م]

<span data-type="title" id=toc-772>عبد الله بن عبد الولي بن محمد الورد:</span>
فاضل، مشارك في بعض العلوم. من آثاره "الجوهر الأصيل المختصر من معالم التنزيل" في التفسير، للبغوي (٢).

الرومي [.. - ١١٦٧ هـ / .. - ١٧٥٤ م]

<span data-type="title" id=toc-773>عبد الله عبدي باشا الرومي، الحنفي:</span>
من رجال الإدارة في العهد العثماني، عين واليا على مرعش وتوفي بها. له "حاشية" عل أوائل تفسير سورة البقرة (٣).

شارح الفصوص [٩٩٢ - ١٠٥٤ هـ / ١٥٨٤ - ١٦٤٤ م]

<span data-type="title" id=toc-774>عبد الله بن عبدي بن محمد الرومي البوسنوي البيراير، المعروف بشارح (الفصوص)</span>
_________
(١) عجائب الآثار ٣: ٧ وهدية العارفينن ١: ٤٨٤ وخطط مبارك ٨: ٥١ وهو فيه عبد الله بن سلامة ..
(٢) بروكلمان الذيل ٢: ٥٥٦.
(٣) هدية العارفين ١: ٤٨٣.','1','314',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (553,'الفصوص: فاضل متصوف، من أهل البوسنة، يعرف عند أهلها باسم "غائبي"، وورد ذكره في كشف الظنون باسم "عبدي". والبيرامي نسبة إلى الطريقة البيرامية، وكان من مشايخها. مات عائدا من الحج، بمدينة قونية ودفن فيها. له "الكشف عن الأمر في تفسير آخر سورة الحشر" و "كشف السر المبهم في أول سورة مريم، و "كشف أسرار البررة في تفسيرآية: {قتل الإنسان ما أكفره} و "سر الفيض والنصر في تفسير سورة العصر" و "سر اليقين في تفسير آية: {واعبد ربك حتى يأتيك اليقين} و "رسالة، في تفسير سورة "والعاديات" و "رسالة" في تفسير سورة "ن والقلم وما يسطرون" و "رسالة" في تفسير آية: {يا أيها الناس اعبدو ربكم}. و "رسالة" في تفسير آية: {كلوا واشربوا ولا تسرفوا}. و "تجلي النور المبين في مرآة: {إياك نعبد وإياك نستعين} (١).

أبو بكر الصديق [٥١ ق - ١٣ هـ / ٥٧٣ - ٦٣٤ م]

<span data-type="title" id=toc-775>عبد الله بن أبي قحافة عثمان بن عامر بن كعب بن سعد التيمي القرشي، أبو بكر:</span>
_________
(١) الجوهر الأسنى ٩٤ وخلاصة الأثر ٣: ٨٦ وهدية العارفين ١: ٤٧٦ والأعلام ٤: ٢٣٦ ومعجم المؤلفين ٦: ٨٠.','1','315',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (554,'أول الخلفاء الراشدين، وأول من آمن برسول الله - صلى الله عليه وسلم - من الرجال، فكتب له أن يكون ثأني اثنين حين يكون النبي - صلى الله عليه وسلم - أول الاثنين، فكان ثاني اثنين في الإسلام، وثاني اثنين في غار الهجرة، وثاني اثنين في الظلة التي آوى إليها النبي - صلى الله عليه وسلم - يوم بدر، وثاني اثنين في كل وقعة من المواقع التي دارت رحاها بين المسلمين والمشركين. ولد بمكة، ونشأ سيدا من سادات قريش وأشراف العرب، وغنيا من كبار موسريهم، وعالما بأنساب القبائل وأخبارها وسياستها. وكان أليفا، ودودا، حسن المعاشرة، متواضعا، لين الجانب، لم يتعال على أحد في الجاهلية والإسلام. ثم كانت له في عصر النبوة مواقف كبيرة، فشهد الحروب، واحتمل الشدائد، وبذل الأموال - وحج بالمسلمين في السنة التاسعة نيابة عن النبي - صلى الله عليه وسلم -، وأمهم في الصلاة في أثناء مرضه. وبويع بالخلافة بعد انتقال النبي - صلى الله عليه وسلم - إلى الرفيق الأعلى سنة ١١ هـ، فشيع بنفسه جيش أسامة بن زيد إلى قضاعة بالشام، وكان النبي - صلى الله عليه وسلم - قد جهزه قبل موته. وحارب المرتدين والممتنعين من دفع الزكاة، وأبلى في ذلك بلاء حسنا. ثم وجه الجيوش لفتح العراق والشام، واتفق له قواد أمناء كخالد بن الوليد، وعمرو بن العاص، وأبي عبيدة بن الجراح، والمثنى بن حارثة. قيل سمي "الصديق" في الجاهلية، وقيل لأنه صدق','1','315',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (555,'بكل ما جاء به النبي - صلى الله عليه وسلم -. وأخباره كثيرة ذكرها مترجموه. ومدة خلافته سنتان وثلاثة أشهر ونصف شهر، وتوفي في المدينة. له في كتب الحديث ١٤٢ حديثا، ويعد من أوائل المفسرين من الصحابة، ولكن لم يرد عنه في التفسير إلا النزر اليسير، ويرجع السبب في ذلك إلى تقدم وفاته، واشتغاله بمهام الخلافة والفتوحات (١).

القريمي [١١٤٥ - ١٢١١ هـ / ١٧٣٢ - ١٧٩٦ م]

<span data-type="title" id=toc-776>عبد الله بن عثمان القريمي ثم القسطنطيني:</span>
قاض، مشارك في علوم الفقه والتفسير، من فقهاء الحنفية. ولي القضاء بالروم إيلي ومصر والمدينة. من آثاره
_________
(١) أبو بكر الصديق لمحمد حسنين هيكل، وأبو بكر الصديق، لعلي الطنطاوي، وعمدةا لتحقيق في بشائر آل الصديق، لإبراهيم العبيدي، وأشهر مشاهير الإسلام، للعظم، وتهذيب الأسماء واللغات ٢: ١٨٢ وحلية الأولياء ٤: ٩٣ وصفة الصفوة ١: ٨٨ وذيل المذيل ١١٣ والمعرفة ١٦٤٩ والموسوعة العربية الميسرة ٣١ وطبقات ابن سعد ٣: ١٦٩ وانظر فهرسته، والكامل لابن الأثير ٢: ٣٢٥ وانظر فهرسته، والإصابة، ت ٤٨٠٨ والرياض النضرة ٤٤، والشفين لطه حسين، وتاريخ الخميس ٢: ١٩٩ وتاريخ مختصر الدول ٩٨ وجامع كرامات الأولياء ١: ١٢٧ وتاريخ الطبري ٢: ٣١٤ وانظر فهرسته، والعبر وفيات سنة ١٣ هـ. وشذرات الذهب ١: ٢٤ والتفسير والمفسرون ١: ٦٣.','1','316',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (556,'"تفسير" قوله تعالى: {يوم يأتي آيات ربك} (١).

العدوي [١١٨١ - ١٢٥٧ هـ / ١٧٦٧ - ١٨٤٢ م]

<span data-type="title" id=toc-777>عبد الله العدوي، أبو محمد، الشهير بالقاضي:</span>
مفسر، عارف بالأدب، من فقهاء المالكية، من أهل مصر. قال في شجرة النور: "كانت له دراية تامة بلغة العرب وأشعارهم وأساليب كلامهم". من آثاره "تفسير سورة القدر" فرغ منها في العشر الأخير من رمضان المبارك سنة ١٢٣٥ هـ (٢).

ابن عطية [.. - ٣٨٣ هـ / .. - ٩٩٣ م]

<span data-type="title" id=toc-778>عبد الله بن عطية بن عبد الله بن حبيب، أبو محمد:</span>
مقرىء، عالم بالتفسير، من أهل دمشق، ولي إمامة مسجد باب الجابية المعروف بمسجد عطية نسبة إليه. مدح بسبب علمه الغزير بالشواهد اللغوية، قيل: كان يحفظ خمسين ألف بيت للاستشهاد على معاني القرآن. له "تفسير القرآن" ويعرف بتفسير ابن عطية، لم يبق منه إلا نقول في كتب المفسرين،
_________
(١) هدية العارفين ١: ٤٨٨.
(٢) فهرست الخديوية ٧/ ١: ٦٠ وشجرة النور ٣٨٣ ومعجم المؤلفين ٦: ٨٢.','1','316',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (557,'وعلى سبيل المثال في "الكشاف" للزمخشري، وفي "الجامع لأحكام القرآن" للقرطبي، وفي "البحر" لابن حيان. ويميز عن ابن عطية الأندلسي (عبد الحق ابن غالب) المفسر أيضا، بأن يقال لصاحب الترجمة "المتقدم" ولعبد الحق "المتأخر" (١).

عبد الله عفيفي [.. - ١٣٦٣ هـ / .. - ١٩٤٤ م]

<span data-type="title" id=toc-779>عبد الله بن عفيفي الباجوري:</span>
أديب، له شعر، تعلم بالأزهر ودار العلوم بالقاهرة. وعلم العربية في مدارس الحكومة، ثم عين "محررا" عربيا في الديوان الملكي، وإماما للملك فؤاد الأول. توفي بالقاهرة. من كتبه "تفسير سورة الفتح وبيان ما اتصل بها من الفتوح الإسلامية والسيرة النبوية" طبع (٢).
_________
(١) غاية النهاية ١: ٤٣٣ وطبقات المفسرين للسيوطي ١٥ وتذكرة الحفاظ ١٠١٧ وطبقات القراء للذهبي ١: ٢٨١ ومفتاح السعادة ٢: ١٠٦ وهدية العارفين ١: ٤٤٧ وتاريخ التراث العربي ١: ٢١٥ وطبقات الداودي ١: ٢٣٩ والأعلام ٤: ٢٣٩ وكشف الظنون ٤٣٩.
(٢) معجم المطبوعات ١٣٤٦ وجريدة البلاغ القاهرية ٤/ ٤/ ١٣٦٣ وتقويم دار العلوم ٤٢٠ وفهرس دار الكتب المصرية ٣: ٣٩٦.','1','317',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (558,'عبد الله بن عمر [١٠ ق هـ - ٧٣ هـ / ٦١٣ - ٦٩٢ م]

<span data-type="title" id=toc-780>عبد الله بن عمر بن الخطاب القرشي العدوي، أبو عبد الرحمن:</span>
صحابي، عرف بالصلاح والزهد والتقوى، وسعة الإلمام بأخبار النبي - صلى الله عليه وسلم - والصحابة. من أعز بيوتات قريش في الجاهلية، وأكبر أبناء عمر، وشقيق حفصة أم المؤمنن. مولده ووفاته بمكة، أسلم مع والده، وهاجر إلى المدينة معه. عاون الخلفاء الراشدين الأربعة في السلم والحرب. ولما قتل عثمان عرض عليه نفر أن يبايعوه بالخلافة فأبى. آثر الحياد في الخلاف بين علي ومعاوية. وغزا إفريقية مرتين، الأولى مع ابن أبي سرح، والثانية مع معاوية بن خديج سنة ٤٣ هـ. عمر طويلا، وكشف بصره في آخر حياته. وهو أكثر الصحابة رواية عن النبي - صلى الله عليه وسلم -. له في كتب الحديث ٢٦٣٠ حديثا. كما ورد عنه اليسير من التفسير (١).
_________
(١) نكت الهميان ١٨٣ وطبقات ابن سعد ٤: ١٤٢ وأسد الغابة ٣: ٢٢٧ والإصابة ت ٤٨٢٥ وحلية الأولياء ١: ٢٩٢ والاستيعاب ٩٥٠ ومعالم الأعيان ١: ٧٠ وتهديب التهذيب ٥: ٣٢٨ وصفة الصفوة ١: ٢٢٨ ووفيات الأعيان ٣: ٢٨ وشذرات الذهب ١: ٨١ وكشف الظنون ٤٣٠ والموسوعة العربية الميسرة ١١٧٨.','1','317',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (559,'البيضاوي [.. - ٦٨٥ هـ / .. - ١٢٨٦ م]

<span data-type="title" id=toc-781>عبد الله بن عمر بن محمد بن علي البيضاوي الشيرازي، أبو سعيد، أو أبو الخير، ناصر الدين:</span>
قاض، مفسر، عالم بالفقه والأصلين والعربية والمنطق والحديث، من أعيان الشافعية. ولد في المدينة البيضاء - قرب شيراز -. وولي قضاء شيراز مدة. وصرف عنه، فرحل إلى تبريز وتوفي فيها. من تصانيفه الكثيرة "أنوار التنزيل وأسرار التأويل" طبع، ويعرف بتفسير البيضاوي. قال صاحب كشف الظنون: "وتفسيره هذا كتاب عظيم الشأن، غني عن البيان، لخص فيه من الكشاف ما يتعلق بالإعراب والمعاني والبيان، ومن التفسير الكبير ما يتعلق بالحكمة والكلام، ومن تفسير الراغب ما يتعلق بالاشتقاق وغوامض الحقائق ولطائف الإشارات، وضم إليه ما ورى زناد فكره من الوجوه المعقولة والتصرفات المقبولة، فجلا رين الشك عن السريرة وزاد في العلم بسطة وبصيرة ... " (١).
_________
(١) طبقات الشافعية ٨: ١٥٧ وكشف الظنون ١٨٦ وبغية الوعاة ٢: ٥٠ وطبقات المفسرين للداودي ١: ٢٤٢ ومفتاح السعادة ٢: ١٠٣ وهدية العارفين ١: ٤٦٢ والبداية والنهاية ١٣: ٣٠٩ وشذرات الذهب ٥: ٣٩٢ ودائرة المعارف الإسلامية (ط ١) ٤: ٤١٨ ونزهة الجليس ٢: ٨٧ ومرآة الجنان ٤: ٢٢٠ وروضات الجنات ٤٥٤.','1','318',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (560,'مستحي زاده [.. - ١١٤٨ هـ / .. - ١٧٣٥ م]

<span data-type="title" id=toc-782>عبد الله بن عمر بن عثمان بن موسى الرومي، المعروف بمستحي زاده:</span>
فقيه حنفي، مفسر، من علماء الدولة العثمانية. توفي ودفن في مقبرة "كسكين ده ده". من آثاره "حاشية" على أنوار التنزيل في التفسير، للبيضاوي، مخطوطة (١).

عبد الله بن عمرو [٧ ق هـ ٦٥ هـ / ٦١٦ - ٦٨٤ م]

<span data-type="title" id=toc-783>عبد الله بن عمرو بن العاص بن وائل السهمي القرشي، أبو محمد، وقيل أبو عبد الرحمن، وقيل أبو نصير:</span>
صحابي، ناسك، غزير العلم، من أهل مكة. كان يكتب في الجاهلية. قال أبو هريرة: "ما كان أحد أكثر حديثا عن رسول الله - صلى الله عليه وسلم - مني إلا عبد الله بن عمرو، فإنه كان يكتب وكنت لا أكتب". وكان يحسن السريانية. أسلم قبل أبيه، وروى عن النبي - صلى الله عليه وسلم - وعن أبي بكر وعمر وعبد الرحمن بن عوف وغيرهم. واستأذن النبي - صلى الله عليه وسلم - في أن يكتب ما يسمع منه، فأذن له. فكان عبد الله
_________
(١) هدية العارفين ووفاته فيه سنة ١١٥٠ هـ وإيضاح المكنون ١: ١٤٢ ووفاته في ١١٤٨ هـ وهو في عثمانلي مؤلفلري ٢: ٢٧ ومخطوطات الدار ١: ٣٩٢ عبد الله بن عثمان بن موسى ووفاته سنة ١١٤٨ هـ. والأعلام ٤: ١٠٣.','1','318',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (561,'يسمي صحيفته تلك "الصادقة". وكان كثير العبادة، فقال له النبي - صلى الله عليه وسلم - "إن لجسدك عليك حقا - " الحديث. وكان يشهد الحروب والغزوات، ويضرب بسيفين. وحمل راية أبيه يوم اليرموك، وشهد صفين مع معاوية. وولاه معاوية الكوفة مدة ثم عزله. ولما ولي يزيد امتنع من بيعته، وانزوى منقطعا للعبادة. وعمي في آخر عمره. في مكان وفاته خلاف، قيل بمصر، وقيل بالشام، وقيل بمكة، وقيل بالطائف .. له ٧٠٠ حديث، ورد عنه اليسير من التفسير (١).

ابن الغسال [.. - ٤٨٧ هـ / .. - ١٠٩٤ م]

<span data-type="title" id=toc-784>عبد الله بن فرج بن غزلون اليحصبي، الطليطلي الأصل، الغرناطي الموطن، أبو محمد، ويعرف بابن الغسال:</span>
حافظ، فقيه مالكي، عارف بالتفسير، روى عن ابن عبد البر وأبي الوليد الباجي وغيرهما. قال ابن بشكوال: أقرأ في الفقه والتفسير، وألف، ووعظ الناس بجامع غرناطة، وكان فقيها جليلا عارفا بالتفسير، شاعرا
_________
(١) طبقات ابن سعد ٤: ٢٦١ والكامل في التاريخ ٣: ٤١٣ وانظر فهرسته، وتهذيب التهذيب ٥: ٣٣٧ وشذرات الذهب ١: ٧٣ والمحبر ٢٧٣ وحلية الأولياء ١: ٢٨٣ والبدء والتأريخ ٥: ١٠٧ وصفة الصفوة ١: ٢٧٠ وكشف الظنون ٤٢٩ والإصابة ت ٤٨٣٨.','1','319',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (562,'مطبوعا ... مات بغرناطة وكان له يوم مشهود، حشر إليه الناس رجالا ونساء (١).

الأشعري [٢١ ق هـ - ٤٤ هـ / ٦٠٢ - ٦٦٥ م]

<span data-type="title" id=toc-785>عبد الله بن قيس بن سليم بن حضار، أبو موسى الأشعري:</span>
صحابي، من نجائهم، وال، فاتح، من أهل زبيد باليمن. قدم مكة عند ظهور الإسلام، فأسلم، استعمله النبي - صلى الله عليه وسلم - على زبيد وعدن. ثم ولي أمر البصرة لعمر بن الخطاب، فافتتح أصبهان والأهواز، ولما ولي عثمان أقره عليها، ثم عزله. فانتقل إلى الكوفة، فطلب أهلها توليته عليهم، فولاه، فاستمر إلى أن قتل عثمان، فأقره، علي. ثم كانت وقعة الجمل، وأرسل علي يدعو أهل الكوفة لينصروه، فأقام إلى أن كان التحكيم، وحكمه علي على نفسه لجلالته وفضله، فخدعه عمرو بن العاص، فارتد أبو موسى إلى الكوفة، فتوفي بها. قال مترجموه: كان من أطيب الناس صوتا بالقرآن، سمع النبي - صلى الله عليه وسلم - قراءته فقال: "لقد أوتي هذا مزمارا من مزامير آل داود ". يعد من الصحابة الذين ورد عنهم اليسير من التفسير (٢).
_________
(١) طبقات المفسرين للداودي ١: ٢٤٣ وبغية الوعاة ٢: ٥٢ والصله ١: ٢٧٦
(٢) طبقات ابن سعد ٤: ١٠٥ وغاية النهاية ١: =','1','319',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (563,'ابن المبارك [١١٨ - ١٨١ هـ / ٧٣٦ - ٧٩٧ م]

<span data-type="title" id=toc-786>عبد الله بن المبارك بن واضح الحنظلي بالولاء، التميمي، المروزي، أبو عبد الرحمن:</span>
الحافظ، شيخ الإسلام، صاحب التصانيف النافعة والرحلة الواسعة. كان من سكان خراسان، وأفنى عمره في الأسفار، حاجا ومجاهدا وتاجرا، وروى عنه مئات العلملء آلاف الكتب. جمع الحديث والفقه والأدب والنحو واللغة والشعر وفصاحة العرب وأيام الناس والشجاعة والسخاء. قيل أنه كان غنيا جدا، يحج عاما ويغزو عاما، وأنه كان ينفق على الفقراء في السنة مائة ألف درهم قال أحمد بن حنبل: "لم يكن في زمان ابن المبارك أطلب للعلم منه .. " وقال الذهبي: "كان رأسا في العلم، رأسا في الذكاء، رأسا في الشجاعة والجهاد، رأسا في الكرم". مات ب "هيت"، على الفرات، منصرفا من غزو الروم. وهو أول من صنف في الجهاد. من كتبه "تفسير القرآن" (١).
_________
= ٤٤٢ وحلية الأولياء ١: ٢٥٦ وصفة الصفوة ١: ٢٢٠ وكشف الظنون ٤٢٩، ٤٣٠ والشذرات ١: ٥٣ والإصابة ت ٤٨٨٩.
(١) تذكرة الحفاظ ١: ٢٧٤ وحلية الأولياء ٨: ١٦٢ وتاريخ بغداد ١: ١٥٢ وطبقات المفسرين للداودي ١: ٢٤٣ وشذرات الذهب ١: ٢٩٥ والرسالة =','1','320',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (564,'ابن أبي شيبة [١٥٩ - ٢٣٥ هـ / ٧٧٦ - ٨٤٩ م]

<span data-type="title" id=toc-787>عبد الله بن محمد بن أبي شيبة إبراهيم بن عثمان العبسي بالولاء، الكوفي، أبو بكر:</span>
محدث، حافظ، مفسر، روى عن ابن المبارك ووكيع بن الجراح، وروى عنه البخاري ومسلم وأبو داود وابن ماجة. وعاش في بغداد وتوفي بها. له "تفسير القرآن" (١).

الدينوري [.. - ٣٠٨ هـ / .. - ٩٢٠ م]

<span data-type="title" id=toc-788>عبد الله بن محمد بن وهب الدينوري، أبو محمد:</span>
محدث، حافظ، مفسر، رحال، من أهل الدينور. طوف الأقاليم وروى عن جماعة من كبار الشيوخ. من
_________
= المستطرفة ٣٧ والجواهر المضية ١: ٢٨١ والعبر ١: ٢٨٠ والنجوم الزاهرة ٢: ١٠٣ والرجال للقيسراني ٢٥٩ واللباب ١: ٣٣٤ والمشاهير لابن حبان ١٩٤ والتاريخ الكبير للبخاري ٣/ ١: ٢٠٢ وتاريخ التراث العربي ١: ٢٧٠ وتهذيب التهذيب ٥: ٣٨٢ وهدية العارفين ١: ٤٣٨.
(١) تذكرة الحفاظ ٤٣٢ وميزان الاعتدال ٧١ وتاريخ بغداد ١٠: ٦٦ والجرح والتعديل ٢: ١٦٠ والشذرات ٢: ٨٠ والنجوم الزاهزة ٢: ٣٨٢ والرسالة المستطرفة ٤٥ والعبر ١: ٤٢١ وطبقات المفسرين للداودي ١: ٢٤٦ والرجال للقيسراني ٢٥٩ وابن سعد ٦: ٢٨٨ وتهذيب التهذيب ٦: ٢ وتاريخ التراث العربي ١: ٢٩٤ والأعلام ٤: ٢٦٠ والبداية والنهاية ١٠: ٣١٥ وابن النديم ٢٨٥.','1','320',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (565,'آثاره "الواضح في تفسير القرآن" مخطوط (١).

ابن أخي رفيع [.. - ٣١٨ هـ / .. - ٩٣١ م]

<span data-type="title" id=toc-789>عبد الله بن محمد بن حسن بن عبد الله بن عبد الملك الكلاعي، مولاهم، أبو محمد، المعروف بابن أخي رفيع:</span>
محدث، حافظ، عارف بالتفسير، من أهل قرطبة، روى عن محمد بن وضاح وطبقته، وقد أدركه. وكان بصيرا بالرجال والعلل له "مختصر تفسير" بقي بن مخلد (٢).

الخزاز [.. - ٣٢٥ هـ / .. - ٩٣٧ م]

<span data-type="title" id=toc-790>عبد الله بن محمد بن سفيان الخزاز، أبو الحسن:</span>
نحوي، لغوي، من تلاميذ المبرد وثعلب. كان معلما في دار الوزير العادل علي بن عيسى بن داود الجراح الكاتب. قال القفطي: "وهو الذي عمل له كتاب "معاني القرآن" ونحله إياه، وكان
_________
(١) تذكرة الحفاظ ٧٥٤ وتاريخ التراث العربي ١: ٢٠٨ وميزان الاعتدال ٤٩٤ ولسان الميزان ٣: ٣٤٤ وطبقات المفسرين للداودي ١: ٢٤٤ والشذرات ٢: ٢٥٢ ومعجم المؤلفين ٦: ١٤٤.
(٢) تذكرة الحفاظ ٨٩١ وطبقات المفسرين للداودي ١: ٢٤٧ وتاريخ علماء الأندلس ١: ٢٦٢ والأعلام ٤: ٢٦٣ ومعجم المؤلفين ٦: ١١٦.','1','321',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (566,'مليح الخط صحيحه، من النحوين الذين خلطوا المذهبين". من تصانيفه "معاني القرآن" (١).

الحياني [٢٧٤ - ٣٦٩ هـ / ٨٨٧ - ٩٧٩ م]

<span data-type="title" id=toc-791>عبد الله بن محمد بن جعفر بن حيان الأنصاري الأصبهاني، أبو محمد، المعروف بأبي الشيخ:</span>
حافظ أصبهان ومسند زمانه، مؤرخ، مفسر، من أهل أصبهان، ونسبته إلى جده حيان. قال الذهبي: "كان مع سعة علمه وغزارة حفظه صالحا خيرا قانتا لله صدوقا". وقال تلميذه أبو نعيم: "كان أحد الأعلام، صنف "الأحكام" و "التفسير" وكان يفيد عن الشيوخ ويصنف لهم ستين سنة". من كتبه "تفسير القرآن" ذكره شيخ الإسلام ابن حجر في الإصابة (٢).
_________
(١) طبقات المفسرين للداودي ١: ٢٤٧ وإنباه الرواة ٢: ١٣٥ وبغية الوعاة ٢: ٥٥ وتاريخ بغداد ١٥: ١٢٣ وكشف الظنون ١٧٣٠ والمنتظم ٦: ٢٩ وتلخيص ابن مكتوم ٩٦ وطبقات النحاة لابن قاضي شهبة ١: ٤٦ ونزهة الألباء ٣٢٩ والبداية والنهاية ١١: ١٨٨ وهدية العارفين ١: ٤٤٥ وابن النديم ٩٠ والأعلام ٤: ٢٦٣.
(٢) تاريخ التراث العربي ١: ٤٩٦ وتذكرة الحفاظ ٣: ٩٤٥ وأخبار أصبهان ٢: ٩٠ والإصابة ١: ١٧٢ والعبر ٢: ٣٥١ والشذرات ٣: ٦٩ والنجوم الزاهرة ٤: ١٣٦ وهدية العارفين ١: ٤٤٧ وطبقات المفسرين =','1','321',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (567,'القباب [.. - ٣٧٠ هـ / .. - ٩٨٠ م]

<span data-type="title" id=toc-792>عبد الله بن محمد بن محمد بن فورك بن عطاء بن مهيار، أبو بكر القباب:</span>
إمام وقته، مقرىء، مفسر مشهور، من أهل أصبهان، روى عنه الهذلي، وقرأ عليه جماعة. قال الحافظ أبو العلاء: "من جلة قراء أصبهان، ومن العلماء بتفسير القرآن، كثير الحديث، ثقة نبيل" (١).

الهروي [٣٩٦ - ٤٨١ هـ / ١٠٠٦ - ١٠٨٩ م]

<span data-type="title" id=toc-793>عبد الله بن محمد بن علي بن محمد بن أحمد الأنصاري الهروي، أبو إسماعيل:</span>
شيخ خراسان في عصره، كان بارعا في اللغة، حافظا للحديث، عارفا بالتاريخ والأنساب، إماما كاملا في التفسير، مظهرا للسنة داعيا إليها. وهو من ذرية أبي أيوب الأنصاري. ولد ب "قندهار" وتوفي بهراة. امتحن مرات وأوذي. قال الذهبي: "كان سيفا مسلولا على المخالفين، وجذعا في أعين المتكلمين،
_________
= ١: ٢٤٠ واللباب ١: ٤٠٤ والكشف ٤٣٧ والأعلام ٤: ٢٦٤ والرسالة المستطرفة ٢٩.
(١) طبقات المفسرين ١: ٢٥١ وغاية النهاية ١: ٤٥٤ وتذكرة الحفاظ ٣: ٩٦٠ والعبر ٢: ٣٥٦ والشذرات ٣: ٧٢ والنجوم الزاهرة ٤: ١٣٩ واللباب ٣: ١٠.','1','322',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (568,'وطودا في السنة لا يتزلزل، خرج به خلق كثير، وفسر القرآن مدة، وفضائله كثيرة". له "تفسير القرآن" (١).

ابن ناقيا [٤١٠ - ٤٨٥ هـ / ١٠٢٠ - ١٠٩٢ م]

<span data-type="title" id=toc-794>عبد الله بن محمد بن الحسين بن ناقيا، أبو القاسم، ويقال له البندار:</span>
شاعر مترسل، أديب، له في العربية يد باسطة. من أهل بغداد، نشأ في فترة الحكم البويهي لها، ثم تقدم عند السلاجقة فصار من خاصة السلطان ملكشاه السلجوقي. رماه بعض القدماء بضعف ديانته وميله إلى المجانة. مات بببغداد، ودفن في مقابر باب الشام. من كتبه "الجمان في تشبيهات القرآن" حققه الدكتور مصطفى الصاوي الجويني، ونشر بالاسكندرية (١٩٧٤) (٢).
_________
(١) طبقات المفسرين ٢٥ وطبقات الداودي ١: ٢٤٩ وتذكرة الحفاظ ١١٨٣ والعبر ٣: ٢٩٧ وطبقات الحنابلة ٢: ٢٤٧ والنجوم الزاهرة ٥: ١٢٧ وهدية العارفين ١: ٤٥٢ وإيضاح المكنون ١: ٣١٠ والذيل على طبقات الحنابلة ١: ٦٤ وطبقات الحنابلة لابن الفراء ٤٠٠ والرسالة المستطرفة ٤٥ والشذرات ٣: ٣٦٥ والمنتظم ٩: ٤٤.
(٢) إنباه الرواة ٢: ١٣٣ والجواهر المضية ١: ٢٨٣ والمنتظم ٩: ٦٨ وبغية الوعاة ٢: ٦٧ وميزان الاعتدال ٢: ٥٣٣ ولسان الميزان ٣: ٢٨٤ وابن خلكان ٣: ٩٨','1','322',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (569,'ابن أبي جعفر [٤٤٧ - ٥٢٦ هـ / ١٠٥٥ - ١١٤٢ م]

<span data-type="title" id=toc-795>عبد الله بن محمد بن عبد الله الخشني، أبو محمد، المعروف بابن أبي جعفر:</span>
عالم بالتفسير، من فقهاء المالكية، من أهل مرسية، تعلم بها وبطليطلة وقرطبة. قال ابن بشكوال: "كان شيخ فقهاء وقته بشرق الأندلس، وأحفظهم للمذهب، مع المعرفة بالتفسير لكتاب الله، والتفنن في المعارف، والمشاركة في علوم، حج وسمع بمكة، وتوفي بمرسية" (١).

الكرجي [.. - ٥٧٧ هـ / .. - ١١٨١ م]

<span data-type="title" id=toc-796>عبد الله بن محمد بن عبد الكريم بن الحسن الكرجي، أبو محمد:</span>
مفسر، مناظر، من فقهاء الشافعية، من أهل قزوين. قال الرافعي: "إمام مرجوع إليه مقبول القول، صنف في "التفسير" مجموعا كبيرا" (٢). وهدية العارفين ١: ٤٥٣ وتاج التراجم ٢٦ ومقدمة كتاب الجمان للدكتور الجويني. ومقدمة "عقد الجمان" بتحقيق الدكتور زرزور ومحمد رضوان الداية. ومجلة الفيصل ٣١: ١٤٧.
_________
(١) طبقات الداودي ١: ٢٤٨ وبغية الملتمس ٣٢٤ والصلة ١: ٢٩٤.
(٢) طبقات الداودي ١: ٢٤٨ وتاريخ قزوين ٣٧٨.','1','323',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (570,'الرازي [٥٦٤ - ٦٥٤ هـ / ١١٦٨ - ١٢٥٦ م]

<span data-type="title" id=toc-797>عبد الله بن محمد بن شاهاور، أبو بكر، نجم الدين الأسدي الرازي:</span>
مفسر، متصوف. ولد بخوارزم. وتوفي ببغداد. من كتبه "بحر الحقائق والمعاني في تفسير السبع المثاني" مخطوط، الجزء الأول منه، في صوفيا (١).

البلخي [٦١١ - ٦٩٨ هـ / ١٢١٤ - ١٢٩٩ م]

<span data-type="title" id=toc-798>عبد الله بن محمد بن سليمان البلخي، جمال الدين:</span>
مفسر، من فقهاء الحنفية. مولده ووفاته بالقدس، أقام مدة بالأزهر. من آثاره كتاب كبير في "التفسير" جمعه من خمسين تفسيرا" (٢).

المرجاني [٦٣٣ - ٦٩٩ هـ / ١٢٣٥ - ١٣٠٠ م]

<span data-type="title" id=toc-799>عبد الله بن محمد بن عبد الملك بن عبد الله، أبو محمد المرجاني:</span>
صوفي، مفسر، مؤرخ، أصله من تونس، ولد بالاسكندرية ومات بتونس. أملى دروسا
_________
(١) دار الكتب الشعبية ٤٤ والشذرات ٥: ٢٦٥ وكشف الظنون ٢٢٤ وهدية العارفين ١: ٤٦١ ومعجم المؤلفين ٦: ١٢٢.
(٢) البداية والنهاية ١٤: ٤.','1','323',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (571,'بالتفسير جمعها ابن السكري من كلامه وسماها "الفتوحات الربانية في المواعيد المرجانية" مخطوط، في التيمورية (١).

السكسكي [.. - ٧٦٤ هـ / .. - ١٣٦٣ م]

<span data-type="title" id=toc-800>عبد الله بن محمد بن عمر بن أبي بكر بن إسماعيل البريهي، ثم السكسكي، أبو محمد:</span>
عالم بالتفسير والحديث والفقه والنحو واللغة والتصوف، من الزهاد، اشتغل بالتدريس. قال الخزرجي: "كان متفننا في العلوم، مبارك التدريس، عظيم الصبر على الطلبة، كثير الحج" (٢).

ابن مفلح [٧٥٠ - ٨٣٤ هـ / ١٣٤٩ - ١٤٣١ م]

<span data-type="title" id=toc-801>عبد الله بن محمد بن مفلح بن محمد بن مفرج، أبو محمد، شرف الدين المقدسي الراميني ثم الدمشقي الصالحي:</span>
شيخ الحنابلة في عصره. نشأ في دمشق وأخذ عن علمائها. قال صاحب الشذرات: "كان علامة في الفقه، أستاذا في الأصول، بارعا في التفسير والحديث، أفتى ودرس
_________
(١) الشذرات ٥: ٤٥١ وهدية العارفين ١: ٤٦٣ وكشف الظنون ١٢٣٧ والتيمورية ٣: ٢٧٦ وشجرة النور ١٩٣.
(٢) طبقات الداودي ١: ٢٥٠ وبغية الوعاة ٢: ٥٩ والعقود اللؤلؤية ٢: ١٣٠.','1','324',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (572,'وناظر". وقال ابن حجر: "انتهت إليه رياسة الحنابلة في زمانه، وعين للقضاء غير مرة فلم يتفق ذلك" (١).

النجري [٨٢٥ - ٨٧٧ هـ / ١٤٢٢ - ١٤٧٣ م]

<span data-type="title" id=toc-802>عبد الله بن محمد بن أبي القاسم بن علي الزيدي العبسي العكي المعروف بالنجري:</span>
فقيه زيدي، أصولي، عارف بالتفسير، نسبته إلى نجرة - قرية باليمن - ولد ونشأ بمدينة حوث، وقدم مكة، ثم القاهرة، وعاد، فتوفي بقرية القابل، في الشمال الغربي من صنعاء. من تصانيفه "شفاء العليل في شرح خمسمائة آية من التنزيل" مخطوط (٢).

الكردي [.. - ١٠٦٤ هـ / .. - ١٦٥٣ م]

<span data-type="title" id=toc-803>عبد الله بن محمد الكردي:</span>
قاض، فقيه، مفسر، ولي القضاء بالمدينة المنورة. له "حاشية على تفسير البيضاوي" (٣).
_________
(١) الشذرات ٧: ٢٠٨ وإنباء الغمر ٣: ٤٦٣ والضوء اللامع ٥: ٦٦.
(٢) البدر الطالع ١: ٣٩٧ والضوء اللامع ٥: ٦٢ وعلوم القرآن ٢٥٨.
(٣) هدية العارفين ١: ٤٧٧ وإيضاح المكنون ١: ١٤٢.','1','324',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (573,'يوسف زاده [١٠٨٥ - ١١٦٧ هـ / ١٦٧٤ - ١٧٥٤ م]

<span data-type="title" id=toc-804>عبد الله بن محمد بن يوسف بن عبد المنان الرومي، المعروف بعبد الله حلمي، ويوسف زاده، ويوسف أفندي، والأماسي:</span>
فقيه حنفي، عالم بالتفسير والقراءات والحديث. ولد في أماسية، بتركيا. واتصل بالسلطان أحمد والسلطان محمود، العثمانيين، فعرفا قدره وأكرماه، وعينه السلطان محمود مدرسا بدار الكتب التي بناها داخل قصره بالآستانة، فاستمر إلى أن توفي. من آثاره "زبدة العرفان في وجوه القرآن" مخطوط، و "حاشية على أنوار التنزيل" للبيضاوي، في التفسير (١).

آلتوني جوق زاده [.. - ١١٨٣ هـ / .. - ١٧٦٩ م]

<span data-type="title" id=toc-805>عبد الله بن محمد، المعروف بآلتوني جوق زاده:</span>
قاض، من علماء الأحناف وأعيانهم، ولد بالقسطنطينية، وبها نشأ وتعلم، ولي قضاء القدس، فقضاء المدينة
_________
(١) هدية العارفين ١: ٤٨٢ وسلك الدرر ٣: ٨٧ وإيضاح المكنون ١: ١٤٢ والتيمورية ٣: ٣١٨ والأزهرية ١: ٤٤ ومعجم المؤلفين ٦: ١٤٥ والأعلام ٤: ٢٧٤.','1','325',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (574,'المنورة، فقضاء الجيش بالأناضول. التوني جوق زاده معناه بالعربية ابن كثير الذهب. من آثاره "جمع الحاوي في شرح تفسير البيضاوي" حاشية على أنوار التنزيل" (١).

ابن الشيخ [١١٦٥ - ١٢٤٢ هـ / ١٧٥٢ - ١٨٢٦ م]

<span data-type="title" id=toc-806>عبد الله بن محمد بن عبد الوهاب:</span>
فقيه حنبلي، عالم بالتفسير والعقائد وعلوم العربية. ولد ونشأ وتعلم في "الدرعية"، وكان مرجع قضاة نجد في عهد الإمام عبد العزيز بن محمد بن سعود، وابنه سعود، وحفيده عبد الله بن سعود، اعتقله إبراهيم باشا (ابن محمد علي باشا والي مصر) بعد استيلائه على الدرعية سنة ١٢٣٣ هـ، ونقله إلى مصر، وتوفي بها (٢).

شبر [١٨٨١ - ١٢٤٢ هـ / ١٧٧٤ - ١٨٢٧ م]

<span data-type="title" id=toc-807>عبد الله بن محمد رضا شبر الحسيني الكاظمي:</span>
مجتهد إمامي، مفسر، ولد بالنجف، وعاش بالكاظمية والحلة، وتوفي بالكرخ ببغداد. كان ينعت بالمجلسي
_________
(١) إيضاح المكنون ١: ١٣٩ و ٣٦٦ وسلك الدرر ٣: ١٠٦ وهدية العارفين ١: ٤٨٥.
(٢) مشاهير علماء نجد ٤٨.','1','325',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (575,'الثاني. من آثاره "الوجيز في تفسير القرآن" ويعرف بتفسير شبر، (ط ٢: القاهرة ١٩٦٦) (١).

رئيس القراء [.. - ١٢٥٢ هـ / .. - ١٨٣٦ م]

<span data-type="title" id=toc-808>عبد الله بن محمد صالح الأيوبي، المعروف برئيس القراء:</span>
مقرىء، واعظ، مفسر، من علماء الروم، ولي الإمامة وتدريس العلوم الآلية في جامع أبي أيوب الأنصاري بإسطنبول، فعرف بالأيوبي. من آثاره "تفسير سورة الفتح" (٢).

النبراوي [.. - ١٢٧٥ هـ / .. - ١٨٥٩ م]

<span data-type="title" id=toc-809>عبد الله بن محمد النبراوي:</span>
فقيه شافعي، مفسر، فرضي، ولد وأقام ببنها العسل، وأصله من نبروه من غربية مصر. توفي بالقاهرة. من آثاره "قرة العين ونزهة
_________
(١) فوائد الرضوية ٢٤٩ ومعجم المؤلفين العراقيين ٢: ٣٢٧ ومعارف الرجال ٢: ٩ ومجلة البذرة س أع ٤: ٢١٢ وروضات الجنات ٣٧٤ وبروكلمان الذيل ٢: ٧٩٣ ورجال الفكر ٢٤٥ وهكذا عرفتهم ٣: ٤١ ومعجم المؤلفين ٦: ١١٨.
(٢) هدية العارفين ١: ٤٨٩ والتيمورية ٣: ١٩٤ وعثمانلي مؤلفلري ٣٧٩ ومعجم المؤلفين ٦: ١٢٣.','1','326',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (576,'الفؤاد" مخطوط، على تفسير الجلالين، في أربعة مجلدات (١).

العلمي [١٢٧٨ - ١٣٥٥ هـ / ١٨٦١ - ١٩٣٦ م]

<span data-type="title" id=toc-810>عبد الله بن محمد بن صلاح الدين العلمي، الحسني نسبا، الغزي مولدا، الدمشقي استقرارا ووفاة:</span>
مفسر، مناظر، فقيه، مشارك في بعض العلوم. تعلم بغزة والأزهر، ودرس بغزة وبيروت، ثم اختير رئيسا لبلدية غزة فمفتشا لمعارفها. وفي سنة ١٣٣٦ هـ (١٩١٨ م) هاجر بأسرته إلى دمشق، فكان من أعضاء المؤتمر السوري الأول، وألقى دروسا في التفسير والإرشاد الديني في الجامع الأموي، إلى أن توفي. من آثاره "تفسير مشكلات القرآن" مخطوط، و "مؤتمر تفسير سورة يوسف" طبع، مجلدان (٢).

الأشني [.. - حيا ١١١٨ هـ / .. - ١٧٠٦ م]

<span data-type="title" id=toc-811>عبد الله بن محمود الأشني:</span>
فاضل، له اشتغال بالتفسير. من آثاره "الحدائق
_________
(١) معجم المطبوعات ١٨٣٧ وهدية العارفين ١: ٤٩٠ وفهرس الأزهرية (ط: ٢) ١: ٢٨١ والتيمورية ٢: ٤٠٠ ومعجم المؤلفين ٦: ١٤٢.
(٢) مؤتمر تفسير سورة يوسف، مفدمته. الأعلام الشرقية ٢: ١٣٤ ومعجم المطبوعات ١٣٤٩.','1','326',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (577,'القدسي في تفسير آية الكرسي" أوله: "الحمد لله الذي انفرد ذاته بوجوب الوجود والبقاء الخ .. ". قال آغا بزرك الطهراني: "والنسخة في كتب المولى محمد علي الخوانساري، كتابتها في سنة ١١١٨ هـ " (١)

ابن مسعود [.. - ٣٢ هـ / .. - ٦٥٣ م]

<span data-type="title" id=toc-812>عبد الله بن مسعود بن غافل بن حبيب الهذلي، أبو عبد الرحمن:</span>
من كبار الصحابة وأحفظهم للقرآن وأقرئهم له، ومن أوائل المحدثين والمفسرين والفقهاء، ومن السابقين إلى الإسلام، وأول من جهر بقراءة القرآن بمكة وأسمعه قريشا بعد رسول الله - صلى الله عليه وسلم -. وهو من أهل مكة، وكان خادم رسول الله - صلى الله عليه وسلم - الأمين، وصاحب سره، ورفيقه في حله وترحاله. هاجر إلى الحبشة ثم إلى المدينة، وشهد الغزوات كلها. وبعد وفاة النبي - صلى الله عليه وسلم - وقف إلى جانب أبي بكر في حروب الردة، وأرسله عمر إلى الكوفة ليشرف على بيت المال ويعلم الناس أحكام الدين. ثم قدم المدينة في خلافة عثمان، فتوفي فيها عن نحو ستين عاما. كتب بيده "مصحفا" يسمى مصحف ابن مسعود. قال الجبوري: "وقد نقل تفسير ابن مسعود
_________
(١) تعليقات آغا بزرك الطهراني على كشف الظنون.','1','327',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (578,'أهل الكوفة الذين عاش بين ظهرانيهم وأخذوا عنه وتعلموا منه ورووا ما عرفوه إلى من جاء بعدهم، حتى أصبح نواة لمدرسة تفسيرة أنشئت بعد ذلك، وهي مدرسة التفسير في الكوفة" (١).

ابن قتيبة [٢١٣ - ٢٧٦ هـ / ٨٢٨ - ٨٨٩ م]

<span data-type="title" id=toc-813>عبد الله بن مسلم بن قتيبة الدينوري، أبو محمد:</span>
العالم، الناقد، الأديب، ومن المصنفين المكثرين. اختلف مترجموه في موطن ولادته، قال فريق أنه ولد بالكوفة، وقال فريق آخر أنه ولد ببغداد، وفريق ثالث لا يجزم برأي ويقول أنه ولد ببغداد، وقيل بالكوفة. والأرجح أنه ولد بالكوفة وسكن بغداد، فالذين ذكروا ذلك أسبق من غيرهم مثل ابن النديم وابن الأنباري. ولأن بعض الثقات يذكر أنه "نزيل بغداد أو سكن بغداد". أخذ عن علماء بغداد علوم
_________
(١) التفسير والمفسرون ١: ٨٣ ونهاية الأرب ١٨: ٢٢٥ والطبقات الكبرى ٣: ١٥٠ وغاية النهاية ١: ٤٥٨ والمحبر ١٦١ وكشف الظنون ١: ٤٢٩ والكامل في التاريخ ١: ١٧ وانظر فهرسته. والإصابة ترجمة رقم ٤٩٥٥ وصفة الصفوة ١: ١٥٤ وتاريخ الطبري ١: ٣٩ و ٤: ٢٧٣ وانظر فهرسته، والبيان والتبيين ١: ١٠٤ والشذرات ١: ٣٨ وإجمال البيان في مبعث من علوم القرآن ١: ٤٨ واللآلىء الحسان في علوم القرآن ٣٦٠ والإسرائيليات في كتب التفسير ٨٦ والموسوعة العربية الميسرة ١١٧٩ والقرآن والتفسير ٩١ وما بعدها.','1','327',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (579,'الحديث والفقه واللغة والتفسير والنحو والأدب والأخبار. وولي قضاء "الدينور" وقضى وقتا من حياته فيها، ولذلك ينسب إليها. وتوفي ببغداد. من آثاره الكثيرة "مشكل القرآن" وقد طبع بتحقيق الأستاذ سيد أحمد صقر، و "غريب القرآن" (١).

ابن آمنة [.. - ٣٤٠ هـ / .. - ٩٥٠ م]

<span data-type="title" id=toc-814>عبد الله بن مطرف بن محمد، أبو محمد، المعروف بابن آمنة:</span>
مفسر، من فقهاء المالكية، من أهل قرطبة بالأندلس، أخذ عن علمائها، ورحل إلى المشرق ولقي الشيوخ، ورجع إلى بلده فتوفي بها. من آثاره "تفسير القرآن" (٢).
_________
(١) ابن قتيبة للدكتور عبد الحميد الجندي، وإنباه الرواة ٢: ١٤٣ وبغية الوعاة ٢: ٦٣ وطبقات المفسرين ١: ٢٤٥ وتاريخ بغداد ١٠: ٧ وتذكرة الحفاظ ٦٣١ ونزهة الألباء ٢٠٩ ومرآة الجنان ٢: ١٩١ وطبقات النحاة لابن قاضي شهبة ٢: ٥٢ ولسان الميزان ٣: ٣٥٧ وابن خلكان ٣: ٤٢ وروضات الجنات ٤٤٧ وميزان الاعتدال ٢: ٥٠٣ والعبر: ٥٦ والبداية والنهاية ١١: ٤٨ وابن النديم ٧٧ والأعلام ٤: ٢٨٠ ومعجم المؤلفين ٦: ١٥٠ وفيه ثبت بمصادر أخرى.
(٢) جذوة المقبس ١: ٢٦٩ وهدية العارفين ١: ٤٤٥ وإيضاح المكنون ١: ٣٠٢ ومعجم المؤلفين ٦: ١٥٣.','1','328',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (580,'الأقفسهي [بعد ٧٤٠ - ٨٢٣ هـ / بعد ١٣٣٩ - ١٤٢٠ م]

<span data-type="title" id=toc-815>عبد الله بن مقداد بن إسماعيل بن عبد الله الأقفسهي، ثم القاهري، ويعرف بالأقفاصي، جمال الدين:</span>
قاض، مفسر، فقيه، إنتهت إليه رياسة المالكية في عصره. أخذ عن الشيخ خليل وغيره، وناب في الحكم مدة، ثم ولي القضاء مرارا، ومات وهو على القضاء في أواخر الدولة المؤيدية. أثنى عليه ابن حجر والسخاوي والمقريزي من آثاره (تفسير القرآن) في ثلاث مجلدات، قال السخاوي: لم يشتهر (١).

ابن وهب [١٢٥ - ١٩٧ هـ / ٧٤٣ - ٨١٣ م]

<span data-type="title" id=toc-816>عبد الله بن وهب بن مسلم الفهري بالولاء، المصري، أبو محمد:</span>
من أئمة فقهاء المالكية، جمع بين الفقه والحديث والعبادة. ولد بمصر، وروى عن عدد من العلماء، وصحب الإمام مالك بن أنس عشرين سنة، عرض عليه القضاء فخبأ نفسه ولزم منزله. وكان حافظا ثقة مجتهدا. قال أبو زرعة: "نظرت في نحو ثلاثين ألف
_________
(١) الضوء اللامع ٥: ٧١ وأنباء الغمر ورفع الأصر ٢: ٣٠٣ والمقريزي ونيل الابتهاج ١٥٥ والشذرات ٧: ١٦٠ وهديه العارفين ١: ٤٦٨.','1','328',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (581,'حديث لابن وهب ولا أعلم أني رأيت له حديثا لا أصل له، وهو ثقة". مات بمصر، قيل: "قرىء عليه كتابه "أهوال القيامة" فخر مغشيا عليه فلم يتكلم بكلمة حتى مات بعد أيام" من كتبه "تفسير القرآن" ذكره الثعلبي في كتابه الكشف والبيان" (١).

اليزيدي [.. - ٢٣٧ هـ / .. - ٨٥١ م]

<span data-type="title" id=toc-817>عبد الله بن يحيى بن المبارك بن المغيرة العدوي، البغدادي، المعروف باليزيدي، أبو عبد الرحمن:</span>
أديب، شاعر، عالم بالنحو واللغة، مقرىء، من أهل بغداد، أخذ عن يحيى بن زياد الفراء وغيره. قال ابن الأنباري: "وذكر المهلبي أن اليزيدي خرج مع المعتصم إلى مصر، ومات بها". له تصانيف منها
_________
(١) تذكرة الحفاظ ١: ٣٠٤ وكشف الظنون ١٤٤٠ وشذرات الذهب ١: ٣٤٧ ووفيات الأعيان ٣: ٣٦ وترتيب المدارك ٢: ٤٢١ والعبر ١: ٣٢٢ والانتقاء ٤٨ وتهذيب التهذيب ٦: ٧١ والديباج ١٣٢ والشذرات ١: ٣٤٧ وغاية النهاية ١: ٤٦٣ ومرآة الجنان ١: ٤٥٨ وبروكلمان: الذيل ١: ٢٥٧ والطبقات الكبرى ٧: ٥١٨ والأدب المصري ٣٨ ومرآة الجنان ١: ٤٥٨ وطبقات الفقهاء ١٢٧ والرجال للقيسراني ٢٦٠ والجرح والتعديل ٢/ ٢: ١٨٩ وتاريخ التراث العربي ٢: ١٣٤.','1','329',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (582,'"غريب القرآن" قال الداني: "وهو كتاب حسن". واليزيدي منسوب إلى يزيد بن منصور بن عبد الله بن يزيد الحميري، خال المهدي العباسي، وكان أبوه يحيى بن المبارك منقطعا إليه. وقد خلط بعض المؤرخين بين مؤلفات صاحب الترجمة ومؤلفات أبيه فعد بعضهم كتاب "غريب القرآن" من بين مؤلفات يحيى (١).

الجويني [.. - ٤٣٨ هـ / .. - ١٠٤٧ م]

<span data-type="title" id=toc-818>عبد الله بن يوسف بن محمد بن حيويه ابن عبد الله الجويني، أبو محمد:</span>
عالم بالتفسير واللغة، من أعيان فقهاء الشافعية، كان يلقب ركن الإسلام. ولد في جوين من نواحي نيسابور، وبها نشأ وتعلم، ثم رحل في طلب العلم فأخذ عن علماء نيسابور ومرو. ولازم القفال المروزي حتى تخرج به مذهبا وخلافا، وأتقن
_________
(١) طبقات المفسرين ١: ٢٥٢ وغاية النهاية ١: ٤٦٣ ونزهة الألباء ١٤٨ وبغية الوعاة ١: ٢٦٥ وطبقات الزبيدي ٧٨ وطبقات ابن المعتز ٣٢٨ وتاريخ بغداد ٣: ٤١٢ وإنباه الرواة ٢: ١٣٤ وهو فيه: عبد الله بن محمد. والفهرست ٥٧ والأغاني ١٨: ٧٣ وتلخيص ابن مكتوم ٢٣٨ ومعجم المؤلفين ٦: ١٣٩ و ١٦٣ وهدية العارفين ١: ٤٤٠.','1','329',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (583,'طريقته، ثم عاد إلى نيسابور فدرس بها إلى أن توفي. قال شيخ الإسلام أبو عثمان الصابوني: "لو كان الجويني في بني إسرائيل لنقلت لنا أوصافه وافتخروا به". وهو والد إمام الحرمين الجويني، من كتبه "تفسير القرآن الكريم" وهو تفسير كبير فسر فيه كل آية بعشرة أوجه. قال الداودي: "يشتمل على عشرة أنواع من العلوم في كل آية" (١).

ابن هشام [٧٠٨ - ٧٦١ هـ / ١٣٠٩ - ١٣٦٠ م]

<span data-type="title" id=toc-819>عبد الله بن يوسف بن أحمد بن عبد الله ابن يوسف، أبو محمد، جمال الدين، ابن هشام:</span>
إمام نحاة عصره. مولده ووفاته بالقاهرة. درس على ابن المرحل وابن السراج والتاج التبريزي وغيرهم. واشتغل بالتدريس في القبة المنصورية والمدرسة الحنبلية. واشتهر، فقورن بسيبويه، قال
_________
(١) طبقات المفسرين للسيوطي ١٥ وطبقات المفسرين للداودي ١: ٢٥٣ وطبقات الشافعية الكبرى ٥: ٧٣ وطبقات ابن هداية ١٤٤ وطبقات العبادي ٢١٢ ومفتاح السعادة ٢: ٣٢٤ والبداية والنهاية ١٢: ٥٥ ووفيات الأعيان ٣: ٤٧ والعبر ٣: ١٨٨ وشذرات الذهب ٣: ٢٦١ والنجوم الزاهرة ٥: ٤٢ ومرآة الجنان ٣: ٥٨ ومعجم البلدان مادة "جوين" وهدية العارفين ١: ٤٥١ وكشف الظنون ٤٤٥ ودمية القصر ١: ٥٣٦ و ٢: ٩٩٨ واللباب ١: ٣١٥.','1','330',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (584,'ابن حجر العسقلاني: "أتقن العربية ففاق الأقران بل الشيوخ، وتخرج به جماعة من أهل مصر وغيرهم. وانفرد بالفوائد الغريبة والمباحث الدقيقة، والاستدراكات العجيبة، والتحقيق البالغ، والاطلاع المفرط، والاقتدار على التصرف بالكلام". وقال ابن خلدون: "ما زلنا ونحن بالمغرب نسمع أنه ظهر بمصر عالم بالعربية يقال له ابن هشام أنحى من سيبويه". "إقتدى بابن جني وامتاز بالابتكار في منهجه، والتيسير في طريقة عرضه، والانفراد ببعض آرائه". من كتبه "تلخيص" كتابي "الإنصاف" و "الإنتصاف" من تفسير الكشاف، قال صاحب كشف الظنون: "هو مختصر لطيف مع يسير زيادة، قال في مقدمته: "اختصرت فيه الانتصاف في الكشاف، وحذفت منه ما وقعت الإطالة به من نقل كلام الزمخشري على وجهه من غير كلام عليه إعجابا به واستحسانا له، وما قابل به الزمخشري في سبه أهل السنة بمثلها، مقتصرا على العقيدة الصحيحة وما يتعلق بالآية منها من دليل وحمل على تأويل، فلم أدع شيئا من معاني الكتاب المذكور، فما وافق منه الصواب أبقيته بحاله، وما خالف ذلك بينت وجه ضعفه وإخلاله" (١).
_________
(١) كشف الظنون ١٤٧٧ والدرر الكامنة ٢: ٤٥١ ومفتاح السعادة ١: ١٥٩ ومعجم المطبوعات ٢٧٣ =','1','330',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (585,'السيواسي [٩٧١ - ١٠٤٩ هـ / ١٥٦٤ - ١٦٣٩ م]

<span data-type="title" id=toc-820>عبد المجيد (شمس الدين) بن محرم (أبي الليث) بن محمد (أبي البركات) مجد الدين، أبو الخير السيواسي:</span>
محدث، مفسر، واعظ، صوفي، من علماء الدولة العثمانية. ولد ونشأ بقرية زيلة. وانتقل إلى سيواس فولي مشيخة زاويتها. ثم استدعاه السلطان محمد الثالث إلى القسطنطينية، فأقام بها للوعظ والإرشاد إلى أن توفي. له تصانيف كثيرة منها "تفسير سورة الفاتحة" (١).

الرومي [.. - ٩٦٠ هـ / .. - ١٥٥٢ م]

<span data-type="title" id=toc-821>عبد المجيد بن نصوح بن إسرائيل الرومي:</span>
فقيه حنفي، مفسر، صوفي، من أهل أماسية. من كتبه "تذكرة أولي الألباب" رسالة في التفسير، قال صاحب كشف الظنون: "جمعها من التفاسير فوجد اثنتي عشرة آية، الحمد لله الذي نور قلوب
_________
= والنجوم الزاهرة ١٠: ٣٣٦ وشذرات الذهب ٦: ١٩١ وبغية الوعاة ٢: ٦٨ وهدية العارفين ١: ٤٦٥ وروضات الجنات ٤٥٥ ودائرة المعارف الإسلامية (ط ١) ١: ٢٥٩ والبدر الطالع ١: ٤٠٠ والأعلام ٤: ١٤٧ ومعجم المؤلفين ٦: ١٦٣.
(١) هدية العارفين ١: ٦٢٠ ومعجم المؤلفين ٦: ١٧٠ وعثمانلي مؤلفلري ١: ١٢٠ والأعلام ٤: ١٥٠.','1','331',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (586,'العلماء .. "". وله "الخوف والحزن". قال صاحب الكشف: "جمع من التفاسير أربع عشرة آية وصف الله تعالى عباده المؤمنين فيها بعدم الخوف والحزن، أولها الحمد لله الذي جعل عباده ... " وغير ذلك. وذكر البغدادي في هدية العارفين أنه مات سنة ٩٦٠ هـ، ثم ذكر في إيضاح المكنون أنه مات سنة ٩٩٦ هـ (١).

الكوراني [.. - ١٠٤٠ هـ / .. - ١٦٤١ م]

<span data-type="title" id=toc-822>عبد المحسن بن سليمان الكوراني الكردي:</span>
مفسر، من فقهاء الشافعية. كان مدرسا في روضة الرسول - صلى الله عليه وسلم - بالمدينة. من آثاره "جامع الأسرار في التفسير، قال صاحب كشف الظنون: أوله "الحمد لله الذي كان ولم يكن معه شيء من الأكوان ... الخ. ذكر فيه أنه صنفه تفسيرا جامعا للظهر والبطن إجابة لسؤال بعض إخوانه، فكتب إلى سورة الأعراف، وأهداه إلى السلطان مراد الرابع" (٢).

ابن جندي [.. - ٥٨٦ هـ / .. - ١١٩٠ م]

<span data-type="title" id=toc-823>عبد المحمود بن أحمد بن علي، أبو محمد، ويعرف بابن جندي:</span>
عارف بالنحو
_________
(١) هدية العارفين ١: ٦٤٠ وكشف الظنون ٨٥٢ و ٨٦٣.
(٢) كشف الظنون ٤٥٧ و ٥٣٤ وهدية العارفين ١: ٦٢.','1','331',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (587,'والعربية والتفسير، ولد بقرية تعرف بالحدادية، وتفقه بواسط والكوفة والبصرة ومكة وبغداد. قال في طبقات المفسرين: "ودرس الفقه، وذكر التفسير، وأفتى، ومات بواسط (١).

السخاوي [.. - حيا سنة ٩٦٠ هـ / .. - حيا ١٥٥٣ م]

<span data-type="title" id=toc-824>عبد المعطي بن أحمد بن محمد السخاوي المدني:</span>
مفسر، مؤرخ، فقيه، من بيت علم وفضل، من أهل المدينة المنورة، وأصله من سخا بمصر، وإليها نسبته. له "فتح المجيد" في تفسير القرآن في ستة أسفار (٢).

باكثير [٩٠٥ - ٩٨٩ هـ / ١٥٠٠ - ١٥٨١ م]

<span data-type="title" id=toc-825>عبد المعطي بن حسن بن عبد الله باكثير المكي ثم الحضرمي:</span>
فقيه شافعي، عارف بالتفسير والحديث، ولد بمكة، وبها تعلم. وانتقل إلى الهند فتوفي بأحمد أباد (٣).
_________
(١) طبقات المفسرين للداودي ١: ٣٥٦ والتكلمة لوفيات النقلة ١: ٣٣٢.
(٢) كشف الظنون ٤٥٣ ونيل الابتهاج ١٨٨ وشجرة النور ٢٧٩ وبروكلمان، الذيل ٢: ٤٥١ ومعجم المؤلفين ٦: ١٧٦.
(٣) الأعلام ٤: ٢٩٩ والشذرات ٨: ٤١٧ والنور السافر ٣٦٤ وهدية العارفين ١: ٦٢٢ ومعجم المؤلفين ٦: ١٧٦ وإيضاح المكنون ١: ٨٠.','1','332',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (588,'ابن حبيب [١٧٤ - ٢٣٨ هـ / ٧٩٠ - ٨٥٣ م]

<span data-type="title" id=toc-826>عبد الملك بن حبيب بن سليمان بن هارون السلمي الإلبيري القرطبي، أبو مروان:</span>
عالم بالتفسير والفقه والتاريخ والأدب. أصله من طليطلة، من بني سليم، أو من مواليهم. ولد في إلبيرة، وسكن قرطبة، ورحل إلى المشرق سنة ٢٠٨ هـ فسمع جماعة من كبار المحدثين. وعاد سنة ٢١٦ هـ فرتبه الأمير عبد الرحمن بن الحكم في طبقة المفتين بقرطبة، وتوفي بها. قيل لسحنون: مات ابن حبيب، فقال: مات عالم الأندلس، بل والله عالم الدنيا". من كتبه الكثيرة "إعراب القرآن" و "الناسخ والمنسوخ" و "تفسير القرآن" ستون جزءا و "رغائب القرآن" (١).
_________
(١) طبقات الداودي ١: ٣٤٧ وكشف الظنون ٩٠٩ ولسان الميزان ٤: ٥٩ وبغية الملتمس ٣٦٤ وبغية الوعاة ٢: ١٠٩ وترتيب المدارك ٣: ٣٠ والديباج ١٥٤ وابن الفرضي ٢٦٩ وتذكرة الحفاظ ٢: ٥٣٧ وتهذيب التهذيب ٦:. ٣٩٠ وجذوة المقتبس ٢٦٣ وميزان الا عتدال ٢: ٦٥٢ وإنباه الرواة ٢: ٢٠٦ والشذرات ٢: ٩٠ والنجوم الزاهرة ٢: ٢٩٣ ومرآة الجنان ٢: ١٢٢ والعبر ١: ٤٢٧ ونفح الطيب ٢: ٥ والمطمح ٣٦ وابن عذاري ٢: ١٦٤ وهدية العارفين ١: ٦٢٤ والمغرب ٢: ٩٦ وطبقات النحاة لابن قاضي شهبة ٢: ١٠٠ وطبقات النحويين للزبيدي ٢٦٠ والأعلام ٤: ٣٠٢.','1','332',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (589,'الديلمي [؟ -؟ هـ /؟ -؟ م]

<span data-type="title" id=toc-827>عبد الملك الديلمي:</span>
متصوف، مفسر، ذكره صاحب كشف الظنون وقال: له "فتوح الرحمن في إشارات القرآن وتفسيره" أوله: الحمد لله حق حمده .. فهذا تفسير بعض آيات القرآن التي يحتاج إليها الصوفية في أحوالهم .. ". ولم أعثر له على ترجمة وافية فيما بين يدي الساعة من كتب الرجال (١).

ابن دعسين [٩٥٢ - ١٠٠٦ هـ /١٥٤٥ - ١٥٩٧ م]

<span data-type="title" id=toc-828>عبد الملك بن عبد السلام بن عبد الحفيظ بن عبد الله بن دعسين الأموي القرشي:</span>
من أئمة اليمن. كان عالما بالتفسير والحديث والفقه والنحو والأدب والنسب، وله نظم. توفي في مخا (٢).

ابن جريج [٨٠ - ١٥٠ هـ / ٦٩٩ - ٧٦٧ م]

<span data-type="title" id=toc-829>عبد الملك بن عبد العزيز بن جريج الأموي بالولاء، أبو الوليد وأبو خالد:</span>
فقيه الحرم المكي، وإمام أهل الحجاز في عصره، وأول مكي رتب الأحاديث ترتيبا
_________
(١) كشف الظنون ١٢٤٠.
(٢) ملحق البدر الطالع ١٤١ وخلاصة الأثر ٣: ٨٨ وهدية العارفين ١: ٦٢٧ والأعلام ٤: ٣٠٥ ومعجم المؤلفين ٦: ١٨٣.','1','333',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (590,'منهجيا. رومي الأصل، ولد بمكة، وقدم العراق، وحدث بالبصرة. قال أحمد بن حنبل: "كان من أوعية العلم". وقال الذهبي: "كان ثبتا، لكنه يدلس". مات بمكة. له "تفسير القرآن". قال فؤاد سزجين: "يعتمد فيما يبدو على تفسير ابن عباس، وعكرمة، ومجاهد، وعطاء بن أبي رباح، واقتبسه الطبري برواية القاسم بن الحسن الهمذاني، عن الحسين بن داود المصيصي، عن الحجاج بن محمد المصيصي. واستخدمه الثعلبي برواية علي بن المبارك الصنعاني" وقال آغا بزرك: "كان عند السيد ابن طاووس نسخة عتيقة منه ينقل عنها في كتابه سعد السعود المؤلف سنة ٦٥١ هـ (١).

إمام الحرمين [٤١٩ - ٤٧٨ هـ / ١٠٢٨ - ١٠٨٥ م]

<span data-type="title" id=toc-830>عبد الملك بن عبد الله بن يوسف بن محمد الجويني، أبو المعالي، ركن الدين، ويقال ضياء الدين، الملقب بإمام الحرمين:</span>
من أكابر فقهاء الشافعية، أصولي،
_________
(١) تذكرة الحفاظ ١: ١٦٩ وابن خلكان ٣: ١٦٣ وتهذيب التهذيب ٦: ٤٠٢ والعبر ١: ٢١٣ وتاريخ بغداد ١٥: ٤٠٠ وميزان الاعتدال ٢: ٦٥٩ وكشف الظنون ٤٣٧ وغاية النهاية ١: ٤٦٩ وهدية العارفين ١: ٦٢٣ وطبقات المفسرين للداودي ١: ٣٥٢ وصفة الصفوة ٢: ١٢٢ وطبقات المدلسين ١٥ والأعلام ٤: ٣٠٥.','1','333',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (591,'متلكم، مفسر، ولد في جوين - من قرى نيسابور - وبها نشأ وتعلم. ورحل إلى بغداد وسمع بها، ثم دخل الحجاز وجاور أربع سنين، وعلم وأفتى بمكة والمدينة فلقب بإمام الحرمين. ثم عاد إلى نيسابور في أوائل ولاية السلطان ألب أرسلان السلجوقي، والوزير يومئذ نظام الملك، فبنى له المدرسة النظامية ليعلم فيها. قال ابن خلكان: "وبقي على ذلك قريبا من ثلاثين سنة غير مزاحم ولا مدافع، مسلم له المحراب والمنبر والخطابة والتدريس ومجلس التذكير يوم الجمعة". توفي بقرية "بشنقان" ودفن بنيسابور. من كتبه "تفسير القرآن" (١).

الهروي [.. - ٤٨٩ هـ / .. - ١٠٩٦ م]

<span data-type="title" id=toc-831>عبد الملك بن علي الهروي:</span>
لغوي، مفسر، قال الصفدي: "كان مؤدبا بهراة وقرأ عليه أكثر فضلائها". من كتبه
_________
(١) كشف الظنون ٤٤٣ وهدية العارفين ١: ٦٢٦ والشذرات ٣: ٣٥٨ والعبر ٣: ٢٩١ وتبيين كذب المفتري ٢٧٨ وابن خلكان ٣: ١٦٧ والمنتظم ٩: ١٨ والسبكي ٥: ١٦٥ ومفتاح السعادة ١: ٤٤٠ و ٢، ١٨٨ والنجوم الزاهرة ٥: ١٢١ والتحفة اللطيفة ٣: ٣١١ ودمية القصر ١: ٥٣٢ والأعلام ٤: ٣٠٦ وطبقات ابن هداية ١٧٤ والعقد الثمين ٥: ٥٠٧ والبداية والنهاية ١٢: ١٢٨ والجويني إمام الحرمين للدكتورة فوقية حسين محمود.','1','334',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (592,'"المنتخب من تفسير الرماني". ذكره الثعلبي في تفسيره (الكشف والبيان وعده من أهل البدع والأهواء" (١).

الأصمعي [١٢٢ - ٢١٦ هـ / ٧٤٠ - ٨٣١ م]

<span data-type="title" id=toc-832>عبد الملك بن قريب بن عبد الملك بن علي بن أصمع الباهلي، أبو سعيد الأصمعي:</span>
راوية العرب، وأحد أئمة العلم باللغة والغريب والأخبار والشعر والبلدان والملح والنوادر. من أهل البصرة. وبها تعلم على الخليل بن أحمد وأبي عمرو بن العلاء، وأخذ عن خلف بن الأحمر. وكان كثير التطواف في البوادي فحفظ لغة البدو. وقدم بغداد في أيام الرشيد فعهد إليه بتعليم الأمين، وكان الرشيد يسميه بشيطان الشعر. قال الشافعي: "ما عبر أحد عن العرب بمثل عبارة الأصمعي" وقال الأخفش: "ما رأينا أحدا أعلم بالشعر من الأصمعي" وكان الأصمعي يقول: "أحفظ ست عشرة ألف أرجوزة". وأخباره كثيرة. من كتبه "غريب القرآن". قال البقاعي "ولا منافاة بينه وبين قوله "أنا لا أفسر حديث رسول الله" لأنه يحتمل أن يكون قال ذلك أولا ثم
_________
(١) طبقات الداودي ١: ٣٥٤ وبغية الوعاة ٢: ١١١ وهدية العارفين ١: ٦٢ وكشف الظنون ٤٤٧.','1','334',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (593,'رأى من يجترىء على حمل شيء من الغريب على ما يتحقق خطأ، فرأى المصلحة في التفسير، أو يكون ماشيا في ذلك على سنن ما نقل وهو أنه يذكر اللفظة ويقول العرب يريد بهذه اللفظة عند إطلاقها كذا" (١).

ابن الفرس [٥٢٤ - ٥٩٩ هـ / ١١٣٠ - ١٢٠٣ م]

<span data-type="title" id=toc-833>عبد المنعم بن محمد بن عبد الرحيم بن محمد الأنصاري الخزرجي، أبو عبد الله، المعروف بابن الفرس:</span>
فقيه مالكي، قاض، نحوي، من علماء غرناطة بالأندلس، وبها نشأ وتعلم. ولي القضاء بجزيرة شقر، ثم بمدينة وادي آش، ثم بجيان، وأخيرا بغرناطة. ثم عزل عنها، ثم وليها وجعل إليه النظر في الحسبة
_________
(١) طبقات المفسرين للداودي ١: ٣٥٤ ووفيات الأعيان ٣: ١٧٠ وإنباه الرواة ٢: ١٩٧ ونزهة الألباء ١١٢ والنجوم الزاهرة ٢: ١٩٠ وبغية الوعاة ٢: ٢٧٣ وميزان الإعتدال ٢: ٦٦٢ وكشف الظنون ١٢٠٤ وهدية العارفين ١: ٦٢٣ ونور القبس ١٢٥ وتاريخ أصبهان ٢: ١٣ والمعارف ٥٤٣ وتهذيب الأسماء واللغات ٢: ٢٧٣ ومرآة الجنان ٢: ٦٤ وتهذيب التهذيب ٦: ٤١٥ واللباب ١: ٥٦ وخلاصة تهذيب الكمال ٢٠٧ وابن النديم ٥٥ وروضات الجنات ٤٥٨ وغاية النهاية ١: ٤٧٠ وطبقات النحاة لابن قاضي شهبة ١: ١٠١ والشذرات ٢: ٣٦ والأعلام ٤: ٣٠٧ ومعجم المؤلفين ٦: ١٨٧ وتاريخ بغداد ١٠: ٤١٠. وفي تاريخ ولادته ووفاته خلاف بين المؤرخين.','1','335',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (594,'والشرطة، مات بغرناطة ودفن بباب البيرة. من كتبه "أحكام القرآن" مخطوط، قال أبو الربيع الكلاعي: "وهو كتاب حسن مفيد، جمعه في ريعان الشبيبتين من طلبه وسنه، فللنشاط اللازم عن ذلك أثر في حسن ترتيبه وتهذيبه، قرأت عليه صدرا من أوله، وناولني جميعه في أصله، وأخبرني أنه فرغ من تأليفه بمرسية سنة ٥٥٣ هـ " (١).

الحموي [.. - ١٠١٦ هـ / .. - ١٦٠٧ م]

<span data-type="title" id=toc-834>عبد النافع بن عمر الحموي:</span>
فاضل، مشارك في علوم التفسير والحديث والكلام، من أهل حماة، سكن طرابلس الشام، وتوفي بأدلب. من آثاره "تفسير سورة الإخلاص" في مجلد. وله نظم وكان هجاء. (٢)

الزملكاني [.. - ٦٥١ هـ / .. - ١٢٥٣ م]

<span data-type="title" id=toc-835>عبد الواحد بن عبد الكريم بن خلف الأنصاري الزملكاني، أبي المكارم، كمال (الدين، ويقال له ابن خطيب زملكا)</span>
_________
(١) طبقات المفسرين للداودي ١: ٣٥٦ وبغية الوعاة ٢: ١١٦ والديباج ٢١٠١ وهدية العارفين ١: ٦٢٩ وفيه وفاته سنة ٥٩٧ ومثله في كشف الظنون ٢٠ وتاريخ قضاة الأندلس ١١٠ والتكلمة لابن الأبار ٢: ٦٥١.
(٢) خلاصة الأثر ٣: ٩٠ وذيل كشف الظنون ١: ٣٠٥ وهدية العارفين ١: ٦٣٢ وفهرست الخديوية ١: ٢٨٠.','1','335',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (595,'الدين، ويقال له ابن خطيب زملك١: قاض، أديب، من فقهاء الشافعية، متميز فى علوم عدة، ولي القضاء بصرخد، ودرس ببعلبك، وتوفي بدمشق، من كتبه "نهاية التأميل في أسرار التنزيل" في التفسير، و "التبيان في علم البيان المطلع على إعجاز القرآن" طبع (١).

الشيرازي [.. - ٤٨٦ هـ / .. - ١٠٩٣ م]

<span data-type="title" id=toc-836>عبد الواحد بن محمد بن علي بن أحمد الشيرازي، ثم المقدسي، ثم الدمشقي، أبو الفرج، الأنصاري السعدي العبادي الخزرجي، من ذرية سعد بن عبادة:</span>
شيخ الشام في وقته، مفسر، من فقهاء الحنابلة، أصله من شيراز، وتفقه ببغداد والرحبة، وسكن بيت المقدس، ثم استقر بدمشق، فنشر مذمب الإمام أحمد بن حنبل. قال العليمي: "كان إماما عالما بالفقه والأصول، شديدا في السنة، زاهدا عارفا عابدا متألها ذا أحوال وكرامات، تخرخ به الأصحاب، وحصل له الاتباع والتلامذة، وكان "تتش" صاحب دمشق يعظمه .. "
_________
(١) كشف الظنون ١٨٩٧ وهدية العارفين ١: ٦٣٥ والسلوك ١: ٣٨٩ وتاريخ الخلفاء للسيوطي ١٩١ والشذرات ٥: ٢٥٤ والعبر، وفيات ٦٥١ هـ والأعلام ٤: ٣٢٥ ومرآة الحنان ٤: ١٢٧ والسبكي ٥: ١٣٣.','1','336',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (596,'ويقال: أن له كتاب "الجواهر" في تفسير القرآن، ثلاثون مجلدا (١).

ابن المنير [٦٥١ - ٧٣٣ هـ / ١٢٥٣ - ١٣٣٣ م]

<span data-type="title" id=toc-837>عبد الواحد بن منصور بن محمد بن المنير، أبو محمد، فخر الدين، عز القضاة الاسكندري:</span>
مفسر، أديب، ناظم، من فقهاء المالكية. من أهل الاسكندرية. حدث وناب في الحكم. قال ابن حجر العسقلاني: "كان مخرج فضلاء المالكية وصدرهم" وقال ابن فرحون: "كان شيخ ثغر الاسكندرية، فقيها فاضلا أديبا، انتفع الناس به". من كتبه "تفسير القرآن" في ست مجلدات (٢).

العلفي [١٠٢٦ - ١١٠٨ هـ / ١٦١٧ - ١٦٩٦ م]

<span data-type="title" id=toc-838>عبد الواسع بن عبد الرحمن بن محمد القرشي الأموي العلفي:</span>
نحوي،
_________
(١) طبقات المفسرين للداودي ١: ٣٦٢ والذيل على طبقات الحنابلة ١: ١٩٨ والمنهج الأحمد ٢: ١٩٠ والشذرات ٣: ٣٧٨ والأنس الجليل ١: ٢٦٣ وهدية العارفين ١: ٦٣٤ والدارس ٢: ٦٥ وطبقات الحنابلة ٤٥١ وإيضاح المكنون ٢: ٢٨٧ والأعلام ٤: ١٧٧ ومختصر طبقات الحنابلة ٤٠١.
(٢) كشف الظنون ٤٤٠ والديباج ١٧٧ وحسن المحاضرة ١: ٤٥٩ والدرر الكامنة ٣: ٣٦ وطبقات المفسرين للداودي: ٣٥٩ والبداية والنهاية ١٤: ١٦٣ وهدية العارفين ١: ٦٣٥.','1','336',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (597,'مفسر، قاض، أصولي، فقيه. ولد ببلاد حيدان باليمن، واستوطن صنعاء وأخذ عن علمائها. ثم درس بها. وكان الإمام المتوكل على الله يقول: "من أراد النحو فليقرأ على عبد الواسع". له تصانيف منها "تفسير سورة الإخلاص" (١).

الاستراباذي [حيا قبل ١٠٣١ هـ / حيا قبل ١٦٢٢ م]

<span data-type="title" id=toc-839>عبد الوحيد بن نعمة الله بن يحيى الاستراباذي:</span>
مفسر، متكلم، مشارك في بعض العلوم، من فقهاء الإمامية. له تصانيف (٢).

الملتاني [.. - ٩٣٢ هـ / .. - ١٥٢٦ م]

<span data-type="title" id=toc-840>عبد الوهاب بن أحمد جلال الدين شريف الله البخاري، ثم الملتاني، الهندي:</span>
صوفي، مفسر، من فقهاء الحنفية. من آثاره "تفسير القرآن" (٣).
_________
(١) البدر الطالع ١: ٤٠٩ وهدية العارفين ١:
(٢) أعيان الشيعة ٣٩: ١٨٤ وفوائد الرضوية ١: ٢٦٠ ومعجم المؤلفين ٦: ٢١٥.
(٣) هدية العارفين ١: ٦٤٠ ومعجم المؤلفين ٦: ٢١٧.','1','337',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (598,'الطنطاوي [.. - حيا ١١٤٩ هـ / .. - ١٧٣٦ م]

<span data-type="title" id=toc-841>عبد الوهاب بن أحمد بن بركات الأحمدي، الطنطاوي:</span>
فقيه شافعي، مفسر، من أهل طنطا. من كتبه "التيسير لمريد التفسير" (١).

النائب [١٢٦٩ - ١٣٤٥ هـ / ١٨٥٢ - ١٩٢٧ م]

<span data-type="title" id=toc-842>عبد الوهاب بن عبد القادر بن عبد الغني بن جعيدان العبيدي، أبو الحسين النائب:</span>
عالم بالفقه والأدب والتفسير، ولد ببغداد، وولي بها أمانة الفتوى والنيابة الشرعية، ثم رياسة محكمة الصلح، فرياسة التمييز الشرعي، وتدريس التفسير في جامعة آل البيت، وقام بإنشاء عدة مدارس من ماله. وكان خطيبا، وله نظم حسن. له تصانيف أكثرها شروح وحواش، منها "الآيات المتشابهات" وغير ذلك (٢)

ابن الحنبلي [.. - ٥٣٦ هـ / .. - ١١٤١ م]

<span data-type="title" id=toc-843>عبد الوهاب بن عبد الواحد بن محمد بن علي الشيرازي الأصل الدمشقي، شرف الإسلام أبو القاسم، المعروف بابن (الحنبلي)</span>
_________
(١) بروكلمان ٢: ٣٩٠ وهدية العارفين ١: ٦٤٣ ومعجم المؤلفين ٦: ٢١٧ و ٢٢١.
(٢) لب الألباب ١: ٨ والأعلام ٤: ٣٣٤.','1','337',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (599,'الحنبلي: مفسر، أصولي، متكلم، من فقهاء الحنابلة، وشيخهم بالشام ورئيسهم بعد والده (انظر ترجمته). ولد بدمشق، وكان سفير صاحبها حين ورد عليها الإفرنج سنة ٥٢٣ هـ أرسله إلى الخليفة المسترشد بالله العباسي ببغداد ليستنجدهم على الفرنج، فأكرمه الخليفة وخلع عليه ووعده بالنجدة. وحدث ببغداد ودمشق، وكان يفسر مجلدة في المجلس الواحد (١).

خلاف [١٣٠٥ - ١٣٧٥ هـ / ١٨٨٨ - ١٩٥٦ م]

<span data-type="title" id=toc-844>عبد الوهاب بن عبد الواحد خلاف:</span>
فقيه، من الكتاب العلماء، مفسر، ولد بكفر الزيات، والتحق بالأزهر سنة ١٩٠٠، وتخرج بمدرسة القضاء الشرعي عام ١٩١٥، وعين مدرسا بها في نفس السنة. اشترك في ثورة سنة ١٩١٩، فعرف بمواقفه الخطابية والكتابية، فأجبر على ترك المدرسة، وانتقل إلى القضاء الشرعي، فعين قاضيا بالمحاكم الشرعية سنة ١٩٢٠، ثم مديرا للمساجد بوزارة الأوقاف سنة ١٩٢٤، إلى أن عين مفتشا بالمحاكم الشرعية في منتصف سنة ١٩٣١.
_________
(١) الذيل على طبقات الحنابلة ١: ١٩٨ والشذرات ٤: ١١٣ وطبقات الداودي ١: ٣٦٢ والأعلام ٤: ٣٣٤ وهدية العارفين ١: ٦٣٨ ومعجم المؤلفين ٦: ٢٢٤.','1','338',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (600,'وفي أوائل سنة ١٩٣٤ انتدبته كلية الحقوق بجامعة القاهرة مدرسا بها، واستمر أستاذا لكرسي الشريعة الإسلامية حتى أحيل إلى المعاش سنة ١٩٤٨، ولكن ظلت تمد مدة خدمته حتى بداية ٥٥ - ١٩٥٦ حين أقعده المرض عن إلقاء المحاضرات. انتخب عضوا بمجمع اللغة العربية فأشرف على وضع معجم القرآن. وألقى سلسلة محاضرات في تفسير القرآن الكريم لعدة سنوات بدار الحكمة. من آثاره "نور من الإسلام" كتيب في تفسير القرآن (١).

الخفاف [.. - ٢٠٤ هـ / .. - ٨١٩ م]

<span data-type="title" id=toc-845>عبد الوهاب بن عطاء، أبو نصر الخفاف العجلي:</span>
محدث، مفسر، فقيه، من علماء البصرة، استوطن بغداد وتوفي بها. روى عنه أحمد بن حنبل ويحي بن معين وغيرهما. وثقه جماعة وضعفه آخرون. قال ابن النديم: له كتاب في "التفسير" و "الناسخ والمنسوخ" (٢).
_________
(١) مجلة لواء الإسلام ٩: ٦٩ والأديب س ١٥ ع ٣: ٧٨ ومجلة التمدن الإسلامي س ٢٢ ع ١٥ و ١٦ وأخبار اليوم ٢١/ ١/ ١٩٥٦ وعمالقة ورواد ٢٨٨ والمجمعيون ١١٧ والصحف المصرية ٢٠/ ١/ ١٩٥٦.
(٢) تذكرة الحفاظ ١: ٣٣٩ وخلاصة تهذيب الكمال ٢١٠ وابن النديم ٢٨٤ وطبقات الداودي ١: ٣٦٣ والعبر ١: ٣٤٦ وتاريخ بغداد ١١: ٢١ وتهذيب التهذيب ٦: ٤٥٠ وكشف الظنون ١٤٣٤.','1','338',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (601,'الواسطي [.. - ٤٢٤ هـ / .. - ١٠٣٣ م]

<span data-type="title" id=toc-846>عبد الوهاب بن علي بن طلحة، أبو القاسم الواسطي:</span>
نحوي، من آثاره "إعراب القرآن" في خمس وعشرين مجلدة (١).

ابن رامين [.. - ٤٣٠ هـ / .. - ١٠٣٩ م]

<span data-type="title" id=toc-847>عبد الوهاب بن محمد بن عمر البغدادي، نور الدين، المعروف بابن رامين:</span>
مفسر، من فقهاء الشافعية. من أهل بغداد، درس بها على أبي القاسم عبد العزيز بن عبد الله الداركي المتوفي سنة ٣٧٥ هـ. ثم سكن البصرة ودرس بها إلى أن توفي. من آثاره "الاستغناء" في تفسير القرآن (٢).

الفامي [٤١٤ - ٥٠٠ هـ / ١٠٢٣ - ١١٠٧ م]

<span data-type="title" id=toc-848>عبد الوهاب بن محمد بن عبد الوهاب ابن محمد، أبو محمد الفامي:</span>
مفسر، مؤرخ، من كبار الشافعية، فارسي الأصل، من أهل شيراز. قدم بغداد سنة ٤٨٣ هـ، وقيل سنة ٤٨٨ هـ، ودرس بالنظامية، ثم عزل بعد سنة. وعاد إلى
_________
(١) معجم المؤلفين ٦: ٢٢٥.
(٢) هدية العارفين ١: ٦٣٧ وطبقات الفقهاء ١٠٤ ومعجم المؤلفين ٦: ٢٢٩.','1','339',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (602,'شيراز وتوفي بها. قال أبو علي الصدفي: دخل بغداد لما كنت بها وأنهض إلى التدريس بالمدرسة النظامية وتلقاه أهل بغداد وخرج إليه كافة من العلماء وأهل الدولة وكان يوما مشهودا .. سمعت عليه وسمعته يقول: "صنفت سبعين تأليفا في ثمانية عشر علما، و "تفسيرا كبيرا" ضمنته مائة وعشرين ألف بيت شاهدا ... ثم رمي بالاعتزال وفر بنفسه .. " (١).

العلوي [٨٣٤ - ٨٧٥ هـ / ١٤٣١ - ١٤٧٠ م]

<span data-type="title" id=toc-849>عبد الوهاب بن محمد بن حسن بن محمد بن أبي الوفا، العراقي الأصل، المقدسي، ثم الخليلي، تاج الدين، أبو نصر:</span>
قاض، مفسر، من فقهاء الشافعية. دخل القاهرة سنة ٨٥٠ هـ وأخذ عن علمائها. وولي القضاء بحلب. قال السخاوي، كتب مجموعا فيه فوائد، ومات قريب الستين ظنا" وقال البغدادي أنه مات سنة ٨٧٥ هـ وذكر له عددا من التصانيف منها "مختصر معالم التنزيل" للبغوي في التفسير، وغير ذلك (٢).
_________
(١) طبقات الداودي ١: ٣٦٤ وطبقات السبكي ٧: ٢٠٥ ولسان الميزان ٤: ٩٠ والمنتظم ٩: ١٥٣ والشذرات ٣: ٤١٣ والبداية والنهاية ١٢: ١٦٨ وهدية العارفين ١: ٤٣٧ وكشف الظنون ٤٥١.
(٢) الضوء اللامع ٥: ١٠٧ وكشف الظنون ١٧٢٦ وهدية العارفين ١: ٦٣٩ وبروكلمان ٢: ١٣٢.','1','339',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (603,'ابن السلار [٦٩٨ - ٧٨٢ هـ / ١٢٩٩ - ١٣٨٠ م]

<span data-type="title" id=toc-850>عبد الوهاب بن يوسف بن إبراهيم الدمشقي، أمين الدين، أبو محمد، ابن السلار:</span>
مقرىء كبير، عالم بالتفسير والنحو والفقه، انتهت إليه رياسة الإقراء بالشام. من أهل دمشق، قرأ بها وبالقاهره. وولي المشيخة الكبرى بدمشق إلى أن توفي. قال ابن الجزري: وهو أول شيخ انتفعت به ولازمته، ووليت بعده المشيخة الكبرى" (١).

التفتازاني [.. - ٥٥٠ هـ / .. - ١١٥٥ م]

<span data-type="title" id=toc-851>عبيد الله بن إبراهيم بن أبي بكر النسائي التفتازاني:</span>
مفسر، محدث، ذكره السمعاني وقال: "كان إماما مفننا مفسرا، محدثا، واعظا، مشتغلا بالعبادة، يتولى الحرث والحصاد بنفسه، ويأكل من كده، سمع نصر الله الخشني، وإسماعيل بن عبد الغافر وصاعد بن سيار الحافظ، روى عنه عبد الرحيم بن السمعاني وأبوه" (٢).
_________
(١) غاية النهاية ١: ٤٨٢ والشذرات ٥: ٢٧٥ وإنباء الغمر ١: ٢٢٥ والدرر الكامنة ٣: ٤٥ وطبقات المفسرين للداودي ١: ٣٦٥.
(٢) طبقات المفسرين للسيوطي ٢٢ وطبقات المفسرين للداودي ١: ٣٦٩ واللباب ١: ١٧٨ والأنساب ١٠٧ ب.','1','340',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (604,'أبو زرعة الرازي [٢٠٠ - ٣٦٤ هـ / ٨١٥ - ٨٧٨ م]

<span data-type="title" id=toc-852>عبيد الله بن عبد الكريم بن يزيد بن فروخ القرشي بالولاء، أبو زرعة الرازي:</span>
من أئمة حفاظ الحديث، من أهل الري، سمع بها وبالحجاز والعراق والشام والجزيرة وخراسان ومصر. وحدث عنه من شيوخه مسلم وأبو حاتم والترمذي وابن ماجة والنسائي وغيرهم. وجالس أحمد بن حنبل، وكان يحفظ مئة ألف حديث. قال أبو يعلى الموصلي: كان أبو زرعة مشاهدته أكبر من اسمه يحفظ الأبواب والشيوخ والتفسير، توفي بالري (١).

البرجاني [؟ -؟ /؟ -؟]

<span data-type="title" id=toc-853>عبيد الله بن عثمان بن عبيد الله اللخمي البرجاني، أبو مروان:</span>
عالم بالقراءات والتفسير والنحو والأدب، من أهل اشبيلية بالأندلس، أخذ عن علمائها وعلماء قرطبة. ذكره ابن بشكوال في "الصلة" ولم يؤرخ مولده ولا وفاته (٢).
_________
(١) تذكرة الحفاظ ٢: ٥٥٧ وطبقات المفسرين للداودي ١: ٣٦٩ وتاريخ بغداد ١٠: ٣٢٦ وطبقات الحنابلة ١: ١٩٩ والرسالة المستطرفة ٦٤ والنجوم الزاهرة ٣: ٣٨ والعبر ٢: ٢٨.
(٢) الصلة ١: ٢٩١ وطبقات المفسرين للداودي ١: ٣٧١.','1','340',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (605,'الأسدي [.. - ٣٨٧ هـ / .. - ٩٩٧ م]

<span data-type="title" id=toc-854>عبيد الله بن محمد بن جرو الأسدي، أبو القاسم:</span>
معتزلي، مفسر، نحوي، مقرىء، عروضي، من أهل الموصل، قدم بغداد وأخذ عن شيوخها. قال القفطي: "من أصحاب أبي علي الفارسي وتلك الحلبة، قرأ وأكثر الأخذ عن النحاة، وشدا شينا من اللغة، وتصدر لإقراء هذا الشأن". من كتبه "الأمد" في علوم القرآن" و "تفسير القرآن" ذكر في بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ مائة وعشرين وجها. وله شعر (١).

القرطبي [٤٠٠ - ٤٦٠ هـ / ١٠١٠ - ١٠٦٨ م]

<span data-type="title" id=toc-855>عبيد الله بن محمد بن مالك القرطبي، أبو مروان:</span>
حافظ، محدث، مفسر، من فقهاء المالكية. من أهل قرطبة. قال ابن بشكوال: كان عالما بوجوه الاختلاف بين فقهاء
_________
(١) طبقات المفسرين للسيوطي ٢٢ وطبقات المفسرين للداودي ١: ٣٧١ وطبقات المفسرين للأدنه وي ٢٧ أولسان الميزان ٤: ١١٥ ومعجم الأدباء ١٢١٢: ٦٢ وبغية الوعاة ٢: ٢٧ وإنباه الرواة ٢: ١٥٤ وروضات الجنات ٤٦٥ وهدي العارفين ١: ٦٤٥ وإيضاح المكنون ١: ٣٠٢.','1','341',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (606,'الأمصار، متواضعا، كثير الورع، مجاهدا، متبذلا في لباسه، قانعا باليسير" له تصانيف (١).

الأوزبكي [.. - ٩٧٦ هـ / .. - ١٥٦٨ م]

<span data-type="title" id=toc-856>عبيد الله خان بن الأمير محمود سلطان الأوزبكي:</span>
من أمراء الأتراك في ما وراء النهر، من آثاره "الفوائد الخاقانية العبيدية" في التفسير (٢).

المارديني [٦٥٠ - ٧٣١ هـ / ١٢٥٢ - ١٣٣١ م]

<span data-type="title" id=toc-857>عثمان بن إبراهيم بن مصطفى المارديني، ويقال له ابن التركماني:</span>
عارف بالتفسير، فقيه، نحوي، محدث، وصفه السيوطي بقوله: شيخ الأصحاب في وقته، إنتهت إليه رياسة الحنفية بالديار المصرية، وتخرج به خلق كثير، مات بالقاهرة (٣).
_________
(١) طبقات المفسرين للسيوطي ٢٢ وطبقات المفسرين للداودي ١: ٣٧٢ وطبقات المفسرين للأدنه وي ٣٣ أوالصلة ١: ٢٩٢.
(٢) كشف الظنون ١٢٩٧ وهدية العارفين ١:
(٣) الدرر الكامنة ٢: ٤٩ وفيه: ولد سنة ٦٦٠ هـ. وحسن المحاضرة ١: ٢٦٧ والفوائد البهية ١١٥.','1','341',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (607,'الخوبوي [.. - ١٢٤١ هـ .. - ١٨٢٦ م]

<span data-type="title" id=toc-858>عثمان بن حسن بن أحمد الشاكر الخوبوي، الرومي:</span>
فقيه حنفي، مفسر، واعظ، مشارك في بعض العلوم، من أهل خوبه (من قرى طربوزون). من آثاره "درة الناصحين" مجالس في التفسير والحديث، فرغ منها سنة ١٢٢٤ هـ (١).

الداني [٣٧١ - ٤٤٤ هـ / ٩٨١ - ١٠٥٣ م]

<span data-type="title" id=toc-859>عثمان بن سعيد بن عثمان بن سعيد بن عمر الأموي بالولاء، القرطبي، ويعرف بالداني، وبابن الصيرفي قديما، أبو عمرو:</span>
إمام في علم القرآن ورواياته وتفسيره ومعانيه وطرقه وإعرابه، عارف بالحديث وأسماء رجاله، كان إليه المنتهى في علم القراءات وإتقان القرآن، أصله من قرطبة، وسكن دانية فنسب إليها. رحل إلى المشرق سنة ٣٩٧ هـ، فدخل القيروان، ومكث بمصر سنة، وحج، ورجع إلى الأندلس سنة ٣٩٩ هـ، فأقام بقرطبة، ثم بسرقسطة، ثم استوطن دانية سنة ٤١٧ هـ حتى وفاته (٢).
_________
(١) إيضاح المكنون ١: ٤٦٢ وهدية العارفين ١: ٦٦١ ومعجم المؤلفين ٦: ٢٥٣.
(٢) طبقات المفسرين ١: ٣٧٣ وطبقات القراء للذهبي ١: ٣٢٥ وطبقات النحاة لابن قاضي شهبة ٢: =','1','342',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (608,'ابن الصلاح [٥٧٧ - ٦٤٣ هـ / ١١٨١ - ١٢٤٥ م]

<span data-type="title" id=toc-860>عثمان بن عبد الرحمن (صلاح الدين) بن عثمان بن موسى الكردي الشهرزوري الشرخاني، أبو عمرو، تقي الدين، المعروف بابن الصلاح:</span>
عارف بالحديث والفقه والتفسير وأسماء الرجال. ولد في شرخان (قرب شهرزور) وسمع بها وبالموصل وبغداد وهمذان ونيسابور ودمشق وحلب وحران. ودرس بالمدرسة الصلاحية ببيت المقدس، ثم دخل دمشق ودرس بالرواحية، ثم ولي مشيخة دار الحديث الأشرفية، فالتدريس بالشامية الصغرى. قال ابن خلكان: "كان أحد فضلاء عصره في التفسير والحديث والفقه، وله مشاركة في عدة فنون، وكانت فتاواه مسددة" (١).
_________
= ١٢٧ وطبقات القراء لابن الجزري ١: ٥٠٣ وهدية العارفين ١: ٦٥٣ والنجوم الزاهرة ٥: ٥٤ وإنباء الرواة ٢: ٣٤١ ونفح الطيب ٢: ١٣٦ وروضات الجنات ٤٦٧ والعبر ٣: ٧٠٢ ومعجم الأدباء١٢: ١٢١ وجذوة المقتبس ٢٨٦ وتذكرة الحفاظ ١١٢٠ والنجوم الزاهرة ٥: ٥٤ وبغية الملتمس ٣٣٩ والرسالة المستطرفة ١٣٩ ومفتاح السعادة ٢: ٤٧ ومرآة الجنان ٣: ٦٢ والشذرات ٣: ٢٧٢ والديباج ١٨٨ والصلة ٢: ٣٨٥ وصفة جزيرة الأندلس ٧٦ والأعلام ٤: ٣٦٦.
(١) طبقات المفسرين للداودي ١: ٣٧٧ وطبقات الأسنوي ١٦٤ وطبقات السبكي ٨: ٣٢٦ والشذرات ٥: ٢٢١ والعبر ٥: ١٧٧ والأنس الجليل ٢: ١٠٤ والبداية والنهاية ١٣: ١٦٨ ومفتاح السعادة ٢: ٦٠.','1','342',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (609,'عثمان بن عفان [٤٧ ق هـ - ٣٥ هـ / ٥٧٧ - ٦٥٦ م]

<span data-type="title" id=toc-861>عثمان بن عفان بن أبي العاص بن أمية بن عبد شمس بن عبد مناف، الأموي القرشي:</span>
أمير المؤمنين، ثالث الخلفاء الراشدين، وأحد العشرة المبشرين. يلقب بذي النورين لأنه تزوج بنتي النبي (صلى الله عليه وسلم) رقية، ثم أم كلثوم. ولد بمكة، وأسلم بعد البعثة بقليل على يدي أبي بكر الصديق. وهاجر الهجرتين، إلى الحبشة، وإلى المدينة. وهو من الستة الذين توفى الرسول (صلى الله عليه وسلم) وهو عنهم راض. وكان غنيا، سمح النفس، في غاية الجود والكرم، فبذل الكثير من ماله في سبيل الإسلام، فجهز جيش العسرة بالمال والإبل والأفراس، وأنفق في شراء بئر ماء للمسلمين من يهودي بالمدينة، وزاد في مسجد الرسول، وعوض الناس عن أرضهم التي أدخلها في المسجد من ماله الخاص. واستعان به النبي الكريم (صلى الله عليه وسلم) في السفارات وكتابة الوحي.
_________
= وابن خلكان ٣: ٢٤٣ ومرآة الزمان ٨: ٧٥٧ وتذكرة الحفاظ ١٤٣٠ وذيل الروضتين ١٧٥ وتاريخ علماء بغداد ١٣٠ وهدية العارفين ١: ٦٥٤ ومعجم المؤلفين ٦: ٢٥٧ والأعلام ٤: ٣٦٩.','1','343',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (610,'وصارت إليه الخلافة بعد عمر بن الخطاب (رضي الله عنه) سنة ٢٣ هـ. فتوسع المسلمون أبان حكمه إلى الشرف، فافتتحوا أرمينية والقوقاز وخراسان وكرمان وسجستان، كما استولوا على قبرس وإفريقية في الغرب. وهو أول من قدم الخطبة في العيد على الصلاة، وأمر بالآذان الأول يوم الجمعة. واتخذ الشرطة، وأمر بكل أرض جلا أهلها عنها أن يستعمرها العرب المسلمون وتكون لهم. اتهم بمحاباته أقرباءه من بني أمية بتعيينهم في مختلف الوظائف، وبمخالفة الدين بإحراقه المصاحف كلها ما عدا المصحف الذي أمر بتعميمه، فجاءته الوفود من الكوفة والبصرة ومصر، فطلبوا منه عزل أقاربه، فامتنع، فحاصروه، ثم تسور عليه بعضهم الجدار فقتلوه صبيحة عيد الأضحى وهو يقرأ القرآن في بيته بالمدينة. روى عن النبي ١٤٦ حديثا. ويعد ممن اشتهر بالتفسير من الصحابة، ولكن لم يرد عنه في التفسير إلا النزر اليسير، ويرجع السبب في ذلك إلى تقدم وفاته واشتغاله بمهام الخلافة والفتوحات (١).
_________
(١) عثمان بن عفان لطه حسين، وعثمان بن عفان لصادق إبراهيم عرجون، وتاريخ الخميس ٢: ٢٥٤ =','1','343',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (611,'المودورنه وي [.. - ١٢١١ هـ / .. - ١٧٩٦ م]

<span data-type="title" id=toc-862>عثمان بن علي المودورنه وي، الرومي، النقشبندي:</span>
عارف بالتفسير، صوفي، فقيه حنفي، من علماء الدولة العثمانية. من تصانيفه "قواعد التفسير" (١).

فضلي [.. - ١١٠٢ هـ / .. - ١٦٩١ م]

<span data-type="title" id=toc-863>عثمان بن فتح الله الشمني الرومي، الملقب بفضلي، والشهير بآت بازاري:</span>
صوفي، من مشايخ الخلوتية، مشارك في عدة علوم، درس في مسجد آت بازاري في القسطنطينية فنسب إليه، ووعظ في جوامع السلاطين، وتوفي بجزيرة قبرس. من آثاره "مرآة أسرار العرفان على إعجاز البيان" حاشية على إعجاز البيان في تفسير أم القرآن، للقونوي (٢).
_________
= والكنى والأسماء ١: ٨ والموسوعة العربية الميسرة ١١٨٧ وشذرات الذهب ٤٠١ والعبر وفيات سنة ٣٥ هـ وغاية النهاية ١: ٥٠٧ والبدء والتاريخ ١٩٤ وصفة الصفوة ١١٢ وحلية الأولياء ١: ٥٥ والمحبر ٣٧٧ والكامل لابن الأثير ٣: ١٦٧ وانظر فهرسته، وتاريخ الطبري ٤: ٢٢٨ وانظر فهرسته، والرياض النضرة ٢: ٨٢، والأعلام ٤: ٣٧٢ والمعرفة ١٦٦٥ والإسلام والحضارة العربية ٢: ١٣٨ ومنهاج السنة ٢: ١٨٦ وشرح نهج البلاغة ٢: ٦١ وتاريخ مختصر الدول ١٠٣.
(١) هدية العارفين ١: ٦٦٠.
(٢) هدية العارفين ١: ٦٥٧ وإيضاح المكنون ١: ٤٥٦.','1','344',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (612,'ابن أبي شيبه [١٥٦ - ٢٣٩ هـ / ٧٧٠ - ٨٥٣ م]

<span data-type="title" id=toc-864>عثمان بن محمد بن إبراهيم بن عثمان بن أبي شيبة الكوفي العبسي، أبو الحسن:</span>
حافظ للحديث، مفسر، من أهل الكوفة، رحل إلى مكة والري، ثم نزل بغداد، وحدث بها عن شريك وسفيان بن عيينة وغيرهما. وعنه البخاري ومسلم وأبو داود وابن ماجة. قال ابن معين: "ثقة مأمون. وسئل عنه أحمد بن حنبل فقال: "ما علمت إلا خيرا". وحكيت عنه تصحيفات لبعض الآيات كأنها على سبيل الدعابة. قال الذهبي: "له أفراد وغرائب، وكان مزاحا حتى في ما يتصحف من القرآن، ولعله تاب". له "تفسير القرآن" (١).

الإستجي [.. - ٣٥٦ هـ / .. - ٩٦٧ م]

<span data-type="title" id=toc-865>عثمان بن محمد بن محاسن الاستجي، أبو سعيد:</span>
مفسر، إخباري، من أهل استجة بالأندلس (قاعدة إقليم اشبيلية الغربي في إسبانيا). قال ابن الفرضي: كان حافظا للتفسير، عالما بأخبار الدهور،
_________
(١) تذكرة الحفاظ ٢: ٤٤٤ والنجوم الزاهرة ٢: ٣٠١ والعبر ١: ٤٣٠ وتاريخ بغداد ١١: ٢٨٣ والشذرات ٢: ٩٢ وطبقات المفسرين للداودي ١: ٣٧٩ وميزان الاعتدال ٣: ٣٥ وتهذيب التهذيب ٧: ١٤٩ وهدية العارفين ١: ٦٥١.','1','344',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (613,'وله في ذلك كتاب نقل أكثره على ظهر قلب" (١).

البكري [١٢٦٦ - ١٣١٠ هـ / ١٨٥٠ - ١٨٩٣ م]

<span data-type="title" id=toc-866>عثمان بن محمد شطا، أبو بكر، البكري:</span>
فقيه شافعي، مفسر، متصوف، من أهل دمياط بمصر، استقر بمكة. له تصانيف منها "تفسير القرآن العظيم" وصل فيه إلى سورة {قد أفلح المؤمنون} (٢).

الياسري [٥٥٠ - ٦١٦ هـ / ١١٥٥ - ١٢٢٠ م]

<span data-type="title" id=toc-867>عثمان بن مقبل بن قاسم بن علي الياسري، البغدادي، أبو عمرو، جمال الدين:</span>
واعظ، من فقهاء الحنابلة، مشارك في علوم التفسير والتاريخ، من أهل الياسرية - من قرى بغداد - وإليها نسبته. قدم بغداد في صباه وأخذ عن علمائها. قال
_________
(١) تاريخ علماء الأندلس ٣٠٥ الترجمة ٩٠١ وهدية العارفين ١: ٦٥١.
(٢) هدية العارفين ١: ٢٤١ وهو فيه: أبو بكر بن محمد، ومثله في إيضاح المكنون ٢: ٣٧٠ ومعجم المؤلفين ٣: ٧٣، و ٦: ٢٧٠ واسمه هنا عثمان بن محمد نقلا عن معجم المطبوعات ٥٧٧، وانظر مجلة "العرب" السعودية س ٥: ٨٦٤. والأعلام ٤: ٢١٤.','1','345',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (614,'ابن رجب: "سمع الحديث الكثير، ووعظ، وتقدم في الوعظ إلى غاية تميز بها عن نظائره في صلاح ودين وسمت" له "كتاب في التفسير والوعظ والفقه والتاريخ" (١). الكماخي [.. - ١١٧١ هـ .. - ١٧٥٨ م]

<span data-type="title" id=toc-868>عثمان بن يعقوب بن حسين بن مصطفى الكماخي، الإسلامبولي، الرومي:</span>
واعظ، مفسر، من فقهاء الحنفية، استقر بالقسطنطينية ودرس ووعظ بها. له "حاشية" على تفسير سورة النبأ للبيضاوي (٢).

ابن أبي رباح [٢٧ - ١١٤ هـ / ٦٤٧ - ٧٣٢ م]

<span data-type="title" id=toc-869>عطاء بن أسلم (أبي رباح) بن صفوان، أبو محمد:</span>
تابعي، محدث، من أجلاء الفقهاء، ومن تلاميذ ابن عباس في التفسير. كان عبدا أسود، ولد باليمن ونشأ بمكة فكان مفتي أهلها ومحدثهم. أدرك مائتين من الصحابة، وروى عن ابن عباس وأبي هريرة وغيرهما. له "تفسير القرآن"
_________
(١) شذرات الذهب ٥: ٦٩ والذيل على طبقات الحنابلة ٢: ١٢٢ ومعجم المؤلفين ٦: ٢٧١.
(٢) هدية العارفين ١: ٦٥٩ ومعجم المؤلفين ٦: ٢٧٢.','1','345',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (615,'استخدمه الطبري والثعلبي في تفسيرهما. و "غريب القرآن" وهو دراسة لشروح ابن عباس، مخطوط، في مكتبة عاطف بتركيا (١).

عطاء بن دينار [.. - ١٢٦ هـ / .. - ٧٤٤ م]

<span data-type="title" id=toc-870>عطاء بن دينار الهذلي، بالولاء، المصري:</span>
محدث، مفسر، روى عن سعيد ابن جبير. وفي "الجرح والتعديل" لابن أبي حاتم أن عطاء لم يسمع مباشرة من سعيد، ولكنه استخدم فقط تفسيره الذي ألفه (سعيد) للخليفة عبد الملك وحفظ في الديوان الأموي. قال صاحب تاريخ التراث العربي: "ومن المحتمل أن الطبري قد استخدمه في دائرة ضيقة بالرواية الآتية: أحمد بن عبد الرحيم البرقي - سعيد بن أبي مريم - عبد الله بن لهيعة - عطاء" كما أن
_________
(١) تهذيب التهذيب ٧: ١٩٩ وميزان الاعتدال ٢: ١٩٧ وابن خلكان ٣: ٢٦١ والعبر ١: ١٤١ ونكت الهميان ١٩٩ وطبقات الشيرازي ٤٤ وابن سعد ٥: ٤٦٧ والمعارف ٤٤٤ والجرح والتعديل ٣/ ١: ٣٣٠ وحلية الأولياء ٣: ٣١٠ وتذكرة الحفاظ ٩٨ والشذرات ١: ١٤٧ وتاريخ التراث العربي ١: ١٨٨ وصفة الصفوة ٢: ١١٩ وهدية العارفين ١: ٦٦٤ وكشف الظنون ٤٥٣ والأعلام ٥: ٢٩ ومعجم المؤلفين ٦: ٢٨٣.','1','346',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (616,'الثعلبي قد استخدمه أيضا مرجعا لكتابه "الكشف والبيان" (١).

عطاء بن السائب [.. - ١٣٦ هـ / .. - ٧٥٣ م]

<span data-type="title" id=toc-871>عطاء بن السائب بن مالك الثقفي، أبو زيد:</span>
محدث ثقة، من التابعين المشهورين ومن مفسري مدرسة الكوفة. قال ابن سعد: "روى عنه المتقدمون، وقد كان تغير حفظه بآخرة، واختلط في آخر عمره". وقال أحمد بن حنبل: "من سمع منه قديما فهو صحيح .. " مات بالكوفة (٢).

الخراساني [٥٠ - ١٣٥ هـ / ٦٧٠ - ٧٥٧ م]

<span data-type="title" id=toc-872>عطاء بن أبي مسلم ميسرة، وقيل عبد الله الخراساني، أبو عثمان، ويقال أبو محمد وأبو صالح:</span>
مفسر، محدث، معروف بالفتوى والجهاد. من أهل سمرقند، وقيل من أهل بلخ. سكن
_________
(١) تهذيب التهذيب ٧: ١٩٨ والجرح والتعديل ٣/ ١: ٣٣٢ والمراسيل لابن أبي حاتم ٥٨ وتاريخ التراث العربي ١: ١٩١ وكشف الظنون ٤٥٣ والأعلام ٥: ٢٩ ومعجم المؤلفين ٦: ٢٨٣.
(٢) طبقات ابن سعد ٦: ٣٣٨ والشذرات ١: ١٦٤ والتاريخ الكبير للبخاري ٦: ٤٦٥ الترجمة ٣٠٠٠.','1','346',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (617,'الشام، ومات بأريحا، ودفن ببيت المقدس. من آثاره "تفسير القرآن" استخدمه الطبري في تفسيره، مخطوط، أوراق منه، في الظاهرية. و "الناسخ والمنسوخ" مخطوط، جزء منه، في الظاهرية. قال صاحب "تاريخ التراث العربي: "ويبدو أن أجزاء من هذا الكتاب قد بقيت في التفاسير المتأخرة وكذلك في الكتب التي تحمل نفس العنوان" (١).

أبو روق [.. - بعد ١٠٥ هـ / .. - بعد ٧٢٣ م]

<span data-type="title" id=toc-873>عطية بن الحارث، أبو روق، الهمذاني الكوفي:</span>
محدث، مفسر، روى عن الضحاك بن مزاحم (ت ١٠٥ هـ) وعكرمة البربري (٢٥ - ١٠٥ هـ) وغيرهما. ذكره ابن سعد في الطبقة الخامسة وقال: هو صاحب "التفسير". وقال الداودي: روى له أبو داود والنسائي وابن ماجة" (٢).
_________
(١) طبقات المفسرين للداودي ١: ٣٧٩ وتهذيب التهذيب ٧: ٢١٢ وحلية الأولياء ٥: ١٩٣ وميزان الاعتدال ٣: ٧٣ وخلاصة تهذيب الكمال ٢٢٦ والعبر ١: ١٨٢ والجرح والتعديل ٣: ٣٣٤ وطبقات ابن سعد ٧: ٣٦٩ والشذرات ١: ١٩٢ ومرآة الجنان ١: ٢٨١ وكشف الظنون ٤٥٣ وتاريخ التراث العربي ١: ١٩٢ ومعجم المؤلفين ٦: ٢٨٣.
(٢) التاريخ الكبير للبخاري ٤/ ١: ١٢ وخلاصة تهذيب الكمال ١٢٦ وطبقات المفسرين للداودي ١: ٣٨٥ وابن سعد ٦: ٣٦٩ وتهذيب التهذيب ٧: ٢٢٤.','1','347',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (618,'العوفي [.. - ١١١ هـ / .. - ٧٢٩ م]

<span data-type="title" id=toc-874>عطية بن سعد بن جنادة العوفي الجدلي القيسي الكوفي، أبو الحسن:</span>
محدث، مفسر، كان يعد من شيعة أهل الكوفة. روى عن ابن عباس وابن عمر وغيرهما من الصحابة. قال صاحب تاريخ التراث العربي: "ويعد عند الكلبي حجة في تفسير القرآن، وكان يأتي في تفسيرة تعبيرات المشبهة بشروح مجازية". مات بالكوفة. من آثاره "تفسير القرآن" نقله الطبري منه نقولا استخدمها في ١٥٦٠ موضعا من تفسيره، كما استخدم نقولا وشواهد منه في تاريخه (١).

الأجهوري [.. - ١١٩٠ هـ / .. - ١٧٧٦ م]

<span data-type="title" id=toc-875>عطية (ويقال عطية الله) بن عطية البرهاني الأجهوري:</span>
فقيه شافعي، مفسر، مشارك في بعض العلوم، من أهل أجهور (بغرب القليوبية بمصر) تعلم ودرس بالأزهر، وتوفي بالقاهرة. من كتبه "إرشاد الرحمن لأسباب النزول والنسخ والمتشابه من القرآن" مخطوط و "كتاب
_________
(١) ميزان الاعتدال ٣: ٧٩ والجرح والتعديل ٣/ ١: ٣٨٢ وتهذيب التهذيب ٧: ٢٢٤ وذيل المذيل ٩٥ والمعارف ٢٥٩ وابن سعد ٦: ٣٠٤ والتاريخ الكبير للبخاري ٤/ ١: ٨ وتاريخ التراث العربي ١: ١٨٧.','1','347',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (619,'الكوكبين النيرين في حل ألفاظ الجلالين" مخطوط في التفسير، حاشية على تفسير الجلالين (١).

عطية بن علي [.. - ٩٨٣ هـ / .. - ١٥٧٦ م]

<span data-type="title" id=toc-876>عطية بن علي بن حسن السلمي المكي، زين الدين:</span>
مفسر، كان عالم مكة وفقيهها في عصره. من كتبه "تفسير القرآن العظيم" ثلانة أجزاء (٢).

عكرمة البربري [٢٥ - ١٠٥ هـ / ٦٤٥ - ٧٢٣ م]

<span data-type="title" id=toc-877>عكرمة بن عبد الله البربري، أبو عبد الله:</span>
مولى عبد الله بن عباس، تابعي موثوق بعدالته ودينه، كان على مكانة عالية من التفسير والفقه، أصله من البربر بالمغرب. روى عن مولاه وعلي بن أبي طالب وعائشة وأبي هريرة وغيرهم. وروى عنه زهاء ثلاثمائة رجل. منهم أكثر من سبعين تابعيا. اختلف العلماء في توثيقه. ولكن أكثرهم وثقو وأثنوا عليه. قال ابن حبان: "كان من علماء زمانه بالفقه
_________
(١) سلك الدرر ٣: ٢٦٥ وهو فيه عطية الله ووفاته سنة ١١٩٤ هـ. وعجائب الآثار ١: ٤ وهدية العارفين ١: ٦٦٥ وإيضاح المكنون ١: ٦٠ و ٢: ٣٩٥ ومعجم المؤلفين ٢٧٨٠٦.
(٢) الأعلام ٦: ٣٣.','1','348',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (620,'والقرآن" وقال الشعبي: "ما بقي أحد أعلم بكتاب الله من عكرمة، وقال يحيى بن معين: "إذا رأيت إنسانا يقع في عكرمة، وفي حماد بن سلمة، فاتهمه على الإسلام". لقي نجدة بن عامر الحروري (رأس الفرقة النجدية - من فرق الخوارج) وكان يحدث برأيه. ودخل المغرب فأخذ عنه أهلها رأي "الصفوية". وعاد إلى المدينة، فطلبه أميرها، فتغيب عنه حتى مات. قال الدكتور محمد حسين الذهبي: "وأما ما رمي به من الميل للخوارج، فافتراء عليه، ولا يكاد يتفق مع سلوكه في حياته" وقال ابن حجر: "فأما البدعة، فإن ثبتت عليه فلا تضر حديثه، لأنه لم يكن داعية، مع أنها لم تثبت عليه". وقال الذهبي: "لا ريب أن هذا الإمام من بحور العلم، وقد تكلم فيه بأنه على رأي الخوارج، ومن ثم أعرض عنه مالك ومسلم". مات بالمدينة هو و "كثير عزة) في يوم واحد، فقيل: "مات أعلم الناس وأشعر الناس". له "تفسير القرآن" اعتمد فيه على تفسير ابن عباس (١).
_________
(١) تذكرة الحفاظ ٩٥ وتهذيب التهذيب ٧: ٢٦٣ وطبقات ابن سعد ٣: ٣٨٥ والمعارف لابن قتيبة ٤٥٥ وشذرات الذهب ١: ١٣٠ وميزان الاعتدال ٣: ٩٣ وحلية الأولياء ٣: ٣٢٦ وخلاصة تهذيب الكمال ٢٢٩ وذيل المذيل ٩٠ وتاريخ التراث العربي ١: ١٧٧ ووفيات الأعيان ٣: ٣٦٥ والنجوم الزاهرة ١: ٢٦٣ والعبر للذهبي ١: ١٣١ وطبقات المفسرين للداودي ١: ٣٨٠ =','1','348',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (621,'علقمة بن قيس [.. - ٦٢ هـ / .. - ٦٨١ م]

<span data-type="title" id=toc-878>علقمة بن قيس بن عبد الله بن مالك النخعي الهمداني، أبو شبل:</span>
تابعي، من مشاهير المفسرين المنتمين إلى مدرسة الكوفة بالعراق. ولد في حياة رسول الله - صلى الله عليه وسلم -، روى عن عمر وعثمان وعلي وابن مسعود وغيرهم. وهو من أشهر رواة عبد الله بن مسعود، وأعرفهم به، وأعلمهم بعلمه، وكان يشبهه في هديه وسمته وفضله. قال إبراهيم النخعي: "كان أصحاب عبد الله الذين يقرئون الناس ويعلمونهم السنة ويصدر الناس عن رأيهم ستة، علقمة، والأسود ... " وذكر الباقين. شهد صفين مع علي، وغزا خراسان وأقام بخوارزم وبمرو ثم سكن الكوفة وتوفي بها. وهو عند أصحاب الكتب الستة (١).

الكاشاني [.. - حيا ١١٠٧ هـ / .. - حيا ١٥٩٨ م]

<span data-type="title" id=toc-879>علم الهدى بن الداعي بن علم الهدي ابن الداعي العاملي الأصل، الكاشاني (المسكن)</span>
_________
= والأعلام ٥: ٤٣ وكشف الظنون ٤٣٥ وهدية العارفين ١: ٦٦١ ومعجم المؤلفين ٦: ٢٩٥ وفي تاريخ وفاته خلاف بين المؤرخين.
(١) تذكرة الحفاظ ٤٨ وتهذيب التهذيب ٧: ٢٧٦ وتاريخ بغداد ١٢: ٢٩٦ وفيه أقوال في وفاته. وحلية الأولياء ٢: ٩٨ وشذرات الذهب ١: ٧٠ وكشف الظنون ١: ٤٣٠ والتفسير والمفسرون ١: ١١٩.','1','349',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (622,'المسكن. محدث، مفسر، من فقهاء الإمامية، ترجم له صاحب أعيان الشيعة وقال: "كان حيا سنة ١١٠٣ هـ " (١).

القمي [.. - حيا ٣٢٩ هـ / .. - حيا ٩٤١ م]

<span data-type="title" id=toc-880>علي بن إبراهيم بن هاشم القمي، أبو الحسن:</span>
مؤرخ، مفسر، من فقهاء الشيعة الإمامية. أخذ عن الكليني المتوفي سنة ٣٢٩ هـ. قال الذهبي: "رافضي جلد، له تفسير فيه مصائب" نسبته إلى مدينة "قم" بإيران. من كتبه "تفسير القرآن" مخطوط، منه نسخ في مدن مشهد والنجف، وله أيضا "الناسخ والمنسوخ" و "فضائل القرآن" (٢).

القطان [٢٥٤ - ٣٤٥ هـ / ٨٦٨ - ٩٥٦ م]

<span data-type="title" id=toc-881>علي بن إبراهيم بن سلمة بن بحر، أبو الحسن القطان:</span>
محدث قزوين وعالمها.
_________
(١) أعيان الشيعة ٤١: ٣١ ومعجم المؤلفين ٦: ٢٩٤.
(٢) معجم الأدباء ١٢: ٢١٥ وميزان الاعتدال ٣: ١١١ ولسان الميزان ٤: ١٩١ والرجال للنجاشي ١٩٧ والفهرست للطوسي (طبقة كلكتا) ٨٩ والذريعة ٤: ٢٨٥ وهدية العارفين ١: ٦٧٨ وطبقات المفسرين ١: ٣٨٥ ومنتهى المقال ٢٠٤ ومعجم المؤلفين ٧: ٩ وتاريخ التراث العربي ١: ٢١٦ والفهرست لابن النديم ٢٢٢ وكشف الحجب للكنتوري ١٣١.','1','349',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (623,'رحل في طلب العلم، فسمع من كبار علماء عصره. قال الخليلي: "شيخ عالم بجميع العلوم، التفسير والفقه والنحو واللغة". وقال تلميذه ابن فارس اللغوي: "سمعت القطان بعد ما علت سنه يقول: "حين رحلت كنت أحفظ مائة ألف حديث، وأنا اليوم لا أقوم على حفظ مائة حديث" (١).

الحوفي [.. - ٤٣٠ هـ / .. - ١٠٣٩ م]

<span data-type="title" id=toc-882>علي بن إبراهيم بن سعيد بن يوسف الحوفي، أبو الحسن:</span>
نحوي، عالم باللغة والتفسير، من قرية شبرا (من حوف بلبيس بمصر). قال ابن خلكان: "له تفسير جيد، واشتغل عليه خلق كثير وانتفعوا به، ورأيت خطه على كثير من كتب الأدب قد قرئت عليه وكتب لأربابها بالقراءة كما جرت عادة المشايخ". من آثاره "البرهان في علوم القرآن" قال صاحب كشف الظنون: "وهو كتاب كبير في عشر مجلدات ذكر فيه الإعراب والغريب والتفسير، وكتاب "إعراب القرآن" قال صاحب الكشف أيض١: "في عشر مجلدات، وهو أوضحها (أي كتب إعراب القرآن). وقال
_________
(١) طبقات المفسرين للداودي ١: ٣٨٢ وتذكرة الحفاظ ٣: ٨٥٦ والنجوم الزاهرة ٣: ٣١٥ ومعجم الأدباء ١٢. والشذرات ٢: ٢٧٠ والعبر ٢: ٢٦٧ وتاريخ التراث العربي ١: ٤٦٤.','1','350',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (624,'القفطي: "أبدع فيه، يتنافس العلماء في تحصيله" (١).

ابن نجية [٥٠٨ - ٥٩٩ هـ / ١١١٤ - ١٢٠٢ م]

<span data-type="title" id=toc-883>علي بن إبراهيم بن نجا بن غنائم الأنصاري، زين الدين، أبو الحسن، المعروف بابن نجية:</span>
واعظ، مفسر، من فقهاء الحنابلة. ولد وتعلم بدمشق، واشتغل بالوعظ والتفسير مدة. بعثه نور الدين محمود بن زنكي رسولا إلى بغداد سنة ٥٦٤ هـ. ووعظ بجامع المنصور. ثم انتقل إلى مصر في أيام صلاح الدين الأيوبي وأقام بها إلى أن مات. وكان صلاح الدين يسميه عمرو بن العاص. قال أبو شامة: "كان صلاح الدين يكاتبه، ويحضر مجلسه هو وأولاده، العزيز، وغيره، ولما فتح صلاح الدين القدس كان معه، وتكلم أول
_________
(١) طبقات المفسرين للسيوطي ٢٥ وطبقات المفسرين للداودي ١: ٣٨١ وطبقات النحاة لابن قاضي شهبة ٢: ١٣٢ والعبر ٣: ١٧٢ والأعلام ٥: ٥٣ وحسن المحاضرة ١: ٥٣٢ وكشف الظنون ١٢٢ و ٢٤١ و ٤٤٦ ومفتاح السعادة ٢: ١٠٧ وهدية العارفين ١: ٦٨٧ ومعجم المؤلفين ٧: ٥ وإنباه الرواة ٢: ٢١٩ وبغية الوعاة ٢: ١٤٥ ومعجم الأدباء ٢: ٢٢١ وابن خلكان ٣: ٣٠٠ والشذرات ٣: ٢٤٧ واللباب ١: ٤٠٢ والبداية والنهاية ١٢: ٤٧.','1','350',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (625,'جمعة أقيمت فيه على كرسي الوعظ، وكان يوما مشهودا". (١)

الأنصاري [٨٤٠ - حيا ٨٧٣ هـ / ١٤٣٦ - حيا ١٤٦٨ م]

<span data-type="title" id=toc-884>علي بن إبراهيم بن أبي بكر الأنصاري، نور الدين، المقسي، ويعرف بالكبشي وبالكلبشاوي:</span>
مفسر، فقيه شافعي، ولد وتعلم بالقاهرة. ودخل دمشق وناب في القضاء، وحج وجاور، ودرس بدمياط وغيرها. أثنى عليه السخاوي وقال: له "الفيض القدسي على آية الكرسي" أوقفني عليه وأجاد فيه". وقال صاحب إيضاح المكنون: فرغ منها سنة ٨٧٣ هـ (٢).

الكوفي [.. - ٣٥٢ هـ / .. - ٩٦٣ م]

<span data-type="title" id=toc-885>علي بن أحمد العلوي الكوفي، أبو القاسم:</span>
فقيه، أصولي، متكلم، مفسر، حكيم، من غلاة الشيعة، من أهل الكوفة، كان في بدايته على طريقة الإمامية، وصنف كتبا في الفقه والأوصياء،
_________
(١) الذيل على طبقات الحنابلة ١: ٤٣٦ وطبقات المفسرين للداودي ١: ٣٨٣ والنجوم الزاهرة ٦: ١٨٣ والشذرات ٤: ٣٤٠ وحسن المحاضرة ١: ٥٥١ والذيل عل الروضتين ٣٤ والكامل لابن الأثير ١١: ٣٩٩.
(٢) الضوء اللامع ٥: ١٥٢ وإيضاح المكنون ٢: ٢١٥ ومعجم المؤلفين ٧: ٤.','1','351',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (626,'ثم أظهر مذهب "المخمسة" القائلين بألوهية علي بن أبي طالب، وبأن "سلمان الفارسي، والمقداد، وأبا ذر، وعمارا، وعمرو بن أمية الضمري، هم الموكلون بمصالح العالم من قبل الرب" وألف كتبا في هذا وغيره. وله كتاب "تفسير القرآن". توفي بموضع يقال له "كرمي" بقرب شيراز (١).

ابن حزم [٣٨٤ - ٤٥٦ هـ / ٩٩٤ - ١٠٦٤ م]

<span data-type="title" id=toc-886>علي بن أحمد بن سعيد بن حزم الظاهري، أبو محمد:</span>
عالم الأندلس في عصره، مؤرخ، محدث، فقيه، مفسر، أديب، أصولي، متكلم، مشارك في علم النحو واللغة والشعر والطب والمنطق والفلسفة، ولد بقرطبة، وكانت له ولأبيه من قبله رياسة الوزارة وتدبر المملكة، فزهد بها وانصرف إلى العلم والتأليف. وكان شافعي المذهب، ثم أصبح ظاهريا، فاشتهر بمذهبه الظاهري في الفقه، ومؤدى هذا المذهب: أن كل قياس لا يستند إلى القرآن والحديث باطل. وانتقد كثيرا من العلماء والفقهاء، فأجمع هؤلاء على تضليله
_________
(١) أعيان الشيعة ٤١: ٤٤ وروضات الجنات ٣٨٢ ومنهج المقال ٢٢٥ وكتاب الرجال ١٨٨ وابن النديم ١: ١٩٢ وهدية العارفين ١: ٦٨١ وفهرست الطوسي ٩١ والأعلام ٥: ٥٧ وإيضاح المكنون ١: ٣٠٩.','1','351',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (627,'وحذروا المسؤولين من فتنته، ونهوا عوامهم عن الدنو منه، والأخذ عنه، فأقصي وطورد، فرحل إلى بادية لبلة (من بلاد الأندلس) فتوفي بها. من تصانيفه الكثيرة "الناسخ والمنسوخ" طبع (١).

الواحدي [.. - ٤٦٨ هـ / .. - ١٠٧٦ م]

<span data-type="title" id=toc-887>علي بن أحمد بن محمد بن علي بن متويه، أبو الحسن الواحدي:</span>
أوحد عصره في التفسير، عالم بالأدب، كان من أولاد التجار، أصله من ساوة (بين الري وهمذان). ولد بنيسابور، وأخذ عنه شيوخها. قال القفطي: "وسار الناس إلى علمه، واستفادوا من فوائده" وكان نظام الملك يكرمه ويعظمه. والواحدي نسبة إلى الواحد بن الديل بن مهرة. توفي بنيسابور. له "البسيط" تفسير كبير، أكثر فيه من الإعراب والشواهد واللغة،
و
_________
(١) بغية الملتمس ٤٠٣ وجذوة المقتبس ٢٩٠ ومطمح الأنفس ٤٥ ونفح الطيب ٢: ٧٧ والذخيرة ١/ ١: ١٤٠ والمغرب في حل المغرب ١: ٣٥٤ والعبر ٣: ٢٣٩ وطبقات صاعد ٨٦ والمجددون في الإسلام ١٩٠ وتذكرة الحفاظ ١١٤٦ ومعجم الأدباء ١٢: ٢٣٥ والمعجب ٣٠ ومرآة الجنان ٣: ٧٩ وتاريخ الحكماء ١٥٦ وابن خلكان ٣: ٣٢٥ والصلة ٤٠٨ ولسان الميزان ٤: ١٩٨ والشذرات ٣: ٢٩٩ وهدية العارفين ١: ٦٩٠ وابن حزم لسعيد الأفغاني، والأعلام ٥: ٥٩ ومعجم المؤلفين ٧: ١٦ وفيه أسماء مصادر أخرى في الحاشية.','1','352',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (628,'"الوسيط" في التفسير أيضا، وهو مختار من البسيط، و "الوجيز" وقد طبع: بمصر سنة ١٣٠٥ هـ بهامش "التفسير المنير لمعالم التنزيل". و "أسباب النزول" طبع بمصر سنة ١٣١٥ هـ (١).

الحرالي [.. - ٦٣٨ هـ / .. - ١٢٤١ م]

<span data-type="title" id=toc-888>علي بن أحمد بن الحسن بن إبراهيم الحرالي التجيبي، أبو الحسن:</span>
مفسر، فقيه، أصولي، من علماء المغرب، أصله من حرالة، من أعمال مرسية بالأندلس. ولد بمراكش وبها نشأ وتعلم. ورحل إلى المشرق، وحج، ولقي العلماء، وتصوف، ثم عاد إلى المغرب واستوطن بجاية في الجزائر. وعاد إلى المشرق فأقام في بلبيس بمصر، ووقع بينه وبين صاحب الديار المصرية كلام، فأخرج منها، فتوجه إلى الشام وأقام بحماة إلى أن توفي. من
_________
(١) طبقات المفسرين للسيوطي ٢٣ وطبقات المفسرين للداودي ١: ٣٨٧ وطبقات النحاة لابن قاض شهبة ٢: ١٣٥ وطبقات السبكي ٥: ٢٤٠ وطبقات ابن هداية الله (بتحفيقنا) ١٦٨ والنجوم الزاهرة ٥: ١٠٤ والعبر ٣: ٢٦٧ وبغية الوعاة ٢: ٤٥ وإنباه الرواة ٢: ٢٢٣ والشذرات ٣: ٣٣٠ ومعجم الأدباء ١٢: ٢٥٧ ومرآة الجنان ٢: ٩٦ وغاية النهاية ١: ٥٢٣ ودمية القصر ٢٠٣ والبداية والنهاية ١٢: ١١٤ وروضات الجنات ٤٨٤ وابن خلكان ٣: ٣٠٣ ومفتاح السعادة ١: ٤٠٢ وفي حاشية إنباه الرواة أسماء مصادر أخرى.','1','352',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (629,'كتبه "مفتاح الباب المقفل لفهم القرآن المنزل" في التفسير، مخطوط، قال الغبريني: "سلك فيه مسلك البيان والإيضاح على نحو ما يقتضيه علم العربية وعلم تنقيح المعقول، وما يبقى وراء هذا سوى علم الأسباب التي عند النزول وعند الحاجة إليها لا بد من ذكرها". وقال الذهبي: "كان فلسفي التصوف، ملأ تفسيره بحقائقه ونتائج فكره، وزعم أنه يستخرج من علم الحروف وقت خروج الدجال ووقت طلوع الشمس من مغربها، وهذه علوم وتحديدات ما علمتها رسل الله، بل كل منهم حتى نوح (عليه السلام) يتخوف من الدجال، وينذر أمته من الدجال، وهذا نبينا - صلى الله عليه وسلم - يقول: "إن يخرج وأنا فيكم فأنا حجيجه". وهؤلاء الجهلة أخوته يدعون متى يخرج، نسأل الله السلامة" وعلق المقري على كلام الذهبي بقوله: "ووقع للذهبي في حق الحرالي كلام على عادته في الحط على هذه الطائفة، وكلام الذهبي في الشيخ يرده كلام الغبريني"، إذ هو أعرف به" (١).
_________
(١) طبقات المفسرين للسيوطي ٢٢ وطبقات المفسرين للداودي ١: ٣٨٦ وميزان الاعتدال ٣: ١١٤ ولسان الميزان ٤: ٢٥٤ وعنوان الدراية (بتحقيقنا) ١٤٣ والعبر ٥: ١٥٧ والشذرات ٥: ١٨٩ وتاج العروس ٧: ٢٧٧ والتكلمة لابن الأبار ٦٨٧ ونفح الطيب ٢: ١٨٧ والنجوم الزاهرة ٦: ٣١٧ وهدية العارفين ١: ٧٠٧ وكشف الظنون ١٧٦٧ ونيل الابتهاج ٢٠١ ودليل =','1','353',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (630,'المهائمي [٧٧٦ - ٨٣٥ هـ / ١٣٧٤ - ١٤٣٢ م]

<span data-type="title" id=toc-889>علي بن أحمد بن علي المهائمي الهندي، أبو الحسن، علاء الدين، المعروف بالمخدوم، من النوائت:</span>
فقيه، متكلم، مفسر، كان يقول بوحدة الوجود، ولد في مهائم (من بنادر كوكن، وهي ناحية من الدكن بالهند، مجاورة للبحر المحيط) والنوائت قوم في بلاد الدكن، قال الطبري: "طائفة من قريش، خرجوا من المدينة خوفا من الحجاج بن يوسف، فبلغوا ساحل بحر الهند وسكنوا به". من كتبه "تبصير الرحمن وتيسير المنان ببعض ما يشير إلى إعجاز القرآن" طبع، مجلدان، و "رسالة" في تفسير آلم (١).

الشيرازي [٧٨٨ - ٨٦١ هـ / ١٣٨٦ - ١٤٥٧ م]

<span data-type="title" id=toc-890>علي بن أحمد بن محمد الشيرازي، ثم المكي، علاء الدين:</span>
فقيه شافعي،
_________
= مؤرخ المغرب ٢٧٠ وقد وردت نسبته في كثير من المصادر بلفظ الحراني وهو تصحيف، وفيهم من أرخ وفاته سنة ٦٣٧ هـ وهي رواية ثانية.
(١) نزهة الخواطر ٣: ١٠٥ وأبجد العلوم ٨٩٣ ومعجم المطبوعات ١٧١٧ والأعلام ٥: ٦٣ وهدية العارفين ١: ٧٣٠ واسمه فيه: "علي بن أحمد بن إبراهيم بن إسماعيل" وكشف الظنون ٣٣٩ وفيه أن كتاب تبصير الرحمن ... لعلي بن أحمد بن علي بن أحمد الأموي الحنبلي المتوفي سنة ٧١٠ هـ. ومعجم المؤلفين ٧: ٩ والترجمة منقولة عن هدية العارفين.','1','353',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (631,'أصولي، مفسر، نحوي، منطقي، صوفي، ولد ببغداد، وأخذ عن غير واحد، وجال وصحب الرجال. قال السخاوي: "تكلم على الناس في علم التوحيد بعبارة بليغة فصيحة دالة على تحققه بكلام القوم، وأما في علوم الأوائل فكان لا يجارى فيها". استوطن مكة المكرمة بعد سنة ٨٣٠ هـ، وأخذ عنه غير واحد، وعلا صيته، لقيه السخاوي سنة ٨٥٦ هـ وسمع منه شيئا من كتبه. مات بمكة. له "تفسير القرآن" وغير ذلك (١).

الكوفي [القرن الثالث الهجري / القرن التاسع الميلادي]

<span data-type="title" id=toc-891>علي بن أسباط بن سالم الكوفي، أبو الحسن:</span>
فقيه إمامي: مفسر، مقرىء، من أهل الكوفة، كان حيا في أواسط القرن الثالث الهجري، له تصانيف منها "تفسير القرآن" (٢).

الأشعري [٢٦٠ - ٣٢٤ هـ / ٨٧٤ - ٩٣٦ م]

<span data-type="title" id=toc-892>علي بن إسماعيل بن إسحاق بن سالم، أبو الحسن، من نسل الصحابي أبي (موسى الأشعري)</span>
_________
(١) الضوء اللامع ٥: ١٨٩.
(٢) منهج المقال ٢٢٦ وكتاب الرجال ١٧٧ وهدية العارفين ١: ٦٧٣ ومنتهى المقال ٢٠٧ وإيضاح المكنون ١: ٣٠٩ وتاريخ التراث العربي ٢: ٢٥٦ والذريعة ٢: ١٦٤.','1','354',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (632,'موسى الأشعري: مؤسس مذهب الأشاعرة، ومن الأئمة المتكلمين المجتهدين، ولد في البصرة، كان في ابتداء أمره معتزليا، ثم رجع إلى هذا المذهب الحق، مذهب أهل السنة، وجاهر بخلاف المعتزلة وناظرهم وظهر عليهم. ورد على الملحدة والشيعة والجهمية والمعتزلة والخوارج وغيرها. توفي ببغداد. قيل بلغت مصنفاته ثلاثمائة كتاب، منها "المختزن" في علوم القرآن. قال السبكي: "وهو كتاب عظيم جدا بلغ فيه سورة الكهف. وقد انتهى مائة جزء، وقيل أنه أكبر من هذا" (١).

القونوي [٦٦٨ - ٧٢٩ هـ / ١٢٧٠ - ١٣٢٩ م]

<span data-type="title" id=toc-893>علي بن إسماعيل بن يوسف القونوي، علاء الدين، أبو الحسين:</span>
فقيه شافعي، صوفي، عارف بالتفسير والأدب والكلام والأصول. ولد بقونية من بلاد
_________
(١) تاريخ التراث العربي ٢: ٣٧٣ ووفيات الأعيان ٣: ٢٨٤ وتاريخ بغداد ١١: ٣٤٦ وطبقات المفسرين للداودي ١: ٣٩٠ وطبقات السبكي ٣: ٣٤٧ والديباج ١٩٣ وابن النديم ٢٣١ والنجوم الزاهرة ٣: ٢٥٩ والمنتظم ٦: ٣٣٢ وروضات الجنات ٤٧٤ والشذرات ٢: ٣٠٣ والعبر ٢: ٢٠٢ ومفتاح السعادة ٢: ١٥٢ والجواهر المضية ١: ٣٥٣ وتذكرة الحفاظ ٨٢١ والبداية والنهاية ١١: ١٨٧ والخطط المقريزية ٢: ٣٥٩ والأنساب ١: ٢٦٦ وهدية العارفين ١: ٦٧٦ والأعلام ٥: ٦٩ ومعجم المؤلفين ٧: ٣٥ وكشف الظنون ٤٤٠.','1','354',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (633,'الروم، ونزل بدمشق سنة ٦٩٣ هـ فدرس بالإقبالية. ثم انتقل إلى القاهرة، فتصوف وتلقى علوم الأدب والفقه، وولي مشيخة سعيد السعداء. ثم ولي قضاء الشام سنة ٧٢٧ هـ فباشره بعفة وصلف وأقام بدمشق إلى أن توفي. قال الإسنوي: "كان أجمع من رأيناه للعلوم خصوصا العقلية واللغوية، لا يشار فيها إلا إليه، وكان قليل المثل من عقلاء الرجال، صالحا كثير الإنصاف، طاهر اللسان، مهيبا وقورا، وكان الناصر يعظمه ويثني عليه" (١).

الكرماني [.. - ١١٤٠ هـ / .. - ١٧٢٧ م]

<span data-type="title" id=toc-894>علي أصغر بن عبد الصمد القنوجي البكري الكرماني:</span>
صوفي، مفسر، من فقهاء الحنفية. من أهل الهند، بكري النسب، أصله من المدينة المنورة، انتقل بعض أسلافه إلى كرمان فنسبوا إليها. مولده ووفاته في قنوج. من آثاره "ثواقب التنزيل" في التفسير، كتفسير الجلالين (٢).
_________
(١) بغية الوعاة ٢: ١٤٩ وطبقات المفسرين للداودي ١: ٣٩١ والسبكي ٦: ١٤٤ والدرر الكامنة ٣: ٩٣ والبدر الطالع ١: ٤٣٩ ومرآة الجنان ٤: ٢٨٠ والبداية والنهاية ١٤: ١٤٧ والشذرات ٦: ٩٠ وهدية العارفين ١: ٧١٧ والأعلام ٥: ٦٩.
(٢) هدية العارفين ١: ٧٦٦ وإيضاح المكنون ١: ٣٤٨ وأبجد العلوم ٩٣٠ والأعلام ٥: ٧٠ ومعجم المؤلفين ٧: ٣٨.','1','355',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (634,'القزويني [.. - ١١٢٠ هـ / .. - ١٧٠٨ م]

<span data-type="title" id=toc-895>علي أصغر بن محمد بن يوسف القزويني:</span>
فقيه إمامي، مفسر، مشارك في بعض العلوم. من أهل قزوين. من كتبه "رموز التفاسير" في تفسير القرآن (١).

قره باش [.. - ١٠٩٧ هـ / .. - ١٦٨٦ م]
على الأطول بن محمد القسطموني الرومي، المعروف ب "قره باش": صوفي، عارف بالكلام والتفسير، تركي مستعرب، من أهل قسطموني - مدينة في شمال غربي تركيا الآسيوية - توفي بين مكة المكرمة والمدينة المنورة بعد أداء فريضة الحج. من كتبه "تفسير سورة طه" (٢).

اليزدي [.. - حيا قبل ١٢٨٨ هـ / .. حيا قبل ١٨٧١ م]

<span data-type="title" id=toc-896>علي أكبر بن محمد جعفر الحسني، الحسيني، اليزدي:</span>
فقيه إمامي، عارف بالأدب واللغة والمنطق والتفسير، له
_________
(١) روضات الجنات ٤١٣ وإيضاح المكنون ١: ٥٨٣ وهدية العارفين ١: ٧٦٤ وفوائد الرضوية ١: ٢٧٤ ومعجم المؤلفين ٧: ٣٩.
(٢) هدية العارفين ١: ٧٦٢ وإيضاح المكنون ١: ٣٠٦ ومعجم المؤلفين ٧: ٣٩.','1','355',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (635,'تصانيف منها "تفسير القرآن الكريم" لم يكمله (١).

الهمذاني [١٢٨٠ - ١٣٢٥ هـ / ١٨٦٣/ ١٩٠٨ م]

<span data-type="title" id=toc-897>علي أكبر بن محمد الهمذاني، صدر الدين:</span>
عالم إمامي، ناظم، مشارك في بعض العلوم. من أهل همذان، مولدا ووفاة، ودفن بالنجف. من آثاره "ناسخ التفاسير"، في ثمانين ألف بيت تقريبا (٢).

ابن الساعي [٥٩٣ - ٦٧٤ هـ / ١١٩٧ - ١٢٧٥ م]

<span data-type="title" id=toc-898>علي بن أنجب بن عثمان بن عبد الله، تاج الدين، أبو طالب، ابن الساعي:</span>
مؤرخ، من كبار المصنفين، كان خازن الكتب للمستنصر العباسي. مولده ووفاته ببغداد. قال الذهبي: "صحب ابن النجار وقرأ عليه تاريخه لبغداد، وسمع من جماعة، وما هو من أحلاس الحديث بل عداده في الاخباريين، مصنفاته كثيرة جدا لعلها وقر بعير". منها "مختصر تفسير البغوي" (٣).
_________
(١) أعيان الشيعة ٤١: ٨٤ ومعجم المؤلفين ٧: ٤٠.
(٢) أعيان الشيعة ٤١: ٨٥ ومعجم المؤلفين ٤١: ٨٥.
(٣) طبقات المفسرين ١: ٣٩٤ والشذرات ٥: ٣٤٣ وتذكرة الحفاظ ١٤٦٩ وتاريخ علماء بغداد ١٣٧ وابن كثير ١٣: ٢٧٠ والرسالة المستطرفة ١٤١ والتعريف =','1','356',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (636,'المرغيناني [٥٣٠ - ٥٩٣ هـ / ١١٣٥ - ١١٩٧ م]

<span data-type="title" id=toc-899>علي بن أبي بكر بن عبد الجليل الفرغاني المرغيناني، أبو الحسن، برهان الدين:</span>
مفسر، حافظ، محدث، فرضي، من كبار فقهاء الحنفية في عصره. نسبته إلى مرغينان - مدينة في فرغانة -. ترجم له اللكنوي وأثنى عليه وقال: "عده ابن كمال باشا من المجتهدين في المذهب الحنفي". مات بسمرقند (١).

ابن الحنائي [٩١٦ - ٩٧٩ هـ / ١٥١١ - ١٥٧١ م]

<span data-type="title" id=toc-900>علي جلبي بن أمر الله بن عبد القادر الحميدي الرومي، الشهير بابن الحنائي:</span>
مفسر، قاض، من أكابر علماء الحنفية، تركي مستعبر، ولي قضاء العسكر بالأناضول. من آثاره "حاشية على تفسير البيضاوي" و "حاشية على الكشاف" للزمخشري (٢).
_________
= بالمؤرخين ١: ٩٢ وأعيان الشيعة ٤١: ٩٨ وهدية العارفين ١: ٧١٢ والجواهر المضية ١: ٣٥٤ والحوادث الجامعة ٣٨٦ ومعجم المؤلفين ٧: ٤٢ والأعلام ٥: ٧١ وتاريخ آداب اللغة العربية ٣: ٢١٣.
(١) الفوائد البهية ١٤١ وهدية العارفين ١: ٧٠٢ والجواهر المضية ١: ٣٨٣ وتاج التراجم ٣١ والأعلام ٥: ٧٣ ومعجم المؤلفين ٧: ٤٥ وطبقات الفقهاء لطاش كبري زاده ٥: ٩٨ وسركيس ١٧٣٩.
(٢) هدية العارفين ١: ٧٤٨.','1','356',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (637,'النوري [... - ١٢٤٦ هـ / ... - ١٨٣٠ م]

<span data-type="title" id=toc-901>علي بن جمشيد النوري، المازندراني، ثم الأصفهاني:</span>
فقيه، مفسر، متكلم، حكيم، من علماء الشيعة الإمامية. أصله من مازندران - بلاد واقعة في إيران جنوب بحر قزوين وشمال جبال البرز - سكن أصفهان. من كتبه "تفسير سورة التوحيد" في مجلد (١).

القزوينى [... - ٣٢٨ هـ / ... - ٩٤٠ م]

<span data-type="title" id=toc-902>علي بن جمعة بن زهير بن قحطبة الأزدي، أبو الحسن، القزويني:</span>
عالم بالأدب والتفسير والحديث، من أهل قزوين، سمع بها وبهمذان وبغداد ومكة. وروى عنه جماعة. ذكره الرافعي في تاريخ قزوين وأثنى عليه (٢).

السعدي [١٥٤ - ٢٤٤ هـ / ٧٧١ - ٨٥٨ م]

<span data-type="title" id=toc-903>علي بن حجر بن إياس السعدي المروزي، أبو الحسن:</span>
حافظ، محدث، له أدب وشعر. روى عنه البخاري ومسلم
_________
(١) روضات الجنات ٤١٧ وهدية العارفين ١: ٧٧٤
(٢) تاريخ قزوين ٤: ٤٠٦ وطبقات المفسرين ١: ٣٩٥.','1','357',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (638,'في صحيحيهما، وعامة الخرسانيين. قال الخطيب البغدادي: "كان يسكن قديما بغداد، ثم انتقل إلى مرو فنزلها ونسب إليها، وانتشر حديثه بها". وقال الذهبي: "حافظ كبير، رحال جوال له تصانيف، منها كتاب "أحكام القرآن" (١).

ابن فضال [... - نحو ٢٩٠ هـ / ... - نحو ٩٠٣ م]

<span data-type="title" id=toc-904>علي بن الحسن بن علي بن فضال، أبو الحسن:</span>
فقيه إمامي، مفسر، محدث، واعظ، مشارك في بعض العلوم، يعد من الثقات، من أهل الكوفة. له تصانيف، منها "تفسير القرآن" و "فضائل القرآن" و "كتاب التنزيل من القرآن" (٢).
_________
(١) طبقات المفسرين للداودي ١: ٣٩٥ وتذكرة الحفاظ ٤٥٠ وخلاصة تهذيب الكمال ٢٣٠ والعبر ١: ٤٤٣ والنجوم الزاهرة ٢: ٣١٩ وتهذيب التهذيب ٧: ٢٩٣ واللباب ١: ٥٤٤ والأعلام ٥: ٧٧ وهدية العارفين ١: ٦٧٢ وتاريخ بغداد ١١: ٤١٦ ومعجم المؤلفين ٧: ٥٧ والكشف ١: ٢٠.
(٢) طبقات المفسرين للداودي ١: ٣٩٧ ومنهج المقال ٢٣٠ وهدية العارفين ١: ٦٧٥ وإيضاح المكنون ١: ٣٠٣ ومعجم المؤلفين ٧: ٦٦ والأعلام ٥: ٧٩ وابن النديم ٢٧٨ والذريعة ١: ٦٣ والرجال ١٨١ وقيل في وفاته نحو سنة ٢٩٠ هـ.','1','357',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (639,'الصندلي [... - ٤٨٤ هـ / ... - ١٠٩١ م]

<span data-type="title" id=toc-905>علي بن الحسن بن علي الصندلي النيسابوري، أبو الحسن:</span>
مفسر، واعظ، من المعتزلة، من أهل نيسابور. دخل بغداد مع طغرل بك سنة ١٠٥٥ م، ثم عاد إلى نيسابور وتزهد وانقطع عن زيارة السلاطين، فرآه السلطان ملكشاه في جامعها، فعاتبه، فقال: "أردت أن تكون من خير الملوك حيث تزور العلماء، ولا أكون من شر العلماء حيث أزور الملوك". له "تفسير القرآن" (١).

الرازي [... - ٢٩١ هـ / ... ٩٠٤ م]

<span data-type="title" id=toc-906>علي بن الحسين بن الجنيد الرازي، أبو الحسن:</span>
محدث، حافظ، كان بصيرا بالرجال والعلل، طوف الكثير وسمع أبا جعفر النفيلي وطبقته. عرف في بلده بالمالكي لكونه جمع حديث مالك. من كتبه "أمثال القرآن" (٢).
_________
(١) الفوائد البهية ١٢٥ والجواهر المضية ١: ٣٥٧ وهدية العارفين ١: ٦٩٣ والأعلام ٥: ٨١ ومعجم المؤلفين ٧: ٦٧. وطبقات المفسرين ١: ٣٩٦.
(٢) طبقات المفسرين ١: ٣٩٧ وتذكرة الحفاظ ٦٧١ والعبر ٢: ٨٩ والشذرات ٢: ٢٠٨.','1','358',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (640,'ابن بابويه [... - ٣٢٩ هـ / ... - ٩٤١ م]

<span data-type="title" id=toc-907>علي بن الحسين بن موسى بن بابويه القمي، أبو الحسن:</span>
فقيه، مفسر، كان شيخ الإماميين بقم في عصره، مولده ووفاته فيها. من كتبه "تفسير القرآن" (١).

الشريف المرتضى [٣٥٥ - ٤٣٦ هـ / ٩٦٦ - ١٠٤٤ م]

<span data-type="title" id=toc-908>علي بن الحسين بن موسى بن محمد بن إبراهيم بن موسى الكاظم، أبو القاسم، من أحفاد الحسين بن علي بن أبي طالب:</span>
نقيب الطالبيين، وأحد الأئمة في علم الكلام والفقه والحديث والتفسير والأدب واللغة، يقول بالاعتزال. مولده ووفاته ببغداد. وكثير من مترجميه يرون أنه هو جامع "منهج البلاغة" لا أخوه الشريف الرضي. قال الذهبي: "وهو - أي المرتضى - المتهم بوضع كتاب نهج البلاغة، ومن طالع الكتاب جزم بأنه مكذوب على أمير المؤمنين علي رضي الله عنه .. الخ. له
_________
(١) الذريعة ٢: ٣٤١ والطوسي ٩٣ والنجاشي ١٨٤ وروضات الجنات ٣٧٧ وهدية العارفين ١: ٦٧٨ ومعجم المؤلفين ٧: ٨١ والأعلام ٥: ٨٧.','1','358',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (641,'تصانيف كثيرة، منها "تفسير الفاتحة" و "تفسير سورة البقرة" (١).

الباقولي [... - بعد ٥٤٣ هـ / .. - ١١٤٨ م]

<span data-type="title" id=toc-909>علي بن الحسين بن علي الأصفهاني الباقولي، أبو الحسن، ويعرف بالجامع:</span>
مفسر، نحوي، ضرير. من تصانيفه "البيان في شواهد القرآن" (٢).

الغزنوي [... - ٥٥١ هـ / ... - ١١٥٦ م]

<span data-type="title" id=toc-910>علي بن الحسن بن عبد الله بن محمد، أبو الحسن، برهان الدين، الغزنوي:</span>
واعظ، عارف بالتفسير والفقه، من أهل غزنة، سمع بها وبمرو والعراق، وحدث
_________
(١) معجم الأدباء ١٣: ١٤٦ وميزان الاعتدال ٣: ١٢٤ ولسان الميزان ٤: ٣٢٣ والنجوم الزاهرة ٥: ٣٩ وإنباه الرواة ٢: ٢٤٩ والمنتظم ٨: ١٢٠ ووفيات الأعيان ٣: ٣١٣ وروضات الجنات ٣٨٣ وأعيان الشيعة ٤١: ١٨٨ وتاريخ بغداد ١١: ٤٠٢ وديوان الشريف المرتضى ١: ١١٧ ومعجم المؤلفين ٧: ٨١ وفي الحاشية ثبت بمصادر أخرى.
(٢) معجم الأدباء ١٣: ١٦٤ وإنباه الرواة ٢: ٢٤٧ ونكت الهميان ٢١١ وروضات الجنات ٤٨٥ ودار الكتب الشعبية ١: ١٨٦ وهدية العارفين ١: ٦٩٧ وبغية الوعاة ٢: ١٦٠ والأعلام ٤: ٢٧٩.','1','359',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (642,'ببغداد. وكان حنفيا يميل إلى التشيع. "وكان السلطان مسعود يزوره وبنى له رباطا بباب الأزج، وكان هو يعظم السلطان ولا يعظم الخليفة، فلما مات السلطان أهين الغزنوي، فاستشفع إلى الخليفة، فقال: ما يرضى أن يحقن دمه: وكان يتمنى الموت مما لاقى من الذل بعد العز" (١).

الجامعي [نحو ١٠٧٠ - ١١٣٥ هـ / نحو ١٦٦٠ - ١٧٢٣ م]

<span data-type="title" id=toc-911>علي بن الحسن بن محيي الدين بن عبد اللطيف بن نور الدين علي بن شهاب الدين أحمد بن أبي جامع العاملي الحارثي الهمذاني:</span>
مفسر، من علماء الشيعة الإمامية. ولي مشيخة الإسلام وبعض الوظائف الشرعية في بلدة خلف آباد. من آثاره "الوجيز في تفسير القرآن العزيز" فرغ من تأليفه في النجف سنة ١١١٨ هـ وطبع في بغداد سنة ١٩٥٣ (الجزء الأول) (٢).
_________
(١) طبقات المفسرين ١: ٣٩٨ والنجوم الزاهرة ٥: ٣٢٣ والمنتظم ١٠: ١٦٦ والشذرات ٤: ١٥٩.
(٢) أعلام العرب في العلوم والفنون ٣: ١٣٧ ومقدمة تفسيره "الوجيز". والأعلام ٤: ٢٨١.','1','359',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (643,'الكسائي [... - ١٨٩ هـ / .. - ٨٠٥ م]

<span data-type="title" id=toc-912>علي بن حمزة بن عبد الله بن عثمان الأسدي بالولاء، الكوفي، أبو الحسن الكسائي:</span>
إمام الكوفيين في النحو واللغة، ومؤسس مدرستهم، وأحد القراء السبعة المشهورين. ولد بباحمشا من قرى الكوفة بالعراق، وانتقل إلى الكوفة غلاما، فدرس القرآن على حمزة الزيات وغيره، ثم قرأ النحو بعد الكبر، وتنقل في البادية، وأقام ببغداد، وأدب الرشيد والأمين والمأمون. قال الجاحظ: "كان أثيرا عند الخليفة حتى أخرجه من طبقة المؤدبين إلى طبقة الجلساء والمؤنسين". له مناظرة مشهورة مع سيبويه. مات برنبويه، إحدى قرى الري، هو ومحمد بن الحسن الشيباني في يوم واحد، وكانا خرجا مع الرشيد فقال: "دفنت الفقه والنحو في يوم واحد". له تصانيف منها "المتشابه في القرآن" مخطوط، "ومعاني القرآن" (١).
_________
(١) طبقات المفسرين للداودي ١: ٣٩٩ وتاريخ بغداد ١١: ٤٠٣ وبغية الوعاة ٢: ١٦٢ وتهذيب التهذيب ٧: ٣١٣ وطبقات القراء للذهبي ١: ١٠٠ وابن النديم ٢٩ والشذرات ١: ٣٢١ وروضات الجنات ٤٧١ واللباب ٣: ٤١ ومرآة الجنان ١: ٤٢١ ومراتب النحويين ٧٤ =','1','360',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (644,'السعدي [٧١٢ - ٧٩٢ هـ / ١٣١٢ - ١٣٩٠ م]

<span data-type="title" id=toc-913>علي بن خلف بن خليل بن عطاء الله، علاء الدين، السعدي الغزي:</span>
مفسر، قاض، مؤرخ، من فقهاء الشافعية. من أهل غزة - بفلسطين - مولدا ووفاة، تولى قضاءها مدة، ثم عزل. من آثاره "التبيان في تفسير القرآن" مخطوط، منه المجلدات ١ - ٣ (١).

الحويزي [١٠١٨ - بعد ١٠٨٧ هـ / ١٦٠٩ - بعد ١٦٧٦ م]

<span data-type="title" id=toc-914>علي بن خلف بن عبد المطلب بن حيدر ابن محمد بن فلاح الموسوي الحسيني الحويزي:</span>
مفسر إمامي، عارف بالحديث والأدب، شاعر، من أهل
_________
= ومعجم الأدباء١٣: ١٦٧ وإنباه الرواة ٢: ٢٥٦ وغاية النهاية ١: ٥٣٥ ووفيات الأعيان ٣: ٢٩٥ ونزهة الألباء ٨٩ وهدية العارفين ١: ٦٦٨ وأعيان الشيعة ٤١: ٢٣٥ والمختصر في أخبار البشر ٢: ١٨ والبداية والنهاية ١٥: ٢٠١ ومفتاح السعادة ١: ١٣٠ والنجوم الزاهرة ٢: ١٣٠ وتنقيح المقال ٢: ٢٨٦ وبغية الوعاة ٢: ١٦٢ والموسوعة العربية الميسرة ١٤٦١ ومعجم المؤلفين ٧: ٨٤ والأعلام ٤: ٢٨٣ ومجلة المشرق ١: ٨٦ والذريعة ١٩: ١٥ وفهرس مكتبة قوله ١: ٢٨. وفي وفاته خلاف كثير.
(١) الدرر الكامنة ٣: ١١٦ وشذرات الذهب ٦: ٣٢٣ وشستربتي ٥٣٠٨/ ٥٣٠٩ والأعلام ٤: ٢٨٥.','1','360',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (645,'الحويزة - جنوبي العراق - ومن حكامها. مدحه بعض الشعراء وأثنى عليه صاحب "السلافة". كان معاصرا لصاحب "أمل الآمل" المتوفى سنة ١١٠٤ هـ. من آثاره "منتخب التفاسير" أربع مجلدات (١).

اللكهنوئي [١٢٠٠ - ١٢٥٩ هـ / ١٧٨٦ - ١٨٤٣ م]

<span data-type="title" id=toc-915>علي بن دلدار اللكهنوئي، الهندي:</span>
فقيه إمامي، مفسر، له مشاركة في بعض العلوم، ولد بلكهنوء، وتعلم بها وبالنجف، ثم رجع إلى بلاده ومنها رحل إلى خراسان. من كتبه "التوضيح المجيد في تفسير كلام الله المجيد" مجلدان (٢).

ابن أبي طلحة [... - ١٤٣ هـ / ... - ٧٦٠ م]

<span data-type="title" id=toc-916>علي بن سالم (أبي طلحة) بن المخارق، الهاشمي بالولاء، أبو الحسن:</span>
محدث، مفسر، قال البخاري: كان بالشام. وقال ابن
_________
(١) هدية العارفين ١: ٧٦٢ وإيضاح المكنون ٢: ٥٦٨ وأمل الآمل ٢: ١٨٧ وأعيان الشيعة ٤١: ٢٥٢.
(٢) أعيان الشيعة ٤١: ٢٥٤ ومعجم المؤلفين ٧: ٧١','1','361',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (646,'حجر: أصله من الجزيرة وانتقل إلى حمص. روى عن مجاهد وغيره، وعنه سفيان الثوري والحكم بن عتيبة وجماعة. وذكر الخطيب البغدادي أن الإمام أحمد بن حنبل قال: إن علي بن أبي طلحة الذي روى عنه الثوري كوفي، وهو غير الشامي - صاحب الترجمة -. ولكن ابن حجر قال: "الصواب أنهما واحد". وفي كشف الظنون أنه ممن ألف في التفسير، وأن له "تفسيرا " رواه عن ابن عباس (١).

القاري [... - ١٠١٤ هـ / ... - ١٦٠٦ م]

<span data-type="title" id=toc-917>علي بن سلطان محمد القاري الهروي، نور الدين:</span>
فقيه حنفي، مفسر، عارف بالحديث، مشارك في أنواع من العلوم، ولد وتعلم بهراة، ورحل إلى مكة وأخذ عن علمائها، واستقر بها إلى أن مات. امتحن بالاعتراض على الأئمة لا سيما الشافعي وأصحابه، ولولا ذلك لاشتهرت مؤلفاته بين الناس. من كتبه "كتاب الجمالين،
_________
(١) تاريخ بغداد ١١: ٤٢٨ والتاريخ الكبير للبخاري ٢/ ٣: ٢٨١ وتهذيب التهذيب ٧: ٣٣٩ وهدية العارفين ١: ٦٦٧ وكشف الظنون ٤٢٨ و ٤٢٩ و ٤٦٠.','1','361',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (647,'حاشية على تفسير الجلالين" طبع جزء منه. و "أنوار القرآن وأسرار الفرقان" في التفسير، مخطوط، ثلاثة مجلدات (١).

الدمناتي [١٢٣٤ - ١٣٠٦ هـ / ١٨١٩ - ١٨٨٨ م]

<span data-type="title" id=toc-918>علي بن سليمان الدمناتي (أو الدمنتي) البوجمعوي، أبو الحسن:</span>
فقيه مالكي، مفسر، محدث، من أعلام المغاربة. ولد بدمنات، ودخل مصر سنة ١٢٩٩ هـ، وتوفي بمراكش. من تصانيفه "تفسير القرآن" (٢).

الزهراوي [... - ٤٣١ هـ / .. - ١٠٤٠ م]

<span data-type="title" id=toc-919>علي بن سليمان الزهراوي، أبو الحسن:</span>
فقيه مالكي، عالم بالتفسير والقراءات والفرائض والعدد والهندسة،
_________
(١) عقود الجوهر ٢٦٤ وخلاصة الأثر ٣: ١٨٥ والفوائد البهية (التعليات) ٨ ومعجم المطبوعات ١٧٩١ والبدر الطالع ١: ٤٤٥ وهدية العارفين ١: ٧٥١ وكشف الظنون ٤٤٥ و ٤٥٤ وإيضاح المكنون ١: ١٤٥ والأعلام ٥: ١٢ ومعجم المؤلفين ٧: ١٠٠
(٢) فهرس الفهارس ١: ١٣٣ وهدية العارفين ١: ٧٧٦ ومعجم المطبوعات ٥٢٧ والأعلام ٥: ١٥٤ ومعجم المؤلفين ١: ١٠٣.','1','362',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (648,'مشارك في الطب وغيره، من أهل غرناطة، كان إمام جامعها والخطيب به. أخذ كثيرا من العلوم الرياضية عن مسلمة ابن أحمد المجريطي، وصحبه مدة. من كتبه "تفسير القرآن" (١).

النيسابوري [.. - ٤٩١ هـ / ... - ١٠٩٨ م]

<span data-type="title" id=toc-920>علي بن سهل بن العباس بن سهل النيسابوري، أبو الحسن:</span>
فقيه شافعي، مفسر، من أهل نيسابور. أخذ عن الصابوني والقشيري وعبد الغافر الفارسي. قال السمعاني: "كان إماما فاضلا زاهدا حسن السيرة، مرضي الطريقة، جميل الأثر، عارفا بالتفسير، جمع كتابا فيه". وفي "هدية العارفين" أن له "زاد الحاضر والبادي" في التفسير (٢).

الأقسرائي [.. - ٢١١١ هـ / .. - ١٦٩٩ م]

<span data-type="title" id=toc-921>علي بن شعبان الأقسرائي الرومي، نزيل القسطنطينية:</span>
فقيه حنفي، واعظ،
_________
(١) بغية الملتمس ٤١٠ والصلة ٢: ٣٩٢ ومعجم الأطباء ٣٠٦ وهدية العارفين ١: ٦٨٦ وطباقات المفسرين
(٢) طبقات السبكي ٥: ٢٥٨ وطبقات المفسرين للداودي ١: ٤٠٤ وهدية العارفين ١: ٦٩٤.','1','362',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (649,'مشارك بالتفسير والحكمة وغير ذلك، توفي بالقسطنطينية. من تصانيفه "تفسير آية {والشمس تجري ..} (١).

الشبيني [... - بعد ١١٩٥ هـ / .. - بعد ١٧٨١ م]

<span data-type="title" id=toc-922>علي بن شلبي الشبيني:</span>
مفسر شافعي، قال في فهرس الأزهرية أنه مصري، فلعل نسبته إلى "شبين الكوم" عاصمة محافظة المنوفية بوسط الدلتا. ولم أجد له ترجمة وافية فيما بين يدي الساعة من كتب الرجال. من آثاره "نور الأنوار" ويعرف بتفسير الشبيني، مخطوط، مجلدان، بخطه سنة ١١٩٥ هـ (٢).

الداغستاني [١١٢٥ - ١١٩٩ هـ / ١٧١٣ - ١٧٨٥ م]

<span data-type="title" id=toc-923>علي بن صادق بن محمد بن إبراهيم بن حسين الداغستاني:</span>
محدث، مفسر، مشارك في أنواع من العلوم، أصله من بلدة شماخ. قرأ في بلاده ثم في ديار بكر والحجاز، ثم استقر في دمشق ودرس
_________
(١) هدية العارفين ١: ٧٦٤
(٢) فهرس الأزهرية ١: ٢٨٠، ٢٥٤، ٢١٣، ٣٠٢، و ٢: ٥٤٧ والأعلام ٤: ٢٩٣.','1','363',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (650,'الحديث في الجامع الأموي تحت قبة النسر، وتوفي بها. من تصانيفه "حاشية على تفسير البيضاوي" (١)

القرمي [... - ٧٧٤ هـ / ... - ١٣٧٢ م]

<span data-type="title" id=toc-924>علي بن صلاح بن أبي بكر بن محمد، علاء الدين، السحومي القرمي:</span>
عالم بالتفسير والعربية والفقه، أقام مدة بحلب، ودرس وانتفع به الناس. قال في الدرر الكامنة: "عالم جليل القدر، يسر القلب ويشرح الصدر، كان عارفا بالفقه والتفسير والأصول والعربية، كثير الانجماع مقبلا على شأنه". مات بحلب (٢).

الصعدي [... - نحو ١٠٧٠ هـ / ... - ١٦٦٠ م]

<span data-type="title" id=toc-925>علي بن صلاح بن علي بن محمد بن عبد الله الصعدي، اليماني، الزيدي:</span>
محدث، أصولي، عارف بالتفسير، من
_________
(١) هدية العارفين ١: ٧٧٠ وسلك الدرر ٣: ٢١٥ وإيضاح المكنون ١: ١٤٥ وثبت ابن عابدين ٢٧ والأعلام ٥: ١٠٦ ومعجم المؤلفين ٧: ١٥٨.
(٢) طبقات المفسرين للداودي ١: ٤٠٤ والدرر الكامنة ٣: ١٢٦.','1','363',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (651,'أهل صعدة باليمن. من تصانيفه "التفصيل لأسباب التنزيل" (١).

الكوكباني [١١٢٠ - ١١٩١ هـ / ١٧٠٨ - ١٧٧٧ م]

<span data-type="title" id=toc-926>علي بن صلاح الدين بن علي بن صلاح الدين الكوكباني الحسني، جمال الدين:</span>
مؤرخ، باحث، من علماء الزيدية باليمن. ولد بكوكبان، وتوفي بصنعاء. له تصانيف منها "الأصداف المنتقاة من سلك جواهر الاسعاف شرح شواهد البيضاوي والكشاف" (٢).

علي بن أبي طالب [٢٣ ق هـ - ٤٠ هـ / ٦٠٠ - ٦٦١ م]

<span data-type="title" id=toc-927>علي بن أبي طالب بن عبد المطلب الهاشمي القرشي، أبو الحسن:</span>
أمير المؤمنين، رابع الخلفاء الراشدين، وأحد العشرة المبشرين، وابن عم النبي (صلى الله عليه وسلم) وصهره وصاحب رايته، وأفقه أهل عصره في أمور دينه ودنياه، وأحد الأبطال الشجعان، وأكابر الخطباء والعلماء بالقضاء. ولد بمكة، وعاش في كنف النبي (صلى الله عليه وسلم) ولم يفارقه، وآمن
_________
(١) هدية العارفين ١: ٧٦٠ ومعجم المؤلفين ٧: ١١١.
(٢) ملحق البدر الطالع ١٦٥ والأعلام ٥: ١٠٧.','1','364',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (652,'برسالته وهو في العاشرة، وافتداه بنفسه ليلة الهجرة، وشهد معه جميع الغزوات، إلا تبوك لأن النبي (صلى الله عليه وسلم) استخلفه في أهل بيته. تزوج فاطمة بنت الرسول وأنجب منها الحسن والحسين وزينب وأم كلثوم. ولما آخى النبي (صلى الله عليه وسلم) بين أصحابه قال له: أنت أخي. بايعه المسلمون بعد مقتل عثمان بن عفان سنة ٣٥ هـ، وبدأ فعزل عمال عثمان على الأمصار، فمنهم من قبل، ومنهم من رفض. وكان معاوية بن أبي سفيان والي الشام من الرافضين، وبينما كان علي يعد العدة للسير إليه، قام بعض أكابر الصحابة يطلبون القبض على قتلة عثمان وقتلهم، وتوقى علي الفتنة فتريث، فغضبت عائشة وقام معها جمع كبير في مقدمتهم طلحة والزبير، وقاتلوا عليا، فكانت وقعة الجمل (سنة ٣٦ هـ) التي انتصر فيها علي، واتخذ بعدها الكوفة عاصمة له. ثم كانت وقعة صفين (سنة ٣٧ هـ) بين جيش علي وجيش معاوية، واستمرت مئة وعشرة أيام، قتل فيها من الفريقين سبعون ألفا، وانتهت بتحكيم أبي موسى الأشعري وعمرو بن العاص، فاتفقا سرا على خلع علي ومعاوية، وأعلن أبو موسى ذلك، وخالفه عمرو فأقر معاوية. فافترق المسلمون ثلاثة أقسام:','1','364',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (653,'الأول بايع لمعاوية وهم أهل الشام، والثاني حافظ على بيعته لعلي وهم أهل الكوفة، والثالث اعتزلهما ونقم على علي رضاه بالتحكيم، ثم كانت وقعة النهروان (سنة ٣٨ هـ) بين علي وأباة التحكيم، فقاتلهم، فقتلوا كلهم وكانوا ألفا وثمانمائة، فيهم جماعة من خيار الصحابة. وأقام علي بالكوفة إلى أن قتله عبد الرحمن بن ملجم المرادي غيلة في مؤامرة ١٧ رمضان المشهورة. واختلف في مكان قبره. وهو أكثر الخلفاء الراشدين رواية عنه في التفسير، لتأخر وفاته إلى زمن كثرت فيه حاجة الناس إلى من يفسر لهم ما خفي عنهم من معاني القرآن، وذلك ناشىء من اتساع رقعة الإسلام، ودخول كثير من الأعاجم في دين الله، مما كاد يذهب بخصائص اللغة العربية" (١).
_________
(١) علي وبنوه لطه حسين، والإمام علي لعبد الفتاح عبد المقصود، وعلي بن أبي طالب لفؤاد أفرام البستاني، وعبقرية الإمام لعباس محمود العقاد، وعلي بن أبي طالب لحنا نمر، وعلي بن أبي طالب لمحمد سليم الجندي، والمعرفة ١٦٦٧ والموسوعة العربية الميسرة ١٢٣٠ وحياة علي بن أبي طالب لمحمد حبيب الله الشنقيطي، وحلية الأولياء ١: ٦١ والإصابة، الترجمة ٥٦٩٠ وتاريخ الخميس ٢: ٢٧٦ ومقاتل الطالبيين ١٤ والإسلام والحضارة العربية ٢: ١٤١ والرياض النضرة ٢: ١٥٣ وشرح نهج البلاغة ٢: ٥٧٩ والكامل في التاريخ ٣: ٣٨٧ وانظر فهرسته، =','1','365',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (654,'ابن كردان [... - ٣٢٤ هـ / ... - ١٠٣٣ م]

<span data-type="title" id=toc-928>علي بن طلحة بن كردان، أبو القاسم:</span>
نحوي، من أهل واسط، مولدا ووفاة، أخذ عن أبي علي الفارسي وغيرهما. وكان متصوفا نزيها. زاره فخر الملك وزير ابن بهاء الدولة وبذل له فلم يقبل. قال الحافظ السلفي: "صنف كتابا كبيرا في "إعراب القرآن" كان يقارب خمسة عشر مجلدا، ثم بدا له فيه فغسله قبل موته" (١).

الشربيني [... - بعد ١٠٢٨ هـ / ... - بعد ١٦١٩ م]

<span data-type="title" id=toc-929>علي بن عبد الرحمن بن محمد الخطيب الشربيني، أبو الحسن:</span>
مفسر، من فقهاء الشافعية، من أهل شربين الغربية بمصر. من آثاره "فتح الرحيم الرحمن" في تفسير آية {إن الله يأمر بالعدل والإحسان} (٢).
_________
= وتاريخ الطبري، ٢: ٣٠٩ والبدء والتاريخ ٥: ٧٣ وصفة الصفوة ١: ١١٨ والأعلام ٤: ٢٩٥ والشذرات ١: ٤٩.
(١) معجم الأدباء ١٣: ٣٩٠.
(٢) هدية العارفين ١: ٧٥٤ وإيضاح المكنون ٢: ١٦٥ ومعجم المؤلفين ٧: ١٢٠.','1','365',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (655,'الجرجاني [... : ٣٩٢ هـ / ... - ١٠٠٢ م]

<span data-type="title" id=toc-930>علي بن عبد العزيز بن الحسن بن علي الجرجاني، أبو الحسن:</span>
قاض، مفسر، عالم بالأدب، من فقهاء الشافعية. من أهل جرجان. أكثر من التطواف، وأخذ عن شيوخ وقته وعلماء عصره في العراق والشام والحجاز. ولاه الصاحب بن عباد قضاء جرجان، فقضاء الري، فقضاء القضاة. مات بنيسابور، وقيل بالري، وهو دون السبعين، ودفن بجرجان. له "تفسير القرآن المجيد" (١).

السبكي [٦٨٣ - ٧٥٦ هـ / ١٢٨٤ - ١٣٥٥ م]

<span data-type="title" id=toc-931>علي بن عبد الكافي بن علي بن تمام بن يوسف السبكي الأنصاري الخزرجي، أبو الحسن، تقي الدين:</span>
شيخ الإسلام في عصره، من أكابر فقهاء الشافعية،
_________
(١) معجم الأدباء ١٤: ١٤ ووفيات الأعيان ٣: ٢٧٨ والسبكي ٣: ٤٥٩ ويتيمة الدهر ٤: ٣ وتاريخ جرجان ٢٧٧ وطبقات المفسرين ١: ٤١٠ ومرآة الجنان ٢: ٣٨٦ وطبقات العبادي ١١١ والبداية والنهاية ١١: ٣٣١ وشذرات الذهب ٣: ٥٦ والنجوم الزاهرة ٤: ٢٠٥ والأعلام ٥: ١١٤ وقيل في وفاته سنة ٣٦٦، وقيل سنة ٣٩٦، وقد أخذت برواية السبكي.','1','366',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (656,'وأحد الحفاظ المفسرين المناظرين. ولد في "سبك العبيد" من أعمال المنوفية، بمصر، وانتقل إلى القاهرة فأخذ عن علمائها، ثم إلى دمشق فولي قضاء الشام سنة ٧٣٩ هـ، فباشره بعفة ونزاهة ست عشرة سنة وشهرا، وولي مشيخة الحديث الأشرفية، والشامية البرانية، والغزالية، والعادلية الكبرى وغيرها، ودرس بكل منها. واعتل، فاستعفى من القضاء، وعاد إلى القاهرة، فأقام بها دون العشرين يوما وتوفي. من تصانيفه الكثيرة "الدر النظيم في تفسير القرآن العظيم" في ثلاث مجلدات، لم يكمله. و "الحكم والأناة في إعراب قوله سبحانه وتعالى: {غير ناظرين إناة ..} (١).

ابن المديني [١٦١ - ٢٣٤ هـ / ٧٧٧ - ٨٤٩ م]

<span data-type="title" id=toc-932>علي بن عبد الله بن جعفر بن نجيح السعدي بالولاء، المديني، البصري، (أبو الحسن)</span>
_________
(١) السبكي ٦: ١٤١ والدرر الكامنة ٣: ١٣٤ وذيل تذكرة الحفاظ ٣٩ وطبقات المفسرين للداودي ١: ٤١٢ وغاية النهاية ١: ٥٥١ وقضاة دمشق ١٠١ وبغية الوعاة ٢: ١٧٦ وحسن المحاضرة ١: ٣٢١ والنجوم الزاهرة ١٠: ٣١٨ وهدية العارفين ١: ٧٢٠ وكشف الظنون ٤٤٨ و ٦٧٥ و ٧٣٦ والبداية والنهاية ١٤: ٢٥٢ وروضات الجنات ٤٩٥ والقلائد الجوهرية ١: ١٠٦ والشذرات ٦: ١٨٠ ومفتاح السعادة ٢: ٣٦٣ والدارس ١: ١٣٤.','1','366',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (657,'أبو الحسن: محدث، مؤرخ، أصولي، نسابة، لغوي، كان حافظ عصره. أصله من المدينة، ولد بالبصرة، وسمع أباه وهشيما وابن عيينة وطبقتهم، وعنه البخاري وأبو داود وجماعة، وتوفي بسر من رأى (سامراء). قال النووي: "صنف في الحديث مائتي مصنف لم يسبق إلى معظمها ولم يلحق في كثير منه". وقال أبود حاتم: "كان علما في الناس في معرفة الحديث والعلل، وما سمعت أحمد بن حنبل سماه قط إنما كان يكنيه تبجيلا له". من تصانيفه الكثيرة "أسباب النزول" قيل هو أول من صنف فيه (١).

ابن أبي الطيب [... - ٤٥٨ هـ / ... - ١٠٦٦ م]

<span data-type="title" id=toc-933>علي بن عبد الله أبي الطيب ابن أحمد النيسابوري، أبو الحسن:</span>
مفسر، شاعر، ولد بنيسابور، وسكن قصبة
_________
(١) تهذيب التهذيب ٧: ٣٤٩ وتذكرة الحفاظ ٤٢٨ وتهذيب الأسماء واللغات ١: ٣٥٠ وكشف الظنون ٧٦ والنجوم الزاهرة ٢: ٢٧٧ وميزان الاعتدال ٣: ١٣٨ وتاريخ بغداد ١١: ٤٥٨ وطبقات الحنابلة ١٦٨ وشذرات الذهب ٢: ٨١ وطبقات الشافعية ١: ٢٦٦ وفهرس المخطوطات المصورة ج ٢ ق ٢. ١٠٩ والأعلام ٤: ٣٠٣ ومعجم المؤلفين ٧: ١٣٢.','1','367',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (658,'"سابزوار". بنيت له مدرسة باسمه في محلة اسفريس سنة ٤١٠ هـ. وحمل إلى السلطان محمود الغزنوي (٣٦١ - ٤٢١ هـ) فلما دخل عليه جلس بغير إذن وشرع في رواية خبر عن النبي (صلى الله عليه وسلم) بغير أمر من السلطان، فأمر السلطان خادمه بلكمه على رأسه، فلكمه لكمة كانت سببا إلى قلة سمعه وطرشه، ثم عرف السلطان منزلته من العلم والدين فاعتذر إليه وأمر له بمال، فلم يقبله وقال: لا حاجة لي في المال، فإن استطعت أن ترد علي ما أخذته مني قبلته وهو سمعي، ثم تناقشا، فخجل السلطان ومال برأسه إليه وعانقه. له عدة تصانيف، في تفسير القرآن، منها "التفسير الكبير" في ثلاثين مجلدا، و "التفسير الأوسط" أحد عشر مجلدا، و "كتاب التفسير الصغير" ثلاثة مجلدات، قال ياقوت: "وكان يملي ذلك من حفظه، ولما مات - رحمه الله - لم يوجد في خزانة كتبه إلا أربع مجلدات، أحدهما فقهي، وآخر أدبي، ومجلدان في التاريخ" (١).
_________
(١) معجم الأدباء١٣: ٢٧٣ وطبقات المفسرين للسيوطي ٢٣ وطبقات المفسرين للداودي ١: ٤٠٥ والأعلام ٥: ١١٩ ومعجم المؤلفين ٧: ١٣٠.','1','367',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (659,'ابن موهب [٤٤١ - ٥٣٢ هـ / ١٠٥٠ - ١١٣٨ م]

<span data-type="title" id=toc-934>علي بن عبد الله بن محمد بن سعيد بن موهب الجذامي، أبو الحسن:</span>
مفسر، من أهل المرية بالأندلس، روى عن أبي العباس العذري، واختص به، وأجاز له أبو الوليد الباجي وابن عبد البر، وحج، وأخذ الناس عنه. من كتبه "تفسير القرآن" (١).

ابن النعمة [.. - ٥٦٧ هـ / .. - ١١٧٢ م]

<span data-type="title" id=toc-935>علي بن عبد الله بن خلف بن محمد بن عبد الرحمن بن عبد الملك الأنصاري، أبو الحسن المعروف بابن النعمة:</span>
عالم بالعربية، حافظ، مفسر، ولد بالمرية بالأندلس، وسكن بلنسية فكان خطيبها وانتهت إليه رياسة الإقراء والفتوى فيها. قال ابن الأبار: "كان عالما متقنا، حافظا للفقه، والتفاسير، ومعاني الآثار، والسنن، متقدما في علم اللسان، فصيحا مفوها، ورعا، معظما عند
_________
(١) طبقات المفسرين للسيوطي ٢٤ وطبقات المفسرين للداودي ١: ٤٠٩ وطبقات المفسرين للأدنه وي ٣٩ ب. والعبر ٤: ٨٨ ومعجم الأدباء ١٤: ٥ والصلة ٢: ٤٠٥ والشذرات ٤: ٩٩ ومرآة الجنان ٣: ٢٦٠.','1','368',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (660,'الخاصة والعامة، وهو خاتمة العلماء بشرق الأندلس". من كتبه "ري الظمآن في تفسير القرآن" في عدة مجلدات (١).

الوهراني [... - ٦١٥ هـ / ... - ١٢١٩ م]

<span data-type="title" id=toc-936>علي بن عبد الله بن ناشر بن المبارك الوهراني، أبو بكر (ويقال: أبو الحسن):</span>
مفسر، نحوي، له شعر، أصله من مدينة وهران بالجزائر، سكن دمشق، وولي الخطابة بجامع داريا (من قرى دمشق). سمع منه أبو الحجاج يوسف بن خليل الدمشقي، وخرج عنه في معجمه قطعة من شعره. له تصانيف منها "تفسير القرآن" (٢).
_________
(١) طبقات المفسرين للسيوطي ٢٣ وطبقات المفسرين للداودي ١: ٤٠٧ وبغية الملتمس ٤١١ ونيل الابتهاج ٢٠٠ وغاية النهاية ١: ٥٥٣ وبغية الوعاة ٢: ١٧١ والنجوم الزاهرة ٦: ٦٦ والشذرات ٤: ٢٢٣ والعبر ٤: ١٩٨ وهدية العارفين ١: ٧٠٠ ومرآة الجنان ٣: ٣٨٢ وفهرس الفهارس ٢: ٢٨ والتكملة ٦٦٩ والمعجم لابن الأبار ٢٨٦ وكشف الظنون ٤٤٠.
(٢) معجم أعلام الجزائر ٣٤٩ وطبقات المفسرين ٢٤ وطبقات المفسرين للداودي ٢: ٤٠٨ وبغية الوعاة ٢: ١٧٢ والعبر وفيات سنة ٦١٥ هـ وتكملة إكمال الإكمال ٣٦٣ وهدية العارفين ١: ٧٠٥ وكشف الظنون ٤٦١ والأعلام ٥: ١٢٠ ومعجم المؤلفين ٧: ١٤١.','1','368',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (661,'التبريزي [٦٧٧ - ٧٤٦ هـ / ١٢٧٨ - ١٣٤٥ م]

<span data-type="title" id=toc-937>علي بن عبد الله بن أبي الحسن بن أبي بكر الأردبيلي التبريزي، أبو الحسن، تاج الدين:</span>
فقيه شافعي، من علمائهم، مشارك في التفسير والحديث والنحو والحساب، ولد في أردبيل بأذربيجان، وسكن تبريز، ودخل بغداد سنة ٧١٠ هـ، ثم حج، ودخل مصر سنة ٧٢٢ هـ. وأفتى وهو ابن ثلاثين سنة، وأصم في آخر عمره، ومات بالقاهرة، قال الذهبي: "هو عالم كبير شهير، كثير التلامذة، حسن الصيانة، من مشايخ الصوفية" وقال السبكي: "كان ماهرا في علوم شتى، وعنى بالحديث بأخرة، وصنف في "التفسير" و "الحديث" و "الأصول" و "الحساب" (١).

الشيرازى [... - ٩٠٧ هـ / ... - ١٥٠١ م]

<span data-type="title" id=toc-938>علي بن عبد الله بن محمود، شرف الدين، الشنفتكي، أو الشيفتكي، (الشيرازي)</span>
_________
(١) السبكي ٦: ١٤٦ وطبقات الأسنوي ٤٦ وطبقات المفسرين للداودي ١: ٤٠٦ وبغية الوعاة ٢: ١٧١ والدرر الكامنة ٣: ١٤٣ وحسن المحاضرة ١: ٥٤٥ والشذرات ٦: ١٤٨ والنجوم الزاهرة ١٠: ١٤٥ وعلماء بغداد ١٤٦ ومعجم الأطباء ٣٥٧ وهدية العارفين ١: =','1','369',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (662,'الشيرازي: نحوي، مفسر، من علماء الشافعية. له تصانيف في الفقه والنحو والتفسير، منها "أحكام الكتاب المبين" في تفسير آيات الأحكام، ألفه بأمر السلطان يعقوب بهادر خان ملك شيراز. منه نسخة بخطه سنة ٨٩٠ هـ في الأزهرية في ١٧٩ ورقة (١).

البدليسي [.. - ٩٠٠ هـ / .. - ١٤٩٥ م]

<span data-type="title" id=toc-939>علي بن عبد الله البدليسي، حسام الدين:</span>
مفسر، صوفي، من فقهاء الحنفية. نسبته إلى بدليس (بلدة من نواحي أرمينية قرب خلاط) له "جامع التنزيل والتأويل" في التفسير، قال البغدادي. ورأيته عند الوزير عبد الرؤوف باشا الرومي، أوله: الرحمن علم القرآن، خلق الإنسان، علمه البيان، إجمالا وتفصيلا تشريفا وتفضيلا .. ، في خمس مجلدات (٢).
_________
= ٧١٩ والأعلام ٥: ١٢١ وهو فيه: علي بن عبد الله الحسين بن أبي بكر.
(١) الأزهرية ١: ٢٠٢ وهو فيه الشنفكي، وعنه الأعلام ٤: ٣٠٧، وهدية العارفين ١: ٧٤٠ وهو فيه الشنفتكي، وعنه معجم المؤلفين ٧: ١٣٦، وكشف الظنون ٦٨ و ١٦١٣ وهو فيه شرف الدين علي الشيرازي.
(٢) هدية العارفين ١: ٧٣٨ وإيضاح المكنون ١: ٣٥٢ وكشف الظنون ١٥١٤ وهو فيه: علي بن الحسين.','1','369',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (663,'السجلماسي [.. - ١٠٥٧ هـ .. - ١٦٤٧ م]

<span data-type="title" id=toc-940>علي بن عبد الواحد بن محمد بن عبد الله، أبو الحسن، الأنصاري السجلماسي الجزائري، من سلالة سعد ابن عبادة الخزرجي:</span>
فقيه، من العلماء، مشارك في التفسير والحديث والأصول والفرائض وغيرها. ولد بتافلات، ونشأ بسجلماسة، وأقام بمصر مدة، واستقر بفاس، فنصب مفتيا في الجبل الأخضر، وتوفي بالجزائر. من تصانيفه الكثيرة "تفسير القرآن" قال البغدادي: "وصل فيه إلى قوله تعالى: {ولكن البر من اتقى} (١).

التركماني [٦٨٣ - ٧٥٠ هـ / ١٢٨٤ - ١٣٤٩ م]

<span data-type="title" id=toc-941>علي بن عثمان بن إبراهيم بن مصطفى المارديني، المعروف بالتركماني، أبو الحسن، علاء الدين:</span>
قاض، مفسر، محدث، فقيه حنفي، من علماء اللغة .. من أهل مصر، درس وأفتى، وولي قضاء الديار المصرية. أخذ عنه عبد القادر
_________
(١) تعريف الخلف ١: ٦٩ وخلاصة الأثر ٣: ١٧٣ وصفوة من انتشر ١٣٥ ووفاته فيه سنة ١٠٥٤ وهدية العارفين ١: ٧٥٦ وإيضاح المكنون ١: ٣٠٥. والأعلام ٤: ٣١٠ وشجرة النور ٣٠٨.','1','370',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (664,'القرشي صاحب الطبقات. من كتبه "بهجة الأديب في بيان ما في كتاب الله العزيز من الغريب" (١).

الصناري [... - ٥٣٩ هـ / ... - ١١٤٤ م]

<span data-type="title" id=toc-942>علي بن عراق الصناري الخوارزمي، أبو الحسن:</span>
نحوي، لغوي، عروضي، مفسر، فقيه. تعلم بخوارزم وبخارى. وعاد إلى جرجانية خوارزم فتكلم في مسائل مع أئمتها، ثم تحول إلى قرية مذانة ووعظ في مسجدها الجامع، واستقر بها إلى أن مات. قال ياقوت: وكان يحفظ اللغات الغريبة والأشعار العويصة، وصنف كتاب "شماريخ الدرر في تفسير القرآن، ولما فرغ منه كتب في آخره:
فرغنا من كتابته عشيا ... وكان الله في عوني وليا
وقد أدرجته نكتا حسانا ... ومعنى يشبة الرطب الجنيا (٢).
_________
(١) طبقات المفسرين للداودي ١: ٤١٦ والفوائد البهية ١٢٣ والجواهر المضية ١: ٣٦٦ وكشف الظنون ٢٥٦ وتاج التراجم ٤٤ والدرر الكامنة ٣: ١٥٦ وهدية العارفين ١: ٧٢٥ وحسن المحاضرة ١: ٤٦٩ والنجوم الزاهرة ١٠: ٢٤٦ والأعلام ٥: ١٢٥.
(٢) معجم الأدباء ١٤: ٦٣ وكشف الظنون ٤٤٧ و ١٠١٩ وبغية الوعاة ٢: ١٧٩ والأعلام ٤: ٢١٢.','1','370',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (665,'العربي [... - ٩٠١ هـ / ... - ١٤٦٠ م]

<span data-type="title" id=toc-943>علي العربي، علاء الدين:</span>
فقيه حنفي، عالم بالتفسير والحديث والأصول، من أهل حلب، وبها نشأ وأخذ عن علمائها، وانتقل إلى بلاد الروم فسكن بروسة وأخذ عن إسماعيل الكوراني وخضر بن جلال الدين الرومي. ثم صار معيدا بمدرسة دار الحديث بأدرنة، فمدرسا بمدارس مغنيسا، فمفتيا بالقسطنطينية، ومات وهو مفت بها. قال اللكنوي: كان جامعا للعلوم الشرعية والعقلية متبحرا ماهرا في التفسير والأصول والحديث" (١).

الباجسرائي [... - ٥٨٨ هـ / ... - ١١٩٢ م]

<span data-type="title" id=toc-944>علي بن أبي العز بن عبد الله الباجسرائي، أبو الحسن:</span>
مفسر، من فقهاء الحنابلة. نسبته إلى باجسرا (من قرى الجزيرة)، سكن بغداد وأخذ عن أبي
_________
(١) الشقائق النعمانية ١: ٢٣٦ والشذرات ٨: هـ والفوائد البهية ١٤٦ وهدية العارفين ١: ٧٣٩ وهو فيه: علي بن عبد الله العربي، علاء الدين الحلبي، المعروف بابن اللجام.','1','371',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (666,'الوقت وابن البطي وغيرهما، مات ببغداد، له "تفسير القرآن" في أربع مجلدات (١).

ابن عقيل [٤٣١ - ٥١٣ هـ / ١٠٤٠ - ١١١٩ م]

<span data-type="title" id=toc-945>علي بن عقيل بن محمد بن عقيل البغدادي الظفري، أبو الوفاء، ويعرف بابن عقيل:</span>
عالم العراق وشيخ الحنابلة ببغداد في وقته. كان قوي الحجة، اشتغل بمذهب المعتزلة في حداثته، وكان يعظم الحلاج، فأراد الحنابلة قتله (سنة ٤٦١ هـ) فالتجأ إلى دار السلطان، ولم يزل أمره في تخبيط إلى سنة ٤٦٥ هـ، فحضر إلى الديوان، وأظهر التوبة حتى تمكن من الظهور. قال ابن الجوزي: "وأفتى ابن عقيل، ودرس وناظر الفحول، واستفتي في الديوان في زمن القائم، في زمرة الكبار، وجمع علم الفروع والأصول وصنف فيها الكتب الكبار". أعظم تصانيفه "كتاب الفنون" بقيت منه أجزاء، وهو في أربعمئة جزء، فيه فوائد كثيرة، في الوعظ، والتفسير، والفقه،
_________
(١) الذيل على طبقات الحنابلة ١: ٣٧٨ وشذرات الذهب ٤: ٢٩٣ ومعجم المؤلفين ٧: ١٥٠.','1','371',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (667,'والأصلين، والنحو، واللغة، والشعر، والتاريخ، والحكايات. قال الذهبي: "كتاب الفنون لم يصنف في الدنيا أكبر منه، حدثني من رأى المجلد الفلاني بعد الأربعمائة .. " (١).

ابن عبدوس [٥١٠ - ٥٥٩ هـ / ١١١٦ - ١١٦٤ م]

<span data-type="title" id=toc-946>علي بن عمر بن أحمد بن عمار بن أحمد ابن علي، أبو الحسن، ابن عبدوس:</span>
مفسر، واعظ، من فقهاء الحنابلة. من أهل حران (بالجزيرة الفراتية). سمع ببغداد من الحافظ أبي الفضل بن ناصر وغيره. وجالسه فخر الدين ابن تيمية في أول اشتغاله وقال عنه: "كان نسيج وحده في علم التذكير، والاطلاع على علوم التفسير، وله فيه التصانيف البديعة، والمبسوطات الوسيعة" مات بحران. له "تفسير القرآن" كبيرا (٢).
_________
(١) طبقات المفسرين للداودي ١: ٤١٧ والذيل على طبقات الحنابلة ١: ١٤٢ وغاية النهاية ١: ٥٥٦ والمنتظم ٩: ٢١٢ ولسان الميزان ٤: ٢٤٣ والشذرات ٤: ٣٥ والعبر ٤: ٢٩ وابن كثير ١٢: ١٨٤ وطبقات القراء للذهبي ١: ٣٨٠ ومرآة الجنان ٣: ٢٥٤ ومناقب الإمام أحمد ٥٢٦ ومرآة الزمان ٨: ٨٣ وهدية العارفين ١: ٦٩٥ والأعلام ٥: ١٢٩ ومعجم المؤلفين ٧: ١٥١ وجلاء العينين ٩٩ والمنهج الأحمد ٢: ٢٥٢.
(٢) هدية العارفين ١: ٦٩٨ والذيل على طبقات الحنابلة ١: ٢٤١ والشذرات ٤: ١٨٣ وطبقات المفسرين للداودي ١: ٤١٨.','1','372',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (668,'ابن الجراح [٢٤٤ - ٣٣٤ هـ / ٨٥٩ - ٩٤٦ م]

<span data-type="title" id=toc-947>علي بن عيسى بن داود ابن الجراح، أبو الحسن البغدادي الحسني:</span>
وزير، من العلماء الرؤساء، من أهل بغداد. فارسي الأصل، وزر للمقتدر بالله دفعتين، الأولى سنة ٣٠٠ هـ وعزل سنة ٣٠٤ هـ، والثانية سنة ٣١٤ هـ وعزل سنة ٣١٦ هـ. قال الصولي: "لا أعلم أنه وزر لبني العباس وزير يشبهه في زهده وعفته وحفظه للقرآن وعلمه بمعانيه، ولا أعلم أنني خاطبت أحدا أعرف منه بالشعر" أخباره كثيرة. له "معاني القرآن وتفسيره" أعانه عليه أبو الحسن الواسطي وأبو بكر بن مجاهد (١).

الرماني [٢٩٦ - ٣٨٤ هـ / ٩٠٩ - ٩٩٤ م]

<span data-type="title" id=toc-948>علي بن عيسى بن علي بن عبد الله، أبو الحسن الرماني، ويعرف بالأخشيدي وبالوراق، وهو بالرماني أشهر:</span>
باحث معتزلي، مفسر، فقيه، أصولي، من
_________
(١) هدية العارفين ١: ٦٧٨ وطبقات المفسرين للداودي ١: ٤١٩ ومعجم الأدباء ١٤: ٦٨ والنجوم الزاهرة ٣: ٢٨٨ والعبر ٢: ٢٣٨ وابن النديم ١٢٩ وروضات الجنات ٤٧٦ وتاريخ بغداد ١٢: ١٤ والمنتظم ٦: ٣٥١ والأعلام ٥: ١٣٣.','1','372',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (669,'كبار النحاة .. أصله من سامراء. ولد ببغداد، وأخذ عن ابن السراج وابن دريد والزجاج. روى عنه هلال بن المحسن، وأبو القاسم التنوخي، والحسن بن علي الجوهري، قال أبو حيان التوحيدي: "لم ير مثله قط علما بالنحو وغزارة بالكلام، وبصرا بالمقالات، واستخراجا للعويص، وإيضاحا للمشكل مع تأله وتنزه ودين وفصاحة، وعفاف ونظافة" مات ببغداد. له كتب، منها "الجامع الكبير في التفسير" وقد اختصره عبد الملك بن علي المؤذن الهروي (انظر ترجمته) وكتاب "النكت في إعجاز القرآن" (١).

الفرزدقي [... - ٤٧٩ هـ / ... - ١٠٨٦ م]

<span data-type="title" id=toc-949>علي بن فضال بن علي بن غالب بن جابر المجاشعي القيرواني، أبو الحسن:</span>
_________
(١) طبقات المفسرين للسيوطي ٢٤ وطبقات المفسرين ١: ٤١٩ وطبقات النحاة لابن قاضي شهبة ٢: ١٧٤ وإنباه الرواة ٢: ٢٩٤ وميزان الاعتدال ٣: ١٤٩ ومفتاح السعادة ١: ١٧٥ ولسان الميزان ٤: ٢٤٨ وبغية الوعاة ٢: ١٨٠ وابن كثير ١١: ٣١٤ والعبر ٣: ٢٥ وابن خلكان ٣: ٢٩٩ ومعجم الأدباء ١٤: ٧٣ وتاريخ بغداد ١٢: ١٦٠ وتذكرة الحفاظ ٣: ٩٨٦ والإمتاع والمؤانسة ١: ١٣٣ ونزهة الألباء ٣١٨ والمنتظم ٧: ١٧٦ والشذرات: ١٠٩ وابن النديم ٦٣ ومرآة الجنان ٢: ٤٢٥ وهدية العارفين ١: ٦٨٣ والكشف ٤٤٧ و ٥٧١.','1','373',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (670,'مؤرخ، عالم بالنحو واللغة والتفسير والأدب، من ذرية الفرزدق الشاعر. من أهل القيروان. وتنقل بين بلدان مصر والشام والعراق والعجم حتى وصل إلى مدينة غزنة فأقام بها مدة، ثم عاد إلى العراق فسكن بغداد وحدث بها عن جماعة من شيوع المغرب، واتصل بنظام الملك، ولم تطل أيامه بعد ذلك. قال عبد الغافر الفارسي: "ورد ابن فضال نيسابور، فاجتمعت به، فوجدته بحرا ما عهدته في البلديين ولا في الغرباء مثله في حفظه ومعرفته وتحقيقه، فأعرضت عن كل شيء وفارقت المكتب ولزمت بابه بكرة وعشية". من كتبه "البرهان العميدي" في تفسير القرآن، في عشرين مجلدا، و "النكت في القرآن" وكتاب في "شرح بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ". وقيل أنه صنف في التفسير كتابا آخر غير الأول سماه "الإكسير في علم التفسير" خمسة وثلاثون مجلدا (١).
_________
(١) طبقات المفسرين للسيوطي ٢٤ وطبقات المفسرين للداودي ١: ٤٢١ وطبقات النحاة لابن قاضي شهبة ٢: ١٧٧ ومعجم الأدباء ١٤: ٩٠ والمنتظم ٩: ٣٣ والنجوم الزاهرة ٥: ١٢٤ وعبر الذهبي ٣: ٢٩٥ وشذرات الذهب ٣: ٣٦٣ وبغية الوعاة ٢: ٨٣ وإنباه الرواة ٢: ٢٩٩ ومرآة الجنان ٣: ١٣٢ وهدية العارفين ١: ٦٩٣ وإيضاح المكنون ١: ١١٦ و ١٧٨ وروضات الجنات ٤٨٥ والبداية والنهاية ١٢: ١٣٢ ولسان الميزان ٤: ٢٤٩.','1','373',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (671,'الراوندي [.. - بعد ٥٧٠ هـ / .. - بعد ١١٧٤ م]

<span data-type="title" id=toc-950>علي بن فضل الله بن علي بن عبيد الله الحسني الراوندي، عز الدين:</span>
فقيه إمامي، مفسر، نسبته إلى راوند - قرية بقاشان بنواحي أصبهان -. له تصانيف منها "تفسير القرآن". ذكره كحاله نقلا عن الأستاذ حسين علي محفوظ، وجعل ولادته سنة ٥٩٩ هـ وقال: كان حيا سنة ٦٥٢ هـ. بينما ترجم لأبيه فضل الله (ج ٨ ص ٧٥) وقال: توفي بكاشان في حدود سنة ٥٧٠ هـ (١).

الخلخالي [القرن ١٢ هـ / القرن ١٨ م]

<span data-type="title" id=toc-951>علي قلي بن محمد الخلخالي:</span>
نحوي، مفسر، من علماء الشيعة الإمامية. توفي بأصفهان في أوائل القرن الثاني عشر الهجري. من تصانيفه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (٢).
_________
(١) تاج العروس ٢: ٣٦٠ مادة روند، وتاريخ ابن الريوندي ٢٥٢ عن تاج العروس، ومعجم المؤلفين ٧:
(٢) أعيان الشيعة ٤٢: ١٣ ومعجم المؤلفين ٧: ١٧٠.','1','374',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (672,'القزويني [... - ١٢٩٠ هـ / ... - ١٨٧٣ م]

<span data-type="title" id=toc-952>علي بن كل محمد بن علي محمد القاربوز آبادي القزويني:</span>
فقيه إمامي، مفسر، واعظ، أصولي، ولد بقاربوز آباد (من قرى قزوين) وأخذ عن علماء قزوين، ورحل إلى أصبهان، ثم إلى زنجان وتوفي بها. من تصانيفه "تفسير القرآن" (١).

الطبري [... - حيا ٣٢٤ هـ / ... - حيا ٩٣٦ م]

<span data-type="title" id=toc-953>علي بن محمد بن مهدي الطبري، الأشعري، أبو الحسن:</span>
فقيه شافعي، محدث، مفسر، صحب أبا الحسن الأشعري بالبصرة وأخذ عنه، قال الحسين بن الحسن الأسدي: "كان مصنفا للكتب في أنواع العلم، حافظا للفقه، والكلام، والتفاسير، والمعاني، وأيام العرب، فصيحا، مبارزا في النظر، ما شوهد في أيامه مثله" (٢).
_________
(١) أعيان الشيعة ٤٢: ١٠ ومعجم المؤلفين ٧:
(٢) طبقات المفسرين للداودي ١: ٤٣٣ والسبكي ٣: ٤٦٦ وتبيين كذب المفتري ١٩٥ وطبقات العبادي ٨٥.','1','374',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (673,'ابن حمشاد [... - ٣٣٨ / ... - ٩٥٠]

<span data-type="title" id=toc-954>علي بن محمد بن سحنون (وقيل: سحنويه، وقيل: سخنويه) بن حمشاد النيسابوري، أبو الحسن:</span>
مفسر، حافظ للحديث، من كبارهم. سمع الحسين بن الفضل، والفضل الشعراني وإبراهيم بن ديزيل وغيرهم. وروى عنه الحاكم النيسابوري وقرظة وبالغ في تعظيمه وقال: ما رأيت في مشايخنا أثبت في الرواية والتصنيف منه" له تصانيف منها "تفسير القرآن" في عشر مجلدات (١).

الأزجي [... - ٤٤٥ هـ / ... - ١٠٥٣ م]

<span data-type="title" id=toc-955>علي بن محمد بن علي الأزجي، أبو الحسن:</span>
مفسر، من أهل بغداد، ضرير، قال الصفدي: "كان عالما بتفسير القرآن، وقد صنف فيه كتابا" والأزجي نسبة إلى باب الأزج محلة كبيرة في شرقي بغداد، ينسب إليها عدد كبير جدا من
_________
(١) تذكرة الحفاظ ٣: ٨٥٥ وهدية العارفين ١: ٦٧٩ والأعلام ٥: ١٤٢ والشذرات ٢: ٣٤٨ والمنتظم ٦: ٣٦٤ وابن كثير ١١: ٢٢٢ والنجوم الزاهرة ٣: ٣٠١ ومعجم المؤلفين ٧: ٨٥ وهو في بعض المصادر: علي بن حمشاد بن سختويه.','1','375',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (674,'أهل العلم. له "مجمع البحرين" في تفسير القرآن (١).

الماوردي [٣٦٤ - ٤٥٠ هـ / ٩٧٤ - ١٠٥٨ م]

<span data-type="title" id=toc-956>علي بن محمد بن حبيب، أبو الحسن الماوردي:</span>
أقضى قضاة عصره، فقيه شافعي، أصولي، مفسر، أديب. ولد في البصرة، وسكن بغداد، وولي القضاء في بلدان كثيرة، ثم جعل "أقضى القضاة" سنة ٤٢٩ هـ في أيام القائم بأمر الله العباسي. وكان يميل إلى مذهب الإعتزال، وبلغ منزلة عند ملوك بني بويه، قال ياقوت: "فكانوا يرسلونه في التوسطات بينهم وبين من يناوئهم، ويرتضون بوساطته ويقفون بتقريراته". نسبته إلى بيع ماء الورد. ووفاته ببغداد. من كتبه "النكت والعيون" في تفسير القرآن، توجد منه مخطوطة المجلد الخامس. وقد اختصره محمد بن علي بن عبد الله الحلي (انظر ترجمته) (٢).
_________
(١) نكث الهميان ٢١٨ وهدية العارفين ١: ٦٨٨ وكشف الظنون ١٥٩٩ ومعجم المؤلفين ٧: ٢١٣.
(٢) طبقات المفسرين ٢٥ وطبقات المفسرين للداودي ١: ٤٢٣ وطبقات ابن هداية (بتحقيقنا) ١٥١ وطبقات السبكي ٥: ٢٦٧ وطبقات الفقهاء ١١٠ وتاريخ بغداد ١٢: ١٠٢ والمنتظم ٨: ١٩٩ ووفيات الأعيان ٣: ٢٨٢ =','1','375',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (675,'البزدوي [٤٠٠ - ٤٨٢ هـ / ١٠١٠ - ١٠٨٩ م]

<span data-type="title" id=toc-957>علي بن محمد بن الحسن بن عبد الكريم بن موسى، أبو الحسن، فخر الإسلام البزدوي:</span>
فقيه أصولي، محدث، مفسر، من أكابر الحنفية. من سكان سمرقند، ونسبته إلى (بزدة) قلعة بقرب نسف. مات بكش، ونقل جثمانه إلى سمرقند ودفن بها. قال السمعاني: "البزدوي فقيه ما وراء النهر، وأستاذ الأئمة، وصاحب الطريقة على مذهب أبي حنيفة" وقال الذهبي: "هو شيخ الحنفية وعالم ما وراء النهر". من كتبه "كشف الأستار" في التفسير، كبير جدا، "يقال أنه مائة وعشرون جزءا، كل جزء في ضخم مصحف" (١).
_________
= ومفتاح السعادة ١: ٣٢٢ وميزان الاعتدال ٣: ١٥٥ ولسان الميزان ٤: ٢٦٠ والعبر ٣: ٢٢٣ والشذرات ٣: ٢٨٥ ومرآة الجنان ٣: ٧٢ ومعجم الأدباء ١٥: ٥٢ والنجوم الزاهرة ٥: ٦٤ وابن كثير ١٢: ٨٠ وهدية العارفين ١: ٦٨٩ وكشف الظنون ٤٥٨ و ١١٨٨ و ١٩٧٨ والأعلام ٥: ١٤٦ ومعجم المؤلفين ٧: ١٨٩.
(١) الفوائد البهية ١٢٤ وهدية العارفين ١: ٦٩٣ والجواهر المضية ١: ٣٧٢ وكشف الظنون ١٤٨٥ وتاج التراجم ٣٠ ومعجم المؤلفين ٧: ١٩٢ والأعلام ٥: ١٤٨.','1','376',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (676,'الكيا الهراسي [٤٥٠ - ٥٠٤ هـ / ١٠٥٨ - ١١١٠ م]

<span data-type="title" id=toc-958>علي بن محمد بن علي، أبو الحسن الطبري، الملقب بعماد الدين، المعروف بالكيا الهراسي:</span>
فقيه شافعي، أصولي، متكلم، مفسر، ولد في طبرستان، وتفقه على إمام الحرمين، وقدم بغداد فسكنها، ودرس بالنظامية إلى أن مات. قال عبد الغافر الفارسي: "كان من رؤوس معيدي إمام الحرمين في الدرس، وكان ثاني أبي حامد الغزالي، بل آصل وأصلح وأطيب في الصوت والنظر، ثم اتصل بخدمة الملك بركيارق، وحظي عنده بالمال والجاه وارتفع شأنه، وتولى القضاء بتلك الدولة". واتهم بمذهب الباطنية فرجم، وأراد السلطان قتله فحماه المستظهر وشهد له. من كتبه "أحكام القرآن" مخطوط (١).

العمراني [... - ٥٦٠ هـ / ... - ١١٦٥ م]

<span data-type="title" id=toc-959>علي بن محمد بن علي بن أحمد، أبو الحسن العمراني الخوارزمي:</span>
مفسر،
_________
(١) تبيين كذب المفتري ٢٨٨ والعبر ٤: ٨ ومرآة الزمان ٨: ٣٧ والمنتظم ٩: ١٦٧ وطبقات السبكي، والشذرات ٤: ٨ وكشف الظنون ٢٠ وهدية العارفين ١: ٦٩٤ ومعجم المؤلفين ٧: ٢٢٠ وابن خلكان ٣: ٢٨٦ والأعلام ٥: ٩.','1','376',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (677,'أديب، من علماء المعتزلة، من بيت كبير في سرخس. قرأ الأدب على الزمخشري، وسمع الحديث من الترجماني وغيره. وكانت له منزلة رفيعة عند السلطان سنجر ابن ملكشاه، ثم حبسه سنة ٥٤٥ هـ. ترجم له ياقوت وأثنى عليه وقال "مات فيما يقارب سنة ٥٦٠ هـ ". من كتبه "شماريخ الدرر في تفسير الآي والسور" (١).

علي بن محمد [... ٥٦١ هـ / ... - ١١٦١ م]

<span data-type="title" id=toc-960>علي بن محمد بن محمد بن هبة الله بن محمد بن علي بن المطلب، مجد الدين، أبو المكارم، تاج الدين:</span>
نحوي، لغوي، من بلغاء الكتاب. حدث بالقاهرة، وسافر إلى الشام، واتصل بالملوك. من تصانيفه "مختصر الغريبين" غريب القرآن والحديث للهروي (٢).

النيريزي [... - ٦٠٢ هـ / ... - ١٢٠٥ م]

<span data-type="title" id=toc-961>علي بن محمد بن علي النيريزي، أبو الحسن:</span>
محدث، مفسر، نسبته إلى قرية
_________
(١) معجم الأدباء ١٥: ٦١ وبغية الوعاة ٢: ١٩٥ وهدية العارفين ١: ٦٩٨ وكشف الظنون ٤٤٦ وطبقات المفسرين ١: ٤٣٠.
(٢) بغية الوعاة ٢: ٢٠١ وهدية العارفين ١: ٦٩٩ وكشف الظنون ١٢٠٩.','1','377',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (678,'"نيريز" من قرى شيراز. قال الذهبي: له "تفسير"، مات سنة ٦٠٢ وله أربع وثمانون سنة". وفي تبصير المنتبه أنه مات سنة ٦٥٢ هـ. وفي معجم المؤلفين أنه مات بشعبان سنة ٦٠٤ هـ، وسمى تفسيره "مجمع البحرين" ومجمع البحرين هو لعلي بن محمد بن علي الأزجي المتوفى سنة ٤٤٥ هـ، وقد سبقت ترجمته في هذا المعجم (١).

الحصار [... - ٦١١ هـ / ... - ١٢١٤ م]

<span data-type="title" id=toc-962>علي بن محمد بن محمد بن إبراهيم بن موسى الخزرجي، أبو الحسن، الحصار:</span>
فقيه، أصولي، مفسر، محدث، إشبيلي الأصل، ولد بفاس، وسمع بها وبسبتة ومصر وغيرها. وجاور بمكة وتوفي بالمدينة. له تصانيف، منها "الناسخ والمنسوخ" (٢).
_________
(١) المشتبه ١: ١١١ وتبصير المنتبه ١: ٢٠٦ وطبقات المفسرين للداودي ١: ٤٣٢ وهدية العارفين ١: ٦٨٨ ترجمة الأزجي وكشف الظنون ١٥٩٩ ومعجم المؤلفين ٧: ٢١٥ ترجمة الأزجي و ٢٢٤.
(٢) جذوة الاقتباس ٢٩٨ وابن الأبار ٦٨٦ وهدية العارفين ١: ٧٠٥ والأعلام ٥: ١٥١ ونيل الابتهاج ٢٠٠ والنسخ في القرآن الكريم ١: ٣٣٥.','1','377',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (679,'السخاوي [٥٥٨ - ٦٤٣ هـ / ١١٦٣ - ١٢٤٥ م]

<span data-type="title" id=toc-963>علي بن محمد بن عبد الصمد بن عبد الأحد الهمداني المصري السخاوي، أبو الحسن، علم الدين:</span>
شيخ القراء بدمشق في زمانه، عالم بالتفسير واللغة والنحو والأصول، وله نظم. ولد بسخا (بمصر) وسمع بالقاهرة والإسكندرية، ثم سكن دمشق وأقرأ الناس بها في جامع بني أمية نيفا وأربعين سنة، وتوفي بها، ودفن بقاسيون. من تصانيفه "تفسير القرآن الكريم" في أربع مجلدات وصل فيه إلى سورة الكهف ولم يتمه (١).

الجياني [... - ٦٦٣ هـ / ... - ١٢٦٥ م]

<span data-type="title" id=toc-964>علي بن محمد بن حسن الأنصاري الإشبيلي، أبو الحسن الجياني:</span>
كاتب
_________
(١) طبقات المفسرين للسيوطي ٢٥ وطبقات المفسرين للأدنه وي ٥٣ ب، وطبقات المفسرين للداودي ١: ٤٢٥ وطبقات القراء للذهبي ٣: ٥٠٣ وطبقات النحاة لابن قاضي شهبة ٢: ١٨٣ وطبقات الشافعية للسبكي ٨: ٢٩٧ وإنباه الرواة ٢: ٣١١ وحسن المحاضرة ١: ٤١٢ وبغية الوعاة ٢: ١٩٢ والنجوم الزاهرة ٦: ٣٥٤ ومرآة الجنان ٤: ١١٠ والعبر ٥: ١٧٨ وذيل الروضتين ١٧٧ وغاية النهاية ١: ٥٦٨ ومرآة الزمان ٨: ٧٥٨ والبداية والنهاية ١٣: ١٧٠ وروضات الجنات ٤٩٢ وكشف الظنون ٤٤٨ والشذرات ٥: ٢٢٢ وابن خلكان ٣: ٣٤٠ ومعجم الأدباء ١٥: ٦٥.','1','378',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (680,'بليغ، شاعر، نحوي، أصله من جيان. قال ابن عبد الملك: "استقضي بحصن القصر من نظر اشبيلية، واستكتبه الرشيد المؤمني، ثم صار يستعمل في الأعمال السلطانية، فولي خطة الإشراف على بلاد حاحة من نظر مراكش". وشرع في الجمع بين "تفسيري الزمخشري وابن عطية" ومات قبل إتمامه. توفي بتامطريت، في المغرب (١).

الرامشي [... - ٦٦٧ هـ / ... - ١٢٦٨ م]

<span data-type="title" id=toc-965>علي بن محمد بن علي الرامشي، حميد الدين:</span>
محدث، مفسر، أصولي، من فقهاء الحنفية، من أهل بخارى. انتهت إليه رياسة العلم في عصره بما وراء النهر، وكان ضريرا. قال اللكنوي: "كان إماما كبيرا فقيها أصوليا محدثا مفسرا جدليا كلاميا حافظا متقنا، له تصانيف كثيرة" (٢).
_________
(١) النيل والتكملة السفر الخاص ١: ٢٨٧
(٢) الفوائد البهية ١٢٥ وتاج التراجم ٣٤ وهدية العارفين ٧١١ ومعجم المؤلفين ٧: ٢١٧ والأعلام ٥: ١٥٤.','1','378',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (681,'الخازن [٦٧٨ - ٧٤١ هـ / ١٢٨٠ - ١٣٤١ م]

<span data-type="title" id=toc-966>علي بن محمد بن إبراهيم بن عمر ابن خليل الشيحي، علاء الدين المعروف بالخازن:</span>
عالم بالتفسير والحديث، مشارك في بعض العلوم، من فقهاء الشافعية. نسبته إلى "شيحة" من أعمال حلب. ولد ببغداد، وسمع بها من ابن الثعالبي، وانتقل إلى دمشق، فسمع من القاسم بن مظفر وغيره، وكان خازن الكتب بالمدرسة السميساطية فيها، فاشتهر بالخازن بسبب ذلك. وتوفي بحلب. له تصانيف، منها "لباب التأويل في معاني التنزيل" في التفسير، يعرف بتفسير الخازن، طبع مرارا بمصر (١).

ابن الدريهم [٧١٢ - ٧٦٢ هـ / ١٣١٢ - ١٣٦١ م]

<span data-type="title" id=toc-967>علي بن محمد بن عبد العزيز بن فتوح الثعلبي، تاج الدين، المعروف (بابن الدريهم وبابن أبي الخير)</span>
_________
(١) طبقات المفسرين للداودي ١: ٤٢٢ وتاريخ علماء بغداد ١٥١ وهدية العارفين ١: ٧١٨ والشذرات ٦: ١٣١ والدرر الكامنة ٣: ١٧١ ومعجم المطبوعات ٨٠٩ وكشف الظنون ٤٥٣ و ١٥٤٠ ومعجم المؤلفين ٧: ١٧٧ و ١٨٦ والأعلام ٥: ١٥٦.','1','379',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (682,'بابن الدريهم وبابن أبي الخير: فقيه شافعي، مشارك في التفسير والحديث والأصول والقراءات والحساب، من أهل الموصل، سافر إلى دمشق والقاهرة تاجرا، أكثر من مرة. ودرس بالجامع الأموي، ودخل حلب. ثم سافر إلى مصر سنة ٧٦٠ هـ فبعثه الناصر حسن رسولا إلى الحبشة، فوصل إلى قوص فمات بها. له تصانيف كثيرة، منها "النسمات الفائحة في آيات الفاتحة" و "كنز الدرر في حروف أوائل السور" (١).

الحموي [... - ٧٩٣ هـ / ... - ١٣٩١ م]

<span data-type="title" id=toc-968>علي بن محمد بن يحيى بن أحمد بن عماد الدين القادري الحموي، علاء الدين:</span>
مفسر، من أحفاد محيي الدين الكيلاني، توفي بحماة. من آثاره "تفسير القرآن" (٢).

ابن اللحام [بعد ٧٥٠ - ٨٠٣ هـ /بعد ١٣٤٩ - ١٤٠١ م]

<span data-type="title" id=toc-969>علي بن محمد بن علي بن عباس بن فتيان البعلي ثم الدمشقي، علاء (الدين، المعروف بابن اللحام)</span>
_________
(١) الدرر الكامنة ٣: ١٨١ والبدر الطالع ١: ٤٧٧ وفيه وفاته سنة ٧٦٦ والأعلام ٥: ١٥٨ وهدية العارفين: ١: ٧٢٣ ومعجم المؤلفين ٧: ٢١٠.
(٢) هدية العارفين ١: ٧٢٦.','1','379',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (683,'الدين، المعروف بابن اللحام: شيخ الحنابلة بالشام في وقته، عالم بالتفسير والفقه والعربية، من أهل بعلبك وبها نشأ وتعلم، ثم انتقل إلى دمشق فأخذ عن علمائها، ودرس وأفتى، وناب في الحكم، ووعظ بالجامع الأموي. وعرض عليه قضاء الشام استقلالا فامتنع. ولما احتل تيمورلنك حلب، خرج ابن اللحام من دمشق إلى القاهرة، فعرض عليه القضاء فامتنع أيضا، وتولى التدريس بالمدرسة المنصورية، ومات بعد ذلك بيسير. أثنى عليه ابن حجر وابن قاضي شهبة وغيرهما (١).

ابن وفا [٧٥٩ - ٨٠٧ هـ / ١٣٥٧ - ١٤٠٥ م]

<span data-type="title" id=toc-970>على بن محمد بن محمد بن وفا، أبو الحسن القرشي الأنصاري الشاذلي:</span>
متصوف، فقيه مالكي، مفسر، إسكندري الأصل، ولد بالقاهرة. قال ابن حجر: "كان يقظا حاد
_________
(١) إنباء الغمر ٢: ١٧٤ وطبقات المفسرين للداودي ١: ٤٣٢ والضوء اللامع ٥: ٣٢٠ والشذرات ٧: ٣١ والإعلام لابن قاضي شهبة ١٨٩ ب وقضاة دمشق ٢٨٨ والدارس ٢: ١٢٤.','1','380',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (684,'الذهن، اشتغل بالأدب والوعظ، وحصل له أتباع وأحدث ذكرا بألحان وأوزان تجمع الناس عليه، وكان له نظم كثير واقتدار على جلب الخلق مع خفة ظاهرة، وكان أبوه معجبا به وأذن له في الكلام على الناس وهو دون العشرين، وشعره ينعق بالاتحاد المفضي إلى الإلحاد، وكذا نظم والده" وأثنى عليه المقريزي فقال: "كان جميل الطريقة، مهيبا معظما، دان أصحابه بحبه، واعتقدوا رؤيته عبادة وبذلوا له رغائب أموالهم". له مؤلفات، منها "تفسير القرآن" (١).

الجرجاني [٧٤٠ - ٨١٦ هـ / ١٣٤٠ - ١٤١٣ م]

<span data-type="title" id=toc-971>علي بن محمد بن على الحسيني، أبو الحسن، المعروف بالشريف الجرجاني، وبالسيد الشريف:</span>
فيلسوف، عالم بالعربية والتفسير والمنطق، مشارك في أنواع من العلوم. ولد في تاكو، قرب
_________
(١) طبقات المفسرين للداودي ١: ٤٣٤ وإنباء الغمر ٢: ٣٠٨ والضوء اللامع ٦: ٢١ والشذرات ٧: ٧٠ والأعلام ٥: ١٥٩ ومعجم المؤلفين ٧: ٢٣١ وهدية العارفين ١: ٧٢٧ وطبقات الشعراني ٢: ٢٠ ومولده فيه سنة ٧٦١ هـ.','1','380',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (685,'إستراباد، وتعلم بجرجان، ودخل مصر فأخذ عن البابرتي ومبارك شاه وغيرهما، وأقام بها أربع سنين، ورحل إلى قرمان في بلاد الروم، ثم استوطن شيراز ودرس فيها. ولما دخلها تيمور سنة ٧٨٩ هـ، رحل إلى ما وراء النهر وأقام بسمرقند مدة، ثم عاد إلى شيراز بعد موت تيمور سنة ٨٠٧ هـ، فأقام إلى أن توفي. له نحو خمسين مصنفا، منها "تفسير الزهراوين" (البقرة وآل عمران) و "حاشية" على أنوار التنزيل للبيضاوي، في التفسير، و "حاشية" على الكشاف للزمخشري، مخطوطة، وصل فيها إلى {إن الله لا يستحيي أن يضرب مثلا ما بعوضة فما فوقها} (البقرة: ٢٦) و "رسالة" في الأنفس والآفاق، يعني {سنريهم آياتنا في الآفاق وفي أنفسهم} (فصلت: ٥٣). وغير ذلك (١).
_________
(١) طبقات المفسرين للداودي ١: ٤٢٨ وبغية الوعاة ٢: ١٩٦ ومفتاح السعادة ١: ٢٠٨ والضوء اللامع ٥: ٣٢٨ والفوائد البهية ١٢٥ والبدر الطالع ١: ٤٨٨ ودائرة المعارف الإسلامية ٦: ٣٣٣ ومعجم المطبوعات ٦٧٨ وروضات الجنات ٤٩٧ وكشف الظنون ١٩٣، ٤٤٨، ٨٤٩، ٨٥٤، ١٤٧٩ وتاريخ آداب اللغة العربية ٣: ٢٥٢ والأعلام ٥: ٧ وهدية العارفين ١: ٧٢٨ ومعجم المؤلفين ٧: ٢١٦.','1','381',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (686,'اليماني [٧٦٩ - ٨٣٧ هـ / ١٣٦٧ - ١٤٣٣ م]

<span data-type="title" id=toc-972>علي بن محمد بن أبي القاسم بن محمد بن جعفر، من سلالة الهادي يحيى بن الحسين:</span>
مفسر يماني، من مجتهدي الزيدية. قال الشوكاني: "كان يقرىء الطلبة في جميع علوم الاجتهاد وفي الأمهات وسائر كتب التفسير، وذكر أن له "تفسيرا للقرآن" في ثمانية مجلدات. قال الزركلي: "منها جزء في شستربتي (٤١٩٥) أنجزه سنة ٧٨٩ هـ." وله "تجريد الكشاف" في التفسير أيضا، مخطوط، في مجلدين، في مكتبة خدابخش. قال الزركلي: "وأضاف إليه (أي الكشاف) لطائف ودقائق" (١).

ابن خطيب الناصرية [٧٧٤ - ٨٤٣ هـ / ١٣٧٢ - ١٤٤٠ م]

<span data-type="title" id=toc-973>علي بن محمد بن سعد بن محمد بن علي بن عثمان، أبو الحسن، علاء الدين الطائي الجبرينى، المعروف بابن خطيب الناصرية:</span>
قاضي حلب
_________
(١) البدر الطالع ١: ٤٨٥ والأعلام ٥: ٨ ومعجم المؤلفين ٧: ٢٢٦ وصحيفة المكتبة بطهران ٣: ١٦.','1','381',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (687,'وعالمها ومؤرخها، أصله من "بيت جبرين بشرقي حلب، ومولده ووفاته بحلب. قال أبو المحاسن ابن تغري بردي: "كان إماما عالما في الفقه والأصول والعربية والحديث والتفسير، وأفتى ودرس بحلب، وقدم القاهرة غير مرة، إلى أن قال: "وكان يتولى القضاء بالبذل، ويخدم أرباب الدولة بأموال كثيرة، وملخص الكلام: أنه كان عالما غير مشكور السيرة، وكان به صمم خفيف". وولي قضاء طرابلس ثم قضاء حلب، قال السخاوي بعد أن أثنى عليه: "وحمدت سيرته في البلدين" وذكره شيخ الإسلام ابن حجر العسقلاني في معجمه وقال: "انفرد برياسة المملكة الحلبية غير مدافع" وقال المقريزي في عقوده: "كان رئيس حلب على الإطلاق، ولم يخلف ببلاد الشام بعده مثله". من آثاره "الطيبة الرائحة في تفسير الفاتحة" قال السخاوي: "انتزعه من تفسير البغوي بزيادات" (١)
_________
(١) البدر الطالع ١: ٤٧٦ والضوء اللامع ٥: ٣٠٣ والسلوك ٤/ ٣: ٨٤٣ والنجوم الزاهرة ١٥: ٤٧٩ والأعلام ٥: ٨ وإعلام النبلاء ٥: ٢٢٤ وهدية العارفي ١: ٧٣١ ومجلة المجمع العلمي العربي ١٦: ١٨٤ وكشف الظنون ٢٤٩.','1','382',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (688,'مصنفك [٨٠٣ - ٨٧٥ هـ / ١٤٠٠ - ١٤٧٠ م]

<span data-type="title" id=toc-974>علي بن محمد (مجد الدين) بن مسعود بن محمود الشاهرودي البسطامي، علاء الدين والملة المعروف بمصنفك:</span>
باحث، من سلالة فخر الدين الرازي، ولد بخراسان، وانتقل إلى هراة (سنة ٨١٢) فتعلم بها، ثم انتقل إلى قونية (سنة ٨٤٨ هـ) معلما، فالآستانة، وتوفي بها. لقب بمصنفك لاشتغاله بالتأليف في حداثة سنه، والكاف للتصغير في لغة العجم. من كتبه "المحمدية" تفسير كبير باللغة الفارسية، ألفه للسلطان محمد خان، قال في كشف الظنون: "وبقي على نقصان، وقد رأيت آخره" و "ملتقى البحرين" في التفسير أيض١: قال في الكشف: "وكثيرا ما يحيل تحقيقات القواعد النحوية على هذا الكتاب في شرح قصيدة البردة، وصرح بأنه تفسير مكمل". و "حاشية" على "تفسير الكشاف" للزمخشري (١).
_________
(١) هدية العارفين ١: ٧٣٥ والبدر الطالع ١: ٤٩٧ والفوائد البهية ١٩٢ والشقائق النعمانية ١: ٢٥٥ ومفتاح السعادة ١: ١٥١ ومعجم المؤلفين ٧: =','1','382',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (689,'الطوسي [... - ٨٧٧ هـ / ... - ١٤٧٣ م]

<span data-type="title" id=toc-975>علي بن محمد الطوسي البتاركاني، علاء الدين:</span>
حكيم، أصولي، من أعيان فقهاء الحنفية، من أهل سمرقند. دخل بلاد الروم وأقام بالقسطنطينية، وأكرمه السلطان مراد العثماني وولاه مدرسة أبيه السلطان محمد خان ببروسة. ولما ولي السلطان محمد بن مراد ولاه إحدى مدارس القسطنطينية في المكان المعروف الآن بجامع زيرك، وقد حضر السلطان درسه فسر منه ومنحه عشرة آلاف درهم. ثم نقل إلى أدرنة، ومنها رحل إلى تبريز، فإلى ما وراء النهر، ومات بسمرقند. من كتبه "حواشي" على حاشية الكشاف للشريف الجرجاني (١).
_________
= ٣٤٠، وهو فيه علي بن محمود ومثله في شذرات الذهب ٧: ٣١٩. وانطر الأعلام ٥: ١٦١ وكشف الظنون ٤٥٨ و ١٦٦٨ و ١٨١٦.
(١) نطم العقيان ١٣٢ وهدية العارفين ١: ٧٣٧ والفوائد البهية ١٤٥ وابن إياس ٢: ١٤٦ والشقائق النعمانية ٦٠ والأعلام ٥: ١٦٢ وكشف الظنون ١٤٧٩ ومعجم المؤلفين ٧: ١٨٥ وبروكلمان ٢: ٢٠٤ والذيل ٢: ٢٧٩.','1','383',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (690,'القوشجي [... - ٨٧٩ هـ / ... - ١٤٧٤ م]

<span data-type="title" id=toc-976>علي بن محمد القوشجي، علاء الدين:</span>
فلكي، رياضي، مشارك في بعض العلوم، من فقهاء الحنفية، أصله من سمرقند، كان أبوه من خدام "ألغ بك" أمير ما وراء النهر، يحفظ له البزاة (ومعنى القوشجي في لغتهم حافظ البازي). قرأ على علماء سمرقند، ورحل، فأخذ عن علماء كرمان وغيرها، ثم عاد إلى بلده، وكان الغ بك قد بنى رصدا لسمرقند، ولم يكمل، فأكمله القوشجي. ومات ألغ وولي ابنه، فرحل القوشجي إلى تبريز، فأكرمه سلطانها الأمير حسن الطويل، وأرسله في سفارة إلى السلطان محمد خان، في بلاد الروم، ليصلح بينهما، فاستبقاه محمد خان عنده، وألف له "المحمدية" رسالة في الحساب، فولاه السلطان مدرسة آيا صوفيا، فأقام بالآستانة، وتوفي بها. له "حاشية" على أوائل حاشية سعد الدين التفتازاني على الكشاف، في التفسير. و "جواهر التفسير" وهو تفسير الزهراوين، أي البقرة وآل عمران، ذكره صاحب هدية','1','383',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (691,'العارفين (١).

الشيرازي [... - ٩٢٢ هـ / ... - ١٥١٦ م]

<span data-type="title" id=toc-977>علي بن محمد الشيرازي، العمري، مظفر الدين:</span>
نحوي متكلم، متصوف، مفسر، من فقهاء الشافعية. سكن حلب سنة ٩١٦ هـ، ثم دخل بلاد الروم فأكرمه السلطان وعينه مدرسا بمدرسة مصطفى باشا بالقسطنطينية. وأضرت عيناه، فاعتزل التدريس وأقام بمدينة بروسة إلى أن مات. له "رسالة" في تفسير قوله تعالى {لو كان فيها آلهة إلا الله لفسدتا} (٢).

الشيرازي [.. - بعد ٩٤٥ هـ / .. - ١٥٣٨ م]

<span data-type="title" id=toc-978>علي بن محمد الشيرازي، علاء الدين:</span>
مفسر، من فقهاء الحنفية. له
_________
(١) البدر الطالع ١: ٤٩٥ والفوائد البهية ٢١٤ والشقائق النعمانية ٩٧ وهدية العارفين ١: ٧٣٦ وكشف الظنون ٤٤٨ و ١٤٧٩ ومعجم المطبوعات ١٥٣٠ وبروكلمان، الذيل ٢: ٣٢٩ وهو فيه القشجي بضم القاف وسكون الشين خطأ.
(٢) الكواكب السائرة ١: ٢٦٣، كشف الظنون ٨٨٨ وهدية العارفين ١: ٧٤١ وشذرات الذهب ٨: ٨٩ ومعجم المؤلفين ٧: ٢٠٤ والأعلام ٥: ١١.','1','384',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (692,'كتب منها "مصباح التعديل في كشف أنوار التنزيل" حاشية على تفسير الزهراوين (البقرة وآل عمران) من تفسير البيضاوي. قال صاحب كشف الظنون: "أولها الحمد لله الذي أنزل على عبده الكتاب .. " فرغ منها في رجب سنة ٩٤٥ هـ " (١).

ابن عراق [٩٠٧ - ٩٦٣ هـ / ١٥٠٢ - ١٥٥٦ م]

<span data-type="title" id=toc-979>علي بن محمد بن علي بن عبد الرحمن بن عراق الكناني، الشامي، ثم الحجازي، سعد الدين، ويقال نور الدين:</span>
فقيه شافعي، مقرىء، محدث، متصوف، له نظم، وله اشتغال بالتفسير، وفيه قوة على نقد الشعر. ولد بساحل بيروت، وقيل بدمشق. ونشأ وأقام بالمدينة المنورة إلى أن توفي وهو خطيبها وإمامها. قال الغزي: "ودخل دمشق وحلب في رحلته إلى بلاد الروم، ثم قدم دمشق سنة ٩٤٧ هـ ومنها توجه إلى القاهرة" وفيها أتم (٩٥٤) تأليف
_________
(١) هدية العارفين ١: ٧٤٤ والأزهرية ١: ٢٩٦ وكشف الظنون ١٩٣ و ١٧٠٥.','1','384',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (693,'كتابه "تنزيه الشريعة المرفوعة عن الأخبار الشنيعة المرفوعة" الذي أهداه إلى السلطان سليمان العثماني. من آثاره الأخرى "الصراط المستقيم إلى معاني بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ" نقله محمد بن بلال الآيديني إلى التركية لرستم باشا (١).

حناوي زاده [٩١٨ - ٩٧٩ هـ / ١٥١٢ - ١٥٧٢ م]

<span data-type="title" id=toc-980>علي بن محمد حناوي زاده، علاء الدين:</span>
قاض، مفسر، فقيه حنفي، شاعر، تركي الأصل والبيئة، مستعرب، ولد في إسبارسة من لواء حميد، ودرس في أدرنة وبروسة وكوتاهية والقسطنطينية. ثم ولي قضاء دمشق، فقضاء بروسة، فأدرنة فالقسطنطينية، فقضاء العسكر في ولاية أناطولي. وفي سنة ٩٧٩ رافق السلطان في رحلته إلى أدرنة فمات بها في شهر رمضان، وحضر جنازته الوزراء والعلماء. من تصانيفه "حاشية على تفسير الكشاف" و "حاشية على
_________
(١) الرسالة المستطرفة ١١٣ والأزهرية ١: ٣٩٨ وشذرات الذهب ٨: ٣٣٧ والكواكب السائرة ٢: ١٩٧ وكشف الظنون ١٠٧٧ والمكتبة العبدلية ٤٨ والأعلام ٥: ١٢ ومعجم المؤلفين ٧: ٢١٨.','1','385',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (694,'تفسير البيضاوى" وقال صاحب العقد المنظوم: "وله رسالة ضخمة تتعلق بالتفسير كتبها بعدما جرت المناظرة بينه وبين الشيخ بدر الدين الغزي" (١).

ابن مطير [٩٥٠ - ١٠٤١ هـ / ١٥٤٣ - ١٦٣٢ م]

<span data-type="title" id=toc-981>علي بن محمد بن أبي بكر بن إبراهيم، ابن مطير، الحكمي العبسي اليماني:</span>
فقيه شافعي، له علم بالتفسير واللغة والأدب، وله نظم. توفي بعبس الحضن من المخلاف السليماني بتهامة اليمن، وإليها نسبته "العبسي". من كتبه "الضنائن" في تكملة تفسير القرآن لجده (٢).

العقيبي [١٠٣٣ - ١١٠١ هـ / ١٦٢٤ - ١٦٩٠ م]

<span data-type="title" id=toc-982>علي بن محمد العقيبي الأنصاري، عفيف الدين:</span>
عالم شافعي، نحوي،
_________
(١) العقد المنظوم ٤١١ والأعلام ٥: ١٢ ومعجم المؤلفين ٧: ٩٣.
(٢) ملحق البدر الطالع ١٧٦ وخلاصة الأثر ٣: ١٨٩ وهدية العارفين ١: ٧٥٥ والأعلام ٥: ١٦٧ وإيضاح المكنون ٢: ٧٣ ومعجم المؤلفين ٧: ١٨٦.','1','385',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (695,'مفسر، أصولي، كان محدث الديار اليمنية في وقته. من أهل تعز مولدا ونشأة ووفاة. نسبته إلى ذي عقب من قرى ذي جبلة باليمن الأسفل. رحل إلى الحرمين رحلتين أطال الغياب في ثانيتهما، وتصدر لإقراء الحديث والإجازة بالأمهات السبع. من كتبه "حاشية على تفسير الجلالين" ٢٠ كراسا (١).

العاملي [... - ١١٤٠ هـ / ... - ١٧٢٨ م]

<span data-type="title" id=toc-983>علي بن محمد طاهر بن عبد الحميد ابن موسى النباطي الأصبهاني نزيل النجف، أبو الحسن:</span>
فقيه إمامي، أصولي، مفسر، له نظم. توفي في حدود ١١٤٠ هـ. من كتبه "مشكاة الأنوار في تفسير القرآن" (٢).
_________
(١) نشر العرف ٢: ٢٦٩ والبدر الطالع ١: ٤٩٦ وهو فيه العقيني، وعنه معجم المؤلفين ٧: ٢١٢ وهدية العارفين ١: ٧٦٣. وانظر الأعلام ٥: ١٤.
(٢) هدية العارفين ١: ٧٦٦ وإيضاح المكنون ٢: ٤٨٧ ومعجم المؤلفين ٧: ٢٠٥.','1','386',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (696,'الطباطبائي [... - ١١٩٥ هـ / .. - ١٧٨١ م]

<span data-type="title" id=toc-984>علي بن محمد رفيع الطباطبائي:</span>
فقيه إمامي، مفسر، متكلم. توفي بأصفهان. من كتبه "حاشية" على "أنوار التنزيل" في التفسير للبيضاوي (١).

السليمي [١١١٣ - ١٢٠٠ هـ / ١٧٠١ - ١٧٨٦ م]

<span data-type="title" id=toc-985>علي بن محمد بن علي بن سليم الدمشقي الصالحي، أبو الحسن علاء الدين، المعروف بالسليمي:</span>
فقيه شافعي، مفسر، من أهل دمشق. من كتبه "تكملة شرح تفسير البيضاوي" للنجم عمر الرومي، من سورة الإسراء إلى آخر القرآن (٢).

الآمدي [... - ١٢١٠ هـ / ... - ١٧٩٥ م]

<span data-type="title" id=toc-986>علي بن محمد الحزوري، الآمدي:</span>
فقيه شافعي، مفسر، من أهل آمد،
_________
(١). . . . . ٤٢: ٣٥
(٢) سلك الدرر ٣: ٢١٩ وهدية العارفين ١: ٧٧١ وفهرس الفهارس ٢: ٣٤٢ والروضة الغناء ١٤٠ وإيضاح المكنون ١: ١٣٩.','1','386',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (697,'ولي الإفتاء بها. من كتبه "تفسير سورة الفاتحة" بالحروف المهملة (١).

الميلي [... - ١٢٤٨ هـ / ... - ١٨٣٣ م]

<span data-type="title" id=toc-987>علي بن محمد الميلي الجمالي:</span>
فقيه مالكي، متكلم، مفسر، نسبته إلى "ميلة" بقرب قسنطينة، بالجزائر. استوطن مصر وتوفي بها. له كتب كثيرة، منها "تحفة الأحباب" في تفسير قوله تعالى {ثم أورثنا الكتاب} (٢).

الحكيم [١٢٠٠ - ١٣٠٠ هـ / ١٧٨٦ - ١٨٨٣ م]

<span data-type="title" id=toc-988>علي بن محمد الحسيني النجفي، الشهير بالحكيم:</span>
فاضل إمامي، مشارك في التفسير والطب والحساب. من أهل النجف مولدا ووفاة. له "حواش" على كتاب "الكليات" في التفسير (٣).
_________
(١) هدية العارفين ١: ٧٧٢ وإيضاح المكنون ١: ٣٠٧.
(٢) معجم أعلام الجزائر ٣٢٤ وهدية العارفين ١: ٧٧٣ والأعلام ٥: ١٧ وبروكلمان ٢: ٦٥٥ والذيل ٢: ٨٨٠.
(٣) أعيان الشيعة ٤٢: ٤٤.','1','387',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (698,'النقوي [١٢٦٠ - ١٣١٢ هـ / ١٨٤٤ - ١٨٩٤ م]

<span data-type="title" id=toc-989>علي محمد بن محمد بن دلدار علي النقوي النصير آبادي:</span>
باحث، من فقهاء الإمامية، من أهل "لكهنوء" بالهند. كان يحسن الفارسية والعربية والسريانية والعبرية. له نحو مئة كتاب أكثرها بالفارسية، ومن العربية "أحسن القصص" في تفسير سورة يوسف، طبع (١).

المرعشي [... - ١٣٤٤ هـ / ... - ١٩٢٥ م]

<span data-type="title" id=toc-990>علي بن محمد حسين بن محمد علي الحسيني المرعشي:</span>
فقيه إمامي، مفسر، متكلم. من كتبه "التبيان في تفسير غريب القرآن" (٢).

النجار [... - ١٣٥١ هـ / ... - ١٩٣٢ م]

<span data-type="title" id=toc-991>علي بن محمد بن عامر النجار:</span>
عالم بالأصول والنحو والتفسير، مشارك في
_________
(١) أعيان الشيعة ٤٢: ٣٤ والذريعة ١: ١١٥ و ٢٨٨ ثم ٥: ٢٨٩ ومعجم المؤلفين ٧: ٢٢٨ والأعلام ٥: ١٧١ وأحسن الوديعة ٢٠١.
(٢) أعيان الشيعة ٤٢: ٤٠ والذريعة ١: ٢٠٧.','1','387',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (699,'بعض العلوم. ولد في "عزبة الحرمل" التابعة لبلدة معنية بمصر، وتعلم بالأزهر بالقاهرة وتخرخ به، ومات بالقاهرة. له تصانيف (١).

ابن مراد [... - حيا ١٠٨٣ هـ / ... - حيا ١٦٧٢ م]

<span data-type="title" id=toc-992>علي بن مراد:</span>
مفسر، ذكره كحاله في كتابه "معجم المؤلفين" نقلا عن الأستاذ حسين علي محفوظ وقال: له " أنوار القرآن في مصباح الإيمان" في التفسير. ولم أعثر على ترجمة وافية له فيما بين يدي الساعة من كتب الرجال (٢).

الرديني [... - ٥٤٠ هـ / ... - ١١٤٥ م]

<span data-type="title" id=toc-993>علي بن مرزوق بن عبد الله الرديني، أبو الحسن:</span>
فقيه عارف بالتفسير، قال فى طبقات المفسرين: "كانت كلمته مقبولة عند الملوك، أقام بمسجد سعد الدولة (بالقاهرة) ثم تحول منه إلى مسجد الرديني الموجود
_________
(١) الأعلام الشرقية ٢: ١٤١ ومعجم المؤلفين
(٢) معحم المؤلفين ٧: ٢٤١.','1','388',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (700,'الآن (القرن التاسع الهجري) بداخل قلعة الجبل" ومات بالقاهرة ودفن بالقرافة (١).

السلمي [... - ٥٣٣ هـ / ... - ١١٣٩ م]

<span data-type="title" id=toc-994>علي بن المسلم بن محمد بن علي بن الفتح، أبو الحسن جمال الإسلام السلمي:</span>
مفسر، فرضي، حاسب، من فقهاء الشافعية، من أهل دمشق، كان مفتي الشام في عصره، لزم الإمام الغزالي مدة إقامته بدمشق وأخذ عنه، قال الغزالي يوم ترك الشام عائدا إلى بلده: "خلفت بالشام شابا إن عاش كان له شأن" وقال ابن عساكر: "لم يخلف بعده مثله". وقال - الذهبي: "له مصنفات في الفقه والتفسير". وهو في بعض مصادر ترجمته: ابن الشهرزوري، وفي بعضها الآخر: ابن السهروردي (٢).
_________
(١) طبقات المفسرين للداودي ١: ٤٣٤
(٢) طبقات المفسرين للسيوطي ٢٦ وطبقات المفسرين للداودي ١: ٤٣٥ وتبيين كذب المفتري ٣٢٦ وطبقات السبكي ٧: ٢٣٥ وشذرات الذهب ٤: ١٠٢ ومرآة الجنان ٣: ٢٦١ والدارس ١: ١٨٠ والعبر ٤: ٩٢ وهدية العارفين ١: ٦٩٦ والأعلام ٤: ٢٢ (ط ٥).','1','388',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (701,'ابن مهزيار [... - نحو ٢٥٠ هـ / ... - ٨٦٥ م]

<span data-type="title" id=toc-995>علي بن مهزيار، أبو الحسن:</span>
فقيه إمامي، مفسر، مشارك في بعض العلوم، من أهل الأهواز، أصله من الدورق بخوزستان، كان هو وأبوه نصرانيين، وأسلما. ونشأ علي في الأهواز، وتفقه، وروى عن علي بن موسى، الملقب بالرضا، ثامن الأئمة الأثني عشر عند الإمامية، المتوفى سنة ٢٠٣ هـ. واختص بأبي الحسن العسكري، علي بن محمد، الملقب بالهادي، المتوفى سنة ٢٥٤ هـ. له تصانيف كثيرة منها "تفسير القرآن" (١).

القمي [... - ٣٠٥ هـ / ... - ٩١٧ م]

<span data-type="title" id=toc-996>علي بن موسي بن يزداد، وقيل يزيد، القمي، النيسابوري، أبو الحسن:</span>
إمام الحنفية في عصره، مفسر، محدث. تصدر بنيسابور
_________
(١) هدية العارفين ١: ٦٧٤ والأعلام ٤: ٢٥ ومنهج المقال ٢٣٩ وتنقيح المقال ٢: ٣١٠ والذريعة ٢: ١٠٥ وكتاب الرجال للنجاشي ١٧٧ وسفينة البحار ٢: ٢٥١ وإيضاح المكنون ١: ٣٠٤.','1','389',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (702,'للإفادة، وتخرج به جماعة من الكبار. من كتبه "أحكام القرآن" قال السيوطي: "وهو كتاب جليل" (١).

الحجازي [٨٤١ - حيا ٩١٦ هـ / ١٤٣٧ - حيا ١٥١٠ م]

<span data-type="title" id=toc-997>علي بن ناصر بن محمد بن أحمد البلبيسي ثم المكي، أبو الحسن، علاء الدين، ويعرف بالحجازي، وبابن ناصر:</span>
فقيه شافعي، مفسر، محدث، ولد بمكة وبها نشأ وتعلم، ودخل القاهرة ودمشق وبيت المقدس، وأخذ عن علمائها. قال في الشذرات: "له تآلف في الحديث والتفسير والأصول". منها "تفسير القرآن الكريم" منه المجلد الخامس في مكتبة خدابخش (٢).
_________
(١) طبقات المفسرين للسيوطي ٢٦ وطبقات المفسرين للداودي ١: ٤٣٦ والجواهر المضية ١: ٣٨٠ وتاج التراجم ٤٢ واللباب ٣: ٥٥ وكشف الظنون ٢٠ والفهرست لابن النديم ٢٠٧ وهدية العارفين ١: ٦٧٥ والأعلام ٤: ٢٦ (ط ٥).
(٢) الضوء اللامع ٦: ٤٥ والشذرات ٨: ٧١ والكواكب السائرة ١: ٢٧٨ وهدية العارفين ١: ٧٤١ وصحيفة المكتبة ٣: ١٨ والأعلام ٥: ٢٧.','1','389',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (703,'الخراساني [١٢٩٧ - ... هـ / ١٨٨٠ - ... م]

<span data-type="title" id=toc-998>علي نقي المعروف بميرزا هادي الخراساني الحائري:</span>
فقيه إمامي، له مشاركة في بعض العلوم. من كتبه "حاشية" على تفسير علي بن إبراهيم القمي: انظر ترجمته (١).

الزيدي [... - حيا ٦٨٠ هـ / ... - حيا ١٢٨١ م]

<span data-type="title" id=toc-999>علي بن يحيى بن محمد البناء الزيدي:</span>
مفسر، له "المنهج القويم في تفسير القرآن الكريم" (٢).

السمرقندي [... - ٨٦٠ هـ / ... - ١٤٥٦ م]

<span data-type="title" id=toc-1000>علي بن يحيى السمرقندي، ثم القرماني، علاء الدين:</span>
فقيه حنفي، مفسر، منطقي، أصله من سمرقند، إستوطنا لارندة من بلاد قرمان، وتوفي بها. من كتبه "بحر العلوم في تفسير القرآن" في أربع مجلدات، وصل فيه
_________
(١) أعيان الشيعة ٤٢: ٩٨ ومعجم المؤلفين ٧:
(٢) معجم المؤلفين ٧: ٢٦١ و  Brock : g,l : ٤٠٤','1','390',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (704,'إلى سورة المجادلة. قال صاحب كشف الظنون: "وهو كتاب كبير فيه فوائد جليلة انتخبها من كتب التفاسير وأضاف إليها فوائد من عنده بعبارات فصيحة" (١).

البكري [٦٧٣ - ٧٢٤ هـ / ١٢٧٤ - ١٣٢٤ م]

<span data-type="title" id=toc-1001>علي بن يعقوب بن جبريل بن عبد المحسن البكري المصري، أبو الحسن، نور الدين:</span>
فقيه شافعي، مفسر، بياني، من أهل القاهرة. هاجم القبط في إحدى كنائسهم، لاستعارتهم قنديلا من جامع عمرو بن العاص، فشكوه إلى السلطان (الملك الناصر) فسمعه السلطان يقول وهو يخطب بين يديه: "أفضل الجهاد كلمة حق عند سلطان جائر" فقال وقد اشتد غضبه: أنا جائر؟ فأجاب نعم - أنت سلطت الأقباط على المسلمن، فطرده، وأمر بقطع لسانه، ثم اكتفى بنفيه من القاهرة، فخرج إلى دهروط، وتوفي بها ودفن بالقاهرة. قال ابن حجر: "وهو ممن كان يشدد على ابن تيمية لما امتحن
_________
(١) كشف الظنون ٢٢٥ وهدية العارفين ١: ٧٣٣ ودار الكتب الشعبية ٤٤ والأعلام ٥: ٣٢.','1','390',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (705,'بالقاهرة" وقال الذهبي: "وثب مرة على ابن تيمية ونال منه". من كتبه " تفسير الفاتحة" (١).

الشطنوفي [٦٤٤ - ٧١٣ هـ / ١٢٤٦ - ١٣١٤ م]

<span data-type="title" id=toc-1002>علي بن يوسف بن حريز بن معضاد ابن فضل اللخمي، أبو الحسن، نور الدين، الشطنوفي:</span>
فقيه شافعي، عالم بالقراءات، نحوي، كان شيخ الديار المصرية في عصره، أصله من البلقاء بالشام. مولده ووفاته بالقاهرة. قال السيوطي: "تولى تدريس التفسير بالجامع الطولوني، وتصدر للإقراء بجامع الحاكم" وقال ابن مكتوم: "كان رئيس المقرئين بالديار المصرية" ومعدودا في المشايخ من النحاة، وله اليد الطولى في التفسير، وعلق فيه "تعليقا" (٢).
_________
(١) الدرر الكامنة ٣: ٢١٤ وذيل العبر ١٣٣ والسبكي ٦: ٤٤٢ وابن كثير ١٤: ١١٤ والشذرات ٦: ٦٦ وهدية العارفين ١: ٧١٧ وكشف الظنون ٤٥٥ وطبقات المفسرين للداودي ١: ٤٣٧ وحسن المحاضرة ١: ٤٢٣.
(٢) طبقات الداودي ١: ٤٣٨ وغاية النهاية ١: ٥٨٥ والدرر الكامنة ٣: ٢١٦ وبغية الوعاة ٢: ٢١٣ وهدية العارفين ١: ٧١٦ والأعلام ٥: ١٨٨ ومعجم المؤلفين ٧: ٢٦٤.','1','391',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (706,'الشريف عمر [٤٤٢ - ٥٣٩ هـ / ١٠٥٠ - ١١٤٥ م]

<span data-type="title" id=toc-1003>عمر بن إبراهيم بن محمد بن أحمد الحسيني العلوي، أبو البركات:</span>
من رجال الحديث واللغة، مشارك في الفقه والتفسير والنحو والأدب، من أهل الكوفة، مولدا ووفاة، سمع بها وببغداد، وأقام بدمشق مدة، ثم بحلب، وولي الإفتاء بالكوفة. كان زيديا معتزليا، وكان يقول: "أنا زيدي المذهب ولكن أفتي على مذهب السلطان (يعني مذهب أبي حنيفة ظاهرا ومذهب زيد تدينا) ". قال القفطي: "كان واسع الرواية، أدرك المشايخ الجلة، وتكرر إليه المحدثون ونقلوا عنه الأحاديث والأخبار لسعة روايته". وقيل: صرخ بالقول بخلق القرآن والقدر (١).

ابن شاهين [٢٩٧ - ٣٨٥ هـ / ٩٠٩ - ٩٩٥ م]

<span data-type="title" id=toc-1004>عمر بن أحمد بن عثمان بن أحمد، (أبو حفص، المعروف بابن شاهين)</span>
_________
(١) طبقات الداودي ٢: ١ ومعجم الأدباء ١٥: ٢٥٧ وبغية الوعاة ٢: ٢١٥ والمنتظم ١٠: ١١٤ والنجوم الزاهرة ٥: ٢٧٦ والبداية والنهاية ١٢: ٢١٩ والشذراث ٤: ١٢٢ وإنباه الرواة ٢: ٣٢٤ ولسان الميزان ٤: ٢٨٠ ونزهة الألباء ٤٧٨ وهدية العارفين ١: ٧٨٣ وأعيان الشيعة ٤٢: ٢١٦.','1','391',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (707,'أبو حفص، المعروف بابن شاهين: محدث، حافظ، مفسر، واعظ، من أهل بغداد، تعلم بها، ثم رحل فسمع بالشام ومصر وفارس وبعض المدائن. له نحو ٣٠٠ مصنف، منها "تفسير القرآن" في نحو ٣٠ مجلدا. قال ابن ماكولا وغيره: "ثقة مأمون، صنف ما لم يصنفه أحد، إلا أنه لحان ولا يعرف الفقه" (١).

الغزنوي [٧٠٤ - ٧٧٣ هـ / ١٣٠٤ - ١٣٧٢ م]

<span data-type="title" id=toc-1005>عمر بن إسحاق بن أحمد الهندي الغزنوي، سراج الدين، أبو حفص:</span>
قاض، من أكابر فقهاء الأحناف، كان علامة في الأصول والمنطق والفروع، تعلم بالهند، وحج، ثم قدم القاهرة نحو سنة ٧٤٠ هـ وأخذ عن علمائها، وولي قضاء العسكر، ثم ولي القضاء استقلالا في شعبان سنة ٧٦٤ هـ،
_________
(١) طبقات الداودي ٢: ٢ وتذكرة الحفاظ ٩٨٧ وتاريخ بغداد ١١: ٢٦٥ ولسان الميزان ٤: ٢٨٣ ومرآة الجنان ٢: ٤٢٦ والنجوم الزاهرة ٤: ١٧٢ وهدية العارفين ١: ٧٨١ وإيضاح المكنون ١: ٣٠٢ والشذرات ٣: ١١٧ وغاية النهاية ١: ٥٨٨ والمنتظم ٧: ١٥٢ والرسالة المستطرفة ٣٨ والأعلام ٥: ١٩٦ ومعجم المؤلفين ٧: ٢٧٣.','1','392',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (708,'وأضيف إليه التدريس بجامع ابن طولون، واستمر في القضاء إلى أن توفي، فكانت ولايته نحو أربع سنين. قال ابن حجر: "كان واسع العلم كبير الإقدام والمهابة، وكان يتعصب للصوفية الاتحادية". له تصانيف منها "تفسير القرآن" ويعرف بتفسير سراج الدين (١).

الفارقي [٥٩٨ - ٦٨٩ هـ / ١٢٠٢ - ١٢٩٠ م]

<span data-type="title" id=toc-1006>عمر بن إسماعيل بن مسعود بن سعد، أبو حفص، ويقال أبو القاسم، رشيد الدين، الربعي الفارقي:</span>
أديب علامة، كتب في ديوان الإنشاء. كانت له يد طولى في التفسير والبيان والبديع واللغه، وانتهت إليه رياسة الأدب في عصره. قال الذهبي: "اشتغل عليه خلق من الفضلاء، وقد
_________
(١) الفوائد البهية ١٤٨ ونزهة الخواطر ٢: ٩٥ ومعجم المطبوعات ١٣٧٩ والدرر الكامنة ٣: وانباء الغمر ١: ٢٧ والبدر الطالع ١: ٥٠٥ وهدية العارفين ١: ٧٩٠ وحسن المحاضرة ١: ٢٦٨ والنجوم الزاهرة ١١: ١٢٠ وشذرات الذهب ٦: ٢٢٨ وكشف الظنون ٤٤٨ وطبقات الفقهاء لطاش كبري زاده ١٢٩ ومفتاح السعادة ٢: ٥٨ ومعجم المؤلفين ٧: ٢٧٧ والأعلام ٥: ٤٢.','1','392',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (709,'وزر وتقدم في دول، وأفتى وناظر، ودرس بالناصرية مدة، وبالظاهرية، فخنقه لص فيها طمعا بماله". له تصانيف (١)

الخرقي [... - ٣٣٤ هـ / ... - ٩٤٦ م]

<span data-type="title" id=toc-1007>عمر بن الحسن بن عبد الله بن أحمد الخرقي، أبو القاسم:</span>
من أعيان الفقهاء الحنابلة، من أهل بغداد، رحل عنها لما ظهر فيها سب الصحابة، ومات بدمشق. له تصانيف منها "تفسير القرآن" أودعها بغداد قبل رحيله عنها فاحترقت في غيبته، وبقي منها "المختصر" طبع، في فقه الحنابلة، ويعرف بمختصر الخرقي. ويقال أن عدد مسائله ٢٣٠٠ مسألة (٢).
_________
(١) طبقات المفسرين للداودي ٢: ٢ وبغية الوعاة ٢: ٢١٦، العبر ٥: ٣٦٣ والنجوم الزاهرة ٧: ٣٨٥ وفوات الوفيات ٣: ١٢٩ والبداية والنهاية ١٣: ٣١٨ والسبكي ١٨: ٣٠٨ وهدية العارفين ١: ٧٨٧ والأعلام ٥: ١٩٩ والزركشي ٢٣٨ والشذرات ٥: ٤٠٩ والدارس ١: ٣٥١ والاسنوي ٢: ٢٨٦.
(٢) العبر ٢: ٢٣٨ وهدية العارفين ١: ٨٠٣ وهو فيه عمرو بن الحسن والشذرات ٢: ٣٤٦ وتاريخ بغداد ١١: ٢٣٤ واللباب ١: ٤٣٥ والمنتظم ٦: ٣٤٦ وطبقات الحنابلة ٢: ٧٥ ومفتاح السعادة ١: =','1','393',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (710,'الترمذي [... - بعد ٨٥٧ هـ / ... - بعد ١٤٥٣ م]

<span data-type="title" id=toc-1008>عمر بن ... . الترمذي:</span>
واعظ، صوفي، مفسر، من آثاره "تفسير سورة الإخلاص" ألفه للملك الظاهر أبي سعيد جقمق العلائي، أحد ملوك دولة الشراكسة بمصر والشام والحجاز، المتوفى سنة ٨٥٧ هـ (١).

عمر بن الخطاب [٤٠ ق هـ - ٢٣ هـ / ٥٨٤ - ٦٤٤ م]

<span data-type="title" id=toc-1009>عمر بن الخطاب بن نفيل بن عبد العزى القرشي العدوي، أبو حفص:</span>
ثاني الخلفاء الراشدين، وأول من لقب بأمير المؤمنن، وأحد أصهار رسول الله (صلى الله عليه وسلم) ومن كبار علماء الصحابة وزهادهم، يضرب بعدله المثل. يجتمع نسبه مع الرسول (صلى الله عليه وسلم) في الجد السابع، ويجتمع معه من جهة أمه في الجد السادس. ولد بمكة، وكان في
_________
= ٤٣٨ والنجوم الزاهرة ٣: ١٧٨ وكشف الظنون ٤٤٦ وطبقات الفقهاء ١٤٦ ومختصر دول الإسلام ١: ١٦٤ ووفيات الأعيان ٣: ٤٤١ وتذكرة الحفاظ ٨٤٧ ومعجم المؤلفين ٧: ٢٨٣ والأعلام ٥.
(١) فهرست الخديويه ٧: ١٧٣ ومعجم المؤلفين ٧: ٢٧٩.','1','393',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (711,'صغره يرعى الغنم، ثم احترف التجارة وكان يختلف فيها إلى الشام. وكان في الجاهلية من أبطال قريش وأشرافهم، وله السفارة فيهم، ينافر عنهم وينذر من أرادوا إنذاره. أسلم قبل الهجرة بخمس سنين، فقوي به المسلمون. قال ابن مسعود: "كان إسلام عمر فتحا، وكان هجرته نصرا، وكانت إمامته رحمة، وما كنا نقدر أن نصلي عند الكعبة حتى أسلم". وصحب عمر الرسول (صلى الله عليه وسلم) بعد إسلامه، فأحسن صحبته، وبالغ في نصرته، ووقف حياته على المدافعة عنه والذود عن الإسلام. وشهد الوقائع. وكان الرسول (صلى الله عليه وسلم) يستشيره في كثير من الأمور، وقد أثر عنه (صلى الله عليه وسلم) أنه قال: "عمر معي وأنا مع عمر، والحق بعدي مع عمر حيث كان". ولقبه ب "الفاروق" وكناه بأبي حفص. بويع بالخلافة يوم وفاة أبي بكر (سنة ١٣ هـ) وفي أيامه واصلت الجيوش العربية الفتوحات التي بدأت في عهد أبي بكر، فتم فتح الشام والعراق، وافتتحت القدس والمدائن ومصر والجزيرة، حتى قيل: انتصب في مدته اثنا عشر ألف منبر في الإسلام. وهو أول من اتخذ ديوانا لضبط المال، وعمل إحصاء','1','394',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (712,'للمسلمين، وفرض العطاء لهؤلاء، مبتدئا بأزواج النبي (صلى الله عليه وسلم) ثم أهل السابقة. وأول من استقضى القضاء، ووضع للعرب التاريخ الهجري، وكانوا يؤرخون بالوقائع. وأمر بإنشاء معسكرات للجند، أصبحت فيما بعد مدنا زاهرة، كالبصرة والكوفة والفسطاط. ينسب إليه كثير من أحكام أهل الذمة الذين حرص على عدم إرهاقهم. وعرف بشدته في الحق، ومخافته من الله، وحرصه على العدل، والعمل على خدمة رعيته. وكان يحاسب ولاته على أعمالهم ويحصي عليهم أموالهم. ولما طلب منه الصحابة قبل وفاته أن يستخلف، جعل الخلافة في ستة، توفي الرسول (صلعبم) وهو عنهم راض وهم: عثمان وعلي وطلحة بن عبيد الله والزبير بن العوام وسعد بن أبي وقاص وعبد الرحمن بن عوف، وأوصى بأن يشهد عبد الله بن عمر ألا يكون له من الأمر شيء. قتله أبو لؤلؤة فيروز الفارسي (غلام المغيرة بن شعبة) غيلة بخنجر في خاصرته وهو في صلاة الصبح في المسجد، وعاش بعد الطعنة ثلاث ليال. له في كتب الحديث ٥٣٧ حديثا. ويعد ممن اشتهر بالتفسر من الصحابة ولم يرد عنهم في التفسير إلا','1','394',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (713,'النزر اليسير (١).

البلقيني [٧٢٤ - ٨٠٥ هـ / ١٣٢٤ - ١٤٠٣ م]

<span data-type="title" id=toc-1010>عمر بن رسلان بن نصير بن صالح الكناني، العسقلاني الأصل، ثم البلقيني، أبو حفص، سراج الدين:</span>
فقيه شافعي، مجتهد، حافظ للحديث، مفسر، من العلماء بالدين. ولد في "بلقينة" من غربية مصر، ونشأ بالقاهرة وأخذ عن علمائها. ودخل بيت المقدس، وولي قضاء الشام سنة ٧٦٩ هـ، فباشر دون السنة، وولي التدريس بالخشابية، والتفسير بجامع ابن طولون، وبالظاهرية. قال مؤرخوه: "انتهت إليه رياسة المذهب والإفتاء، وقصد من أقطار الأرض
_________
(١) عبقرية عمر للعقاد، والفاروق عمر لمحمد حسين هيكل، وتاريخ عمر بن الخطاب للطنطاوي، وتاربخ عمر بن الخطاب لبشير يموت، وأشهر مشاهير الإسلام للعظم، وصفة الصفوة ١: ١٠١ وحلية الأولياء ١: ٣٨ وأخبار القضاة ١: ١٠٥ والكنى والأسماء ١: ٧ وتهذيب الأسماء واللغات ٢: ٣ والبدء والتاريخ ٥: ٨٨ وتاريخ الخميس ١: ٢٥٩ و ٢: ٢٣٩ والإصابة، الترجمة ٥٧٣٨ والكامل لابن الأثير ٣: ٣٩ وانظر فهرسته، وتاريخ الطبري ٢: ٥٢٥ وانظر فهرسته. والشذرات ١: ٣٣ والموسوعة العربية ١٢٣٦.','1','395',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (714,'للأخذ عنه، وبالفتاوى، وأتاه الناس من الهند واليمن وبغداد وخراسان وبلاد الروم والمغرب والشام والحجاز. مات بالقاهرة. من تصانيفه الكثيرة "الكشاف على الكشاف" حاشية على "الكشاف عن حقائق التنزيل" للزمخشري، في ثلاث مجلدات (١).

البغدادي [١١٥٥ - ١١٩٤ هـ / ١٧٠٣ - ١٧٨٠ م]

<span data-type="title" id=toc-1011>عمر بن عبد الجليل بن محمد بن جميل البغدادي، القادري:</span>
فقيه حنفي، مفسر، نحوي، ولد ببغداد، وبها نشأ وتعلم، ثم قدم الشام واستوطن دمشق إلى أن توفي. له "الكمالين على الجمالين" في التفسير، حاشية على الجمالين لعلي بن سلطان محمد القاري. قال المرادي: "وصل فيها إلى قوله تعالى في أوائل سورة آل عمران: {يختص برحمته من
_________
(١) الضوء اللامع ٦: ٨٥ والبدر الطالع ١: ٥٠٦ وحسن المحاضرة ١: ٣٢٩ وإنباء الغمر ٢: ٢٤٥ وقضاة دمشق ١٥٩ وهدية العارفين ١: ٧٩٢ والشذرات ٧: ٥١ وذيل تذكرة الحفاظ ٢٠٦ وتاج العروس ٩: ١٤٣ والأعلام ٥: ٢٠٥ ومعجم المؤلفين ٧: ٢٨٤ وكشف الظنون ١٤٧٩ والسلوك ٣/ ٣: ١١٠٨.','1','395',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (715,'يشاء والله ذو الفضل العظيم} في نحو ثلاثين كراسة، مات قبل أن يكملها .. " (١).

القزويني [... - ٧٤٥ هـ / ... - ١٣٤٤ م]

<span data-type="title" id=toc-1012>عمر بن عبد الرحمن بن عمر البهبهائي الكناني القزويني الفارسي، أبو حفص، سراج الدين:</span>
مفسر، مشارك في بعض العلوم. قال في الشذرات: "اخترقته المنية شابا عن سبع أو ثمان وثلاثين سنة". وقد خلط بعض المؤرخين بينه وبين عمر بن علي بن عمر القزويني (٦٨٣ - ٧٥٠ هـ) المترجم له في الدرر الكامنة. من كتبه "حاشية" على الكشاف في التفسير للزمخشري، قال صاحب كشف الظنون: سماه "الكشف على الكشاف". مخطوط (٢).
_________
(١) سلك الدرر ٣: ١٧٩ وإيضاح المكنون ٢: ٣٨٢ وهدية العارفين ١: ٧٩٩ والأعلام ٥: ٢٠٨ ومعجم المؤلفين ٧: ٢٨٧.
(٢) طبقات الداودي ٢: ٥ والشذران ٦: ١٤٣ وهدية العارفين ١: ٧٨٩ وكشف الظنون ١٤٨٠ وفهرست الخديوية ١: ١٩٢ وفي حاشية طبقات الداودي أسماء بعض المصادر التي في ترجمت لعمر بن علي ابن عمر القزويني، يراجع منها ذيل تذكرة الحفاظ صفحة ٣٥٨.','1','396',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (716,'بامخرمة [٨٨٤ - ٩٥٢ هـ / ١٤٧٩ - ١٥٤٦ م]

<span data-type="title" id=toc-1013>عمر بن عبد الله بن أحمد بامخرمة الشيباني الحميدي:</span>
شاعر، متصوف، من أهل حضرموت. ولد في مدينة الهجرين وتعلم بها وبعدن. وعلت له شهرة، فنفاه السلطان بدر بن عبد الله ابن جعفر الكثيري سلطان حضرموت، إلى الشحر ثم سيوون، فتصوف، وصنف كتبا منها "الوارد القدسي في تفسير آية الكرسي" (١).

العرضي [٩٥٠ هـ - ١٠٢٤/ ١٥٤٣ - ١٦١٥ م]

<span data-type="title" id=toc-1014>عمر بن عبد الوهاب بن إبراهيم ابن محمود العرضي، القادري:</span>
فقيه شافعي، محدث، له علم بالتاريخ والتفسير، من أهل حلب مولدا ووفاة. اشتغل بالتدريس مدة أربعين سنة، وولي إفتاء الشافعية بحلب. له "حاشية" على تفسير أبي السعود (٢).
_________
(١) تاريخ الشعراء الحضرميين ١: ١٣٠ والأعلام ٥: ٢١٣ ومعجم المؤلفين ٧: ٢٩٣.
(٢) خلاصة الأثر ٣: ٢١٥ وسلك الدرر ٢: ٧٨ وريحانة الألباء ١٣٩ وهدية العارفين ١: ٧٩٦ وإعلام النبلاء ٦: ٢٠٠ وفهرس الفهارس ٢: ١٧٩ والأعلام ٥: ٢٩٣ ومعجم المؤلفين ٧: ٢٩٦.','1','396',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (717,'الجنزي [٤٧٨ - ٥٥٠ هـ / ١٠٨٥ - ١١٥ م] أ

<span data-type="title" id=toc-1015>عمر بن عثمان بن الحسين بن شعيب الجنزي، أبو حفص:</span>
من أئمة الأدب في عصره، له باع طويل في النحو ومعرفة كلام العرب. من أهل جنزة، قرية من قرى أذربيجان. قرأ على أبي المظفر بن أبي العباس الأبيوردي. قال القفطي: "ورد بغداد والبصرة وخوزستان، وذاكر الفضلاء حتى صار علامة زمانه وواحد عصره، وشرع في إملاء "تفسير" لو تم لم يوجد مثله" (١).

ابن علاء الدين [... - ١٠٥٨ هـ / ... - ١٦٤٨ م]

<span data-type="title" id=toc-1016>عمر بن علاء الدين بن عبيد بن حسن بن عمر الغزي، المعروف بابن علاء الدين:</span>
فقيه حنفي، عارف بالتفسير، من أهل غزة (بفلسطين) مولدا ووفاة. تعلم بها وبالقاهرة. وولي الإفتاء بغزة نحو سنة ١٠٥٠
_________
(١) إنباه الرواة ٢: ٣٢٩ وبغية الوعاة ٢: ٢٢١ وطبقات المفسرين للسيوطي ٢٧ وطبقات المفسرين للداودي ٢: ٤ ومعجم الأدباء ١٦: ٦٢ وهدية العارفين ١: ٧٨٣ وإيضاح المكنون ١: ٣٠٤.','1','397',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (718,'واستمر إلى أن توفي. من آثاره "رسالة" في قوله تعالى: {إن رحمة الله قريب من المحسنين} و "رسالة" في قوله تعالى {وفي السماء رزقكم وما توعدون} و "رسالة" في قوله تعالى {ومن يتق الله يجعل له مخرجا ويرزقه من حيث لا يحتسب} وغير ذلك (١).

ابن عبد الحموي [؟ -؟ هـ /؟ -؟ م]

<span data-type="title" id=toc-1017>عمر بن علي بن سليمان بن يعقوب ابن عبد الحموي:</span>
مفسر، ذكره الداودي في طبقاته وقال: "له تفسير في نحو الثلاثين مجلدا" ولم يزد على ذلك. ولم أعثر له على ترجمة وافية فيما بين يدي الساعة من كتب الرجال (٢)

ابن الملقن [٧٢٣ - ٨٠٤ هـ / ١٣٢٣ - ١٤٠١ م]

<span data-type="title" id=toc-1018>عمر بن علي بن أحمد بن عبد الله الأنصاري، سراج الدين، أبو حفص، المعروف بابن الملقن:</span>
فقيه
_________
(١) خلاصة الأثر ٣: ٢١ ومعجم المؤلفين ٧: ٢٩٧.
(٢) طبقات المفسرين للداودي ٢: ٥.','1','397',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (719,'شافعي، عارف بالنحو والحديث وتاريخ الرجال. أصله من وادي آش بالأندلس، رحل أبوه منها إلى التكرور. ثم إلى القاهرة، فولد له صاحب الترجمة فيها. ومات أبوه وهو - أي عمر - طفل، فتزوجت أمه من شيخ كان يلقن القرآن في الجامع الطولوني، فعرف الطفل به، أي بابن الملقن. قال ابن حجر: "وكان ربما عرف بابن النحوي، ولذلك اشتهر بها ببلاد اليمن، واشتهر بكثرة التصانيف حتى كان يقول إنها بلغت ثلاثمائة تصنيف". أثنى عليه التاج السبكي وأبو البقاء والعلائي بينما أكثر القول فيه علماء الشام ومصر ومنهم ابن حجي. من آثاره "مختصر تفسير القرطبي" جامع أحكام القرآن، و "غريب كتاب الله العزيز" (١).

ابن عادل [... - بعد ٨٨٥ هـ / ... - بعد ١٤٧٥ م]

<span data-type="title" id=toc-1019>عمر بن علي بن عادل الدمشقي، سراج الدين:</span>
من علماء الحنابلة في القرن التاسع الهجري، من أهل
_________
(١) إنباء الغمر ٢: ٢١٧ والضوء اللامع ٦: ١٠٠ وشدرات الذهب ٧: ٤٤ والبدر الطالع ١: ٥٠٨ وهدية العارفين ١: ٧٩١.','1','398',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (720,'دمشق، وصاحب التفسير الكبير "اللباب في علوم الكتاب" مخطوط، قال صاحب كشف الظنون: "في ست مجلدات، وهو تفسير مشهور". منه نسخة سلطانية في ٧٠٠٠ ورقة، بقلم واحد، وورق واحد، في خزانة "كتاب سراي" بمغنيسا. ومنه المجلدات ١ و ٢ و ٣، ٥، و ٨ في الرباط، كتب في آخر سورة طه أنه فرغ من تفسيرها في ١٥ رمضان ٨٨٠ هـ. وفي شستربتي والظاهرية، والزيتونة ودار الكتب مجلدات متفرقة منه. قال الزركلي: "وفي مكتبة مدرسة بشير آغا بالمدينة نسخة منه قريبة من الكمال". ولم أعثر له على ترجمة وافية فيما بين يدي الساعة من كتب الرجال. ولعله هو المقصود في الترجمة التي ذكرتها قبل السابقة، أي ترجمة ابن عبد الحموي (١).

الإسبيري [... - ١٢٠٢ هـ / ... ١٧٨٨ م]

<span data-type="title" id=toc-1020>عمر بن علي بن إبراهيم بن خليل الإسبيري:</span>
قاض، فقيه حنفي،
_________
(١) هدية العارفين ١: ٧٩٤ وكشف الظنون ١٥٤٣ وعلوم القرآن ٢٨١ - ٢٨٤ ودار الكتب المصرية. ١: ٦٠ والمخطوطات المصورة ١: ٤١ ومعجم المؤلفين ٧: ٣٠٠ وبروكلمان ٢: ١١٤ والذيل ٢: ١٤ وشستربتي ٣٣٠٨، ٥٠٨٣ والأعلام ٥: ٥٨.','1','398',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (721,'منطقي، متصوف، له اشتغال بالتفسير، من أهل إسبير، قرية قرب أرزن الروم، درس بجامع الفاتح، وولي قضاء إسكدار. قال صاحب هدية العارفين: "له "الرسالة الحودية في تفسير آية في قصة النوحية" (١).

البجيري [٢٢٣ - ٣١١ هـ / ٨٣٨ - ٩٢٣ م]

<span data-type="title" id=toc-1021>عمر بن محمد بن بجير بن حازم الهمذاني السمرقندي البجيري، أبو حفص:</span>
محدث ما وراء النهر في عصره، مفسر، كان من قرية بسمرقند اسمها "خشو فغن" ويقال لها رأس القنطرة. ونسبته إلى جده بجير. رحل في طلب الحديث مرات، وسمع في خراسان والبصرة والكوفة والشام والحجاز ومصر، ويوم دخل مصر في ذي القعدة سنة ٢٤٨ هـ صادف جنازة الإمام الحافظ المحدث أحمد بن صالح الطبري ثم المصري، فشهدها. قال أبو سعد الإدريسي: "كان ثبتا في الحديث، له العناية التامة في طلب الآثار والرحلة" وقال
_________
(١) هدية العارفين ١: ٧٩٩ وبروكلمان ٢: ٤٢٩ ومعجم المؤلفين ٧: ٢٩٧.','1','399',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (722,'الذهبي: "لم يقع لي من عواليه لبعد دياره، وهو صدوق، وقد تفرد بحديث حسن، الخ". أما ما ذكره الزركلي في "لأعلام" من أن الذهبي قال: "سمعت منه ستين ألف حديث أو أكثر" فهو وهم، فالذهبي مات بعد البجيري بنيف وأربعمائة سنة. من كتبه "تفسير القرآن" (١).

النسفي [٤٦١ - ٥٣٧ هـ / ١٠٦٨ - ١١٤٢ م]

<span data-type="title" id=toc-1022>عمر بن محمد بن أحمد بن إسماعيل، أبو حفص، نجم الدين النسفي:</span>
فقيه حنفي، عالم بالتفسير والأدب والتاريخ، ولد بنسف، وبها نشأ وتعلم. ورحل في طلب العلم، ودخل بغداد ومكة، وبها لقي الزمخشري. وكان يلقب بمفتي
_________
(١) طبقات المفسرين للداودي ٢: ٧ واللباب ١: ١٢٢ والبداية والنهاية ١١: ١٤٩ وتذكرة الحفاظ ٧١٩ وشذرات الذهب ٢: ٢٦٢ والعبر ٣: ١٤٩ والنجوم الزاهرة ٣: ٢٠٩ ومعجم البلدان مادة "خشوفغن". وهدية العارفين ١: ٧٨٠ وتاريخ التراث العربي ١: ٤٣٤ ومعجم المؤلفين ٧: ٣٠٧ والأعلام ٥: ٦٠ ومختصر دول الإسلام ١: ١٤٧ والإكمال ١: ١٩٥ و ٤٦٤ وتبصير المنتبه ١: ١٢٣ والمشتبه ٤٨.','1','399',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (723,'الثقلين. سكن سمرقند وتوفي بها. قال الذهبي: "روى عن إسماعيل بن محمد النوحي فمن بعده، وله أوهام كثيرة". وقال السمعاني: "كان إماما فاضلا مبرزا متفننا، كتب إلي بالإجازة بجميع مسموعاته ومجموعاته، ولم أدركه بسمرقند حيا، وحدثني عنه جماعة، وقد ذكرته لكثرة تصانيفه، وشيوع ذكره وإن لم يكن إسناده عاليا". له نحو مئة مصنف، منها ((التيسير في التفسير" مخطوط، قال في كشف الظنون: "أوله الحمد لله الذي أنزل القرآن شفاء ... الخ .. ذكر في الخطبة مائة اسم من أسماء القرآن، ثم عرف التفسير والتأويل، ثم شرع في المقصود وفسر الآيات بالقول وبسط في معناها كل البسط، وهو من الكتب المبسوطة في هذا الفن". و "الأكمل الأطول" في تفسير القرآن أيضا، في أربع مجلدات، مخطوط (١).
_________
(١) تاج التراجم ٤٧ ومعجم الأدباء ١٦: ٧٠ وطبقات الداودي ٢: ٥ وطبقات المفسرين للسيوطي ٢٧ والجواهر المضية ١: ٣٩٤ والفوائد البهية ١٤٩ ومفتاح السعادة ١: ١٢٧ والعبر ٤: ١٠٢ وهدية العارفين ١: ٧٨٣ ولسان الميزان ٤: ٣٢٧ وكشف الظنون ٥١٩ ومرآة الجنان ٢: ٢٦٨ وإيضاح المكنون ١: ١١٧ والأعلام ٥: ٦٠.','1','400',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (724,'البسطامي [٤٧٥ - ٥٦٢ هـ / ١٠٨٢ - ١١٦٧ م]

<span data-type="title" id=toc-1023>عمر بن محمد بن عبد الله بن محمد، أبو شجاع، ضياء الدين، البسطامي، ثم البلخي:</span>
محدث، مفسر، واعظ أديب، شاعر حاسب، ولد ببلخ وأخذ عن علمائها، ثم رحل فسمع بمرو وهراة وبخارى وسمرقند. روى عنه السمعاني وابنه وغيرهما. ومات ببلخ (١).

السهروردي [٥٣٩ - ٦٣٢ هـ / ١١٤٥ - ١٢٣٤ م]

<span data-type="title" id=toc-1024>عمر بن محمد بن عبد الله بن محمد ابن عبد الله بن عمويه، أبو حفص شهاب الدين القرشي التيمي البكري السهروردي:</span>
فقيه شافعي، مفسر، واعظ، من كبار الصوفية، ولد بسهرورد بمقاطعة الجبل بفارس، وقدم بغداد فكان شيخ الشيوخ بها، وحج
_________
(١) تذكرة الحفاظ ١٣١٨ وطبقات المفسرين للداودي ٢: ٨ وشذرات الذهب ٤: ٢٠٦ وطبقات الشافعية للسبكي ٧: ٢٤٨ والنجوم الزاهرة ٥: ٣٧٦ وهدية العارفين ١: ٧٨٤ ومرآة الزمان ٨: ٣٣٠ والعبر ٤: ١٧٨ ومعجم المؤلفين ٧: ٣١٣.','1','400',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (725,'مرارا وربما جاور في بعض حججه، وأوفده الخليفة إلى عدة جهات رسولا. وأقعد في آخر عمره، فكان يحمل إلى الجامع في محفة. من كتبه "بغية البيان في تفسير القرآن" مخطوط (١).

السكوفي [.. - ٧١٧ هـ / .. - ١٣١٧ م]

<span data-type="title" id=toc-1025>عمر بن محمد بن حمد بن خليل السكوفي، أبو علي:</span>
مقرىء، مفسر، من فقهاء المالكية، من أهل اشبيلية بالأندلس، نزل تونس. من كتبه "التمييز لما أودعه الزمخشري من الاعتزالات في تفسير الكتاب العزيز" مخطوط، قال في كشف الظنون: "تكلم فيه في الإمام فخر الدين وغيره بما لا يعاب به عالم". و "مقتضب التمييز" (٢).
_________
(١) وفيات الأعيان ٣: ٤٤٦ والنجوم الزاهرة ٦: ٢٨٣ والبداية والنهاية ١٣: ١٤٣ وتذكرة الحفاظ ١٤٥٨ ومفتاح السعادة ٢: ٣٥٥ ومرآة الزمان ٨: ٦٧٩ ومرآة الجنان ٤: ٧٩ وذيل الروضتين ١٦٣ وطبقات السبكي ٨: ٣٣٨ وشذرات الذهب ٥: ١٥٣ والعبر ٥: ١٢٩ وطبقات المفسرين ٢: ١٠ والأعلام ٥: ٢٢٣ ومعجم المؤلفين ٧: ٣١٣ وكشف الظنون ١٩٦٥.
(٢) نيل الابتهاج ١٩٥ وهدية العارفين ١: ٧٨٨ والأعلام ٥: ٢٢٤ وكشف الظنون ١٤٨٢.','1','401',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (726,'الأسكوبي [... - ١٠٣٣ هـ / ... - ١٦٢٤ م]

<span data-type="title" id=toc-1026>عمر بن محمد الأسكوبي، الديرهوي، ثم القسطنطيني النقشبندي:</span>
فقيه حنفي، واعظ، مفسر، خلوتي، صوفي، من مشايخ الطرق. وعظ بجامع آيا صوفي١: من آثاره "فتح الغطاء عن وجه العذراء" حاشية على تفسير البيضاوي من سورة الرحمن إلى آخر القرآن. قال البغدادي أولها "الحمد لله الذي كان في أزل الأزل موجودا بوجوده" (١).

القبيباتي [٧٢٤ - ٧٩٢ هـ / ١٣٢٤ - ١٣٩٠ م]

<span data-type="title" id=toc-1027>عمر بن مسلم بن سعيد بن عمر ابن بدر بن مسلم القرشي البلخي الأصل، العينتابي، ثم الدمشقي، أبو حفص، زين الدين، المعروف. بالقبيباتي:</span>
محدث، واعظ، مفسر، من فقهاء الشافعية. ورد دمشق بعد سنة ٧٤٠ هـ فنزل القبيبات فنسب إليها. درس بالمسرورية والناصرية،
_________
(١) هدية العارفين ١: ٧٩٧ وإيضاح المكنون ٢: ١٦٧.','1','401',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (727,'وأفتى، ثم امتحن وسجن، ومات في سجن القلعة. قال ابن حجي: "برع في علم التفسير، وأما علم الحديث فكان حافظا عارفا بالرجال، سمع الكثير من شيوخنا، وله مشاركة في العربية .. " وقال ابن حبيب: "كان عالما كبير القدر بين العلماء، والوجاهة بين الناس، له معرفة تامة في علم التفسير، والحديث النبوي، والمواعظ، واللطائف". كما أثنى عليه شيخ الإسلام ابن حجر العسقلاني (١).

العجمي [... - ٨٠٩ هـ / ... - ١٤٠٦ م]

<span data-type="title" id=toc-1028>عمر بن منصور بن سليمان، سراج الدين القرمي، المعروف بالعجمي:</span>
فقيه حنفي، مفسر، من أهل القاهرة، ولي حسبتها، ودرس الفقه بجامع ابن طولون والتفسير بالمنصورية، كما ولي مشيخة الأيتمشية بباب الوزير. أثنى عليه ابن حجر
_________
(١) طبقات المفسرين ٢: ١١ وذيل تذكرة الحفاظ ٣٦٨ والشذرات ٦: ٣٢٣ والدرر الكامنة ٣: ٢٧١ وإنباء الغمر ١: ٤٠٥ وهو فيه عمر بن سعيد بن عمر ين بدر بن سلم بن سعيد والسلوك ج ٣ ق ٢: ٧٥٧.','1','402',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (728,'العسقلاني والمقريزي والعيني والسخاوي. مات في القاهرة (١).

الواني [١٠٧٤ - ١١٢٦ هـ / ١٦٦٣ - ١٧١٤ م]

<span data-type="title" id=toc-1029>عمر بن نوح الواني، بدر الدين:</span>
فقيه حنفي، مفسر، من أهل وانة، في تركيا الشرقية وولي الإفتاء بها. من آثاره "أنيس الرمس في تفسير آية جري الشمس" {والشمس تجري لمستقر لها ذلك تقدير العزيز العليم} (٢).

الإسكندري [٧٦١ - ٨٤٢ هـ / ١٣٦١ - ١٤٣٨ م]

<span data-type="title" id=toc-1030>عمر بن يوسف بن عبد الله بن محمد بن خلف بن غالي بن محمد بن تميم العفيفي، القبايلي، اللخمي، السكندري، سراج الدين، أبو علي، ويعرف بالبسقلوني، نسبة إلى قرية بسقلون قرب إسكندرية:</span>
فقيه مالكي، عالم بالعربية والتفسير والفرائض، له نظم كثير. ولد
_________
(١) انباء الغمر ٢: ٨٠٩ والضوء اللامع ٦: ١٣٩ والسلوك ٤/: ٤٩.
(٢) هدية العارفين ١: ٧٩٨ وإيضاح المكنون ١: ١٤٩.','1','402',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (729,'بالإسكندرية، وبها نشأ وتعلم. وصفه البقاعي بالعلامة الثقة الضابط .. وقال السخاوي: "كان حيا سنة ٨٤٤، ورأيت ابن عزم أرخ وفاته سنة ٨٤٢ ووصفه بشيخنا .. ". من آثاره " تفسير الفاتحة" و "تفسير" من أول سورة النبأ إلى آخر القرآن، في مجلد، سماه بعضهم "سراج الإغراب في التفسير والمعاني والبيان" قال السخاوي: شحنه فوائد وأجاد فيه .. " (١).

الزيني [... - بعد ٩٠٠ هـ / ... - بعد ١٤٩٦ م]

<span data-type="title" id=toc-1031>عمر بن يونس بن عمر الزيني العمري:</span>
فاضل مصري، له اشتغال بالتفسير، كان والده من الأعيان. قال السخاوي: "شاب حسن الشكالة، كتب الخط الحسن. تردد إليه الزين قاسم الحنفي لإقرائه، وأعانه على تفسير سورة الكهف، وسيرته ذميمة وفاقته متجددة". له "إغاثة اللهف في تفسير سورة الكهف" و "مطالع
_________
(١) الضوء اللامع ٦: ١٤٢ ونيل الابتهاج ١٩٦ وكشف الظنون ٣٦٦ وهدية العارفين ١: ٧٩٢ ومعجم المؤلفين ٨: ٥.','1','403',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (730,'الكشف لمطالع اللهف" مختصر الإغاثة (١).

السلاوي [.. - حيا ٦٤٠ هـ / ... - ١٢٤٣ م]

<span data-type="title" id=toc-1032>عمران بن موسى بن ميمون الهواري السلاوي، أبو موسى:</span>
مفسر، حافظ، أديب، نحوي من أهل سلا بالمغرب، تعلم بها وبالأندلس. قال ابن الزبير: "أقرأ العربية بغرناطة وكان يعرف بها بالسلاوي". مات بسلا بعد رجوعه إليها من الأندلس بعد ربيع الآخر سنة ٦٤٠ هـ (٢)

الجاحظ [١٦٣ - ٢٥٥ هـ / ٧٨٠ - ٨٦٩ م]

<span data-type="title" id=toc-1033>عمرو بن بحر بن محبوب الكناني بالولاء، الليثي، أبو عثمان، الشهير بالجاحظ:</span>
من أئمة الأدب العربي، ورئيس الفرقة الجاحظية من المعتزلة. من أهل البصرة مولدا ووفاة، وتعلم
_________
(١) كشف الظنون ١٢٩ و ٤٣٤ و ١٧١٨ وهدية العارفين ١: ٧٩٦ والضوء اللامع ٦: ١٤٤.
(٢) طبقات المفسرين ٢: ١٨ وبغية الوعاة ٢: ٢٣٣ والصلة ١٦٦.','1','403',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (731,'بها وببغداد، فنبه ذكره في علوم الأدب واللغة، وأحاط بمعارف عصره، فلم يترك موضوعا إجتماعيا أو ثقافيا أو أدبيا إلا كتب فيه، فصور أحوال عصره وحياة أهله زمانه وأخلاقهم وعاداتهم تصويرا يمتزج فيه الجد بالدعابة. وتقرب من الخلفاء والوزراء إلى أن ولي المتوكل العباسي فتنكر للمعتزلة وللقائلين بها فتوارى الجاحظ وعاد إلى البصرة ولازم منزله الذي أصبح مثوى الأدب ومحط رجاله. وفلج في آخر عمره، ومات والكتاب على صدره، قتلته مجلدات من الكتب وقعت عليه. وكان دميما، قبيحا، قصيرا، لقب بالجاحظ لنتوء عينيه وبروزهما. قال الذهبي: "كان من أئمة البدع". وقال ثعلب: "ليس بثقة ولا مأمون" وقال ابن قتيبة في اختلاف الحديث: "ثم نصير إلى الجاحظ وهو أحسنهم للحجة استنارة، وأشدهم تلطفا لتعظيم الصغير حتى يعظم، وتصغير العظيم حتى يصغر، ويكمل الشيء وينقصه، فنجده مرة يحتج للعثمانية على الرافضة، ومرة للزيدية على أهله السنة، ومرة يفضل عليا، ومرة يؤخره، ويقول: "قال رسول الله (صلى الله عليه وسلم) كذا.". من','1','404',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (732,'كتبه الكثيرة "نظم القرآن" و "آي القرآن" و "معاني القرآن" و "المسائل في القرآن" (١).

عمرو بن عبيد [٨٠ - ١٤٤ هـ / ٦٩٩ - ٧٦١ م]

<span data-type="title" id=toc-1034>عمرو بن عبيد بن باب، التيمي بالولاء، أبو عثمان البصري:</span>
شيخ المعتزلة في عصره، ومفتيها، من الزهاد المشهورين. ولد في بلخ، وكان جده "باب" قد جلب إليها أسيرا في سبي فارس. وأبوه نساجا ثم شرطيا للحجاج في البصرة. تتلمذ
_________
(١) طبقات المفسرين ٢: ١٣ وتاريخ بغداد ١٢: ٢١٣ وتذكرة الحفاظ ٥٤١ وميزان الاعتدال ٣: ٢٤٧ ولسان الميزان ٤: ٣٥٥ ومعجم الأدباء ١٦: ٧٤ ووفيات الأعيان ٣: ٤٧٠ ونزهة الألباء ١٩٢ وبغية الوعاة ٢: ٢٨٢ وطبقات المعتزلة ٦٧ ومرآة الجنان ٢: ١٥٦ وروضات الجنات ٥٠٣ ومروج الذهب ٤: ١٩٥ وابن كثير ١١: ١٩ واللباب ١: ٢٠٢ وسرح العيون ١٣٦ وأمالي المرتضى ١: ١٩٤ وأمراء البيان ٣١١ والجاحظ لفؤاد أفرام البستاني، والجاحظ لشفيق جبري، وأدب الجاحظ للسندوبي، والجاحظ لطاهر الكيالي، والجاحظ لخليل مردم، والأنساب ١١٨ والفرق بين الفرق ١٦٠ ومقدمة كتاب التاج بقلم أحمد زكي باشا، والأعلام ٥: ٢٣٩ وهدية العارفين ١: ٨٠٢ ولمحات في الأدب العربي ١٢٧ وتاريخ آداب اللغة العربية ٢: ١٦٧ ودائرة المعارف للبستاني ومصادر الدراسة الأدبية ١: ١٣٧ ومعجم المؤلفين ٨: ٧.','1','404',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (733,'عمرو أول الأمر على الحسن البصري (راجع ترجمته في هذا المعجم) وكان تقيا ورعا، من المحدثين، ثم انفصل عن أصحاب الحسن مع واصل بن عطاء، وتأثر كثيرا بآراء واصل، وانفرد بميله إلى الخليفة عثمان، وكان يفضل أبا بكر الصديق على علي بن أبي طالب. وعلى الرغم من موقفه غير "العلوي" فقد كان صديقا حميما للخليفة المنصور العباسي وأخباره كثيرة معه. وفي العلماء من يراه مبتدعا، قال يحيى بن معين: "كان من الدهرية الذين يقولون إنما الناس مثل الزرع". وقال أحمد بن حنبل: "ما كان عمرو بن عبيد بأهل أن يحدث عنه". توفي ب"مران" بقرب مكة، ورثاه المنصور العباسي، ولم يسمع بخليفة رثى من دونه، سواه. له رسائل وكتب منها "تفسير القرآن" كتبه عن الحسن البصري، قال فؤاد سزكين: "وقد وصل إلينا (التفسير) في روايات كثيرة في كتب التفسير" (١).
_________
(١) العثمانية ٢٦٥ والفهرست ٢٠٣ وأخبار أصبهان ٢: ٣٣ وميزان الاعتدال ٣: ٢٧٣ وتاريخ بغداد ١٢: ١٦٦ والحور العين ١١٠ والعبر ١: ١٩٣ والبداية والنهاية ١٠: ٧٨ ووفيات الأعيان ٣: =','1','405',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (734,'الفلاس [... - ٢٤٩ هـ / .. - ٨٦٤ م]

<span data-type="title" id=toc-1035>عمرو بن علي بن بحر بن كنيز، أبو حفص الباهلي، الصيرفي الفلاس:</span>
حافظ للحديث، مفسر، له اشتغال بالتاريخ: من أهل البصرة، روى عن سفيان بن عيينة ويحيى القطان وأبي داود الطيالسي وطبقتهم، وروى عنه الأئمة الستة والنسائي أيضا بواسطة، وأبو زرعة ومحمد بن جرير الطبري وغيرهم. سكن بغداد، وتردد إلى أصبهان مرات، ومات بسر من رأى (سامراء). قال أبو حاتم: "كان أرشق من علي بن المديني، وهو بصري صدوق" وقال النسائي: "ثقة، صاحب حديث، حافظ" وذكره الدارقطني وقال: "كان من الحفاظ، وبعض رجال الحديث يفضلونه على ابن المديني ويتعصبون له". له "تفسير القرآن" رواه عنه علي بن إسماعيل بن حماد (١).
_________
= ٤٦٦ والبيان والتبيين ١: ١٤ والمعتزلة ٣٥ وتاريخ التراث العربي ٢: ٣٦١ والتفسير والمفسرون ١: ١٤٤ وهدية العارفين ١: ٨٠٢ وشرح نهج البلاغة ١: ٧ ومروج الذهب ٤: ٢٢ والأعلام ٥: ٢٥٢.
(١) طبقات المفسرين للداودي ٢: ١٧ وتهذيب التهذيب ٨: ٨٠ وتذكرة الحفاظ ٢: ٤٨٧ واللباب =','1','405',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (735,'الكوفي [؟ -؟ هـ /؟ -؟ م]

<span data-type="title" id=toc-1036>عمرو بن هشيم الكوفي:</span>
له كتاب "فضائلا القرآن". ذكره ابن النديم، ولم يزد على ذلك (١).

آخوند [... - ١١٧٦ هـ / ... - ١٧٦٢ م]

<span data-type="title" id=toc-1037>عناية الله بن عبد الله الوابكي البخاري الحنفي الشهير بآخوند:</span>
فقيه، مدرس، عارف بالتفسير والحكمة، من آثاره "حاشية" على تفسير سورة البقرة، للبيضاوي، و "حاشية" على جزء النبأ منه، كما في إيضاح المكنون. وقال آغا بزرك: له "حاشية" عليه وعلى حاشية قاسم الحنفي (المتوفى سنة ٨٧٩ هـ) عليه. نسختان منها في كتب مدرسة سبهسالار (٢). ٢: ٢٣٠ والشذرات ٢: ١٢٠ وتبصير المنتبه ٣: ١١٨٨ والأعلام ٥: ٢٥٤ وتاريخ بغداد ١٢:
_________
(١) الفهرست لابن النديم (طبعة فلوجل) ٣٧ وهو فيه: عمرو بن هشيم، وطبعة رضا - تجدد (بطهران) ٣٩ هو: عمر بن هيثم. وطبقات المفسرين ١: ١٨ نقلا عن الفهرست.
(٢) هدية العارفين ١: ٨٠٤ وإيضاح المكنون ١: ١٤١ وتعليقات آغا برزك ٢٠.','1','406',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (736,'العلائيه وي [.. - ٩٩٤ هـ / .. - ١٥٨٥ م]

<span data-type="title" id=toc-1038>عوض بن عبد الله العلائيه وي المنوغادي:</span>
فقيه حنفي، تركي الأصل، مستعرب، من القضاة، ولي قضاء الجيش بالروم إيلي. من كتبه "حاشية" على "أنوار التنزيل" في التفسير للبيضاوي (١).

القاضي عياض [٤٧٦ - ٥٤٤ هـ / ١٠٨٣ - ١١٤٩ م]

<span data-type="title" id=toc-1039>عياض بن موسى بن عياض بن عمرون اليحصبي السبتي، أبو الفضل:</span>
إمام وقته في الحديث وعلومه، عالم بالتفسير والفقه واللغة والنحو وكلام العرب، وأنسابهم وأيامهم، أندلسي الأصل، تحول جده إلى فاس، ثم سكن مدينة سبتة، وولد صاحب الترجمة بها. ورحل إلى الأندلس وأخذ عن علمائها. وولي قضاء سبتة، ثم قضاء غرناطة (سنة ٥٣١ هـ) ولم يطل أمده بها، ثم قضاء سبتة ثانيا، وتوفي بمراكش. وللمقري كتاب في سيرته وأخباره سماه
_________
(١) هدية العارفين ١: ٨٠٤','1','406',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (737,'"أزهار الرياض في أخبار القاضي عياض" (١).

الخوشناوي [... - بعد ١٢٠٠ هـ / ... - بعد ١٧٨٦ م]

<span data-type="title" id=toc-1040>عيسى بن أحمد بن ميكائيل الخوشناوي الكردي السهراني:</span>
فقيه شافعي، مفسر، تولى الإفتاء والتدريس بكركوك (بالعراق). من تصانيفه "تفسير القرآن" من سورة مريم إلى آخر القرآن، و "تفسير سورة الفتح" ألفه للوزير سليمان باشا والي بغداد، و "تفسير سورة الإخلاص" بالتركية (٢).
_________
(١) طبقات المفسرين للداودي ٢: ١٨ وتذكرة الحفاظ ٤: ١٣٠٤ وتاريخ قضاة الأندلس ١٠١ وبغية الملتمس ٤٢٥ والصلة ٢: ٤٢٩ والمعجم لابن الأبار ٢٩٤ والديباج ١٦٨ وجذوة الاقتباس ٢٧٧ وقلائد العقيان ٢٢٢ والشذرات ٤: ١٣٨ وأزهار الرياض ١: ٢٣ وإنباه الرواة ٢: ٣٦٣ والنجوم الزاهرة ٥: ٢٨٥ ومفتاح السعادة ٢: ١٤٩ والعبر ٤: ١٢٢ وروضات الجنات ٥٠٦ وتهذيب الأسماء واللغات ٢: ٤٣ والرسالة المستطرفة ١٠٦ وفهرس الفهارس ٢: ١٨٣ والبداية والنهاية ١٢: ٢٢٥ وهدية العارفين ١: ٨٠٥ والمختصر في أخبار البشر ٣: ٢٣ والتكملة ٦٩٤ وابن خلكان ٣: ٤٨٣ وتاج العروس (حصب) والأعلام ٥: ٢٨٢ ومعجم المؤلفين ٨: ١٦١ ومقدمة ترتيب المدارك.
(٢) هدية العارفين ١: ٨١٢ وإيضاح المكنون ١: ٣٠٦ و ٣٠٧ و ٣٠٩.','1','407',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (738,'السكتاني [... - ١٠٦٢ هـ / ... - ١٦٥٢ م]

<span data-type="title" id=toc-1041>عيسى بن عبد الرحمن السكتاني، أبو مهدي:</span>
فقيه مالكي، قاض، تفوق في فقه المالكية والتفسير، من أهل مراكش مولدا ووفاة، كان مفتيها وقاضيها وعالمها في عصره. وصنف كتبا (١).

الإسكندراني [٥٥٠ - ٦٢٩ هـ / ١١٥٥ - ١٢٣٢ م]

<span data-type="title" id=toc-1042>عيسى بن عبد العزيز بن عيسى بن عبد الواحد بن سليمان اللخمي الشريشي الأصل، ثم الإسكندراني، موفق الدين، أبو القاسم:</span>
مقرىء، محدث، نحوي، مفسر، لغوي، من أهل الإسكندرية وبها توفي. قال ابن حجر: "سماعاته للحديث من السلفي وغيره صحيحة، فأما في القراءات فليس بثقة ولا مأمون، وضع أسانيد وادعى أشياء لا وجود لها، وهاه غير واحد .. ". من آثاره "بيان مشتبه القرآن" و "تدريج أهل
_________
(١) خلاصة الأثر ٣: ٢٣٥ وهدية العارفين ١: ٨١١ والسعادة اأابدية ١٠٨ ونشر المثاني ١: ٢٠١ ومعجم المؤلفين ٨: ٢٦ والأعلام ٥: ٢٨٨.','1','407',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (739,'البدايات" في التفسير، مخطوط، الجزء الخامس منه (١).

الثقفي [... - ١٤٩ هـ / ... - ٧٦٦ م]

<span data-type="title" id=toc-1043>عيسى بن عمر الثقفي البصري:</span>
نحوي، مقرىء، من أهل البصرة. وهو شيخ الخليل وسيبويه وابن العلاء، وأول من هذب النحو ورتبه، وعلى طريقته مشى سيبويه وأشباهه. وكان صاحب تقعير في كلامه واستعمال للغريب فيه. ولم يكن ثقفيا وإنما نزل في ثقيف فنسب إليهم. وسلفه من موالي خالد بن الوليد. قال صاحب "تاريخ التراث العربي": وأقدم علماء اللغة الذين نعرفهم، ونعرف تفسيراتهم النحوية للقرآن معرفة تكاد تكون دقيقة، هم: عيسى بن عمرو الثقفي و ... ولكن لم تصل إلينا أعمالهم العلمية - للأسف - وإنما نعرف أعمال تلاميذهم" (٢).
_________
(١) بغية الوعاة ٢: ٢٣٥ وغاية النهاية ١: ٦٠٩ وهدية العارفين ١: ٨٠٨ وروضات الجنات ٥٠٨ ومعجم المؤلفين ٨: ٢٧ ولسان الميزان ٤: ٤٠١.
(٢) تاريخ التراث العربي ١: ١٧٨ ومرآة الجنان ١: ٣٠٧ والشذرات ١: ٢٢٤ وغاية النهاية =','1','408',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (740,'الصفوي [٩٠٠ - ٩٥٣ هـ / ١٤٩٥ - ١٥٤٦ م]

<span data-type="title" id=toc-1044>عيسى بن محمد بن عبيد الله بن محمد، أبو الخير، قطب الدين الحسني الحسيني الإيجي، المعروف بالصفوي:</span>
فقيه شافعي، متصوف، منطقي، مفسر، هندي الموطن، أخذ عن أبيه وغيره من علماء كجرات، ثم رحل إلى دلي وحضر مجالس علمائها. وأكرمه السلطان إبراهيم بن إسكندر شاه. وحج، وجاور بمكة سنين. وزار بيت المقدس وبلاد الروم فأكرمه السلطان سليمان وأنعم عليه. ثم استوطن مصر. قال صاحب شذرات الذهب: "كان من أعاجيب الزمان". من كتبه "تفسير" من سورة عم إلى آخر القرآن، و "حاشية" على تفسير سورة الفاتحة للبيضاوي (١).
_________
= ومعجم الأدباء ١٦: ١٤٦ والنجوم الزاهرة ٢: ١١ ونزهة الألباء ٢٥ وابن خلكان ٣: ٤٨٦ ونور القبس ٤٦ وصبح الأعشى ٢: وطبقات النحويين للزبيدي ٤٠ وإنباه الرواة ٢: ٣٧٤ والبداية والنهاية ١٠: ١٠٥ وروضات الجنات ٥٥٧ والأعلام ٥: ٢٩١ وبغية الوعاة ٢: ٢٣٧.
(١) هدية العارفين ١: ٨١ والشذرات ٨: ٢٩٧ والأعلام ٥: ٢٩٤ ومعجم المؤلفين ٨: ٣٢. وقيل في وفاته سنة ٩٥٥ هـ.','1','408',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (741,'ابن داية [... - نحو ١٣٠ هـ / ... - ٧٤٨ م]

<span data-type="title" id=toc-1045>عيسى بن ميمون الجرشي المكي، أبو موسى، المعروف بابن داية:</span>
محدث، مفسر، روى عن مجاهد وابن أبي نجيح وغيرما، وعنه سفيان الثوري وسفيان ابن عيينة وكيسان. وثقة جماعة منهم ابن حبان، وقال الأجري: "ثقة إلا أنه يرى القدر". له "تفسير القرآن" قال صاحب هدية العارفين: تفسير صغير (١).
_________
(١) هدية العارفين ١: ٨٠٥ وتهذيب التهذيب ٨: ٢٣٥ والإكمال لابن ماكولا ٢: ٢٣٦ الحاشية.','1','409',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (742,'<span data-type="title" id=toc-1046>حرف الغين</span>
الغافقي = عبد الكبير بن محمد ٦١٧ هـ
الغافقي = محمد بن أيوب ... ٦٠٨ هـ
ابن غالبون = أبو بكر بن غالبون ... حيا ١٢٣٦ هـ
غرس الدين ابن النقيب = خليل بن أحمد ... ٩٧١ هـ
الغزالي = أحمد بن محمد ٥٢٠/ هـ
الغزالي (حجة الإسلام) = محمد بن محمد ٥٠٥ هـ
الغزنوي = أحمدبن إسماعيل ... بعد ٥٢٠ هـ
الغزنوي = أحمشاد بن عبد السلام ... ٥٥٢ هـ
الغزنوي = علي بن إبراهيم ... ٥٨٢ هـ
