INSERT INTO [b13846] VALUES (748,'(ف)

الزنجاني [١٢٦٨ - ١٣٣٨ هـ / ١٨٥٢ - ١٩٢٠ م]

<span data-type="title" id=toc-1050>فتح علي بن ولي بن علي عسكر الزنجاني:</span>
فقيه إمامي، مفسر، مشارك في بعض العلوم، نسبته إلى زنجان، مدينة في إيران الشمالية، من مؤلفاته "مجمع الأنوار ومعدن الأسرار" في تفسير القرآن الكريم (١)

الكاشاني [... - ٩٨٨ هـ / ... - ١٥٨٠ م]

<span data-type="title" id=toc-1051>فتح الله بن شكر الله الكاشاني:</span>
مفسر، محدث، له اشتغال بالتاريخ،
_________
(١) معجم المؤلفين ٨: ٤٨','1','417',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (749,'من فقهاء الشيعة الإمامية. نسبته إلى كاشان أو قاشان في وسط إيران. من آثاره "زبدة التفاسير" في مجلدين، و "منهج الصادقين في إلزام المخالفين في تفسير القرآن المبين" منظومة باللغة الفارسية عدد أبياتها سبعة عشر ألف بيت، في خمس مجلدات، واختصرها في مجلدين وسماها "خلاصة المنهج" (١).

البيلوني [٩٧٧ - ١٠٤٢ هـ / ١٥٧٠ - ١٦٣٢ م]

<span data-type="title" id=toc-1052>فتح الله، ويقال محمد فتح الله، بن محمود بن محمد بن الحسن العمري (الأنصاري البيلوني)</span>
_________
(١) أعيان الشيعة ٤٢: ٢٦١ وفوائد الرضوية ٣٤٥ وهدية العارفين ١: ٨١٥ وإيضاح المكنون ١: ٤٣٧ و ٢: ٥٩٢ وروضات الجنات ٥٠٨ ومعجم المؤلفين ٨: ٥١.','1','417',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (750,'الأنصاري البيلوني: أديب، شاعر، عارف بالتفسير، من أهل حلب مولدا ووفاة. نقل ابن معصوم في كتابه "سلافة العصر" نماذج من شعره. من تصانيفه "حاشية على تفسير البيضاوي" (١).

الشرواني [... - حيا ٨٨٠ هـ / .. - ١٤٧٥ م]

<span data-type="title" id=toc-1053>فتح الله بن أبي يزيد بن عبد العزيز بن إبراهيم الشرواني:</span>
نحوي، عارف بالتفسير، من فقهاء الشافعية. قال السخاوي: "حج بعد ٨٧٠ هـ، ودخل القاهرة في رجوعه، وهو إلى بعد الثمانين في قيد الحياة". من آثاره "تفسير آية الكرسي" (٢).

الطريحي [... - ١٠٨٥ هـ / ... - ١٦٧٤ م]

<span data-type="title" id=toc-1054>فخر الدين بن محمد بن علي بن أحمد ابن طريح الرماحي الطريحي:</span>
فقيه
_________
(١) إعلام النبلاء ٦: ٢٣٩ وفهرس الفهارس ٢: ٢٦٣ والسلافة ٣٩٨ وهدية العارفين ١: ٨١٥ وإيضاح المكنون ١: ١٤٠ والأعلام ٥: ٣٣٤ ومعجم المؤلفين ٨: ٥٣ وخلاصة الأثر ٣: ٢٥٤.
(٢) الضوء اللامع ٦: ١٦٦.','1','418',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (751,'إمامي، مفسر، محدث، لغوي، مؤرخ، من أهل النجف وبها نشأ وتعلم، وتوفي بالرماحية. من تصانيفه الكثيرة "مجمع البحرين ومطلع النهرين" في تفسير غريب القرآن والحديث طبع، و "كشف غوامض الفرآن" و "نزهة الخاطر وسرور الناظر" مخطوط في بيان لغات القرآن، و "مشارق النور للكتاب المشهور" في تفسير القرآن، وغير ذلك (١).

الكوفي [... - نحو ٣١٠ هـ / ... - نحو ٩٢٢ م]

<span data-type="title" id=toc-1055>فرات بن إبراهيم بن فرات الكوفي:</span>
فقيه إمامي، مفسر، من أهل الكوفة. من آثاره "تفسير القرآن" مخطوط، بمدينة مشهد (٢).

الخطيب [١٢٤٤ - ١٣١١ هـ / ١٨٢٨ - ١٨٩٣ م]

<span data-type="title" id=toc-1056>أبو الفرج (محمد) بن عبد القادر ابن صالح بن عبد الرحيم الخطيب:</span>
_________
(١) مجلة المجمع العلمي العربي ٢٢: ٥٠٣ وأعيان الشيعة ٤٢: ٢٦٥ وروضات الجنات ٥١٠ وفوائد الرضوية ٣٤٨ والأعلام ٥: ٣٣٨ ومعجم المؤلفين ٨: ٥٦ وهدية العارفين ١: ٤٣٢ وهو فيه فخر الدين، طريح بن محمد.
(٢) تاريخ التراث العربي ٢: ٢٥٩ والذريعة ٤: ٢٩٨.','1','418',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (752,'مفسر، محدث، من كبار الشافعية في عصره. مولده ووفاته بدمشق. أجيز من بعض علماء الحجاز ومصر، وتصدر للتدريس في الجامع الأموي. من تصانيفه "التنزيل وأسرار التأويل" في التفسير، في ثلاثين مجلدا (١).

الواسطي [٣٥٥ - ٤٣٦ هـ / ٩٦٦ - ١٠٤٤ م]

<span data-type="title" id=toc-1057>فرج بن عمر بن الحسن بن أحمد، أبو الفتح الواسطي، ويقال البصري:</span>
مقرىء حاذق، مفسر، تعلم بواسط وبالجامدة - من أعمال واسط - ثم ببغداد، وسكنها ومات بها، قال أبو طاهر بن سوار: قرأت عليه في منزله بدرب الناووس سنة أربع وثلاثين وأربعمائة، وكان من الأبدال" وأثنى عليه ابن الجزري (٢).

ابن لب [٧٠١ - ٧٨٣ هـ / ١٣٠٢ - ١٣٨١ م]

<span data-type="title" id=toc-1058>فرج بن قاسم بن أحمد بن لب - وقيل ليث - أبو سعيد التغلبي (الغرناطي)</span>
_________
(١) أعيان دمشق ٣٥٧ ومنتخبات التواريخ ٧٠٣ والأعلام ٥: ٢١٢ وهو فيه محمد (أبو الفرج).
(٢) غاية النهاية ٢: ٧ وطبقات المفسرين للداودي ٢: ٢٥ وتاريخ بغداد ١٢: ٣٩٧ ونكت الهميان ٢٢٧','1','419',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (753,'الغرناطي: نحوي، لغوي، مفسر، من كبار علماء المالكية في عصره، انتهت إليه رياسة الفتوى بالأندلس. من أهل غرناطة، وولي الخطابة بجامعها الكبير. قال في الإحاطة: "كان عارفا بالعربية واللغة، مبرزا في التفسير، قائما على القراءات، مشاركا في الأصلين والفرائض والأدب، معظما عند الخاصة والعامة" (١).

الحويزي [١٠٣١ - نحو ١١٠٠ هـ / ١٦٢٢ - نحو ١٦٨٩]

<span data-type="title" id=toc-1059>فرج الله بن محمد بن درويش بن محمد بن حسين الحويزي:</span>
مؤرخ، أديب، شاعر، مفسر، من علماء الإمامية. نسبته إلى حويزة بين البصرة وخوزستان. من تآليفه "تفسير القرآن" (٢).
_________
(١) إنباء الغمر ١: ٣٤٩ وطبقات المفسرين ٢: ٢٥ وبغية الوعاة ٢: ٢٤٣ والشذرات ٦: ٢٨٠ والكتيبة الكامنة ٦٧ والديباج ٢٢٠ وغرة الحجال ٢: ٤٥٣ ونيل الابتهاج ٢١٩ وهدية العارفين ١: ٨١٦
(٢) الذريعة ٢: ٤٨٧ و ٤: ٤١ وروضات الجنات ٥١١ وأعيان الشيعة ٤٢: ٢٦٨ وفوائد الرضوية ٣٤٩ وهدية العارفين ١: ٨١٦.','1','419',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (754,'الجرجاني [... - حيا ٤٥٨ هـ / ... - حيا ١٠٦٦ م]

<span data-type="title" id=toc-1060>الفضل بن إسماعيل التميمي الجرجاني، أبو عامر:</span>
نحوي، مفسر، أديب، ناظم، من الكتاب، من أهل جرجان. أخد عن علمائها، وصحب عبد القاهر الجرجاني واضع أصول البلاغة المتوفى سنة ٤٧١ هـ. وكتب مدة للشيخ الرئيس أبي المحاسن الجرجاني وغيره. ورحل إلى نيسابور وغزنة (سنة ٤٥٨ هـ). ترجم له عبد الغافر الفارسي (٤٥١ - ٥٢٩ هـ) في كتابه "السياق" وأثنى عليه ولم يذكر تاريخ وفاته، قال ياقوت: "لكنه كان قد مات في حياة عبد الغافر". من كتبه "البيان في علم القرآن" (١).

الطبرسي [... - ٥٤٨ هـ / ... - ١١٥٣ م]

<span data-type="title" id=toc-1061>الفضل بن الحسن بن الفضل الطبرسي، أمين الدين، أبو علي:</span>
مفسر، لغوي، من كبار علماء الشيعة الإمامية. نسبته إلى طبرستان. من
_________
(١) طبقات المفسرين للداودي ٢: ٢٨ ومعجم الأدباء ١٦: ١٩٢ وبغية الوعاة ٢: ٢٤٥ وهدية العارفين ١: ٨١٩ وكشف الظنون ٢٦٣.','1','420',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (755,'آثاره "مجمع البيان في تفسير القرآن والفرقان" طبع في بيروت، في ست مجلدات. و "الوسيط" في أربع مجلدات، و "الوجيز" في مجلد، و "الوافي" و "جامع الجوامع" أو "جوامع الجامع" و "الكاف الشاف من كتاب الكشاف" صنفه بعد "مجمع البيان" وبعد أن اطلع على "الكشاف" ليكون جامعا بين فوائد الكتابين بوجه الاختصار كما صرح به في مقدمته. وفي رأي بعض علماء الشيعة الإمامية أن كتاب "الكاف الشاف" هو "جامع الجوامع" وكل هذه الكتب في التفسير (١).

المروزي [... - ٢١١ / .. - ٨٢٦ م]

<span data-type="title" id=toc-1062>الفضل بن خالد المروزي، أبو معاذ، مولى باهلة:</span>
نحوي، مقرىء، عارف بعلوم القرآن، من أهل مرو.
_________
(١) أمل الآمل ٢: ٢١٦ وأعيان الشيعة ٤٢: ٢٧٦ و ٣٠٩ وروضات الجنات ١٢. وتنقيح المقال ٢: ٧ ومقدمة مجمع البيان طبعة دار الحياة بيروت وهدية العارفين ١: ٨٢٠ والذريعة ٢: ٢٤٠ وفوائد الرضوية ٣٥٠ والتفسير والمفسرون ٢: ١٠٤ والقرآن والتفسير ١٨٦ وسفينة البحار ٢: ٨٠ وكشف الظنون ١٦٠٢ والأعلام ٥: ٣٥٢.','1','420',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (756,'روى عن ابن المبارك وغيره، وعنه الأزهري، وأكثر عنه في "التهذيب". وذكره ابن حبان في "الثقات". قال الأزهري: "له كتاب في القرآن حسن" (١).

ابن دكين [١٣٠ - ٢١٩ هـ / ٧٤٨ - ٨٣٤ م]

<span data-type="title" id=toc-1063>الفضل بن دكين (عمرو) بن حماد ابن زهير التيمي بالولاء، الملائي، أبو نعيم:</span>
محدث حافظ، عارف بالتفسير، من شيوخ البخاري ومسلم، من أهل الكوفة. وكان إماميا، وإليه نسبة الطائفة الدكينية. روى عنه أحمد ابن حنبل والبخاري وجماعة. وفي أيامه امتحن المأمون العباسي الناس في مسألة القول بخلق القرآن، ودعاه والي الكوفة، فسأله، فقال: "أدركت الكوفة وبها أكثر من سبعمائة شيخ، الأعمش فمن دونه، يقولون القرآن كلام الله، وعنقي أهون عندي من
_________
(١) طبقات المفسرين ٢: ٢٨ ومعجم الأدباء ٦: ٢١٤ وغاية النهاية ٢: ٩ وبغية الوعاة ٢: ٢٤٥ ومعجم المؤلفين ٨: ٦٧ وهدية العارفين ١: ٨١٨ وفيه: له كتاب في القراءات، ومثله في كشف الظنون ١٤٤٩.','1','421',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (757,'زري هذا". قال الداودي: له "تفسير" (١).

ابن شاذان [... - ٢٦٠ هـ / ... - ٨٧٤ م]

<span data-type="title" id=toc-1064>الفضل بن شاذان بن الخليل الأزدي النيسابوري، أبو محمد:</span>
فقيه إمامي، عالم بالكلام، مشارك في التفسير والقراءات والفرائض وغيرها. له نحو ١٨٠ كتابا، منها كتاب في "التفسير" (٢).

رشيد الدولة [... - ٧١٦ هـ / ... - ١٣١٦ م]

<span data-type="title" id=toc-1065>فضل الله (رشيد الدولة، أو رشيد الدين) بن أبي الخير (عماد الدولة) بن (علي (موفق الدولة) أبو الفضل الهمذاني)</span>
_________
(١) طبقات المفسرين للداودي ٢: ٢٩ وتذكرة الحفاظ ١: ٣٧٢ والتاريخ الكبير للبخاري ٤: ١١٨ وتاريخ بغداد ١٢: ٣٤٦ وشذرات الذهب ٢: ٤٦ وميزان الاعتدال ٣: ٣٥ وأعيان الشيعة ٤٢: ٢٧٥ والرسالة المستطرفة ٤٦ والعبر ١: ٣٧٧ وخلاصة تهذيب الكمال ٢٦٢ والفهرست لابن النديم ٢٨٣ وهدية العارفين ١: ٨١٨ والأعلام ٥: ٣٥٣ ومعجم المؤلفين ٨: ٦٧.
(٢) هدية العارفين: ١: ١٧ ومنهج المقال ٢٦٠ وكتاب الرجال ٢١٦ وطبقات المفسرين ٢: ٣٠ والذريعة ٢: ٥١٠ والطوسي ١٢٤ وابن النديم ٢٨٧ والأعلام ٥: ٣٥٥ ومعجم المؤلفين ٨: ٦٩.','1','421',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (758,'علي (موفق الدولة) أبو الفضل الهمذاني: من الوزراء العلماء في دولة الايلخانيين، له اشتغال بالفلسفة والطب والتاريخ. اتصل بملك التتار محمود غازان (٦٩٤ - ٧٥٣ هـ) وتقدم عنده بالطب فاستوزره. ثم ولي الوزارة لأخيه أولجاتيو خدابنده محمد (٧٠٣ - ٧١٦ هـ) من بعده. وبنى في تبريز عددا من الخوانك - جمع خانكاه - والمدارس. ومرض خدابنده، فاشترك رشيد الدولة في علاجه، فمات (٧١٦ هـ). وتولى الحكم بعده أبو سعيد بهادر (٧١٦ - ٧٣٦ هـ) فعزل رشيد الدولة من الوزارة، ثم استدعاه الأمير جوبان واتهمه بأنه كان سبب موت خدابنده، فقتلوه وفصلت أعضاؤه وأرسل إلى كل بلد عضو منها، وحمل رأسه إلى تبريز ونودي عليه: "هذا رأس اليهودي الملحد. وقالوا إن أباه كان يهوديا عطارا، وأن رشيد الدولة أسلم قبل أن يتصل بمحمود غازان. وقد أحرقت كتبه بعد قتله. قال ابن حجر: "كان يناصح المسلمين ويذب عنهم ويسعى في حقن دمائهم" وقال الذهبي: "كان له رأي ودهاء ومروءة". صنف "مفتاح التفاسير" مخطوط، في تفسير القرآن على طريقة','1','422',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (759,'الفلاسفة، فنسب إلى الإلحاد. قال البغدادي: "أوله (الحمد والشكر والثناء واجب لله .. ". وقد وضعه مقدمة لتفسير له يعرف بالتفسير الرشيدي (١).

الراوندي [... - ٥٧٠ هـ / ... - ١١٧٤ م]

<span data-type="title" id=toc-1066>فضل الله بن علي بن عبيد الله الحسيني الراوندي الكاشاني، أبو الرضا، ضياء الدين:</span>
من علماء الشيعة الإمامية، نسبته إلى راونده، من قرى كاشان. قال الحر العاملي: "جمع مع علو النسب كمال الفضل والحسب، وكان أستاذ أئمة عصره، روى عن أبي علي الطوسي". له تصانيف، منها "الكافي" في تفسير القرآن" (٢).
_________
(١) طبقات المفسرين ٢: ٣٠ والشذرات ٦: ٤٤ وفيه مقتله سنة ٧١٧ وهدية العارفين ١: ٨٢١ وهو فيه فضل الله بن أبي الخير يحيى ومقتله سنة ٧١٦ وقيل سنة ٧١٨ والدرر الكامنة ٣: ٣١٤ وهو فيه .. بن أبي الخير بن غالي وحقق مقتله سنة ٧١٦ والذريعة ٣: ٢٦٩ وفيه علي مكان غالي وعرفه برشيد الدين وإيضاح المكنون ١: ٥٢٣ وكشف الظنون ٤٤٧ وفيه مقتله سنة ٧١٨ هـ واسم جده علي، ومثله في السلوك ٢: ١٨٩ واسم جده عالي، والبداية والنهاية ١٤: ٨٧ والأعلام ٥: ٣٥٩ ومجلة الثقافة المصرية ٧: ٤٨١ وانظر الفهرست. ومعجم المؤلفين ٨: ٧٤.
(٢) أمل الآمل ٢: ٢١٧ وأعيان الشيعة ٤٢: ٢٩٦ وروضات الجنات ٥١٤ وإيضاح المكنون ١: ٣٠٥ وهدية العارفين ١: ٨٢١ ومعجم المؤلفين ٨: ٧٥.','1','422',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (760,'فكري ياسين [١٣١٤ - ١٣٧٠ هـ / ١٨٩٧ - ١٩٥١ م]

<span data-type="title" id=toc-1067>فكري بن ياسن الأزهري:</span>
باحث مصري، من علماء الأزهر، ولد في "قصر هور" مركز ملوي. شارك في الحركة الوطنية سنة ١٩١٩ م، وتخرج بالأزهر ودرس فيه. وكان من الداعين إلى إصلاحه ففصل منه سنة ١٩٣١ ثم أعيد إليه سنة ١٩٣٥ م. واختير مراقبا للثقافة فيه فاستمر إلى أن توفي. من آثاره "غريب القرآن" طبع (١).

البناني [... - حيا ٩٠٢ هـ / ... - حيا ١٤٩٧ م]

<span data-type="title" id=toc-1068>فيض الله بن زين العابدين البناني:</span>
مفسر، مقرىء، من تصانيفه "دستور الحافظ" في تفسير القرآن العظيم (٢).

فيضي [٩٥٤ - ١٠٠٤ هـ / ١٥٤٧ - ١٩٥٤ م]

<span data-type="title" id=toc-1069>فيض الله (المعروف بفيضي) بن مبارك، الأكبر آبادي، أبو الفضل:</span>
_________
(١) الأزهر في ألف عام ٢: ٤٩ والأعلام ٥: ١٥٤.
(٢) بروكلمان ٢: ٦١٠ وهدية العارفين ١: ٨٢٣ ومعجم المؤلفين ٨: ٨٥.','1','423',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (761,'مفسر، عارف بالأدبين العربي والفارسي، مشارك في بعض العلوم. من أهل أكبر آباد بالهند. أخذ عن أبي الفضل الكازروني ورفيع الدين الصفدي. واتصل بالسلطان أكبر (٩٦٣ - ١٠١٤ هـ) ولقب بملك الشعراء. توفي بأكبر آباد. من تصانيفه بالعربية "سواطع الإلهام في تفسير القران" بالحروف المهملة، طبع (١).

الأرضرومي [١٠٤٨ - ١١١٥ هـ / ١٦٣٨ - ١٧٠٣ م]

<span data-type="title" id=toc-1070>فيض الله بن محمد بن محمد بن أحمد الأرضرومي:</span>
مفسر، من أكابر فقهاء الحنفية في عصره، ولد بأرزن الروم وبها نشأ وتعلم، وأقام بأدرنة، وحج سنة ١٠٧٨ هـ واجتمع بعلماء الحرمين ودمشق. قال المرادي: "قتل شهيدا في فتنة أدرنة". من آثاره "حاشية على تفسير سورة النبأ" لعصام، و "حواش على تفسير البيضاوي" (٢).
_________
(١) هدية العارفين ١: ٨٢٣ وأبجد العلوم ٨٩٦ ومعجم المطبوعات ١٤٧٢ والأعلام ٥: ٣٧٥ ومعجم المؤلفين ٨: ٨٦ وكشف الظنون ٤٦١.
(٢) هدية العارفين ١: ٨٢٣.','1','423',NULL,NULL,'0');
