القمولي = أحمد بن محمد ٧٢٧ هـ
القمي = إبراهيم بن محمد ١١٦٨ هـ
القمي = حبيب الله بن زين العابدين ... ١٣٥٩ هـ
القمي = سعد بن عبد الله ... ٣٠١ هـ
القمي = علي بن إبراهيم ... حيا ٣٢٩ هـ
القمي = علي بن موسى ... ٣٠٥ هـ
القمي = محمد بن الحسن ... ٣٤٣ هـ
القمي = محمد بن محمد رضا ... حيا ١١٠٧ هـ
القنازعي = عبد الرحمن بن مروان ... ٤١٣ هـ
ابن قندس = أبو بكر بن إبراهيم ... ٨٦١١ هـ
القندهاري = سعد الله بن غلام ... ١٣٠٦ هـ
القنوجي = رستم علي بن علي ... ١١٧٨ هـ
قوام السنة = إسماعيل بن محمد ٥٣٥ هـ
ابن القوبع = محمد بن محمد ٧٣٨ هـ
القوشجي = علي بن محمد ٨٧٩ هـ
القوصي = الحسن بن أبي بكر ... ٦٨٢ هـ
القوصي = محمد بن إبراهيم ... ٥٩٦ هـ
القونوي = أحمد بن قلمشاه ...؟','1','428',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (766,'القونوي = إسماعيل بن محمد ١١٩٥ هـ
القونوي = عبد الرحمن بن إبراهيم ... ٩٧٢ هـ
القونوي = علي بن إسماعيل ... ٧٢٩ هـ
القونوي = محمودبن أحمد ... ٧٧١ هـ','1','429',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (767,'(ق)

الرسي [١٦٩ - ٢٤٦ هـ / ٧٨٥ - ٨٦٠ م]

<span data-type="title" id=toc-1072>القاسم بن إبراهيم بن إسماعيل الحسني العلوي، أبو محمد، المعروف بالرسي:</span>
فقيه، شاعر، من أئمة الزيدية. كان يسكن جبال قدس من أطراف المدينة. وبعد وفاة أخيه محمد (سنة ١٩٩ هـ) تولى قيادة أتباعه. مات في الرس، وهو جبل أسود بالقرب من ذي الحليفة على ستة أميال من المدينة. قال في "تاريخ التراث العربي": أسس اتجاها زيديا ينسب إليه هو "القاسمية"، وما يزال هذا الاتحاه موجودا إلى الآن. كان يرى رأي المعتزلة في قضية الألوهية وكان مخالفا لرأي المرجئة". من آثاره','1','431',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (768,'"الناسخ والمنسوخ" مخطوط، و "تفسير القرآن" مخطوط (١).

ابن أصبغ [٢٤٧ - ٣٤٠ هـ / ٨٦٢ - ٩٥٠ م]

<span data-type="title" id=toc-1073>قاسم بن أصبغ بن محمد بن يوسف البياني القرطبي، أبو محمد:</span>
محدث الأندلس في عصره، من أئمة المالكية ولد في بيانة من أعمال قرطة وسمع بقرطبة، ثم رحل إلى المشرق فسمع بمصر والعراق والحجاز. وعاد إلى
_________
(١) تاريخ التراث العربي ٢: ٢٩٤ ومقاتل الطالبيين ٥٥٣ وأعيان الشيعة ٤٢: ٢٣١ وتراجم الرجال ٢٩ وتاريخ اليمن ١٨ ومعجم الشعراء للمرزباني ٢١٧ والأعلام ٦: ٥ ومعجم المؤلفين ٨: ٩١ وإتحاف المسترشدين ٤١ وفيه أن دعوته الأولى سنة ١٩٩ هـ كانت بمصر، وبويع بيعة ثانية في الكوفة سنة ٢٢٠ هـ.','1','431',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (769,'الأندلس فسكن قرطبة، فكان له بها قدر عظيم وسمع منه أمير المؤمنين الناصر لدين الله عبد الرحمن بن محمد قبل ولايته، وولي عهده ابنه الحكم. وكانت الرحلة إليه بالأندلس. من كتبه "الناسخ والمنسوخ" و "أحكام القرآن" (١).

السمرقندي [... - بعد ٨٨٨ هـ / ... - بعد ١٤٨٣ م]

<span data-type="title" id=toc-1074>أبو القاسم بن أبي بكر الليثي السمرقندي:</span>
أديب عارف بالتقسير، من فقهاء الحنفية. من آثاره "حاشية" على تفسير البيضاوي، أوله١: الحمد لله الذي نزل على عبده الكتاب الحكيم، هدى وبشرى للمؤمنين" منه نسخة مخطوطة في مدرسة النواب مع حاشية أبي الفضل القرشي الصديقي (٢).
_________
(١) طبقات المفسرين للداودي ٢: ٣٠ وبغية الملتمس ٤٣٣ وتاريخ علماء الأندلس ٣٦٤ وجذوة المقتبس ٣١١ تذكرة الحفاظ ٨٥٣ ونفح الطيب ٢: ٤٧ والديباج ٢٢٢ وبغية الوعاة ٢: ٢٥١ والرسالة المستطرفة ٢٥ ولسان الميزان ٤: ٤٥٨ والنجوم الزاهرة ٣: ٣٠٧ ومعجم الأدباء ١٦: ٢٣٦ وهدية العارفين ١: ٨٢٦ والأعلام ٦: ٧.
(٢) تعليقات آغا بزرك الطهراني على كشف الظنون ٢٠ وكشف الظنون ٨٩٨ ومعجم المطبوعات ١٠٤٤ والأعلام ٥: ١٧٣ ومعجم المؤلفين ٨: ١٠٣.','1','432',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (770,'الدمشقي [القرن الثالث الهجري / القرن التاسع الميلادي]

<span data-type="title" id=toc-1075>القاسم بن الخليل الدمشقي:</span>
فقيه إمامي، مفسر. قال ابن النديم: "هو من طبقة جعفر بن مبشر (المتوفي سنة ٢٣٤ هـ) وله كتب، منها "تفسير القرآن" (١).

البياتي [... - ١٣٢٥ هـ / ... - ١٩٠٧ م]

<span data-type="title" id=toc-1076>قاسم خير الدين بن محمد الحنفي البغدادي البياتي، أبو الخير:</span>
متصوف، له علم بالحديث والتفسير، من أهل بغداد. له تصانيف (٢).

أبو عبيد [١٥٧ - ٢٢٤ هـ / ٧٧٤ - ٨٣٨ م]

<span data-type="title" id=toc-1077>القاسم بن سلام الهروي الأزدي الخزاعي، بالولاء، الخراساني البغدادي، أبو عبيد:</span>
من كبار العلماء بالحديث والفقه والتفسير والقراءات والأدب. من أهل هراة، وبها نشأ وتعلم، وأقام ببغداد
_________
(١) طبقات المفسرين ٢: ٣٢ ولسان الميزان ٤: ٤٥٩ والفهرست لابن النديم ٢٠٦ و ٢٢٠.
(٢) لب الألباب ١: ١١٩ والأعلام ٦: ٩.','1','432',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (771,'مدة، ثم ولي القضاء بطرسوس ثماني عشرة سنة أيام ثابت بن ناصر. ودخل دمشق، ورحل إلى مصر سنة ٢١٣ هـ، وعاد إلى بغداد فسمع الناس من كتبه، وحج، فتوفي بمكة. وكان منقطعا للأمير عبد الله بن طاهر الخزاعي - أمير خراسان -، كلما ألف كتابا أهداه إليه، وأجرى له عشرة آلاف درهم. قال الذهبي: "من نظر في كتب أبي عبيد علم مكانه من الحفظ والعلم .. " وقال الجاحظ: "لم يكتب الناس أصح من كتبه، ولا أكثر فائدة". من آثاره "الناسخ والمنسوخ في القرآن" و "غريب القرآن" و "فضائل القرآن" (١).
_________
(١) طبقات المفسرين للداودي ٢: ٣٢ وطبقات القراء للذهبي ١: ١٤١ وطبقات الحنابلة ١: ٢٥٩ وطبقات الفقهاء ٧٦ والطبقات الكبرى ٧: ٥٣ وطبقات السبكي ٢: ١٥٣ وطبقات النحاة لابن قاضي شهبة ٢: ٢٢٣ وطبقات النحويين واللغويين ١٩١ وتاريخ بغداد ١٢: ٤٠٣ وغاية النهاية ٢: ١٧ وتاريخ التراث العربي ١: ٢٢٢ وتاريخ آداب اللغة العربية ٢: ١١٧ والكامل في التاريخ ٦: ٥٠٩ وخلاصة تهذيب الكمال ٢٦٥ وهدية العارفين ١: ٨٢٥ وابن خلكان ٤: ٦٠ ومعجم المطبوعات ١٢١ وتهذيب التهذيب ٨: ٢١٥ والتهذيب للأزهري ١: ٢١ وتهذيب الأسماء واللغات ٢: ٢٥٧ وإنباه الرواة ٣: ١٢ وميزان الاعتدال ٣: ٣٧١ ومقدمة كتاب الأجناس. والعبر ١: ٣٩٢ ومعجم الأدباء ١٦: ٢٥٤ والفهرست ٧٨ ونزهة الألباء ١٣٦ ومرآة الجنان ٢: ٨٣ وروضات الجنات ٥٢٦ وبغية الوعاة ٢: ٢٥٣ =','1','433',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (772,'التونسي [... - ١٢١٥ هـ / ... - ١٨٠٠ م]

<span data-type="title" id=toc-1078>قاسم بن علي التونسي، الملقب بزيرو:</span>
نحوي، مفسر، من أهل تونس. زار المدينة المنورة ثم دخل سورية وأقام بحلب إلى أن توفي. من آثاره تفسير قوله تعالى: {والله خلقكم وما تعملون} (١).

ابن الريولي [٣٨٨ - ٤٥١ هـ / ٩٩٨ - ١٠٥٩ م]

<span data-type="title" id=toc-1079>القاسم بن الفتح بن محمد بن يوسف، أبو محمد، المعروف بابن الريولي:</span>
عالم بالحديث والتفسير والقراءات، عارف باختلاف الأئمة، من أهل الفرج بالأندلس. رحل إلى المشرق، وحج. وعاد إلى بلده، فتوفي بها. أثنى عليه ابن صاعد الأندلسي وغيره (٢).
_________
= والشذرات ٢: ٥٤ ومفتاح السعادة ٢: ٣٠٦ والنسخ في القرآن الكريم ١: ٣١٢ وتذكرة الحفاظ ٤١٧ والأعلام ٦: ١٠ وكشف الظنون ١٢٠٤ و ١٢٠٧ و ١٢٠٩ و ١٢٧٧ و ١٩٢١.
(١) هدية العارفين ١: ٨٣٤ وإيضاح المكنون ١: ٣٠٢ ومعجم المؤلفين ٨: ١٠٦.
(٢) طبقات المفسرين للسيوطي ٧ وطبقات المفسرين للداودي ١: ٣٧ والصلة لابن بشكوال ٢: ٤٧٠ ونفح الطيب ٣: ٤٢٣ و ٤: ٣٣٥ وهو فيه ابن أفريولة، ومعجم المؤلفين ٨: ١١٠.','1','433',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (773,'الشاطبي [٥٣٨ - ٥٩٠ هـ / ١١٤ - ١١٩٤ م]

<span data-type="title" id=toc-1080>القاسم بن فيره بن خلف بن أحمد الرعيني، أبو محمد وأبو القاسم الشاطبي:</span>
إمام القراء، عالم بالحديث والتفسير واللغة. ولد بشاطبة (بالأندلس) وقرأ بها وببلنسية. ثم حج واستوطن مصر. قال ابن الأبار: "تصدر للإقراء بمصر، فعظم شأنه، وبعد صيته، وانتهت إليه الرياسة في الإقراء .. ". وكان ضريرا. وهو صاحب "حرز الأماني" قصيدة في القراءات، تعرف بالشاطبية. قال ابن خلكان: "ولقد أبدع فيها كل الإبداع، وهي محمدة قراء هذا الزمان في نقلهم، فقل من يشتغل في القراءات إلا ويقدم حفظها ومعرفتها، وهي مشتملة على رموز عجيبة وإشارات خفية لطيفة، وما أظنه سبق إلى أسلوبها". مات بالقاهرة (١).
_________
(١) غاية النهاية ٢: ٢٠ وطبقات القراء للذهبي ٢: ٤٥٧ وحسن المحاضرة ١: ٤٩٦ ومعجم الأدباء ١٦: ٢٩٣ والعبر ٤: ٢٧٣ والذيل والتكملة ٥: ٥٤٨ ونكث الهميان ٢٢٨ وتذكرة الحفاظ ١٣٥٦ وبغية الوعاة ٢: ٢٠٦ ومفتاح السعادة ٢: ٤٩ والسبكي ٧: ٢٧٠ والديباج ٢٢٤ والشذرات ٤: ٣٠١ والنجوم الزاهرة ٦: ١٣٦ ومرآة الجنان ٣: ٤٦٧ وروضات الجنات ٥٢٨ وابن خلكان ٤: ٧١ والتكملة رقم ١٩٧٣ والبداية والنهاية ١٣: ١٠ وهدية العارفين ١: ٨٢٨ والأعلام ٦ =','1','434',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (774,'ابن قطلوبغا [٨٠٢ - ٨٧٩ هـ /١٣٩٩ - ١٤٧٤ م]

<span data-type="title" id=toc-1081>قاسم بن قطلوبغا بن عبد الله، زين الدين، أبو العدل السودوني (نسبة إلى معتق أبيه سودوني الشيخوني) الجمالي:</span>
مؤرخ، من فقهاء الحنفية، مشارك في بعض العلوم. ولد بالقاهرة وبها نشأ وتعلم. وحج، ودخل بيت المقدس ودمشق وأخذ عن علمائها. قال السخاوي: "إمام علامة، قوي المشاركة في فنون، ذاكر لكثير من الأدب ومتعلقاته، واسع الباع في استحضار مذهبه وكثير من زواياه وخباياه، متقدم في هذا الفن، طلق اللسان، قادر على المناظرة وإفحام الخصم، لكن حافظته أحسن من تحقيقه". من كتبه "غريب القرآن" مخطوط، و "حاشية" على أنوار التنزيل، للبيضاوي، في التفسير (١).
_________
=: ١٤ والذيل على الروضتين ٧ ونفح الطيب ٢: ٢٢ ومعجم المؤلفين ٨: ١١٠.
(١) الضوء اللامع ٦: ١٨٤ والبدر الطالع ٢: ٤٥ وشذرات الذهب ٧: ٣٢٦ والفوائد البهية ٩٩ بالتعليفات. وكشف الظنون ١٩٣ وهدية العارفين ١: ٨٣٠ وبروكلمان، الذيل ٢: ٩٣ والأعلام ٦: ١٥ ومعجم المؤلفين ٨: ١١١.','1','434',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (775,'البياني [... - ٢٧٦ هـ / ... - ٨٩٠ م]

<span data-type="title" id=toc-1082>قاسم بن محمد بن قاسم بن محمد بن سيار الأموي، مولاهم، البياني القرطبي: أبو محمد:</span>
من كبار الفقهاء والمحدثين في الأندلس. كان جده مولى للخليفة الوليد بن عبد الملك (٤٨ - ٩٦ هـ). نسبته إلى بيانة بالأندلس. ومولده ووفاته بقرطبة. رحل إلى مصر رحلتين، ولازم ابن عبد الحكم حتى برع في الفقه وصار إماما مجتهدا لا يقلد أحدا. قال ابن الفرضي: "كان يذهب مذهب الحجة والنظر ويميل إلى مذهب الشافعي". من كتبه "تفسير القرآن" (١).

الأصفهاني [... - ١٢٠٣ هـ / ... - ١٧٨٩ م]

<span data-type="title" id=toc-1083>أبو القاسم بن محمد إسماعيل بن محمد الحسيني، الخواتون آبادي:</span>
فقيه إمامي، أصولي، مفسر، حكيم، من كتبه "حاشية" على "تفسير الكاشي" (٢).
_________
(١) نفح الطيب ٢: ٥٠ وجذوة المقتبس ٣٢٩ وبغية الملتمس ٤٣١ وإيضاح المكنون ١: ٣٠٢ وتذكرة الحفاظ ٦٤٨ وشذرات الذهب ٢: ١٧ والديباج ٢٢١ والأعلام ٦: ١٥ ومعجم المؤلفين ٨: ١٢٢ وتاريخ علماء الأندلس ٣٦٨.
(٢) أعلام الشيعة ٢: ٥٠.','1','435',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (776,'قبيضة بن عقبة [... - ٢١٥ هـ / ... - ٨٣٠ م]

<span data-type="title" id=toc-1084>قبيصة بن عقبة بن محمد الكوفي، أبو عامر، السوائي، من بني سواءة بن عامر ابن صعصعة:</span>
حافظ للحديث، مفسر، من أهل الكوفة. روى عن سفيان الثوري، وعنه روى البخاري وغيره. وهو أحد شيوخ الإمام أحمد. قال ابن سعد: "وكان ثقة صدوق كثير الحديث، توفي بالكوفة في خلافة المأمون". من آثاره "التفسير" قال سزجين: استخدمه الطبري بالرواية الآتية: حدثني المثنى بن إبراهيم قال: حدثنا قبيصة. ولقد أخذ البخاري منه ٤٧ مرة" (١).

قتادة بن دعامة [٦١ - ١١٨ هـ / ٦٨٠ - ٧٣٦ م]

<span data-type="title" id=toc-1085>قتادة بن دعامة بن قتادة بن عزيز، أبو الخطاب السدوسي البصري:</span>
مفسر، حافظ للحديث، فقيه، عالم بالشعر
_________
(١) شذرات الذهب ٢: ٣٥ وابن سعد ٦: ٤٠٣ وتاريخ التراث العربي ١: ٢٦ والتاريخ الكبير للبخاري ٤/ ١: ١٧٧ والجرح والتعديل ٢/ ٣: ١٢٦ وكشف الظنون ٤٥٦.','1','435',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (777,'والأنساب وتاريخ العرب الأقدمين، كان أحد من يضرب به المثل في حفظه. قال: "ما قلت قط لمحدث: أعد علي، وما سمعت أذناي شيئا قط، إلا وعاه قلبي". روى عن أنس بن مالك وعدد من كبار التابعين. قال الذهبي: "وكان يرى القدر، وقد يدلس في الحديث، ومع هذا الاعتقاد الرديء، ما تأخر أحد عن الاحتجاج بحديثه". مات بواسط في الطاعون. من آثاره "تفسير القرآن". قال صاحب "تاريخ التراث العربي": استخدمه الخطيب البغدادي. ويبدو أنه كان تفسيرا كبيرا ضخما، ولقد استخدمه الطبري أكثر من ٣٠٠٠ مرة، وربما نقل كل مادته نقلا، وذلك بالرواية التالية: حدثنا بشر بن معاذ، قال: حدثنا يزيد بن زريع عن سعيد عن قتادة. ولقد عرف الثعلبي عدا ذلك روايتين أخريين لهذا الكتاب". وقال صاحب كشف الظنون: "له - أي تفسير قتادة - طرق منها طريق خارجة بن مصعب السرخسي، وقد زاد خارجة فيه من جهته مقدار ألف حديث. وطريق شيبان ابن عبد الرحمن النحوي، وطريق معمر"، ولقتادة أيضا "الناسخ والمنسوخ في كتاب الله، الذي حصل على جواز روايته الخطيب البغدادي في دمشق. و','1','436',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (778,'"عواشر القرآن" اقتبس منه ابن سعد في الطبقات الكبرى (١).

ابن شريح [... - ٣١٦ هـ / ... - ٩٢٨ م]

<span data-type="title" id=toc-1086>قتيبة بن أحمد بن شريح البخاري، أبو حفص:</span>
مفسر، من فقهاء الإمامية. روى عن سعيد بن مسعود المروزي وأبي يحيى بن أبي مسرة. روى عنه نصوح بن واصل. قال السيوطي "له تفسير كبير" (٢).

اللاري [... - نحو ١٠٥٠ هـ / ... - نحو ١٦٤٠ م]

<span data-type="title" id=toc-1087>قطب الدين بن عبد الحي الزاهدي الكبيري الحسيني اللاري:</span>
مفسر، له "حاشية على الكشاف" مخطوطة (٣).
_________
(١) نكت الهميان ٢٣٠ وطبقات المفسرين ٢: ٤٣ وتهذيب التهذيب ٨: ٣٠١ وطبقات الفقهاء ٧٢ وخلاصة تهذيب الكمال ٢٦٨ وميزان الاعتدال ٣: ٣٨٥ وغاية النهاية ٢: ٢٥ وطبقات ابن سعد ٧: ٢٢٩ والعبر ١: ١٤٦ وياقوت ١٧: ١٣٣ والشذرات ١: ١٥٣ وابن خلكان ٤: ٨٥ وهدية العارفين ١: ٨٣٤ وكشف الظنون ٤٥٦ والمعارف ٢٣٤ وتاريخ التراث العربي ١: ١٨٩ وتذكرة الحفاظ ١٢٢ وطبقات المدلسين ١٦ والتفسير والمفسرون ١: ١٢٥.
(٢) طبقات المفسرين للسيوطي ٢٨ وطبقات الداودي ٢: ٤٤ ومعجم المؤلفين ٨: ١٢٧ وهدية العارفين ١:
(٣) شستريني ٤٢٨١ والأعلام ٥: ٢٠٠.','1','436',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (779,'قيس بن مسلم [... - ١٢٠ هـ / ... - ٧٣٨ م]

<span data-type="title" id=toc-1088>قيس بن مسلم الجدلي، أبو عمرو:</span>
محدث ثقة، من مشاهير مفسري مدرسة الكوفة. قال البخاري: "وهو من قيس عيلان". روى عن طارق بن شهاب وعبد الرحمن بن أبي ليلى وسعيد بن جبير، روى عنه الثوري وشعبة. وقال ابن سعد: "مات بالكوفة، وكان ثقة ثبتا له حديث صالح". وفي الشذرات أنه ما رفع رأسه إلى السماء منذ زمان تعظيما لله تعالى" (١).
_________
(١) ابن سعد ٦: ٣١٧ والبخاري ٧: ١٥٤ والشذرات ١: ١٥٧ والكشف ٤٢٩.','1','437',NULL,NULL,'0');
