INSERT INTO [b13846] VALUES (799,'(م)

الإمام مالك [٩٣ - ١٧٩ هـ / ٧١٢ - ٧٩٥ م]

<span data-type="title" id=toc-1096>مالك بن أنس بن مالك بن أبي عامر الأصبحي الحميري، أبو عبد الله:</span>
إمام دار الهجرة، وأحد الأئمة الأربعة عند أهل السنة، وإليه تنسب المالكية. ويعد من أدق المحدثين في عصره. ولد بالمدينة، ولا تتفق الروايات على سنة ولادته، فتذكرها ما بين سنتي ٩٠ و ٩٧ هـ. أخذ عن ربيعة بن فروخ والزهري وغيرهما. ولما قام محمد بن عبد الله العلوي (النفس الزكية) بثورته مطالبا بالخلافة، لم يبد مالك أي اعتراض على الثورة، ولما فشلت، وشي به، فعاقبه والي المدينة. ووجه إليه الخليفة هارون الرشيد - عندما كان','2','460',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (800,'يحج عام ١٧٩ هـ - ليأتيه ويحدثه، فقال العلم يؤتى. فقصد الرشيد منزله واستند إلى الجدار، فقال مالك: يا أمير المؤمنن من إجلال رسول الله إجلال العلم، فجلس بين يديه، فحدثه. ومات مالك في نفس السنة بالمدينة. من كتبه "غريب القرآن" (١).
_________
(١) مالك بن أنس لمحمد أبي زهرة، والديباج ١٧ وتذكرة الحفاظ ٢٠٧ وتهذيب التهذيب ١٠: ٥ وتهذيب الأسماء واللغات ٢: ٧٥ والتاريخ الكبير للبخارى ٣/ ١/ ٣١٠ والمشاهير لابن حبان ١٤٠ والنجوم الزاهرة ٢: ٩٦ وطبقات الشيرازى ٤٢ وجمهرة الأنساب ٤٣٥ وغاية النهاية ٢: ٣٥ وحلية الأولياء ٦: ٣١٦ والرسالة المستطرفة ١٣ وذيل المذيل ١٠٦ وترتيب المدارك ١: ١٠٢ وصفة الصفوة ٢: ٩٩ وابن خلكان ٤: ١٣٥ والبداية والنهاية ١٠: ١٣٤ والأعلام ٦: ١٢٨ وطبقات المفسرين للداودي ٢: ٢٩٣ وتاريخ التراث العربي ٢: ١٢٠ وتاريخ الفكر الأندلسي ٤١٧ =','2','460',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (801,'ابن الدهان [٥٣٤ - ٦١٢ هـ / ١١٤٠ - ١٢١٥ م]

<span data-type="title" id=toc-1097>المبارك بن المبارك بن سعيد، أبو بكر، وجيه الدين ابن الدهان الواسطي:</span>
نحوي، أديب، مفسر، لغوي، عارف بالقراءات والفقه والطب والنجوم وعلوم الأوائل، من أهل واسط، استوطن بغداد، ودرس بالنظامية. وكان ضريرا، يحسن التركية والفارسية والرومية والحبشية والزنجية. مات ببغداد (١).

البكري [... - ٤٢٩ هـ / ... - ١٠٣٨ م]

<span data-type="title" id=toc-1098>مبارك، مولى محمد بن عمرو، أبو الحسن، البكري:</span>
مفسر، فقيه مالكي، محدث، من أهل إشبيلية،
_________
= ومرآة الجنان ١: ٣٧٣ والانتقاه ٨ ومفتاح السعادة ٢:
(١) طبقات المفسرين للداودي ٢: ٣٠٠ ونكت الهميان ٢٣٣ وغاية النهاية ٢: ٤١ والسبكي ٨: ٣٥٤ ومرآة الزمان ٨: ٥٧٣ وإنباه الرواة ٣: ٢٥٤ والشذرات ٥: ٥٣ والنجوم الزاهرة ٦: ٢٤٩ ومرآة الجنان ٤: ٦٤ والبدايه والنهاية ١٣: ٦٩ وذيل الروضين ٩٠ وطبقات النحاة (لابن قاضي شهبة) ٢: ٣٢٦ وبغية الوعاة ٢: ٢٧٣ ومعجم المؤلفين ٨: ١٧٣ والعبر ٥: ٤٣ وابن خلكان ٤: ١٥٢ وإرشاد الأريب ٦: ٢٣١.','2','461',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (802,'روى بالأندلس عن جماعة من الشيوخ، وحج سنة ٤٥٨ هـ، ولقي عددا من رجال الحديث وروى عنهم. قال ابن بشكوال: "كان خيرا حافظا للتفسير، ذا حظ من علم الحديث والرأي .. " (١).

ابن الأثير [٤٥٥ - ٦٠٦ هـ / ١١٥٠ - ١٢١٠ م]

<span data-type="title" id=toc-1099>المبارك بن محمد بن محمد بن محمد ابن عبد الكريم بن عبد الواحد الشيباني الجزري، أبو السعادات، مجد الدين:</span>
محدث، لغوي، مفسر، أصولي، مشارك في بعض العلوم، ولد ونشأ في جزيرة ابن عمر، من أعمال الموصل، ثم انتقل والده به وبأخويه إلى الموصل في سنة ٥٧٩ هـ حيث أقاموا بها إقامة دائمة. وخدم المبارك الزنكيين وكبار موظفيهم، فكان كاتب الإنشاء لمجاهد الدين قايماز وزير عز الدين مسعود صاحب الموصل، ثم تولى ديوان رسائل عز الدين نفسه، ثم ديوان ابنه نور الدين أرسلان شاه من بعده. ثم أصيب بالنقرس فبطلت حركة يديه
_________
(١) طبقات المفسرين ٢: ٣٠٢ والصلة ٢: ٦٣٤ الترجمة ١٣٩٤.','2','461',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (803,'ورجليه، ولازمه هذا المرض إلى أن توفي بقرية قصر حرب. وقد ألف كتبه كلها أثناء مرضه، إملاء على طلبته. من كتبه "الإنصاف في الجمع بين الكشف والكشاف" في التفسير، جمع فيه بين تفسير الثعلبي والزمخشري (١).

الأكبر آبادي [... - ١٠٠١ هـ / ... - ١٥٩٣ م]

<span data-type="title" id=toc-1100>مبارك بن موسى الأكبر آبادي:</span>
مفسر، فقيه حنفي، هندي الموطن، له تصانيف، منها "منبع عيون المعاني" في تفسير القرآن (٢).

مجاهد بن أصبغ [٣٠٥ - ٣٨٢ هـ / ٩١٨ - ٩٩٢ م]

<span data-type="title" id=toc-1101>مجاهد بن أصبغ بن حسان، أبو الحسن البجاني:</span>
مؤرخ، أديب،
_________
(١) معجم الأدباء ١٧: ٧١ والبداية والنهاية ١٣: ٥٤ وبغية الوعاة وروضات الجنات ٥٨٥ والأعلام ٦: ١٥٢ والنجوم الزاهرة ٦: ١٩٨ وشذرات الذهب ٥: ٢٢ والسبكي ٥: ١٥٣ وطبقات ابن قاضي شهبة ٢: ٢٣٨ ومرآة الجنان ٤: ١١ ومقدمة التاريخ الباهر، وهدية العارفين ٢: ٢ وكشف الظنون ١٨٢ و ٤٣٧ وابن خلكان ٤: ١٤١.
(٢) هدية العارفين ٢: ٢ وإيضاح المكنون ٢: ٥٦٦.','2','462',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (804,'مشارك في بعض العلوم، من أهل بجانة (من أعمال الزهراء بالأندلس). له كتب، منها "الناسخ والمنسوخ" (١).

مجاهد بن جبر [٢١ - ١٠٤ هـ / ٦٤٢ - ٧٢٢ م]

<span data-type="title" id=toc-1102>مجاهد بن جبر، أبو الحجاج المكي، مولى بني مخزوم:</span>
تابعي، إمام في التفسير، ولد في مكة، وسمع عائشة وأبا هريرة وعبد الله بن عمر، وعبد الله ابن عباس، وكان أقل أصحابه رواية عنه في التفسير ولكنه أوثقهم. قال: قرأت القرآن على ابن عباس ثلاث مرات، أقف عند كل آية أسأله: فيما نزلت وكيف كانت؟ وهو أحد القائلين بالمذهب العقلي في تفسير القرآن. تنقل في الأسفار، واستقر في الكوفة، وكان لا يسمع بأعجوبة إلا ذهب لينظر إليها، ذهب "بئر برهوت" في حضرموت، وذهب إلى "بابل" يبحث عن هاروت وماروت. شهد العلماء النقاد بعلو مكانته في التفسير، فقال الثوري: "إذا جاءك التفسير عن مجاهد
_________
(١) تاريخ علماء الأندلس ٢: ٢٢ والأعلام ٥: ٢٧٧ ومعجم المؤلفين ٨: ١٧٦.','2','462',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (805,'فحسبك به". وقال الذهبي: "أجمعت الأمة على إمامة مجاهد والاحتجاج به" وقال قتادة: "أعلم من بقي بالتفسير مجاهد". له "تفسير" اعتمد عليه الشافعي والبخاري وغيرهما، ونقل البخاري كثيرا من تفسير مجاهد في كتاب التفسير من الجامع الصحيح. قال فؤاد سزكين: "وفي تفسيره نجد كثيرا من التفسيرات المجازية والمشبهة، وكان بعض تفسيره يقرأ بتحفظ، ذلك لأنه كان كثيرا ما يستشير علماء النصارى وأحبار اليهود وأما في حقل الفقه فقد جعل "للرأي" منزلة هامة. وقد نقل الطبري من هذا التفسير حوالي ٧٠٠ مرة في مواضع مختلفة، عن طريق الرواية وعن طريق تفاسير أخرى. ويقال: إنه مات وهو ساجد (١).
_________
(١) تذكرة الحفاظ ٩٢ وتهذيب التهذيب ١٠: ٤٢ وابن سعد ٤: ٤٦٦ وميزان الاعتدال ٣: ٩ وحلية الأولياء ٣: ٢٧٩ والمعارف ٢٢٧ وغاية النهاية ٢: ٤١ وتاريخ التراث العربي ١: ١٨٥ ومعجم الأدباء. وطبقات الفقهاء ٤٥ وابن النديم ٣٣ وصفة الصفوة ٢: ١١٧ والأعلام ٦: ١٦١ وهدية العارفين ١: ٤ وكشف الظنون ١٥٨ وفجر الإسلام ٢٥١ ومقدمة ابن تيمية في أصول التفسير ٢٨.','2','463',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (806,'محسن الطويل [... - ١٢٥٥ هـ / ... - ١٨٣٩ م]

<span data-type="title" id=toc-1103>محسن بن حسين الطويل:</span>
قارىء، مفسر، من أهل صنعاء باليمن. كان مرجعا في القراءات السبع. له "بلوغ الأماني في مستودعات السبع المثاني" في تفسير سورة الفاتحة. (١).

ابن كرامة [٤١٣ - ٤٩٤ هـ / ١٠٢٢ - ١١٠١ م]

<span data-type="title" id=toc-1104>المحسن بن محمد بن كرامة الجشمي البيهقي، أبو سعد، ويقال له الحاكم الجشمي:</span>
مفسر، متكلم، أصولي، حنفي، ثم معتزلي فزيدي. قرأ بنيسابور وغيرها. وهو من شيوخ الزمخشري. دخل اليمن واشتهر بصنعاء، وقتل بمكة، قيل لرسالة ألفها اسمها "رسالة الشيخ إبليس إلى إخوانه المناحيس". من آثاره "التهذيب" في تفسير القرآن، ثمانية مجلدات، منها الرابع والسادس والثامن، وهو الأخير، كتب سنة ٥٦٥ هـ. (مكتبة الفاتيكان ١٠٢٣، ١٠٢٥، ١٠٢٦ عربي. قال صاحب كشف الظنون:
_________
(١) نيل الوطر ٢: ١٩٩ والأعلام ٦: ١٧٤.','2','463',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (807,'فسره بالقول ذكر القراءة أولا ثم اللغة ثم الإعراب ثم المعنى ثم الأحكام، رأيت منها نسخة مكتوبة مؤرخة بسنة ٦٥٢ هـ ". و، تفسيران " بالفارسية، مبسوط وموجز (١).
محسن محمد = محمد بن مرتضى

النحوي [... - حيا سنة ١١٢٨ / ... - حيا - ١٧١٦ م]

<span data-type="title" id=toc-1105>محسن بن محمد طاهر القزويني، النحوي:</span>
فقيه إمامي، مفسر، مشارك في بعض العلوم، من أهل قزوين، وتنسب إليه الطائفة النحوية بها. من كتبه الكثيرة "تفسر القرآن" (٢).

الهروي [... - ٤١٤ هـ / ... - ١٠٢٣ م]

<span data-type="title" id=toc-1106>محمد بن آدم بن كمال الهروي، أبو المظفر:</span>
عالم بالأدب، مفسر، نسابة، متكلم، من فقهاء الحنفية. قال عبد
_________
(١) كشف الظنون ٥١٧ والأعلام ٦: ١٧٦ وتراجم الرجال ٣٢ ومعجم المؤلفين ٨: ١٨٧.
(٢) أعيان الشيعة ٤٣: ١٧٣ و ٢٠٣.','2','464',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (808,'الغافر الفارسي: "إمام في الأدب، فاق أقرانه ومن تقدمه من الأئمة باستخراج المعاني وشرح الأبيات، وله أمثال وغرائب التفسير بحيث يضرب به المثل، وكان يقعد للتدريس في النحو وشرح الدواوين والتفسير وغير ذلك، وذكره القفطي في كتابه "المحمدون من الشعراء" وقال: "فاضل ابن فاضل، له أدب ويد طولى في علم النسب، صنف فيه كتابا مختصرا، وله يد في علم الكلام على مذهب العدل، وشعره قليل جدا" (١).

البلخي [... - ٢٤٤ هـ / ... - ٨٥٨ م]
محمد بن أبان البلخي، أبو بكر، ويعرف ب"حمدويه" محدث ثقة، حافظ، مفسر، من أهل بلخ مولدا ووفاة، رحل فسمع من سفيان بن عيينة وطبقته. وكان مستملي وكيع بن الجراح. حدث عنه الجماعة، سوى
_________
(١) طبقات المفسرين للداودي ٢: ٤٥ والمحمدون من الشعراء ١٤٤ والوافي بالوفيات ١: ٣٣٤ وتذكرة الحفاظ ٤٩٨ وميزان الاعتدال ٣: ٤٥٤ والنجوم الزاهرة ٢: ٣١٩ والعبر للذهبي ١: ٤٤٣ ومعجم الأدباء ١٧: ١١٦ والشذرات ٢: ١٠٥ والأعلام ٦: ١٨٠ ومعجم المؤلفين ٩: ٣٥','2','464',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (809,'مسلم. قال الذهبي: "كان من الأئمة المصنفين في الحديث، مشهورا بالعلم والحفظ". وقال الداودي: "جمع، وصف "التفسير" (١).

ابن المنذر [٢٤٢ - ٣١٩ هـ / ٨٥٦ - ٩٣١ م]

<span data-type="title" id=toc-1107>محمد بن إبراهيم بن المنذر النيسابوري، أبو بكر:</span>
محدث ثقة، فقيه كبير، مجتهد، مفسر، ولد بنيسابور، وعاش بمكة، وكان شيخ الحرم بها. أثنى عليه كثيرون، ومع ذلك فقد جرح لأنه نسب إلى أبي حنيفة ومالك والشافعي آراء لا توجد في كتبهم. وعده السبكي وأبو إسحاق الشيرازي والنووي من أصحاب الشافعي. قال الشيرازي: "صنف في اختلاف العلماء كتبا لم يصنف أحد مثلها، واحتاج إلى كتبه الموافق، والمخالف" وقال النووي: "لا يلتزم التقيد في الاختيار بمذهب أحد بعينه ولا يتعصب لأحد، ولا على عادة أهل الخلاف، بل يدور مع ظهور الدليل،
_________
(١) طبقات المفسرين للداودي ٢: ٤٥ والوافي ١: ٣٣٤ والعبر ١: ٤٤٣ وتذكرة الحفاظ ٤٩٨ والنجوم الزاهرة ٢: ٣١٩ وشذرات الذهب ٢: ١٠٥ وميزان الاعتدال ٣: ٤٥٤.','2','465',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (810,'ودلالة السنة الصحيحة، ويقول بها مع من كانت، ومع هذا فهو عند أصحابنا معدود من أصحاب الشافعي .. " وزاد فقال: "مجمع على إمامته، وجلالته، ووفور علمه، وجمعه بين التمكن في علمي الحديث والفقه واعتماد علماء الطوائف كلها في نقل المذاهب ومعرفتها على كتبه". في تاريخ وفاته خلاف، وقد أخذت بالتاريخ الذي ذكر. شيخ الإسلام ابن حجر العسقلاني في" لسان الميزان" ففيه تحقيق وفاته سنة ٣١٩ هـ. من آثاره "تفسير القرآن"، قال الذهبي: "تفسير كبير، في بضعة عشر مجلدا، يقضي له بالإمامة في علم التأويل أيضا". منه مخطوطة قديمة جدا تبدأ بالآية ٢٧ من سورة البقرة، قال صاحب "تاريخ التراث العربي": "ووصلت إلينا نصوص مأخوذة منه على هامش تفسير ابن أبي حاتم الرازي! (١).
_________
(١) طبقات المفسرين للسيوطي ٢٨ وطبقات المفسرين للداودي ٢: ٥٠ وطبقات الشافعية للسبكي ٣: ١٠٢ وطبقات الشافعية للعبادي ٦٧ وطبقات الفقهاء للشيرازي ٨٩ والعقد الثمين ١: ٤٠٧ ووفيات الأعيان ٤: ٢٠٧ والفهرست لابن النديم - وتذكرة الحفاظ ٣: ٧٨٢ وميزان الاعتدال ٣: ١٥٠ ولسان الميزان ٥: ٥٧٠ والمهذب ٣: ٣٩٠ ومرآة الجنان ٢: ٢٦١ وتهذيب الأسماء واللغات ٢: ١٩٧ والوافي =','2','465',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (811,'ابن أبي زينب [... - ٣٦٠ هـ / ... - ٩٧١ م]

<span data-type="title" id=toc-1108>محمد بن إبراهيم بن جعفر، الكاتب النعماني البغدادي، أبو عبد الله، المعروف بابن أبي زينب:</span>
فقيه إمامي، مفسر، من أهل النعمانية بالعراق، انتقل إلى بغداد وأخذ عن الكليني، ثم سافر إلى الشام وتوفي في حدود سنة ٣٦٠ هـ. من كتبه "تفسير القرآن" (١).

الرازي [... - ٤٩٤ هـ / ... - ١١٠٠ م]

<span data-type="title" id=toc-1109>محمد بن إبراهيم بن الحسن، أبو بكر الرازي:</span>
فقيه حنفي، مفسر، زاهد، سكن الإسكندرية وأخذ عن علمائها، وتوفي بها. له كتاب في
_________
= بالوفيات ١: ٣٣٦ والمقصل للسيوطي ٢٨ والرسالة المستطرفة ٧٧ والكافي في معرفة علماء مذهب الشافعي ٢: ١١٨ وهدية العارفين ٢: ٣١ وكشف الظنون ٤٤٠ و ٤٦٠ والأعلام ٦: ١٨٤ وتاريخ التراث العربي ٢: ١١٨ وطبقات الشافعية لابن هداية الله الحنسيني ٥٩ وصلة تاريخ الطبري ١٣٤ وطبقات الشافعية لابن قاضي شهبة ٢: ٩.
(١) هدية العارفين ٢: ٤٦ وفوائد الرضوية ٣٧٧ وإيضاح المكنون ١: ٣١٠ وأمل الأمل ٢: ٢٣٢ والنجاشي ٢٩٧.','2','466',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (812,'"تأويل آيتي القتل في سورة النساء" (١).

الغساني [... - ٥٣٦ هـ / ... - ١١٤٢ م]

<span data-type="title" id=toc-1110>محمد بن إبراهيم بن أحمد بن أسود الغساني، أبو بكر:</span>
مفسر، قاض، من بيت علم ودين، من أهل المرية بالأندلس. رحل إلى مصر ولقي بها أبا بكر الطرطوشي. وعاد إلى بلده، واستقضي بمرسية مدة طويلة، ثم صرف. وسكن مراكش وتوفي بها. له "تفسير القرآن" (٢).

الزنجاني [... - ٧٢١ هـ / ... - ١٣٢١ م]

<span data-type="title" id=toc-1111>محمد بن إبراهيم بن إسماعيل الزنجاني:</span>
فقيه شافعي، قاض، مفسر، أخذ عن البيضاوي، وولي القضاء بشيراز. له تصانيف، منها "تفير القرآن" (٣).
_________
(١) طبقات المفسرين للداودي ٢: ٤٦ والجواهر المضية ٤: ٢
(٢) طبقات المفسرين للداودي ٢: ٤٦ والصلة ٢: ٥٥٣ وبغية الملتمس ٤٦ والمعجم لابن الأبار ١٢٦ وإيضاح المكنون ٣٠٨ ونفح الطيب ٧: ٤٦ وهدية العارفين ٢: ٨٨.
(٣) هدية العارفين ٢: ١٤٤.','2','466',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (813,'القوصي [٥٤٠ - ٥٩٦ هـ / ١١٤٦ - ١٢٠٠ م]

<span data-type="title" id=toc-1112>محمد بن إبراهيم بن محمد بن علي ابن رفاعة، كمال الدين، أبو الفتوح القوصي:</span>
فقيه، مفسر، نحوي، أصولي، لغوي، من القضاة، من أهل قوص بصعيد مصر. ولي القضاء بالأعمال القوصية عدة سنين، ومدح بعدة مدائح (١).

ابن جماعة [٦٣٩ - ٧٣٣ هـ / ١٢٤١ - ١٣٣٣ م]

<span data-type="title" id=toc-1113>محمد بن إبراهيم بن سعد الله بن جماعة الكناني الحموي، بدر الدين، أبو عبد الله:</span>
قاض، من فقهاء الشافعية، عالم بالحديث والفقه والتفسير والأصول، مشارك في غير ذلك. ولد في حماه، وسمع بها وبمصر ودمشق ومكة، وحدت بالكثير، وتفرد في وقته. وولي الحكم والخطابة بالقدس، ثم القضاء بمصر، فقضاء الشام، ثم قضاء مصر إلى أن شاخ وعمي وثقل
_________
(١) طبقات المفسرين للداودي ٢: ٤٦ وبغية الوعاة ١: ١٥ والوافي بالوفيات ٢: ٢٧ والطالع السعيد ٤٨٢ والمقفى للمقريزي.','2','467',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (814,'سمعه، فطلب الإعفاء من القضاء فأعفي، ولزم داره إلى أن مات. من كتبه "غرر البيان لمبهمات القرآن" و " الفوائد اللائحة من سورة الفاتحة" و "كشف المعاني في المتشابه من المثاني" (١).

ابن الشهيد [٧٢٨ - ٧٩٣ هـ / ١٣٢٨ - ١١٣٩ م]

<span data-type="title" id=toc-1114>محمد بن إبراهيم بن أبي بكر بن محمد بن أبي الكرم، أبو الفتح، وقيل أبو بكر، فتح الدين، ابن الشهيد:</span>
كاتب السر بدمشق، فقيه، مفسر، أديب، ناظم، أصله من نابلس. ولد بالرملة، ودخل دمشق فدرس بالناصرية والظاهرية، وولي كتابة السر بها مرارا، مع ولاية مشيخة الشيوخ، ومدة ولايته خمس عشرة سنة وأشهر. ثم جرت له محنة اختفى بسببها مدة
_________
(١) نكت الهميان ٢٣٠ وذيل تذكرة الحفاظ ١٠٧ والوافي بالوفيات ٢: ١٨ والأنس الجليل ٢: ١٣٦ والدرر الكامنة ٣: ٣٦٧ والبداية والنهاية، ١: ١٦٣ والرسالة المستطرفة ٢١٤ وقضاة دمشق ٨٠ ومرآة الجنان ٤: ٢٨٧ والنجوم الزاهرة ٩: ٢٩٨ وهدية العارفين ٢: ١٤٨ والشذرات ٦: ١٠٥ وذيل العبر ١٧٨ ودول الإسلام ٢: ١٨٣ وفوات الوفيات ٣: ٢٩٧ وحسن المحاضرة ٩: ٤٢٥ وإيضاح المكنون ٢: ١٤٥ و ٢٠٩ و ٣٦٧ وكشف الظنون ١٤٩٥.','2','467',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (815,'نظم فيها "السيرة النبوية" لابن سيد الناس، في بضعة عشر ألف بيت، مع زيادات دلت على سعة باعه في العلم، وسماها "الفتح القريب في سيرة الحبيب" وحدث بها في القاهرة. ولما خلع الظاهر برقوق (سنة ٧٩١ هـ) ونفي إلى الكرك، ثم خلص منها وزحف على دمشق وحاصرها، "قام ابن الشهيد في وجهه وجمع لمحاربته". وظفر برقوق، وعاد إلى مصر سلطانا سنة ٧٩٢ هـ، فأمر بالقبض على ابن الشهيد، فحمل إلى القاهرة مقيدا وأودع السجن مع المجرمين. ثم أمر به (في شعبان ٧٩٣ هـ) فأخرج إلى ظاهر القاهرة فضربت عنقه. قال ابن حجر: "كان أحد أفراد الدهر ذكاء وعلما ورياسة ونظما، مهر في التفسير والفقه، وبرع في الأدب والفضائل، وأقرأ "الكشاف" وغيره" (١).

ابن الوزير [٧٧٥ - ٨٤٠ هـ / ١٣٧٣ - ١٤٣٦ م]

<span data-type="title" id=toc-1115>محمد بن إبراهيم بن علي بن المرتضى الحسني القاسمي، أبو عبد (الله، عز الدين، من آل الوزير)</span>
_________
(١) إنباء الغمر ١: ٤٢٦ والدرر الكامنة ٣: ٣٨٣ والشذرات ٦: ٣٢٩ ونزهة النفوس والأبدان ١: ٣٢٤ و ٣٣١ ومطالع البدور ١: ١٠ وهدية العارفين ٢: ١٧٤.','2','468',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (816,'الله، عز الدين، من آل الوزير: من علماء الزيدية باليمن، ولد في هجرة الظهر، من شظب، أحد جبال اليمن، وتعلم بصنعاء وصعدة ومكة. مات بصنعاء. من آثاره "قواعد التفسير" مخطوط (١).

الخطيب الوزيري [... - ٨٩١ هـ / ... - ١٤٨٥ م]

<span data-type="title" id=toc-1116>محمد بن إبراهيم بن عثمان بن سعيد، شمس الدين، الخراشي الأصل، القاهري، المعروف بالخطيب الوزيري:</span>
صوفي، مفسر، فقيه مالكي، من أهل القاهرة، سمع بها وبدمشق، وحج وجاور، ودرس بالجمالية والمؤيدية. له "حاشية على تفسير البيضاوي" (٢).
_________
(١) البدر الطالع ٢: ٨١ والضوء اللامع ٦: ٢٧٢ وهدية العارفين ٢: ١٩٠ والأعلام ٦: ١٩١ ومعجم المؤلفين ٨: ٢١٠.
(٢) الضوء اللامع ٦: ٢٥٩ وفيه أنه كان مجاورا سنة ٨٩٨ هـ وهدية العارفين ٢: ٢١٤ ووفاته في سنة ٨٩١ ومثله في إيضاح المكنون ١: ١٤. وكشف الظنون ١٠٣٣ ووفاته فيه سنة ٨٩٠ هـ و ١٨٠٥ ووفاته سنة ٨٦٧ هـ.','2','468',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (817,'النكساري [... - ٩٠١ هـ / ... - ١٤٩٦ م]

<span data-type="title" id=toc-1117>محمد بن إبراهيم بن حسن، محيي الدين، النكساري، الرومي:</span>
فقيه حنفي، مفسر، مشارك في بعض العلوم. درس في جامع أيا صوفيا وجامع السلطان محمد خان. قال الغزي: "كان حافظا للقرآن الكريم عارفا بعلم القراءات ماهرا بالتفسير، عالما بالعربية والعلوم الشرعية والعقلية". من كتبه "تفسير سورة الدخان" أهداه إلى السلطان بايزيد خان. قال صاحب الشقائق النعمانية: "وهو تأليف يدل على صاحبه أنه آية كبرى في علم التفسير". وله أيضا "حاشية على "أنوار التنزيل" في التفسير، للبيضاوي" (١).

ابن الخطيب [... - ٩٠١ هـ / ... - ١٤٩٥ م]

<span data-type="title" id=toc-1118>محمد بن إبراهيم الرومي، محيي الدين، الشهير بابن الخطيب:</span>
فقيه
_________
(١) الكواكب السائرة ١: ٢٣ والشذرات ٨: ٩ والفوائد البهية ١٥٥ والشقائق النعمانية ١: ٤٠٥ وإيظاح المكنون ١: ١٤٢ وكشف الظنون ٤٥٠ وهدية العارفين ٢: ٢٩٨ وهو في بعض المصادر: التكشاري، والتكساري.','2','469',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (818,'حنفي، أصولي، متكلم، مفسر، تركي مستعرب. قال صاحب الشذرات: "كان من مشاهير موالي الروم، تولى المناصب وترقى فيها حتى جعله السلطان محمد بن عثمان معلما لنفسه". حج وجاور بمكة. له "حاشية" على "حاشية" السيد علي بن محمد الجرجاني على الكشاف، في التفسير (١).

صدر الدين الكبير [٨٢٨ - ٩٠٣ هـ / ١٤٢٥ - ١٤٩٨ م]

<span data-type="title" id=toc-1119>محمد بن إبراهيم بن محمد بن إسحاق، يتصل نسبه بالإمام زين العابدين:</span>
فقيه إمامي، له اشتغال بالتفسير، من أهل شيراز. اشتهر بقوة العارضة، وكان له منصب الصدارة للسلطان "شاه طهماسب" الصفوي، وقتله التركمان في شيراز. يلقب بصدر الدين الكبير تمييزا عن الصدر الشيرازي الآتية ترجمته. من تصانيفه "حاشية" على الكشاف في التفسير (٢).
_________
(١) الكواكب السائرة ١: ٢٤ والشذرات ٨: ٩ وهدية العارفين ٢: ٢١٨ وكشف الظنون ٢: ١٤٧٩ وعثمانلي مؤلفلري ١: ٢٩٤ وفهرس دار الكتب ١: ١٧٤.
(٢) الأعلام ٥: ٣٠٢ وشهداء الفضيلة ١٠٠.','2','469',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (819,'ابن الحنبلي [٩٠٨ - ٩٧١ هـ / ١٥٠٢ - ١٥٦٣ م]

<span data-type="title" id=toc-1120>محمد بن إبراهيم بن يوسف بن عبد الرحمن الحلبي القادري التاذفي، رضي الدين ابن الحنبلي، أبو عبد الله:</span>
مؤرخ، أديب، له اشتغال بالتفسير، ومشارك في عدة علوم، من أهل حلب مولدا ووفاة. دخل دمشق وانتفع به جماعة، وحج. قال في الشذرات: كان إماما بارعا مفننا مسندا مصنفا ... " له نيف وخمسون مصنفا، منها "تعليقة" على أنوار التنزيل في التفسير، للبيضاوي (١).

الصدر الشيرازي [... - ١٠٥٩ هـ / ... - ١٦٤٩ م]

<span data-type="title" id=toc-1121>محمد بن إبراهيم بن يحيى القوامي الشيرازي، الملا صدر الدين:</span>
فيلسوف، من القائلين بوحدة الوجود، من أهل شيراز. كان يعرف بالأخوند (الأستاذ). رحل إلى أصبهان وتعلم فيها. وتوفي بالبصرة، وهو متوجه إلى مكة حاجا. من كتبه "أسرار الآيات
_________
(١) شذرات الذهب ٨: ٣٦٠ ونهر الذهب ١: ٨ وهدية العارفين ٢: ٢٤٨ وكشف الظنون ١٩٣ والأعلام ٥: ٣٠٢ ومعجم المؤلفين ٨: ٢٢٣.','2','470',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (820,'وأنوار البينات" جزء في التفسير، طبع. و "تفسير آية الكرسي" طبع و "تفسير آية النور" طبع و "تفسير سورة الجمعة" طبع و "تفسير سورة الطارق" طبع و "تفسير سورة الواقعة" طبع (١).

ابن الصائغ [... - ١٠٦٦ هـ / ... - ١٦٥٦ م]

<span data-type="title" id=toc-1122>محمد بن إبراهيم الدروري، سري الدين، المعروف بابن الصائغ:</span>
فقيه حنفي، له اشتغال بالتفسير، كان يجيد الفارسية والتركية، من أهل مصر، درس بالمدرسة السليمانية والمدرسة الصرغمتشية. ورحل إلى بلاد الروم بطلب من شيخ الإسلام، ووجه إليه رتبة قضاء القدس. مات بالقاهرة، من كتبه "حاشية" على تفسير البيضاوي، و "رسالة" في تحقيق تفسير بعض الآيات (٢).
_________
(١) فوائد الرضوية ٣٧٨ وأعيان الشيعة ٤٥: ٩٩ وسلافة العصر ٤٩٩ وروضات الجنات ٣٣١ ومعجم المطبوعات ١١٧٤ وهدية العارفين ٢: ٢٧٩ والأعلام ٥: ٣٠٣ وإيضاح المكنون، ٧، ٣٠٢، ٣٠٦، ٣٠٨ ومعجم المؤلفين ٨: ٢٠٣ ومجلة المجمع العلمي ٩: ٦٦١ و ٧٢٣ و ١٠: ٢٩ والذريعة ٢: ٣٩ و ٢٧٩ والتيمورية ٣: ١٧٠.
(٢) هدية العارفين ٢: ٢٨٧ وخلاصة الأثر ٣: ٣١٧ ومعجم المؤلفين ٨: ١٩٨ وإيضاح المكنون ١: ١٣٩.','2','470',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (821,'ابن القصير [١٠١١ - ١٠٩٣ هـ / ١٦٠٢ - ١٦٨٢ م]

<span data-type="title" id=toc-1123>محمد بن إبراهيم، شمس الدين ابن القصير:</span>
فقيه شافعي، متكلم، له اشتغال بالتفسير، من أهل "حمص" بسورية، ولي الإفتاء بها نحو سبعة وأربعين سنة وتوفي بدمشق. من آثاره "أجوبة" عن أسئلة سئل عنها في التفسير والفقه بحلب ودمشق. قال صاحب خلاصة الأثر: "رأيتها وانتخبت منها أشياء نفيسة" (١).

الأبراشي [... - بعد ١٢٥٠ هـ / ... - بعد ١٨٣٤ م]

<span data-type="title" id=toc-1124>محمد بن إبراهيم الأبراشي:</span>
موقت شافعي مصري، نحوي، مفسر، له "تفسير سورة القدر" مخطوط، في الأزهرية فرغ منه سنة ١٢٥٠ هـ (٢).

الحسيني [١٢٧٠ - ١٣٥٩ هـ / ١٨٥٤ - ١٩٤٠ م]

<span data-type="title" id=toc-1125>محمد بن إبراهيم الحسيني:</span>
مفسر، من أعيان طرابلس الشام وعلمائها،
_________
(١) خلاصة الأثر ٣: ٣٢١ والأعلام ٥: ٣٠٣
(٢) إيضاح المكنون ١: ١٣٩ ومعجم المؤلفين ٨: ١٩٠ والأزهريه ٦: ٢٩٢.','2','471',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (822,'مولده ووفاته فيها، تعلم بالأزهر في القاهرة. من آثاره "تفسير الحسيني" طبع الأول منه، سنة ١٣٣٢ وينتهي إلى آخر سورة البقرة (١).

اين كيسان [... - ٢٩٩ هـ / ... - ٩١٢ م]

<span data-type="title" id=toc-1126>محمد بن أحمد بن إبراهيم، أبو الحسن، المعروف بابن كيسان:</span>
من العلماء بالنحو واللغة، مفسر، من أهل بغداد. أخذ عن المبرد وثعلب، فحفظ المذهبين البصري والكوفي، قال ياقوت: "لكنه إلى مذهب البصرين أميل". وقال ابن الأنباري: "خلط المذهين فلم يضبط منهما شيئا". قيل في وفاته سنة ٢٩٩ وقيل سنة ٣٢٠ هـ، والأول أشهر. من كتبه "معاني القرآن". قال الزركلي: "وفي خزانة شستربتي (رقم ٣٥٣٨) "المصابيح في تفسير القرآن العظيم" لأبي الحسن محمد بن أحمد بن كيسان". قلت: ليس لهذا الكتاب ذكر في مصادر ترجمته، إنما ذكر له كتاب "مصابيح الكتاب" فهل يكون هو؟ ومما يؤكد أن لصاحب
_________
(١) مجلة المجمع العلمي العربي ١٦: ٤٨٠ وأعلام الأدب والفن ٢: ٣٤٧ ومعجم المطبوعات ٧٧٤.','2','471',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (823,'الترجمة كتاب في التفسير، ما ذكره الصفدي عن ابن مجاهد المقرىء وهو قوله: " .. وله التصانيف والأقوال المشهورة في التفاسير ومعاني الآيات .. " (١).

التميمي [٢٥٥ - ٣٠٥ هـ / ٨٦٩ - ٩١٧ م]

<span data-type="title" id=toc-1127>محمد بن أحمد بن عبد الله بن بكير التميمي، أبو بكر، ويقال أبو بكير:</span>
فقيه مالكي، جدلي، قاض، كان أحذق الناس بمذهب مالك. من أهل بغداد، له كتب منها كتاب في "أحكام القرآن" (٢).

ابن الخياط [... - ٣٢٠ هـ / ... - ٩٣٢ م]

<span data-type="title" id=toc-1128>محمد بن أحمد بن منصور، أبو بكر ابن الخياط:</span>
نحوي، أصله من
_________
(١) تاريخ بغداد ١: ٣٣٥ وتاريخ ابن كثير ١١: ١١٧ وتاريخ أبي الفداء٢: ١٦٠ وطبقات المفسرين للداودي ٢: ٥٣ ونزهة الألباء ٢٣٥ ومعجم الأدباء ١٧: ١٣٧ والوافي ٢: ٣١ والشذرات ٢: ٢٣٢ وروضات الجنات ٦٠٠ ومرآة الجنان ٢: ٢٣٦ وإنباه الرواة ٣: ٥٧ والنجوم الزاهرة ٣: ١٧٨ وبغية الوعاة ١: ١٨.
(٢) الوافي بالوفيات ٢: ٥٩ والديباج ٢١٣.','2','472',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (824,'سمرقند، أقام ببغداد، وجرت بينه وبين الزجاج مناظرة، وكان يخلط نحو البصريين بالكوفيين. مات بالبصرة، من كتبه "معاني القرآن" (١)

الصفواني [... - بعد ٣٤٦ هـ / ... - بعد ٩٥٧ م]

<span data-type="title" id=toc-1129>محمد بن أحمد بن عبد الله بن قضاعة، أبو عبد الله، الصفواني:</span>
من علماء الشريعة الإمامية. نسبته إلى صفوان بن مهران. قال ابن النديم: "لقيته سنة ٣٤٦ هـ، وكان يزعم أنه لا يقرأ ولا يكتب". ذكر له صاحب هدية العارفين كتاب "ثواب القرآن" و "جوامع التفسير" (٢).

العسال [٢٦٩ - ٣٤٩ هـ / ٨٨٣ - ٩٦٠ م]

<span data-type="title" id=toc-1130>محمد بن أحمد بن إبراهيم بن سليمان بن محمد الأصبهاني، أبو (أحمد، المعروف بالعسال)</span>
_________
(١) الوافي ٢: ٨٨ وبغية الوعاة ١: ٤٨ ومعجم الأدباء ١٧: ١٤١ ونزهة الألباء ٣٤٧ وإنباه الرواة ٣: ٥٤ وكشف الظنون ١٧٣٠ وطبقات المفسرين ٢: ٨٤.
(٢) ابن النديم ٢١٧ وروضات الجنات ٥٥٤ وفوائد الرضوية ٣٨٨ وكتاب الرجال ٢٧٩ وفهرست الطوسي ١٣٣ ومنتهى المقال ٢٥٧ وهدية العارفين ٢: ٤٢.','2','472',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (825,'أحمد، المعروف بالعسال: محدث، حافظ، مفسر، قاض، مقرىء، من أهل أصبهان، وولي قضاءها. سمع بها وبهمذان وبغداد والكوفة والبصرة والحرمين وواسط والري وخوزستان. وحدث ببغداد. قال أبو نعيم: "هو من الكبار في المعرفة، والإتقان، والحفظ" وقال ابن منده: "كتبت عن ألف وسبعمائة شيخ فلم أر فيهم مثل العسال وأبي إسحاق بن حمزة". من كتبه " تفسير القرآن" (١).

الذهلي [٢٧٩ - ٣٦٧ هـ / ٨٩٣ - ٩٧٨ م]

<span data-type="title" id=toc-1131>محمد بن أحمد بن عبد الله بن نصر ابن بجير الذهلي السدوسي البصري البغدادي، أبو الطاهر:</span>
محدث، مفسر، شاعر، مناظر قوي الحجة، قاض. من فقهاء المالكية، أصله من البصرة، ومولده ببغداد. تولى قضاء البصرة وواسط نيابة عن والده. ثم ولي قضاء "مدينة المنصور" نحو أربعة أشهر
_________
(١) الوافي ٢: ٤١ وذكر أخبار أصبهان ٢: ٢٨٣ وتاريخ بغداد ١: ٢٧٠ وتذكرة الحفاظ ٣: ٨٨٦ والشذرات ٢: ٣٨٠ والنجوم الزاهرة ٣: ٣٢٥ والعبر ٢: ٢٨٢ وهديه العارفين ٢: ٤٣ وطبقات المفسرين ٢: ٥١.','2','473',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (826,'(سنة ٣٢٩ هـ). وقضاء الشرقية ببغداد نحو خسة أشهر (سنة ٣٣٤ هـ). ودخل مصر سنة ٣٤٠ هـ، وحج منها، وعاد إليها فولي القضاء بها سنة ٣٤٨ هـ، فاستمر إلى أن دخل جوهر الصقلي مصر، فأقره، وألزمه أن يحكم في المواريث والطلاق والهلال بقول الشيعة. ووصل "المعز" فخلع عليه وأبقاه على ولايته، وأشرك معه في القضاء علي بن النعمان. وأصيب بفالج، فصرف عن العمل سنة ٣٦٠ هـ، وأقام بمصر إلى أن توفي. وقيل إنه لم يعزل بل استعفى قبل موته بقليل وفي "قضاة دمشق" لابن طولون أنه ولي قضاء دمشق. له "مختصر تفسير الجياني" و "مختصر تفسير البلخي، (١).

الأزهري [٢٨٢ - ٣٧٠ هـ / ٨٩٥ - ١٩٨١ م]

<span data-type="title" id=toc-1132>محمد بن أحمد بن الأزهر بن طلحة ابن نوح بن أزهر، الأزهري الهروي، (أبو منصور)</span>
_________
(١) الوافي ٢: ٤٥ وتاريخ بغداد ١: ٣١٣ والديباج ٣١١ والولاة والقضاة ١٦٠ و ٥٨١ الملحق. وقضاة دمشق ٣٤ والعبر ٢: ٣٤٤ والشذرات ٣: ٦٠ والنجوم الزاهرة ٤: ١٣٠ وطبقات المفسرين للداودي ٢: ٦٨ وهديه العارفين ٢: ٤٨ والأعلام ٦: ٢٠٢ ومعجم المؤلفين ٨: ٢٨٤ وتاريخ التراث العربي ٢: ١٥٢.','2','473',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (827,'أبو منصور: إمام في اللغة والأدب، فقيه شافعي، مفسر، من أهل هراة مولدا ووفاة. عني بالفقه أولا، فأخذ عن علماء هراة وبغداد. ثم غلب عليه علم العربية، فرحل في طلبه وقصد القبائل وتوسع في أخبارهم. ووقع في إسار القرامطة، فكان مع فريق من هوازن "يتبعون مساقط الغيث أيام النجيع، ويرجعون إلى أعداد المياه في محاضرهم زمن القيظ، ويتكلمون بطباعهم البدوية، ولا يكاد يوجد في منطقهم لحن أو خطأ فاحش، قال: "فبقيت في أسرهم دهرا طويلا، واستفدت منهم ألفاظا جمة" كما قال في مقدمة كتابه "تهذيب اللغة". من آثاره الأخرى "التقريب في تفسير القرآن" و "فوائد" منقولة من تفسير للمزني مخطوط (١).
_________
(١) طبقات المفسرين للداودى ٢: ٦١ وطبقات الشافعية للسبكي ٣: ٦٣ وطبقات الشافعية لابن هداية الحسيني ٩٤ ومعجم الأدباء ١٧: ١٦٤ ووفيات الأعيان ٤: ٣٣٤ ونزهة الألباء ٢٢١ وبغية الوعاة ١: ١٩ والوافي بالوفيات ٢: ٤٥ وتذكرة الحفاظ ٣: ٩٦٠ واللباب (الأزهري) ومفتاح السعادة ١: ١١١ وروضات الجنات ١٧٥ وشذرات الذهب ٣: ٧٢ وهدية العارفين ٢: ٤٩ ومرآة الجنان ٢: ٣٩٥ وكشف الظنون ٤٢، و ٤٨، و ٤٦٥ والنجوم الزاهرة ٤: ١٣٩ وطبقات النحاة لابن قاضي شهبة ١: ٥ والأعلام ٦: ٢ والعبر ٢: ٣٥٦ ومقدمة تهذيب اللغة، ودائرة المعارف الإسلامية.','2','474',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (828,'المروزي [٣٠١ - ٣٧١ هـ / ٩١٤ - ٩٨١ م]

<span data-type="title" id=toc-1133>محمد بن أحمد بن عبد الله بن محمد، أبو عبد الله، وقيل أبو زيد الفاشاني المروزي:</span>
من أئمة فقهاء الشافعية، محدث، مفسر، نسبته إلى فاشان (من قرى هراة). ودخل بغداد وحدث بها، ثم خرج إلى مكة وجاور بها سبع سنين وحدث هناك بصحيح البخاري عن محمد بن يوسف الفربري، قال الخطيب البغدادي: "وأبو زيد أجل من روى هذا الكتاب." وقال الصفدي: "كان من الأئمة الأجلاء حسن النظر مشهورا بالعلم، حافظا للمذهب، وله فيه وجوه غريبة". ذكر له صاحب كشف الظنون كتاب "تفسير المسعودي" (١).

الشنبوذي [٣٠٠ - ٣٨٧ ب / ٩١٣ - ٩٩٧ م]

<span data-type="title" id=toc-1134>محمد بن أحمد بن إبراهيم، أبو الفرج الشنبوذي:</span>
عالم بالقراءات
_________
(١) تاريخ بغداد ١: ٣١٤ وطبقات الشافعية للسبكي ٢: ١٠٨ وطبقات الشيرازي ١١٥ وطبقات ابن هداية ٩٦ وطبقات العبادي ٩٣ والمنتظم ٧: ١١٢ والوافي بالوفيات ٢: ٧١ والعبر للذهبي ٢: ٣٦٠ وشذرات الذهب ٣: ٧٦ والعقد الثمين ١: ٢٩٧ وتبيين كذب المفترى ١٨٩ ووفيات الأعيان ٤: ٢٠٨ وهدية العارفين ٢: ٥٠ وكشف الظنون ١: ٤٥٨.','2','474',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (829,'والتفسير، من أهل بغداد. رحل ولقي الشيوخ، وقرأ على الحسن بن شنبوذ (وإليه نسب لكثرة ملازمته له)، وغيره. وكان يحفظ خمسين ألف بيت من الشعر شواهد للقرآن. قال الذهبي: "أكثر الترحال في طلب القراءات وتبحر فيها، واشتهر اسمه وطال عمره، وكان عالما بالتفسير وعلل القراءات" وقال أبو عمرو الداني: "مشهوز نبيل حافط ماهر حاذق، كان يتجول في البلدان" له "تفسير القرآن" لم يكمله، و "الإشارة في تلطيف العبارة" في علم القرآن (١).

المحاملي [... - ٤٠٧ هـ / ... - ١٠١٦ م]

<span data-type="title" id=toc-1135>محمد بن أحمد بن القاسم الضبي، أبو الحسن، ويقال أبو الحسن، المحاملي:</span>
فقيه شافعي، مفسر، من
_________
(١) الوافي ٢: ٣٩ وطبقات المفسرين للداودي ٢: ٥٤ وتذكرة الحفاظ ٣: ١٠٢ والمنتظم ٧: ٢٠٤ والعبر ٣: ٤٠ وغاية النهاية ٢: ٥٠ ومعجم الأدباء ١٧: ١٧٣ والنجوم الزاهرة ٤: ١٩٩ واللباب ٢: ٣٠ وطبقات المفسرين للسيوطي ٣٧ وطبقات القراء للذهبي ١: ٢٦٨ ومعجم المؤلفين ٨: ٢٢٦ وتاريخ بغداد ١: ٢٧١ والشذرات ٣: ١٠٩ ووفاته في بعض هذه المصادر سنة ٣٨٨ هـ.','2','475',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (830,'أهل بغداد، سمع من أبي زيد المروزي (السابقة ترجمته) وغيره. وهو من شيوخ سليم الرازي. والمحاملي نسبة إلى المحامل التي يحمل عليها الناس في السفر. من آثاره "تفسير الصحابة" قال الثعلبي: "قرأته كله عل مصنفه" و "تفسيرالنبي - صلى الله عليه وسلم - " قال الثعلبي: "سمعت بعضه من مصنفه وأجازني بالباقي" (١).

التجيبي [... - ٤١٩ هـ / ... - ١٠٢٨ م]

<span data-type="title" id=toc-1136>محمد بن أحمد بن عبد الرحمن بن محمد بن عبد الرحمن بن صمادح التجيبي، أبو يحيى:</span>
وال أندلسي، عارف باللغة، من أهل سرقسطة. كان واليا على وشقة (في شمال شرقي الأندلس)، ثم تخلى عنها لابن عمه منذر بن يحيى التجيبي. قال ابن الأبار: "كان مع رياسته من أهل العلم والأدب والفضل، وله اختصار في "غريب القرآن" (طبع، جزآن) إستخرجه من تفسير الطبري، ورواه
_________
(١) كشف الظنون ٤٥٢ و ٤٥٩ والشذرات ٣: ١٨٥ وهدية العارفين ٢: ٦٠ ومعجم المؤلفين ٨: ١٠٣ وويفات الأعيان ٤: ٢٠٨ ترجمة المروزي.','2','475',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (831,'عنه ابنه أبو الأحوص معن بن محمد أمير المرية". مات غرقا في البحر وهو في طريقه لأداء فريضة الحج (١).

ابن مطرف [٣٨٧ - ٤٥٤ هـ / ٩٩٧ - ١٠٦٢ م]

<span data-type="title" id=toc-1137>محمد بن أحمد بن مطرف الكناني، أبو عبد الله، ويعرف بالطرفي:</span>
عالم بالقراءات، عارف باللغة، من أهل قرطبة. قال ابن الجزري: "عرف بالطرفي لكونه يؤم بمسجد طرفة بقرطة، وكان عجبا في القراءات، أخذ الناس عنه كثيرا. له كتاب "القرطين" طبع، جمع فيه بين كتابي "غريب القرآن" و "مشكل القرآن" لابن قتيبة (٢).

ابن اللجالش [... - ٤٩٠ هـ / ... - ١٠٩٧ م]

<span data-type="title" id=toc-1138>محمد بن أحمد بن عبد الله المري، أبو عبد الله، ويعرف بابن اللجالش:</span>
نحوي، أصولي، فقيه مالكي، له
_________
(١) التكملة لابن الأبار ١: ٣٨٢ ت ١٠٦١.
(٢) الصلة ٢: ٥٣٨ وغاية النهاية ٢: ٧٩ وكتاب القرطين مقدمته، ومقدمة الناشر. وتاريخ علماء الأندلس ٢: ١١٤ والأعلام ٥: ٣١٤.','2','476',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (832,'اشتغال في التفسير، من أهل المرية بالأندلس. رحل إلى المشرق واستوطن مكة، وأخذ عن الجويني وكريمة المروزية، وأخذ الناس عنه هنالك. له "مختصر تفسير الطبري" (١).

الزهري [... - ٦٢٧ هـ / ... - ١٢٢٠ م]

<span data-type="title" id=toc-1139>محمد بن أحمد بن سليمان بن أحمد ابن إبراهيم الزهري الإشبيلي الأندلسي، أبو عبد الله:</span>
عالم بالأدب، نحوي، له اشتغال بالتفسير. ولد بمالقة، وطاف الأندلس، ثم رحل، فدخل مصر قبل سنة ٥٩٠ هـ، فسمع الحديث بها. ودخل الشام وبلاد الجزيرة، وقدم بغداد سنة ٥٩٠ هـ وعمره ثلاثون سنة، وأقام بها مدة، وسمع من شيوخها. ثم دخل أصبهان وبلاد الجبل. وسكن الكرج - جيورجيا اليوم - ثم انتقل إلى بروجرد، وأقرأ بها الأدب. وتوفي شهيدا، قتله التتار في بروجرد في شهر رجب. من كتبه
_________
(١) الصلة ٢: ٥٦٣','2','476',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (833,'"البيان فيما أبهم من الأسماء في القرآن" (١).

ابن الخشاب [... - نحو ٦٥٠ هـ / ... - نحو ١٢٥٢ م]

<span data-type="title" id=toc-1140>محمد بن أحمد بن سهل الخزرجي اليماني المعروف بابن الخشاب:</span>
فاضل، له اشتغال بالتفسير، من أهل اليمن. من آثاره "الدر النظيم في خواص القرآن العظيم" مخطوط، في مكتبة الأوقاف ببغداد. قال صاحب كشف الظنون: "أوله: الحمد لله الذي أطلع من آفاق كتابه العزيز ... " ذكر أنه جمع فيه بين كتاب البرق اللامع" للوادياشي (أنظر ترجمته) وبين كتاب الغزالي في خواص فواتح السور وآيات من القرآن، وأورد في أوله فصول في فضائل القرآن وتلاوته ودعاء الختم وفضل البسملة وآداب القراءة، ثم بدأ بذكر خواص الفاتحة والبقرة إلى آخر القرآن الكريم. ولهذه النسخة مختصر منسوب إلى اليافعي". وقال آغا بزرك الطهراني، في تعليقه عل كشف الظنون:
_________
(١) بغية الوعاة ١: ٢٥ والوافي بالوفيات ٢: ١٠٤ ونفح الطيب ٢: ٢١٤ ومعجم الأدباء ١٧: ٢٧٧ وكشف الظنون ٢٦٣ ومعجم المؤلفين ٨: ٢٦٠ و ١٠: ٤٧ والأعلام ٧: ١٨ وهدية العارفين ٢: ١١٠.','2','477',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (834,'"وفي نسخة الحاج عماد الموقوفة للخزانة الرضوية أنه (أي الدر النظيم) للشيخ أبي محمد عبد الله بن أحمد بن عبيد الله الحوري المتوفى سنة ٥٦٧ هـ. وفيه أنه جمعه من كتاب "البرق اللامع والغيث الهامع" للقاضي أبي بكر الغساني، ومن خواص آيات القرآن وفواتح السور للإمام أبي حامد حجة الإسلام. وقال آغا بزرك: كذا ترجمه السيوطي ص ٢٧٦ وعنه الزركلي". قلت: هذه الترجمة التي ذكرها آغا بزرك غير موجودة - كما ذكرت هنا - في بغية الوعاة أو الأعلام، إنما توجد ترجمة لأبي محمد عبد الله بن أحمد بن أحمد بن أحمد بن عبيد الله بن نصر، ابن الخشاب، المتوفى سنة ٥٦٧ هـ (انظر ترجمته) وهي تختلف عن ترجمة الخزانة الرضوية باسم الجد وما فوق والنسبة، فليحرر (١).

ابن الموقع [٦٢٣ - ٦٥٦ هـ / ١٢٢٦ - ١٢٥٨ م]

<span data-type="title" id=toc-1141>محمد بن أحمد بن محمد بن أحمد بن الحسين الموصلي، أبو عبد الله، (المعروف بشعلة، ويقال له ابن الموقع)</span>
_________
(١) بروكلمان ١: ٤١٤ وفيه وفاته نحو ٦٥٠ هـ وهدية العارفين ٢: ٩٧ وفيه وفاته سنة ٥٦٧ ومثله في كشف الظنون ٧٣٦ ومعجم المؤلفين ٨: ٢٦٨ و ٢٨٨ والأعلام ٥: ٣٢١ والكشاف لطلس ١٦٨.','2','477',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (835,'المعروف بشعلة، ويقال له ابن الموقع: عارف بالقراءات، من فقهاء الحنابلة، له نظم، كان أبوه موقعا عند "خير بك" كافل حلب. وهاجر محمد إلى القاهرة بعد زوال الدولة الجركسية، وتوفي بالموصل. قال الذهبي: "كان شابا فاضلا، ومقرئا محققا، ذا ذكاء مفرط، وفهم ثاقب، ومعرفة تامة بالعربية واللغة، وشعره في غاية الجودة، نظم في الفقه وفي التاريخ وغيره، وكان، مع فرط ذكائه، صالحا زاهدا متواضعا .. " من كتبه "الناسخ والمنسوخ" في القرآن الكريم، قال ابن رجب: "وكلامه فيه يدل على تحقيقه وعلمه" (١).

القرنبى [... - ٦٥٦ هـ / ... - ١٢٥٨ م]

<span data-type="title" id=toc-1142>محمد بن أحمد بن محمد بن عبد المجيد القرنبي، سراج الدين:</span>
من أئمة فقهاه الحنفية، تفقه ببخارى، وتخرج به علماء. قال صاحب "الجواهر المضية": كان هذا الرجل حافظا
_________
(١) ذيل طبقات الحنابلة ٢: ٢٥٦ والشذرات ٥: ٢٨١ والوافي بالوفيات ٢: ١٢٢ وغاية النهاية ٢: ٨٠ والأعلام ٦: ٢١٧ والمنهج الأحمد.','2','478',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (836,'واعظا، مفتيا، مفسرا، مدققا، محققا، توفي ببخارى ودفن بمقبرة أهل الجنة ظاهر كلاباذ" (١).

ابن سراقة [٥٩٢ - ٦٦٢ هـ / ١١٩٦ - ١٢٦٤ م]

<span data-type="title" id=toc-1143>محمد بن أحمد بن محمد بن إبراهيم ابن الحسين، أبو بكر، محيي الدين الأنصاري الشاطبي، المعروف بابن سراقة:</span>
شيخ دار الحديث الكاملية، بالقاهرة، فقيه مالكي، أديب، صوفي، له شعر. ولد في شاطبة بالأندلس وسمع بها من أبي القاسم أحمد ابن بقي، ثم رحل في طلب الحديث، فسمع ببغداد وحلب. وتولى مشيخة دار الحديث البهائية بحلب، ثم قدم مصر وتولى مشيخة دار الحديث الكاملية بالقاهرة بعد وفاة ابن سهل القصري سنة ٦٤٢ هـ، فاستمر إلى أن توفي، ودفن بسفح المقطم. قال الصفدي: "كان أحد الأئمة المشهورين بغزارة الفضل وكثرة العلم والجلالة والنبل وأحد المشايخ المعروفين بطريق القوم، وله في ذلك إشارات لطيفة مع ما جبل
_________
(١) طبقات المفسرين ٢: ٧٦ والمشتبه ٢: ٥٠٦ وتبصير المنتبه ٣: ١١٠٦ والجواهر المضية ٢: ٢٢.','2','478',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (837,'عليه من كرم الأخلاق واطراح التكلف ورقة الطبع وفي الجانب، من آثاره "إعجاز القرآن" (١).

القرطبي [... - ٦٧١ هـ ... / ١٢٧٣ م]

<span data-type="title" id=toc-1144>محمد بن أحمد بن أبي بكر بن فرح الأنصاري الخزرجي الأندلسي، أبو عبد الله القرطبي:</span>
من كبار المفسرين، محدث، صالح متعبد، من أهل قرطبة بالأندلس، رحل إلى المشرق واستقر بمنية ابن خصيب في شمالي أسيوط، بمصر، وتوفي فيها. قال الذهبي: "إمام متفنن، متبحر في العلم، له تصانيف مفيدة، تدل على كثرة اطلاعه ووفور عقله وفضله، وقد سارت
_________
(١) الوافي بالوفيات ١: ٢٠٨ وذيل الروضتين ٢٣٠، ونفح الطيب ٢: ٦٣ وشذرات الذهب ٥: ٣١٠ والنجوم الزاهرة ٧: ٢١٦ وفوات الوفيات ٣: ٢٤٥ وابن الشعار ٧: ٧٨ ومرآة الجنات ٤: ١٦٠ والبداية والنهاية ١٣: ٢٤٣ وحسن المحاضرة ١: ٢١٠ والزركشي ٢٠١ وهدية العارفين ٢: ١٢٧ قلت: ورد اسمه في هذه المصادر: محمد بن محمد بن إبراهيم ... ثم ظفر خير الدين الزركلي صاحب الأعلام، بخطه (أي صاحب الترجمة) على الجزء الحادي والعشرين من كتاب التكملة لوفيات النقلة، وسمي نفسه محمد بن أحمد بن محمد، فصححه في الأعلام، كما اعتدته هنا.','2','479',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (838,'بتفسيره العظييم الشأن الركبان" وقال أيضا "رحل وكتب وسمع، وكان يقظا فهما حسن الحفظ مليح النظم حسن المذاكرة ثقة حافظا". من آثاره "جامع أحكام القرآن والمبين لما تضمنه من السنة وآي القرآن (الفرتان) " طبع، عشرون جزءا، ويعرف بتفسير القرطبي. قال ابن فرحون: "وهو من أجل التفاسير وأعظمها نفعا، أسقط منه القصص والتواريخ، وأثبت عوضها أحكام القرآن، واستنباط الأدلة، وذكر القراءات، والإعراب، والناسخ والمنسوخ" .. (١).

ابن سجمان الشريشي [٦٠١ - ٦٨٥ هـ / ١٢٠٤ - ١٢٨٦ م]

<span data-type="title" id=toc-1145>محمد بن أحمد بن محمد بن عبد الله ابن سجمان الوائلي البكري الشريشي، أبو بكر، جمال الدين:</span>
فقيه مالكي، نحوي، أصولي، مفسر، ولد
_________
(١) الوافي ٢: ١٢٢ وطبقات المفسرين للسيوطي ٢٨ وطبقات المفسرين للداودي ٢: ٦٥ ونفح الطيب ٢: ٢١٠ والشذرات ٥: ٣٣٥ والديباج ٢: ٣٠٨ وشجرة النور ١٩٧ والأعلام ٥: ٣٢٢ ومعجم المؤلفين ٨: ٢٤٠ وبروكلمان، التكملة ١: ٧٣٧ وهدية العارفين ٢: ١٢٩ وغاية النهاية ٢: ٨ وكشف الظنون ٥٣٤.','2','479',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (839,'بشريش، ورحل إلى المشرق، فسمع بالإسكندرية ودمشق وحلب وإربل وبغداد، وأقام بالقاهرة والقدس، ثم سكن دمشق، يفتي ويدرس، وطلب لقضائها فامتنع ورعا، وتوفي بها. تخرخ به ابن تيمية، والمزي، والذهبي، والبرزالي وغيرهم (١).

القسطلاني [٦١٤ - ٦٨٦ هـ / ١٢١٨ - ١٢٨٧ م]

<span data-type="title" id=toc-1146>محمد بن أحمد بن علي بن محمد القيسي الشاطبي، أبو بكر، قطب الدين التوزري القسطلاني:</span>
عالم بالحديث ورجاله، فقيه، مشارك في بعض العلوم، أصله من توزر (بإفريقية) من بلاد قسطيلية. ولد بمصر، ونشأ بمكة. وقام برحلة سنة ٦٤٩ هـ فسمع ببغداد والجزيرة والشام ومصر. وطلب من مكة، وفوضت له مشيخة دار الحديث الكاملية بالقاهرة إلى أن توفي. قال الصفدي: "درس وأفتى، وكان شفي عالما عاملا، زاهدا
_________
(١) طبقات المفسرين ٢: ٧٤ والوافي ٢: ١٣١ والنفح ٢: ١٣١ والشذرات ٥: ٣٩٢ وتاريخ علماء بغداد ١٧٧ والأعلام ٦: ٢١٩ وبغية الوعاة ١: ٤٤ وطبقات ابن قاضي شهبة ١: ١٩ وهدية العارفين ٢: ١٣٥.','2','480',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (840,'عابدا، جامعا للفضايل، كريم النفس، حسن الأخلاق، قليل المثل". من كتبه "تفسير آيات من القرآن الكريم" مخطوط (١).

الخويي [٦٢٦ - ٦٩٣ هـ / ١٢٢٩ - ١٢٩٤ م]

<span data-type="title" id=toc-1147>محمد بن أحمد بن خليل بن سعادة ابن جعفر الخويي شهاب الدين، أبو عبد الله:</span>
قاضي دمشق، وابن قاضيها، محدث، مفسر، أصولي، نحوي، من فقفاء الشافعية. ولد بدمشق، وبها نشأ وتعلم. وأجاز له جماعة من أصبهان وبغداد ومصر والشام. ولي قضاء القدس سنة ٦٥٧ هـ، ثم قضاء المحلة وبهنسة (بمصر) ثم قضاء حلب، فقضاء الديار المصرية، فقضاء الشام. وتوفي بدمشق. والخويي نسبة إلى "خوى" من أعماله أذربيجان. له "أقاليم التعاليم" في الفنون السبعة التفسير
_________
(١) الوافي ٢: ١٣٢ والشذرات ٥: ٣٩٧ والرسالة المستطرفة ٩٢ وطبقات السبكي ٥: ١٨ والنجوم الزاهرة ٧: ٣٧٣ وفوات الوفيات وحسن المحاضرة ١: ٢٣٦ وبروكلمان: التكملة ١: ٨٠٩ والأعلام ٦: ٢١٩ وتاريخ علماء بغداد ١٧٣.','2','480',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (841,'والحديث والفقه والأدب والطب والهندسة والحساب" (١).

الوادي آشي [... - ٦٩٤ هـ / ... - ١٢٩٥ م]

<span data-type="title" id=toc-1148>محمد بن أحمد بن محمد الغساني الوادي آشي، أبو بكر:</span>
فقيه مالكي، عارف بعلوم القرآن، من أهل وادي آش بالأندلس. من كتبه "البرق اللامع والغيث الهامع" في فضائل القرآن العظيم والفرقان الحكيم. قال صاحب كشف الظنون: "لخص فيه زبدة ما في كتب فضائل القرآن العظيم وخواصها وعدد الآيات والحروف" (٢).

ابن القماح [٦٥٦ - ٧٤١ هـ / ١٢٥٨ - ١٣٤٠ م]

<span data-type="title" id=toc-1149>محمد بن أحمد بن إبراهيم بن حيدرة ابن علي، أبو عبد الله، ابن القماح القرشي المصري:</span>
مفسر، من فقهاء الشافعية، من أهل مصر. أفتى ودرس
_________
(١) الوافي ٢: ١٣٧ وفوات الوفيات وقضاة دمشق ٧٩ والأنس الجليل ٢: ٤٦٦ وحسن المحاضرة ١: ٣١٣ وروضات الجنات ٢٠٣ وبغية الوعاة ١: ٢٣ والشذرات ٥: ٤٢٣ واسمه فيه أحمد والصواب محمد.
(٢) هدية العارفين ٢: ١٣٧ وكشف الظنون ١: ٢٣٩.','2','481',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (842,'وحدث، وناب في الحكم بجامع الصالح بالقاهرة، ونسب إليه التساهل في الأحكام، فامتنع عز الدين بن جماعة من استنابته، فأقبل على تدريس الفقه إلى أن مات. قال الذهبي: "كان آية في حفظ القرآن الكريم وفي الذكاء مشكورا في الفتاوى" من آثاره كتاب في "تفسير القرآن" (١).

ابن جزي الكلبي [٦٩٣ - ٧٤١ هـ / ١٢٩٤ - ١٣٤٠ م]

<span data-type="title" id=toc-1150>محمد بن أحمد بن محمد بن عبد الله ابن يحيى، بن جزي الكلبي، أبو القاسم:</span>
فقيه مالكي، عالم بالأصول والتفسير واللغة، من أهل غرناطة، ومن شيوخ لسان الدين ابن الخطيب. فقد وهو يحرض الناس يوم معركة طريف. من كتبه "التسهيل لعلوم التنزيل" في التفسير، طبع، أربعة أجزاء (٢).
_________
(١) الوافي ٢: ١٥٠ والسبكي ٥: ٢١٢ والدرر الكامنة وأعيان العصر ورقة ١٤٥ والأعلام ٦: ٢٢١.
(٢) الدرر الكامنة ٣: ٤٦، وطبقات المفسرين ٢: ٨١ والديباج المذهب ٢٩٥ وغاية النهاية ٢: ٨٣ وأزهار الرياض ٣: ١٨٤ ونفح الطيب وإيضاح المكنون ٢٨٨.','2','481',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (843,'ابن قدامة المقدسي ٧٠٥ - ٧٤٤ هـ / ١٣٠٥ - ١٣٤٣ م]

<span data-type="title" id=toc-1151>محمد بن أحمد بن عبد الهادي بن عبد الحميد بن عبد الهادي بن يوسف، شمس الدين، أبو عبد الله، ابن قدامة المقدسي الجماعيلي الأصل، ثم الدمشقي الصالحي:</span>
حافظ للحديث، عارف برجاله، من كبار الحنابلة، مهر في الفقه والأصول والعربية والتفسير، قال الصفدي: "لو عاش لكان آية، كنت إذا لقيته سألته عن مسائل أدبية وقواعد عربية فينحدر كالسيل، وكنت أراه يوافق المزي في أسماء الرجال ويرد عليه فيقبل منه". من تصانيفه الكثيرة " التفسير المسند" لم يكمله، و "جزء" في قوله تعالى: {لمسجد أسس على التقوى} (٩: ١٠٨). توفي بدمشق ودفن بسفح قاسيون (١).
_________
(١) طبقات المفسرين للداودي ٢: ٧٩ وتذكرة الحفاظ ٤: ١٥٠٨ والوافي ٢: ١٦١ والشذرات ٦: ١٦٣ وهدية العارفين ٢: ١٥١ والبدر الطالع ٢: ١٠٨ والدرر الكامنة ٣: ٤٢١ والذيل على طبقات الحنابلة ٢: ٤٣٦ والبداية والنهاية ١٤: ٢١٠ والدارس ٢: ٨٨ وبغية الوعاة ١: ٢٩ والأعلام ٦: ٢٢٢ ومعجم المؤلفين ٨: ٢٨٧.','2','482',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (844,'ابن اللبان [٦٧٩ - ٧٤٩ هـ / ١٢٨١ - ١٣٤٨ م]

<span data-type="title" id=toc-1152>محمد بن أحمد بن عبد المؤمن الإسعردي الدمشقي، شمس الدين، أبو عبد الله ابن اللبان:</span>
مفسر، محدث، من علماء العربية. ولد بدمشق، وسمع بها من اليونيني والدمياطي وغيرهما، وانتقل إلى مصر، فسمع بالإسكندرية والقاهرة، وحج، وعاد فاستقر بالقاهرة ودرس بقبة الإمام الشافعي، وبالخشابية. في أكثر مصادر ترجمته: مولده سنة ٦٨٥، إلا أن المحدث شهاب الدين بن أيبك خرج له جزءا وحدث به، وسأله عن مولده فقال: في العشر الأخير من شوال سنة تسع وسبعين وستمائة بدمشق". من كتبه "رد معاني الآيات المتشابهات إلى معاني الآيات المحكمات" فى التفسير، طبع. و "تفسير القرآن" يوجد الجزء الأول منه (١).
_________
(١) طبقات المفسرين للداودي ٢: ٧٦ والوافي ٢: ١٦٨ وهدية العارفين ٢: ١٥٥ والشذرات ٦: ١٦٣ ومعجم المطبوعات ٢٢٩ والسبكي ٥: ٢١٣ وحسن المحاضرة ١: ٤٢٨ ومرآة الجنان ٤: ٣٣٣ والدرر الكامنة ٣: ٤٢٠ وذيل العبر ٢٧١ وكشف الظنون ٨٣٧ والأعلام ٦: ٢٢٣ ومعجم المؤلفين ٨: ٢٨٦ وذيل تذكرة الحفاظ ١٢١.','2','482',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (845,'ابن المنفلوطي [٧١٣ - ٧٧٤ هـ / ١٣١٣ - ١٣٧٢ م]

<span data-type="title" id=toc-1153>محمد بن أحمد بن إبراهيم بن يوسف، أبو عبد الله، ولي الدين، العثماني الديباجي، المعروف بابن المنفلوطي وبالملوي:</span>
فقيه شافعي، أصولي، مفسر، صوفي. نشأ بدمشق وسمع بها من الحجار وأسماء بنت صصرى، ثم توجه إلى بلاد الروم وأخذ عن جماعة من علمائها. وعاد، فطلب إلى الديار المصرية في أيام الناصر القلاووني حسن بن محمد (من ملوك الدولة القلاوونية بمصر والشام) ودرس بالمدرسة التي أنشأها، ودرس التفسير بالمنصورية وغيرهما. قال ابن حجر: كان فاضلا متواضعا، عارفا بالتفسير والفقه والأصلين والتصوف" (١).

الوانوغي [٧٥٩ - ٨١٩ هـ / ١٣٥٧ - ١٤١٦ م]

<span data-type="title" id=toc-1154>محمد بن أحمد بن عثمان بن عمر، أبو عبد الله، التونسي الوانوغي:</span>
عالم بالتفسير والأصلين والعربية والفرائض
_________
(١) طبقات المفسرين للداودي ٢: ٥٨ والشذرات ٦: ٢٣٣ وإنباء الغمر ١: ٤٦ والدرر الكامنة ٣: ٣٩٥ وهدية العارفين ٢: ١٦٦.','2','483',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (846,'والحساب. ولد بتونس، وأخذ عن علمائها. ومات بمكة. قال السيوطي: "كان شديد الذكاء، سريع الفهم، حسن الإيراد للتدريس والفتوى، يعاب عليه إطلاق لسانه في العلماء، ومراعاة السائلين في الافتاء" (١).

ابن مرزوق الحفيد [٧٦٦ - ٨٤٢ هـ /١٣٢٤ - ١٤٣٨ م]

<span data-type="title" id=toc-1155>محمد بن أحمد بن محمد بن أحمد بن محمد بن أبي بكر بن مرزوق، العجيسي التلمساني، أبو عبد الله، المعروف بالحفيد، أو حفيد ابن مرزوق:</span>
فقيه، حجة في المذهب المالكي، عالم بالأصول، حافظ للحديث، مفسر، نحوي، ناظم، ولد بتلمسان، وبها نشأ وتعلم. ورحل إلى تونس وفاس، ثم دخل القاهرة فلقي بها العلامة ابن خلدون والفيروز آبادي والنويري صاحب "نهاية االأرب" وأخذ عنهم. حج سنة ٧٩٠ هـ رفقة الإمام ابن عرفة، وحج ثانية سنة ٨١٩ هـ فلقي شيخ الإسلام ابن حجر
_________
(١) بغية الوعاة ١: ٣١ والضوء اللامع ٧: ٣ ونيل الابتهاج ٢٨٦ وشذرات الذهب ٧: ١٣٨.','2','483',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (847,'العسقلاني وأخذ عنه. مات بتلمسان. له تصانيف كثيرة منها "تفسير سورة الإخلاص" على طريقة الحكماء. و "إغتنام الفرصة في محادثة عالم قفصة" في الفقه والتفسير (١).

ابن الضياء [٧٨٩ - ٨٥٤ هـ /١٣٨٧ - ١٤٥٠ م]

<span data-type="title" id=toc-1156>محمد بن أحمد بن الضياء محمد القرشي العمري المكي، بهاء الدين أبو البقاء، المعروف بابن الضياء:</span>
قاض، مفسر، من فقهاء الحنفية، مشارك في بعض العلوم، صاغاني الأصل، ولد بمكة، وأخذ عن علمائها، وولي قضاءها. قال السيوطي: "وانفرد بالشيخونية في مذهبه في بلاد الحجاز." مات بمكة. من تصانيفه "تفسير القرآن" (٢).
_________
(١) أعلام الجزائر ٢٩٠ والبستان ٢٠١ ونيل الابتهاج ٢٩٣ والضوء اللامع ٧: ٥٠ وهدية العارفين ٢: ١٩١ وتاريخ الجزائر العام ٢: ٢١٠ وفهرس الفهارس ١: ٣٩٤ والأعلام ٦: ٢٢٨ والبدر الطالع ٢: ١١٩ ودليل مؤرخ المغرب ١٨٧ و ٢٣٣ ومعجم المؤلفين ٨: ٣١٧ وشجرة النور ٢٥٣.
(٢) طبقات الداودي ٢: ٧٥ ونظم العقيان ١٣٧ والبدر الطالع ٢: ١٢٠ والتبر المسبوك ٣٣٤ والضوء اللامع ٧: ٨٤ وكشف الظنون ٤٣٨ وا لأعلام ٦: ٢٢٩ ومعجم المؤلفين ٩: ١٥ وهدية العارفين ٢: ١٩٧.','2','484',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (848,'ابن بنت الآقصرائي [٧٩٠ - ٨٥٩ هـ / ١٣٨٨ - ١٤٥٥ م]

<span data-type="title" id=toc-1157>محمد بن أحمد بن أبي يزيد بن محمد، محب الدين، أبو السعادات السرائي (نسبة لمدينة ببلاد الدست) العجمي الأصل، القاهري، ويعرف بابن بنت الآقصرائي:</span>
فقيه حنفي، مفسر، محدث، ولد بالقاهرة، وبها نشأ وتعلم، وسافر إلى الإسكندرية ودمشق وحلب وبيت المقدس وآمد، وغزا مع الجيش لفتح جزيرة قبرس سنة ٨٢٨ هـ، وحج مرارا، ودرس التفسير بالمؤيدية. ومات بمكة. من آثاره "حاشية" على الكشاف، في التفسير، قال السخاوي: "جمع فيها ما رآه من حواشي الطيبي والجاربردي والقطب والتفتازاني وأكمل الدين وإعراب السمن وغيره، مع التوفيق بين ما ظاهره الاختلاف من كلامهم، وصل فيها إلى آخر سورة النساء" (١).
_________
(١) الضوء اللامع ٧: ١١٥ وهدية العارفين ٢: ٢٠١ ونظم العقيان ١٣٩ وكشف الظنون ١٤٨١.','2','484',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (849,'جلال الدين المحلي [٧٩١ - ٨٦٤ هـ / ١٣٨٩ - ١٤٥٩ م]

<span data-type="title" id=toc-1158>محمد بن أحمد بن محمد بن إبراهيم بن أحمد، جلال الدين المحلي:</span>
مفسر، أصولي، من فقهاء الشافعية، نسبته إلى المحلة الكبرى من الغربية بمصر. ولد بالقاهرة وبها نشأ وتعلم. درس بالبرقوقية وبالمؤيدية، وعرض عليه القضاء الأكبر فامتنع. قال السخاوي: "كان إماما. علامة محققا نظارا مفرط الذكاء صحيح الذهن، حتى قيل إن ذهنه يثقب الماس، وكان هو يقول عن نفسه: إن ذهني لا يقبل الخطأ" وكان حاد المزاج لا سيما في الحر، ولم يكن يقدر على الحفظ، حفظ مرة كراسا من بعض الكتب فامتلأ بدنه حرارة. وكان مهيبا صداعا بالحق، يواجه بذلك الظلمة والحكام، ويأتون إليه فلا يأذن لهم. ألف كتبا انتشرت في حياته. وأجل كتبه التي لم تكمل تفسيرالقرآن، أتمه جلال الدين السيوطي، فسمي "تفسير الجلالين" (١)
_________
(١) طبقات المفسرين للداودي ٢: ٨٠ والضوء اللامع ٧: ٣٩ وحسن المحاضرة ١: ٤٤٣ وشذرات الذهب ٧: ٣٠٣ والبدر الطالع ٢: ١١٥ وخطط مبارك ١٥ وصفحات لم تنشر ٦٨ وهدية العارفين ٢: ٢٠٢ وبدائع الزهور ٢: ٦٢ وكشف الظنون ٤٤٥.','2','485',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (850,'الخطيب الشربيني [... - ٩٧٧ هـ / ... - ١٥٧٠ م]

<span data-type="title" id=toc-1159>محمد بن أحمد الشربيني، شمس الدين، المعروف بالخطيب الشربيني:</span>
مفسر، من فقهاء الشافعية، من أهل القاهرة. درس وأفتى في حياة شيوخه، وحج مرارا. له تصانيف، منها "السراج المنير" في تفسير القرآن، في أربع مجلدات، طبع في القاهرة (١).

المغربي [... - ١٠٠٥ هـ / ... - ١٥٩٧ م]

<span data-type="title" id=toc-1160>محمد بن أحمد بن عيسى المغربي:</span>
فقيه مالكي، مفسر، له تصانيف، منها "غاية الاتحاف فيما خفي من كلام القاضي والكشاف" في التفسير (٢).

وحيي زاده [٩٤٠ - ١٠١٨ هـ / ١٥٣٣ - ١٦٠٩ م]

<span data-type="title" id=toc-1161>محمد بن أحمد الأزنيقي، أبو عبد الله، المعروف بوحيي زاده:</span>
عالم
_________
(١) شذرات الذهب ٨: ٣٨٤ وهو فيه: محمد بن محمد. وهدية العارفين ٢: ٢٥٠ ومعجم المطبوعات ١١٠٨ والأعلام ٦: ٢٣٤ ومعجم المؤلفين ٨: ٢٦٩
(٢) هدية العارفين ٢: ٢٦٢ وإيضاح المكنون ٢: ١٣٨.','2','485',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (851,'بالعربية، محدث، مشارك في بعض العلوم، رومي مستعرب، أصله من بلدة. أزنيق، حدت بدار الحديث بأسكدار. له تصانيف، منها "تعليقات" في التفسير (١)

طاشكبري زاده [٩٥٩ - ١٠٣٠ هـ / ١٥٥٢ - ١٦٢١ م]

<span data-type="title" id=toc-1162>محمد بن أحمد بن مصطفى بن خليل، كمال الدين طاشكبري زاده:</span>
قاض، له اشتغال بالتفسير، والتاريخ، رومي، قال النجم الغزي: "لم أر روميا أفصح منه باللسان العربي". ولي القضاء بحلب ثم بدمشق سنة ١٠٠٥ هـ، وساءت سيرته بعد عشرة أشهر فأعيد إلى حلب، وترقى إلى أن ولي قضاء العسكرين. وهو ابن طاشكبري زاده صاحب "الشقائق النعمانية". من آثاره "حاشية على أنوار التنزيل" في التفسير، للبيضاوي، إلى سورة الكهف (٢).
_________
(١) الأعلام ٦: ٨ وخلاصة الأثر ٣: ٣٥٣ وهدية العارفين ٢: ٢٦٨ ومعجم المؤلفين ٨: ٢٣ وعثمانلي مؤلفلري ١: ١٨٢ وفيه وفاته سنة ١١٠٨ خطأ.
(٢) خلاصة الأثر ٣: ٣٥٦ وهدية العارفين ٢: ٢٧١ وإيضاح المكنون ١: ١٤١ والأعلام ٦: ٨ ومعجم المؤلفين ٩: ٢١.','2','486',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (852,'العوفي [... - ١٠٥٠ هـ / ... - ١٦٤٠ م]

<span data-type="title" id=toc-1163>محمد بن أحمد العوفي:</span>
عالم بالقراءات، مفسر، من آثاره "كتاب" جامع لسور القرآن الكريم وبيان ما نزل بمكة وبالمدينة وعدد آي السور والناسخ والمنسوخ (١).

الطرسوسي [... - ١١١٧ هـ / ... - ١٧٠٥ م]

<span data-type="title" id=toc-1164>محمد بن أحمد بن محمد الطرسوسي:</span>
فقيه حنفي، له اشتغال بالتفسير، أصولي. من آثاره "تفسير سورة لقمان" و "تفسير سورة الفاتحة وسورة العصر وسورة الكوثر" وكلها مخطوطة (٢).
_________
(١) فهرس الأزهرية ١: ١٠٦ و ١١٥، وهو فيه: العقوي، والعفوي، وهدية العارفين ٢: ٢٦٣ وهو فيه، العوفي، ووفاته سنة ١٠٠٦ هـ وصفحة ٢٧٩، ووفاته في حدود سنة ١٠٤٩ ومعجم المؤلفين ٨: ٢٩٣ و ٣٠٦ ودار الكتب الشعبية ١: ١٦٩.
(٢) هدية العارفين ٢: ٣٠٩ والأعلام ٦: ٢٣٩ والخزانة التيمورية ١: ٧٩ و ١٧٦ و ٣: ١٨٢ وكشف الظنون ١٦٥٧ ومعجم المطبوعات ١٢٣٨.','2','486',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (853,'عقيلة [... - ١١٥٠ هـ / ... - ١٧٣٧ م]

<span data-type="title" id=toc-1165>محمد بن أحمد بن سعيد بن مسعود المكي، جمال الدين، أبو عبد الله، المعروف بعقيلة، والملقب بالظاهر:</span>
مؤرخ، محدث، مشارك في بعض العلوم. ولد بمكة، وأخذ عن علمائها، ورحل إلى الشام والروم والعراق. ودرس بالمدرسة الجقمقية بدمشق. وتوفي بمكة. من مصنفاته "الإحسان في علوم القرآن" و "تفسير قوله تعالى: {سبحان الذي أسرى ..} الآية. (١)

زيني النجفي [.. - ١٢١٦ هـ / .. - ١٨٠١ م]

<span data-type="title" id=toc-1166>محمد بن أحمد بن زين الدين الحسني البغدادي، النجفي، المعروف بزيني:</span>
مفسر، أديب، شاعر، لغوي، من علماء الشريعة الإمامية. من آثاره "تفسير القرآن" (٢).
_________
(١) هدية العارفين ٢: ٣٢٣ وإيضاح المكنون ١: ٣٢ و ٢: ٩ وسلك الدرر ٤: ٣٠ وفهرس الفهارس ٢: ٣٩ والرسالة المستطرفة ٦٣ والأعلام ٦: ٢٤٠ ومعجم المؤلفين ٨: ٢٦٤.
(٢) أعيان الشيعة ٤٣: ٢٦٧ ومعجم المؤلفين ٨: ٢٦٢ و ٩: ٢٣٠.','2','487',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (854,'الخربتاوي [... - ١٢١٧ هـ / ... - ١٨٠٢ م]

<span data-type="title" id=toc-1167>محمد بن أحمد بن محمد الخربتاوي البحيري:</span>
فقيه مالكي، محدث، مفسر، نسبته إلى خربتا من قرى البحيرة بمصر. من آثاره "فتح الرحمن بتفسير القرآن" مخطوط، في أربع مجلدات، فرغ منه سنة ١٢١٣ هـ. (١)

المعسكري [١١٥٠ - ١٢٣٩ هـ / ١٧٣٧ - ١٨٢٤ م]

<span data-type="title" id=toc-1168>محمد بن أحمد بن عبد القادر بن محمد الراشدي الجليلي المعسكري، المعروف بأبي راس:</span>
مؤرخ، من العلماء بالحديث ورجاله، له مشاركة في الفقه والتفسير والأنساب وغير ذلك. من أهل معسكر، مولدا ووفاة. رحل في طلب العلم فزار مصر والشام والحجاز وتونس والمغرب. له نحو ٥٠ كتابا منها "تفسير القرآن". (٢)
_________
(١) دار الكتب ٢: ١٦٧ والتيمورية ٣: ٨٦ والأزهرية ١: ٢٧٥.
(٢) معجم أعلام الجزائر (ط: ٢) ٣٠٦ وتعريف الخلف ٢: ٢٣٢ وفهرس الفهارس ١: ١٠٤ ودليل مؤرخ المغرب ١: ١٠٦ والأعلام ٦: ٢٤٢ ومعجم المؤلفين ٨: ٢٧٧ ومجلة البحث العلمي عدد ٨: ١٢٦.','2','487',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (855,'الفرخ آبادي [... - ١٢٤٩ هـ / ... - ١٨٣٣ م]

<span data-type="title" id=toc-1169>محمد بن أحمد بن علي الحسيني الفرخ آبادي، الهندي، ولي الله:</span>
فقيه حنفي، ناظم، عارف بالتفسير، مشارك في بعض العلوم، هندي الموطن. من آثاره "نظم الجواهر" في تفسير القرآن الكريم. (١)

السوسي [١٢٨٥ - ١٣٦٩ هـ / ١٨٦٨ - ١٩٥٠ هـ]

<span data-type="title" id=toc-1170>محمد بن أحمد بن الحاج المكي بن أحمد بن علي، أبو الفتح السوسي:</span>
فقيه مالكي، عالم بالعربية، مشارك في التفسير والحديث، أصله من هشتوكة من جزولة، مولده ووفاته بمكناس، قال المنوني: اشتغل بالتدريس قرابة ستين عاما ابتداء من عام ١٣١١ هـ إلى وفاته. وكان آخر ما أقرأه من الدروس الليلية هو تفسير القرآن الكريم بالجلالين، واستمر فيه حتى شارف ختمه حيث وقف على سورة المعارج، فنزل به مرضه الذي توفي منه .. " (٢)
_________
(١) هدية العارفين ٢: ٣٦٤ وإيضاح المكنون ٢: ٦٥٨ ومعجم المؤلفين ٨: ٢٩٥.
(٢) مجلة دعوة الحق س ١٦ العدد ٧: ١٥١.','2','488',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (856,'الإمام الشافعي [١٥٠ - ٢٠٤ هـ / ٧٦٧ - ٨٢٠ م]

<span data-type="title" id=toc-1171>محمد بن إدريس بن العباس بن عثمان بن شافع الهاشمي القرشي المطلبي، أبو عبد الله:</span>
أحد الأئمة الأربعة عند أهل السنة، وإليه تنسب الشافعية كافة. أصله من قبيلة قريش، ويمت بصلة القرابة البعيدة إلى النبي العربي الكريم، صلى الله عليه وسلم. ولد في غزة - بفلسطين - ويقال أنه ولد في اليوم الذي توفي فيه أبو حنيفة، وحمل إلى مكة وهو ابن سنتين، وعاش طفولته فقيرا، ودرس بمكة وأخذ عن علمائها. ولما بلغ العشرين من عمره رحل إلى المدينة المنورة ولقي الإمام مالك بن أنس،
وبعد وفاة مالك (سنة ١٧٩ هـ = ٧٩٥
م) رحل الشافعي إلى اليمن، فاكتسب هناك شهرة كبيرة. ثم قبض عليه وأحضر إلى هارون الرشيد. وبعد عدة سنوات عفي عنه، فترك بغداد سنة ١٨٨ هـ وذهب إلى حران والشام ومصر. وعاد إلى بغداد سنة ١٩٥ هـ، واشتغل بالتدريس، واستمر حتى سنة ١٩٨ هـ عاد بعدها إلى القاهرة فنزلها إلى حين وفاته. من كتبه "أحكام','2','488',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (857,'القرآن" حققه العطار في جزئين بالقاهرة سنة ١٩٥١ - ١٩٥٢. (١)

أبو حاتم الرازي [١٩٥ - ٢٧٧ هـ / ٨١٠ - ٨٩٠ م]

<span data-type="title" id=toc-1172>محمد بن إدريس بن المنذر بن داود ابن مهران، أبو حاتم الحنظلي الرازي:</span>
أحد الأئمة الحفاظ الأثبات، من أهل الري مولدا ووفاة. بدأ في تدوين الحديث سنة ٢٥٩ هـ، ورحل في طلبه إلى العراق والشام ومصر وبلاد الروم، وبقي في الرحلة زمانا. قال: أول ما
_________
(١) تكاد مصادر ترجمته لا تحصر، وقد ألفت في سيرته كتب كثيرة، ونشير هنا إلى بعض هذه المصادر: تاريخ بغداد ٢: ٥٦ وحلية الأولياء ٩: ٦٣ وطبقات الفقهاء للشيرازي ٤٩ ومعجم الأدباء ١٧: ٢٨١ وابن خلكان ٤: ١٦٣ والوافي ٢: ١٧١ وتهذيب الأسماء واللغات ١: ٤٤ والانتقاء ٦٥ وتذكرة الحفاظ ٣٦١ وغاية النهاية ٢: ٩٥ وتهذيب التهذيب ٩: ٢٥ والشافعي حياته وعصر. لأبي زهرة. والمجددون في الإسلام ٩٢ والجرح والتعديل ٣: ٢٠١ ومرآة الجنان ٢: ١٣ وطبقات السبكي الجزء الأول، والنجوم الزاهرة ٢: ١٧٦ والشذرات ٢: ٩ وابن النديم ٢٦٣ وابن أبي يعلى ١: ٢٨٠ وتاريخ التراث العربي ٢: ١٦٥ ومعجم المؤلفين ٩: ٣٢ والإمام الشافعي للشيخ مصطفى عبد الرازق. وطبقات المفسرين ٢: ٩٨.','2','489',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (858,'رحلت أقمت سبع سنين ومشيت على قدمي زيادة على ألف فرسخ، ثم تركت العدد، وخرجت من البحرين إلى مصر ماشيا، ثم إلى الرملة ماشيا، ثم إلى طرسوس ولي عشرون سنة". "وكانت حافظته القوية ومعرفته الواسعة برواة الحديث موضع فخر". حدث عنه جماعة من شيوخه الصغار. ومن أقرانه أبو زرعة الرازي وأبو زرعة الدمشقي. ومن أصحاب السنن أبو داود والنسائي. وكان يقول: "أكتب أحسن ما تسمع، واحفظ أحسن ما تكتب، وذاكر بأحسن ما تحفظ". من آثاره "تفسير القرآن" مخطوط، المجلد الثالث منه، في المكتبة المحمودية بالمدينة (الرقم ٤٩ تفسير) كتب سنة سنة ٨٧٢ هـ (ذكر في مجمع اللغة ٤٩: ٧٢) (١).
_________
(١) ابن أبي يعلى ١: ٢٨٤ وتاريخ بغداد ٢: ٧٣ وتذكرة الحفاظ ٥٦٧ وتهذيب التهذيب ٩: ٣٩ وشذرات الذهب ٢: ١٧١ ومناقب ابن حنبل لابن الجوزي ١٢٣ ومفتاح السعادة ٢: ١٦٩ وهدية العارفين ٢: ١٩ والوافي ٢: ١٨٣ وتقدمة المعرفة لابن أبي حاتم ٣٤٩ والمنتظم لابن الجوزي ٥: ١٠٧ وتاريخ التراث العربي ١: ٣٩٠ والأعلام ٦: ٢٥٠ ومعجم المؤلفين ٩: ٣٥ والرسالة المستطرفة ١٠٤. وطبقات الشافعية ١: ٢٦٩ وطبقات الحنابلة ١: ٢٨٤.','2','489',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (859,'ابن إدريس [... - ٧٣٠ هـ / ... - ١٣٣٠ م]

<span data-type="title" id=toc-1173>محمد بن إدريس بن علي بن عبد الله ابن الحسن بن حمزة الزيدي اليماني، الشهير بابن إدريس:</span>
مفسر، فقيه، من أشراف اليمن وأمرائها، من أهل صنعاء. قال في البدر الطالع: "كان فقيها عارفا بارعا متقنا عارفا بالأصول والفروع، وله شعر حسن ومصنفات كثيرة". من آثاره "التيسير" في تفسير القرآن الكريم، و "الإكسير الإبريز في تفسير القرآن العزيز" و "الحسام المرهف تفسير غريب المصحف" و "الدرة المضية في الآيات المنسوخة الفقهية" و "النهج القويم في تفسير القرآن الكريم" (١)

ابن خزيمة [٢٢٣ - ٣١١ هـ / ٨٣٨ - ٩٢٤ م]

<span data-type="title" id=toc-1174>محمد بن إسحاق بن خزيمة بن المغيرة السلمي، النيسابوري، أبو بكر:</span>
إمام نيسابور في عصره، فقيه مجتهد، عالم
_________
(١) البدر الطالع ٢: ١٢٦ وهدية العارفين ٢: ١٤٧ والعقود اللؤلؤية ١: ٣٢٤ و ٤١٠ وإيضاح المكنون ٦٩٤ ومعجم المؤلفين ٩: ٣٤ وملحق البدر الطالع ٥٢ ترجمة والده.','2','490',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (860,'بالحديث، مشارك في بعض العلوم، من فقهاء الشافعية، ولد بنيسابور، ورحل إلى العراق والشام والجزيرة ومصر. روى عنه البخاري ومسلم في غير الصحيح. لقبه السبكي بإمام الأئمة. له أكثر من ١٤٠ مصنفا، منها "تفسير القرآن". (١)

ابن منده [٣١٠ - ٣٩٥ هـ / ٩٢٢ - ١٠٠٥ م]

<span data-type="title" id=toc-1175>محمد بن إسحاق بن محمد بن يحيى، ابن منده، أبو عبد الله العبدي (نسبة إلى عبد يا ليل) الأصبهاني:</span>
من كبار حفاظ الحديث، الراحلين في طلبه، المكثرين من التصنيف فيه. ولد بأصبهان، وأخد عن علمائها، ثم رحل فسمع بخراسان والعراق والحجاز ومصر والشام وبخارا، وعدة شيوخه الذين سمع وأخذ عنهم ١٧٠٠ شيخ، وقد بقي في الرحلة نيفا وثلاثين سنة. قال الذهبي: "ما بلغنا أن أحدا من هذه الأمة سمع ما سمع، ولا جمع ما
_________
(١) الوافي ٢: ١٩٦ وتذكرة الحفاظ ٢: ٢٥٩ ومرآة الجنان ٢: ٢٦٤ وهدية العارفين ٢: ٢٩ والشذرات ٢: ٢٦٢ والبداية والنهاية ١١: ١٤٩ وطبقات الشافعية ٢: ١٣٠.','2','490',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (861,'جمع، وكان ختام الرحالين وفرد المكثرين مع الحفظ والمعرفة والصدق وكثرة التصانيف" من كتبه "الناسخ والمنسوخ" (١)

القونوي [... - ٦٧٣ هـ / ... - ١٢٧٥ م]

<span data-type="title" id=toc-1176>محمد بن إسحاق بن محمد بن يوسف بن علي القونوي الرومي، صدر الدين:</span>
صوفي، مشارك في بعض العلوم، من أهل قونية. تزوج الشيخ محي الدين ابن العربي أمه، ورباه، فكان من كبار تلاميذه، وكان شافعي المذهب. مات بقونية وهو ابن اثنتين وثلاثين سنة. من كتبه "إعجاز البيان" في تفسير الفاتحة، على لسان القوم، طبع بمصر، و "تفسير البسملة" مخطوط. (٢)
_________
(١) تذكرة الحفاظ ١٠٣١ والوافي ٢: ١٩٥ وميزان الإعتدال ٣: ٢٦ ولسان الميزان ٥: ٧٠ والمنتظم ٧: ٢٣٢ والنجوم الزاهرة ٤: ٢١٣ وهدية العارفين ٢: ٥٧ والشذرات ٣: ١٤٦ والبداية والنهاية ١١: ٣٣٦ وغاية النهاية ٢: ٩٨ والرسالة المستطرفة ٣٠ وطبقات الحنابلة ٢: ١٦٧ ومجلة المجمع العلمي العربي ٨: ١٢٧.
(٢) هدية العارفين ١: ١٣٠ والوافي ٢: ٢٠٠ وجامع كرامات الأولياء ١: ١٣٣ ومعجم المطبوعات ١٥٣٢ ومفتاح السعادة ١: ٤٥١ و =','2','491',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (862,'الحليمي [١٠٩١ - ١١٧٢ م / ٤٨٤ - ٥٦٧ هـ]

<span data-type="title" id=toc-1177>محمد بن أسعد بن محمد بن نصر الحليمي، ويقال ابن حليم، العراقي، أبو المظفر:</span>
واعظ، مفسر، لغوي، من فقهاء الحنفية. نشأ وتفقه ببغداد، وسكن دمشق ودرس بمدرسة طرخان، ثم بنى له معين الدولة مدرسة، فأقبل عليه الناس. قال ابن النجار: "كان فسلا في دينه، خليعا، قليل المروءة، كذابا، توفي بدمشق. له "تفسير القرآن". (١)

القزويني [... - ٥٨٩ هـ / ... - ١١٩٣ م]

<span data-type="title" id=toc-1178>محمد بن أسعد بن أحمد الزاكاني القزويني، أبو عبد الله:</span>
مفسر، مناظر، مدرس، من فقهاء
_________
= ٢: ٢١١ والسبكي ٥: ١٩ وكشف الظنون ١٢٠ و ٤٥٥ والأعلام ٦: ٢٥٤ ومعجم المؤلفين ٩: ٤٢.
(١) طبقات المفسرين للسيوطي ٢٩ وطبقات المفسرين للداودي ٢: ٨٧ والوافي ٢: ٢٠٣ والشذرات ٤: ٢١٨ والجواهر المضية ٢: ٣٢ وتاج التراجم ٥٣ وطبقات النحويين لابن قاضي شهبة ١: ٢٨ ومرآة الجنان ٣: ٣٨٢ وكشف الظنون ٤٣٧.','2','491',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (863,'الشافعية، من أهل قزوين. وهو خال الإمام عبد الكريم بن محمد الرافعي (أنظر ترجمته في هذا المعجم) تعلم بقزوين ثم بأصبهان وسمع بهما الحديث. وسافر إلى همذان، فناب بها في قضائها، وحمدت سيرته. (١)

الدواني [٨٣٠ - ٩١٨ هـ / ١٤٢٧ - ١٥١٢ م]

<span data-type="title" id=toc-1179>محمد بن أسعد الصديقي الدواني، جلال الدين:</span>
قاض، متكلم، مفسر، منطقي، يعد من الفلاسفة. ولد في دوان (من بلاد كازرون). وسكن شيراز، وولي قضاء فارس. قال الشوكاني: "ارتحل إليه أهل الروم وخراسان وما وراء النهر، وتكاثر تلامذته، وكان من أدبهم أنه إذا تكلم نكسوا رؤوسهم تأدبا ولم يتكلم أحد منهم بشيء، ومات سنة ٩١٨"، وقال السخاوي "إنه في سنة ٨٩٧ كان حيا، وكان عمره إذ ذاك بضعا وسبعين، فيكون قد عاش نحو تسعين سنة". وفي النور السافر، وفاته سنة ٩٢٨ وعنه "شذرات الذهب". وفي
_________
(١) طبقات المفسرين للداودي ٢: ٨٦ وتاريخ قزوين ١: ٧٢.','2','492',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (864,'"كشف الظنون" وفاته ٩٠٧ هـ. من كتبه "تفسير سورة الكافرون" و "سورة الإخلاص" و "المعوذتبن"، ثم جمعها وسماها "تفسير القلاقل" وهي جمع قل. و "تفسير سورة الكوثر". (١)

الطوسي [... - ٢٤٢ هـ / ... - ٨٥٦ م]

<span data-type="title" id=toc-1180>محمد بن أسلم بن سالم بن يزيد، أبو الحسن الكندي، مولاهم، الطوسي:</span>
من حفاظ الحديث الثقات، مفسر، نعته الذهبي بشيخ المشرق. وقال محمد بن رافع: "دخلت على ابن أسلم فما شبهته إلا بأصحاب النبي - صلى الله عليه وسلم -. مات بنيسابور، من كتبه "تفسير القرآن". (٢)
_________
(١) البدر الطالع ٢: ١٣٠ والضوء اللامع ٧: ١٣٣ والنور السافر ١٣٣ وأعيان الشيعة ٤٣: ٢٨٧ والشذرات ٨: ١٦٠ وكشف الظنون ٤٥٠ و ٤٥٧ ومعجم المطبوعات ٨٩١ ودائرة المعارف الإسلامية ٩: ٣٠٧ والأعلام ٦: ٢٥٧ ومعجم المؤلفين ٩: ١٧ وروضات الجنات ١٦٢ والفوائد البهية بالحاشية ٨٩ وأعلام العرب في العلوم والفنون ٣: ٨ والذريعة ٢: ٢٦٠
(٢) الوافي ٢: ٢٠٤ وتذكرة الحفاظ ٥٣٢ وحلية الأولياء ٩: ٢٣٨ وهدية العارفين ٢: ١٣ =','2','492',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (865,'البخاري [١٩٤ - ٢٥٦ هـ / ٨١٠ - ٨٧٠ م]

<span data-type="title" id=toc-1181>محمد بن إسماعل بن إبراهيم بن المغيرة البخاري، أبو عبد الله:</span>
شيخ الإسلام وإمام الحفاظ، صاحب "الجامع الصحيح" المعروف بصحيح البخاري. ولد في بخارى، ونشأ يتيما. ورحل (سنة ٢١٠ هـ) في طلب الحديث إلى سائر محدثي الأمصار، فزار خراسان والجبال والعراق والحجاز والشام ومصر، وسمع من نحو ألف شيخ، وجمع نحو ست مئة ألف حديث اختار منها في صحيحه ما وثق برواته. قال ابن خزيمة: "ما تحت أديم السماء أعلم بالحديث من البخاري"، ثم أقام في بخارى، وكان أميرها خالد بن أحمد السدوسي قد استقدم إليه بعض كبار الحفاظ، وصنف له نصر بن أحمد البغدادي "مسندا" وطلب من البخاري أن يوافيه، فامتنع، فأخرجه من بخارى إلى خرتنك، فمات فيها. وفي مجلة "العرب" الباكستانية: أن قبر البخاري انثر، وبني مكانه قبر آخر،
_________
= والشذرات ٢: ١٠٠ والجرح والتعديل ٣/ ٢: ٢٠١ ومختصر دول الإسلام ١: ١١٤ ومعجم المؤلفين ٩: ٥٢ والأعلام ٦: ٢٥٧.','2','493',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (866,'وهو في قرية تعرف الآن بقرية "خواجه صاحب" على ٣٠ كيلو مترا من سمرقند، في طريق بخارى ... له "التفسير الكبير" ذكره الفربري، وقال صاحب "تاريخ التراث العربي": "وفي النصف الأول من القرن الثالث الهجري بذل أكثر علماء الحديث جهودا في تأليف تفاسير للقرآن أيضا بجانب تأليفهم في الحديث، فالبحاري الذي اختار صحيحه من كتب الحديث، قدم أيضا للقرآن تفسيرا مستقلا، يحتوي على بقايا من التفاسير السابقة المؤلفة في القرنين الأول والثاني الهجريين، ولكن ليس متبعا نظامها، وفي أغلب الأحيان بدون إسناد. وكذلك يحتوي أيضا على بقايا من كتب اللغة المؤلفة في القرن الثاني الهجري" (١).
_________
(١) تذكرة الحفاظ ٥٥٥ وتاريخ بغداد ٢: ٤ والجرح والتعديل ٣/ ٢/ ١٩١ وابن النديم ٢٨٦ وطبقات ابن أبي يعلى ١: ٢٧١ والوافي ٢: ٢٠٦ وتهذيب التهذيب ٩: ٤٧ والإكمال ١: ٢٥٩ واللباب ١: ١٢٠ والشذرات ٢: ١٣٤ والسبكي ٢: ٢ وابن خلكان ٤: ١٨٨ ومرآة الجنان ٢: ١٦٧ ومفتاح السعادة ٢: ١٣٠ وابن كثير ١١: ٢٤ والعبر ٢: ١٢ والنجوم الزاهرة ٣: ٢٥ وطبقات المفسرين ٢: ١٠٠ وهدية العارفين ٢: ١٦ ومجلة العرب الباكستانية س ١٣ عدد رمضان وشوال ١٣٧٩: ١٣ وتاريخ التراث العربي ١: =','2','493',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (867,'الترمذي [... - ٢٨٠ هـ / ... - ٨٩٣ م]

<span data-type="title" id=toc-1182>محمد بن إسماعيل بن يوسف السلمي الترمذي، أبو إسماعيل:</span>
حافظ، محدث، مفسر، روى عنه الإمام الترمذي في "جامعه" والنسائي في "سننه". قال الخطيب البغدادي: "كان فهما متقنا مشهورا بمذهب السنة". ووثقه الدارقطني والنسائي. له كتاب "ناسخ القرآن ومنسوخه". (١)

السمرقندي [... - بعد ٦٩٠ هـ / ... - بعد ١٢٩٠ م]

<span data-type="title" id=toc-1183>محمد بن أشرف الحسيني السمرقندي، شمس الدين:</span>
عالم بالمنطق والفلك والهندسة، مفسر، من أهل سمرقند. قال في كشف الظنون: "له الصحائف" في التفسير، أتمه
_________
= ١٩٧ و ٣٠٦ وتهذب الأسماء واللغات ١/ ١: ٦٧ ودائرة المعارف الإسلامية ٣: ٤١٩ والأعلام ٦: ٢٥٨ ومعجم المؤلفين ٩: ٥٢.
(١) طبقات المفسرين للداودي ٢: ١٠٤ وتاريخ بغداد ٢: ٤٢ والوافي بالوفيات ٢: ٢١٢ وتهذيب التهذيب ٩: ٦٢ وشذرات الذهب ٢: ١٧٦ والبداية والنهاية ١١: ٦٩ وهدية العارفين ٢: ٢٠ ومعجم المؤلفين ٩: ٦٢','2','494',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (868,'الشيخ أحمد بن محمود القرماني، الأصم المتوفى سنة ٩٧١ هـ (أنظر ترجمته في هذا المعجم). ثم ذكر له كتابا آخر باسم الصحائف وقال إنه في الكلام. وأرخ وفاة السمرقندي سنة ٦٠٠ هـ. وقال صاحب هدية العارفين: "له شرح على المقدمة البرهانية للنسفي، رأيته وفيه أنه فرغ منه سنة ٦٩٠ هـ ". (١).

الدومري [... - ١١٨٥ هـ / ... - ١٧٧١ م]

<span data-type="title" id=toc-1184>محمد أعظم بن خير الزمان الحسيني الدومري الهندي:</span>
مؤرخ، مفسر، متكلم، من فقهاء الحنفية. من أهل الهند. من كتبه "تفسير القرآن". (٢)

جوي زاده [... - ٩٥٤ هـ / ... - ١٥٤٧ م]

<span data-type="title" id=toc-1185>محمد بن إلياس الحنفي الرومي، محيي الدين، المعروف بجوي زاده:</span>
_________
(١) كشف الظنون ٤٠، ١٠٧٤ و ١٠٧٥ وهدية العارفين ٢: ١٠٦ ومعجم المطبوعات ١٠٤٦ وفهرس الأزهرية ٣: ٤٥١ وتراث العرب العلمي ٢٧٧ وبروكلمان، الذيل ١: ٤٨٩ والأعلام ٦: ٣٩ ومعجم المؤلفين ٩: ٦٣.
(٢) هدية العارفين ٢: ٣٣٩ وإيضاح المكنون ١: ٣٠٥.','2','494',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (869,'قاض، عالم بالتفسير والفقه والأصول. تركي الأصل والمنشأ. ولي القضاء بمصر، فقضاء العساكر الأناضولية، ثم ولي الإفتاء بالقسطنطينية، وأنكر على الشيخ محيي الدين ابن العربي بعض أقواله، فعزله السلطان من الإفتاء، فاشتغل بالتدريس. وأعيد للقضاء في عساكر الروم إيلي، فاستمر إلى أن توفي. قال الغزي: "كان مرضي السيرة، محمود الطريقة، قوالا بالحق لا فيف في الله لومة لائم، له يد طولى في الفقه والتفسير والأصول ومشاركة في سائر العلوم، وقال صاحب "الشقائق": "كان سيفا من سيوف الحق قاطعا فاصلا بين الحق والباطل، حسنة من حسنات الأيام، له "تعليقات" لم تشتهر (١).

أميرباد شاه [نحو ٩٧٢ هـ / ١٥٦٥ م]

<span data-type="title" id=toc-1186>محمد أمين بن محمود البخاري، المعروف بأمير باد شاه:</span>
مفسر،
_________
(١) الكواكب السائرة ٢: ٢٨ و ٢٣٧ و ٣: ٧٩ وعنه شذرات الذهب ٨: ٣٠٣ وهدية العارفين ٢: ٢٤٢ وإيضاح المكنون ٢: ٤٣٩ ووفاته فيه سنة ٩٥٢ هـ. ومعجم المؤلفين ٩: ٦٦ والأعلام ٦: ٢٦٥.','2','495',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (870,'صوفي، محقق، من فقهاء الحنفية. من أهل بخارى، كان نزيلا بمكة. قال صاحب هدية العارفين: "توفي في حدود ٩٧٢ وقيل ٩٨٧ هـ ". من آثاره "حاشية على تفسير البيضاوي إلى سورة الأنعام" و "تفسير سورة الفتح". (١)

الشرواني [... - ١٠٣٦ هـ / ... - ١٦٢٦ م]

<span data-type="title" id=toc-1187>محمد أمين بن صدر الدين الشرواني:</span>
مفسر، مشارك في بعض العلوم، من فقهاء الحنفية. نسبته إلى شروان - من نواحي بخارى -. كانت إقامته في آمد بديار بكر، وأقام مدة في الآستانة ودرس بمدرسة السلطان أحمد. من كتبه "تفسير سورة الفتح" مخطوط، و "حاشية على تفسير البيضاوي" مخطوطة لم تكمل، قال في كشف الظنون: "وهي إلى قوله تعالى: {ألم، ذلك الكتاب} أورد عبارة البيضاوي تماما بقوله، وبدأ بما بدأ به "الصفدي" في "شرح لامية العجم" وهو قوله: الحمد الله
_________
(١) كشف الظنون ١٩٣ و ٤٥٠ و ١٢٦٠ وبروكلمان  G.١١.٤١٢ S,١١ - ٥٨٣  ودار الكتب ١: ٣٨٢ وهدية العارفين ٢: ٢٤٩.','2','495',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (871,'الذي شرح صدر من تأدب .. الخ .. (١).

ابن عابدين [١١٩٨ - ١٢٥٢ هـ / ١٧٨٤ - ١٨٣٦ م]

<span data-type="title" id=toc-1188>محمد أمين بن عمر بن عبد العزيز عابدين الدمشقي:</span>
فقيه الشام وإمام الحنفية في عصره. مولده ووفاته بدمشق. من كتبه "حواش على أنوار التنزيل" في التفسير للبيضاوي، التزم فيها أن لا يذكر شيئا ذكره المفسرون. (٢)

ابن الضريس [٢٠٠ - ٢٩٤ هـ / ١١٣٦ - ١٢١٢ م]

<span data-type="title" id=toc-1189>محمد بن أيوب بن يحيى بن الضريس البجلي الرازي، أبو عبد الله:</span>
محدث، حافظ، ولد في حدود سنة ٢٠٠ هـ،
_________
(١) كشف الظنون ١٩٢ وهدية العارفين ٢: ٢٧٥ وخلاصة الأثر ٣: ٤٧٥ وفهرست الخديوي ١: ١٦٧ و ٤: ١٧٦ وتاريخ آداب اللغة العربية ٣: ٣٥٥ والأعلام ٦: ٢٦٦ ومعجم المؤلفين
(٢) روض البشر ٢٢٠ ومعجم المطبوعات ١٥٥ وهدية العارفين ٢: ٣٦٧ وعقود اللآلي ٢٣٢ ومنتخبات التواريخ ٢: ٦٨٠ وحلية البشر. والأعلام ٦: ٢٦٧ ومعجم المؤلفين ٩: ٧٧.','2','496',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (872,'ومات بالري. حدث عنه أحمد بن حنبل وجماعة. من آثاره "فضائل القرآن" مخطوط في الظاهرية. و "تفسير القرآن" (١).

الشنقيطي [١٣٢٥ - ١٣٩٣ هـ / ١٩٠٧ - ١٩٧٣ م]

<span data-type="title" id=toc-1190>محمد الأمين بن محمد المختار بن عبد القادر الجكني الشنقيطي:</span>
مفسر، باحث، من علماء شنقيط بموريطانيا، ولد وتعلم بها. وحج سنة ١٣٦٧ هـ (١٩٤٨ م) واستقر مدرسا في المدينة المنورة، ثم في الرياض، فالجامعة الإسلامية بالمدينة. وتوفي بمكة. من كتبه "أضواء البيان في نفسير القرآن بالقرآن". ١٠ مجلدات، طبع في دار "عالم الكتب" بيروت، لبنان. (٢)
_________
(١) طبقات المفسرين للداودي ٢: ١٠٥ وتذكرة الحفاظ ٢: ٦٤٣ والعبر ٢: ٩٨ وهدية العارفين ٢: ٢١ وكشف الظنون ٤٥٨ و ١٢٧٧ والشذرات ٢: ٢١٦ والنجوم الزاهرة ٣: ١٦٢ والرسالة المستطرفة ٥٨ والأعلام ٦: ٢٧٠ ومعجم المؤلفين ٩: ٨٣ والوافي ٢: ٢٣٤ وتاريخ التراث العربي ١: ٢٠٨.
(٢) مشاهير علماء نجد ٥١٧ و ٥٤٠ ومجلة المنهل عدد ذي الحجة ١٣٩٣ صفحة ٩٨٢.','2','496',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (873,'الغافقي [٥٣٠ - ٦٠٨ هـ / ١١٣٦ - ١٢١٢ م]

<span data-type="title" id=toc-1191>محمد بن أيوب بن محمد بن وهب ابن نوح الغافقي، أبو عبد الله:</span>
قاض، من أعيان فقهاء المالكية، مشارك في القراءات والتفسير والحديث والأنساب. من أهل بلنسة بالأندلس. قال ابن الأبار: "ولي خطة الشورى في حياة شيوخه، وكان رأسا في الراسخين من العلماء، وصدرا في المشاورين من الفقهاء، برع في علوم اللسان، وتقدم في الفتيا واطلع على الآداب، واضطلع بالغريب، وشارك في التفسير، وتحقق بالقراءات، وانتهت إليه الرياسة في عقد الشروط، وولي قضاء بعض الكور الخ، (١)

التاذفي [٦٢٨ - ٧٠٥ هـ / ١٢٣١ - ١٣٠٦ م]

<span data-type="title" id=toc-1192>محمد بن أيوب بن عبد القاهر التاذفي الحلبي، بدر الدين:</span>
مقرىء مشهور، مفسر، فقيه حنفي، من أهل تاذف،
_________
(١) التكملة لابن الأبار ٢: ٥٨٢ (ت ١٥٥٦) والشذرات ٥: ٣٤ وغاية النهاية ٢: ١٠٣ والوافي ٢: ٢٣٩ وبغية الوعاة ١: ٥٨.','2','497',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (874,'سكن حلب وأخذ وسمع من علمائها، ثم رحل إلى مصر فسمع بها وأقرأ الناس مدة، وعاد إلى دمشق بعد سنة ٦٨٠ هـ فأقرأ بها، ودخل حماه، فسكنها إلى أن مات. قال الذهبي: "حضرت عنده وكتبت عنه". له "مختصر الراشف من زلال الكاشف" في التفسير، قال في كشف الظنون: "اختصره من الكشاف مع المحاكمات من فوائد أبي العباس المهدوي، ومن كتاب أبي الليث السمرقندي، ومن الكشف والبيان للثعلبي، أوله "الحمد لله المتكلم بالقرآن المبين .. ". (١)

الداماد [... - ١٠٤١ هـ / ... - ١٩٣١ م]

<span data-type="title" id=toc-1193>محمد باقر بن محمد الحسيني الأسترابادي، المعروف بالمير الداماد:</span>
فقيه إمامي، أصولي، متكلم، مفسر، حكيم، مشارك في أنواع في العلوم، شاعر بالعربية والفارسية. من أهل أصبهان، وأصله من أستراباد. كان مقربا لدى الشاه عباس الصفوي.
_________
(١) كشف الظنون ١٦٢٧ وغاية النهاية ٢: ١٠٢ وهدية العارفين ٢: ١٤٠ والدرر الكامنة ٤: ١٤ و "الوافي" ٢: ٢٣٩.','2','497',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (875,'توفي ودفن بالنجف. من تصانيفه "سدرة المنتهى" في التفسير، مخطوط. (١)

الفاضل الإيرواني [... - ١٣٠٦ هـ / ... - ١٨٨٩ م]

<span data-type="title" id=toc-1194>محمد باقر بن محمد باقر الإيرواني، المعروف بالفاضل الإيرواني:</span>
فقيه إمامي، مشارك في بعض العلوم، نسبته إلى إيروان - من مدن تركستان -. تعلم في كربلاء والنجف. ودرس الفقه والأصول في مسجد الطوسي بالنجف، ومات بها. له "حاشية" على تفسير البيضاوي. (٢).

الأصفهاني [٢٥٤ - ٣٢٢ هـ / ٨٦٨ - ٩٣٤ م]

<span data-type="title" id=toc-1195>محمد بن بحر الأصفهاني، أبو مسلم:</span>
وال، من بلغاء الكتاب، عالم بالتفسير وبغيره من صنوف العلم،
_________
(١) أعيان الشيعة ٤٤: ١١٣ والذريعة ٢: ٢٣٧ و ٢٦١ و ٥٠٧ وخلاصة الأثر ٤: ٣٠١ وهدية العارفين ٢: ٢٧٦ والأعلام ٦: ٢٧٢ ومعجم المؤلفين ٩: ٩٣.
(٢) أعيان الشيعة ٤٤: ٩١','2','498',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (876,'معتزلي، من أهل أصفهان. ولي أصفهان وبلاد فارس للمقتدر بالله العباسي، واستمر إلى أن دخل علي بن بويه أصفهان في منتصف ذي القعدة سنة ٣٢١ هـ، فعزل، من كتبه "جامع التأويل لمحكم التنزيل" في التفسير، على مذهب المعتزلة، أربعة عشر مجلدا، جمع سعيد الأنصاري الهندي نصوصا منه وردت في "مفاتيح الغيب" المعروف بتفسير الفخر الرازي، وسماها "ملتقط جامع التأويل لمحكم التنزيل" طبع في جزء صغير. (١)

محمد بخيت [١٢٧١ - ١٣٥٤ هـ / ١٨٥٤ - ١٩٣٥ م]

<span data-type="title" id=toc-1196>محمد بخيت بن حسين المطيعي الحنفي:</span>
مفتي الديار المصرية، ومن
_________
(١) معجم الأدباء ١٨: ٣٥ والوافي ٢: ٢٤٤ ولسان الميزان ٥: ٨٩ وبغية الوعاة ١: ٥٩ والفهرست طبعة فلوجل ١٣٦ ومقدمة "ملتقط جامع التأويل" وكشف الظنون ٥٣٨، وقد علق الناشر على اسم الأصفهاني بقوله: لعله هو أبو مسلم محمد بن علي بن مهربزد الأصفهاني المتوفى سنة ٤٥٧ قاله في ميزان الاعتدال". وهدية العارفين ٢: ٧١ وهو فيه: محمد بن علي بن مهربزد بن بحر، أبو مسلم الأصفهاني. وذكر له نفس التصانيف. وتاريخ التراث العربي ٢١٠ وطبقات المعتزلة لابن المرتضى ٩١.','2','498',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (877,'كبار فقهائها. ولد في بلدة المطيعة من أعمال أسيوط، وقدم القاهرة في أوائل سنة ١٢٨١، وتعلم في الأزهر، ودرس فيه. ثم عمل في القضاء الشرعي سنة ١٢٩٧ هـ، واتصل بالسيد جمال الدين الأفغاني. وقام الشيخ محمد عبده بحركته الإصلاحية، فكان من أشد المعارضين له، وفي سنة ١٣٣٣ هـ (١٩١٤ م) عين مفتيا للديار المصرية، فاستمر إلى سنة ١٣٣٩ هـ (١٩٢١ م). ومات بالقاهرة. من كتبه "المدخل المنير في مقدمة علم التفسير" (١)

ابن بركات [٤٢٠ - ٥٢٠ هـ / ١٠٢٩ - ١١٢٦ م]

<span data-type="title" id=toc-1197>محمد بن بركات بن هلال بن عبد الواحد السعيدي، أبو عبد الله:</span>
_________
(١) مرآة العصر ٢: ٤٦٧ ورياض الجنة ١: ١٦٢ وكنز الجوهر في تاريخ الأزهر ١٧٢ والأعلام الشرقية ٣: ٥٦ ومجلة الرسالة القاهرية ٣: ١٧٥٧ ومجلة نور الإسلام ٦: ٥٨٣ وصفوة العصر ١: ٥٠١ ومعجم المطبوعات ٥٣٨ وجريدة الأهرام ٢١ و ٢٩ رجب ١٣٥٤ والمعجم الوجيز ٢٧ والفكر السامي ٤: ٣٨ والكنز الثمين ١١٨ ومعجم المؤلفين ٩: ٩٨ وفي الحاشية أسماء مصادر أخرى، والأعلام ٦: ٢٧٤.','2','499',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (878,'نحوي مصر في عصره، عالم بالقرآن، له اشتغال بالتاريخ. قال في "إنباه الرواة": كان عالي المحل في النحو واللغة وسائر فنون الأدب، منحطا في الشعر إلى أدنى الرتب". عاش مئة سنة وثلاثة أشهر. من آثاره "الإيجاز" مخطوط، في معرفة ما في القرآن من ناسخ ومنسوخ. ألفه للأفضل بن أمير الجيوش. (١)

الواني [... - ١٠٩٦ هـ / ... - ١٦٨٥ م]

<span data-type="title" id=toc-1198>محمد بن بسطام الخوشابي الحسيني الرومي، المعروف بالواني:</span>
مفسر، واعظ، فقيه حنفي، من علماء الدولة العثمانية. من أهل "خو شاب" القريبة من بلدة (وان) التركية. نفي إلى قرية "كستل" من قرى بروسة،
_________
(١) إنباء الرواة ٣: ٧٨ والمحمدون من الشعراء ١٦٧ وبغية الوعاة ١: ٥٩ ومعجم الأدباء ١٨: ٣٩ والشذرات ٤: ٦٢ وهدية العارفين ٢: ٨٤ والوافي ٢: ٢٤٧ ومرآة الجنان ٣: ٢٢٥ ومجلة المشرق ٣٥: ١٨٣ وحسن المحاضرة ١: ٢٢٨ وطبقات ابن قاضي شبهة ١: ٢٨ ومعجم المؤلفين ٩: ١٠١ وخريدة القمر قسم شعراء مصر ٢: ١٥٦ وبروكلمان، الذيل: ٩٨٧ وكشف الظنون ٣٠٣ و ٧١٥ والأعلام ٦: ٢٧٦.','2','499',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (879,'وتوفي بها. من كتبه "خلاصة التفاسير" و "عرائس القرآن ونفائس الفرقان" في قصص الأنبياء. قال في كشف الظنون: "وهو أحسن وأفيد من "عراش" الثعلبي، ذكر فيه من تفسير البيضاوي وحواشيه ومن الكشاف وحواشيه". (١)

الغزي [١٢٧٤ - ١٣٣٩ هـ / ١٨٥٧ - ١٩٢١ م]

<span data-type="title" id=toc-1199>محمد بشير بن محمد هلال بن محمد الألاجاتي، المعروف بالغزي:</span>
قاض، من أعيان حلب، مشارك في الفقه والمنطق والتفسير والتجويد، ولد بحلب، وابتدأ حياته بالتدريس في مساجدها، ثم انتخب نائبا عنها في مجلس النواب العثماني أيام الترك، ثم ولي قضاءها بعد خروجهم من بلاد الشام. وفي "الأعلام" للزركلي: "ولم يكن من آل الغزي، وإنما رباه أخوه لأمه الشيخ كامل الغزي، فنسب إليهم". من كتبه "تفسير القرآن"
_________
(١) هدية العارفين ٢: ٢٩٩ وإيضاح المكنون ١: ٤٣٤ وكشف الظنون ١١٣١ ومعجم الؤلفين ٩: ١٠٢ ومخطوطات الظاهرية، قسم التاريخ ٢: ٣٤٧ وعثمانلي مؤلفلري ٢: ٥٠.','2','500',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (880,'مختصر، قيل: يمكن طبعه على هامش المصحف. (١)

المقدمي [... - ٢٣٤ هـ / ... - ٨٤٩ م]

<span data-type="title" id=toc-1200>محمد بن أبي بكر بن علي بن عطاء ابن مقدم، أبو عبد الله الثقفي، مولاهم، البصري المقدمي:</span>
محدث، مفسر، من أهل البصرة. روى عنه البخاري ومسلم وأبو يعلى، وروى النسائي عن رجل عنه، وثقه ابن معين وأبو زرعة. له "تفسير القرآن" سمعه منه الحسن بن سفيان النسوي، شيخ خراسان في وقته. (٢)

الأندقاني [... . - حيا ٥٢٦ هـ / ... - حيا ١١٣٢ م]

<span data-type="title" id=toc-1201>محمد بن أبي بكر بن أحمد الإسفراييني، أبو الحسن الأندقاني:</span>
_________
(١) أعلام النبلاء ٧: ٦٢٣ وأدباء حلب ٥٠ وإيضاح المكنون ١: ٥٧ و ٤٩٥: ٢ والأعلام ٦: ٢٧٨ وفهرس الأزهرية ١: ١١٤ ومعجم المؤلفين ٩: ١٠٣.
(٢) طبقات المفسرين للداودي ٢: ٩٧ والوافي ٢: ٢٥٩ وخلاصة تهذيب الكمال ٣٨٠ واللباب ٣: ١٩٦ وتهذيب التهذيب ٩: ٨٠ وتذكرة الحفاظ ٧٠٤ ترجمة الحسن بن سفيان.','2','500',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (881,'مفسر، صوفي، من أهل إسفراين، توطن قزوين: قال عبد الغافر الفارسي: "كان له قبول عند الأكابر والعوام، وحظ من التفسير والحديث والفقه والخلاف، وكتب بخطه الكثير من كل فن لحرصه على الجمع" (١)

الفرغاني [... - حيا ٥١٩ هـ / ... - حيا ١١٩٥ م]

<span data-type="title" id=toc-1202>محمد بن أبي بكر بن يوسف الفرغاني، أبو عبد الله:</span>
لغوي، مشارك في بعض العلوم، من أهل فرغانة. له تصانيف، منها "البيان في غريب القرآن" (٢).

الرازي [... - بعد ٦٦٦ هـ / ١٢٦٨ م]

<span data-type="title" id=toc-1203>محمد بن أبي بكر بن عبد القادر الرازي، زين الدين، أبو عبد الله:</span>
لغوي، مفسر، أديب، من فقهاء الحنفية، أصله من الري، زار مصر
_________
(١) طبقات المفسرين للداودي ٢: ٩٠ وتاريخ قزوين ١: ٧٥.
(٢) هدية العارفين ٢: ١٠٤ وإيضاح المكنون ١: ٢٠٦ وكشف الظنون ١٧٥٥ ونعته بالشيخ الإمام مختار الإسلام.','2','501',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (882,'والشام، ودخل بلاد الروم فكان ب "قونية" (في وسط تركيا الآسيوية) سنة ٦٦٦ هـ. وهو صاحب "مختار الصحاح" في اللغة، فرغ منه ليلة أول رمضان سنة ٦٦٠ هـ. وللباحث المؤرخ عبد الله مخلص رسالة في سيرته سماها "صاحب مختار الصحاح" حقق فيها خطأ القول بأنه توفي سنة ٧٦١ هـ، أو أنه كان من رجال القرن الثامن. وقال الزركلي: "وفي الفاتيكان (١٥٤١ عربي) نسخة من كتابه "حدائق الحقائق" كتب عليها اسمه: محمد بن محمد بن أبي بكر"؟. من آثاره "الذهب الابريز في تفسير الكتاب العزيز" و "أنموذج جليل في أسئلة وأجوية من غرائب آي التنزيل" طبع، و " غريب القرآن" قال صاحب كشف الظنون "أنه فرغ من تأليفه سنة ٦٦٨ هـ ". وذكر فيه أن طلبة العلم وحملة القرآن سألوه أن يجمع لهم تفسير غريب القرآن، فأجابهم، وضم إليه شيئا من الإعراب والمعاني. (١)
_________
(١) هدية العارفين ٢: ١٢٧ ومعجم المطبوعات ٩١٧ وفيه: وهو من علماء القرن الثامن، كان موجودا سنة ٧٦٨ هـ. وعبد الله مخلص في مجلة المجمع العلمي العربي بدمشق =','2','501',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (883,'المكي [٦٣٣ - ٦٩٦ هـ / ١٢٣٦ - ١٢٩٦ م]

<span data-type="title" id=toc-1204>محمد بن أبي بكر بن خليل بن إبراهيم بن يحيى العسقلاني المكي، رضي الدين، أبو عبد الله:</span>
شيخ الحرم المكي وفقيهه، نحوي، مفسر، من فقهاء الشافعية، روى عن ابن الجميزي وغيره، وعنه البرزالي وابن العطار. قال ابن حبيب: "كان إماما عالما فاضلا، جليل القدر، يعرف التفسير معرفة تامة، عليه مدار الفتوى بمكة". (١)

ابن مجير [... - ٧٠٩ هـ / ... - ١٣٠٩ م]

<span data-type="title" id=toc-1205>محمد بن أبي بكر بن مجير، شمس الدين:</span>
مفسر، متكلم، خطيب،
_________
= ٢٢: ٤١٨ وكشف الظنون ٩٢ و ١٢٠٨ ومجلة الرسالة ٨: ١٨٢٠ والأعلام ٦: ٢٧٩ ومعجم المؤلفين ٩: ١١٢ ومخطوطات الرياض ٥: ١٢٨ ومقدمة الصحاح لأحمد عبد الغفور العطار ٢٠٢ وهو فيه: محمد بن محمد، وأنه من أهل القرن الثامن، وله تاريخ لطيف من أول الخلافة الإسلامية إلى القرن الثامن، منه نسخة في المكتبة الأهلية بباريس.
(١) النجوم الزاهرة ٨: ١١١ وشذرات الذهب ٥: ٣٧، والوافي بالوفيات ٢: ٢٦٤ وتذكرة النبيه ١: ١٨٧ و ٢٠١.','2','502',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (884,'من أهل حصن الأكراد، وولي الخطابة فيها. له "تفسير القرآن" (١).

الإخنائي [٦٦٤ - ٧٣٢ هـ / ١٢٦٦ - ١٣٣١ م]

<span data-type="title" id=toc-1206>محمد بن أبي بكر بن عيسى بن بدران بن رحمة السعدي الإخنائي، علم الدين:</span>
قاض، محدث، مفسر، من فقهاء الشافعية. ولد بالقاهر، وأخذ عن الدمياطي وغيره، وولي قضاء الإسكندرية، فقضاء الشام خلفا للقونوي، ومات بها. وهو أخو محمد بن أبي بكر الإخنائي (المالكي المذهب) قاضي الديار المصرية المتوفى سنة ٧٥٠ هـ. قال الذهبي عن صاحب الترجمة: "من نبلاء العلماء، وقضاة السداد، شرع في "تفسير القرآن" (٢)
_________
(١) طبقات المفسرين للداودي ٢: ٩٨ والجواهر المضية.
(٢) طبقات الداودي ٢: ٩٧ والوافي ٢: ٢٦٩ وابن كثير ١٤: ١٦٠ وذيل العبر ١٧٥ والدرر الكامنة ٤: ٢٧ والسبكي ٦: ٤٥ وقضاة دمشق ٩٢ والشذرات ٦: ١٠٣. وانظر ترجمة أخيه في الديباج ٢: ٣٢١ والدرر الكامنة ٣: ٤٠٧ وشجرة النور ١: ١٨٧.','2','502',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (885,'ابن النقيب [٦٦١ - ٧٤٥ هـ / ١٢٦٣ - ١٣٤٤ م]

<span data-type="title" id=toc-1207>محمد بن أبي بكر بن إبراهيم بن عبد الرحمن، شمس الدين ابن النقيب:</span>
مفسر، محدث، قاض، من فقهاء الشافعية. من أهل دمشق، ولي قضاء حمص فطرابلس الشام فحلب، وعاد إلى دمشق فتولى التدريس بالشامية، وحدث وخرجت له مشيخة. من كتبه "مقدمة في التفسير" (١)

ابن قيم الجوزية [٦٩١ - ٧٥١ هـ / ١٢٩٢ - ١٣٥٠ م]

<span data-type="title" id=toc-1208>محمد بن أبي بكر بن أيوب بن سعد الزرعي الدمشقي، أبو عبد الله، شمس الدين، المعروف بابن قيم الجوزية:</span>
من كبار فقهاء الحنابلة، محدث، مفسر، متكلم جدلي، مشارك في بعض العلوم. ولد بدمشق، ولازم ابن تيمية وتخرج به. قال ابن حجر: "وهو الذي هذب كتبه ونشر علمه، واعتقل معه في قلعة
_________
(١) الدرر الكامنة ٤: ١٩ وهدية العارفين ٢: ١٥٢ والسبكي ٦: ٤٤ ومفتاح السعادة ١: ٤٤٣ ومعجم المؤلفين ٩: ١٠٤ وبروكلمان ٢: ١٠.','2','503',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (886,'دمشق بعد أن أهين وطيف به على جمل مضروبا بالدرة (العصا). وأفرج عنه بعد موت ابن تيمية. فتصدر لنشر العلم. ومات بدمشق. من كتبه "تفسير المعوذتين" طبع مرارا، و "تفسير الفاتحة" ولمحمد أويس الندوي كتاب "التفسير القيم، للإمام ابن القيم" استخرجه من مؤلفاته. (١)

الزكي [... - ٧٨٢ هـ / ١١٣٨٠ م]

<span data-type="title" id=toc-1209>محمد بن أبي بكر بن أحمد بن عمر الدوالي الزبيدي، أبو عبد الله، جمال الدين، المعروف بالزكي:</span>
عالم بالأدب، مشارك بالتفسير والحديث والنحو واللغة، من فقهاء الشافعية من أهل اليمن. قال في "العقد الثمين": "انتهت إليه الرياسة باليمن في علم الأدب" وقال ابن حجر: "كان نابغه في
_________
(١) طبقات المفسرين ٢: ٩٠ والوافي ٢: ٢٠٧ والبدر الطالع ٢: ١٤٣ والشذرات ٦: ١٦٨ والدرر الكامنة ٤: ٢١ والنجوم الزاهرة ١٠: ٢٤٩ والعبر ٢٨٢ والذيل على طبقات الحنابلة ٢: ٤٤٧ وروضات الجنات ٢٠٥ والبداية والنهاية ١٤: ٢٣٤ وبغية الوعاة ١: ٦٢ وهدية العارفين ٢: ١٥٨ وكشف الظنون ٨٩ و ١٦٨ و ٣٤١ و ٤٥٥ ومعجم المطبوعات ٢٢٢ والتاج المكلل ٤١٦.','2','503',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (887,'الأدب، مشاركا في غيره، وأشعاره سائرة في اليمن" (١).

ابن جماعة [٧٤٩ - ٨١٩ هـ / ١٣٤٨ - ١٤١٦ م]

<span data-type="title" id=toc-1210>محمد بن أبي بكر بن عبد العزيز بن محمد بن إبراهيم، أبو عبد الله عز الدين الكناني الحموي ثم المصري، المعروف بابن جماعة:</span>
فقيه شافعي، عالم بالأصول والجدل والفقه والتفسير والحديث واللغة والبيان، مشارك في غير ذلك، أصله من حماة (بسورية)، ومولده في ينبع (على شاطىء البحر الأحمر). إنتقل إلى القاهرة وسكنها وتتلمذ لابن خلدون والتاج السبكي والسراج البلقيني وغيرهم، قال ابن حجر: ومال إلى المعقول فأتقنه حتى صار أمة وحده، وبقيت طلبة البلد كلها عيالا عليه في ذلك. وكان أعجوبة دهره في حسن التقرير، ولم يرزق ملكة في الاختصار ولا سعادة في حسن التصنيف، بل كان بين قلمه
_________
(١) إنباء الغمر ١: ٢٢٧ والشذرات ٦: ٢٧٦ وطبقات المفسرين ٢: ٨٩ والعقد الثمين ١: ٤٢٥ وبغية الوعاة ١: ٦٢.','2','504',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (888,'ولسانه كما بينه هو وآحاد طلبته". له تصانيف كثيرة جمعت أسماؤها في كراسين. توفى بالقاهرة بالطاعون (١).

محب الدين [٩٤٩ - ١٠١٦ هـ / ١٥٤٢ - ١٦٠٨ م]

<span data-type="title" id=toc-1211>محمد بن أبي بكر بن داود بن عبد الرحمن العلواني الحموي، أبو الفضل، المعروف بمحب الدين بن تقي الدين:</span>
فقيه حنفي، مفسر، من كبار علماء عصره. ولد بحماة (بسورية)، وولي القضاء بمصر وحمص وحصن الأكراد ومعرة النعمان ومعرة نسرين وكلس واعزاز، ثم استقر بدمشق فولي بها القضاء نيابة بالمحكمة الكبرى، وقضاء العسكر بها، وقضاء الركب الشامي، وأفتى ودرس. مات بدمشق. من كتبه "تنزيل الآيات" طبع في شرح شواهد الكشاف، و "حواش" على أنوار
_________
(١) طبقات المفسرين ٢: ٩٤ والضوء اللامع ٧: ١٧١ وحسن المحاضرة ١: ٥٨٤ وإنباء الغمر ٣: ١١٥ وشذرات الذهب ٧: ١٣٩ وبغية الوعاة ٦٣ والبدر الطالع ٢: ١٤٧ وهدية العارفين ٢: ١٨٢ ومعجم المطبوعات ٦٥ والأعلام ٦: ٢٨٢ ومعجم المؤلفين ٩: ١١١.','2','504',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (889,'التنزيل، في التفسير، للبيضاوي. (١).

ساجقلي زاده [... - ١١٥٠ هـ / ... - ١٧٣٧ م]

<span data-type="title" id=toc-1212>محمد بن أبي بكر المرعشي، المعروف بساجقلي زاده:</span>
فقيه حنفي، صوفي، مفسر، مشارك في بعض العلوم، من أهل مرعش. كان مدرسا وإماما في جامعها. رحل ودخل دمشق، والتقى بالشيخ عبد الغني النابلسي وتصوف على يده. توفي بمرعش. من آثاره "رسالة " في الآيات المتشابهات، و "غاية البرهان في بيان أعظم آية في القرآن" في تفسير آية الكرسي. (٢)
_________
(١) خلاصة الأثر ٣: ٣٢٢ وهدية العارفين ٩: ٢٦٧ والأعلام ٦: ٢٨٥ ومعجم المؤلفين ٩: ١٠٩.
(٢) هدية العارفين ٢: ٣٢٢ وإيضاح المكنون ٢: ١٣٨ وتاريخ آدات اللغة العربية ٣: ٣٥١ وكتاب عثمانلي مؤلفلري ١: ٣٢٥ ومعجم المطبوعات ٩٩٥ ومعجم المؤلفين ٩: ١١٨ و ١٢: ١٤ وانظر حاشيته. وعلوم القرآن ٤٤ ودار الكتب الشعبية ١: ١٢٧. والأعلام ٦: ٦٠ وطوبقبو ٣: ٧٠٠ وفهرس البلدية ٦٧ وفهرس التيمورية ٣: ١٢٧.','2','505',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (890,'بهاء الدين زاده [... - نحو ٩٥٢ هـ / ... - ١٥٤٥ م]

<span data-type="title" id=toc-1213>محمد بن بهاء الدين بن لطف الله البيرامي الرومي، محيي الدين المعروف ببهاء الدين زاده:</span>
فقيه حنفي، صوفي، من الموالي الرومية، مشارك في الفقه وعلم الكلام والتفسير والحديث. من أهل قيصرية. ولي إفتاء "التخت السلطاني"، وحج سنة ٩٥١ هـ ومات بعد عودته. من كتبه "تفسير القرآن العظيم" (١)

الزركشي [٧٤٥ - ٧٩٤ هـ / ١٣٤٤ - ١٣٩٢ م]

<span data-type="title" id=toc-1214>محمد بن بهادر بن عبد الله الزركشي، أبو عبد الله، بدر الدين:</span>
فقيه شافعي، أصولي، مفسر، أديب، تركي الأصل، مصري المولد والوفاة، سمع بحلب وبدمشق، ودرس وأفتى، وولي مشيخة خانقاه كريم الدين بالقرافة الصغرى. من تصانيفه الكثيرة "البرهان في علوم
_________
(١) هدية العارفين ٢: ٢٣٨ والكواكب السائرة ٢: ٢٩ والشقائق النعمانية ٢: ٣٠ والشذرات ٨: ٢٩٣.','2','505',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (891,'القرآن" و "تفسير القرآن العظيم" وصل فيه إلى سورة مريم. و "كشف المعاني" في الكلام على قوله تعالى: {ولما بلغ أشده ..} (١)

البركلي [٩٢٩ - ٩٨١ هـ / ١٥٣٢ - ١٥٧٣ م]

<span data-type="title" id=toc-1215>محمد بن بير علي البركلي الرومي، محيي الدين:</span>
فقيه حنفي، مفسر، محدث، فرضي، عالم بالعربية نحوا وصرفا. تركي الأصل والمنشأ. من أهل قصبة "بالي كسرى"، ودرس بقصبة "بركي" فنسب إليها، ويقاله في هذه النسبة: بيركلي، وبركلي وبركوي. وانتقل في أواخر أيامه إلى القسطنطينية، وتوفي بها. من تصانيفه "تفسير سورة البقرة" (٢).
_________
(١) إنباء الغمر ١: ٤٤٦ والدرر الكامنة ٤: ١٧ وشذرات الذهب ٦: ٣٣٥ وحسن المحاضرة ١: ٤٣٧ وطبقات المفسرين للداودي ٢: ١٥٧ والنجوم الزاهرة ١٢: ١٣٤ والرسالة المستطرفة ١٤٢ والسلوك ٣/ ٢: ٧٧٩ وورد اسمه في بعض هذه المصادر محمد بن عبد الله بن بهادر.
(٢) هدية العارفين ٢: ٢٥٢ ومعجم المطبوعات ٦١٠ والمجددون في الإسلام ٣٧٧ والعقد المنظوم ٤٣٦ والأعلام ٦: ٢٨٦ ومعجم المؤلفين ٩: ١٢٣.','2','506',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (892,'البرغاني [١١٨٤ - ١٢٦٤ هـ / ١٧٧٠ - ١٨٤٨ م]

<span data-type="title" id=toc-1216>محمد تقي بن محمد البرغاني:</span>
فقيه إمامي، أصولي، متكلم، ولد في "برغان" - من قرى طهران - وإليها نسبته وبها نشأ وتعلم. وتنقل بين قزوين وقم وأصفهان والعراق وطهران، ثم استقر في قزوين. وأصدر فتوى بتكفير الفرقة البابية، فاغتاله نفر منهم وهو يصلي في المسجد ليلا. من آثاره "مجالس المؤمنين" طبع في الأخبار والمواعظ والتفسير والحديث. (١)

النقوي [١٢٣٤ - ١٢٨٩ هـ / ١٨١٩ - ١٨٧٢ م]

<span data-type="title" id=toc-1217>محمد تقي بن حسين بن دلدار علي النقوي، النصير آبادي، اللكهنوئي، سيد العلماء:</span>
مفسر، أصولي، فرضي، من أكابر فقهاء الإمامية، من أهل لكهنؤ بالهند، مولدا ومنشأ ووفاة، جمع مكتبة عظيمة ضمت عددا كبيرا من المخطوطات والكتب النادرة.
_________
(١) أعيان الشيعة ٤٤: ١٣٤ وأعلام الشيعة ٢: ٢٢٦ وشهداء الفضيلة ٣٢٣ وأحسن الوديعة ٣٠.','2','506',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (893,'من تصانيفه الكثيرة "ينابيع الأنوار في تفسير كلام الله الجبار" قال العاملي: "برز منه مجلدان ضخمان إلى سورة آل عمران، يشتمل على كثير من الحقائق، وقد تصدى فيه للمناظرة مع فخر الدين الرازي في تفسيره الكبير" (١)

الكاشاني [١٢٣٦ - ١٣٢١ هـ / ١٨٢١ - ١٩٠٣ م]

<span data-type="title" id=toc-1218>محمد تقي بن محمد حسين الكاشاني:</span>
نزيل طهران، من مشاهير علماء الإمامية في الحديث والتفسير والفقه والكلام. تعلم في النجف، ومات بطهران. من تصانيفه "إيضاح المشكلات" في التفسير (٢)

القيصري [... - ١٣١١ هـ / ... - ١٨٩٣ م]

<span data-type="title" id=toc-1219>محمد ثابت بن عبد الله القيصري الرومي:</span>
فقيه حنفي، مفسر، مشارك
_________
(١) أعيان الشيعة ٤٤: ١١٩ والذريعة ١: ٥١٨ وأحسن الوديعة ٦٧ وأعلام الشيعة ٢:
(٢) أعيان الشيعة ٤٤: ١٢٥ والذريعة ٢: ٤٩٩ و ٤: ٤٨٩ وأعلام الشيعة ١: ٢٥٣ وهدية العارفين ٢: ٣٩٢ ونقباء البشر ١: ٢٥٣.','2','507',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (894,'في بعض العلوم. من آثاره "مرآة الحامدين في كشف أسرار المحققين" تفسير الفاتحة (١)

ثناء الله الهندي [... - ١٢١٦ هـ / ... - ١٨٠١ م]

<span data-type="title" id=toc-1220>محمد ثناء الله الباني بتي النقشبندي الهندي:</span>
فقيه حنفي، مفسر، من أهل الهند، من آثاره "التفسير المظهري" أوله: الحمد لله ذي العظمة والكبرياء الخ .. سبعة أجزاء في مجلد واحد، طبع بالهند. (٢)

اليماني [... - نحو ١٩٠ هـ / ... - نحو ٨٠٦ م]

<span data-type="title" id=toc-1221>محمد بن ثور الصنعاني اليماني، أبو عبد الله:</span>
محدث ثقة، مفسر، زاهد، من أهل صنعاء. روى عن معمر بن راشد وابن جريج وغيرهما، روى عنه عبد الرزاق بن همام وزيد بن المبارك وآخرون. وثقه النسائي وابن معين، وقال ابن أبي حاتم. سألت أبي ما حال
_________
(١) هدية العارفين ٢: ٢٩٣
(٢) هدية العارفين ٢: ٣٥٣ ومعجم المطبوعات ١٦٤٥ وإيضاح المكنون ١: ٣١٠ و ٢: ٤٢٠ واسمه هنا محمد سعد الله.','2','507',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (895,'ابن ثور؟ قال: "الفضل والعبادة والصدق". ذكره ابن حبان في "الثقات" وقال: "مات سنة تسعين ومائة أو قبلها بقليل أو بعدها بقليل". له "تفسير القرآن" رواه عن ابن جريج (١).

ابن جرير الطبري [٢٢٤ - ٣١٠ هـ / ٨٣٩ - ٩٢٣ م]

<span data-type="title" id=toc-1222>محمد بن جرير بن يزيد الطبري، أبو جعفر:</span>
من مشاهير المؤرخين والمفسرين وأئمة العلماء، ولد في آمل طبرستان، وبها نشأ وحفظ القرآن صغيرا، ثم رحل في طلب العلم، فسمع بالري وبغداد والبصرة والكوفة والشام ومصر، وعاد، فاستوطن بغداد، واعتنق المذهب الشافعي، وعارض الحنابلة، وأسس مذهب "الجريرية" في الفقه، وهو فرع من الشافعية، فلم يعمر طويلا. وعرض عليه القضاء فامتنع، والمظالم فأبى.
_________
(١) تهذيب التهذيب ٩: ٨٧ والتاريخ الكبير للبخاري ١: ٥٢ وتاريخ الطبري ١: ٤٠ وانظر فهرسته، والفهرست لابن النديم ٣٤ وفيه "أن ابن ثور روى تفسيره عن معمر". وطبقات المفسرين ٢: ١٠٦ نقلا عن ابن النديم. والتفسير والمفسرون ٨٠ و ١٤٤.','2','508',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (896,'قال الخطيب البغدادي: "جمع من العلوم ما لم يشاركه فيه أحد من أهل عصره، من كتبه "جامع البيان في تفسير القرآن" ويعرف بتفسير الطبري، طبع في ٣٠ جزءا، وهو أوسع كتاب وصلنا في التفسير بالمأثور، قال الإمام النووي: "كتاب ابن جرير في التفسير لم يصنف أحد مثله .. " وقال أبو حامد الاسفرايني: "لو سافر رجل إلى الصين حتى يحصل له تفسير ابن جرير لم يكن ذلك كثيرا .. " (١)
_________
(١) تاريخ بغداد ٢: - ١٦٢ وتذكره الحفاظ ٧١٠ وتهذيب الأسماء واللغات ١: ٧٨ وطبقات المفسرين للسيوطي ٣٠ وطبقات المفسرين للداودي ٢: ٦ - ١ وطبقات القراء للذهبي ١: ٢١٣، وطبقات الفقهاء: ٧٦، والمنتظم ٦:.١٧ ومعجم الأدباء ١٨: ٤٠ وميزان الاعتدال ٣: ٤٩٨ والوافي ٢: ٢٨٤ ووفيات الأعيان ٤: ١٩١ والنجوم الزاهرة ٣: ٢٠٥ واللباب ٢: ٢٧٤ ولسان الميزان ٥: ١٠٠ وغاية النهاية ٢: ١٠٦ وروضات الجنات ١٦٣ والبداية والنهاية ١١: ١٤٥ والشذرات ٢: ٢٦٠ وفوائد الرضوية ٤٤٦ وتنقيح المقال ٢: ٩٠٠ وطبقات الشافعية للسبكي ٣: ١٢٠ والموسوعة العربية الميسرة ١١٥٣ والأعلام ٦: ٦٩ وهدية العارفين ٢: ٢٦ وكنوز الأجداد ١١٧ ومعجم المؤلفين ٩: ١٤٨ وطبقات العبادي ٥٢ ومقدمة تفسير الطبري لمحمود محمد شاكر. وكشف الظنون ٤٣٧ والتفسير والمفسرون ١: ٢٠٥ والمذاهب الإسلامية في تفسير القرآن ٨٥ ومختصر دول الإسلام ١: ١٤٧ ومرآة الجنان ٢: ٢٦١.','2','508',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (897,'الشرواني [... - ١٠٦٣ هـ / ... - ١٦٥٣ م]

<span data-type="title" id=toc-1223>محمد بن جمال الدين بن رمضان الشرواني السكري، محيي الدين:</span>
مفسر، من آثاره "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي. قال في كشف الظنون: "في مجلدين، أولها "قال الفقير بعد حمد الله العليم العلام .. ". وفي إيضاح المكنون أنها في أربع مجلدات، وقال آغا بزرك: "توجد نسخة منها عند محمد المشكاة في طهران عليها خط التملك ١٠٩٢ هـ (١)

ابن البابا [٦٩٧ - ٧٤١ هـ / ١٢٩٨ - ١٣٤٠ م]

<span data-type="title" id=toc-1224>محمد بن جنكلي بن البابا بن محمد، ناصر الدين:</span>
أحد أمراء الدولة الناصرية بمصر. ولد بديار بكر، وقدم مع والده القاهرة سنة ٧٠٣ هـ، وبها نشأ وتعلم. كان في أول أمره حنفيا، ثم تحول حنبليا، ومال في آخر أمره إلى مذهب أهل الظاهر. قال المقريزي: "كان آية في معرفة فقه السلف ونقل
_________
(١) كشف الظنون ١: ١٨٩ وإيضاح المكنون ١: ١٤٠ والكشاف لطلس ٢٦ وعنه أخذنا تاريخ وفاته. وتعليقات آغا بزرك على كشف الظنون ٢٠.','2','509',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (898,'مذاهبهم وأقوال الصحابة والتابعين، وشارك في علم التفسير والبيان، وحدث." وقال ابن حجر: "وخرج له أبو الحسين الدمياطي أربعين حديثا حدث بها قبل موته" (١)

البلاغي [١٢٨٢ - ١٣٥٢ هـ / ١٨٦٤ - ١٩٣٣ م]

<span data-type="title" id=toc-1225>محمد جواد بن حسن بن طالب بن عباس بن إبراهيم البلاغي النجفي الربعي:</span>
فقيه إمامي، من كبارهم، مفسر، متكلم، أصولي، عارف بالعبرية والفارسية والإنجليزية. من أهل النجف مولدا ووفاة. شارك في حركة العراق الاستقلالية وثورة ١٩٢٠ ضد الإنجليز. من آثاره "آلاء الرحمن في تفسير القرآن" طبع الجزآن الأول والثاني منه. (٢)
_________
(١) طبقات المفسرين ٢: ١١٥ والوافي ٢: ٣١٠ والدرر الكامنة ٤: ٣٦ والنجوم الزاهرة ١٥: ٥٥ والمقفى ١: ١٩٣.
(٢) نقباء البشر ١: ٣٢٣ وأعلام الشيعة ١: ٣٢٣ وأعيان الشيعة ١٧: ٦٧ والأعلام ٦: ٣٠٢ ومعجم المؤلفين ٣: ١٦٤ و ٩: ١٦٣.','2','509',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (899,'مغنية [١٣٢٢ - ١٤٠٠ هـ / ١٩٠٤ - ١٩٧٩ م]

<span data-type="title" id=toc-1226>محمد جواد مغنية العاملي:</span>
مفسر، من علماء الشيعة الإمامية. ولد في طيردبا (قضاء صور) بلبنان، وتعلم بالنجف. وعاد، فسكن بيروت، وشارك في النهضة الإسلامية الثقافية وغيرها، وله تآليف في هذه المجالات. مات ببيروت ودفن بالنجف. من أشهر آثاره "التفسير الكاشف" ٧ مجلدات، طبع، و "التفسير المبين على هامش القرآن الكريم" (١)

السمين [... - ٢٣٥ هـ / ... - ٨٥٠ م]

<span data-type="title" id=toc-1227>محمد بن حاتم بن ميمون المروزي ثم البغدادي، أبو عبد الله القطيعي، المعروف بالسمين:</span>
حافظ للحديث، مفسر، أصله من مرو، وسكن بغداد. روى عن وكيع وابن عيينة وابن مهدي والقطان وطبقتهم. وروى عنه مسلم وأبو داود وأبو زرعة وأبو حاتم وغيرهم. وثقه ابن عدي والدارقطني، وذكره ابن حبان في الثقات، قال أبو
_________
(١) تجارب محمد جواد مغنية بقلمه، طبع سنة ١٩٨٠.','2','510',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (900,'حفص الفلاس: ليس بشيء، وقال الذهبي: "وهذا جرح مردود". ونقل الذهبي عن ابن سعد قوله: "جمع كتابا في "تفسير القرآن" كتبه عنه الناس ببغداد، وكان ينزل قطيعة الربيع". (١)

ابن حبان [٢٧٠ - ٣٥٤ هـ / ٨٨٤ - ٩٦٥ م]

<span data-type="title" id=toc-1228>محمد بن حبان بن أحمد بن حبان بن معاد بن معبد التميمي الحنظلي، أبو حاتم البستي، ويقال ابن حبان:</span>
من مشاهير المحدثين في عصره، مؤرخ، فقيه، جغرافي، عالم باللغة والطب والفلك. ولد في بست - من بلاد سجستان -. ورحل إلى خراسان والشام ومصر والعراق والجزيرة والحجاز. وولي القضاء بسمرقند مدة، ثم في نسا وغيرها من مدن خراسان. وسكن نيسابور وحدث بها بمصنفاته. قال في لسان الميزان: "وكانت الرحلة إلى مصنفاته بخراسان". وعاد إلى
_________
(١) طبقات المفسرين للداودي ٢: وتذكرة الحفاظ ٢: ٤٥٥ والوافي ٢: ٣١٥ وتهذيب التهذيب ٩: ١٠١ وتاريخ بغداد ٢: ٢٦٦ وشذرات الذهب ٢: ٨٦ وهدية العارفين ٢: ١٢ ومعجم المؤلفين ٩: ١٦٧.','2','510',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (901,'بست سنة ٣٤٠ هـ (٩٥١ م) فوفد عليه العلماء من مختلف الأمصار ليأخذوا عنه حق رواية كتبه. وتوفي في بست. من تصانيفه الكثيرة "تفسير القرآن" مخطوط، قسم منه (من سورة ٢٩ إلى آخر القرآن) في مكتبة جامعة استنبول. (١)

حسام زاده [... - ١٠٨٠ هـ / ... - ١٦٦٩ م]

<span data-type="title" id=toc-1229>محمد بن حسام الدين الرومي، المعروف بحسام زاده:</span>
فقيه حنفي، قاض، مفسر، تركي الأصل، مستعرب، ولي قضاء مكة، ومات معزولا عن القضاء. له "تفسير القرآن" إلى سورة المائدة. (٢)
_________
(١) الوافي ٣١٧: ٢ ولسان الميزان ١١٢: ٥ والنجوم الزاهرة ٣: ٣٤٢ وشذرات الذهب ٣: ١٦ ومرآة الجنان ٢: ٣٥٧ وتذكرة الحفاظ ٩٢٠ واللباب ١: ١٥١ وطبقات السبكي ٢: ١٤١ والبداية والنهاية ١١: ٢٥٩ وميزان الاعتدال ٣: ٥٥٦ ومختصر دول الإسلام ١: ١٧٢ وتاريخ التراث العربي ١: ٤٧١ وكنوز الأجداد ١٥٤ والأعلام ٦: ٣٠٦ وهدية العارفين ٢: ٤٤ ومفتاح السعادة ٢: ١٥ ومعجم المؤلفين ٩: ١٧٣.
(٢) هدية العارفين ٢: ٢٩٢.','2','511',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (902,'الرؤاسي [... - ١٨٧ هـ / ... - ٨٠٣ م]

<span data-type="title" id=toc-1230>محمد بن حسن بن أبي سارة الرؤاسي النيلي، أبو جعفر:</span>
أول من وضع من الكوفيين كتابا في النحو. سمي بالرؤاسي لأنه كان كبير الرأس، وكان ينزل النيل فقيل له النيلي. وهو أستاذ الكسائي والفراء. من كتبه "معاني القرآن" (١)

الأحول [... - حيا ٢٥٩ م هـ / ... - حيا ٧٣ م]

<span data-type="title" id=toc-1231>محمد بن الحسن دينار الأحول الكوفي، أبوالعباس:</span>
عالم بالعربية، أديب، جعله الزبيدي في طبقة المبرد وثعلب. وقال الصفدي: "كان ناسخا غزير العلم واسع الفهم جيد الرواية حسن الدراية". روى عنه نفطويه النحوي
_________
(١) طبقات المفسرين ٢: ١٣٠ والوافي ٢: ٣٣٤ ومعجم الأدباء ١٨: ١٢١ و ٢٥٣ وهدية العارفين ٢: ٧ وروضات الجنات ١٥٦ وبغية الوعاة ١: ٨٢ وغاية النهاية ٢: ١١٦ ومراتب النحويين ٢٤ وكشف الظنون ١٧٣٠ وبروكلمان: الذيل ١: ١٧٧ والفهرست ٦٤ ونزهة الألباء ٥٤ والمقتبس ٢٧٩ والأعلام ٧: ١٥٤ وطبقات الزبيدي ١٣٥ وإنباه الرواة ٤: ٩٩ وهو في بعض هذه المصادر محمد بن أبي سارة علي','2','511',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (903,'ومحمد بن العباس اليزيدي. من كتبه "غريب القرآن" ذكره ابن النديم (١).

الصفار [... - ٢٩٠ هـ / ... - ٩٠٣ م]

<span data-type="title" id=toc-1232>محمد بن الحسن بن فروخ الصفار، أبو جعفر، المعروف بالأعرج القمي:</span>
من فقهاء الشيعة الإمامية، من أهل قم. له تصانيف منها "فضائل القرآن". (٢)

ابن دريد [٢٢٣ - ٣٢١ هـ / ٨٣٨ - ٩٣٣ م]

<span data-type="title" id=toc-1233>محمد بن الحسن بن دريد الأزدي، أبو بكر:</span>
لغوي وشاعر، وهو صاحب "المقصورة الدريدية". ولد في البصرة، وقرأ على علمائها. وانتقل إلى عمان فأقام اثني عشر عاما، ثم سافر إلى جزيرة ابن عمر، ثم رحل إلى
_________
(١) الوافي وتاريخ بغداد ٢: ١٨٥ والفهرست لابن النديم ٣٧ و ٨٧ ومعجم الأدباء ١٨: ١٢٥ وهدية العارفين ٢: ١٦ وكشف الظنون ١٤٤٧ وبغيب الوعاة ١: ٨١ وطبقات المفسرين للداودي ٢: ١٤٣ وهو فيه: محمد بن دينار.
(٢) هدية العارفين ٢: ٢٤ وإيضاح المكنون ٢: ١٩٩ وفهرست الطوسي ١٤٣.','2','512',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (904,'نواحي فارس فقلده "آل ميكال" ديوان فارس، ومدحهم "بالمقصورة" فوصلوه بعشرة آلاف درهم. ولما عزل آل ميكال رجع إلى بغداد، واتصل بالمقتدر العباسي فأجرى عليه في الشهر خمسين دينارا إلى أن مات. من كتبه "غريب القرآن" لم يكمله. (١)

القمي [.. - ٣٤٣ هـ / .. - ٩٥٤ م]

<span data-type="title" id=toc-1234>محمد بن الحسن بن أحمد بن الوليد القمي، أبو جعفر:</span>
فقيه إمامي، محدث، مفسر، عارف بالرجال. من آثاره "تفسير القرآن" (٢).
_________
(١) طبقات المفسرين ٢: ١١٩ والوافي ٢: ٣٣٩ وتذكرة الحفاظ ٣: ٨١٠ ومعجم الأدباء ١٨: ١٢٧ وميزان الاعتدال ٣: ٥٢٥ وبغية الوعاة ١: ٧٦ ومراتب النحويين ٨٤ ولسان الميزان ٥: ١٣٢ وتاريخ بغداد ٢: ١٩٥ ووفيات الأعيان ٤: ٣٢٣ والعبر ٢: ١٨٧ ونزهة الألباء ٢٥٨ ومرآة الجنان ٢: ٢٨٢ والشذرات ٢: ٢٨٩ وطبقات السبكي ٣: ١٣٨ وروضات الجنات ١٦٦ والمنتظم ٦: ٢٦١ وأعيان الشيعة ٤٤: ١٦ والنجوم الزاهرة ٣: ٢٤٠ وغاية النهاية ٢: ١١٦ وهدية العارفين ٢: ٣٢ وابن النديم ٦١ والأعلام ٦: ٣١٠ ومعجم المؤلفين ٩: ١٨٩ وفي الحاشية ذكر لمصادر أخرى، ومثله في إنباه الرواة ٣: ٩٢.
(٢) الطوسي ١٥٦ وهدية العارفين ٢: ٤١ ومعجم المؤلفين ٩: ١٨٣.','2','512',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (905,'النقاش [٢٦٦ - ٣٥١ هـ / ٨٨٠ - ٩٦٢ م]

<span data-type="title" id=toc-1235>محمد بن الحسن بن محمد بن زياد بن هارون بن جعفر، أبو بكر النقاش:</span>
عالم بالقراءات والتفسير. أصله من الموصل. ولد ببغداد ونشأ بها، وسمع بالشام ومصر والجزيرة والموصل والجبال وخراسان وما وراء النهر والكوفة والبصرة ومكة. وكان في مبدأ أمره يتعاطى نقش السقوف والحيطان فعرف بالنقاش. ضغفه جماعة قال البرقاني: "كل حديث النقاش منكر" وقال الخطيب البغدادي: "في حديثه مناكير بأسانيد مشهوره" وقال الذهبي: "الذي وضح أن هذا الرجل مع جلالته ونبله متروك ليس بثقة". من تصانيفه "شفاء الصدور المهذب في تفسير القرآن"، قال هبة الله اللالكائي: "تفسير النقاش شقاء للصدور وليس شفاء الصدور" وله "الإشارة في غريب القرآن" و "الموضح في معاني القرآن". (١)
_________
(١) طبقات المفسرين للسيوطي ٢٩ وطبقات المفسرين للداودي ٢: ١٣٢ ووفيات الأعيان ٤: ٢٩٨ وتاريخ بغداد ٢: ٢٠١ والرسالة المستطرفة ٧٧ وميزان الاعتدال ٣: ٥٢١ ومعجم الأدباء ١٨: ١٤٦ والوافي ٢: ٣٤٥ وغاية النهاية ٢: ١١٩ والمنتظم ٧: ١٤ والعبر ٢: ٢٩١ والسبكي ٣: ١٤٥ وتذكرة الحفاظ ٣: ٩٠٨ والفهرست ٣٣ ولسان =','2','513',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (906,'ابن مقسم العطار [٢٦٥ - ٣٥٤ هـ / ٨٧٨ - ٩٦٥ م]

<span data-type="title" id=toc-1236>محمد بن الحسن بن يعقوب بن الحسن ابن الحسن بن محمد، بن مقسم العطار، أبو بكر:</span>
عالم بالقراءات والعربية، مفسر، فقيه، من أهل بغداد، مولدا ووفاة. قال ياقوت: "كان من أعرف الناس بالقراءات وأحفظهم لنحو الكوفيين، ولم يكن فيه عيب إلا أنه قرأ بحروف تخالف الإجماع، واستخرج لها وجوها من اللغة والمعنى، فشاع أمره، فأحضر إلى السلطان واستتابه، وقيل: استمر يقرىء بما كان عليه إلى أن مات. من كتبه "الأنوار" في تفسير القرآن. (١)
_________
= الميزان ٥: ١٣٢ والبداية والنهاية ١١: ٢٤٢ والشذرات ٣: ٨ وطبقات القراء للذهبي ١: ٢٣٦ والأعلام ٦: ٣١٠ ومعجم المؤلفين ٩: ٢١٤ ومرآة الجنان ٢: ٣٤٧ وتاريخ التراث العربي ٢١٤
(١) الوافي ٢: ٣٣٧ وغاية النهاية ٢: ١٢٣ وطبقات المفسرين ٢: ١٢٧ وبغية الوعاة ١: ٨٩ وتاريخ بغداد ٢: ٢٠٦ والعبر ٢: ٣١٠ ومعجم الأدباء ١٨: ١٥٠ والشذرات ٣: ١٦ والبداية والنهاية ١١: ٢٥٩ وهدية العارفين ٢: ٤٧ والمنتظم ٧: ٣٠ والأعلام ٦: ٣١١ ومعجم المؤلفين ٩: ٢٢٧ وفهرست ابن النديم ٣٥ والكامل في التاريخ ٨ أنظر فهرسته. وكشف الظنون في أماكن متفرقة. ومثله في إيضاح المكنون.','2','513',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (907,'الزوزني [... - ٣٧٠ هـ / ... - ٩٨٠ م]

<span data-type="title" id=toc-1237>محمد بن الحسن (وقبل: الحسين، وقيل: عبد الحسن) بن سليمان الزوزني، أبو جعفر:</span>
فقيه شافعي، مفسر، قاض، محدث، شاعر. ولي القضاء بنواحي خراسان وما وراء النهر. ذكر أن مصنفاته في التفسير والحديث والفقه وأنواع العلوم، تزيد على المائة. قدم على الصاحب بن عباد، فعرض عليه القضاء على شرط انتحال مذهبه - يعني الاعتزال - فامتنع وقال: "لا أبيع الدين بالدنيا" قال مترجموه: "وكان مع علو مرتبته في العلم يحب منصب القضاء". توفي ببخارى. (١)

الختن [٣١١ - ٣٨٦ هـ / ٩٢٤ - ٩٩٦ م]

<span data-type="title" id=toc-1238>محمد بن الحسن بن إبراهيم الاستراباذي، وقيل: الجرجاني، أبو عبد الله، المعروف بالختن:</span>
من أئمة الشافعية في عصره، كان مقدما في الأدب، ومعاني القرآن، والقراءات، ومن العلماء المبرزين في النظر والجدل. دخل نيسابور
_________
(١) طبقات المفسرين ٢: ١٢٣ ويتيمة الدهر ٤: ٣٤٣ وطبقات السبكي ٣: ١٤٣ ومعجم المؤلفين ٩: ١٩٣.','2','514',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (908,'سنة ٢٢٧ فأقام بها إلى آخر سنة ٣٣٩، ثم انتقل إلى أصبهان، ومنها إلى العراق، فنيسابور، ثم عاد إلى جرجان ومات بها. وقيل له الختن لأنه كان ختن الإمام أبي بكر الإسماعيلي. (١)

ابن فورك [... - ٤٠٦ هـ / ... - ١٠١٥ م]

<span data-type="title" id=toc-1239>محمد بن الحسن بن فورك الأنصاري الأصبهاني، أبو بكر:</span>
فقيه شافعي، متكلم، أصولي، مفسر، محدث، واعظ، أديب، نحوي. سمع بالبصرة وبغداد، وورد الري، وحدث بنيسابور، وبني له بها مدرسة ودارا. ثم دعي إلى غزنة بالهند، وجرت له بها مناظرات، فلما عاد إلى نيسابور سم في الطريق، فمات، فنقل إليها. قال في النجوم الزاهرة: "قتله محمود بن سبكتكين بالسم، لقوله: كان رسول الله - صلى الله عليه وسلم - رسولا في حياته فقط، وإن روحه قد بطل
_________
(١) طبقات المفسرين ٢: ١١٧ والوافي ٢: ٢٢٨ والسبكي ٣: ١٣٦ وطبقات بن هداية الله (بتحقيقنا) ١٠٤ والعبر ٣: ٣٣ وطبقات الفقهاء ١٢١ والشذرات ٣: ١٢٠ ومرآة الجنان ٢: ٤٣١ وتاريخ جرجان ٤٠٨ وطبقات العبادي ١١١ وتهذيب الأسماء واللغات ٢: ٢٥٥ وهدية العارفين ٢: ٥٥ وابن خلكان ٤: ٢٠٣.','2','514',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (909,'وتلاشى". له نحو مئة كتاب، منها "تفسير القرآن" الجزء الثالث منه، في خزانة فيض الله باستنبول، الرقم ٥٠، و "حل الآيات المتشابهات" في ٧٤ ورقة، بخزانة عاطف باستنبول رقم ٤٣٣، و "غريب القرآن" في ١٣٩ ورقة في خزانة سليم أغا اسكيدار باستنبول رقم ٢٢٧. (١)

الطوسي [٣٨٥ - ٤٦٠ هـ / ٩٩٥ - ١٠٦٧ م]

<span data-type="title" id=toc-1240>محمد بن الحسن بن علي الطوسي، أبو جعفر:</span>
فقيه الشيعة الإمامية ومصنفهم. ولد في طوس، وانتقل إلى بغداد سنة ٤٠٨ هـ وأقام أربعين سنة، وكان يسكن بالكرخ. وقد أحرقت كتبه عدة مرات بمحضر من الناس. ثم تحول إلى "الغري" بالنجف، وأقام بالمشهد يفقه
_________
(١) طبقات المفسرين للداودي ٢: ١٢٩ والوافي ٢: ٣٤٤ وتاج التراجم ١٨٤ والعبر ٣: ٩٥ وتبيين كذب المفتري ٢٣٢ وابن خلكان ٤: ٢٧٢ والشذرات ٣: ١٨١ والنجوم الزاهرة ٤: ٢٤٠ وطبقات السبكي ٣: ٥٢ وإنباء الرواة ٣: ١١٠ ومرآة الجنان ٣: ١٧ وهدية العارفين ٢: ٦٠ وكشف الظنون ٤٣٩ وتاريخ التراث العربي ٢: ٣٨٧ والأعلام ٦: ٣١٣ ومعجم المؤلفين ٩: ٢٠٨ واللباب لابن الأثير ٢: ٢٢٦ وشذرات الذهب ٣: ١٨١.','2','515',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (910,'الناس إلى أن توفي. من تصانيفه "التبيان في تفسير القرآن" في عشر مجلدات، حققه ونشره أحمد حبيب العاملي، و "المسائل الدمشقية في تفسير القرآن" وهي اثنتي عشرة مسألة في تفسير القرآن، و "المسائل الرجبية" في تفسير آي من القرآن (١)

الجعفري [... - ٤٦٣ هـ / ... - ١٠٧١ م]

<span data-type="title" id=toc-1241>محمد بن الحسن بن علي بن حمزة البغدادي المعروف بالجعفري، أبو يعلى:</span>
فقيه إمامي، متكلم، مفسر، من أهل بغداد، كان يتفقه على مذهب الشافعي، من كتبه "تفسير القرآن" (٢)

الفتال [القرن السادس الهجري / القرن الثاني عشر الميلادي]

<span data-type="title" id=toc-1242>محمد بن الحسن بن علي بن أحمد بن علي، أبو علي، الفتال النيسابوري (الفارسي)</span>
_________
(١) مقدمة التبيان بقلم العاملي، وأعيان الشيعة ٤٤: ٣٣ والوافي ٢: ٣٤٩ والمتظم ٨: ٢٥٢ وطبقات المفسرين ٢: ١٢٧ وطبقات السبكي ٤: ١٢٦ وطبقات المفسرين للسيوطي ٢٩ والبداية والنهاية ١٢: ٩٧ والنجوم الزاهرة ٥: ٨٢ وهدية العارفين ٢: ٧٢ وإيضاح المكنون ١: ٢٢٣ وروضات الجنات ٥٨٠ والذريعة ٣: ٣٢٨ والفهرست للطوسي ٢٨٥ وكشف الظنون ٤٥٢ والأعلام ٦: ٣١٥
(٢) هدية العارفين ٢: ٧٣ والنجاشي ٢٨٨ وفوائد الرضوية ٤٥٦.','2','515',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (911,'الفارسي: واعظ، مفسر، فقيه إمامي. من آثاره "التنوير في معاني التفسير" (١)

الشيباني [... - حيا ٦٤٠ هـ / ... - حيا ١٢٤٢ م]

<span data-type="title" id=toc-1243>محمد بن الحسن الشيباني:</span>
مفسر، من كتبه "نهج البيان عن كشف معاني القرآن" في التفسير، ألفه باسم الخليفة العباسي المستنصر بالله منصور بن محمد، الذي ولي الخلافة سنة ٦٢٣ وتوفي سنة ٦٤٠ هـ. (٢)

ابن المؤذن [... - ٦٦٩ هـ / ... - ١٢٩٧ م]

<span data-type="title" id=toc-1244>محمد بن الحسن بن علي بن محمد بن بندار ابن طفيل المرادي، أبو عبد الله، المعروف بابن المؤذن:</span>
من العلماء بالعربية، شاعر، مفسر، من أهل غرناطة بالأندلس، وبها نشأ وتعلم. قال ابن الخطيب: "كان صاحب قدم في العربية، إماما في اللغة والأخبار، شاعرا مجيدا، حافظا للتفسير كاتبا، بقية من بقايا أهل الأدب". (٣)
_________
(١) روضات الجنات ٥٩١ وإيضاح المكنون ١: ٣٣٤ ومعجم المؤلفين ٩: ٢٠٠.
(٢) معجم المؤلفين ٩: ١٩٤.
(٣) بغية الوعاة ١: ٨٦ وطبقات المفسرين للداودي ٢: ١٢٩.','2','516',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (912,'الواسطي [٧١٧ - ٧٧٦ هـ / ١٣١٧ - ١٣٧٤ م]

<span data-type="title" id=toc-1245>محمد بن حسن بن عبد الله بن محمد الحسيني الواسطي، أبو عبد الله شمس الدين:</span>
مفسر، أصولي، متكلم، من شيوخ الشافعية. من أهل واسط. قال ابن حجر: "اشتغل ببلاده، ثم قدم فسمع الحديث بمصر وبرع في الفقه والأصول". وانتقل إلى دمشق فدرس بالصارمية وأعاد بالشامية البرانية، وتوفي بها. من كتبه "تفسير القرآن" كبير. (١)

الكواكبي [١٠١٨ - ١٠٩٦ هـ / ١٦٠٩ - ١٦٨٥ م]

<span data-type="title" id=toc-1246>محمد بن حسن بن أحمد الكواكبي الحلبي:</span>
فقيه حنفي، أصولي، من أهل حلب، مولدا ووفاة، وتولى الإفتاء بها. من كتبه "حاشية على تفسير البيضاوي" مخطوطة و "أبحاث تتعلق بسورة الأنعام" مخطوطة (٢)
_________
(١) إنباء الغمر ١: ٩٠ والدرر الكامنة ٤: ٤١ والدارس ١: ٣٢٨ والتراث ٦: ٢٠٥ و ٢٤٤ ومعجم المؤلفين ٩: ١٩٨ وفي الحاشية أسماء مصادر أخرى، وطبقات المفسرين للداودي ٢: ١٢٥.
(٢) خلاصة الأثر ٣: ٤٣٧ وإعلام النبلاء ٦: ٣٨٠ وهدية العارفين ٢: ٢٩٩ وإيضاح المكنون ١٤٢ والأعلام ٦: ٣٢١ ومعجم المؤلفين ٩: ١٨٢.','2','516',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (913,'الفاضل الهندي [١٠٦٢ - ١١٣٧ هـ / ١٦٥٢ - ١٧٢٥ م]

<span data-type="title" id=toc-1247>محمد بن حسن بن محمد الأصبهاني، بهاء الدين، المعروف بالفاضل الهندي:</span>
فقيه إمامي، من كبارهم، مفسر، من أهل أصبهان، توفي بها قتيلا. من كتبه "تفسير القرآن". (١)

الجنوي [١١٣٥ - ١٢٠٠ هـ / ١٧٢٣ - ١٧٨٦ م]

<span data-type="title" id=toc-1248>محمد بن الحسن الجنوي الحسني التطاوني، أبو عبد الله:</span>
فقيه مالكي، مفسر، أصولي، مشارك في بعض العلوم. ولد بمدشر (أزجن)، وسكن مكناس، وتوفي بمراكش. من كتبه "حاشية" على أنوار التنزيل، في التفسير للبيضاوي. و "حاشية" على تفسير الجلالين (٢).
_________
(١) فوائد الرضوية ٤٧٧ وهدية العارفين ٢: ٣١٨ وإيضاح المكنون ١: ٣٠٩ وأعيان الشيعة ٤٣: ٣٣٦ ووفاته فيه سنة ١١٢٦.
(٢) شجرة النور الزكية ٣٧٥ وأخبار مكناس ٤: ١٣٥ وفيه وفاته سنة ١٢٠٠ هـ. والأعلام المراكشية ٥: ٩٣ ومعجم المؤلفين ٩: ١٨٧ والأعلام ٦: ٩٢.','2','517',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (914,'الورنو سفادراني [... - حيا ١٢٤١ هـ / ... - حيا ١٨٢٦ م]

<span data-type="title" id=toc-1249>محمد حسن بن محمد كاظم الورنو سفادراني (نسبة إلى قرية من قرى أصبهان):</span>
فقيه إمامي، مفسر، قال آغا بزرك الطهراني: له "تفسير القرآن" فرغ منه في سلخ ذي القعدة سنة ١٢٤١ هـ " (١).

الأصفهاني [١٢٠٧ - ١٢٦٣ هـ / ١٧٩٣ - ١٨٤٧ م]

<span data-type="title" id=toc-1250>محمد حسن بن محمد تقي بن محمد سعيد الموسوي اليزدي الأصفهاني:</span>
فقيه إمامي، عارف بالتفسير، مشارك في بعض العلوم. له "إعجاز القرآن" وتفسير آية {يا أرض ابلعي}. (٢)

أبو الهدى الصيادي [١٢٦٦ - ١٣٢٨ هـ / ١٨٤٩ - ١٩٠٩ م]

<span data-type="title" id=toc-1251>محمد بن حسن وادي بن علي بن خزام الصيادي الرفاعي الحسينى، أبو الهدى:</span>
صوفي، شاعر، مشارك في العلوم الإسلامية والأدبية، عارف بفن الموسيقى
_________
(١) أعلام الشيعة ٢: ٣٤٩.
(٢) أعلام الشيعة ٢: ٣١٥.','2','517',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (915,'وضروب الإيقاع، كان أشهر علماء الدين في عصره. ولد في خان شيخون من أعمال معرة النعمان، وتعلم بحلب وبغداد. وعاد إلى سورية، فولي نقابة الأشراف لعموم ولاية حلب، ثم نقابة أشراف دار الخلافة: النظر على نقباء أشراف عموم ولايات سورية وديار بكر وبغداد والبصرة. ثم سكن الآستانة، واتصل بالسلطان عبد الحميد، فأكرمه وأغدق عليه وقلده مشيخة المشايخ. ثم وظف (سنة ١٣٠٠ هـ) للنظارة على أفراد الأمراء من العائلة المالكة. إمتاز بالذكاء والدهاء الخارق، وحظي عند السلطان فكان من كبار ثقاته، ولعب في عهده دورا خطيرا، كما كان له شأن يذكر في سياسة الدولة العثمانية. واستمر في خدمته زهاء ثلاثين سنة. ولما خدع السلطان على يد جمعية الاتحاد والترقي، نفي أبو الهدى إلى جزيرة الأمراء في "رينكيبو" فمات فيها. من تصانيفه الكثيرة المنسوبة إليه "نفحة الرحمن في تفسير القرآن". (١)
_________
(١) القول الفصل، لحسن حسني الطويراني. ومعجم الشيوخ ٢: ١٤٤ وأدباء حلب ١٠٥ ومعجم المطبوعات ٣٥٣ والأعلام الشرقية ٣: ١١٧ والحركة الأدبية في حلب ١١٣ وفهرس الفهارس ١: ١١٤ وأعلام الأدب والفن ١: ٣١٠ والمذكرات ١: ٢٤٢ ومصادر الدراسة الأدبية ٣: ٨٨.','2','518',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (916,'الزنجاني [١٢٥٦ - ١٣٤٠ هـ / ١٨٤٠ - ١٩٢٢ م]

<span data-type="title" id=toc-1252>محمد حسن بن قنبر علي بن محمد حسن الزنجاني:</span>
فقيه إمامي، من العلماء، من أهل زنجان. من آثاره "تبيان البيان في قواعد القرآن" (١).

الحجوي [١٢٩١ - ١٣٧٦ هـ / ١٨٧٤ - ١٩٥٦ م]

<span data-type="title" id=toc-1253>محمد بن الحسن بن العربي بن محمد الحجوي الثعالبي الفلالي:</span>
فقيه مالكي، باحث، له اشتغال بالسياسة، من أهل فاس، سكن مكناسة ووجدة والرباط. تولى عدة وظائف في عهد الحماية الفرنسية، منها: رئاسة المجلس العلمي، ورئاسة الاستئناف الشرعي الأعلى، ووزارتي المعارف والعدل، كما أسندت إليه سفارة المغرب في الجزائر (١٣٢١ - ١٣٢٣ هـ)، ونقم عليه مواطنوه وابتعدوا عنه، ثم مات بالرباط ودفن بفاس، فهجر أهلها المسجد المجاور لتربته. وفي عهد الاستقلال نقلته الحكومة المغربية إلى مكان مجهول بفاس. من آثاره
_________
(١) علماء معاصرون ١٢٨ ومصفى المقال ١٣٦ وأعلام الشيعة ٤٢٦.','2','518',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (917,'"تفسير الآيات العشر الأولى من سورة قد أفلح المؤمنون" (١).

مخلوف [١٢٧٧ - ١٣٥٥ هـ / ١٨٦٠ - ١٩٣٦ م]

<span data-type="title" id=toc-1254>محمد حسنين بن محمد مخلوف العدوي، شمس الدين:</span>
فقيه مالكي، مفسر، أديب، ولد في قرية "بني عدي" التابعة لمركز منفلوط من أعمال مصر، وتخرج بالأزهر سنة ١٣٠٥ هـ، ودس فيه، ثم كان من أعضاء مجلس إدارته، فأنشأ مكتبته ونظمها. وعين شفي للجامع الأحمدي، فمديرا عاما للمعاهد الدينية ووكيلا للأزهر. وفي سنة ١٣٣٤ هـ انقطع للتدريس، وتوفي بالقاهرة. له "المدخل المنير في مقدمة علم التفسير" (٢)

الشريف الرضي [٣٥٩ - ٤٠٦ هـ / ٩٧٠ - ١٠١٥ م]

<span data-type="title" id=toc-1255>محمد بن الحسين بن موسى بن محمد العلوي الحسيني الموسوي، أبو الحسن، (الشريف الرضي)</span>
_________
(١) جريدة العلم المغربية ٣١/ ١٠/ ١٩٥٧ والفكر السامي ٤: ١٩٩ والعز والصولة ٢: ٥٣ والأعلام ٦: ٩١.
(٢) الأعلام الشرقية ٢: ١٦٠ ومعجم المطبوعات ١٦٤٨ وجامع التصانيف الحديثة ٢: ٣٦ والأعلام ٦: ٦٢٣ ومعجم المؤلفين ٩: ٢٣١.','2','519',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (918,'الشريف الرضي: أديب، شاعر، إمامي معتزلي، كان أشعر الطالبين على كثرة المجيدين فيهم. نظم في المدح والافتخار وشكوى الزمان والرثاء والغزل والإخوانيات. مولده ووفاته في بغداد. ولاه الخليفة الطائع نقابة الطالبين في حياة والده، وخلع عليه بالسواد. واستعفى سنة ٤٥٠ هـ فأعفي، ثم أعيد سنة ٤٠٣ هـ. من آثاره "حقائق التأويل في متشابه التنزيل" و "تلخيص البيان في مجازات القرآن" (١)

السلمي [٣٢٥ - ٤١٢ هـ / ٩٣٦ - ١٠٢١ م]

<span data-type="title" id=toc-1256>محمد بن الحسن بن محمد بن موسى الأزدي السلمي النيسابوري، أبو عبد الرحمن:</span>
شيخ الصوفية وصاحب تاريخهم وطبقاتهم وتفسيرهم. كتب الحديث بنيسابور ومرو والعراق
_________
(١) تاريخ بغداد ٢: ٢٤٦ وتذكرة الحفاظ ١٠٦٥ وعبقرية الشريف الرضي لزكي مبارك - ووفيات الأعيان ٤: ٤١٤ وأعيان الشيعة ٤٤: ١٧٣ وشذرات الذهب ٣: ١٨٢ والوافي بالوفيات ٢: ٣٧٤ ومرآة الجنان ٣: ١٨ وروضات الجنات ٥٧٣ والمحمدون من الشعراء ٢٤٣ وإنباه الرواة ٣: ١١٤ والمنتظم ٧: ٢٧٩ والموسوعة العربية الميسرة ١٠٨٣ والنجاشي ٢٨٣.','2','519',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (919,'والحجاز، وحدث أكثر من أربعين سنة، وبلغت تصانيفه مئة أو أكثر، منها "حقائق التفسير" مخطوط مختصر، على طريقة أهل التصوف، قال في كشف الظنون: "ذكر فيه أن أكثر أهل الظاهر جمع في أنواع فوائد القرآن، ولم يشتغل أحد بفهم خطابه على لسان الحقيقة ولا بجمعه إلا آيا متفرقة نسبت إلى العباس بن عطاء ذكر أنها عن جعفر ابن محمد الصادق وكان قد سمع منهم في ذلك حروفا فضمها إلى مقالتهم ورتبها على السور الفرقانية فكانت كالتفسير، قرأه الثعلبي على مصنفه، لكن المفسرون من أهل الظاهر تكلموا فيه على ما هو دأبهم في أمثاله فقال الواحدي: "زعم أنه صنف حقائق التفسير فإن كان اعتقد أن ذلك تفسير فقد كفر، وطعن فيه ابن الجوزي أيضا". وقال الذهبي: "وكان وافر الجلالة، له كتاب سماه "حقائق التفسير" ليه لم يصنفه، فإنه تحريف وقرمطة .. " وقال السبكي مخاطبا شيخه الذهبي: "لا ينبغي أن تصف بالجلالة من تدعي فيه التحريف والقرمطة، وكتاب "حقائق التفسير" قد كثر الكلام فيه من قبل أنه اقتصر فيه على ذكر تأويلات، ومحامل','2','520',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (920,'للصوفية، ينبو عنها ظاهر اللفظ". منه مخطوطات في تركيا وغيرها. (١)

أبو يعإلى [٣٨٠ - ٤٥٨ هـ / ٩٩٠ - ١٠٦٦ م]

<span data-type="title" id=toc-1257>محمد بن الحسين بن محمد بن خلف ابن الفراء، أبو يعلى:</span>
شيخ الحنابلة في عصره، كان عالما في الأصول والفروع وأنواع الفنون. ارتفعت مكانته عند القادر والقائم العباسيين، وولاه الخليفة القائم قضاء دار الخلافة والحريم، وحران وحلوان، وكان قد امتنع، واشترط أن لا يحضر أيام المواكب، ولا يخرج في الاستقبالات ولا يقصد دار السلطان، فقبل القائم شرطه. قال
_________
(١) طبقات المفسرين للسيوطي ٣١ وطبقات المفسرين للداودي ٢: ١٣٧ وتاريخ التراث العربي ٢: ٤٩٧ وتذكرة الحفاظ ٣: ١٠٤٦ والوافي بالوفيات ٢: ٣٨٠ وميزان الاعتدال ٣: ٥٢٣ ولسان الميزان ٥: ١٤٠ والعبر ٣: ١٠٩ وشذرات الذهب ٣: ١٩٦ ومرآة الجنان ٣: ٢٦ وطبقات الشافعية للسبكي ٤: ١٤٣ وفيه: وفاته سنة ٣٣٠ وقيل ٣٢٥ ومثله في أكثر مصادر ترجمته. والمنتظم ٨: ٦ وهدية العارفين ٢: ٦١ وكشف الظنون ٦٧٣ وتاريخ بغداد ٢: ٢٤٨ والنجوم الزاهرة ٤: ٢٥٦ والرسالة المتطرفة ٤١ وطبقات الصوفية، مقدمته، لنور الدين شريبه ١٦ - ٤٩.','2','520',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (921,'الذهبي: "لم يكن له خبرة بعلل الحديث ولا برجاله، واحتج بأحاديث كثيرة واهية في الأصول والفروع. وأما في الفقه ومذاهب الناس ونصوص أحمد واختلافها فإمام لا يجارى". مات ببغداد، وعطلت الأسواق لجنازته. من كتبه "أحكام القرآن" (١).

الزاغولي [٤٧٢ - ٥٥٩ هـ / ١٠٨٠ - ١١٦٤ م]

<span data-type="title" id=toc-1258>محمد بن الحسن بن محمد بن الحسن بن علي بن يعقوب المروزي البنجديهي الزاغولي، أبو عبد الله:</span>
حافظ للحديث، مفسر، لغوي، من فقهاء الشافعية. ولد بزاغول من قرى "بنج ديه" بمرو الروذ، وأقام واشتهر بمرو. ورحل إلى هراة، وكتب الكثير. حدث عنه أبو سعد السمعاني وولده أبو المظفر. له "قيد الأوابد" في أكثر من
_________
(١) تاريخ بغداد ٢: ٢٥٦ والوافي ٣: ٧ وطبقات الحنابلة ٢: ١٩٣ وشذرات الذهب ٣: ٣٠٦ والبداية والنهاية ١٢: ٩٤ ومجلة الثقافة المصرية ٧: ١٤٩ والمختصر في تاريخ البشر ٢: ١٩٥ ومعجم المؤلفين ٩: ٢٥٥ ومناقب الإمام أحمد لابن الجوزي. ومختصر طبقات الحنابلة ٣٧٧ والأعلام ٦: ٩٩.','2','521',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (922,'أربعمائة مجلدة، في التفسير والحديث والفقه واللغة (١).

المعيني [... - نحو ٥٨٤ هـ / ... - نحو ١١٨٨ م]

<span data-type="title" id=toc-1259>محمد بن الحسين المعيني، المعيلفي، أبو الفضائل:</span>
مفسر، نسبته إلى حصن معين باليمن. له "لوامع البرهان وقواطع البيان في معاني (تفسير) القرآن" قال في كشف الظنون: "مختصر، أوله، الحمد لله الذي جعل الحمد مفتتح قرآنه ... ". وقال البغدادي: "في مجلد، موجود في دار الكتب جزيه جي زاده ببروسة" (٢).

العامري [٦٠٣ - ٦٨٠ هـ / ١٢٠٦ - ١٢٦١ م]

<span data-type="title" id=toc-1260>محمد بن الحسين بن رزين بن موسى بن عيسى، أبو عبد الله، تقي الدين العامري:</span>
قاض، محدث،
_________
(١) طبقات السبكي ٦: ٩٩ وطبقات المفسرين للداودي ٢: ١٣٦ وتذكرة الحفاظ ٤: ١٣٣٧ والشذرات ٤: ١٨٧ والوافي ٢: ٣٧٣ وكشف الظنون ١٣٦٧.
(٢) هدية العارفين ٢: ١٠٢ وكشف الظنون ١٥٦٩.','2','521',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (923,'مفسر، من فقهاء الشافعية. ولد بحماة (بسورية) وتعلم بها وبحلب. وقدم دمشق فولي وكالة بيت المال في أيام صلاح الدين الأيوبي، وتدريس الشامية البرانية وغيرها. ورحل إلى مصر سنة ٦٥٨ هـ فولي تدريس المدرسة الظاهرية سنة ٦٦٢ هـ وفوض إليه قضاء القاهرة والوجه البحري سنة ٦٦٥ هـ، ثم أضيف إليه قضاء مصر والوجه القبلي (سنة ٦٧٦) فكمل له قضاء القضاة بالديار المصرية كالها. وعزل، ثم أعيد، فاستمر إلى أن توفي. من آثاره "تفسير القرآن" (١).

بهاء الدين العاملي [٩٥٣ - ١٠٣١ هـ / ١٥٤٧ - ١٦٢٢ م]

<span data-type="title" id=toc-1261>محمد بن الحسين بن عبد الصمد الحارثي العاملي الهمذاني، بهاء الدين:</span>
أديب، من علماء الإمامية، شاعر، ولد ببعلبك، وانتقل مع والده وهو صغير إلى بلاد العجم، فولاه شاه
_________
(١) طبقات المفسرين للداودي ٢: ١٣٣ والنجوم الزاهرة ٧: ٣٥٣ والسبكي ٨: ٤٦ وابن كثير ١٣: ٢٩٨ والشذرات ٥: ٣٦٨ والعبر ٥: ٣٣١ وتذكرة الحفاظ ١٤٦٥ وذيل مرآة الزمان ٤: ١٢٤ وهدية العارفين ٢: ١٣٣ وحسن المحاضرة ١: ٤١٧ و ٢: ١٦٧ وكشف الظنون ٤٣٨.','2','522',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (924,'عباس رياسة العلماء. ثم رحل إلى مصر، وزار بيت المقدس ودمشق وحلب، وعاد إلى أصبهان، فتوفي فيها، ونقل، فدفن في طوس. من تصانيفه الكثيرة "العروة الوثقى" في التفسير. (١)

ابن إمام اليمن [... - ١٠٦٧ هـ / ... - ١٦٥٧ م]

<span data-type="title" id=toc-1262>محمد بن الحسين بن الإمام القاسم ابن محمد بن علي الحسني:</span>
أمير يماني، عارف بالتفسير، من أعيان الدولة المتوكلية. توفي بصنعاء. من آثاره "منتهى المرام، شرح آيات الأحكام" وهي مئتان ونيف وعشرون آية طبع (٢).

الأنقروي [... - ١٠٩٨ هـ / ... - ١٦٨٧ م]

<span data-type="title" id=toc-1263>محمد بن حسين الأنقروي الرومي:</span>
فقيه حنفي، مفسر، من علماء الترك. ولد بأنقرة، وتعلم بالقسطنطينية،
_________
(١) أعيان الشيعة ٤٤: ٢١٦ وخلاصة الأثر ٣: ٤٤٠ وأعلام الشيعة ٢: ٣٦٨ وهدية العارفين ٢: ٢٧٣ ونزهة الجليس ١: ٢٤٩.
(٢) ملحق البدر الطالع ١٩٦ وخلاصة الأثر ٣: ٤٥٥.','2','522',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (925,'وولي قضاء يني شهر، ومصر، والقسطنطينية، والروم ايلي. ثم عين شفي للإسلام مدة قصيرة، وعاجلته الوفاة، عن نحو سبعين عاما. قال المحبي: "لم يكن أفقه منه في العصر الأخير ولا أحكم من رأيه في التقرير والتحرير، وعرفه ب "شيخ الإسلام وعالم الروم وفقيهها وصدر الدولة ووجيهها". وربما قيل له الأنكوري نسبة إلى أنكورية وهي أنقرة. من آثاره "تفسير آية الكرسي" مخطوط (١).

الخاتون آبادي [... - ١١٥١ هـ / ... - ١٧٣٩ م]

<span data-type="title" id=toc-1264>محمد حسين بن محمد صالح بن عبد الواسع الحسيني الخاتون آبادي الأصفهاني:</span>
فقيه إمامي، محدث، مفسر، من علماء دولة الشاه حسين الصفوي بأصفهان، ولما فتحت على يد الأفغان رحل إلى خاتون آباد، وتوفي بها. قال في "أعيان الشيعة": "كان
_________
(١) هدية العارفين ٢: ٣٠٠ وخلاصة الأثر ٤: ٣١٤ والكشاف ٧١ والكتخانة ٣: ٨٧ ومعجم المؤلفين ٩: ٢٣٤ والأعلام ٦: ١٠٣.','2','523',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (926,'واحد زمانه في الفقه والحديث والتفسير وحسن الخط، وألف مؤلفات بديعة" (١)

نيل فروش [... - ١١٦٠ هـ / ... - ١٧٤٧ م]

<span data-type="title" id=toc-1265>محمد حسين الأصفهاني، المعروف بنيل فروش، أي بائع النيل:</span>
فقيه إمامي، مفسر، من تلامذة على أصغر المهندس. من آثاره "تفسير القرآن" (٢).

الشيرازي [... - حيا ١٢٨١ هـ / ... - حيا ١٨٦٤ م]

<span data-type="title" id=toc-1266>محمد حسين بن على مراد الشيرازي:</span>
حكيم، مفسر، من فقهاء الإمامية. من آثاره "شرح وتفسير سورة الجمعة" فرغ منه سنة ١٢٨١ هـ (٣).

الشيرازي [... - ١٣٤٠ هـ / ... - ١٩٢٢ م]

<span data-type="title" id=toc-1267>محمد حسين بن خليل (الميرزا) الشيرازي:</span>
فقيه إمامي، أصولي،
_________
(١) أعيان الشيعة ٤٤: ٢٧٠ وفوائد الرضوية ٤٩٤ والذريعة ٢: ٣٠١.
(٢) أعيان الشيعة ٤٤: ٢٧٤.
(٣) أعلام الشيعة ٢: ٤١٠.','2','523',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (927,'متكلم، مفسر. مات في سامراء. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (١).

الظواهري [... - ١٣٦٥ هـ / ... - ١٩٤٦ م]

<span data-type="title" id=toc-1268>محمد الحسيني بن إبراهيم الظواهري:</span>
مفسر، أديب، ولد بكفر الظواهري (بشرقية مصر) وتعلم بالأزهر، ثم بالجامع الأحمدي بطنطا. وتولى التدريس في كلية أصول الدين، وتوفي بالقاهرة. من كتبه "التحقيقات الواضحة في تفسير سورة الفاتحة وأوائل سورة البقرة وآية الكرسي" طبع، و "القول السديد في تفسير آيات النسخ والطلاق والربا من القرآن المجيد" طبع (٢).

الفناري [٧٥١ - ٨٣٤ هـ / ١٣٥٠ - ١٤٣١ م]

<span data-type="title" id=toc-1269>محمد بن حمزة بن محمد، شمس الدين الفناري الرومي:</span>
قاض، مفسر، عالم
_________
(١) أعيان الشيعة ٤٤: ٢٧٣.
(٢) الأعلام الشرقية ٢: ١٤٤ والأعلام ٦: ٣٣٩ والمكتبة الأزهرية.','2','524',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (928,'بالمنطق والأصول، من فقهاء الحنفية. تعلم في بلده، ثم رحل إلى مصر (سنة ٧٧٨ هـ)، فأخذ عن بعض علمائها. وعاد، فولي قضاء بروسة. ثم تحول إلى قونية وأقام بها إلى أن ألحقت بالبلاد العثمانية (سنة ٧٩٣ هـ) فعاد إلى بروسة وولي قضاء المملكة العثمانية، وارتفع قدره عند السلطان "بايزيد خان" واشتهر ذكره وشاع فضله. وحج سنة ٨٢٢ هـ، وعاد إلى بيت المقدس، فاستدعاه الملك المؤيد شيخ المحمودي ليستفهمه عن أحوال البلاد الرومية، فدخل القاهرة في الرابع من صفر سنة ٨٢٣ هـ، فأكرمه السلطان وأمر رجال الدولة بإكرامه. قال ابن حجر "وكتم ما يبوح به في بلاده من محبة ابن العربي، وشغل الناس في "الفصوص" وغيرها، فأقام بالقاهرة مجموع الخاطر قليل الفضول إلى أن سافر سالما في الثاني والعشرين من ربيع الأول (سنة ٨٢٣ هـ) ". ثم حج سنة ٨٣٣ هـ شكرا لله على إعادة بصره إليه، وكان قد أشرف على العمى، أو عمي، وشفي. ومات بعد عودته من الحج. من كتبه "عين الأعيان في تفسير القرآن" وهو "تفسير الفاتحة" طبع في الآستانة سنة ١٣٢٦ هـ (١).
_________
(١) بغية الوعاة ١: ٩٧ وفيه: الفنري (بفتحتين) =','2','524',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (929,'كوزل حصاري [... - ١٠١٠ هـ / ... - ١٦٠١ م]

<span data-type="title" id=toc-1270>محمد بن حمزة الكوزل حصاري الآيديني، الرومي:</span>
فقيه حنفي، مفسر، ذكره البغدادي في "هدية العارفين" (٢: ٢٦٥) وقال: من تآليفه "أزهار التنزيل في التفسير" رسالة في أحكام الجمعة، ورسالة في أحكام الشهيد، ورسالة في الزكاة. ثم أورد (صفحة ٣٤٦) ترجمة لشخص اسمه محمد بن حمزة الآيديني وقال: "المعروف بحاجي أمير زاده الحنفي المفتي المتوفى سنة ١٢٠٤ هـ " وذكر الرسائل السابقة من بين مؤلفاته. وفي كتابه إيضاح المكنون (٢: ٢١٦) ذكر كتابا اسمه "فيما شاع بين الناس واشتهر" وقال هو لمحمد بن حمزة
_________
= نسبة إلى صنعة الفنيار، نقلا عن الكافيجي. ومفتاح السعادة ١: ٤٥٢ وفيه أن قول السيوطي (في بغية الوعاة) "ليس بصحيح، وإنما نسبته إلى قرية اسمها فنار". وهدية العارفين ٢: ١٨٨ والشذرات ٧: ٢٠٩ وإنباء الغمر ٣: ٢١٦ و ٢١٧ و ٤٦٤ والضوء اللامع ١١: ٢١٨ والسلوك ٤ ق ١: ٥٢٣ و ٥٢٤ و ٥٢٥ وعقد الجمان ٢٥ ق ٣ ورقة ٤٩٤ والشقائق النعمانية ١: ٨٤ ومعجم المطبوعات ١٤٦٠ والبدر الطالع ٢: ٢٦٦ والفوائد البهية ١٦٦ وكشف الظنون ٤٥٥ و ١١٨١ والمنهل الصافي (مخطوط) ٤: ٦٧٢ والأعلام ٦: ٣٤٢ ومعجم المؤلفين ٩: ٢٧٢.','2','525',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (930,'الآيديني الكورا الحصاري فرغ منه سنة ١١١٦ هـ " (١).

العينتابي [... - ١١١١ هـ / ... - ١٦٩٩ م]

<span data-type="title" id=toc-1271>محمد بن حمزة العينتابي، ثم السيواسي، الدباغ، المشهور بتفسيري أفندي:</span>
نزيل طرابلس الشام، فقيه حنفي، متكلم، له اشتغال بالتفسير. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (٢)

ابن حمويه [٤٤٩ - ٥٣٠ هـ / ١٠٥٧ - ١١٣٥ م]

<span data-type="title" id=toc-1272>محمد بن حمويه بن محمد بن حمويه الجويني، أبو عبد الله:</span>
شيخ الصوفية بخراسان، قرأ الفقه والأصلين على إمام الحرمين، ثم انقطع إلى الزهد والعبادة، وحج مرات. قال الصفدي: "وكان سنجر شاه والملوك يزورونه، ولا يغشى أبوابهم ولا يقبل صلاتهم ولا يأكل من
_________
(١) انظر عدا المراجع المذكورة في المتن، الكشاف ٢٤٧ والخديوية ٧/ ٤٠١.
(٢) هدية العارفين ٢: ٣٠٧ وسلك الدرر ٤: ٣٨ وإيضاح المكنون ١: ١٤١ وفيه: نزيل دمشق بدلا من طرابلس الشام، وكشف الظنون ١١٤٨.','2','525',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (931,'الأوقاف، له قطعة أرض يزرعها خادم له، وبنى "خانقاه" إلى جانب داره وأوقف عليها أوقافا" له "لطائف الأذهان في تفسير القرآن" (١).

الجنابذي [... - ١٣١١ هـ / ... - ١٨٩٤ م]

<span data-type="title" id=toc-1273>محمد (سلطان) بن حيدر بن نخند ابن سلطان محمد بن دوست الجنابذي الخراساني:</span>
مفسر، من علماء الشيعة الإمامية. نسبته إلى جنابذ (كونابذ) قرية بنواحي نيسابور من آثاره "بيان السعادة في مقامات "العبادة" تفسير القرآن على مذهب الشيعة الإمامية، في مجلدين، ألفه سنة ١٣١١ هـ وطبع في طهران سنة ١٣١٤ هـ (٢).

البرقي [... - نحو ٢٣٠ هـ / ... - نحو ٨٤٥ م]

<span data-type="title" id=toc-1274>محمد بن خالد بن عبد الرحمن بن محمد بن علي البرقي: أبو جعفر:</span>
عالم إمامي، من كبارهم، من أهل برقة من قرى قم ببلاد فارس، وأصله من
_________
(١) الوافي بالوفيات ٣: ٢٨ وشذرات الذهب ٤:
(٢) معجم المطبوعات ٧١٤ والتيمورية ١: ١٣ و ٦٧، و ٣: ٦٣.','2','526',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (932,'الكوفة. كان في زمن المعتصم بالله العباسي (٢١٨ - ٢٢٧ هـ) له تصانيف كثيرة ذكر بعضها ابن النديم، منها "فضائل القرآن". وقد وهم صاحب طبقات المفسرين ونسب هذا الكتاب لابن صاحب الترجمة أحمد بن محمد (١).

ابن تيمية [٥٤٢ - ٦٢٢ هـ / ١١٤٨ - ١٢٢٥ م]

<span data-type="title" id=toc-1275>محمد بن الخضر بن محمد بن الخضر ابن على بن عبد الله بن تيمية الحراني، فخر الدين، أبو عبد الله:</span>
مفسر، واعظ، من فقهاء الحنابلة، كان شيخ حران وعالمها وخطيبها. مولده ووفاته فيها. رحل إلى بغداد وأخذ عن علمائها، قال الذهبي: "كان إماما في الفقه، إمامأ في التفسير، إماما في اللغة". من كتبه "التفسير الكبير" عدة مجلدات (٢).

ابن المززبان المحولي [... - ٣٠٩ هـ / ... - ٩٢١ م]

<span data-type="title" id=toc-1276>محمد بن خلف بن المرزبان بن بسام، أبو بكر الآجري المحولي:</span>
_________
(١) ابن النديم ٢٧٦ وطبقات المفسرين ١: ٧٢ وانظر معجم المؤلفين ٢: ٩٧.
(٢) طبقات المفسرين للداودي ٢: ١٣٩ وطبقات المفسرين للسيوطي ٣٢ وابن خلكان ٤: ٣٨٦ والوافي ٣: ٣٧ والبداية والنهاية ١٣: ١٠٩ والعبر ٥: ٩٢ وذيل الروضتين ١٤٦ والنجوم الزاهرة ٦: ٢٦٣ والشذرات ٥: ١٠٢ والذيل على طبقات الحنابلة ٢: ١٥١.','2','526',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (933,'مؤرخ، عالم بالأدب، مشارك في بعض العلوم. سكن قرية المحول غربي بغداد فنسب إليها. وكان أحد التراجمة، ينقل الكتب الفارسية إلى العربية. قال الخطيب البغدادي: "كان إخباريا مصنفا حسن التأليف" من آثاره "الحاوي في علوم القرآن" في ٢٧ جزءا (١).

الإلبيرى [٤٥٧ - ٥٣٧ هـ / ١٠٦٥ - ١١٤٢ م]

<span data-type="title" id=toc-1277>محمد بن خلف بن موسى، أبو عبد الله الأنصاري الإلبيري:</span>
متكلم، عارف بالحديث والتفسير والفقه والأدب والطب. أصله من إلبيرة وإليها نسبته سكن قرطبة. ذكره الداودي في "طبقات المفسرين" وقال: له "الإفصاح والبيان في الكلام على القرآن" (٢).
_________
(١) طبقات المفسرين للداودي ٢: ١٤١. وتاريخ بغداد ٥: ٢٣٧ ولسان الميزان ٥: ١٥٧ والوافي ٣: ٤٤ والنجوم الزاهرة ٣: ٢٠٣ والمحمدون من الشعراء ٣٠١ والمنتظم ٦: ١٦٥ والعبر ٢: ١٤٤ واللباب ٣: ١٧٧ وتذكرة الحفاظ ٢: ٧٥٧ والشذرات ٢: ٢٥٨ وهدية العارفين ٢: ٣٦ والأعلام ٦: ٣٤٨ ومعجم المؤلفين ٩: ٢٨٥ وبروكلمان: الذيل ١: ١٨٩.
(٢) طبقات المفسرين للداودي ٢: ١٤٢ والوافي ٣: ٤٦ والديباج ٣١٣ وبروكلمان: الذيل ١: ٧٦٢ ومعجم المؤلفين ٩: ٢٨٦ وابن الأبار ١: ٤٣٩.','2','527',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (934,'ابن خلفة الأبي [... - ٨٢٧ هـ / ... - ١٤٢٤ م]

<span data-type="title" id=toc-1278>محمد بن خلفة بن عمر الأبي الوشتاتي:</span>
فقيه مالكي، مفسر، عالم بالحديث، قاض، من أهل تونس، نسبته إلى أبة من قراها. ولي قضاء الجزيرة سنة ٨٠٨ هـ. وتوفي بتونس. من كتبه "تفسير القرآن" في ثماني مجلدات (١).

ابن غلبون [... - نحو ١١٥٠ هـ / ... - نحو ١٧٣٧ م]

<span data-type="title" id=toc-1279>محمد بن خليل غلبون الطرابلسي المصراتي، أبو عبد الله:</span>
مؤرخ، عارف بالتفسير والحديث والفقه من أهل مصراتة بليبيا. درس بالأزهر، وعاد إلى بلده، فدرس التفسير والفقه والحديث. وكان عنيفا على أهل البدع، له معهم مناظرات (٢).
_________
(١) البدر الطالع ٢: ١٦٩ وذيل الابتهاج ٢٨٧ وشجرة النور ٢٤٤ ومعجم المطبوعات ٣٦٣ ومعجم المؤلفين ٩: ٢٨٧ والأعلام ٦: ٣٤٩
(٢) أعلام ليبيا ٢٧٣ وأعلام من طرابلس الغرب ١٢٣.','2','527',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (935,'مكي زاده [١١١٦ - ١٢١٢ هـ / ١٧٠٤ - ١٧٩٧ م]

<span data-type="title" id=toc-1280>محمد بن خليل الرومي، المعروف بمكي زاده:</span>
مفسر فرضي، ناظم. ولد بمكة، وكان شيخ الإسلام فيها. له تصانيف بالعربية والتركية، منها "حاشية" على أنوار التنزيل، في التفسير للبيضاوي (١).

محمد خليل [... - نحو ١٢٢٠ هـ / ... - نحو ١٨٠٥ م]

<span data-type="title" id=toc-1281>محمد خليل بن داود بن محمد:</span>
فقيه إمامي، فلكي، تولى المشهد الرضوي بخراسان، وتوفي بالهند. من تصانيفه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (٢).

القاوقجي [١٢٢٤ - ١٣٠٥ هـ / ١٨٠٩ - ١٨٨٨ م]

<span data-type="title" id=toc-1282>محمد بن خليل بن إبراهيم، أبو المحاسن القاوقجي:</span>
فقيه حنفي، محدث، كان مسند أهل الشام في عصره. ولد في طرابلس الشام،
_________
(١) إيضاح المكنون ١٤٢ وهدية العارفين ٢: ٣٥١
(٢) معجم المؤلفين ٩: ٢٨٩ وهدية العارفين ٢: ٣٥١.','2','528',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (936,'وتلقى مبادىء العلوم فيها. ورحل إلى مصر سنة ١٢٣٩ هـ، فدرس في الأزهر وأقام ٢٧ سنة. وعاد إلى بلده، ومات حاجا بمكة. قال الكتاني: "ألف نحو مئة تصنيف ما بين مطول ومختصر، وعلى أسانيده اليوم المدار في غالب بلاد مصر والشام والحجاز". من كتبه "ربيع الجنان في تفسير القرآن" (١).

ابن أبي الخير [... - بعد ٧٠٠ هـ / ... - بعد ١٣٠١ م]

<span data-type="title" id=toc-1283>محمد بن أبي الخير، قطب الدين:</span>
فقيه، مفسر، نحوي، من كتبه "التقريب" في تفسير القرآن (٢).

ابن دلف [... - ٣٣٥ هـ / ... - ٩٤٦ م]

<span data-type="title" id=toc-1284>محمد بن دلف:</span>
محدث، مفسر، من فقهاء المالكية. من أهل وشقة بالأندلس. ترجم له القاضي عياض وقال: "هو مولى لابن عمروس صاحب وشقة، كان من أهل العلم والفصاحة
_________
(١) فهرس الفهارس ١: ٦٩ والأعلام الشرقية ٣: ١٢ والرسالة المستطرفة ١١٥ ومعجم المطبوعات ١٤٩٠.
(٢) شد الإزار ٤٣٢.','2','528',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (937,'والحفظ لمعاني القرآن وتفاسيره، وكان من العباد المجتهدين، حج وانصرف، فجلس للناس يعلمهم ويفتيهم، ويحدثهم، وسماه صاحب تاريخ علماء الأندلس: ابن دليق. وهو في طبقات المفسرين: ابن دليف (١)

محمد رشيد رضا [١٢٨٢ - ١٣٥٤ هـ / ١٨٦٥ - ١٩٣٥ م]

<span data-type="title" id=toc-1285>محمد رشيد بن علي رضا بن محمد شمس الدين بن منده علي خليفة القلموني، البغدادي الأصل، الحسيني النسب:</span>
السيد الإمام حجة الإسلام في هذا العصر، عالم بالحديث والتفسير والأدب والتاريخ، ومن كبار الدعاة إلى التجديد والإصلاح في العالم الإسلامي، وصاحب مجلة "المنار". ولد ونشأ بالقلمون، من أعمال طرابلس الشام، وتعلم فيها وفي طرابلس وبيروت. ورحل إلى مصر سنة ١٣١٥ هـ (١٨٩٧ م) فلازم الإمام الشيخ محمد عبده وتتلمذ له. ثم أصدر مجلة "المنار" (١٥ مارس ١٨٩٨) لبث آرائه في الإصلاح الديني
_________
(١) ترتيب المدارك ٣: ٤٥٤ وتاريخ علماء الأندلس ٢: ٥٣ وطبقات المفسرين للداودي ٢: ١٤٢.','2','529',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (938,'والإجتماعي، فكانت في طليعة المجلات العربية والإسلامية. وزار دمشق في أعقاب إعلان الدستور العثماني (١٣٢٦ هـ ١٩٠٨ م) وبينما هو يخطب على منبر الجامع الأموي، اعترضه أحد أعداء الإصلاح، فكانت فتنة في دمشق، اضطر على أثرها، إلى العودة إلى مصر، وأنشأ مدرسة الدعوة والإرشاد. وفي أيام حكم الملك فيصل ابن الحسين، قصد سورية مرة ثانية، وانتخب رئيسا للمؤتمر السوري، وغادرها إثر دخول الفرنسيين إليها سنة ١٩٢٥، فأقام في مصر، ثم رحل إلى الهند والحجاز وأوربا. وعاد ليستقر في القاهرة. توفي إثر حادث سيارة كان راجعا بها من السويس إلى القاهرة. أشهر آثاره مجلة المنار (٣٤ مجلدا) و "تفسير القرآن" ١٢ مجلدا منه، ولم يكمله. وهو له وليس للإمام محمد عبده كما يشاع (١).
_________
(١) السيد رشيد رضا أو إخاء أربعين سنة، وحاضر العالم الإسلامي ١: ٢٨٤ والإسلام والتجديد في مصر ١٦٩ والمجددون في الإسلام ٥٣٩ وأعلام الأدب والفن ٢: ٣٥٧ ومجلة المجمع العلمي العربي ٥: ٣٢٧ و ١٣: ٣٤٨ و ٤٤٢ و ١٥: ٣٦٥ وذكرى حجة الإسلام صاحب المنار. ومعجم المطبوعات ٩٣٤ والمذكرات لفخري البارودي ١: ٧٠ ومذكرات عن الثورة العربية للغصين ٢١٤ ومجلة نور الإسلام ٣ =','2','529',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (939,'الأصفهاني [... - حيا ١١٢٧ هـ / ... - حيا ١٧١٥ م]

<span data-type="title" id=toc-1286>محمد رضا بن محمد مؤمن الإمامي الخاتون آبادي الأصفهاني:</span>
من علماء أصفهان والمدرسين بها. من كتبه "خزائن الأنوار" تفسير كبير (١).

رفيع الدين [... - ١٢٣٨ هـ / ... - ١٨٢٢ م]
محمد رفيع الدين. مفسر. له "تفسير آية النور" (٢).

الواسطي [... - ٣٠٧ هـ / ... - ٩١٩ م]

<span data-type="title" id=toc-1287>محمد بن زيد بن علي بن الحسن الواسطي، أبو عبد الله:</span>
معتزلي، من كبار علماء الكلام. أصله من واسط، سكن بغداد وتوفي بها. قال الصفدي: "كان من كبار المعتزلة، أخذ عن أبي علي الجبائي، وكان في زمانه عالي
_________
= ٣٣٠ والموسوعة العربية الميسرة ١٦٦٠ وتاريخ الصحافة العربية ٤: ١٧٤ والأعلام ٦: ٣٦١ ومعجم المؤلفين ٩: ٣١٠ وفيه أسماء مصادر أخرى كثيرة.
(١) أعيان الشيعة ٤٤: ٣٤٧
(٢) بروكلمان: الذيل ٢: ٦١٦ ومعجم المؤلفين ٩: ٣٢٠.','2','530',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (940,'الصيت، كثير الأصحاب، ومن أخف عالم الله روحا، وهو الذي هجا "نفطويه" بقوله: من سره أن لا يرى فاسقا ... فليجتنب من أن يرى نفطويه أحرقه الله بنصف اسمه ... وصير الباقي صراخا عليه من آثاره "إعجاز القرآن في نظمه وتأليفه" (١).

ابن السائب الكلبي [... - ١٤٦ هـ / ... - ٧٦٣ م]

<span data-type="title" id=toc-1288>محمد بن السائب بن بشر بن عمرو بن الحارث الكلبي، أبو النضر:</span>
إمام في التفسير والأنساب وأخبار العرب. مولده ووفاته بالكوفة. روى عن الشعبي وجماعة. استدعاه والي البصرة سليمان بن علي العباسي، ففسر القرآن بالبصرة. قال البخاري: "تركه القطان وابن مهدي". وقال النسائي: "حدث عنه ثقاث من الناس ورضوه في التفسير، وأما في الحديث ففيه مناكير". أخرج له أبو داود في
_________
(١) طبقات المفسرين للداودي ٢: ١٤٣ والوافي ٣: ٨٢ وابن النديم ١٧٢ ولسان الميزان ٥: ١٧٢ وكشف الظنون ١٢٠ وهدية العارفين ١: ٢٥.','2','530',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (941,'المراسيل، والترمذي وابن ماجه في التفسير. وشهد وقعة دير الجماجم مع ابن الأشعث. من كتبه "تفسير القرآن" مخطوط، في مكتبات استانبول. قال سزكين: "هذا التفسير لم يستخدمه الطبري فى تفسيره، وإنما استخدمه في تاريخه قليلا" (١).

الكريدي [١٢٦٠ - ١٣١٣ هـ / ١٨٤٤ - ١٨٩٥ م]

<span data-type="title" id=toc-1289>محمد سري باشا بن محمد صالح الكريدي الرومي الحنفي:</span>
أديب تركي، مستعرب، كاتب، من الولاة في عهد الدولة العثمانية. توفي بالقسطنطينية. من تصانيفه "سر الإستواء" في تفسير بعض الآيات، باللغة التركية، و "سر التنزيل" في التفسير، بالتركية أيضا. و "سر القرآن" و "سر الفرقان" (٢).
_________
(١) طبقات المفسرين للداودي ٢: ٩٤٤ وابن خلكان ٤: ٣٠٩ والعبر ١: ٢٠٦ وهدية العارفين ٢: ٧ وكشف الظنون ٤٥٧ وخلاصة تهذيب الكمال ٢٨٨ والوافي ٣: ٨٣ والشذرات ١: ٢١٧ وميزان الاعتدال ٣: ٥٥٦ وابن النديم ٩٥ وتهذيب التهذيب ٩: ١٧٨ وأعيان الشيعة ٤٥: ١٤٥ والذريعة ٤: ٣١١ والمعارف ٥٣٥ وتاريخ التراث العربي ١: ١٩٣ والأعلام ٣٠٧ ومعجم المؤلفين ١٠: ١٥
(٢) هدية العارفين ٢: ٣٩٥ وإيضاح المكنون ١: ٩ و ١٠ و ١١.','2','531',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (942,'العوفي [... - ٢٧٦ هـ / ... - ٨٨٩ م]

<span data-type="title" id=toc-1290>محمد بن سعد بن محمد بن الحسن ابن عطية العوفي:</span>
محدث، من أهل بغداد. قال الخطيب البغدادي: "كان لينا في الحديث، وروى الحاكم عن الدارقطني أنه لا بأس به". له "تفسير القرآن". قال في كشف الظنون: "عن ابن عباس، ذكره الثعلبي" وانظر تعليق أحمد محمد شاكر على الأثر ٣٠٥ في تفسير الطبري ١: ٢٦٣ (١)

الرعينى [٦٨٥ - ٧٧٨ هـ / ١٢٨٦ - ١٣٧٦ م]

<span data-type="title" id=toc-1291>محمد بن سعيد بن محمد بن عثمان الأندلسي، الفاسي، أبو عبد الله، الرعيني:</span>
فقيه مالكي، رحالة، عالم بالحديث، مشارك في بعض الفنون، أندلسي الأصل، مولده ووفاته بفاس. روى عن نحو ستين شفي من أهل المشرق والمغرب. من آثاره "تفسير سورة الكوثر" (٢).
_________
(١) لسان الميزان ٥: ١٧٤ وكشف الظنون ٤٥٤ وهدية العارفين ٢: ١٩ وتاريخ بغداد ٥: ٣٢٢ والوافي ٣: ٨٩ والنسخ في القرآن الكريم ١: ٣٢٠.
(٢) شجرة النور ٢٣٦ ووفاته فيه: ٧٧٩ وجذوة الاقتباس ١٤٧ وفهرس الفهارس ١: ٣٢٦ وفي الابتهاج ٢٧١ ودليل مؤرخ المغرب ٢: ٢٧٢.','2','531',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (943,'محمد سعيد زاده [... - ١١٩٤ هـ / ... - ١٧٨٠ م]

<span data-type="title" id=toc-1292>محمد سعيد بن (الوزير) حسن باشا الرومي الحنفي:</span>
قاض، أديب، مشارك في بعض الفنون، تركي الأصل، مستعرب. ولي القضاء بالقسطنطينية، وتوفي بها. من آثاره "تفسير سورة الزلزلة" (١).

العرفي [١٣١٤ - ١٣٧٥ هـ / ١٨٩٦ - ١٩٥٦ م]

<span data-type="title" id=toc-1293>محمد سعيد بن أحمد العرفي:</span>
سياسي، عالم، مشارك في الحديث والتفسير والفقه والتاريخ. ولد بدير الزور. عمل في خدمة الجيش العثماني، ثم ولي نيابة المحكمة الشرعية في بلده، فمحاميا للمعارف بالوكالة، واشتغل بالسياسة، ونفي في أيام الانتداب الفرنسي إلى أنطاكية ودمشق. وأقام بمصر مدة. وعاد، فانتخب عضوا في مجلس النواب السوري، ثم عين مفتيا لمحافظة الفرات، وانتخب عضوا بالمجمع
_________
(١) هدية العارفين ٢: ٣٤٣ ومعجم المؤلفين ١٠: ٢٨.','2','532',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (944,'العلمي العربي، فرئيسا للمجلس الإسلامي الأعلى. من كتبه "تفسير القرآن العظيم" مخطوط (١).

الجمحي [١٥٠ - ٢٣١ هـ / ٧٦٧ - ٨٤٦ م]

<span data-type="title" id=toc-1294>محمد بن سلام بن عبيد الله بن سالم الجمحي، بالولاء، أبو عبد الله:</span>
عالم بالأدب، إخباري، راوية، حافظ. من أهل البصرة. قدم بغداد سنة ٢٢٢ هـ واستقر بها إلى أن مات. وكان يقول بالقدر، فقال أهل الحديث: يكتب عنه الشعر، أما الحديث فلا". من كتبه "غريب القرآن" (٢).
_________
(١) معجم المؤلفين ١٠: ٢٦ ومجلة العرفان ٤٣: ٧٩٥ وأعلام الأدب والفن ٢: ٣١ ومن هو في سورية ١: ٢٨٧ و ٢: و ٤٩٨ ومجلة المجمع العلمي العربي ٣١: ٣٣٩ والأعلام ٦: ١٤٤.
(٢) طبقات المفسرين للداودي ٢: ١٥١ ومقدمة محقق طبقات فحول الشعراء، والوافي ٣: ١١٤ ولسان الميزان ٥: ١٨٢ وميزان الاعتدال ٣: ٥٦٧ وبغية الوعاة ١: ١١٥ ومراتب النحويين ٦٧ وهدية العارفين ٢: ١٢ وتاريخ بغداد ٥: ٣٢٨ والعبر ١: ٤٠٩ والشذرات ٢: ٧١ وإنباه الرواة ٣: ١٤٣ ونزهة الألباء ١٥٧ والنجوم الزاهرة ٢: ٢٦٠ ومعجم الأدباء ١٧: ٢٠٤. ومعجم المؤلفين ١٠: ٤١ ومجلة الكتاب ١٢: ٣٧٩ و ٥١٣ و ٥٢٢.','2','532',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (945,'القضاعي [... - ٤٥٤ هـ / ... - ١٠٦٢ م]

<span data-type="title" id=toc-1295>محمد بن سلامة بن جعفر بن علي بن حكمون بن إبراهيم، أبو عبد الله، القضاعي:</span>
فقيه شافعي، قاض، مؤرخ، محدث، روى عن شيوخ مكة والشام ومصر والواردين عليها. ولي الكتابة للوزير علي بن أحمد الجرجرائي بمصر، في أيام الفاطميين، (بعد سنة ٤١٨ هـ). وأرسل في سفارة إلى الروم، فأقام قليلا في القسطنطينية. وولي القضاء بمصر نيابة، وتوفي فيها. قال ابن ماكولا: "كان فقيها على مذهب الشافعي، متفننا في عدة علوم، وصنف وحدث، ولم أر بمصر من يجري مجراه" وقال ابن عساكر: "ثقة أمين، قدم إلى دمشق مجتازا لبلاد الروم، رسولا من صاحب مصر". من كتبه "تفسير القرآن" قيل: في نحو ٤٠ مجلدا، وقيل: عشرون مجلدا (١).
_________
(١) طبقات المفسرين للداودي ٢: ١٥٢ والوافي ٣: ١١٦ والسبكي ٤: ١٥٠ وحسن المحاضرة ١: ٤٠٣ والإشارة إلى من نال الوزارة ٣٦ ومرآة الجنان ٣: ٧٥ والشذرات ٣: ٢٩٣ والعبر ٣: ٢٣٣ وهدية العارفين ٢: ٧١ واللباب ٢: ٢٦٩ والرسالة المستطرفة ٥٧ ومعجم المطبوعات ١٥١٥ والأعلام ٧: ١٦ ومعجم المؤلفين ٩٠: ٤٢.','2','533',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (946,'الإسكندري [... - ١١٤٩ هـ / ... - ١٧٣٧ م]

<span data-type="title" id=toc-1296>محمد بن سلامة بن إبراهيم بن خليل بن محمد، الضرير الإسكندري:</span>
مفسر، شاعر، مشارك في بعض العلوم. من أهل الإسكندرية. تعلم بالقاهرة، وتوفي بمكة. من كتبه "تفسير القرآن" مخطوط، نظما، في عشر مجلدات، سماه "تحفة الفقير في بعض ما جاء في التفسير" (١).

الرشيدي [... - بعد ١٣٠٠ هـ / ... - بعد ١٨٨٣ م]

<span data-type="title" id=toc-1297>محمد بن سلامة بن عبد الخالق بن حسن الجمل، الرشيدي الشافعي:</span>
عارف بالقراءات والتفسير، من أهل رشيد بمصر. من آثاره "عمدة البيان في زبدة نواسخ القرآن" رسالة مخطوطة، كتبها سنة ١٢٧٦ هـ. وله رسالة في التجويد فرغ من تأليفها سنة ١٣٠٠ هـ (٢).
_________
(١) سلك الدرر ٤: ١٢٣ وهدية العارفين ٢: ٣٢٢ وإيضاح المكنون ١: ٣٠٣ ومعجم المؤلفين ١٠: ٤٢ وعلوم القرآن ١٧١ والأعلام ٦: ١٤٦.
(٢) معجم المؤلفين ١٠: ٤٤ والأعلام ٧: ١٧ والتيموربة ٣: ١١١.','2','533',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (947,'الأردبيلي [... - حيا ١٠٥٥ هـ / ... - حيا ١٦٤٥ م]

<span data-type="title" id=toc-1298>محمد (الميرزا) بن سلطان، الملقب بالمحقق الأردبيلي:</span>
مفسر، من آثاره "تيسير المرام لتفسير الكلام" "في ترجمة بعض الكلمات القرآنية المحتاجة إلى التصفح في الكتب بترتيب السور". كما يقول آغا بزرك (١).

العطار [١٢٣٧ - ١٣٠٧ هـ / ١٨٢٢ - ١٨٩٠ م]

<span data-type="title" id=toc-1299>محمد سليم بن ياسين بن حامد العطار:</span>
من أعيان دمشق وعلمائها، ومن مدرسي التفسير والحديث بها. قال الحصني: "درس البخاري في جامع السلطان سليمان، وكان يلقي درس التفسير في محراب الشافعي، والحديث في مشهد الحسن. وكان الناس يتسابقون ويتزاحمون لسماع درسه في التفسير" (٢).

القزويني [... - بعد ٢٩٥ هـ / ... - بعد ٩٠٨ م]

<span data-type="title" id=toc-1300>محمد بن سليمان بن داود بن عقبة ابن رؤبة القزويني، أبو جعفر:</span>
عارف
_________
(١) تعليقات آغا بزرك على كشف الظنون ٣٨
(٢) منتخبات التواريخ ٧٢٢ وأعيان دمشق ٣٤٦','2','534',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (948,'بالتفسير والحديث والقراءات، من أهل قزوين. قال الرافعي: "كبير في علوم القرآن، سمع منه كتابه "الوافر" في القراءات سنة ٢٩٥ هـ " (١).

الصعلوكي [٢٩٦ - ٣٦٩ هـ / ٩٠٨ - ٩٨٠ م]

<span data-type="title" id=toc-1301>محمد بن سليمان بن محمد بن سليمان بن هارون الحنفي - من بني حنيفة - العجلي، أبو سهل الصعلوكي:</span>
شيخ عصره، وإمام وقته في الفقه والتفسير واللغة والنحو والكلام وغير ذلك. ولد بأصبهان. ودخل العراق ودرس بالبصرة، ثم استوطن نيسابور إلى أن توفي بها. قال الصاحب ابن عباد: "ما رأينا مثله ولا أرى مثل نفسه". وقال في "يتيمة الدهر": "وله شعر كثير يذكر في شعر الأئمة، ويروى لشرف صاحبه وتحسين الكتب بذكره" (٢).
_________
(١) طبقات المفسرين ٢: ١٤٧ وتاريخ قزويين ١٧١
(٢) طبقات المفسرين للداودي ٢: ١٤٧ وطبقات المصنف ٩٢ والوافي ٣: ١٢٤ والسبكي ٣: ١٦٧ والشذرات ٣: ٦٩ وابن خلكان ٤: ٢٠٤ والعبر ٢: ٣٥٢ ويتيمة الدهر ٤: ٤١٩ ومفتاح السعادة ٢: ١٧٧ وطبقات العبادي ٩٩ والنجرم الزاهرة ٤: ١٣٦.','2','534',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (949,'الشاطبي [٥٨٥ - ٦٧٢ هـ / ١١٨٩ - ١٢٧٤ م]

<span data-type="title" id=toc-1302>محمد بن سليمان بن محمد بن سليمان بن عبد الملك المعافري، أبو عبد الله الشاطبي، ويقال له ابن أبي الربيع:</span>
عالم بالقراءات، مفسر، مشارك في بعض العلوم. ولد بشاطبة، ورحل، فسمع بمصر والشام والحجاز، ثم استقر بالإسكندرية وانقطع للعبادة إلى أن توفي. من آثاره "اللمعة الجامعة في العلوم النافعة" في تفسير القرآن" (١).

ابن النقيب [٦١١ - ٦٩٨ هـ / ١٢١٤ - ١٢٩٩ م]

<span data-type="title" id=toc-1303>محمد بن سليمان بن الحسن بن الحسين البلخي، المقدسي، أبو عبد الله، جمال الدين، المعروف بابن النقيب:</span>
مفسر، من فقهاء الحنفية، ولد بالقدس وسمع بها، وانتقل إلى القاهرة، ودرس بالعاشورية، وأقام بالأزهر مدة. وعاد إلى القدس فتوفي
_________
(١) طبقات المفسرين للداودي ٢: ١٤٥ وهدية العارفين ٢: ١٢٩ وغاية النهاية ٢: ١٤٩ وحسن المحاضرة ١: ٥٢١ والنجوم الزاهرة ٧: ٢٤٣ ونفح الطيب ٢: ١٤٠ والوافي ٣: ١٢٨ وإيضاح المكنون ٢: ٤١١.','2','535',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (950,'بها. له "تفسير القرآن" قال الصفدي: "وهو تفسير حافل جمع فيه خمسين مصنفا، وذكر فيه أسباب النزول والقراءات والإعراب واللغة والحقائق وعلم الباطن، قيل إنها في خمسين مجلدة، ومنه نسخة بجامع الحاكم بالقاهرة أظنها في ثمانين مجلدة". وسماه صاحب كشف الظنون "التحرير والتحبير لأقوال أئمة التفسير في معاني كلام السميع البصير" وقال: "وهو كبير في نيف وخمسين مجلدا، وقد اعتني به ما لم يعتن بغيره". وقال الشعراني: "إنه مائة مجلدة ضخمة ما طالعت أوسع منه" (١).

الكافيجي [٧٨٨ - ٨٧٩ هـ / ١٣٨٦ - ١٤٧٤ م]

<span data-type="title" id=toc-1304>محمد بن سليمان بن سعد بن مسعود الرومي، محيي الدين، أبو عبد الله الكافيجي:</span>
فقيه حنفي، نحوي،
_________
(١) طبقات المفسرين للداودي ٢: ١٤٤ وكشف الظنون ٣٥٨ و ٤٤٠ والأنس الجليل ٢: ٢١٧ وحسن المحاضرة ١: ٤٦٧ والعبر ٥: ٣٩٨ والنجوم الزاهرة ٨: ١٨٨ وطبقات المفسرين للسيوطي ٣٢ والفوائد البهية ١٦٨ والسلوك ١ ق ٣: ٨٨١ والشذرات ٥: ٤٤٢ والوافي ٣: ١٣٦ والبدر السافر ١٠٧ والجواهر المضية ٢: ٥٧ وفوات الوفيات ٣: ٣٨٢ وهدية العارفين ٢: ١٣٩ وتذكرة النبيه ١: ٢١٥.','2','535',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (951,'مفسر، من كبار العلماء بالمعقولات. رومي الأصل، ولد ب"ككجة كي" من بلاد صروخان. وقدم الشام وأقرأ بها، وحج، ودخل بيت المقدس، ثم دخل القاهرة واشتهر بها وأخذ عنه علماؤها، ولازمه السيوطي ١٤ سنة. قال السخاوي: "وقد عظمه الملوك خصوصا ملك الروم ابن عثمان". عرف بالكافيجي لكثرة اشتغاله بالكافية في النحو، وولي وظائف، منها مشيخة الخانقاه الشيخونية، وانتهت إليه رياسة الحنفية بمصر. زادت تصانيفه على المائة وغالبها صغير، منها "التيسير في قواعد التفسير" قال صاحب كشف الظنون: "قيل: كان يفتخر به ظنا منه أنه لم يسبق إليه، ولعله لم ير كتاب "البرهان" للزركشي، ولو رآه لاستحيى منه، أوله، الحمد لله الذي أنزل القرآن رحمة للأنام .. الخ. رتبه على بابين وخاتمة، وذكر فيه الأمير تمربغا الظاهري". و "جواب في تفسير: {والنجم إذا هوى} و "نزهة الإخوان في آي القرآن" في تفسير آية {يا لوط إنا رسل ربك} و "نيل المرام في تفسير قوله تعالى: {وما ربك بظلام ..} و "كشف النقاب للأصحاب والأحباب" في إعجاز','2','536',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (952,'القرآن" (١).

التنكابني [... - نحو ١٣١٠ هـ / ... - نحو ١٨٩٢ م]

<span data-type="title" id=toc-1305>محمد بن سليمان التنكابني:</span>
فقيه إمامي، طبيب، ناظم، مشارك في بعض العلوم. له تصانيف، منها "توسيع التفسير" (٢).

المنصوريوري [... - ١٣٤٩ هـ / ... - ١٩٣٠ م]

<span data-type="title" id=toc-1306>محمد سليمان المنصوريوري، ثم اليتيالوي:</span>
مؤرخ، مفسر، أديب في اللغتين الفارسية والهندية، عارف بالإنكليزية وملم بالعبرية، مشارك في العلوم الإسلامية. من آثاره "الجمال والكمال" في تفسير سورة يوسف (٣).
_________
(١) الضوء اللامع ٧: ٢٥٩ والبدر الطالع ٢: ١٧١ والشقائق النعمانية ١: ١٢٤ والشذرات ٧: ٣٢٦ والفوائد البهية ١٦٩ وبغية الوعاة ١: ١١٧ وروضات الجنات ٢١٠ وهدية العارفين ٢: ٢٠٨ وإيضاح المكنون ٣٦٩ و ٦٥٣ وكشف الظنون ٥٢٠ ومعجم المؤلفين ١٠: ٥١ والأعلام ٧: ٢٢.
(٢) أعيان الشيعة ٤٥: ١٧٨ وهدية العارفين ٢: ٣٩٢ وإيضاح المكنون ١: ٣٣٧ ومعجم رجال الفكر ٩٣ والذريعة ١: ١٥ وأحسن الوديعة ١: ١٢١
(٣) معجم المؤلفين ١٠: ٥٥ ومجلة الحج ١١: ٧٧٢.','2','536',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (953,'أبو رجاء البصري [.. - نحو ١٣٠ هـ / ... - نحو ٧٤٨ م]

<span data-type="title" id=toc-1307>محمد بن سيف الأزدي الحداني، أبو رجاء البصري:</span>
من رجال الحديث الثقات. من أهل البصرة. روى عن أبي بريدة والحسن وغيرهما. وروى عنه شعبة وابن علية، وأخرج له النسائي، وأبو داود في المراسيل. قال في طبقات المفسرين: "له " تفسير" (١).

الشاشي [... - ٩٨٠ هـ / ... - ١٥٧٢ م]

<span data-type="title" id=toc-1308>محمد الشاشي الفركندي النقشبندي، كمال الدين:</span>
فقيه حنفي، استوطن القسطنطينية، ومات بها. قال البغدادي: "كان عالما فاضلا، له "رسالة" في التفسير، حاشية على تفسير البيضاوي (٢).

الكوراني [... - ١٠٧٨ هـ / ... - ١٦٦٧ م]

<span data-type="title" id=toc-1309>محمد شريف بن يوسف بن محمود ابن كمال الدين الكوراني الصديقي (الشاهوي الرويسي)</span>
_________
(١) التاريخ الكبير للبخاري ١: ١٠٤ وطبقات المفسرين للداودي ٢: ١٥٤ وخلاصة تهذيب الكمال ٢٩٥ وابن النديم ٣٤.
(٢) هدية العارفين ٢: ٢٥٢ وإيضاح المكنون ١: ١٤١ والكواكب السائرة ١: ٦٠ و ٦٣.','2','537',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (954,'الشاهوي الرويسي: فقيه شافعي، من العلماء الزهاد. تعلم في بلده، وحج سنة ١٠٥٥ هـ وجاور سنتين. وعاد، فأقرأ في بلده. ثم عاد وجاور مدة. ودخل اليمن، فأخذ عنه جماعة، ومات بأب من مدن اليمن، من آثاره "حاشيتان" على تفسير البيضاوي، الأولى إلى أواخر سورة الكهف، والثانية إلى آخر القرآن (١).

أسعد زاده [... - ١٢٠٤ هـ / ... - ١٧٩٠ م]

<span data-type="title" id=toc-1310>محمد شريف بن محمد أسعد بن إسماعيل الرومي، المعروف بأسعد زاده:</span>
من شيوخ الإسلام في العهد العثماني، وتوفي معزولا عن المشيخة، له تصانيف بالتركية والعربية، فمن العربية، خلاصة التبيين في تفسير سورة ياسين" (٢).

الخاتون آبادي [١٠٥٨ - ١١١٦ هـ / ١٦٤٨ - ١٧٠٤ م]

<span data-type="title" id=toc-1311>محمد صالح بن عبد الواسع بن عبد الواسع الحسيني الخاتون آبادي (الأصبهاني)</span>
_________
(١) خلاصة الأثر ٤: ٢٨٠ وهدية العارفين ٢: ٢٩١ وإيضاح المكنون ١: ١٤٢.
(٢) هدية العارفين ٢: ٣٤٧.','2','537',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (955,'الأصبهاني: فقيه إمامي، مشارك في بعض العلوم. له "تفسير الفاتحة" (١). ابن أبي السعود [.. - ١٢٦٨ هـ ... . - ١٨٥٢ م]

<span data-type="title" id=toc-1312>محمد بن صالح أبي السعود السباعي الحفناوي المصري الشافعي:</span>
عارف بالتفسير، من آثاره "حاشة على تفسير الجلالين" مخطوطة في ثلاث مجلدات (٢).

ابن ملوكة [... - ١٢٧٦ هـ / ... - ١٨٦٠ م]

<span data-type="title" id=toc-1313>محمد بن صالح بن مجدي بن ملوكة التونسي:</span>
فقيه مالكي، فرضي، حاسب، مشارك في بعض العلوم. كان مدرسا في جامع الزيتونة: وعرضت عليه خطط القضاء، والفتوى، فأعرض عنها. له تصانيف، منها "تفسير سورة الفاتحة" و " أسرار فواتح القرآن" (٣).
_________
(١) أعيان الشيعة ٤٥: ٢٤٢ وهدية العارفين ٢: ٣٠٠ ووفاته فيه ١٠٩٩ وفوائد الرضوية ٥٤٦ وإيضاح المكنون ١: ٣٠٧.
(٢) الأعلام ٧: ٣٤ وهدية العارفين ٢: ٣٧٣ وإيضاح المكنون ١: ٣٠٤ وفهرست الخديوية ١: ١٦٥.
(٣) الأعلام ٧: ٣٤ وشجرة النور ٣٩٠ وهدية العارفين ٢: ٣٧٦ وإيضاح المكنون ١: ٣٠٧.','2','538',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (956,'البرغاني [... - ١٢٨١ هـ / ... - ١٨٦٤ م]

<span data-type="title" id=toc-1314>محمد صالح بن محمد إسماعيل البرغاني القزويني:</span>
فقيه إمامي، أصولي، مفسر، متكلم، ولد في برغان (من قرى طهران)، دخل قزوين، وتعلم في النجف، ومات بكربلاء. من آثاره "بحر العرفان في تفسير القرآن" طبع، في سبع مجلدات، ويعرف بتفسير البرغاني (١).

القيصري [... - ١٣٠٢ هـ / ... - ١٨٨٥ م]

<span data-type="title" id=toc-1315>محمد صالح بن عبد الله القيصري الرومي، المعروف بطورون، أي الحفيد:</span>
فقيه حنفي، مفسر، تركي الأصل، مستعرب. له تصانيف، منها "إشارات القرآن" (٢).
_________
(١) أعيان الشيعة ٤٥: ٢٤٠ وانظر صفحة ٢٣٦ ترجمة رقم ٢٢٥١ وصفحة ٢٣٧ ترجمة رقم ٢٢٥٤ فأسماء المؤلفات تدل على أنها لشخص واحد. وانظر هدية العارفين ٢: ٣٧٧ وإيضاح المكنون ١: ٣٠٤ وأحسن الوديعة ٣٥. ومعجم المؤلفين العراقيين ٣: ١٩٥ والأعلام ٦: ١٦٤.
(٢) هدية العارفين ٢: ٣٨٤.','2','538',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (957,'صديق حسن خان [١٢٤٨ - ١٣٠٧ هـ / ١٨٣٢ - ١٨٩٠ م]

<span data-type="title" id=toc-1316>محمد صديق خان بن حسن بن علي ابن لطف الله الحسيني البخاري القنوجي، أبو الطيب:</span>
من رجال الإصلاح الإسلامي. ولد في بلدة قنوج بالهند، وبها نشأ وتعلم، وانتقل إلى "دهلي" فأخذ عن علمائها، ثم سافر إلى بهوبال ومنها إلى الحجاز، فحج، وجاور نحو ثمانية أشهر، ثم عاد إلى بهوبال واستوطنها، واستوزر وناب، وتزوج بملكة إقليم الدكن "جهان بيكم" ولقب بنواب عالي الجاه أمير الملك بهادر. له نيف ومئة كتاب بالعربية والفارسية والهندية ما بين مطول ومختصر، منها "فتح البيان في مقاصد القرآن" طبع في عشرة أجزاء، في التفسير" و "نيل المرام من تفسير آيات الأحكام" طبع، و "خلاصة الكشاف" في إعراب القرآن، طبع، وغير ذلك (١).
_________
(١) التاج المكلل (لصاحب الترجمة) ٥٤١ وأبجد العلوم ٩٣٩ وفهرس الفهارس ١: ٢٦٩ وتاريخ آداب اللغة العربية ٤: ٢٣٨ ومعجم المطبوعات ١٢٠١ وهدية العارفين ٢: ٣٨٨ وجلاء العينين ٣٠ وإيضاح المكنون ١: ١٠٧ و ٢: ٦٩٨ ومعجم المؤلفين ١٠: ٩٠ والأعلام ٧: ٣٦.','2','539',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (958,'اللاري [... - ٩٧٩ هـ / ... - ١٥٧١ م]

<span data-type="title" id=toc-1317>محمد بن صلاح الدين بن جلال الدين بن كمال الدين محمد الأنصاري السعدي العبادي، المعروف بمصلح الدين اللاري:</span>
فقيه شافعي، مؤرخ، نحوي، مفسر، ولد باللار (بين الهند وشيراز) وبها نشأ وتعلم. وانتقل إلى الهند، فالقسطنطينية، فديار بكر، وأقام بآمد، وزار حلب سنة ٩٦٤ وحج، وعاد إلى آمد فدرس وأفتى وصنف، إلى أن توفي. من تصانيفه "حاشية على أنوار التنزيل" في التفسير، إلى آخر الزهراوين. و "تفسير سورة القدر" (١)

الكلاوي [... - ١٣٣٤ هـ / ... - ١٩١٦ م]

<span data-type="title" id=toc-1318>محمد بن طالب بن سعيد الكلاوي:</span>
فقيه، أصولي، مفسر، ولد ب"كلة" قرية من أعمال حلب تابعة لقضاء
_________
(١) هدية العارفين ٢: ٢٥١ والعقد المنظوم ٤١٩ وذيل الشقائق النعمانية ١٦٩ ومعجم المطبوعات ١٥٨٥ والشذرات ٨: ٣٥٠ ووفاته فيه: سنة ٩٦٧ تقريبا، وكشف الظنون ١٩١ والتيمورية ٣: ٢٦٢.','2','539',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (959,'إدلب، وتوفي بظاهر حلب. له تصانيف منها "حواش" على حاشيتي القونوي والشهاب الخفاجي، على أنوار التنزيل، في التفسير، للبيضاوي (١).

ابن الوزير [... - ٣٦٥ هـ / ... - ٩٧٦ م]

<span data-type="title" id=toc-1319>محمد بن طاهر بن محمد بن الحسن بن الوزير:</span>
أديب، مفسر، واعظ، محدث، من أهل نيسابور. كان أولا حنفيا، ثم تحول شافعيا. قال الصفدي: "كان كثير العلوم فصيح اللسان بارع الوعظ، سمع منه الحاكم أبو عبد الله (٢).

ابن القيسراني [٤٤٨ - ٥٠٧ هـ / ١٠٥٦ - ١١١٣ م]

<span data-type="title" id=toc-1320>محمد بن طاهر بن علي بن أحمد المقدسي الشيباني، أبو الفضل، المعروف بابن القيسراني:</span>
حافظ للحديث مؤرخ، نسابة، رحالة، متكلم، من أهل بيت المقدس، سمع
_________
(١) إعلام النبلاء ٧: ٥٧٢ ومعجم المؤلفين ١٠: ٩٥.
(٢) طبقات المفسرين للداودي ٢: ١٥٥ واللباب ٣: ٣٦٤ وميزان الاعتدال ٣: ٥٨٦ ولسان الميزان ٥: ٢٠٧.','2','540',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (960,'بها وبمصر والشام والحجاز والجزيرة والعراق وأصبهان والجبال وفارس وخراسان وخوزستان، واستوطن همدان، وتوفي ببغداد، قال الصفدي: "كان داودي المذهب، فمن أثنى عليه فلحفظه الحديث وإلا فالجرح أولى به .. " من آثاره "الناسخ والمنسوخ" (١).

ابن يونس [... - ٨٣٣ هـ / ... - ١٤٣٠ م]

<span data-type="title" id=toc-1321>محمد بن طاهر بن يونس:</span>
قاض، من كبار فقهاء الشافعية، من بيت رياسة وعلم. أصله من أربل. قال السخاوي: "برع في الفقه والتفسير، وولي قضاء الموصل كآبائه من قبله سنين، وتمول وفخم، وحمدت سيرته، إلى أن ثار أصبهان بن قرا يوسف وعاث بتلك البلاد، فلما أخذ الموصل عذبه حتى هلك في العقوبة .. ". له "تفسير
_________
(١) لسان الميزان ٥: ٢٠٧ والمتظم ٩: ١٧٧ والوافي ٣: ١٦٦ وهدية العارفين ٢: ٨٢ ومرآة الجنان ٣: ١٩٥ والبداية والنهاية ١٢: ١٧٦ وشذرات الذهب ٤: ١٨ والعبر للذهبي ٤: ١٤ وتذكرة الحفاظ ١٢٤٢ وميزان الاعتدال ٣: ٥٨٧ وابن خلكان ٤: ٢٨٧ ومعجم أعلام فلسطين. (مخطوط) للمؤلف.','2','540',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (961,'القرآن " في مجلدين (١).

الفتني [٩١٠ - ٩٨٦ هـ / ١٥٠٤ - ١٥٧٨ م]

<span data-type="title" id=toc-1322>محمد طاهر الصديقي الهندي، الفتني، جمال الدين:</span>
عالم بالحديث ورجاله، لغوي، مفسر، كان يلقب بملك المحدثين. ولد في "فتن" من بلاد كجرات بالهند، وبها نشأ وتعلم، وحج، فأخذ عن علماء مكة والمدينة. وعاد إلى بلده، فساعد الفقراء على طلب العلم، وانقطع للتدريس والتصنيف. ودعا إلى مناوأة البواهير وكانوا قومه، وانكر عليهم بدعتهم، فانفردوا به فقتلوه بالقرب من " أجين"، ودفن في فتن. من آثاره "مجمع بحار الأنوار في غرائب التنزيل ولطائف الأخبار" طبع، في أربعة أجزاء. وهو كتاب جامع لكشف مطالب الآيات والآثار وإيضاح معاني كتاب وأحاديث رسوله المختار (غريب القرآن والحديث) (٢).
_________
(١) الضوء اللامع ٧: ٢٧٤ وهدية العارفين ٢: ١٨٧ وإيضاح المكنون ١: ٣٠٣ ومعجم المؤلفين ١٠: ١٠٣.
(٢) معجم المطبوعات ١٦٧٠ والنور السافر ٣٦١ والشذرات ٨: ٤١٠ وهدية العارفين ٢: ٢٥٥ وكشف الظنون ١٥٩٩ والرسالة المستطرفة ١١٣ وإيضاح المكنون ١: ٣٠٨ وأبجد العلوم ٨٩٥ وسبحة المرجان ٤٣ والأعلام ٧: ٤٢ ومعجم المؤلفين ١٠: ١٠٠.','2','541',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (962,'مكي زاده [... - ١١٢٨ هـ / ... - ١٧١٦ م]

<span data-type="title" id=toc-1323>محمد طاهر بن محمد مكي الرومي، المعروف بمكي زاده:</span>
قاض، من فقهاء الحنفية، مفسر، تركي الأصل، مستعرب، ولي القضاء ببيت المقدس. من آثاره "تفسير سورة الإخلاص" (١)

قاضي زاده [... - ١٢٥٤ هـ / ... - ١٨٣٨ م]

<span data-type="title" id=toc-1324>محمد طاهر بن عمر التوقادي، ثم الاستمبولي الرومي، المعروف بقاضي زاده:</span>
فقيه حنفي، متكلم، مفسر، تولى مشيخة الإسلام. من آثاره "تفسير سورة الإخلاص" (٢).

ابن عاشور [١٢٩٦ - ١٣٩٣ هـ / ١٨٧٩ - ١٩٧٣ م]

<span data-type="title" id=toc-1325>محمد الطاهر بن عاشور:</span>
رئيس المفتين المالكيين بتونس، وأحد كبار علمائها، مفسر، لغوي، نحوي، أديب، من دعاة الإصلاح الإجتماعي والديني: ولد ونشأ وتعلم بتونس.
_________
(١) هدية العارفين ٢: ٣١٣ وإيضاح المكنون ١: ٣٠٥.
(٢) هدية العارفين ٢: ٣٧٠ ومعجم المؤلفين ١٠: ٩٩.','2','541',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (963,'وتخرج بشهادة التطويع سنة ١٨٩٩. ودرس في جامع الزيتونة وفي المدرسة الصادقية، عين شفي للإسلام مالكيا (سنة ١٩٣٢)، وسمي شيخ الجامع الأعظم وفروعه عام ١٩٤٢، ثم شفي عميدا للجامعة الزيتونية عام ١٩٥٦. كما شارك في تأسيس الجمعية الزيتونية والجمعية الخلدونية. انتخب عضوا بمجمع اللغة العربية بمصر سنة ١٩٥٠، وبالمجمع العلمي العربي بدمشق سنة ١٩٥٥. له أبحاث ودراسات ومقالات كثيرة نشرت في كبريات المجلات بتونس ومصر. وتوفي بتونس. من آثاره "التحرير والتنوير" في تفسير القرآن، في ٣٠ جزءا في نحو ٧ آلاف صفحة، صدر منها عشرة أجزاء. (١)

الباقلاني [٣٣٨ - ٤٠٣ هـ / ٩٥٠ - ١٠١٣ م]

<span data-type="title" id=toc-1326>محمد بن الطيب بن محمد بن جعفر، أبو بكر الباقلاني:</span>
متكلم، فقيه، قاض، من كبار علماء الكلام، انتهت
_________
(١) مجلة المنهل ٣٩: ٧٩٢ ومصادر الدراسة الأدبية ٣: ٥٧ وفيه وفاته سنة ١٩٧٠، خطأ، وهي وفاة ابنه العلامة الشيخ محمد الفاضل بن عاشور. وجريدة العمل التونسية ٨/ ١١/ ١٩٦٨.','2','542',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (964,'إليه الرياسة في مذهب الأشاعرة. ولد ونشأ في البصرة، وتعلم ببغداد. استدعاه عضد الدولة إلى شيراز لمجادلة المعتزلة، فتغلب عليهم. وبقي معه إلى أن دخلا بغداد، فعاش فيها. ثم وجهه سفيرا عنه إلى ملك الروم في القسطنطينية فكانت له فيها مناظرات مع علماء النصرانية بين يدي الملك، فتغلب عليهم أيضا. واشتغل بالتدريس العام، ثم لأبناء عضد الدولة. وولي قضاء الثغر مدة. توفي ببغداد. من آثاره "إعجاز القرآن" طبع، قال في الموسوعة العربية: "رد فيه على الأدباء والبلاغيين والمتكلمين. وأجمل الإعجاز فيما في القرآن من مغيبات وتاريخيات، وفي نظمه الغريب، ورأى أنه نظم خارج عن معهود العرب. تتعذر معانيه على البشر، ويشتمل على فصاحة معجزة. ونفى كل شبه بينه وبين الشعر والسجع، واضطر من أجل ذلك إلى التحامل والتكلف في نقد الشعراء والكتاب القدماء، فكتابه هام في علم الكلام قليل الأهمية في النقد" (١).
_________
(١) الباقلاني وكتابه إعجاز القرآن، مقدمة المحقق. وترتيب المدارك ٤: ٥٨٥ والديباج ٢٦٧ والعبر ٣: ٨٦ والوافي ٣: ١٧٧ وتاريخ بغداد ٥: ٣٧٩ والموسوعة العربية الميسرة ٣١٣ وابن كثير ١١: ٣٥٠ وتبيين كذب =','2','542',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (965,'ابن كيران [١١٧٢ - ١٢٢٧ هـ / ١٧٥٨ - ١٨١٢ م]

<span data-type="title" id=toc-1327>محمد بن الطيب بن عبد المجيد بن عبد السلام بن كيران، أبو عبد الله:</span>
فقيه مالكي، حافظ، محدث، مشارك في عدة علوم، من أهل فاس مولدا ونشأة، وتوفي بالشهدة. قال صاحب شجرة النور الزكية: "ألف تآليف مختلفة الأوضاع مفيدة، منها "تفسير القرآن العظيم" من سورة النساء إلى قوله تعالى في سورة غافر: {يا قوم إنما هذه الحياة الدنيا متاع ..} و "تفسير الفاتحة" و "تفسير سورة البقرة" لم يكملها (١).

الطيب الأنصاري [١٢٩٦ - ١٣٦٣ هـ / ١٨٧٩ - ١٩٤٤ م]

<span data-type="title" id=toc-1328>محمد الطيب بن إسحاق بن الزبير بن محمد الأنصاري الخزرجي المدني:</span>
مدرس، ناظم، له اشتغال بالتفسير، المفتري ٢١٧ والشذرات ٣: ١٦٨ والمنتظم ٧: ٢٦٥ وتاريخ التراث العربي ٢: ٣٨٤ واللباب ١: ٩٠ والنجوم الزاهرة ٤: ٢٣٤ وتذكرة الحفاظ ١٠٧٩ وابن خلكان ٤: ٢٦٩ وهدية العارفين ٢: ٥٩ والأعلام ٧: ٤٦ وروضات الجنات ١٧٧ ومعجم المؤلفين ١٠: ١٠٩.
_________
(١) شجرة النور ٣٧٦ وسلوة الأنفاس ٢: ٣٤٩ والأعلام ٧: ٤٧.','2','543',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (966,'ولد ونشأ في مكان يسمى "المراقد" بالمغرب، وانتقل إلى المدينة المنورة سنة ١٣٢٥ هـ فدرس في المسجد النبوي الشريف إلى آخر حياته. من آثاره "تحبير التحرير في اختصار تفسير الإمام ابن جرير" مخطوط (١).

ابن طيفور [... - ٥٦٠ هـ / ... - ١١٦٥ م]

<span data-type="title" id=toc-1329>محمد بن طيفور السجاوندي الغزنوي، أبو عبد الله:</span>
مفسر، نحوي، لغوي، عارف بالقراءات، له "عين المعاني في تفسير السبع المثاني" في تفسير القرآن، قال القفطي: "ذكر فيه النحو وعلل القراءات والأبيات ومعانيها واللغة إلى غير ذلك من معاني التفسير، في مجلدات، أعدادها قليلة، وفوائدها كثيرة جليلة، واختصر ولده هذا التفسير وسماه "إنسان العين" (٢).
_________
(١) الأعلام ٧: ٤٧
(٢) كشف الظنون ١١٨٢ والوافي ٣: ١٧٨ وغاية النهاية ٢: ١٥٧ وإنباه الرواة ٣: ١٥٣ وطبقات المفسرين للسيوطي ٣٢ وطبقات المفسرين للداودي ٢: ١٥٥ وطبقات ابن قاضي شهبة ١: ٥٩. وبروكلمان ١: ٤٠٨ والذيل ١: ٧٢٤ ومعجم المؤلفين ١٠: ١١٢.','2','543',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (967,'ابن الحجام [... - حيا ٣٢٨ هـ / ... - حيا ٩٤٠ م]

<span data-type="title" id=toc-1330>محمد بن العباس بن علي بن مروان البزاز، المعروف بابن الحجام، أبو عبد الله:</span>
فقيه إمامي، أصولي، مفسر، مشارك في بعض العلوم. ذكر له العاملي ١١ مؤلفا، منها "تفسير القرآن" كبير، و "الناسخ والمنسوخ" (١).

أبو المواهب [١٠٤٤ - ١١٢٦ هـ / ١٦٣٤ - ١٧١٤ م]

<span data-type="title" id=toc-1331>محمد بن عبد الباقي بن عبد القادر البعلي الدمشقي، أبو المواهب:</span>
مقرىء، عارف بالتفسير، من فقهاء الحنابلة. أصله من بعلبك، مولده ووفاته بدمشق، وكان مفتي الحنابلة بها. زار مصر سنة ١٠٧٢ هـ. له رسائل في تفسير بعض الآيات الكريمة منها "رسالة" في تفسير قوله تعالى {فأكلا منها فبدت لهما سوآتهما} (سورة طه: ١٢١) ورسالة في تفسير قوله تعالى: {قالوا يا أبانا ما لك لا تأمنا على يوسف وإنا له لناصحون} (سورة يوسف: الآية ١١) (٢).
_________
(١) أعيان الشيعة ٤٥: ٢٦٣ والنجاشي ٢٦٨ والطوسي ١٤٩ ومعجم المؤلفين ١٠: ١٢٠.
(٢) هدية العارفين ٢: ٣١٢ وفهرس الفهارس ١: ٣٨١ والأعلام ٧: ٥٥ وسلك الدرر ١: ٦٧ وهو فيه =','2','544',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (968,'السبكي [٧٠٧ - ٧٧٧ هـ / ١٣٠٧ - ١٣٧٥ م]

<span data-type="title" id=toc-1332>محمد بن عبد البر بن يحيى بن علي بن تمام، بهاء الدين أبو البقاء السبكي:</span>
قاض، عالم بالعربية والأدب، مفسر، من فقهاء الشافعية. من أهل مصر، لازم أبي حيان وقرأ العربية عليه. وانتقل إلى دمشق سنة ٧٣٩ هـ وأقام بها مدة، وولي قضاءها نيابة عن قريبه تقي الدين السبكي، ثم وليه استقلالا. ثم ولي قضاء طرابلس الشام. ورجع إلى القاهرة فولي قضاء العسكر ووكالة بيت المال (سنة ٧٦٥ هـ) ثم قضاء القاهرة (٧٦٦ هـ) ثم قضاء دمشق، ومات بها. قال ابن حبيب: "كان حجة في التفسير واللغة والنحو والأدب، قدوة في الأصول والفروع" (١).

الإله آبادي [١٢٥٢ - ١٣٣٣ هـ / ١٨٢٤ - ١٩١٥ م]

<span data-type="title" id=toc-1333>محمد بن عبد الحق بن شاه محمد بن يار محمد الاله آبادي الهندي المكي:</span>
_________
= أبو المواهب بن عبد الباقي. ومعجم المؤلفين ١٠: ١٢٢.
(١) إنباء الغمر ١: ١٢١ والوافي ٣: ٢١٠ والنجوم الزاهرة ١١: ١٣٦ وبغية الوعاة ١: ١٥٢ والدرر الكامنة ٤: ١٠٩ وشذرات الذهب ٦: ٢٥٣.','2','544',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (969,'مفسر، أصولي، من فقهاء الحنفية، له اشتغال بالفلسفة والتصوف على طريقة الشيخ الأكبر. ولد في "اله آباد" بالهند، وبها نشأ وتعلم. حج سنة ١٢٨٣ هـ وأقام بالمدينة أربع سنوات. وسكن مكة، وعرف فيها بشيخ الدلائل، لأن الحجاج الهنود كانوا يأخذون منه إجازة "دلائل الخيرات" ويبايعونه، وتوفي بها. ودفن بالمعلاة. من تصانيفه "الإكليل على مدارك التنزيل وحقائق التأويل" طبع، في شرح تفسير مدارك التنزيل، لعبد الله ابن محمد النسفي، سبعة أجزاء في ثلاثة مجلدات (١).

الأسمندي [٤٨٨ - ٥٥٢ هـ / ١٠٩٥ - ١١٥٧ م]

<span data-type="title" id=toc-1334>محمد بن عبد الحميد بن الحسن بن الحسن بن حمزة، أبو الفتح، علاء الدين الأسمندي السمرقندي:</span>
من كبار فقهاء الحنفية، مناظر، متكلم، ولد بسمرقند، ونسبته إلى أسمند من قراها. دخل بغداد حاجا سنة ٥٥٢ هـ
_________
(١) الأعلام ٧: ٥٧ ومعجم المطبوعات ١٦٧٣ ومعجم المؤلفين ١٠: ١٢٩.','2','545',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (970,'وحدث بها، وناظر علماءها. أملى كتابا في "التفسير" (١).

ابن بنت الميلق [٧٣١ - ٧٩٧ هـ / ١٣٣١ - ١٣٩٥ م]

<span data-type="title" id=toc-1335>محمد بن عبد الدائم بن محمد بن سلامة، أبو المعالي، ناصر الدين، المعروف بابن بنت الميلق، ويختصر فيقال ابن الميلق:</span>
قاض، مفسر، صوفي، واعظ بليغ، من أهل مصر. كان شافعيا شاذليا. ولاه الظاهر برقوق (٧٨٤ - ٨٠١ هـ) قضاء القضاة الشافعية بالديار المصرية في شعبان سنة ٧٨٩ هـ. قال ابن حجر: "فباشره بمهابة وصرامة، ولم يحمد مع ذلك في ولايته، وأهين بعد عزله (٧٩١ هـ) بمدة". وقال المقريزي: "امتحن بولاية القضاء، فلم تشكر سيرته، وعزل ونكب بأخذ مال كبير منه ظلما، وغورت عينه" ثم انقطع عن الأعمال إلى أن توفي. من تصانيفه "الأنوار اللائحة في أسرار
_________
(١) طبقات المفسرين للسيوطي ٣٥ وطبقات المفسرين للداودي ٢: ١٧٧ والوافي ٣: ٢١٨ والمنتظم ١٠: ٢٢٦ والجواهر المضية ٢: ٧٤ وتاج التراجم ٥٦ والنجوم الزاهرة ٥: ٣٧٩ والفوائد البهية ١٧٦ ولسان الميزان ٥: ٢٤٣ ووفاته في بعض هذه المراجع سنة ٥٦٣ والشذرات ٣: ٢١٠.','2','545',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (971,'الفاتحة" في تفسيرها، "منه نسخة بخط أبي الفضل محمد الخطيب الطسوجي في سنة ٩١١ هـ "، قال آغابزرك: "وفي النسخة "تفسير التوحيد" للمفسر المذكور في الخزانة الرضوية (١).

ابن الست [١١١٦ - ١١٩٩ هـ / ١٧٠٤ - ١٧٨٥ م]

<span data-type="title" id=toc-1336>محمد بن عبد ربه بن علي العزيزي، المعروف بأبى الست:</span>
فاصل، مشارك في بعض العلوم، من أهل العزيزية بشرقية مصر. كانت أمه سرية رومية، فاشتهر بنسبته إليها. له "حاشية" على تفسير آية الكرسي" (٢).

التميمي [... - بعد ٣٦٠ هـ / ... - بعد ٩٧١ م]

<span data-type="title" id=toc-1337>محمد بن عبد الرحمن بن الفضل بن الحسن التميمي الجوهري، أبو بكر:</span>
_________
(١) إنباء الغمر١: ٢٣١ و ٣٨٥ و ٥٠٣ ونزهة النفوس والأبدان ١: ٦٨ و ١٥٥ و ١٧٠ و ٢٥٨ و ٤١٩ والسلوك ٣ ق ٣: ٩٢، و ٥٦٥، ٥٧٥، ٦٣٣، ٦٣٤، ٦٦٤، ٨٤٦، و ٩٤١ والدرر الكامنة ٤: ١١٤ وهدية العارفين ٢: ١٧٥ والأعلام ٧: ٦٠ ومعجم المطبوعات ١٨٩ وتعليقات آغا بزرك على كشف الظنون ٢١.
(٢) الخطط الوفيقية ١٤: ٥٠ وعجائب الآثار ٢: ١٠٠ وهدية العارفين ٢: ٣٤٤ والأعلام ٧: ٦٠.','2','546',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (972,'مقرئ مشهور، مفسر، أخذ عنه أبو نعيم وقال: "خطيب، صاحب التفاسير والقراءات، وأثنى عليه ابن الجزري" (١).

ابن صبر [٣٢٠ - ٣٨٠ هـ / ٩٣٢ - ٩٩١ م]

<span data-type="title" id=toc-1338>محمد بن عبد الرحمن بن صبر (وقيل بن صرد)، أبو بكر:</span>
معتزلي مشهور، عالم بالكلام، خبير بالتفسير، ولي القضاء بعسكر المهدي. له كتاب في "تفسير القرآن" (٢).

القاضي الرئيس [٣٧٨ - ٤٧٨ هـ / ٩٨٨ - ١٠٨٥ م]

<span data-type="title" id=toc-1339>محمد بن عبد الرحمن بن أحمد بن علي النسوي، أبو عمرو:</span>
مفسر، قاض،
_________
(١) طبقات المفسرين للسيوطي ٤٠ وطبقات المفسرين للداودي ٢: ١٨٤ وغاية النهاية ٢: ١٦٥ ومعجم المؤلفين ١٠: ١٤٨.
(٢) طبقات المفسرين للدودي ٢: ١٨١ وطبقات المفسرين للسيوطي ٣٣ وميزان الاعتدال ٣: ٦٢٧ ولسان الميزان ٥: ٢٥٥ وتاج التراجم ٦٤ وهدية العارفين ٢: ٥١ وتاريخ بغداد ٢: ٣٢١ ومعجم المؤلفين ١٠: ١٣٦ وانظر أيضا ترجمة محمد بن عبد الله بن جعفر (الآتية في هذا المعجم) ولسان الميزان ٥: ٢٤١.','2','546',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (973,'من فقهاء الشافعية، له شعر ومعرفة بالأدب. ولد في نسا (بخراسان) وبها نشأ وتعلم، ورحل إلى العراق ونيسابور وجرجان ومصر والشام والحجاز. قال السبكي: "بعث رسولا إلى دار الخلافة ببغداد من جهة الأمير طغرلبك، وكان سلاطين السلجوقية يعتمدونه فيما يعن لهم من المهمات. وولاه القائم بأمر الله القضاء بخوارزم، ولقبه بأقضى القضاة" وقال السمعاني: "هو المعروف بالقاضي الرئيس، وكان من أكابر أهل عصره فضلا وحشمة وقبولا عند الملوك". وقال الجرجاني: "صنف كتبا في "الفقه" و "التفسير" (١).

الشاطبي [... - ٥١٩ هـ / ... - ١١٢٥ م]

<span data-type="title" id=toc-1340>محمد بن عبد الرحمن بن موسى بن عياض، أبو عبد الله المخزومي الشاطبي المنتيشي:</span>
إمام في التفسير والقراءات، له مشاركة في عدة علوم. تصدر للإقراء بشاطبة. وسمع بالمرية ومرسية ومالقة.
_________
(١) طبقات المفسرين للسيوطي ٣٦ وطبقات المفسرين للداودي ٢: ١٧٨ وطبقات الشافعية للسبكي ٤: ١٧٥.','2','547',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (974,'أثنى عليه ابن الأبار (١).

الزاهد البخاري [... - ٥٤٦ هـ / ... - ١١٥١ م]

<span data-type="title" id=toc-1341>محمد بن عبد الرحمن بن أحمد، أبو عبد الله البخاري، علاء الدين، الملقب بالزاهد:</span>
مفسر، من أهل بخارى. قال السمعاني: "كان إماما مفننا مذكرا أصوليا متكلما. صنف كتابا في "التفسير" أكثر من ألف جزء أملاه في آخر عمره، ولكنه كان مجازفا متساهلا" (٢).

ابن عسكر [٧٠١ - ٧٦٧ هـ / ١٣٠١ - ١٣٦٥ م]

<span data-type="title" id=toc-1342>محمد بن عبد الرحمن بن عسكر البغدادي:</span>
مفسر، عالم بالعربية والمنطق والأصول، من كبار علماء المالكية في عصره، من أهل بغداد، ولي قضاءها
_________
(١) المعجم لابن الأبار ١٠٠ وطبقات المفسرين للداودي ٢: ١٨٤ وبغية الملتمس ٩٠.
(٢) طبقات المفسرين للسيوطي ٣٦ وطبقات المفسرين للداودي ٢: ١٧٧ وتاج التراجم ٥٦ والفوائد البهية ١٧٥ والوافي ٣: ٢٣٢ والجواهر المضية ٢: ٧٦ وهدية العارفين ٢: ٩١ وكشف الظنون ٤٥٤ و ٤٥٨ والشذرات ٧: ١٥٧.','2','547',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (975,'ثم الحسبة بها، ودرس بالمستنصرية. من آثاره "تفسير كبير". قال صاحب الديباج: "بلغني قبل وفاته بنحو خمسة عشر عاما أنه وصل فيه إلى سورة تبارك" (١).

ابن الصائغ [٧٠٨ - ٧٧٦ هـ / ١٣٠٨ - ١٣٧٥ م]

<span data-type="title" id=toc-1343>محمد بن عبد الرحمن بن علي، شمس الدين الزمردي، ابن الصائغ:</span>
مفسر، أديب، نحوي، لغوي، من فقهاء الحنفية. من أهل مصر. قال ابن حجر: "ولي قضاء العسكر وإفتاء دار العدل، ودرس بالجامع الطولوني وغيره". من كتبه "المنهج القويم في فوائد تتعلق بالقرآن الكريم" (٢).
_________
(١) طبقات المفسرين للداودي ٢: ١٨٢ وفيه وفاته سنة ٧٧٦ والديباج ٣٣٣ وفيه وفاته سنة ٧٩٦ وشجرة النور ٢٢٢ وفيه وفاته سنة ٧٦٧.
(٢) طبقات المفسرين ٢: ١٨٢ وإنباء الغمر ١: ٩٥ والفوائد البهية ١٧٥ وإنباء الغمر ١: ٩٥ والتراث ٦: ٢٤٨ وغاية النهاية ٢: ١٦٣ وتاج التراجم ٦٤ وبغية الدعاة ١: ١٥٥ وكشف الظنون ١٨٨٣ والدرر الكامنة: ١١٩ وحسن المحاضرة ١: ٧١، وهدية العارفين ٢: ٩٩ و ١٦٨.','2','548',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (976,'السخاوي [٨٣١ - ٩٠٢ هـ / ١٤٢٧ - ١٤٩٧ م]

<span data-type="title" id=toc-1344>محمد بن عبد الرحمن بن محمد بن أبي بكر بن عثمان، أبو الخير، أبو عبد الله، شمس الدين السخاوي:</span>
مؤرخ ناقد، عالم بالحديث والتفسير والأدب، من فقهاء الشافعية، ملأت شخصيته الحركة الأدبية المصرية زهاء نصف قرن. ولد بالقاهرة في أسرة أصلها من بلدة (سخا) من أعمال الغربية بمصر. وتلقى علومه على أصحاب أبيه وبخاصة ابن حجر العسقلاني الذي كان يتبوأ مركز الزعامة العلمية في مصر. وكان بدء اتصال السخاوي بأستاذه ابن حجر في سنة ٨٣٨ هـ، ولبثت العلاقة بينهما حتى توفي ابن حجر سنة ٨٥٢ هـ. ثم تنقل بين مصر والشام والحجاز فترة طويلة، فأخذ عن شيوخ دمياط والإسكندرية ومكة والمدينة وبيت المقدس والخليل ونابلس ودمشق وحمص وحماه وحلب. ولما عاد إلى مصر عكف على التدريس والإقراء في الظاهرية والبرقوقية، والفاضلية وسعيد السعداء ودار الحديث الكاملية وغيرها. وحج مرات وجاور، ثم تخلى عن التدريس وعكف على التأليف، وكانت قد انتهت','2','548',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (977,'إليه الرياسة يومئذ في معظم علوم عصره ولا سيما الحديث. وعرض عليه الأمير يشبك الداوادار ان يقرأ التاريخ بمجلس السلطان الظاهر خشقدم، فأبى، ثم عرض عليه أن يتولى القضاء، فاعتذر. وسافر إلى مكة ليحج للمرة السابعة، وتردد حينا بين مكة والمدينة، ثم استقر أخيرا بالمدينة، إلى أن توفي بها في ١٣ ذي القعدة، حسب رواية صاحب الكواكب السائرة. ولكن صاحب الشذرات يضع وفاته بمكة في ٢٨ شعبان. صنف زهاء مئتي كتاب (١).

الإيجي [٨٣٢ - ٩٠٥ هـ / ١٤٢٩ - ١٥٠٠ م]

<span data-type="title" id=toc-1345>محمد بن عبد الرحمن بن محمد بن عبد الله بن محمد الحسني الحسيني الإيجي، معين الدين:</span>
مفسر، من فقهاء الشافعية. ولد ب"إيج" بنواحي شيراز، وتعلم بها وبكرمان وخراسان.
_________
(١) المؤرخون في مصر ٣٩ ومؤرخو مصر الإسلامية ١٢٧ والتبر المسبوك، مقدمته، وعلم التاريخ عند المسلمين ٣٧١ والتعريف بالمؤرخين ١: ٢٥٢ والتاج المكلل ٤٣٩ والضوء اللامع ٨: ٢ والكواكب السائرة ١: ٥٣ والشذرات ٨: ١٥ ومعجم المطبوعات ١٠١٢ وهدية العارفين ٢: ٢١٩ وزيدان ٣: ١٨٣ وفهرس الفهارس ٢: ٣٣٥ والبدر الطالع ٢: ١٨٤ والنور السافر ١٦.','2','549',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (978,'وتولى الإفتاء ببلده. وحج سنة ٨٦٧ هـ وسكن مكة أكثر من عشر سنين. قال السخاوي: "وانتفع به جماعة، وعمل " تفسيرا" في مجلد ضخم، ورسالة في "تفسير سورة الكوثر". وقال صاحب كشف الظنون: له "جوامع التبيان في التفسير" ذكر فيه أن والده شرع فكتب من سورة الأنعام نبذا ثم ترك وقال له: أنت مأمور بذلك، فشرع في الروضة الشريفة في الثاني من جمادى الآخرة سنة ٩٠٤، واختتمه في ٢٥ رمضان سنة ٩٠٥ هـ، الخ" وعنه أخذ البغدادي في هدية العارفين فسمى تفسيره "جوامع التبيان" وأضاف: وله "تفسير سورة الفاتحة". وسماه صاحب معجم المطبوعات "جامع البيان في تفسير القرآن" وذكر أنه طبع مرتين بهذا الاسم، الأولى في لاهور سنة ١٨٧٩ م، والثانية بدهلي سنة ١٢٩٦ هـ (١).

العلقمي [٨٩٧ - ٩٦٩ هـ / ١٤٩١ - ١٥٦١ م]

<span data-type="title" id=toc-1346>محمد بن عبد الرحمن بن علي بن أبي بكر العلقمي، شمس الدين:</span>
فقيه
_________
(١) الضوء اللامع ٨: ٣٧ ومعجم المطبوعات ٥٠٠ وكشف الظنون ٤٥٢ و ٦١٠ وهدية العارفين ٢: ٢٢٣ وإيضاح المكنون ١: ٣٠٣ وفهرست الخديوية ١: ١٥٩ والأعلام ٧: ٦٨.','2','549',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (979,'شافعي، مفسر، محدث، من أهل مصر. تتلمذ لجلال الدين السيوطي وأخذ عن البدر الغزي، ودرس بالجامع الأزهر. من آثاره "قبس النيرين" حاشية على تفسير الجلالين، فرغ منها سنة ٩٥٢ هـ، مخطوط (١).

القيسي [نحو ٦٣٠ - ٧٠١ هـ /نحو ١٢٣٣ - ١٣٠٢ م]

<span data-type="title" id=toc-1347>محمد بن عبد الرحيم بن الطيب، أبو القاسم، ويقال أبو العباس، القيسي:</span>
علامة المغرب في القراءات، عارف بالتفسير والعربية والحديث، ولد بالجزيرة الخضراء بالأندلس، وبها نشأ وتعلم. ثم سكن سبتة، فأكرمه أميرها أبو القاسم العزفي. وكان ضريرا. قال في طبقات المفسرين: "كان إليه المنتهى في العصر في معرفة القراءات وضبطها
_________
(١) الشذرات ٨: ٣٣ ووفاته فيه سنة ٩٦٣ تقريبا، والكواكب السائرة ٢: ٤١ وفيه: تأخرت وفاته عن سنة ٩٦١ وهدية العارفين ٢: ٢٤٢ وفيه ولادته سنة ٨٧٩ ووفاته سنة ٩٦١ هـ، وكشف الظنون ٤٤٥ وفيه: فرغ من رسالته القبس سنة ٩٥٢ هـ، وصفحة ٥٦٠ وفيها وفاته سنة ٩٢٩ هـ. والخديوية ١: ٣٩٣ والعبدلية (الثاني من الزيتونة) ١٨٥ وفيهما وفاته سنة ٩٦٩ ومعجم المؤلفين ١٠: ١٤٤.','2','550',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (980,'وأدائها، وكان عارفا بالتفسير، حمل عنه أهل سبتة (١).

المخللاتي [١١٢٤ - ١٢٠٧ هـ / ١٧١٢ - ١٧٩٢ م]

<span data-type="title" id=toc-1348>محمد بن عبد الرحيم بن علي بن عبد الله الرحيباني الأصل، ثم الدمشقي، المعروف بالمخللاتي:</span>
فرضي، عالم بالميقات. من أهل دمشق مولدا ووفاة. يقال أنه صاحب "تفسير المخللاتي، وهو تفسير مختصر غريب الأسلوب" (٢).

البرزنجي [١٠٤٠ - ١١٠٣ هـ / ١٦٣٠ - ١٦٩١ م]

<span data-type="title" id=toc-1349>محمد بن عبد الرسول بن عبد السيد ابن عبد الرسول الحسني البرزنجي:</span>
فقيه شافعي، أصولي، له علم بالتفسير والأدب. برزنجي الأصل. ولد وتعلم بشهرزور، ورحل إلى همذان وبغداد ودمشق والقسطنطينية والحجاز ومصر،
_________
(١) نكت الهميان ٢٥٤ وطبقات المفسرين ٢: ١٨٥ والدرر الكامنة ٤: ١٢٨ وغاية النهاية ٢: ١٧١.
(٢) منتخبات التواريخ لدشق ٦٨٦ وروض البشر ٢٣٤ والأعلام ٧: ٧٣ والتيمورية ١: ١٨٦ و ٣: ٢٧٤.','2','550',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (981,'وأخذ عن علمائها. ثم استقر بالمدينة ودرس وتوفي بها. من آثاره "أنهار السلسبيل" في شرح أنوار التنزيل، في التفسير، للبيضاوي، و "الضاوي على صبح فاتحة البيضاوي" و "غاية الاهتمام بتفسير قوله تعالى: {ولو أنما في الأرض من شجرة أقلام ...} (سورة لقمان، الآية ٢٧) (١).

المناوي [٩٥٢ - ١٠٣١ هـ / ١٥٤٥ - ١٦٢٢ م]

<span data-type="title" id=toc-1350>محمد عبد الرؤوف بن تاج العارفين ابن علي بن زين العابدين الحدادي ثم المناوي القاهري، زين الدين:</span>
فقيه شافعي، من كبار العلماء بالدين والفنون وصاحب كتاب "فيض القدير في شرح الجامع الصغير" أخذ الفقه عن شمس الدين الرملي وغيره، والحديث والأدب وغيرها عن مشايخ عصره. وكان قليل الطعام كثير السهر، فمرض وضعفت أطرافه، فأملى تآليفه على ولده تاج الدين محمد. عاش في القاهرة وتوفي بها. له نحو ٨٠ مصنفا، منها "تفسير سورة الفاتحة،
_________
(١) سلك الدرر ٤: ٦٥ وتاريخ السلمانية ٢٧٧ وهدية العارفين ٢: ٣٠٣ والأعلام ٧: ٧٥ ومعجم المؤلفين ١٠: ١٦٥ ومشاهير الكرد ٢: ١٢٨.','2','551',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (982,'وبعض سورة البقرة" (١).

الليثي [... - بعد ١٣٣٢ هـ / ... - بعد ١٩١٤ م]

<span data-type="title" id=toc-1351>محمد عبد السلام الليثي:</span>
مفسر، له "القول المبين في تفسير بعض الآيات الواردة في قصص الأنبياء والمرسلين" (٢).

بوستة [... - بعد ١٣٤٦ هـ / ... - ١٩٢٧ م]

<span data-type="title" id=toc-1352>محمد بن عبد السلام بن أحمد بوستة:</span>
لغوي، مفسر، من أهل مراكش. له "تفسير غريب القرآن" مخطوط، في خزانة الرباط، قال الزركلي: "ولعله بخطه" (٣).

معيد زاده [٩٢٢ - ٩٨٣ هـ / ١٥١٦ - ١٥٧٥ م]

<span data-type="title" id=toc-1353>محمد بن عبد العزيز، المعروف بمعيد زاده:</span>
فقيه حنفي، مفسر، م! ث! ارك في
_________
(١) معجم المطبوعات ١٧٩٨ وخطط مبارك ١٦: ٥٠ وهدية العارفين ١: ٥١٠ وخلاصة الأثر ٢: ٤١٢ وعقود الجوهر ٢٥٧ والبدر الطالع ١: ٣٥٧ والأعلام ٧: ٧٥ ومعجم المؤلفين ٥: ٢٢٠ و ١٠: ١٦٦ وهو في بعض المصادر عبد الرؤوف بن علي.
(٢) التيمورية ٣: ٢٤٤ ومعجم المؤلفين ١٠: ١٧٠
(٣) من تعليق على مخطوطة التفسير في خزانة الرباط (٢١١٤ ك) والأعلام ٦: ٣٠٧.','2','551',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (983,'كثير من العلوم. ولد بمرعش، وتعلم بها وبالقسطنطينية، ودرس بها وبأدرنة وبروسة، ثم نقل إلى مدرسة السلطان سليمان بدمشق، وأفتى بها. وعين لقضاء بيت المقدس، فتوفي قبل الجلوس في مجلس القضاء. قال صاحب "ذيل الشقائق": وهو أول قاض بها من زمرة الموالي، وكانت له اليد الطولى في العلوم الأدبية، والقدم الراسخ في الفنون العربية، مع المشاركة التامة في سائر العلوم المتداولة، وله "تعليقات" على بعض المواضع من التفسير" (١).

الخولي [١٣١٠ - ١٣٤٩ هـ / ١٨٩٢ - ١٩٣١ م]

<span data-type="title" id=toc-1354>محمد بن عبد العزيز بن علي الشاذلي الخولي:</span>
واعظ، مفسر، محدث، فقيه، ولد في "الحامول" من أعمال المنوفية بمصر، وتخرج بمدرسة القضاء الشرعي بالقاهرة، وتوفي بها. من آثاره "تفسير سورة ق" (٢).
_________
(١) ذيل الشقائق النعمانية ٤٨٣ وشذرات الذهب
(٢) الأعلام الشرقية ٢: ١٦٩ والأعلام ٧: ٧٩ ومعجم المطبوعات ٨٥١ ومعجم المؤلفين ٧: ٧٩ وحريدة الأهرام ١٥/ ١/ ١٩٣١.','2','552',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (984,'ابن عتيق [١٠٢٠ - ١٠٨٨ هـ / ١٦١١ - ١٦٧٧ م]

<span data-type="title" id=toc-1355>محمد بن عبد العظيم الصديقي، الشهير بابن عتيق:</span>
نحوي، له اشتغال في التفسير، ولد بحمص (بسورية) وانتقل إلى القاهرة في أيام شبابه وأخذ عن البرهان اللقاني وغيره من العلماء. قال المحبي: "فبرع في سائر الفنون وفاق أقرانه وتقوى على حل المشكلات العلمية ... " مات بالقاهرة. من آثاره "نتيجة الفكر في إعراب أوائل السور" مخطوط، في دار الكتب المصرية. و "نخبة البيان فيما وقع من التكرير في القرآن" (١)

الأردبيلي [٩٨٠ - ١٠٣٦ هـ / ١٥٧٢ - ١٦٢٧ م]

<span data-type="title" id=toc-1356>محمد بن عبد الغني بن مير باد شاه الأردبيلي، جمال الدين، المعروف بغني زاده:</span>
قاض، مفسر، تركي مستعرب، ولي قضاء القسطنطينية وقضاء العسكر،
_________
(١) خلاصة الأثر ٤: ٣٤ وهو فيه: محمد بن عتيق. وهدية العارفين ٢: ٢٩٦ وإيضاح المكنون ٢: ٦٣٠ وفهرس الخديوية ١: ٢١٨ وفهرس دار الكتب ١: ٦٤ ومعجم المؤلفين ١٠: ١٧٧ و ٢٨٠.','2','552',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (985,'ولشعراء عصره فيه مدائح كثيرة. من آثاره "حاشية" على أنوار التنزيل، للبيضاوي، في التفسير. قال المحبي: "وكلامه فيها يدل على تحقيق عظيم" (١).

البيطار [١٢٦٥ - ١٣٢٨ هـ / ١٨٤٩ - ١٩١٠ م]

<span data-type="title" id=toc-1357>محمد بن عبد الغني بن حسن بن إبراهيم البيطار، بهاء الدين:</span>
ناثر، ناظم، له علم بالتصوف، من أهل دمشق مولدا ووفاة. قرأ على والده وعميه محمد البيطار وعبد الرزاق البيطار. وأكثر من مطالعة كتب الصوفية. وهو والد الأستاذ محمد بهجت البيطار. من آثاره "الواردات الإلهية" في التفسير، على الطريقة الصوفية (٢).

النابلسي [٧٢٧ - ٧٩٧ هـ / ١٣٢٧ - ١٣٩٥ م]

<span data-type="title" id=toc-1358>محمد بن عبدد القادر بن عثمان بن عبد الرحمن بن عبد المنعم، الجعفري النابلسي، أبو عبد الله، شمس الدين:</span>
فقيه حنبلي، كان عالم أهل
_________
(١) خلاصة الأثر ٤: ٩ وهدية العارفين ٢: ٢٧٥
(٢) حلية البشر ١: ٣٨٠ ومعجم المؤلفين ١٠: ١٧٩ ومعجم المطبوعات ٦١٩.','2','553',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (986,'نابلس في وقته، وبها نشأ وتعلم. رحل إلى دمشق وصحب ابن قيم الجوزية وتفقه عليه. يقال له "الجنة" لكثرة ما فيه من الفضائل. حدث وأفتى. قال ابن حجر: "انتفع به الناس، وكانت له عناية بالحديث، وفيه يقظة". مات بنابلس. من كتبه "تفسير قسم من القرآن الكريم" (١).

ابن إسرائيل [... - ١٠١٥ هـ / ... - ١٦٠٦ م]

<span data-type="title" id=toc-1359>محمد بن عبد القادر بن أحمد بن أبي بكر بن إسرائيل بن إسماعيل اليمني:</span>
لغوي، له نظم، من أهل حضرموت. له تصانيف، منها "شذور الإبريز في لغات الكتاب العزيز" في تفسير غريب القرآن" (٢).

الشهرستاني [٤٧٩ - ٥٤٨ هـ / ١٠٨٦ - ١١٥٣ م]

<span data-type="title" id=toc-1360>محمد بن عبد الكريم بن أحمد الشهرستاني، أبو الفتح:</span>
فيلسوف،
_________
(١) إنباء الغمر ١: ٥٠٣ والشذرات ٦: ٣٤٩ والدرر الكامنة ٤: ١٣٨ والأعلام ٧: ٨١.
(٢) خلاصة الأثر ٤: ١١ وتاريخ الشعراء الحضرميين ١: ٢١١ وهدية العارفين ٢: ٢٦٦ وإيضاح المكنون ٢: ٤٢ والأعلام ٧: ٨٩ ومعجم المؤلفين ١٠: ١٨٠.','2','553',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (987,'محقق في الفقه والكلام والأديان والملل والنحل، يلقب بالأفضل. مولده ووفاته بشهرستان - بين نيسابور وخوارزم -. تفقه على أبي نصر القشيري وغيره، وقرأ علم الكلام على أبي القاسم الأنصاري وتفرد به. ودخل خوارزم وسكنها مدة، ثم تحول إلى خراسان، وسمع بنيسابور. وحج سنة ٥١٠ هـ، وعاد في نفس السنة إلى بغداد وأقام بها ثلاث سنين ووعظ بها. و "كان حسن المحاورة والوعظ، وكانت معارفه في علم الكلام والفرق الكلامية والدينية والفلسفية من الشمول بحيث تعد مصنفاته فيها من المصادر التي لا يستغنى عنها من تلك المعارف". وقال ياقوت الحموي في وصفه: "الفيلسوف المتكلم، صاحب التصانيف، كان وافر الفضل، كامل العقل، ولولا تخبطه في الاعتقاد ومبالغته في نصرة مذاهب الفلاسفة والذب عنهم لكان هو الإمام". من آثاره "مفاتيح الأسرار ومصابيح الأبرار" في التفسير، مخطوط، منه نسخة كتبت سنة ٦٦٧ هـ في خزانة مجلس الشورى الوطني بطهران. و "تفسير سورة يوسف" بأسلوب فلسفي (١)
_________
(١) معجم البلدان (مات: شهرستان) والوافي ٣: =','2','554',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (988,'القزويني [... - ٥٨٠ هـ / ... - ١١٨٤ م]

<span data-type="title" id=toc-1361>محمد بن عبد الكريم بن الفضل القزويني الرافعي، أبو الفضل:</span>
محدث، مفسر، من فقهاء الشافعية. تفقه بقزوين وبغداد ونيسابور. وهو والد الإمام الرافعي. قال السبكي: "له تصانيف في الحديث والفقه والتفسير" (١).

المغيلي [... - ٩٠٩ هـ / ... - ١٥٠٣ م]

<span data-type="title" id=toc-1362>محمد بن عبد الكريم بن محمد المغيلي التلمساني، أبو عبد الله:</span>
فقيه مالكي، مفسر، متكلم، له نظم. نسبته إلى
_________
= ٢٧٨ وتذكرة الحفاظ ١١١٣ وشذرات الذهب ٤: ١٤٩ ولسان الميزان ٥: ٢٦٣ وطبقات الشافعية للسبكي ٤: ٧٨ والعبر ٤: ١٣٢ وتاريخ حكماء الإسلام ١٤١ ومرآة الجنان ٣: ٢٨٩ وروضات الجنات ١٨٦ والمختصر في أخبار البشر ٣: ٢٩ وفيات الأعيان ٤: ٢٧٣ ومفتاح السعادة ١: ٢٦٤ ومختصر دول الإسلام ٢: ٤٥ والأعلام ٦: ٢١٥ ومعجم المؤلفين ١٠: ١٨٧ ومجلة معهد المخطوطات ٣: ٣١ وتاريخ آداب اللغة العربية ٣: والموسوعة العربية الميسرة ١٠٩٨.
(١) طبقات المفسرين للداودي ٢: ١٨٥ وطبقات الشافعية للسبكي ٦: ١٣١ وطبقات الشافعية لابن هداية الله الحسيني ٢١١ والوافي بالوفيات ٣: ٢٨٠ ومختصر ذيل تاريخ بغداد للذهبي ١: ٧٤ ومعجم المؤلفين ١٠: ١٩٠.','2','554',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (989,'مغيلة، قبيلة من البربر. نشأ بتلمسان. وقع نزاع بينه وبين الإمام جلال الدين السيوطي في علم المنطق. وناوأ اليهود في "توات" بقرب تلمسان، وكانت له معهم مشاحنات أدت إلى قتالهم وهدم كنائسهم. زار بلاد السودان واجتمع بسلطان "كنو" وكتب له رسالة في أمور السلطنة، ومنها ارتحل إلى بلاد التكرور. وكان في سفره، ينشر أحكام الشرع ويحض على الأمر بالمعروف والنهي عن المنكر. توفي في توات. من تصانيفه "البدر المنير في علوم التفسير" (١).

المرعشي [١١٩٨ - ١٢٨٠ - هـ / ١٧٨٤ - ١٨٦٣ م]

<span data-type="title" id=toc-1363>محمد بن عبد الكريم الحسيني المرعشي:</span>
فقيه إمامي، أديب، ناظم، مفسر. توفي بأصفهان. من كتبه "تفسير القرآن" (٢).
_________
(١) معجم أعلام الجزائر ٣٠٨ ونيل الابتهاج ٣٣٠ والبستان ٢٥٣ وتعريف الخلف ١: ١٦٦ وفهرس الفهارس ٢: ٩٢ وهدية العارفين ٢: ٢٢٤ وكشف الظنون ٨٤٥ ومعجم المؤلفين ١٠: ١٩١ والأعلام ٧: ٨٤ ودوحة الناشر وفيه: توفي في أول العشرة الثالثة من القرن العاشر، ومثله في طبقات الحضيكي. وشجرة النور ٢٧٤ وتاريخ نيجيريا ١٢٥ ومصباح الأرواح ٢٢.
(٢) أعيان الشيعة ٤٥: ٢٧٣.','2','555',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (990,'بخاري زاده [... - ٩٨٦ هـ / ... - ١٥٧٨ م]

<span data-type="title" id=toc-1364>محمد بن عبد اللطيف، المعروف ببخاري زاده:</span>
قاض، فقيه، مفسر، تولى قضاء طرابلس الشام وتوفي وهو على قضائها. من تصانيفه "حواش" على تفسير البيضاوي (١).

شقير [١٠١٨ - ١٠٧٢ هـ / ١٦٠٩ - ١٦٦٢ م]

<span data-type="title" id=toc-1365>محمد بن عبد اللطيف بن محمد بن أبي بكر الخلوتي الدمشقي، المعروف بشقير:</span>
فقيه حنفي مفسر، ناظم، من أهل دمشق، وبها نشأ وتعلم. وسافر إلى القدس ومصر والحجاز. مات بدمشق. له "رسائل في التفسير" (٢).

ابن أبي الثلج [... - ٢٥٧ هـ / ... - ٨٧١ م]

<span data-type="title" id=toc-1366>محمد بن عبد الله بن إسماعيل بن أبي الثلج البغدادي:</span>
محدث، مفسر، أصله من الري، صاحب أحمد بن حنبل، روى عن يزيد بن هارون
_________
(١) العقد المنتظم ٥٣٣ ومعجم المؤلفين ١٠: ١٩٢.
(٢) خلاصة الأثر ٤: ١٥.','2','555',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (991,'وغيره، وعنه البخاري والترمذي وابن أبي حاتم وآخرون. له "تفسير القرآن" (١).

ابن عبد الحكم [١٨٢ - ٢٦٨ هـ / ٧٩٨ - ٨٨٢ م]

<span data-type="title" id=toc-1367>محمد بن عبد الله بن عبد الحكم، المصري، أبو عبد الله:</span>
فقيه مصر في عصره، انتهت إليه الرياسة في العلم فيها. كان مالكي المذهب، ولازم الإمام الشافعي، ثم رجع إلى مذهب مالك. وحمل في فتنة القول بخلق القرآن إلى ابن أبي دواد في بغداد، فلم يجبه، فرده إلى مصر. وتوفي بها. من كتبه "أحكام القرآن" (٢).
_________
(١) طبقات المفسرين ٢: ١٥٦ وخلاصة تهذيب الكمال ٢٩٣.
(٢) طبقات المفسرين للداودي ٢: ١٧٤ وطبقات الفقهاء لشيرازي ٩٩ وطبقات الشافعية للسبكي ٢: ٦٧ وطبقات الشافعية للعبادي ٢٠ وطبقات الشافية لابن هداية الله الحسيني. والوافي ٣: ٣٣٨ وميزان الاعتدال ٣: ٦١١ والعبر للذهبي ٢: ٣٨ وتذكرة الحفاظ ٥٤٦ والانتقاء ١١٣ وحسن المحاضرة ١: ١٢٤ وغاية النهاية ٢: ١٧٩ والديباج المذهب ٢٣١ وشذرات الذهب ٢: ١٥٤ ومفتاح السعادة ٢: ٢٩٥ والنجوم الزاهرة ٣: ٤٤ ومرآة الجنان ٢: ١٨١ ووفيات الأعيان ٤: ١٩٣ وهدية العارفين ٢: ١٨ والأعلام ٧: ٩٤ ومعجم المؤلفين ١٠: ٢٢٢ والفهرست لابن النديم ٢١١ وتهذيب التهذيب ٩: ٢٦٠ وخلاصة تهذيب الكمال ٢٩٤.','2','556',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (992,'مطين [٢٠٢ - ٢٩٧ هـ / ٨١٧ - ٩١٠ م]

<span data-type="title" id=toc-1368>محمد بن عبد الله بن سليمان الحضرمي الكوفي، أبو جعفر، المعروف بمطين:</span>
من حفاظ الحديث، كان محدث الكوفة في عصره. حط عليه محمد بن عثمان بن أبي شيبة، وحط هو على ابن أبي شيبة، وآل أمرهما إلى القطيعة. قال ابن حجر: "مطين وثقه الناس وما أصغوا إلى ابن أبي شيبة". لقب مطين لأنه كان وهو صغير يلعب مع الصبيان في الماء فيطينون ظهره" من كتبه "تفسير القرآن" (١).

البردعي [... - حيا ٣٤٠ هـ / ... - حيا ٩٥١ م]

<span data-type="title" id=toc-1369>محمد بن عبد الله البردعي، أبو بكر:</span>
فقيه معتزلي، قال ابن النديم في الفهرست: "رأيته في سنة ٣٤٠ وكان بي آنسا، يظهر مذهب الاعتزال، وكان خارجيا وأحد فقهاء الشراة". وذكر له
_________
(١) طبقات المفسرين ٢: ١٦٠ والوافي ٣: ٣٤٥ وميزان الاعتدال ٣: ٦٠٧ والرسالة المستطرفة ٦٣ ولسان الميزان ٥: ٢٣٣ وتذكرة الحفاظ ٢: ٦٦٢ وهدية العارفين ٢: ٢٣ والفهرست ٢٣٢ وفيه وفاته: ٢٩٨.','2','556',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (993,'"الناسخ والمنسوخ في القرآن" (١).

السعدي [... - حيا سنة ٣٥١ هـ / ... - حيا سنه ٩٦٢ م]

<span data-type="title" id=toc-1370>محمد بن عبد الله بن سليمان، أبو سليمان السعدي:</span>
مفسر، من علماء الشافعية. قال السيوطي: "سمع ببغداد أبا عبد الله المحاملي (ت ٣٣٠ هـ) ودعلجا (ت ٣٥١ هـ) وكان شافعيا أشعريا، حسن التكلم في التفسير، صنف كتبا فيه، منها "مجتبى التفسير" جمع فيه الصغير والكبير والقليل والكثير مما أمكنه، وكتاب "الجامع الصغير في مختصر التفسير" وكتاب "المهذب في التفسير" (٢).

ابن أشتة [... - ٣٦٠ هـ / ... - ٩٧١ م]

<span data-type="title" id=toc-1371>محمد بن عبد الله بن محمد بن أشتة اللوذري، أبو بكر الأصبهاني:</span>
عالم بالقراءات والعربية، من أهل أصبهان.
_________
(١) الفهرست لابن النديم ٢٣٧ وهدية العارفين ٢: ٤٠ وطبقات المفسرين ٢: ١٧٤ والأعلام ٧: ٩٧ والنسخ في القرآن الكريم ١: ٣٢٦.
(٢) طبقات المفسرين للسيوطي وطبقات المفسرين للداودي ٢: ١٦٠.','2','557',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (994,'سكن مصر وتوفي بها. قال ابن الجزري: "أستاذ كبير، وإمام شهير، ونحوي محقق ثقة". من كتبه "رياضة الألسنة" في إعراب القرآن ومعانيه (١).

ابن صبر [... - ٣٨٠ هـ / ... - ٩٩١ م]

<span data-type="title" id=toc-1372>محمد بن عبد الله بن جعفر بن محمد ابن الحسن بن الفهم المعروف بابن صبر، أبو بكر الحنفي</span>
(سبقت ترجمته باسم محمد بن عبد الرحمن، وأشرت هنالك إلى مصادر الترجمة. وحيث أنه ذكر في مصادر أخرى باسم محمد بن عبد الله، فقد ذكرته هنا للتنبيه على أنه رجل واحد، اختلف المؤرخون في اسم أبيه. وجده الخ .. (٢).

الهروي [... - ٣٨١ هـ / ... - ٩٩١ م]

<span data-type="title" id=toc-1373>محمد بن عبد الله بن عمرو، أبو جعفر الهروي، الفقيه</span>،
صاحب
_________
(١) غاية النهاية ٢: ١٨٤ وبغية الوعاة ١: ١٤٢ وطبقات المفسرين ٢: ١٥٧ والأعلام ٧: ٩٧ وهدية العارفين ٢: ٤٧.
(٢) بالإضافة إلى المصادر السابقة في حاشية ترجمته باسم محمد بن عبد الرحمن، تراجع المصادر التالية: طبقات المفسرين للداودي ٢: ١٥٨ واللباب ٢: ٢٣٤ وفيه نسبتة إلى جده "صبر"، وانظر معجم المؤلفين ١٠: ١٣٦ و ٣٠٧.','2','557',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (995,'"التفسير". كذا ذكره السيوطي ولم يزد، ولم أعثر على ترجمة وافية له فيما بين يدي الساعة من كتب الرجال (١).

ابن أبي زمنين [٣٢٤ - ٣٩٩ هـ / ٩٣٦ - ١٠٠٩ م]

<span data-type="title" id=toc-1374>محمد بن عبد الله بن عيسى بن محمد المري، أبو عبد الله، المعروف بابن أبي زمنين:</span>
فقيه مالكي، مفسر، أصولي، محدث، من الوعاظ الأدباء، من أهل البيرة، سكن قرطبة، وسمع منه وهب بن مسرة، وابن الشاط، وغيرها. ثم عاد إلى البيرة فتوفي بها. سئل: لما قيل لكم بنو أبي زمنين؟ فقال: لا أدري. له تصانيف، منها "تفسير القرآن" اختصره من "تفسير يحيى بن سلام" مخطوط في خزانة القرويين (الرقم ٤٠/ ٣٤) (٢).

الخطيب الإسكافي [... - ٤٢٠ هـ / ... - ١٠٢٩ م]

<span data-type="title" id=toc-1375>محمد بن عبد الله الخطيب الإسكافي، أبو عبد الله:</span>
عالم بالأدب
_________
(١) طبقات المفسرين ٣٧ وعنه الداودي ٢: ١٦١
(٢) طبقات المفسرين للسيوطي ٣٤ وطبقات المفسرين للداودي ٢: ١٦١ والوافي ٣: ٣٢١ وبغية الملتمس ٧٧ والعبر ٣: ٧١ وتذكرة الحفاظ ٣: ١٠٢٩ والشذرات ٣: ١٥٦ وجذوة المقبس ٥٣ والأعلام ٧: ١٠١ والديباج ٢٦٩.','2','558',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (996,'واللغة، من أهل أصبهان. كان إسكافا، وحبب إليه العلم، فأخذ عن مشيخة وقته في بلده، وصار من الأعلام المشهورين. وكان معاصرا للصاحب بن عباد ومن أصحابه. وولي الخطابة بالري، فعرف بالخطيب. من كتبه "درة التنزيل وغرة التأويل" تكلم فيه على الآيات المتكررة بالكلمات المتفقة والمختلفة التي يقصد الملحدون التطرق منها إلى عيبها وأجاب عنها، طبع في القاهرة، وبيروت (١٩٧٣ م) (١).

أبو بكر بن العربي [٤٦٨ - ٥٤٣ هـ / ١٠٧٦ - ١١٤٨ م]

<span data-type="title" id=toc-1376>محمد بن عبد الله بن محمد بن عبد الله ابن أحمد المعافري الأندلسي الإشبيلي، أبو بكر ابن العربي:</span>
قاض، حافظ للحديث، من كبار فقهاء المالكية، بلغ رتبة الإجتهاد في علوم الدين، وبرع في الأدب. ولد في إشبيلية، وبها نشأ وأخذ عن علمائها. ورحل إلى المشرق (سنة
_________
(١) الوافي ٣: ٣٣٧ وكشف الظنون ٧٣٩ ومعجم الأدباء ١٨: ٢١٤ وبغية الوعاة ١: ١٤٩ وهدية العارفين ٢: ٦٤ ويتيمة الدهر ٢: ٤٥ والأعلام ٧: ١٠٢ ومعجم المؤلفين ١٠: ٢١١ ومقدمة درة التنزيل. طبعة بيروت بقلم عادل نويهض.','2','558',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (997,'٤٨٥) فأخذ عن علماء مصر والشام وبغداد والحجاز. وعاد، (سنه ٤٩٥ هـ) فتصدر للتدريس، ورحل إليه للسماع والأخذ عنه. وولى قضاء إشبيلية، (سنة ٥٣٨ هـ) ثم صرف عنه، فأقبل على نشر العلم والتأليف. مات بقرب فاس وحمل إليها ودفن بها. قال ابن بشكوال: "ختام علماء الأندلس وآخر أئمتها وحفاظها". من آثاره "أحكام القرآن" (طبع) مجلدان، و "الناسخ والمنسوخ" مخطوط في خزانة القروين بفاس، و "قانون التأويل" مخطوط، جزآن منه، في التفسير، و "أنوار الفجر المنير" في التفسير، ألفه في عشرين سنة. قيل إنه في ثلاثين مجلدا، وقيل: في ثمانين، كانت في خزانة أمير المسلمين أبي عنان بمراكش (١).
_________
(١) مقدمة كتاب "العواصم من القواصم" بغلم محب الدين الخطيب، وطبقات المفسرين للسيوطي ٣٤ وطبقات المفسرين للداودي ٢: ١٦٢ ونفح الطيب ٢: ٢٥ والديباج المذهب ٢٨١ والشذرات ٤: ١٤١ وبغية الملتمس ٨٢ ومرآة الجنان ٣: ٢٧٩ وتذكرة الحفاظ ١٢٩٤ والبداية والنهايه ١٢: ٢٢٨ والصلة ٢: ٥٥٨ ووفيات الأعيان ٤: ٢٩٦ وقضاة الأندلس ١٠٥ والوافي ٣: ٣٣٠ وأزهار الرياض ٣: ٨٦ وجذوة الاقتباس ١٦٠ وسلوة الأنفاس ٣: ١٩٨ والمغرب في حلى المغرب ١: ٢٤٩ والأعلام ٧: ١٠٦.','2','559',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (998,'ابن ظفر [٤٩٧ - ٥٦٥ هـ / ١١٠٤ - ١١٧٠ م]

<span data-type="title" id=toc-1377>محمد بن عبد الله أبي محمد بن محمد ابن ظفر الصقلي المكي، أبو عبد الله، حجة الدين:</span>
أديب، مفسر، نحوي، لغوي، رحالة، ولد في صقلية، ونشأ بمكة، وتنقل في البلاد، فدخل مصر وإفريقية والأندلس، وأقام بالمهدية مدة، ثم انتقل إلى صقلية، فمصر، ومنها إلى حلب ثم حماه، فاستوطنها وتوفي بها. من تصانيفه "ينبوع الحياة" في التفسير، مخطوط، ١٢ مجلدا، وقيل: له تفسير كبير آخر (١).
_________
(١) بغية الوعاة ١: ١٤٢ وهو فيه: محمد بن عبد الله بن محمد، ومولده بمكة. وعنه طبقات المفسرين للداودي ٢: ١٦٧ ثم ترجم له (ص ٢٤٥) نقلا عن المقفى وهو فيه: محمد بن محمد بن محمد بن ظفر، وكناه أبا هاشم وأبا عبد الله، ومولده بصقلية ومثله في المكتبة الصقلية ٦٦٥. ولسان الميزان ٥: ٣٧١ وهو فيه محمد بن محمد بن أبي محمد بن ظفر، ووفاته سنة ٥٩٨ أو ٥٦٧ على اختلاف الأقوال. وهدية العارفين ٢: ٩٦ والوافي ١: ١٤١ ومعجم الأدباء ١٩: ٤٨ ومفتاح السعادة ١: ٢٣٣ والمختصر في أخبار البشر ٣: ٥٢ وروضات الجنات ١٨٨ ووفيات الأعيان ٤: ٣٩٥ وخريدة القصر (قسم شعراء الشام) ٣: ٤٩ والعقد الثمين ٢: ٣٤٤. ومجلة المجمع العلمي العربي ٥: ١٣٤ والأعلام ٦: ٢٣٠ وهدية العارفين ٢: ٩٦.','2','559',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (999,'ابن ميمون [.. - ٥٦٧ هـ / .. - ١١٧٢ م]

<span data-type="title" id=toc-1378>محمد بن عبد الله بن ميمون بن إدريس بن محمد العبدري، أبو بكر:</span>
مفسر، مقرىء، نحوي، أديب، شاعر، من بلغاء الكتاب. أصله من قرطبة، قال ابن الأبار: "خرج منها في الفتنة فنزل مراكش وأقرأ بها العربية والآداب، وعرف مكانه، وتوفي بها (١).

الأنصاري [٥٧٤ - ٦٤٠ هـ / ١١٧٨ - ١٢٤٣ م]

<span data-type="title" id=toc-1379>محمد بن عبد الله بن محمد بن خلف، أبو عبد الله، ويقال أبو بكر، الأنصاري:</span>
مقرئ، نحوي، مفسر، واعظ، من أهل بلنسية، أقرأ بها وولي الخطابة بجامعها، وأقام مدة بشاطبة، وتوفي بأوريولة (٢).
_________
(١) بغية الوعاة ١: ١٤٧ وطبقات المفسرين للداودي ٢: ١٧٢ والديباج ٣٠٢ والمطرب من أشعار أهل المغرب ١٩٨ وهدية العارفين ٢: ٩٦ والمغرب في حل المغرب ١: ١١١ والتكملة لابن الأبار ٢: ٥١١ ورايات المبرزين ٤٦ ومعجم المؤلفين ١٠: ٢٥٠ وكشف الظنون ١٧٨٨.
(٢) غاية النهاية ٢: ١٧٨ وطبقات القراء للذهبي ٢: ٥١٤ وطبقات المفسرين للداودي ١٥٩: ٢ =','2','560',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1000,'المرسي [٥٧٠ - ٦٥٥ هـ / ١١٧٤ - ١٢٥٧ م]

<span data-type="title" id=toc-1380>محمد بن عبد الله بن محمد بن أبي الفضل السلمي المرسي، أبو عبد الله، شرف الدين:</span>
عالم بالتفسير والحديث والفقه والأصول والنحو، ضرير، ولد بمرسية بالأندلس، ورحل (سنة ٦٠٧ هـ) فدخل مصر والحجاز والعراق، وأقام ببغداد، يقرأ ويسمع بالنظامية، ثم رحل إلى خراسان ووصل إلى مرو الشاهجان، وسمع بنيسابور وهراة. وعاد إلى بغداد، وأقام مدة في الموصل وحلب ودمشق. وحج وعاد إلى دمشق. ثم سكن المدينة، ومنها انتقل إلى مصر سنة ٦٢٤ هـ. وخرج منها يريد الشام، فمات بين الزعقة والعريش. ودفن بتل الزعقة. من كتبه "ري الظمآن في تفسير القرآن" يزيد على عشرين جزءا، و "التفسير الأوسط" عشرة أجزاء، و "التفسير الصغير" ثلاثة (١).
_________
= والتكملة لابن الأبار ٢: ٦٥١ الترجمة رقم ١٦٧١ والحلل السندسية للأمير شكيب أرسلان عن تكملة ابن الأبار.
(١) طبقات المفسرين للداودي ٢: ١٦٨ والوافي ٣: ٣٥٤ ومرآة الجنان ٤: ١٣٧ والشذرات ٥: ٢٦٩ وهدية العارفين ٢: ١٢٥ ونفح الطيب ٢: ٢٤١ =','2','560',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1001,'الأرزكياني [... - ٧٨٤ هـ / ... - ١٣٨٢ م]

<span data-type="title" id=toc-1381>محمد بن عبد الله الأرزكياني، شرف الدين:</span>
محدث، مفسر، قال ابن حجر: "أحد فضلاء العجم، انتفع به أهل تلك البلاد، وقدم الشام قبل الثمانين (٧٨٠ هـ) .. الخ". له "شرح الكشاف". والأرزكياني نسبة إلى رجل من بخارى اسمه أرزكيان أسلم على يد علي بن أبي طالب (١).

ملا مسكين [... - بعد ٨١١ هـ / ... - بعد ١٤٠٨ م]

<span data-type="title" id=toc-1382>محمد بن عبد الله الهروي، معين الدين، المعروف بملا مسكين:</span>
فقيه حنفي، مفسر، من أهل هراة. سكن
_________
= ومعجم الأدباء ١٨: ٢٠٩ وإيضاح المكنون ١: ٦٠٤ وبغية الوعاة ١: ١٤٤ والعقد الثمين ٢: ٨١ وذيل مرآة الزمان ١: ٧٦ والنجوم الزاهرة ٧: ٥٩ والسبكي ٨: ٦٩ وطبقات المفسرين للسيوطي ٣٥ وبروكلمان ١: ٣١٢ وكشف الظنون ٤٥٨ والأعلام ٧: ١١٠ ومعجم المؤلفين ١٠: ٢٤٤ والإتقان ١: ٤٩ ومدخل إلى علوم القرآن والتفسير ١٧٦.
(١) إنباء الغمر ١: ٧٨٤ والشذرات ٦: ٢٨٤ وهدية العارفين ٢: ١٧١ وإيضاح المكنون ٢: ٤٨٤ وهو فيه الأرزنجاني، ومثله في معجم المؤلفين ١٠: ١٩٩ وهو ينقل عن الإيضاح.','2','561',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1002,'سمرقند، وفيها ألف كتابه "شرح كنز الدقائق" سنة ٨١١ هـ. من آثاره "بحر الدرر" في تفسير القرآن (١).

ابن ناصر الدين [٧٧٧ - ٨٤٢ هـ / ١٣٧٥ - ١٤٣٨ م]

<span data-type="title" id=toc-1383>محمد بن عبد الله (أبي بكر) بن محمد ابن أحمد بن مجاهد بن يوسف القيسي الدمشقي، شمس الدين، الشهير بابن ناصر الدين:</span>
حافظ للحديث، من فقهاء الشافعية. أصله من حماة، ولد في دمشق، وأخذ عن شيوخها والقادمين إليها، ورحل فسمع بحلب وبعلبك ومكة والمدينة ومصر. وصار حافظ الشام غير منازع. وولي مشيخة دار الحديث الأشرفية (سنة ٨٣٧ هـ). قتل شهيدا في إحدى قرى دمشق. قال المقريزي: ولم يخلف في الشام بعده مثله". من كتبه "مختصر إعراب القرآن للسفاقسي" مخطوط، النصف الثاني منه، في المكتبة الظاهرية بدمشق (٢).
_________
(١) كشف الظنون ٢٢٤ ومعجم المطبوعات ١٧٩٥
(٢) السلوك ٤/ ٣: ١٢٤٨ والضوء اللامع ٨: ١٠٣ البدر الطالع ٢: ١٩٨ والشذرات ٧: ٢٤٣ وهدية العارفين ٢: ١٩٣ والأعلام ٧: ١١٥ ومعجم المؤلفين ١٠: ٢٣٦.','2','561',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1003,'المخزومي [٧٩٣ - ٨٨٥ هـ / ١٣٩١ - ١٤٨٠ م]

<span data-type="title" id=toc-1384>محمد بن عبد الله بن المبارك بن محمد المخزومي الرفاعي الحسيني، سراج الدين:</span>
شيخ الإسلام في عصره، ولد بواسط، في العراق، ورحل إلى الشام ومصر، وسكن آخر عمره بغداد وتوفي بها. وإليه تنسب محلة الشيخ سراج الدين من محالها. له "البيان في تفسير القرآن" (١).

الدهلوي [... - ٩٧٠ هـ / ... - ١٥٦٣ م]

<span data-type="title" id=toc-1385>محمد بن عبد الله بن حميد الدين الدهلوي الكولياري الهندي:</span>
مفسر، صوفي، فقيه حنفي. كان جده من سادات نيسابور، انتقل إلى الهند واستقر بها إلى أن مات. وبها ولد حفيده صاحب الترجمة. من تصانيفه "الكشف الجليل" في تفسير القرآن (٢).
_________
(١) ترجمته في آخر كتابه صحاح الأخبار المطبوع بمصر سنة ١٣٠٦ هـ. وانظر معجم المطبوعات ١٧١٨ ومجلة لغة العرب ٩: ١٨١ وهدية العارفين ٢: ٢١٠ وإيضاح المكنون ١: ٢٠٦ ومعجم المؤلفين ١٠: ٢٣٤ والأعلام ٧: ١١٦ والعقود الجوهرية ٢٢.
(٢) هدية العارفين ٢: ١٦٣ وهو فيه: محمد بن عبد الله حميد الدين ووفاته سنة ٧٦٤. و ٢٤٧ وهو فيه محمد بن عبد الله بن حمد الدين، ووفاته سنة ٩٧٠ هـ =','2','562',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1004,'اللارندي [... - ٩٩٤ هـ / ... - ١٥٨٥ م]

<span data-type="title" id=toc-1386>محمد بن عبد الله اللارندي، الرومي:</span>
فقيه حنفي، مفسر، اشتغل بالتدريس. له "تفسير القرآن" (١).

الأنسي [... - ١٠٤٣ هـ / ... - ١٦٣٣ م]

<span data-type="title" id=toc-1387>محمد بن عبد الله الغشم الأنسي اليماني الزيدي:</span>
فاضل، من أهل اليمن. من آثاره "تفسير القرآن" (٢).

زيتونة [١٠٨١ - ١١٣٨ هـ / ١٦٧٠ - ١٧٢٦ م]

<span data-type="title" id=toc-1388>محمد بن عبد الله، المعروف بزيتونة، المنستيري، أبو عبد الله:</span>
عالم تونس ومفتيها وفقيهها في عصره. ولد بالمنستير، وأصيب بفقد بصره في صغره، وتفقه بالقيروان وتونس، وحج وأخذ عن علماء مصر والحجاز. وعاد فاستقر بتونس، ودرس بالمرادية،
_________
= وإيضاح المكنون ٢: ٢٨٧ وهو فيه حميد الدين محمد بن عبد الله، وفيه وفاته سنة ٩٧٠ هـ.
(١) هدية العارفين ٢: ٢٥٨
(٢) هدية العارفين ٢: ٢٧٨ والبدر الطالع ٢: ١٩٩.','2','562',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1005,'وتخرج به كثير من علمائها. وتوفي بها. من تصانيفه حاشية على تفسير أبي السعود، جاوز بها نصفه، في ١٦ جزءا، سماها "مطالع السعود وفتح الودود على إرشاد شيخ الإسلام أبي السعود" (١).

لبي حافظ [... - ١١٩٥ هـ / ... - ١٧٨١ م]

<span data-type="title" id=toc-1389>محمد بن عبد الله الرومي الحنفي، المعروف بلبي حافظ:</span>
قاض، كان حافظا للكتب السلطانية، فعرف بالحافظ. من آثاره "لب التفاسير في معرفة أسباب النزول والتفسير" (٢).

الديربي [... - ١٢١٨ هـ / ... - ١٨٠٣ م]

<span data-type="title" id=toc-1390>محمد بن عبد الله بن أحمد الديربي الغنيمي الخلوتي:</span>
فاضل، ذكره البغدادي وقال: "توفي في حدود سنة ١٢١٨ هـ، له "نصرة أهل التعليم بشرح بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ" أوله
_________
(١) شجرة النور ٣٢٤ وذيل البشائر ١٣٢ وهدية العارفين ٢: ٣١٢ وإيضاح المكنون ٢: ٤٩٧
(٢) هدية العارفين ٢: ٣٤٣ وإيضاح المكنون ٢: ٤٠٠.','2','563',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1006,'"الحمد لله الذي جعل البسملة مشتملة على معاني الكتب السماوية ... الخ" (١).

القيصري [... - ١٢٥٠ هـ / ... - ١٨٣٤ م]

<span data-type="title" id=toc-1391>محمد بن عبد الله القيصري النقشبندي، الملقب بسعيد:</span>
فقيه حنفي، مفسر، صوفي، له تصانيف منها "تفسير سورة والعاديات" (٢).

النقشبندي [... - ١٣٠٩ هـ / ... - ١٨٩٢ م]

<span data-type="title" id=toc-1392>محمد بن عبد الله القسطنطيني الرومي النقشبندي، الملقب برائف:</span>
فقيه حنفي، مفسر، مشارك في بعض العلوم تركي الأصل، مستعرب. له تصانيف منها "تفسير القرآن" و "تفسير سورة يوسف" (٣).

ابن الزواك [١٢٤٠ - ١٣١١ هـ / ١٨٢٦ - ١٨٩٣ م]

<span data-type="title" id=toc-1393>محمد بن عبد الله بن أحمد الحسيني (الزواك الحديدي)</span>
_________
(١) إيضاح المكنون ٢: ٦٥١ وهدية العارفين ٢: ٣٥٤.
(٢) هدية العارفين ٢: ٣٦٧ ومعجم المؤلفين ١٠: ٢٣٢.
(٣) هدية العارفين ٢: ٣٩١ ومعجم المؤلفين ١٠: ٢٣٢.','2','563',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1007,'الزواك الحديدي: فقيه زيدي، عارف بالتفسير والتصوف، ولد ببندر الحديدة. اشتغل بالتدريس وولي الفتيا في حياة شيوخه. توفي بالزيدية، شمالي الحديدة. من آثاره "حاشية على تفسير الجلالين" (١).

الدهلوي [... - ١٣٢٢ هـ / ... - ١٩٠٤ م]

<span data-type="title" id=toc-1394>محمد بن عبد الله القادري الدهلوي، أبو منصور، ناصر الدين:</span>
فقيه حنفي، مفسر، من أهل دهلي بالهند، وولي الإفتاء بها. من آثاره "تبجيل التنزيل في تفسير القرآن الجليل" (٢).

دراز [... - ١٣٧٧ هـ / ... - ١٩٥٨ م]

<span data-type="title" id=toc-1395>محمد بن عبد الله دراز:</span>
فقيه، أديب، كان من هيأة كبار العلماء بالأزهر. ولد بمحلة دياي (دسوق) وتعلم بالإسكندرية، ودرس بها وبالأزهر، وأرسل في بعثة علمية إلى
_________
(١) أئمة اليمن سيرة المنصور: ١٤٣.
(٢) هدية العارفين ٢: ٣٩٨ وإيضاح المكنون ١: ٢٢٠.','2','564',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1008,'فرنسا، وحصل على شهادة الدكتوراه من السوربون (١٩٤٧). وعاد إلى مصر، فاشتغل بالتدريس بالأزهر ودار العلوم وجامعة القاهرة وكلية الشرطة. نال عضوية جماعة كبار العلماء (١٩٤٩) واختير عضوا باللجنة العليا لسياسة التعليم، وفي مجلس الإذاعة، وفي اللجنة الاستشارية الثقافية في الأزهر. وافته المنية بمدينة لاهور بباكستان حيث كان يمثل مصر في المؤتمر العلمي الإسلامي. من مؤلفاته "تفسير آيات الأحكام" بالاشتراك مع درويش (١).

محمد عبد المطلب [١٢٨٨ - ١٣٥٠ هـ / ١٨٧١ - ١٩٣١ م]

<span data-type="title" id=toc-1396>محمد بن عبد المطلب بن واصل بن بكر ابن بخيت، من أسرة أبي الخير، من جهينة:</span>
شاعر، أديب، خطيب، ولد في باصونة (من قرى جرجا بمصر). وتعلم في الأزهر ودار العلوم، وتخرج مدرسا، واختير عضوا بجمعية المحافظة على القرآن الكريم، وجمعية الشبان المسلمين وجمعية الهداية الإسلامية،
_________
(١) مجلة "المجلة" القاهرية، عدد ١٥: ١٠٦ والموسوعة العربية الميسرة ٧٨٧ ومجلة لواء الإسلام القاهرية سنة ١١: ٦٨٩ والأزهرية ٧: ٢٤٨.','2','564',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1009,'وشارك في الحركة الوطنية بشعره ومقالاته وخطبه. كان معتزا بنسبه العربي الصريح، غيورا على الدين، ملتزما أساليب الشعراء المتقدمين. من آثاره "إعجاز القرآن" (١).

التستري [٣٥٥ - بعد ٤٣٠ هـ / ٩٦٦ - بعد ١٠٣٩ م]

<span data-type="title" id=toc-1397>محمد بن عبد الملك بن سليمان بن أبي الجعد التستري، أبو بكر:</span>
عالم حنبلي، من أهل تستر بعربستان، سمع بخراسان والعراق، ودخل الأندلس تاجرا سنة ٤٣٠. قال الخزرجي: "كان عالما بفنون علم القرآن من قراءات، وإعراب، وتفسير .. " (٢).

الكرجي [٤٥٨ - ٥٣٢ هـ / ١٠٦٦ - ١١٣٨ م]

<span data-type="title" id=toc-1398>محمد بن عبد الملك بن محمد بن عمر ابن محمد الكرجي، أبو الحسن:</span>
محدث، مفسر، شاعر، أديب، من
_________
(١) شعراء مصر ٤١ وأعلام الأدب والفن ٢: ٤٥٩ وفي الأدب الحديث ٢: ٣٧٣ والأعلام ٧: ١٢٥ ومقدمة ديوان شعره، ومجلة الرسالة ١٥: ٥٩٣ والمنتخب من أدب العرب ١: ٩٨.
(٢) الصلة ٢: ٦٠٠ وطبقات المفسرين ٢: ١٨٧.','2','565',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1010,'فقهاء الشافعية، نسبته إلى الكرج، بين أصبهان وهمذان. قال السبكي: "أفنى عمره في جمع العلم ونشره، وروى عنه جماعة، وصنف تصانيف في المذهب، والتفسير .. ". ونسبه ابن الأثير إلى الكرخ وقال: "سمع بها وأصفهان وهمذان وغيرها". له تصانيف منها "تفسير القرآن" (١).

المرجاني [٧٢٤ - ٧٨١ هـ / ١٣٢٤ - ١٣٧٩ م]

<span data-type="title" id=toc-1399>محمد بن عبد الملك بن عبد الله بن محمد بن محمد المرجاني، أبو عبد الله:</span>
مفسر، فقيه مالكي، أصله من تونس، سكن الإسكندرية، ثم نزل مكة وتوفي بها. قال ابن حجر: "كان خيرا صالحا صاحب عبادة ومعرفة بالفقه وعناية بالتفسير، وكان يعرف علم الحرف" (٢).
_________
(١) طبقات المفسرين ٢: ١٨٧ وطبقات الشافعية ٦: ١٣٧ والنجوم الزاهرة ٤: ٢٦٢ والمتظم ١٠: ٧٥ والشذرات ٤: ١٠٥ والعبر ٤: ٨٩ والبداية والنهاية ١٢: ٢١٣ ومرآة الزمان ٨: ١٦٧ وهدية العارفين ٢:
(٢) إنباء الغمر ١: ٢٠٧ وشذرات الذهب ٦: ٢٧٢.','2','565',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1011,'البغدادي [... - ١٠١٦ هـ / ... - ١٦٠٧ م]

<span data-type="title" id=toc-1400>محمد بن عبد الملك البغدادي:</span>
فقيه حنفي، مفسر، مشارك في بعض العلوم، من أهل بغداد. قدم دمشق سنة ٩٧٧ هـ. ودرس بالدرويشية وبالجامع الأموي، ثم تولى مشيخته فسمي شيخ الحرم الأموي. مات بدمشق. له "ذيل" على حاشية ملا خسرو إلى تمام سورة البقرة من تفسير البيضاوي، ألفه سنة ١٥١٢ هـ، أوله "الحمد لله هادي المتقين" (١).

محمد عبده [١٢٦٦ - ١٣٢٣ هـ / ١٨٤٩ - ١٩٠٥ م]

<span data-type="title" id=toc-1401>محمد عبده بن حسن خير الله، من آل التركماني:</span>
من مؤسسي النهضة المصرية الحديثة، وكبار الدعاة إلى التجديد والإصلاح في العالم الإسلامي، ومفتي الديار المصرية. ولد بحصة شبشير من قرى إقليم الغربية، ونشأ بقرية "محلة نصر" من قرى شبراخيت بإقليم البحيرة. تعلم بالجامع الأحمدي بطنطا،
_________
(١) خلاصة الأثر ٤: ٣١ وكشف الظنون ١٩٠ وهدية العارفين ٢: ٢٦٧ ومعجم المؤلفين ١٠: ٢٥٦.','2','566',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1012,'ثم بالأزهر بالقاهرة، حيث اتجه إلى دراسة العلوم الطبيعية والتاريخية إلى جانب العلوم الإسلامية. وجاء جمال الدين الأفغاني إلى مصر، فلازمه وأخذ عنه الفلسفة والمنطق، وتأثر به كثيرا. ونال شهادة العالمية (سنة ١٨٧٧ م) فاشتغل بالتدريس في دار العلوم ومدرسة الألسن، وأخذ ينشر آراءه الحرة في الصحف، مما أثار عليه حقد المحافظين، فعزل من وظيفته وأبعد إلى قريته. ثم دعي إلى رئاسة تحرير "الوقائع المصرية"، وكانت حكومية، فصارت في عهده صحيفة الرأي الحر. وقامت الثورة العرابية، فنفخ في نارها، وكان في جملة مناصريها، وأفتى - على ما قيل - بخلع الخديوي توفيق، وحين أخمدت، واحتل الإنكليز مصر، سجن ٣ أشهر للتحقيق، ونفي إلى بلاد الشام، فأقام في بيروت سنة، انتدب خلالها للتعليم الديني في الكلية الإسلامية، ثم سافر إلى باريس، فأصدر مع جمال الدين الأفغاني مجلة "العروة الوثقى" (١٣ مارس ١٨٨٤) لمحاربة الإستعمار والطغيان في البلاد الإسلامية. ولمحمد عبده فيها "فصول في النقد والجدل والتوجيه السياسي العالي، بلغ فيها الذروة في نضج التفكير واستواء البيان وسمو','2','566',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1013,'المعاني". ثم عاد إلى بيروت، وقضى فيها بضع سنوات معلما ومربيا، ولقيه الأمير شكيب أرسلان وأخذ عنه واستفاد منه. ولما عفي عنه، عاد إلى مصر (سنة ١٣٠٦ هـ) وتولى منصب القضاء في المحاكم الأهلية، ثم عضوية مجلس إدارة الأزهر ممثلا للحكومة، ثم مستشارا في محكمة الاستئناف، فمفتيا للديار المصرية (سنة ١٣١٧ هـ) واستمر إلى أن توفي بالإسكندرية، ودفن في القاهرة، قال أحد مؤرخيه: "ويتلخص منهجه فى تفسير القرآن، في فهم الدلالة اللغوية، واستخدام العقل، والاستفادة من التقدم العلمي، وطرح البدع والخرافات. وقد صور العقيدة الإسلامية تصويرا سليما، وأنشأ جيلا من العلماء حذا حذوه في التفسير، على رأسه محمد رشيد رضا ومصطفى المراغي". من آثاره "تفسير جزء عم"، ونسب إليه تفسير القرآن، وهو لمحمد رشيد رضا (١).
_________
(١) تاريخ الأستاذ الإمام، ومحمد عبده لعثمان أمين، وسيرة الإمام الشيخ محمد عبده لمصطفى عبد الرازق، ورائد الفكر المصري لعثمان أمين، ومحمد عبده لعبد المنعم حمادة، والشيخ محمد عبده لأحمد الشايب، والشيخ محمد عبده لعبد الجواد سليمان، والشيخ محمد عبده لمحمد صبيح، والإسلام والتجديد في مصر ٢٠ - ١٦٨، وتاريخ الصحافة العربية ٢: ٢٨٧ وفيض الخاطر ٨: ١٥٥ وزعماء الإصلاح في العصر الحديث ٢٨٥ وفي الأدب =','2','567',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1014,'السندي [... - ١١٣٨ هـ / ... - ١٧٢٦ م]

<span data-type="title" id=toc-1402>محمد بن عبد الهادي التتوي السندي، أبو الحسن، نور الدين:</span>
عالم بالحديث والتفسير والفقه والعربية. ولد في السند وأخذ عن علمائها، ورحل إلى تستر وأخذ بها عن جملة من الشيوخ. ثم هاجر إلى الحجاز وتوطن المدينة المنورة إلى أن توفي. من آثاره "حاشية على تفسير البيضاوي" (١).

غلام ثعلب [٢٦١ - ٣٤٥ هـ / ٨٧٥ - ٩٥٧ م]

<span data-type="title" id=toc-1403>محمد بن عبد الواحد بن أبي هاشم، أبو عمر المطرز الباوردي، المعروف بغلام (ثعلب)</span>
_________
= الحديث ٢٢٧ وحاضر العالم الإسلامي ١: ٢٨٣ وأدب المقالة الصحفية ٦٢ وكنز الجوهر في تاريخ الأزهر ١٦٥ ورواد النهضة الحديثة ١٩٥ والأدب العربي المعاصر لشوقي ضيف ١٩٠ وتاريخ آداب اللغة العربية ٤: ٢٨٠ وأشهر مشاهير أدباء الشرق ٢: ٨٢ وأعلام الصحافة العربية ٦٨ ولمحة في تاريخ الأزهر، ومعجم المطبوعات ١٦٧٧ والأزهر بين الماضي والحاضر، والمكتبة البلدية، فهرس التفسير ٨ - ١٠ وصفوة العصر ١: ٥١٧ وبناة النهضة العربية ٧٧ والأعلام ٦: ٢٥٣ ومعجم المؤلفين ١٠: ٢٧٢ ومصادر الدراسة الأدبية ٢: ٥٩٧.
(١) إيضاح المكنون ١: ١٤٠ وهدية العارفين ٢: ٣١٨ وسلك الدرر ٤: ٦٦ وفهرس الفهارس ١: ١٠٣ ومعجم المطبوعات ١٠٥٦ وفهرست الخديوية ١: ٣٣١ و ٣٨٠ والأعلام ٧: ١٣٢ ومعجم المؤلفين ١٠: ٢٦٢.','2','567',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1015,'ثعلب: من أئمة اللغة. نسبته إلى باورد (وهي أبي ورد بخراسان). صحب ثعلبا النحوي زمانا حتى لقب "غلام ثعلب"، وأكثر من الأخذ عنه، واستدرك على كتابه الفصيح. وكان الأشراف والكتاب وأهل الأدب يحضرون عنده ليسمعوا منه كتب ثعلب وغيرها. قيل أنه أملى من حفظه ثلاثين ألف ورقة. وتوفي ببغداد. من كتبه "اليواقيت" رسالة في غريب القرآن، مخطوطة (١).

الطبري [... - بعد ٤٤٦ هـ / ... - بعد ١٠٥٤ م]

<span data-type="title" id=toc-1404>محمد بن عبد الواحد بن محمد الطبري، أبو طاهر:</span>
مفسر، روى عن أبي يعلى الخليلي القزويني المتوفى سنة ٤٤٦ هـ (١٠٥٤ م). ذكره الرافعي في تاريخ قزوين وقال: مفسر، له كتاب "التفريد في فضائل التوحيد" ولم يؤرخ مولده ولا وفاته (٢).
_________
(١) الأعلام ٧: ١٣٢ ونزهة الألباء ٣٤٥ والشذرات ٢: ٢٧٠ وتاريخ بغداد ٢: ٣٥٦ وابن خلكان ٤: ٣٢٩ وروضات الجنات ٦١٤ ولسان الميزان ٥: ٢٦٨ ومعجم الأدباء ١٨: ٢٢٦ والمنتظم ٦: ٣٨٠ وإنباه الرواة ٣: ١٧١. والوافي ٤: ٧٢ والشذرات ٢: ٣٧٠.
(٢) تاريخ قزوين ١٣٠ و ٣٤١ ترجمة عبد الجبار بن محمد. وطبقات المفسرين ٢: ١٨٨.','2','568',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1016,'الملاحي [٥٤٩ - ٦١٩ هـ / ١١٥٤ - ١٢٢٢ م]

<span data-type="title" id=toc-1405>محمد بن عبد الواحد بن إبراهيم بن مفرج ابن أحمد الغافقي الملاحي، أبو القاسم:</span>
مؤرخ، محدث، حافظ، نسابة أديب، عارف بالتفسير، من أعلام المالكية. من أهل غرناطة، ونسبته إلى قرية الملاحة (على بريد من غرناطة) نزلها سلفه. حدث، وأخذ عنه الناس، وانتفعوا به. أثنى عليه ابن الأبار وغيره. من كتبه "فضائل القرآن" مخطوط، في الإسكندرية (١).

ضياء الدين المقدسي [٥٦٩ - ٦٤٣ هـ / ١١٧٤ - ١٢٤٥ م]

<span data-type="title" id=toc-1406>محمد بن عبد الواحد بن أحمد بن عبد الرحمن بن إسماعيل بن منصور السعدي، المقدسي، ثم الدمشقي الصالحي الحنبلي، أبو عبد الله، ضياء الدين:</span>
حافظ حجة، مؤرخ، كان محدث الشام وشيخ السنة في وقته. ولد في الدير المبارك في قاسيون بدمشق، وسمع
_________
(١) الوافي ٤: ٦٨ وتذكرة الحفاظ ١٤٠٢ والتكملة ٢: ٦٠٩ الرقم ١٦٠٤ ونيل الابتهاج ٢٢٨ وهدية العارفين ٢: ١١١ وفهرس الفهارس ٢: ٢٥٢.','2','568',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1017,'بها وبمصر وبغداد وأصبهان وهمذان ونيسابور وهراة، وكتب عن أزيد من خمسمائة شيخ. وتنسب إليه "دار الحديث الضيائية" بسفح قاسيون، شرقي الجامع المظفري، ووقف بها كتبه. قال الصفدي: "جمع بين فقه الحديث ومعانيه وشدا طرفا من الأدب وكثيرا من اللغة والتفسير ونظر في الفقه وناظر فيه" توفي بدمشق. من كتبه الكثيرة "فضائل القرآن" (١).

ابن الهمام [٧٩٠ - ٨٦١ هـ / ١٣٨٨ - ١٤٥٧ م]

<span data-type="title" id=toc-1407>محمد بن عبد الواحد بن عبد الحميد ابن مسعود، السيواسي ثم الإسكندري، كمال الدين، المعروف بابن الهمام:</span>
من أكابر فقهاء الحنفية، عارف بالتفسير والفرائض والنحو والمنطق والحساب والتصوف واللغة والموسيقى وأصول الديانات. أصله من سيواس.
_________
(١) ذيل طبقات الحنابلة ٢: ٢٣٦ وتذكرة الحفاظ ١٤٠٥ والعبر ٥: ١٧٩ والنجوم الزاهرة ٦: ٣٥٤ والشذرات ٥: ٢٢٤ والوافي ٤: ٦٥ وفوات الوفيات ٣: ٤٢٦ والبداية والنهاية ١٣: ١٦٩ والدارس ٢: ٩١ والقلائد الجوهرية ١: ٧٦ ومجلة المجتمع العربي ١٣: ٣٢١ ومجلة معهد المخطوطات ٢: ٩٠','2','569',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1018,'ولد بالإسكندرية وبها نشأ وتعلم، وقدم القاهرة وأخذ عن علمائها. وأقام بحلب عند شيخه ابن الشحنة إلى أن مات. وحج وجاور بالحرمين، ثم كان شيخ الشيوخ بالخانقاه الشيخونية بمصر. ومات بالقاهرة (١).

النيسابوري [... - ٣٣٨ هـ / ... - ٩٤٩ م]

<span data-type="title" id=toc-1408>محمد بن عبدوس بن أحمد بن الجنيد، أبو بكر، النيسابوري:</span>
مقرىء، مفسر، واعظ، من أهل نيسابور. ذكره صاحب طبقات المفسرين وقال: "إمام في القراءات، عالم بمعاني القرآن، سمع السري بن خزيمة، وأبا عبد الله البوشنجي، وتلا على حمدون المقرىء، وأبي الحسن ابن شنبو، سمع منه الحاكم وأثنى عليه" (٢).
_________
(١) الضوء اللامع ٨: ١٢٧ وبغية الوعاة ١: ١٦٦ والبدر الطالع ٢: ٢٠١ والشذرات ٧: ٢٩٨ والفوائد البهية ١٨٠ وهدية العارفين ٢: ٢٠١ والأعلام ٧: ١٣٤ ومعجم المؤلفين ١٠: ٢٦٤.
(٢) طبقات المفسرين للداودي ٢: ١٩١.','2','569',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1019,'الجبائي [٢٣٥ - ٣٠٣ هـ / ٨٤٩ - ٩١٦ م]

<span data-type="title" id=toc-1409>محمد بن عبد الوهاب بن سلام بن خالد بن حمزة، أبو علي الجبائي:</span>
من أئمة المعتزلة، ورئيس علماء الكلام في عصره، وإليه نسبة الطائفة "الجبائية". ولد في "جبى" - من قرى البصرة - وانتقل إلى البصرة، وأخذ عن الشحام الذي كان أهم رجال المعتزلة بها، ولما مات الشحام، أصبح الجبائي رئيسا لهذه المدرسة حتى وفاته. أخذ عنه ابنه أبو هاشم، وأبو الحسن الأشعري، ثم خرج الأشعري عليه وعلى مذهب المعتزلة لاختلافه معه في مسألة الصلاح والأصلح. له مقالات وآراء انفرد بها في المذهب. من كتبه "تفسير القرآن" في عشر مجلدات؛ لم يصلنا منها شيء. وقد رد عليه الأشعري في كتابه "تفسير القرآن والرد على من خالف البيان من أهل الإفك والبهتان". ويقال إن ابن فورك قد أخذ كثيرا من تفسير الجبائي. وله أيضا كتاب "متشابه القرآن" اقتبس منه ابن أبي الحديد في كتاب "شرح نهج البلاغة" ج ٣: ٣٤، ٥١، ٥٤ من غير أن يسمي هذا الكتاب، كما اعتمد','2','570',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1020,'عليه أبو عمر أحمد بن محمد بن حفص الخلال في كتابه "الرد على الجبرية والقدرية فيما تعلقوا به من متشابه القرآن الكريم" (١).

ابن الحنبلي [٥٧٨ - ٦٥٢ هـ / ١١٨٢ - ١٢٥٤ م]

<span data-type="title" id=toc-1410>محمد (ويقال في اسمه سعد) بن عبد الوهاب بن عبد الكافي بن عبد الوهاب بن عبد الواحد الأنصاري، الشيرازي الأصل، الدمشقي، أبو بكر، ويقال: أبو اليمن، وأبو المعالي، وأبو سعيد، المعروف بابن الحنبلي:</span>
واعظ، عارف بالتفسير، ولد في دمشق وبها نشأ وتعلم. ورحل، فوعظ في مصر والمغرب والأندلس، ومات بالقرب من مراكش. قال
_________
(١) طبقات المفسرين للسيوطي ٣٣ وطبقات المفسرين للداودي ٢: ١٨٩ وطبقات المعتزلة ٨٠ ووفيات الأعيان ٤: ٢٦٧ والوافي بالوفيات ٤: ٧٤ وتاريخ التراث العربي ٢: ٤٠٦ ولسان الميزان ٥: ٢٧١ وروضات الجنات ١٦١ والمنتظم ٦: ١٣٧ وشذرات الذهب ٢: ٢٤٩ والبداية والنهاية ١١: ١٢٥ والنجوم الزاهرة ٣: ١٨٩ والأعلام ٧: ١٣٦ ومعجم المؤلفين ١٠: ٢٦٩ والموسوعة العربية الميسرة ٦١١ ودائرة المعارف الإسلامية ٦: ٢٧٠ - ٢٧٤ وتراجم الرجال ٣٥ وتبيين كذب المفتري ١٣٧ واللباب ١: ٢٢٥.','2','570',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1021,'الداودي: "حفظ الكثير، وعرف التفسير. وكان يفتتح مجالس وعظه بالتفسير بعد الخطبة والدعاء وشيء من أخبار الصالحين .. وكان شافعي المذهب، مستحسن المنزع، لولا حرص كان فيه من باب التكسب، ومع ذالك فقد كان من حسنات وقته" (١).

عبد الكريم زاده [... - ٩٧٥ هـ / ... - ١٥٦٨ م]

<span data-type="title" id=toc-1411>محمد بن عبد الوهاب بن عبد الكريم، المعروف بعبد الكريم زاده:</span>
مفسر، لغوي، من فقهاء الحنفية، تركي الأصل، عربي التصانيف. كان جده عبد الكريم قاضيا بالعسكر في دولة السلطان محمد خان. وتولى أبوه عبد الوهاب "الدفتردارية" في عهد السلطان سليم خان. ونشأ هو متفرغا للعلم، فأخذ عن علماء عصره حتى نبغ واشتهر. قال صاحب "شذرات الذهب": وكان حلو المفاكهة، طيب المعاشرة، لا يكتب بالقلم الذي يكتب
_________
(١) طبقات المفسرين للداودي ٢: ١٩٠ وذيل طبقات الحنابلة ٢: ٢٦٧.','2','571',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1022,'به اسم الله تعالى". من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي، وصل فيها إلى سورة طه. قال صاحب كشف الظنون: "ولم تنتشر" (١).

محمد بن عبد الوهاب [١١١٥ - ١٢٠٦ هـ / ١٧٠٣ - ١٧٩٢ م]

<span data-type="title" id=toc-1412>محمد بن عبد الوهاب بن سليما بن أحمد بن راشد بن يزيد التميمي النجدي:</span>
زعيم النهضة الدينية الإصلاحية الحديثة في جزيرة العرب. ولد في العيينة بنجد، ونشأ بها، ورحل مرتين إلى الحجاز، فمكث في المدينة مدة قرأ بها على بعض علمائها. ودخل البصرة فأوذي فيها. وعاد إلى نجد، فسكن حريملاء، ثم انتقل إلى العيينة، ناهجا نهج السلف الصالح داعيا إلى التوحيد ونبذ البدع، وقصد أميرها عثمان بن حمد بن معمر النجدي (ت: ١١٦٣ هـ) وكان مما قاله له: "أرجو إن قمت بنصر لا اله الأ الله أن يظهرك الله تعالى وتملك نجدا
_________
(١) شذرات الذهب ٨: ٨٠ و ٣٨٥ وهدية العارفين ٢: ٢٥٠ وكشف الظنون ١٩١ والأعلام ٦: ٢٥٦ ومعجم المؤلفين ١٠: ٢٧٠.','2','571',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1023,'وأعرابها". فوعده بمساعدته، ثم تلكأ. ففارقه ابن عبد الوهاب إلى محمد ابن سعود (ت: ١١٧٩ هـ) أمير الدرعية، فقبل دعوته، وتعاهدا على أن يكون إبن سعود "حارسا للدين وناصرا للسنة"، وأن يستمر ابن عبد الوهاب على الجهر بدعوته. ولما اتسع نطاق ملك السعوديين (محمد بن سعود، ومن بعده ابنه عبد العزيز، ثم سعود بن عبد العزيز) انتشرت دعوته حتى بلغت أطراف عمان ونجران واليمن وعسير إلى شواطىء الفرات وبادية الشام، ومن الخليخ العربي إلى البحر الأحمر. كما تأثر بها رجال الإصلاح في الهند ومصر والعراق وسورية وتونس وغيرها. وعرف من والاه وشد إزره في قلب جزيرة العرب بأهل التوحيد "إخوان من أطاع الله" وسماهم خصومهم بالوهابيين نسبة إليه، وكانت وفاته في الدرعية، وحفداؤه اليوم يعرفون ببيت "الشيخ" ولهم مقام رفيع عند العائلة المالكة السعودية. من آثاره "تفسير سورة الفاتحة" (١).
_________
(١) محمد بن عبد الوهاب، لأحمد عبد الغفور عطار، والموسوعة العربية الميسرة ٢١ والمجددون في الإسلام ٤٣٧ ومختصر طبقات الحنابلة ١٣٧ والأعلام =','2','572',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1024,'ابن منظور [... - ٧٥٠ هـ / ... - ١٣٤٩ م]

<span data-type="title" id=toc-1413>محمد بن عبيد الله بن محمد بن يوسف، أبو بكر بن منظور القيسي:</span>
أديب، خطيب، من أعلام القضاة في الأندلس. أصله من إشبيلية، من بيت علم وأدب وفضل. نشأ بمالقة. قال النباهي: "ولي القضاء بجهات شتى من الأندلس، فحمدت سيرته، وشكرت طريقته، ثم تقدم ببلده مالقة قاضيا وخطيبا بقصبتها، وله تآليف سمعت عليه بعضها وناولني سائرها، منها "البرهان والدليل، في خواص سور التنزيل" (١).
_________
= ٧: ١٣٧ ومعجم المؤلفين ١٠: ٢٦٩ والمسك الأذفر ١١١ وهدية العارفين ٢: ٣٥٠ ومعجم المطبوعات ١٦٩ ومجلة الثقافة (المصرية) ٥: ٢٠٤١ و ٢٠٦٥ ومجلة الزهراء ٣: ٤١٧ وزعماء الإصلاح ٧ والفكر السامي ٤: ١٩٦ وحاضر العالم الإسلامي ٤: ١٦١ ومجلة المقتطف ٢٧: ٢٩٥ و ٣٠: ٦٩٩ وتاريخ آداب اللغة العربية ٣: ٣٥٦ ومحاضرات عن الحركات الإصلاحية ومراكز الثقافة في الشرق الإسلامي ٥٦ وتاريخ مصر الحديث ٢: ١٥٦ (ط ٢) وتاريخ نجد الحديث ٢١ وأبجد العلوم ٨٧١.
(١) تاريخ قضاة الأندلس ١٥٤ والدرر الكامنة ٤: ١٥٦ وهدية العارفين ٢: ١٥٦ والأعلام ٦: ٢٦٠ ومعجم المؤلفين ٩٠: ٢٧٩.','2','572',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1025,'الجعد [... - ٢٨٨ هـ / ... - ٩٠١ م]

<span data-type="title" id=toc-1414>محمد بن عثمان بن مسبح الشيباني، أبو بكر، المعروف بالجعد:</span>
نحوي، لغوي، عالم بالقراءات والتفسير، من أهل بغداد. ووفاته في أكثر مصادر ترجمته سنة نيف و ٣٢٠ هـ. قال الزركلي: "وفي كتاب "الألقاب" مخطوط، لابن الفرضي: توفي في شهر رمضان من سنة ٢٨٨ هـ ودفن في باب السلام". من كتبه "معاني القرآن" و "غريب القرآن" و "ناسخ القرآن ومنسوخه" قال الخطيب البغدادي: "حدث به أبو بكر أحمد بن علي بن جعفر بن سلم عنه، وهو من أحسن الكتب وأجودها، (١).

ابن أبي شيبة [.. - ٢٩٧ هـ / ... - ٩٠٩ م]

<span data-type="title" id=toc-1415>محمد بن عثمان بن محمد بن أبي (شيبة العبسي الكوفي، أبو جعفر)</span>
_________
(١) طبقات المفسرين للداودي ٢: ١٩٣ وتاريخ بغداد ٣: ٤٧ ومعجم الأدباء ١٨: ٢٥٠ وإنباه الرواة ٣: ١٨٤ وبغية الوعاة ١: ١٧١ ونزهة الألباء ٣٠٩ والوافي بالوفيات ٤: ٨٢ والنسخ في القرآن الكريم ١: ٣٢٥ وهدية العارفين ٢: ٢٩ والأعلام ٦: ٢٦٠ ومعجم المؤلفين ١٠: ٢٨٧ وكشف الظنون ١٩٢٠ وفيه أن له "ناسخ الحديث ومنسوخه" خطأ. والفهرست لابن النديم ٩٠.','2','573',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1026,'شيبة العبسي الكوفي، أبو جعفر: مؤرخ لرجال الحديث، من الحفاظ، سمع ابن المديني وأحمد بن يونس وغيرهما. وروى عنه الطبراني والنجاد. مختلف في توثيقه. مات ببغداد عن نيف وثمانين عاما. قال الذهبي: "له تآليف مفيدة، منها كتاب "فضائل القرآن" (١).

العليابادي [... - حيا ٦٢٨ هـ / ... - حيا ١٢٣١ م]

<span data-type="title" id=toc-1416>محمد بن عثمان بن محمد، حسام الدين، العليابادي السمرقندي:</span>
فقيه حنفي، مفسر، له "مطلع المعاني ومنبع المباني" في تفسير القرآن، في مجلدات. قال صاحب كشف الظنون: "وهو تفسير كبير بالقول، أوله: الحمد لله الذي أنزل القرآن هدى وبيانا الخ. افتتح في إملائه يوم الأربعاء لثلاث ليال
_________
(١) طبقات المفسرين للداودي ٢: ١٩٢ وميزان الاعتدال ٣: ٦٤٢ والمنتظم ٦: ٩٥ ولسان الميزان ٥: ٢٨٠ وتذكرة الحفاط ٢: ٦٦١ والنجوم الزاهرة ٣: ١٧١ والوافي بالوفيات ٤: ٨٢ وتاريخ بغداد ٣: ٤٢ وشذرات الذهب ٢: ٢٢٦ والعبر للذهبي ٢: ١٠٨ والأعلام ٦: ٢٦٠ ومعجم المؤلفين ١٠: ٢٨٥.','2','573',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1027,'خلون من رجب سنة ٦٢٨ هـ، وذكر في ديباجته ما ذكره صاحب الكشاف من لزوم العلمين" (١).

الخراساني [... - ٧٤٢ هـ / ... - ١٣٤١ م]

<span data-type="title" id=toc-1417>محمد بن عثمان الخراساني، نور الدين:</span>
محدث، مفسر، من أهل خراسان. له "لطائف التفسير" (٢).

الميرغني [١٢٠٨ - ١٢٦٨ هـ / ١٧٩٣ - ١٨٥٢ م]

<span data-type="title" id=toc-1418>محمد عثمان بن محمد بن أبي بكر بن عبد الله الميرغني المحجوب، الحنفي الحسيني:</span>
مفسر، متصوف، ولد بالطائف، وتعلم بمكة، وتصوف، ونشر الطريقة الميرغنية في الحجاز. وانتقل إلى مصر فدخل منفلوط وأسيوط، ثم قصد السودان فأكرمه أهلها وأخذوا عنه الطريقة الميرغنية. وتوفي بالطائف. من آثاره "تاج التفاسير لكلام الملك الكبير" طبع،
_________
(١) هدية العارفين ٢: ١١٢ وكشف الظنون ١٧٢١ ومعجم المؤلفين ١٠: ٢٨٦
(٢) شد الإزار ٧٩ ومعجم المؤلفين ١٠: ٢٨٢','2','574',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1028,'مجلدان (١).

النجار [١٢٥٥ - ١٣٣١ هـ / ١٨٣٩ - ١٩١٣ م]

<span data-type="title" id=toc-1419>محمد بن عثمان بن محمد النجار، أبو عبد الله:</span>
فقيه مالكي، أصولي، محدث، مفسر، من أهل تونس، تعلم بجامع الزيتونة، ودرس. وأسندت إليه خطة العدالة سنة ١٢٧١ هـ ثم "الفتوى" سنة ١٣١٣ هـ واستمر إلى أن توفي. قال صاحب شجرة النور: "كان عالما بالأنساب، وتراجم المؤلفين، متبحرا في العلوم النقلية، إماما في العلوم: العقلية، له "تقريرات" على تفسير البيضاوي" (٢)

السجستاني [... - ٣٣٠ / ... - ٩٤١ م]

<span data-type="title" id=toc-1420>محمد بن عزيز السجستاني، أبو بكر العزيزي:</span>
مفسر، أديب. لغوي،
_________
(١) جامع كرامات الأولياء ١: ٣٦٥ ومعجم المطبوعات ١٨٢٨ والأعلام ٧: ١٤٤ وفهرس التيمورية ٣: ٢٩٨ وهدية العارفين ٢: ٣٧٣ ومعجم المؤلفين ١٠: ٢٨٦ وفهرس دار الكتب المصرية ٨: ٢٣٧.
(٢) هدية العارفين ٢: ٣٧٧ ومعجم المطبوعات ١٦٣١ الأعلام الشرقية ٢: ١٧٧ وشجرة النور ٤٢١ والأعلام ٦: ٢٦٣ ومعجم المؤلفين ١٠: ٢٨٦.','2','574',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1029,'أقام ببغداد، وأخذ عن أبي بكر الأنباري، اشتهر بكتابه "غريب القرآن" طبع سنة ١٣٢٥ هـ على حروف المعجم. واسمه الأصلي "نزهة القلوب" أو "التبيان في تفسير القرآن". قال صاحب تاريخ التراث العربي: "يقال أنه عمل فيه خمسة عشر عاما، ثم قدمه إلى أستاذه أبي بكر بن الأنباري ليعطي رأيه فيه، وقد صحح له فيه بعض المواضع. وبمقارنة هذا الكتاب بكتاب "مجاز القرآن" لأبي عبيدة يتضح لنا أن هذا الكتاب المشهور ليس في حقيقة الأمر إلا مختصرا عاديا من كتاب "مجاز القرآن". وربما كان السبب في شهرة هذا الكتاب يرجع إلى أن المؤلف قد أدرك وعرف قيمة كتاب أو عبيدة كمرجع لا ينضب معينه لتفسير القرآن، وأنه كذلك يسر استعماله بترتيب المواد المختارة منه وفق أشكالها ترتيبا أبجديا" (١).
_________
(١) الوافي ٤: ٩٥ وفيه عزير، بزاي أولى وراء ثانية، وأكثر الناس يقولونه بزايين .. وقال الدارقطني: بالزاي، وكان معاصره". واللباب ٢: ٣٣٨ وفيه: محمد بن عزير .. "ومن قاله بزاءين فقد أخطأ". والقاموس ٢: ١٨٣ مادة عزز: محمد بن عزيز .. والبغادنة يقولون بالراء وهو تصحيف، وبعضهم صنف فيه وجمع كلام الناس وقد ضرب في حديد بارد" وفي تاج العروس (٤: ٥٧) تعليق','2','575',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1030,'العزيزي [القرن ١٣ الهجري / القرن ١٩ الميلادي]
محمد العزيزي: فقيه شافعي، مفسر، له "خلاصة البيان في ذكر ما أورده مولانا حسن أفندي شيخ زاده من الإشكال في قوله تعالى: {وما أرسلنا من قبلك من رسول ولا نبي} الآية" (١).

الباقر [٥٧ - ١١٤ هـ / ٦٧٦ - ٧٣٢ م]

<span data-type="title" id=toc-1421>محمد بن علي زين العابدين بن الحسن بن علي بن أبي طالب الهاشمي القرشي، أبو جعفر الباقر:</span>
خامس الأئمة الإثني عشر عند الإمامية. كان سيد بني هاشم في زمانه. يعد مفسرا وزاهدا وفقيها. اشتهر بالباقر من مسهب في إثبات أنه بالزاي لا بالراء". وفي المشتبه (٢: ٤٦٠١) أنه بالراء لا بالزاي، وفي تبصير المنتبه (٣: ٩٤٨) أنه بالزاي ... " الخ، وانظر أيضا بغية الوعاة ١: ١٧١ ونزهة الألباء ٣١٤ وتاريخ التراث العربي ١: ٢١١ وكشف الظنون ١١٤٠ و ١٢٠٨ و ١٩٤٥ وهدية العارفين ٢: ٣٦ والأعلام ٧: ١٤٩.
_________
(١) الأزهرية ١: ٢٢٧ ومعجم المؤلفين ١٠: ٢٩٢. ونسبة صاحب الترجمة لقرية "العزيزية" بشرقية مصر، وممن عرف بهذه النسبة علي بن علي العزيزي المتوفى سنة ١١٩٩ هـ وله تصانيف.','2','575',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1031,'قولهم: بقر العلم، يعني: شقه فعلم أصله وخفيه. قال صاحب تاريخ التراث العربي: "انتشرت أكثر معارفه عن طريق ابنه جعفر، وله تلميذ آخر معروف ومشهور، هو أبو الجارود زياد ابن المنذر. ولم ينضم أبو الجارود بعد موت أستاذه إلى ابنه جعفر، ولكنه انضم إلى أخيه زيد، ونقل آراء الباقر إلى المدرسة الزيدية الناشئة". من آثاره "تفسير القرآن" رواه عنه أبو الجارود (١).

الحكيم الترمذي [... - نحو ٣٢٠ هـ / ... - نحو ٩٣٢ م]

<span data-type="title" id=toc-1422>محمد بن علي بن الحسن بن بشر، أبو عبد الله، الحكيم الترمذي:</span>
حافظ، محدث، صوفي، عالم بأصول الدين، من كبار مشايخ خراسان في وقته. من أهل "ترمذ". ألف كتاب "ختم الولاية" فاتهم بالزندقة، ونفي
_________
(١) تذكرة الحفاظ ١٢٥ وتهذيب التهذيب ٩: ٣٥٠ وحلية الأولياء ٣: ١٨٠ وطبقات الداودي ٢: ١٩٨ وابن خلكان ٤: ١٧٤ والذريعة ١: ٣١٥ ونزهة الجليس ٢: ٢٣ وذيل المذيل ٩٦ وأعيان الشية ٤: ٣ وتاريخ التراث العربي ٢: ٢٣٩ والأعلام: ١٥٣ وصفة الصفوة ٢: ٦٠ واليعقوبي ٣: ٦٠ وقيل في وفاته: سنة ١١٧ أو ١١٨ هـ.','2','576',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1032,'من بلده، وقيل: اتهم باتباع طريقة الصوفية في الإشارات ودعوى الكشف، وقيل فضل الولاية على النبوة. ورد بعض العلماء هذه التهمة عنه. قال السبكي: "فجاء إلى بلخ فقبله أهلها لموافقته إياهم على المذهب". وقال ابن حجر "عاش إلى حدود العشرين وثلاث مائة، فإن الأنباري سمع منه سنة ثماني عشرة وثلاث مائة". من آثاره "مسألة" في شرح سورة محمد الآية ١٩، وفيها ترتيب (للعمال) وفق منازلهم الصوفية في الجنة في ٩ طبقات. (مخطوط) و "تفسير آية: {شرقية ولا غربية} (سورة ٢٤ الآية ٣٥) (١).

ابن عبدك [... - ٣٤٧ هـ / ... - ٩٥٨ م]

<span data-type="title" id=toc-1423>محمد بن علي بن عبدك (اختصار (عبد الكريم) الجرجاني، أبو أحمد، المعروف بالعبدكي وابن عبدك)</span>
_________
(١) طبقات الصوفية للسلمي ٢١٧ وتذكرة الحفاظ ٦٤٥ وحلية الأولياء ١٠: ٢٣٣ ومجلة المشرق ٥٥: ٢٤٥ ولسان الميزان ٥: ٣٠٨ والرسالة المستطرفة ٤٣ ومعجم المطبوعات ٦٣٣ وتاريخ التراث العربي ٢: ٤٦٤ وهدية العارفين ٢: ١٥ ومفتاح السعادة ٢: ١٧٠ ومعجم المؤلفين ١٠: ٣١٥ وكشف الظنون في مواضع متفرقة، والأعلام ٧: ١٥٦ ومنجد الأعلام ١٨٦ ودائرة المعارف الإسلامية ٥: ٢٢٧ وطبقات السبكي ٢: ٢٠ والفهرس التمهيدي ١٣٩ و ١٤٥ و ١٤٩.','2','576',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1033,'عبد الكريم) الجرجاني، أبو أحمد، المعروف بالعبدكي وابن عبدك: فقيه، متكلم، مفسر، أديب، من أهل جرجان، وكان مقدم الشيعة بها، استوطن نيسابور مدة، ومات بجرجان. روى عنه الحاكم. له كتب، منها "تفسير القرآن" (١).

القفال الكبير [٢٩١ - ٣٦٥ هـ / ٩٠٤ - ٩٧٦ م]

<span data-type="title" id=toc-1424>محمد بن على بن إسماعيل الشاشي، أبو بكر، المعروف بالقفال الكبير:</span>
إمام عصره بما وراء النهر، محدث، مفسر، أصولي، لغوي، أديب، مولده ووفاته في الشاش (وراء نهر سيحون) رحل في طلب الحديث إلى خراسان والعراق والحجاز والشام والثغور، وانتشر عنه المذهب الشافعي في ما وراء النهر، وهو أول من صنف الجدل الحسن من الفقهاء. من تصانيفه "تفسير القرآن". قال الذهبي: "سئل أبو سهل الصعلوكي عن تفسير أبي بكر القفال، فقال: قدسه من وجه
_________
(١) اللباب ٢: ٣١٢ وفهرست الطوسي ١٩٣ وأعيان الشيعة ٤٦: ٦٢ وتاج التراجم ٤٤ ومنهج المقال ٣٠٩.','2','577',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1034,'ودنسه من وجه، أي دنسه من جهة نصرة مذهب الإعتزال". وقال ابن عساكر: "بلغني أنه كان مائلا عن الاعتدال قائلا بالاعتزال في أول أمره، ثم رجع إلى مذهب الأشعري (١).

ابن بابويه [٣٠٦ - ٣٨١ هـ / ٩١٦ - ٩٩١ م]

<span data-type="title" id=toc-1425>محمد بن علي بن الحسين بن موسى ابن بابويه القمي، أبو جعفر، ويعرف بالشيخ الصدوق:</span>
محدث، حافظ، مفسر، أصولي، من كبار فقهاء الشيعة الإمامية. لم ير في القميين مثله، نزل بالري، وارتفع شأنه في خراسان، وورد بغداد وحدث بها عن أبيه، وتوفي ودفن بالري. من آثاره "تفسير القرآن" و "الناسخ والمنسوخ" (٢).
_________
(١) الوافي ٤: ١١٢ وابن هداية ٨٨ وابن خلكان ٤: ٢٠٠ وتبيين كذب المفتري ١٨٢ والسبكي ٣: ٢٠٠ وتهذيب الأسماء واللغات ٢: ٢٨٢ والعبر ٢: ٣٣٨ وشذرات الذهب ٣: ٥١ والنجوم الزاهرة ٤: ١١١ وطبقات الشيرازي ١١٢ وهدية العارفين ٢: ٣٠٨ وطبقات المفسرين ٢: ١٩٦ وطبقات المفسرين للسيوطي ٢٦ ومرآة الجنان ٢: ٣٨١ وطبقات العبادي ٩٢ واللباب. ومعجم المؤلفين ١٠: ٣٠٨ والأعلام ٧: ١٥٩ وقيل في وفته: سنة ٣٣٠ أو ٣٣٥ أو ٣٣٦ أو ٣٦٥ أو ٣٦٦ هـ.
(٢) أعيان الشيعة ٤٦: ١٥٣ ومنتهى المقال ٢٨٢ وهدية العارفين ٢: ٥٢ والذريعة ٥: ١٤٠ وروضات =','2','577',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1035,'الأدفوي [٣٠٤ - ٣٨٨ هـ / ٩١٦ - ٩٩٨ م]

<span data-type="title" id=toc-1426>محمد بن علي بن أحمد الأدفوي، أبو بكر:</span>
نحوي، مفسر، مقرىء، من أهل أدفو - بصعيد مصر الأعلى -. كان يبيع الخشب بالقاهرة. أخذ النحو عن أبي جعفر النحاس، وانفرد بالإمامة في وقته في قراءة نافع، رواية ورش. مات بالقاهرة. من آثاره "الإستغناء" في تفسير القرآن، في مئة وعشرين مجلدا، صنفه في اثنتي عشرة سنة. قال الذهبي: "منه نسخه بمصر بوقف القاضي الفاضل عبد الرحيم". وقال الأدفوي صاحب الطالع السعيد: "وقفت على كتابه "الإستغناء" في التفسير في مجلدات كثيرة، رأيت منه من نسخة عشرين مجلدا، ويقال إنه في مائة أو ما يقاربها" وقال القفطي: وهو أكبر كتاب صنف في التفسير، جمع فيه من العلوم ما لم يجتمع لغيره" (١).
_________
= الجنات ٥٥٧ وفهرست الطوسي ١٥٦ ومنهج المقال ٣٠٧ وتنقيح المقال ٣: ١٥٤ ودائرة المعارف الإسلامية (ط ١) ١: ٩٤ ومعجم المطبوعات ٤٣.
(١) طبقات المفسرين للسيوطي ٣٨ وطبقات المفسرين للداودي ٢: ١٩٤ وإنباه الرواة ٣: ١٨٦ =','2','578',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1036,'الوزان [... - نحو ٤١٠ هـ / ... - نحو ١٠١٩ م]

<span data-type="title" id=toc-1427>محمد بن علي بن المظفر الوزان:</span>
مفسر، عارف بالعربية، عاش في نهاية القرن الرابع وأوائل القرن الخامس الهجري، من آثاره "لغة القرآن" مخطوط (١).

الأصبهاني [... - ٤١٤ هـ / ... - ١٠٢٣ م]

<span data-type="title" id=toc-1428>محمد بن علي بن حمويه الأصبهاني، أبو بكر، المعروف بالجمال:</span>
مفسر، واعظ، من أهل أصبهان. ترجم له السيوطي في طبقات المفسرين وقال: "كان ملك العلماء في وقته بأصبهان" (٢).
_________
= وبغية الوعاة ١: ١٨٩ والوافي ٤: ١١٧ وتاج العروس ١٠: ١٢٨ وهدية العارفين ٢: ٥٦ وكشف الظنون ٧٩ و ٤٤١ وحسن المحاضرة ١: ٤٩٠ وتاريخ التراث العربي ١: ٢١٧ وغاية النهاية ٢: ١٩٨ وطبقات ابن قاضي شهبة ١: ٩٧ والشذرات ٣: ١٣٠ والطالع السعيد ٥٥٢ والكواكب السيارة ١٥٧ والقاموس ٣: ١١٦ وروضات الجنات ٦٩٧ وجامع كرامات الأولياء ١: ١٠٢ والأعلام ٧: ١٦٠ وهو في بعض هذه المصادر محمد بن علي بن محمد أو محمد بن محمد.
(١) تاريخ التراث العربي ١: ٢٢١
(٢) طبقات المفسرين للسيوطي ٣٩ وعنه طبقات الداودي ٢: ٢٢١. قلت: لم أعثر على ترجمة وافية له =','2','578',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1037,'ابن مهريزد [٣٦٦ - ٤٥٩ هـ / ٩٧٧ - ١٠٦٧ م]

<span data-type="title" id=toc-1429>محمد بن علي بن محمد بن الحسين مهريزد الأصبهاني، أبو مسلم:</span>
معتزلي، عالم بالتفسير والأدب، كان محدث أصبهان في عصره، وتوفي بها. قال ابن حجر: "كان من كبار المعتزلة، عارفا بالعربية .. " له "تفسير القرآن" على مذهب المعتزلة، في عشرين مجلدا، سماه "جامع التأويل لمحكم التنزيل" (١).

الدامغاني [٣٩٨ - ٤٧٨ هـ / ١٠٠٧ - ١٠٨٥ م]

<span data-type="title" id=toc-1430>محمد بن علي بن محمد بن الحسن بن عبد الملك، أبو عبد الله الدامغاني:</span>
_________
= في كتب الرجال، ولم يذكر في وفيات سنة ٤١٤ هـ، وفي كتاب "أخبار أصبهان" (٢: ٣٠٤) ترجمة لأصبهاني اسمه: محمد بن علي بن أحمد الحمال، وكنيته أبو بكر. ولكن هذا عاش في القرن الثاني للهجرة، فقد روى عن شعبة المتوفى سنة ١٦٠ هـ وعن خالد بن الحارث المتوفى سنة ١٨٦ هـ.
(١) الوافي ٤: ١٣٠ والداودي ٢: ٢١١ وبغية الوعاة ١: ١٨٨ وطبقات المفيرين للسيوطي ٣٢ وميزان الاعتدال ٣: ٦٥٥ ولسان الميزان ٥: ٢٩٨ وهدية العارفين ٢: ٧١ وإيضاح المكنون ١: ٣٥٢ والشذرات ٣: ٣٠٧ والأعلام ٧: ١٦٣.','2','579',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1038,'قاضي القضاة، وشيخ الحنفية في زمانه. ولد بدامغان، وتفقه بها وبنيسابور، ثم ببغداد (سنة ٤١٨ هـ). قال الصفدي: حصل العلم على الفقر والقنوع، وآلى به الأمر إلى أن ولي قضاء القضاة للمقتدر بالله العباسي ولأبيه، بعد أن كان يحرس في درب الرياح، وانتشر ذكره، وكان مثل القاضي أبو يوسف في أيامه حشمة وسؤددا وعقلا ووجاهة". وبقي في القضاء نحو ثلاثين سنة. ذكر له بروكلمان كتاب "الزوائد والنظائر" في غريب القرآن مخطوط (١).

ابن الجوزي [٤٢٨ - ٤٨٣ هـ / ١٠٣٧ - ١٠٩٠ م]

<span data-type="title" id=toc-1431>محمد بن علي - ويقال يعلى - بن محمد بن وليد بن عبيد المعافري، أبو بكر، ويعرف بابن الجوزي:</span>
فقيه مالكي، مفسر، متكلم، من أهل إشبيلية، وأصله من قرطبة. دخل
_________
(١) الجواهر المضية ٢: ٩٦ والفوائد البهية ١٨٢ وتاريخ بعداد ٣: ١٠٩ والنجوم الزاهرة ٥: ١٢١ والوافي ٤: ١٣٩ وهدية العارفين ٢: ٧٤ واللباب ١: ٤٨٦ وبروكلمان ١: ٣٧٣ والذيل ١: ٦٣٧ والأعلام ٧: ١٦٣.','2','579',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1039,'إفريقية (تونس) وأخذ عن الديباجي وغيره. له "تفسير القرآن" لم يكمله (١).

النسفي [... - ٥١٧ هـ / ... - ١١٢٣ م]

<span data-type="title" id=toc-1432>محمد بن علي بن يحيى بن يوسف، ابن هبيرة، أبو الرضا النسفي، ثم البغدادي:</span>
مفسر، نحوي، حافظ للحديث، من أهل نسف - من بلاد ما وراء النهر -. دخل بغداد وأخذ عن طراد الزينبي (٣٩٨ - ٤٩١ هـ) مسند العراق في عصره، وحدث عنه. قال الصفدي: "كان حافظا، له معرفة تامة بالتفسير والنحو والأدب" (٢).

ابن شهر اشوب [٤٨٨ - ٥٨٨ هـ / ١٠٩٥ - ١١٩٢ م]

<span data-type="title" id=toc-1433>محمد بن علي بن شهر اشوب السروي المازندراني، أبو جعفر، رشيد الدين:</span>
عالم بالحديث والتفسير
_________
(١) طبقات المفسرين للداودي ٢: ٢١٣ وشجرة النور ١٢١ وهدية العارفين ٢: ٧٥ والصلة. ومعجم المؤلفين ١١: ٦١.
(٢) الوافي ٤: ١٤٧ وطبقات المفسرين للسيوطي ٣٨ وطبقات المفسرين للداودي ٢: ٢١٢.','2','580',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1040,'والأصول، من فقهاء الشيعة الإمامية. من سارية مازندران، خافه واليها، فأمره بالخروج منها، فذهب إلى بغداد في أيام المقتفي العباسي، وعظمت منزلته. ثم انتقل إلى الموصل، واستقر بحلب وتوفي بها. من كتبه "أسباب نزول القرآن" و "تأويل متشابهات القرآن" مخطوط (١).

النوقاني [... - ٥٩٢ هـ / ... - ١١٩٦ م]

<span data-type="title" id=toc-1434>محمد بن علي بن نصر بن أبي سعيد النوقاني، فخر الدين، أبو عبد الله، وأبو المفاخر:</span>
فقيه شافعي، مفسر، أصولي، منطقي، محدث، نسبته إلى نوقان - إحدى مدينتي طوس -. تفقه بنيسابور، ثم قدم بغداد وولي مدرسة أم الخليفة الناصر. قال ابن النجار:
_________
(١) إعلام النبلاء ٤: ٣٠٨ وهو فيه ابن شهر اسوب، ومثله في الوافي ٤: ١٦٤ وبغية الوعاة ١: ١٨١. وفي لسان الميزان ٥: ٣١٠ ابن سهراسوب. وانظر أيضا تنقيح المقال ١٥٦ وأعيان الشيعة ٤٦: ١٣٦ وطبقات المفسرين للسيوطي ٣٧ وطبقات المفسرين للداودي ٢: ١٩٩ وهدية العارفين ٢: ١٠٢ وروضات الجنات ٥٧٥ وسفينة البحار ١: ٧٢٦ ومعجم المطبوعات ١٦٠٧ ومصفى المقال ٤١٤ وفوائد الرضوية ٥٦٨ والأعلام ٧: ١٦٣ والذريعة ٢: ١٢ و ٣: ٣٠٦ ومعجم المؤلفين ١١: ١٧.','2','580',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1041,'وأكثر الفقهاء من المدرسين ببغداد من الشافعية والحنابلة تلاميذه. وقال الصفدي: "له يد في التفسير والمنطق، مات ببغداد" (١).

ابن عسكر [... - ٦٣٦ هـ / ... - ١٢٣٩ م]

<span data-type="title" id=toc-1435>محمد بن علي بن الخضر بن هارون الغساني، أبو عبد الله، المعروف بابن عسكر:</span>
قاض، أديب، نحوي مقرىء، عالم بالتاريخ والحديث، من أهل مالقة بالأندلس، ولي قضاءها نيابة ثم أصالة، واستعفى فلم يجب، وحسنت سيرته، فاستمر على ذلك بقية عمره. من كتبه "المشرع الروي في الزيادة على غريبي الهروي" في القرآن والحديث (٢)
_________
(١) طبقات المفسرين للسيوطي ٣٩ وطبقات المفسرين للداودي ٢١١ والوافي ٤: ١٧١.
(٢) اختصار القدح ١٣٠ وفيه مات بمالقة سنة ٦٣٨ هـ. وقضاة الأندلس ١٢٣ ونفح الطيب ٢: ٣٥١ وانظر فهرسته، والمغرب ١: ٤٣١ والتكملة ٢: ٦٤١ وكشف الظنون ١٢٠٩ و ١٦٩٢ وبغية الوعاة ١: ١٧٩ وهدية العارفين ٢: ١١٣ والإحاطة ٢: ١٢٢ والأعلام ٧: ١٧٠ ومعجم المؤلفين ١١: ٧.','2','581',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1042,'الشيخ الأكبر ابن عربي [٥٦٠ - ٦٣٨ هـ / ١١٦٥ - ١٢٤٠ م]

<span data-type="title" id=toc-1436>محمد بن علي بن محمد بن أحمد بن عبد الله الحاتمي الطائي الأندلسي، أبو بكر، المعروف بمحيي الدين بن عربي، الملقب بالشيخ الأكبر:</span>
صوفي، عرف بمذهبه في وحدة الوجود. ولد في مرسية بالأندلس، ودرس الفقه والحديث باشبيلية، وسمع بقرطبة، وقام برحلة، فدخل بجاية - بالمغرب الأوسط - ومصر والحجاز والشام وما بين النهرين وبلاد الروم. وأنكر عليه أهل مصر آراءه، فعمل بعضهم على إراقة دمه، وحبس، فسعى في خلاصه أبو الحسن علي بن أبي نصر فتح البجائي (وكان من أكابر فقهاء المالكية في وقته) فنجا. ثم استقر في دمشق، وتوفي بها، ودفن بسفح قاسيون. وكان ظاهري المذهب في العبادات، باطني النظر في الاعتقادات. واتهم بأنه يشيع المذاهب المضلة في الاتحاد، والحلول، ووحدة الوجود، وممن اتهمه ابن تيمية وابن خلدون وابن حجر العسقلاني والبقاعي. وبرأه الفيروز آبادي والفخر الرازي والسيوطي والصفدي. وقال','2','581',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1043,'الذهبي: "له توسع وذكاء، وقوة خاطر، وحافظة، وتدقيق في التصوف، وتواليف جمة في العرفان، لولا شطحه في كلامه وشعره ولعل ذلك وقع منه حال سكره وغيبته - فيرجى له الخير". له نحو ثلاثمائة كتاب ورسالة، أملى أسماء ٢٥١ منها سنة ٦٣٢ هـ - قبل وفاته بنحو ست سنوات -. منها المتعلقة بالتفسير "الجمع والتفصيل في أسرار معاني التنزيل" قال: أكملت منه إلى قوله تعالى" {وإذ قال موسى لفتاه لا أبرح} .. (الكهف: ٦٠) وجاء بديعا في شأنه، ما أظن على البسيطة من نزع في القرآن ذلك المنزع .. الخ. وقال صاحب نفح الطيب: "وهو تفسير كبير، بلغ فيه إلى تفسير سورة الكهف عند قوله تعالى {وعلمناه من لدنا علما} (الكهف: ٦٥) وتوفي ولم يكمل، وهذا التفسير كتاب عظيم، كل سفر بحر لا ساحل له". قلت: لعل الشيخ الأكبر فسر الآيات الخمس (٦٠ - ٦٥ التي ذكرها صاحب النفح) بعد أن أملى أسماء كتبه سنة ٦٣٢ هـ. وله أيضا "كتاب المثلثات الواردة يا القرآن الكريم" مثل قوله تعالى {لا فارض ولا بكر عوان ..} وقوله تعالى {ولا تجهر بصلاتك ولا تخافت بها وابتغ','2','582',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1044,'بين ذلك سبيلا}. و "كتاب المسبعات الواردة في القرآن الكريم" مثل قوله تعالى {خلق سبع سموات} وقوله تعالى {وسبعة إذا رجعتم}. وكتاب "الرمز في حروف أوائل السور" (١).
_________
(١) طبقات المفسرين للسيوطي ٣٨ وطبقات المفسرين للداودي ٢: ٢٠٢ والوافي بالوفيات ٤: ١٧٣ والنجوم الزاهرة ٦: ٣٣٩ وعنوان الدراية (بتحقيقنا) ١٥٦ وفوات الوفيات ٣: ٤٣٥ ومرآة الجنان ٤: ١٠٠ ومرآة الزمان ٧٣٦ وعبر الذهبي ٥: ١٩٨ والبداية والنهاية ١٣: ١٥٦ وشذرات الذهب ٥: ١٩٥ ولسان الميزان ٥: ٣١١ وميزان الاعتدال ٣: ٦٥٩ والزركشي ٢٩٦ ونفح الطيب ٢: ١٦١ وغاية النهاية ٢: ٢٠٨ ومفتاح السعادة ١: ١٨٧ وتاريخ فلاسفة الإسلام ٢٧٥ والذيل على الروضتين ١٧٠ وروضات الجنات ١٩٢ والذيل والتكملة ٦: ٤٩٣ وعقود الجوهر ١٣ والمجددون في الإسلام ٢٧٥ وفهرس الفهارس ١: ٢٣٣ وجامع كرامات الأولياء ١: ١٩٨ وتاريخ آداب اللغة العربية ٣: ١٠٨ وتاريخ الفكر الأندلسي ٣٧١ واليواقيت والجواهر ٦: ومعجم المطبوعات ١٣٥ والأعلام ٧: ١٧٠ ودائرة المعارف الإسلامية (ط ١) ١: ٢٣١ وهدية العارفين ٢: ١١٤ والرحلة العياشية ١: ٣٤٤ وفهرس التيمورية ٣: ١٠١ والتكملة لابن الأبار ١: ٣٥٦ وجذوة الاقتاس ١٧٥ ومحيي الدبن ابن عربي لطه عبد الباقي سرور، ومقدمة فصوص الحكم لأبي العلا عفيفي، والفتوح المكية مقدمة ناشره، ودائرة المعارف (للبستاني). والكبريت الأحمر في بيان علوم الشيخ الأكبر للشعراني، والبرهان الأزهر في مناقب الشيخ الأكبر للقادري. وتنبيه الغبي إلى تكفير ابن عربي. وفهرس المخطوطات المصورة ٢: ٢٢١ ومعجم المؤلفين ١١: ٤٠.','2','582',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1045,'ابن الخيمي [٥٤٩ - ٦٤٢ هـ / ١١٥٤ - ١٢٤٥ م]

<span data-type="title" id=toc-1437>محمد بن علي بن علي بن علي بن المفضل، أبو طالب، مهذب الدين الحلي، المعروف بابن الخيمي:</span>
لغوي، أديب، شاعر، ولد في الحلة المزيدية، وسمع ببغداد ودمشق، ثم انتقل إلى مصر وأقام بالقاهرة إلى أن توفي. قال ابن خلكان: "وحضرت الصلاة عليه، وكان إماما في اللغة راوية للشعر والأدب". من كتبه "أمثال القرآن" و "حروف القرآن" (١).

العلوي [٥٧٤ - ٦٥٣ هـ / ١١٧٨ - ١٢٥٦ م]

<span data-type="title" id=toc-1438>محمد بن علي بن محمد بن علي بن علوي، أبو علوي، الحسيني نسبا، الحضرمي محتدا:</span>
متصوف كبير، مفسر، فقيه، كان يلقب بالأستاذ الأعظم، من أهل تريم (بحضرموت)
_________
(١) بغية الوعاة ١: ١٨٤ والبدر السافر ١٣٣ ووفيات الأعيان ٢: ٣٤٢ والوافي ٤: ١٨١ وفوات الوفيات ٣: ٤٤١ وهدية العارفين ٢: ١٢١ والأعلام ٧: ١٧١.','2','583',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1046,'مولدا ووفاة. قال في "تاريخ حضرموت": "كان إذا تكلم في التفسير فهو حامل رايته، أو ذاكر في الحديث فهو ذو روايته، أو أفتى في الفقه فهو مدرك غايته" (١).

الأنصاري [... - ٦٦٢ هـ / ... - ١٢٦٤ م]

<span data-type="title" id=toc-1439>محمد بن علي بن العابد الأنصاري، أبو عبد الله، الفاسي ثم الأندلسي:</span>
مؤرخ، أديب، شاعر، لغوي، مفسر، من أهل فاس، وبها نشأ وتعلم. رحل إلى الأندلس واستوطن غرناطة إلى أن توفي. قال ابن الخطيب: "كان إماما في الكتابة، والأدب، واللغة، والإعراب، والتاريخ، والفرائض، والحساب، درس الحديث، ونسخ الدواوين الكبار، وضبط كتب اللغة، وقيد على كتب الحديث، واختصر "تفسير الزمخشري" وأزال عنه الاعتزال" (٢).
_________
(١) تاريخ حضرموت ٢: وفيه أسماء المخطوطات التي ترجمت له.
(٢) الإحاطة ٢: ٢٨٧ وفيه: وفاته سنة ٧٦٢ هـ ومثله في نيل الابتهاج ٢٥٤ والإعلام بمن حل مراكش وفاس من الأعلام ٣: ١٠٢. وبغية الوعاة ١: ١٨١ وهدية العارفين ٢: ١٢٨ والعلوم والآداب على عهد =','2','583',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1047,'ابن دقيق العيد [٦٢٥ - ٧٠٢ هـ / ١٢٢٨ - ١٣٠٢ م]

<span data-type="title" id=toc-1440>محمد بن علي بن وهب بن مطيع، أبو الفتح، تقي الدين القشيري، الشهير بابن دقيق العيد:</span>
شيخ الإسلام، قاضي القضاة، من أكابر العلماء بالأصول، مجتهد، أصل أبيه من منفلوط (بمصر) انتقل إلى قوص، وولد له صاحب الترجمة في ينبع على ساحل البحر الأحمر من أرض الحجاز، ونشأ بقوص، ورحل، فأخذ عن علماء دمشق والإسكندرية والقاهرة. وولي قضاء الديار المصرية سنة ٦٩٥ هـ خلفا لابن بنت الأعز، فاستمر إلى أن توفي بالقاهرة. وكان مع غزارة علمه، طريفا، له أشعار، وملح وأخبار. قال ابن حبيب: "كان يحقق مذهبي الشافعي ومالك تحقيقا عظيما، مع معرفته بمذهبه بين المذاهب، والتفسير، والحديث، والأصلين، والنحو، واللغة، وغيرها من العلوم الدقيقة. انتهت إليه رئاسة العلم، وخضع له الأكابر، وعظمه الملوك، الموحدين ١٣٩ وفيه تحقيق وفاته سنة ٦٦٢ خلافا لما وقع في الإحاطة ونيل الابتهاج وجذوة الاقتباس من جعلها ٧٦٢. وانظر كشف الظنون ١٤٨١.','2','584',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1048,'وكان وحيد دهره، وفريد عصره في العلوم العقلية والنقلية .. ". وللأستاذ علي صافي حسين دراسة عنه، وقد ذيلها بمجموعة من شعره. (١).

الصنعاني [... - قبل ٧٠٩ هـ / ... - قبل ١٣٠٩ م]

<span data-type="title" id=toc-1441>محمد بن علي بن أحمد بن يعيش الصنعاني، سابق الدين:</span>
نحوي، مفسر، من أهل صنعاء. له "تفسير القرآن". ولم أعثر على ترجمة وافية له فيما بين يدي الساعة من كتب الرجال (٢).
_________
(١) تذكرة النبيه ١: ١٨٦ و ٢٥٤ والدرر الكامنة ٤: ٣١٠ والوافي ٤: ١٩٣ وتذكرة الحفاظ ١٤٨١ والطالع السعيد ٥٦٧ ودول الإسلام ٢: ١٥٨ والبداية والنهاية ١٤: ٢٧ والبدر الطالع ٢: ٢٢٩ وطبقات الشافعية للسبكي ٦: ٢ ومرآة الجنان ٤: ٢٣٦ والزركشي ٢٩٩ والديباج المذهب ٢: ٣١٨ والنجوم الزاهرة ٨: ٢٠٦ والسلوك ١: ٩٢٩ وشذرات الذهب ٦: ٥ وفوات الوفيات ٣: ٤٤٢ ومفتاح السعادة ٢: ٢١٩ والرسالة المستطرفة ١٣٥ والرد الوافر ٣٠ وطبقات الشاذلية الكبرى ٩٦ والحياة العقلية في عصر الحروب الصليبية ١٦٧ وهدية العارفين ٢: ١٤٥ والمجددون في الإسلام ٢٦٧ ومعجم المؤلفين ١١: ٧٠ والأعلام ٦: ٢٨٣ ومجلة الرسالة ١٧: ٩٠٨ وفي إحكام الأحكام ١: ١٤ - ٤٣ (طبعة مصر ١٣٧٢ هـ) ترجمة واسعة له.
(٢) معجم المؤلفين ١٠: ٣٠٧ نقلا عن بروكلمان','2','584',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1049,'الشامي [٦٧١ - ٧١٥ هـ / ١٢٧٢ - ١٣١٥ م]

<span data-type="title" id=toc-1442>محمد بن علي بن يحيى بن علي الغرناطي، أبو عبد الله، المعروف بالشامي:</span>
نحوي، أديب، شاعر، مفسر، من فقهاء المالكية، من أهل غرناطة بالأندلس، وبها نشأ وتعلم. وحج، وأقام بالحرمين الشريفين مدة، ومات بالمدينة المنورة. قال ابن حجر العسقلاني: "كان يناظر في الفقه على مذهب مالك والشافعي، وله شعر جيد". قلت: أورد له صاحب نفح الطيب قصيدة أنشدها على قبر سيدنا حمزة بن عبد المطلب رضي الله تعالى عنه. من كتبه "تفسير القرآن" و "الإستدراك على التعريف والأعلام فيما أبهم في القرآن من الأسماء والأعلام" (١).

الجذامي [.. - ٧٢٣ م / .. - ١٣٢٣ هـ]

<span data-type="title" id=toc-1443>محمد بن علي بن محمد بن أحمد بن الفخار الأركشي الجذامي، أبو بكر:</span>
مفسر، نحوي، من فقهاء المالكية.
_________
(١) نفح الطيب ٢: ٦٦١ والدرر الكامنة ٤: ٢١٤ وهدية العارفين ٢: ١٤٣ وبغية الوعاة ١: ١٩٣ ومعجم المؤلفين ١١: ٧١','2','585',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1050,'ولد ونشأ في أركش بالأندلس، وخرج منها حين استولى عليها العدو، فاستوطن شريش وتعلم بها، ثم انتقل إلى الجزيرة الخضراء، ومنها إلى مالقة، فاستوطنها إلى أن مات بها. ألف نحو الثلاثين تأليفا في فنون مختلفة، منها "تحبير الجمان في تفسير أم القرآن" (١).

ابن الزملكاني [٦٦٧ - ٧٢٧ هـ / ١٢٦٩ - ١٣٢٧ م]

<span data-type="title" id=toc-1444>محمد بن علي بن عبد الواحد بن عبد الكريم الأنصاري، أبو المعالي، كمال الدين، المعروف بابن الزملكاني:</span>
فقيه، قاض، انتهت إليه رياسة الشافعية في عصره. ولد وتعلم بدمشق. ونسته إلى زملكا من قراها. درس بالعادلية الصغرى وتربة أم الصالح، ثم بالشامية البرانية والظاهرية الجوانية وغيرها. وولي نظر ديوان "الأفرم" ونظر الخزانة ووكالة بيت المال. وكتب في ديوان الإنشاء، وولي
_________
(١) بغية الوعاة ١: ١٨٧ والدرر الكامنة ٤: ١٩٩ وطبقات المفسرين للداودي ٢: ٢٠٩ ووفاته فيه سنة ٧٠٣ والديباج المذهب ٣٠٣ وكشف الظنون ٤٥٥ وهدية العارفين ٢: ١٥٩ وفيه وفات سنة ٧٥٤ هـ. والأعلام ٧: ١٧٥.','2','585',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1051,'قضاء حلب أكثر من سنتين، ثم طلب إلى مصر، فقصدها، فتوفي في بلبيس، ودفن بالقاهرة. من كتبه "البرهان في إعجاز القرآن" و "مختصر البرهان" (١).

ابن المارستاني [٦٤٧ - ٧٢٤ هـ / ١٢٤٩ - ١٣٢٤ م]

<span data-type="title" id=toc-1445>محمد بن علي بن عبد القوي بن عبد الباقي، محيي الدين التنوخي المعري، ثم الدمشقي، المعروف بابن المارستاني:</span>
فقيه حنفي، عارف بالنحو والتفسير، ولد بدمشق، ورحل إلى مصر، واستوطن القاهرة ودرس بها. خرج له الدمياطي مشيخة. قال الصفدي: "كان ورعا زاهدا مفسرا من كبار الحنفية. مات بالقاهرة" (٢).
_________
(١) فوات الوفيات ٤: ٧ وكشف الظنون ٢٤١ والدارس ١: ٣١ والدرر الكامنة ٤: ١٩٢ والوافي ٤: ٢١٤ والبداية والنهاية ١٤: ١٣١ وهدية العارفين ٢: ١٤٦ وذيله العبر ١٥٤ والسبكي ٥: ٢٥١ والشذرات ٦: ٧٨ والبدر السافر ١٣٤ ومفتاح السعادة ٢: ٢١٨ ومرآة الجنان ٤: ٢٧٧ والزركشي ٣٠٣ والقاموس والتاج ومعجم البلدان مادة زملك.
(٢) طبقات المفسرين للداودي ٢: ٢٠٠ والجواهر المضية ٢: ٩٤ والدرر الكامنة ٤: ١٨٧ والوافي ٤: ١١٣.','2','586',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1052,'المصري [... - ٧٤٥ هـ / ... - ١٣٤٤ م]

<span data-type="title" id=toc-1446>محمد بن علي المصري، أبو عبد الله:</span>
نحوي، لغوي، مفسر، عارف بالحديث والفقه والقراءات. قال الخزرجي: "أعاد بالمؤيدية بتعز، ودرس بالمجاهدية بها" (١).

الفخر المصري [... - ٧٤٥ هـ / ... - ١٣٤٤ م]

<span data-type="title" id=toc-1447>محمد بن علي بن إبراهيم بن عبد الكريم، فخر الدين، أبو الفضائل، أبو المعالي، المصري الأصل، الدمشقي، المعروف بالفخر المصري:</span>
محدث، مفسر، أصولي، من فقهاء الشافعية. ولد بمصر، وتحول مع أبيه إلى دمشق وهو صغير، وتعلم بها. وناب في القضاء عن القزويني والقونوي، ثم استعفى سنة ٧٢٩ هـ. ودرس وأفتى، وحج مرارا وجاور في بعضها. قال ابن حجر: "وحصلت له محنة في أيام "تنكز" وانتزعت منه جهاته، ثم أعيدت بعد "تنكز".
_________
(١) بغية الوعاة ١: ١٩٦ وطبقات المفسرين للداودي ٢: ٢١٢ والشذرات ٦: ١٤٤.','2','586',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1053,'وتوفي بدمشق. من آثاره "تفسير القرآن" (١).

ابن النفاش [٧٢٠ - ٧٦٣ هـ / ١٣٢٠ - ١٣٦١ م]

<span data-type="title" id=toc-1448>محمد بن علي بن عبد الواحد بن يحيى بن عبد الرحيم الدكالي، ثم المصري، شمس الدين، أبو أمامة، المعروف بابن النقاش:</span>
فقيه شافعي، واعظ، مفسر، محدث، نحوي. ولد في نصف رجب سنة ٧٢٥، وقيل سنة ٧٢٣، وقيل سنة ٧٢٥ هـ ودرس بعدة مدارس في مصر، ووعظ بجامع دمشق، وتوفي بالقاهرة. من تصانيفه "السابق اللاحق" تفسير مطول، التزم فيه أن لا ينقل حرفا من تفسير أحد ممن تقدمه، وذكر في أوله أن الحامل له عليه أنه شرع في إلقاء التفسير في الجامع الأزهر في شهر رمضان فأكمله، فبلغه أن بعض الناس استقصر علمه، فشرع في إملاء تفسير على الفاتحة، فأقام فيه مدة طويلة، ثم شرع في كتابه "السابق اللاحق" (٢).
_________
(١) الدرر الكامنة ٤: ١٧٠ والشذرات ٦: ١٧٠ وهدية العارفين ٢: ١٥٩، وفيه أن له تفسير القرآن.
(٢) الدرر الكامنة ٤: ١٩٥ وبغيه الوعاة ١: ١٨٣ والشذرات ٦: ١٩٨ وطبقات المفسرين للداودي ٢: =','2','587',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1054,'البلنسي [٧٢٤ - ٧٨٢ هـ / ١٣٢٤ - ١٣٨٠ م]

<span data-type="title" id=toc-1449>محمد بن علي بن أحمد بن محمد الأوسي البلنسي، ثم الغرناطي، أبو عبد الله:</span>
نحوي، مفسر، من علماء غرناطة. أثنى عليه لسان الدين بن الخطيب وقال: "وجرت له محنة مع السلطان، ثم صفح عنه لحسن تلاوته". له "تفسير القرآن" كبير جدا، وكتاب في "مبهمات القرآن" (١).

ابن اليونانية [٧٠٧ - ٧٩٣ هـ / ١٣٠٧ - ١٣٩١ م]

<span data-type="title" id=toc-1450>محمد بن علي بن أحمد بن محمد اليونيني البعلي، شمس الدين، المعروف بابن اليونانية:</span>
قاض،
_________
= ٢٠٠ والنجوم الزاهرة ١١: ١٣ والبداية والنهاية ١٤: ٢٩٢ وذيل العبر ٣٤٩ والبدر الطالع ٢: ٢١١ وكشف الظنون ٤٤٠ و ٩٧٣ وهدية العارفين ٢: ١٦٢ ومعجم المؤلفين ١١: ٢٦ والأعلام ٧: ١٧٧.
(١) طبقات المفسرين ٢: ٢١١ وبغية الوعاة ١: ١٩٨ والدرر الكامنة ٤: ٢٠٧ ونيل الابتهاج ٢٧٠ وكشف الظنون ٤٢١ و ٤٠٤ وفيه وفاته سنة ٧١٥ هـ، وبروكلمان: الذيل ٢: ٣٧٧ وفيه وفاته سنة ٧٤٦ ومعجم المؤلفين ١٠: ٣٠٥ و ١١: ٤٦.','2','587',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1055,'مفسر، من فقهاء الحنابلة، من أهل بعلبك، وولي قضاءها سنة ٧٨٩ هـ. له "مختصر تفسير ابن كثير" في أربع مجلدات (١).

ابن نور الدين [... - نحو ٨٢٠ هـ / ... - نحو ١٤١٧ م]

<span data-type="title" id=toc-1451>محمد بن علي بن عبد الله بن إبراهيم الخطيب، أبو عبد الله، الشهير بابن نور الدين، ويعرف بالموزعي:</span>
مفسر، عالم بالأصول، نسبته إلى "موزع" كمجمع، قرية باليمن على طريق الحاج من عدن. قال السخاوي: "جرت له مع صوفية وقته أمور بان فيها فضله، ومات في حدود العشرين". من آثاره "تيسير البيان لأحكام القرآن" مخطوط، المجلد الأول منه بالبصرة، في ٥٠٠ صفحة، قال صاحب هدية العارفين: "فرغ منه سنة ٨٠٨ هـ (٢).
_________
(١) شذرات الذهب ٦: ٣٣١ والدرر الكامنة ٤: ١٧٥ وفيه: مات سنة ٧٨٣ هـ وهدية العارفين ٢: ١٧٤ والأعلام ٧: ١٧٨ ومعجم أعلام لبنان للمؤلف.
(٢) إيضاح المكنون ١: ٣٤٣ وهدية العارفين ٢: ١٧٨ والضوء اللامع ٨: ٢٢٣ والأعلام ٦: ٢٨٧ ومعجم المؤلفين ١١: ٢٤.','2','588',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1056,'ابن هلال [... - ٩٣٣ هـ / ... - ١٥٢٧ م]

<span data-type="title" id=toc-1452>محمد بن علي، شمس الدين، المعروف بابن هلال، العرضي الأصل، ثم الحلبي:</span>
مفسر، نحوي، متكلم، ناظم، أخذ بحلب عن العلاء الموصلي وغيره، وانتقل إلى القاهرة، فأخذ عن الشيخ خالد بن عبد الله الأزهري (٨٣٨ - ٩٠٥ هـ) ولازمه مدة طويلة، ثم عاد إلى حلب، ودرس بجامعها، وتوفي فيها. له شعر فاحش الهجو. من آثاره "حاشية" على تفسير البيضاوي (١).

القره باغي [... - ٩٣٢ هـ / ... - ١٥٣٥ م]

<span data-type="title" id=toc-1453>محمد بن علي القره باغي، محيي الدين، الرومي:</span>
مفسر، محدث، أصولي، من فقهاء الحنفية، أصله من بلاد العجم، انتقل إلى بلاد الروم، واشتغل بالتدريس في أزنيق وغيرها،
_________
(١) الشذرات ٨: ٢٠٠ والكواكب السائرة ١: ٦٨ وإيضاح المكنون ١: ١٣٩ وهدية العارفين ٢: ٢٣٢ وأعلام النبلاء ٥: ٦١، والأعلام ٧: ١٨٢ ومعجم المؤلفين ١١: ٦٩.','2','588',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1057,'ومات وهو مدرس بها. قال طاشكبري زاده: "كان عالما، فاضلا، كاملا، له معرفة تامة بالتفسير". من آثاره "حاشية" على تفسير البيضاوي، و "حاشية" على الكشاف، وتفسير قوله تعالى: {يوم يأتي بعض آيات ربك" (١).

الداودي [... - ٩٤٥ هـ / ... - ١٥٣٩ م]

<span data-type="title" id=toc-1454>محمد بن علي بن أحمد، شمس الدين الداودي:</span>
شيخ أهل الحديث في عصره، من علماء الشافعية، قيل: وكان مالكيا، عاش في القاهرة، وتلمذ للحافظ المؤرخ العالم جلال الدين السيوطي. ذكره الغزي وقال: أثنى عليه المسند العلامة جمال الدين بن نهد، وشيخ الإسلام الوالد". توفي بالقاهرة ودفن بتربة فيروز بالصحراء خارج باب النصر. من آثاره "طبقات المفسرين" في مجلدين، حققه الأستاذ علي محمد عمر، وطبع بمصر سنة ١٣٩٢ هـ
_________
(١) هدية العارفين ٢: ٢٣٦ وإيضاح المكنون ١: ١٤١ و ٢: ٣٥٣ والشقائق ٢٧٢ والشذرات ٨: ١٥٠ والكواكب السائرة: ٢: ٧٠.','2','589',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1058,'(١٩٧٢ م) و "الاتحاف بتمييز ما تبع فيه البيضاوي صاحب الكشاف" (١).

ابن طولون [٨٨٠ - ٩٥٣ هـ / ١٤٧٥ - ١٥٤٦ م]

<span data-type="title" id=toc-1455>محمد بن علي بن أحمد (المدعو محمد) بن علي بن خمارويه بن طولون الدمشقي الصالحي، شمس الدين، أبو عبد الله:</span>
مؤرخ، محدث، مسند، فقيه حنفي، نحوي، مشارك في التعبير والطب وغيرها من العلوم، وله نظم. ولد بصالحية دمشق بالسهم الأعلى قرب المدرسة الحاجبية، وتفقه بعمه الجمال بن طولون وغيره. ولي تدريس الحنفية بمدرسة أبي عمر، وإمامة السليمية بالصالحية. قال في الكواكب السائرة: "كانت أوقاته معمورة كلها بالعلم والعبادة ... وكتب بخطه كثيرا من الكتب وعلق ستين جزءا سماها "التعليقات" أكثرها من جمعه، وبعضها من تأليفات شيخه السيوطي. وكان واسع الباع في غالب
_________
(١) شذرات الذهب ٨: ٢٦٤ والأعلام ٧: ١٨٤ والكواكب السائرة ٩: ٧١ ومعجم المؤلفين ١٠: ٣٠٤ وطبقات المفسرين، مقدمة المحقق علي محمد عمر. وكشف الظنون ٢: ١١٠٧.','2','589',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1059,'العلوم المشهورة. مات بدمشق". من آثاره "رسالة" في تفسير قوله سبحانه وتعالى: {الرحمن على العرش استوى} أولها "الحمد لله الذي استواؤ .. " (١).

الشطيبي [٨٨٢ - ٩٦٣ هـ / ١٤٧٧ - ١٥٥٦ م]

<span data-type="title" id=toc-1456>محمد بن علي بن محمد بن حسن الصقلي الأندلسي البرجي، أبو عبد الله، المعروف بالحاج الشطيبي:</span>
مؤرخ، مفسر، رحل في طلب العلم، وحج. مات في "تازغدرة" من جبال غمارة في ريف المغرب. من تصانيفه "اللباب في مشكلات الكتاب"، في التفسير، منه نسخة في قبيلة بني زروال بالمغرب، وثانية سقيمة في خزانة الرباط (٣٣٦ أوقاف) (٢).
_________
(١) شذرات الذهب ٨: ٢٩٨ والكواكب السائرة ٢: ٥٢ وفهرس الفهارس ٢: ٢٨٩ وهدية العارفين ٢: ٢٤٠ ومجلة معهد المخطوطات ٢: ١٣٩ و ٥: ١٥٨ والقلائد الجوهرية مقدمته بقلم محمد أحمد دهمان.
(٢) درة الحجال ١: ٢٤٧ وفيه وفاته سنة ٩٦٠ هـ وقبيلة بني زروال ٦٤ ودوحة الناشر ١٤ واسمه فيها محمد بن أحمد بن محمد بن علي، ومثله في مناقب الحضيكي. ودليل مؤرخ المغرب ١: ١٥٦ وهو فيه: محمد بن علي بن عطية البرجي، المشهور بالحاج =','2','590',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1060,'الخروبي [نحو ٨٩٠ - ٩٦٣ هـ / ١٤٨٥ - ١٥٥٦ م]

<span data-type="title" id=toc-1457>محمد بن علي الخروبي، أبو عبد الله، الطرابلسي، السفاقسي، ثم الجزائري:</span>
من مشاهير فقهاء المالكية في وقته، ولد بقرية قرقارش (من قرى طرابلس الغرب وأصبحت الآن جزءا منها) ومات أبوه وهو صغير، فربته أمه. تعلم بطرابلس ومصراته، وأقام مدة في صفاقس وأخرى بمدينة تونس. ثم دخل الجزائر (نحو سنة ٩٣٤ هـ) فأقام بها، وولي الخطابة بجامعها. ونال شهرة واسعة. وفي سنة ٩٥٩ هـ دخل مراكش سفيرا بين سلطان آل عثمان وملك المغرب من السعديين الشيخ المهدي، للمهادنة بينهما. ثم دخلها مرة ثانية سنة ٩٦١ هـ للغرض نفسه. توفي بمدينة الجزائر. من آثاره "رياض الأزهار وكنز الأسرار" في التفسير، كبير في ٨ مجلدات، مخطوط، في مكتبة طلعت رقم ٣٦٤، بدار الكتب المصرية (١).
_________
= الشطيبي، ووفاته فيه سنة ٩٦٣ هـ، والأعلام ٧:
(١) من رسالة بعث بها إلى المؤلف الأستاذ عبد الحق ابن محمود من المغرب. ومعجم أعلام الجزائر (ط ٢)','2','590',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1061,'الإسترابادي [... - ١٠٢٨ هـ / ... - ١٦١٩ م]

<span data-type="title" id=toc-1458>محمد بن علي بن إبراهيم الإسترابادي، المعروف بالميرزا محمد:</span>
عالم بالتراجم، محدث، مفسر، فقيه إمامي، من أهل استرباذ (من أعمال طبرستان) توفي بمكة. من آثاره "تفسير آيات الأحكام" مخطوط (١).

شيخ زاده [... - ١٠٥٥ هـ / ... - ١٦٤٥ م]

<span data-type="title" id=toc-1459>محمد بن علي الوارداري الرومي، أبو النور، المعروف بشيخ زاده:</span>
فقيه حنفي، مشارك في بعض العلوم. من
_________
= ١٣٢ وتعريف الخلف ٢: ٤٨٣ والمنهل العذب ٢٠٠ وطبقات الحضيكي ٢: ٣١ ونفحات النسرين والريحان ١١٦ والاستقصا ٥: ٣١ وشجرة النور ٦٨٤ وأعلام ليبيا ٢٨٦ وفيه أن المؤلف رأى نسخة التفسير، وفي نهاية الجزء الثامن منها ما يثير إلى أنه بخطه، أنجزه في غرة ربيع الثاني ٩٦٤ هـ. وعلق بأن هذا لا يتفق مع تاريخ وفات المعروف ٩٦٣ هـ. والأعلام ٦: ٢٩٢ ومرآة المحاسن ٢٠٩ وهدية العارفين ٢: ٢٤٥ ومعجم المؤلفين ١١: ٦.
(١) خلاصة الأثر ٤: ٤٦ وسلافة العصر ٤٩٩ والذريعة ١: ٤٣ وهدية العارفين ٢: ٢٧١ وفوائد الرضوية ٥٥٤ والأعلام ٧: ١٨٦ ومعجم المؤلفين ١٠: ٢٩٨.','2','591',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1062,'أحفاد الأمير أورنوس. له "تفسير القرآن" إلى سورة المائدة، و "تعاليق" على التفاسير (١).

ابن علان [٩٩٦ - ١٠٥٧ - هـ / ١٥٨٨ - ١٦٤٧ م]

<span data-type="title" id=toc-1460>محمد علي بن محمد علان بن إبراهيم ابن محمد البكري الصديقي:</span>
مفسر، محدث، مشارك في عدة علوم، من أهل مكة، نشأ وتوفي بها. قال المحبي: "ألف كتبا كثيرة تزيد على الستين" منها "ضياء السبيل إلى معالم التنزيل" في التفسير، و "رفع الإلتباس ببيان اشتراك معاني الفاتحة وسورة الناس" (٢).

التفسيري [... - ١٠٧٧ هـ / ... - ١٦٦٦ م]

<span data-type="title" id=toc-1461>محمد بن علي الرومي، المعروف بالتفسيري:</span>
فقيه حنفي، قاض، مفسر، ولي القضاء بأسكوب. له "رسائل على أنوار التنزيل"
_________
(١) هدية العارفين ٢: ٢٨١.
(٢) خلاصة الأثر ٤: ١٨٤ وهدية العارفين ٣: ٢٨٣ والأعلام ٧: ١٨٧ ومعجم المؤلفين ١١: ٥٤، ١٧٨ وج ٨: ١١٧ وكشف الظنون ١٠٩١.','2','591',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1063,'للبيضاوي (١).

الحصكفي [١٠٢٥ - ١٠٨٨ هـ / ١٦١٦ - ١٦٧٧ م]

<span data-type="title" id=toc-1462>محمد بن علي بن محمد بن علي بن عبد الرحمن الحصني المعروف بعلاء الدين الحصكفي:</span>
فقيه حنفي، نحوي، أصولي، من أهل دمشق، ونسبته إلى حصن كيفا في ديار بكر. أخذ عن علماء دمشق والرملة وبيت المقدس والمدينة المنورة، وتولى إفتاء الحنفية بدمشق، وتوفي بها. من كتبه "تعليقة" على أنوار التنزيل، في التفسير، للبيضاوي (٢).

نيازي المصري [... - ١١٠٥ هـ / ... - ١٦٩٤ م]

<span data-type="title" id=toc-1463>محمد بن علي الملاطي، الملقب بنيازي المصري:</span>
صوفي، مفسر، من مشايخ الخلوتية. توفي بجزيرة لمنى. من كتبه
_________
(١) هدية العارفين ٢: ٢٩١ ومعجم المؤلفين
(٢) خلاصة الأثر ٤: ٦٣ وهدية العارفين ٢: ٢٩٥ وإيضاح المكنون ١: ١٤٠ وفهرس الفهارس ١: ٢٥٧ ومعجم المطبوعات ٧٧٨.','2','592',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1064,'"تفسير سورة الفاتحة" و "تفسير سورة يوسف" (١).

النجار [... - ١١٤٠ هـ / ... - ١٧٢٨ م]

<span data-type="title" id=toc-1464>محمد بن علي النجار التستري:</span>
فاضل إمامي، مفسر، واعظ، من أهل تستر، سافر إلى أصبهان وغيرها، وأخذ عن جماعة من فقهاء الإمامية. له "تفسير القرآن" (٢).

التهانوي [... - بعد ١١٨٥ هـ / ... - ١٧٤٥ م]

<span data-type="title" id=toc-1465>محمد بن علي بن محمد حامد بن محمد صابر الفاروقي الحنفي التهانوي:</span>
باحث هندي، من كتبه "سبق الغايات في نسق الآيات" طبع بالهند سنة ١٣١٦ هـ (٣).

الشيخ علي الحزين [١١٠٣ - ١١٨١ هـ / ١٦٩٢ - ١٧٦٧ م]

<span data-type="title" id=toc-1466>محمد علي بن أبي طالب بن عبد الله بن (علي، المعروف بالشيخ علي الحزين، الزاهدي الجيلاني، أبو المعالي)</span>
_________
(١) هدية العارفين ٢: ٣٠٥ ومعجم المؤلفين ١١: ٦٤
(٢) أعيان الشيعة ٤٦: ١١٢ ومعجم المؤلفين ١١:
(٣) معجم المطبوعات ٦٤٥ وهدية العارفين ٢: ٣٢٦ وبروكلمان: الذيل ٢: ٦٢٨ وتاريخ آداب اللغة العربية ٣: ٣٥٤ والأعلام ٧: ١٨٨ والكتخانة ٤: ١٧٩.','2','592',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1065,'علي، المعروف بالشيخ علي الحزين، الزاهدي الجيلاني، أبو المعالي: فاضل إمامي، لغوي، مشارك في بعض العلوم، ولد بأصبهان، وتوفي ببنارس بالهند. من كتبه "شجرة الطور في شرح آيات النور"مخطوط" (١).

الشوكاني [١١٧٣ - ١٢٥٠ هـ / ١٧٦٠ - ١٨٣٤ م]

<span data-type="title" id=toc-1467>محمد بن علي بن محمد بن عبد الله بن الحسن بن محمد الشوكاني، أبو عبد الله:</span>
فقيه، أصولي، محدث، مفسر، من كبار علماء اليمن، من أهل صنعاء. ولد ب "هجرة شوكان" - من بلاد خولان باليمن - ونشأ وتعلم بصنعاء"، وولي قضاءها سنة ١٢٢٩ ومات بها. له أكثر من مئة كتاب منها "فتح القدير الجامع بين فني الرواية والدراية من علم التفسير" طبع، خمسة مجلدات، و "مطلع البدرين ومجمع البحرين" في التفسير أيضا، و "جواب السائل في تفسير {والقمر قدرناه منازل ..} " (٢).
_________
(١) الذريعة ١: ٣١٥ و ٣١٧ والأعلام ٧: ١٨٩ وهدية العارفين ٢: ٢٩٤ ومولده فيه سنة ١٠١٣ ووفاته سنة ١٠٨٤، وبروكلمان: الذيل ٢: ٦١٣ ومعجم المؤلفين ١١: ١٩ نقلا عن هدية العارفين.
(٢) البدر الطالع ٢: ٢١٤ وهدية العارفين ٢: ٣٦٥ =','2','593',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1066,'الهندي [... - ١٢٦٠ هـ / ... - ١٨٤٤ م]

<span data-type="title" id=toc-1468>محمد علي قلي بن محمد حسين الموسوي النيسابوري الكنتوري الهندي:</span>
فاضل إمامي، مشارك في بعض العلوم، من كتبه "تقريب الأفهام في تفسير آيات الأحكام" (١).

القراجه داغي [... - حيا ١٣٠٦ هـ / ... - حيا ١٨٨٩ م]

<span data-type="title" id=toc-1469>محمد علي القراجه داغي:</span>
فقيه إمامي، مفسر، دخل العراق وأخذ عن علماء النجف. ورجع إلى إيران وأقام بتبريز. من كتبه "تفسير القرآن" كبير (٢).

النجفي [... - ١٣٣٤ هـ / ... - ١٩١٦ م]

<span data-type="title" id=toc-1470>محمد علي بن ميرزا محمد الشاه عبد العظيمه النجفي:</span>
فقيه إمامي، مفسر،
_________
= وإيضاح المكنون ٢: ١٦٩ و ٥٠٠ والتاج المكلل ٣٠٥ والرسالة المستطرفة ١١٤ ونيل الوطر ٢: ٢٩٧ وفهرس الفهارس ٢: ٤٠٨ وأبجد العلوم ٨٧٧ والأعلام ٧: ١٩١ ومعجم المؤلفين ١١: ٥٣.
(١) أعيان الشيعة ٤٦: ١٦١.
(٢) أعيان الشيعة ٤٦: ١٠٠.','2','593',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1067,'تعلم بالنجف، ومات في طويج بين النجف وكربلاء. له تصانيف، منها "منتخب التفسير في غريب القرآن" (١).

الشهرستاني [١٢٨٠ - ١٣٤٦ هـ / ١٨٦٣ - ١٩٢٧ م]

<span data-type="title" id=toc-1471>محمد علي بن محمد حسين بن محمد علي الشهرستاني:</span>
فقيه إماما، أصولي، مفسر، من أهل كرباء مولدا ووفاة. من كتبه "التبيان في تفسير غرائب القرآن" (٢).

المرزوقي [... - ١٣٥٥ هـ / ... - ١٩٣٦ م]

<span data-type="title" id=toc-1472>محمد عليان المرزوقي الشافعي:</span>
متكلم، مفسر، ولد في كفر علي غالي التابعة لمركز منيا القمح بالشرقية بمصر، وتعلم بالأزهر، وتوفي بالقاهرة. من كتبه "مشاهد الإنصاف على شواهد الكشاف" حاشية على تفسير الكشاف، للزمخشري (٣).
_________
(١) الذريعة ١: ٥٠٢ وأعيان الشيعة ٤٦: ٧٨ والأعلام ٧: ١٩٥ ومعجم المؤلفين ١١: ٦٠
(٢) أعيان الشيعة ٤٦: ١٤٥.
(٣) الأعلام الشرقية ٢: ١٧٣ ومعجم المطبوعات ١٦٣٤ وهو فيه محمد أبو عليان، ومعجم المؤلفين ١١: ٧٣ والأعلام ٧: ١٩٩.','2','594',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1068,'الواقدي [١٣٠ - ٢٠٧ هـ / ٧٤٧ - ٨٢٣ م]

<span data-type="title" id=toc-1473>محمد بن عمر بن واقد السهمي الأسلمي بالولاء، المدني، الواقدي، أبو عبد الله:</span>
مؤرخ، حافظ، قاض، حجة في الفقه والحديث، ولد بالمدينة، وروى عن سفيان الثوري وابن جريج وغيرهما، وعنه ابن ماجه. تاجر بالحنطة، وضاعت ثروته؛ فانتقل إلى العراق سنة ١٨٠ هـ في أيام هارون الرشيد، واتصل بيحيى بن خالد البرمكي، فأفاض عليه عطاياه، وقربه من الخليفة، فولي قضاء الجانب الرقي من بغداد. وكان المأمون يكرم جانبه، ويبالغ في رعايته، وولاه (سنة ٢٠٤ هـ) القضاء بعسكر المهدي، فاستمر إلى أن توفي. قال إبراهيم الحربي: "كان الواقدي أعلم الناس بأمر الإسلام، فأما الجاهلية فلم يعمل فيها شيئا". وقال الخطيب البغدادي: "هو ممن طبق شرق الأرض وغربها ذكره، ولم يخف على أحد عرف أخبار الناس أمره، وسارت الركبان بكتبه في فنون العلم، من المغازي والسير، والطبقات وأخبار النبي (ص) وكتب الفقه، واختلاف الناس في الحديث، وكان جوادا كريما مشهورا بالسخاء" وذكره الحافظ الذهبي وقال: "اتفق على','2','594',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1069,'ترك حديثه وهو من أوعية العلم، لكنه لا يتقن الحديث، وهو رأس في المغازي والسير ويروي عن كل ضرب". والواقدي نسبة إلى واقد وهو جده. من كتبه "تفسير القرآن" (١).

البصري [... - ٣٠٠ هـ / ... - ٩١٢ م]

<span data-type="title" id=toc-1474>محمد بن عمر بن سعيد الباهلي البصري:</span>
من كبار المعتزلة. قال ابن حجر: "كان له مجلس للصوفية، وكان رقيق العبارة" وقال الداودي: "مولده بالبصرة ومنشؤه بها، كان حسن الاضطلاع بصناعة الكلام على مذهب البصريين، وحكي أن أبا علي (الجبائي). كان يحضر مجلسه، له من الكتب "إعجاز القرآن" (٢).
_________
(١) تاريخ بغداد ٣: ٣ ومعجم الأدباء ١٨: ٢٧٧ وأخبار القضاة ٣: ٢٧٠ وتهذيب التهذيب ٩: ٣٦٣ وعبر الذهبي ١: ٣٥٣ وشذرات الذهب ٢: ١٨ وطبقات ابن سعد ٧: ٣٣٤ والوافي بالوفيات ٤: ٢٣٨ وميزان الاعتدال ٣: ٦٦٢ ووفيات الأعيان ٤: ٣٤٨ وتاريخ جرجان ٢٠٧ والبداية والنهاية ١٠: ٢٦١ ومختصر دول الإسلام ١: ٩٩ وأعيان الشيعة ٤٦: ١٧٠ والمختصر في أخبار البشر ٢: ٣٠ ومرآة الجنان ٢: ٣٦ ونور القبس ٣١١ وعيون الأثر ١: ١٧ والمغازي الأولى ومؤلفها ١٠١ والأعلام ٧: ٢٠٠ وهدية العارفين ٢: ١٠ وكشف الظنون ٤٦٠ والديباج المذهب ٢: ١٦١ وترتيب المدارك ٢: ٤٠٤ ومصفى المقال ٤٢١ ومعجم المؤلفين ١١: ٩٥ وروضات الجنات ١٥٦ والفهرست لابن النديم ١: ٩٨.
(٢) طبقات المفسرين للداودي ٢: ٢١٧ ولسان الميزان ٥: ٣٢٠ وفضل الاعزال ٣١٠.','2','595',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1070,'المديني [٥٠١ - ٥٨١ هـ / ١١٠٨ - ١١٨٥ م]

<span data-type="title" id=toc-1475>محمد بن عمر بن أحمد بن عمر بن محمد الأصبهاني المديني، أبو موسى:</span>
من حفاظ الحديث، المصنفين فيه. ونسبة "المديني" إلى مدينة أصبهان، ومولده ووفاته فيها. زار بغداد وهمذان. قال الصفدي: "كان واسع الدائرة في معرفة الحديث وعلله وأبوابه ورجاله وفنونه، لم يكن في وقته أعلم منه ولا أحفظ ولا أعلى سندا، وروى عنه جماعة من الحفاظ". من كتبه "المغيث" أكمل به كتاب الغريبين للهروي (١).

السيغي [... - ... / ... - ...]

<span data-type="title" id=toc-1476>محمد بن عمر السيغي، أبو بكر:</span>
مفسر كبير، نسبته إلى سيغ - ويقال صيغ - ناحية بخراسان. ذكره الذهبي ولم يؤرخ وفاته (٢)
_________
(١) تذكرة الحفاظ ١٣٣٤ وطبقات السبكي ٤: ٩٠ والوافي ٤: ٢٤٦ والعبر. ووفيات الأعيان ٤: ٢٨٦ وغاية النهاية ٢: ٢١٥ وشذرات الذهب ٤: ٢٧٣ والأعلام ٧. ٢٠٢ ومعجم المؤلفين ١١: ٧٦ وكتاب الروضتين ٢: ٦٨ ومرآة الجنان ٣: ٤٢٣ وهدية العارفين ٢: ١٠٠.
(٢) تاج العروس ٦: ١٨ والقاموس المحيط ٣: ١٠٨ والمشتبه ١: ٣٥٢ وتبصير المنتبه ٢: ٧٢٥ والإكمال لابن ماكولا ٤: ٤٩٦ وطبقات المفسرين للداودي: ٢٢٠.','2','595',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1071,'الفخر الرازي [٥٤٤ - ٦٠٦ هـ / ١١٥٠ - ١٢١٠ م]

<span data-type="title" id=toc-1477>محمد بن عمر بن الحسين بن الحسن بن علي التيمي البكري، أبو عبد الله، فخر الدين الرازي:</span>
الإمام المفسر المتكلم، أوحد زمانه في المعقول والمنقول وعلوم الأوائل: لقب بشيخ الإسلام. من ذرية أبي بكر الصديق. أصله من طبرستان. ولد في الري، وإليها نسبته. ويقال له ابن خطيب الري. رحل إلى خوارزم وما وراء النهر وخراسان، وأتقن علوما كثيرة وبرز فيها. وكان شافعيا أشعريا، ناظر المعتزلة، واشتغل بالتدريس في الحيرة. وانقطع في أواخر أيامه للوعظ فكان يحضر مجلسه الخاص والعام. له مصنفات كثيرة أقبل الناس عليها في حياته يتدارسونها. وتوفي في هراة، وخلف تركة ضخمة. من كتبه "مفاتيح الغيب" ثماني مجلدات في تفسير القرآن الكريم، و "ترجع شهرة الرازى ومكانته في تاريخ الفكر الإسلامي إلى هذا الكتاب، إذ جمع بين المباحث الكلامية والفلسفية والدينية، ورد فيه على تأويلات المعتزلة للقرآن، وضمنه محاولته في التوفيق بين الفلسفة والدين" وله أيضا "نهاية الإيجاز في دراية الإعجاز" في علوم البلاغة وبيان إعجاز القرآن" طبع، و','2','596',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1072,'"مفاتيح العلوم" في تفسير الفاتحة، مخطوط، مجلدان، و "تفسير سورة الإخلاص" مخطوط، و "أسرار التنزيل وأنوار التأويل" قال صاحب كشف الظنون: "في مجلد، أوله: الحمد لله الذي أظهر من آثار سلطانه .. الخ، ذكر فيه أنه على أربعة أقسام: في الأصول، في الفروع، في الأخلاق، في المناجات والدعوات، لكنه توفي قبل إتمامه، فبقي في أواخر القسم الأول" ويقول الأستاذ الزركان: "وهذا القسم الذي أنجزه كله في الآيات القرآنية الدالة على وجود الله وحكمته، ومحاولة فلسفتها. منه نسخ خطية في القاهرة وإستانبول. و "تفسير سورة البقرة" على الوجه العقلي لا النقلي، و "رسالة في التنبيه على بعض الأسرار المودعة في بعض آيات القرآن الكريم"، قال آغا بزرك الطهراني: "وتفسيره هذا في أربعة مطالب، رأيت النسخة في مجموعة من كتب الشيخ عبد الحسن الحلي. أوله: تفسير التوحيد في الإخلاص، ثم تفسير الإلهيات والنبوات والمعاد في سورة الأعلى، ثم في تقرير المعاد في سورة التين، ثم في ضبط الأعمال في سورة العصر. وآخره: فقد ذكرت لك تفسير هذه السور الأربعة في المطالب الأربعة" (١).
_________
(١) طبقات المفسرين للداودي ٢: ٢١٤ وطبقات =','2','596',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1073,'ابن مغايظ [٥٥٨ - ٦٣١ هـ / ١١٦٣ - ١٢٣٤ م]

<span data-type="title" id=toc-1478>محمد بن عمر بن يوسف، أبو عبد الله القرطبي، المعروف بابن مغايظ:</span>
مقرىء معروف، مفسر، نحوي، من فقهاء المالكية. ولد بقرطبة، ونشأ بفاس، وحج وسمع بمكة والإسكندرية. ومات بمصر. قال الحافظ الذهبي: "كان إماما صالحا، زاهدا مجودا للقراءات، عارفا بوجوهها، بصيرا بمذهب مالك، حاذقا بفنون العربية، وله يد طولى في "التفسير" (١). المفسرين للسيوطي ٣٩ وطبقات الشافعية للسبكي ٨: ٨١ والوافي بالوفيات ٤: ٢٨٤ وميزان الاعتدال ٣: ٣٤٠ ولسان الميزان ٤: ٤٢٦ ووفيات الأعيان ٤: ٢٤٨ والبداية والنهاية ١٣: ٥٥ وذيل الروضتين ٦٨ وعبر الذهبي ٥: ١٨ وشذرات الذهب ٥: ٢١ والنجوم الزاهرة ٦: ١٩٧ وهدية العارفين ٢: ١٠٧ وكشف الظنون ٨٣، ٤٤٧، ٤٤٩، ٤٥٤ والموسوعة العربية الميسرة ٨٥٢ وتاريخ مختصر الدول ٢٤٠ و ٢٥٤ ومرآة الجنان ٤: ٧ وطبقات الشافعية لابن هداية الحسيني (بتحقيقنا) ٢١٦ وتاريخ آداب اللغة العربية ٣: ١٠٢ وتاريخ الأدب في إيران ٦١٥ وتاريخ الحكماء للقفطي ٢٩١ والجامع المختصر ٩: ٣٠٦ والمذاهب الإسلامية في تفسير القرآن ١٢٢ ومجلة لواء الإسلام س ٣ عدد ٩ ص ٦٥ وإلى طه حسين في عيد ميلاده السبعين ١٩٣ والتفسير والمفسرون ١: ٢٩٠ وكتاب "فخر الدين الرازي وآراؤه الكلامية والفلسفية" للأستاذ محمد صالح الزركان، وفيه ثبت بمصادر أخرى كثيرة، وهو أوفى كتاب في سيرة الرازي طبع حتى اليوم. وانطر أيضا الأعلام ٧: ٢٠٣ ومعجم المؤلفين ٧٩: ١١.
_________
(١) طبقات المفسرين للداودي ٢: ٢١٩ وطبقات =','2','597',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1074,'ابن رشيد [٦٥٧ - ٧٢١ هـ / ١٢٥٩ - ١٣٢١ م]

<span data-type="title" id=toc-1479>محمد بن عمر بن محمد بن عمر بن محمد ابن إدريس بن سعيد، أبو عبد الله، محب الدين، المعروف بابن رشيد الفهري السبتي:</span>
رحالة، محدث، نحوي، لغوي، حافظ للأخبار والتواريخ والسير، عارف بالقراءات السبع، خطيب، مفسر، من أهل سبتة، ودرس بها على أبي الحسين بن أبي الربيع النحوي، ثم أخذ عن علماء إفريقية (تونس) والأندلس. ورحل إلى المشرق لأداء فريضة الحج سنة ٦٨٣ هـ، فدخل إفريقية ومصر والشام وأخذ بها وبالحجاز عمن لقي من الأئمة. وعاد إلى سبتة، عن طريق الأندلس، سنة ٦٨٦ هـ، فأقام بها حتى سنة ٦٩٢ هـ، حين دعاه صديقه الوزير أبو عبد الله ابن الحكيم إلى غرناطة، فانتقل إليها وولي الإمامة والخطابة بجامعها الأعظم، ثم قضاء الأنكحة، واستمر إلى أن اغتيل الوزير ابن الحكيم عام ٧٠٨ هـ، فعاد إلى المغرب وأقام بمراكش، وقدم للصلاة والخطبة بجامعها العتيق، ثم
_________
= القراء للذهبي ٢: ٥١٥ وغاية النهاية ٢: ٢١٩ وبغية الوعاة ١: ٢٠١ وشذرات الذهب ٥: ١٤٥ والوافي بالوفيات ٤: ٢٦١ وعبر الذهبي ٥: ١٢٥ والنجوم الزاهرة ٦: ٢٨٧.','2','597',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1075,'استدعاه السلطان أبو سعيد المريني إلى فاس، وصار من خواصه بها، وأقام على ذلك إلى أن توفي. وصفه ابن خلدون "بكبير مشيخة المغرب، وسيد أهله، شيخ المحدثين الرحالة". وأثنى عليه لسان الدين ابن الخطيب، وابن القاضي في درة الحجال، وابن حجر العسقلاني وغيرهم. له تآليف كثيرة أهمها وأعظمها قيمة علمية رحلته إلى المشرق التي سماها "ملء العيبة فيما جمع بطول الغيبة في الوجهتين الكريمتين إلى مكة وطيبة" (١).

القلشاني [٨١٧ - ٨٩٠ هـ / ٨٩٢ - ١٤٨٥ م]

<span data-type="title" id=toc-1480>محمد بن عمر بن محمد بن عبد الله بن خلف الله بن عبد السلام، أبو عبد الله، القلشاني - وقيل: القلجاني </span>-:
فقيه مالكي، عارف بالتفسير والنحو، من أهل
_________
(١) الوافي بالوفيات ٤: ٢٨٤ وسلوة الأنفاس ٢: ١٩١ وجذوة الاقتباس ١٨٠ والدرر الكامنة ٤: ٢٢٩ وبغية الوعاة ١: ١٩٩ وذيل العبر ١٢١ وذيل طبقات الحفاظ ٩٧ والديباج المذهب ٢: ٢٩٧ وطبقات المفسرين للداودي ٢: ٢١٧ وغاية النهاية ٢: ٢١٩ ومجرة النور ١: ٢١٦ ودرة الحجال ٢: ٩٦ والنبوغ المغربي ١: ٢٠٦ وابن رشد لعبد الله كنون، وأزهار الرياض ٢: ٣٤٧ والرسالة المستطرفة ١٣٤ والبدر الطالع ٢: ٢٣٤ وفهرس الفهارس ١: ٣٣٢ ومجلة معهد المخطوطات ٥: ٣١ ومعجم المؤلفين ١١: ٩٣ والأعلام ٧: ٢٠٨ ومجلة دعوة الحق عدد ٢ س ٣.','2','598',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1076,'تونس، وولي قضاء الجماعة فيها (سنة ٨٥٩ هـ) وصرف بأمر السلطان (سنة ٨٧٥ هـ). وحج، وأقام بالقاهرة وأقرأ فيها الفقه وأصوله والنحو والتفسير. وعاد إلى تونس وتوفي بها (١).

بحرق [٨٦٩ - ٩٣٠ هـ / ١٤٦٥ - ١٥٢٤ م]

<span data-type="title" id=toc-1481>محمد بن عمر بن مبارك بن عبد الله الحميري الحضرمي، جمال الدين، الشهير ب"بحرق</span>":
فقيه شافعي، أديب، باحث، نحوي، متصوف، نعته الزبيدي بعلامة اليمن. ولد بحضرموت، وتعلم بها وبعدن وزبيد ومكة والمدينة. وولي القضاء بالشحر ثم استقال ورحل إلى الهند، فأكرمه السلطان مظفر شاه الثاني (٩١٧ - ٩٣٢ هـ) وقربه وعظمه، وأقام إلى أن مات في أحمد آباد. من آثاره الكثيرة "تفسير آية الكرسي" (٢).
_________
(١) الضوء اللامع ٨: ٢٥٧. ونيل الابتهاج ٣٢٣ والحلل السندسية في الأخبار التونسية ١: ٦٨٨ وتوشيح الديباج مخطوط ١٥٥.
(٢) شذرات الذهب ٨: ١٧٦ والنور السافر ١٤٣ والضوء اللامع ٨: ٢٥٣ ومعجم المطبوعات ٥٣٢ ومجلة الزهراء ٤: ٩٦ وهدية العارفين ٢: ٢٣٠ وتاج العروس ٦: ٢٨٤ ومعجم المؤلفين ١١: ٨٩ والأعلام ٦: ٣١٥.','2','598',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1077,'الدارندي [... - ١١٥٢ هـ / ... - ١٧٣٩ م]

<span data-type="title" id=toc-1482>محمد بن عمر بن عثمان الدارندي، الرومي الحنفي:</span>
فاضل، له مشاركة في الأدب والتفسير. من آثاره "رسالة على تفسير البيضاوي" لقوله تعالى: {طه ما أنزلنا عليك القرآن لتشقى} (١).

نووي الجاوي [... - ١٣١٦ هـ / ... - ١٨٩٨ م]

<span data-type="title" id=toc-1483>محمد بن عمر بن علي نووي الجاوي البنتني إقليما، التناري بلدا، أبو عبد المعطي:</span>
مفسر، متصوف، متكلم، من فقهاء الشافعية. هاجر إلى مكة، وتوفي بها. من آثاره "مراح لبيد لكشف معنى القرآن المجيد" ويسمى أيضا "التفسير المنير لمعالم التنزيل المفسر عن وجوه محاسن التأويل"، طبع، مجلدان (٢).
_________
(١) هدية العارفين ٢: ٣٢٤ ومعجم المؤلفين ١١: ٨٧
(٢) هدية العارفين ٢: ٣٩٤ وهو فيه محمد نوري بن عمر، ووفاته سنة ١٣١٥ هـ. ومعجم المطبوعات ١٨٧٩ وتاريخ الشعراء الحضرميين ٣: ١٧١ وفيه أنه توفي بمكة سنة ١٣١٦ وفهرس التيمورية وفيه: المتوفى سنة ١٣١٢ على ما أخبرنا به أحد فضلاء جاوة. ومعجم المؤلفين ٨٧: ١١ والأعلام ٧: ٢٠٩.','2','599',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1078,'الجور [... - ٣٥٩ هـ / ... - ٩٧٠ م]

<span data-type="title" id=toc-1484>محمد بن عمران بن موسى الجوري، أبو بكر:</span>
نسابة، أديب، نحوي. نسبته إلى جور من أعمال فارس، (وإليها ينسب الورد الجوري). روى عنه الحاكم أبو عبد الله وقال: "كان علامة في معرفة الأنساب وعلوم القرآن" (١).

الكرماني [... - ٨٢٧ هـ / ... - ١٤٢٤ م]

<span data-type="title" id=toc-1485>محمد بن عوض بن خضر بن حسن الكرماني، جلال الدين:</span>
عارف بالتفسير والعربية والمنطق. من أهل كرمان ببلاد فارس. حج وجاور بمكة سنتين. ثم انتقل إلى اليمن، فأكرمه ملكها الناصر الرسولي أحمد بن إسماعيل (٨٠٣ - ٨٢٧ هـ). قال الداودي: فاشتهر ذكره، وأخذ عنه الطلبة، وأدركه الأجل بعدن" (٢).
_________
(١) طبقات المفسرين ٢: ٢٢٥ وبغية الوعاة ١: ٢٠٢ والمشتبه ١: ١٨٩ حاشية رقم ٢ واللباب ١: ٣٠٧.
(٢) طبقات المفسرين ٢: ٢٢٠ والضوء اللامع ٨: ٢٧٢.','2','599',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1079,'السيرافي [... - بعد ٢٤٠ هـ / ... - ٨٥٤ م]

<span data-type="title" id=toc-1486>محمد بن عون بن داود السيرافي:</span>
عارف بالتفسير، له اشتغال بالحديث، نسبته إلى سيراف من بلاد فارس. روى عن أبي بحر عبد الواحد بن غياث البصري المتوفى سنة ٢٤٠ هـ. وروى عنه محمد بن إسماعيل بن مهران الإسماعيلي (ت: ٢٩٥ هـ) وقال: "كان ينسب إلى التفسير، ولم يكن في الحديث بذاك" (١).

المكناسي [... - ١٢٩٤ هـ / ... - ١٨٧٧ م]

<span data-type="title" id=toc-1487>محمد العياشي بن المكي المكناسي:</span>
فقيه مالكي، صوفي، مفسر، من أهل مكناس. من آثاره "تفسير قوله تعالى: {ثم أورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه} (٢).
_________
(١) طبقات المفسرين ٢: ٢٢١ ولسان الميزان ٥: ٣٣٢
(٢) أخبار مكناس ٤: ٢٥٩ ومعجم المؤلفين ١١: ١٠١.','2','600',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1080,'السكسكي [... - ٧٦٠ هـ / ... - ١٣٥٩ م]

<span data-type="title" id=toc-1488>محمد بن عيسى بن عبد الله السكسكي، شمس الدين:</span>
فقيه شافعي، نحوي، مفسر، من أهل مصر. سكن دمشق، وتولى مشيخة الخانقاه الشهابية بها. قال ابن رافع: "علق في "التفسير" شيئا" (١).

الجزائري [١٢٤٣ - ١٣١٠ هـ / ١٨٢٨ - ١٨٩٢ م]

<span data-type="title" id=toc-1489>محمد بن عيسى الجزائري:</span>
من الكتاب البلغاء، عارف باللغة والتفسير، ولد ونشأ وتعلم بمدينة الجزائر. وانتقل إلى تونس سنة ١٢٧٢ هـ، وتولى رئاسة الكتابة العامة بالوزارة الكبرى سنة ١٢٧٦ هـ، ثم خطة الإنشاء سنة ١٣٠٢ هـ، ثم انقطع للعلم إلى أن توفي. وفي "تعريف الخلف" "ورسائله تدل على أنه في طبقة عليا من الفهم والعلم .. ". من آثاره "الثريا لمن كان بعجائب القرآن حفيا"
_________
(١) الشذرات ٦: ١٨٩ وطبقات المفسرين ٢: ٢٢١ وفيه وفاته سنة ٧٧٠ هـ وبغية الوعاة ١: ٢٠٥ والدرر الكامنة ٤: ٢٤٦ وهدية العارفين ٢: ١٦٣ وفيه وفاته سنة ٧٦٥ ومعجم المؤلفين ١١: ١٠٦.','2','600',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1081,'رسالة، في التفسير، طبعت بتونس سنة ١٣٠٧ هـ و "الماس في احتباك يعجز الجنة والناس" تفسير قوله تعالى: {ومن يكرههن فإن الله من بعد إكراههن غفور رحيم} طبعت سنة ١٣٠٦ هـ (١).

ملا خسرو [... - ٨٨٥ هـ / ... - ١٤٨٠ م]

<span data-type="title" id=toc-1490>محمد بن فرامرز بن علي، المعروف بملا - أو منلا. أو المولى - خسرو:</span>
قاض، أصولي، بياني، من فقهاء الحنفية، مشارك في بعض العلوم، رومي الأصل، أسلم أبوه، ونشأ هو مسلما، فتبحر في علوم المعقول والمنقول. ودرس بمدينة أدرنة في مدرسة شاه ملك، ثم بمدينة بروسة. وولي القضاء بالقسطنطينية، وضم إليه قضاء غلطة واسكدار وتدريس آياصوفيا. ثم صار مفتيا بالتخت السلطاني، وعظم أمره، وعمر عدة مساجد بالقسطنطينية، وتوفي بها، وحمل إلى مدينة بروسة، فدفن بها في مدرسته. من كتبه "حاشية على أنوار التنزيل" في التفسير، للبيضاوي. قال صاحب كشف
_________
(١) معجم أعلام الجزائر ١١٢ وتعريف الخلف ٢: ٥٢٨ وهدية العارفين ٢: ٣٩١ والمكتبة البلدية، فهرس التفسير ٢٦ وشجرة النور ١٣، وفيه وفاته سنة ١٣٠٣ وإيضاح المكنون ٢: ٤١٩ ومعجم المؤلفين ١١: ١٠٤','2','601',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1082,'الظنون: "وهي من أحسن التعليقات عليه بل أرجحها، إلى قوله سبحانه وتعالى: {سيقول السفهاء ..} البقرة: ١٤٢". و "رسالة في تفسير قوله تعالى: {يوم يأتي بعض آيات ربك} (الأنعام: ١٥٨) كتبها بأمر السلطان محمد خان لكونها حجة للمعتزلة وعلى أهل السنة في الظاهر، وقد حل الملا هذا الإشكال وكشف مراد صاحب الكشاف والبيضاوي فيما ذكراه من الوجوه" (١).

الذكي [٤٢٧ - ٥١٦ هـ / ١٠٣٦ - ١١٢٢ م]

<span data-type="title" id=toc-1491>محمد بن أبي الفرخ بن فرخ بن أبي القاسم المازري، أبو عبد الله، ويعرف بالذكي:</span>
مفسر، نحوي، لغوي، أديب، ولد بمازرة بجزيرة صقلية، وتحول إلى القيروان، ورحل إلى المغرب الأقصى، ثم عاد إلى إفريقية، ومنها قصد المشرق، فجال في مصر والشام والعراق
_________
(١) الفوائد البهية ١٨٤ وهدية العارفين ٢: ٢١١ وكشف الظنون ١٩٠ و ٨٥٥ والضوء اللامع ٨: ٢٧٠ والشذرات ٧: ٣٤٢ ومعجم المطبوعات ١٧٩٠ والأعلام ٧: ٢١٩ ومعجم المؤلفين ٤: ٩٩ و ١١: ١٢٢ ونظم العقيان ١٠٩ ومفتاح السعادة ٢: ٦١ والأزهرية ٢: ١٥٣ وهو في بعض هذه المصادر: خسرو بن فرامور، وانطر ما كتبه الزركلي في حاشية الأعلام حول اسم أبيه.','2','601',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1083,'وخراسان وغزنة، وخاصم في الهند أئمة مخاصمات آلت إلى طعنهم فيه، ثم عاد إلى أصبهان ومات بها. قال حسن حسني عبد الوهاب: "له تآليف كثيرة في القراءات والتفسير واللغة والنحو" (١).

وجدي [١٢٩٥ - ١٣٧٣ هـ / ١٨٧٨ - ١٩٥٤ م]

<span data-type="title" id=toc-1492>محمد فريد بن مصطفى وجدي بن علي رشاد:</span>
كاتب إسلامي متعمق بدراسة الإسلام وأصوله وتاريخه، ومصلح إجتماعي ديني أوقف قلمه للدفاع عن الإسلام والعرب والعربية، وأحد مشاهير قادة الفكر في مصر في النصف الأول من القرن العشرين. ولد ونشأ في الإسكندرية، وأقام وتعلم في دمياط حيث كان والده وكيل محافظ فيها. ثم انتقل معه إلى السويس، وفيها بدأ حياته الصحفية، فأصدر مجلة "الحياة" في يونيو ١٨٩٩ و "أوقفها على المباحث الروحية الحديثة، واستشفاف أسرار النفس الإنسانية وظواهرها". ثم سكن القاهرة، وعمل
_________
(١) الوافي ٤: ٣٢٠ وهو فيه: محمد بن الفرج وبغية الوعاة ١: ٢٩٠ والإمام المازري لحسن حسني عبد الوهاب ٩٣ وهدية العارفين ٢: ٧٩ وفيه: توفي في حدود سنة ٥٠٠ هـ. والأعلام ٦: ٣٢٨ والمخطوطات المصورة ١: ٣٩٨ ومعجم المؤلفين ١١: ١٢٤.','2','602',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1084,'في وظيفة صغيرة بديوان الأوقاف، أنشأ بعدها مطبعة أصدر بها جريدة "الدستور" في نوفمبر ١٩٠٧، فمجلة "الوجديات" سنة ١٩١٠، فمجلة "الحياةط - مرة ثانية - في نوفمبر ١٩١٢، فجريدة "الوجديات" في ١٥ فبراير ١٩٢١ - ١٥ أبريل ١٩٢٢، أعاد بعدها إصدار جريدة "الدستور" في ٩ نوفمبر من نفس السنة. ولما أعيد الشيخ محمد مصطفى المراغي إلى مشيخة الأزهر سنة ١٩٣٥، عهد إليه برئاسة تحرير مجلة "نور الإسلام" التي كان يصدرها الأزهر، والتى سميت فيما بعد "مجلة الأزهر"، فتولاها أكثر من عشرة أعوام "كان له في كل عدد مقال الافتتاح يعالج به موضوعا دينيا أو إجتماعيا أو تاريخيا". واعتزلها قبل وفاته بنحو عامين. قال المستشرق بول كراوس  Paul Kraus (١٩٠٤ - ١٩٤٤) "  يستمد أدبه وعلمه من وثيق إيمانه، وصدق إسلامه، وإيمانه بالله يضيء به ظلمات الفكر، وبحوثه تهدي الحيارى من قرائه". وقال العقاد: "قليل النظير في نزاهته وصدقه وغيرته علي المصلحة القومية واستعداده للتضحية بماله وراحته في سبيل المبدأ الذي يرعاه، ولا يتزحزح عنه قيد أنملة". اقترن اسمه بعملين جليلين هما "دائرة معارف القرن الرابع عشر، العشرين"','2','602',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1085,'وتقع في عشر مجلدات، وتفسير القرآن الكريم المعروف ب "صفوة العرفان" وهو تفسير موجز مشهور (١)

الرؤاسي [... - ٤١٦ هـ / ... - ١٠٢٥ م]

<span data-type="title" id=toc-1493>محمد بن الفضل بن محمد بن جعفر بن صالح، أبو بكر الرؤاسي، ويعرف بميرك البلخي:</span>
مفسر، من أهل بلخ، روى عن أحمد بن محمد بن نافع، وغيره. قال الذهبي: "كان إليه المنتهى في الوعظ والتذكير، مات في مجلسه أربعة أنفس. وهو آخر من روى عن قتيبة". من آثاره "تفسير كبير" للقرآن (٢).
_________
(١) تاريخ الصحافة العربية. ومعجم المطبوعات ١٤٥١ ومحمد فريد وجدي لطه الحاجري، وقصة الأدب المعاصر ١: ٦٥ والأعلام الألف ١: ١٣٤ وفي أوقات الفراغ ١٦٤ ومجلة نور الإسلام ٣: ٧ وفريد وجدي لأنور الجندي، وأشهر مشاهير أدباء الشرق ١: ١١٤ والأعلام ٦: ٣٢٩ وجريدة الأهرام ١٠/ ٢/ ٥٤ ومجلة الفتح ٤: ١٢٩ والهلال عدد آذار (مارس) ١٩٥٤ ومعجم المؤلفين ١١: ١٢٦ وأرخ حسن حسني عبد الوهاب (في الأهرام ١٦/ ٢/ ١٩٥٤) ولادته سنة ١٨٧٥، ومجله المنار ١٠: ٣٧٦ و ٤٥٦ و ٥٤١ و ٣٣: ١٤٥ و ٢٣٨ والمحافظة والتجديد في النثر العربي المعاصر في مائة عام: ٥٠٩ ومصادر الدراسة ٣: ١٣٩٥ ومجلة المجلات ٧: ٦٦٤.
(٢) الوافي ٤: ٣٢٢ وطبقات المفسرين للسيوطي ٣٨ وطبقات المفسرين للداودي ٢: ٢٢٢ والجواهر المضية ٢: ١١١ واللباب ١: ٤٧٨ ومعجم المؤلفين ١١: ١٢٩.','2','603',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1086,'ابن فضل [... - بعد ١١٣٢ هـ / ... - بعد ١٧١٩ م]

<span data-type="title" id=toc-1494>محمد بن فضل علي خان:</span>
فاضل، له اشتغال بالتفسير، من آثاره "زهرات روضات النعيم في تفسير بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ" (١).

ابن فضيل [... - ١٩٥ هـ / ... - ٨١١ م]

<span data-type="title" id=toc-1495>محمد بن فضيل بن غزوان بن جرير الضبي، مولاهم، أبو عبد الرحمن:</span>
حافظ للحديث، من فقهاء الإمامية، من أهل الكوفة. له تصانيف، منها "تفسير القرآن" (٢).

الرومي [... - ١٣١٨ هـ / ... - ١٩٠٠ م]

<span data-type="title" id=toc-1496>محمد فوزي بن عبد الله الرومي، الشهير بمفتي أدرنة:</span>
فقيه حنفي، مفسر،
_________
(١) معجم المؤلفين ١١: ١٢٨ وبروكلمان: الذيل
(٢) غاية النهاية ٢: ٢٢٩ والعبر ١: ٣١٩ وتهذيب التهذيب ٩: ٤٠٥ وهدية العارفين ٢: ٩ وميزان الاعتدال ٤: ٩ وتذكرة الحفاظ ١: ٣١٥ وأعيان الشيعة ٤٦: ١٩٠ وابن النديم ٢٦٦ والنجوم الزاهرة ٢: ١٤٨ وطبقات المفسرين ٢: ٢٢٣.','2','603',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1087,'قاضي، تولى قضاء الجيش بالروم إيلي. من كتبه "تفريج القلق في تفسير سورة الفلق" و "تسيير الفلك في تفسير سورة الملك" و "قدس العرفان في تفسير سورة والنجم من القرآن" (١)

ابن الأنبارى [٢٧١ - ٣٢٨ هـ / ٨٨٤ - ٩٤٠ م]

<span data-type="title" id=toc-1497>محمد بن القاسم بن محمد بن بشار بن الحسن بن بيان، أبو بكر، ابن الأنباري:</span>
نحوي، لغوي، أديب، محدث، مفسر، كان من أحفظ الناس للغة والنحو والشعر وتفسير القرآن. قيل: "كان يحفظ مئة وعشرين تفسيرا من تفاسير القرآن بأسانيدها، وثلاثمائة ألف شاهد في القرآن". ولد في الأنبار (على الفرات)، وأخذ عن أبيه وثعلب وجماعة من العلماء، وعنه الدارقطني وغيره. قال القفطي: "سمع عالما من الأئمة في زمانه، وروى عنه مثل ذلك". وكتب عنه وأبوه حي، وكان يملي في ناحية من المسجد وأبوه في ناحية أخرى، فكانت حلقتاهما متجاورتين. وكان يتردد إلى أولاد الخليفة الراضي بالله، يعلمهم. قال النديم في
_________
(١) إيضاح المكنون ١: ٢٨٩ و ٣٠٠ و ٢: ٢٢١ وهدية العارفين ٢: ٣٩٦.','2','604',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1088,'الفهرست: "كان أفضل من أبيه وأعلم، في نهاية الذكاء والفطنة وجودة القريحة وسرعة الحفظ، ومع ذلك ورعا من الصالحين، لا يعرف له حرمة ولا زلة، وكان يضرب به المثل في حضور البديهة وسرعة الجواب ... ". توفي ببغداد، من آثاره "المشكل في معاني القرآن" لم يتمه، قال في "إنباه الرواة": "بلغ فيه إلى (طه) وقد أملاه سنين كثيرة". و "الناسخ والمنسوخ" ذكره السيوطي والزركشي، و "ضمائر القرآن" ذكره صاحب كشف الظنون (١).
_________
(١) تذكرة الحفاظ ٣: ٨٤٢ ونزهة الألباء ٣٣٠ ووفيات الأعيان ٤: ٣٤١ وتاريخ بغداد ٣: ١٨١ وغاية النهاية ٢: ٢٣٠ والوافي ٤: ٣٤٤ وبغية الوعاة ١: ٢١٢ ومعجم الأدباء ١٨: ٣٠٦ وشذرات الذهب ٢: ٣١٥ والعبر ٢: ٢١٤ وإنباه الرواة ٣: ٢٠١ وطبقات المفسرين للداودي ٢: ٢٢٦ والنسخ في القرآن الكريم ١: ٣٢٥ وطبقات الحنابلة ٢: ٦٩ ودائرة المعارف الإسلامية ٣: ٥ ومجلة المجمع العلمي العربي ٣٤: ٢٧٣ وروضات الجنات ١٦٨ ومرآة الجنان ٢: ٢٩١ والمختصر في أخبار البشر ٢: ٩٢ والبداية والنهاية ١١: ١٩٦ واللباب ١: ٨٦ وتاريخ آداب اللغة العربية ٢: ٢١١ والأنساب ١٤٩ وطبقات ابن قاضي شهبة ١: ١٢٠ والنجوم الزاهرة ٣: ٢٦٩ وطبقات النحويين واللغويين ١٥٣ والمنتظم ٦: ٣١١ ومناقب الإمام أحمد ٥١٥ والكامل في التاريخ ٨: ٣٦٥ وكشف الظنون ١٠٨٧ والموسوعة العربية الميسرة ١٠ والفهرست ٨٢. والأعلام ٦: ٣٣٥ ومعجم المؤلفين ١١: ١٤٣ ومجلة الآثار ٣: ١٧٨.','2','604',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1089,'ابن القرطي [نحو ٢٧٣ - ٣٥٥ هـ / نحو ٨٨٦ - ٩٦٦ م]

<span data-type="title" id=toc-1498>محمد بن القاسم بن شعبان بن محمد بن ربيعة، أبو إسحاق، ابن القرطي، ويقال له ابن شعبان، من نسل عمار بن ياسر:</span>
رأس الفقهاء المالكيين بمصر في وقته، وأحفظهم لمذهب مالك، مع التفنن في التاريخ والأدب. كان، كما يقول ابن فرحون، كثير الذم لبني عبيد (الفاطميين) وكان يدعو الله أن يميته قبل دخولهم مصر، فكان ذلك. وقيل أن المعز الفاطمي معد بن إسماعيل بعث إليه بكتاب ومئة مثقال مع رسول، فقرض البسملة من أعلى الكتاب وأحرق باقيه بالشمعة أمام الرسول، ورد المئة عليه، وقال له: "لولا أنه ثبت عندي أنك سني، ما خرجت من هذه الدار، ولجعلت من يقتلك". وقال ابن حبان: "كان الحكم المستنصر أمير المؤمنين بالأندلس يوجه سرا كل عام إلى كل واحد من علماء مصر مئة مثقال، وبضعفها لأبي إسحاق، وفعل ذلك بعده صاحب القيروان: فردها أبو إسحاق وأساء القول فيه. وذكر في الديباج أنه "كان واسع الرواية، كثير الحديث، مليح التأليف، شيخ الفتوى، حافظ البلد، ولكنه مع غزارة علمه كان يلحن ولم','2','605',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1090,'يكن له بصر بالعربية". وقال أبو الحسن القابسي: "في كتبه غرائب من قول مالك، وأقوال شاذة عن قوم لم يشتهروا بصحبته، ليست مما رواه ثقات أصحابه". ونسبته "القرطي" إلى بيع القرط. من كتبه "أحكام القرآن" (١)

البقالي [٤٩٠ - ٥٦٢ هـ / ١٠٩٧ - ١١٦٧ م]

<span data-type="title" id=toc-1499>محمد بن أبي القاسم بن بابجوك، البقالي الخوارزمي، أبو الفضل الملقب بزين المشايخ، ويعرف بالآدمي:</span>
عالم بالأدب، مفسر، نحوي، من فقهاء الحنفية، من أهل خوارزم. تتلمذ للزمخشري وجلس بعده في حلقته، وشهر اسمه وبعد صيته. قال ياقوت: "كان إماما في الأدب، وحجة في لسان العرب، جم الفوائد، حسن الاعتقاد، كريم النفس، غير خائض فيما لا يعنيه، له يد في الترسل ونقد الشعر". وقال الصفدي:
_________
(١) ترتيب المدارك ٣: ٢٩٣ وطبقات المفسرين للداودي ٢: ٢٢٤ والمشتبه ٢: ٥٢٥ وتبصير المنتبه ٣: ١١٦٦ والديباج ٢: ١٩٤ وحسن المحاضرة ١: ٣١٣ واللباب ٣: ٢٦ وهو فيه ابن سفيان تحريف ابن شعبان، ومثله في تاج العروس ٥: ٢٠٤ والديباج ٢: ١٩٤ وهو فيه القرطبي خطأ. ومعجم المؤلفين ١١: ١٤٠ والأعلام ٦: ٣٣٥.','2','605',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1091,'عرف بالآدمي لحفظه مقدمة في النحو للآدمي". أما البقالي فهو البقال الذي يبيع الأشياء المتفرقة من الفواكه اليابسة وغيرها. والعجم يزيدون الحاء، وهي هنا زيادة العجم لا نسبة. مات في جرجان خوارزم. من كتبه "مفتاح التنزيل" في تفسير القرآن، مخطوط، الثالث منه في المكتبة الظاهرية، و "التنبيه على إعجاز القرآن" (١).

الربعي [٦٣٩ - ٧١٥ هـ / ١٢٤١ - ١٣١٥ م]

<span data-type="title" id=toc-1500>محمد بن أبي القاسم بن عبد السلام، شمس الدين، أبو عبد الله الربعي:</span>
فقيه مالكي، أصولي، مفسر، قاض، ولد بمدينة تونس، وبها نشأ وتعلم، ثم انتقل إلى القاهرة، فسمع بها، وتولى نيابة الحكم بالحسينية مدة، ثم قضاء الإسكندرية سنة ٧٠٧ هـ، وعزل، فعاد إلى القاهرة واستقر بها إلى أن مات. قال ابن حجر: له "مختصر تفسير ابن الخطيب" (٢).
_________
(١) معحم الأدباء ١٩: ٥ وطبقات المفسرين للداودي ٢: ٢٣٠ والوافي ٤: ٣٤٠ وبغية الوعاة ١: ٢١٥ وطبقات المفسرين للسيوطي ٤٠ والفوائد البهية ١٦١ والجواهر المضية ٢: ٣٧٢ والأعلام ٦: ٣٣٥ وهدية العارفين ٢: ٩٨ وكشف الظنون و ٤٨٨ و ١٧٦٠ ومعجم المؤلفين ١١:
(٢) طبقات المفسرين للداودي ٢: ٢٣١ والدرر الكاممنة =','2','606',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1092,'الرصاع [... - ٨٩٤ هـ / ... - ١٤٨٩ م]

<span data-type="title" id=toc-1501>محمد بن قاسم الأنصاري، أبو عبد الله، الرصاع:</span>
فقيه مالكي، عارف بالتفسير والحديث. ولد بتلمسان، ونشأ واستقر بتونس (٨٣١ هـ) وتوفي بها. ولي قضاء الجماعة، ثم اقتصر في أواخر أيامه على إمامة جامع الزيتونة والخطابة فيه، متصدرا للإفتاء وإقراء الفقه والعربية. وعرف بالرصاع لأن أحد جدوده كان نجارا يرصع المنابر. قال السخاوي: "وبلغني أنه شرع في "تفسير" (١)

الأخوان [... - ٩٠٤ هـ / ... - ١٤٩٧ م]

<span data-type="title" id=toc-1502>محمد بن قاسم، محيي الدين، الشهير بالأخوين:</span>
مفسر، متكلم، من أهل دمشق. قال صاحب كشف الظنون "له تعليقة على الزهراوين من تفسير البيضاوي". وفي دار الكتب المصرية بالقاهرة مخطوطة باسم "حاشية الأخوين
_________
= ٤: ٢٦٦ والديباج المذهب ٢: ٣١٣ ومعجم المؤلفين
(١) الضوء اللامع ٨: ٢٨٧ والبستان ٢٨٣ وشجرة النور ٢٥٩ ومعجم المطوعات ٩٣٩ وفهرست الرصاع ٨.','2','606',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1093,'على بعض المواضع من أنوار التنزيل وأسرار التأويل للبيضاوي" (١).

الأماسي [٨٦٤ - ٩٤٠ هـ /١٤٦٠ - ١٥٣٤ م]

<span data-type="title" id=toc-1503>محمد بن قاسم بن يعقوب بن أحمد الأماسي، محيي الدين، ابن الخطيب قاسم:</span>
فقيه حنفي، مفسر، عارف بالحديث والتاريخ والموسيقى، من علماء الروم (الترك) عربي التصانيف. ولد بأماسية وبها نشأ وتعلم، وولي مدرسة السلطان بايزيد بها، ثم السليمانية بجوار آياصوفيا. قال في شذرات الذهب: "كان عالما صالحا، له مهارة في القراءات والتفسير واطلاع على العلوم الغريبة كالأوفاق .. والجفر ... ". مات بأماسية، وهو مدرس (٢).

ابن قرقماس [٨٠٢ - ٨٨٢ هـ / ١٤٠٠ - ١٤٧٨ م]

<span data-type="title" id=toc-1504>محمد بن قرقماس بن عبد الله، (الناصري، ناصر الدين، ويعرف بابن قرقماس)</span>
_________
(١) كشف الظنون ١٩٢ ومعجم المؤلفين ١١: ١٣٦ ومخطوطات دار الكتب المصرية ٢٣٤ والأعلام ٧: ٥
(٢) شذرات الذهب ٨: ٢٤٢ وهدية العارفين ٢: ٢٣٥ والكواكب السائرة ٢: ٥٧ والأعلام ٧: ٢٢٩ ومعجم المؤلفين ١١: ١٤٨ والشقائق النعمانية ٢٣٧ وجامعة الرياض ٧: ٦ وكشف الظنون ٨٣٣.','2','607',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1094,'الناصري، ناصر الدين، ويعرف بابن قرقماس: أديب، شاعر، من أعيان الحنفية، ولد بالقاهرة، وبها نشأ وتعلم، وتقدم عند الظاهر خشقدم، وحج، وزار بيت المقدس، ومات بالقاهرة. من آثاره "فتح الرحمن في تفسير القرآن" في عشرين مجلدة، منه جزآن في صوفية. قال السخاوي: "نسخه من مواضع وفيه ما ينتقد". وله أيضا "نثر الجمان المنتظم من فتح الرحمن" أوله: "الحمد لله منزل القرآن لخير أمة أخرجت للناس .. " وقال: فلما يشر الكريم بختم كتابي فتح الرحمن، قصدني عين الإخوان أن ألخص منه تفسيري المسجع على انفراد، فما عدلت، لأني جمعت فيه للنحاة وعلماء القراءات والمفسرين أقوالهم، وما عن لي من إعراب وتفسير، واعتراضات وتحرير، فتكررت الآيات مرات، وختمتها بسجعات نثر أحسن من نثر الجمان فانتقيتها ونقحتها .. " (١).
_________
(١) الضوء اللامع ٨: ٢٩٢ ونظم العقيان ١٥٨ وكشف الظنون ١٢٣٢ و ١٩٢١ والأعلام ٧: ٢٣٢ وهدية العارفين ٢: ٢١٠ وطبقات المفسرين ٢: ٢٣١ والكتخانة ٢: ١٣٧ و ٤: ١٣٧. وابن إياس ٢: ١٨١ وفهرس دار الكتب ٢: ٢٠١ ومعجم المؤلفين ١١: ١٥٠ والأحمدية ١٩١ ودار الكتب الشعبية ١: ١٠٠.','2','607',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1095,'الكليبولي [... - ... هـ / ... - ... م]

<span data-type="title" id=toc-1505>محمد بن كاتب الكليبولي:</span>
مفسر، من آثاره "تفسير الفاتحة" قال صاحب كشف الظنون: "ألفه ردا على الوجودية كما ذكر في ديباجته (١).

الطرابلسي [١٢٤٥ - ١٣١٥ هـ / ١٨٣٠ - ١٨٩٧ م]

<span data-type="title" id=toc-1506>محمد كامل بن مصطفى بن محمود الطرابلسي:</span>
فقيه حنفي، عارف بالتفسير، ولد بالزاوية - بليبيا - وتعلم بها وبطرابلس الغرب، ثم بالأزهر الشريف بالقاهرة (١٢٦٣ - ١٢٧٠ هـ). وولي الإفتاء بطرابلس سنة ١٣١١ هـ واستمر إلى أن توفي. من آثاره "حواش على تفسير البيضاوي" مجلد، مخطوط في طرابلس (٢).

السرغيني [... - ١١٦٤ هـ / ... - ١٧٥١ م]

<span data-type="title" id=toc-1507>محمد الكبير بن محمد بن محمد بن محمد السرغيني العنبري:</span>
فقيه مالكي، له
_________
(١) كشف الظنون ٤٥٥
(٢) أعلام من طرابلس ١٧١ وأعلام ليبيا ٣٢٥','2','608',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1096,'اشتغال بالتفسير والحديث، من أهل المغرب. من آثاره "تفسير قوله تعالى: {وهو معكم أينما كنتم} (الحديد: ٤) (١).

الكرماني [... - بعد ٩٣٦ هـ / ... - بعد ١٥٢٩ م]

<span data-type="title" id=toc-1508>محمد الكرماني، محي الدين:</span>
مفسر، من المفتين، من أهل كرمان مولدا ووفاة، وولي الإفتاء بها. حج سنة ٩٣٥ هـ ودخل دمشق في صفر سنة ٩٣٦، فأكرمه قاضيها وجماعة من أهلها وأحسنوا إليه، وعاد إلى بلده. قال ابن العماد: "توفي تقريبا سنة ٩٣٧ هـ ". وفي هدية العارفين أنه: "سافر إلى مكة ورجع إلى بلده سنة ٩٦٣ هـ ".؟ من آثاره "تفسير القرآن" (٢).

القرظي [... - ١١٨ هـ / ٧٣٦ م]

<span data-type="title" id=toc-1509>محمد بن كعب بن سليم بن عمرو، أبو حمزة، ويقال أبو عبد الله، القرظي:</span>
_________
(١) هدية العارفين ٢: ٣٢٨ وسلوة الأنفاس ٢: ٣٤٠
(٢) هدية العارفين ٢: ٢٤٥ والكواكب السائرة ٢: ٦٨ والشذرات ٨: ٢٢٦ ومعجم المؤلفين ١٢: ٨ و ٢٠٨.','2','608',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1097,'تابعي، من كبار العلماء. ولد في حياة النبي (ص). ونزل الكوفة سنة ٤٠ هـ. ثم رجع إلى المدينة. قال صاحب "تاريخ التراث العربي": "استخدم الثعلبي تفسيره في كتابه الكشف والبيان، ومن الروايات التي ذكرها الطبري في تاريخه يتضح أن ابن اسحاق قد استخدم كتابا للقرظي ذا مضمون تاريخي، فهل هذا هو كتاب في المغازي - بمعنى الكلمة - أو أنه كتاب تفسير يتضمن معلومات تاريخية وفيرة؟ " وقال عون بن عبد الله: "ما رأيت أحدا أعلم بتأويل القرآن من القرظي". مات بالمدينة، قيل سنة ١٠٨ أو ١١٧ أو ١٢٠ هـ (١).

الكيلاني [... - ١٠٩٨ هـ / ... - ١٦٨٧ م]

<span data-type="title" id=toc-1510>محمد الكيلاني، المعروف بمولى شمسا:</span>
فاضل إمامي. قال العاملي: "له "تفسير سورة الإنسان" (٢)
_________
(١) تاريخ التراث العربي ١: ١٩٠ وتهذيب التهذيب ١: ٢٠، وحلية الأولياء ٣: ٢١٢ وغاية النهاية ٢: ٢٣٣. والكامل في التاريخ ١: ١١٠، ٤٢٧، ٦٧٩ و ٤: ٢٢٧ و ٥: ١٤١ والشذرات ١: ١٣٦ والعبر وفيات سنة ١٠٨ هـ.
(٢) أعيان الشيعة ٤٦: ٢١٠ ومعجم المؤلفين ١١: ١٤٦.','2','609',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1098,'محمد لطفي جمعة [١٣٠٣ - ١٣٧٢ هـ / ١٨٨٦ - ١٩٥٣ م]

<span data-type="title" id=toc-1511>محمد لطفي ابن الشيخ جمعة بن أبي الخير الإسكندري:</span>
محام، صحفي، أديب، من كبار الكتاب والخطباء والمترجمين، ومن أعضاء المجمع العلمي العربي بدمشق، كان في طليعة المشتغلين بالحركة الوطنية المصرية في مطلع هذا القرن. ولد في الإسكندرية وتعلم بها وبطنطا والقاهرة، وأتقن اللغتين الإنجليزية والفرنسية، وألم بلغات أخرى. وتعرف بالأستاذ الإمام الشيخ محمد عبده، والشيخ علي يوسف، وحافظ إبراهيم، وأحمد شوقي، وغيرهم من أكابر رجال السياسة والوطنية والأدب، وتأثر بالأستاذ الإمام ومصطفى كامل وقاسم أمين. ثم سافر إلى فرنسا (سنة ١٩٠٨) وأحرز إجازة الحقوق في العلوم القانونية من جامعة ليون سنة ١٩١٠ وجال - في خلال إقامته بفرنسا - في بعض الدول الأوروبية، وشارك في عدد من المؤتمرات الكبرى، وكتب عشرات المقالات في الصحف الأوروبية منددا بالإستعمار مدافعا عن حق مصر في الحرية والإستقلال. وعاد إلى مصر، فحرر في أشهر الصحف المصرية، ولا سيما "السياسة الأسبوعية" و "البلاغ" و','2','609',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1099,'"المقطم"، وزاول مهنة المحاماة نحو ست وثلاثين عاما، وترافع فيها في أهم وأخطر القضايا السياسية التي شغلت الأذهان في النصف الأول من هذا القرن، مثل قضية "السيرلي ستاك" سنة ١٩٢٥، وقضية القنابل سنة ١٩٣٢، وقضية الجمعية السرية التي قتل فيها أمين عثمان باشا سنة ١٩٤٦ م ". واشتهر بنقده الشديد لكتاب "الشعر الجاهلي" لطه حسين، وترجم إلى العربية عددا من نفائس الكتب. وتوفي بالقاهرة. من آثاره "تفسير القرآن" مخطوط. (١)

حكيم شاه [... - ٩٢٠ هـ / ... - ١٥١٤ م]

<span data-type="title" id=toc-1512>محمد بن مبارك شاه بن محمد الهروي، ثم الرومي الحنفي، شمس الدين، المعروف بحكيم شاه:</span>
نحوي، متكلم، منطقي، مفسر، في تاريخ وفاته خلاف بين المؤرخين، قيل: مات سنة ٩٢٨، وقيل: في حدود سنة ٩٢٠، وقيل سنة ٩٦٦ هـ وفي الشقائق النعمانية أنه من علماء
_________
(١) محمد لطفي جمعة كتاب في سيرته لرابح لطفي جمعة. ومجلة الأديب أغطس ١٩٥٣، مجلة المجمع العلمي العربي ٧: ٨٩ و ٥٧٠ والأعلام ٧: ٢٣٨ ومعجم المؤلفين ١١: ١٦٦ ومذكرات المؤلف.','2','610',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1100,'القرن التاسع. من آثاره "تفسير القرآن" من سورة النحل إلى آخر القرآن (١).

ابن الأشعث [... - نحو ٣٣٠ هـ / ... - نحو ٩٣١ م]

<span data-type="title" id=toc-1513>محمد بن محمد بن الأشعث بن محمد الكوفي، أبو الحسن:</span>
فقيه إمامي، محدث، مفسر، سكن مصر، وكتب عنه ابن عدي. قال في "هدية العارفين" له "جوامع التفسير" (٢).

ابن اللباد [٢٥٠ - ٣٣٣ هـ / ٨٦٤ - ٩٤٤ م]

<span data-type="title" id=toc-1514>محمد بن محمد بن وشاح اللخمي بالولاء، أبو بكر ابن اللباد:</span>
عالم بالتفسير واللغة، من فقهاء المالكية، من أهل القيروان. قال أبو العرب: "كان فقيها جليل القدر، عالما باختلاف أهل المدينة وإجتماعهم، مهيبا مطاعا دينا ورعا زاهدا، من الحفاظ المعدودين والفقهاء المبرزين". فلج في آخر
_________
(١) هدية العارفين ٢: ٢٢٩ ودار الكتب ١: ٣٩٤ والشقائق النعمانية ٢٠٠ ومعجم المؤلفين ١١: ١٥١ و ١٧٠ ومعجم المطبوعات ١٦٣٢.
(٢) لسان الميزان ٥: ٣٦٢ وهدية العارفين ٢: ٣٦ وأعيان الشيعة ٤٥: ٣١٣.','2','610',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1101,'عمره، ومات بالقيروان (١).

الماتريدي [... - ٣٣٣ هـ / ... - ٩٤٤ م]

<span data-type="title" id=toc-1515>محمد بن محمد بن محمود، أبو منصور الماتريدي، السمرقندي، الملقب ب"إمام الهدى" و "إمام المتكلمين، و "مصحح عقائد المسلمين" و "رئيس أهل السنة" وإليه نسبة الماتريدية:</span>
من أهل "ماتريد" محلة بسمرقند فيما وراء النهر. من آثاره "تأويلات أهل السنة" في تفسير القرآن، طبع، بتحقيق الدكتور إبراهيم عوضين (٢).

الحجاجي [٢٨٥ - ٣٦٨ هـ / ٨٩٨ - ٩٧٩ م]

<span data-type="title" id=toc-1516>محمد بن محمد بن يعقوب بن إسماعيل بن حجاج النيسابوري، أبو الحسين:</span>
_________
(١) الديباج ٢٤٩ والوافي ١: ١٣٠ وشجرة النور ٨٤ ومعالم الإيمان ٢: ٢٣ والأعلام ٧: ٢٤٢ ومعجم المؤلفين ١١: ٣٠٩ وترتيب المدارك ٣: ٣٠٤.
(٢) الفوائد البهية ١٩٥ وهدية العارفين ٢: ٣٦ وكشف الظنون ٣٣٥ والجواهر المضية ٢: ١٣٠ وتاج التراجم ٤٣ ومعجم المؤلفين ١١: ٣٠٠ وفي الحاشية أسماء لمصادر أخرى. وتاريخ التراث العربي ١: ٢٢٣ و ٢: ٣٧٨.','2','611',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1102,'محدث، حافظ، مقرىء، عارف بعلوم القرآن. من أهل نيسابور. سمع ببغداد، وروى عنه ابن منده والحاكم وغيرهما. من آثاره "ناسخ القرآن ومنسوخه" (١).

النيسابوري [... - بعد ٣٩٠ هـ / ... - بعد ١٠٠٠ م]

<span data-type="title" id=toc-1517>محمد بن محمد بن زكريا النيسابوري، أبو سعيد:</span>
فقيه، مفسر، ثقة في الرواية، من أهل نيسابور. قدم قزوين غازيا، وتوفي بعد سنة ٣٩٠ هـ. قال الرافعي: "روى عنه الخليلي في مشيخته" (٢).

الشيخ المفيد [٣٣٦ - ٤١٣ هـ / ٩٤٧ - ١٠٢٢ م]

<span data-type="title" id=toc-1518>محمد بن محمد بن النعمان بن عبد السلام العكبري، الحارثي، البغدادي، الكرخي، المعروف بالشيخ المفيد، وبابن المعلم، أبو عبد الله:</span>
فقيه، أصولى، متكلم، من شيوخ
_________
(١) الوافي ١: ١٢٨ وتذكرة الحفاظ ٣: ٩٤٤ وهدية العارفين ٢: ٤٩ وكشف الظنون ١٩٢١ وشذرات الذهب ٣: ٦٧.
(٢) طبقات المفسرين للداودي ٢: ٢٣٤ وتاريخ قزوين ١٥٢.','2','611',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1103,'الإمامية. ولد بعكبرا من أعمال الدجيل بالعراق (على عشرة فراسخ من بغداد) وقرأ عليه الرضي والمرتضى. قال الذهبي: "أكثر من الطعن على السلف، وكانت له صولة، في دولة عضد الدولة". له قريب من مائتي مصنف، منها "الكلام في وجوه إعجاز القرآن" (١).

الرامشي [٤٠٤ - ٤٨٩ هـ / ١٠١٣ - ١٠٩٦ م]

<span data-type="title" id=toc-1519>محمد بن محمد بن همياه، أبو نصر الرامشي:</span>
محدث، عالم بالقراءات وعلوم القرآن، نحوي، لغوي، من أهل نيسابور، رحل في طلب الحديث، فسمع بالعراق والحجاز والشام ومصر، وقرأ بمعرة النعمان على أبي العلاء، وعقد له مجلس للإملاء بنيسابور، وأملى في المدرسة النظامية.
_________
(١) الوافي ١: ١١٦ وفهرست الطوسي ١٥٧ ولسان الميزان ٥: ٣٦٨ والمنتظم ٨: ١١ وشذرات الذهب ٣: ١٩٩ وأعيان الشيعة ٤٦: ٢٠ والأعلام ٧: ٢٤٥ ومعجم المؤلفين ١١: ٣٠٦ ومجلة العرفان ٣: ٢٥٣ ومرآة الجنان ٣: ٢٨ وروضات الجنات ٤: ٢٤ والنجاشي ٢٨٣ وتاريخ بغداد ٣: ٢٣١ والنجوم الزاهرة ٤: ٢٥٨ وتاريخ المقال ٣: ١٨٠ ومصفى المقال ٤٢٣ وفوائد الرضوية ٦٢٨ ومنهج المقال ٣١٧ ومختصر دول الإسلام ١: ١٩١.','2','612',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1104,'أثنى عليه عبد الغافر الفارسي. توفي بنيسا بور (١).

الغزالي [٤٥٠ - ٥٠٥ هـ / ١٠٥٨ - ١١١١ م]

<span data-type="title" id=toc-1520>محمد بن محمد بن محمد بن أحمد الغزالي الطوسي، أبو حامد، حجة الإسلام:</span>
فيلسوف، متصوف، من أعيان فقهاء الشافعية، ولد بالطابران (قصبة طوس، بخراسان) ورحل إلى جرجان فأخذ عن أبي نصر الإسماعيلي، ثم إلى نيسابور فأخذ عن إمام الحرمين ولازمه، ثم جلس للإقراء، وحضر مجلس نظام الملك، فأقبل عليه نظام الملك، فعظمت منزلة الغزالي. وندب للتدريس بنظامية بغداد، ثم أقبل على العبادة والسياحة، فحج، ورجع إلى دمشق فاستوطنها عشر سنين، ثم سار إلى القدس والإسكندرية. وعاد إلى طوس، فطلبه الوزير فخر الدين ابن نظام الملك إلى نظامية نيسابور فأجاب إلى ذلك، ثم عاد إلى الطابران وتوفي
_________
(١) طبقات المفسرين للداودي ٢: ٢٣٣ وبغية الوعاة ١: ٢١٨ والوافي ١: ١٢٤ ووفاته فيه سنة ٤٩٠.','2','612',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1105,'بها. نسبته إلى صناعة الغزل (عند من يقوله بتشديد الزاي) أو إلى غزالة من قرى طوس (لمن قال بالتخفيف). من كتبه الكثيرة "جواهر القرآن" طبع، و "الوقف والإبتداء" في التفسير، مخطوط، و "ياقوت التأويل في تفسير التنزيل" كبير، قيل: في نحو أربعين مجلدا، وغير ذلك (١).

القطواني [٤٤٤ - ٥٠٦ هـ / ١٠٥٢ - ١١١٢ م]

<span data-type="title" id=toc-1521>محمد بن محمد بن أيوب، أبو محمد القطواني:</span>
مفسر، واعظ، من فقهاء الحنفية، من أهل قطوان، موضع بسمرقند. ذكره السمعاني في الأنساب وأثنى عليه وقال: "كان مفتيا واعظا مفسرا. سقط عن دابته منصرفا من
_________
(١) المجددون في الإسلام ١٨١ وتبين كذب المفتري ٢٩١ ومعجم المطبوعات ١٤٠٨ وتاريخ فلاسفة الإسلام ٦٧ ووفيات الأعيان ٤: ٢١٦ وطبقات السبكي ٤: ١٠١ وتاريخ الفلسفة في الإسلام ١٩٦ واللباب ٢: ١٧٠ وكنوز الأجداد ٢٧٢ ومرآة الجنان ٣: ١٧٧ والأعلام ٧: ٢٤٧ ومعجم المؤلفين ١١: ٢٢٦ والغزالي لكارادفو (ترجمة زعيتر) والمنتظم ٩: ١٦٨ والنجوم الزاهرة ٥: ٢٠٣ ومفتاح السعادة ٢: ١٩١ والأخلاق عند الغزالي لزكي مبارك، والحقيقة في نظر الغزالي لسليمان دنيا، وشذرات الذهب ٤: ١٠ والأنس الجليل ١: ٢٩٩ ومصادر الدراسة الأدبية ١: ٢٦٩ وفي حاشيته ثبت بمصادر ومراجع أخرى كثيرة.','2','613',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1106,'صلاة الجمعة، فمات من ذلك، (١).

ابن بنان [٥٠٧ - ٥٩٦ هـ / ١١١٣ - ١٢٠٠ م]

<span data-type="title" id=toc-1522>محمد بن محمد بن محمد بن محمد بن بنان، أثير الدين أبو الطاهر الأنباري ثم المصري:</span>
من الكتاب البلغاء. عرفه ابن قاضي شهبة بالقاضي الأمير ذي الرياستين. أصله من الأنبار، ومولده ووفاته بالقاهرة. تولى ديوان النظر في الدولة الفاطمية، فلما زالت دولتهم على يد السلطان صلاح الدين الأيوبي، ولي الإسكندرية وتنيس وغير ذلك. وكان القاضي الفاضل ممن يغشى بابه ويمدحه. ثم انتقل إلى اليمن ووزر لسيف الإسلام طغتكين بن أيوب، ودخل بغداد رسولا من قبل سيف الإسلام سنة ٥٨٢ هـ. ثم عاد إلى القاهرة، فاعتقله الصالح بن رزيك لدين كبير كان عليه، وحبسه بالجامع الأزهر، وحاول أحد الدائنين الإعتداء عليه وهو على سطح الجامع، ففر من بين يديه وألقى نفسه من السطح،
_________
(١) الفوائد البهية ١٨٦ والمشتبه ٢: ٥٣٢ وطبقات المفسرين ٢: ٢٣٤ والجواهر المضية ٢: ١١٥ واللباب ٣: ٤٧ والأنساب ٤٥٨ ب.','2','613',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1107,'فتهشم، ومات بعد أيام. من كتبه "تفسير القرآن المجيد" (١).

السجاوندي [... - نحو ٦٠٠ هـ / ... - نحو ١٢٠٤ م]

<span data-type="title" id=toc-1523>محمد بن محمد بن طيفور، سراج الدين، أبو طاهر، السجاوندي:</span>
فقيه حنفي، فرضي، مفسر، حاسب. من آثاره "إنسان العين" في تفسير القرآن، اختصر فيه "عين المعاني في تفسير السبع المثاني" لأبيه (راجع ترجمته في هذا المعجم) (٢).

الأسفراييني [... - ٦٨٤ هـ / ... - ١٢٨٥ م]

<span data-type="title" id=toc-1524>محمد بن محمد بن أحمد، تاج الدين (الأسفراييني)</span>
_________
(١) طبقات المفسرين ٢: ٢٤٨ والوافي ١: ٢٨١ وإنباه الرواة ٣: ٢٠٩ والنجوم الزاهرة ٦: ١٥٩ والعبر ٤: ٢٩٤ وحسن المحاضرة ١: ٣٧٥ والشذرات ٤: ٣٢٧ وفوات الوفيات ٣: ٢٥٩ ومختصر الدبيثي ١: ١٢٢ وتاج العروس ٩: ١٤٥ والأعلام ٧: ٢٥٣.
(٢) الجواهر المضية ٢: ١١٩ ولم يذكر وفاته، وهدية العارفين ٢: ١٠٦ وفيه: المتوفى في حدود سنة ٦٠٠ وقيل ٧٠٠ هـ. والمستدرك على الكشاف ١٦٢، ١٦٦ وهو فيه: سراج الدين، محمد بن عبد الرشيد". ومعجم المطبوعات ١٠٠٧. وإنباه الرواة ٣: ١٥٣ في ترجمة والده. ومعجم المؤلفين ١١: ٢٣٣ والأعلام ٧: ٢٧.','2','614',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1108,'الأسفراييني: عالم بالنحو. له فيه كتب. ذكره السيوطي وقال: "لم أقف له على ترجمة". وفي فهرست الخديوية أنه مات سنة ٦٨٤ هـ. من كتبه "فاتحة الإعراب بإعراب الفاتحة" مخطوط (١).

النسفي [٦٠٠ - ٦٨٧ هـ / ١٢٠٣ - ١٢٨٩ م]

<span data-type="title" id=toc-1525>محمد بن محمد بن محمد، أبو الفضل، برهان الدين النسفي:</span>
فقيه حنفي، عالم بالتفسير والأصول والكلام. من أهل نسف، سكن بغداد وتوفي بها. من كتبه "الواضح" في تلخيص "مفاتيح الغيب" في التفسير لفخر الدين الرازي (٢).
_________
(١) معجم المطبوعات ٤٣٦ وهدية العارفين ٢: ١٣٤ وعنه أخذت تاريخ وفاته. وبغية الوعاة ١: ٢١٩ والأعلام ٧: ٢٥٩ ومعجم المؤلفين ١١: ١٨٠ وكشف الظنون ٣. ١٥.
(٢) الوافي ١: ٢٨٢ والفوائد البهية ١٩٤ وتذكرة النبيه ١: ١٢٠ وطبقات المفسرين للداودي ٢: ٢٥٠ وهدية العارفين ٢: ١٣٥ وكشف الظنون ١٧٥٦ والجواهر المضية ٢: ١٢٧ والشذرات ٥: ٣٨٥ وتاج التراجم ٥٨ ومرآة الجنان ٤: ٢٠٠ والأعلام ٧: ٢٦٠.','2','614',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1109,'الزندني [... - نحو ٧٠٠ هـ / ... - نحو ١٣٠٠ م]

<span data-type="title" id=toc-1526>محمد بن محمد بن محمد، تاج الدين، أبو المحامد، البخاري الزندني:</span>
مقرىء ما وراء النهر، محدث، مفسر، من أهل زندة - قرية ببخارى -. كتب عنه أبو العلاء الفرضي وقال: "له معرفة تامة بروايات القراء وطرقهم في السبع والشواذ، عارف بعلل القراءات" ولم يؤرخ وفاته، وقال ابن الجزري: "أظنه بقي إلى قريب السبعمائة بل تجاوزها" (١).

الكاشغري [... - ٧٠٥ هـ / ... - ١٣٠٥ م]

<span data-type="title" id=toc-1527>محمد بن محمد بن علي الكاشغري:</span>
فقيه، نحوي، لغوي، مفسر، واعظ، صوفي كان حنفيا وتحول شافعيا، أصله من كاشغر، جاور بمكة أربع عشرة سنة، ودخل اليمن، فأقام بتعز، وتوفي بساحل موزع (٢).
_________
(١) المشتبه ١: ٣٠٥ وطبقات المفسرين للداودي ٢: ٢٤٤ وغاية النهاية ٢: ٢٥٤ و ٢٤٦ ترجمة الجنبدي.
(٢) طبقات المفسرين للداودي ٢: ٢٤٤ وبغية الوعاة ١: ٢٣٠ والعقود اللؤلؤية ١: ٣٦٨ والأعلام ٧: ٢٦١ ومعجم المؤلفين ١١: ٢٤٩ وهدية العارفين ٢: ١٤٠.','2','615',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1110,'الحميري [... - حيا ٧٢٦ هـ / ... - حيا ١٣٢٦ م]

<span data-type="title" id=toc-1528>محمد بن محمد بن عبد النور الحميري التونسي:</span>
فقيه مالكي، مفسر، كان من صدور العلم في عصره، أخذ عن أبي القاسم بن زيتون وأبي محمد بن برطلة. من كتبه "نفحات الطيب في اختصار تفسير ابن الخطيب" في سبعة أسفار (١).

ابن القوبع [٦٦٤ - ٧٣٨ هـ / ١٢٦٦ - ١٣٣٨ م]

<span data-type="title" id=toc-1529>محمد بن محمد بن عبد الرحمن بن يوسف الجعفري، ركن الدين، أبو عبد الله، ابن القوبع:</span>
أديب، لغوي، مفسر، له اشتغال بالحكمة والطب. ولد بتونس، وبها نشأ وتعلم. وانتقل إلى دمشق فسمع بها من ابن القواس وغيره، ودرس بالمنكوتمرية، وأعاد بالناصرية وغيرها، ودرس الطب بالمارستان المنصوري، وناب في الحكم بالقاهرة، وتوفي بها.
_________
(١) الديباج المذهب ٣٣٧ وطبقات المفسرين للداودي ٢: ٢٤١ ومعجم المؤلفين ١١: ٢٤١. وشجرة النور ١: ٢٠٦.','2','615',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1111,'له كتب، منها "تفسير سورة ق" في مجلد (١).

البزرالي [٦٨١ - ٧٣٥ هـ / ١٢٨٢ - ١٣٣٥ م]

<span data-type="title" id=toc-1530>محمد بن محمد بن محمود بن قاسم البرزالي، شمس الدين، أبو عبد الله:</span>
فقيه حنبلي، عالم بالتفسير والأصلين والأدب وغير ذلك، من أهل بغداد. درس بالمستنصرية. قال الصفدي: "ورأس في الطب، وصنف فيه، وسافر إلى الهند، ورجع، ومات ببغداد" (٢).

ابن الإمام [٦٨٢ - ٧٤٥ هـ / ١٢٨٣ - ١٣٤٤ م].

<span data-type="title" id=toc-1531>محمد بن محمد بن علي بن همام بن راجي الله ابن سرايا، أبو الفتح، تقي الدين، المعروف بابن الإمام:</span>
محدث، عالم بالقراءات والتفسير، من فقهاء الشافعية. عسقلاني الأصل، من أهل مصر، تخرج
_________
(١) الدرر الكامنة ٤: ٢٩٩ وبغية الوعاة ١: ٢٢٦ والوافي ١: ٢٣٨ وطبقات المفسرين للداودي ٢: ٢٣٧ وهدية العارفين ٢: ١٤٩ ومعجم المؤلفين ١١: ٢٣٣.
(٢) الوافي ١: ٢٣٧ وطبقات المفسرين للداودي ٢: ٢٥١ والشذرات ٦: ١١١ وابن رجب ٢: ٤٢٥ والدرر الكامنة ٥: ٣.','2','616',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1112,'بالحافظ الدمياطي، وتولى الإمامة بالجامع الصالحي، وتوفي بظاهر القاهرة. له "متشابه القرآن، رتبه على السور (١).

الشعيبي [٦٧٧ - ٧٤٧ هـ / ١٢٧٨ - ١٣٤٦ م]

<span data-type="title" id=toc-1532>محمد بن محمد بن محمد بن زنكي الأسفراييني العراقى، أبو عبد الله، المعروف بالصدر الشعيبي:</span>
فقيه شافعي، نحوي، متكلم، مفسر، ولد بأسفرايين، وتنقل في إيران مدة، وأقام ببخارى، وانتقل إلى بغداد سنة ٧٠٥ هـ وتوفي بها. من تصانيفه "الناسخ والمنسوخ" مخطوط (٢).

القطب التحتاني [٦٩٤ - ٧٦٦ هـ / ١٢٩٥ - ١٣٦٥ م]

<span data-type="title" id=toc-1533>محمد (أو محمود) بن محمد الرازي، أبو عبد الله، قطب الدين:</span>
إمام في المنطق والحكمة، عالم بالتفسير والمعاني والبيان، مشارك في النحو، من أهل الري، أخذ عن
_________
(١) الدرر الكامنة ٤: ٣٢٣ والنجوم الزاهرة ١٠: ١٤٦ وغاية النهاية ٢: ٢٤٥ وشذرات الذهب ٦: ١٤٤ وفيه مولده سنة ٦٧٧ هـ كما في الدرر الكامنة وهدية العارفين ٢: ١٥٢.
(٢) الأعلام ٧: ٢٦٥ وهدية العارفين ٢: ١٥٣ و ١٧٤ (وهنا وفاته سنة ٧٩٢ هـ) وتاريخ علماء بغداد ٢٠٤ وذيل كشف الظنون ٢: ٥٥٧ وفيه: مات سنة ٧٩٢ هـ .. ومعجم المؤلفين ١١: ٢٧٨.','2','616',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1113,'العضد وغيره. ودخل دمشق سنة ٧٦٣ هـ واشتغل بها في العقليات، وأقام بها إلى أن توفي. وعرف بالتحتاني تمييزا له عن شخص آخر يكنى قطب الدين أيضا، كان يسكن معه في أعلى المدرسة الظاهرية بدمشق. من كتبه "حاشية" على الكشاف في التفسير للزمخشري، وصل فيها إلى سورة طه (١).

ابن الموصلي [٦٩٩ - ٧٧٤ هـ / ١٣٠٠ - ١٣٧٢ م]

<span data-type="title" id=toc-1534>محمد بن محمد بن عبد الكريم بن رضوان ابن عبد العزيز البعلي، شمس الدين، ابن الموصلي:</span>
نحوي، أديب، له نظم، من فقهاء الشافعية. ولدفي بعلبك، وتعلم بها وبدمشق وحماة، وسكن دمشق أعواما وتصدر بالجامع الأموي للإفادة، ودخل القاهرة، وأقام بطرابلس الشام وتوفي بها. له "غاية الإحسان" في قوله تعالى {إن الله يأمر
_________
(١) طبقات السبكي ٦: ٣١ وطبقات الإسنوي ٤٧ وطبقات المفسرين للداودي ٢: ٢٥٣ ومفتاح السعادة ١: ٢٩٨ وبغية الوعاة ٢: ٢٨١ والشذرات ٦: ٢٠٧ والنجوم الزاهرة ١١: ٨٧ ومعجم المطبوعات ٩١٨ وأعيان الشيعة ٤٥: ٣٤٣ وتاريخ الأدب الفارسي ١٩٩ وهدية العارفين ٢: ١٦٣ والدرر الكامنة ٥: ١٠٧ واسمه فيه محمود، وقال: يقال اسمه محمد، وبه جزم ابن كثير وابن رافع وابن حبيب. وبالأول، أي محمود، جزم الإسنوي". قلت: اسمه في أكثر المصادر: محمد بن محمد.','2','617',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1114,'بالعدل والإحسان} (سورة النحل: ٩٠) و "بهجة المجالس ورونق المجالس" خمس مجلدات، يتضمن الكلام على آيات وغيرها (١).

الأقسرائي [... - بعد ٧٧٦ هـ / ... - بعد ١٣٧٤ م]

<span data-type="title" id=toc-1535>محمد بن محمد بن محمد بن الإمام فخر الدين محمد، جمال الدين المعروف بالأقسرائي:</span>
مفسر، عارف بالطب واللغة والأدب، نسبته إلى "أق سراي" من بلاد الروم، ومعناها "القصر الأبيض". وهو حفيد الإمام فخر الدين محمد الرازي. قال طاشكبري زاده: "كان مدرسا في بلاد قرامان بمدرسة "السلسلة" وقد شرط بانيها أن لا يدرس فيها إلا من حفظ "الصحاح" للجوهري، فعين لها جمال الدين. وكتب "حواش على الكشاف". وقال في كشف الظنون أن له "حاشية" على الكشاف فيها اعتراضات على حاشية قطب الدين محمد بن محمد الرازي المتوفى سنة ٧٦٦ هـ. في تاريخ وفاته خلاف بين مؤرخيه، قيل: سنة ٧٧١، وقيل: سنة ٧٧٩، وقيل سنة ٧٩١
_________
(١) الوافي بالوفيات ١: ٢٦٢ والدرر الكامنة ٤: ٣٠٦ وإنباء الغمر ١: ٥٢ والشذرات ٦: ٢٣٦ وهدية العارفين ٢: ١٦٦ وبغية الوعاة ١: ٢٢٨ وطبقات المفسرين للداودي ٢: ٢٣٩ والأعلام ٧: ٢٦٩ ومعجم المؤلفين ١١: ٢٣٥.','2','617',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1115,'هـ. قال الزركلي: "وفي خزانة داماد إبراهيم في إسطنبول نسخة من كتابه "إيضاح الإيضاح" بخطه، انجزها في شعبان ٧٧٦ هـ " (١)

البابرتي [٧١٤ - ٧٨٦ هـ / ١٣١٤ - ١٣٨٤ م]

<span data-type="title" id=toc-1536>محمد بن محمد بن محمود بن أحمد، أكمل الدين، أبو عبد الله ابن الشيخ شمس الدين ابن الشيخ جمال الدين، الرومي البابرتي:</span>
أصولي، مفسر، أديب، نحوي، من فقهاء الحنفية. نسبته إلى بابرت، على بعد مائة كيلومتر من "أرضروم" في تركيا، أو إلى بابرتي من أعمال دجيل بالعراق، والأول أشهر وأصح لقول بعض مترجميه، ومنهم ابن حجر، إنه رومي. تعلم ببلده، ثم رحل إلى حلب ونزل بالمدرسة السادجية وأقام بها مدة. وانتقل إلى القاهرة بعد سنة ٧٤٠ هـ فأخذ عن أبي حيان وغيره. قال ابن حجر: "وصحب شيخو (الناصري) واختص به، وقرره شفي بالخانقاه التي أنشأها وفوض
_________
(١) هدية العارفين ٢: ١٦٦ وكشف الظنون ١٤٧٨ والشقائق النعمانية ١٤ والفوائد البهية ١٩١ ومجلة معهد المخطوطات ٥: ٢٧٥ والأعلام ٧: ٤٠ ومعجم المؤلفين ١١: ١٩٢ وبروكلمان، الذيل ٢: ٣٢٨ وفهرس دار الكتب ٢: ١٧٧.','2','618',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1116,'أمورها إليه، فباشرها أحسن مباشرة وعمر أوقافها، وزاد معاليمها. وعرض عليه القضاء مرارا فامتنع. وكان الظاهر يبالغ في تعظيمه ... " توفي بالقاهرة، وحضر السلطان فمن دونه جنازته، وأراد السلطان حمل نعشه فمنعه الأمراء، وحمله بعضهم. من آثاره "حاشية" على الكشاف للزمخشري، قال صاحب كشف الظنون: رأيت منها مجلدا على الفاتحة، وقطعة من البقرة، ولا أدري أكملها أم لا، وصل فيها إلى تمام الزهراوين، أوله: الحمد لله علام الغيوب كشاف الكروب". وذكر بعض مترجميه أنه عمل "تفسيرا للقرآن" ومن هؤلاء ابن حجر في كتابه "إنباء الغمر". وقد علق قارىء على إحدى مخطوطات الإنباء بقوله: "هو ليس بتفسير مستقل، بل حاشية على تفسير القاضي البيضاوي لكنه لم يكمله، رأيته وطالعته وانتفعت به" (١).
_________
(١) إنباء الغمر ١: ٢٩٨ والدرر الكامنة ٥: ١٨ وبغية الوعاة ١: ٢٣٩ والنجوم الزاهرة ١١: ٣٠١ والفوائد البهية ١٩٥ وطبقات المفسرين ٢: ٢٥١ وشذرات الذهب ٦: ٢٩٣ وهدية العارفين ٢: ٢٧١ وتاج التراجم ٦٦ وكشف الظنون ٤٣٣ و ١٤٧٨ وبدائع الزهور ١: ٢٦١ والتعريف بابن خلدون ٢٧٢ والسلوك ج ٣ ق ٢: ٥٢١ و ٥٢٧ ونزهة النفوس والأبدان ١: ١٠٩ والأعلام ٧: ٤٢ ومعجم المؤلفين ١١: ٢٩٨. ومفتاح السعادة ٢: ١٣٢ وإيضاح المكنون ٢: ٣٥٣ وطبقات الفقهاء ١٢٦.','2','618',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1117,'ابن عرفه [٧١٦ - ٨٠٣ هـ / ١٤٠٠ - ١٣١٦ م]

<span data-type="title" id=toc-1537>محمد بن محمد بن عرفة بن حماد الورغمي، أبو عبد الله:</span>
فقيه تونس وإمامها وعالمها وخطيبها. مولده ووفاته فيها. تولى إمامة الجامع الأعظم سنة ٧٥٠ هـ، وقدم لخطابته سنة ٧٧٢ وللفتوى سنة ٧٧٣. قال ابن حجر: "صار إليه المرجع في الفتوى ببلاد المغرب، وكان معظما عند السلطان فمن دونه، وعلق عنه بعض أصحابه كلاما في التفسير كثير الفوائد في مجلدين، وكان يلتقطه في حالة قراءتهم عليه ويدونونه أولا بأول، وكلامه فيه دال على توسع في الفنون وإتقان وتحقيق" (١).

ابن الشحنة [٧٤٩ - ٨١٥ هـ / ١٣٤٨ - ١٤١٢ م]

<span data-type="title" id=toc-1538>محمد بن محمد بن محمد بن محمود، أبو الوليد، محب الدين، ابن الشحنة الحلبي:</span>
قاض، مفسر، أصولي، من فقهاء الحنفية، له اشتغال بالأدب والتاريخ، من
_________
(١) ذيل تذكرة الحفاظ ١٩٣ وغاية النهاية ٢: ٢٤٣ وإنباء الغمر ٢: ١٩٢ وذيل الابتهاج ٢٧٤ وبغية الوعاة ١: ٢٢٩ وشذرات الذهب ٧: ٣٨ والديباج ٣٣٧ والضوء اللامع ٩: ٣٤٠ وطبقات المفسرين ٢: ٢٣٥ والأعلام ٧: ٢٩٥ وشجرة النور ٢: ٢٤٣ ودرة الحجال ٢: ٢٨٠ والوفيات لابن قنفذ ٣٧٩.','2','619',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1118,'أهل حلب مولدا ووفاة. تعلم بها وبدمشق والقاهرة، وأفتى ودرس. ولي قضاء حلب سنة ٧٧٨ هـ، وصرف وأعيد مرات، واستقضي بدمشق والقاهرة، ثم استقر بقضاء حلب إلى أن توفي. وامتحن وسجن. وصفه ابن حجر العسقلاني بالعلامة وقال: "واشتغل قديما ونبغ وتميز في الفقه والأدب والفنون". من كتبه "تفسير القرآن" و "شرح الكشاف" للزمخشري، قال ابنه: ولم يكملهما. ونظم ألف بيت في عشرة علوم منها الفقه والأصول والتفسير" (١).

الأزنيقي [... - ٨٢١ هـ / ... - ١٤١٨ م]

<span data-type="title" id=toc-1539>محمد بن محمد الأزنيقي الرومي، قطب الدين:</span>
فقيه حنفي، مفسر، نسبته إلى أزنيق في بلاد الروم، له تصانيف بالتركية والعربية، فمن العربية "تفسيرالقرآن". قال صاحب كشف الظنون: "وهو كبير في مجلدات" (٢).
_________
(١) السلوك ٤/ ١: ٢٥٤ والنجوم الزاهرة ١٣: ١٤٦ وإنباء الغمر ٢: ٥٣٤ والبدر الطالع ٢: ٢٦٤ وإعلام النبلاء ٥: ١٦١ والشذرات ٧: ١١٣ والضوء اللامع ١٠: ٣ وهدية العارفين ٢: ١٨٠ والأعلام ٧: ٢٧٣ ومعجم المؤلفين ١١: ٢٩٥.
(٢) كشف الظنون ٤٥٧ وهدية العارفين ٢: ١٨٤ وبروكلمان  G, ١١ : ٢٢٥, S, ١١ : ٣١٥','2','619',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1119,'البخاري [٧٤٦ - ٨٢٢ هـ / ١٣٤٥ - ١٤٢٠ م]

<span data-type="title" id=toc-1540>محمد بن محمد بن محمود بن محمد بن محمد ابن مودود، شمس الدين الجعفري البخاري الزاهد:</span>
فقيه حنفي، عالم بالتفسير، من أهل بخارى، قدم مكة وجاور بها. قال ابن حجر: "وانتفع الناس به في علوم المعقول، ومات بمكة في العشر الأخير من ذي الحجة عن ست وسبعين سنة". وقيل مات بالمدينة. من تصانيفه "تفسير القرآن العظيم" في مئة مجلد (١).

ابن الجزري [٧٥١ - ٨٣٣ هـ / ١٣٥٠ - ١٤٢٩ م]

<span data-type="title" id=toc-1541>محمد بن محمد بن محمد بن علي بن يوسف، أبو الخير، شمس الدين، العمري الدمشقي، ثم الشيرازي، ويعرف بابن الجزري:</span>
حجة في القراءات، محدث، حافظ، مفسر، ناظم، له اشتغال بالتاريخ، مشارك في بعض العلوم. ولد بدمشق، وتفقه بها، وطلب الحديث
_________
(١) إنباء الغمر ٣: ٢١٠ والضوء اللامع ١٠: ٢٠ الترجمة ٥٧ والفوائد البهية ١٩٩ وهدية العارفين ٢: ١٨٣ وقيه: له تفسير سور من جزءي الملك والنبأ، بالفارسية، ومثله في كشف الظنون ٤٤٧، ٤٤٨ والشذرات ٧: ١٥٧ والأعلام ٧: ٢٧٣. ومعجم المؤلفين ١٠: ٥ و ١١: ٣٠٠.','2','620',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1120,'والقراءات، وعمر للقراء مدرسة سماها "دار القرآن" ورحل إلى مصر مرارا. وولي قضاء الشام سنة ٧٩٣ هـ، ثم دخل بلاد الروم فأخذ أهل البلاد عنه علم القراءات، فلما كانت الفتنة سنة ٨٠٥ هـ أخذه تيمورلنك معه إلى بلاد ما وراء النهر وأنزله بمدينة كش. وفي سنة ٨٠٧ هـ رحل إلى خراسان، ودخل هراة وأصبهان ثم شيراز، فولي قضاءها. ثم رحل إلى الديار المقدسة وحج، وجاور بالمدينة وبمكة. ثم دخل العراق ومصر واليمن، وعاد إلى شيراز فتوفي بها. نسبته إلى جزيرة ابن عمر. له تصانيف في التفسير والحديث والفقه والعربية، منها "فضائل القرآن" مخطوط، جزء منه. و "كفاية الألمعي" في تفسير آية {يا أرض ابلعي} (١).

الأسفراييني [٧٩٨ - بعد ٨٤٩ هـ / ١٣٩٥ - بعد ١٤٤٥ م]

<span data-type="title" id=toc-1542>محمد بن محمد بن علي بن محمد بن علي، صدر الدين الرؤاسي، العكاشي، (الأسدي، القرشي، الشقاني، الأسفراييني)</span>
_________
(١) غاية النهاية ٢: ٢٤٧ والضوء اللامع ٩: ٢٥٥ وكتاب النشر، مقدمته. وهدية العارفين ٢: ١٨٧ وإنباء الغمر٣: ٤٦٦ وقضاة دمشق ١٢١ والبدر الطالع ٢: ٢٥٧ وشذرات الذهب ٧: ٢٠٤ ومعجم المطبوعات ٦٢ وفهرس الفهارس ١: ٢٢٣ والشقائق النعمانية ٢٥ ومجلة معهد المخطوطات ٢: ١٢٢ ومفتاح السعادة ١: ٣٩٢ والأعلام ٧: ٢٧٤ ومعجم المؤلفين ١١: ٢٩٩ وكشف الظنون ١٤٩٧.','2','620',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1121,'الأسدي، القرشي، الشقاني، الأسفراييني: فقيه شافعي، مفسر، أصولي، ولد بشقان - قصبة من بلاد خراسان - وبها نشأ وتعلم. وحج، فلقيه المؤرخ المفسر إبراهيم بن عمر البقاعي بمكة في شهر ربيع الأول سنة ٨٤٩ هـ وسمع منه وأثنى عليه. من آثاره "حواش على تفسير البيضاوي" و "الرسالة الفتحية في تفسير أوائل سورة الفتح" (١).

ابن إمام الكاملية [٨٠٨ - ٨٧٤ هـ / ١٤٠٥ - ١٤٧٠ م]

<span data-type="title" id=toc-1543>محمد بن محمد بن عبد الرحمن بن علي بن يوسف، أبو عبد الله، كمال الدين ابن إمام الكاملية:</span>
فقيه شافعي، أصولي، مفسر، من أهل القاهرة. كان يلي إمامة المدرسة الكاملية كأبيه. وسافر إلى الحجاز، فأدركه الأجل عند رأس ثغرة حامد، ودفن هناك. من كتبه "شرحان" على تفسير البيضاوي، مطولا ومختصرا (٢).
_________
(١) طبقات المفسرين للداودي ٢: ٢٤١ ونظم العقيان ١٦٥ والضوء اللامع ٩: ١٥٧ وعنوان الزمان (مخطوط) ٤:
(٢) نظم العقيان ١٦٣ والضوء اللامع ٩: ٩٣ وفيه: وفاته سنة ٨٦٤ هـ. والبدر الطالع ٢: ٢٤٤ وكشف الظنون ١٩٤ وإيضاح المكنون ١: ١٣٨ والأعلام ٧: ٢٧٨ ومعجم المؤلفين ١١: ٢٣١ وهدية العارفين ٢: ٢٠٦.','2','621',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1122,'ابن أمير حاج [٨٢٥ - ٨٧٩ هـ / ١٤٢٢ - ١٤٧٤ م]

<span data-type="title" id=toc-1544>محمد بن محمد بن محمد بن حسن بن علي المعروف بابن أمير حاج، ويقال له ابن الموقت، أبو عبد الله، شمس الدين:</span>
فقيه حنفي، أصولي، مفسر، من أهل حلب، تعلم بها وبحماه والقاهرة، وحج مرارا وجاور، وأفتى وأقرأ، وأقام ببيت المقدس، ومات بحلب. من كتبه "ذخيرة القصر في تفسير سورة والعصر" قال صاحب كشف الظنون: "أوله "الحمد لله الذي كرم نوع الإنسان .. " (١).

ابن قطلوبغا [... - ٨٨١ هـ / ... - ١٤٧٧ م]

<span data-type="title" id=toc-1545>محمد بن محمد بن عمر بن قطلوبغا، سيف الدين البكتمري:</span>
فقيه حنفي، أصولي، عالم بالعربية والتفسير، مصري، تركي الأصل، قال السخاوي: "كان ابن الهمام يصفه بأنه محقق الديار المصرية ويرجحه على سائر الجماعة .. ". تولى تدريس الفقه والتفسير في عدة مدارس.
_________
(١) الضوء اللامع ٩: ٢١٠ وكشف الظنون ٤٥١ و ٨٢٤ وهدية العارفين ٢: ٢٥٨ ونظم العقيان ١٦١ والرسالة المستطرفة ١٤٦ والشذرات ٧: ٣٢٨ وإعلام النبلاء ٥: ٢٨٥ ومعجم المؤلفين ١١: ٢٧٤ والأعلام ٧: ٢٧٨.','2','621',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1123,'وأخذ عنه جلال الدين السيوطي. مات بالقاهرة. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (١).

الزلديوي [... - ٨٨٢ هـ / ... ١٤٧٧ م]

<span data-type="title" id=toc-1546>محمد بن محمد بن عيسى العفوي الزلديوي، أبو عبد الله:</span>
فقيه مالكي، مفسر، من أهل تونس. عمر حتى زاد على المائة. من تصانيفه "تفسير القرآن" (٢).

جلبي خليفة [... - ٨٨٦ هـ / ... - ١٤٨١ م]

<span data-type="title" id=toc-1547>محمد بن محمد الجمالي البكري الخلوتي الرومي، المعروف بجلبي خليفة:</span>
فقيه، محدث، مفسر، توفي في الطريق إلى الحج. من آثاره "تفسير آية الكرسي" و "تفسير الفاتحة" (٣).
_________
(١) بغية الوعاة ١: ٢٣١ وفيه: ولد تقريبا عل رأس ثمانمائة. والضوء اللامع ٩: ١٧٣ وفيه: ولد تقريبا سنة ثمان وتسعين وسبعمائة أو التي بعدها. وهدية العارفين ٢: ٢١٠ والشذرات ٧: ٢٣٣٢ وحسن المحاضرة ١: ٢٧٣ والأعلام ٧: ٢٧٩.
(٢) شجرة النور ٢٥٩ وفيه: وفاته سنة ٨٧٤ والضوء اللامع ٩: ١٧٩ وهدية العارفين ٢: ٢١٠ وإيضاح المكنون ١: ٣٠٥ ونيل الابتهاج ٣١٥.
(٣) هدية العارفين ٢: ٢١٢ وإيضاح المكنون ٢: ٥١٠','2','622',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1124,'ابن العماد [٨٢٥ - ٨٨٧ هـ / ١٤٢٢ - ١٤٨٢ م]

<span data-type="title" id=toc-1548>محمد بن محمد بن علي بن محمد البلبيسي ثم القاهري، شمس الدين، ويعرف بابن العماد:</span>
فقيه شافعي، مفسر، ولد في بلبيسيا بمصر، ونشأ بها، وسكن القاهرة. ورحل إلى مكة والمدينة وبيت المقدس والخليل والمحلة وغيرها، وجاور بمكة وبالمدينة. وتوفي بالقاهرة. من كتبه "اختصار أنوار التنزيل" في التفسير، للبيضاوي، مع زيادات، حسنة، و "كشف السرائر في معنى الوجوه والأشباه والنظائر" وهو نوع من أنواع التفسير، حققه الدكتور فؤاد عبد المنعم أحمد (١).

البلقيني [٨٢١ - ٨٩٠ هـ / ١٤١٨ - ١٤٨٥ م]

<span data-type="title" id=toc-1549>محمد بن محمد بن عبد الرحمن بن عمر بن رسلان الكناني، البلقيني الأصل، القاهري، بدر الدين، أبو السعادات:</span>
فقيه شافعي، مفسر، محدث، نحوي، أصولي، ولد بالقاهرة، وأخذ عن علمائها، وناب في القضاء، وولي قضاء
_________
(١) هدية العارفين ٢: ٢١٢ وفيه: توفي بالمد ينة. والضوء اللامع ٩: ١٦٢ ومجلة الفيصل ٤١: ١٥٨. وكشف السرائر مقدمة المحقق، والأعلام ٧: ٥٠.','2','622',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1125,'العسكر، وتصدر للتدريس بجامع الأزهر وبغيره من الأماكن والبلاد، وأخذ عنه جماعة التفسير والحديث والفقه والفرائض والأصلين والعربية وغير ذلك. مات بالقاهرة. من كتبه "حاشية" على شرح "أنوار التنزيل" للإسنوي (١).

ابن أبي شريف [٨٢٢ - ٩٠٦ هـ / ١٤١٩ - ١٥٠١ م]

<span data-type="title" id=toc-1550>محمد بن محمد بن أبي بكر بن علي بن مسعود ابن رضوان، أبو المعالي، كمال الدين، المعروف بابن أبي شريف:</span>
فقيه شافعي، عالم بالأصول، مفسر، متكلم. ولد بالقدس، وأخذ عن علمائها، ورحل إلى القاهرة فأخذ عن شيخ الإسلام ابن حجر وغيره، وحبج، وجاور، وسمع بمكة وبالمدينة، واستوطن القاهرة ودرس بها وأفتى، ثم عاد إلى القدس، وتولى عدة مدارس، وتوفي بها. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي، لم تكمل (٢).
_________
(١) الضوء اللامع ٩: ٩٥ وهدية العارفين ٢: ٢١٣ وإيضاح المكنون ١: ١٣٩ والشذرات ٧: ٣٤٩ والبدر الطالع ٢: ٢٤٢.
(٢) الضوء اللامع ٩: ٦٤ ونظم العقيان ١٥٩ والبدر الطالع ٢: ٢٤٣ وهدية العارفين ٢: ٢٢٢ والأنس الجليل ٢: ٧٠٦ والشذرات ٨: ٢٩ والنور السافر ٤٣ والكواكب السائرة ١: ١١ وكشف الظنون ١٩٣ والأعلام ٧: ٢٨١ ومعجم المؤلفين ١١: ٢٠٠.','2','623',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1126,'البردعي [... - ٩٢٧ هـ ... - ١٥٢١ م]

<span data-type="title" id=toc-1551>محمد بن محمد بن محمد البردعي، التبريزي، محيي الدين:</span>
عالم تركي، مشارك في بعض العلوم، له معرفة تامة بالعربية. دخل شيراز وهراة وقرأ على علمائها. ثم درس بمدرسة أحمد باشا في بروسة، ثم بأدرنة، وتوفي بها. قال صاحب الشذرات: "كان حسن الأخلاق، متواضعا، يكتب الخط الحسن مع سرعة الكتابة" وقال صاحب الشقائق النعمانية: "كان له إنشاء بالعربية والفارسية في غاية الحسن، وتوفي في سنة ٩٢٨ أو سنة ٩٢٩ هـ ". من كتبه "حاشية" على تفسير البيضاوي (١).

ابن الحاج حسن [... - ٩٣٩ هـ / ... - ١٥٣٢ م]

<span data-type="title" id=toc-1552>محمد شاه بن محمد حسن الرومي المعروف بابن الحاج حسن:</span>
قاض، مفسر، من فقهاء الحنفية، تركي مستعرب، درس في القسطنطينية، ثم ولي القضاء بكوتاهية
_________
(١) الكواكب السائرة ١: ١٨ والشقائق النعمانية ٢٤٠ والشذرات ٨: ١٥٦ وهدية العارفين ٢: ٢٢٩ وإيضاح المكنون ١: ١٣٩ والأعلام ٧: ٢٨٣ ومعجم المؤلفين ١١: ٢٧٢.','2','623',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1127,'وغيرها، من آثاره "حاشية على تفسير البيضاوي" (١).

البكري [٨٩٩ - ٩٥٢ هـ / ١٤٩٣ - ١٥٤٥ م]

<span data-type="title" id=toc-1553>محمد بن محمد بن عبد الرحمن بن أحمد بن محمد بن أحمد بن محمد بن عوض بن عبد الخالق، أبو الحسن البكري الصديقي:</span>
مفسر، متصوف، من علماء الشافعية. مولده ووفاته بالقاهرة. كان يقيم عاما بمصر وعاما بمكة. قال في الشذرات: "وهو أول من حج من علماء مصر في محفة، ثم تبعه الناس، وكان له الإقبال العظيم من الخاص والعام، وشاع ذكره في أقطار الأرض مع صغر سنه". من كتبه "تسهل السبيل" في تفسير القرآن، ويسمى "تفسير البكري" منه نسخة في دار الكتب المصرية "برقم ٣٣ تفسي" جاء في نهايتها بخط والده محمد بن عبد الرحمن (٨١٧ - ٨٩١ هـ): "واعلم أن مؤلف هذا التفسير .. ولدي .. أبو الحسن محمد البكري .. وكتب ذلك الفقير .. محمد جلال الدين البكري". وقد نسب البغدادي (إيضاح المكنون ١: ٢٨٧) هذا
_________
(١) هدية العارفين ٢: ٢٣٤ وإيضاح المكنون ١: ١٤٥ وفيه: وفاته سنة ٩٣٣ ومعجم المؤلفين ١١: ٢٠٩ والشقائق النعماية ٢٥٣.','2','624',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1128,'التفسير لمحمد بن عبد الرحمن البكري المتوفى سنة ٩٩٤ هـ. وهذا غلط، فمحمد بن عبد الرحمن هو والد صاحب الترجمة، أما المتوفى سنة ٩٩٤ هـ فهو ابنه، وليس للإثنين أي تفسير. أما صاحب كشف الظنون (٢: ١٩٩٦) فقد سمى التفسير "الواضح الوجيز في تفسير القرآن العزيز" ونسبه لأبي الحسن محمد بن عبد الرحمن البكري المتوفى سنة ٩٥٠ هـ (كذا) وقال: وكان سنه حين الفراغ منه ثمانية وعشرين سنة، كما قال والده في آخره". قلت: الكتاب واحد لا ريب في ذلك، إنما يوجد خطأ واضح في نسخة دار الكتب، فكيف يكون الكتاب لولد مات أبوه قبل ولادته هو ب "٨" سنوات؟ (١).
_________
(١) الكواكب السائرة ٢: ١٩٤ وهو فيه: علي بن محمد، ومثله في الشذرات ٨: ٢٩٢ وهدية العارفين ٢: ٢٣٩ وهو فيه: محمد بن ابي محمد بن عبد الرحمن، وجامع كرامات الأولياء ١: ٣٠٣ وهو فيه: محمد بن محمد جلال الدين .. ولم يستبعد المؤلف أن يكون الأصل فيه "محمد علي، واقتصر بعض من تربوا له على "محمد"، وغيرهم على "علي" وقال: إن محمدا البكري الكبير هو ابن صاحب الترجمة ذكر أن اسم أبيه محمد. والنور السائر ٤١٤ ترجمة ابنه محمد بن محمد بن محمد. والأعلام ٧: ٢٨٥ ومعجم المؤلفين ١١: ٢٣٠ وانظر مادة البكري في دائرة المعارف الإسلامية. ومجلة المجمع العلمي العراقي ٨: ٣٠٦ وخطط مبارك ٣: ١٢٧ وتاريخ آداب اللغة العربية ٣: ٣٣٤ وإيضاح المكنون ١: ٤٦٠ و ٢: ٦٧١ وكشف الظنون ١: ٣٧٦.','2','624',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1129,'سبط المرصفي [... - ٩٦٦ هـ / ... - ١٥٥٩ م]

<span data-type="title" id=toc-1554>محمد بن محمد العمري، زين العابدين، المعروف بسبط المرصفي:</span>
متصوف، من فضلاء الشافعية، له نظم وتصانيف كثيرة، منها "الإبريز الخاص في فضائل البسملة وسورة الإخلاص" و "مطلع النيرين في تفسير الفاتحة وآية الكرسي والمعوذتين" (١).

عرب زاده [٩١٩ - ٩٦٩ هـ / ١٥١٣ - ١٥٦٢ م]

<span data-type="title" id=toc-1555>محمد بن محمد الأنطاكي، ثم البروسوي، الشهير بعرب زاده:</span>
فقيه حنفي، مفسر، رومي، له نظم وتآليف بالعربية، درس في بروسه ثم في القسطنطينية، وغضب عليه شيخ الإسلام، فضرب ونفي إلى بروسة مدة سنتين. وعفي عنه فأعيد إلى التدريس، ثم عين قاضيا بالقاهرة، وركب البحر، فلما اجتاز جزيرة رودس غرق بعض ركاب السفينة، وكان منهم. من كتبه "حاشية" على أنوار التنزيل" في التفسير،
_________
(١) هدية العارفين ٢: ٢٤٦ وجامعة الرياض ٦: ١٢٤ والأعلام ٧: ٥٨.','2','625',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1130,'للبيضاوي. مخطوط (١).

الفارسكوري [... - بعد ٩٦٤ هـ / ... - ١٥٥٧ م]

<span data-type="title" id=toc-1556>محمد بن محمد الفارسكوري، شمس الدين:</span>
فقيه حنفي، عارف بالتفسير، نسبته إلى بلدة فارسكور قرب دمياط بمصر. كان إماما في الجامع الغوري بالقاهرة. من آثاره "الإبانة في معرفة الأمانة" تفسير. قال في مقدمته أنه لما ورد القسطنطينية سنة ٩٦٤ هـ وجد بها نظاما وقانونا على نمط الشرع الشريف يعول عليه سلطانها ووزراؤه لقوله تعالى: {إن الله يأمركم أن تؤدوا الأمانات إلى أهلها} فكتب "الإبانة" في تفسير هذه الآية (٢).

أبو السعود [٨٩٨ - ٩٨٢ هـ / ١٤٩٣ - ١٥٧٤ م]

<span data-type="title" id=toc-1557>محمد بن محمد بن مصطفى العمادي، المولى أبو السعود:</span>
مفسر، أصولي، شاعر، عارف باللغات العربية والتركية والفارسية، من فقهاء الحنفية وعلماء الترك
_________
(١) العقد المنظوم ٢: ٢٢٨ وهدية العارفين ٢: ٢٤٧ وإيضاح المكنون ١: ١٤١ والأعلام ٧: ٢٨٨ ومعجم المؤلفين
(٢) كشف الظنون ١: ١ و ٤٣٤ وهدية العارفين ٢: ٢٤٦ ومعجم المؤلفين ١١: ٢٥٨.','2','625',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1131,'المستعربين، ولد بقرية بالقرب من القسطنطينية، وقرأ على والده كثيرا، ولازم المولى سعدي جلبي. ودرس في بلاد متعددة، ثم تقلد القضاء في بروسة فالقسطنطينية فقضاء العسكر في ولاية الروم إيلي ودام عليه مدة ثمان سنين. وأضيف إليه الإفتاء سنة ٩٥٢ هـ. قال صاحب الشذرات: "كان ذا مهابة عظيمة، فيه ميل زائد لأرباب الرئاسة". توفي بالقسطنطينية، قال صاحب "العقد المنظوم": وقد حضر جنازته العلماء والوزراء وسائر أرباب الديوان ... ودفن في جوار أبي أيوب الأنصاري". من آثاره "معاقد الطراف في أول تفسير سورة الفتح من الكشاف" حاشية على الكشاف للزمخشري. و "إرشاد العقل السليم إلى مزايا الكتاب الكريم" قال صاحب كشف الظنون: "اشتهر صيته وانتشر نسخه في الأقطار، ووقع التلقي بالقبول من الفحول والكبار، لحسن سبكه ولطفت تعبيره، فصار يقال له خطيب المفسرين. ومن المعلوم أن تفسير أحد سواه بعد الكشاف والقاضي (أنوار التنزيل) لم يبلغ إلى ما بلغ من رتبة الإعتبار والإشهار، والحق أنه حقيق به، مع ما فيه من المنافي لدعوى التنزيه". طبع. أول مرة سنة ١٢٧٥ هـ في بولاق بمصر (١).
_________
(١) العقد المنظيم ٣٩٤ والبدر الطالع ١: ٢٦١ والشذرات =','2','626',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1132,'الغزي [٩٠٤ - ٩٨٤ هـ / ١٤٩٩ - ١٥٧٧ م]

<span data-type="title" id=toc-1558>محمد بن محمد بن محمد بن أحمد بن عبد الله الغزي العامري الدمشقي، أبو البركات، أبو الجود، بدر الدين:</span>
عالم بالتفسير والحديث والأصول، مقرىء، ناظم، من فقهاء الشافعية، ولد بدمشق، وتعلم بها وبالقاهرة. ودرس وأفتى وشيوخه أحياء، وولي مشيخة القراء بالجامع الأموي وإمامة المقصورة، ودرس بالعادلية والفارسية والشامية البرانية والمقدمية، ورحل إليه طلاب العلم من الآفاق، ثم لزم العزلة عن الناس في أواسط عمره، فكان لا يزور أحدا من الأعيان ولا الحكام بل يقصدونه، وهو أبو نجم الدين محمد صاحبا الكواكب السائرة". مات بدمشق. له مئة وبضعة عشر كتابا، منها ثلاثة تفاسير، منثور ومنظومان، أشهرها المنظم الكبير في مئة وثمانين ألف بيت، سماها "التيسير في التفسير" قال صاحب كشف الظنون: "وأنكر كثير من العلماء عليه نظمه لأنه يؤدي إلى إخراج القرآن العظيم من نظمه الشريف
_________
= ٨: ٣٩٨ وهدية العارفين ٢: ٢٥٣ والكواكب السائرة ٣: ٣٥ ومعجم المطبوعات ٣١٥ والفوائد البهية ٨١ وكشف الظنون ٦٥، ٤٤١، ١٤٨٠، ١٤٨١ والأعلام ٧: ٥٩ ومعجم المؤلفين ١١: ٣٠١.','2','626',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1133,'لإدخاله في الوزن ما لم يكن من النظم الشريف". وله أيضا "تفسير آية الكرسي" (١).

البهنسي [... - ١٠٠١ هـ / ... - ١٥٩٢ م]

<span data-type="title" id=toc-1559>محمد بن محمد بن عبد الرحمن البهنسي العقيلي النقشبندي الخلوتي:</span>
متصوف، شافعي، جاور بمكة سنة ٩٩٥ هـ. من آثاره "تفسير القرآن" مخطوط، الجزء ٢١ منه، في معهد المخطوطات بالقاهرة (٢).

اللقيمي [... - بعد ١٠٠٤ هـ / ... - بعد ١٥٩٥ م]

<span data-type="title" id=toc-1560>محمد بن محمد اللقيمي، أبو الزهد:</span>
مفسر، شافعي، من آثاره "الشهاب المضيء بأنوار التنزيل" مخطوط، الجزء الثاني منه، وهو حاشية على تفسير البيضاوي، فرغ منه سنة ١٠٠٤ هـ (٣).
_________
(١) شذرات الذهب ٨: ٤٠٣ والكواكب السائرة ٣: ٣ والبدر الطالع ٢: ٢٥٢ وهدية العارفين ٢: ٢٨٥ وكشف الظنون ٤٤٣ و ٤٥٤ وإيضاح المكنون ١: ٣٤٣ ومعجم المؤلفين: ١١: ٢٧٩.
(٢) هدية العارفين ٢: ٢٥٩ وبروكلمان ٢: ٣٤٠ والذيل ٢: ٤٦٩ و ٤٩٣ والكتخانة ٢: ١٠١ ومعجم المؤلفين ١١: ٢٣٠.
(٣) مكتبة البلدية (تفسير) ٢٩ والأزهرية ١: ٢٧١ معجم المؤلفين ١١: ٢٦٢.','2','627',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1134,'الكرخي [٩١٠ - ١٠٠٦ هـ / ١٥٠٤ - ١٥٩٨ م]

<span data-type="title" id=toc-1561>محمد بن محمد الكرخي البكري، أبو عبد الله، بدر الدين:</span>
فقيه شافعي، أصولي، فرضي، عارف بالتفسير، اشتهر بمصر وتوفي بها. من آثاره "مجمع البحرين ومطلع البدرين على تفسير الجلالين" مخطوط أربع مجلدات، و "حاشية على الجرلين" في مجلدين، و "المنهج الأسنى في آية الكرسي والأسماء الحسنى" مخطوط (١)

البكري [٩٧١ - ١٠٠٧ هـ / ١٥٦٤ - ١٥٩٨ م]

<span data-type="title" id=toc-1562>محمد بن محمد بن محمد، أبو السرور، زين العابدين ابن أبي المكارم البكري، ويسمى تاج العارفين:</span>
مفتي السلطنة بمصر، كان آية في علم التصوف، وعالما بالتفسير. وهو أول من لقب بمفتي السلطنة في الديار المصرية. من تآليفه "تفسير القرآن" أربع مجلدات، لم يبيض، و "تفسير سورة الأنعام" مجلدان، و "تفسير سورة الكهف"
_________
(١) خلاصة الأثر ٤: ١٥٢ وهدية العارفين ٢: ٢٦٣ وإيضاح المكنون ١: ٣٠٤ و ٢: ٤٣٣ وكشف الظنون ٤٤٥ ومعجم المؤلفين ١١: ٢٦١ والأعلام ٧: ٢٩٠ والكتخانة ١: ١٩٨.','2','627',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1135,'مجلد، و " تفسير سورة الفتح" مجلد (١).

حجازي الواعظ [٩٥٧ - ١٠٣٥ هـ / ١٥٥٠ - ١٦٢٥ م]

<span data-type="title" id=toc-1563>محمد بن محمد بن عبد الله الأكراوي القلقشندي، المعروف بمحمد حجازي الواعظ:</span>
فقيه شافعي، عالم بالتفسير والحديث. ولد في منزلة (أكري من منازل الحاج المصري في توجهه إلى الحجاز). وسكن قلقشندة، وتوفي بالقاهرة. له تصانيف كثيرة، منها "الجواب المصون" في تفسير آية: {وما تعبدون} " و "كشف اللثام عن آية: {أحل لكم ليلة الصيام} " و "معترك الخلاص فى تكرير سورة الإخلاص" (٢).

البكري [٩٧٤ - ١٠٣٧ هـ / ١٥٦٦ - ١٦٢٨ م]

<span data-type="title" id=toc-1564>محمد (أبو المواهب) بن محمد بن محمد بن (علي (أبي الحسن) البكري الصديقي المصري)</span>
_________
(١) خلاصة الأثر ١: ١١٧ و ٤٧٤ وهو فيه: تاج العارفين ابن محمد. ومثله في هدية العارفين ١: ٢٤٥ وإيضاح المكنون ١: ٣٠٣ و ٣٠٦ و ٣٠٧ ومعجم المؤلفين ٣: ٨٨ وانظر أيضا بيت الصديق ٧٨ والأعلام ٧: ٢٩٠ وخطط مبارك ٣: ١٢٦.
(٢) خلاصة الأثر ٤: ١٧٤ والأعلام ٧: ٢٩٢ ومعجم المؤلفين ١١: ٢٨٣ وهديه العارفين ٢: ٢٧٤ وذيل كشف الظنون ٢: ٥٠٧.','2','628',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1136,'علي (أبي الحسن) البكري الصديقي المصري: أديب، له شعر ونثر، عالم بالتفسير. قال المحبي: "كان في بداية أمره مائلا إلى الخلاعة وكانت مجالسه مشحونة بأنواع الطرب والملاهي. وآلت إليه مشيخة المشايخ، بعد وفاة أخيه (أبي السرور تاج العارفين محمد سنة ١٠٠٧ هـ) فظهر بمظهر أسلافه وتصدر للتدريس وإملاء التفسير، إلى أن مات (١).

لويزان [... - حيا ١٠٤٤ هـ / ... - حيا ١٦٣٤ م]

<span data-type="title" id=toc-1565>محمد (الأمير) بن محمد (ظهير الدين)، معز الدين الشهير بلويزان الحسيني:</span>
فاضل إمامي، له اشتغال بالتفسير، قال العاملي: "له "تفسير سورة هل أتى" كتبه في حيدرآباد الدكن باسم السلطان عبد الله قطشاه، وبأمر الشيخ محمد بن خاتون العاملي وزير السلطان المذكور، فرغ منه سنة ١٠٤٤ هـ " (٢).
_________
(١) بيت الصديق ١٨٥ وخلاصة الأثر ١: ١٤٥ واسمه فيه "أبو المواهب بن محمد". وريحانة الألباء ٣١٦، والأعلام ٧: ٢٩٢.
(٢) أعيان الشيعة ٤٥: ٣٣٤','2','628',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1137,'ابن أبي السرور [١٠٠٥ - ١٠٨٧ هـ / ١٥٩٦ - ١٦٧٦ م]

<span data-type="title" id=toc-1566>محمد بن محمد أبي السرور زين العابدين ابن محمد أبي المكارم البكري الصديقي، المعروف بابن أبي السرور:</span>
مؤرخ، مفسر، انتهت إليه رآسة بيت البكري بمصر. ولد وتوفي بالقاهرة. له تصانيف، منها "تفسير القرآن" كبير، ويعرف بتفسير ابن أبي السرور (١).

البخشي [١٠٣٨ - ١٠٩٨ هـ / ١٦٢٨ - ١٦٨٧ م]

<span data-type="title" id=toc-1567>محمد بن محمد بن محمد البخشي الخلوتي البكفالوني الحلبي:</span>
فقيه شافعي، صوفي، مفسر، ناظم، ولد في بكفالون من قرى حلب، وتعلم بها وبدمشق، وسكن حلب، ثم حج وجاور، وتوفي بمكة. من تصانيفه "رسالة في تفسير: {سبح اسم ربك الأعلى} مخطوطة (٢).
_________
(١) خلاصة الأثر ٣: ٤٦٥ وبيت الصديق ٧٣ و ٧٨ وهدية العارفين ٢: ٢٩٥ وفهرس الفهارس ١: ٣٧٦ والأعلام ٧: ٢٩٤ وانظر حاشيته، ومعجم المؤلفين ١١: ٢٩٣ وفي الحاشية أسماء لمصادر أخرى.
(٢) إعلام النبلاء ٦: ٤٠٢ وخلاصة الأثر ٤: ٢٠٨ وهدية العارفين ٢: ٣٠٠ والأعلام ٧: ٢٩٤ ومعجم المؤلفين ١١: ٢٨٨.','2','629',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1138,'مير زاهد [... - ١١٠١ هـ / ... - ١٦٨٩ م]

<span data-type="title" id=toc-1568>محمد (مير زاهد) بن محمد أسلم الحسيني الهروي:</span>
باحث، له علم بالحكمة والمنطق، من فضلاء الأفغان. كان محتسب العسكر بكابل، وتوفي بها. له كتب بالعربية والفارسية، منها "تفسير القرآن" بالفارسية (١).

القمي [... - حيا ١١٠٧ هـ / ... . - حيا ١٦٩٦ م]

<span data-type="title" id=toc-1569>محمد بن محمد رضا بن إسماعيل بن جمال الدين القمي:</span>
فقيه إمامي، مفسر، أصله من قم، ومولده بمشهد. من كتبه "كنز الدقائق وبحر الغرائب" في تفسير القرآن (٢).
_________
(١) الأعلام ٧: ٢٩٥ ومعجم المطبوعات ١٨٩٣ وهدية العارين ٢: ٣٠١ وفيه: الكابلي المولد، الهندي المنشأ والدار". وبروكلمان، الذيل ٢: ٦٢١.
(٢) أعيان الشيعة ٤٥: ٣٢٨ وهدية العارفين ٢: ٣٠٤ ومعجم المؤلفين ١١: ٢١٧ وإيضاح المكنون ٢: ٣٨٥.','2','629',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1139,'البليدي [١٠٩٦ - ١١٧٦ هـ / ١٦٨٥ - ١٧٦٣ م]

<span data-type="title" id=toc-1570>محمد بن محمد بن محمد الحسني التونسي المعروف بالبليدي:</span>
فقيه مالكي، أصولي، عالم بالعربية والتفسير والقراءات، جزائري الأصل، من أهل بليدة. سكن القاهرة وتوفي بها. من كتبه "حاشية على تفسير البيضاوي" (١).

الخادمي [١١١٣ - ١١٧٦ هـ / ١٧٠١ - ١٧٦٣ م]

<span data-type="title" id=toc-1571>محمد بن محمد بن مصطفى بن عثمان، أبو سعيد الخادمي:</span>
مفسر، أصولي، من فقهاء الحنفية وعلمائهم. أصله من بلخ، وقيل من بخارى، قدم جده عثمان إلى "خادم"، من قرى قونية بالأناضول، واستوطنها. وفيها ولد صاحب الترجمة. اشتهر بدرس ألقاه في جامع أياصوفية باستنبول، في تفسير الفاتحة. توفي بخادم. من آثاره "حاشية على تفسير سورة الإخلاص" لابن سينا، و "حاشية على تفسير سورة النبأ" و "رسالة في تفسير قوله تعالى: {إن بعض الظن إثم} " و "رسالة في تفسير البسملة" و "رسالة في تفسير: {قل
_________
(١) سلك الدرر ٤: ١١٠ وإيضاح المكنون ١: ١٣٩ ومعجم المؤلفين ١١: ٢٧٥ والأعلام ٧: ٢٩٦.','2','630',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1140,'اللهم مالك الملك} و "رسالة في تفسير: {قد أفلح المؤمنون} " و "رسالة في تفسير: {وما تشاءون إلا أن يشاء الله ...} " (١).

التافلاتي [... - ١١٩١ هـ / ... - ١٧٧٧ م]

<span data-type="title" id=toc-1572>محمد بن محمد بن الطيب التافلاتي الأزهري الخلوتي، المالكي ثم الحنفي:</span>
فقيه، أصولي، محدث، باحث، ولد بالمغرب الأقصى، ورحل إلى طرابلس الغرب صغيرا، ثم إلى مصر فتعلم في الأزهر، ودخل دمشق مرارا، ثم استقر في القدس، وولي إفتاء الحنفية بها، إلى أن توفي "له تصانيف ناهزت الثمانين، منها "أسرار البسملة" و "حسن التبيان في معنى مدلول القرآن" (٢).
_________
(١) معجم المطبوعات ٨٠٨ و ١٩٧٣ - ١٩٧٤ وعثمانلي مؤلفلري ١: ٢٩٦ وهدية العارفين ٢: ٣٣٣ وهو فيه محمد بن مصطفى ووفاته سنة ١١٧٦ هـ. و ٤٥٢ وهو فيه مصطفى بن أحمد ووفاته سنة ١١٨٦ هـ. ومعجم المؤلفين ١١: ٢١٣ وهو فيه محمد بن محمد، وأنه كان حيا ١٩٨٦ هـ. و ٣٠١ وهو فيه محمد بن محمد بن مصطفى، وكان حيا ١١٦٨ هـ، و ١٢: ٣١ وهو فيه محمد بن مصطفى، ووفاته ١١٧٦ هـ، و ٢٣٨ وهو فيه: مصطفى بن أحمد ووفاته ١١٨٦ هـ. والتحرير الوجيز ٢١ والكشاف ١٠٢ ودار الكتب الشعبية ١٠: ١٧١ والأزهرية ٣: ٧٥٥ وطوبقو ٢: ٣٦٧ والخديوية ٢: ٧٠ و ٦: ١٤١ ودار الكتب المصرية ٦: ١٦٥ ومخطوطات الدار ١: ٣٨٩ - ٣٩١.
(٢) سلك الدرر ٤: ١٠٢ وهدية العارفين ٢: ٣٤١ - والأعلام ٧: ٢٩٦ ومعجم المؤلفين ٩١: ٢٢٧.','2','630',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1141,'الزبيدي [١١٤٥ - ١٢٠٥ هـ / ١٧٣٢ - ١٧٩٠ م]

<span data-type="title" id=toc-1573>محمد بن محمد بن محمد بن عبد الرزاق الحسيني الزبيدي، أبو الفيض، الملقب بمرتضى:</span>
عالم باللغة والحديث والرجال والأنساب، مشارك في عدة علوم. أصله من واسط في العراق، ومولده في "بلجرام" في الشمال الغربي من الهند، ومنشأه في زبيد باليمن. رحل إلى الحجاز، وأقام بمصر، فاشتهر فضله، وكاتبه ملوك الحجاز والهند واليمن والشام والعراق والمغرب الأقصى والترك والسودان والجزائر. وكان يحسن التركية والفارسية وبعضا من لسان الكرج. وتوفي بالطاعون بمصر. من تصانيفه الكثيرة "منح الفيوضات الوفية فيما في سورة الرحمن من أسرار الصفة الإلهية" (١).

الأمير [١١٥٤ - ١٢٣٢ هـ / ١٧٤٢ - ١٨١٧ م]

<span data-type="title" id=toc-1574>محمد بن محمد بن أحمد بن عبد القادر بن محمد بن عبد العزيز السنباوي الأزهري، المعروف بالأمير:</span>
فقيه مالكي، عالم بالعربية
_________
(١) هدية العارفين ٢: ٣٤٧ وعجائب الآثار ٢: ١٩٦ والخطط التوفيقية ٣: ٩٤ وتاريخ آداب اللغة العربية ٣: ٣١٠ وفهرس الفهارس ١: ٣٩٨ وإيضاح المكنون ٢: ٥٧٧ والأعلام ٧: ٢٩٧ ومعجم المؤلفين ١١: ٢٨٢ وانظر حاشيته.','2','631',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1142,'والتفسير. أصله من المغرب، ولد في ناحية سنبو من أعمال منفلوط بمصر، وتعلم في الأزهر، وتوفي بالقاهرة. قال الزركلي: "اشتهر بالأمير لأن جده أحمد كانت له إمرة الصعيد. من تصانيفه "تفسير المعوذتين" مخطوط. و "تفسير سورة القدر" مخطوط (١).

البيومي أبو عياشة [١٢٦٣ - ١٣٣٥ هـ / ١٨٤٧ - ١٩١٧ م]

<span data-type="title" id=toc-1575>محمد (البيومي) بن محمد بن علي بن حسن (أبي عياشة) بن بسيوني بن عطية النجار بن يوسف الحسني الدمنهوري المصري:</span>
فقيه شافعي، له اشتغال بالمذاهب الأربعة والفرائض وبعض العلوم. من أهل دمنهور مولدا ووفاة. من كتبه "تحفة الإخوان في تفسير بعض كلمات من القرآن مخطوط" (٢).

التازي [... - ١٣٥٤ هـ / ... - ١٩٣٥ م]

<span data-type="title" id=toc-1576>محمد (فتحا) بن محمد بن أحمد (الخصاصي التازي)</span>
_________
(١) حلية البشر ٣١٠، وهدية العارفين ٢: ٣٥٨، وعجائب الآثار ٤: ٢٨٤ وفهرس الفهارس ١: ٩٢ ومعجم المطبوعات ٤٧٣ والمكتبة البلدبة، فهرس التفسير ٨ وإيضاح المكنون ١: ٣٥٧، والأعلام ٧: ٢٩٨، ومعجم المؤلفين ١٨٣: ١١.
(٢) الأعلام ٧: ٣٠٣.','2','631',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1143,'الخصاصي التازي: مفسر، قاض، من أهل تازة بالمغرب، وولي قضاءها. أخذ عنه سلطان مراكش المولى عبد العزيز بن الحسن العلوي. توفي بمراكش. من آثاره "تفسير القرآن" في عدة مجلدات (١)

المروروذي [... - ٥٩٩ هـ / ... - ١٢٠٣ م]

<span data-type="title" id=toc-1577>محمد بن محمود المروروذي النيسابوري، ظهير الدين، أبو جعفر:</span>
فقيه شافعي، مفسر، من آثاره "البصائر" في تفسير القرآن. قال صاحب كشف الظنون: "فرغ منه سنة ٥٧٧ هـ " (٢).

الأصفهاني [٦١٦ - ٦٨٨ هـ / ١٢١٩ - ١٢٨٩ م]

<span data-type="title" id=toc-1578>محمد بن محمود بن محمد بن عياد السلماني، أبو عبد الله، شمس الدين الأصفهاني:</span>
قاض، مفسر، من فقهاء الشافعية، انتهت إليه الرياسة في معرفة الأصول والفقه. من أهل أصبهان، ولد وتعلم بها، وكان والده نائب السلطنة. ولما استولى العدو على أصبهان رحل إلى بغداد، ثم إلى الروم. ودخل الشام بعد سنة ٦٥٠ هـ
_________
(١) الأعلام (ط ٥) ٧: ٨٢.
(٢) هدية العارفين ٢: ١٠٥ وكشف الظنون ٢٤٦ و ٤٦٠.','2','632',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1144,'فولي قضاء منيج في أيام الناصر، ثم دخل مصر وولي قضاء قوص، فقضاء الكرك. وعاد إلى مصر فدرس بالصاحبية ومشهد الحسين، وتوفي بها. من آثاره "الجامع بين التفسير الكبير والكشاف" (١).

النيسابوري [... - ٧٩١ هـ / ... - ١٣٨٩ م]

<span data-type="title" id=toc-1579>محمد بن محمود بن عبد الله النيسابوري، شمس الدين:</span>
مفسر، نحوي، أصولي، من فقهاء الحنفية. قال في طبقات المفسرين: "قدم القاهرة، وناب في القضاء، وولي إفتاء دار العدل، ومشيخة سعيد السعداء، ودرس التفسير والنحو والأصول، وكان صدرا من صدور الحنفية، ومفخرا من مفاخر مصر" (٢).

الفارسي [القرن ٨ الهجري / القرن ١٤ الميلادي]

<span data-type="title" id=toc-1580>محمد بن محمود الفارسي، علاء الدين:</span>
_________
(١) شذرات الذهب ٥: ٤٠٦ والوافي ٥: ١٢ والفوائد البهية ١٩٨ وطبقات السبكي ٥: ٤١ والنجوم الزاهرة ٧: ٣٨٢ والعبر للذهبي ٥: ٣٥٩ والزركشي ٣٠٨ وبغية الوعاة ١: ٢٤٠ وهدية العارفين ٢: ١٣٦ وحسن المحاضرة ١: ٣١٣ والبداية والنهاية ١٣: ٣١٥ وطبقات الأسنوي ١: ١٥٥ ومرآة الجنان ٤: ٢٠٨ وفوات الوفيات ٤: ٣٨.
(٢) طبقات المفسرين ٢: ٢٥٢ وإنباء الغمر ١: ٣٨٩.','2','632',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1145,'مفسر، أديب، ناظم. من كتبه تفسير للقرآن جامع لأقاويل المفسرين سماه "المختار من كتب الأخيار" (١).

الشرواني [... - ٩١٢ هـ / ... - ١٥٠٦ م]

<span data-type="title" id=toc-1581>محمد بن محمود بن حاجي الشرواني، ثم القسطنطيني:</span>
طبيب، له معرفة بالتفسير والحديث وعلوم العربية. من أهل شيروان في بخارى. انتقل إلى القسطنطينية وخدم بطبه السلطان محمد خان المتوفى سنة ٨٨٦ هـ. وحج، وأقام بمصر مدة، قرأ فيها على بعض علمائها، وعاد إلى الروم (٢).

المغلوي [... - ٩٤٠ هـ / ... - ١٥٣٣ م]

<span data-type="title" id=toc-1582>محمد بن محمود المغلوي الوفائي الرومي:</span>
فقيه حنفي تركي، تفقه وتأدب بالعربية. درس في بروسه وكوتاهية. من آثاره "تنوير الضحى في تفسير سورة
_________
(١) شد الإزار ١٤٦ ومعجم المؤلفين ١٢: ٤
(٢) الشقائق النعمانية ١٣٥ وهو فيه شكر الله الشيرواني، وقال أنه مات في أيام دولة السلطان محمد خان، وهو خطأ. وهدية العارفين ٢: ٢٢٥ وهو فيه المعروف بشكر الله الشرواني. وكشف الظنون ٩٢٨ وبروكلمان، الذيل ٢: ٣٢٧ والأعلام ٧: ٣٠٩ ومعجم المؤلفين ١١: ٣١٦.','2','633',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1146,'والضحى، و "تفسير آية الكرسي" واسمه "مفتاح كنوز الأسماء". (١)

الأماسي [... - ٩٧٣ هـ / ... - ١٥٦٦ م]

<span data-type="title" id=toc-1583>محمد بن محمود باشا بن محمد الأماسي:</span>
فقيه حنفي، مفسر، من أهل أماسية، وولي الإفتاء بها. قال صاحب الشقائق: "كان عالما فاضلا. مفسرا محدثا واعظا، مؤثرا في القلوب، مقبول السيرة، مجاب الدعوة .. " من آثاره "حاشية على أنوار التنزيل في التفسر" (٢).

المنشي [... - ١٠٠١ هـ / ... - ١٥٩٢ م]

<span data-type="title" id=toc-1584>محمد بن محمود الصاروخي الآقحصاري الرومي، محيي الدين، الشهير بالمنشي:</span>
أديب، مفسر، فقيه حنفي، من أهل آق حصار من أعمال صاروخان بمغنيسيا. تولى مشيخة الحرم النبوي، ومات بمكة، وقيل بالمدينة. من آثاره "تنزيل التنزيل"
_________
(١) الشقائق النعمانية ٢٨٧ وهدية العارفين ٢: ٢٣٤ وكشف الظنون ٥٠١ والكواكب السائرة ٢: ٥٨ وفيه وفاته ٩٦٣ هـ؟ وتعليق آغا بزرك على كشف الظنون ٣٢.
(٢) هدية العارفين ١: ٢٤٩','2','633',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1147,'مخطوط، في التفسير. قال صاحب كشف الظنون: "وهو مختصر وجيز كتفسير الجلالين وأزيد منه، بدأ فيه في مستهل رمضان سنة ٩٨١ هـ بآق حصار معنونا باسم السلطان مراد ابن سليم خان، فتشرف من ميامنه بمشيخة الحرم النبوي في آخر الربيعين سنة ٩٨٢ هـ. أوله: الحمد لله الذي أنزل على عبده الكتاب .. وذكر فيه أنه اقتصر على قراءة حفص راوي عاصم" (١).

المناشيري [٩٨١ - ١٠٣٩ هـ / ١٥٧٣ - ١٦٣٠ م]

<span data-type="title" id=toc-1585>محمد بن محمود بن محمود بن أحمد بن خضر المناشيري الصالحي الدمشقي:</span>
فلكي موقت، له اشتغال بالتفسير، من أهل دمشق. له "الفلك المشحون في تفسير بعض معاني كتاب الله المكنون" (٢).

دباغ زاده [... - ١١١٤ هـ / ... - ١٧٠٢ م]

<span data-type="title" id=toc-1586>محمد بن محمود بن أحمد، الشهير بدباغ (زاده، الرومي)</span>
_________
(١) هدية العارفين ٢: ٢٦٠ وعثمانلي مؤلفلري ٢: ٢٠ وخلاصة الأثر ٣: ٤٠٠ وكشف الظنون ٤٥٩ و ١٩٥٠ ومعجم المؤلفين ١١: ٣٢٠.
(٢) هدية العارفين ١: ٢٤٩.','2','634',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1148,'زاده، الرومي: فقيه حنفي، مفسر، تولى مشيخة الإسلام مرتين، ثم عزل. من كتبه بالعربية "حاشية على جزء النبأ" من أنوار التنزيل في التفسير، للبيضاوي، وله بالتركية "تبيان في تفسير القرآن" (١).

عبد الحميد [١٣١٨ - ١٣٩٣ هـ / ١٩٠٠ - ١٩٧٣ م]

<span data-type="title" id=toc-1587>محمد (الشيخ) محيي الدين بن عبد الحميد:</span>
نحوي، فقيه، باحث، من أعضاء مجمع اللغة العربية بالقاهرة، ورئيس لجنة الفتوى بالأزهر. ولد بقرية كفر الحمام بالشرقية، وتعلم بدمياط، وحصل على شهادة الأزهر العالمية النظامية بالقاهرة سنة ١٩٢٥ م. ودرس بمصر والسودان. ثم كان عميدا لكلية اللغة العربية. واختير عضوا لمجمع اللغة سنة ١٩٦٤. حقق وصحح عشرات الكتب وأشرف على طبعها. من آثاره الكثيرة "تفسير جزء عم" (٢).

الكنتي [... - بعد ١٢٧٠ هـ / ... - بعد ١٨٥٣ م]

<span data-type="title" id=toc-1588>محمد بن المختار بن أحمد، أبو (عبد الله، الكنتي)</span>
_________
(١) هدية العارفين ٢: ٣٠٧ وإيضاح المكنون ١: ١٤٠ و ٥٧٣ ووفاته فيه سنة ١٠٠٠ هـ والخديوية ٢: ٨٦ ومعجم المؤلفين ١١: ٣١٣ والأعلام ٧: ٨٩.
(٢) مجلة الأديب عدد مارس ١٩٧٣ والمجمعيون ١٩٦ والأزهر في ألف عام ٣: ١١٢ والأعلام ٧: ٩٢.','2','634',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1149,'عبد الله، الكنتي: مؤرخ، من فقهاء المالكية، له مشاركة في بعض العلوم. من أهل شنقيط. نسبته إلى زاوية الكنت. قال صاحب دليل مؤرخ المغرب: "توفي بعد سنة ١٢٧٠ هـ ". من آثاره "تفسير الفاتحة" مخطوط (١).

المرزوقي [... بعد ١٠٦٠ هـ / ... - ١٦٥٠ م]

<span data-type="title" id=toc-1589>محمد (زين العابدين) بن مدين بن أبي العباس المرزوقي:</span>
فاضل، له اشتغال بالتاريخ والتفسير، مالكي حسيني، من آثار. "تفسير سورة ألم نشرح" مخطوط، في التيمورية (٢).

الكاشاني [... - ١٠٩١ هـ / ... - ١٦٨٠ م]

<span data-type="title" id=toc-1590>محمد بن مرتضى بن فيض الله محمود الكاشاني، ويقال القاشاني والكاشي:</span>
فقيه إمامي، مفسر، أصولي، ناظم. ورد اسمه محسن بن مرتضى، ومحسن محمد،
_________
(١) الأعلام المراكشية ٢: ٣٥ وتراجم أدباء شنقيط ٣٥٦ ودليل مؤرخ المغرب ١: ٢١٦ والبلدية: التصوف ١٣.
(٢) التيمورية ١: ٧٨ و ٣: ٢٧٨ والمخطوطات المصورة، قسم التاريخ ٢/ ٤ رقم ١، ٢١٤١ ص ٤٥٦.','2','635',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1150,'ومحسن بن محمد، ومحمد محسن، ويقال له: ملا محسن فيض الله الكاشي: من أهل كاشان أوقاشان - في وسط إيران - من تصانيفه الكثيرة "الصافي في تفسير كلام الله الوافي" طبع، و "الأصفى" طبع، بهامش الأول، مختصره، و "تنوير المواهب" في التفسير أيضا (١).

السدي [... - ١٨٦ هـ / ... - ٨٠٢ م]

<span data-type="title" id=toc-1591>محمد بن مروان بن عبد الله بن إسماعيل بن عبد الرحمن، مولى عبد الرحمن بن زيد بن الخطاب، السدي:</span>
محدث، متهم بالكذب، مفسر، من أهل الكوفة. قدم بغداد وحدث بها. قال يحى بن معين: "السدي صاحب التفسير محمد بن مروان ليس بثقة" وقال أبو حاتم: "ذاهب الحديث، متروك الحديث، لا يكتب حديثه البتة" له "تفسير" رواه عن محمد بن سائب الكلبي (انظر ترجمته في هذا المعجم) (٢).
_________
(١) فوائد الرضوية ٦٣٣ وسلافة العصر ٤٩٩ وهدية العارفين ٢: ٦ وروضات الجنات ٥٤٢ وإيضاح المكنون ١: ٩٠ و ٣٣٥ و ٢: ٦٣ وفهرس المخطوطات المصورة ١: ١٧٣ وبروكلمان ٢: ٤١٣ والذيل ٢ - : ٥٨٤.
(٢) تاريخ بغداد ٣: ٢٩١ والتاريخ الكبير للبخاري ١: ٢٣٢ وغاية النهاية ٢: ٢٦١ وميزان الاعتدال ٤: ٣٢ وطبقات المفسرين للداودي ٢: ٢٥٤ وخلاصة تهذيب الكمال ٣٠٦ والشذرات ١: ٣٢٥ وكشف الظنون ٤٢٩.','2','635',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1151,'قطرب [... - ٢٠٦ هـ / ... - ٨٢١ م]

<span data-type="title" id=toc-1592>محمد بن المستنير بن أحمد، أبو علي، الشهير بقطرب:</span>
من العلماء بالنحو واللغة والأدب، من أهل البصرة، من الموالي. أخذ النحو عن سيبويه، وهو الذي دعاه بقطرب فلزمه. وكان يرى رأي المعتزلة النظامية، فأخذ عن النظام مذهبه واتصل بأبي دلف العجلي وأدب أولاده. من تصانيفه "معاني القرآن" قيل: لم يسبق إلى مثله. و "إعراب القرآن" و "الرد على الملحدين في متشابه القرآن" (١).

العياشي [... - نحو ٣٢٠ هـ / ... - نحو ٩٣٢ م]

<span data-type="title" id=toc-1593>محمد بن مسعود بن محمد بن عياش السلمي، أبو النضر:</span>
فقيه إمامي، من كبارهم، مشارك في عدة علوم، من أهل سمرقند. اشتهرت كتبه في نواحي خراسان اشتهارا عظيما، وهي تزيد على
_________
(١) بغية الوعاة ١: ٢٤٢ وإنباه الرواة ٣: ٢١٩ والعبر ١: ٣٥ ومفتاح السعادة ١: ١٦٠ وهدية العارفين ٢: ٩ وروضات الجنات ١٥٦ ولسان الميزان ٥: ٣٧٨ ونزهة الألباء ٩١ والبداية والنهاية ١٠: ٢٥٩ والشذرات ٢: ١٥ ومرآة الجنان ٢: ٣١ وتاريخ بغداد ٣: ٢٩٨ ومعجم الأدباء ١٩: ٥٢ والأعلام ٧: ٣١٥ وطبقات المفسرين للداودي ٢: ٢٥٤ ووفيات الأعيان ٣: ٤٩٠ والوافي ٥: ١٩.','2','636',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1152,'مئتي كتاب، منها "تفسير القرآن" ويعرف بتفسير العياشي، موجود نصفه الأول. و "فضائل القرآن" (١).

السيرافي [... - حيا ٧١٢ هـ / ... - حيا ١٣١٢ م]

<span data-type="title" id=toc-1594>محمد بن مسعود بن محمود الفالي السيرافي، قطب الدين:</span>
نحوي، مفسر، من أهل فال - بين شيراز وهرمز -. من آثاره "تقريب التفسير" مخطوط، وهو تلخيص الكشاف للزمخشري. قال صاحب كشف الظنون: "أتمه في التاسع من شوال سنة ٦٩٨ ببلدة شيراز، أولها الحمد لله الذي جعل كتابه الكريم مفتاحا للسرور ... الخ". أزال اعتزاله وبعض إطنابه، فهذب ونقح وضم إلى مواضع الانغلاق حلا وبيانا، وهو كتاب صغير الحجم، وجيز النظم، مشتمل على محض الأهم من الكشاف مع زيادات شريفة". وكان والده (انظر الفالي - مسعود بن محمود) من العلماء بالتفسير أيضا (٢).
_________
(١) روضات الجنات ٥٥٦ والذريعة ٤: ٢٩٥ والفهرست لابن النديم ١٩٤ وهدية العارفين ٢: ٣٢ ومنهج المقال ٣١٩ وفوائد الرضوية ٦٤٢ وفهرست الطوسي ١٣٦ وتنقيح المقال ٣: ١٨٣ والأعلام ٧: ٣١٦ وتاريخ التراث العربي ٢١٠ ومعجم المؤلفين ١٢: ٢٠.
(٢) كشف الظنون ٤٨١١ وهدية العارفين ٢: ١٤٢ والأزهرية ٤: ٢٥٤.','2','636',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1153,'الزهري [٨٥ - ١٢٤ هـ / ٦٧٨ - ٧٤٢ م]

<span data-type="title" id=toc-1595>محمد بن مسلم بن عبيد الله بن شهاب الزهري، أبو بكر:</span>
حافظ زمانه، وأحد أكابر الفقهاء والمحدثين بالمدينة، وهو من أهلها. يقال إنه أول من دون الحديث. قال أبو داود: "حديثه ألفان ومائتان، النصف منها مسند". وقال أبو الزناد: "كنا نطوف مع الزهري على العلماء ومعه الألواح والصحف ويكتب كل ما يسمع". نزل الشام واستقر بها. من آثاره "تنزيل القرآن" رسالة، طبعت (١).

حاجي حسن زاده [... - ٩١١ هـ / ... ١٥٠٥ م]

<span data-type="title" id=toc-1596>محمد بن مصطفى ابن الحاج حسن الباليكسري الرومي، شمس الدين، ويعرف بحاجي حسن زاده:</span>
فقيه حنفي قاض، مشارك في التفسير واللغة
_________
(١) ميزان الاعتدال ٤: ٤٠ وتذكرة الحفاظ ١٠٨ وحلية الأولياء ٣: ٣٦٠ وغاية النهاية ٢: ٢٦٢ والوافي ٥: ٢٤ وتاريخ الإسلام ٥: ١٣٦ وتهذيب التهذيب ٩: ٤٤٥ وطبقات ابن سعد ٤: ١٢٦ والمعارف ٤٧٢ وصفة الصفوة ٢: ٧٧ والشذرات ١: ١٦٢ ومعجم الشعراء للمرزباني ٣٤٥ ووفيات الأعيان ١٧٧٠٤ وبروكلمان، الذيل ١: ١٠٢.','2','637',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1154,'والتصريف، من مستعربي الترك. درس في عدة مدارس في بروسة والقسطنطينية، وولي قضاء العسكر بالأناضول، ثم قضاء الجيش بالروم إيلي. وبني بالقسطنطينية مدرسة ومسجدا ودارا للتعليم، وبها دفن وقد جاوز التسعين. من آثاره "حاشية" على سورة الأنعام من تفسير البيضاوي (١).

الإسكليبي [... - ٩٢٢ هـ / ... - ١٥١٦ م]

<span data-type="title" id=toc-1597>محمد (محي الدين) بن مصطفى الشهير بياوصي الإسكليبي:</span>
صوفي، مشارك في بعض العلوم، وهو والد أبي السعود العمادي، المفسر المشهور. من آثاره "تعليقة" على "أنوار التنزيل" فى التفسير، للبيضاوي (٢).

شيخ زاده [... - ٩٥١ هـ / ... - ١٥٤٤ م]

<span data-type="title" id=toc-1598>محمد (محي الدين) بن مصطفى (مصلح الدين) بن شمس الدين (القوجوي، الشهير بشيخ زاده)</span>
_________
(١) شذرات الذهب ٨: ٥٦ والشقائق النعماية ١: ٢٤٨ وهدية العارفين ٢: ٢٢٥ والكوا كب السائرة ١: ٧١ والفوائد البهية ٢٠١ وكشف الظنون ١٩١ والأعلام ٧: ٣١٩.
(٢) هدية العارفين ٢: ٢٢٧ وكشف الظنون ١٩٢.','2','637',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1155,'القوجوي، الشهير بشيخ زاده: فقيه حنفي، مفسر، فرضي، كان مدرسا بالقسطنطينية، من كتبه "حاشية على أنوار التنزيل" في التفسير للبيضاوي، طبعت ببولاق في ستة مجلدات سنة ١٢٦٣ هـ، ثم في خمسة مجلدات في نفس السنة، ثم في الآستانة سنة ١٢٨٣ و ١٣٠٦ في أربعة مجلدات. قال حاجي خليفة: "وهي أعظم الحواشي فائدة وأكثرها نفعا وأسهلها عبارة، كتبها أولا على سبيل الإيضاح والبيان للمبتدىء في ثماني مجلدات، ثم أستأنفها ثانيا بنوع تصرف فيه وزيادة عليه، فتلاعبت بالنسختين أيدي النساخ حتى كاد أن لا يفرق بينهما .. " (١).

العيشي [... - ١٠١٦ هـ / ... - ١٦٠٧ م]

<span data-type="title" id=toc-1599>محمد بن مصطفى التيره وي الرومي، المعروف بالعيشي:</span>
فقيه حنفي، مفسر، درس بمدرسة ابن ملك ببلده تيره، في بلاد
_________
(١) الشقائق النعمانية ٦٦١ وهو فيه: محمد بن مصلح الدين، ووفاته سنة ٩٥٠ وشذرات الذهب ٨: ٢٨٦ ووفاته فيه سنة ٩٥٠ واسمه محمد بن مصطفى، ومثله في الكواكب السائرة ٢: ٥٩ وهدية العارفين ٢: ٢٣٨ وفيه: توفي سنة ٩٥١ ومثله في كشف الظنون ١٨٨ والأعلام ٧: ٣٢٠ ومعجم المؤلفين ١٢: ٣٢ ومعجم المطبوعات ١١٦٦ وتعليقات آغا بزرك على كشف الظنون ٢١.','2','638',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1156,'الترك. من آثاره "تفسير القرآن" (١).

الكسري [... - ... هـ / ... - ... م]

<span data-type="title" id=toc-1600>محمد بن مصطفى الكسري:</span>
مفسر، له "تفسير الفاتحة" قال صاحب كشف الظنون: "مختصر، أوله: الحمد لله الذي نور قلوب العارفين" (٢).

آقكرماني [... - ١١٧٤ هـ / ... - ١٧٦١ م]

<span data-type="title" id=toc-1601>محمد بن مصطفى حميد الكفوي، المعروف بآقكرماني:</span>
فقيه حنفي، قاض، من العلماء. تولى القضاء بمكة وتوفي بها. من كتبه "شرح الحمدلة" و "شرح البسملة" و "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (٣).

الخضري [١٢١٣ - ١٢٨٧ هـ / ١١٩٨ - ١٨٧٠ م]

<span data-type="title" id=toc-1602>محمد بن مصطفى بن حسن الدمياطي، المعروف بالخضري:</span>
نحوي، فقيه، مفسر، مشارك في
_________
(١) كشف الظنون ٥٤، وهدية العارفين ٢: ٢٦٧
(٢) كشف الظنون ٤٥٥
(٣) هدية العارفين ٢: ٣٣٢.','2','638',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1157,'بعض العلوم، مولده ووفاته بدمياط بمصر. دخل الأزهر فمرض وصمت أذناه، فعاد إلى بلده واشتغل بالعلوم الشرعية والفلسفية، واستخرج طريقة لمخاطبته بأحرف إشارية بالأصابع، فتعلمها منه أصحابه فكانوا يخاطبونه بها. من تصانيفه "رسالة في مبادىء علم التفسير" و "منظومة في مشابهات القرآن" طبعتا بمصر (١).

المراغي [١٢٩٨ - ١٣٦٤ هـ / ١٨٨١ - ١٩٤٥ م]

<span data-type="title" id=toc-1603>محمد بن مصطفى بن محمد بن عبد المنعم المراغي:</span>
من رجال الإصلاح الديني والإجتماعي في مصر، مفسر، كاتب بليغ، خطيب، ولد في بلدة مراغة من أعمال جرجا بالصعيد، وحفظ القرآن، ثم التحق بالأزهر وتتلمذ للأستاذ الإمام الشيخ محمد عبده. نال شهادة العالمية سنة ١٩٠٤ هـ، وعين مدرسا بالأزهر، ثم اختير ضمن البعثة التي سافرت إلى السودان لوضع أسس المحاكم الشرعية فيه، وعين بعد ذلك قاضيا في دنقلا، ثم نقل إلى مدينة الخرطوم. وفي سنة ١٩٠٧ عاد
_________
(١) هدية العارفين ٢: ٣٧٩ وهو فيه: محمد بن عبد الله، ووفاته سنة ١٢٨٨ ومعجم المطبوعات ٨٨٦ ولأعلام ٧: ٣٢٢ ومعجم المؤلفين ١٢: ٢٧.','2','639',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1158,'إلى مصر وعين مفتشا للدروس الدينية ومدرسا بالأزهر. وفي سنة ١٩٠٨ عين قاضيا لقضاة السودان، واستمر حتى اندلاع ثورة ١٩١٩ فأيدها وقاد حركة مناصرتها في السودان، فأعيد إلى مصر، وعين في السنة نفسها رئيسا للتفتيش بالمحاكم الشرعية، ثم رئيسا لمحكمة مصر الكلية، فعضوا في المحكمة الشرعية العليا، فرئيسا لها (سنة ١٩٢٣). وعين شيخا للأزهر سنة ١٩٢٨، فوضع في إصلاحه قانونا جديدا، وحالت عقبات دون إنجازه، فاستقال من منصبه، ثم أعيد سنة ١٩٣٥ فاستمر إلى أن توفي بالإسكندرية، ودفن بقرب ضريح السيدة نفيسة بالقاهرة. من آثاره "تفسير سورة الحجرات" و "تفسير سورة الحديد" و "آيات من سورة الفرقان" و "تفسير سورتي لقمان والعصر" طبعت كلها. (١).
_________
(١) الأعلام الشرقية ٢: ١٧٦ ومجلة الأزهر ١٦: ٣٣٤ ومجلة الكتاب ١: ٤٨ ومجلة نور الإسلام ٦: ٢٧٠ والمجددون في الإسلام ٥٤٥ والسياسة والأزهر ٥٥ والأعلام ٧: ٣٢٤ والموسوعة العربية الميسرة ١٦٧٨ ومجلة المجمع العلمي العربي ٢١: ٢٨٩ ومعجم المؤلفين ١٢: ٣٤ واتجاهات التفسير في العصر الحديث ٢١١ والتفسير والمفسرون ٢: ٢٦١ والإمام المراغي لأنور الجندي. والإسلام والتجديد في مصر ٢٠٠ ومصادر الدراسة ٣: ١١٧٨.','2','639',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1159,'المهدي الزيدي [... - ٧٢٨ هـ / ... - ١٣٢٨ م]

<span data-type="title" id=toc-1604>محمد بن المطهر بن يحي بن المرتضى بن المطهر، من سلالة الهادي إلى الحق:</span>
إمام زيدي، كان فقيها، عارفا بالتفسير، واسع العلم، ولد بهجرة الكريش من بلاد الأهنوم، وبويع بالخلافة بعد موت أبيه (سنة ٦٩٠ هـ). كانت بينه وبين سلاطين اليمن (بني رسول) وقائع كثيرة. توفي في ذي مرمر ودفن بصنعاء. من آثاره "عقود العقيان في الناسخ والمنسوخ من القرآن" قال الزركلي: "رأيت مخطوطة المجدد الأول منه فى مكتبة الأمبروزيانة" (١).

البطاوري [... - ١٣٥٥ هـ / ... - ١٩٣٦ م]

<span data-type="title" id=toc-1605>محمد المكي بن محمد بن علي بن عبد الرحمن الشرشالي، أبو حامد البطاوري:</span>
قاض، أديب، له اشتغال بالحديث والتفسير، من أهل الرباط مولدا ووفاة، وكان شيخ
_________
(١) البدر الطالع ٢: ٢٧١ وهدية العارفين ٢: ١٤٧ وإيضاح المكنون ٢: ١١٤ والأعلام ٧: ٣٢٦ ومعجم المؤلفين ١٢: ٣٧.','2','640',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1160,'الجماعة بها، وولي قضاءها أحد عشر عاما - ١٣٢٣ - ١٣٣٣ هـ. وكان قبل ذلك، تقلب في وظائف كتابية سلطانية بطنجة وإسبانيا وفرنسا وإنكلترة. له تصانيف كثيرة (١).

المرادي [... - ٢٩٠ هـ / ... - ٩٠٣ م]

<span data-type="title" id=toc-1606>محمد بن منصور بن يزيد المرادي، أبو جعفر:</span>
مفسر، محدث، مؤرخ، من فقهاء الزيدية. توفي سنة نيف وتسعين ومائتين. له "التفسير الكبير" و "التفسير الصغير" (٢).

البيهقي [... - حيا سنة ٣٦٩ هـ / ... - حيا ٩٨٠ م]

<span data-type="title" id=toc-1607>محمد بن منصور بن محمد بن أحمد بن حميد البيهقي، أبو عبد الله:</span>
محدث، عارف بالتفسير، من فقهاء الشافعية. من أهل نيسابور. قال عبد الغافر الفارسي: "هو رجل فاضل كبير صنف فوائد منها كتاب "زهرة العلوم في معاني القرآن" (٣).
_________
(١) معجم الشيوخ ٢: ٥٦ والأعلام ٧: ١١٠
(٢) ابن النديم ٢٤٤ وتراجم الرجال ٣٦ وتاريخ التراث العربي ٢: ٢٤٤ ومعجم المؤلفين ١٢: ٥٣
(٣) الوافي ٥: ٧١.','2','640',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1161,'البرجي [... - ٤٨٨ هـ / ... - ١٠٩٥ م]

<span data-type="title" id=toc-1608>محمد بن منصور بن الحسن البرجي، أبو سهل، المعروف بالعروضي:</span>
محدث، فقيه، مفسر، من أهل البرج (من قرى أصبهان). أخذ عن أبي نعيم ولازمه. له "غريب القرآن" (١).

السمعاني [٤٦٦ - ٥١٠ هـ / ١٠٧٤ - ١١١٦ م]

<span data-type="title" id=toc-1609>محمد بن منصور بن محمد ابن عبد الجبار التميمي السمعاني المروزي، أبو بكر:</span>
محدث، حافظ، واعظ، خطيب، أديب، من فقهاء الشافعية، مولده ووفاته بمرو. سمع بنيسابور وبغداد وهمذان وأصبهان ومكة وغيرها. وهو والد الحافظ، تاج الإسلام عبد الكريم صاحب كتاب الأنساب. أملى مائة وأربعين مجلسا في الوعظ، وكان قد وصل في التفسير الذي يذكره في مجلس الوعظ إلى قوله تعالى {اليوم أكملت لكم دينكم}
_________
(١) الإ كمال ١: ٤٢١ واللباب ٢: ٣٣٦ وطبقات المفسرين للداودي ٢: ٢٥٧.','2','641',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1162,'الآية. (المائدة: ٣) وتوفي عقب ذلك (١).

القصري [... - ٥٤٧ هـ / ... - ١١٥٢ م]

<span data-type="title" id=toc-1610>محمد بن منصور بن إبراهيم القصري، أبو بكر:</span>
مقرىء، مفسر، من أهل بغداد. قال الصفدي: "كان حافظا للتفسير، عالما بالقراءات، وله حلقة بجامع المنصور يورد فيها التفسير كل جمعة" وقال الذهبي: "كان رأسا في التفسير والقراءات، له حلقة بجامع المنصور، مات وله سبعون سنة" (٢).

ابن سوده [١٢٢٠ - ١٢٩٤ هـ / ١٨٠٥ - ١٨٧٧ م]

<span data-type="title" id=toc-1611>محمد المهدي بن الطالب بن سودة، أبو عيسى:</span>
فقيه مالكي، مفسر، من كبار الحفاظ، من آثاره "هداية المنان الكبرى على السبع المثاني الرائقة
_________
(١) طبقات السبكي ٧: ٥ وطبقات المفسرين للداودي ٢: ٢٥٧ وطبقات ابن هداية ٧٢ والمنتظم ٩: ١٨٨ والأنساب ٣٠٨ أو العبر ٤: ٢٢ والشذرات ٤: ٢٩ والبداية والنهاية ١٢: ١٨٠ واللباب ١: ٥٦٣ والأعلام ٧: ٣٣٢ وإنباء الرواة ٣: ٢١٦ والوافي ٥: ٧٥ ومرآة الجنان ٣: ٢٠٠.
(٢) الوافي ٥: ٦٨ وغاية النهاية ٢: ٢٦٦ وطبقات القراء للذهبي، وطبقات المفسرين للداودي ٢: ٢٥٦.','2','641',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1163,'الألفاظ المهذبة المعاني" وهو تفسير سورة الفاتحة على طريقة الصوفية. طبع (١).

القزويني [١٢٦٢ - ١٣٣٥ هـ / ١٨٤٦ - ١٩١٧ م]

<span data-type="title" id=toc-1612>محمد بن المهدي بن الحسن بن أحمد بن محمد بن الحسن القزويني، الحلي:</span>
فقيه إمامي، مشارك في عدة علوم. له كتب منها "الأمالي" في التفسير والأدب والفقه والتاريخ (٢).

ابن الفرغاني [... - ٣٢٠ هـ / ... - ٩٣٢ م]

<span data-type="title" id=toc-1613>محمد بن موسى الواسطي، أبو بكر، الملقب بابن الفرغاني:</span>
متصوف، من كبار أصحاب الجنيد، ولد في فرغانة، وقضى شبابه في بغداد، ثم انتقل منها إلى خراسان، وأقام بمرو فمات بها. قال السلمي: "هو من علماء مشايخ القوم، لم يتكلم أحد في أصول
_________
(١) معجم المطبوعات ١٠٦٢ و ١٦٩٨ وفهرست الخديوية ١: ٢٢٢ و ٢: ٢٣٤ ومعجم المؤلفين ١٢: ٥٥ وشجرة النور ٤٠٣ ومجلة تطوان ٦: ٧٠ ودليل مؤرخ المغرب رقم ١٣٧٩ والأعلام ٧: ١١٤.
(٢) معجم المؤلفين ١٢: ٥٦.','2','642',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1164,'التصوف مثل ما تكلم هو، وكان عالما بالأصول، وعلوم الظاهر". وقال ابن يونس: "كان عالما بالفقه والتفسير، ويتفقه على أهل الظاهر، وقد رمي بالقدر". وفي طبقات المفسرين للسيوطي أنه كان قاضيا بالرملة". أفاد السلمي في "التفسير" من رسائل له في الأخلاق (١).

الدميري [٧٤٢ - ٨٠٨ هـ / ١٣٤١ - ١٤٠٥ م]

<span data-type="title" id=toc-1614>محمد بن موسى بن عيسى بن علي الدميري، أبو البقاء، كمال الدين:</span>
باحث، أديب، من فقهاء الشافعية، نسبته إلى دميرة (بمصر)، مولده ووفاته بالقاهرة. قال السخاوي: "برع في التفسير والحديث والفقه وأصوله والعربية والأدب وغيرها. وأفتى ودرس، وانتفع به جماعة، وأقام مدة بمكة والمدينة. كان اسمه أولا كمال بغير إضافة، وكان يكتبه بخطه في كتبه، ثم تسمى محمدا وصار يكشط الأول" (٢).
_________
(١) طبقات المفسرين للسيوطي ٤٠ وطبقات المفسرين للداودي ٢: ٢٦٢ وطبقات الصوفية ٣٠٢ وحلية الأولياء ١٥: ٣٤٩ والمنتظم ٦: ٢٦٢ والرسالة القشيرية ٣٢ والوافي ٥: ٨٥ وتاريخ التراث العربي ٢: ٤٧٦ ونتائج الأفكار القدسية ١: ١٧٨.
(٢) الضوء اللامع ١٠: ٥٩ وإنباء الغمر ٢: ٣٤٨ والفوائد البهية ٢٠٣ والشذرات ٧: ٧٩ والبدر الطالع ٢: =','2','642',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1165,'العسيلي [... - ١٠٣١ هـ / ... - ١٦٢٢ م]

<span data-type="title" id=toc-1615>محمد بن موسى بن علاء الدين العسيلي:</span>
نحوي، مفسر، من أهل القدس، أخذ عن علمائها، وتوفي بها ودفن في مقبرة مأمن الله. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي، لم يكملها (١).

غلامك [... - ١٠٤٥ هـ / ... - ١٦٣٥ م]

<span data-type="title" id=toc-1616>محمد بن موسى البوسنوي السرائي، الشهير بغلامك:</span>
قاض، مفسر، من علماء الترك المستعربين. ولد في بلدة سراي بالبوسنة، وأكمل تعليمه بالقسطنطينية، وولي قضاء حلب، وسافر إلى أسكدار، ثم إلى حصار، ولزم الخلوة، إلى أن مات. من آثاره "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي. قال في كشف الظنون: "وهي إلى آخر سورة الأنعام كتبها على طريق الإيجاز بل على سبيل التعمية والألغاز، أولها الحمد لله الذي فضل بفضله العالمين على الجاهلين" (٢).
_________
= ٢٧٢ ومفتاح السعادة ١: ١٨٦ وهدية العارفين ٢: ١٧٨ وروضات الجنات ٢٠٨ وحسن المحاضرة ١: ٢٤٩ والأعلام ٧: ٣٤٠ ومعجم المؤلفين ١٢: ٦٥.
(١) خلاصة الأثر ٤: ٢٣٤ وهدية العارفين ٢: ٢٧٢.
(٢) خلاصة الأثر ٤: ٣٠٢ وفيه: والكاف في غلامك =','2','643',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1166,'القرا حصاري [... - نحو ٩٥٠ هـ / ... - نحو ١٥٤٣ م]

<span data-type="title" id=toc-1617>محمد بن نجيب القرا حصاري الرومي:</span>
فقيه حنفي، تركي مستعرب، له اشتغال بالتفسير وغيره. من آثاره "رونق التفاسير" مخطوط، في مغنيسا رقم ١٣٣١ (١).

الدامغاني [... - ٧٧٥ هـ / ... - ١٣٧٤ م]

<span data-type="title" id=toc-1618>محمد بن نصر الله بن محمد الدامغاني، افتخار الدين:</span>
مفسر، منطقي، مشارك في بعض العلوم. نسبته إلى دامغان بين الري ونيسابور. له أكثر من مائة كتاب منها "كاشف السجاف عن وجه الكشاف" سبع مجلدات، مخطوط، الجزء الأول منه (٢). للتصغير بالفارسية كما هي في مصنفك. وهدية العارفين ٢: ٢٧٨ وفيه: علامك (بالعين المهملة) وقال. تصغير العلامة. والجوهر الأسنى ١١٦ وكشف الظنون ١٩٣ وإعلام النبلاء ٦: ٢٤٦ ومعجم المؤلفين ١٢: ٦٢ والأعلام ٧: ٣٤١.
_________
(١) هديه العارفين ٢: ٢٣٨ وكشف الظنون ٩٣٣ وفيه: رونق التفاسير، ولم يذكر اسم المؤلف .. ودار الكتب ٥: ٢٠٩ والأعلام ٧: ١٢٣.
(٢) شد الإزار ٦٦ ومعجم المؤلفين ١٢: ٧٩ وطلس في الكشاف، الرقم ١٩٠.','2','643',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1167,'نصوحي [... - ١١٣٠ هـ / ... - ١٧١٨ م]

<span data-type="title" id=toc-1619>محمد بن نصوح الاسكداري الخلوتي، الشهير بنصوحي:</span>
مفسر، متكلم، تركي مستعرب. من كتبه "تفسير القرآن" في عشر مجلدات (١).

ابن الأخرم [٢٦٠ - ٣٤١ هـ / ٨٧٤ - ٩٥٢ م]

<span data-type="title" id=toc-1620>محمد بن النضر بن مر بن الحر الربعي، أبو الحسن، ويقال أبو عمرو، المعروف بابن الأخرم:</span>
شيخ الإقراء بالشام، مفسر، ولد بقينية خارج دمشق، وأخذ القراءة عن هارون الأخفش، وهو من جلة أصحابه وأضبطهم، وكان له حلقة عظيمة. قال ابن عساكر: "طال عمره، وارتحل الناس إليه، وكان عارفا بعلل القراءات، بصيرا بالتفسير والعربية .. " مات بدمشق (٢).

النور العربي [... - ١٣٠٥ هـ / ... - ١٨٨٨ م]

<span data-type="title" id=toc-1621>محمد نور الدين بن ... . الحسيني (النقشبندي، المعروف بنور العربي)</span>
_________
(١) هدية العارفين ٢: ٣١٤.
(٢) طبقات المفسرين للداودي ٢: ٢٦٣ وغاية النهاية ٢: ٢٧٠ والشذرات ٢: ٣٦١ وطبقات المفسرين للسيوطي ٤٠ وطبقات القراء للذهبي ١: ٢٣٤ والنجوم الزاهرة ٣: ٣٠٩ والعبر ٢: ٢٥٧ والوافي ٥: ١٣١.','2','644',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1168,'النقشبندي، المعروف بنور العربي: مفسر متصوف، شارك في بعض العلوم، له تصانيف كثيرة، منها "تفسير سورة الفاتحة" و "تفسير سورة الفتح" و "تفسير سورة يوسف" (١).

الطهطاوي [... . - ١٣٤٦ هـ / ... - ١٩٢٩ م]

<span data-type="title" id=toc-1622>محمد (نور الدين) ابن عبد الرحيم فراج الطهطاوي:</span>
فاضل، من أهل مصر، له، غاية المأمول، من بلوغ السول، في تفسير قوله تعالى: {لقد جاءكم رسول} طبع، اختصره من كتاب "بلوغ السول بتفسير {لقد جاءكم رسول} لأحمد رافع الطهطاوي (انظر ترجمته في هذا المعجم) (٢).

ابن الهادي [٦٥١ - ٧٢٠ هـ / ١٢٥٣ - ١٣٢٠ م]

<span data-type="title" id=toc-1623>محمد بن الهادي بن تاج الدين، عز الدين:</span>
مفسر، فقيه، من آثاره
_________
(١) هدية العارفين ٢: ٣٨٦ وإيضاح المكنون ٣٠٦ و ٣٠٧ ولم أعثر له على ترجمة وافية فيما بين يدي الساعة من كتب الرجال.
(٢) الأعلام ٧: ٣٥٠ وفهرست التيمورية ١: ٢١٧ و ٣: ٢٧٣ ومعجم المؤلفين ١٠: ١٦٠.','2','644',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1169,'"الروضة والغدير في تفسير آي الأحكام من تنزيل الحكيم القدير" (١).

الطهراني [... - ١٣٢١ هـ / ... - ١٩٠٣ م]

<span data-type="title" id=toc-1624>محمد هادي بن محمد أمين الطهراني، نزيل النجف:</span>
فقيه إمامي، نحوي، له اشتغال بالتفسير، ولد ونشأ بطهران، وانتقل إلى أصفهان، ثم إلى النجف، وبها توفي. من كتبه "تفسير آية النور" طبع (٢).

الدندري [... - ٦٧٤ هـ / ... - ١٢٤٩ م]

<span data-type="title" id=toc-1625>محمد بن هبة الله بن جعفر بن هبة الله بن محمد بن شيبان الربعي الدندري، أبو بكر، سراج الدين:</span>
فقيه شافعي، قاض، أصولي، مفسر، من أهل دندرا، وتولى الحكم بها وبأدفو من بلاد الصعيد بمصر. قال المقريزي: "برع في الفقه والأصلين
_________
(١) بروكلمان: الذيل ٢: ٢٤١ ومعجم المؤلفين ١٢: ٨٤.
(٢) أحسن الوديعة ١: ١٦٦ والأعلام ٧: ٣٥٠ و ١: ٨٣ و ٢: ٢٥ و ٤: ٢٠٤ و ٣٣٤.','2','645',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1170,'والتفسير، وتوفي ببلده" (١).

أبو الهذيل العلاف [١٣٥ - ٢٣٥ هـ / ٧٥٣ - ٨٥٠ م]

<span data-type="title" id=toc-1626>محمد بن الهذيل بن عبد الله بن مكحول العبدي، مولى عبد القيس، أبو الهذيل العلاف:</span>
من أئمة المعتزلة، يعد رائد التأليف في علم الكلام عندهم، "بالرغم من أن فلسفته كانت مختلفة إلى حد ما عن مجمل تعاليمهم". ولد بالبصرة، وانتقل إلى بغداد وانضم إلى واصل بن عطاء، وأحضره الخليفة المأمون مع النظام إلى قصر الخلافة في سنة ٢٠٤ هـ، وقال المأمون في حقه "أطل أبو الهذيل على علم الكلام كإطلال الغمام على الأنام". وكان حسن الجدل قوي الحجة، سريع الخاطر، كف بصره في آخر عمره، وتوفي بسامراء. وعاشت المدرسة التي أسسها مدة طويلة من بعده. له "متشابه القرآن" ذكره ابن النديم والخياط والأشعري والشريف المرتضى والشهرستاني والجاحظ (٢).
_________
(١) الطالع السعيد ٦٣٦ وطبقات المفسرين للداودي ٢:
(٢) لسان الميزان ٥: ٤١٣ ونكت الهميان٢٧٧ وتاريخ =','2','645',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1171,'الهمداني [... - ١٣٤٩ هـ / ١٩٣٠ م]

<span data-type="title" id=toc-1627>محمد الهمداني الفارسي، المعروف بالكمال:</span>
فقيه إمامي، مشارك في بعض العلوم، قال في مجلة "العرفان" الصيداوية: له ١١ مؤلفا، منها "تفسير القرآن" (١).

الطليطلي [... ٣٥٢ هـ / ... - ٩٦٣ م]

<span data-type="title" id=toc-1628>محمد بن وسيم بن عمر (وقيل بن سعدون) القيسي، أبو بكر الطليطلي:</span>
فقيه مالكي، محدث، نحوي، مفسر، من أهل طليطلة، سمع بها وبقرطبة. قال القاضي عياض: "وكان أعمى، ذا بصر بالفقه والحديث، وحظ من علم العربية واللغة والشعر والتفسير والفرائض والحساب، شاعرا ذكيا" (٢).
_________
= بغداد ٣: ٣٦٦ ونهاية الإقدام ١٨٠ والفصل لابن حزم ٢: ١٩٣ و ٣: ٨٣ و ٤: ٨٣ و ١٩٢ وابن خلكان ٤: ٢٦٥ ومروج الذهب ٢: ٢٩٨ وروضات الجنات ١٥٨ والشذرات ٢: ٨٥ والانتصار ١٧٩ وطبقات المعتزلة ٤٤ ومواضع متفرقة من البدء والتأريخ ومقالات الاسلاميين والفرق بين الفرق. والأعلام ٧: ٣٥٥ وتاريخ التراث العربي ٢: ٣٩٩ وقيل في وفاته عام ٢٢٦ و ٢٢٧ و ٢٣٥ هـ.
(١) مجلة العرفان الصيداوية ٢٣: ٣٥٠ ومعجم المؤلفين ١٢: ٩٣.
(٢) طبقات المفسرين للداودي ٢: ٢٦٦ وتاريخ علماء الأندلس ٢: ٦٦ والمدارك ٤: ٤٦٠ وبغية الوعاة ١: ٢٥٩.','2','646',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1172,'الإزميري [... - ١١٦٥ هـ / ... - ١٧٥٢ م]

<span data-type="title" id=toc-1629>محمد بن ولي بن رسول القيرشهري، ثم الإزميري:</span>
فقيه حنفي، مفسر، مشارك في بعض العلوم، ولي الإفتاء بإزمير وتوفي بها. من تصانيفه الكثيرة "بدائع البرهان في علوم القرآن" و "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (١).

الطرطوشي [٤٥١ - ٥٢٠ هـ / ١٠٥٩ - ١١٢٦ م]

<span data-type="title" id=toc-1630>محمد بن الوليد بن محمد بن خلف بن سليمان بن أيوب القرشي الفهري الأندلسي، أبو بكر الطرطوشي، ويقال له ابن أبي رندقة:</span>
فقيه مالكي، من حفاظ الحديث، مفسر، أديب، من أهل طرطوشة بشرق الأندلس. تفقه ببلاده، ورحل إلى المشرق سنة ٤٧٦ هـ، وحج، وزار العراق ومصر وفلسطين ولبنان، وأقام مدة في الشام، وسكن الإسكندرية، ودرس بها، واستمر فيها إلى أن توفي. من كتبه "مختصر تفسير الثعلبي" مخطوط (٢).
_________
(١) هدية العارفين ٢: ٣٢٨ ومعجم المؤلفين ١٢: ١٥
(٢) الوافي ٥: ١٧٥ وأزهار الرياض ٣: ١٦٢ ونفح =','2','646',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1173,'القطعي [... - ٢٥٣ هـ / ... - ٨٦٧ م]

<span data-type="title" id=toc-1631>محمد بن يحيى بن مهران (أبي حزم) القطعي البصري، أبو عبد الله:</span>
عالم بالقراءات، من حفاظ الحديث الثقات. أصله من البصرة، روى عنه الجماعة، والبخاري في غير الجامع. قال أبو حاتم: "صالح الحديث صدوق". وذكره ابن حبان في الثقات. ترجم له الداودي في "طبقات المفسرين" وقال: له "لغات القرآن". كما ترجم له صاحب "تاريخ التراث العربي" وقال: "وذكر ابن النديم في الفهرست (ص ٣٦) كتابه متشابه القرآن". والذي في الفهرست (ص ٣٩) - طبعة طهران ١٩٧١ - القطعي، وهو أحمد بن جعفر، وليس القطعي (١).
_________
= الطيب ١: ٥١٧ ووفيات الأعيان ٤: ٢٦٢ وتاريخ الفكر الأندلسي ١٧٤ وهدية العارفين ٢: ٨٥ والديباج ٢٧٦ وبغية الملتمس ١٢٥ والصلة ٥٧٥ والنجوم الزاهرة ٥: ٢٣١ والمغرب ٢: ٤٢٤ والشذرات ٤: ٦٢ والعبر ٤: ٤٨ وحسن المحاضرة ١: ١٩٢ ومرآة الجنان ٣: ٢٢٥ ومفتاح السعادة ١: ٣٤٣ ودائرة المعارف الإسلامية ١: ٧٧ وفي تاريخ وفاته خلاف بين بعض مترجميه.
(١) الوافي ٥: ١٨٤ والتهذيب ٩: ٥٥٨ وتذكرة الحفاظ ٥٤٨ وغاية النهاية ٢: ٢٧٨ وتاريخ التراث العربي ١: ١٦٢ وطبقات المفسرين للداودي ٢: ٢٦٧ والرجال للقيرواني ٤٧٨. والفهرست طبعة طهران ٣٨ و ٣٩.','2','647',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1174,'المرتضى [٢٧٨ - ٣١٠ هـ / ٨٩١ - ٩٢٢ م]

<span data-type="title" id=toc-1632>محمد بن يحيى بن الحسين بن القاسم ابن إبراهيم العلوي الطالبي، أبو القاسم، الملقب بالمرتضى:</span>
إمام زيدي، من بني الرسي الذين حكموا بصعدة وصنعاء. عالم بالفقه والأصول والتفسير. ولي الإمامة بعد وفاة أبيه الهادي إلى الحق (سنة ٢٩٨ هـ) وخوطب بالمرتضى لدين الله. ثم اعتزل بعد ستة أشهر. توفي بصعدة، من آثاره "تفسير القرآن" وهو إكمال للتفسير الذي بدأه جده وأضاف إليه أبوه (١).

الصولي [... - ٣٣٥ هـ / ... - ٩٤٦ م]

<span data-type="title" id=toc-1633>محمد بن يحيى بن عبد الله بن العباس ابن محمد بن صول، أبو بكر الصولي:</span>
نديم، من الأدباء الشعراء. كان من حفدة ملوك جرجان قبل الإسلام. نشأ ببغداد، فدرس الأدب والحديث والقرآن والتاريخ. واشتهر بلعب الشطرنج
_________
(١) تاريخ التراث العربي ٢: ٣٠٧ ومعجم الأسرات الحاكمة ١: ٩٨٧ والأعلام ٨: ٣ والرجال للجنداري ٣٦ ومعجم المؤلفين ١٢: ١٠١ والوافي ٥: ١٨٥ وبروكلمان الذيل ١: ٣١٦.','2','647',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1175,'فتقرب به إلى الخلفاء العباسيين، فنادم الراضي والمكتفي والمقتدر، وفي أواخر حياته جفاه الخليفة، فضاقت حاله، وانتقل إلى البصرة، وتوفي بها مستترا. من تصانيفه "الشامل في علم القرآن" لم يتمه (١).

الشلوبين [... - ٦٤٠ هـ / ... - ١٢٤٢ م]

<span data-type="title" id=toc-1634>محمد بن يحيى بن أحمد بن خليل الشلوبين الإشبيلي، أبو سعيد:</span>
عالم بالتفسير، من فقهاء المالكية، من أهل إشبيلية بالأندلس. رحل إلى المشرق، وحج، وعاد، فمات إثر وصوله في عشر الأربعين وستمائة. قال المقريزي: "ألف كتابا في "الأحكام" وكتابا في "غوامض التأويل" واعتنى بعلم التفسيراعتناء كبيرا" (٢).
_________
(١) الوافي ٥: ١٩٠ ومعجم الأدباء ١٩: ١٠٩ ولسان الميزان ٥: ٤٢٧ وتاريخ بغداد ٣: ٤٢٧ والنجوم الزاهرة ٣: ٢٩٦ والفهرست ١٦٨ ومعجم الشعراء ٤٣١ والعبر ٢: ٢٤١ والشذرات ٢: ٣٣٩ وهدية العارفين ٢: ٣٨ وإيضاح المكنون ٢: ٣٩ والمنتظم ٦: ٣٥٩ ومرآة الجنان ٢: ٣١٩ والبداية والنهاية ١١: ٢١٨ وابن خلكان ٤: ٣٥٦ والأعلام ٨: ٤ والموسوعة العربية الميسرة ١١٣٧.
(٢) طبقات المفسرين للداودي ٢: ٢٦٧.','2','648',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1176,'ابن زهرة [٧٥٨ - ٨٤٨ هـ / ١٣٥٧ - ١٤٤٤ م]

<span data-type="title" id=toc-1635>محمد بن يحيى بن أحمد، شمس الدين ابن زهرة:</span>
فقيه شافعي، مفسر، ولد في "حبراض" وانتقل إلى طرابلس، فدمشق، ودرس بالجامع الأموي. وعاد إلى طرابلس فأقام بها يقرىء ويحدث ويفتي. قال السخاوي: "وأثرى، وصار شيخ تلك البلاد، وحج مرارا، وزار بيت المقدس. وتوفي بطرابلس، ولم يخلف بعده بها مثله". من كتبه "فتح المنان في تفسير القرآن" في نحو عشر مجلدات، و "تعليقة" كالتذكرة في مجلد كبير يشتمل على تفسير وحديث الخ (١).

بهران [٨٨٨ - ٩٥٧ هـ / ١٤٨٣ - ١٥٥٠ م]

<span data-type="title" id=toc-1636>محمد بن يحيى بن محمد بن أحمد بن محمد بن موسى بن أحمد بهران، التميمي النسب، البصري الأصل، (الصعدي المولد والوفاة، سراج الدين)</span>
_________
(١) إنباء الغمر ٣: ٤٩١ حوادث سنة ٨٣٦ والضوء اللامع ١٠: ٧٠ والبدر الطالع ٢: ٢٧٦ وكشف الظنون ٤٣٨ وإيضاح المكنون ١: ٣٠٢ والتبر المسبوك ١١٣ وهدية العارفين ٢: ١٩٥ والأعلام ٧: ١٣٩ ومعجم المؤلفين ١٢: ٩٨.','2','648',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1177,'الصعدي المولد والوفاة، سراج الدين: فقيه زيدي، من كبارهم، مشارك في أنواع من العلوم. من أهل صعدة باليمن. له تصانيف منها "التكميل الشاف لتفسير الكشاف" (١).

التاذفي [٨٩٩ - ٩٦٣ هـ / ١٤٩٤ - ١٥٥٦ م]

<span data-type="title" id=toc-1637>محمد بن يحيى بن يوسف الربعي التاذفي، أبو البركات، جلال الدين:</span>
قاض حنبلي، ثم حنفي، ولد بحلب، وأخذ عن جماعة بها وبالقاهرة. ولي نيابة قضاء الحنابلة بحلب عن أبيه، ثم ولي نظر الجامع الأموي بدمشق عن والده، ثم سافر إلى مصر، فولي القضاء في "رشيد" فقضاء "المنزلة" مرتين، فقضاء حوران بسورية. وعزل سنة ٩٤٩ فأقام مدة في حماة، وتوفي بحلب. من كتبه "القول المذهب في بيان ما في القرآن من الرومي المعرب" (٢).
_________
(١) البدر الطالع ٢: ٢٧٨ وإيضاح المكنون ٣١٧ وهدية العارفين ٢: ٢٤٣ ومجلة المجمع العلمي العهربي ١٢: ١٢٨ و ٣٤: ٥١٢ والأعلام ٨: ١١ ومعجم المؤلفين ١٢: ١٠٩.
(٢) شذرات الذهب ٨: ٣٣٩ وأعلام النبلاء ٦: ٢٥ وهدية العارفين ٢: ٢٤٥ وكشف الظنون ١٣٦٥ وبروكلمان ٢: ٣٣٥ والذيل ٢: ٤٦٣ والأعلام ٨: ١١ ومعجم المؤلفين ١٢: ١١٣.','2','649',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1178,'ابن ماجه [٢٠٩ - ٢٧٣ هـ / ٨٢٤ - ٨٨٧ م]

<span data-type="title" id=toc-1638>محمد بن يزيد الربعي القزويني، أبو عبد الله، ابن ماجه:</span>
من كبار الحفاظ الثقات، وأحد الأئمة في علم الحديث، مفسر، مؤرخ، من أهل قزوين، رحل في طلب الحديث، فسمع في الري والبصرة والكوفة وبغداد والشام ومصر والحجاز، وكان من بين كبار شيوخه أصحاب مالك بن أنس والليث بن سعد. اشتهر بكتابه "السنن" وهو أحد الكتب الستة المعتمدة عند أهل السنة، قال الذهبي: "كتاب حسن لولا ما كدره أحاديث واهية ليست بالكثيرة" وقال أبو زرعة: "لعل لا يكون فيه (كتاب السنن) تمام ثلاثين حديثا مما في إسناده ضعف". أما المستشرق فؤاد سزكين فيقول: "إن كتابه يضم كثيرا من الأحاديث الضعيفة". وللتوسع في معرفة آراء رجال الحديث والمؤرخين في ابن ماجه و "سننه" يمكن الرجوع إلى مصادر ترجمته. أما عن لقبه، فيقول الإمام الرافعي (تاريخ قزوين ١٦٥) وماجه، لقب يزيد، والد أبي عبد الله، كذلك رأيت بخط أبي الحسن القطان، وهبة الله بن زاذان، وقد يقال: محمد بن يزيد بن ماجه، والأول','2','649',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1179,'أثبت .. " له "تفسير القرآن" (١).

المبرد [٢١٠ - ٢٨٦ هـ / ٨٢٥ - ٨٩٩ م]

<span data-type="title" id=toc-1639>محمد بن يزيد بن عبد الأكبر بن عمير ابن حسان الثمالي الأزدي، أبو العباس، المعروف بالمبرد:</span>
شيخ أهل النحو، وحافظ علم العربية، وأحد أئمة الأدب والأخبار والتفسير. ولد بالبصرة، وسكن بغداد، وأخذ بها عن المازني والسجستاني وغيرهما، فصار إمام البصريين. وأخذ عنه نفطويه والزجاج والسراج وغيرهم. وعنفت الخصومة بينه وبين معاصره ثعلب (انظر ترجمته) إمام الكوفيين، الذي لم يكن فصيح العبارة ولا قادرا على التعليلات والأقيسة النحوية، فكان يتهرب من مناظرته،
_________
(١) تذكرة الحفاظ ٦٣٦ وطبقات المفسرين ٢: ٢٧٢ والعبر ٢: ٥١ وتهذيب التهذيب ٩: ٥٣٠ وابن خلكان ٤: ٢٧٩ والمنتظم ٥: ٩٠ والنجوم الزاهرة ٣: ٧٠ والبداية والنهاية ١١: ٥٢ والشذرات ٢: ١٦٤ ومرآة الجنان ٢: ١٨٨ وتاريخ التراث العردبي ١: ٣٧٧ وتاريخ قزوين ١٦٥ وخلاصة تهذيب الكمال ٣١٢ ومفتاح السعادة ٢: ١٣٩ والرسالة المستطرفة ١٢ وكشف الظنون ٤٣٩ و ١٤٠٥ وهدية العارفين ٢: ١٨ وبروكلمان الترجمة العربية ٣: ١٩٨ والوافي ٥: ٢٢٠ والأعلام ٨: ١٥ ومعجم المؤلفين ١٢: ١١٥.','2','650',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1180,'ويرى كثير من الدارسين لتاريخ النحو العربي أن هذه الخصومة بين الرجلين الكبيرين أصل التفرقة بين مدرستي البصرة والكوفة في النحو، "وكان الدارسون قبلهما يأخذون من المدرستين دون كبير تفرقة". أما عن لقبه "المبرد" فيقال أن شيخه المازني لما ألف كتابه "الألف واللام" سأل المبرد عن دقيقه وعويصه، فأجابه بأحسن جواب، فقال له: "قم فأنت المبرد بكسر الراء" أي المثبت للحق، فغيره الكوفيون، وفتحوا الراء. وقال الزبيدي في شرح خطبة القاموس: "المبرد بفتح الراء المشددة عند الأكثر وبعضهم يكسر". مات ببغداد. من كتبه "إعراب القرآن" و "معاني القرآن" ويعرف بالكتاب التام. وكتاب "الحروف ومعاني القرآن" إلى سورة طه، و "ما اتفقت ألفاظه واختلفت معانيه في القرآن" طبع بمصر بتحقيق الأستاذ عبد العزيز الميمني سنة ١٣٥٠ هـ (١).
_________
(١) أخبار النحويين البصريين ٩٦ وبغية الوعاة ١: ٢٦٩ وتاريخ بغداد ٣: ٣٨٠ والبداية والنهاية ١١: ٧٩ ونزهة الألباء ٢١٧ وطبقات المفسرين ٢: ٢٦٧ وإنباء الرواة ٣: ٢٤١ والمتظم ٦: ٩ والعبر ٢: ٧٤ وهدية العارفين ٢: ٢٠ ومفتاح السعادة ١: ١٥٧ ومعجم الأدباء ١٩: ١١١ وشذرات الذهب ٢: ١٩٠ =','2','650',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1181,'الكلينى [... .. - ٣٢٩ هـ / ... - ٩٤١ م]

<span data-type="title" id=toc-1640>محمد بن يعقوب بن إسحاق، أبو جعفر الكليني:</span>
فقيه إمامي، عارف بالأخبار والحديث والتفسير. من أهل "كلين" بالري، سكن في بغداد وكان شيخ الشيعة فيها. وتوفي بها. له كتب منها "فضائل القرآن" (١).

الفيروزابادي [٧٢٩ - ٨١٧ هـ / ١٣٢٩ - ١٤١٤ م]

<span data-type="title" id=toc-1641>محمد بن يعقوب بن محمد بن إبراهيم ابن عمر بن أبي بكر بن أحمد، أبو طاهر، مجد الدين الشيرازي (الفيروزابادي)</span>
_________
= ومرآة الجنان ٢: ٢١٠ ولسان الميزان ٥: ٤٣٠ والمزهر ٢: ٤٠٨ و ٤١٩ و ٤٢٧ و ٤٦٤ ووفيات الأعيان ٤: ٣١٣ ونور القبس ٣٢٤ والحياة الأدبية في العصر العباسي ٣٢٤ وسمط اللآلي ٣٤٠ والأعلام ٧: ١٤٤ والنجوم الزاهرة ٣: ١١٧ وكنوز الأجداد ١٠١ والأنساب الورقة ١١٦ أ ١١٦ ب مادة (الثمالي) وغاية النهاية ٢: ٢٨٥ واللباب ١: ٢٤١ مادة (الثمالي) وطبقات النحويين واللغويين للزبيدي ١٠١ والمختصر في أخبار البشر ٢: ٥٨ ومعجم المؤلفين ١٢: ١١٤ ومعجم الشعراء٤٤٩ وروضات الجنات ٤٠٠ والموسوعة العربية الميسرة ١٦٤٠ وكشف الظنون ١٢٣ وأماكن متفرقة. وتاريخ آداب اللغة العربية ٢: ٢١٦ وطبقات النحاة لابن قاضي شهبة ١: ١٤٦.
(١) هدية العارفين ٢: ٣٥ وإيضاح المكنون ٢: ١٩٧ وروضات الجنات ٥٥٠ وفوائد الرضوية ٦٥٧ والأعلام ٨: ١٧.','2','651',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1182,'الفيروزابادي: من أئمة اللغة والأدب، قاض، كان مرجع عصره في اللغة والحديث والتفسير، ولد بكارزين (بكسر الراء، وتفتح، كما في القاموس) من أعمال شيراز، ونشأ بها. وانتقل إلى شيراز وأخذ عن علمائها، ثم انتقل إلى العراق، وجال في مصر والشام، ودخل بلاد الروم والهند. ورحل إلى زبيد سنة ٧٩٦ هـ فتلقاه ملكها الأشرف إسماعيل وبالغ في إكرامه، وقرأ عليه، فسكنها وولي قضاءها. واستمر بزبيد مدة عشرين سنة، وقدم في خلال هذه المدة مكة مرارا وجاور بالمدينة والطائف. وتوفي بزبيد. من كتبه "بصائر ذوي التميز في لطائف الكتاب العزيز" طبع منه ثلاثة أجزاء، و "تيسير فاتحة الاناب في تفسير فاتحة الكتاب" مجلد كبير، و "الدر النظيم المرشد إلى مقاصد القرآن العظيم" و "حاصا كورة الخلاص في فضائل سورة الإخلاص" و "قطبة الخشاف لحل خطبة الكشاف" قال في كشف الظنون: "ثم كتب ثانيا وسماه "نغبة الرشاف من خطبة الكشاف" وذكر أن الأول أصيب بكفة الإتلاف عند مغيرة الاعجاف، وأعاد العمل سنة ٧٦٨ هـ،. وينسب له "تنوير المقباس','2','651',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1183,'في تفسير ابن عباس" طبع بالقاهرة سنة ١٣٠٢ هـ، و ١٣١٦ هـ و ١٣٣٢ هـ و ١٩٣٧ و ١٩٦٥ م (١).

الفريابي [١٢٠ - ٢١٢ هـ / ٧٣٨ - ٨٢٧ م]

<span data-type="title" id=toc-1642>محمد بن يوسف بن واقد الضبي بالولاء، التركي الأصل، أبو عبد الله الفريابي:</span>
عالم بالحديث، من الحفاظ. حدث عن الأوزاعي وسفيان الثوري وغيرهما، وقرىء عليه بمكة، ونزل قيسارية بفلسطين وتوفي بها. وقد ارتحل إليه أحمد بن حنبل فبلغه موته فرجع من حمص. قال البخاري: "كان من أفضل
_________
(١) تاريخ التراث العربي ١: ١٨٢ والضوء اللامع ١٠: ٧٩ وروضات الجنات ٢٠٧ ومفتاح السعادة ١: ١١٩ والبدر الطالع ٢: ٢٨٠ وبغية الوعاة ١: ٢٧٣ وشذرات الذهب ٧: ١٢٦ وهدية العارفين ٢: ١٨٠ وطبقات المفسرين للداودي ٢: ٢٧٤ وذيل تذكرة الحفاظ ٢٥٦ وكشف الظنون ٢٤٦ - ٤٥٥ - ٥٠٢ - ٥١٩ - ٦٢٤ - ٧٣٦ - ١٣٥١ - ١٤٨٠ والعقود اللؤلؤية ٢: ٢٦٤ وأزهار الرياض ٣: ٣٨ وأنيس الجليس ٢: ١٢٣ والأعلام ٨: ١٩ ومعجم المؤلفين ١٢: ١١٨ وعقود الجوهر ٣٠١ وتاريخ آداب اللغة العربية ٣: ١٥٧ والروض العاطر ٢: ٢٤٩ ومجلة الجنان سنة ١٨٧١ ص ٧٠١ وتاج العروس ١: ١٣ وفهرس التيمورية ١: ١٦٣، ٢٤٣ ثم ٣: ٢٣٢ والشقاثق النعماية ٢١ والموسوعة العربية الميسرة ١٣٤٥ ومنجد الأعلام ٥٣٥ ومجلة الحج ١١: ١٨٠.','2','652',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1184,'أهل زمانه" وروى عنه ٢٦ حديثا. من كتبه "تفسير القرآن" رواه عنه عبد الله ابن محمد بن سعيد بن أبي مريم، واستخدمه الثعلبي في كتابه "الكشف والبيان" (١).

ابن بندار [... - بعد ٣٨ هـ / ... - بعد ٩٩٤ م]

<span data-type="title" id=toc-1643>محمد بن يوسف بن بندار:</span>
مفسر، ذكره صاحب طبقات المفسرين وقال: "له كتاب "علم نكت القرآن" لخصه من كتاب أبي الحسن علي بن عيسى البغدادي النحوي" المتوفى سنة ٣٨٤ هـ. ولم أعثر له على ترجمة وافية فيما بين يدي الساعة من كتب الرجال (٢).

الكفرطابي [... - ٥٥٣ هـ / ... - ١١٥٨ م]

<span data-type="title" id=toc-1644>محمد بن يوسف بن عمر بن علي الكفرطابي، أبو عبد الله، ويعرف بابن (المنيرة، نزيل شيزر)</span>
_________
(١) تاريخ التراث العربي ١: ٢٠٥ وتذكرة الحفاظ ٣٧٦ والرسالة المستطرفة ٦٧ والنجوم الزاهرة ٢: ٢٠٤ وطبقات المفسرين للداودي ٢: ٢٩٢ وتهذيب التهذيب ٩: ٥٣٥ وهدية العارفين ٢: ١٠ والأعلام ٨: ٢٠ ومعجم المؤلفين ١٢: ١٤٠ والوافي ٥: ٢٤٣ والعبر ١: ٣٦٣.
(٢) طبقات المفسرين للداودي ٢: ٢٨٠.','2','652',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1185,'المنيرة، نزيل شيزر: أديب، نحوي، لغوي، له شعر، نسبته إلى "كفرطاب" بين المعرة وحلب في سورية. قال ابن عساكر: "سمع الحديث من أبي السمح الفقيه الحنفي نزيل شيزر، وقرأ الأدب على أبي عبد الله الطليطلي، قدم دمشق ثم رجع إلى شيزر. وفي "الأعلام" للزركلي أنه "انقطع في جامع حلب أربعين سنة، يصلي بالناس، ويقرىء العلوم. له "غريب القرآن" (١).

السمرقندي [... - ٥٥٦ هـ / ... - ١١٦١ م]

<span data-type="title" id=toc-1645>محمد بن يوسف بن محمد بن علي بن محمد العلوي الحسني، أبو القاسم، ناصر الدين، المدني السمرقندي:</span>
أديب، عالم بالتفسير والحديث والوعظ، من فقهاء الحنفية، من أهل سمرقند، حج سنة ٥٤٢ هـ وأقام في عودته مدة في بغداد، ودخل مرو، ومات بسمرقند، وقيل: قتل بها صبرا. وكان يبسط
_________
(١) خريدة القصر شعراء الشام ١: ٥٧٣ ومعجم الأدباء ١٩: ١٢٢ وكشف الظنون ١٢٠٨ وهدية العارفين ١: ٧٨ والأعلام ٧: ١٤٩ وبغية الوعاة ١: والوافي ٥: ٢٤٧.','2','653',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1186,'لسانه في حق الأئمة والعلماء (١).

ابن سعاده [٤٩٦ - ٥٦٥ هـ / ١١٠٣ - ١١٧٠ م]

<span data-type="title" id=toc-1646>محمد بن يوسف بن سعادة، أبو عبد الله:</span>
فقيه مالكي، قاض، نحوي، كان عارفا بالسنن والآثار، والتفسير، والفروع، والأدب، وعلم الكلام، مائلا إلى التصوف. أصله من بلنسية، ولد وتعلم بمرسية. سمع أبا علي الصدفي واختص به وأكثر عنه، ورحل إلى المشرق سنة ٥٢٠ هـ، فحج، وأخذ عن علماء مكة والإسكندرية والمهدية. وعاد إلى مرسية في سنة ٥٢٦ هـ، فولي خطة الشورى بها مضافة إلى الخطبة بجامعها، ثم ولي قضائها، فقضاء شاطبة، وتوفي بها مصروفا عن القضاء، في منسلخ ذي الحجة سنة ٥٦٥ ودفن أول يوم من سنة ٥٦٦ هـ (٢).
_________
(١) طبقات المفسرين ٢: ٢٩١ والجواهر المضية ٢: ١٤٧ والفوائد البهية ٢١٩ وهدية العارفين ٢: ٩٤ والأعلام ٨: ٢٢ ومعجم المؤلفين ١٢: ١٣٧.
(٢) التكملة ٢: ٥٠٥ الترجمة ١٣٩٠ وبغية الملتمس ١٣٢ وطبقات المفسرين ٢: ٢٨٠ والشذرات ٤: ٢١٨ وبغية الوعاة ١: ٢٧٧ والديباج ٢٨٧ ومعجم ابن الآبار ١٧٦ والوافي ٥: ٢٥٠ ومعجم الأدباء ١٩: ١٠٩ وهو فيه محمد بن يحيى، ونفح الطيب: ٥٦٥.','2','653',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1187,'الغزنوي [٥٢٢ - ٥٩٩ هـ / ١١٢٨ - ١٢٠٣ م]

<span data-type="title" id=toc-1647>محمد بن يوسف بن علي الغزنوي، أبو الفضل:</span>
محدث، مقرىء، مفسر، من أكابر فقهاء الحنفية، أصله من حلب، ومولده ببغداد، ونسبته - إلى غزنه، دخل القاهرة وتوفي بها. قال السيوطي: "وسمع الحديث من أبي الفضل بن ناصر، وروى عنه الرشيد العطار والمنذري بالإجازة" وقال ابن الجزري: "مقرىء فاضل، فقيه مفسر، سمع في صغره من أبي بكر قاضي المارستان، وغيره، وقرأ عليه العلاماتان أبو الحسن السخاوي وأبو عمرو بن الحاجب وغيرهما". (١)

المزدغي [٦٢٣ - ٦٥٥ هـ / ١٢٢٦ - ١٢٥٧ م]

<span data-type="title" id=toc-1648>محمد بن يوسف بن عمران المزدغي، أبو عبد الله:</span>
فقيه مالكي، مفسر، محدث، خطيب، أديب، من أهل فاس مولدا ووفاة. أخذ عنه ابن عبد الملك صاحب "الذيل والتكملة". من كتبه
_________
(١) طبقات المفسرين للداودي ٢: ٢٩١ وشذرات الذهب ٤: ٣٤٣ وغاية النهاية ٢: ٢٨٦ والجواهر المضية ٢: ١٤٧ والفوائد البهية ٢٠٤.','2','654',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1188,'"تفسير القرآن" انتهى فيه إلى سورة الفتح (١).

الجزري [٦٣٧ - ٧١١ هـ /١٢٣٩ - ١٣١٢ م]

<span data-type="title" id=toc-1649>محمد بن يوسف بن عبد الله بن محمود، أبو عبد الله، شمس الدين الجزري:</span>
فقيه شافعي، خطيب، عارف بالتفسير والأصلين والمنطق والطب والرياضيات وغير ذلك من العلوم. كان أبوه صيرفيا بجزيرة ابن عمر، فولد ونشأ بها. وسافر إلى مصر، فأقام بقوص ثم بالقاهرة، ودرس بمدرسة الشريف بن ثعلب والصاحبية والشريفية والمعزية، وولي خطابة الجامع الطولوني وجامع الصالح خارج باب زويلة. وقرأ عليه تقي الدين السبكي وروى عنه. قال المقريزي: "كان له يد طولى في تفسير القرآن". (٢)
_________
(١) شجرة النور ١٩٩ ونيل الابتهاج ٢٢٩ وسلوة الأنفاس ٢: ٣٨. ومعجم المؤلفين ١٢: ١٣٣.
(٢) طبقات المفسرين للداودي ٢: ٢٨٣ وطبقات السبكي ٦: ٣١ وشذرات الذهب ٦: ٤٢ والدرر الكامنة: ٦٧٥ وتاريخ علماء بغداد ٢١١ وبغية الوعاة ١: ٢٧٩ وهدية العارفين ٢: ١٤٢ وحسن المحاضرة ١: ٥٤٤ وذيل العبر ٦٣ والنجوم الزاهرة ٩: ٢٢١ والسلوك ٢ ق ١: ١١٤ والوافي ٥: ٢٦٣ والأعلام ٨: ٢٥.','2','654',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1189,'أبو حيان النحوي [٦٥٤ - ٧٤٥ هـ / ١٢٥٦ - ١٣٤٤ م]

<span data-type="title" id=toc-1650>محمد بن يوسف بن علي ين يوسف ابن حيان الغرناطي الجياني: النفزي، أثير الدين، أبو حيان:</span>
نحوي عصره، ولغويه، ومفسره، ومحدثه، ومقرئه، ومؤرخه، وأديبه. ولد بمطخشارش من أعمال غرناطة. ودرس القرآن والحديث والنحو واللغة، ببلش ومالقة والمرية بالأندلس، وتنقل بالمغرب ومصر والشام والحجاز. وسمع من نحو أربعمائة وخمسين شيخا. وتولى التدريس بمدارس مصر والشام ومساجدها. واشتهرت تصانيفه في حياته وقرئت عليه. كان ظاهري المذهب، وتحول بمصر شافعيا، وقد ظهرت آثار مذهبه في تفسيره ونحوه. توفي بالقاهرة بعد أن كف بصره. من كتبه "البحر المحيط" في تفسير القرآن، طبع بمصر، ثماني مجلدات، والناحية النحوية هي أبرز ما فيه من البحوث التي تدور حول آيات الكتاب العزيز، و "النهر الماد" طبع، اختصر به البحر المحيط، و "تحفة الأريب بما في القرآن من الغريب" طبع (١).
_________
(١) الوافي ٥: ٢٦٧ ونكت الهميان ٢٨٠ والموسوعة العربية ٣٢ وبغية الوعاة ١: ٢٨٠ والنجوم الزاهرة ١٠ =','2','655',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1190,'ناظر الجيش [٦٩٧ - ٧٧٨ هـ / ١٢٩٨ - ١٣٧٧ م]

<span data-type="title" id=toc-1651>محمد بن يوسف بن أحمد بن عبد الدائم، محب الدين الحلبي ثم المصري، المعروف بناظر الجيش:</span>
عالم بالعربية، مفسر، محدث، ولد بحلب، وبها نشأ وتعلم، ثم قدم القاهرة ولازم أبا حيان وغيره. ودرس التفسير بالمنصورية، وحج سنة ٧٣٢ هـ، ثم ولي نظر الجيش بالديار المصرية. قال ابن حجر: "كان عالي الهمة نافذ الكلمة كثير البذل والجود والرفد للطلبة والرفق بهم والمبالغة في السعي في قضاء حوائجهم". توفي بالقاهرة. (١)
_________
=: ١١١ وطبقات السبكي ٦: ٣١ والرسالة المستطرفة ١٠١ والدرر الكامنة ٥: ٧٠ وحسن المحاضرة ١: ٥٣٤ والبدر الطالع ٢: ٢٨٨ وذيل العبر ٢٤٥ وذيل تذكرة الحفاظ ٢٣ وغاية النهاية ٢: ٢٨٥ والشذرات ٦: ١٤٥ ونفح الطيب ٢: ٥٣٥ وفوات الوفيت ٤: ٧١ ودائرة المعارف الإسلامية ١: ٣٣٢ وهدية العارفين ٢: ١٥٢ وطبقات المفسرية للداودي ٢: ٢٨٦ والأعلام ٨: ٢٦ ومعجم المطبوعات ٣٠٦ وكشف الظنون ٦. والتفسير والمفسرون ١: ٣١٧ والبلغة ٢٠٣ والكنية الكامنة ٨١ والنور السافر ١٧٨ والزركشي ٣١٥.
(١) إنباء الغمر ١: ١٤٧ وحسن المحاضرة ١: ٥٣٧ وغاية النهاية ٢: ٢٨٤ وبغية الوعاة ١: ٢٧٥ والنجوم الزاهرة ١١: ١٤٣ والوافي ٥: ٢٩٠ والدرر الكامنة ٥: ٦١ وفيه مولده بالقاهرة. وطبقات المفسرين للداودي ٢: ٢٧٩.','2','655',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1191,'الكرماني [٧١٧ - ٧٨٦ هـ / ١٣١٧ - ١٣٨٤ م]

<span data-type="title" id=toc-1652>محمد بن يوسف بن علي بن سعيد، شمس الدين الكرماني:</span>
عالم بالفقه والتفسير والحديث والأصلين والعربية. أخذ عن والده وغيره، ولازم عضد الدين الإيجي اثنتي عشرة سنة، ثم طاف البلاد ودخل مصر والشام والحجاز والعراق، واستوطن بغداد وتصدى لنشر العلم بها ثلاثين سنة، وأقام مدة بمكة، ومات راجعا منها بمحلة تعرف بروض مهنا، ونقل إلى بغداد فدفن بها. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي، وصل فيها إلى سورة يوسف. قال في كشف الظنون: "وهي في مجلد، أولها "الحمد لله الذي وفقنا للخوض .. " و "أنموذج الكشاف" حاشية عليه، أولها "لا أحصي ثناء عليك، أنت كما أثنيت على نفسك، الحمد لله حمدا يوافي نعمه .. " نسخة منه في كتب مدرسة فاضلخان، تاريح كتابتها ٩٣٣ مخطوط. وضمائر القرآن" (١).
_________
(١) إنباء الغمر ١: ٢٩٩ والشذرات ٦: ٢٩٤ والدرر الكامنة ٥: ٧٧ وبغية الوعاة ٣: ٢٧٩ وكشف الظنون ١٨٥ و ١٨٩ وهدية العارفين ٢: ١٧٢ والبدر الطالع ٢: ٢٩٢ والنجوم الزاهرة ١١: ٣٠٣ وطبقات المفسرين ٢: ٢٨٥ والأعلام ٨: ٢٧ والتاج المكلل ٤٦٩ والبلدية: تفسير ٣٩ وتعليقات آغا بزوك ٢٠.','2','656',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1192,'الدهلوي [٧٢١ - ٨٢٥ هـ / ١٣٢١ - ١٤٢٢ م]

<span data-type="title" id=toc-1653>محمد بن يوسف بن علي بن محمد الحسيني، أبو الفتح صدر الدين الدهلوي:</span>
مفسر، زاهد، مشارك في بعض العلوم. ولد وتعلم في دهلي بالهند. واستقدمه فيروز شاه البهمني إلى كلبركة سنة ٨١٥ هـ فسكن بها يدرس ويفيد ويصنف إلى أن توفي. له تصانيف بالعربية والفارسية، فمن العربية، "تفسير القرآن" كتابان، أحدهما على منوال الكشاف. (١)

السنوسي [٨٣٢ - ٨٩٥ هـ / ١٤٢٨ - ١٤٩٠ م]

<span data-type="title" id=toc-1654>محمد بن يوسف بن عمر بن شعيب السنوسي الحسني - من جهة الأم - أبو عبد الله:</span>
كبير علماء تلمسان وزهادها في عصره، كان عالما بالتفسير والحديث وعلم التوحيد وغير ذلك. نشأ بتلمسان، وأخذ عن نصر الزواوي وغيره. أخباره كثيرة، ذكر بعضها تلميذه الملالي في كتابه "المواهب القدسية فى المناقب السنوسية". له تصانيف كثيرة، منها "تفسير سورة
_________
(١) الأعلام ٨: ٢٩ وهدية العارفين ٢: ١٨٤.','2','656',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1193,'(ص) وما بعدها من السور" و "مختصر حاشية التفتازاني على الكشاف" و "تفسير القرآن" إلى قوله تعالى: {أولئك هم المفلحون". (١)

الشمس الشامي [... - ٩٤٢ هـ / ... - ١٥٣٦ م]

<span data-type="title" id=toc-1655>محمد بن يوسف بن علي بن يوسف، أبو عبد الله شمس الدين الشامي:</span>
مؤرخ، مفسر، حافظ للحديث، ولد في صالحية دمشق، وسكن الرقوقية بصحراء القاهرة إلى أن توفي. من كتبه "الإتحاف بتمييز ما تبع فيه البيضاوي صاحب الكشاف" قال في كشف الظنون: "أوله الحمد لله الهادي للصواب" وقال الزركلي: "وهو رسالة رأيت نسخة منها في خزانة الرباط كتب عليها تأليف الشريف ابن حمزة الأرميوني". و "الجامع الوجيز الخادم للغات القرآن العزيز". (٢)
_________
(١) معجم أعلام الجزائر ١٨٠ والبستان ٢٣٧ وتعريف الخلف ١: ١٧٦ وهدية العارفين ٢: ٢١٦ وطبقات الحضيكي ١: ٢٢٤ ودوحة الناشر ٨٩ وفهرس الفهارس ٢: ٣٤٣ والأعلام ٨: ٢٩ ومعجم المؤلفين ١٢: ١٣٢ وشجرة النور ٢٦٦ ودرة الحجال ٢: ١٤١ ونيل الابتهاج ٣٢٥.
(٢) شذرات الذهب ٨: ٢٥٠ وهدية العارفين ٢: ٢٣٦ وفهرس الفهارس ٢: ٣٩٢ والرسالة المستطرفة ١١٣ وكشف الظنون ٧ و ١٩٣ وتاريخ آداب اللغة','2','657',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1194,'ابن أبي اللطف [... - ١٠٢٨ هـ / ... - ١٦١٩ م]

<span data-type="title" id=toc-1656>محمد بن يوسف بن أبي اللطف، رضي الدين:</span>
فقيه حنفي، أديب، مفسر، من أهل بيت المقدس. له كتب، منها "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي. قال في كشف الظنون: "وهي حاشية على الأنوار مع الكشاف وتفسير أبي السعود، في مجلد ضخم، أوله "الحمد لله الذي أنزل على عبده الكتاب .. علقها في درسه عند الصخرة إلى آخر (سورة) الأنعام، وأرسلها إلى المولى أسعد المفتي". (١)

بحثي الرومي [... - ١٠٣٣ هـ / ... - ١٦٢٤ م]

<span data-type="title" id=toc-1657>محمد بن يوسف الحميدي، المعروف ببحثي الرومي:</span>
قاض، مفسر، فقيه حنفي، تركي مستعرب، من أهل أنقرة، تولى قضاء العسكر بالأناضول، من آثاره "حاشية" على أنوار التنزيل، في التفسير، العربية ٣: ٣١٣ وبروكلمان ٢: ٣٠٤ والذيل ٢: ٤١٥ وفهرس التيمورية ٣: ٩٧ و ١٥٨ والأعلام ٨: ٣٠ ومعجم المؤلفين ١٢: ١٣١.
_________
(١) كشف الظنون ٦٦ و ١٩٣ وهدية العارفين ٢: ٢٧١ وخلاصة الأثر ٤: ٢٧٢ والأعلام ٨: ٣١.','2','657',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1195,'للبيضاوي. (١)

الإسبيري [١١٣٣ - ١١٩٤ هـ / ١٧٢١ - ١٧٨٠ م]

<span data-type="title" id=toc-1658>محمد بن يوسف بن يعقوب بن علي ابن محسن بن الشيخ إسكندر الغزالي الحلبي الشهير بالإسبيري:</span>
فقيه حنفي، أصولي، عارف بالتفسير، ولد بعينتاب، وأقام بحلب وولي الإفتاء بها. من كتبه "تعليقات" على تفسيري الكشاف والبيضاوي. (٢)

أطفيش [١٢٣٦ - ١٣٣٢ هـ / ١٨٢٠ - ١٩١٤ م]

<span data-type="title" id=toc-1659>محمد بن يوسف بن عيسى بن صالح أطفيش، وينتهي نسبه إلى عمر بن حفص الهنتاني جد العائلة الحفصية المالكة - سابقا - في تونس:</span>
مجتهد، من أكابر العلماء بالفقه والأدب واللغة والتفسير، ومن رجال النهضة الإصلاحية
_________
(١) هدية العارفين ٢: ٢٧٤ وإيضاح المكنون ١: ١٣٩ ومعجم المؤلفين ١٢: ١٢٥.
(٢) إعلام النبلاء ٧: ١٠٣ وهدية العارفين ٢: ٣٤٢ وسلك الدرر ٤: ١٢٠ والأعلام ٨: ٣١ ومعجم المؤلفين ١٢: ١٤١.','2','658',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1196,'الحديثة في الجزائر. ولد في بني يسقن، وحفظ القرآن واستظهره وهو ابن ثماني سنين، ثم أخذ عن علماء بلده، حتى نبغ واشتهر. وسافر إلى الديار المقدسة مرتين وكان يؤلف وهو في السفينة. عرف بعدائه الشديد للاستعمار، وحبه للعالم الإسلامي وغيرته عليه. وكان له أثر بارز في قضية بلاده السياسية ونهضتها الإصلاحية يدل على وطنية صادقة. عكف على التدريس والتصنيف والوعظ والإرشاد إلى أن توفي في مسقط رأسه عن ستة وتسعين عاما. من آثاره في التفسير "هميان الزاد ليوم المعاد" طبع في ستة أجزاء، و "التيسير" في سبعة أجزاء، و "داعي العمل ليوم الأمل" في أربعة أجزاء، فسر فيه القرآن من سورة الرحمن إلى سورة الناس. قال إبراهيم أطفيش: "لقد رأيت في هذا التفسير من التحقيق ما لم أره في غيره" (١) وقامت شهرة صاحب الترجمة في العالم الإسلامي على كتابه (شرح النيل وشفاء العليل) في الفقه الأباضي.
_________
(١) معجم أعلام الجزائر ٢١ ونهضة الجزائر الحديثة ١: ٢٨٩ والأعلام الشرقية ٢: ١٥٠ والأعلام ٨: ٣٢ ومعجم المؤلفين ١٢: ١٣٣ وفهرس دار الكتب ٣: ١٤٧ وفهرس التيمورية ٢: ١٩٨ والتفسير والمفسرون ٢: ٣١٩ وفيه دراسة وجيزة عن تفسير صاحب الترجمة (هميان الزاد) لا نوافق على كل ما جاء فيها من آراء.','2','658',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1197,'الحسني [١٢٦٧ - ١٣٥٤ هـ / ١٨٥١ - ١٩٣٥ م]

<span data-type="title" id=toc-1660>محمد بن يوسف بن عبد الرحمن بن عبد الوهاب بن عبد الله بن عبد الملك بن عبد الغني المغربي المراكشي البيباني، بدر الدين الحسني:</span>
محدث، مفسر، متكلم، مشارك في بعض العلوم، أصله من محلة ورياد العروس بمراكش. ولد بدمشق، وحفظ كثيرا من متون العلوم المختلفة، وانقطع للعبادة والتدريس، ورحل إلى الحجاز مرتين، وإلى مصر والقدس وغيرها، وارتفعت مكانته عند الحكام وأهل الشام. وتوفي بدمشق، من آثاره "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي. (١)

الساوجي [... - نحو ٦٣٠ هـ / ... - ١٢٣٣ م]

<span data-type="title" id=toc-1661>محمد بن يونس الساوجي، جمال الدين:</span>
زاهد، كان شيخ الطريقة القرندلية، نسبته إلى "ساوج بلاق" مقاطعة في شمالي إيران. أقام بدمشق مدة، ثم انتقل إلى دمياط ومات بها. قال
_________
(١) نفحة البشام ١١١ ورياض الجنة ٢: ١٦٥ والدر الفريد ١٤ والأعلام ٨: ٣٣ ومعجم المؤلفين ١٢: ١٣٩.','2','659',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1198,'الصفدي: "ذكر شمس الدين الجزري في تاريخه أنه رأى كراريس بخطه من "تفسير القرآن" له". (١)

الأصبهاني [٤٥٨ - ٥٣٦ هـ / ١٦٠٦ - ١١٤١ م]

<span data-type="title" id=toc-1662>محمود بن أحمد بن عبد المنعم بن ماشاذه، أبو منصور الأصبهاني:</span>
محدث، مفسر، من أهل أصبهان مولدا ووفاة. روى عنه أبو موسى المديني وابن السمعاني وغيرهما. قال السمعاني: "إمام مفسر واعظ، كان له التقدم والجاه العريض، وكان أوحد وقته، والمرجوع إليه في بلده". (٢)

الساغرجي [٤٨٠ - حوالي ٥٥٥ هـ / ١٠٨٧ - حوالي ١١٦٠ م]

<span data-type="title" id=toc-1663>محمود بن أحمد بن الفرج بن عبد العزيز، أبو المحامد السمرقندي السغدي الساغرجي:</span>
مفسر، محدث، من فقهاء الحنفية، نعته الذهبي بشيخ الإسلام. رحل وكتب وأملى الحديث بسمرقند. قال السمعاني: "إمام مبرز في التفسير
_________
(١) الوافي ٥: ١٧٥ والدارس ٢: ٢١٠
(٢) طبقات المفسرين للسيوطي ٤٠ وطبقات المفسرين =','2','659',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1199,'والحديث والأصول، والمتفق، والمفترق، والوعظ .. " (١)

الزنجاني [٥٧٣ - ٦٥٦ هـ / ١١٧٧ - ١٢٥٨ م]

<span data-type="title" id=toc-1664>محمود بن أحمد بن محمود بن بختيار، أبو الثناء، ويقال أبو المناقب، الزنجاني:</span>
لغوي، مفسر، محدث، أصولي، من فقهاء الشافعية. من أهل زنجان بقرب أذربيجان، استوطن بغداد، وولي فيها نيابة قضاء القضاة، وعزل. ودرس بالنظامية وبالمستنصرية. استشهد ببغداد أيام دخول هولاكو إليها. من آثاره "تفسير القرآن". (٢)

القونوي [... - ٧٧١ هـ / ... - ١٣٦٩ م]

<span data-type="title" id=toc-1665>محمود بن أحمد بن مسعود بن عبد (الرحمن القونوي، أبو الثناء، جمال الدين، وعرف بابن السراج)</span>
_________
= للداودي ٢: ٣٥٨ وطبقات السبكي ٧: ٢٨٥ واللباب
(١) طبقات المفسرين للداودي ٢: ٣٠٩ وطبقات المفسرين للسيوطي ٤١ وتاج التراجم ٦٩ واللباب ٣: ٢٤٢ والجواهر المضية ٢: ١٥٦.
(٢) هدية العارفين ٢: ٤٠٥ وطبقات السبكي ٨: ٣٦٨ والنجوم الزاهرة ٧: ٦٨ وطبقات المفسرين للداودي ٢: ٣٠٩ والأعلام ٨: ٣٧ ومعجم المؤلفين','2','660',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1200,'الرحمن القونوي، أبو الثناء، جمال الدين، وعرف بابن السراج: قاض، أصولي متكلم، عارف بالتفسير، من فقهاء الحنفية. من أهل دمشق، ولي قضاءها وتوفي بها. من كتبه "تهذيب أحكام القرآن". (١)

العيني [٧٦٢ - ٨٥٥ هـ / ١٣٦١ - ١٤٥١ م]

<span data-type="title" id=toc-1666>محمود بن أحمد بن موسى بن أحمد بن الحسن بن يوسف بن محمود، أبو محمد، بدر الدين الشهير بالعيني:</span>
مؤرخ، مفسر، فقيه حنفي، أصولي، لغوي، نحوي، من كبار المحدثين. ولد في عينتاب (بين حلب وأنطاكية) وبها نشأ وتعلم، ثم رحل طلبا للعلم، فأخذ عن أجلة الشيوخ في حلب وبهسنا وكختسا وملطية. وسافر إلى الحج سنة ٧٨٨ هـ، فأخذ في بيت المقدس عن علاء الدين السيرامي، ثم صحبه معه إلى القاهرة ولازمه. وولي فيها الحسبة وقضاء الحنفية
_________
(١) طبقات المفسرين للداودي ٢: ٣١٠ والجواهر المضية ٢: ١٥٦ والفرائد البهية ٢٠٧ وهدية العارفين ٢: ٤٠٩ والدرر الكامنة ٥: ٩٠ وتاج التراجم ٧٠ وقضاة دمشق ٢٠٠ والرد الوافر ١٦٨ والأعلام ٨: ٣٧ وفي سنة وفاته اختلاف يسير.','2','660',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1201,'ونظر السجون، وتقرب من الملك المؤيد شيخ المحمودي حتى عد من أخصائه ولما ولي الأشرف برسباي السلطنة سامره ولزمه، وكان يكرمه ويقدمه. ثم صرف عن وظائفه سنة ٨٣٣ هـ، لكنه أعيد لحسبة القاهرة سنة ٨٣٥ وبقي فيها حتى سنة ٨٤٢ فعزل عنها، ولم يل بعد ذلك وظيفة عامة في الدولة، وتفرغ للتأليف والتدريس والفتوى، إلى أن توفي بالقاهرة، ودفن بمدرسته في حارة كتامة بحي الأزهر. من كتبه "حواش" على تفسير البغوي. و "حواش" على تفسير أبي الليث، و "حواش" على تفسير الكشاف. (١)

ابن قاضي سماونة [... - ٨٢٣ هـ / ... - ١٤٢٠ م]

<span data-type="title" id=toc-1667>محمود بن إسرائيل بن عبد العزيز، (بدر الدين، الشهير بابن قاضي سماونة)</span>
_________
(١) السيف المهند، مقدته. معجم المطبوعات ١٤٥٢ وزيدان ٢١٢ والضوء اللامع ١٠: ١٣١ ونظم العقيان ١٧٤ والبدر الطالع ٢: ٢٩٤ والشذرات ٧: ٢٨٧ والتبر المسبوك ٣٧٥ وإعلام النبلاء ٥: ٢٥٥ وحسن المحاضرة ١: ٢٧٠ والمؤرخون في مصر ٢٠ والتعريف بالمؤرخين ١: ٣٣٢ ومفتاح السعادة ١: ٢١٥ وفهرس الفهارس ٢: ٢١٦ والذيل على رفع الاصر ٤٢٨ والأعلام ٨: ٣٨ والجواهر المضية ٢: ١٦٥ ومعجم المؤلفين ١٢: ١٥٠ وبغية الوعاة ٢: ٢٧٥.','2','661',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1202,'بدر الدين، الشهير بابن قاضي سماونة: فقيه حنفي، مفسر، صوفي، مشارك في بعض العلوم، من القضاة. ولد وتعلم بقلعة سماونة في سنجق كوتاهية بتركيا. ورحل إلى قونية ثم إلى مصر. وحج وتصوف، ورحل إلى تبريز مرشدا، فأكرمه فيها الأمير تيمور خان. وعاد إلى مصر، فبلاد الروم، واستقر في أدرنة، فنصب قاضيا للعسكر. وحبس في وشاية، ففر، وصار إلى زغرة من ولاية الروم إيلي، فاتهم بأنه يريد السلطنة، فأخذ وقتل بسيروز. من كتبه "تفسير القرآن" في مجلدين. (١)

الصادقي [... - ٩٧٠ هـ / ... - ١٥٦٣ م]

<span data-type="title" id=toc-1668>محمود بن الحسن الأفضلي الحاذقي، الشهير بالصادقي الكيلاني:</span>
فقيه شافعي، مفسر، جاور بالمدينة المنورة وتوفي بها. من كتبه "هداية الرواة إلى الفاروق المداوي للعجز عن تفسير
_________
(١) مفتاح السعادة ٢: ١٤٨ وكشف الظنون ٤٤٣ وهدية العارفين ٢: ٤١٠ وتاج العروس ١٠: ١٨٤ والشقائق النعمانية ٢٣ ومعجم المطبوعات ٢١٠ والأعلام ٨: ٤٠ والفوائد البهية التعليقات، ١٢٧ ووفاته في بعض هذه المصادر سنة ٨٩٨ واسم أبيه إسماعيل. وانظر أيضا معجم المؤلفين ١٢: ١٥٢ ودائرة المعارف الإسلامية ١: ٢٥٩.','2','661',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1203,'البيضاوي" حاشية على أنوار التنزيل، فرغ منها سنة ٩٥٣ هـ. من سورة الأعراف إلى آخر القرآن. (١)

الكرماني [... - بعد ٥٠٠ هـ / ... - ١١٠٧ م]

<span data-type="title" id=toc-1669>محمود بن حمزة بن نصر الكرماني، برهان الدين، أبو القاسم، ويعرف بتاج القراء:</span>
عالم بالقراءات، نحوي، مفسر، من أهل كرمان. قال ياقوت: "أحد العلماء الفقهاء النبلاء، صاحب التصانيف والفضله، كان عجبا في دقة الفهم وحسن الاستنباط، لم يفارق وطنه ولا رحل .. ". من كتبه "البرهان في متشابه القرآن" مخطوط، قال في كشف الظنون: "مختصر أوله "الحمد لله الذي أنزل الفرقان" ذكر فيه الآيات المتشابهات التي تكررت فيه وسببها وفائدتها وحكمتها". و "لباب التأويل"، و "لباب التفاسير"، مخطوط، قال في كشف الظنون: أوله "الحمد لله منزل القرآن غير محدث ولا مخلوق الخ." ذكر في كتابه "البرهان في متشابه القرآن" أنه بين ما ذكره فيه
_________
(١) هدية العارفين ٢: ٤١٣ وكشف الظنون ١٨٩ ومعجم المؤلفين ١٢: ١٥٩ والأزهرية ١: ٣٠٣ طبعة ثانية.','2','662',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1204,'بشرائطه في هذا التفسير مشتملا، على أكثر ما فيه". و "العجائب والغرائب" قال في كشف الظنون: "أولها نبدأ باسم الله ونحمده ونعبده .. الخ". ذكر فيه ان أكثر الناس يرغبون في غرائب تفسير القرآن وعجائب تأويله، فجمع لهم ما قدر مقنعا لرغبتهم لما روي عن النبي عليه السلام: "أعربو القرآن والتمسوا غرائبه" وعن ابن عباس: "أن هذا القرآن ذو شجون وفنون، وظهور وبطون، لا تنقضي عجائبه". وأوجز في العبارة ولم يتعرض لذكر الآيات الظاهرة والوجوه المعروفة، فإنه قد أودع جميع ذلك في كتابه لباب التفاسير". وقال السيوطي في "الإتقان": "فيه أقوال منكرة لا يحل الإعتماد عليها ولا ذكرها إلا للتحذير منها" من ذلك أنه نقل قول "أبي مسلم" في "حم عسق": إن الحاء حرب علي ومعاوية، والميم ولاية المروانية، والعين ولاية العباسية، والسن ولاية السفيانية، والقاف قدرة مهدي، وقال: "أردت بذلك أن يعلم أن فيمن يدعي العلم حمقى". ومنه نقله قول من قال في (ألم): معنى ألف، ألف الله محمدا فبعثه نبيا، ومعنى لام لامه الجاحدون وأنكروه، ومعنى ميم الجاحدون المنكرون، من الموم، وهو البرسم" قال الزركلي: "وثمة ترهات أخرى','2','662',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1205,'حكاها في تفسيره، نقل السيوطي بعضها ونقل طاشكبري بعضا آخر، واستنكر إيراده لها" (١).

المناستري [... - ١٠٢٦ هـ / ... - ١٦١٧ م]

<span data-type="title" id=toc-1670>محمود بن الخياط المناستري الرومي:</span>
فقيه حنفي، قاض، توفي بمكة. من آثاره "رسالة" في التفسير. (٢)

الرفاعي [... - ١٣٥٠ هـ / ... ١٩٣١ م]

<span data-type="title" id=toc-1671>محمود بن ... ... ... ... الرفاعي:</span>
صوفي، مفسر، من أهل طرابلس الشام. من آثاره "تفسير سورة البقرة". (٣)
_________
(١) بغية الوعاة ٢: ٢٧٧ ومعجم الأدباء ١٩: ١٢٥ وطبقات المفسرين للداودي ٢: ٣١٢ وغايه النهاية ٢: ٢٩١ وهدية العارفين ٢: ٤٠٢ وكشف الظنون ٢٤١ و ٣٢، و ١١٢٦ و ١١٩٧ و ١٥٤١ ومفتاح السعادة ١: ٤٢١ والإتقان ٢: ٢٢١ والأعلام ٨: ٤٤ ومعجم المؤلفين ١٢: ١٦١ والكتب خانة ١: ١٣٣ و ٧: ٣٩٧
(٢) هدية العارفين ٢: ٤١٤ ومعجم المؤلفين ١٢: ١٦٣.
(٣) مجلة العرفان ٢١: ٢٦٠ ومعجم المؤلفين ١٢: ١٦٤.','2','663',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1206,'شلتوت [١٣١٠ - ١٣٨٣ هـ / ١٨٩٣ - ١٩٦٣ م]

<span data-type="title" id=toc-1672>محمود شلتوث:</span>
عالم بالدين وشيخ الجامع الأزهر، خطيب موهوب، من دعاة الإصلاح الديني والإجتماعي، ولد في "منية بني منصور" بالبحيرة بمصر. وتعلم بمعهد الإسكندرية الديني، وكان ترتيبه الأول في جميع سني الدراسة. وتخرج بالأزهر سنة ١٩١٨، وتنقل في التدريس إلى أن نقل مدرسا بالقسم العالي بالأزهر سنة ١٩٢٧. وسعا إلى إصلاح الأزهر، فعارضه بعض كبار الشيوخ، وفصله الشيخ الظواهري من وظيفته سنة ١٩٣١، فعمل في المحاماة حتى سنة ١٩٣٥، أعيد بعدها إلى الأزهر، مدرسا بكلية الشريعة، ثم عضوا في لجنة الفتوى، ثم وكيلا لكلية الشريعة، ثم عضوا في جماعة كبار العلماء (١٩٤١)، فعضوا في مجمع اللغة العربية ١٩٤٦، ثم شيخا للأزهر (١٩٥٨) وظل في منصبه إلى وفاته، من آثاره المطبوعة "تفسير القرآن" أجزاء منه في مجلد، ولم يتم رسالة "القرآن والقتال" (١).
_________
(١) دليل الطبقة الراقية ٦٩٠ والشخصيات البارزة ٢٩٦ والأزهر في ألف عام ٣: ١١٢ ومجلة مجمع اللغة العربية بالقاهرة ١٩: ١٤٧ و ١٥٥ والموسوة العربية الميسرة ١٠٩١ والأعلام ٧: ١٧٣.','2','663',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1207,'العبدلاني [... - ١١٧٣ هـ / ... - ١٧٦٠ م]

<span data-type="title" id=toc-1673>محمود بن عباس بن سليمان العبدلاني الكردي الشهرزوري، نزيل دمشق:</span>
فقيه شافعي، مفسر، ولد ونشأ ودرس في عبدلان، وولي الإفتاء في "كوي صنجق" وانتقل إلى حلب، ثم سكن دمشق وتوفي بها. له "زبدة الأنفاس في تفسير سورة الإخلاص". (١)

الأصبهاني [٦٧٤ - ٧٤٩ هـ / ١٢٧٦ - ١١٣٤٩ م]

<span data-type="title" id=toc-1674>محمود بن عبد الرحمن بن أحمد بن محمد بن أبي بكر بن علي، أبو الثناء، شمس الدين الأصبهاني:</span>
مفسر، نحوي، عالم بالعقليات، من فقهاء الشافعية. ولد بأصبهان، وتعلم بها وبتبريز ورحل إلى دمشق سنة ٧٢٥ هـ، فأكرمه أهلها، وسمع منه ابن تيمية فأعجب به وبالغ في تعظيمه. ولازم الجامع الأموي، ودرس بعد ابن الزملكاني بالرواحية. وانتقل إلى القاهرة سنة ٧٣٢ هـ وتولى تدريس المعزية بها.
_________
(١) سلك الدرر ٤: ١٢٧ وإيضاح المكنون ١: ٦٠٩ وهدية العارفين ٢: ٤١٦ وبروكلمان ٢: ٣٢٧ ومعجم المؤلفين ١٢: ١٧٣ والأعلام ٨: ٥٢.','2','664',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1208,'وبنى له الأمير قوصون الخانقاه بالقرافة، ورتبه شفي فيها في صفر سنه ٧٣٦ هـ، واستمر إلى أن توفي شهيدا بالطاعون، ودفن بالقرافة. من كتبه تفسير كبير للقرآن (منه الجزء الرابع مخطوط) سماه "أنوار الحقائق الربانية في تفسير الآيات القرآنية" قال الصفدي: "رأيته يكتب في تفسيره من خاطره من غير مراجعة، وقد جمع فيه بين "الكشاف" و "مفاتيح الغيب" جمعا حسنا بعبارة وجيزة، مع زيادات واعتراضات في مواضع كثيرة .. " (١)

الآيديني [... - ٩٥٦ هـ / ... - ١٥٤٩ م]

<span data-type="title" id=toc-1675>محمود بن عبد الله الرومي، بدر الدين، المعروف بالآيديني:</span>
مفسر، كان مدرسا في مدرسة الوزير محمود باشا
_________
(١) مفتاح السعادة ٢: ١٧٨ والدرر الكامنة ٥: ٩٥ وطبقات السبكي ٦: ٢٤٦ وهدية العارفين ٢: ٤٠٩ وإيضاح المكنون ١: ١٤٣ وبغية الوعاة ٢: ٢٧٨ والبدر الطالع ٢: ٢٩٨ وروضات الجنات ٢١٤ وتاريخ علماء بغداد ٢١٨ والشذرات ٦: ١٦٥ وطبقات المفسرين للداودي ٢: ٢١٣ والتعريف بالمؤرخين ١: ١٨٩ والأعلام ٨: ٥٢ ومعجم المؤلفين ١٢: ١٧٣ والفوائد البهية ١٩٨ وكشف الظنون ٤٤٢.','2','664',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1209,'الصوفي بالقسطنطينية، وتوفي بها. له "تفسير القرآن". (١)

الموصلي [... - ١٠٨٢ هـ / ... - ١٦٧١ م]

<span data-type="title" id=toc-1676>محمود بن عبد الله الموصلي:</span>
فقيه حنفي، صوفي، مفسر، ولد بالموصل وبها نشأ وتعلم، وأفتى، ثم انتقل إلى حلب، وتوفي بها عن ٨٣ سنة تقريبا. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي. (٢)

الألوسي الكبير [١٢١٧ - ١٢٧٠ هـ / ١٨٠٢ - ١٨٥٤ م]

<span data-type="title" id=toc-1677>محمود بن عبد الله بن محمود بن درويش الحسيني الآلوسي، شهاب الداين، أبو الثناء:</span>
شيخ علماء العراق في عصره، مفسر، محدث، فقيه، أديب، لغوي، مشارك في بعض العلوم. نسبته إلى جزيرة آلوس في وسط
_________
(١) هدية العارفين ٢: ٤١٣ وكشف الظنون ٤٤٣ ومعجم المؤلفين ١٢: ١٧٦ والشقائق النعمانية ٣٠٥.
(٢) إعلام النبلاء ٦: ٣٤٨ وهدية العارفين ٢: ٤١٦ وإيضاح المكنون ١: ١٤٢ وخلاصة الأثر ٤: ٣١٩.','2','665',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1210,'نهر الفرات، على خمس مراحل من بغداد. ولد ببغداد، وتقفد الإفتاء فيها سنة ١٢٤٨ هـ، وعزل، فانقطع للعلم. ثم سافر إلى الموصل، فالآستانة، ومر بماردين وسيواس، وأكرمه السلطان عبد المجيد، وعاد إلى بغداد، وعكف على التأليف إلى أن توفي. له تصانيف، أشهرها "روح المعاني في تفسير القرآن والسبع المثاني" في تسع مجلدات. شرع في تأليفه سنة ١٢٥٢ وفرغ منه سنة ١٢٥٧، طبع ببولاق ١٣٠١ - ١٣١٠ هـ. وهو تفسير جليل جامع لخلاصة كل ما سبقه من التفاسير. وله أيضا "دقائق التفسير" مخطوط (١).
_________
(١) مقدمة "روح المعاني" وتراجم مشاهير الشرق ٢: ٢٣٦ وأعلام العراق ٢١ والمسك الأذفر ١: ٥ والبغداديون أخبارهم ومجالسهم ٢٥٩ وجلاء العينين ٢٧ وأعيان البيان ٩٩ وهدية العارفين ٢: ٤١٨ والتاج المكلل ٣٦٠ وفهرس الفهارس ١: ٩٧ والموسوعة العربية ١٦٦٤ وتاريخ آداب اللغة العربية ٤: ٢٥٧ ونهضة العراق لمحمد مهدي البصير ٢١٩ ومصادر الدراسة الأدبية ٤٧ وحديقة الورد في مدائحه، والآداب العربية في القرن التاسع عشر ١: ٨٩ ومعجم المطبوعات ١: ٣ وحلية البشر. والأعلام ٨: ٥٣ ومعجم المؤلفين ١٢: ١٧٥ وإيضاح المكنون ١: ٥٨٦. والتفسير والمفسرون ١: ٣٥٢.','2','665',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1211,'النيسابوري [... - حيا ٥٥٣ هـ / ... - ١١٥٨ م]

<span data-type="title" id=toc-1678>محمود بن علي (أبي الحسن) بن الحسن النيسابوري القزويني، أبو القاسم، نجم الدين، الشهير ببيان الحق:</span>
مفسر، فقيه، لغوي، أديب. قال ياقوت: "له تصانيف ادعى فيها الإعجاز" منها "إيجاز البيان في معاني القرآن" قال في كشف الظنون: "يشتمل على أكثر من عشرة آلاف فائدة". وقال البغدادي: "فرغ منه بالخجند سنة ٥٥٣ و "باهر البرهان في مشكلات معاني القرآن" و "درر الكلمات على غرر الآيات الموهمة للتعارض والشبهات" و "غرر الأقاويل في معاني التنزيل" و "زبدة التفاسير ولمعة الأقاويل". (١)

الزمخشري [٤٦٧ - ٥٣٨ هـ / ١٠٧٥ - ١١٤٤ م]

<span data-type="title" id=toc-1679>محمود بن عمر بن محمد بن أحمد الخوارزمي الزمخشري، جار الله، أبو (القاسم)</span>
_________
(١) معجم الأدباء ١٩: ١٢٤ وبغية الوعاة ٢: ٢٧٧ وكشف الظنون ٢٠٥ والأعلام ٨: ٤٣ وهو في هذه المراجع محمود بن أبي الحسن بن الحسين. وهدية العارفين ٢: ٤٥٣ وهو فيه: محمود بن أبي الحسن علي. وإيضاح المكنون ١: ١٦٢ و ٤٦٨ و ٦١٠ و ٢: ٥٨ و ١٤٤ وطبقات المفسرين للداودي ٢: ٣٩٩.','2','666',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1212,'القاسم: إمام عصره في اللغة والنحو والبيان والتفسير. ولد في زمخشر من قرى خوارزم، ورحل إلى عدة أماكن، وخاصة إلى مكة حيث جاور بها زمنا فلقب بجار الله. وأخذ بمذهب الإعتزال، ودافع عنه في قوة حقا عد خاتمة شيوخ المعتزلة. ومات بالجرجانية من قرى خوارزم بعد رجوعه من مكة. أشهر كتبه "الكشاف عن حقائق التنزيل" فى تفسير القرآن، فرغ من تأليفه عام ٥٢٨ وطبع، وقد صادف نجاحا كبيرا، وإن اعتمد على آراء المعتزلة. وعارضه مفسرون آخرون للرد على ما فيه من آراء اعتزالية كالقاضي البيضاوي وغيره (١).
_________
(١) طبقات المفسرين للسيوطي ٤١ وطبقات المفسرين للداودي ٢: ٣١٤ وتذكرة الحفاظ ١٢٣٨ ومعجم الأدباء ١٩: ١٢٦ ونزهة الالباء ٣٩١ والموسوعة العربية ٩٢٦ وميزان الاعتدال ٤: ٧٨ ولسان الميزان ٦: ٤ وإنباء الرواة ٣: ٢٦٥ وتاج التراجم ٧١ والمنتظم ١٠: ١١٢ وتاج العروس ٣: ٢٤٢ والعبر ٤: ١٠٦ والشذرات ٤: ١١٨ والنجوم الزاهرة ٥: ٢٧٤ والجواهر المضية ٢: ١٦٠ وروضات الجنات ٢١١ وهدية العارفين ٢: ٤٠٢ ومفتاح السعادة ٢: ٩٧ ومرآة الجنان ٣: ٢٦٩ والبداية والنهاية ١٢: ٢١٩ ومنهج الزمخشري في تفسير القرآن لمصطفى الصاوي. ومعجم المطبوعات ٩٥٣ والأعلام ٨: ٥٥ وكنوز الأجداد ٢٩١ ومعجم المؤلفين ١٢: ١٨٦ ووفيات الأعيان ٥: ١٦٨ وطبقات المعتزلة ٢٠. والتفسير والمفسرون ١: ٤٢٩.','2','666',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1213,'الزنجاني [١٣٠٩ - ١٣٧٥ هـ / ١٨٩٢ - ١٩٥٦ م]

<span data-type="title" id=toc-1680>محمود بن أبي الفضائل بن عبد الواسع الحسيني الزنجاني:</span>
فقيه، متكلم، مفسر، من كتبه "حاشية" على تفسير ملا صدرا (١).

الإفسنجي [٦٢٧ - ٦٧١ هـ / ١٢٣٠ - ١٢٧٢ م]

<span data-type="title" id=toc-1681>محمود بن محمد بن داود الإفسنجي اللؤلؤي البخاري، أبو المحامد:</span>
فقيه حنفي، حافظ، مفسر، واعظ، متكلم ولد ببخارى. وتفقه على الإمام محمد بن أحمد القرشي، وسمع من محمد بن أبي جعفر الترمذي. توفي شهيدا في وقعة التتار ببخارى (٢).

محمود حمزة [١٢٣٦ - ١٣٠٥ هـ / ١٨٢١ - ١٨٨٧ م]

<span data-type="title" id=toc-1682>محمود بن محمد نسيب بن حسين بن يحيى حمزة الحسيني الحمزاوي:</span>
فقيه
_________
(١) معجم المؤلفين ١٢: ١٨٩.
(٢) طبقات المفسرين للسيوطي ٤١ وطبقات المفسرين للداودي ٢: ٣١٧ والفوائد البهية ٢١٠ والجواهر المضية ٢: ١٦١ وتاج التراجم ٧٢ وهدية العارفين ٢: ٤٠٥ والأعلام ٨: ٦٠ ومعجم المؤلفين ١٢: ١٩٥.','2','667',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1214,'حنفي، مفسر، أصولي، متكلم، مشارك في بعض العلوم. أصله من حران، ومولده ونشأته ووفاته في دمشق تقلب في مناصب شرعية انتهت به إلى فتوى الشام سنة ١٢٨٤ هـ. وسافر إلى الآستانة والأناضول، واختير عضوا في مجلس دمشق الكبير. وكان آية الزمان في كتابة الخطوط الدقيقة، فقد كتب سورة الفاتحة على ثلثي حبة أرز، وأولع بالصيد حتى اشتهر بحسن الرماية والتفنن بها. ولما جرت حادثة دمشق سنه ١٨٦٠ حمى كثيرا من المسيحين من الذبح، فعرفت الدول له ذلك، وأهداه نابليون الثالث هدية نفيسة. من كتبه "در الأسرار" طبع، في تفسير القرآن الكريم بالحروف المهملة (١).

الشيرازي [٦٣٤ - ٧١٠ هـ / ١٢٣٦ - ١٣١١ م]

<span data-type="title" id=toc-1683>محمود بن مسعود بن مصلح الفارسي، قطب الدين الشيرازي:</span>
_________
(١) تراجم مشاهير الشرق ٢: ٢٤٠ وتاريخ آداب اللغة العربية ٤: ٢٧٤ وتراجم أعيان دمشق ١٥ وهدية العارفين ٢: ٤٢٠ ومعجم المطبوعات ١٧٠٦ والآداب العربية ٢: ٨٢ ومنتخبات التواريخ لدمشق ٢: ٧٦٨ والمكتبة البلدية، فهرس التفسير ٢٣ ونفحة البسام ١١٨ وفهرس الفهارس ١: ٢٧٧ والأعلام ٨: ٦٤ وإيضاح المكنون ٤٤٣.','2','667',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1215,'قاض، مفسر، مشارك في الفقه والأصول والرياضيات والمنطق والحكمة والطب وغير ذلك. ولد بشيراز، وقرأ على والده، ثم قصد نصير الدين الطوسي وقرأ عليه. ودخل بلاد الروم فولي قضاء سيواس وملطية. وكان يجيد لعب الشطرنج ويديمه، ويضرب بالرباب ويجلس في حلق المساخر. وزار العراق والشام ومصر. ثم سكن تبريز، وتوفي بها. من كتبه "فتح المنان في تفسير القرآن" في نحو ٤٠ مجلدا، منه الجزء الأول مخطوط، ويعرف بتفسير العلامي، و "مشكلات التفاسيىر" مخطوط (١).

الخاني [... - ١٣٥٠ هـ / ... - ١٩٣١ م]

<span data-type="title" id=toc-1684>محيي الدين بن أحمد بن محمد الخاني الدمشقي:</span>
فاضل، من أهل دمشق مولدا ووفاة. نسبته إلى خان شيخون بقرب معرة النعمان. كان مدرسا ابتدائيا، من كتبه حسن البيان في تفسير
_________
(١) البدر الطالع ٢: ٢٩٩ وهدية العارفين ٢: ٤٠٦ والدرر الكامنة ٥: وتاريخ علماء بغداد ٢١٩ وروضات الجنات ٢١٤ وكشف الظنون ٤٥٣ و ١٢٣٥ و ١٦٩٥ والنجوم الزاهرة ٩: ٢١٣ والأعلام ٨: ٦٥ ومعجم المؤلفين ١٢: ٢٠٢.','2','668',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1216,'مفردات من القرآن" طبع (١).

الدهلوي [... - ٧٦٤ هـ / ... - ١٣٦٣ م]

<span data-type="title" id=toc-1685>مخلص بن عبد الله الدهلوي الهندي، حميد الدين:</span>
فقيه حنفي، مفسر، من أهل دهلي بالهند. من كتبه "كشف الكشاف" في تفسير القرآن (٢).

البخاري [١٠٥٠ - ١١٣٢ هـ / ١٦٤٠ - ١٧٢٠ م]

<span data-type="title" id=toc-1686>مراد بن علي بن داود بن كمال الدين ابن صالح بن محمد الحسيني البخاري:</span>
فاضل، مشارك في التفسير والفقه والحديث وغير ذلك. وهو جد آل المرادي الدمشقيين. ولد في سمرقند، وهاجر إلى الهند فأخذ الطريقة النقشبندية، وتصوف، ثم قصد الديار الحجازية للحج وزيارة قبر النبي (صلى الله عليه وسلم) وجاور ثلاث سنين، ثم رحل إلى العراق وبلاد العجم، وأقام ببغداد مدة، ثم رحل إلى مكة ثانيا، فمصر، فدمشق وسكنها بعد
_________
(١) تراجم أعيان دمشق ١٢٣ والأعلام ٨: ٦٨ ومعجم المؤلفين ١٢: ٢٠٦.
(٢) نزهة النواظر ١٦١ ومعجم المؤلفين ٢: ٢١١.','2','668',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1217,'سنة ١٠٨٠ هـ. وسافر سنة ١٠٩٢ هـ إلى القسطنطينية، فمكث خمس سنين، وأقطعه السلطان مصطفى خان بعض القرى بدمشق. ينسب إليه، المدرسة المرادية والمدرسة النقشبندية اليرانية ومسجدها بدمشق. توفي بالقسطنطينية. من كتبه "المفردات القرآنية" في تفسير الآيات، مجلدان باللغات العربية والفارسية والتركية (١).

الكرمي [... - ١٠٣٣ هـ / ... - ١٦٢٤ م]

<span data-type="title" id=toc-1687>مرعي بن يوسف بن أبي بكر بن أحمد الكرمي المقدسي الحنبلي:</span>
مؤرخ، أديب، عارف بالتفسير والحديث، من كبار الفقهاء. ولد في طول كرم بفلسطين. وانتقل إلى القدس، ثم إلى القاهرة، فكان أحد أكابر علماء الحنابلة فيها، وتوفي بها. من نصانيفه الكثيرة "الكلمات السنيات" مخطوط، فى التفسير، و "قلائد المرجان في الناسخ والمنسوخ من القرآن" مخطوط. و "البرهان في تفسير القرآن" و "إتحاف
_________
(١) سلك الدرر ٤: ١٢٩ وهدية العارفين ٢: ٤٢٤ وبروكلمان ٢: ٣٤٤ والذيل ٢: ٦٦٣ وهو فيه محمد مراد.','2','669',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1218,'ذوي الألباب - في قوله تعالى: {يمحو الله ما يشاء ويثبت وعنده أم الكتاب} و "الآيات المحكمات والمتشابهات" و "فتح المنان بتفسير آية الامتنان" و "قلائد العقيان في قوله تعالى: {إن الله يأمر بالعدل والإحسان} (١).

مرة الطيب [نحو ... - ٩٠ هـ / نحو ... - ٧٠٩ م]

<span data-type="title" id=toc-1688>مرة بن شراحيل الهمذاني الكوفي، ويقال له مرة الطيب ومرة الخير:</span>
تابعي، محدث ثقة، مفسر، روى عن أبي بكر وعمر وعلي، رضي الله عنهم، قال الذهبي: وثقة يحيى بن معين، ويقال إنه سجد حتى أكل التراب جبهته، وكان بصيرا بالتفسير (٢).

مسروق ابن الأجذع [... - نحو ٦٣ هـ / ... - نحو ٦٨٣ م]

<span data-type="title" id=toc-1689>مسروق بن الأجدع بن مالك بن أمية الهمذاني الوادعا الكوفي، أبو عائشة:</span>
_________
(١) خلاصة الأثر ٤: ٣٥٨ وهدية العارفين ٢: ٤٢٦ ومعجم المؤلفين ١٢: ٢١٨.
(٢) طبقات المفسرين للداودي ٢: ٣١٧ وتذكرة الحفاط ٦٧ والطبقات الكبرى لابن سعد ٦: ١١٦ والتاريخ الكبير للبخاري ٨: ٥ وخلاصة تهذيب الكمال ٣١٨.','2','669',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1219,'إمام في التفسير، عالم خبير بمعاني القرآن، محدث، فقيه، من أشهر رجال مدرسة التفسير بالعراق. من أهل اليمن، كان أبوه فارسها في زمانه. قدم مسروق المدينة في أيام أبي بكر الصديق، وروى عن الخلفاء الأربعة، وابن مسعود، وأبي ابن كعب، وغيرهم. ثم قدم العراق وسكن الكوفة، وشهد حروب علي بن أبي طالب. وكان شريح القاضي يستشيره في معضلات المسائل. قال علي ابن المديني: "ما أقدم على مسروق من أصحاب عبد الله (بن مسعود) أحدا. وقال ابن معين: "ثقة، لا يسأل عن مثله " وذكره ابن حبان في الثقات، وأخرج له الستة (١).

البيهقي [... - ٥٤٤ هـ / ... - ١١٤٩ م]

<span data-type="title" id=toc-1690>مسعود بن علي بن أحمد بن العباس الصواني البيهقي، أبو المحاسن، فخر الزمان:</span>
عالم بالأدب، شاعر، مفسر، أصولي. من آثاره "تفسير القرآن"
_________
(١) تهذيب التهذيب ١٠: ١٠٩ والإصابة الترجمة ٨٤٠٨ والإكليل ١٠: ٧٧ وتذكرة الحفاظ ٤٩ وتاريخ بغداد ١٣: ٢٣٢ وعلم التفسير ٣٩ وكشف الظنون ٤٣٠ والتفسير والمفسرون ١: ١١٩.','2','670',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1220,'مخطوط، الخامس منه، في صوفية (١).

التفتازاني [٧١٢ - ٧٩٣ هـ / ١٣١٢ - ١٣٩٠ م]

<span data-type="title" id=toc-1691>مسعود بن عمر بن عبد الله التفتازاني، سعد الدين:</span>
عالم بالعربية والبيان والمنطق وغير ذلك، ولد بتفتازان (من بلاد خراسان) وأخذ عن القطب والعضد، وانتفع الناس بتصانيفه. وأقام بسرخس، وأبعده تيمورلنك إلى سمرقند، فتوفي فيها، ودفن في سرخس. قال ابن حجر: "انتهت إليه معرفة علوم البلاغة والمعقول بالمشرق بل بسائر الأمصار، لم يكن له نظير في معرفة هذه العلوم، من كتبه "حاشية" على الكشاف، في التفسير للزمخشري. قال في كشف الظنون: "وهي ملخصة من حاشية الطيبي مع زيادة تعقيد في العبارة ولم يتمها، وصل فيها إلى سورة الفتح، وفرغ منها سنة ٧٨٩ هـ " (٢).
_________
(١) إرشاد الأريب ٧: ١٥٩ ودار الكتب الشعبية ٤٩ وبغية الوعاة.
(٢) طبقات المفسرين للداودي ٢: ٣١٩ وإنباء الغمر ١: ٣٨٩ وهو فيه محمورد بن عمر، ومثله في الدرر الكامنة ٥: ١٠٠ ولكن ابن حجر عاد فترجم له (٥: ١١٩) باسم مسعود بن عمر. ومفتاح السعادة ١: ٢٠٥ والشذرات ٦: ٣١٩ وبغية الوعاة ٢: ٢٨٥ وهدية =','2','670',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1221,'ابن ماشاذه [... - ٥٧٦ هـ / ... - ١١٨١ م]

<span data-type="title" id=toc-1692>مسعود بن محمد بن أحمد بن عبد المنعم بن ماشاذه، أبو عبد الله الأصبهاني:</span>
فقيه، مفسر، واعظ، سمع من البرجي والصيرفي وغيرهما. وقدم بغداد حاجا، وأدرك ولاية المستضيء، ودخل على الناصر وبايعه. توفي بأصبهان بعد عوده من الحج بقليل. قال ابن النجار: كان إماما حافظا بالفقه والخلاف والتفسير والوعظ، حدث ببغداد، ووعظ، ولقي القبول التام". له "كتاب في التفسير" (١).

النيسابوري [٥٠٥ - ٥٧٨ هـ / ١١١٢ - ١١٨٢ م]

<span data-type="title" id=toc-1693>مسعود بن محمد بن مسعود الطريثيثي النيسابوري، أبو المعالي، قطب الدين:</span>
فقيه شافعي، مفسر، أصولي، أديب، ولد بنيسابور، وتعلم بها وبمرو. ودرس بنظامية نيسابور، ثم قدم بغداد، فدمشق، (سنة ٥٤٠ هـ) وسكنها مدة،
_________
= العارفين ٢: ٤٢٩ وكشف الظنون ١٤٧٨ وروضات الجنات ٣٠٩ والبدر الطالع ٢: ٣٠٣ والأعلام ٨:
(١) طبقات المفسرين للسيوطي ٤٢ وطبقات المفسرين للداودي ٢: ٣٢١ وهو فيه مسعود بن محمود. ومعجم المؤلفين ١٢: ٣٢٩.','2','671',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1222,'ودرس بالمجاهدية والغزالية. ثم خرج إلى حلب ودرس بها، ومنها سافر إلى بغداد، فهمذان، وأقام بها مدة، ثم عاد إلى دمشق ودرس بها. واتصل بالسلطان صلاح الدين الأيوبي وصنف له "عقيدة" كان السلطان يقرئها أولاده الصغار. قال في طبقات المفسرين (للداودي): "كان إماما في المذهب والخلاف والأصول والتفسير والوعظ، أديبا مناظرا". وتوفي بدمشق (١).

ابن أبي الخير [... - ٦٧٨ هـ / ... - ١٢٧٩ م]

<span data-type="title" id=toc-1694>مسعود بن محمد بن أبي الخير، صفي الدين، أبو الخير:</span>
فقيه، مفسر، مشارك في عدة علوم. له "تهذيب الكشاف" في التفسير (٢).
_________
(١) تذكرة الحفاظ ١٣٤١ والدارس ١: ٢٢٧ والنجوم الزاهرة ٦: ٩٤ والعبر ٤: ٢٣٥ وشذرات الذهب ٤: ٢٦٣ والبداية والنهاية ١٢: ٣١٢ ومرآة الجنان ٣: ٤١٣، وهدية العارفين ٣: ٤٢٩ وطبقات السبكي ٧: ٢٩٧ ووفيات الأعيان ٥: ١٩٦، ومرآة الزمان ٨: ٣٧٢، وطبقات المفسرين للداودي ٢: ٣١٩، ومعجم المؤلفين ١٢: ٢٣٠، والأعلام ٧: ٢٢٠، ومنتخبات من كتب التاريخ ٢٨٢.
(٢) معجم المؤلفين ١٢: ٢٣٠ وشد الإزار: ٤٣٠.','2','671',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1223,'الفالي [... - ٦٧٨ هـ / ... - ١٢٧٩ م]

<span data-type="title" id=toc-1695>مسعود بن محمود الفالي، صفي الدين:</span>
مفسر، من أهل فال - بين شيراز وهرمز -. ذكره الذهبي وقال: العلامة المفسر" ولم يزد (١). الشيرازي [... - ٧٢٥ هـ ... .. - ١٣٢٥ م]

<span data-type="title" id=toc-1696>مسعود بن المظفر بن محمد الشيرازي، جلال الدين أبو الميامن:</span>
مفسر، مشارك في بعض العلوم، من أهل شيراز. ذكرء صاحب شد الإزار (٢).

الزنجي [١٠٠ - ١٧٩ هـ / ٧١٨ - ٧٩٥ م]

<span data-type="title" id=toc-1697>مسلم بن خالد بن مسلم بن سعيد القرشي المخزومي، مولاهم، المعروف بالزنجي:</span>
إمام أهل مكة، من كبار الفقهاء، أصله من الشام. روى عن هشام بن عروة وابن جريج وغيرهما. وبه تفقه الإمام الشافعي قبل أن يلقى مالكا. عد من المحدثين الضعفاء. قال سويد:
_________
(١) طبقات المفسرين ٢: ٣٢١ والمشتبه ٢: ٤٩٦ وتبصير المنتبه ٣: ١١٤٩.
(٢) شد الإزار ٨٩ ومعجم المؤلفين ١٢: ٢٣١.','2','672',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1224,'لقب بالزنجي لسواده، وفي اللباب: لقب بالزنجي على الضد لبياضه. له " تفسير"مخطوط (١).

البصري [القرن الثالث الهجري / القرن التاسع الميلادي]

<span data-type="title" id=toc-1698>مسلم بن سفيان البصري:</span>
مفسر، مقرىء، من أهل البصرة. روى القراءة عن يعقوب بن اسحاق بن زيد الحضرمي البصري، أحد القراء العشرة، المتوفى
سنة ٢٠٥ هـ = ٨٢١ م. روى القراءة
عنه ابنه الحسن. ذكره ابن الجزري وقال: المفسر الضرير، ولم يؤرخ وفاته (٢).

النفيعى [... - بعد ٦٠٠ هـ / ... - بعد ١٢٠٤ م]

<span data-type="title" id=toc-1699>مسلم بن سلامة بن شبيب النفيعي السنجاري:</span>
قال ياقوت: كان ضريرا
_________
(١) تذكرة الحفاظ ٢٥٥ وفيه: مات سنة ١٨٠ وله ثمانون سنة". ومثله في غاية النهاية ٢: ٢٩٧ وسماه مسلم بن خالد بن سعد بن جرجة بن خالد". والتاريخ الكبير للبخاري ٤/ ١: ٢٦٠ واللباب ٢: ٧٧ والمعارف ٢٥٥ و ٢٩٠ وطبقات الفقهاء ٤٨ والأعلام ٨: ١١٨ والكامل لابن الأثير ٦: ٤٥ و ١٤٧ وفي وفاته سنة ١٧٩ هـ. وشذرات الذهب ١: ٢٩٤ وفيه وفاته سنة ١٨٠ هـ وله ثمانون سنة (وهو ينقل عن العبر للذهبي).
(٢) طبقات المفسرين للداودي وغاية النهاية ٢: ٢٩٨.','2','672',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1225,'فقيها، أديبا له معرفة تامة بالتفسير، قدم حلب مع أخيه مسلم، ومات بها أظن بعد الستمائة". وفي "تاج التراجم" نقلا عن ابن العديم أنه مات قبل الثمانمائة. والصواب ما ذكره ياقوت، لأن وفاته - أي وفاة ياقوت - كانت سنة ٦٢٦ هـ = ١٢٢٩ م، وصاحب الترجمة من معاصريه (١).

التميمي [... - ١٨٦ هـ / ... - ٨٠٢ م]

<span data-type="title" id=toc-1700>المسيب بن شريك، أبو سعيد، التميمي الشقري:</span>
محدث، من أهل الكوفة، روى عن الأعمش. أجمع أهل العلم على ترك حديثه. قال صاحب كشف الظنون: له "تفسير" ذكره الثعلبي في تفسيره" (٢).
_________
(١) معجم البلدان (النفعية) وتاج التراجم ٧٧ وهو فيه النقيعي (بالقات) خطأ، وطبقات المفسرين للداودي ٢: ٢٢٢ والجواهر المضية ٢: ١٧٣.
(٢) لسان الميزان ٦: ٣٨ والتاريخ الكبير للبخاري ٧: ٤٠٨ وكشف الظنون ٤٥٨ وفي اللسان أقوال كثيرة لعلماء الحديث تقرر أنه كان رديء الحفظ، كذابا، لا يكتب حديثه.','2','673',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1226,'ابن التمجيد [... - نحو ٨٨٠ هـ / ... - نحو ١٤٧٥ م]

<span data-type="title" id=toc-1701>مصطفى بن إبراهيم الرومي، مصلح الدين، المعروف بابن التمجيد:</span>
مفسر، من علماء الدولة العثمانية. كان معلما للسلطان محمد خان الفاتح، المتوفى سنة ٨٨٦ هـ عن ٥٣ عاما. له "حاشية على تفسير البيضاوي" طبعت بهامش حاشية القونوي على تفسيرالكشاف سنة ١٢٨٥ هـ بالآستانة. قال في كشف الظنون: وهي (الحاشية) مفيدة جامعة لخصها من حواشي الكشثاف .. " قال آغا بزرك: نسختان منها في مكتبة مدرسة شبهسالار بطهران (١).

نور الدين [... - ٩٨١ هـ / ... - ١٥٧٣ م]

<span data-type="title" id=toc-1702>مصطفى بن أحمد الفلبه وي القسطنطيني الرومي، مصلح الدين، الشهير بنور الدين زاده:</span>
مفسر،
_________
(١) هدية العارفين ٢: ٣٣، وفيه وفاته في حدود ٨٤٢ هـ وكشف الظنون ١٨٨ ومعجم المؤلفين ١٢: ٢٣٦ ومعجم المطبوعات ٥٣ وفيه: نبغ في حدود سنة ٨٨٠ هـ والأزهرية ١: ٢٥٣ وفيها وفاته بعد سنة ٨٨٠ هـ. والشقائق النعمانية ٦٢ ولم يذكر تاريخ وفاته. وتعليقات آغا بزرك: ٢٠.','2','673',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1227,'صوفي، تولى مشيخة الزاوية بأيا صوفية. من آثاره "تفسير القرآن" إلى سورة الأنعام (١).

غزي زاده [... - ١٢٠٤ هـ / ... - ١٧٩٠ م]

<span data-type="title" id=toc-1703>مصطفى بن أحمد البرسوي، المعروف بغزي زاده، والملقب بنسيب:</span>
شاعر تركي، مستعرب، له تصانيف بالعربية والتركية، فمن العربية "حاشية" على أنوار التنزيل في التفسير، للبيضاوي، سماها، تزيين المقامات" (٢).

مصطفى الحكيم [... - ١٣٤١ هـ / ... - ١٩٢٢ م]

<span data-type="title" id=toc-1704>مصطفى بن أحمد الحكيم:</span>
باحث مصري، من فقهاء الشافعية، أزهري، له اشتغال بالتفسير، من آثاره "حاشية" على تفسير النسفي لسورة مريم وبعض سورة "طه" مخطوط، و "مقدمة لعلم التفسير" (٣).
_________
(١) هدية العارفين ٢: ٤٣٥ وكشف الظنون ٤٦٠ ومعجم المؤلفين ١٢: ٢٣٩.
(٢) هدية العارفين ٢: ٤٥٢.
(٣) ملحق الجزء الأول من فهرس دار الكتب المصرية ٤، ٨، ٢٩، ٢٠، ٢١، و ٦: ١٦٣ ومعجم المؤلفين ١٢: ٢٣٨','2','674',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1228,'الفيلورنوي [... - ١٢٤٤ هـ / ... - ١٨٢٨ م]

<span data-type="title" id=toc-1705>مصطفى بن إسماعيل الفيلورنوي، ويعرف بالمنطقي:</span>
باحث، من فضلاء الروم، مشارك في التفسير والمنطق، وغيرهما. من أهل فيلورنة بجوار مناستر تولى التدريس، وولي الإفتاء في مناستر، وعاد إلى بلده بعد فتنة. وركبته الديون فقام برحلة، ثم عاد وتوفي في فيلورنة. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي، مخطوط، من سورة النبأ إلى آخر القرآن، كما ذكر في مقدمة كتابه زبدة الحقائق (١).

العمادي [... - ١٠٢٢ هـ / ... - ١٦١٣ م]

<span data-type="title" id=toc-1706>مصطفى بن جعفر العمادي الرومي، الملقب بصنع الله:</span>
فقيه حنفي، من علماء الدولة العثمانية، ولي الإفتاء ومشيخة الإسلام. من آثاره "حاشية" على أوائل الكشاف، في التفسير، للزمخشري (٢).
_________
(١) الأعلام ٨: ٣٠ وهدية العارفين ٢: ٤٥٦ ومعجم المؤلفين ١٢: ٢٤٢ وعثمانلي مؤلفلري ٢: ٣٦.
(٢) هدية العارفين ٢: ٤٣٩ ومعجم المؤلفين ١٢: ٢٤٥.','2','674',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1229,'السباعي [١٣٢٣ - ١٣٨٤ هـ / ١٩١٥ - ١٩٦٧ م]

<span data-type="title" id=toc-1707>مصطفى بن حسني، أبو حسان، السباعي:</span>
باحث في الشريعة، كاتب، خطيب، مجاهد، من رجال النهضة الإسلامية الحديثة. ولد في مدينة حمص بسورية وتعلم بها وبالأزهر الشريف بالقاهرة. اعتقله الانجليز في مصر وفلسطين - في أيام احتلالهم القطرين العربيين - ستة أشهر، ثم أسلموه إلى الفرنسيين - المنتدين على سورية ولبنان - فسجنوه في لبنان ٣٠ شهرا. وأطلق سراحه، ونشب القتال بين العرب واليهود في فلسطين سنة ١٩٤٨، فقاد كتيبة من مجاهدي الإخوان المسلمين للدفاع عن بيت المقدس. وبعد النكبة، نال شهادة "الدكتوراه" من الأزهر (١٩٤٩) بكتاب (السنة ومكانتها فى التشريع الإسلامي". وعاد إلى دمشق، فاختير مراقبا عاما لجمعية الإخوان المسلمين، وعين أستاذا بكلية الحقوق (١٩٥٠) فعميدا لكلية الشريعة (١٩٥٥). وأصيب بشلل نصفي سنة ١٩٥٧، وهو منشىء مجلة "حضارة الإسلام" الشهرية المعروفة في العالمين العربي والإسلامي. من تآليفه الكثيرة','2','675',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1230,'"مجموعة" في التفسير فسر فيها سورا من القرآن الكريم منها سورة طه، والحجرات، والحشر، والبروج، وتبارك، والشورى، وقد شاركه في تفسير شيء منها بعض زملائه من المجاهدين المؤمنين الصابرين. ويعمل الأستاذ الشيخ زهير الشاويش الآن على تجميع ما للأستاذ السباعي من تفسير ليخرجه مفردا، وقد اطلعني على صفحات من هذه المجموعة بخط الأستاذ السباعي نفسه (١).

الطرسوسي [... - ٨٧١ هـ / ... - ١٤٦٧ م]

<span data-type="title" id=toc-1708>مصطفى بن حمزة بن محمد بن إبراهيم الطرسوسي، أبو الميامن:</span>
فقيه حنفي، مفسر، من أهل طرسوس (في جنوبي تركيا الآسيوية). من آثاره "مستراض الأنوار ومستفاض الأسرار" حاشية على أنوار التنزيل، في التفسير، للبيضاوي (٢).
_________
(١) معلومات أمدني بها العالم الجليل الشيخ زهير الشاويش. وانظر من هو في سورية ٣٥٢ والأعلام (ط ٤) ٧: ٢٣٢ ومجلة حضارة الإسلام س ٥ ع جمادي الآ خرة، رجب، شعبان ١٣٨٤ (١٩٦٤).
(٢) هدية العارفين ٢: ٤٣٣ ومعجم المؤلفين ١٢: ٢٤٩.','2','675',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1231,'الذهبي [... - ١٢٨٠ هـ / ... - ١٨٦٣ م]

<span data-type="title" id=toc-1709>مصطفى بن حنفي بن حسن الذهبي:</span>
فقيه شافعي، مفسر، مشارك في بعض العلوم. مولده ووفاته بمصر. أخذ عن الدمنهوري والفضل الفضالي والقويني وغيرهم، حتى برع واشتهر، ثم تصدر للإقراء والتدريس إلى أن توفي، من تصانيفه "تفسير غريب القرآن العظيم" رسالة، طبعت بمصر (دون تاريخ) (١).

الدوماني [... - حيا ١١٨٧ هـ / ... - حيا ١٧٧٣ م]

<span data-type="title" id=toc-1710>مصطفى بن ... الدوماني:</span>
فقيه حنبلي، مفسر، مشارك في عدة علوم. ولد في دوما، من أقضية دمشق، ونشأ في صالحية دمشق. قال الحصني: أخذ عن علماء عصره، وكان آية باهرة في العلوم والفنون، اشتهر أمره وعلا قدره، ورحل إلى مصر وولي مشيخة الرواق في الأزهر، ثم رحل إلى الآستانة ومات بها. من آثاره "ضوء النيرين لفهم تفسير الجلالين" مجلدان (٢).
_________
(١) معجم المطبوعات ١: ٩١٢ وتفسير غريب القرآن له، ٢٤٩، والأعلام ٨: ١٣٣ ومعجم المؤلفين ١٢: ومنجد الأعلام ٢٩٩.
(٢) منتخبات التواريخ لدمشق ٢: ٦٧٧ ومختصر طبقات الحنابلة. ومعجم المؤلفين ١٢: ٢٥١.','2','676',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1232,'سروري [٨٩٧ - ٩٦٩ هـ / ١٤٩٢ - ١٥٦٢ م]

<span data-type="title" id=toc-1711>مصطفى بن شعبان الرومي، مصلح الدين، المعروف بسروري:</span>
مفسر، محدث، من فقهاء الحنفية، له مشاركة في عدة علوم، عارف باللغات العربية والتركية والفارسية. ولد بقصبة "كليبولي" - على الدردنيل - وأخذ عن طاشكبري زاده وغيره. وناب في القضاء، ودرس بالقسطنطينية وغيرها. وتوفي ودفن عند مسجده بقصبة قاسم باشا بالقسطنطينية. من آثاره "الحواشي الكبرى" و "الحواشي الصغرى" كلاهما على تفسير البيضاوي، قال صاحب "العقد المنظوم"، وأول الكبرى: الحمد لله الذي جعلني كشاف القرآن، وصيرني قاضيا بين الحق والبطلان .. " وله أيضا تفسير سورة "يوسف" قال حاجي خليفة: "وهو أبسط من جميع التفاسير، أوله "الحمد لله الذي أنزل إلينا .. الخ، فرغ منه في رجب سنة ٩٥٤ هـ " (١).
_________
(١) العقد المنظم ٣٤٣ والشذرات ٨: ٣٥٦ وهدية العارفين ٢: ٤٣٤ وعثمانلي مؤلفلري ٢: ٢٢٥ وكشف الظنون ١٨٩ و ٤٥١ وعطائي ١: ٢٣ والأعلام ٨: ١٣٦ ومعجم المؤلفين ١٢: ٢٥٦.','2','676',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1233,'الرافعي [١٢٩٧ - ١٣٥٦ هـ / ١٨٨٠ - ١٩٣٧ م]

<span data-type="title" id=toc-1712>مصطفى صادق بن عبد الرزاق بن سعيد بن أحمد بن عبد القادر الرافعي:</span>
عالم بالأدب، من أئمة اللغة والبيان الرفيع في النهضة الأدبية الحديثة، شاعر، ناقد لاذع، من كبار الكتاب، إمتاز أسلوبه بفخامة تراكيبه وجودة معانيه وبعد مراميه. ولد في بهتيم من قرى القليوبية بمصر، من عائلة لبنانية الأصل، فنشأ نشأة دينية في أسرة اشتهرت في الحفاظ على التقاليد الإسلامية. وبدأ حياته الأدبية شاعرا في مدرسة البارودي، فأخرج عدة دواوين ظهر أولها سنة ١٩٠٢ م. وعين كاتبا في محكمة طنطا الأهلية، وأصيب بصمم، فكان يكتب له ما يراد مخاطبته به. وانتخب عضوا بالمجمع العلمي العربي بدمشق. وصفه المنفلوطي بأنه من شعراء المعاني كأبي تمام. تحول في الشطر الثاني من حياته إلى النثر، فكتب عدة كتب من نوع النثر الشعري. قال الزركلي: "شعره نقي الديباجة على جفاف في أكثره، ونثره من الطراز الأول". وقال طه حسين: "إن أسلوب الرافعي قديم جدا لا يلائم العصر الذي نعيشه". من آثاره','2','677',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1234,'المتعلقة بتفسير كتاب الله العزيز "إعجاز القرآن والبلاغة النبوية" وهو الجزء الثاني من كتابه تاريخ آداب العرب طبع (١).

بركوي زاده [... - ٩١٩ هـ / ... - ١٥١٣ م]

<span data-type="title" id=toc-1713>مصطفى بن عبد الله الرومي مصلح الدين، الشهير ببركوي زاده:</span>
قاض، فقيه حنفي، مفسر تركي، ولي القضاء بأدرنة. من آثاره "تفسير" آية الحج و "تفسير سورة القدر" (٢).

داماد واني [... - ١٠٩٨ هـ / ... - ١٦٨٣ م]

<span data-type="title" id=toc-1714>مصطفى بن عبد الله الكردي، الشهير بداماد واني:</span>
مدرس، له اشتغال
_________
(١) حياة الرافعي لمحمد سعيد العريان، وحديث الأربعاء ٣: ١١ و ٢٤ وأعلام الأدب والفن ٢: ٤١٤ والأدباء الخمس لإسماعيل عبد الحميد، وشعراء العصر ١: ٢١٣ وشعراء الشام والعراق ومصر ٢٦٣ وتاريخ الأدب العربي الحديث للصفوف المتوسطة ١٩٣ وتاريخ الأدب العربي لفاخوري وفي أوقات الفراغ ٢٠٢ ومعجم المطبوعات ٩٢٦ والموسوعة العربية الميسرة ٨٥٥ ومعجم المؤلفين ١٢: ٢٥٦ ودراسة في أدب الرافعي لنعمات أحمد فؤاد، ومجلة الرسالة ٥: ٨٢١، ٨٤١، ٨٦١، ٨٦٣، ومصادر الدراسة الأدبية ٢: ٣٧٥ وانظر حاشيته. والأعلام ٧: ٣٥.
(٢) هدية العارفين ٢: ٤٣٣.','2','677',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1235,'بالتفسير، دخل القسطنطينية، ثم درس في المدرسة السلطانية بمدينة بروسة. وتوفي بها. من آثاره "حاشية" على أنوار التنزيل في التفسير، للبيضاوي (١).

القسطلاني [... - ٩٠١ هـ / ... - ١٤٦٠ م]

<span data-type="title" id=toc-1715>مصطفى بن محمد القسطلاني الرومي، مصلح الدين، ويعرف بالكستلي:</span>
قاض، فقيه حنفى، مدرس، شارك في بعض العلوم. درس في بعض المدارس، ثم ولي قضاء بروسة ثلاث مرات، ثم قضاء أدرنة، فقضاء القسطنطينية. وعزل، وعين له كل يوم مائة درهم. وتوفى بالقسطنطينية ودفن بجوار أبي أيوب الأنصاري. له كتب منها "رسالة" في تفسير قوله تعالى: {فسحقا لأصحاب السعير ..} (سورة الملك: ١١) (٢)

بستان [٩٠٤ - ٩٧٧ هـ / ١٤٩٩ - ١٥٧٠ م]

<span data-type="title" id=toc-1716>مصطفى بن محمد علي الآيديني (التيروي الرومي، المعروف ببستان أفندي)</span>
_________
(١) هدية العارفين ٢: ٤٤٢.
(٢) شذرات الذهب ٨: ١١ والكواكب السائرة ١: ٣٠٦ والبدر الطالع ٢: ٣٠٨ وهدية العارفين ٢: ٤٢٢ وكشف الظنون ٨٥٤ ومعجم المؤلفين ١٢: ٢٨٢.','2','678',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1236,'التيروي الرومي، المعروف ببستان أفندي: قاض، فقيه حنفي، مفسر، من مستعربي الترك، ولد في قصبة "تيرا" وولي قضاء بروسة، وأدرنة، والقسطنطينية، وعسكر الأناضول، والروم ايلى، واستمر في القضاء الأخير خمس سنين، ثم عزل. من آثاره "تفسير سورة الأنعام" في مجلد، وهو حاشية على تفسير البيضاوي لهذه السورة (١).

الواعظ [١٢٦٣ - ١٣٣١ هـ / ١٨٤٧ - ١٩١٣ م]

<span data-type="title" id=toc-1717>مصطفى بن محمد بن محمد بن جعفر الأدهمي الحسيني، أبو إسماعيل الواعظ، ويسمى مصطفى نور الدين:</span>
مؤرخ، فقيه حنفي، من أعيان بغداد، مولده ووفاته فيها. تقلب في مناصب متعددة، منها الإفتاء بالحلة والديوانية، وانتخب نائبا عن بغداد في مجلس "المبعوثان" العثماني. من كتبه "بلوغ النيل" في الكلام على قوله تعالى {وأتموا الصيام إلى الليل} (البقرة ١٧٨) و "تفسير مفردات القرآن" مخطوط (٢).
_________
(١) هدية العارفين ٢: ٤٣٥ وكشف الظنون ١٩١ و ٤٥٠، والشذرات ٨: ٣٨٥ والعقد المنظوم ٣٩٥ والأعلام ٨: ١٤٢ ومعجم المؤلفين ١٢: ٢٨٠.
(٢) البغداديون أخبارهم ومجالسهم ٢٦٨ ولب الألباب ٢٣٣ والروض الأزهر ١٥٨ وهدية العارفين ٢: ٤٦١ والأعلام ٨: ١٤٦ ومعجم المؤلفين ١٢: ٢٨٤.','2','678',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1237,'نجا [١٢٦٩ - ١٣٥٠ هـ / ١٨٥٣ - ١٩٣٢ م]

<span data-type="title" id=toc-1718>مصطفى بن محيي الدين بن مصطفى ابن محمد عبد القادر نجا:</span>
من كبار علماء الدين، ومفتي بيروت من سنة ١٣٢٧ إلى أن توفي، مولده ووفاته فيها. قرأ على يوسف الأسير وإبراهيم الأحدب وغيرهما. وأذن له بالإرشاد في بيروت، وترأس لجنة مدرسة ثمرة الإحسان، وجمعية المقاصد الخيرية الإسلامية. من آثاره "تفسير جزء عم" مخطوط (١).

الأسير [١٢٧٣ - ١٣٣٣ هـ / ١٨٥٦ - ١٩١٥ م]

<span data-type="title" id=toc-1719>مصطفى بن يوسف بن عبد القادر بن محمد الحسيني الأسير:</span>
متأدب، له اشتغال بالتفسير، من أهل بيروت مولدا ووفاة، كان في العهد العثماني من موظفي حكومة بيروت، ثم دمشق. وهو نجل العلامة الشيخ يوسف ووالد الشاعر صلاح. من آثاره "هدية الإخوان في تفسير ما أبهم على العامة من ألفاظ
_________
(١) أعلام الأدب والفن ٢: ٣٤٣ والأعلام الشرقية ٢: ١٨٨ والأعلام ٨: ١٤٧ ومعجم المؤلفين ١٢: ٢٨٧ وتنوير الأذهان ١: ٥١٠ ومعجم أعلام لبنان للمؤلف، مخطوط.','2','679',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1238,'القرآن، طبع في بيروت ١٣٠٧ وبدمشق ١٣٣١ هـ (١).

الضمدي [... - ١٠٤٨ هـ / ... - ١٦٣٩ م]

<span data-type="title" id=toc-1720>المطهر بن علي بن محمد بن علي بن حسن بن إبراهيم الضمدي اليماني، أبو محمد:</span>
مفسر، أديب، نحوي، من فقهاء الزيدية، من أهلا ضمد باليمن. من كتبه "الفرات النمير في تفسير الكتاب المنير" قال في البدر الطالع: "وهو تفسير مفيد جدا مع اختصاره، يدل على قوة ملكة صاحبه في العلوم، ورسوخ قدمه في فنون عدة" وقال المحبي: "وهو أجل مصنفاته أحسن فيه العبارات وجود فيه الرمز والإشارات، وقد حظي باليمن بالقبول عند الفحول ومدحه كثير من علمائه بالأشعار الرائقة والمدائح الفائقة .. (٢) ".
_________
(١) معجم المطبوعات ٤٤٨ واكتفاء القنوع ٤٧٤ والأعلام ٧: ٢٤٧.
(٢) البدر الطالع ٢: ٣١٠ وهدية العارفين ٢: ٤٦٢ وإيضاح المكنون ٢: ١٨١ ومعجم المؤلفين ١٢: ١٦٦: ٢ وذكره باسم مصطفى، ثم صفحة ٢٩٥ باسم مطهر، وخلاصة الأثر ٤: ٤٠٣ وهو فيه مصطفى بن علي بن نعمان، وولادته سنة ١٠٠٤ هـ. ولم يذكر تاريخ وفاته.','2','679',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1239,'الموصلي [٥٥١ - ٦٣٠ هـ / ١١٥٦ - ١٢٣٣ م]

<span data-type="title" id=toc-1721>المعافى بن إسماعيل بن الحسن بن أبي السنان الشيباني الموصلي، أبو محمد، جمال الدين ويعرف أيضا بابن الحدوس:</span>
فقيه شافعي، مفسر، عارف بالحديث والأدب. من أهل الموصل مولدا ووفاة. أخذ عن علمائها وحدث وأفتى وناظر. من كتبه "نهاية البيان في تفسير القرآن" مخطوط، خمسة أجزاء (١).

ابن طرار [٣٠٣ - ٣٩٠ هـ / ٩١٦ - ١٠٠٠ م]

<span data-type="title" id=toc-1722>المعافى بن زكريا بن يحيى بن حميد بن حماد بن داود الجريري النهرواني، أبو الفرج ابن طرار:</span>
قاض، فقيه، أديب، نحوي، شاعر، مفسر، مولده ووفاته بالنهروان (في العراق). سمع من البغوي وطبقته، وولي القضاء بباب الطاق ببغداد، نيابة. وقيل له الجريري لأنه كان
_________
(١) تذكرة الحفاظ ١٤٥٦ وطبقات السبكي ٨: ٣٧٤ وهدية العارفين ٣: ٤٦٥ وكشف الظنون ٢٦٣ و ٤٥٩ و ١٩٨٧ وشذرات الذهب ٥: ١٤٣ ومعجم المؤلفين ١٢: ٣٠١ وطبقات المفسرين للداودي ٢: ٢٢٢ والأعلام ٨: ١٦٩.','2','680',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1240,'على مذهب الإمام أبي جعفر محمد بن جرير الطبري. وكان معاصرا لابن النديم صاحب "الفهرست"، وقد ذكره وذكر مصنفاته، وقال " ... وله من الكتب في الفقه وغيره ما أنا ذاكره إلى وقتنا هذا ... " ثم سمى له ١٩ كتابا من بينها كتاب في "تأويل القرآن" ولم يذكر اسمه. وفي "معجم الأدباء" و "بغية الوعاة" و "الشذرات" أن له "تفسير كبير"، قيل: في ستة مجلدات، ولم يذكره البغدادي في هدية العارفين" أو "إيضاح المكنون" ولا حاجي خليفة في "كشف الظنون". كما أغفلت ذكره أكثر المصادر القديمة التي ترجمت لابن طرار. أما الزركلي فقال: "لعله: البيان الموجز عن علوم القرآن المعجز" وهذا أيضا لم يذكره حاجى خليفة ولا البغدادي ولا ابن النديم (١)
_________
(١) طبقات المفسرين للداودي ٢: ٣٢٣ وابن خلكان ٥: ٢٢١ وابن النديم ٢٣٦ وغاية النهاية ٢: ٣٠٢ وبغية الوعاة ٢: ٢٩٣ والنجوم الزاهرة ٤: ٢٠١ وإنباه الرواة ٣: ٢٩٦ ونزهة الألباء ٣٢٩ ومعجم الأدباء ١٩: ١٥١ وتذكرة الحفاظ ١٠١٠ والعبر ٣: ٤٧ وتاريخ بغداد ١٣: ٢٣٠ والكامل في التاريخ ٩: ١٦٣ والمنتظم ٧: ٢١٣ والبداية والنهاية ١١: ٣٢٨ وهدية العارفين ٢: ٤٦٤ ومرآة الجنان ٢: ٤٤٣ والأنساب ١٢٩ ١: ١٠٥١ واللباب ٣: ٢٤٩ والأعلام ٨: ١٦٩ وتاج العروس ٣: ٣٥٩ (طرار). ومعجم المؤلفين ١٢: ٣٠٢ ومجلة المجمع العلمي العربي ٣٠: ٣٨٠.','2','680',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1241,'معمر بن المثنى [١١٠ - ٢٠٩ هـ / ٧٢٨ - ٨٢٤ م]

<span data-type="title" id=toc-1723>معمر بن المثنى التيمي بالولاء، البصري، أبو عبيدة:</span>
عالم باللغة والشعر والأخبار والنسب. كان أصله من باجروان قرب الرقة، ومولده ووفاته في البصرة. استقدمه هارون الرشيد إلى بغداد سنة ١٨٨ هـ، وقرأ عليه أشياء من كتبه. قال الجاحظ: لم يكن في الأرض خارجي أعلم بجميع العلوم منه" وقيل: كان إباضيا، شعوبيا يطعن في الأنساب. قال ابن قتيبة: كان يبغض العرب وصنف في مثالبهم كتبا، ولما مات لم يحضر جنازته أحد. لأنه لم يكن يسلم من لسانه شريف ولا غيره. له نحو ٢٠٠ مؤلف، منها "مجاز القرآن" طبع، جزآن، و "إعراب القرآن" مخطوط و "معاني القرآن" و "غريب القرآن" و "بحار القرآن" (١).
_________
(١) تاريخ بغداد ١٣: ٢٥٢ ونزهة الألباء ١٠٤ وتهذيب الأسماء واللغات ٢: ٢٦٠ وبغية الوعاة ٢: ٢٩٤ والنجوم الزاهرة ٢: ١٨٤ وتهذيب التهذيب ١٠: ٢٤٦ وابن خلكان ٥: ٢٣٥ ومفتاح السعادة ١: ١٠٥ وإنباه الرواة ٣: ٢٧٦ وميزان الاعتدال ٤: ١٥٥، وتذكرة الحفاظ: ٣٧١ وشذرات الذهب ٢: ٢٤ وابن النديم ٥٣ والعبر ١: ٣٥٩ ومرآة الجنان ٢: ٤٤ وطبقات المفسرين للداودي ٢: ٣٢٦ ومعجم الأدباء ١٩: ١٥٤ وأخبار النحويين البصريين ٦٧ وهدية العارفين ٢: ٤٦٦ وكشف الظنون ١٢٠٣ و ١٧٣٠ والأعلام ٨: ١٩١ ونور القبس ١٠٩ ومعجم المؤلفين ١٢: ٣١٠.','2','681',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1242,'الفراهي [... - ٩٠٧ هـ / ... - ١٥٠١ م]

<span data-type="title" id=toc-1724>معين الفراهي:</span>
مفسر، ذكره آغا بزرك في تعليقه على كشف الظنون (ص ٣٢) وقال: له "الواضحة" تفسير الفاتحة.

المفضل بن سلمة [... - نحو ٢٩٠ هـ / ... - ٩٠٣ م]

<span data-type="title" id=toc-1725>المفضل بن سلمة بن عاصم، أبو طالب:</span>
نحوي، لغوي، عالم بالأدب، أخذ عن أبيه وابن الأعرابي وثعلب وابن السكيت وغيرهم من العلماء، واستكثر من الرواية ونقل اللغة. وكان كوفي المذهب، مليح الخط، ومن خاصة الفتح ابن خاقان (ت: ٢٤٧ هـ) وزير المتوكل العباسي. وكان أبوه سلمة صاحب الفراء ووراويته. روى عن المفضل أبو بكر الصولي، وزعم أنه سمع منه سنة ٢٩٠ هـ، وقال: "وكان منزله بباب خراسان .. ". من كتبه "ضياء القلوب" في معاني القرآن، قال ابن خلكان: "نيف وعشرون جزءا" (١).
_________
(١) تاريخ بغداد ١٣: ١٢٤ وبغية الوعاة ٢: ٢٩٦ وطبقات المفسرين للداودي ٢: ٣٢٨ ومراتب النحويين ١٥٧ ونزهة الألباء ٢٠٢ وإنباه الرواة ٣: ٣٠٥ ومعجم =','2','681',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1243,'الشيرازي [... - بعد ١٣١٠ هـ / ... - بعد ١٨٩٢ م]

<span data-type="title" id=toc-1726>مفيد بن محمد بن محمد كاظم بن محمد مفيد الشيرازي، الملقب داور:</span>
فقيه، عارف بالتفسير وغيره، من كتبه "حاشية" على تفسير صافي (١).

الخراز [... - نحو ١٥٠ هـ / ... - ٧٦٧ م]

<span data-type="title" id=toc-1727>مقاتل بن حيان، أبو بسطام البلخي الخراز:</span>
مفسر، حافظ للحديث، مؤرخ، كان عالم خراسان في وقته. حدث عن عكرمة ومجاهد والضحاك وغيرهم. وثقه يحيى بن معين وأبو داود. وقال الذهبي: كان إماما صادقا ناسكا خيرا كبير القدر صاحب سنة وأتباع. هرب في أيام خروج أبي مسلم الخراساني إلى كابل ودعا
_________
= الأدباء ١٩: ١٦٣ ووفيات الأعيان ٤: ٢٠٥ وكشف الطنون ١٠٩١ وتاريخ آداب اللغة العربية ٢: ٢١٧ وطبقات ابن قاضي شهبة ١: ٢٥٤ والمزهر ٢: ٤١٣ ووفيات الأعيان ٤: ٢٠٥ (في ترجمة ابنه أبي الطيب). ومجلة المشرق ٤١: ٣٠١ ومعجم المطبوعات ١٧٧٠ والموسيقى العراقية ٧٣ والفهرست ٨٠ والأعلام ٧: ٢٧٩ ومعجم المؤلفين ١٢: ٣١٤ وهدية العارفين ٢: ٤٩٨. ووفاته في بعض هذه المصادر سنة ٢٩٠ وفي بعضها الآخر سنة ٣٠٠ هـ. كما ذكر له كتاب اسمه "معاني القرآن": قلت. لعله كتاب ضياء القلوب.
(١) هدية العارفين ٢: ٤٦٩.','2','682',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1244,'خلقا إلى الإسلام فأسلموا". وكان معاصرا لمقاتل بن سليمان التالية ترجمته، وقد خلط كثير من المؤرخين بينهما. من آثاره "تفسير القرآن" قال فؤاد سزكن: "وهو أحد مراجع الثعلبي في كتابه "الكشف والبيان" ومن الكتب التي حصل عليها الخطيب البغدادي في دمشق على إجازتها وروايتها. ولقد استخدمه أيضا الطبري في تفسيره وتاريخه (١) ".

مقاتل بن سليمان [... - ١٥٠ هـ / ... - ٧٦٧ م]

<span data-type="title" id=toc-1728>مقاتل بن سليمان بن بشير الأزدي بالولاء، البلخي، أبو الحسن:</span>
مفسر، متكلم، أصله من بلخ، عاش أولا في البصرة، ثم بعد ذلك في بغداد وحدث بها. اختلف العلماء في أمره، فمنهم من وثقه في الرواية، ومنهم من نسبه إلى الكذب. وكان متروك الحديث. قال
_________
(١) تبصير المنتبه ١: ١٦١ والمشتبه ١: ١٦١ الحاشية رقم ٢ وتذكرة الحفاظ: ١: ١٧٤ وخلاصة تهذيب الكمال ٣٣٠ وميزان الاعتدال ٤: ١٧١ وطبقات المفسرين للداودي ٢: ٣٢٩ ولسان الميزان ٦: ٨٢ الترجمة ٢٩٧ والتاريخ الكبير للبخاري ٤ ق ٢: ١٣ والتهذيب لابن حجر ١٠: ٢٧٧ وتاريخ الطبري، انظر فهرسته. وتاريخ التراث العربي ١: ١٩٧ وكشف الظنون ٤٥٩.','2','682',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1245,'الذهبي في "تذكرة الحفاظ" عقب ترجمة مقاتل بن حيان (السابقة ترجمته): فأما مقاتل بن سليمان المفسر فكان في هذا الوقت، وهو متروك الحديث، وقد لطخ بالتجسيم مع أنه كان من أوعية العلم، بحرا في التفسير". وتوفي بالبصرة. من كتبه "تفسير خسمائة آية من القرآن" عن الأوامر والنواهي، مخطوط، و "وجوه حرف القرآن" والنسخة الباقية منه بتنقيح تلميذه أبي نصر بعنوان "الوجوه والنظائر في القرآن" مخطوط، وكتاب "اللغات في القرآن" وكتاب (الجوابات في القرآن" و "الناسخ والمنسوخ" و "نوادر التفسير" و "التفسير الكبير" وهو أقدم تفسير كامل للقرآن وصل إلينا، جمع نسخه من أنحاء العالم، وقام بتحقيقه تمهيدا لنشره الدكتور عبد الله شحاته. قال فؤاد سزكين: "لا يمكن الاعتماد كثيرا على تفسيره، لأنه أطلق العنان لخياله في الشرح، وأيضا لأنه أراد أن يكمل الأساليب الموجزة في القرآن بروايات النصارى واليهود، وزيادة على ذلك فإنه كان يستخدم تفاسير قديمة من غير أن يذكر إسنادا، ومن غير أن يكون له حق الرواية" وقال: وهو - التفسير - في رواية أبي صالح الهذيل بن حبيب الدندني (الذي كان يعيش في سنة ١٩٠','2','683',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1246,'هـ) ولقد أضات هذا - في بعض المواضع - في نص مقاتله من أسانيد من الآخرين (١).

الحلي [... - ٨٢٦ هـ / ... - ١٤٢٣ م]

<span data-type="title" id=toc-1729>المقداد بن عبد الله بن محمد بن الحسين بن محمد السيوري الحلي الأسدي:</span>
فقيه إمامي، أصولي، متكلم، مفسر، من تلاميذ الشهيد الأول محمد بن مكي. توفي بالنجف. من كتبه "كنز العرفان في فقه القرآن طبع فى طهران سنة ١٣٣١ هـ (٢).
_________
(١) طبقات المفسرين للداودي ٢: ٣٣٠ وتاريخ التراث العربي ١: ١٩٨ وطبقات ابن سعد ٧: ٣٧٣ وتاريخ بغداد ٨: ١٦٠ ووفيات الأعيان ٥: ٢٥٥ وخلاصة تهذيب الكمال ٣٣١ وهدية العارفين ٢: ٤٧٠ وميزان الاعتدال ٤: ١٧٣ والجرح والتعديل ٤/ ١: ٣٥٤ والفهرست لابن النديم ١٧٩ وتهذيب التهذيب ١٠: ٢٧٩ والقرآن والتفسير ٩٨ و ١٠٤ و ١٠٧ وتذكرة الحفاظ ١٦٠. وتهذيب الأسماء واللغات ٢: ١١١ وكشف الظنون ٤٥٩ و ١٤٦٩ و ٢٠٠١ وفهرس الأزهرية ١: ٢٠٧.
(٢) أمل الآمل ٢: ٣٢٥ ومعجم المطبوعات ١٧٧٢ وكشف الظنون ١٥١٧ وإيضاح المكنون ٢: ٣٨٦ وهدية العارفين ٢: ٤٧٠ والأعلام ٨: ٢٠٧ ومعجم المؤلفين ١٢: ٣١٨.','2','683',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1247,'مكي بن حموش [٣٥٥ - ٤٣٧ هـ / ٩٦٦ - ١٠٤٥ م]

<span data-type="title" id=toc-1730>مكي بن أبي طالب حموش بن محمد ابن مختار الأندلسي القسيي، أبو محمد:</span>
عالم بالتفسير والعربية، مقرئ، من أهل القيروان، وبها نشأ وتعلم، وحج، فسمع بمصر ومكة، وعاد إلى بلده، وأقرأ بها. ثم دخل قرطبة أيام المظفر بن أبي عامر (سنة ٣٩٣ هـ) وخطب وأقرأ بجامعها، فعلا ذكره، ورحل الناس إليه. مات بقرطبة. من تصانيفه "الهداية إلى بلوغ النهاية" في معاني القرآن وتفسيره وأنواع علومه، مخطوط، بضعة أجزاء، من سبعين جزء، و "مشكل إعراب القرآن" - وقد ذكر في كتب التراجم بأسماء مختلفة - مثل "شكل إعراب القرآن" و "إعراب القرآن" و "تفسير إعراب القرآن" و "إعراب مشكل القرآن" الخ .. حقق هذا الكتاب الأستاذ ياسين محمد السواس، وأصدره مجمع اللغة العربية بدمشق (١٩٧٤) ويقع في مجلدين. وله أيضا "الإيضاح للناسخ والمنسوخ" مخطوط، في خزانة القرويين بفاس، و "الإيجاز" في الناسخ والمنسوخ، مخطوط، و "مشكل غريب القرآن" ثلالة أجزاء،','2','684',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1248,'ألفه بمكة سنة ٣٨٩ هـ. و "المأثور عن مالك في أحكام القرآن وتفسيره" عشرة أجزاء، و "مختصر أحكام القرآن" أربعة أجزاء، و "الإبانة عن معاني القرآن" جزء، وقيل هو في القراءات مخطوط، و "بيان إعجاز القرآن" و "مشكل المعاني والتفسير" خمسة عشر جزءا، و "الاستيفاء" في قوله تعالى: {إلا ما شاء ربك} (هود: ١٠٧) جزء و "شرح اختلاف العلماء" في قوله تعالى: {ما يعلم تأويله إلا الله} (آل عمران - ٧) جزء، و "شرح" قوله تعالى: {وما خلقت الجن والإنس إلا ليعبدون" (الذاريات ٥٦) جزء، و "شرح" قوله تعالى: {ولقد ذرأنا لجهنم} الآية (الأعراف ١٧٩) و "شرح" الاختلاف في قوله تعالى: {ما جعل الله من بحيرة} (المائدة ١٠٣) جزء، و "شرح" قوله تعالى: {شهادة بينكم} الآيات الثلاث (المائدة ١٠٦) جزء و "شرح" قوله تعالى: {فلما تراءى الجمعان} (الشعراء ٦١) جزء و "شرح" الاختلاف في قوله تعالى: {ثم أورثنا الكتاب الذين اصطفينا} (فاطر ٣٢) جزء (١).
_________
(١) طبقات المفسرين للداودي ٢: ٢٣١ ومشكل إعراب القرآن، مقدمته، لياسين محمد السواس. وبغية =','2','684',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1249,'المخلصي [٤٣٩ - بعد ٤٩٦ هـ / ١٠٤٧ - بعد ١١٠٣ م]

<span data-type="title" id=toc-1731>منبه بن محمد بن أحمد بن علي بن منال بن أبي سهل، أبو وهب بن أبي جعفر المخلصي:</span>
فقيه حنفي، شاعر، واعظ، حسن المعرفة بالتفسير. قال الداودي: قدم بغداد حاجا سنة ٤٩٦ هـ، وحدث بها كه أبي حامد أحمد الشجاعي وأبي نصر الحداد، وروى عنه من أهلها أبو عبد الله البيضاوي. وإنما سمي المخلصي، لأن والده كان صادقا مخلصا فيما يقول للملوك والسلاطين، وكان ينفق من ماله على من يقرأ عليه" (١). الوعاة ٢: ٢٩٨ ومعجم الأدباء ١٩: ١٦٧ وشذرات الذهب ٣: ٢٦٠ ونزهة الألباء ٣٤٧ ومفتاح السعادة ٢: ٨٤ والديباج المذهب ٣٤٦ وغاية النهاية ٢: ٣٠٩ وعبر الذهبي ٣: ١٨٧ ومرآة الجنان ٣: ٥٧ وهدية العارفين ٢: ٤٧٠ ووفيات الأعيان ٥: ٢٧٤ وجذوة المقتبس ٣٢٩ وبغية الملتمس ٤٥٥ والنجوم الزاهرة ٥: ٤١ والصلة ٢: ٥٩٧ وطبقات القراء للذهبي ١: ٣١٦ وإنباه الرواة ٣: ٣١٣ وكشف الظنون ٢٠، ٣٠٦، ٢١٠، ١٦٩٥، ١٩٢٠، ٢٠٤١، وشجرة النور ١٠٧ والأعلام ٨: ٢١٤ ومعجم المؤلفين ١٣: ٣ ومجلة الفيصل ٣٢: ١٤٧ ونفح الطيب ٣: ١٧٩ ومعالم الإيمان ٢: ٢١٣.
_________
(١) طبقات المفسرين للداودي ٢: ٣٣٢.','2','685',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1250,'الهمذاني [... - ٦٤٣ هـ / ... - ١٢٤٥ م]

<span data-type="title" id=toc-1732>المنتخب بن أبي العز بن رشيد، أبو يوسف، منتجب الدين الهمذاني:</span>
عالم بالقراءات والعربية، صوفي، قرأ بمصر وبدمشق، ثم تصدر للإقراء، فكان شيخ الإقراء بالتربة الزنجليلية جوار دار الطعم بدمشق، وتوفي بها. قال الذهبي: "كان سوقه كاسدا مع وجود السخاوي" وقال أبو شامة: "كان مقرئا مجودا، وانتفع بشيخنا أبي الحسن (السخاوي) في معرفة قصيدة الشاطبي، ثم تعاطى شرح القصيدة فخاض بحرا عجز عن سباحته، وجحد حق تعليم شيخنا له وإفادته ... ". من كتبه "الفريد في إعراب القرآن المجيد" مخطوط، في أربع مجلدات (١).

ابن المنجى [٦٣١ - ٦٩٥ هـ / ١٢٣٤ - ١٢٩٦ م]

<span data-type="title" id=toc-1733>المنجى بن عثمان بن أسعد بن المنجى (بن بركات بن المؤمل، أبو البركات، زين الدين ابن المنجى التنوخي، المعري الأصل، الدمشقي)</span>
_________
(١) غية النهاية ٢: ٣١٠ وطبقات القراء للذهبي ٢: ٥٠٨ وبغية الوعاة ٢: ٣٠٥ ومرآة الجنان ٤: ١١١ والشذرات ٥: ٢٢٧ وهدية العارفين ٢: ٤٧٢ وطبقات المفسرين ٢: ٣٣٣ والأعلام ٨: ٢٢٢ وكشف الظنون ١٢٥٨ ومعجم المؤلفين ١٣: ٧ وتراجم رجال القرنين ١٧٥.','2','685',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1251,'بن بركات بن المؤمل، أبو البركات، زين الدين ابن المنجى التنوخي، المعري الأصل، الدمشقي: فقيه حنبلي، مفسر، أصولي، نحوي، درس وأفتى وناظر، وانتهت إليه رياسة المذهب بالشام في وقته. وكان له في الجامع حلقة للأشغال والفتوى نحو ثلاثين سنة، متبرعا لا يتناول على ذلك معلوما. قال البرزالي. كان عالما بفنون شتى من الفقه والأصلين، اجتمع له العلم والدين، والمال والجاه وحسن الهيئة" وقال الذهبي: "كان حسن الأخلاق، معروفا بالذكاء وصحة الذهن، وجودة المناظرة، وطول النفس في البحث". من كتبه "تفسير القرآن الكريم" في مجلدات (١).

البلوطي [٢٧٣ - ٣٥٥ هـ / ٨٨٦ - ٩٦٦ م]

<span data-type="title" id=toc-1734>منذر بن سعيد بن عبد الله بن عبد الرحمن بن قاسم بن عبد الله النفزي القرطبي، أبو الحكم البلوطي:</span>
قاضي
_________
(١) طبقات المفسرين للداودي ٢: ٣٣٤ وشذرات الذهب ٥: ٤٣٣ وهدية العارفين ٢: ٤٧٢ والدارس ٢: ١٢٠ وانظر فهرسته. وذيل طبقات الحنابلة ٢: ٣٣٢ والبداية والنهاية ١٣: ٣٤٥ والأعلام ٨: ٢٢٤ والقاموس المحيط مادة (نجا) ومثله في تاج العروس، ومعجم المؤلفين ١٣: ٧ وتذكرة النبيه ١: ١٩٠.','2','686',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1252,'قضاة الأندلس في عصره، فقيه، عالم بالتفسير، خطيب، شاعر. نسبته إلى فحص البلوط بقرب قرطبة، سمع بالأندلس، ورحل حاجا سنة ٣٠٨ هـ، فأتام في رحلته أربعين شهرا، أخذ بها عن بعض علماء مصر ومكة. وغلب عليه التفقه بمذهب داود (المذهب الظاهري) فكان يؤثر مذهبه، ويحتج بمقالته، ويأخذ بها لنفسه، فإذا جلس مجلس الحكومة، قضى بمذهب مالك الذي عليه العمل في بلده. ولي قضاء ماردة وما والاها من مدن الجوف، فقضاء الثغور الشرقية، فقضاء الجماعة بقرطبة سنة ٣٣٩، واستمر إلى أن توفي فيها. قال ابن الفرضي: لم تحفظ له قضية جور، ولا جربت عليه في أحكامه زلة". من كتبه "الناسخ والمنسوخ" و "الإنباه على استنباط الأحكام من كتاب الله" ويسمى "أحكام القرآن" (١).
_________
(١) طبقات المفسرين ٢: ٣٣٦ وقضاة قرطبة ١٢٠ وقضاة قرطبة وعلماء افريقية ١٧٥ رقم ٤٩ وتاريخ علماء الأندلس ١٤٤ وجذوة المقتبس ٣٢٦ وبغية الوعاة ٢: ٣٠١ وشذرات الذهب ٣: ١٧ ومعجم الأدباء ٧: ١٧٨ ومرآة الجنان ٢: ٣٥٨ وبغية الملتمس ٤٥٠ ومطمح الأنفس ٣٧ وتاريخ قضاة الأندلس ٦٦ وإنباء الرواة ٣: ٣٢٥ ونفح الطيب ١: ٣٧٠ و ٥٧٠ وانظر فهرسته. وشجرة النور ٩٠ وفهرست ابن خير ٥٤ وأزهار الرياض ٢: ٢٩٤ وهدية العارفين ٢: ٤٧٢ والنسخ في القرآن الكريم ١: ٣٢٦ وصفة جزيرة الأندلس ١٤٠.','2','686',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1253,'الكازروني [... - ٨٦٠ هـ / ... ١٤٥٦ م]

<span data-type="title" id=toc-1735>منصور بن الحسن بن علي بن اختيار الدين فريدون بن علي، العماد القرشي العدوي العمري الكازروني:</span>
فقيه شافعي، عالم بالتفسير، محدث، متكلم. نسبته إلى كازرون (مدينة في غربي إيران). أخذ عن ابن الجزري وغيره. قال السخاوي: "جاور بمكة في سنة ٨٥٨ هـ، واستمر مجاورا، منجمعا عن الناس، لا يخرج من بيته غالبا، إلى أن مات" له نحو مئة كتاب، منها "لطائف الألطاف في تحقيق التفسير ونقد الكشاف" لم يكمله (١).

النسابوري [٣٣٧ - ٤٢٢ هـ / ٩٤٨ - ١٠٣١ م]

<span data-type="title" id=toc-1736>منصور بن الحسين بن محمد بن أحمد ابن القاسم، أبو نصر النيسابوري:</span>
مفسر مشهور، محدث، حدث عن أبي العباس الأصم، وعنه شيخ الإسلام أبو إسماعيل الأنصاري، وعبد الواحد
_________
(١) الضوء اللامع ١٠: ١٧٠ وشذرات الذهب ٧: ٢٩٧ وهدية العارفين ٢: ٤٧٥ وإيضاح المكونون ٢: ٤٠٣ والأعلام ٨: ٢٣٦.','2','687',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1254,'القشيري (١).

المسدي [٥٧٠ - ٦٥١ هـ / ١١٧٤ - ١٢٥٣ م]

<span data-type="title" id=toc-1737>منصور بن سرار بن عيسى بن سليم، أبو على الأنصاري الاسكندري، المعروف بالمسدي:</span>
فقيه مالكي، مؤدب، مقرىء، ناظم، مفسر. روى عنه الدمياطي وغيره. قال السيوطي: وله شهرة ببلده. من آثاره القرآن" (٢).

منصور بن سعيد [... - حيا ٣٥٣ هـ / ... - حيا ٩٦٤ م]

<span data-type="title" id=toc-1738>منصور بن سعيد بن أحمد بن الحسن، أبو النصر:</span>
مفسر، انفرد صاحب "كشف الظنون" بذكره وقال "له تاج المعاني في تفسير السبع المثاني"، وهو كبير في مجلدات، أوله "أحق ما صرفت إليه الرغبة، وجردت فيه العناية الخ" ذكر ديباجة طويلة بليغة، ثم ذكر أن القائد أبا
_________
(١) شدرات الذهب ٣: ٢٢٥ وطبقات المفسرين للداودي ٢: ٣٣٨.
(٢) طبقات المفسرين للسيوطي ٤٢ وطبقات المفسرين للداودي ٢: ٣٣٨ وغاية النهاية ٢: ٣١٢ وحسن المحاضرة ١: ٤٠١ والأعلام ٨: ٢٣٨.','2','687',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1255,'علي بجكم كان راغبا في كتاب الله سبحانه وتعالى مولعا، فأشار إلى تأليفه فألفه سنة ٣٥٣ هـ، وقدم مقدمة في الحروف والإعراب ثم شرع في المقصود وأورد فيه جميع ما في التفسير بعبارات لطيفة وألفاظ فصيحة تدل على مهارته في الأدب". قلت: لم أعثر له على ترجمة وافية فيما بين يدي الساعة من كتب الرجال. أما بجكم التركي، فقد كان من كبار رجال الدولة في عهد الراضي بالله العباسي، وقتل غيلة سنة ٢٢٩ هـ. وأخباره كثيرة سردها ابن الأثير في كتابه "الكامل في التاريخ" الجزء الثامن، وأبو بكر الصولي في كتابه "أخبار الراضي بالله والمتقي لله" (١).

الطبلاوي [... - ١٠١٤ هـ / ... - ١٦٠٦ م]

<span data-type="title" id=toc-1739>منصور الطبلاوي، سبط ناصر الدين محمد بن سالم المتوفى سنة ٩٦٦ ه</span>ـ:
فقيه شافعي، عالم بالعربية والبلاغة. نسبته إلى "طبلية" من قرى المنوفية بمصر. ومولده ووفاته بالقاهرة. من كتبه "السر القدسي في تفسير آية الكرسي" مخطوط،
_________
(١) كشف الظنون ٢٧٠ ومعجم المؤلفين ١٣: ٦٣.','2','688',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1256,'فرغ من تأليفه في شوال سنة ٩٩٧ هـ قال حاجي خليفة: أوله، حمدا لمن أظهر أسرار التنزيل .. الخ، رتبه على مقدمة تتضمن ثلاثة أبواب وعلى مقصد وخاتمة وفيها بابان". و "منهج التيسير إلى علم التفسير" مخطوط، وهو شرح لنظم علم التفسير لعبد الرؤوف الزمزمي المكي. قال صاحب كشف الظنون: أوله، الحمد لله الكريم المتعال مانح الإكرام والإجلال .. الخ. أتمه في شوال سنة ٩٨٩ هـ. و "المسترضى في تفسير قوله تعالى: {ولوسف يعطيك ربك فترضى}. مخطوط (١).

خطيب السقيفة [... - ٩٦٧ هـ / ... - ١٥٦٠ م]

<span data-type="title" id=toc-1740>منصور بن عبد الرحمن الحريري، زين الدين، الشهير بخطيب السقيفة:</span>
فاضل من الشافعية، كان له يد طولى في التفسير والعربية. مولده ووفاته بدمشق. أخذ عن البدر الغزي وغيره. وولي الخطابة في جامع السقيفة خارج باب توما
_________
(١) خلاصة الأثر ٤: ٤٢٨ وهدية العارفين ٢: ٤٧٥ وكشف الظنون ٤٤٣ و ٨٩٠ و ١٩٧٠ والأعلام ٨: ٢٣٩ ومعجم المؤلفين ١٣: ١٥ وتاج العروس (طبلية) وفي الضوء اللامع ١١: ٢١٢ الطبلاوي: نسبة لطبلاوى من قرى الوجه البحري.','2','688',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1257,'بدمشق. وكان خادم ضريح الشيخ رسلان مدة طويلة ورحل إلى بلاد الروم، وأقام مدة بحلب (١).

السمعاني [٤٢٦ - ٤٨٩ هـ / ١٠٣٥ - ١٠٩٦ م]

<span data-type="title" id=toc-1741>منصور بن محمد بن عبد الجبار بن أحمد المروزي السمعاني التميمي الحنفي ثم الشافعي، أبو المظفر:</span>
مفسر، عالم بالحديث، من أهل مرو، مولدا ووفاة. تفقه على مذهب أبي حنيفة، ثم دخل بغداد سنة ٤٦١ هـ، وانتقل إلى المذهب الشافعي. وعاد إلى بلده، فاضطرب أهلها لذلك وقام عليه العوام، فرحل إلى طوس، ومنها إلى نيسابور، فأقام بها مدة ثم عاد إلى مرو. وقدمه نظام الملك على أقرانه فيها. من آثاره "تفسير القرآن" ويعرف بتفسير السمعاني، مخطوط، ثلاث مجلدات (٢).
_________
(١) الكواكب السائرة ٣: ٢١٠ والشذرات ٨: ٣٥١.
(٢) العبر ٣: ٣٢٦ والشذرات ٣: ٣٩٣ وكشف الظنون ٤٤٩ والنجوم الزاهرة ٥: ١٦٠ ومفتاح السعادة ٢: ٣٣٢ وطبقات السبكي ٥: ٣٣٥ ومرآة الجنان ٣: ١٥١ والبداية والنهاية ١٢: ١٥٣ والأنساب ٣٠٧ ب. واللباب ٢: ١٣٩ وهدية العارفين ٢: ٤٧٣ والرسالة المستطرفة ٤٣ وطبقات المفسرين للداودي ٢: ٣٣٩ والمنتظم ٩: ١٠٢ ومعجم المؤلفين ١٣: ٢١ والأعلام ٧: ٢٠٣.','2','689',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1258,'الدشتكي [... - ٩٤٨ هـ / ... - ١٥٤١ م]

<span data-type="title" id=toc-1742>منصور بن محمد صدر الدين بن إبراهيم الحسيني الدشتكي الشيرازي، غياث الدين:</span>
من كبار العلماء بالحكمة والإلهيات، عارف بالتفسير، من أهل شيراز مولدا ووفاة، تنسب إليه المدرسة المنصورية بها، ونسبته إلى (دشتك" من قرى أصبهان. ولي منصب الصدارة مدة في عهد الشاه طهماسب الصفوي. من آثاره "حاشية على تفسير الكشاف" و "تفسير سورة الإنسان" مخطوط، قال صاحب كشف الظنون: "وهو مختصر، أوله: أحمد الله على جميل سلطانه، الخ .. فيه تحقيقات لطيفة ومباحث شريفة" (١).

المراد آبادي [... - ١٢٠١ هـ / ... - ١٧٨٧ م]

<span data-type="title" id=toc-1743>منعم خان المراد آبادي:</span>
من أمراء الهند، أديب، مفسر، له "تفسير القرآن " (٢).
_________
(١) هدية العارفين ٢: ٤٧٥ وكشف الظنون ٤٤٩ وفوائد الرضوية ٦٦٨ وروضات الجنات (ط ٢) ٧٤٠ والأعلام ٧/ ٢٠٤٠ ومعجم المؤلفين ١٣: ١٩.
(٢) هدية العارفين ٢: ٤٧٦.','2','689',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1259,'الشيرازي [... - ٩٥٦ هـ / ... - ١٥٤٩ م]

<span data-type="title" id=toc-1744>مهدي بن عبد الله الشيرازي، ثم الرومي:</span>
مفسر، ناظم، أصله من شيراز، نزل القسطنطينية ودرس بها. من آثاره "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي، و "تعليقة" على الكشاف، في التفسير، للزمخشري (١).

السدوسي [... - ١٩٥ هـ / ... - ٨١٠ م]

<span data-type="title" id=toc-1745>مؤرخ بن عمرو بن الحارث بن ثور بن حرملة بن علقمة السدوسي البصري، أبو فيد:</span>
عالم بالعربية والحديث والأنساب والأخبار، من أعيان أصحاب الخليل بن أحمد، من أهل البصرة. روى عن أبي عمرو بن العلاء وشعبة بن الحجاج وغيرهما. وكان يقول: "قدمت من البادية ولا معرفة لي بالقياس في العربية، وإنما كانت معرفتي قريحتي، وأول ما تعلمت القياس في حلقة أبي زيد الأنصاري". وكان له اتصال
_________
(١) هدية العارفين ٢: ٤٨٤ والشقائق النعمانية ٣٠٩ وكشف اظنون ١٤٨١ وإيضاح المكنون ١: ١٤١ وقيل في وفاته سنة ٩٥٧ هـ.','2','690',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1260,'بالمأمون العباسي، ورحل معه إلى خراسان، فسكن مدة بمرو، وانتقل إلى نيسابور وأقام بها وكتب عن مشايخها. من كتبه "غريب القرآن" قال الخطيب البغدادي: "رواه عنه أهل مرو". وله أيضا "معاني القرآن" (١).

الإستجي [٢٣٧ - ٣٠٦ هـ / ٨٥١ - ٩١٨ م]

<span data-type="title" id=toc-1746>موسى بن أزهر بن موسى بن حريث ابن قيس بن جبير، أبو عمر الإستجي الأندلسي:</span>
أديب، كان إماما في اللغة والحديث وغريبه، حافظا للتفسير والمشاهد والشعر. من أهل إستجه بالأندلس، مات بعد منصرفه من الغزو بقلعة رباح (٢).
_________
(١) طبقات المفسرين للداودي ٢: ٣٤٠ وتاريخ بغداد ١٣: ٢٨٥ ونزهة الألباء ١٣٠ ومعجم الأدباء ١٩: ١٩٦ وبغية الوعاة ٢: ٣٠٥ ووفيات الأعيان ٥: ٣٠٤ والفهرست لابن النديم ٤٨ والمؤتلف والمختلف ٥٤ ومراتب النحويين ٦٧ وإنباه الرواة ٣: ٣٢٧ والمعارف ٥٤٣ ونور القبس ١٠٤ وطبقات ابن قاضي شهبة ٢: ٢٦١ وكشف الظنون ١٢٠٧ و ١٤٦٠ وهدية العارفين ٢: ٢٤٦ والأعلام ٨: ٢٦٦ والمزهر ٢: ٢٣٢ وجمهرة الأنساب ٢٩٩ وفي بعض مصادر ترجمته أنه مات سنة ١٩٥ هـ وقيل أنه عاش إلى بعد المئتين.
(٢) طبقات المفسرين للداودي ٢: ٣٤١ وتاريخ علماء الأندلس ٢: ١٤٨ وبغية الوعاة ٢: ٣٠٦ والأعلام ٨: ٢٦٨.','2','690',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1261,'الميرثلي [٥٢٢ - ٦٠٤ هـ / ١١٢٨ - ١٢٠٧ م]

<span data-type="title" id=toc-1747>موسى بن حسين بن موسى بن عمران القيسي، أبو عمران الزاهد، المعروف بالميرتلي، أو المارتلي:</span>
نسبة إلى بلدة "حصن مارتلة" من حصون باجه بالأندلس، أحد شعراء الزهد، عالم بالتفسير والفقه والحديث والأدب. قال ابن سعيد في "الغصون اليانعة": سكن اشبيلية، واشتهر بالزهد والانقطاع حتى كان في ذلك واحد وقته، يزوره الملوك ويتبركون به ويستوهبون دعاءه، وكان لا يقبل من أحد شيئا، وإنما كان له ما يقوم به من ملك ورثه من جهة طيبة". وقال الحميري: اشتهر باشبيلية بالصلاح، وله شعر "مدون منقول" ثم أورد خبرا لطيفا عنه مع المنصور الموحدي (١).
_________
(١) انفرد ابن الأبار في كتابيه "التكملة ٦٨٧ا" "وتحفة القادم رقم ٥٨" بتسميته موسى بن حسين بن موسى بن عمران، وتعريفه بالميرتلي، وقال: توفي في أول جمادى الأولى سنة ٦٠٤، وهو ابن اثنتين وثمانين سنة". واتفقت المصادر الأخرى على تسميته موسى بن عمران، إلا أنها اختلفت بتعريفه وتاريخ ومكان وفاته. ففي "الغصون اليانعة" ١٣٥ هو المارتلي ووفاته باشبيلية سنة ٦٠٤. ومثله في "المغرب في حلى المغرب" ١: ٤٠٦ إلا أنه أرخ وفاته في آخر مدة ناصر بني عبد المؤمن. ومثله في "صفة جزيرة الأندلس ١٧٥ إلا أنه أرخ وفاته سنة ٥٩١ هـ. وعرفه مؤلف الذخيرة السنية بالمرتالي وقال مات بفاس سنة ٦٠٣ =','2','691',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1262,'ابن سيار [... - نحو ١٥٠ هـ / ... - ٧٦٧ م]

<span data-type="title" id=toc-1748>موسى بن سيار الأسواري:</span>
أحد القصاص، عارف بالتفسير، له رواية ضعيفة للحديث، وذكر أنه كان قدريا. من أهل البصرة. روى عن بكر بن عبد الله المتوفى سنة ١٠٨ هـ، وعطية العوفي المتوفي سنة ١١١ هـ. قال الجاحظ: "كان من أعاجيب الدنيا، كانت فصاحته بالفارسية في وزن فصاحته بالعربية، وكان يجلس في مجلسه المشهور به، فتقعد العرب عن يمينه، والفرس عن يساره، فيقرأ الآية من كتاب الله ويفسرها للعرب بالعربية، ثم يحول وجهه إلى الفرس فيفسرها لهم بالفارسية، فلا يدرى بأي لسان هو أبين، واللغتان إذا التقتا في اللسان الواحد أدخلت كل واحدة منها الضيم على صاحبتها، إلا ما ذكرنا من لسان موسى بن سيار .. " (١).
_________
= ودفن خارج باب الفتوح، وعنه أخذ صاحب دليل مؤرخ المغرب ٢: ٣٨٦.
(١) البيان والتبيين ١: ٣٦٨ ولسان الميزان ٦: ١٢٠ والأعلام ٨: ٢٧٢ والأنساب ٣٧ وحركات الشيعة المتطرفين ٢٠٩.','2','691',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1263,'الثقفي [... - نحو ١٩٠ هـ / ... - ٨٠٥ م]

<span data-type="title" id=toc-1749>موسى بن عبد الرحمن الثقفي الصنعاني، أبو محمد:</span>
وسم بأنه مفسر غير موثوق به روى عن عبد الملك بن جريج، وعنه عبد الغني بن سعيد الثقفي. قال ابن عدي: يعرف بأبي محمد المفسر، وهو منكر الحديث". وقال ابن حبان: دجال وضع على ابن جريج عن عطاء عن ابن عباس كتابا في التفسير". من آثاره "تفسير القرآن" منه مخطوطة في الأمبروزيانا من سورة ١٩ إلى ٣٧. وقد ذكر في مجلة الدراسات الشرقية  R.S.O ٢ : ٧ - ١٣  بأنه تفسير ابن عباس. ومنه مخطوطة في مكتبة محمود بالمدينة تضم بقية من السورتين الأخيرتين، وقد ذكر بأنه تفسير ابن عباس أيضا (١).

القطان [٢٣٢ - ٣٠٦ هـ / ٨٤٦ - ٩١٩ م]

<span data-type="title" id=toc-1750>موسى بن عبد الرحمن بن حبيب، أبو الأسود، المعروف بالقطان:</span>
قاض،
_________
(١) تاريخ التراث العربي ١: ٢٠٣ وميزان الاعتدال ٣: ٢١٣ ولسان الميزان ٥: ١٢٤.','2','692',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1264,'مفسر، حافظ للحديث، من فقهاء المالكية. أصله من موالي بني أمية. أخذ عن محمد بن سحنون وغيره، وروى عنه تميم بن أبي العرب وجماعة. ولاه إبراهيم بن أحمد (من أمراء الأغالبة أصحاب افريقية) قضاء طرابلس (طرابلس الغرب). قال الداودي: "فنفذ الحقوق وأخذها للضعيف من القوي، فبغي عليه وأوذي، فعزل، وحبس في الكنيسة شهورا" واتفق أن عرضت قضية اختلف فيها القضاة، فأفتى بها وهو في السجن، فقال الوالي: مثل هذا لا يحبس، وأطلقه. له كتاب "أحكام القرآن" اثنا عشر جزءا (١).

صفي المولوي [... - ١١٥٧ هـ / ... - ١٧٤٤ م]

<span data-type="title" id=toc-1751>موسى بن علي المولوي الرومي صفي الدين بن جلال الدين، المعروف بصفي درده:</span>
شاعر تركي، مستعرب، له مشاركة في التفسير والحديث. من أهل
_________
(١) طبقات علماء افريقية للخشني ١٥٩ وطبقات علماء افريقية وتونس تحقيق الشابي واليافي ١٠٤ حاشية رقم ٥ والبيان المغرب: ١: ١٨١ والديباج المذهب ٣٤٢ وشجرة النور ٨١ وفيه وفاته سنة ٣٠٩ خطأ. والأعلام ٨: ٢٧٤ وترتيب المدارك انظر فهرسته، ومعجم المؤلفين ١٣: ٤١.','2','692',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1265,'بلدة آتنة في بلاد الروم (تركيا). أخذ التفسير والحديث عن عبد الغني النابلسي. وتولى مشيخة خانقاه المولوية بمحلة قاسم باشا بالقسطنطينية، وتوفي بها (١).

النهدي [... - ٢٢٠ هـ / ... - ٨٣٥ م]

<span data-type="title" id=toc-1752>موسى بن مسعود البصري، أبو حذيفة النهدي:</span>
محدث، مفسر، من أهل البصرة. أحد شيوخ البخاري، اختلف العلماء في أمره، منهم من وثقه، ومنهم من ضعفه. له "تفسير القرآن". قال سزكين: استخدمه الطبري في تاريخه (١: ٥٩٤)، وفي تفسيره في رواية أحمد بن منصور. واستخدمه الثعلبي في كتابه الكشف والبيان في رواية جعفر بن محمد بن الليث الزيادي الجوهري" (٢).
_________
(١) هدية العارفين ٢: ٤٨٢ ومعجم المؤلفين ١٣:
(٢) التاريخ الكبير للبخاري ٤/ ١: ٢٩٥ وتاريخ التراث العربي ١: ٢٠٦ وشذرات الذهب ٢: ٤٨ ووفاته فيهما سنة ٢٢٠ هـ وهدية العارفين ٢: ٤٧٧ وعرفه بالهندي خطأ، وعنه معجم المؤلفين ١٣: ٤٨ وانظر العبر وفيات سنة ٢٢٠ هـ والتهذيب ١٠: ٣٧٠.','2','693',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1266,'ابن يونس [٥٥١ - ٦٣٩ هـ / ١١٥٦ - ١٢٤٢ م]

<span data-type="title" id=toc-1753>موسى بن يونس بن محمد بن منعة بن مالك العقيلي، كمال الدين، أبو الفتح بن رضي الدين الموصلي:</span>
فيلسوف، علامة بالرياضيات والحكمة والأصول وشرائع النصارى واليهود، عارف بالتفسير والأدب والموسيقى والسير. ولد بالموصل، وتعلم بها وبالمدرسة النظامية ببغداد. وقصده العلماء للأخذ عنه. وكان أهل الذمة يقرأون عليه التوراة والإنجيل ويشرحهما شرحا وافيا. واستخرج في علم "الأوفاق" طرقا لم يهتد إليها أحد. واتهم في عقيدته لغلبة العلوم العقلية عليه. وتوفي بالموصل. من كتبه "كشف المشكلات وإيضاح المعضلات" في تفسير القرآن (١).
_________
(١) طبقات السبكي ٨: ٣٧٨ وطبقات المفسرين للداودي ٢: ٣٤٣ والعبر ٥: ١٦٢ والبداية والنهاية ١٣: ١٥٨ وهدية العارفين ٢: ٤٧٩ وشذرات الذهب ٥: ٢٠٦ ومفتاح السعادة ٢: ٣٥٦ وابن خلكان ٥: ٣١١ والفلاكة والمفلوكون ٨٤ ومرآة الجنان ٤: ١٠١ والحوادث الجامعة ١٤٩ والأعلام ٨: ٢٨٨ وذيل كشف الظنون ٢: ٣٦٧. وتراث العرب العلمي ٣٤٤ وعيون الأنباء ١: ٣٠٦ ومختصر دول الإسلام ٢: ١١٠ ومعجم المؤلفين ١٣: ٥١.','2','693',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1267,'الشبلنجي [١٢٥٢ - حيا ١٣٢٢ هـ / ١٨٣٦ - حيا ١٩٠٤ م]

<span data-type="title" id=toc-1754>مؤمن بن حسن مؤمن الشبلنجي:</span>
باحث، من أهل شبلنجة - من قرى مصر قرب بنها العسل - تعلم بالأزهر، وأقام في جواره. قال سركيس: "توفي في أوائل القرن الرابع عشر للهجرة". وقال البغدادي "وهو الآن، أعني في سنة ١٣٢٢ هـ، موجود بالأزهر". من كتبه "فتح المنان في تفسير غريب القرآن" (١).

مولوي [... .. - حيا قبل ١٣١١ هـ / ... - حيا قبل ١٨٩٣ م]

<span data-type="title" id=toc-1755>ميرزا محمد مولوي:</span>
مفسر، من آثاره "تفسير الجليل" طبع بلكناو بالهند سنة ١٨٩٣ م (٢).
_________
(١) هدية العارفين ٢: ٤٨٣ وإيضاح المكنون ٢: ١٧٤ وهو فيهما: سيد مؤمن، ومثله في معجم المطبوعات ١١٠٠، والأعلام ٨: ٢٩١ وفيه وفاته بعد ١٣٠٨.
(٢) معجم المطبوعات ١٨٢٦.','2','694',NULL,NULL,'0');
