النوري = علي بن حمشيد ... ١٢٤٦ هـ
نوعي الرومي = يحيى بن علي ... ١٠٠٧ هـ
النوقاني = محمد بن علي ... ٥٩٢ هـ
نووي الجاوي = محمد بن عمر ... ١٣١٦ هـ
نيازي المصري = محمد بن علي ... ١١٠٥ هـ
النيريزي = علي بن محمد ٦٠٢ هـ
النيسابوري = أحمد بن محمد ٣٥١ هـ
النيسابوري = أحمد بن محمد ٣٥٣ هـ
النيسابوري = الحسن بن محمد ٤٠٦ هـ
النيسابوري (النظام) = الحسن بن محمد بعد ٨٥٠ هـ
النيسابوري = الحسين بن الفضل ... ٢٧٢ هـ
النيسابوري = سيعد بن محمد نحو ٤٤٠ هـ
النيسابوري = سلمان بن ناصر ... ٥١٢ هـ
النيسابوري = علي بن سهل ... ٤٩١ هـ
النيسابوري = محمد بن عبدوس ... ٣٣٨ هـ
النيسابوري = محمد بن محمد بعد ٣٩٠ هـ
النيسابرري = محمد بن محمود ... ٧٩١ هـ
النيسابوري = محمود بن علي ... حيا ٥٥٣ هـ
النيسابوري = مسعود بن محمد ٥٧٨ هـ
النيسابوري = منصور بن الحسين ... ٤٢٢ هـ
نيل فروش = محمد حسين الأصفهاني ... ١١٦٠ هـ','2','697',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1271,'(ن)

الديلمي [... - ٤٤٤ هـ / ... - ١٠٥٢ م]

<span data-type="title" id=toc-1757>الناصر بن الحسين بن محمد بن عيسى الحسني الطالبي، أبو الفتح، المعروف بالديلمي:</span>
إمام زيدي، مفسر، ولد وتعلم في بلاد الديلم (في الجنوب الغربي لبحر قزوين) ودخل اليمن سنة ٤٣٧ هـ، فدعا إلى نفسه بالإمامة، وبايعته بعض القبائل، فجمع العساكر، ودخل مدينة صعدة، ثم سار إلى صنعاء فملكها في نفس السنة. وجعل إقامته في "ذي بين" واختط حصن "ظفار ذي بين". وفي أيامه ظهر علي بن محمد بن علي الصالحي - مؤسس دولة الصليحيين في اليمن - واستولى على صنعاء وغيرها، فتقاعد الناس عن نصره','2','698',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1272,'أبي الفتح، فجعل يتنقل من بلد إلى بلد خوفا من الصليحي. وفي سنة ٤٤٤ هـ، سار إلى بلاد "عنس"، فقصده الصليحي وقتله في نيف وسبعين نفرا من أصحابه، في وقعة كانت بينهما بقاع فيد (أو نجد الحاج)، من بلاد عنس، ودفن في "ردمان" وقيل في قرية "أفيق". وقبره فيها معروف. وفي إسمه ونسبه وتاريخ دخوله اليمن وعام وفاته، خلاف. من آثاره كتاب في "تفسير القرآن" في أربعة أجزاء (١).
_________
(١) غاية الأماني ١: ٢٤٦ - ٢٥٠ وهو فيه: الإمام الناصر لدين الله أبو الفتح الحسين بن ناصر بن محمد، ورفع نسبه إلى علي بن أبي طالب. وعنه أخدت تاريخ دخوله اليمن وتاريخ وفاته. وتاريخ الدول الإسلامية ومعجم الأسرات الحاكمة ١: ٢١١ وفيه تاريخ توليه الحكم في صعدة سنة ٤٣٠ هـ، وسماه الناصر أبو الفتح الديلمي. ومعجم الأسرات الحاكمة لزامباور ١٨٨ وفيه =','2','698',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1273,'ناصر بن منصور [... - ... هـ / ... - ... م]

<span data-type="title" id=toc-1758>ناصر بن منصور بن أبي القاسم:</span>
مفسر، ذكره الداودي فقال: له "تفسير" في ثماني مجلدات كبار عظام، يحتج لأبي حنيفة وأصحابه، ويذكر الأحكام ومسائلها مفصلا. قال الفقيه محمد بن أبي بكر بن حيكاس: وجدت هذا الكتاب بمكة المشرفة، وطالعته واستفدت منه، وهو كتاب جليل، هذا لفظ نفيس الدين ومن خطه نقل". قلت: لم أعثر على ترجمة له فيما بين يدي الساعة من كتب الرجال (١).

نافع القارئ [... - ١٦٩ هـ / ... - ٧٨٥ م]

<span data-type="title" id=toc-1759>نافع بن عبد الرحمن بن أبي نعيم الليثي بالولاء المدني:</span>
أحد القراء
_________
= أنه تولى الحكم سنة ٤٣٠ وأنه قتل سنة ٤٤٥ هـ، وسماه الناصر أبو الفتح الديلمي بن الحسين بن محمد. وبلوغ المرام في شرح مسك الختام للعرشي ٣٦ وهو فيه: "الإمام والناصر لدين الله، أبو الفتح بن الناصر بن الحسين، ورفع نسبه إلى الحسين بن علي بن أبي طالب، وزاد، وقيل في نسبه غير ذلك. وأرخ دخوله اليمن سنة ٤٣٠ والأعلام ٨: ٣٠٩
(١) طبقات المفسرين للداودي ٢: ٣٤٤ وكشف الظنون ٤٥٩.','2','699',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1274,'السبعة. أصله من أصبهان. نشأ بالمدينة، وانتهت إليه رياسة القراء فيها، وأقرأ الناس نيفا وسبعين سنة. قال ابن الجزري: "كان أسود اللون، حالكا، صبيح الوجه، حسن الخلق، فيه دعابة". مات بالمدينة. من آثاره "تفسير" في عدة صفحات، مخطوط (١).

الحميري [... - ٥٧٣ هـ / ... - ١١٧٨ م]

<span data-type="title" id=toc-1760>نشوان بن سعيد الحميري، أبو سعيد، أو أبو الحسن:</span>
قاض، لغوي، مؤرخ، أديب، فقيه، أصولي، من الأمراء، من أهل بلدة حوث من بلاد حاشد، شمالي صنعاء. اختاره أهل جبل صبر من اليمن أميرا عليهم. قال ياقوت: استولى على قلاع وحصون، وقدم أهل جبل صبر، حتى صار ملكا .. ". من آثاره "التبيان في تفسير القرآن" مخطوط، الجزء الرابع منه (٢).
_________
(١) ميزان الاعتدال ٤: ٢٤٢ وتهذيب التهذيب ١٠: ٤٠٧ وذكر أخبار أصبهان ٢: ٣٢٦ والتاريخ الكبير ٢/ ٤: ٨٧ وغاية النهاية ٢: ٣٣٠ وابن النديم ٣١ وابن خلكان ٥: ٣٦٨ وتاريخ التراث العربي ١: ١٥٤ والمعارف ٥٨٢.
(٢) غاية الأماني ٢٩٥ ومعجم الأدباء ١٩: ٢١٧ وبغية الوعاة ٢: ٣١٢ ومجلة المجتمع العلمي العربي ٢٦','2','699',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1275,'الليثي [... - ٨٩ هـ / ... ٧٠٨ م]

<span data-type="title" id=toc-1761>نصر بن عاصم الليثي:</span>
عالم بالعربية، من فقهاء التابعين، اشتغل بتفسير القرآن. وهو من أوائل واضعي النحو. قال صاحب "تاريخ التراث العربي: "ولا بد أن تبحث القطع الباقية من التفاسير بحثا جديدا، يهدف إلى معرفة متى بدأت أول محاولة لتفسير القرآن تفسيرا نحويا (أو التوسل بالتحليل النحوي في تفسير القرآن)، فنحن نعرف أن أبا الأسود الدؤلي (المتوفى سنة ٦٩ هـ) وكذلك تلميذيه - بصفة خاصة - نصر بن عاصم ويحيى بن يعمر قد اشتغلوا بتفسير القرآن، .. الخ" (١).

ابن أبي مريم [... - بعد ٥٦٥ هـ / ... - بعد ١١٧٠ م]

<span data-type="title" id=toc-1762>نصر بن علي بن محمد الشيرازي (الفارسي الفسوي، أبو عبد الله، ابن أبي مريم):</span>
٥٩٠ والأعلام ٨: ٣٣٥ والموسوعة العربية الميسرة ٧٤٢ والفهرس التمهيدي ٢٤٩، ٢٨٣، ٣٨٥ وبروكلمان ١: ٣٦٤ والذيل ١: ٥٢٧ وهدية العارفين ٢: ٤٨٩.
_________
(١) بغية الوعاة ٢: ٣١٣ وتاريخ التراث العربي ١: ١٧٨ وإرشاد الأريب ٧: ٢١٠ وطبقات النحويين للزبيدي ٢٧.','2','700',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1276,'الفارسي الفسوي، أبو عبد الله، ابن أبي مريم: عالم شيراز وخطيبها وأديبها في عصره. قال ياقوت: "كان المرجوع إليه في الأمور الشرعية والمشكلات الأدبية". له "تفسير القرآن" (١).

السمرقندي [... - ٣٧٣ هـ / ... - ٩٨٣ م]

<span data-type="title" id=toc-1763>نصر بن محمد بن أحمد بن إبراهيم السمرقندي، أبو الليث، الملقب بإمام الهدى:</span>
مفسر، محدث، صوفي، من أئمة الحنفية. قيل في وفاته: سنة ٣٧٥ و ٣٨٣ و ٣٩٣ هـ. من كتبه "تفسير القرآن" مخطوط، قال حاجي خليفة: "وهو كتاب مشهور، لطيف مفيد، خرج أحاديثه زين الدين قاسم بن قطلوبغا، وترجمه إلى التركية أحمد بن محمد المعروف بابن عربشاه". ونقح هذه الترجمة أبو الفضل موسى الأزنيقي. وترجمه أيضا أحمد الداعي المتوفى حوالي سنة ٨٢٠ هـ. وقال الزركلي عن الأصل: منه
_________
(١) ياقوت ١٩: ٢٢٤ والبغية ٢: ٣١٤ وغاية النهاية ٢: ٣٣٧ والداودي ٢: ٣٤٤ وهدية العارفين ٢: ٤٩١ وكشف الظنون ٤٣٧ وإنباه الرواة ٣: ٣٤٤ وهو فيه نصر بن عبد الله، وقال: هو في زماننا هذا، وقيل إنه كان في سنة ٥٨٧ هـ موجودا.','2','700',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1277,'أجزاء متفرقة، وهو غير كبير، اقتنيت منه الجزء الأخير، أوله تفسير سورة "الحاقة"، وله أيضا "تفسير جزء: عم يتساءلون" مخطوط، موجز. ونسب إليه "بحر العلوم" في التفسير، والصواب أنه لعلي السمرقندي، وقد سبقت ترجمته في هذا المعجم. (١)

الهوريني [... - ١٢٩١ هـ / ... - ١٨٧٤ م]

<span data-type="title" id=toc-1764>نصر (أبو الوفاء) بن الشيخ نصر يونس الوفائي الهوريني الأحمدي الأزهري الأشعري الحفني الشافعي:</span>
عالم باللغة والأدب، مفسر، أزهري، من أهل مصر، أرسلته حكومتها - في زمن محمد علي باشا - إلى فرنسا إماما لإحدى بعثاتها، فأقام مدة، تعلم فيها الفرنسية. ولما عاد ولي رياسة تصحيح المطبعة الأميرية، فصحح كثيرا من كتب العلم والتاريخ واللغة. من كتبه "تفسير سورة الملك" مخطوط (٢).
_________
(١) تاريخ التراث العربي ١: ٢٢٣ و ٢: ٩٧ والداوي ٢: ٣٤٥ والفوائد البهية ٢٢٠ ومفتاح السعادة ٢: ٢٧٧ والجواهر المضية ١: ١٩٦ والأعلام ٨: ٢٤٨ وكشف الظنون ٤٤١ وتاج التراجم ٧٩.
(٢) الموسوعة العربية الميسرة ١٩١٦ وتاريخ الترجمة والحركة الثقافية في عصر محمد علي ١٨١ وتاريخ آداب =','2','701',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1278,'المناستري [... - ٩٧٦ هـ / ... - ١٥٦٨ م]

<span data-type="title" id=toc-1765>نصر الله بن عبد الله المناستري الرومي الحنفي:</span>
مفسر، تركي مستعرب، كان مدرسا بالقسطنطينية وتوفي بها. له "حاشية" على أنوار التنزيل في التفسير، للبيضاوي (١).

الخلخالي [... - ٩٦٢ هـ / ... - ١٥٥٥ م]

<span data-type="title" id=toc-1766>نصر الله بن محمد العمري العجمي الخلخالي:</span>
مفسر، من فقهاء الشافعية، مشارك في بعض العلوم، نزل مدينة حلب (بسورية) ودرس فيها بالعصرونية، وتوفي بها بالطاعون. من كتبه "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (٢).
_________
= اللغة العربية ٤: ٢٣٥ وهدية العارفين ٢: ٤٩٢ ومعجم المؤلفين ١٣: ٩٣ وفهرس دار الكتب المصرية ١: ٥١٣ والبعثات العلمية ١٧٤.
(١) هدية العارفين ٢: ٤٩٤ وكشف الظنون ١٩١.
(٢) شذرات الذهب ٨: ٣٣٣ وإعلام النبلاء ٦: ١٨ والكواكب السائرة ٢: ٢٥٥ وهدية العارفين ٢: ٤٩٣ ووفاته في سنة ٩٤٦ هـ. والأعلام ٨: ٣١.','2','701',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1279,'النضر بن شميل [١٢٢ - ٢٠٣ هـ / ٧٤٠ - ٨١٩ م]

<span data-type="title" id=toc-1767>النضر بن شميل بن خرشة بن يزيد بن كلثوم المازني التميمي، أبو الحسن:</span>
عالم بأيام العرب ورواية الحديث وفقه اللغة. ولد بمرو - من بلاد خراسان - ونشأ بالبصرة وأصله منها. وأخذ عن الخليل بن أحمد، وأقام بالبادية زمنا طويلا وأخذ عن فصحاء العرب. وعاد إلى مرو فولي قضاءها. واتصل بالمأمون العباسي فأكرمه وقربه، وتوفي بمرو. من كتبه "غريب القرآن" (١).

البلخي [... - ١٠٣٦ هـ / ... - ١٦٢٧ م]

<span data-type="title" id=toc-1768>نظام الدين بن عبد الشكور البلخي الهندي:</span>
مفسر، من فقهاء الحنفية، مشارك في بعض العلوم. من كتبه "رياض القدس" في تفسير الجزء الأخير
_________
(١) إنباه الرواة ٣: ٣٦٨ ونور القبس ٩٩ ومرآة الجنان ٢: ٨ ومعجم الأدباء ١٩: ١٣٨ ونزهة الألباء ١١٠ وهدية العارفين ٢: ٤٩٤ ووفيات الأعيان ٥: ٣٩٧ وتذكرة الحفاظ ٣١٤ وجمهرة الأنساب لابن حزم ٢١١ وعبر الذهبي ١: ٣٤٢ والأعلام ٨: ٣٣ والفلاكة والمفلوكون ٦٤ ومراتب النحويين ٦٦ وطبقات الزبيدي ٥٣ والمزهر ٢ وتاريخ ابن الوردي ١: ٢١٥ ومعجم المؤلفين ١٣: ١٠١.','2','702',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1280,'من القرآن (١).

القاضي النعمان [... - ٣٦٣ هـ / ... - ٩٧٤ م]

<span data-type="title" id=toc-1769>النعمان بن محمد بن منصور بن أحمد ابن حيون التميمي، أبو حنيفة، ويقال له القاضي النعمان:</span>
عالم بالفقه والقرآن والأدب والتاريخ، من أركان الدعوة للفاطميين - العبيديين - بمصر. ولد ونشأ بالقيروان، وكان مالكيا ثم تحول إلى مذهب الباطنية. اتصل بخلفاء العبيديين - الفاطميين - منذ قيام دولتهم، وقدم مع المعز إلى مصر، وولي بها القضاء مشاركة مع أبي الطاهر الذهلي الذي كان يلي القضاء قبل الفتح الفاطمي. ويعد النعمان فقيه الشيعة الإسماعيلية الأبهر، فهو الذي دون الفقه الشيعي الإسماعيلي في كتب كثيرة. ويعرف في تاريخ الدعوة الفاطمية باسم القاضي النعمان تمييزا له عن سميه أبي حنيفة النعمان - رضي الله عنه - صاحب المذهب السني المعروف. ووصفه الحافظ الذهبي بالعلامة المارق. وقال ابن حجر
_________
(١) هدية العارفين ٢: ٤٩٥ وإيضاح المكنون ١: ٦٠٢','2','702',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1281,'العسقلاني: "في تصانيفه ما يدل على انحلال عقيدته" وقال الداودي: "له "تأويل القرآن" فيه تحريف كبير" (١).

النخجواني [... - ٩٢٠ هـ / ... - ١٥١٤ م]

<span data-type="title" id=toc-1770>نعمة الله بن محمود النخجواني، ويعرف بالشيخ علوان:</span>
مفسر، صوفي كبير، أصله من "نخجوان" على حدود القفقاس، رحل إلى الأناضول واستقر بآقشهر واشتهر وتوفي بها. من كتبه "الفوائح الإلهية والمفاتيح الغيبية الموضحة للكلم القرآنية والحكم الفرقانية" طبع، مجلدان في التفسير، على الطريقة الصوفية. قال صاحب الشقائق النعمانية:
_________
(١) طبقات المفسرين للداودي ٢: ٣٤٦ ولسان الميزان ٦: ١٦٧ وشذرات الذهب ٣: ٤٧ والنجوم الزاهرة ٤: ١٠٦ ومرآة الجنان ٢: ٣٧٩ والذريعة ٣: ٢٥١ وفوائد الرضوية ٦٩٣ وروضات الجنات ٧٢٧ والهمة في آداب أتباع الأئمة، مقدمة الدكتور محمد كامل حسين، ودعائم الإسلام، مقدمة آصف علي فيظي، وديوان المؤيد في الدين داعي الدعاة، مقدمة الدكتور محمد كامل حسين، والولاة والقضاة ٢٨٦ واتعاظ الحنفا ١: ٢١٥ الحاشية رقم ٢ وانظر فهرسته، ووفيات الأعيان ٥: ٤١٥ و: في أدب مصر الفاطمية. ومختصر دول الإسلام ١: ١٧٥ ورسالة افتتاح الدعوة، مقدمة الدكتورة وداد القاضي، والبعثة المصرية ٤٢ وبروكلمان (الترجمة العربية) ٣: ٣٤١ وهدية العارفين ٢: ٤٩٥ والأعلام ٨: ٤١.','2','703',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1282,'"كتبه بلا مراجعة للتفاسير، وأدرج فيه من الحقائق والدقائق ما يعجز عن إدراكه كثير من الناس، مع الفصاحة في عبارته والبلاغة في تعبيراته" وله أيضا "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (١).

السيدة نفيسة [١٤٥ - ٢٠٨ هـ / ٧٦٢ - ٨٢٤ م]

<span data-type="title" id=toc-1771>نفيسة بنت الحسن بن زيد بن الحسن ابن علي بن أبي طالب:</span>
من ربات العبادة والصلاح والزهد والورع، عالمة بالتفسير والحديث. ولدت بمكة، ونشأت في المدينة. وانتقلت إلى القاهرة مع زوجها إسحاق بن جعفر الصادق، وقيل: مع أبيها الحسن الذي عين واليا على مصر من قبل أبي جعفر المنصور. وحجت ثلاثين حجة، وكانت تحفظ القرآن الكريم وتفسيره. وسمع عليها الإمام
_________
(١) الشقائق النعماية ٢١٤ وهو فيه: بابا نعمة الله ومنجد الأعلام ٧٠٧ وهدية العارفين ٢: ٤٩٨ ومعجم المطبوعات ١٨٤٩ والأزهرية ١: ٢٥٣ والأعلام ٩: ١٢ والتيمورية ٣: ٣٠٣ ومعجم المؤلفين ١٣: ١١١ وعثمانلي مؤلفلري ١: ٤٠ وكشف الظنون ١٨٩ و ١٢٩٢ وفي هامشه عند ذكر "الفواتح الإلهية": قيل: هو لمحيي الدين بن عربي.','2','703',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1283,'الشافعي، ولما مات أمرت بجنازته فأدخلت إليها فصلت عليه. وتوفيت في القاهرة. وهي صاحبة المشهد المعروف باسمها فيها. وللمصريين فيها اعتقاد عظيم (١).

الورداني [... - بعد ١٠٥ هـ / ... - بعد ٧٢٣ م]

<span data-type="title" id=toc-1772>نهشل بن سعيد بن وردان الورداني، أبو سعيد، ويقال أبو عبد الله الخراساني النيسابوري، ويقال الترمذي:</span>
محدث، بصري الأصل، سكن خراسان. روى عن الضحاك بن مزاحم المتوفى سنة ١٠٥ هـ، وآخرين. قال ابن حجر: روى عنه الثوري وهو من أقرانه، وأبو عمرو بن العلاء وهو أكبر منه". ونهشل متروك الحديث، ضغفه واتهمه بالكذب البخاري وإسحاق بن راهويه وأبو داود الطيالسي ويحيى بن معين وأبو حاتم وأبو زرعة والدارقطني وابن حبان والنسائي والحاكم .. ذكر له ابن النديم كتاب "تفسير القرآن" وقال: رواه عن
_________
(١) عبر الذهبي ١: ٣٥٥ وحسن المحاضرة ١: ٢١٨ وشذرات الذهب ٢: ٢١ والنجوم الزاهرة ٢: ١٨٥ والدر المنثور ٥٢١ ومرآة الجنان ٢: ٤٣ ووفيات الأعيان ٥: ٤٢٣ وخطط مبارك ٥: ١٣٥ وأعلام النساء ٥: ١٨٧ والأعلام ٩: ١٦.','2','704',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1284,'الضحاك بن مزاحم" (١).

الأحمد آبادي [١٠٦٤ - ١١٥٥ هـ / ١٦٥٤ - ١٧٤٢ م]

<span data-type="title" id=toc-1773>نور الدين بن محمد صالح الأحمد آبادي:</span>
من علماء العربية بالهند، مفسر، محدث، منطقي، مولده ووفاته في أحمد آباد. له نحو ١٥٠ تصنيفا في التفسير والحديث والعقائد وعلوم العربية والمنطق، أكثرها شروح وحواش، منها "تفسير القرآن" مختصر، و "التفسير الرباني" على أوائل سورة البقرة، و "التفسير النوراني للسبع المثاني" و "حاشية" على أنوار التنزيل، للبيضاوي، وغير ذلك. (٢)

الأصبهاني [... - ١٢١٢ هـ / ... - ١٧٩٧ م]

<span data-type="title" id=toc-1774>نور علي بن فيض علي شاه الطبسي (الأصبهاني)</span>
_________
(١) طبقات المفسرين للداودي ٢: ٣٤٦ والفهرست لابن النديم ٣٦ وميزان الاعتدال ٤: ٣٥٧ والتاريخ الكبير للبخاري ٢/ ٤: ١١٥ وتهذيب التهذيب ١٠: ٤٧٩.
(٢) أبجد العلوم ٩١١ وسبجة المرجان ٩٤ وهدية العارفين ١: ١٧٣ وهو فيه نور الدين أحمد بن محمد صالح ومثله في إيضاح المكنون ١: ١٣٩ و ٣٠٥ و ٣٠٩ ومعجم المؤلفين ٢: ١١١ (أحمد بن محمد) و ١٣: ١٢٢ (نور الدين بن محمد صالح. والأعلام ٩: ٢٩.','2','704',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1285,'الأصبهاني: مفسر، من مشايخ العلوية. قال البغدادي: ساح في أكثر البلاد، ثم سكن الموصل إلى أن توفي بها. من آثاره "تفسير سورة البقرة" (١).

التستري [٩٥٦ - ١٠١٩ هـ / ١٥٤٩ - ١٦١٠ م]

<span data-type="title" id=toc-1775>نور الله بن عبد الله (شريف الدين) ابن نور الله (ضياء الدين) بن محمد شاه المرعشي التستري (الشوشتري) من نسل الإمام زين العابدين:</span>
مجتهد، من علماء الإمامية، ينعت بالقاضي ضياء الدين. من أهل تستر. رحل إلى الهند، فولاه السلطان أكبر شاه قضاء القضاة بلاهور، واشترط عليه ألا يخرج في أحكامه عن المذاهب الأربعة، فاستمر إلى أن أظهر غير ذلك، فقتل تحت السياط في مدينة أكبر آباد. وقيل: قتل بسبب تأليفه كتاب "إحقاق الحق". له تصانيف كثيرة، منها |حاشية" على تفسير البيضاوي (٢).
_________
(١) هدية العارفين ٢: ٤٩٩ وإيضاح المكنون ١: ٣٠٦.
(٢) أمل الآمل ٢: ٣٣٦ وشهداء الفضيلة ١٧١ =','2','705',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1286,'الشرواني [... - ١٠٦٥ هـ / ... - ١٦٥٥ م]

<span data-type="title" id=toc-1776>نور الله بن محمد رفيع بن عبد الرحيم الشرواني:</span>
فقيه حنفي، مشارك في بعض العلوم. درس في "بروسة" وتوفي بها. من آثاره "حاشية" على أنوار التنزيل، في التفسير، للبيضاوي (١).
_________
= والذريعة ١: ٣٩١ وانظر فهرسته. وفوائد الرضوية ٦٩٦ وهدية العارفين ٢: ٤٩٨ ومصفى المقال ٤٨٥ ومفتاح الكنوز ١: ٢٨ والأعلام ٩: ٣٥ وفي الحاشية أسماء لمصادر أخرى.
(١) هدية العارفين ٢: ٩٩، والأعلام ٩: ٣٠ وعثمانلي مؤلفلري ٢: ٤٣.','2','705',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1287,'<span data-type="title" id=toc-1777>حرف الهاء</span>
ابن الهائم = أحمد بن محمد ٨١٥ هـ
ابن الهادي = محمد بن الهادي ... ٧٢٠ هـ
الهادي إلى الحق = يحيى بن الحسين ... ٢٩٨ هـ
ابن الهاشم = عبد الحق بن عبد الواحد ... ١٣٦٨ هـ
أبو الهذيل العلاف = محمد بن الهذيل ... ٢٣٥ هـ
الهروي = أحمد بن محمد ٤٠١ هـ
الهروي = حسين بن علي ... ٩١٠ هـ
الهروي = حيدر بن محمد بن إبراهيم ... ٨٥٤ هـ
الهروي = عبد بن محمد ٤٣٤ هـ
الهروي = عبد الله بن محمد ٤٨١ هـ
الهروي = عبد الملك بن علي ... ٤٨٩ هـ
الهروي = محمد بن آدم ... ٤١٤ هـ
الهروي = محمد بن عبد الله ... ٣٨١ هـ
أبو هريرة = عبد الرحمن بن صخر ... ٥٩ هـ
ابن هشام = عبد الله بن يوسف ... ٧٦١ هـ
الهكاري = أحمد بن أحمد ... ٧٦٣ هـ
ابن هلال = محمد بن علي ... ٩٣٣ هـ
أبو هلال العسكري = الحسن بن عبد الله ... ٣٩٥ هـ
ابن الهمام = محمد بن عبد الواحد ... ٨٦١ هـ
همت زاده = عبد الشكور بن عبد الله ... ١١٨٠ هـ
الهمذاني = إبراهيم بن حسين ... ١٠٢٦ هـ','2','707',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1288,'الهمذاني = الحسن بن الفتح ... بعد ٥٠٠ هـ
الهمذاني = رضا بن محمد أمين ... ١٢٤٧ هـ
الهمذاني = علي أكبر بن محمد ١٣٢٥ هـ
الهمذاني = (رشيد الدولة) فضل الله بن أبي الخير ... ٧١٦ هـ
الهمذاني = المنتجب بن أبي العز ... ٦٤٣ هـ
الهندي = محمد علي قلي ... ١٢٦٠ هـ
الهواري = عبد الرحمن بن موسى ... ق ٢ هـ
الهواري، انظر هود بن محكم هود بن محكم ... ق ٣ هـ
الهوريني = نصر بن الشيخ نصر ... ١٢٩١ هـ
الهيثم بن عدي بن عبد الرحمن ... ٢٠٧ هـ
الهيصم = الهيصم بن محمد ٤٦٧ هـ','2','708',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1289,'(هـ)

أخفش باب الجابية [٢٠١ - ٢٩٢ هـ / ٨١٦ - ٩٠٥ م]

<span data-type="title" id=toc-1778>هارون بن موسى بن شريك التغلبي، أبو عبد الله:</span>
شيخ القراء بدمشق، ومولده ووفاته فيها. يعرف بالأخفش الدمشقي، أو أخفش باب الجابية (من أحياء دمشق). قال السيوطي: "كان قيما بالقراءات السبع، عارفا بالتفسير والنحو والمعاني والغريب والشعر، وهو خاتمة الأخفشين من أهل دمشق، وعنه اشتهرت قراءة أهل الشام". (١).
_________
(١) غاية النهاية ٢: ٣٤٧ وبغية الوعاة ٢: ٣٢٠ ومعجم الأدباء ١٩: ٢٦٣ وطبقات المفسرين للداودي ٢: ٣٤٧ وشذرات الذهب ٢: ٢٠٩ ومرآة الجنان ٢: ٢٢٠ والأعلام ٩: ٤٥ ومعجم المؤلفين ١٣: ١٣٠ والنجوم الزاهرة ٣: ١٣٣.','2','709',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1290,'ابن عيسى [... - ١٢٩٢ هـ / ... - ١٨٧٥ م]

<span data-type="title" id=toc-1779>هاشم بن حسين بن عمر عيسى الشافعي:</span>
نحوي، مفسر، له اشتغال بالحديث واللغة. من أهل حلب، درس بها في المدرسة البهائية، ثم في الجامع الكبير وجامع العادلية. له تصانيف، منها "تعليقات في التفسير" (١).

البحراني [... - ١١٠٧ هـ / ... - ١٦٩٦ م]

<span data-type="title" id=toc-1780>هاشم بن سليمان بن إسماعيل بن عبد الجواد الحسيني الكتكاني التوبلي:</span>
فقيه إمامى، مفسر، عارف بالعربية
_________
(١) إعلام النبلاء ٧: ٣٦٨ والأعلام ٩: ٤٧.','2','709',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1291,'والرجال. نسبته إلى "توبلي" و "كتكان" من قرى البحرين، وتوفي ودفن بالأولى. له تصانيف كثيرة، قال صاحب روضات الجنات: "وكتبه مجرد جمع وتأليف، لم يتكلم في شيء منها على ترجيح في أقوال أو بحث أو اختيار مذهب، ولا أدري إن كان ذلك قصورا أم تورعا"، منها "البرهان في تفسير القرآن" طبع في مجلدين، و "الهادي وضياء النادي" في تفسير القرآن أيضا (١).

ابن سلامة [... - ٤١٠ هـ / ... - ١٠١٩ م]

<span data-type="title" id=toc-1781>هبة الله بن سلامة بن نصر بن علي، أبو القاسم:</span>
مقرىء، مفسر، نحوي، ضرير، من أهل بغداد، وبها وفاته. كانت له حلقة في جامع المنصور. وكان ضريرا، من أحفظ الأئمة للتفسير. من كتبه "الناسخ والمنسوخ" في القرآن، طبع: بهامش كتاب نزول القرآن للواحدي، سنة ١٣١٥ هـ. و "تفسير القرآن" (٢).
_________
(١) فوائد الرضوية ٧٠٥ وأمل الآمل ٢: ٣٤٩ وأنوار البدرين ١٣٧ وهدية العارفين ٢: ٥٠٣ ومصفى المقال ٤٨٩ والأعلام ٩: ٤٨ ومعجم المؤلفين ١٣: ١٣٢ وإيضاح المكنون ١: ١٧٩ و ٢: ٧١٦ ومعجم المطبوعات ١: ٥٣٢.
(٢) نكت الهميان ٣٠٢ وطبقات المفسرين للداودي ٢ =','2','710',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1292,'ابن البارزي [٦٤٥ - ٧٣٨ هـ / ١٢٤٨ - ١٣٣٨ م]

<span data-type="title" id=toc-1782>هبة الله بن عبد الرحيم بن إبراهيم ابن هبة الله بن المسلم بن هبة الله، أبو القاسم، شرف الدين ابن البارزي الجهني الحموي:</span>
قاض، حافظ للحديث، مفسر، انتهت إليه مشيخة الشافعية ببلاد الشام. من أهل حماة. حدث بها وبدمشق، وأفتى ودرس. وولي قضاء حماة مدة طويلة بلا أجر، وعين مرات لقضاء مصر فاستعفى. وحج مرات، وذهب بصره في كبره، ولما مات أغلقت حماه لمشهده. من تصانيفه الكثيرة "البستان في تفسير القرآن" طبع، و "روضات جنات المحبين في تفسير القرآن المبين" اثنا عشر مجلدا، و "الناسخ والمنسوخ" و "أسرار التنزيل" و "بديع القرآن" (١).
_________
=: ٣٤٧ وطبقات المفسرين للسيوطي ٤٢ وبغيه الوعاة ٢: ٣٢٣ وغاية النهاية ٢: ٣٥١ وتذكرة الحفاظ ٣: ١٠٥١ ومعجم الأدباء ١٩: ٢٧٥ وهدية العارفين ٢: ٥٠٤ وشذرات الذهب ٣: ١٩٢ وتاريخ بغداد ١٤: ١٧ والأعلام ٩: ٥٩ ومعجم المؤلفين ١٢: ١٣٨ وتاريخ التراث العربي ١: ٢١٩ وفيه: ويعرت كتابه بعنوان آخر "عقود العقيان في الناسخ والمنسوخ من القرآن". والنسخ في القرآن الكريم ١: ٣٢٨.
(١) طبقات المفسرين للداودي ٢: ٣٥٠ وطبقات السبكي ٦: ٢٤٨ ومفتاح السعادة ٢: ٣٦٧ وهدية =','2','710',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1293,'القفطي [٦٠٠ - ٦٩٧ هـ / ١٢٠٣ - ١٢٩٧ م]

<span data-type="title" id=toc-1783>هبة الله بن عبد الله بن سيد الكل، أبو القاسم، بهاء الدين القفطي:</span>
محدث، مفسر، أصولي، نحوي، متكلم، من فقهاء الشافعية. ولد بقفط (قيل: سنة ٥٩٧، وقيل ٦٠٠، وقيل ٦٠١، وقيل ٦٠٧ هـ) وتفقه بقوص، وولي فيها أمانة الحكم، ثم توجه إلى إسنا حاكما ومعيدا بالمدرسة العزية، فمدرسا، ثم ترك القضاء وعكف على العلم والعبادة. وتوفي بإسنا ودفن بالمدرسة المجدية. من كتبه "تفسير القرآن" وصل فيه إلى {كهيعص} أول سورة مريم (١).

البوصيري [٥٠٦ - ٥٩٨ هـ / ١١١٢ - ١٢٠١ م]

<span data-type="title" id=toc-1784>هبة الله (ويسمى أيضا بسيد الأهل) بن علي بن مسعود بن ثابت بن هاشم (الأنصاري الخزرجي، المنستيري الأصل، المصري المولد والدار، أبو القاسم البوصيري)</span>
العارفين ٢: ٥٠٧ والبدر الطالع ٢: ٣٢٤ والشذرات ٦: ١١٩ ومرآة الجنان ٤: ٢٩٧ والدرر الكامنة ٥: ١٧٤ والنجوم الزاهرة ٩: ٣١٥ وإيضاح المكنون ١: ١٨١ وكشف الظنون ٨٣ و ٩٢٢ وغاية النهاية ٢: ٣٥١ ونكت الهميان ٣٠٢ وفيه "وله تفسيران". والأعلام ٩: ٦٠.
_________
(١) الطالع السعيد ٦٩١ وطبقات السبكي ٨: ٣٩٠ وحسن المحاضرة ١: ٤٢٠ والشذرات ٥: ٤٣٩ وبغية الوعاة ٢: ٣٢٥ وهدية العارفين ٢: ٥٠٦ وكشف الظنون ٤٣٨ و ٤٥٧ وطبقات الداودي ٢: ٣٤٨.','2','711',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1294,'الأنصاري الخزرجي، المنستيري الأصل، المصري المولد والدار، أبو القاسم البوصيري: أديب، كاتب، محدث، حدث بالقاهرة والإسكندرية، وكان في آخر حياته مسند الديار المصرية وتوفي بمصر. من آثاره "مختصر في علم الناسخ والمنسوخ" مخطوط (١).

العلائي [... - ١٠٣٩ هـ / ... - ١٦٣٠ م]

<span data-type="title" id=toc-1785>هداية الله بن محمد العلائي الرومي:</span>
فقيه حنفي، أصولي، مفسر، من آثاره "تعليقة" على أنوار التنزيل، في التفسير، للبيضاوي (٢).

المشهدي [... - ١٢٤٨ هـ / ... - ١٨٣٢ م]

<span data-type="title" id=toc-1786>هداية الله بن مهدي الرضوي الخراساني المشهدي:</span>
مفسر إمامي. له
_________
(١) وفيات الأعيان ٩: ٦٧: والشذرات ٤: ٣٣٨ ومرآة الجنان ٣: ٤٠٩ والعبر ٤: ٣٠٦ والنجوم الزاهرة ٦: ١٨٢ وحسن المحاضرة ١: ١٥٨ والأعلام ٩: ٦٣.
(٢) كشف الظنون ١٩٢ وهدية العارفين ٢: ٥٠٧ وعنهما أخذت تاريخ وفاته. ومعجم المؤلفين ١٣: ١٤٥ وفيه وفاته سنة ١١٣٢ هـ، ومصدره الكشف والهدية.','2','711',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1295,'"تفسير" أنجز منه عشرة أجزاء من أول القرآن، وعشرة من آخره (١).

هشام بن علي [... - ... هـ / ... - ... م]

<span data-type="title" id=toc-1787>هشام بن علي بن هشام:</span>
ذكره ابن النديم تحت عنوان الكتب المصنفة في ناسخ القرآن ومنسوخه، ولم يزد على ذلك (٢).

السلمي [١٥٣ - ٢٤٥ هـ / ٧٧٠ - ٨٥٩ م]

<span data-type="title" id=toc-1788>هشام بن عمار بن نصير بن ميسرة بن أبان السلمي، ويقال الظفري، أبو الوليد:</span>
خطيب دمشق ومقرئها ومحدثها ومفتيها، قاض، رحل في طلب العلم، وحدث عن مالك بن أنس والوليد بن مسلم وآخرين، وحدث عنه البخاري وأبو داود والنسائي وابن ماجه، وروى الترمذي عن البخاري عنه. قال في "تهذيب التهذيب": "كان مشتهرا بالعقل والفصاحة والرواية والعلم
_________
(١) هدية العارفين ٢: ٥٠٧ والأعلام ٩: ٦٩
(٢) طبقات المفسرين ٢: ٣٥٢ عن الفهرست لابن النديم ٤٠.','2','712',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1296,'والدراية ..) له كتاب "فضائل القرآن" (١).

الواسطي [١٠٤ - ١٨٣ هـ / ٧٢٢ - ٧٩٩ م]

<span data-type="title" id=toc-1789>هشيم بن بشير بن أبي خازم قاسم بن دينار السلمي، أبو معاوية، الواسطي، نزيل بغداد:</span>
مفسر، فقيه، من ثقات المحدثين. كان محدث بغداد، حدث عنه شعبة ويحيى القطان وزياد بن أيوب وأحمد بن حنبل وغيرهم. قال الإمام أحمد: لزمته أربع سنين ما سألته عن شيء إلا مرتين هيبة له "وقال يعقوب الدورقي: كان عند هشيم عشرون ألف حديث". وقال الذهبي: لا نزاع في أنه كان من الحفاظ الثقات إلا أنه كثير التدليس، فقد روى عن جماعة لم يسمع منهم". من آثاره "تفسير القرآن". قال فؤاد سزكين: هو أحد مراجع الطبري والذي استخدمه أيضا
_________
(١) طبقات القراء للذهبي ١: ١٦٠ وطبقات المفسرين للداودي ٢: ٣٥٢ والنجوم الزاهرة ٢: ٣٢١ وغاية النهاية ٢: ٣٠٤ وتذكرة الحفاظ ٢: ٤٥١ وميزان الاعتدال ٤: ٣٥٢ والعبر ١: ٤٤٥ وتهذيب التهذيب ١١: ٥٥ وشرحا ألفية العراقي ١: ٧٧ والشذرات ٢: ١٠٩ والأعلام ٨: ٨٧.','2','712',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1297,'في تفسيره وفي تاريخه برواية يعقوب بن إبراهيم الدورقي. وأما الثعلبي فقد استخدمه برواية زياد بن أيوب، (١).

الخوارزمي [... - ٨١٩ هـ / ... - ١٤١٦ م]

<span data-type="title" id=toc-1790>همام بن أحمد الخوارزمي، همام الدين:</span>
نحوي، مفسر، من أهل خوارزم، وبها نشأ وتعلم. ثم انتقل إلى حلب، فالقاهرة، وولي بها مشيخة مدرسة جمال الدين الاستادار ودرس بها التفسير من أول "الكشاف" إلى قوله تعالى: {فلما قضينا عليه الموت ...} (سورة سبأ: ١٤) فمات بعد أيام بالطاعون (٢).

هود بن محكم [القرن الثالث الهجري / القرن التاسع الميلادي]

<span data-type="title" id=toc-1791>هود بن محكم الهواري الأوراسي:</span>
_________
(١) تهذيب التهذيب ١١: ٥٩ وتذكرة الحفاظ ١: ٢٤٨ وتاريخ بغداد ١٤: ٨٥ وابن النديم ٢٢٨ وميزان الاعتدال ٤: ٣٠٦ وطبقات المفسرين ٢: ٣٥٢ وتاريخ التراث العربي ١: ٢٠٢ وكشف الظنون ٤٦١ وهدية العارفين ٢: ٥١٠ وطبقات المدلسين ١٨ والأعلام ٩: ٨٩.
(٢) إنباء الغمر ٣: ٨١٩ وفيه: وقد يدعى محمدا أيضا". وبغية الوعاة ٢: ٣٢٩ وطبقات المفسرين للداودي ٢: ٣٥٤ وشذرات الذهب ٧: ١٤٣ والضوء اللامع ٧: ١٢٨ (محمد بن أحمد) و ١٠: ٢٠٩ (همام).','2','713',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1298,'مفسر من علماء الإباضية. كان والده قاضيا بتيهرت على عهد الإمام أفلح بن عبد الوهاب (١٩٠ - ١٤٠ هـ). عاش هود في النصف الثاني من القرن الثالث الهجري. له "تفسير القرآن" مخطوط، خاص بالإباضية، يقوم أحد كبار باحثيهم على مراجعته ونشره (١).

الهيثم ابن عدي [١١٤ - ٢٠٧ هـ / ٧٣٢ - ٨٢٢ م]

<span data-type="title" id=toc-1792>الهيثم بن عدي بن عبد الرحمن بن زيد ابن أسيد الثعلى الطائي البحتري الكوفي، أبو عبد الرحمن:</span>
مؤرخ، عالم بالأدب والنسب، راوية، أصله من "منبج". ولد بالكوفة، ونقل من كلام العرب وعلومها وأشعارها ولغاتها الشيء الكثير، واختص بمجالسة المنصور والمهدي والهادي والرشيد، وروى عنهم. وهو عند علماء الحديث من المدلسين، ومن غير الثقات. ولم يكن من أهل هذا الشأن. وكان يتعرض لأحوال الناس وأخبارهم فيرويها على وجهها ويشيع ما كتموا، فكره لذلك، وطعن في نسبه، وهجاه الشعراء. وقيل: كان
_________
(١) الجواهر لبرادي ٢١٩ وتاريخ التراث العربي ١: ٢٠٨ ومعجم أعلام الجزائر للمؤلف.','2','713',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1299,'يرى رأي الخوارج. مات في "فم الصلح" قرب واسط عند الحسن بن سهل السرخسي (١٦٦ - ٢٣٦ هـ). من كتبه "لغات القرآن" (١).
_________
(١) معجم الأدباء ١٩: ٣٠٤ ولسان الميزان ٦: ٢٠٩ وميزان الاعتدال ٤: ٣٢٤ وإنباه الرواة ٣: ٣٦٥ والنجوم الزاهرة ٢: ١٨٤ ووفيات الأعيان ٦: ١٠٦ وابن النديم ٩٩ وتاريخ بغداد ١٤: ٥٠ ومرآة الجنان ٢: ٣٢ وهدية العارفين ٢: ٥١١ وطبقات الداودي ٢: ٣٥٤.','2','714',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1300,'الهيصم [... - ٤٦٧ هـ / ... - ١٠٧٤ م]

<span data-type="title" id=toc-1793>الهيصم بن محمد بن عبد العزيز الهيصم:</span>
مفسر، لغوي، من أهل نيسابور. قال صاحب السياق: تصدر للتدريس وللتعليم، وكان عالما بالتفسير، ذا معرفة بالأحاديث والأسانيد، فارس ميدان البلاغة نظما ونثرا، كثير الحفظ لأصول العربية، أملى بنيسابور" (١).
_________
(١) طبقات المفسرين للداودي ٢: ٣٥٥.','2','714',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1301,'<span data-type="title" id=toc-1794>حرف الواو</span>
