الوادي آشي = عبد الصمد بن عبد الرحمن ... ٦١٩ هـ
الوادي آشي = محمد بن أحمد ... ٦٩٤ هـ
الوارثي = أحمد بن عبد الرحمن ... ١٠٤٥ هـ
الوارداري = عبد الكريم الوارداري ... ١٠٠٣ هـ
الواسطي = صدقة بن حسين ... ٥٥٧ هـ
الواسطي = عبد الوهاب بن علي ... ٤٢٤ هـ
الواسطي = محمد بن حسن ... ٧٧٦ هـ
الواسطي = محمد بن زيد ... ٣٠٩ هـ
الواسطي = هشيم بن بشير ... ١٨٣ هـ
الواسطي = يحيى بن عبد الله ... ٧٣٨ هـ
الواسطي = يزيد بن هارون ... ٢٠٦ هـ
واصل بن عطاء الغزال ... ١٣١ هـ
الواعظ = مصطفى بن محمد ١٣٣١ هـ
الواقدي = محمد بن عمر ... ٢٠٧ هـ
الوانوغي = محمد بن أحمد ... ٨١٩ هـ
الواني = محمد بن بسطام ... ١٠٩٦ هـ
الواني = محمد بن نوح ... ١١٢٦ هـ
وجدي، انظر محمد فريد وجدي
الوحيد البهبهاني = أحمد بن محمد ١٢٤٣ هـ','2','715',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1302,'وحيي زاده = محمد بن أحمد ... ١٠١٨ هـ
ابن ورد = أحمد بن محمد ٥٤٠ هـ
الورد = عبد الله بن عبد الولي ... قبل ١١٧٣ هـ
الورداني = نهشل بن سعيد ... حيا ١٠٥ هـ
الورنوسفادراني = محمد حسن بن محمد كاظم ... حيا ١٢٤١ هـ
الوزان = محمد بن علي ... نحو ٤١٠ هـ
ابن الوزير = محمد بن إبراهيم ... ٨٤٠ هـ
ابن الوزير = محمد بن طاهر ... ٣٦٥ هـ
الوزير المغربي = الحسين بن علي ... ٤١٨ هـ
ابن وفا = علي بن محمد ٨٠٧ هـ
الوفراوندي = يونس بن محمد قبل ٤٣٨ هـ
وكيع بن الجراج بن مليح ... ١٩٧ هـ
ابن أم ولد = عبد الأول بن حسين ... ٩٥٠ هـ
أبو الوليد الباجي، أنظر الباجي، سليمان بن خلف
ابن ولي الدين = عبد الكريم بن ولي الدين ... ١١٠٠ هـ
ابن وهب = عبد الله بن وهب ... ١٩٧ هـ
وهب بن منبه ... ١١٤ هـ
الوهراني = علي بن عبد الله ... ٦١٥ هـ','2','716',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1303,'(و)

واصل ابن عطاء [٨٠ - ١٣١ هـ / ٧٠٠ - ٧٤٨ م]

<span data-type="title" id=toc-1795>واصل بن عطاء الغزال، أبو حذيفة:</span>
إمام المعتزلة، ومؤسس مذهبهم في العدل والتوحيد، ومن أئمة البلغاء والمتكلمين. ولد بالمدينة، ونشأ بالبصرة، وانتظم في حلقة الحسن البصري، فلما ظهر الاختلاف وقالت الخوارج بتكفير مرتكب الكبائر، وقالت الجماعة بأنهم مؤمنون وإن فسقوا بالكبائر، خرج واصل عن الفريقين، وقال: "إن الفاسق من هذه الأمة لا مؤمن ولا كافر، بل هو بمنزلة بين المنزلتين، فطرده الحسن من مجلسه، فاعتزل عنه، وجلس إلى عمرو بن عبيد، فقيل لهما ولأتباعهما معتزلون، ومنهم طائفة تنسب إليه تسمى','2','717',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1304,'(الواصلية). وعمل على نشر مذهب الاعتزال فبعث من أصحابه عبد الله بن الحارث إلى المغرب، وحفص بن سالم إلى خراسان، والقاسم إلى اليمن، وأيوب إلى الجزيرة، والحسن بن ذكوان إلى الكوفة، وعثمان الطويل إلى أرمينية. قال ابن حجة الحموي في كتابه "ثمرات الأوراق": "وما زال مذهبهم ينمو إلى أيام الرشيد، فوضعه موضع البحث بين العلماء، ولما ولي المأمون ناصر المعتزلة وعاقب مخالفيهم، وتابعه المعتصم ثم الواثق. ولما كانت أيام المتوكل كتب إلى الآفاق بمخالفة القائلين في الاعتزال، وضعف شأن المعتزلة حتى ذهبت بمذهبهم الأيام .. ". وكان واصل يلثغ بالراء فيجعلها غينا، فتجنب الراء في خطابه، قال الجاحظ: "رام أبو حذيفة','2','717',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1305,'إسقاط الراء من كلامه، وإخراجها من حروف منطقه، فلم يزل يكابد ذلك ويغالبه، ويناضله ويساجله، ويتأنى لستره والراحة من هجنته، حتى انتظم له ما حاول، واتسق له ما أقل". ولم يكن غزالا، وإنما لقب به لأنه كان يلازم الغزالين ليعرف المتعففات من النساء فيجعل صدقته لهن. له كتب منها "معاني القرآن" (١).
_________
(١) طبقات المفسرين للداودي ٢: ٣٥٦ وأمالي المرتضى (طبقة الحلبي) ١: ١٦٣ وميزان الاعتدال ٤: ٣٢٩ وطبقات المعتزلة لابن المرتضى ٢٨ والبيان والتبيين ١: ١٤ وانظر فهرسته، ومعجم الأدباء ١٩: ٢٤٣ ولسان الميزان ٦: ٢١٤ ووفيات الأعيان ٦: ٧ ومختصر الفرق ٩٧ ومرآة الجنان ١: ٢٧٤ والنجوم الزاهرة ١: ٣١٣ والفرق بين الفرق ١١٧ ومقاتل الطالبين ٢٩٣ والكامل للمبرد ٣: ١٩٢ والفهرست لابن النديم ٢٠٢ والموسوعة العربية الميسرة ١٩٣٩ وتاريخ آداب اللغة العربية ٢: ٢٤٣ والعقيدة والشريعة في الإسلام لجولدتسيهر ٩٠ والمعتزلة لـ "ت. و. أرنولد" ١٨ وضحى الإسلام ج ٣ وشذرات الذهب ١: ١٨٢ ورغبة الآمل ٧: ٧٨، ١١٤، ١١٦ وتاريخ الإسلام للذهبي ٥: ٣١١ والتفسير والمفسرون ١: ٣٦٨ وتبيين كذب المفتري، مقدمته ١٠ - ١١ وتاريخ الجدل لأبي زهرة، وتاريخ التراث العربي ٢: ٣٥٩ ومجلة الأزهر ٢٨: ٣٦ وروضات الجنات ٤: ٢٣٠ ومروج الذهب ٤: ٢٢ وهدية العارفين ٢: ٤٩٩ ومعجم المؤلفين ١٣: ١٥٩.','2','718',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1306,'الكجراتي [٩١١ - ٩٩٨ هـ / ١٥٠٥ - ١٥٩٠ م]

<span data-type="title" id=toc-1796>وجيه الدين العلوي الكجراتي:</span>
متكلم، أصولي، فرضي، عارف بالحديث والتفسير، ولد في "جابانير" من بلاد كجرات بالهند، وتعلم وأقام ومات في كجرات. من كتبه "حاشية، على أنوار التنزيل، في التفسير، للبيضاوي (١).

اليشكري [... - حوالي ١٦١ هـ / ... - حوالي ٧٧٨ م]

<span data-type="title" id=toc-1797>ورقاء بن عمر بن كليب اليشكري، ويقال الشيباني، أبو بشر الكوفي، نزيل المدائن:</span>
محدث، مفسر، يقال أصله من مرو. نعته الذهبي بالإمام الحجة شيخ السنة، وقال: توفي سنة نيف وستين ومائة". وسئل أحمد بن حنبل: ورقاء أحب إليك في تفسير ابن أبي نجيح أو شيبان؟ قال: كلاهما ثقة وورقاء أوثقهما، إلا أنهم يقولون لم يسمع التفسير كله، يقولون بعضه عرض" وسئل يحيى ابن معين: أيما أحب إليك تفسير ورقاء
_________
(١) أبجد العلوم ٨٩٦ وسبحة المرجان ٤٥ والأعلام ٩: ١٢٤.','2','718',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1307,'أو تفسير شيبان وسعيد عن قتادة؟ قال: تفسير ورقاء لأنه عن ابن أبي نجيح عن مجاهد" وقال ورقاء نفسه: "كتاب التفسير قرأت نصفه على ابن أبي نجيح وقرأ علي نصفه". من آثاره "تفسير القرآن" قال فؤاد سزكين: "وتفسيره الذي يعتمد في الأصل على تفسير مجاهد في رواية ابن أبي نجيح، كان تفسيرا طيبا، استخدمه الطبري بالرواية الآتية: حدثني الحارث بن محمد ابن أبي أسامة (المتوفى سنة ٢٨٢ هـ / ٨٩٥ م) قال: حدثنا الحسن الأشيب (المتوفى سنة ٢٥٨ هـ / ٨٢٣ م) قال: حدثنا ورقاء، وهذا التفسير كان أيضا أحد مراجع الثعلبي في كتابه "الكشف والبيان" (١).

أبو الوفاء العرضي [٩٩٣ - ١٠٧١ هـ / ١٥٨٥ - ١٦٦١ م]

<span data-type="title" id=toc-1798>أبو الوفاء بن عمر بن عبد الوهاب بن إبراهيم بن محمود بن علي العرضي:</span>
مفتي حلب وابن مفتيها، مؤرخ، نحوي، أديب، مفسر، صوفي، من
_________
(١) تذكرة الحفاظ ٢٣٠ وتهذيب التهذيب ١١: ١١٣ وغايه النهاية ٢: ٣٥٨ واللباب ٣: ٤١٤ وتاريخ التراث العربي ١: ٢٠٠.','2','719',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1308,'فقهاء الشافعية، له نظم حسن. من أهل حلب مولدا ووفاة. من آثاره "حاشية" على أنوار التنزيل، في التفسير للبيضاوي (١).

وكيع بن الجراح [١٢٩ - ١٩٧ هـ / ٧٤٦ - ٨١٢ م]

<span data-type="title" id=toc-1799>وكيع بن الجراح بن مليح الرؤاسي، أبو سفيان:</span>
حافظ ثبت، محدث العراق في عصره. ولد في الكوفة، وسمع هشام ابن عروة وابن جريج والأوزاعي وغيرهم. وأراد هارون الرشيد أن يوليه قضاء الكوفة، فامتنع ورعا. وتركز عمله العلمي في تصنيف الحديث. ويقال إنه كان ذا ذاكرة ممتازة، فكان يحاضر دون أن ينظر في كتاب أمامه. قال الإمام أحمد بن حنبل: ما رأيت أحدا أوعى منه ولا أحفظ، وكيع إمام المسلمين". والرؤاسي نسبة إلى رؤاس وهو بطن من قيس عيلان. توفي بفيد راجعا من الحج. له كتب، منها "تفسير القرآن" رواه عنه محمد بن إسماعيل الحساني. واستخدمه
_________
(١) إعلام النبلاء ٦: ٣٠٨ وخلاصة الأثر ١: ١٤٨ وهدية العارفين ٢: ٢٨٨ ومعجم المؤلفين ١٣: ١٦٥ وتاريخ آداب اللغة العربية ٣: ٣٢٠ وانفرد الزركلي بتسميته محمد بن عمر (الاعلام ٧: ٢٠٨).','2','719',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1309,'الثعلبي في الكشف والبيان (١).

البكائي [... - ١١٨٣ هـ / ... - ١٧٦٩ م]

<span data-type="title" id=toc-1800>ولي الدين بن خليل البكائي الرومي:</span>
فقيه حنفي، مفسر، من أهل القسطنطينية، وتوفي بها. من آثاره "الأحاديث الأربعون في بيان فضائل سورة الإخلاص" قال صاحب إيضاح المكنون: "أوله، الحمد لله الذي بين ذاته بالأحد" (٢).

جار الله الرومي [... - ١١٥١ هـ / ... - ١٧٣٨ م]

<span data-type="title" id=toc-1801>ولي الدين بن مصطفى الينيشهري، (القسطنطيني، أبو عبد الله، الملقب جار الله الرومي)</span>
_________
(١) تذكرة الحفاظ ١: ٣٠٦ وتاريخ التراث العربي ١: ٢٧٣ وتاريخ بغداد ١٣: ٤٦٦ وحلية الأولياء ٨: ٣٦٨ والعبر ١: ٣٢٤ وميزان الاعتدال ٤: ٣٣٥ وطبقات ابن أبي يعلى ١: ٣٩١ والجواهر المضية ٢: ٢٠٨ والرجال للقيسراني ٥٤٦ والتاريخ الكبير للبخاري ٤ ق ٢: ١٧٩ وابن النديم ٢٢٦ والطبقات الكبرى ٦: ٣٩٤ والمشاهير لابن حبان ١٧٣ والأعلام ٩: ١٣٥ وطبقات المفسرين للداودي ٢: ٣٥٧ والشذرات ١: ٣٤٩ وكشف الظنون ٤٦١.
(٢) عثمانلي مؤلفلري ١: ٤٦ وهدية العارفين ٢: ٥٠١ والأعلام ٩: ١٣٧ وبروكلمان، الذيل ٢: ٩٤٦ وإيضاح المكنون ١: ٣٠٦ ومعجم المؤلفين ١٣: ١٦٨.','2','720',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1310,'القسطنطيني، أبو عبد الله، الملقب جار الله الرومي: فقيه حنفي، أصولي، مفسر، ولد في يني شهر، وجاور بمكة سبع سنوات، وسكن القسطنطينية، فبنى فيها مدرسة ومكتبة قرب مسجد الفاتح. ولما توفي دفن بالمدرسة، ونقلت المكتبة إلى جامع السلطان بايزيد. من كتبه "حاشية" على تفسير البيضاوي (١).

البوني [... - ٣١٠ هـ / ... ٩٢٢ م]

<span data-type="title" id=toc-1802>الوليد بن أبان بن بونة الأصبهاني، أبو العباس:</span>
من حفاظ الحديث الثقات، مفسر، من أهل أصبهان. نسبته إلى جده بونة. سمع أحمد بن الفرات، وأسيد بن عاصم وغيرهما. حدث عنه الطبراني وأبو الشيخ، وأهل أصبهان. من كتبه "تفسير القرآن" (٢).
_________
(١) هدية العارفين ٢: ٥٠١ وعثمانلي مؤلفلري ١: ٢٦٧ والأعلام ٩: ١٣٧ وبروكلمان، الذيل ٢: ٨٥٤.
(٢) تبصير المنتبه ١: ١١١ واللباب ١: ١٨٨ وتاريخ أصبهان ٢: ٣٣٤ ومرآة الجنان ٢: ٢٥٠ وتذكرة الحفاظ ٣: ٧٨٤ والشذرات ٢: ٢٦١ وهدية العارفين ٢: ٥٠٠. والمشتبه ١: ١١٤ وطبقات المفسرين ٢: ٣٦٠ ومعجم المؤلفين ١٣: ١٦٩ والإكمال لابن ماكولا ١: ٣٧١ وإيضاح المكنون ٢: ٤٨٣ والأعلام ٨: ١١٩.','2','720',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1311,'وهب بن منبه [٣٤ - ١١٤ هـ / ٦٥٤ - ٧٣٢ م]

<span data-type="title" id=toc-1803>وهب بن منبه الأبناوي الصنعاني الذماري، أبو عبد الله:</span>
عالم أهل اليمن، مؤرخ، كثير الإخبار عن الكتب القديمة، عارف بأساطير الأولين ولا سيما الإسرائيليات. يعد في التابعين. أصله من أبناء الفرس الذين بعث بهم كسرى إلى اليمن. وفي "تاريخ العرب قبل الإسلام" "يقال أنه من أصل يهودي، وكان يزعم أنه يتقن اليونانية والسريانية والحميرية؛ ويحسن قراءة الكتابات القديمة". ولد ومات بصنعاء، وولاه عمر بن عبد العزيز قضاءها. وكان يقول: سمعت اثنين وتسعين كتابا كلها أنزلت من السماء، اثنان وسبعون منها في الكنائس، وعشرون في أيدي الناس لا يعلمها إلا قليل، ووجدت في كلها أن من أضاف إلى نفسه شيئا من المشيئة فقد كفر". واتهم بالقدر، ورجع عنه. ويقال: ألف فيه كتابا ثم ندم عليه. وحبس في كبره وامتحن. وفي وفاته خلاف، قيل: سنة ١١٥ و ١١٤ و ١٢٥ وقال المناوي: عن نحو ثمانين سنة، وقال ابن خلكان: عن تسعين. وله ترجمة طويلة في تاريخ دمشق. ذكر له صاحب "هدية العارفين" كتاب "تفسير القرآن".','2','721',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1312,'واكتفى صاحب كشف الظنون بالقول: "تفسير وهب". وأقواله منثورة في كتب التفسير ككتاب الطبري، وكتب الأدب كعيون الأخبار والمعارف وغير ذلك (١).
_________
(١) تذكرة الحفاظ ١٠٠ وتهذيب التهذيب ١١: ١٦٦ وتهذيب الأسماء واللغات ٢: ١٤٩ وتاريخ الإسلام للذهبي ٥: ١٤ ومعجم الأدباء ١٩: ٢٥٩ وطبقات ابن سعد ٥: ٥٤١ ومرآة الجنان: ١: ٢٤٨ وحلية الأولياء،: ٢٣ وشذرات الذهب ١: ١٥٠ وطبقات الخواص ١٦١ والكواكب الدرية ١: ١٧٨ وهدية العارفين ٢: ٥٠١ وميزان الاعتدال ٤: ٣٥٢ وابن خلكان ٦: ٣٥ وتاريخ العرب قبل الإسلام ١: ٤٤ وذيل المذيل ٩٥ وكشف الظنون ٤٦١: والأعلام ٩: ١٥٠ والموسوعة العريبة ١٩٦٩ ومعجم المؤلفين ١٣: ١٧٤.','2','721',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1313,'<span data-type="title" id=toc-1804>حرف الياء</span>
اليابري = عبد الله بن طلحة ... ٥١٨ هـ
ابن اليازجي = اسماعل بن عبد الباقي ... ١١٢١ هـ
الياسري = الحسن بن علي ... ٦٢٢ هـ
الياسري = عثمان بن مقبل ... ٦١٦ هـ
اليافعي = عبد الله بن أسعد ... ٧٦٨ هـ
اليحصبي، انظر القاضي عياض يحيى بن أكتم ... ٢٤٢ هـ
اليزدي = علي أكبر بن محمد ... حيا ١٢٢٨ هـ
يزيد بن هارون ... ٢٥٦ هـ
اليزيدي = إبراهيم بن يحيى ... ٢٢٥ هـ
اليزيدي = عبد الله بن يحيى ... ٢٣٧ هـ
اليزيدي = محمد بن العباس ... ٣١٠ هـ
اليشكري = ورقاء بن عمر ... ١٦١ هـ
أبو يعلى = أنظر ابن الفراء
ابن يعمر، انظر العدواني
يعقوب عفوي = يعقوب بن مصطفى ... ١١٤٩ هـ
ابن يعيش = الحسن بن محمد ٧٩١ هـ
اليغرفي = يحيى بن سلطان ... ق ٧ هـ
اليماني = علي بن محمد ٨٣٧ هـ
اليماني = محمد بن نور ... ٢١١ هـ
اليملاحي = أحمد بن محمد ١٣٥٨ هـ','2','723',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1314,'اليمني، انظر الجلال اليمني
يوسف زاد = عبد الله بن محمد ١١٦٧ هـ
أبو يوسف = يعقوب بن إبراهيم ... ١٨٢ هـ
ابن اليونانية = محمد بن علي ... ٧٩٣ هـ
ابن يونس = محمد بن طاهر ... ٨٣٣ هـ
ابن يونس = موسى بن يونس ... ٦٣٩ هـ
يونس بن جب الضبي، النحوي ... ١٨٢ هـ يونس بن عبد الرحمن ... ٢٠٨ هـ','2','724',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1315,'(ي)

السيراوندية [... - ٥٠٢ هـ / ... - ١١٠٨ م]

<span data-type="title" id=toc-1805>ياسمينة بنت سعد بن محمد السيراوندي:</span>
واعظة، مفسرة، من أهل سيراوند (من قرى همذان). قال في معجم البلدان: سمعت من مشايخ همذان والغرباء، وكانت واعظة ترجع إلى فضل من التفسير والأدب والخط، ثم تركت الوعظ وحجت وجلست في بيتها سنين، وكانت حسنة السيرة صدوقة" (١).

ابن آدم [... - ٢٠٣ هـ / ... - ٨١٨ م]

<span data-type="title" id=toc-1806>يحيى بن آدم بن سليمان الأموي (القرشي المخزومي، مولى آل أبي معيط، أبو زكرياء، وينعت بالأحول)</span>
_________
(١) معجم البلدان ٣: ٢٩٥ (سيراوند) وأعلام النساء ٥: ٢٩٥.','2','725',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1316,'القرشي المخزومي، مولى آل أبي معيط، أبو زكرياء، وينعت بالأحول: من ثقات أهل الحديث، مقرىء، فقيه، من أهل الكوفة. وثقه ابن معين والنسائي، وقال أبو داود: "ذاك أوحد الناس". مات بفم الصلح من قرى واسط. من كتبه "مجرد أحكام القرآن" (١).

ابن مزين [... - ٢٥٩ هـ / ... - ٨٧٣ م]

<span data-type="title" id=toc-1807>يحيى بن إبراهيم بن مزين، أبو زكريا:</span>
عالم بلغة الحديث ورجاله من فقهاء
_________
(١) غاية النهاية ٢: ٣٦٤ وخلاصة تهذيب الكمال ٣٦١ وابن النديم ٢٢٧ وتذكرة الحفاظ ٣٥٩ وشذرات الذهب ٢: ٨ ومرآة الجنان ٢: ١٠ وهدية العارفين ٢: ٥١٤ وتهذيب التهذيب ١١: ١٧٥ وطبقات المفسرين ٢: ٣٦٠ والأعلام ٨: ١٣٣. ومعجم المطبوعات: ٢٦٠.','2','725',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1317,'المالكية. أصله من طليطلة، انتقل إلى قرطبة، فأكرمه أميرها عبد الرحمن وابتنى له دارا، ووصله بصلة جزيلة. ورحل إلى المشرق، وحج، ودخل العراق، وسمع بمصر. وولي قضاء طليطلة. قال ابن لبابة: "هو أفقه من رأيت في علم مالك وأصحابه". مات بقرطبة. من كتبه "فضائل القرآن" (١).

ابن الرقيعة [... - ٣٠٣ هـ / ... - ٩١٦ م]

<span data-type="title" id=toc-1808>يحيى بن إسحاق بن يحيى بن أحمد بن يحيى الليثي، أبو إسماعيل، ويعرف بابن الرقيعة:</span>
فقيه مالكي محدث، لغوي، عارف بالتفسير، من أهل قرطبة. روى عن أبيه، عن جده، ورحل فسمع بإفريقية (تونس) وبمصر والعراق. قال صاحب الديباج. "وشوور في الأحكام، وكان متصرفا في العربية، واللغة، والتفسير، وألف الكتب المبسوطة في اختلاف أصحاب مالك وأقواله .. " مات بقرطبة (٢).
_________
(١) تاريخ علماء الأندلس ٢: ١٨١ وجذوة المقتبس ٣٧٣ وبغة الملتمس ٤٨٢ والديباج المذهب ٢: ٣٦١ وهو فيه: يحيى بن زكريا بن إبراهيم. وطبقات المفسرين ٢: ٣٦٧.
(٢) الديباج المذهب ٢: ٣٥٧ وطبقات المفسرين للداودي ٢: ٣٦٢ وجذوة المقتبس ٣٧٣ وبغية الملتمس ٤٨٣ وشجرة النور ١: ٧٧.','2','726',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1318,'يحيى بن أكثم [١٥٩ - ٢٤٢ هـ / ٧٧٥ - ٨٥٧ م]

<span data-type="title" id=toc-1809>يحيى بن أكثم بن محمد بن قطن التميمي الأسيدي المروزي، أبو محمد:</span>
قاض، رفيع القدر، عالي الشهرة، واسع العلم والفقه، كثير الأدب، حسن المحاضرة، يتصل نسبه بأكثم بن صيفي (ت: ٩ هـ) حكيم العرب المعروف. ولد بمرو، وسمع من ابن المبارك وطبقته، واتصل بالمأمون العباسي أيام مقامه بمرو، فولاه قضاء البصرة (سنة ٢٠٢ هـ) ثم عزل عنه بالقاضي إسماعيل بن حماد (سنة ٢١٠ هـ). وأقام يحيى في بغداد، بعد عزله، فعرف المأمون ما فيه من علم وذكاء وحزم "فأدناه إليه وقربه منه وخصه برعايته وعطفه حتى غلب عليه دون الناس جميعا، ثم ولاه (سنة ٢١٣ هـ) قضاء القضاة ببغداد، وأضاف إليه تدبير مملكته، فأصبح صاحب الكلمة العليا والأمر النافذ في الدولة، وكان الوزراء لا يقدمون ولا يؤخرون في شيء إلا بعد عرضه عليه. ولم يزل في هذه الرعاية من المأمون والحظوة لديه حتى كانت سنة ٢١٦ هـ، فوجهه إلى بعض جهات الروم، فعاد ظافرا. وفي نفس السنة دخل المأمون','2','726',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1319,'مصر، وكان يحيى في حاشيته، فغضب عليه وأعاده إلى بغداد، وكتب إلى ولي عهده المعتصم يحذره منه ومن أمثاله. ومات المأمون بعد سنتين، وصلي المعتصم، فعزل يحيى عن القضاء، فلزم بيته إلى أن ولي المتوكل على الله، فأعاده إلى ولاية القضاء، وخلع عليه، ثم غضب عليه وعزله سنة ٢٤٠ هـ وأخذ أمواله، وألزم منزله. وعزم على الحج والمجاورة بمكة، فرحل إليها ومعه أخته، فبلغه أن المتوكل صفا عليه، فانقلب راجعا، فلما كان بالربذة (من قرى المدينة) مرض وتوفي فيها، ودفن هناك. وكان يتهم بأمور شاعت عنه وتناقلها الناس في أيامه، فذكر شيء منها للإمام أحمد بن حنبل، فقال: سبحان الله، سبحان الله، ومن يقول هذا؟ وأنكر ذلك إنكارا شديدا. وقال الخطيب البغدادي: "وكان يحيى سليما من البدعة، ينتحل مذهب أهل السنة، وسئل ابن حنبل عنه فقال: ما عرفناه ببدعة". وفي تاريخ بغداد أن كتبه في الفقه أجل كتب، تركها الناس لطولها. من كتبه "إيجاب التمسك بأحكام القرآن" (١).
_________
(١) أخبار القضاة ٢: ١٦١ وتاريخ بغداد ١٤: ١٩١ وطبقات المفسرين للداودي ٢: ٣٦٢ وخلاصة تهذيب الكمال ٣٦١ والنجوم الزاهرة ٢: ٣١٦ وفيه: ولي القضاء بالبصرة وبغداد والكوفة وسامراء. وعبر الذهبي ١ =','2','727',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1320,'البيكندي [... - ٢٤٣ هـ / ... - ٨٥٧ م]

<span data-type="title" id=toc-1810>يحيى بن جعفر بن أعين الأزدي البكري البيكندي، أبو زكريا:</span>
مفسر، من أهل بيكند (بن بخاري وجيحون) سكن بكيون (قرية هنالك) وتوفي بها. من آثاره "تفسير القرآن" (١).

الهادي إلى الحق [٢٢٠ - ٢٩٨ هـ / ٨٣٥ - ٩١١ م]

<span data-type="title" id=toc-1811>يحيى بن الحسين بن القاسم بن إبراهيم الحسني العلوي الرسي، أبو الحسين، الهادي إلى الحق:</span>
من أئمة الزيدية. ولد بالمدينة، وعاش عند أقارب له في "الفرع" من أرض الحجاز. ونشأ فقيها عالما ورعا. وراسله أبو العتاهية الهمذاني (وكان من ملوك اليمن) ودعاه إلى بلاده، فسافر إليها مع خمسين من أتباعه
_________
=: ٤٣٩ وووفيات الأعيان ٦: ١٤٧ وشذرات الذهب ٢: ١٠١ وميزان الاعتدال ٤: ٣٦١ ولسان الميزان ٦: ٧٦ وتهذيب التهذيب ١١: ١٧٩ والجواهر المضية ٢: ٢١٠ وطبقات الحنابلة ١: ١٤٠ ومرآة الجنان ٢: ١٣٥ ومختصر دول الإسلام ١١٥ والأعلام ٩: ١٦٧ والكامل في التاريخ ٦: ٤١٩ وانظر فهرسته، وعصر المأمون ١: ٤٤٠. وقيل في وفاته سنة ٢٤٣ وقيل سنة ٢٤٦ هـ.
(١) هدية العارفين ٢: ٥١٥.','2','727',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1321,'ونزل صعدة سنة ٢٨٣ هـ، وتقع على طريق الحج بين مكة وصنعاء، وحل بها في عهد الخليفة المعتضد، وبايعه أبو العتاهية وعشائره وبعض قبائل خولان وبني الحارث بن كعب وبني عبد المدان، وخوطب بأمير المؤمنين، وتلقب بالهادي إلى الحق. وفتح نجران، وأقام بها مدة، وقاتل عمال بني العباس إلى أن سقطت صنعاء في يده (سنة ٢٨٨ هـ). وامتد ملكه فخطب له بمكة، وضربت السكة باسمه، وتوفي بصعدة. من آثاره "تفسير القرآن" قال صاحب تاريخ التراث العربي: "هذا الكتاب تكملة للكتاب الذي بدأه جده القاسم وعمه محمد. وقد تناول فيه عددا من السور وأكمله من بعده ابناه المرتضى والناصر، ويعتبر هذا الكتاب أساس تفسير عبد الله بن أحمد بن إبراهيم الزيدي الشرفي (عاش في القرن الحادي عشر الهجري). وله أيضا "تفسير آية الكرسي" (١).
_________
(١) الإكليل ١٠: ١١٨، ١٨١، ٢١٠، والحور العين ١٩٦ وتاريخ اليمن للواسعي ٢١ وتراجم الرجال ٤١ وابن النديم ٢٤٤ وهدية العارفين ٢: ٥١٧ وتاريخ التراث العربي ٢: ٢٩٩ والأعلام ٩: ١٧١ ومعجم المؤلفين ١٣: ١٩١.','2','728',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1322,'ابن القاسم [١٠٣٥ - بعد ١٠٩٩ هـ / ١٦٢٥ - بعد ١٦٨٧ م]

<span data-type="title" id=toc-1812>يحيى بن الحسين بن (الإمام) القاسم ابن محمد بن علي:</span>
مؤرخ يماني، بحاثة، من أهل صنعاء، له تصانيف تزيد على الأربعين كتابا، عالج فيها فنونا عديدة، منها التاريخ والتفسير. وفي تاريخ وفاته خلاف، قيل سنة ١٠٨٠ هـ، وقيل سنة ١١٠٠ هـ. من كتبه المتعلقة بموضوعنا "البيان لما خفي من القرآن" في التفسير، مخطوط (١).

المؤيد الزيدي [٦٦٩ - ٧٤٧ هـ / ١٢٧٠ - ١٣٤٦ م]

<span data-type="title" id=toc-1813>يحيى بن حمزة بن علي بن إبراهيم بن محمد بن إدريس بن علي بن جعفر، الحسيني العلوي الطالبي:</span>
من أئمة الزيدية وعلمائهم في اليمن. ولد بصنعاء، وأظهر الدعوة بعد وفاة المهدي محمد بن المظهر سنة ٧٢٩ هـ، وتلقب بالمؤيد بالله، أو المؤيد برب العزة. واستمر إلى أن توفي في حصن هران قبلي
_________
(١) غاية الأماني لصاحب الترجمة، مقدمة الدكتور سعيد عشور. ونشر العرف المجلد الثاني ٨٥٤ والبدر الطالع ٢: ٣٢٨ وهدية العارفين ٢: ٥٣٣ والأعلام ٩: ١٧٣.','2','728',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1323,'ذمار، ونقل إلى مدرسة ذمار فدفن فيها. من تصانيفه الكثيرة "الطراز المتضمن لأسرار البلاغة وعلوم حقائق الإعجاز" طبع بمصر، ثلاثة أجزاء (١).

ابن الخلوف [٤٦٦ - ٥٤١ هـ / ١٠٥٤ - ١١٨٣ م]

<span data-type="title" id=toc-1814>يحيى بن خلف بن نفيس، أبو بكر الغرناطي، ويعرف بابن الخلوف:</span>
مقرىء كبير، مفسر، من أهل غرناطة. رحل إلى المشرق، وسمع بالإسكندرية ودمشق وبغداد. وعاد فتصدر للإقراء بجامع غرناطة. قال ابن الأبار: وطال عمره وشاع ذكره وكان رأسا في القراءات، عارفا بالتفسير كثير التفنن الخ" (٢).

ابن الربيع [٥٢٨ - ٦٠٦ هـ / ١١٣٤ - ١٢١٠ م]

<span data-type="title" id=toc-1815>يحيى بن الربيع بن سليمان بن حراز ابن سليمان، العدوي العمري الواسطي (البغدادي، أبو علي، مجد الدين)</span>
_________
(١) غاية الأماني ٢: ٥١٤ ومعجم المطبوعات ١٩٤٤ والبدر الطالع ٢: ٣٣١ وإيضاح المكنون ٢: ٨٢ وهدية العارفين ٢: ٥٢٦ والأعلام ٩: ١٧٤.
(٢) طبقات المفسرين للداودي ٢: ٣٦٢ وغاية النهاية ٢: ٣٦٩ وطبقات القراء للذهبي ٢: ٤٠٧.','2','729',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1324,'البغدادي، أبو علي، مجد الدين: فقيه شافعي، أصولي، مفسر، مقرىء، محدث، له اشتغال بالتاريخ. من أهل واسط. وبها نشأ وتعلم، ثم تفقه ببغداد ونيسابور. وناب في القضاء ببغداد سنة ٥٨٤ هـ. وأنفذ في سفارة إلى صاحب غزنة، وإلى ملك هراة سنة ٥٩٣ هـ وولي تدريس النظامية والنظر في أوقافها سنة ٥٩٨ هـ. ومات ببغداد. من آثاره "تفسير القرآن" في أربع مجلدات (١).

الفراء [١٤٤ - ٢٠٧ هـ / ٧٦١ - ٨٢٢ م]

<span data-type="title" id=toc-1816>يحيى بن زياد بن عبد الله بن منصور الديلمي، أبو زكريا، المعروف بالفراء:</span>
أعلم الكوفيين بالنحو واللغة وفنون الأدب، فقيه، متكلم، عالم بأيام العرب وأخبارها، عارف بالنجوم والطب. ولد بالكوفة، ودرس اللغة والقرآن بها وبالبصرة وبغداد على الرواسي، ويونس ابن حبيب والكسائي. وانتقل إلى بغداد،
_________
(١) طبقات المفسرين للداودي ٢: ٣٦٤ وطبقات المفسرين للسيوطي ٤٣ وغاية النهاية ٢: ٣٧٠ وطبقات الشافعية ٨: ٣٩٣ والعبر ٥: ٢٠ والشذرات ٥: ٢٣ وذيل الروضتين ٦٩ والنجوم الزاهرة ٦: ١٩٩ والبداية والنهاية ١٣: ٥٣ والأعلام ٩: ١٧٦ ومعجم المؤلفين ١٢: ١٩٦.','2','729',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1325,'واتخذه المأمون العباسي مؤدبا لولديه، فكان أكثر مقامه بها، فإذا كان آخر السنة أتى الكوفة، فأقام أربعين يوما يفرق في أهله ما جمعه. وكان يميل إلى الاعتزال، ويصوغ عبارته صياغة فلسفية. اشتهر بالفراء لأنه - كما قيل - كان يفري الكلام. وعرف أبوه زياد "بالأقطع" لأن يده قطعت في معركة "فخ" سنة ١٦٩ هـ وقد شهدها مع الحسين بن علي بن الحسن، في خلافة موسى الهادي. ومات الفراء بطريق مكة، ووجد "كتاب سيبويه" تحت رأسه، فقيل: إنه كان يتتبع خطأه ويتعمد مخالفته. قال ابن حجر العسقلاني: "ذكره ابن حبان في الثقات، وعلق عنه البخاري في موضعين في تفسير "الحديد" و "العصر" ولم يذكره المزي، من آثاره "معاني القرآن" طبع، أملاه في مجالس عامة، كان في جملة من يحضرها نحو ثمانين قاضيا (١).
_________
(١) طبقات المفسرين للداودي ٢: ٣٦٦ وابن خلكان ٦: ١٧٦ وتذكرة الحفاظ ٣٧٢ وتهذيب التهذيب ١١: ١١٢ ومعجم الأدباء ٢٠: ٩ ونزهة الألباء ٩٨ والبداية والنهاية ١٠: ٢٦١ وتاريخ بغداد ١٤: ١٤٩ ومفتاح السعادة ١: ١٧٨ والعبر ١: ٣٥٤ وبغية الوعاة ٢: ٣٣٣ وغاية النهاية ٢: ٣٧١ ونور القبس ٣٠١ ومراتب النحويين ٨٦ ومرآة الجنان ٢: ٣٨ وشذرات الذهب ٢: ١٩ واللباب ٢: ٤١٤ والأعلام ٩: ١٧٨ وابن النديم ٦٦ وهدية العارفين ٢: ٥١٤ ومعجم المؤلفين ١٣: ١٩٨ والمختصر في أخبار البشر ٢: ٣٠.','2','730',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1326,'ابن سعدون [٤٨٦ - ٥٦٧ هـ / ١٠٩٣ - ١١٧٢ م]

<span data-type="title" id=toc-1817>يحيى بن سعدون بن تمام بن محمد الأزدي القرطبي، أبو بكر، ضياء الدين:</span>
عالم بالقراءات وعلوم القرآن والحديث والنحو واللغة، من أهل قرطبة، قرأ بها على ابن النحاس، ثم رحل فقرأ بالمهدية، وسمع بالإسكندرية والقاهرة، ودخل بغداد فأخذ عن علمائها. وأقام بدمشق مدة، ثم استوطن الموصل، ورحل منها إلى أصبهان، ثم عاد إليها وتوفي بها. قال ابن خلكان: "كان ثقة صدوقا أخذ عنه شيوخ ذلك العصر .. ". وذكر السمعاني، أنه اجتمع به بدمشق وسمع منه (١).

ابن سلام [١٢٤ - ٢٠٠ هـ / ٧٤٢ - ٨١٥ م]
يحيى بن سلام بن أبي ثعلبة التيمي بالولاء، من تيم ربيعة، البصري ثم الافريقي: مفسر، فقيه، حافظ، محدث،
_________
(١) طبقات المفسرين للداودي ٢: ٣٦٨ وبغية الوعاة ٢: ٣٣٤ وغاية النهاية ٢: ٣٧٢ ووفيات الأعيان ٦: ١٧١ ونفح الطيب ٢: ١١٦ والعبر ٤: ٢٠٠ وطبقات القراء للذهبي ٢: ٤٢٩ وبغية الوعاة ٢: ٣٣٤ ومعجم الأدباء ٢٠: ١٤ ومرآة الجنان ٣: ٣٨٠ والنجوم الزاهرة ٦: ٦٦ والمغرب ١: ١٣٥.','2','730',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1327,'لغوي، ولد بالكوفة، ونشأ بالبصرة فنسب إليها. أدرك نحو عشرين من "التابعين" وسمع منهم وروى عنهم. ورحل إلى مصر، فسمع منه عبد الله بن وهب، وغيره من الأئمة، ومنها انتقل إلى افريقية (تونس) فاستوطنها. قال ابن الجزري: "وسكن افريقية (تونس) دهرا، وسمع الناس بها كتابه في "تفسير القرآن" وليس لأحد من المتقدمين مثله، وكان ثقة ثبتا ذا علم بالكتاب والسنة ومعرقة باللغة والعربية". وقال أبو العرب: قدم افريقية، وله مصنفات كثيرة في فنون العلم، وكان ثقة ثبتا، من الحفاظ". وقال ابن حجر العسقلاني: ضعفه الدارقطني، في الحديث، وذكره ابن حبان في الثقات، وقال: وربما أخطأ". وحج في آخر عمره، فتوفي في عودته من الحج، بمصر، ودفن بالمقطم. من آثاره "تفسير القرآن" مخطوط، غير كامل، منه ٧ كراسات (٩٩ ورقة) كتبت سنة ٣٨٣ هـ، في الزيتونية بتونس، ومنها نسخة مصورة في دار الكتب بالقاهرة. قال فؤاد سزكين: (اختصره أبو عبد الله محمد بن عبد الله بن أبي زمنين" (انظر. ترجمته في هذا المعجم). ولابن','2','731',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1328,'سلام كتاب آخر في التفسير هو "التصاريف" (تفسير القرآن مما اشتبهت أسماؤه وتصرفت معانيه) مخطوط، بالقيروان، ومنه نسخة مصورة في دار الكتب المصرية بالقاهرة (١).

اليغرفي [آخر القرن ٧ الهجري / آخر القرن ١٣ الميلادي)

<span data-type="title" id=toc-1818>يحيى بن سلطان اليغرفي، أبو زكريا:</span>
مقرئ، نحوي، فقيه مالكي. ذكره ابن رشيد (٦٥٧ - ٧٢١ هـ) في رحلته، وقال: أحد المحققين للعربية، مع مشاركة في تفسير وأدب" ومنطق، وأصول. تخرج به نجباء تونس، وكان له بها جاه وصيت .. " (٢)

الواسطي [٦٦٢ - ٧٣٨ هـ / ١٢٦٤ - ١٣٣٧ م]

<span data-type="title" id=toc-1819>يحيى بن عبد الله بن الحسن بن عبد (الملك الواسطي، أبو الفضل)</span>
_________
(١) طبقات المفسرين للداودي ٢: ٣٧١ وغاية النهاية ٢: ٣٧٣ وميزان الاعتدال ٣: ٢٩٠ ولسان الميزان ٦: ٢٥٩ وفهرست ابن خير ٥٦ وتاريخ التراث العربي ١: ٢٠٤ ورياض النفوس ١: ١٢٢ وطبقات علماء افريقية لأبي العرب ١١١ ومعالم الإيمان ١: ٢٣٩ وفي برنامج العبدلية، الأول من الزيتونة بتونس، صفحة ٤٤ - ٤٦ وصف لمجلد فيها من تفسير صاحب الترجمة. والأعلام ٩: ١٨٣ ومعجم المؤلفين ٨: ٢٠٠ وفهرست المخطوطات ١: ١٦٠.
(٢) طبقات المفسرين للداودي ٢: ٣٧٠ وهو ينقل عن بغية الوعاة ٢: ٣٣٥.','2','731',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1329,'الملك الواسطي، أبو الفضل: فقيه العراق في زمانه، من أعيان الشافعية. قال الذهبي: "قرأ القرآن والفقه والأصلين والعربية وبرع في الفقه وتخرج به الأصجاب"من آثاره "ناسخ القرآن ومنسوخه" (١).

الخطيب التبريزي [٤٢١ - ٥٠٢ هـ / ١٠٣٠ - ١١٠٩ م]

<span data-type="title" id=toc-1820>يحيى بن علي بن محمد الشيباني التبريزي، أبو زكريا:</span>
من أئمة اللغة والنحو والأدب. أصله من تبريز، نشأ ببغداد، ورحل إلى بلاد الشام، فقرأ على أبي العلاء المعري، وسمع من أبي الفتح سليم بن أيوب الرازي في مدينة صور. ودخل مصر، ثم عاد إلى بغداد، وولي تدريس الأدب بالنظامية وخزانة الكتب بها، واستمر إلى أن توفي. من كتبه "تفسير القرآن" و "الملخص في إعراب القرآن" أربع مجلدات، مخطوط (٢).
_________
(١) الدرر الكامنة ٥: ١٩٤ وهدية العارفين ٢: ٥٢٦ وإيضاح المكنون ٢: ٦١٥ والأعلام ٩: ١٩١ وغاية النهاية ٢: ٣٧٤.
(٢) طبقات المفسرين للداودي ٢: ٣٧٢ ومعجم الأدباء ٢٠: ٢٥ وبغية الوعاة ٢: ٣٣٨ ومرآة الجنان ٣: ١٧٢ والنجوم الزاهرة ٥: ١٩٧ والعبر ٤: ٥ والشذرات ٤ (٢) ونزهة الألباء ٣٧٢ والبداية والنهاية ١٢: ١٧٢ والمنتظم ٩: ١٦١ وهدية العارفين ٢: ٥١٩ وكشف الطنون ١٢٣ و ٤٤٦ ودمية القصر ١: ٢٦١ وانظر فهرسته.','2','732',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1330,'نوعي الرومي [٩٤٠ - ١٠٠٧ هـ / ١٥٣٣ - ١٥٩٩ م]

<span data-type="title" id=toc-1821>يحيى بن علي بن نصوح، المعروف بنوعي الرومي:</span>
قاض، بحاثة، مشارك في عدة علوم. ولد في قصبة "طغرة" وتعلم بالقسطنطينية، وولي قضاء العسكر، وعهد إليه بتعليم أبناء السلطان مراد. توفي بالقسطنطينية. من كتبه "تفسير سورة الملك" (١).

الحيسي [... - حيا ١١٠٤ هـ / ... - حيا ١٦٩٣ م]

<span data-type="title" id=toc-1822>يحيى بن علي بن محمد بن مهدي الحسيسي القاسمي:</span>
مؤرخ يماني، عالم بالتفسير والحديث والفقه وأصوله والنحو وغير ذلك. نسبته إلى الحيس قرية من قرى اليمن (٢).

السجستاني [٣٣٢ - ٤٢٢ هـ / ٩٤٤ - ١٠٣١ م]

<span data-type="title" id=toc-1823>يحيى بن عمار، أبو زكريا الشيباني (السجستاني)</span>
_________
(١) ذيل الشقائق ٤١٩ وخلاصة الأثر ٤: ٤٧٤ وهدية العارفين ٢: ٥٣١ والأعلام ٩: ١٩٩.
(٢) ملحق البدر الطالع ٢٣٣ وهدية العارفين ٢: ٥٣٣ وفيه: المتوفى في حدود سنة ١١٠٠ وتاج العروس ٤: ١٣٥ ونشر العرف ٢: ٨٧٧ وهو فيه الحبسي.','2','732',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1331,'السجستاني: فقيه حنبلي، عالم بالتفسير، سكن هراة وتوفي بها. قال صاحب الشذرات: "وكان له القبول التام بتلك الديار لفصاحته وحسن موعظته وبراعته في التفسير". وقال الداودي: "يحيى بن عمار المفسر من شيوخ شيخ الإسلام عبد الله بن محمد الهروي" (١).

المنقاري [١٠١٨ - ١٠٨٨ هـ / ١٦٠٩ - ١٦٧٧ م]

<span data-type="title" id=toc-1824>يحيى بن عمر العلائي الرومي، المعروف بمنقاري زاده:</span>
قاض تركي، مفسر، مشارك في بعض العلوم، ينعت بشيخ الإسلام. درس، ودرس بالقسطنطينية. عين قاضيا لمصر سنة ١٠٦٤ هـ ثم قاضيا لمكة، فالقسطنطينية. وتولى قضاء العسكر بالروم ايلي. ثم منصب الفتوى سنة ١٠٧٣ هـ. وتوفي بأسكدار. من كتبه "حاشية" على تفسير البيضاوي (٢).
_________
(١) طبقات المفسرين للدادوي ٢: ٣٧٣ والشذرات ٣: ٢٢٦ وتاريخ جرجان ٤٧٣ ودمية القصر ٢: ٨٨٧.
(٢) هدية العارفين ٢: ٥٣٣ وإيضاح المكنون ١: ١٤٢ وخلاصة الأثر ٤: ٤٧٧ والأعلام ٩: ٢٠٢ والأزهرية ٣: ٤٥٢ وعثمانلي مؤلفلري ٢: ٥٥.','2','733',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1332,'التكريتي [٥٣١ - ٦١٦ هـ / ١١٣٦ - ١٢١٩ م]

<span data-type="title" id=toc-1825>يحيى بن القاسم بن مفرج بن درع بن الخضر، أبو زكريا الثعلبي التكريتي:</span>
فقيه شافعي مقرىء مفسر، نحوي، ولد بتكريت وولى قضاءها، ثم انتقل إلى بغداد سنة ٦٠٧ هـ، ودرس بالنظامية وتوفي ببغداد" (١).

الفاضل اليمني [٦٨٠ - ٧٥٠ هـ / ١٢٨١ - ١٣٤٩ م]

<span data-type="title" id=toc-1826>يحيى بن القاسم بن عمرو بن علي ابن خالد العلوي، عماد الدين، ويقال عز الدين، اليماني الصنعاني، المعروف بالفاضل اليمني، وبالفاضل العلوي:</span>
مفسر، نحوي، أديب، من علماء الشافعية باليمن. من أهل صنعاء، قرأ على مشايخ اليمن، ثم رحل وأخذ عن علماء بغداد والشام وخراسان. ولقيه صاحب " الوافي
_________
(١) معجم الأدباء ٢٠: ٢٩ وطبقات المفسرين ٢: ٣٧٣ ومرآة الزمان ٨: ٦٠٨ وذيل الروضتين ١٢٠ والبداية والنهاية ١٣: ٨٦ وطبقات السبكي ٨: ٣٥٦ وبغية الوعاة ٢: ٣٣٩ وهدية العارفين ٢: ٥٣٢ والأعلام ٩: ٢٠٤.','2','733',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1333,'بالوفيات" صلاح الدين الصفدي في دمشق سنة ٧٤٩ هـ، وترجم له. قال الشوكاني: "ولم يذكره ابن حجر في "الدرر الكامنة" فهو من فاته من الأكابر المشهورين" ومات قافلا من رحلته بالشرحة، ويقال أنه مات بجهة "اللجب، أحد المواضع المشهورة باليمن، ويسمى عند أهل اللجب بالشولبي. من كتبه "تحفة الأشراف في كشف غوامض الكشاف" مخطوط، قال صاحب كشف الظنون: "أولها: الحمد لله الذي أنزل قرآنه العظيم .. الخ. و "درر الأصداف في حل عقتد الكثساف" مخطوط، قال صاحب كشف الظنون: "فرغ من تأليفها في صفر سنة ٧٣٨ هـ " (١).

الفزاري [... - ٣٦٦ هـ / ... - ٩٧٧ م]

<span data-type="title" id=toc-1827>يحيى بن مجاهد بن عوانة الفزاري، الإلبيري، أبو بكر:</span>
عارف بالتفسير والقراءات، من أهل البيرة، سكن قرطبة. قال ابن الفرضي: "كان منقطع القرين في العبادة بعيد الاسم
_________
(١) البدر الطالع ٢: ٣٤٠ وكشف الظنون ١٤٨٠ وبغية الوعاة ٢: ٣٣٩ وهدية العارفين ٢: ٥٢٧ والأعلام ٩: ٢٠٤ ومعجم المؤلفين ١٣: ٢١٩.','2','734',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1334,'في الزهد، حج، وعني بعلم القرآن والقراءات والتفسير" وسمع بمصر من الأسيوطي وابن الورد وابن شعبان وغيرهم. وكان له حظ من الفقه والرواية، إلا أن العبادة غلبت عليه، ولا أعلمه حدث (١).

العنبري [٢٦٨ - ٣٤٤ هـ / ٨٨١ - ٩٥٥ م]

<span data-type="title" id=toc-1828>يحيى بن محمد بن عبد الله بن عطاء بن صالح، أبو زكريا، العنبري، مولى بني حرب، السلمي:</span>
لغوي، مفسر، أديب، من فقهاء الشافعية، من أهل نيسابور. سمع أبا الحسن الحرسي وأحمد بن سلمة وغيرهما، وروى عنه أبو بكر بن عبدوس المفسر، والمشايخ من طبقته. قال في "معجم الأدباء": "كان عالما بالتفسير، لغويا أديبا فاضلا، اعتزل الناس وقعد عن حضور المحافل بضع عشرة سنة، فقال القاضي عبد الحميد بن عبد الرحمن النيسابوري: ذهبت الفوائد من مجلسنا بعد أبي زكريا" وأطال الحاكم في ترتجمته
_________
(١) طبقات المفسرين ٢: ٣٧٥ وجذوة المقتبس ٣٥٦ وتاريخ علماء الأندلس ٢: ١٩٠ وبغية الملتمس رقم ١٤٩٠ ونفح الطيب ٢: ٦٣١.','2','734',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1335,'وأثنى عليه (١).

التلمساني [... - ٦٥٢ هـ / ... - ١٢٥٤ م]

<span data-type="title" id=toc-1829>يحيى بن محمد بن موسى التجيبي التلمساني أبو زكريا:</span>
مفسر، واعظ، من فقهاء المالكية، من أهل تلمسان، وبها نشأ وتعلم. قال الذهبي: "حج وجاور، وسمع بمكة من أبي الحسن ابن البناء، وسكن الإسكندرية، ووعظ، وصنف في "التفسير" و "الرقائق" (٢)

المناوي [٧٩٨ - ٨٧١ هـ / ١٣٩٦ - ١٤٦٧ م] أ

<span data-type="title" id=toc-1830>يحيى بن محمد بن محمد بن محمد بن أحمد بن مخلوف بن عبد السلام، أبو زكريا، شرف الدين بن سعد الدين الحدادي المناوي:</span>
فقيه شافعي، قاض، عارف بالتفسير والحديث والعربية، من
_________
(١) شذرات الذهب ٢: ٣٦٩ وطبقات السبكي ٣: ٤٨٥ وطبقات المفسرين للسيوطي ٤٣ وطبقات المفسرين للداودي ٢: ٣٥٧ والنجوم الزاهرة ٣: ٣١٤ ومعجم الأدباء ٢٠: ٣٤ وعبر الذهبي ٢: ٢٦٥ واللباب ٢: ٣٦١
(٢) معجم أعلام الجزائر (للمؤلف) ط ٢: ٨٣ وطبقات المفسرين للسيوطي ٤٢ وطبقات المفسرين للداودي ٢: ٢٧٦ ومعجم المؤلفين ١٣: ٢٣٠.','2','735',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1336,'أهل القاهرة، منشأ ووفاته بها، وأصله من منية بني خصيب، في الصعيد، ونسبته إليها. ولاه الظاهر قضاء الديار المصرية، وحمدت سيرته ومدحه بعض كبار الشعراء، كالنواجي. قال السخاوي: وأقرأ الفقه والأصلين والعربية والتفسير والحديث، وله نظم ونثر، وامتحن مرات" ولما مات رثاه كثيرون (١).

الأقصرائي [٧٩٧ - ٨٨. هـ / ١٣٩٧ - ١٤٧٥ م]

<span data-type="title" id=toc-1831>يحيى بن محمد بن إبراهيم بن أحمد، أبو زكريا، أمين الدين، الأقصرائي، ويقال الأقسرائي:</span>
محدث، مفسر، أصولي، من فقهاء الحنفية. تركي الأصل، من بلدة "أقصرا"، وقيل: من آق سراي. مولد. ووفاته بالقاهرة. أقرأ وأفتها، وكان من تلاميذ. السخاوي صاحب "الضوء اللامع"، ومما قاله في ترجمته له: وأقرأ الفقه والأصلين والتفسير والحديث والعربية والمعاني والبيان، وانشالت عليه الفضلاء من كل مذهب فأخذوا عنه .. وعرض عليه قضاء مذهبه مرة بعد أخرى وهو يمتنع،
_________
(١) الضوء اللامع ١٠: ٢٥٤ والشذرات ٧: ٣١٢.','2','735',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1337,'ودرس التفسير بالمؤيدية، والفقه والحديث بالصرغمتشية، واشتهر ذكره، وبعد صيته (١).

التفتازاني [... - ٨٨٧ هـ / ... ١٤٨٢ م]

<span data-type="title" id=toc-1832>يحيى بن محمد بن مسعود بن عمر التفتازاني الهروي، الشهير بالحفيد، والملقب بشيخ الإسلام:</span>
فقيه حنفي، مفسر، من آثاره "حاشية" على أوائل حاشية الكشاف لجده السعد التفتازاني المتوفى سنة ٧٩٣ هـ. (٢)

ابن حجي [٨٣٨ - ٨٨٨ هـ / ١٤٣٥ - ١٤٨٣ م]

<span data-type="title" id=toc-1833>يحيى بن محمد بن عمر بن حجي بن موسى بن أحمد بن سعد، السعدي الحسباني الأصل، الدمشقي، ثم القاهري، أبو زكريا، المعروف بابن حجي:</span>
مفسر، من فقهاه الشافعية، ولد ونشأ بدمشق، وانتقل إلى القاهرة (نحو سنة ٨٤٨ هـ) فقرأ على علمائها. وولي نظر الجيش سنة ٨٦٥
_________
(١) الضوء اللامع ١٠: ٢٤٠. والأعلام ٨: ١٦٨.
(٢) كشف الظنون ١٤٧٩ وهدية العارفين ٢: ٥٢٩.','2','736',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1338,'- ٨٦٦ هـ، ولم يكن ذلك من طبعه، وعكف على تدريس التفسير وغيره بالمنصورية، قال السخاوي: "وأقرأ فيه الكشات قراءة فائقة، استوفى فيها الحواشي، ونحوها، وامتلأت الأعين بحسن تأديته حفظا وتقريرا، وتزاحموا عليه في آخر وقت وفرغ نفسه له. وللشعراء فيه مدائح، وكان مائلا لابن عربي، ووجد في كتبه من تصانيفه ما لم يجتمع عند غيره" (١).

الشاوي [١٠٣٠ - ١٠٩٦ هـ / ١٦٢١ - ١٦٨٥ م]

<span data-type="title" id=toc-1834>يحيى بن محمد بن محمد بن عبد الله بن عيسى، أبو زكريا، الشاوي، الملياني، الجزائري:</span>
نحوي، مفسر، متكلم، من فقهاء المالكية. ولد بمدينة مليانة ونشأ وتعلم بمدينة الجزائر. وحج سنة ١٠٧٤ هـ. وأقام مدة بالقاهرة وتصدر للإقراء بالأزهر الشريف. وقام برحلتين إلى بلاد الروم (تركيا) فأكرمه شيخ الإسلام يحيى المنقاري، والصدر الأعظم، وحضر مجلسا علميا بحضرة السلطان. وعاد إلى القاهرة
_________
(١) الضوء اللامع ١٠: ٢٥٢ وصفحات لم تنشر من بدائع الزهور ١٠٣ و ١٠٨ والأعلام ٩: ٢١٢.','2','736',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1339,'وتولى التدريس في الأشرفية والسليمانية والصرغمتشية وغيرها. قاله المحبي: "قرأنا عليه (في تركيا) "تفسير سورة الفاتحة" من تفسير البيضاوي، وأجازنا جميعا بإجازة نظمها لنا ... وكانت له قوة في البحث وسرعة الاستحضار للمسائل الغريبة، وبداهة الجواب لما يسأل عنه من غير تكلف .. " ومات في سفينة، راحلا للحج، ونقل جثمانه إلى القاهرة ودفن بالقرافة الكبرى. من كتبه "الحاكمة" حاشية على تفسير الزمخشري وتفسير ابن عطية (١).

البجلي [... - حيا ١٣٦ هـ / ... - حيا ٧٥٣ م]

<span data-type="title" id=toc-1835>يحيى بن المهلب البجلي، أبو كدينة، من بني الربعة:</span>
محدث ثقة، مفسر، من أهل الكوفة. روى عن عطاء بن السائب (المتوفى سنة ١٣٦ هـ) وحصين بن عبد الرحمن السلمي الكوفي (المتوفى في
_________
(١) معجم أعلام الجزائر (للمؤلف) ط ٢: ١٨٦ وخلاصة الأثر ٤: ٤٨٦ وفهرس الفهارس ٢: ٤٤٦ وشجرة النور ٣١٦ وتعريف الخلف (ط ١) ٢: ١٨٧ والتحفة المرضية ٧٥ والفكر السامي ٤: ١١٦ وهدية العارفين ٢: ٥٣٣ والأعلام ٩: ٢١٤ ومعجم المؤلفين ١٣: ٢٢٧ وفهرس الأزهرية ١: ١١٩ وإيضاح المكنون ٢: ٢٢٤ وسمط النجوم العوالي.','2','737',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1340,'نفس السنة) وغيرهما. وعنه الأسود بن عامر شاذان الشامي، نزيل بغداد، (المتوفى سنة ٢٠٨ هـ) وغيره. وثقه ابن معين وأبو داود والنسائي والعجلي ويعقوب بن سفيان ومحمد بن سعد صاحب الطبقات، وذكره ابن حبان في "الثقات". قال في طبقات المفسرين: "له تفسير" (١).

ابن يعمر العدواني [... - ١٢٩ هـ / ... - ٧٤٦ م]

<span data-type="title" id=toc-1836>يحيى بن يعمر الوشقي العدواني البصري، أبو سليمان، ويقال: أبو سعيد:</span>
أول من نقط المصاحف. وكان من علماء التابعين، عارفا بالحديث والتفسير والفقه والنحو ولغات العرب، فصيحا، ينطق بالعربية المحضة، طبيعة فيه غير متكلف. ولد بالأهواز، وسكن البصرة. روى عن عثمان وعلي وأبي ذر وأبي هريرة وأبي موسى الأشعري وعائشة، وعنه يحيى ابن عقيل وجماعة. وثقه أبو زرعة وأبو حاتم والنسائي وابن سعد. وذكره ابن حبان في "الثقات" وقال: كان من
_________
(١) طبقات المفسرين للداودي ٢: ٣٧٦ والتاريخ الكبير للبخاري ٨: ٣٠٥ وخلاصة تهذيب الكمال ٣٦٨ وتهذيب التهذيب ١١: ٢٨٩ والطبقات الكبرى لابن سعد ٦: ٣٨٢.','2','737',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1341,'فصحاء أهل زمانه، وأكثر علما باللغة". وتشيع لأهل البيت من غير انتقاص لفضل غيرهم. اختلف مترجموه في قصة دخوله خراسان، قيل: دخلها مع يزيد بن المهلب، (سنة ٨٣ هـ)، وقيل نفاه الحجاج بن يوسف من العراق فتوجه إليها. ولما ولي قتيبة ابن مسلم على الري، ولاه القضاء بمرو، ثم عزل بتهمة إدمان النبيذ، فيما يقال. وحول اشتغاله بالتفسير، راجع ما ذكره صاحب تاريخ التراث العربي (١: ١٧٨ ترجمة نصر بن عاصم) وقد نقلناه في ترجمة نصر في هذا المعجم (١).

العجلي [... - ١٨٩ هـ / ... - ٨٠٥ م]

<span data-type="title" id=toc-1837>يحيى بن يمان العجلي الكوفي، أبو زكريا:</span>
محدث، مفسر، من أهل الكوفة، روى
_________
(١) تاريخ التراث العربي ١: ١٧٨ وتهذيب التهذيب ١١: ٣٠٥ ومعجم الأدباء ٢٠: ٤٢ وطبقات النحويين واللغويين للزبيدي ٢٧ ومرآة الجنان ١: ٢٧١ وتهذيب التهذيب ١١: ٣٠٥ والكامل في التاريخ ٤: ٩٦ و ٤٩٩ و ٥: ٣١٦ وفيه وفاته سنة ١٢٩ ومثله في نزهة الألباء ١٧ والتاريخ الكبير للبخاري والبداية والنهاية ٢: ٣٨١ وفيه توفي قبل سنة ٩٠ هـ. وأخبار القضاة ٣: ٣٠٥ وغاية النهاية ٢: ٣١٨ ونور القبس ٢١ والنجوم الزاهرة ١: ٢١٧ ووفيات الأعيان ٦: ١٧٣ والجهشياري ٤١ وبغية الوعاة ٢: ٢٤٥.','2','738',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1342,'عن أبيه وسفيان الثوري وهشام بن عروة وغيرهم. وعنه يحيى بن معين وأبو بكر وعثمان ابنا أبي شيبة وآخرون. أخرج له الجماعة سوى البخاري. وكان صدوقا ثقه كثير الحفظ، سريعه، إلا أنه فلج، وتغير حفظه، وغلط فيما يرويه. له كتاب "التفسير" مخطوط، في المكتبة الظاهرية بدمشق (١).

<span data-type="title" id=toc-1838>يزيد بن أيوب</span>
[... - ... هـ / ... - ... م]
يزيد بن أيوب: نحوي، مفسر، ذكره صاحب "الجواهر المضية" وقال: كان إماما عالما بالتفسير، والنحو، أستاذ كمال الدين بن أحمد بن الحسن قاضي القضاة، وبه انتفح وعليه تخرج" ولم أعثر له على ترجمة وافية (٢).
_________
(١) تهذيب التهذيب ١١: ٣٠٦ وفيه مجمل أقوال رجال الحديث في صاحب الترجمة. وانظر التاريخ الكبير للبخاري ٦/ ٢: ١٣٣ والعبر ١: ٣٠٤ وشذرات الذهب ١: ٣٢٥ وغاية النهاية ٢: ٣٨١ وتذكرة الحفاظ ٢٨٦ وتاريخ التراث العربي ١: ٢٠٢ والأعلام ٨: ١٧٧.
(٢) طبقات المفسرين للداودي ٢: ٣٧٧ والجواهر المضية في طبقات الحنفية ٢: ٢١٩.','2','738',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1343,'يزيد بن هارون [١١٨ - ٢٥٦ هـ / ٧٣٦ - ٨٢١ م]

<span data-type="title" id=toc-1839>يزيد بن هارون بن زاذان بن ثابت السلمي بالولاء، الواسطي، أبو خالد:</span>
مفسر، من حفاظ الحديث الثقات، كان واسع العلم بالدين. قيل: أصله من بخارى، ومولده ووفاته بواسط. روى عن عاصم الأحول ومحمد بن إسحاق وطبقتهما. وروى عنه الإمام أحمد ابن حنبل وابن المديني وغيرهما. كان يقول: أحفظ أربعة وعشرين ألف حديث بإسنادها ولا فخر، وأحفظ للشاميين عشرين ألفا لا أسأل عنها". وقدر من كان يحضر مجلسه بسبعين ألفا. قال ابن ناصر الدين: كان حافظا إماما ثقه مأمونا مناقبه جمة خطيرة" وقال المأمون العباسى: لولا مكان يزيد بن هارون لأظهرت أن القرآن مخلوق، فقيل: ومن يزيد حتى يتقى؟ قال: أخاف إن أظهرته فيرد علي، فيختلف الناس وتكون فتنة". له "تفسير القرآن" قال فؤاد سزكين: استخدمه الطبري في تفسيره وتاريخه، في رواية مجاهد بن موسمى بن فروخ، المتوفى سنة ٢٤٤ هـ " (١)
_________
(١) تذكرة الحفاط ٣١٧ وتاريخ بغداد ١٤: ٣٣٧ وابن =','2','739',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1344,'أبو يوسف [١١٣ - ١٨٢ هـ / ٧٣١ - ٧٩٨ م]

<span data-type="title" id=toc-1840>يعقوب بن إبراهيم بن حبيب بن خنيس بن سعد الأنصاري الكوفي البغدادي، أبو يوسف:</span>
فقيه، أصولي، مجتهد، من حفاظ الحديث، عالم بالتفسير والمغازي وأيام العرب، وهو صاحب الإمام أبي حنيفة، وتلميذه، وأول من نشر مذهبه. ولد بالكوفة. سمع من عطاء بن السائب وطبقته، ثم لزم أبا حنيفة، فغلب عليه الرأي. وولي القضاء ببغداد أيام المهدي والهادي، وصار كبير القضاة في عهد الرشيد، وكان لهذا أثره في دعم المذهب الحنفي ونشره. توفي ببغداد، ودفن في مقابر قريش بكرخ بغداد بقرب أم جعفر زبيدة (١).
_________
= النديم ٢٢٨ والتهذيب لابن حجر ١١: ٣٦٦ والشذرات ٢: ١٦ وهدية العارفين ٢: ٥٣٦ والتاريخ الكبير للبخاري ٤ ق ٢: ٣٦٨ وكشف الظنون ٤٦١ وفيه: المتوفى سنة ١١٧، خطأ. والأعلام ٩: ٢٤٧ وتاريخ التراث العربي ١: ٢٠٥ والتفسير والمفسرون ١: ١٤١.
(١) أخبار القضاة ٣: ٢٥٤ والعبر ١: ٢٨٤ والجواهر المضية ٢: ٢٢ وتذكرة الحفاظ ٢٩٢ ومرآة الجنان ١: ٣٨٢ وتاريخ بغداد ١٤: ٢٤٢ والبداية والنهاية ١٠: ١٨٠ والنجوم الزاهرة ٢: ١٠٧ والفوائد البهية ٢٢٥ وهدية العارفين ٢: ٥٣٦ وابن النديم ٢٠٣ والشذرات ١: ٢٩٨ وابن خلكان ٦: ٣٧٨ والتاريخ الكبير للبخاري ٤ ق. ٢: ٣٩٧.','2','739',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1345,'الدورقي [١٦٦ - ٢٥٢ هـ / ٧٨٢ - ٨٦٦ م]

<span data-type="title" id=toc-1841>يعقوب بن إبراهيم بن كثير ابن زيد بن أفلح بن منصور العبدي، أبو يوسف الدورقي:</span>
محدث، حافظ، من أهل بغداد، رحل وجمع وصنف، وأخذ عنه الأئمة الستة. والدورقي هنا: نسبة إلى لبس "الدورقية" وهي قلانس طوال، وليس إلى دورق من بلاد خوزستان. قال الداودي: له "تفسير القرآن " (١).

قرا يعقوب [٧٨٩ - ٨٣٣ هـ / ١٣٨٧ - ١٤٢٩ م]

<span data-type="title" id=toc-1842>يعقوب بن إدريس بن عبد الله بن يعقوب الرومي النكدي، الشهير بقرا يعقوب:</span>
فقيه حنفي، أصولي، مفسر، نحوي، ولد بنكدة، من بلاد قرمان، وإليها نسبته، وبها نشأ وتعلم. ودخل البلاد الشامية، وحج، ثم رجع وأقام بـ "لارندة" يدرس ويفتي. ثم قدم
_________
(١) تهذيب التهذيب ١١: ٣٨١ والمشتبه ١: ٢٩٠ وهدية العارفين ٢: ٥٣٧ وتذكرة الحفاظ ٢: ٥٠٥ وخلاصة تهذيب الكمال ٥: ٣٧٢ وطبقات المفسرين ٢: ٣٧٧ والأعلام ٩: ٢٥٣.','2','740',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1346,'القاهرة بعد موت المؤيد، واجتمع بمدير المملكة ططر، فأكرمه ووصله بمال جزيل، فاقتنى كتبا كثيرة، ورجع إلى بلاده، فأقام بـ"لارندة" إلى أن توفي. من آثاره "حاشية" على تفسير البيضاوي (١).

ابن السكيت [١٨٦ - ٢٤٤ هـ / ٨٠٢ - ٨٥٨ م]

<span data-type="title" id=toc-1843>يعقوب بن إسحاق، أبو يوسف، ابن السكيت:</span>
من أئمة اللغة والأدب. أصله من دورق بالأهواز، ويظن أنه ولد ببغداد. وبها تعلم، ورحل إلى البادية. أدب أبناء العامة ببغداد، ثم أبناء آل طاهر بسامرا، ثم اتصل بالمتوكل الفاسي، فعهد إليه بتأديب أولاده، وجعله في عداد ندمائه، ثم قتله لتشيعه. قيل: سأله عن ابنيه المعتز والمؤيد: أهما أحب إليه أم الحسن والحسين؟ فقال ابن السكيت: والله إن قنبرا خادم علي خير منك ومن ابنيك. فأمر الأتراك فداسوا بطنه،) وسلوا لسانه، وحمل
_________
(١) إنباء الغمر ٣: ٤٥٣ والضؤ اللامع ١٠: ٢٨٢ والفوائد البهية ٢٢٦ وإيضاح المكنون ١: ١٤٢ والشذرات ٧: ٢٠٧ وهدية العارفين ٢: ٥٤٦ والأعلام ٩: ٢٥٤ وبغية الوعاة.','2','740',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1347,'إلى داره فمات. من كتبه "غريب القرآن " (١).

عاصمي [٩٠٨ - ١٠٠٣ هـ / ١٥٠٢ - ١٥٩٥ م]

<span data-type="title" id=toc-1844>يعقوب بن الحسن الكشميري، الشهير بعاصمي:</span>
محدث، مفسر، أديب، له مؤلفات بالعربية والفارسية. فمن العربية "تفسير القرآن" (٢).

ابن خطيب القلعة [... - ٧٧٤ هـ / ... - ١٣٧٢ م]

<span data-type="title" id=toc-1845>يعقوب بن عبد الرحمن بن عثمان بن يعقوب، شرف الدين ابن خطيب القلعة:</span>
مقرىء، مفسر، خطيب، عارف بالعربية، من فقهاء الشافعية. من أهل "حماة" في سورية. قال ابن حجر: انتهت إليه رياسة العلم ببلدته، وانتفع به الناس" (٣).
_________
(١) نزهة الألباء ١٢٢ وتاريخ بغداد ونور القبس ٣١٩ ومراتب النحويين ٩٥ وطبقات الزبيدي ٢٢١ وابن النديم ٧٢ ومرآة الجنان ٢: ١٤٧ ومعجم الأدباء ٢٠: ٥٠ والشذرات ٢: ١٠٦ وابن خلكان ٦: ٣٩٥ وهدية العارفين ٢: ٥٣٦ والأعلام ٩: ٢٥٥ ومعجم المؤلفين ١٣: ٢٤٣ وفي الحاشية سرد بمصادر أخرى.
(٢) هدية العارفين ٢: ٥٤٧.
(٣) غاية النهاية ٢: ٣٩١ والدرر الكامنة ٥: ٢٠٩ وإنباء الغمر ١: ٥٥ وبغية الوعاة ٢: ٣٥٠ وشذرات الذهب ٦: ٢٣٧ والأعلام ٨: ٢٠٠ ومعجم المؤلفين ١٣: ٢٥١.','2','741',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1348,'الغزنوي [... - ٨٥١ هـ / ... - ١٤٤٧ م]

<span data-type="title" id=toc-1846>يعقوب بن عثمان الغزنوي:</span>
مفسر، من أعيان مشايخ النقشبندية، من أهل غزنة، له تصانيف بالعربية والفارسية، فمن العربية "تفسير القرآن". وطبع له بالهند "تفسير الفاتحة" بالفارسية (١).

يعقوب عفوي [... - ١١٤٩ هـ / ... - ١٧٣٦ م]

<span data-type="title" id=toc-1847>يعقوب بن مصطفى فنائي الأماسي، الرومي، الخلوتي، الملقب بـ"عفوي</span>":
مفسر، صوفي، واعظ، من فقهاء الحنفية، أكثر تصانيفه بالعربية. أصله من أماسية، تعلم بها وباسكدار. ووعظ في "يكي جامع" بالقسطنطينية أيام الخميس. وتوفي باسكدار. من كتبه "نتيجة التفاسير" جزء في تفسير سورة يوسف، فرغ منه سنة ١١٣٣ هـ، وطبع بالآستانة سنة ١٢٦٦ هـ (٢).
_________
(١) هدية العارفين ٢: ٥٤٦ وكشف الظنون ٤٥٥.
(٢) معجم المطبوعات ١٣٤٤ والأعلام ٨: ٢٠٢ ومعجم المؤلفين ١٣: ٢٥٢ وهدية العارفين ٢: ٥٤٧ وفيه نسبة الكتاب إلى صاحب الترجمة. وفي ٢: ٤٥٠ نسبة إلى مصطفى بن كمال الدين البكري، ومثله في إيضاح المكنون ٢: ٦٢٢.','2','741',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1349,'الثغري [... - ٥٧٩ هـ / ... - ١١٨٣ م]

<span data-type="title" id=toc-1848>يوسف بن إبراهيم بن عثمان، أبو الحجاج العبدري الغرناطي، المعروف بالثغري:</span>
حافظ، محدث، مفسر، مقرىء، من فقهاء المالكية. من أهل غرناطة، قال ابن الزبير: خرج منها وقدم "مرسية" فى الفتنة، وسعى له في الخطبة بجامع قليوشة فرحل إليها واستقر بها إلى أن توفي" وقال ابن الأبار: "كان حافظا محدثا، فقيها، مقرئا، راوية، ضابطا، مفسرا، أديبا، لم أر أفضل ولا أزهد منه، ولا أحفظ لحديث وتفسير منه" (١)

الثلائي [... - ٨٣٢ هـ / ... - ١٤٢٩ م]

<span data-type="title" id=toc-1849>يوسف بن أحمد بن محمد بن أحمد بن عثمان، نجم الدين، اليماني الثلائي:</span>
فقيه زيدي، فرضي، عارف بالتفسير، من أهل هجرة العين، من ثلا باليمن. وبها توفي. له "الثمرات اليانعة والأحكام الواضحة القاطعة" في تفسير آيات الأحكام، مخطوط، ثلاثة
_________
(١) صلة الصلة ٢١٣ والتكملة لابن الأبار رقم ٢٠٨٢ وبغية الملتمس رقم ١٤٣٧ وغاية النهاية ٢: ٣٩٢ وطبقات المفسرين للداودي ٢: ٣٧٨.','2','742',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1350,'مجلدات (١).

الدجوي [١٢٨٧ - ١٣٦٠ هـ / ١٨٧٠ - ١٩٤٦ م]

<span data-type="title" id=toc-1850>يوسف بن أحمد بن نصر بن سويلم الدجوي:</span>
فقيه مالكي، من علماء الأزهر. ولد في قرية (دجوة) من أعمال القليوبية بمصر. كشف بصره في صغره، وتعلم بالأزهر، وتوفي بعزبة النخل من ضواحي القاهرة، ودفن في عين شمس، من آثاره "تفسير قوله تعالى: {لا يسأل عما يفعل} رسالة، طبعت بمصر (٢).

بحري [... - ١٢٤٥ هـ / ... - ١٨٢٩ م]

<span data-type="title" id=toc-1851>يوسف بن ... ... . الملقب ببحري الرومي:</span>
تركي مستعرب، فقيه، مفسر، له مؤلفات بالتركية والعربية، من أهل بلدة "وزير كوبري". من آثاره "تفسير
_________
(١) البدر الطالع ٢: ٣٥٠ وتراجم الرجال ٤٣ وهدية العارفين ٢: ٥٥٩ والأعلام ٨: ٢١٥ والدر الفريد ٢٩ وبروكلمان، الذيل ٢: ٢٥٠.
(٢) الكنز الثمين ١: ٢٧٠ والأعلام الشرقية ٣: ١٨٤ ومعجم المطبوعات ٨٦٧ ومعجم المؤلفين ١٣: ٢٧٢ والغيث المروي في ترجمة الإمام الدجوي، ومجلة المنار ٣١: ٦٩١ والأعلام ٩: ٢٨٧.','2','742',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1351,'القرآن" (١).

الأماسي [٨٩٣ - ٩٨٦ هـ / ١٤٨٨ - ١٥٧٨ م]

<span data-type="title" id=toc-1852>يوسف بن حسام الدين بن الياس الأماسي الرومي، المعروف بمحشي البيضاوي، وبالمولى سنان الدين، ويقال له عجم سنان البردعي:</span>
قاض، مفسر، من فقهاء الحنفية، تركي مستعرب. ولد بقصبة سونسة، ورحل في طلب العلم، وأخذ عن محي الدين الفناري وعلاء الدين الجمالي وغيرهما. وتنقل في التدريس. وولي قضاء حلب فدمشق فأدرنة فالقسطنطينية فقضاء الجند في ولاية الأناضول، ثم عزل، وتوفي بالقسطنطينية، وقد أناف على التسعين. من تصانيفه "حاشية على تفسير البيضاوي" مخطوط، قال صاحب كشف الظنون: وهي حاشية مقبولة، من أول الأنعام إلى آخر الكهف، وقال صاحب الشذرات: "أظهر فيها اليد البيضاء والحجة الزهراء". وله أيضا "تعليقة" على تفسير سورة الملك والمدثر والقمر، قال صاحب كشف الظنون: "ألحقها
_________
(١) هدية العارفين ٢: ٥٧١ ومعجم المؤلفين ١٣: ٢٧٩.','2','743',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1352,'بالحاشية الأولى وأهداها إلى السلطان سليم خان الثاني". وله أيضا "تضليل التأويل" طبع (١).

الحلوائي [٧٣٠ - ٨٠٤ هـ / ١٣٣٠ - ١٤٠٢ م]

<span data-type="title" id=toc-1853>يوسف بن الحسن بن محمود السرائي الأصل، التبريزي، الشهير بالحلوائي، عز الدين:</span>
مفسر، محدث، أصولي، من فقهاء الشافعية، من أهل تبريز، تعلم بها وببغداد. وتحول إلى ماردين، فأقام بها مدة وأكرمه صاجها. وعاد إلى تبريز فعكف على التأليف. وحج، وزار المدينة، وجاور بها. ورجع إلى الجزيرة، فقطنها إلى أن مات بها. من آثاره "حاشية على الكشاف" في التفسير (٢).
_________
(١) العقد المنظوم ٤٨٩ وهو فيه: يوسف، المشتهر بالمولى سنان. وهدية العارفين ٢: ٥٦٤ وعنه أخذت اسمه كاملا، وكشف الظنون ١٩١١٩ والشذرات ٨: ٤١٢ والأعلام ٩: ٣٠٩ وعثمانلي مؤلفلري ٢: ٥٤ ومعجم المؤلفين ١٣: ٢٨٩ وفهرس مكتبة البلدية (تفسير) ٨.
(٢) إنباء الغمر ٢: ١٣٠ (وفيات ٨٠٢ هـ) و ٢: ٢٢٢ (وفيات ٨٠٤ هـ) ومثله في الشذرات ٧: ٢٠ و ٧: ٤٦ والضوء اللامع ١٠: ٣٠٩ وفيه مات سنة اثنتين، وقيل سنة أربع". وهدية العارفين ٢: ٥٥٩ وهو فيه يوسف بن محمود. وبغية الوعاة ٢: ٣٥٦ وطبقات المفسرين للداودي ٢: ٣٧٩ وهو ينقل عن إنباء الغمر. ومعجم المؤلفين ١٣: ٢٩٢ وكشف الظنون ١٨٨٠.','2','743',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1353,'قاضي بغداد [... - ٩٢٢ هـ / ... - ١٥١٦ م]

<span data-type="title" id=toc-1854>يوسف بن حسن الحسيني الشيرازي، قوام الدين، المعروف بقاضي بغداد:</span>
فقيه حنفي، أصولي، متكلم، من القضاة. من أهل شيراز، سكن بغداد، وولي قضاءها مدة. قال صاحب الشذرات: "ولما حدثت فتنة "ابن أردبيل" رحل إلى ماردين، وسكن بها مدة، ثم دخل بلاد الروم، فعينه السلطان مدرسا في بروسة" وتوفي بها. من آثاره "كتاب جامع لمقدمات التفسير" و "تفسير" قوله تعالى: {يا أرض ابلعي ماءك} (١).

الحسفاوي [... - ٨٢٩ هـ / ... - ١٤٢٦ م]

<span data-type="title" id=toc-1855>يوسف بن خالد بن أيوب، جمال الدين الحسفاوي:</span>
قاض، من فقهاء الشافعية، نسبته إلى حسفايا من قرى حلب، ونشأ وتعلم بحلب، وبماردين، وولي القضاء بملطية، فحلب، فطرابلس، فصفد، ودخل القاهرة. قال السخاوي: "كان ذكيا عارفا بالتفسير والنحو والفقه .. الخ"
_________
(١) هدية العارفين ٢: ٥٦٣ وشذرات الذهب ٨: ٨٥ والكواكب السائرة ١: ٣١٩ وكشف الظنون ٩٠١ والشقائق النعمانية ١: ٤٦٩ والأعلام ٩: ٣٠٠ ومعجم المؤلفين ١٣: ٢٩٠.','2','744',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1354,'توفي بطرابلس (١).

البساطي [٧٤١ - ٨٢٩ هـ / ١٣٤٠ - ١٤٢٦ م]

<span data-type="title" id=toc-1856>يوسف بن خالد بن نعيم بن مقدم بن محمد بن حسن الطائي البساطي، جمال الدين، أبو المحاسن:</span>
فقيه مالكي، قاض، مشارك في بعض العلوم. من أهل القاهرة، وبها نشأ وتعلم. وناب في القضاء عن أخيه وغيره، ثم استقل به في رجب سنة ٨٠٤ بعد عزل المؤرخ ابن خلدون، ولكنه عزل بعد ثلاثة أشهر. ثم تكرر تعيينه وعزله مرات، ثم ولي الحسبة ثلاثة أشهر، ودرس بالمؤيدية وغيرها. من آثاره "إعراب القرآن" من سورة الطارق إلى آخر القرآن" (٢).
_________
(١) شذرات الذهب ٧: ١٩١ وهو فيه: "الحفناوي، نسبة إلى "حفنا" قرية بمصر،، والضوء اللامع ١٠: ٣١٢ وعنه أخذت نسبته. وإعلام النبلاء ٥: ١٧٩ وهو ينقل عن الضوء أيضا. وإنباء الغمر ٢: ٣٨٠ وطبقات المفسرين للداودي ٢: ٣٧٩.
(٢) الضوء اللامع ١٠: ٣١٢ و ٤: ١٤٦ (ترجمة ابن خلدون) والتعريف بابن خلدون ورحلته ٣٨٣ وحسن المحاضرة ٢: ١٢٣ وابن خلدون حياته وتراثه ٩٦ و ٩٧ و ٢١٧ و ٢١٨ و ٢٢٣ والنجوم الزاهرة ١٤: ٢٠٣ و ٢٢٢ ونزهة النفوس والأبدان ٢: ٢٠٠ و ٢٠٩ و ٢١٧ و ٢١٨ و ٢٥٩ و ٥٠٧ ورفع الإصر (مخطوط) ورقه ١٥٩ ونيل الابتهاج ٣٥٣.','2','744',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1355,'ابن الجوزي [٥٨٠ - ٦٥٦ هـ / ١١٨٥ - ١٢٥٨ م]

<span data-type="title" id=toc-1857>يوسف بن عبد الرحمن بن علي بن محمد ابن علي بن عبيد الله بن عبد الله بن حماد بن الجوزي، القرشي التيمي، البكري، البغدادي، محيي الدين، أبو محمد، وأبو المحاسن:</span>
أستاذ دار الخلافة المستعصمية وسفيرها، محدث، مفسر، أصولي، من أكابر فقهاء الحنابلة في عصره. ولد ببغداد، وهو ابن العلامة المحدث المؤرخ أبي الفرج ابن الجوزي. سمع من أبيه وغيره، وقرأ بواسط، واشتغل بالفقه والخلاف والأصول، وعلا أمره وعظم شأنه. ثم ولي الحسبة بجانبي بغداد، والنظر في الوتوف العامة، ووقوف جامع السلطان، ثم عزل عن جميع ذلك، (سنة ٦٠٩ هـ) وانقطع في داره يعظ ويفتي ويدرس، ثم أعيد إلى الحسبة (سنة ٦١٥ هـ). وصدرت رسائل الديوان إلى مصر والروم والشام والشرق والموصل والجزيرة، عدة مرات، من إنشائه. وحدث ببغداد ومصر وسواهما. وأرسله الخليفة المستنصر إلى ملوك الأطراف، فاكتسب مالا كثيرا، وأنشأ "المدرسة الجوزية" في دمشق، ووقف عليها أوقافا','2','745',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1356,'كثيرة، وولي التدريس بالمستنصرية ببغداد، ثم ولي "استاذ دارية" دار الخلافة في أيام المستعصم، واستمر إلى أن قتله التتار شهيدا، صبرا، هو وأولاده الثلاثة، يوم دخول هولاكو بغداد، بظاهر سور كلواذا. ومما يذكر عن سفاراته إلى ملوك الأطراف، أن الخليفة المستنصر انفذه في رسالة إلى حلب سنة ٦٣٤ هـ، فمات ملكها أحمد بن صلاح الدين الأيوبي، وإلى الروم، فمات سلطانهم، وإلى الملك الأشرف موسى بن محمد (٦٣٥ هـ) فمات، وإلى أخيه الملك العادل محمد، فتوفي، وتشاءم الناس من قدومه إليهم، حتى قال أبو القاسم السنجاري: قل للخليفة رفقا ... لك البقاء الطويل أرسلت فيهم رسولا ... سفيره عزرئيل قال الذهبي: "كان إماما كبيرا، وصدرا معظما، عارفا بالمذهب، كثير المحفوظ، ذا سمت ووقار، درس وأفتى وصنف، وأما رياسته وعقله، فينقل بالتواتر، حتى إن الملك الكامل - مع عظم سلطانه - قال: كل أحد يعوزه زيادة عقل إلا محيي الدين ابن الجوزي، فإنه يعوزه','2','745',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1357,'نقص عقل". من تصانيفه "معادن الإبريز، في تفسير الكاب العزيز" (١).

الأسير [١٢٣٢ - ١٣٠٧ هـ / ١٨١٧ - ١٨٨٩ م]

<span data-type="title" id=toc-1858>يوسف بن عبد القادر بن محمد الحسيني الأسير:</span>
كاتب، أديب، شاعر، فقيه، فرضي، له اشتغال بالصحافة. ولد في صيدا، ونشأ بها، ودرس بدمشق، ثم عاد إلى صيدا، فتعاطى التجارة. وتوجه إلى الأزهر بمصر للتفقه في العلوم العقلية والنقلية، فأقام سبع سنين. وعاد إلى صيدا، ومنها انتقل إلى طرابلس الشام، فأقام ثلاث سنوات تولى خلالها رياسة كتاب محكمتها الشرعية. ثم تولى منصب الإفتاء في مدينة عكا (بفلسطين). وعين مدعيا عاما مدة أربع سنين في جبل لبنان. وسافر إلى الآستانة، فتولى رئاسة تصحيح الكتب في نظارة المعارف، وتدريس العربية في دار المعلمين. وعاد إلى بيروت فكان
_________
(١) ذيل طبقات الحنابلة ٢: ٢٥٨ والشذرات ٥: ٢٨٦ وهدية العارفين ٢: ٥٥٥ والدارس ٢: ٦٢ وطبقات المفسرين للداودي ٢: ٣٨٠ والنجوم الزاهرة ٧: ٦٦ ومرآة الزمان ١: ٣٣٢ والبداية والنهاية ١٣: ٢٠٣ والأعلام ٨: ٢٣٦ ومعجم المؤلفين ١٣: ٣٠٧.','2','746',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1358,'معاونا لقاضيها ومدرسا في بعض أشهر مدارسها، كما تولى رئاسة التحرير لجريدتي "ثمرات الفنون" و "لسان الحال" مدة. وتوفي ببيروت. له "هدية الإخوان في تفسير ما أبهم على العامة من ألفاظ القرآن" (١).

ابن عبد البر [٣٦٨ - ٤٦٣ هـ / ٩٧٨ - ١٠٧١ م]

<span data-type="title" id=toc-1859>يوسف بن عبد الله بن محمد بن عبد البر النمري القرطبي، أبو عمر:</span>
شيخ علماء الأندلس، وكبير محدثيها في وقته، مؤرخ، عارف بالرجال والأنساب، أديب، بحاثة، قاض، من فقهاء المالكية، يقال له حافظ المغرب. ولد بقرطبة، وتفقه على علمائها، ورحل عنها في الفتنة، فجال في غرب الأندلس مدة، ثم تحول إلى شرقيها، وسكن "دانية" من بلادها، و "بلنسية" و "شاطبة" في أوقات مختلفة. واشتهر، فرحل إليه الناس وسمعوا منه. ثم لحق ببلاط بني الأفطس ببطليوس، وعينه الملك المظفر بن الأفطس قاضيا لأشبونة، ثم شنترين.
_________
(١) تاريخ الصحافة العربية ١: ١٣٥ ونفحة البشام ١٣ وتراجم مشاهير الشرق ٢: ٢٢١ ورواد النهضة ٩٣ وأعلام الأدب والفن ٢: ٣٢٥ ومصادر الدراسة الأدبية ٢: ١٢٣ وتاريخ آداب اللغة العربية ٤: ٢٧٥ وهدية العارفين ٢: ٧٥١ والأعلام ٩: ٣١٥.','2','746',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1359,'وتوفي بشاطبة. قال أبو الوليد الباجي: "لم يكن بالأندلس مثل أبي عمر في الحديث، وهو أحفظ أهل المغرب". و "النمري" نسبة إلى النمر بن قاسط، من أسد بن ربيعة جاهلي، كان له بالمدينة عقب كثير، ارتد جماعة منهم في أيام أبي بكر الصديق، فأبادهم خالد بن الوليد. ودخل بعضهم الأندلس في أيام الفتح، فكانت سكناهم بحصن وضاح من عمل رية (Reiyo)  من تصانيفه الكثيرة "البيان في تأويلات القرآن" (١).

المارديني [... - ٨١٩ هـ / ... - ١٤١٦ م]

<span data-type="title" id=toc-1860>يوسف بن عبد الله المارديني:</span>
فقيه حنفي، مفسر، واعظ، من أهل ماردين، قدم القاهرة ووعظ الناس بالجامع
_________
(١) ترتيب المدارك ٤: ٨٠٨ وتذكرة الحفاظ ١١٢٨ والعبر للذهبي ٣: ٢٥٥ والصلة ٦٣٧ وجذوة المقتبس ٣٤٤ ومطمح الأنفس ٦١ وبعية الملتمس ٤٧٦ والبداية والنهاية ١٢: ١٠٤ وتاريخ الفكر الأندلسي ٢٦٧ وروضات الجنات ٤: ٢٣٩ والديباج ٢: ٣٦٧ وشجرة النور ١: ١١٩ ووفيات الأعيان ٧: ٦٦ وشذرات الذهب ٣: ٣١٤ واللباب ٢: ٢٥٣ ومرآة الجنان ٣: ٨٩ وهدية العارفين ٢: ٥٥٠ وفهرس الفهارس ٢: ٢١٨ ومختصر دول الإسلام ١: ٢١١ والمختصر في أخبار البشر ٢: ١٩٧ والأعلام ٨: ٢٤٥ والمغرب في حل المغرب ٢: ٤٠٧ وكشف الظنون ٢٦٣ ودول الطوائف ٤٣٤.','2','747',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1360,'الأزهر. قال ابن حجر: "كان لين الجانب متواضعا حافظا للتفسير والمواعظ، توفي بالطاعون" (١).

الأرميوني [... - ٩٥٨ هـ / ... - ١٥٥١ م]

<span data-type="title" id=toc-1861>يوسف بن عبد الله بن سعيد الحسيني الأرميوني، المصري، جمال الدين:</span>
مفسر، مقرىء، محدث، من تلاميذ السيوطي. من أهل أرميون من قرى غربية مصر. من كتبه "المعتمد في تفسير: {قل هو الله أحد} " و "أربعون حديثا تتعلق بآية الكرسي" و "أربعون حديثا تتعلق بسورة الإخلاص" وكلها مخطوطة (٢).

الإمام [... - ١٠٥٥ هـ / ... - ١٦٤٥ م]

<span data-type="title" id=toc-1862>يوسف بن عبد الله الدمشقي، الشهير بالإمام:</span>
فاضل، من أهل دمشق، له اشتغال بالتفسير والحديث والفقه والمنطق والعربية، وله "كتاب" تضمن مسائل
_________
(١) الضوء اللامع ١٠: ٣١٩ وإنباء الغمر ٢: ٣٧٧ رقم ٤٩ وحاشية رقم ٢ و ٣: ١٢٣.
(٢) هدية العارفين ٢: ٥٦٤ والشذرات ٨: ٣٢٢ والكواكب السائرة ٢: ٢٦١.','2','747',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1361,'تتعلق بهذه العلوم، ألفه بإشارة من السلطان مراد خان (١).

الأندخوذي [... . - ... هـ / ... - ... م]

<span data-type="title" id=toc-1863>يوسف بن عبيد الله اللؤلؤي الأندخوذي:</span>
مفسر، نسبته إلى أندخوذ، بلدة بنواحي بلخ مما يلي مرو، قال صاحب كشف الظنون: له "الينابيع" في التفسير. ولم أعثر له على ترجمة وافية (٢).

الإسكليبي [... - ١١٩٩ هـ / ... - ١٧٨٥ م]

<span data-type="title" id=toc-1864>يوسف بن عثمان الإسكليبي الرومي:</span>
مفسر، واعظ، من فقهاء الحنفية. كان واعظا في جامع السليمانية بالقسطنطينية. من آثاره "الموهبة الإلهية والعطية السبحانية" في تفسير القرآن (٣).

ابن عدون [١١٥٨ - بعد ١٢٢٣ هـ / ١٧٤٥ - بعد ١٨٠٨ م]

<span data-type="title" id=toc-1865>يوسف بن عدون بن الشيخ حمو، أبو يعقوب:</span>
من أوائل المصلحين الكبار في
_________
(١) هدية العارفين ٢: ٥٦٦.
(٢) كشف الظنون ٢٠٥١.
(٣) هدية العارفين ٢: ٥٧٥ ومعجم المؤلفين ١٣: ٣١٧.','2','748',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1362,'وادي ميزاب، بالجزائر. استخلفه الشيخ عبد العزيز الثميني في مسجد بني يزقن لما أسن وعجز. أقام بالقاهرة أربع سنين في رجوعه من الحج ولقي كبار علماء الأزهر. من آثاره "حاشية على تفسير البيضاوي" (١).

سبط ابن الجوزي [٥٨١ - ٦٥٤ هـ / ١١٨٥ - ١٢٥٦ م]

<span data-type="title" id=toc-1866>يوسف بن قزأوغلي - أو قزغلي - بن عبد الله، أبو المظفر، شمس الدين، سبط أبي الفرج ابن الجوزي:</span>
مؤرخ، محدث، واعظ، مفسر، ولد ببغداد، وبها نشأ تحت كنف جده لأمه أبي الفرج بن الجوزي. وانتقل إلى دمشق، فوعظ بها، ودرس وأفتى، وكان وافر الحرمة عند الملوك، وتوفي فيها. من كتبه "تفسير القرآن" في تسعة وعشرين مجلدا، وقيل: في سبعة وعشرين (٢).
_________
(١) معجم أعلام الجزائر ٢٠٦ ونهضة الجزائر الحديثة ١: ٢٨٢.
(٢) طبقات المفسرين للداودي ٢: ٣٨٣ وميزان الاعتدال ٤: ٤٧١ ولسان الميزان ٦: ٣٢٨ وتاج التراجم ٨٣ والنجوم الزاهرة ٧: ٣٩ ومفتاح السعادة ١: ٢٥٥ والبداية والنهاية ١٣: ١٩٤ والجواهر المضية ٢: ٢٣١ ومرآة الجنان ٤: ١٣٦ والعبر ٥: ٢٢٠ والشذرات ٥: ٢٦٦ وتاريخ علماء بغداد ٢٣٦ والفوائد البهية ٢٣٠ والسلوك ١: =','2','748',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1363,'الأصم [... - ١٠٠٢ هـ / ... - ١٥٩٤ م]

<span data-type="title" id=toc-1867>يوسف بن محمد الكردي، الشهير بالأصم:</span>
فقيه شافعي، مفسر، مشارك في بعض العلوم. قال المحبي: سمي بالأصم: لأنه كان يطالع ومر عليه عسكر كثير فتلوثت ثيابه بالطين من مشي خيلهم، ولم يشعر بهم". له "منقول التفاسير" طبع بالتركية، في تفسير القرآن، مشهور ببلاد الأكراد (١).

القرباغي [... - بعد ١٠٣٥ هـ / ... - بعد ١٦٢١ م]

<span data-type="title" id=toc-1868>يوسف بن محمد خان القرباغي:</span>
متكلم، من أهل قرباغ من قرى همذان. له كتب، منها "تفسير قوله تعالى: {ليس كمثله شيء} مخطوط (٢).
_________
= ٤٠١ وهدية العارفين ٢: ٥٥٤ والدارس ١: ٤٧٨ والتعريف بالمؤرخين ٦٩ وذيل مرآة الزمان ١: ٣٩ والأعلام ٩: ٣٢٤ وكشف الظنون ٤٤٨.
(١) خلاصة الأثر ٤: ٥٠٩ وهدية العارفين ٢: ٥٦٥ وإيضاح المكنون ٢: ٥٨٤ ومعجم المؤلفين ١٣: ٣٢٩.
(٢) مفتاح الكنوز ١: ١١٨ و ٢: ٤٢٢ وهدية العارفين ٢: ٥٦٦ وخلاصة الأثر ٤: ٥١٠ والأعلام ٩: ٣٣٩','2','749',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1364,'الكوراني [... - نحو ١٠٠٠ هـ / ... - نحو ١٥٩٢ م]

<span data-type="title" id=toc-1869>يوسف بن محمود بن كمال الدين الكوراني الصديقي:</span>
متكلم، مفسر، من فقهاء الشافعية. أخذ عن كثير من شيوخ بلاده. من آثاره "حاشية" على أنوار التنزيل في التفسير، للبيضاوي (١).

القطان [... - ٢٥٢ هـ / ... - ٨٦٦ م]

<span data-type="title" id=toc-1870>يوسف بن موسى بن راشد بن بلال القطان، أبو يعقوب الكوفي:</span>
محدث، مفسر، من أهل الكوفة، نزل الري، ثم بغداد. روى عنه البخاري وأبو داود والترمذي وابن ماجه. ذكره الداوودي في طبقاته وقال: له "تفسير" (٢).

سنان الخلوتي [... - ٩٨٩ هـ / ١٥٨١ م]

<span data-type="title" id=toc-1871>يوسف بن يعقوب الرومي الخلوتي، سنان الدين، المعروف بسنان الخلوتي:</span>
_________
(١) هدية العارفين ٢: ٥٦٥ وخلاصة الأثر ٤: ٥٠٨.
(٢) طبقات المفسرين للداودي ٢: ٣٨٤ وخلاصة تهذيب الكمال ٣٧٨.','2','749',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1365,'فقيه حنفي، تركي مستعرب، مشارك في عدة علوم. تولى مشيخة الحرم النبوي، وتوفي بالمدينة المنورة. له كتب، منها "تضليل التأويل" (١).

الجمال المصرى [٥٥٥ - ٦٢٣ هـ / ١١٦٠ - ١٢٢٦ م]

<span data-type="title" id=toc-1872>يونس بن بدران بن فيروز بن صاعد بن غالي بن علي، الشيبي القرشي الحجازي الأصل، أبو الوليد، جمال الدين المصري:</span>
فقيه شافعي، فرضي، مفسر، مشارك في عدة علوم، ولد بمصر، وسمع من السلفي وغيره، وولي الوكالة السلطانية بالشام، ودرس بالأمينية والعادلية، وترسل عن "الملك العادل" إلى الخليفة، وإلى الملوك بالروم، وبلاد الشرق. وفي سنة ٦١٩ هـ ولي قضاء القضاة بالشام. قال السيوطي: هو أول من دؤس بالعادلية، وكان يقول أولا درسا في التفسير، فلما أكمل تفسير القرآن، توفي عقب ذلك، ودفن بداره (٢).
_________
(١) هدية العارفين ٢: ٥٦٤. وانسوا أيضا ترجمة يوسف بن حسام الدين في هذا المعجم فقد ذكر كتاب تضليل التأويل ومؤلفاته.
(٢) قضاة دمشق، ٦ وذيل الروضتين ١٤٨ والدارس ١: ١٨٦ ومرآة الزمان ٨: ٦. والعبر ٥: ٩٧ والنجوم الزاهرة ٦: ٢٦٦ وطبقات السبكي ٨: ٣٦٦ وطبقات المفسرين ٢: ٣٨٤ والشذرات ٥: ١١٢ والأعلام ٩: ٣٤٣ وحسن المحاضرة ١: ٤١١ والبداية والنهاية ١٣: ١١٤ وهدية العارفين ٢: ٥٧٢.','2','750',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1366,'يونس بن حبيب [٩٤ - ١٨٢ هـ / ٧١٣ - ٧٩٨ م]

<span data-type="title" id=toc-1873>يونس بن حبيب الضبي بالولاء، أبو عبد الرحمن، ويعرف بالنحوي:</span>
إمام نحاة البصرة في عصره، أديب، عالم بالشعر، عارف بطبقات شعراء العرب، من قرية "الجبل" على دجلة، بين بغداد وواسط. أخذ عن أبي عمرو بن العلاء، وأخذ عنه سيبويه وروى عنه في كتابه. وكانت له حلقة بالبصرة ينتابها أهل العلم وطلاب الأدب وفصحاء الأعراب والبادية. من كتبه "معاني القرآن" كبير وصغير (١).

بونس بن الرحمن [... - ٢٠٨ هـ / ... - ٨٢٣ م]

<span data-type="title" id=toc-1874>يونس بن عبد الرحمن مولى علي بن يقطين، أبو محمد:</span>
فقيه إمامي، محدث، مفسر، من أهل العراق، من أصحاب موسى بن جعفر سابع الأئمة
_________
(١) نزهة الأبلاء ٤٩ وبغية الوعاة ٢: ٣٦٥ ومراتب النحويين ٢١ ومعجم الأدباء ٢٠: ٦٤ ووفيات الأعيان ٧: ٢٤٤ ونور القبس ٤٨ والبيان والتبيين ١: ١٧٤ والمعارف ٥٤١ وابن النديم ٤٢ ومرآة الجنان ١: ٣٨٨ والشذرات ١: ٣٠٥ وتهذيب التهذيب ٥: ٣٤٦ وغايه النهاية ٢: ٤٠٦ والأعلام ٩: ٣٤٤ وطبقات المفسرين ٢: ٣٨٥.','2','750',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1367,'الإثني عشر، عند الإمامية. كان علي ابن موسى الملقب بالرضى، وثامن الأئمة الإثني عشر، يشبهه بسلمان الفارسي، له نحو ثلاثين كتابا، منها "تفسير القرآن" (١).

الوفراوندي [... - ... هـ / ... - ... م]

<span data-type="title" id=toc-1875>يونس بن محمد بن إبراهيم الوفراوندي:</span>
_________
(١) تنقيح المقال ٨٣٣ ومنتهى المقال ٣٣٦ ومنهج المقال ٣٧٧ وفهرست الطوسي ١٨١ وابن النديم ٢٢٠ والنجاشي ٣١١ والأعلام ٩: ٣٤٥ وهدية العارفين ٢: ٥٧٢.','2','751',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1368,'نحوي، له كتب، منها "الشافي في علم القرآن". ذكره ابن النديم المتوفى سنة ٤٣٨ هـ (١).
_________
(١) بغية الوعاة ٢: ٣٦٥ وطبقات المفسرين ٢: ٣٨٦ وابن النديم ٨٦ ومعجم الأدباء ٢٠: ٦٨ وهدية العارفين ٢: ٥٧٢. آخر الجزء الثاني من معجم المفسرين ويليه المستدرك بعد هذه الصفحة','2','751',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1369,'<span data-type="title" id=toc-1876>المستدرك</span>','2','753',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1370,'<span data-type="title" id=toc-1877>(أ)</span>
ابن فرتون [... - ٥٣٨ هـ / ... - ١١٤٣ م]

<span data-type="title" id=toc-1878>إبراهيم بن أحمد بن خلف بن الحسن السلمي، أبو إسحاق، ويعرف بابن فرتون:</span>
محدث، مفسر، أصولي، من فقهاء المالكية، من أهل فاس مولدا ووفاة. سمع بها وبسجلماسة والأندلس. (١)

الرياحي [١١٨٠ - ١٢٦٦ هـ / ١٧٦٦ - ١٨٥٠ م]

<span data-type="title" id=toc-1879>إبراهيم بن عبد القادر بن أحمد ابن إبراهيم الرياحي التونسي، أبو (إسحاق)</span>
_________
(١) شجرة النور ١٣٤ وتكلملة ابن الأبار ١: ١٧٥.','2','755',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1371,'إسحاق: عالم بالفقه والتفسير، متصوف مالكي. ولد في تستور، ونشأ وتعلم بتونس، وكان رئيس المفتين بها وإمامها وخطيبها بالجامع الأعظم. وحج ودخل مصر، وأرسله في سفارة إلى الآستانة. توفي بتونس. من كتبه "رسالة" في تفسير قوله تعالى: {إن الصلاة كانت على المؤمنين كتابا موقوتا} (١).

<span data-type="title" id=toc-1880>إبراهيم بن محمد، السفاقسي</span>
[(٧٤٢ هـ) تقدم في ١: ١٩] يزاد في ترجمته بعد ولخصه:
_________
(١) شجرة النور ٣٨٧ واليواقيت الثمينة ١: ٨٩ وفهرس الفهارس ١: ٣٢٨ =','2','755',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1372,'مخطوط، توجد ست نسخ منه في المكتبة الملكية بالرباط (من ٢٤٩ - ٩٨٣٨). ويزاد في هامشه: ومعجم المحدثين والمفسرين ٤٨.

التادلي [١٢٤٢ - ١٣١١ هـ / ١٨٢٧ - ١٨٩٤ م]

<span data-type="title" id=toc-1881>إبراهيم بن محمد بن عبد القادر الحسني الطالبي، أبو اسحاق التادلي:</span>
شيخ مشايخ الرباط في عصره، من كبار فقهاء المالكيه، مولده ووفاته بالرباط، تعلم بها وبفاس ومكناس، ورحل إلى المشرق مرتين، وحج وجاور. وعاد مارا بالبلاد الإسبانية فقرأ فيها بعض العلوم الحديثة، ودرس بالرباط أكثر من ثلاثين سنة، وبلغت تصانيفه مائة وعشرين، بعضها لم يتم، منها "حاشية" على الإتقان في علوم القرآن، في التفسير، للسيوطي. (١).
_________
= ومعجم المطبوعات ١٣٨١ وهدية العارفين ١: ٤٢ والاعلام ١: ٤٨.
(١) معجم المحدثين والمفسرين ١٠ والأعلام =','2','756',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1373,'المودودي [١٣٢١ - ١٣٩٩ هـ / ١٩٠٣ - ١٩٧٩ م]

<span data-type="title" id=toc-1882>أبو الأعلى المودودي:</span>
من كبار رجال الإصلاح والتجديد في الإسلام في القارة الهندية، كاتب، صحافي، مجاهد، ولد في مدينة "أورنك آباد" في ولاية حيدر آباد، وبها نشأ وتعلم. وعمل في الصحافة فتولى رئاسة تحرير عدة صحف مثل "تاج" و "مسلم" ثم أصدر مجلة "ترجمان القرآن" (١٩٢٣) لبث آرائه في الإصلاح الديني والإجتماعي فكان لها دور أساسي في نهضة الحركة الإسلامية في القارة الهندية. وفي سنة ١٩٤١ أسس "الجماعة الإسلامية" في مدينة لاهور، وانتخب أميرا لها. وبعد قيام دولة باكستان هاجر إلى لاهور حيث أنشأ حركة المطالبة بوضع دستور إسلامي للدولة الجديدة. وقد سجن واعتقل مرات، إلا أن ذلك لم يزده إلا تصميما على رفعة شأن الإسلام. ولم يقتصر نشاطه على
_________
= ١: ٧١، وفي الحاشية أسماء مصادر ترجمته.','2','756',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1374,'باكستان، وإنما تعداه إلى جميع الأقطار الإسلامية، فزار فلسطين ومصر والسعودية وغيرها، وشارك في عدة مؤتمرات إسلامية في القدس والمدينة المنورة. توفي بنيويورك إثر عملية جراحية. من آثاره "تفسير سورة النور" و "المصطلحات الأربعة في القرآن". (١).

الفارسي [... - ٣١٨ هـ / ... - ٩٣٠ م]

<span data-type="title" id=toc-1883>أحمد بن أحمد بن زياد الفارسي القيرواني، أبو جعفر:</span>
فقيه مالكي، مفسر، عارف باللغة، عالم بالوثائق، من أهل القيروان. من كتبه "أحكام القرآن" في عشرة أجزاء. (٢).

الشباسي [١٢١٣ - ١٢٩٢ هـ / ١٧٩٨ - ١٨٧٥ م]

<span data-type="title" id=toc-1884>أحمد بن أحمد، أبو العباس المعروف بمنة الله الشباسي:</span>
مفسر من
_________
(١) الموسوعة الحركية ١٣ ومذكرات المؤلف.
(٢) طبقات علماء إفريقية ١٦٨ والديباج ١٦٩ وشجرة النور ٨١.','2','757',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1375,'فقهاء المالكية، نسبته إلى شباس - وتعرف بشباس الملح - من قرى مصر. تعلم بالأزهر. من مؤلفاته "رسالة في البسملة" و "رسالة" في تفسير قوله تعالى: {يسألونك عن الخمر والميسر}. (١).

الحضرمي [... - ١٠٥٢ هـ / ... - ١٦٤٢ م]

<span data-type="title" id=toc-1885>أحمد بن حسين بن محمد بن علي بن أحمد بافقيه الحضرمي:</span>
عالم يماني، من أهل مدينة تريم، تعلم بها وبمكة والمدينة. ثم جاور بمكة وتوفي فيها. قال المحبي: "برع في التفسير والحديث والفقه" (٢)

الحباك [٨٠٤ - ٨٧٠ هـ / ١٤٠١ - ١٤٦٥ م]

<span data-type="title" id=toc-1886>أحمد بن سعيد القيمجيسي (المكناسي الورزيغي، أبو العباس، ويعرف بالحباك)</span>
_________
(١) اليواقيت الثمينة ٧٩ وشجرة النور ٣٨٤ ومعجم المطبوعات ٧١٨ والأعلام
(٢) ملحق البدر الطالع ٣٠ وخلاصة الأثر ١: ١٨٣.','2','757',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1376,'المكناسي الورزيغي، أبو العباس، ويعرف بالحباك: فقيه مالكي، عارف بالتفسير، أديب، ولد بمكناسة، وسكن فاس، وولي الخطابة بجامع القرويين، ودرس التفسير بالمدرسة المتوكلية. قال تلميذه محمد بن محمد اليسيثني: "قرئ عليه تفسير ابن عطية، وما أدركت أورع منه". (١).

النقاوسي [... - بعد ٧٦٥ هـ / ... - بعد ١٣٦٤ م]

<span data-type="title" id=toc-1887>أحمد بن العباس النقاوسي، أبو العباس:</span>
عالم بالتفسير، والفقه والحديث والأدب واللغة والنحو والمنطق، من أهل نقاوس بالجزائر. سكن تلمسان ثم استقر بتونس واشتغل بالتدريس. (٢).
_________
(١) إتحاف أعلام الناس: ١: ٣١٣ وشجرة النور ٢٦٤ ونيل الابتهاج ١: و ٣٣٨ ترجمة البسيثني. والأعلام ١: ١٢٦ ومعجم المؤلفين ١: ٢٣٤.
(٢) معجم أعلام الجزائر (ط ٣) ٣٣١ ونيل الابتهاج ٦٩ والحلل السندسية ٨١٤ و ٨٣١.','2','758',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1377,'ابن مضاء [٥١٣ - ٥٩٢ هـ / ١١٢٠ - ١١٩٦ م]

<span data-type="title" id=toc-1888>أحمد بن عبد الرحمن بن محمد ابن سعيد بن حريث بن عاصم بن مضاء اللخمي القرطبي، أبو العباس:</span>
محدث ثقة، مقرىء، مفسر، عارف بالفقه وأصوله وبالطب والهندسة والحساب، عالم بالعربية. أصله قديما من جيان ثم من شرانة - من قرى شريش - ومولده بقرطبة. ولي القضاء بفاس وبجاية، ثم بمراكش سنة ٥٧٨ هـ. وصرف عن القضاء، فعاد إلى أشبيلية وأقام بها يسمع الحديث إلى أن مات. من كتبه "تنزيه القرآن عما لا يليق من البيان" (١).

ابن عبد الصمد [٥١٩ - ٥٨٢ هـ / ١١٢٥ - ١١٨٧ م]

<span data-type="title" id=toc-1889>أحمد بن عبد الصمد بن أبي (عبيدة محمد بن أحمد، الأنصاري الخزرجي الساعدي - نسبة إلى سعد بن عبادة - أبو جعفر)</span>
_________
(١) معجم المحدثين والمفسرين ١: ٦٦ وجذوة الاقتباس ٧١ وبغية الملتمس ١٩٣، والتكملة ١: ٧٩ والديباج ٤٧ والإعلام بمن حل مراكش وأغمات من الأعلام ١: ٢٣٣ والذيل والتكملة ق ١: ٢١٢ والأعلام ١: ١٤٢ وبغية الوعاة.','2','758',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1378,'عبيدة محمد بن أحمد، الأنصاري الخزرجي الساعدي - نسبة إلى سعد بن عبادة - أبو جعفر: حافظ، محدث، عارف بالأدب، من فقهاء المالكية، ولد بقرطبة، وسكن غرناطة مدة وبجاية إخرى، ثم استوطن مدينة فاس، وتصدر لاسماع الحديث والتكلم على معانيه بجامع القرويين، وكف بصره في آخر عمره، فكان خادمه يكتب عنه، وتوفي بفاس. من كتبه "نفس الصباح" في غريب القرآن وناسخه ومنسوخه (١).

الهلالي [١١١٣ - ١١٧٥ هـ / ١٧٠١ - ١٧٦١ م]

<span data-type="title" id=toc-1890>أحمد بن عبد العزيز بن رشيد بن محمد الهلالي السجلماسي، أبو العباس، من ذرية أبي إسحاق ابن هلال:</span>
عالم بالتفسير والحديث واللغة
_________
(١) الوافي بالوفيات ٧: ٦٦ والتكملة ١: ٨٥ والذيل والتكملة ق ١: ٢٣٩ والديباج ٥٠ وشجرة النور ١٥٦ وجذوة الاقتباس ٧٠ وتعريف الخلف ٢: ٦١ والأعلام ١: ١٤٦.','2','759',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1379,'والمنطق، من فقهاء المالكية، له نظم. من أهل سجلماسة. حج مرتين، وأخذ عن علماء مصر والحجاز. توفي بمدغرة تافيلالت. من كتبه "تفسير القرآن" مخطوط. في المكتبة الملكية بالرباط رقم ١٠٥٧ (١).

البوني [... - ٦٢٢ هـ / ... - ١٢٢٥ م]

<span data-type="title" id=toc-1891>أحمد بن على بن يوسف، تقي الدين، أبو العباس البوني:</span>
صوفي، من أشهر المصنفين العرب في العلوم الخفية، من أهل بونة، المعروفة بعنابة، شرقي الجزائر. توفي بالقاهرة. من كتبه الكثيرة "تحفة الأحباب ومنية الأنجاب في أسرار بسم
_________
(١) شجرة النور ٣٥٥ واليواقيت الثمينة ١٩ وفهرس الفهارس ٣: ٤٢١ وهدية العارفين ١: ١٧٦ ونشر المثاني ٢: ٢٧٣ ومجلة دعوة الحق، مارس ١٩٧٤: ١٧٧، ومعجم المحدثين والمفسرين ١٦ ومعجم المطبوعات ١٨٩٣ ومعجم المؤلفين ١: ٢٧٥ والنبوغ المغربي ٢٩١ والأعلام ١: ١٥١.','2','759',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1380,'الله وفاتحة الكتاب" (١).

المنجور [٩٢٦ - ٩٩٥ هـ / ١٥٢٠ - ١٥٨٧ م]

<span data-type="title" id=toc-1892>أحمد بن علي بن عبد الرحمن، أبو العباس المنجور:</span>
عالم بالفقه والتفسير والكلام والمنطق والنحو والبيان والعروض والتاريخ، وكان موسيقيا بارعا، وأحد الأبطال في لعب الشطرنج والنرد. وكان أمير المؤمنين أبو العباس السعدي يجله ويكرمه. أصله من مكناس، أقام بفاس وتوفي بها. من آثاره "مراقي لآيات السعد" أو "مراقي المجيد في آيات السعيد" وهو تفسير للقرآن الكريم، توجد نسخة منه في مكتبة الأسكوريال (١٣٥٨ - ١٣٩٦) وأخرى في خزانة الرباط (٨١٢ د) (٢).
_________
(١) معجم أعلام الجزائر (ط ٣) ١: ٣١٤ ومعجم المطبوعات: ١: ٦٠٧ وتعريف الخلف ٢: ٥٢٢ وهدية العارفين ١: ٩٠ والأعلام ١: ١٦٩ وجامع كرامات الأولياء ١: ٣١٤.
(٢) صفوة من انتشر ٤ ونزهة الحادي =','2','760',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1381,'الزموري [بعد ٩٣٠ - ١٠٠١ هـ / بعد ١٥٢٤ - ١٥٩٤ م]

<span data-type="title" id=toc-1893>أحمد بن علي الزموري، أبو العباس:</span>
محدث، مفسر، من فقهاء المالكية، مولده ووفاته بفاس. كان له "تفسير" يقرأه بجامع الأندلس فيها. وفي يوم ختمه لأول مرة "حضر مجلسه من الخاصة والعامة من لا يعد من الناس" ثم بدأ بختمة أخرى فكان آخر ما فسره قوله تعالى: {سارعوا إلى مغفرة من ربكم وجنة عرضها السموات والأرض أعدت للمتقين}. ومات بعد ذلك (١).
_________
= ٢٧ و ١٣٣ ودوحة الناشر ٤٥ وإتحاف أعلام الناس ١: ٣١٩ والنبوغ المغربي ٢٥٠ وفهرس الفهارس ٢: ٦ وجذوة الاقتباس ٦٧ وسلوة الأنفاس ٣: ٦٠ والإعلام بمن حل مراكش ٢: ٣١ والنبوغ المغربي ٢٥٠ وشجرة النور ٢٨٧ ومعجم المحدثين والمفسرين ١٦ ومعجم المؤلفين ٢: ١٠ والأعلام ١: ١٨٠ ونيل الابتهاج
(١) محمد المنوني في مجلة البحث العلمي ٧: ٢٥٠ وشجرة النور ٢٩٤ وسلوة الأنفاس ١: ٢٧٠.','2','760',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1382,'المدني [١٠٧٠ - ١١٣٥ هـ / ١٦٦٠ - ١٧٢٣ م]

<span data-type="title" id=toc-1894>أحمد بن علي المدني، أبو العباس، نجيب الدين:</span>
نحوي، منطقي عارف بالتفسير، مولده ووفاته بالمدينة المنورة. درس بمدرسة رستم باشا وبالمسجد النبوي الشريف. من آثاره "شرح البسملة" في مجلد ضخم. (١).

المرسي [... - ٦٨٦ هـ / ... - ١٢٨٧ م]

<span data-type="title" id=toc-1895>أحمد بن عمر بن محمد، أبو العباس الأنصاري المرسي:</span>
زاهد كبير، عارف بالتفسير من أهل الإسكندرية، وأصله من الأندلس. أخذ عن أبي الحسن الشاذلي ولازمه وصار شيخ الطريقة بعده. درس تفسير ابن عطية وغيره، ولأهل الإسكندرية فيه اعتقاد كبير إلى
_________
(١) سلك الدرر ١: ١٤٨ وهدية العارفين ١: ١٧٠ ومعجم المؤلفيه ٢: ٢٣.','2','761',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1383,'اليوم. (١).

النفراوي [١٠٤٣ - ١١٢٥ هـ / ١٦٣٣ - ١٧١٣ م]

<span data-type="title" id=toc-1896>أحمد بن غنيم بن سالم النفراوي، أبو العباس:</span>
فقيه مالكي، مشارك في علوم التفسير والنحو وغيرها. انتهت إليه الرئاسة في المذهب المالكي بمصر. من كتبه "شرح البسملة" رسالة، (٢).

البوني [١٠٦٣ - ١١٣٩ هـ / ١٦٥٣ - ١٧٢٦ م]

<span data-type="title" id=toc-1897>أحمد بن قاسم بن محمد بن ساسي التميمي البوني، أبو العباس:</span>
فقيه مالكي، من كبارهم، عالم بالحديث، مفسر، من أهل بونة بالجزائر. رحل إلى المشرق، وحج،
_________
(١) النجوم الزاهرة ٧: ٣٧١ ونيل الابتهاج ٦٤ والوافي ٧: ٢٦٤ وشجرة النور ١٨٧ ونفح الطيب ٢: ٣٨٩ والأعلام ١: ١٧٩.
(٢) شجرة النور ٣١٨، واليواقيت الثمينة ١: ٤٠ وهدية العارفين ١: ١٦٩ وعجائب الآثار ١: ١٣٢ وانظر فهرسته.','2','761',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1384,'وأخذ عن علماء الأزهر. وعاد إلى الجزائر فأخذ عنه جماعة من العلماء. من كتبه الكثيرة "إتحاف الأقران ببعض مسائله القرآن" و "تحفة الأريب بأشرف غريب" اختصر فيه "غريب القرآن" للعزيزي (١).

ابن رشد [٤٨٧ - ٥٦٣ هـ / ١٠٩٤ - ١١٦٨ م]

<span data-type="title" id=toc-1898>أحمد بن محمد بن أحمد بن رشد، أبو القاسم، ويقال أبو العباس:</span>
قاض، من فقهاء المالكية، مولده ووفاته بقرطبة، وولي قضاءها سنة ٥٣٢ هـ، ثم استعفي فأعفي، وهو والد الفيلسوف ابن رشد. قال مخلوف: "له "تفسير" في أسفار" وليس في مصادر ترجمته ما يؤكد ذلك. (٢).
_________
(١) معجم أعلام الجزائر (ط ٣) ٤٩ وشجرة النور ٣٢٩ والتحفة المرضية ٧٧ وفهرس الفهارس ١: ١٦٩ وتعريف الخلف ٢: ٥١٥ والأعلام ١: ١٨٩.
(٢) بغية الملتمس ١٥٦ الترجمه ٣٦٩ وتاريخ قضاة الأندلس، ١٠٣ ترجمة حمد بن =','2','762',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1385,'القصار [... - حيا ٧٩٠ هـ / ... - ١٣٨٨ م]

<span data-type="title" id=toc-1899>أحمد بن محمد بن عبد الرحمن الأزدي، الشهير بالقصار، التونسي، أبو العباس:</span>
نحوي، مفسر، كان معاصرا لابن عرفة. من تآليفه "حاشية على الكشاف" في التفسير للزمخشري. (١).

اللجائي [٧٩٢ - ٨٤٣ هـ / ١٣٩٠ - ١٤٣٩ م]

<span data-type="title" id=toc-1900>أحمد بن محمد بن عيسى بن علي اللجائي، شهاب الدين:</span>
فقيه مالكي، عالم بالتفسير والقراءات والعربية والحساب، ولد بفاس وناب في قضائها، وحج، ودخل القاهرة. ثم انتقل إلى التكرور فأقام سنة أقرأ بها التفسير. (٢).
_________
= حمدين، وتاريخ فلاسفة الإسلام ١١٥ ترجمة ابنه الفيلسوف ابن رشد. وشجرة النور ١٤٦ الترجمة ٤٣٨.
(١) نيل الابتهاج ٧٤ وشجرة النور ٢٢٦.
(٢) الضوء اللامع ٢: ١٦٣. ومعجم المحدثين والمفسرين ١٧.','2','762',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1386,'المقري [٩٨٦ - ١٠٤١ هـ / ١٥٧٨ - ١٦٣١ م]

<span data-type="title" id=toc-1901>أحمد بن محمد بن يحيى بن عبد الرحمن بن أبي العيش، أبو العباس، المقري التلمساني:</span>
مؤرخ، أديب، حافظ، كان آية في علم الكلام والتفسير والحديث. ولد بتلمسان، وبها نشأ، وأخذ عن عمه سعيد المقري وانتقل إلى فاس سنة ١٠٠٩ هـ (١٦٠٠ م) وحضر مجلس علي ابن عمران السلاسي في جامع القرويين، وناقشه في بعض مسائل الفقه، فاعترف له السلاسي بالتفوق عليه وأقر له بقوة الحجة والنباهة. ثم انتقل إلى مراكش في نفس السنة فسر الخليفة المنصور السعدي بمقدمه وأكرمه وقربه. وتعرف المقري في مراكش على جماعة من العلماء والأدباء جرت بينه وبينهم مطارحات ومداعبات ومساجلات ذكر بعضها في كتابه "روضة الآس"، وفي منتصف ربيع الثاني سنة ١٠١٠ هـ (سبتمبر ١٦٠١ م) عاد إلى فاس ثم غادرها في منتصف ذي القعدة إلى مسقط','2','763',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1387,'رأسه تلمسان. وفي أوائل السنة ١٠١٣ هـ (١٦٠٤ م) قصد فاسا مرة ثانية، فأسندت إليه (سنة ١٠٢٢ هـ) ولاية الفتوى والخطابة والإمامة في جامع القرويين. وفي أواخر رمضان ١٠٢٧ هـ (سبتمبر ١٦١٨ م) خرج للحج، فدخل القاهرة (سنة
١٠٢٨ هـ = ١٦١٩ م) ومنها توجه
إلى الديار المقدسة، وعاد إلى القاهرة
(سنة ١٠٢٩ هـ = ١٦٢٠) فأقام نحو
شهرين، ثم دخل القدس الشريف والشام، وتكررت زياراته إلى الحجاز، وأملي بها دروسا عديدة. توفي بالقاهرة في جمادى الآخرة، ودفن بمقبرة المجاورين. وكل ما كتب حول مكان وتاريخ وفاته، غير هذا، فهو من أوهام المؤرخين. من آثاره "إعراب القرآن، (١).
_________
(١) معجم أعلام الجزائر (ط ٣) ٣٠٩، والمقري وكتابه نفح الطيب، للدكتور محمد بن عبد الكريم، والمقري صاحب نفح الطيب لمحمد عبد الغني حسن، =','2','763',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1388,'المهمنداري [١٠٢٤ - ١١٠٥ هـ / ١٦١٥ - ١٦٩٤ م]

<span data-type="title" id=toc-1902>أحمد بن محمد بن عبد الوهاب الحلبي، المعروف بالمهمنداري:</span>
فقيه حنفي، مفسر، مشارك في عدة علوم، ولد وتعلم بحلب، ووله الإفتاء بدمشق سنة ١٠٧٦ هـ. من آثاره "مباحث" في التفسير. (١).
_________
= والمقري صاحب نفح الطيب للجنحاني. وروضة الآس، مقدمته، وأزهار الرياض مقدمته، وخلاصة الأثر ١: ٣٠٢، وريحانة الألباب ٢: ١٧٤ واليواقيت الثمينة ١: ٢٩ والتاج المكلل ٣٢٤ وتراجم إسلامية ٣٧٣ وسلافة العصر ٥٨٩ وتعريف الخلف ١: ٤٤ والأعلام ١: ٢٢٦ وهدية العارفين ١: ١٥٧ وصفوة من انتشر ٧٢ والزاوية الدلائية ١٠٨ والاعلام بمن حل مراكش وأغمات من الاعلام ٢: ١٠٦ وتاريخ آداب اللغة العربية ٣: ٤٢٤ ودائرة المعارف الإسلامية. والاستقصا. ومجلة العربي مارس ١٩٦٣ وأكتوبر ٦٧ ونفح الطيب مقدمة الدكتور إحسان عباس.
(١) سلك الدرر: ١: ١٨٦.','2','764',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1389,'الباقاني [١١١٨ - ١١٩٥ هـ / ١٧٠٦ - ١٧٨١ م]

<span data-type="title" id=toc-1903>أحمد بن محمد الباقاني النابلسي:</span>
مفسر، محدث، أصولي، نحوي، عارف بالأدب. من أهل نابلس، ونسبته إلى "باقة" من أعمالها. تعلم بدمشق، واشتغل بالتدريس في نابلس. وتوفي بها. (١).

التجاني [١١٥٠ - ١٢٣٠ هـ / ١٧٣٧ - ١٨١٥ م]

<span data-type="title" id=toc-1904>أحمد بن محمد بن المختار بن أحمد الشريف، أبو العباس التجاني:</span>
شيخ "الطائفة التجانية" بالمغرب، عالم بالتفسير، والأصول والفروع، من فقهاء المالكية، ولد في "عين ماضي" بالجزائر. تعلم بفاس، وحج، وأقام بتلمسان يدرس التفسير
_________
(١) سلك الدرر ١: ١٩١.','2','764',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1390,'والحديث، وتوفي بفاس (١).

<span data-type="title" id=toc-1905>أحمد بن محمد، ابن عجيبة</span>
[(١٢٢٤ هـ) تقدم في ١: ٧٧] يزاد في نهاية ترجمته بعد مجلدات: توجد ست نسخ منه في المكتبة الملكية بالرباط. وطبع بمصر.

ابن الخياط [١٢٥٢ - ١٣٤٣ هـ / ١٨٣٦ - ١٩٢٤ م]

<span data-type="title" id=toc-1906>أحمد بن محمد بن عمر بن عبد الهادي الزكاري الفاسي، أبو العباس، ابن الخياط:</span>
من أكابر فقهاء المالكية في عصره، عارف، بالتفسير والحديث والفرائض، مولده ووفاته بفاس. من كتبه "جواب لمن يتصدى لقراءة التفسير وليس أهلا له" طبع (٢).
_________
(١) معجم أعلام الجزائر (ط ٣) ٦٢ وشجرة النور ٣٣٨ والأعلام ١: ٢٤٥.
(٢) معجم المحدثين والمفسرين ١٧ وشجرة النور ٤٣٦ وفهرس الفهارس ١: ٢٨٨ =','2','765',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1391,'الحسناوي [... - ١٣٢٨ هـ / ... - ١٩١٠ م]

<span data-type="title" id=toc-1907>أحمد بن موسى السلاوي الحسناوي:</span>
عالم بالحديث والتفسير، من فقهاء المالكية. له "تقاييد" على كلام شيخه أبي المواهب سيدي العربى بن السائح على آيات قرآنية وأحاديث نبوية (١).

ابن فرتون [... - ٦٦٠ هـ / ... - ١٢٦٢ م]

<span data-type="title" id=toc-1908>أحمد بن يوسف بن أحمد بن يوسف بن إبراهيم السلمي الفاسى، أبو العباس، المعروف بابن فرتون:</span>
مؤرخ، عارف بالتفسير والحديث والرجال. ولد بفاس، وتعلم بها وبسبتة، ثم دخل الأندلس سنة ٦٣٥ هـ، فأخذ عن علماء مالقة
_________
= ورياض الجة ١: ١٢٧ والأعلام الشرقية ٣: ٨٢ ودليل مؤرخ المعرب ٢: ٣٢٧ الأعلام ١: ٢٥٠ ومعجم المؤلفين
(١) معجم المحدثين والمفسرين ١٧ وأعلام الفكر المعاصر ٢: ٧٣.','2','765',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1392,'والجزيرة الخضراء وعاد إلى سبتة، فاستقر بها إلى أن توفي عن سن عالية. من آثاره "الاستدراك والإتمام للتعريف والإعلام" استدرك فيه على السهيلي (انظر ترجمته) في كتابه "التعريف والإعلام". (١).

المحاسني [... - ١١٠٢ هـ / ... - ١٦٩١ م]

<span data-type="title" id=toc-1909>إسماعيل بن تاج الدين بن أحمد المحاسني:</span>
خطيب الجامع الأموي
_________
(١) النبوغ المغربي ١٥٥ ومعجم المحدثين والمفسرين ٢٢ ترجمة السهيلي، وشجرة النور ٢٠٠ ومجلة رسالة المغرب عدد ١٤٢: ١٢ وفهرس الفهارس ٢: ٢٧٢ وجذوة الاقتباس ٤٦ ونيل الابتهاج ٦٣ =','2','766',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1393,'وإمامه، عارف بالتفسير، مولده ووفاته بدمشق. ولي تدريس التفسير بالمدرسة النقوية وغيرها. وكان أديبا حسن النظم. (١).

النابلسي [... - ١١٦٣ هـ / ... - ١٧٥٠ م]

<span data-type="title" id=toc-1910>إسماعيل بن عبد الغني بن إسماعيل النابلسي:</span>
فقيه حنفي، مفسر، مولده ووفاته بدمشق. درس تفسير البيضاوي في صالحية دمشق (٢).
_________
= ومعجم المؤلفين ٢: ٢٠٨ والأعلام ١: ٢٧٤.
(١) سلك الدرر ١: ٢٥٠ والأعلام ١: ٣١١.
(٢) سلك الدرر ١: ٢٥٦.','2','766',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1394,'<span data-type="title" id=toc-1911>(ب)</span>
السلوي [... - ١٣٧٦ / ... - ١٩٥٧ م]

<span data-type="title" id=toc-1912>أبو بكر بن الطاهر بن حجي زنيبر السلوي:</span>
فقيه مالكي، قاض، مفسر، من أهل سلا، ولي القضاء والإفتاء، من آثاره "إرشاد الله" في تفسير القرآن، مخطوط، في عدة مجلدات (١).

معلم الوزير [... - ١٠٧٧ هـ / ... - ١٦٦٧ م]

<span data-type="title" id=toc-1913>أبو بكر (المنلا) بن عبد الرحمن الكردي الحريري، المعروف بمعلم الوزير:</span>
فقيه شافعي، قاض، مفسر. قدم دمشق سنة ١٠٧١ هـ. وولي قضاء صيدا. قال المحبي: "درس التفسير بالجامع الأموي، وكان فضلاء الأكراد يحضرون درسه، وهو آخر من
_________
(١) معجم المحدثين والمفسرين ١٥، وأعلام الفكر المعاصر ٢: ٢٦٦.','2','767',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1395,'أدركناهم بدمشق من محققي الأكراد، وتوفي بها" (١). الدكالي []

<span data-type="title" id=toc-1914>بلقاسم بن محمد بن إبراهيم الدكالي، أبو محمد:</span>
نحوي، أديب، مفسر، من فقهاء المالكية، نسبته إلى دكالة، بلدة بالمغرب. قال أبو العباس المنجور: "كان من الأساتيذ المعتبرين، عارفا بعلوم القرآن أداء ورسما وتفسيرا، وكان ينقل عن التفسير كلام فارسي التفسير ابن عطية والزمخشري، ويضيف إلى ذلك من كلام الصفاقسي وغيره " (٢).
_________
(١) خلاصة الأثر: ١: ٨٦.
(٢) محمد المنوني في مجلة البحث العلمي ٧: ٢٦٤ ودوحة الأثر ٤٥ وجذوة الاقتباس ٣١٩.','2','767',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1396,'<span data-type="title" id=toc-1915>(ج)</span>
الكتاني [١٢٥٠ - ١٣٢٣ هـ / ١٨٣٤ - ١٩٠٥ م]

<span data-type="title" id=toc-1916>جعفر بن إدريس الحسني الكتاني، أبو المواهب:</span>
محدث، نسابة، صوفي، من فقهاء المالكية. مولده ووفاته بفاس. ترجم الكثيرين من مشاهير أسرته، وأهل القرن الثالث عشر والأئمة الأعلام. من كتبه "تفسير الفاتحة" (١).
_________
(١) رياض الجنة ١: ١٧٣ وفهرس الفهارس ١: ١٣١ وشجرة النور ٤٣٣ ومعجم المطبوعات ١٥٤٥ والأعلام الشرقية ٢: ٩٢ ومصادر الدراسة الأدبية ٣: ١٠٥٦ والأعلام ٢: ١١٥ ومعجم المؤلفين ٣: ١٣٣.','2','768',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1397,'البتروني [... - ١٠٣٩ هـ / ... - ١٦٢٩ م]

<span data-type="title" id=toc-1917>أبو الجود بن عبد الرحمن بن محمد بن عبد السلام بن أحمد البتروني الحلبي:</span>
مفتي الحنفية بحلب، انتقل إليها أبوه من البترون - قرب طرابلس بلبنان - سنة ٩٦٤ هـ، وبها ولد صاحب الترجمة وتعلم وصار صدر البلاد الحلبية ومفتيا ومدرسا. وكان بارعا في التفسير والفقه الحنفي. وولي قضاء القدس والمدينة. (١).
_________
(١) خلاصة الأثر ١: ١١٤ وترجمة ابنه ١: ١٠.','2','768',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1398,'<span data-type="title" id=toc-1918>(ح)</span>
المسيلي [... - نحو ٥٨٠ هـ / ... - نحو ١١٨٥ م]

<span data-type="title" id=toc-1919>حسن بن علي بن محمد المسيلي، أبو علي:</span>
عالم جزائري، من فقهاء المالكية. نسبته إلى المسيلة، نشأ في مدينة بجاية وولي قضاءها وتوفي بها. من كتبه "التفكر فيما تشتمل عليه السور والآيات من المبادىء والغايات" (١).
_________
(١) معجم أعلام الجزائر (ط ٣) ٢٩٩ وعنوان الدراية (بتحققنا) ٣٣ ونيل الابتهاج ١٠٤ وأنس الفقير ٣٤ وتعريف الخلف ١: ٥٩ والأعلام ٢: ٢٢٠ ومعجم المؤلفين ٣: ٦٢ م.','2','769',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1399,'العشماوي [١٣٤٠ - ١٣٩١ هـ / ١٩٢١ - ١٩٧٢ م]

<span data-type="title" id=toc-1920>حسن بن محمد العشماوي:</span>
كاتب، محام، من رجالات النهضة الإسلامية الحديثة وقادة حركة الإخوان المسلمين. ولد في المنيا بصعيد مصر، وأتم دراسة الحقوق في القاهرة سنه ١٩٤٢، ثم تخصص في القانون الخاص والشريعة الإسلامية. مارس أعمال النيابة العامة والمحاماة والقضاء، وشارك في الحياة السياسية المصرية، واختير عضوا في لجنة وضع الدستور المصري سنة ١٩٥٣. مات في الكويت من آثاره "مع','2','769',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1400,'القرآن - زاد الرحلة الأولى في كتاب الله"، في التفسير (١).

اليوسي [١٠٤٠ - ١١٠٢ هـ / ١٦٣٠ - ١٦٩١ م]

<span data-type="title" id=toc-1921>الحسن بن مسعود بن محمد، أبو علي، نور الدين اليوسي:</span>
من كبار فقهاء المالكية، ونابغة علماء عصره. نسبته إلى قبيلة آيت يوسي من سجلماسة ودرعة وسوس ومراكش ودكالة، ثم عاد إلى الزاوية ودرس بها، وانتقل إلى فاس فتصدر للتدريس بالقرويين. ثم خرج إلى البادية واستوطن قبيلته، ودرس بها، فانتشرت عنه فنون المعارف في قبائل المغرب، وتوفي في قبيلته. قال أحد مترجميه: "مكث في تفسير الفاتحة بمراكش قريبا من ثلاثة أشهر" (٢).
_________
(١) مع القرآن - كلمة الناشر. ومذكرات الؤلف.
(٢) النبوغ المغربي ٢٨٥ ومعجم المحدثين والمفسرين ١٩ والاستقصا ٤: ٥١ وصفوة من انتشر ٢٠٦ وشجرة النور ٣٢٨ وفهرس الفهارس ٢: ٤٦٤ وسلوة الأنفاس ٣: ٨١ ونشر المثاني ٢: ١٤٢ واليواقيت الثمينة =','2','770',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1401,'التونسي [... - ١٣٢٣ هـ / ... - ١٩٠٥ م]

<span data-type="title" id=toc-1922>حسين بن أحمد بن حسين التونسي، أبو محمد:</span>
عالم تونس ومفتيها في عصره. قال مخلوف: "ختمت بعصره أعصر العلماء الأعلام ... آية الله تعالى في التفسير". (١).

السملالي [... - ٨٩٩ هـ / ... - ١٤٩٤ م]

<span data-type="title" id=toc-1923>الحسين بن علي بن طلحة الشوشاوي، أبو عبد الله الركراكي السملالي:</span>
مفسر مغربي، مقرىء، فقيه، من بلاد سوس. توفي بتارودنت ودفن برأس وادي سوس. من آثاره: "الفوائد الجميلة على الآيات الجليلة"
_________
= ١: ١٣٣ ومجلة المغرب الجديد س ١ عدد ٤: ١٧ و ٥: ٢٤ و ٧: ١٧ ودليل مؤرخ المغرب ١: ٨٩ وهدية العارفين ١: ٢٩٦ وتاريخ آداب اللغة العربية ٣: ٣٠٦ وعجايب الآثار ١: ١٧٣ والأعلام ٢: ٢٢٣ ومعجم المطبوعات ١٩٥٩ ومعجم المؤلفين.
(١) شجرة النور: ٤١٧.','2','770',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1402,'مباحث في نزول القرآن وكتابته، م<span data-type="title" id=toc-1924>خ</span>طوط، منه نسخة في المكتبة الملكية بالرباط (١٤٦٥) وأخرى في الظاهرية بدمشق. (١)

(خ)
الحمصاني [... - ١١٢٣ هـ / ... - ١٧١١ م]

<span data-type="title" id=toc-1925>خليل بن محمد بن عمر بن أحمد بن رمضان، المعروف بالحمصاني الدمشقي:</span>
مفسر،
_________
(١) معجم المحدثين والمفسرين ١٩ والنبوغ المغربي: ٢١٦ والأعلام ٢: ٢٤٧.','2','771',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1403,'خطيب، من فقهاء الشافعية، مولده ووفاته بدمشق. درس بالمدرسة الحجازية وولي الإمامة في الجامع الأموي. قال المرادي: "كان علامة له يد طولى في التفسير، يحل مشكلات البيضاوي". (١).
_________
(١) سلك الدرر ٢: ٩٨.','2','771',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1404,'<span data-type="title" id=toc-1926>(ر)</span>
اليعقوبية [أوائل ١٤ هـ / أواخر ١٩ م]

<span data-type="title" id=toc-1927>رقية بنت الحاج أمين العايش اليعقوبية:</span>
عارفة بالتفسير، من أهل شنقيط. كانت تتوخى أسباب الن<span data-type="title" id=toc-1928>ز</span>ول وعلوم القرآن. (١).

(ز)
المقدسي [... - ١٠٣٥ هـ / ... - ١٦٢٦ م]

<span data-type="title" id=toc-1929>زكريا بن إبراهيم بن عبد العظيم ابن أحمد، أبو يحيى المعري المقدسي:</span>
مفسر، من فقهاء الحنفية، مشارك في عدة علوم، ولد في القدس (بفلسطين) وتعلم بها وبالقاهرة. قال المحبي: كان فقيها مفسرا له باع طويل في كثير من الفنون، ولي افتاء الحنفية بالقدس، ودرس وأفاد ... " (٢)
_________
(١) معجم المحدثين والمفسرين ٢٠.
(٢) خلاصة الأثر ٢: ١٧٢.','2','772',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1405,'<span data-type="title" id=toc-1930>(س)</span>
<span data-type="title" id=toc-1931>سعدي ياسين</span>
[١٣٠٧ - ١٣٩٦ هـ / ١٨٨٧ - ١٩٧٦ م]
سعدي ياسين: داعية إسلامي، عارف بالتفسير والفقه، خطيب، مدرس، ولد بدمشق، وتعلم بها وبالمدينة المنورة وبيروت، ثم درس بالحجاز (١٩٢٧) وبيروت (١٩٢٩)، وشارك في وفود الدعوة إلى الله في السودان وإيران وأفريقيا. كما كان عضوا في المجلس الشرعي الإسلامي الأعلى وفي جمعية مكارم الأخلاق الإسلامية. توفي في بيروت. من آثاره مخطوط عن "إعجاز القرآن وتفسير بعض آياته" (١).
_________
(١) المرسوعة الحركية: ٩٥.','2','773',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1406,'الكواكبي [١٠٩٠ - ١١٣٧ هـ / ١٦٧٩ - ١٧٢٥ م]

<span data-type="title" id=toc-1932>أبو السعود بن أحمد بن محمد الكواكبي:</span>
فقيه حنفي، مفسر، من أهل حلب، وولي الإفتاء بها واشتغل بالتدريس، قال المرادي: "أقرأ التفسير مدة إفتائه بالمدرسة الخسروية المشروطة لمفتي حلب قراءة تحقيق" (١).

المولى سليمان [١١٨٠ - ١٢٣٨ هـ / ١٧٦٦ - ١٨٢٢ م]

<span data-type="title" id=toc-1933>سليمان بن محمد بن عبد الله بن (إسماعيل، أبو الربيع، الشريف العلوي)</span>
_________
(١) سلك الدرر: ١: ٥٧ ومعجم المؤلفين ٤: ٢١٧.','2','773',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1407,'إسماعيل، أبو الربيع، ال<span data-type="title" id=toc-1934>ش</span>ريف العلوي: من سلاطين دولة الأشراف العلويين في مراكش. بويع بفاس سنة ١٢٠٦ هـ، وامتنعت عليه مراكش، فزحف إليها سنة ١٢١١ هـ، فبايعه أهلها، وأقام فيها مدة ثم انتقل إلى مكناسة. وتوفي بمراكش. كان محبا للعلم والعلماء، وله عناية بالحديث والتفسير. قال مخلوف: "وتصدر لإقراء العلوم وأفاد وأجاد، وحضر دروسه في "التفسير" الشيخ إبراهيم الرياحي" (١).

(ش)
الدكالي [... - ١٣٥٦ هـ / ... - ١٩٣٧ م]

<span data-type="title" id=toc-1935>أبو شعيب بن عبد الرحمن الدكالي الصديقي:</span>
حافظ عصره، كان آية في علوم القرآن وقراءاته وإعرابه وناسخه ومنسوخه وأنواع تفسيره، نادرة في علوم الحديث رواية ودراية. وللأستاذ إبراهيم بن أحمد الكتاني كتاب في سيرته تحت عنوان "أبو شعيب والسلفية" تعرض فيه للسلفية "وما كان لأبي شعيب من الأثر في الوسط المغربي حتى انقشعت سحب الجهل والاعتقادات الفاسدة عن كثير من الناس" (٢).
_________
(١) مجلة دعوة الحق عدد ٤ (فبراير ١٩٦٨) بحث لعلامة المغرب عبد الله كنون تحت عوان: "عناية المولى سليمان العلوي بعلم التفسير". وشجرة النور ٣٨٠ وفهرس الفها رس ٢: ٣٢٨ والاستقصا ٤: ١٢٩ ومعجم المحدثين والمفسرين ٢٠ والأعلام ٣: ١٩٧.
(٢) أعلام الفكر ٢: ٢٦٩ ودليل مؤرخ المغرب ٢٤١ رقم ٩٥٦ ومعجم المحدثين والمفسرين ١٥.','2','774',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1408,'<span data-type="title" id=toc-1936>(ص)</span>
الشرواني [١٠٣٢ - ١١٢٠ هـ / ١٦٢٣ - ١٧٠٨ م]

<span data-type="title" id=toc-1937>صادق بن روح الله بن محمد الأمين الشرواني:</span>
مفتي الديار الرومية، قاض، مفسر، من فقهاء الحنفية. من أهل القسطنطينية، وتعلم بها. ولى قضاء دمشق، فقضاء مصر، فقضاء القسطنطينية، فقضاء العسكرين. ثم ولي الإفتاء بدار السلطنة (١١١٨ هـ). له "تحريرات" على مباحث من التفسير (١).
_________
(١) سلك الدرر ٢: ٢٠٢.','2','775',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1409,'الشريف السجلماسي [... - ١٢٧٩ هـ / ... - ١٨٦٣ م]

<span data-type="title" id=toc-1938>الصادق بن محمد الهاشمي الشريف السجلماسي:</span>
فقيه مالكي، قاض، عالم، في الحديث والتفسير، من أهل سجلماسة، وولي قضاءها، كان السلطان محمد بن عبد الرحمن ابن هشام (من ملوك الدولة السجلماسية العلوية بالمغرب) يحضر مجلسه في الحديث. (١).
_________
(١) معجم المحدثين والمفسرين ٢٠.','2','775',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1410,'صالح بن عمر [١٢٨٧ - ١٣٤٧ هـ / ١٨٧١ - ١٩٢٨ م]

<span data-type="title" id=toc-1939>صالح بن عمر بن داود بن صالح ابن يحمد الأعلى:</span>
مفسر، من علماء الإباضية في المغرب الكبير. ولد في بني يسقن، وبها نشأ وتعلم. فقد بصره في الخامسة من عمره، وحج مرتين واجتمع إلى علماء الحجاز والمجاورين في الحرم الشريف وبحث معهم في المسائل العلمية وفي مشاكل العالم الإسلامي، كما حضر دروس الأزهر الشريف وجالس عددا من كبار علمائه أثناء رحلته إلى الديار المقدسة. أنشأ معهدا للعلوم الشرعية والعربية في مسقط رأسه (سنة ١٨٨٩ م) وكان يقوم بالتدريس فيه','2','776',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1411,'وحده. من آثاره "القول الوجيز في كلام الله العزيز" في التفسير، لم يكمله. (١).

الصالحي [١٠٤٥ - ١١٢٠ هـ / ١١٣٦ - ١٧٠٨ م]

<span data-type="title" id=toc-1940>أبو الصفا بن أحمد بن أيوب العدوي الصالحي:</span>
قاض، من فقهاء الحنفية، مفسر، نحوي، ولد بدمشق، وولي الإفتاء بها، كما ولي قضاء قارا (٢)

بنت المختار [... - ١٣٢٣ هـ / ... - ١٨٩٩ م]

<span data-type="title" id=toc-1941>صفية بنت المختار:</span>
عارفة بالتفسير والتجويد، من أهل المغرب. اشتغلت بالتدريس. (٣).
_________
(١) نهضة الجزائر الحديثة ٢: ١٤٤.
(٢) سلك الدرر ١: ٦٢.
(٣) معجم المحدثين والمفسرين ٢٠.','2','776',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1412,'<span data-type="title" id=toc-1942>(ط)</span>
<span data-type="title" id=toc-1943>الطباطبائي</span>
[١٣٢١ - ١٤٠٤ هـ / ١٩٠٣ - ١٩٨٣ م]
مفسر، فيلسوف زاهد، من أكابر علماء الشيعة الإمامية، ولد في تبريز، وبها أكمل تعليمه الإبتدائي، وفي الثانية والعشرين من عمره انتقل إلى النجف في العراق، حيث واصل دراسته، وكان أكثر اهتمامه منصبا على الحكمة والأصول والتفسير والفقه، ولم يغادر النجف حتى نال درجة الاجتهاد، عاد بعدها إلى إيران وتصدر للتدريس في مدينة "قم" فاجتمع حوله المئات من طلبة العلوم الدينية من كل أنحاء إيران ليتعلموا على يديه العلم والتربية والفكر. من آثاره "الميزان" فى تفسير القرآن، في عشرين مجلدا، طبع قيل: "استغرق منه عشرين سنة متتالية متتابعة". (١).
_________
(١) جريدة كيهان العربي العدد ١٥٥، ٤ صفر ١٤٠٤ = ١٠/ ١١/ ٨٣.','2','777',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1413,'الديري [... - ١٠٧١ هـ / ... - ١٦٦١ م]

<span data-type="title" id=toc-1944>طه بن صالح بن يحيى بن محمد، أبو الرضا، الديري المقدسي:</span>
فقيه حنفي، أصولي، مفسر، مولده ووفاته بالقدس الشريف. وولي نيابة الحكم بها (١٠٢٢ هـ) وبمكة (١٠٤٤ هـ). قال المحبي: "درس التفسير بالباب القبلي في الصخرة" (١).

الجبريني [١١٠٥ - ١١٧٨ هـ / ١٦٩٣ - ١٧٦٤ م]

<span data-type="title" id=toc-1945>طه بن مهنا الجبريني المحتد، الحلبي:</span>
مفسر، عارف بالحديث، من فقهاء الشافعية. مولده ووفاته بحلب، تعلم بها وبالحجاز. له تصانيف (٢).
_________
(١) معجم أعلام فلسطين (مخطوط) وخلاصة الأثر ٢: ٢٦ وبلادنا فلسطين ١٠/ ٢: ١١٨.
(٢) إعلام النبلاء ٧: ٣١ وعنه أخذت تاريخ ولادته وسلك الدرر ٢: ٢١٩ وولادته فيه سنة ١٠٨٤ هـ. والأعلام ٣: ٣٣٤.','2','777',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1414,'<span data-type="title" id=toc-1946>(ع)</span>
ابن مغيزل [... - ١١٣٩ هـ / ... - ١٧٣٧ م]

<span data-type="title" id=toc-1947>عبد الباقي بن عبد الرحمن بن محمد، المعروف بابن مغيزل:</span>
فقيه شافعي مفسر، مشارك في عدة علوم. ميلاده ووفاته بدمشق. قال المرادي: "كانت به منقبة عجيبة ماتت بموته، وهي أنه كان إذا حضر في محضر فيه أحد من أهل العلم ذكر في التفسير مبحثا من تفسير البيضاوي أو تفسير الزمخشري، ينتشر البحث ويستفيد غالب الحاضرين" (١).
_________
(١) سلك الدرر ٢: ٢١٣.','2','778',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1415,'<span data-type="title" id=toc-1948>عبد الحق بن غالب، ابن عطية</span>
[(٥٤٢ هـ) تقدم في ١: ٢٥٧] يزاد في ترجمته بعد "المحرر الوجيز في تفسير الكتاب العزيز": مخطوط، منه خمسة أجزاء بخزانة جامعة القرويين (ق ١٨٦) وبالمكتبة الملكية بالرباط اثنتا عشرة نسخة. وقد بوشر بطبعه. ويزاد في هاشمه: ومعجم المحدثين والمفسرين ١٢ وتاريخ ابن خلدون ٧: ٧٩٤.

الشرفي [... - ١٠٧٧ هـ / ... - ١٦٦٦ م]

<span data-type="title" id=toc-1949>عبد الحفيظ بن عبد الله المهلا (الهدوي الشرفي)</span>','2','778',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1416,'الهدوي الشرفي: من كبار علماء اليمن في وقته. أخذ عن علماء صنعاء وزبيد وحصن شهارة. قال المحبي: "كان لا يمر في علم التفسير والفقه والحديث والنحو والصرف والمعاني والبيان وسائر العلوم راو إلا وأملى أحواله وأخباره .. وقرأ من كتب التفسير الكشاف، والثمرات، وتجريد الكشاف، والإتقان للسيوطي، والبغوي، والبيضاوي .. ". (١).

المولى عبد الحفيظ [١٢٨٠ - ١٣٥٦ هـ / ١٨٦٣ - ١٩٣٧ م]

<span data-type="title" id=toc-1950>عبد الحفيظ بن الحسن بن محمد الحسني العلوي، أبو المواهب:</span>
من سلاطين الدولة العلوية في المغرب الأقصى. ولد بفاس. انتدبه السلطان عبد العزيز بن الحسن عاملا (خليفة) بمراكش، فنادى به الجنود وأهل القبائل سلطانا فيها سنة ١٣٢٥ هـ (١٩٠٧ م). وانقسمت
_________
(١) خلاصة الأثر ٢: ٣٠٦.','2','779',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1417,'الدولة بين عبد العزيز في فاس وأخيه عبد الحفيظ في مراكش. واتخذ كل منهما دولة أجنبية لمناصرته. وخلع عبد العزيز بفاس، وانتظم الأمر لعبد الحفيظ، فانتقل إلى فاس. وقام أخ ثان له (المولى زين) بثورة في مكناس، فاستنجد عبد الحفيظ بفرنسا، فقضت على الثورتين، وأعلنت حمايتها للمغرب بعد أن أمضى عبد الحفيظ "معاهدة ٣٠ مارس ١٩١٢" المعروفة بمعاهدة الحماية، ثم أنزل عن العرش في ٣ أغسطس من السنة نفسها، ورحل إلى فرنسا، ثم استقر باسبانيا حتى سنة ١٩٢٥، عاد بعدها إلى فرنسا وأقام بها إلى أن مات، وكان فقيها أديبا عارفا بالتفسر والحديث. من آثاره "نيل النجاح والفلاح في علم ما به القرآن لاح" أرجوزة في إعجاز القرآن، طبعت بفاس سنة ١٣٢٧ هـ (١).
_________
(١) الموسوعة العربية الميسرة ١١٨٠ ومعجم المحدثين والمفسرين ٢١ والأعلام ٥: ٥٠ وتاريخ الدول الإسلامية ومعجم الأسر الحاكمة ٩٨.','2','779',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1418,'الوغليسي [... - ٧٨٦ هـ / ... - ١٣٨٤ م]

<span data-type="title" id=toc-1951>عبد الرحمن بن أحمد الوغليسي، أبو زيد:</span>
شيخ الجماعة بجاية في عصره. نعته مخلوف بالفقيه الأصولي المحدث المفسر، وقال: "كان عمدة أهل زمانه وفريد عصره وأوانه" أشهر مؤلفاته كتاب "الأحكام الفقهية" (١).

الحضرمي [٩٤٥ - ١٠١٤ هـ / ١٥٣٨ - ١٦٠٥ م]

<span data-type="title" id=toc-1952>عبد الرحمن بن أحمد بن عبد الرحمن بن علي بن أبي بكر السقاف الحسيني الحضرمي:</span>
قاضي القضاة ومفتي الشافعية بديار حضرموت، مولده ووفاته في تريم. برع في التفسير والحديث، وتخرج به جماعة (٢).
_________
(١) شجرة النور ٢٣٧ ونيل الابتهاج ١٦٨ ومعجم المؤلفين ٥: ١٢٣.
(٢) ملحق البدر الطالع ١١٧ وخلاصة الأثر ٢: ٣٥٩.','2','780',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1419,'<span data-type="title" id=toc-1953>عبد الرحمن بن عبد الله السهيلي</span>
[(٥٨١ هـ) تقدم في ١: ٢٦٧] يشار إلى أن كتابه "التعريف والإعلام" مخطوط، منه نسخة بالمكتبة العامة بالرباط (١٢٠٢ هـ د) وست نسخ في المكتبة الملكية بالرباط (١٦٠٥ - ٦٢٤٥). قال الأستاذ عبد العزيز بنعبد الله: وفي نسختين أيضا (٦٢٤٥ - ٤٦٦٤) يحمل الكتاب اسم تنبيه الأنام وعليه تعليقان هما: • التكميل والإتمام لكتاب التعريف والإعلام "لمحمد بن علي بن خضر بن هارون بن عسكر الغساني المتوفي ٦٣٦ هـ وتوجد نسخة منه بدار الكتب
المصرية) (٨٧٢ = تفسير).
• الإستدراك والإتمام للتعريف والإعلام. نسبه صاحب "جذوة الاقتباس" (ص ٥٧) لأبي العباس أحمد بن يوسف بن أحمد بن فرتون السلمي المتوفي ١٠٦٠ هـ / ١٦٦١ م.','2','780',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1420,'ويزاد في نهاية هاشمه: ومعجم المحدثين والمفسرين ٢٢.

<span data-type="title" id=toc-1954>عبد الرحمن بن محمد القصري</span>
[(١٠٣٦ هـ) تقدم في ١: ٢٧٦] يزاد في نهاية ترجمته بعد الجلالين: مخطوط في المكتبة
العامة بالرباط ٦٢٧ و ١٨٣٧ = د
في جزءين يحتويان على ٢٦١ ورقة. و "تفسير الفاتحة" مخطوط في المكتبة العامة ٢٠٧٤ - د. ويزاد في هاشمه: ونشر المثاني ١: ١٥٠ وسلوة الأنفاس ٢: ٣٥٢ ومعجم المحدثين والمفسرين ٢٣ وشجرة النور ٢٩٩ ومرآة المحاسن ١٥٨. البوني [؟ -؟]

<span data-type="title" id=toc-1955>عبد الرحيم بن علي بن إسحاق ابن مروان القرشي، محي الدين، البوني:</span>
فقيه، مشارك في بعض العلوم، من أهل بونة. قال صاحب كشف الظنون: "له "منافع القرآن"','2','781',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1421,'أوله: الحمد لله الذي أجرى على ألسنتنا الضعيفة كتابه العظيم .. الخ. أبدع لكل أمرما هو مخصوص به من الآيات وما أخذه عن أرباب الروايات ... وفيه مختصر مروي عن الإمام جعفر الصادقا". ولم أعثر على تاريخ وفاته.

<span data-type="title" id=toc-1956>عبد السلام بن عبد الرحمن، ابن برجان</span>
[(٥٣٦ هـ)] تقدم في ١: ٨٢. يزاد في نهاية ترجمته: "ويوجد مجلد ضخم منه، أوله سورة الأعراف في مكتبة السيد عبد الحي الكتاني الملحقة بالمكتبة الوطنية بالرباط". ويزاد في نهاية هاشمه: ومعجم المحدثين والمفسرين والقراء ١٠ وهو فيه: محمد بن عبد الرحمن. وشجرة النور ١٣٢ وهو فيه: أبو الحكم عبد الرحمن بن أبي الرجال.','2','781',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1422,'الفاسي [١٠٠٧ - ١٠٩١ هـ / ١٥٩٩ - ١٦٨٠ م]

<span data-type="title" id=toc-1957>عبد القادر بن علي بن يوسف بن محمد المغربي الفاسي، أبو محمد:</span>
محدث، مفسر، من فقهاء المالكية، كان من كبار الشيوخ في عصره، ولد ونشأ في "القصر" وانتقل إلى فاس سنة ١٠٢٥ هـ، وتوفي بها، قال في شجرة النور: "ومع غزارة علمه لم يتصدر لتأليف خاص وإنما كانت تصدر عنه أجوبة عن مسائل سئل عنها وجمعها بعض أصحابه" (١).

الصفوري [١٠١٠ - ١٠٨١ هـ / ١٦٠١ - ١٦٧٠ م]

<span data-type="title" id=toc-1958>عبد القادر بن مصطفى يوسف الصفوري الأصل، الدمشقي:</span>
فقيه شافعى أصولي، نحوي، مفسر،
_________
(١) شجرة النور ٣٨٤ وصفوة من انتشر ١٨١ ونشر المثاني ٢: ٥٨ وخلاصة الأثر ٢: ٤٤٤ ومعجم المطبوعات ١٤٣٠ ومعجم المحدثين والمفسرين ٢٤ والأعلام ٤: ٤١ والنبوغ المغربي ٢٨٣.','2','782',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1423,'محدث، أصله من صفورية - بفلسطين - ومولده ووفاته بدمشق، تعلم بها وبالقاهرة، ودخل بلاد الروم، ودرس في دار الحديث الأشرفية والجامع الأموي. قال المحبي: كان من أساطين أفاضل عصره، مشهور الذكر بعيد الصيت، فقيها، محدثا، مفسرا .. " (١).

ابن الأسلمي [... - بعد ٤٢٠ هـ / ... - ١٠٢٨ م]

<span data-type="title" id=toc-1959>عبد الله بن محمد بن عيسى بن وليد، أبو محمد، ابن الأسلمي ويقال أيضا ابن الأسلمية:</span>
نحوي، محدث، عارف بالتفسير، من فقهاء المالكية، من أهل مدينة الفرج المعروفة بوادي الحجارة. قال ابن الأبار: كان صاحب رواية وعناية، أحد الأئمة المنقلين في العلوم، المتقدمين في
_________
(١) خلاصة الأثر ٢: ٤٦٧ وبلادنا فلسطين ٦/ ٢: ١٠٧ وهدية العارفين ١: ٦٠٢ وفهرس الفهارس ٢: ١٥٥ ومعجم المؤلفين ٥: ٣٠٧ ومعجم أعلام فلسطين (مخطوط - للمؤلف).','2','782',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1424,'معرفة لسان العرب والإحاطة به، المشار إليه بالكمال .. " من آثاره كتاب في "تفسير قوله تعالى: {يا أيها الذين آمنوا شهادة بينكم ..} الآية، إلى آخر الآي الثلاث [المائدة ١٠٦ - ١٠٨] (١).

الشرفي [القرن (١١) الهجري / القرن (١٧) الميلادي]

<span data-type="title" id=toc-1960>عبد الله بن أحمد بن إبراهيم بن علي بن محمد، الحسني الشرفي:</span>
مفسر، من علماء اليمن. له "المصابيح الساطعة الأنوار المجموعة من تفسير الأئمة الأطهار" في ست مجلدات، قال ابن زبارة: ابتدأ فيه بآخر القرآن تبعا لما فعله الإمام القاسم بن علي العياني، وهو يدل على تمكنه في العلوم واطلاعه على أقوال الأئمة .. " (٢).
_________
(١) التكملة ٢: ٧٩٤ الترجمة ١٩٤٤ وشجرة النور ١١٣ والأعلام ٤: ٢٦٥.
(٢) ملحق البدر الطالع ١٢٦ وبروكلمان، الملحق ٢: ٥٦١ ومعجم المؤلفين ٦: ٢٠.','2','783',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1425,'العيدروس [٩٤٥ - ١٠١٩ هـ / ١٥٣٨ - ١٦١٠ م]

<span data-type="title" id=toc-1961>عبد الله بن شيخ بن عبد الله بن شيخ العيدروس، أبو محمد:</span>
من كبار علماء حضرموت في وقته، ولد في تريم، وتعلم بها وبمكة والمدينة، ورحل إلى أحمد آباد. وعاد إلى بلده فتصدر لتدريس. قال المحبي: "كان عالما متضلعا تفسيرا وحديثا وأصولا، وأخذ عنه خلق لا يحصون، وقصده الناس من أقصى البلاد، وصار شيخ البلاد الحضرمية (١).

طورسون زاده [... - ١٠١٩ هـ / ... - ١٦١٠ م]

<span data-type="title" id=toc-1962>عبد الله بن طورسون بن مراد الرومي، الموصوف بفيض الله، والشهير بطورسون زاده:</span>
قاض، من فقهاء الحنفية، من موالي الروم، اشتغل بالتدريس، ثم ولي قضاء القدس، فبغداد، فقضاء أيوب.
_________
(١) خلاصة الأثر: ١: ٤٩','2','783',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1426,'فإسكدار، وتوفي وهو قاض عليها. له تصانيف منها "تعليقات" على تفسير القرآن. (١).

ابن المهلا [٩٥٠ - ١٠٢٨ هـ / ١٥٤٣ - ١٦١٩ م]

<span data-type="title" id=toc-1963>عبد الله بن عبد الله بن المهلا بن سعيد بن علي النسائي، ثم الشرفي الأنصاري الخزرجي:</span>
مفسر، عالم بالعربية، من كبار علماء اليمن في وقته، ولد في الدعلية من الشرف الأعلى، وتعلم بها، ثم رحل في طلب الحديث والفقه فأخذ عن علماء صنعاء وغيرها. مات بالشجعة. قال المحبي: كان شيخ شيوخ زمانه، وإمام الاجتهاد في أوانه، نظيرا للسعد التفتازاني في علوم العربية والتفسير .. " (٢).
_________
(١) خلاصة الأثر ٣: ٥١ وهدية العارفين ١: ١٧٤ ومعجم المؤلفين ٦: ٦٥.
(٢) خلاصة الأثر ٣: ٥٧ والبدر الطالع ١: ٤٠٠ وملحق البدر الطالع: ١٣٢.','2','784',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1427,'الحضرمي [٩٥٨ - ١٠١٨ - هـ / ١٥٥١ - ١٦٠٩ م]

<span data-type="title" id=toc-1964>عبد الله بن عمر بن عبد الله بن أحمد باجمال الحضرمي:</span>
صوفي معروف من أهل حضرموت. درس التفسير والحديث والفقه. (١).

باعلوي [... - ١٠٣٩ هـ / ... - ١٦٢٩ م]

<span data-type="title" id=toc-1965>عبد الله بن محمد بن أحمد بن حسن باعلوي:</span>
عالم يماني، من أهل تريم مولدا ووفاة: أخذ عن علماء اليمن والحجاز، وجاور بالحرمين سنين. وعاد إلى بلده وتصدر للتدريس لا سيما التفسير والحديث، قال المحبي: وأخذ عنه خلق كثير" (٢).

ابن فرحون [٦٩٣ - ٧٦٩ هـ / ١٢٩٤ - ١٣٦٨ م]

<span data-type="title" id=toc-1966>عبد الله بن محمد بن أبي القاسم (ابن فرحون اليعمري، أبو محمد)</span>
_________
(١) خلاصة الأثر: ٣: ٦٣.
(٢) خلاصة الأثر: ٣: ٦٩.','2','784',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1428,'ابن فرحون اليعمري، أبو محمد: عالم بالحديث، مفسر، من فقهاء المالكية، أصله من تونس، ولد ونشأ وتعلم بالمدينة المنورة، ودرس بالحرم النبوي. قال في الديباج: "انفرد آخر عمره بعلو الإسناد، لم يكن بالمدينة أعلى سندا منه، وانتهت إليه الرئاسة هناك مع جاه لم يشاركه فيه أحد" له "أسئلة وأجوبة عن آيات من القرآن" (١).
المختاري [؟ =؟]

<span data-type="title" id=toc-1967>عبد الله بن محمد الفلاني المختاري:</span>
مفسر مغربي، ذكره الأستاذ الباحث عبد العزيز بن عبد الله وقال: "من آثاره "ضياء التأويل في معاني التنزيل" مخطوط، في المكتبة الملكية بالرباط ٢٦٦٥، وفي المكتبة العامة ٢٥٤١ - د (النصف الأول في
_________
(١) شجرة النور ٢٠٣ والديباج ١٤٤ والدرر الكامنة ٢: ٤٠٦ وهو فيه: الأندلسي الأصل، نزيل المدينة. وهدية العارفين ١: ٤٦٧.','2','785',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1429,'٤٢٨ ص) (١).

الحميدي [٩٣٠ - ١٠٠٣ هـ / ١٥٢٤ - ١٥٩٤ م]

<span data-type="title" id=toc-1968>عبد الواحد بن أحمد الحميدي، أبو محمد:</span>
فقيه مالكي، مفسر، له معرفة بالأدب، مولده ووفاته بفاس، ولي قضاءها سنة ٩٧٠ هـ، إلى أن توفي، فكان أعدل قضاة المغرب في زمانه، ومن أطولهم مدة في القضاء. درس التفسير في جامع القرويين، وكان يحضر مجلسه خواص الطلبة والفقهاء، وأخذ عنه كثيرون (٢).

ابن عاشر [... - ١٠٤٠ هـ / ... - ١٦٣٠ م]

<span data-type="title" id=toc-1969>عبد الواحد بن أحمد بن علي ابن عاشر الأنصاري الفاسي، أبو (مالك)</span>
_________
(١) معجم المحدثين والمفسرين ٢٥.
(٢) سلوة الأنفاس ٢: ٦ ومحمد المنوني في مجلة البحث العلمي ٧: ٢٥٢ ونشر المثاني ١: ٢٧ والأعلام ٤: ١٧٥ وشجرة النور: ٢٩٤.','2','785',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1430,'مالك: عالم بالقراءات، مشارك في الأصلين والتفسير والحديث والتصوف والنحو والعروض والبيان والمنطق والطب والهيئة والحساب. من أهل فاس مولدا ووفاة. له تصانيف في علم القراءات. (١). بنعبد الله [؟ -؟]

<span data-type="title" id=toc-1970>عبد الواحد بن علي بنعبد الله:</span>
عالم مغربي، من فقهاء المالكية، وهو والد البحاثة المؤرخ عبد العزيز بن عبد الله. من آثاره "القول الحميد في تعظيم القرآن المجيد" طبع بالرباط في ٣٩ صفحة. (٢).

المستغانمي [القرن ١٣ هـ / القرن ١٧ م]

<span data-type="title" id=toc-1971>عثمان بن سعيد المالقي (المستغانمي، أبو سعيد)</span>
_________
(١) خلاصة الأثر ٣: ٩٦ وسلوة الأنفاس ٢: ٢٧٥ واليواقيت الثمينة ١: ٢٣٠ وهدية العارفين ١: ٦٣٦ وصفوة من انتشر ٥٩ والأعلام ٤: ٣٢٣ والنبوغ المغربي ٢٤٨ ومعجم المؤلفين ٧: ٢٠٥.
(٢) معجم المحدثين والمفسرين: ٢٥.','2','786',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1431,'المستغانمي، أبو سعيد: مفسر، نحوي، من فقهاء المالكية. من أهل مستغانم (في الجمهورية الجزائرية). له، تفسير القرآن الكريم" كبير وصغير. (١).

نور الدين الحلبي [٩٧٥ - ١٠٤٤ هـ / ١٥٦٧ - ١٦٣٥ م]

<span data-type="title" id=toc-1972>علي بن إبراهيم بن أحمد بن علي بن عمر الحلبي، أبو الفرج، نور الدين ابن برهان الدين:</span>
مؤرخ، أديب، مشارك في بعض العلوم، من فقهاء الشافعية. أصله من حلب، ومولده ووفاته بمصر. وهو صاحب "السيرة الحلبية". من كتبه "تعليقة" على أوائل تفسير البيضاوي، وشرح على شرح البسملة لشيخ الإسلام زكريا الأنصاري سماه "خير الكلام على البسملة والحمدلة لشيخ الإسلام" (٢).
_________
(١) معجم أعلام الجزائر: ٢٩٧.
(٢) خلاصة الأثر ٣: ١٢٢ وفهرس الفهارس ١: ٢٥٥ وهدية العارفين ١: ٧٥٥ والأعلام ٥: ٥٤ ومعجم المؤلفين ٧: ٣.','2','786',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1432,'ابن حرزهم [... - ٥٩٩ هـ / ... - ١٢٠٣ م]

<span data-type="title" id=toc-1973>علي بن إسماعيل بن محمد بن عبد الله بن حرزهم، أبو الحسن:</span>
محدث، حافظ، مفسر، من أهل فاس، ومن كبار فقهائها، ومدرسيها العباد الزقاد. أخذ عن أبي بكر العربي المعافري وغيره. ودرس بفاس ومراكش. وهو شيخ أبي مدين الغوث. أخباره كثيرة (١).

ابن الزقاق [... - ٦٠٥ هـ / ... - ١٢٠٨ م]

<span data-type="title" id=toc-1974>علي بن القاسم بن يونش (بالشين المعجمة) الإشبيلي، أبو الحسن ابن الزقاق:</span>
عالم بالعربية، من أهل إشبيلية، رحل إلى المشرق، ونزل الجزيرة الفراقية، وخطب برأس
_________
(١) معجم المحدثين والمفسرين ١١ وسلوة الأنفاس ٣: ٧١ وجذوة الاقتاس ٢٩٣ وروض القرطاس ١٩١ ونيل الابتهاج ١٩٨ والتشوف إلى رجال التصوف ١٤٧ وأنس الفقير ١٢ والنبوغ المغربي ٩٠.','2','787',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1433,'عين الخابور مدة، وسكن دمشق ثم حلب. وحج، فمات عائدا بطريق مكة. له "شرح مفردات القرآن" (١).

الغرناطي [... - ٥٧٧ هـ / ... - ١١٨٢ م]

<span data-type="title" id=toc-1975>علي بن محمد الغرناطي:</span>
مفسر، من فقهاء المالكية، من أهل غرناطة. وتوفي بمراكش. قال عبد العزيز بنعبد الله: "صحب أبا بكر بن العربي المعافري فرآه مقتصرا على علم التفسير فقال: "إن هذا سيكون له شأن". وقد فسر القرآن للناس من أوله إلى آخره" (٢).

<span data-type="title" id=toc-1976>علي بن محمد، الحصار</span>
[(٦١١ هـ) تقدم في ١: ٣٧٧] يزاد في نهاية ترجمته بعد والمنسوخ: وهو ثلاثة أوضاع،
_________
(١) إنباه الرواة ٢: ٣٠٤ وبغية الوعاة ٢: ١٨٤ وهو فيه ابن الدقاق. وتاج العروس ٤: ٩٦٣ ومعجم المحدثين والمفسرين ٤٤ والأعلام ٥: ١٣٧.
(٢) معجم المحدثين والمفسرين: ٢٧.','2','787',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1434,'الأكبر والأوسط والأصغر. وله أيضا "مقالة في إعجاز القرآن". ويزاد في نهاية هامشه: ومعجم المحدثين والمفسرين ٣٧٧ والنبوغ المغربي ١٥٨.

العيدروس [٩٨٤ - ١٠٤١ هـ / ١٥٧٦ - ١٦٣١ م]

<span data-type="title" id=toc-1977>علي بن عبد الله بن شيخ بن عبد الله العيدروس، المشهور بزين العابدين، وتاج العابدين:</span>
كبير علماء حضرموت في وقته، من أهل تريم مولدا ووفاة. قال المحبي: "كان في استحضار مباحث التفسير والحديث والتصوف آية لا تدرك، جلس للتدريس فدرس التفسير وحضره من أشياخه جمع كثير ... " (١).

الأجهوري [٩٦٧ - ١٠٦٦ هـ / ١٥٦٠ - ١٦٥٦ م]

<span data-type="title" id=toc-1978>علي بن محمد بن عبد الرحمن (ابن علي، أبو الإرشاد، نور الدين الأجهوري)</span>
_________
(١) خلاصة الأثر ٣: ١٦٦.','2','788',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1435,'ابن علي، أبو الإرشاد، نور الدين الأجهوري: عالم بالحديث والتفسير، من فقهاء المالكية. نسبته إلى "أجهور الورد" من قرى مصر. قال المحبي: "كان محدثا فقيها رحلة كبير الشأن، أملى الكثير من الحديث والتفسير والفقه" توفي بالقاهرة. له كتب ورسائل كثيرة منها رسالة في تفسير آية الصوم سماها "فضائل رمضان" (١).

ابن هارون [... - ٩٥١ هـ / ... - ١٥٤٥ م]

<span data-type="title" id=toc-1979>علي بن موسى بن علي بن هارون، أبو الحسن المضغري (ويقال المطغري)، المعروف بابن هارون:</span>
فقيه مالكي، مفسر، مشارك في عدة علوم، من أهل مضغرة (مطغرة) تلمسان. انتقل إلى فاس سنة ٨٩١ هـ، ولازم العلامة ابن غازي
_________
(١) خلاصة الأثر ٣: ١٥٧ وخطط مبارك ٨: ٣٣ وفهرس الفهارس ٢: ١٧١ وهدية العارفين ١: ٧٥٨ والأعلام ٥: ١٦٨ ومعجم المؤلفين ٧: ٢٠٧ وصفوة من انتشر ١٢٦ وهو فيه: علي بن أحمد بن عبد الرحمن.','2','788',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1436,'نحوا من تسع وعشرين سنة. وتوفي بها. من آثاره "تفسير القرآن" ذكره القادري (١).

عمر راسم [١٣٠٠ - ١٣٧٩ هـ / ١٨٨٣ - ١٩٥٩ م]

<span data-type="title" id=toc-1980>عمر راسم بن علي بن سعيد بن محمد البجائي:</span>
صحفي، خطاط كبير اشتهر بخطه العربي الجميل ومقدرته في رسم المنمنمات، من الرعيل الأول في الإصلاح والكفاح. ولد بمدينة الجزائر وتعلم بكتاتيبها، ثم اعتمد على نفسه فتعلم العربية والفرنسية. عرف منذ صباه بأفكاره الإصلاحية، وكان من أوائل
_________
(١) نشر المثاني ٦٠ وشجرة النور ٢٧٨ وهو فيه: المضغري من مضغرة سجلماسة. ونيل الابتهاج ٢١٢ ومعجم أعلام الجزائر (ط ٣) ٣٠٥.','2','789',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1437,'الجزائريين المعتنقين لمذهب الأستاذ الإمام محمد عبده الإصلاحي، والداعين إليه. أنشأ جريدة "الجزائر" في ١٧ أكتوبر ١٩٠٨، ثم جريدة "ذو الفقار" في ٢٥ أكتوبر سنة ١٩١٣. وكان اسمه المستعار "أبو المنصور الصنهاجي". سجنه الفرنسيون في الحرب العالمية الأولى فلاقى المحن الشديدة في سجنه. قال الأستاذ أحمد توفيق المدني: "وهو ممن نكبوا على يد الاستعمار القاسي نكبة سوداء أثرت على البقية الباقية من حياته .. ". من آثاره "تفسير القرآن الكريم" كتبه في سجنه. مات بمدينة الجزائر. (١).
_________
(١) تاريخ الصحافة العربية ٤: ٢٦٠ و ٢٦٢ والصحافة العربية نشأتها وتطورها ٢٢٣ ومجلة كلية الآداب الجزائرية عدد ١ (١٩٦٤) و "تقويم الأخلاق" (يناير ١٩٢٧) ٤٨ وحياة كفاح ٥٢. ومعجم أعلام الجزائر ٢٤٣.','2','789',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1438,'<span data-type="title" id=toc-1981>(ف)</span>
البركلي [... - ١٠٣٠ هـ / ... - ١٦٢٠ م]

<span data-type="title" id=toc-1982>فضل الله بن محمد الرومي البركلي:</span>
ف<span data-type="title" id=toc-1983>ق</span>يه خفي، واعظ، مفسر، قدم القسطنطينية في حدود سنة ١٠٢٠ هـ. فولي تدريس التفسير في جامع السلطان سليم، قال المحبي: "وكان للناس عليه إقبال تام". (١).

(ق)
السلوي [القرن (٩) الهجري / القرن (١٥) الميلادي]

<span data-type="title" id=toc-1984>أبو القاسم الشريف الإدريسي السلوي:</span>
فقيه مالكي، عارف بالحديث والتفسير. من أكابر تلامذة ابن عرفة (محمد بن محمد - انظر ترجمته). له "تقييد" في التفسير عن ابن عرفة، في مجلدين. (٢).
_________
(١) خلاصة الاثر ٣: ٢٨٦.
(٢) نيل الابتهاج ٢٢٥ وشجرة النور ٢٥٠ ومعجم المحدثين والمفسرين ١٨ ومجلة البحث العلمي عدد ٧: ٢٦١ والنبوغ المغربي ٢١٦.','2','790',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1439,'الدهلوي [... - نحو ٧٢٥ هـ / ... - نحو ١٣٢٥ م]

<span data-type="title" id=toc-1985>القاسم بن عمر الدهلوي:</span>
مفسر، من علماء الهند، ولد ونشأ وتعلم بدهلي، من آثاره "لطائف التفسير". (١).

البرشكي [... - ٩٩٠ هـ / ... - ١٥٨٢ م]

<span data-type="title" id=toc-1986>قاسم بن أبي القاسم البرشكي، أبو الفضل:</span>
فقيه تونس وعالمها وخطيبها، مفسر، واعظ، ولي الإمامة
_________
(١) نزهة الخواطر: ١١٣.','2','790',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1440,'والخطابة في جامع الزيتونة سنة ٩٧٢ هـ، واستمر إلى أن مات. (١).

الغساني [٩٥٢ - ١٠٣٢ م / ١٥٤٤ - ١٦٢٢ م]

<span data-type="title" id=toc-1987>أبو القاسم بن محمد بن أبي القاسم بن أبي نعيم الغساني الفاسي:</span>
مفسر، بياني، خطيب من فقهاء المالكية، من أهل فاس، ووله قضاء الجماعة بها. قال مخلوف: "كان من كبار الشيوخ الذين لهم الشهرة والصيت، متضلعا في الفنون ماهرا
_________
(١) شجرة النور ٢٨٢.','2','791',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1441,'في المعقول والبيان والتفسير، وكان خطيبا بليغا، توفي مقتولا، (١).

ابن مخلص [؟ -؟ هـ /؟ -؟ م]

<span data-type="title" id=toc-1988>قاسم بن محمد بن أحمد بن عبد الملك بن مخلص:</span>
فاضل مغربي. ذكره الأستاذ عبد العزيز بن عبد الله ولم يؤرخ له وقال إن له "أدلة التوحيد والنبوة والبعث من آيات القرآن المكسبة للقلوب مزيد الإيمان والإيقان" (٢)
_________
(١) شجرة النور: ٢٩٨.
(٢) معجم المحدثين والمفسرين: ٢٩.','2','791',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1442,'<span data-type="title" id=toc-1989>(ك)</span>
كاظم ميرزا [(قبل ١٢٧٥ هـ) تقدم في ١: ٤٤٣] تصحح ترجمته كما يلي:

كاظم ميرزا [١٢١٣ - ١٢٨٧ هـ / ١٨٠٢ - ١٨٧٠ م]

<span data-type="title" id=toc-1990>كاظم ميرزا بك:</span>
مستشرق، أذربيجاني الأصل، تصفه بعض المصادر بالعجمي المتنصر، أقام مدة في مدينة "قازان" في روسيا ودرس اللغتين العربية والفارسية في جامعتها، ثم انتقل إلى','2','792',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1443,'بطرسبورغ (لينينغراد) فكان أول عميد للكلية الشرقية للغات فيها. كان من كبار الدارسين في الإسلاميات، متقنا للغات الأوروبية الرئيسية، أكسبته دراساته وتحقيقاته عن الحضارة العربية - الإسلامية شهرة واسعة في الغرب. من آثاره "مفتاح كنوز القرآن في الكشف عن كلمات القرآن" طبع في بطرسبورغ سنة ١٨٥٩ م (١).
_________
(١) المستشرقون ٣: ٩٣، ومعجم المطبوعات ١٨٢٦ ومجلة الفكر العربي ٣١: ٢٤٧.','2','792',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1444,'<span data-type="title" id=toc-1991>(م)</span>
التلمساني [... - ٨٤٥ هـ / ... - ١٤٤١ م]

<span data-type="title" id=toc-1992>محمد بن إبراهيم بن عبد الرحمن بن محمد بن عبد الله، ابن الإمام، أبو الفضل التلمساني:</span>
عالم بالتفسير والفقه، مشارك في علوم الأدب والطب والتصوف. من أهل تلمسان. رحله إلى المشرق فدخل القاهرة، وبيت المقدس، وحج. وهو أول من أدخل للمغرب "شامل بهرام" وغيره من الكتب. له "أبحاث" في التفسير، تكلم فيها مع المقري الجد، قال صاحب نيل الابتهاج: "مفيدة، كتبتها مع ما كتبت من فوائده','2','793',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1445,'التفسيرية". (١).

اين خويز [؟ -؟ /؟ -؟]

<span data-type="title" id=toc-1993>محمد بن أحمد بن عبد الله بن خويز، ويقال خواز، منداد، أبو بكر:</span>
من كبار المالكيين العراقيين، وتلميذ أبي بكر الأبهري. له تصانيف منها "أحكام القرآن" (٢).
_________
(١) معجم أعلام الجزائر. وشجرة النور ١: ٢٥٤ ودرة الحجال ٢: ٢٨٩ والبستان ٢٢٠ ونيل الابتهاج ٣٠٥ وتعريف الخلف ٢: ٣٣٠ والضوء اللامع: ١٠: ٧٤٠ وهو فيه محمد بن يحيى بن إبراهيم.
(٢) شجرة النور ١٠٣ والعراقي ٢: ٥٢ =','2','793',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1446,'ابن غازي [٨٤١ - ٩١٩ هـ / ١٤٣٧ - ١٥١٣ م]

<span data-type="title" id=toc-1994>محمد بن أحمد بن محمد بن محمد بن علي بن غازي العثماني المكناسي، أبو عبد الله:</span>
مؤرخ، عالم بالتفسير والحديث والقراءات والحساب والأدب، من فقهاء المالكية. نسبه في بني عثمان، قبيلة من كتامة بمكناسة الزيتون. ولد ونشأ وتعلم بها، وانتقل إلى فاس في طلب العلم فأقام بها مدة، ثم عاد إلى بلده وتصدر للتدريس. وفي سنة ٨٩١ هـ اضطر إلى مغادرة مكناس، فتحول إلى فاس، وولي الخطبة والإمامة في جامع القرويين، وجلس للإقراء، فطار صيته في الآفاق، وقصده الناس من أنحاء المغرب العربي. قال تلميذه عبد الواحد الونشريسي "حضرت مجالس إقرائه في الفقه والعربية
_________
= والمالكية بالمشرق ٨٦ ولسان الميزان ٥: ٢٩١ والديباج ٢٦٨ وطبقات الفقهاء ١٤٢ ومعجم المؤلفين ٨: ٢٨٢ وترتيب المدارك ١: ٥١.','2','794',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1447,'والتفسير والحديث وغيرها، وكلها في غاية الاحتفال ... " (١).

اليسيثني [٨٩٧ - ٩٥٩ هـ / ١٤٩٢ - ١٥٥٢ م]

<span data-type="title" id=toc-1995>محمد بن أحمد بن عبد الرحمن اليسيثني الفاسي، أبو عبد الله:</span>
فقيه مالكي، متكلم، مفسر، نحوي، من أهل فاس، أخذ بها عن مشاهير أعلامها، ورحل إلى المشرق سنة ٩٣٠ هـ، فأخذ عن علماء تلمسان وقسنطينة وتونس ومصر ومكة. وعاد إلى فاس، فتولى بها الفتوى، ودرس التفسير والحديث والفقه والأصول والنحو والبيان. وتوفي بها. (٢).
_________
(١) سلوة الأنفاس ٢: ٧٣ والنبوغ المغربي ٢٠٨ وذكريات مشاهير رجال المغرب - ابن غازي. ومحمد المنوني في مجلة البحث العلمي ٧: ٢٦٣ وفهرس الفهارس ١: ٢١٠ وإتحاف أعلام الناس ٤: ٢ ونيل الابتهاج ٣٣٣ وشجرة النور ٢٧٦ ومجلة المجمع العلمي العربي ٢٨: ٤٣٩ وهدية العارفين ٢: ٢٢٦ ودليل مؤرخ المغرب ٥٢ والأعلام ٥: ٣٣٦.
(٢) النبوغ المغربي ٢٥٠ وشجرة النور ٢٨٣ ونيل الابتهاج ٣٣٨ والفكر السامي =','2','794',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1448,'ابن الوقاد [... - ١٠٠١ هـ / ... - ١٥٩٣ م]

<span data-type="title" id=toc-1996>محمد بن أحمد بن محمد، المعروف بابن الوقاد:</span>
قاض، عالم بالتفسير والحديث والفقه والأدب، من أهل تلمسان، هاجر منها على أثر الاحتلال التركي لها إلى المغرب الأقصى ونزل مدينة ترودانت. وقد لقي في بداية الأمر بعض الصعوبات لاستحكام العجمة في ألسنة السوسيين، فذهب إلى سجلماسة فمكناس ففاس. ثم عاد إلى ترودانت، فولي التدريس والفتوى والإمامة بجامعها الكبير. وهو أول من قرأ الجامع الصحيح للبخاري بها قراءة ضبط وإتقان، وخطب فيها ببراعة اللسان، وأول من أحيا بها ليلة المولد بإجتماع الناس في منزله وقراءة قصائد مدحه - صلى الله عليه وسلم - (١).
_________
= ٤: ١٠١ وهو فيه: محمد بن عبد الرحمن.
(١) معجم أعلام الجزائر ٣٤٣ وما فيه من مراجع.','2','795',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1449,'شمس الدين الرملي [٩١٩ - ١٠٠٤ هـ / ١٥١٢ - ١٥٩٦ م]

<span data-type="title" id=toc-1997>محمد بن أحمد بن حمزة، شمس الدين الرملي:</span>
فقيه الديار المصرية في عصره، ومرجعها في الفتوى، يقال له: الشافعي الصغير. نسبته إلى الرملة من قرى المنوفية بمصر. ومولده ووفاته بالقاهرة. ولي إفتاء الشافعية، ودرس التفسير والحديث والأصول والفروع والنحو والمعاني والبيان. (١).

الأسطواني [١٠١٦ - ١٠٧٢ هـ / ١٦٠٧ - ١٦٦١ م]

<span data-type="title" id=toc-1998>محمد بن أحمد بن محمد بن حسين، المعروف بالأسطواني:</span>
فقيه حنفي، إخباري، واعظ، من أهل دمشق، تعلم بها وبالقاهرة. ودخل عاصمة الخلافة وولي الإمامة بجامع السلطان أحمد، فالوعظ في جامع
_________
(١) خلاصة الأثر ٣: ٣٤٢ ومعجم المطبوعات ٩٥٢ والأعلام ٦: ٢٣٥.','2','795',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1450,'السلطان أبي الفتح محمد خان. وعاد إلى دمشق فتصدر للتدريس في الجامع الأموي. قال المحبي: "ظفرت له بتحريرات على عبارات في التفسير والفقه .. " (١).

المسناوي [١٠٧٢ - ١١٣٦ هـ / ١٦٦١ - ١٧٢٣ م]

<span data-type="title" id=toc-1999>محمد بن أحمد بن محمد المسناوي الدلائي، أبو عبد الله:</span>
عالم مغربي، من فقهاء المالكية، كان راسخ القدم في علوم العربية والفقه والحديث والتفسير والكلام. قال عبد الله كنون: "كان آية في الحفظ والإتقان، قد أعطي الملكة العجيبة في التدريس والعارضة القوية في الفتوى فأصبح الحجة الذي لا ينقض قوله، ولا يكون الرجوع إلا إليه ... ". (٢)
_________
(١) خلاصة الأثر ٣: ٣٨٨.
(٢) النبوغ المغربي ٢٨٦ وشجرة النور: ٣٣٣','2','796',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1451,'النيفر [١٢٢٢ - ١٢٧٧ هـ / ١٨٠٧ - ١٨٦٠ م]

<span data-type="title" id=toc-2000>محمد بن أحمد بن قاسم النيفر، أبو عبد الله:</span>
قاض، مفسر، من فقهاء المالكية. مولده بتونس ووفاته بالمدينة. درس التفسير بباب الشفاء أحد أبواب جامع الزيتونة. "فكان درسه جامعا لأعيان العلماء والمدرسين فأتى بالعجب العجاب .. ". وولي القضاء بتونس سنة ١٢٦٣ هـ وخطة الفتوى سنة ١٢٦٧ هـ، وحج، فتوفي بالمدينة. (١).

التاودي [١١١١ - ١٢٠٩ هـ / ١٧٠٠ - ١٧٩٤ م]

<span data-type="title" id=toc-2001>محمد التاودي بن الطالب (ويقال: محمد الطالب) بن محمد بن علي، ابن سودة المري الفاسي، أبو عبد الله:</span>
شيخ مشايخ المغرب، وفقيه
_________
(١) شجرة النور ٣٩٠ وهو فيه محمد بن محمد بن أحمد. وعنوان الأريب ٢: ١٠٨ ومجلة الهداية الإسلامية ٢: ١٠٧ والأعلام ٦: ٢٤٣.','2','796',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1452,'المالكية في عصره. من أهل فاس، أخذ عن مشيختها، وعنه الجم الغفير من أبناء المغرب العربي. حج سنة ١١٨١ هـ فدرس بالأزهر الشريف بالقاهرة وبالحرمين الشريفين في الحجاز. وعاد، فكانت له رياسة العلم بفاس والمغرب كله. قال عبد الله كنون: "كان مقدما في كل العلوم لا سيما التفسير والحديث والفقه والتصوف والكلام والمنطق والأصول" توفي بفاس. (١).

الدلائي [٩٦٧ - ١٠٤٦ هـ / ١٠٤٦ - ١٦٣٧ م]

<span data-type="title" id=toc-2002>محمد بن أبي بكر الدلائي، أبو عبد الله:</span>
عالم في التفسير والحديث والكلام، ومن أعظم شيوخ "الزاوية الدلائية في المغرب، أخذ عنه
_________
(١) النبوغ المغربي ٢٩٣ وشجرة النور ٣٧٢ وفهرس الفهارس ١: ١٨٥ والفكر السامي ٤: ١٢٧ وتاج العروس ٢: ٣٨٧ وسلوة الأنفاس ١: ١١٤ ودليل مؤرخ المغرب ٨١ و ٨٩ و ١٠١ ومعجم المطبوعات ١٦٤٣ والأعلام ٦: ٦٢.','2','797',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1453,'المقري صاحب "نفح الطيب" وأثنى عليه ". (١).

ابن عجلان [١٠٣٦ - ١٠٩٦ هـ / ١٦٢٦ - ١٦٨٥ م]

<span data-type="title" id=toc-2003>محمد بن حسن، الشهير بابن عجلان، الحسيني الدمشقي:</span>
نقيب الأشراف بدمشق. ولي نيابة القضاء وقضاء المواريث بها. قال المحبي: "وله في التفسير يد طائلة، وأقرأه في السليمية، وكان آخر درس أقرأه في تفسير سورة مريم، قوله تعالى: {ويقول الإنسان أإذا ما مت لسوف أخرج حيا}. (٢).

قاضى القضاة [... - ١٠٨٧ هـ / ... - ١٦٧٧ م]

<span data-type="title" id=toc-2004>محمد ... ... المعروف بقاضي القضاة:</span>
مفسر، ولي قضاء
_________
(١) شجرة النور ٣٠١ والزاوية الدلائية ٧٦ ونفح الطيب ٢: ٤٧٩ والاستقصا ٦: ٩٦.
(٢) خلاصة الأثر ٣: ٤٣٦ وقد وردت الآية الكريمة فيه هكذا: {وإذا مت فسوف أبعث حيا} وهو خطأ واضح،','2','797',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1454,'القدس، والمدينة، فقضاء الشام سنة ١٠٨٥ هـ، فقضاء أدرنة، ثم قضاء القسطنطينية. قال المحبي: "ولما دخلت أدرنة كان قاضيا بها فاجتمعت به مرات، وكان له مباحثة جيدة في التفسير ناقشني في عبارات سطرت منها أشياء" توفي بالقسطنطينية. (١).

ابن الطيب [١١١٠ - ١١٧٠ هـ / ١٦٩٨ - ١٧٥٧ م]

<span data-type="title" id=toc-2005>محمد بن الطيب بن محمد بن محمد بن موسى، أبو عبد الله، شمس الدين الشرقي الفاسي:</span>
إمام أهل اللغة في عصره، محدث، ولد بفاس وأخذ عن جلة علمائها. ورحل إلى المشرق فحج ودرس بالحرم النبوي، ودخل إلى بلاد الروم والشام ومصر فأخذ عنه خلق كثير. وعاد إلى المدينة المنورة وأقام بها إلى أن توفي. وهو شيخ الزبيدي صاحب "تاج العروس". والشرقي نسبة إلى "شراقة" على مرحلة من فاس. من آثاره الكثيرة "شرح شواهد
_________
(١) خلاصة الأثر ٤: ٣١٣.','2','798',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1455,'الكشاف" (١).

القادري [١١٢٤ - ١١٨٧ هـ / ١٧١٢ - ١٧٧٣ م]

<span data-type="title" id=toc-2006>محمد بن الطيب بن عبد السلام الحسني القادري:</span>
مؤرخ، من أهل فاس، اشتهر بكتابيه "نشر المثاني" و "التقاط الدرر". وللأستاذ هاشم العلوي القاسمي كتاب في سيرته ودراسة عن كتابه التقاط الدرر. من آثاره "الفتح والتيسير في آيات التطهير" مخطوط، في المكتبة الملكية ٤٤٧٦/ ٤٥٨٤. (٢).
_________
(١) معجم المحدثين والمفسرين ٣٢ والنبوغ المغربي ٢٩١ وتاج العروس ١: ٣ والرسالة المستطرفة ٦٣ ومجلة المجمع العلمي العربي ١٢: ٥٥، و ٣٠: ٨٧ وفهرس الفهارس ٢: ٣٩٦ وهدية العارفين ٢: ٣٣١ والأعلام ٧: ٤٧ ومعجم المؤلفين ١٠: ١١١ وسلك الدرر ٤: ٩١
(٢) سلوة الأنفاس ٢: ٣٥١ ومقدمة تحقيق كتاب التقاط الدرر بقلم هاشم العلوي وما فيه من مصادر. ومعجم المحدثين والمفسرين: ٣٢.','2','798',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1456,'الكتاني [١٣٠٥ - ١٨٨٨ هـ / ١٣٨٢ - ١٩٦٢ م]

<span data-type="title" id=toc-2007>محمد عبد الحي بن عبد الكبير ابن محمد الحسني الإدريسي، المعروف بعبد الحي الكتاني:</span>
عالم بالحديث ورجاله، ولد وتعلم بفاس. كان مواليا للفرنسيين، وجاهر بالبيعة لابن عرفة - صنيعتهم - بعد إبعاد السلطان محمد الخامس إلى مدغشقر سنة ١٩٥٣. ولما استقل المغرب (١٩٥٥) وعاد السلطان إلى بلاده، كان عبد الحي في باريس، فاستمر إلى أن مات بها، له تفسير قوله تعالى: {وإذا قرىء القرآن فاستمعوا له وانصتوا} مخطوط، في المكتبة العامة بالرباط (١٩٢٩ - د). (١).

التلمساني [... - ٩١١ هـ / ... - ١٥٠٥ م]

<span data-type="title" id=toc-2008>محمد بن عبد الرحمن بن أبي (العيش الخزرجي التلمساني، أبو عبد الله)</span>
_________
(١) شجرة النور ٤٣٧ وفهرس الفهارس (ط ١) مقدمته، والجزء الثاني منه ص ٢٠ و (ط ٢) مقدمة الدكتور إحسان عباس والأعلام ٧: ١٨٧.','2','799',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1457,'العيش الخزرجي التلمساني، أبو عبد الله: مفسر، أصولي، أديب، شاعر، من فقهاء المالكية، أصله من أشبيلية بالأندلس، ولد ونشأ وتعلم بتلمسان، ثم أفتى ودرس، وفتاويه معروفة نقل بعضها الونشريسي في كتابه "المعيار". من كتبه "تفسير القرآن" (١).

الرجراجي [القرن (١١) الهجري / القرن (١٧) الميلادي]

<span data-type="title" id=toc-2009>محمد بن عبد الرحمن (وقيل: بن أبي عبد الله) الرجراجي:</span>
فقيه مالكي مغربي، مفسر. ندبه السلطان المنصور السعدي (٩٥٦ - ١٠١٢ هـ) إلى الجمع بين تفسيري البسيلي والسلوي مع شرح ما لم يتكلم عليه المفسران من تفسير ابن عرفة، ففعل، وقد كان هذا التفسير الذي جمعه
_________
(١) معجم أعلام الجزائر (ط ٣) والبستان ٢٥٤ وتعريف الخلف ٢: ٢٣٣ ونيل الابتهاج ٣٣٢ وشجرة النور ٢٧٤.','2','799',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1458,'الرجراجي من محتويات خزانة المنصور (١).

المراكشي [؟ -؟ /؟ -؟]

<span data-type="title" id=toc-2010>محمد بن عبد الرحمن المراكشي:</span>
فاضل مغربي، له "حذقات القرآن " مخطوط، في المكتبة الملكية بالرباط ١٠٥٢. ولم أعثر له على ترجمة وافية. (٢).

ابن زكري [... - ١١٤٤ هـ / ... - ١٧٣١ م]

<span data-type="title" id=toc-2011>محمد بن عبد الرحمن بن زكري، أبو عبد الله:</span>
عالم بالعربية والتفسير والحديث والتصوف والأدب، من فقهاء المالكية، من أهل فاس. له تآليف تشهد بطول باعه وكثرة اطلاعه. قال مخلوف: "ولكل
_________
(١) محمد المنوني في مجلة البحث العلمي ٧: ٢٦٣ والإعلام بمن حل بمراكش ٤: ٢٦٣.
(٢) معجم المحدثين والمفسرين ٣٣.','2','800',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1459,'من الشيخين عبد المجيد المنالي وأحمد بن عبد السلام بناني تأليف مستقل في التعريف به". من آثاره "تفسير سورة "الإخلاص" مخطوط، في المكتبة العامة بالرباط (رقم ٢٢١٦) " وبالمكتبة الملكية (مجموع ١٠٨ - ١١٦) ٤٧٥٠. و "شرح سورة الفاتحة" (المكتبة الملكية ٨٨٨) وتفسير سورة الكهف "مخطوط في نفس المكتبة رقم ٤٧٥١). و "أجوبة على استشكالات في تفسير الفاتحة" مخطوط، في المكتبة العامة رقم ٢٢١٦ - د (مجموع ٩٠ - ١٠٦). (١).

ابن مسرة [٢٦٩ - ٣١٩ هـ / ٨٨٣ - ٩٣١ م]

<span data-type="title" id=toc-2012>محمد بن عبد الله بن مسرة بن نجيح الجبلي، أبو عبد الله:</span>
متفلسف أندلسي، من أهل قرطبة. أخذ عن
_________
(١) معجم المحدثين والمفسرين ٣٢ والنبوغ المغربي ٢٨٨ وشجرة النور ٣٣٥ وسلوة الأنفاس ١: ١٥٥ والاستقصا ٤: ٢٨ ونشر المثاني ٢: ١٤٠ والأعلام ٧: ٦٩ ومعجم المؤلفين ١٠: ١٤٠.','2','800',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1460,'أبيه ومحمد بن وضاح الخشني. كان يجمع بين مبادىء المتصوفة وبين أصول الاعتزال. أثر تفكيره الفلسفي والصوفي تأثيرا كبيرا في الأندلس حيث كثر تلاميذه، مما أثار الشك في سلامة آرائه، واتهم بالزندقة، فخرج إلى المشرق (سنة ٣٠١ هـ) فدخل القيروان، والحجاز، وحج غير مرة، ثم عاد إلى بلاده. أثارت آراؤه بعض الخصومات الجدلية في المشرق والمغرب. قال ابن الفرضي: "وكان يحرف التأويل في كثير من القرآن " (١).

المظفر ابن الأفطس [... - ٤٦١ هـ / ... - ١٠٦٨ م]

<span data-type="title" id=toc-2013>محمد بن عبد الله بن محمد بن مسلمة التجيبي الأندلسي، الملك المظفر، أبو بكر ابن الأفطس:</span>
صاحب بطليوس  Badajoz  بالثغر
_________
(١) جذوة المقتبس ٦٣ الترجمة ٨٣ ومعجم المحدثين والمفسرين ٤١ وتاريخ علماء الأندلس ق ٢: ٣٩ ونفح الطيب ٣: ١٧٨ وتاريخ الأدب الأندلسي - وسيادة قرطبة - ٣١ والأعلام ٧: ٩٥.','2','801',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1461,'الشمالي من الأندلس. من ملوك الطوائف. ولي الملك بعد وفاة أبيه سنة ٤٣٧ هـ واستمر إلى أن توفي. كان من أعلم أهل عصره، شغوفا بالشعر والأدب، فارسا شجاعا. له "تفسير القرآن" (١).

<span data-type="title" id=toc-2014>محمد بن علي، ابن عسكر</span>
[(٦٣٦ هـ) تقدم في ٢: ٥٨١] يزاد في نهاية ترجمته بعد والحديث:: " و "التكملة والإتمام لكتاب التعريف والإعلام" مخطوط، توجد نسخة منه بدار
الكتب المصرية (٧٦٢ = تفسير).
ونسخة في خزانة عاشر أفندي، باستنبول، الرقم ٩٣.

ابن السكاك [... - ٨١٨ هـ / ... - ١٤١٥ م]

<span data-type="title" id=toc-2015>محمد بن أبي غالب بن أحمد ابن علي بن أحمد المكناسي (العياضي، المعروف بابن السكاك)</span>
_________
(١) البيان المغرب ٣: ٢٢٠ والوافي بالوفيات ٣: ٣٢٣ والتكملة ١٢٣ ودول الطوائف ٨٤ ومعجم المحدثين والمفسرين ٤٢ والأعلام ٧: ٢٢٨.','2','801',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1462,'العياضي، المعروف بابن السكاك: قاض، مفسر، أصولي، له اشتغال بالتاريخ. من أهل مكناس، ولي قضاء الجماعة بفاس وتوفي بها. (١).

الهاملى [١٢٣٩ - ١٣١٥ هـ / ١٨٢٣ - ١٨٩٧ م]

<span data-type="title" id=toc-2016>محمد بن أبي القاسم بن رجيح، أبو عبد الله، الشهير بالهاملي:</span>
فقيه مالكي، برع في التفسير والحديث والأصول والنحو، ولد في الحامدية قرب حاسي بحبح في شمال الصحراء (في الجزائر). وتعلم في زاوية علي الطيار، وزاوية ابن أبي داود في زواوة. أسس الزاوية المعروفة بزاوية الهامل سنة ١٢٨٠ هـ (١٨٦٣ م) وتوفي في بويرة الصحارى في طريق عودته من الجزائر العاصمة إلى زاويته (٢).
_________
(١) سلوة الأنفاس ٢: ١٤٤ - ١٤٦ وأخبار مكناس ٣: ٥٨٨ وشجرة النور ٢٥١.
(٢) معجم أعلام الجزائر ٣٣٥ وتعريف الخلف ٣٣٦ ونهضة الجزائر الحديثة ١: ٥٦ ومعجم المؤلفين ١١: ٢٦٠.','2','802',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1463,'المشذالي [نحو ٨٢٢ - ٨٦٥ هـ / نحو ١٤١٧ - ١٤٦١ م]

<span data-type="title" id=toc-2017>محمد بن محمد بن أبي القاسم المشدالي، أبو الفضل:</span>
مفسر، عالم بالحديث ورجاله، أصولي، فرضي، فقيه، من أشهر علماء المالكية في عصره. ولد في بجاية، وتعلم بها وبتلمسان. ثم تصدر للإقراء والتدريس. وفي سنة ٨٤٥ هـ (١٤٤١ م) دخل عنابة وقسنطينة وتونس، ثم توجه إلى المشرق عن طريق قبرص، فدخل بيروت ودمشق وطرابلس الشام وحماه، وسكن بيت المقدس مدة. وحج سنة ٨٤٩ هـ (١٤٤٥ م) وجاور، ثم دخل القاهرة، ودرس بها "فبهر العقول وأدهش الألباب" ولقي الإمام السخاوي الذي خصه بترجمة وافية في كتابه "الضوء اللامع". ومما قاله: "وقد حصلت بيننا إجتماعات وصحبة، ورأيت منه من حدة الذهن، وذكاء الخاطر، وصفاء الفكر، وسرعة الإدراك، وقوة الفهم، وسعة الحفظ، وتوقد','2','802',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1464,'القريحة، واعتدال المزاج، وسداد الرأي، واستقامة النظر، ووفور العقل، وطلاقة اللسان، وبلاغة القول، ورصانة الجواب، وغزارة العلم، وحلاوة الشكل، وخفة الروح، وعذوبة المنطق، ما لم أره من أحد". ثم غادر مصر "وتشتت في البلاد والقرى، وركب البحر والبر، وتطور على أنحاء مختلفة وهيئات متنوعة، إلى أن مات غريبا فريدا في "عين تاب " (بين حلب وأنطاكية) (١).

المجاصي [؟ -؟ هـ /؟ -؟ م]

<span data-type="title" id=toc-2018>محمد بن محمد المجاصي:</span>
له "غريب القرآن" (٦٩٥ بيتا) مخطوط، في مكتبة الجزائر ٤١٣ (٢).
_________
(١) الضوء اللامع ٩: ١٨٠ ونظم العقيان ١: ١٦٠ وفي الابتهاج ٣١٥: تاريخ الجرائر العام ٢: ٢٧١ وعنوان الزمان (مخطوط). وشجرة النور ٢٦٣. ومعجم أعلام الجزائر ٣٠١.
(٢) معجم المحدثين والمفسرين: ٨٦.','2','803',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1465,'الحطاب [٩٠٢ - ٩٥٤ هـ / ١٤٩٧ - ١٥٤٧ م]

<span data-type="title" id=toc-2019>محمد بن محمد بن عبد الرحمن ابن حسين الرعيني، أبو عبد الله، المعروف بالحطاب:</span>
مفسر، أصولي، صوفي، من فقهاء المالكية أصله من طرابلس الغرب. هاجر والده إلى مكة سنة ٨٧٧ هـ، واستقر بها إلى أن مات، وولد صاحب الترجمة بمكة وتعلم بها. وعاد في أواخر حياته إلى طرابلس ومات فيها. من كتبه "تفسير القرآن" وصل فيه إلى سورة الأعراف، و "حاشية" على "أنوار التنزيل" في التفسير، للبيضاوي. (١).

الشيخ المهدي السعدي [٨٩٦ - ٩٦٤ هـ / ١٤٩١ - ١٥٥٧ م]

<span data-type="title" id=toc-2020>محمد بن محمد بن محمد بن عبد الرحمن بن زيدان الحسني، أبو (عبد الله، المعروف بالشيخ والملقب بالسلطان المهدي)</span>
_________
(١) شجرة النور ٢٧٠ ونيل الابتهاج ٣٣٧ وهدية العارفين ٢: ٢٤٢ والمنهل العذب ١: ١٩٥ والأعلام ٧: ٥٨ ومعجم المؤلفين ١١: ٢٣٠.','2','803',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1466,'عبد الله، المعروف بالشيخ والملقب بالسلطان المهدي: ثالث سلاطين الدولة السعدية بالسوس ومراكش. كان من عظماء الرجال، مهيبا، غزير العلم، تفقه في صغره وعني بالتفسير، وحفظ صحيح البخاري وديوان المتنبي. قتل غيلة بظاهر "تارودانت" ونقلت جثته إلى مراكش فدفنت بها في "روضة السعديين". له "حواش على التفسير". (١).

نجم الدين الغزي [٩٧٧ - ١٠٦١ هـ / ١٥٧٠ - ١٦٥١ م]

<span data-type="title" id=toc-2021>محمد بن محمد بن محمد بن محمد بن أحمد بن عبد الله بن مفرج الغزي العامري القرشي الدمشقي، أبو المكارم، نجم الدين:</span>
مؤرخ، باحث أديب، مشارك في علمي التفسير والحديث، من أهل دمشق مولدا ووفاة. وهو صاحب كتاب "الكواكب
_________
(١) تاريخ الدول الإسلامية ومعجم الأسرات الحاكمة ٩٤ وجذوة الاقتباس ١٣٢ ومعجم المحدثين والمفسرين ٢٩ والأعلام ٧: ٢٨٧.','2','804',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1467,'السائرة في تراجم أعيان المئة العاشرة". درس وأفتى. من كتبه "تفسير من سورة الإسراء إلى آخر سورة طه" أملاه في مجالس وعظه. (١).

العيثاوي [... - ١٠٨٠ هـ / ... - ١٦٦٩ م]

<span data-type="title" id=toc-2022>محمد بن محمد بن أحمد العيثاوي الدمشقي:</span>
فقيه، من أهل دمشق، نسبته إلى عيثا من قرى البقاع في لبنان. ولي تدريس صحيح البخاري بالجامع الأموي. قال المحبي: له "تحريرات" على التفسير وغيره، لكنها لم تجمع" (٢).
_________
(١) خلاصة الأثر ٤: ١٨٩ وتراجم بعض أعيان دمشق ١٠١ وفهرس الفهارس ٢: ٨٢ وهدية العارفين ٢: ٣٣٧ ومجلة المقتبس ٥: ٧٣٧ ومجلة الكاتب المصري ٣: ٧٣٩ ومجلة المجمع العلمي العربي ٢٢: ٤٥٣ وفهرس مخطوطات الظاهرية ليوسف العش ٦: ١٨٧ والكواكب السائرة ١ مقدمة المحقق، والأعلام ٧: ٢٩٢ ومعجم المؤلفين ١١: ٢٨٨.
(٢) خلاصة الأثر ٤: ٢٠١.','2','804',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1468,'المغربي [١٠٣٧ - ١٠٩١ هـ / ١٦٢٧ - ١٦٨٣ م]

<span data-type="title" id=toc-2023>محمد بن محمد بن سليمان بن الفاسي بن طاهر السوسي الروداني المغربي:</span>
محدث، مشارك في التفسير والرياضيات والهيئة والنحو والمعاني والبيان، من فقهاء المالكية، ولد بتارودنت (من قرى السوس الأقصى) وتعلم بالمغرب، ورحل إلى الشرق، وجاور بمكة والمدينة سنين، ثم نفي إلى دمشق فأقام إلى أن توفي فيها. له تصانيف. قال المحبي: "وكان له في التفسير وأسماء الرجال وما يتعلق به يد طائلة" (١).

النيفر [... - ١٣١٢ هـ / ... - ١٨٩٤ م]

<span data-type="title" id=toc-2024>محمد (فتحا) بن محمد النيفر، أبو عبد الله:</span>
قاض، محدث، مفسر، من فقهاء المالكية، مولده ووفاته
_________
(١) خلاصة الأثر ٤: ٢٠٤ وهدية العارفين ٢: ٢٩٨ والأعلام ٧: ٢٩٤ معجم المؤلفين ١١: ٢٢١ وفهرس الفهارس ١: ٣١٧.','2','805',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1469,'بتونس. قال مخلوف: "تولى القضاء ثم الفتيا ثم صرف عنها، وتصدى للتدريس كالتفسير وغيره ... " (١).

<span data-type="title" id=toc-2025>محمد بن المختار، الكنتي</span>
[(بعد ١٢٧٠) تقدم في ٢: ٦٣٤] يزاد في نهاية ترجمته بعد مخطوط: في المكتبة العامة بالرباط، و "لطائف القدسي في فضائل آية الكرسي" مخطوط، في المكتبة الملكية بالرباط ٥٤٩٣. ويزاد في نهاية هامشه: ومعجم المحدثين والمفسرين ٣٨.

ابن جلون [١٢٦٤ - ١٢٩٨ هـ / ١٨٤٨ - ١٨٨١ م]

<span data-type="title" id=toc-2026>محمد المدني بن علي بن جلون الفاسي، أبو عبد الله:</span>
قاض، محدث، مفسر، من أهل فاس، ولي قضاء الصويرة. من آثاره "نزهة ذوي العقل السليم في بعض علوم بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ". (٢).
_________
(١) شجرة النور: ٤١٥.
(٢) شجرة النور ٤٠٥ الترجمة ١٦٢٧ ومعجم المحدثين والمفسرين ٣٤.','2','805',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1470,'الطرنباطي [... - ١٢١٤ هـ / ... - ١٧٩٩ م]

<span data-type="title" id=toc-2027>محمد بن مسعود بن أحمد العثماني الأموي، أبو عبد الله الطرنباطي:</span>
قاض، نحوي، أديب، له نظم حسن، من فقهاء المالكية. أصله من الأندلس. ولي القضاء بسجلماسة ثم بثغر الصويرة. ومات بالطاعون بفاس. من آثاره "كتاب في البسملة والحمدلة" (١).

الملغروي [... - ١٠٨١ هـ / ... - ١٦٧٠ م]

<span data-type="title" id=toc-2028>محمد الملغروي:</span>
مفسر، من القضاة، نسبته إلى "ملغرة! قرية في بلاد الترك بينها وبين أدرنة مرحلتان. ولي قضاء المدينة المنورة سنة ١٠٧٥ هـ، ثم قضاء مكة. ومات بالقسطنطينية. قال المحبي: "كان له في التفسير اليد الطولى، ودرس
_________
(١) شجرة النور ٣٧٤ ومعجم المطبوعات ١٢٤٠ وسلوة الأنفاس ٢: ٢٦٨ والأعلام ٧: ٩٦ ومعجم المؤلفين ١٢: ١٦٠.','2','806',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1471,'تفسير القاضي البيضاوي بالروضة الشريفة، فأجاد وأفاد، وكان درسه أعجب درس قرأه الموالي أمثاله بالمدينة" (١).

الصالحي [٩٥٦ - ١٠١٢ هـ / ١٥٤٩ - ١٦٠٣ م]

<span data-type="title" id=toc-2029>محمد بن نجم الدين بن محمد الصالحي الهلالي:</span>
شاعر، من الكتاب من أهل دمشق مولدا ووفاة. قال المحبي: "وبرع في الفقه والتفسير والأدب، مع الذكاء المفرط وحسن الفهم ... " (٢).

المواق [... - ٨٩٧ هـ / ... - ١٤٩٢ م]

<span data-type="title" id=toc-2030>محمد بن يوسف بن أبي القاسم ابن يوسف العبدري الغرناطي، أبو عبد الله، الشهير بالمواق:</span>
عالم غرناطة ومفتيها وإمامها وصالحها في
_________
(١) خلاصة الأثر ٤: ٣١٢.
(٢) خلاصة الأثر ٤: ٢٣٩ وريحانة الألبا ١٤ وهدية العارفين ٢: ٢٦٥ والأعلام ٧: ٣٤٥.','2','806',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1472,'وقت<span data-type="title" id=toc-2033>ه</span>. م<span data-type="title" id=toc-2031>ن </span>فقهاء المالكية. من آثاره "سنن المهتدين في مقامات الدين" طبع، تفسير الآية ٢٩ من سورة فاطر، في تسع مقامات. (١).

(ن)
<span data-type="title" id=toc-2032>نعمة الله بن محمود:</span>
[تقدم في ٧٥٣: ٢] يزاد في نهاية ترجمته بعد للبيضاوي: و "تفسير جزء عم" حققه ونشره الأستاذ محمود شلبي.

(هـ)
المدغري [... - نحو ١٢٦٥ هـ / ... - نحو ١٨٦٩ م]

<span data-type="title" id=toc-2034>هاشم بن محمد بن محمد بن محمد بن عبد الله بن طاهر المدغري:</span>
فقيه مالكي، مفسر، مشارك في بعض العلوم، من أهل مدغرة بالمغرب "كان يقرئ تفسير ابن عطية يحضره علماء سجلماسة". (٢).
_________
(١) معجم المحدثين والمفسرين ٣٧ وجذوة الاقتباس ٢٠٣ وشجرة النور ٢٦٢ وسلوة الأنفاس ٣: ٩٦ ومعجم المطبوعات ١٨١٤ وتاج العروس ٧: ٧٤ ونيل الابتهاج ٣٢٤ ونشر المثاني ١: ٦٢.
(٢) معجم المحدثين والمفسرين ٣٩.','2','807',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1473,'<span data-type="title" id=toc-2035>(ي)</span>
السراج [... - ١٠٠٧ هـ / ... - ١٥٩٨ م]

<span data-type="title" id=toc-2036>يحيى بن محمد السراج الحميري الفاسي، أبو زكرياء:</span>
فقيه مالكي، نحوي، مفسر، من أهل فاس. ولي الفتوى بها، والإمامة والخطابة بمسجد القرويين، وأقرأ التفسير، ودرس المدونة بمدرسة العطارين. (١).
_________
(١) النبوغ المغربي ٢٤٨ وسلوة الأنفاس ٢: ٥٧٢ وشجرة النور ٢٩٤ الترجمة ١١٢٨ ومحمد المنوني في مجلة البحث العلمي ٧: ٢٤٩.','2','808',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1474,'ألورجلاني [٥٠٠ - ٥٧٠ هـ / ١١٠٦ - ١١٧٥ م]

<span data-type="title" id=toc-2037>يوسف بن إبراهيم بن مياد السدراتي الورجلاني، أبو يعقوب:</span>
مؤرخ، مفسر، من أكابر فقهاء الإباضية. من أهل ورجلان مولدا ووفاة. رحل في شبابه إلى الأندلس، وسكن قرطبة طلبا للعلم. شبهه الأندلسيون بالجاحظ. وعاد إلى بلده، ومنها انتقل إلى المشرق، وزار أشهر حواضره العلمية، ولقي أكابر العلماء والشيوخ، كما وصل في إحدى رحلاته العلمية إلى أواسط إفريقية وإلى قريب من خط الاستواء. ثم','2','808',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1475,'استقر بورجلان منقطعا لخدمة العلم، قيل إنه لم يخرج من داره مدة سبعة أعوام، لم يكن يرى فيها إلا ناسخا، وللأقلام باريا، وللدراسة فاعلا، أو للحبر طابخا، أو للدواوين مقابلا، أو للكتب مفسرا". وتوفي بمسقط رأسه. من آثاره "تفسير القرآن الكريم" في سبعين جزءا، قال البرادي: "رأيت منه في بلاد "ريغ" سفرا كبيرا لم أر،','2','809',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1476,'ولا رأيت قط، سفرا أضخم منه ولا أكبر منه، حررت إنه يجاوز سبعمائة ورقة أو أقل أو أكثر، فيه تفسير فاتحة الكتاب والبقرة وآل عمران .. فلم أر ولا رأيت أبلغ منه ولا أشفى للصدر في لغة أو إعراب أو حكم مبين أو قراءة ظاهرة أو شاذة أو ناسخ أو منسوخ أو جميع العلوم منه .. " (١).
_________
(١) السير ٤٤٣ ودائرة المعارف ٥: ٢٤٨ ومعجم المطبوعات ١٩١٤ وتاريخ الجزائر العام ١: ٤١٥ والأعلام ٩: ٢٨١ والجامع الصحيح، مقدمة السالمي، ومعجم المؤلفين ١٣: ١٦٧ ومنجد الأعلام ٧٤١ ومعجم أعلام الجزائر: ٧٤١.','2','809',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1477,'<span data-type="title" id=toc-2038>فهرس كتب ورسائل التفسير</span>
_________
اسم الكتاب ... اسم المفسر ... رقم الصفحة
_________
آلاء الرحمن في تفسير القرآن ... محمد جواد البلاغي ... ٥٠٩
آي القرآن ... الجاحظ ... ٤٤
الآيات المتشابهات ... أحمد بن يزيد بن بقي ... ٨٣
الآيات المتشابهات ... عبد الوهاب بن عبد القادر النائب ... ٣٣٧
الآيات المحكمات والمتشابهات ... مرعي بن يوسف الكرعي ... ٦٦٩
الآيات النيرات للخوارق ... المعجزات ... ابن حجر العسقلاني ... ٥١
الإبانة عن معاني القرآن ... مكي بن أبي طالب ... ٦٨٤
الإبانة في معرفة الأمانة ... محمد بن ... محمد الفارسكوري ... ٦٢٥
أبحاث تتعلق بسورة الأنعام ... محمد بن حسن الكواكبي ... ٥١٦
أبحاث في التفسير ... محمد بن إبراهيم التلمساني ... المستدرك
الإبريز الخاص ... محمد بن محمد سبط المرصفي ٦٢٥
إتحاف الأقران ببعض مسائل القرآن ... أحمد بن قاسم البوني ... المستدرك
الإتحاف بتمييز ما تبع فيه البيضاوي الكشاف ... محمد بن يوسف الشامي ... ٦٥٧','2','811',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1478,'_________
اسم الكتاب ... اسم المفسر ... رقم الصفحة
_________
الإتحاف بتمييز ما تبع فيه البيضاوي الكشاف ... محمد بن علي الداودي ... ٥٨٩
الإتحاف لطلبة الكشاف ... صالح بن مهدي المقبلي ... ٢٢٣
إتحاف ذوي الألباب في قوله تعالى: {يدعو الله ما شاء وعنده أم الكتاب} ... مرعي بن يوسف الكرمي ... ٦٦٩
الإتقان في علوم القرآن ... عبد الرحمن السيوطي ... ٢٦٤
الأجوبة عن الأسئلة ... أحمد بن عبد الرحمن الوارثي ... ٤٢
الاحتجاج بالقرآن ... إسماعيل بن إسحاق ... ٨٨
الإحسان في علوم القرآن ... محمد بن أحمد عقيلة ... ٤٨٧
أحسن القصص (تفسير سورة يوسف) علي بن محمد النقوي ... ٣٨٧
أحكام القرآن ... إبراهيم بن خالد الكلبي ... ١٣
أحكام القرآن ... أحمد بن علي الجصاص ... ٤٨
أحكام القرآن أحمد بن علي الباغاني ... ٤٩
أحكام القرآن ... أحمد بن محمد الطحاوي ... ٥٩
أحكام القرآن ... أحمد بن المعذل ... ٨٠
أحكام القرآن ... أحمد بن يوسف السمين ... ٨٤
أحكام القرآن ... إسماعيل بن إسحاق الجهضمي ... ٨٨
أحكام القرآن ... بكر بن محمد القشيري ... ١١٠
أحكام القرآن ... جبير بن غالب ... ١٢٤
أحكام القرآن ... حفص بن عمر القارىء ... ١٦٣
أحكام القرآن ... داود بن علي الظاهري ... ١٨١
أحكام القرآن ... عبد الله بن أحمد بن المغلس ... ٣٠٣
أحكام القرآن ... عبد المنعم بن محمد، ابن الفرس ... ٣٣٠
أحكام القرآن ... علي بن حجر السعدي ... ٣٥٧
أحكام القرآن ... علي بن محمد الكيا الهراسي ... ٣٧٦
أحكام القرآن ... علي بن موسى القمي ... ٣٨٩
أحكام القرآن ... قاسم بن أصبغ ... ٤٣٢','2','812',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1479,'_________
اسم الكتاب ... اسم المفسر ... رقم الصفحة
_________
أحكام القرآن ... محمد بن أحمد التميمي ... ٤٧٢
أحكام القرآن ... محمد بن إدريس، الإمام الشافعي ... ٤٨٨
أحكام القرآن ... محمد بن الحسين، أبو يعلى ٥٢١
أحكام القرآن ... محمد بن عبد الله بن عبد الحكم ... ٥٥٦
أحكام القرآن ... محمد بن عبد الله، أبو بكر ابن العربي ٥٥٩
أحكام القرآن ... محمد بن القاسم، ابن القرطي ... ٦٠٠
أحكام القرآن = الإنباه على استنباط الأحكام
أحكام القرآن ... موسى بن عبد الرحمن القطان ... ٦٩٢
أحكام القرآن ... أحمد بن أحمد الفارسي ... المستدرك
أحكام القرآن ... محمد بن أحمد بن خويز ... المستدرك
اختصار أنوار التنزيل ... محمد بن محمد، ابن العماد ... المستدرك
اختصار البحر المحيط ... عبد الرحمن بن أحمد ابن البغداوي ... ٢٦٢
اختصار تفسير الرسغني ... عبد الصمد بن إبراهيم ابن الحصري ... ٢٨٤
اختصار ضياء القلوب ... عبد الغني بن القاسم المدني ... ٢٩١
أدلة التوحيد والنبوة والبعث من آيات القرآن ... قاسم بن محمد بن مخلص ... المستدرك
إرشاد الرحمن لأسباب النزول والنسخ والمتشابه من القرآن ... عطيه الأجهوري ... ٣٤٧
إرشاد العقل السليم إلى مزايا الكتاب الكريم ... محمد بن محمد، أبو السعود ... ٦٢٦
الإرشاد في تفسير القرآن ... عبد السلام ابن برحان ... ٢٨٨
إرشاد الله في تفسير القرآن ... أبو بكر بن الطاهر السلوي ... المستدرك
إزالة الغبن ... حيدر علي الفيض آبادي ... ١٦٥
أزهار التنزيل في التفسير ... محمد بن حمزة الكوزل حصاري ... ٥٢٥
أسباب النزول ... أحمد بن علي ابن حجر ... ٥١
أسباب النزول ... عبد الرحمن بن علي ابن الجوزي ... ٢٦٩','2','813',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1480,'_________
اسم الكتاب ... اسم المفسر ... رقم الصفحة
_________
أسباب النزول ... علي بن أحمد الواحدي ... ٣٥٢
أسباب النزول ... عبد بن عبد الله بن المديني ... ٣٦٧
أسباب نزول القرآن ... محمد بن علي بن شهراشوب ... ٥٨٠
الاستدراك والإتمام للتعريف والإعلام فيما أبهم في القرآن من الأسماء والأعلام ... أحمد بن يوسف بن فرتون ... المستدرك
الاستدراك على التعريف والإعلام فيما أبهم في القرآن من الأسماء والأعلام ... محمد بن علي الشامي ... ٥٨٥
الاستغناء في تفسير القرآن ... محمد بن علي الأدفوي ... ٥٧٨
الاستغناء في تفسير ... القرآن ... عبد الوهاب بن محمد بن رامين ... ٣٣٩
الاستيفاء في تفسير قوله تعالى: {إلا ما شاء ربك ...} ... مكي بن طالب حموش ... ٦٨٤
أسرار الآيات وأنوار البينات ... محمد بن إبراهيم الشيرازي ... ٤٧٠
أسرار البسملة ... محمد بن محمد التافلاتي ... ٦٣٠
أسرار التنزيل ... هبة الله بن عبد الرحيم ... ٧١٠
أسرار التنزيل وأنوار التأويل ... محمد بن عمر (الفخر الرازي) ... ٥٩٦
أسرار فواتح القرآن ... محمد بن صالح بن ملوكة ... ٥٣٨
إسعاف الطالبين بتفسير كتاب الله المبين ... إسماعيل بن محمد العجلوني ... ٩٤
أسهل المناهج في تفسير سورة المعارج ... صالح بن محمد الثوقادي ... ٢٣٣
أسئلة وأجوبة عن آيات القرآن ... عبد الله بن محمد بن فرحون ... المستدرك
الإشارات الإلهية والمباحث الأصولية ... سليمان بن عبد القوي الصرصري ... ٢١٦
إشارات القرآن ... محمد صالح القيصري ... ٥٣٨
الإشارة في تلطيف العبارة ... محمد بن أحمد الشنبوذي ... ٤٧٥','2','814',NULL,NULL,'0');
INSERT INTO [b13846] VALUES (1481,'_________
اسم الكتاب ... اسم المفسر ... رقم الصفحة
_________
الإشارة في غريب القرآن ... محمد بن الحسن النقاش ... ٥١٣
الأصداف المنتقاة من سلك جواهر الإسعاف شرح شواهد البيضاوي والكشاف ... علي الكوكباني ... ٣٦٤
الأصفى في تفسير القرآن ... محمد بن مرتضى ... الكاشاني ... ٦٣٥
أضواء البيان في تفسير القرآن بالقرآن ... محمد الأعين الشنقيطي ... ٤٩٦
أطراف أحاديث التفسير ... عبد السلام بن تيمية ... ٢٨٣
إعجاز البيان في تفسير الفاتحة ... محمد بن إسحاق القونوي ... ٤٩١
إعجاز القرآن ... سعيد بن محمد النيسابوري ... ٢٠٩
