INSERT INTO [b143160] VALUES (259,'٢٠٢ (ج ١ - ٢، ٣٧٤ ورقة، فى القرن الحادى عشر الهجرى)، ٢٠٣ (ج ١، ٢٧٠ ورقة، سنة ٦٩٥ هـ)، ٢٠٤ (ج ٨، ٢١٢ ورقة فى القرن الحادى عشر الهجرى)، جورلولو على ١١٧ (٧٨٤ هـ)، ٤٤٣ - ٤٤٥، قاضى زاده ٧٨ (سنة ٨٧٨ هـ) قليج ٢٤٧، شاه زاده محمد ١١ (سنة ٨٥٤ هـ)، تورهان ٥٨ (سنة ٧٧٠ هـ)، اسماعيل ٨٣ - ٨٥ (٨٤١ هـ)، لاله لي ٥٥٥ - ٥٥٩ (١ - ٥، القرن التاسع الهجرى)، سليم أغا ٢١٠ - ٢٠٤ (ج ١ - ٤، فى القرن الثامن الهجرى)، ولى الدين ٧١١ - ٧١٥، عاطف أفندى ٥٢٤ (ج ١، ١٧٤ ورقة، ٧٧٥ هـ)، ٥٢٥ (ج ٢، ٣، ٣٨٤ ورقة، فى القرنين السابع والحادى عشر الهجريين)، ٥٢٦ (ج ٣، ٧٩٣ ورقة)، ٥٢٧ (ج ٤، ١٥٣ ورقة، فى القرن السابع الهجرى)، ٥٢٨ (ج ٥، ٢٠٠ ورقة، سنة ٧٠٩ هـ) ٥٩٠ (ج ٣، ١١٧ ورقة، حوالى سنة ٧٠٠ هـ)، ٥٩١ (١/ ٢٥٦ ورقة فى القرن الثامن الهجرى)، محمود باشا ٩٢ - ٩٨ (٥٧٣ ورقة، حوالى ٩٠٠ هـ)، (ج ١، ٢٣٧ ورقة، فى القرن الثامن الهجرى، (ج ٣، ٢٨٣ ورقة، سنة ٧٥٧ هـ)، (ج ٤، ٣٢٢ ورقة، فى القرن الثامن الهجرى)، (ج ٥، ٢٠٨ ورقة فى القرن الثامن الهجرى)، (٢٦٩ ورقة، سنة ٧٥٩ هـ)، (٢١٣ ورقة، سنة ٧٥٧ هـ)، قرة چلبى ٥٥ - ٦١ (ج ١، ٢٦٥ ورقة، فى القرن الثامن الهجرى)، (ج ٢، ٢٦٢ ورقة، فى القرن الثامن الهجرى)، (ج ٣، ٢٢١ ورقة، فىالقرن الثامن الهجرى)، (ج ٤، ٢٣٦ ورقة، فى القرن الثامن الهجرى)، (ج ٥، ٢٠٢ ورقة فى القرن الثامن الهجرى)، (ج ٦، ١٩٤ ورقة، فى القرن الثامن الهجرى)، (ج ٧، ٢٠٨ ورقة، فى القرن الثامن الهجرى)، مدرسة مصلى ٦١ (ج ١، ٤٢٤ ورقة فى القرن الثامن الهجرى)، يوسف أغا (السليمانية) ١٦٠ - ١٦١ (ج ١، ج ٣، ٢٦٠، ٢٦٤ ورقة، فى القرن التاسع الهجرى) مانيسبه ٢١٩ (٢٠٠ ورقة، سنة ٧١٦ هـ) ادرنة (السليمية) ٨٠١، (أربعة مجلدات) جوروم ٢٥٢/ ١ (الأوراق ١ - ١٣٣، سنة ٦٦٦ هـ بخط المؤلف)، تشستربيتى ٣١٣٥ (ج ١، ١٧٦ ورقة، فى القرن السابع الهجرى)، ييل ٢٦٤) ٢٤ - A ورقة، فى القرن العاشر الهجرى، انظر: نيموى ٦٩٠)، الأزهر ١: ٦١٧ - ٦١٨ (عدة مجلدات)، الأوقاف ببغداد ٢٩٠٨، ٢٩٦١ (انظر: أسعد طلس ص ٤٩).
وطبع فى: لكنو سنة ١٢٨٥ هـ، ودهلى سنة ١٣٠٤ هـ، ١٣٠٩ هـ، والقاهرة فى ٤ مجلدات سنة ١٢٧١ هـ، وفى خمسة مجلدات سنة ١٢٨٣ هـ، وعلى هامش الإرشاد للقسطلانى بولاق سنة ١٣٠٥ هـ، وفى القاهرة سنة ١٣٢٠ هـ/ ١٣٢٣ هـ، وقد طبع مع متن القسطلانى فى تسعة مجلدات، القاهرة ١٩٢٩ م- ١٩٣٠ م.
وقد اختار منه عبد الله بن محمد بن عبد القادر بن ناصر الأنصارى (المتوفى سنة ٧٢٤ هـ/ ١٣٢٣ م انظر: الدرر الكامنة لابن حجر ٢/ ٢٩٥ - ٢٩٦): جاريت ١٣٦٤ (ج ١، ٢٧٤ ورقة، سنة ٧١٥ هـ، بخط المؤلف).','1','268',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (260,'وله حاشية بعنوان «النكت على المنهاج» لأحمد بن لؤلؤ بن النقيب (المتوفى سنة ٧٦٩ هـ/ ١٣٦٨ م) انظر بروكلمان ملحق ٢/ ١٠٤) القاهرة، ملحق ٣/ ١٧٦، ٢٦٢٥٠ ب (ج ١، ٢٢٢ ورقة، سنة ٨٧٢ هـ).
٦ - «المفصح المفهم والموضح الملهم لمعانى صحيح مسلم» لأبى عبد الله محمد بن يحيى بن هشام الأنصارى (المتوفى سنة ٦٤٦ هـ/ ١٢٤٨ م، انظر، تكملة الصلة لابن الأبار (الجزائر) ٣٦١ - ٣٦٢، بغية الوعاة للسيوطى ١١٥)، ويوجد فى: طلعت حديث ٧٩٤ (١٠٦ ورقة، سنة ٧٣٤ هـ، انظر: فؤاد سيد فى:
مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٢٣٠).
٧ - له شرح من تأليف محمد بن محمود البابرتى (المتوفى سنة ٧٨٦ هـ/ ١٨٣٤ م انظر بروكلمان ٢/ ٨٠) جار الله ٣٥٤ (ج ٣، ١٨٥ ورقة، ٧٠١ هـ).
٨ - له شرح من تأليف ابن المهندس عبد الله بن محمد بن إبراهيم الصالحى الحنفى/ (٦٩١ هـ/ ١٢٩٢ م- ٧٦٩ - ٨/ ١٣٦٨ م، انظر: الدرر الكامنة لابن حجر ٢/ ٢٨٢، وهدية العارفين ١/ ٤٦٦)، جامعة لينينجراد ٩٧٨ (سنة ٧٢٣ هـ» -٩ Zop.Koll.Vost.١ ,٣٧٠، (تحفة المنجد المفهم فى غريب صحيح مسلم» لمؤلف مجهول، جمع فيه تعليقات أستاذه سبط ابن العجمى (المتوفى سنة ٨٤١ هـ/ ١٤٣٨ م)، تم سنة ٨١٦ هـ فى حلب، ويوجد فى حميديه (٣٠٧)، ١١٨ (٣٤٨ ورقة، سنة ٨١٦ هـ، انظر» -١٠ Weisweiler.٦١: فضل المنعم فى شرح صحيح مسلم»، لشمس الدين أبى عبد الله محمد بن عطاء الله بن محمد الرازى (٧٦٧ هـ/ ١٣٦٥ م- ٨٢٩ هـ/ ١٤٢٦ م، انظر: الضوء اللامع للسخاوى ٨/ ١٥١ - ١٥٥)، فيض الله ٤٤٢ - ٤٤٣ (مجلدان، ٢٤٨ ورقة، ٢٣٠ ورقة، ٨٤٦ هـ)، بنكيبور ٥/ ١/ ٨٨ - ٨٩، رقم ٢٠٢، (مجلد واحد ٣٤١ ورقة، ٨٢٦ هـ).
١١ - «الديباج على صحيح مسلم» لجلال الدين السيوطى (المتوفى سنة ٩١١ هـ/ ١٥٠٥ م انظر بروكلمان ٢/ ١٤٣) ويوجد مخطوطا فى: بلدية الإسكندرية ٢٠٣٤ د (١٠١٢ هـ)، القرويين بفاس، الرقم القديم ٤٩٤، بيشاور ٢٩١، قوله ١/ ٤١٧، مكتبة شيخ الإسلام عارف حكمت بالمدينة ٢٦ (انظر، Spies ,ZDMG ٩٠ /١٩٣٦ /١٠٨ الأزهر ١/ ٤٩٦، حديث ٥٢٤ (١٨٠ ورقة، سنة ٩٧٥ هـ)، القاهرة، ثان ١/ ١١٦، حديث ٢١٩.
_________
(٣٠٧) وليس فى عاشر أفندى كما ذكر ذلك بروكلمان','1','269',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (261,'وعليه «وشى الديباج على صحيح مسلم» لمحمد بن محمد بن على بن البجمعوي الدّمنتى، انظر بروكلمان ٢/ ٤٨٥، القاهرة، دار الكتب ١٢٩٨.
١٢ - بغية القارئ والمتفهم ليحيى بن محمد السنباطى، انظر بروكلمان ملحق ١ تحت رقم ١٠ (تم سنة ٩٥٨ هـ/ ١٥٥١ م)، جوتا ٢ (قطعة منه فى الأوراق ٦٣ - ٦٤).
١٣ - (شرح) لشهاب الدين أحمد بن عبد الحق (قبل سنة ٩٦٢ هـ)، جار الله ٣٢٥ (ج ١، ٢٩٢، ورقة، ٩٦٢ هـ).
١٤ - (شرح) لعبد الرءوف المناوى (المتوفى سنة ١٠٣١ هـ/ ١٦٢٢ م انظر بروكلمان ٢/ ٣٠٦، (الموصل ١٥٥).
١٥ - عناية الملك المنعم لشرح صحيح مسلم لعبد الله بن محمد يوسف أفندى زاده (المتوفى سنة ١١٦٧ هـ/ ١٧٥١ م، انظر Bursali Taher ,Osm.Muell.١ ,٣٦٦: أسعد أفندى ٣٨١ - ٣٨٣، حميدية ٣٤٣ - ٣٤٥، لاله لي ٥٦٠ - ٥٦٢، نور عثمانية ١٠٤٢ (٣٦٨ ورقة، ١١٦٤ هـ، بخط المؤلف)، ١٠٤٣ (٢٨٧ ورقة، سنة ١١٦٤ هـ، بخط المؤلف)، سليم أغا ٢٠٥ (ج ١، ٢، ٣٩٧ ورقة، فى القرن الثانى عشر الهجرى)، سراى مدينة ٢٤٥ (ج ١ ٤٩٩ ورقة سنة ١١٦٥ هـ بخط المؤلف)، ٢٤٦ (ج ٣، ٣١٧ ورقة، سنة ١١٦٦ هـ بخط المؤلف).
١٦ - شرح فارسى بعنوان: «منبع العلم» لنور الحق بن عبد الحق الدهلوى (المتوفى سنة ١٠٧٣ هـ/ ١٦٦٢ م). أكمله ابنه فخر الدين محب الله، بنكيبور ١٤/ ٦١ - ٦٢ رقم ١٢٠٧ (٦٠٧ ورقة، فى القرن الثالث عشر الهجرى) (٣٠٨).
١٧ - وعليه «حاشية» لأبى الحسن محمد بن عبد الهادى السّندى (المتوفى ١١٣٦ هـ/ ١٧٢٣ م انظر بروكلمان ٢/ ٣٩١)، مكتبة جامعة إستنبول ٢١٠٠. طبع فى ملتان (بدون تاريخ).
١٨ - «حاشية شرح مسلم» لعلى بن أحمد السعيدى (كان يعيش سنة ١١٦٨ هـ/ ١٧٥٤ م) لاله لي ٢٦٢٨ (١٩٧ ورقة، ١١٦٨ هـ، بخط المؤلف).
١٩ - شرح مع ترجمة هندوستانية لمولوى وحيد الزمان، لاهور ١٣٠٤ - ١٣٠٦ هـ.
٢٠ - (شرح) مع ترجمة بنجابية دون الأسانيد، لعبد العزيز بن غلام رسول، لاهور ١٣٠٧ هـ
_________
(٣٠٨) المخطوطة رقم ١٠٠٧، التى ذكرها بروكلمان بمكتبة الجمعية الآسيوية بالبنغال تحذف لعدم وجودها.','1','270',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (262,'٢١ - «السراج الوهاج من كشف مثالب صحيح مسلم بن الحجاج» لصديق حسن خان المتوفى سنة ١٣٠٧ هـ/ ١٨٩٠ م انظر بروكلمان ٢/ ٥٠٣، بهوبال ١٣٠٢ هـ.
٢٢ - «فتح الملهم بشرح صحيح مسلم» لفضل الله جابر أحمد الديبندى العثمانى، دهلى (دستى صورة)، مجلدان، ١٩٣٤ م./ ٢٣ - شرح ناقص لا يعرف مؤلفه، فيض الله ٤٥٠.
٢٤ - شرح غير معروف المؤلف، كابول، مطبعة الرياسة ٢٤٥ (ج- ٢، ٢٣٧ ورقة، ١١٢٣ هـ، انظر:
مجلة معهد المخطوطات العربية ٣/ ٨٥).

<span data-type="title" id=toc-546>شروح على أجزاء من صحيح مسلم:</span>
٢٥ - «المصباح فى عيون الصحاح، وهو العاشر من أقوال مسلم بن الحجاج» لعبد الغنى بن عبد الواحد بن على الجماعيلى المقدسى (المتوفى سنة ٦٠٠ هـ/ ١٢٠٣ م انظر بروكلمان ١/ ٣٥٦)، الظاهرية، مجموع ٣٠ (٢١٧ أ- ٢٤٤ ب، بخط المؤلف).
٢٦ - «غنية المحتاج فى ختم صحيح مسلم بن الحجاج» تأليف محمد بن عبد الرحمن السخاوى (المتوفى سنة ٩٠٢ هـ/ ١٤٩٧ م انظر بروكلمان ٢/ ٣٤) القاهرة، دار الكتب، حديث ٢٥٦٩ (٢٦ ورقة، فى القرن التاسع الهجرى، وعلى صحيفة الغلاف خط المؤلف، انظر: فهرس معهد المخطوطات ١/ ٨٩).
٢٧ - «شرح خطبة مسلم بن الحجاج»، لأحمد بن محمد بن أبى بكر القسطلّانى (المتوفى سنة ٩٢٣ هـ/ ١٥١٧ م انظر بروكلمان ٢/ ٧٣) المنصورة ١١٠/ ٢ (١١٤٠ هـ، انظر: عبد الرحمن عبد التواب فى مجلة: معهد المخطوطات العربية ٤/ ٢٨٦).

<span data-type="title" id=toc-547>مختصرات لصحيح مسلم:</span>
١ - لأبى عبد الله محمد بن عبد الله بن تومرت (المتوفى سنة ٥٢٤ هـ/ ١١٣٠ م، انظر بروكلمان ١/ ٤٠١) تشستربيتى ٤١٦٤ (٦٥ ورقة، فى القرن السادس الهجرى).
٢ - «تلخيص صحيح مسلم» لأحمد بن عمر الأنصارى القرطبى (المتوفى سنة ٦٥٦ هـ/ ١٢٥٨ م انظر بروكلمان ١/ ٣٨٤) جار الله ٢٦٤ (ج ٢، ١٩٦ ورقة، ٦٩٩ هـ)، تشستربيتى ٣٥٩٢ (٢٦١ ورقة فى القرن الثامن الهجرى)، طلعت، حديث ٨٠٦ (١٣٢ ورقة، فى القرن الثامن الهجرى).','1','271',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (263,'٣ - المختصر (الجامع المعلم بمقاصد جامع مسلم)» لأبى محمد عبد العظيم بن عبد القوى المنذرى (المتوفى سنة ٦٥٦ هـ/ ١٢٥٨ م انظر بروكلمان ١/ ٣٦٧)، مؤلف سنة ٦٣٩ هـ، ويوجد مخطوطا فى: برلين ١٢٤١ (٢٧٣ ورقة، حوالى ٦٥٠ هـ تقريبا)، ميونيخ ١١٩ (٣٠٠ ورقة، ٦٩٢ هـ، جامع ينى ٢٧٥ (٢٠٩ ورقة ٦٤٦ هـ)، السليمانية ٣٢٢ (٢٨١ ورقة، ١١٤٨ هـ) الفاتيكان (فيدا) ١٠٣٣ (٢٤٢ ورقة، ٦٤٩ هـ)، القرويين بفاس ١٠٩ (٦٤٥ هـ، انظر: مجلة معهد المخطوطات العربية ٥/ ١٤)، آصفي، حديث ٦٧ (١٧٣ ورقة، ٩٤٣ هـ)، رئيس الكتاب ٢٤٥ (٢٧٤ ورقة، ٦٨٨ هـ)، فاتح ١١٤١ (ج ١، ٢٧٤ ورقة ٧١٥ هـ)، ١١٤٢ (ج ٢، ٢٠٤ ورقة ٧١٥ هـ)، مكتبة جامعة إستنبول ٣٥٨٣ هـ (١، ٢٠٧ ورقة، فى القرن السابع الهجرى)، أيا صوفية ٨٨٤ (ج ١، ١٦٣ ورقة، ٧٢٣ هـ) ٨٨٥ (ج ٢، ٢١٨ ورقة، فى القرن الثامن الهجرى)، الكتانى بالرباط ٥٤٤ (٢٣٣ ورقة، ٧٦١ هـ)، تيمور ٢: ١٥٦، حديث ٥٢٣ (ج ١، ٩١٨ هـ)، منجانا ٥٨٧ (٢٩٧ ورقة، فى القرن العاشر الهجرى، انظر: الفهرس ١١٧)، ييل) ٦٥٣ L مجلدان ١٣١٤ م. انظر: نيموى رقم ٦٨٦).
٤ - «مختار الإمام مسلم» جمعه: محمد مصطفى عمارة، القاهرة (دون تاريخ).
٥ - «مختصر» مجهول المؤلف: البلدية بالإسكندرية ١١٥٩ ب (١١٥٩ هـ).
٦ - «الرباعيات من صحيح مسلم» ٢٥ حديثا بأربعة رواة فقط، لأمين الدين محمد بن إبراهيم الوانى (المتوفى ٧٣٥ هـ/ ١٣٣٥ م، انظر: الدرر الكامنة لابن حجر ٣/ ٢٩٣)، ويوجد مخطوطا فى بنكيبور ٥/ ٢/ ١٨٤، ٤٦٢/ ٢ (الأوراق ١٥ - ١٩، فى القرن الثامن الهجرى).
وهناك كتب مكملة أو مقلّدة صنفت فيها أحاديث أخرى على طريقة مسلم، ولا يمكن أن تعد مختصرات من صحيح مسلم (٣٠٩) أ- «المسند المخرج على الكتاب مسلم بن الحجاج»، لأبى عوانة يعقوب بن إسحاق الأسفراييني (المتوفى سنة ٣١٦ هـ/ ٩٢٨ م) انظر رقم ١٤٩ من هذا الباب.
ب- «المسند المستخرج على كتاب أبى الحسين محمد بن الحسن»، لأبى نعيم أحمد بن عبد الله الأصفهانى (المتوفى ٤٣٠ هـ/ ١٠٣٨ م، انظر بروكلمان ١/ ٣٦٢).

<span data-type="title" id=toc-548>أ- كتب حول صحيح مسلم:</span>
١ - «رجال صحيح الإمام مسلم» لأحمد بن على بن محمد بن منجويه الأصفهاني/ (المتوفى سنة
_________
(٣٠٩) تصحح عند بروكلمان فقد عدّها مختصرات له.','1','272',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (264,'٤٢٨ هـ/ ١٠٣٦ م، انظر: رقم. ٣٢٧ من هذا الباب) البلدية بالإسكندرية ١٢٤ ب (٢١٠ ورقة، سنة ٦٦٤ هـ، انظر: فهرس معهد المخطوطات العربية ٢ برقم ٦٨٠).
٢ - «جزء فيه أحاديث صحيحة مما رواه مسلم بن الحجاج بين المصطفى وبينه تسعة نفر» لأبى عبد الله محمد بن عبد الواحد المقدسى (المتوفى سنة ٦٤٣ هـ/ ١٢٤٥ م انظر بروكلمان ١/ ٣٩٩، الظاهرية، حديث ٣٤٨ (من ٥١ أ- ٥٥ ب، فى القرن السابع الهجرى).
٣ - «جزء فيه الرواة عن الإمام مسلم» لأبى عبد الله محمد بن عبد الواحد المقدسى (المتوفى سنة ٦٤٣ هـ/ ١٢٤٥ م انظر بروكلمان ١/ ٣٩٩) الظاهرية، مجموع ٥٢/ ٧ (من ٧٩ أ- ٨٤ أ، فى القرن الثامن الهجرى)، ٨٢/ ١٠.
٤ - «غرر الفوائد المجموعة فى بيان ما وقع فى صحيح مسلم من الأحاديث المقطوعة» لرشيد الدين أبى الحسن يحيى بن على القرشى العطار (المتوفى ٦٦٢ هـ/ ١٢٦٤ م، انظر: الأعلام للزركلى ٩/ ١٩٩، معجم المؤلفين لكحالة ١٣/ ٢١٣)، برلين ١٢٣٢ (ج ١، ٣٥ ورقة، ٨٦٥ هـ)، ١٢٣٣، (الأوراق ٤٠ - ٥٩، حوالى سنة ٨٠٠ هـ)، داماد إبراهيم ٣٩٦/ ٦ (١٦٤ أ- ١٧٣ ب، سنة ٨٦٦ هـ).

<span data-type="title" id=toc-549>ب- حول رواة مسلم والبخارى ورواياتهم:</span>
١ - «رجال البخارى ومسلم» لأبى الحسن على بن عمر الدّار قطنى (المتوفى سنة ٣٨٥ هـ/ ٩٩٥ م، انظر كذلك رقم ٢٤٩ من هذا الباب)، آصفية، رجال ١٧٢ (٤٠ ورقة، فى القرن الثامن الهجرى، انظر:
فهرس معهد المخطوطات العربية ٢ برقم ١٠٦٢).
٢ - «ذكر قوم ممن أخرج لهم البخارى ومسلم فى صحيحيهما، وضعفهم النّسائى فى كتاب الضعفاء» للدارقطنى، سراى، أحمد الثالث ٦٢٤/ ٢١ (٢٥٣ أ- ٢٥٤ ب، ٦٢٦ هـ)، كوبريلى ٤٠/ ٤ (١٧٢ أ- ٢٠٠ ب، فى القرن السابع الهجرى).
٣ - «أسماء الصحابة التى اتفق فيها البخارى ومسلم وما انفرد كل منهما» للدارقطنى، القاهرة، ثان ٨/ ١٨، مجموع ٨٠١ (١٠٣ ورقة، ١٠٦٠ هـ).
٤ - «رسالة فى بيان ما اتفق عليه البخارى ومسلم وما انفرد به أحدهما عن الآخر» للدارقطنى، سيرز ٦٢٠/ ٥ (الأوراق ٣٥ - ٣٧).
٥ - «كتاب التتبع وهو ما أخرج على الصحيحين وله علة»، للدارقطنى، السعيدية بحيدرآباد، حديث ٣٥٥ (من ١١٥ ب- ١٣٤ ب، ٧٨٦ هـ).','1','273',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (265,'٦ - «تسمية من أخرجهم البخارى ومسلم» لأبى عبد الله الحاكم النيسابورى (المتوفى سنة ٤٠٤ هـ/ ١٠١٤ م، انظر: كذلك رقم ٢٩٧ من هذا الباب).
٧ - «المدخل إلى معرفة الصحيحين» للحاكم النّيسابورى، انظر: كذلك رقم ٢٩٧ من هذا الباب).
٨ - «تقييد المهمل وتمييز المشكل» للحسين بن محمد الجيّانى (المتوفى ٤٩٨ هـ/ ١١٠٥ م انظر بروكلمان ١/ ٣٦٨)، وهو فهرس هجائى لرواة الصحيحين الذين تشابهت أسماؤهم ونقد لهم، بايزيد ١٢١١/ ١ (١٩ ورقة، ٦٢٨ هـ، انظر، Weisweiler، وانظر كذلك معلومات جديدة لما ورد فى بروكلمان ١/ ٣٦٨).
٩ - «التنبيه على الأوهام الواردة فى الصحيحين» وهو يتناول الرواية والرواة، للحسين بن محمد الجيّانى: بايزيد ٢١١/ ٢ (الأوراق ١٩ - ١٩٩، ٦٢٨ هـ، انظر: فهرس معهد المخطوطات ١/ ٧٠).
١٠ - «الجمع بين رجال الصحيحين» لمحمد بن طاهر بن على القيسرانى (المتوفى ٥٠٧ هـ/ ١١١٣ م، انظر بروكلمان ١/ ٣٥٥).
١١ - «المعلم بأسامى شيوخ البخارى ومسلم» لمحمد بن إسماعيل بن خلفون (المتوفى سنة ٦٣٦ هـ/ ١٢٣٨ م، انظر: معجم المؤلفين لكحالة ٩/ ٦١)، الأزهر ١/ ٣٧٤ مصطلح الحديث ١٣٦ (ج ١ و ٢، ١٦١ ورقة، ١٣٠ ورقة، ٦٥٤ هـ، انظر فهرس معهد المخطوطات العربية ٢ رقم ٤٩٨).
١٢ - «تسمية رجال صحيح مسلم الذين انفرد بهم عن البخارى» لمحمد بن أحمد الذهبى (المتوفى ٧٤٨ هـ/ ١٣٤٨ م انظر بروكلمان ٢/ ٤٦)، لاله لي ٢٠٨٩ (٦ ورقات، ٧٣١ هـ)، انظر فايسفايلر» -١٣ Weisweiler.١٠١. رجال البخارى ومسلم» لأحمد بن أحمد بن موسى الهكّارى (المتوفى ٧٦٣ هـ/ ١٣٦٢ م انظر بروكلمان ملحق ٢/ ٢٧٤) تيمور، تاريخ ٥٤٣ (ج ١، ٢٦٥ ورقة، بخط المؤلف، انظر: فهرس معهد المخطوطات ٢ برقم ٢٥٩).
١٤ - «الرياض المستطابة فى جملة من روى فى الصحيحين من الصحابة» تأليف يحيى بن أبى بكر العامرى الشافعى (المتوفى سنة ٨٩٣/ ١٤٨٨ م انظر بروكلمان ملحق ٢/ ٢٢٥ - ٢٢٦). انظر كذلك فايسفايلر» -١٥ Weisweiler.١٠٧. قرة العين فى ضبط أسماء رجال الصحيحين» تأليف عبد الغنى بن أحمد البحرانى الشافعى (كان يعيش سنة ١١٧٤ هـ/ ١٧٦١ م، انظر معجم المؤلفين لكحالة ٥/ ٢٧٠). طبع فى حيدرآباد ١٣٢٣ هـ.','1','274',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (266,'<span data-type="title" id=toc-550>ج- شروح لصحيحى البخارى ومسلم:</span>
١ - «تفسير غريب ما فى الصحيحين» لمحمد بن أبي نصر الحميدى (المتوفى سنة ٤٨٨ هـ/ ١٠٩٥ م انظر بروكلمان ١/ ٣٣٨)، تيمور، لغة ٨٠ (١٨١ ورقة فى القرن السابع الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٦٨، ٣٤٥).
٢ - «شرح مشكلات الصحيحين، المستخرج من مشارق الأنوار للقاضى عياض» لأبى إسحاق إبراهيم بن يوسف بن قرقول (المتوفى سنة ٥٦٩ هـ/ ١١٧٣ م. انظر بروكلمان ١/ ٣٧٠) ترك المستخرج ما يتعلق بما ورد فى الموطأ من الأحاديث، كوبريلى ٣٣٤ (٢٥٧ ورقة، ٧٥٧ هـ).
٣ - «كشف مشكل حديث الصحيحين»، لأبى الفرج عبد الرحمن بن على بن محمد الجوزى (المتوفى سنة ٥٩٧ هـ/ ١٢٠٠ م انظر بروكلمان ١/ ٥٠١)، جاريت ١٤٥٠ (٢٢١ ورقة، فى القرن السابع الهجرى)، (بخصوص النسخ الأخرى انظر بروكلمان ملحق ١/ ٩١٨ رقم ٢٧ د ٤ - «مشكل الصحيحين» لخليل بن كيكلدى بن عبد الله (المتوفى سنة ٧٦١ هـ/ ١٣٥٩ م انظر بروكلمان ٢/ ٦٤) چلبى عبد الله ٧٦/ ١ (١ - ٢٣٨ أ، فى القرن الثامن الهجرى).
٥ - «كشف النقاب عما روى الشيخان للأصحاب» تأليف خليل بن كيكلدى، چلبى عبد الله ٧٦/ ٢ (٢٣٩ أ- ٢٥٦ أفى القرن الثامن الهجرى).

<span data-type="title" id=toc-551>د الروايات المشتركة فى البخارى ومسلم والمستدرك عليهما</span>.
١ - «كتاب الجمع بين الصحيحين» تأليف محمد بن عبد الله الجوزقى (المتوفى سنة ٣٨٨ هـ/ ٩٩٨ م، انظر كذلك رقم ٢٣٥ من هذا الباب).
٢ - «أطراف الصحيحين» لخلف بن محمد على الواسطى (المتوفى سنة ٤٠١ هـ/ ١٠١١ م).
٣ - «المستدرك على الصحيحين» لمحمد بن عبد الله الحاكم النيسابورى (المتوفى سنة ٤٠٤ هـ/ ١٠١٤ م، انظر رقم ٢٩٧ من هذا الباب).
٤ - «الجمع بين الصحيحين» لمحمد بن أبى نصر الحميدى (المتوفى سنة ٤٨٨ هـ/ ١٠٩٥ م انظر بروكلمان ملحق ١/ ٥٧٨) وعليه شرحان.
(أ) «الإفصاح عن معانى الصحاح»، ليحيى بن محمد بن هبيرة (انظر بروكلمان ١/ ٣٣٨) (المتوفى سنة ٥٦٠ هـ/ ١١٦٥ م، انظر بروكلمان ١/ ٣٩٨)','1','275',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (267,'(ب) «كلام الضياء المقدسى على شيء من أحاديث الجمع بين الصحيحين» تأليف محمد بن عبد الواحد (المتوفى سنة ٦٤٣ هـ/ ١٢٤٥ انظر بروكلمان ١/ ٣٩٩)، الظاهرية مجموع ٨٥/ ١٢.
٥ - «الجامع بين الصحيحين لأبى نعيم عبيد الله بن الحسن بن أحمد بن الحداد (المتوفى سنة ٥١٧ هـ/ ١١٢٣ م، انظر: شذرات الذهب لابن العماد ٤/ ٥٦، مرآة الجنان لليافعى ٣/ ٢٢١)، تشستربيتى ٣٤٤٧ (٣٩٩ ورقة، سنة ٥١٠ هـ، مع تصحيحات للمؤلف).
٦ - الجمع بين الصحيحين لعبد الحق بن عبد الرحمن بن عبد الله الإشبيلى بن الخراط (المتوفى سنة ٥٨١ هـ/ ١١٨٥ م)، نور عثمانية ٧٦٩ (ج ١، ٢٥١ ورقة، سنة ٦٦٧ هـ)، ٧٧٠ (٢، ٣٤٦ ورقة، سنة ٧٢٢ هـ)، لاله لي ٣٩٥ (كامل، ٢٦٨ ورقة، سنة ٦١١ هـ).
٧ - «عمدة الأحكام مما اتفق عليه الإمامان البخارى/ ومسلم» تأليف عبد الغنى بن عبد الواحد الجمّاعيلى (المتوفى سنة ٦٠٠ هـ/ ١٢٠٣ م)، انظر بروكلمان ١/ ٤٥٦.
٨ - «الجمع بين الصحيحين لأبى حفص، عمر بن بدر بن سعيد الموصلى (المتوفى سنة ٦٢٢ هـ/ ١٢٢٥ م)، انظر بروكلمان ملحق ١/ ٦١٠ ومعلومات جديدة لذلك.
٩ - «البيان عما اتفق عليه الشيخان لأبى المجد إسماعيل بن هبة الله بن سعيد الموصلى ابن باطيش (المتوفى سنة ٦٥٥ هـ/ ١٢٥٧ م، انظر: طبقات الشافعية للسبكى ٥/ ٥١، وشذرات الذهب لابن العماد ٥/ ٢٦٧)، عاطف ٥٩٩، (٢٥٦ ورقة، سنة ٦٤١ هـ).
١٠ - «مفيد السامع والقارى مما اتفق عليه مسلم والبخارى» لأحمد بن عبد الرحمن بن محمد الحريرى (المتوفى سنة ٧٥٨ هـ/ ١٣٥٧ م) شهيد على ٥٧٥ (٢٥٧ ورقة، سنة ٨٤١ هـ).
١١ - «شرح زوائد مسلم على البخارى» لعمر بن رسلان البلقينى (المتوفى سنة ٨٠٥ هـ/ ١٤٠٣ م انظر بروكلمان ٢/ ٩٣) الاوقاف ببغداد ٣٠١٢ - ٣٠١٤ (ج ٥ - ٧، انظر: طلس ٤١).
١٢ - «أحكام الصحيحين» لمحمد شريف بن مصطفى التّوقادى، طبع فى بولاق ١٢٩٠ هـ» (على هامس مسلم- المجلد الأول).
١٣ - «الجمع بين الصحيحين» مجهول المؤلف، الأوقاف ببغداد ٢٨٠١ (٧٢٧ هـ، انظر طلس ٣٩).','1','276',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (268,'<span data-type="title" id=toc-552>ثانيا: كتاب الكنى والأسماء:</span>
شهيد على ١٩٣٢ (٥٧ ورقة، منقول عن نسخة الدّارقطنى المتوفى سنة ٣٨٥ هـ/ ٩٩٥ م، انظر ما كتبه ريتر H.Ritter ,Der Islam ١٧ /١٩٢٧ /٢٥٠: الظاهرية، مجموع ١ (الأوراق ٤١ - ١٠٤ ب، فى القرن السادس الهجرى، انظر: الظاهرية العش ٢٠٢ - ٢٠٣، وبه معلومات مخالفة)، سراى، أحمد الثالث ٢٩٦٩/ ٣ (من ٤٠٠ أ- ٤٦٠ أ، ٦٢٦ هـ)، طلعت، مصطلح ١٢٧ (٧٦ ورقة، انظر: فؤاد سيد، فى: مجلة معهد المخطوطات العربية ٣/ ٣٣٢، القاهرة، دار الكتب مصطلح ٢٨٢).

<span data-type="title" id=toc-553>ثالثا: كتاب المنفردات والوحدان:</span>
بنكيبور ١٢/ ٣٦ رقم ٦٩١ (٢٦ ورقة) مخطوط حديث، السعيدية بحيدرآباد، حديث ٣٥٢ (من ٦٦ ب- ٧٩ ب). طبعة حجر، أكرا ١٣٢٣ هـ.

<span data-type="title" id=toc-554>رابعا: كتاب الطبقات:</span>
يتناول فيه معاصرى الرسول الذين رأوه ورووا عنه، والذين شاهدوه فقط ولم يرووا عنه: سراى، أحمد الثالث ٦٢٤/ ٢٦ (من ٢٧٩ أ- ٢٩٧ ب، ٦٢٨ هـ، انظر: فهرس معهد المخطوطات العربية ٢ رقم ٣٩٤).

<span data-type="title" id=toc-555>خامسا: رجال عروة بن الزبير (انظر ترجمته فى القسم الخاص بالتاريخ):</span>
الظاهرية، مجموع ٥٥/ ١١ (الأوراق ١٣٩ - ١٤٧، سنة ٤٦٣ هـ، بخط الخطيب البغدادى، انظر:
العش ٢٢٥).

<span data-type="title" id=toc-556>سادسا: كتاب التمييز:</span>
الظاهرية، مجموع ١١/ ١ (من ١ ب- ١٥ أ، فى القرن السادس الهجرى).

<span data-type="title" id=toc-557>٧٧ - العجلى</span>
هو أبو الحسن، أحمد بن عبد الله بن صالح، العجلى، ولد سنة ١٨١ هـ/ ٧٩٧ م،','1','277',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (269,'فى الكوفة، وعاش بها وبالبصرة وببغداد، ثم ترك العراق وقت محنة خلق القرآن، واستقر فى طرابلس الغرب، وكان ذا قدر جليل فى المغرب مثل أحمد بن حنبل، ويحيى بن معين، وتوفى فى طرابلس ٢٦١ هـ/ ٨٧٥ م.

<span data-type="title" id=toc-558><span data-type="title" id=toc-561>أ- مصادر ترجمته:</span></span>
تاريخ بغداد للخطيب ٤/ ٢١٤ - ٢١٥، تذكرة الحفاظ للذهبى ٥٦٠ - ٥٦١، مرآة الجنان لليافعى ٢/ ١٧٣، شذرات الذهب لابن العماد ٢/ ١٤١، كشف الظنون لحاجى خليفة ٥٨٢، انظر ما ورد فى كتاب روزنتال: علم التاريخ عند المسلمين. Rosenthal ,History ٣٦٠ ومعجم المؤلفين لكحالة ١/ ٢٩٤.

<span data-type="title" id=toc-559>ب- آثاره:</span>
«الثّقات» بترتيب أبى الحسن على بن أبى بكر بن سليمان الهيثمى (المتوفى ٨٠٧ هـ/ ١٤٠٤ م انظر بروكلمان ملحق ٢/ ٨١) شهيد على ٢٧٤٧/ ١ (من ١ - ٦٧ ب، ٨٠٩ هـ).
وهناك مقتسات- من الأصل- فى الإصابة لابن حجر ١/ ٤٠٤، ٥٩٥، ٨٧١، ١٠٧١، ٢/ ٨٣٠، ١١١٨؛ ٣/ ٤٦، ١٤٩، ١٨٥، ١٩٣، ١٩٤، ٢٣٣، ٢٥٦، ٣٠٤، ٥٧٥، ١٢٧٦؛ ٤/ ٢٦٨.

<span data-type="title" id=toc-560>٧٨ - يعقوب بن شيبة</span>
هو أبو يوسف، يعقوب بن شيبة بن الصّلت بن عصفور، السّدوسى، ولد قرابة سنة ١٨٠ هـ/ ٧٩٦ م فى البصرة، سكن بغداد وسامرّاء، كان محدثا ثقة، وفقيها على مذهب مالك. وتوفى فى بغداد سنة ٢٦٢ هـ/ ٨٧٥ م.
أ- مصادر ترجمته:
تاريخ بغداد للخطيب ١٤/ ٢٨١ - ٢٨٣، تذكرة الحفاظ للذهبى ٥٧٧ - ٥٧٨، الدول للذهبى ١/ ١١٦، الديباج لابن فرحون ٣٥٥، البداية والنهاية لابن كثير ١١/ ٣٥، النجوم الزاهرة لابن تغرى بردى ٣/ ٣٧، شذرات الذهب لابن العماد ٢/ ١٤٦ الأعلام للزركلى ٩/ ٢٦١ معجم المؤلفين لكحالة ١٣/ ٢٥٠.','1','278',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (270,'<span data-type="title" id=toc-562><span data-type="title" id=toc-565>ب- آثاره:</span></span>
المسند الكبير المعلل ويرى الذهبى أنه أفضل مسند، ولم يصل إلينا منه إلا الجزء العاشر بعنوان «مسند أمير المؤمنين عمر ابن الخطاب»، وذلك بالمكتبة الخاصة بسامى حدّاد فى بيروت (٢٥ ورقة) ونسخة مصورة بالقاهرة، ملحق ٣/ ٦٠ - ٦١ (تحت رقم ١٩٠٦٠ ب)، وطبع فى بيروت ١٩٤٠ م.

<span data-type="title" id=toc-563>٧٩ - بشر بن مطر</span>
هو أبو أحمد بشر بن مطر بن ثابت الدقاق الواسطى، عاش فى سامراء، وكان محدثا ثقة، وروى عن سفيان بن عيينة، ويزيد بن هارون وغيرهما، وتوفى سنة ٢٦٢ هـ/ ٨٧٥ م.

<span data-type="title" id=toc-564><span data-type="title" id=toc-567>أ- مصادر ترجمته:</span></span>
الجرح والتعديل لابن أبى حاتم ١/ ١/ ٣٦٨، لسان الميزان لابن حجر ٢/ ٣٣.
ب- آثاره:
«حديث»، الظاهرية، مجموع ٩٤ (القسم الثالث، من ٨٧ أ- ٩٤ ب سنة ٧٠٢ هـ).

<span data-type="title" id=toc-566>٨٠ - ابن عاصم</span>
هو أبو جعفر، محمد بن عاصم الثقفى، الأصفهاني، روى عن سفيان بن عيينة، وأطلق عليه «مسند أصفهان»، وتوفى سنة ٢٦٢ هـ/ ٨٧٦ م.
أ- مصادر ترجمته:
أخبار أصبهان لأبى نعيم ٢/ ١٨٩، الوافى بالوفيات للصفدى ٣/ ١٨٠، تذكرة الحفاظ للذهبى ٥١٧، التهذيب لابن حجر ٩/ ٢٤٠ - ٢٤١، معجم المؤلفين لكحالة ١٠/ ١١٥.','1','279',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (271,'<span data-type="title" id=toc-568><span data-type="title" id=toc-571>ب- آثاره:</span></span>
١ - الجزء العالى (وكان مشهورا معروفا) انظر: الذهبى، وابن حجر فى المراجع السابقة): الظاهرية مجموع ٨٥/ ١٣ (من ١٩٦ أ- ٢٠٣ ب، فى القرن السابع الهجرى).
٢ - أحاديث: الظاهرية، مجموع ٦٨ (من ١٤٧ أ- ١٦٠ أ، فى القرن الخامس الهجرى، مع أحاديث أحمد بن عصام).

<span data-type="title" id=toc-569>٨١ - سعدان بن نصر</span>
هو أبو عثمان، سعدان بن نصر بن منصور، الثقفى البغدادى البزاز، سمع سفيان ابن عيينة، وكان يسمى «بمسند بغداد»، وتوفى سنة ٢٦٥ هـ/ ٨٧٨ م.

<span data-type="title" id=toc-570><span data-type="title" id=toc-573>أ- مصادر ترجمته:</span></span>
تاريخ بغداد للخطيب ٩/ ٢٠٥ - ٢٠٦، المنتظم لابن الجوزى ٥/ ٢/ ٥١، تذكرة الحفاظ للذهبى ٥٦٥، الدول للذهبى ١١٦، شذرات الذهب لابن العماد ٢/ ١٤٩.
ب- آثاره:
الحديث: الظاهرية، عام ٤٥٥٦ (٢٣ ورقة، ٣٩٨ هـ)، كوبريلى ١٥٨٤ (القسم الأول من ١٣ أ- ٢٤ أ، سنة ٦٥٨ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٧٩).

<span data-type="title" id=toc-572>٨٢ - المخرّمى</span>
هو أبو محمد عبد الله بن محمد بن أيوب المخرّمى، ولد حوالى سنة ١٧٥ هـ/ ٧٩١ م، سمع سفيان بن عيينة وغيره، كان من المحدثين الثقات، وتوفى سنة ٢٦٥ هـ/ ٨٧٨ م/.
أ- مصادر ترجمته:
تاريخ بغداد للخطيب ١٠/ ٨١ - ٨٢، المنتظم للجوزى ٥/ ٢/ ٥٢، تذكرة الحفاظ للذهبى ٥٦٥.','1','280',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (272,'<span data-type="title" id=toc-574><span data-type="title" id=toc-577>ب- آثاره:</span></span>
«حديث» برواية إسماعيل الصفار، الظاهرية مجموع ٨١/ ٩ (من ١١٣ أ ١٢٧ ب سنة ٥٧٣ هـ).

<span data-type="title" id=toc-575>٨٣ - ابن حرب</span>
هو أبو الحسن على بن حرب بن محمد الطّائى الموصلى، ولد سنة ١٧٠ هـ/ ٧٨٦ م، وكان محدثا عارفا بالتاريخ العربى القديم، كما كان شاعرا. روى عن سفيان بن عيينة، ووكيع بن الجراح وغيرهما؛ وروى عنه النّسائى وغيره، ويعتبر من الثقات. وتوفى سنة ٢٦٥ هـ/ ٨٧٩ م.

<span data-type="title" id=toc-576>أ- مصادر ترجمته:</span>
الجرح والتعديل لابن أبى حاتم ٣/ ١/ ١٨٣، تاريخ بغداد للخطيب ١١/ ٤١٨ - ٤٢٠ المنتظم للجوزى ٥/ ٢/ ٥٢، تذكرة الحفاظ للذهبى ٥٦٥، التهذيب لابن حجر ٧/ ٢٩٤ - ٢٩٦، شذرات الذهب لابن العماد ٢/ ١٥٠، الأعلام للزركلى ٥/ ٧٨، معجم المؤلفين لكحالة ٧/ ٥٧.
ب- آثاره:
«حديث»: الظاهرية، مجموع ٦٧/ ٥، ٧٣ (من ٧٥ أ- ٨٢ ب، فى القرن السادس الهجرى).

<span data-type="title" id=toc-578>٨٤ - ابن ملّاس</span>
هو محمد بن هشام بن ملّاس الفزارى، كتب حوالى ٢٦٦ هـ/ ٨٧٩ م:
«السّباعيّات»، شهيد على ٥٣٩/ ٣ (من ٢٤ أ- ٢٥ ب، سنة ٥٩٦).

<span data-type="title" id=toc-579>٨٥ - أبو زرعة الرازى</span>
هو أبو زرعة، عبيد الله بن عبد الكريم بن يزيد، الرازى، ولد سنة','1','281',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (273,'٢٠٠ هـ/ ٨١٥ م فى الرى، قدم بغداد وجالس أحمد بن حنبل وذاكره. ويعدّ أبو زرعة وصديقه أبو حاتم الرازى من أشهر المدققين فى الحديث، وخليفته الحقيقى هو ابن أبى حاتم الرازى (المتوفى سنة ٣٢٧ هـ/ ٩٣٩ م)، وتوفى أبو زرعة فى الرى سنة ٢٦٤ هـ/ ٨٧٨ م.

<span data-type="title" id=toc-580><span data-type="title" id=toc-583>أ- مصادر ترجمته:</span></span>
التقدمة لابن أبى حاتم ٣٢٨ - ٤٩، تاريخ بغداد للخطيب ١٠/ ٣٢٦ - ٣٣٧، طبقات الحنابلة لابن أبى يعلى ١/ ١٩٩ - ٢٠٣، التهذيب لابن حجر ٧/ ٣٠ - ذ؟ + الأعلام للزركلى ٤/ ٣٥٠.

<span data-type="title" id=toc-581><span data-type="title" id=toc-584>ب- آثاره:</span></span>
«كتاب الزهد» وتوجد بقية منه فى الإصابة لابن حجر ٤/ ١٥٣.

<span data-type="title" id=toc-582>٨٦ - التّرقّفى</span>
هو أبو محمد العباس بن عبد الله بن أبى عيسى، الباكسائي ويعرف بالتّرقّفى.
سكن بغداد ودمشق، وكان ثقة، وتوفى سنة ٢٦٧ هـ/ ٨٨٠ م.
أ- مصادر ترجمته:
تاريخ بغداد للخطيب ١٢/ ١٤٣ - ١٤٤، التهذيب لابن عساكر ٧/ ٢٢٥، المنتظم للجوزى ٥/ ٢/ ٦١، معجم البلدان لياقوت ١/ ٨٣٨، اللباب لابن الأثير ١/ ١٧٣، تذكرة الحفاظ للذهبى ٥٦٦.
ب- آثاره:
«جزء»: الظاهرية، مجموع ٩٣/ ٣ (من ٤٠ ب- ٥٦ أ، سنة ٥٧٦ هـ).

<span data-type="title" id=toc-585>٨٧ - سمّويه</span>
هو أبو بشر، إسماعيل بن عبد الله بن مسعود، العبدى سمويه، أصله من','1','282',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (274,'أصفهان، سمع فى رحلاته الطويلة الكثيرة من عدد من/ العلماء منهم أحمد بن حنبل. كان محدّثا ثقة وعالما بالفقه، وتوفى سنة ٢٦٧ هـ/ ٨٨٠ م.

<span data-type="title" id=toc-586><span data-type="title" id=toc-589>أ- مصادر ترجمته:</span></span>
أخبار أصبهان لأبى نعيم ١/ ٢١٠ - ٢١١، التهذيب لابن عساكر ٣/ ٢٤، اللباب لابن الأثير ١/ ٥٦٦، الأعلام للزركلى ١/ ٣١٤، معجم المؤلفين لكحالة ٢/ ٢٧٨.

<span data-type="title" id=toc-587><span data-type="title" id=toc-590>ب- آثاره:</span></span>
«الفوائد»: الظاهرية، مجموع ١١٤ (من ٣٤ أ- ٤٤ أ، فى القرن السابع الهجرى).

<span data-type="title" id=toc-588>٨٨ - زكرويه</span>
هو أبو يحيى، زكريا بن يحيى بن أسد، المروزى، المعروف بزكرويه، عاش فى بغداد، وروى عن سفيان بن عيينة، ومعروف الكرخى وغيرهما. وتوفى سنة ٢٧٠ هـ/ ٨٨٣ م.
أ- مصادر ترجمته:
تاريخ بغداد للخطيب ٨/ ٤٦٠ - ٤٦١، المنتظم للجوزى ٥/ ٢/ ٧٧، شذرات الذهب لابن العماد ٢/ ١٦٠.
ب- آثاره:
«حديث»: الظاهرية، مجموع ٨١ (من ١١٣ أ- ١٢٨ ب، سنة ٥٧٣ هـ- مع حديث ابن أيوب المخرّمى).

<span data-type="title" id=toc-591>٨٩ - ابن عفّان العامرى</span>
هو أبو محمد، الحسن بن على بن عفان العامرى الكوفى، كان يعد من المحدثين','1','283',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (275,'الثقات. روى عنه ابن ماجه، وابن أبى حاتم وغيرهما، وتوفى سنة ٢٧٠ هـ/ ٨٨٣ م.

<span data-type="title" id=toc-592><span data-type="title" id=toc-595>أ- مصادر ترجمته:</span></span>
الجرح والتعديل لابن أبى حاتم ١/ ٢/ ٢٢، تذكرة الحفاظ للذهبى ٥٧٣، التهذيب لابن حجر ٢/ ٣٠١ - ٣٠٢.

<span data-type="title" id=toc-593><span data-type="title" id=toc-596>ب- آثاره:</span></span>
«الأمالى والقراءة» شهيد على ٥٤٦/ ٦ (من ٨٨ أ- ٩١ ب، فى القرن الثامن الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٦٢)، القاهرة ثان ١/ ١٠٧ حديث ١٥٥٨ (انظر كذلك: القاهرة، ملحق ١: ٢١٤ حول أحاديث أبى جعفر محمد بن على بن عفان العامرى، وإبراهيم بن إسحاق بن أبى العنبس).

<span data-type="title" id=toc-594>٩٠ - القزّاز</span>
هو أبو بكر محمد بن سنان بن الذّيّال، بن خالد، القزاز البصرى عاش فى بغداد، كان من الثقات، وتوفى سنة ٢٧١ هـ/ ٨٨٤ م.
أ- مصادر ترجمته:
الجرح والتعديل لابن أبى حاتم ٣/ ٢/ ٢٧٩، تاريخ بغداد للخطيب ٥/ ٣٤٣ - ٣٤٦، تذكرة الحفاظ للذهبى ٥٧٩، التهذيب لابن حجر ٩/ ٢٠٦، كشف الظنون لحاجى خليفة ٥٨٩، هدية العارفين ٢/ ١٨، معجم المؤلفين لكحالة ١٠/ ٥٧.
ب- آثاره:
«حديث»:
الظاهرية، مجموع ١٨/ ١ (من ١ أ- ٤ أ، فى القرن السابع الهجرى)، كذلك مجموع ٣١ (من ١٦٣ أ ١٦٧ ب، فى القرن السادس الهجرى)، القاهرة، دارالكتب، حديث ١٥٥٨ (انظر ٢٠٢ - ٢١٢، والصفحات ٢٦٩ - ٢٧٥، فى القرن الثامن الهجرى).','1','284',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (276,'<span data-type="title" id=toc-597>٩١ - العطاردى</span>
هو أبو عمر أحمد بن عبد الجبار بن محمد العطاردى الكوفى، ولد سنة ١٧٧ هـ/ ٧٩٤ م فى الكوفة، وروى مغازى ابن إسحاق عن روايته يونس بن بكير، والآراء مختلفة حول الثقة به، وتوفى سنة ٢٧٢ هـ/ ٨٨٦ م فى الكوفة.

<span data-type="title" id=toc-598><span data-type="title" id=toc-601>أ- مصادر ترجمته:</span></span>
الجرح والتعديل لابن أبى حاتم ١/ ١/ ٦٢، تاريخ بغداد ٤/ ٢٦٢ - ٢٦٥، التهذيب لابن حجر ١/ ٥١ - ٥٢، شذرات الذهب لابن العماد ٢/ ١٦٢، الأعلام للزركلى ١/ ١٤٠.

<span data-type="title" id=toc-599>ب- آثاره:</span>
«حديث»: الظاهرية، مجموع ٧٥/ ٩.

<span data-type="title" id=toc-600>٩٢ - ابن ماجه</span>
هو أبو عبد الله، محمد بن يزيد القزوينى بن ماجه، ولد سنة ٢٠٩ هـ/ ٨٢٤ م.
وسمع فى رحلاته العلمية فى العراق والشام ومصر ومكة من عدد كبير من الشيوخ، ويعتبر من أكبر المحدثين الثقات. وقد عرفته الأجيال التالية مؤلفا لأحد كتب السنن الجامعة، ويبدو أنه لم يكن ثمة إجماع على الاعتراف بمكانته، فكتابه يضم كثيرا من الأحاديث الضعيفة، وفوق هذا فقد انتقى مادته من عدد قليل من المصادر. ويبدو أن مصنف أبى بكر بن أبى شيبة كان أساس عمله (انظر ما كتبه عنه سزكين فى كتابه عن مصادر البخارى ص، Sezgin ,Buh.Kayn.٨١ ٨١ وتوفى سنة ٢٧٣/ ٨٨٦ م.
أ- مصادر ترجمته:
المنتظم للجوزى ٥/ ٢/ ٩٠، وفيات الأعيان لابن خلكان ١/ ٦١٣، تذكرة الحفاظ للذهبى ٦٣٦ -','1','285',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (277,'٦٣٧، الدول للذهبى ١/ ١٣٠، التهذيب لابن حجر ٩/ ٥٣٠ - ٥٣٢، البداية والنهاية لابن كثير ١١/ ٥٢، النجوم الزاهرة لابن تغرى بردى ٣/ ٧٠، مرآة الجنان لليافعى ٢/ ١٨٨، شذرات الذهب لابن العماد ٢/ ١٦٤، الأعلام للزركلى ٨/ ١٥، معجم المؤلفين لكحالة ١٢/ ١١٥ - ١١٦، كتب عنه وستنفلد فى كتابه عن المؤرخين العرب Wustenfeld ,Geschichts.٧١.: كتب عنه جولد تسيهر فى كتابه فى الدراسات الإسلامية:
Goldziher, Muh. Stud. II, ٢٦٢ - ٢٦٣.
(وانظر بروكلمان ١/ ١٦٣ تحت رقم ٤).

<span data-type="title" id=toc-602>ب- آثاره:</span>
«السّنن» حول رواية الكتاب، انظر ما كتبه روبسون:
J. Robson, The Transmissionof Ibn Maga''s" Sunan", Journalof Semitic Studies, III ١٩٥٨/ ١٢٩ - ١٤١
المخطوطات: المتحف البريطانى ١٥٦٤، الإضافات ٢٧٥١٣ (٣٠٥ ورقة، ٧٣٠ هـ)، الجزائر ٤٩٢ (١٦١ ورقة، ١١٩٦ هـ)، ٤٩٣ (٣٤٢ ورقة، ١١٩٦ هـ)، راغب ٢٥٩ (٢٧٨ ورقة، ١١٦٣ هـ)، ٢٦٠ (٢٣٤ ورقة، ١١٦٩ هـ)، نور عثمانية ٨١٢ (٢٥٤ ورقة، ١١٦٢ هـ)، ٨١٥ (٢٣٢ ورقة، ١١٦٢ هـ)، ٨١٦ (٢٣٠ ورقة، ١١٠٠ هـ)، ٨١٧ (٢٥١ ورقة)، ٨١٨ (٣٤٨ ورقة ١١٦٤ هـ)، ٨١٩ (١٩٧ ورقة)، ٨٢٠ (٣٠٠ ورقة، ١١٥٤ هـ) (٣١٠)، أيا صوفية ٥٤٢ (١٧٩ ورقة ١١٣٧ هـ)، ٥٤٣ (٤٠٢ ورقة، ١١٤٣ هـ)، ٥٤٤ (٢٤٥ ورقة، ١١٣٣ هـ)، كوبريلى ٢٩٣ (٢٢٠ ورقة، ٧٩٠ هـ)، داماد إبراهيم ٤٠٠ (١٣٠ ورقة ٨٤٥ هـ) ٤٠١ (١٦٣ ورقة، ٧٦٥ هـ) السليمانية ٣١٤ (٢٧٥ ورقة)، ٣١٥ (٣٠٦ ورقة، ١١٤١ هـ)، سليم أغا ١٦٤ (٢٣٨ ورقة)، بنكيبور ٥/ ١/ ١٢٧، رقم ٢٢٠ (٣٥٠ ورقة ١٢٦٢ هـ)، الظاهرية، حديث ٢١٤، ٢٢٠، رامبور ١/ ٨٦ حديث ١٥٨ (١٢٩١)، «قوله ١/ ١٢٢»، شهيد على ٤١٩ (٢/ ٢٣٨ ورقة، ٨٦٥ هـ)، لاله لي ٤٥٦ (١٩٦ ورقة، فىالقرن الثانى عشر الهجرى)، رئيس الكتاب ١٤٢ (٣١٨ ورقة، فى القرن الثانى عشر الهجرى) ١٤٣ (٣٠٠ ورقة، ١١٥٨ هـ)، حاجى محمود ٥٩٧ (٢٣٠ ورقة، ١١٨٠ هـ)، طرخان ٥٠ (٤٥٨ ورقة، فى القرن الثانى عشر الهجرى)، فيض الله ٣٢٦ (٢٩٢ ورقة، ١٠٠٤ هـ)، ٣٢٧ (من ٨ - ١٦، ١٥٥ ورقة، ٥٦١ هـ)، ٣٢٨ (٢١٠ ورقة، ٦٠٠ هـ)، ٣٢٩ (٢٠٠ ورقة، ٨٢٤ هـ)، جار الله ٢٩٠ (٣٦٢ ورقة، ٦٠١ هـ)، حكيم ١٨٥ (٣٤٩ ورقة، ٧٩٠ هـ)، ١٨٦ (١٦٩ ورقة، ١١١٩ هـ)، ١٨٧، فاتح ٧٦١ (٢٨٥ ورقة، فى القرن الثامن الهجرى)، ٧٦٢ (٢٩٤
_________
(٣١٠) الأرقام ٨١١، ٨١٣، ٨١٤ قد ضاعت.','1','286',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (278,'ورقة، ١١٣٥ هـ)، ٧٦٤ (٢٣٥ ورقة، ٦٢٣ هـ)، عاطف ٤٤٤ (٢٦٢ ورقة، ١١٦٥ هـ)، مراد ملا ٤٠٠ (٢٩٤ ورقة، ٦٢٤ هـ)، ٤٠١ (٢٥٧ ورقة، ١١٦٣ هـ)، ولى الدين ٥٨٠ (٣١١ ورقة، ١١٦٠ هـ)، ٥٨١ (٤١٠ ورقة، ١١١٥ هـ)، مكتبة جامعة إستنبول ٤٩٦ (٣٢٤ ورقة، ١١٦٥ هـ)، ٥١٠٠ (٢٣٩ ورقة، ١٢٥١ هـ)، باريس ٧٠٦ (٣٠٣ ورقة، ٧٣٠ هـ، انظر: فايدا ٦٢٧، وأيضا ما كتبه فايدا عن الإجازات:
Certificat ٤
)، منجانا ٣٣٥ (١٧٨ ورقة، ٩١٣ هـ، انظر: فهرس ١٢٦)، تيمور ٢/ ١٦١، حديث ٥٢٢ (مجلدان، فى القرن السادس الهجرى)، الأزهر ١/ ٥٠٩، حديث ١٩٧ (٣٩٣ ورقة، ١١٩٤ هـ)، ٢٩٤ (٣٢٨ ورقة، ١١٩٤ هـ)، القاهرة، ملحق ١/ ٤٦٦ رقم ٢٢٩٥٧ ب (٢٥٦ ورقة، ١٢٨٥ هـ)، بلدية الإسكندرية ٣٧٣ ج، ١٠٩١ ب، ١٢٢٠ ب (٨٤٧ هـ)، الأوقاف ببغداد ٢٨٢٨ - ٢٨٢٩ (٨٣٤ هـ، انظر: طلس، رقم ٣٥٨)، الكتانى بالرباط ٤١٨ (٢٦٣ ورقة). وطبع فى دهلى/ ١٢٣٣ هـ، ١٢٧٣ هـ، ١٨٨٩ م، ١٩٠٥ م، وفى لاهور ١٣١١ هـ، وفى القاهرة ١٣١٣ هـ، وبتحقيق محمد فؤاد عبد الباقى سنة ١٩٥٢ م، ١٩٥٣ م، انظر ما كتبه باريت:
R. Paret, Oriens ٨/ ١٩٥٥/ ١٨٨

<span data-type="title" id=toc-603>شروح كتاب السنن:</span>
أ- «الإعلام بسنته عليه السلام» (غير كامل)، تأليف علاء الدين مغلطاى بن قلج (المتوفى ٧٦٢ هـ/ ١٣٦١ م انظر بروكلمان ٢/ ٤٨) القاهرة، ثان ١/ ٩٠ (٣١١)، حديث ٢٧٥ (٣ مجلدات، نسخة عن الأصل)، بنكيبور ٥/ ١/ ١٢٨ رقم ٢٢١ (المجلد الثانى، ١٨٩، ورقة/ ٧٣٩ هـ، بها ملاحظات بخط المؤلف)، «قوله ١/ ١٢٢»، فيض الله ٣٦٢ (المجلد الثانى، ٢١٩ ورقة، ٧٣٧ هـ، بخط المؤلف).
ب- «مصباح الزجاجة على سنن ابن ماجه» لجلال الدين السيوطى (المتوفى ٩١١ هـ/ ١٥٠٥ م)، الظاهرية، حديث ٢٣٦ (٨٤ ورقة، فى القرن العاشر الهجرى)، بنكيبور ٥/ ١/ ١٣٠ رقم ٢٢٢ (٤٩ ورقة، ١١١٦ هـ)، رامبور ١/ ١١٥، حديث ٣٨١ (١٢٧٥ هـ)، القاهرة، ثان ١/ ١٤٨، حديث ١١ ش، طبع على هامش طبعة دهلى ١٢٨٢ هـ. وله مختصر بعنوان: «نور المصباح» لعلى بن سعيد الدمنتى البجمعوي (المتوفى ١٣٠٦ هـ/ ١٨٨٩ م انظر بروكلمان ملحق ٢/ ٧٣٧) وطبع بالقاهرة ١٢٩٩ هـ.
ج- «كفاية الحاجة فى شرح ابن ماجه» لأبى الحسن بن عبد الهادى السّندى (المتوفى ١١٣٦ هـ/ ١٧٢٤ م، انظر: الجبرتى ١/ ٨٥)، أدرنة- السليمية ٧٦٠ (١١٣٣ هـ، ويحتمل أن تكون بخط المؤلف)، وبعنوان آخر هو «حاشية السندى» القاهرة ثان ١/ ١١١، حديث ٢٨٠، طبع فى القاهرة ١٣١٣ هـ.
_________
(٣١١) وليس ٢٦٩ كما ذكر بروكلمان','1','287',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (279,'د- «رفع العجاجة» مع ترجمة هندوستانية، تأليف مولوى وحيد الزمان، القاهرة ١٣١٣ هـ.
هـ- «إنجاح الحاجة» تأليف عبد الغنى الدّهلوى. طبع فى دهلى ١٢٨٢ هـ.
و- «مفتاح الحاجة» تأليف محمد بن عبد الله بنجابى حزاروى، لكنو ١٣١٥ هـ.
ز- «ما تدعو إليه الحاجة على سنن ابن ماجه» تأليف شمس الدين أبى الرضا محمد بن حسن الزّبيدى الشافعى (كتب حوالى سنة ٩١٣ هـ/ ١٥٠٧ م). القاهرة، دار الكتب، حديث ٢٤٢٤ (المجلد الثالث، ٣٦٠ ورقة، ٩١٣ هـ، بخط المؤلف، انظر: فهرس معهد المخطوطات العربية ١/ ٩٤).
ح- «مصباح الزجاجة فى زوائد ابن ماجه» تأليف أحمد بن أبى بكر بن إسماعيل الكنانى البوصيرى (المتوفى ٨٤٠ هـ/ ١٤٣٦ م انظر بروكلمان ٢/ ٦٧) القاهرة، ثان ١/ ١٤٨، حديث ٤٤٢، وعليه حواش مجهولة المؤلف، فى: حفيد أفندى ٣٥ (٢٦٨ ورقة، فى القرن الثانى عشر الهجرى).
ط- حول أحاديثه الموجودة فى مجموعات الحديث الخمس الأخرى:
«زوائد على الكتب الخمسة» لنور الدين بن حجر الهيثمى (المتوفى ٨٠٧ هـ/ ١٤٠٥ م انظر بروكلمان ٢/ ٧٦)، آصفية ١/ ٦٣٢ حديث ٤١٠.
ى- حول الرجال المذكورين فى السّنن:
«المجرد فى أسماء رجال كتاب سنن أبى عبد الله بن ماجه كلهم سوى من أخرج له منهم فى أحد الصحيحين» تأليف محمد بن أحمد بن عثمان الذهبى (المتوفى ٧٤٨ هـ/ ١٣٤٨ م انظر بروكلمان ٢/ ٤٦)، الظاهرية، حديث ٥٣١ (٢٠ ورقة، بخط المؤلف، انظر العش ٢١٤).
ل- «ثلاثيات»:
الظاهرية، مجموع ٥٩ (من ٣٨ أ- ٤٠ ب، ٧٣٤ هـ).

<span data-type="title" id=toc-604>٩٣ - أبو أمية</span>
هو أبو أمية، محمد بن إبراهيم بن مسلم، الخزاعى الطرسوسى، أصله من بغداد ثم سكن طرسوس، وقد ولد حوالى سنة ١٨٠ هـ/ ٧٩٦ م وروى عن أبى داود الطيالسى وغيره، ويعتبر- بصفة عامة- محدثا ثقة، وكان ابن حبّان لا يراه أهلا للثقة، لأنه روى فى رحلته إلى مصر دون كتب، فوقع لذلك فى عدة أخطاء (انظر:
التهذيب لابن حجر ٩/ ١٥ - ١٦)، وتوفى فى طرسوس سنة ٢٧٣ هـ/ ٨٨٦ م.','1','288',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (280,'<span data-type="title" id=toc-605><span data-type="title" id=toc-608>أ- مصادر ترجمته:</span></span>
الجرح والتعديل لابن أبى حاتم ٣/ ٢/ ١٨٧، تاريخ بغداد للخطيب ١/ ٣٩٤ - ٣٩٦، المنتظم للجوزى ٥/ ٩٠ - ٩١، تذكرة الحفاظ للذهبى ٥٨١، شذرات الذهب لابن العماد ٢/ ١٦٤، الأعلام للزركلى ٦/ ١٨٣، معجم المؤلفين لكحالة ٨/ ٢١٩/.

<span data-type="title" id=toc-606><span data-type="title" id=toc-609>ب- آثاره:</span></span>
«جزء من المسند» الظاهرية، مجموع ١٠١/ ١٤ (من ١٩٣ أ- ٢٠٤ ب، فى القرن السادس الهجرى).

<span data-type="title" id=toc-607>٩٤ - أحمد بن ملاعب</span>
هو أبو الفضل، أحمد بن ملاعب بن حيان، المخرّمى، ولد فى بغداد سنة ١٩١ هـ/ ٨٠٦ م، وروى عن الفضل بن دكين، وعفّان بن مسلم وغيرهما. كان من الثقات، وتوفى فى بغداد سنة ٢٧٥ هـ/ ٨٨٨ م.
أ- مصادر ترجمته:
تاريخ بغداد للخطيب ٥/ ١٦٨ - ١٦٩، تذكرة الحفاظ للذهبى ٥٩٥.
ب- آثاره:
«حديث»: الظاهرية، مجموع ٢/ ٥ (من ٤٥ أ- ٥٠ ب، ٥٦٥ هـ).

<span data-type="title" id=toc-610>٩٥ - ابن أبى غرزة</span>
هو أبو عمرو أحمد بن حازم بن أبى غرزة الغفارى، الكوفى. كان محدثا ثقة، توفى سنة ٢٧٥ هـ/ ٨٨٨ م.','1','289',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (281,'<span data-type="title" id=toc-611><span data-type="title" id=toc-614>أ- مصادر ترجمته:</span></span>
تذكرة الحفاظ للذهبى ٥٩٤ - ٥٩٥، الدول للذهبى ١/ ١٢٢، البداية والنهاية لابن كثير ١١/ ٥٦، شذرات الذهب لابن العماد ٢/ ١٦٨ - ١٦٩، الأعلام للزركلى ١/ ١٠٤، معجم المؤلفين لكحالة ١/ ١٨٦.

<span data-type="title" id=toc-612>ب- آثاره فى:</span>
«مسند عابس الغفارى وجماعة من الصحابة»:
الظاهرية، مجموع ٨٠/ ١٤ (من ١٦٢ أ- ١٧١ ب، فى القرن السادس الهجرى).

<span data-type="title" id=toc-613>٩٦ - أبو داود</span>
هو أبو داود سليمان بن الأشعث بن إسحاق الأزدى السجستانى، ولد سنة ٢٠٢ هـ/ ٨١٧ م، وأصله من سجستانى، لا من القرية التى تحمل هذا الاسم بالقرب من البصرة (انظر: تذكرة الحفاظ للذهبى ٥٩١). ويبدو أنه بدأ رحلاته العلمية فى وقت مبكر، فقد كان سنة ٢٢٠ هـ فى بغداد (انظر: المصدر السابق). وكان تلميذا لأحمد ابن حنبل، ولكنه سمع فى رحلاته الطويلة عددا من المحدثين المبرزين. روى عنه الترمذى، والنّسائى، وأبو عوانة، وابنه أبو بكر بن أبى داود، وأستاذه أحمد بن حنبل، وبعد أن أقام فى عدد من المدن استقر فى البصرة استجابة للخليفة الموفّق، وتوفى بها سنة ٢٧٥ هـ/ ٨٨٨ م.
أ- مصادر ترجمته:
الجرح والتعديل لابن أبى حاتم ٢/ ١/ ١٠١ - ١٠٢، تاريخ بغداد للخطيب ٩/ ٥٥ - ٥٩، طبقات الحنابلة لابن أبى يعلى ١/ ١٥٩ - ١٦٢، وفيات الأعيان لابن خلكان (بولاق) ١/ ٢٦٨ - ٢٦٩، تهذيب ابن عساكر ٦/ ٢٤٤ - ٢٤٦، المنتظم لابن الجوزى ٥/ ٩٧ - ٩٨، اللباب لابن الأثير ١/ ٥٣٣، الدول للذهبى ١/ ١٢٢، التهذيب لابن حجر ٥/ ١٦٩ - ١٧٣، طبقات الشافعية للسبكى ٢/ ٤٨، البداية والنهاية لابن كثير ١١/ ٥٤ - ٥٦، مرآة الجنان لليافعى ٢/ ١٨٩ - ١٩٠، شذرات الذهب لابن العماد ٢/ ١٦٧، الأعلام للزركلى ٣/ ١٨٢، معجم المؤلفين لكحالة ٤/ ٢٥٥، بروكلمان ١/ ١٦١.','1','290',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (282,'كتب عنه جولد تسيهر فى كتابه فى الدراسات الإسلامية:
I. Goldziher, Muh. Stud. II, ٢٥٠, ٢٥٥ - ٢٥٦.
كتب عنه وستنفلد فى كتابه عن الشافعية Wustenfeld ,Schaf.٤٧.: كتب عنه وليام مارساى فى دائرة المعارف الإسلامية- الطبعة الأوربية الأولى:
W. Marcais, EI, ١, ٨٧ - ٨٨.
وانظر مقالة روبسون فى دائرة المعارف الإسلامية الطبعة الأوربية الثانية:
J. Robson, EI, ١, ١١٤.

<span data-type="title" id=toc-615>ب- آثاره:</span>
<span data-type="title" id=toc-616>أولا: «كتاب السنن»</span>
استقى المصنف مادته من مصادر تضم نصف مليون حديث، وذلك ليحفظ سنة النبى. ويرى البعض أن كتاب أبى داود مما لا يستغنى عنه، ولكنه يأتى- بصفة عامة- بعد كتب البخارى/، ومسلم من ناحية القيمة العلمية- حول رواياته انظر ما كتبه روبسون:
G. Robson, The Transmissionof Abu D''s" Sunan", BSOASXIB, ١٩٥٢, ٥٧٩ - ٥٨٨
المخطوطات: برلين ١٢٤٦ (المجلد الأول ١٨١ ورقة، ٧٨٦ هـ)، ١٢٤٧ (قسم واحد، ٢٠ ورقة، ٤٧٧ هـ) ١٢٤٨ (٣ أقسام، ٧١ ورقة، حوالى ١١٠٠ هـ)، ميونيخ ١٢١ (الأقسام من ٢١ - ٢٦، ١٣٩ ورقة، ٥٢٠ هـ)، باريس ٧٠٧ (٢٦٢ ورقة فى القرن السادس الهجرى)، ٧٠٨ (مجلد واحد، ١٧٢ ورقة، ٦٠٤ هـ، انظر: فايدا ٦٢٧، وانظر ايضا ما كتبه عن دراسته عن الإجازات (Certificat ,١٠ ,١١، جامع ينى ٢٠٨ (٤٦٩ ورقة، ٧٧١ هـ)، أيا صوفية ٥٤٥، ٥٤٦، نورعثمانية ٨٢٢ (٣٨٠ ورقة، ١٠٦٥ هـ) ٨٢٣، ٨٢٤، (٢٥١ ورقة، فى القرن الثانى عشر الهجرى) كوبريلى ٢٩٤/ ١ (٣١٢) (١/ ٣٢٥ أ، ٨٠٠ هـ)، مراد ملا ٣٩٦ (٤٢٣ ورقة، ١١٣٣ هـ)، ٣٩٧ (٣٦٨ ورقة، القرن العاشر الهجرى)، سليم أغا ١٦٥ (٣٩٧ ورقة) (٣١٣) وهنك نسخ أخرى ذكرها بروكلمان فى الأصل والملحق، يضاف إليها: - شهيد على ٤٢٠ (ج ١، ٢٥١ ورقة، فى القرن السابع الهجرى)، ٤٢١ (ج ٢، ٢٠٦ ورقة فى القرن السابع الهجرى)، حكيم ١٨٨ (٤٢١ ورقة، ١١٥٥ هـ) ١٩٠ (٣٤٩ ورقة، فى القرن الثانى عشر الهجرى)، الفاتح ٧٦٣ (٣٧٨ ورقة، فى القرن الثانى عشر الهجرى)، جار الله ٢٠٢٥ (٨٤ ورقة، فى القرن السادس
_________
(٣١٢) ليس رقم ١٢٤ كما ذكر ذلك بروكلمان.
(٣١٣) الرقم ٢٧١ فى السليمانية يحذف لعدم وجوده.','1','291',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (283,'الهجرى)، حسن حسنى ٢٢٧ (٣٨٥ ورقة فى القرن الثانى عشر الهجرى)، الحميدية ٢٥٥ (٣٥١ ورقة، ١١٦٥ هـ)، ٢٥٦ (٣٥٧ ورقة، فى القرن الحادى عشر الهجرى)، خالد أفندى ٩١ (٣٥٩ ورقة، ١١٣٦ هـ)، مهرشاه ٥٧/ ١ (من ١ - ١٥٧ ب، ١١٧٨ هـ)، لاله لي ٤٥٧ (٣٨١ ورقة، فى القرن الثانى عشر الهجرى)، ٤٥٨ (١٦٨ ورقة، فى القرن الثامن الهجرى) ٤٥٩ (ج ١ - ٦، ٢٦٦ ورقة فى القرن الثامن الهجرى)، ٤٦٠ (ج ١، ٢٧٣ ورقة، ٧٤٦ هـ)، فيض الله ٣٣٠ (ج ١، ٢٣٢ ورقة، فى القرن التاسع الهجرى)، ٣٣١ (ج ٢، ١٧٦ ورقة، ٨٠٦ هـ)، ٣٣٣ (ج ١، ٢١١ ورقة، حوالى ٦٤٠ هـ)، ٣٣٣ (ج ٢، ١٨٠، ورقة ٦٥٤ هـ)، ٣٣٤ (ج ٣، ١٨١ ورقة، ٦٥٤ هـ)، ٣٣٥ (ج ٣، ١٨٧ ورقة ٦٥٠ هـ)، رئيس الكتاب ١٤٥/ ١ (الأوراق من ١ - ٣٦٣، ٤٩٢ هـ)، أيا صوفية ٦٨/ ٢ (الأوراق من ٢٨١ - ٥٢٥، ١٠٤٦ هـ)، مكتبة جامعة إستنبول ٥٠٩٨ (٣١٣ ورقة، ١٢٥٠ هـ)، عاطف ٤٤٥ (١:
٢٤١ ورقة، ٥٦٧ هـ)، أنقرة، صائب ١٢٠٧ (٥٢٠ هـ)، برلين ١٢١٠ (المجلد الرابع، فى القرن الرابع الهجرى)، الرباط ١٣٣٤ (٢٨٨ ورقة، ٢٥٨ ورقة، انظر: فهرس ١: ٦٣)، تشستربيتى، ٣١٢٣ (قسم واحد ١٥٦ ورقة، فى القرن الرابع الهجرى)، منجانا ٥٣٧ (١٢٩ ورقة، ١٠٧٠ هـ، انظر: فهرس رقم ١١٨)، تيمور ٢: ١٥٩، حديث ١٨٦، ٣٦٧، طلعت، حديث ٧٨٦، بلدية الإسكندرية ٣٥٨٠ ج (٦٧٣ هـ).
٣٥٨١ هـ (ج ١، ١٠٩٦ هـ)، الأوقاف ببغداد ٢٦٤٧ (انظر: طلس ص ٤٠)، عليكرة، سبحان ٢٩٧٦٢/ ٣٤ (١٦٢ ورقة، ١١٠٢ هـ).
طبع بالقاهرة سنة ١٢٨٠ هـ، وفى لكنو سنة ١٨٤٠ م، ١٨٧٧ م، ١٨٨٨ م، ١٣٠٥ هـ، ١٣١٨ م، وفى دهلى ١٢٧١ هـ، ١٢٧٢ هـ، ١٢٨٣ هـ، وفى حيدرآباد ١٣٢١ هـ.
وعلى هامش شرح الموطأ للزرقانى، فى القاهرة ١٣١٠ هـ، ١٣٢٠ هـ.

<span data-type="title" id=toc-617>شروح كتاب السنن:</span>
١ - «معالم السنن»: لأبى سليمان حمد بن محمد بن إبراهيم الخطّابى (المتوفى سنة ٣٨٨ هـ/ ٩٩٨ م، انظر: كذلك رقم ٢٥٢ فى علم الحديث)، فيض الله ٥٤٣ (المجلد الثانى، ١٦٦ ورقة، فى القرن الخامس الهجرى)، فاتح ٨١١ (٢٩٣ هـ، ٥٤٤ هـ)، لاله لي ٥٠٣ - ٥٠٥ (٣ مجلدات ٢٣٣ ورقة، ١٩١ ورقة، ٢٥٢ ورقة، ٥٥٧ هـ، القرن السادس الهجرى)، جامع ينى ٢٩٣ (٣٠٠ ورقة، ٥٦٢ هـ)، ٢٩٤ (١٥١ ورقة، ٨٧٦ هـ)، مراد ملا ٦٠٦ - ٦٠٧ وأرقامها الحالية من ٦١١ - ٦١٣ (ج ١، ١٩٤ ورقة، ٧٨١ هـ، ج ٢، ١٤٢ ورقة، فى القرن الثامن الهجرى، ج ٣، ٢٨٠ ورقة، ٦١٥ هـ)، سراى، أحمد الثالث ٤١٦ (٢٨٢ ورقة، ٧٧٤ هـ) ٤١٨ (٣١٦ ورقة، ١١٠٥ هـ)، أيا صوفية ٥٨٢ (ج ١، ٣٤٤ ورقة،','1','292',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (284,'١١٢٧ هـ)، ٥٨٣ (ج ٢٤٨ ورقة، ٦٧٤ هـ)، عاطف ٤٧١ (٣١٤) (ج ١، ١٩١ ورقة، ٨٨٧ هـ، انظر:
فايسفايلر ٥٥)، القاهرة، ثان ١، حديث ٧٩٥ (المجلد الثانى) حلب (ج ١، انظر: مجلة المجمع العلمى العربى بدمشق ٨/ ١٩٢٨ م/ ٣٦٩)، الجزائر ١٢٧٤/ ٢ (الأوراق ١٤٢ - ٢٧٧، فى القرن الثالث عشر الهجرى)، مشهد ٤/ ٨٧، ٢٦٦، جوتا ٢ (الأوراق ٦٤، الديباجة) (٣١٥)، فيض الله ٣٣٠ (ج ١، ٢٣٢ ورقة) ٣٣١ (ج ٢، ١٧٦ ورقة)، ٣٣٢ (ج ١: ٢١١ ورقة، ٦٥٤ هـ)، ٣٣٤ (ج ٣، ١٨١ ورقة)، ٣٣٥ (ج ٤، ١٨٧ ورقة، ٦٥٥ هـ)، سراى، أحمد الثالث ٤١٧ (ج ١، ٢٢١ ورقة، ٦٧٤ هـ، فهرس معهد المخطوطات العربية ١: ١٠٦)، السليمانية ٢٧١: (٣٢٩ ورقة، فى القرن العاشر الهجرى)، أيا صوفية ٦٨٧ (١٨٧ ورقة)، رئيس الكتاب ١٤٤ (٣٥٢ ورقة، فى القرن العاشر الهجرى)، داماد إبراهيم ٣٠٤ (٣٣٧ ورقة، فى القرن الحادى عشر الهجرى)، الظاهرية، حديث ٧٩٠ (ج ١، ٣٣١ ورقة)، الأوقاف ببغداد ٦٦٦٩ (٥٧١ هـ، انظر: طلس ص ٤٢).
طبع فى حلب ١٩٢٠ - ١٩٢٤ م. ١٩٣٢ - ١٩٣٤ م. وطبع بتحقيق أحمد محمد شاكر ومحمد حامد الفقى فى القاهرة ١٩٤٨ م./ ٢ - «العدّ المورود فى حواشى سنن أبى داود» تأليف عبد العظيم المنذرى (المتوفى سنة ٦٥٦ هـ/ ١٢٥٨ م انظر بروكلمان ١/ ٣٦٧) رئيس الكتاب ١٣٦ (٣٣٣ ورقة، ٦٧٦ هـ).
٣ - «شرح» محمود بن أحمد العينى (المتوفى ١٨٥٥/ ١٤٥١ م انظر بروكلمان ٢/ ٥٢)، القاهرة ثان دار الكتب ١/ ١٢٧، حديث ٢٨٦ (المجلد الأول، ٢٧٤ ورقة، فى القرن التاسع الهجرى بخط المؤلف، انظر:
فهرس معهد المخطوطات العربية ١/ ٨٤، القاهرة ملحق ٢/ ٤٢).
٤ - «شرح» لعمر بن رسلان بن نصر البلقينى (المتوفى سنة ٨٠٥ هـ/ ١٤٠٢ م انظر بروكلمان ٢/ ٩٣). المحمودية بالمدينة المنورة ٧٤ (٥ مجلدات، انظر:
Spies, ZDMG. ٩٠/ ١٩٣٦/ ١٠٩
٥ - «شرح» لأحمد بن حسين بن أرسلان الرملى (المتوفى سنة ٨٤٤ هـ/ ١٤٤٠ م انظر بروكلمان ٢/ ٩٦). مراد ملا ٤٣٨ - ٤٤٨، جوتا ٢ (الأوراق ٦٧، الديباجة)، لاله لي ٤٩٨ (ج ١، ٣٨٤ ورقة، ٨٤٧ هـ)، ٤٩٩ (ج ٢، ٨٤٧ هـ)، ٥٠٠ (ج ٣، ٣٢٩ ورقة ٨٣٥ هـ)، ٥٠١ (ج ٤، ٣٤٢ ورقة، ٨٤٥ هـ)، ٥٠٢، بورسه، حراچى زاده ٢٠٠ - ٢٠٣ (أربع مجلدات).
_________
(٣١٤) ليس رقم ٤٩١ كما فى بروكلمان.
(٣١٥) تحذف الأرقام ٤١٤ فى طوب قابو سراى؛ ١٣٠٨، ٥ بالمكتب الهندى التى ذكرها بروكلمان.','1','293',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (285,'٦ - «مرقاة الصعود إلى سنن أبى داود» تأليف السيوطى (المتوفى سنة ٩١١ هـ/ ١٥٠٥ م): كوبريلى ٤١٧ (٩٨٧ هـ)، الرباط ٢٣٨ (٣٠٠ ورقة، ١٢٩٨ هـ انظر: فهرس بروفنسال ٦١) بيشاور ٣٦٨، جوتا ٢ (الأوراق ٦٦، الديباجة)، سراى، أحمد الثالث ٦٤٧/ ١ (٢٥٦ ورقة، ٩٠٦ هـ وبآخره إجازة المؤلف).
وله مختصران بعنوان «درجات مرقاة الصعود» تأليف على بن سليمان الدمنتى الباجمعوى انظر بروكلمان ٢/ ٤٨٥. وطبع فى القاهرة ١٢٩٨ هـ (٣١٦).
٧ - «فتح الودود على سنن أبى داود» تأليف أبى الحسن محمد عبد الهادى السّندى (المتوفى سنة ١١٣٨ ٥/ ١٧٢٦ م، انظر: معجم المؤلفين لكحالة ١٠/ ٢٦٢)، القاهرة، ثان: ١/ ١٣٤، حديث ٢٨١، الظاهرية، حديث ٢٢٦، (قوله ١/ ١٤١)، راغب ٢٧٥ (٣٤١ ورقة، فى القرن الثانى عشر الهجرى)، مكتبة جامعة إستنبول ١٨٩٧ (٣٠٩ ورقة، ١٢٠٣ هـ)، دار الكتب بالقاهرة ٢٢٢٤٣ ب (مجلدان، ١٣٦١ هـ، نسخة من مخطوط القاهرة، حديث ٢٨١)، دمشق فى مكتبة سعيد حمزة الخاصة، انظر: مجلة معهد المخطوطات العربية ٥/ ١٩٥٩/ ٢١٩).
٨ - حاشية: «عون الودود» تأليف محمد بن عبد الله بنجابى الحزاروى، لكنو ١٣١٨ هـ ٩ - «تعليقات المحمود» تأليف فخر الحسين كنجوهى، كوانبور ١٩٠٥ م.
١٠ - «عون المعبود» تأليف محمد أشرف أمير عظيم آبادى، ومحمد شمس الحق عظيم آبادى. دهلى ١٣٢٢ هـ.
١١ - وكتب عليه مولوى وحيد الزمان حاشية باللغة الهندوستانية، لاهور ١٨٨٢ م.
١٢ - «غاية المقصود فى حل سنن أبى داود» تأليف محمد شمس الحق عظيم آبادى. الهند «دون تاريخ».

<span data-type="title" id=toc-618>مختارات من كتاب السنن ومختصرات له:</span>
أ- «المجتبى» لأبى محمد عبد العظيم بن عبد القوى المنذرى (المتوفى سنة ٦٥٦ هـ/ ١٢٥٨ م انظر بروكلمان ١/ ٣٦٧)، المتحف البريطانى ١٥٦٥ - ١٥٦٨ (٣١٧)، إضافات ٢٧٥١٤ - ١٧ (المجلدات ١ - ٤، ١٦٣ ورقة، ٢٢١ ورقة، ١٦٦ ورقة، ٢٢٠ ورقة، ٧١٣ هـ)، جاريت ١٣٦٦ (ج ١، ١٦٩ ورقة، فى
_________
(٣١٦) ليس سنة ١٢٨٥ هـ كما ذكر ذلك بروكلمان.
(٣١٧) ليس ١٨٦٥ - ٦٨ كما ذكر بروكلمان.','1','294',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (286,'القرن الثامن الهجرى)، بلدية الإسكندرية ٨١٤ ب (٢٢٤ ورقة، فى القرن التاسع الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٩٧)، الظاهرية، حديث ٢١٨ (مجلدان)، جوتا ٦٠٠ (قطعة منه من ص ٨٧ وما بعدها)، - طرخان ٧١ (٤١٠ ورقة، ٧٧٤ هـ)، قاضى زاده ٩٠ (١٦٢ ورقة)، محمود باشا ٧٢ - ٧٦ (كامل، سنة ٦٨٠ هـ)، البنغال ١٨٩ (١٦٥ ورقة، فى القرن الثالث عشر الهجرى)، يوسف بمراكش ٦١ (قسم ١٦). وطبع فى حيدرآباد ١٣٤٢ هـ، وحققه أحمد محمد شاكر ومحمد حامد الفقى القاهرة ١٩٤٨ م.
ب- لمحمد بن الحسن بن على البلخى، عاش فى القرن السابع الهجرى (؟ ) ماتسّه عمومى ٢٨١/ ١ (من ١ ب- ١١٣ أ، فى القرن السابع الهجرى، انظر: أحمد آتش فى مجلة: معهد المخطوطات العربية ٤/ ١٩٥٨/ ٢٠).
وعن كتاب السنن ألف ابن قيم الجوزية (المتوفى ٧٥١ هـ/ ١٣٥٠ م انظر بروكلمان ٢/ ١٠٥) كتابا بعنوان: «تهذيب سنن أبى داود». طبع فى دهلى سنة ١٨٩١، وحققه أحمد محمد شاكر ومحمد حامد الفقى. القاهرة ١٩٤٨ م/.
وعن شيوخ أبى داود ألف أبو على حسين بن محمد بن أحمد الجيّانى (المتوفى سنة ٤٩٨ هـ/ ١١٠٥ م انظر بروكلمان ١/ ٣٦٨) كتابا بعنوان: «تسمية شيوخ أبى داود». لاله لي ٢٠٨٩/ ٩ (ص ٧٤ أ- ٩٩ أفى القرن الثامن الهجرى، انظر: فايسفايلر ٩٥).

<span data-type="title" id=toc-619>ثانيا: «المسائل التى خالف عليها الإمام أحمد بن حنبل»:</span>
هذا الكتاب من رواية أبى داود، الظاهرية، حديث ٣٣٤ (١٠٠ ورقة، ٢٦٦ هـ، وأغلب الظن أن هذا المخطوط منقول عن نسخة أبى داود).

<span data-type="title" id=toc-620>ثالثا: «إجابته على سؤالات أبى عبيد محمد بن على بن عثمان الآجرّى»:</span>
(انظر: رقم ١٢٢ من هذا الباب).

<span data-type="title" id=toc-621>رابعا: «رسالة فى وصف تأليفه لكتاب السنن»:</span>
الظاهرية، حديث ٣٤٨ (من ١٨٨ أ- ١٩١ أ، فى القرن السابع الهجرى)، وطبع بتحقيق محمد زاهد الكوثرى، القاهرة ١٣٦٩ م.','1','295',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (287,'<span data-type="title" id=toc-622>خامسا: «كتاب الزهد»</span>
القرويين بفاس (انظر: مجلة معهد المخطوطات العربية ٥/ ١٩٥٩/ ١٤).

<span data-type="title" id=toc-623>سادسا: «تسمية إخوة الذين روى عنهم الحديث»:</span>
الظاهرية: مجموع ١٢٩ (الأوراق ٢١٦/ أ- ٢٢٣ ب، فى القرن السادس الهجرى، انظر العش ٢٠٣).

<span data-type="title" id=toc-624>سابعا: «كتاب المراسيل»</span>
رئيس الكتاب ١٤٥/ ٢ (من ١٣٦٤ أ- ٣٩١ ب، ٤٩٢ هـ)، كوبريلى ٢٩٤ - ٢ (من ٣٢٧ ب- ٣٥٦ ب، ٨٠٠ هـ)، الأزهر ١/ ٦٠٠ - ٦٠١، حديث ٣٥٢٦ (٦٩ ورقة)، ٤٢٢٨ (٢٢ ورقة)، ٤٢٢٩ (٤٢ ورقة، ١١٤٤ هـ). طبع فى القاهرة ١٣١٠ هـ.

<span data-type="title" id=toc-625>ثامنا: كتاب فى الرجال </span>(؟ )
الظاهرية مجموع ٤٦/ ١.

<span data-type="title" id=toc-626>تاسعا: كتاب القدر</span>
ذكره ابن حجر فى كتابه الإصابة ٣/ ١٠٥ (٣١٨)

<span data-type="title" id=toc-627>٩٧ - بقىّ بن مخلد</span>
هو أبو عبد الرحمن، بقىّ بن مخلد بن يزيد القرطبى، ولد سنة ٢٠١ هـ/ ٨١٧ م وقام برحلتين إلى مصر والشام والحجاز وبغداد طلبا للعلم، امتدت الأولى أربعة عشر عاما والثانية عشرين عاما. روى عن أحمد بن حنبل، وأبى بكر بن أبى شيبة وغيرهما. ألف عددا كبيرا من الكتب، ونسخ كتبا مشهورة أخرى ذهب بها إلى وطنه الأندلس. كان ثقة حجة واسع المعرفة، ولذا كان موضع مدح وتقريظ، وفوق هذا فيروى أنه أول محدث رتب الأحاديث فى «المسند» على أسماء الصحابة، ثم رتب
_________
(٣١٨) تحذف الكتب التى ذكرها بروكلمان فى الملحق تحت ثانيا وثالثا.','1','296',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (288,'حديث كل صاحب على أسماء الفقه فى أبواب الأحكام، (انظر: إرشاد الأريب لياقوت الحموى، لندن ٢/ ٣٦٨). أما تفسيره المفقود للقرآن الكريم فقال فيه ابن حزم: إنه لم يؤلف فى الإسلام مثله (انظر: المصدر السابق)، وتوفى سنة ٢٧٦ هـ/ ٨٨٩ م فى الأندلس.

<span data-type="title" id=toc-628>أ- مصادر ترجمته:</span>
تاريخ علماء الأندلس لابن الفرضى ١/ ١٠٧ - ١٠٩، جذوة المقتبس للحميدى ١٦٧ - ١٦٨، فضل الأندلس لابن حزم، انظر Andalus ١٩ /١٩٣٤ /٧٦: الصلة لابن بشكوال رقم ٢٧٩، التهذيب لابن عساكر ٣/ ٢٧٧ - ٢٨٢، الإرشاد لياقوت (القاهرة) ٧/ ٧٥ - ٨٥، البغية للضبى ٢٢٩، المنتظم للجوزى ٥/ ٢/ ١٠٠، طبقات الحنابلة لابن أبى يعلى ١/ ١٢٠، تذكرة الحفاظ للذهبى ٦٢٩ - ٦٣١، الدول للذهبى ١/ ١٢٢، البداية والنهاية لابن كثير ١١/ ٥٦ - ٥٧، نفح الطيب للمقرى (القاهرة) ٩/ ٢٨٥ - ٢٩٣، شذرات الذهب لابن العماد ٢/ ١٦٩، مرآة الجنان لليافعى ٢/ ١٩٠، طبقات المفسرين للسيوطى ٩ - ١٠، كشف الظنون لحاجى خليفة ٤٤٤، ١٦٧٩، الأعلام للزركلى ٢/ ٣٣، معجم المؤلفين لكحالة ٣/ ٥٣ - ٥٤، بروكلمان ١/ ١٦٤ تحت رقم ٦.

<span data-type="title" id=toc-629>ب- آثاره:</span>
١ - ذكر ما للصحابة من الحديث من العدد (كتاب الأعداد)، ذكره ابن عبد البر فى كتاب الاستيعاب، انظر / Weisweiler No.٩١: أما أبو محمد عبد الله بن يونس بن محمد المرادى (المتوفى سنة ٣٣٠ هـ/ ٩٤٢ م، انظر: الجذوة للحميدى ٢٤٨) فليس مؤلف الكتاب بل روايته (٣١٩) برلين ٩٩١٥/ ١٠ (الأوراق ٢٧ - ٢٩)، الظاهرية، مجموع ٣١ (٢٣٩ أ/ ٢٤٩ ب، فى القرن السادس الهجرى).
٢ - «المسند» ومنه اقتباسات فى الإصابة لابن حجر ١/ ١٥٠، ٢٨٣، ٤١٠، ٤١٣، ٤٥٣، ٥١٤، ٩٤٤؛ ٢/ ١٤٠، ٤٦٣، ١٢٦٢؛ ٣/ ٥، ٩٣٥، ١٠٦٢، ١١٨٠؛ ٤/ ٣، ١٥١، ٩٠٧.
٣ - المنتقى من حديث بقى بن مخلد (وهنّاد، والفارسى، والجوهرى، ومن أمالى ابن السمرقندى)، الظاهرية، مجموع ١٢٩ (٢٢٥ أ- ٢٣٦ ب (فى القرن السابع الهجرى).
_________
(٣١٩) يصحح ما عند بروكلمان.','1','297',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (289,'<span data-type="title" id=toc-630>٩٨ - أبو حاتم الرازى</span>
هو أبو حاتم، محمد بن إدريس بن المنذر، الحنظلى الرازى، ولد سنة ١٩٥ هـ/ ٨١١ م فى الرى. وقف حياته منذ شبابه على دراسة الحديث، وكان أول كتبه الحديث فى سنة تسع ومائتين، كان أحد الأئمة الحفاظ الأثبات، مشهورا بالعلم برواة الحديث مذكورا بالفضل، وقد ضاعت الكتب التى ألفها حول المحدثين والجرح والتعديل، ولكن بعض أقسامها قد وصل إلينا فى مقتبسات ضمتها كتب ابنه، أبى محمد عبد الرحمن ابن أبى حاتم (انظر: رقم ١٦٣ من هذا الباب)، وتوفى فى الرى سنة ٢٧٧ هـ/ ٨٩٠ م.

<span data-type="title" id=toc-631>أ- مصادر ترجمته:</span>
التقدمة لابن أبى حاتم ٣٤٩ - ٣٧٢، تاريخ بغداد للخطيب ٢/ ٧٣ - ٧٧، طبقات الحنابلة لابن أبى يعلى ١/ ٢٨٤، المنتظم للجوزى ٥/ ٢/ ١٠٧ - ١٠٨، تذكرة الحفاظ للذهبى ٥٦٧ - ٥٦٩، الدول للذهبى ١/ ١٢٢، الوافى بالوفيات للصفدى ٢/ ١٨٣، التهذيب لابن حجر ٩/ ٣١ - ٣٤، شذرات الذهب لابن العماد ٢/ ١٧١، الأعلام للزركلى ٦/ ٢٥٠، معجم المؤلفين لكحالة ٩/ ٣٥، انظر ما كتبه عنه لاوست فى الصحيفة التذكارية للوى ماسينيون:
H. Laoust, in: Mel. L. Massignon III, ١٥.

<span data-type="title" id=toc-632>ب- آثاره:</span>
١ - كتاب الزهد، منه مختارات فى الظاهرية، مجموع ٢٨/ ١٠ (١٣٨ أ- ١٤٦ ب، ٥٠٦ هـ).
٢ - الاعتقاد، أشار إليه ابن أبى يعلى فى طبقات الحنابلة ١/ ٢٨٦.
٣ - أما عن إجابته على أسئلة أبى عثمان سعيد بن عمرو بن عمار حول الضعفاء والكذابين ...
إلخ» (٣٢٠)، انظر الترجمة رقم ١١٤ فى هذا الباب.
_________
(٣٢٠) ذكر الزركلى أن كتاب «الزينة» لهذا المؤلف قد وصل إلينا وهذا ليس بصحيح. وأغلب الظن أنه خلط بين هذا المؤلف وأبى حاتم أحمد بن همدان الرازى المتوفى سنة ٣٢٢ هـ/ ٩٣٣، ويأتى ذكر الأخير فى باب الفقه.','1','298',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (290,'<span data-type="title" id=toc-633>٩٩ - الدّير عاقولى</span>
هو أبو يحيى، عبد الكريم بن الهيثم بن زياد القطّان، أصله من دير العاقول.
سافر إلى بغداد وواسط والبصرة والكوفة ودمشق ومصر. وسمع أبا الوليد الطيالسى، والفضل بن دكين وغيرهما. وروى عنه الترمذى وغيره. ويعدّ من الثقات، وتوفى فى بلدته دير العاقول سنة ٢٧٨ هـ/ ٨٩١ م/.

<span data-type="title" id=toc-634>أ- مصادر ترجمته:</span>
تاريخ بغداد للخطيب ١١/ ٧٨ - ٧٩، تذكرة الحفاظ للذهبى ٦٠٢، شذرات الذهب لابن العماد ٢/ ١٧٢، كشف الظنون لحاجى خليفة ١٢٩٧، هدية العارفين ١/ ٦٠٧.

<span data-type="title" id=toc-635>ب- آثاره:</span>
«حديث الإفك»:
الظاهرية، تصوف ١٢١ (٣٩ أ- ٤٧ ب، ٦٠٨ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٧٨).

<span data-type="title" id=toc-636>١٠٠ - التّرمذى</span>
هو أبو عيسى، محمد بن عيسى بن سورة (٣٢١) السّلمى التّرمذى، ولد سنة ٢١٠ هـ/ ٨٢٥ م فى بوغ من أعمال ترمذ على نهر جيحون. ودرس أول الأمر فى بخارى، ثم طاف خراسان والعراق والحجاز. ويعد البخارى أحد شيوخه، وقد أخذ مكانه فى خراسان بعد وفاته. ولا نعرف الكثير عن حياته، ولكنا نعرف أنه كفّ بصره فى خريف عمره، وتوفى سنة ٢٧٩ هـ/ ٨٩٢ م فى ترمذ. وأهم مؤلفاته كتابه «الجامع» الذى عدّ من الكتب الأصول المعتمدة، وقد امتاز فى المقام الأول بملاحظاته النقدية حول الأسانيد، وبإضافة الآراء المتباينة للمدارس الفقهية المختلفة.
_________
(٣٢١) ليس «ابن سهل» كما ذكر بروكلمان.','1','299',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (291,'<span data-type="title" id=toc-637>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٣٣، الأنساب للسمعانى ١٠٦ أ، الوفيات لابن خلكان (بولاق) ١/ ٦١٢ - ٦١٣، اللباب لابن الأثير ١/ ١٧٤، تذكرة الحفاظ للذهبى ٦٣٣ - ٦٣٥، ميزان الاعتدال للذهبى ٣/ ١١٧، الدول للذهبى ١/ ١٢٣، الوافى بالوفيات للصفدى ٤/ ٢٩٤ - ٢٩٦، نكت الهميان للصفدى ٢٦٤ - ٢٦٥، التهذيب لابن حجر ٩/ ٣٨٧ - ٣٨٩، البداية والنهاية لابن كثير ١١/ ٦٦ - ٦٧، مرآة الجنان لليافعى ٢/ ١٩٣، شذرات الذهب لابن العماد ٢/ ١٧٤ - ١٧٥، محمد يوسف البنورى: «الترمذى صاحب الجامع فى السنن» فى: مجلة المجمع العلمى العربى بدمشق ٣٢/ ١٩٥٧/ ٣٠٨ - ٣٢٦، الأعلام للزركلى ٧/ ٢١٣، معجم المؤلفين لكحالة ١١/ ١٠٤ - ١٠٥.
كتب عنه وستنفلد فى كتابه عن المؤرخين العرب:
Wustenfeld, Geschichts. ٧٥.
كتب عنه جولد تسيهر فى كتابه الدراسات الإسلامية:
Goldziher, Muh. Stud. II, ٢٥٠ - ٢٥٤
كتب عنه قنسنك فى دائرة المعارف الإسلامية- الطبعة الأوربية الأولى:
A. J. Wensink, EI. IV. ٨٥٢ - ٨٥٣

<span data-type="title" id=toc-638>ب- آثاره:</span>
<span data-type="title" id=toc-639>أولا: «الجامع الصحيح»</span>
دراسات عنه:
«فضائل الكتاب الجامع» لأبى القاسم عبيد بن محمد بن عباس الإسعردى (المتوفى ٦٩٢ هـ/ ١٢٩٣ م- انظر: الأعلام للزركلى ٤/ ٣٤٢)، صائب بأنقرة ٢١٦٧ (القرن الثامن الهجرى).
كتب روبسون عن رواية الجامع الصحيح للترمذى:
J. Robson, The Transmissionof Tirmidhi''s Jamd; BSOAS. ١٦/ ١٩٥٤/ ٢٥٨ - ٢٧٠
وكتب آربرى ملاحظة عن إحدى مخطوطاته:
A. J. Arberry, Notesona T. Manuscript, RSO, ١٨/ ١٩٤٠, ٣١٥ - ٣٢٧.
