INSERT INTO [b143160] VALUES (293,'<span data-type="title" id=toc-640>شروح الجامع الصحيح:</span>
١ - «عارضة الأحوذى (٣٢٤) فى شرح التّرمذى» لأبى بكر محمد بن العربى المعافرى (المتوفى ٥٤٣ هـ/ ١١٤٨ م انظر بروكلمان) القزويين بفاس ١٨٦، شيخ الإسلام بالمدينة ٢٤٠ (قطعة:
٢١٦ انظر Spies ,ZDMG ٩٠ /١٩٣٦ /١٠٩ الأزهر ١/ ٥٥٤، حديث ٣٢ (مجلد ٢، ٢١٦ ورقة)، وطبع فى: «مجموعة شروحى أربعى ترمذى» كوانبور ١٢٩٩ هـ، وبالقاهرة فى ١٣ مجلدا ١٣٥٠ هـ- ١٣٥٢ هـ.
٢ - «شرح» تأليف الحسين بن مسعود البغوى (المتوفى ٥١٠ هـ/ ١١١٧ م انظر بروكلمان ١/ ٣٦٣) المكتبة المحمودية بالمدينة المنورة ٣٥ (القسم الأخير فقط، انظر:
Spies, ZDMG ٩٠١/ ٦٣٩١/ ١٠٩
٣ - شرح تأليف أبى الفتح محمد بن محمد بن سيّد الناس (المتوفى ٧٣٤ هـ/ ١٣٣٤ م انظر بروكلمان ٢/ ٧١)، لاله ٥١٤ (١، ٢٧٦ ورقة، فى القرن الثامن الهجرى).
٤ - «شرح» تأليف زين الدين عبد الرحيم بن الحسينى العراقى (المتوفى ٨٠٦ هـ/ ١٤٠٤ م انظر بروكلمان ٢/ ٦٧). من المرجع أنه ألف شرحين:
أولهما: تكملة لشرح ابن سيد الناس، الإسكوريال ١٤٦٤ (١٧٢ ورقة)، دار الكتب بالقاهرة، حديث ٢٥٠٤ (٢٥٦ ورقة، قد يكون بخط المؤلف، انظر: فهرس معهد المخطوطات العربية ١/ ٦٩)، المحمودية بالمدينة المنورة ٣٣ (نسخة ابن حجر العسقلانى، انظر Spies ,ZDMG ٩٠١ /٦٣٩١ /١٠٩ جوتا ٢ (٦٨، الديباجة).
وثانيهما: فى عدة مجلدات بعنوان: «شرح سنن الترمذى»، فيض الله ٣٦٣ (مجلد واحد، ٢٢٦ ورقة، فى القرن التاسع الهجرى)، ٣٦٤ (المجلد السابع، ٢٧٥ ورقة بخط المؤلف).
٥ - «قوت المغتذى» لجلال الدين السيوطى، مراد ملا ٣٦٣ (١٥٩ ورقة)، ١٠٢٨ هـ)، إمبروزيانا (المنجد) ٢٨٩/ ٢ (١٥٠ ورقة، ١٠٩٠ هـ) بلدية الإسكندرية ب ١٣٤٣، وطبع فى كوانبور ١٢٩٩ هـ. ومختار منه: «نفع قوت المغتذى» تأليف على بن سليمان الدمنتى البجمعوي (المتوفى ١٣٠٦ هـ/ ١٨٨٨ م انظر بروكلمان ٢/ ٤٨٥). وطبع بالقاهرة ١٢٩٨ هـ، وكذلك على هامش طبعة دهلى ١٣٤٢ هـ.
_________
(٣٢٤) تصحح عند بروكلمان.','1','302',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (294,'٦ - شرح تأليف سراج أحمد السّرهندى، طبع فى كوانبور ١٢٩٩ هـ.
٧ - تأليف محمد بن الطيب السندى المدنى (١٢٩٦ هـ/ ١٨٧٩ م- ١٣٦٣ هـ/ ١٩٤٤ م): مكتب شيخ الإسلام بالمدينة ٢٤١ (قطعة منه، انظر:
Spies, ZDMG. ٩٠/ ١٠٩
٨ - «الطّيب الشّذى فى شرح التّرمذى» تأليف إشفاق الرحمن كند هلوى. دهلى ١٩٣٤ م.
٩ - «العرف الشّذى على جامع الترمذى» تأليف محمد أنور شاه. طبع طبعة هندية بالحجر ١٣٤٤ هـ.
١٠ - «شرح علل كتاب الجامع للترمذى» تأليف عبد الرحمن بن أحمد بن رجب الحنبلى (المتوفى ٧٩٥ هـ/ ١٣٩٣ م انظر بروكلمان ٢/ ١٠٧) سراى، أحمد الثالث ٣/ ٥٣٢ (١٥٢ ورقة، فى القرن الثامن الهجرى/، انظر: فهرس معهد المخطوطات العربية ١/ ٨٤) القاهرة ثان (١/ ٧٥ مصطلح ٤٩ (١٤١ ورقة، ٨٨٩ هـ، انظر: فهرس معهد المخطوطات العربية ٨٤، القاهرة مصطلح ٢٤٨).
١١ - «شرح» تأليف عبد القادر بن إسماعيل الحسنى القادرى: القاهرة، ثان ١، ١٢٥، ١٣١ (المجلدات ٢، ٣، ٤).
١٢ - «الأحاديث المستغربة الواردة فى الجامع الصحيح للترمذى» تأليف أحمد بن العلائى الشافعى (فى القرن الثامن الهجرى)، شهيد على ٣٥٣ (حوالى ١٥٠ ورقة، ٩٠٤ هـ).

<span data-type="title" id=toc-641>مختارات ومختصرات من الجامع الصحيح:</span>
إ- «مختصر» لنجم الدين سليمان بن عبد القوى بن عبد الكريم بن سعيد البغدادى الصّرصرى (المتوفى ٧١٦ هـ/ ١٣١٦ م انظر بروكلمان ملحق ٢/ ١٣٣، انظر: الأعلام للزركلى ٣/ ١٨٩) القاهرة ثان ١/ ١٤٤، حديث ٤٨٧.
ب- «مختصر» لمحمد بن عقيل البالسى (المتوفى ٧٢٩ هـ/ ١٣٢٩ م، انظر: طبقات الشافعية للسبكى ٦/ ٢٣، والدرر الكامنة لابن حجر ٤/ ٥٠)، باريس ٧١٠ - ٧١١ (مجلدان ١٦٤ ورقة، ١٨٣ ورقة، سنة ٧٤٧ هـ).
ج- «مختصر» لأبى الفضل محمد تاج الدين عبد المحسن القلعى (ألّف سنة ١١٤٧ هـ/ ١٧٣٥ م) بالقاهرة ثان ١/ ٩٤، حديث ٣٦٠، ٣٦١، «الموصل ١٢٧، ٩٨».','1','303',NULL,NULL,'0');
