INSERT INTO [b143160] VALUES (503,'١٠٥، ١٢٩، ١٣٣، المعارف لابن قتية ٣١٧ معجم البلدان لياقوت ٢/ ٨٦٧، وفوق هذا فقد روى عنه كل من البخارى ومسلم ستين حديثا. (١٢٢)

<span data-type="title" id=toc-1330>٢ - عقيل بن أبى طالب</span>
هو أبو يزيد، عقيل (بن أبى طالب) عبد مناف الهاشمى، وهو أخو على بن أبى طالب وأسن منه، برز اسمه فى الجاهلية (•)، وقاتل فى بدر فى صفوف الكفار وأسره المسلمون، أسلم قبيل صلح الحديبية. كان الناس يأخذون عنه الأنساب فى مسجد المدينة، وكان يحكى لهم عن أيام ومثالب قريش. كان أحد ثلاثة علماء/ كلفهم عمر بن الخطاب بتدوين أنساب العرب (١٢٣)، ومات مكفوف البصر سنة ٦٠ هـ/ ٦٨٠ م.

<span data-type="title" id=toc-1331>أ- مصادر ترجمته:</span>
الطبقات لابن سعد (ليدن) ٤/ ٢٨ - ٣٠، بيروت ٤٢ - ٤٤، المحبّر لابن حبيب ٤٥٧، البيان والتبيين للجاحظ ١/ ٣٢٢، مقاتل الطالبيّين لأبى الفرج الأصفهانى ٧، أنساب الأشراف للبلاذرى ١/ ٣٠١، ٣٥٦، ٣٦٥، المعارف لابن قتيبة ٥٨، ٧٧، ١٠٢، ٢٨٧، العقد الفريد لابن عبد ربه ٢/ ٣٥٦، ٣/ ٢٠٤، ٤، ٤، ٥، ٦، ٧، ٢٩، ٦/ ٩٩، نكت الهميان للصّفدى ٢٠٠ - ٢٠١، الإصابة لابن حجر ٢/ ١١٧٥ - ١١٧٦، التهذيب لابن حجر ٧/ ٢٥٤، الأعلام للزركلى ٥/ ٣٩، ومقدمة ابن خلدون (الترجمة الإنجليزية): ٢/ ٢١ - ٢٢ وانظر: فستنفلد فى كتابه عن المؤرخين Wustenfeld ,Geschichts NR.١

<span data-type="title" id=toc-1332>ب- آثاره:</span>
روى عنه النسائى وابن ماجه حديثا واحدا (انظر مسند ابن حنبل ١/ ٢٠١) وله أقاصيص جاءت فى العقد الفريد، وذكر له ابن أبى الحديد خبرا طويلا فى شرح نهج البلاغة ١١/ ٢٥٠ - ٢٥٤.
_________
(١٢٢) الرجال للقيسرانى ٧٦.
(١٢٣) الطبقات لابن سعد (بيروت) ٣/ ٢٩٥، تاريخ الطبرى ١/ ٢٧٥٠.
(•) ترجع مكانته في الجاهلية إلى أنه أحد من يتحاكم الناس إليهم فى المنافرات. (المترجم).','2','30',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (504,'<span data-type="title" id=toc-1333>٣ - مخرمة</span>
هو أبو صفوان، مخرمة بن نوفل بن أهيب، الزّهرى القرشى، ولد قبل الهجرة بستين عاما تقريبا. أسلم بعد فتح مكة، وكان يعد من كبار التابعين ورواة الشعر العربى القديم. من بين المخضرمين. كلفه عمر بن الخطاب أن يسهم مع عالمين آخرين فى إعداد ثبت بأنساب العرب. (١٢٤) كان من بين من وضعوا حدود المنطقة الحرام فى مكة. (١٢٥) وكفّ بصره فى خلافة عثمان، وتوفى سنة ٥٤ هـ/ ٧٦٤ م.

<span data-type="title" id=toc-1334>أ- مصادر ترجمته:</span>
السيرة لابن هشام ١/ ٤٢٧، الطبقات لابن سعد (بيروت ١/ ٨٩) نسب قريش لمصعب ٣٦٢ - ٣٦٣، البيان والتبيين للجاحظ ٢/ ٣٢٣، المعارف لابن قتيبة ١٦٠، ١٦٨، ٢١٩، فتوح البلدان للبلاذرى (القاهرة ١٩٥٧) ٦٣٠ - ٦٤١ تاريخ الطبرى ١/ ١٢٩١، ١٣٠٧، ١٦٣٠، ١٦٨٠، الاستيعاب لابن عبد البر ١/ ٢٦٠، نكت الهميان للصفدى ٢٨٧ - ٢٨٨، مقدمة ابن خلدون ٢/ ٢١ الأعلام للزركلى ٨/ ٧٢، وذكره فستنفلد فى كتابه عن المؤرخين العرب برقم Wustenfeld ,Geschichts.Nr.٣ ٣

<span data-type="title" id=toc-1335>٤ - الأقرع بن حابس التميمى</span>
كان من سادات العرب فى الجاهلية حكما مرموق المكانة. أسلم وشهد فتح مكة.
وصفه أبو عبيدة فى النقائض (١/ ١٤١) بأنه «كان عالم العرب فى زمانه»، وتوفى سنة ٣١ هـ/ ٦٥١.
_________
(١٢٤) انظر: الطبقات لابن سعد (بيروت) ٣/ ٢٩٥، تاريخ الطبرى ١/ ٢٧٥٠ - ٢٧٥١.
(١٢٥) انظر: الإصابة لابن حجر Wustenfeld ,Geschichts.Nr.٣، ٧٩٤ /٣','2','31',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (505,'<span data-type="title" id=toc-1336>أ- مصادر ترجمته:</span>
نسب قريش لمصعب ٧، البيان والتبيين للجاحظ ١/ ٢٩٠، ٣١٧، أنساب الأشراف للبلاذرى ١/ ٣٨٥، ٥٣٠، المعارف لابن قتيبة ١٧٤، ٢٨٤، الاشتقاق لابن دريد ١٤٦، تاريخ الطبرى ١/ ١٦٣٠، ١٦٧٦، ١٦٨٠، ٢٠٦٦ - ٢٠٦٨، الأغانى (بولاق) ١٠/ ٩٧ (دار الكتب) ١١/ ٢٧٨، الاستيعاب لابن عبد البر ١/ ٤٦، التهذيب لابن عساكر ٣/ ٨٦ - ٩١، معجم البلدان لياقوت ٢/ ١٤٩، ٣/ ١٢٢، الإصابة لابن حجر ١/ ١١٢، خزانة الأدب ٣/ ٣٩٧، الأعلام للزركلى ١/ ٣٤٣/.

<span data-type="title" id=toc-1337>٥ - عبيد بن شريّة</span>
عبيد (أو: عبيد) بن شريّة (١٢٦) الجرهمى. عاش فى الجاهلية والإسلام ويقال إنه عمّر طويلا إلى أن أدرك نهاية حكم معاوية. (١٢٧) كان راويةالأعشى (١٢٨) وعنه روى كذلك قصائد لطرفة (١٢٩) ويبدو أن جزءا من هذه الأشعار التى جاء بها صحيحة النسبة وهى موجودة فى دواوينها (١٣٠)، ولكن بعضها منحول (١٣١) وقد نفت نبيهة عبود الشكوك التى ثارت حول أخباره. (١٣٢) وأما زعم كرنكو بأنه شخصية خرافية وهمية اخترعها ابن النديم (١٣٣) فلا يقوم عليه دليل، وخاصة أن أبا حاتم السجستانى الذى عاش قبل ابن النديم بمائة وخمسين عام قد عرف عبيد بن شرية، وعده من المعمّرين. (١٣٤) ونقل المسعودى أجزاء من كتابه، ولاحظ أنه كان متداولا،
_________
(١٢٦) أو شرية، أو شرية:
(١٢٧) انظر: كتاب المعمّرين لأبى حاتم ٤٠
(١٢٨) انظر: مصادر الشعر الجاهلى لناصر الدين الأسد ٢٤٠.
(١٢٩) انظر: مقدمة تحقيق ريشر لمعلقة طرفة بشرح أبى بكر .. ابن الأنبارى، إستانبول O.Rescher ,Tarafas.Mu''allaqa .. ١٣٢٩
(١٣٠) نشأة التدوين التاريخى لحسين نصار ١٩.
(١٣١) مصادر الشعر الجاهلى لناصر الدين الأسد ٢٤٧.
(١٣٢) نبيهة عبود فى دراساتها عن البرديات العربية N.Abott ,Studiesin Ar.Literary Papyri ,S.٩ - ١٠
(١٣٣) انظر: الأعلام للزركلى ٤/ ٣٤١ - الهامش.
(١٣٤) كتاب المعمرين ٤٠','2','32',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (506,'وكان كثيرون يرونه منحولا. (١٣٥) ومن المرجّح أنه هو عبيد الذى ذكره كرنكو فى الأسطر الأخيرة من مادة «طرفة» فى دائرة المعارف الإسلامية (٤/ ٧١٨) راوية لشعر الأعشى. وعبيد بن شرية مؤلف أقدم كتاب فى الأمثال (١٣٦).

<span data-type="title" id=toc-1338>أ- مصادر ترجمته:</span>
التيجان لابن هشام ٢٠٩، مروج الذهب للمسعودى ٣/ ١٧٣ - ١٧٥، ٢٧٥، ٤/ ٨٩، إرشاد الأريب لياقوت (القاهرة) ١٢/ ٧٢ - ٧٨، هدية العارفين ١/ ٦٤٥، الأعلام للزركلى ٤/ ٣٤١، معجم المؤلفين لكحالة ٦/ ٢٣٤، علم التاريخ للدّورى ١٥، انظر ما كتبه عنه بلاشير فى Blachere ,Arabica ١ /١٩٥٤ /٥٥ وبروكلمان:
ملحق ١/ ١٠٠.

<span data-type="title" id=toc-1339>ب- آثاره:</span>
١ - «كتاب فى أخبار اليمن وأشعارها وأنسابها» المتحف البريطانى، الملحق ٥٧٨، مخطوطات شرقية ٢٩٠١/ ٢، (الأوراق من ١١١ - ١٨١، سنة ١٠٣١ هـ) (١٣٧)، نشره كرنكو ذيلا لكتاب التيجان لابن هشام ٣١١ - ٤٩٢، حيدرآباد ١٣٤٧ هـ ٢ - «كتاب الأمثال» أفاد منه الميدانى، انظر ما كتبه سلهايم فى كتابه عن الأمثال العربية -٣ Sellheim ,١٤٩. يبدو أن بعض النصوص التاريخية موجودة عند ابن إسحاق، أحدها «الحميريين» وهذا النص موجود عند وهب بن منبّه فى «الملوك» ص ٦٦ - ٦٩. (وصل قسم منه فى كتاب التيجان لابن هشام)، وهناك نص آخر فى ص ٢٠٩ - ٢١١.

<span data-type="title" id=toc-1340>٦ - صحار بن العبّاس</span>
هو صحار بن العباس (أو عيّاش) بن شراحبل العبدى، من بنى عبد القيس،
_________
(١٣٥) مروج الذهب ٤/ ٨٩.
(١٣٦) ابن النديم ٩٠، وكتاب سلهايم فى: الأمثال العربية Sellhsim ,Spriswrter.S.٢٩: وابن خلكان (ترجمة):
٣/ ١٢١.
(١٣٧) تحذف المخطوطات الأخرى التى ذكرها بروكلمان، الملحق ١/ ١٠٠','2','33',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (507,'كان صحابيا ومن أنصار عثمان. اشترك فى فتح مصر، وشهد موقعة صفين فى صفوف معاوية. كان نسابة ومؤلف كتاب فى الأمثال. (١٣٨) ولم يذكر اسمه فى كتب الأمثال المتأخرة إلا نادرا. (١٣٩) وينسب إليه كتاب فى النسب ذكره الجاحظ فى الحيوان ٣/ ٢٠٩.

<span data-type="title" id=toc-1341><span data-type="title" id=toc-1343>أ- مصادر ترجمته:</span></span>
الطبقات لابن سعد (بيروت) ٧/ ٦١، ٨٧، المحبّر لابن حبيب ٢٩٤، البيان والتبيين للجاحظ ١/ ٩٦ - ٩٧، ٤/ ٤٦، الحيوان للجاحظ ١/ ٩٠، ٩١، ٣٦٥، ٣/ ٢٠٩، ٣٦٧، ٤/ ٢٣٠، ٥/ ٣٣٠، ٣٣١، الاشتقاق لابن دريد ٢٠١، تاريخ الطبرى ١/ ٢٥٣٧، ٢٦٨٢، ٢٧٠٧، الإصابة لابن حجر ٢/ ٤٧٢ - ٤٧٥، مصادر الشعر الجاهلى لناصر الدين الأسد ١٦٨، الأعلام للزركلى ٣/ ٢٨٧، معجم المؤلفين لكحالة ٥/ ١٧، ذكره فستنفلد فى كتابه عن المؤرخين العرب Wustenfeld ,Geschichts.No.١٢. وذكره بلاشير فى كتابه فى تاريخ الأدب العربى Blachere ,Litterature ٩٤.:

<span data-type="title" id=toc-1342>٧ - حويطب</span>
هو حويطب بن عبد العزى بن أبى قيس، هو أحد أربعة قرشيين كانوا علماء بالشعر والأخبار والأنساب. أسلم بعد فتح مكة، شهد حنين والطائف. ثم انتقل إلى المدينة حيث توفى هناك سنة ٥٣ هـ/ ٦٧٢ م- فيما يقال- عن مائة وعشرين عاما.
وكان أحد من وضعوا أحجار حدود المنطقة الحرام. (١٤٠)
أ- مصادر ترجمته:
نسب قريش لمصعب ٤٢٥، ٤٢٦، البيان والتبيين للجاحظ ٢/ ٣٢٣، المحبر لابن حبيب ٩١، ١٠١.
_________
(١٣٨) ابن النديم ٩٠.
(١٣٩) انظر سلهايم، الأمثال العربية Sellheim ,Sprichworter ,S.٢٩ - ٩٩ وقد ترجمه إلى العربية رمضان عبد التواب بعنوان «الأمثال العربية القديمة مع اعتناء خاص بكتاب الأمثال لأبى عبيد» نشر دار الأمانة ومؤسسة الرسالة، بيروت ١٩٧١.
(١٤٠) تاريخ الطبرى ١/ ٢٥٢٨، الإصابة لابن حجر ٤/ ٧٩٤.','2','34',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (508,'١٠٥، ١٠٦، ١١٠، ٣٣٧، ٤٤٧، ٤٧٣، ٤٧٤، المعارف لابن قتيبة ١٥٩، تاريخ الطبرى ١/ ١٥٤١، ١٥٩٥، ٣٠٤٧، أنساب الأشراف للبلاذرى ١/ ٢٢٠، ٢٢٨، ٣١٢، ٣٥٠، ٣٥٢، ٣٦٢، ٤٠٩، ٤٤١، ٤٤٥، ٤٤٦، الاستيعاب لابن عبد البر ١٤٥، الأعلام للزركلى ٢/ ٣٢٧.

<span data-type="title" id=toc-1344>ب- آثاره:</span>
له قصة ذكرها ابن عبد ربه فى: العقد الفريد ٤/ ٣٣ - ٣٤.

<span data-type="title" id=toc-1345>٨ - زياد بن أبيه</span>
أخو معاوية المستلحق، وواليه على العراق، ولد زياد فى العام الأول للهجرة.
ويعتبر أحد أربعة دهاة كانوا كبار ساسة عصره. ومن المرجّح أنه صاحب أول مؤلف فى المثالب ويقال أنه ألفه ليكون أداة فى يد أبنائه للدفاع عما يوجه إلى أصالتهم. (١٤١) وكان هذا الكتاب متداولا فى القرن الثانى للهجرة، وقرظه الهيثم بن عدى (المتوفى ٢٠٦ هـ/ ٨٢١ م) وسيأتى الحديث عنه كثيرا، وقام أبو عبيدة (المتوفى ٢٠٨ هـ/ ٨٢٣) (١٤٢) بتهذيب نصه من جديد. (١٤٣) وقد وصلت إلينا بضع جمل من كتاب زياد مقتبسة فى «كتاب العرب» لابن قتيبة. (١٤٤) /.
وقد أمر الخليفة هشام بن عبد الملك (المتوفى ١٢٥ هـ/ ٧٤٣ م) النّضر بن شميل الحميرى (١٤٥) وخالد بن سلمه المخزومى (المتوفى ١٣٢ هـ/ ٧٥٠ م) (١٤٦) بتأليف
_________
(١٤١) انظر: الفهرست لابن النديم، ٨٩
(١٤٢) انظر: بروكلمان ١/ ١٠٣.
(١٤٣) انظر: سمط اللآلئ للبكرى ٨٠٨
(١٤٤) انظر رسائل البلغاء ٣٤٦.
(١٤٥) سيرته فى: تاريخ دمشق لابن عساكر، وله مخطوطة فى الظاهرية تاريخ ١٦، ٢٨٣ وما بعدها (نقلا عن: التدوين للعش ١٩).
(١٤٦) انظر: التهذيب لابن عساكر ٥/ ٥٢ - ٥٣','2','35',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (509,'كتاب يسمى «كتاب الواحدة» فى «مثالب العرب ومناقبها» يكون مخفّفا من تأثير كتاب زياد بن أبيه. (١٤٧) وكان هذا الكتاب متداولا فى القرن الخامس الهجرى.
وتوفى زياد سنة ٥٣ هـ/ ٦٧٣ م. وقد كتب أخبار زياد بن أبيه- فيما يروى- كل من أبى مخنف، هشام بن محمد الكلبى (المتوفى ٢٠٦ هـ/ ٨٢١ م، انظر ص ٢٦٨ من هذا الكتاب) وعبد العزيز بن يحيى الجلودى (المتوفى ٣٣٢ هـ/ ٩٤٤ م) (١٤٨).

<span data-type="title" id=toc-1346>أ- مصادر ترجمته:</span>
تاريخ الطبرى ١/ ٢٤٦٥ - ٢٤٦٦، ٢/ ٦٦ - ٨٧، ٣/ ٥٨، مروج الذهب للمسعودى ٥/ ١٥ وما بعدها، الأغانى (بولاق) ١٢/ ٧٣ - ٧٥، ١٦/ ٣ - ٩، ٢١/ ٢١ - ٤٠، معجم البلدان لياقوت ١/ ٩٠٥، مقدمة ابن خلدون (ترجمة) ٣/ ٥ - ١٥ وابن خلدون: البدء والتاريخ ٦/ ٢، خزانة الأدب ٢/ ٥١٧، ذكره فلها وزن فى كتابه فى تاريخ الدولة العربية وسقوطها.
Wellhausen,: Dasarabische Reichundsein Sturz, ٧٥,- ٨١
دائرة المعارف الإسلامية (مقالة (Lammens الطبعة الأوربية الأولى ٤/ ١٣٣٤ - ١٣٣٥، الأعلام للزركلى ٣/ ٨٩ - ٩٠ وانظر بروكلمان ١/ ٦٣.

<span data-type="title" id=toc-1347>ب- آثاره:</span>
وصلت إلينا بعض رسائله وخطبه فى كتاب «شرح نهج البلاغة لأبى الحديد ١٦/ ١٧٧ - ٢٠٤.
أما خطبته التى ألقاها واليا لمعاوية على البصرة فقد سجلها لنا أبو مخنف فى: كتاب «الغارات»:
صائب بأنقرة ٥٤١٨ (من ١٨٩ ب- ١٩١ أ)

<span data-type="title" id=toc-1348>٩ - النخّار بن أوس</span>
كان خطيبا ونسابة، وهو فى رأى ابن الكلبى أعظم علماء العرب فى
_________
(١٤٧) انظر: سمط اللآلى للبكرى ٨٠٨.
(١٤٨) انظر: الرجال للنجاشى ٢٤٥، والذريعة ١/ ٣٣١.','2','36',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (510,'الأنساب (١٤٩) ولد فى حياة النبى، وكان معاصرا لجميل بثينة، وتوفى سنة ٦٠ هـ/ ٦٨٠ م.

<span data-type="title" id=toc-1349><span data-type="title" id=toc-1352>أ- مصادر ترجمته:</span></span>
البيان والتبيين للجاحظ ١/ ٢٥، ١٠٥، ٢٣٧، ٣٣٣، الأغانى (بولاق)، ٧/ ١٠١، دار الكتب ٨/ ١٣٧، الجمهرة لابن حزم ٤١٩ (٤٤٨ ط هارون ١٩٦٢) تاجالعروس ٣/ ٥٥٩، الأعلام للزركلى ٨/ ٣٢٩.

<span data-type="title" id=toc-1350><span data-type="title" id=toc-1353>ب- آثاره:</span></span>
ينسب له «كتاب فى الأمثال» ذكره الجاحظ فى الحيوان ٣/ ٢٠٩ - ٢١٠١.

<span data-type="title" id=toc-1351>١٠ - أبو كلاب ورقاء بن الأشعر لسان الحمّرة</span>
كان من أفضل النسابين، وكان خطيبا حكيما، ولد فى الجاهلية وأدرك ظهور الإسلام.
أ- مصادر ترجمته:
الحيوان للجاحظ ٢/ ٢٠٠، ٣، ٩، ٢٠، المعارف لابن قتيبة ٢٦٦، الاشتقاق لابن دريد ٢١٣، الفهرست لابن النديم ٨٩، فيستنفلد: المؤرخون العرب Wustenfeld ,Geschichts.Nr.٦.
ب- آثاره:
له قصة ذكرها أبو الفرج، فى: الأغانى (بولاق (١٤/ ١٤٣، (القاهرة) ١٦/ ٨٩ - ٩١. وذكر الجاحظ فى الحيوان ٣/ ٢٠٩. أنه ألف كتابا فى الأمثال.
_________
(١٤٩) انظر: الإصابة ٣/ ١٢٠٢.','2','37',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (511,'<span data-type="title" id=toc-1354>١١ - أبو جهم</span>
هو أبو جهم (أو الجهم) عامر (أو عمير، أو: عبيد) بن حذيفة، أسلم يوم فتح مكة. وصفه الجاحظ بأنه قرشى عارف بالشعر والأخبار والأنساب (١٥٠)، توفى عن عمر متقدم حوالى سنة ٧٠ هـ/ ٦٩٠ م.

<span data-type="title" id=toc-1355>أ- مصادر ترجمته:</span>
نسب قريش لمصعب ٣٦٩، تاريخ الطبرى ١/ ٢٧٣٢، ٢٩٧١، ٣٠٤٨، العقد لابن عبد ربه ٤/ ٢٨٦، الاستيعاب لابن عبد البر ٢/ ٦٣١، سمط اللآلى للبكرى ٥٣٩ - ٥٤٠، الإصابة لابن حجر ٤/ ٦٢ - ٦٣، الأعلام للزركلى ٤/ ١٧.

<span data-type="title" id=toc-1356>ب- آثاره:</span>
تروى له «قصة» فى كتاب المحاسن والأضداد المنسوب للجاحظ ص ٢١٩، وفى العقد لابن عبد ربه ١/ ٥٢، وفى الأمالى للقالى ٢/ ١٠٦، وسمط اللآلى للبكرى ٥٤٠.

<span data-type="title" id=toc-1357>١٢ - ابن الكوّاء</span>
هو عبد الله بن عمرو بن الكواء اليشكرى، اشترك فى صف على بن أبى طالب فى معركة صفين، ثم رحل بعد التحكيم إلى حروراء حيث اختاره الخوارج زعيما من زعمائهم. وتوفى على الأرجح سنة ٨٠ هـ/ ٦٩٩ م. ويعتبر أحد كبار علماء النسب فى صدر الإسلام. ويبدو أن ابن حجر أفاد من كتاب له. (١٥١)
_________
(١٥٠) انظر البيان والتبيين للجاحظ ٢/ ٣٢٣.
(١٥١) انظر الإصابة لابن حجر ١/ ١٠٣.','2','38',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (512,'<span data-type="title" id=toc-1358><span data-type="title" id=toc-1360>أ- مصادر ترجمته:</span></span>
البيان والتبيين للجاحظ ٢/ ٢٥٣، المعارف لابن قتيبة ٢٦٦، أنساب الأشراف للبلاذرى ١/ ٤٨٩، الاشتقاق لابن دريد ٢٠٥، تاريخ الطبرى ١/ ٧٤ - ٧٥ (وبه مسائل على بن أبى طالب عن السواد الذى فى القمر) ٢٩٢٦ وانظر (الفهرس ٤٨٣)، الأغانى (بولاق) ١٣/ ٥٤ و (دار الكتب) ١٤/ ٢٧٦، الفهرست لابن النديم ٩٠، كتب عنه فستنفلد فى كتابه عن المؤرخين العرب Wustenfeld ,Geschichts.Nr.٩ .. وانظر:
برونوف، الخوارج ص Brunnow ,Die Charidschiten ,s.١٥.: ١٥

<span data-type="title" id=toc-1359>١٣ - مثجور بن غيلان الضبى</span>
أصله من البصرة، كان خطيبا وعالما بالأنساب. توفى حوالى سنة ٨٥ هـ/ ٧٠٤ م. ذكر الجاحظ أنه ألف كتابا فى الأنساب، كان متداولا فى ذلك الوقت. (١٥٢)
أ- مصادر ترجمته:
البيان والتبيين للجاحظ ١/ ٣٤١، الاشتقاق لابن دريد ١٢٠، الجمهرة لابن حزم ١٩٣، تاج العروس للزبيدى ٣/ ٧٣، الأعلام للزركلى ٦/ ١٥٦ - ١٥٧.

<span data-type="title" id=toc-1361>١٤ - ابن الكيّس</span>
هو زيد بن الكيس النّمرى، يرجح أنه عاش فى صدر الإسلام، ويبدو أنه ألف كتابا فى الأنساب: (١٥٣) البيان والتبيين للجاحظ ١/ ٣٠٤، المعارف لابن قتيبة ٢٦٦، الفهرست لابن النديم ٩٠، فيستنفلد المؤرخون العرب Wustenfeld ,Geschichts.Mr.٨.:
_________
(١٥٢) انظر: الحيوان للجاحظ ٣/ ٢١٠.
(١٥٣) انظر: الحيوان للجاحظ ٣/ ٢١٠.','2','39',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (513,'<span data-type="title" id=toc-1362>١٥ - دغفل بن حنظلة</span>
دغفل بن حنظلة بن زيد، الشّيبانى، «نسّابة العرب» ضرب به المثل فى معرفة الأنساب. عاش فى حياة النبى صلّى الله عليه وسلم ولكنه لم يسمع منه شيئا. بعد ذلك وفد على معاوية فى أيام خلافته فسأله فى العربية وفى أنساب العرب وفى النجوم فأعجبه علمه، فعهد إليه بتعليم ابنه يزيد/ ففعل. غرق يوم دباه سنة ٦٥/ ٦٨٥ فى فارس. وصفه الجاحظ (١٥٤) بأنه «علامة». كانت معارفه الواسعة فى الأنساب مضرب المثل فقيل فى المثل «أنسب من دغفل» (١٥٥) ذكره عدد من الشعراء فى شعرهم، (١٥٦) وذكر الفرزدق كتابه فى الأنساب. (١٥٧)

<span data-type="title" id=toc-1363>أ- مصادر ترجمته:</span>
البيان والتبيين للجاحظ ١/ ٢٤٧، ٢٧٣، ٣٠٤، ٣١٨، ٢/ ٨٠، ٢٥٣، الحيوان للجاحظ ٣/ ٤٨٩، المحبّر لابن حبيب ٤٧٨، المعارف لابن قتيبة ٤٨، ٢٥٦، عيون الأخبار لابن قتيبة ٢/ ١١٨، تاريخ الطبرى ١/ ١٨٣٥، الأغانى (بولاق) ١/ ٧، ٨، ٦/ ٥، ٧/ ١٢٤، الاشتقاق لابن دريد ٢١١، مروج الذهب للمسعودى ٤/ ١٦٦، الجمهرة لابن حزم ٢٩٩، معجم البلدان لياقوت ٢/ ٤٠٩، ٤/ ٨٩٩، زهر الآداب للحصرى ٤/ ٣٤، الإصابة لابن حجر ١/ ٩٧٥، ٢/ ٤٧٤، التهذيب لابن حجر ٣/ ٢١٠ - ٢١١، الأعلام للزركلى ٣/ ١٨ - ١٩، مصادر الشعر الجاهلى لناصر الدين الأسد ١٦٠، ١٦٢، ٢١٧، ٢١٨، ٣٢٢، فيستنفلد: المؤرخون العرب Wustenfeld ,Geschichts.Nr.٤ وانظر: كارل بروكلمان ملحق ١/ ١٠١.

<span data-type="title" id=toc-1364>ب- آثاره:</span>
١ - «السيرة عن دغفل الشّيبانى»
_________
(١٥٤) البيان والتبيين ١/ ٤٧، ٨٥، ١٢١
(١٥٥) الأمثال للميدانى ٢/ ٢٧٣.
(١٥٦) البيان والتبيين للجاحظ ١/ ١٣٠، ٣٢٣، ٣٤٠، ٣٥١
(١٥٧) النقائض ١٨٩.','2','40',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (514,'أصالة هذه السيرة موضع خلاف، وتوجد فى مخطوط امبروزيانا، كابروتى، الأصل ٣ (من ١ ب- ٦٤ أ، ٥٤٩ هـ، انظر: فهرس معهد المخطوطات العربية ٢ رقم ١١٠٣)، وانظر بصفة خاصة ما كتبه لوفجرن عن دغفل ودعبل بوصفهما راويتين لقصة عرب الجنوب:
O. Lofgren, Dagfalund Di''bilals Gewahrsmannerder Sudarabischen Sage. in: Studi Orinonoredi G. L. Della Vida II, ٩٤ - ٩٩.
٢ - «كتاب التظافر والتناصر» طبع فى استنبول ١٣٠٢ هـ، فى كتاب «التحفة البهية» ص ٣٨، ويضم- فيما يقال- خطبة للنابغة الذبيانى، ألقاها بين يدى الحارث الغسانى.
(انظر: النابغة فى باب الشعر- وسيأتى ذلك فى الجزء الثالث).
٣ - «التشجير» هذا الكتاب شجرة للأنساب، ذكر الهمدانى هذا الكتاب فى الإكليل. (١٥٨). وإذا كان ابن النديم قد ذكر فى الفهرست ٨٩ عن دغفل أنه «لا مصنف له» فلا بد أن نتبين مراده من هذه العبارة، فربما كان يعنى أنه لم يعرف له مصنفا، أو أنه يعنى بكلمة مصنف: كتابا ذا ترتيب منهجى (انظر: ما ذكره حول حماد الراوية، فى الفصل الخاص بتاريخ التراث وتاريخ الحضارة).

<span data-type="title" id=toc-1365>١٦ - علاقة</span>
هو علاقة (بكسر العين) أو علاقة (بفتح العين) بن كرسم (أو كريم) الكلابى عاش فى عهد يزيد بن معاوية.

<span data-type="title" id=toc-1366>أ- مصادر ترجمته:</span>
الإرشاد لياقوت (لندن) ٥/ ٦٦، (القاهرة) ١٢/ ١٩٠، مصادر الشعر الجاهلى لناصر الدين الأسد ١٦٨، معجم المؤلفين لكحالة ٦/ ٢٩٣، بروكلمان ١/ ٦٧.

<span data-type="title" id=toc-1367>ب- آثاره:</span>
لا نعلم يقينا هل كان علاقة مؤلف كتاب «الأمثال» الذى ضم خمسين ورقة أو أنه كان راوية كتاب
_________
(١٥٨) انظر لوفجرن فى المرجع السابق ٩٤.','2','41',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (515,'عبيد بن شرية الذى اطلع عليه ابن النديم (انظر الفهرست ٩٠)، فالأمر لا يتضح من نص الفهرست.
وفهم ياقوت من هذا النص أنه مؤلفه. (١٥٩) أما البكرى فى شرحه لكتاب أبى عبيد فى الأمثال فقد ذكر اسمه مرة واحدة. (١٦٠) أما السؤال: هل اعتبره البكرى مؤلّفا أو راوية فقط لعبيد بن شرية، فسيبقى على الأقل فى الوقت الحالى بدون إجابة.

<span data-type="title" id=toc-1368>١٧ - خبيب</span>
هو خبيب بن عبد الله بن الزبير الأسدى، أكبر أبناء عبد الله بن الزبير، روى عن عائشة، وكعب الأحبار وروى كذلك عن أبيه. وروى عنه الزهرى وابنه الزبير وغيرهما. ويعد من العلماء الذين طالعوا الكتب القديمة. (١٦١) كان يهتم اهتماما خاصا بنسب قريش، وشغل كذلك فيما يبدو بالتنجيم، وهو أحد الزهاد الأوائل. ولا نعلم ما الذى ألّفه من الكتب، فالعبارة القائلة عنه: «كان يعلم علما كثيرا»، وردت فى المصادر، وقد تعنى أنه ألف كتبا وتوفى سنة ٩٣ هـ/ ٧١١ م.

<span data-type="title" id=toc-1369>أ- مصادر ترجمته:</span>
نسب قريش لمصعب ٢٣٩ - ٢٤٠، التاريخ الكبير للبخارى ٢/ ١/ ٢٠٨ - ٢٠٩، المعارف لابن قتيبة ١١٦، الجرح والتعديل لابن أبى حاتم ١/ ٢/ ٣٨٧، التهذيب لابن حجر ٣/ ١٣٥ - ١٣٦.

<span data-type="title" id=toc-1370>١٨ - مقاتل الأحول</span>
هو مقاتل الأحول بن سنان بن مرثد، من المرجح أنه عاش فى أواخر العصر الأموى، ويعتبره أبو عبيدة أحد رواته.
_________
(١٥٩) انظر: ناصر الدين الأسد، مصادر الشعر الجاهلى ص ١٦٨.
(١٦٠) انظر: سلهايم، الأمثال العربية ص Sellheim ,Sprichworter ,S.٢٩: ٢٩
(١٦١) انظر: نسب قريش للزبير ١/ ٣٦ - ٣٨.','2','42',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (516,'هناك «خبر»، رواه مقاتل وغيره حول موت كليب بن ربيعة، وما قيل فى ذلك من الشعر، ذكره أبو الفرج الأصفهانى فى الأغانى (دار الكتب) ٥/ ٣٤، ٤١، ٤٢ - ٤٤، ٤٥ - ٥٢، انظر كذلك أبو عبيدة فى النقائض ٩٠٥ - ٩٠٧.

<span data-type="title" id=toc-1371>١٩ - المسمعى</span>
هو عامر بن عبد الملك المسمعى، ومن المرجّح أنه عاش فى العصر الأموى.
وهناك خبر مروى عنه فى حرب بكر وتغلب وما قيل فى ذلك من أشعار، ويبدو أن أبا الفرج الأصفهانى قد نقله فى كتاب الأغانى (دار الكتب) ٥/ ٤٤، ٥٣ - ٥٧، انظر أيضا: النقائض ٤٩٤، ٦٤٦، ٦٤٨، ٧٤٨.','2','43',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (517,'<span data-type="title" id=toc-1372>ثانيا: العصر العباسى (حتى حوالى ٤٣٠ ه</span>ـ)
تاريخ العصر الجاهلى عنى به فى العصر العباسى النسابون والمؤرخون وعدد كبير من اللغويين. كانوا يجمعون كتب التراجم من العصر الأموى ويكملونها ويهذبونها. وهناك مثال لذلك، فأبو عبيدة معمر بن المثنى (المتوفى ٢١٠ هـ/ ٨٢٥ م) هذب كتاب المثالب لزياد بن أبيه المتوفى ٥٣ هـ/ ٦٧٣ م على نحو جديد. (١٦٢) وفوق هذا فقد تطورت كتب عبيد بن شريّه ومعاصريه عن الأمثال فى مؤلفات القرنين الثانى والثالث للهجرة. (١٦٣) وقد ضاعت أكثر مؤلفات أولئك العلماء/، وحسبنا أن نعتمد على نقول منها فى المؤلفات المتأخرة لنعيد تكوينها. وأقدم الكتب التى وصلت إلينا من العصر العباسى هى بعض كتب ابن الكلبى ذات موضوعات عن الجاهلية بصفة خاصة، ووصل إلينا كذلك كتاب ضخم لأبى عبيدة فى نقائض جرير والفرزدق، هو- أيضا- مصدر قيم لتاريخ العرب القديم.

<span data-type="title" id=toc-1373>١ - خالد بن طليق</span>
أقدم علماء الأنساب فى العصر العباسى هو خالد بن طليق بن محمد بن عمران الخزاعى عينه الخليفة المهدى سنة ١٦٦ هـ/ ٧٨٢ م قاضيا على البصرة، وذكر له ابن النديم الكتب التالية:
١ - «كتاب المآثر» ٢ - «كتاب المتزوّجات» ٣ - «كتاب المنافرات» ٤ - «كتاب البرهان»
_________
(١٦٢) انظر سمط اللآلئ للبكرى ٨٠٨.
(١٦٣) انظر: سلهايم، الأمثال العربية ص Selleim Sprichworter.٢٩ - ٣١ ,٩٨.٩٩.، ٩٨، ٣١ - ٢٩','2','45',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (518,'ولم تصل إلينا هذه الكتب، وقد أشار إليه الطبرى فى تاريخه مرتين. (١٦٤)

<span data-type="title" id=toc-1374><span data-type="title" id=toc-1376>أ- مصادر ترجمته:</span></span>
الطبقات لابن سعد ٧/ ١/ ٥، الفهرست لابن النديم ٩٥، الأغانى ١٧/ ٢٤، ٢٧، جمهرة أنساب العرب لابن حزم ٢٢٦، معجم البلدان لياقوت ١/ ٦٤٥، النجوم الزاهرة لابن تغرى بردى ٢/ ٥١، كتب عنه فستنفلد فى كتابه عن المؤرخين العرب Wustenfeld ,Geschichts.Nr.٩:

<span data-type="title" id=toc-1375>٢ - الضّحّاك بن عثمان</span>
هو الضّحّاك بن عثمان بن الضحاك القرشى، علامة المدينة المنورة بأخبار العرب وأيامها وأشعارها، كان من أكبر أصحاب مالك. عينه هارون الرشيد واليا على المدينة، وتوفى بمكة سنة ١٨٠ هـ/ ٧٩٦ م.
أ- مصادر ترجمته:
نسب قريش لمصعب ٢٣٤، الجمهرة للزبير ٤٠١ - ٤٠٤، الطبقات لابن سعد (بيروت) ٥/ ٤٢٢، التهذيب لابن حجر ٤/ ٤٤٧، الأعلام للزركلى ٣/ ٣٠٩.

<span data-type="title" id=toc-1377>ب- آثاره:</span>
لا نعرف أسماء كتبه. ويبدو أن الواقدى أكثر من الإفادة من كتابه (أو كتبه)، وترجع الاقتباسات الواردة عنه فى تاريخ الطبرى وطبقات ابن سعد إلى الواقدى. وقد استخدم الزبير بن بكار نسخة منه بخط المؤلف. (انظر: الجمهرة ١/ ٦، الإصابة ١/ ٣٥١، ٣/ ١٠٠٧). وهناك مقتبسات منه فى الجمهرة للزبير بن بكار، وفى كتاب الأغانى (دار الكتب) مثلا ٣/ ١٢٣، ٥/ ٧٥.
_________
(١٦٤) انظر: تاريخ الطبرى ٣/ ٥٠٦، ٥١٨.','2','46',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (519,'<span data-type="title" id=toc-1378>٣ - أبو اليقظان</span>
هو أبو اليقظان سحيم (أو عامر) بن حفص، كان أحد كبار العلماء بتاريخ الجاهلية. ذكر له ابن النديم (١٦٥) خمسة كتب. ذكره المدائنى بأسماء مختلفة، هى: أبو اليقظان، وسحيم بن حفص، وعامر بن حفص، وعامر بن أبى محمد، وعامر بن الأسود، وسحيم بن الأسود، وعبيد الله بن حفص، وأبو إسحاق. (١٦٦)، وأخذ عنه الطبرى وذكره باسم سحيم بن/ حفص. (١٦٧) وعامر بن حفص، (١٦٨) وأبو اليقظان. (١٦٩). وقد وصلت إلينا اقتباسات كثيرة عنه فى كتاب المعارف لابن قتيبة.
وتوفى سنة ١٩٠ هـ/ ٨٠٦ م

<span data-type="title" id=toc-1379>أ- مصادر ترجمته:</span>
المردفات للمدائنى فى نوادر المخطوطات ١/ ٨٠، الحيوان للجاحظ ٢/ ١٠، ٣/ ٢٠٩، الاشتقاق لابن دريد ١٤٤، الإرشاد لياقوت (ليدن) ٤/ ٢٢٦، (القاهرة) ١١/ ١٨٠، هدية العارفين ١/ ٤٣٥ - ٤٣٦، معجم المؤلفين لكحالة ٥/ ٥٣، علم التاريخ للدورى ٤٠، ذكره فستنفلد فى كتابه عن المؤرخين العرب Wustenfeld ,Geschichts.Nr.٣٦: ذكره روزنتال فى كتابه فى علم التاريخ عند المسلمين:
Rosenthal, History, ٣١٠.

<span data-type="title" id=toc-1380>٤ - لقيط المحاربى</span>
هو أبو هلال لقيط بن بكير بن النّضر المحاربى، وهو من أهل الكوفة، كان
_________
(١٦٥) الفهرس ٩٤.
(١٦٦) انظر المرجع السابق.
(١٦٧) انظر: فهرس تاريخ الطبرى ٢٢٣.
(١٦٨) المرجع السابق ٣٠٥.
(١٦٩) المرجع السابق ٦٥٠','2','47',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (520,'زاهدا، عالما بالأنساب وبأخبار العرب وأشعارها. وتوفى سنة ١٩٠ هـ/ ٨٠٦ م ومن تلاميذه ابن الأعرابى.

<span data-type="title" id=toc-1381>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٩٤، الارشاد لياقوت (ليدن) ٦/ ٢١٨ - ٢٢٠، (القاهرة) ١٧/ ٣٦ - ٤١، الأعلام للزركلى ٦/ ١٠٨، معجم المؤلفين لكحالة ٨/ ١٥٧.

<span data-type="title" id=toc-1382>ب- آثاره</span>
ذكر له ابن النديم الكتب التالية:
١ - «كتاب النساء» ٢ - «كتاب السمر» ٣ - «كتاب الحرّاب واللصوص» ٤ - «كتاب أخبار الجن» وهناك مقتبسات عنه فى الأغانى (دار الكتب) ١/ ٩٩ - ١٠٠، ٢/ ٨٧، ٣/ ١٣ - ١٤، ٤/ ٢٧٤، ٦/ ٧٠ - ٧١، ١٩٠ - ١٩١، ٨/ ٥٩، ٣٢٣، ١٢/ ١٥٩ - ١٦٠، ٢٩٠، ١٣/ ١٩٧ - ١٩٨، ١٤/ ٢٩٩، ١٥/ ٣٣٧ - ٣٣٨، ٣٥٠ - ٣٥١، ٣٧٧ - ٣٧٨، ١٦/ ١٣٣، ٣٧٨. ويبدو أن هذه المقتبسات أخذت من كتابه المصنف فى «الأخبار» الذى ذكره المرزبانى فى «المقتبس» ٢٩١، وقد ذكر ياقوت الحموى بعض أشعار للقيط، انظر: إرشاد الأريب فى المواضع السابقة.

<span data-type="title" id=toc-1383>٥ - أبو البخترى</span>
هو أبو البخترى، وهب بن وهب بن كبير، قرشى ولد فى المدينة. كان محدثا ضعيفا لا يوثق به، ولكنه كان من العلماء بالأنساب والأخبار. عند ما انتقل إلى بغداد فى خلافة هارون الرشيد، ولّاه قضاء العسكر ثم قضاء المدينة، وتوفى سنة ٢٠٠ هـ/ ٨١٥ م.','2','48',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (521,'<span data-type="title" id=toc-1384>أ- مصادر ترجمته:</span>
التاريخ الكبير للبخارى ٤/ ١٧٠، المعارف لابن قتيبة ٢٥٨، الفهرست لابن النديم ١٠٠، المقتبس للمرزبانى ٣١٢ - ٣١٣، تاريخ بغداد للخطيب ١٣/ ٤٨١ - ٤٨٧، الإرشاد لياقوت (ليدن) ٧/ ٢٣٢ - ٢٣٣، (القاهرة) ١٩/ ٢٦٠، ميزان الاعتدال للذهبى ٣/ ٢٧٨، لسان الميزان لابن حجر ٦/ ٢٣١ - ٢٣٤، ذكره روزنتال فى كتابه فى علم التاريخ عند المسلمين Rosenthal ,History ,٣٢٩: الأعلام للزركلى ٩/ ١٥٠، معجم المؤلفين لكحالة ١٣/ ١٧٤.

<span data-type="title" id=toc-1385>ب- آثاره:</span>
ذكر له الفهرست (ص ١٠٠) الكتب الآتية:
١ - «كتاب صفة النبى» ٢ - «كتاب فضائل الأنصار» ٣ - «كتاب الفضائل الكبير» ٤ - «كتاب نسب ولد إسماعيل» ٥ - «كتاب طسم وجديس» ٦ - «كتاب الرّايات»

<span data-type="title" id=toc-1386>٦ - إبراهيم بن موسى</span>
هو إبراهيم بن موسى بن صديق، كان جده لأمه عبد الله بن الزبير، وكان عالما بالشعر وتاريخ العرب/ والفقه، وكان زاهدا. توفى- على الأرجح- حوالى سنة ٢٠٠ هـ/ ٨١٥ م. انظر: نسب قريش للزبير ١/ ٩١، ٢٣٠. وقد أفاد الزبير من كتبه، (١٧٠) وذكر ياقوت فى معجم البلدان (٤/ ٥٥٦) شعرا له.
_________
(١٧٠) انظر: نسب قريش للزبير ١/ ٩١، ٢٣٠، ذكر الزبير مرة أنه استخدم كتبه ثم قال فى تاريخ حياته «كان .. من أهله العلم، نظر فى العلم» وهذا يعنى- غالبا- أن مدونى سيرته يشيرون إلى مؤلّف هام. (انظر أيضا مقدمة هذا الفصل).','2','49',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (522,'<span data-type="title" id=toc-1387>٧ - عمارة بن القدّاح</span>
هو أبو محمد عبد الله بن محمد بن عمارة بن القدّاح الأنصارى النسابة، أصله من المدينة، واستقر فى بغداد. كان من كبار علماء الأنساب فى عصره. (١٧١) ومن تلاميذه مصعب الزبير، وابن سعد، وعمر بن شبّة. ومن المرجّح أنه توفى فى أواخر القرن الثانى الهجرى.

<span data-type="title" id=toc-1388>أ- مصادر ترجمته:</span>
الجرح والتعديل لابن أبى حاتم ٢/ ١٥٨، تاريخ بغداد للخطيب ١٠/ ٦٢، معجم المؤلفين لكحالة ٦/ ١٣٤ (وقد أخطأ سهوا فى ذكر البيانات الخاصة بزمن حياته)

<span data-type="title" id=toc-1389>ب- آثاره:</span>
«نسب الأنصار» هو أحد المصادر الأساسية لابن سعد فى تاريخه للأنصار (١٧٢)، ونادرا ما ذكر ابن سعد اسم كتاب ابن القدّاح. (١٧٣) وقد احتفظ ابن حجر كذلك بقطع كثيرة منه فى الإصابة: ١/ ٣٩٩، ٤١٢، ٤٣٨، ٥٦٠، ٥٩٧، ٦٠٩، ٢/ ٦٣، ٦١٠، ٦٢٠، ١٢٧٩، ٣/ ٥١٢، ٧٥٢، ٧٦٠، ٧٦٢، ٧٦٥، ٧٦٩، ٧٧٦، ٧٧٨، ٧٨١، ٩٧٨، ٩٨٠، ١٠٤٩، ١١٤٠، ١١٥٧، ١١٦٤، ١٣٣٨، ٤/ ٣٠٢، ٣١١، ٨٣٣، انظر كذلك: تاريخ الطبرى ٣/ ٢٥٥٢. (١٧٤) كان ابن القداح يسمى مصادره من حين لآخر. فقد أفاد فى ذكر «أصحاب» رسول الله من كتاب بخط مؤلفه داود بن الحسين (المتوفى ٤٧٩، ٥٢٢، ٦٢٦) وكان أحيانا يقول: هكذا وجد فى الكتب (١٧٥) (المرجع السابق ٥١٤).
_________
(١٧١) انظر: الطبقات لابن سعد (بيروت) ٣/ ٤٤٢.
(١٧٢) نفس المرجع ٣/ ٤٤٧ - ٦٢٧.
(١٧٣) انظر: الطبقات لابن سعد (بيروت ٣/ ٤٧٠، ٥١٣، ٥٤٨.
(١٧٤) انظر: ص ٢٨٥ من كتابنا.
(١٧٥) المرجع السابق ٣/ ٥١٤.','2','50',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (523,'<span data-type="title" id=toc-1390>٨ - ابن السّائب الكلبى</span>
هو أبو المنذر، هشام بن محمد بن السّائب الكلبى، توفى أبوه سنة ١٤٦ هـ/ ٧٦٣ م. وولد هو فى الكوفة، وتوفى بها سنة ٢٠٤/ ٨١٩ م، أو ٢٠٦ هـ ورث الاهتمام بتاريخ العرب القديم عن والده الذى يرجع إليه الفضل فى جزء من معارفه فى هذا الميدان. شغل بموضوعات مختلفة من التاريخ العربى القديم، شأنه فى هذا شأن معاصريه أبى عبيد، وعلّان الشعوبى، والهيثم بن عدىّ. اعتمد فى الأنساب على كتاب ألفه (١٧٦) أو رواه (١٧٧) أبوه، وكان يفيد فى تاريخ الفرس من الكتب المترجمة عن الفارسية (١٧٨) وذلك على النحو الذى عرف فى عصره. (١٧٩) وقد احتفظ الطبرى بمقتبسات كثيرة/ من هذه الكتب، أخذها فيما يبدو من مؤلفات هشام. (١٨٠) وقد استخدم فى تاريخه للأمويين كتبا كثيرة منها ما ألفه عوانة بن الحكم. (١٨١) ومعروف كذلك أنه أفاد من نقوش كنائس الحيرة للتعرف على تاريخ اللخميين. (١٨٢) وقد تحرج العلماء المسلمون من المعلومات التى جاء بها (على الرغم مما ذكره ياقوت فى معجم البلدان ٢/ ١٥٨) وربما لم يكونوا مغالين فى هذا. (١٨٣)

<span data-type="title" id=toc-1391>أ- مصادر ترجمته:</span>
الرجال للنجاشى (بومباى) ٣٠٥ - ٣٠٦، (إيران) ٣٣٩ - ٣٤٠، تاريخ بغداد للخطيب البغدادى
_________
(١٧٦) انظر: علم التاريخ للدورى ص ٤١.
(١٧٧) انظر: الفهرست لابن النديم ١٠٨.
(١٧٨) انظر: علم التاريخ للدورى ص ٤١.
(١٧٩) انظر ما كتبه جولدتسيهر فى كتابه فى الدراسات الإسلامية Goldziher ,Muh.Stud.I.١٩٨
(١٨٠) انظر: جواد على: مجلة المجمع العلمى العراقى ٢/ ١٩٥١ م ١٣٦ - ١٣٩.
(١٨١) انظر: علم التاريخ للدورى ٢٣٢ - ٢٤٧، ٣٠١ - ٣١١
(١٨٢) انظر ما كتبه نولدكه عن تاريخ الفرس والعرب Noldeke ,Geschichteder Perserund Araber.XXVII: وجواد على فى تاريخ العرب قبل الاسلام ١/ ٤٧ - ٤٨، وما كتبه أوليندر عن ملوك كندة O.Olinder ,Kingsof Kinda ١٦ - ١٧.:
(١٨٣) انظر ما كتبه كاسكل O.Cackel ,Islamica ٣ ,١٩٢٩ /٨٧.:','2','51',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (524,'١٤/ ٤٥ - ٤٦، وفيات الأعيان لابن خلكان ٢/ ٢٥٨ - ٢٥٩، الإرشاد لياقوت (القاهرة) ١٩/ ٢٨٧ - ٢٩٢، لسان الميزان لابن حجر ٦/ ١٩٦ - ١٩٧، مرآة الجنان لليافعى ٢/ ٢٩، شذرات الذهب لابن العماد ٢/ ١٣، الأعلام للزركلى ٩/ ٨٧، معجم المؤلفين لكحالة ١٣/ ١٤٩ - ١٥٠. وكتب ليالى عرضا لما كتبه ابن الكلبى ليوم كلاب الأول:
C. J. Lyali, Ibn Kalbi''s Accountofthe First Dayofal- Kulab. In: Or. Stud. T. Noldekegewidmet. I, ١٩٠٦, ١٢٧ - ١٥٤.

<span data-type="title" id=toc-1392>ب- آثاره:</span>
١ - «النسب الكبير» أو «الجمهرة» (١٨٤) نقل البلاذرى أكثر مادته فى كتابه فى الأنساب (١٨٥)، ويوجد مخطوطا فى: الإسكوريال ١٦٩٨ (٢٦٥ ورقة، ٦٢٦ هـ، انظر: فهرس معهد المخطوطات العربية ٢ رقم ٤٠٨، ١٢٨٥). المتحف البريطانى ١٢٠٢، الإضافات ٢٣٢٩٧ (قسم واحد من ٢٦٠ ورقة، ٦٥٣ هـ، انظر فهرس معهد المخطوطات العربية ٢ رقم ١٩٩) وكذلك ٩١٥، الإضافات ٢٢٣٧٦ (٩٦ ورقة، فى القرن الثانى عشر الهجرى)، انظر: القاهرة، ملحق ١/ ٢٢٢، باريس ٢٠٤٧ (قطعة فى ١٣ ورقة، ٢٠٠ هـ، انظر: فايدا، مجموعة منتخبة للمخطوطات العربية القديمة فى باريس) أمانيسه، عمومى ٦٥٩٤ (من ٣٢ أ- ٩٢ ب، ١١٠١ هـ، انظر أحمد آتش فى:
مجلة معهد المخطوطات العربية ٤/ ١٩٥٨/ ٢٣ - ٢٤.) مختصرات النسب الكبير: أ- «المقتضب من كتاب جمهرة النسب» لياقوت الحموى (المتوفى سنة ٦٢٦ هـ/ ١٢٢٩ م انظر بروكلمان ١/ ٤٨٠، فهرس الكتب العربية الموجودة بدار الكتب بالقاهرة ٥/ ٣٥٥ (١٨٦)، تاريخ ١٠٥ م (١١٧ ورقة، انظر: فهرس معهد المخطوطات ٢ رقم ١٢٤٣)، الأزهر ٥/ ٥٦٦ تاريخ ٣٢٣ (١٣٩ ورقة).
ب- وهناك مختصر يبدو أنه من تأليف المبارك بن أبى بكر بن أحمد بن الشّعار الموصلى (المتوفى سنة ٦٥٤ هـ/ ١٢٥٦ م انظر بروكلمان ملحق ٣/ ١٢١٧) راغب ٩٩٩ (١٦٧ ورقة، ٦٦٥ هـ،
_________
(١٨٤) انظر: جواد على: جمهرة ... مجلة المجمع العراقى ١/ ٣٣٧ - ٣٤٩.
(١٨٥) انظر مقدمة كتاب أنساب الأشراف لمحمد حميد الله، القاهرة ١٩٥٩ م، ص ٦.
(١٨٦) ليس ٣٠٥ كما جاء عند بروكلمان.','2','52',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (525,'انظر: فهرس معهد المخطوطات العربية ٢ رقم ٧٨٩)، انظر: حمد الجاسر فى مجلة المجمع العلمى العربى بدمشق ٢٧/ ٤١، ٢٩/ ٦٣١ - ٦٣٢ انظر: جواد على فى المجلة السابقة ٢٨/ ٦٥٧ - ٦٦٤.
ج- مختصر أعده مجهول، مكتبة جامعة برنستون ٢٤٨٨ (انظر: سومر ٧/ ٢٤٩) انظر كذلك:
الأغانى (دار الكتب) ٤/ ٣٠٢ ولقد أعاد كاسكل ترتيبه وقدم له وعلق عليه وسماه جمهرة النسب:
W. Caskel, Gamharatan- nasab, Das Genealogische Werkdes Hisamibn M. al- Kalbi Bd. I, Einl. (Caskel) , Tafeln (Strenziok) , Bd. II, Erlaut (Caskel) , Register (Strenziok- Caskel) Leiden ١٩٦٦.
٢ - «كتاب نسب الخيل فى الجاهلية والإسلام» جوتا ٢٠٧٨ (١٨ ورقة، نسخة حديثه)، الإسكوريال ١٧٠٥/ ٢ (الأوراق من ١٢ - ٢٦، ٤٩٩ هـ بخط أبى منصور الجواليقى) ولى الدين ٣١٧٨/ ٤ (١٨٧) (من ١٦ أ- ٢٣ ب فى القرن العاشر الهجرى).
عاطف ٢٠٠٣/ ٣ (من ٣٠ أ- ٤٦ ب ١١٠٠ هـ)، تيمور ٥٠، مكتبة الإمام المهدى بسامراء (القرن السادس الهجرى، انظر: مجلة معهد المخطوطات العربية ٤/ ٢١٣)، ونشره ليفى ديلافيدا:
Levidella Vida. In: Leslivresdeschev aux, Leiden ١٩٢٨.
ونشره كذلك أحمد زكى باشا بالقاهرة ١٩٤٦ م.
٣ - «كتاب الأصنام» القاهرة ملحق ١/ ٥٤ (الأوراق من ٥٢ - ٥٦، غير كامل، وحققه أحمد زكى باشا، اعتمادا على هذا المخطوط فيما يرجح) قونية ٤٨٧٩ (من ص ١١٨ - ١٣٣، ١٠٨٣، بخط عبد القادر البغدادى مؤلف الخزانة، انظر (N.Hoca ,Sarkiyat Mecm.IV ,١٢١): السماوى بالنجف (انظر: مجلة معهد المخطوطات العربية ٤/ ٢١٧؟ ). وقد ضم كتاب فلها وزن عن بقايا الوثنية العربية
J. Wellhausen, Reste Desarabischen Heidentums, ١٨٨٧
ترجمة ألمانية للفقرات التى اقتبسها ياقوت الحموى من كتاب الأصنام لابن الكلبى وشرحا لهذه الفقرات، وحققه أحمد زكى باشا- القاهرة ١٩٢٤ م.
_________
(١٨٧) ليس هذا المخطوط فى بايزيد ٣٠٧٨ كما جاء عند بروكلمان.','2','53',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (526,'وطبعت نسخة أحمد زكى مع ترجمة ألمانية وملاحظات بقلم كلنكى- روزنبرجر
R. Klinke- Rosenberger, Das Gotzenbuch K. al- Asnamdesb. al- Kalbi, Leibzig ١٩٤١.
وقد قام ستومر بالتعليق على هذا العمل فى مجلة المستشرقين الألمان العدد رقم ٩٨ لسنة ١٩٤٤:
F. Stummer, Bemerkungenzum Gotzenbuchdes Ibnal- Kalbi, in: ZDMG ٩٨/ ١٩٤٤/ ٣٧٧ - ٣٩٤.
وعن هذه الترجمة كتب جويدى M.Guidi ,RSO ٢٠ /١٩٤٢ - ٤٤ /٣٢٢ - ٣٢٤: وترجمه نبيه أمين فارس إلى اللغة الانجليزية، برنستون ١٩٥٢، انظر:
وعن هذه الترجمة كتب موسكاتى»

٤ - S.Moscati ,Orientalia ٢٣ /١٩٥٤ /٢٠٠.: كتاب مثالب العرب»:
دار الكتب بالقاهرة، أدب ٩٦٠٢ (٦٦ ورقة، فى القرن السابع الهجرى انظر: فهرس المخطوطات ٢ رقم ٤٠٤)، المتحف ببغداد ١٤٦٥ (انظر: كوركيس عواد فى: سومر ١٣/ ٧١)، وربما توجد له مخطوطة فى السماوى بالنجف، انظر: مجلة معهد المخطوطات العربية ٤/ ٢١٧، ومنه مقتبسات فى الإصابة ١/ ٦٣٦، ٢/ ٧١٨، ٣/ ٩٥٠، ١٢٨٩، ٤/ ١٧٧، ٩٣٠. (•)
٥ - «كتاب فى الأمثال» (١٨٨) ذكره ابن النديم فى الفهرست ٩٦، ومن المرجح أن مقتبسات منه فى كتاب «الرموز» لابن أبى السرح (سيأتى فى صفحة ٣٧٠ من أصل هذا الكتاب)، انظر: مجلة المجمع العلمى بدمشق ١١/ ٦٤٢ - ٦٤٣، ٦٤٤، ٦٥٠، ٦٥٢، ٦٥٣، ٦٥٤، وكذلك فى كتاب «الأمثال» لأبى عبيد بن سلّام، انظر: ما كتبه سلهايم فى كتابه عن الأمثال العربية:
Sellheim, Sprich Worter ٧٦.
٦ - «قصيدة النذير» (؟ ) بشرح لأبى بكر بن دريد: سراى، أحمد الثالث ٢٣٢٩ (من ١٦ ب- ٢٣ ب، ٦١٦ هـ).
_________
(•) حققه الدكتور أمجد حسن فى رسالة جامعية لدرجة الدكتوراه من جامعة لاهور (١٩٧٩). (المترجم).
(١٨٨) يحذف كتاب «أسواق العرب» الذى ذكره بروكلمان. ولم يرد اسم هذا الكتاب إلا عند محمد حميد الله.','2','54',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (527,'٧ - «أخبار بكر وتغلب» المتحف ببغداد ١٢ (؟ )، انظر: الذريعة ١/ ٣٢٣.
٨ - روى ابن السائب الكلبى، ديوان حاتم الطائى، وقد اعتمد عليه المرزبانى فى صنعته للديوان، وقد وصل إلينا قسم من هذه الرواية، انظر إضافات بروكلمان إلى المجلد الأول ص ٢٧.
٩ - «إياد بن نزار» هل هو الذى ذكره ابن النديم باسم كتاب افتراق ولد نزار؟ (انظر: الفهرست ٩٦)، مسكوة بطهران ٩/ ٧٧٩ رقم ٢١٤١ (١٣ ورقة، ٦٧٧ هـ).
١٠ - وروى ابن السائب الكلبى ديوان لقيط وقد وصلت إلينا منه عدة نسخ، انظر تكملة بروكلمان إلى المجلد الأول ص ٢٧.
١١ - «أخبار (أو: خبر) مجنون» انظر: الأغانى (دار الكتب) ٢/ ١١، وقد وصلت إلينا منه مقتبسات فى الأغانى: ٢/ ٢١ - ٢٢، ٢٩ - ٣١، ٦١ - ٦٢، ٦٨ - ٧٠، ٩٥ - ٩٦.
١٢ - «فتوح الشام» أفاد منه ابن حجر فى الإصابة ١/ ٧٦٨، ٣/ ٥٥١.
١٣ - «كتاب الدقائق» وقد وصلت إلينا منه فقرة حول الفترة المكية فى حياة الرسول، فى الإصابة لابن حجر ٤/ ٣٠٩ - ٣١٠.
١٤ - «كتاب افتراق العرب»:
أفاد منه ياقوت فى معجم البلدان ١/ ١٢٧، ١٤٩، ١٤٩، ٢/ ٢٨٨، ٣/ ٨١٤ (انظر كذلك: الفهرست','2','55',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (528,'لابن النديم ٩٦)، وانظر أيضا ما كتبه هير عن المصادر التاريخية والجغرافية لمعجم البلدان لياقوت الحموى:
F. J. Heer, Hist. undgeogr. Quellen Yaquts, S. ٤ - ٥.
١٥ - «كتاب أنساب البلدان» أو «كتاب أنساب المواضيع» منه مقتبسات فى معجم البلدان لياقوت ٢/ ٦٠، ٦٥٢، ٨٧٦، ٤/ ٤٤١ (انظر: الفهرست ٩٧، وهير فى المرجع السابق ٥). ومن المرجّح أن البلاذرى قد أفاد من هذا الكتاب برواية العباس بن المؤلف هشام ابن الكلبى، (انظر فهرس فتوح البلدان).
١٦ - «كتاب ابتداء الغناء والعيدان» أفاد منه الأدفوى فى كتابه «الإمتاع» دار الكتب بالقاهرة، تصوف ٣٦٨ (٢٢ أ، ١١٩ ب)، ويبدو أن عددا من المقتبسات قد/ وصلت إلينا منه فى الأغانى، انظر: العقد الفريد لابن عبد ربه ٦/ ٢٧، ٢٨ - ٢٩.
١٧ - «كتاب الجمل» أفاد منه ابن أبى الحديد فى «شرح نهج البلاغة» ٦/ ٢١٥ وابن معصوم فى «الدرجات الرفيعة» ١٩٧ - ١٩٨.
١٨ - «كتاب أخبار صفّين» اقتبس منه ابن أبى الحديد فى «شرح نهج البلاغة» ٦/ ٣١٦ ولا بد من بحث ما إذا كان هذا الكتاب قد وصل إلينا فى المخطوط الموجد فى إمبروزيانا ١٢٩، وبرلين (حاليا: توبنجن Qu ٢٠٤٠ (أم لا.
١٩ - «كتاب الألقاب» استخدمه ابن ماكولا فى «الإكمال» ٤/ ٢٩٥، ٢٣٢ ملاحظة: يعد ابن الكلبى من مصادر الطبرى الأساسية (انظر فهرس تاريخ الطبرى ٦١٣ - ٦١٤)، وقد استخدم الطبرى كتبه فى حديثه عن تاريخ الفرس والحيرة، وذكر مقتبسات منها مسبوقة بعبارة:','2','56',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (529,'«حدثنى» أو «أخبرنى»، أو بإسناد ذى علمين، ولكنه كان يستخدم كتبه فى أكثر الأحوال دون حق روايتها، ويقدم لهذه المقتبسات بعبارات مثل: «حدّثت عن هشام» أو «ذكر هشام .. » أو «قال»، أى أنه أخذها- وجادة- كما يقول علماء الحديث.

<span data-type="title" id=toc-1393>٩ - علّان الشّعوبى الورّاق</span>
كان أصله الفارسى موضع فخره. اتصل بالبرامكة، واشتغل فى عهد هارون نساخا فى مكتبة بيت الحكمة. كان نسابة ذا اهتمام خاص بمثالب العرب. وقد توفى فى أوائل القرن الثالث الهجرى.

<span data-type="title" id=toc-1394>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ١٠٥ - ١٠٦، الإرشاد لياقوت (لندن) ٥/ ٦٦ - ٦٨، (القاهرة) ١٢/ ١٩١ ١٩٦ لسان الميزان لابن حجر ٤/ ١٨٧، وانظر ما كتبه جولدتسيهر فى كتابه فى الدراسات الإسلامية:
Goldziher, Muh. Stud. I, ١٤٧ - ٢٠٨.

<span data-type="title" id=toc-1395>ب- آثاره:</span>
أفاد أبو الفرج فى كتاب الأغانى (دار الكتب) ١٤/ ٨٧، ٨٨ - ٨٩ من أحد كتب علان الشعوبى فى المثالب.

<span data-type="title" id=toc-1396>١٠ - مصعب الزبيرى</span>
هو أبو عبد الله، مصعب بن عبد الله بن مصعب، الزبيرى، كان عم الزبير ابن بكار وشيخه، ولد بالمدينة المنورة نحو سنة ١٥٦ هـ/ ٧٧٣ م. ودرس على الإمام مالك وغيره ثم رحل بعد ذلك إلى بغداد. وكان يعتبر نفسه عالما فى الأنساب ومحدّثا، وكان','2','57',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (530,'ذا موهبة فى الشعر. وقد احتفظ كتاب الأغانى ببعض مراثيه ومدائحه فى معاصريه.
وتوفى ببغداد سنة ٢٣٦ هـ/ ٨٥١ م وحسب مصادر أخرى ٢٣٣ هـ/ ٨٤٨ م.

<span data-type="title" id=toc-1397>أ- مصادر ترجمته:</span>
الطبقات لابن سعد ٥/ ٣٢٥، ٧/ ٢/ ٨٤، التاريخ الكبير للبخارى ٤/ ١/ ٣٥٣، الفهرست لابن النديم: ١١٠، تاريخ بغداد للخطيب ١٣/ ١١٢ - ١١٤، الأنساب للسمعانى: ٢٧١، ميزان الاعتدال للذهبى ٣/ ١٧٣، تهذيب التهذيب لابن حجر ١٠/ ١٦٢ - ١٦٤، الأعلام للزركلى ٨/ ١٥٠، معجم المؤلفين لكحالة ١٢/ ٢٩١ - ٢٩٢ كتب عنه بروفنسال فى، E.L.Provencal ,Arabica I /١٩٥٤ /٩٢ - ٩٥: بروكلمان ملحق ١/ ٢١٢.

<span data-type="title" id=toc-1398>ب- آثاره:</span>
١ - «الجمهرة فى نسب قريش» القرويين بفاس، ٢٩٤ (١١٦٤ هـ)، مدريد ٣٥٠ وهى الآن برقم ٥٣٣ (النصف الأول ٦٨ ورقة، فى القرن السادس الهجرى)، المتحف البريطانى، مخطوطات شرقية ١١٣٣٦ (١١٠ ورقة، ١٣١ هـ)، اليوسفية بمراكش ٤٠٠، الكتانى بالرباط بدون رقم (١٢١ ورقة/، فى القرن الحادى عشر الهجرى) حققه ليفى بروفنسال، القاهرة ١٩٥٣ م، انظر: عزّ الدين التنوخى فى: مجلة المجمع العلمى العربى فى دمشق ٢٩/ ١٩٥٤/ ٥٩٣ - ٦٠٣.
٢ - وله «حديث» فى الظاهرية مجموع ١١٧/ ٦ (من ١٣٨ أ- ١٥٣ ب، ٤٦٠ هـ) تشستربيتى ٣٨٤٩/ ٤ (الأوراق من ٣٦ - ٥٨، ٥٧٩ هـ).

<span data-type="title" id=toc-1399>١١ - الهيثم بن عدىّ</span>
هو الهيثم بن عدى بن عبد الرحمن الثعلى، ولد فى الكوفة قبل سنة ١٣٠ هـ/ ٧٤٧ م، وعاش فى واسط، كان مؤرخا عالما بالأنساب وأديبا. ترجع شهرته فى المقام','2','58',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (531,'الأول إلى كتبه فى المثالب. ويعتبر سابقا للطبرى بسبب مؤلفه تاريخ العالم بالترتيب الزمنى (انظر: ابن النديم ١٠٠) وتوفى سنة ٢٠٦ هـ/ ٨٢١ م أو ٢٠٧ هـ/ ٨٢٢.

<span data-type="title" id=toc-1400>أ- مصادر ترجمته:</span>
البيان والتبيين للجاحظ ١/ ٣٤٧، ٣٦١، المعارف لابن قتيبة ٢٦٧، ٥٣٨ (الطبعة الثانية)، مروج الذهب للمسعودى ١/ ١٠، الفهرست لابن النديم ٩٩ - ١٠٠، تاريخ بغداد للخطيب ١٤/ ٥٠ - ٥٤، الإرشاد لياقوت (لندن) ٧/ ٢٦١ - ٢٦٦، (القاهرة) ١٩/ ٣٠٤ - ٣١٠، وفيات الأعيان لابن خلكان (بولاق) ٢/ ٢٦٩ - ٢٧٣، لسان الميزان لابن حجر ٦/ ٢٠٩ - ٢١١، البداية والنهاية لابن كثير ١٠/ ٢٦١، الأعلام للزركلى ٩/ ١١٤ - ١١٥، معجم المؤلفين لكحالة ١٣/ ١٥٦، جواد على فى: مجلة المجمع العلمى العربى ٢/ ١٩٥١/ ١٦٢ - ١٦٣، علم التاريخ للدّورى ٤٢ - ٤٣.
كتب عنه فستنفلد فى كتابه عن المؤرخين العرب تحت رقم ٤٤:
Wustenfeld, Geschichts. Nr. ٤٤
كتب عنه جولدتسيهر فى كتابه فى الدراسات الإسلامية:
Goldziher, Muh. Stud. I, ١٩٥.
كتب عنه لوت فى مجلة المستشرقين الألمان عدد ٢٣ ص ٦٠٣:
Loth, ZDMG, ٢٣/ ٦٠٣

<span data-type="title" id=toc-1401>ب- آثاره:</span>
ذكر له ابن النديم خمسين كتابا، لم يظهر منها إلى الآن شئ، ولكن هناك نقول من كتبه المختلفة بقيت للآن وقد أخذت بصفة خاصة فى أنساب الأشراف للبلاذرى، والمعارف لابن قتيبة، وتاريخ الطبرى (انظر الفهرس ٦١٩) ومروج الذهب للمسعودى والكتب الأخرى. وقد جمع الدورى (المرجع السابق ٣١٩ - ٣٢٥) بعض المقتبسات الواردة عند البلاذرى والطبرى. لا تعطينا دراسة الدورى لقيمة هذه الأخبار صورة صادقة، وذلك لأنه لم يلتفت إلا إلى الأخبار المسبوقة بعبارات: «قال»، «ذكر»، باعتبارها مقتبسات من مصادر مدونة، وعد الأخبار الأخرى من مصادر شفوية (انظر كذلك ص ٤٣).
١ - هناك قطعة من كتابه «المثالب» (١٨٩) فى الأغانى (طبعة ساسى) ١/ ١٢ - ١٤، وأخرى فى الإصابة ١/ ٦٣٦.
_________
(١٨٩) لا نعلم إلى الآن علم اليقين ما إذا كانت هناك نسخة بحوزة كرنكو كما ذكر بروكلمان أم لا.','2','59',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (532,'٢ - «أخبار مجنون أو خبر مجنون» انظر الأغانى (دار الكتب) ٢/ ٦٣، ومنه مقتبسات فى: الأغانى (دار الكتب) ٢/ ١٥ - ١٦، ٢٢ - ٢٥، ٦٠ - ٦١، ٦٣ - ٦٤، ٧٠ - ٧١، ٧١ - ٧٢، ٧٣، ٨١، ٨٥ - ٩٠.
٣ - «خبر أبى قطيفة عمرو بن الوليد» انظر: الأغانى (دار الكتب) ١/ ٢١، ٢٢ - ٢٣، ٢٦ - ٢٧

<span data-type="title" id=toc-1402>١٢ - سهل بن هارون</span>
هو أبو عمرو سهل بن هارون، نشأ فى البصرة، واشتهر فيها قبل أن يتصل بخدمة هارون الرشيد، ثم صار كاتبا للخليفة المأمون، وولاه رياسة خزانة الحكمة.
مدح بأنه: «بزرجمهر الإسلام»، كان فارسى الأصل شعوبيا/ يتعصب للعجم على العرب. توفى سنة ٢١٥ هـ/ ٨٣٠ م.

<span data-type="title" id=toc-1403>أ- مصادر ترجمته:</span>
البيان والتبيين للجاحظ ١/ ٥٢، العقد الفريد لابن عبد ربه (انظر: الفهرس) ١١٨، زهر الآداب للحصرى ٣/ ٢٦٠، الإرشاد لياقوت (القاهرة) ١١/ ٢٦٧، فوات الوفيات للكتبى ١/ ١٨١، أمراء البيان لمحمد كرد على ١/ ١٥٩ - ١٩٠، أعيان الشيعة للعاملى ٣٥/ ٤٠٩ - ٤١٠، الأعلام للزركلى ٣/ ٢١١، معجم المؤلفين لكحالة ٤/ ٢٨٦، بروكلمان ملحق ١/ ٢١٣، كتب عنه جولدتسيهر فى كتابه فى الدراسات الإسلامية Goldziher ,Muh.Stud.I ,١٦١.: كتب عنه ريشر فى كتابه فى تاريخ الأدب العربى Rescher ,Abriss ١١.٢٧٣ - ٢٧٤: كتب عنه بيلا فى كتابه عن البيئة البصرية Ch.Pellat ,Milieu ٢٢٢:

<span data-type="title" id=toc-1404>ب- آثاره:</span>
وصفه الجاحظ فى البيان والتبيين (١/ ٥٢) بأنه «من الخطباء الشعراء الذين قد جمعوا الشعر والخطب','2','60',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (533,'والرسائل الطوال والقصار والكتب الكبار المخلدة والسير الحسان المدونة والأخبار المولدة». ألف كتابا مشهورا باسم: «ثعلة وعفرة» على غرار ابن المقفع فى «كليلة ودمنة»، فضله عليه المسعودى في كتابه مروج الذهب ١/ ١٥٩، وقد ضاع هذا الكتاب، وقد وصلت إلينا من مؤلفاته:
١ - «النمر والثعلب» الزيتونة بتونس ٢٨٨ (من ٦٤ أ- ٨٣ ب، وتأليفه لهذا الكتاب موضع نظر).
٢ - «رسالة فى البخل» وردت فى كتاب البخلاء «للجاحظ (بتحقيق فلوتن ١٠ والصفحات التى تليها)، العقد لابن عبد ربه ٦/ ٢٠٠ - ٢٠٤.

<span data-type="title" id=toc-1405>١٣ - العقيقى</span>
هو أبو الحسين، يحيى بن الحسن بن جعفر، الحجّة العقيقى، أصله من المدينة ومولده بها سنة ٢١٤/ ٨٢٩ م. وألف عددا من الكتب، منها: أخبار المدينة، ونسب آل أبى طالب. وتوفى فى مكة سنة ٢٧٧ هـ/ ٨٩٠ م.

<span data-type="title" id=toc-1406>أ- مصادر ترجمته:</span>
الرجال للنجاشى (إيران) ٣٤٤، الذريعة ١/ ٣٤٩، ٣٧٨، الأعلام للزركلى ٩/ ١٧٠.

<span data-type="title" id=toc-1407>ب- آثاره:</span>
له كتاب: «نسب آل أبى طالب» وكان أحد مصادر «مقاتل الطالبيين» لأبى الفرج الأصفهانى، أفاد منه بالإسناد التالى: «حدّثنى أحمد بن سعيد، قال: حدثنا يحيى بن الحسن .. » إلخ، وأحيانا بالإسناد: «قال يحيى بن الحسن». وفوق هذا، فهذا الكتاب أحد المصادر الرئيسية لكتاب «بحر الأنساب»، ويوجد مخطوطا فى وهبى ١٣٠٥ (١٦٣ ورقة، ٩٠٣ هـ). وهناك قطعة منه عند ابن أبى الحديد فى «شرح نهج البلاغة ١٦/ ٥٠ - ٥١. ولا بد من بحث مخطوط أنساب الطالبيين بمكتبة المتحف فى بغداد ١٥٧٥ (الأوراق ٦٩ - ١٥٠، انظر: سومر ١٣/ ١٩٥٧/ ٤٥) للتأكد من كونه لهذا الكتاب أم لغيره.','2','61',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (534,'<span data-type="title" id=toc-1408>١٤ - أبو فراس</span>
هو أبو فراس محمد بن فراس بن محمد بن عطاء، هو نسابة روى عن هشام الكلبى. ومن المرجّح أنه توفى فى النصف الأول من القرن الثالث الهجرى.
له كتاب فى الأنساب استخدمه ابن ماكولا فى الإكمال، انظر: مثلا ٢/ ٥٨، ٨٨، ١١٠.

<span data-type="title" id=toc-1409>١٥ - الحنبصى</span>
هو أبو نصر محمد بن عبد الله بن سعيد الحنبصى، وصفه الهمدانى بأنه أكبر نسابة ومؤرخ للتاريخ الحميرى القديم، أفاد من نقوش وكتب عربية جاهلية. مدحه شاعر معاصر له بأنه فاق كل النسابين العرب وبزهم جميعا (انظر الإكليل للهمدانى/ ١/ ٥ - ٦). وكان يعيش حتى سنة ٢٩٥ هـ/ ٩٠٧ م فى قصر حنبص فى اليمن (الإكليل للهمدانى ٨/ ٥٢، الأنساب للسمعانى ٤/ ٢٧٩).

<span data-type="title" id=toc-1410>ب- آثاره:</span>
له كتاب «نسب حمير» وكان من أهم مصادر الهمدانى فى كتابه الإكليل ١/ ٥٣، ٥٥ - ٥٦، ٨/ ٢٣ - ٢٤، ٢٦، ٣٢، ٥١، ٨٥، ١٤٦، ١٤٩ - ١٥٠، وذكره ابن ماكولا فى الإكمال: ٣/ ١٩٢، ٣٢٦، ٤/ ٣٦٩، ٣٧٠.

<span data-type="title" id=toc-1411>١٦ - شبل النسّابة</span>
هو أبو الفتح شبل بن تكين النسابة المصرى، وصفه ابن ماكولا بأنه نسابة كبير. كان يعيش قرابة سنة ٣٤٢ هـ/ ٩٥٣ م. له كتاب فى النسب استخدمه ابن','2','62',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (535,'ماكولا فى كتابه الإكمال معتمدا على نسخة بخط المؤلف انظر: مثلا ١/ ٣٩، ١٧٩، ١٨٨، ١٩٢، ١٩٣، ٢/ ٥٧، ٣٣٩، ٤/ ٢٣، ٣٢٧، ٤٥٤.

<span data-type="title" id=toc-1412>١٧ - ابن الحبّاب</span>
هو أحمد بن الحبّاب بن حمزة بن غيلان الحميرى، روى عن عبد الله بن جعفر ابن درستويه (المتوفى ٣٤٧ هـ/ ٩٥٨ م). له كتاب فى النسب أفاد منه ابن ماكولا- كثيرا- فى الإكمال، انظر مثلا: ١/ ٢١٤، ٢/ ٦٠، ١٤٤، ٤٠٠، ٥٣٠، ٥٧٤، ٤/ ١.

<span data-type="title" id=toc-1413>١٨ - العبيدلى</span>
أبو الحسن، محمد بن محمد بن على بن عبيد الله، الأعرج الحسينى العبيدلى، الملقب بشيخ الشرف. أصله من بغداد، وعاش فى الموصل. وعدّ فى عصره نسابة كبيرا. ويقال إنه عاش أكثر من مائة عام، وتوفى فى دمشق سنة ٤٣٧ هـ/ ١٠٤٥ م.

<span data-type="title" id=toc-1414>أ- مصادر ترجمته:</span>
الوافى بالوفيات للصفدى ١/ ١١٨، لسان الميزان لابن حجر ٥/ ٣٦٦، الذريعة ٤/ ٥٠٨ - ٥٠٩.
الأعلام للزركلى ٧/ ٢٤٥ - ٢٤٦، معجم المؤلفين لكحالة ١١/ ٢٤٦، بروكلمان ملحق ١/ ٢١٢.

<span data-type="title" id=toc-1415>ب- آثاره:</span>
«تهذيب الأنساب ونهاية الأعقاب»:
اختصره المؤلف من كتابه الكبير «الكامل فى أنساب آل أبى طالب» (١٩٠)، ليدن ٩١١ (١٦٩ ورقة، انظر: فورهوف ١٥٠ مع إضافات، وقد يكون هذاالمختصر من «تهذيب الأنساب» لأبى عبد الله الحسين بن محمد بن القاسم بن على طباطبا، انظر: الذريعة ٤/ ٥٠٨)، النجف، مكتبة محمد الجواد الجزائرى (٩٦٩ هجرية، غير كامل، انظر: الذريعة ٤/ ٥٠٩).
_________
(١٩٠) يحذف ما عند بروكلمان ويصحح.','2','63',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (536,'<span data-type="title" id=toc-1416>الفصل الثالث سيرة الرسول صلّى الله عليه وسلم</span>
<span data-type="title" id=toc-1417>أولا: العصر الأموى</span>
سبق أن أوضحنا بقدر من التفصيل، (ص ٢٥١ وما بعدها) أن أقدم وأبسط ما دوّن عن حياة الرسول صلّى الله عليه وسلم هو ما نجده عند متأخرى الصحابة، وفى الجيل التالى لهم مباشرة، أى عند قدامى التابعين ظهرت سيرة الرسول بالمعنى الحقيقى للكلمة فى كتب كبيرة نسبيا باسم «المغازى». وقد عرفت هذه الكتب فى وقت مبكر باسم «السيرة». وربما يكون الزّهرى أول من استخدم كلمة «السيرة» مصطلحا لذلك (انظر ترجمة الزهرى). وتعد السيرة من أقدم أشكال التدوين التاريخى عند المسلمين، ويبدو أن عددا من المؤلفين فى القرن الأول الهجرى قد اهتم بالتأليف فيها. ونستطيع اعتمادا على القطع التى نعرفها من كتب المغازى أن نذكر المؤلفين التالين:

<span data-type="title" id=toc-1418>١ - سعيد بن سعد بن عبادة الخزرجى</span>
يعدّه أكثر المؤلفين صحابيا كأبيه، وكان أبوه قد عرف فى الجاهلية بثقافته وخلقه، فلقّب لذلك بالكامل. وتقول بثض المعلومات التى وصلت إلينا بأن سعيدا ولد فى حياة الرسول صلّى الله عليه وسلم ولكنه لم يلتق به. ويبدو أن سعيدا من أوائل من دونوا أشياء عن حياة الرسول. صلّى الله عليه وسلم وربما نظر فيها معدلا ما كتبه أبوه.
وكان كتابه موجودا فى نسخته الأصلية فى أوائل العصر العباسى عند حفيده سعيد بن عمرو (انظر: التهذيب لابن حجر ٤/ ٦٩)، ويبدو أن قسما مما كتب قد وصل إلينا فى كتب المساند مثل مسند ابن حنبل (٥/ ٢٢٢)، وأبى عوانة (انظر: الإصابة','2','65',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (537,'٢/ ١٢٢٣)، وقد ذكره أبو عوانة بعبارة: «وجدت فى كتاب سعد بن سعيد بن عبادة .. ». كما وصلت إلينا نصوص منه- فيما يبدو- عند الطبرى ١/ ١١١، ١١٢ وغير ذلك. ولا نعلم عام وفاته.
غير أن ابنه شرحبيل، وهو أحد مؤلفى «المغازى» قد توفى- فيما يقال- وقد ناهز المائة عام سنة ١٢٣ هـ/ ٧٤٠ م.

<span data-type="title" id=toc-1419>أ- مصادر ترجمته:</span>
الطبقات لابن سعد (بيروت) ٥/ ٨٠ - ٨١، المحبّر لابن حبيب ٤٢٣، ٤٣١، ٤٣٢، المعارف لابن قتيبة ١٣٢، الجرح والتعديل لابن أبى حاتم ٢/ ١/ ٢٤ - ٢٥.

<span data-type="title" id=toc-1420>٢ - سهل بن أبى حثمة</span>
هو سهل بن أبى حثمة المدنى الأنصارى ويكنى أبا يحيى أو أبا محمد، ولد سنة ٣ هـ/ ٦٢٤ م. ويبدو أنه من شباب الصحابة الذين دونوا عن حياة الرسول ومغازيه.
ويتضح من بعض القطع التى وصلت إلينا فى جلاء أن حفيده أو حفيد حفيده محمد ابن يحيى بن سهل- الذى روى عنه الواقدى كثيرا- كان يملك نسخة مما دونه. ولما كان محمد بن يحيى بن سهل هذا لم يحصل على إجازة بروايتها كان يقول: «وجدت فى كتاب آبائى» (انظر: الإصابة ١/ ٦٣٠ - ٦٣١، الطبقات لابن سعد- بيروت- ١/ ٣٣٢). وربما نستطيع أن نجد قسما كبيرا من هذا فى المخطوط الكامل لكتاب الواقدى (انظر: المغازى ٩٥، ١٠٨ - ١٠٩، ١٩٧). وقد وصلت إلينا منه بعض قطع (انظر أنساب الأشراف للبلاذرى ١/ ٥٠٩، تاريخ الطبرى ١/ ١٢٦٤، ١٦٠٩، ١٧٥٧، ١٧٨٢، ١٧٨٣، الطبقات لابن سعد- بيروت- ١/ ٣٣٢، ٤٨٩، ٤٩٠، ٥٠٢، ٣/ ٢١٢) ويروى أنه توفى فى عهد الخليفة معاوية (٤١ هـ/ ٦٦١ م-','2','66',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (538,'٦٠ هـ/ ٦٨٠ م) وروى عن محمد بن سهل ابنه محمد، وابن أخيه محمد بن سليمان، وبشير بن يسار الأنصارى (الذى روى عنه ابن إسحاق، انظر: التهذيب لابن حجر ١/ ٤٧٢)، ونافع بن جبير بن مطعم، وعروة، ولم يستطع الزّهرى أن يروى عنه رواية مباشرة، ولذا فقد كانت رواياته عنه من المرسل.

<span data-type="title" id=toc-1421><span data-type="title" id=toc-1424>أ- مصادر ترجمته:</span></span>
الجرح والتعديل لابن أبى حاتم ٢/ ١/ ٢٠٠، الإصابة لابن حجر ٢/ ٢٧٦، التهذيب لابن حجر ٤/ ٢٤٨ - ٢٤٩.

<span data-type="title" id=toc-1422>ب- آثاره:</span>
وتوجد أحاديثه فى مسند ابن حنبل ٣/ ٤٤٨، ٤/ ٢ - ٣.

<span data-type="title" id=toc-1423>٣ - سعيد بن المسيّب</span>
هو أبو محمد، سعيد بن المسيّب بن حزن، المخزومى، ولد سنة ١٣ هـ/ ٦٣٤ م كان نسابة ومؤرخا ومحدثا وفقيها. كان يصدر أحكامه الفقهية اعتمادا على أحكام عمر بن الخطاب، ولذا سمى: «راوية عمر»، ومن تلاميذه: الزّهرى، وقتادة، وحفيدا عمر بن الخطاب: محمد بن عبد الله وسالم. وتوفى بالمدينة سنة ٩٤ هـ/ ٧١٣ م.
أ- مصادر ترجمته:
الطبقات لابن سعد (بيروت) ٥/ ١١٩ - ١٤٣، الحيوان للجاحظ ٣/ ٢١٠، الجرح والتعديل لابن أبى حاتم ٢/ ١/ ٥٩ - ٦١، حلية الأولياء لأبى نعيم ٢/ ١٦١ - ١٧٥، التهذيب لابن حجر ٤/ ٨٤ - ٨٨، الأعلام للزركلى ٣/ ١٥٥.','2','67',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (539,'<span data-type="title" id=toc-1425>ب- آثاره:</span>
ويتضح من القطع التى وصلت إلينا عند الطبرى، أن الطبرى، أفاد على نحو مباشر، مما كتبه سعيد بن المسيب عن حياة الرسول- دون مغازيه- ومما كتبه عن الفتوح.

<span data-type="title" id=toc-1426>٤ - عبيد الله بن كعب</span>
هو أبو فضالة عبيد الله بن كعب بن مالك الأنصارى، أحد قدامى التابعين. لا نعلم سنة ميلاده، وقد توفى سنة ٩٧ هـ/ ٧١٥ م أو ٩٨ هـ. روى عن أبيه، وروى عنه/ الزهرى وأخوه معبد وغيرهم. ولقد وصفه محمد بن إسحاق بأنه أحد كبار علماء الأنصار (انظر تاريخ الطبرى ١/ ١٣٦٤)، واعتمد عليه باعتباره أحد مؤلفى المغازى المرموقين. ويبدو من القطع الباقية عند الطبرى (١/ ١٣٦٤، ١٤٦٣، ١٤٧٩، ١٠٥١ - ١٥٠٨، ١٥٠٩ - ١٥١٠) أن كتابه فى المغازى كان صغيرا. وقد أفاد منه ابن إسحاق- كما يبدو عند الطبرى- برواية راو لم يذكر اسمه، وقد قدم لأخباره بالإسناد التالى: «عمن لا يتّهم عن عبيد الله .... ».

<span data-type="title" id=toc-1427>أ- مصادر ترجمته:</span>
الطبقات لابن سعد (ليدن) ٥/ ٢٠١، (بيروت) ٢٧٣، الجرح والتعديل لابن أبى حاتم ٢/ ٢/ ٣٣١ - ٣٣٢، التهذيب لابن حجر ٧/ ٤٤ - ٤٥، تقريب التهذيب لابن حجر ١/ ٥٣٨، فيشر:
التراجم A.Fischer ,Biographien ٣٠.٣٠

<span data-type="title" id=toc-1428>٥ - الشّعبى</span>
هو أبو عمرو، عامر بن شراحيل، الشّعبى، ولد بالكوفة سنة ١٩ هـ/ ٦٤٠ م، فيما','2','68',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (540,'بعد اتصل بعبد الملك بن مروان. كان محدثا وعالما فى الفقه والمغازى عارفا بالشعر راوية له. كان سفير عبد الملك بن مروان إلى ملك الروم، وعينه عمر بن عبد العزيز قاضيا. وتوفى سنة ١٠٣ هـ/ ٧٢١ م.

<span data-type="title" id=toc-1429>أ- مصادر ترجمته:</span>
الطبقات لابن سعد (ليدن) ٦/ ١٧١ - ١٧٨، (بيروت) ٢٤٦ - ٢٥٦، المعارف لابن قتيبة ٢٢٩، تاريخ بغداد للخطيب ١٢/ ٢٢٧ - ٢٣٣، سمط اللآلى للبكرى ٧٥١، حلية الأولياء لأبى نعيم ٤/ ٣١٠ - ٣٣٨، التهذيب لابن عساكر ٧/ ١٣٨ وما بعدها، وفيات الأعيان لابن خلكان (بولاق) ١/ ٣٠٦ - ٣٠٧، تذكرة الحفاظ للذهبى ٧٩ - ٨٨ التهذيب لابن حجر ٥/ ٦٥ - ٦٩، تقريب التهذيب لابن حجر ١/ ٣٨٧، الأعلام للزركلى ٤/ ١٨ - ١٩، معجم المؤلفين لكحالة ٥/ ٥٤.
ذكرت له المصادر الآتية الكتب التالية: ١ - «المغازى»، انظر: تاريخ بغداد للخطيب ١٢/ ٢٣٠.
٢ - «الفرائض والجراحات»، انظر المرجع السابق ص ٢٣٢.
٣ - «الكفاية فى العبادة والطاعة»، (حول المقتبسات منه، انظر: كحالة فى الموضع السابق).
٤ - وله كذلك كتاب فى الفتوح أملاه من حفظه أمام قتيبة بن مسلم، انظر: تذكرة الحفاظ للذهبى ٨٦، وهناك قطع منه وصلت إلينا عند الطبرى (انظر: الفهرس ٢٧١) أخذها عن كتبه: «المبتدأ» و «المغازى» و «الفتوح».
٥ - «كتاب الشورى ومقتل عثمان» وتوجد قطعة كبيرة منه برواية عوانة بن الحكم- إسماعيل بن أبى خالد وذلك فى «شرح نهج البلاغة» لابن أبى الحديد ٩/ ٤٩ - ٥٨ (وقد يكون نص القطعة من كتابين مختلفين).

<span data-type="title" id=toc-1430>٦ - أبان بن عثمان بن عفّان</span>
كان مع عائشة وهو فى السادسة عشرة من عمره فى «وقعة الجمل» سنة ٣٦ هـ/ ٦٥٦ م، ولم يكن له دور سياسى يذكر. وعين سنة ٧٥ هجرية واليا على المدينة (انظر:','2','69',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (541,'الطبقات لابن سعد ٥/ ١١٢)، وعزله عبد الملك سنة ٨٣ هـ (انظر: تاريخ الطبرى ٢/ ١١٢٧). وتتفاوت الروايات حول عام وفاته بين ٩٦ هـ، ١٠٥ هـ ويعدّ أبان بن عثمان من فقهاء المدينة (انظر: التهذيب لابن حجر ١/ ٩٧) / وهو من أقدم من ألفوا كتبا فى المغازى (انظر: مقال تسترستين فى دائرة المعارف الإسلامية الطبعة الأوربية الأولى المجلد الأول ص ٥) والأخبار الخاصة بها (انظر: مقال يوسف هوروفتس Horovitz ,Isl.Cult.١ /١٩٢٧ /٥٣٦ (وروى عنه المغيرة بن عبد الرحمن (الطبقات لابن سعد ٥/ ١٥٦، انظر Fuck: فك فى كتابه عن محمد بن إسحاق بالألمانية ص ٨)، لم يقتبس عنه من المؤلفين فى المغازى أو المؤرخين إلّا اليعقوبى (١/ ٣). وعلى العكس من هذا فقد روت عنه كتب الحديث، (انظر: علم التاريخ للدورى ٢١).

<span data-type="title" id=toc-1431>٧ - عروة بن الزّبير</span>
هو عروة بن الزبير بن العوام الأسدى، ولد بين عام ٢٣ هـ/ ٦٤٣ م، ٢٩ هـ/ ٦٤٩ م. كان حفيد أخ لخديجة، وهو أخو عبد الله بن الزبير وكان أصغر من عبد الله بثلاثين عاما تقريبا، ولم يشترك فى وقعت الجمل. عاش فى مصر من سنة ٥٨ هـ إلى سنة ٦٥ هـ، وكان إلى جانب أخيه عبد الله بن الزبير عند ما حاصر الأمويون مكة المكرمة. وبعد هزيمة عبد الله بن الزبير اتجه عروة إلى عبد الملك بن مروان، وتوفى سنة ٩٤ هـ ويعدّ عروة أحد فقهاء المدينة السبعة. ولقد روى هشام بن عروة أن أباه أحرق عددا من كتب الفقه فى يوم الحرّة سنة ٦٣ هـ، ثم ندم على ذلك ندما شديدا (انظر: الطبقات لابن سعد ٥/ ١٣٣). كان محدثا علم تلاميذه الحديث وقدم لهم أيضا معارفه عن حوادث صدر الإسلام. وقد وصلت إلينا بعض كتب له فى كتب ابن إسحاق والواقدى والطبرى، ولنا أن نعدها أقدم ما وصل إلينا مدونا عن سيرة الرسول. أما الخبر القائل بأن عروة ألف كتابا فى «المغازى» فليس له مصدر قديم','2','70',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (542,'(انظر: كشف الظنون لحاجى خليفة ١٧٤٧). وهناك قصة يتضح منها أن إجاباته عن سيرة الرسول- والتى كان يقدمها مدونة- إنما اعتمدت على الاحاديث التى جمعها هو بنفسه (انظر: مقال يوسف هوروفتس فى مجلة الحضارة الإسلامية (Horovitz ,Isl.Cult.١ /١٩٢٧ /٥٤٥ وذكر السخاوى فى الإعلان (ص ٨٨) أن الزهرى وأبا الأسود محمد بن عبد الرحمن بن نوفل (المتوفى سنة ١٣١ هـ/ ٧٤٨ م) قد رويا المغازى عن عروة (انظر: التهذيب لابن حجر ٩/ ٣٠٧ - ٣٠٨، وكذلك الإصابة لابن حجر ١/ ٢٥٥).

<span data-type="title" id=toc-1432>أ- مصادر ترجمته:</span>
الطبقات لابن سعد ٥/ ١٣٢، المعارف لابن قتيبة ١١٤ التاريخ الكبير للبخارى ٤/ ١/ ٣١ - ٣٢ حلية الأولياء لابى نعيم ٢/ ١٧٦ - ١٨٣. الوفيات لابن خلكان (القاهرة ١٢٩٩ هـ) ١/ ٣٩٨ - ٤٠٠.
التهذيب لابن حجر ٧/ ١٨٠ - ١٨٥. ساخاو فى تقديمه لتحقيق الطبقات لابن سعد (ليدن E.Sachau ,Einl.zu Ibn Sa''d III.S.XVIII. (الأعلام للزركلى ٥/ ١٧، علم التاريخ للدورى ٦١ - ٧٦.
وانظر ما كتبه فلهاوزن Wellhausen ,Skizzenund Vorarbeiten Vl ٤: : ومقالة يوسف هوروفتس فى مجلة الحضارة الإسلامية Horovitz ,Isl.Cult.١ /١٩٢٧ /٥٤٢: ومقال فتشيا فى دائرة المعارف الإسلامية V.Vacca ,El ,IV ,١١٣٣ - ١١٣٤ ١١٣٤ - ١١٣٣ /٤

<span data-type="title" id=toc-1433>ب- آثاره:</span>
وقد جمع كل من هوروفتس والدورى (فى المصدر السابق ٦٤ - ٧١، ١٣٧ - ١٤٢) / قطعا من آثاره، انظر كذلك الإصابة ٢/ ٨، ٨٢١. وحول رواته ألف مسلم: «رجال عروة ابن الزبير»: الظاهرية مجموع ٥٥ (الأوراق من ١٣٩ - ١٤٧، ٤٦٣ هـ، بخط الخطيب البغدادى، انظر: العش: ٢٢٥).
أما كتبه إلى عبد الملك بن مروان (انظر: تاريخ الطبرى ١/ ١١٨١، ١٢٨٤ - ١٢٨٨)، فقد ترجمها شبرنجر إلى اللغة الألمانية فى كتابه تاريخ وتعاليم محمد:
Springer, Leben I, ٣٥٦ ff, II, ٤٢ ff., III, ١٤٢ ff.','2','71',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (543,'<span data-type="title" id=toc-1434>٨ - شرحبيل بن سعيد</span>
كان أحد المؤلفين الأوائل فى ميدان المغازى، عرف على بن أبى طالب (انظر:
التهذيب لابن حجر ٤/ ٣٢٢). ويقال إنه توفى سنة ١٢٣ هـ بعد أن جاوز مائة عام.
وقد ذكر موسى بن عقبة (المتوفى ١٤١ هـ/ ٧٥٨ م) أن شرحبيل أعد قوائم بالمهاجرين وبمن اشتركوا فى وقعتى بدر وأحد (المصدر السابق ١٠/ ٣٦١). وعدّه سفيان بن عيينة (المتوفى ١٩٨ هـ/ ٨١٤ م) أحسن من عرف المغازى (المرجع السابق ٤/ ٣٢١).
ولم يرو عنه ابن إسحاق والواقدى، بينما أخذ عنه ابن سعد فى طبقاته (ليدن) ١/ ١/ ١٦٠ خبرا عن هجرة الرسول) انظر: مقال يوسف هوروفتس فى مجلة الحضارة الإسلامية عددا Horovitz ,Isl.Cult.١ /١٩٢٧ /٥٥٣.: (١٩٢٨)

<span data-type="title" id=toc-1435><span data-type="title" id=toc-1437>أ- مصادر ترجمته:</span></span>
الجرح والتعديل لابن أبى حاتم ٢/ ١/ ٣٣٨ - ٣٣٩، معجم البلدان لياقوت ١/ ٢٦٩، علم التاريخ للدّورى ٢٢.

<span data-type="title" id=toc-1436>٩ - القاسم بن محمد</span>
هو أبو محمد، القاسم بن محمد بن أبى بكر الصديق، ولد حوالى سنة ٣٧ هـ/ ٦٥٧ م. وكان فى عصره عالما عظيما مثل عروة، ويعد الشعبى والزهرى من رواته.
عمى فى أواخر أيامه، وتوفى سنة ١٠٧ هـ/ ٧٢٥ م.
أ- مصادر ترجمته:
الطبقات لابن سعد (بيروت) ٥/ ١٨٧ - ١٩٤، الجرح والتعديل لابن أبى حاتم ٣/ ٢/ ١١٨، حلية','2','72',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (544,'الأولياء لأبى نعيم ٢/ ١٨٣، صغة الصفوة للجوزى ٢/ ٤٩، نكت الهميان للصفدى ٢٣٠، التهذيب لابن حجر ٨/ ٣٣٣ - ٣٣٥، تقريب التهذيب لابن حجر ٢/ ١٢١، الأعلام للزركلى ٦/ ١٥.

<span data-type="title" id=toc-1438>ب- آثاره:</span>
يمكن تقسيم القطع التى ذكرها له الطبرى إلى مجموعات ثلاث:
١ - قطع حول سيرة النبى، ترجع إلى القاسم، أخذها الطبرى من كتاب لعبد الله بن وهب الفهرى (المتوفى ١٩٧ هـ/ ٨١٣ م) (انظر: تاريخ الطبرى ١/ ١٥١، ١٥٣، ١٧٤، ٣٥٠، ٥٢٨).
٢ - قطع مختلفة (انظر: المصدر السابق ١/ ١١٧٤، ١٧٥١، ١٨١٢) ٣ - وهناك خمسة وعشرون موضعا (انظر: فهرس تاريخ الطبرى ٤٥٧ من ص ١٨٧٣ حتى ص ٣١٠٦) بها بقايا كتاب فى المغازى، يتضمن بضع أخبار عن أبى بكر الصديق وعمر بن الخطاب وعثمان بن عفان، وعن وقعة الجمل. وترجع هذه القطع عند الطبرى إلى سيف بن عمر، وكان قد أفاد من كتاب القاسم برواية سهل بن يوسف السلمى. وهناك قطع أخرى نجدها عند الواقدى والبلاذرى.

<span data-type="title" id=toc-1439>١٠ - عاصم</span>
هو عاصم بن عمر بن قتادة المدنى أبو عمرو أو عمر، تابعى روى عن بعض الصحابة مثل جابر بن عبد الله وأنس بن مالك. وروى عنه ابنه الفضل، وروى عنه كذلك زيد بن أسلم وأبو الأسود (ربيب عروة) ومحمد بن إسحاق، وغيرهم، / كان عالما مشهورا بالمغازى. وأمره الخليفة عمر بن عبد العزيز بأن يقرأ فى الجامع الأموى قصص المغازى ومناقب الصحابة. وتوفى سنة ١٢٠ هـ/ ٧٣٧ م.

<span data-type="title" id=toc-1440>أ- مصادر ترجمته:</span>
المعارف لابن قتيبة ٢٣٦، الجرح والتعديل لابن أبى حاتم ٣/ ٣٤٦، الرجال للقيسرانى ٣٨٣، التهذيب لابن حجر ٥/ ٥٣ - ٥٤، الإصابة لابن حجر ٣/ ٤٤٧، انظر مقال هوروفتس فى مجلة الحضارة','2','73',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (545,'الإسلامية عدد Horovitz ,Isl.Cult.٢ /١٩٢٨ /٣١ - ٣٣.: (١٩٢٨) ٢ انظر ما كتبه فيشر عن التراجم تحت رقم A.Fischer ,Biographien ٢٢ .. ٢٢

<span data-type="title" id=toc-1441>ب- آثاره:</span>
هناك مقتبسات عند ابن إسحاق والواقدى برواية محمد بن صالح بن دينار، وعند ابن سعد نقلا عن الواقدى، وكذلك عند الطبرى (انظر: الفهرس ٣٠٣) وهذه مأخوذة- فى معظمها- عن المقتبسات الواردة عن ابن إسحاق وبعضها عن الواقدى.

<span data-type="title" id=toc-1442>١١ - الزّهرى</span>
هو أبو بكر، محمد بن مسلم بن عبيد الله بن عبد الله بن شهاب الزّهرى، ولد سنة ٥٠ هـ/ ٦٧٠ م، أو سنة ٥١ هـ، أو سنة ٥٦ هـ، أو ٥٧، أو ٥٨ هـ، فالمصادر تختلف فى تحديد عام ميلاده وتوفى سنة ١٢٤ هـ/ ٧٤٢ م. كان الزهرى محدثا، ومؤرخا عارفا بالشعر، وينسب إليه محدثا أنه قام بعملين هامين:
فهو أول من أسند الحديث (انظر: التقدمة لابن أبى حاتم ص ٢٠) وهو كذلك أول من دون الحديث (انظر: جامع بيان العلم لابن عبد البر ١/ ٧٣). ويتضح لنا من فحص سلاسل أسانيد الأحاديث أن اسم الزهرى- يحتل عند معظمهم فى الورود المكان الثانى بعد اسم الرسول. وهذا يجعلنا نقرر له دوره الكبير فى تاريخ الحديث.
ولم يكن الإسناد غير معروف عند من درس عليهم الزهرى، إلّا أنه اهتم بسلاسل الأسانيد لعدد كبير من الأحاديث التى لم يكن إسنادها ضروريا قبل ذلك. وكانت هذه الأحاديث مدونة أو تروى رواية شفوية منتشرة فى العالم الإسلامى آنذاك. وكان عليه وهو أحد التابعين أن يبحث عن أوائلالتابعين وكذلك عن الصحابة الذين أدركوا الرسول صلّى الله عليه وسلم وسمعوا أحاديثه منه أو كانوا أصحاب هذه الأحاديث التى كانت فى عصر الزهرى تتداول شفويا أو تحريريا. وكان ذلك ممكنا فى','2','74',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (546,'ذلك الوقت المبكر نسبيّا لرجل كالزهرى، وقد استطاع أن يكتب أسماء هؤلاء فى نصوصه، وأن يجعلها تروى بعد ذلك (انظر: سزجين فى كتابه عن مصادر البخارى (Sezgin ,Buh ,Kayn.S.٢٠ وفوق هذا فقد حضر إليه قوم بكراريس تضم أحاديث أرادوا أن يثبتوا لها أسانيد حتى يستطيعوا روايتها. ولما كان من الصعب عمليا أن تلبى كل الرغبات بشأن كل هذه النصوص للراغبين فيها أو الاستماع إلى مضمونها، فقد اتجه الزهرى إلى حل عملى. فكان يجيز للتلميذ أن يروى النص دون سماع/ على شيخ أو قراءة عليه، وهذا ما أطلق عليه فى علم أصول الحديث فيما بعد اسم «الإجازة والكتابة». وتذكر كتب الحديث بعض أمثلة لهذا توضح منهج الزهرى فى هذا (انظر: الكفاية فى علم الرواية للخطيب البغدادى ٣١٨). وقد أسئ فهم هذا إذا وجد «جولدتسيهر» فى ذلك دليلا على أن الزهرى أراد بذلك أن يتيح للحكام الأمويين إيجاد مادة دينية تخدم مصالح أسرة بنى أمية (انظر: جولدتسيهر فى كتابه الدراسات الإسلامية Goldziher ,Muh.Stud.II.٣٨: وقارن: هوروفتس فى المرجع السابق ص (Horovitz: ٧٤ ويرتبط بهذا اللبس خبر آخر أسئ فهمه كذلك، يقول الخبر: «كنا نكره كتاب العلم، حتى أكرهنا عليه هؤلاء الأمراء فرأينا ألا نمنعه أحدا من المسلمين» (الطبقات لابن سعد ٢/ ١٣٥، حلية الأولياء لأبى نعيم ٣/ ٣٦٣). وقد ترجم هذا النص ترجمة خطأ مؤداها: أنهم رغبوا عن تدوين العلم ولكن الأمراء أجبروهم على ذلك. (١٩١) وفى رأيى أن هذه العبارة معناها: رغبنا عن رواية الأحاديث بطريق الكتابة (١٩٢) أى بنسخ النصوص نسخا وروايتها دون أن تكون قد قرئت على شيخ
_________
(١٩١) هوروفتس، فى دائرة المعارف الإسلامية- الطبعة الألمانية ٤/ ١٣٤٢ - النهر الأيمن- الأسطر ١٣ - ١٥، جولدتسيهر: «لقد أجبر هؤلاء الأمراء الناس على تدوين الحديث Goldziher ,Muh.Stud.II ,٣٨ «انظر كذلك بحث شبرنجر فى Sprenger ,JASB ١٨٥٦ ,٣٢٢ ,Nr.٧١ وموير Muir.The Lifeof Mahomet.I.P.XXXIII على أن هوروفتس قد توصل فى مقاله فى Isl.Cult.٢ ,١٩٢٨ /٤٧ - ٤٨ إلى رأى أفضل.
(١٩٢) تدريب الراوى للسيوطى ص ١٤٦.','2','75',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (547,'أو سمعت منه حتى أجبرنا هؤلاء الأمراء على ذلك فقررنا ألا نحجب هذا عن أحد. (١٩٣) أما دوره الثانى فهو أنه: أول من دون الحديث، فالمقصود به أنه يمثل مرحلة تالية من مراحل تدوين الحديث، وليس المقصود أنه أول من أثبت الأحاديث فى صورة مكتوبة (انظر: هوروفتس فى: دائرة المعارف الإسلامية (الألمانية) ٤/ ١٣٤٢).
ويتضح من المعلومات الخاصة بأول تدوين للأحاديث، ومن البقايا التى وصلت إلينا منها: أن تدوين الأحاديث إنما يرجع إلى وقت مبكر، وأنه لم يكن على الزهرى إلا أن يجمع هذه النصوص المدونة المتناثرة فى كراريس مختلفة وأن ينظر فيها. وسبقه/ إلى ذلك أبو بكر بن حزم إذ كلفه عمر بن عبد العزيز بذلك. (١٩٤) وتدلنا عبارة تلميذ الزهرى وهو الليث بن سعد (ولد سنة ٩٢ هـ/ ٧١١ م وتوفى سنة ١٧٥ هـ/ ٧٩١ م. انظر كذلك ترجمته فيما يأتى من هذا الكتاب) وتثبت لنا ذلك فى وضوح، ونص العبارة: «يا أبا بكر لو وضعت للناس هذه الكتب ودونت فتفرغت».
فأجاب الزهرى: «ما نشر أحد من الناس هذا العلم نشرى» (انظر: تراجم رجال روى عنهم محمد بن إسحاق تحقيق فيشر لنصوص الذهبى (٦٩ Fischer ,Biographien. ونحن نعلم عن تلميذه معمر أن مجموعات الزهرى كانت فى مكتبة الأمويين قدر حمل عدد من الجمال (الطبقات لابن سعد (بيروت) ٢/ ١٣٦، هو روفتس (Horovitz ,Isl.Cult.II ,٤٨ ويقول الطبرى عن الزهرى إنه كان مؤرخا ورائدا فى «علم
_________
(١٩٣) تعيننا الحقيقة التاريخية على فهم المعنى الحقيقى لهذه الكلمة، ولم يكن المقصود بهذا إلا رغبة الخليفة هشام أن تعد لابنه نسخة لعدد من الأحاديث بنسخها كتّاب له (الذهبى، انظر (Horovitz ,P ,٤٧ ,Fischer ,Biographien ,S.٦٩:
(١٩٤) انظر: الطبقات لابن سعد (بيروت) ٢/ ١٣٤، ٨/ ٥٥٣ تاريخ البخارى ١/ ٣١، وجولدتسيهر، دراسات إسلامية Goldziher ,Muh.Stud.II ,٣٨: ٣٨ /٢ وسزكين، مصادر البخارى Sezgin ,Buh ,Kay.S.١٤: ١٤','2','76',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (548,'المغازى» وفى «أخبار قريش والأنصار». وهو كذلك راوية لأخبار الرسول والصحابة انظر: (المنتخب من ذيل المذيّل- طبعة الحسينية بالقاهرة ١٣٣٦ هـ، ص ٩٧، وكذلك علم التاريخ للدورى ٨١) وقد عرف الزهرى كذلك مصطلح السيرة (انظر: الأغانى طبعة ساسى ١٩/ ٥٩، وفوك فى كتابه عن محمد بن إسحاق (بالألمانية) ص ٢.

<span data-type="title" id=toc-1443>أ- مصادر ترجمته:</span>
التاريخ الكبير للبخارى ١/ ١/ ٢٢١، التاريخ الصغير للبخارى (ط الله آباد) ١٣٢٥ هـ، ٩٣، ١٠٤، ١٤٤، المعارف لابن قتيبة ٢٣٩ (القاهرة) ٤٧٢، معجم الشعراء للمرزبانى ٤١٣، الأغانى (بولاق) ٤/ ٤٨، وما بعدها، ٢٤٥ وما بعدها، حلية الأولياء لأبى نعيم ٣/ ٣٦٠ - ٣٦١، صفة الصفوة لابن الجوزى ٢/ ٧٧ - ٧٨، البداية والنهاية لابن كثير ٩/ ٣٤٠ - ٣٤٨، غاية النهاية لابن الجزرى ٢/ ٢٦٢، الكامل لابن الأثير ٧/ ٢٨٩، تاريخ الإسلام للذهبى ٥/ ١٣٦ - ٦٥٢، علم التاريخ للدورى ٢٠ - ٣٢، ٧٦ - ١٠٢، ١٤٣ - ١٥١، الاعلام للزركلى ٧/ ٣١٧، معجم المؤلفين لكحالة ١٢/ ٢١، بروكلمان ١/ ٦٥، ساخاو فى مقدمة تحقيق طبقات ابن سعد ج ٣/ ١، ج ١٣، ج ١٩.
Sachau, Einl. zu Ibn Sa''d III, XIII, XIX. Sachau, in: MSOSAS. VII, llff.
هوروفتيس، فى دائرة المعارف الإسلامية (الألمانية Horovitz ,EI ,IV ,١٣٤٢ - ١٣٤٣.١٣٤٣ - ١٣٤٢ /٤ (كتب عنه جولدتسيهر: فى مجلة المستشرقين الألمان Goldziher ,in: ZDMG ٥٠ /٤٧٤: كتب عنه ساخاو: فى مجلة الدراسات الشرقية لجامعة برلين Sachau ,in: MSOSAs.VII ,١١ ff. كتب عبد العزيز الدّورى دراسة باللغة الإنجليزية عن الزهرى وبدايات التدوين التاريخى فى الإسلام فى مجلة مدرسة الدراسات الشرقية والإفريقية
ABDAL''AZIZADDU RI, al- Zuhri: Astudyonthe Beginningsof Historywritingin Islam. In: BSOAS ١٩/ ١٩٥٧/ ١ - ١٢.

<span data-type="title" id=toc-1444>ب- آثاره:</span>
١ - «المغازى» ذكره حاجى خليفة (إستانبول) ص ١٧٤٧، وقال السخاوى إن الزهرى روى المغازى عن عروة (انظر: إعلان بالتوبيخ ٨٨، وانظر روزنتال Rosenthal ,History ٣٢٣ ويبدو أن البخارى قد أفاد من هذا الكتاب برواية موسى بن عقبة (انظر: صحيح البخارى ٥/ ٨٦، علم التاريخ للدورى ٧٩)، وقد ذكر','2','77',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (549,'السخاوى (فى المرجع السابق) كتاب «المغازى» رواية عن الحجاج بن أبى منيع (المتوفى بعد سنة ٢١٦ هـ/ ٨٣١ م، انظر: الطبقات لابن سعد، بيروت ٧/ ١٧٥، التاريخ الكبير للبخارى ١/ ٢/ ٣٨٠) وقد وصلت إلينا مقتبسات منه جمعها الدورى، انظر: المرجع السابق ٨٢ - ٩٢، ١٤٣ - ١٥١. وهناك إشارة إلى رواية الكتاب عن إسحاق بن إبراهيم تلميذ الزهرى (انظر: لسان الميزان لابن حجر ١/ ٣٤٧).
وتوجد له مخطوطة فى مجموعة، محفوظة بالظاهرية، مجموع ٩٢، ١٧٤ أ.
٢ - «نسب قريش»:
ذكر قرّة بن عبد الرحمن- وهو أحد معاصرى الزهرى-/ أنه لم يؤلف إلا هذا الكتاب (انظر:
فيشر Fischer ,Biographien ٦٩: ويبدو أن مصعب الزبيرى قد اعتمد فى كتابه: «نسب قريش» على كتاب الزهرى (انظر: نسب قريش للزبيرى ٣). ولذا فنكاد نقول بأنه قد وصلتنا فى هذا الكتاب مقتبسات كثيرة منه (انظر: الجمهرة لابن حزم ٥، وانظر كذلك الإصابة ١/ ١٠٢١).
٣ - «أسنان الخلفاء» هو سجل زمنى، احتفظ الطبرى لنا فى تاريخه ٢/ ٤٢٨ بقطعتين منه.
٤ - «الناسخ والمنسوخ فى القرآن» هذبه الحسين بن محمد اللّمى (المتوفى سنة ٤١٢ هـ ١٠٢١ م، انظر ترجمته فى الجزء الخاص بالتصوف من هذا الكتاب) ويوجد مخطوطا فى بايزيد ٤٤٥ (١٦ ورقة، القرن السابع الهجرى انظر: فهرس معهد المخطوطات ١/ ٤٨)، برنستون، مجموعة يهوذا ٢٢٨/ ٢.
٥ - «أحاديث» رواها أبو محمد الحسن بن على بن محمد بن الحسن الجوهرى (المتوفى سنة ٤٥٤ هـ/ ١٠٦٢ م)، ويوجد مخطوطا فى ليبزج ٣٢٠/ ٢ (من ٤١ أ- ١٢٣ ب، ٤٥٤ هـ)، ومنه مختارات فى الظاهرية، مجموع ٨٣/ ١٠، انظر: الإصابة ١/ ١٦٦، ٢/ ١٢١، ٧٢٥، ٨٢١، ٣/ ١٥١.
٦ - «تنزيل القرآن» برنستون، مجموعة يهوذا ٢٢٨ - حققه صلاح الدين المنجّد، بيروت ١٩٦٣ م.','2','78',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (550,'٧ - «مشاهد النبى» رواه يونس بن يزيد الأيلى (المتوفى سنة ١٥٩ هـ/ ٧٧٥ م)، انظر: (التاريخ الكبير للبخارى ٤/ ٢/ ٤٠٦) ذكره السخاوى فى الإعلان بالتوبيخ ص ٨٨، ويبدو أن الطبرى قد احتفظ ببعض مقتبسات منه.
٨ - وله مقطوعة من أربعة أبيات ترجع إلى عبد الملك بن مروان، ذكرها المرزبانى فى معجم الشعراء ٤١٣.

<span data-type="title" id=toc-1445>١٢ - السّبيعى</span>
هو أبو إسحاق عمرو بن عبد الله، السبيعى الهمدانى، ولد سنة ٣٢ هـ/ ٦٥٣ م.
عاش فى الكوفة، يقال إنه سمع من ٣٨ صحابيا، وكان مشهورا بمعرفة المغازى (انظر:
فتوح مصر للواقدى، سراى أحمد الثالث ١٣٨٩، ٩ ب) وتوفى سنة ١٢٧ هـ/ ٧٤٥ م.

<span data-type="title" id=toc-1446>أ- مصادر ترجمته:</span>
الطبقات لابن سعد (ليدن) ٦/ ١٢٣ (بيروت) ١٧٨، المعارف لابن قتيبة ٢٢٩، الجرح والتعديل لابن أبى حاتم ٣/ ٢٤٢، تذكرة الحفاظ للذهبى ١١٤ - ١١٦ التهذيب لابن حجر ٨/ ٦٣ - ٦٧، تقريب التهذيب لابن حجر ٢/ ٧٣، الأعلام للزركلى ٥/ ٢٥١.

<span data-type="title" id=toc-1447>ب- آثاره:</span>
يتضح من القطع التى وردت عنه عند الطبرى (انظر: فهرس تاريخ الطبرى: ٣٢) أنه استخدم كتبه فى المغازى والفتوح استخداما مباشرا، ويبدو أن الواقدى كذلك أخذ عنه نصوصا كثيرة (انظر:
المعارف لابن قتيبة ٩١، ١٠٦).

<span data-type="title" id=toc-1448>١٣ - يعقوب بن عتبة</span>
هو يعقوب بن عتبة بن المغيرة الثقفى المدنى، كان معاصرا للزهرى، عارفا بسيرة الرسول، صلّى الله عليه وسلم توفى سنة ١٢٨ هـ/ ٧٤٥ م.','2','79',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (551,'<span data-type="title" id=toc-1449>أ- مصادر ترجمته:</span>
التهذيب لابن حجر ١١/ ٣٩٢، تقريب التهذيب لابن حجر ٢/ ٣٧٦، وانظر: تراجم رجال روى عنهم محمد بن إسحاق للذهبى، تحقيق فيشر A.Fischer ,Biographien ٨٧ - ٨٨.

<span data-type="title" id=toc-1450>ب- آثاره:</span>
يتضح لنا من القطع التى وصلت إلينا عنه عند الطبرى (انظر فهرس تارخى الطبرى ص ٦٤٦) أن كتابه فى «السيرة» لم يكن يتضمن «المغازى» ويبدو كذلك أنه لم يكن يذكر رواة أخباره إلا نادرا. وقد أخذ الطبرى مقتبساته عن يعقوب عن طريق ابن إسحاق- وهناك قطعتان ذكرهما الطبرى فى تاريخه ١/ ٢٥٢٩، ٢٨٩٤، يبدو أنهما مأخوذتان عن كتاب للواقدى.

<span data-type="title" id=toc-1451>١٤ - عبد الله بن أبى بكر</span>
هو عبد الله بن أبى بكر بن محمد بن عمرو بن حزم المدنى، ولد سنة ٥٦ أو ٦٠ هـ/ ٦٧٩ م فى المدينة، وروى عن أبيه (انظر مقدمة علم الحديث فى هذا الكتاب) وكان أبوه مؤرخا ومحدثا وفقيها. وروى كذلك عن الصحابى أنس بن مالك وغيره، وروى عنه مالك وابن جريج وهشام بن عروة وغيرهم. وكان عبد الله مؤرخا ومحدثا مثل أبيه ولكنه ظل فى منطقة الظل أثناء حياة أبيه فقد طغت شخصيته الممتازة عليه. وأخذ من القطع التىوصلت إلينا عند ابن إسحاق والواقدى وابن سعد والطبرى وغيرهم ما لاحظه هوروفتس أن عبد الله مؤلف كتاب «المغازى» الذى رواه فيما يبدو ابن أخيه عبد الملك بن محمد القاضى (المتوفى ١٧٦ هـ/ ٧٩٢ م). وقد ألف هذا- بدوره- فى «المغازى» كذلك (انظر: الفهرست لابن النديم: ٢٢٦). وروى عبد الله كذلك بعض كتب الرسول إلى معاصريه، ومنها كتبه إلى ملوك حمير (انظر:
تاريخ الطبرى ١/ ١٧١٧ - ١٧٢٠). وتوفى عبد الله ١٣٠ هـ/ ٧٤٧ م أو ١٣٥ هـ','2','80',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (552,'<span data-type="title" id=toc-1452><span data-type="title" id=toc-1454>أ- مصادر ترجمته:</span></span>
الطبقات لابن سعد (بيروت) ٧/ ٦٨، التاريخ الكبير للبخارى ٣/ ١/ ٥٤، التهذيب لابن حجر ٥/ ١٦٤ - ١٦٥، هوروفتس Horovitz ,Isl.Cult.٢ /١٩٢٨ /٢٢ - ٣١.

<span data-type="title" id=toc-1453>١٥ - يزيد بن رومان</span>
هو يزيد بن رومان الأسدى المدنى، أبو روح، مولى آل الزبير بن العوام، عاصر متأخرى التابعين ولكنه لم يرو عن الصحابة. كان محدثا ومؤلفا فى المغازى، وكان يعتمد فى المقام الأول على عروة والزهرى. وروى عنه محمد بن إسحاق، ومالك بن أنس وهشام بن عروة وغيرهم. ويبدو أن كتابه فى المغازى قد وصل إلى الواقدى فى معظمه برواية محمد بن صالح بن دينار (المتوفى سنة ١٦٨ هـ/ ٧٨٤ م)، وكان أيضا مؤلف كتاب فى المغازى انظر: (التهذيب: لابن حجر ٩/ ٢٢٥ - ٢٢٦) وبذلك وصل إلينا فى الطبقات لابن سعد. وتوفى ١٣٠ هـ/ ٧٤٧ م.
أ- مصادر ترجمته:
الرجال للقيسرانى ٥٧٣، غاية النهاية لابن الجزرى ٢/ ٣٨١، تاريخ الإسلام للذهبى ٥/ ١٨، التهذيب لابن حجر ١١/ ٣٢٥، الأعلام للزركلى ٩/ ٢٣٤، انظر: تراجم رجال روى عنهم محمد بن إسحاق للذهبى تحقيق فيشر A.Fischer ,Biographien ٨٤.:

<span data-type="title" id=toc-1455>ب- آثاره:</span>
هناك مقتبسات منه عند الطبرى (انظر فهرس تاريخ الطبرى ص ٦٤١) أخذها عن ابن سعد والواقدى وابن إسحاق.

<span data-type="title" id=toc-1456>١٦ - أبو الأسود</span>
هو أبو الأسود محمد بن عبد الرحمن بن نوفل بن الأسود الأسدى، ربيب عروة','2','81',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (553,'بن الزبير، كان تابعيا فى عمر الزهرى. ويبدو أنه لم يرو عن صحابى قط، وكان عروة فى مقدمة شيوخه. روى عنه الزهرى وعبيد الله بن أبى جعفر- مؤرخ مصر- وعبد الله ابن لهيعة وشعبة والليث وغيرهم. وتعتبر مروياته/ موضع ثقة، أما كتابه فى «المغازى» فيمكن بحثه بحثا دقيقا اعتمادا على ما وصل ألينا من اقتباسات فى الإصابة لابن حجر، وهو يقوم فى معظمه على عروة. وتوفى سنة ١٣١ هـ/ ٧٤٨ م أو ١٣٧ هـ

<span data-type="title" id=toc-1457>أ- مصادر ترجمته:</span>
التقدمة لابن أبى حاتم ١٢٠، التهذيب لابن حجر ٩/ ٣٠٧ - ٣٠٨.

<span data-type="title" id=toc-1458>ب- آثاره:</span>
توجد مقتبسات من كتابه فى «المغازى»، ذكرها البلاذرى فى: أنساب الأشراف ١/ ١١٢، ٣٥١، والطبرى ١/ ١١٦٧، ١١٦٩، ١٢٦٤. وربما كان هذا الكتاب برواية مصعب بن ثابت، وندين لابن حجر بما حفظ لنا من هذا الكتاب فى كتابه الإصابة: ١/ ٣٢٣، ٤٠٥، ٤٣٢، ٤٤٨، ٤٥٩، ٤٧١، ٥٦٤، ٥٦٧، ٥٧٤، ٥٩٥، ٥٩٦، ٥٩٧، ٦٠٨، ٦٠٩، ٦٥٩، ١٠١٣، ١٠١٥، ١٠٣٠، ١٠٥٧، ١٠٦١، ١٠٦٢، ٢/ ١٣٤، ١٤٣، ١٨٧، ١٨٩، ٢٠٠، ٢٤٥، ٢٨٣، ٦٣٥، ٧٣٠، ٧٩٧، ٨٥٧، ٨٥٩، ١٠٩٠، ١١٣٨، ١١٤٧، ١٢٨٢، ٣/ ٧١، ٥٠٢، ٥٨٣، ٦٠٥، ٧٨٧، ٧٩٥، ١١٧٠، ١١٧٤، ١٢٣٣، ١٢٤٥، ١٣٠٨، انظر أيضا:
الطبقات لابن سعد (بيروت) ١/ ٢١٣، ٢١٥/ ٣/ ١٣٦، ١٦٤.

<span data-type="title" id=toc-1459>١٧ - داود بن الحسين</span>
هو أبو سليمان دود بن الحسين الأموى، كان تلميذ عكرمة ونافع وغيرهما. وهو شيخ مالك وابن إسحاق. وكان مثل أستاذه عكرمة يميل إلى الخوارج. ضعف بعض المحدثين رواياته. واعتبرها بعضهم جديرة بالثقة. ويبدو أنه قصر اهتمامه على تدوين حياة الرسول وصحابته. وتوفى سنة ١٣٥ هـ/ ٧٥٢ م.','2','82',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (554,'<span data-type="title" id=toc-1460><span data-type="title" id=toc-1463>أ- مصادر ترجمته:</span></span>
المعارف لابن قتيبة ٢٣٢، علل الحديث لابن أبى حاتم ١/ ٣٨٠، الرجال للقيسرانى ١/ ١٢٩، التهذيب لابن حجر ٣/ ١٨١ - ١٨٢.

<span data-type="title" id=toc-1461><span data-type="title" id=toc-1464>ب- آثاره:</span></span>
أفاد عبد الله بن محمد بن عمارة الانصارى (المتوفى حوالى سنة ٢٠٠ هـ/ ٨١٥ م) من كتاب لأبى سليمان بخطه (انظر: الطبقات لابن سعد، بيروت ٣/ ٤٤٩). والمرجّح أن الواقدى أخذ هذا الكتاب برواية إبراهيم بن أبى حبيبة (انظر مثلا: المغازى ٥٣، ٦٦، ٧٤، ٨٧، ١٢١، ١٤١، ١٤٢، ١٤٤، ١٤٥، ١٤٦، ١٥١، ١٥٦، ١٦٢، الطبقات لابن سعد (بيروت) ١/ ١٥٣، ١٥٨، ٢٢٧، ٢٤١، ٢/ ٢٩٨، ٣/ ٥، ٤٨، ٦٤، ٩٧، ١٤١، ١٦٠، ٨/ ١٢، ٣٣، ١٥٩، ٢٠٥، أنساب الأشراف للبلاذرى ١/ ١٠٤، ١٢٠، ١٢٣، ١٩٧، ٢٥٣، الطبرى ١/ ١١٢٩). اما المقتبسات التى وصلت إلينا منه عند الطبرى (انظر: فهرس تاريخ الطبرى ١٧٦) فيبدو أنها أخذت عن طريق مؤلفات بن إسحاق والواقدى.

<span data-type="title" id=toc-1462>١٨ - أبو المعتمر</span>
هو أبو المعتمر سليمان بن طرخان التّيمى، ولد سنة ٤٦ هـ/ ٦٦٦ م، وروى عن الصحابى أنس بن مالك وعدد من قدامى التابعين مثل الحسن البصرى. وكان بصفة عامة موضع ثناء لدقة مروياته. وتوفى بالبصرة ١٤٣ هـ/ ٧٦٠ م.
أ- مصادر ترجمته:
الطبقات لابن سعد (بيروت) ٧/ ٢٥٢ - ٢٥٣، التاريخ الكبير للبخارى ٢/ ١/ ٢٢ - ٢٣، المعارف لابن قتيبة ٢٤٠، الجرح والتعديل لابن أبى حاتم ٢/ ١/ ١٢٤ - ١٢٥، التهذيب لابن حجر ٤/ ٢٠١ - ٢٠٣، الإعلان بالتوبيخ للسخاوى ٨٨.
ب- آثاره:
«كتاب المغازى» حصل الخطيب البغدادى فى دمشق على إجازة بروايته (انظر: مشيخة للخطيب البغدادى، فى:','2','83',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (555,'الظاهرية، مجموع ١٨، ١٢٦ ب)، ويبدو أن الطبرى قد أفاد منه بالرواية التالية: «حدّثنى يعقوب بن إبراهيم، قال: حدّثنا معتمر بن سليمان التّيمى، قال: حدثنا أبى»، انظر: / فهرس تاريخ الطبرى: ٢٥٠، ٥٦٠ عن ابنه معتمر، وهناك قطعتان مهمتان منه فى تاريخ الطبرى ١/ ٢٩٦٣ - ٢٩٦٥، ٣٠٠٦، ٣٠٠٧، وهناك مقتبسات منه فى صحيح البخارى وخصوصا فى المجلد الخامس (القسم الخاص بالمغازى).

<span data-type="title" id=toc-1465>١٩ - موسى بن عقبة</span>
هو أبو محمد موسى بن عقبة بن أبى عيّاش الأسدى، لا نعلم عام ميلاده. ولكنا نعلم أنه التقى وهو غلام سنة ٦٨ هـ/ ٦٨٧ م بعبد الله بن عمر فى طريقه حاجا إلى مكة (انظر: التهذيب لابن حجر ١٠/ ٣٦٢، وتاريخ الطبرى ٢/ ٧٨٢)، وعلى ذلك فإنه على أبعد تقدير ولد سنة ٥٥ هـ (انظر: هوروفتس (٥٦١ - ٤٦١/ ٨٢٩١، ٢ Horovitz ,Isl.Cult. كان تلميذ الزهرى وعاش فى المدينة. وكانت له فى مسجد الرسول حلقة علم يمنح فيها كذلك إجازاته العلمية. وكان جل اهتمامه- مؤرخا- متّجها إلى مغازى الرسول والخلفاء الراشدين. وقد دون كذلك أسماء المهاجرين إلى الحبشة وأسماء المشتركين فى بيعتى العقبة. ويبدو من بضع مواضع أنه ذكر الأمويين (انظر: الطبقات لابن سعد ٥/ ٢٨٣). وكان يعرض مادته التاريخية وفق السنين، وهو منهج يبدو لنا أنه كان قد استخدم قبل ذلك عند عدد من أسلافه، منهم: عبد الله بن أبى بكر بن حزم (المتوفى سنة ١٢٠ هـ/ ٧٣٧ م) انظر (Horovitz ,Isl.Cult.٢ /١٩٢٨) ٢٧ - ٢٨ ,١٦٧: وقلما كان يستشهد فى تاريخه بالشعر (انظر: الطبقات لابن سعد ٣/ ٢٤١). وتوفى سنة ١٤١ هـ/ ٧٥٨ م.

<span data-type="title" id=toc-1466>أ- مصادر ترجمته:</span>
الجرح والتعديل لابن أبى حاتم ٤/ ٢/ ١٥٥، تذكرة الحفاظ للذهبى ١٤٨، علم التاريخ للدّورى ٢٧، الأعلام للزركلى ٨/ ٢٧٦، معجم المؤلفين لكحالة ١٣/ ٤٣.','2','84',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (556,'<span data-type="title" id=toc-1467>ب- آثاره:</span>
«كتاب المغازى» قرظه الإمام مالك كثيرا (انظر: التهذيب لابن حجر ١٠/ ٣٦١). وقد اعتمد موسى اعتمادا أساسيا على الزهرى، غير أن موسى لم يكن قد تلقى فيما يبدو إجازة برواية «مغازى» الزهرى، فإنه كان يأخذ عنه مقدما لذلك بعبارة: قال ابن «شهاب» أو «زعم ابن شهاب» وذكره مرة واحدة بعبارة: «حدّثنى الزهرى» مشيرا بذلك الى اقتباس- أغلب الظن أنه- ليس من كتابه «المغازى» بل هو من كتاب آخر فى «المغازى» رواه الزهرى (انظر: هوروفتس، المرجع السابق ١٦٦). وقد استخدم كذلك كتب عبد الله بن عباس التى كان كريب مولى ابن عباس قد أودعها لديه، وكانت حمل بعير (انظر: الطبقات لابن سعد ٥/ ٢١٦، وهوروفتس: المرجع السابق ١٦٧). وقد روى كتابه فى «المغازى» ابن أخيه إسماعيل بن إبراهيم بن عقبة (المتوفى ١٥٨ هـ/ ٧٧٤ م، انظر: الطبقات لابن سعد ٥/ ٢٨٣ وانظر ايضا القطعة الباقية فى برلين). وقد استخدم ياقوت نسخة منه بخط أبى نعيم الأصفهانى (معجم البلدان ٤/ ١٠٠٨، وانظر كذلك ٣/ ٨٧٢). ويبدو أن أصل الكتاب قد ضاع ولم تبق منه نسخ. وهناك قطعة من نص منه هذبه يوسف بن محمد بن عمر بن قاضى شهبة (المتوفى ٧٨٩ هـ/ ١٣٨٧ م) فى برلين ١٥٥٤ (الأوراق ٧٤ - ٧٧، سنة ٧٨٢)، وقد حقق ساخاو هذه القطعة وترجمها إلى الألمانية Sachau ,in: SBBA ١١ /١٩٠٤ /٤٤٥ - ٤٧٠.: وكتب شاخت عن أهمية هذه القطعة:
J. Schacht, On Musab. Oqba''s K. al- M. in: Acta Or.: (Kopenhagen) ٢١/ ١٩٥٣/ ٢٨٨ - ٣٠٠
وانظر كذلك. Oriens ٧ /١٩٥٤ /١٥٤:
واختصره يوسف بن عبد الله بن عبد البر النّمرى (المتوفى سنة ٤٦٣ هـ/ ١٠٧١ م، انظر بروكلمان ١/ ٣٦٨) بعنوان «كتاب الدرر فى/ اختصار المغازى والسّير»، وقد وصلت إلينا قطعة منه فى «أمالى ابن معروف» محمد بن عبيد الله بن أحمد (المتوفى سنة ٣٩٠ هـ/ ٩٩٩ م، انظر: الوافى بالوفيات للصفدى ٤/ ٦ - ٧) ويوجد مخطوطا فى الظاهرية، حديث ٣٨٧ (من ٣١ ب- ٣٢ أ، فى القرن السابع الهجرى).
وقد استخدم ابن حجر نسخا مختلفة متباينة من كتاب المغازى. (انظر: الإصابة ٣/ ١٣٤٩) واحتفظ لنا بقطع تمثل قسما كبيرا منه: ١/ ٢٩، ٦١، ٨٣، ٩٥، ١٣٢، ١٧٤، ١٨٢، ١٩٠، ٣٢٣، ٣٧٧، ٣٨٧، ٣٩٩، ٤٠٢، ٤٠٤، ٤٠٥، ٤٠٨، ٤٢٤، ٤٢٩، ٤٣٢، ٤٤٨، ٤٥٩، ٤٧١، ٤٧٢، ٤٨١، ٥٦٥، ٥٦٧، ٥٨٧، ٥٩٤، ٥٩٥، ٥٩٧، ٦٠٩، ٦١١، ٦١٩، ٦٢٩، ٦٣٨، ٦٤٢، ٦٥٩، ٧٠٢، ٧٠٧، ٧٢٣، ٨٤٥، ٨٦٧، ٨٧١، ٨٧٥، ٩٣٦، ٩٤٢، ٩٤٣، ٩٤٤، ٩٩٥، ١٠٢٠، ١٠٢١، ١٠٣٠، ١٠٥١، ١٠٦٥، ٢/ ٣٠، ٣١، ٣٤، ٣٥، ٥٣، ٥٩، ١٠٥، ١١٣، ١١٩، ١٢٠، ١٢٦، ١٤٣، ١٤٥، ١٤٧، ١٥٢، ١٥٣، ١٥٧، ١٨٧،','2','85',NULL,NULL,'0');
