INSERT INTO [b143160] VALUES (747,'The XXIst Volumeofthe K. al- Agh: ed. R. E. Brunnow, Leyden ١٨٨٣.
وأعد له جويدى فهارس هجائية نقلها إلى العربية م مسعود- القاهرة ١٣٢٣ هـ:
I. Guidi, Tablesalphabetiq ues, Leiden ١٨٩٥ - ١٩٠٠.
وأما الطبعة الحديثة بالقاهرة فقد ظهر منها بدار الكتب حتى الآن ١٦ مجلدا، ١٩٢٣ - ١٩٦١، وظهرت نفس الأجزاء مصورة ١٩٦٤ - ١٩٦٥ م. ونشر عبد الستار أحمد فراج المجلد ١٥ - ٢٠ في بيروت ١٩٥٨ - ١٩٦٠ (٢٩٢).
مختارات كتاب الأغانى:
أ- «مختار الأغانى فى الأخبار والتهانى» لمحمد بن مكرم بن منظور (المتوفى سنة ٧١١ هـ/ ١٣١١ م انظر بروكلمان ٢/ ٢١)، جوتا ٢١٢٦ (٧٠٠ ورقة، ١١٤٨ هـ)، المتحف البريطانى ١٢٨٠ (٥٨٦ ورقة، ١١٧١ هـ)، جاريت/ ١٨٠ (يضم الأسماء التى تبدأ بالعين والغين، ٢١٥ ورقة)، كوبريلى ١٣٨٢ (ج ٢، ٢٢٨ ورقة، ٦٧٢ هـ بخط المؤلف)، ١٣٨٣ (ج ٣/ ١٢٢ ورقة، ٦٧٢ هـ، بخط المؤلف)، ١٣٨٤ (ج ٤/ ٢١٦ ورقة، ٦٧٢ هـ بخط المؤلف) ١٣٨٥ (ج ٧/ ١٩٣ ورقة، ٦٧٢ هـ بخط المؤلف)، ١٣٨٦ ج ٨/ ٢١٣ ورقة، ٦٧٢ هـ بخط المؤلف، انظر فهرس المخطوطات العربية ١/ ٥٢٢)، بلدية الإسكندرية ١/ ١١٥ رقم ١١٩٠ ب (٢/ ١٨٣ ورقة ٦٨٣ هـ، انظر فهرست معهد المخطوطات العربية ١/ ٥٢١)، الأزهر ٥/ ٢٤٢، الإضافات ٦٣٩ (٢/ ٥٥١ ورقة، ومنه مصورة فى دار الكتب بالقاهرة (٢٩٣) ثان ٤/ ٧٧ ب) ظهر منه المجلد الأول بالقاهرة، ١٩٢٧ م، (ظهرت الأجزاء الستة التالية بالقاهرة حتى ١٩٦٦).
ب- «تجريد الأغانى من المثالث والمثانى» لجمال الدين محمد بن سالم بن نصر الله بن واصل الحموى (المتوفى سنة ٦٩٧ هـ/ ١٢٩٧ م انظر بروكلمان ١/ ٣٢٢)، المتحف البريطانى ٥٧١ الإضافات ٧٣٣٩ (٣٤١ ورقة، ١١١٠ هـ)، أيا صوفيا ١٤٠٠ (ج ١: ٥٥٠ ورقة، ٦٦٦ - ٨ ومنه مصورة القاهرة ثان ٣/ ٤٣، انظر فهرست معهد المخطوطات العربية ١/ ٤٣٤)، متحف الأوقاف التركية ٢٠٠٧ (ج ٢/ ٢٩٧ ورقة، ٦٦٦ هـ)، رئيس الكتاب ٩٠٢ (٢٢١ ورقة القرن السابع الهجرى، كامل)، وحققه طه حسين، وإبراهيم الأبيارى فى ٣ مجلدات، القاهرة ١٩٥٥ - ١٩٥٩ م (٢٩٤).
ج- مختصر الأغانى لأبى الربيع سليمان بن عبد الله بن عبد المؤمن (المتوفى سنة ٦٠٤ هـ/ ١٢٠٧ م، انظر الأعلام للزركلى ٣/ ١٩٠، معجم المؤلفين لكحالة ٤/ ٢٦٧) الأوقاف بالرباط ١٥٤ (المجلد الأول، ٦٠٧ هـ).
_________
(٢٩٢) تتضمن نشرة عبد الستار فراج للأغانى كل أجزاء الكتاب، مع خلاف فى الترتيب الداخلى (المترجم) ولها فهارس كاملة تقع فى مجلدين كاملين.
(٢٩٣) نشرته دار الشعب بالقاهرة (١٩٦٦).
(٢٩٤) نشرته دار الشعب بالقاهرة (١٩٦٦).','2','285',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (748,'د- مختصر للحسين بن على (بن محمد يوسف) المغربى (المتوفى سنة ٤١٨ هـ/ ١٠٢٧ م انظر بروكلمان ١/ ٣٥٤)، باريس ٥٧٦٦، ٥٧٦٩ (انظر فايدا ٢٥٠).
هـ- بشير التهانى- لمؤلف مجهول: الجزائر ١٧٩٥ - ١٧٩٩ (٥ مجلدات من القرن الثالث عشر الهجرى، ومن المرجح أنه بخط مؤلفه).
٢ - «مقاتل الطالبيين»:
المتحف البريطانى الملحق ٢٥٢/ ١، مخطوطات شرقية ٣٩٨٨ (١٦٥ ورقة القرن الحادى عشر الهجرى)، برلين ٢٩٠٩، مكتبة جامعة إستنبول ٤١٧٠ (١٦٥ ورقة، ١٣٠٨ هـ)، أميرى ٢٢٥٥ (من ١ - ١٤٩ أ، ١٠٩٤ هـ)، كلكتا الجمعية الآسيوية الملكية فى البنغال ١٣٤٨ (٢١٠ ورقة، ١٠٦٤ هـ، انظر فهرس معهد المخطوطات العربية ٢ رقم ١٢٤٢، لا يوجد فى الكتالوج) القاهرة ثان ٨/ ١١، رقم ٥٥١٤ (٢٠٤ ورقة، ١٠٧٢ هـ)، مشكاة بطهران ١٠/ ١٦٠٥ رقم ٢٧٣٥ (١٧١ ورقة، ١٢٨٥ هـ)، وطبع فى النجف ١٣٥٣، وطبع كذلك على هامش «المنتخب فى المراثى والخطب» لفخر الدين النجفى بومباى ١٣١١ هـ، وطبع كاملا فى طهران ١٣٠٧ هـ، وحققه كذلك السيد أحمد صقر القاهرة ١٩٤٩ م.
٣ - «كتاب الخمارين والخمارات»:
منه قسم فى حوزة (تاجر الكتب) أحمد عبيد فى دمشق، انظر: الأعلام للزركلى ٥/ ٨٨ هامش ١.
٤ - «أدب الغرباء»:
موجود بطهران فى حوزة فيروزانفر ويعده للطبع صلاح الدين المنجّد فى بيروت.
ملاحظة: ذكر بروكلمان كتابا بعنوان «كشف الكربة» .. إلخ، والكتاب ليس له، فقط خلط بروكلمان هنا بين أبى الفرج الأصفهانى وأبى الفرج البغدادى (انظر بروكلمان ملحق ٢/ ١٣٠).

<span data-type="title" id=toc-1912>٢١ - ابن زنجى الكاتب</span>
هو أبو القاسم، إسماعيل بن محمد بن إسماعيل، المعروف بابن زنجى الكاتب، روى عن أبيه، وعن محمد بن خلف وكيع وغيرهما، وتوفى سنة ٣٧٨ هـ/ ٩٩٨ م.

<span data-type="title" id=toc-1913>أ- ترجمته فى:</span>
تاريخ بغداد للخطيب ٦/ ٣٠٨.','2','286',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (749,'<span data-type="title" id=toc-1914>ب- له كتب قد يكون منها:</span>
«كتاب الكتّاب»:
الذى يبدو أنه أحد المصادر الأساسية لكتاب «الوزراء» للصابى.

<span data-type="title" id=toc-1915>٢٢ - الخالديان</span>
الخالديان هما الأخوان أبو عثمان سعيد (المتوفى فى أواخر القرن الرابع الهجرى) وأبو بكر محمد بن هاشم بن وعلة (المتوفى حوالى سنة ٣٨٠ هـ/ ٩٩٠ م). وقد ألفا معا عددا من الكتب المختلفة، وأعدا مختارات من الشعر المحدث/ بعنوان: «حماسة شعر المحدثين» ولم تصل إلينا هذه المجموعة كما اختارا مجموعة من شعر الجاهليين والإسلاميين والمحدثين بعنوان «الأشباه والنظائر»، وأرادا بهذا أن يثبتا تفوّق الشعر الجاهلى والمخضرم.

<span data-type="title" id=toc-1916>أ- مصادر ترجمتهما:</span>
الفهرست لابن النديم ١٦٩، إرشاد الأريب لياقوت (لندن) ٤/ ٢٣٦ - ٢٣٧، (القاهرة) ١١/ ٢٠٨ - ٢١٢، فوات الوفيات للكتبى ١/ ١٧٠، اللباب لابن الأثير ١/ ٣٣٩، أعيان الشيعة للعاملى ٣٥/ ٩٩ - ١١٦، الخالديان لمحمد يوسف فى مجلة المجمع العلمى العربى بدمشق، ٢٥/ ١٩٥٠/ ٤٩ - ٦١ (•)، الأعلام للزركلى ٣/ ١٥٦، ٧/ ٣٥٢، معجم المؤلفين لكحالة ٤/ ٢٣٣، ١٢/ ٨٨، بروكلمان ١/ ١٤٧ تحت رقم ٢.

<span data-type="title" id=toc-1917>ب- آثارهما:</span>
١ - «الأشباه والنظائر»:
انظر: المجلد الثانى فى كتابنا وانظر بروكلمان ١/ ٢٠.
٢ - «التحف والهدايا»:
القاهرة ثان ٣/ ٤٤، أدب ٨٣ ش (غير كامل)، سراى، أحمد الثالث ٢٦١٨ (من ١٠٤ أ- ١٣٩ أ،
_________
(•) جمع سامى الدهان: ديوان الخالدين، دمشق مطبوعات مجمع اللغة العربية ١٩٦٩ - المترجم.','2','287',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (750,'٦٧٧ هـ)، كوبريلى ١٤٠٨ (٨٦ ورقة ١٠٦٧ هـ)، حكيم أوغلو ٩٤٦/ ١ (١ - ٣٧ أ، ١١١٥ هـ)، حققه سامى الدهان القاهرة ١٩٥٨.
٣ - «مختار شعر بشار بن برد»:
انظر المجلد الثانى من كتابنا وانظر بروكلمان ١/ ٧٣.
٤ - «كتاب الديارات»:
ذكره ياقوت فى إرشاد الأريب (لندن) ٢/ ٢٣ - ٢٨.
٥ - وصلت إلينا فى يتيمة الدهر للثعالبى (ط ثالثة) ٢/ ١٨٤ - ٢٠٨ قطع من قصائد لهما ادعى السرى الرفاء (انظر بروكلمان ١/ ٩٠) أنها مسروقة منه انظر ريتر فى أسرار البلاغة، وهناك قطعتان منها فى برلين ٧٥٦٢ (٢٩٤).
Ritter, Geheimnisse, ٢٣٤ - ٢٣٥.
_________
(٢٩٤) ليست مخطوطة برلين رقم ٧٥٦٧، ٣، ٤، كما ذكر بروكلمان.','2','288',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (751,'<span data-type="title" id=toc-1918>تصنيف العلوم</span>
إن التطور السريع للعلم، وتدوين آلاف الكتب فى مختلف مجالات التأليف وترجمة كثير من الكتب، ووجود آثار أخرى لثقافات أجنبية لا بد أنها دفعت العلماء المسلمين فى وقت مبكر إلى تصنيف العلوم. ومن الممكن أن نفترض أن هناك علاقة بين بواكير هذا وبداية تصنيف كتب الحديث وبين ظهور كتب الغريب المصنف بعد ذلك فى النصف الثانى من القرن الثانى الهجرى. إلّا أننا بسبب معلوماتنا القليلة حول تطور هذا الضرب من ضروب التأليف لا نستطيع أن نرسم صورة دقيقة، كما أننا ينقصنا فى الوقت الحالى تصوّر دقيق لمستوى العلم فى نهاية العصر الأموى عند ما ظهر واصل بن عطاء (المتوفى ١٣١ هـ/ ٧٤٨ م) مؤلفا لكتابه: «كتاب طبقات أهل العلم والجهل» (انظر وفيات الأعيان لابن خلكان «بولاق» ٢/ ٢٢٦).
وأقدم كتب نعرفها فى تصنيف العلوم المختلفة هى كتب من مرحلة تالية، مثل «كتاب فى أقسام العلم الإنسى»، «وكتاب فى ماهية العلم وأصنافه» ليعقوب بن إسحاق/ الكندى (انظر: الفهرست لابن النديم: ٢٥٦). ولم تصل إلينا هذه الكتب كما لم يصل إلينا كتاب أقسام العلوم لأبى زيد البلخى (المتوفى ٣٢٢ هـ/ ٩٣٤ م، انظر: بروكلمان الملحق ١/ ٤٠٨). ويبدو أن هذه الكتب المفقودة تشبه كتبا أخرى وصلت إلينا هى:
«كتاب إحصاء العلوم» للفارابى (المتوفى ٣٣٩ هـ/ ٩٥٠ م) و «مفاتيح العلوم» للخوارزمى انظر بروكلمان ملحق ١/ ٤٣٤، و «رسائل إخوان الصفاء» المجهولة المؤلف، و «كتاب جوامع العلوم» لابن فريعون، أحد تلاميذ أبى زيد البلخى (٢٩٥).
وفى القرن الرابع الهجرى ظهر اهتمام بربط تصنيف العلوم بالأخبار الخاصة بحياة المشتغلين بكل فرع منها. وينبغى البحث عن بدايات هذا الفرع من فروع التأليف عند الوراقين المجتهدين فى الجمع والتصنيف، وعند هواة الكتب فى القرنين الثالث
_________
(٢٩٥) انظر: ص ٣٣٨ من أصل هذا الكتاب وانظر ما كتبه ريتر Ritter ,Oriens ٣ /١٩٥٠ /٨٣ - ٨٥. وما كتبه روزنتال فى كتابه علم التاريخ عند المسلمين yrotsiH, lahtnesoE,٣٢ - ٣٤.: هكذا فى إرشاد الأريب ١٤/ ١٥٣ وفى الأصل الألمانى، أما فى تاريخ بغداد (١٢/ ١١) فاسمه (على بن عيسى الكوفى)، وفى إنباه الرواة: على بن محمد بن الزبير المعروف بابن الكوفى، ٢/ ٣٠٥ - المترجم.','2','289',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (752,'والرابع الهجريين. ويبدو أن أبا الحسن بن الكوفى وهو هاو للكتب، مغرم وعالم بها، كان مصدر كثير من الدوافع لذلك. وأعظم كتاب نعرفه منها هو كتاب «الفهرست» لابن النديم وهو لا يضم تصنيفا للعلوم وتراجم للعلماء فحسب، بل نجد فيه كذلك مادة خصبة من مختلف المكتبات. ولم يجد هذا العمل العظيم فى القرون التالية من يكمله ماضيا على نهجه أو ينقحه. فقد أبدلوا هذا الجهد بوضع قوائم لكتب بأعيانها كانوا قد تلقوا حق روايتها وإقرائها، وذلك مثل كتاب «الرجال» للنجاشى (المتوفى ٤٥٠ هـ/ ١٠٥٨ م انظر بروكلمان ملحق ١/ ٥٥٦) أو كتاب «فهرست ابن خير الأشبيلى» (المتوفى سنة ٥٧٥ هـ/ ١١٧٩ م، انظر بروكلمان ١/ ٤٩٩).

<span data-type="title" id=toc-1919>١ - ابن الكوفى</span>
هو أبو الحسن، علي بن محمد بن عبيد بن الزبير* الأسدى المعروف بابن الكوفى، ولد سنة ٢٥٤ هـ/ ٨٦٨ م فى الكوفة، وتتلمذ على ثعلب. كان من جماعى الكتب وأرباب الهوى فيها، جمع مكتبة كبيرة حافلة، تفرقت بعض مجلداتها فى العالم وكانت موجودة فى عصر القفطى، وياقوت. ويقال بأن ولعه بالكتب لم يدع له لتأليف الكتب إلّا وقتا قليلا. غير أن ابن النديم ذكر له ص ٧٩: كتابا فى «معانى الشعر واختلاف العلماء»، و «كتاب القلائد والفرائض فى اللغة والشعر». وقد اطلع ياقوت الحموى على نسخة بخط المؤلف من كتاب «الهمز» من تأليفه واعتمد عليه ابن النديم فى الفهرست فى مقالات مختلفة ولا سيما فيما يتعلق بالمؤرخين واللغويين الكوفيين (انظر الصفحات ٤، ٥٢، ٥٨، ٦٦، ٦٨، ٦٩، ٧٠، ٧١، ٧٢، ٧٤، ٧٨، ٨٩، ٩٥، ٩٦، ١٠٠، ١٠١ - ١٠٤، ١٠٧، ١٠٨ .. الخ، ١١١، ١١٢، ١٤١، ١٥٨) غير أنه من الصعب أن نعرف ما إذا كان ابن النديم قد نقل من كتبه المذكورة، أو استفاد من الملاحظات المختلفة/ المدونة على هامش كتب فى مكتبته الضخمة التى خلّفها، أو أن ابن النديم استخدم كراسا أو أكثر كانت به ملاحظات حول الكتب أو أنه- استخدم فهرسا لمكتبة ذلك الهاوى. فابن النديم يأخذ القائمة الطويلة','2','290',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (753,'لمؤلفات المدائنى (انظر ص ١٠١ - ١٠٤) من كتاب (بخط؟ ) ابن الكوفى. كما أخذ عنه مرة أخرى تراجم مجموعة من العلماء، أو على حد تعبير ابن النديم «طائفة أصبنا ذكرهم بخط ابن الكوفى» (انظر ص ١٠٨ وما بعدها). ويتضح من موضع فى الفهرست لابن النديم (ص ٣٥٨) أنه قرأ كراسا لابن الكوفى، به ملاحظات لغوية وأدبية وتاريخية وغير ذلك، وبآخره ملحق يضم جدولا بأشكال الحروف المختلفة، نسخة عن ابن وحشية. وعلى كل حال فليس لنا أن نخرج من هذه الاقتباسات كما خرج لبيرت Lippert بأن ابن الكوفى كان صاحب مؤلف فى تاريخ الكتب، استوعب كل مجالات التراث العربى، (Lippert ,WZKM ,XI ,١٤٧)، وتوفى سنة ٣٤٨ هـ/ ٩٦٠ م.

<span data-type="title" id=toc-1920>أ- مصادر ترجمته:</span>
تاريخ بغداد للخطيب البغدادى ١٢/ ٨١، إرشاد الأريب لياقوت (لندن) ٥/ ٣٢٦، ٣٢٨، (القاهرة) ١٤/ ١٥٣ - ١٥٦، إنباه الرواة للقفطى ٢/ ٣٠٥ - ٣٠٦، بغية الوعاة للسيوطى ٣٥٠، شذرات الذهب لابن العماد ٢/ ٣٧٩، الأعلام للزركلى ٥/ ١٤٢ - ١٤٣، معجم المؤلفين لكحالة ٧/ ٢١٣. وكتبه عنه ليبرت باعتباره ممهدا لابن النديم:
J. Lipper, Ibnal Kufi, ein Vorganger Nadims, WZKM ١١/ ١٤٧ - ١٥٥.','2','291',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (754,'<span data-type="title" id=toc-1921>٢ - ابن النّديم</span>
هو أبو الفرج، محمد بن أبى يعقوب إسحاق النديم الورّاق البغدادى. نكاد لا نعلم شيئا عن حياته، كما أننا لا نعرف سنة مولده، غير أنه يشير فى كتابه الفهرست ص ٢٣٧ إلى أنه قد ولد قبل سنة ٣٢٠ هـ/ ٩٣٢ م. وقد ذكر فى هذا الموضع، أنه تعرف على المعتزلى أبى بكر البردعى محمد بن عبد الله سنة ٣٤٠ هـ، وأنهما تصادقا وذكر أسماء بعض كتبه. كما أننا لا نعلم عام وفاته على نحو دقيق: ذكر ابن النجار (المتوفى ٦٤٣ هـ/ ١٢٤٥ م) أنه توفى سنة ٣٨٥ هـ/ ٩٩٥ م، (انظر الفهرست ص ١٢ الملاحظة رقم ٢)، وذكر الصفدى (فى الوافى بالوفيات ٢/ ١٩٧) أنه توفى سنة ٣٨٠ هـ/ ٩٩٠ م. أماالذهبى فلا يعرف تاريخا دقيقا لذلك، وجعل وفاته فى أوائل القرن الخامس الهجرى (انظر لسان الميزان لابن حجر ٥/ ٧٢). وتدل الأحداث المذكورة فى كتاب الفهرست من عام ١٣٩٢ هـ (انظر: ص ٨٧) وبعد سنة ٤٠٠ هـ (ص ١٦٩) على صحة هذا الفرض. على أنه من الممكن أن تكون هذه التعليقات بغير قلم المصنف وعلى كل حال فالمؤكد أن ابن النديم كان يؤلف الفهرست سنة ٣٧٧ هـ.
يقال إن ابن النديم ألف بجانب كتابه الفهرست كتابا آخر بعنوان «كتاب التشبيهات» (إرشاد الأريب لياقوت ٦/ ٤٠٨)، ولكننا لا نعلم شيئا عن/ هذا الكتاب.
وترجع مكانة ابن النديم إلى أنه أول من ألف تاريخا للتراث العربى قد يكون وحيدا فى بابه، ويتضح من المعلومات الواردة به ومن المصادر المختلفة للمقالات أن العرب قد اهتموا فى وقت مبكر بتسجيل كتبهم المؤلفة وتصنيفها تصنيفا موضوعيا بل وبترتيبها إلى حد ما وفى معايير التاريخ للتراث. فبواكير تأليف كتب الأغانى إنما','2','292',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (755,'ترجع مثلا إلى العصر الأموى. وكان اليعقوبى قد أرخ فى تاريخه لحركة الترجمة من اليونانية إلى العربية، قبل أن يؤلف ابن النديم كتابه بقرن كامل من الزمان (٢٩٦).
وقد أشار ابن النديم نفسه إلى هذه الجهود التى اعتمد عليها. ورغم هذا يبقى جهده عظيما، فهو صاحب أهم كتاب فى تاريخ التراث العربى وأكثرها شمولا. كان ابن النديم وراقا، وأتاح له ذلك أن يرى معظم الكتب التى ذكرها وأن يحاول تحديد قيمتها العلمية والمادية. ويجوز لنا هنا أن نثق بما يقوله من أنه رأى هذا الكتاب أو ذلك أو شاهد نسخة منه بخط المؤلف أو غير ذلك. لقد توافرت لديه إمكانيات مثل هذا العمل، فقد عرف الكثير وكان حجة ثقة وأتاحت له حرفته جمع الكثير من المادة. ورغم هذا كله فقد فاتته كثرة من مؤلفات نعرفها من أخبار كثيرة قديمة وردت عنها فى مصادر مبكرة وصل إلينا بعضها أيضا. هذا ويمكن أن نثبت أن بعض المعلومات التى جاء بها لم يخل من تناقض (٢٩٧)، قد يرجع إلى أنه أفاد من مصادر مختلفة، ولذا يجد الباحث نفسه مضطرا لأن يرفض بين الحين والحين ودون تردد زعما أو مقولة له (٢٩٨).
لقد اعتمد ابن النديم بالإضافة إلى كتاب أبى الحسن بن الكوفى الذى سبق ذكره، على مصادر أخرى كان يذكرها بين حين وآخر لم يصل إلينا أكثرها. فهو يعتمد فى التراجم كثيرا على أبى سعيد السيرافى (٢٩٩)، وأبى الفرج الأصفهانى (٣٠٠)، وعلى كتاب لأبى العباس ثعلب بخط أبى عبد الله الحسن بن على
_________
(٢٩٦) انظر ما كتبه كلامروت:
Klamroth ,ZDMGXL /١٨٩ - ٢٣٣ ;٦١٢ - ٦٣٨ ,XLI ٤١٥ - ٤٤٢. وانظر أيضا ما كتبه ليبرت J.Lippert ,WZKMXI ,١٤٧.:
(٢٩٧) انظر مثلا أخباره المتناقضة عن الكتب الأولى عن الدولة (انظر ما سبق ذكره ص ٣١٠ من الأصل الألمانى).
(٢٩٨) انظر مثلا أخباره عن حماد الراوية، سبق ذكره ص ٣٦٧ من الأصل الألمانى.
(٢٩٩) انظر الفهرست لابن النديم ٥٠، ٥٣، ٥٨ وقد اقتبس كتابه «أخبار النحويين».
(٣٠٠) انظر الفهرست لابن النديم ١٤١ والأغانى (طبعة دار الكتب) ١/ ٥ - ٦.','2','293',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (756,'بن مقلة (المتوفى سنة ٣٢٨ هـ/ ٩٤٠ م) (٣٠١)، كذلك على معاصر له هو أبو الفتح النحوى (٣٠٢)، وعلى نسخة بخط مؤلفها أبى الحسن عبد الله بن محمد بن سفيان الخزاز/ (المتوفى ٣٢٥ هـ/ ٩٣٧ م) (٣٠٣)، وعلى كتاب «أخبار علماء الكوفة» بخط مؤلفه أبى الطيب أخى الشافعى (كان حيّا حوالى سنة ٣٣١ هـ/ ٩٤٢ م) (٣٠٤) وعلى بضع قوائم كتب (٣٠٥)، كما اعتمد كذلك على كتب تاريخية (٣٠٦) وأخرى فى الفرق والأديان (٣٠٧)، وعلى كتب ترجمت لباحثين عرب فى الطبيعيات (٣٠٨)، وعلى بعض الكتب حول علماء التراث الأوربى القديم (٣٠٩).
_________
(٣٠١) انظر الفهرست لابن النديم ٣٩، ٤٢، ٥٣، ٥٥، ٦٦، ٦٩، ٧٤، ٩١.
(٣٠٢) انظر الفهرست لابن النديم ٤٢، ١٤٥، ٢٠٩، وفيه يقول عنه «صاحب بنى الفرات».
(٣٠٣) الفهرست لابن النديم ٥٦، ٥٩، وأيضا ٨٢، وتاريخ بغداد ١٠/ ١٢٣ وإنباه الرواة للقفطى ٢/ ١٣٠ - ١٣١.
(٣٠٤) انظر الفهرست لابن النديم ٦٤، ٦٥، ٩٢.
(٣٠٥) انظر مثلا الفهرست الذى أعده عبدان الإسماعيلى بأسماء كتبه (انظر المرجع السابق ١٨٩)، وهناك أيضا قائمة بمؤلفات الإمام الشافعى كتبها ابن أبى يوسف (انظر المرجع السابق ٢١٠)، وهناك أيضا فهرست كتب أبى بكر الرازى، انظر المرجع السابق ٣٠٢، وفهرست كتب أرسطو بخط يحيى بن عدى، انظر المرجع السابق ٢٥٢.
(٣٠٦) انظر مثلا تاريخ ثابت بن سنان (المتوفى ٣٦٣ هـ/ ٩٧٤ م انظر بروكلمان ١/ ٣٢٤)، انظر المرجع السابق ١٩١، و «كتاب الأخبار الداخلة فى التاريخ» بخط أبى القاسم الحجازى انظر أيضا المرجع السابق ٢٠٥، ٢٠٦، ٢٠٩، ٢١٠، و «كتاب أخبار خراسان»، وهو كتاب قديم مجهول المؤلف انظر المرجع السابق ٣٤٥.
(٣٠٧) انظر مثلا نقض الإسماعيلية لأبى عبد الله بن رزّام، انظر المرجع السابق ١٨٦، وعن «أخبار بابك» إشارة إلى كتاب لواقد بن عمرو التميمى، انظر المرجع السابق ٣٤٣ - ٣٤٤، و «الرد على النصارى للقحطبى» (كان معاصرا لسعيد ابن البطريق المتوفى ٣٢٨ هـ/ ٩٣٩ م، انظر المرجع السابق ٢٩٣)، وانظر أيضا ص ٣٤٢. وهناك كتاب مجهول المؤلف عن الصابئة فى ترجمة أحمد بن عبد الله بن سلام فى عصر هارون الرشيد انظر ص ٢٢، وهناك كتاب عن الصابئة والحرانيين ليعقوب بن إسحاق الكندى بخط تلميذه أحمد بن الطيب السرخسى انظر ص ٣١٨ - ٣٢٠، وعنهم أيضا «كتاب فى الكشف عن مذهب الحرانيين» لأبى يوسف عيسى النصرانى، انظر المرجع السابق ٣٢٠ - ٣٢١، وهناك كتاب بخط معاصره أبى سعيد وهب بن إبراهيم بن طازاد النصرانى، انظر المرجع السابق ٣٢١ - ٣٢٥، وانظر أيضا ١٣٠، وهناك كتاب مجهول المؤلف عن معتقدات الهند كان فى نسخة من سنة ٢٤٩ هـ، عرفه أيضا ابن النديم فى نسخة بخط الكندى، (انظر أيضا المرجع السابق ٣٤٥ - ٣٤٧) وعن معتقدات الصين هناك قصة لقس نجرانى من سنة ٣٧٧ هـ ذكرها أيضا ص ٣٤٩.
(٣٠٨) هناك كتاب بخط مصنفه كان عند جعفر بن الخليفة المكتفى (المتوفى ٣٢٠ هـ/ ٩٣٢ م) يتناول المعارف الطبيعية عند العرب وربما كان يتناول أيضا المعارف الطبيعية عند اليونان (انظر ص ٢٧٥، ٢٧٩، وربما أيضا ص ١٦).
(٣٠٩) أفاد مثلا ص ٢٨٦، ٢٨٧، ٢٩٢ من كتاب «تاريخ الأطباء» لإسحاق بن حنين، وقد وصل إلينا هذا الكتاب','2','294',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (757,'<span data-type="title" id=toc-1922>أ- مصادر ترجمته:</span>
إرشاد الأريب لياقوت (القاهرة) ١٨/ ١٧، كشف الظنون لحاجى خليفة ١٣٠٣ جواد على: علم ابن النديم باليهودية والنصرانية، فى مجلة المجمع العلمى العراقى ٨/ ١٩٦١/ ٨٤ - ١١٣، ١٠/ ١٩٦٢/ ١٥٦ - ١٨٣ وكذلك ما كتبه فيك فى دائرة المعارف الإسلامية (الطبعة الألمانية) ٣/ ٨٧٣ - ٨٧٤، الأعلام للزركلى ٦/ ٢٥٣، معجم المؤلفين لكحالة ٩/ ٤١ - ٤٢، بروكلمان ١/ ١٤٧ تحت رقم ٣، كتب جراى عن المادة الإيرانية فى كتاب الفهرست:
L. H. Gray, Iranian Materialinthe Fihrist, Museon ٣ ser. ١/ ١٩١٥/ ٢٤ - ٣٩.
كتب روزن عن كتاب الفهرست:
V. Rosen, K. Fihristu I, ٨٩, ٢٢, ZVO ٢٣/ ١٩١٥/ ٢٣٣ - ٢٤٤.
كتب آربرى عن مادة جديدة فى كتاب الفهرست:
A. Arberry, New Materialonthe K. al- F. of Ibnan- N., IRAMiscellany I/ ١٩٤٩/ ١٩ - ٤٥.
كتب يوهان فك عن التراث العربى فى الكيمياء فى كتاب الفهرست لابن النديم، مع ترجمة إنجليزية للمقالة العاشرة من الكتاب وتعليق عليها:
J. Fuck, The Arabic Literatureon Alclemyaccording toan- Nadim (A. D. ٩٨٧) , ATranslationof the/ tenth Discourseofthe Bookofthe Catalogue With Introductionand Commentary, Ambix ٤/ ١٩٥١/ ٨١ - ١٤٤. J. Fuck, EI, III, ٨٧٣ - ٨٧٤.
مخطوطات كتاب الفهرست: أ- المخطوطات التى اعتمدت عليها طبعة فلوجل:
_________
(نشره روزنتال وترجمه إلى اللغة الإنجليزية، وذلك فى (F.Rosenthal ,i N: Oriens ٧ /١٩٥٤ /٥٥ - ٨٤: وقد أشار أيضا إلى «تاريخ الأطباء» المنسوب إلى يوحنا النحوى، انظر ص ٢٨٦، ٢٨٧، ٢٨٩، ٢٩٣ ومع هذا فقد عرفه على نحو غير مباشر، وذلك عن طريق نقول إسحاق بن حنين منه، كما افترض شتانيشنايدر فى كتابه عن الترجمات العربية Steinschreider ,Arab Ubers.١٦: قارن أيضا تاريخ الأطباء لإسحاق وذلك فى المرجع السابق ٦٤ - ٦٩، وهناك- أيضا- كتاب مجهول المؤلف فى تاريخ الحكماء القدماء، انظر ص ٢٨٧، وأشار الى كتاب بخط ثابت بن قرّة عن عدد الأطباء الذين ذكرهم بقراط، انظر ص ٢٩٣ - ٢٩٤، وانظر أيضا المرجع السابق لشتانيشنايدر، ص ٢٢، أما عن هرمس فقد أفاد من كتاب منسوب لرجل من آل ثوابة، انظر ص ٣٥٢ ٣٥٣.','2','295',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (758,'باريس ٤٤٥٧ (٢٣٧ ورقة ٦٢٧ هـ، انظر فايدا ٣٤٢)، ٤٤٥٨ (٢٤٦ ورقة، نسخة حديثة عن قسم من مخطوطات كوبريلى ١١٣٤). قارن ما كتبه ماسينيون وريتر عنها:
Massignon, Bibliographie Hallagienne Nr. ١٧٢, Ritter, Islam ١٧/ ١٩٢٨/ ١٦.
ليدن ٢ (من ورقة ٢٤٧ - ٢٥٤، وتتضمن الكتاب ابتداء من المقالة السابعة، انظر ما كتبه هوتسما:
Houtsma, WZKM. ٤/ ١٨٩٠/ ٢١٧ - ٢٣٥.
وريتر فى المرجع السابق)، ٢١ (قطع منه ص ٣١٥ - ٣٢١، ٣٩٨ - ٤٦٩، انظر فورهوف ٨٣).
فيينا ٣٣ (نسخة غير مقابلة بأصل المؤلف مأخوذة من مخطوطات كوبريلى ١١٣٤، ١٦٦ ورقة، ١٨٤٠ م)، ٣٤ (منسوخه عن كوبريلى ١١٣٥، ١٦٧ ورقة).
ب- المخطوطات التى لم يعتمد عليها فلوجل اعتمادا مباشرا أو لم يفد منها على الإطلاق:
كوبريلى ١١٣٤ (١٧٩ ورقة، ليست قديمة جدا، انظر: ريتر فى المرجع السابق: ١٧ - ٢٠)، ١١٣٥ (١١٨ ورقة، ٦٠٠ هـ، انظر: ريتر فى المرجع السابق ١٦ - ١٧)، شهيد على ١٩٣٤ (١٨١ ورقة قديما نسبيا انظر: ريتر فى المرجع السابق ٢٠ - ٢٢)، تشستربيتى ٣٣١٥ (١١٩ ورقة، القرن الخامس الهجرى، عن نسخة بخط المؤلف)، عارف حكمت بالمدينة المنورة، تاريخ ٤٨٨ من هذه المخطوطات نسخ فى التيمورية والأوقاف ببغداد ٧٨٤، (انظر كوركيس عواد فى سومر ١٣/ ٦٨)، السعيدية بتونك راجبتنا ٤٤) Tonk Rajputana ورقة، القرن الحادى عشر، انظر ما كتبه يوهان فك عن مواد جديدة لكتاب الفهرست):
J. Fuck, Neue Materialienzum Fihrist, ZDMG ٩٠/ ١٩٣٦/ ٢٩٨ - ٣٢١.
وهناك مخطوطة فى تطوان كذلك. نشر الكتاب وعلق عليه فلوجل وبعد وفاته رودجر ومولر J Rodiger ,A.Muller. فى مجلدين ليبزج ١٨٧١ - ٧٢، وصور بالأوفست فى بيروت دار خياط ١٩٦٤، وطبع بالقاهرة ١٣٤٨ هـ. ونشر يوهان فوك قسما من مخطوط تشستربيتى تضمن نصوصا لم تكن قد نشرت عن حركة المعتزلة، ونشر البحث فى الصحيفة التذكارية محمد شافى:
J. Fuck, Somehithertounpu blished Textsonthe Mutazilite Movement From Ibnan- Nadimsal- Fihrist, Mel. Muh. Safi, Lahore II, ١٩٥٥, ٥١ - ٧٤.

<span data-type="title" id=toc-1923>٣ - ابن فريغون</span>
هو متغبّى (مبتغى؟ ) بن فريعون (فريعون؟ ) (٣١٠) كان تلميذا لأبى زيد أحمد
_________
(٣١٠) ليس من الممكن حاليا التعرف على اسم المؤلف أو اسم أبيه على وجه الدقة. كما أن قراءة روزنتال (ص ٣٢)','2','296',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (759,'بن سهل البلخى، (المتوفى سنة ٣٢٢ هـ/ ٩٣٤ م، انظر بروكلمان ١/ ٢٢٩). توفى على الأرجح فى النصف الثانى من القرن الرابع الهجرى.

<span data-type="title" id=toc-1924>آثاره:</span>
١ - «كتاب جوامع العلوم»:
هذا الكتاب تصنيف للعلوم على طريقة التشجير، ويتناول علوم اللغة، وفن الكتابة، والسياسة والحرب والأخلاق والعقيدة والعبادة والتنجيم وعلم الفراسة والسحر الهندى والطلسمات والكيمياء. وكل اهتمام المؤلف منصرف إلى التصنيف من وجهة نظر فلسفية حرة، انظر ما كتبه ريتر:
Ritter, Oriens III, ٨٣.
وانظر روزنتال: علم التاريخ عند المسلمين:
Rosenthal, History, ٣٢ - ٣٣.
وانظر كذلك دنلوب:
D. M. Dunlop, The Gawamial- ulumof Ibn Farigun, in: Z. V. Togana Armagan, ١٩٥٠ - ٥٥, PP.
.٣٥٣ - ٣٤٨ يوجد مخطوطا فى: سراى، أحمد الثالث ٢٦٧٥ (٨٠ ورقة، القرن السادس الهجرى، انظر: ريتر فى المرجع السابق) ٢٧٦٨ (٨٦ ورقة، ٣٩٦ هـ، انظر: المرجع السابق) وهناك مصورتان عن المخطوطين فى القاهرة ثان ٤/ ١٨٢، معارف عامة ٥٢٧، ٥٢٨، الإسكوريال ٩٥٠ (٨٤ ورقة، قبل سنة ٦٥٥ هـ عن أصل من سنة ٣٩٣ هـ).

<span data-type="title" id=toc-1925>٤ - القرّاب</span>
هو أبو يعقوب، إسحاق بن إبراهيم بن محمد، القراب السّرخسى، ولد سنة ٣٥٢ هـ/ ٩٦٣ م فى هرات، كان مؤرخا ومحدثا كثير التأليف وتوفى سنة ٤٢٩ هـ/ ١٠٣٨ م.
_________
قراءة مغايرة، فهو عنده ابن فريغون (بفتح الفاء وكسر الراء وضم الغين). وقد عرف الثعالبى (اليتيمة طبعة ثالثة ٤/ ٢٧٥) فى القرن الرابع الهجرى شخصية باسم فريغون (فريغون).','2','297',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (760,'<span data-type="title" id=toc-1926>أ- مصادر ترجمته:</span>
طبقات الشافعية للسبكى ٣/ ١١٤ - ١١٥، شذرات الذهب لابن العماد ٣/ ٢٤٤، الأعلام للزركلى ١/ ٢٨٥، معجم المؤلفين لكحالة ٢/ ٢٢٨.

<span data-type="title" id=toc-1927>ب- آثاره:</span>
١ - «تاريخ السنين»:
هذا كتاب فى تراجم العلماء مرتب زمنيا من بدء الإسلام حتى سنة ٤٢٩ هـ، وهو أحد مصادر تهذيب التهذيب لابن حجر، انظر مثلا ٢/ ١٤٧، ٥/ ٢٨٨، ٩/ ٢٤٣.
٢ - «فضائل الرمى فى سبيل الله»:
يتضمن أحاديث حول رمى القوس: كوبريلى ٣٨٤ (الأوراق ١ - ١٠، القرن السابع الهجرى)، جامعة ميتشجان رقم ٤٧٩ (٦٠٠ هـ)، وانظر ما كتبه ريتر Ritter ,Islam ,١٨ /١٩٢٩ /١٤٣ - ١٤٤.: وطبع الكتاب مع الترجمة الإنجليزية التى أعدها فضل الرحمن بافى فى:
ISl. Cult. ٣٤/ ١٩٦٠/ ١٩٥ - ٢١٨.
وانظر بروكلمان ملحق ١/ ٦١٩.
*** ٥ - أفاد البيرونى (المتوفى سنة ٤٤٠ هـ/ ١٠٤٨ م) فى كتابه: «الآثار الباقية عن القرون الحالية».
من كتب كثيرة فى تاريخ الحضارة لم يصل إلينا منها إلّا قطع قليلة وهذه الكتب هى:
١ - «كتاب معارف الروم»:
لأبى الحسين أحمد بن الحسين الكاتب الأهوازى (عاش فى القرن الثالث أو الرابع الهجرى انظر بروكلمان ملحق ١/ ٣٨٧). وألفه اعتمادا على ملاحظاته الخاصة ومنه قطع باقية فى «الآثار الباقية .. » للبيرونى ٢٨٩ - ٢٩١، ٢٩٣ - ٢٩٤.
٢ - «كتاب التاريخ»:
لأبى الفرج إبراهيم بن أحمد بن خلف الزّنجانى الحاسب (القرن الرابع الهجرى) وهذا الكتاب حول عادات الفرس وأديانهم، منه قطع فى: الآثار الباقية .. للبيرونى: ٤٤، ١١٦، ١١٧، ١٢٦ - ١٢٩، ٢١٥ - ٢١٩، ٢٣٠، ٣١٩ - ٣٢٠.
٣ - «كتاب فى علّة أعياد الفرس»:
لزادويه بن شاهويه (القرن الرابع الهجرى؟ )، ومنه قطع باقية فى: الآثار الباقية .. للبيرونى ٤٤، ٢١٧ - ٢١٩، ٢٢١ - ٢٢٢.','2','298',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (761,'<span data-type="title" id=toc-1929>مقدمة</span>
طبقا للرأى الذى ذهبنا إليه فى هذا الكتاب بالنسبة إلى تطور التأليف فى فروع التراث العربى الأخرى (والذى بحسبه مثلا أن الكتاب الأثرى الذى ألفه سيبويه فى النحو لا بد أنه سبقته كتب أخرى مهدت له، وأن كتب المؤرخين الكبيرة فى نفس الفترة مثل كتب ابن إسحاق وسيف بن عمر هى جمع لكتب سابقة عليها، وأن تفاسير كثيرة للقرآن الكريم فى القرن الأول الهجرى قد دخلت فى المؤلفات التى ظهرت بعد ذلك)، يجب أيضا أن نعدّ كتب الفقه الضخمة التى ألفت فى القرن الثانى الهجرى نتيجة تطور مواز وثمة معلومات عن منهج علماء الفقه المبكرين وعن ترتيبهم الزمنى وتوزيعهم المكانى وصلت إلينا فى كتب طبقات الفقهاء وأدب القضاء وأخبار القضاة. ولكى نرسم صورة لتطور حركة التأليف فى هذا الميدان فإن علينا أن نبحث بأنفسنا- إلى جانب ما جاء به ابن النديم من أخبار قليلة عن نقاط هامة (بمثابة دعائم) نستخلصها من هنا ومن هناك فما نعرضه هنا من بيان ليس مستوعبا، كما أننا لا نهدف إلى استيفائه. وإنما غرضنا أن نقدم إلمامة بكتب الفقه التى سبقت الجوامع المعروفة لنا، المؤلفة فى القرن الثانى للهجرة تشير الدراسات الحديثة إلى خبر لهشام بن عروة يقول بأن كثيرا من كتب الفقه كانت فى حوزة أبيه عروة بن الزبير يوم الحرّة (•) (٢٦، أو ٢٧ من ذى الحجة سنة ٦٣ هـ، الموافق ٢٦/ ٨/ ٦٨٣ م)، وأنها احترقت كلها وأنه حزن لذلك حزنا شديدا (طبقات ابن سعد ٥/ ١٣٣).
وقد أكد كثيرون أن هذا الخبر ليس بذى قيمة كبيرة، فلم يكن من الممكن أن تكون هذه «كتبا» بمعنى الكلمة، وهى لا يمكن أن تكون أكثر من أوراق غير منتظمة دوّنت فيها أشياء (انظر ما كتبه جولد تسيهر فى دائرة المعارف الإسلامية- الطبعة الأوربية الأولى-
_________
(•) انظر فلهاوزن فى كتابه «الدولة العربية Wellhausen ,Dasarabiscbe Reich ٩٨: «','3','3',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (762,'٢/ ١٠٨، وبروكلمان الملحق ١/ ٢٨٢). وهذا تفسير نمطى للأخبار القائلة بوجود كتب فى صدر الإسلام، وقد تكرر هذا الحكم كثيرا فى مجالات أخرى من دراساتنا الحديثة، فى الوقت الذى لم يستطع فيه هؤلاء الباحثون/ نفى صحة هذه المعلومات نفيا كاملا. لم يزعم أحد أن هذه الكتب كانت بنفس حجم الكتب التالية أو بترتيبها نفسه، إلا أنها كانت كتبا وسميت كتبا طبقا لمفهوم ذلك الوقت. ونستطيع عن طريق الأخبار المعروفة لنا والقدر الطفيف جدا المتبقى من هذه الكتب أن نتعرف عليها (أى على هذه الكتب) بصورة أدق.
فمنذ زمن الرسول (صلّى الله عليه وسلم) وصحابته ونحن نعلم بوجود تدوين لمسائل الفقه. وعلى ذلك فلا بد أن بعض الأوامر المكتوبة لصحابة الرسول كانت متداولة فى القرن الأول الهجرى على الأقل. وعند ما دخل الخليفة الأموى عمر بن عبد العزيز (المتوفى ١٠١ هـ/ ٧٢٠ م) - عقب توليته الحكم- المدينة المنورة، أمر بالبحث عن مدونتين قديمتين، الأولى «كتاب الصدقات» للنبى (صلّى الله عليه وسلم) والأخرى بنفس التسمية لعمر بن الخطاب. وعند ما وجد عمر بن عبد العزيز الكتابين أمر بنسخهما (انظر كتاب «الأموال» لأبى عبيد ٥٠١ - ٥٠٢، وكذلك تاريخ الخلفاء للسيوطى ٢٣١) (٢)، وقد احتفظ بالنسخة الأصلية أبو بكر بن محمد بن عمرو بن حزم المتوفى ١٢٠ هـ/ ٧٣٧ م، انظر:
المبسوط للسرخسى ٢/ ١٥٢). وروى جده عمرو بن حزم بن زيد رسالة النبى إليه، وفيها كلام فى الفرائض والزكاة والدّيات ... إلخ، وقد دخلت هذه الرسالة فيما بعد فى الكتب الفقهية لأبى داود والنسائى وغيرهما. وقد روى هذه الرسالة بالتالى عمرو بن حزم وحفيده (الإصابة ٢/ ١٢٦٤).
وتلقى أنس بن مالك كتابا من أبى بكر الصديق عن فرائض الصدقة، كما أمر
_________
(٢) النص: «عن الزهرى، قال: كتب عمر بن عبد العزيز إلى سالم بن عبد الله أن يكتب إليه بسيرة عمر بن الخطاب فى الصدقات فكتب إليه فى الذى سأل وكتب إليه: إنك إن عملت بمثل عمل عمر فى زمانه ورجاله فى مثل زمانك ورجالك كنت عند الله خيرا من عمر».','3','4',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (763,'الرسول بها («مسند» ابن حنبل ١/ ١٨٣ - ١٨٤ والرد على بشر المريسى للدّارمى ص ١٣٠، و «تقييد العلم» للخطيب البغدادى ٨٧).
وذكر حفيد الخليفة عمر بن الخطاب أنه وجد بعد وفاة جده صحيفة فى قائم سيفه عن الخراج الواجب على الماشية («الكفاية» للخطيب البغدادى ٣٥٣ - ٣٥٤). وكانت «صحيفة» على بها تدوين لمسائل فقهية (انظر الدّارمى ص ١٣٠) تحفظ كذلك/ فى قائم سيفه (انظر مسند ابن حنبل ٢/ ٣٥ - ٣٦، ١٢١ - ١٢٢، ١٣١).
وذكر ابن الحنفية، وهو ابن على بن أبى طالب أن أباه أعطاه يوما ما صحيفة ليذهب بها إلى عثمان وأن هذه الصحيفة كانت تضم ما أوصى به الرسول حول الصدقة (انظر:
«فتح البارى» لابن حجر ٧/ ٢٣). وإلى جانب هذا فقد ذكر- من قديم الزمن- كتاب لسعد بن عبادة (المتوفى ١٥ هـ/ ٦٣٦ م)، روى فيه أحد أبناء سعد ما اعتاد عليه الرسول فى التشريع (انظر: جولد تسيهر، Goldziher ,Muh.Stud.II ,٩ - ١٠ والترمذى ١/ ٢٥١، و «جامع بيان العلم» لابن عبد البر ١/ ٧٢). ويمكن تتبع آثار من هذه الكتب- التى لم تكن تتكون إلّا من بضع أوراق على أرجح الاحتمالات- فى كتب علم الحديث. ولا بد من بحث: إلى أى حدّ كانت الإفادة منها فى المؤلفات الفقهية الخالصة فى القرنين الثانى والثالث الهجريين. (٣) ولا بد أن نذكر فى هذا الصدد الرسالة المشهورة التى كتبها عمر بن الخطاب إلى أبى موسى الأشعرى والرسالة الثانية التى كتبها إلى معاوية فى القضاء، وتذكر الرسالتان أيضا فى كتب غير كتب الفقه كثيرا (انظر: «أخبار القضاة» لوكيع ١/ ٧٠ - ٧٣، ٢٨٤، وكذلك ٧٤ - ٧٥، والبيان والتبيين للجاحظ ٢/ ٢٩٢ - ٢٩٣).
والكتب المذكورة آنفا أقرب فى تسميتها ضوابط فقهية أو فتاوى فقهية من أن يطلق عليها رسائل فى الفقه. ونستطيع أن نعتبر أول رسائل فقهية حقة تلك التى ظهرت أثناء حركة الجمع والتأليف، وأقدم مؤلفى هذه الرسائل هم شباب الصحابة. وقد ذكر لنا المؤرخ المصرى ابن يونس (المتوفى ٣٤٧/ ٩٥٨ م) مثالا هاما يتضح منه أن تلميذ عبد الله بن عمرو بن العاص (ولد ٧ قبل الهجرة وتوفى ٦٥ هـ/ ٦٨٤ م)، وهو حسين بن
_________
(٣) انظر المدونة ٢/ ٦٩، ٧١، ٧٣، ٩٣','3','5',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (764,'شفىّ بن ماتع الأصبحى (المتوفى ١٢٩ هـ/ ٧٤٦ م) روى عنه فى مصر كتابين أحدهما بعنوان: «قضى رسول الله صلّى الله عليه وسلم فى كذا، وقال رسول الله صلّى الله عليه وسلم كذا»، والكتاب الآخر بعنوان «ما يكون من الأحداث إلى يوم القيامة». (٤) ويعدّ زيد بن ثابت أيضا (ولد ١١ قبل الهجرة/ ٦١١، وتوفى ٤٥ هـ/ ٦٦٥ م) من أوائل الفقهاء المشاهير، ويبدو أنه كان قد جمع مثل هذه الكتب. وقد سبق أن ذكرنا من قبل أنه كان يفضل رأيه الخاص (انظر ص ٦٢ وما بعدها من هذا الكتاب). وروى عنه تلميذه قبيصة (١ هـ/ ٦٢٢ م- ٨٦ هـ/ ٧٠٥ م) - وهو أحد الفقهاء المشاهير فى المدينة- كتابا فى الفرائض (انظر العلل لابن حنبل ١/ ٢٣٦) وظل هذا الكتاب/ متداولا فى القرن السادس الهجرى، وقد اعتمد على هذا الكتاب كل من مالك والشافعى فى كتبهما عن الفرائض، كما تابعهما الطبرى فى تأليفه لكتاب الفرائض، كما ذكره ابن خير الاشبيلى فى الفهرست (ص ٢٦٣). وهناك أخبار كثيرة هامة حول ذلك احتفظ بها البيهقى (المتوفى ٤٥٨ هـ/ ١٠٦٦ م) فى كتاب السنن فى الباب الخاص بالميراث وقد أفرد فيه قسما خاصا لزيد بن ثابت ومكانته الممتازة وكتابه الفرائض. ويتضح من الأخبار أن هذه الرسالة عدّت عند التابعين مصدرا لا غنى عنه فى موضوع المواريث، ويرى الزّهرى أن المعرفة بقوانين الميراث كانت ستضيع لولا أن زيد بن ثابت دونها وحفظها بذلك.
يقول الزهرى «لولا أن زيد بن ثابت كتب الفرائض لرأيت أنها ستذهب من الناس». ومما يدل على تقدير قيمة هذه الرسالة أنها شرحت فى وقت مبكر، شرحها أبو الزناد عبد الله بن ذكوان (المتوفى ١٣١ هـ/ ٧٤٨ م) وكان فقيها مرموقا فى عصره، وصف بأنه كان «صاحب كتابة وحساب»، وتدلنا القطع الباقية عند البيهقى على أن أبا الزناد كان يملك كتاب الفرائض لزيد بن ثابت برواية خارجة بن زيد. ويبدو أن الشارح كان يذكر متن الكتاب أول الأمر ثم يبدأ شرحه بكلمة: «قال». وقد روى هذا الشرح عبد الرحمن بن أبى الزناد، وتتكرر فى كل قطعة ملاحظته التالية: «أمّا معانى هذه الفرائض وأصولها عن زيد بن ثابت، وأما التفسير تفسير أبى الزناد على معانى زيد بن ثابت» (انظر السنن الكبرى ٦/ ٢١٣، ٢٢٥، ٢٢٦، ٢٢٧، ٢٢٩، ٢٣١، ٢٣٢، ولا سيما الصفحات ٢٣٤، ٢٣٦، ٢٣٨،
_________
(٤) توجد هذه القطعة من كتاب تاريخ مصر لابن يونس فى الخطط للمقريزى ٢/ ٣٣٢.','3','6',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (765,'٢٤٥، ٢٥٠). وإلى جانب تلك المواضع التى شرحت، يبدو أن البيهقى احتفظ أيضا بنصوص أخرى من كتاب الفرائض لزيد. وقد روى خارجة بن زيد إلى جانب هذا رسالة أبيه فى الميراث، وهى تلك الرسالة التى وجهت إلى معاوية بن أبى سفيان ووصلت إلينا قطع منها- أيضا- عند البيهقى (الصفحات ٢٤٧، ٢٤٨، ٢٤٩). وهناك كتاب اخر لزيد بن ثابت عنوانه «كتاب فى الدّيات». وقد أمر عمر بن عبد العزيز بإحراق هذا الكتاب (انظر العلل لابن حنبل ١/ ٢٥٦)، ولا نعلم السبب فى ذلك.
وتشير أخبار كثيرة إلى أنه كان من المألوف عند الصحابة فى هذا الجيل أن يتبادلوا الرسائل مع بعضهم البعض فى القضايا الفقهية. وقد كتب، مثلا نافع بن الأزرق إلى عبد الله بن العباس يسأله رأيه فى نصيب/ الأقارب فى الميراث ويسأله أيضا رأيه فى قتل الأطفال (انظر العلل لابن أبى حاتم الرازى ١/ ٣٠٧)، وقد ذكر عبد الله بن هرمز أنه باعتباره كاتب عبد الله بن العباس كتب إلى نجدة الحرورى (المتوفى ٦٩ هـ/ ٦٨٨ م) التى تنسب إليه فرقة «النجدية»، يسأله ما إذا كانت نساء النبى قد رافقته فى الحرب أم لا (انظر أنساب الأشراف للبلاذرى ١/ ٥١٧)، وقد وصل إلينا قسم من هذه المراسلات فى المدونة ٣/ ٦. وفى هذه الفترة المبكرة قيل إن المسلمين كانوا يطلعون على كتب كثيرة من كتب الأديان الأخرى، وبعض هذه الكتب فى موضوعات فقهية. والطريف هنا أن نشير إلى الخبر القائل بأن فى معركة اليرموك ضد البيزنطيين (حوالى سنة ١٦ هـ) وقع فى يد عبد الله بن عمرو حمل بعيرين من الكتب، وكان يذكر لمستمعيه ما جاء فيها (الرد على بشر المريسى للدّارمى ١٣٦، وفتح البارى لابن حجر ١/ ١٦٦)، وليس من شأننا هنا أن نناقش قضية اللغة التى كانت هذه الكتب مدونة بها وقضية ترجمتها، فلهذا موضع تال فى القسم الخاص بالترجمة.
ومن المرجّح أن الجيل الأوسط من التابعين قد شغلوا بهذه الرسائل الفقهية المبكرة كما شغلوا- مثلا- بكتب المغازى الأولى وبالتفاسير القرآنية، فهى ترجع أيضا إلى هذه الفترة. ومعروف أن الشعبى (المتوفى ١٠٣ هـ/ ٧٢١ م) وهو مؤرخ وفقيه وعالم بالشعر كان ينقل كثيرا من هذه الكتب المبكرة. وذكر مجاهد أنه رأى الشعبى يملى ثلاث أوراق فى','3','7',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (766,'الصدقات والفرائض (انظر العلل لابن حنبل ١/ ٣٤٠) وذكر الخطيب البغدادى (تاريخ بغداد ١٢/ ٢٣٢) كتاب الفرائض والجراحات من تأليفه. وأملى الضحّاك بن مزاحم (المتوفى ١٠٥ هـ/ ٧٢٣ م) على تلميذه حسين بن عقيل «كتاب مناسك الحج» (انظر جامع بيان العلم لابن عبد البر ١/ ٧٢). وذكر أبو الزناد عبد الله بن ذكوان أنه كان يكتب الحلال والحرام. وكان الزهرى يدوّن كل ما سمع (انظر جامع بيان العلم لابن عبد البر ١/ ٧٣).
وذكر حاجى خليفة تحت رقم ١٢٤٦ كتاب الفرائض لأيوب بن أبى تميمة السختيانى، ويبدو أن بعض المصادر ومنها السنن الكبرى للبيهقى «والمدوّنة» قد احتفظت بقطع من كتب الفرائض هذه (٥).
ويبدو أن بداية تأليف كتب الفقه المصنفة فى أبواب كانت فى أواخر القرن الأول وأوائل القرن الثانى للهجرة، حتى وإن ألفت الكتب المصنفة فى الحديث فى الربع الثانى من القرن الثانى للهجرة. وهناك نص لابن قيم الجوزية يقول بأن فتاوى الزّهرى كانت فى ثلاثة أسفار، وأن فتاوى الحسن البصرى كانت فى سبعة أسفار مرتبة على أبواب الفقه (انظر إعلام الموقعين، ط القاهرة ١٣٢٥، ١/ ٢٦، وما كتبه جولد تسيهر فى دائرة المعارف الإسلامية- الطبعة الأوربية الأولى ٢/ ١٠٨، انظر بروكلمان ملحق ١/ ٢٨٢).
وأقدم كتاب نعرفه من هذا النوع هو كتاب: السنن فى الفقه لمكحول بن شهراب (المتوفى ١١٦ هـ/ ٧٣٤ م، انظر الفهرست لابن النديم ٢٢٧، (وجولد تسهير (Goldziher Muh Stud.II ,٢١٢، وقد عرف له الفقهاء المتأخرون مؤلفا آخر، هو: «كتاب الحج» (انظر التهذيب لابن حجر ٨/ ١٧٨).
وفى صدر الإسلام أيضا عاش فقهاء مهدوا لمدرسة الرأى، فهناك عدد من الصحابة كانوا يقولون بالرأى إذا لم يكن هناك حكم للرسول (صلّى الله عليه وسلم)، ويذكر هؤلاء
_________
(٥) لا بد أن نذكر بصفة خاصة، من الروايات الكثيرة المختلفة، الرواية التالية: « ... عن المغيرة (بن مقسّم) عن أصحابه وعن أصحاب إبراهيم النخعى والشعبى وعن إبراهيم والشعبى «انظر السنن الكبرى ٦، ٢٤١، ٢٤٧، ٢٥٠، ٢٥١). والراجح أن المقصود بهذا هو كتاب الفرائض للمغيرة بن مقسم (المتوفى ١٣٦ هـ/ ٧٥٣ م انظر تذكرة الحفاظ للذهبى ١٤٣، والتهذيب لابن حجر ١٠/ ٢٦٩ - ٢٧١ والتقريب لابن حجر ٢/ ٢٧٠). ويضم قطعا من كتاب الشعبى وإبراهيم النخعى.','3','8',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (767,'كثيرا فى كتب الطبقات وكتب الفقه. وأول من يمثل هذا المبدأ الفقهى هو الصحابى عبد الله بن مسعود (المتوفى ٣٢ هـ/ ٦٥٣ م). وأقدم من جاء بعده علقمة بن قيس (المتوفى ٦٢ هـ/ ٦٨١ م) والأسود بن يزيد (المتوفى حوالى ٧٥ هـ/ ٦٩٤ م). ومن مدرسة الرأى أيضا حماد بن أبى سليمان (المتوفى ١٢٠ هـ/ ٧٣٨ م). فى العراق وقد التقى حوله عدد كبير من التلاميذ، وربيعة بن أبى فرّوخ التّيمى (المتوفى ١٣٦ هـ/ ٧٥٢ م) فى المدينة الملقب مدحا له بربيعة الرأى. وأبرز تلاميذ حمّاد بن أبى سليمان هو أبو حنيفة، وأهم تلاميذ ربيعة الرأى هو مالك. وقال مالك يوم دفن أستاذه بأن النظر الفقهى قد انتهى يوم حمل ربيعة إلى قبره (شرح الموطأ للزرقانى. (Goldziher ,Muh.stud.II ,٨٠، ٤٤ /٣
والحقيقة التى كانت معروفة وقتئذ أنّ المدرسة التى نسبت فيما بعد لأبى حنيفة ما هى إلّا امتداد لهذه الفترة المبكرة، وهناك عبارة هامة كل الأهمية بالنسبة لتاريخ الفقه الإسلامى ذكرها الجاحظ فى «كتاب التوحيد» - وهو كتاب مفقود- تقول هذه العبارة بأن ثمامة المعتزلى (المتوفى ٢١٣ هـ/ ٨٢٨ م) ذكر فى كتابه الذى ألفه فى نقد أبى حنيفة أنه لم يقصده أصلا، بل قصد به انتقاد عبد الله بن مسعود وعلقمة والأسود، لأنهم هم الذين طبقوا مبدأ الرأى قبل أبى حنيفة (انظر الدرجات الرفيعة لابن معصوم ٢٦).
وينبغى ألا ننسى هنا أن نشير إلى تصور غير صحيح، /، إذ يرى البعض أن أصحاب الحديث كانوا يكونون مجموعة معارضة لأصحاب الرأى، وأن أصحاب الحديث كانوا يجدون ثغرات فى مادة الحديث فيضعون الأحاديث ويؤلفون الأقاصيص لسد هذه الثغرات. وأن أصحاب الرأى كانوا ضد تدوين الأحاديث، وألفوا فى ذلك أقاصيص تؤيد آراءهم (انظر Goldziher ,Muh.stud.II ,٧٨ - ٧٩: ويرجع سبب هذا التصور إلى عدم المعرفة بمصطلح «كتابة الحديث» وهو ما سبق أن ناقشناه تفصيلا فى مقدمة الباب الخاص بالحديث، فالأخبار والأقاصيص التى تذكر فى كتب الحديث ليست ضد كتابة الحديث، ولكنها ضد روايته بطريقة الكتابة. وفضلا عن هذا فليس هناك من دليل على الزعم القائل بأن هذه الأخبار والأقاصيص من اختراع أنصار مدرسة الرأى.
وقد نبّه محقق كتاب «تقييد العلم» إلى خطأ الرأى القائل بأن أنصار مدرسة الرأى كانوا','3','9',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (768,'ضد تدوين الحديث، وأن غيرهم كان يؤيد ذلك (٦) وأشار إلى أن كثيرا من أصحاب الحديث تروى عنهم بالمثل أقوال فى رفض الكتابة. وللأسف فإن رأية القائل بأن أتباع المدرستين كانوا يعارضون التدوين، يقوم على سوء فهم شائع.
وقد وصلت إلينا من هذه الفترة الأولى فى تاريخ الفقه كتب قليلة نادرة، هى:
الكتاب المنسوب لسليم بن قيس الهلالى (كان يعيش فى عهد الحجاج بن يوسف المتوفى سنة ٩٥ هـ/ ٧١٤ م).
«كتاب المناسك» لقتادة بن دعامة (المتوفى ١١٨ هـ/ ٧٣٦ م).
«كتاب مناسك الحج وآدابه» لزيد بن على (المتوفى ١٢٢ هـ/ ٧٤٠ م).
«كتاب المجموع» لزيد بن على.
وقد يمكن تعويض نقص المادة التى وصلت إلينا من هذه الفترة ببحث سلاسل الرواة فى أقدم الكتب الفقهية التى وصلت إلينا، إذ احتفظت لنا هذه الكتب بقطع من كتب سابقة ضاعت، كما سبق توضيح ذلك فى باب الحديث.
ينبغى لنا هنا أن نذكر بعض الفقهاء المشاهير الكثيرين الذين عاشوا فى القرن الأول الهجرى وشاركوا فى تطور كتب الفقه. وقد راعينا فى اختيار هؤلاء أن نذكر الفقهاء الذين نعرف عن مؤلفاتهم فى الوقت الحاضر- شيئا دقيقا، ولا بد أن نشير هنا مرة أخرى إلى أن من وصف من رجال القرنين الأول والثانى/ للهجرة فى التراث العربى بأنه عالم كان أساسا من المؤلفين، ولكن كتب الطبقات لا تذكرهم كمؤلفين ولا تذكر أسماء مؤلفاتهم، ويمكن التحقق منهم ومن مؤلفاتهم عن طريق عبارات عارضة فى مؤلفات أخرى. ولنا على ذلك أمثلة كافية فيمن نذكره من علماء القرنين الأول والثانى للهجرة فى الفصول التالية وغيرها من فصول هذا الكتاب. ولنذكر هنا مثالا واحدا، هو أن ربيعة الرأى كان مؤلفا مع أن ذلك لم يذكر على نحو مباشر فى المراجع الرئيسية ولا فى كتب الطبقات، وهو لم يوصف إلا بأنه صاحب علم كثير (انظر مثلا التهذيب لابن حجر ٣/ ٢٥٩). حتى ابن النديم (فى الفهرست ٢٠٢) لم يذكر له مؤلفا.
_________
(٦) انظر مقدمة تحقيق كتاب تقييد العلم بقلم يوسف العش ٢١ - ٢٢.','3','10',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (769,'ومعروف أن ابن النديم لم يكن على معرفة بكل الكتب العربية التى وجدت آنذاك.
ومع هذا كان من الممكن أن نوافق ابن النديم فى هذا الزعم، لو لم تكن المؤلفات المتأخرة قد ذكرت آراء كثيرة منسوبة لربيعة الرأى ولو لم تصفه المصادر بأنه عالم، ولم لم يكن كثيرون من معاصريه وسابقيه من أصحاب الكتب.
وبالتالى فلا بد من أن يحاول المؤرخ أن يبحث عن بضع إشارات فى كتب الفقه توضح ذلك.
ولما كان ربيعة أستاذا لمالك، فإن كتب فقه المالكية تعدّ أفضل المصادر لبحث ذلك، فالمدوّنة تضم مادة غزيرة لا لربيعة الرأى وحده، بل لكل الفقهاء المبكرين الآخرين.
وهناك قسم كبير من مادة «المدونة» يرجع إلى موطأ عبد الله بن وهب (المتوفى ١٩٧ هـ/ ٨١٣ م) فقد أفاد مؤلف المدونة- عن مؤلفين تقدموا عليه، تارة عن طريق مصادر أخرى وتارة على نحو مباشر، وعلى هذا النحو الأخير نقلت آراء ربيعة. فهناك موضع نجد فيه مؤلف المدونة يحاول شرح رأى مأخوذ عن ابن وهب وأبى الزناد فرجع إلى كتاب لربيعة وبحث فيه، وذكر الباب الذى رجع إليه من الكتاب (انظر المدونة ١/ ٢٩) بقوله:
«وقد قال ربيعة فى أول الكتاب فى تبعيض الغسل». وقد ذكر الكتاب على نحو يفترض معرفة القارئ به وباعتباره مصدرا لكل الاقتباسات المماثلة عن ربيعة.
وبخصوص مصادر موطأ مالك يمكن أن نذكر مثالا، فمالك لم يذكر عموما المصادر التى استخدمها باستثناء ما ورد ذكره من المدونات القديمة فى المصادر التى أخذ عنها. (٧) ونادرا ما يعيننا/ الشراح فى التعرف على المصادر التى ارتبطت بأسماء الرواة ففى الإسناد «عن الثقة عنده، عن بكير بن عبد الله بن الأشج، عن بسر .. » إلخ (٨) حاول الشراح أن يتعرفوا على ما أشير إليه هنا بكلمة «الثقة»، دون التصريح باسمه، وقالوا فى هذا بأن مالكا أخذ هذا النص من كتب بكير بن عبد الله برواية ابنه مخرمة (انظر تجريد
_________
(٧) مثلا: «أن معاوية كتب إلى زيد بن ثابت يسأله عن الجد فكتب إليه زيد بن ثابت ... » (انظر الموطأ، فى: ميراث الجد).
(٨) انظر الموطأ، فى: (الاستئذان).','3','11',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (770,'التمهيد لما فى الموطأ من المعانى والأسانيد لابن عبد البر ٢٤٤ - ٢٤٥)، يقول: «وجده مالك فى كتب بكير أخذها من مخرمة». ونعرف من نص آخر أن مالكا عرف بكير بن عبد الله (المتوفى ١٢٠ هـ/ ٧٣٧ م)، ولكنه أفاد من كتابه برواية ابنه لأنه لم يرد آنذاك أن يسمع من بكير نفسه لأنه لم يكن يقدر رأى ربيعة أستاذ مالك، نص العبارة: «أدركه مالك ولم يسمع منه، وكان بكير سئ الرأى فى ربيعة، فأظنه تركه من أجل ربيعة، وإنما عرف مالك بكيرا بنظره فى كتاب مخرمة» التهذيب لابن حجر ١/ ٤٩٣.
ويصنف فقهاء الفترة السابقة لظهور المذاهب الفقهية السنية فى كتب طبقات الفقهاء وفق المناطق، ثم يرتبون بعد ذلك ترتيبا تاريخيا.','3','12',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (771,'<span data-type="title" id=toc-1930>الفصل الأول كتب الفقه فى العصر الأموى</span>','3','13',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (772,'<span data-type="title" id=toc-1931>١ - زيد بن ثابت</span>
هو زيد بن ثابت بن الضّحّاك الأنصارى، حضر إلى النبى صلّى الله عليه وسلم وهو فى الحادية عشرة من العمر بعد أن حفظ عددا من الآيات القرآنية، وأصبح بعد ذلك كاتبا للوحى عند النبى. وكان موهوبا، تعلم اللغة السريانية تنفيذا لرغبة الرسول- فى مدة وجيزة ليقرأ الرسائل الموجهة إليه. (٩) واستطاع بعد فترة قصيرة أن يقرأ التوراة، وأطلق عليه لقب «ربى» الجماعة، وذلك لفطنته وكثرة معارفه. وعهد إليه الخلفاءالراشدون بالمهام الكبيرة فى الإدارة، وكانوا يعدونه أيضا أفضل عارف بالمواريث، أى «علم الفرائض». صحب الخليفة عمر فى موقعة اليرموك، وتولى فيها تقسيم الغنائم. واختاره الخليفة عثمان/ ليشارك فى تدوين المصحف، وتوفى سنة ٤٥ هـ/ ٦٦٦ م.

<span data-type="title" id=toc-1932>أ- مصادر ترجمته:</span>
الطبقات لابن سعد ٢/ ٢/ ١١٥ - ١١٧، (بيروت) ٢/ ٣٥٨ - ٣٦٢، انظر أيضا الفهرس، السيرة لابن هشام ٥٦٠، التاريخ الكبير للبخارى ٢/ ١/ ٣٨٠ - ٣٨١، المعارف لابن قتيبة ١٣٣، تاريخ الطبرى (الفهرس)، أخبار القضاة لوكيع ١/ ١٠٧ - ١١٠، الجرح والتعديل لابن أبى حاتم ١/ ٢/ ٥٥٨.
طبقات الفقهاء للشيرازى ١٥ - ١٦، التهذيب لابن حجر ٣/ ٣٩٩، نولدكه، Noldeke ,Geschichtedes Qorans II ,٣٥٩ فينسنك فى دائرة المعارف الإسلامية- الطبعة الأوربية الأولى- ٤/ ١٢٩٣ - ١٢٩٤، سارتون فى دائرة المعارف الإسلامية- الطبعة الأوربية الثانية- ١/ ٤٦٥، الأعلام للزركلى ٣/ ٩٥.
_________
(٩) الطبقات لابن سعد ٢/ ٢ - ١١٥، (بيروت) ٢/ ٣٥٨. النص: «تعلم كتاب العبرانية، أو قال: السريانية»، وقيل «لما قدم رسول الله صلّى الله عليه وسلم المدينة، قال لى: تعلم كتاب اليهود، فإنى والله ما آمن اليهود على كتابى».','3','15',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (773,'<span data-type="title" id=toc-1933>ب- آثاره:</span>
١ - «كتاب الفرائض»، كانت إجازته عند ابن خير الإشبيلى (المتوفى ٥٧٥ هـ/ ١١٧٩ م) وذكر ابن خير أن كتابى «الفرائض» لمالك والشافعى قد اعتمدا على هذا الكتاب، كما يصدق هذا أيضا على كتاب «الفرائض» للطبرى (١٠)، ولم يبحث إلى الآن ما بقى من هذا الكتاب فى كتب الفقه والحديث المتأخرة، وهناك قسم كبير وصل إلينا فى شرح أبى الزناد عبد الله بن ذكوان (المتوفى ١٣١ هـ/ ٧٤٨ م) فى كتاب «السنن الكبرى» للبيهقى (١١) وإلى جانب هذا فيبدو أن فى هذا الكتاب بقايا عديدة لم تشرح. (١٢).
٢ - «رسالة فى الفرائض» عن الميراث وجهها إلى معاوية. بقى منها قسم فى كتاب السنن للبيهقى ٢٤٧، ٢٤٨، ٢٤٩.
٣ - «كتاب الدّيات»، أمر الخليفة عمر بن عبد العزيز بإحراق هذا الكتاب لسبب غير معروف لنا. (١٣).

<span data-type="title" id=toc-1934>٢ - شريح بن الحارث</span>
هو شريح بن الحارث بن قيس الكندى، أبو أميّة، أصله من اليمن، ولد فى حياة الرسول ولكنه لم يره. وفى خلافة عمر بن الخطاب وخلافة عثمان بن عفان ثم فى العصر الأموى ولى القضاء، سنة واحدة فى الكوفة، ثم فى البصرة بعد ذلك إلى أن عزله الحجاج بن يوسف سنة ٧٧ هـ/ ٦٩٦ م. روى شريح عن عمر بن الخطاب، وعلى بن أبى طالب وابن مسعود وغيرهم. وروى عنه الشعبى، وابن سيرين، وإبراهيم النّخعى وغيرهم. ويعد من أشهر الفقهاء والقضاة فى صدر الإسلام. ولقد وصل إلينا عنه كثير من الآراء التى لا تخلو من الاختلاف، وهذا ممكن لأنه استمر أكثر من ٦٠
_________
(١٠) انظر فهرس ابن خير ٢٦٣ وكذلك «العلل» لابن حنبل ١/ ٢٣٦
(١١) لقد لفت نظرى مشكورا العالم الدمشقى يوسف العش إلى وجود مثل هذا الاقتباس.
(١٢) انظر، السنن الكبرى للبيهقى ٦/ ٢١٠ - ٢٦٠
(١٣) انظر، العلل لابن حنبل ٢٥٦','3','16',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (774,'عاما مشتغلا بالفقه، ولكنه لا يدل على التعسف فى إصدار الأحكام، ولا يدفعنا إلى الشك فى هذه المعلومات. (١٤) وتوفى شريح سنة ٧٨ هـ/ ٦٩٧ م، وهو فى سن متقدمة.

<span data-type="title" id=toc-1935>أ- مصادر ترجمته:</span>
الطبقات لابن سعد ٦/ ٩٠ - ١٠٠، (بيروت) ٦/ ١٣١ - ١٤٥، التاريخ الكبير للبخارى ٢/ ٢/ ٢٢٩ - ٢٣٠، المعارف لابن قتيبة ٢٢١، حلية الأولياء ٤/ ١٣٢ - ١٤١، طبقات الفقهاء/ للشيرازى ٥٩ - ٦٠، شرح نهج البلاغة لابن أبى الحديد ١٤/ ٢٨ - ٢٩، التهذيب لابن حجر ٤/ ٣٢٦ - ٣٢٨، الأعلام للزركلى Schacht ,Origins ٢٢٨ - ٢٢٩، ٢٣٦ /٣

<span data-type="title" id=toc-1936>ب- آثاره:</span>
توجد مجموعة قيمة من آراء ابن شريح ندين بها لوكيع فى كتابه «أخبار القضاة» ٢/ ١٨٩ - ٣٩٨.
ويكاد يكون ثابتا أن وكيعا قد استخدم كتبا عن شريح ترجع أول الأمر إلى تلاميذ شريح مثل: الشعبى وإبراهيم النخعى، والحكم بن عتيبة (المتوفى ١١٣ هـ/ ٧٣١ م).

<span data-type="title" id=toc-1937>٣ - قبيصة</span>
هو قبيصة بن ذؤيب بن حلحلة الخزاعى، ولد سنة ٨ هجرية بالمدينة، وكان من الصحابة الذين رأوا النبى صلّى الله عليه وسلم ولم يحدثوه. روى قبيصة عن كبار الصحابة ولا سيما عن زيد بن ثابت. واتفقت الآراء على أنه كان ذا مكانة مرموقة بين فقهاء الطبقة الأولى، وكان عالما واسع المعرفة. وفى خريف عمره عاش فى بلاط الأمويين بدمشق، وتولى الكتابة للخليفة عبد الملك وكان أمينا على خاتمه. وتوفى سنة ٨٦ هـ/ ٧٠٥ م، وقيل ٨٧ هـ.
_________
(١٤) انظر Schacht ,Onigins ١٩٤،','3','17',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (775,'<span data-type="title" id=toc-1938>أ- مصادر ترجمته:</span>
الطبقات لابن سعد ٥/ ١٣١، (بيروت) ٥/ ١٧٦، التاريخ الكبير للبخارى ٤/ ١/ ١٧٤، المعارف لابن قتيبة ٢٢٨، طبقات الفقهاء للشيرازى ٣٣، التهذيب لابن حجر ٨/ ٣٤٦ - ٣٤٧، الأعلام للزركلى ٦/ ٢٦.

<span data-type="title" id=toc-1939>ب- آثاره:</span>
روى قبيصة كتابه فى «الفرائض» عن زيد بن ثابت (١٥) ونحن لا نعرف إلى الآن الكتب التى ألفها فى الفقه بنفسه. أما آراؤه فقد بقيت فى كتب الفقه المتأخرة.

<span data-type="title" id=toc-1940>٤ - النّخعى</span>
هو أبو عمران إبراهيم بن يزيد بن قيس النّخعى، أصله من الكوفة، ولد سنة ٥٠ هـ/ ٦٧٠ م، وكان تابعيا روى عن عائشة، وأنس بن مالك، وعن قدامى التابعين.
ومن تلاميذه ورواته حمّاد بن أبى سليمان الذى كان أستاذ أبى حنيفة. وكان النخعى أحد الفقهاء الكبار بالكوفة، وكان يعتمد «الرأى» فى الفقه. ومع ذلك فكان يرى أن الرأى لا يستقيم إلا برواية وأنه لا رواية إلا برأى. (١٦) وكان مثله الأعلى الصحابى عبد الله بن مسعود. وأما الأحكام القائلة بأن الفترة ما قبل النخعى التى كان فيها ابن مسعود ومعاصروه أصحاب المكانة العالية فى الفقه، لا تعتمد على أسس تاريخية، وأن الكثير من الآراء التى تنسب إلى النخعى ليست صحيحة كما ذكر «شاخت» (١٧)، فإن هذه الأحكام ذاتية محضة، وترتبط بالتصور العام السائد عن البدء المتأخر للعلوم الإسلامية. ويضم كتاب «المدونة» وكتب أبى يوسف، والشيبانى،
_________
(١٥) انظر العلل لابن حنبل ١/ ٢٣٦.
(١٦) انظر حلية الأولياء لأبى نعيم ٤/ ٢٢٥
(١٧) انظر Schacht ,Ongins ,٢٣٤ ,٢٣٥،','3','18',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (776,'والشافعى/ آراء فقهية كثيرة للنخعى. ونحن ندين بالفضل لكتاب حلية الأولياء (١٨) لأبى نعيم لذكره مجموعة قيمة من هذه الآراء، وتوفى النخعى سنة ٩٦ هـ/ ٧١٥ م.

<span data-type="title" id=toc-1941><span data-type="title" id=toc-1944>أ- مصادر ترجمته:</span></span>
الطبقات لابن سعد ٦/ ١٨٨ - ١٩٩، التاريخ الكبير للبخارى ١/ ١/ ٣٣٣ - ٣٣٤، المعارف لابن قتيبة ٢٣٤ - ٢٣٥، طبقات الفقهاء للشيرازى ٦٢، غاية النهاية لابن الجزرى ١/ ٢٩، التهذيب لابن حجر ١/ ١٧٧ - ١٧٩، الأعلام للزركلى ١/ ٧٦.

<span data-type="title" id=toc-1942>ب- آثاره:</span>
ذكر أبو نعيم فى المصدر السابق جزءا كبيرا من آرائه بالرواية الآتية: «حدّثنا إبراهيم (بن عبد الله)، حدّثنا محمد، حدّثنا قتيبة بن سعيد، حدّثنا جرير (بن حازم) عن منصور (بن المعتمر) عن إبراهيم» ومن المرجح أن أبا نعيم قد أفاد من كتاب جرير بن حازم، الذى أفاد بدوره من كتاب النخعى أو من كتاب منصور بن المعتمر (المتوفى ١٣٢ هـ/ ٧٤٩ م) تلميذ النخعى.

<span data-type="title" id=toc-1943>٥ - مكحول</span>
هو مكحول بن أبى مسلم شهراب الدمشقى، كان تابعيا روى عن عائشة وأبى هريرة وأبىّ بن كعب وغيرهم. ومن رواته الأوزاعى، ومحمد بن إسحاق وغيرهما. كان محدثا قام برحلات كثيرة، كما كان أحد الفقهاء المشهورين فى عصره. ويقال إنه كان قدريّا، وسنة وفاته بين ١١٢ هـ/ ٧٣٠ م وبين ١١٩ هـ/ ٧٣٧ م.
أ- مصادر ترجمته:
الطبقات لابن سعد ٧/ ١٦٠ - ١٦١، (بيروت) ٧/ ٤٥٣ - ٤٥٤، التاريخ الكبير للبخارى
_________
(١٨) حلية الأولياء لأبى نعيم ٤/ ٢١٩ - ٢٤٠.','3','19',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (777,'٤/ ٢/ ٢١، الفهرست لابن النديم ٢٢٧، حلية الأولياء لأبى نعيم ٥/ ١٧٧ - ١٩٣، طبقات الفقهاء للشيرازى ٥٣، وفيات الأعيان لابن خلكان ٢/ ١٦٠ - ١٦١، ميزان الاعتدال للذهبى ٣/ ١٩٨، التهذيب لابن حجر ١٠/ ٢٨٩ - ٢٩٣، الأعلام للزركلى ٨/ ٢١٢، معجم المؤلفين لكحالة ١٢/ ٣١٩.

<span data-type="title" id=toc-1945><span data-type="title" id=toc-1948>ب- آثاره:</span></span>
ذكر ابن النديم كتابين له هما: «كتاب السنن فى الفقه» و «المسائل فى الفقه». أما «كتاب الحج» فرواه تلميذه العلاء بن الحارث الحضرمى (المتوفى ١٣٦ هـ/ ٧٥٣ م). (١٩) ويبدو أن بقايا كثيرة من كتبه موجودة فى كتب الفقه المتأخرة، وعلى الأخص فى كتاب «الموطأ» لمالك وكتاب «المدونة».

<span data-type="title" id=toc-1946>٦ - حمّاد</span>
هو حمّاد بن أبى سليمان الأشعرى، أبو إسماعيل الكوفى، كان تابعيا روى عن الصحابى أنس بن مالك وعن عدد من قدامى التابعين من بينهم إبراهيم النخعى، والشعبى وسعيد بن جبير، وسعيد بن المسيب. كان من أوائل من أدخلوا «الرأى» فى الفقه بالكوفة. ويعد إبراهيم النخعى أهم أساتذته، وأبو حنيفة أشهر تلاميذه. وكان حماد من المرجئة، ويعدّ محدّثا ضعيفا، وتوفى حوالى سنة ١٢٠ هـ/ ٧٣٨ م/.

<span data-type="title" id=toc-1947>أ- مصادر ترجمته:</span>
الطبقات لابن سعد ٦/ ٢٣١، التاريخ الكبير للبخارى ٢/ ١/ ١٨ - ١٩، المعارف لابن قتيبة ٢٤٠، طبقات الفقهاء للشيرازى ٦٣، التهذيب لابن حجر ٣/ ١٦ - ١٨، انظر جولد تسيهر فى دائرة المعارف الإسلامية Goldziher ,El /٢ /١٥٨ وانظر يوسف شاخت Schacht ,Origins ٢٣٧ - ٢٤٠:
ب- آثاره:
لم تحدثنا المصادر عن نشاطه فى التأليف، ومع ذلك نفترض أن يكون قد ألّف كتبا مثل علماء عصره،
_________
(١٩) انظر، التهذيب لابن حجر ٨/ ١٧٨.','3','20',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (778,'وهناك إشارات فى «كتاب الآثار» لأبى يوسف و «الآثار» للشّيبانى و «الأم» للشافعى وكتاب «المدونة» يبدو أنها تشير إلى كتب له، وإن كانت هذه الكتب لم تذكر ذلك صراحة. (٢٠).

<span data-type="title" id=toc-1949>٧ - بكير بن عبد الله بن الأشجّ</span>
هو بكير بن عبد الله بن الأشج القرشى، أصله من المدينة ثم عاش بمصر. روى عن قدامى التابعين، ومن رواته يزيد بن أبى حبيب وابنه مخرمة. كان فقيها ومحدثا معدودا من العلماء فى عصره، وتوفى حوالى سنة ١٢٠ هـ/ ٧٣٧ م.

<span data-type="title" id=toc-1950>أ- مصادر ترجمته:</span>
التاريخ الكبير للبخارى ١/ ٢/ ١١٣، الجرح والتعديل لابن أبى حاتم ١/ ١/ ٤٠٣ - ٤٠٤، التهذيب لابن حجر ١/ ٤٩١ - ٤٩٣.

<span data-type="title" id=toc-1951>ب- آثاره:</span>
أفاد مالك فى الموطأ من كتابه فى الفقه بالرواية الآتية: «عن الثقة عن بكير بن عبد الله بن الأشج». (٢١) كما استخدم مؤلف «المدونة» هذا الكتاب كثيرا، انظر على سبيل المثال: ٣/ ١٢، ١٥، ٢٢، ٣٣، ٤٩، ٩٧، ٩/ ٢٤، ٢٥.

<span data-type="title" id=toc-1952>٨ - أبو الزّناد</span>
هو أبو الزناد عبد الله بن ذكوان القرشى، ولد سنة ٦٥ هـ/ ٦٨٤ م، كان أحد كبار الفقهاء من التابعين بالمدينة. وكان أعلم من ربيعة الرأى- كما يقول بعض العلماء (٢٢)، كما كان محدثا ثقة وعالما بالعربية ومتقنا للحساب، وتوفى سنة ١٣١ هـ/ ٧٤٨ م.
_________
(٢٠) قارن، شاخت فى المصدر السابق.
(٢١) انظر، التجريد لابن عبد البر ٢٤٣ - ٢٤٦
(٢٢) انظر، التهذيب لابن حجر ٥/ ٢٠٤.','3','21',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (779,'<span data-type="title" id=toc-1953><span data-type="title" id=toc-1956>أ- مصادر ترجمته:</span></span>
التاريخ الكبير للبخارى ٣/ ١/ ٨٣، المعارف لابن قتيبة ٢٣٥، الجرح والتعديل لابن أبى حاتم ٢/ ٢/ ٤٩ - ٥٠، طبقات الفقهاء للشيرازى ٣٨، التهذيب لابن عساكر ٧/ ٣٨٢ - ٣٨٣، الأعلام للزركلى ٤/ ٢١٧.

<span data-type="title" id=toc-1954>ب- آثاره:</span>
ذكر ابن حجر (٢٣) أنه ألف كتابا فى الفقه، ولكنه لم يذكر اسم هذا الكتاب. والأحاديث التى ذكرها مالك فى الموطأ والتى تبلغ ٥٤ حديثا عن طريق أبى الزناد (٢٤) يحتمل أن تكون من ذلك الكتاب الفقهى الذى ذكره ابن حجر. ووصلت إلينا من كتابه «تفسير على فرائض زيد بن ثابت» بقايا كثيرة فى كتاب «السنن الكبرى» للبيهقى (انظر البيهقى ص ٣٩٦).

<span data-type="title" id=toc-1955>٩ - زيد بن أسلم</span>
هو زيد بن أسلم العدوى، هو تابعى روى عن عائشة، وأبى هريرة، وجابر بن عبد الله وغيرهم. ومن رواته ابنه عبد الرحمن، ومالك بن أنس، وابنجريح، وأيوب/ السّختيانى وغيرهم. وكان زيد من مشاهير الفقهاء بالمدينة، كما كان مفسرا؛ وهو أحد الفقهاء الذين دعاهم الوليد بن يزيد إلى دمشق ليسمع آراءهم فى قضايا فقهية، وتوفى سنة ١٣٦ هـ/ ٧٥٣ م.
أ- مصادر ترجمته:
التاريخ الكبير للبخارى ٢/ ١/ ٣٨٧، المعارف لابن قتيبة ٩٥، الجرح والتعديل لابن أبى حاتم ١/ ٢/ ٥٥٥، تذكرة الحفاظ للذهبى ١٣٢، التهذيب لابن حجر ٣/ ٣٩٥ - ٣٩٦، الأعلام للزركلى ٣/ ٩٥.
_________
(٢٣) فى المصدر السابق ٢٠٥.
(٢٤) انظر التجريد لابن عبد البر ٩٢ - ٩٩','3','22',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (780,'<span data-type="title" id=toc-1957>ب- آثاره:</span>
نحن لا نعرف له كتابا فى الفقه، ولكن هذا لا يمنع من أن نفترض أنه قد ألّف كتابا أو أكثر من كتاب كما فعل فقهاء عصره، فإنا نجد المصادر المتأخرة قد ذكرت آراء باسمه رويت عنه، وقد روى مالك فى الموطأ ٥١ رواية عنه. (٢٥).
ويضاف إلى ذلك أن آراءه الخاصة جمعها ابن عبد البر نقلا عن الموطأ، بعنوان: «مراسيل زيد بن أسلم عن نفسه» (٢٦)، وفوق ذلك فإننا نجد كثيرا من آرائه الفقهية فى «تفسيره» وكان هذا التفسير الأساس الذى بنى عليه ابنه عبد الرحمن تفسيره، ويبدو أنه قد بقى كله أو بعضه فى تفسير الطبرى (انظر ترجمته فى الجزء الأول)، أما عن استخدام تفسير زيد بن أسلم فى الأغراض الفقهية، فانظر أخبار القضاة لوكيع ١/ ٤٤.

<span data-type="title" id=toc-1958>١٠ - ربيعة الرأى</span>
هو ربيعة بن أبى عبد الرحمن فرّوخ التّيمى، ولد بالمدينة وعاش بها، كان تابعيا، وروى عن الصحابى أنس بن مالك وعن كبار التابعين. وروى عنه مالك، وسفيان الثورى، وشعبة، وحمّاد بن سلمة وغيرهم. كان من أصحاب «الرأى» فى الفقه أفادوا منه عند عدم وجود نص، وسمى لذلك ربيعة الرأى (٢٧) تقديرا له، وكان أصحاب الحديث يعدونه من أفضل رواته ويعارضون اللقب السابق. (٢٨) وكان له حلقة علم بالمدينة المنورة طيلة عمره، ويعدّ مالك من أحسن تلاميذه، وتوفى ربيعة سنة ١٣٦ هـ/ ٧٥٣ م فى العراق.
_________
(٢٥) انظر، التجريد لابن عبد البر ٣٨ - ٥١.
(٢٦) انظر المصدر السابق ٥١ - ٥٤.
(٢٧) انظر البيان والتبيين للجاحظ ١/ ١٠٢.
(٢٨) انظر تاريخ بغداد للخطيب ٨/ ٤٢٤.
وانظر أيضا: شاخت Schacht ,Origlns ٢٤٧ - ٢٤٨،','3','23',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (781,'<span data-type="title" id=toc-1959>أ- مصادر ترجمته:</span>
التاريخ الكبير للبخارى ٢/ ١/ ٢٨٦ - ٢٨٧، المعارف لابن قتيبة ٢٣٤، ٢٤٩، حلية الأولياء لأبى نعيم ٣/ ٢٥٩ - ٢٦٦، تاريخ بغداد للخطيب ٨/ ٤٢٠ - ٤٢٧، طبقات الفقهاء للشيرازى ٣٧ - ٣٨ التهذيب لابن حجر ٣/ ٢٥٨ - ٢٥٩، الأعلام للزركلى ٣/ ٤٢.
Glodziher, Muh. Stud. II, ٧٩ - ٨٠ Schacht, Origins, ٥٤ ff., ١١٤ f., ٢٤٧ - ٢٤٨.

<span data-type="title" id=toc-1960>ب- آثاره:</span>
لم يذكر ابن النديم (٢٩) كتابا واحدا له. ومع ذلك كان له كتاب واحد- على الأقل- ظل يستخدم عند الفقهاء فى القرن الثالث الهجرى، ومن المرجحأن هذا الكتاب كان مصدرا هاما لعبد الله بن وهب (المتوفى ١٩٧ هـ/ ٨١٣ م) فى كتابه «الموطأ»، وقد احتفظت المدونة- فيما يبدو- بقسم كبير منه، وقد أفاد مؤلف المدونة من كتاب ربيعة مباشرة. وكان يذكر الاقتباس مقدّما له بصيغة «قال» / ومرة أخرى: «وقد قال ربيعة فى أول الكتاب فى تبعيض الغسل» (٣٠) والراجح أن مالكا فى «الموطأ» قد استخدم هذا الكتاب الذى يحتوى على عدد كبير من آراء ربيعة بجانب الروايات المنقولة عنه. (٣١).

<span data-type="title" id=toc-1961>١١ - يحيى بن سعيد</span>
هو أبو سعيد يحيى بن سعيد بن قيس الأنصارى، تابعى من المدينة، روى عن الصحابى أنس بن مالك وعن عدد من كبار التابعين. روى عنه الزّهرى، ومالك، والأوزاعى، وسفيان الثورى، وشعبة وغيرهم. ولى القضاء بالمدينة فى زمن الوليد بن عبد الملك (٨٦ هـ/ ٧٠٥ م- ٩٦ هـ/ ٧١٥ م)، وبالهاشمية فى العراق فى عهد أبى جعفر المنصور (حكم من ١٣٦ هـ/ ٧٥٤ م- ١٥٨ هـ/ ٧٧٥ م). ويعدّ يحيى بن سعيد من فقهاء المدينة ذوى المكانة العالية. يقول الثورى إن أهل المدينة كانوا يعتبرونه
_________
(٢٩) الفهرست ٢٠٢.
(٣٠) انظر، المدوّنة ١/ ٢٩. وربما كان المراد بقوله فى «أول الكتاب» كتاب المدونة. انظرها ١/ ١٦.
(٣١) انظر، التجريد لابن عبد البر ٣٤ - ٣٨.','3','24',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (782,'أعلم من الزهرى (٣٢)، وقد دون ربيعة الرأى آراءه، وتوفى بالبصرة سنة ١٤٣ هـ/ ٧٦٠ م.

<span data-type="title" id=toc-1962>أ- مصادر ترجمته:</span>
الطبقات لابن سعد ٧/ ٢/ ٤٧ (بيروت) ٧/ ٢٩٣، التاريخ الكبير للبخارى ٤/ ٢/ ٢٧٥ - ٢٧٦.
المعارف لابن قتيبة ٢٥٧، أخبار القضاة لوكيع ١/ ١٧٨ - ١٧٩، طبقات الفقهاء للشيرازى ٣٩، تاريخ بغداد للخطيب ١٤/ ١٠١ - ١٠٦، التهذيب لابن حجر ١١/ ٢٢١ - ٢٢٤، الأعلام للزركلى ٩/ ١٨١.
Schacht, Origins ٢٤٨

<span data-type="title" id=toc-1963>ب- آثاره:</span>
لا نعرف- إلى الآن- ما اذا كان قد ألف كتابا أو كتبا فى الفقه (٣٣)، وأما آراؤه فنجد الكبير منها فى «الموطأ» لمالك (٣٤) وفى كتاب «الأم» للشافعى وفى «المدونة».
وقد سبق ذكر عدد من متقدمى الفقهاء فى فصول مختلفة من كتابنا هذا، وهؤلاء هم:
١ - عبد الله بن العباس (المتوفى ٦٨ هـ/ ٦٨٧ م).
٢ - عروة بن الزّبير (المتوفى ٩٤ هـ/ ٧١٢ م).
٣ - سعيد بن المسيّب (المتوفى ٩٤ هـ/ ٧١٣ م).
٤ - الشّعبى (المتوفى ١٠٣ هـ/ ٧٢١ م).
٥ - الضحّاك بن مزاحم (المتوفى ١٠٥ هـ/ ٧٢٣ م).
٦ - الحسن البصرى (المتوفى ١١٠ هـ/ ٧٢٨ م).
٧ - وهب بن منبّه (المتوفى ١١٠ هـ/ ٧٢٨ م أو ١١٤ هـ).
٨ - محمد بن سيرين (المتوفى ١١٠ هـ/ ٧٢٨ م).
_________
(٣٢) انظر، التهذيب لابن حجر ١١/ ٢٢٢.
(٣٣) انظر، التهذيب لابن حجر ١١/ ٢٢٢، من ١ - ٣
(٣٤) انظر، التجريد لابن عبد البر ٢٠٩ - ٢٣٦.','3','25',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (783,'٩ - عطاء بن أبى رباح (المتوفى ١١٤ هـ/ ٧٣٢ م).
١٠ - قتادة بن دعامة (المتوفى ١١٨ هـ/ ٧٣٦ م).
١١ - الزّهرى (المتوفى ١٢٤ هـ/ ٧٤٢ م).
١٢ - يزيد بن أبى حبيب (المتوفى ١٢٨ هـ/ ٧٤٥ م).
١٣ - أيوب بن أبى تميمة السّختيانى (المتوفى ١٣١ هـ/ ٧٤٨ م).
١٤ - عبيد الله بن أبى جعفر (المتوفى ١٣٥ هـ/ ٧٥٢ م).','3','26',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (784,'<span data-type="title" id=toc-1964>الفصل الثانى كتب الفقه فى العصر العباسى (حتى حوالى سنة ٤٣٠ هجرية)</span>','3','27',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (785,'<span data-type="title" id=toc-1965>أولا: المذاهب الأربعة:</span>
فى الربع الثانى من القرن الثانى الهجرى واصل تلاميذ علماء أواخر العصر الأموى الذين كانوا أيضا مثل شيوخهم محدثين وفقهاء- ما كان قد بدأه الشيوخ من تنظيم للمواد الفقهية طبقا لموضوعاتها. وكان أكثر هؤلاء من أوائل أصحاب كتب «المصنف» فى عالم الحديث (٣٥) وكونت الاختلافات المكانية فى هذه الفترة المبكرة عند علماء أوائل العصر العباسى اتجاهات فقهية معينة. فقد جعل أتباع مدرسة الكوفة للرأى تأثيرا جوهريا فى الفقه، وبعد وفاة حماد بن أبى سليمان (المتوفى حوالى ١٢٠ هـ/ ٧٣٨ م) التقوا حول تلميذه أبى حنيفة الذى يعدّ عند تلاميذه أول مؤسس لمدرسة فقهية. وظهر معاصراه سفيان الثورى (المتوفى ١٦١ هـ/ ٧٧٨ م) بالبصرة، والأوزاعى (المتوفى ١٥٧ هـ/ ٧٧٤ م) بالشام بمنهجين فى الفقه، وأشاد بهما أتباعهما باعتبارهما مؤسسى مدرستين مستقلتين.
وفى أوائل العصر العباسى كان مالك بن أنس (المتوفى ١٧٩ هـ/ ٧٩٥ م) أكبر ممثل لمدرسة المدينة والصفة المميزة لتعاليمه هى الأخذ بعمل أهل المدينة واعتباره أساسا من أسس التشريع الأربعة. ويعد عند أتباعه مؤسسا للمذهب المسمى باسمه. وانتشر هذا المذهب- على الأخص- فى المنطقة الغربية من العالم الإسلامى، ونحى من طريقه مذهب الأوزاعى والظاهرية، كما حافظ المذهب المالكى على مكانته إلى اليوم بوصفه واحدا من المذاهب الأربعة فى فقه السنة.
وأسس الشافعى (المتوفى ٢٠٤ هـ/ ٨٢٠ م) مدرسة بالمعنى الدقيق للكلمة كان تلميذا لمالك كما أنه درس على تلاميذ أبى حنيفة، ولذلك كانت آراؤه وسطا بين المدرستين. وكانت مصر وبغداد المركزين الرئيسيين لآرائه فى أول الأمر، ولكن عدد
_________
(٣٥) انظر: مقدمة الحديث فى العصر العباسى ص ٩٠','3','29',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (786,'أتباعه كان فى نمو مطرد خلال القرنين الثالث والرابع الهجريين فى كل أنحاء العالم الإسلامى، ولا يزال هذا المذهب السنى موجودا إلى الآن فى بعض البلاد الإسلامية.
أما المدرسة السنية الرابعة التى لا تزال لها مكانتها إلى اليوم فقد/ أسسها أحمد بن حنبل (المتوفى ٢٤١ هـ/ ٨٥٥ م) الذى استمد كل أحكامه من الكتاب والسنة ولم يأخذ من «الرأى» إلا ما ألجأته إليه الضرورة.','3','30',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (787,'<span data-type="title" id=toc-1966>أ- فقه الحنفية</span>
<span data-type="title" id=toc-1967>١ - أبو حنيفة</span>
هو أبو حنيفة النعمان بن ثابت بن زوطى (•)، ولد حوالى سنة ٨٠ هـ/ ٦٩٩ م بالكوفة، وكان جده زوطى قد جلب من فارس إلى الكوفة عبدا واعتقه سيده وكان من قبيلة تيم الله، أما والده ثابت فقد ولد حرا فى هذه القبيلة. ولقد وفّق أبو حنيفة إلى سماع عدد كبير من كبار التابعين بالكوفة والتعليم منهم. ومن المرجح أنه رأى بعض الصحابة ولكنه لم يرو عنهم. ومن شيوخه أبو عمرو الشّعبى (المتوفى ١٠٤ هـ/ ٧٢٢ م)، وعطاء بن أبى رياح (المتوفى ١١٤ هـ/ ٧٣٢ م)، وحمّاد بن أبى سليمان (المتوفى ١٢٠ هـ/ ٧٣٧ م) ويقال إن حمادا أثر فى ثقافته الفقهية تأثيرا قويا، ظل يسمع منه نحو ثمانية عشر عاما. ويروى عنه أنه تولى حلقة الدرس أثناء سفر شيخه حماد إلى البصرة. وبعد عودة حماد من سفره أعلن خطأ عشرين إجابة من إجابات أبى حنيفة الستين على أسئلة وجهت إليه. ولذلك قرر أبو حنيفة أن يحضر دروس حماد فقط (٣٦) ولم يحاضر إلا بعد موت شيخه. حضر حلقته كل من أبى يوسف (المتوفى ١٨٢ هـ/ ٧٩٨ م)، وزفر بن الهذيل (المتوفى ١٥٨ هـ/ ٧٧٤ م)، ومحمد بن الحسن الشيبانى (١٨٩ هـ/ ٨٠٥ م). ويقال إنه كان يميل فى آرائه العقيدية إلى المرجئة، وكان يكسب قوت يومه من التجارة، ويقال إن الخلفاء الأمويين المتأخرين والخليفة المنصور من العباسيين كانوا يجبرونه على تولى منصب القضاء، ولكنه كان يرفض فى كل مرة، وسجن بسبب ذلك ببغداد، وتوفى هناك سنة ١٥٠ هـ/ ٧٦٧ م).
_________
(•) كذا فى الفهرست لابن النديم، وفى تذكرة الحفاظ: زوطا.
(٣٦) انظر: تاريخ بغداد للخطيب ١٣/ ٣٣٣.','3','31',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (788,'والحكم على صحة نسبة مؤلفات أبى حنيفة له من الأمور الصعبة فى تاريخ التراث العربى، وإن كان الرأى السائد يميل إلى أن أبا حنيفة لم يؤلف كتابا واحدا منها (٣٧) ولكن كيف يستطيع المرء أن يتصور أبا حنيفة، وهو مؤسس مذهب فى الفقه لم يؤلف كتابا قط فى حين أن أساتذته كانوا يؤلفون كتبا وأن معاصريه فى البلاد الإسلامية المختلفة كانوا يؤلفون كتبا كثيرة مبوبة. وقد وصلت بعض الأخبار من معاصريه/ حول كتبه. (٣٨) حتى إنه ليقال إن سفيان الثّورى (المتوفى ١٦١ هـ/ ٧٧٨ م) وهو صاحب مذهب مستقل قد نسخ كتاب «الرهون» لأبى حنيفة. (٣٩).
أما الأوزاعى (المتوفى ١٥٧ هـ/ ٧٧٤ م) وهو مؤسس مذهب فقهى ولم يكن يعرف أبا حنيفة حق المعرفة ولم يكن يقدره أطلعه عبد الله بن المبارك (المتوفى ١٨١ هـ/ ٧٩٧ م) على كتاب لأبى حنيفة كان عنده (٤٠).
إلّا أن عبد الله بن المبارك نفسه قد طعن فى صحة نسبه كتاب «الحيل» إلى أبى حنيفة. (٤١) وأمّا الطريقة التى كان يكتب بها أبو حنيفة كتبه فليس هناك ما يدل على أنه كان يؤلفها بطريقة تخالف معاصريه، كما رأينا فى باب الحديث. ويبدو أن أكثر كتبه التى وصلت إلينا من عمل تلاميذه ولكنه كتب- على الأقل- «الرسالة» الموجهة إلى عثمان البتى (المتوفى ١٤٣ هـ/ ٧٦٠ م) فى الدفاع عن «الإرجاء».
_________
(٣٧) جونبول فى دائرة المعارف الإسلامية (الطبعة الأولى) ١/ ٩٦، وشاخت (الطبعة الثانية) ١/ ١٢٣.
(٣٨) تاريخ بغداد للخطيب ١٣/ ٣٤٢. سأل أبو مسلم المستملى يزيد بن هارون (فى عهد المنصور ١٣٦ هـ/ ٧٥٤ م- ١٥٨ هـ/ ٧٧٥ م): «ما تقول- يا أبا خالد- فى أبى حنيفة والنظر فى كتبه؟ قال: انظروا فيها إن كنتم تريدون أن تفقهوا، فإنى ما رأيت أحدا من الفقهاء يكره النظر فى قوله.»
(٣٩) المصدر السابق: «احتال الثورى فى كتاب الرهن حتى نسخه»
(٤٠) النص: «يا خراسانى من هذا المبتدع الذى خرج بالكوفة يكنى أبا حنيفة؟
فرجعت إلى بيتى فأقبلت على كتب أبى حنيفة فأخرجت منها مسائل من جياد المسائل ... والكتاب فى يدى.
فقال: أى شئ هذا الكتاب؟ »، المصدر السابق ١٣/ ٣٣٨.
(٤١) المرجع السابق ٤٢٦ - ٤٢٧.','3','32',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (789,'<span data-type="title" id=toc-1968>أ- مصادر ترجمته:</span>
المقالات للأشعرى ١/ ١٣٨ - ١٣٩، الفهرست لابن النديم ٢٠١، طبقات الفقهاء للشيرازى ٦٧ - ٦٨، تاريخ بغداد للخطيب ١٣/ ٣٢٣ - ٤٥٤، الانتقاء لابن عبد البر ١٢١ - ١٧٥، الوفيات لابن خلكان (بولاق) ٢/ ٢١٥ - ٢١٩، اللباب لابن الأثير ١/ ٣٢٥، الجواهر للقرشى ١/ ٢٦ - ٣٢، تذكرة الحفاظ للذهبى ١٦٨ - ١٦٩، البداية والنهاية لابن كثير ١٠/ ١٠٧، مرآة الجنان لليافعى ١/ ٣٠٩ - ٣١٢، النجوم الزاهرة لابن تغرى بردى ٢/ ١٢ - ١٥.
A. V. Kremer, Kulturgeschichte. l, ٤٩١ - ٤٩٧
جونبول فى دائرة المعارف الإسلامية Th.W.Juymboll ,El ,l ,٩٦ - ٩٧.٩٧ - ٩٦ /١ كيرن فى:
F. Kern, Abu Hanifaeleteesira taiin: Keletitanulmanyo k Goldziher ١٩١٠, ٢٣٣ - ٢٤٣ (Ind. ls I. No. ٣٣٨٦) .
آدمز، فى:
C. C. Adams, A. H. Championof Liberalismand Toleranoeinlslam, MW ٣٦/ ١٩٤٦/ ٢١٧ - ٢٢٧.
محمد حميد الله، فى:
M. Hamidullah, Codificationof Muslim''lawbya. H. in. Z. V. Togan, Armagan ١٩٥٠ - ١٩٥٥, ٣٦٩ - ٣٧٨.
أبحاث حول أبى حنيفة: ١ - اختلاف أبى حنيفة وابن أبى ليلى لأبى يوسف يعقوب بن إبراهيم (المتوفى ١٨٢ هـ/ ٧٩٨ م).
حققه أبو الوفا الأفغانى القاهرة ١٣٥٧ هـ.
٢ - «ردود على أبى حنيفة» لأبى بكر بن أبى شيبة (المتوفى ٢٣٥ هـ/ ٨٤٩ م) (انظر رقم ٥٠ فى باب الحديث ج ١)، حققه محمد زاهد الكوثرى، القاهرة ١٣٦٥ هـ.
٣ - «فصل فى مناقب أبى حنيفة» لأبى العباس أحمد بن الصلت الحمانى (المتوفى ٣٠٢ هـ/ ٩١٤ م وقيل ٣٠٨ هـ) (٤٢)، طلعت، مجموعة ٤٣٢ (من ٣٦ أ- ٣٩ ب، فى القرن العاشر الهجرى).
٤ - «مقام أبى حنيفة عند الملوك» فى كتاب «مكارم الأخلاق» المجهول المؤلف فى القرن الرابع الهجرى/ البنغال ٢/ ٥٤٧ رقم ١٠٦٢ (ست ورقات).
_________
(٤٢) انظر: تاريخ بغداد للخطيب ٤/ ٢٠٧ - ٢١٠.','3','33',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (790,'٥ - «نبذة من مناقب أبى حنيفة» لأبى الحسين أحمد بن محمد القدورى. يحتمل أن يكون هذا الكتاب موجزا لشرحه على كتاب «المختصر» للكرخى، راغب ١٤٧٩/ ٦ (الأوراق ٦٩ - ٧١).
٦ - «مناقب أبى حنيفة» لأبى عبد الله حسين بن على بن محمد الصّيمرى (المتوفى ٤٣٦ هـ/ ١٠٤٤ م انظر بروكلمان ١/ ٣٧٢)، فاتح ٤٤٩٤/ ١ (من ١ - ٥٤ أ، ٦٠٥ هـ)، عاطف ١٧٨٤ (٩٨ ورقة، فى القرن السابع الهجرى)، شهيد على ١٨٦٧ (١٢٨ ورقة، ٥٣٦ هـ)، القاهرة ثان ٥/ ٣١٨، تاريخ ٣١٠، مانيسا ١٣٤٢/ ١، (الأوراق ١ - ٩١، ٥٦٣ هـ).
٧ - «مختلف بين أبى حنيفة والشافعى» لأبى محمد عبد الله بن الحسين الناصحى (المتوفى ٤٤٧ هـ/ ١٠٥٥ م انظر بروكلمان ١/ ٣٧٣)، وهبى ٤٦٤ (٣٣٩ ورقة ٤٢١ هجرية).
٨ - «كتاب البيان والبرهان فى جمل من فضائل الإمام الأعظم» لأبى بكر عتيق بن داود اليمانى (المتوفى ٤٦٠ هـ/ ١٠٦٨ م) (٤٣)، شهيد على ١٩٣٣/ ١ (من ١ - ٦٤ ب، فى القرن الثامن الهجرى).
٩ - «مناقب الإمام الأعظم» لأبى بكر أحمد بن على بن ثابت الخطيب البغدادى (المتوفى ٤٦٣ هـ/ ١٠٧١ م انظر بروكلمان ١/ ٣٢٩)، سليم أغا ٥٣٨/ ٣ (الأوراق ١٩١ - ٢٠٣).
ورد على الخطيب البغدادى. شرف الدين عيسى بن أبى بكر بن أيوب (المتوفى ٦٢٤ هـ/ ١٢٢٧ م انظر بروكلمان ١/ ٣٨٠) فى كتابه: «السهم المصيب فى الرد على الخطيب .. إلخ» وطبع بالقاهرة ١٣٥١ هـ.
١٠ - «فضائل أبى حنيفة» لأبى القاسم عبد الله بن محمد بن أحمد بن يحيى السعدى (فى القرن الخامس الهجرى)، الظاهرية مجموع ٦٣ (جزء ٤، الأوراق ٤٤ - ٦٢، ٥٣٧ هـ، انظر: العش ٢٦١)، القاهرة ثان ٥/ ٢٨٨، تاريخ ٧٨ م (٥٥٠ هـ).
١١ - «مناقب الإمام أبى حنيفة» للموفق بن أحمد بن إسحاق المكى الخوارزمى (المتوفى ٥٦٨ هـ/ ١١٧٢ م انظر بروكلمان ملحق ١/ ٥٤٩، ٦٤٢)، طبع بحيدرآباد ١٣٢١ هـ. واختصره تقى الدين يحيى بن محمد بن يوسف الكرمانى (المتوفى ٨٣٣ هـ/ ١٤٣٠ م). (٤٤) حكيم أوغلى ٨٠٦ (٥٣ ورقة، ٨٥٤ هـ).
_________
(٤٣) انظر: الجواهر للقرشى ١/ ٣٤٣، وهدية العارفين ١/ ٦٥١.
(٤٤) انظر: معجم المؤلفين لكحالة ١٣/ ٢٣٠.','3','34',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (791,'١٢ - «مناقب أبى حنيفة وأخبار أصحابه» لأبى الحسن الدّينورى (فى القرن السادس الهجرى)، مانيسا ١٣٤٢/ ٣ (الأوراق ٩٨ - ١٦٣، ٥٦٣ هـ، انظر آتش فى مجلة معهد المخطوطات العربية ٤/ ١٨).
١٣ - «فصل على تقديم مذهب أبى حنيفة» لمحمود بن منصور بن أبى الفضل (يحتمل أن يكون فى القرن السادس الهجرى إذا كان هو نفسه محمود بن منصور بن منصور) (٤٥)، تشستربيتى ٤٢١٦ (الأوراق ٢٨ - ٣٨ القرن السابع الهجرى).
١٤ - «الرد والانتصار لأبى حنيفة إمام فقهاء الأمصار» أو «الفوائد المهمة فى الذب عن أبى حنيفة» لأبى الوجد محمد بن محمد بن عبد الستار العمادى الكردرى (المتوفى ٦٤٢ هـ/ ١٢٤٤ م، انظر بروكلمان ١/ ٣٨١).
١٥ - مناقب الامام أبى حنيفة، لمحمد بن محمد بن النقيب (عاش نحو سنة ٧٤٥ هـ/ ١٣٤٤ م) حراجى زاده فى بورسه ١٠٤٦/ ٢ (من ٥٤ ب- ١٠٤ أ، ٧٤٥ هـ، بخط المؤلف).
١٦ - «مناقب الامام أبى حنيفة» لمحمد بن أحمد بن عثمان الذهبى (المتوفى سنة ٧٤٨ هـ/ ١٣٤٨ م انظر بروكلمان ٢/ ٤٦)، توجد مخطوطة له بإحدى مكتبات حيدرآباد، نشره محمد زاهد الكوثرى، وأبو الوفا الأفغانى القاهرة ١٣٦٦ هـ.
١٧ - «الرسالة النضرة لمذهب الإمام الأعظم أبى حنيفة» لمحمد بن محمود أكمل الدين البابرتى (المتوفى سنة ٧٨٦ هـ/ ١٣٨٤ م انظر بروكلمان ٢/ ٨٠)، ليتسج ٧٠٧ (من ٢ أ- ٨ أ)، القاهرة بان ٨/ ١٤٧، ٢٤٧، حليم، تاريخ ٢٥.
١٨ - «منازل الأئمة الأربعة ... الخ» ليحيى بن إبراهيم بن محمد السّلماسى الفاتح ٤٤٤٦/ ٢ (من ٨٩ أ- ١٣٩ ب، ٨٢٢ هـ).
١٩ - «مناقب ... إلخ» لمحمد بن محمد الكردى البزازى (المتوفى ٨٢٧ هـ/ ١٤٢٤ م انظر بروكلمان ٢/ ٢٢٥)، حراجى زاده فى بورسة ١٠٥٥ - (١٧٣ ورقة)، القاهرة ثان ٥/ ٣٦١ تاريخ ١٠٧ م (٨٢٩ هـ)، طبع بحيدرآباد ١٣٢١ هـ.
٢٠ - «تبيض الصحيفة فى مناقب أبى حنيفة» لعبد الرحمن بن أبى بكر السيوطى (المتوفى ٩١١ هـ/ ١٥٠٥ م انظر بروكلمان ٢/ ١٤٤). طبع بحيدرآباد ١٣١٧ هـ.
_________
(٤٥) انظر: معجم البلدان لياقوت ٣/ ٥٣٥.','3','35',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (792,'٢١ - «عقود الجمان فى مناقب أبى حنيفة النعمان» لشمس الدين أبى عبد الله محمد بن يوسف بن على بن يوسف الدمشقى (المتوفى ٩٤٢ هـ/ ١٥٣٥ م انظر بروكلمان ٢/ ٣٠٤) ومخطوطاته فى (فينا ١١٨٠، جامع ينى ٨٧٠، أيا صوفية ٣٣٠٩، القاهرة أول ٥/؟ ، ٢٧٠، باتنة ٢/ ٣١٢ رقم ٢٤٥٤).
٢٢ - «الخيرات الحسان فى مناقب الإمام .. الخ» لأحمد بن محمد بن على بن حجر الهيتمى (المتوفى ٩٧٣ هـ/ ١٥٦٥ م انظر بروكلمان ٢/ ٣٨٨) وهذا الكتاب رد اعتبار أبى حنيفة من مطاعن الغزالى، طبع بالقاهرة ١٣٠٤ هـ، ١٣١١ هـ، ١٣٢٦ هـ.
٢٣ - «قلائد عقود الدّرر والعقيان فى مناقب الإمام أبى حنيفة النعمان» لأبى القاسم عبد العليم بن أبى القاسم بن عثمان الحنفى (ألفه سنة ٩٧٤ هـ/ ١٥٦٦ م) الظاهرية تاريخ ٥٠ (١٦٣ ورقة/ فى القرن الحادى عشر الهجرى، انظر: العش ٢٦٣)، جاريت ٧١٤ (١٣٧ ورقة، فى القرن الحادى عشر الهجرى) (الموصل ٢٥١، ٤٣).
٢٤ - «مناقب ... إلخ» لعبد الغفور بن حسين بن على الألمعى، سليم أغا ٥٣٨/ ٤ من ٢٠٧ - ٢١٠، ٩٩٦ هـ).
٢٥ - «مناقب أبى حنيفة وصاحبيه أبى يوسف ومحمد بن الحسن» لأبى الليث محرّم بن محمد الزّيلعى (المتوفى سنة ١٠٠٧ هـ/ ١٥٩٩ م انظر بروكلمان ملحق ٢/ ٦٥١)، موجود بخط المؤلف بالتيمورية، تراجم ٧٦٠ (٢٦ ورقة، انظر: فهرست معهد المخطوطات ٢ رقم ٥١٦ (السليمانية ٨٤٠ رقم ٣٤٢٥، فاتح ٥٣٢٨، قوله بالقاهرة ٢/ ٢٤٧ - ٢٤٨ (كتب سنة ١٠١٦ هـ/ ١٦٠٧ م).
٢٦ - «الدّر المنظّم فى مناقب الإمام الأعظم» لنوح بن مصطفى الرومى المصرى (المتوفى سنة ١٠٧٠ هـ/ ١٦٥٩ م انظر بروكلمان ٢/ ٣١٤)، ولى الدين ٥٧١/ ٤ (من ٦٦ أ- ٧٩ ب، فى القرن الحادى عشر الهجرى)، القاهرة ثان ٥/ ١٧٧ مجموعة ٣٧٣، الأزهر ٥/ ٤٣٦ مجموعة ٢٢٨ (الأوراق ٣٣ - ٤٨).
٢٧ - «إتحاف المهتدين بمناقب أئمة الدين (الأربعة)» لأحمد بن عبد المنعم بن خيام الدمنهورى (المتوفى سنة ١١٩٢ هـ/ ١٧٧٨ م انظر بروكلمان ٢/ ٣٧١) القاهرة ثان ٥/ ٦، تاريخ ٨٥٨.
٢٨ - «الريحان لمناقب النعمان» لمؤلف مجهول، سراى أحمد الثالث ٢٦٦٨، (٥٣ ورقة ٩٨٥ هـ، انظر فهرس معهد المخطوطات ٢، رقم ٢٧٤).
٢٩ - «مناقب أبى حنيفة» لمؤلف مجهول، القاهرة ثان ٥/ ٣٦١ مجموعة ٧٨.
٣٠ - «مناقب الأئمة الأربعة» لمؤلف مجهول، القاهرة ثان ٥/ ٣٦٠، طبع بتونس ١٢٨٠ هـ.','3','36',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (793,'٣١ - «النوادر المنيفة بمناقب الإمام أبى حنيفة» لعبد الأول الجنبورى طبع على الحجر بكوانبور ١٣١٠ هـ.
٣٢ - «حياة الإمام أبى حنيفة» للسيد عفيفى، بالقاهرة ١٣٥٠ هـ.
٣٣ - «أبو حنيفة» لمحمد أبو زهرة، القاهرة ١٩٤٧ م.
٣٤ - «أبو حنيفة والقيم الإنسانية» لمحمد يوسف موسى، القاهرة ١٩٥٧ م.

<span data-type="title" id=toc-1969>ب- آثاره:</span>
أولا: «الفقه الأكبر» ويقال إن ابنه حمادا رواه عنه (٤٦)، والكتاب يبدأ بأصل التوحيد، ومخطوطاته: فينا ١٩٠٣/ ٢ (الأوراق ١٠١ - ١٠٥، ١١٥٥ هـ)، المتحف البريطانى مخطوطات شرقية ٧٧٢١/ ١ (٤٧) (انظر إليس، قائمة وصفية ٥٣) (٤٨) القاهرة ثان ١/ ٢٠٠ - ٢٠١، كلام ١٢٧، ١٧٥، ٢٠٣، ١٠٧٩، ١١٢٧، بوهار ٤٥٦/ ٣ (الأوراق ٥٤ - ٥٧، فى القرن الثالث عشر الهجرى)، قوقاز- المتحف الأسيوى بطرسبورج ٧٥٧ - ٧٥٩)، سليم أغا ٣٩٤/ ٢ (من ٩٧ ب- ١٠٣ أ)، سراى ريفان ٢٠٣٦/ ١ (من ١ ب- ٣ ب، فى القرن الثانى عشر الهجرى) مكتبة جامعة إستنبول ١٤٩٧ (من ١٣٩ ب- ١٤٣ ب، فى القرن الثانى عشر الهجرى)، ٣٧٨٣ (من ٣٩ ب- ٤١ ب، فى القرن الثالث عشر الهجرى)، ٥٢٧٧ (٧ ورقات، القرن ١٣ هـ)، بايزيد ٣١٦٣/ ٣ (١٩٠ ب- ١٩٤ ب، فى القرن الثالث عشر الهجرى)، برلين ١٩٢٣/ ١ (الأوراق ١ - ٤، ١٠٦٤ هـ)، ١٩٢٤ (الأوراق ١٤١ - ١٤٤، حوالى ١٠٠٠ هـ)، صائب بأنقرة ٢٧٨٣/ ١، كابول، انظر مجلة معهد المخطوطات ٢/ ٦، باريس ١٢٣٠ (الأوراق ١ - ٦، فى القرن الحادى عشر الهجرى)، الأزهر ٣/ ٢٩٤ - ٢٩٥، كلام ١٣٦ (الأوراق ١ - ٤)، ٣٩٢٢ (الأوراق ١ - ١٢) مجموع ١٣٣ (الأوراق ٩٩ - ١٠٨، ١٢٤٤ هـ)، مجموع ٢٣٧ (الأوراق ٦٢ - ٦٥، ٣٧٥ (الأوراق ١٥٨ - ١٦٣، ١٠٩٨ هـ)، ٤٨٦ (الأوراق ٣٦ - ٣٩، ١٢٣٥ هـ)، طشقند ٥/ ٤٣٧ - ٤٣٩ أرقام ٤٨٠٩ - ٤٨١٦. طبع بالقاهرة ١٣٢٣، وبحيدرآباد ١٣٤٢ هـ.
وترجم إلى اللغة الأوردية وطبع بدلهى ١٢٨٩، وكذلك إلى اللغة البنجابية وطبع بلاهور ١٨٩٠ م. ترجمه هل إلى الألمانية J.Hell ,von M.bis Ghazali ,Jena ١٩١٥ ,٢٩ ff.:
_________
(٤٦) انظر: الجواهر للقرشى ٢/ ٢٦٥.
(٤٧) ليس رقم ٤٦ كما ذكر بروكلمان.
(٤٨) ليس رقم ٤٦ كما ذكر بروكلمان.','3','37',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (794,'الشروح: ١ - (يطابق رقم ٢ عند بروكلمان فى الملحق) لعلى بن محمد البزدوى (المتوفى ٤٨٢ هـ/ ١٠٨٩ م انظر بروكلمان ١/ ٣٧٣، قوقاز، المتحف الأسيوى ببطرسبورج ٩٣٨)، حققه لورد استانلى:
Lord Stanleyof Alderley, London ١٨٦٢.
(انظر قائمة وصفية ٢/ ٤٤١).
٢ - (يطابق رقم ٣ عند بروكلمان فى الملحق) لأكمل الدين محمد بن محمود بن أحمد البابرتى (المتوفى ٧٨٦ هـ/ ١٣٨٤ م انظر بروكلمان ٢/ ٨٠)، السليمانية ٧٦٩/ ١ (من ١ - ٤٤ أ، فى القرن الحادى عشر الهجرى)، حميدية ٣٨٨ (من ٢٢٤ أ- ٢٧٢ ب، ١١٣٧ هـ) كوبريلى ٧٠٣/ ٢ (من ١٠٣ أ- ١٣٦ ب، ١١١٤ هـ)، بايزيد ٣٠٨١، (١٠٠ ورقة فى القرن الحادى عشر الهجرى)، حاجى محمود باستنبول ١٣٢٤/ ١ (من ١ - ٨٢ ب ١١٦٣ هـ)، الأزهر ٣/ ٢٣٣، كلام ٢٧٦٩ (٣٥ ورقة، وبعنوان «الإرشاد»، القاهرة ثان ١/ ١٦٣، كلام ٧، ٨، ١١٨٥، منجانا ١٨١ رقم ٩٣٤ (٣٩ ورقة، ١٠٨١ هـ)، منجانا ١٨٢ رقم ٢٨٥ (٧١ ورقة ١٢١٢ هـ).
٣ - (يطابق رقم ٤ فى ملحق بروكلمان) تأليف إلياس بن إبراهيم السّينوبى (المتوفى ٨٩١ هـ/ ١٤٨٦ م، انظر (Osm.Muell ,l ,٢٢٢ شهيد على ١٦٧٠ (٧٥ ورقة، ٨٦٧ هـ)، المعهد الإسلامى باستنبول Ist.Isl tetk Enst. طشقند ٤٨١٨/.
٤ - (يطابق رقم ٥ فى ملحق بروكلمان) تأليف أبى المنتهى أحمد محمد المغنيسوى (أتمه سنة ٩٣٩ هـ/ ١٥٣٢ م (Osm.Muell.l ,٢٢٢ باريس ٦٣٩٢ (الأوراق ٩١ - ١٠٧، ١٢٧٤ هـ) الفاتيكان، فيدا ٢٩٦/ ١٠ (الأوراق ١٠٥ - ١٢٩، فى القرن العاشر الهجرى)، برلين ١٩٢٩ (الأوراق ٢٧ - ٥٠، حوالى ١١٠٠ هـ)، ١٩٣٠ (٦٥ ورقة، حوالى ١١٥٠ هـ) مانشستر ٦٣٩ (الأوراق ٩٨ - ١٢٠، القرن الثانى عشر الهجرى)، جوتا ٦٤١ (٧٠ ورقة)، كيمبردج، إضافات ٣٦٣٠ (٣٦ ورقة، عن القرن العاشر الهجرى، انظر: كيمبردج، كتالوج الملحق ٩٠٠)، جاريت ١٦٦٦ (٣٩ ورقة)، ١٦٦٧ (٢٤ ورقة، فى القرن الثانى عشر الهجرى)، ١٦٦٨ (٣٦ ورقة، فى القرن الثانى عشر الهجرى) السليمانية ٨٥٥ (من ٣٦ ب، ٧١ أ ١١١٢ هـ) قليج على ٥٥٣ (من ١ - ٢٥ ب، ١١٦٢ هـ)، سليم أغا ٦٤٥/ ١ (من ١ - ٥١ أ، فى القرن الحادى عشر الهجرى)، الزيتونة بتونس ٣/ ٦٤ رقم ١٤٢٣ (٥٥ ورقة) بنگيبور ١٠/ ٦ رقم ٤٨٧، (١٩ ورقة، ١٢٥٣ هـ) بلدية الإسكندرية ٤٦٦٩/ ١ (٩٣٩ هـ)، «برلين بريل BDMG ٣٢ ١٧٧ قولة ١ - ١٩٢، بودليانا ٤٤٨/ ٢ (قوقاز) المتحف الأسيوى بطرسبورج ٩٣٨، بوهار ٧٦٠/ ٧٦١»، بايزيد ٣٠٨١ (١٠٠','3','38',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (795,'ورقة، فى القرن الحادى عشر الهجرى)، نور عثمانية ٢١٧٨/ ٢ (من ٦٧ أ- ٧٠ أ، ١٠٧٨ هـ)، حاجى محمود باستنبول ٦٤٠٢/ ٦، الحميدية ١٤٥٩/ ١، رشيد ٣٢٥، فاتح ٣٠٩٢، ٥٣٠٧/ ٣، ٥٣٩٢ (من ٩٠ ب- ١٠٢ أ، ١١٤٢ هـ)، أسعد ٣٥٢١/ ٢ (من ١٦٤ ب- ١٦٧ ب، فى القرن الحادى عشر الهجرى)، ينى ١١٩٠/ ٧ (من ٣٠٢ ب- ٣٠٤ ب، فى القرن الحادى عشر الهجرى)، نافز ٨٠٦، حسن حسنى عبد الوهاب ٥٨٣/ ١، ١٠٨٠/ ٣، قليج على ٨٣٦، ١٠٤٣/ ٥، لاله لي ٣٧٢/ ١ (من ١ - ٧ ب، فى القرن الثانى عشر الهجرى) ٢١٤٥/ ١ (من ١ - ١٠ ب، فى القرن الثالث عشر الهجرى)، رشيد ١٠٣٦/ ١ (من ٩ أ- ١١ ب، فى القرن الثانى عشر الهجرى)، داماد إبراهيم ٤٨٦/ ٣ (من ٥٦ - ٥٩ أ، فى القرن الثالث عشر الهجرى)، عاشر ٤١٢/ ٢ (من ٦١ ب- ٦٥ ب، ١٢٠٩ هـ)، ٤٥٢/ ١٤ (من ٢٠٤ ب ٢١٤ أ، ١١٣٩ هـ)، ييل أ- ٢٦٢ (٣٧ ورقة، فى القرن الحادى عشر الهجرى، انظر: نموى ٥ رقم ٩٢٩)، الزفازيق ٢٨٠١ (انظر: مجلة معهد المخطوطات العربية ٣/ ٨٥)، طبع فى قازان ١٨٩٦ م، ودلهى ١٣٠٦ هـ، وحيدرآباد ١٣٢١ هـ.
٥ - (يطابق رقم ٦ فى ملحق بروكلمان) «المنح الأزهر» لعلى القارى الهروى (المتوفى سنة ١٠١٤ هـ/ ١٦٠٥ م انظر بروكلمان ٢/ ٣٩٤) برلين ١٩٣١ (١٦٥ ورقة، حوالى ١١٥٠ هـ) جوتا ٦٤٢، أيا صوفية ٢٣١٣ - ٢٣١٥ نور عثمانية ٢١٨٥، كيمبردج، إضافات ٣٦٢٦/ ٩ (١٠٨ ورقة، فى القرن الرابع عشر الهجرى، انظر كتالوج الملحق ٨٩٩)، مانشستر ٤٢٦ (١٠١ ورقة، فى القرن الثانى عشر الهجرى)، الزيتونة بتونس ٣/ ٤٢ رقم ١٣٧١ (١٧٣ ورقة)، ١٤٧٢، (١١١ ورقة)، قليج على ٥٥٤ (٢٣٣ ورقة) فى القرن الثانى عشر الهجرى) السليمانية ٧٦٩/ ٣ (من ٦٩ أ- ٢٢٩ ب. فى القرن الحادى عشر الهجرى)، ٦٤٥/ ١ (من ١ - ٥١ أ، فى القرن الحادى عشر الهجرى (٤٨)، بنگيور ١٠/ ٦ رقم ٤٨٨ (١١٨ ورقة، فى القرن الثانى عشر الهجرى)، ٤٨٩، (١٣٢، ورقة، فى القرن الثانى عشر الهجرى)، جاريت ٢٠٩١/ ٤ (فى القرن الثانى عشر الهجرى)، «قوقاز المتحف الأسيوى ببطرسبورج ٩٣٨»، الظاهرية، عام ٥٢٣٩، ٨١٢٥، ٨٢٦٤، «الموصل ٣، BDMG، ٦١، ٩٦ ب، ٣١ أ، باتنة ١٢٢، ١٢٢٣ - ٦»، شهيد على ١٦٦٩ (١٤٧ ورقة، ١٠٧١ هـ) سراى، أحمد الثالث، ١٨٦٧/ ١ (الأوراق ١ - ٢١٩، ١٠٩٤ هـ)، ريفان ٦٤٥ (١٧١ ورقة، فى القرن الثانى عشر الهجرى)، داماد إبراهيم ١١٥٣ (١٦٦ ورقة، فى القرن الثالث عشر الهجرى)، حميدية ٧٦٢ (١٥١ ورقة، فى القرن الثانى عشر الهجرى)، وهبى ٢٠٦٢/ ٤ (الأوراق ١٨ - ٤٨، فى القرن الثانى عشر الهجرى)، لاله لي ٢٣٤٢ (١٣١ ورقة، فى القرن الثانى عشر الهجرى)، ٢٣٤٣ (١٥٣ ورقة، ١٠١٩ هـ)، ٢٣٤٤ (٨٠ ورقة)، ٢٣٤٥ (١٠٢ ورقة)، ٢٣٤٦ (٢٣٩ ورقة، ١٠٨٠ هـ)، كوبريلى ٣/ ٢٤٩ (١٧٧ ورقة، ١١٢٨ هـ)، ٧٠٣/ ١ (من ١ - ١٠١ أ، ١١٤١ هـ)، جار الله ١٢٢٨/ ١.
_________
(٤٨) تحذف مخطوطة سليم أغا ٤٤ التى ذكرها بروكلمان.','3','39',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (796,'حكيم ٨٣٧، تشستربيتى ٣٥٦٨ (١٤٦ ورقة، ١٠١٠ هـ، بخط المؤلف)، مكتبة جامعة استنبول ١٠٦٨، (١٥٠ ورقة، ١١١٦ هـ)، ٢٤٥٨ (١٤٥ ورقة، فى القرن الثانى عشر الهجرى)، بلدية الإسكندرية ٣٠٩٤/ ٣.
طبع بطشقند ١٣١٢ هـ، وبالقاهرة ١٣٢٣ هـ، وبكوانبور ١٣٢٧ هـ، وبالقاهرة ١٩٥٥ م.
٦ - (يطابق رقم ١١ عند بروكلمان فى الملحق) تأليف علاء الدين على البخارى وأهداه الى ألغ بك (حكم من ٨٥٠ هـ/ ١٤٤٧ م- ٨٥٣ هـ/ ١٤٤٩ م)، بنگيبور ١٠/ ٥ رقم ٤٨٦ (٤٠ ورقة، ١٠٨٧ هـ)، رامبور ١/ ٣١١، كلام ٢٢٤.
٧ - تأليف محمد بن محمد خطيب زاده (كتبه حوالى ٩٢٠ هـ/ ١٥١٤ م)، مانيسا ٩٦٤ (٢٧٣ ورقة، ٩٢٠ هـ، بخط المؤلف).
٨ - (يطابق رقم ١٦ فى ملحق بروكلمان) «القول الفصل إذ كله جدّ/ وما هو بالهزل» لمحمد بن بهاء الدين بن لطف الله البيرامى (المتوفى سنة ٩٥٦ هـ/ ١٥٤٩ م، انظر: معجم المؤلفين لكحالة ١١/ ١٦٥)، القاهرة ثان ١/ ٢٠٣، كلام ١٣١، ٢٨٢، راغب ٧٨٩ (١٩٦ ورقة، ٩٢٥ هـ، بخط المؤلف)، مكتبة جامعة إستنبول ٢٣٩٩/ ١ (الأوراق ١ - ٢١٤ ب، ١٢١٣ هـ).
٩ - «مختصر الحكمة النبوية» للحكيم إسحاق الرومى (المتوفى سنة ٩٥٠ هـ/ ١٥٤٣ م، انظر: معجم المؤلفين لكحالة ٢/ ٢٣٣)، حسن حسنى ١١٩١ (الأوراق ١ - ٧٨)، ليدن، مخطوطات شرقية ٦٣٧٢ (الأوراق ٧٩ - ١٣١، ١٠٨٦ هـ، انظر: فورهوف ٨٤)، بلدية الإسكندرية ٣٥٦٣، ٥٢٠٦.
١٠ - (يطابق رقم ١٤ فى ملحق بروكلمان) تأليف على بن مراد العمرى الموصلى (المتوفى سنة ١١٤٧ هـ/ ١٧٣٤ م انظر: معجم المؤلفين لكحالة ٧/ ٢٤١)، المتحف البريطانى، الملحق ١٢٥٣، مخطوطات شرقية ٤٣٧٥ (الأوراق ١٤٤ - ١٥٤، فى القرن الثانى عشر الهجرى) لاله لي ٢٣٢٥ (٤٠ ورقة).
١١ - (يطابق رقم ١٢ فى ملحق بروكلمان) تأليف أبى الفتح عثمان الشافعى، «المتحف الأسيوى ببطرسبورج بخارى ٧٦٢».
١٢ - (يطابق رقم ١٥ فى ملحق بروكلمان) تأليف معين الدين أبى الحسن عطاء الله بن محمد القرشاوى، قازان ١٨٩٠ م.
١٣ - (يطابق رقم ١٧ فى ملحق بروكلمان) «درر الأنهر» لعبد القادر سلهتى، حيدرآباد بالهند ١٢٩٨ هـ.','3','40',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (797,'١٤ - (يطابق رقم ١٨ فى ملحق بروكلمان) «الضوء الأكثر» تأليف النّصيحى الفاهمى الطّرسونى، رامبور ١/ ٣١٣، كلام ٢٤٣ (٨١ ورقة).
١٥ - تأليف إبراهيم بن حسن الإشكدراوى (المتوفى ١٢٦٠ هـ/ ١٨٥٤ م) رشيد ٩٩٠/ ٣ (من ٢٠ ب- ٦٠ أ، ١٢٦٠ هـ).
«ونظم كتاب الفقه الأكبر إبراهيم بن حسام الجرميانى (المتوفى ١٠١٦ هـ/ ١٦٠٧ م)، بخارى، المتحف الآسيوى ببطرسبورج ٣٧٦٤»، لاله لي ٣٧٦٠/ ٣ (من ٤١ أ- ٥٠ ب، ١٠١٢ هـ، بخط المؤلف)، «وترجمه إلى التركية مير وحدى، وأتمة ١٠٩٩ هـ/ ١٦٨٨ م، مستقيم زاده (المتوفى سنة ١٢٠٢ هـ ٢/ ١٧٨٧ م)، القاهرة ثان ٧/ ٤٠٨، وطبع باستنبول بدون تاريخ».
حول كتاب الفقه الأكبر: «سبعيات الفقه الأكبر» لأبى الطيب حمدان حمدويه الطّرسوسى (؟ أبى الطيب حمدان بن حمزة، انظر: الجواهر للقرشى ١/ ٢٢٧)، اسعد افندى ٣٦١٣/ ٧، (الأوراق ٣٩ - ٤٣)، حسن حسنى بتونس ١٨٧/ ٢ (٦٦ ورقة، ١١٨٤ هـ)، باريس ٩٧٨/ ٦ (الأوراق ٤٦ - ٩٦، فى القرن الحادى عشر الهجرى) (٤٩).
ثانيا: «الفقه الأكبر الفقه الأبسط» يقال ان أبا المطيع الحكم بن عبد الله بن سلمة (المتوفى ١٩٩ هـ/ ٨١٤ م) (٥٠)، تلميذ أبى حنيفة رواه عنه. (٥١) ومخطوطاته فى: القاهرة ثان ١/ ٢٠٠، مجموع ٦٤ م، ٢١٥ م. وتوجد نسخة مصورة منه فى ليدن، انظر: فورهوف ٨٤، فاتح ٥٣٩٢/ ٢ (٦٠ أ- ٨٨ أ، ١١٤٢ هـ)، سليم أغا ٥٨٧/ ٣ (من ١٤٦ ب- ١٥٣ ب، ٧٦١ هـ). طبع بالقاهرة ١٣٠٧ هـ، ١٣٢٤ هـ، ونشره محمد زاهد الكوثرى بالقاهرة ١٣٦٨ هـ.
الشروح: ١ - لإبراهيم بن إسماعيل الملطى (واسمه فى مخطوطات أخرى أبو ابراهيم إسماعيل بن إسحاق الخطيرى، ربما عاش فى القرن الخامس الهجرى) (٥٢).
_________
(٤٩) تحذف الشروح التى ذكرها بروكلمان بالأرقام ٨، ١٠، ١٣، ١٦.
(٥٠) انظر: تاريخ بغداد للخطيب ٨/ ٢٢٣ - ٢٢٥.
(٥١) انظر: الجواهر للقرشى ٢/ ٢٦٦.
(٥٢) انظر: الجواهر للقرشى ١/ ٣٥، ١٤٦.','3','41',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (798,'وهذا الشرح نفسه هو الشرح المنسوب إلى أبى منصور الماتريدى (٥٣)، بنگيبور ١٠/ ٢ ومخطوطاته فى سليم أغا ٥٨٧/ ٤ (من ١٥٤ أ- ١٦٦ أ، ٧٦١ هـ)، فيض الله ٢١٥٥/ ١ (من ١ - ١٣ أ، ٩٩٦ هـ). وطبع بحيدرآباد ١٣٢١ هـ.
٢ - لعطاء بن على بن محمد الجوزجانى (كتب قبل سنة ٦٨٧ هـ/ ١٢٨٨ م) فاتح ٣١٣٧/ ١ (من ١ أ- ٥٣ ب، ٦٨٧ هـ)، ٣١٣٩/ ٥ (من ١١٧ ب- ١٥١ ب)، ينى ١١٩٠/ ٤ (من ٢٧٩ أ- ٢٩٧ ب، ١٠٧٣ هـ)، انظر أيضا: فاتح ٥٣٩٢/ ٢ (على الهامش ٦٠ أ- ٨٨ أ، ١١٤٢ هـ).
ثالثا: «مسند أبى حنيفة» ١ - برواية ابى يوسف، ورواه بعده ابنه يوسف، القاهرة ثان ١/ ١٤٧، حديث ١٦١٧ (١٧٨ ورقة، فى القرن السادس الهجرى)./ ٢ - فى رواية الحسن بن زياد اللؤلؤى (المتوفى ٢٠٤ هـ/ ٨١٩ م)، الأوقاف ببغداد ١٠٠٠٥ (انظر:
طلس رقم ٤٤٤).
٣ - (يطابق رقم ٣ فى ملحق بروكلمان) بتهذيب أبى محمد عبد الله بن محمد بن يعقوب بن الحارث البخارى الحارثى الأستاذ (المتوفى ٣٤٠ هـ/ ٩٥٠ م) (٥٤) برلين ١٩٢٨ (من ٧ ب- ١٥٢ أ)، القاهرة ثان ١/ ١٤٧، حديث ٤٣٠ (حوالى ٢٠٠ ورقة)، ٧٦٨ (٥٢ ورقة غير كامل)، ١٦٥٩ (١٢٢ ورقة، فى القرن التاسع الهجرى)، سليم أغا ٢٣٣ (١٩٥ ورقة فى القرن التاسع الهجرى)، الأزهر ١/ ٦٠٣، حديث ١٩٣٠ (١٧٤ ورقة، ١١٤٩ هـ، انظر فهرس معهد المخطوطات ١/ ٩٩)، سراى، أحمد الثالث ٣٦٦ (١٢٢ ورقة، ٧٣٣ هـ، انظر: فهرس ٢/ ١٠٥)، فاتح ٤٤٩٤/ ٣ (من ٥٦ ب- ١٦١ أ، ٦٠٤ هـ)، شهيد على ٥٦٣ (من ١ - ١٦٠ أ، فى القرن الثامن الهجرى)، الظاهرية عام ٤٦٩٦ (١٨٥ ورقة، ٨٧٧ هـ).
٤ - فى رواية أبى بكر محمد بن إبراهيم بن على بن عاصم زاذان المقرئ (المتوفى ٣٨١ هـ/ ٩٩١ م، انظر: رقم ٢٤٥ فى باب الحديث ج ١)، فيض الله ٥٠٧ (من ١٤٤ أ- ١٩١ ب، ٨٤١ هـ).
٥ - فى رواية أبى عبد الله محمد بن إسحاق بن محمد بن منده (المتوفى ٣٩٥ هـ/ ١٠٠٥ م انظر: رقم ٢٧٦ فى باب الحديث ج ١)، «باتافيا بجاكارتا، الملحق ٨٤، برلين ١٩٢٩».
_________
(٥٣) انظر: بروكلمان، الملحق ١/ ٢٨٥.
(٥٤) انظر: الجواهر للقرشى ١/ ٢٨٩ - ٢٩٠، شذرات الذهب لابن العماد ٢/ ٣٥٧','3','42',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (799,'٦ - فى رواية أبى نعيم أحمد بن عبد الله بن أحمد الأصفهانى (المتوفى ٤٣٠ هـ/ ١٠٣٨ م انظر بروكلمان ١/ ٣٦٢) سراى أحمد الثالث ٣٦٥ (٦٠ ورقة، فى القرن السادس الهجرى، انظر: فهرس معهد المخطوطات ١/ ٩٩، فهرس ٢/ ١٠٤ - ١٠٥).
٧ - (يطابق رقم ٢ فى ملحق بروكلمان) فى رواية أبى عبد الله الحسين بن محمد بن خسرو البلخى (المتوفى ٥٢٠ هـ/ ١١٢٦ م انظر بروكلمان ملحق ١/ ٦٣٩) سراى، أحمد الثالث ١٥٨ (فى القرن التاسع الهجرى)، ييل ل- ٢٧٧ (١٩٠ ورقة، فى القرن التاسع عشر الميلادى، انظر نيموى ٦٥٥)، برلين ١٩٢٩. (٥٥).
٨ - عن حسام الدين على بن أحمد بن مكى الرازى (المتوفى ٥٩٨ هـ/ ١٢٠١ م انظر بروكلمان ملحق ١/ ٦٤٩) سراى، أحمد الثالث ٣٦٤ (١٥٨ ورقة، ٨٣٨ هـ، انظر: فهرس ٢/ ١٠٤).
٩ - (يطابق رقم ١٠ فى ملحق بروكلمان) عن موسى بن زكرياء بن إبراهيم الحصكفى (المتوفى ٦٥٠ هـ/ ١٢٥٢ م) (٥٦)، بلدية الإسكندرية ٣٩١٢ ج (١٣١٦ هـ)، بنگيبور ٥/ ١، ١٤٧ رقم ٢٣٥ (٤٥ ورقة، ١٠٠٥ هـ)، ٢٣٦ (٩٦ ورقة، ١٢٦٣ هـ)، بايزيد ١٢٢٥ (٨٥ ورقة، ١١٨٧ هـ)، طبع بالهند ١٣١٦ هـ، وبالقاهرة ١٣٢٧ هـ، وحلب ١٩٦٢ م. وقد أعاد على القارئ ترتيب الكتاب فرتبه وفق الشيوخ وشرحه، آصفية ١/ ٦٣٨ حديث ٢١٩ (١٢٥٠ هـ)، ولى الدين ٧١٧ (الأوراق ١ - ١٥٥، ١١٣٠ هـ)، برلين الشرقية ١٨٣٢، بنگيبور ٥/ ١/ ١٥١ - ١٥٣ رقم ٢٣٧ (٩٦ ورقة، ١٠٨٣ هـ). طبع بالهند ١٣٠٠ هـ، وبلاهور ١٣١٢ هـ.
١٠ - (يطابق رقم ٧ فى ملحق بروكلمان) عن أبى المؤيد محمد بن محمود الخوارزمى (المتوفى ٦٤٥ هـ/ ١٢٤٧ م انظر بروكلمان ملحق ١/ ٦٥٤) بعنوان: «مسانيد أبى حنيفة» أو «جامع المسانيد» القاهرة ثان ١/ ١٤٦، حديث ٤٧ م (٢١٢ ورقة، ٢٣٩ ورقة، ٨٥١ هـ)، ٤١٦ (٣٧٥ ورقة، ١٢٧١ هـ)، ٤٧٥ (٢٩٨ ورقة، ١١٣٤ هـ)، الأزهر، رواق الأتراك، حديث ٥١٨ (٤٢٥ ورقة، ١١٤٢ هـ، انظر: شناخت ١/ ١٠)، قليج ٢٧٣ (٢٥٠ ورقة، ٨٥٨ هـ) ٢٧٤ (٢٧٤ ورقة ١١٩٣ هـ)، بلدية الإسكندرية ١٦٠٧ (١٢٦٣ هـ)، بنكيبور ٥/ ١/ ١٥٣ - ١٥٥ رقم ٢٣٨ (٤٦٤ ورقة، ١٢٤٦ هـ)، ٢٣٩ (ج ١، ١٨٨ ورقة) «باتئة ١/ ٤٧، ٤٨١، ٢/ ٥٠٢، ٢٦٨٨»، سراى، أحمد الثالث ٣٦٧ (٤٢٢ ورقة، ٨٢٥ هـ، انظر فهرس
_________
(٥٥) عند بروكلمان ١٨٢٨ والصحيح ١٩٢٩.
(٥٦) انظر: الجواهر للقرشى ٢/ ١٨٥ - ١٨٦.','3','43',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (800,'٢/ ١٠٥ وفهرس معهد المخطوطات ١/ ٩٩)، لاله لي ٤٣٥ (١٨٢ ورقة، ٨٥٠ هـ)، عاطف ٦١١ (٢٠٣ ورقة، ٨٤١ هـ)، راغب ٢٥٣ (٣٨٢ ورقة)، جوروم ٣٧٣ (٤٩٩ ورقة، فى القرن العاشر الهجرى)، الظاهرية حديث ٥٨٢ (٤٤٦ ورقة، ٨٤٢ هـ). وطبع بحيدرآباد ١٣٣٢ هـ.
واختصر بعض موضوعات هذا الشرح أبو البقاء محمد بن أحمد بن الضياء الحنفى (المتوفى ٨٥٤ هـ/ ١٤٥٠ م انظر بروكلمان ملحق ٢/ ٢٢٢) بعنوان: «المستند مختصر المسند»، كوبريلى ٤٢١ (٣٠٧ ورقة، ٨٥٧ هـ)، رئيس الكتاب ٢٥٥ (١١٨ ورقة، ١١٢٦ هـ)، سوهاج، تفسير ١٠ (١٨ ورقة، ٨٨١ هـ، انظر: فهرس معهد المخطوطات ١/ ١٠٩؟ )./.
١١ - عن القاسم بن قطلوبغا الحنفى (المتوفى ٨٧٩ هـ/ ١٤٧٤ م انظر بروكلمان ٢/ ٨٢)، البنغال ١٦٠ (٤٤٩ ورقة، فى القرن الحادى عشر الهجرى)، برلين ١٢٥٥ (٤٥٢ ورقة حوالى ١١٥٠ هـ).
١٢ - عن سيّد بن عيسى بن محمد الثعالبى (فى القرن الثامن الهجرى)، كوبريلى ٤٢٠ (٧٢ ورقة، فى القرن الثانى عشر الهجرى).
١٣ - وكتب عن كتاب الفقه وفق الفصول محمد عابد بن أحمد السّندى (المتوفى ١٢٥٧ هـ ١٨٤١ م) (٥٧)، المكتب الهندى، دلهى، لغة عربية ٣٠٥، (انظر: شاخت ٢/ ٥) (٥٨). وطبع بالهند ١٣٠٤ (على هامش كتاب» الأدب المفرد «للبخارى») وفى لكنو ١٣١٨ هـ وبالقاهرة ١٣٢٧ هـ.
١٤ - عن محمد حسن اللّكنوى، الهند ١٣٠٩ - ١٣١٦ هـ.
١٥ - وهناك كتب مختلفة عنه لا نعرف من أعدّها، توجد مخطوطة بدار الكتب حديث ١٥٥، طشقند ٤٨١٥ (٨٧٠ هـ، انظر: مجلة معهد المخطوطات ٦/ ٣٢٣).
أعمال أخرى حول المسند: أ- «تنوير السّند فى إيضاح رموز المسند» وهو شرح لكتاب المسند، ألفه عثمان بن يعقوب بن الحسين بن مصطفىالكماخى (حوالى ١١٦٦ هـ/ ١٧٥٢ م) طلعت، حديث ٨٥٤ (٢٤٩ ورقة، ١١٧١ هـ، انظر:
فؤاد سيد فى مجلة معهد المخطوطات ٣/ ٢٣٠).
_________
(٥٧) انظر: معجم المؤلفين لكحالة ١٠/ ١١٣.
(٥٨) تحذف المخطوطات التى ذكرها بروكلمان بمكتبة آصفية ١/ ٦٣٨، ٣٥٧.','3','44',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (801,'ب- «مقصد المسند مع موطأ الإمام محمد (الشيبانى)» وهو مختصر للمسند من عمل أبى عبد الله محمد بن عباد بن ملكداد الخلاطى (المتوفى ٦٥٢ هـ/ ١٢٥٤ م انظر بروكلمان ١/ ٣٨١)، القاهرة ثان ١/ ١٤٥، حديث ٤٤٠/ ٢ (٣٧ ورقة، ٧٩٠ هـ).
ج- عن شيوخه كتاب: «مشيخة أبى حنيفة» لأبى أمية مروان بن ثوبان، القاهرة ثان ١/ ١٠٧ حديث ١٢٥٩ (٤ ورقات)، وكتاب «ذكر من روى عنه أبو حنيفة»، لمؤلف مجهول، أنقرة، صائب ٢٨٨٩ (٢٤٦ ورقة، فى القرن الثامن الهجرى)، شهيد على ٥٦٣ (من ١٦١ أ- ١٦٩ ب، فى القرن الثامن الهجرى).
د- مجموعات أخرى من الروايات: «جزء من رواية أبى حنيفة» جمعها أبو الحجاج يوسف بن خليل الدمشقى (المتوفى ٦٤٨ هـ/ ١٢٥٠ م) (٥٩)، الظاهرية مجموع ٥٩/ ١١ (من ١٢٠ أ- ١٢١ ب، فى القرن الثامن الهجرى).
هـ- «أحاديث سبعة عن سبعة من الصحابة» تخريج الحسن بن أبى بكر بن أبى القاسم، فاتح ٤٥٩٢/ ٢ (من ٩١ أ- ٩٤ ب، ٨٠٥ هـ).
ز- «كتاب الأربعين المختارة من حديث الامام أبى حنيفة»، تخريج يوسف بن عبد الهادى (المتوفى ٩٠٩ هـ/ ١٥٠٣ م انظر بروكلمان ٢/ ١٠٧)، الظاهرية، مجموع ٥٩ (من ٢٢ أ- ٣٧ أ، فى القرن العاشر الهجرى، بخط المؤلف).
ح- «عوالى الإمام أبى حنيفة»، جمعها الدمشقى، الظاهرية، مجموع ٧/ ٥، (من ٤٣ أ- ٤٩ ب، فى القرن السادس الهجرى).
رابعا: «وصية»: وهى وصية إلى أصدقائه فى أصول الإسلام، توجد فى روايتين وبينهما بعض الاختلافات. وبشأن أول هذه الوصية، انظر برلين ٣٩٧٠ (الأوراق ١٠٧ - ١٠٩)، ٣٩٧١ (من ١٠٧ ب- ١١١ أ)، (من ٦٠ ب- ٦٤ ب)، ٣٩٧٢ (الأوراق من ١ - ٦، فى القرن الثانى عشر الهجرى).
المخطوطات التى ذكرها بروكلمان: الفاتيكان، فيدا ٢٩٦/ ١٢ (الأوراق ١٣٥ - ١٣٦، فى القرن العاشر الهجرى)، القاهرة ثان ١/ ٢١٣ مجموعة ٧٨، ٨٦، ١٥٧، ٢٠٥، ٢١٣، ٢١٥ م، ٥٤٣، الإسكوربال
_________
(٥٩) انظر: الأعلام للزركلى ٩/ ٣٠٤، ومعجم المؤلفين لكحالة ١٣/ ٢٩٧.','3','45',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (802,'١٥٦٣/ ١ (الأوراق ١ - ٦، فى القرن التاسع الهجرى)، المتحف البريطانى ٧٧٢١/ ٢، انظر: قائمة وصفية ٦)، «بولونيا ١٩٧/ ٢، بخارى، المتحف الاسيوى بطرسبورج ١١٣٨ - ١١٣٩، تيمور، فقه ٧٠٦، قولة ١/ ٢١٣، ٢٧٠ (٦٠) فاتح ٥٣٩٢/ ٥ (من ١٠٩ ب- ١١٥ ب، ١١٤٢ هـ)، مكتبة جامعة إستنبول ١٤٩٧ (من ١٤٤ أ- ١٤٦ ب، فى القرن الثانى عشر الهجرى)، بايزيد ٢٩٨٧/ ١ (من ١ - ٤ أ، ١١٠٠ هـ)، ٣٠٨٠/ ٢ من ٤ ب- ٦ أ، ١١٧٨ هـ)، عاطف ٢٨١٧/ ٢٥ (من ٥٨ أ- ٥٩ أ، فى القرن الثانى عشر الهجرى)، ٢٩٣٦/ ٢ (من ١٠٥ ب- ١١١ أ، ١٠٦٦ هـ)، لاله لي ٢٤١٠/ ٢ (من ٧ ب- ١١ ب، فى القرن الثانى عشر الهجرى)، ٣٦٩٧/ ٣، (من ٨٣ ب- ٨٧ أ، فى القرن الثانى عشر الهجرى)، ٣٧٤٩/ ٢ (من ١٨ ب- ١٩ أ، فى القرن ١٢ هـ)، أسعد ٣٥٦٥/ ٣ (٤ أ- ٥ أ، فى القرن الثانى عشر الهجرى)، نور عثمانية ٢٤٢٣/ ٢ (من ٨٠ أ- ٨٣ ب، فى القرن الحادى عشر الهجرى) ينى ١١٩٠/ ٥ (من ٢٩٨ أ- ٣٠٠ ب، فى القرن الحادى عشر الهجرى)، منجانا، فهرس رقم ١٨٥ - ١٨٧، حيدرآباد، انظر، ٣٩٧ ZDU G ٩١ /: ليدن ١٩٧٦، (الأوراق ١١١ - ١١٢، انظر: فورهوف ٣٩٨)، باريس ٧٦٢ (الأوراق ٣ - ٦)، ٩٧٨/ (الأوراق ٩ - ١٢)، ١١٢٢، (الأوراق ٨٣ - ٨٩)، ١٢٣٠ (الأوراق ٦ - ٩)، ١٣٩١ (الأوراق ١١٤ - ١١٦)، ٢٣١٦/ ٤ (الأوراق ٣٧ - ٤٠، فى القرن الحادى عشر الهجرى)، ٤٨٠٠ (الأوراق ٤٣ - ٤٤)، ٤٨٠٨ (أوراق ٥١ - ٥٥)، انظر أيضا، فيدا ٧٢٥، كابول؟ انظر: مجلة معهد المخطوطات ٢/ ٦، الأزهر ٣/ ٣٣٩، كلام ٣٣٠٧ (الأوراق ١٩٤ - ١٩٦، ١١٣٣ هـ)، مجموعة ٢٣٧ (الأوراق ٦٥ - ٦٨)، مجموعة ٩٧٥ (الأوراق ٩٤ - ٩٦). وطبع بالقاهرة ١٩٣٦ م. وترجم إلى التركية ١٩٦٢.
الشروح: ١ - لمحمد بن محمود البابرتى (المتوفى ٧٨٦ هـ/ ١٣٨٤ م انظر بروكلمان ٢/ ٨٠)، مانشستر ٦١٤ (الأوراق ٣ - ٢٦، ١٠٥٣ هـ، انظر الفهرس ٧٨٨)، جاريت ٢٠٢١/ ٩ (١١٠٨ هـ)، سليم أغا ١٥٠ (من ١ - ٣١ ب فى القرن الثانى عشر الهجرى)، أسعد أفندى ١٢١٠/ ٣ (من ٥٠ ب ٧٢ ب، فى القرن الحادى عشر الهجرى)، لاله إسماعيل ٢٧٢ (٣٠ ورقة، فى القرن ١٢ الهجرى لاله لي ٢٣٨٨ (٣٠ ورقة، ١١٤٦ هـ)، حاجى محمود ١٤٧٦، (٢٧ ورقة فى القرن الثانى عشر الهجرى)، عاشر ٤١٢/ ٤ (الأوراق ٨٦ - ١١٧، ١٢٠٩ هـ)، مكتبة جامعة إستنبول ١٥٥٣/ ٣ (من ٦ ب- ١٩ أ، فى القرن الثانى عشر الهجرى)، ٣١٤٠ (من ١٠١ أ- ١٢٥ ب، فى القرن الحادى عشر الهجرى)، ٣٨٢٣ (من ١ - ٣٩ ب، فى القرن الثالث عشر الهجرى)، أيا صوفية ٢٣٣٠، (٤٥ ورقة، ٨٥١ هـ)، نور عثمانية ٢٢٠٦ (من ١ ب-
_________
(٦٠) تحذف المخطوطات التى ذكرها بروكلمان فى باريس ٦٣٧٨، وفى بريل التى توجد الآن فى جاريت ١٧٦٩.','3','46',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (803,'٣٥ ب، ١٠١٧ هـ)، باريس ٤٨٠٣ (الأوراق ٣ - ١٦، انظر: فايدا ٧٢٥)، منجانا ٨٢٠/ ٢ (٤٨ ورقة فى القرن الثانى عشر الهجرى).
٢ - «الجواهر المنيفة» لملّا حسين بن إسكندر الرومى الحنفى (المتوفى حوالى ١٠٨٤ هـ/ ١٦٧٢ م) (٦١)، جاريت ١٧٦٩ (١٣ ورقة، فى القرن الثانى عشر الهجرى)، آصفية ٢/ ١٠٨٠، فقه حنفى ٣٧٢، الجزائر ٥٥٣ (٢٠ ورقة، ١١٨٢ هـ) (قوقاز، المتحف الأسيوى ببطرسبورج ٩٤٦) (٦٢). مكتبة جامعة إستنبول ٥٢٧٢، (٢٠ ورقة ١١٢٩ هـ)، لاله إسماعيل ٦٨٩/ ٧ (الأوراق ٢٣٨ - ٢٦٠، فى القرن الحادى عشر الهجرى)، كوبريلى ٣/ ٢٤٧/ ٢ (الأوراق ٣٧ - ٥٣، ١١٢٩ هـ)، اسعد أفندى ١١٩٧/ ٢، ١٢١٠/ ٢ (من ٣٤ أ- ٤٩ ب، ١٠٦٧ هـ)، ١٤١٤/ ٢ (١١٣٤ هـ)، ٣٦٤٧/ ٣، نور عثمانية ٢١٥٥/ ٤، القاهرة ثان، ١/ ١٧١، كلام ١٦ م، ٢٨٨، ٦٦٦، ٦٨٦، ٨٧٧، الأزهر ٣/ ١٣٦، كلام ١٦٣٠ (٣٩ ورقة ١٢٤٤ هـ). وطبع بحيدرآباد ١٣٢١ هـ.
٣ - «ظهور العطية» للإمام الحصونى (كتب حوالى سنة ١٠٥٦ هـ/ ١٦٤٦ م) (٦٣)، جوتا ٦٥٨ (٣٩ ورقة)، بايزيد ٢٩٣٦/ ١ (من ١ - ١٠٢ ب، ١٠٦٧ هـ)، منجانا ١٢٣١ (٨١ ورقة، ١٠٦٨ هـ، انظر:
الفهرس ١٨٩).
٤ - لإبراهيم (بن حسن) نور الدين أفندى الإشكدراوى (المتوفى ١٢٦٠ هـ/ ١٨٤٤ م) (٦٤) رشيد أفندى ٩٩٠/ ٤ (من ٦٢ ب- ٧٩ أ، ١٢٦٠ هـ) وطبع باستنبول.
خامسا: (وصية إلى ابنه حمّاد) برلين ٣٩٦٦ (الأوراق ١ ب- ٢ أ، فى القرن الحادى عشر الهجرى)، ميونيخ ٦٤٨/ ٦ (الأوراق ٧٤ - ٧٥)، (بخارى المتحف الآسيوى ببطرسبورج ١١٣٧، بلدية الإسكندرية، فنون ١٥٦/ ٧) (٦٥)، بايزيد ٣٠٨٠/ ٤ (من ٧ ب- ١٠ ب، ١١٧٨ هـ)، مكتبة جامعة استنبول ١٥١٦/ ٢ (من ٩ ب ١٠ ب فى القرن الثانى عشر الهجرى)، ٦٤٦١/ ٢ (من ٦ ب- ٩ ب، فى القرن الثانى عشر الهجرى)، فاتح ٥٣٩٢/ ٧ من ١٢٥ ب- ١٣٦ ب، ١١٤٢ هـ)، الأزهر ٣/ ٧٥٧، آداب، فضائل ١١٤٠، (الأوراق ٧ - ٩٤،
_________
(٦١) انظر: معجم المؤلفين لكحالة ٣/ ٣١٤.
(٦٢) تحذف المخطوطات التى ذكرها بروكلمان فى: باريس ٧٦٢، وميونيخ ٦٤٨.
(٦٣) انظر هدية العارفين ٢/ ٩٠.
