(٦٥) تحذف المخطوطات التى ذكرها بروكلمان فى الإسكوريال ١٥٦٣.','3','47',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (804,'١٢٨٩ هـ)، ولهذه الوصية شرح ألّفه عثمان بن مصطفى (سنة ١٠٥٩ هـ/ ١٦٤٩ م) بعنوان: «زبدة النصائح»، المتحف البريطانى، (الملحق ٢٥٢، مخطوطات شرقية ٢٨٩٠ (الأوراق ١ - ١٧، ١١٥٧ هـ)، مكتبة جامعة إستنبول ٥٨٠٨/ ١ (من ١ - ١٥ ب، ١١٢٤ هـ)، أسعد أفندى ٤٣٧/ ٣ (من ٧ ب- ٨ أ، ١٢٠٩ هـ)، الأزهر ٣/ ٧٠٥ مجموعة ٦٤١ (الأوراق ١ - ٧، ١٠٦١ هـ).
سادسا: «وصية» إلى تلميذه يوسف بن خالد السّمتى البصرى (٦٦)
برلين ٣٩٦٨ (من ٢٦ ب- ٢٨ أ، ١١٤٢ هـ)، الجزائر ١٣٨٤ (الأوراق ١ - ٦٤، ٩٩٦ هـ) (٦٧)، فاتح ٥٣٩٢/ ٤ (من ١٠٢ ب- ١٠٨ أ، ١١٤٢ هـ)، مكتبة جامعة إستنبول ٦٤٦١/ ١ (من ١ - ٦ أفى القرن الثانى عشر الهجرى)، على أميرى ٤٣٠٥/ ٣ (من ١٧ ب- ١٩ أ)، أسعد أفندى ٢٨٣/ ٢ (من ٣٨ ب- ٤١ أ، فى القرن الثالث عشر الهجرى)، ٤٣٧/ ٤ (من ٨ أ- ٨ ب، ١٢٠٩ هـ)، الظاهرية، مجموع ١٣٠ (من ٣٥ أ- ٣٧ ب فى القرن الحادى عشر الهجرى)، ميونيخ ٦٤٨ (الأوراق ٧٥ - ٧٦)، وشرح هذه الوصية أحمد بن محمد بكيازارى، برلين ٣٩٦٩ (الأوراق ١ - ٥٠، حوالى ١٢٠٠ هـ).
سابعا: «وصية إلى تلميذه القاضى أبى يوسف بن إبراهيم (المتوفى ١٨٢ هـ/ ٧٩٨ م) (بلدية الإسكندرية، فنون ١٥٦، ٧)، فاتح ٥٣٩٢/ ٧ (من ١٢٥ ب- ١٣٦ ب، ١١٤٢ هـ) /، مكتبة جامعة إستنبول ١٥٥٣/ ١ (من ١ - ٥ ب، فى القرن الثانى عشر الهجرى)، بايزيد ٣٠٨٠/ ٤ (من ٧ ب- ١٠ ب، ١١٧٨ هـ)، برلين ٣٩٦٧ (الأوراق ١٠٤ - ١١٠)، ليدن ١٩٢٨ (الأوراق ٦٥ - ٧٥)، باريس ٢٣١٦ (الأوراق ٣٠ - ٣٧، فى القرن الحادى عشر الهجرى)، ٦٣٧٨ (الأوراق ٤١ - ٤٩، فى القرن الثانى عشر الهجرى، انظر فايدا ٧٢٥)، القاهرة ثان ١/ ٣٧٥، مجموعة ٥، ٢٥١، ٣٦٤، ٣٩٣، ٤٢١.
ثامنا: «رسالة» الى عثمان البتّى (بسبب اتهامه بأنه من المرجئة) هو عثمان بن سليمان بن جرموز (٦٨) المتوفى سنة ١٤٣ هـ/ ٧٦٠ م، القاهرة أول ٧/ ٥٥٣، مجموع ٦٤ (نسخة مصورة منه فى ليدن، انظر: فورهوف ٣٠٨)، فاتح ٥٣٩٢/ ٦ (من ١١٦ ب- ١٢٤ أ، ١١٤٢ هـ)،
_________
(٦٦) انظر: طبقات الفقهاء للشيرازى: ١١٥، الجواهر للقرشى ٢/ ٢٢٧.
(٦٧) تحذف المخطوطة التى ذكرها بروكلمان بليدن ١٩٢٨.
(٦٨) انظر: المعارف لابن قتيبة ٧٦، ١٩١، أخبار القضاة لوكيع ١/ ٣٢٣، ميزان الاعتدال للذهبى ٢/ ١٨٩، المشتبه للذهبى ٤٥، لسان الميزان لابن حجر ٦/ ٦٣٤.','3','48',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (805,'سليم أغا ٥٨٧/ ١٠ (من ١٧٠ ب- ١٧٤ أ، ٦٧٤ هـ)، طلعت، مجموع ٦٩٥ (٦٦٤ هـ)، الأزهر ٣/ ٣٣٩، كلام ٣٩٢٢ (الأوراق ٣٤ - ٤٣)، ونشره محمد زاهد الكوثرى بالقاهرة ١٣٦٨ هـ.
تاسعا: «رسالة أخرى إلى عثمان البتّى» سليم أغا ٥٨٧/ ١١ (من ١٧٤ أ- ١٧٦ أ، ٧٦٤ هـ).
عاشرا: «كتاب العالم والمتعلّم».
رواه تلميذه أبو مقاتل حفص بن سلم السّمرقندى (المتوفى ٢٠٨ هـ/ ٨٢٣ م)، القاهرة ثان ١/ ٣٤٥، مجموع ٦٤ م، جاريت ٢٠٢١/ ١ (١١٠٨ هـ)، ٢١٢٢/ ١ (١١٣٩ هـ)، رامبور ١/ ٣١٨، كلام ٢٧٠ (١٦ ورقة، ٨٦٣ هـ)، فاتح ٥٣٩٢/ ١، (من ١ - ٥٥ ب، ١١٤٢ هـ)، أسعد أفندى ٣٥٢٢/ ٢ (من ٩ ب- ١٨ أ، فى القرن الثانى عشر الهجرى) داماد إبراهيم ٢٩٧/ ٤١ (من ٣٨٥ ب- ٤٠١ أ، فى القرن الثالث عشر الهجرى)، رشيد أفندى ١٠١٦/ ١ (من ٢ ب- ٢٢ ب، ١١٨٧ هـ)، ينى ١١٩٠/ ٨ (من ٣٠٥ ب ٣١٧ أ، فى القرن الحادى عشر الهجرى)، سليم أغا ٥٨٧/ ١٣ (من ١٧٨ أ- ١٩٢ أ، فى القرن التاسع الهجرى)، عاطف ٢٨٢٩/ ٥ (من ١٩ ب- ٤٠ ب، فى القرن الحادى عشر الهجرى)، مكتبة جامعة إستنبول ٢٣٢٤ (١٣ ورقة، ١٢٩٢ هـ)، ٥٨٥٣ (١٠١٣ هـ)، تشستربيتى ٤٢١٦ (الأوراق ١ - ٢٧، فى القرن السابع الهجرى)، ليدن مخطوطات شرقية ٦٣٧٢/ ٥ (الأوراق ١٦٩ - ١٨٦، انظر: فورهوف ١٢)، الأزهر ٣/ ٩٣، كلام ٣٩٢٢ (الأوراق ١٣ - ٣٤).
وطبع بحيدرآباد ١٣٤٩ هـ، ونشره محمد زاهد الكوثرى بالقاهرة ١٣٦٨ هـ.
وشرح هذا الكتاب أبو بكر محمد بن الحسن بن فورك (المتوفى ٤٠٦ هـ/ ١٠١٥ م انظر: رقم ١٩ فى العقائد): مراد ملّا ١٨٢٧ (من ١٥٩ ب- ٢٢٥ ب، ٧٩٧ هـ).
حادى عشر: «القصيدة الكافية (النعمانية) فى مدح النبى صلّى الله عليه وسلم» القاهرة ثان ٣/ ٢٩٠، مجموع ٢٩٩، مجموع ٤٣ م (١١٩١ هـ)، القاهرة، ملحق ٢/ ٢١٣ رقم ١٦٠٠٨ ز (ست ورقات، ١٢٠٦ هـ)، هندلبرج ٣٢٧ (انظر berenbach ,ZDMG ٩١ /١٩٣٧ /٣٨٦: وطبعت باستنبول ١٢٦٨ هـ، مع ترجمة تركية مكتوبة بين أبيات القصيدة لإبراهيم ابن محمد يالوجى فى «المجموعة الكبرى»، إستنبول ١٢٧٦ هـ، ومع شرح بالنثر والشعر، ومع شرح بالهندوستانى بعنوان:
«رحمة الرحمن»، لمحمد أعظم بن محمديار. دلهى ١٨٩٧ م.','3','49',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (806,'ثانى عشر: مجادلة لأحد الدهريين» القاهرة ثان ١/ ٢٠٥ مجموعة ١٠٥ م.
ثالث عشر: «معرفة المذاهب» (؟ ) رامبور ١/ ٣٢٢، كلام ٣٠٥/ ٢، (البنغال ١٧٢٦، بخارى، المتحف الأسيوى ببطرسبورج ٩٥٧).
رابع عشر: «الضوابط الثلاثة».
وهو كتاب فى الفقه. وله شرح لا نعرف ما اسم مؤلفه إلا أنه حسن، وهذا الشرح بعنوان «الوصول إلى الكنز الأكبر وإلى ما هو الأنفع من الكبريت الأحمر»، جاريت ٢١٢٠/ ٣ (٩ ورقات، فى القرن الثالث عشر الهجرى).
خامس عشر: «رسالة فى الفرائض» (باتنة ٢/ ٣٦٢ رقم ٢٥٤٥، ٣) سادس عشر: «دعاء أبى حنيفة» بنكيبور ٢٦/ ٣١ رقم ٢٧٩١/ ٥ (الورقتان ٨ - ٩، فى القرن الثانى عشر الهجرى).
سابع عشر: «مخاطبة أبى حنيفة مع جعفر بن محمد بن أحمد الرضا» (المتوفى ١٤٨ هـ/ ٧٦٥ م انظر بروكلمان ملحق ١/ ١٠٤).
بلدية الإسكندرية ١٤١٦ ب/ ١.
ثامن عشر: «فتاوى أبى حنيفة ومحمد بن الحسن الشيبانى انظر: (الشيبانى) تاسع عشر: «كتاب المقصود فى الصرف» / وهذا الكتاب نسب إلى أبى حنيفة فى زمن متأخر، وتوجد منه مخطوطات كثيرة فى مكتبات إستنبول (٦٩).
_________
(٦٩) يحذف كتاب «نصائح» الذى ذكره بروكلمان رقم ١٦.','3','50',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (807,'<span data-type="title" id=toc-1970>٢ - زفر</span>
هو أبو الهذيل، زفر بن الهذيل بن قيس، العنبرى، أحد تلاميذ أبى حنيفة المرموقين، ولد سنة ١١٠ هـ/ ٧٢٨ م. وأصل والده من أصفهان، ويقال إن زفر هو الذى أدخل مذهب أبى حنيفة فى الرأى إلى البصرة، وكان قاضيا بها. وتوفى بالبصرة سنة ١٥٨ هـ/ ٧٧٥ م.

<span data-type="title" id=toc-1971>أ- مصادر ترجمته:</span>
المعارف لابن قتيبة ٢٤٩، أخبار القضاة لوكيع ١/ ٣٢٣، ٢/ ٨٢، الجرح والتعديل لابن أبى حاتم ١/ ٢/ ٦٠٨ - ٦٠٩، الفهرست لابن النديم ٢٠٤، أخبار أصفهان لأبى نعيم ١/ ٣١٧ - ٣١٨ الانتقاء لابن عبد البر ١٧٣، الوفيات لابن خلكان (بولاق) ١/ ٢٣٧، ميزان الاعتدال للذهبى ١/ ٣٤٨، الجواهر للقرشى ١/ ٢٤٣ - ٢٤٤، ٢/ ٥٣٤، لسان الميزان لابن حجر ٢/ ٤٧٦ - ٤٧٨، شذرات الذهب لابن العماد ١/ ٢٤٣، الأعلام للزركلى ٣/ ٧٨، معجم المؤلفين لكحالة ٤/ ١٨١.

<span data-type="title" id=toc-1972>ب- آثاره:</span>
ذكر ابن النديم فى كتابه الفهرست أنه ألف كتبا، وذكر له حاجى خليفة فى كتابه كشف الظنون (ص ١٧٨٢) كتابا بعنوان «المقالات». وهناك عدد لا يحصى من الاقتباسات من كتبه لا تزال موجودة فى كتب فقه الحنفية، وعلى الأخص فى كتب السرخسى. وعنه وعن آرائه انظر كتاب: «لمحات النظر فى سيرة الإمام زفر» لمحمد زاهد الكوثرى، القاهرة ١٣٦٨ هـ.

<span data-type="title" id=toc-1973>٣ - أبو يوسف</span>
هو أبو يوسف الكوفى، يعقوب بن إبراهيم بن حبيب بن سعد بن حبتة، وكان سعد بن حبته صحابيا. ولد سنة ١١٣ هـ/ ٧٣١ م بالكوفة. قرأ على هشام بن عروة، وأبى إسحاق الشيبانى، وسليمان التميمى، ومحمد بن إسحاق. وكان شيوخه فى الفقه محمد بن عبد الرحمن بن أبى ليلى، وأبا حنيفة. واقتصر بعد ذلك على دروس أبى','3','51',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (808,'حنيفة فقط، فاستغرقت كل جهده حتى إن أسرته الفقيرة شكته إلى أبى حنيفة لسوء التغذية، وتقديرا لموهبة تلميذه فرض أبو حنيفة لهذه الأسرةراتبا تعيش منه، وتولى أبو يوسف القضاء ببغداد فى خلافة الهادى (حكم من ١٦٩ هـ/ ٧٨٥ م- ١٧٠/ ٧٨٦ م)، وظل بها يقضى بين الناس بعدل ومهارة حتى وفاته. ويقال إنه كان أول من خوطب «بقاضى القضاة». وكان فى مذهبه الفقهى يعترف بمبدأ الرأى، ومع ذلك فقد كان هناك فرق بينه وبين شيخه أبى حنيفة فى أنه كان يعتمد على الحديث أكثر من شيخه (٧٠). وفى كتاب «الخراج» لأبى يوسف يتضح إقراره بالحق الدينى فى حكم الدولة. وتوفى ببغداد سنة ١٨٢ هـ/ ٧٩٨ م.

<span data-type="title" id=toc-1974>أ- مصادر ترجمته:</span>
أخبار القضاة لوكيع ٣/ ٢٥٤ - ٢٦٤، الفهرست لابن النديم ٢٠٣، تاريخ بغداد للخطيب ١٤/ ٢٤٢ ٢٦٢، مناقب الإمام الأعظم/ للموفق المكى ٢/ ٢٠٨ - ٢٤٦، تاريخ جرجان للسهمى ٤٤٤ - ٤٤٥، الوفيات لابن خلكان (بولاق) ٢/ ٤٠٠ - ٤٠٦، ميزان الاعتدال للذهبى ٣/ ٣٢١، تذكرة الحفاظ للذهبى ٢٩٢ - ٢٩٤، الجواهر للقرشى ٢/ ٢٢٠ - ٢٢٢، مناقب الإمام الأعظم للكردرى ٢/ ١١٧ - ١٤٥، البداية والنهاية لابن كثير ١٠/ ١٨٠ - ١٨٢، تاج التراجم لابن قطلوبغا ٨١، النجوم الزاهرة لابن تغرى بردى ٢/ ١٠٧ - ١٠٩، مرآة الجنان لليافعى K.Kufrali ,IAIV ,٥٩ - ٦٠ .. ٣٨٨ - ٣٨٢ /١ شاخت فى دائرة المعارف الإسلامية (الإنجليزية J.Schact ,Origins ٣٠١ - ٣٠٦.، ١٦٥ - ١٦٤ /١ (الأعلام للزركلى ٩/ ٢٥٢، معجم المؤلفين لكحالة ١٣/ ٢٤٠ - ٢٤١، بروكلمان ١/ ١٧١.
كتب عن سيرة أبى يوسف: ١ - «مناقب أبى يوسف» للذهبى، بتحقيق محمد زاهد الكوثرى وأبى الوفاء الأفغانى، القاهرة ١٣٦٦ هـ.
٢ - «مناقب أبى حنيفة وصاحبيه أبى يوسف ومحمد بن الحسن الشيبانى» للزيلعى محرم بن محمد أبى البركات (المتوفى ١٠٠٧ هـ/ ١٥٩٩ م).
٣ - «حسن التقاضى فى سيرة الإمام أبى يوسف القاضى» لمحمد زاهد الكوثرى، القاهرة ١٩٤٨ م.
_________
(٧٠) حيث كانت شروط أبى حنيفة لقبول الحديث أكثر.','3','52',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (809,'٤ - قاضى قضاة بغداد وأثره فى الفقه الإسلامى، لصبحى المحمصانى، بمجلة المجمع العلمى بدمشق ٤٠/ ١٩٦٥/ ١١٧ - ١٣٦.

<span data-type="title" id=toc-1975>ب- آثاره:</span>
١ - كتاب الخراج:
هذا الكتاب فى ضريبة الأراضى وبأوله مقدمة ينبه فيها الخليفة هارون الرشيد إلى ذلك وتوجد مخطوطاته فى: برلين ٥٦٠٥ (١٤٤ ورقة، ١١٣٦ هـ)، ٥٦٠٦ (١١٧ ورقة، فى القرن الثالث عشر الهجرى)، باريس ٢٤٥٢ (١٠١ ورقة، ١١٩٥ هـ)، ٢٤٥٣ (١٤٠ ورقة، فى القرن الثالث عشر الهجرى)، ٥٨٧٦ (١٠٨ ورقة، ١٠٠٢ هـ، انظر: فايدا ٣٨٧)، المكتب الهندى دلهى ٦٨٠ (١٠٩ ورقة، فهرس ٢/ ١٥١١)، المتحف البريطانى، الملحق ٢٧١ مخطوطات شرقية ٣٦١٧ (١٢٥ ورقة، ١٠٧٦ هـ)، أيا صوفية ١١٤٥ (١٥٩ ورقة، فى القرن الثامن الهجرى)، نور عثمانية ١٥١٦ (١٨٧ ورقة، ١٠٩٩ هـ)، ١٥١٧ (٧٩ ورقة، ١١٣١ هـ) كوبريلى ٥٥٩ (الأوراق ٤٤ - ٨٩، ١٠٢٢ هـ)، ٥٦٠ (٨٤ ورقة، ٨٤٤ هـ)، مراد ملّا ٨٢٥ (من ١ - ١٢١ ب، ١٠٤٨ هـ)، ٨٢٦ (من ١ - ١٣٦ أ، ١٠٣٤ هـ)، السليمانية ٤٤٣ (١٠١ ورقة، ٩٦٨ هـ)، الزيتونة بتونس ٤/ ٤٣٢ رقم ٢٩٠٤ (١٠٩ ورقة)، رامبور ١/ ٢٣٩، فقه ٤٦٥ - ٤٦٦، (بشاور ٥٦٥)، شهيد على ٧٦٨ (١٢٩ ورقة، فى القرن الحادى عشر الهجرى)، ٩٤٢/ ١ (من ١ - ٤٨ أ، ١٠٤٦ هـ)، لاله لي ١٦٠٥ (٦٥ ورقة، ١١٣١ هـ)، رئيس الكتاب ٣٢٣ (١١٦ ورقة، ١١١٣ هـ)، لاله إسماعيل ٨٤ (١٠٢ ورقة، ١٠٣٢ هـ)، داماد إبراهيم ٥٢٣ (١٥٨ ورقة، ١١١٠ هـ)، ٥٢٤ (١٧٩ ورقة، ٩٠٣ هـ)، وهبى ١٠٤٩/ ١ (من ١ - ٨٧ ب، فى القرن الثانى عشر الهجرى)، فاتح ٣٤٨٧ (١٠٠ ورقة، فى القرن العاشر الهجرى) ٣٤٨٨/ ١ (من ١ - ٥٦ ب، فى القرن التاسع الهجرى)، ٣٤٨٩ (الأوراق ١ - ١٨٠، ٩٦٥ هـ)، نور عثمانية ١٨٠٤/ ١ (من ١ - ٧٨ أ، ١١٣١ هـ)، برتو باشا ١٨١ (١٠٣ ورقة)، سراى، أحمد الثالث ١٠٥٤ (٢٦٤ ورقة فى القرن التاسع الهجرى) ١٠٥٥ (٢١٩ ورقة)، ١٠٥٦ (١٤٥ ورقة، ٩١٥ هـ)، سراى مدينة ٣٥٢ (١٧٨ ورقة، ١١٤٢ هـ، انظر فهرس ٢/ ٣٧٧)، ولى الدين ٥٦٣ (٩٧ ورقة، فى القرن الثانى عشر الهجرى)، ٥٦٤ (٩٩ ورقة، فى القرن الحادى عشر الهجرى)، ٥٦٥ (١٠٢ ورقة، فى القرن الحادى عشر الهجرى)، راغب ٩٦٨ (١٣٨ ورقة، ١٠٩٧ هـ)، عاطف ٨٢٢ (١٣٩ ورقة، فى القرن التاسع الهجرى)، مكتبة جامعة إستنبول ٢٢٠٧ (١٠٠ ورقة، فى القرن الحادى عشر الهجرى)، ٣٤٩٧ (١١٥ ورقة، فى القرن الثانى عشر الهجرى)، قونية يوسف أغا ٥٠٨٤ (١٣٢ ورقة، ٨٨٢ هـ)، جوروم ١٥٣٨ (٢١٧ ورقة، ٩٧٥ هـ)، تشستربيتى ٣٠٨٣ (الأوراق ١ - ٦٤، فى القرن التاسع الهجرى)، الأوقاف ببغداد ٦٣٤ (فى القرن الثامن الهجرى، انظر: كوركيس عواد فى مجلة سومر (Hoenerbach ,Oriens ٨ /١٩٥٩ /٩٧، ١٣٤ /٤ وطبع ببولاق ١٣٠٢ هـ، وبالقاهرة ١٣٥٢ هـ.','3','53',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (810,'وترجمه إلى التركية محمد معيد زاده بعنوان: «وضع الآصار عن علماء الأمصار» شهيد على ٦١٧ (٩٨٢ هـ)، وكذلك ترجمه أيضا إلى التركية رود سلوزاده محمد (١١١٣ هـ/ ١٧٠١ م، انظر ٥١٣، (Osm.Muell.I: مراد ملّا ١٠٩٣، (١١٢ ورقة، ١١٢٨ هـ)، عاشر أفندى ٥٧١، ٥٧٢، أيا صوفية ١١٤٤/ ١، ١١٤٥ عاطف ٤٠٢ (١٤٣ ورقة ١٠٩٥ هـ)، سراى، ريفان ٦١٥، (شهيد على ٧١٧ - ٧١٨، ١٠٩٤ هـ). وترجمه تريبودو P.Tripodo إلى الإيطالية، روما ١٩٠٦، وفانيان E.Fagnan إلى اللغة الفرنسية، باريس ١٩٢١.
الشروح: ١ - «فقه الملوك ومفتاح الرّتاج المرصّد على خزانة كتاب الخراج» لعبد العزيز بن محمد الرّحبى (ألّفه سنة ١١٦٩ هـ/ ١٧٥٦ م)، لاله لي ١٦٠٩ (١١٦٩ هـ، بخط المؤلف، نسخة مصورة بالقاهرة، ثان ١/ ٥٦)، محمودية بالمدينة (انظر: شبيس، (Spies ,ZDMG ٩٠ /١١٥: وهبى ١٠٤٤ (٤٣٧ ورقة، ١١٨٤ هـ)، أنقرة ١٥٣٠، الظاهرية ٥٩٠٠، عابدين بدمشق، انظر: مجلة معهد المخطوطات العربية ٥/ ٢١٣/.
٢ - «كتاب المخارج فى الحيل»، ولقد بقى معظمه إن لم يكن كله، بتهذيب تلميذه أبى الحسن الشيبانى، انظر: ما كتبه شاخت» -٣ Schacht ,in: Islam ١٥ /١٩٢٦ /٢١٧.: كتاب الآثار» دار الكتب بالقاهرة، بتحقيق أبى الوفاء الأفغانى، القاهرة ١٣٥٥ هـ.
٤ - «كتاب الرد على سير الأوزاعى»، ولقد بقى فى كتاب الأم «للشافعى ٧/ ٣٠٣ - ٣٣٦، وحققه أبو الوفاء الأفغانى، القاهرة ١٣٥٧ هـ.
٦ - «أدب القاضى»، المكتبة الأهلية بتونس ٥٠٦.
٧ - «كتاب اللطائف»، استخدمه ابن حجر فى الإصابة ١/ ٨٦٢.
٨ - «الأمالى»، ذكره السّرخسى فى كتابه الأصول ١/ ٣٣٣.

<span data-type="title" id=toc-1976>٤ - الشّيبانى</span>
هو أبو عبد الله، محمد بن الحسن بن فرقد الشّيبانى، ولد بواسط سنة ١٣٢ هـ/ ٧٤٩ م. نشأ بالكوفة وجالس، وهو ابن أربعة عشر عاما، أبا حنيفة وأخذ','3','54',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (811,'عنه وتأثر بمنهجه فى الرأى. ومن شيوخه المشهورين سفيان الثورى، والأوزاعى، ومالك بن أنس، وأبو يوسف وكان له دور كبير فى إعداده الفقهى. استطاع الشيبانى وهو فى العشرين من عمره أن يحقق بدروسه مجدا ومكانة حتى إنه كاد يزاحم شيخه أبا يوسف فى المكانة. ولاه الخليفة هارون الرشيد عام ١٨٠ هـ/ ٧٩٦ م القضاء بالرّقة وسرعان ما عزل. ولما خرج الرشيد سنة ١٨٩ هـ/ ٨٠٥ م إلى خراسان صحبه الشيبانى وهناك ولّاه القضاء بها مرة ثانية، ولكنه توفى فى نفس السنة برنبويه من أعمال الرى. وكان الشيبانى متبحرا فى علوم اللغة، وكان فى قضايا العقيدة يقول برأى المرجئة، ولا شك فهو أحد مؤسسى المدرسة الحنفية. وكان فى أحكامه الفقهية لا يرفض الرأى ولكنه كان يقدم الحديث على الرأى.

<span data-type="title" id=toc-1977>أ- مصادر ترجمته:</span>
الطبقات لابن سعد ٧/ ٢/ ٧٨، المعارف لابن قتيبة ٢٥١، تاريخ الطبرى ٣/ ٢٥٢١، الفهرست لابن النديم ٢٠٣ - ٢٠٤، طبقات الفقهاء للشيرازى ١١٤، تاريخ بغداد للخطيب ٢/ ١٧٢ - ١٨٢، الانتقاء لابن عبد البر ١٧٤، الأنساب للسمعانى ٣٤٢ أ، الوفيات لابن خلكان (بولاق) ١/ ٤٥٣، الجواهر للقرشى ٢/ ٤٢ - ٤٤، الوافى بالوفيات للصفدى ٢/ ٣٣٢ - ٣٣٤، مناقب الإمام الأعظم للكردرى ٢/ ١٤٦ - ١٦٧، لسان الميزان لابن حجر ٥/ ١٢١ - ١٢٢، تاج التراجم لابن قطلوبغا ٤٠، البداية والنهاية لابن كثير ١٠، ٢٠٢ - ٢٠٣، النجوم الزاهرة لابن تغرى بردى ٢/ ١٣٠ - ١٣١، شذرات الذهب لابن العماد ١/ ٣٢١، مرآة الجنان لليافعى ١/ ٤٢٢ - ٤٢٤، الأعلام للزركلى ٦/ ٣٠٩، معجم المؤلفين لكحالة ٩/ ٢٠٧. هفننج فى دائرة المعارف الإسلامية ٤/ ٢٩١، بروكلمان ١/ ١٧١.
Flugel, Classenderhanafi tischen Rechtsg. ٢٨٣. B. de Meynard, Ja ٤, Serie XX, ١٨٩٢, ٤٠٦ - ٤١٩. l. Dimitroff. as- S. undseincorpusjur isal- Gami '' as- Sagir, in: MSOS/ As II/ ١٩٠٨/ ٦٠ - ٢٠٦/ ٧٥ - ٩٨. H. Kruse, Die Begrundungderisl amischen Volkerrechtslehr e, M. as- S., Hugo Grotiusder Moslimen, Saeculum V, ١٩٥٤, ٢٢١ - ٢٤١. H. Kruse, Al Shaibanionlntern ationalinstrumen tsin: Pakistan Hist. Soc. Jour, I, ٢, ١٩٥٣.
٩٠ - ١٠٠. O. Spies, Klassischesislam isches Recht, ٢٣٩ - ٢٤١.','3','55',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (812,'O. Spies, Ungrand Juristemusulman M. b. al- H. as- S. in: Rapports Gen. au Congr. intern. dedroit compare, Bruxelles ١٩٦٠, ١٢٥ - ١٢٩. G. Wiedensohler, Die Lehrevonden Mangelnbeim Kaufnach Islamischem Recht, Diss. Bonn, ١٩٦٠.
كتب عن الشيبانى: ١ - «كتاب الرد على محمد بن الحسن» فى «كتاب الأم» لمحمد بن إدريس الشافعى، القاهرة ١٣٢٥، ٧/ ٢٧٧ - ٣٠٣.
٢ - «مناقب محمد بن الحسن الشيبانى» للذهبى (المتوفى ٧٤٨ هـ/ ١٣٤٨ م انظر بروكلمان ٢/ ٤٦) بتحقيق محمد زاهد الكوثرى وأبى الوفاء الأفغانى القاهرة ١٣٦٦ هـ.
٣ - «مناقب أبى حنيفة وصاحبيه أبى يوسف ومحمد بن الحسن الشيبانى» (انظر: أبا حنيفة) للزيلعى محمد بن أبى البركات (المتوفى ١٠٠٧ هـ/ ١٥٩٩ م).
٤ - «بلوغ الأمانى فى سيرة الإمام الشيبانى» لمحمد زاهد الكوثرى. القاهرة.

<span data-type="title" id=toc-1978>ب- آثاره:</span>
أولا: «المبسوط» أو «كتاب الأصل فى الفروع»، ويوجد مخطوطا فى:
أيا صوفية ١٠٢٦ (٤١٠ ورقة، ١١٤٠ هـ)، نور عثمانية ١٣٧٧ (٤٣٤ ورقة، ١١٥٢ هـ)، السليمانية ٥٨٦ (٢٣٧ ورقة، ١٨٤ ورقة، ١١٥٢ هـ)، سليم أغا ٢٨٥ (١، ٢٧٨ ورقة، فى القرن الحادى عشر الهجرى)، ٢٨٦ (٢، ٢٧٥ ورقة، فى القرن الحادى عشر الهجرى)، مراد ملّا ١٠٣٨ - ١٠٤١ (أربعة أجزاء، ٦٣٩ هـ).
كوبريلى ٥٣٧ (١، ٢٢٥ ورقة، ٧٢٨ هـ)، ملا جلبى ٣٩ (٢٥٤ ورقة، ٩٥٩ هـ)، جار الله ٥٧٥ (١، ٢٠٥ ورقة)، ٥٧٦ (٢، ٩٥٠ هـ)، ٥٧٧ - ٥٧٩ (الأجزاء ٢ - ٤)، ٥٨٠ (٩٨ ورقة)، ٥٨١ (٢، ٢٢٨ ورقة)، عاطف ٧٤٢ (١، ٢٥٢ ورقة، فى القرن التاسع الهجرى)، ٧٤٣ (الأجزاء، ٢، ٣، ٤٣٦ ورقة، فى القرن التاسع الهجرى)، ٧٤٤ (الأجزاء ٤، ٥، ٤١٢ ورقة، فى القرن التاسع الهجرى)، ٧٤٥ (الجزء ٦، ٢٤١ ورقة فى القرن التاسع الهجرى)، ينى ٩٨ (٦٢٥ ورقة، ٩٧١ هـ)، بشير أغا ٢٠٦ (٣٧٩ ورقة، ١١١٨ هـ)، راغب ٤٥٠ (٣٠١ ورقة، ١١٣٨ هـ)، عاشر أفندى ٨٧ - ٩١ (الأجزاء ١ - ٥، ١٢٠٩ هـ)، سراى، أحمد الثالث ٦٩٧ (الأجزاء ١، ٢، ٣١١ ورقة، ٣٧٧ ورقة، فى القرن الثامن الهجرى)، ٦٩٨ (٢٧٨ ورقة، فى القرن الحادى عشر الهجرى)، ٦٩٩ (٦٤٥ ورقة، فى القرن الحادى عشر الهجرى، انظر:','3','56',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (813,'فهرس ٢/ ٣٧٥)، قرة مصطفى ٢٤٥ (٢٣٣ ورقة، فى القرن الثامن الهجرى)، ٢٥٢ - ٢٥٤ (الأجزاء ١ - ٣)، ٣١١ (٢٧٥ ورقة، فى القرن السابع الهجرى)، فيض الله ٦٦٤ (جزء ضخم جدا) ٦٦٥ - ٦٦٩ (خمسة أجزاء، ٧٥٣ هـ)، القاهرة ثان ١/ ٤٠٢، فقه حنفى ٣٣ (جزءان، ٢٥١ ورقة، فى القرن السابع الهجرى)، ٣٤ (١٣٤ ورقة ٦٦٦ هـ)، ١٤١ (١٣١ ورقة، فى القرن التاسع الهجرى)، ٣٨٢ (٢٣٩ ورقة، فى القرن الرابع الهجرى، انظر: فهرس معهد المخطوطات ١/ ٢٥٥)، ٤٩٦ (فى جزءين فى القرن الثامن الهجرى)، ٦٢٣ (جزءان، فى القرن الثامن الهجرى)، الأزهر ٢/ ١٠٢، فقه حنفى ٢٠٢ (٢١٣ ورقة)، ٢٠٦٦ (١٥ قسما)، انظر: شاخت رقم ٣/ ٣ ورقم ٣، (قوله ١/ ٣٠٦)، طرخان ٩٨ - ٩٩، كوبريلى ٧٢٨، حميدية ٥٤٦، (٦٨٦ ورقة)، قسطمونى ٢٨٣٢ (٣٥٠ ورقة، ١١١٩ هـ)، تشستربيتى ٥٣٠٦ (١٣٧ ورقة، فى القرن الثامن الهجرى)، ونشر شفيق شحاتة الجزء الأول بالقاهرة ١٩٥٤ م (انظر، (MIDEO ,II ,٢٧٨: ونشر أبو الوفاء الأفغانى الجزء الأول فى حيدرآباد.
واختصره عبد الرحيم بن أبى عصام البلخى (على المخطوطة: البهدى)، جار الله ٨٧٣ (١١٩ ورقة، فى القرن السادس الهجرى)، انظر شاخت ١/ ٣ ج (٧١).
انظر أيضا:
A. d''Emilia, IL- Hiyaras- Sartnel Asl. di Saibani, Scrittiinonoredi G. Furlan, RSO ٣٢/ ١٩٥٧/ ٦٣٣ - ٦٤٠.
ثانيا: «الزيادات»:
مخطوطاته: لاله لي ٩٤٦ (٥٩ ورقة، ٦٧٢ هـ)، جار الله ٦٧٩/ ١ (الأوراق ١ - ١١٦، ٦٩٥ هـ)، أيا صوفية ١٣٨٥/ ٥ (من ٣٣٧ أ- ٤٢٧ ب، ١١٢٩ هـ)، فاتح ١٥٥٥/ ٢ (من ١٤٢ ب- ٢٣٨ ا، ٦٨١ هـ)، القاهرة ثان ١/ ٤٣٦، فقه حنفى ١٢٤٢ (٧١ ورقة، مخطوطة حديثة)، انظر ساخت: ١ رقم ٨)، ينى ٣٩٥/ ١ (الأوراق ١ - ١٠١، فى القرن التاسع الهجرى)، عابدين بدمشق (٦٠٧ هـ، انظر: مجلة معهد المخطوطات ٥/ ٢١٢). ورتبها أبو المحامد محمود/ بن أحمد الحصيرى (المتوفى ٦٣٦ هـ/ ١٢٣٨ م انظر بروكلمان ١/ ٣٨٠)، ولى الدين ١٣٥٠/ ٣ (من ١٢٤ ب- ٣٠٨ ب، ٦١٦ هـ، وعليها ملاحظات بخط الحصيرى).
ورتبها صدر الدين سليمان بن وهب الحنفى (المتوفى ٦٧٧ هـ/ ١٢٧٨ م انظر بروكلمان ملحق ١/ ٦٥٦)، ترتيبا يعتمد إلى حدّ ما على شرح حسن بن منصور قاضي خان، جار الله ٦٧٩/ ١ (١١٦ ورقة،
_________
(٧١) ذكر بروكلمان فى الملحق شرحين لكتاب «الأصل» للشيبانى، والحقيقة أنهما لكتاب «الكافى» للمروزى (أتى تحت رقم ٢١). يبدو أن الشرح الموجود بالمكتب الهندى ١٤٢٢ ليس لكتاب «الأصل». كما يحذف أيضا المختصر الذى ذكر عند بروكلمان.','3','57',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (814,'٦٩٥ هـ)، فاتح ١٥٥٥/ ٣ (من ١٤٢ أ- ٢٣٨ أ، ٦٨١ هـ)، ١٥٥٦/ ٢ (من ١٠٥ أ- ١٦٦ أ، ٧٠٠ هـ)، ١٦٦٦ (١١٨ ورقة، ٦٩٥ هـ)، جورليلى ٢٣٨ (٧١٩ هـ)، بخارى، المتحف الأسيوى ببطرسبورج ٤٩٥)، وعلق عليه محمد بن محمود بن محمد السديدى الزوزنى (المتوفى سنة ٨٠١ هـ/ ١٣٩٨ م انظر بروكلمان ٢/ ١٩٨) بعنوان «ملاك الإفادات فى شرح الزيادات»، جور لولو ٢٣٨/ ٢ (٧١٩ هـ).
الشروح: ١ - لأحمد بن محمد بن عمر العتّابى (المتوفى سنة ٥٨٦ هـ/ ١١٩٠ م انظر بروكلمان ١/ ٣٧٥)، ملّا جلبى ٤٧ (٢٧٣ ورقة، ٦٥١ هـ)، ٤٨ (٢٦٤ ورقة، ٦٥١ هـ)، فاتح ١٧٠٩ (١٨٩ ورقة، ٦٧٢ هـ)، ١٧١٠ (٢٠٧ ورقة)، بايزيد ٢٤٣٦ (١٧٠ ورقة، ٧١١ هـ)، جور لولو ١٨٥/ ١ (الأوراق ١ - ١٦٦، ٧٦١ هـ)، قرة مصطفى ١٨٩/ ١ (٧٢) (الأوراق ١ - ١٦٦، فى القرن التاسع الهجرى)، ولى الدين ١٣٥٠/ ١ (من ١ - ١١٤ ب، ٦١٥ هـ)، شهيد على ٨٠٨ (٣١٣ ورقة، فى القرن التاسع الهجرى)، حراچى زاده فى بورسة، فقه ٢١٣ (٢٣٨ ورقة، فى القرن التاسع الهجرى)، القاهرة ثان ١/ ٤٤١، فقه حنفى ٢٤٧ (انظر: شاخت ١/ ٨ أ، ٢/ ٥ أ)، أسعد أفندى ١٠٦٦ (٢٧٨ ورقة)، فيض الله ٧٦٣ (١٢٦ ورقة)، جوروم ١٤٥١/ ٢ (١٤٨ ورقة، ٦٦٣ هـ)، تشستربيتى ٣٠١٨ (الأوراق ١ - ١٨٨، ٥٩٠ هـ)، جامع الشيخ بالإسكندرية ١١ (٢٦٦ ورقة، فى القرن السابع الهجرى، انظر: فهرس معهد المخطوطات ١/ ٢٦٣).
٢ - لفخر الدين الحسن بن منصور بن محمود الأزجندى قاضي خان (المتوفى سنة ٥٩٢ هـ ١١٩٦ م انظر بروكلمان ١/ ٣٧٦)، برلين الشرقية ١١٩١ (٢٣٩ ورقة ٧١٣ هـ)، فاتح ١٧٠٦ (٣٣١ ورقة، فى القرن السابع الهجرى)، ١٧٠٧ (٣١٠ ورقة، فى القرن الثامن الهجرى)، ١٧٠٨ (٤٥٧ ورقة، ٧٢٣ هـ)، قره مصطفى ١٨٨ (١٩٧ ورقة، ٧٦٥ هـ)، رئيس الكتاب ٣٨١ (٢٩٢ ورقة، ٩٥٦ هـ)، عاشر أفندى ١١٤ (٣٣٣ ورقة، ٨٣٩ هـ)، مكتبة جامعة إستنبول ٣٥٠ (٣١٤ ورقة، ١٠١٠ هـ)، ملّا چلبى ٥٢ (٢٦٥ ورقة، ٩٧٢ هـ)، عاطف ٨٦٥ (٢٩٦ ورقة، ٩٧٤ هـ)، راغب ٥١٨ (٢٤٦ ورقة، فى القرن العاشر الهجرى)، لاله لي ٩٧٤ (١، ٢٠٨ ورقة، فى القرن التاسع الهجرى)، انظر: شاخت ١/ ٨ ب، ٢/ ٥ ب، القاهرة ثان ١/ ٤٤١، فقه حنفى ٣٨٠ (منه نسخة مصورة بالقاهرة، ملحق ٢/ ٥٤)، بنگيبور ج ١٩/ ١/ ٩٥ رقم ١٥٩٤ (٢، ٢٢٣ ورقة، ٦٤٠ هـ)، بلدية الإسكندرية ١٢٣٠ ب (٦٥٤ هـ)، مراد ملّا ١٠٢٢ (٣٢١ ورقة، ٦٩٢ هـ)، تشستربيتى ٣١٦٦ (٢٣٢ ورقة، ٢١٢، ٦٦٧ هـ)، الأوقاف ببغداد ٤٠٥٠ (انظر: طلس ٦٧)، طلعت، مجموع ٤٢٢ (قطعة).
_________
(٧٢) يصحح الرقم عند بروكلمان.','3','58',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (815,'واختصر هذا الشرح صدر الدين سليمان، برلين ٤٤٤١ (قطعة، الأوراق ٥٧ - ٧٢، حوالى ٨٠٠ هـ).
٣ - لمؤلف مجهول، المكتب الهندى، دلهى، مخطوطات عربية ٥٥٧، ملّا جلبى ٤٩ (١، ٣٠٢ ورقة، فى القرن الثامن الهجرى)، مراد ملّا ١٠٢٢ (٣٢١ ورقة، ٦٩٢ هـ).
ثالثا: «زيادات الزبادات»: جورلولو ١٨٥/ ٢ (١٤ ورقة، ٧٦١ هـ)، قره مصطفى ١٨٩/ ٢، تشستربيتى ٣٠١٨/ ٢ (الأوراق ١٨٩ - ٢٠٨، ٥٩٠ هـ).
وعليها شرحان: ١ - «النّكت» لمحمد بن أحمد السرخسى (المتوفى ٤٨٣ هـ/ ١٠٩٠ م انظر بروكلمان ١/ ٣٧٣)، عاشر أفندى ١١٤/ ٢ (من ٣٣٤ أ- ٣٥٣ أ، ٨٣٩ هـ)، جار الله ٦٧٩/ ٢ (من ١١٧ أ- ١٣٧ ب، ٦٩٥ هـ)، أيا صوفية ١٣٨٥/ ٦ (من ٤٠٤ أ- ٤٢٧ ب، ١١٣٤ هـ)، ولى الدين ١٣٥٠/ ٢ (من ١١٣ أ- ١٢٤ ب، ٦١٤ هـ)، فاتح ١٥٥٥/ ٣ (من ٢٣٩ ب- ٢٥٩ أ، ٦٨١ هـ، انظر: شاخت ١/ ٩ أ (بخارى، المتحف الأسيوى ببطرسبورج ٤٦٣ مختصر)، ونشره أبو الوفاء الأفغانى بحيدرآباد ١٣٧٨ هـ، انظر:
Schacht, in: Oriens ١٣ - ١٤ (١٩٦١) ٤٨٦.
٢ - لأحمد بن محمد العتّابى (المتوفى ٥٨٦ هـ/ ١١٩٠ م انظر بروكلمان ١/ ٣٧٥)، قره مصطفى ١٨٩ ونشره أبو الوفاء الأفغانى، حيدرآباد ١٣٧٨ هـ، انظر: شاخت فى مجلة.٤٨٦/ ١٩٦١ /١٤، ١٣ Oriens
رابعا: «الجامع الكبير» هذا الكتاب الفقهى فى الفروع، ينى ٣٩٢ (٣٠٢ ورقة، فى القرن السادس الهجرى)، أياصوفية ١٣٨٥/ ٣ (من ١٥٤ أ- ٢٥٨ أ، ١١٢٧ هـ)، ولى الدين ١٠٧١ (١٧١ ورقة، ٩٥٨ هـ)، القاهرة ثان ١/ ٤١٤، فقه حنفى ٧٤٠ (٢٩٢ ورقة، فى القرن التاسع الهجرى)، انظر: / شاخت ١ رقم ٥ (٧٣)، الظاهرية، فقه حنفى ١١٢ (٢١٦ ورقة، ٧٦٦ هـ، انظر: مجلة المجمع العلمى العربى بدمشق ١٢/ ٤٤٣)، فاتح ١٥٤٣ (٢٦١ ورقة، ٧٢٧ هـ).
وطبع بالقاهرة ١٣٥٦ هـ، ونشره أبو الوفاء الأفغانى بحيدرآباد ١٣٥٦ هـ، ورتّبه أحمد بن محمد البخارى العتّابى (المتوفى ٥٨٦ هـ/ ١١٩٠ م)، فاتح ١٥٥١ (١١٣ ورقة، ٧١٧ هـ).
_________
(٧٣) المخطوطة التى ذكرها بروكلمان بمكتبة فاتح ١٥٥١ ليس هذا موضعها.','3','59',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (816,'«ترتيب الجامع الكبير .... ووجيزه» لصدر الدين سليمان بن وهب الحنفى (المتوفى ٦٧٧ هـ/ ١٢٧٨ م انظر بروكلمان ١/ ٣٨٢)، فاتح ١٥٥٦/ ١ (من ١ - ١٠٤ أ، فى القرن السابع الهجرى).
الشروح: ١ - لأحمد بن على الجصّاص الرّازى (المتوفى ٣٧٠ هـ/ ٩٨١ م، انظر: رقم ٢٣ الآتى)، القاهرة ثان ١/ ٤٣٩، فقه حنفى ٧٤٥ (ج ١، ٢، ٣٥٧ ورقة، ١٢١ ورقة، ٥٥٩ هـ)، ٧٤٦ (ج ٢، ٣٩١ ورقة، ٥٦٠ هـ، انظر: شاخت ٢ رقم ٤ أ، فهرس معهد المخطوطات ١/ ٢٦٣).
٢ - (يطابق رقم ٣ فى ملحق بروكلمان) لأحمد بن منصور الإسبيجابي (المتوفى ٤٨٠ هـ/ ١٠٨٧ م، انظر: معجم المؤلفين لكحالة ٢/ ١٨٣)، مراد ملّا ٨٥٨ (٣٠١ ورقة، فى القرن السابع الهجرى، ناقص من آخره، انظر: شاخت ١/ ٥ ب).
٣ - لأحمد بن محمد السّرخسى (المتوفى ٤٨٣ هـ/ ١٠٩٠ م)، القاهرة ثان ١/ ٤٤٠، فقه حنفى ١٠٧ (٧٣٦ هـ)، نقلا عن شاخت ٢/ ٣ ب توجد نسخ أخرى بدار الكتب بالقاهرة فقه حنفى ٤٩٢ (ج ٨، ٢٩٥ ورقة)، ٤٩٣ (ج ١٢، ٣٣٥ ورقة)، ٤٩٤ (٢١٨ ورقة)، ٧٨٨ (١٧١ ورقة)، ٧٨٩ (ج ٩)، حراچى زاده فى بورسه، فقه ٢٠٥ (ج ٣، ٧٧٤ هـ). (٧٤) ٤ - لعمر بن عبد العزيز بن مازه (المتوفى ٥٣٦ هـ/ ١١٤١ م انظر بروكلمان ١/ ٣٧٤)، ولى الدين ١١٥٧ (٢٩٧ ورقة، ٦٧٩ هـ) (٧٥)، فيض الله ٧٤٧ (١٦٤ ورقة، ٦٦٠ هـ).
٥ - لمحمد بن الحسين أبى بكر البخارى جواهر زاده (المتوفى ٤٨٣ هـ ١٠٩٠ م انظر: الجواهر للقرشى ١/ ٢٣٦، ٢/ ٤٩) فاتح ١٨٦٧ (ج ٢، ٣٦٠ ورقة، فى القرن الثامن الهجرى).
٦ - (يطابق رقم ٥ فى ملحق ١ عند بروكلمان) «نكت الجامع الكبير» أو «إشارات الجامع الكبير» لأبى الفضل عبد الرحمن بن محمد الكرمانى (المتوفى ٥٤٣ هـ/ ١١٤٩ م انظر بروكلمان ١/ ٣٧٤)، فاتح ١٥٥٤ (٣٣٤ ورقة، ٦٤٩ هـ، انظر: فهرس معهد المخطوطات ١/ ٢٦٣)، ملّا چلبى ٤١ (٢٧٩ ورقة، ٦١٣ هـ) حسين چلبى فى بورسه ٢٨٩/ ١ (١ أ- ٢٠٠ ب، ٥٣٦ هـ)، انظر: شاخت ١/ ٥ ج، الزيتونة بتونس ٤/ ٤٩ - ٥٠ رقم ١٨٥٢ (٢١٧ ورقة، ٦٢٥ هـ)، لاله لي ٨٠٤ (٢٣٧ ورقة، ٩١٠ هـ)، نجده ٤٧٢ (١٩٤ ورقة، ٦٧٧ هـ).
_________
(٧٤) تحذف المخطوطة التى ذكرها بروكلمان بالزيتونة بتونس ٤/ ١٤٦ رقم ٢١٢١
(٧٥) تحذف مخطوطة بلدية الإسكندرية، فقه حنفى ١٧ التى ذكرها بروكلمان.','3','60',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (817,'٧ - (رقم ٢ عند بروكلمان، الملحق ١/ ٢٨٩، حيث ذكر خطأ أن الشارح هو نصر بن محمد السمرقندى)، لعلاء الدين محمد بن عبد الحميد السمرقندى العالمى (المتوفى ٥٥٢ هـ/ ١١٥٧ م انظر بروكلمان ١/ ٦٤١)، مراد ملا ٨٥٤ (٤١٥ ورقة، ٧٦٣ هـ)، ولى الدين ١١٥٩ (٢٩٠ ورقة فى القرن السابع الهجرى)، إسمهان ١٣٩، فيض الله ٧٤٥ (ج ١، ٢٠٦ ورقة، ٦٥٥ هـ)، ٧٤٩ (٣١٤ ورقة) (٧٦)، السليمانية ٤٦٢ (١، ٢٥١ ورقة، فى القرن الثامن الهجرى)، انظر: شاخت ١/ ٥ أ، ٥ ى، ٣/ ٥ أ (حيث ذكر- بدون سبب- أن مؤلف هذا الشرح هو أبو الليث السمرقندى، ولقد ذكر حاجى خليفة فى كشف الظنون ٥٦٨، ٥٦٩ أن كلا السمرقنديين شارح لكتاب الجامع الكبير، ومع ذلك فإن ما ذكره من معلومات تثبت نسبة هذا الشرح إلى علاء الدين السمرقندى حيث ذكر ما ابتدأ به هذا الشرح، وهذه البداية تتفق مع بدايات المؤلفات التى وصلت إلينا، كما تتفق أيضا مع ما نسب إلى علاء الدين هذا. وعلى العكس من ذلك فإنه لا يمكن إثبات أن أبا الليث السمرقندى كان شارحا لكتاب الجامع الكبير).
٨ - (يطابق رقم ٦ فى ملحق بروكلمان) لمحمد بن أحمد بن مازه (المتوفى حوالى ٥٧٠ هـ/ ١١٧٤ م انظر بروكلمان ١/ ٣٧٥)، قره چلبى ١١٧ (١٧٠ ورقة، فى القرن السابع الهجرى) انظر: شاخت ١/ ٥ هـ.
٩ - (يطابق رقم ٧ فى ملحق بروكلمان) لأحمد بن محمد العتّابى (المتوفى ٥٨٦ هـ/ ١١٩٠ م انظر بروكلمان ملحق ١/ ٦٤٣) /، قره مصطفى ١٨٦ (٦٣٩ هـ)، داماد إبراهيم ٥٣٨ (٢٢٥ ورقة، فى القرن السابع الهجرى) انظر شاخت ١/ ٥.
١٠ - (يطابق رقم ٨ فى ملحق بروكلمان) لعبد المطلب بن الفضل بن عبد المطلب الحلبى (المتوفى ٦١٦ هـ/ ١٢١٩ م، انظر معجم المؤلفين لكحالة ٦/ ١٧٥)، المتحف البريطانى، الملحق ٢٧٢ مخطوطات شرقية ٣٠٩٥ (الأوراق ١ - ١٢٨، فى القرن الثامن الهجرى)، قره مصطفى ١٨٧، (ج ٢، ٢٦٤ ورقة، ٦١٢ هـ، عليه تعليقات بخط المؤلف)، جار الله ٦٧٣ (٣٧٢ ورقة، ٩٦٢ هـ)، ٦٧٤ (٣٠٥ ورقة، ٩٨٥ هـ)، القاهرة ثان ١/ ٤٣٩، فقه حنفى ١٠٢ (ج ٢، ٤٣٠ ورقة)، انظر: شاخت ٤/ ٢، j ٥ /١ / هـ.
١١ - «أصول الجامع الكبير» لعيسى بن أبى بكر بن أيوب الملك المعظم (المتوفى ٦٢٤ هـ/ ١٢٢٧ م انظر بروكلمان ١/ ٣٨٠)، بشير أغا ٢٠٨ (٢٠٥ ورقة، ٧٢٣ هـ).
١٢ - (يطابق رقم ٩ أفى ملحق بروكلمان) «التحرير» لمحمود بن أحمد الحصيرى (المتوفى ٦٣٦ هـ/ ١٢٣٨ م انظر بروكلمان ١/ ٣٨٠)، جاريت ١٦٨٥ (ج ٢، ٢٣٦ ورقة)، بلدية الإسكندرية ١٣٤٩ ب (فى القرن السابع الهجرى)، القاهرة ثان ١/ ٤٠٧، فقه حنفى ٩٧ (أجزاء ١، ٣، ٤، ٥، ٦،
_________
(٧٦) تصحح عند بروكلمان.','3','61',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (818,'٧١٩ هـ)، ٩٨ (أربعة أجزاء، حوالى ٦٢٣ هـ)، ٩٩ (الأجزاء ٢ - ٧، ٢٣١ ورقة، ٢٤٥ ورقة، ٢٣١ ورقة، ٢٦٠ ورقة، ٢٣٦ ورقة، ٢٣٦ ورقة بخط المؤلف، ٦١٦ هـ، انظر: فهرس معهد المخطوطات ١/ ٢٥٦).
١٠٠ (ستة أجزاء)، ١٠٦ (٢٢٦ ورقة، ٦١٦ هـ) ٣٤٩، ٧٦٢، ٧٦٨، رامبور ١، فقه ١٧٤، ٧٦ - ٧٩ (أربعة أجزاء) (٧٧)، جار الله ٦٧٠ (ج ٥، ٦٢٦ هـ)، ٦٧١ (ج ٤ ٢١٠ ورقة)، أيوب ٩١ (قبل ٦٢٢ هـ)، ملّا چلبى ٤٢ - ٤٣ (ج ١، ٢، ١٨٣ ورقة، ٢٠٠ ورقة، ٧١٣ هـ)، نور عثمانية ١٥٨٠ - ١٥٨١ (ج ١، ٢، ٤٢٧ ورقة، ٤٢٧ ورقة، ٩٧٠ هـ)، راغب ٥١٤ (ج ١، ١٨٨ ورقة، فى القرن الحادى عشر الهجرى)، ٥١٥ (ج ٢، ٤١٥ ورقة، ٦٧٤ هـ)، ٥١٦، (ج ٣، ٤٣٦ ورقة، فى القرن الثامن الهجرى)، ٥١٧ (٢٦٣ ورقة)، حميدية ٢٢٢ (٥٩٣ ورقة، ٩٦٧ هـ)، الجامع الكبير فى بورسه، فقه ٤ (مخطوطة قديمة نسبيا)، انظر: شاخت ١/ ٥ ن، ٣/ ٥ ف، آصفية فقه ١٣٢ - ١٣٣ (٥٠٣ ورقة، ٢٤٧ ورقة، ١١٣٩ هـ)، فيض الله ٩٤٨، ٧٥١ (٥٩٨ ورقة)، عاطف ٨٥٩ (ج ١، ٦٥١ ورقة فى القرن الحادى عشر الهجرى)، ٨٦٠ (ج ٢، ٦٥٩ ورقة، فى القرن الحادى عشر الهجرى)، حاجى خليل ٩١ (جزء ان، حوالى ٣٠٠ ورقة، ٦٢٢ هـ عليها سماع من المؤلف)، تشستربيتى ٣٦٣٢ (ج ٥، ٢٣٤ ورقة، فى القرن السابع الهجرى)، ٣٦٣٤ (٢٩٠ ورقة، فى القرن الثامن الهجرى)، ٣٨٢٥، (٢٥٤ ورقة، فىالقرن الثامن الهجرى)، طلعت، فقه ٧١٢ (٦٦٣ هـ)، الأزهر ٢/ ١١٥، فقه حنفى ٢٨٠٢ (ستة أجزاء، ١٣١٧ هـ)، ج ٧/ ٣١ - ٣٢ رقم ٦٢٨٩١ (ج ٣، ١٤٨ ورقة).
١٣ - (يطابق رقم ٩ ب فى ملحق بروكلمان) «الوجيز» للمؤلف، قره چلبى ١١٦ (٢٣٨ ورقة، ٦٦٥ هـ)، فيض الله ٩٤٨ (١٩٣ ورقة، فى القرن التاسع الهجرى)، فاتح ١٦٩٦ (١٥٣ ورقة)، ١٦٩٦ (١٥٨ ورقة، ٦٦٣ هـ)، ولى الدين ١٢٢٥ (٢٣٧ ورقة، ٦٧٤ هـ)، ١٣٥٠/ ٣ (من ١٢٥ ب- ٣٠٨ أ، ٦١٦ هـ)، جار الله ٧١٦ (١٨٩ ورقة، ١٠١١ هـ) مراد ملّا ٧٧٢ (٢٣٩ ورقة، ١٠٧٦ هـ) انظر: شاخت ١/ ٥ م، ٣/ ٥ ل، بنگيبور ١٩/ ١/ ٩١ رقم ١٥٩٢ (٣٨٧ ورقة، ١١٣٨ هـ).
١٤ - (يطابق رقم ١٠ فى ملحق بروكلمان) «التيسير» لأبى الحسن على بن خليل بن على بن الحسين الدّمشقى (المتوفى ٦٥١ هـ/ ١٢٥٣ م انظر الجواهر للقرشى ١/ ٣٦٢)، أيوب، حاجى بشير ٩٠، ٩٢ (جزءان، ٢٥٠ ورقة ٢٢٠ ورقة، ٧٣١ هـ، ٧٢٠ هـ)، القاهرة ثان ١/ ٤٣٩، فقه حنفى ١١٢ (٢٧٣ ورقة، فى القرن الثامن الهجرى)، انظر: شاخت ١/ ٥ و، محمودية بالمدينة ٧١٩ (٧٨)، انظر: شبيس Spies ,in ZDMG ٩٠ /١١٥
_________
(٧٧) يصحح الرقم عند بروكلمان فى الملحق.
(٧٨) نسب بروكلمان هذا الشرح إلى محمد بن عباد الخلاطى والصحيح ما ذكرنا.','3','62',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (819,'١٥ - «النور اللامع فى أصول الجامع» لمحمود بن حمزة الحسينى (المتوفى ١٣٠٥ هـ/ ١٨٨٧ م) القاهرة، ملحق ٣/ ١٨٣ - ١٨٤ رقم ١٩٩٥٠ ب (الأوراق من ١ - ١٤، ١٣٠٢ هـ) (٧٩) نظم كتاب الجامع الكبير: ١ - نظمه شعرا أحمد بن أبى المؤيد المحمودى النّسفى (ألّفه سنة ٥١٥ هـ/ ١١٢١ م) يوجد مع الشرح، باريس ٨٢٠ (٢٨٠ ورقة، ٦١٨ هـ، انظر: فايدا ٣٥١) /، برلين الشرقية ١٣٤٣ (٣٧٩ ورقة، ٦٣٢ هـ)، سراى، أحمد الثالث ٧٣٢ (١٨٢ ورقة فى القرن الثامن الهجرى)، ٧٣٣ (٢٠٨ ورقة، ٧٢٦ هـ. انظر: فهرس معهد المخطوطات ١/ ٢٦٥، قارن كتالوج ٢/ ٣٨٦، حيث ذكر أن الشارح هو أوحد الدين الكرمانى)، فيض الله ٧٥٠ (٢٢١ ورقة، ٧٠٩ هـ)، فاتح ١٦٨٨ (١٤٥ ورقة، فى القرن الحادى عشر الهجرى)، القاهرة ثان ١/ ٤٦٩، فقه حنفى ٥٤٨، ٥٥٩ (١٥٨ ورقة، ٦٤٦ هـ)، تيمور، فقه ٦٢٠ (١٦٥ ورقة، ١٣٠٥ هـ)، انظر: شاخت ١/ ٥ د، ٢/ ٤ ب، (المتحف الآسيوى ببطرسبورج ١٠٤)، كوبريلى ٦٥٦ (١٧٢ ورقة، ٩٦٧ هـ)، لاله لي ١١٨٩/ ٢ (٧٢٠ هـ)، سوهاج، فقه ٣٣ (١٩٣ ورقة، ٦١٠ هـ، انظر: فهرس معهد المخطوطات ١/ ٢٧٥).
وهناك شرح آخر منظوم بعنوان «تفهيم التحرير فى شرح الجامع الكبير» لمحمود بن عبيد الله بن سعيد الحارثى (المتوفى ٦٠٦ هـ/ ١٢٠٩ م. انظر: الجواهر للقرشى ٢/ ١٥٩) القاهرة ثان ١/ ٤١١، فقه حنفى ٩٥ (١٨١ ورقة، ١٨٨ ورقة، ٦٨٨ هـ)، انظر: شاخت ٢/ ٤، رئيس الكتاب ٣٠٦ (٣١٩ ورقة، ٦٧٩ هـ) ويوجد مختصر له بسراى، أحمد الثالث ٧٣٥ (٢٢٩ ورقة، ١١٠٠ هـ).
٢ - «الفرائد شرح نظم تلخيص الجامع الكبير» لبدر الدين محمود بن عمر التّقادى (ألّفه ٨٠٥ هـ/ ١٤٠٢ م)، سراى، أحمد الثالث ١٢٢٠ (٢٨٣ ورقة، فى القرن التاسع الهجرى).
مختصرات الجامع الكبير: ١ - مختصر لأبى نصر أحمد بن محمد العتّابى البخارى (المتوفى سنة ٥٨٦ هـ/ ١١٩٠ م انظر بروكلمان ملحق ١/ ٦٤٣)، الفاتح ٢١٣٢/ ١ (من ١ - ١٢٢ ب، ٧١٢ هـ).
٢ - مختصر لمحمد بن عبّاد بن ملكداد الخلاطى (المتوفى ٦٥٢ هـ/ ١٢٥٤ م انظر بروكلمان ١/ ٣٨١) برلين ٤٥٠٨ (٩٧ ورقة، حوالى ١٠٠٠ هـ)، شهيد على ٦٠٧/ ١ (من ١ - ١٢١ أ، ٧٨٠ هـ)، سراى، أحمد الثالث ٧٣٠ (٢٩٠ ورقة، فى القرن التاسع الهجرى)، ٧٣١ (٢٤٠ ورقة، ٨٦٥ هـ انظر: فهرس
_________
(٧٩) يحذف الشرح رقم ٦ الذى ذكره بروكلمان.','3','63',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (820,'٢/ ٣٨٢)، فيض الله ٦٩٦ (٢٥٣ ورقة ٨٦٤ هـ)، بايزيد ٢٣١٠ (١٣١ ورقة، فى القرن الثانى عشر الهجرى)، مكتبة جامعة إستنبول ٥٣٢٧ (١٨٠ ورقة، حوالى ٩٠٠ هـ)، جار الله ٦٠٣ (٢٣٥ ورقة، فى القرن الحادى عشر الهجرى)، حسين چلبى فى بورسه، فقه ٢ (٦٥٢ هـ)، حراچى زاده فى بورسه، فقه ١٠، القاهرة ثان ١/ ٤١١، فقه حنفى ٢٧ م (١٣٠ ورقة)، انظر: شاخت ١/ ٥، ٢/ ٤ ج، (قوله ١/ ٣١٤)، شهيد على ١٠١٤، فاتح ١٥٥٢ (١٩٩ ورقة، ٨٢١ هـ)، أسعد أفندى ٥٨٤ (١٤٧ ورقة، ٨٦٨ هـ)، أيا صوفية ١٠٧٣ (١٠٢ ورقة، فى القرن العاشر الهجرى)، داماد إبراهيم ٤٩٩ (٣٦١ ورقة، ٧٧١ هـ)، حفيد أفندى ٥٧ (١٥١ ورقة، فى القرن الثانى عشر الهجرى، لاله لي ٨٤١ (١٧٦ ورقة)، ينى ٣٧٨ (١٧٥ ورقة، فى القرن التاسع الهجرى)، خسرو باشا ٧٧ (١٤٨ ورقة، ١١٧٦ هـ)، حسن حسنى باستنبول ٥١٣ (١٨٧ ورقة، ٧٢٢ هـ)، عاطف ٧٦١ (١١٦ ورقة، فى القرن العاشر الهجرى)، محمود باشا ١٧٥ (١٨٢ ورقة، ٧٨٤ هـ)، وهبى ٤٦٣ (١٨١ ورقة، ٦٨٠ هـ)، المتحف البريطانى مخطوطات شرقية ١٢٢٠٣، ليدن ٦٥٥ (١٥٠ ورقة، ٨٨٢ هـ، انظر: فورهوف ٩٢)، ييل ٣٧٠ (١٦٠ ورقة، فى القرن الثانى عشر الهجرى، انظر: نموى ٩٢٣)، الأزهر، ٢/ ١٢٤، فقه حنفى ٢٤٣٤ (٣٤١ ورقة، ٨٦١ هـ).
شروح لمختصرات الجامع الكبير: ١ - شرح لتاج الدين أحمد بن إبراهيم بن برهان الحلبى (المتوفى ٧٣٨ هـ/ ١٣٧٨ م) (٨٠) الزيتونة بتونس ٤/ ١٤٥ رقم ٢١١٨ (ج ١، ٢، ٢٦٣ ورقة).
٢ - «تحفة الحريص فى شرح التلخيص» لعلى بن بلبان بن عبد الله النّميرى الفارسى (المتوفى ٧٣٩ هـ/ ١٣٣٩ م انظر بروكلمان ملحق ٢/ ٨٠)، فيض الله ٧٣٧ - ٧٣٩ (ثلاثة أجزاء ٢٣٧ ورقة، ٢٢٨ ورقة، ٢١٥ ورقة، ٨٨٣ هـ)، ٧٤٠ - ٧٤١ (جزءان، ٢٩٢ ورقة، ٣٢٩ ورقة، فى القرن التاسع الهجرى)، ٧٤٢ (٥٢٩ ورقة، فى القرن التاسع الهجرى)، ينى ٤٢٦ - ٤٢٧ (ج ١، ٢، ٢٧٠ ورقة، ٢٥٣ ورقة، ٨٥٧ هـ)، شهيد على ٧٩٧، (ج ١، ١٥٢ ورقة، فى القرن الثامن الهجرى) ٧٩٨ (ج ٢، ١٢١ ورقة، فى القرن الثالث عشر الهجرى)، ولى الدين ١١٥٨ (٣٤٩ ورقة، فى القرن الثامن الهجرى)، القاهرة ثان ١/ ٤٠٨، فقه حنفى ٢٠ م (سبعة أجزاء بخط المؤلف، الجزء الخامس غير موجود)، ١٠٣ (١٦١ ورقة)، ١٢٣ (٣٢٢ ورقة)، ١٢٠٧ (خمسة أجزاء)، انظر: شاخت ١/ ٥، ٢/ ٤ هـ، ٣/ ٥ هـ بلدية الإسكندرية ١٢٨٦ (ج ٢)، تونس ٤/ ٧٦ رقم ١٩٢٩ - ١٩٣٠ (ج ١، ٢، ٥٤٢ ورقة، ٥٥٣ ورقة)، أسعد أفندى ٧٨٧ (الجزء الأخير)، داماد إبراهيم ٥٣٤ - ٥٣٥ (ج ١، ٢، ٣٥٩ ورقة، ٣٦٧ ورقة، ٨٦١ هـ)، جار الله ٦٥٣ (ج ٥، ١٤٠ ورقة، فى القرن الثامن الهجرى)، سراى، أحمد الثالث ١٠٦٥ (ج ٢، ٣٩٣ ورقة ٧٨٦ هـ،
_________
(٨٠) انظر: هدية العارفين ١/ ١٠٩.','3','64',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (821,'انظر: فهرس ٢/ ٣٨٢)، مكتبة جامعة إستنبول ٤٧٥٩ (٤٨٦ ورقة، ٨٨١ هـ)، تشستربيتى ٣٨٦٠ (ج ٢، ٢٠٢ ورقة، فى القرن الثامن الهجرى)، جامع الشيخ/ بالإسكندرية ١١٥ (ج ٤، ٢٨٧، فى القرن التاسع الهجرى)، ١٣٦ (كامل، ٦٠٠ ورقة، فى القرن العاشر الهجرى، انظر: فهرس معهد المخطوطات ١/ ٢٥٧).
واختصر هذا الشرح عمر بن إسحاق الشّبلى (المتوفى سنة ٧٧٣ هـ/ ١٣٧١ م انظر بروكلمان ٢/ ٨٠)، برلين ٤٥٠٩ (١٨٧ ورقة، بخط المؤلف، حوالى ٧٦٠ هـ).
٣ - «التنوير» لمسعود بن محمد بن محمد بن سهل الغجدوانى (المتوفى حوالى ٧٧٢ هـ/ ١٣٧٠ م انظر بروكلمان ملحق ٢/ ٢٧٠)، ينى ٤٢٨ (من ١ - ١٠ ورقة)، القاهرة ثان ١/ ٤٤١ فقه حنفى ٩٦ (مخطوطة شاملة، ٢٤٥ ورقة)، السليمانية ٤٥٩ (٨١) (ج ١، ٤٥٠ ورقة)، فيض الله ٧٤٦ (ج ٢، ٣٤٠ ورقة، ٧٧٩ هـ)، ٧٥٧ - ٧٥٨ (ج ١، ٢، ٣٤٠ ورقة، ٤٠٥ ورقة، فى القرن الثامن الهجرى)، ملّا چلبى ٤٤ (ج ١، ٢٠٠ ورقة، فى القرن العاشر الهجرى)، ٤٥ - ٤٦ (ج ١، ٢، ٢١٧ ورقة، ٢٧١ ورقة، فى القرن العاشر الهجرى)، سراى أحمد الثالث ٧٣٤ (ج ١، ٣٧٨ ورقة، ٨٥٢ هـ)، ٧٣٦ (ج ١، ٢، ٣١٤ ورقة، ٣٥٤ ورقة، ٨٢٩ هـ)، ١٠١٥ (ج ١، ١٧٧ ورقة، ٨٥٠ هـ انظر: فهرس ٢/ ٣٨٧)، شهيد على ٧٩٩ - ٨٠٠ (٣٦٧ ورقة، ٣٤٧ ورقة، ٨٦٧ هـ)، ٨٠١ (٤٤٩ ورقة، ٨٨٠ هـ)، انظر شاخت ١/ ٥ س ٣/ ٥ ك، لاله لي ٩٦٣ (٤٠ ورقة فى القرن الثامن الهجرى، انظر: فهرس معهد المخطوطات ١/ ٢٦٢)، ٩٦٧ (ج ٢، ٣٦٨ ورقة، ٨١٥ هـ)، ولى الدين ١١٥٢ (ج ٢، ٨٥٤ هـ)، طرخان ٢/ ٢٩ (٢٨٤ ورقة، ٨٥٥ هـ)، ييل ل- ٥٧٤ (٣٣٨ ورقة، فى القرن الثامن الهجرى، يحتمل أن يكون بخط المؤلف، انظر: ينموى ٩٠١)، تيمور فقه ٦٦٦ (٣٨٠ ورقة ٨٩٩ هـ، انظر أيضا: القاهرة، ملحق ١/ ١٨٨).
٤ - شرح لمحمد بن محمود أكمل الدين البابرتى (المتوفى ٧٨٦ هـ/ ١٣٨٤ م انظر بروكلمان ملحق ٢/ ٨٩) القاهرة ثان ١/ ٤٣٩ فقه حنفى ٧٦٩ (١٤٦ ورقة)، جار الله ٦٥٥ (ج ١، ٢٥٧ ورقة فى القرن الحادى عشر الهجرى)، انظر: شاخت ٢/ ٤، ٣/ ٥، الزيتونة بتونس ٤/ ١٤٤ رقم ٢١١٧ (ج ١، ٢٨٩ ورقة).
٥ - شرح لسعد الدين مسعود بن عمر التفتازانى (المتوفى ٧٩٢ هـ/ ١٣٩٠ م انظر بروكلمان ٢/ ٢١٥) ينى ٤٢٨/ ٤٢٨ (ج ١، ٣٥٠ ورقة، فى القرنالتاسع الهجرى)، مراد ملّا ٨٤٨ (١٦٤ ورقة، فى القرن التاسع الهجرى)، رشيد أفندى ٢٠٣ (٩٠ ورقة)، لاله لي ٩٦٣/ ١ (من ١ - ٣٩ ب، ناقص، فى القرن العاشر الهجرى).
_________
(٨١) يصحح الرقم عند بروكلمان.','3','65',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (822,'٦ - شرح لمحمد بن حمزة الفنارى (المتوفى ٨٣٤ هـ/ ١٤٣٠ م انظر بروكلمان ٢/ ٢٣٣)، جار الله ٦٥٦ (٦٧ ورقة، فى القرن الحادى عشر الهجرى) انظر: شاخت ١/ ٥، لاله لي ٩٦٣/ ٢ (٨٠ ورقة، فى القرن التاسع الهجرى، بخط المؤلف، انظر فهرس معهد المخطوطات ١/ ٢٦٢) ينى ٤٢٨/ ٢ (٧٩ ورقة، فى القرن الثانى عشر الهجرى).
٧ - «التمحيص» لشمس الدين الهروى (فى القرن التاسع الهجرى)، جار الله ٦٧٥/ ١ (الأوراق ١ ٢٦٢، فى القرن التاسع الهجرى، بخط المؤلف)، انظر: شاخت ١/ ٥.
٨ - شرح لمجهول، لاندبرج برلين ٦٥٥ (ج ٣) (٨٢).
٩ - ويوجد شرح مترجم إلى اللغة التركية لمحمد الموقوفاتى، شهيد على ٧١٩ (١١٠ ورقة، فى القرن الثانى عشر الهجرى).
١٠ - «ملتقطات من الجامع الصغير والكبير»، وهو مختارات لأبى الوفاء الطيبى الحلبى (ألّفه سنة ٩٣٦ هـ/ ١٥٢٩ م)، لاله لي ٦٧٠ (٥٧٦ ورقة، ٩٣٦ هـ، بخط المؤلف).
ومنه: «الوجيز» تهذيب جديد مختصر (٨٣) للجامع الكبير لأبى الربيع سليمان بن وهب (المتوفى ٦٧٧ هـ/ ١٢٧٨ م انظر بروكلمان ١/ ٣٨٢)، فاتح ١٥٥١ (١١٣ ورقة، ٧١٧ هـ)، ١٥٥٣ (٢١٧ ورقة، ٧٠٤ هـ)، ١٥٥٥/ ١ (من ١ - ١٤٢ أ، ٦٨١ هـ) ١٥٥٦/ ١ (من ١ - ١٠٤ أ، ٧٠٠ هـ)، عاطف ٧٦٨ (٢٩٠ ورقة ٧٥٦ هـ)، فيض الله ٩٤٧، (١٢٣ ورقة، ٩٦٥ هـ)، حراچى زاده فى بورسه، فقه ٢٠٣، ٢٠٤ (٢٩٩ ورقة)، انظر: شاخت ١/ ٥، أسعد أفندى ١٠٤١ (١٨٧ ورقة، ١١٧٧ هـ)، دار الكتب بالقاهرة، فقه حنفى ٤٨٥ (١٩١ ورقة، ٧٢٩ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٧٦).
وعليه: شرح لعثمان بن إبراهيم بن مصطفى الماردينى (المتوفى ٧٣١ هـ/ ١٣٣١ م (٨٤) مراد ملا ٨٥٥ ٨٥٧ (أجزاء ١ - ٣، ٣٧٧ ورقة، ٣٧٠، ٢٦٠ ورقة، ٧٥٥ هـ)، جار الله ٦٦٩ (٨٥)، (١٧٠ ورقة، ٧٥٥ هـ)، القاهرة ثان ١/ ٤٤١، فقه حنفى ١٠١ (ج ١)، ١٠٤ (ج ٣، ٢٣٧ ورقة) ٢١٤ (ج ١، ٢٤٠ ورقة)، ٣٥١ (ج ٢، ٣٣٢ ورقة)، ٤٩٥ (ج ٢، جزء ضخم ٨١٢ هـ)، انظر شاخت: ١/ ٥، ٢/ ٤ (٨٦).
_________
(٨٢) ذكر بروكلمان أن مؤلف هذا الشرح هو نفسه الخلاطى صاحب المختصر، وهذا غير صحيح.
(٨٣) ليس شرحا لمختصر الخلاطى كما ذكر ذلك بروكلمان، الملحق ١/ ٢٩٠.
(٨٤) انظر: الدرر الكامنة لابن حجر ٢/ ٤٣٥، وتاج التراجم لابن قطلوبغا: ٣٠.
(٨٥) ذكر بروكلمان أن الرقم ٦٦٩١ والصحيح ما ذكرنا.
(٨٦) تحذف مخطوطة ملاجلبى ٤٩ التى ذكرها بروكلمان.','3','66',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (823,'ولكتاب «الوجيز» مختصر/ يسمى «المنتخب السليمانى، لأبى العباس أحمد بن إبراهيم بن عبد الغنى السّروجى (المتوفى ٧١٠ هـ/ ١٣١٠ م، انظر: بروكلمان الملحق ٢/ ٤٣٤، ومعجم المؤلفين لكحالة ١/ ١٤٠)، جار الله ٩٠٣/ ٢ (من ٩١ أ- ١٥١ ب).
خامسا: «الجامع الصغير» (٨٧) بتنقيح أبى طاهر محمد بن محمد بن سفيان الدّبّاس البغدادى (المتوفى حوالى ٣٤٠ هـ/ ٩٥١ م) (٨٨)، نور عثمانية ١٤٣٨ (٦٦ ورقة، ١١١٣ هـ) القاهرة ثان ١/ ٤١٣، فقه حنفى ٧٦٥، ٧٧٥، ٩٦٩ (٨٩)، بايزيد ٢٢٧١ (من ١ - ٨١ أ، فى القرن الثانى عشر الهجرى)، لاله لي ٨٤٩/ ١ (من ١ - ١٠٣ أ، ٧٥٩ هـ)، مراد ملّا ٧٦٤ (٦٠٠ ورقة) أسعد أفندى ٦٢٣ (٥٧ ورقة، ٩٥٩ هـ)، جار الله ٦٠٦/ ١ (٤٠ ورقة)، عاشر أفندى ٩٥ (١٠٧ ورقة، ٩٥٠ هـ)، أيا صوفيا ١٣٨٥/ ٣ (من ٢٥٩ ب- ٣٣٦ أ، ١١٢٩ هـ)، فيض الله ٦٩٨/ ١ (من ١ - ٦٠ ب، ٩٨٨ هـ).
وطبع على هامش «كتاب الخراج» ببولاق ١٣٠٢ هـ، ولكنو ١٢٩١ هـ، ١٢٩٤ هـ، ١٣٢٨ هـ.
شروح الجامع الصغير: ١ - شرح لعلى بن محمد بن حسين البزدوى (المتوفى ٤٨٢ هـ/ ١٠٨٩ م انظر بروكلمان ١/ ٣٧٣)، رامبور ١/ ٢٠٧، فقه ٢٦٢ (٢٠٢ ورقة)، سراى أحمد الثالث ٧٢٦ (٣٣٤ ورقة، فى القرن التاسع الهجرى، انظر: فهرس ٢/ ٣٨٤)، ٧٢٧ (٤١٢ ورقة، ٥٢١ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٢)، عاطف ٤٥٥ (٣٨٥ ورقة، ٩٥١ هـ)، مراد ملّا ٨٥١ (٢٦٨ ورقة، ٧٥٧ هـ)، فيض الله ٧٥٣ (٢٩٤ ورقة، ٥٥٢ هـ)، جار الله ٦٠٥ (٣١١ ورقة، ٦٦٠ هـ)، ٦٦٢ (٣٤٧ ورقة، فى القرن العاشر الهجرى)، عابدين بدمشق (فى القرن الثامن الهجرى، انظر: مجلة معهد المخطوطات العربية ٥/ ٢١٣).
٢ - شرح لأبى بكر محمد بن أبى سهل السّرخسى (المتوفى ٤٨٣ هـ/ ١٠٩٠ م) وهبى ٥٦٥ (٢٧٧ ورقة، ٩٤٨ هـ).
٣ - شرح لأبى الأزهر محمد بن أحمد بن الإسبيجابي الخجندي (المتوفى حوالى
_________
(٨٧) انظر: كشف الظنون لحاجى خليفة ٥٦١ فى علاقة أبى يوسف بهذا الكتاب.
(٨٨) انظر: الجواهر للقرشى ٢/ ١١٦ - ١١٧، والوافى بالوفيات للصفدى ١/ ١٦٢.
(٨٩) جميع المخطوطات الأخرى التى ذكرها بروكلمان ليس هذا موضعها.','3','67',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (824,'٥٠٠ هـ/ ١١٠٦ م) (٩٠)، فاتح ١٦٩٥ (٢٠١ ورقة، فى القرن السادس الهجرى)، فيض الله ٧٦٠ (٢٤٣ ورقة، ٥٤١ هـ، بخط المؤلف).
٤ - شرح لأحمد بن أبى المؤيّد المحمودى النسفى (ألّفه حوالى ٥١٥ هـ/ ١١٢١ م) (٩١) البنغال ٣٨٦ (١٦٧ ورقة، فى القرن الثامن الهجرى).
٥ - تهذيب للجامع الصغير مع شرح له، يحمل غالبا عنوان: «جامع الصدر الشهيد» لعمر بن عبد العزيز بن مازه (المتوفى ٥٣٦ هـ/ ١١٤١ م انظر بروكلمان ١/ ٣٧٤)، وهذا التهذيب يعتمد على عمل أبى طاهر محمد بن شعبان الدبّاس (المذكور آنفا)، برلين ٤٤٣٧ (١٤٩ ورقة، حوالى ١٠٠٠ هـ)، القاهرة ثان ١/ ٤١٣، فقه حنفى ٧٦٥، ٧٧٥، ٩٦٩، المكتب الهندى ٤٠٩٧ (٢٩٦ ورقة، كتالوج ٢/ ١٥١٢)، بنكيبور ١٩/ ١/ ٩٣ رقم ١٥٩٣ (١٥٤ ورقة، ٧٨٣ هـ)، بلدية الإسكندرية ١٣١٥ ب (٧٥٤ هـ)، جوتا ٩٩٨ (١٨٥ ورقة)، ميونيخ ٢٦١ (١٠٨ ورقة)، ٢٦٢ (١٩٩ ورقة، ٨٧٠ هـ)، ٢٦٣ (١٨٥ ورقة، ٧١٥ هـ)، جاريت ١٦٧٢ (٢٤١ ورقة، ٧٨١ هـ)، ١٦٧٣ (١٥٩ ورقة، ٨٠٨ هـ)، باريس ٨٢١ (١٩٤ ورقة، ٧٤٠ هـ)، ٨٢٢ (٢٠٨ ورقة، ١٦٤٠ هـ)، انظر: فايدا (٣٥١)، ليدن ١٧٧٤ (١٩٨ ورقة، ٨٨٤ هـ، انظر: فورهوف ٩٤)، ينى ٤٣٤ (٢٠٢ ورقة، ١٠٢١ هـ)، سليم أغا ٢٩٧ (١٤٩ ورقة، ٧٧٧ هـ)، الزيتونة بتونس ٤/ ٨٤، رقم ١٩٣٨ (١٢٠ ورقة)، رامبور ١/ ٢٠٧، فقه ٢٦٠ (٢١٨ ورقة، ١٠٠٢ هـ)، هيدلبرج ١٤٩ (٢٦٢ ورقة، ١١٠١ هـ، انظر مجلة، (٢٢٦/ ٦ zs آصفية ٢/ ١٠٨٠، فقه حنفى ١/ (١٠٦٢ هـ)، ١٠٨٨، فقه حنفى ١٥٣ (٨٠٢ هـ)، جار الله ٦٦٤ (٧٣٦ هـ)، ٦٦٥ (٦١٤ هـ)، سراى، أحمد الثالث ٧٢٤ (٢٧٥ ورقة، ٧٤٧ هـ) انظر: فهرس ٢/ ٣٨٣)، سراى، ريفان ٥٩٦ (١٤٣ ورقة، ٧٦٢ هـ)، لاله لي ٨٥٠ (١٨٤ ورقة، ٦٨٧ هـ)، رشيد أفندى ١٨٤ (٧٨٩ هـ)، شهيد على ٧٩١ (١٤٠ ورقة ٦٢٩ هـ)، فيض الله ٧٤٧ (١٦٣ ورقة، ٦٦٠ هـ) حسن حسنى بإستنبول ٣٤٢ (١٦٤ ورقة، ٧٣٣ هـ)، ٣٤٢ (٢٢٦ ورقة، ٧٩٦ هـ)، أيا صوفية ١٠٩٤ (٢٠٠ ورقة، ٨٥٥ هـ)، ١٣٥٨ (١٧٣ ورقة، ٩٠٣ هـ)، قره حلبى ١١١) ١٩٦ ورقة فى القرن العاشر الهجرى)، ١١٤ (٣٦٨ ورقة، فى القرن العاشر الهجرى)، مراد ملّا، ٨٤٠ (٢٨٩ ورقة، ٧٧١ هـ)، محمود باشا ١٧٧ (١٩٣ ورقة، ٧٦٨ هـ)، سيرز ٨٤٩ (١٠٩٩ هـ)، عموجة حسين ١٩٦، مانيسا/ ٧٤١ (١٥٩ ورقة، ٧٢٤ هـ)، قونية، يوسف أغا ٤٨٠٨ (٢٦٥ ورقة، ٥٧٤ هـ)، ييل أ- ٢٥ (٢٠٦ ورقة، ١٦٦٩ م انظر: نيموى ٩٤٦)، البنغال ٣٨٧ (٢٤٣ ورقة ٨٦٩ هـ)، ٣٨٨ (١٣٧ ورقة، فى القرن الثامن الهجرى)، كيمبردج، مخطوطات شرقية ١٠٠٦ (٩) (٢١٩ ورقة ٧٢٤ هـ، انظر: آربرى ٣) الظاهرية، فقه حنفى ١٠٩ (٢٣٨ ورقة،
_________
(٩٠) انظر: كشف الظنون لحاجى خليفة ٥٦٢.
(٩١) انظر: معجم المؤلفين لكحالة ٢/ ١٩١.','3','68',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (825,'٧٦٠ هـ)، ١١٠ (٢٩٠ ورقة)، ١١١ (١٦٦ ورقة)، انظر مجلة المجمع العلمى العربى بدمشق ١٢/ ٤٤٣، الأزهر ٢/ ١٢٨ فقه حنفى ١٥ (١٤١ ورقة)، ٢٥٦ (١٤٦ ورقة، ٧٤٥ هـ)، ٣٨٩ (١٥٥ ورقة)، ١٩٧٧ (٢٣٠ ورقة، ٥٠١ هـ)، ٢٤١٧ (١٥٧ ورقة، ٧٣٥ هـ)، ٢٨٢٢ (١٨٨ ورقة).
ولهذا الشرح حاشية للأسود على بن عمر علاء الدين (المتوفى ٨٠٠ هـ/ ١٣٩٨ انظر: معجم المؤلفين لكحالة ٧/ ١٥٨)، فاتح ١٦٨٦ (٣٣٠ ورقة، ٧٩٨ هـ).
٦ - شرح لأبى المفاخر عبد الغفور بن لقمان بن محمد الكردرى (المتوفى ٥٦٢ هـ/ ١١٦٦ م، انظر:
معجم المؤلفين لكحالة ٥/ ٢٦٩)، سراى، أحمد الثالث ٧٢٨ (٣٠٥ ورقة، فى القرن التاسع الهجرى، انظر:
فهرس ٢/ ٣٨٤).
٧ - شرح لعلاء الدين محمد بن عبد الحميد السمرقندى (المتوفى ٥٦٣ هـ/ ١١٦٨ م، انظر: بروكلمان ملحق ١/ ٦٤١ ومعجم المؤلفين لكحالة ١٠/ ١٣٠)، ، مراد ملّا ٨٥٤ (٤٨٥ ورقة، ٧٦٤ هـ).
٨ - شرح لأبى نصر أحمد بن محمد بن عمر البخارى العتّابى (المتوفى ٥٨٦ هـ/ ١١٩٠ م انظر بروكلمان ملحق ١/ ٦٤٣)، ينى ٤٣٥، (١٧٦ ورقة، ٧٠٨ هـ)، القاهرة ثان ١/ ٤٤١، فقه حنفى ٢٤٧ (٩٢)، جار الله ٦٦٧ (١٨٢ ورقة، ٦٢٧ هـ)، فيض الله ٧٥٩، (١٩٠ ورقة، ٩٧٠ هـ)، فاتح ١٦٨٥ (١٨٩ ورقة، ٨٦٥ هـ)، نور عثمانية ١٤٣٩ (١٦٦ ورقة، ٨٦٨ هـ)، سراى، أحمد الثالث ٧٢٩/ ١ (من ١ ١٦٧ ب، ٦١٨ هـ، انظر: فهرس ٢/ ٣٨٥، وفهرس معهد المخطوطات العربية ١/ ٢٦٢)، قره چلبى ١٠٢ (٢١٨ ورقة، فى القرن السابع الهجرى)، ١٠٣ (٣٢٩ ورقة ٦٥٦ هـ)، وهبى ٤٦٣ (١٨١ ورقة، ٦٨٠ هـ) قسطمونى ٣٤٥٨ (٢٥٠ ورقة، ٧٢٩ هـ)، جوروم ١٤٢٦ (١٩٠ ورقة ٦٣٩ هـ).
٩ - شرح للحسن بن منصور قاضي خان (المتوفى ٥٩٢ هـ/ ١١٩٦ م انظر بروكلمان ١/ ٣٧٦) ينى ٤٣٦ (٢٤٦ ورقة، ٧٢٠ هـ)، مراد ملّا ٨٥٢ (٢٤٢ ورقة، ٩٩١ هـ)، القاهرة ثان ١/ ٤٤١، فقه حنفى ٧٤٣ (جزءان، ٧٤٤ هـ)، بلدية الإسكندرية ١٢٨٣ ب (١٤٧ ورقة، ٩١٠ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٨٣)، الزيتونة بتونس ٤/ ١٤٥ رقم ٢١١٩ (٣٤٦ ورقة، ١١٨٤ هـ)، رامبور ١/ ٢٠٦، فقه ٢٥٩ (٤٣٣ ورقة)، عاطف ٨٥٧ (٣٤٣ ورقة، ٦٢٩ هـ)، جار الله ٦٦١ (٥٦٠ ورقة، فى القرن التاسع الهجرى)، ٦٦٨ (٢٥٨ ورقة، ٩٩٤ هـ)، فيض الله، ٧٥٢ (٢٦٢ ورقة، فى القرن الثامن الهجرى)، فاتح ١٦٨٧ (٣١٩ ورقة، ٦٨٨ هـ) ١٦٩٠ (٣٤٦ ورقة، ٧٠٥ هـ)، جور لولو ١٨٤ (٢٢٦ ورقة، ٩٨١ هـ).
_________
(٩٢) تحذف المخطوطات الآتية التى ذكرها بروكلمان: آصفية ٢/ ١٠٢٨، جوتا ٩٩٥، ميونيخ ٢٦١ - ٢٦٣، درسدن ١٠٥، ليدن ١٧٧٤، باريس ٨٢١ - ٨٢٢.','3','69',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (826,'١٠ - شرح لبرهان الدين إمام الحرمين (عاش- على أرجح الاحتمالات- فى القرن السادس الهجرى، انظر: الجواهر للقرشى ٢/ ٣٦٢)، الظاهرية، فقه حنفى ٣٧٣ (٩٣) (٢٣٠ ورقة، ٥٨٥ هـ)، انظر مجلة المجمع العلمى العربى بدمشق ١٢/ ٤٤٣).
١١ - شرح لعلى بن أحمد بن المكى الرازى (المتوفى ٥٩٨ هـ/ ١٢٠١ م انظر بروكلمان ملحق ١/ ٦٤٩) (٩٤)، تشستربيتى ٣٣١٦ (١٠٣ ورقة، فى القرن السابع الهجرى).
١٢ - شرح لمحمود بن أحمد بن عبد السيد البخارى الحصيرى (المتوفى ٦٣٦ هـ/ ١٢٣٨ م انظر بروكلمان ١/ ٣٨٠)، باتنه ١/ ٩١، ٩٢٨)، سراى، خزينة ٤٠ (ج ١، ١٧١ ورقة، ٦٨٤ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٧٦)، دار الكتب بالقاهرة، فقه حنفى ٩٧ (خمسة أجزاء، ٧١٩ هـ)، ٩٨ (أربعة أجزاء، قبل ٦٢٣ هـ)، ٩٩ (ستة أجزاء، بخط المؤلف)، ١٠٠ (ستة أجزاء)، ١٠٦ (جزء واحد، ٦١٦ هـ)، ٣٤٩، ٧٦٢، ٧٦٨، انظر: شاخت ٢/ ٤ ف.
١٣ - شرح لأحمد بن إسماعيل بن محمد التّيمور طاشى (ألّفه قبل ٦٥٠ هـ/ ١٢٥٢ م)، انظر الجواهر للقرشى ١/ ٦١)، جار الله ٦٦٣ (٢٥٤ ورقة، فى القرن الثامن الهجرى)، فيض الله ٧٥٤ (ج ١، ٢٧٠ ورقة، فى القرن الثامن الهجرى)، ٧٥٥/ ١ (٢٤٤ ورقة، ٧٨٥ هـ) ٧٥٦ (ج ٢، ٤١٢ ورقة، فى القرن التاسع الهجرى) تشستربيتى ٥٢٠٣ (٢٥٣ ورقة، فى القرن الثامن الهجرى)، بلدية الإسكندرية ٣٧٠٧ ج (٧١١ هـ)، القاهرة، ملحق ٢/ ٢١ رقم ١٩٦٩٢ ب (٤٥٤ ورقة، ٧٩٠ هـ، نقلا عن نسخة كتبت سنة ٦٥٠ هـ)./ نظم الجامع الصغير: ١ - ونظمه أبو حفص عمر بن محمد بن أحمد النّسفى (المتوفى ٥٣٧ هـ/ ١١٤٢ م انظر بروكلمان ١/ ٤٢٧)، القاهرة ثان ١/ ٤٦٩، فقه حنفى ٥٦٠، ٨٢٢.
٢ - «لمعة البدر» لمحمود (مسعود) بن أبى بكر بن حسين الفراهى (المتوفى حوالى ٦٤٠ هـ/ ١٢٤٢ م انظر بروكلمان ١/ ٣٨١)، باريس ٨٢٣ (٤٤ ورقة، فى القرن الثامن الهجرى)، القاهرة ثان ١/ ٤٥٩ فقه حنفى ٥٤٩، لاله لي ٨٤٩/ ٢ (من ١٠٥ ب- ١٥٢ أ، ٧٥٩ هـ)، ١١٤٦ (٦٤ ورقة، ٨٣٨ هـ).
_________
(٩٣) ذكر بروكلمان أن الرقم هو ٣٨٣ والصحيح ما ذكرناه.
(٩٤) يصحح عند بروكلمان.','3','70',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (827,'وله شرح بعنوان: «ضوء اللمعة» لعلاء الدين محمد بن عبد الرحمن الخجندى (انظر: كشف الظنون لحاجى خليفة ٥٦٤)، القاهرة ثان ١/ ٤٤٣، فقه حنفى ٢٢٨.
٣ - ونظمه أيضا محمود بن حمزة، مفتى دمشق (نظمه سنة ١٢٨٧ هـ)، مكتبة جامعة إستنبول ٥٣٥ (١٨ ورقة، ١٢٨٧ هـ، بخط المؤلف).
«ملتقطات من الجامع الصغير والكبير» لأبى الوفاء الطّيبى الحلبى، وهو مختارات من الكتابين (اختارها سنة ٩٣٦ هـ/ ١٥٢٩ م)، لاله لي ٦٧٠/ ٥٧٦ ورقة ٩٣٦ هـ، بخط المؤلف).
سادسا: «كتاب الآثار»:
يضم مجموعة الأحاديث الأساسية التى بنى عليها أبو حنيفة مذهبه، بايزيد ١٨٥٣ (٨٨ ورقة، ١٢١٨ هـ)، فيض الله ٦٤٤ (١٠١ ورقة، فى القرن الثامن الهجرى)، ينى ٥٦٨ (١٢٠ ورقة، ٧٤٤ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ١٠٢)، سليم أغا ٢٧٥ (١٧٨ ورقة، ٧٦٢ هـ)، ولى الدين ٤٦٦ (١٠٦ ورقة، فى القرن العاشر الهجرى)، ٤٦٧ (١١٥ ورقة، ١٠٥٢ هـ)، كوبريلى ٢٣٣، (١٢١ ورقة فى القرن التاسع الهجرى)، ٣٨٨/ ٢ (الأوراق ٧٥ - ١٤٩، ١٠٧٩ هـ)، لاله لي ٧٩٨ (١١٦ ورقة، فى القرن العاشر الهجرى)، القاهرة ثان ١/ ٣٩٨، فقه حنفى ٤٣ (١١٨ ورقة، ١٢٩٢ هـ)، ١٠٤ م (١٣٤ ورقة)، الخالدية بالقدس فقه حنفى ٩٠ (١١٣٧ هـ)، انظر شاخت ١ رقم ٤، ٣، بنگيبو ١٩/ ٩٦ رقم ١٥٩٥ (١٠٨ ورقة ١١٣٠ هـ)، آصفية ١/ ٦٥٦، حديث ٣٢٥ (٩٥) (الموصل ٨٠، ٢١)، سراى أحمد الثالث ١١٦٣ (١٣٩ ورقة، ٨٧٤ هـ)، فيض الله ٢٥٦، (٧٧٤ هـ)، جار الله ٦٣٢/ ٢ (الأوراق ٥٩ - ١٠٠)، رئيس الكتاب ١٠٥ (٩٠ ورقة ١١٢٨ هـ)، عاشر أفندى ٤٠٠/ ١ (الأوراق ١ - ٩٢، فى القرن الثانى عشر الهجرى)، أيا صوفيا ١٣٨٥/ ٢ (٨٨ ورقة، فى القرن الثانى عشر الهجرى)، حراچى زاده فى بورسه ٥٨٠ (فى القرن الثامن الهجرى)، المكتبة الأهلية بتونس ٢٤٩، حليم بالقاهرة، فقه حنفى ١٥.
ونشره عبد الحى لكنوى فى لكنو ١٨٨٣ م، ولاهور سنة ١٣٠٩ هـ (مع شرح باللغة الأردية).
سابعا: كتاب السّير الصغير ألفه قبل كتاب السير الكبير، وصل فى شرح محمد بن أحمد السرخسى (المتوفى ٤٨٣ هـ/ ١٠٩٠ م) ويوجد فى حفيد أفندى ٩٥/ ٤ (من ١٩١ أ- ١٩٨ ب، فى القرن التاسع الهجرى)
_________
(٩٥) تحذف المخطوطات التى ذكرها بروكلمان بآصفية ١/ ٦٥٦، حديث ٦٦١.','3','71',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (828,'ثامنا: «كتاب السير الكبير»
H. Kruse, Die Begrundungderisl amischen Volkerrechtslehr ein: Saeculum ٥/ ١٩٥٤/ ٢٢١ - ٢٤١.
هذا كتاب فى القانون الدولى العام، وقد وصل بعد تجريده من الإسناد فى شرح محمد بن أحمد السرخسى (المتوفى ٤٨٣ هـ/ ١٠٩٠ م انظر بروكلمان ١/ ٣٧٣) المخطوطات: برلين ٤٩٧٥ (ج ١، ٢٤٣ ورقة ٦٢٠ هـ)، فينا ١٧٧٨ (٣٨٤ ورقة، ١١١٢ هـ)، باريس ٧٨٤/ ١ (الأوراق ١ - ١٢٨، ٧٥٤ هـ، ناقص؟ )، ٧٨٥ (١٧٩ ورقة، فى القرن التاسع الهجرى؟ )، باريس ٨٣٧ - ٨٣٨ (٢٤٨ ورقة، ٢٧٣ ورقة، ١٠٨٨ هـ، انظر: فايدا ٦٢٥)، ليدن ١٧٧٥ (٤٣٧ ورقة، ٨٠٠ هـ)، انظر: فورهوف ٣٤٣)، القاهرة ثان ١/ ٤٤٠ فقه حنفى ٦٤ (١١٥٨ هـ)، ٦٥ م (١١١٧ هـ)، ٦٦ م، (فى القرن الثانى عشر الهجرى)، ٧٧٠ (قطعة، فى القرن السابع الهجرى)، ينى ٤٤٠ (٣٣٢ ورقة ٩٤٩ هـ)، ٤٤١ (٣٦٣ ورقة، فى القرن الحادى عشر الهجرى)، رئيس الكتاب ٣٣٥ (٤٦٢ ورقة، ١١٢٧ هـ)، سراى، أحمد الثالث ١١٤٨ (٤٤١ ورقة، ١١١٠ هـ)، ١١٤٩ (٣٥٤ ورقة، فى القرن الثامن الهجرى)، سراى ريفان ٦٥٠، (٣٦١ ورقة، ١١١٠ هـ، انظر فهرس ٢/ ٣٨٠ - ٣٨١)، آيا صوفية ١٢٢٣ (٣٥٣ ورقة، فى القرن الحادى عشر الهجرى)، ١٢٢٤ (٣٦٦ ورقة، ١١٤٩ هـ)، ١٢٢٥ (٣٨٨ ورقة، ١٢٥٧ هـ)، ١٢٢٦ (٤٤٤ ورقة، ١٠٩٣ هـ)، راغب ٥٠٥ (٣٤٧ ورقة، ١١٤٤ هـ)، ٥٠٦ (٣٩٩ ورقة، فى القرن الحادى عشر الهجرى)، عاطف ٨٦٦ (٣٥٨ ورقة، فى القرن الحادى عشر الهجرى)، فاتح ١٧١١ (٤٠٩ ورقة، ١١١٩ هـ) ١٧١٢، ٤٦٨ ورقة، ١١٤٦ هـ)، ١٧١٣ (٣٦٧ ورقة، ١١٢٨ هـ)، ١٧١٤ (٣٩٠ ورقة، ١١١٢ هـ)، ١٧١٥ (٢١١ ورقة، ١١١٥ هـ)، سليم أغا ٣٢٣ (٩٦) (٣٣٤ ورقة)، ٣٣٠ (٣٣٤ ورقة ١٠٨٦ هـ)، فيض الله ٧٦٤ (٣٥٠ ورقة، حوالى ١٠٠٠ هـ)، ٧٦٥ (٢٥٠ ورقة فى القرن العاشر الهجرى)، لاله لي ٩٧٥ (٢٤٩ ورقة/ فى القرن العاشر الهجرى)، مهرشاه ٣١٣ (٣٣٥ ورقة ١١٣١ هـ)، نور عثمانية ١٥٨٤ - ١٥٩٨ (١٤ نسخة، نسخت فى القرن الثانى عشر الهجرى)، أفيون جدك أحمد ٤٧ (٨٦٧ هـ)، رشيد أفندى بقيصرية ٣١٣، قونية، يوسف أغا ٤٧٢٤ (٤٦٧ ورقة، ١١٦٧ هـ)، الظاهرية فقه حنفى ١١٥ (٣٣٦ ورقة، انظر مجلة المجمع العلمى العربى بدمشق ١٢/ ٤٤٣)، (قوله ١/ ٣٦١) (٩٧)، جار الله ٤٣١، أسعد أفندى ٧٣٦ (٣٧٨ ورقة، حوالى ١١٠٠ هـ)، داماد إبراهيم ٥٤٠ (٣٦٢ ورقة، ١١٢٥ هـ)، ٥٤١ (٢٨٥ ورقة، ٨٧٢ هـ)، حميدية ٤٩٤ (٢٢٣ ورقة، فى القرن الثامن عشر الهجرى)، ٤٩٥ (٤٨٣ ورقة، فى القرن الثانى عشر الهجرى)، ٤٩٦ (٣٩٣ ورقة، فى القرن الثانى عشر الهجرى)، ٤٩٧ (٣٧٥ ورقة، ١١٧٧ هـ)، ٤٩٨ (٢٨٨ ورقة، ١١٨٧ هـ)، كوبريلى ١٠٨٨ (٣١٣ ورقة، ٥٧٨ هـ)، أيا صوفية ١١٩٩
_________
(٩٦) يصحح الرقم عند بروكلمان
(٩٧) تحذف مخطوطة فاتح رقم ٦٥٠ التى ذكرها بروكلمان، قارن شاخت ١ رقم ٧ وكذلك ٢ رقم ٦.','3','72',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (829,'(٢٢٧ ورقة فى القرن الثامن الهجرى)، ولى الدين ١١٦٦ (٣٩٦ ورقة، ١١٤٤ هـ)، مراد ملّا ٨٣٦ (٤١٦ ورقة، ١١٧٧ هـ)، ٨٣٧ (٣٥٢ ورقة، فى القرن الحادى عشر الهجرى)، مكتبة جامعة إستنبول ٣٢٨٩ (٣٤٥ ورقة، ١١٤١ هـ)، ٥٨٦٦ (٤٦٩ ورقة، فى القرن الثانى عشر الهجرى)، ٥٨٦٨ (٣٨٥ ورقة، ١١٥٠ هـ)، بايزيد ٥٣٣٥ (ج ١، ٢، ٤٤٨ ورقة، ٤٢٨ ورقة، حوالى ١٠٠٠ هـ)، أماسية ٢١٩، (٢١٩ ورقة، فى القرن الثانى عشر الهجرى)، حكيم أوغاى على ٧٧٠ (ج ١، ١٤٣ ورقة)، تشستربيتى ٣٦٥٩ (٤٦٣ ورقة، فى القرن الحادى عشر الهجرى)، منجانا ١١٠١ (٣٥٠ ورقة، ١٠٩٤ هـ، انظر: الفهرس ١٩٠)، طلعت بالقاهرة، فقه حنفى ١٠٨٩ (١١٤١ هـ)، حليم بالقاهرة، فقه حنفى ٧، عابدين بدمشق (١١٥٨ هـ انظر مجلة معهد المخطوطات العربية ٥/ ٢١٣)، الظاهرية ٥٨٥٤ (١١٣٠ هـ)، الزيتونة بتونس ٤/ ١٤٦ رقم ٢١٢١ (٢٦٠ ورقة، ١١٣٠ هـ)، الجامعة الأمريكية ببيروت ٣٤٩، ٢٩٧ (قبل ٦٣١ هـ).
وطبع بحيدرآباد ١٣٣٥ - ١٣٣٦ هـ. ونشره صلاح الدين المنجّد فى ثلاثة أجزاء بالقاهرة ١٩٥٧، ١٩٥٨، ١٩٦٠، انظر: ريتر Ritter ,Oriens ١١ (١٩٥٨) ٣٠٨ - ٣٠٩. وترجمة إلى التركية محمد منيب عين تابى (المتوفى ١٢٣٨ هـ/ ١٨٢٢ م انظر، (Osm.Muell.II ,٣٤ الظاهرية، فقه ١١٦ (٣٩١ ورقة، ١٢١٦ هـ)، أسعد أفندى ٥٦٤ (٤٣٦ ورقة) وطبعت هذه الترجمة باستنبول ١٢٤١ هـ ولهذا المترجم شرح للكتاب بالتركية بعنوان «تيسير المسير» يلدز ٤١٣، وهوامش وتقريرات كلمات مهرشاه ١١٠ (٢٩١ ورقة، فى القرن الثانى عشر الهجرى)، خسرو باشا ٣٨٢ (١٧١ ورقة بخط المؤلف).
تاسعا: «كتاب المخارج فى الحيل عن أبى حنيفة برواية أبى يوسف» يبدو أنه من تأليف أبى حنيفة ورواية أبى يوسف، شهيد على ٩٦٢ (٧٢ ورقة، ٩٣٠ هـ) القاهرة ثان ١/ ٤٥٦، فقه حنفى ٥٥٠، انظر: شاخت ١/ ٣ أ، ملّا چلبى ٥٧/ ٤ (من ١٦٨ أ- ١٩٠ ب، ٨٦٢ هـ)، ونشره شاخت على أساس المخطوطتين الأوليين بليپتسج ١٩٣٠ م، انظر:
Probster, in Islamica ٥/ ٥٨ ff. Schacht, in Islamica ٦/ ٢٦٠ - ٢٦٦.
عاشرا: «عقيدة»:
وتوجد فى ٧٩ بيتا من الشعر. ونسبتها إلى الشيبانى موضع نظر، برلين ١٩٣٣ (من ٢٩ ب- ٩٤ ب)، انظر أيضا برلين ١٩٣٤، جوتا ٦٥٩ (٤ ورقات، حديث)، ٦٦٠ (ثلاث ورقات، حديث)، باريس ٤٥٨٥ (من ١٣ ب- ١٥ ب، فى القرن الحادى عشر الهجرى، انظر: فايدا ٢٦٤)، بودليانا ١/ ٨١٧/ ٣، ترست ٣٩٧٢ (٩٨٢ هـ) كذلك ١٢٧٣، مرش ٥٧٦، انظر: ٢/ ٥٩٦ ب (بطرسبرج شانيكوف ١٦١/ ٢،','3','73',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (830,'الإمبروزيانا هـ ٤٣٧)، الفاتيكان، فايدا ٤١٦ (الأوراق ٩٥ - ٩٧، ١١١٨ هـ)، وهبى ٢٠٠٠/ ١٢ (من ١٠٣ ب- ١٠٥ أ، برتو ٦٢٦/ ٨، (من ٣٤٠ ب- ٣٤٢ ب، ١١١٨ هـ)، فاتح ٣٠٠١/ ٢ (الأوراق ٣ - ١٢، ١١٦٠ هـ)، ٣٠٩٥/ ١ (من ١ - ٤ أ، فى القرن التاسع الهجرى)، تشستربيتى ٤٧٦٣/ ٢ (الأوراق ٩٦ - ٩٩، ٨٨٩ هـ)، منجانا ١٩١ رقم ٨٢١/ ٢ (أربع أوراق من القرن الحادى عشر الهجرى)، ليبتسج ٨٩١/ ١٣، ليدن ٢١٨١ (الأوراق ٩٣ - ٩٥، ١٠٩٤ هـ، انظر: فورهوف ١٨)، باريس ٣٠١٩ (الأوراق ١٠٣ - ١١٣) ٣٤٣٠ (الأوراق ٥ - ٧)، ٥٤٣٢ (الأوراق ٣٢٠ - ٣٢٤)، ٥٥١٩ (الأوراق ١٥١ - ١٥٤)، ٥٦٠٣ (الأوراق ١٤٦ - ١٤٨)، ٥٦٤٧ (الأوراق ٩٤ - ٩٧)، ٥٦٨٨ (الأوراق ٨٤ - ٨٦)، ٥٦٩٠ (الأوراق ٣٢ - ٣٥)، ٥٧٠٠ (الأوراق ٣٣ - ٣٦، انظر فايدا، ٢٦٤)، الأزهر ٣/ ٣٢٣، ٣٢٤ (ثمانى مخطوطات)، رامبور ١/ ٣١٥ كلام ٢٥١ ب.
وطبع بالقاهرة ١٢٧٦، ١٢٨١، ١٢٩٦، ١٢٩٧، ١٣٠٣، ١٣٠٤ هـ، ١٣١٨، ١٣٢٣ هـ انظر: القاهرة ثان ١/ ٢٠٢).
شروح كتاب «عقيدة»: «بديع المعانى فى شرح عقيدة» لمحمد بن عبد الله بن عبد الرحمن بن محمد الشافعى قاضى عجلون (المتوفى ٨٧٦ هـ/ ١٤٧٣ م. انظر معجم المؤلفين لكحالة ١٠/ ٢٢٣)، جوتا ٩٩/ ١٣/ (٩٨) (الأوراق ٩٨ - ١٠٥ - ٩٧٠ هـ)، ٦٦١ (٣٤ ورقة) (٩٩)، باريس ٣٢٠٤ (الأوراق ٧١ - ٩٢، ١١٧١ هـ)، انظر: فايدا ٢٦٤)، جاريت ١٥٦٣ (٣٥ ورقة فى القرن الحادى عشر الهجرى)، ٢٠٠٣/ ٨ (١١٠٣ هـ)، المتحف البريطانى، الملحق ١٦٧ مخطوطات شرقية ٤٢٦٤ (٣٢ ورقة، فى القرن الثانى عشر الهجرى)، ١٢٥٣، مخطوطات شرقية ٤٣٧٥/ ٣ (الأوراق ١١٨ - ١٤٣، فى القرن الحادى عشر أو الثانى عشر الهجرى)، (الموصل ١٠٨، ١١٨) (١٠٠)، فاتح ٣٠٩٥/ ٢ (الأوراق ٥ - ٥٢، ٨٩٥ هـ)، شهيد على ٦٣٧ (الأوراق ١٩٣ - ٢٤٠، ٩١٢ هـ)، رشيد باستنبول ٣١٨/ ١ (الأوراق ١ - ٢٩، ٩٢٩ هـ).
٢) «بيان المعانى فى عقيدة الشيبانى» لعلوان على بن عطية بن حسن الحموى (المتوفى ٩٣٦ هـ/ ١٥٢٩ م انظر بروكلمان ٢/ ٣٣٣)، برلين ١٩٣٥ (٩١ ورقة، حوالى ١١٥٠ هـ)، الظاهرية ٦٢، توحيد ٤٦.
_________
(٩٨) ليس رقم ١٢٩٩ كما ذكر ذلك بروكلمان.
(٩٩) تحذف مخطوطة ليبزج ٨٩١/ ٢١ التى ذكرها بروكلمان.
(١٠٠) تحذف مخطوطة رامبور ١/ ٣١٥، ٢٥٢ التى ذكرها بروكلمان.','3','74',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (831,'حادى عشر: «كتاب الكسب»:
يوجد مع شرح عليه للسرخسى، المدينة (انظر: شبيس (Spies ,in ZDMG ٩٠ /١١٥ ونشره محمد عرنوس بالقاهرة ١٩٣٨ م. ومنه مختصر لأبى عبد الله محمد بن سماعة بن عبد الله التميمى (عاش من ١٣٠ هـ/ ٧٤٧ م- ٢٣٣ هـ/ ٨٤٧ م، انظر: رقم ١١ فى هذا الفصل).
ثانى عشر: «الحجة فى اختلاف أهل الكوفة وأهل المدينة» وهو أقدم كتاب وصل إلينا فى مسائل الخلاف الفقهى، نور عثمانية ١٤٩٢، (٢٠١ ورقة، فى القرن الحادى عشر الهجرى، انظر: شاخت ١ رقم ٦، وفهرس معهد المخطوطات العربية ١/ ٣٣٠)، (باتنه ١/ ٦٨، ٨٦٩)، آصفية ٢/ ١١٠٠ فقه حنفى ٢٧٨، سراى، مدينة ٣٥٢ (بعنوان: «الحجج المبينة على أهل المدينة» (١٧٨ ورقة، ١١٤٢ هـ)، انظر: فهرس معهد المخطوطات العربية ١/ ٢٥٩)، وطبع فى لكنو ١٨٨٨.
ثالث عشر: «جزء من الأمالى» يوجد برواية تلميذه سليمان بن شعيب الكيسانى (المتوفى ٢٧٨ هـ ٨٩١ م انظر: الجواهر للقرشى ١/ ٢٥٢)، آصفية ٢/ ١٠٧٢، فقه حنفى ١٤٦، وطبع فى حيدرآباد ١٣٦٠ هـ.
رابع عشر: «بعض الفتاوى» نسبت إليه، وهى مع الفتاوى التى يقال إن أبا حنيفة ألّفها، باريس ٤٥٤٤/ ٢، (١٩ ورقة، فى القرن العاشر الهجرى).
خامس عشر:
روايته لموطأ مالك (انظر باب: علم الحديث).
سادس عشر: «الصلاة» آصفية ٢/ ١١٠٠، فقه حنفى ١٤٣ - يوزغات ٣٣٥ (١٠٢ ورقة، فى القرن التاسع الهجرى، انظر:
أحمد آتش Ank.Isl.Ilm.Enst.,I ,٤٨.','3','75',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (832,'سابع عشر: «مسائل فى العبادات والمعاملات» القاهرة، ملحق ٣/ ٥٤ رقم ٢٠٣٣١ ب (١٧٠ ورقة، ١٢٤١ هـ). ونسبة هذا الكتاب إلى الشيبانى غير مؤكدة.
ثامن عشر: «كتاب الاستحسان» أفاد منه السرخسى فى كتابه «الأصول» ١/ ٣٢٨، ٣٣٢، ٢/ ٢٢، ٢٤.
تاسع عشر: «كتاب أدب القاضى» أفاد منه السرخسى فى الأصول ٢/ ١١٣، ١٣٣.
عشرون: «كتاب الإقرار» أفاد منه السرخسى فى الأصول ١/ ٢٧٢، ٢/ ٣٥١.
حادى وعشرون: «كتاب الإكراه» أفاد منه السرخسى فى الأصول ١/ ١٢١، ١٨٦ ٢/ ٢٠ - ٢١.
ثانى وعشرون: «كتاب التحرى» أفاد منه السرخسى فى الأصول ١/ ٦٣.
ثالث وعشرون: «كتاب البيوع» أفاد منه السرخسى فى الأصول ٢/ ٢٠٥.
رابع وعشرون: «كتاب الدعوة» استخدمه السرخسى فى الأصول ١/ ١٨٥، ١٨٦','3','76',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (833,'خامس وعشرون «كتاب الحدود» استخدمه السرخسى فى كتابه الأصول ٢/ ٢٠١.
سادس وعشرون: «كتاب السرقة» استخدمه السرخسى فى كتابه «الأصول ٢/ ٢٠١ سابع وعشرون: «كتاب الشرب» أفاد منه السرخسى فى كتابه الأصول ٢/ ١٠٠ ثامن وعشرون: «كتاب الشركة» أفاد منه السرخسى فى كتابه «الأصول» ٢/ ٤٨.
تاسع وعشرون: «كتاب الشهادة» أفاد منه السرخسى فى كتابه «الأصول» ١/ ٣٥١ ثلاثون: «كتاب الصوم» أفاد منه السرخسى فى كتابه «الأصول» ١/ ٢٦ حادى وثلاثون: «كتاب الطلاق» استخدمه السرخسى فى كتابه «الأصول» ١/ ٨٥، ٢/ ١٨٨، ٢٠٢/ ثانى وثلاثون: «كتاب العارية» استخدمه السرخسى فى كتابه الأصول ٢/ ٣٠٨.
ثالث وثلاثون: «كتاب المضاربة»','3','77',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (834,'ذكره السرخسى فى كتابه «الأصول» ١/ ١٣٣، ٢٠٦ رابع وثلاثون: «كتاب الوصايا» ذكره السرخسى فى كتابه «الأصول» ١/ ١٣٣.

<span data-type="title" id=toc-1979>٥ - هشام بن عبيد الله الرّازى</span>
هو هشام بن عبيد الله الرازى، كان تلميذا لأبى يوسف والشيبانى، توفى سنة ٢٠١ هـ/ ٨١٧.

<span data-type="title" id=toc-1980>أ- مصادر ترجمته:</span>
الجواهر للقرشى ٢/ ٢٠٥، ميزان الاعتدال للذهبى ٣/ ٢٥٤، لسان الميزان لابن حجر ٤/ ١٩٥، التهذيب لابن حجر ١١/ ٤٧ - ٤٨، حاجى خليفة ١٩٨١، هدية العارفين ٢/ ٥٠٨، الأعلام للزركلى ٩/ ٨٥، معجم المؤلفين لكحالة ١٣/ ١٤٩.

<span data-type="title" id=toc-1981>ب- آثاره:</span>
«كتاب النوادر فى الفروع» كان مصدرا للسرخسى فى كتابه «المبسوط»، وعلى سبيل المثال فى ٢/ ١٦٣، ١٩٣، وفى ٣/ ١٦، ٦٧، ٧٠.

<span data-type="title" id=toc-1982>٦ - اللّؤلؤى</span>
هو أبو على، الحسن بن زياد اللؤلؤى أصله من الكوفة، ولد نحو سنة ١١٦ هـ/ ٧٣٤ م. أقام مدة طويلة ببغداد، وكان أحد تلاميذ أبى حنيفة ذوى المكانة، كما كان من المتحمسين فى الدفاع عن مذهب شيخه فى «الرأى». ولا يعدّ من الرواة الثقات ويقال إنه لم يكن موفقا عند ما كان قاضيا بالكوفة، توفى سنة ٢٠٤ هـ/ ٨١٩ م.','3','78',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (835,'<span data-type="title" id=toc-1983><span data-type="title" id=toc-1986>أ- مصادر ترجمته:</span></span>
أخبار القضاة لوكيع ٣/ ١٨٨ - ١٨٩، الفهرست لابن النديم ٢٠٤، تاريخ بغداد للخطيب ٧/ ٣١٤ - ٣١٧، ميزان الاعتدال للذهبى ١/ ٢٢٨، تاج التراجم لابن قطلوبغا ١٦، حاجى خليفة ١٤١٥، ١٤٧٠، ١٥٧٤، الأعلام للزركلى ٢/ ٢٠٥، معجم المؤلفين لكحالة ٣/ ٢٢٧.

<span data-type="title" id=toc-1984>ب- آثاره:</span>
«مسند أبى حنيفة»، الأوقاف بغداد ١٠٠٠٥ (انظر: طلس ٤٨).
وذكر ابن النديم له الكتب الآتية:
«كتاب أدب القاضى»، «كتاب الخصال»، و «كتاب معانى الإيمان»، كتاب النفقات»، «كتاب الخراج»، «كتاب الفرائض»، «كتاب الوصايا». ومن المرجحأن الطبرى قد استخدم «كتاب الخراج» فى كتابه «الاختلاف» الذى أخرجه شاخت: ٧ - ٨، ٣٣، ٤١، ٤٢، ٦٣، ٨١، ٨٣، ٨٥، ١٥٧، ١٦١.

<span data-type="title" id=toc-1985>٧ - الجوزجانى</span>
هو أبو سليمان، موسى بن سليمان، الجوزجانى من منطقة تتبع بلخ، شبّ فى بغداد، وكان تلميذا لأبى يوسف ومحمد بن الحسن الشيبانى. عرض عليه المأمون القضاء فاعتذر، ثم عرضه على صديقه الشاب المعلى بن منصور (المتوفى ٢١١ هـ/ ٨٢٦ م) فاعتذر أيضا، والراجح أنه توفى بعد سنة ٢٠٠ هـ/ ٨١٥ م (١٠١) بقليل.
أ- مصادر ترجمته:
تاريخ بغداد للخطيب ١٣/ ٣٦ - ٣٧، الجواهر للقرشى ٢/ ١٨٦، الأعلام للزركلى ٨/ ٢٧٢، معجم المؤلفين لكحالة ١٣/ ٣٩، بروكلمان ١/ ١٧٣ رقم ٤.
_________
(١٠١) ذكر بروكلمان، الملحق ١/ ٢٩٢ أنه توفى سنة ٢٨٠ هـ/ ٨٩٣ م والصحيح ما ذكرنا','3','79',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (836,'<span data-type="title" id=toc-1987><span data-type="title" id=toc-1990>ب- آثاره:</span></span>
«النوادر؟ » القاهرة ثان ١/ ٤٥٦ (قسمان)، فقه ٣٨٢، ٤٩١ (٧٣٧ هـ)، واستخدمه السّرخسى فى كتابه «الأصول» ١/ ٣٤، ومن المحتمل أن الطبرى استخدمه أيضا فى كتابه «الاختلاف»: ٨٢، ١٣١، ١٣٣، ١٤٠، ٢٤٦.

<span data-type="title" id=toc-1988>٨ - المعلّى</span>
هو أبو يعلى، المعلّى بن منصور، الرازى، عاش ببغداد، وكان تلميذا لأبى يوسف، ومحمد بن الحسن الشيبانى. روى عن مالك بن أنس، والليث بن سعد، وعبد الله ابن لهيعة، وغيرهم. وروى عنه على بن المدينى، وأبو بكر بن أبى شيبة وغيرهما.
كان من أصحاب «الرأى» كما كان ثقة فى رواية الحديث وكان ورعا طلبوه للقضاء (غير مرة) فأبى. وتوفى سنة ٢١١ هـ/ ٨٢٦ م ببغداد.

<span data-type="title" id=toc-1989>أ- مصادر ترجمته:</span>
التاريخ الكبير للبخارى ٤/ ١/ ٣٩٥، تاريخ بغداد للخطيب ٨/ ١٨٨ - ١٩٠، ميزان الاعتدال للذهبى ٣/ ١٨٦، تذكرة الحفاظ للذهبى ٣٧٧، التهذيب لابن حجر ١٠/ ٢٣٨، الجواهر للقرشى ٢/ ١٨٨، تاج التراجم لابن قطلوبغا ٥٤، هدية العارفين ٢/ ٤٦٦، الأعلام للزركلى ٨/ ١٩٩، معجم المؤلفين لكحالة ١٢/ ٣٠٩.
ب- آثاره:
«كتاب النوادر (فى الفقه)» مكتبة جامعة إستنبول، ٤٣٥٢ (١٢٩ ورقة، فى القرن الثامن الهجرى).

<span data-type="title" id=toc-1991>٩ - عيسى بن أبان</span>
هو عيسى بن أبان بن صدقة، كان تلميذا لمحمد بن الحسن الشيبانى. ولى','3','80',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (837,'القضاء بالبصرة فى الأحد عشر عاما الأخيرة من حياته. وتوفى سنة ٢٢١ هـ/ ٨٣٦ م.

<span data-type="title" id=toc-1992>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠٥، تاريخ بغداد للخطيب ١١/ ١٥٧ - ١٦٠، للقرشى ١/ ٤٠١ - ٤٠٢، هدية العارفين ١/ ٨٠٦، الأعلام للزركلى ٥/ ٢٨٣، معجم المؤلفين لكحالة ٨/ ١٨، بروكلمان ملحق ١/ ٩٥٠.

<span data-type="title" id=toc-1993><span data-type="title" id=toc-1995>ب- آثاره:</span></span>
«الحجة الصغيرة» بنگيبور ١٩/ ١/ ٦٩ رقم ١٥٩٦ (١٦٨ ورقة، ١١٦٦). وذكر له ابن النديم خمسة كتب أخرى. وتوجد اقتباسات منها فى «كتاب الأصول» للسّرخسى ١/ ٢٥، ٢٩٢، ٢٩٣، ٢٩٤، ٣٠٤، ٣٢٨، ٣٦١، ٣٦٣، ٢/ ٧، ٢١.

<span data-type="title" id=toc-1994>١٠ - الحنفى</span>
هو يحيى بن (أبى) بكر (بكير) الحنفى، ذكره ابن النديم (الفهرست ٢٠٨) بين الحنفية، ومع ذلك فلم يذكره كل من ابن النديم والقرشى (١٠٢) وابن قطلوبغا (١٠٣) بنسبته «الحنفى». هو من أهل العراق، انظر: الفهرست لابن النديم. وقد يكون هو يحيى بن أبى بكير النخعى الذى ولد بالكوفة، وتوفى بمصر، سنة ٢٣٠ هـ/ ٨٤٤ م (١٠٤). (انظر بروكلمان ملحق ١/ ٢٩٢).
ب- آثاره:
«مختصر فى (بيان) الاعتقاد» «أسير الملاحدة فى الألفاظ المكفرة»، ويقال إن المؤلف كتبه أولا باللغة الفارسية، ثم نقله بعد ذلك إلى اللغة العربية.
_________
(١٠٢) الجواهر ٢/ ٢١١.
(١٠٣) تاج التراجم: ٨٣.
(١٠٤) انظر: التهذيب لابن حجر ١١/ ١٩٠.','3','81',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (838,'المخطوطات: برلين ١٠٣١٠ (الأوراق ٥ - ١٩)، باريس ١٢٨٧ (الأوراق ١ - ١٤، فى القرن الثانى عشر الهجرى)، ١٣٩١ (الأوراق ١٢٦ - ١٤٥، فى القرن الحادى عشر الهجرى، انظر: فايدا ٢٨٩)، بلدية الإسكندرية ٢١٧٩ د، (١١٠٨ هـ)، ٥٢٢٧/ ١٠ ج (١٦ ورقة، ١١١٨ هـ، انظر فهرس معهد المخطوطات العربية ١/ ١١٥)، (درسدن ٩٣، ٥، ٢١٦ رقم ١، قوقاز، المتحف الأسيوى بطرسبورج ٩٢٥)، أسعد أفندى ٣٧٨٠ (من ٢٢٢ ب- ٢٣٨ أ، ١٠٨٧ هـ)، القاهرة، ملحق ١/ ٣٦٧ رقم ١٩٩٤٠ ب، (الأوراق ١ - ١٩) (١٠٥).

<span data-type="title" id=toc-1996>١١ - محمد بن سماعة</span>
هو أبو عبد الله، محمد بن سماعة بن عبيد (أو عبد) الله، الكوفى، ولد حوالى سنة ١٣٠ هـ/ ٧٤٧ م. روى أبى يوسف ومحمد بن الحسن الشيبانى والليث بن سعد ويعلى بن خالد الرازى وغيرهم. ولى القضاء ببغداد بعد موت أبى يوسف. ولكن المأمون عزله بعد ذلك. توفى سنة ٢٣٣ هـ/ ٨٤٧ م.

<span data-type="title" id=toc-1997>أ- مصادر ترجمته:</span>
مروج الذهب للمسعودى ٧/ ٢١٠ - ٢١١، الفهرست لابن النديم ٢٠٥، تاريخ بغداد للخطيب ٥/ ٣٤١ - ٣٤٣، طبقات الفقهاء للشيرازى ١١٦، الجواهر للقرشى ٢/ ٥٨ - ٥٩، الوافى بالوفيات للصفدى ٣/ ١٣٩ - ١٤٠، الأعلام للزركلى ٧/ ٢٣ - ٢٤، معجم المؤلفين لكحالة ١٠/ ٥٧.

<span data-type="title" id=toc-1998>ب- آثاره:</span>
١ - «الاكتساب فى الرزق المستطاب» على أساس «كتاب الكسب» لمحمد بن الحسن الشيبانى، القاهرة أول ٦/ ١٩٤، علوم المعاش ١٤ (٤٥ ورقة، فى القرن التاسع الهجرى).
٢ - «النوادر»، استخدمه السّرخسى فى كتابه «المبسوط».
_________
(١٠٥) كل المخطوطات الأخرى عند بروكلمان هى لأرجوزة فى الميراث. ويبدو أنها لمؤلف آخر حديث متحد معه فى الاسم.','3','82',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (839,'<span data-type="title" id=toc-1999>١٢ - هلال الرأى</span>
هو هلال بن يحيى بن مسلم البصرى، لقب بهلال الرأى، لسعة علمه وكثرة أخذه بالقياس. كان تلميذا لأبى يوسف وزفر. وتوفى بالبصرة سنة ٢٤٥ هـ/ ٨٥٩ م.

<span data-type="title" id=toc-2000>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠٥، الجواهر للقرشى ٢/ ٢٠٧، تاج التراجم لابن قطلوبغا ٥٩، الأعلام للزركلى ٩/ ٩٥، معجم المؤلفين لكحالة ١٣/ ١٥٢، بروكلمان ١/ ١٧٣ رقم ٥.

<span data-type="title" id=toc-2001>ب- آثاره:</span>
١ - «كتاب أحكام الوقف (الأوقاف)» المخطوطات: داماد إبراهيم ٦٦٩ (الأوراق ١ - ١٢٦ ٧٣٠ هـ)، السليمانية ٣٧٩ (١٧٦ ورقة، ٩٧٢ هـ)، ولى الدين ١٥٨٦ (١٠٦) (٢١٩ ورقة، ٨٦٦ هـ)، ١٥٨٧ (١٠٧)، (١٤٤ ورقة، ١٠٠٧ هـ)، جار الله ٥٧١: (٢٢٦ ورقة، ٨٨٦ هـ)، مراد ملّا ٧٣٣ (٢٣٩ ورقة، ٩٧٢ هـ)، رشيد باستنبول ٢٨٨/ ٢ (الأوراق ٤١ - ١٣٢، ٩٨٥ هـ)، قره چلبى ٩٢/ ١ (الأوراق ١ - ١٧٣، ٩٨٧ هـ)، راغب ٤٤١/ ٢ (من ٦٨ ب- ٢١٩ ب، ٩٩٨ هـ)، قره مصطفى ١٥٦/ ١ (من ١ - ٨٤ أ، ٩٥٨ هـ)، لاله لي ٨٠١ (١٦٨ ورقة، فى القرن التاسع الهجرى)، رضا باشا بمكتبة جامعة إستنبول حاليّا ٦٣٠ (من ١٩٣ أ- ٢٤١ ب، فى القرن الحادى عشر الهجرى)، وهبى ٤٦٧/ ٢ (الأوراق ١٣٢ ب- ٢٨٦ ب، ٨٩٤ هـ)، حراچى زاده فى بورسة ٦٢٦ (فى القرن التاسع الهجرى)، القاهرة ثان ١/ ٣٩٩، فقه حنفى ٢٠/ ٢، ١٠٦٠ (٨٨ ورقة، ٨٤٦ هـ)، ١١٢ م (حوالى ٨٠ ورقة ١١٠٥ هـ)، انظر: شاخت، Schacht ,Bibl.I ,No ١٧ آصفية فقه ١٩٨ (١٦٠ ورقة، ٩٥٩ هـ)، ليدن ١٧٧٦ (١٣٣ ورقة، ١٠٠١ هـ، انظر: فورهوف ٧، بلدية الإسكندرية ٣٦٧٢ ج (١٨٨ ورقة، ٨٨٩ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٥٤)، ٤٤٢٦ ج (١٢٧٨ هـ)، رامبور ١/ ٢٤٠، فقه ٤٧٧، (١٧١ ورقة)، المكتب الهندى مخطوطات عربية ٤١١٤ (١٦٦، ٧٠٥ هـ)، فاتح ١٤٦٩ (٨٠ ورقة، ٩٥١ هـ)، ينى ٣٥١/ ١ (الأوراق ١ - ٦٩)، ١١٩٠/ ٢ (الأوراق ٩٧ ١٩٣)، حفيد أفندى ٤٦، قسطمونى ٣٣٨٧ (٩١ ورقة، ٩٣١ هـ)، تشستربيتى ٤٧٣٢ (١٣٦ ورقة،
_________
(١٠٦) ليس رقم ١٦٨٦ كما ذكر بروكلمان.
(١٠٧) ليس رقم ١٦٨٧ كما ذكر بروكلمان.','3','83',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (840,'٨٩٣ هـ)، قونيه ٥٠٤٥ (٨١ ورقة، فى القرن الحادى عشر الهجرى)، حليم بالقاهرة، فقه حنفى ١٤، الأزهر ٢/ ٢٩٩ - ٣٠٠ (أربع نسخ)، وطبع بحيدرآباد ١٣٥٥ هـ.
وهناك مختارات منه بعنوان: «المنتخب من وقفى هلال والخصّاف» لأبى المحاسن محمود بن أحمد بن مسعود القونوى (المتوفى ٧٧١ هـ/ ١٣٦٩ م انظر بروكلمان ٢/ ٨١)، شهيد على ٢٧٦٢/ ١ (١٠٨) (من ١ - ٥١ ب، ٧٥٥ هـ)، ولى الدين ١٣٤٤/ ٧ (من ٤٢ أ- ٦٩ ب، ٨٥٧ هـ)، مكتبة جامعة إستنبول ١٢٨٦ (٢٩ ورقة، ١٠٤٢ هـ)، برلين الغربية ٢٠٧٣/ ٣ (من ٩٢ أ- ١٠١ ب، ٨٨٣ هـ)، تيمور، فقه ٧٠٦ (الأوراق ١ - ٣٤، ٨٧٩ هـ)، منجانا ٢٥١، رقم ١٠٢٧/ ٣ (٣٠ ورقة، ٧٥٥ هـ).
وله أيضا مختصر بعنوان: «مختصر (أو جمع) وقفى هلال والخصّاف «لأبى محمد بن عبد الله بن الحسين الناصحى (المتوفى ٤٤٧ هـ/ ١٠٥٥ م، انظر بروكلمان ١/ ٣٧٣).
وله مختصر آخر بعنوان/: «الإسعاف فى أحكام الأوقاف» لعلى بن أمير الله قنال زاده (المتوفى ٩٧٩ هـ/ ١٥٧١ م انظر بروكلمان ملحق ٢/ ٦٤٤)، قونيه يوسف أفندى ٥٠٤٥ (٨١ ورقة فى القرن الحادى عشر الهجرى).

<span data-type="title" id=toc-2002>١٣ - محمد بن مقاتل الرّازى</span>
هو محمد بن مقاتل الرازى، كان تلميذا لمحمد بن الحسن الشّيبانى. روى عن وكيع بن الجرّاح وغيره. وحضر عليه وسمع منه البخارى وغيره. وكان فقيها ذا مكانة.
توفى سنة ٢٤٨ هـ/ ٨٦٢ م.

<span data-type="title" id=toc-2003>أ- مصادر ترجمته:</span>
ميزان الاعتدال للذهبى ٣/ ١٣٩، التهذيب لابن حجر ٩/ ٤٦٩ - ٤٧٠، لسان الميزان لابن حجر ٥/ ٣٨٨، الجواهر للقرشى ٢/ ١٣٤.
_________
(١٠٨) يصحح الرقم عند بروكلمان.','3','84',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (841,'<span data-type="title" id=toc-2004><span data-type="title" id=toc-2007>ب- آثاره:</span></span>
«المحاورة بين الشيخين عبد الله بن بابويه القمّى الشيعى (؟ ) ومحمد بن مقاتل فى شأن التفضيل بين قريش وبنى هاشم»، القاهرة، ملحق ٣/ ٢١ رقم ١٩٨٨٢ ب الأوراق ١٤ - ٢٨).
وجمع أبو الليث السمرقندى (المتوفى ٣٧٣ هـ/ ٩٨٣ م) آراء ابن مقاتل الرازى فى كتابه «النوازل» (انظر: رقم ٢٤ كتاب رقم ٦ من هذا الفصل).

<span data-type="title" id=toc-2005>١٤ - ابن الثّلجى</span>
هو أبو عبد الله، محمد بن شجاع يعرف بابن الثلجى، كان تلميذا للحسن بن زياد، كما كان ورعا وحنفيا مرموق المكانة. عاش بالعراق، وكان به ميل إلى المعتزلة.
توفى سنة ٢٥٧ هـ/ ٨٧٠ م، وقيل ٢٦٠ هـ أو ٢٦٦ هـ.

<span data-type="title" id=toc-2006>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠٦ - ٢٠٧، تاريخ بغداد للخطيب ٥/ ٣٥٠ - ٣٥٢ طبقات الفقهاء للشيرازى ١١٨، ميزان الاعتدال ٣/ ٧١ - ٧٢، الجواهر للقرشى ٢/ ٦٠ - ٦١، الوافى بالوفيات للصفدى ٣/ ١٤٨، التهذيب لابن حجر ٩/ ٢٢٠ - ٢٢١، الأعلام للزركلى ٧/ ٢٨، معجم المؤلفين لكحالة ١٠/ ٦٤.
ب- آثاره:
تنسب له الكتب الآتية:
«كتاب المناسك»، «كتاب تصحيح الآثار»، «كتاب المضاربة»، «كتاب الرد على المشبّهة». ويوجد من هذه الكتب كثير من الاقتباسات فى الكتب المتأخرة للحنفية، وبخاصة فى كتاب «النوازل فى الفروع»، «وعيون المسائل» لأبى الليث السمرقندى (المتوفى ٣٧٣ هـ/ ٩٨٣ م، انظر رقم ٢٤ من هذا الفصل).

<span data-type="title" id=toc-2008>١٥ - الخصّاف</span>
هو أبو بكر أحمد بن عمر (عمرو) الشّيبانى الخصّاف. حدّث عن أبى عاصم','3','85',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (842,'النبيل وأبى داود الطيالسى وغيرهما. وكان مقدّما عند الخليفة المهتدى بالله، ولمّا قتل المهتدى نهب فذهب بعض كتبه. توفى ببغداد سنة ٢٦١ هـ/ ٨٧٤ م.

<span data-type="title" id=toc-2009>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠٦، طبقات الفقهاء للشيرازى ١١٨، طبقات الشافعية لابن هداية ٢٤، الجواهر للقرشى ١/ ٨٧ - ٨٨، تاج التراجم لابن قطلوبغا ٧، الأعلام للزركلى ١/ ١٧٨، معجم المؤلفين لكحالة ١١/ ٣٥، وانظر بروكلمان ١/ ١٧٣ رقم ٦، وانظر فلوجل Flugel ,Hanef.٢٩١.:

<span data-type="title" id=toc-2010>ب- آثاره:</span>
١ - «كتاب أحكام الوقف أو (الأوقاف)» برلين ٤٧٦١ (الأوراق ١٨٠ - ٢٧٤، ٩٧٨ هـ) (برلين الشرقية ١٨٠٣)، جوتا ١١٢٧ (٢١٣ ورقة، ١٠١١ هـ)، ليدن ١٧٧٨/ ٢ (١٣٣ ورقة، ٧٤٧ هـ)، (قوقاز، المتحف الأسيوى ببطرسبورج ٩٢١)، آيا صوفية ١٠١٢ (١٦٩ ورقة، فى القرن العاشر الهجرى)، نور/ عثمانية ١٨٩١ (١٩١ ورقة، ٩٩٢ هـ) ينى ٣٤٩/ ١ (الأوراق- ١ - ١٤١، ٨٨٠ هـ)، ٣٥٠ (١٢٥ ورقة، ٨٩٤ هـ)، قاضى زاده ١١٧ (٢٣٠ ورقة، ٨٧٧ هـ)، قره چلبى ٩٢/ ٢ (من ١٧٤ أ- ٣٩٦ أ ٩٣٦ هـ)، وهبى ٥٤٣ (١٣٢ ورقة، ٩٧٤ هـ)، سراى، أحمد الثالث ١١٩٤ (٢٤٧ ورقة، ٨٣٧ هـ، انظر فهرس معهدالمخطوطات العربية ١/ ٢٥٤)، قره مصطفى ١٥٦/ ٢ (٨٧ أ- ١٩٧ أ، ٩٥٨ هـ)، بايزيد ٢١٣١ (١١٩ ورقة، ٨٩٤ هـ)، رضا بمكتبة جامعة استنبول حاليّا ٦٣٠/ ١ (من ١ - ٧٢ أ، فى القرن الحادى عشر الهجرى)، ملّا چلبى ٧٥/ ١ (من ١ - ٩٥ أ، ٨٦٢ هـ)، شهيد على ١٠٠٨ (مختصر)، جار الله ٩١٤، (١٦٧ ورقة، ١٠٦١ هـ)، الزيتونة بتونس ٤/ ٤٧ رقم ١٨٤٦ (٢٣٥ ورقة)، القاهرة ثان ١/ ٣٩٩، فقه حنفى ١٩ (١٢٥ ورقة، ٨٥٢ هـ)، لاله لي ٧٩٩ (١٥٣ ورقة، ٦٥٩ هـ)، عاطف ٧٢٩ (٧٧٩ هـ)، ينى ٣٤٩/ ١ (الأوراق ١ - ١٤١، ٨٨٠ هـ)، ٣٥٠ (٨٥١ هـ)، ولى الدين ١٠٢٠ (١٤٢ ورقة، ٩٠٢ هـ)، ١٥٤٠/ ٢ (٢٠٢ ورقة، ١٠١٨ هـ)، رئيس الكتاب ٢٩٥ (١١١ ورقة، ٩١١ هـ)، ٢٩٦ (١٠٨ ورقة)، عموجه حسين ١٦٦ (١٤٥ ورقة، ٩١٨ هـ)، مراد ملّا ٧٣٢ (٢٠٧ ورقة، ٩٧٥ هـ)، أيا صوفية ١٠١٢/ ١ (الأوراق ١ - ١١٧، ٩٨٤ هـ)، نور عثمانية ١٨٩١، (١٩١ ورقة، ٩٩٢ هـ)، برتو ١٥٥ (١٢٩ ورقة، ١٠١٥ هـ)، داماد إبراهيم ٤٨٧ (١٩٣ ورقة)، ٤٩٦/ ٤ (مخطوطة حديثة)، أسعد أفندى ١٠٤٣ (١٠٠ ورقة)، ١٠٤٤ (٢٠٠ ورقة)، فيض الله ٦٥٤ (٢٠٧ ورقة)، ٦٥٥ (١٣٢ ورقة)، سراى، أحمد الثالث ١٠٩٦ (١٧٤ ورقة، ٨٧٢ هـ) يوزغات ٣٢٦، سيرز ٧٧٦ (٨٨٨ هـ). حراچى زاده فى بورسه ٤٤٤ (١٧٤ ورقة، فى القرن الثامن الهجرى)، أماسية','3','86',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (843,'٢٢٠ (٩٩ ورقة، ١١٩٠ هـ)، تشستربيتى ٤٣٧٠ (٤١ ورقة، فى القرن السادس الهجرى)، حسن حسنى عبد الوهاب بتونس (١٨٥ ورقة، انظر: فهرس معهد المخطوطات العربية ١/ ٢٥٤)، القاهرة، ملحق ١/ ٢٤ رقم ٢٠٤٩٩ ب (الأوراق ١ - ٢٤٧، ١٠٥٧ هـ)، انظر Schacht ,Bibl.I ,١٩ ,III ,١٤: انظر أيضا:
كوركيس عواد فى مجلة: سومر ٧/ ٢٧٦، وطبع بالقاهرة ١٣٢٢ هـ. ومنه مختارات توجد مع مختارات أخرى من كتاب بنفس الاسم ألّفه هلال الرأى بعنوان: «المنتخب من وقفى هلال والخصاف» لمحمود بن أحمد القونوى (انظر هلال الرأى رقم ١٢).
٢ - «كتاب أدب القاضى» فاتح ٢٢٦٩ (١٢٠ ورقة، ٨٠٤ هـ)، ملّا چلبى ٥٧/ ٢ (من ٩٦ أ- ١٢١ أ، ٨٦٢ هـ)، انظر:
شاخت ١/ ١٨، (قوله ١/ ٣٠٤، برلين الغربية ١٨٥٧)، رامبور ١/ ١٦٢ ورقة، فقه ٥ (١١٦٥ هـ)، فيض الله ٦٦٠ (١٩٢ ورقة، ٩٩٣ هـ)، كوبريلى ٥٤٦/ ٢ (١٢٣ ورقة، ٩٠٠ هـ)، سراى، أحمد الثالث ١٠١٥ (١٧٧ ورقة، ٨٥٠ هـ انظر: فهرس ٢/ ٣٨٧)، سراى، مدينة ٣٥٥ (٢٦٦ ورقة، ٩٤٧ هـ)، قونيه يوسف أفندى ٧٤٦٢ (٨٨ ورقة، ٦٠٠ هـ)، ليدن ١٧٧٧ (٢٦٥ ورقة، ٥٩٢ هـ، انظر: فورهوف ١) طلعت بالقاهرة، فقه ٤٦٣.
شروح كتاب أدب القاضى: أ- شرح لأبى بكر أحمد بن على الجصاص الرازى (المتوفى ٣٧٠ هـ/ ٩٨١ م، انظر: رقم ٢٣ من هذا الفصل)، (باتنه ١/ ٩٢، ٩٣٣) (١٠٩)، جار الله ١٦٨٩، (٢٠١ ورقة، ٦٢٩ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٢)، فيض الله ٦٥٨ (٢١٥ ورقة، فى القرن الخامس الهجرى)، ٦٥٩ (١٦٩ ورقة، فى القرن العاشر الهجرى).
ب- شرح عبد الله بن الحسين النيسابورى (المتوفى ٤٤٧ هـ/ ١٠٥٥ م)، أدرنة السليمية ٢٠٢ (٤٨٠ هـ).
ج- شرح لعمر بن عبد العزيز بن مازه (المتوفى ٥٣٦ هـ/ ١١٤١ م انظر بروكلمان ١/ ٣٧٤)، ينى ٣٥٦ (٣٦٩ ورقة، ٨٤٦ هـ)، ٣٥٧ (١٦١ ورقة، فى القرن الثامن الهجرى) ٤٢٣ (٢٥٧ ورقة، فى القرن الثامن الهجرى)، كوبريلى ٥٣١ (٢٤٩ ورقة، ٩٧٣ هـ)، ٥٣٢ (١٧٠ ورقة، ٩٩٦ هـ)، بايزيد ٢٧٩٨ (٢٢٤ ورقة، فى القرن العاشر الهجرى)، عاطف ٧٣٣ (١٦٠ ورقة، فى القرن العاشر الهجرى)، راغب ٥٠٧ (٢٣٢ ورقة فى القرن التاسع الهجرى)، السليمية ٣٢٥ (١٩٠ ورقة، ٩٨٣ هـ، انظر: فهرس معهد
_________
(١٠٩) تحذف مخطوطة ليدن ١٧٧٧ التى ذكرها بروكلمان.','3','87',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (844,'المخطوطات العربية ١/ ٢٦٢)، المكتب الهندى، مخطوطات عربية ٣٨٥٩ (حوالى ١٩٠ ورقة، انظر: Schacht, Bibl. II ١٠ a
وانظر فهرس ٢ رقم ١٥١٣)، المتحف البريطانى، الملحق ٢٧٣، مخطوطات شرقية ٢٤٠٧ (١٩٠ ورقة، ٩٦٠ هـ)، دار الكتب بالقاهرة، فقه حنفى ٢١٠٧ (٢٢٥ ورقة، ٧٩٣ هـ)، الزيتونة بتونس ٤/ ١٤٢ - ١٤٣ رقم ٢١١٠ (١٩٠ ورقة)، ٢١١١ (٢٣٦ ورقة)، ٢١١٢ (١٤٢ ورقة، ٩٩٨ هـ)، بلدية الإسكندرية ٣٧٧٤ ج (٣٧٧ ورقة، فى القرن السابع الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٢)، جاريت ١٦٦٩ (٢١٣ ورقة، ١١٠٥ هـ)، آصفية ٢/ ١٠٨٨، فقه حنفى ١٥٦ (١٢٦١ هـ)، شهيد على ٦٨٧ (٦٨٣ هـ)، ٧٩١ (١٤٠ ورقة، ٦٢٩ هـ)، ولى الدين ١٦٨٨ (٢٣٧ ورقة، ٨٧٠ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٢)، عاطف ١١٠٨ (١٦٩ ورقة، ٩٩٦ هـ)، مراد ملّا ٧٣٧ (٢٣٣ ورقة، ٩٧٥ هـ)، ٧٤٧ (١٤٦ ورقة، ٩٧٤ هـ)، سراى، أحمد الثالث ١٠١٧ (٢٣٥ ورقة ٨٧٠ هـ، انظر: فهرس ٢/ ٣٨٨)، سراى، مدينة ٣٥٦ (١٦١ ورقة/، فى القرن العاشر الهجرى، انظر: فهرس ٢/ ٣٨٨، فهرس معهد المخطوطات العربية ١/ ٢٦٢)، رشيد باستنبول ٢٦٤ (٢٦٤ ورقة، فى القرن التاسع الهجرى)، السليمانية ٣٨٠ (١٧٧ ورقة، ٩٦٢ هـ) كوبريلى ٦٧٥ (٢٦٢ ورقة، فى القرن الثانى عشر الهجرى)، ينى ٣٥٨/ ١ (من ١ - ١٤٥ أ- ٩٤٩ هـ)، ٤٢٣ (٢٥٧ ورقة، فى القرن الثامن الهجرى)، ٤٢٤ (١٥٣ ورقة، فى القرن العاشر الهجرى)، قاضى زاده ١١٨ (٤٣٦ ورقة، ٥٩٦ هـ)، فاتح ٢٣٢٠ (١٩٥ ورقة فى القرن التاسع الهجرى)، حافظ أحمد ١٤ (٢٥٧ ورقة، ٩٦٦ هـ)، فيض الله ٧٣٤ (١٧٨ ورقة فى القرن التاسع الهجرى)، قره چلبى ١١١ (١٤٦، فى القرن العاشر الهجرى)، وهبى ٤٠٠ (٢٩٦ ورقة، فى القرن التاسع الهجرى)، أسعد أفندى ٥١٤ (١٨١ ورقة، ٨٦٢ هـ)، رشيد بقيصرية ٣٠٧ (٢٦٥ ورقة، فى القرن العاشر الهجرى)، تشستربيتى ٣٤٦٤/ ١ (من ١ - ٢٩٢ أ، فى القرن السادس الهجرى)، ٥١٥٩ (١٦٣ ورقة، فى القرن التاسع الهجرى)، بنگيبور ١٩/ ١/ ٩٧، رقم ١٥٩٧ (١٦٦ ورقة، ٩٨٠ هـ)، المتحف البريطانى، مخطوطات شرقية ٩٢٢٠ (١٨٠ ورقة، فى القرن العاشر الهجرى)، طلعت بالقاهرة، فقه ٥٤٧، الأوقاف ببغداد ٣٥٠٥ (انظر: طلس ٦٧)، عابدين بدمشق (فى القرن الثامن الهجرى، انظر: مجلة معهد المخطوطات العربية ٥/ ٢١٣).
د- شرح لأبى عبد الله محمد بن على بن أبى القاسم بن أبى رجاء القاسمى الخجندى (ذكره حاجى خليفة فى كشف الظنون ٤٧)، أيا صوفية ١٢٠٣ (٨٧ ورقة، فى القرن العاشر الهجرى انظر: Schacht, Bib I. I, ١٨ b (١١٠)
هـ- شرح لمؤلف مجهول، آصفية ٢/ ١٠٨٨، فقه حنفى ١٥٦ (١٢٦١ هـ).
_________
(١١٠) تحذف بقية البيانات الأخرى التى ذكرها بروكلمان.','3','88',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (845,'٣ - «كتاب الحيل والمخارج» «وهو يبحث فى علاقة الإنسان بغيره فيما يتعلق بالأعمال التجارية وكيفية الاستفادة من ذلك»، برلين ٤٩٧٢ (١٢٣ ورقة)، ٤٩٧٣ (٣٧٤ ورقة، حوالى ٩٣٠ هـ)، أياصوفية ١١٤٣ (٩٥ ورقة، ٩٤٥ هـ)، القاهرة ثان ١/ ٤١٨، فقه حنفى ١٥٤ (١٠٩ ورقة، ٤٩٦ هـ)، ١٠١٧ (١٣١٣ هـ، انظر: شاخت ١/ ٢٠)، بلدية الإسكندرية، فقه حنفى ٣٦٣٩، ج (فى القرن السادس الهجرى)، آصفية ٢/ ١٠٨٤ فقه حنفى ١١٩ (١٢٤٦ هـ)، السليمانية ٤٥٣/ ١ (١٤٠ ورقة، ٩٠٢ هـ)، ٧٠٨ (الأوراق ٢٣٥ - ٢٧٤)، شهيد على ٧٦٦ (٨٠ ورقة، ٩٥٣ هـ)، ٧٦٧ (٩٥٥ هـ)، عاطف ٨٢١ (٤٧ ورقة، ٩٢٧ هـ)، جامعة إستنبول ٣٦٥١/ ٣ (من الأوراق ١٠١، فى القرن العاشر الهجرى)، ملّا چلبى ٥٧/ ٣، جار الله ٢٧٨، فيض الله ٢١٦٢/ ٦ (١٠١٦ هـ)، أيا صوفية ١٠١٢/ ٢ (الأوراق ١١٨ - ١٦٩، ٩٨٤ هـ)، المكتب الهندى دلهى، عربى ٦٧٨ (قسم واحد، ٥٧ ورقة، فى القرن الحادى عشر الهجرى، فهرس ٢ رقم ١٦٩٦)، الأزهر ١/ ١٤٦ (أربع نسخ)، طلعت بالقاهرة، فقه ٦١٦، انظر أيضا: القاهرة، ملحق Schacht ,,Bib I.I ,No.٢٠ ,III ١٥، ٢٣٦ /٢ وطبع بالقاهرة ١٣١٦ هـ، ونشره شاخت BSPH L ٤ هانوفر ١٩٢٣ م.
٤ - «كتاب النفقات» مع شرح لعمر بن عبد العزيز بن مازه (المتوفى ٥٣٦ هـ/ ١١٤١ م انظر بروكلمان ١/ ٣٧٤)، كوبريلى ١٥٨٨/ ٥ (من ١٨٣ أ- ٢٠٤ ب فى القرن الثانى عشر الهجرى)، ولى الدين ١٥٤٦/ ٢ (من ٨١ أ- ١٠٢ أ، ١١٠٠ هـ)، أسعد أفندى ١٠٢٦/ ١ (من ١ - ٧٢ ب، ١١٥٥ هـ)، شهيد على ٢٧٢٥ (الأوراق ١ - ١٧) (١١١)، عاطف ٧٢٧/ ٢ (من ٢٦ أ- ٤٤ أ، ١١١٥ هـ)، صائب بأنقرة (٣١ ورقة)، انظر:
شاخت ١/ ٢١، ٣/ ١٦ أ، شهيد على ١٠٦١ (من ٩٨ أ- ١١٦ ب، ٦٢٥ هـ)، كوبريلى ٦٨٩/ ٩ (١٩ ورقة)، عاطف ١٠٣٠/ ٢ (من ٩١ أ- ١٣٧ ب، فى القرن العاشر الهجرى)، تشستربيتى ٣٤٦٤ (الأوراق ٢٩٢ - ٣١٥ فى القرن السادس الهجرى)، وطبع بحيدرآباد ١٣٤٩ هـ.
٥ - «كتاب الرضاع» مراد ملّا ٧٣١/ ٤ (من ٩٤ ب- ١٢٠ أ، فى القرن الثامن الهجرى).

<span data-type="title" id=toc-2011>١٦ - ابن المغلّس الحمانى</span>
هو أبو العباس، أحمد بن الصلت بن المغلّس، الحمّانى، أحد تلاميذ بشر بن
_________
(١١١) ليس ٢٧٥٢، ٢٧٥٣ كما ذكر بروكلمان.','3','89',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (846,'الوليد الكندى (المتوفى ٢٣٨ هـ/ ٨٥٢ م) (١١٢). روى عن أبى عبيد القاسم بن سلّام وغيره. ولا يعدّ من الثقات. توفى سنة ٣٠٢ هـ/ ٩١٤ م، وقيل ٣٠٨ هـ.

<span data-type="title" id=toc-2012><span data-type="title" id=toc-2015>أ- مصادر ترجمته:</span></span>
تاريخ بغداد للخطيب ٤/ ٢٠٧ - ٢١٠، الجواهر للقرشى ١/ ٦٩ - ٧٠، كشف الظنون لحاجى خليفة ١٨٣٧.

<span data-type="title" id=toc-2013><span data-type="title" id=toc-2016>ب- آثاره:</span></span>
«فصل فى مناقب أبى حنيفة» يوجد فى طلعت بالقاهرة، مجموع ٤٣٢.

<span data-type="title" id=toc-2014>١٧ - القلانسى</span>
هو أبو عبد الله محمد بن خزيمة البلخى القلانسى، توفى سنة ٣١٤ هـ/ ٩٢٦ م.
أ- مصادر ترجمته:
الجواهر للقرشى ٢/ ٥٣.
ب- آثاره:
«التهذيب»، سراى أحمد الثالث ٨٧٣ (١١٨ ورقة، فى القرن العاشر الهجرى، انظر فهرس معهد المخطوطات العربية ١/ ٢٥٨، فهرس ٢/ ٦٠٨).

<span data-type="title" id=toc-2017>١٨ - البرذعى</span>
هو أبو سعيد أحمد بن الحسين الأشروسنى البرذعى، رحل إلى بغداد، وتفقه على
_________
(١١٢) انظر: الجواهر للقرشى ١/ ١٦٦ - ١٦٧.','3','90',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (847,'مذهب أبى حنيفة، أما فى العقيدة فكان معتزليا رد على الظاهرية. خرج إلى الحج فقتله القرامطة سنة ٣١٧ هـ/ ٩٢٩ م.

<span data-type="title" id=toc-2018>أ- مصادر ترجمته:</span>
تاريخ بغداد للخطيب ٤/ ٩٩ - ١٠٠، طبقات المعتزلة لابن المرتضى ١٠١، الجواهر للقرشى ١/ ٦٦ - ٦٧، بروكلمان ملحق ١/ ٢٩٣ رقم ٦ ب.

<span data-type="title" id=toc-2019>ب- آثاره:</span>
«مسائل الخلاف» الزيتونة بتونس ٤/ ٢٠٩، الرقم القديم ٢٣٠٢، والرقم الجديد ١٦١٩ (٢٣٦ ورقة، ٨٦٣ هـ).

<span data-type="title" id=toc-2020>١٩ - الطّحاوى</span>
هو أبو جعفر، أحمد بن محمد بن سلامة بن عبد الملك، الأزدى الطحاوى الحجرى. ولد بطحا- قرية بصعيد مصر- سنة ٢٣٩ هـ/ ٨٥٣ م كما ذكر ذلك عن نفسه. وكان أول شيوخه خاله أبو إبراهيم إسماعيل المزنى أحد تلاميذ الشافعى المشهورين ثم ترك خاله الذى لم يأخذ عنه كثيرا، فصحب الفقيه الحنفى أبا جعفر أحمد بن أبى عمران موسى بن عيسى. (١١٣) ثم خرج إلى الشام سنة ٢٦٨ هـ/ ٨٨١ م، فلقى شيخ القضاة أبا حازم عبد الحميد بن عبد العزيز (المتوفى ٢٩٢ هـ/ ٩٠٤ م)، ولكنه رجع بعد ذلك الى مصر فوجد العون لدى محمد بن عبدة قاضى مصر. ويقال إنه دخل السجن بعد ذلك بسبب حقد أحد أعوانه عليه. وكان الطحاوى أولا وقبل كل شيء عالما بالفقه. وكان يفتخر بأنه يستطيع أن يكتب عقودا بدون أخطاء. وكان الطحاوى محدثا روى عن خاله المزنى مسند الشافعى. توفى سنة ٣٢١ هـ/ ٩٣٣ م.
_________
(١١٣) انظر: الجواهر للقرشى ١/ ١٢٧ - ١٢٨.','3','91',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (848,'<span data-type="title" id=toc-2021>أ- مصادر ترجمته:</span>
ولاة مصر للكندى، انظر: الفهرس ٦١٩، الفهرست لابن النديم ٢٠٧، طبقات الفقهاء للشيرازى ١٢٠، التهذيب لابن عساكر ٢/ ٥٤ - ٥٥، الأنساب للسمعانى ٣٦٨ أ، وفيات الأعيان لابن خلكان (بولاق) ١/ ٢٣، اللباب لابن الأثير ٢/ ٨٢، تذكرة الحفاظ للذهبى ٨٠٨ - ٨١١، لسان الميزان لابن حجر ١/ ٢٧٤ - ٢٨٢، البداية والنهاية لابن كثير ١١/ ١٧٤، تاج التراجم لابن قطلوبغا ٦، الجواهر للقرشى ١/ ١٠٢ - ١٠٥، مرآة الجنان لليافعى ٢/ ٢٨١، شذرات لابن العماد ٢/ ٢٨٨، النجوم الزاهرة لابن تغرى بردى ٣/ ٢٤٠، الحاوى فى سيرة الإمام أبى جعفر الطحاوى لمحمد زاهد الكوثرى، القاهرة ١٣٦٨، الأعلام للزركلى ١/ ١٩٧، معجم المؤلفين لكحالة ٢/ ١٠٧، بروكلمان ١/ ١٧٣ رقم ٧.
انظر كرنكو فى دائرة المعارف الإسلامية- الطبعة الأوربية الأولى- ٤/ ٦٥٨ - ٦٦٦.
وانظر كذلك فلوجل ووستنفلد:
flugel, Hanef. ٢٩٦ - ٢٩٨. Wustenfeld, Geschichts. ١٠٢.

<span data-type="title" id=toc-2022>ب- آثاره:</span>
١ - «معانى الآثار» (يطابق رقم ٢ عند بروكلمان)، يوجد مخطوطا فى:
برلين ١٢٦٣ (ج ٧، ٢٦٦ ورقة، حوالى ٩٠٠ هـ)، ١٢٦٤ (الربع الأخير ٣٧٥ ورقة، حوالى ١٠٠٠ هـ)، ١٢٦٥ (ج ٤، ٢١٥ ورقة ٨١٧ هـ)، فاتيكان فيدا ٥٢٧ (ج ١، ٥٠٧ ورقة، فى القرن الثامن الهجرى)، ينى ٥٧١ (من ١ ب- ٢٠٠ أ، فى القرن الثانى عشر الهجرى)، السليمانية ٤٥٧ (ج ٢، ٢٤٤ ورقة، فى القرن التاسع الهجرى)، مراد ملّا ٥٣٥ - ٥٣٦ (ج ١، ٢، ٢٨٢ ورقة، ٢٦٣ ورقة، ٦٩٨ هـ)، بنگيبور ٥/ ٢/ ١٩ - ٢٣ رقم ٣٠٥ - ٣٠٧ (من ج ١ - ٣، ٢٩٩ ورقة، ٣٨١ ورقة، ١٧٧ ورقة ٦٨٤ هـ)، (باتنه ١/ ٥٤، ٥٤٢/ ٤ قولة ١/ ١٥٣) (١١٤)، سراى، أحمد الثالث ٥٩٣ (ج ١، ٢، ٨٤٨ هـ)، ٥٩٤ (ج ١، ٣، ٨٧١ هـ)، عاطف ٦٣٢ (٣١٠ ورقة، ١١٣٩ هـ)، كوبريلى ٢٩٧ (ج ١، ٢٣٠ ورقة، حوالى ٨٠٠ هـ)، ٣٤٢ (ج ٣، ١٨٠ ورقة، ٧٣١ هـ)، فيض الله ٤٦٦ (ج ١، ١٩٢ ورقة، فى القرن السادس الهجرى)، ٨١٥ (ج ٢، ٢٥٣ ورقة، فى القرن العاشر الهجرى)، ٨١٦ (ج ٣، ٢٢٧ ورقة، ٧٣٤ هـ)، جار الله ٧٣٦ - ٧٣٨ (من ج ١ - ٣، ٢٧٩ ورقة، ٢٦٣ ورقة ١٥٣ ورقة، ٧٣٠ هـ)، ٧٣٩ (ج ١، ٢٢٧ ورقة، ٧٣٣ هـ)، ٧٤٠ (ج ٢ ٣٤٥ ورقة، فى القرن التاسع الهجرى)، نور عثمانية ١١١٣ (ج ١، ٢٨٨، ورقة ٨٣١ هـ)، ١١١٤ (ج ٢، ٣٢٦ ورقة، ٩٣٧ هـ)، أيا صوفية ١٢٠٢ (ج ٢،
_________
(١١٤) تحذف مخطوطات رامبور ١/ ٩٢، ٢١٢/ ٢١٣ باريس ١١١٠، وآصفية ١/ ٦١٨، ٦٤٣ التى ذكرها بروكلمان.','3','92',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (849,'٢٦٢ ورقة، ٦٦٧ هـ)، محمود باشا ١١١ (ج ١، ٢٤٥ ورقة ٨٢٥ هـ)، ١١٢ (ج ٢، ٢٤٣ ورقة، ٨٢٥ هـ)، أدرنة، سليمية ٨٢٣ (٨٩٧ هـ)، القاهرة ثان ١/ ١٤٩ (توجد بعض النسخ)، وطبع فى لكنو ١٣٠٠/ ١٣٠٢ هـ فى جزءين.
شروح معانى الآثار: أ- «تصحيح معانى الآثار» يحتمل أن يكون لمحمد بن محمد الباهلى المالكى (المتوفى ٣١٤ هـ/ ٩٢٦ م. انظر: تاريخ بغداد للخطيب ٣/ ٢١٤ - ٢١٥)، بنگيبور ٥/ ٢/ ٢٣ - ٢٤ رقم ٣٠٨ (٢٧٩ ورقة، ناقص).
ب- «مبانى الأخبار فى شرح معانى الآثار» لمحمود بن أحمد العينى (المتوفى ٨٥٥ هـ/ ١٤٥١ م، انظر بروكلمان ٢/ ٥٢)، جاريت ١٣٩٤ (ج ١، ٢٢٢ ورقة فى القرن العاشر الهجرى)، آصفية، حديث ٦٤٣ (ج ٥، ٥٢٩ ورقة، ٨٠٨ هـ، بخط المؤلف)، القاهرة ثان ١/ ١٤٣ حديث ٤٩٢ (ج ١، ٤، ٥، ٧، ٨، ٩ بخط المؤلف، ٨٠٧ - ٨٢٠ هـ انظر: شاخت ٢/ ١٣ ب، راجع: فهرس معهد المخطوطات العربية ١/ ٩٤).
ج- «نخب الأفكار فى تنقيح مبانى الأخبار» للعينى المؤلف السابق، القاهرة ثان ١/ ١٥٧، حديث ٥٢٦ (الأجزاء ١ - ٨، بخط المؤلف ٨٠٨ - ٨١٩ هـ، انظر: شاخت ٢/ ١٣ ث، وفهرس معهد المخطوطات العربية ١/ ١١١ - ١١٢)، سراى، أحمد الثالث ٦٥٣ (من ج ٢ - ٥، ٨٨٢ - ٨٨٤ هـ، منقولة عن نسخة بخط المؤلف انظر: فهرس معهد المخطوطات العربية ١/ ١١٢).
د- «معانى الأخيار فى أسامى رجال معانى الآثار» للعينى المؤلف السابق، سراى مدينة ٤٧٧ (ج ٤، ١١٤١ هـ)، ٤٨٥ (ج ١، ٢، ١١٣٩ هـ).
هـ- «شرح معانى الآثار» لأبى الفضل بن نصر بن محمد الدّهستانى، قسطمونى ٣٠٠٣ (٣٥٠ ورقة، ١١٥٤ هـ).
و- شرح لمؤلف مجهول، باريس ١١١٠ (ج ١، ٢٤٠ ورقة، فى القرن العاشر الهجرى).
مختصرات لمعانى الآثار: أ- مختصر لمحمد بن أحمد بن رشد القرطبى (المتوفى ٥٢٠ هـ/ ١١٢٦ م انظر بروكلمان ١/ ٣٨٤) القاهرة ثان ١/ ١٤٥، حديث ٤١٩ (١٨٨ ورقة، ٨٠٩ هـ، انظر: شاخت ٢/ ١٣ أ، وفهرس معهد المخطوطات العربية ١/ ٩٧).','3','93',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (850,'ب- مختصر لعبيد بن محمد بن عبد العزيز السمرقندى (المتوفى ٧٠١ هـ/ ١٣٠١ م، انظر: الدرر الكامنة لابن حجر ٢/ ٤٣٣)، فيض الله ٩٣١/ ١ (من ١ - ١٤١ ب، ٨٩٣ هـ).
ج- مختصر لعبد الله بن يوسف بن محمد الزّيلعى (المتوفى ٧٦٢ هـ/ ١٣٦٠ م، انظر: معجم المؤلفين لكحالة ٦/ ١٦٥)، كوبريلى ٢/ ٦٦ (٧٤٥ هـ).
٢ - (يطابق رقم ٣ عند بروكلمان) «بيان مشكل الأحاديث (الآثار)» برلين ١٢٦٦ (ج ٣، ١٨٣ حوالى ١٠٠٠ هـ)، ١٢٦٧ (النصف الثانى من المجلد الثالث، ١٤٥ ورقة، ٧٤٩ هـ)، رامبور ١/ ٩٢، حديث ٢٠٨ - ٢١١ (أربعة أجزاء)، فيض الله ٢٧٣ - ٢٧٩ (٢٩٢ ورقة، ٢٩٣ ورقة، ٢٨٢ ورقة، ٣٠١ ورقة، ٣٤٦ ورقة، ٢٢٨ ورقة، ٢٤٥ ورقة، ٧٩٨ - ٨٠٢ هـ، ٨٥٩ هـ، ٨٦٠ هـ)، محمود باشا ١٠٧/ ٧ (قطعة واحدة). وطبع بحيدرآباد فى أربعة مجلدات ١٣٣٣ هـ.
ومنه مختصر لسليمان بن خلف الباجى (المتوفى ٤٧٤ هـ/ ١٠٨١ م انظر بروكلمان ١/ ٤١٩). هذا المختصر بقى قسم منه فى المختصر الثانى المسمى: «المعتصر من المختصر» لجمال الدين يوسف بن موسى بن محمد الحنفى (المتوفى ٨٠٣ هـ/ ١٤٠٠ م، انظر: الضوء اللامع للسخاوى ١٠/ ٣٣٥)، المتحف البريطانى ١٥٦٩، إضافات ٢٧٥١٨ (١٤٩ ورقة، فى القرن التاسع الهجرى)، بنگيبور ٥/ ٢/ ٢٤ - ٢٥/ رقم ٣٠٩ (٢٧٣ ورقة، ٧٩٧ هـ)، فاتح ٢١٤٧ (١٢٨ ورقة، ٧٨٣، بخط المؤلف)، رئيس الكتاب ٢٦٥ (١٤٥ ورقة، ١١٣٢ هـ). وطبع بحيدرآباد ١٣٠٧ هـ، ١٣٦٢ هـ.
٣ - «كتاب الشروط الكبير» (يطابق رقم ٤ عند بروكلمان) وهو يحتوى على تعليمات إلى الموثقين والكتاب (انظر: شبيس، (٢٤١. O.Spies ,Klass ishesislamisches Recht شهيد على ٨٨١ (٢١٩ ورقة، فى القرن الخامس الهجرى)، ٨٨٢ (١٨٠ ورقة، فى القرن الخامس الهجرى، انظر: شاخت ١/ ٢٦، وفهرس معهد المخطوطات العربية ١/ ٢٦٦)، وبرلين ٤١/ ٢، القاهرة ثان ١/ ٤٥٦، فقه حنفى ١٣٩، ١٤٠ «كتاب أذكار الحقوق والرهون» (القطعتان الموجودتان بدار الكتب بالقاهرة).
ونشره شاخت ضمن مطبوعات أكاديمية هيدلبرج ١٩٢٦ - ١٩٢٧، انظر:
Schacht, in: Situngsberichte, Heidelberg ١٩٢٦ - ١٩٢٧
أما البحث الرابع من السلسلة السابقة) «وكتاب الشفعة» المأخوذ من كتاب الشروط الكبير فقد نشرهما شاخت فى السلسلة السابقة أيضا ١٩٢٩ - ١٩٣٠ م.
٤ - «كتاب الشروط الصغير» (يطابق رقم ٤ أعند بروكلمان) ويوجد مخطوطا فى: قره مصطفى ٢٤٠ (١٤٤ ورقة، ٨٢١ هـ)، مراد ملّا ٩٩٧ (١٧٠ ورقة، ٨٥٨ هـ)،','3','94',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (851,'٩٩٨ (١٧٥ ورقة، ٨٧٠ هـ، انظر: شاخت ١/ ٢٧)، فيض الله ١٠٣٣ (١٢٢ ورقة، ٨٩٢ هـ، انظر:
فهرس معهد المخطوطات العربية ١/ ٢٦٦).
٥ - «اختلاف الفقهاء» وله مختصر لأحمد بن على الجصّاص (المتوفى ٣٧٠ هـ/ ٩٨١ م. انظر: رقم ٢٣ من هذا الفصل)، القاهرة ثان ١/ ٤٠٠، فقه حنفى ٦٤٧ (ج ٢، ٢٤٦ ورقة، فى القرن الثامن الهجرى، انظر: شاخت ١/ ٢٤، وفهرس معهد المخطوطات العربية ١/ ٣٢٩)، جار الله ٨٧٢ (ج ٤، ١٥٥ ورقة، ٤٨١ هـ).
٦ - «المختصر فى الفقه» فيض الله ٩٤٩ (١٨٠ ورقة، فى القرن الثانى عشر الهجرى)، جار الله ٨٧٦ (١٤٨ ورقة، فى القرن الثامن الهجرى)، الأزهر، فقه حنفى ٩٥٨ (٢٠٧ ورقة، ومنه نسخة مصورة بدار الكتب بالقاهرة، ملحق رقم ٣/ ٣٢)، نشره أبو الوفاء الأفغانى بالقاهرة ١٣٧٠ هـ.
شروح كتاب المختصر فى الفقه: أ- شرح لأبى بكر أحمد بن على الجصاص الرازى (المتوفى سنة ٣٧٠ هـ/ ٩٨١ م، انظر رقم ٢٣ من هذا الفصل)، دار الكتب بالقاهرة، فقه حنفى ٤٩٨، (٢٤٠ ورقة ٧٤٨ هـ)، ٧٥٦ (١٩٠ ورقة، فى القرن الخامس الهجرى، انظر فهرس معهد المخطوطات العربية ١/ ٢٦٣). قونية يوسف أفندى ٣٥٨١ - ٣٥٨٤ (الأجزاء ١ - ٤، ٢٤٦ ورقة، ٢٢٠ ورقة، ٢٣٧ ورقة، ٢٤٠ ورقة، ٧١٣ هـ)، سراى، أحمد الثالث ٣/ ١٠٧٦ (ج ٢، ٣٢٥ ورقة، ٨٠١ هـ، انظر: فهرس ٢/ ٣٩٠)، جار الله ٧١٨ (٢١٨ ورقة، ٧٦٩ هـ)، ٨٧١ (ج ١، ٢٣٨ ورقة، فى القرن التاسع الهجرى)، ٨٧٢ (ج ٤، ١٥٥ ورقة، ٤٨١ هـ. انظر فهرس معهد المخطوطات العربية ١/ ٢٦٤).
ب- شرح لشمس الأئمة محمد بن أحمد السّرخسى (المتوفى ٤٨٣ هـ/ ١٠٩٠ م انظر بروكلمان ١/ ٣٧٣) تشستربيتى ٣٩٢٣ (٣٣٩ ورقة، فى القرن السابع الهجرى) (١١٥).
ج- شرح لبهاء الدين على بن محمد بن إسماعيل الإسبيجابي السمرقندى (المتوفى ٥٣٥ هـ/ ١١٤١ م. انظر معجم المؤلفين لكحالة ٧/ ١٨٣)، جار الله ٦٨٢ (ج ١، ٢، ١٧٢ ورقة، ٨٧٠ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٤)، ٦٨٣ (٥٨١ هـ)، مراد ملّا ٥٦ (ج ١، ٢٦٠ ورقة، فى القرن السابع الهجرى)، فيض الله ٨٠٣ (٤٧٠ ورقة، فى القرن التاسع الهجرى)، ينى ٤٥٧ (٣٥٢ ورقة)، ٤٥٨ (٧٠١ ورقة، ١٠١١ هـ) انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٤ - ٢٦٥،
_________
(١١٥) تحذف مخطوطة السليمانية ٥٩٥ التى ذكرها بروكلمان.','3','95',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (852,'حكيم أوغلى ٣٤٤ (٣٦٠ ورقة، ٩٣٨ هـ)، داماد إبراهيم ٥٦٢ (٥٠٤ ورقة ٢١٣ هـ)، عاطف ٨٩٦ (٤١٨ ورقة، ٦٣٨ هـ)، ٨٩٧ (٣١٧ ورقة، فى القرن الحادى عشر الهجرى)، سراى، أحمد الثالث ١٠٧٥ (٣٣٨ ورقة ٧٧٧ هـ، انظر: فهرس ٢/ ٣٨٩).
د- شرح لمحمد بن أحمد الخجندى الإسبيجابي (ربما عاش فى القرن السادس الهجرى، انظر الجواهر للقرشى ٢/ ٢٧)، شهيد على ٨١٥ (ج ١، ٢٢٧ ورقة، فى القرن السادس الهجرى)، ٨١٦ (ج ٢، ٢٠٦ ورقة، ٥٥٢ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٤) بعنوان: «الحاوى»، كوبريلى ٥٨٨ (ج ١، ٢، ٣٨٠ ورقة).
هـ- شرح لأبى نصر أحمد بن محمد بن مسعود الوبرى (انظر الجواهر للقرشى ١/ ١٢١)، الأوقاف ببغداد ٣٦٢٥ (٧٩٣ هـ، انظر: كوركيس عواد فى مجلة: سومر ٤/ ١٢٩) (١١٦) ٧ - «العقيدة» (أو: العقائد، أو: بيان السنة والجماعة)، انظر:
Schacht, lslam ٢١/ ٢٨٨ - ٢٨٩ (E. K. Elder (Macdonald Presentation, Vol. ١٩٣٣, ١٩٧ - ١٢٧.
برلين ١٩٣٨ (الأوراق ٤٤ - ٥٦)، ١٩٣٩ (الأوراق ١٣ - ١٧)، جوتا ٦٦٣، (خمس ورقات، حديث)، القاهرة ثان ١/ ١٩٦ كلام ١١٣، ١٧١، ١٧٢ كوبريلى ٨٤٧/ ١ (من ١ - ١٥ أ، ٨٢١ هـ) (١١٧)، جاريت ٢١٢٧/ ١ (/ فى القرن التاسع (الهجرى)، بلدية الإسكندرية ١٩٦٨/ ١ د (ست ورقات، ٧٨٣ هـ، انظر فهرس معهد المخطوطات العربية ٢/ ١١٩)، رامبور ١/ ٣١٥، كلام ٢٥٣ (١٢٩٢ هـ)، راغب ١٤٧٩/ ١ (ست ورقات، ١١١٠ هـ)، وهبى ٤٨١، ٢٠٨٨/ ٤، شهيد على ١٧٠٦/ ٧، رشيد باستنبول ٣٢١ (٩٨٩ هـ)، برتو باشا ٦٤٧/ ٢٣، ٦٥٠/ ١ فاتح ١٠٣٩/ ٥ أسعد أفندى ١٢٥٩/ ١، رئيس الكتاب ٣٠٤/ ٢، لاله لي ٢١٥٣/ ٢، ٢٤١١/ ١، ٢٤١٢/ ١، ٢٣٨٠/ ٣، لاله إسماعيل ٦٨٩/ ١، مهرشاه ٢٩٤/ ٢، حسن حسنى باشا ١١٦٠/ ٧، أيا صوفيه ٥٧٤/ ٦، ٢٧٩٢/ ٣، مكتبة جامعة إستنبول ٣٨٢٣، صائب بأنقرة ١٥٥٩ (من ١١٣ أ- ١٠٦ ب)، ٢٧٨٣/ ١٢، باريس ٤٨٠٨ (الأوراق ٣٠/ ٣٨. انظر:
فايدا ٢٦٧)، تشستربيتى ٤٤٠٠/ ٩ (الأوراق ٩٤ - ٩٩، القرن الثانى عشر الهجرى)، القاهرة، ملحق ٢/ ١٣٦ رقم ٢٠٥٧٦ ب (الأوراق ١١٥ - ١٢٩)، ٣٦٠٠ ج (من ورقة، ٩ - ١٨، ١٠٣١ هـ)، طلعت
_________
(١١٦) ذكر بروكلمان خطأ أن الشرحين الثالث والرابع لمؤلف واحد هو: أحمد بن منصور الإسبيجابي (المتوفى ٤٨٠ هـ/ ١٠٨٧ م)، ولذلك فإن مؤلف مخطوطة جامعة بومباى ٣٢ (٢٧٦ ورقة، فى القرن العاشر الهجرى، فهرس رقم ٤٨) موضع نظر.
(١١٧) تحذف مخطوطة كوبريلى ٣٤٨ التى ذكرها بروكلمان.','3','96',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (853,'مجموع ١٥٧/ ٤ (من ١٢٦ ب- ١٣٠ ب، فى القرن التاسع الهجرى)، ٣٤٥، وطبع بقازان ١٨٩٣ م، وحلب ١٣٤٠ هـ، وطبعه حجر بسكاربور ١٩٠٠ م.
شروح العقيدة: أ- شرح لإسماعيل بن إبراهيم بن أحمد الشيبانى (المتوفى ٦٢٩ هـ/ ١٢٣١ م) (١١٨)، كوبريلى ٨٤٧/ ٢ (من ورقة ١٥ - ٤٥، ٨٢١ هـ)، رئيس الكتاب ٣٠٤/ ٣، (من ورقة ٧٦ - ١٠٣، ١١٣٥ هـ)، برتو باشا ٦٤٧/ ٢٤ (من ورقة ٢٣١ - ٢٤٤، ١١٦٥ هـ)، تشستربيتى ٤٤٤٦/ ٣ (من ورقة ٦٣ - ٩٠، ٩٠٦ هـ)، القاهرة، ملحق ٢/ ٥٠ رقم ٢٢٨٩٦ ب (٢٧ ورقة، ١٠٧٧ هـ) ب- «النور اللامع والبرهان الساطع» منجبرس نجم الدين بن يلنقلج عبد الله التركى (المتوفى ٦٥٢ هـ/ ١٢٥٤ م)، ينى ٧٦٠/ ١ (من ١ - ١٢٥ أ، فى القرن التاسع الهجرى)، أيا صوفيه ٢٣١١ (١٦٦ ورقة، ٨٣٢ هـ)، كوبريلى ٨٤٨ (٢٩٣ ورقة، ٦٤٨ هـ)، ٨٦١/ ٢ (٢٢٤، ورقة فى القرن السابع الهجرى)، جوتا ٦٦٤ (٢١١ ورقة، ٨٨١ هـ) (١١٩)، لاله لي ٢٣١٨ (١١٤ ورقة، ٧١٤ هـ) ٢٣٢٠/ ١ (من ١ - ٧٤ ب ٨٨٢ هـ)، رئيس الكتاب ٥٦٣ (٢٣١ ورقة، ٦٤٦ هـ).
ج- شرح هبة الله بن أحمد بن معلّى التّركستانى (المتوفى ٧٣٢ هـ/ ١٣٣١ م انظر: معجم المؤلفين لكحالة ١٣/ ١٣٤)، جاريت ١٥٤٣ (٩١ ورقة، فى القرن التاسع الهجرى)، مراد ملّا ١٣٩٤/ ١ (١٤٩ ورقة، ١٠٧٠ هـ).
د- شرح لمحمود بن أحمد بن مسعود القونوى (المتوفى ٧٧١ هـ/ ١٣٦٩ م انظر بروكلمان ملحق ٢/ ٩٠) الإسكوريال ١٥٦٣/ ٣ (من ١٠ أ- ١٧١ فى القرن الثامن الهجرى)، (بلدية الإسكندرية توحيد ٣٠؟ )، الأزهر ٣/ ٣٠٠، توحيد ٣٣٨ (٧٥٩ هـ)، وطبع بقازان ١٣١١ هـ.
هـ- شرح لسراج الدين أبى حفص عمر بن إسحاق الشّبلى (المتوفى ٧٧٣ هـ/ ١٣٧٢ م انظر بروكلمان ملحق ٢/ ٨٩) القاهرة ثان ١/ ١٩٠، كلام ٢٣٥، مهرشاه ٢٩٤/ ٣ (من ورقة، ١٠ - ٥٩، ١١٥٨ هـ).
و- شرح لمحمد بن محمد بن محمود البابرتى (المتوفى ٧٨٦ هـ/ ١٣٨٤ م انظر بروكلمان ملحق ٢/ ٨٩)، أسعد أفندى ١٢٥٩/ ٢ (من ٩ ب- ٦٦ ب، ١٠٩٩ هـ)، لاله إسماعيل ٦٨٩/ ٢ (من ١٠ ب- ٩٠ ب، ١١٤٨ هـ).
_________
(١١٨) انظر: الجواهر للقرشى ١/ ١٤٤.
(١١٩) تحذف مخطوطات جاريت ٢١٢٧، وبلدية الإسكندرية، توحيد ٢٥ التى ذكرها بروكلمان.','3','97',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (854,'ز- شرح لعلى بن على بن محمد بن أبى العز الدمشقى (المتوفى ٧٩٢ هـ/ ١٣٩٠ م انظر: معجم المؤلفين لكحالة ٧/ ١٥٦)، لاله لي ٢٣٢٠/ ٢ (من ٧٥ أ- ١٧٧ ب ٨٨٣ هـ).
ح- «نور اليقين فى أصول الدين» لكافى حسن أفندى الآقحصارى (المتوفى ١٠٢٥ هـ/ ١٦١٦ م انظر بروكلمان ٢/ ٤٤٣)، ليبتسج ١٩٠/ ٢.
ط- شرح مجهول المؤلف بإيحاء من سيف الدين الناصرى (المتوفى ٧٥٨ هـ/ ١٣٥٧ م) جوتا ٦٦٥، المكتب الهندى JRAS /٩٣٩ /٣٥٩، ٤٥٦٩ ى- شرح لمؤلف مجهول (برنستون ١٥٥ ب).
ك- شرح لمؤلف مجهول فى تشيستربيتى ٥٢١٩ (٨٣ ورقة، فى القرن الثامن الهجرى).
ل- يوجد شرح ببرلين ١٩٤٠ (من ورقة ١ - ٤١، ٧٧٥ هـ).
٨ - «التسوية بين «حدّثنا» وبين «أخبرنا» تشستربيتى ٣٤٩٥ (من ورقة ١١٦ - ١٢٢، ٧٣٨ هـ)، الظاهرية، مجموع ٩٢/ ١٧ (من ٢٩٦ أ- ٣٠٢ ب، فى القرن السادس الهجرى) (١٢٠).

<span data-type="title" id=toc-2023>٢٠ - الكرابيسى</span>
هو أبو الفضل محمد بن صالح بن محمد بن الهيثم الكرابيسى السمرقندى، روى عن عبد الله بن عبد الرحمن الدارمى (المتوفى ٢٥٥ هـ/ ٨٦٩ م) وغيره. وتوفى سنة ٣٢٢ هـ/ ٩٣٤ م.

<span data-type="title" id=toc-2024>أ- مصادر ترجمته:</span>
كشف الظنون لحاجى خليفة ١٢٥٧، هدية العارفين ٢/ ٣٣، الأعلام للزركلى ٧/ ٣٢، معجم المؤلفين لكحالة ١٠/ ٨٥، بروكلمان ملحق ١/ ٢٩٥ رقم ٩ أ.
_________
(١٢٠) ملاحظات:
أ- كتاب السنن: الذى ذكره بروكلمان، الملحق ١/ ٢٩٣ تحت رقم ١ للشافعى وليس للطحاوى.','3','98',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (855,'<span data-type="title" id=toc-2025><span data-type="title" id=toc-2028>ب- آثاره:</span></span>
«كتاب الفروق» سراى، أحمد الثالث ١١٨١/ ١ (من ١ - ٥٤ أ، ١٠٠٣ هـ انظر، Schacht , (Islamica ٢ /٥٠٨: فيض الله ٩٢١/ ١ (من ١ - ٢٥ ب، فى القرن التاسع الهجرى).

<span data-type="title" id=toc-2026>٢١ - المروزى</span>
هو أبو الفضل محمد بن محمد بن أحمد بن عبد الله المروزى، الشهير بالحاكم الشهيد، ولد بمرو، ولا بد أن يكون مولده قبل سنة ٢٤١ هـ/ ٨٥٥ م، ذلك لأنه روى عن أحمد بن حنبل ومعاصريه. عمل قاضيا ببخارى ثم أصبح وزيرا بخراسان لحميد السامانى. وكان مؤلفا كثير التصانيف كما كان رواية ثقة. وقتله فى منزله جنود ثائرون سنة ٣٣٤ هـ/ ٩٤٥ م، ودفن فى مرو.

<span data-type="title" id=toc-2027>أ- مصادر ترجمته:</span>
المنتظم لابن الجوزى ٦/ ٣٤٦ - ٣٤٧، الجواهر للقرشى ٢/ ١١٢ - ١١٣، هدية العارفين ٢/ ٣٧، الأعلام للزركلى ٧/ ٢٤٢، معجم المؤلفين لكحالة ١١/ ١٨٥، بروكلمان ١/ ١٧٤ رقم ٩.
ب- آثاره:
«الكافى فى الفقه» اعتمد فى تأليفه على كتب الشيبانى، برلين الشرقية ١٦٦٢، أيا صوفية ١٣٦٢ (ج ١/ ٤٢٩ ورقة، ١١٤٧ هـ)، ١٣٦٣ (٢/ ٤٣٢ ورقة، ١١٤٧ هجرية)، فيض الله ٩٢٢ (١، ٣٩٢ ورقة، ٨٥٥ هجرية)، ٩٢٣ (٦٠١ ورقة، فى القرن العاشر الهجرى) القاهرة ثان ١/ ٤٥٥ فقه حنفى ٤٠٠ (ج ١، ٢٥٩ ورقة، ٧٢٦ هجرية، انظر: فهرس معهد المخطوطات العربية ١/ ٢٧٠ وانظر بروكلمان ملحق ١/ ٢٨٩، وشاخت ١/ ٣ ب، ٣/ ٣ أ)، شهيد على ٩١٢ (٥٦٩ ورقة، ٩٦٤ هـ)، داماد إبراهيم ٦٤٦ (٦٣٢ ورقة)، حميدية ٥٤٥ (٤٧١ ورقة)، فيض الله ٦٨٦ (ج ٢، ٩٥٥ هـ)، ٩٩٣ (ج ٢، ٢٢٥ ورقة)، السليمانية ٥٨٠ (٦٦٧
_________
ب- كتاب صحيح الآثار: الذى ذكره بروكلمان ١/ ١٨٢ تحت رقم ٨ يبدو أنه هو كتاب معانى الآثار.
ج- يحذف كتاب «معالم الآثار» الذى ذكره بروكلمان. الملحق ١/ ٩٥٠.','3','99',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (856,'ورقة، ١٩ ورقة، ١١٢١ هـ)، جار الله ٨٣٠ (٤٦٧ ورقة)، عاطف ١٠٠٥ (٢٤٠ ورقة)، ١٠٠٦ (٧١٤ هـ)، ١٠٠٧ هـ (٤١٧ هـ)، سراى أحمد الثالث ٧١٩ (ج ٢، ٣، ٢٨٢ ورقة، ٢٦٩ ورقة، فى القرن الثامن الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٢٧٠)، جورلولو ٣١١ (ج ٢، ١١٤ ورقة، ٨٩١ هـ)، تشيستربيتى ٤٢٦٢ (٢٣٠ ورقة، فى القرن السادس الهجرى).
شروح الكافى فى الفقه: أ- شرح لمحمد بن أحمد بن سهل السّرخسى (المتوفى ٤٨٣ هـ/ ١٠٩٠ م)، المخطوطات: (انظر بروكلمان ملحق ١/ ٢٨٩)، المتحف البريطانى ملحق ٢٧٦/ ٧، مخطوطات شرقية ١١٠٨ - ١١٠٩ (٢٦١ ورقة، ٢٣٦ ورقة من القرن الثانى عشر الهجرى، المكتب الهندى ١١ (٢٤٣ ورقة، ١١٥٠ هـ، وفهرس ١ رقم ٢٠٤)، فاتح ٢٠٥٧ - ٢٠٨٦ (مجموعة من القرن السابع الهجرى)، أسعد أفندى ٧١٥، ٩٠٢، عاطف ١٠١٧ - ١٠٢٥ (تسعة أجزاء فى القرن الحادى عشر الهجرى)، سراى، ريفان كوشك ٦١٧ - ٦١٨ (جزءان، ٦٦٣ هـ)، جورلولو ٢٤٠ - ٢٤١، ٢٤٣ - ٢٤٧، رئيسالكتاب ٣٨٧ - ٣٩٠ (أربعة أجزاء، ١١٣٣ هـ)، حكيم أوغلى ٣٨١ - ٣٨٦، (نسخة كاملة ١١٤٦ هـ)، عموجة حسين ٢٢٢ - ٢٢٣ (جزءان)، سراى، أحمد الثالث ٧١٤ (نسخة كاملة، ثلاثة أجزاء ٩٩٥ هـ)، ١٤٤٢ (مجلدان كبيران وشاملان وجميلان من سنة ٨٣٤ هـ)، أيا صوفية ١٠٣١ (ج ٢، ٨٤٣ هـ)، ١٣٧٩ - ١٣٨٠ (١، ٢، ٧٤١ هـ)، رستم باشا ١٣٢ - ١٣٧ (المجلدات ٢ - ٨)، محمود باشا ٢٣١ (٤١٠ ورقة)، اسماخان ١٩٦ - ١٩٩ (المجلد الأول والأخير)، كوبريلى ٦٤٢ - ٦٤٣ (نسخة كاملة، فى القرن التاسع الهجرى)، مراد ملّا ١٠٢٥ - ١٠٣٧ (نسخة كاملة، ٩٧٠ هـ)، ينى ٥٤٢ - ٥٤٤ (ج ١، ٣، ٤، ٩٨٥ هـ)، جار الله ٨٣٩ - ٨٤٣ (أربع مجلدات، فى القرنين الثامن والعاشر الهجريين)، بايزيد ٢١٠١، ٢١٤٧ (ج ٣، ٤، فى القرن العاشر الهجرى)، يحيى أفندى ١١٩ (نسخة كاملة، ٧٨٥ هـ)، حميدية ٥٤٨ (نسخة كاملة ٩٧٦ ورقة، فى القرن الثانى عشر الهجرى)، ٥٤٩ (كاملة، ١١٣٢ هـ)، فيض الله ٩٨٣ - ٩٩٢ (عشرة أجزاء، من القرن السابع إلى القرن العاشر الهجرى)، الأزهر ٢/ ٢٥٠، فقه حنفى ٢٦٩٨ (ثلاثة أجزاء، ٩٨٥ هـ)، ٣٠٣٤ (أحد عشر جزءا، فى القرن الثالث عشر الهجرى)، القاهرة ثان ١/ ٤٥٩، (أجزاء متعددة)، البنغال ٣٩٨ (ج ٦، ٧، ٦٢٤ هـ، ٦٢٧ هـ)، ٣٩٩ (ج ٦، ١١٤٩ هـ)، رامبور ١/ ٢٤٥، فقه ٥٠٥ (ج ٤)، بوهار بكلكتا ١٥١ (مجلد واحد، فى القرن الحادى عشر الهجرى).
وطبع فى ثلاثين مجلدا بالقاهرة ١٣٢٤ - ١٣٣١ هـ.
ب- شرح لأحمد بن منصور الإسبيجابي (المتوفى ٤٨٠ هـ/ ١٠٨٧ م، انظر: الجواهر للقرشى ١/ ١٢٧)، نور عثمانية ١٦٠٢ - ١٦٠٣ (/ ٤٣٥ ورقة، حوالى ٤٠٠ ورقة، ٥٧٠ هـ، انظر: شاخت','3','100',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (857,'١/ ٣ د. وهناك شك فى نسبة هذا الشرح إلى هذا المؤلف، ولكنه ليس على بن محمد بن إسماعيل الإسبيجابي، انظر: كشف الظنون لحاجى خليفة ١٣٧٨).
وله مختصر بعنوان «الشافى فى اختصار الكافى» لأبى البقاء محمد بن أحمد بن ضياء الدين القرشى (المتوفى ٨٥٤ هـ/ ١٤٥٠ م) (الخالدية بالقدس ٢٤/ ١٠٩ انظر بروكلمان ملحق ١/ ٢٨٩).

<span data-type="title" id=toc-2029>٢٢ - الكرخى</span>
هو أبو الحسن عبيد الله بن الحسين بن دلّال الكرخى، ولد بكرخ جدّان سنة ٢٦٠ هـ/ ٨٧٣ م، سكن بغداد ودرس بها فقه أبى حنيفة، ثم انتهت إليه رياسة أصحاب أبى حنيفة. وكان عظيم العبادة صبورا على الفقر والحاجة. أصيب بالفالج فى آخر حياته. كتب أصحابه إلى سيف الدولة يلتمسون منه ما ينفق عليه. ومات الكرخى قبل أن يصله كتاب سيف الدولة ومعه عشرة آلاف درهم. مات سنة ٣٤٠ هـ/ ٩٥٢ م.

<span data-type="title" id=toc-2030>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠٨، تاريخ بغداد للخطيب ١٠/ ٣٥٣ - ٣٥٥، لسان الميزان لابن حجر ٤/ ٩٨ - ٩٩، الجواهر للقرشى ١/ ٣٣٧، تاج التراجم لابن قطلوبغا ٢٩، كشف الظنون لحاجى خليفة ٥٦٣، ٥٧٠، هدية العارفين ١/ ٦٤٦، الأعلام للزركلى ٤/ ٣٤٧، معجم المؤلفين لكحالة ٦/ ٢٣٩، بروكلمان ملحق ١/ ٢٩٥ رقم ٩ ب.

<span data-type="title" id=toc-2031>ب- آثاره:</span>
١ - «الأصول» (رامبور ٢/ ٣٧٤)، شهيد على ٢٧٧٢ (من ٧٩ أ- ٧٩ ب، فى القرن التاسع الهجرى)، وعليه شرح لأبى حفص عمر النسفى (المتوفى ٥٣٧ هـ/ ١١٤٢ م انظر بروكلمان ١/ ٤٢٧) شهيد على ٢٧٦٢ (من ٨ أ- ٨٦ ب، فى القرن التاسع الهجرى)، وطبع فى مجموعة بالقاهرة بدون تاريخ. ونظم كتاب الأصول شعرا أحمد حسين سالم الشباسى، القاهرة، ملحق ٣/ ١٢٩ رقم ٢١٥٣٣ ب (٤ ورقات، ١٣٢٩ هـ، بخط المؤلف).','3','101',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (858,'٢ - «المختصر» وصل هذا الكتاب فى شرح أبى الحسين أحمد بن محمد القدورى (المتوفى ٤٢٨ هـ ١٠٣٧ م، ويعدّ من أمهات الكتب فى فقه الحنفية، انظر، مراد ملّا ٨٩٥، ١ أ)، رامبور ١/ ٢١٠، فقه ٢٨٢ (ج ٣، ٤٠٨ ورقة، ٩٢٥ هـ)، داماد إبراهيم ٥٦٣ (٤٩٥ ورقة، ٣٣٥ هـ)، مراد ملّا ٨٩٥ (ج ١، ٣٧٩ ورقة، ٩٢٤ هـ)، ٨٩٦ (ج ٣، ٣٦١ ورقة، ٩٣٢ هـ)، ٨٩٧ (ج ٤، ٤٠٢ ورقة، ٥٦٦ هـ)، ينى جامع ٧٦٠/ ٢ (١٢٥ أ- ١٢٥ ب، فى القرن التاسع الهجرى، تحتوى هذه القطعة من المقدمة على معلومات هامة فى تاريخ الفقه الحنفى).
ولهذا الكتاب شروح وهى:
أ- شرح لأحمد بن منصور الإسبيجابي (المتوفى ٤٨٠ هـ/ ١٠٨٨ م)، مراد ملّا ٩٠٤ (١٨٤ ورقة، بخط المؤلف).
ب- شرح لأبى الفضل عبد الرحمن بن محمد الكرمانى (المتوفى ٥٤٣ هـ/ ١١٤٩ م انظر بروكلمان ١/ ٣٧٤) كوبريلى ٥٤٠ (٢٦٣ ورقة، ٥٣٠ هـ)، عاطف ٩١١ (٢٥٣ ورقة، ٨٢٣ هـ)، أسعد أفندى ٩٦٤ (٢٤٨ ورقة، ٩٨٨ هـ).

<span data-type="title" id=toc-2032>٢٣ - الجصّاص</span>
هو أبو بكر أحمد بن على الجصّاص الرازى، ولد سنة ٣٠٥ هـ/ ٩١٧ م، ورد بغداد سنة ٣٢٥ هـ/ ٩٣٧ م ليدرس على أبى الحسن الكرخى، وأصبح بعد ذلك مدرسا للفقه. ولكنه ترك بغداد مرتين، مرة إلى الأهواز وأخرى إلى نيسابور، ثم عاد إلى بغداد سنة ٣٤٤ هـ/ ٩٥٥ م. وبقى بها حتى توفى (١٢١) سنة ٣٧٠ هـ/ ٩٨١ م.

<span data-type="title" id=toc-2033>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠٨، تاريخ بغداد للخطيب ٤/ ٣١٤ - ٣١٥، المنتظم لابن الجوزى ٧/ ١٠٥ ١٠٦، تذكرة الحفاظ للذهبى ٩٥٩، الجواهر للقرشى ١/ ٨٤ - ٨٥، البداية والنهاية لابن كثير ١١/ ٢٩٧، شذرات الذهب لابن العماد ٣/ ٧١، الأعلام للزركلى ١/ ١٦٥، معجم المؤلفين لكحالة ٢/ ٧ - ٨، بروكلمان ملحق ١/ ٣٣٥ رقم ٣ ج.
_________
(١٢١) ليس فى نيسابور كما ذكر ذلك بروكلمان.','3','102',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (859,'<span data-type="title" id=toc-2034>ب- آثاره:</span>
١ - «أحكام القرآن» كوبريلى ٣٥ (٤٧٦ ورقة، فى القرن الثامن الهجرى)، نور عثمانية ١٠٧ (٧٣٢ ورقة، ٩٧١ هـ)، مراد ملّا ١٧ (ج ١، ٣١٧ ورقة، فى القرن الثامن الهجرى)، ١٨ - ٢٠ (أجزاء ١ - ٣، فى القرن الثامن الهجرى)، ٢١ (ج ٤، ٣٥٣ ورقة ٥٦٧ هـ)، ولى الدين ١٢٥ (٢٥٢ ورقة، فى القرن الثامن الهجرى)، ١٢٦ (٢٤٧ ورقة، فى القرن الثامن الهجرى)، ١٢٧ (٢٥٧ ورقة، فى القرن الثامن الهجرى)، بنگيبور ١٤٦٩، ١٨/ ٢، ١٥٤ (ج ٣، ١٦٦ ورقة)، الزيتونة بتونس ١/ ١٥ رقم ٧١ (ج ٢ رقم ٣٠٨ ورقة)، ٧٢ ٧٣ (مجلدان)، القاهرة ثان ١/ ٣١ رقم ١٢ (ج ٢)، ١٣ (ج ١، ٢، ٧)، ٤٧ (ستة مجلدات) ٧٠٩ (خمسة مجلدات)، برلين الشرقية ١٩٠٥، رامبور ١/ ١٦٢، تفسير ٥ (٢٣١ ورقة، ١١٦٥ هـ)، شهيد على ٣٨ (ج ١، ٣٣٨ ورقة، فى القرن الثامن الهجرى)، ٣٩ (ج ٢، ٣٣٦ ورقة، فى القرن الثامن الهجرى)، ٤٠ (المجلد الأخير ٣٣١ ورقة، فى القرن التاسع الهجرى)، رئيس الكتاب ١١ (ج ١، ٢٨٨ ورقة، ٨٦٠ هـ) ١٢ (ج ٢، ٢٢٣ ورقة، ١١٥٧ هـ)، ١٣ (ج ٣، ٢٨٠ ورقة فى القرن التاسع الهجرى)، مراد ملّا ١٧ - ٢١، عاطف ٦٥٠، ابراهيم افندى ٣١٣ (٧٢٣ هـ)، فيض الله ٦٥١ (٤٥٣ ورقة فى القرن الثانى عشر الهجرى، جار الله ٢٧ (٢٤٧ ورقة، فى القرن التاسع الهجرى)، ٢٨ (٢٣١ ورقة، فى القرن التاسع الهجرى، ٢٩ (ج ٢، ٢٢٩ ورقة، فى القرن السابع الهجرى)، ٣٠ (١٤٨ ورقة، ٦٥٠ هـ)، ٣١ (ج ٤، ٢٣٢ ورقة)، ٣٢ (ج ١، ٤٥٠ ورقة، فى القرن الثامن الهجرى).
وحققه كلسلى رفعت باستنبول ١٣٣٥ - ١٣٣٨ هـ. وطبع بالقاهرة ١٣٤٧ هـ.
٢ - «أصول الفقه» القاهرة ثان ١/ ٣٧٨، أصول الفقه ٢٦ (ج ٢، ١٦٥ ورقة)، ١٩١ (ج ١، ١٥٠ ورقة، انظر: القاهرة، ملحق ١/ ٥٥)، ٢٢٩ (٣٣٢ ورقة، ٧٤٨ هـ، انظر فهرس معهد المخطوطات العربية ١/ ٢٤٠)، الأزهر ٧/ ٢٠.
٣ - «شرح الجامع الكبير» (انظر: شرح رقم ١ للجامع الكبير لمحمد بن الحسن الشيبانى).
٤ - «شرح المختصر» للطحاوى.
٥ - «اختصار اختلاف الفقهاء» للطحاوى، انظر رقم ١٩ فى هذا الفصل.','3','103',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (860,'٦ - «شرح أدب القاضى» للخصّاف.

<span data-type="title" id=toc-2035>٢٤ - أبو اللّيث السّمرقندى</span>
هو أبو الليث نصر بن محمد بن أحمد السمرقندى، كان فقيها حنفيا ومفسرا، ومتصوفا. توفى سنة ٣٧٣ هـ/ ٩٨٣ م، وقيل ٣٧٥ وقيل ٣٩٣ هـ.

<span data-type="title" id=toc-2036>أ- مصادر ترجمته:</span>
الجواهر للقرشى ٢/ ١٩٦، تاج التراجم لابن قطلوبغا ٥٨ - ٥٩، الأعلام للزركلى ٨/ ٣٤٨، معجم المؤلفين لكحالة ١٣/ ٩١، شاخت فى دائرة المعارف الإسلامية (الإنجليزية) ١/ ١٣٧، بروكلمان ١/ ١٩٦ رقم ٦.
H. Ritter, Turkiyat VII- VIII, ١٠; Flugel, Hanef. ٣٠٢

<span data-type="title" id=toc-2037>ب- آثاره:</span>
١ - «تفسير القرآن» برلين ٧٣٤ (ج ٢، ١٧٥ ورقة، ٥٥٦ هـ)، ٧٣٥ (ج ٢، ٢٧٠ ورقة، حوالى ٧٠٠ هـ)، ٧٣٦ (قطعة، ١٠٨ ورقة، حوالى ٦٥٠ هـ)، ٨٢٤ (ج ٣، ٢٣٤ ورقة، ١٠١٢ هـ)، ميونيخ ٧٨ (ج ٣، ١٩٩ ورقة)، ليدن ١٦٥٣ (ج ١، ١٦٢ ورقة، انظر: فورهوف ٣٥٣)، المتحف البريطانى، الملحق ٩٧ - ٩٨، مخطوطات شرقية ٢٩٢٢ (ج ٢، ١٢٧ ورقة، ٧٦٤ هـ)، ٢٩٢٣ (ج ٣، ١٩٨ ورقة، ٦٩٢ هـ)، عبد الوهاب بتونس ١/ ٥١ رقم ١٤٤ (ج ٤) ٢٢٠ ورقة)، أيا صوفية ١٤٨ (ج ١، ٣٦٤ ورقة، حوالى ٧٠٠ هـ)، ١٤٩ (ج ٢، ٥٨٠ ورقة)، كوبريلى ٧٢ (٦٩٥ ورقة، فى القرن التاسع الهجرى)، نور عثمانية ٢٢٨ (٦١٢ ورقة، ١٠٨٩ هـ)، ٢٢٩ (٥٤٦ ورقة، ٩٩١ هـ) ٢٣٠ (ج ١، ٣٢٤ ورقة، ١١٣٩ هـ)، ٢٣١ (ج ٢، ٣٢٦ ورقة ١١٣٩ هـ)، ٢٣٢ (ج ٣، ٣١٦ ورقة، ١١٣٩ هـ)، قليج على ٥٠ (٤٠١ ورقة، ١١٠٢ هـ)، سليم أغا ٥٧ (ج ١، ٣٠٥ ورقة، ٧٣٥ هـ)، وهبى ١٢٧ (ج ٣، ٢٥٦ ورقة، فى القرن السادس الهجرى)، ١٤٥ (ج ١، ٢٥٦ ورقة، القرن السابع الهجرى)، سراى، أحمد الثالث ٣٤ (٥٦٢ ورقة، ٩٩٩ هـ)، ريفان كوشك ١١٧ (ج ١، ١٨٤ ورقة ٧٢٤ هـ، فهرس ١/ ٤٤٦)، مراد ملّا ٥٧ (الآن فى متحف الأوقاف التركية) بشير أغا ٢٠ (ج ٢، ٥٣٨، ٩٧٣ هـ)، حسن محمود ٥٨ (ج ١، ٢١٨ ورقة، فى القرن الثامن الهجرى)، ١٨٠ (قطعة، ٨٠ ورقة، ٨٢٢ هـ)، ٢١٠ (قطعة ١٠٦ ورقة، ١١١٤ هـ)، بايزيد ٢٩٨ -','3','104',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (861,'٣٠١ (من جزء ١ - ٣)، الجامع الكبير فى بورسة أولو جامع، تفسير ٤، حسن چلبى فى بورسة تفسير ٥، حراچى زاده، تفسير ٣٥، ١١٤ رشيد أفندى بقيصرية ٣٩ (؟ ) ٦٥ (ثلاثة مجلدات)، الخالدية بالقدس، تفسير ٣٩ (٦٧٣ هـ) (١٢٢)، القاهرة ثان ١/ ٣٧ تفسير ٣ - ٦، ٧ م، ٨ م وأيضا ١/ ٥٠ (انظر: فهرس معهد المخطوطات العربية ١/ ٢٨) /، مجموع ٣٧٨ (قطعة واحدة)، الظاهرية ٥٢١ (ج ١، ١٦١ ورقة، فى القرن العاشر الهجرى)، ٥٢٢ (ج ٢، ٣٥٩ ورقة، ٨٨٤ هـ، انظر: شاخت ٣/ ٣٣)، ٦٢٤٩ (قطعة من ورقة ١ - ٥٧)، ٧٧٣٢ (٣٦٣ ورقة، انظر: عزة حسن ١/ ٢٠٥ - ٢٠٦)، (قوقاز المتحف الأسيوى ببطرسبورج ٩٢٦، الموصل ٢٣١/ ٦٩، رامبور ١/ ٢٤، قوله ١/ ٤٧) (١٢٣) عاطف ١١٤ (٣٩٧ ورقة)، ١١٥ (٧٠٢ هـ)، ١١٦ (٧٨٠ هـ)، ١١٧ (٧٠٣ هـ)، مكتبة جامعة إستنبول ١٨١٢ (٨٤٤ هـ)، ١٧٦٨ (١٠٠٨ هـ)، ١٨٨٣، ١٨٩١، مدرسة ينى ٩ - ١١، قليج ٥١ (٢٣٩ ورقة)، سراى كغشلار ٥٩٦ (ج ٥، ٢٩٣ ورقة، ٨٥٥ هـ انظر: فهرس ١/ ٤٤٦)، قسطمونى ٩٣٣ (٧١٩ هـ)، تشيستربيتى ٣١٨٠ (المجلد الأخير، ١٦٧ ورقة، ٧٢٤ هـ)، ٣٦٦٨ (٣٤٥ ورقة، ٦٧٢ هـ)، ٣٦٨٨ (ج ٢، ٣٥١ ورقة، ٧٦٣ هـ)، منجانا ٨ رقم ٩٤١، الأزهر ١/ ٢١٢، تفسير ١٤٤ (جزء ١ - ٢، ١١٣٥ هـ، ١٢٢٩ هـ)، تيمور ١/ ٢٠، ١٨٥ - ١٨٦، تفسير ١٣٦، ١٥٤ (ج ٣، ٩٩١ هـ)، ١٨٢ (ج ٣، ٨٩٣ هـ)، ٥٢٤، بلدية الإسكندرية ١٠٦٦ ب، ١٢٣١ ب، ٣٦٧٣ ج، الأوقاف ببغداد ٢٢٣٤ (٩٤٧ هـ)، انظر: (طلس ٢٢).
وترجمة إلى التركية أحمد بن محمد بن عبد الله بن عربشاه (المتوفى ٨٥٤ هـ/ ١٤٥٠ م انظر بروكلمان ٢/ ٢٨) سيرز ٧٩ - ٨٢ (أقسام متفرقة)، حسن محمود ٥٠ (٣٣٧ ورقة، ١٠٠٣ هـ)، ٥١ - ٥٥، ٦٢ - ٦٨، ١١٠ - ١١١، دوغملو وبابا ٨ (ثلاثة مجلدات كاملة)، شهيد على ٢٨٨٠ - ٢٨٨٢ (٩٦١ هـ)، فاتح ١٦٠ - ١٦٣، ١٦٤ - ١٦٦ (١٠٦٩ هـ)، صالحة خاتون ٤١ - ٤٤، سليم أغا ٤٤، هاراسوفيتس فى فيزبادن بألمانيا الغربية (٤١٠ ورقة، ٨٤٨ هـ، انظر:
R. Hartmann, OLZ ١٩٢٤/ ٤٩٧ - ٥٠٣. (J. Schacht, OLZ ١٩٢٧/ ٤٧٤ ff.
