(١٢٢) ذكر بروكلمان فى الملحق ١/ ٣٤٧، هذا الرقم وبجانبه علامة استفهام.
(١٢٣) تحذف مخطوطات: الإسكوريال ١٤٣٤، وهبى ١٦٠٧، وبشير أغا ٢٠، بوهار ١٥٠ التى ذكرها بروكلمان.','3','105',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (862,'١١٦٥ هـ)، ١٣٨ - ١٣٩ (ج ١ - ٢، ٤١٨ ورقة، ٣٧٩ ورقة)، انظر» -٢ Osm.Muell.II ,١٧١ - ١٧٢. (١٢٤) خزانة الفقه»:
«وهو موجز لفروع الفقه الحنفى، جوتا ٩٩١ (١٣٥ ورقة، ٩٩٧ هـ)، ٩٩٢ (١٥٣ ورقة)، ٩٩٣، (١٣١ ورقة، ١٠٩٨ هـ)، برلين ٤٤٤٤ (١١٠ ورقة، فى القرن الحادى عشر الهجرى)، ٤٤٤٥ (من ورقة ١٣٢ - ١٧٩، ٩٣٠ هـ)، برلين الغربية ٢٠٢٥، باريس ١٨٢٦ (٩١ ورقة، ١١٥٢ هـ)، جاريت ١٦٧٠ (١٠٢ ورقة، ٩٢٢ هـ)، ١٦٧١ (١٣٦ ورقة، ١٠٨٧ هـ)، بلدية الإسكندرية ٧٤٠ د (٧٧٣ هـ) ١٢٠٤ ب (١٣١٦ هـ)، ٣٠٨٣ ج (١١٤٩ هـ)، القاهرة ثان ١/ ٤١٨، فقه حنفى ١٦١، ١٦٢، ٦٣، ١٣٣٥، ١٥١٢، ميونيخ ٢٤٨ (١٢٥) (٢٤٢ ورقة، ١١٣٤ هـ)، كيمبردج ٣٧٢٥/ ٧ (من ورقة ١ - ١٠٢، انظر: الملحق رقم ٤٢٦)، المتحف البريطانى مخطوطات شرقية ٥٨٠٤/ ١ (فى القرن العاشر الهجرى، انظر:
قائمة وصفية ٢٣، الفاتيكان، فيدا ٢٥١ (٨٨ ورقة، قبل ٩٨٥ هـ)، أيا صوفية ١١٤٨ (١٣٣ ورقة، ١٠٣٨ هـ)، جامع ينى ٤١٦ (١١٢ ورقة، ١٠٤٦ هـ)، ٦٧٧/ ٤ (ورقة ٢٢٨ - ٢٨٣، ٩٧٣ هـ)، مراد ملّا ٨٢٣ (٨٧ ورقة، ١٠٧٥ هـ)، ٨٢٤/ ١ (من ١ - ١٠٤ ب، ١٠٠٣ هـ)، داماد إبراهيم ٦٩٦/ ٣ (من ١١٥ ب- ١٦٢ ب، فى القرن الثانى عشر الهجرى)، قليج ٤٦٦ (٢٥٠ ورقة ١١١٠ هـ)، عبد الوهاب بتونس ٤/ ٢٣٤ رقم ٢٣٥٩/ ١ (من ورقة ١ - ٧٤، ١١٨٥ هـ)، (المتحف الأسيوى ببطرسبورج ١٠٥)، رامبور ١/ ١٩٠، فقه ١٧٣، الخالدية بالقدس ٤٢، ٦٦ - ٦٧، الموصل، ١٧٣ - ١٧٥، آصفية ٢/ ١٠٨٤ رقم ٦٤٣، ١٠٩٨، فقه ٣٦٣ (؟ )، شهيد على ٧٧٢ (٩٦٧ هـ)، حكيم أوغلو ٤٢٥/ ١ (من ورقة ١ - ١٣٣، ٨٥٣ هـ)، ٣٣٠ (٨٢ ورقة، ١٠١١ هـ)، فيض الله ٧٢٤ (٧٨ ورقة، ٩٩٢ هـ)، جار الله ٩٢٢ (٨٨ ورقة، فى القرن الثالث عشر الهجرى)، ٦٣٨ (٨٧ ورقة)، ٦٣٨ (٥٥ ورقة، ٨٤٧ هـ)، كوبريلى ٥٥٩/ ٢ (٨٣ ورقة، ٩٧٩ هـ)، سراى أحمد الثالث ٨٨٧ (١١٠ ورقة، ٧٦١ هـ، انظر: فهرس ٢/ ٣٩١)، عاطف ٧٦٢/ ٢ (من ١٢٣ أ- ٢٠٢ ب، فى القرن الحادى عشر الهجرى)، ١٠٣٠/ ١ (من ١ - ٨٩ أ، فى القرن الحادى عشر الهجرى)، قليج ٤٦٦/ ٢ (من ١٩٣ ب- ٢٥٠ أ- فى القرن الحادى عشر الهجرى)، الجامع الكبير فى بورسة ١١٤٧ (١١٣ ورقة)، قسطمونى ٩٦ (٩٠ ورقة، فى القرن العاشر الهجرى)، الإسكوريال ١٤٣٤/ ٢ (من ١٩١ أ- ٣١٤، ٩٥١ هـ)، الأزهر ٢/ ١٤٧ (تسع نسخ)، منجانا ٥٣١ رقم ١٠٢٧/ ٢ (٦٠ ورقة، ١٠٩٠ هـ)، طشقند ٣٢١١ (انظر: مجلة معهد المخطوطات العربية ٦/ ٣٢٤) /،
_________
(١٢٤) ذكر بروكلمان أن النص العربى طبع بالقاهرة ١٣١٠ هـ وكذلك ذكر شاخت فى دائرة المعارف الإسلامية (الإنجليزية) ١/ ١٣٧ والصحيح أنه لم يطبع بعد.
(١٢٥) ذكر بروكلمان أن هذا الرقم هو ٢٧٨ والصحيح كما ذكرنا.','3','106',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (863,'وطنطا (انظر: مجلة معهد المخطوطات العربية ٣/ ٢٦٤)، والرباط ١٢٦٦ (١٤٠ ورقة، ٩٧٦ هـ، انظر:
فهرس ١/ ٣٨٠)، وأخرج قسما منه كلنبرج:
J. H. Callenberg, Juriscirca Christianos Muhammedanici Particulae ... Halae Magd. ١٧٢٩.
٣ - «الفتاوى من أقاويل المشايخ» عبد الوهاب بتونس ٤/ ١٧٩، رقم ٢٢١١، (١٨٦ ورقة، ١٠٥٥ هـ)، ٤/ ١٩٩ رقم ٢٢٧٩ (١٦٠ ورقة، ١١٣٩ هـ)، فاتح ٣٣٥٢ (ج ١، ١٩٥ ورقة، ٦١٩ هـ)، ٢٣٥٣ (ج ٣، ١٩٧ ورقة، ٦١٩ هـ).
٤ - «مختلف الرواية» وصل إلينا هذا الكتاب بروايتين مختلفتين:
أ- رواية قديمة ببايزيد ٢١٦٧ (١٩٦ ورقة، ٤٥٧ هـ).
ب- رواية علاء الدين العالم السمرقندى (المتوفى ٥٥٣ هـ/ ١١٥٨ م انظر بروكلمان ١/ ٣٧٤)، جامع ينى ٤٧١ (٢٣٠ ورقة، ٦٣٠ هـ)، عاشر أفندى ١١٧ (٢٦٠ ورقة، ٦٥٣ هـ)، أسعد ٩٦٥ (٣٧٩ ورقة ٦٥٢ هـ)، شهيد ٩٦٧ (٢٤٠ ورقة، سنة ٦٨١ هـ)، ٩٦٨ (٢٣٠ ورقة ٦٥٤ هـ)، فيض الله ٩٥٣ (٤٠٨ ورقة، ٦٦١ هـ)، بايزيد ٢٣٠٤ (٣٢٥ ورقة، ٩٦٧ هـ)، السليمانية ٦٠٤ (٢٩٥ ورقة، ٧٦٥ هـ)، جار الله ٨٧٤ (١٤٦ ورقة، فى القرن الثامن الهجرى)، نور عثمانية ١٣٧٣ (٢١٠ ورقة، ٦٦٩ هـ)، كوبريلى ٦٥٠ (٢٥٩ ورقة، ٧٣٠ هـ)، حراچى زاده فى بورسة فقه ٢٠٦، الأزهر، فقه حنفى ٢٧٩٦ (١٢٢ ورقة، ٨٤٤ هـ) تيمور، فقه ٥٣١ (٢٧٩ ورقة، ٦١٤ هـ)، القاهرة ثان ١/ ٤٦١، فقه حنفى ١١٩ (٧٣٣ هـ)، ٤٥٨ (٢٤٨ ورقة، ٦٦٩ هـ)، باريس ٨٢٥/ ١ (من ورقة ١ - ٣٢١، ٥٩١ هـ انظر: فايدا ٤٩٣)، المتحف البريطانى ١٩٣، إضافات ٧٢٦٩ (٢١٨ ورقة، ١١١٢ هـ)، ملّا چلبى ٧٠ (١٧٤ ورقة، ٧٥٨ هـ) متحف الآثار باستنبول ٦٣٥ (٢٦٤ ورقة، ٥٧٥ هـ).
ج- وعليه حاشية لصفى الدين عمر الحنفى، مكتبة جامعة إستنبول ٥٤٠٨ (انظر شاخت ٣/ ٣٣)، الخالدية بالقدس ١، اختلاف ٦، انظر:
British Mus. Quart IV ٨.?
٥ - «عيون المسائل فى الفروع»: (تطابق رقم ١٥ عند بروكلمان):
الزيتونة بتونس ٤/ ١٧١ رقم ٢١٨٩ (١٥٠ ورقة، ١٢٠٨ هـ)، ٤/ ١٩٩ رقم ٢٢٧٩ (١٦٠ ورقة)، داماد إبراهيم ٦٩٧/ ١ (ورقة ١ - ٧٩، ٩٠٠ هـ)، ٧٢٥/ ٢ من (٢٥١ أ- ٣٤٩ ب، فى القرن الحادى عشر الهجرى)، لاله لي ١١٠٨ (١٢٩ ورقة، حوالى ١٠٠٠ هـ)، جار الله ٨١٧ (٢٣٦ ورقة، ١١٠٤ هـ).','3','107',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (864,'ونشره عبد الرزّاق القادرى بحيدرآباد- مطبعة لجنة نشر العلوم الإسلامية ١٩٦٠ م.
شروح عيون المسائل: أ- شرح للعلاء السمرقندى محمد بن عبد الحميد (المتوفى ٥٥٣ هـ/ ١١٥٨ م انظر بروكلمان ملحق ١/ ٦٤١)، رامبور ١/ ٢٠٨، فقه ٢٧٤ (١٦٧ ورقة)، الأزهر ٢/ ١٩٢، فقه حنفى ١٩٨٠ (٢٩٦ ورقة ٦٥٣ هـ).
ب- شرح لمحمد بن عمر بن عربى الجاوى (المتوفى بعد سنة ١٨٨٨ م انظر بروكلمان ملحق ٢/ ٨١٣) طبع بالقاهرة ١٣٠١ هـ، ١٣٠٣، ١٣٠٨ هـ، وبمكة ١٣١١ هـ.
٦ - «النوازل فى الفروع» وهو مجموعة من آراء محمد بن مقاتل الرّازى (المتوفى ٢٤٨ هـ/ ٨٦٢ م)، ومحمد بن شجاع الثلجى (المتوفى ٢٥٧ هـ/ ٨٧٠ م) ومحمد بن سلمة (المتوفى ٢٧٨ هـ/ ٨٩١ م) ونصير بن يحيى البلخى (المتوفى حوالى ٢٥٠ هـ/ ٨٦٤ م. انظر الجواهر للقرشى ٢/ ١١٧، ٢٠٠)، و (محمد) بن محمد بن سلّام البلخى (المتوفى ٣٠٥ هـ/ ٩١٧ م، انظر: الجواهر للقرشى ٢/ ١١٧)، وأبى بكر محمد بن أحمد الإسكاف البلخى (المتوفى ٣٣٣ هـ/ ٩٤٥ م، انظر: الجواهر للقرشى ٢/ ٢٣٩، ومعجم المؤلفين لكحالة ٨/ ٢٣٢) وعلى بن أحمد الفارسى وأبى جعفر محمد بن عبد الله بن محمد البلخى (المتوفى ٣٦٢/ ٩٧٢ م، انظر: الجواهر للقرشى ٢/ ٦٨)، انظر: كشف الظنون لحاجى خليفة ١٩٨١.
المخطوطات: فاتح ٢٤١٤ (٢٧٩ ورقة، ٩٨٥ هـ)، سراى، أحمد الثالث ٨٧٩ (٣١٤ ورقة، ٦١٧ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٧٦)، رئيس الكتاب ٣٩٥/ ١ (من ١ - ١٣٨ ب، ١٠٩٠ هـ)، عاطف ١١٧٠ (٢٢٧ ورقة، فى القرن التاسع الهجرى)، ١١٨١ (٣٨٠ ورقة، ١١٨٤ هـ)، مكتبة جامعة إستنبول ١٧٣٩ (٩٩ ورقة، ٩٠٠ هـ)، ٣٤٥٩ (٣٩١ ورقة، فى القرن السادس الهجرى، نسخة جميلة، انظر: فهرس معهد المخطوطات العربية ١/ ٢٧٦)، شهيد على ٩٣٥ (٣٢٢ ورقة، فى القرن السابع الهجرى)، داماد إبراهيم ٧٢٥/ ١ (٢٤٩ ورقة، فى القرن الحادى عشر الهجرى)، حكيم أوغلى ٤١٨ (٤٢٠ ورقة، ١١٠١ هـ)، رشيد باستنبول ٢٧٤ (١٩٨ ورقة، ٩٧٠ هـ)، جار الله ٩٦٠ (٢٤٤ ورقة، ٧٥٩ هـ)، كوبريلى ٦٨٣ (٣٧٠ ورقة، ١٠٨٣ هـ).
وهناك «مختار النوازل» الذى يختلف عن المخطوطات السابقة، ويوجد فى الأزهر ٢/ ٢٩١، فقه حنفى ٣١٠٥ (٢٢٤ ورقة، ١١٦٢ هـ)، القاهرة ثان ١/ ٤٧١، فقه حنفى ٥٦٥، ٥٦٦، ٩٦٦، حسن حسنى ٤٤٩ (١٩١ ورقة، ٩٨٥ هـ)، فاتح ٢٤٧٣ (١٤٤ ورقة، ١٠٦٩ هـ)، منجانا ٥٤٣ (١٤٤ ورقة ٩٦٢ هـ، فهرس ٥٣٢).','3','108',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (865,'٧ - «المقدمة فى الصلاة» (يطابق رقم ٥ فى ملحق بروكلمان) (١٢٦):
جوتا ٧٦٢ (٢٣ ورقة، ذكرت هناك مخطوطات أخرى)، برلين ٣٥٠٦، (١٢ ورقة، حوالى ٩٠٠ هـ)، ٣٥٠٧ (من ورقة ٢٦ - ٣٤، حوالى ١١٠٠ هـ)، باريس ١١٢١ (٥٠ ورقة، ٩٨٥ هـ)، ١١٢٢ (٢٦ ورقة، فى القرن الحادى عشر الهجرى)، ١١٤١/ ٢ (من ورقة ١٦ - ٨١، فى القرن العاشر الهجرى)، ١١٤٢/ ٢، (من ورقة ١٤ - ٦٧، ٩٩٥ هـ)، بلدية الإسكندرية ٢١٧٨ د، أيا صوفية ١٤٤٢ (٤٦ ورقة، فى القرن التاسع الهجرى)، ١٤٤٣/ ١ (من ١ - ٥٩ أ، فى القرن العاشر الهجرى)، ١٤٤٥ (٢٤ ورقة، فى القرن التاسع الهجرى)، ١٤٤٦/ ١ (من ١ - ٤٩ أ، فى القرن التاسع الهجرى)، ١٤٤٧ (٣٢ ورقة، ١٠٩٨ هـ)، ١٤٤٨/ ١ (من ١ - ٥٩ أ، فى القرن العاشر الهجرى)، القاهرة ثان ١/ ٤٦٥ (ذكر ٣٥ مخطوطة)، ميونيخ ١٥٧ (٣٦ ورقة، مع ترجمة تركية بينالسطور)، حسن عبد الوهاب بتونس ٤/ ٢٢٥ رقم ٢٣٤٩/ ١ (١١٦٥ هـ)، الجزائر ٧٦٤ (١١٧٦ هـ) (قوله ١/ ٣٩٦ - ٣٩٧)، رامبور ١/ ٢٥٣، فقه رقم ٥٦٧، (توبنجن ٢٠٩)، المتحف الأسيوى ببطرسبورج قوقاز ٩٤٣، المتحف الأسيوى ببطرسبورج بخارى ٩٤٠)، الظاهرية فقه حنفى ٧٣، (سباط ٣٨٤/ ٢)، سراى (انظر: فهرس ٢/ ٣٩٢ - ٣٩٥، حيث ذكر ١٤ مخطوطة)، رئيس الكتاب ١٢٠٧/ ٥ (من ٤٧ أ- ٨٧ أ، ٩٧١ هـ)، لاله لي ١٢٨٢/ ٢ (٨٩ ورقة، ١٠٦٨ هـ)، برتو باشا ١٧٧ (٣٧ ورقة، ١١٠٣ هـ) رشيد باستنبول ١٠٢٥/ ١١ (من ورقة ٢٢٦ - ٢٦٣، ١٠٢٦ هـ)، جار الله ٨٧٨/ ٣ (ورقة ١٦٩ - ٢٠٥)، ٨٨٦ (٦٩ ورقة، ٩٥٥ هـ)، فيض الله ٢١٦٥/ ١ (من ١ - ١٥ ب فى القرن الثانى عشر الهجرى)، سليم أغا ٣٩٣/ ١ (من ١ - ٢٨ ب، ١١٢٩ هـ).
وله ترجمة بالتركية، أياصوفية ١٤٤٤ (٢١٥ ورقة، ٨٧٤ هـ).
شروح المقدمة: أ- «تقدمة جبريل بن حسن بن عثمان الجنجوى (المتوفى ٧٥٢ هـ/ ١٣٥١، انظر: كشف الظنون لحاجى خليفة ١٧٩٦)، برلين ٣٥٠٨ (من ٧٩ - ١١٧، حوالى ٨٠٠ هـ)، ميونيخ ١٥٨ (١٢٧) (١٩٤ ورقة، ١٠٣٤ هـ)، بلدية الإسكندرية ١٩٥٨ (١٠١٠ هـ)، سليم أغا ٣٩٣/ ٢ (من ٢٩ ب- ١٨٥ ب، ١١١٥ هـ)، (بولونيا ٢٥٥، ١)، ويوجد بمكتبة حسين محفوظ بالعراق (١٠٨٦ هـ/ انظر: مجلة معهد المخطوطات العربية ٦/ ٢٥).
_________
(١٢٦) وترجمها إلى الفرنسية زايا شكوفسكى بناء على مخطوطة أيا صوفيا ١٤٥١:
Letraitearabe Mukaddimatd''Abou- l- lait As- Samarkandien Version Mamelouk- Kiptchak, by A. Zajaczkowski Warszawa ١٩٦٢.
وانظر كذلك برنر W.M.Brinner ,in: JAOS ٨٤ /١٩٦٤ /٤١٨ - ٤١٩.:
(١٢٧) يصحح الرقم عند بروكلمان.','3','109',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (866,'ب- «التوضيح» لمصطفى بن زكرياء بن آي دغمش القرمانى (المتوفى ٨٠٩ هـ/ ١٤٠٦ م، انظر الضوء اللامع للسخاوى ١٠/ ١٦٠)، برلين ٣٥٠٩ (١٨٧ ورقة، حوالى ١٢٠٠ هـ)، باريس ١١٢٤ (٨٦ ورقة، فى القرن العاشر الهجرى) ١١٢٥ (١٠٣ ورقة، فى القرن العاشر الهجرى)، ١١٢٦/ ١ (من ورقة ١ - ١١٨، ٩٩١ هـ)، ١١٢٧ (١٨٣ ورقة، ١٠٠٥ انظر فايدا ٦٨٤)، جاريت ١٩٤٤ (٩٢ ورقة، فى القرن العاشر الهجرى)، بلدية الإسكندرية ١٠٨٤ ب (٣٧ ورقة، ٨٦٥ هـ)، ١٩٧٤ (١٠٣٠ هـ)، ٢٢٢٧ (١١١٦ هـ) ٣٥٣٢ (١١١٧ هـ)، بنگيبور ١٩/ ٩٨ رقم ١٥٩٨ (١٠٨ ورقة، قبل ٨٣٨ هـ)، ميونيخ ١٥٩ (٢٢٣ ورقة)، كيمبردج، إضافات ٧٥٧ (٢٠٢ ورقة، ٩٩٨ هـ) انظر: فهرس ١/ ٥٣)، الفاتيكان، فيدا، بورجيانى ٢٩ (١٠٥ ورقة، فى القرن العاشر الهجرى)، قليج ٤١٧ (حوالى ١١٠ ورقة، فى القرن العاشر الهجرى)، السليمانية ٧٧٢/ ٣ (من ٨١ أ- ١٨٦ أ، فى القرن العاشر الهجرى)، الزيتونة بتونس ٤/ ٩٠ رقم ١٩٥٥ (١٥٧ ورقة، ١١٩٠ هـ)، (قوله ١/ ٣١٤، ١٥، رامبور ١/ ٢١٠، فقه ٢٨٩، جوتنجن ٣/ ٣٢٨ مخطوطات عربية ٤٤)، المتحف البريطانى، مخطوطات شرقية ٥٦٩٠ (١٥٥ ورقة، ٩٥٤ هـ، انظر: قائمة وصفية ١٥)، (بولونيا ٢٠٣ - ٢٠٤)، الظاهرية، فقه حنفى ٧٥ - ٧٧، (الخالدية بالقدس ٢٢، ٧٢)، سراى أحمد الثالث ٧٨٢ (٨٦ ورقة، ٨٥٥ هـ)، ١١٥٠ (٢٢٤ ورقة، ٨٥٢ هـ)، سراى كغشلار ٧٧٩ (٢٠٦ ورقة انظر فهرس ٢/ ٣٩٥)، حفيد أفندى ٩ (٥٧ ورقة ١٠٥٣ هـ)، مكتبة جامعة إستنبول ٢٦٣٣، ٥٤٢٧، قونية يوسف ٤٩٩٦ (١٢٧ ورقة، فى القرن العاشر الهجرى)، الكتانى الرباط ٧٢٥ (١٤٢ ورقة)، برلين (الشرقية) ١٩٩٣.
ج- شرح لحسن بن حسين الطولونى (المتوفى ٩٠٩ هـ/ ١٥٠٣ م)، بلدية الإسكندرية ٥٢٥١ ج (٢٦ ورقة، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٥).
د- شرح لطف الله النسفى الفاضل الكيدانى (حوالى ٧٥٠ هـ/ ١٣٤٩ م انظر بروكلمان ٢/ ١٩٨)، الموصل ١٠٧، ٤٠.
هـ- شرح لمحمد بن إبراهيم بن محمد الحلبى (المتوفى ٨٧٩ هـ/ ١٤٧٤ م، انظر: معجم المؤلفين لكحالة ٨/ ٢١٧)، ميونيخ ١٦٠ (٣٠ ورقة) (مكرم ٣٤).
و- شرح لشمس الدين محمد بن حسام الدين القهستانى (المتوفى ٩٦٢ هـ/ ١٥٥٥ م، انظر: معجم المؤلفين لكحالة ٩/ ١٧٩)، جاريت ١٩٥٨ (٤٢ ورقة، ١١٠٢ هـ).
ز- «قطرات الغيث على مقدمة أبى الليث» لمحمد بن محمد قاضى زاده (المتوفى ١٠٤٤ هـ/ ١٦٣٤ م انظر بروكلمان ٢/ ٤٣٤)، شهيد على ٩٠٦ (٤٠ ورقة).','3','110',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (867,'ح- «مخدرات الفهوم فيما يتعلق بالتراجم والعلوم» لأبى المرشد المالكى المغربى، الجزائر ٧٥٧/ ٢، (من ورقة ١٥ - ٢٩، ١١٨٦ هـ).
ط- شرح لمؤلف/ مجهول، جاريت ١٨٩٠ (٦٧ ورقة، ٨٥٣ هـ)، وآخر مجهول المؤلف أسعد أفندى ١٥٨١/ ١٠ (من ٢٠٠ ب- ٢١٣ ب، ١٠٧٨ هـ)، وثالث بعنوان: «الجوهرة المثمنة»، بلدية الإسكندرية ٣٩٢١ (١١٣٩ هـ)، ورابع فى مشهد بإيران ٥/ ٤٥٧، فقه ٧٢٣ (٣٥ ورقة).
٨ - «بيان عقيدة الأصول» (يطابق رقم ٦، ٧ عند بروكلمان):
يبحث فى العقيدة، المكتب الهندى ٢٩٠٦ (تسع ورقات مع ترجمة بين السطور بلغة الملايو، انظر: لوط ٣٨١)، ٢٦٨٦ (من ورقة ١ - ١١، انظر: لوط ١٠٤٦)، (باتافيا، الملحق ١٦٦ - ١٦٧)، ليدن، فورهوف ٤٤ - ٤٥ (ذكر هناك ١٣ مخطوطة)، القاهرة ثان ١/ ١٨٢، كلام ٢٣ م، باريس ٤٨٠٨ (من ورقة ١٩١ - ٢٠٠ انظر: فايدا ٢٦٥)، ميونيخ ٢١٢ (من ورقة ١ - ١١، فى القرن الثالث عشر الهجرى)، توبنجن ٢٠٠ (من ورقة ١ - ١٣ مع هامش بلغة جاوه).
A. W. Juynboll, Tijdschr. voorde Taal-, Landen Volkenk. von Ned. lndie, Ser IV, vol. IV (١٨٨١) ٢١٥ - ٢٣١, ٢٦٧ - ٢٧٤.
وهو تحقيق مع ترجمة إلى لغة الملايو، ليدن، انظر: فورهوف ٤٥.
وله شرح لمؤلف مجهول بعنوان: «بهجة العلوم»، برلين ١٩٤٥ (من ٢٣ أ- ٣١ أ)، المكتب الهندى ٢٥٨٧ (من ورقة ٢ - ٢١، انظر لوط ٤٧٠)، المتحف البريطانى ٨٧١، إضافات ٧٦١٢/ ٢ (من ورقة ١١ ٣٣ فى القرن الثانى عشر الهجرى)، ست مخطوطات انظر: فورهوف ٤٥).
٩ - «بستان العارفين» (يطابق رقم ٨ فى ملحق ١ عند بروكلمان):
وهو كتاب ثقافى يتضمن موضوعات مختلفة فى الدين والفلسفة والشريعة وغير ذلك، وينقسم إلى ١٥٥ فصلا قصيرا، برلين ٨٣٢٢ (١٥١ ورقة، ١١٤٧ هـ)، ٨٣٢٣ (١٢٤ ورقة، حوالى ١١٠٠ هـ)، جاربث ٩٢٠ (١٥٩ ورقة، ١٢١٤ هـ)، ٢١٣٣/ ١ (١١٢٤ هـ)، كمبردج ١٢٧) ١٥٧ Qq ورقة، انظر: فهرس ١/ ٢١)، وأيضا رقم ٥٩ (٨٣ ورقة، انظر: كتالوج ٢/ ٣١)، باريس ٤٨٠٣ (مختارات، من ورقة ٣ - ١٦، فى القرن الحادى عشر الهجرى)، ٤٨١٠ (٣٦ ورقة، ٨٧٠ هـ)، القاهرة ثان ١/ ٢٧٢، تصوف ١٤٩١، مجموع ٨٢، ١٢٦ م، عليگره ٢٩٧٦٣/ ٣٠ (١٢٧) ورقة، بنگيبور ١٣/ ٢ - ٤ رقم ٨٢١ (٦٣ ورقة، فى القرن التاسع الهجرى)، ٨٢٢ (١٣٩ ورقة، ١١٩٥ هـ)، أيا صوفية ١٦٨٣/ ١ (من ورقة ١ - ١٢٨)، ١٦٨٤ (٧٦ ورقة، ١١٢٤ هـ)، ١٦٨٥/ ١ (٢٣٢ ورقة، ٩٧٥ هـ)، ١٦٨٦ (١٦٥ ورقة، ٥٥٧ هـ)، نور عثمانية ٢٢٨٣ (٨١ ورقة، ١٠٣٩ هـ)، ٢٢٨٤ (٨٠ ورقة، فى القرن الحادى عشر الهجرى)، فاتح ٢٥٦٣ (٢٢٥ ورقة)، قليج ٦٩٧ (حوالى ١٢٠ ورقة، فى القرن العاشر الهجرى)، السليمانية ٦٩٢ (من ورقة ١ -','3','111',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (868,'١٢٤، ١٠٦٦ هـ)، سليم أغا ٤٦٨ (١٠٧ ورقة، ٩٥٦ هـ)، (المتحف الآسيوى ببطرسبورج ٥٥، ٩٢٤)، بومباى ١٦٦، فهرس ٢٤٤، فلورنسا ٣٠ (فهرس ٢٧١)، (بولونيا ٧٦، ٢٤٥/ ١) (١٢٨)، سراى، أحمد الثالث ١٥٥١ (١٧٤ ورقة، فى القرن العاشر الهجرى)، حراجى زاده ٧٥٧ (٨٤٠ هـ)، تشيستربيتى ٣٢٤٥ (٨٩ ورقة، ٦٣٩ هـ)، ليدن ٧٠٧ (٦٨ ورقة، فى القرن الرابع عشر الهجرى، انظر: فورهوف ٥٥)، ييل- ٢٤٩، (٩٢ ورقة، فى القرن الحادى عشر الهجرى، انظر نيموى ١٤٥٠)، العباسية بالبصرة ٧٥ (١٣٠ ورقة، ٨٩٤ هـ، انظر: فهرس ١/ ٧٨)، دار الكتب بالقاهرة ٢٣٠٦٨ (من ورقة ١ - ١١٥، ٧٣١ هـ)، وطبع باستنبول ١٢٨٩ هـ، وبكلكتا ١٨٦٨ م، وببولاق ١٢٨٩ هـ، وببومباى ١٣٠٤ هـ.
١٠ - «تنبيه الغافلين» (يطابق رقم ٩ فى ملحق ١ عند بروكلمان):
وهو نظرات فى الأخلاق والتقوى والحكم والمواعظ على أساس أحاديث النبى (صلّى الله عليه وسلم)، برلين ٨٧٣٥ (٢٥٦ ورقة، حوالى ١١٠٠ هـ)، ٨٧٣٦ (٣١٦ ورقة، حوالى ٩٠٠ هـ)، فيينا ١٨٣٧ (١٥٦ ورقة، ١٠٢٥ هـ)، ليبزج ١٥٩ (٩٣ ورقة)، ١٦٠ (من ٣ ب- ٥٦٠ أ، ١٠٠٠ هـ)، المكتب الهندى ٢١٤٧ (٢٧٦ ورقة، ١١٤٥ هـ، فهرس ١ رقم ١٤٧)، جاريت ١٨٨٩ (٢٧٧ ورقة، ١٠٨٤ هـ)، بلدية الإسكندرية ١٣٦١ (٧٦٦ هـ)، ٤٤٢٨ (١٢٦٩ هـ)، القاهرة ثان ١/ ٢٨١، تصوف ٣٤١، ٨٨٧، ٨٨٨، ٩٨٦، المتحف البريطانى مخطوطات شرقية ٥٧٦٤ (١٥٣ ورقة فى القرن الحادى عشر الهجرى، قائمة وصفية ١٩)، الفاتيكان، فيدا ٤٧٠/ ١ (من ورقة ١ - ٧٤، ٨٥٩ هـ)، الزيتونة بتونس ٣/ ١١٦ - ١١٧ رقم ١٤٩٣ (٢٢٠ ورقة، ٨١٥ هـ)، ١٤٩٦ (٣٣٩ ورقة)، مشهد بإيران ١/ أخبار ٧٨ (١٨٤ ورقة، ٩٥١ هـ)، بنگيبور ١٣/ ٤ - ٥ رقم ٨٢٣ (٢٢٨ ورقة)، ٨٢٤ (٢٩٦ ورقة، ١١٨٧ هـ)، أيا صوفية ١٧٤١ (٣٨٩ ورقة، ٨٤٣ هـ)، ١٧٤٢ (٢٨٢ ورقة، ٨٤٥ هـ)، فاتح ٢٥٩٧ (٢٠٨ ورقة، ٦٤٣ هـ)، السليمانية ٦٩٩ (٢٢٢ ورقة، فى القرن التاسع الهجرى)، سليم أغا ٤٧٩ (٤٢٨ ورقة، ١٠٠٠ هـ)، الجزائر ٨٧٢ - ٨٧٥، (بولونيا ١٠٨، المتحف الآسيوى ببطرسبورج قوقاز ٩٢٧، المتحف الأسيوى، ببطرسبورج بخارى ٢٩٥، الظاهرية ٦٦، ٥٨، القرويين/ بفاس، الرقم القديم ٦٦٤، ٧٣٩، ١٤٩٩، ١٥٠٠، بشاور ٦٩١، ١٠٢٠، ١٠٣٧)، آصفية ٢/ ١٥٩٠، مواعظ ٢، سراى، أحمد الثالث ١٣٦٨ (٣٢١ ورقة، فى القرن العاشر الهجرى)، ١٣٦٩ (٢١٠ ورقة، ٧٠٣ هـ)، ١٣٧٠ (٢٧٧ ورقة، فى القرن السابع الهجرى)، ١٣٧١ (فى القرن السابع الهجرى)، عاطف ١٥٤٥ (٢٦٨ ورقة، ١٠٦٧ هـ)، مراد ملّا ١٢٥٨ (٢١١ ورقة، ٧٨٦ هـ)، وهبى ٦٢٠ (٢٠٧ ورقة، ١٠٤٣ هـ)، مكتبة جامعة إستنبول ٢٤٦٩، ٢٦٦٦، ٢٧٣٦، قونية
_________
(١٢٨) تحذف مخطوطة فينا ١٨٣٧ التى ذكرها بروكلمان.','3','112',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (869,'يوسف ٤٩٩٠ (٢٦١ ورقة، ٥٩٥ هـ)، الجامع الكبير فى بورسه ٢٤٦٦/ ٣ (من ٨٥ ب- ١١٦ أ)، تشستربيتى ٣٠١٠ (١٧٩ ورقة، ٥١٥ هـ)، ليدن ١٧٧٩ (قطعة من ورقة ١٥٨ - ١٦٦).
وطبع ببومباى ١٨٨٤، وبالقاهرة ١٢٧٩ هـ، ١٣٠٢، ١٣٠٦، ١٣٠٩، ١٣١٣، ١٣٢٢، ١٣٢٦، ١٣٣٣، ١٣٣٤ هـ.
(ويوجد قسم منه بعنوان: «مسالك الواعظين، ومنهج الراغبين»، نابولى ٣٤ (فهرس ٢٦٣).
كما يوجد له مختصر من ترجمة إسبانية، باريس ٧٧٤/ ٤، ويوجد تنقيح له باللغة الفارسية فى برلين، مخطوطات فارسية ٢٦٥، باريس، فارسى ١٣٠٨، المتحف البريطانى ١٠٦٤، فيينا ٣/ ٢٠٨، ميسور ٥٣، ٢٩، ١٠٤، ٤٠، الجمعية الآسيوية بالبنغال ١٠٦٣).
١١ - «أسرار الوحى» أو «الوجه» وهو الحديث الذى جرى بين الذات العليا سبحانه وبين النبى (صلّى الله عليه وسلم) فى المعراج.
برلين ٢٦٠٠ (من ورقة ٥٥ - ٦٥، ١١٧٠ هـ)، ٢٦٠١ (من ورقة ٤٩ - ٦١) وله شرح بالفارسية لبرهان الدين محمد بختيار بخارى (من القرن الثانى عشر الهجرى)، أيا صوفية ٢٠١٦ (٨٦ ورقة، فى القرن الثانى عشر الهجرى، بخط المؤلف).
١٢ - «قرّة العيون، ومفرّح القلب المحزون» (١٢٩) (يطابق رقم ١١ فى ملحق ١ عند بروكلمان) وهو يتضمن العقاب عن الكبائر، طبع ببولاق ١٣٠٠ هـ (على هامش كتاب «مختصر التذكرة القرطبية للشعرانى») و ١٣٠٤ هـ (على هامش كتاب «الروض الفائق لشعيب بن مدين»).
١٣ - «شرح الفقه الأكبر لابى حنيفة» أسعد أفندى ١٥٨١/ ١٠ (من ٢٠٠ ب- ٢١٣ ب، ١٠٧٨ هـ).
١٤ - «دقائق الأخبار فى ذكر الجنة والنار» آصفية ١/ ٦٢٨، حديث ١٢٩، فاتح ٢٦١٩ (١٠٠ ورقة، فى القرن العاشر الهجرى)، لاله لي ١٣٦٢/ ٢ (من ٥٤ ب- ١٣٤ أفى القرن العاشر الهجرى) تشستربيتى ٣٦٧٤ (من ورقة ٢٦ - ٣٢، فى القرن الثامن الهجرى).
١٥ - «تأسيس الفقه» بلدية الإسكندرية ١٧٩٢ د. (يطابق رقم ١٦ عند بروكلمان)
_________
(١٢٩) يجب أن نبحث أصالة هذا الكتاب من جديد.','3','113',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (870,'١٦ - «شرع الإسلام» (باتنه ٢/ ٤٤٥، ٢٦٢١/ ٦). (يطابق رقم ١١ عند بروكلمان) ١٧ - «المعارف فى شرح الصحائف» وهو أدلة على وجود الله، رامبور ١/ ٣٢١ كلام ٣٠٣ (٨٢ ورقة)، عاطف ١٢٩١ (٢١٧ ورقة، ٩٢٧ هـ)، أسعد أفندى ١٢٥٣ (١٣٨ ورقة، ٦٨٨ هـ)، ١٢٥٤ (من ٥٩ ب- ١٣٦ ب، فى القرن الثامن الهجرى)، لاله لي ٢٢٩٢ (١٧٩ ورقة، فى القرن العاشر الهجرى)، طلعت، كلام ٢٨٥ (١٨٥ ورقة ٨٦٩ هـ، انظر: فؤاد سيد فى مجلة معهد المخطوطات العربية ٣/ ٢٣٤).
١٨ - «تاسيس النظر (المختلف بين أصحاب الفقه)» سراى، أحمد الثالث ١١٩٧ (١٦٦ ورقة، ٧٥٤ هـ انظر فهرس معهد المخطوطات العربية ١/ ٢٤٢).
١٩ - «رسالة المعرفة والإيمان» أسعد أفندى ١٥٠٠/ ٤ (من ١٧ أ- ١٨ أفى القرن الثانى عشر الهجرى).
٢٠ - «رسالة فى الحكم» وهى نظرات دينية فى المخلوقات والحوادث، كوبريلى ٢/ ١٤٩/ ٢ (من ٤٥ ب- ٨٣ أ، فى القرن الحادى عشر الهجرى)، نافذ باشا ٨٦٠ (٣٠ ورقة، فى القرن الحادى عشر الهجرى)، وهذه الرسالة هى:
«جامع الحكم» بمكتبة جامعة إستنبول ١٩٠ (من ورقة ١٥٨ - ٢٢٠، ١١٦١ هـ).
٢١ - «قوت النفس فى معرفة الأركان الخمس» راغب ١٤٥٤/ ٢ (من ١٦ - ٤٥ ب، ١١٠٠ هـ)، تشستربيتى ٣٧١٧ (٧١ ورقة، ٦٩٢ هـ).
٢٢ - «تحفة الأنام فى مناقب الأئمة الأربعة الأعلام» تشستربيتى ٣٩٣٦/ ١ (من ١ - ٩١ أ، فى القرن العاشر الهجرى).
٢٣ - «اللطائف المستخرجة من صحيح البخارى» مكتبة جامعة إستنبول ٢٠١٨ (٢٩ ورقة، ٩٦١ هـ).
٢٤ - «رسالة فى الفقه»؟
شبين الكوم بمصر (انظر: مجلة معهد المخطوطات العربية ٢/ ٢٦٧).','3','114',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (871,'<span data-type="title" id=toc-2038>٢٥ - البيهقى</span>
هو أبو القاسم إسماعيل بن الحسن (الحسين) بن على الغازى البيهقى، كان فقيها وأديبا، كتب حوالى سنة ٤٠٢ هـ/ ١٠١١ م.

<span data-type="title" id=toc-2039><span data-type="title" id=toc-2042>أ- مصادر ترجمته:</span></span>
الإرشاد لياقوت (لندن) ٢/ ٢٦١ - ٢٦٢، القاهرة ٦/ ١٤٠ - ١٤١، بغية الوعاة للسيوطى ١٩٤، الجواهر للقرشى ١/ ١٤٧، كشف الظنون لحاجى خليفة ١٠٢٤، ١٤٩٨، ١٥٩٣، ١٦٣٢، الأعلام للزركلى ١/ ٣٠٨، معجم المؤلفين لكحالة ٢/ ٢٦٤، بروكلمان ١/ ١٧٤ رقم ١١.

<span data-type="title" id=toc-2040>ب- آثاره:</span>
«الشامل» قضايا فقهية على شكل فتاوى، اعتمد على «المبسوط» و «الزيادات للشيبانى». القاهرة ثان ١/ ٤٣٨، فقه حنفى ٢٢٥ (ج ١، ٣٣٢ ورقة، ٥٢٨ هـ) ٨٢٣ (ج ٢، ٢٥٨ ورقة، ٥٢٩ هـ).

<span data-type="title" id=toc-2041>٢٦ - القدورى</span>
هو أبو الحسين، أحمد بن محمد بن أحمد بن جعفر، القدورى، ولد سنة ٣٦٢ هـ/ ٩٧٢ م، ودرس الفقه على محمد بن يحيى الجرجانى ثم أصبح شيخا للمدرسة الحنفية ببغداد، وكان عالما بالحديث، روى عنه الخطيب البغدادى. وتوفى سنة ٤٢٨ هـ/ ١٠٣٧ م ببغداد ودفن بدرب أبى خلف.
أ- مصادر ترجمته:
تاريخ بغداد للخطيب ٤/ ٣٧٧، وفيات الأعيان لابن خلكان (بولاق) ١/ ٢٦، الأنساب للسمعانى ٤٤٤ ب، اللباب لابن الأثير ٢/ ٢٤٧، الجواهر للقرشى ١/ ٩٣ - ٩٤، ٢/ ٣٣٦ - ٣٣٧، تاج التراجم لابن قطلوبغا ٥، البداية والنهاية لابن كثير ١٢/ ٤٠ شذرات الذهب لابن العماد ٣/ ٢٣٣، مرآة الجنان لليافعى','3','115',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (872,'٣/ ٤٧، النجوم الزاهرة لابن تغرى بردى ٥/ ٢٤ - ٢٥، محمد بن شنب فى دائرة المعارف الإسلامية ٢/ ١١٨٤، الأعلام للزركلى ١/ ٢٠٦، معجم المؤلفين لكحالة ٢/ ٦٦، بروكلمان ١/ ١٧٤ رقم ١٢، وانظر كذلك فلوجل Flugel ,Hanef.٣٠٥:

<span data-type="title" id=toc-2043>ب- آثاره:</span>
١ - «المختصر» برلين ٤٤٥١ (١٢٠ ورقة فى القرن الثانى عشر الهجرى)، ٤٤٥٢ (١٤٠ ورقة، ٦٤٩ هـ)، جوتا -) BDMG ٩٩٥ ٩٩٤ مكتبة الجمعية الاستشراقية الألمانية) ٣٣ - ٣٤، باريس ٨٢٧ (١٦٥ ورقة، فى القرن التاسع الهجرى)، ٨٢٨ (١٧٦ ورقة، ٩٣٢ هـ)، ٨٢٩ (٩٣ ورقة ٩٣٢ هـ)، ٨٣٠/ ١ (من ورقة ١ - ١٢٧، ٩٩٤ هـ)) ٨٣١، (١٥٣ ورقة، ١٠٣٦ هـ)، ٨٣٢ (١٢٧ ورقة، فى القرن الحادى عشر الهجرى)، ٨٣٣/ ١ (من ورقة ١ - ١٤١، ١٠٩٠ هـ)، الجزائر ٩٧٨ - ٩٨٠، (ثلاث مخطوطات)، المتحف البريطانى ٢٦٧، إضافات ٧٢٦٠ (١٩٨ ورقة، ١٠٤١ هـ)، جاريت ١٦٧٤ (١٥٧ ورقة، ٩٥٨ هـ)، ١٦٧٥ (٢٥٥ ورقة، ٩٥٨ هـ)، ١٦٧٦ (١٣٢ ورقة، فى القرن الحادى عشر الهجرى)، أيا صوفية ١٤٢٤ (٩٠ ورقة)، ١٤٢٥ (١٧٧ ورقة، ٩٥٣ هـ)، ١٤٢٦ (٥٩ ورقة)، ١٤٢٧ (١٤٣ ورقة)، ١٤٢٨ (١١٣ ورقة، ١٠٧٥ هـ)، ١٤٢٩ (١٥٢ ورقة، ٩١٣ هـ)، ١٤٣٠ (١٩٧ ورقة)، ١٤٣١ (٢٢٤ ورقة)، قليج على ٤٥٥ (٩٩ ورقة، ٩٨٤ هـ)، ٤٥٦ (١٢٤ ورقة، ٨٩٠ هـ)، ٤٥٧ (٢٨٩ ورقة، ٩٦٤ هـ)، ٤٥٨ (١٥٩ ورقة، فى القرن الحادى عشر الهجرى)، ٤٥٩ (١٥٢ ورقة، فى القرن العاشر الهجرى)، سليم أغا ٣٨٥ (١١١ ورقة، ١١٩٦ هـ)، ٣٨٦ (١٤١ ورقة فى القرن التاسع الهجرى)، ٣٨٧ (١٣٢؛ ١١١٣ هـ) (١٣٠) بخصوص المخطوطات الأخرى انظر بروكلمان، أسعد أفندى ٨٦١ - ٨٦٣، وهبى ٤٨٢، ٤٩١، لاله لي ١١٢٣ - ١١٣٩، حاجى محمود ٩٢١ - ٩٢٢، لاله إسماعيل ٩٩، ٥٤١، حميدية ٥٤٠، دار المثنوى ١٢١، ١٢٢، فيض الله ٩٥٠ - ٩٥٢ جار الله ٨٧٨/ ٨٧٩، رشيد باستنبول ١٢٤٢، نور عثمانية ١٧٧٦ - ١٧٨٨، فاتح ٢١٢٦، ٢١٣٣ - ٢١٣٨، نافذ باشا ٢٩٩، قاصدجى زاده ٢١٩، بشير أغا ٢٩١، ٢٩٢، شاه زاده ٤١، ملّا چلبى ٥٣، مسيح باشا ١٦، ٨٢، سراى (حوالى ٢٥ نسخة، انظر: فهرس ٢/ ٣٩٧ - ٤٠٣).
وطبع فى دلهى ١٨٤٧ م، ولاهور ١٨٧٠ م، وقازان ١٨٩٠، ١٩٠٩ م وبومباى ١٣٠٣ هـ وإستنبول ١٣١٠ هـ، ١٣١٧ - ١٣١٨ هـ، والقاهرة ١٩٥٧ م.
وترجم إلى الفرنسية، ونشر فى باريس ١٨٢٩، ثم فى تونس:
Ch. Solvet, G. H. Bousquetet L. Bercher, Lestatutpersonne lendroitmusulman hanefite, Texteet trad. annotedu M. al- Quduri, Tunis. O. J.
_________
(١٣٠) تحذف مخطوطة ينى ٥٢٧ عند بروكلمان.','3','116',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (873,'مكملات لكتاب المختصر:
١ - «تحفة الفقهاء» لعلاء الدين أبى بكر محمد بن أحمد السمرقندى (المتوفى ٥٥٣ هـ/ ١١٥٨ م انظر بروكلمان ١/ ٣٧٤) وصل إلينا هذا الكتاب فى عدة مخطوطات (انظر بروكلمان ١/ ٣٧٤ والإضافات).
٢ - «تكملة» لحسام الدين على/ بن أحمد بن مكى الرازى (المتوفى ٥٩٨ هـ/ ١٢٠١ م انظر بروكلمان ملحق ١/ ٦٤٩)، باريس ٨٥٤ (٢٣٢ ورقة، فى القرن الثامن الهجرى)، الزيتونة بتونس ٤/ ٨٨، رقم ١٩٤٩ (١١٢ ورقة)، جار الله ٦٠٢ (١٧٤ ورقة، ٦٠٨ هـ)، ولى الدين ١٠٥٩ (١٩٥ ورقة، ٧٣٥ هـ)، سراى، أحمد الثالث ٩٧٩ (٢٩٢ ورقة، ٧٩٠ هـ)، ٩٨٠ (١٨٥ ورقة، ٧٦٢ هـ، انظر فهرس معهد المخطوطات العربية ١/ ٢٥٨)، ٩٨١ (٨٥٠ هـ، انظر: فهرس ٢/ ٤١٠)، ميونيخ ٢٥٩ (١٦٩ ورقة، فى القرن الحادى عشر الهجرى).
وهذه التكملة شرح لنفس المؤلف، مكتبة جامعة إستنبول ٣٣٩٤ (٢٨٣ ورقة، ٦٠٢ هـ).
شروح المختصر: ١ - شرح لأبى نصر أحمد بن محمد بن الأقطع البغدادى (المتوفى ٤٧٤ هـ/ ١٠٨١ م، انظر: معجم المؤلفين لكحالة ٢/ ١٤٨)، باريس ٨٣٤ (ج ٣ ٢٦٥ ورقة، ٦١١ هـ، انظر فايدا ٤٩٨)، القاهرة ثان ١/ ٤٣٩، فقه حنفى ٣٧٠ (جزءان)، ٧٣٧ (ج ١، ٢، ٣، ١٦٧ ورقة، ١٧٦ ورقة، ١٣٧ ورقة، ٧٩٤ - ٧٩٥ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٥)، المتحف البريطانى، مخطوطات شرقية ٥٩٦٢ (قائمة وصفية)، (٢٧ Deser.List ج ٢، ٣، ٣٠٩ ورقة، ٣٠٧، فى القرن الثالث عشر الهجرى)، قليج ٣٩٥ (ج ١، ٢٠٧ ورقة، فى القرن التاسع الهجرى)، ٣٩٦ (ج ٢، ٢٤٨ ورقة، ٨٦٢ هـ)، السليمانية ٤٩٣ (ج ١، ٣٦٠ ورقة، فى القرن التاسع الهجرى)، ٤٩٤ (ج ٢، ٢٤٦، ورقة ٨٣٢ هـ)، سراى، أحمد الثالث ٩٨٣ (٢٤٥ ورقة، فى القرن الثامن الهجرى)، ٩٨٣ (٣٨٤ ورقة، ٥٩١ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٦٥، فهرس ٢/ ٤٠٣)، ٩٨٤ (ج ١، ٢، ٢٢٣ ورقة، ١٨٩ ورقة، فى القرن التاسع الهجرى)، سراى، مدينة ٣٤٤ - ٣٤٥، (ج ١، ٢، ٢٢٩ ورقة ٢٩٦ ورقة، ٨٥٤ هـ، ٥٨٥٥ هـ، انظر: فهرس ٢/ ٤٠٤)، مراد ملّا ٨٩٩ (ج ١، ١٤٨ ورقة، ٨٤٩ هـ)، ٩٠٠ (ج ٢، ١٢٣ ورقة، ٨٤٩ هـ)، عاطف ٨٩٩ (ج ١، ٣٢٤ ورقة، ٥١٩ هـ)، أسعد أفندى ٧٤٢ (٢٣٥ ورقة)، ٧٤٣ - ٧٤٤ (ج ١، ج ٢، ٢٤٧ ورقة، ٢٥١ ورقة)، جار الله ٧٢٥ (٣١٠ ورقة، فى القرن الثامن الهجرى)، فاتح ١٧٦٩ (٣٧٣ ورقة)، ١٧٧١ - ١٧٧٢ (ج ١، ٢، ٣١٣ ورقة، ٢٩٦ ورقة، ٨٤٩ هـ)، جورلولو ٢٠٧ (٣٩٠ ورقة، ٨٤٩ هـ)، أيا صوفية ٨٤ (ج ١، ٢٩٩ ورقة، فى القرن التاسع الهجرى)، ٨٥ (ج ٢، ٢٩١ ورقة، ٨٥٢ هـ)، ٨٦ (ج ٣، ٣٠٨ ورقة، ٩٣٩ هـ)، ٨٧ (جزء واحد، ٢٦١ ورقة، فى القرن التاسع الهجرى)، ٨٨ (ج ١، ٢٩٢ ورقة، ٨٦٠ هـ)، رشيد بقيصرية ٢٣٣ (٣٨٢ ورقة، فى القرن العاشر','3','117',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (874,'الهجرى) قونية يوسف ٤٧٩٧ - ٤٧٩٨ (جزءان)، ٢٧٥ ورقة، ٢٩٣ ورقة ٨٠٣ هـ)، الأزهر ٢/ ١٨٦، فقه حنفى ٩٨ (٢٤٧ ورقة).
وله شرح مجهول المؤلف بعنوان: «شرح غريب احاديث ابن الأقطع»: فاتح ١٧٦٨ (٢٦٠ ورقة، ٧٣٥ هـ، وأول هذه المخطوطة «قال .. هذا تعليق وضعته لبيان السهو .. ».
وهناك شرح آخر لقاسم بن قطلوبغا (المتوفى ٨٧٩ هـ/ ١٤٧٤ م انظر بروكلمان ٢/ ٨٢) بعنوان:
«غريب الأحاديث المذكورة فى شرح مختصر القدورى للأقطع»، لاله لي ٨٣٩ (٩٧ ورقة، بخط المؤلف).
٢ - «شرح مشكلات القدورى» لخواهرزاده محمد بن الحسين أبى بكر البخارى (المتوفى ٤٨٣ هـ/ ١٠٩٠ م، انظر: الجواهر للقرشى ١/ ٢٣٦، ٢/ ٤٩) جامع ينى ٤٠٤ (من ١ - ٢٢٠ أ، ١١٠٠ هـ)، كوبريلى ٥٨٩ (٢٢٤ ورقة، ٨٩٢ هـ، السليمانية ٤٩٩ مكرر ١٤٧ ورقة، ٧٧٢ هـ)، (بلدية الإسكندرية، فقه حنفى ٢١؟ )، شهيد ٨٤٧ (١٧٣ ورقة، ٧١٣ هـ)، وهبى ٥٠١ (٩٨ ورقة، ٩٢٦ هـ)، فيض الله ٧٩٦، (٢١٦ ورقة، ٨٧٠ هـ)، خسرو باشا ٩٢ (١٨١ ورقة، فى القرن الثامن الهجرى)، وهذا الكتاب هو نفسه «فوائد القدورى»، نافذ باشا ٢٧٤ (١٣٦ ورقة، فى القرن العاشر الهجرى)، حسن حسنى باستنبول ٤٢١ (١٦٤ ورقة، فى القرن الحادى عشر الهجرى)، الأزهر ٢/ ٢٨٧، فقه حنفى ١٦٦٧ (٨٠ ورقة، ٨٧٧ هـ).
٣ - «إيضاح مختصر القدورى» لركن الدين أبى الفضل عبد الرحمن بن محمد الكرمانى (المتوفى ٥٤٣ هـ/ ١١٤٩ م انظر بروكلمان ملحق ١/ ٦٤١)، الخالدية القدس ١٠٨٥ (٩٧٩ هـ، انظر: طلس فى مجلة المجمع العلمى بدمشق ٢٠/ ٣٤٣).
٤ - «ملتمس الإخوان» لأبى المعالى عبد الرب بن منصور بن إسماعيل الغزنوى (المتوفى حوالى ٥٠٠ هـ/ ١١٠٦ م، انظر: معجم المؤلفين لكحاله ٥/ ١١١)، فاتح ١٧٧٥، (ج ١، ٢٧٣ ورقة، ٥٣٧ هـ) (١٣١).
