INSERT INTO [b143160] VALUES (875,'مكتبة جامعة إستنبول ٣٢٣٠ (٣٣٦ ورقة، ٧٣٧ هـ) /، وزارة المعارف بكابول (٢٢٦ ورقة، ١٣١٢ هـ، انظر. (١٥٤ MIDEOIII ,٣ /٤٥١:
