INSERT INTO [b143160] VALUES (876,'حوالى ٩٠٠ هـ)، جامع ينى ٤٦٩ (٣٧٠ ورقة، ٨٧٧ هـ)، مراد ملّا ٩٠٥ (ج ٢، ٩٢ ورقة، فى القرن العاشر الهجرى)، السليمانية ٤٩٩ (٣٧٨ ورقة، ٨٢٦ هـ)، سليم أغا ٣٤٠ (ج ١، ٩٣٢ هـ)، ٣٤١ (ج ٢، ١٩٧ ورقة، ٩٣٢ هـ)، بنگيبور ١٩/ ١٠١ رقم ١٦٠٢ (٣٨٧ ورقة، فى القرن العاشر الهجرى)، الزيتونة بتونس ٤/ ٢٢٠ رقم ٢٣٣٤ (٤٠٦ ورقة، ٨٤٢ هـ)، ٢٣٣٥ - ٢٣٣٦ (ج ١، ٢، ٢٢٢ ورقة، ١٧٠ ورقة)؛ مراد ملّا ٩٠٦ (٤٧٨ ورقة، فى القرن الثامن الهجرى)، عاطف ٩٠٧ (٣٥١ ورقة، ١٠٥٠ هـ) مكتبة جامعة إستنبول ١٨٤٨ - ١٨٤٩ (ج ١، ٢، ٨٧٤ هـ)، سراى، أحمد الثالث ٩٧٧ (٣٨٧ ورقة، فى القرن العاشر الهجرى)، ٩٧٨ (ج ٢، ٢١٥ ورقة، ٨٥٧ هـ)، ٩٨٥ (٣٦٠ ورقة، ١٠٠٤ هـ، انظر: فهرس ٢/ ٤٠٥ - ٤٠٦)، أدرنة السليمانية ٨٦٥ (٧٢١ هـ)، ٧٥، قسطلمونى ٣٤٦٧ (حوالى ٣٠٠ ورقة، ٧٢٩ هـ)، القاهرة ملحق ٣/ ٦ رقم ٢٣٥٢١ ب (ج ٢، ٢١٧ ورقة، ٨٤٩ هـ)، الأزهر ٢/ ١٩٣، فقه حنفى ١٢٦٢ (٤٢٧ ورقة، ١٢١٧ هـ).
١١ - «الفوائد البدرية» لحميد الدين على بن محمد بن على الضرير الرّاموشى البخارى (المتوفى ٦٦٦ هـ/ ١٢٦٧ م انظر بروكلمان ملحق ١/ ٥١٩)، (قوله ١/ ٣٨١).
١٢ - شرح لجلال الدين عمر بن محمد بن عمر الخبّازى (المتوفى ٦٩١ هـ/ ١٢٩٢ م انظر بروكلمان ١/ ٣٨٢)، عاطف ٩٠٦ (٢١٦ ورقة، ٧٢٣ هـ).
١٣ - «الينابيع فى معرفة الأصول والتفاريع» لرشيد الدين أبى عبد الله محمد بن عبد الله رمضان الرومى الشّبلى (المتوفى ٧٦٩ هـ/ ١٣٦٧ هـ، انظر: معجم المؤلفين لكحالة ١٠/ ٢١٩)، برلين (الشرقية) ١٨٤٨، بلدية الإسكندرية ١٢٣٢ ب (٢٦٢ ورقة، ٧٢٣، انظر، فهرس معهد المخطوطات العربية ١/ ٢٧٧)، الظاهرية، أصول الفقه ٢١؛ تشستربيتى/ ٣٥٤٤ (٢٨٧ ورقة، ٧٢٢ هـ)، الأزهر ٢/ ١٩٣، فقه حنفى ١٩٨٢ (١٤٦ ورقة)، ٣٠٠ فقه حنفى ٣١٢٧ (ج ١/ ١٠٢ ورقة).
١٤ - (يطابق رقم ٨ فى ملحق بروكلمان) «الجوهرة النيرة» لأبى بكر بن على بن موسى الحدّاد العبّادى اليمنى (المتوفى ٨٠٠ هـ/ ١٣٩٧ م انظر بروكلمان ٢/ ١٨٩)، برلين ٤٤٥٣ (ج ١، ٢٣٤ ورقة، ١٠٢٠ هـ)، باريس ٨٣٥ (٣٠٠ ورقة ٩٧٤ هـ، انظر: فايدا ٣٥٦)، القاهرة ثان ١/ ٤١٥ فقه حنفى ٩٢، ١٢٧ - ١٢٩، عليكرة ٢٩٧٦٣/ ٦٦ - ٦٧ (ج ١، ٢١٦ ورقة، ١٦٨ ورقة، ١٠٩٢ هـ)، بنگيبور ١٩/ ١/ ١٠٣ رقم ١٦٠٤ (٥١٦ ورقة، ١١٠١ هـ)، قليج ٣٩٧ (ج ١، ٢٧٤ ورقة، ١٠٨٣ هـ)، ٣٩٨ (٢٣٧ ورقة، ١٠٦٨ هـ)، الفاتيكان، فيدا ١٢٢٢ (ج ١/ ١٩٣ ورقة، فى القرن الثامن الهجرى)، قولة ١/ ٣١٩)، رامبور ١/ ١٨٤، فقه ١٣٢ (٣٥١ ورقة)، هيدلبرج ٣١٠ (٣٧٨ ورقة، ١٠٦٣ هـ، انظر:
Berenbach ZS ١٠/ ٨٤
)، المتحف البريطانى ١٦٠٠، مخطوطات شرقية ٩٦ (٥٠٩ ورقة، حوالى ٨٠٠ هـ)، ومخطوطات شرقية ٥٥٧٣ (ج ١، ٣٣٣ ورقة، ١١٠٦ هـ، انظر: إليس، قائمة وصفية ٢٧)، حيدرآباد','3','120',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (877,'آصفية ٢/ ١٠٨٠، فقه حنفى ٦٤ (١٣٣)؛ سراى، أحمد الثالث ٩٧٦ (٣٤٤ ورقة، ١٣٠١ هـ)، الأمانة ٧٩٥ (٣٨٠ ورقة، ١١١١ هـ)، كغشلار ٧٤٥ (٣١٥ ورقة ٩٩٥ هـ)، ريفان ٦١٩ (٤٧١ ورقة، ١٠٣٨ هـ، انظر: فهرس ٢/ ٤٠٨ - ٤٠٩)، وهبى ٣٩٦ (ج ١، ٤٥٤ ورقة، فى القرن العاشر الهجرى)، ٣٩٧ (٤٤٧ ورقة، ٩٦٦ هـ)، كوبريلى ٥٩٠ (٢٤٣ ورقة، ٩٥٠ هـ)، منجانا ٥٤٢ (٥٩٠ ورقة، ١١٤٤ هـ، فهرس رقم ١٩٣)، الأزهر ٢/ ١٣٥ - ١٣٦، (١٢ مخطوطة)، بلدية الإسكندرية ١٠٥١، ٢١٨٠ (١٢٣٩ هـ)، الجزائر ٩٨٣ - ٩٨٤.
وطبع باستنبول ١٣٠١ هـ، ١٣١٦ هـ، ١٣٢٣ هـ، وفى دلهى ١٣٢٧ هـ، وفى لاهور ١٣٢٨ هـ.
١٥ - (يطابق رقم ٨ فى ملحق بروكلمان) «السراج الوهّاج» للشارح السابق، جامع ينى ٤٦٢ (ج ١، ٤٦٩ ورقة، ٩٦٤ هـ)، ٤٦٣ (ج ٢، ٤٨٨ ورقة، فى القرن التاسع الهجرى)، ٤٦٤ (ج ١، ١٨١ ورقة، ٨٥٤ هـ)، ٤٦٥ ج ٢، ٥١٨ ورقة، ٨٥٥)، كوبريلى ٥٩١ - ٥٩٤ (فى القرن التاسع الهجرى)، القاهرة ثان ١/ ٤٣٧ (ثمانى مخطوطات)، بنگيبور ١٩/ ١/ ١٠٢ رقم ١٦٠٣ (٢٢٤ ورقة، ٨١١ هـ)، لاندبرج- بريل ٦٠١ (ثلاثة أجزاء، ٩٤٥ هـ)، مراد ملّا ٩٠٧ - ٩٠٨ (ج ١، ٢، ٤٢٣ ورقة، حوالى ٤٠٠ ورقة، ٩٩٨ هـ)، السليمانية ٤٩٥ - ٤٩٨ (من جزء ١ - ٤، ٢٩١ ورقة، ٣٤٣ ورقة، ٢٩٥ ورقة، ٣٥٥ ورقة، ٩٥٢ هـ)، الزيتونة بتونس ٤/ ١٣٩ رقم ٢٠٩٨ (ج ١، ٣١٠ ورقة)، ٢١٠٠ - ٢١٠٦ (ج ١، ٢، ٤ - ٨)، ٢١٠٧، ٢١٠٨، ٢١٠٩، آصفية ٢/ ١٠٨٨، فقه ٦٦ - ٦٨ (من جزء ١ - ٣، ٩٤١ هـ)، (توبنجن ١١٥، الخالدية بالقدس ٢٠، ٤٨)، الظاهرية، فقه حنفى ٨٧ - ٩٠ (أربعة أجزاء)؛ سراى، أحمد الثالث ٩٧٠ (ج ١، ٢، ٣٣٨ ورقة، ٥٢٤ ورقة، ٩٦٨ هـ)، ٩٧١ (ج ١، ٢، ٤٦٩ ورقة، ٤٦٧ ورقة، ٩٣٨ هـ)، ٩٧٢ (ج ١ - ٢، ٥٢٩ ورقة، ٤٥٣ ورقة، ٩٨٨ هـ، ٩٧٢ هـ)، ٩٧٤ (ج ٢، ٣٦٨ ورقة، ٩٦٨ هـ) كغشلار ٨٢٨ - ٨٢٩ (٢٩٠ ورقة، ١٠٤ ورقة، ٩٢٤ هـ)، ريفان ٦٢٥ (ج ٤، ٣٣٩ ورقة، ٨٦٣ هـ، انظر: فهرس ٢/ ٤٠٦ - ٤٠٨)، أيا صوفية ١٢٦٣ (ج ١، ٤٣٠ ورقة، فى القرن الثانى عشر الهجرى)، ١٢٦٤ (ج ٢، ٢٩٩ ورقة فى القرن العاشر الهجرى)، ١٢٦٥ (ج ٣، ١٦٧ ورقة، ٨٧٥ هـ)، ١٢٦٦ (ج ٤، ٣٠٧ ورقة، فى القرن العاشر الهجرى)، حراچى زاده فى بورسه ٤٢٢، (ج ١، فى القرن الثامن الهجرى)، الأزهر ٢/ ١٨٢، فقه حنفى ٢٠٣ (ج ١، ١٨٢ ورقة)، ٣٧١ (ثمانية مجلدات ١٢٩٦ هـ).
١٦ - (يطابق رقم ٩ فى ملحق بروكلمان) «جامع المضمرات والمشكلات» ليوسف بن عمر بن يوسف الصوفى الكادورى (المتوفى ٨٣٢ هـ/ ١٤٢٩ م، كشف الظنون لحاجى خليفة ١٦٣٢، ١٦٣٣، ١٨٣٨):
ينى ٤٦٧ (من ١ أ- ٣٤٤ ب، فى القرن الحادى عشر الهجرى)، ٤٦٨ (١ ب- ٤٤٤ أ- ١٠٠٧ هـ)، المكتب الهندى بدلهى ٧٢١ (المجلد الأخير، ٣٩٤ ورقة، فى القرن الحادى عشر الهجرى، فهرس ٢ رقم
_________
