INSERT INTO [b143160] VALUES (878,'١٥٢١)، ٤١٢١ (من ورقة ١ - ١٨٠، ٨٧٤ هـ)، رامبور ١/ ١٨٣، فقه ١٣٢ (٢٠١ ورقة)، ليبتسج ٣٥٦ (٢٧٧ ورقة)، الموصل ٦٢، ١٢٨، آصفية ٢/ ١١٠٦، فقه حنفى ٦٣؛ نور عثمانية ١٦٢٠ (٤٥٧ ورقة، فى القرن العاشر الهجرى)، مكتبة جامعة إستنبول ٦٠٣ (٣٥٢ ورقة، فى القرن التاسع الهجرى)، سليم أغا ٣٣٩ (ج ٢، ١٩٢ ورقة، ٨٠١ هـ)، عاطف ٩٠٩ (٩٥٥ هـ)، الأزهر ٢/ ١٣١، فقه حنفى ٢٨٢٩ (٤٢١ ورقة ١١٩٤ هـ).
١٧ - «حاشية» على الشرح السابق لمحمد شاه بن محمد الفنارى (المتوفى ٨٣٩ هـ/ ١٤٣٥ م انظر بروكلمان ملحق ٢/ ٣٢٩)، رشيد باستنبول ١٨٦ (٦٩ ورقة، بخط المؤلف).
١٨ - «المهم الضرورى» لعبد الرحيم الآمدى (ألفه للسلطان العثمانى/ سليم الأول المتوفى ٩٢٦/ ١٥٢٠ م): جار الله ٧٢٨ - ٧٣٠ (ثلاثة أجزاء، ٩٧٠ هـ)، لاله لي ١٠١٦ (ج ١، ٣٤٦ ورقة، ١١٠٩ هـ).
١٩ - «حدق العيون» لعبد الأول بن حسين بن حسن بن حامد الرومى (المتوفى ٩٥٠ هـ/ ١٥٤٣ م، انظر: معجم المؤلفين لكحالة ٥/ ٦٧)، لاله لي ١٠٠٢ (١٧٣ ورقة، ١٠٧٥ هـ).
٢٠ - «شرح ديباجة القدورى» لمحمد بن عبد الله الألبسانى (أهداه للسلطان العثمانى مراد الثالث ٩٧٤ هـ/ ١٥٦٦ هـ/ ٩٨٢ هـ/ ١٥٧٤ م)، عاشر أفندى ٩٧، (٢٣ ورقة، فى القرن الحادى عشر الهجرى).
٢١ - (يطابق رقم ١١ فى ملحق بروكلمان) «المنن على مختصر القدورى» ليوسف بن محمد بن سليمان الزّغوانى (المتوفى حوالى ١١٤٤ هـ/ ١٧٣١ م)، الزيتونة بتونس ٤/ ٢٥٧ رقم ٢٤٢١ - ٢٤٢٢ (ج ١، ٢، ٤١٧ ورقة، ٥١٨ ورقة، ١١٤٤ هـ) ٢٢ - (يطابق رقم ١٢ فى ملحق بروكلمان) «اللباب فى شرح الكتاب» لعبد الغنى الميدانى (المتوفى ١٢٨٩ هـ/ ١٨٨١ م، انظر: معجم المؤلفين لكحالة ٥/ ٢٧٤)، الظاهرية، فقه حنفى ٩٢، (قولة ١/ ٣٨٦)، وطبع فى إستنبول ١٢٧٥، وفى القاهرة ١٣٣٠ هـ، ١٣٣١ هـ، ١٣٤٦ هـ، ١٣٥٤ هـ.
٢٣ - (يطابق رقم ١٣ فى ملحق بروكلمان) «فاتح القدورى» لمؤلف مجهول: ميونيخ ٢٥٧ (٢١٤ ورقة، قبل ٧١٤ هـ)، المكتب الهندى بدلهى ٥٩٠ (٢٢٧ ورقة، فى القرن الثانى عشر الهجرى، فهرس ٢/ ١٥٢).
٢٤ - شرح لمؤلف مجهول، الأزهر ٧/ ٤٠، فقه حنفى ٣٦٠٤ (٢٥٤ ورقة، نسخة منقولة عن نسخة دار الكتب بالقاهرة، فقه حنفى ٣٨٧).','3','122',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (879,'٢٥ - «أنوار البدوري على كتاب القدورى» لمؤلف مجهول، البنغال ٣٩٦، (٢٦٤ ورقة، ٩٦٩ هـ).
٢٦ - شرح لمؤلف مجهول، تشستربيتى ٤٧٤٦ (٢٣٦ ورقة، ٥٦٣ هـ).
٢٧ - شرح لمؤلف مجهول، بلدية الإسكندرية ١/ ٣٠٩٧ ج (١٠٤١ هـ).
ويوجد نقد لكتاب المختصر بعنوان:
«تصحيح مختصر القدورى» أو «الترجيح والتنقيح على القدورى» للقاسم بن عبد الله بن قطلوبغا (المتوفى ٨٧٩ هـ/ ١٤٧٤ م انظر بروكلمان ٢/ ٨٢)، بلدية الإسكندرية ٣٨٥٩ ج (٩٩٨ هـ)، الخالدية بالقدس ٢٠، ٢٩، انظر: مجلة المجمع العلمى بدمشق ٢٠/ ٤٤٧: جار الله ٦٠١ (١٤٢ ورقة، ٨٦٩ هـ، على المخطوطة ملاحظات بخط المؤلف)، سليم أغا ٣٣٨ (٤٢٢ ورقة، فى القرن التاسع الهجرى)، شهيد على ٩١٥/ ١ (من ورقة ١ - ٥٥، ٩٨٦ هـ)، ٩١٦/ ١ (من ورقة ١ - ٨٠)، مكتبة جامعة إستنبول ٢٦٤٢، حفيد أفندى ٧٣، القاهرة، ملحق ١/ ١٦٠ رقم ٢٢٥٩١ ب (٦١ ورقة).
وترجمه إلى التركية مع شرح له أبو محمد شريف مغربى زاده، هيدلبرج مخطوطات تركية ٢٩٩ (٢٧٣ ورقة، فى القرن الثالث عشر الهجرى، انظر (Berenbach ,in: ZS ١٠ /٩٥.: واختصر كتاب «مختصر القدورى» محمد بن بدر الدين المنشى (المتوفى ١٠٠١ هـ/ ١٥٩٣ م انظر بروكلمان ٢/ ٤٣٩)، لاله لي ١١٢٢ (١٠٨ ورقة، ٩٩٢ هـ).
ثانيا: «التجريد» يدافع فيه عن وجهة نظر أبى حنيفة فى الفقه ويرد على الشافعى، المتحف البريطانى ١١٩٤، إضافات ٢٤٠٠٠ (٣٤١ ورقة، ٧٥٧ هـ، القاهرة ثان ١/ ٤٠٧ فقه حنفى ٢٩٠، ٨٠٣، برلين ٤٤٥٥ (ج ٢، ٢٦٣ ورقة، ٩٦٣ هـ)؛ سراى، أحمد الثالث ٩٨١ (ج ١، ٨١٥، ٨٥٠ هـ)، ٩٨٢ (ج ٣، ٢٠٢ ورقة، ٨٧٤ هـ، فهرس ٢/ ٤١٠ - ٤١١)، وهبى ٤٤٤ (ج ٢، ٢٨٩ ورقة، ٩٧٣ هـ)، نور عثمانية ١٤٠٥ (٦٩٤ ورقة، فى القرن التاسع الهجرى)، فاتح ٢٠٤٠ (٣٧٢ ورقة، ٨٤٢ هـ)، تشستربيتى ٣٥٢٣ (ج ١، ٤٦٠ ورقة، ٧٥٧ هـ)، ٣٥٧١ (ج ٢، ١٨٤ ورقة ٨٧٤ هـ)، وتوجد لهذا الكتاب «تكملة» لأبى بكر عبد الرحمن بن محمد السّرخسى (المتوفى ٤٣٩ هـ/ ١٠٤٧ م)، وهبى ٤٤٢ (ج ٣، ٢٤٥، ورقة، ٧٨٠ هـ).
ثالثا: «شرح مختصر الكرخى»، انظر: الكرخى رقم ٢٢ من هذا الفصل.
رابعا: حديث أو (جزء) كوبريلى ١٥٨٤ (من ٤١ أ- ٤٤ أ، فى القرن التاسع الهجرى، انظر: فهرس معهد المخطوطات','3','123',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (880,'العربية ١/ ٧٩)، القاهرة ثان ١/ ١٠٧، حديث ١٥٥٩ (من ٦ أ- ٩ ب فى القرن الثامن الهجرى)، نسخة منقولة عن النسخة السابقة، القاهرة، ملحق ١/ ٢١٥)، الظاهرية مجموع ١٨ (٢٥٥ أ- ٢٥٩ ب، فى القرن السابع الهجرى).
خامسا: «نبذة من مناقب أبى حنيفة» راغب أفندى ١٤٧٩/ ٦ (من ورقة ٦٩ - ٧١) (١٣٤).

<span data-type="title" id=toc-2044>٢٧ - الدّبوسى</span>
هو أبو زيد، عبد الله (عبيد الله) بن عمر بن عيسى، الدّبوسى، لا نعرف شيئا يذكر عن حياته، كان اهتمامه الرئيسى «علم الخلاف»، وهو العلم الذى يبحث فى الخلاف بين المذاهب. ولا بد أن له دورا كبيرا فيه، وذكر ابن خلكان (فى كتابه وفيات الأعيان، بولاق ١/ ٣١٧) أنه «أول من وضع علم الخلاف وأبرزه إلى الوجود» وتوفى ٤٣٠ هـ/ ١٠٣٩ م فى بخارى.

<span data-type="title" id=toc-2045>أ- مصادر ترجمته:</span>
اللباب لابن الأثير ١/ ٤١٠، البداية والنهاية لابن كثير ١٢/ ٤٦ - ٤٧، تاج التراجم لابن قطلوبغا ٢٦ - ٢٧، ٦٣ - ٦٤، الجواهر للقرشى ١/ ٣٣٩، شذرات الذهب لابن العماد ٣/ ٢٤٥، الأعلام للزركلى ٤/ ٢٤٨، معجم المؤلفين لكحالة ٦/ ٩٦، وانظر بروكلمان ١/ ١٧٥ رقم ١٣.
Flugel, Hanef. ٢٧٤.

<span data-type="title" id=toc-2046>ب- آثاره:</span>
١ - «الأمد الأقصى» وهو تعاليم وإرشادات أخلاقية عقيدية، برلين (الغربية) ١٤٩٥، الإسكوريال ٥٥٩ (٢٢١ ورقة
_________
(١٣٤) ملاحظتان: أ- يحذف الكتابان اللذان ذكرهما بروكلمان فى الأصل ١/ ١٨٤، بعنوان: «تكملة المختصر» و «مشكلات القدورى».
ب- كذلك يحذف الشرحان اللذان ذكرهما بروكلمان لمختصر القدورى برقمى ٢، ٣، فى الملحق ١/ ٢٩٦.','3','124',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (881,'٩٧٨ هـ)، المتحف البريطانى، مخطوطات شرقية ٦٤٩٧ (١٨١ ورقة ٩٧٩ هـ)، عاطف أفندى ١٣٨٤ (١٥٠ ورقة، ٦٥٢ هـ)، ولى الدين ١٦٣٣ (٢٠٩ ورقة فى القرن العاشر الهجرى)، ١٦٨٢ (٩٠ ورقة، ٥٢١ هـ) لاله لي ١٣٣٧/ ١٣٨ ورقة فى القرن العاشر الهجرى)، (المتحف الآسيوى ببطرسبورج بخارى ١١٢)؛ شهيد على ١٤٥٩/ ٢ (من ورقة ١٥ - ١٣٦)، أسعد أفندى ٣٥٢١/ ١ (من ورقة ١ - ١٦٣)، جار الله ٩٨٤ (٢٢٩ ورقة، فى القرن التاسع الهجرى)، سراى خزينة ٣٠٧ (١١٤ ورقة ١١٤٩ هـ، انظر:
فهرس معهد المخطوطات العربية ١/ ١٤٦)، قونية قيونوغلو ٢٠٠٢/ ١ (من ١ - ١٥٥ ب، فى القرن السابع الهجرى)، الأزهر ٣/ ٥٣٨، تصوف ٨٠٧ (١٢٩ ورقة ٩٦٣ هـ).
٢ - «تقويم الأدلة فى اصول الفقه»:
جامع ينى ٣١٠ (٢٢٦ ورقة، فى القرن الثالث عشر الهجرى) (١٣٥)، القاهرة ثان ١/ ٣٨١، أصول فقه ٢٥٥، الخالدية بالقدس ١٤/ ١ (انظر: مجلة المجمع العلمى العربى بدمشق ٢٠/ ٤٤٠)؛ عاطف ٦٦٠/ ١ (من ورقة ١ - ٢٣٨، ٩٩٦ هـ)، وهبى ٣٥٠ (١٥٩ ورقة، فى القرن الثانى عشر الهجرى)، لاله لي ٦٩٠ (٢٦١ ورقة، ٧١٥ هـ)، فيض الله ٥٧٠ (١٧٢ ورقة، ٥٥٢ هـ)، جار الله ٤٤٨، كوبريلى ٢/ ٧١ (١٦٦ ورقة، ٧٢٣ هـ)، قونية، يوسف قرمان ١١٠ وتوجد الآن فى يوسف أغا ٦٧٩٩ (١٩٥ ورقة، ٤٧٢ هـ، انظر أحمد آتش فى، (A.Ates ,in: Belleten ١٦ ,٦٦: تشستربيتى ٣٣٤٣ (٢٠٣ ورقة، ٩٧٤ هـ)؛ ومنه مختصر مجهول المصنف، جوتا ٩٢٩ (ست ورقات).
٣ - «تأسيس النظر (أو النظائر)» فى الخلافات الفقهية:
القاهرة ثان ١/ ٣٧٩، ٤٠٦، أصول فقه ٣٦٠، فقه حنفى ١١١ م، ليبتسج ٣٤٩ (٩١ ورقة)؛ تشستربيتى ٣٢٠٢ (من ورقة ١ - ٤٥، ٨٦٤ هـ)، ٥٠٧٤ (١١١ ورقة، فى القرن الثامن الهجرى)، طشقند ٣٠٤٦ (٢٤٩ ورقة، ٤٨٧ هـ)، وطبع بالقاهرة ١٣٢٠ هـ.
٤ - «كتاب الأسرار (والتقديم للأدلة) فى الفروع»: مراد ملّا ٧٥٠ - ٧٥١ (ج ١، ٢، ٢٧٠ ورقة، ٣٤٤ ورقة، ٤٨٧ هـ)، سليم أغا ٢٧٩ (٤٨٩ ورقة، ١١٣٣ هـ)، عارف حكمت بالمدينة (جزءان، فى القرن الثامن الهجرى، انظر: شبيس فى، (١١٥/ ٩٠ ZDMG: هيدلبرج ٢٦١ (ج ٢، ٣، فى القرن الثانى عشر الهجرى، انظر: بيرنباخ (Berenbachin: ZS ١٠ /٨٦، رامبور ١/ ١٦٤، فقه ١٧ (ج ٢، ٤١٢ ورقة، ١١٣٣ هـ)؛ سراى، أحمد الثالث ٧٢٩/ ٢ (من ١٦٩ أ- ٢٦٣، ٦١٩ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٤٠)، ١١٠٦ (٣٠٢ ورقة، فى القرن التاسع الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٢٤٠، وانظر: فهرس ٢/ ٤١١)، ولى الدين ١٤٠٢ (٦٠٨ ورقة، فى القرن الثانى
_________
(١٣٥) تحذف مخطوطة قليج على ٦٩٠ التى ذكرها بروكلمان.','3','125',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (882,'عشر، الهجرى)، شهيد على ٦٨٩ (ج ١، ٣٦٣ ورقة فى القرن السادس الهجرى)، داماد إبراهيم ٤٩٠ (٥٥٩ ورقة، فى القرن التاسع الهجرى)، حميدية ٤٤٤ (٤٦٩ ورقة، فى القرن الحادى عشر الهجرى)، فيض الله ٥٦٠ (٦٥١ ورقة ١١١١ هـ)، ٦٦١ (ج ١، ٣١٣ ورقة، ٦٦٢ هـ)، نور عثمانية ١٣٧٥ (٣٣٣ ورقة، فى القرن الحادى عشر الهجرى)، أيا صوفية ١٠١٩ (٨١٤ ورقة، فى القرن الثانى عشر الهجرى)، ١٠٢١ (٦٨٣ ورقة، ٩٥٤ هـ)، كوبريلى ٥٣٣ (٦٤١ ورقة، ٩٨٥ هـ)، ٥٣٤ (٤٥٣ ورقة، ٩٨٤ هـ)، تشستربيتى ٥١٥٠ (ثلاثة أجزاء، ٧١٣ هـ)، عابدين بدمشق (فى القرن العاشر الهجرى، انظر: مجلة معهد المخطوطات العربية ٥/ ٢١٣).
٥ - «كتاب التعليقة فى مسائل الخلاف بين الأئمة)»:
عاطف ٦٦٠/ ٢ (من ورقة ٢٣٩ - ٢٨٧، ٩٦٦ هـ)؟ «جزء من مسائل الخلاف»، تيمور، عبادات ٣٥٩.

<span data-type="title" id=toc-2047>٢٨ - عبد الرحمن السّرخسى</span>
هو عبد الرحمن بن محمد السرخسى، كان تلميذا للقدورى، أتى البصرة وتولى فيها منصب القضاء مرتين، وتوفى سنة ٤٣٩ هـ/ ١٠٤٧ م.

<span data-type="title" id=toc-2048>أ- مصادر ترجمته:</span>
الجواهر للقرشى ١/ ٣٠٧ - ٣٠٨، تاج التراجم لابن قطلوبغا ٢٤ - ٢٥، كشف الظنون لحاجى خليفة ٣٤٦، ٤٧١، هدية العارفين ١/ ٥١٦، معجم المؤلفين لكحالة ٥/ ١٧٤.

<span data-type="title" id=toc-2049>ب- آثاره:</span>
«تكملة التجريد» وهو تكملة لكتاب «التجريد» للقدورى، وهبى ٤٤٢ (ج ٣، ٢٤٥ ورقة، ٧٨٠ هـ).

<span data-type="title" id=toc-2050>٢٩ - ابن نصرويه</span>
أبو الحسن على بن إبراهيم بن نصرويه، أصله من سمرقند، ولد سنة','3','126',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (883,'٣٦٥ هـ/ ٩٧٥ م، ثم جاء إلى بغداد، ويعد من علماء الحنفية ذوى المكانة فى عصره توفى فى طريق عودته من الحج سنة ٤٣٩ هـ/ ١٠٤٧ م، وقيل ٤٤٠ هـ.

<span data-type="title" id=toc-2051>أ- مصادر ترجمته:</span>
تاريخ بغداد للخطيب ١١/ ٣٤٢، الجواهر للقرشى ١/ ٣٤٩، شذرات الذهب لابن العماد ٣/ ٢٦٦.

<span data-type="title" id=toc-2052>ب- آثاره:</span>
«الفوائد»، الظاهرية، حديث ٢٩٧ (من ورقة ٤٣ - ٥٧، انظر: ناصر ٥٧).','3','127',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (884,'<span data-type="title" id=toc-2053>ب- فقه المالكية</span>
<span data-type="title" id=toc-2054>١ - مالك</span>
هو أبو عبد الله، مالك بن أنس بن مالك بن أبى عامر، الأصبحى، هو سليل أمير حميرى، ولد بالمدينة. ولا تتفق الروايات على سنة ولادته، فتذكرها بين سنتى ٩٠ هـ/ ٧٠٨ م و ٩٧ هـ/ ٧١٥ م. ويقال إنه كان فى شبابه يعيش بين جماعة من المغنين، ولكنه تحول بعد ذلك إلى دراسة الفقه حين نصحته أمه بذلك (١٣٦). ومن شيوخه ربيعة بن فروخ (المتوفى ١٣٦ هـ/ ٧٥٣ م أو ١٣٣ هـ)، وكان يعلم الفقه على أساس «الرأى»، والزّهرى وهشام بن عروة، ونافع مولى ابن عمر. ولقد قضى أكثر حياته بالمدينة المنورة. ولم يبد أى اعتراض سنة ١٤٥ هـ على ثورة محمد بن عبد الله العلوى، المطالب بالخلافة. ولما فشلت الثورة عاقبه والى المدينة، ولكنه تصالح بعد ذلك مع الحكومة. زار الخليفة هارون الرشيد حلقته الدراسية بالمدينة المنورة وهو يحج عام ١٧٩ هـ، وفى ذلك العام توفى مالك بالمدينة. ويعدّ مالك محدثا مدققا. عده أبناء الجيل التالى له مؤسسا لمدرسة مستقلة فى الفقه. وأهم تعاليم مالك التى تظهر فى موطئه الاعتراف «بالعمل»، أى بما هو معمول به فى المدينة، وبجانب ذلك يأتى «الحديث» مصدرا للاستدلال الفقهى/. ولا نستطيع التأكد الآن من مدى ما ورد عند مالك فى موطئه من وجهات نظر جديدة وأصيلة فى بحثه لهذا المبدأ، لأن سائر الكتب التى تحمل هذا العنوان لم تصل إلينا.

<span data-type="title" id=toc-2055>أ- مصادر ترجمته:</span>
التاريخ الكبير للبخارى ٣/ ١/ ٣١٠، المعارف لابن قتيبة ٢٥٠، ٢٩٠، تاريخ الطبرى، انظر
_________
(١٣٦) انظر: الأغانى (بولاق) ٤/ ٣٩ (الشنقيطى): ٢٢٢.','3','129',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (885,'الفهرس، المشاهير لابن حبان ١٤٠، الفهرست لابن النديم ١٩٨ - ١٩٩، «مكارم الأخلاق» لمؤلف مجهول من القرن الرابع الهجرى: البنغال ١٠٦٢ (مقام مالك عند الملوك)، طبقات الفقهاء للشيرازى ٤٢ - ٤٣، حلية الأولياء لأبى نعيم ٦/ ٣١٦ - ٣٥٥، الانتقاء لابن عبد البر ٨ - ٦٣، تذكرة الحفاظ للذهبى ٢٠٧ - ٢١٣، التهذيب لابن حجر ١٠/ ٥ - ٩، البداية والنهاية لابن كثير ١٠/ ١٧٤ - ١٧٥، الديباج لابن فرحون ١١ - ٢٩، النجوم الزاهرة لابن تغرى بردى ٢/ ٩٦ - ٩٧، الأعلام للزركلى ٦/ ١٢٨ معجم المؤلفين لكحالة ٨/ ١٦٨، بروكلمان ١/ ١٧٥ رقم ١، وانظر جولد تسيهر:
Goldziher, Muh. Stud. II ٢١٣ - ٢٢٦
وانظر برجشتراسر Bergstrasser ,Islam ١٤ /١٩٢٥ /٧٦ - ٨١.: وانظر يوسف شاخت فى دائرة المعارف الإسلامية J.Schacht ,EI.III ,٢٢٣ - ٢٢٧: و:
R. Brunschvig, Polemiquesmediev alesautourdurite de Mahk, Andalus ١٥/ ١٩٥٠/ ٣٧٧ - ٤٣٥ .. A. Bekir, Histoirede I''ecolemalikitee n Orient Jusqua''Lafindumo yen- age, Tunis ١٩٦٢, ٢٧ - ٩٩.
دراسات عن مالك بن أنس: ١ - «التوسط بين مالك وابن القاسم العتقى فى مسائل المدوّنة» للقاسم بن خلف بن عبد الله الطرطوشى (المتوفى ٨٧٨ هـ/ ١٤٧٣ م، انظر: الديباج لابن فرحون: ٢٢٥): مكناس ٢١٨.
٢ - «منازل الأئمة الأربعة» أبى حنيفة ومالك والشافعى وأحمد ليحيى بن إبراهيم بن أحمد بن محمد السلماسى» فاتح ٤٤٤٦/ ٢ (من ٨٩ أ- ١٣٩ ب، ٨٢٢ هـ).
٣ - «إرشاد السالك إلى مناقب مالك» ليوسف بن الحسن بن أحمد الحنبلى (المتوفى ٩٠٩ هـ/ ١٥٠٣ م انظر بروكلمان ٢/ ١٠٧)، الظاهرية تاريخ ٣٣٨ (٤٥٢ ورقة، ٨٨٧ هـ، بخط المؤلف. انظر: العش ٢٧٥، فهرس معهد المخطوطات العربية ٢ رقم ٢٢).
٤ - «تزيين الممالك لمناقب سيدنا الإمام مالك» للسيوطى، طبع بالقاهرة ١٣٢٤ هـ (قبل المدونة).
٥ - «مالك: حياته، عصره آراؤه وفقهه» لمحمد أبو زهرة، القاهرة ١٩٤٦ م.
٦ - «مالك بن أنس» لأمين الخولى. ثلاثة أجزاء، القاهرة ١٩٥١ م انظر أيضا عبد الله كنون فى مجلة معهد الدراسات المغربية ٥/ ١٩٥٧/ ٢٦٣ - ٢٧٤.

<span data-type="title" id=toc-2056>ب- آثاره:</span>
أولا: «الموطأ» هو كتاب حديث وكتاب فقه، ويقال إن أول نسخة منه كانت تضم ٩٠٠٠ حديث وإنه اختصره','3','130',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (886,'مرارا (١٣٧). كما أنه يضم بجانب ذلك «فتاوى» العلماء الثقات ويضم الموطأ فى صورته الأخيرة مائة حديث مسند، و ٢٢٢ حديث مرسل، و ٦١٣ حديث موقوف و ٢٨٥ رأى التابعين (١٣٨).
وهناك روايات عديدة مختلفة للموطأ، وهو يشبه فى ذلك «الجامع الصحيح» للبخارى، ومع ذلك لا نستطيع أن نستنتج من هذا الاختلاف بين الروايات أن مالكا كان يجيز موطأه غير متحرج ولا مدقق (١٣٩) أو أن أحد تلاميذه وليس المؤلف- هو الذى نقحه (١٤٠). على أن الأخبار واضحة فى أن مالكا هو الذى ألف الموطأ إلى آخر كلمة فيه، وأنه رواه «قراءة» و «مناولة» (١٤١)./ والسبب فى هذا الاختلاف يجب أن يبحث فى ضوء الأوقات المختلفة التى نشأت فيها. وأما فيما يتعلق بملاحظات الرواة على الروايات، فإن ذلك كان أمرا مألوفا فى تلك الحقبة من الزمن، وفى كل مجال من مجالات العلم.
دراسات حول روايات الموطأ: ١ - «الزيادات التى تقع فى الموطأ عند يحيى بن يحيى عن مالك» ليوسف بن عبد الله بن عبد البر (المتوفى ٤٦٣ هـ/ ١٠٧٠ م (انظر بروكلمان ١/ ٣٦٨)، صائب بأنقرة ٣٣٣٢/ ٢ (فى القرن السابع الهجرى)، وطبع فى القاهرة ١٣٥٠ هـ.
٢ - «ما أغفله الخطيب فى الرواة عن مالك» لرشيد الدين يحيى بن على بن عبد الله بن على القرشى (المتوفى ٦٢٢ هـ/ ١٢٢٥ م، انظر: معجم المؤلفين لكحالة ١٣/ ٢١٣)، سراى أحمد الثالث ٦٢٤/ ٧ (من ١٠٢ ب- ١٠٣ ب، ٧٢٩ هـ).
٣ - «ترتيب المسالك لرواة موطأ مالك» لأبى على بن الزهراء (كتبه قبل سنة ٧٠٣ هـ/ ١٣٠٣ م)، ابن يوسف بمراكش ٤٧٦ (٧٠٣ هـ).
٤ - «إتحاف السالك برواة الموطأ عن الإمام مالك» لمحمد بن عبد الله بن حمد بن أحمد القيسى (المتوفى ٨٤٢ هـ/ ١٤٣٨ م. انظر معجم المؤلفين لكحالة ٩/ ١١٢)، الأزهر ١/ ٣٠٧، مجموع ١٠٠٣ (من ورقة ٨١ - ١٥٨، ٩٠٣ هـ).
J. Schacht, Deuxeditionsinco nnuesdu Muwatta, in: Stud. Or. inonoredi G. I. della Vida ٢/ ١٩٥٦/ ١٤٧٧ - ٤٩٢
_________
(١٣٧) تعليق فى الأصول لالكياالهرّاسي، وانظر: التعريف بكتاب الموطأ لعلى عبد اللطيف، القاهرة ١٣٨٢ هـ، ص ٨.
(١٣٨) انظر: أيضا ص ١٦ من المرجع السابق.
(١٣٩) انظر Galdziher ,Muh.Stud.II ,٢٢١:
(١٤٠) انظر:
Schacht. Deuxeditiopsineo snudsdu Muwattain: Stud Orinonoredi G. L. della Vida II, ٤٧٧
(١٤١) انظر: آداب الشافعى لابن أبى حاتم (القاهرة ١٩٥٣) ٢٨.','3','131',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (887,'أما بخصوص القطع الباقية من هذا الكتاب فالملاحظ أن البخارى لم يستخدم «الموطأ» مباشرة وإنما أفاد من المصادر التى نقلت عنه (انظر (Sezgin ,Buh.Kayn.٦٠: وبخاصة اللوحة الملحقة بذلك البحث (ص ٣٠٥ - ٣١٧).
ومن الروايات العديدة للموطأ يوجد فى الوقت الحاضر ثلاث روايات كاملة ورواية غير كاملة، كما توجد بعض روايات لا تضم إلا قطعا منه:
١ - رواية يحيى بن يحيى بن كثير المصمودى (المتوفى ٢٣٤ هـ/ ٨٤٨ م فى قرطبة)، برلين ١١٤٣ (١٥٢ ورقة، حوالى ٧٠٠ هـ)، باريس ٦٧٥ (١٥٤ ورقة، ٧٢٦ هـ)، ٦٧٦ (قطعة، ٢٦ ورقة، فى القرن الثامن الهجرى)، ٦٧٧ (١٧١ ورقة، ١١٩٨ هـ)، ٦٧٨ (١٩٥ ورقة، ١١٩١ هـ)، ٤٥٣٨ (١١٠ ورقة، ٥٩٦ هـ)، ٥٤٠٤ (١٣٩ ورقة، فى القرن الثالث عشر الهجرى) (١٤٢)، الزيتونة بتونس ٢/ ٢٠٦ - ٢١٧، رقم ١٠٥١ (١٩٤ ورقة، ١١٩٦ هـ)، ١٠٥٢ (١٩٤ ورقة، ١١٩٣ هـ)، المكتبة العامة بالرباط، ليفى بروفنسال ١٨ - ٢١ (أربع نسخ حديثة، انظر أيضا: فهرس ١/ ٨٠، القرويين بفاس ١٦٥ (٦٢٨ هـ)، ٦٠٥ (٥٠٢ هـ انظر. Schachtlin: Etudes Or.١ /٢٧٢: وانظر أيضا: الفاسى فى مجلة معهد المخطوطات العربية ٥/ ١٩٥٩/ ١٢)، سليم أغا ٢٤١ (١٤٣) (١٤٣ ورقة، ١١٢٦ هـ)، بنگيبور ٥/ ١/ ١٢١ (٤٢٠ ورقة، ١٢٣٣ هـ) (١٤٤)، الجزائر ٤٢١ (١٦٧ ورقة، ١١٨٦ هـ)، ٤٢٢، ٤٢٣ (١٤٥) (٤٨٥ ورقة، فى القرن الثانى عشر الهجرى)، تلمسان ١٩، ٢٠، رامبور ١/ ١٢٠ فقه رقم ٤١٥، (طنجة- الجامع الأعظم ٥/ ٥٠)؛ ولى الدين ٨٦١ (٢٤٧ ورقة، ٦٤٣ هـ)، نور عثمانية ١٢٧٦ (٢٤١ ورقة، فى القرن العاشر الهجرى)، ١٢٧٧ (١٧١ ورقة، ١١٦٢ هـ)، فاتح ١٢٠٧ (٣٤٠ ورقة، ١١١١ هـ)، ١٢٠٨ (٨٦ ورقة، ١١٤١ هـ)، ١٢٠٩ (٢٠٥ ورقة، ٧٨٥ هـ)، عاطف ٦٣٢ (٣٤٣ ورقة)، مكتبة جامعة إستنبول ٤٣١٧ (٥٤٨ هـ)، راغب ٣٥٧ (٢٣٦ ورقة، ١١٦٣ هـ)، شهيد على ٥٨٨ (٩٩٠ هـ)، ٥٨٩ (٩٨٦ هـ)، جار الله ٤٢٨ (٨٠٢ هـ)، أسعد أفندى ٤٤٩ (١٨٨ ورقة)، حكيم أوغلى ٢٨٩ (٢٧٣ ورقة، ١١١١ هـ)، سراى أحمد الثالث ٢٧٥ (٢٣٩ ورقة، ٥٤٦ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ١١١، انظر: فهرس ٢/ ١٣)، سراى، مدينة ١٠٣ (فى القرن العاشر الهجرى)، صائب بأنقرة ٢١٨٠/ ١ (فى القرن الخامس الهجرى)، تشستربيتى ٣٠٠١ (الثلث الأخير من المخطوطة، ٢٧٧ هـ)، باريس ٥٤٦٥، ٥٧٢٩ (انظر:
فايدا ٥١٧)، الأزهر ١/ ٦٢٦ - ٦٢٩ (٣٤ نسخة)، بلدية الإسكندرية ٢١٥١ د (١٠٧٩ هـ)، الرباط
_________
(١٤٢) تحذف مخطوطة المتحف البريطانى ١٥٩٠ التى ذكرها بروكلمان.
(١٤٣) ليس رقم ٣٤١ كما ذكر بروكلمان.
(١٤٤) يحذف رقم ٢٤٨٣ عند بروكلمان.
(١٤٥) ليس ٤٢١ - ٤٢٤ كما ذكر بروكلمان.','3','132',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (888,'٨٤٠ (١٣٤ ورقة، فى القرن الرابع الهجرى)، الجلاوى بمراكش ٧٠٨ (٦١٣ هـ)، وطبع مرات عديدة فى مختلف البلاد الإسلامية، منها طبعة محمد فؤاد عبد الباقى فى جزءين بالقاهرة ١٩٥١، انظر: مجلة -٢ Oriens ٨ ,١٩٥٥ /١٨٨، رواية محمد بن الحسن الشيبانى (المتوفى ١٨٩ هـ/ ٨٠٤ م)، كوبريلى ٤٦١ (٢٠٢ ورقة، ٥٦٥ هـ)، سراى، أحمد الثالث ٢٧٦ (١٣٥ ورقة، ١٠٦٧ هـ)، رئيس الكتاب ٢٧٤ (٩٦٧ هـ)، برلين ١١٤٤ (١١٧ ورقة، ١١٩٨ هـ)، المتحف البريطانى ١٥٩٠، مخطوطات شرقية ٩٠ (١٥١ ورقة، ١١٣٠ هـ)، محمود أفندى ٤٩٩ (١٣٧ ورقة، ١٢٦٤ هـ)، (المتحف الآسيوى ببطرسبورج المجموعة القوقازية ٩٤٤، بشاور ٢٦٨)، يوسف أغا بقونية ٣٨٢٣ (١١٧ ورقة، ١١٧٥ هـ)، دار الكتب بالقاهرة ١/ ١٥٥ حديث ٤٣٩ (١١٤٥ هـ)، ٤٤٠ (٤٩٠ هـ)، جاريت ١١٣٨ (١٠٤ ورقة)، رامبور ١/ ١٢٠، فقه ٤١٦ - ٤١٧؛ البنغال ١٦٢ (١١٧ ورقة، ٩٣٣ هـ)، بنگيبور ٥/ ١/ ٢ رقم ١٢٢ (١١٩ ورقة، ١٠٠٥ هـ)، الحمزاوية بالرباط ٣٠ (٨٥٥ هـ)، وطبع فى لوديانا ١٨٧٦ م وفى لكنو ١٨٨٠ م، وفى قازان ١٩٠٩ م، وفى القاهرة بتحقيق عبد الوهابعبد اللطيف.
٣ - رواية سويد بن سعيد بن سهل الحدثانى (المولود حوالى ١٤٠ هـ/ ٧٥٧ م والمتوفى ٢٤٠ هـ/ ٨٥٤ م، انظر: تاريخ بغداد للخطيب: ٩/ ٢٢٨ - ٢٣٢، والتهذيب لابن حجر ٤/ ٢٧٢ - ٢٧٥)، الظاهرية، حديث ٣٦٠ (ناقص، ١١٧ ورقة، عليه سماع من سنة ٤٢٩ هـ انظر Schacht ,in: Etudesor I /٤٧٨ - ٤٨٣: وفهرس معهد المخطوطات العربية ١/ ١١١ حيث ذكر خطأ برقم ٢٦١) ٤ - رواية يحيى بن عبد الله بن بكير القرشى (عاش ١٥٤ هـ/ ٧٧١ م- ٢٣١ هـ/ ٨٤٥ م، انظر: تذكرة الحفاظ للذهبى ٤٢٠، التهذيب لابن حجر ١١/ ٢٣٧ - ٢٣٨). وهذه الرواية كان يطلق عليها فى زمن متأخر تارة اسم: «موطأ الإمام المهدى» وتارة أخرى: «محاذى الموطأ». هذا العنوان الأخير وضع خطأ لمختصر ابن تومرت انظر بروكلمان ملحق ١/ ٢٩٨ رقم ١ أالظاهرية، مجموع ٤٣ (٢٧٢ ورقة، فى القرن الخامس الهجرى)، الجزائر ٤٢٤ (١٤٥ ورقة، ٥٩٠ هـ)، القرويين بفاس ١٨١ (قبل ٨١١ هـ)، الكتانى بالرباط ٥٢٤ (انظر: شاخت فى المصدر السابق: ٤٨٩ - ٤٩٠)، الجلاوى بمراكش ١٢٢٢ (٨٧ ورقة، ٥٩٧ هـ)، وطبع فى عليگره ١٩٠٧ هـ.
٥ - رواية أبى عبد الله عبد الرحمن بن القاسم (المتوفى ١٩١ هـ/ ٨٠٦ م). وهذه الرواية بقيت فى «الملخص» لعلى بن محمد بن خلف القابسى (المتوفى ٤٠٣ هـ/ ١٠١٢ م، انظر: رقم ٣١ من هذا الفصل):
عارف حكمت بالمدينة ٣٥ (انظر (Spiesin: ZDMG ٩٠ /١٩٣٦ /١٠٩.:','3','133',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (889,'٦ - رواية أبى مصعب أحمد بن أبى بكر الزّهرى (المتوفى ٢٤٢ هـ/ ٨٥٦ م) الظاهرية مجموع ٦٣/ ١٥ (قطعة من ١٨٢ أ- ١٨٩ ب، فى القرن السابع الهجرى)، فى هذه الرواية «جزء فيه ثلاثة عشر حديثا موافقة من موطأ أبى مصعب»: الظاهرية، مجموع ٥٢ (من ٥٨ - ٦١ أ، فى القرن الثامن الهجرى).
وتضم مكتبة القيروان قطعا مخطوطة لروايات أخرى: منها رواية ابن وهب وهى التى بقى منها قسم فى كتاب: «اختلاف الفقهاء» للطبرى، ولقد استخدم هذه الرواية بالسند الآتى «حدّثنى يونس (بن عبد الأعلى) عن ابن وهب عن مالك» (١٤٦).
شروح لرواية يحيى بن يحيى المصمودى: ١ - «تفسير غريب الموطأ» لأحمد بن عمران بن سلامة الأخفش (عاش قبل ٢٥٠ هـ/ ٨٦٤ م)، صائب بأنقرة ٢١٨٠ (من ١٨٢ أ- ٢٠٠ ب، فى القرن الخامس الهجرى)، القيروان (٣٩٤ هـ، انظر:
مجلة معهد المخطوطات العربية ٢/ ٣٦٢).
٢ - شرح ليحيى بن زكريا بن إبراهيم بن مزين (المتوفى ٢٥٩ هـ/ ٨٧٣ م)، القيروان، (قطعة ٣٩٤ هـ، انظر مجلة معهد المخطوطات العربية ٢/ ٣٦٢).
٣ - شرح لخلف بن الفرج (بن عثمان الكلاعى، المتوفى ٣٧١ هـ/ ٩٨١ م، انظر: تاريخ علماء الأندلس لابن الفرضى، رقم ٤١٥)، القيروان (٣٩٤ هـ، انظر: مجلة معهد المخطوطات العربية ٢/ ٣٦٢).
٤ - شرح لأبى المطرّف عبد الرحمن بن مروان القنازعى (المتوفى ٤١٣ هـ/ ١٠٢٢ م) ولم يبق من هذا الشرح إلا قطعا بمكتبة القيروان (انظر: مجلة معهد المخطوطات العربية ٢/ ٣٦٢).
٥ - (يطابق رقم ٧ فى ملحق بروكلمان) «النّامى» لأحمد بن نصر الداودى (المتوفى ٤٠٢ هـ/ ١٠١١ م، انظر: رقم ٣٠ من هذا الفصل)، القرويين بفاس ١٧٥/.
٦ - «الاستذكار فى شرح مذاهب علماء الأمصار مما رسمه الإمام مالك فى الموطأ من الرأى والآثار» ليوسف بن عبد الله بن محمد عبد البر (المتوفى ٤٦٣ هـ/ ١٧٠ م، انظر: بروكلمان ١/ ٣٦٨)، المتحف البريطانى مخطوطات شرقية ٥٩٥٤ (٢١٦ ورقة فى القرن الثامن أو التاسع الهجرى، انظر: إلّيس. قائمة وصفية ١٧)، القاهرة ثان ١/ ٨٩، حديث ٢٤ (جزءان تالفان)، محمود بالمدينة المنورة ٥٥ (انظر؛ (Spies ,in: ZDMG ٩٠ /١٩٣٦ /١١٠: سراى، مدينة ٣٠٨ (ج ٢، ١٤٥ ورقة، ١٢٣١ هـ)، ٣٠٩ (ج ٥ ٢٧٠ ورقة، ١٢٣١ هـ)، ٣٢٦ (قسم واحد، ١٣٠ ورقة، ٨٥٠ هـ)، ٣٢٧ (١٣٠ ورقة، ٨٥٠ هـ، انظر: فهرس
_________
(١٤٦) يحذف ما ذكره بروكلمان من «الموطأ الصغير» لابن وهب وأنه موجود بكوبريلى.','3','134',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (890,'٢/ ١٤ - ١٥)، برلين الشرقية ١٦٣٩، الجلاوى بالرباط ٥٨٨ (القسم السابع، ١٦٠ ورقة، فى القرن السابع الهجرى)، الكنانى بالرباط ١٧٨ (قسم واحد ٤١ ورقة)، الإسكوريال ٩٨٦؟ (قسم واحد، ١٦٩ ورقة، ٤٧٤ هـ، انظر:
(Vajda, in: Andalus, ٨٢/ ١٩٦٣/ ٦٢ - ٦٣.
ويوجد هذا الشرح مع «المقدمة» فى: الكتانى بالرباط، مجموعة ٧١/ ١٠ (١٧٢ ورقة)، الظاهرية، مجموع ٧١ (المقدمة فقط من ١١٦ أ- ١٢٢ ب، ٥٧٣ هـ).
وأعاد المؤلف نفسه تأليف هذا الشرح وهذبه بعنوان: «التمهيد لما فى الموطأ من المعانى والأسانيد»، بالقاهرة ثان ١/ ٩٨ حديث ٣١٥ (٢٤٨ ورقة، ٤٠٣ ورقة، فى القرن الثامن الهجرى)، ٧١٦ (٢٩٥ ورقة، فى القرن الثامن الهجرى، انظر: فهرست معهد المخطوطات العربية ١/ ٦٩)، القرويين بفاس ١١٦ (٥٠٥ هـ) ١٧٧، ٨٠٧ (٦٨٢ هـ)، الظاهرية، حديث ٣٣٢ (١٩٢ ورقة، فى القرن الثامن الهجرى)، محمود بالمدينة المنورة ٤٧ (انظر:
Susa, Bull. de Corr. Afr. ١٨٨٣. (Spies, ZDMG ٩٠/ ١٩٣٦/ ١١٠.
أيا صوفيا ٦٧ ج ٢ (١٨٩ ورقة، فى القرن العاشر الهجرى)، فيض الله ٢٩٥ (مجلد واحد، ٢٧٥ ورقة، ٧٣٧ هـ، انظر: فهرست معهد المخطوطات العربية ١/ ٦٩)، ٤٦٨ (ج ٨، ٢٤٦ ورقة، ٧٣٨ هـ. انظر:
فهرست معهد المخطوطات ١/ ٦٩)، سراى مدينة ٣٢٦ (مجلد واحد ١٣٠ ورقة، فى القرن الثامن الهجرى، انظر المرجع السابق ١/ ٧٠)، تيمور بالقاهرة ٢/ ١٦٤ حديث ٢٩٢ (ج ٥، ٢٩٦ ورقة، ٧٢٠ هـ، انظر:
المرجع السابق)، صائب بأنقرة ٢٢٨١/ ٣ (قسم ١٤٩ ب- ٢٤٥ ب، فى القرن السابع الهجرى)، طلعت بالقاهرة، حديث ٦٧٢ (ج ٣، ٢٧٨ ورقة، نسخة حديثة، انظر: مجلة معهد المخطوطات العربية ٣/ ٢٣٠)، الجلاوى بالرباط ١٣، (ج ١، ٢)، السعيدية بحيدرآباد، حديث ٩٨ (٢٤١ ورقة، فى القرن الثامن الهجرى).
وجرّد هذا الكتاب من أسانيده، بعنوان: «تجريد التمهيد لما فى الموطأ من المعانى والأسانيد والتّقصّى لحديث الموطأ وشيوخ الإمام مالك»، القاهرة ثان ٩٨، حديث ٩ ش، وطبع هذا الكتاب بالقاهرة ١٣٥٠ هـ.
وعلى هذا الكتاب يوجد كتاب اخر بعنوان: «التقريب لكتاب التقصى»، لأبى عبد الله الأنصارى (؟
محمد بن الحسين بن أحمد بن محمد الظاهرى المتوفى ٥٣٢ هـ/ ١١٣٧ م، انظر: معجم المؤلفين لكحالة ٩/ ٢٣٢)، القرويين بفاس، الرقم القديم (٥١٩).','3','135',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (891,'ومن الشرح السابق مختصر لمحمد بن أحمد بن فرح القرطبى (المتوفى ٦٧١ هـ/ ١٢٧٢ م انظر بروكلمان ١/ ٤١٥)، (القرويين بفاس ٥٢٣) (١٤٧) ٧ - (يطابق رقم ٢ فى ملحق بروكلمان) «المنتقى شرح الموطأ» لأبى الوليد سليمان بن خلف الباجى (المتوفى ٤٧٤ هـ/ ١٠٨١ م انظر بروكلمان ١/ ٤١٩) القاهرة ثان ١/ ١٥٢ حديث ١٠٦٥ (أقسام من ٥ - ١٦)، القرويين بفاس ١٧١ - ١٧٢، عثمان بالمدينة المنورة ٢ (انظر (Spies ,in ZDMG ٩٠ /١٩٣٦ /١١٠: انظر أيضا Vajda ,in: Andalus ٢٨ /١٩٣٦ /٦٣ وطبع فى سبعة مجلدات بالقاهرة ١٣٣١ - ١٣٣٢ هـ.
٨ - شرح لأبى الوليد هشام بن أحمد بن هشام الوقّاشى (المتوفى ٤٨٩ هـ/ ١٠٩٦ م انظر بروكلمان ١/ ٣٨٤)، الإسكوريال ١٠٦٧ (١٣٥ ورقة، ٧١٤ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٨٥، وانظر) -٩ (Vajda ,in: Andalus ٢٨ /٨٧ - ٨٨. يطابق رقم ٣ أعند بروكلمان) «الدرّة الوسطى فى مشكل الموطأ» لأبى عبد الله محمد بن خلف بن موسى الأنصارى الإلبيريّ (المتوفى ٥٣٧ هـ/ ١١٤٢ م انظر بروكلمان ملحق ١/ ٧٦٢)، انظر: الوافى بالوفيات للصفدى ٣/ ٤٦)، المتحف البريطانى ١٩١، إضافات ٩٥١٩/ ١ (الأوراق ١ - ١٨٢، ٨١٠ هـ).
١٠ - (يطابق رقم ٣ فى ملحق بروكلمان) «المسالك فى شرح الموطأ» لأبى بكر محمد بن العربى (المتوفى ٥٤٣ هـ/ ١١٤٨ م انظر بروكلمان ملحق ١/ ٧٣٢)، القرويين ١٨٠ (٧١١ هـ)، الجزائر ٤٢٥ - ٤٢٦ (ثلاثة مجلدات، ١٠٢٩) (١٤٨)، طلعت بالقاهرة، حديث ٧٩٣ (١٣٠ ورقة، ٦٩١ هـ، انظر: مجلة معهد المخطوطات العربية ٣/ ٢٣٠)، القاهرة، ملحق ٣/ ٥١ رقم ٢١٨٧٥ ب ٧٦٦ ورقة، ١٣٥٩ هـ، منسوخة عن مخطوطة طلعت السابقة)، الحمزاوية بالرباط ٢٤ (ج ١، ٤، ٥٧٩ هـ).
١١ - «القبس على موطأ مالك بن أنس» للمؤلف السابق، نور عثمانية ١١١٥ (١٧٨ ورقة، ٨٧٢ هـ)، القرويين بفاس ١٧٠، ١٨٠ (انظر شاخت rO. tE: ni, thcahcS.I ,٢٨٢. الجزائر ٤٢٧ (١٠١ ورقة، ٦٣٦ هـ)، الجلاوى بالرباط ٢٥، مراكش ٢٤٣.
١٢ - «الأنوار فى الجمع بين المنتقى والاستذكار» لمحمد بن سعيد بن أحمد بن زرقون، المتوفى ٥٨٦ هـ/ ١١٩٠ م، انظر: معجم المؤلفين لكحالة ١٠/ ٢٥)، الأزهر ١/ ٤١٣ حديث ٤٢ (ج ٣، فى القرن
_________
(١٤٧) يحذف كتاب (الكافى) وهو مختصر من كتاب الاستذكار الذى ذكره بروكلمان لأنه كتاب مصنف فى موضوعات فقهية، انظر شاخت Schacht ,Et.Or.II ٢٧٨.:
(١٤٨) تحذف نسخة القرويين بفاس ٥٢٦ عند بروكلمان.','3','136',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (892,'السابع الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٧٨)، الأوقاف بالرباط ١٤٥ (ج ٤، ٧٠٢ هـ).
١٣ - «المختار الجامع بين المنتقى والاستذكار» لأبى عبد الله محمد بن عبد الحق بن سليمان اليعفرى النّدرومى التّلمسانى (المتوفى ٦٢٥ هـ/ ١٢٢٨ م انظر بروكلمان ملحق ١/ ٦٢٧): القرويين بفاس ١٧٣ (ج ١)، ١٧٤ (ج ٢، ٧٠٢ هـ)، ١٧٦ (ج ١، انظر: شاخت:
. (Schacht, in: Et. Or. I, ٢٨٢ - ٢٨٣.
١٤ - (يطابق رقم ٤ فى ملحق بروكلمان) «العهد الكبير لأبى على بن الزّهراء (كتبه سنة ٧٠٣ هـ/ ١٣٠٣ م): القرويين بفاس الرقم القديم (٥٢١).
١٥ - «الممهّد الكبير الجامع لمعانى السنن والأخبار وما تضمنه موطأ مالك ... إلخ» - لأبى على عمر بن على بن يوسف بن هادى بن عثمان العثمانى الوريغلى: القرويين بفاس ١٧٨ (كراستان ٧٠٩ - ٧١٠ هـ، انظر: مجلة معهد المخطوطات العربية ٥/ ١٠)، اليوسفية بمراكش ٤٧٣.
١٦ - (يطابق رقم ٥ فى ملحق بروكلمان) «تنوير الحوالك» لجلال الدين السيوطى (المتوفى ٩١١ هـ/ ١٥٠٥ م): مراد ملّا ٥٣٨ (١١١ ورقة، ١٠١٦ هـ، نسخة منقولة عن نسخة المؤلف)، بنگيبور ٥/ ١/ ٣ رقم ١٢٣ (٢٣٠ ورقة، ١٣٠٠ هـ)، محمود بالمدينة المنورة ٤٨ (انظر (Spies ,in: ZDMG ٩٠ /١٩٣٦ /١١٠.، : بلدية الإسكندرية ٢٢٩١ ج (١١١٠ هـ)، (قولة ١/ ١٠٧)؛ سراى، مدينة ٣٢٨ (٢١٦ ورقة، ١١٠٠ هـ، انظر: الفهرس ٢/ ١٦)، الكتانى بالرباط ٤٤٦ (١١١٠ هـ)، الأزهر ١/ ٤٣٤، حديث ٣٦٣ (٢٢٧ ورقة)، ٦٢٥ (١٦٢ ورقة)، وطبع بالقاهرة ١٣٤٣ هـ، ١٣٥٣ هـ.
١٧ - «أنوار كوكب أنهج المسالك بمزج موطأ الإمام مالك» لمحمد بن عبد الباقى الزّرقانى (المتوفى ١١٢٢ هـ/ ١٧١٠ م انظر بروكلمان ٢/ ٣١٩) قليج ٢٥٨ (ج ٢، ٤٨٠ ورقة، ١١١٦ هـ)، ٢٥٩ (ج ٣، ٤٨٣ ورقة، ١١١٦ هـ)، الزيتونة بتونس ٢/ ١٣٠ - ١٣٣ (١٤٩)، رقم ٨١٢ - ٨١٤ (ثلاثة مجلدات) ٨١٥ - ٨١٧ (ثلاثة مجلدات، ١١٢٢ هـ)، ٨١٨ - ٨٢٠ (ثلاثة مجلدات، ١١٣٥ هـ) ٨٢١ (١١٦٧ هـ)، القاهرة ثان ١/ ١٢٨، حديث ٢٣٧ (جزءان)، (قوله ١/ ١٣٢) (١٥٠)، حكيم ٢٥٥، ٢٥٦ برتو ١١٦ - ١١٨، نور عثمانية ١١١٦ - ١١١٨، فاتح ١٠٠٠ - ١٠٠٥، أيا صوفيا ٦٠ - ٦٢، كوبريلى ٢/ ٦٠ - ٦١ (٤٣٨ ورقة، ٣٨٦ ورقة، ١١٥٢ هـ)، عاطف ٥٦٢ - ٥٦٤، ولى الدين ٧٦٠ - ٧٦١، يوسف بقونية ٣٨٢٤ - ٣٨٢٥ (ج ١، ٢، ٤٧١ ورقة، ٤٧٧ ورقة، ١١١٧ هـ)، ٣٨٢٦ (ج ٣ ٣٥٢ ورقة، ١١١٢ هـ، يحتمل أن تكون
_________
(١٤٩) يصحح ما عند بروكلمان.
(١٥٠) تحذف مخطوطة داماد زاده ٥٣٧ عند بروكلمان.','3','137',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (893,'بخط المؤلف)، الكتانى بالرباط ٣٥٦ (٤٢٦ ورقة)، ٦١٣ (٤٤٤ ورقة، بخط المؤلف)، بالرباط ٧٢١ (ثلاثة مجلدات)، ٩٥٠ (ج ٣ انظر: فهرس ١/ ٦٤)، ييل ٧٠٦، ٩٨٢ (ج ١، ٤، ٢٣٢ ورقة، فى القرن الثالث عشر الهجرى)، الأوقاف ببغداد ٢٩٦٦ انظر: طلس ٣٦، وطبع فى أربعة مجلدات بالقاهرة، ١٢٧٩ - ١٢٨٠، ١٣١٠، ١٣٢٠، ١٣٢٥ هـ، ١٩٣٦ م.
١٨ - «المصفّى»: وهو شرح باللغة الفارسية لولى الله بن عبد الرحيم الدّهلوى (المتوفى ١١٧٦ هـ/ ١٧٦٢ م) بنگيبور ١٤/ ٥٨ رقم ١٢٠٢ (٣٠٢ ورقة، ١٢٠٦ هـ) (١٥١)، طبع حجر فى دلهى ١٢٩٣ هـ.
١٩ - شرح لأبى العباس أحمد بن الحاج المكى السّدراتى (المتوفى ١٢٥٣ هـ/ ١٨٣٧ م)، الرباط ٢٥٢ (الربع الأول ٣٠٥ ورقة، انظر: بروفنسال ٢٢).
٢٠ - «كشف الغطاء عن معانى ألفاظ الموطأ» لعمر بن محمد حمد، باريس ٥٤١٤، ٥٤٦٦ (الأوراق ١٦٢ - ٢٨٥، انظر: فايدا ٤٢١).
٢١ - شرح لأبى عبد الله بن محمد المدنى بن على الفاسى (المتوفى ١٣٠٢ هـ/ ١٨٨٥ م، انظر: معجم المؤلفين لكحالة ١٢/ ١٠).
٢٢ - «المحلىّ على أسرار الموطا» لسلام الله بن شيخ الإسلام بن فخر الدين: المكتب الهندى، دلهى مخطوطات عربية ١٨٠ (١١٧٠ هـ، انظر: شاخت» -٢٣ (Scacht ,Bibl.I.٢ b. المسالك على موطأ مالك» لإدريس القابسى (؟ )»، عثمان بالمدينة المنورة ٣ (انظر Spies ,in: (ZDMG ٩٠ /١٩٣٦ /١١٠: رقم ١٠؟
شروح للموطأ فى رواية محمد بن الحسن الشيبانى: أ- شرح لعلى القارى الهروى (المتوفى ١٠١٤ هـ/ ١٦٠٥ م انظر بروكلمان ٢/ ٣٩٤) بالقاهرة، حديث ٣٢٣ (٤٣٣ ورقة، ١٢٦٩ هـ)، السليمانية ٢٨٩ (٤٧٤ ورقة، ١٠١٣ هـ، بخط المؤلف)، فيض الله ٤٦٧، (٣٥٣ ورقة، ١٠١٣ هـ)، راغب ٣٢٨ (٣٢٦ ورقة، ١١٣٠ هـ)، يوسف أغا بقونية، حديث ١٤٣ (مجلدان ١٠١٣ هـ؛ (Schacht ,I ,٢ a ,III ,٢٢ a، سراى؛ أحمد الثالث ٤٢٠ (٢٩٦ ورقة، ١١١٦ هـ)، سراى: مدينة ٣٢٤ (١٧٨ ورقة، انظر: فهرس ٢/ ١٤)، رئيس الكتاب ٢١٠ (١٠٩ ورقة)، ٢١١ (٤١٨
_________
(١٥١) تحذف مخطوطة آصفية ١/ ٦٨٦ عند بروكلمان.','3','138',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (894,'ورقة)، حكيم ٢٥٤ (٣٤١ ورقة، فى القرن الثانى عشر الهجرى)، لاله لي ٥٨١ (٢٢١ ورقة، ١١٣٣ هـ)، ولى الدين ٧٦٢ - ٧٦٣، مكتبة جامعة إستنبول ٨٦٩.
ب- شرح لإبراهيم بن حسين بيرى زاده (المتوفى ١٠٩٦ هـ/ ١٦٨٥ م): يوسف أغا بقونية ٣٨٣٤ (٥١٠ ورقة، ١١١٠ هـ).
ج- «المهيّأ فى كشف الموطأ» (١٥٢)، لعثمان بن يعقوب بن حسين الكماخى (المتوفى ١١٧١ هـ/ ١٧٥٨ م، انظر (Osm.Muell.I ,٣٦٧، : قليج ٢٦٠، (٥١٧ ورقة فى القرن الثانى عشر الهجرى)، راغب ٣٢٧ (٥٠٩ ورقة، ١١٦٧ هـ)، القاهرة ثان ١/ ١٥٣، حديث ٥٨٦ (١١٧٠ هـ)؛ سراى، مدينة ٣٢٥ (٤٣١ ورقة، ١١٧٠ هـ، بخط المؤلف، انظر: فهرس معهد المخطوطات العربية ١/ ١١١، فهرس ٢/ ١٦).
د- «التعليق الممجّد» (١٥٣) لعبد الحى اللكنوى (المتوفى ١٣٠٤ هـ/ ١٨٧٧ م)، طبع بالهند ١٢٩٢ هـ.
كتب أخرى على الموطأ: ١ - «الملخص لما فى الموطأ من الحديث المسند» لعلى بن محمد بن خلف القابسى (المتوفى ٤٠٣ هـ/ ١٠١٢ م انظر ص ١٦٢): بنگيبور ٥/ ١/ ٩ رقم ١٢٨ (٨٩ ورقة، ٦٢٨ هـ)، عارف حكمت بالمدينة ٣٥ (انظر (Spies ,in: ZDMG ٩٠ /١٩٣٦ /١٠٩؛ : شهيد على ٣٩٠/ ٢ (من ٤ أ- ٨٨ ب، فى القرن الحادى عشر الهجرى)، ٥٥٦ (١٠٧ ورقة، ٧١١ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ١٠٩)، القرويين بفاس ٨٠٥، ١٣٩١، طلعت حديث ٦٦٢.
٢ - «التعريف بمن ذكر فى موطأ مالك بن أنس من الرجال والنساء» لأبى عبد الله محمد بن يحيى بن أحمد القرطبى بن الحذّاء (المتوفى ٤١٦ هـ/ ١٠٢٥ م، انظر ص ١٦٤) (١٥٤)، القرويين بفاس ١١٨، ١٧٩ (٦٤٦ هـ)، ٩٩٣.
٣ - «أسماء الرواة للإمام مالك بن أنس» لأبى بكر أحمد بن على الخطيب البغدادى (المتوفى ٤٦٣ هـ/ ١٠٧١ م انظر بروكلمان ١/ ٣٢٩).
ووصل إلينا منه: «مجرد أسماء .... إلخ» لأبى الحسين يحيى بن عبد الله بن على القرشى (المتوفى
_________
(١٥٢) ذكره بروكلمان فى الشروح لروايات مختلفة للموطأ والصحيح ما ذكرنا.
(١٥٣) ليس «المنجد» كما ذكر بروكلمان.
(١٥٤) يصحح رقم ٨ عند بروكلمان','3','139',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (895,'٦٦٢ هـ/ ١٢٦٣ م، انظر تذكرة الحفاظ للذهبى ١٤٤٢)، سراى أحمد الثالث ٦٢٤ (١٧ ورقة، ٧٢٩ هـ)، انظر: فهرس معهد المخطوطات العربية ٢/ رقم ٧٧٥.
٤ - «الإيماء إلى أطراف أحاديث الإمام مالك» لأبى العباس أحمد بن طاهر بن على بن عيسى بن عبادة (المتوفى ٥٣٢/ ١١٣٧ م، انظر: الأعلام للزركلى ١/ ١٣٦، ومعجم المؤلفين لكحالة ١/ ٢٥٥)، كوبريلى ٢٥٣ (٢٧٦ ورقة، فى القرن السادس الهجرى).
٥ - «كشف المغطّى فى فضل الموطا» لأبى القاسم على بن الحسن بن عساكر (المتوفى ٥٧١ هـ/ ١١٧٦ انظر بروكلمان ١/ ٣٣١)، الظاهرية، مجموع ١٠١/ ٢٣.
٦ - «أسماء شيوخ مالك» لأبى عبد الله محمد بن إسماعيل بن محمد بن خلفون/ الأزدى (٥٥٥ هـ/ ١١٦٠ م- ٦٣٦ هـ/ ١٢٣٩ م، انظر: معجم المؤلفين لكحالة ٩/ ٦١)، الإسكوريال ١٧٤٧ (٩٢ ورقة، فى القرن السادس الهجرى انظر: فهرس معهد المخطوطات العربية ٢، رقم ٣٢).
٧ - «إسعاف المبطّا برجال الموطأ» لجلال الدين السيوطى (المتوفى ٩١١ هـ/ ١٥٠٥ م): طلعت مجموع ٦٨٥ (الأوراق ٢٤٧ - ٢٨٠، ١١٥١ هـ، انظر: فؤاد سيد فى مجلة معهد المخطوطات العربية ٣/ ٢٣١، فهرس: مصطلح ١/ ١٥٧). وطبع بحيدرآباد ١٣٢٠ هـ، وبالقاهرة ١٣٥٦ هـ/ ١٩٣٧ م (فى نهاية كتاب التنوير).
٨ - «تجريد أحاديث الموطأ»، لأبى القاسم عبد الرحمن بن يحيى القرشى (أكمله سنة ٩٦٤ هـ/ ١٥٥٧ م): القرويين بفاس ٩٩٥.
٩ - «تلخيص أحاديث الموطأ»، لأبى حامد البياتى، (القرويين بفاس، الرقم القديم ٥٢٩؟ ).
١٠ - «لوامع الأنوار فى نظم غريب الموطأ ومسلم» لمحمد بن المصلىّ، مكتبة جامعة إستنبول ٣١٣٠ (١١٢ ورقة، فى القرن العاشر الهجرى).
١١ - «المسوّى من أحاديث الموطأ» (١٥٥) (تنسيق جديد لرواية يحيى بن يحيى المصمودى لولى الله بن عبد الرحيم الدّهلوى العمرى (المتوفى ١١٧٦ هـ/ ١٧٦٢ م، انظر: معجم المؤلفين لكحالة ١٣/ ١٦٩)، المكتب الهندى، دلهى، مخطوطات عربية ١٧٨ (٣٠٦ ورقة، انظر: شاخت، ، (Schacht II ,٢ a، رامبور ١/ ١١٣، حديث ٣٦١ (٢٧٣ ورقة، ١١٦٤ هـ)، آصفية ١/ ٦٧٢، حديث ٣ (١١٧٦ هـ)، (باتنة ١/ ٦١، ٦١٩ - ٦٢٠).
_________
(١٥٥) يحذف كتاب «المغازى» لعبد المؤمن .. إلخ، الذى ذكره بروكلمان تحت رقم ١١ وذلك، لأن هذا الكتاب هو كتاب الموطأ برواية الشيبانى.','3','140',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (896,'١٢ - كتاب لسلام الله بن شيخ الإسلام الدّهلوى (باتنة ١/ ٥٤، ٥٤٦).
١٣ - «المنتقى من الموطأ» لمؤلف مجهول، تشستربيتى ٥٤٩٨/ ٣، (الأوراق ٥١ - ٨٥، فى القرن السابع الهجرى).
١٤ - «خلاصة الدلائل فى تنقيح المسائل» لمؤلف مجهول، برلين ٣٥٢٤).
ثانيا: «رسالة» فيها تحذير للخليفة هارون الرشيد، ووزيره يحيى البرمكى وقد شك السيوطى فى صحة نسبة هذه الرسالة إلى مالك (انظر: تزيين الممالك ٤١)، الإسكوريال ٥٥٦ (الأوراق ٥٣ - ٧٧، ٩٩٥ هـ) ييل ١٥ أ (٢١ ورقة، فى القرن الثالث عشر الهجرى انظر، (Nemoy ١٤٤٣: وطبع ببولاق ١٣١١ هـ.
ثالثا: «مسائل وأجوبتها» رواها عبد الله بن عبد الحكم (المتوفى ٢١٤ هـ/ ٨٢٩ م) وسمعها هو وعبد الله بن وهب وعبد الرحمن بن القاسم: جوتا ١١٤٣ (٢٢٤ ورقة، فى القرن السابع الهجرى)؛ بلدية الإسكندرية ١٢١٠/ ٣ ج (٦٦ ورقة، فى القرن السادس الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٢٨١).
رابعا: «الأحاديث» التى رواها- على أرجح الاحتمالات- مالك، وليست موجودة بالموطأ، ثم رويت وتناولتها الدراسات بعد ذلك:
أ- «جزء فيه عوالى أحاديث مالك، رواية هشام بن عمار» (المتوفى ٢٤٥ هـ/ ٨٥٩ م): الظاهرية، مجموع ٩٨/ ٥.
ب- «غرائب حديث الإمام .. مالك» لمحمد بن المظفّر بن موسى بن عيسى البزاز (المتوفى ٣٧٨ هـ/ ٩٨٨ م): الظاهرية، حديث ٢٧٩ (١٦ ورقة، ٦١٢ هـ).
ج- «عوالى الإمام مالك بن أنس»، رواية الشريف أبى القاسم على بن إبراهيم جمع سليم بن أيوب بن سليم الرازى (المتوفى ٤٤٧ هـ/ ١٠٥٥ م، انظر: معجم المؤلفين لكحالة ٤/ ٢٤٣): الظاهرية، حديث ٣٨٧ (١١٣ ورقة، ٦ ورقات، ٦٢٢ هـ)، القاهرة، ملحق ٢/ ١٤٥، رقم ٢٢٧٣٢ ب (٧ ورقات ١٣٤٥ هـ).
د- «جزء فيه أحاديث مالك، تخريج أبى بكر الخطيب البغدادى» (المتوفى ٤٦٣ هـ/ ١٠٧١ م)، الظاهرية، مجموع ١٠١/ ٤.','3','141',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (897,'هـ- «بغية الملتمس فى أحاديث مالك بن أنس» لصلاح الدين العلائى خليل بن محمد بن أحمد (المتوفى ٧٦٩ هـ/ ١٣٦٨ م انظر بروكلمان ٢/ ٦٤)، الظاهرية، حديث ٣٤٢ (٤٠ ورقة).
و- «جزء فيه من حديث مالك»، الظاهرية، مجموع ٩٣ (من ١٩٣ أ- ٢٠٨ ب، فى القرن السادس الهجرى).

<span data-type="title" id=toc-2057>٢ - ابن زياد التّونسى</span>
هو أبو الحسن على بن زياد التونسى، كان تلميذا لمالك، وألّف عدة كتب حول آراء مالك، توفى حوالى ١٨٤ هـ/ ٨٠٠ م.

<span data-type="title" id=toc-2058><span data-type="title" id=toc-2061>أ- مصادر ترجمته:</span></span>
طبقات الفقهاء للشيرازى ١٢٩، الديباج لابن فرحون ١٩٣، معجم المؤلفين لكحالة ٧/ ٩٦.

<span data-type="title" id=toc-2059>ب- آثاره:</span>
وصلت إلينا بعض القطع من كتاب أو عدة كتب له فى «المدوّنة».

<span data-type="title" id=toc-2060>٣ - ابن القاسم العتقى</span>
هو أبو عبد الله، عبد الرحمن بن القاسم بن خالد، العتقى، ولد بمصر سنة ١٣٢ هـ/ ٧٤٩ م، ثم انتقل إلى المدينة المنورة، وسمع بها دروس مالك عشرين عاما، يعدّ من أهم تلاميذه ورواته. وكان العتقى شيخا لسحنون، وتوفى سنة ١٩١ هـ/ ٨٠٦ م.
أ- مصادر ترجمته:
الجرح والتعديل لابن أبى حاتم ٢/ ٢/ ٢٧٩، الفهرست لابن النديم ١٩٩، الانتقاء لابن عبد البر','3','142',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (898,'٥٠، الرجال للقيسرانى ٢٩٣، تذكرة الحفاظ للذهبى ٣٥٦ - ٣٥٧، الديباج لابن فرحون ١٤٦ - ١٤٧، التهذيب لابن حجر ٦/ ٢٥٤ - ٢٥٥، الأعلام للزركلى ٤/ ٩٧، معجم المؤلفين لكحالة ٥/ ١٦٥، بروكلمان ١/ ١٧٦ رقم ٢.

<span data-type="title" id=toc-2062>ب- آثاره:</span>
١ - «المدونة» أول مؤلف لهذا الكتاب هو ابن القاسم العتقى، وعنه رواه تلميذه أسد بن الفرات (المتوفى ٢١٣ هـ/ ٨٢٨ م) وأضاف إليه مسائل فقهية تجادل فيها أهل العراق لذا كانت تسمى «أسئلة أهل العراق». ولقد انتشر هذا الكتاب أول الأمر مع إجابات ابن القاسم فى القيروان برواية أسد بن الفرات، ولذلك أطلق عليه اسم «الأسدية». وأما سحنون (المتوفى ٢٤٠ هـ/ ٨٥٤ م) وهو تلميذ آخر لابن القاسم، فقد نسخ الكتاب أول الأمر بالرواية السابقة، ثم سمعه على ابن القاسم، وبذلك أتيح له النص الكامل مع الجديد من عبارات ابن القاسم. ويبدو أن ترتيب المادة فى هذه الرواية مختلف كل الاختلاف عن ترتيب رواية أسد التى لم تكن مبوبة ولا معنونة، لقد جاء سحنون بهذه الرواية إلى القيروان، ثم كتب ابن القاسم إلى أسد وطلب منه نسخته بروايته لأنه أراد أن يقارن هذه الرواية ويصححها برواية سحنون، ولكن أسدا غضب لذلك. ولكن سحنون هذب النص، ونسقه تنسيقا جديدا وأضاف إليه زيادات من «الموطأ» وسمى هذا العمل باسم «المدونة الكبرى»، ومع ذلك فقد بقى قسم من هذا الكتاب فى رواية أسد بدون تنقيح. وسماه «المختلطة» (انظر: التنبيهات المستنبطة على المدونة والمختلطة للقاضى عياض:
القيروان بفاس ٣٣٠، وانظر: شاخت Schacht ,in: Et.Or.I ,٢٨١ - ٢٨٢: وانظر ايضا: رياض النفوس للمالكى ١/ ١٨٠ - ١٨١).
وكتب أبو عبيد قاسم بن خلف الجبيرى (المتوفى ٨٧٨ هـ/ ١٤٧٣ م) كتاب: التوسّط بين مالك وابن القاسم فى المسائل التى اختلفا فيها من مسائل المدوّنة، أى فى اختلاف الآراء بينهما، ويوجد فى مخطوط مكناس ٢١٨. وأما مخطوطات المدونة فسنذكرها عند الكلام على سحنون، تحت رقم ١٠ من هذا الفصل.
٢ - مجموعة من الأسئلة وجهها إلى مالك بن أنس.
المتحف البريطانى ٢٥١، إضافات ٩٤٩٧ (/ الأوراق ١ - ١١، فى القرنين الحادى عشر والثانى عشر الهجريين، انظر الفهرس: ٧٦٩)، مدريد ٦٠/ ٤ (١٧ ورقة ٨٨٣ هـ، انظر -٣ (Derenburgs.١٥: رسالة نسبت إليه فى الفقه المالكى، باريس ١٠٥٠/ ١ (الأوراق ١ - ٩٥، ١١٩٠ هـ).','3','143',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (899,'٤ - ووصلت إلينا روايته للموطأ عن مالك فى: «الملخّص» لعلى بن محمد بن خلف القابسى (المتوفى ٤٠٣ هـ/ ١٠١٢ م) انظر: رقم ٥ من رواياتالموطأ.

<span data-type="title" id=toc-2063>٤ - عبد الله بن وهب</span>
هو أبو محمد، عبد الله بن وهب بن مسلم الفهرى القرشى، المصرى، ولد بمصر سنة ١٢٥ هـ/ ٧٤٣ م، كان محدثا ومفسرا وفقيها. ودرس على مالك منذ سنة ١٤٨ هـ/ ٧٦٥ م، وروى عنه. وهو الوحيد الذى لقبه مالك «فقيه مصر» (انظر:
التهذيب لابن حجر ٦/ ٧٤)، وتوفى سنة ١٩٧ هـ/ ٨١٢ م بمصر.

<span data-type="title" id=toc-2064>أ- مصادر ترجمته:</span>
الطبقات لابن سعد (بيروت) ٧/ ٥١٨، التاريخ الكبير للبخارى ٣/ ١/ ٢١٨، الجرح والتعديل لابن أبى حاتم ٢/ ٢/ ١٨٩ - ١٩٠، الرجال للقيسرانى ٢٦٠، طبقات الفقهاء للشيرازى ١٢٧، وفيات الأعيان لابن خلكان (بولاق) ١/ ٣١٢ - ٣١٣، تذكرة الحفاظ للذهبى ٣٠٤ - ٣٠٦، ميزان الاعتدال للذهبى ٢/ ٨٦، الديباج لابن فرحون ١٣٢ - ١٣٣، شذرات الذهب لابن العماد ١/ ٣٤٧ - ٣٤٨، مرآة الجنان لليافعى ١/ ٤٥٨، الأدب المصرى لمحمد كامل حسين ٣٨ - ٤٣، الأعلام للزركلى ٤/ ٢٨٩، معجم المؤلفين لكحالة ٦/ ١٦٢، بروكلمان ملحق ١/ ٢٥٧ رقم ١ ج.

<span data-type="title" id=toc-2065>ب- آثاره:</span>
١ - «كتاب الجامع فى الحديث» مكتوب على بردية عثر عليها فى «إدفو»، (الآن القاهرة أول، ملحق ١٣، غير كاملة، ٩١ ورقة، قبل ٢٧٦ هـ).
وتوجد قطعة منه فى، تشستربيتى ٣٤٩٧ (٦١ ورقة، فى القرن الثالث الهجرى). ونشره فايل J David Weil. بالقاهرة ١٩٤٢ م. ويوجد بعنوان: «مسند» فى الظاهرية مجموع ٤٠ (من ١٠٦ أ- ١٧١ ب، فى القرن الخامس الهجرى).
٢ - قسم من أحاديثه برواية أبى صالح عبد الله بن صالح (المتوفى ٢٢٣ هـ/ ٨٣٧ م)، القاهرة ثان ١/ ١٠٨، حديث ١٥٥٨، (١٦ ورقة، فى القرن الثامن الهجرى، انظر: القاهرة، ملحق ١/ ٢١٦).','3','144',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (900,'٣ - «التفسير» فى رواية يونس بن عبد العلاء، ذكره الثعلبى فى كتابه: «الكشف والبيان» (ص ٧ أ).
٤ - «الموطأ الكبير» يكاد هذا الكتاب أن يكون أهم مصدر للمدونة.
وعن شيوخ ابن وهب: «سفر فيه جميع شيوخ عبد الله بن وهب القرشى الذى روى عنهم» (- أخبار ابن وهب وفضائله لابن بشكوال، انظر: الدرر الكامنة لابن حجر ٣/ ١٢٦)، توجد مخطوطة منه عند إبراهيم الكتانى بالرباط

<span data-type="title" id=toc-2066>٥ - أشهب</span>
هو أبو عمرو أشهب بن عبد العزيز بن داود القيسى، ولد فى مصر سنة ١٤٥ هـ/ ٧٦٢ م. كان تلميذا لابن وهب وفى الوقت نفسه كاتبا له، كما كان محدثا ثقة، وفقيها مرموقا عالى المكانة. وتوفى سنة ٢٠٤ هـ/ ٨١٩ م بعد موت الشافعى بأيام.

<span data-type="title" id=toc-2067>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ١٩٩، الانتقاء لابن عبد البر ٥١/ ١١٢، وفيات الأعيان لابن خلكان (بولاق) ١/ ٩٧ - ٩٨، التهذيب لابن حجر ١/ ٣٥٩ - ٣٦٢، شجرة النور لمخلوف ١/ ٥٩، الأعلام للزركلى ١/ ٣٣٥./

<span data-type="title" id=toc-2068>ب- آثاره:</span>
«كتاب الحج» (ج ٤، ٥) برواية سحنون، القيروان، انظر أيضا: إبراهيم شبوح فى: مجلة معهد المخطوطات العربية ٢/ ١٩٥٦/ ٣٦٣.

<span data-type="title" id=toc-2069>٦ - أسد بن الفرات</span>
هو أبو عبد الله، أسد بن الفرات بن سنان، ولد بحرّان سنة ١٤٢ هـ/ ٧٥٩ م. قدم طفلا مع أبيه إلى القيروان ثم إلى تونس. وبعد مدة رجع إلى المشرق، وأخذ بالمدينة المنورة عن مالك موطّأه وغير ذلك. وفى العراق اشترك فى حلقات علمية أخرى','3','145',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (901,'«لأصحاب الرأى» ومن بينهم أبو يوسف، ومحمد بن الحسن الشيبانى. ثم ارتحل من العراق بعد موت مالك (سنة ١٧٩ هـ/ ٧٩٥ م) وذهب إلى مصر. كانت له مكانة كبيرة فى الفقه، فولاه زيادة الله بن إبراهيم الأغلبى القضاء بالقيروان سنة ٢٠٣ هـ/ ٨١٨ م، ثم تولى قيادة الحملة التى فتحت صقلية سنة ٢١٠ هـ/ ٨٢٦ م.
ولكنه مات أثناء محاصرة مدينة سرقوسة سنة ٢١٣ هـ/ ٨٢٨ م.

<span data-type="title" id=toc-2070>أ- مصادر ترجمته:</span>
رياض النفوس للمالكى ١/ ١٧٢ - ١٨٩، معالم الإيمان لابن ناجى ٢/ ٢ - ١٧، الأعلام للزركلى ١/ ٢٩١، دائرة المعارف الإسلامية (الطبعة الأولى) ١/ ٤٩٤، انظر: مارسيه فى (الطبعة الإنجليزية الثانية) ١/ ٦٨٥.

<span data-type="title" id=toc-2071>ب- آثاره:</span>
«الأسدية» وهو تهذيب لكتاب «المدونة» لابن القاسم، قرأها زميله سحنون مرة أخرى على ابن القاسم، وغيّر جزءا كبيرا منها ما عدا الجزء الأخير الذى بقى دون تغيير، ولذلك يسمى هذا القسم باسم «المختلطة» لأنه بلا منهج (انظر: المدوّنة لابن القاسم رقم ٣ من هذا الفصل)، وتوجد فى مخطوطة قديمة جدا بمكتبة القرويين بفاس ٧٩٦ بعنوان: «المدونة والمختلطة».
مؤلفات جمعت المدوّنة والمختلطة: أ- «الجامع لمسائل المدونة والمختلطة» لأبى بكر محمد بن عبد الله يونس الصّقلى (المتوفى ٤٥١ هـ/ ١٠٥٩ م، انظر: الديباج لابن فرحون: ٢٧٤)، القرويين بفاس ٣٤٢ (٧٣٤ هـ)، ٣٤٣، ٣٨٣، ٨٢٠ (٥١٩ هـ)، مكناس ٣٠٢، ٣٣٤.
ب- «التنبيهات المستنبطة على المدونة والمختلطة للقاضى عياض (المتوفى ٥٤٤ هـ/ ١١٤٩ م انظر بروكلمان ١/ ٣٦٩، القرويين ٣٣٠، انظر:
Schacht, in: Et. Or. I, ٢٨١ - ٢٨٢: U. Rizzitano, A. b. F." Faqih" e" qadi", d''Ifriqiyain: RSO ٣٦/ ١٩٦١/ ٢٢٥ - ٢٤٣.','3','146',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (902,'<span data-type="title" id=toc-2072>٧ - ابن عبد الحكم</span>
هو أبو محمد عبد الله بن عبد الحكم بن أعين بن ليث المصرى، ولد بالإسكندرية سنة ١٥٥ هـ/ ٧٧٢ م، سمع دروس وموطأ مالك فى المدينة، ثم عاد إلى مصر، فصار بها فقيها مشهورا، وتوفى سنة ٢١٤ هـ/ ٨٢٩ م.

<span data-type="title" id=toc-2073>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ١٩٩، الانتقاء لابن عبد البر ٥٢، الديباج لابن فرحون ١٣٤، التهذيب لابن حجر ٥/ ٢٨٩ - ٢٩٠، شذرات الذهب لابن العماد ٢/ ٣٤، الأعلام للزركلى ٤/ ٢٢٩، معجم المؤلفين لكحالة ٦/ ٦٧.

<span data-type="title" id=toc-2074>ب- آثاره:</span>
١ - «المختصر الكبير فى الفقه».
(يعتبر مصدرا أساسيا عند مالكية العراق، انظر التهذيب لابن حجر ٤/ ٢٨٩)، القرويين بفاس ٨١٠ (٣٠ ورقة).
وعليه شرح محمد بن عبد الله بن محمد بن صالح الأبهرى (المتوفى ٣٧٥ هـ/ ٩٨٦ م انظر: رقم ٢٥ من هذا الفصل) / الأزهر ٢/ ٣٦٧ - ٣٦٨، فقه مالكى ١٦٥٥ (ج ٣، ٧، ١٢، ٣١٨ ورقة، ١٤٠ ورقة، ٨٦ ورقة ٦٠٤ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٨٠ - ٢٨١).
٢ - روايته لمسائل مالك وأجوبتها (انظر: مالك بن أنس).
٣ - «سيرة عمر بن عبد العزيز».
وصلت إلينا فى تهذيب ابنه أبى عبد الله محمد (المتوفى ٢٦٨ هـ/ ٨٨٢ م. انظر: رقم ١٦ من هذا الفصل).

<span data-type="title" id=toc-2075>٨ - أبو زيد عبد الرحمن بن أبى الغمر</span>
هو أبو زيد، عبد الرحمن بن أبى الغمر عمر بن عبد العزيز، درس على عبد الرحمن','3','147',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (903,'ابن القاسم العتقى. كان فقيها ومحدثا. وأدرك مالكا ولم يسمع منه. وروى عنه البخارى وأبو زرعة وغيرهما، وتوفى سنة ٢٣٤ هـ/ ٨٤٨ م.

<span data-type="title" id=toc-2076>أ- مصادر ترجمته:</span>
الديباج لابن فرحون ١٤٨ - ١٤٩، التهذيب لابن حجر ٦/ ٢٤٩ - ٢٥٠، شجرة النور لمخلوف ٦٦ - ٦٧.

<span data-type="title" id=toc-2077>ب- آثاره:</span>
«كتاب المجالس» فى (الفقه) القيروان.

<span data-type="title" id=toc-2078>٩ - عبد الملك بن حبيب</span>
هو أبو مروان، عبد الملك بن حبيب بن سليمان السّلمى، القرطبى، (عاش من ١٧٤ هـ/ ٧٩٠ م- ٢٣٨ هـ/ ٨٥٢ م)، ولقد ذكر ضمن مؤرخى الأندلس (انظره فيما تقدم). وكان يعدّ من أعظم فقهاء المدرسة المالكية، ووصل إلينا من كتبه الكثيرة فى الفقه قسم صغير من: «كتاب الواضحة فى السنن والفقه»، و «كتاب الغاية والنهاية».

<span data-type="title" id=toc-2079>١٠ - سحنون</span>
هو عبد السلام بن سعيد بن حبيب التنوخى، الملقب بسحنون باسم طائر شديد لشدته فى المسائل. ولد بالقيروان سنة ١٦٠ هـ/ ٧٧٦ م. وبدأ دراسته عند أشهر شيوخ القيروان وهو بهلول بن راشد (المتوفى ١٨٣ هـ/ ٧٩٩ م). سافر إلى أفريقية (تونس) بوصية من شيخه لدراسة المذهب المالكى على علىّ بن زياد المتوفى ١٨٣ هـ/ ٧٩٩ م)، ولكنه رحل بعد ذلك إلى مصر ليكمل دراسته على أشهر تلاميذ مالك وهم عبد الرحمن بن القاسم، وابن وهب، وأشهب. وغير مؤكد أنه ذهب أيضا إلى','3','148',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (904,'المدينة أو إلى مكة فى حياة مالك وسمع منه. ولكنه أخذ عن تلاميذ مالك فى مكة والمدينة والشام، وهو فى طريق الحج ثم عاد إلى القيروان سنة ١٩١ هـ/ ٨٠٦ م، وأخيرا قبل من محمد بن الأغلب منصب القضاء سنة ٢٣٤ هـ/ ٨٤٩ م، وكان قد رفضه سنين طويلة، ولكن بشرط ألا يأخذ على ذلك رزقا أو صلة. وتوفى بالقيروان سنة ٢٤٠ هـ/ ٨٥٤ م/.

<span data-type="title" id=toc-2080>أ- مصادر ترجمته:</span>
رياض النفوس للمالكى ١/ ٢٤٩ - ٢٩٠، طبقات الفقهاء للشيرازى ١٣٢ - ١٣٣، وفيات الأعيان لابن خلكان (بولاق) ١/ ٣٦٦ - ٣٦٧، الديباج لابن فرحون ١٦٠ - ١٦٦، معالم الإيمان لابن ناجى ٢/ ٤٩ - ٦٨، مرآة الجنان لليافعى ٢/ ١٣١ - ١٣٢، كرنكو فى دائرة المعارف الإسلامية ٤/ ٦٨ - ٧٠، الأعلام للزركلى ٤/ ١٢٩، معجم المؤلفين لكحالة ٥/ ٢٢٤، بروكلمان ملحق ١/ ٢٩٩ رقم ٢ أ.

<span data-type="title" id=toc-2081>ب- آثاره:</span>
١ - «المدوّنة الكبرى» هذا العمل تهذيب جديد لكتاب عبد الرحمن بن القاسم (انظر: رقم ٣ فى هذا الفصل) الذى نقحه وأضاف عليه أسد بن الفرات (انظر ما سبق).
مخطوطاته: جاريت ١٨٣٤ (قسم واحد، ١٢٦ ورقة، فى القرن الخامس الهجرى)، بلدية الإسكندرية ١٢١٠ ب (قسم واحد، ٦٦ ورقة، فى القرن السادس الهجرى، انظر فهرس معهد المخطوطات العربية ١/ ٢٨١)، وتوجد قطع من مخطوطة المدوّنة من القرن الرابع والخامس الهجريين فى لوفن ببلجيكا، (انظر هنفنج، (Heffening ,Museon ٥٠ ,١٩٣٧ /٨٦ ff القرويين بفاس ٥٧٧ (٤٩٤ ورقة، انظر مجلة معهد المخطوطات العربية ٥/ ١٢)، المتحف البريطانى، مخطوطات شرقية ٦٥٨٦ (قسم واحد، ٩١ ورقة، فى القرن السادس الهجرى، انظر: قائمة وصفية ٢٦)، (وتوجد أجزاء من مخطوطة بالقيروان حوالى ٤٠٠ هـ/ ١١١٠ م فى مكتبة خاصة، انظر: كرنكو فى المصدر السابق). وهبى ٤٣٠ (قسم واحد، ٨٦ ورقة، فى القرن السادس الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٢٨١)، القيروان (ثمان ورقات، ٢٥٨ هـ)، القرويين بفاس ٣١٩ (ج ١، فى القرنين الرابعأو الخامس الهجريين)، أيضا ٧٩٦ (فى القرن','3','149',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (905,'الرابع الهجرى، انظر: مجلة معهد المخطوطات العربية ٥/ ١٠)، ١٣٣٥ (قطعة، ٥١٧ هـ، انظر (Et.Or.I ,٢٣٧، Schacht , الكنانى بالرباط ٣٤٣ (٥٥ ورقة، فى القرن الرابع الهجرى)، تشستربيتى ٣٠٠٦ (٩٠ ورقة، ٤١ ورقة، ١٢ ورقة، فى القرن السادس الهجرى)، ٤٨٣٥ (٧٣ ورقة، ٥١٠ هـ)، الأزهر ٢/ ٤٠٥، فقه مالكى ١٧٤٢ (أقسام ٢ - ١٦ فى خمسة مجلدات، بعضها منسوخ سنة ٥٢٧)، وطبع بالقاهرة ١٣٢٣، ١٣٢٤، ١٣٢٥، ١٣٤٥ هـ.
وعلى هذه المدونة كتب:
E. Amar, lagrand M. RMM ١٠/ ١٩١٠/ ٥٢٤ - ٥٣٢; A. d''Emilia, II" bai" alnella Mudauwana, RSO ٢٤/ ١٩٤٩/ ٤٥ - ٥٨; E. Amar, Etudedu Vol. XIVdela M. al- kubrade S., RSO ٢٨/ ١٩٥٣/ ٤٧ - ٧٧. E. Amar, ILK. al- Gasbnella M. di Sohnun, RSO ٢٨/ ١٩٥٣/ ٧٩ - ٩٨. C. H. Bousquet, Ibnal- Qasim, la Moudawwana (Recensionde Sohnoun (Analyse, AIEO ١٤/ ١٩٥٨/ ١٧٧ - ٢٠٦, ١٧/ ١٩٥٩/ ١٦٩ - ٢١١, ٨/ ١٩٦٠/ ١٧٣ - ٢٦٥.
شروح المدونة: ١ - «التنبيه على مبادئ التوجيه» لمحمد بن إبراهيم بن عبدوس بن بشير المالكى؟ (المتوفى ٢٦٠ هـ/ ٨٧٤ م، انظر: رقم ١٥ من هذا الفصل): الزيتونة بتونس ١٠٤٤٧ (١٢٤ ورقة)، (القرويين بفاس رقم القديم ٨٨٩، ٨٢٦، ٨٨٤)، انظر بروكلمان ملحق ٢/ ٩٦٠.
٢ - شرح لأبى القاسم عبيد الله بن الحسن بن الحسين بن الجلّاب البصرى (٣٧٨ هـ/ ٩٨٨ م انظر:
رقم ٢٦ من هذا الفصل)، (القرويين بفاس، الرقم القديم ٧٩٩)، الرباط ١٤٤٧ (قطعة، الأوراق ٢٩ - ٣١، انظر: الفهرس ١/ ٢٩٤).
٣ - «المقدّمات الممهّدات لبيان ما اقتضته رسوم المدوّنة» لأبى الوليد محمد بن أحمد بن رشد (المتوفى ٥٢٠ هـ/ ١١٢٦ م انظر بروكلمان ١/ ٣٨٤)، سراى، أحمد الثالث ١٢١٥ (ج ٢، ٢٣٢ ورقة، ٧١٣ هـ انظر:
الفهرس ٢/ ٣٧٠)، الإسكوريال ٩٩٣ (ج ٣، ٢٢٣ ورقة، ٨٧٠ هـ، انظر فايدا (٦٨ Vajde ,Andalus ٢٨ /٦٧ - القرويين بفاس ٣٥٣، ٤٩٩، وطبع بالقاهرة ١٣٢٥ هـ ملحقا بكتاب «تزيين المنتهى» للسيوطى.
٤ - شرح لأبى عبد الله محمد بن على بن محمد المازرى (المتوفى ٥٣٦ هـ/ ١١٤١ م انظر بروكلمان ١/ ٣٨٤): الأوقاف بالرباط ١٥٠ (ج ١، ١٣٩ ورقة، فى القرن العاشر الهجرى).
٥ - «طراز المجالس» لسند بن عنان بن إبراهيم الأزدى (المتوفى ٥٤١ هـ/ ١١٤٦ م، انظر: الديباج لابن فرحون: ١٢٦ - ١٢٧): الرباط ٨٧٨ (٣٠٣ ورقة انظر: الفهرس ١/ ٢٩٦).','3','150',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (906,'٦ - «التنبيهات (١٥٦) المستنبطة فى شرح مشكلات المدوّنة والمختلطة» للقاضى عياض اليحصبى (المتوفى ٥٤٤ هـ/ ١١٤٩ م انظر بروكلمان ١/ ٣٦٩)، الإسكوريال ٩٩٢ (١٥٧) (١٤٣ ورقة، فى القرن السابع الهجرى. انظر: فايدا (Vaida ,Andalus ٢٨ /٦٥ - ٦٧، القرويين بفاس ٣٣٣ (٧٨٦ هـ، انظر:
Schacht, Et. Or. ١, ٢٨١ (
، ٣٣٤، ٣٣٥، ٣٣٦ (٦٨٧ هـ)، توبنجن ١٢٣، مراكش ١٧٩، مكناس ١٣٢، ٢٨٠./.
٧ - «مناهج التحصيل ونتائج لطائف التأويل فى شرح مشكلات المدونة» (١٥٨) لأبى الحسن على بن سعيد بن تمسريت الرّجراجى (كتبه ٦٣٣ هـ/ ١٢٣٥ م)، القرويين بفاس ٣٨١ (ثلاثة مجلدات، انظر:
(Schacht, Et. Or. ١, ٢٨٣
الأوقاف بالرباط ٨٨ (ج ٤١، ٦٩٣ هـ)، الكتانى بالرباط ٦٤٧ (ج ١، ٢٧٣ ورقة)، القاهرة ثان ١/ ٤٩٣، فقه مالكى ٩٥ (ج ١، ٢، ٢٤٠ ورقة، ٢٦٠ ورقة، ٧٣٣ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٨٢).
٨ - (يطابق رقم ٦ فى ملحق بروكلمان) «التقييد على المدونة» لأبى الحسن على بن محمد بن عبد الحق الصّغيّر (المتوفى ٧١٩ هـ/ ١٣١٩ م، انظر: الأعلام للزركلى ٥/ ١٥٦): القرويين بفاس ٣٢٦ (عشرة أقسام، انظر (Schacht ,Et.Or.١ ,٢٨٣: ليدن ١٧٧٣ (ج ٤، ٥، ١٥٣ ورقة، ١٣١ ورقة، انظر: فورهوف ٢٢٠)، باريس ١٠٥٤/ ١ (الأوراق ١ - ١٢٩، فى القرن السابع الهجرى، انظر: فايدا ٦٧٢)، الكتانى بالرباط ٦٤٧، (ج ١، ٢٧٣ ورقة)، الزيتونةبتونس ٤/ ٣٠٤ رقم ٢٥٠٦ - ٢٥١٠ (ج ١ - ٥، ٣٩٣٢ ورقة)، الإسكوريال ٩٨٧ (ج ٥، ١٨٦ ورقة، ٧٤٤ هـ)، ٩٨٨ - ٩٩١ (ج ٦، ٧، ٩، ١٢، ٩٤٤ هـ، انظر فايدا (١٥٩) (Vaida ,Andalus ٢٨ ,٦٣ - ٦٥ وعلى هذا الشرح كتب ما يأتى: «إتخاف ذوى الذكاء والمعرفة بتكميل تقييد أبى الحسن وتحليل تقييد ابن عرفة» (المتوفى ٨٠٣ هـ/ ١٤٠٠ م انظر بروكلمان ٢/ ٢٤٧) لأبى عبد الله محمد بن أحمد بن محمد بن غازى المكناسى (المتوفى ٩١٩ هـ/ ١٥١٣ م انظر بروكلمان ٢/ ٢٤٠)، (القرويين بفاس، الرقم القديم ٨٠١).
_________
(١٥٦) يصحح ما عند بروكلمان.
(١٥٧) تحذف مخطوطة الإسكوريال رقم ٩٨٦ التى ذكرها بروكلمان، انظر فايدا فى المرجع المذكور ٦٤.
(١٥٨) نسب بروكلمان هذا الكتاب إلى محمد بن أحمد بن رشد رقم ٣، وهذا خطأ.
(١٥٩) لقد شك فايدا فى نسبة هذا الكتاب إلى ابن الصّغيّر المكتوب اسمه على غلاف المخطوطة. والتأكد من ذلك إنما يكون بالرجوع إلى النسخة الكاملة الموجودة، بتونس.','3','151',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (907,'تكملات ومختصرات ومؤلفات أخرى حول المدوّنة: «النوادر والزيادات على ما فى المدوّنة وغيرها من الأمهات» لابن أبى زيد القيروانى (المتوفى ٣٨٦ هـ/ ٩٩٦ م): أيا صوفية ١٤٧٩ - ١٤٩٧ (١٩ مجلدا، فى القرن السادس الهجرى انظر: فهرس معهد المخطوطات العربية ١/ ٢٨٢ - ٢٨٤)، القرويين بفاس ٣٣٨ (ج ٤، ٧١٠ هـ، انظر) ٧٩٣ ٤٧٢، ١ (Schacht ,Et.Or.: قسم واحد، ٣٨٣ هـ)، باريس ٦٠٨٥ (قسم واحد، ٩٤ ورقة، ٤٧٢ هـ، انظر: فايدا ٥٢٣)، ميونيخ ٣٤٠ (١، ١٤٢ ورقة) (١٦٠)، الزيتونة بتونس ٥١٩١ (انظر: محمد طالبى:
M. Talbi, Cahiersde Tunisie ١٥/ ١٩٥٦/ ٢٨٩ - ٢٩٣
)، المسجد الأعظم بتطوان، (مجلدان، انظر: مجلة معهد المخطوطات العربية ١/ ١٧٤)، وفى مكتبة اليزيد إبراهيم بن صالح الخاصة بتطوان (ج ٧، انظر مجلة معهد المخطوطات العربية ١/ ١٨٦)، آصفية ٢/ ١١٧٦، فقه مالكى ٥ (انظر بروكلمان ملحق ١/ ٣٠٢).
٢ - «مختصر المدوّنة» لأبى زيد القيروانى: القرويين بفاس ٣٣٩، تيمور بالقاهرة فقه ٣٣٧، قطع مختلفة بالقيروان، الأوراق ١٣٧ - ١٥٨، ١٩٩ - ٢٢٩، ٢٣٠ - ٢٤٦، ٢٤٧ - ٢٦٥، ٢٧٨ - ٢٩٧، ٢٩٨ - ٣٠٩، ٣١٠ - ٣١٣، ٣١٤ - ٣٣٢. (١٦١) ٣ - «تهذيب فى اختصار المدوّنة» لأبى سعيد خلف بن سعيد البراذعي (كان يعيش سنة ٤٣٠ هـ/ ١٠٣٩ م)، القرويين بفاس ٣٢٠ (ج ٤، ٥٣٣ هـ انظر Schacht: فى المصدر السابق ١/ ٢٧٥)، ٣٢١، ٣٢٢ (٩٤٣ هـ)، ٣٢٣، ٣٢٤ (٨٦٩ هـ)، ٣٢٥، ١١١٨ (٧٧٨ هـ) ١١١٩، باريس ١٠٥١ (٢٥٧ ورقة، ٨٥٠ هـ)، ١٠٥٦، (٢٣٠ ورقة، فى القرن الثامن الهجرى)، ١٠٥٣ (٢٥٢ ورقة، فى القرن العاشر الهجرى)، ١٠٥٤/ ٢ (الأوراق ١٣١ - ٢٢٢، فى القرن السابع الهجرى)، سراى، أحمد الثالث ٨٧٢ (٢٧٠ ورقة، ٨٤٢ هـ، انظر فهرس معهد المخطوطات العربية ١/ ٢٧٩، والفهرس ١/ ٣٦٩)، تشستربيتى ٣٩٥٢ (١٣٦ ورقة، ٥٧٣ هـ)، ٤٧١٩ (٢٨٦ ورقة، فى القرن التاسع الهجرى)، بلدية الإسكندرية
_________
(١٦٠) هذه المخطوطة ذكرها بروكلمان على أنها تكملة مجهولة المؤلف وبرقم ٣٤٩، وهذا خطأ ولا بد أيضا من تصحيح سنة وفاة محمد بن عبدوس حيث ذكر أنه توفى سنة ١٨٠ هـ والصحيح أنه توفى ٢٦٠ وقيل ٢٦١ هـ أو ٢٥٨ هـ انظر: قضاة قرطبة للخشنى: ١٨٢، والديباج لابن فرحون ٢٣٧ - ٢٣٨ وانظر أيضا:
Schacht ,a.a.O.٢٧٥.
(١٦١) ذكر بروكلمان فى الملحق ١/ ٣٠٠ أن هذا المختصر طبع عدة مرات وأن كرنكو نقل عنه فى مقاله فى دائرة المعارف الإسلامية، وهذا غير صحيح لأن هذا المختصر لم يطبع من قبل.','3','152',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (908,'١٠٥٢ ب (٢٤٣ ورقة، ٨٣٦ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٢٧٩)، طلعت بالقاهرة، فقه مالكى ٩٥، الأزهر ٢/ ٣١٤، فقه مالكى ١٦٥٤/ (٢٤٤ ورقة)، الإسكوريال ٩٩٥ (٢٧١ ورقة، ٨٠٤ هـ، انظر فايدا (Vaida ,Andalus ٢٨ /٦٨ - ٦٩ الزيتونة بتونس ٤/ ٢٨٥ - ٢٨٦، رقم ٢٤٥٢ - ٢٤٥٥، رقم جديد ١٧١٩ (٢١٠ ورقة)، ١٧٢٠ (١٧٧ ورقة، ٨٧٩ هـ)، ١٩٩١ (٣٧٧ ورقة)، ٥٦٧١ (٢١٠ ورقة، ٨٦٢ هـ)، ٩٩٩٦ (٣٠٠ ورقة، ٩٦٤ هـ، انظر أيضا: إبراهيم شبوح فى: مجلة معهد المخطوطات العربية، (H.R.Idris ,Mel.L.Mass.٢ - ١٩٥٧ /٣٤٨، ٣٦٧ /١٩٥٦ /٢ الكتانى بالرباط ٨٣٤ (ج ١، ٣٠١ ورقة)، الحمزاوية ٩٠، اليوسفية بمراكش، ٣٥ (ج ١، ٦)، مكناس ٣١٦.
وحول هذا ألّفت الكتب التالية: أ- «تعليق» لأبى مهدى عيسى الوانوغى (كان يعيش ٨٠٣ هـ/ ١٤٠٠ م؛ انظر: نيل الابتهاج للتنبكتى: ٢٨٦)، الكتانى بالرباط ٨٣٠ (ج ١، ١٧٢ ورقة). وعليه: «تكملة» لأبى عبد الله محمد بن أبى القاسم بن محمد بن عبد الصمد المشدّالى (المتوفى ٨٦٦ هـ/ ١٤٦٢ م، انظر: نيل الابتهاج للتنبكتى ٣١٤)، الفاتيكان فيدا ٢٥٧ (٢٨٠ ورقة، ٨٨٢ هـ)، الجزائر ١٠٧٢ (٢٤٣ ورقة، ٨٣٦ هـ)، المسجد الأعظم بالجزائر ٧٣ (١٣٥ ورقة، ٨٧٧ هـ).
ب- «الشرح الصغير على تهذيب البراذعي» لقاسم بن عيسى بن ناجى (١٦٢) (المتوفى ٨٣٧ هـ/ ١٤٣٣ م انظر بروكلمان ٢/ ٢٣٩): القرويين بفاس ٣٣١ (انظر (Schacht ,Et.Or.I ,٢٤٨: ج- «الشرح الكبير» للمؤلف السابق: تونس ٤/ ٣٠٥، رقم ٢٥١١ - ٢٥١٢، (ج ١، ٢، ٢٥٥ ورقة، ٢٢٥ ورقة، ١١٣٦ هـ)، فاس ١١٢٠ (؟ ).
د- «تهذيب لمسائل التهذيب» لمؤلف مجهول: (القرويين بفاس الرقم القديم ٨٨٣).
٤ - «الجامع لمسائل المدوّنة والمختلطة» مختصر لأبى بكر محمد بن عبد الله بن يونس الصّقلى، (المتوفى ٤٥١ هـ/ ١٠٥٩ م، انظر: معجم المؤلفين لكحالة ١٠/ ٢٥٢) (١٦٣): فاس ٣٤٢ (٧٣٤ هـ، انظر ٥١٩) ٨٢٠، ٣٨٣، Schacht , (Et.Or.I ,٢٧٧: هـ)، ١٣٤٣ (٧٤٠ هـ)، (؟ كراسة واحدة فقط، انظر: شاخت فى المصدر السابق)، مراكش ٤٧٧ (ج ١)، مكناس ٣٠٢، ٣٣٤، الكتانى بالرباط ٨٢٣ (١٧٣ ورقة).
٥ - «تهذيب الطالب وفائدة الراغب على المدونة» لعبد الحق بن محمد بن هارون السهمى (الصّقلى (المتوفى ٤٦٦ هـ/ ١٠٧٤ م انظر بروكلمان ملحق ١/ ٦٦١ القرويين بفاس، الرقم القديم ٨٥٤.
_________
(١٦٢) يصحح عند بروكلمان.
(١٦٣) يصحح عند بروكلمان الملحق ١/ ٦٦٣.','3','153',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (909,'٦ - «كتاب النّكت والفروق من المدوّنة والمختلطة باختصار اللفظ فى طلب التفرقة بين المسلمين ومعرفة اختلاف حكمتها» للمؤلف السابق، مدريد ٧٨ (١٠٠ ورقة، ٤٥٩ هـ، بخط المؤلف). (١٦٤).
٧ - التبصرة (تعليق على المدونة) لأبى الحسن على بن عبد الله بن إبراهيم اللّخمى المطيطى (المتوفى ٤٩٨ هـ/ ١١٠٤ م انظر بروكلمان ملحق ١/ ٦٦١) القرويين بفاس ٣٦٧ (٦٢٤ ورقة)، ٣٦٨ (٥١٨ هـ)، ٣٦٩، ٣٧٠ (٥١٣ هـ)، برلين الغربية ٣١٤٤.
٨ - «نظم الدّرّة فى تلخيص المدوّنة» لعبد الله بن عبد الرحمن بن عمر المعرّى الشّارمساحيّ (المتوفى ٦٦٩ هـ/ ١٢٧١ م، انظر: معجم المؤلفين لكحالة ٦/ ٧١)، (القرويين بفاس، الرقم القديم ٩٣٢). (١٦٥).

<span data-type="title" id=toc-2082>١١ - أبو مصعب</span>
هو أبو مصعب أحمد بن أبى بكر القاسم بن الحارث الزّهرى، كان تلميذا وراوية لمالك، ولد حوالى سنة ١٥٠ هـ/ ٧٦٧ م وعاش فى المدينة. كان قاضيا، ولكن قطام ابن جعفر أعفاه من منصبه سنة ٢١٠ هـ. وكان فى فتاويه يجعل للرأى مكانة كبيرة بجانب النقل. وتوفى سنة ٢٤٢ هـ/ ٨٥٦ م.

<span data-type="title" id=toc-2083>أ- مصادر ترجمته:</span>
الجرح والتعديل لابن أبى حاتم ١/ ١/ ٤٣، أخبار القضاة لوكيع ١/ ٢٥٨ - ٢٥٩، طبقات الفقهاء للشيرازى ١٢٦ - ١٢٧، الديباج لابن فرحون ٣٠، تذكرة الحفاظ للذهبى ٤٨٢ - ٤٨٤، التهذيب لابن حجر ١/ ٢٠ - ٢١، شذرات الذهب لابن العماد ٢/ ١٠٠

<span data-type="title" id=toc-2084>ب- آثاره:</span>
١ - «المختصر فى الفقه»، القرويين بفاس ٨٧٤ (١٧٤ ورقة، ٣٥٩ هـ، انظر: حسن حسنى عبد الوهاب فى مجلة معهد المخطوطات العربية ١/ ١٩٥٥/ ٨٦ وشاخت (Schacht ,Et.Or.I ,٢٧٣ - ٢٧٤ -
_________
(١٦٤) يصحح عند بروكلمان أنه مؤلف كتاب «الكبير على المدونة»
(١٦٥) تحذف المؤلفات التى ذكرها بروكلمان تحت رقم ٢، ٣ فالمؤلف هو محمد بن سحنون.','3','154',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (910,'٢ - روايته لموطأ مالك وهى مائة حديث زيادة على رواية يحيى بن بكير (انظر: التهذيب لابن حجر ١/ ٢٠)، الظاهرية، مجموع ٦٣/ ١٥ (قطعة، من ١٨٢ أ- ١٨٩ ب، فى القرن السابع الهجرى).

<span data-type="title" id=toc-2085>١٢ - العتبى</span>
هو محمد بن أحمد بن عبد العزيز الأموى، العتبى القرطبى، رحل إلى المشرق وسمع من كثير من مشاهير العلماء، ثم عاد إلى وطنه بالأندلس فأصبح بها من كبار فقهاء المدرسة المالكية، وتوفى سنة ٢٥٥ هـ/ ٨٦٩ م.

<span data-type="title" id=toc-2086>أ- مصادر ترجمته:</span>
الجذوة للحميدى ٣٦ - ٣٧، البغية للضبى ٣٧، الديباج لابن فرحون ٢٣٨ شذرات الذهب لابن العماد ٢/ ١٢٩، معجم المؤلفين لكحالة ٨/ ٢٧٦، فهرست ابن خير ٢٤١، اللباب لابن الاثير ٢/ ١١٩، نفخ الطيب للمقرى (ليدن) ١/ ٦٠٣ الأعلام للزركلى ٦/ ١٩٧، بروكلمان ١/ ١٧٧ رقم ٣.

<span data-type="title" id=toc-2087>ب- آثاره:</span>
«المستخرجة من الأسمعة مما ليس فى المدوّنة» بحث هذا الكتاب فى مشكلات الفقه المالكى، وكان هذا الكتاب أحد المصادر الرئيسية لأبى زيد القيروانى (انظر: رقم ٢٧ من هذا الفصل، وشاخت (Schacht ,Et.Or.I ,٢٧٥ - فى كتابه: «النوادر والزيادات: » باريس ١٠٥٥ (١١٥ ورقة، فى القرن الخامس الهجرى)، ٦١٥١ (٢١ ورقة، فى القرن الخامس الهجرى، انظر: فايدا - (Vajda ,٧١٨ الإسكوريال ٦١٢/ ١ (قسم واحد، الأوراق ١ - ٥) قطعة منه فى القيروان، انظر مجلة معهد المخطوطات العربية ٢/ ٣٦٠.
وعليه شرح بعنوان: «كتاب البيان والتحصيل والشرح والتوجيه والتعليل فى المسائل المستخرجة للعتبى» لأبى الوليد محمد بن أحمد بن رشد (المتوفى ٥٢٠ هـ/ ١١٢٦ م): القرويين بفاس ٢/ كبير (٧٢٨ هـ، انظر: شاخت (Schacht ,Et.Or.I ,٢٧٩ تطوان (الأجزاء ٣ - ١٠، ١١٩٨ هـ، انظر: مجلة معهد المخطوطات العربية ١/ ١٧٣)، وكذلك (ج ٧، ١١٧٩ هـ، انظر: مجلة معهد المخطوطات العربية ١/ ١٨٨).','3','155',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (911,'وهذبه أبو محمد عبد الله بن أبى زيد القيروانى (المتوفى ٣٨٦ هـ/ ٩٩٦ م، انظر: رقم ٢٧ من هذا الفصل) بعنوان «تبويب المستخرجة»، وقد وصلت إلينا قطع قديمة من هذا الكتاب فى القيروان بعنوان «كتاب التخيير والتمليك والخلع»، (حوالى ٢٠ ورقة، ومنه ميكروفيلم فى المتحف الإسلامى بتونس، وانظر كذلك بروكلمان ملحق ١/ ٦٦٢) (١٦٦).

<span data-type="title" id=toc-2088>١٣ - ابن سحنون</span>
هو أبو عبد الله، محمد بن سحنون عبد السلام بن سعيد التنوخى، ولد بالقيروان سنة ٢٠٢ هـ/ ٨١٧ م، وتعلم فيها على يد والده سحنون وغيره من العلماء، ثم سافر إلى مكة المكرمة والمدينة المنورة فقابل أبا مصعب وسلامة بن شبيب. وكان عالما ذا مكانة رفيعة، تفوق مكانة والده، كان أيضا عالما فى التاريخ، وتوفى سنة ٢٥٦ هـ/ ٨٧٠ م.

<span data-type="title" id=toc-2089>أ- مصادر ترجمته:</span>
قضاة قرطبة للخشنى ١٧٨ - ١٨٢، رياض النفوس للمالكى ٣٤٤ - ٣٦٠، تذكرة الحفاظ للذهبى ٥٦٥، الديباج لابن فرحون ٣٣٤ - ٣٣٧، الوافى بالوفيات للصفدى ٣/ ٨٦، مرآة الجنان لليافعى ٢/ ١٨٠، معالم الإيمان لابن ناجى ٢/ ٧٩ - ٨٩، الأعلام للزركلى ٧/ ٧٦، شذرات الذهب لابن العماد ٢/ ١٥٠، معجم المؤلفين لكحالة ١٠/ ١٦٩.

<span data-type="title" id=toc-2090>ب- آثاره:</span>
١ - «النوازل» ذكره ابن خير (فى فهرسه ٢٥٤) بعنوان: «نوازل الصلاة من ديوان محمد بن سحنون» الكتانى بالرباط ٨٨٤ (١٠٩ ورقة).
٢ - «الرسالة السّحنونيّة وهى أسئلة وأجوبة فى فروع الفقه».
صائب بأنقره ٢٧٩٠/ ١ (الأوراق ١ - ٨٠، ١١١٥ هـ)، الأزهر، ٢/ ٣٤٦، فقه مالكى ١٢٦٧
_________
(١٦٦) يحذف ما ذكره بروكلمان رقم ٢ فى الأصل ورقم ٣ فى الملحق من المؤلفات، لأنها أقسام من «المستخرجة».','3','156',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (912,'(الأوراق ١٠٨ - ١٦٠، فى القرن العاشر الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ٢٨٠)، القرويين بفاس ١٣٨٤/ ١ (من ١ ب- ٥٩، ١١٧٥ هـ)، القاهرة، ملحق ١/ ١٦ رقم ٢١٢٠٢ ب (الأوراق ٨ - ٨٧، ١٢٤٦ هـ).
٣ - «آداب المعلمين» (رسالة فى تعليم الصبيان) (١٦٧)، الزيتونة بتونس ١٠٠٤٠ (٩ ورقات)، تحقيق حسن حسنى عبد الوهاب بتونس ١٩٣١ م، وكذلك أحمد فؤاد الأهوانى بالقاهرة ١٩٥٥، وترجمها لو كونت إلى الفرنسية بعنوان:
G. Lecomte, Le Livredesreglesde la Conduitedesmaitr esdecole, in: REl ٢١/ ١٩٥٣/ ٧٧ - ١٠٥
٤ - «كتاب أصول الدين»: الأوقاف بالرباط ١٠٧٦ (ضمن مجموعة).
٥ - له أيضا قسم من موعظة يوجد فى كتاب: «رياض النفوس للمالكى» ٣٤٨ - ٣٤٩.

<span data-type="title" id=toc-2091>١٤ - ابن مزين</span>
هو أبو زكرياء يحيى بن زكرياء بن إبراهيم بن مزين، أصله من طليطلة، انتقل إلى قرطبة، وحبس فترة من الزمن فى عهد عبد الرحمن الثانى (٢٠٦ هـ/ ٨٢٢ م- ٢٣٨ هـ/ ٨٥٧ م. ثم رحل إلى المشرق فعرف فى مصر والعراق عددا كبيرا من العلماء، ثم عاد إلى طليطلة فتولى بها القضاء. وتوفى سنة ٢٥٩ هـ، ٨٧٣ م وقيل سنة ٢٦٠ هـ.

<span data-type="title" id=toc-2092>أ- مصادر ترجمته:</span>
تاريخ علماء الأندلس لابن الفرضى ٢/ ١٧٨، الجذوة للحميدى ٣٥٠، فهرست ابن خير ٣٠٣، البغية للضبي ٤٨٢، الديباج لابن فرحون ٣٥٤ - ٣٥٥، الأعلام للزركلى ٩/ ١٦٠، معجم المؤلفين لكحالة ١٣/ ١٨٢.

<span data-type="title" id=toc-2093>ب- آثاره:</span>
«تفسير الموطأ (المالك)»، وتوجد منه قطع من سنة ٣٩٤ هـ فى مكتبة القيروان.
_________
(١٦٧) يجب أن تراجع على «كتاب آداب المتعلمين» المجهول المؤلف الموجود بمشكاة فى طهران ١١/ ٢٠١٦ رقم ٣٠٧٢ (الأوراق ١٥٠ - ١٦٥، ١٠٨٧ هـ).','3','157',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (913,'<span data-type="title" id=toc-2094>١٥ - ابن بشير</span>
هو أبو عبد الله محمد بن إبراهيم بن عبدوس بن بشير، ولد بالقيروان سنة ٢٠٢ هـ/ ٨١٧ م. كان تلميذا لسحنون وأصبح- أيضا- ثقة إماما. وتوفى سنة ٢٦٠ هـ/ ٨٧٤ م.

<span data-type="title" id=toc-2095>أ- مصادر ترجمته:</span>
قضاة قرطبة للخشنى ١٨٢، طبقات الفقهاء للشيرازى ١٣٤، الديباج لابن فرحون ٢٣٧ - ٢٣٨، رياض النفوس للمالكى ٣٦٠ - ٣٦٣، البيان المغرب لابن عذارى ١/ ١١٦، معالم الإيمان لابن ناجى ٢/ ٩٠ - ٩٤، الأعلام للزركلى ٦/ ١٨٣، معجم المؤلفين لكحالة ٨/ ٢٠٩.

<span data-type="title" id=toc-2096>ب- آثاره:</span>
١ - «المجموعة» وصل إلينا منها قسم فى كتاب «النوادر والزيادات» لابن أبى زيد القيروانى (وانظر:
(Schacht, Et. Or. I, ٢٧٥
٢ - «شرح مسائل من المدونة» ولا بد من بحث ما إذا كان هذا الشرح (انظر بروكلمان ملحق ١/ ٣٠٠ رقم ٨) هو الكتاب المعروف باسم: «شرح ابن بشير» أو «التنبيه على مبادئ التوجيه- (انظر: رقم ١٠، شرح رقم ١ لسحنون من هذا الفصل).
٣ - «كتاب الوثائق» وصلت إلينا قطع منه فى الكتاب الذى يحمل نفس الاسم/ لمحمد بن أحمد بن العطار (انظر: رقم ٢٩ من هذا الفصل)، وانظر: شاخت (Schacht ,Et.Or.I ,٢٧٦.-

<span data-type="title" id=toc-2097>١٦ - ابن عبد الحكم</span>
هو أبو عبد الله محمد بن عبد الله بن عبد الحكم، ولد سنة ١٨٢ هـ/ ٧٩٩ م، وهو أخو مؤرخ مصر أبو القاسم عبد الرحمن بن عبد الحكم، وتلميذ الإمام الشافعى، تحول إلى المالكية، وعارض الشافعية، وكان يعدّ فى مصر حجة زمانه فى مجال الحديث والقرآن والفقه. وتوفى سنة ٢٦٨ هـ/ ٨٨٢ م.','3','158',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (914,'<span data-type="title" id=toc-2098><span data-type="title" id=toc-2101>أ- مصادر ترجمته:</span></span>
وفيات الأعيان لابن خلّكان ١/ ٥٧٨، الوافى بالوفيات للصفدى ٣/ ٣٣٨ - ٣٣٩، ميزان الاعتدال للذهبى ٣/ ٨٦ - ٨٧، تذكرة الحفاظ للذهبى ٥٤٦، الديباج لابن فرحون ٢٣١ - ٢٣٢، مفتاح السعادة لطاش كبرى زاده ٢/ ١٥٥ - ١٥٦، شذرات الذهب لابن العماد ٢/ ١٥٤، الأعلام للزركلى ٧/ ٩٤ - ٩٥، معجم المؤلفين لكحالة ١٠/ ٢٢٣، بروكلمان ملحق ١/ ٢٢٨ رقم ١ ب.

<span data-type="title" id=toc-2099>ب- آثاره:</span>
١ - «سيرة (أو فضائل) عمر بن عبد العزيز» يبدو أنه يختلف عن كتاب والده عبد الله بن عبد الحكم، المؤلف بنفس العنوان ببعض الإضافات:
باريس ٢٠٢٧ (٧١ ورقة، ١٠١٧ هـ)، اياصوفية ٣٢٣٩ (١٠٩ ورقة، فى القرن التاسع الهجرى)، سراى، أحمد الثالث ٢٩٠٦/ ١ (من ١ أ- ١٥١ ب، ٥٦٤ هـ)، وتوجد نسختان فى مكتبة خاصة فى دمشق، وباسم والده عبد الله بن عبد الحكم نشره أحمد عبيد بالقاهرة ١٩٢٧، وبدمشق ١٩٥٤، ١٩٦٤، ١٩٦٦ م.
٢ - «فضائل عبد الملك بن عمر بن عبد العزيز، وسهل بن مزاحم» سراى، أحمد الثالث ٢٩٠٦/ ٢ (من ١٥١ أ- ١٥٥ ب، ٥٦٤ هـ) (١٦٨)

<span data-type="title" id=toc-2100>١٧ - ابن الموّاز</span>
هو أبو عبد الله، محمد بن إبراهيم بن زياد الإسكندرى بن الموّاز المالكى، ولد سنة ١٨٠ هـ/ ٧٩٦ م، تتلمذ على محمد بن عبد الحكم وغيره، كان له فضل كبير فى تبويب فروغ الفقه المالكى. وتوفى سنة ٢٦٩ هـ/ ٨٨٢ م، وقيل ٢٨١ هـ.
أ- مصادر ترجمته:
طبقات الفقهاء للشيرازى ١٣١، الوافى بالوفيات للصفدى ١/ ٣٣٥، الديباج لابن فرحون ٢٣٢ -
_________
(١٦٨) يحذف كتاب «معرفة علوم الحديث» الذى نسب إليه فى فهرس الإسكوريال- ٣/ ١٥٢ ومؤلف هذا الكتاب هو محمد بن عبد الله بن محمد الحاكم النيسابورى انظر: رقم ٢٩٧، كتاب: من باب الحديث.','3','159',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (915,'٢٣٣، شذرات الذهب لابن العماد ٢/ ١٧٧، مرآة الجنان لليافعى ٢/ ١٩٤، الأعلام للزركلى ٦/ ١٨٣، معجم المؤلفين لكحالة ٨/ ٢٠٠.

<span data-type="title" id=toc-2102><span data-type="title" id=toc-2105>ب- آثاره:</span></span>
«الموّازية» بحث هذا الكتاب فى فروع الفقه المالكى، ويوجد منه قطعة قديمة فى ١٥ ورقة فى المكتبة الخاصة ملك محمد الطاهر بن عاشور بتونس، انظر: الأعلام للزركلى ١٠/ ١٨٣.

<span data-type="title" id=toc-2103>١٨ - ابن وضّاح</span>
هو أبو عبد الله محمد بن وضّاح بن بزيع القرطبى. ولد بقرطبة سنة ١٩٩ هـ/ ٨١٥ م. رحل إلى المشرق مرتين طلبا للمعرفة. كان فقيها ومحدثا وزاهدا ولا يعدّ من العلماء الكبار فى عصره. وتوفى سنة ٢٨٦ هـ ٨٩٩ م.

<span data-type="title" id=toc-2104>أ- مصادر ترجمته:</span>
الجذوة للحميدى ٨٧ - ٨٨، فهرست ابن خير ١٥٠، البغية للضبى ١٢٣ - ١٢٤ ميزان الاعتدال للذهبى ٣/ ١٤٥، لسان الميزان لابن حجر ٥/ ٤١٦ - ٤١٧، البداية والنهاية لابن كثير ١١/ ٨٢، الأعلام للزركلى ٧/ ٣٥٨، معجم المؤلفين لكحالة ١٢/ ٩٤، بروكلمان ملحق ٢/ ٩٧٨ رقم ٣/ ١٣١٧.
ب- آثاره:
١ - «اتقاء البدعة»:
جاريت ٢٠٧٠/ ١ (١٢٢٧ هـ)، وبعنوان: «البدعة والنهى عنها» بدمشق ونشره محمد دهمان بدمشق ١٣٤٩، انظر: مجلة المجمع العلمى العربى بدمشق ١١/ ١٢٧.
٢ - «كتاب فيه ما جاء من الحديث فى النظر إلى الله» تونس، حسن حسنى عبد الوهاب.','3','160',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (916,'<span data-type="title" id=toc-2106>١٩ - الكنانى</span>
هو أبو زكرياء يحيى بن عمر بن يوسف بن عامر الكنانى، ولد بالأندلس سنة ٢١٣ هـ/ ٨٢٨ م، ومن المرجح أن مولده بقرطبة وتعلم فى سنى الصبا من عبد الملك بن حبيب (المتوفى ٢٣٨ هـ/ ٨٥٢ م)، ولا بد أنه رحل فى شبابة إلى القيروان لأنه أدرك دروس سحنون (المتوفى ٢٤٠ هـ/ ٨٥٤ م)، ثم سمع فى مصر دروس يحيى بن بكير (المتوفى ٢٣١ هـ/ ٨٤٥ م) وفى الحجاز دروس أبى مصعب الزّهرى، ووصف بأنه ناقد وعالم مدقق. وبعد عودته إلى القيروان أصبح حجة متقدما فى العلم بموطإ «مالك» و «المدونة» لسحنون، ويبدو أنه استوطن سوسة فى أخريات حياته. وتوفى سنة ٢٨٩ هـ/ ٩٠٢ م.

<span data-type="title" id=toc-2107>أ- مصادر ترجمته:</span>
تاريخ علماء الأندلس لابن الفرضى ٢/ ١٨١، الجذوة للحميدى ٣٥٤ - ٣٥٥، قضاة قرطبة للخشنى ١٨٤ - ١٨٦، البغية للضبى ٤٩٠ - ٤٩١، معالم الإيمان لابن ناجى ٢/ ١٥٦ - ١٦٥، رياض النفوس للمالكى ١/ ٣٩٦ - ٤٠٦، الديباج لابن فرحون ٣٥١ - ٣٥٣ لسان الميزان لابن حجر ٦/ ٢٧٠ - ٢٧٢، محمود على مكى فى صحيفة معهد الدراسات الإسلامية بمدريد ٤/ ١٩٥٦/ ٦٥ - ١٠٢، الأعلام للزركلى ٩/ ٢٠٠، معجم المؤلفين لكحالة ١٣/ ٢١٧.

<span data-type="title" id=toc-2108>ب- آثاره:</span>
١ - «كتاب أحكام السوق» يبدو أن هذا الكتاب أقدم كتاب مستقل فى «الحسبة» وصل إلينا، ويوجد فى مكتبة حسن حسنى عبد الوهاب بتونس، كما يوجد موجز له فى كتاب «المعيار المغرب» لأحمد بن يحيى التّلمسانى الونشريسى (المتوفى ٩١٤ هـ/ ١٥٠٨ م، انظر بروكلمان ملحق ٢/ ٣٤٨، ونشر القطع الباقية منه محمود على مكى فى البحث المذكور ص ١٠٣ - ١٥١.
وترجمه إلى الإسبانية جارسيا جومس:
E. Garcia Gomez, Unas." Ordenanzasdelzoc o" delsiglo LX; Trad. delmasantiguo antecentedelostr atadosandalucesd e Hisba, porunautorandalu z, in: Andalus ٢٢/ ١٩٥٧/ ٢٥٣ - ٣١٦.','3','161',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (917,'<span data-type="title" id=toc-2109>٢٠ - الجهضمى</span>
هو أبو إسحاق إسماعيل بن إسحاق بن إسماعيل بن حمّاد الجهضمى الأزدى، ولد بالبصرة سنة ١٩٩ هـ/ ٨١٥ م. كان مفسرا ومحدثا وإخباريا وفقيها على مذهب مالك. وكان لكتبه الفقهية أثر كبير فى المالكية (انظر: تاريخ بغداد للخطيب ٦/ ٢٨٥). ظل يعمل قاضيا طيلة حياته ببغداد. ولقد وصف الخطيب البغدادى (ص ٢٨٦) كتبه: «احكام القرآن، و «القراءات»، و «معانى/ القرآن» بأنها تتجاوز كثيرا من الكتب المصنفة. درس عليهاللغويان المبرد وأبو بكر الأنبارى، وتوفى سنة ٢٨٢ هـ/ ٨٩٥ م ببغداد.

<span data-type="title" id=toc-2110>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠٠، المنتظم لابن الجوزى ٥/ ٢/ ١٥١ - ١٥٣، الإرشاد لياقوت (لندن) ٢/ ٢٥٧ - ٢٦٢، (القاهرة) ٦/ ١٢٩ - ١٤٠، اللباب لابن الأثير ٦/ ١٤٢، تذكرة الحفاظ ٦٢٥ - ٦٢٦، الدول للذهبى ١/ ١٢٤، غاية النهاية لابن الجزرى ١/ ١٦٢ رقم ٧٥٤، بغية الوعاء للسيوطى ١٩٣، الديباج لابن فرحون ٩٢ - ٩٥، البداية والنهاية لابن كثير ١١/ ٧٢، شذرات الذهب لابن العماد ٢/ ١٧٨، مرآة الجنان لليافعى ٢/ ١٩٤، الأعلام للزركلى ١/ ٣٠٥، معجم المؤلفين لكحالة ٢/ ٢٦١، وانظر روزنتال، Rosenthal ,History ٣٢٩: وانظر بروكلمان ملحق ١/ ٢٧٣ رقم ٧ د.

<span data-type="title" id=toc-2111>ب- آثاره:</span>
١ - «فضل الصلاة على النبى صلّى الله عليه وسلم»:
كوبريلى ٤٢٨/ ٣ (من ١١٧ ب- ١٣٥ ب، ٨٥٥ هـ، انظر، (Weisweiler ,No.٦٧: الأزهر ٣/ ٧٣٠، حليم بالقاهرة (١١٣٩) ٣٤١٩٥ (الأوراق ٧ - ١٩، ١٣٠٢ هـ)، الظاهرية، مجموع ٣٨ (من ٨٦ أ- ٩٨ أ، فى القرن السابع الهجرى).
٢ - «أحاديث مالك بن أنس» تنقيح الجهضمى، الظاهرية مجموع ٩٣/ ١٤.
٣ - تنقيح «أحاديث» أيوب بن أبى تميمة السختيانى (المتوفى ١٣١ هـ/ ٧٤٨ م، انظر: رقم ١٢ فى باب الحديث)، الظاهرية، مجموع ٤/ ٢.','3','162',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (918,'٤ - «أحكام القرآن»، القيروان بتونس (قطعة، ٤٠٢ هـ).

<span data-type="title" id=toc-2112>٢١ - الهيثم بن سليمان</span>
عاش فى القرن الثالث الهجرى، وألف: أدب القاضى والقضاة، يوجد مخطوطا فى القيروان (الجزء الرابع).

<span data-type="title" id=toc-2113>٢٢ - ابن الورّاق</span>
هو أبو بكر، محمد بن أحمد بن محمد الجهم، الوراق المروزى، كان فقيها ومحدثا، ويقال إنه ألّف خمسين كتابا عن المذهب المالكى والانتصار له. وتوفى سنة ٣٢٩ هـ/ ٩٤١ م وقيل ٣٣٣ هـ.

<span data-type="title" id=toc-2114>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠٠، الديباج لابن فرحون ٢٤٣ - ٢٤٤، تاريخ بغداد للخطيب ١/ ٢٨٧، معجم المؤلفين لكحالة ٩/ ٣.

<span data-type="title" id=toc-2115>ب- آثاره:</span>
مسائل الخلاف، يوجد فى فاس والقرويين ٤٨٩.

<span data-type="title" id=toc-2116>٢٣ - ابن اللّبّاد</span>
هو أبو بكر، محمد بن محمد بن وشاح بن اللباد اللّخمى، أصله من القيروان، كان تلميذا ليحيى بن عمر بن يوسف الكنانى (انظر: رقم ١٩ من هذا الفصل)، وتوفى سنة ٣٣٣ هـ/ ٩٤٤ م بالقيروان.','3','163',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (919,'<span data-type="title" id=toc-2117><span data-type="title" id=toc-2120>أ- مصادر ترجمته:</span></span>
قضاة قرطبة للخشنى ٢٢٣ - ٢٢٤، الوافى بالوفيات للصفدى ١/ ١٣٠، الديباج لابن فرحون ٢٤٩ - ٢٥٠، معالم الإيمان لابن ناجى ٣/ ٢٣، الأعلام للزركلى ٧/ ٢٤٢، معجم المؤلفين لكحالة ١١/ ٣٠٩، وانظر بروكلمان ملحق ١/ ٣٠١ رقم ٣ أ.

<span data-type="title" id=toc-2118><span data-type="title" id=toc-2121>ب- آثاره:</span></span>
«كشف الرّواق عن الصروف الجامعة للأواق».
يتناول هذا الكتاب الموازين الشرعية المقررة، الجزائر ١٣٢٤/ ١ (الأوراق ١ - ٣، ١٢٢٦ هـ)، انظر أيضا الأعلام للزركلى ٧/ ٢٤٢.

<span data-type="title" id=toc-2119>٢٤ - الذّهلى</span>
هو أبو طاهر محمد بن أحمد بن عبد الله بن نصر الذهلى البصرى، كان فقيها ومفسرا وأديبا، ولد سنة ٢٧٩ هـ/ ٨٩٣ م. وكان قاضيا ببغداد وواسط ودمشق ومصر.
وتوفى فى مصر سنة ٣٦٧ هـ/ ٩٧٨ م.
أ- مصادر ترجمته:
تاريخ بغداد للخطيب ١/ ٣١٣ - ٣١٤، الوافى، بالوفيات للصفدى ٢/ ٤٥، الديباج لابن فرحون ٣١٤ - ٣١٦، قضاة دمشق لابن طولون ٣٤ - ٣٥، معجم المؤلفين لكحالة ٨/ ٢٨٤.
ب- آثاره:
١ - «الحديث»، انتقى منه أبو الحسن الدّار قطنى، الأوقاف ببغداد ٢٨٨٦/ ٢، الفهرس ص ٢٥٣، (ج ٢٣/ ٥٥٢ هـ، انظر: كوركيس عواد فى مجلة سومر ٣/ ١٩٤٧ م ٢٤٢)، ومنه: «عشرة أحاديث منتخبة من فوائد الذهلى»، الظاهرية ١٠٧/ ٨.
٢ - تذكر له المصادر كتبا منها: «كتاب فى الفقه»، و «مختصر تفسير الجيّانى» و «مختصر تفسير البلخى».','3','164',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (920,'<span data-type="title" id=toc-2122>٢٥ - الأبهرى</span>
هو أبو بكر محمد بن عبد الله بن محمد الأبهرى، ولد فى أبهر سنة ٢٨٧ هـ/ ٩٠٠ م وعاش ببغداد، وانتهت إليه الرياسة بين فقهاء المالكية فى عصره. ولقد وجه إليه الحنفية والشافعية أسئلة فى قضايا خلافية، ويقال إنه ظل فى جامع المنصور ببغداد ستين عاما يلقى دروسه ويفتى بين الناس، وتوفى سنة ٣٧٥ هـ/ ٩٨٥ م.

<span data-type="title" id=toc-2123>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠١، طبقات الفقهاء للشيرازى ١٤١، تاريخ بغداد للخطيب ٥/ ٤٦٢ - ٤٦٣، الوافى بالوفيات للصفدى ٣/ ٣٠٨، الديباج لابن فرحون ٢٥٥ - ٢٥٨، شذرات الذهب لابن العماد ٣/ ٨٥ - ٨٦، النجوم الزاهرة لابن تغرى بردى ٤/ ١٤٧، هدية العارفين ٢/ ٥٠، الأعلام للزركلى ٧/ ٩٨، معجم المؤلفين لكحالة ١٠/ ٢٤١.

<span data-type="title" id=toc-2124>ب- آثاره:</span>
١ - «شرح المختصر الكبير فى الفقه» لعبد الله بن عبد الحكم (المتوفى ٢١٤ هـ/ ٨٢٩ م) انظر رقم ٧ كتاب رقم ١ من هذا الفصل).
٢ - «الفوائد المنتقاة الغرائب الحسان»: الظاهرية، مجموع ٥٩ (١٣٢ ب- ١٥٠ ب، عليه سماع من سنة ٥٥٧ هـ)

<span data-type="title" id=toc-2125>٢٦ - ابن الجلّاب</span>
هو عبيد الله بن الحسن أبو القاسم ابن الجلاب البصرى، تفقه بالأبهرى، وقد سبقت ترجمته، توفى سنة ٣٧٨ هـ/ ٩٨٨ م. ولا نعرف شيئا عن حياته، واسمه غير متفق عليه فى الكتب، فالشيرازى (فى طبقات الفقهاء: ١٤٢) يذكره باسم أبى القاسم عبد الرحمن بن عبد الله بن الجلاب.','3','165',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (921,'<span data-type="title" id=toc-2126>أ- مصادر ترجمته:</span>
الديباج لابن فرحون ١٤٦، شجرة النور لمخلوف ٩٢، هدية العارفين ١/ ٤٤٧، معجم المؤلفين لكحالة ٦/ ٢٣٨، وانظر بروكلمان ملحق ١/ ٣٠١ رقم ٤.

<span data-type="title" id=toc-2127>ب- آثاره:</span>
١ - «كتاب التفريع فى الفقه المالكى» المتحف البريطانى ٢٢٨، إضافات ٩٥٢١/ ١ (فى القرن الثانى عشر الهجرى)، مدريد ٢ (١٦٠ ورقة)، ٧٤ (١٨٦ ورقة)، ١٠٢/ ١ (٢٦٨ ورقة، ٨٥٤ هـ)، ١٣٥/ ٥ (١٠٨٦ هـ)، القرويين بفاس ٢٣٧ (ج ١، ٧٠١ هـ)، ١١٢١ (٦٢٧ هـ)، ١١٢٢، الجزائر ١٠٣٦ (١٨٨ ورقة، ٨٤٢ هـ)، وتوجد منه قطعة:
«كتاب الصلاة» ببلدية الإسكندرية ٤٩٨٦/ ١ ج، -؟ برلين ٤٤٤٣ (الأوراق ١٢٩ - ١٣٢، ٨٣٠ هـ)، الظاهرية مجموع، فقه مالكى ١٠، الزيتونة بتونس ١٠١٤٩ (٢١٨ ورقة، ١٠١٠ هـ)، الجلاوى بالرباط ٢٧ (١٣٩ ورقة، ٦٩٣ هـ)، ومنه: قطعة «كتاب السهو فى الصلاة» فى طلعت بالقاهرة، مجموع ٦٩٨.
وله شرح لقاسم بن عيسى بن ناجى القيروانى (المتوفى ٨٣٧ هـ/ ١٤٣٣ م انظر بروكلمان ملحق ٢/ ٣٣٧)، الزيتونة بتونس ٤/ ٣١٣ رقم ٢٥٣٤ (١٧٥ ورقة، ١٠٠٥ هـ)، وله مختصر أعده عبد السلام التونسى، القرويين بفاس، الرقم القديم ٩١١.
٢ - «شرح «المدوّنة»، انظر رقم ١٠ شرح قم ٢ من هذا الفصل.

<span data-type="title" id=toc-2128>٢٧ - ابن أبى زيد القيروانى</span>
هو أبو محمد، عبد الله بن أبى زيد عبد الرحمن القيروانى النّفزى (١٦٩).
هو سليل أسرة من نفزة فى الأندلس، ولد بالقيروان سنة ٣١٠ هـ/ ٩٢٢ م. وبدأ تعليمه فى سن مبكرة، وأخذ عن عدد من كبار العلماء فى القيروان مثل أبى بكر محمد ابن محمد بن اللّباد وأبى الحسن الخولانى وأبى العرب محمد بن أحمد بن تميم، وظل
_________
(١٦٩) يصحح الاسم عند بروكلمان.','3','166',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (922,'يأخذ العلم حتى وهو فى طريق الحج عن كثير من العلماء. ذاع صيته عالما عظيما فى وقت مبكر فيما يبدو ولقب بمالك الصغير، ورسالته أوضح عرض لفقه المالكية. وتوفى فى القيروان ٣٨٦ هـ/ ٩٩٦ م.

<span data-type="title" id=toc-2129>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ٢٠١، فهرست ابن خير ٢٤٤، الديباج لابن فرحون ١٣٦ - ١٣٨، معالم الإيمان لابن ناجى ٣/ ١٣٥ - ١٥١، مرآة الجنان لليافعى ٢/ ٤٤١، النجوم الزاهرة لابن تغرى بردى ٤/ ٢٠٠، شذرات الذهب لابن العماد ٣/ ١٣١، الأعلام للزركلى ٤/ ٢٣٠، معجم المؤلفين لكحالة ٦/ ٧٣ شجرة النور لمخلوف ٩٦، محمد بن شنب: دائرة المعارف الإسلامية ٢/ ٣٨٠.
M. Ben Cheneb, Act. XIVCongr. Or. Alger, ١٩٠٥, ٤٩٨ - ٥٠٠. H. R. Idris. Deux Juristes Kairouanisde L''epoque Ziride AIEO ١٢/ ١٩٥٤/ ١٢٤ - ١٧٢.
وانظر بروكلمان ملحق ١/ ٣٠١ رقم ٥.

<span data-type="title" id=toc-2130>ب- آثاره:</span>
أولا: «الرسالة»:
هذه الرسالة متن فقهى جامع، برلين ٤٤٤٦ (٦٨ ورقة، ٨٦٠ هـ)، ٤٤٤٧ (٨٠ ورقة، ١٠٩١ هـ، وهناك ذكرت ثلاث مخطوطات أخرى)، جوتا ١٠٤٥ (١٠ ورقات، نسخة حديثة)، باريس ١٠٥٧/ ١ (الأوراق ١ - ٦٨، فى القرنين العاشر أو الحادى عشر للهجرة)، ١٠٥٨ (١٤٠ ورقة، ١٠٥٩ هـ)، ١٠٥٩، (١٢٨ ورقة، ١٠٨٣ هـ)، ١٠٦٠/ ١ (الأوراق ١ - ٦٩، فى القرن الحادى عشر الهجرى)، ١٠٦١/ ١ (الأوراق ١ - ٧٧، فى القرنين الحادى عشر أو الثانى عشر الهجرى)، ٤٨٠٩ (٢٣٥ ورقة فى القرن الثالث عشر الهجرى)، الإسكوريال ٢٤٨/ ٦ (من ٤٤ ب- ١٠٧ ب)، ١٥٢٨ (١١٢ ورقة)، فاتيكان، فيدا ٤١٦ (الأوراق ٣ - ٥٢، ١١١٨ هـ)، ١٣٤٣ (١٥١ ورقة، فى القرن السابع الهجرى)، الزيتونة بتونس ٤/ ٣٠٠ رقم ٢٥٠٠ (٧٤ ورقة)، القاهرة ثان ١/ ٤٨٢، فقه مالكى ١٣٤، ٢٦٠، ٢٧٥، ٣٢٠، ٣٥٦، الجزائر ٧٦٨/ ٥ (الأوراق ١٣١ - ٢٢٥، فى القرن الثانى عشر الهجرى)، ٧٦٩/ ٤ (الأوراق ١٥ - ٣٠، الثانى عشر الهجرى)، ١٠٣٧ - ١٠٤٦، (نابولى ٢٥، الفهرس/ ٢٩٩)، المتحف لبريطانى، مخطوطات شرقية ٦٥٨٥/ ١ (انظر: إلّيس، (Ellis ,Descr.list ٢٨ مانشستر ٨١ (١١٧ ورقة، فى القرن العاشر الهجرى،','3','167',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (923,'الفهرس رقم ١٩٣)، نور عثمانية ١٧٧٥ (٩٥ ورقة، فى القرن السابع الهجرى انظر: فهرس معهد المخطوطات العربية ١/ ٢٨٠)، حسن حسنى باستنبول ١١٦٠/ ٩ (من ١١٨ ب- ١٢٠ ب، فى القرن العاشر الهجرى)، باريس ٥٢٩٠ (الأوراق ١ - ٣٠)، ٥٤٦٠ (الأوراق ٢٣٧ - ٢٦٣)، ٥٤٩٥ (الأوراق ١ - ٩٦)، ٥٥٥٨ (الأوراق ١٨٦ - ٢٠١)، ٥٥٨٦ (الأوراق ١ - ١٧٧)، ٥٥٩٤ (الأوراق ٤٥ - ١٤٧)، ٥٦٠٦ (الأوراق ١٣٥ - ١٣٨)، ٥٦٣٦ (الأوراق ١ - ١٨)، ٥٦٤٦ (الأوراق ٣ - ١٢٠)، ٥٦٧٦ (الأوراق ١ - ١٠٩، ١١٢ - ١٢٢)، ٥٧٠٣ (الأوراق ٢٧ - ٢١٩)، ٥٧٣١، ٦٤٧٨ (الأوراق ١ - ٣٥، انظر: فايدا ٥٨٤)، أمستردام ٨ ى ٢٩/ ٥ (الأوراق ١٧٥ - ١٩٢، انظر فورهوف ٣١٥)، الأزهر ٢/ ٣٤٣ (٢٥ نسخة) وكذلك ٧/ ٦٤ - ٦٥، بلدية الإسكندرية ٢٢٠٥ د، انظر: القاهرة، ملحق ١/ ٣٤٨ (نسختان)، الرباط ١/ ٢٨٣ (ثلاث نسخ)، الكتانى بالرباط ٥ (فى القرن العاشر الهجرى)، وطبع عدة مرات بالحجر فى فاس وأعيد طبعه بالقاهرة ١٢٩٦، ١٣٠٤، ١٣١٤، ١٣١٩، ١٣٢٣، ١٣٢٤، ١٢٣٨، ١٣٤٠.
وترجمه إلى الإنجليزية: رسل وعبد الله المأمون السهروردى:
First Stepsin Muslim Jurisprudence, consistingof Excerptsfrom Bakuratas- Sadof Ibn of Ibn Abi Zayd, Withar. text, Engl. transl .. andnotesby A. D. Russelland Abdallahal- Mamun Suhrawardy, London ١٩٠٦. H. R. Idris, Notesur L''identification dudedicatairedel a Risalad''Ibna. Zaidal- Q., Cahiers de Tunisie I/ ١٩٥٣/ ٦٣ - ٦٨
وترجمها فانيان إلى اللغة الفرنسية وشرحها وأعد لها فهرسا تحليليا:
E. Fagnan, La R. outraiteabregede droitmalekiteetm oralemusulmane. Paris ١٩١٤.
ونشر نصها العربى وترجمها إلى الفرنسية برشيه L.Bercher الجزائر ١٩٤٥.
شروح الرسالة: ١ - «نصح المقالة فى شرح الرسالة» لأبى عبد الله محمد بن على بن محمد بن الفخّار الجذامى/ (المتوفى ٧٢٣ هـ/ ١٣٢٣ م، انظر: معجم المؤلفين لكحالة ١١/ ٤٢): ميونيخ ٣٤٢ (١٩٧، ٨٩٣ هـ)، الإسكوريال ١٠٦٣ (٢٦٣ ورقة، فى القرن العاشر الهجرى انظر: فايدا» -٢ (Vajda ,Andalus ٢٨ /٨٦ - ٨٧ - تقييد على الرسالة» لأبى الحجاج يوسف بن عمر الأنفاسى الجيلانى (المتوفى ٧٦١ هـ/ ١٣٦٠ م، انظر: معجم المؤلفين لكحالة ١٣/ ٣٢٠): ميونيخ ٣٤٣ (١٧٠) (٥١٠ ورقة، ٨٩١ هـ)، المتحف البريطانى ١٦٤، إضافات ٩٥٤٢ (ج ١، ١٤٧ ورقة، ٩٩٦ هـ)، بلدية الإسكندرية ١٦٧١ ب- (١٠٠٦ هـ)،
_________
(١٧٠) يصحح ما عند بروكلمان.','3','168',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (924,'١٨٤٩ ب (ج ١، ١٠٠٢ هـ)، الإسكوريال أول ١٠٥٩، الجزائر ١٠٦١ (١٦٨ ورقة)، الكتانى بالرباط ٦٦٥ (ج ١ ١٩٤ ورقة)، ٨٢١ (٢٤٤ ورقة).
٣ - شرح لعبد الله بن يوسف البلوى الشّبيبى القيروانى (المتوفى ٧٨٢ هـ/ ١٣٨٠ م انظر شجرة النور لمخلوف: ٢٢٥): الزيتونة بتونس ٤/ ٣٠٦ رقم ٢٥١٤ (١٩٠ ورقة). (١٧١).
٤ - شرح للقاسم بن عيسى بن ناجى (المتوفى ٨٣٧/ ١٤٣٣ م انظر بروكلمان ٢/ ٢٣٩): الزيتونة بتونس ٤/ ٣٠٨ رقم ٢٥٢١ - ٢٥٢٢ (ج ١، ٢، ٢٥٤ ورقة، ٢٣٢ ورقة)، باريس ٥٠٣٣ (١٤١ ورقة، ٨٩٧ هـ)، (القرويين بفاس ٨٩١ (؟ )، الإسكوريال أول ١١٢٣، المسجد الكبير بطنجة ٢/ ٧١)، الجزائر ١٠٤٩ (٩٣ ورقة، ٨٧٢ هـ)، ١٠٥٠ (غير كاملة، ٢٠٦ ورقة فى القرن الحادى عشر الهجرى)، بلدية الإسكندرية ١٤٥١ ب (٢٤٨ ورقة ٩٦٦ هـ، انظر فهرس معهد المخطوطات العربية ١/ ٢٨٠)، الرباط ٥٣٧ (٢٣٣ ورقة، ٩٨٤ هـ)، ٨٥٣ (٢٦٢ ورقة، انظر: الفهرس ١/ ٢٨٦)، وطبع بالقاهرة ١٣٣٢ هـ.
٥ - «تحرير المقالة» لأحمد بن محمد بن عبد الله القلشانى (المتوفى ٨٦٣ هـ/ ١٤٥٩ م، انظر: نيل الابتهاج لأحمد بابا ٧٨): الفاتيكان، فيدا ١٣٥٥ (١٨١ ورقة، ٩٥٤ هـ)، الزيتونة بتونس ٤/ ٣٠٦ رقم ٢٥١٣ (٢٦٢ ورقة، ١٣٢٠ هـ)، الجزائر ١٠٤٧ (ج ٢، ٢٤٩ ورقة، ٨٧٣ هـ)، ١٠٤٨ (ج ٢، ٢٠٠ ورقة، ٨٧٢ هـ)، (القرويين بفاس، الرقم القديم ٩٥١ - ٩٥٤، الإسكوريال أول ١٠٦٠، الرباط ٨٤١ (ج ١، ٢، ١٧٤ ورقة، ٢٢٣ ورقة، انظر الفهرس ١/ ٢٦٣)، الكتانى بالرباط ٦٩٩ (٢١٥ ورقة)، ٨٢٧ (١٤٧ ورقة، ٢١٢ ورقة).
٦ - «مرشد المبتدئين» لأبى محمد سعيد بن (سليمان) الحسين بن محمد الحميدى (أتمها ٩٦٤ هـ/ ١٥٥٦ م): الزيتونة بتونس ٤/ ٣٧٦ رقم ٢٧٨٤ (٢٩٨ ورقة، قبل ١١٥٣ هـ)، بلدية الإسكندرية ٢٢٨٧ د.
٧ - شرح أحمد بن أحمد بن محمد زرّوق (المتوفى ٨٩٩ هـ/ ١٤٩٣ م انظر بروكلمان ٢/ ٢٥٣)، بلدية الإسكندرية ١٤٤٨ ب (١٠٨٠ هـ)، (القرويين بفاس، الرقم القديم ٩٥٠)؛ جامع ينى ٧٤٤/ ١ (الأوراق ١ - ٤٨).
وطبع بالقاهرة ١٣٣٢ هـ.
٨ - «إيضاح المسالك» لداود بن على بن محمد الغلتاوى الأزهرى (المتوفى ٩٠٢ هـ/ ١٤٩٧ م، انظر:
_________
(١٧١) تحذف مخطوطة بلدية الإسكندرية ١٠ عند بروكلمان.','3','169',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (925,'الضوء اللامع للسخاوى ٣/ ٢١٥ - ٢١٦، ونيل الابتهاج لأحمد بابا: ١١٦ - ١١٧): الزيتونة بتونس ٤/ ٢٧٧ رقم ٢٤٢٦ (٢٩٨ ورقة ١١٣٠ هـ)، ٢٤٢٧ (٢٣٠ ورقة، ٩٤٧ هـ)، الكتانى بالرباط ٨٥٣ (١٩٨ ورقة).
٩ - ويقال إن أبا الحسن على بن محمد بن خلف المتوفى الشاذلى (المتوفى ٩٣٩ هـ/ ١٥٣٢ م) قد ألّف شروحا كاملة لهذا الكتاب وشرحين آخرين لبعض موضوعاته (انظر: الزيتونة بتونس ٤/ ٢٨٢). وتوجد من هذه الشروح:
أ- «الفتح الربانى»، ليدن ١٧٨٠ (٤٠٤ ورقة، ١٠٠٦ هـ، انظر فورهوف (Voorh.٣١٥ - ب- «تحقيق المبانى وتحرير الأمانى»، المتحف البريطانى ١٣٢، إضافات ٦٩٠٩/ ٢ (فى القرنين الحادى عشر أو الثانى عشر الهجرى)، الظاهرية، فقه مالكى ٧؛ باريس ٥١١١ (انظر: فايدا (Vajda ٦٥٨ الزيتونة بتونس ٤/ ٢٨١ - ٢٨٣ رقم ٢٤٣٨ (٤٥١ ورقة، ١٠٩٦ هـ)، ٢٤٣٩ (ج ٢، ١٢٢ ورقة)، ٢٤٤٠ (ج ٣، ١٥٦ ورقة)، الرباط ٨٥٤ (ج ١، ٢٨٤ ورقة)، ٨٥٦ (ج ٢، ١٥٧ ورقة، ١٠٥٩ هـ، انظر: الفهرس ١/ ٢٦٧)، الأزهر ٢/ ٣١٣، فقه مالكى ١٤٣ (ج ١، ٣٠٨ ورقة)، ٥٦٤ (ج ١، ٤٤٣ ورقة، ٥١٩ ورقة، ١١٨٦ هـ)، ٧٣٢ (ج ٢، ٥٢٠ ورقة ١٠٧٤ هـ).
ج- «كفاية الطالب»، الزيتونة بتونس ٤/ ٣٥٨ رقم ٢٧٤٢ (٢٢٨ ورقة)، ٢٧٤٣ (٢٢٢ ورقة، ١٢٥٨ هـ)، ٢٧٤٤ (قطعة، ٥٩ ورقة)، باريس ١٠٦٢، (١٠٤ ورقة، فى القرن الحادى عشر الهجرى)، الجزائر ١٠٥١ - ١٠٥٩ (تسع مخطوطات)، ١٩٥٠ - ١٩٥١ (مخطوطتان)، القاهرة ثان ١/ ٤٩٠ (١١ نسخة)، كرافت ٤٦٠، جوتا ١٠٤٦ (٤٢٨ ورقة، نسخة جديدة)، أيضا ١٠٤٧ (٣٤٢ ورقة) /، المتحف البريطانى الملحق ٣٠٢، مخطوطات شرقية ٢٩٨٩ (الأوراق ١ - ١٦٥، ١٣٠١ هـ)، ليدن ١٧٨١ (٣٩٢ ورقة، انظر فورهوف، (Voorh.٣١٥ رامبور ١/ ٢٤٢، فقه ٤٨٦، الإسكوريال ثان ١٢٢١؛ باريس ٥٠١٦، ٥٣١٣، ٥٣٨٤، ٥٤٦٩، ٥٤٩٦، ٥٥٠٢، ٥٦١٧ (الأوراق ٤٢ - ٢٠٧)، ٥٦٢١، ٥٦٨٥ (الأوراق ١٦٤ - ٢٢٨، انظر، (Vajde ٤٢٧: القاهرة ثان ٢/ ٢٦١ رقم ٢٤٢١٩ ب (٤٤٥ ورقة، ١٢٧٩ هـ)، بلدية الإسكندرية ١٤٥٢ ب، الرباط ٣٠٣، ٤٠١، ٥٧١ (٢٧٦ ورقة، ١٢٨٠ هـ)، ١٤٦٥ (٢٣٣ ورقة، ١٠٩٣ هـ، انظر: الفهرس ١/ ٣٠٧ - ٣٠٨)، المسجد الكبير بالجزائر ٧٥ (١٠٩٦ هـ)، ٧٦ (١٠٤١ هـ)، ٧٧.
وطبع بالقاهرة ١٨٦٤ م (؟ )، ١٣٠٥، ١٣٠٩، ١٣١٠، ١٣٢٥ هـ.
ولهذا الكتاب حاشية تسمى «كفاية الكفاية» لمحمد بن عبد الملك (كتبها ١١٢٩ هـ/ ١٧١٧ م)، الجزائر ١٠٦٠ (الأوراق ٣ - ١٣٧). وكذلك لعلى بن أحمد العدوى الصعيدى (المتوفى ١٨٩ هـ/ ١٧٧٥ م انظر بروكلمان ٢/ ٣١٩)، الزيتونة بتونس ٤/ ٢٩٣، رقم ٢٤٧٩ - ٢٤٨٠ (ج ١، ٢، ٣٦٩ ورقة، ٣٤٦ ورقة).','3','170',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (926,'وطبع بالقاهرة ١٢٨٨، ١٣٠٥، ١٣١٠، ١٣٢٨، ١٣٣٥، ١٣٤٤ هـ، ١٩٣٨ م.
د- غاية الأمانى: الزيتونة بتونس ٤/ ٣٥٠ رقم ٢٧١٧ - ٢٠٢٧ (ج ١ - ٣، ١٦٤ ورقة، ٢٥٩ ورقة، ١٩٥ ورقة، ١٠٢٩ هـ).
ولا بد من بحث ما إذا كانت النسخة التالية مطابقة فى مضمونها لكتاب «كفاية الطالب» - وهو ما نرجحه- أو أنها خاصة بشرح آخر: القرويين بفاس، الرقم القديم ٩٥٧ - ٩٥٩، ٩٦٣ - ٩٦٤، ٩٧٠ تلمسان ٧، المسجد الأكبر بطنجة ١/ ١٢).
١٠ - «تنوير المقالة بحل ألفاظ الرسالة» لمحمد بن إبراهيم التّتائى (المتوفى ٩٤٢ هـ/ ١٥٣٥ م انظر بروكلمان ٢/ ٣١٦)، باريس ٤٥٤٨ (٣٥٧ ورقة، ١٠٩١ هـ)، ٥٣٢٧ (٣٣٥ ورقة فى القرن الثانى عشر الهجرى)، القاهرة ثان ١/ ٤٧٨ (خمس نسخ)، ميونيخ ٣٤٤ (ج ٢، ٣٠٢ ورقة، ١١٥٦ هـ)، بلدية الإسكندرية ١٠٥٧ ب، ١٤٤٧ ب، الرباط ٢٢ (١٢٦ ورقة، ١٢٧٩ هـ، انظر بروفنسال، Provencal No.١٥٢ (الزيتونة بتونس ٤/ ٣٠٩ رقم ٢٥٢١ - ٢٥٢٩ (أربع نسخ)، الجزائر ١٠٦٢ (١٨٠ ورقة، فى القرن الثانى عشر الهجرى).
وعلى هذا الشرح كتب على بن محمد الأجهورى (المتوفى ١٠٦٦ هـ/ ١٦٥٦ م انظر بروكلمان ٢/ ٣١٧)، كتابه: «المنقذ من الضلالة»: الرباط ٢١٠ (١٨٤ ورقة، ١١٠٨ هـ، انظر: بروفنسال (Provencal No.١٥٣ الزيتونة بتونس ٤/ ٢٩١ رقم ٢٤٧٤ - ٢٤٧٥ (ج ١، ٢)، ٢٤٧٦ (ج ٢)، القاهرة ثان ١/ ٤٨٤، فقه مالكى ٥، الكتانى ٧٠٦ (٣٧٤ ورقة).
كما كتب حاشية عليه على بن چلبى الشّبّينى (١٧٢)، (فى القرن الثانى عشر الهجرى انظر معجم المؤلفين لكحالة ٧/ ٥٣)، بنگيبور ١٠/ ٧٤ رقم ٥٧٥ (٩ ورقات فى القرن الثانى عشر الهجرى).
١١ - شرح لعبد الله بن أحمد الفاكهى (المتوفى ٩٧٢ هـ/ ١٥٦٤ انظر بروكلمان ٢/ ٣٨٠): رامبور ١/ ٢٠٧، فقه ٢٦٨ (٢١٤ ورقة).
١٢ - «الفواكه الدوانى على رسالة .. إلخ» لأحمد بن غنيم بن سالم النّفراوى (المتوفى ١١٢٥ هـ/ ١٧١٣ م انظر بروكلمان ملحق ٢/ ٤٣٩)، ميونيخ ٣٤٥ (ج ٢، ٦٣٩ ورقة، ١١٥٢ هـ)، باريس ١٠٦٣ - ١٠٦٩، ٥٣٠٥، الزيتونة بتونس ٤/ ٣٠٧ - ٣٠٨ رقم ٢٥١٤ - ٢٥١٥ (ج ١، ٢، ١١١٢ هـ)، ٢٥١٦ (ج ٢)، ٢٥١٧ - ٢٥٢٠ (ج ١ - ٤، ١١٧٠ هـ).
_________
(١٧٢) يصحح ما عند بروكلمان.','3','171',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (927,'سراى، ريفان ٥٢٠ (٣٧٩ ورقة، ١١٥٤ هـ)، باريس ٥٢٧٢، ٥٢٧٣ (انظر فايدا ٣٤١)، الأزهر ٧/ ٧٥ رقم ٥٣١٩١ (ج ٢، ٣٧٠ ورقة ١١٧٣ هـ)، ٥٣١٩٩ (ج ١، ٢، ٤٢١ ورقة، ٤٧١ ورقة)، بلدية الإسكندرية ١٤٥٣ ب (١١٤٩ هـ)، الرباط ٦٢٩ (ج ١ - ٣، ١١١٢ هـ، انظر: الفهرس ١/ ٣٠٣)، القاهرة، ملحق ٢/ ١٨٩ رقم ٢٢٢٥٨ ب (ج ١، ٢، ١١٣٨ هـ).
١٣ - شرح لأبى عبد الله محمد بن قاسم جسّوس (المتوفى ١١٨٢ هـ/ ١٧٦٨ م، انظر: معجم المؤلفين لكحالة ١١/ ١١٩): الرباط ١٢٨ (٢٩٨ ورقة، ١٢١٣ هـ)، ١٩٣ (٢٠٤ ورقة، ١٢٠٧ هـ)، ٤٠٥ (٢٩٠ ورقة، ١٢٥٧ هـ، انظر: بروفنسال رقم ١٥٤ - ١٥٦)، وطبع فى فاس ١٣١٢ هـ.
١٤ - «حلل المقالة فى شرح الرسالة» لمؤلف يسمى الزناتى: باريس ٥٣٣٦ (١٣٩ ورقة، فى القرن الثالث عشر الهجرى، انظر فايدا ٣٨٤؟ «حاشية على الرسالة» للزياتى (؟ )، الكتانى بالرباط ٨١٥/ ٢ (من ص ٤٠ - ٢١٤).
١٥ - شروح مجهولة المؤلف: برلين ٣٥٤٨ (٢٧٢ ورقة)، المتحف البريطانى ٨٥٢ - ٨٥٣، إضافات ٩٥١٠، ٩٥٤٥، باريس ١٠٧٠ (٤٥٠ ورقة، ١١٦٥ هـ)، وفى مدريد ١١٤ (٢٥١ ورقة، ٩٩٧ هـ) بعنوان «المنهج»، بالرباط ٨٥٥ (الأوراق ١٢٤ - ٢٠٣، انظر: الفهرس ١/ ١٢٤) بعنوان «تعليق»، الجزائر ١٠٦٥ - ١٠٧٠/ (أكثرها غير كامل وهو من عدة مخطوطات)، القرويين بفاس، الرقم القديم ٩٦٥).
ونظم الرسالة شعرا محمد بن أحمد بن عبد الله الفاسى العثمانى المكناسى (المتوفى ٩١٩ هـ ١٥١٣ م، انظر: نيل الابتهاج لأحمد بابا: ٣٣٣).
ونظمها مع شرحها محمد بن محمد بن عبد الرحمن الخطاب (المتوفى ٩٥٣ هـ/ ١٥٤٦ انظر بروكلمان ٢/ ٣٨٧): المتحف البريطانى، الملحق ٣٠٢، مخطوطات شرقية ٢٩٨٩/ ٢ (من ١٦٦ ب- ١٧٩ أ، ١٣٠١ هـ)، الجزائر ١٠٥٩ (الأوراق ١٣٧ - ١٤٧)، ١٠٦٣ (الأوراق ١ - ١٩)، ١٠٦٤ (الأوراق ١ - ٩).
ثانيا: «السّنن» أو «كتاب الجامع فى السنن والآداب والمغازى والتاريخ»: القرويين بفاس ٦٤٥ (٥٣٢ هـ)، المتحف البريطانى ٨٨٨/ ٨، إضافات ٩٦٩٢، (الأوراق ٢١١ - ٢٤٩، فى القرن العاشر الهجرى)، الرباط (اطلعت على هذا المخطوط- وقد ضاع منى رقمه- يقع فى ٨٨ ورقة، من القرن الثامن الهجرى).
ثالثا: «النوادر والزيادات على ما فى المدوّنة وغيرها من الأمّهات» (انظر رقم ١٠ كتاب رقم ١ من التكملات والمختصرات).','3','172',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (928,'رابعا: «مختصر المدوّنة والمختلطة» (انظر رقم ١٠، كتاب رقم ٢) خامسا: «تهذيب العتبيّة» «تبويب المستخرجة» ومنه «كتاب التخيير والتمليك والخلع من المستخرجة من الأسمعة مما ليس فى المدوّنة بتبويب عبد الله بن أبى زياد (انظر رقم ١٢).
سادسا: «الرد على ابن مسرّة المارق» (المتوفى ٣١٩ هـ/ ٩٣١ م، انظر بروكلمان ملحق ١/ ٣٧٨)، ووصل إلينا قطعة من هذا الكتاب فى «طبقات علماء أفريقية» لأبى العرب التميمى، وترجم إلى الفرنسية ٢٤٤ - ٢٤٥، البيان المغرب ١/ ٢٨٠ سابعا: شعر فى مدح النبى صلّى الله عليه وسلم: المتحف البريطانى ١٦١٧، إضافات ٢٦٨٨٢ أ (الأوراق ٨٠ - ٨٢، القرن الثانى عشر الهجرى).
ثامنا: «الذب عن مذهب مالك»: تشستربيتى ٤٤٧٥ (١٥٣ ورقة، ٣٧١ هـ). (•)

<span data-type="title" id=toc-2131>٢٨ - ابن القصّار البغدادى</span>
هو أبو الحسن، على بن عمر بن أحمد المعروف بالقصّار البغدادى، كان تلميذا
_________
(•) ملاحظات:
(أ) يحذف كتاب «جملة مختصرة من واجب أمور الديانة» مما ذكره بروكلمان. فلبعض المخطوطات هذا العنوان لأنه أول باب فى الرسالة، انظر مثلا: طلعت بالقاهرة، فقه مالكى ٥٥ (١١٧ ورقة، ١١٨٣ هـ). ونحن نشك أيضا فيما ذكره بروكلمان من أنّ ابن أبى زيد هو مؤلف كتاب «مدخل إلى علم الدين والديانة» (انظر بروكلمان الأصل ١٨٨ رقم ٥ أ). فهذا المخطوط- فيما يبدو- هو نفس «الرسالة». وعلاوة على ذلك فإن الاسم الذى ذكره بروكلمان يصدق على الابن والأب، وأما الابنان فأحدهما يسمى أبو بكر أحمد والثانى أبو حفص عمر (انظر إدريس فى المصدر السابق ١٦٨). كما يجب حذف الشرحين اللذين ذكرهما بروكلمان (رقم ١٣، ١٤ فى الأصل).
(ب) نسب ابن خلدون فى المقدمة (انظر الترجمة الإنجليزية ١/ ٢٦١) إلى أبى زيد كتاب «أحكام المعلمين والمتعلمين»، وليس هذا- فيما يبدو- صحيحا، والحقيقة أن الكتاب الذى استخدمه يحمل نفس الاسم، ولكنه لأحمد بن محمد القابسى (انظر رقم ٣١ من الفقه المالكى فى هذا الكتاب)، وانظر أيضا إدريس فى المصدر السابق ١٥٠.
(ج) يحذف كتاب «عقيدة» عند بروكلمان، فالمخطوط المذكور به يضم شرح أحمد بن أحمد زرّوق على «رساله ابن أبى زيد».','3','173',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (929,'لأبى بكر الأبهرى (انظر رقم ٢٥)، ثم أصبح قاضيا ببغداد ويعدّ من كبار فقهاء المالكية وفيما عدا ذلك لا نعرف عنه شيئا. وتوفى ٣٩٨ هـ/ ١٠٠٨ م.

<span data-type="title" id=toc-2132><span data-type="title" id=toc-2135>أ- مصادر ترجمته:</span></span>
طبقات الفقهاء للشيرازى ١٤٢، الديباج لابن فرحون ١٩٩، تاريخ بغداد للخطيب ١٢/ ٤١ - ٤٢.

<span data-type="title" id=toc-2133>ب- آثاره:</span>
١ - «عيون الادلة فى مسائل الخلاف بين فقهاء الأمصار» عدّه الشيرازى مؤلف أفضل كتاب مالكى فى الخلافيات: القرويين بفاس ٤٩٧، (٨٧٢ هـ)، انظر بروكلمان ملحق ٢/ ٩٦٣ رقم ٤٩.
واختصره أبو محمد القاضى عبد الوهاب بن على بن نصر الثعلبى (المتوفى ٤٢٢ هـ/ ١٠٣١ م انظر بروكلمان ١/ ٣٨٣)، القرويين بفاس ٢٩١ (ج ٢، ٦١٢ هـ، انظر: شاخت Schacht ,Et.Or.I /٢٧٦ ولكنه لم يتعرف على الكتاب الأصلى.
٢ - «مقدمة فى أصول الفقه»، الأزهر ٢/ ٨٠، أصول الفقه ١٧٠ (الأوراق ١ - ٢٨، ٧٩٢ هـ).

<span data-type="title" id=toc-2134>٢٩ - ابن العطّار</span>
هو أبو عبد الله، محمد بن أحمد بن عبيد الله بن سعيد الأموى، المعروف بابن العطار، ولد بقرطبة سنة ٣٣٠ هـ/ ٩٤٢ م، ودرس على ابن عبد ربه، وأبى بكر بن القوطية وغيرهما. وإلى جانب علمه بالفقه كان متقنا للنحو والشعر والرياضيات.
وتوفى سنة ٣٩٩ هـ/ ١٠٠٨ م.
أ- مصادر ترجمته:
الصلة لابن بشكوال رقم ١٠٤٨، الديباج لابن فرحون ٢٦٩، معجم المؤلفين لكحالة ٨/ ٢٨٧، الوافى بالوفيات للصفدى ٢/ ٥٣، شجرة النور لمخلوف ١٠١.','3','174',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (930,'<span data-type="title" id=toc-2136><span data-type="title" id=toc-2139>ب- آثاره:</span></span>
«الوثائق المجموعة» (وعنوانه فى فهرست ابن خير ٢٥٢: «كتاب الوثائق والسّجلات»): القرويين بفاس ٤٧٠ (ج ٣، كما توجد نسخة أخرى فى هذه المكتبة، الرقم القديم ١١٠٩، وقد خلط بروكلمان، الملحق ٢/ ١٠٠ بينه وبين مؤلف شافعى متأخر، اسمه- أيضا- ابن العطار، وعن محتوى الكتاب انظر:
شاخت Schacht ,Et.Or.I ,٢٧٦ ;Turin (Nallino) ٣١.

<span data-type="title" id=toc-2137>٣٠ - ابن نصر الدّاودى</span>
هو أبو جعفر، أحمد بن نصر الداودى، كان فقيها ومحدثا، عاش فى طرابلس، وتوفى فى تلمسان سنة ٤٠٢ هـ/ ١٠١١ م.

<span data-type="title" id=toc-2138>أ- مصادر ترجمته:</span>
فهرست ابن خير ٨٧، ٤٤٠، شجرة النور لمخلوف ١١٠، الديباج لابن فرحون ٣٥، معجم المؤلفين لكحالة ٢/ ١٩٤.
ب- آثاره:
١ - «كتاب الأموال»: الإسكوريال ١١٦٥/ ١ (الأوراق ١ - ٥٥، ٦٧٧ هـ انظر: فهرست معهد المخطوطات العربية ١/ ٢٧٨، ٥٤٨).
ونشر حسن حسنى عبد الوهاب قسما منه يختص بأفريقية والأندلس وصقلية وترجمه إلى الفرنسية انظر» -٢ F.Dachraouiin: Et.Or.II ,١٩٦٢ ,٤٠١ - ٤٤٤.: الأسئلة والأجوبة» (فى الفقه)، الزيتونة بتونس ١٠٤٨٦ (١٢١ ورقة).
٣ - «النامى فى شرح الموطأ»، القرويين بفاس ١٧٥.','3','175',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (931,'<span data-type="title" id=toc-2140>٣١ - القابسى</span>
هو أبو الحسن على بن محمد بن خلف المعافرى القيروانى القابسى، كان فقيها ومحدثا. من المرجح أنه ولد ٣٢٤ هـ/ ٩٣٦ م بالقيروان (أو حضر إليها فى صباه مع والديه)، كان كفيفا، ورحل إلى المشرق سنة ٣٥٢ هـ/ ٩٣٦ م فأقام فى كل من مكة ومصر، وواصل فيهما تلقى العلم على يد عدد من العلماء ثم عاد إلى القيروان سنة ٣٥٧ هـ/ ٩٦٧ م، وتوفى بها سنة ٤٠٣ هـ/ ١٠١٢ م.

<span data-type="title" id=toc-2141>أ- مصادر ترجمته:</span>
المدارك للقاضى عياض (مخطوطة بالقاهرة) الأوراق ٢٨٣ - ٢٨٥، وفيات الأعيان لابن خلكان ١/ ٤٢٧، نكت الهميان للصفدى ١/ ٢١٧ - ٢١٨، الديباج لابن فرحون ١٩٩ - ٢٠١، معالم الإيمان لابن ناجى ٣/ ١٦٨ - ١٨٠، تذكرة الحفاظ للذهبى ١٠٧٩ - ١٠٨٠، الدول الذهبى ١/ ١٧٦، غاية النهاية لابن الجزرى ١/ ٥٦٧، البداية والنهاية لابن كثير ١١/ ٣٥١، النجوم الزاهرة لابن تغرى بردى ٤/ ٢٣٣ - ٢٣٤، شذرات الذهب لابن العماد ٣/ ١٦٨، التربية والتعليم فى الإسلام فى رأى القابسى لأحمد فؤاد الأهوانى القاهرة ١٩٥٥ م، الأعلام للزركلى ٥/ ١٣٥، معجم المؤلفين لكحالة ٧/ ١٩٤ - ١٩٥، بروكلمان ملحق ١/ ٢٧٧ رقم ١٦ أ، وانظر:
H. R. Idris, Deux Juristes Kairouanaisde Lepoqueziride, AIEO ١٢/ ١٩٥٤/ ١٧٣ - ١٩٨.

<span data-type="title" id=toc-2142>ب- آثاره:</span>
١ - «الملخص لما فى الموطأ من الحديث المسند» (انظر: مالك رقم ١ من كتب أخرى حول الموطأ) يضم ٢٥٠ حديثا.
٢ - «الرسالة المفصلة لأحوال المتعلمين وأحكام المعلمين والمتعلمين»، باريس ٤٥٩٥ (٩٧ ورقة، ٧٠٦ هـ، انظر، (Vajda ٥٢٣: ونشرها أحمد فؤاد الأهوانى بالقاهرة ١٩٥٥ م.

<span data-type="title" id=toc-2143>٣٢ - القنازعى</span>
هو أبو المطرف عبد الرحمن بن مروان بن عبد الرحمن الأنصارى القنازعى، أصله من قرطبة، ولد سنة ٣٤١ هـ/ ٩٣٢ م، وفى عام ٣٦٧ هـ/ ٩٧٧ م رحل إلى المشرق ثم','3','176',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (932,'عاد إلى وطنه بعد وقت طويل. كان عالما فى الفقه عارفا بالقراءات والحديث. وتوفى سنة ٤١٣ هـ/ ١٠٢٢ م بقرطبة.

<span data-type="title" id=toc-2144><span data-type="title" id=toc-2147>أ- مصادر ترجمته:</span></span>
الجذوة للحميدى ٢٦٠، المغرب فى حلى المغرب ١/ ١٦٦ - ١٦٧، غاية النهاية لابن الجزرى ١/ ٣٨، شذرات الذهب لابن العماد ٣/ ١٩٨، الصلة لابن بشكوال ١/ ٣٠٩ - ٣١١، البغية للضبى ٣٥٨ الديباج لابن فرحون ١٥٢، الأعلام للزركلى ٤/ ١١٢، معجم المؤلفين لكحالة ٥/ ١٩٤.

<span data-type="title" id=toc-2145><span data-type="title" id=toc-2148>ب- آثاره:</span></span>
«تفسير الموطأ» وتوجد منه قطعة فى القيروان انظر: مجلة معهد المخطوطات العربية ٢/ ٣٦٢.

<span data-type="title" id=toc-2146>٣٣ - ابن الحذّاء</span>
هو أبو عبد الله محمد بن يحيى بن أحمد التميمى القرطبى، المعروف بابن الحذّاء، ولد سنة ٣٣٦ هـ/ ٩٤٨ م. كان فقيها ومحدثا وأديبا. تولى أول الأمر القضاء فى أشبيلية ثم فى سرقسطة وتوفى سنة ٤١٦ هـ/ ١٠٢٥ م، وقيل ٤١٠ هـ.
أ- مصادر ترجمته:
تاريخ علماء الأندلس لابن الفرضى ٢/ ٨٨، فهرست ابن خير ٩٣، ٢٤٢، ٢٦٧، الإرشاد لياقوت (لندن) ٧/ ١٣٥ (القاهرة) ١٩/ ١٠٨ - ١٠٩، الديباج لابن فرحون ٢٧٢ - ٢٧٣، مرآة الجنان لليافعى ٣/ ٢٩، النجوم الزاهرة لابن تغرى بردى ٤/ ٢٦٤، شذرات الذهب لابن العماد ٣/ ٢٠٦، هدية العارفين ٢/ ٦٣، شجرة النور لمخلوف ١١٢، الأعلام للزركلى ٨/ ٥، معجم المؤلفين لكحالة ١٢/ ٩٩ - ١٠٠.
ب- آثاره:
«التعريف بمن ذكر فى موطأ مالك من الرجال والنساء»، القرويين بفاس ١١٨، ١٧٩ (٦٤٦ هـ)، ٩٩٣.','3','177',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (933,'<span data-type="title" id=toc-2149>٣٤ - البراذعي</span>
هو أبو سعيد، خلف بن أبى القاسم سليمان الأزدى القيروانى البراذعي، ولد بالقيروان، كان تلميذا لابن أبى زيد القيروانى، والقابسى (المتوفى ٤٠٣ هـ/ ١٠١٢ م). وأقام أولا فى صقلية ثم رحل بعد ذلك/ إلى أصفهان. اهتم فى كتبه بتهذيب الكتب الأساسية المبكرة فى فقه المالكية. ولا نعلم على وجه التأكيد سنة وفاته، ومن المحتمل أنه كان يعيش سنة ٤٣٠ هـ/ ١٠٣٩ م.

<span data-type="title" id=toc-2150>أ- مصادر ترجمته:</span>
التهذيب لابن عساكر ٥/ ١٧٠، سير النبلاء للذهبى ١١/ ١١٦، (نقلا عن معجم المؤلفين لكحالة ٤/ ١٠٦)، معالم الإيمان لابن ناجى ٣/ ١٨٤، الأعلام للزركلى ٢/ ٣٥٩ - ٣٦٠، وانظر بروكلمان ملحق ١/ ٣٦٢ رقم ٦.

<span data-type="title" id=toc-2151>ب- آثاره:</span>
«تهذيب فى اختصار المدوّنة» (انظر رقم ١٠، كتاب رقم ٣ من تكملات ومختصرات) (١٧٣).
_________
(١٧٣) أبو عبد الله محمد بن على بن تومرت الوارد عند بروكلمان تحت رقم ٧ (ملحق ١/ ٦٩٧) لا ينتمى إلى فقهاء هذه الفترة، وذلك لأنه توفى سنة ٥٢٤ هـ/ ١١٣٠ م.','3','178',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (934,'<span data-type="title" id=toc-2152>ج- فقه الشافعية</span>
<span data-type="title" id=toc-2153>١ - الشافعى</span>
هو أبو عبد الله، محمد بن إدريس بن العباس، الشافعى، أصله من قبيلة قريش ويمت بصلة القرابة إلى النبى صلّى الله عليه وسلم، وأمه من بنى أزد. ولد فى غزة (وقيل فى عسقلان أو منى أو اليمن) سنة ١٥٠ هـ/ ٧٦٧ م. ويقال إنه ولد فى اليوم الذى توفى فيه أبو حنيفة. جاء إلى مكة مع أمهعند ما كان فى الثانية من عمره، ونشأ بها فقيرا. وكان يتردد وهو غلام على البدو ليسمع ويتعلم العربية الخالصة، وأشعار العرب. ويقال إنه نجح فى إتقان اللغة والشعر أثناء إقامته فى مكة حتى أن العالم اللغوى الأصمعى سمع منه «ديوان الهذليّين» و «ديوان الشّنفرى». ودرس فى مكة على مسلم بن خالد الزنجى (المتوفى ١٧٩ هـ/ ٧٩٥ م)، وسفيان بن عيينة (المتوفى ١٩٥ هـ/ ٨١١ م) الفقه والحديث، كما حفظ «موطأ» مالك.
ولما بلغ العشرين من عمره رحل إلى مالك بالمدينة، وقرأ عليه الموطأ، الأمر الذى جعل مالك يتعجب لقراءته عليه من ذاكرته، فأبقاه عنده، وظل بالمدينة عند مالك حتى سنة وفاته ١٧٩ هـ/ ٧٩٥ م ثم رحل مع عمه أبى مصعب إلى اليمن، حيث اكتسب هناك شهرة كبيرة لسلوكه الطيب ومعارفه الواسعة، وبايع الإمام الزيدى يحيى ابن عبد الله (١٧٤) فقبض عليه مع غيره وأحضروه إلى الخليفة هارون الرشيد بالرقّة حيث بقى الرشيد بضع سنوات معتزلا بسبب الاضطرابات فى بغداد، ثم قدم الشافعى إليه وعفى عنه، وتعرف بمحمد بن الحسن الشيبانى المشهور الذى كانت له
_________
(١٧٤) انظر: فان أرندونك
C. van Arendonk, De Opkomstvanhet Zaidietische Imamaat ٦٠, ٢٩٠.','3','179',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (935,'مكانة كبيرة عند الخليفة، وسرعان ما وقع/ نزاع بين العالمين. ولما رأى الشافعى أن الوقت لم يحن بعد ليقف موقف المعارض لآراء الشيبانى ترك بغداد سنة ١٨٨ هـ/ ٨٠٤ م وذهب عن طريق حرّان والشام إلى مصر. وقد استقبل والى مصر الشافعى فى مصر باعتباره تلميذا لمالك آنذاك، ولكنه اضطرّ سنة ١٩٥ هـ/ ٨١٠ م أن يهاجر من مصر إلى بغداد، وفى بغداد اشتغل بالتدريس ونجح فى ذلك نجاحا كبيرا.
وفى سنة ١٩٨ هـ/ ٨١٤ م عاد الشافعى إلى مصر مرة أخرى بصحبة عبد الله بن موسى- ابن والى مصر الجديد- وكان يقدر الشافعى ويحترمه كثيرا. وقام الشافعى سنة ٢٠٠ هـ/ ٨١٥ م بالحج إلى مكة ثم عاد إلى مصر، وتوفى بالفسطاط سنة ٢٠٤/ ٨٢٠ ودفن فى مقبرة فى سفح جبل المقطم.
وعلى العكس من أبى حنيفة ومالك فقد عدّ الشافعى نفسه مؤسسا لمذهب جديد. وكان يقف جامعا بين مذاهب «الرأى» عند أبى حنيفة. ومذهب أهل الحديث عند مالك. ويعد الشافعى مؤسس علم «أصول الفقه» وأعطى «للرأى» مجاله فى حدود معينة بتمسكه بمبدأ «القياس».

<span data-type="title" id=toc-2154>أ- مصادر ترجمته:</span>
الجرح والتعديل لابن أبى حاتم ٣/ ٢/ ٢٠١ - ٢٠٤، الفهرست لابن النديم ٢٠٩ - ٢١٠، حلية الأولياء لأبى نعيم ٩/ ٦٣ - ١٦١، طبقات الشافعية للعبّادى ٦ - ٧، طبقات الفقهاء للشيرازى ٤٨ - ٥٠، تاريخ بغداد للخطيب ٢/ ٥٦ - ٧٣، الانتقاء لابن عبد البر ٦٥ - ١٢١، وفيات الأعيان لابن خلكان ١/ ٥٦٥ - ٥٦٨، الإرشاد لياقوت (لندن) ٦/ ٣٦٧ - ٣٩٨، (القاهرة) ١٧/ ٢٨١ - ٣٢٧، طبقات الحنابلة لابن أبى يعلى ١/ ٢٨٠ - ٢٨٤، اللباب لابن الأثير ٢/ ٥، تذكرة الحفاظ للذهبى ٣٦١ - ٣٦٣، الوافى بالوفيات للصفدى ٢/ ١٧١ - ١٨١، الديباج لابن فرحون ٢٢٧ - ٢٣٠، غاية النهاية لابن الجزرى ٢/ ٩٥ - ٩٨، التهذيب لابن حجر ٩/ ٢٥ - ٣١، طبقات الشافعية للسبكى ١/ ١٧٢ - ١٧٥، البداية والنهاية لابن كثير ١٠/ ٢٥١ - ٢٥٤، طبقات الشافعية لابن هداية الله ٢/ ٢، شذرات الذهب لابن العماد ٢/ ٩ - ١١، مرآة الجنان لليافعى ٢/ ١٣ - ٢٨، النجوم الزاهرة لابن تغرى بردى ٢/ ١٧٦ - ١٧٧، هنفنج','3','180',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (936,'فى دائرة المعارف الإسلامية (الطبعة الألمانية) ٤/ ٢٧١ - ٢٧٣، الأعلام للزركلى ٦/ ٢٤٩ - ٢٥٠، معجم المؤلفين لكحالة ٩/ ٣٢ - ٣٤، بروكلمان ١/ ١٧٨ رقم ١، (وتوجد معلومات عنه فى مقالات عديدة ظهرت فى المجلات المصرية)، وانظر كذلك: دى خويه فى مقاله فى مجلة المستشرقين الألمان.
De Goeje, Einigesuberden Imamas- S. ZDMG ٤٧/ ١٨٩٣/ ١٠٦ - ١١٧.
وانظر جولدتسيهر فى كتابه «مذهب ابن حزم الظاهرى Goldziher ,Zahiriten ٢٠ - ٢٦ «وانظر هفنج فى كتابه «الإسلام»:
Heffening, Islam, Frendenrecht, Hannaver ١٩٢٥, ١٤٥ - ١٤٦.
