INSERT INTO [b143160] VALUES (1257,'نسخة حديثة، انظر: عزة حسن ١/ ١٧٦)، فاتح ٦٣٨ (٧٢ ورقة، ٨٧٢ هـ)، ٣٤٨٨/ ٢ (من ١٨١ أ- ٢٧٩ أ، ٩٦٥ هـ)، صنعاء ٦٢، وطبع بالقاهرة ١٣٢٦، ١٣٢٩ هـ.
وعليه: «كتاب الشرح والبيان لما أشكل من كلام سهل» لأبى القاسم عبد الرحمن بن محمد الصّقلىّ (المتوفى حوالى سنة ٣٨٠ هـ/ ٩٩٠ م، وانظر معجم المؤلفين لكحالة ٥/ ١٨١).
كوبريلى ٧٢٧/ ٢ (من ١٥٣ أ- ٢٠٥ أ، القرن السابع الهجرى)، أسعد ١٦٢٣ (٤٢ ورقة، ١١٠٠ هـ).
٢ - «كتاب المعارضة والرد على أهل الفرق وأهل الدعاوى فى الأحوال»:
كوبريلى ٧٢٧/ ٣ (من ٢٠٥ أ- ٢٤٢ أ، القرن السابع الهجرى).
٣ - «كلمات سهل بن عبد الله التسترى»:
(من المرجح أنها جمعت فى القرن السابع الهجرى): فى، كوبريلى ٧٢٧/ ١ - (من ١ - ١٥٢ ب فى القرن السابع الهجرى)، وهى بعنوان: «كلمات الإمام الربانى سهل بن عبد الله التسترى»، مكتبة جامعة إستنبول ٤٠٨٩ (٣٤ ورقة، القرن الحادى عشر الهجرى)، أسعد ٣٥٢٧ (من ٢١٧ ب- ٢٤٨ ب)، حوالى ١١٠٠ هـ».
٤ - «رسالة فى الحكم والتصوف»:
أيا صوفية ٤١٢٨/ ٤ (من ورقة ١٤٨ - ١٦٨، القرن السابع الهجرى، انظر: فهرس معهد المخطوطات العربية ١/ ١٦١).
٥ - «رسالة فى الحروف»:
تشستربيتى ٣١٦٨/ ٣ (من ورقة ٨٣ - ٨٧، ٦٨٦ هـ).
٦ - «مناقب أهل الحق ومناقب أهل الله عزّ وجل»:
شرحها محمد الراثينى (فى القرن السابع الهجرى)، طلعت، تصوف ١٥٨١ (٣٦٧ ورقة، ٦٧٥ هـ).
٧ - «لطائف القصص فى قصص الأنبياء»،
طلعت، مجموع ٢٨٣.
٨ - «مقالة فى المنهيّات»:
طهران، كلية الحقوق ٢٥١ ج (من ١٢ أ- ١٩ أ، ١٢٧٩ هـ، انظر الفهرس ص ٤٨٧).','4','130',NULL,NULL,'0');
INSERT INTO [b143160] VALUES (1258,'<span data-type="title" id=toc-2780>٢١ - الجنيد</span>
هو أبو القاسم الجنيد بن محمد بن الجنيد الخزاز القواريرى، ولد على الأرجح حوالى سنة ٢١٥ هـ/ ٨٣٠ م فى بغداد وشب فيها، وأصل أسرته من نهاوند. كان ابن أخى وتلميذ أبى الحسن السّرىّ السّقطى (المتوفى سنة ٢٥٣ هـ/ ٨٦٧ م)، تتلمذ فى التصوف على الحارث المحاسبى، وأبى جعفر محمد بن على القصّاب (المتوفى سنة ٢٧٥ هـ/ ٨٨٨ م، انظر: تاريخ بغداد ٣/ ٦٢) وكذلك على أبى اليزيد (بايزيد) البسطامى. وقد درس الفقه على أبى ثور إبراهيم بن خالد تلميذ الإمام الشافعى.
ولم يكن الجنيد صوفيا فحسب، بل كان كذلك متكلما ولقب «بسيد الطائفة»، و «طاوس العلماء». وكان صوفيا يقول: بفضل صفاء النفس على الإغراق فى الصوفية، وكان متكلما يقول: بأن معرفة الله/ لا تأتى إلّا بطريق العقل (انظر إيوار فى دائرة المعارف الإسلامية، الطبعة الأوربية الأولى ١/ ١١١٠).
وكان الجنيد يكتب بأسلوب معقد مغرق فى التجريد، ذلك الأسلوب الذى صقله الحلاج بعد ذلك، ليصبح أسلوب العبارات الرنانة المغرقة فى الجدل كما يقول ماسينيون (الطواسين ١٥٧). وتوفى الجنيد سنة ٢٩٨ هـ/ ٩١٠ م فى بغداد، ودفن فى مقابر الشونيزية بجانب ضريح عمه.

<span data-type="title" id=toc-2781>أ- مصادر ترجمته:</span>
الفهرست لابن النديم ١٨٣، ١٨٥، طبقات الصوفية للسلمى ١٥٣ - ١٦٣ (ط القاهرة)، ١٤١ - ١٥٠ (ط ليدن)، حلية الأولياء لأبى نعيم ١٠/ ٢٥٥ - ٢٨٧، تاريخ بغداد للخطيب ٧/ ٢٤١ - ٢٤٩، وفيات الأعيان لابن خلكان ١/ ١٤٦، المنتظم لابن الجوزى ٦/ ١٠٥، طبقات الشافعية للسبكى ٢/ ٢٨ - ٣٧، البداية والنهاية لابن كثير ١١/ ١١٣، مرآة الجنان لليافعى ٢/ ٢٣١ - ٢٣٦، شذرات الذهب لابن العماد ٢/ ٢٢٨،
كتب هارتمان دراسة فى مجلة R.Hartmann ,in: Islam ٦ /٦٩ - ٧٠:
كتب ماسينيون L.Massignon ,Essai ٢٧٣.Recueil ٤٩.:','4','131',NULL,NULL,'0');
