INSERT INTO [b143162] VALUES (883,'رشر، فى، (ZDMG ٦٨ /١٩١٤ /٤٩: فاجنر، الموضع المذكور ٣٣٩)، المتحف العراقى ببغداد ٢١٦٢ (٣٤٦ ورقة، نسخ فى ١٢٦٦ هـ، انظر: كوركيس عواد، فى: سومر ١٤/ ١٩٥٨/ ١٢، رقم ٦١)، دار الكتب بالقاهرة، أدب ٢٥ م (نسخة حديثة، انظر الفهرس، طبعة ثانية ٣/ ١١٦، فاجنر، الموضع المذكور ٣٣٤)، باريس ٤٨٢٩ (٤٩٠ ورقة، من القرن ١٣ الهجرى، منقول عن مخطوط فاتح ٣٧٧٣، انظر فايدا ٣٠٢، فاجنر، الموضع المذكور، ص ٣٣٠)، باريس ٤٨٣٠ (٥٣٤ ورقة، من القرن ١٣ الهجرى، منقول عن مخطوط فاتح ٣٧٧٤، انظر: قايدا ٣٠٢، فاجنر، الموضع المذكور ص ٣٣٠)، باريس ٤٨٣١ (٣١٠ ورقة، من القرن ١٣ الهجرى، منقول عن مخطوط فاتح ٣٧٧٥، انظر: فايدا ٣٠٢، فاجنر، الموضع المذكور، ص ٣٣٨)، برلين ٧٥٣٢ (٢٧٥ ورقة، نسخ نحو ١٢٠٠ هـ، انظر: فاجنر، الموضع المذكور، ص ٣٣٣)، المكتب الهندى بلندن ٣٨٦٧ (٢٣٤ ورقة، نسخ فى ٥٩٤ هـ، انظر: فاجنر، الموضع المذكور، ص ٣٣٥ - ٣٣٧)، المتحف البريطانى، الإضافات ٢٤٩٤٨ (١٤٠ ورقة، من القرن السابع الهجرى، انظر: فاجنر، الموضع المذكور ٣٣٨ - ٣٣٩)، ييل ٤٩٥) L -١٠ ورقة، نسخ فى ١٢٩٢ هـ، انظر: نموى، رقم ٢٦٢، فاجنر، الموضع المذكور، ص ٣٣٩)، ييل ١٨٩) I -٧٠٧ ورقة، نسخ فى ١٠٥٩ هـ، انظر: نموى رقم ٢٦١، فاجنر، الموضع المذكور، ص ٣٣٩ - ٣٤٠)، المتحف الأسيوى بلنينجراد ٢٦٣ (الورقة ١ - ٨٥، لعلها نسخة حديثة، انظر: روزن ٢١٠ - ٢١٣، فاجنر، الموضع المذكور، ص ٣٤٠)، الفاتيكان ٤٥٦ (٢٦٩ ورقة، من القرن العاشر الهجرى، انظر: قايدا ١/ ٤٨، فاجنر، الموضع المذكور، ص ٣٤٠).
النشرات:
تعتمد الطبعات القاهرية القديمة على مخطوطة دار الكتب بالقاهرة، أدب ٢٥ م (انظر: فاجنر، الموضع المذكور، ص ٣٣٤)، ونشره إيفالد فاجنر محققا، على كل المخطوطات المهمة، مع إدخال تعاليق وشروح مختلفة فى النص، بعنوان «ديوان أبى نواس»، الجزء الأول (خمس الديوان)، فيسبادن والقاهرة ١٩٥٨، والجزء الثانى، فيسبادن وبيروت ١٩٧٢، وانظر فى عرضه ونقده:
E. Wagner, Abu Nuwas, Eine Studie ... , S. ٤٧٢,
وانظر أيضا: أحمد الجندى، فى: مجلة المجمع العلمى العربى بدمشق ٣٧/ ١٩٦٢/ ٦٨٥ - ٦٨٦، وراجع فى العمل الذى تقدّم نشر الديوان:
E. Wagner, Die Uberlieferungdes Abu Nuwas- Diwanundseine Handschriften, Mainz ١٩٥٧.
٥ - أبو اسحاق إبراهيم بن أحمد بن محمد الطبرى النحوى توزون (تورون، فيزون، بيروز) (المتوفى سنة ٣٥٥/ ٩٦٦، انظر: إنباه الرواة، للقفطى ١/ ١٥٨ - ١٥٩)، ولا يبدو أن واحدة من المخطوطات التى وصلت إلينا تثبت أنه مؤلفها، إلا أن تصنيف توزون كان من أشهر التصانيف، وكان كثير التداول فى عهد ياقوت (إرشاد الأريب ١/ ٣٦، وانظر أيضا: ابن خلكان ١/ ١٦٩)، حتى إن ف. روزن قدّر أنه مؤلف هذا الشرح، وحديثا ذهب أيضا إ. فاجنر (انظر (٣٢٦ - ٣٢٣ Uberlieferung إلى هذا الرأى،','4','117',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (884,'مستندا إلى حجج ذات بال، والتصنيف «يقع فى عشرة حدود، غير أن ترتيبه يجرى على ترتيب حمزة من الفصل الثالث إلى الثانى عشر ... وفضلا عن ذلك، تتفق أغلب الروايات مع حمزة، مخالفة للصولى» (فاجنر، الموضع المذكور ٣٢٣، ٣٢٤)، على أنه، من ناحية أخرى، ينقل فيه مرارا عن الصولى نقلا مباشرا، وهذا التصنيف يزيد على ما عمله الصولى بمقدار الثلث. تقريبا، إذ إن اختيار الأشعار فيه ليس متفحّصا كاختيار الصولى.
/ المخطوطات: بايزيد ٥٧٦٧ (١٨٨ ورقة، من القرن ١١ الهجرى، انظر فاجنر، الموضع المذكور، ص ٣٥٦)، خزانة عبد الحميد نافع بالقاهرة (انظر: المرجع نفسه ٣٥٧ - ٣٥٨)، قبة الغورى بالقاهرة ٢٢٩ (٣٠٣ ورقة، منقول عن طبعة الحجر بالقاهرة ١٢٧٧ هـ؟ ، انظر المرجع نفسه ٣٥٨)، المتحف البريطانى ١٩٤٠٤ إضافات (٣٤٠ ورقة، نسخ فى ١٠٠٢ هـ، انظر المرجع نفسه ٣٥٦ - ٣٥٧)، الإسكوريال ٣١١ (٣٧١ ورقة، نسخ فى ١٠٠٢ هـ، انظر المرجع نفسه ٣٥٧)، المتحف الأسيوى بليننجراد ٢٦٣ (الورقة ٨٥ - ٣١٢، انظر المرجع نفسه ٣٥٨، وراجع ٣٤٠).
ومخطوط عبد الحميد نافع، الذى يشتمل على الديوان كاملا فى هذا التصنيف، كان هو الأصل لطبعة الحجر المنقّحة فى القاهرة ١٢٧٧ هـ (انظر المرجع نفسه ٣٢٥، ٣٥٧ - ٣٥٨، وراجع: فون كريمر، فى: .
