INSERT INTO [b143162] VALUES (917,'المقدمة، ص ٦)، الظاهرية، عام ٣٣٢٣ (الورقة ٨٩ - ٩٧، من القرن الخامس الهجرى، انظر: فهرس عزة حسن ٢/ ٢٢١، راجع: مجلة المجمع العلمى العربى بدمشق/ ١٨/ ١٩٤٣/ ١٨٣.
B. Lewin, Diwan III
المقدمة ص ٣)، دار الكتب بالقاهرة، مجموع ٤٨٠ (نسخ فى ١٣٢٤ هـ، انظر: الفهرس، طبعة ثانية ٣/ ٧٦)، مجموع ١٦٦ م (انظر: الفهرس، طبعة ثانية ٣/ ٢٨٥)، وتوجد قصيدة فى رثاء المعتضد، برلين ٧٥٤٣/ ٢ (الورقة ٨٦ أ)، وقصائد مفردة، برلين ٨٥٤٣/ ١، ٣، برلين ٨٢١٨ (الورقة ٥٢ أ- ب)، برلين ٨٤٦٥ (الورقة ١٢٤ ب)، جوتا ٢٦ (الورقة ٢٠ ب، ٤٧ أ- ب، ١٠٦ ب، ١٣١ أ)، وثمة قصائد فى منتخبات شعرية كثيرة، وبضعة أبيات فى شرح ابن رشد لكتاب «الشعر» لأرسطو، وفى ترجمته اللاتينية: W.F.Boggessin: JAOS ٨٨ /١٩٦٨ /٦٥٧ - ٦٧٠ انظر Hermannus Alemannus، فى مواضع مختلفة.
طبع الديوان فى: القاهرة ١٨٩١ (فى جزءين)، ونشره محيى الدين الخياط، فى بيروت ١٣٣١، ونشره، B Lewin الجزء الرابع فى إستنبول ١٩٤٥، الجزء الثالث فى إستنبول ١٩٥٠، انظر: تعليق بلاشير، فى:
Oriens ١/ ١٩٤٨/ ١٠٥ - ١٠٦
ونشره رشيد الصفار، فى ثلاث مجلدات، بالقاهرة ١٩٥٨ - ١٩٥٩، ونشر أيضا فى بيروت، دار صادر ١٩٦١، ونشره يونس أحمد السامرائى، فى بغداد ١٩٧٨.
ونشر لنج» C.Lang أرجوزة فى تاريخ المعتضد»، مع ترجمة إلى الألمانية، وشرح فى:
ZDMG ٤٠/ ١٨٨٦/ ٥٦٣ - ٦١١ - ٤١/ ١٨٨٧/ ٢٣٢ - ٢٧٩
وطبعت فى القاهرة ١٣٢٩ (انظر: سركيس ٢٤٣)، وشرها محمد عبد المنعم خفاجى، فى «رسائل ابن المعتز»، القاهرة ١٩٤٦، ص ٧٩ - ١٠٧، كما نشر «أرجوزة فى ذم الصبوح»، (انظر: قطب السرور، للرقيق ٣٣٩ - ٣٤٧) فى الكتاب نفسه، ص ١٠٧ - ١١٤.
دراسات فى شعره: محمد عبد المنعم خفاجى، التشبيه فى شعر ابن المعتز وابن الرومى، القاهرة. Schoeler.Naturdichtung ٢٣٥ - ٢٧٢ ١٩٤٩
٢ - «كتاب فى سرقات الشعراء»، سبق ذكره ص ٦٤.
٣ - «رسالة فى محاسن شعر أبى تمام ومساويه» (انظر: الموشح، للمرزبانى ٣٠٧) وانظر فى سائر مؤلفاته باب «كتب الأدب»، وسبق ذكره ص ١٠٠، ١٠٥، ٤٣٩.','4','151',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (918,'<span data-type="title" id=toc-1565>ابن سكّرة الهاشمى</span>
هو أبو الحسن محمد بن عبد الله بن محمد البغدادى، ويلقّب أيضا بابن رائطة (أو رابطة)، كان من ولد على، أحد أبناء الخليفة المهدى. نظم، فضلا عن قصائده فى المديح، شعرا فى المجون والسخف خاصة، كمعاصره الأشهر ابن الحجاج، الذى يكثر ذكره معه، توفى سنة ٣٨٥/ ٩٩٥.

<span data-type="title" id=toc-1566>أ- مصادر ترجمته:</span>
يتيمة الدهر ٣/ ٣ - ٣٠، تاريخ بغداد ٥/ ٤٦٥ - ٤٦٦، وفيات الأعيان، لابن خلكان ١/ ٦٦٦ - ٦٦٨، الوافى بالوفيات، للصفدى ٣/ ٣٠٨ - ٣١٢.
Me Z, Renaissance ٥٢٧; Ritter, Geheimnisse ٣٧٠, Anm.
الأعلام، للزركلى ٧/ ٩٩، معجم المؤلفين، لكحالة ١٠/ ٢٤٧، مراجع تراجم الأدباء العرب، للوهابى ١/ ٩٥ - ٩٦، وانظر: بروكلمان، فى الملحق I ,١٣١

<span data-type="title" id=toc-1567>ب- آثاره:</span>
ديوانه (انظر الوافى بالوفيات للصفدى ٣/ ٣٠٩)، الذى قيل: إنه ضم خمسين ألف بيت فى أربع مجلدات، وكانت عشره منها فى مغنية سوداء اسمها خمرة (انظر: اليتيمة ٣/ ٣)، رواه أبو العلاء صاعد بن الحسن الرّبعى (المتوفى نحو سنة ٤١٨/ ١٠٢٦، انظر: بروكلمان، فى الملحق، I ,٢٥٤ يأتى ذكره ص ٦٩٦)، وجلبه إلى الأندلس (انظر: فهرست ابن خير ٤٠٦)، وثمة قطع/ من شعره فى: يتيمة الدهر (نحو ٤٠٠ بيت)، المنتخب الميكالى، حماسة ابن الشجرى، نهاية الأرب، للنويرى، الدر الفريد.

<span data-type="title" id=toc-1568>آدم بن عبد العزيز</span>
حفيد الخليفة عمر بن عبد العزيز، أقام بالعراق منذ صدر الدولة العباسية، ونظم أشعارا فى الخمر، وشكّ فى كونه زنديقا، مات فى العقد السابع من القرن الثانى الهجرى.','4','152',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (919,'<span data-type="title" id=toc-1569><span data-type="title" id=toc-1572>أ- مصادر ترجمته:</span></span>
الأغانى ١٥/ ٢٨٦ - ٢٩١، تاريخ بغداد ٧/ ٢٥ - ٢٧، تهذيب ابن عساكر ٢/ ٣٦١ - ٣٧٤، الوافى بالوفيات، للصفدى ٥/ ٢٩٤ - ٢٩٧.

<span data-type="title" id=toc-1570><span data-type="title" id=toc-1573>ب- آثاره:</span></span>
ذكر ابن الجراح، فى كتاب الورقة (انظر: الفهرست، لابن النديم، طهران، ص ١٨٤)، أن شعره كان عشرين ورقة، وفى المصادر الآنفة الذكر قطع منشعره، وانظر أيضا: البيان والتبيين، للجاحظ ٣/ ٢٠١، الأشباه والنظائر، للخالديين ٢/ ٢١٧.

<span data-type="title" id=toc-1571>أبو حفص الشطرنجى</span>
هو عمر بن عبد العزيز، وكان شاعرا عند عليّة بنت المهدى، (توفيت سنة، ٢١٠/ ٨٢٥)، وفى القصر ببغداد، كان أديبا ظريفا، ولا عبا بالشطرنج معروفا.
أ- مصادر ترجمته:
الأغانى ٢٢/ ٤٣ - ٥٠، سمط اللآلى ٥١٧، قطب السرور، للرقيق ٣١٦، الأعلام، للزركلى ٥/ ٢٠٩.
ب- آثاره:
ألّف «كتاب العطر» (انظر: ابن النديم ١٧١)، وقيل: إن ديوانه كان خمسين ورقة (ابن النديم ١٦٤)، وتوجد قطع من شعره فى: الزهرة، لابن داود، زهر الآداب، للحصرى، محاضرات الراغب، نهاية الأرب، للنويرى ٢/ ٢٤٣، الدر الفريد ١/ ١/ ص ١٧٩، ٢/ الورقة ٥٤ أ، ٢٢٠ أ.

<span data-type="title" id=toc-1574>على بن جبلة العكوك</span>
هو على بن جبلة بن مسلم الأبناوى، أبو الحسن، زعموا أنه من أبناء الموالى، من','4','153',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (920,'الشيعة الخراسانية، ولد سنة ١٦٠/ ٧٧٦ ببغداد، وقيل: إن الأصمعى هو الذى لقبه بالعكوك (أى السمين)، أصيب بالعمى منذ حداثته/، وعرف خاصّة بأنه شاعر مدح، كانت وفاته سنة ٢١٣/ ٨٢٨.
وفضلا عن مدائحه ومراثيه، وصلت إلينا أبيات فى الهجاء، وأشعار فى الخمر له، وقد نسبت إليه أيضا، دون حق فيما يبدو، «القصيدة اليتيمة» المشهورة (يأتى ذكرها)، وحظى شعره بتقدير عال عند بعض معاصريه، وعند الشعراء واللغويين الأصغر منه سنا.

<span data-type="title" id=toc-1575>أ- مصادر ترجمته:</span>
الشعر والشعراء، لابن قتيبة ٥٥٠ - ٥٥٣، كتاب بغداد، لابن أبى طاهر طيفور ١٣٦ - ١٣٧، ١٥٨ - ١٥٩، طبقات الشعراء، لابن المعتز، طبعة أولى ٧٦ - ٨٢، طبعة ثانية ١٧١ - ١٨٥، تاريخ الطبرى ٣/ ١١٥٣ - ١١٥٤، أخبار أبى تمام، للصولى ٢٠ - ٢١، ١١٤، الورقة، لابن الجراح ١٠٦ - ١٠٩، الأغانى ٢٠/ ١٤ - ٤٢، تاريخ بغداد ١١/ ٣٥٩، سمط اللآلى، للبكرى ٣٣٠، وفيات الأعيان، لابن خلكان ١/ ٤٣٨ - ٤٤١، مسالك الأبصار، لابن فضل الله ١٣/ الورقة ١٧٩ ب- ١٨١ ب، نكت الهميان، للصفدى ٢٠٩ - ٢١٠
Rescher, Abriss II, ٢٩ - ٣٣; Br. I, ٧٨, SI, ١٢٠; Ritter, Geheimnisse ٣٤, Anm; R. Blacherein: EI I, ٣١٥/ ٣١٦;
عصر المأمون، لفريد رفاعى ٢/ ٤٣١ - ٤٣٩، أحمد نصيف الجنابى، «شعر على بن جبلة»، المقدمة ص ١١ - ٨٢، الأعلام، للزركلى ٥/ ٧٥ - ٧٦.

<span data-type="title" id=toc-1576>ب- آثاره:</span>
ديوانه الذى ذكر ابن النديم (ص ١٦٥) أنه كان ١٥٠ ورقة، واطلع عليه أيضا عبد القادر البغدادى (خزانة الأدب ١/ ١٠)، يبدو أنه ضاع، وكان من بين مصادر أبى الفرج الأصفهانى كتب لمحمد بن القاسم بن مهرويه (الشطر الثانى من القرن الثالث/ التاسع)، ولأحمد بن أبى طاهر طيفور (المتوفى سنة ٢٨٠/ ٨٩٣).','4','154',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (921,'وما وصلنا من شعره قد جمعه ونشره أحمد نصيف الجنابى، النجف ١٩٧١، وحسين عطوان، «شعر على ابن جبلة»، القاهرة ١٩٧٢، وانظر فيه: محمد ى. زين الدين، فى: مجلة المجمع العلمى العربى بدمشق ٤٩/ ١٩٧٤/ ٤٣٦ - ٤٤٣، وانظر أيضا: حماسة الظرفاء، الورقة ١١٩، ١٣٦ أ، الحماسة المغربية، الورقة ١٥ ب، ٨٨ أ- ب، سفينة الأدباء، الورقة ٣٣ أ- ٣٤ أ، الدر الفريد، الجزء الثانى، فى ٧ مواضع.

<span data-type="title" id=toc-1577>القصيدة اليتيمة:</span>
هذه القصيدة الدالية المشهورة، ومع ذلك يقل ورودها فى كتب الأدب المتقدمة، نظمها شاعر يرجّح أن أصله من تهامة، وهو يتغنى فيها بمحبوبتهدعد، ويصف فيها جمال جسدها، وأضيفت القصيدة إلى شعراء كثيرين (١٧ على ما يزعم)، لم يمكن إلى الآن تحقيق نسبتها إلى واحد منهم. وترجع أقدم وجوه نسبتها إلى رواتها، وعزاها ثعلب (المتوفى ٢٩١/ ٩٠٤) إلى الشاعر دوقلة المنبجى، الذى لا نعرف عنه شيئا غير ذلك، وذكر الحسن بن وهب المنبجى فى مخطوط برلين ٧٥٣٥/ ٥ أفضت إلى تشخيصه/ بالكاتب الشاعر الحسن بن وهب الحارثى (انظر: ف. آلورد، الفهرس ٦/ ٥٥٤)، وليس ذلك بمقنع، إذ إن القصيدة كان قد رواها أبو عبيدة (المتوفى نحو سنة ٢١٠/ ٨٢٥)، والأصمعى (المتوفى نحو سنة ٢١٦/ ٨٣١)، (انظر: صلاح الدين المنجد، «القصيدة اليتيمة»، المقدمة ص ١٠)، وينطبق ذلك أيضا على نسبتها إلى على بن جبلة العكوك، وأبى الشيص، وهما مع «المنبجى» أكثر الشعراء الذين ينسب إليهم نظمها، والأرجح أيضا ألّا تكون من نظم ذى الرمة، الذى جاء ذكره فى رواية محمد بن حبيب (المتوفى سنة ٢٤٥/ ٨٥٩)، (انظر: المنجد، فى الموضع المذكور، ص ١٠، ١٤ - ١٥).
ولخّص أبو القاسم على بن المحسّن بن على التنوخى (المتوفى سنة ٤٤٧/ ١٠٥٥، انظر: معجم المؤلفين، لكحالة ٧/ ١٧٥) روايات اللغويين الخمسة المشار إليهم، وبلغتنا القصيدة فى روايته هذه.','4','155',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (922,'المخطوطات: دار الكتب بالقاهرة، أدب ١٨٦٤ (ضمن مجموع، نسخ فى ٥٨٣ هـ)، مجموع ١٤١ و ١٩٤ (انظر: الفهرس، طبعة ثانية ٣/ ٤٣٧ - ٤٣٨)، أدب ٥٠٩ و ١١٩٤ و ١٨٤٢ (فى ذيل «شرح الهاشميات» لأبى رياش، انظر: الفهرس، طبعة ثانية ٣/ ٢٢٧ - ٢٢٨)، أدب ٦٨ م (فى ذيل «العقد الفريد»، انظر: الفهرس، طبعة ثانية ٣/ ٢٥٣)، ١٦٢٧٦ ز (فى ذيل «غيث الأدب فى شرح لاميتى العجم والعرب»، انظر: شعر على بن جبلة، النجف ١٩٧١، ص ١٠٨)، الظاهرية، عام ٥٨٢٩ (الورقة ٧١ أ- ٧٢ ب، نسخ فى ١٣٣٠ هـ، انظر: فهرس عزة حسن ح ٢/ ٣٣٨ - ٣٣٩)، الظاهرية ٧٩ (الورقة ١٥٦ أ- ١٥٨ ب ضمن مجموع، من القرن السابع الهجرى، انظر: المنجّد، فى الموضع المذكور، ١٧ - ١٨)، وثمة مخطوط فى رامبور (انظر المنجّد، فى الموضع المذكور، ص ١٩ - ٢٠)، برلين ٧٥٣٥/ ٥، المتحف البريطانى، مخطوطات شرقية ٣٧٥٢/ ٧ (الورقة ١٢٣ - ١٢٨، مع تعليقات، من القرن العاشر الهجرى، انظر: الملحق، رقم ١٢١١)، وتوجد أيضا فى: جمهرة الإسلام، للشيزرى، الورقة ٢٧ ب- ٢٩ أ (راجع:
مجلة المجمع العلمى العربى بدمشق ٣٣/ ١٩٥٨/ ٦)، وثمة تخميس لمجهول فى: برلين ٧٥٣٥/ ٤ و ٨٢٦١/ ١٤ الورقة ٣٩ أ- ٤٣ ب).
نشرها جورجى زيدان، اعتمادا على مخطوط حديث من مخطوطات القاهرة (٦٠ بيتا) فى: الهلال ١٤/ ١٩٠٥/ ١٧٤، وما بعدها (انظر: المنجد، فى الموضع المذكور، ص ٥)، ونشرها استنادا إلى مخطوط رامبور (٦٣ بيتا) عبد العزيز الميمنى، فى: الزهراء ٣/ ١٩٢٦/ ٢٢٤ وما بعدها (انظر: المنجد، فى الموضع المذكور، ص ٦ - ٧)، ونشرها عبد الله الجبورى على مخطوط فى خزانته الخاصة، فى: «أشعار أبى الشيص الخزاعى»، بغداد ١٩٦٧، ونشرها، على أحد مخطوطات القاهرة ومخطوطى الظاهرية، صلاح الدين المنجد، فى بيروت ١٩٧٠، انظر: تعليق أحمد الجندى عليه، فى: مجلة المجمع العلمى العربى بدمشق ٤٦/ ١٩٧١/ ٤١٠ - ٤١١، وطبعت أيضا فى «شعر على بن جبلة»، النجف ١٩٧١ والقاهرة ١٩٧٢.

<span data-type="title" id=toc-1578>محمود الوراق</span>
هو محمود بن (ال) حسن الوراق النّخّاس، عاش فى بغداد، وكانت وفاته نحو سنة ٢٣٠/ ٨٤٥، أو قبلها، وباعتباره شاعر أمثال وحكم ومواعظ، فقد قيل: إنه لم يقصر بهذا عن صالح بن عبد القدوس، وسابق البربرى./

<span data-type="title" id=toc-1579>أ- مصادر ترجمته:</span>
أخبار أبى تمام، للصولى ١٤٧، طبقات الشعراء لابن المعتز، طبعة أولى ١٧٤ - ١٧٥، طبعة ثانية','4','156',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (923,'٣٦٧ - ٣٦٨، الكامل، للمبرد، انظر الفهرس، الأغانى ١٤/ ١٩٧ - ١٩٨، زهر الآداب، للحصرى ٩٧ - ٩٩، سمط اللآلى، للبكرى ٣٢٨، تاريخ بغداد ١٣/ ٨٧ - ٨٩، فوات الوفيات، للكتبى ٢/ ٥٦٢ - ٥٦٤، نهاية الأرب، للنويرى ٣/ ٨٨، الأعلام، للزركلى ٨/ ٤٢ - ٤٣.

<span data-type="title" id=toc-1580><span data-type="title" id=toc-1583>ب- آثاره:</span></span>
نشر عدنان راغب العبيدى مجموعة من شعره، جمعها من كتب الأدب، بعنوان: «ديوان محمود بن حسن الوراق»، بغداد ١٩٦٩، وانظر أيضا: المنتخب الميكالى، الورقة ٧٧ أ، ١٠٩ أ، حماسة الظرفاء، الورقة ٤٢ ب، ٥٦ ب، ٦١ أ، ٧٣ ب، ١٣٩ أ، الدر الفريد، فى ١٩ موضعا.

<span data-type="title" id=toc-1581>أبو سعد المخزومى</span>
هو عيسى بن خالد بن الوليد، من ولد الصحابى الحارث بن هشام المخزومى، أقام فى بغداد، ومدح المأمون (١٩٨/ ٨١٣ - ٢١٨/ ٨٣٣)، وهاجى دعبل بن على، قيل: إنه توفى بالرّىّ.

<span data-type="title" id=toc-1582>أ- مصادر ترجمته:</span>
طبقات الشعراء، لابن المعتز طبعة أولى ١٣٩ - ١٤١، طبعة ثانية ٢٩٥ - ٢٩٨، معجم الشعراء، للمرزبانى ٢٦٠، الموشح للمرزبانى ٣٤٧، الأغانى ٢٠/ ١٧٠ - ١٨٥، فى مواضع مختلفة، سمط اللآلى، للبكرى ٥٧٨، نهاية الأرب، للنويرى ٣/ ٩١، الأعلام، للزركلى ٥/ ٢٨٦.
ب- آثاره:
قيل: إن ديوانه كان ١٥٠ ورقة (الفهرست، لابن النديم، طهران، ص ١٨٩)، جمع ر. فرج رزّوق قطعا من شعره، ونشرها، فى بغداد ١٩٧١، انظر فضلا عن ذلك: الحماسة المغربية، الورقة ٤٨ أ- ب (١٩ بيتا)، الدر الفريد ٢/ الورقة ٨٧ ب، ١٩٦ أ، ٢٦٥ أ Schawahid -Indeces ٣٢٥.،','4','157',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (924,'<span data-type="title" id=toc-1584>ابن الطبيب الطنبورى</span>
هو إسحاق بن خلف، شيعى، وزعموا أنه مانوىّ، كان شاعرا، ونديما، وموسيقيا لدى المعتصم، توفى نحو سنة ٢٣٠/ ٨٤٥.

<span data-type="title" id=toc-1585>مصادر ترجمته:</span>
طبقات الشعراء، لابن المعتز، طبعة أولى ١٣٨، طبعة ثانية ٢٩٢ - ٢٩٣، الفهرست، لابن النديم ٣٣٨، فوات الوفيات، للكتبى ١/ ١٦ - ١٧، الوافى بالوفيات، للصفدى ٨/ ٤١١ - ٤١٢.
G. Vajda, Leszindiqs .. in: RSO ١٧/ ١٩٣٨/ ١٨١;
شعراء بغداد للخاقانى ٢/ ١١٢ - ١١٤، الأعلام، للزركلى ١/ ٢٨٦.

<span data-type="title" id=toc-1586>ب- آثاره:</span>
كانت أشعاره مجموعة فى ديوان (انظر: فوات الوفيات، للكتبى ١/ ١٧)./

<span data-type="title" id=toc-1587>محمد بن عبد الملك الزيات</span>
هو أبو جعفر محمد بن عبد الملك بن أبان الزيات، ولد ببغداد فى سنة ١٧٣/ ٧٨٩، بدأ حياته السياسية كاتبا، وبعد انتصار المأمون على خصمه فى الخلافة إبراهيم بن المهدى، حمل ابن الزيات هذا الأخير، بقصيدة عملها، على أن يردّ أموالا إلى التجار كان قد اقترضها منهم، (وكذلك أيضا من أبيه)، (انظر: أشعار أولاد الخلفاء، للصولى ٢٧ - ٣٠، كتاب بغداد، لابن أبى طاهر طيفور ١٠٨ - ١١٠، الأغانى، طبعة ثانية ١٠/ ٤٧ - ٤٩)، وبعد عام ٢٢١/ ٨٣٦ استوزره المعتصم، وظل فى منصبه هذا إلى حين وفاته سنة ٢٣٣/ ٨٤٧.
ولقد لقيت معارفه وقدراته الأدبية والشعرية تقديرا عاليا جدا من معاصرين له كثيرين، ومن بينهم أبو تمام والبحترى (انظر: الحيوان، للجاحظ ١/ ٦٧ - ٦٨، تاريخ بغداد ٢/ ٣٤٢ - ٣٤٣)، ومما يؤيد ما كان له من مكانة فى الحياة العلمية والأدبية فى','4','158',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (925,'عصره، أن حنين بن إسحاق، ترجم له مقال جالينوس» Devoce كتاب فى الصوت» (انظر: تاريخ التراث العربى، (III ,١٠٣ وأن الجاحظ قدّم له كتاب الحيوان (انظر:
الفهرست، لابن النديم، طهران، ص ٢١٠)، وأن «رسالة الجاحظ فى الغضب والرضى» كانت كذلك موجّهة إليه (انظر: المرجع نفسه، الترجمة الإنجليزية ٤٠٨، وراجع ٤٠٠).
وعدّ إلى جانب إبراهيم بن العباس الصولى أفضل الشعراء الكتاب فى زمانه، ولم يعرف أبو الفرج (الأغانى، طبعة ثانية ٢٠/ ٤٦) للصولى سوى أشعار قليلة ومقطعات، وعلى العكس منه كان للزيات قصائد طوال أيضا، قيل: إنها كانت كلها جيدة.

<span data-type="title" id=toc-1588>أ- مصادر ترجمته:</span>
البيان والتبيين، للجاحظ ٢/ ٢٥٥، الحيوان، للجاحظ ٢/ ١٢٩ - ١٣٠ أيضا، عيون الأخبار، لابن قتيبة، انظر الفهرس، طبقات الشعراء، لابن المعتز، ط. أولى ١٨٤ - ١٨٥، ط. ثانية ٣٨٩ - ٣٩٠، تاريخ الطبرى، انظر الفهرس، أخبار الشعراء، للصولى ٢٠٦، ٢٠٧، ٢١٧، أخبار أبى تمام، للصولى، انظر الفهرس، العقد الفريد، انظر الفهرس، معجم الشعراء، للمرزبانى ٤٢٥، إعتاب الكتاب، لابن الأبّار ١٣٣ - ١٣٨، وانظر الفهرس، وفيات الأعيان، لابن خلكان ٢/ ٧٠ - ٧٤، الوافى بالوفيات، للصفدى ٤/ ٣٢ - ٣٤.
Sourdel, Vizirat.
انظر الفهرس، وهى نفسها فى:
EI ٢ III, ٩٧٤ - ٩٧٥
عصر المأمون، لفريد رفاعى ٣/ ٢٧٨ - ٢٨٢، أمراء البيان، لكرد على ٢٧٨ - ٣٠٦، الأعلام للزركلى ٧/ ١٢٦ - ١٢٧، معجم المؤلفين، لكحالة ١٠/ ٢٥٤، بروكلمان، الملحق. l ,١٢١،
ترك الزيات ديوانا، وقف عليه ابن النديم (ص ١٦٦)، وكان ٥٠ ورقة، ومجموعة من الرسائل («كتاب رسائل»، انظر: ابن النديم ١٢٢، «ديوان رسائل»، انظر: ابن خلكان ٢/ ٧٢، الوافى بالوفيات، للصفدى ٤/ ٣٤). وأفاد الصولى (أخبار الشعراء ٢١٩) من تأليف نثرى له بخطه، وبتوسّط ابن أبى/ دؤاد ألّف للمعتصم كتابا فى ثورة بابك وهزيمته، وقيل: إنه كان أفضل من سائر التواريخ (انظر: إعتاب الكتاب، لابن الأبار ١٣٤).','4','159',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (926,'ذكره هارون بن على المنجّم فى «كتاب البارع» (انظر ابن النديم ١٤٤)، وأورد له فيه أيضا نخبة من أشعاره (انظر: ابن خلكان ٢/ ٧١، ٧٢)، وكان من مصادر أبى الفرج عن الزيات «كتاب» لأبى بكر الصولى خاصة (الأغانى، طبعة ثانية ٢٠/ ٤٦ - ٤٩، ٥٠ - ٥٢، ٥٣ - ٥٤، ٥٤ - ٥٦)، ولعله كان «كتاب الوزراء»، (انظر: ابن النديم ١٥١، وراجع: أخبار الشعراء، للصولى ٢٠٦).
وصل إلينا ديوانه الذى لا نعلم صانعه.
المخطوطات: مكتبة نصيرى بطهران (٤٥ ورقة، نسخ فى ٤٥٤ هـ)، التيمورية شعر ٢٩٧، دار الكتب بالقاهرة، أدب ٦٨ ش (٤١ ورقة، نسخة حديثة، انظر: الفهرس، طبعة ثانية ٣/ ١٠٨، فهرس معهد المخطوطات العربية ١/ ٤٥٢)، ومنه نسخة بالدار ١٤١٤٢ ز (انظر: فهرست المخطوطات ١/ ٣٢٥)، ييل ٧٩) ٤٨ - L ورقة، من القرن ١٣ الهجرى، انظر: نموى، رقم ٣٨٢)، نشره جميل سعيد، ربما اعتمادا على مخطوط دار الكتب، أدب ٦٨ ش، القاهرة ١٩٤٩، وتوجد أيضا قصائد وقطع له فى المصادر الآنفة الذكر، وانظر فضلا عن ذلك: الزهرة، لابن داود، إرشاد الأريب، لياقوت ١/ ٣٠ - ٣١، الدر الفريد ٢/ الورقة ٢٨٦ أ- ٣٥٣ ب.
وقيل: إن ابنه عبيد الله بن محمد بن عبد الملك الزيات (انظر: باب كتب الأدب) كان شاعرا مقلّا (انظر: ابن النديم ١٦٧).
وكان أحد أخلافه، أبو طالب أحمد بن الحسين بن على بن أحمد بن محمد بن عبد الملك الزيات (القرن الرابع/ العاشر)، راوى ترجمة «كتاب الفلاحة النبطية» (انظر: تاريخ التراث. (IV ,٣٢٦ - ٣٢٧

<span data-type="title" id=toc-1589>أبو حكيمة الكاتب</span>
هو راشد بن إسحاق بن راشد، وكنيته أبو حكيمة، بحسب القطعة التى وصلت إلينا من ديوانه، وبعض مصادر أخرى، وأبو حليمة فيما سوى ذلك، كان كاتبا شاعرا مرموقا فى العصر العباسى، وكتب لعبد الله بن طاهر (المتوفى سنة ٢٣٠/ ٨٤٤) بخراسان، حيث نظم أشعار غزل فى غلام لعبد الله، ورثاه بعد وفاته، والتحق','4','160',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (927,'بأصدقاء محمد بن عبد الملك الزيات، توفى فى طريق الحج إلى مكة، ولا يعرف تاريخ وفاته.

<span data-type="title" id=toc-1590>أ- مصادر ترجمته:</span>
طبقات الشعراء، لابن المعتز، طبعة أولى ١٨٤ - ١٨٥، طبعة ثانية، ٣٠٩، ٣٨٩ - ٣٩١، ٤١٦، الورقة، لابن الجراح ٧٦ (وثمة مصادر أخرى)، الموشح، للمرزبانى ٢٣٨، الأغانى، طبعة ثانية ٢٠/ ٥١ - ٥٢، إرشاد الأريب، لياقوت ٤/ ٢٠٣ - ٢٠٤.
انظر: بروكلمان، فى الملحق، I ,١٢٣ أحمد أبو على، المنتخل فى تراجم شعراء المنتحل ٣٢٢ - ٣٢٣.

<span data-type="title" id=toc-1591>ب- آثاره:</span>
ذكر ابن النديم (ص ١٦٦) أن ديوانه كان ٧٠ ورقة، / ويوجد جزء منه فى: برلين ٧٥٣٨ (الورقة ١ - ٣١، نسخ نحو سنة ٦٥٠ هـ)، وتوجد قطع أخرى فى المصادر الآنفة، وانظر أيضا: حماسة الظرفاء، الورقة ٣٧ ب، زهر الآداب، للحصرى (٣٣ بيتا)، حماسة ابن الشجرى، الحماسة البصرية (٣٠ بيتا)، الدر الفريد ١/ ١/ ص ١٢٢، ١/ ٢/ ص ١٠٨، ٢/ الورقة ٢٠ أ، ٣٣ ب، ٢٠٦ أ، ٢٩٨ أ.

<span data-type="title" id=toc-1592>إسحاق بن إبراهيم الموصلى</span>
هو الأديب والموسيقى أبو محمد إسحاق بن إبراهيم بن ماهان الموصلى، (المتوفى سنة ٢٣٥/ ٨٥٠، انظر: تاريخ التراث، (I ,٣٧١ كان ديوانه ٥٠ ورقة (انظر: الفهرست، لابن النديم، طهران، ص ١٨٨).
وقف أبو الفرج على «مجموع شعره»، (انظر: الأغانى ١٠/ ١١١)، ووردت له أبيات فى كتب الأدب، والمنتخبات الشعرية، جمعها محمد أ. العزيزى، ونشرها بعنوان: «ديوان إسحاق الموصلى»، بغداد ١٩٧٠.

<span data-type="title" id=toc-1593>عبد الله بن أبى الشيص</span>
هو ابن الشاعر أبى الشيص، عاش فى الشطر الثانى من القرن الثانى/ الثامن،','4','161',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (928,'وأوائل القرن الثالث/ التاسع، فى بغداد، عانى من السوداء، ورثى محمد بن على الرضا، (المتوفى سنة ٢٢٠/ ٨٣٥، انظر: الأعلام، للزركلى ٧/ ١٥٥)، وأبا تمام، وعد ابن أبى الشيص نفسه أشعر الناس، مات بعد أن زجّ نفسه فى دجلة فى يوم شديد البرد.

<span data-type="title" id=toc-1594>أ- مصادر ترجمته:</span>
أخبار أبى تمام، للصولى ٢٧٨ - ٢٧٩، طبقات ابن المعتز ط. أولى ١٧٣ - ١٧٤، ط. ثانية ٣٦٥ - ٣٦٦، الأغانى ١٦/ ٤٠٠، ٢٠/ ١٧٣.

<span data-type="title" id=toc-1595>ب- آثاره:</span>
قيل: إن الجاحظ، وعلى بن مهدى الكسروى (عاش قبل ٢٨٩/ ٩٠٢، انظر: معجم المؤلفين، لكحالة ٧/ ٢٤٧)، رويا بعض شعره (انظر: تاريخ بغداد ١٠/ ٦٤)، وقف ابن النديم (ص ١٦١) على مجموع لشعره، وكان ٧٠ ورقة، وجمع عبد الله الجبورى قطع شعره القليلة التى بلغتنا، ونشرها فى «أشعار أبى الشيص وأخباره»، بغداد ١٩٦٧، المقدمة ص ١١ - ١٤.

<span data-type="title" id=toc-1596>إبراهيم بن العباس الصّولى</span>
وكنيته أبو إسحاق، وأصله من أسرة من الموالى الأتراك، كان أخا لجدّ أبى بكر الصولى المعروف، وقيل: / إنه كان ابن أخت الشاعر العباس بن الأحنف، ولد فى سنة ١٧٦/ ٧٩٢، (أو ١٦٧/ ٧٨٣)، (انظر: إرشاد الأريب، لياقوت ١/ ٢٦٠)، وأصبح واحدا من مشاهير كتّاب عصره، وشاعرا جليلا، وانعقدت بينه وبين الخلفاء والأعيان صلات شخصية متنوعة، وتقلد عدة مناصب من عصر المأمون حتى عصر المتوكل، كانت وفاته بسامراء، فى سنة ٢٤٣/ ٨٧٥، (انظر: الأغانى ١٠/ ٤٤ وراجع:
مروج الذهب، للمسعودى ٧/ ٢٣٧).
وصفه ثعلب بأنه أشعر المحدثين، ولم يرو شعر كاتب قط غيره (انظر: الأغانى','4','162',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (929,'١٠/ ٥٩)، وعدّه المسعودى أيضا (مروج الذهب ٧/ ٢٣٧ - ٢٣٨) أشعر الشعراء الكتّاب، وقال صديقه دعبل: «لو تكسّب إبراهيم بن العباس بالشعر لتركنا فى غير شيء» (الأغانى ٢٠/ ٤٤)، وروى أن أبا تمام قال قريبا من ذلك (انظر: ابن النديم ١٢٢).

<span data-type="title" id=toc-1597>أ- مصادر ترجمته:</span>
أخبار الشعراء، للصولى ١٦٦، ٢٠٧، أخبار أبى تمام، للصولى، انظر الفهرس، الفهرست، لابن النديم، ص ١٢٦، أمالى المرتضى ١/ ٤٨٢ - ٤٨٨، تاريخ بغداد ٦/ ١١٧ - ١١٨، وفيات الأعيان، لابن خلكان ١/ ١١ - ١٣، إعتاب الكتاب، لابن الأبّار ١٤٦ - ١٥٢، أعيان الشيعة، للعاملى Rescher ,Abriss II ,٨٥ - ٥٨ ;١٨ - ١٦ /٦، ٣٠٤ - ٢٧٧ /٥ بروكلمان، فى الملحق، I ,٢١٨ شعراء بغداد، للخاقانى ١/ ٢٩ - ٤٣، أمراء البيان، لمحمد كرد على ٢٤٤ - ٢٧٧، عبد العزيز الميمنى، الطرائف الأدبية ١١٨ - ١٢٥، شعراء سامراء، للسامرائى ٨ - ١٤، الأعلام، للزركلى ١/ ٣٨، معجم المؤلفين، لكحالة ١/ ٤٢.

<span data-type="title" id=toc-1598>ب- آثاره:</span>
قيل: إن إبراهيم بن العباس كان يتشدد فى اختيار ما يطلع الناس عليه من شعره، فلم يدع من بعض القصائد إلّا أبياتا يسيرة (انظر: الأغانى ١٠/ ٤٤، ٥٠)، وقد جمع بنفسه ديوان شعره، وكتبه بخطّه (انظر:
إرشاد الأريب، لياقوت ١/ ٢٧٦ - ٢٧٧)، وترجع رواية الديوان التى بلغتنا إلى حفيد أخيه أبى بكر الصولى (راجع ابن النديم ١٦٦)، واعتمد أبو بكر الصولى على روايات ثعلب، وعلىّ بن يحيى المنجّم، وأبى ذكوان القاسم بن إسماعيل البصرى (سبق ذكره ص ٥٩)، وكان راوية الشاعر منذ ولايته فى الأهواز (انظر:
الميمنى، الطرائف الأدبية، ص ١٢٦ - ١٢٧) ١ - الديوان، ثمة مخطوط برواية أبى بكر الصولى فى إستنبول، وهبى ١٧٤٤ (١٧ ورقة، نسخ فى ١١٣٨ هـ عن أصل يرجع إلى سنة ٤٠٩ هـ)، نشره عبد العزيز الميمنى، فى: الطرائف الأدبية، القاهرة ١١٣٧، ص ١٢٦ - ١٨١، وتوجد فى المكتبة ذاتها قطع أخرى ١٨٢ - ١٨٨، وانظر أيضا: المنتخب الميكالى، الورقة ٢٦ أ، ١٣٣ أ، ١٦٥ أ، ١٧٩ ب، ٢٠٣ ب، الزهرة، لابن داود ١٠١، سفينة الأدباء الورقة ٢٢٤ أ- ٢٢٥ أ، الحماسة المغربية، الورقة ٤٩ ب، ٧٧ أ، بهجة المجالس، لابن عبد البر ٢٦١، الدر الفريد، فى ٣٨ موضعا.','4','163',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (930,'ومن بين مؤلفاته الأخرى (انظر: ابن النديم ١٢٢، ٣١٧، وراجع: إرشاد الأريب، / لياقوت ١/ ٢٧٧) كانت الكتب التالية، لا تزال، فى القرن السابع/ الثالث عشر، موجودة فى إحدى خزائن الكتب بحلب (انظر: ب. سباط فى، ٢١/ ١٩٤٦ /٤٩ MIE رقم ٣٦٧، ٣٧٦، ص ٣٤، رقم ٦١٢، ص ٣٦، رقم ٦٤٥).
٢ - «كتاب، أو ديوان رسائل»، ومنه قطع فى: إعتاب الكتاب، لابن الأبّار، وعصر المأمون، لفريد رفاعى، فى مواضع كثيرة.
٣ - «كتاب الدولة (العباسية)»، وقيل: إنه كان كبيرا، وفى الفهرست، لابن النديم ٣٤٤ اقتباس منه.
٤ - «كتاب الطبيخ».
٥ - «كتاب العطر».

<span data-type="title" id=toc-1599>على بن الجهم</span>
هو على بن الجهم بن بدر، أبو الحسن، من بنى سامة (لؤىّ بن غالب)، أصله من أسرة عربية، استوطنت مرو، (انظر: الديوان، ص ١٨٦)، ولعله ولد هنالك، أو فى بغداد التى انتقل إليها أبوه (انظر: خليل مردم، مقدمة الديوان، ص ٤)، وكان مولده نحو سنة ١٨٩/ ٨٠٥، إذ كان عمره خمسين عاما، عند ما حبسه المتوكل، (انظر:
الأغانى ١٠/ ٢١١، وراجع: خليل مردم، مقدمة الديوان ص ٤ - ٥)، فى سنة ٢٣٩/ ٨٥٣ (انظر: تاريخ الطبرى ٣/ ١٤١٩، وراجع: مروج الذهب، للمسعودى ٧/ ٢٤٩)، نشأ ببغداد، وكان أول اشتهاره بالشعر فى عصر المأمون، (انظر: خليل مردم، مقدمة الديوان، ص ٨)، وباعتباره سنيا متشددا هاجى العلويين (انظر:
الأغانى ١٠/ ٢٠٥ - ٢٠٦)، ومال إلى «أهل الحديث»، واتصل بأحمد بن حنبل (انظر: طبقات الحنابلة، لابن أبى يعلى ١/ ٢٢٣)، وكان على بن الجهمقاضيا بحلوان على عهد المعتصم (٢١٨/ ٨٣٣ - ٢٢٧/ ٨٤٢)، (انظر: الأغانى ١٠/ ٢١٠، وخليل مردم، مقدمة الديوان، ص ٨ - ٩)، وخصّ فيما بعد بالمتوكل فترة، حتى صار من جلسائه، (انظر: خليل مردم، مقدمة الديوان، ص ٩ وما بعدها)، قاتل متطوعا مع','4','164',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (931,'الجنود المقاتلة على الحدود، فلقى حتفه سنة ٢٤٩/ ٨٦٣، فى خساف، بالقرب من حلب.
ووصف على بن الجهم بأنه فصيح، مفلق، مطبوع، وكان يضع لسانه حيث يشاء، خبيثا لاذعا فى هجائه (انظر: طبقات الشعراء، لابن المعتز، طبعة أولى ١٥١، ط. ثانية ٣٢٠)، وقد أثنى- مرارا على قصيدته الداليّة، التى قالها وهو محبوس، ومن المرجح جدا أنه أول شاعر- ولعل ذلك بتأثير أبان اللاحقى- نظم تاريخ الخلفاء فى قصيدة مزدوجة (انتظر: خليل مردم، مقدمة الديوان ٣٩ - ٤٠، وفون جرونباوم، فى: .
(G. E. V. Grunebaum JNES ٣/ ١٩٤٤/ ١١

<span data-type="title" id=toc-1600>أ- مصادر ترجمته:</span>
الموشى، للوشاء، انظر الفهرس، أخبار أبى تمام، للصولى ٦١ - ٦٣، أخبار الشعراء، للصولى ٨١، ٨٩، العقد الفريد، لابن عبد ربه ٦/ ٤٠٢، ٤٠٧، معجم الشعراء، للمرزبانى ٢٨٦، الموشح، للمرزبانى ٣٤٤ - ٣٤٥، تاريخ بغداد ٧/ ١٧٠، ١١/ ٣٦٧ - ٣٦٩، سمط اللآلى، للبكرى ٥٢٦، مسالك الأبصار، لابن فضل الله ١٣/ الورقة ١٢٧ أ- ١٣٠ أ، وفيات الأعيان، لابن خلكان ١/ ٤٤١ - ٤٤٢.
وانظر: بروكلمان، فى الأصل، I ,٧٩ وفى الملحق I ,١٢٣ وانظر: جب فى دائرة المعارف الإسلامية، ط.
أوربية ثانية H.A.R.Gbiiin: EII ,٣٨٦، ٣٨٦ /١ / عصر المأمون، لفريد رفاعى Rescher Abriss II ,٣٥ - ٣٧.٤٣٠ - ٤٢٣ /٢ الأعلام، للزركلى ٥/ ٧٧، معجم المؤلفين، لكحالة ٧/ ٥٤ - ٥٥، مع ذكر مصادر أخرى.

<span data-type="title" id=toc-1601>ب- آثاره:</span>
يبدو أن ديوانه، بصنعة أبى بكر الصولى (انظر: ابن النديم ١٥١) مفقود، وذكر ابن خلكان أن «ديوان شعره صغير» (١/ ٤٤١)، ويبدو أن ذلك الديوان لا يطابق صنعة الطولى، ولا الروايات الناقصة التى وصلت إلينا (انظر: خليل مردم، مقدمة الديوان، ص ٤٥ - ٤٧).
١ - الديوان، يوجد مخطوطا فى: الإسكوريال ٣٦٩/ ٣ (الورقة ٤٨ - ٧٠، نسخ فى ١٠٠٢ هـ)، نشره','4','165',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (932,'خليل مردم بك، مع إضافة قطع أخرى من شعره، فى دمشق ١٩٤٩، انظر: تعليق شفيق جبرى، فى: مجلة المجمع العلمى العربى بدمشق ٢٥/ ١٩٥٠/ ٢٨٣ - ٢٨٥.
B. Lewin: Oriens ٤/ ١٩٥١/ ١٧٤
مصطفى جواد، فى: مجلة المجمع العلمى العربى بدمشق ٢٩/ ١٩٥٤/ ٦٢١ - ٦٢٧، ٣٠/ ١٩٥٥/ ١٦١ - ١٦٦.
R. Blacherein: Arabica ١/ ١٩٥٤/ ٢١٩
وطبع طبعة ثانية مزيدة، فى بيروت ١٩٧١.
٢ - «القصيدة الرصافية» فى المتوكل، توجد قطع منها فى: برلين، ٤/ ٩٣ Pet.) ٤ /٧٥٣٩ الورقة ٤٨ أ)، ٨٢١٨ (الورقة ٥١ أ)، ٨٢٥٩ (الورقة ٤٨ أ)، ٨٢٨٤ (الورقة ٤٣ ب- ٤٤ ب)، ٨٤٧١ (الورقة ١٩٥ ب- ١٩٦ ب، ٥٣ بيتا)، وفى «سفينة الأدب» المجهول المؤلف، برلين ٨٢٠٢ (الورقة ١٨ أ- ١٩ أ)، وفى: جمهرة الاسلام، الورقة ٢١٠ أ- ٢١١ أ، ونشرها خليل مردم ناقصة، على قطع من كتب الأدب، فى ذيل الديوان، ص ١٤١ - ١٤٨، ونشرت كاملة على كل المصادر الآنفة الذكر، بمثابة استدراك على نشرة الديوان، فى: مجلة المجمع العلمى العربى بدمشق ٢٦/ ١٩٥١/ ٦٨ - ٧٢ (راجع، (١٩٦/ ١٩٥٤ /٧ / Oriens وقام بتشطيرها محمود خيرت، «تنوير الفهم فى شرح وتشطير قصيدة ابن الجهم»، القاهرة ١٣١٧، ومحمد الجنبيهى، «موازنة الأوزان ومسامرة الندمان»، القاهرة ١٣١٨ (انظر: خليل مردم، الموضع المذكور، ص ١٤٢).
٣ - «المحبّرة فى التاريخ»، قصيدته المزدوجة فى تاريخ الخلفاء حتى عصره، واستأنفها أبو بكر أحمد ابن محمد بن عبد الله بن أبى شيخ (المتوفىسنة ٣٢٠/ ٩٣٢)، (انظر: تاريخ التراث، (I ,٣١٠ وتبدأ بمقدمة فى الخلق والجنة والخطيئة الأولى، ووردت قطع منها عن أبى زيد البلخى، فى البدء والتاريخ ٢/ ٨٥ - ٨٦، والمسعودى، فى: مروج الذهب ١/ ٦٣، ونشرها خليل مردم، فى ذيل الديوان، ص ١٥٧ - ١٥٩، ونشرت كاملة كاستدراك، فى: مجلة المجمع العلمى العربى بدمشق ٢٦/ ١٩٥١/ ٤٤ - ٦٧، على مخطوط لمحمد السماوى، ونسخة من مخطوطين قديمين، ونشرة مطبوعة (فى النجف) لهذه القصيدة، قيل إنها صودرت واتلفت (انظر خبرا للسماوى برجع إلى سنة ١٣٢٧ هـ فى: مجلة المجمع العلمى العربى بدمشق ٢٦/ ١٩٥١/ ٦٧)، ولم يستفد بعد من مخطوط لاله لي ٢٥٥ (الورقة ٨٦ - ٩٤، نسخ فى ٧٩٤ هـ)، وتشستربيتى ٤٣٧٩ (١٢ ورقة، نسخ فى ١٠٣٨ هـ)./

<span data-type="title" id=toc-1602>مروان بن أبى الجنوب</span>
حفيد مروان بن أبى حفصة (سبق ذكره ص ٤٤٧)، وكان يكنّى مثله بأبى','4','166',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (933,'السّمط، وقد يطلق عليه مروان الأصغر، تمييزا له عن جده، وكان شاعر مديح فى بلاط الخلفاء: المأمون، والمعتصم، والواثق، والمتوكل، وقيل: إن المتوكل أجزل له العطاء، وولّاه إلى حين البحرين واليمامة، كانت وفاته بعد سنة ٢٤٠/ ٨٥٤، ومن المحتمل أن القصيدة التى زعموا أنه أيضا نظمها (انظر: مروج الذهب، للمسعودى ٧/ ٣٧٧ - ٣٧٨) فى تولى المعتز الخلافة (٢٥٢/ ٨٦٦)، هى لابنه محمد بن مروان بن أبى الجنوب (انظر: تاريخ الطبرى ٣/ ١٦٥١ - ١٦٥٢)، ويبدو أن مروان كان مقلدا فى أسلوبه لجده، ويوصف شعره بأنه وسط، وساقط، و «بارد».

<span data-type="title" id=toc-1603>أ- مصادر ترجمته:</span>
البيان والتبيين، للجاحظ ١/ ٦٣ - ٦٤، تاريخ الطبرى ٣/ ١٤٦٥ - ١٤٦٩، الأغانى ١٢/ ٨٠ - ٨٧، معجم الشعراء، للمرزبانى ٣٩٩، الموشح، للمرزبانى ٣٠٢ - ٣٠٣، ٣٤٤ - ٣٤٥، الديارات، للشابشتى ٦، تاريخ بغداد ١٣/ ١٥٣ - ١٥٥، وفيات الأعيان، لابن خلكان ٢/ ١١٩، الأعلام، للزركلى ٨/ ٩٨، معجم المؤلفين، لكحالة ١٢/ ٢٢١.

<span data-type="title" id=toc-1604>ب- آثاره:</span>
ذكر ابن النديم (ص ١٦٠)، أن شعره كان نحو ١٥٠ ورقة، ووصلت إلينا قطع من شعره فى المصادر المتقدمة الذكر، لا سيما فى الأغانى، وطبقات الشعراء، لابن المعتز، وأيضا فى: تاريخ الطبرى ٣/ ١٣٢٤، مروج الذهب، للمسعودى ٧/ ٣٠٥ - ٣٠٦، الحماسة البصرية ٢/ ٢٩٣، خزانة الأدب ٢/ ١٩٧، وانظر فضلا عن ذلك: .
Schawahid- Indices ٣٤٠
ويشبّه الشعر فى آل مروان بن أبى حفصة بالماء الحار، ابتداؤه فى نهاية الحرارة ثم تلين حرارته، ثم يفتر، ثم يبرد، وينتهى (فى الجيل الخامس: متوّج) إلى الجمود (انظر:
الموشح، للمرزبانى ٣٠٣، الأغانى ١٢/ ٨٠)، وعرف ابن النديم دواوين أخرى لأفراد آخرين من هذه العائلة، وذكر عدد أوراقها (ص ١٦٠ - ١٦١، وراجع طبعة طهران، ص ١٨٢ - ١٨٣، والترجمة الإنجليزية، ص ٣٥٤):','4','167',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (934,'محمد بن مروان بن أبى الجنوب: من المحتمل أنه نظم قصائد فى انتقال الخلافة من المستعين إلى المعتز، سنة ٢٥٢/ ٨٦٦، انظر: تاريخ الطبرى ٣/ ١٦٥١ - ١٦٥٢ (١٢ بيتا)، ١٦٥٢ - ١٦٥٣ (١٥ بيتا)، وقصيدة فى مدح المعتز، انظر: تاريخ الطبرى ٣/ ١٦٧٢ - ١٦٧٥ (٢٨ بيتا)، كان ديوانه نحو ٥٠ ورقة.
متوّج (أو فتوح؟ ، راجع تاريخ الطبرى ٣/ ١٤٦٧، الهامش) بن محمود بن مروان بن أبى الجنوب: شاعر فى بلاط المكتفى (٢٨٩/ ٩٠٢ - ٢٩٥/ ٩٠٨) (انظر:
الموشح، للمرزبانى ٣٠٣)، كان ديوانه نحو ١٠٠ ورقة.
أبو سليمان إدريس بن سليمان (بن يحيى) بن أبى حفصة: أحد إخوة مروان بن أبى حفصة الأكبر (انظر: تاريخ الطبرى ٣/ ٥٩٤، الموشح، للمرزبانى ٣٠٣). كان ديوانه نحو ١٠٠ ورقة./ محمد بن إدريس: ابنه، وكان مقلّا.
آمنة بنت الوليد بن يحيى بن أبى حفصة: مقلّة أبو السّمط عبد الله بن السمط (راجع: الموشح، للمرزبانى ٣٠٣) لعله هو:
عبد الله بن أبى السمط، من شعراء المأمون، انظر: تاريخ الطبرى ٣/ ١١٥٩، كان ديوانه نحو ١٠٠ ورقة.

<span data-type="title" id=toc-1605>سعيد بن حميد</span>
هو سعيد بن حميد بن سعيد، وكنيته أبو عثمان، كاتب شاعر، وكان من أصل فارسى، أقام ببغداد وسامراء، وعرف بأنه كاتب للخليفة المستعين (٢٤٨/ ٨٦٢ - ٢٥١/ ٨٦٦)، وقع التهاجى بينه وبين أبى العيناء وأبى على البصير، وكان صديقا لفضل الشاعرة.','4','168',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (935,'<span data-type="title" id=toc-1606>أ- مصادر ترجمته:</span>
مروج الذهب، للمسعودى ٧/ ٣٢٥ - ٣٢٨، تاريخ الطبرى، انظر الفهرس، الأغانى ١٨/ ١٥٤ - ١٦٧، الفهرست، لابن النديم ١٢٣، سمط اللآلى ١٦١ - ١٦٢، نهاية الأرب، للنويرى ٣/ ٩٣.
Ritter, Geheimnisse ٢٢٩ Anm.
الأعلام، للزركلى ٣/ ١٤٦.

<span data-type="title" id=toc-1607>ب- آثاره:</span>
١ - كان ديوانه ٥٠ ورقة (انظر: ابن النديم ١٦٦)، وثمة قطع من شعر مجموعة من كتب الأدب، فى:
«رسائل سعيد بن حميد» (سيأتى ذكرها) ص ١١٩ - ١٦١، وانظر أيضا: المنتخب الميكالى، الورقة ١٦ أ، ١٧ أ- ب، ٣١ ب، ٨٨ ب، ١٠٢ أ، ١٠٢ ب، الدر الفريد فى ١٧ موضعا.
٢ - لم تصل إلينا مجموعة رسائله (انظر: ابن النديم ١٢٣)، وجمع ى. إ. السامرائى من كتب الأدب ٤٣ قطعة من رسائله ومكاتباته، ونشرها فى: «رسائل سعيد بن حميد وأشعاره» بغداد ١٩٧١.
٣ - «كتاب انتصاف العجم من العرب»، ويعرف أيضا ب «كتاب التسوية». (انظر: ابن النديم ١٢٣).

<span data-type="title" id=toc-1608>أبناء حميد بن عبد الحميد الطوسى:</span>
<span data-type="title" id=toc-1609>أبو جعفر محمد بن حميد</span>
(انظر فى أبيه: (EI ١ III ,٥٧٣ فائد جيش المأمون، حارب بابك، وقتل سنة ٢١٤/ ٨٢٩.
نظم أبو تمام قصيدة فى رثائه.
معجم الشعراء، للمرزبانى ٤٢٧، الموشح، للمرزبانى، انظر الفهرس، الأغانى ١٠/ ١٧٩، ١٤/ ٩٧، ١٧/ ٣٩٠، المحمدون، للقفطى ٢٢٣ - ٢٢٤، الكامل، لابن الأثير، طبعة ثانية، الجزء السادس، انظر الفهرس، الوافى بالوفيات، للصفدى Sourdel ,Vizirat ٧٣٠، ٢٩ /٣ الأعلام، للزركلى ٦/ ٣٤٣.
كان مقلّا (انظر: ابن النديم ١٦٥)./','4','169',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (936,'<span data-type="title" id=toc-1610>إسحاق بن حميد:</span>
كان ديوانه ٧٠ ورقة (انظر: ابن النديم ١٦٥).

<span data-type="title" id=toc-1611>أبو نهشل بن حميد:</span>
من ندماء الخليفة الأمين، مدحه البحترى، وبنى قبة على قبر أبى تمام.
المرزبانى، فى المرجعين المذكورين، الأغانى ١٠/ ١٩٨ - ١٩٩، ٢٠/ ١٤٨، ١٨٣، وفيات الأعيان، ابن خلكان ١/ ١٥٣.
من المقلّين (انظر: ابن النديم ١٦٥).

<span data-type="title" id=toc-1612>أبو نصر (أو أبو نضير) بن حميد:</span>
كان يعرف دعبل بن على.
المرزبانى، فى المرجعين المذكورين، الأغانى ٢٠/ ١٢٩ - ١٣٠.
كان مقلّا (انظر: ابن النديم ١٦٥).

<span data-type="title" id=toc-1613>خالد بن يزيد الكاتب</span>
هو أبو الهيثم خالد بن يزيد (التميمى؟ ) الكاتب البغدادى، أصل أسرته من خراسان، ويبدو أنه نشأ ببغداد، وقد حظى على عهد هارون الرشيد بشهرة فى الشعر (انظر: مروج الذهب، للمسعودى ٦/ ٣٦٥ - ٣٦٦)، وفى خلافة المعتصم (٢١٨/ ٨٣٣ - ٢٢٧/ ٨٤٢)، كان زمانا كاتبا للجيش ببعض الثغور (انظر الأغانى، طبعة أولى ٢١/ ٤٥)، ووقع التهاجى بينه وبين البحترى وأبى تمام، وتوفى فى سن عالية ببغداد سنة ٢٦٢/ ٨٧٦ (انظر: النجوم الزاهرة، لابن تغرى بردى ٣/ ٣٦)، أو سنة ٢٦٩/ ٨٨٣ (انظر: إرشاد الأريب، لياقوت ٦/ ١٧٣).
وكان فى المقام الأول شاعر غزل، يؤثر المقطّعات، أثنى ابن المعتز (الطبقات، طبعة أولى ١٩٢، طبعة ثانية ٤٠٥) على شعره ثناء عاليا.','4','170',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (937,'<span data-type="title" id=toc-1614><span data-type="title" id=toc-1617>أ- مصادر ترجمته:</span></span>
الديارات، للشابشتى ١١ - ١٤، تاريخ بغداد ٨/ ٣٠٨ - ٣١٤، سمط اللآلى، للبكرى ٣١١، فوات الوفيات، للكتبى ١/ ٢٩٦ - ٢٩٧، الأعلام، للزركلى ٢/ ٣٤٣، معجم المؤلفين، لكحالة ٤/ ٩٨.

<span data-type="title" id=toc-1615>ب- آثاره:</span>
رتّب أبو بكر الصولى ديوانه (انظر: سمط اللآلى ٤٢٥) على الحروف، (فى ٢٠٠ ورقة)، (انظر: ابن النديم ١٦٦)، ويبدو أن بعض صنعة الصولى قد وصل إلينا.
المخطوطات: بشير أغا ٥٢٦ (الورقة ١ - ٦١، نسخ سنة ١١١٢ هـ)، الظاهرية، عام ٣٣٣١ (٨٩ ورقة، على الحروف، نسخ سنة ١١١٠ هـ، انظر: فهرس عزة حسن ٢/ ١٣٧، انظر: بروكلمان، فى الملحق، (III، ٣٩١١ وتوجد نسخة منقولة عنه فى: التيمورية، بالقاهرة (انظر: سمط اللآلى ٣١١، الهامش)، وترد قطع له، وأبيات مفردة، فى: المنتخب الميكالى، الورقة ٨٢ ب، ٢٢٠ ب، / الزهرة، لابن داود ٦٣، ٢٨٩، الدر الفريد ١/ ١/ ١٢٨، ١/ ٢/ ١١١، ٢/ الورقة ٣٢ ب، ٩٥ ب، ١٨٠ ب، ٢٦٨ أ.

<span data-type="title" id=toc-1616>ابن أبى فنن</span>
هو أبو عبد الله أحمد بن أبى فنن صالح، كان كاتبا، وشاعرا مرموقا، فى بغداد، ومن المقرّبين إلى محمد بن عبد الله بن طاهر، والفتح بن خاقان، وقيل: إنه توفى بين عامى ٢٦٠/ ٨٧٤ و ٢٧٠/ ٨٨٣.
أ- مصادر ترجمته:
أخبار الشعراء، للصولى، انظر الفهرس، طبقات الشعراء لابن المعتز، طبعة أولى ١٨٨، طبعة ثانية ٣٩٦ - ٣٩٧، وفى مواضع أخرى، سمط اللآلى، للبكرى ٢٤٥، الديارات، للشابشتى ٨١، تاريخ بغداد ٤/ ٢٠٢ - ٢٠٣، إرشاد الأريب، لياقوت ٦/ ١٢٣، فوات الوفيات، للكتبى ١/ ٨٣، الوافى بالوفيات، للصفدى ٦/ ٤٢٣، شعراء بغداد، للخاقانى ١/ ٢٨٩ - ٢٩٠.','4','171',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (938,'<span data-type="title" id=toc-1618>ب- آثاره:</span>
قيل: إن ديوانه كان ١٠٠ ورقة (انظر: ابن النديم ١٦٦)، وترد قطع من شعره فى المصادر المتقدمة الذكر، وانظر أيضا: المنتخب الميكالى، الورقة ١٤٤ ب، الزهرة، لابن داود، زهر الآداب، للحصرى، الأشباه والنظائر، للخالديين، محاضرات الراغب، فى ٨ مواضع، الدر الفريد ٢/ الورقة ١١٢ ب، ١٢٨ أ.

<span data-type="title" id=toc-1619>ابن الرومى</span>
هو أبو الحسن على بن العباس بن جريج (جرجس أو جرجيس، معرّب Gregorios أو، (Georgios وكان أبوه من أصل رومى (يونانى)، وآل أمه من الفرس. ولد سنة ٢٢١/ ٨٣٦، فى بعض الضواحى غربى بغداد (انظر: معجم الشعراء، للمرزبانى ٢٨٩، وفيات الأعيان، لابن خلكان، ١/ ٤٤٣)، تلقّى دروسه على يد محمد بن حبيب، الذى كان صديقا لأبيه (انظر: إرشاد الأريب، لياقوت ٦/ ٤٧٤)، وقد بدأ ينظم الشعر فى حداثته، ويتكسب بالمديح، فإذا لم يصله ممدوحه هجاه، وكان شيعيا عن اقتناع، وفى عام ٢٥٠/ ٨٦٤ رثى يحيى بن عمر، الذى أخفق فى ثورته (انظر:
الأعلام، للزركلى ٩/ ٢٠٠)، وهاجم العباسيين، وكان أخذ نفسه بالابتعاد عن بلاطهم من أول أمره (انظر:
(S. Boustanyin: EIIII, ٩٠٨
وقد وجد تشجيعا عند آل طاهر، وأفراد أسرة نوبخت، وغيرهم من أصحاب النفوذ، وعند بعض الأعيان من المعتزلة والعلويين، ويبدو أنه لم يكن له اتصال مباشر/ بالأسرة الحاكمة، فيما عدا الموفق (المتوفى سنة ٢٧٨/ ٨٩١)، وأهان كثيرا ممن أنعموا عليه بتعاظمه وأهاجيه، وقيل: إنه أصبح فى شيخوخته خاصّة عدوانيا عنيدا، وزاد من ذلك شدة اعتقاده فى الخرافات، وفقده أولاده وزوجته (المرجع السابق ٩٠٨)، توفى فى بغداد، سنة ٢٨٣/ ٨٩٦ (وقيل: سنة ٢٧٦، أو ٢٨٤ هـ، انظر: معجم الشعراء، للمرزبانى ٢٨٩، تاريخ بغداد ١٢/ ٢٦، وفيات الأعيان، لابن خلكان ١/ ٤٤٣).
كان ابن الرومى واحدا من أكثر شعراء العرب شعرا، وقد أثنى، على بلاغته فى','4','172',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (939,'المديح والهجاء، وبخاصة على شعره فى الوصف (انظر: معجم الشعراء، للمرزبانى ٢٨٩، وفيات الأعيان، لابن خلكان ١/ ٤٤٢)، وقد جرى نقاش بين طه حسين (من حديث الشعر والنثر ٢٢٧) وعمر فروخ، فيما إذا كان أسلوبه يدل على إرث يونانى، أو أنه كان على الإطلاق عارفا باللغة اليونانية، ورأى سليمان البستانى (مقدمة ترجمته للإلياذة، ص ١٥٥)، وبطرس البستانى (أدباء العرب ٢/ ٢٩٤) ذلك محتملا. أما يوليوس جرمانوس، الذى ذكر الآراء فى ذلك (انظر:
(Julius Germanus, lbn- Rumi''s Dichtkunst ٢٧٨
فقد أنهى النقاش فى البدء بقوله: «لم يكن بوسع ابن الرومى أن يتعلم اليونانية من أقارب أبيه، على الرغم من كثرة افتخاره بروميته» (المرجع السابق ٢٦٦، وراجع فيه ص ٢٦٧).

<span data-type="title" id=toc-1620>أ- مصادر ترجمته:</span>
أخبار أبى تمام، للصولى ٢٥، ٦٧، مروج الذهب، للمسعودى، انظر الفهرس، الأغانى ١٠/ ٥٩، طبعة ثانية ٢٠/ ٧٢، الموشح، للمرزبانى ٣٥٧ - ٣٥٨، رسالة الغفران، للمعرى، انظر الفهرس، زهر الآداب، للحصرى، انظر الفهرس، سمط اللآلى، للبكرى ١٦٠ - ١٦١، مسالك الأبصار لابن فضل الله ١٣/ الورقة ١٥٥ أ- ١٧٦ أ، المنتظم، لابن الجوزى ٥/ ١٦٥ - ١٦٨، إرشاد الأريب، لياقوت ١/ ٢٢٤ - ٢٢٧، فى مواضع مختلفة، معاهد التنصيص ١/ ١٠٨ - ١١٨ وانظر: بروكلمان، فى الأصل، I ,٧٩ - ٨٠ وفى الملحق، I ,١٢٣ - ١٢٥ عباس محمود العقاد، ابن الرومى، حياته من شعره، القاهرة ١٩٣١، طبعة ثانية ١٩٣٨ (•)، أنيس المقدسى، أمراء الشعر العربى، بيروت ١٩٣٢، ٢٧٢ - ٣١٨، أعيان الشيعة، للعاملى ٤١/ ٢٨١ - ٢٨٤،
Rhuvon Guest, Lifrandwordsoflb ner Rumi, London ١٩٤٤
وانظر فيه:
A. S. Trittonin: BSOAS ١١/ ١٩٤٣ - ٤٦/ ٨٧٣ - ٨٧٤; A. J. Arberryin: JRa S ١٩٤٥ - ١٩١, ١٩٢; E. Gracia Gomezin: Andalus ١٠/ ١٩٤٥/ ٤٧٦ - ٤٧٧
ونقل حسين نصار كتاب روفون جست: ابن الرومى حياته وشعره، إلى العربية، طبع فى بيروت دون
_________
(•) طبع أيضا فى بيروت ١٩٦٨ (المراجع)','4','173',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (940,'تاريخ، عمر فروخ، ابن الرومى، بيروت ١٩٤٢، طبعة ثانية ١٩٤٦، محمد عبد الغنى حسن، ابن الرومى، القاهرة ١٩٥٥.
Ritter, Geheimnisse ١٣٠ - ١٣١, ١٦٣ - ١٦٤; A. K. Julius Germanus, lbn. Rumi''s Dichtkunstin: Acta Orient, Hung. ٦/ ١٩٥٦/ ٢١٥ - ٢٨٦
(المصادر من ٢١٦ - ٢١٨)، إيليا سليم الحاوى، ابن الرومى، فنه ونفسيته من خلال شعره، بيروت ١٩٥٩،
S. Boustany, lbnar- Rumi, savieetsonoeuvre, Bd, l, Beirut ١٩٦٧.
وانظر كذلك: مقال سليم البستانى، فى: دائرة المعارف الإسلامية، (الطبعة الأوربية الثانية EIIII ,٩٠٧ - ٩٠٩، ٩٠٩ - ٩٠٧ /٣ مع ذكر مصادر أخرى، أحمد الجندى، «ابن الرومى، شاعر لم ينصفه التاريخ»، فى: مجلة المجمع العلمى العربى بدمشق ٤١/ ١٩٦٦/ ٤٨٧ - ٤٩٤، الأعلام، للزركلى ٥/ ١١٠، معجم المؤلفين، لكحالة ٧/ ١١٤ - ١١٥ (وذكر فيه مصادر ودراسات أخرى)، مراجع تراجم الأدباء العرب، للوهابى ١/ ٨١ - ٨٨.

<span data-type="title" id=toc-1621>ب- آثاره:</span>
روى شعره عدّة رواة وأدباء/، (انظر: تاريخ بغداد ١٢/ ٢٣)، وروى ديوانه الذى لم يكن بادئ الأمر مبنيا على الحروف، أبو الحسن على بن عبيد الله، المعروف بابن المسيّب الكاتب أو بالمسيّبى (انظر:
ابن النديم ١٦٥)، وكان صديقا للشاعر، وألّف أيضا «كتاب أخبار ابن الرومى»، الذى اطّلع عليه أيضا ياقوت، ونقل عنه (انظر: إرشاد الأريب ١/ ٢٢٤، ٢٢٧)، وكان أبو جعفر محمد بن يعقوب مثقال الواسطى (يأتى ذكره ص ٦٠٣)، غلام ابن الرومى وراويته، وكان هو نفسه شاعرا، وقيل أيضا: إن ابن الرومى سرق شعره، وعن مثقال روى أبو الحسن بن العصب الملحى ديوانه (انظر: ابن النديم ١٦٦، الموشح، للمرزبانى ٤٤٨، الوافى بالوفيات، للصفدى ٥/ ٢٢٢)، ولعله كان من بين الرواة المباشرين أيضا غلام ابن الرومى الشاعر ابن الحاجب (يأتى ذكره ص ٦٠٣)، وعمل أبو بكر الصولى الديوان على الحروف (انظر: ابن النديم ١٦٥)، وقد وصل إلينا بعض عمله على الأقل، وقيل: إن أبا الطيب ورّاق ابن عبدوس جمعه من جميع النسخ المعروفة إلى عهده، مما هو على الحروف وغيرها، وزاد عليها نحو ١٠٠٠ بيت (انظر: ابن النديم ١٦٥)، وألّف أحمد بن عبيد الله بن محمد بن عمّار الثقفى، وكان صديقا للشاعر «كتاب أخبار ابن الرومى والاختيار (ات) (أو المختار) من شعره» (انظر: ابن النديم ١٤٨، وراجع: طبعة طهران، ص ١٦٦، إرشاد الأريب لياقوت ١/ ٢٢٧)، وقيل: إن الخالديين ألّفا «كتاب فى أخبار شعر ابن الرومى» (انظر: ابن النديم ١٦٩)، وإن ابن سينا اختار طائفة من شعره، وشرح مواضع مشكلة فيه (انظر: كشف الظنون ٧٦٦)، وبلغنا ديوانه كاملا.','4','174',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (941,'المخطوطات: رواية لم تتحقق نسبتها، كاملة فى أربعة مجلدات، وتوجد فى: برنستون، جاريت ١٩ (المجلد الأول، من قافية الألف- الخاء، ١٥٨ ورقة، المجلد الثانى، من قافية الدال- السين، ٢٠٤ ورقة، المجلد الثالث، من قافية الشين- الكاف، ١٥٤ ورقة، المجلد الرابع، من قافية اللام، ٢١٣ ورقة)، صنعة أخرى (؟ )، منها ثلاث مجلدات، من أصل أربع، فى: إستنبول، سراى، ريفان كوشك ٧٠١ (المجلد الأول، من قافية الألف- الدال، ٢٥٦ ورقة، نسخ فى ٦٥١ هـ)، نور عثمانية ٣٨٥٩ (تكملة مخطوط ريقان، المجلد الثانى، من قافية الدال- الضاد، ٢٦١ ورقة، نسخ فى ٦٥٢ هـ)، نور عثمانية ٣٨٦٠ (المجلد الثالث، حتى قافية الكاف، ٢٥١ ورقة، نسخ فى ٦٥٢ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٤٥١ - ٤٥٢)، ويوجد مجلّد، برواية أبى بكر الصولى، فى: دار الكتب بالقاهرة، أدب ١٣٩ (مخطوط قديم، انظر: الفهرس، طبعة ثانية ٣/ ١٠٧)، ويوجد المجلد الأول، من رواية أبى سعيد أحمد بن محمد الطاهر العقيلى، فى: سراى، أحمد الثالث، ٢٥٥٨ (قافية الألف- الظاء، ٢٤١٢ ورقة، من القرن السادس الهجرى، انظر: أرشر، فى O.Rescher ,in: RSO ٤ /١٩١١ - ١٩ /٧١٧ - ٧١٨ /: فهرس معهد المخطوطات العربية ١/ ٤٥٢)، مجلدات مفردة لروايات لم تتحقق نسبتها أو لمجهولين: دار الكتب، بالقاهرة، أدب ٥٩٢ (المجلد الثانى، من الدال- الضاد، نسخ فى ١٢٨٧ هـ)، أدب ١٣٧١ (مجلدان، نسخا فى ١٣١٤ هـ)، أدب ١٩٦٥ (مجلد، نسخ فى ١٣٢٩ هـ، انظر: الفهرس، طبعة ثانية ٣/ ١٠٧ - ١٠٨)، أجزاء مفردة لإحدى الروايات (من الدال- الميم مع بعض الحذف)، الظاهرية بدمشق، عام ٨٥٠٥ (١٨٦ ورقة)، ٨٥٠٦ (١٨٨ ورقة)، ٨٥٠٧ (١٨٦ ورقة)، ٨٥٠٨ (١٦٣ ورقة)، ٨٥٠٩ (٧٩ ورقة، وجميعها من القرن ١٤ الهجرى، انظر: فهرس عزة حسن ٢/ ١٥٢ - ١٥٥)، الإسكوريال ٢٧٧ (المجلد الأخير، ابتداء من اللام، لعله تتمة مخطوط نور عثمانية ٣٨٦٠، ٢٩٠ ورقة، نسخ فى ٦٥٢ هـ)، ليدن، مخطوطات شرقية ٢٧٨٧ (١٠٠ ورقة، نسخة حديثة، انظر: فورهوف ٦٤)، العباسية بالبصرة ب حديثة، انظر: فورهوف ٦٤)، العباسية بالبصرة ب ٢٤ (١٢٥ ورقة، انظر: على الخاقانى فى: مجلة المجمع العلمى العراقى ٨/ ١٩٦١/ ٢٤٨) (١٢).
مختارات أخرى وقصائد مفردة:
«مختار ديوان ابن الرومى»، لجمال الدين أبى بكر محمد بن نباتة (المتوفى سنة ٧٦٨/ ١٣٦٦، انظر:
بروكلمان، (١٠، II أياصوفية ٤٢٦١ (الورقة ١ ب- ١٣٦ ب، من القرن الثامن الهجرى، انظر: أ. رشر فى:
O. Rescher, WZKM ٢٦/ ١٩١٢/ ٩٠
_________
(١٢) يجب حذف كوبريلى ٢٥٥٩ المذكور عند بروكلمان، فى الملحق، ١٢٥، I وشرح ابن رشد لديوان ابن الرومى مشكوك فيه، وعلى أى حال لا وجود لنسخة منه فى مكتبات إستنبول.','4','175',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (942,'فهرس معهد المخطوطات العربية ١/ ٥٢٢)، ومنه نسخة مصورة فى دار الكتب بالقاهرة، أدب ٥٢٢٢ (انظر: الفهرس، طبعة ثانية ٣/ ٣٣٧ - ٣٣٨)، قصائد مفردة فى الظاهرية، عام ٣٣٢٣ (الورقة ٥٩ ب- ٧٦ أ، من القرن الخامس الهجرى، انظر: فهرس عزة حسن ٢/ ١٦٥٦ - ١٥٧)، أسعد ٣٥٤٢/ ١٤ (الورقة ١٣١ - ١٣٥، نسخ فى ٥٦٤ هـ، راجع (M.Bergein: BEO ١٦ /١٩٥٨ - ٦٠ /٢١ وثمة «ضادية» فى: يوسف أغا بقونية ٤٨٠١ (الورقة ٢١٥ - ٢٢٠)، وتوجد مرثية فى من قتل من أهل البصرة فى إحدى ثورات العلويين، فى: جمهرة الإسلام، الورقة ٦١ أ- ٦٢ ب (راجع: مجلة المجمع العلمى العربى بدمشق ٣٣/ ١٩٥٨/ ٧)، وترد له قصائد وأبيات فى منتخبات شعرية أخرى.
مجموعات جديدة من كتب الأدب: الظاهرية، عام ٨٧٣٨ (٢٩ ورقة، من القرن ١٤ الهجرى، انظر:
فهرس عزة حسن ٢/ ٤٠٣ - ٤٠٤)، ٨٧٣٩ (٨٦ ورقة، من القرن ١٤ الهجرى، انظر: فهرس عزة حسن ٢/ ١٥٥ - ١٥٦)، ٥٤ (١٠٨ ورقة، مخطوط حديث، انظر: فهرس عزة حسن ٢/ ١٥٢).
«حل أبيات ابن الرومى الثلاثة التى أولها ... »، لمحيى الدين أبى المظفر يحيى بن أبى المعالى محمد بن يحيى بن محمد، يوجد فى دار الكتب بالقاهرة، أدب ٨٥٠٣ (أربع ورقات، من القرن السابع، انظر: فهرس معهد المخطوطات العربية ١/ ٤٤٥).
نشرات لبعض الديوان: كامل كيلانى، «ديوان ابن الرومى، اختيار وتصنيف .. »، القاهرة ١٩٢٤، محمد شريف سليم، «ديوان ابن الرومى» (مع شرح)، القاهرة ١٩١٧ - ١٩٢٢، (من قافية الألف إلى الخاء)، وعقب ذلك S.Boustany ,lbnar -Rumi ,Diwan أطروحة مطبوعة على الآلة الكاتبة، باريس ١٩٦١ (من الدال- الزاى) و ١٩٦٧ (من السين- الظاء)، وأحدث نشرة لحسين نصار، «ديوان ابن الرومى»، الجزء الأول، القاهرة ١٩٧٣ (من الألف- الثاء)، الجزء الثانى، القاهرة ١٩٧٤ (من الجيم- الذال).
وترجم عبد الكريم يوليوس جرمانوس بعض القصائد إلى الألمانية فى:
A. K. Julius Germanuslbn- Rumi''s Dichtkunstin: Acta Orient. Hung. ٦/ ١٩٥٦/ ٢١٥ - ٢٨٦
وإلى المجرية، فى:
Azarabszellemise gmegujhoddada, Budapest ١٩٤٤
وإلى الإيطالية، فى:
Sulleormedi Maometto, Mailand ١٩٣٨, II, ٩٣','4','176',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (943,'وكتب محمد عبد المنعم خفاجى دراسة بعنوان: «التشبيه فى شعر ابن المعتز وابن الرومى»، مصر ١٩٤٩، وانظر أيضا:
Schoeler Naturdichtung ١٦٧ - ٢٣٤

<span data-type="title" id=toc-1622>النّاجم</span>
هو أبو عثمان سعد (أو سعيد) بن الحسن بن شدّاد المضرى، أديب وشاعر، وكان صديقا وراوية لابن الرومى، توفى سنة ٣١٤/ ٩٢٦.

<span data-type="title" id=toc-1623><span data-type="title" id=toc-1626>أ- مصادر ترجمته:</span></span>
معجم الشعراء، للمرزبانى ٤٥٩، الموشح، للمرزبانى ٣٣٨، رسالة الغفران للمعرى ٤٧٨، ٤٨٢، سمط اللآلى، للبكرى ٥٢٥، الديارات، للشابشتى ٦١، إرشاد الأريب، لياقوت ٤/ ٢٣١ - ٢٣٢، فوات الوفيات، للكتبى ١/ ٣٤٥ - ٣٤٦، الأعلام، للزركلى ٣/ ١٣٣.

<span data-type="title" id=toc-1624>ب- آثاره:</span>
لا نعرف شيئا عن ديوانه، وترد قطع من شعره فى المصادر المتقدمة الذكر، وانظر أيضا: المنتخب الميكالى، الورقة ١٣١ أ، محاضرات الراغب ١/ ٢٨٥، ٣١٩، ٢/ ٦٩٠، ٧٢٢، ٣/ ٢٨٦، زهر الآداب، للحصرى ١٥٣، ٣٩٤، ١٠٠١، / نهاية الأرب، للنويرى ٢/ ٥٠، ٤/ ١٠٦، ٥/ ١١٣، ١١٤، ١١٦، ١١٨، ١٢٠، ١١/ ١٥٠، الدر الفريد ١/ ١/ ص ١٢٠، ٢/ الورقة ٢٢٦ أ.

<span data-type="title" id=toc-1625>ابن بسّام</span>
هو أبو الحسن على بن محمد بن نصر بن منصور بن بسّام العبرتائى، ويقال له أيضا البسّامى، ابن أخت حمدون بن إسماعيل، وكان كاتبا أديبا، وشاعرا هجّاء، ببغداد. توفى سنة ٣٠٢/ ٩١٤، أو سنة ٣٠٣/ ٩١٥، وله من العمر نحو سبعين سنة.
أ- مصادر ترجمته:
معجم الشعراء، للمرزبانى ٢٩٤ - ٢٩٥، مروج الذهب، للمسعودى، ج ٨، انظر الفهرس، تاريخ','4','177',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (944,'بغداد ١٢/ ٦٣، وفيات الأعيان، لابن خلكان ١/ ٤٤٤ - ٤٤٥، إرشاد الأريب، لياقوت ٥/ ٣١٨ - ٣٢٦، فوات الوفيات، للكتبى ٢/ ١٦٧ - ١٦٨.
Ritter, Geheimnisse ٣٧١ Anm.
الأعلام، للزركلى ٥/ ١٤١، معجم المؤلفين، لكحالة ٧/ ٢٣٦.

<span data-type="title" id=toc-1627>ب- آثاره:</span>
١ - كان ديوانه مائة ورقة (انظر: ابن النديم ١٦٧)، أورد ياقوت قطعا من شعره، وانظر أيضا: حماسة الظرفاء، الورقة ٩٨ ب، ١٠٩ ب، ١١٣ ب، المنتخب الميكالى، الورقة ١٩٠ ب، زهر الآداب، للحصرى، الفهرس، محاضرات الراغب، فى ١٢ موضعا، الدر الفريد، فى ١١ موضعا.
وذكر له ابن النديم (ص ١٥٠، وراجع: إرشاد الأريب، لياقوت ٥/ ٣١٩) غير ذلك:
٢ - ديوان الرسائل، ٣ - أخبار عمر بن أبى ربيعة، (وأثنى عليه ابن النديم، وياقوت الذى وقف عليه).
٤ - أخبار الأحوص.
٥ - مناقضات الشعراء.
٦ - كتاب المعاقرين (عن الزنج).

<span data-type="title" id=toc-1628>أبو بكر بن العلاف</span>
هو الحسن بن على بن أحمد النهروانى، كان شارعا، وراويا، فى بغداد، ونديما للمعتضد (٢٧٩/ ٨٩٢ - ٢٨٩/ ٩٠٢). توفى، مكفوف البصر، فى سن عالية، سنة ٣١٨، أو ٣١٩/ ٩٣١.
وظل مشتهرا بقصيدته التى رثى فيها هرّا كان له، (وعدد أبياتها فى الأصل ٦٥).','4','178',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (945,'<span data-type="title" id=toc-1629>أ- مصادر ترجمته:</span>
تاريخ بغداد ٧/ ٣٧٩ - ٣٨٠، وفيات الأعيان، لابن خلكان ١/ ١٧٢ - ١٧٤، نكت الهميان، للصفدى ١٣٩ - ١٤٢، شذرات الذهب، لابن العماد ٢/ ٢٧٧ - ٢٧٩، النجوم الزاهرة، لابن تغرى بردى ٣/ ٢٣٠ - ٢٣١، الأعلام، للزركلى ٢/ ٢٢٥، وبه ذكر لمصادر أخرى.

<span data-type="title" id=toc-1630>ب- آثاره:</span>
يقال: إن ديوانه، الذى يضم أخباره أيضا قد جمعه أحد أقاربه، وكان يحتوى على أربعمائة ورقة، (انظر: ابن النديم ١٦٨)، وترد قطع من شعره فى المصادر المذكورة آنفا، / انظر فضلا عن ذلك: حماسة الظرفاء، الورقة ٣٨ أ، ٥٠ ب- ٥١ أ، (١١ بيتا، لأبى بكر هبة الله بن الحسن العلاف)، المنتخب الميكالى، الورقة ٥٨ أ، الدر الفريد ١/ ١/ ص ١١٠، ٢/ الورقة ٢٢ ب، ٢٤ أ، ١٧١ أ، ١٧٦ أ، ٢١٣ ب، ٢١٦ أ.

<span data-type="title" id=toc-1631>أبو بكر الشّبلى</span>
هو دلف بن جحدر (وفى صيغة هذا الاسم خلاف)، ولد سنة ٢٤٧/ ٨٦١، فى سامراء، وكان من مريدى الحلّاج المتصوف. توفى فى بغداد سنة ٣٣٤/ ٩٤٦ (انظر:
تاريخ التراث العربى، I ,٦٦٠ السامرائى، شعراء سامراء ٥٠ - ٥١).
جمع كامل الشيبى قطعا من أشعاره، الواردة فى كتب الأدب، ونشرها ببغداد ١٩٦٧.
<span data-type="title" id=toc-1632>الزّاهى</span>

هو أبو القاسم، أو أبو الحسن، على بن إسحاق بن خلف الزاهى، ولد ببغداد، سنة ٣١٨/ ٩٣٠، وكان علويّا يحترف الاتّجار بالقطن. ونظم قصائد مدح فى «أهل البيت» خاصة، ولكنه مدح أيضا بعض أعيان عصره. كانت وفاته سنة ٣٥٢/ ٩٦٣، أو سنة ٣٦٠/ ٩٧١ (انظر: تاريخ بغداد ١١/ ٣٥٠، وفيات الأعيان، لابن خلكان ١/ ٤٤٨).','4','179',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (946,'ويمتدح فيه دعابته، وقدرته على إصابة الوصف.

<span data-type="title" id=toc-1633><span data-type="title" id=toc-1636>أ- مصادر ترجمته:</span></span>
يتيمة الدهر ١/ ٢٤٩ - ٢٥١، المنتظم، لابن الجوزى ٧/ ٥٩.
بروكلمان، ٩٠، I أعيان الشيعة، للعاملى ٤١/ ٦٥ - ٦٩، وثمة مصادر أخرى مذكورة فى: الأعلام، للزركلى ٥/ ٦٨، ومعجم المؤلفين، لكحالة ٨/ ٣٤.

<span data-type="title" id=toc-1634>ب- آثاره:</span>
أشار الثعالبى (١/ ٢٤٩، ٢٥٠) إلى أنه لم يجد أشعار الزاهى مجموعة، ولذلك اضطر إلى مراجعة الرواة أنفسهم، والاعتماد على مذكرات ومصادر أخرى مكتوبة، ذكر عناوين اثنين منها، ولم يعرف أيضا الخطيب البغدادى (١١/ ٣٥٠) سوى أشعار قليلة للزاهى، وظن أنه كان مقلّا، وقيل: إن ديوانه الكامل كان فى أربعة أجزاء (انظر وفيات الأعيان لابن خلكان ١/ ٤٤٨)، ووصل إلينا نحو ٣٠ بيتا فى: يتيمة الدهر ١/ ٢٤٩ - ٢٥١، وتاريخ بغداد ١١/ ٣٥٠، ونهاية الأرب، للنويرى ١١/ ١٨٢./

<span data-type="title" id=toc-1635>الناشئ الأصغر</span>
هو أبو الحسين (أو أبو الحسن) على بن عبد الله بن وصيف البغدادى الحلّاء، ولد سنة ٢٧١/ ٨٨٤ ببغداد، حيث كان لأبيه دكان عطّار، تتلمذ لأبى سهل إسماعيل بن على النوبختى، وغيره من علماء بغداد المعروفين، واشتهر متكلما، وشاعر مديح. وتوفى سنة ٣٦٥/ ٩٧٥، أو سنة ٣٦٦/ ٩٧٧، ببغداد، ودفن فى ناحية الكاظمية الحالية.
وفضلا عن مدحه بعض أعيان عصره، فقد نظم، باعتباره علويّا، مدائح كثيرة فى أهل البيت.
أ- مصادر ترجمته:
يتيمة الدهر ١/ ٢٤٨ - ٢٤٩، الفهرست، لابن النديم ١٧٨، إرشاد الأريب، لياقوت ٥/ ٢٣٥ - ٢٤٤، وفيات الأعيان، لابن خلكان ١/ ٤٤٧ - ٤٤٨، لسان الميزان، لابن حجر ٥/ ٢٣٨ - ٢٤٠.
BR. Sl. ٨٨١, Anm. Blachere, Un Poetearabe ... , S. ١٣١, ١٣٢','4','180',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (947,'محمد كامل حسين، فى الأدب المصرى الإسلامى ٢٥٠، ج. فان إس:
J. van Ess. Fruhemu''tazilitx che Haresiographie. Zweides Nasial- akbar (gest. ٢٩٣ H.) , Beirut ١٩٧١, s. ٣:
الأعلام، للزركلى ٥/ ١١٩، وثمة مصادر أخرى مذكورة فى: معجم المؤلفين، لكحالة ٧/ ١٤٢.

<span data-type="title" id=toc-1637>ب- آثاره:</span>
يبدو أن الناشئ عمل ديوانه بنفسه (انظر: الذريعة ٩/ ٧٤٥، ١١٥٢)، وروى أنه أملاه فى الكوفة (انظر: إرشاد الأريب، لياقوت ٥/ ٢٣٩ - ٢٤٠)، وكان ذلك إمّا قبل سنة ٣١٦/ ٩٢٨، عند ما التقى هنالك فعلا بالمتنبى وهو لا يزال شابّا (انظر: بلاشير، فى الموضع المذكور، ص ٣٤، الهامش)، وإما فى سنة ٣٢٥/ ٩٣٧ (انظر: إرشاد الأريب، لياقوت ٥/ ٢٣٩)، وعندئذ لم يلتق بالمتنبى، الذى كان فى ذلك الحين بالشام (انظر: بلاشير، فى الموضع المذكور، ص ٨٧ وما بعدها)، ولم يصل إلينا ديوانه، وثمة مجموعة لقطع من شعره من عمل محمد السماوى فى النجف، بمكتبة آية الله الحكيم (انظر: الفهرس ١٩٦٢، ص ١٤٧)، وفضلا عن ذلك ترد له أبيات فى: المنتخب الميكالى، الورقة ٨٨ ب، والدر الفريد ١/ ١/ ص ١٦٢، وانظر أيضا:
Schawahid- Indices ٣٤٢
وفهرس أبيات الناشئ الأكبر ص ١٥٦ - ١٦١، من كتاب فان إس، المذكور آنفا، فمن الجائز أن من بينها أبيات للناشئ الأصغر.
ويذكر له من بين سائر كتبه (انظر: ابن النديم ١٧٨، وفهرست الطوسى ١١٥ - ١١٦) «كتاب الإمامة» (أو «كتاب فى الإمامة»)، (انظر: الرجال، للنجاشى ٢٠٨، وراجع: هدية العارفين، للبغدادى ٦٨١ - ٦٨٢، والذريعة ٢/ ٣٣١).
وذكر ياقوت شاعرا آخر له نفس اللقب، هو الناشئ الأحصّى، من أهل أحصّ، بالقرب من حلب، والذى زار سيف الدولة، كما فعل الناشئ الأصغر (انظر:
معجم البلدان M.Canard ,Suyfal Daula ,Algier ١٩٣٤ ,S.٣٥٣ - ٣٥٥، ١٥٣ - ١٥٢ /١ راجع: حماسة ابن الشجرى، الهامش على رقم ٩٢٩).
ورويت أبيات فى الصاحب بن عباد، ل: إسماعيل الناشئ (انظر: المنتخب','4','181',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (948,'الميكالى، الورقة ٢١٤ ب- ٢١٥ أ، زهر الآداب، للحصرى ٢٧٠، نهاية الأرب، للنويرى ٣/ ١١٤)./

<span data-type="title" id=toc-1638>ابن البقّال</span>
هو أبو الحسن على بن يوسف البغدادى، كان نديما للوزير المهلّبى، ذهب فى شعره مذهب النامى. كانت وفاته قبل سنة ٣٨٠/ ٩٩٠.
ترد قطع من شعره بعضها طويل، (مجموعها ٩٠ بيتا)، عند ياقوت، فى: إرشاد الأريب ٥/ ٥٠٧ - ٥١٣.

<span data-type="title" id=toc-1639>أبو إسحاق الصابئ</span>
هو إبراهيم بن هلال الحرّانى، المولود سنة ٣١٣/ ٩٢٥، وصاحب ديوان الإنشاء فى عهد معز الدولة، وعز الدولة، اشتغل أيضا بالرياضة والفلك (انظر: تاريخ التراث (V ,٣١٤ توفى سنة ٣٨٤/ ٩٩٤ (انظر كذلك: باب الأدب) كانت أشعاره مجموعة فى ديوان (انظر: ابن النديم ١٣٤)، وكانت لا تزال نسخة منه معروفة فى حلب، فى القرن السابع/ الثالث عشر، (انظر: ب. سباط، فى:
P. Sbath, MIE ٤٩/ ١٩٦٤/ ٢٢, No. ٣٨٦
ويعزى إلى الشريف الرضى: «كتاب مختار شعر أبى إسحاق الصابى» (انظر: الرجال، للنجاشى ٣١١).
وردت له أشعار وقطع فى: يتيمة الدهر ١/ ٣٥، ١١٣، ٢٦٨ - ٢٦٩، ٢٧١، ٢/ ٢٤٢ - ٢٤٤، ٢٥٧ - ٣٠٧، وقصيدته النونية فى ٢/ ٣٠٠ - ٣٠٢، وكذلك فى: جمهرة الإسلام، الورقة ١٨٦ أ- ١٨٧ أ، (راجع:
خليل مردم، فى: مجلة المجمع العلمى العربى بدمشق ٣٣/ ١٩٥٨/ ١٣). وله أيضا أبيات فى: المنتخب الميكالى فى ١٢ موضعا، حماسة الظرفاء، الورقة ١٠٦ أ، ١٤٧ ب، محاضرات الراغب، زهر الآداب، للحصرى، بهجة المجالس، لابن عبد البر، إرشاد الأريب، لياقوت ١/ ٣٢٤ - ٣٥٨، نهاية الأرب، للنويرى، الدر الفريد، فى نحو ٣٠ موضعا، معاهد التنصيص، وانظر أيضا:
Schawahid- Indices ٣٢٤, ٣٤٥
وراجع: شعراء بغداد، للخاقانى ١/ ١٤٨ - ١٩١','4','182',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (949,'<span data-type="title" id=toc-1640>ابن الحجّاج</span>
هو أبو عبد الله الحسين بن أحمد بن محمد النّيلى البغدادى، أحد أبناء أسرة من الكتاب والموظفين والشيعة، ولد نحو سنة ٣٣٠/ ٩٤١ فى بغداد، وبعد أن تلقى تعليمه، وخدم مدة قصيرة فى ديوان الإنشاء، أصبح شاعر مديح للوزراء والأعيان من البويهيين، وكان محتسب بغداد لزمن قصير، فى أيام الوزير ابن بقية (استوزر من سنة ٣٦٢/ ٩٧٣ - ٣٦٦/ ٩٧٧). وتوفى سنة ٣٩١/ ١٠٠١، ودفن فى بغداد، عند مشهد موسى/ الكاظم بن جعفر الصادق. ومما يدل على مبلغ تقدير أشعاره تلك الأثمان العالية التى دفعها الناس لديوانه. وأن الشريف الرضى، بذاته، قام باختيار نخبة من شعره، ونظم قصيدة رثاه بها بعد وفاته (انظر:
(MEZ, Renaissance ٢٥٩
وتتضح أيضا شهرته من أن ابن سناء الملك، (المتوفى سنة ٦٠٨/ ١٢١١، انظر:

بروكلمان، (٢٦١، I قد طالب فى كتابه عن صناعة الموشح «دار الطراز»، أن تكون «الخرجة»، أى القفل الأخير من الموشح، على طريقة ابن قزمان من قبل اللحن، وعلى طريقة ابن الحجاج من قبل السّخف H.Ritterin: Oriens ٢ /١٩٤٩ /٢٧٢ وراجع له:
S. M. Sternin: Andalus ١٣/ ١٩٤٨/ ٣٤٤, Geheimnisse ٣١٤, Anm
وانظر بخلاف ذلك تفسيرا مغايرا لهذا النص عند:
G. ar- Rikabi, LAPoesieprofanes ousles Ayyubides, Paris ١٩٤٩, S. ١٧٧.
ولقى شعر ابن الحجاج فى المجون مقلدا بين شعراء المرابطين هو محمد بن مسعود البجّاني، (أوائل القرن الخامس/ الحادى عشر)، (انظر:
E. Garcia Gomez, Nuevostestimonio ssobrea Sevilladelospoet asmusulmanesin: Andalus ١٤/ ١٩٤٩/ ٤٤ - ١٧٤
يأتى ذكره ص ٦٩٦)','4','183',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (950,'<span data-type="title" id=toc-1641>أ- مصادر ترجمته:</span>
يتيمة الدهر ٣/ ٣١ - ١٠٤، مسالك الأبصار، لابن فضل الله ١٥/ ١٣١ ب- ١٣٥ ب، تاريخ بغداد ٨/ ١٤ - ١٥، المنتظم، لابن الجوزى ٧/ ٢١٦ - ٢١٨، وفيات الأعيان لابن خلكان ١/ ١٩٤ - ١٩٥، إرشاد الأريب، لياقوت ٤/ ٦ - ١٦، معاهد التنصيص ٣/ ١٨٨ - ٢٠٠، وانظر بروكلمان، فى الأصل، I ٨١ - ٨٢ وفى الملحق I، ١٣٠ - ١٣١ وانظر: مرجليوت D.S.Margoliouth فى: دائرة المعارف الإسلامية، ط.
أوربية أولى ٢/ ٤٠٤ - ٤٠٥، وشارل بيلا ط. أوربية ثانية، Ch.Pellat ٨٧١ - ٨٧٠ /٣ أعيان الشيعة، للعاملى ٢٥/ ٨١ - ١٦٠ وثمة مصادر أخرى فى: الأعلام، للزركلى ٢/ ٢٤٩، ومعجم المؤلفين، لكحالة ٣/ ٣١٢ - ٣١٣.

<span data-type="title" id=toc-1642>ب- آثاره:</span>
قيل أن ديوانه الكبير (انظر: اليتيمة ٣/ ٣٢، فهرست ابن خير ٤٠٦) كان فى عشر مجلدات، (انظر:
ابن خلكان ١/ ١٩٤، إرشاد الأريب، لياقوت ٤/ ٦)، ويبدو أنه بقى محفوظا لنا إلى حدّ بعيد، ولم تصل إلينا الاختيارات التى صنعها الشريف الرضى (يأتى ذكره ص ٥٩٧).
المخطوطات: لندن، المتحف البريطانى، الإضافات ٧٥٨٨ (الجزء الثانى، من قافية الدال- الراء، ١٧٤ ورقة، نسخ فى سنة ٥٤٤ هـ، انظر الفهرس، ص ٢٧٨، رقم ٥٨٤)، المتحف البريطانى، مخطوطات شرقية ٤٥٩١ (من قافية الدال- الراء، يتفق فى بعضه مع المخطوط السابق إضافات ٧٥٨٨، ٢٤٧ ورقة، من القرن السادس الهجرى، انظر: الملحق، رقم ١٠٤٨)، جوتنجن، مخطوطات عربية ٧٦/ ٢ (من قافية الطاء- اللام، الورقة ١٥٢ - ٢٦٠)، تشستربيتى ٣٧٨٢ (٢٢٩ ورقة، نسخ فى ٦٢٠ هـ)، دار الكتب بالقاهرة، أدب ٧٣٤٢ (٢٣٠ ورقة، نسخ فى ٦٢٠ هـ، انظر: فهرس معهد المخطوطات العربية ١/ ٤٥٠، ومنه نسخة مصورة، انظر: الفهرس، طبعة ثانية ٧/ ١٢٨)، وثمة نسخة عنه بالدار ١٠٤٤٦ ز (كتبت سنة ١٣٥٥ هـ، انظر فهرست المخطوطات ملحق ١/ ٣٢٤)، التيمورية، شعر ٦٠٦ (قافية الباء، ٣٢ ورقة، انظر فهرس معهد المخطوطات العربية ١/ ٤٥٠)، التيمورية، شعر ٤٦٨، ٦٥٧ (انظر Ch.Pellatein: EIIII ,٧٨٠ وراجع (H.Ritterin: Oriens ٢ /١٩٤٩ /٢٧٢ Geheimnisse ٣١٤ , الأوقاف ببغداد ٥٧٣٠ (من قافية الميم إلى آخره، نسخ فى ١٣١٣ هـ، انظر: أسعد طلس، رقم ٢٠٨٢)، الظاهرية بدمشق. عام ٨٥٦٣ (من قافية التاء- الحاء، ٧١ ورقة، من القرن الخامس- السادس الهجرى، انظر فهرس عزة حسن ٢/ ١٣٣ - ١٣٤، ويحتمل أنه كان فيما مضى عند عبيد بدمشق، انظر (Geheimnisse ٣١٧ ,H.Ritterin: Oriens ٢ /١٩٤٩ /٢٧٢','4','184',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (951,'مكتبة جامعة إستنبول) A ٣١٧٧ من قافية الميم إلى آخره، ١٧٣ ورقة، نسخ سنة ١٢٨٩ هـ، عن أصل يرجع إلى سنة ٦٢٠ هـ، انظر H.Ritterin: Oriens ٢ /١٩٤٩ /٢٧٢ فهرس معهد المخطوطات العربية ١/ ٤٥٠)، ومنه مصورة فى أوبسالا/، رقم ٤٠٥٢ (انظر:
(H. Ritter Geheimnisse ٣١٤
وهبى ١٥١٦ (الورقة ٥١ أ- ٨٠ ب، نحو ألف بيت من الجزء السابع، وليست مرتبة على الحروف فى هذه الصنعة، انظر:
(H. Ritterin: Oriens ٢/ ١٩٤٩/ ٢٧٢
وثمة قطع أخرى من شعره فى المنتخبات الشعرية، كيتيمة الدهر (٣/ ٣٢ - ١٠٤)، وزهر الآداب للحصرى، وحماسة الظرفاء، وحماسة ابن الشجرى، وبهجة المجالس، لابن عبد البر، والحماسة المغربية، والمنتخب الميكالى، وجمهرة الإسلام (انظر: مجلة المجمع العلمى العربى بدمشق ٣٣/ ١٩٥٨/ ٨)، وما إلى ذلك.
ولأبى القاسم هبة الله بن الحسين الأسطرلابى، (المتوفى سنة ٥٣٤/ ١١٤٠) مختصر منه بعنوان: «درّة التاج من شعر ابن الحجاج»، (انظر: إرشاد الأريب، لياقوت ٧/ ٢٤١ - ٢٤٢، كشف الظنون ٧٦٥، وبحسبه يتألف من ١٤١ بابا، مقسمة على فنون الشعر)، يوجد مخطوطا فى: باريس ٥٩١٣ (١٩٣ ورقة، نسخ فى ٥٥٩ هـ، نسخة ابن الخشاب وتعليقاته [توفى سنة ٥٦٧/ ١١٧٢، انظر: بروكلمان، [، I ٤٩٣ انظر:
Blochet ١٣٩
، راجع فايدا ٣٠٢)، وكتب عنه على الطاهر، the secomplementaire باريس ١٩٥٣، (انظر:
شارل بيلا Ch.Pellat فى: دائرة المعارف الإسلامية الطبعة الأوربية الثانية ٣/ ٨٧٠ - ٨٧١).
ولجمال الدين محمد بن نباتة، (المتوفى سنة ٦٧٨/ ١٣٦٦، انظر: بروكلمان (II ,١١ منتخب بعنوان:
«تلطيف المزاج من شعر ابن الحجاج»، يوجد مخطوطا، فى: كوبناهجن ٢٦٠ (١٢٢ ورقة)، وعنه «لطائف التلطيف»، لتقى الدين بن حجّة الحموى، (المتوفى سنة ٨٣٧/ ١٤٣٤، انظر: بروكلمان، (، II ١٦ يوجد مخطوطا، فى: جوتا ٢٢٣٥/ ١ (الورقة ١ - ٣٧).
«ملح من شعر ابن (ال) - حجاج»، لمجهول، فى الظاهرية، عام ٥٨٦١ (٢٩ ورقة، مخطوط حديث، انظر: فهرس عزة حسن ٢/ ٣٨٥ - ٣٨٦).

<span data-type="title" id=toc-1643>السّلامى</span>
هو أبو الحسن محمد بن عبد الله (أو عبيد الله) بن محمد القرشى المخزومى','4','185',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (952,'السلامى، كان مولده سنة ٣٣٦/ ٩٤٨، سكن بغداد، والموصل، وفارس أيضا، وفيها اتصل بالصاحب بن عباد، وعضد الدولة، توفى سنة ٣٩٣/ ١٠٠٣.

<span data-type="title" id=toc-1644><span data-type="title" id=toc-1647>أ- مصادر ترجمته:</span></span>
يتيمة الدهر ٢/ ٣٩٦ - ٤٣١، ٣/ ١٢٩، ٢٢٢ - ٢٢٣، تاريخ بغداد ٢/ ٣٣٥، مسالك الأبصار، لابن فضل الله ١٥/ الورقة ٩٦ أ- ١٠٣، الوافى بالوفيات، للصفدى ٣/ ٣١٧ - ٣١٩، الأعلام، للزركلى ٧/ ١٠٠، مع ذكر مصادر أخرى.

<span data-type="title" id=toc-1645>ب- آثاره:</span>
قيل: إن ديوانه كان نحو ٥٠٠ ورقة (انظر: الفهرست، لابن النديم ١٦٨)، نشر صبيح رديف قطعا من شعره، جمعها من كتب الأدب، بغداد ١٩٧١، وانظر أيضا: المنتخب الميكالى، الورقة ١٠ ب، ٥٠ أ، سفينة الأدباء، الورقة ١٨٠ ب- ١٨٨، الدر الفريد ٢/ الورقة ٥٠ أ، ١٩٦ ب، ٢١٤ أ، ٢٢٣ أ.

<span data-type="title" id=toc-1646>ابن نباتة السّعدى</span>
هو أبو نصر عبد العزيز بن عمر بن محمد السعدى التميمى العراقى، ولد سنة ٣٢٧/ ٩٣٩، وأقام زمانا عند/ سيف الدولة فى حلب، وعند ابن العميد والصاحب ابن عباد فى فارس، توفى سنة ٤٠٥/ ١٠١٥، ببغداد.
أ- مصادر ترجمته:
تاريخ بغداد ١٠/ ٤٦٦ - ٤٦٨، وفيات الأعيان، لابن خلكان Blachere ,Unpoetearabe .. ,S.١٣٣ ,١٤١، ٣٧٢ - ٣٧٠ /١ انظر: بروكلمان I ,٩٥ والملحق I ,١٥٢ Ritter ,Geheimnisse ٩٤ ,Anm. وفى مواضع أخرى، الأعلام، للزركلى ٤/ ١٤٨ - ١٤٩، معجم المؤلفين، لكحالة ٥/ ٢٥٥.','4','186',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (953,'<span data-type="title" id=toc-1648>ب- آثاره:</span>
قيل: إن ديوانه كان نحو ٤٠٠ ورقة (انظر: الفهرست، لابن النديم ١٦٩)، وأتى أبو نصر سهل بن المرزبان، (المتوفى نحو سنة ٤٢٠/ ١٠٣٠) بنسخة منه إلى فارس (انظر: يتيمة الدهر ٢/ ٣٨٠).
المخطوطات: الأحمدية بتونس ٤٥٧١ (٢٠٣ ورقة، من القرن الخامس الهجرى)، نور عثمانية ٣٨٠١ (٢٠٩ ورقة، مخطوط قديم جدّا)، دار الكتب بالقاهرة، أدب ٥٢ ش (١٩٣ ورقة، نسخة حديثة، انظر:
الفهرس، طبعة ثانية ٣/ ١١٢، فهرس معهد المخطوطات العربية ١/ ٤٥٤)، ويوجد منتخب فى: أسعد ٣٥٤٢/ ١٣ (الورقة ١٢٦ - ١٣٠، انظر:
(M. Bergein: BEO (Damaskus) ١٦/ ١٩٥٨/ ٦٠ - ٢١
وثمة مقامة فى برلين، ١٩٦٦ Spr) ٨٥٣٦ الورقة ٤٠ ب- ٤٦ أ، نسخت نحو سنة ٨٠٠ هـ)، وترد له قصائد وقطع فى: يتيمة الدهر ١/ ٣١ - ٣٢، ٢/ ٣٨٠ - ٣٩٦، المنتخب الميكالى، الورقة ١٨ ب، ٢١ ب، ١٣٩ أ، ٢٠٣ ب، ٢٢٠ أ، حماسة الظرفاء، الورقة ٥٦ ب، محاضرات الراغب ١ - ٣ فى تسعة مواضع، الحماسة المغربية، الورقة ٥٥ أ، ٦٦ ب، حماسة ابن الشجرى، رقم ٩٤٢، الدر الفريد فى ٤٢ موضعا، انظر أيضا: .
