INSERT INTO [b143162] VALUES (1042,'<span data-type="title" id=toc-1942>ب- آثاره:</span>
«- بقى ديوانه محفوظا فى مخطوطات كثيرة: دار الكتب بالقاهرة، أدب ٦٦١١ (٧٨ ورقة، نسخ فى ١١٨١ هـ، انظر الفهرس، طبعة ثانية ٧/ ١٣١)، دار الكتب بالقاهرة ١٨٧٠ (نسخ فى ١٠٧٢ هـ)، أدب ٢٢٠٤ (انظر الفهرس، طبعة ثانية ٣/ ١١٤)، أدب ١٩٣٠٧ ز (٩٦ ورقة، انظر الفهرس ملحق ١/ ٣٢٦).
طلعت، أدب ٤٦٣٦ (ص ١ - ١١٢)، طلعت ٤٦٠٤ (٩٠ ورقة، نسخ فى ١١١٣ هـ)، الظاهرية، عام ٣٣٢٨ (الورقة ١٢٣ - ١٨٨، نسخ فى ١٠٢٥ هـ، انظر فهرس عزة حسن ٢/ ٢٣٦ - ٢٣٧)، العباسية بالبصرة، أ- ١١٨ (٢٠٠ صفحة، من القرن ١٢ الهجرى، انظر على الخاقانى فى مجلة المجمع العلمى العراقى ٨/ ٩٦١/ ٢٣٠)، الأوقاف ببغداد ٥٧٥٧/ ١٠ (آخره ناقص، انظر طلس ص ٣٠٠، رقم ٣٤٠٤)، جامع الباشا بالموصل (انظر مخطوطات الموصل لداود الحلبى ص ٤٧، رقم ١٥). الحسنية بالموصل (نسخ فى ١٠٠٣ هـ، الفهرس السابق ص ١٣٣، رقم ٢٠٤)، يحيى باشا، بالموصل (انظر الفهرس نفسه ص ٢٢٨، رقم ٣)، الأحمدية بتونس ٤٥٦٦ (من القرن السابع الهجرى، مخطوط جيد جدا)، الأحمدية ٤٥٦٧، ٤٥٦٨ (نسختان حديثتان)، الأوقاف بالرباط (نسخ فى ١٠٠٢ هـ، راجع li stedemanuscripts ص ٥٣، رقم ٢٣٠) الكتانى ٢٦٩ (الورقة ١ - ٢٨٣)، مراكش ١٤٣ (نسخ فى ١١١٤ هـ)، / بنكيبور ٢٥١٦ (١٠٧ ورقة، نسخ فى ١١٥٦ هـ، انظر الفهرس ٢٣/ ١٩ - ٢١). برلين ٧٥٨٥/ ١ (الورقة ١ - ٥٧.
نسخ فى ١١٤٦ هـ)، برلين ٧٥٨٦ (٨٤ ورقة، نسخ فى ١٠٤١ هـ)، المتحف البريطانى، مخطوطات شرقية ٣١٦١ (٨٩ ورقة، من القرن ١٢ الهجرى، انظر الفهرس رقم ١٠٤٦)، المتحف البريطانى، مخطوطات شرقية ٣٧٦٧ (١٠٩ ورقة، من القرن السابع الهجرى، انظر الفهرس رقم ١٠٤٧)، بودليانا بأكسفورد ٨٦) hunt.٥٢٧، ورقة، نسخ فى ١٠٦٧ هـ، انظر يورى ص ٢٦٦ - ٢٦٧، رقم ١٢٩١)، مدريد ٢١٠ (٩١ ورقة). باريس ٣١٠٨ (١٨٩ ورقة، نسخ فى ٨٥٨ هـ)، باريس ٦٠٣١ (٨٦ ورقة، من القرن ١٢ الهجرى)، باريس ٦٠٣٤ (قطعة، الورقة ١١٩ - ١٢٠، نسخ فى ١١٨٠ هـ، راجع فايدا ٣٠٤)، المتحف الآسيوى بلنينجراد ١٢٢ (١٣٨ ورقة)، المتحف الآسيوى ١٢٣ (١٣٥ ورقة، نسخ فى ١٠٢٠ هـ، انظر v rosen ,noticessommaires. ص ٢٣٢ - ٢٣٣، رقم ٢٨٠، ٢٨١، مجلة معهد المخطوطات العربية ٦/ ١٩٦٠/ ٣٢١). وثمة بضعة قصائد فى أسعد ٣٥٤٢/ ٢٤ (الورقة ١٦٧ - ١٧٨، نسخت فى ٥٦٤ هـ، راجع، (m.bergein: beodamaskus ١٦ /١٩٥٨ - ٦٠ /٢١ قصيدة فى جعفر بن على الحمدونى فى جمهرة الإسلام، للشّيزرى. الورقة ١٩٦ أ- ١٩٧ أ (راجع خليل مردم، فى مجلة المجمع العلمى العربى بدمشق ٣٣/ ١٩٥٨/ ١٤)، وترد له أيضا قصائد وقطع فى: زهر الآداب، للحصرى، وعنوان المرقصات، لابن سعيد، وسفينة الأدباء، والحماسة المغربية، وله أبيات كثيرة فى: الدر الفريد، وانظر أيضا: مختارات من الشعر الأندلسى، لنيكل ١٦ - ١٧.','5','9',NULL,NULL,'0');
INSERT INTO [b143162] VALUES (1043,'طبع ديوانه فى: بولاق سنة ١٢٧٤، ومع شرح لمحمد أنيس، فى بيروت ١٨٨٤، وطبع فى بيروت سنة ١٣٢٦ (انظر سركيس ٢٧١)، ومع شرح لعلى الزاهدىّ بعنوان «تبيين المعانى فى شرح ديوان ابن هانى» فى حيدرآباد ١٣٢٦، والقاهرة ١٣٥٢ (انظر فهرس مشار ٣٧٢، ٣٨٢)، ومع تقديم لكرم البستانى فى بيروت، دار صادر ١٩٦٤، وترجمت له بعض قصائد وأبيات مفردة عند a.v.kremer فى الموضع المشار إليه ص ٤٨٣ - ٤٩٤، وعند
R. p. dewhust, abutammanandlbnh aniin: jras ١٩٢٦, ٦٣٩ - ٦٤٢ nykl, poetry ٢٩
٢ - «تاريخ ابن هانى». ذكره صاحب كشف الظنون ٢٨٠.

<span data-type="title" id=toc-1943>تميم بن المعزّ الفاطمىّ</span>
هو أبو على تميم بن المعز بن المنصور الفاطمى، وكان أكبر أبناء الخليفة المعز لدين الله (حكم من ٣٤١/ ٩٥٣ إلى ٥٦٣/ ٩٧٥). ولد سنة ٣٣٧/ ٩٤٨ فى المهدية، ونشأ بها، وبالمنصورية، واتجه سنة ٣٦٢/ ٩٧٣ مع المعز إلى الفسطاط (انظر. (el ٢ ll ,٨٥٢ أقصى من الولاية، وعاش حياة الأديب والشاعر المثقف. كان صديقا لبعض أفراد أسرة الرّسّى العلوية المعروفة بالشعر، من ولد ابن طباطبا الرّسّى. توفى سنة ٣٧٤/ ٩٨٤ أو سنة ٣٧٥/ ٩٨٥.
وأشعاره التى وصلت إلينا فى ديوانه أغلبها فى مدح وتهنئة أبيه المعز، وأخيه العزيز، وفيها إلى ذلك أشعار فى الغزل، ووصف الطبيعة، والطرد، والافتخار. «وقد قورن بابن المعتز؛ فكان كلا الأميرين فى وضعين متشابهين، وكان لهما هوايات متشابهة، وثمة كذلك وجوه شبه/ فى صورة شعرهما، وربما أيضا فى ضروب من التأثر.
إلا أن الأمير العباسى كان أعلى موهبة». (هـ. ريتر فى. (oriens ١١ /١٩٥٨ /٣١١

<span data-type="title" id=toc-1944>أ- مصادر ترجمته:</span>
