INSERT INTO [b143164] VALUES (327,'٥ - و ٦ - طهران: مكتبة أصغر مهدوي ٣٩٧ (١ أ- ١٥٦ ب، ٧٣٢ هـ.) و ٣٣٦ (١١ ب- ١٦٦ أ، القرن الثاني عشر الهجري) (١).
٧ - هناك نسخة ضمن ممتلكات الدكتور نظام الدين، حيدرآباد وقد أهديت لمعهد الشرق، الألمانى، بيروت.
إن الكتب السبعين التي ترجمها على ما يظهر ren alduscremonensis إلى اللاتينية، نشرها برتلو بالرجوع إلى المخطوطة اللاتينية في المكتبة الوطنية فى باريس رقم ٧١٥٦، في مجلة
mem. del''ac. dessciences) archeolgieethist oiredessciences
٤٩/ ١٩٠٦/ ٣١٠ - ٣٦٣) . هناك مخطوطات لاتينية أخرى، كراوس i ص ٤٢. وانظر ما درسه e.o.v.lippmann النشأة entstehung ص ٣٦٧ - ٣٦٩، روسكا: «الكتب السبعون لجابر بن حيان d iesiebzigbucherd esgabiribnhajjan، «مجلة دراسات فى تاريخ الكيمياء، stud.z.gesch.d.chemie تكريما ل، e.o.v.lippmann برلين ١٩٢٧ م ص ٣٨ - ٤٧، وانظر كراوس، i ص ٤٣.

<span data-type="title" id=toc-390>١ - «كتاب اللاهوت»:</span>
الموضوع الرئيسى فيه عمل الإكسير من المواد العضوية. المخطوطات: جار الله ١٥٥٤ (٢ أ- ٧ أ)، تيمور: طبيعيات ٦٧ (ص ١ - ٦)، انظر كراوس رقم ١٢٣؛ كذلك في بورسه: حسين شلبى ٧٤٣ (ص ١٩ - ٢٧)، مكتبة جامعة اسطانبول أ ٦٣١٤/ ١، قاستامونو ٨١١/ ١ (غير كاملة)، انظر كذلك فهرس المخطوطات.١٥٣ - ١٥٢، iii ,iv

<span data-type="title" id=toc-391>٢ - «كتاب الباب»:</span>
يبحث في الإكسير بشكل رئيسي: جار الله ١٥٥٤ (٧ أ- ١٤ أ). القاهرة:
_________
(١) ليس صحيحا ما جاء في، rima م ٦ ص ٧٢ من أن مخطوطة لهذا المجموع موجودة في مكتبة ملك فى طهران.','1','323',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (328,'دار، طبيعيات ٧٣١ (١٢٣ أ- ١٢٤ ب)؛ تيمور: طبيعيات ٦٧ (ص ٦ - ١٤) وانظر كراوس رقم ١٢٤، وكذلك في بورسه: حسين شلبى، ٧٤٣ (ص ٢٧ - ٤٠) مكتبة جامعة اسطانبول أ ٦٤١٢/ ٢، قاستامونو ٨١١/ ٢ وانظر كذلك فهرس المخطوطات.١٠١، iii ,iv

<span data-type="title" id=toc-392>٣ - «كتاب الثلاثين كلمة»:</span>
مخطوطة تيمور تحمل العنوان: «كتاب الإيضاح المعروف بالثلاثين كلمة»، جار الله ١٥٥٤ (١٤ أ- ٢٠)، القاهرة: دار، طبيعيات ٧٣١ (١٢٤ ب- ١٢٦ ب)، تيمور: طبيعيات ٧٦ (ص ١٤ - ٢٠، انظر كراوس رقم ١٢٥)؛ كذلك في بورسه:
حسين شلبى ٧٤٣ (ص ٤٠ - ٤٧)، مكتبة جامعة اسطانبول أ ٦٣١٤/ ٣، قاستامونو ٨١١/ ٣ وانظر كذلك في فهرس المخطوطات.١١٠ iii ,iv

<span data-type="title" id=toc-393>٤ - «كتاب المنى»:</span>
في عمل الإكسير في ٢٥ يوما، جار الله ١٥٥٤ (٢٠ ب- ٢٥ ب)، القاهرة:
دار، طبيعيات ٧٣١ (١٢٦ ب- ١٢٩ أ)، تيمور: طبيعيات ٦٧ (ص ٢٠ - ٢٨) وانظر كراوس رقم ١٢٦؛ وكذلك فى بورسه: حسين شلبى ٧٤٣ (ص ٤٧ - ٥٨)، مكتبة جامعة اسطانبول أ ٦٣١٤/ ٣، قاستامونو ٨١١/ ٤؛ انظر كذلك فهرس المخطوطات.١٦٣، iv ,iii

<span data-type="title" id=toc-394>٥ - «كتاب الهدى»:</span>
في عمل الإكسير في ٢٠ يوما، جار الله ١٥٥٤ (٢٥ ب- ٣٣ أ)، القاهرة:
دار، طبيعيات ٧٣١ (١٣٠ ب)، تيمور: طبيعيات ٦٧ (غير كاملة)، انظر كراوس رقم ١٢٧؛ كذلك فى بورسه: ح. شلبي ٧٤٣ (ص ٥٨ - ٦٨)، مكتبة جامعة اسطانبول أ ٦٣١٤/ ٥، قاستامونو ٨١١/ ٥، انظر كذلك فهرس المخطوطات.١٦٩، iv ,iii','1','324',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (329,'<span data-type="title" id=toc-395>٦ - «كتاب الصفات»:</span>
وقد ذكر فيه ما يتصل بعلاقة كتب جابر بأعمال الفلاسفة القدامى من أمثال فيثاغورس، آريوس، سقراط، وبالمناسبة فقد شرح كذلك عمل الإكسير في ١٥ يوما، جار الله ١٥٥٤ (٣٣ أ- ٣٨ ب)، القاهرة: دار، طبيعيات ٧٣١ (١٣٠ ب غير كاملة) انظر كراوس رقم ١٢٨؛ كذلك فى بورسه: حسين شلبى ٧٤٣ (ص ٦٨ - ٧٧) مكتبة جامعة اسطانبول، ٦/ ٦٣١٤ a. قاستامونو: ٨١١/ ٦، انظر كذلك فهرس المخطوطات.١٣١، iv ,iii

<span data-type="title" id=toc-396>٧ - «كتاب العشرة»:</span>
عمل الإكسير في عشرة أيام، جار الله ١٥٥٤ (٣٨ ب- ٤٦ ب)، تيمور، طبيعيات ٦٧ (ص ٤١ - ٤٦)، انظر كراوس رقم ١٢٩؛ كذلك فى بورسه: ح.
شلبي ٧٤٣ (ص ٧٧ - ٨٧) مكتبة اسطانبول، ٧/ ٦٣١٤ a. قاستامونو ٨١١/ ٧ وانظر كذلك نظر فهرس المخطوطات.١٣٦، iv ,iii

<span data-type="title" id=toc-397>٨ - «كتاب النعوت»:</span>
في عمل الإكسير في تسعة أيام، جار الله ١٥٥٤ (٤٦ ب- ٥٢ ب)، تيمور:
طبيعيات ٦٧ (ص ٤٦ - ٥٢) وانظر كراوس رقم ١٣٠؛ كذلك فى بورسه: ح.
شلبي ٧٤٣ (ص ٨٧ - ٩٥)، مكتبة جامعة اسطانبول، ٨/ ٦٣١٤ a. قاستامونو ٨١١/ ٨، وانظر كذلك نظر فهرس المخطوطات.١٦٦، iv ,iii

<span data-type="title" id=toc-398>٩ - «كتاب العهد»:</span>
في عمل الإكسير في ثمانية أيام، جار الله ١٥٥٤ (٥٢ ب- ٥٧ ب). القاهرة:
دار، طبيعيات ٧٣١ (٦٧ أ- ٦٨ ب)، تيمور: طبيعيات ٦٧ (ص ٥٢ - ٥٥)، انظر كراوس رقم ٣١؛ بورسه: ح. شلبي ٧٤٣ (ص ٩٥ - ١٠٢) مكتبة جامعة اسطانبول، ٩/ ٦٣١٤ a. قاستامونو ٨١١/ ٩ وانظر أيضا فهرس المخطوطات.١٣٧، iv ,iii هناك مخطوطة أخرى في الرباط، ملك ٦٩٢٣/ ٢٠ (١٢٧ أ- ١٣١ ب)، لقد ذكر في مخطوطة أخرى في الرباط، ملك ٦٩٢٣/ ٢٠ (١٢٧ أ- ١٣١)، لقد ذكر في مخطوطة حلب: حلاق ص ٢٣٦ مجهولة المؤلف.','1','325',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (330,'<span data-type="title" id=toc-399>١٠ - «كتاب السبعة»:</span>
في عمل الإكسير في سبعة أيام، جار الله ١٥٥٤ (٥٧ ب- ٦٣ أ)، القاهرة:
دار طبيعيات ٧٣١ (٦٨ ب- ٧١ أ)، تيمور: طبيعيات ٦٧ (ص ٥٥ - ٦١)، انظر كراوس رقم ١٣٢؛ كذلك فى بورسه: ح. شلبي ٧٤٣ (ص ١٠٢ - ١٠٩)، مكتبة جامعة اسطانبول أ ٦٣١٤/ ١٠، قاستامونو ٨١١/ ١٠ وانظر كذلك فهرس المخطوطات.١٢٨، iii ,iv

<span data-type="title" id=toc-400>١١ - «كتاب الحى»:</span>
في طوائف صنعوية مختلفة:
١ - في أصحاب الطبائع الأربع (آريوس و ... سقراط) ٢ - أصحاب نظرية الحجر.
٣ - أصحاب الكبريت والزئبق ٤ - أصحاب من يقول في أن الإكسير يعمل من الأجسام الحيوانية كالنقى والشعر والدم والبول وغيرها.
هذا وقد أحال إلى اللوح الزمردي كذلك. المخطوطات: جار الله ١٥٥٤ (٦٣ أ- ٦٦ أ)، القاهرة: دار، طبيعيات ٧٣١ (٧١٠)، تيمور: طبيعيات ٦٧٠ (ص ٦١ - ٦٥)، انظر كراوس رقم ١٣٣؛ كذلك في بورسه: ح. شلبى ٧٤٣ (ص ١٠٩ - ١١٤)، مكتبة جامعة اسطانبول ١١/ ٦٣١٤ a. قاستامونو ٨١١/ ١١، وانظر كذلك فهرس المخطوطات.١١٧، iv ,iii

<span data-type="title" id=toc-401>١٢ - «كتاب الحكومة»:</span>
في عمل الإكسير على رأي من يقول إن الدم (أساس للإكسير)، عمل النشادر بالدم، في «أصحاب الملح»، جار الله ١٥٥٤ (٦٦ أ- ٦٩ ب)، تيمور:
طبيعيات ٦٧ (ص ٦٥ - ٦٨)، انظر كراوس رقم ١٣٤، كذلك فى بورسه: ح.
شلبي ٧٤٣ (ص ١١٤ - ١١٨)، مكتبة جامعة اسطانبول ١٢/ ٦٣١٤ a. قاستامونو ٨١١/ ١٢ وانظر كذلك فهرس المخطوطات.١١٦، iv ,iii','1','326',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (331,'<span data-type="title" id=toc-402>١٣ - «كتاب البلاغة»:</span>
تمام الكتاب الكبير، جار الله ١٥٥٤ (٦٩ ب- ٧٣ أ)، تيمور: طبيعيات ٦٧ (ص ٦٨ - ٧٢)، وانظر كراوس رقم ١٣٥؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١١٨ - ١٢١)، مكتبة جامعة اسطانبول، ١٣/ ٦٣١٤ a. قاستامونو ٨١١/ ١٣، وانظر فهرس المخطوطات.١٠٢ iv ,iii

<span data-type="title" id=toc-403>١٤ - «كتاب المشاكلة»:</span>
«في المحاصيل الأربعة من تقطير المواد الحيوانية: الماء والزيت والتراب والنار وفي التدابير الأربعة: الصبغ والانحلال، والتثبيت والتحويل projektion «و «الإعادة»، جار الله ١٥٥٤ (٧٣ أ- ٧٦ أ)، تيمور: طبيعيات ٦٧ (ص ٧٢ غير كامل)، انظر كراوس رقم ١٣٦؛ كذلك في بورسه: ح. شلبي ٧٤٣ (ص ١٢١ - ١٢٦)، مكتبة جامعة اسطانبول، ١٤/ ٦٣١٤ a. قاستامونو ٨١١/ ١٤، وانظر كذلك فهرس المخطوطات.١٥٨، iv ,iii

<span data-type="title" id=toc-404>١٥ - «كتاب الخمسة عشر»:</span>
في التدابير الكيميائية التي تستغرق ١٥ يوما، جار الله ١٥٥٤ (٧٦ أ- ٧٩ أ)، القاهرة: دار، طبيعيات ٧٣١ (٧٢ أ- ٧٣ أ)، وانظر كراوس رقم ١٣٧؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٢٦ - ١٣٠)، مكتبة جامعة اسطانبول، ١٥/ ٦٣١٤ a. قاستامونو ٨١١/ ١٥ وانظر كذلك فهرس المخطوطات.١٢١، iii ,iv يبدو أن ترجمة لاتينية موجودة في كمبردج، كلية، trinity رقم ١٣٦٣ (١٣٧ أ- ١٤٠ أ) وانظر هولميارد في:
proc. oftheroy. soc. ofmed., sect. hist. ofmed. ١٦/ ١٩٢٣/ no. ٣٨

<span data-type="title" id=toc-405>١٦ - «كتاب الكفء»:</span>
«في تبدل الألوان التي تظهر في الإكسير أثناء عمله، سرد فيه ٣٦٠ لونا وفرقا في اللون فى ١٢ فقرة». المخطوطات: جار الله ١٥٥٤ (٧٩ أ- ٨١ أ)، القاهرة:','1','327',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (332,'دار، طبيعيات ٧٣١ (٧٣ ب- ٧٤ ب)، انظر كراوس رقم ١٣٨، كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٣٠ - ١٣٥)، مكتبة جامعة اسطانبول، ١٦/ ٦٣١٤ a. قاستامونو ٨١١/ ١٦، وانظر كذلك فهرس المخطوطات.١٥٠، iii ,iv

<span data-type="title" id=toc-406>١٧ - «كتاب الإحاطة»:</span>
«نسب مزج العناصر الأربعة بالنسبة لعمل الإكسير على رأي طوائف، مختلفة ... »، جار الله ١٥٥٤ (٨١ أ- ٨٤ أ)، القاهرة: دار، طبيعيات ٧٣١ (٧٤ ب- ٧٦ ب)، وانظر كراوس رقم ١٣٩؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٣٥ - ١٣٩)، مكتبة جامعة اسطانبول ١٧/ ٦٣١٤ a. قاستامونو ٨١١/ ١٧ وانظر كذلك فهرس المخطوطات.٩٣، iii ,iv

<span data-type="title" id=toc-407>١٨ - «كتاب الراووق»:</span>
يعالج «أصل العناصر الأربعة ومنزلتها في العالم، نشوء سائر الموجودات الثلاثة بالدوران الثلاثي للفلك، دفاع عن السيمياء، الطبائع الأربع ... »، جار الله ١٥٥٤ (٨٤ أ- ٨٦ أ)، القاهرة: دار، طبيعيات ٧٣١ (٧٦ ب- ٧٧ ب)، تيمور: طبيعيات ٦٧ (ص ١٠١، غير كاملة)، انظر كراوس رقم ١٤٠؛ كذلك بورسه: حسين شلبي ٧٤٣ (ص ١٣٩ - ١٤٢)، مكتبة جامعة اسطانبول، ١٨/ ٦٣١٤ a. قاستامونو ٨١١/ ١٨، وانظر كذلك فهرس المخطوطات، ١٢٤، iii ,iv وقد نشر باول كراوس مختارا منه، القاهرة ١٩٣٥ م في: مختار رسائل ص ٤٦٠ - ٤٦٤.

<span data-type="title" id=toc-408>١٩ - «كتاب القبة»:</span>
«في التمييز بين الطبائع الباطنة والظاهرة، تقليد بعض الأحجار النفيسة ... » جار الله ١٥٥٤ (٨٦ ب- ٨٩ ب)، القاهرة: دار، طبيعيات ٧٣١ (٧٧ ب- ٧٩ أ)، تيمور: طبيعيات ٦٧ (ص ١٠١ - ١٠٤) انظر كراوس رقم','1','328',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (333,'١٤١؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٤٢ - ١٤٧)، مكتبة جامعة اسطانبول ١٩/ ٦٣١٤ a. قاستامونو ٨١١/ ١٩ وانظر كذلك فهرس المخطوطات.١٤٥، iii ,iv

<span data-type="title" id=toc-409>٢٠ - «كتاب الضبط»:</span>
«في الطوارئ التي تتخذ إذا ما أخفقت التدابير الكيميائية، وصف طريقة مزج العناصر (الطرق الأربع) وصف طريقة التوليد، طريقة في الإحلال والتثبيت»، جار الله ١٥٥٤ (٨٩ ب- ٩٢ ب)، القاهرة: دار، طبيعيات ٧٣١ (٧٩ أ- ٨٠ ب)، تيمور: طبيعيات ٦٧ (ص ١٠٤ - ١٠٧)، انظر كراوس رقم ١٤٢؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٤٧ - ١٥١)، مكتبة جامعة اسطانبول ٢٠/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٠ وانظر كذلك فهرس المخطوطات.١٣٢، iii ,iv

<span data-type="title" id=toc-410>٢١ - «كتاب الأشجار»:</span>
«فى تكوين السبعين كتابا، سرد للمواد النباتية المستعملة في الصنعة، فعلها على المعادن «جار الله ١٥٥٤ (٩٢ ب- ٩٥ ب)، القاهرة: دار، طبيعيات ٧٣١ (٨٠ ب- ٨٢ أ)، تيمور: طبيعيات ٦٧ (ص ١٠٧ - ١١٠)، انظر كراوس رقم ١٤٣؛ كذلك بورسه: ح. شلبي ٧٤٣ (ص ١٥١ - ١٥٥)، مكتبة جامعة اسطانبول، ٢١/ ٦٣١٤ a. قاستامونو ٨١١/ ٢١، انظر كذلك فهرس المخطوطات.٩٨، iii ,iv

<span data-type="title" id=toc-411>٢٢ - «كتاب المواهب»:</span>
«في التدابير المستعملة في المواد النباتية، التقطير ومحاصيله: الماء والدهن والنار والتراب، تحضير المواد الموجودة في النبات، عمل الزئبق والكبريت والنشادر، استخدام الأرواح لدى عمل الخمائر والملاغم» جار الله ١٥٥٤ (٩٥ ب- ٩٨ ب)، القاهرة: دار، طبيعيات ٧٣١ (٨٢ أ- ٨٤ أ)، تيمور:','1','329',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (334,'طبيعيات ٦٧ (ص ١١٠ - ١١٢) انظر كراوس رقم ١٤٤؛ كذلك في بورسه:
حسين شلبي ٧٤٣ (ص ١٥٥ - ١٥٩)، مكتبة جامعة اسطانبول ٢٢/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٢، وانظر فهرس المخطوطات.١٦٤، iii ,iv

<span data-type="title" id=toc-412>٢٣ - «كتاب العطاء»:</span>
«في تصعيد النشادر من المواد النباتية، تصعيد الكبريت والزئبق» جار الله ١٥٥٤ (٩٨ ب- ١٠١ ب)، القاهرة: دار، طبيعيات ٧٣١ (٨٤ أ- ٨٤ ب)، تيمور: طبيعيات ٦٧ (ص ١١٢ - ١١٣)، كراوس رقم ١٤٥، كذلك بورسه:
ح. شلبي ٧٤٣ (ص ١٥٩ - ١٦٣)، مكتبة جامعة اسطانبول، ٢٣/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٣ انظر كذلك فهرس المخطوطات.١٣٧، iii ,iv

<span data-type="title" id=toc-413>٢٤ - «كتاب الملاعب»:</span>
«في تكوين عناصر النبات .... » الخ، جار الله ١٥٥٤ (١٠١ ب- ١٠٤ أ)، القاهرة: دار، طبيعيات ٧٣١ (٨٤ ب- ٨٦ أ)، تيمور:
طبيعيات ٦٧ (ص ١١٣ - ١١٦)، كراوس رقم ١٤٦؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٦٣ - ١٦٧)، مكتبة جامعة اسطانبول، ٢٤/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٤.

<span data-type="title" id=toc-414>٢٥ - «كتاب المخنقة»:</span>
«استعمال الكبريت والزئبق، خلاصات من مواد نباتية لدى معالجة الذهب»، جار الله ١٥٥٤ (١٠٤ أ- ١٠٧ أ)، القاهرة: دار، طبيعيات ٧٣١ (٨٦ أ- ٨٧ أ)، تيمور: طبيعيات ٦٧ (ص ١١٦ - ١١٨)، كراوس رقم ١٤٧؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٦٧ - ١٧١) مكتبة جامعة اسطانبول، ٢٥/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٥، انظر كذلك فهرس المخطوطات.١٥٧، iii ,iv','1','330',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (335,'<span data-type="title" id=toc-415>٢٦ - «كتاب الإكليل»:</span>
«في تصعيد الزئبق»، جار الله ١٥٥٤ (١٠٧ أ- ١٠٩ ب)، القاهرة: دار، طبيعيات ٧٣١ (٨٧ أ- ٨٨ أ)، تيمور: طبيعيات ٦٧ (ص ١١٨ - ١٢٠)، كراوس رقم ١٤٨، كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٧١ - ١٧٥)، مكتبة جامعة اسطانبول، ٢٦/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٦، وانظر كذلك فهرس المخطوطات.١١٩، iii ,iv

<span data-type="title" id=toc-416>٢٧ - «كتاب الخلاص»:</span>
«في نظرية الأرواح، احمرار وتثبيت الزئبق ... »، جار الله ١٥٥٤ (١٠٩ ب- ١١٢ أ)، القاهرة: دار، طبيعيات ٧٣١ (٨٨ أ- ٨٩ أ)، تيمور:
طبيعيات ٦٧ (ص ١٢٠ - ١٢١)، كراوس رقم ١٤٩؛ كذلك في بورسه: ح.
شلبي ٧٤٣ (١٧٥ - ١٧٩) مكتبة جامعة اسطانبول، ٢٧/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٧ وانظر كذلك فهرس المخطوطات.١١٩، iii ,iv

<span data-type="title" id=toc-417>٢٨ - «كتاب الوجيه»:</span>
«في المعالجة بالتراب الذي يحصل عليه بتقطير المواد النباتية»، جار الله ١٥٥٤ (١١٢ أ- ١١٥ ب)، القاهرة: دار، طبيعيات ٧٣١ (٨٩ أ- ٩٠ أ)، تيمور: طبيعيات ٦٧ (ص ١٢١ - ١٢٣)، كراوس رقم ١٥٠؛ كذلك في بورسه:
ح. شلبي ٧٤٣ (ص ١٧٩ - ١٨٣)، مكتبة جامعة اسطانبول، ٢٨/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٨، وانظر كذلك فهرس المخطوطات.١٧٢، iii ,iv

<span data-type="title" id=toc-418>٢٩ - «كتاب الرغبة»:</span>
«في الذوبانات، في ذوبان النشادر، والكبريت والزئبق»، جار الله ١٥٥٤ (١١٥ ب- ١١٨ أ)، القاهرة: دار، طبيعيات ٧٣١ (٩٠ أ- ٩٠ ب)، تيمور:
طبيعيات ٦٧ (ص ١٢٣ - ١٢٥)، كراوس رقم ١٥١؛ كذلك في بورسه: ح.
شلبي ٧٤٣ (ص ١٨٣ - ١٨٧)، مكتبة جامعة اسطانبول، ٢٩/ ٦٣١٤ a. قاستامونو ٨١١/ ٢٩ وانظر فهرس المخطوطات.١٢٣ iii ,iv','1','331',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (336,'<span data-type="title" id=toc-419>٣٠ - «كتاب الخلقة»:</span>
«في الدهون و (في أنواع مختلفة من) الحليب»، جار الله ١٥٥٤ (١١٨ أ- ١٢١ أ)، القاهرة: دار، طبيعيات ٧٣١ (٩٠ ب- ٩١ ب)، تيمور:
طبيعيات ٦٧ (ص ١٢٥ - ١٢٦)، كراوس رقم ١٥٢؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٨٧ - ١٩٠)، مكتبة جامعة اسطانبول، ٣٠/ ٦٣١٤ a. قاستامونو ٨١١/ ٣٠ وانظر فهرس المخطوطات.١٢٠، iii ,iv

<span data-type="title" id=toc-420>٣١ - «كتاب الهيئة»:</span>
«في طوائف مختلفة، ترى كل طائفة معدنا آخر كأساس للكيمياء»، جار الله ١٥٥٤ (١٢١ أ- ١٢٣ ب)، القاهرة: دار، طبيعيات ٧٣١ (٩١ ب- ٩٢ ب)، تيمور: طبيعيات ٦٧ (ص ١٢٨ - ١٣٠)، كراوس رقم ١٥٣؛ كذلك في بورسه:
حسين شلبي ٧٤٣ (ص ١٩٠ - ١٩٤)، مكتبة جامعة اسطانبول ٣١/ ٦٣١٤ a. قاستامونو ٨١١/ ٣١ وانظر فهرس المخطوطات، ١٧٠، iii ,iv ترجمةلاتينية بعنوان. li bercondonationis

<span data-type="title" id=toc-421>٣٢ - «كتاب الروضة»:</span>
«في الرصاص، رطوبات الجسم الأربعة ... » جار الله، ١٥٥٤ (١٢٣ ب- ١٢٦ ب)، تيمور: طبيعيات ٦٧ (ص ١٢٨ - ١٣٠)، كراوس رقم ١٥٤؛ كذلك في بورسه: حسين شلبي ٧٤٣ (ص ١٩٤ - ١٩٨)، مكتبة جامعة اسطانبول، ٣٢/ ٦٣١٤ a. قاستامونو ٨١١/ ٣٢ انظر فهرس المخطوطات، ١٢٥، iii ,iv ونشره باول كراوس: القاهرة ١٩٣٥ م في:
«مختار رسائل» ص ٤٦٥ - ٤٧٠.','1','332',NULL,NULL,'0');
INSERT INTO [b143164] VALUES (337,'<span data-type="title" id=toc-422>٣٣ - «كتاب المشتري الناصع»:</span>
«في القلعي ولطائفه، استعمال الإكسير على القلعي، معالجته بالنار، تطهيره «جار الله ١٥٥٤ (١٢٦ ب- ١٢٩ أ)، تيمور: طبيعيات ٦٧ (ص ١٣٠ - ١٣٣) كراوس رقم ١٥٥؛ كذلك في بورسه: ح. شلبي ٧٤٣ (ص ١٩٨ - ٢٠١) مكتبة جامعة اسطانبول، ٣٣/ ٦٣١٤ a. قاستامونو ٨١١/ ٣٣.

<span data-type="title" id=toc-423>٣٤ - «كتاب النقد»:</span>
«في لطائف الحديد، ذوبان الحديد ... وتحوله ... »، جار الله ١٥٥٤ (١٢٩ أ- ١٣١ ب)، تيمور: طبيعيات ٦٧ (ص ١٣٣ - ١٣٥)، كراوس رقم ١٥٦؛ كذلك في بورسه: ح. شلبي ٧٤٣ (ص ٢٠١ - ٢٠٥)، مكتبة جامعة اسطانبول، ٣٤/ ٦٣١٤ /٦٣١٤ a. قاستامونو ٨١١/ ٣٤ انظر فهرس المخطوطات، ١٦٧، iii ,iv نشره باول كراوس: القاهرة ١٩٣٥ في: «مختار رسائل» ص ٤٧٠ - ٤٧٢.

<span data-type="title" id=toc-424>٣٥ - «كتاب الطاهر»:</span>
«في الذهب»، جار الله ١٥٥٤ (١٣١ ب- ١٣٤ ب)، القاهرة: دار، طبيعيات ٧٣١ (٤٣ أ- ٤٤ أ) تيمور: طبيعيات ٦٧ (ص ١٣٥ - ١٣٧)، كراوس رقم ١٥٧؛ كذلك في بورسه: ح. شلبى ٧٤٣ (ص ٢٠٥ - ٢٠٩) مكتبة جامعة اسطانبول، ٣٥/ ٦٣١٤ a. قاستامونو ٨١١/ ٣٥ وانظر كذلك فهرس المخطوطات.١٣٣، iii ,iv','1','333',NULL,NULL,'0');
