INSERT INTO [b143164] VALUES (516,'ينبغي الإشارة إليه أن كتاب «نهاية الأرب» (ج ١٢ ص ١٢٢) ذكر كتابا ل الزهراويى تحدث فيه عن عمل نضوج الطيب من النبات.
