INSERT INTO [b143165] VALUES (171,'و ١٥٤٢ (٣٩٦ ورقة، نسخت سنة ٧٩٢ هـ، انظر، O.Rescherin: MSOS (١٤ /١٩١١ /١٨٠ ومن نسختى كوبريلى مصورتان بدار الكتب بالقاهرة، لغة ٥٩٤ (انظر الفهرس الثانى ٢/ ١٢)، نور عثمانية ٤٧٤٥ (٣٩٤ ورقة، نسخت سنة ١١٤٠ هـ)، ينى جامع ١١٢٤ (٢٩٣ ورقة، نسخت سنة ٣٧٩ هـ، راجع، O. (Rescherin: MSOS ١٥ /١٩١٢ /٣ ولى الدين ٣١٠٠ (٢٩٥ ورقة، من القرن ١١ للهجرة)، داماد إبراهيم ١١١٧ (٤٣٧ ورقة، نسخت سنة ١٠٨٣ هـ)، فاتح ٥١٨٦ (٣٨٦ ورقة، نسخت ١١٣٢ هـ، انظر فهرس المخطوطات ١/ ٣٥٢ - والرقم هنا خطأ)، أيا صوفيا ٤٦٧٢ (٢٧٧ ورقة، نسخت سنة ١١٣٠ هـ، انظر، O.Rescher (in: WZKM ٢٦ /١٩١٢ /٨٨ عاطف ٢٧١٣ (٣٧٥ ورقة، نسخت سنة ١١٤٩ هـ)، مراد ملا ١٧٧٦ (٣٢١ ورقة، نسخت سنة ٤٧٤ هـ، راجع فهرس المخطوطات ١/ ٣٥٢ - والرقم هنا خطأ). فيض الله ٢٠٦٥ (٣٥٩ ورقة، نسخت سنة ٤٨٦ هـ)، آصفية بحيدرآباد، لغة ١ (٢٣٣ ورقة، نسخت سنة ١٠٧٨ هـ، انظر الفهرس ٢/ ١٤٣٤؛ فهرست مشروح ٢/ ٣٧٧ - ٣٧٩) بنكيبور ١٩٦٣ (٣٥٤ ورقة، من القرن ١٤ هـ، انظر الفهرس ٢٠/ ٢ - ٣، رضا برامبور، نحو ٢٥ (نسخت سنة ١٢٨٢، انظر الفهرس ١/ ٥٠٩)، الكاظمية، مكتبة الصدر الخاصة (الجزء الرابع والسادس، نسخا سنة ٣٩٧ هـ، انظرمجلة معهد المخطوطات العربية ٤/ ١٩٥٨/ ٢٣٩)، نابلس، مجموعة الجوهرى (٣٠٠ ورقة، انظر مجلة المجمع العلمى العربى بدمشق ٤/ ١٩٢٤/ ٤٥٦، / ٥/ ١٩٢٥/ ٢٣٤)، دار الكتب بالقاهرة، لغة ٢٤ (مجلدان يشتملان على الأجزاء ٣ - ٤ و ٧ - ٨، نسخة قديمة)، ولغة ١٠٩ (انظر الفهرس الثانى ٢/ ١١ - ١٢)، التيمورية، لغة ٥١٧، مكناس ٥ (قوبلت على نسخة المؤلف)، المكتبة العامة بالرباط، د ١٠٠٩ (١٩٥ ورقة، نسخت سنة ١٣١٦ هـ، انظر فهرس علوش ١/ ٣٦٢ - ٣٦٣)، القرويين بفاس ١٢٤١ (نسخة عتيقة)، و ٥٤٣ (الجزء التاسع، ٨٧ ورقة، انظر فهرس الفاسى ٢/ ٥٦ - ٥٧، وراجع فهرس بيل، رقم ١٢٤٥ و ١٢٥٨)، باريس ٤٢٣١/ ٥ (الجزء الأول، ٨٩ ب- ١١٤، ٦٣١ هـ)، و ٤٢٣٣ (الجزء الثانى، ١٥٦ ورقة، من القرن ١١ هـ، راجع فچدا ٣٥٠)، ليدن) Or.٣٢١ a -c ثلاثة مجلدات، ١٦٤ ورقة،','1','175',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (172,'و ٣٤٦ ورقة، و ٣٠٢ ورقة، والمجلد الأول بعضه حديث، والثانى والثالث نسخا سنة ٦٤٤ هـ، انظر فورهوف ٩١)، المتحف البريطانى) ٤١٧٨ Or. قطعة، ٦٠ ورقة، من القرن الخامس للهجرة، انظر الملحق رقم ٨٣٧)، و) ٥٨١١ Or. الجزء الأول، ٢٦١ ورقة، من القرن الخامس للهجرة، انظر، (Ellis ,Descr.List ٥٢ برنستون، جاريت (يهودا) ٢٣٣٩ (١٨٦ ورقة، وعليها سماعان يرجعان إلى سنتى ٣٩٧، و ٤١٨ هـ، انظر ماخ رقم ٣٧٥٣)؛ نشره كرنكو في ثلاثة مجلدات مع مجلد للفهارس، فى حيدرآباد ١٣٤٥ - ١٣٥١؛

<span data-type="title" id=toc-165>الدراسات:</span>
A. Siddiqi, Ibn Duraydandhistrea tmentofloan Wordsin: Allahabad Univ. Studies ٦/ ١٩٣٠/ ٦٦٩ - ٧٥٠;
حسين نصار، المعجم العربى ٤٠٤ - ٤٣٤؛ م. ك بركات، «الجمهرة لابن دريد» فى: البحث العلمى (مكة) ٣/ ١٩٨٠/ ٣٨٥ - ٤٠٥.
أ) «فائت الجمهرة» لأبى عمر الزاهد (المتوفى سنة ٣٤٥/ ٩٥٧)، ووردت نقول عنه، انظر ص ١٥٧ بعد.
ب) «جوهرة الجمهرة» مختصر للصاحب بن عباد (المتوفى سنة ٣٨٥/ ٩٩٥)، وقيل إنه قد وصل إلينا، انظر ص ٢٠٧ بعد.
ج) «شرح شواهد الجمهرة» لأبى سعيد الحسن بن عبد الله السيرافى (المتوفى سنة ٣٦٨/ ٩٧٩)، وقد وصل إلينا بعضه، انظر فصل الأدب.
د) «مختصر الجمهرة» لابن التيّانى، انظر فى نقول عنه ص ٢٥٦ بعد.
هـ) «نظم الجمهرة» ليحيى بن معطى الزّواوى (المتوفى سنة ٦٢٨/ ١٢٣١، انظر إنباه الرواة للقفطى ٤/ ٣٨ - ٣٩)، ذكره ياقوت في «إرشاد الأريب» ٧/ ٢٩٢؛ والسيوطى في بغية الوعاة ٤١٦.
و) «مختصر» لمحمد بن نصر الله بن عنين (المتوفى سنة ٦٣٠/ ١٢٣٢)، ذكره ابن خلكان ٢/ ٣٣.
ز) «مختصر» لمجهول، ويوجد مخطوطا في المتحف البريطانى، مجموعة','1','176',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (173,'بلشاه (انظر،
(E. Edwardsin: Festschrift E. G. Browne, Cambridge ١٩٢٢, S. ١٤٩, (No. ٨٧
، ولعله هو مختصر بن عنين.
٢ - «المتناهى (فى اللغة)»، ولم يرد له ذكر في قوائم كتبه، انظر نقلا عنه في الأمالى للقالى ٢/ ٤٤.
٣ - «الاستدراك على الخليل (فى كتاب العين)»، ذكره ابن النديم ص ٤٣، ولعل المراد به الكتاب الآتى ذكره.
٤ - «كتاب التوسّط» وهو كذلك على كتاب العين، توسط فيه بين الخليل ومنقده المفضل بن سلمه (انظر ص ١٣٩ بعد)، والكتاب عبارة عن تعاليق علقها ابن دريد حين قرأ عليه أبو على بن مقلة (المتوفى سنة ٣٢٨/ ٩٤٠) وابن أو أبو حفص (١٠٥) كتاب «الرد على الخليل ... » للمفضل ابن سلمه (انظر ص ١٤٠ بعد). وقد جمع أبو حفص نصوصها وأخرجها موسومة بالاسم الآنف الذكر. وقد رأى ابن النديم (ص ٦٢) الكتاب في نحو المائة ورقة.
٥ - «كتاب ما سئل عنه لفظا فأجابه عنه حفظا»، ولعله في مسائل في اللغة، جمعه عنه على بن اسماعيل بن حرب، انظر ابن النديم ٦١؛ «وإنباهالرواة» للقفطى ٣/ ٩٦./

<span data-type="title" id=toc-166>ولابن دريد في الأمثال والأقسام والاشتقاق:</span>
١ - «أمثال على بن أبى طالب»، وتوجد مخطوطة في باريس ٣٩٧١/ ٣ (٤٩ ب، من القرن العاشر، راجع فجدا ٢٥٨).
٢ - «الأمثال النبوية»، مخطوط في المكتبة العامة بالرباط، مجموعة الكتانى (ضمن مجموع، انظر مجلة معهد المخطوطات العربية ٥/ ١٩٥٩/ ١٨٥، رقم ١٣٧).
٣ - «كتاب الملاحن»، ذكرته كل القوائم، انظر أيضا فهرست ابن خير ٣٦٦.
_________
(١٠٥) لعله هو أبو حفص الأصفهاني الورّاق ممن جمعتهم حضرة الصاحب بن عباد (انظر إرشاد الاريب لياقوت ٢/ ٣٢٦).','1','177',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (174,'المخطوطات: الاسكوريال ٤٤٢/ ٥ (الأوراق ١٣٠ - ١٥٠)، و ٤٦٧/ ٤ (الأوراق ٥١ - ٧٣، من القرن السادس للهجرة)، الفاتيكان ١٤٧٩/ ٥ (الأوراق ٣٣ - ٦٢، من القرن ٨ - ٩ للهجرة، انظر فيدا ١/ ٢٣٦)، برلين ٧١٠٠/ ١ (الأوراق ١ - ١١ ب، نسخت سنة ١٠٩١ هـ)، جوتا ٤١٧ (١٧ ورقة، من القرن العاشر للهجرة)، برنستون، جاريت ٢٩٢ هـ (١٣ ورقة، نسخت سنة ١٣٠١ هـ، انظر الفهرس، رقم ٢٥١)، و (يهودا) ٣٥٦٨ (٢١٣ ب- ٢٢٠ ب، انظر ماخ رقم ٣٧٨٥)، عاطف ٢٨٠٠/ ٢ (٤٩ ب- ٥٩ ب، نسخت نحو سنة ١٠٠٠ هـ، راجع، (O.Rescherin: MFO ٥ /١٩١٢ /٤٩٤ بشير أغا (أيوب) ١٩٤/ ١ (الأوراق ١ - ٢٨ ب، نسخت سنة ٦١٩ هـ، راجع، (O.Rescherin: MFO ٥ /١٩٧٢ /٥٣٥ التيمورية بدار الكتب بالقاهرة، لغة ٥٣٠، المكتبة العامة بالرباط، مجموعة الكتانى ٣٥٩/ ١ (الأوراق ١ - ١٥، نسخة جيدة، راجع مجلة معهد المخطوطات ٥/ ١٩٥٩/ ١٨٨، رقم ١٩٦)؛ وتوجد نسخة عليها تعليقات لمحمد بن أحمد البهوتى (المتوفى سنة ١٠٨٨/ ١٦٧٨، انظر (Br.GII ,٣٠٨ ,S.II ,٤٢٠ بمكتبة عارف حكمت بالمدينة، مجموع ٦٠ (قديم) أو مجموع ١٠٤/ ٤ (الأوراق ١٣٣ - ١٤٦، انظر «المنتخب» لكحالة ١٠٥).
نشره هـ. توريكه في:
Festschr. derorient. Sectionder ٣٦. Vers. Deutscher Philologen U. Schulmeis- ter, Heidelberg ١٨٨٢,
وطبع في القاهرة سنة ١٣٢٣ هـ، ونشره أيضا إبراهيم إطفيّش الجزائرى، القاهرة ١٣٤٧ هـ.
وهناك «أرجوزة في شرح ملاحن ابن دريد» لأبى بكر محمد بن محمد بن إدريس القضاعى (المتوفى سنة ٧٠٧/ ١٣٠٧)، ذكرها ابن فرحون في «الديباج المذهّب» ٣٠٢.
٤ - «كتاب الاشتقاق» (وفي عدة قوائم و» MIE ٤٩ /١٩٤٦ /٦ ,No.١٠٥ اشتقاق','1','178',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (175,'أسماء القبائل»)، ومن بين من نقلوا عنه الصغاتى في «العباب» (انظر ١/ ٣٠)،

<span data-type="title" id=toc-167>المخطوطات:</span>
أسعد ٢٣٥٧ (٢٩٣ ورقة، نسخت سنة ٩٢٢ هـ)، المكتبة العامة بالرباط، أوقاف ١٧٣ (١٥٨ ورقة، كتبها ابن مكتوم قبل سنة ٧٤٩، ومنها مصورة بمعهد المخطوطات بالقاهرة، ليدن ٩٨) ٣٦٢ Or. ورقة، نسخت سنة ٦٦٨ هـ، انظر فرهوف ١٤٢؛ نشره ف. فيستنفلد، جوتنجن ١٨٥٤، وطبع في القاهرة ١٩٤٧، ونشر محمد عبد المنعم خفاجى منتخبات منه مع مقدمة في: «فصيح ثعلب» والشروح التى عليه، القاهرة ١٩٤٩، ونشره أيضا عبد السلام محمد هارون، القاهرة ١٩٥٨، انظر فى هذه النشرة: صلاح الدين المنجد في: مجلة معهد المخطوطات العربية ٥/ ١٩٥٩/ ١٥٩ - ١٦٠.
٥ - «رقعة أبى الحسن الصّيمرى المتكلّم إلى أبى بكر محمد بن الحسن بن دريد ... وجوابه»، وهى مسائل في الاشتقاق، نقلها السيوطى في «الأشباه والنظائر» ٣/ ٥٧ - ٦١.

<span data-type="title" id=toc-168>وله تآليف مفردة في اللغة في الموضوعات الآتية:</span>
١ - «كتاب السرج واللجام»، ورد ذكره في كل القوائم، انظر أيضا فهرست ابن خير ٣٩٩؛ «خزانة الأدب» للبغدادى ١/ ٤٩١؛ المخطوطات: شهيد على ٢٧١٩/ ٧ (١٢٥ أ- ب، ١٣٤ أ- ب، ١٣٩ أ- ١٤٢ ب، نسخت سنة ٥٩٦ هـ)، التيمورية بدار الكتب بالقاهرة، لغة ٤٥٩/ ٥ (نسخت سنة ٦٠٩ هـ)، ليدن) ١/ ٦٥٧ Or. الأوراق ١ - ٨، انظر فورهوف ٣٣١)؛ نشره وليم رايت W.Wrightin: Opusculaarabica ,London ١٨٥٩ ,S.١ - ١٤ ,: وابراهيم السّامرائى في بغداد ١٩٧٠.
٢ - «كتاب الخيل» («الكبير» و «الصغير»، مذكور في كل القوائم و «خزانة الأدب» ١/ ٤٩١)، وكان موجودا في القرن ٧/ ١٣ باحدى مكتبات حلب (انظر','1','179',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (176,'پولس سباط في، (MIE ٤٩ /١٩٤٦ /١٩ ,No.٣٤٩: انظر نقلا عنه ورد في الإصابة لابن حجر ٣/ ١٠٣٣.
٣ - «قصيدة في وصف الأسد»، وتوجد مخطوطة في كوبريلى ١٢٩٦/ ٢ (٩٦ ب- ٩٧ ب، من القرن السادس للهجرة).
٤ - «كتاب السلاح»، ذكرته كل القوائم، وكان في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر پولس سباط، الموضع المذكور، ص ٢٨، رقم ٥١٠)./ ٥ - «كتاب وصف المطر والسحاب» أو «كتاب صفة السحاب والغيث» (ابن النديم)، وقد وصل إلينا، ونشر، انظر تاريخ التراث العربى ٧/ ٣٥٣.
٦ - «كتاب الأنواء»، انظر في نقول عنه المرجع السابق ص ٣٥٤.

<span data-type="title" id=toc-169>وله في لغة القرآن:</span>
١ - «كتاب اللغات (فى القرآن)»، ذكره ابن النديم وغيره، ونقل عنه ابن دريد نفسه في «الجمهرة» ٢/ ٤٠٠، ٣/ ٧٨، ٢٤٧؛ وفي «الاشتقاق»، القاهرة ١٩٥٨، ص ٨٠، انظر مقدمته ص ١٩.
٢ - «كتاب غريب القرآن»، ولم يتمه، ذكره ابن النديم والمصادر التى نقلت عنه.

<span data-type="title" id=toc-170>الزجاجى</span>
هو أبو القاسم عبد الرحمن بن اسحاق (المتوفى سنة ٣٣٧/ ٩٤٩، وقيل ٣٣٩/ ٩٥٠، أو ٣٤٠/ ٩٥٢)، مؤلف كتاب «الجمل» الذى طار ذكره من بعد، وغيره من المؤلفات النحوية الكثيرة (انظر فصل النحو). وصنّف أيضا طائفة من الكتب في اللغة وفي علوم لغة القرآن.
١ - «كتاب الإبدال والمعاقبة والنظائر»، ويوجد مخطوطا في رئيس الكتاب ٨٧٩/ ٥ (١٠٢ ب- ١١٦؛ من القرن العاشر للهجرة، راجع فهرس المخطوطات، / ٣٧٧)، ومنه مصورة بجامعة القاهرة ٢٢٩٦٧ (انظر مجلة المجمع العلمى العربى بدمشق','1','180',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (177,'٣٤/ ١٩٥٩/ ٤٤٥)، بشير أغا (أيوب) ١٩٤/ ٢ (٢٩ أ- ٧٤ أ، نسخت سنة ٦١٩ هـ)؛ نشره عزّ الدين التنوخى في: مجلة المجمع العلمى العربى بدمشق ٣٧/ ١٩٦٢/ ٢٤٠ - ٢٧٥، ٤٢٨ - ٤٧٥، ٦٠٢ - ٦٣٨، ودمشق ١٩٦٢، انظر أيضا «نصوص فى فقه اللغة العربية للسيد يعقوب بكر ٢/ ٣٠٣ - ٣١٥ (مقارنا بنصوص مناظرة للغويين آخرين).
٢ - «اختصار (أو مختصر) الزاهر»، مختصر من كتاب «الزاهر» لابن الأنبارى (المتوفى سنة ٣٢٨/ ٩٤٠) مع إصلاحات، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣٤١ - ٣٤٢). وجمع فيه جمل الألفاظ التى ذكرها ابن الأنبارى وشرحها شرحا مختصرا موجزا، وحذف منها الشواهد (انظر «كشف الظنون» ٩٤٨، وراجع (Sellheim ,Sprich Worter ١٢٦ وأضاف الشارح بابا ختم به الكتاب جمع فيه ألفاظا نادرة، وبعض فوائد لغوية خاصة.

<span data-type="title" id=toc-171>المخطوطات:</span>
دار الكتب بالقاهرة، لغة ٥٥٧ (نسخت سنة ٦٢٠ هـ، انظر الفهرس الثانى ٢/ ١٦؛ عزّ الدين التنوخى في: مجلة المجمع العلمى العربى بدمشق ٣٧/ ١٩٦٢/ ٢٤٧)، القرويين بفاس ١٤٧٧، ييل ١٨١) ٢٨٧ - L ورقة، نسخت سنة ١٣٠٩ هـ، انظر نيموى رقم ٢٠٩).
٣ - (الاستدراك على إصلاح المنطق) لابن السكيت (المتوفى نحو سنة ٢٤٣/ ٨٥٧)، واسم الكتاب مستنبط من اسم مصنّف أبى سعيد عبد الرحمن بن محمد بن دوست (المتوفى سنة ٤٣١/ ١٠٤٠) «الرد على الزجاجى فيما استدركه على ابن السكيت في إصلاح المنطق»، ولم يصل إلينا، انظر ص ٢٣٧ بعد.
٤ - «كتاب الأنواء»، وقد وصل إلينا، انظر تاريخ التراث العربى ٧/ ٣٥٤.

<span data-type="title" id=toc-172>وللزجاجى في لغة القرآن:</span>
١ - «كتاب (تفسير) أشتقاق اسماء الله تعالى وصفاته المستنبطة من التنزيل وما','1','181',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (178,'يتعلق بها من اللغات والمصادر والتأويل» (راجع/ مازن المبارك في: مجلة المجمع العلمى العربى بدمشق ٣٤/ ١٩٥٩/ ٤٤٤ - ٤٤٥)،

<span data-type="title" id=toc-173>المخطوطات:</span>
عارف حكمت بالمدينة، أحزاب ٤٦ (١٣٥ ورقة، نسخت نحو سنة ٤٣٤ هـ)، ومنها مصورة بمعهد المخطوطات بالقاهرة، دار الكتب بالقاهرة، لغة ٣ ش (١٤٩، منسوخة عن مخطوطة المدينة، انظر فهرس المخطوطات ١/ ٣٤٠ - ٣٤١؛ الفهرس الثانى ٢/ ٢٧)، نشره ع. ع. المبارك، «الزجاجى ومذهبه في اللغة والنحو مع تحقيق كتابه اشتقاق أسماء الله»، أطروحة، القاهرة ١٩٧٢، وطبع في النجف ١٩٧٤ (انظر المورد ٧، ١/ ١٩٧٨/ ٢٦٥)، وانظر للمؤلف نفسه «الزجاجى وكتابه اشتقاق .. » فى: المورد ٣، ١/ ١٩٧٤/ ٢٨٥ - ٢٩١، ونشره أيضا أ. ى. الدقاق، دمشق ١٩٧٥.
٢ - «رسالة في بيان الأسئلة الواردة على البسملة وأجوبتها»، وتوجد مخطوطة في دار الكتب بالقاهرة، نحو ٦٧ ش (ضمن مجموع قديم، انظر الفهرس الثانى ٢/ ١١٢، ١٦٤).

<span data-type="title" id=toc-174>الجلودى</span>
هو أبو أحمد عبد العزيز بن يحيى بن أحمد، من علماء الشيعة، وكان أكثر اشتغاله بالتاريخ والفقه والأدب. وعنى أيضا بمسائل اللغة. توفى سنة ٣٣٢/ ٩٤٤ (انظر فضل الأدب).

<span data-type="title" id=toc-175>وله في اللغة:</span>
كتاب المطر، كتاب السحاب والمطر والرعد والبرق، كتاب الأطعمة، كتاب الأشربة، كتاب اللّباس، ذكرها كلها النجاشى، فى «الرجال» ١٨٢.','1','182',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (179,'<span data-type="title" id=toc-176>وله في القرآن:</span>
١ - كتاب التفسير، ٢ - كتاب القراءات، ٣ - كتاب ما نزل فيه من القرآن، ذكرها جميعها النجاشى ١٨٠.

<span data-type="title" id=toc-177>وله فى النحو:</span>
كتاب النحو، ذكره النجاشى ١٨٢.

<span data-type="title" id=toc-178>ابن درستويه</span>
هو أبو محمد عبد الله بن جعفر بن درستويه بن المرزبان الفسوى، ولد سنة ٢٥٨/ ٨٧٢ في «فسا» من مدن فارس، وذهب من بعد إلى العراق حيث أخذ عن المبرد وثعلب. انحاز إلى البصريين وألف كتبا في الرد على الكوفيين.
ووصف بأنه كان مصنّفا متقنا بارعا، ويوحى إلينا ما وصلنا من مصنفاته بانه كان نقادا ألمعيا. وتتناول كتبه في المقام الأول اللغة، كما تتناول أيضا النحو (انظر فصل النحو) ولغة القرآن. توفى سنة ٣٤٧/ ٩٥٨/ «طبقات النحويين» للزبيدى ١٢٧؛ ابن النديم ٦٣، «تاريخ العلماء» للتنوخى ٤٧ أ، «تاريخ بغداد» ٩/ ٤٢٨ - ٤٢٩؛ «نزهة الألباء» للأنبارى ٢، ١٩٧ - ١٩٨؛ «إنباه الرواة» للقفطى ٢/ ١١٣ - ١١٤؛ ابن خلكان ١/ ٣١٥ - ٣١٦؛ «مسالكالأبصار» لابن فضل الله ٤/ ٢٩٩ - ٣٠٠؛ ابن قاضي شهبة، المخطوط ص ٣٢٥ - ٣٢٦؛ «بغية الوعاة» للسيوطى ٢٧٩ - ٢٨٠؛ «البداية» لابن كثير ١١/ ٢٦٢.
Br. SI, ١٧٤; Rescher, Abri BII, ١٥١ - ١٥٢;
«الأعلام» للزركلى» N.Cetinin: Sark.Mecm.I ,١٠٧، ٢٠٤ /٤ معجم المؤلفين» لكحالة ٦/ ٤٠؛
G. Lecomtein: Arabica ١٠/ ١٩٦٣/ ٢٨٨ - ٢٨٩; J. C. Vadetin: EI, III ٢, ٧٥٨;
عبد الله الجبورى، «ابن درستويه، حياته وآثاره»، بغداد ١٩٧٤.','1','183',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (180,'<span data-type="title" id=toc-179>ولابن درستويه في الغريب، والاشتقاق، وصيغ الألفاظ ومعانيها:</span>
١ - «تفسير قصيدة شبيل بن عزرة»، وهو شرح لقصيدة في الغريب لشبيل بن عزرة (المتوفى نحو سنة ١٤٠/ ٧٥٧)، انظر ص ٢٣ آنفا. وروى ابن درستويه النصّ عن السكرى، ورواه أبو عمر الزاهد عن ابن درستويه (انظر ابن النديم والقفطى).
٢ - «كتاب في علل الاشتقاق وحججه»، وقد نقل عنه في كتابه «تصحيح الفصيح»، انظر ع. الجبورى في مقدمة المجلد الأول، بغداد ١٩٥٧، ص ٤٢.
٣ - «كتاب إبطال الأضداد»، ذكره ابن النديم، والقفطى، وابن خلكان، انظر فى نقول عنه ع. الجبورى، الموضع المذكور ص ٢٢ - ٢٣، وللجبورى دراسة فيه: «الأضداد وموقف ابن درستويه منها» فى: المورد ٢، ٣/ ١٩٧٣/ ٤٢ - ٤٨.
٤ - «كتاب إبطال القلب»، ونقل عنه فى كتابه «تصحيح الفصيح» وعنه نقل السيوطى في «المزهر» ١/ ٤٨١.
٥ - «كتاب في اتفاق الألفاظ والمعانى»، ونقل عنه في كتابه «تصحيح الفصيح»، انظر الموضع المذكور ص ٤٢.

<span data-type="title" id=toc-180>وله تآليف مفردة في موضوعات، وفي تفسير ألفاظ، ومنها:</span>
١ - «كتاب الحى والميت» أو «كتاب الحياة والموت»، ذكره ابن النديم، والقفطى، وابن خلكان، ونقل عنه في كتابه «تصحيح الفصيح» (انظر ع.
الجبورى، الموضع المذكور ص ٣٣)، ونقل عنه أيضا أبو حيان الأندلسى في «تذكرة النحاة»، الجزء الثانى من مخطوط مكتبة الأوقاف بالرباط ٢١٤، ص ٤٧٧.
وذكر في الكتاب ١١٠ من الألفاظ المتعلقة بالحياة والموت، مقسومة على ٤٢ بابا.
ويوجد مخطوطا في سراى كوغشلار ١٠٩٦/ ١١ (٨٠ ب- ٨٩ ب، نسخت سنة ٧٠٧ هـ، انظر (Isl.Tetk.Enst.Derg.٢ /١٩٥٨ /٢٥٤ وعليه استدراك لابن القطاع (المتوفى سنة ٥١٤/ ١١٢٠) زاد فيه ٣٤٣ لفظا، على تقسيم أبواب كتاب ابن درستويه ورتبه على حروف الهجاء، المخطوطة نفسها (٨٩ ب- ٩١ أ، انظر آنفا).','1','184',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (181,'٢ - «كتاب الأزمنة»، ذكره ابن النديم، والقفطى، ولم يتمه، لم يصل إلينا، راجع تاريخ التراث العربى ٧/ ٣٥٥.
٣ - «الرد على ابن خالويه في الكل والبعض»، ذكره ابن النديم، والقفطى.
٤ - «تفسير الشيء»، لم يذكره إلا ابن النديم، والقفطى، ولعله تصحيف «تفسير السّبع»، وهو شرح على المعلقات.
وله تآليف على كتاب «العين» للخليل بن أحمد (انظر ص ٥٣ آنفا)، وغيره.
١ - «الرد على المفضّل في الردّ على الخليل»، ذكره ابن النديم (٦٣) وغيره (انظر ع. الجبورى، الموضع المذكور ص ٣٧ - ٣٩)./ ٢ - «الانتصار لكتاب العين»، لم يذكره غير القفطى في «إنباه الرواة» ٢/ ١١٤.
ولعل المراد به هو الكتاب الذى ملكه ابن القفطى ووصفه في موضع آخر بأنه «تصنيف مفيد»، وقد استوفى فيه ابن درستويه الكلام على الخلاف في نسبة كتاب «العين» إلى الخليل (الإنباه ١/ ٣٤٣).
٣ - «نقض كتاب العين» (فلوجل) أو «تبصّر كتاب العين» (طهران)، لم يذكره سوى ابن النديم، ولعله يقرأ «تنصّر (ل) كتاب العين»، وعلى هذا يكونهو الكتاب الآنف الذكر.
٤ - «الرد على من نقل كتاب العين عن الخليل»، ولم يرد ذكره إلا عند ابن النديم.
٥ - «تصحيح الفصيح»، وهو شرح على كتاب الفصيح لثعلب (المتوفى سنة ٢٩١/ ٩٠٤)، ذكره ابن النديم، وأثنى عليه القفطى، ونقل عنه فيمن نقل السيوطى فى «المزهر»، وصاحب «تاج العروس» ٢ ١٠/ ٣٤٩ (جبر)، ١١/ ٦١ (حفر)،

<span data-type="title" id=toc-181>المخطوطات:</span>
تشستربيتى ٤١٤٥ (١٦٠ ورقة، من القرن السادس هـ.)، عارف حكمت بالمدينة، لغة ٢٦ (٢٥٩ ورقة، نسخت سنة ٥٦١ هـ، انظر «المنتخب» لكحالة ٤٧)، ومنها مصورة بمعهد المخطوطات بالقاهرة؛ ونشره على مخطوطة المدينة ع.
الجبورى، المجلد الأول، بغداد ١٩٧٥.','1','185',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (182,'<span data-type="title" id=toc-182>ولابن درستويه فى لغة القرآن والحديث:</span>
١ - «كتاب في القرآن»، هكذا سماه فيما نقله عنه في «تصحيح الفصيح» (انظر ع. الجبورى، مقدمة المجلد الأول، بغداد ١٩٧٥، ص ٤٣)، ذكره ابن النديم والقفطى باسم «كتاب المعانى في القرآن»، وقيل انه لم يتمه، انظر فيما يحتمل من نقول عنه: الجبورى في الموضع المذكور ص ٤٤.
٢ - «التوسط بين الأخفش (المقرئ) وثعلب في تفسير (أو معنى) القرآن»، ذكره ابن النديم والقفطى وابن خلكان وغيرهم، انظر الجبورى في الموضع السابق ص ٣١ - ٣٢.
٣ - «الرد على الفراء في المعانى»، أى كتاب الفرّاء «معانى القرآن، ذكره ابن النديم والقفطى وابن خلكان وغيرهم، انظر الجبورى ص ٣٦.
٤ - «غريب الحديث»، ذكرته كل القوائم ومصادر أخرى في التراجم، انظر الجبورى ص ٤١ - ٤٢، وكان من مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) فى «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربى بدمشق ٣٥/ ١٩٦٠/ ٥٤٤).

<span data-type="title" id=toc-183>أبو الجود</span>
هو القاسم بن محمد بن رمضان العجلانى، كان لغويا، ونحويا. وكان حيا، فيما يظهر، نحو سنة ٣٥٠/ ٩٦١.
ابن النديم ٨٤؛ «إرشاد الأريب» لياقوت ٦/ ١٩٩؛ «إنباه الرواة» للقفطى ٣/ ٢٧ - ٢٨؛ «بغية الوعاة» للسيوطى» Flugel ,Gramm.Schulen ٢٣٢.-.٣٨٠ معجم المؤلفين» لكحالة ٨/ ١١٨.
وله في اللغة «كتاب الفرق»، ذكره ابن النديم وآخرون.
وله في النحو، والهجاء والأبنية:
١ - «كتاب المختصر للمتعلمين» ذكره ابن النديم (٨٤) وغيره./','1','186',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (183,'٢ - «كتاب المقصور والممدود»، ذكره ابن النديم وغيره، راجع أ. ع. هريدى فى مجلة معهد المخطوطات العربية ٢٠، ٢/ ١٩٧٤/ ٦٤.
٣ - «كتاب المذكر والمؤنث». ذكره ابن النديم وغيره.

<span data-type="title" id=toc-184>ابن رمضان</span>
هو الحسن بن محمد (بدلا من: محمد بن الحسن) بن رمضان العجلانى، أخو أبى الجود (انظر آنفا)، وكان حيا أيضا نحو سنة ٣٥٠/ ٩٦١.
ابن النديم ٨٤؛ «إرشاد الأريب» لياقوت ٦/ ٤٩٥، «الوافى بالوفيات» للصفدى ٢/ ٣٥٥؛ «بغية الوعاة» للسيوطى Flugel ,Gramm.Schulen ٢٣٢.-.٣٣

<span data-type="title" id=toc-185>وذكر من تآليفه:</span>
١ - «كتاب أسماء الخمر وعصيرها»، ذكره ابن النديم وآخرون.
٢ - «كتاب الديرة»، ذكر ابن النديم وغيره.

<span data-type="title" id=toc-186>أبو على الفارسى</span>
هو الحسن بن أحمد بن عبد الغفار (عاش من سنة ٢٨٨/ ٩٠١ إلى سنة ٣٧٧/ ٩٨٧)، كان واحدا من أجلّ نحاة زمانه (انظر فصل النحو)، وألّف أيضا بعض مؤلفات فى اللغة، لم يصل إلينا منها إلا قطع منها.
انظر في قوائم مصنفاته: ابن النديم ٦٤ (طهران، ص ٦٩، الترجمة الإنجليزية ١٣٩ - ١٤٠)؛ «إنباه الرواة» للقفطى ١/ ٢٧٤؛ «إرشاد الاريب» لياقوت ٣/ ١٣؛ ابن خلكان ١/ ١٦٤؛ «بغية الوعاة» للسيوطى ٢١٧.','1','187',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (184,'<span data-type="title" id=toc-187>ومن كتب أبى على:</span>
١ - «شرح الإصلاح»، وهو شرح على «إصلاح المنطق» لابن السكيت (المتوفى سنة ٢٤٤/ ٨٥٨)، نقل عنه الميدانى في مجمع الأمثال (انظر ع.
التكريتى في: المورد ٣، ٢/ ١٩٧٤/ ١٤).
٢ - وعالج أيضا مسائل لغوية في كتبه في المسائل، انظر فصل النحو.
٣ - «كتاب الأنواء»، انظر في نقول عنه: تاريخ التراث العربى ٧/ ٣٥٧.

<span data-type="title" id=toc-188>وله في لغة القرآن:</span>
١ - «كتاب الأغفال فيما أغفله الزجّاج من المعانى» ويسمى أيضا «المسائل المصلحة» (١٠٦)، وهو في انتقاد كتاب «معانى القرآن» للزجاج (المتوفى سنة ٣١١/ ٩٢٣)، ومذكور في القوائم، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣١٠)، وانظر فيما نقل عنه في الكتب، وفي محتواه عبد الفتاح اسماعيل شلبى، «أبو على الفارسى»، القاهرة ١٩٥٨، ص ٤٧٦ - ٤٨٧. المخطوطات: شهيد على ٢٩٧ (المجلد الثانى، ٢٢٩ ورقة، نسخت سنة ٦٥٤ هـ)، و ٢٩٨ (المجلد الاول، ١٩٨ ورقة، نسخت سنة ٦٥٤ هـ)، دار الكتب بالقاهرة، / تفسير ٥٢ (٢٢٩ ورقة، من القرن السادس للهجرة، وانظر الفهرس الثانى ١/ ٣٢؛ فهرس المخطوطات ١/ ٢٠)، وتفسير ٦٩٩ (انظر الفهرس الثانى ١/ ٣٣)، وتفسير ٨٧٥ (انظر عبد الفتاح اسماعيل شلبى، المرجع المذكور ص ٤٧٩).
ولابن خالويه (المتوفى سنة ٣٧٠/ ٩٨٠)، كتاب في نقده سمّاه «ردّ كتاب الأغفال» انظر في نقول عنه: ص ١٨٠ بعد. ولأبى على رد عليه باسم «نقض الهاذور (١٠٧) (انظر خزانة الادب ١/ ٣٥٢)، ذكره ياقوت والقفطى، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣١٠)، ونقل عنه في «خزانة الأدب» (انظر «إقليد الخزانة» للميمنى ١٢٥)، راجع «أبيات المغنى» للبغدادى ٤/ ٣٤٩، وترجع نقول
_________
(١٠٦) وخطأ «إعراب القرآن، (Br.SIII ,١١٩٥) «انظر فهرس دار الكتب الثانى ١/ ٣٣.
(١٠٧) من الهذر، وهو الكثير الردئ، أو سقط الكلام.','1','188',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (185,'البغدادى إلى نسخة كان يملكها، انظر كتابه «حاشية على شرح بانت سعاد» ١/ ٩٢.
٢ - «كتاب تفسير (يا أيها الذين آمنوا إذا قمتم إلى الصلاة)»، ذكره ياقوت.
٣ - «كتاب التّتبّع لكلام أبى على الجبّائى في التفسير»، على «كتاب التفسير» الذى ألفه أبو على محمد بن عبد الوهاب الجبّائى المعتزلىّ (المتوفى سنة ٣٠٣/ ٩١٥، انظر تاريخ التراث العربى ١/ ٦٢١ - ٦٢٢)، «نحو مائة ورقة» «إرشاد الاريب» لياقوت ٣/ ١٣.

<span data-type="title" id=toc-189>على بن حمزة البصرى</span>
هو أبو القاسم على بن حمزة البصرى، لغوى لا نعلم من أمره غير هذا، وكثيرا ما يقع الخلط بين اسمه واسم حمزة بن الحسن الأصفهاني. وكتابه الوحيد الذي وصل إلينا في أغلاط اللغويين، يدل على ذهن حادّ نقّاد. توفى سنة ٣٧٥/ ٩٨٥ بصقلية.
«إرشاد الاريب» لياقوت ١ ٥/ ٢٠٢ - ٢٠٤، ٢ ١٣/ ٢٠٨ - ٢١١؛ ابن قاضى شهبة، المخطوط ص ٤٢١ - ٤٢٢؛ «بقية الوعاة» للسيوطى ٣٣٧؛ «هدية العارفين» للبغدادى»؛ Rescher ,Abri BII ,٢٠٦ - .٦٨٢ /١ معجم المؤلفين» لكحالة ٧/ ٨٣.

<span data-type="title" id=toc-190>ومن كتبه في اللغة:</span>
(أ) «كتاب التنبيهات على أغاليط (أغلاط) الرواة»، انتقادات وتصحيحات على الكتب الآتية:
١ - كتاب النوادر لأبى زيد الكلابى (انظر ص ٣٩ آنفا)، ٢ - كتاب النوادر لأبى عمرو الشيبانى (انظر ص ١٢٢ بعد)، ٣ - كتاب النبات لأبى حنيفة الدينورى (انظر تاريخ التراث العربى ٤/ ٣٤٣)،','1','189',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (186,'٤ - كتاب الكامل للمبرد (انظر فصل الأدب)، ٥ - كتاب الفصيح لثعلب (انظر ص ١٤١ بعد)، ٦ - الغريب المصنف لأبى عبيد (انظر ص ٨٢ آنفا)، ٧ - إصلاح المنطق لابن السكيت (انظر ص ١٣٠ بعد)، ٨ - المقصور والممدود لابن ولّاد (انظر فصل النحو).
وتورد قوائم الكتب أسماء أجزاء الكتاب مقرونا كل منها باسمه الخاص (الردّ على: .. فى ... )، ويذكر بعضها أيضا في المراجع على أنها كتب مفردة (انظر مثلا «إرشاد الأريب» لياقوت ٥/ ٨١: كتاب الرد على ابن ولّاد في المقصور والممدود).
والجزء الأخير، وهو نقد لكتاب «الحيوان» للجاحظ، رآه ياقوت مع سائر الأجزاء بمصر، لم يصل إلينا مخطوطا.

<span data-type="title" id=toc-191>المخطوطات:</span>
دار الكتب بالقاهرة، لغة ٥٠٢ (١١١ ورقة، آخره ناقص، من القرن السادس للهجرة، راجع فهرس المخطوطات ١/ ٣٤٧ - ٣٤٨)، وأغلب المخطوطات الآتى ذكرها إنما هى نسخ عن تلك المخطوطة، ومنها: دار الكتب لغة ٥٠٣ و ٢٢ ش (انظر الفهرس الثانى ٢/ ٩)، مصورة منها هـ ٤٥٤٧ (انظر الملحق ١/ ١٨٦)، التيمورية بدار الكتب، لغة ١٨١ (حديثة) و ١٨٢ (حديثة، راجع مجلة المجمع العلمى العربى بدمشق ٣/ ١٩٢٣/ ٣٤٠)، الأزهر، لغة ١٥٣ (١٥٣ ورقة، انظر الفهرس ٤/ ١١)، ويحتمل أن منه المخطوط الغفل من المؤلف/: «التنبيهات على كامل المبرّد» فى المكتبة العامة بالرباط، الحمزاوية ١٨٩/ ٢ (انظر م.
المنونى في: تطوان ٨/ ١٩٦٣/ ١٣٨)، المتحف البريطانى ١١٥) ٣٠٨١ Or. ورقة، نسخت سنة ١٢٩٥ هـ، انظر الملحق رقم ٤٨١، مكتبة جامعة ستراسبورج، مجموعة شپيتا سابقا (حديثة، انظر (Th.Noldekein: ZDMG ٤٠ /١٨٨٦ /٣١٣ ليدن) ١/ ٤٤٦ Or.، الأوراق ١ - ١٠ أ، نسخت سنة ٤٩٥ هـ، الردود على المبرد','1','190',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (187,'وابن السكيت وثعلب، انظر فورهوف ٣٦٥)، ييل ١١٠) ٤٢ - L، ورقة، نسخت سنة ١٣٠٧ هـ، انظر نيموى رقم ٤٢٥)؛ نشره عبد العزيز الميمنى، القاهرة ١٩٦٧ (مع المقصور والممدود للفراء، انظر رمضان عبد التواب في: مجلة كلية اللغة العربية، الرياض ٧/ ١٩٧٧/ ٧٩)، وانظر فى محتواه:
P. Bronnle, '' Aliibn Hamzaandhiscriti cismsonfamous Arabic Philologists in: Actesxlle Congr. intern. Orient., Bd. III, ٢, Rom ١٨٩٩, S. ٥ - ٣٢; R. Bell in: JRAS ١٩٠٤, S. ٩٥ - ١١٨.
(ب) «كتاب الآباء والأمّهات»، ذكره البغدادى.
وفي ميدان الأدب، قيل إن على بن حمزة البصرى جمع أو صنع «ديوان شعر أبى طالب». ولم يصل الديوان إلينا، انظر تاريخ التراث العربى ٢/ ٢٧٤.

<span data-type="title" id=toc-192>ابن المعلّى الأزدى</span>
هو أبو عبد الله محمد بن المعلّى الأسدىّ الأزدى البصرى.
كان نحويا ولغويا بصريا. وتعيّن المصادر زمان حياته تعيينا غير مباشر، وذلك عن طريق أسماء من روى عنهم.
والأغلاط الفاحشة الغريبة عند ياقوت والتى توهم بانه عاش قبل زمان حياته الحقيقى بمائة سنة، يمكننا- وذلك استنادا إلى إسنادات مباشرة في مصنّفه «كتاب الترقيص» (نقله السيوطى في المزهر) وبيانات أوردها الصفدى- أن نصلحها على النحو الآتى: ابن المعلىّ روى عن الشاعر ابن لنكك البصرى (المتوفى نحو سنة ٣٦٠/ ٩٧٠، انظر تاريخ التراث العربى ٢/ ٥١٠) ومن طريق معاصره أبى رياش البصرى (انظر إنباه الرواة للقفطى ٤/ ١١٨ - ١١٩) عن ابن دريد وغيره.
وقيل إنه فضلا عن ذلك حصل على إجازة من ابن دريد (المتوفى سنة ٣٢١/ ٩٣٣) بالرواية عنه. والقول بأنه روى عن أبى إسحاق إبراهيم (ابن العباس) الصولى (المتوفى سنة ٢٤٣/ ٨٥٧)، كما ذكر ياقوت، لا بد ان يكون خلطا بينه','1','191',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (188,'وبين أبى بكر محمد بن يحيى الصولى (المتوفى سنة ٣٥٥/ ٩٤٦). ومن طريق أبى العباس الفضل بن محمد بن سهل (وليس الفضل بن سهل، كما ورد عند ياقوت) روى عن أبى عبد الله محمد بن عبد الله بن عاصم التميمى حزنبل (نحو سنة ٢٧٠/ ٨٨٣، انظر ابن النديم ٧٣، «الوافى بالوفيات» للصفدى ٣/ ٣٢٨)، الذي كان تلميذا لابن السكيت (المتوفى نحو سنة ٢٤٤/ ٨٥٨).
أما سائر من روى عنهم فيصعب التحقق منهم، ولعلهم غير معروفين تماما./ ومما سلف ذكره يمكن أن نخلص إلى أنّ ابن المعلى قد عاش فيما بين حوالى سنة ٣١٠/ ٩٢٢ وسنة ٣٨٠/ ٩٩٠ (يصحح ما ورد في تاريخ التراث العربى ٢/ ٢٤٩).
«إرشاد الأريب» لياقوت ٧/ ١٠٧؛ «الوافى بالوفيات» للصفدى ٥/ ٤٣؛ «بغية الوعاة» للسيوطى»؛ Br.S.I ,٢٠ - .١٠٦ معجم المؤلفين» لكحالة ١٢/ ٤٢.

<span data-type="title" id=toc-193>ولابن المعلى من الكتب:</span>
١ - «كتاب الترقيص»، وهو بحسب عنوانه مجموعة من أغانى المهد، ولكن يبدو من غير المحقق أن ذلك كان الغرض الحقيقى الذى توخاه المؤلف من الكتاب، فليس من بين النقول الخمسة عشر المعروفة إلا نقلان خاصان حقيقة بأغانى المهد، أما سائرها فتشتمل على مادة لغوية تتعلق في المقام الأول بالهجاء الصحيح لصيغ أعلام وأسماء منسوبه، وبعض ذلك بمثابة شرح على أبيات من الشعر.
ولا يبعد أن يكون المؤلف قد أراد بلفظ الترقيص معنى «المرقص، المطرب»، وأن يجمع كتابا يضمنه منتخبات مما استطرفه من الشعر، وعنى فيه على ما يظهر بالشرح اللغوى عناية خاصة. وكان الكتاب من مصادر الصغانى في «التكملة» (انظر مقدمة المحقق ١/ ٨) و «العباب» (انظر ١/ ٣٠). وانظر في نقول عنه:
«الإصابة» لابن حجر ٣/ ٨٨٥، ٤/ ٦٦٢، «خزانة الأدب» للبغدادى ٤/ ٤١؛','1','192',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (189,'«المزهر» للسيوطى ١/ ١٤٠، ٣٥٣، ٣٧٣، ٣٩٦ - ٣٩٧، ٤٤٠، ٢/ ١٥٠، ٢٤٠، ٢٥١، ٢٧٠، ٣٠٧ - ٣٠٩، ٣٢٩، ٤٤٩، ٤٥٣، ٤٦٠.
٢ - «شرح ديوان ابن مقبل» (انظر تاريخ التراث العربى ٢/ ٢٤٩)، وأورد ياقوت نقولا كثيرة عن نسخة بخط الشارح، فى معجم البلدان (انظر الفهرس).
٣ - «كتاب المشاكهة (فى اللغة)»، وهو تصنيف في اللغة، نقل عنه السيوطى فى «المزهر» ١/ ٣٩٤، ٥٨٢، ٢/ ٩١، ١٠٦، ٤٥٢.

<span data-type="title" id=toc-194>الرّمّانى</span>
هو أبو الحسن على بن عيسى بن على (ولد سنة ٢٩٦/ ٩٠٩، وتوفى سنة ٣٨٤/ ٩٩٤)، وفضلا عن اشتغاله بالنحو (انظر فصل النحو) اشتغل أيضا باللغة وعلوم القرآن.
انظر في قوائم كتبه: ابن النديم ٦٣ - ٦٤ (طهران ٦٩؛ الترجمة الانجليزية ١٣٩)؛ «إنباه الرواة» للقفطى ٢/ ٢٩٥ - ٢٩٦؛ «إرشاد الأريب» لياقوت ٥/ ٢٨١ - ٢٨٢؛ «بغية الوعاة» للسيوطى ٣٤٤.

<span data-type="title" id=toc-195>ومن آثاره اللغوية:</span>
١ - «الألفاظ المترادفة أو المتقاربة المعنى»، ولم يرد ذكره في القوائم، ويوجد مخطوطا في دار الكتب بالقاهرة، لغة ٢ (نسخ سنة ١٢٨٤ هـ، انظرالفهرس الثانى ٢/ ٤)، ييل ٨) ١٥٠ - L، ورقات، نسخت سنة ١٣٠٦ هـ، انظر نيموى، رقم ٢٠١)؛ ونشره مع شرح م. م. الرفاعى، القاهرة ١٣٢١، وطبع في ديوباند (الهند) ١٣٣٤.
٢ - «كتاب الاشتقاق» (انظر «المزهر» للسيوطى ١/ ٣٥١) - ذكر ابن النديم، والقفطى، وياقوت أنه صنف «كتاب الاشتقاق الكبير» وكذلك «كتاب الاشتقاق الصغير»، أو «المستخرج» - وكان موجودا في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر پولس سباط في (MIE ٤٩ /١٩٤٦ /٦ ,No.١٠٦:','1','193',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (190,'<span data-type="title" id=toc-196>وله في لغة القرآن:</span>
١ - «الجامع في علم (أو تفسير) القرآن»، وهو «كتابه المشهور في التفسير» («نزهة الألباء» للأنبارى ٢١٨)، ذكره ياقوت والقفطى، وهو من مصادر ابن سيده (انظر «المخصص» ١/ ١٣)، ووردت نقول عنه في كتاب «الجماهر» للبيرونى، / حيدرآباد ١٣٥٥، ص ٢٢٣.

<span data-type="title" id=toc-197><span data-type="title" id=toc-198>المخطوطات:</span></span>
الجزء السابع، باريس ٦٥٢٣ (١٩٠ ورقة، من القرن السابع، راجع فجدا ٣٥٢)، الجزء العاشر، طشقند، الأكاديمية ٣١٣٧ (نسخة قديمة، انظر مجلة معهد المخطوطات العربية ٦/ ١٩٦٠/ ٣٢٣)، الجزء الثانى عشر، المسجد الأقصى بالقدس ٢٩ (١٥٠ ورقة، ومنه ميكروفلم بمعهد المخطوطات بالقاهرة (على ما ذكر مازن المبارك في «الرمانى النحوى»، بيروت ١٩٧٤، ص ٦٤).
وما ورد في فهرس المكتبة التيمورية (١/ ٧٦، ١٧١) من أن المخطوطة المرقمة تفسير ٢٠١ (نسخت سنة ١٠٩٦ هـ) تحتوى على «تفسير جزء عم» ... للرمانى، قائم على وهم.
٢ - «كتاب إعجاز القرآن» (كما ذكر ابن النديم، وياقوت، و پولس سباط في، (MIE ٤٩ /١٩٤٦ /٧ ,No ,١٢٧ أو «النّكت في إعجاز القرآن» (كما ذكر القفطى، والمخطوطات)،
المخطوطات:
بغدادلى وهبى ٦٢ (٢٢ ورقة، نسخت سنة ٦٥٢ هـ، انظر فهرس المخطوطات ١/ ٤٩ - ٥٠)، طلعت بدار الكتب بالقاهرة، بلاغة ٢٣٢١ (٥٢ ورقة، نسخت سنة ٦٠٤ هـ)، التيمورية، مجموع ٢٩٨ (نسخت سنة ١٣١٨ هـ)، وتفسير ٥٣٤ (نسخت سنة ١٣١٨ هـ، انظر الفهرس ١/ ١٥٣، ٢٤٣)؛ نشره ع. الصديقى، دلهى ١٩٣٤.','1','194',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (191,'ثم نشره أيضا محمد خلف الله وزغلول سلّام في: ثلاث رسائل في إعجاز القرآن»، القاهرة ١٩٥٥، طبعة ثانية ١٩٦٨، ص ١٠٥ - ١٤٤، انظر في هذه النشرة عمر رضا كحالة في: مجلة المجمع العلمى العربى بدمشق ٤٦/ ١٩٧١/ ١٦٠ - ١٦١، راجع محمد خلف الله في: مجلة كلية الآداب (جامعة الاسكندرية) ٨/ ١٩٥٤/ القسم الأوربى ١٥ - ١٦. وانظر في الكتاب
H. Foda, Laformuledusens: essaisur, an- Nukatfii''gazal- Qur''an''''de Rummaniin: Analysestheorie ١٩٨٢, ١ S- ٤٣ - ٧٨.
٣ - «كتاب غريب القرآن»، لم يذكره إلا القفطى.
٤ - «كتاب المتشابه في علم القرآن»، لم يذكره إلا القفطى.
٥ - «كتاب المختصر في علم السور القصار» لم يذكره أيضا سوى ابن القفطى.
٦ - «كتاب الألفات (فى القرآن)»، ذكره ابن النديم والقفطى وياقوت.
٧ - «شرح كتاب المعانى»، شرح على «معانى القرآن» لابراهيم بن السرى الزجاج (المتوفى سنة ٣١١/ ٩٢٣)، ذكره القفطى وياقوت.

<span data-type="title" id=toc-199>استدراك على الجزء الأول من تاريخ التراث العربى:</span>
على ما ذكر القفطى في إنباه الرواة ٢/ ٢٩٥ - ٢٩٦ ألّف الرومانى في الاعتزال والفقه والفلسفة:
١ - «كتاب صنعة الاستدلال»، ويضم سبعة كتب.
٢ - «شرح معونة»، وهو شرح لكتاب أبى بكر أحمد بن على بن الإخشيد المعتزلى (المتوفى سنة ٣٢٦/ ٩٣٨، انظر «معجم المؤلفين» لكحالة ١/ ٣٢٠، ٢/ ٢٣ - ٢٤) فى الأصول، وقد بقى ناقصا كأصله.
٣ - «نكت المعونة بالزيادات»، على الكتاب السابق نفسه.
٤ - «كتاب جواب ابن الإخشيد في علم القرآن».
٥ - «كتاب الأسماء والصفات (لله)».
٦ - «شرح الأسماء والصفات لأبى على»، ولعل المراد به هو أبو على محمد بن','1','195',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (192,'عبد الوهاب الجبّائى (المتوفى سنة ٣٠٣/ ٥١٥، انظر تاريخ التراث العربى ١/ ٦٢١ - ٦٢٢).
٧ - «كتاب فى الصفات»، كتاب صغير. وذكره السيوطى (بغية الوعاة ٣٤٤) وياقوت (إرشاد الأريب ٥/ ٢٨١) يذكر «كتاب شرح الصفات».
٨ - «كتاب صفات النفس».
٩ - «كتاب ما يجوز على الأنبياء وما لا يجوز».
١٠ - «كتاب الرّويّة (فى النقض على الأشعرى)»، وهو نقض على أبى الحسن على بن اسماعيل الأشعرى (المتوفى نحو سنة ٣٢٤/ ٩٣٥، انظر تاريخ التراث العربى ١/ ٦٠٢ - ٦٠٤).
١١ - «كتاب نقض التثليث (على يحيى بن عدى)»، وهو انتقاد لعقيدة التثليث وجّهه إلى يحيى بن عدى (المتوفى سنة ٣٦٤/ ٩٧٤).
١٢ - «كتاب تجانس الأفعال».
١٣ - «كتاب استحقاق الذمّ».
١٤ - «كتاب نقض استحقاق الذمّ» على أبى هاشم عبد السلام بن محمد الجبائى (المتوفى سنة ٣٢١/ ٩٣٣، انظر تاريخ التراث العربى ١/ ٦٢٣).
١٥ - «كتاب الرد على المسائل البغداديات»، وهو رد على مسائل/ أبى هاشم، ذكره أيضا ابن المرتضى فى «المعتزلة» ص ١١٠، وانظر فى نقل عنه: «سر الفصاحة» للخفاجى ١٤١، راجع تاريخ التراث العربى ١/ ٦٢٣.
١٦ - «كتاب المجالس (فى استحقاق الذم)».
١٧ - «كتاب الإمامة»؛ وقد ألف محمد بن محمد بن النعمان الشيخ المفيد المتوفى سنة ٤١٣ هـ/ ١٠٢٢ م كتابا فى نقضه سماه: «كتاب النقض على علىّ بن عيسى فى الإمامة»، انظر تاريخ التراث العربى ١/ ٥٤٩ - ٥٥١)، انظر فهرست الطوسى ١٨٧.
١٨ - «كتاب الرؤية».
١٩ - «كتاب السؤال والجواب»؛','1','196',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (193,'٢٠ - «كتاب الأكوان»؛ ٢١ - «كتاب تحريم المكاسب»؛ ٢٢ - «كتاب الخطر والإباحة».
٢٣ - «كتاب مسائل أحمد بن إبراهيم البصرى» (أبو رياش، انظر ص ١١١ آنفا)؛ ٢٤ - «كتاب مسائل ابن جابى (؟ )».
٢٥ - «كتاب مسائل أبى على بن الناصر فى القرآن»، ومن المحتمل جدا انه ابن للحسن بن على بن الحسن الناصر الكبير (المتوفى سنة ٣٠٤/ ٩١٧، انظر «معجم المؤلفين» لكحالة ٣/ ٢٥٢، «المعتزلة» لابن المرتضى ١١٧).
٢٦ - «كتاب مجالس ابن الناصر».
٢٧ - «كتاب جواب مسائل طلحة فى علم القرآن»، ولعله هو المؤرخ.
المحدّث، المعتزلى، طلحة بن محمد بن جعفر الشاهد (المتوفى سنة ٣٨٠/ ٩٩٠، انظر «الأعلام» للزركلى ٣/ ٣٣٢).
٢٨ - «كتاب مسائل أبى العلاء (؟ )؛ ٢٩ - «كتاب المسائل فى اللطيف من الكلام»؛ ٣٠ - «كتاب المسائل والجواب فى الأصلح الواردة من مصر»؛ ٣١ - «كتاب الأصلح الكبير»؛ ٣٢ - «كتاب الأصلح الصغير»؛ ٣٣ - «كتاب تهذيب الأصلح»؛ ٣٤ - «كتاب جوامع العلم (فى التوحيد)»؛ ٣٥ - «كتاب أدلةالتوحيد»؛ ٣٦ - «كتاب الإرادة»؛ ٣٧ - «كتاب نكت الإرادة»؛ ٣٨ - «كتاب المعلوم والمجهول والنفى والإثبات»؛ ٣٩ - «كتاب الأسباب»؛ ٤٠ - «كتاب الحقيقة والمجاز»؛','1','197',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (194,'٤١ - «كتاب نكت الأصول»؛ ٤٢ - «كتاب أدب الجدل»؛ ٤٣ - «كتاب أصول المجاز»؛ ٤٤ - «كتاب أصول الفقه»؛ ٤٥ - «كتاب الرد على الدهرية»؛ ٤٦ - «كتاب المنطق»؛ ٤٧ - «كتاب الرسائل (فى الكلام)»؛ ٤٨ - «كتاب القياس»؛ ٤٩ - «كتاب مبادئ العلوم»؛ ٥٠ - «كتاب العلوم»؛ ٥١ - «كتاب المباحث»؛ ٥٢ - «كتاب المعارف»؛ ٥٣ - «كتاب الأوامر»؛ ٥٤ - «كتاب العلل»؛ ٥٥ - «كتاب العوض»؛ ٥٦ - «كتاب التوبة»؛ ٥٧ - «كتاب مقالات المعتزلة»؛ ٥٨ - «كتاب الأخبار والتمييز»؛ ٥٩ - «كتاب تفضيل على (بن أبى طالب)»؛ ٦٠ - «كتاب الرد على من قال بالأحوال»؛ ٦١ - «كتاب التعليق»؛ ٦٢ - «كتاب فى الطبائع»؛ ٦٣ - كتاب أماليه.

<span data-type="title" id=toc-200>ابن شاهين</span>
أبو العباس أحمد بن سعيد بن شاهين بن على بن ربيعة البصرى، وهو لغوى مغمور، يحتمل أنه عاش فى القرن الرابع/ العاشر.','1','198',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (195,'ابن النديم ٨٠؛ «إرشاد الأريب» لياقوت ١/ ١٣٤؛ «الوافى بالوفيات» للصفدى ٦/ ٣٨٩؛ «بغية الرعاة» للسيوطى ١٣٣ - ١٣٤.

<span data-type="title" id=toc-203>ومن آثاره </span>«كتاب ما قالته العرب وكثر فى أفواه العامة»؛ ذكرته المصادر الأنفة الذكر.
<span data-type="title" id=toc-201>ب- الكوفة</span>
<span data-type="title" id=toc-202>الشرقى بن القطامىّ</span>
هو أبو المثنّى الوليد بن الحصين بن جمال، كان لغويا كوفيا، نسّابة، راوية للشعر، وما يتعلق به من أخبار، قصّاصا، ومحدثا «ضعيفا»، وجغرافيا (انظر فصل الجغرافية).
روى عن لقمان بن عامر، ومجالد بن سعيد، وغيرهما.
ومن رواته ابن الكلبى، والمدائنى، ويزيد بن هارون.
استقدمه الخليفة المنصور إلى بغداد، وعهد إليه بتأديب الأمير المهدىّ. توفى نحو سنة ١٥٠/ ٧٦٧.
«الحيوان» للجاحظ ٣/ ٢٠٩، «المعارف» لابن قتيبة ٥٣٩؛ تاريخ الطبرى ٢/ ٤٨١؛ «مروج الذهب» للمسعودى ١/ ١٠، ٢١٦، ٦/ ٢٥١؛ ابن النديم ٩٠؛ «المقتبس» للمرزبانى ٢٧٥ - ٢٧٦؛ «تاريخ بغداد» ٩/ ٢٧٨ - ٢٧٩؛ «نزهة الألباء» ٢٢ - ٢٣؛ «لسان الميزان» لابن حجر ٣/ ١٤٢ - ١٤٣؛ «المزهر» للسيوطى Sellheim ,Sprichworter ٣٠ - ٣١.٤١٤، ٣٤٧ /٢
ومن آثاره:
١ - «قصيدة الغريب» (قد تكون مصحوبة بشرح)، ذكرها ابن النديم ١٧٠؛ وربما نقل عنها فى «البارع» للقالى ٥٥٩، «التهذيب» للأزهرى ٥/ ١٨٨؛','1','199',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (196,'و «الصحاح» للجوهرى (١) ٤٣؛ و «الحيوان» للجاحظ ٦/ ٤٧٨، ٧/ ٣١ - ٣٢، ٢٤٥.
٢ - «كتاب الأمثال»، ذكره حاجى خليفة، طبعة فلوجل ٥/ ٣٩٢ (انظر زلهيم، المرجع المذكور ص ٣٠)؛ ويحتمل ورود نقول عنه فى «الفاخر» للمفضل ٢٥، ٣٨، ٧٩، ٩٤، و «الدرة الفاخرة» لحمزة الأصفهاني (١) ١٧٥، (٢) ٤٢١ - ٤٢٢، ٤٣٢؛ و «فصل المقال» للبكرى ٢٦٣ - ٢٦٤، ٣٥٤ - ٣٥٥؛ و «مجمع الأمثال» (انظر ع. التكريتى فى: المورد ٣، ٣/ ١٩٧٤/ ١١٣)؛ و «الزاهر» للأنبارى ١/ ٤٥٩، ٢/ ٢٣٨.
وانظر في مروياته من الشعر والتاريخ: «النقائض» لأبى عبيده ٧٤٧ - ٧٤٨؛ «عيون الأخبار» لابن قتيبة ١/ ١٣٩ - ١٤٠، ٢/ ١٣٩؛ «مروج الذهب» ٣/ ١٤٢، ٢٤٧ - ٢٤٩؛ ٤/ ٣٧١، ٣٨٧، ٦/ ٢٥١ - ٢٥٦؛ «الأغانى» ٢/ ١٣١ - ١٣٢؛ ٥/ ٦٢ - ٦٤؛ ١١/ ٥٣ - ٥٤؛ ١٥/ ٣٨ - ٤٥، ٣١٢ - ٣١٤؛ ١٦/ ٢٠٤ - ٢٠٥، ٣٥٤، ٣٥٧، ٣٧٣، ١٩/ ٢٢، ١٣٠ - ١٣٢، ٢٢/ ٨٦ - ٩٠، ٣٥٥ - ٣٥٩؛ «الحيوان» للجاحظ ٥/ ٣٠٢ - ٣٠٣؛ وانظر أيضا تاريخ الطبرى، الفهرس ص ٢٦٨.
وكان ممن ألّفوا فى أخبار العشاق، انظر ابن النديم ٣٠٦.
انظر أيضا فصل الجغرافية.

<span data-type="title" id=toc-204>المفضّل الضّبّىّ</span>
هو أبو العباس المفضّل بن محمد بن يعلى الضّبّىّ، ولد فى الكوفة، وكان زوج أم ابن الأعرابى ومعلّمه./ وكان من خيرة علماء الكوفيين. أخذ عن سماك بن حرب، وأبى اسحاق السبيعى، وعاصم بن أبى النّجود وغيرهم.','1','200',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (197,'ومن تلاميذته الفراء، وابن الأعرابى. وكان أكبر عنايته بالشعر، ولكنه كان أيضا عالما بالنحو، والغريب، وأيام العرب (انظر بغية الوعاة للسيوطى ٣٩٦). توفى نحو سنة ١٧٠/ ٧٨٦.
«المعارف» لابن قتيبة ٥٤٥، ٥٤٦؛ ابن النديم ٦٨ - ٦٩؛ «مراتب النحويين» لأبى الطيب ٧١؛ «طبقات النحويين» للزبيدى ٢١٠؛ «المقتبس» للمرزبانى ٢٧٢ - ٢٧٤؛ «مجالس العلماء» للزجاجى ١٤ - ١٥، ٣٥ - ٤١؛ «تاريخ بغداد» ١٣/ ١٢١ - ١٢٢؛ «إرشاد الأريب» لياقوت ٢ ١٩/ ١٦٤ - ١٦٧؛ «نزهة الألباء» للأنبارى ٢ ٣٥ - ٣٧؛ «إنباه الرواة» للقفطى ٣/ ٢٩٨ - ٣٠٥؛ ابن قاضى شهبة، المخطوط ص ٥٠٢ - ٥٠٣؛ «غاية النهاية» لابن الجزرى ٢/ ٣٠٧.
Br. GI, ١١٦, SI, ١٧٩; Rescher, Abri BII, ١٧٥ - ١٧٦; Ilse Lichtenstadterin: EI, III, ٦٧٤ - ٦٧٥;
«الأعلام» للزركلى ٨/ ٢٠٤، «معجم المؤلفين» لكحالة ١٢/ ٣١٦.

<span data-type="title" id=toc-205>ومن آثاره اللغوية:</span>
١ - «كتاب الأمثال»، ذكرته كل القوائم (انظر أيضا فهرست ابن خير ٣٨٤)، وهو أقدم مصنّف في الأمثال، ويحتوى على نحو من ١٦٠ «مثلا»، وكان مصدرا لكثير من المجموعات المتأخرة (انظر (Sellheim ,Sprichworter ٤٥ - ٤٩ ويوجد مخطوطا، مع حواش متأخرة في: أسعد ٣٥٩٨/ ١ (١ أ- ٦٩ ب، من القرن السادس للهجرة).
وطبع في استنبول ١٣٠٠، والقاهرة ١٣٢٧.
٢ - «كتاب الألفاظ»، ذكره ابن النديم وياقوت، وكان موجودا في القرن ٧/ ١٣ فى مكتبة بحلب (انظر پولس سباط في. (MIE ٤٩ /١٩٤٦ /٨ ,No.١٤٤: ومن الجائز أنه كان مصدرا من مصادر الأزهرى (التهذيب ١/ ١٠) التى لم ينص عليها.

<span data-type="title" id=toc-206>القاسم بن معن</span>
هو أبو عبد الله القاسم بن معن بن عبد الرحمن المسعودى الكوفى، كان فقيها،','1','201',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (198,'وتولى القضاء في الكوفة. اشتغل باللغة، والشعر، والنحو، والحديث. وأثنوا عليه كثيرا لثقته وعلمه. حضر أيضا مجالس أبى حنيفة.
ومن تلامذته ابن الأعرابى، والليث بن المظفر، والفراء. توفى سنة ١٧٥/ ٧٩١، وفي قول آخر سنة ١٨٧/ ٨٠٣.
ابن النديم ٦٩؛ «طبقات النحويين» للزبيدى ١٤٦ - ١٤٧؛ «المقتبس» للمرزبانى ٢٧٩ - ٢٨١؛ «إرشاد الأريب» لياقوت ٦/ ١٩٩ - ٢٠٢؛ «إنباه الرواة» للقفطى ٣/ ٣٠ - ٣١؛ «تهذيب التهذيب» لابن حجر ٨/ ٣٣٨ - ٣٣٩؛ ابن قاضى شهبة، المخطوط ص ٤٨٥ - ٤٨٦؛ «بغية الوعاة» للسيوطى ٣٨١.

<span data-type="title" id=toc-207>ومن أثاره اللغوية:</span>
١ - «كتاب الغريب المصنف»، ذكره ياقوت وغيره، والظاهر أن نقولا عنه وردت في: «القلب والإبدال» لابن السكيت (تحقيق هفنر) ٦٠؛ الصحاح للجوهرى (٣) ٩٩٦: فضلا عن نقلين في «الغريب المصنف» لأبى عبيد (انظر رمضان عبد التواب، الموضع المذكور ص ١٢٧)؛ و «الأمالى» للقالى ٢/ ٢٨٤؛ «مقاييس اللغة» لابن فارس ٢/ ٢٢./ ٢ - «كتاب النوادر (فى اللغة)»، ذكره المرزبانى وغيره، انظر «البيان والتبيين» للجاحظ ١/ ٤٦؛ و «الموشح» للمرزبانى ٢٣٨؛ و «العقد الفريد» لابن عبد ربه ١/ ٢٤٦؛ و «المذكر والمؤنث» لابن الانبارى ٦٥٨.
هذا وقد قيل إنه ألف كتابا في النحو.

<span data-type="title" id=toc-208>الكسائى</span>
هو أبو الحسن على بن حمزة بن عبد الله (المتوفى سنة ١٨٩/ ٨٠٥)، نحوى مشهور، إلا أن الآراء مختلفة فيه (انظر فصل النحو)، وهو أحد القراء السبعة، ويعد فضلا عن ذلك من اللغويين.','1','202',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (199,'<span data-type="title" id=toc-209>له من الكتب:</span>
١ - «كتاب ما يلحن فيه العوام»، لم تذكره قوائم مصنفاته، ويوجد مخطوطا فى: برلين ١١١، ١٩٩ PM.) ٧١٠٣ ب ١١٥ أ)، المتحف البريطانى ٩) ٣/ ١٢٠٧٠ Or.، ب- ١٦ ب، نسخت سنة ٣٣٠ هـ)، طهران، كلية الحقوق ٧٥ - ب (٤٩ ب- ٥٤ ب، انظر الفهرس ٤٥١ - ٤٥٢)، الأوقاف ببغداد، مجموعة كوركيس عواد، القسم الخامس في مجموع (٤١ أ- ٤٦ أ، نسخ سنة ١٠٥٩ هـ، انظر مجلة المجمع العلمى العربى بدمشق ٢٤/ ١٩٤٩/ ٢٨٣)، طلعت بدار الكتب بالقاهرة، مجموع ١٩٠ (ص ٣٠٧ - ٣٢٦، من القرن التاسع للهجرة)؛ نشره بروكلمان على مخطوطة برلين في) ZA ١٣ /١٨٩٨ /٢٩ - ٤٦ وانظر فيه نولدكه، المجلة نفسها ص ١١١ - ١١٥).
ونشره على مخطوطة التيمورية بدار الكتب ٣٦٩ (انظر مجلة المجمع العلمى العربى بدمشق ٥٤/ ١٩٧٩/ ٣٦٧) عبد العزيز الميمنى في: «ثلاث رسائل»، بالقاهرة ١٣٤٤، ص ١٩ - ٦٨، ومنه قطعة للمقارنة بنصوص أخرى في اللحن أوردها السيد يعقوب بكر في: «نصوص في فقه اللغة العربية»، الجزء الأول، بيروت ١٩٧٠، ص ٣٦٢ - ٣٧٧.
٢ - «كتاب النوادر»، وعلى ما ذكر ابن النديم، كان موجودا في ثلاث روايات مختلفة، انظر تاريخ التراث العربى ٢/ ٨٦. وقد كان برواية الفراء مصدرا من مصادر الأزهرى (انظر ١/ ١٦)؛ وهو أيضا من مصادر الصغانى في «التكملة» (انظر ١/ ٨) و «العباب» (انظر ١/ ٢٩)، ويحتمل أن عنه النقول التالية: فى «العباب» ١/ ٤٨، ٦٦، ٧٦، ٨٣، ١١٠، ١١٩، ١٣٩، ١٤١، ١٥٤، ١٥٩، ١٦١، ١٧٢، ١٧٧، ١٨٢، ١٨٦، ١٨٩، ٢٠٠، ٢٠٥.
ووردت نقول كثيرة عنه في «كتاب النوادر» لأبى مسحل (انظر الفهرس ص ٧١٧). ونقل عنه صاحب «تاج العروس» ١١/ ٤٦ (حضر)؛ ومن المحتمل أيضا أن ابن الحداد قد نقل عنه في كتاب «الأفعال» فى مواضع كثيرة، (انظر الفهرس ٤، ٢/ ٥٧٢).','1','203',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (200,'<span data-type="title" id=toc-210>وله في لغة القرآن:</span>
١ - «كتاب معانى القرآن»، ذكرته كل القوائم (انظر أيضا «تاريخ بغداد» ١١/ ٤١١)، وهو أحد أهم كتبه، ومصدر من مصادر الثعلبى في «الكشف»، والأزهرى (انظر ١/ ١٦).
٢ - «متشابه القرآن»، لم تذكره قوائم مصنفاته،

<span data-type="title" id=toc-211>المخطوطات:</span>
بايزيد ٤٣٦/ ١ (الأوراق ١ - ٧٥ أ، من القرن الثامن الهجرى، راجع فهرس المخطوطات ١/ ٤٥؛ و، (O.Pretzlin: Islamica ٦ /١٩٣٤ /٢٤١ الظاهرية، عام ٥٥٢٠ (٣٢ ورقة، نسخت سنة ١٣٤٧ هـ عن مخطوطة أخرى كتبت سنة ٧١٦ هـ، انظر فهرس عزة حسن ١/ ٣٩١)، جامع الشيخ بالاسكندرية ١٣/ ٢ (١٦ ورقة، انظر فهرس المخطوطات ١/ ١٤)، باريس ٦٦٥/ ٤ (٦٤ ب- ٧٧، من القرن ١١ هـ، راجع فجدا ٥١٤)، تشستربيتى ٣١٦٥/ ١ (١ ب- ١٥ ب، من القرن السابع للهجرة). ومن الجائز أن في المخطوطات نصين مختلفين (١٠٨).
وقد نظم «متشابه القرآن» شعرا محمد بن عبد الرحمن بن محمد السخاوى (المتوفى سنة ٩٠٢/ ١٤٩٧)، ورد ذكره في «كشف الظنون» ١٥٨٤./

<span data-type="title" id=toc-212>أبو بكر بن عيّاش</span>
هو شعبة (أو أحمد أو عبد الله) بن عيّاش بن سالم الأسدى الكوفى. ولد سنة
_________
(١٠٨) وتنبغى مقارنتها أيضا بكتاب يحيط به الشك هو «كتاب ما تشابه من ألفاظ القرآن وتناظر من كلمات الفرقان» ويوجد مخطوطا في دار الكتب بالقاهرة، قوله، قراءات ١٥ (الجزء الثانى ضمن مجموع، نحو ٢٠ ورقة، نسخت في القرن ١٢ هـ؛ (انظر الفهرس ١/ ٢٨؛ و (Br.G ٢ I ,١١٨.','1','204',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (201,'٩٥/ ٧١٤. وكان بارعا في قراءة القرآن. روى عن عاصم بن بهدله وعطاء السائب. وروى عنه كثيرون، منهم الكسائى، وأبو عمر الدّورىّ.
وهو واحد من أقدم من عرفنا من المصنفين في غريب الحديث. وشارك فضلا عن ذلك في رواية الشعر. توفى سنة ١٧٣/ ٧٨٩ أو ١٤٥/ ٧٦٢ (انظر «تقريب التهذيب» لابن حجر ٢/ ٣٩٩).
«المعارف» لابن قتيبة ٥٣٩؛ ابن النديم ٢٩؛ «المقتبس» للمرزبانى ٢٨٢ - ٢٨٣؛ «إرشاد الأريب» لياقوت ٢/ ٣٧٣ - ٣٨٠؛ «غاية النهاية» لابن الجزرى ١/ ٣٢٥ - ٣٢٧.
«غريب الحديث»، ذكره له ابن حجر في الموضع المشار إليه آنفا.
ومن المحتمل أنه صنف كتابا ضمّنه أخبار بعض الشعراء، وكثيرا ما يكون هو نفسه صاحب روايتها.
وإلى جانب هذا روى فيه أيضا عن الحسن بن عمرو الفقيمى، والحارث بن عبد الرحمن، ومحمد بن المنتشر، وعبد الملك بن عمير، وعن أبيه.
أما رواته فهم: أبو يعقوب الحزيمى، والهيثم بن عدى، وابن عمار، ويحيى بن آدم، ومحمد بن زهير السعدى، ومحمد بن الطفيل، وأبو محلّم، والزبير بن بكّار، انظر «الأغانى» ١/ ٣١٤، ٢/ ١٧٧، ٩/ ١٦٣، ١١/ ١٩٧، ١٢/ ٦٠ - ٦٥، ١٩٤، ٢٩٩، ١٥/ ١٣٤ - ١٣٥، ١٣٦ - ١٣٧، ٢٤١، ٣٦٧ - ٣٦٨، ٣٧١ - ٣٧٢، ٣٩٤، ٢٢/ ٢٧٨، ٣٤١ - ٣٤٢؛ و «الموشح» للمرزبانى ٢٠٧؛ و «عيون الأخبار» لابن قتيبة ١/ ٥ - ٦، ٦١ - ٦٢، ٢/ ١١٩، ١٣٤، ٣/ ١١٢ - ١١٣، ٤/ ٩٧ - ٩٨، ١٠١ - ١٠٢.

<span data-type="title" id=toc-213>الأحمر</span>
هو أبو الحسن على بن المبارك (وعند ياقوت: الحسن)، أحد أصحاب الكسائى.
نشأ بالبصرة ثم رحل إلى بغداد، حيث دبّر له الكسائى وظيفة مؤدّب ببلاط','1','205',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (202,'العباسيين. ويعدّ الأمين فى تلامذته. وكان حسن العلم بالنحو؛ وبينه وبين الأصمعى والفراء منافسة. توفى سنة ١٩٤/ ٨١٠ فى طريق الحج إلى مكة.
«التهذيب» للأزهرى ١/ ١١؛ ابن النديم ٦٦؛ «المقتبس» للمرزبانى ٣٠١؛ «مراتب النحويين» لأبى الطيب ٨٩؛ «طبقات النحويين» للزبيدى ١٤٧؛ «تاريخ بغداد» ١٢/ ١٠٤ - ١٠٥؛ «إرشاد الأريب» لياقوت ٥/ ١٠٨ - ١١١؛ «إنباه الرواة» للقفطى ٢/ ٣١٣ - ٣١٧، ٤/ ١٠٤؛ «بغية الوعاة» للسيوطى ٣٣٤.

<span data-type="title" id=toc-214>ومن أثاره:</span>
١ - «كتاب التفسير»، ذكره ابن النديم، والقفطى، فى الموضع المذكور ٤/ ١٠٤.
٢ - «يقين البلغاء»، ذكره ابن النديم (واسمه عند ياقوت «تفنن البلغاء»).
وربما كانت النقول التى تربو على ١٥٠ نقلا فى/ «الغريب المصنف» لأبى عبيد مأخوذة عنه، انظر رمضان عبد التواب، الموضع المذكور ص ١٢١؛ انظر أيضا «التهذيب» للأزهرى ١٠/ ١٢٠، ٣٧٠؛ ١٢/ ٤٧، ١٤١، ١٧٢، ١٥/ ٣٣٦؛ «الصحاح» للجوهرى (١) ٧١، ٢١٧، (٤) ١٦٤٣، (٤) ٢٢٨٥؛ «التكملة» للصغانى ٢/ ٢٩٤؛ «البارع» للقالى (فى نحو تسعة مواضع، انظر «الفهرس» ص ٧٤٥)؛ «الألفاظ» لابن السكيت ٧٦، ٤١٣، ٥٣٤؛ «مجمع الأمثال» للميدانى (انظر ع. التكريتى فى: المورد ٣، ٣/ ١٩٧٤/ ١١٦)؛ ومن الجائز أن نقولا أخرى عنه فى: «النبات» لأبى حنيفة ٣/ ٦٠، ٧٩، ١٢٤، ١٣٨، ١٥٠، ١٨٦، ١٩٩؛ «الأفعال» لابن الحداد ١/ ٩٤، ٢/ ١١٩، ٣٢٠، ٤٧١، ٣/ ١٧٢، ٣٤٩؛ «العباب» للصغانى (طاء) ١٣٢؛ «تاج العروس» ١/ ٣٥٩ (فيأ)، ٤/ ١٦ (قرب)، ١٩٠ (لبب)، ٨/ ٢٢٩ (سوذ)، ١٠/ ٤٩ (أسر)، ٦٠ (أصر)، ٤٢٣ (جزر)، ١٢/ ٣٦٠ (صور).
انظر فيما زعموه له فى اللحن، «التصحيف» لأبى أحمد العسكرى ١٧٣ - ١٧٧.','1','206',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (203,'<span data-type="title" id=toc-215>برزخ</span>
هو أبو محمد برزخ (بزرج) بن محمد العروضى، الذى اشتهر بمناقضة معاصره الأسنّ. الخليل بن أحمد، فى العروض (انظر فصل الأدب)، واشتغل فضلا عن العروض باللغة. كانت وفاته نحو سنة ٢٠٠/ ٨١٦.

<span data-type="title" id=toc-216>وله من الكتب:</span>
١ - «كتاب تفسير الغريب»، ذكره ابن النديم ص ٧٢، وذكره غيره.
٢ - «كتاب بناء الكلام»، رآه ابن النديم مخطوطا فى جلود.

<span data-type="title" id=toc-217>نصر بن يوسف</span>
أحد تلامذة الكسائى، ولعل وفاته كانت نحو سنة ٢٠٠/ ٨١٦.
ابن النديم ٦٦؛ «إرشاد الأريب» لياقوت ٧/ ٢١١؛ «بغية الوعاة» للسيوطى ٤٠٤.

<span data-type="title" id=toc-218>ومن آثاره:</span>
١ - «كتاب خلق الإنسان»، ذكره ابن النديم وغيره.
٢ - «كتاب الإبل»، ذكره ابن النديم وغيره.

<span data-type="title" id=toc-219>الأموى</span>
أبو محمد عبد الله بن سعيد بن أبان، أخو يحيى بن سعيد المؤرخ (انظر تاريخ التراث العربى ١/ ٢٩٣) الكوفى، توجه إلى بغداد حيث سمع من زياد بن عبد الله البكائى، وروى أيضا عن أبى ثوابة الأسدى (انظر ابن النديم ص ٤٥؛ «إنباه الرواة» للقفطى ٤/ ٩٨). ووصف بكونه نحويا ولغويا متقنا؛ ولطول إقامته بين','1','207',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (204,'الفصحاء من الأعراب يعدّ أيضا منهم. وممن رووا عنه أبو عبيد بن سلام. توفى بعد سنة ٢٠٣/ ٨١٩ (انظر تاريخ التراث العربى ٢/ ٨٧).
«التهذيب» للأزهرى ١/ ١١ - ١٢؛ ابن النديم ٤٨؛ «طبقات النحويين» للزبيدى ٢١١؛ «إنباه الرواة» للقفطى ٢/ ١٢٠؛ «بغية الوعاة» للسيوطى ٢٨٢./

<span data-type="title" id=toc-220>ومن كتبه:</span>
١ - «كتاب النوادر»، ذكره ابن النديم وغيره، وكان من المصادر الرئيسية لكتاب «النوادر» لأبى مسحل، انظر الفهرس ص ٧١٧؛ ونقل عنه الأزهرى فى «التهذيب» ٦/ ١٩٩، ٣٧٧، ١٠/ ٣٠٦، ١١/ ٣٣، ٣٥٣، ٤٨٧، ١٢/ ٦٦، ١٥٥؛ وفضلا عن ذلك يبدو أنه بقيت لنا أجزاء كثيرة منه- ولكن لم تنقل بحروفها- فى «الغريب المصنف» لأبى عبيد، انظر رمضان عبد التواب، الموضع المذكور ص ١١٧. وانظر كذلك «الصحاح» للجوهرى (١) ٣٩، ٤٧، ٦٠، ٦٥، ٢٢٠، ٢٨٢، ٤٠٥، ٤١٦، (٢) ٦٥٦، ٨١١، ٨٣٢، ٩٨٩، (٣) ١٠١٧، ١٠٤٤، (٤) ١٣٤٩، ١٣٩٩، ١٥١١، ١٦٣٨، ١٦٤٢، (٥) ١٨٠٠، ١٨٨٧، ١٨٩٦، ١٦٠٦، ١٩٢٧، ١٩٥٤، ١٩٧٨، (٦) ٢١٨٦، ٢٢٤٥؛ «مقاييس اللغة» لابن فارس، الفهرس ٦/ ٤٠١؛ «التكملة» للصغانى ٢/ ٣٧٨، ٣/ ٣٤٣، ٤٦٤، ٤٧١، ٤/ ٥٥٤؛ وربما ٥/ ١٢٥، ٣٥، ٣٤٨، ٦/ ٢١٩؛ ٢٦١، ٣١١؛ «البارع» للقالى (فى نحو ١١ موضعا، انظر الفهرس ص ٧٤٦)؛ «إصلاح المنطق» لابن السكيت (انظر الفهرس ٤٩٥)؛ «الألفاظ» لابن السكيت ٩٣، ٩١، ٦٣٦؛ «الأزمنة» للمرزوقى ١/ ١٨٨، ٣١٥؛ «تاج العروس» ١/ ٧٨، ١٤٠؛ ويحتمل انه نقل عنه أيضا فى: «النبات» لأبى حنيفة ٣/ ١٥٦، ١٩٨؛ «الأفعال» لابن الحداد ١/ ٥١٣، ٢/ ١٣٩، ١٨٠، ١٩١، ٢٤٣، ٣٠٦، ٣٠٨؛ «العباب» للصغانى ١/ ٢٤، ٤٩، ٦١، ٧١، ٧٩، ٨٥، ١٢٥، ١٢٩، ١٤٨، ١٥٣، ٢٠٢؛ «تاج العروس» ٢ ١/ ٣٠٥ (صأصأ)، ٤/ ٧٣ (قلب)، ٦/ ١٩٤ (لهج)، ١١/ ٢٨٧ (درر)، ١٧/ ١١٦ (جهش).
٢ - «كتاب رحل البيت»، ذكره ابن النديم وآخرون.','1','208',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (205,'<span data-type="title" id=toc-221>هشام بن الكلبى</span>
أبو المنذر هشام بن محمد بن السائب، المتوفى سنة ٢٠٤/ ٨١٩ أو ٢٠٦/ ٨٢١، كان مؤرخا، عارفا بالشعر (انظر فصل الأدب). والى جانب ذلك أشتغل أيضا باللغة.

<span data-type="title" id=toc-222>له من الكتب:</span>
١ - «كتاب الأمثال»، ولم يصل إلينا، انظر تاريخ التراث العربى ١/ ٢٧٠.
انظر ما نقل عنه فى «مجمع الأمثال» للميدانى، ع. التكريتى فى: المورد ٣، ٣/ ١٩٧٤/ ١٠٥، ١١٥ - ١١٦.
٢ - «كتاب السيوف»، ذكره ابن النديم ص ٩٦، ونقل عنه الصغانى فى «التكملة» ٤/ ٤٨٥، وهو من مصادره فى «العباب» وأسماه «كتاب أسماء سيوف العرب المشهورة» (انظر ١/ ٢٧).
وذكر ابن النديم أسماء الكتب التالية أيضا:
٣ - «كتاب لغات القرآن»، ٤ - «كتاب الخيل»، ٥ - «كتاب القداح»، ٦ - «كتاب أسنان الجزور».

<span data-type="title" id=toc-223>الهيثم بن عدى</span>
هو أبو عبد الرحمن الهيثم بن عدى (المولود قبل سنة ١٣٠/ ٧٤٧، والمتوفى سنة ٢٠٦ هـ أو سنة ٢٠٧/ ٨٢٢) وكان يغلب عليه التاريخ (انظر تاريخ التراث العربى ١/ ٢٧٢). وأولع برواية الشعر وصنف مصنفات كثيرة فى الأدب (انظر فصل الأدب)؛ وقد اشتغل قليلا باللغة.','1','209',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (206,'<span data-type="title" id=toc-224>وله من الكتب:</span>
١ - «كتاب اللغات»، أدخله الخطيب البغدادى فى القرن الخامس/ الحادى عشر إلى دمشق (انظر التسمية للمالكى فى «الخطيب البغدادى» ليوسف العش، ص ١٠٣، رقم ٢٥٢).
٢ - ولعل له «كتاب الأمثال» وكان من مصادر «مجمع الأمثال» للميدانى، انظر ع. التكريتى فى: المورد ٣، ٣/ ١٩٧٤/ ١١٨.
سيأتى ذكر معاصره أبى يحيى محمد بن كناسة (المتوفى سنة ٢٠٧/ ٨٢٢ أو ٢٠٩/ ٨٢٤، انظر تاريخ التراث العربى ٢/ ٥٣٣، ٧/ ٣٤٢ وفصل الأدب) بين اللغويين الكوفيين.
وإلى مصنّفه «كتاب الأنواء» ترجع، على ما يظهر، النقول الواردة فى: «النبات» لأبى حنيفة ٣/ ١١؛ «تاج العروس» ٢ ٦/ ٣٠٦ (برح)، ٨/ ١٩٤، ١٩٥ (سعد ١٥/ ٥٢٩ (حدس)./

<span data-type="title" id=toc-225>أبو عمرو الشّيبانى</span>
هو إسحاق بن مرار، ولد فى رمادة بالقرب من الكوفة سنة ١١٠/ ٧٢٨. وكانت أمه نبطية. نسب إلى بنى شيبان لأنه كان معلّما لأولاد أسر منهم. ولا نعرف عن حياته إلا شيئا قليلا جدا.
وكان من أساتذته المفضل الضبىّ، والمحدّث ركن الشامى. وغلب عليه الاشتغال برواية الشعر القديم (انظر تاريخ التراث العربى ٢/ ٧٤٩، الفهرس) واللغة.
يعدّ من أقدم أصحاب المعاجم المرتبة على الموضوعات. وبالغ ثعلب فزعم أنه كان مع أبى عمرو من العلم والسماع عشرة أضعاف ما كان معأبى عبيدة من البصريين (إرشاد الأريب لياقوت ١/ ٢/ ٢٣٥). وكان طوال حياته يصنف فى لغة العرب ويجمع شعرها. وكثير من الدواوين التى وصلت إلينا ترجع أصلا إلى جمعه وتدوينه. ولم يبلغنا ديوان واحد من دواوين نيف وثمانين قبيلة (انظر ابن النديم ص ٦٨) جمعها. وتتفاوت الأخبار فى تاريخ وفاته بين سنة ٢٠٥/ ٨٢٠، وسنة ٢٠٦ هـ، وسنة ٢١٣ هـ.','1','210',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (207,'«مراتب النحويين» لأبى الطيب ٩١ - ٩٢؛ «طبقات النحويين» للزبيدى ٢١١ - ٢١٢؛ «المقتبس» للمرزبانى ٢٧٧ - ٢٧٨؛ ابن النديم ٦٨؛ «تاريخ العلماء» للتنوخى ٨٧ ب؛ «تاريخ بغداد» ٦/ ٣٢٩ - ٣٣٢؛ «نزهة الألباء» للأنبارى ٢ ٦١ - ٦٤؛ «إرشاد الأريب» لياقوت ٢ ٦/ ٧٧ - ٨٤؛ «إنباه الرواة» للقفطى ١/ ٢٢١ - ٢٢٩، مخطوط فى طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٣٠ ب- ٢٣١ أ؛ شذرات ابن العماد ٢/ ٢٣ - ٢٤؛ «مسالك الأبصار» لابن فضل الله ٤/ ٢٢٣ - ٢٢٤؛ ابن قاضى شهبة، المخطوط ص ٢١٣ - ٢١٤.
Flugel, Gramm. Schulen ١٣٩ - ١٤٢; Rescher, Abri BII, ١٧٦ - ١٧٧;
«الأعلام» للزركلى ١/ ٢٨٩؛ «معجم المؤلفين» لكحالة ١/ ٢٣٨ - ٢٣٩؛
Br. GI, ١١٦, SI, ١٧٩; F. Krenkowin: EI, IV, ٢٩١ - ٢٩٢; G. Troupeauin: EI, Supple- ment ١٥ - ١٦.
وسنذكر أسماء مصنفاته التى لم تصل إلينا تبعا لقوائم كتبه الواردة فى الفهرست لابن النديم ٦٨ (طهران، ص ٧٥؛ الترجمة الإنجليزية ١٥١)؛ و «إرشاد الأريب» لياقوت ٢/ ٢٣٥؛ و «إنباه الرواة» للقفطى ١/ ٢٢٧؛ وابن خلكان ١/ ٨١؛ «الوافى بالوفيات» للصفدى ٨/ ٤٢٥، «بغية الوعاة» للسيوطى ١٩٢.

كتب فى اللغة (١٠٩)، مجموعة من الأمثال، بعض مسائل فى علوم اللغة:
١ - «كتاب الجيم» ذكرته خمس قوائم، وهو معجم، إلا أنه ليس بمعجم لألفاظ اللهجات، كما يظن كثيرا. وقلما تنقل عنه كتب اللغة المتأخرة (انظر، W.Diem ,Das (Kitabal -Gim ... ,s.١١٩ فمن ذلك أيضا أن أبا عبيد لم ينتفع به فى «الغريب المصنف» (انظر-
R. Abdel- Tawab, Das Kitabal- Garlbal- Musannaf ... , Heppenheim. (١٩٦٢, s. ١١٤
وزعموا أنّ أبا عمرو «بخل» به، فلم يقرأه عليه/ أحد من مشاهير اللغويين (انظر مراتب النحويين لأبى الطيب ٩١ - ٩٢).
ويوجد مخطوطا فى الاسكوريال ٥٧٢ (٢٨٦ ورقة، من القرن الرابع أو الخامس
_________
(١٠٩) ونسب إليه خطأ عند ابن النديم كتاب «شرح كتاب الفصيح» أى شرح كتاب ثعلب المتأخر عنه فى الزمان.','1','211',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (208,'للهجرة، راجع فهرس المخطوطات ١/ ٣٦٥)، ونشر ديم جزءا منه: «باب الكاف من كتاب الجيم ... » في: الأبحاث ٢٢/ ١٩٧١/ ٣ - ٥٠، وحققه إبراهيم الأبيارى، ومحمد خلف الله أحمد وآخرون، فى ثلاث مجلدات بالقاهرة ١٩٧٤ - ١٩٧٥.

<span data-type="title" id=toc-227>الدراسات:</span>
F. Krenkow, The Kitabal- Jim ... in: JRAS ١٩٢٥, S. ٧٠١ - ٧٠٦; W. Diem, Das Kitabal- Gimdes Abu Amras- Saibanl, Diss. Munchen ١٩٦٨;
ف. م. مفيد آل ياسين، فهرس أعلام معجم «الجيم»، فى المورد ١٠، ٢/ ١٩٨١/ ٢٧٧ - ٢٩٤.
٢ - «كتاب الحروف»، ذكره ابن النديم، ونقل عنه الآمدى فى «المؤتلف والمختلف» ١١٨ - ١١٩؛ وابن السيد فى «الاقتضاب» ١١٢ وفى مواضع أخرى كثيرة، وصاحب «تاج العروس» ١/ ٥٤. وذهب الميمنى فى مصنفه «كتاب فى طبقات النحاة واللغويين» (انظر فصل النحو، ونقله القفطى فى إنباه الرواة ١/ ٢٢٦، وانظر ص ٢٢٤ منه) إلى أن «كتاب الحروف» هو «كتاب الجيم».
وعلى خلاف هذا، يذكره الصغانى («التكملة» ١/ ٨ و «العباب» ١/ ٣٠) إلى جانب كتاب الجيم مصدرا آخر من مصادره.
٣ - «كتاب اللغات» ذكرته خمس قوائم، وذهب القفطى وابن خلكان إلى أنه هو «كتاب الجيم» و «كتاب الحروف». وهو عند ابن النديم من تصنيفابنه عمرو بن أبى عمرو الشيبانى، إلّا أنّ جواز عدّه راويا للكتاب أولى.
٤ - «كتاب الغريب المصنف»، ورد ذكره فى خمس قوائم، وكذلك ذكره السيوطى فى «المزهر» ١/ ٩٦ (كتاب الغريب)، رواه ابنه عمرو. ونقل عنه تلميذه أبو عبيد فى كتابه الغريب المصنف ٥٥٠ مرة (انظر:
R. Abdel- Tawab, Das Kitabal- Garib (al- Musannaf ... S. ١١١
، ويحتمل أنه كان يقصد هذا الكتاب.
وكان الكتاب أيضا مصدرا لحبيش بن إبراهيم التفليسى (المتوفى سنة','1','212',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (209,'٦٢٩/ ١٢٣٢) فى مصنفه «قانون الأدب» (انظر مجلة المجمع العربى بدمشق ٥٠/ ١٩٧٥/ ٤٠١).
٥ - «كتاب الأمثال»، لم تذكره القوائم، وكان مصدرا من مصادر الميدانى، انظر؛ Sellheim ,Sprichworter ١٤٨ ع. التكريتى فى: المورد ٣، ٣/ ١٩٧٤/ ١١٧.
٦ - «كتاب النوادر (الكبير)»، انظر تاريخ التراث العربى ٢/ ٨٧، رواه ابنه عمرو، انظر «مراتب النحويين» لأبى الطيب ٩١. وانظر فى نقول عنه: المرجع الأخير ص ١٩ - ٢٠؛ «التهذيب» للأزهرى (١/ ١٣، وراجع ص ٢٢) ٤/ ٣٦٠، ٣٧٢، ٥/ ١٤٣، ٣٤١، ٩/ ٢٣٩ وربما فى مواضع أخرى كثيرة؛ وفى «لغات متفرقة» لجمهول، كوبريلى ٣٩٣/ ١٨ (٩١ ب- ٩٢ أ، ٦٣٥ هـ)؛ «المزهر» للسيوطى (انظر الفهرس)، تحفة المجد الصريح للفهرى، مخطوطة القاهرة (انظر ص ١٤٤ بعد)، ص ٨٣، ١١٩، وربما ص ١٢٢ أيضا؛ وقد انتقده على بن حمزة البصرى (المتوفى سنة ٣٧٥/ ٩٨٥) موردا نقولا عنه فى الباب الثانى من كتاب «التنبيهات على أغاليط الرواة» انظر ص ١١٠ آنفا.
٧ - «(كتاب ال) أمالى»، كتبه إملاء أحمد بن حنبل، انظر تاريخ بغداد ٦/ ٣٣٠؛ «تهذيب التهذيب» لابن حجر ١٢/ ١٨٣.
٨ - «كتاب الفرح»، وورد عنه نقل فى الصرف فى «تذكرة النحاة» لأبى حيان الأندلسى، الجزء الثانى، من مخطوطة الأوقاف بالرباط ٢١٤، ص ٢٣٧ - ٢٣٨.

<span data-type="title" id=toc-228>ومن تآليفه المفردة فى الإنسان والحيوان والنبات </span>(١١٠):
١ - «كتاب خلق الإنسان»، ذكره ابن النديم، والقفطى، وابن خلكان، والسيوطى، راجع «كشف الظنون» ٧٢٢ - ٧٢٣. ويستفيض ورود الألفاظ المتعلقة بخلق الإنسان فى كتاب «الجيم» أيضا (انظر ف. ديم، الموضع المذكور ص ٨٦).
٢ - «كتاب الخيل»، جاء ذكره فى كل القوائم، وكان موجودا فى القرن ٧/ ١٣ فى إحدى/ مكتبات حلب (انظر پولس سباط فى. (MIE ٤٩ /١٩٤٦ /١٩ ,No.٣٤٦: وفى
_________
(١١٠) انظر فى موضوع «الأنواء» بكتابه الغريب المصنف: تاريخ التراث العربى ٧/ ٣٤١.','1','213',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (210,'كتاب «الجيم» كثير من ألفاظ الخيل (انظر ف. ديم، الموضع المذكور، ص ٨٧).
وجعل ابن النديم الكتاب من تصنيف ابنه عمرو بن أبى عمرو الشيبانى. ويحتمل أنه لم يكن إلا راويا للكتاب، انظر أيضا تاريخ التراث العربى ٣/ ٣٦٥.
٣ - «كتاب الإبل»، ذكره ابن النديم، والقفطى، وابن خلكان. وألفاظ هذا الباب مستفيضة أيضا فى كتاب «الجيم» (انظر ف. ديم، الموضع المذكور، ص ٨٧).
٤ - «كتاب النحلة»، هكذا جاء عند ابن النديم، والقفطى، وابن خلكان.
ويحتمل أن يكون «كتاب النخل (أو النخلة)»، انظر ما نقل عنه فى «كتاب النبات» لأبى حنيفة الدينورى (راجع تاريخ التراث العربى ٤/ ٣٣١). وقدأورد كثيرا من ألفاظ النبات فى «كتاب الجيم» أيضا (انظر ف. ديم، الموضع المذكور، ص ٨٧).

<span data-type="title" id=toc-229>وله فى لغة الحديث:</span>
«غريب الحديث»، ذكرته كل القوائم، وكان موجودا فى القرن ٧/ ١٣ فى إحدى مكتبات حلب (انظر پولس سباط فى. (MIE ٤٩ /١٩٤٦ /٣٧ ,No.٦٦٥: وذكر ابن النديم أن أبا عمرو الشيبانى كان أيضا ثقة فى الحديث. وذكر أيضا أن ابنه عمرو صنف كذلك «كتاب غريب الحديث». والأكثر احتمالا أنه روى كتاب أبيه. وكان الكتاب من مصادر حبيش بن إبراهيم التفليسى (المتوفى سنة ٦٢٩/ ١٢٣٢) فى مصنفه «قانون الأدب» (انظر مجلة المجمع العلمى العربى بدمشق ٥٠/ ١٩٧٥/ ٤٠١).

<span data-type="title" id=toc-230>الفرّاء</span>
هو أبو زكرياء يحيى بن زياد بن عبد الله، المولود سنة ١٤٤/ ٧٦١، والمتوفى سنة ٢٠٧/ ٨٢٢، نحوى جليل (انظر فصل النحو)، ألف أيضا طائفة من المؤلفات فى اللغة، وفى علوم لغة القرآن.
انظر فى قوائم كتبه: ابن النديم ٦٧ (طهران ص ٧٣ - ٧٤)؛ الترجمة الإنجليزية','1','214',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (211,'١٤٧؛ «إرشاد الأريب» لياقوت ٧/ ٢٧٨؛ «إنباه الرواة» للقفطى ٤/ ١٦ - ١٧؛ ابن خلكان ٢/ ٣٠٣؛ «بغية الوعاة» للسيوطى ٤١١.
وانظر أيضا مناظرات، تتناول أيضا مسائل فى اللغة، مع خصوم وأصحاب له فى:
«مجالس العلماء» للزجاجى ١٧٨، ١٨٤ - ١٨٥، ٢٦٩ - ٢٧٠، وراجع: محمد خير الحلوانى، «كتاب الإنصاف فى مسائل الخلاف» فى: مجلة المجمع العلمى العربى بدمشق ٤٨/ ١٩٧٣/ ١٣٠ - ١٥١، ٦٢٢ - ٦٤٤. وثمة نقل طويل عن تأليف له فى اللغة لم يذكر اسمه، يعرض لمسائل فى تاريخ العربية ومعانى بعض الألفاظ، ورد عند الصفدى فى «التذكرة الصلاحية»، الجزء الأخير من مخطوط حكيم أوغلى ٦٧١ (٦٤ ب- ٦٦ أ، من القرن ٩ للهجرة). وهناك نقول فى اللغة عن كتب لم تذكر أسماؤها، برواية سلمة بن عاصم (انظر فصل النحو)، أوردها الأزهرى فى التهذيب ١٢/ ٤٥، ٥٣، ٥٧، ٨٤، ١١١.
وانظر فى انتقاد تصحيف بعض الألفاظ أو الغلط فى تفسيرها: «التصحيف» لأبى أحمد العسكرى ١٣١ - ١٣٣.
١ - «كتاب البهى» (وهو ما تلحن فيه العامة)، أو «كتاب البهاء)، وهو كتاب فى اللحن ألفه لعبد الله بن طاهر (ابن النديم)، جاء ذكره فى كل قوائم كتبه، وعرف أيضا فى الأندلس/ (انظر فهرست ابن خير ٣١١ - ٣١٢).
وذهب ابن خلكان إلى أن ثعلب غيّره ورتّبه على صورة أخرى فى كتابه «الفصيح». وليس فى الكتابين اختلاف إلا فى شئ قليل، وليس لثعلب سوى تغيير الترتيب. وكان الكتاب من مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) فى «تحفة المجد الصريح فى شرح كتاب الفصيح (انظر مجلة مجمع اللغة العربية بدمشق ٣٥/ ١٩٦٠/ ٥٤٣)، وورد نقل عنه أيضا فى «تذكرة النحاة» لأبى حيان الأندلسى، الجزء الثانى، مخطوط الأوقاف بالرباط ٢١٤، ص ٣٥٢.
٢ - «كتاب المشكل» أو «كتاب مشكل اللغة»، وقيل إنه كان يوجد منه نسخة صغيرة وأخرى كبيرة، انظر «تاريخ بغداد» ١٤/ ١٥٠؛ «إنباه الرواة» للقفطى ٤/ ١١؛ ياقوت، الموضع المذكور.','1','215',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (212,'٣ - «كتاب النوادر»، مذكور فى كل قوائم كتبه (راجع «المزهر» للسيوطى ١/ ٩٦)، انظر فى نقول عنه تاريخ التراث العربى ٢/ ٨٧، وكذلك الصغانى فى «ما بنته العرب على فعال» ص ٣٢؛ وفى «التكملة» ٤/ ١٠٧ (راجع المقدمة ١/ ٨)؛ وفى «العباب» ١/ ٢٩، (طاء) ٤٣، ١٩٢، ٢١٥، ومن المحتمل ٥٤، ٧٨، ٨٠، ٨٢، ٨٥؛ و «البصائر» لأبى حيان ٢/ ٣٧٣ - ٣٧٤؛ والأنبارى فى «الأضداد» (انظر الفهرس ص ٥٠١)؛ وفى «الزاهر» (انظر الفهرس ٢/ ٦١٨ - ٦٢٠، مع طرح المواضع العديدة المأخوذة من «معانى القرآن»)؛ و «معجم البلدان» لياقوت ١/ ٣٥٨، ٣٥٩، ٢/ ١٣٢، ٤٧٩، ٣/ ٨٩٦، ٤/ ٢٣٢ (واستعملياقوت نسخة أملاها ثعلب سنة ٢٨٣ هـ)، وربما أيضا ١/ ٣٦١، ٨٣٣، ٩٣٥، ٢/ ١٤٣، ٤٤٠، ٥٢٨، ٣/ ٢٦٣، ٥١٨، ٥٩٢، ٦٠٧، ٦٠٩، ٦٢٠، ٦٤٥، ٧٠٦، ٧٠٨، ٧٢٨، ٧٣٧، ٨٨١، ٤/ ٣٢، ٦٧، ١٦٦؛ «تاج العروس» ٢ ٨/ ٣١ (حضد)، ١٩/ ١٤٨ (بسط).
٤ - «كتاب الأيام والليالى والشهور»، وصل إلينا وطبع، انظر تاريخ التراث العربى ٧/ ٣٤٣.
٥ - «كتاب يافع ويفعة»، من الجائز أنه مصنّف فى اللغة، وقيل انه أحد الكتب القليلة التى كان الفرّاء يمليها من نسخة، انظر «تاريخ بغداد» ١٤/ ١٥٣؛ و «إنباه الرواة» للقفطى ٤/ ١٤.
٦ - «كتاب الفاخر» (١١١)، ذكره ابن النديم وغيره، مصنّف فى اللغة؟
٧ - «كتاب الأمالى»، ونقل عنه ياقوت فى «معجم البلدان» ٢/ ٤٤٠، ٣/ ٣٨٩ معلومات جغرافية.
٨ - ومن الراجح جدا أن له «كتاب الأضداد»؛ إذ يذكر اسمه مع أسماء مؤلفى كتب «الأضداد» (الأصمعى، قطرب، ابن السكيت، ثعلب، أبو حاتم، ابن
_________
(١١١) وليس «الفاخر فى الأمثال، (Br.SI.١٧٩) «راجع. Sellheim.Sprichworter ٧٣ ويحتمل مع ذلك أن الفراء ألّف كتابا فى الأمثال، على ما يمكن تقديره من النقول العديدة الواردة عند الميدانى (انظر ع. التكريتى فى: المورد ٣، ٢/ ١٩٧٤/ ١٠٤ - ١٠٥).','1','216',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (213,'الأنبارى) باعتباره مصدرا من مصادر أبى محمد سعيد بن المبارك ابن الدهان النحوى (المتوفى سنة ٥٦٩/ ١١٧٤) فى مصنفه «تهذيب كتاب الأضداد»، ويوجد مخطوطا فى نور عثمانية ٤٨٨٤، الورقة ١ ب.

<span data-type="title" id=toc-231>وله فى لغة القرآن والحديث:</span>
١ - «معانى القرآن»، وهو تفسير للقرآن، وقيل إنه كان نحو ألف ورقة، أى ثلث مقدار كتب الفرّاء (انظر إنباه الرواة للقفطى ٤/ ٤، ١٤).
وبادخال زيادات وتعديلات فى النص الأصلى نشأت عدة روايات (انظر «تاريخ بغداد» ١٤/ ١٥٢ - ١٥٣).
وكان الكتاب، على ما ذكر ابن النديم، فى أربعة أجزاء، وذكرته قوائم كتبه كلها، وأثنى عليه القفطى كثيرا، وكان من مصادر الأزهرى؛ والثعلبى فى الكشف؛ وابن جنى فى «المحتسب» (انظر المقدمة ١/ ١٥)؛ و «خزانة الأدب» (انظر إقليد الخزانة للميمنى ١٠٥)، وغيرهم.

<span data-type="title" id=toc-232>المخطوطات:</span>
وهبى ٦٦ (الجزء الأول، ٢١٠ ورقة، من القرن الرابع للهجرة، انظر فهرس المخطوطات ١/ ٤٦، وراجع:
(H. Ritterin: Islam ١٧/ ١٩٢٨/ ٢٤٩; O. Pretzlin: Islamica ٦/ ١٩٣٤/ ١٦ - ١٧
، نور عثمانية ٣٢٠ (الأخير، لعله الجزء الرابع، ١٥١ ورقة، من القرن الرابع للهجرة، انظر فهرس المخطوطات ١/ ٤٦)، و ٤٥٩ (١٨٧ ورقة، من القرن ١٢ هـ)، دار الكتب بالقاهرة، تفسير ١٠ ش (٢٢٥ ورقة، نسخة حديثة، انظر فهرس المخطوطات ١/ ٤٦)، نسخ بالقاهرة مصورة عن مخطوطات استنبول (انظر فهرس دار الكتب، الملحق ٣/ ٧٣ - ٧٤)، عارف حكمت بالمدينة، تفسير ١٥٨ (١٩٠ ورقة، من القرن العاشر للهجرة)، ومنها مصورة بمعهد المخطوطات بالقاهرة، المكتبة العامة بالرباط، الأوقاف ١٨٨ (الجزء الثانى، ١٢٥ ورقة، نسخت سنة ٥٧٨ هـ، راجع، (L istedemanuscrits ٩ ,No.١٧ ومنها مصورة بمعهد المخطوطات بالقاهرة؛ نشر فى','1','217',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (214,'ثلاثة مجلدات، أولهما بتحقيق أحمد يوسف نجاتى ومحمد على النجار، القاهرة ١٩٥٥ (انظر فيه جورج شحاته قنواتى فى، (MIDEO ٣ /١٩٥٦ /٢٨٣.: والثانى بتحقيق محمد على النجار، القاهرة ١٩٦٦، والثالث بتحقيق عبد الفتاح/ شلبى وعلى النجدى، القاهرة ١٩٧٢ - الدراسات:
E. Beck. Die Partikel''Idanbei al- Farra''und Sibawaihin: Orientalia ١٥/ ١٩٤٦/ ٤٣٢ - ٤٣٨: ders., Studienzur Geschichtederkuf ischen Koranlesunginden beiden ersten Jahrhunderten. Iin: Orientalia ١٧/ ١٩٤٨/ ٣٢٦ ff.
(وبه تتعلق ص ٣٢٦ - ٣٣٨)
ders .. Diedogmatischrel igiose Einstellungdes Grammatikers Yahyab. Ziyadal- Farra, in: Museon ٦٤/ ١٩٥١/ ١٨٧ - ٢٠٢; ders., Die Zuverlassigkeitd er Uberlieferungvon au Ber''utmanischen Variantenbeial- Farra, in: Orientalia ٢٣/ ١٩٥٤/ ٤١٢ - ٤٣٥; ders., Die Ausnahmepartikel '' illabeial- Farra''und Sibawaihin: Orientalia ٢٥/ ١٩٥٦/ ٤٢ - ٧٣; ders., Dieb. Mas''udvariantenb eial- Farra. Iin: Orientalia ٢٥/ ١٩٥٦/ ٣٥٦ - ٣٨٣, IIebd. ٢٨/ ١٩٥٩/ ١٨٦ - ٢٠٥, IIIebd. S. ٢٣٠ - ٥٦.
وانظر فى شواهده الشعرية أ. م. أمين الورد، «الظئريات. أدلة كتاب معانى القرآن لأبى زكرياء، يحيى بن زياد الفراء» فى: المورد ١٠، ١/ ١٩٨١/ ٤٠١ - ٤٧٦.

<span data-type="title" id=toc-233>ومما علق به عليه:</span>
أ) «زيادات» لأبى الفضل المنذرى (المتوفى سنة ٣٢٩/ ٩٤١)، وصلت إلينا نقول عنها.
ب) «الرد على الفراء فى المعانى» لابن درستويه (المتوفى سنة ٣٤٧/ ٩٥٨)، لم يصل إلينا، انظر ص ١٠٨ آنفا.
٢ - «كتاب اللغات (فى القرآن)»، ذكرته كل قوائم كتبه (انظر أيضا «المزهر» للسيوطى، وكان سابقا ضمن مجموع فى أولو جامع ببورسة، ولم يعد موجودا هنا لك.
٣ - «غريب الحديث»، ذكره الدّاودى فى طبقات المفسرين ٢/ ٣٦٧.

<span data-type="title" id=toc-234>أبو توبة النحوى</span>
هو ميمون بن حفص، أحد تلامذة الكسائى، وكان نحويا ولغويا وأديبا. انتقل','1','218',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (215,'إلى بغداد، حيث اشتغل بها مؤدّبا. والظاهر انه توفى حوالى سنة ٢١٥/ ٨٣٠ (فى نحو سن الأصمعى).
ابن النديم ٣٠؛ «تاريخ بغداد» ١٣/ ٢١٠؛ «إرشاد الأريب» لياقوت ٧/ ٢٠١؛ «إنباه الرواة» للقفطى ٣/ ٣٣٨؛ «بغية الوعاة» للقفطى ٤٠١.
وعلى ما يبدو وردت نقول عن كتاب له فى اللغة فى أمالى اليزيدى ٥٧ - ٥٨، ٦٠، ٦٥، ٦٩، ٧١، ٧٣، ٧٤، ٧٥.

<span data-type="title" id=toc-235>سعدان بن المبارك</span>
هو أبو عثمان سعدان بن المبارك من أهل بغداد، وكان ينتمى إلى مدرسة الكوفة، إلا أنه روى أيضا عن أبى عبيدة من البصريين. كان ضريرا وتوفى سنة ٢٢٠/ ٨٣٥.
ابن النديم ٧١؛ «تاريخ بغداد» ٩/ ٢٠٣؛ «نزهة الألباء» للأنبارى ٢٠٦ - ٢٠٧، ٢ ١٠٣؛ «إنباه الرواة» للقفطى ٢/ ٥٥؛ «إرشاد الأريب» لياقوت ١ ٤/ ٢٢٩، ٢ ١١/ ١٨٩ - ١٩٠؛ «نكت الهميان» للصفدى ١٥٧؛ «بغية الوعاة» للسيوطى ٢٥٤.- «الأعلام» للزركلى ٣/ ١٤٠؛ «معجم المؤلفين» لكحالة ٤/ ٢١٤./

<span data-type="title" id=toc-236>ومن كتبه:</span>
١ - «كتاب الأمثال»، ورد ذكره عند ابن النديم وفى خمس أخرى من قوائم كتبه (راجع، (Sellheim ,Sprichworter ٥٥ ويحتمل أنه قائم على كتاب أستاذه أبي عبيدة، وربما لم يكن المقصود إلا كتاب أبى عبيدة برواية سعدان (١١٢)
_________
(١١٢) وروى أيضا، على سبيل المثال، «نقائض جرير والفرزدق» لأبى عبيدة (ابن النديم وثلاث قوائم أخرى لكتبه؛ تاريخ التراث العربى ٢/ ٣٦٢، ٣٦٣)، وكان نصيبه الخاص فيه هو بعض تعليقات على النص (انظر أ. أ. بيفان فى مقدمة النقائض، ص ١١)، وروى ديوان الفرزدق عن أبى عبيدة (انظر تاريخ التراث العربى ٢/ ٣٦١).','1','219',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (216,'٢ - «كتاب خلق الإنسان»، مذكور فى كل القوائم.
٣ - «كتاب الوحوش»، ذكرته كل القوائم، انظر فى نقول عنه تاريخ التراث العربى ٣/ ٣٦٦.
٤ - «كتاب الأرضين والمياه والجبال والبحار»، مذكور فى كل القوائم، انظر فصل الجغرافية. ولعل منه نقلا عند الصغانى فى «العباب» (طاء) ٢١٦.
٥ - «كتاب المناهل»، لم يذكره سوى السيوطى، انظر فصل الجغرافية.

<span data-type="title" id=toc-237>اللحيانى</span>
هو أبو الحسن على بن المبارك (أو حازم) اللحيانى، من تلامذة الكسائى، وسمع أيضا من أبى عبيدة، والأصمعى، وأبى زيد، وأبى عمرو الشيبانى. وكان من بعد أستاذا لأبى عبيد القاسم بن سلام.
«مراتب النحويين» لأبى الطيب ٨٩ - ٩٠؛ «التهذيب» للأزهرى ١/ ٢١ - ٢٢؛ «طبقات النحويين» للزبيدى ٢١٣؛ ابن النديم ٤٨؛ «تاريخ العلماء» للتنوخى ٨٧ أ- ٨٧ ب؛ «نزهة الألباء» للأنبارى ١ ٢٣٥ - ٢٣٦، ٢ ١٢١ - ١٢٢؛ «إنباه الرواة» للقفطى ٢/ ٢٥٥؛ «إرشاد الأريب» لياقوت ١ ٥/ ٢٩٩ - ٣٠٠، ٢ ١٤/ ١٠٦ - ١٠٨؛ مخطوط فى طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٢٤ ب- ٢٢٥ أ، ٢٣١ أ؛ ابن قاضى شهبة، المخطوط ص ٤١٤، ٤٢٧؛ «بغية الوعاة» للسيوطى ٣٤٦؛ «المزهر» للسيوطى ٢ ٢/ ٤١٠.-
Flugel, Gramm. Schulen ٥٣; Rescher, Abri BII, ١٧٤ - ١٧٥,
«معجم المؤلفين» لكحالة ٧/ ٥٦، ١٧٤.

<span data-type="title" id=toc-238>ومن كتبه:</span>
١ - «كتاب النوادر»، وهو أشهر كتبه، ولم يذكر له كل من ترجموا له كتابا غيره، وكان ذائعا مستحسنا، وعرف أيضا في الأندلس (انظر فهرست ابن خير ٣٧٩). انظر فى نقول عنه: تاريخ التراث العربى ٢/ ٨٦، وكذلك «مراتب النحويين» لأبى الطيب','1','220',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (217,'٩؛ و «العباب» للصغانى ١/ ٢٩، ١٩٧ وفى مواضع أخرى كثيرة؛ و «الصحاح» للجوهرى (١) ٢٤٩، ٢٥٢، ٢٨٢، ٣٢٨، ٣٤٠ وفى مواضع كثيرة غيرها؛ و «الممتع» لابن عصفور ١٠٧؛ و «نزهة الألباء» للأنبارى ١٢١؛ و «تحفة المجد الصريح» للفهرى (انظر مجلة المجمع العلمى العربى بدمشق ٣٥/ ١٩٦٠/ ٥٤٤)؛ و «تاج العروس» ١/ ٤٤، ٤٢٢ وفى مواضع أخرى كثيرة؛ ومن المحتمل جدا أن النقول العديدة فى كتاب «النبات» لأبى حنيفة (انظر الفهرس، مجلد ٣ و ٥) مأخوذة عنه. ويبدو فضلا عن ذلك أنه يعد من المصادر الرئيسية لابن سيده فى «المحكم»، وكذلك يبدو أن كثيرا من النقول فى «التهذيب» للأزهرى، و «لسان العرب»، ومعجمى الصغانى «التكملة»، و «العباب» (انظر أيضا على سبيل المثال ١/ ٧٣، ١٣٥، ١٤٠، ١٤٤، ١٧٢، ١٩٨) قد أخذت منه. ونقل عنه الزبيدى فى «تاج العروس» ٢ مسمّيا إياه فى ٢/ ٦٢ (ترب)، ٣/ ١٥٤ (شكب)، ٤/ ٣٢٠ (نهب)، ٥/ ٢٠٨ (حدث)، ٧/ ٢٨٦ (شيخ)، ٩/ ٢٥٨ (وجد). وانظر فى أغلاط الكتاب:
«التنبيه» لحمزة الأصفهانى ٨٨؛ و «التصحيف» لأبى أحمد العسكرى ٣١، ١٨٥ - ١٨٦.
٢ - «كتاب الأمثال»، ويحوى أمثالا على صيغة «أفعل من»، وقيل إنه كان يشبه كتاب أستاذه الأصمعى فى الموضوع نفسه، وكان بالتأكيد معتمدا عليه. وقد عرف الكتاب أبو عبيد ثم حمزة الأصفهانى وأفادا منه فى مؤلفيهما فى الأمثال (انظر. Sellheim , (Sprichworter ١٣١ - ١٣٢ ووردت نقول عنه فى «الدرة الفاخرة» لحمزة الأصفهاني ٤٩٥، راجع الكتاب نفسه ص ٥٥؛ و «مجمع الأمثال» للميدانى (انظر ع. التكريتى فى: المورد ٣، ٢/ ١٩٧٤/ ١٥، ٣، ٣/ ١٩٧٤/ ١١٠)؛ و «نزهة الألباء» للأنبارى ١٢٢./

<span data-type="title" id=toc-239>ابن الأعرابى</span>
هو عبد الله محمد بن زياد، وكان ابنا لعبد سندىّ، ولد سنة ١٥٠/ ٧٦٧. ولا تذكر مصادرنا سبب تسميته هو وأخيه إسحاق بن زياد بالأعرابى. سمع من زوج أمه','1','221',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (218,'المفضل الضبى، ومن الكسائى، وأبى زياد الكلابى، والقاسم بن معن الكوفى.
ومن تلامذته ابن السكيت، وأبو عبيد القاسم بن سلام، وأبو الحسن الطوسي، وأبو عكرمة الضبى، وأبو إسحاق الحربى، وثعلب. وتميز ابنالأعرابى على الخصوص فى مجال اللغة ورواية الشعر القديم، ومعرفة أنساب العرب. على انه لا ينبغى لنا أن نأخذ امتداح تلميذه ثعلب له بقوله: «انتهى علم اللغة (والشعر) والحفظ إلى ابن الأعرابى» (إرشاد الأريب لياقوت ٧/ ٥) بمعناه الحرفى. وكذلك ما أخبر به ثعلب من أن ابن الأعرابى، فى مجالسه التى كان يحضرها زهاء مائة إنسان، لم يستعمل عند السؤال والجواب كتابا قط، وأن مقدار ما كان يمليه- من غير كتاب- يحمل على أجمال (المرجع نفسه)، لا ينبغى، فى رأيي، أن نبالغ فى تقدير معناه فنفهم منه أنه كان يستغنى عن كتب خاصة به. وكان يقارب فى النحو مبادئ مدرسة البصرة المعارضة، فى حين أنه كان مبغضا لأبى عبيدة والأصمعى، شيخى هذه المدرسة.
توفى سنة ٢٣١/ ٨٤٦ وله من العمر ٨١ عاما (فى رأى).
«مجالس العلماء» للزجاجى ١٦ - ١٧، ٢٩ - ٣٢، ٤٤ - ٤٥، ١٠٠، ١٠٣، ٢٢٧ - ٢٢٨، ٢٧٤ - ٢٧٥، ٢٨٢ - ٢٨٧؛ «مراتب النحويين» لأبى الطيب ٩٢ - ٩٣؛ «طبقات النحويين» للزبيدى ٢١٣ - ٢١٥؛ ابن النديم ٦٩؛ «المقتبس» للمرزبانى ٣٠٢ - ٣٠٧؛ «تاريخ العلماء» للتنوخى ٨٧ أ؛ «تاريخ بغداد» ٥/ ٢٨٢ - ٢٨٥؛ «نزهة الألباء» للأنبارى ٢ ١٠٣ - ١٠٦؛ «إرشاد الأريب» لياقوت ٢ ١٨/ ١٨٩ - ١٩٦؛ «إنباه الرواة» للقفطى ٣/ ١٢٨ - ١٣٧؛ «الوافى بالوفيات» للصفدى ٣/ ٧٩ - ٨٠؛ «مسالك الأبصار» لابن فضل الله ٢ ٤/ ٢٣٠ - ٢٣١؛ «بغية الوعاة» للسيوطى ٤٢ - ٤٣؛ «المزهر» للسيوطى ٢ ٢/ ٤١١، ٤٦٤.-
Br. GI, ١١٦ - ١١٧, SI, ١٧٩ - ١٨٠; Rescher, Abri BII, ١٨٠ - ١٨١
؛ «الأعلام للزركلى» ٦/ ٣٦٥ - ٣٦٦؛ «معجم المؤلفين» لكحالة Ch.Pellatin: EI ,III ٢ ,٧٠٦ - ٧٠٧؛ ١٢ - ١١ /١٠

<span data-type="title" id=toc-240>ومن كتبه:</span>
١ - «تفسير الأمثال»، ذكرته كل قوائم كتبه، وكان أحد مصادر أبى عبيد فى','1','222',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (219,'مجموعة أمثاله (انظر تاريخ بغداد ١٢/ ٤٠٤ - ٤٠٥). ومن المؤكد أن المفضل الضبى كان مصدرا من مصادر ابن الأعرابى الذى روى كتابه فى الأمثال. ووردت نقول عنه فى «التهذيب للأزهرى» (٥/ ٨٦)، و «مجمع الأمثال» للميدانى، انظر ع. التكريتى فى: المورد ٣، ٢/ ١٩٧٤/ ٢٤ - ٢٦، وراجع» -٢ Sellheim ,Sprichworter ٤٩. كتاب الألفاظ»، ورد ذكره في كل قوائم كتبه، وكان مصدرا لأحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣)./ ٣ - «كتاب النوادر»، سنعرض له في فصل الأدب، وكان يشتمل على مادة لغوية جمة. ولهذا ظل طوال قرون مصدرا أساسيا للغويين، مثل ابن الحداد في كتابه «الأفعال» (انظر الفهرس ٤، ٢/ ٥١٣)؛ والصغانى في «العباب» ١/ ٢٩، ٥٨، ٦١، ٦٤، ٦٦، ٦٩، ٧٠، ٧٢، ٧٣، ٨٠، ٨٨، ٩٥، ١٠٤، ١٠٦، ١٠٧، ١١٠، ١١٥، ١١٦، ١٢٢، ١٢٨، ١٣٢، ١٣٣، ١٣٧، ١٣٨، ١٤٠، ١٤١، ١٥٩، ١٦٣، ١٦٥، ١٦٩، ١٧٧، ١٨١، ١٨٣، ١٨٧، ١٨٩، ٢٠٢. وكان من بين المصادر التى أكثر ابن سيده النقل عنها في «المحكم»، وانظر أيضا معجم البلدان لياقوت ٢/ ٧٨، ٢١١، ٤١٥، ٧٢٦، ٣/ ١٧٣، ٤٦٩، ٤/ ١٧٠؛ وتاج العروس ٢ ٣/ ٧٩ (سهب)، ٨/ ٣٧٩ (عسجد)، ١١/ ٤١٢ (زجر)، ١٤/ ٦ (كبر)، ١٦/ ١٣ (خسس)، ٣٠٧ (غدس)، ١٩/ ٥٢٩ (فرط).
٤ - «كتاب الأمالى»، انظر فصل الأدب.
وعنه نقل الأزهرى قطعة طويلة في المترادفات التى تدخل كلمة «ابن» في تركيبها (ما يعرف بالابن) في «التهذيب» ١٥/ ٥٠٤ - ٥٠٦.

<span data-type="title" id=toc-241>وله تآليف مفردة في الانسان والحيوان والنبات والزراعة وغير ذلك:</span>
١ - «خلق الإنسان» (انظر كشف الظنون ٧٢٢)، وهو من مصادر كتاب «خلق الإنسان» لثابت بن أبي ثابت (انظر ص ١٣٧ بعد)، و «غاية الإحسان في','1','223',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (220,'خلق الإنسان» للسيوطى، برلين ١، ٩٨٠ Spr) V.٧.٣٨ ب- ٣٣ أ)، ٤ أ، ٩ ب.
٢ - «كتاب أسماء خيل العرب وفرسانها»، ولعله هو «كتاب نسب الخيل» الذى تذكره قوائم كتبه، وفي صورته التى بين أيدينا تنقيح متأخر لنص تقوم نسبته إلى ابن الأعرابي على ما ذكره الجواليقي في صفحة العنوان. ولا يتبيّن من الأسانيد الشحيحة إلا أن المؤلف قد روى عن يعقوب بن محمد الزّهري (المتوفي سنة ٢١٣/ ٨٢٨، انظر «تهذيب التهذيب» لابن حجر ١١/ ٣٩٦ - ٣٩٧)، أى أنه كان معاصرا لابن الأعرابي. المخطوطات: الأسكوريال ١٧٠٥/ ١ (١ أ- ١١ ب، نسخت سنة ٤٩٩ هـ، بخط الجواليقى)، ولى الدين ٣١٧٨/ ٣ (٨ أ- ١٥ أ، من القرن العاشر للهجرة)، التيمورية بدار الكتب بالقاهرة، مجموع ٥٠/ ٣ (ص ٢٨ - ٦٥، القرن ١٣ هـ)، مكتبة الصدر الخاصة بالكاظمية (انظر مجلة معهد المخطوطات ٤/ ١٩٥٨/ ٢٤٠)، المكتبة القادرية ببغداد ١٢٣٦ (انظر نشرية ٧/ ١٩٧٤/ ٥٢٤)، المتحف العراقى ببغداد ٥٢٧/ ٤ (نسخة بخط محمد السماوى، انظر كوركيس عواد في:
سومر ١٣/ ١٩٥٧/ ٧٩)، المتحف العراقي (مجموعة الآلوسى) ٨٦٦٣/ ١ (الأوراق ١ - ٢٦، انظر أسامة ناصر النقشبندى في: المورد ٤، ١/ ١٩٧٥/ ١٩٤، رقم ٢٥٠)، كلية الآداب ببغداد، سابقا معهد الدراسات الإسلامية (انظر القائمة المطبوعة على الآلة الكاتبة ٨٣، رقم ٩٥٦)؛ نشره ديلا فيدا
G. Levi Della Vida, Les Livresdeschevaux, Leiden ١٩٢٨, ٥٠ - ١٠٠
استدراك: تبيّن باطّلاعنا على المخطوطات أنه يترجّح عدّ الجواليقى نفسه منقح الكتاب. ولعله أيضا هو الذى أدخل فيه النصوص المأخوذة من كتاب «تسمية الخيول في الجاهلية والإسلام» لابن حبيب (انظر ص ٩١ آنفا) والواردة في أول الكتاب.
وكتاب ابن حبيب نفسه. (انظر تاريخ التراث العربي ٣/ ٣٦٧)، الذى كان يبدو أن مخطوط عاطف ٢٠٠٣/ ٢ (١٣ ب- ٢٩ أ، نسخ في ١١٠٠ هـ) يشتمل عليه، قد ظهر كذلك أنه هو هذا التنقيح الذى قام به الجواليقى لكتاب ابن الأعرابى.
٣ - «كتاب الخيل» (أو «خلق الفرس»، انظر كشف الظنون ٧٢٣)، ذكرته كل قوائم مصنفاته، راجع تاريخ التراث العربي ٣/ ٣٦٥.','1','224',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (221,'٤ - «كتاب الذباب»، رآه ابن النديم بخط السكّرى، راجع تاريخ التراث العربي ٣/ ٣٦٥.
٥ - «كتاب النبات»، مذكور في كل قوائم كتبه، انظر تاريخ التراث العربي ٤/ ٣٣٤ - ٣٣٥.
٦ - «كتاب النّبت والبقل»، ذكره ابن النديم، وياقوت، والسيوطى. وكانت منه نسخة في القرن ٧/ ١٣ في مكتبة بحلب (انظر پولس سباط في، MIE ٤٩ /١٩٤٦ /٤٨ , (No.٨٧٠: راجع تاريخ التراث العربي ٤/ ٣٣٤ - ٣٣٥.
٧ - «كتاب صفة النّخل»، ذكرته كل قوائم كتبه، راجع تاريخ التراث العربي، ٤/ ٣٣٤ - ٣٣٥.
٨ - «كتاب صفة الزرع»، مذكور في خمس قوائم، انظر المرجع المذكور آنفا.
٩ - «كتاب البئر»، وكان معروفا برواية ثعلب في الأندلس (انظر فهرست ابن خير ٣٧٣). المخطوطات: المتحف العراقى ببغداد (مجموعة الآلوسى) ٨٦٥٣/ ٤ (أربع ورقات، نسخت سنة ١٣١٧ هـ، انظر أسامة ناصر النقشبندى في: المورد ٤، ١/ ١٩٧٥/ ٢٠٥، رقم ٤٠٣)، كلية الآداب ببغداد، سابقا معهد الدراسات الإسلامية (الجزء السادس ضمن مجموع، انظر القائمة المطبوعة على الآلة الكاتبة ٨٦، رقم ٩٩٢)، دار الكتب بالقاهرة، مجموع ١٦٦ م/ ٧ (٤١ أ- ٤٤ ب، نسخت سنة ١٢٠٥ هـ)، ومنه نسخة بالدار، لغة ٢٢٩/ ١٠ (ص ١٣١ - ١٤٢، راجع الفهرس الثانى ٢/ ٢٧)، وفي مجموع آخر (ص ١ - ٤٩، حديثة) والتيمورية، لغة ٣٣١/ ١٠ (ص ١٨١ - ١٩٥، نسخت سنة ١٣١٩ هـ)، ييل) ٣٣٣ - L، ست ورقات، نسخت سنة ١٣٠٩ هـ، انظر نيموى رقم ١٦٤٠)، نشره ش. الآلوسى في: المقتبس ٦/ ٩١٢/ ٣ - ٩، ونورى حمودى القيسى، بغداد ١٩٧٧، ورمضان عبد التواب، القاهرة ١٩٧٠./ ١٠ - «كتاب صفة الدّرع»، لم يذكره سوى ياقوت والسيوطى.
١١ - «كتاب الأنواء»، مذكور في كل القوائم، انظر في نقول عنه: تاريخ التراث العربي ٧/ ٣٤٥؛ ويحتمل ورود نقل عنه أيضا في «تاج العروس» ٢ ٧/ ٢٨٨ (شيخ).','1','225',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (222,'١٢ - «كتاب المعاقبات»، وهو من مصادر أحمد بن يوسف الفهرى في «تحفة المجد الصريح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣)، ونقل عنه في تاج العروس ٢ ٥/ ١٩ (غلت).

<span data-type="title" id=toc-242>الكرنبائىّ</span>
هو أبو على هشام بن إبراهيم، من أهل كرنبا (بالقرب من الأهواز)، كان لغويا، عالما بالشعر وأيام العرب، جالس الأصمعى. ويحتمل أنه توفى نحو سنة ٢٤٠/ ٨٥٤.
ابن النديم ٧٠؛ «إرشاد الأريب» لياقوت ٧/ ٢٤٩، «إنباه الرواة» للقفطى ٣/ ٣٩؛ «بغية الوعاة» للسيوطى» Flugel ,gramm.Schulen ١٥٢ - ١٥٣ - .٤٠٨ معجم المؤلفين» لكحالة ١٣/ ١٤٧.

<span data-type="title" id=toc-243>له من الكتب:</span>
١ - «كتاب الوحوش»، ذكره ابن النديم وغيره، وهو من مصادر «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر ص ١٤٤ بعد) لأحمد بن يوسف الفهرى (المتوفى ٦٩١/ ١٢٩٢)، انظر المقدمة، تحقيق عبد العزيز الميمنى في: مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣. ونقل عنه في تاج العروس ٢ ١/ ٤١٧ (لبأ، وذكر المؤلف باسم هشام).
وذكر له ابن النديم كتبا أخرى:
٢ - «كتاب الحشرات»؛ ٣ - «كتاب خلق الخيل»؛ ٤ - «كتاب النبات».

<span data-type="title" id=toc-244>ابن السكّيت</span>
هو أبو يوسف يعقوب بن إسحاق (انظر تاريخ التراث العربي ٤/ ٣٣٥؛','1','226',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (223,'٧/ ٣٤٧) لقّب بلقب أبيه «السكيت» (أى الكثير السكوت) الذى كان كذلك نحويا، لغويا وتلميذا للكسائى.
وأصل أسرته من دورق بخوزستان؛ إلا أن ابن السكيت ولد ببغداد وتلقى دروسه الأولى على أبيه، وسمع من أبي عمرو الشيبانى، وقطرب، وابن الأعرابى، والفراء، واللحيانى. ومن تلامذته أبو حنيفة الدينورى، والسّكرى، والمفضل بن سلمة.
ومع ان ابن السكيت قد اشتغل أيضا بالنحو (انظر فصل النحو)؛ إلا أن أكبر عنايته كانت باللغة ورواية الشعر. ويبدو أن إسهامه فى تشكيل صناعة المعاجم العربية كبير جدا، ويمكن استخلاصه اعتمادا على ما نقل فى المصنفات الضخمة للأجيال التالية عن كتبه التى لا حصر لها وفقد أغلبها.
وكان ينتمى أساسا إلى مدرسة الكوفة، إلا أنه كان كذلك وثيق الصلة بالبصريين.
وكان بين أساتذته من البصريين أبو عبيدة والأصمعى. أمر الخليفة المتوكل بقتله سنة ٢٤٣/ ٨٥٧، وقيل سنة ٢٤٤ هـ، أو سنة ٢٤٦ هـ./ «مراتب النحويين» لأبى الطيب ٩٥ - ٩٦؛ «طبقات النحويين» للزبيدى ٢٢١ - ٢٢٣؛ ابن النديم ٧٢ - ٧٣؛ «المقتبس» للمرزبانى ٣١٩ - ٣٢٠؛ «تاريخ العلماء» للتنوخى ٨٧ أ؛ «نزهة الألباء» للأنبارى ١ ٢٣٨ - ٢٤١، ٢ ١٢٢ - ١٢٤؛ «تاريخ بغداد» ١٤/ ٢٧٣ - ٢٧٤؛ «الرجال» للنجاشى ٣٤٩ - ٣٥٠؛ ابن خلكان ٢/ ٤٠٨ - ٤١١؛ «إرشاد الأريب» لياقوت ١ ٧/ ٣٠٠ - ٣٠٢، ٢ ٢٠/ ٥٠ - ٥٢؛ «إنباه الرواة» للقفطى ٤/ ٥٠ - ٥٧؛ مخطوط في طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٢٨ أ- ٢٣٠ ب، «مسالك الأبصار» لابن فضل الله ٤/ ٢٣١ - ٢٣٣؛ ابن قاضى شهبة، المخطوط ص ٥٤٢ - ٥٤٤؛ «بغية الوعاة» للسيوطى ٤١٨ - ٤١٩.
Flugel, Gramm. Schulen ١٥٨ - ١٦١; Br. GI, ١٧٧, SI, ١٨٠ - ١٨١; Rescher, Abri B II, ١٧٨ - ١٨٠; M. Ben Chenebin: EI, ١ II, ٤٤٥; ٢ III, ٩٤٠ - ٩٤١;
«الأعلام» للزركلى ٩/ ٢٥٥؛ «معجم المؤلفين» لكحالة ١٣/ ٢٤٣ - ٢٤٤؛ رمضان عبد التواب، في مقدمته لكتاب «الحروف»، القاهرة ١٩٦٩؛ محى الدين','1','227',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (224,'توفيق إبراهيم، ابن السكيت اللغوى، بغداد ١٩٦٩ (انظر المورد ٧، ١/ ١٩٧٨/ ٢٦٥).
سنذكر ما لم يصل إلينا من مؤلفاته تبعا لقوائم كتبه عند ابن النديم ٧٢ - ٧٣ (طهران، ص ٧٩؛ الترجمة الانجليزية ١٥٩ - ١٦٠)؛ «الرجال» للنجاشى ٣٥٠؛ «إرشاد الأريب» لياقوت ٧/ ٣٠١ - ٣٠٢؛ «إنباه الرواة» للقفطى ٤/ ٥٥ - ٥٦؛ ابن خلكان ٢/ ٤١١.

<span data-type="title" id=toc-245>ومن مؤلفاته العامة والشاملة في اللغة:</span>
١ - «إصلاح المنطق»، وهو أشهر كتبه، نال تقديرا عاما وحظى برواج كبير، وكان مصدرا لكثيرين منهم الأزهرى (انظر ١/ ٢٣)، والصغانى في «التكملة» (انظر ١/ المقدمة ص ٨)، وانظر في روايته، B.Lewinin: Oriens ٣ /١٩٥٠ /٣٢٦ - ٣٢٧: وراجع فهرست ابن خير ٣٣٠ - ٣٣٣؛ و پولس سباط في MIE ٤٩ /١٩٤٦ /٧ ,No.١١٤ ..

<span data-type="title" id=toc-246>المخطوطات:</span>
المتحف البريطانى ٩٦) ٣٠٧٤ Or. ورقة، من القرن السادس أو السابع للهجرة، انظر الملحق، رقم ٨٣١)، تشستربيتى ٣٢٦٣ (٢٦٧ ورقة، من القرن السادس للهجرة)، بودليانا بأكسفورد) ١/ ٢٢٨ Poc، الأوراق ١ - ٧٧، انظر الفهرس ٢/ ١٨١ - ١٨٢، رقم ٢١٣)، ليدن) ٤٤٦ Or.، في تهذيب التبريزى، انظر بعد)، برلين، ٣٨٨٠ Or.Oct، سابقا مجموعة دحداح ١٧٠ (١٨٢ ورقة)، الاسكوريال ٢٩ (١٣٤ ورقة، نسخت سنة ٦٢٧ هـ)، و ١١٢ (١٨٦ ورقة، نسخت سنة ٥٣١ هـ، راجع فهرس المخطوطات ١/ ٣٤١)، المتحف الآسيوى بليننجراد (معهد علوم آسيا)، انظر مجلة معهد المخطوطات ٢٣، ٢/ ١٩٧٧/ ١٣٦، صوفيا ١٩٤) ١٩٧٣ O.P.، ورقة، انظر فهرس درويش ٢/ ٣ - ٨)، كوبريلى ١٢٠٧ (٢٥٦ ورقة، نسخت سنة ٤٨٨ هـ، راجع B.Lewin في الموضع المذكور، ص ٣٢٦)، و ١٢٠٨ (٢٥٣ ورقة، نسخت سنة ٥٥٧ هـ، راجع B.Lewin في الموضع المذكور)، و ١٢٠٩ (الأوراق ١ - ١٧٧ ب، نسخت سنة ٤٤٧ هـ)، كوبريلى ٢، ٢٥٦ (الأوراق ١ -','1','228',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (225,'١٤٧ أ، نسخت سنة ١١٦٢ هـ)، فيض الله ١٥٦٠ (١٧٢ ورقة، من القرن السادس للهجرة، انظر، (O.Rescherin: ZDMG ٦٨ /١٩١٤ /٣٧٧ نور عثمانية ٤٦٩٢ (٢٠٩ ورقة، من القرن السادس للهجرة، عن نسخة كتبت في سنة ٣٩٥ هـ، انظر B.Lewin في الموضع المذكور)، لا له لى ٣٥٣٤ (١ ب- ١٧١ أ، نسخت سنة ٥٦٩ هـ)، و ٣٥٣٥ (٢ ب- ١٦٨ أ، نسخت سنة ٥٢٥ هـ)، و ٣٥٣٦ (١ ب- ١٧٧ ب)، نسخت سنة ٦١٦ هـ، انظر في المخطوطات الثلاثة B.Lewin في الموضع المذكور)، عاطف ٢٧١٢ (الأوراق ١ - ٢٠٧ أ، وعليها إجازة في سنة ٤٨١ هـ من الخطيب التبريزى)، سليم أغا ١٢١٨ (١٨٥ ورقة، نسخت سنة ٤٧٠ هـ)، مدرسة أسعد أفندى شيخ الاسلام ١١٥ (٢٥٩ ورقة، من القرن الخامس للهجرة)، قاصد جى زاده ٦٥٧ (١٥٠ ورقة، من القرن العاشر للهجرة)، قسطمونى ١٢٣٠ (٢٠٠ ورقة، من القرن السادس للهجرة)، الأوقاف ببغداد ٦٠١٦ (٢٣٨ ورقة، لعلها من القرن الخامس للهجرة، انظر طلس، رقم ٢٢٤٠؛ وفهرس الجبورى ٣/ ٢١٦، رقم (٥١١٨)، دار الكتب بالقاهرة، لغة ٣٤١ (نسخت سنة ٤٧٦ هـ)، ولغة ٢٧ م (نسخت سنة ٧٨٥ هـ)، ولغة ٢٨ م (كتبت سنة ١١٣٦ هـ عن نسخة تاريخها سنة ٣٦٠ هـ، انظر الفهرس الثانى ٢/ ٢)، البلدية بالاسكندرية ٢٠١٣ د (المجلد الثانى، نسخ سنة ٤١٨ هـ)، و ٣٨٣٠ ج (وعليها إجازة فى سنة ٦٥٠ هـ، كتبت عن نسخة تاريخها سنة ٣٢٤ هـ، انظر الفهرس، الجزء الأول، قسم اللغة ٦١)، المنصورة (وعليها سماع تاريخه سنة ٣٧٢ هـ، أهداها الملك فاروق سنة ١٩٥١، انظر عبد الرحمن عبد التواب في: مجلة معهد المخطوطات ٤/ ١٩٥٨/ ٢٧٨، رقم ٩٨)، ومنها مصورة بدار الكتب ٤٥٨٠ هـ، اليوسفية بمراكش ٢٥٧ (نسخت سنة ٥٧٢ هـ، ولعلها توجد الآن في الرباط)، الخزانة الملكية بالرباط ١٨٠ (نحو ٢٠٠ ورقة، نسخت سنة ٥٩١ هـ، مخطوطة جيدة)، ومنها مصورة بمعهد المخطوطات بالقاهرة، المكتبة العامة بالرباط، د ٩٧٥/ (٩٤ ورقة، نسخت سنة ١٣٢٩ هـ)، ود ٩٧٥ مكرر (٩٨ ورقة، نسخت سنة ٩٥٧ هـ، انظر فهرس علوش ١/ ٣٦٣ - ٣٦٤)، الحمزاوية بالرباط ١٦٢/ ١ (الجزء الثانى، انظر محمد المنونى في: تطوان ٨/','1','229',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (226,'١٩٦٣/ ١٥٧)، عارف حكمت بالمدينة، مجموع ١٣٦ (نسخة قديمة) أو مجموع ١٥٠/ ١ (الأوراق ١ - ١٠٧، انظر المنتخب لكحالة ١١٠)؛ نشره أحمد محمد شاكر وعبد السلام هارون على مخطوطات المنصورة والقاهرة والاسكوريال، القاهرة ١٩٤٩، ط ٢ ١٩٥٦، انظر في هذه الطبعة Oriens ٣ /١٩٥٠ /٣٢٥ - ٣٢٨. ومنه مختارات مع نصوص مناظرة للغويين آخرين فى «نصوص في فقه اللغة العربية» للسيد يعقوب بكر، الجزء الأول، بيروت ١٩٧٠، ص ٣٧٨ - ٣٨٨.

<span data-type="title" id=toc-247>وله مختصرات، وشروح، واستدراكات ... الخ</span>.
١ - (استدراك على إصلاح المنطق) لأبي القاسم عبد الرحمن بن إسحاق الزجاجى (المتوفي نحو سنة ٣٣٧/ ٩٤٩)، لم يصل إلينا، انظر ص ١٠٥ آنفا.
(أ) «الرد على الزجاجى فيما استدركه على ابن السكيت» لأبي سعيد عبد الرحمن ابن محمد بن دوست (المتوفى سنة ٤٣١/ ١٠٤٠)، لم يصل إلينا، انظر ص ٢٣٧ بعد.
٢ - شرح لأبي منصور محمد بن أحمد الأزهرى (المتوفى سنة ٣٧٠/ ٩٨٠)، لم يصل إلينا، انظر ص ٢٠٤ بعد.
٣ - انتقاد لعلى بن حمزة البصرى (المتوفى سنة ٣٧٥/ ٩٨٥) فى الباب السابع من مؤلّفه «كتاب التنبيهات على أغاليط الرواة»، وصل إلينا، انظر ص ١١٠ آنفا.
٤ - شرح لأبي على الفارسى (المتوفى سنة ٣٧٧/ ٩٨٧)، انظر فى نقول عنه ص ١٠٩ آنفا.
٥ - «شرح أبيات إصلاح المنطق» لابن السيرافى (المتوفى سنة ٣٨٥/ ٩٩٥)، وصل إلينا، انظر فصل الأدب.
وقد علق عليه بكتاب «قيد الأوابد ... » انتقاد على ابن السيرافى للأسود الغندجانى (المتوفى سنة ٤٣٦/ ١٠٤٤)، لم يصل إلينا، انظر فصل الأدب.
٦ - «جوامع كتاب إصلاح المنطق»، مختصر لزيد بن رفاعة (أواخر القرن ٤/ ١٠)، وصل إلينا وطبع، انظر ص ١٨٣ بعد.','1','230',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (227,'٧ - شرح لابى المظفر محمد بن آدم بن كمال الهروى (المتوفى سنة ٤١٤/ ١٠٢٣)، لم يصل إلينا، انظر ص ٢٢٧ بعد.
٨ - «كتاب المنخّل»، مختصر للوزير المغربي (المتوفى سنة ٤١٨/ ١٠٢٧)، وصل إلينا، انظر ص ٢٤٥ بعد.
٩ - «تهذيب إصلاح المنطق» لأبي على الحسن بن المظفّر النيسابورى الضرير (المتوفى سنة ٤٤٢/ ١٠٥٠، انظر «معجم المؤلفين» لكحالة ٣/ ٢٩٦)، ذكر في «إرشاد الأريب» لياقوت ٣/ ٢١٩، و «كشف الظنون» ١٠٨.
١٠ - «شرح مع زيادة ألفاظ في الغريب لأبي العباس أحمد بن محمد بن أحمد بن بلال المرسى (المتوفى نحو ٤٦٠/ ١٠٦٨)، انظر الوافى بالوفيات للصفدى ٧/ ٣٦١، وراجع «بغية الوعاة» للسيوطى ١٥٧؛ كشف الظنون ١٠٨.
١١ - «تهذيب إصلاح المنطق» ليحيى بن على الخطيب التبريزى (المتوفى سنة ٥٠٢/ ١١٠٩).

<span data-type="title" id=toc-248>المخطوطات:</span>
دار الكتب بالقاهرة، ٥٧٠٧ هـ (١٣٧ ورقة، انظر الملحق ١/ ١٨٩)، الأزهر، لغة ٩٧ (٣٣٨ ورقة، نسخت سنة ١٠٠٦ هـ، انظر الفهرس ٤/ ١٢)، الأحمدية بتونس ٤٩٤٦ (نسخت سنة ٤٩٦ هـ)، ليدن ٢٢١) ٤٤٦ Or.، ورقة، ومعه النص الأصلى، نسخت سنة ٤٩٥ هـ، انظر فورهوف ١٤٠؛
F. Rundgrenin: Orientalia (Suecana ٣/ ١٩٥٤/ ١٣٨ - ١٤١
، و) ٥٩٧ Or. نسخة حديثة، المقدمة فحسب)، ييل ٣٤١) ٤٦٢ - L، ورقة، نسخت سنة ١٣٠٩ هـ، انظر نيموى رقم ٢٠٧)؛ نشره ناقصا م. زكى، وص. على، ومحمد بدر الدين النعسانى، القاهرة ١٣٢٥.
وقد عقّب عليه بكتاب «الرد على الخطيب التبريزي في إصلاح المنطق» لأبي محمد عبد الله بن أحمد بن الخشّاب البغدادى (المتوفى سنة ٥٦٧/ ١١٧٢، انظر معجم المؤلفين لكحالة ٦/ ٢٠)، مذكور في «إرشاد الأريب» لياقوت ٤/ ٢٨٧؛ كشف الظنون ١٠٨.
١٢ - «مختصر» للخطيب التبريزى أيضا، ذكر في خزانة الأدب ١/ ١١/','1','231',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (228,'١٣ - «إيضاح ... »، وهو كذلك للخطيب التبريزى (انظر خزانة الأدب ٢/ ٨٢)، ولعله هو نفسه الكتاب الذى نقل عنه باسم «شرح» (انظر خزانة الأدب ٢/ ١٩٣، ٤/ ١٢٤).
١٤ - شرح لعبد الله بن محمد بن السيد البطليوسى (المتوفى سنة ٥٢١/ ١١٢٧)، نقل عنه مرارا في «خزانة الأدب»، انظر إقليد الخزانة للميمنى ٨.
١٥ - «تكملة إصلاح المنطق» لموهوب بن أحمد بن محمد بن الجواليقى (المتوفى سنة ٥٣٩/ ١١٤٤)، انظر في نقول عنه خزانة الأدب ٤/ ٩١.
١٦ - «مختصر» لعون الدين يحيي بن محمد بن هبيرة (المتوفي سنة ٥٦٠/ ١١٦٥)، ذكر في كشف الظنون ١٠٨.
١٧ - «مختصر» لأبي المكارم على بن محمد بن محمد النحوى (المتوفي سنة ٥٦١/ ١١٦٦؛ انظر معجم المؤلفين لكحالة ٧/ ٢٣١)، مذكور في بغيةالوعاة للسيوطى ٣٥٣؛ كشف الظنون ١٠٨.
١٨ - «مختصر» لناصر الدين المطرّزى (المتوفي سنة ٦١٠/ ١٣١٢)، ورد ذكره في «كشف الظنون» ١٠٨.
١٩ - «المشوّف المعلم ... » ولعله هو كتاب «المشوّف المعلم في ترتيب إصلاح المنطق على حروف المعجم» لأبي البقاء عبد الله بن الحسين بن عبد الله العكبرى (المتوفى سنة ٦١٦/ ١٢١٩)، الذى ذكره له الصفدى (انظر إنباه الرواة للقفطى ٢/ ١٧٧ هامش ٢) وصاحب كشف الظنون ١٠٨، ويوجد مخطوطا في حميدية باستنبول ١٤٢٩/ ١ (الأوراق ١ - ١٤٧ أ، نسخت سنة ١١٦٢ هـ) ٢٠ - شرح لأبي جعفر أحمد بن يوسف بن على الفهرى اللّبلى (المتوفى سنة ٦٩١/ ١٢٩٢)، انظر في نقل عنه «خزانة الأدب» ٦/ ٢٩٥.
٢١ - «خطبة الإصلاح» لمحمد بن عبد الغفور الكلاعى (القرن ٧/ ١٣)، وفي الوقت نفسه معارضة لخطبة «فصيح ثعلب» لأبي العلاء المعرى (انظر ص ١٤٤','1','232',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (229,'بعد)، ذكرها الكلاعى في «إحكام صنعة الكلام»، بيروت ١٩٦٦، ص ٢٨، ٣١.
٢ - «كتاب البحث»، ذكره ابن النديم، والقفطى، وياقوت، وهو من مصادر الصغانى في «العباب» (انظر ١/ ٢٨)، ويوجد مخطوطا في ثلاثة أجزاء بدار الكتب بالقاهرة، التيمورية، لغة ١٣٨ (أوله ناقص، ٦٥ ورقة، نسخت سنة ١٣٢٦ هـ، راجع فهرس المخطوطات ١/ ٣٤٢).
٣ - «التوسعة (في كلام العرب)»، انظر «كشف الظنون» ٥٠٧، ١٤٠٦، ونقل عنه السيوطى في الاشباه والنظائر ١/ ٢٧٢ (انظر رمضان عبد التواب، في مقدمة «كتاب الحروف» لابن السكيت، القاهرة ١٩٦٩، ص ٢١)، وتاج العروس ٢ ٦/ ١٩٠ (لفج).
٤ - «كتاب الزّبرج»، ذكره ابن النديم وثلاث قوائم أخرى، وهو من مصادر ابن سيده في «المخصص» (انظر ١/ ١٢).
٥ - «كتاب النوادر»، لم يصل إلينا، انظر تاريخ التراث العربي ٢/ ٨٨.

<span data-type="title" id=toc-249>وله في صيغ الألفاظ ومعانيها:</span>
١ - «كتاب الألفاظ» (١١٣)، وهو كتاب في المترادفات يشتمل على ١٥٧ بابا، جاء ذكره في خمس قوائم (انظر فهرست ابن خير ٣٢٩ - ٣٣٠؛ پولس سباط في MIE ٤٩ / (١٩٤٦ /٨ ,No.١٤٣ وهو من مصادر الأزهرى (انظر ١/ ٢٣)؛ والصغانى في «التكملة» (انظر ١/ ٨)، و «العباب» (انظر ١/ ٢٨)؛ وحبيش التفليسى (المتوفى سنة ٦٢٩/ ١٢٣٢) في «قانون الأدب» (انظر مجلة المجمع العلمى العربي بدمشق ٥٠/ ١٩٧٥/ ٤٠١)؛ وأحمد بن يوسف الفهرى (المتوفي سنة ٦٩١/ ١٢٩٢) في «تحفة المجد الصريح» (انظر مجلة المجمع العلمي العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٢)؛ «خزانة الأدب» (انظر ١/ ١١).
_________
(١١٣) «وقد حمل إلينا كتاب كبير فى الألفاظ مقدار ثلاثين جلدا، ونسب إلى ابن السكيت.
فسألت المنذرى عنه (يعنى أبا الفضل محمد بن أبي جعفر المنذرى، المتوفى سنة','1','233',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (230,'<span data-type="title" id=toc-250><span data-type="title" id=toc-251>المخطوطات:</span></span>
القرويين بفاس ٣٦٥/ ٨٠ (الجزءان الأول والثالث، ٢٠٠ ورقة، قرئت سنة ٥١١ هـ، انظر، L istedemanuscrits ٤٨ ,No.٢٠٤ وراجع مجلة المجمع العلمى العربي بدمشق ٣٣/ ١٩٥٨/ ٦٨٤ - ٦٨٥؛ فهرس بيل ٩٨، رقم ١٢٤٤)، ومنها مصورة بمعهد المخطوطات بالقاهرة، المكتبة العامة بالرباط، الحمزاوية ٢٧ (٦٠٧ هـ، انظر محمد المنونى في: تطوان ٨/ ١٩٦٣/ ١٣٧)، باريس ٢٤٣٢ (٢٦١ ورقة، نسخت سنة ١٢٠٠ هـ، انظر قجدا ٢٥٥)، المكتب الهندي بلندن) ٣٢٢٥ Ar.، انظر (F.Krenkowin: Islamica ٧ /١٩٣٨ /٢. وقد علق على كتاب الألفاظ بكتابين:
(أ) «شرح أبيات «كتاب الألفاظ» لابن السيرافي (المتوفي سنة ٣٨٥/ ٩٩٥)، لم يصل إلينا، انظر فصل الأدب.
(ب) «تهذيب الألفاظ» للخطيب التبريزى (المتوفي سنة ٥٠٢/ ١١٠٩)، ويوجد مخطوطا في ليدن ٥٥٣) ٥٩٧ Or. ورقة، نسخت سنة ٤٨٩ هـ، انظر فورهوف ١٠، مدمج في النص الأصلى، انظر. (F.Rundgrenin: Orientalia Suecana ٣ /١٩٥٤ /١٤١.
نشره لويس شيخو، «كنز الحفاظ في كتاب تهذيب الألفاظ»، بيروت ١٨٩٦ - ١٨٩٨، وأعيد طبعه في بيروت ١٩٧٦، «كتاب مختصر تهذيب الألفاظ»، نشره كذلك لويس شيخو، بيروت ١٨٩٧.
٢ - «كتاب الأضداد»، ذكرته خمس قوائم (انظر فهرست ابن خير ٣٨٢؛ و پولس سباط في، (MIE ٤٩ /١٩٤٦ /٧ ,No.١٢٤ وفيه نقول عن أبي عبيدة والأصمعى، ويحتمل أنها من كتابيهما في «الأضداد».
المخطوطات:
رئيس الكتاب ٨٧٥ (٤٣ ورقة، من القرن الثامن للهجرة، راجع O.Rescherin:
_________
٣٢٩/ ٩٤١) فلم يعرفه، وإلى اليوم لم أقف على مؤلف الكتاب على الصحة ... » (الأزهرى ١/ ٢٣)، ويحتمل أنه غير الكتاب المذكور.','1','234',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (231,'(MFO ٥/ ١٩١٢/ ٥٠٩
بغدادلى وهبى ١٩٤٧ (٦ ورقات، من القرن ١٣ هـ)؛ دار الكتب بالقاهرة، لغة ٦ ش (ص ٦١ - ٧٩، نسخت سنة ١٢٩٢ هـ عن إحدى مخطوطتى استنبول بخط الشنقيطى)؛ نشرها أوغست هفنر في «ثلاثة كتب في الأضداد»، بيروت ١٩١٢، ص ١٦٣ - ٢٢٠. ومنه ٢٩ نقلا في «الأضداد» للأنبارى (انظر الفهرس ص ٤٩٣ - ٤٩٤).
ومن الأكثر في الاحتمال أن مخطوطة قينا ٣٥٥/ ٦ (١٣٩ أ- ١٤٩ ب، من القرن الرابع للهجرة) تحتوى على رواية أخرى للكتاب، انظر رمضان عبد التواب
Das Kitab al- Garlbal- Musannaf ... , Heppenheim ١٩٦٢, S. ٩٤
؛ نفسه، «كتاب الأضداد للأصمعى ليس للأصمعى» في: المكتبة (المثنى) رقم ٥٥ (١٩٦٦).
وكان هفنر قد نشره، في الكتاب المذكور ص ٥ - ٧٠، على أنه للأصمعى اعتمادا منه على قول جاء في المخطوطة. وانظر في مختارات منه مقارنة بنصوص من كتب أخرى في الأضداد: «نصوص في فقه اللغة العربية للسيد يعقوب بكر، الجزء الثانى، بيروت ١٩٧١، ص ١٩٣ - ٢٠١.
٣ - «كتاب القلب والإبدال»، ذكر في أربع قوائم (انظر أيضا «الخصائص» لابن جنى ٢/ ٨٨؛ فهرست ابن خير ٣٨١)، وكان مصدرا من مصادر الأزهرى (انظر ١/ ٢٣) والصغانى في «التكملة» (انظر الجزء الأول، المقدمة ص ٨) و «العباب» (انظر ١/ ٢٨)، وانظر في نقول عنه: «الصحاح» للجوهرى (٢) ٨٩١، ٩١١، (٣) ٩٩٨، (٥) ٢٠٠٥؛ «معجم ما استعجم» للبكرى ٥٤٣ («كتاب في الإبدال»)؛ «الممتع» لابن عصفور ٦١٦؛ «خزانة الأدب» (انظر إقليد الخزانة للميمنى ٨٣)؛ «المزهر» للسيوطى (انظر الفهرس ٢/ ٦٣٨: «كتاب الإبدال»). ونقل فيه عن أبي عبيدة والأصمعى، ويحتمل أنه عن كتابيهما في الإبدال.

<span data-type="title" id=toc-252>المخطوطات:</span>
لا له لى ١٩٠٣/ ٢ (٢٤ أ- ٧٦ ب، من القرن السابع للهجرة، انظر؛ O.Rescher in: MO ٧ /١٩١٣ /١٠٤ وفهرس المخطوطات ١/ ٣٦٤)، سليم أغا ٨٩٣/ ٣','1','235',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (232,'(١٥٨ أ- ١٩٠ ب، نسخت سنة ١١١٤ هـ، انظر، O.Rescherin: ZDMG ٦٨ /١٩١٤ / (٥٧ ينى جامع ١١٩٥/ ٣ (١١٠ أ- ١٤٧ ب، نسخت سنة ٥٠٥ هـ، انظر فهرس المخطوطات ١/ ٣٦٤)، أمبروزيانا بميلانو، + ٩٥ (٨٣ ورقة، ناقصة، نسخت نحو سنة ٩٠٠ هـ، انظر فهرس لوفجرن/ ترينى ١/ ١٥٥، رقم ٢٨٩، وراجع مجلة معهد المخطوطات ٣/ ١٩٥٧/ ٣٤٧)؛ نشره أوغست هفنر على مخطوطة لا له لى في «الكنز اللغوى في اللسن العربي»، ليبزغ ١٩٠٥، ص ٣ - ٦٥ ونشره مرة أخرى حفنى شرف، القاهرة ١٩٧٨ ومنه مختارات مع نصوص مناظره للغويين آخرين في: «نصوص في فقه اللغةالعربية» للسيد يعقوب بكر، ٢/ ٢٧٣ - ٣٠٢.
وقد همّ «بشرح كتاب ابن السكيت في القلب والإبدال»، ابن جنى (انظر الخصائص ٢/ ٨٨)./ ٤ - «كتاب المثنّى والمكنّى والمبنى (١١٤) والمواخى والمشبه والمنحل» (ورد اسمه كاملا في «المزهر» للسيوطى ٢/ ١٩١)، ذكره ابن النديم، وكان من مصادر الصغانى في «التكملة» (انظر الجزء الأول، المقدمة ص ٨)، وأحمد بن يوسف الفهرى (المتوفي سنة ٦٩١/ ١٢٩٢)، في «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٢)، وكان كذلك من مصادر ابن سيده في «المخصص» (انظر ١/ ١٢، الأبواب المناظرة ١٣/ ١٦٩ - ٢٢٢) والسيوطى في «المزهر» (انظر الفهرس ٢/ ٦٤٨)، وانتقده أبو أحمد العسكرى في «شرح ما يقع فيه التصحيف والتحريف» ١٨١. وكما يؤخذ مما نقل عنه ابن سيده والسيوطى (الذي يذكر أيضا عناوين الأبواب) كان الجزء المتعلق بصيغ الأسماء يحتوى على «باب ذو» (انظر المخصص ١٣/ ٢٢٠، وترد النقول ابتداء من ص ٢١١؛ «المزهر» ١/ ٥٣٠) الذي يتضمن «باب البديهة» (انظر المخصص ١٣/ ٢٢١ - ٢٢٢؛ المزهر ١/ ٥٣٠ - ٥٣١). ويحتوى الجزء المتعلق بالمثنى على عناوين مثل: «باب ما جاء مثنى من
_________
(١١٤) ونقل أيضا عن كتاب له باسم «كتاب الأبناء» على أنه كتاب مستقل برأسه، انظر ص ١٣٥ بعد.','1','236',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (233,'الأسماء (وورد مسهبا ومع شواهد أيضا. في «المخصص» ١٣/ ٢٢٣ - ٢٢٦، وجاء الفصل كله موجزا ومجردا من الشواهد في «المزهر» ٢/ ١٧٣ - ١٨٢)، «باب الاسمين يغلب أحدهما على صاحبه» (انظر بعد)، «باب ما جاء مثنى من الأسماء لاتفاق الاسمين» (المخصص ١٣/ ٢٢٩؛ المزهر ٢/ ١٨٧) ولعله قد وصل إلينا باسم «كتاب الاسمين» في مخطوطة بصنعاء في مكتبة المنصور الخاصة، مجموع ١١ (ورقة واحدة، قديمة، انظر مجلة معهد المخطوطات ٢٤/ ١٩٧٨/ ٣٩، رقم ١٢٧)، «(باب) ممّا جاء مثنى ممّا هو (صفة) لقب ليس باسم» (المخصص ١٣/ ٢٣٠، المزهر ٢/ ١٨٨ - ١٨٩)، «باب ما جاء مجموعا وإنما هو اثنان أو واحد في الأصل (انظر بعد).
واشتملت أجزاء أخرى من الكتاب على عناوين كالعناوين الآتية (العبارة على التقريب): «باب المجموع على التغليب» (انظر المزهر ٢/ ٢٠٤)، «باب الألفاظ التى تقال للمجهول» (المزهر ٢/ ٢٤٤)، «باب أيمان العرب» (المزهر ٢/ ٢٦١ - ٢٦٢)، وباب فيما خرج عن الأيمان المعتادة التى في أولها واو (المزهر ٢/ ٢٦٢ - ٢٦٣)، و «باب ما يدعى به عليه (١١٥)» (المزهر ٢/ ٢٦٣ - ٢٧٠). ووصل إلينا من هذا الكتاب بابان بالتأكيد، وربما أربعة:
١ - «باب حروف تقع مستعارة».
٢ - «باب الحروف التى جوّزتها العرب أو غلطت فيها»، ٣ - «باب الاسمين يغلب أحدهما على صاحبه» (١١٦).
٤ - «باب ما جاء مجموعا وإنما هو اثنان أو واحد في الأصل» (١١٧)؛ في مخطوطة رئيس الكتاب ٨٧٩/ ٤ (١٠٠ أ- ١٠٢ ب، من القرن ١١ هـ)، ومنها مصورة في جامعة القاهرة ٢٢٩٦٧، ومنها نسخة بدار الكتب بالقاهرة، لغة ٦ ش/ ٣ (الأوراق ١٧ - ١٩، نسخة الشنقيطى، كتبت سنة ١٢٩٢ هـ)، التيمورية بدار الكتب، لغة ٣٣٢/ ٣ (الأوراق ٣٨ - ٤٨، نسخت سنة ١٣٢٨ هـ)،
_________
(١١٥) انظر فيما بعد «كتاب الدعاء» الذى نقل عنه البكرى على أنه كتاب مستقل بذاته.
(١١٦) تتفق نقول المخصص ١٣/ ٢٢٧ - ٢٢٨ والمزهر ٢/ ١٨٥ - ١٨٧ فى العنوان وإلى حد بعيد فى المحتوى مع النص المنشور.
(١١٧) كذلك «المخصص» ١٣/ ٢٣٤ - ٢٣٦ و «المزهر» ٢/ ١٩١ - ١٩٢ مقارنين بصفحة ٥٣ - ٥٥ من النص المنشور.','1','237',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (234,'نشرها رمضان عبد التواب باسم «كتاب الحروف التى يتكلم بها في غير موضعها»، القاهرة ١٩٦٩.
٥ - «مجاز ما جاء في الشعر وحرّف عن جهته» (كذا ذكره ابن قاضى شهبة، طبقات النحاة) أو «ما جاء في الشعر وما حرّف عن جهته» (على ما ذكره القفطى).
وربما كان يرجع إلى هذا الكتاب البابان الأولان من الأبواب الأربعة في مخطوطة رئيس الكتاب ٨٧٩/ ٤ (انظر رقم ٤ - آنفا).
٦ - «كتاب الأجناس»، كتاب «كبير» (ابن النديم)، ذكرته أربع قوائم، ومن الجائز أن نقلا عنه ورد في آخر «مجالس» الخطيب الإسكافي، مخطوط كوبريلى ٢٠٩، الورقة ١٢٤ ب.
٧ - «كتاب ما اتفق لفظه واختلف معناه»، لم يذكره غير النجاشي./ ٨ - «كتاب الأصوات»، ذكره القفطى والنجاشى وابن خلكان (انظر أيضا فهرست ابن خير ٣٨٢)، وكان من مصادر ابن سيده في «المخصص» (انظر ١/ ١٢)، وانظر في نقول عنه «المزهر» للسيوطى (الفهرس ٢/ ٦٣٨).

<span data-type="title" id=toc-253>وله في الأمثال والعبارات والدعاء والأقسام وصيغ أسماء الأعلام:</span>
١ - «كتاب الأمثال» ذكره ابن النديم وياقوت وابن خلكان، وكان في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر يولس سباط في، (MIE ٤٩ /١٩٤٦ /٨ ,No.١٥٣: ونقل عنه الميدانى (انظر ع. التكريتى في: المورد ٣، ٣/ ١٩٧٤/ ١٠٦ - ١٠٧)، وأيضا الاغاني، طبعة برونو، ٢١/ ١٨٩ - ١٩٠، ١٩٢، ٢٠٣ - ٢٠٤؛ وحمزة الأصفهاني في «الدرة الفاخرة» ١٥٢، ٢٤٤، ٥٠٧ (وذكر هنا اسم الكتاب) ٥٤٦؛ والبكرى في «فصل المقال»، بيروت ١٩٧١، ص ١٠٨، ٣٣٦، راجع «مجالس العلماء» للزجاجى ٤٩، وانظر أيضا. Sellheim ,Sprichworter ١١٢','1','238',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (235,'٢ - «كتاب الدعاء»، نقل عنه البكرى في المرجع المذكور، ص ٨٠، ولعله هو نفسه «باب ما يدعى به عليه» من «كتاب المثنى» (انظر آنفا).
٣ - «كتاب الأبناء»، انظر نقلا عنه في «المعجم ما أستعجم» للبكرى ٩١٧، ولعله كذلك من أبواب كتاب المثنى (انظر آنفا).

<span data-type="title" id=toc-254>وله تآليف مفردة في الإنسان والحيوان والنبات والأشياء وغير ذلك:</span>
١ - «كتاب خلق الإنسان»، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣٨٢)، انظر نقلا عنه في «الاقتضاب» لابن السيد البطليوسى.
٢ - «كتاب الفرق»، مذكور عند ابن النديم وفي ثلاث قوائم أخرى، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣٨٢)، وكان من مصادر ابن سيده في المخصص (انظر ١/ ١٢)، والصغانى في «التكملة» (انظر الجزء الأول، المقدمة ص ٨) و «العباب» (انظر ١/ ٢٨)، والجواليقى في «المعرب» (انظر على سبيل المثال ٣٠١). ونقل عنه الفهرى في «تحفة المجد الصريح»، مخطوطة القاهرة (انظر ص ١٤٤ بعد)، ص ٦٦.
٣ - «كتاب الوحوش»، ذكره النجاشى وياقوت والقفطى وابن خلكان، وكان في القرن ٧/ ١٣ في إحدى خزائن الكتب بحلب (انظر پولس سباط في، MIE ٤٩ / (١٩٤٦ /٥٠ ,No.٩٠٤: ومن الجائز أن نقولا عنه في «حياة الحيوان الكبرى» للدّميرى (انظر. (J.de Somogyiin: JA ٢١٣ /١٩٢٨ /١٧
٤ - «كتاب الإبل»، مذكور في الفهرست لابن النديم وفي ثلاث قوائم أخرى.
٥ - «كتاب الحشرات»، ذكره ياقوت والقفطى وابن خلكان، وكان موجودا في القرن ٧/ ١٣ بمكتبة من مكتبات حلب (انظر بولس سباط في الموضع المذكور ص ١٧ رقم ٣١٣).
٦ - «كتاب الطير»، ذكره النجاشى؛ وفي «الهداية» لإسماعيل البغدادى ذكر كتاب باسم «كتاب منطق الطير»، ولعله خطأ.','1','239',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (236,'٧ - «كتاب النبات والشجر» ويقال أيضا «الشجر والنبات»، ذكرته خمس قوائم، وعرف في الأندلس (انظر فهرست ابن خير ٣٨٢)، وكان في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في الموضع المذكور ص ٤٨، رقم ٨٦٩)، وانظر في نقول عنه: تاريخ التراث العربي ٤/ ٣٣٥.
٨ - «كتاب السرج واللجام»، مذكور عند ابن النديم وفي ثلاث قوائم أخرى.
٩ - «كتاب الأنواء»، ذكره ابن قاضى شهبة، وإسماعيل البغدادى في الهداية ٢/ ٥٣٧، انظر في نقول عنه: تاريخ التراث العربي ٧/ ٣٤٧.
١٠ - «كتاب الأيام والليالى»، ذكره ابن النديم وياقوت، وكان في القرن ٧/ ١٣ في إحدى خزائن الكتب بحلب (انظر بولس سباط، الموضع المذكور، ص ١٠، رقم ١٧٧)، وانظر في نقول عنه: تاريخ التراث العربي ٧/ ٣٤٧.
١١ - «كتاب الأرضين والجبال والأودية»، ذكره النجاشى، انظر فصل الجغرافية./

<span data-type="title" id=toc-255>وفي لغة القرآن:</span>
«غريب القرآن»، غير وارد في القوائم المتقدمة، ولم يذكره إلّا البغدادى في الهداية ٢/ ٥٣٦.

<span data-type="title" id=toc-256>أبو عكرمة الضبّي</span>
هو عامر بن عمران بن زياد، من أهل سامراء، سمع من ابن الأعرابي وابن السكيت وأبي مسحل وغيرهم، وسمع منه صعوداء محمد بن هبيرة، والقاسم الأنبارى.
وفضلا عن مؤلفاته في اللغة، كان أبو عكرمة راوية جليلا للشعر والأخبار. فروى عن دواوين عن ابن الأعرابي، وكذلك المفضليات (انظر تاريخ التراث العربي ٢/ ٥٣)، وعنه أخذها ابن الأنبارى، توفي سنة ٢٥٠/ ٨٦٤.
«مراتب النحويين» لأبي الطيب ٩١؛ «إرشاد الأريب» لياقوت ١ ٤/ ٢٨٣، ٢ ١٢/ ٣٩؛ «بغية الوعاة» للسيوطى Rescher ,Abri BII ,١٨١ - ١٨٢ ;Br.SI ,١٨٠ ;.٢٧٤','1','240',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (237,'رمضان عبد التواب، مقدمته لكتاب «الأمثال»، دمشق، بدون تاريخ.
١ - «كتاب الأمثال»، لم يذكره من ترجموا له، وقام راويه الأول، أبو القاسم إبراهيم بن السرى بن يحيى التميمى بإكماله بذيل صغير زاده. انظر في محتواه وروايته. Sellheim ,Sprichworter ١٠٨ - ١١١

<span data-type="title" id=toc-257>المخطوطات:</span>
الاسكوريال ١٧٠٥/ ٥ (٣٩ أ- ٥٨ أ، نسخت سنة ٤٩٩ هـ، بخط موهوب بن أحمد الجواليقى)، وهى أصل لكل المخطوطات الأخرى، مثل ولى الدين ٣١٧٨/ ٧ (٣٢ أ- ٤٥ ب، من القرن العاشر للهجرة، انظر، (O.Rescherin: MO ٧ /١٩١٣ /١٠٨ عاطف ٢٠٠٣/ ٦ (٦٥ أ- ٩٥ ب، نسخت سنة ١١٠٠ هـ، راجع O.Rescherin: (MFO ٥ /١٩١٢ /٤٩٠ سامرا، الإمام المهدى، الرسالة السابعة ضمن مجموع (نسخت قبل سنة ٩٨٣ هـ، انظر مجلة معهد المخطوطات ٤/ ١٩٥٨/ ٢١٣ - ٢١٤)، دار الكتب بالقاهرة، مجموع ٢ ش (١٨ ورقة، نسخها الشنقيطى سنة ١٢٩٥ هـ من إحدى مخطوطات استنبول، انظر فهرس المخطوطات ١/ ٤٢٧)، التيمورية بدار الكتب، مجموع ٥٠/ ٦ (ص ١٢٤ - ١٨٤، من القرن ١٣ هـ).
نشره رمضان عبد التواب، دمشق، بدون تاريخ (١٩٧١).
٢ - «كتاب الإبل والغنم»، ذكره ياقوت في إرشاد الأريب ٤/ ٢٨٣.
٣ - «كتاب الخيل»، انظر «مراتب النحويين» لأبي الطيب ٩١، ياقوت، الموضع نفسه، «بغية الوعاة» للسيوطى ٧٤؛ و «المزهر» له ٢/ ٤١١؛ «تاج العروس» ١/ ١١. وكانت منه نسخة في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر بولس سباط في. (MIE ٤٩ /١٩٤٦ /١٩ ,No.٣٤٥:

<span data-type="title" id=toc-258>ثابت بن أبي ثابت</span>
هو أبو محمد ثابت بن أبي ثابت سعيد (أو محمد أو على) اللغوى، كان تلميذا لأبي عبيد بن سلام. لقى الأعراب وأخذ عنهم. ويعد من كبار الكوفيين. ولا يعرف تاريخ مولده ولا وفاته؛ ومن الراجح أنه توفي حوالى منتصف القرن الثالث/ التاسع./','1','241',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (238,'«طبقات النحويين» للزبيدى ٢٢٥؛ ابن النديم ٦٩؛ «تاريخ العلماء» للتنوخى ٨٧ أ؛ «إنباه الرواة» للقفطى ١/ ٢٦١؛ «إرشاد الأريب» لياقوت ٢/ ٣٩٦ - ٣٩٧، ٧/ ١٤٠ - ١٤١؛ ابن قاضى شهبة، المخطوط ص ٤١٢ (حيث ورد اسمه: على بن ثابت بن أبي ثابت)؛ «غاية النهاية» لابن الجزرى ١/ ١٨٨؛ «بغية الوعاة» للسيوطى ٢١٠ - وانظر مقدمة تحقيق «مجاز القرآن» لأبي عبيدة ١/ ١، ٢، ٤، «معجم المؤلفين» لكحالة ٣/ ١٠٠.

<span data-type="title" id=toc-259>وله في اللغة:</span>
١ - «كتاب خلق الإنسان» (١١٨)، أطراه القفطى، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣٦٣ - ٣٦٥). وذكر المؤلف من بين مصادره كتبا في «خلق الإنسان» لأبي عبيد (لعله باب «خلق الإنسان» من «الغريب المصنف»)، والأثرم، والأصمعى، وسلمه بن عاصم، وأبي زيد الأنصارى.

<span data-type="title" id=toc-260>المخطوطات:</span>
التيمورية، لغة ١٦٦ (١١٩ ورقة، نسخت سنة ٥٣٩ هـ، راجع فهرس المخطوطات ١/ ٣٥٣) ومنها نسخة بدار الكتب، ٥٢٣٩ هـ (نسخت سنة ١٣٥٨ هـ)، ومصورة منها أيضا، ٦٤٤٤ هـ (انظر الملحق ١/ ٣٠٠)، القرويين بفاس ٥٣٩/ ١ (١ ب- ٧٧ ب، نسخت سنة ٦٠٠ هـ، راجع Li stedesmanuscrits (٥٠ ,No.٢١٧ و ٨٣٤ (٧٧ ورقة، لعلها من القرن السادس للهجرة، انظر مجلة المجمع العلمى العربي بدمشق ٥١/ ١٩٧٦/ ٣٦٢)، ومن المخطوطتين مصورتان بمعهد المخطوطات بالقاهرة، آيت عياش، الحمزاوية (ولعلها اليوم في الرباط) ١٣٧ (وعليها تمليك تاريخه سنة ٦٢٠ هـ، انظر محمد المنونى في: تطوان ٨/ ١٩٦٣/ ١٣٧)؛ والظاهر أن ١٩ نقلا في كتاب الأفعال لابن الحداد ترجع إليه (انظر الفهرس ٤، ٢/ ٥٤٢).
_________
(١١٨) وعلى ما ذكر في تعريف بمجلة المجمع العلمى العربي بدمشق ٣/ ١٩٢٣/ ٣٤٠ عدّ على سبيل الوهم بابا من «كتاب التنبيهات على أغاليط الرواة» لعلى بن حمزة البصرى (Br.SI , (١٧٦ - ١٧٧','1','242',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (239,'نشره عبد الستار أحمد فراج على مخطوطة التيمورية، الكويت ١٩٦٥.
٢ - «كتاب الفرق»، ذكره ابن النديم، والقفطى، وياقوت، انظر أيضا فهرست ابن خير ٣٨٢، وهو مثل «خلق الانسان» من مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في شرحه على «كتاب الفصيح» لثعلب (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣).
ويوجد مخطوطا في القرويين بفاس ٥٣٩/ ٢ (٧٨ أ- ١٠٩ أ، نسخت سنة ٦٠٠ هـ، راجع؛ (Li stedesmanuscrits ٥١ ,NO.٢١٨ واعتمادا على هذه المخطوطة نشره محمد الفاسي، الرباط ١٩٧٤، انظر في هذه النشرة إبراهيم السامرائي في: مجلة معهد المخطوطات ٢٢/ ١٩٧٦/ ١٤٧ - ١٦١، وفي القرويين مخطوطة أخرى ٨٣٤ (٣٨ ورقة، من القرن السادس، ومعه كتاب «خلق الإنسان»)، انظر محمود محمد الطناحى في: مجلة المجمع العلمى العربي بدمشق ٥١/ ١٩٧٦/ ٣٥٩ - ٣٨٦ مع قراءات مختلفة عن النص المطبوع.
٣ - «كتاب الوحوش»، ذكره ابن النديم، والقفطى، وياقوت.
٤ - «كتاب خلق الفرس»، ذكره ابن النديم، وياقوت، انظر كشف الظنون ٧٢٣.
٥ - «كتاب الزجر والدعاء»، ذكره ابن النديم، والقفطى، وياقوت.
٦ - «كتاب لحن (العامة)»، نقل عنه الفهرى في «تحفة المجد الصريح»، مخطوطة القاهرة (انظر ص ١٤٤ بعد) ص ٢٤، ٢٥، ٥٢، ١٢٢، ١٢٤، ١٣٥.

<span data-type="title" id=toc-261>وفي النحو والصرف:</span>
١ - «كتاب مختصر العربية»، ذكره ابن النديم، وياقوت، والقفطى بين مصنفاته.
٢ - «كتاب فعل وأفعل»، نقل عنه الفهرى في المرجع المذكور، ص ٣٦، ١٤٢ (واسمه هنا: «كتاب الأفعال»).','1','243',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (240,'<span data-type="title" id=toc-262>وفي لغة القرآن:</span>
١ - «كتاب القراءات»، ذكره التنوخى في تاريخ العلماء، ورقة ٨٧ أ، ضمن مصنفاته.
٢ - «كتاب في الناسخ والمنسوخ»، انظر المصدر نفسه.
٣ - «كتاب تفسير غريب القرآن»، المصدر نفسه.
وله «كتاب في الشعر» ذكره التنوخى، الموضع المذكور، بين مصنفاته./

<span data-type="title" id=toc-263>الأحول</span>
هو أبو العباس محمد بن الحسن بن دينار الأحول. كان لغويا، من أكابر العلماء بالشعر العربي القديم (انظر فصل الأدب) (١١٩). وكان يورّق لحنين بن إسحاق. روى عن محمد بن زياد بن الأعرابي، وروى عنه نفطويه (المتوفى سنة ٣٢٣/ ٩٣٥، انظر ص ١٤٩ بعد). كان معاصرا للمبرد وثعلب ويكبرهما سنا. ولم تتعين سنة وفاته، إلا أنه من الجائز أن نجعلها في الربع الأخير من القرن الثالث/ التاسع.
ابن النديم ٧٩؛ «المقتبس» للمرزبانى ٣٣٧؛ «تاريخ بغداد» ٢/ ١٨٥؛ «إنباه الرواة» للقفطى ٣/ ٩١ - ٩٢؛ «إرشاد الأريب» لياقوت ١ ٦/ ٤٨٢ - ٤٨٣، ٢ ١٣/ ١٢٥ - ١٢٦؛ «الوافي بالوفيات» للصفدى ٢/ ٣٤٤ - ٣٤٥؛ «بغية الوعاة» للسيوطى ٣٣؛ «هدية العارفين» ٢/ ١٦.- «معجم المؤلفين» لكحالة ٩/ ١٩١.

<span data-type="title" id=toc-264>ومن كتبه في اللغة:</span>
١ - «كتاب الأمثال»، ذكره السيوطى؛ وفضلا عن ذلك روى كتب «الأمثال» للمفضل الضبى، وأبي عبيدة، وأبي عبيد (انظر» -٢ (Sellheim ,Sprichworter ٨٤. كتاب ما اتفق لفظه واختلف معناه»، ذكرته جميع القوائم.
_________
(١١٩) نسب إليه القفطى فضلا عن ذلك «كتاب علوم الأوائل»، ولعله خطأ مردّه إلى قول المرزبانى (أورده ياقوت في إرشاد الأريب ٦/ ٤٤٢): «كان ورّاقا يورّق لحنين بن إسحاق المتطبب فى منقولاته لعلوم الأوائل.','1','244',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (241,'٣ - «كتاب الأشباه»، ورد ذكره في الفهرست لابن النديم، وفي ثلاث قوائم أخرى، انظر أيضا تاريخ بغداد ٢/ ١٨٥.
٤ - «كتاب فعل وأفعل»، ذكر في جميع القوائم وفي كشف الظنون ١٤٤٧.
٥ - «كتاب السلاح»، ذكره ابن النديم وغيره.
٦ - «كتاب الدواهى»، مذكور في كل القوائم، وكذلك «تاريخ بغداد» ٢/ ١٨٥. ومنه مختارات ملحقة بكتاب «الدواهى» لأبي عبيدة، مخطوطة طوب قابو سراى، كغشلار ١٠٩٦/ ٨ (٧٦ أ، نسخت سنة ٧٠٧ هـ، راجع شيشين، نوادر ١/ ٢٦١).
٧ - «كتاب الآباء والأمهات والأبناء والبنات»، انظر نقولا عنه في «المزهر» للسيوطى ١/ ٥٠٦، ٥٠٧ - ٥٠٨، ٥١٣ - ٥١٦، ٢/ ١٥٧.

<span data-type="title" id=toc-265>صعوداء</span>
هو أبو سعيد محمد بن هبيرة الأسدى، من أهل سامراء، قدم بغداد واتصل بعبد الله بن المعتز، وعمل له رسالة لخص فيها ما وجّه إلى أبي عبيد بن سلام. ويحتمل أن وفاته كانت نحو سنة ٢٨٠/ ٨٩٣./ ابن النديم ٧٤؛ «تاريخ بغداد» ٣/ ٣٧٠ - ٣٧١؛ «إرشاد الأريب» لياقوت ٧/ ١٣٣؛ «انباه الرواة» للقفطى ٢/ ٨٥؛ «الوافي بالوفيات» للصفدى ٥/ ١٦٠؛ «بغية الوعاة» للسيوطى»؛ Flugel ,Gramm.Schulen ١٦٤ - .١١٠ معجم المؤلفين» لكحالة ١٢/ ٩١.
وله «رسالة فيما أنكرته العرب على أبي عبيد القاسم بن سلام وما وافقته فيه»، ذكرها ابن النديم وغيره.
<span data-type="title" id=toc-266>المفضّل بن سلمة</span>

هو أبو طالب المفضل بن سلمة بن عاصم، أخذ عن أبيه سلمة بن عاصم، وابن الأعرابي، وابن السكيت، وعمر بن شبة، وثعلب الذي كان يكبره ببعض السنين.','1','245',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (242,'وتلميذه المعروف هو أبو بكر الصولى. وكان من أصحاب نعمته وأصدقائه الوزيران الفتح بن خاقان وإسماعيل بن بلبل.
وكان ما يحسنه هو اللغة. وكان بين مؤلفاته الكثيرة ردّ على كتاب «العين»، اختلفت الآراء في صحته.
وكان فضلا عن ذلك مفسرا للقرآن، ونحويا (انظر فصل النحو)، وأديبا. وصنف أيضا كتابا في الموسيقى، ونظم الشعر. توفي نحو سنة ٢٩٠/ ٩٠٣.
«مراتب النحويين» لأبي الطيب ٩٧؛ «المقتبس» للمرزبانى ٣٣٩، و «معجم الشعراء» له ٣٨٤؛ ابن النديم ٧٣ - ٧٤؛ «نزهة الألباء» للانبارى ٢ ١٣٨ - ١٣٩؛ «تاريخ بغداد» ١٣/ ١٢٤ - ١٢٥؛ «إرشاد الأريب» لياقوت ١ ٧/ ١٧٠، ٢ ١٩/ ١٦٣؛ «إنباه الرواة» للقفطى ٣/ ٣٠٥ - ٣١١؛ مخطوط في طبقاتاللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٢٦ أ؛ ابن قاضى شهبة، المخطوط ص ٥٠١ - ٥٠٢؛ «بغية الوعاة» للسيوطى ٣٩٦؛ و «المزهر» له ٢/ ٤١٣.-
Flugel, Gramm. Schulen ١٦٢ - ١٦٤; Br. GI, ١٨١; Rescher, Abri BII, ١٨٢ - ١٨٣
؛ «الأعلام» للزركلى ٨/ ٢٠٣؛ «معجم المؤلفين» لكحالة ١٢/ ٣١٤ رمضان عبد التواب، في مقدمته لكتاب «المذكر والمؤنث».
وسنذكر ما لم يصل إلينا من مصنفاته استنادا إلى قوائم كتبه الواردة عند ابن النديم ٧٣ - ٧٤ (طهران ص ٨٠؛ الترجمة الانجليزية ١٦١ - ١٦٢)، «نزهة الألباء» للانبارى ١٣٨ - ١٣٩؛ ابن خلكان ١/ ٥٨٣؛ إنباه الرواة للقفطى ٣/ ٣٠٦؛ إرشاد الأريب لياقوت ٧/ ١٧٠؛ بغية الوعاة للسيوطى ٣٩٦.

<span data-type="title" id=toc-267>فمن كتبه في الأمثال، واللغة، والرد على المتقدمين:</span>
١ - «كتاب الفاخر» (١٢٠)، وهو مجموعة من العبارات السائرة والأمثال والأقوال
_________
(١٢٠) مع العنوان الفرعى المضلّل: «فيما يلحن فيه العامة»، أو «فى لحن العامة» وما شاكل ذلك (ابن النديم؛ الأنبارى؛ القفطى؛ ياقوت؛ السيوطى)، بل إن اسم الكتاب في مخطوطة صائب هو: «كتاب ما يلحن فيه العامة»، وهو خطأ أدّى إليه ما بأوّل الكتاب (انظر رمضان عبد التواب في: مجلة معهد المخطوطات ١٧/ ١٩٧١/ ٢٩٤ - ٢٩٥).','1','246',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (243,'المأثورة، الخ، لا خطة لها أو ترتيب، ويعتمد على مجموعات أخرى متقدمة، ويستند إلى شروح محمد بن حبيب، وكثرت الإفادة منه فيما بعد (انظر، Sellheim , (Sprichworter ١١٤ - ١٢١ وممن نقل عنه الميدانى في «مجمع الأمثال» (انظر ع.
التركيتى في: المورد ٣، ٢/ ١٩٧٣/ ٢٩ - ٣٠) والبغدادى في «خزانة الأدب» (انظر إقليد الخزانة للميمنى ٧٩).

<span data-type="title" id=toc-268>المخطوطات:</span>
نور عثمانية ٤١٣٩ (١٢٩ ورقة، من القرن السادس/ أو السابع للهجرة، انظر، (O.Rescherin: ZDMG ٦٤ /١٩١٠ /٤٩٩ - ٥٠٠ فاتح ٤٠٠٩ (١٤٠ ورقة، من القرن ١١ للهجرة، انظر فهرس المخطوطات ١/ ٥٠٤، راجع، O.Rescherin: MFO ٥ / (١٩١٢ /٥٠٤ صائب بأنقره ١٤٣٧/ ٤ (٣٣ ورقة، من القرن السادس للهجرة)، كيمبردج، إضافات ٢٩٣٠ (١٣٣ ورقة، من القرن الرابع للهجرة، انظر براون ١٧٣، رقم ٩١٦)، الظاهرية، عام ٧٩٣٦ (٩٨ ورقة، نسخت سنة ١٣٠٦ هـ، انظر فهرس اللغة ١١٨ - ١١٩)، الزيتونة بتونس ٢٣٦٢ (١٦٥ ورقة، نسخت سنة ٥٦٤ هـ).
ونشره ستورى C.A.Storey على مخطوطتى كمبردج ونور عثمانية، في ليدن ١٩١٥.
ونشره عبد العليم الطحاوى ومحمد على النجار، القاهرة ١٩٦٠.
والخمس الأول منه موجود باسم «غاية الأرب في معانى ما يجرى على ألسن العامة ... » في مخطوطة أسعد ٣٥٩٠/ ٦ (١٣٣ ب- ١٥٢ أ، من القرن ١٢ هـ)؛ وطبع ضمن «خمس رسائل»، استنبول ١٣٠١، ص ٢٣٢ - ٢٦٤، والقاهرة ١٩٢٧، وهو موافق لطبعة ستورى ص ١ - ٥٢.
وقد اختصره وشرحه محمد ابن الحسن بن محمد بن الحس (في كتاب سماه «المختصر من الفاخر في الأمثال السائرة»)، ويوجد مخطوطا في مكتبة جامعة استنبول ٥٠) ٦٥٦ A.Y. ورقة، من القرن السابع للهجرة، ولعلها بخط المؤلف، راجع فهرس المخطوطات ١/ ٥٢٤).
٢ «كتاب البارع (في علم اللغة)»، ذكرته جميع قوائم مصنفاته. وقيل إن','1','247',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (244,'المفضل لم يتم من هذا الكتاب، وهو آخر ما صنّف، إلا حروف الهمزة والهاء والعين والحاء والغين والخاء.
ويبدو أنه قد وقع خلط عند القفطى (إنباه الرواة ٣/ ٩٧، ٣٠٦، ٤/ ٦٢) بين اسم هذا الكتاب والكتاب التالى ذكره. وقد حصل له من أحد هذين الكتابين عدة مجلدات، عليها تعليقات بخط ابن السيرافي. وكان الكتاب من مصادر الصغانى في «التكملة» (انظر ١/ ٨) و «العباب» (انظر ١/ ٣٠) ونقل عنه الأزهرى، غالبا بطريق المناذرى ومسميا المؤلف «أبو طالب النحوى»، في «التهذيب» ٢/ ١٨٥، ٢٣٨، ٢٦٠، ٢٦٨، ٣/ ١٩٦، ١٩٧، ٢٥٥، ٣٨٨، ٥/ ١٩٦، ٢٠٧، ٦/ ١٧٥، ٧/ ١٠٦، ٨/ ٢٧٦، ٢٧٧، ٩/ ٤٠٥، ١٠/ ٢٨١، ٦٤٥، ١١/ ١٠٧، ٣٧٧، ١٢/ ٥٢، ١٦٨، ٤٤٠، ١٤/ ٤٥، ٤٢٢، ١٥/ ١٧٨، ٥٧٨، ٥٨٩، ٥٩٤، ٦٧٣.
٣ - «الرد على الخليل وإصلاح ما في كتاب العين من الغلط والمحال والتصحيف» ويسمى أيضا «الاستدراك على الخليل في كتاب العين وإصلاح ما فيه من الغلط والتصحيف»، ذكرته جميع القوائم.
وخلطه القفطى لعلة لا نتبينها بالكتاب السالف الذكر.
انظر نقولا عنه في «المزهر» للسيوطى ١/ ٩٠ (وراجع ص ٨٦ - ٨٧ منه)، ونقل عنه باسم «إخراج ما في كتاب العين من الغلط» و «الرد على صاحب العين» في تاج العروس ٢ ٦/ ٥١٤ (شيح)، ١٩/ ٤٧٠ (عذط). وهو أيضا من مصادر الصغاتى (انظر آنفا).
وقد ألف ابن دريد (المتوفى سنة ٣٢١/ ٩٣٣) (كتاب التوسط)، وتوسط فيه بين الخليل والمفضل، ولم يصل إلينا، انظر ص ١٠٣ آنفا.
كذلك ألف أبو عبد الله نفطويه (المتوفي سنة ٣٢٣/ ٩٣٥) كتابا سماه (الرد علي المفضل في نقضه على الخليل) ولم يصل إلينا، انظر ص ١٥٠ بعد.
٤ - «كتاب الاشتقاق»، مذكور في كل قوائم مصنفاته، راجع «المزهر» للسيوطى ١/ ٣٥١.','1','248',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (245,'<span data-type="title" id=toc-269>وله تآليف مفردة في موضوعات شتى منها:</span>
١ - «كتاب خلق الانسان»، ذكر في الفهرست لابن النديم، وفي ثلاث قوائم أخرى.
٢ - «كتاب العطر وأجناسه» أو «كتاب الطّيب»، ذكره ابن النديم (انظر أيضا ص ٣١٧)، وياقوت، انظر نقولا عنه في «درة الغواص» للحريرى ٣٥؛ و «خزانة الأدب» ٣/ ٣٤٣.
٣ - «كتاب الزرع والنبات والنخل وأنواع الشجرة» كذا ذكره ابن/ النديم، وابن خلكان، وياقوت؛ واسمه عند القفطى: «كتاب البلاد والزرع والنبات».
٤ - «كتاب الأنواء والبوارح»، ذكره ابن النديم، وياقوت، ولم يصل إلينا، راجع تاريخ التراث العربي ٧/ ٣٥٠.

<span data-type="title" id=toc-270>وله في لغة القرآن:</span>
١ - «ضياء القلوب (في معانى القرآن وغريبه ومشكله)»، في نيف وعشرين جزءا (انظر ابن النديم ٣٤، ٧٣)، ذكرته أربع قوائم، راجع فهرست ابن خير ٥٧؛ «تاريخ بغداد» ١٣/ ١٢٤.
٢ - «كتاب معانى القرآن»، وهو مستقل عن الكتاب السالف الذكر، مذكور عند ابن النديم ٣٤، ٧٣، وفي ثلاث قوائم أخرى.
٣ - «جلاء الشّبه (في الرد على المشبهة)»، وهو تفسير فقهى لألفاظ وآيات من القرآن، وردّ به على المشبّهة، ذكره ابن النديم وآخرون بين مصنفاته. ويوجد مخطوطا في داماد إبراهيم ٢٧٢/ ٣ (٩٧ أ- ١٢٤ أ، نسخت سنة ١١٠٨ هـ).

<span data-type="title" id=toc-271>ثعلب</span>
هو أبو العباس أحمد بن يحيى بن زيد (ولد سنة ٢٠٠/ ٨١٥، وتوفي سنة ٢٩١/ ٩٠٤)، وكان يتعصب لمدرسة الكوفة، ومنافسا للمبرد. وفضلا عن مصنفاته في النحو (انظر فصل النحو) ألّف كتبا كثيرة في اللغة وتاريخ الأدب (انظر فصل الأدب).','1','249',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (246,'انظر في قوائم مصنفاته في ابن النديم ٧٤ (طهران، ص ٨١؛ الترجمة الانجليزية ١٦٣ - ١٦٤)؛ ابن خلكان ١/ ٣٧؛ «إنباه الرواة» للقفطى ١/ ١٥٠ - ١٥١؛ «إرشاد الأريب» لياقوت ٢/ ١٥٢ - ١٥٣؛ «بغية الوعاة» للسيوطى ١٧٣.
وترد أيضا شروح لثعلب على مسائل لغوية في «مجالس العلماء» للزجاجى ١١٦ - ١١٨، ١١٩ - ١٢٦، ٢٧٦ - ٢٨١، ٣٢٥ - ٣٣٠، انظر أيضا تاريخ التراث العربي ٧/ ٣٥٤.

١ - «كتاب الفصيح»، وجاء اسمه في بعض<span data-type="title" id=toc-272> المخطوطات </span>وفي فهرست ابن خير (٣٣٦ - ٣٣٩): «اختيار فصيح الكلام».
وهو أحد المصنفات المعتبرة في علم اللغة المتقدم، وقد رمى المؤلف مرارا بانتحاله، وأن مما انتحله فيه «كتاب الحلى» لأبي على الحسن بن داودالرقي (العقد الأول من القرن ٣/ ٩، انظر «إرشاد الأريب» لياقوت ٢/ ١٥٣، و ٣/ ٦٩؛ «بغية الوعاة» للسيوطى ١٧٣).
المخطوطات:
فاتح ٥٤١٣ (١٤ أ- ٣٩ أ، نسخت سنة ٥٢٠ هـ، راجع فهرس المخطوطات ١/ ٣٦٣)، بشير أغا ١٩٣/ ٣ (٤٨ أ- ٩٥ ب، نسخت سنة ٦٢٠ هـ، انظر الفهرس نفسه)، قاصد جى زاده ٦٧٧/ ١ (الأوراق ١ - ١٧ أ، نسخت سنة ٩٧٠ هـ)، أسعد ٣١٤٤/ ١ (الأوراق ١ - ٣٩، من القرن الخامس للهجرة)، و ٣٢٤٦/ ١ (نسخت سنة ٧٩٩ هـ) سراى أحمد الثالث، ٢٧٣٧/ ٣ (١٤١ ب- ١٦٩ ب، نسخت سنة ٦٧٣ هـ، راجع فهرس المخطوطات ١/ ٣٦٣)، و ٢٧٧٤ (٥٧ ورقة، انظر الفهرس ٤/ ٦)، لا له لى ٣٥٨٠/ ١ (الأواق ١ - ٣٢ ب، نسخت سنة ١١٥٥ هـ)، و ٣٥٨١ (٤٨ ورقة، من القرن السادس للهجرة)، با يزيد ٦٨٧٨ (٢٧ ورقة، من القرن السابع للهجرة)، عاطف ٢٧٦١/ ٢ (الأوراق ٢٩٠ - ٣٠٧)، مكتب جامعة استنبول ١٦٠) ١٢٦٤ A.Y. ورقة، من القرن ١١ هـ)، و ٨) ٣٩٢٩ A.Y. ورقات، نسخت سنة ١٠٠٣، مختارات؟ )، رشيد أفندى بقيصريه ١٠٢٦ (٦٧ ورقة، من القرن السادس للهجرة، انظر، (Li stedesmanuscrits ٢ ,No.١٩ جوروم ١٩١٣/ ٢ (٣٢ أ- ٦٠ ب، من القرن الثامن للهجرة، راجع أحمد آتش','1','250',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (247,'في، (Isl.Ilim.Enst.Derg.١ /١٩٥٩ /٥٨ - ٥٩ يوسف أغا بقونيه ٥٠٠٨ (٤٤ ورقة، نسخت قبل سنة ٣٢٥ هـ، راجع، Li stedesmanuscrits ٢٠ ,No.٩٧ وأحمد آتش في:
(Belleten ١٦/ ١٩٥٢/ ٦٢
، كلية الآداب ببغداد، سابقا/ معهد الدراسات الإسلامية (نسخت سنة ٥٤٤ هـ، انظر القائمة المطبوعة على الآلة الكاتبة ٢٠، رقم ١٧٣)، مكتبة الصدر الخاصة بالكاظمية (انظر مجلة معهد المخطوطات ٤/ ١٩٥٨/ ٢٤٠، رقم ٢٢)، البلدية بالإسكندرية ٣٨١٢ ج/ ٤ (جزء منه، نسخ سنة ١٠٠٠ هـ، انظر فهرس الفنون المنوعة ص ٨٩)، عارف حكمت بالمدينة، لغة ٣٢ (٦٠ ورقة، نسخت سنة ١١١١ هـ، انظر المنتخب لكحالة ٤٧)، المكتبة العامة بالرباط، الحمزاوية ٢٣١ (نسخت سنة ٦٢٤ هـ، ناقصة)، الاسكوريال ١٧٩١/ ٤ (٧٩ ب- ٩٧)، الفاتيكان ١١٧٧/ ٥ (الأوراق ١٠٩ - ١٢٤ ب، القرن التاسع للهجرة، انظر فيدا ١/ ١٧٨ - ١٧٩)، ليدن ٥٤) ٦٤٩ OR. ورقة، نسخت سنة ٨٤٢ هـ، انظر فورهوف ٧٧)، برلين، ٤/ ٤٧ We.) ٦٩٣١ الأوراق ٨٨ - ١١٦، نسخت سنة ٦٣٦)، و ٦٩٣٢/ ١ و ٢، كيمبردج ٤٣) ٨/ ١١٩٢ OR.، ورقة، نسخت سنة ٥٧٣ هـ، انظر آربرى رقم ١٠١)، تشستربيتى ٣٩٩٩/ ١ (الأوراق ١ - ٢٨، نسخت سنة ٦٢٠ هـ، نسخها ياقوت الرومى، انظر في هذه المخطوطة
A. J. Arberry, Avolume intheautographeo f Yaqutthegeograph er .. , London ١٩٥١, Ch. Beatty Mono- Grahps ٣
و٥١٩٧ (٦٠ ورقة، نسخت سنة ٥٧٣ هـ)، برنستون، جاريت ٦٢) L ٤٣ ورقة، من القرن الثامن للهجرة، انظر الفهرس، رقم ٢٤٨).
ونشره يعقوب بارت في ليبتسج ١٨٧٦، على مخطوطات روما وليدن وبرلين، انظر طبعات أخرى فيما يلى ذكره من الشروح.

شروح (١٢١) وزيادات وانتقادات على كتاب الفصيح:
١ - «مؤاخذات على الفصيح» أو «الرد على ثعلب في الفصيح» للزجاج (المتوفى سنة ٣١١/ ٩٢٣)، وقد وصل إلينا مختصر منه، انظر ص ٩٩ آنفا. وانتصر لأبي
_________
(١٢١) نسب في كشف الظنون ١٢٧٢ خطأ شرح للمبرد (المتوفى سنة ٢٨٥/ ٨٩٨ هـ).','1','251',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (248,'العباس ثعلب فيما تتبعه عليه أبو اسحاق الزجاج ابن خالويه (المتوفي سنة ٣٧٠/ ٩٨٠)، وهو غير شرحه الذي وصل إلينا (انظر رقم ٦ فيما يلى)، راجع ص ١٧٩ بعد.
٢ - «زيادات الفصيح» لمحمد بن عثمان الجعد الشيبانى (المتوفى نحو سنة ٣٢٠/ ٩٣٢)، وقد وصل إلينا، انظر ص ١٧٥ بعد.
٣ - «فائت الفصيح»، زيادات لأبي عمر الزاهد (المتوفى سنة ٣٤٥/ ٩٥٦)؛ تلميذ ثعلب، وقد وصل إلينا، انظر ص ١٥٦ بعد.
٤ - «شرح الفصيح» وهو كذلك لأبي عمر الزاهد، ولم يصل إلينا، انظر ص ١٥٦ بعد.
٥ - «تصحيح الفصيح» لابن درستويه (المتوفى سنة ٣٤٧/ ٩٥٨)، وقد وصل إلينا ونشر، انظر ص ١٠٨ آنفا.
٦ - شرح لابن خالويه (المتوفي سنة ٣٧٠/ ٩٨٠)، وقد وصل إلينا، انظر ص ١٧٩ بعد.
٧ - «كتاب التنبيهات على أغاليط الرواة» لعلى بن حمزة البصرى (المتوفي سنة ٣٧٥/ ٩٨٥)، وفي الباب الخامس منه نقد كتاب ثعلب، وقد وصل إلينا، انظر ص ١١٠ آنفا، وراجع -٨ R.Bellin: JRAS ١٩٠٤ ,S.٩٥ - ١١٨. شرح لابن جنى (المتوفي سنة ٣٩٢/ ١٠٠١)، ولم يصل إلينا، انظر فصل النحو.
٩ - «كتاب تمام فصيح الكلام» لأحمد بن فارس (المتوفي سنة ٣٩٥/ ١٠٠٥)، وقد وصل إلينا ونشر، انظر ص ٢١٢ بعد.
١٠ - «الانتصار لثعلب»، وهو كذلك لابن فارس، ولم يصل إلينا، انظر ص ٢١٢ بعد.
١١ - شرح لأبي هلال العسكرى (المتوفي بعد سنة ٣٩٥/ ١٠٠٥)، وقد وصل إلينا، انظر ص ١٨٥ بعد.
١٢ - شرح لمحمد بن عيسى العطار (المتوفي نحو سنة ٤٠٠/ ١٠١٠)، انظر في نقول عنه فصل النحو.','1','252',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (249,'١٣ - شرح ليوسف بن عبد الله الزّجاجى (المتوفي سنة ٤١٥/ ١٠٢٤)، ولم يصل إلينا، انظر ص ٢٢٨ بعد./ ١٤ - شرح لابن الجبّان، المعروف أيضا بأبي منصور اللغوى (المتوفي بعد سنة ٤١٦/ ١٠٢٥)، وقد وصل إلينا، انظر ص ٢٢٩ بعد.
١٥ - شرح لأبي على أحمد بن محمد بن الحسن المرزوقي (المتوفي سنة ٤٢١/ ١٠٣٠)، وقد وصل إلينا، انظر ص ٢٣٠ بعد.
١٦ - «تهذيب الفصيح» لأبي سهل محمد بن على الهروى (المتوفي سنة ٤٣٣/ ١٠٤١)، ولم يصل إلينا، انظر ص ٢٤٧ بعد.
١٧ - «إسفار كتاب الفصيح» وهو كذلك لأبي سهل الهروى، وقد وصل إلينا بخطه، انظر ص ٢٤٧ بعد.
١٨ - «التلويح في شرح الفصيح» لأبي سهل الهروى أيضا، وقد وصل إلينا، انظر ص ٢٤٧ بعد.
١٩ - شرح لمكيّ بن أبي طالب (المتوفي سنة ٤٣٧/ ١٠٤٥)، وقد وصلت إلينا نقول عنه، انظر ص ٢٥١.
٢٠ - «شرح الفصيح» لابن التيّانى (تمّام بن غالب بن عمر، المتوفي سنة ٤٣٦/ ١٠٤٤، انظر ص ٢٥٦ بعد)؛ نقل عنه في تاج العروس ٢ ١٠/ ١٥ (أثر).
٢١ - «ذيل فصيح الكلام»، ويسمّى أيضا «فصيح الكلام ... » لأبي الفوائد محمد ابن على الغزنوى (ألفه سنة ٤٤٢/ ١٠٥٠)، زاد عليه ١٠٠٠ كلمة مرتبة على حروف المعجم، المخطوطات: لا له لى ٣٦١٤/ ٢ (الأوراق ١ - ٥٨، نسخت سنة ٥٨٣ هـ، راجع، (O.Rescherin: MO ٧ /١٩١٣ /١٠٥ بشير أغا (أيوب) ١٩٣/ ٣ (٤٨ أ- ٩٥ ب، نسخت سنة ٦٢٠ هـ، راجع فهرس المخطوطات ١/ ٣٦٣).
٢٢ - شرح لأبي على الحسن بن أحمد الاستراباذى (المتوفي قبل سنة ٤٦٤/ ١٠٧٢)، ذكره ياقوت في إرشاد الأريب ٣/ ٢٦؛ السيوطى في بغية الوعاة ٢١٨؛ كشف الظنون ١٢٧٣، وكان موجودا في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في. (MIE ٤٩ /١٩٤٦ /٣١ ,No.٥٥٨:
٢٣ - شرح لأبي القاسم عبد الله بن محمد بن الحسن بن ناقياء اللغوى (المتوفي سنة','1','253',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (250,'٤٨٥/ ١٠٩٢، انظر معجم المؤلفين لكحالة ٦/ ١١٦)، انظر «إنباه الرواة» للقفطى ٢/ ١٥٦؛ «بغية الوعاة» للسيوطى ٢٩٢، ويوجد مخطوطا بالموصل، الحجيّات، ولعله الآن بمكتبة الأوقاف (٤٨ ورقة، انظر فهرس چلبى ١١٥، رقم ٢٣١). وكتب فيه عبد الوهاب محمد على العدوانى رسالة ماجستير، القاهرة ١٩٧٣ (انظر المورد ٧، ١/ ١٩٧٨/ ٢٦٤).
٢٤ - شرح لمجمّع بن محمّد بن أحمد المسكنى النحوى (من أهل القرن ٥/ ١١، انظر الأعلام للزركلى ١٣/ ٤١٦)، مذكور في الذريعة ١٣/ ٣٨٦.
٢٥ - شرح لابن السيد البطليوسى (المتوفى سنة ٥٢١/ ١١٢٧)، وورد عنه ١١ نقل في المزهر للسيوطى (انظر الفهرس ٢/ ٦٤٤).
٢٦ - شرح لجار الله محمود بن عمر الزمخشرى (المتوفى سنة ٥٣٨/ ١١٤٤)، نقل عنه الفهرى في تحفة المجد الصريح، مخطوطة القاهرة ص ٩، ١٤، ٢٣، ٢٦، ٣٤، ٤٢، ٤٣، ٤٧، ٥٢، ٥٣، ٥٤، ٥٩، ٧١، ٨٥، ١٠٥، ١٣٩، ١٥٣، ١٦٤، ١٦٥.
٢٧ - «شرح غريب الفصيح» لأحمد بن عبد الجليل بن عبد الله التّدميرى (المتوفى سنة ٥٥٥/ ١١٦٠، انظر «معجم المؤلفين» لكحالة ١/ ٢٦٠)، وذكراسم الكتاب في «بغية الوعاة» للسيوطى ١٣٨، انظر «كشف الظنون» ١٢٧٣، ونقل عنه في تاج العروس ٢ ١٠/ ٣٥٦، ٣٥٧ (جبر) ونقل عنه الفهرى من نسخة بخط المؤلف، في تحفة المجد الصريح، مخطوطة القاهرة ص ١١، ١٢، ٦٠، ٨٠ وفي مواضع أخرى كثيرة؛ ويوجد مخطوطا في نور عثمانية ٣٩٩٢ (٩٧ ورقة، من القرن السابع للهجرة).
٢٨ - شرح لأبي حفص عمر بن محمد بن أحمد القضاعى (المتوفى نحو سنة ٥٧٠/ ١١٧٤)، وهو مذكور في بغية الوعاة للسيوطى ٣٦٣؛ وكشف الظنون ١٢٧٣.
٢٩ - شرح لمحمد بن أحمد بن هشام السبتى اللخمى (كان حيا سنة ٥٥٧/ ١١٦٢)، ونقل عنه في خزانة الأدب (انظر إقليد الخزانة للميمنى ٨١) ونقل عنه الفهرى من نسخة بخط المؤلف، في الكتاب المذكور آنفا ص ٤١ وربما أيضا في ص ٥١، ١٣٤، ١٤٣، ١٤٩، ١٦٥، ١٦٦.','1','254',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (251,'<span data-type="title" id=toc-274>المخطوطات:</span>
المكتبة العامة بالرباط، مجموعة الكتانى ٣٥٩/ ٢ (ص ٣٣ - ٢٤٢، نسخت سنة ٦١٦ هـ)، الخزانة الملكية بالرباط ١٩٤٤ (١٠٠ ورقة، نسخة حديثة)، ومنها مصورة بمعهد المخطوطات بالقاهرة، دار الكتب الوطنية بتونس، الأحمدية ٣٩٦٥/ ٤ (الأوراق ٣٧ - ٨٥، انظر فهرس منصور ١٤٠).
٣٠ - شرح لأبي بكر محمد بن خلف بن عبد الله بن صاف (المتوفى نحو سنة ٥٨٥/ ١١٩٠)، ذكره الصفدى (الوافي بالوفيات ٣/ ٤٦)، وممن نقل عنه الفهرى (من الطبقة الثانية من تلاميذه) في تحفة المجد الصريح ص ٤٨.
٣١ - شرح لأحمد بن على بن هبة الله بن المأمون (المتوفى سنة ٥٨٦/ ١١٩٠)، ذكر في إرشاد الأريب لياقوت ٢/ ٥٥؛ الوافي بالوفيات للصفدى ٧/ ٢١٣؛ بغية الوعاة للسيوطى ١٥١، وراجع كشف الظنون ١٢٧٣.
٣٢ - شرح لأبي البقاء عبد الله بن الحسين بن عبد الله العكبرى (المتوفى سنة ٦١٦/ ١٢١٩)، ذكره الصفدى (انظر إنباه الرواة للقفطى ٢/ ١١٧ الهامش).
٣٣ - شرح لأبي بكر محمد بن طلحة بن محمد الإشبيلى (المتوفى سنة ٦١٨/ ١٢٢١)، نقل عنه الفهرى في تحفة المجد الصريح ص ٢٠، ٧٣، ٨١ - ٨٢، ١٠٨، ١٢٥، ١٤٣، راجع مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٤، سطر ٤.
٣٤ - شرح لابن الدهان اللغوى، نقل عنه الفهرى أيضا ص ٩، ١١٢.
٣٥ - «ذيل الفصيح»، زيادات لعبد اللطيف بن يوسف البغدادى (المتوفى سنة ٦٢٩/ ١٢٣١)، انظر «الوافي بالوفيات» للصفدى (عن إنباه الرواة للقفطى ٢/ ١٩٤ الهامش)؛ «خزانة الأدب» ١/ ١١؛ «كشف الظنون» ١٢٧٤؛ وطبع على مخطوطة في القاهرة (؟ ) /، القاهرة ١٢٨٥ (في ذيل شرح الهروى).
ونشره محمد بدر الدين أبو فراس النعسانى ومحمد أمين الخانجى في: «الطرفة الأدبية»، القاهرة ١٩٠٧، ص ٩٩ - ١٢٨، ونشره أيضا محمد عبد المنعم خفاجى في:
«فصيح ثعلب. والشروح التى عليه»، القاهرة ١٩٤٩ (كذلك في ذيل شرح الهروى).','1','255',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (252,'٣٦ - «تحفة المجد الصريح في شرح كتاب الفصيح» لأبي جعفر أحمد بن يوسف ابن على الفهرى الليلى (المتوفى سنة ٦٩١/ ١٢٩٢، انظر «معجم المؤلفين» لكحالة ٢/ ٢١٢ - ٢١٣)، وهو أحد شرحين قيل إنه كتبهما (انظر بغية الوعاة للسيوطى ١٧٦)، وقد أثنوا عليه كثيرا (انظر كشف الظنون ١٢٧٣)؛ وذكرت في مقدمته طائفة من المصادر الطريفة المفقودة اليوم. ونقل عنه في «خزانة الأدب» (انظر إقليد الخزانة للميمني ٨١)، وكثيرا أيضا في «تاج العروس» ٢، من ذلك مثلا ١/ ٤١٧ (لبأ)، ٤٦٩ (نكأ)، ٤/ ١١٧ (كذب)، ١٦٩ (كلب)، ٢٦١، ٢٦٢ (نسب)، ٤٥٣ (بهت)، ٥٧٧ (شتت)، ٧/ ١٧٤ (نصح)، ١٠/ ١٥ (أثر). المخطوطات: المكتبة العامة بالرباط، الحمزاوية ١٣١ (الجزء الأول، انظر محمد المنونى في: تطوان ٨/ ١٩٦٣/ ١٣٩)، دار الكتب بالقاهرة، لغة ٢٠ ش (الجزء الأول، ٨٤ ورقة، راجع الفهرس الثانى ٢/ ٧).
ونشر عبد العزيز الميمنى مقدمته على مخطوطة القاهرة في: مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤١ - ٥٤٥، انظر أيضا محمد الطاهر بن عاشور في:
المجلة نفسها ٣٧/ ١٩٦٢/ ١٩٩ - ٢٠٦.
٣٧ - شرح لأبي على عبد الكريم بن الحسن بن الحسين بن علّان السكرى النحوى (القرن ٧/ ١٣؟ انظر الوافى بالوفيات للصفدى، عن فهارس جبرييلى
(Gabrieli, Indicein: Rend. Accad. Lincei, ser. V, ٢٢/ ١٩١٣/ ٥٧٤
، وورد ذكره في كشف الظنون ١٢٧٣.
٣٨ - شرح لأبي بكر محمد بن محمد بن إدريس القضاعى (المتوفى سنة ٧٠٧/ ١٣٠٧)، انظر الديباج المذهب لابن فرحون ٣٠٢، وراجع كشف الظنون ١٢٧٣.
٣٩ - شرح لتاج الدين أحمد بن عبد القادر بن أحمد بن مكتوم (المتوفى سنة ٧٤٩/ ١٣٤٨)، ذكره السيوطى في بغية الوعاة ١٤١، راجع كشف الظنون ١٢٧٣.
٤٠ - شرح لأبي القاسم عبد الله بن عبد الرحيم بن ثعلب الأصفهاني، ويوجد مخطوطا في رضا برامبور، لغة ٣٨ (انظر الفهرس ١/ ٥١٠، راجع عبد العزيز الميمنى في: مجلة المجمع العلمى العربي بدمشق ٩/ ١٩٢٩/ ٦١٥).
٤١ - «ترتيب فصيح اللغة العربية»، وهو ترتيب لمحتوى الكتاب على حروف','1','256',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (253,'المعجم صنعه أ. ح. ستّى (النصف الأول من القرن ١٤/ ٢٠)، ويوجد مخطوطا في دار الكتب بالقاهرة ٨٤١٩ هـ/ ١ (مكتوب بخط مرتّبه في سنة ١٣٢٣، انظر الملحق ١/ ١٥٢).

<span data-type="title" id=toc-275>مصنفات أخرى على كتاب الفصيح:</span>
١ - «كتاب خطبة الفصيح» لأبي العلاء أحمد بن عبد الله المعرى (المتوفى سنة ٤٤٩/ ١٠٥٧)، صاغ المحتوى اللغوى لكتاب الفصيح في نص يحمد به الله، قيل إن مقداره كان خمس عشرة كراسة، على ما جاء في قائمة بمصنفاته وضعها مجهول، ونقلها القفطى (إنباه الرواة ١/ ٥٩). وكان الكتاب معروفا في الأندلس (انظر فهرست ابن خير ٣٤٣). وأورد الكلاعى (انظر بعد) بدايته في «إحكام صنعة الكلام»، بيروت ١٩٦٦، ص ١٨٠ - ١٨١، راجع ص ٣٨، ٢٣١ منه.
وقد ألف على كتاب المعرى:
(أ) «تفسير خطبة الفصيح» أو «كتاب شرح لغة خطبة الفصيح»، وهو شرح للمعرى نفسه، انظر «إنباه الرواة» للقفطى ١/ ٥٩؛ الكلاعى، الموضع المذكور ص ٢٣١.
(ب) «خطبة الإصلاح» (أى إصلاح المنطق لابن السكيت) لأبي القاسم محمد بن عبد الغفور الكلاعى (النصف الأول من القرن ٦/ ١٢)، وهى معارضة للكتاب السالف الذكر، انظر كتابة «إحكام صنعة الكلام» ص ٢٨، ٣١.
(ج) «جهد النصيح في معارضة المعرى في خطبة الفصيح» لأبي الربيع سليمان بن موسى بن سالم الكلاعى (المتوفى سنة ٦٣٤/ ١٢٣٧)، انظر نفح الطيب، تحقيق إحسان عباس ٤/ ٤٧٥؛ الديباج المذهب لابن فرحون ١٢٢./ ٢ - «نظم الفصيح»، منظومه على بحر الرجز لعبد الحميد بن هبة الله بن محمد ابن أبي الحديد المدائنى (المتوفى سنة ٦٥٥/ ١٢٥٧).

<span data-type="title" id=toc-276>المخطوطات:</span>
الإسكوريال ١٨٨ (٣٤ ورقة، نسخت سنة ٧٠٩ هـ)، جوروم ١٩١٣/ ٣','1','257',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (254,'(٦٢ ب- ٨٨ ب، من القرن الثامن للهجرة، انظر أحمد آتش في Isl.Ilim. (Enst.Derg.١ /١٩٥٩ /٥٩: نشره م. بدوى المختون في مجلة معهد<span data-type="title" id=toc-277><span data-type="title" id=toc-278>المخطوطات </span></span>٢٥، / ١٩٧٩/ ٦١ - ١٦٧.
٣ - «نظم الفصيح»، منظومة لشهاب الدين محمد بن أحمد بن الخليل الخويّى (المتوفي سنة ٦٩٣/ ١٢٩٤)، ذكرها الصفدى في الوافي بالوفيات ٢/ ١٣٨؛ والكبتى في فوات الوفيات ٢/ ٣٦٨؛ راجع كشف الظنون ١٢٧٣.
٤ - «موطأة الفصيح»، منظومة لأبي الحكم مالك بن عبد الرحمن بن المرحّل الأنصارى (المتوفي سنة ٦٩٩/ ١٣٠٠) في ١٣٤٠ بيتا من الرجز، انظر «بغية الوعاة» للسيوطى ٣٨٤.
المخطوطات:
نور عثمانية ٤٤٧٥/ ١ (الأوراق ١ - ٢٥، نسخت في سنة ١١٢٤ هـ، ومعها شرح)، الظاهرية، عام ٧٦٢٥/ ١ (١ أ- ٣٣ أ، انظر فهرس اللغة ١٩٢ - ١٩٣)، دار الكتب بالقاهرة، لغة ٢٩٦ (نسخت سنة ١٢٧٤، انظر الفهرس الثانى ٢/ ٤٣)، ولغة ٧٨٠ (٣٤ ورقة، انظر الفهرس الثانى ٧/ ١٩)، و ٥٦٦٤ هـ (٤٢ ورقة، نسخت سنة ١٢٩١ هـ، انظر الملحق ٣/ ١٧٠)، دار الكتب الوطنية بتونس، الأحمدية ٣٩٦٥ (١٦ ورقة، انظر فهرس منصور ١٥٥)، المكتبة العامة بالرباط د ١٠٣٧ (٦١ ب- ١٠٧ أ)، ود ١٦٣٩ (١٦ أ- ٤٨ ب، انظر فهرس علوش ١/ ٣٦٨، رقم ١٤٧٩، ١٧٥٠)، الكتانى بالرباط ٤٩١/ ٧ (ص ٤٥٩ - ٥١٥)، ييل ٢٢) ٢٦٤ - L، ورقة، نسخت نحو سنة ١٢٠٠ هـ، انظر نيموى رقم ١٧٤).
وقد علّق عليه بكتاب «موطئة الفصيح لموطأة الفصيح»، وهو شرح لمحمد بن الطيب الفاسى (المتوفي سنة ١١٧٠/ ١٧٥٧، انظر م. العربي العزوزى في: مجلة المجمع العلمى العربي بدمشق ٣٠/ ١٩٥٥/ ٨٧ - ٩٠)، وهو أصلا في مجلدين.
المخطوطات:
دار الكتب بالقاهرة، لغة ١٧٩ (المجلد الأول، انظر الفهرس الثانى ٢/ ٤٢)،','1','258',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (255,'ولغة ١٥ ش (المجلد الأول، ٥٨ ورقة، من القرن ١٣ هـ، انظر فهرس<span data-type="title" id=toc-279> المخطوطات </span>١/ ٣٧٥)، و ٥٠١٠ هـ (٣٤٠ ورقة، نسخت سنة ١١٤٤ هـ، انظر الملحق ٣/ ١٤٣ - ١٤٤).
٥ - «حلية الفصيح نظم (ما قد جاء في) الفصيح»، منظومة لأبي عبد الله محمد بن أحمد بن على بن جابر الهوارى الأندلسى الأعمى (المتوفي سنة ٧٨٠/ ١٣٧٨) نظمها سنة ٧٤٧/ ١٣٤٦ في ١٦٨٠ بيتا من الرجز (انظر كشف الظنون ١٢٧٣ - ١٢٧٤، راجع «بغية الوعاة» للسيوطى ١٤).
المخطوطات:
لا له لى ٣٥٥٦ (٦٠ ورقة، نسخت سنة ٧٧٤ هـ، انظر، O.Rescherin: MFO (٥ /١٩١٢ /٥٢٤ جوروم ١٩١٣/ ٤ (٩٠ ب- ١٥٣ ب، نسخت سنة ٧٨٦ هـ، انظر أحمد آتش في، (Isl.Ilim.Enst.Derg.١ /١٩٥٩ /٥٩ - ٦٠: الظاهرية، عام ١٥٩٠ (٥٩ ورقة، نسخت سنة ٧٥٧ هـ، انظر فهرس اللغة ١٧٤ - ١٧٥)، دار الكتب بالقاهرة، لغة ٦٣٩ (نسخت سنة ٧٥٨ هـ)، ومجموع ٦٦٥ (انظر الفهرس الثانى ٢/ ٢٤٨)، و ٤٨١٩ هـ/ ٢ (نسخت سنة ١٣٢٣ هـ، انظر الملحق ١/ ١٥٢، ٢٨٩)، باريس ٤٤٥٢/ ٦ (٧٢ ب- ١١٦، نسخت سنة ٧٧٠ هـ، انظر فجدا ٣٨٠)، ما نشستر ٧٥٢ (٥١ ورقة، نسخت نحو سنة ٨٠٠ هـ)، تشستربيتى ٥١٧٠ (٥٦ ورقة، من القرن التاسع للهجرة)، ييل) ٢٤٧ - L، الأوراق ١ - ٣٥ أ، من القرن ١٢ هـ، انظر نيموى رقم ١٦٨)، جاريت، ٢٩١ هـ (٤٥ ورقة، نسخت سنة ١٢٣٤ هـ، انظر الفهرس رقم ٢٥٠).
٦ - «نظم الفصيح»، منظومة لأبي عبد الله محمّد بن محمد بن جعفر بن المشتمل (؟ ) المرّى البلياني، ذكرت في «كشف الظنون» ١٢٧٣.
٧ - «الجامع المهذّب في شرح مشكل فصيح ثعلب»، منظومة لمجهول، المتحف العراقي ببغداد ١٨٨٣/ ٦ (ثلاث ورقات، انظر فهرس النقشبندى ٨٧، رقم ٣٠٧).
٨ - «القصيدة الخالية»، ١٣ بيتا في معانى كلمة «الخال»، كما يؤخذ من إحدى مخطوطات مصنفة «كتاب الأمالى» ببرلين (انظر بعد)، وعنها نقل العسكرى في','1','259',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (256,'الصناعتين، القاهرة ١٩٧١، ص ٤٣٨ - ٤٣٩، المخطوطات: الظاهرية، عام ٤٥٢٤ (من القرن السادس أو السابع للهجرة، انظر فهرس عزة حسن ٢/ ١)، برلين ٧٠٦٦/ ١ و، ٢١٥ MF.) ٢ الورقة ٤٦٢ و، ٦٢٩ PM. الأوراق ١٠٤ ب- ١٠٦ ب)، راغب ١١٦٢/ ٢ (٣٤٤ أ، نسخت سنة ٩٩٥ هـ)./ ٩ - «كتاب استخراج الألفاظ (من الأخبار)» أو «كتاب الألفاظ»، ذكره ابن النديم، وابن خلكان، والقفطى، وياقوت.
١٠ - «كتاب الأضداد»، وكان معروفا في الأندلس في مقدار جزء (انظر فهرست ابن خير ٣٨١). كان من مصادر أبي محمد سعيد بن المبارك بن الدهان النحوى (المتوفي سنة ٥٦٩/ ١١٧٤) في كتابه «تهذيب كتاب الأضداد»، انظر مخطوطة نور عثمانية ٤٨٨٤، الورقة ١ ب.
١١ - «مجاز الكلام وتصاريفه»، وورد عنه نقل في الأضداد في «المزهر» للسيوطى ١/ ٣٩٣.

<span data-type="title" id=toc-280>في الأمثال، والعبارات السائرة، والأيمان، واللحن:</span>
١ - «كتاب الأمثال (السائرة)»، ذكره ابن النديم، والقفطى، وابن خلكان و «كشف الظنون»، وربما نقل عنه ابن الانبارى في كتاب «الزاهر» (انظر، Sellheim , (Sprichworter ١٢٤ - ١٢٥ ونقل عنه الميدانى في «مجمع الأمثال» (انظر ع. التكريتى في: المورد ٣، ٢/ ١٩٧٤/ ١٥، ٣، ٣/ ١٩٧٤/ ١١٢).
٢ - «كتاب الأيمان والدواهى»، ذكره ابن النديم، والقفطى، وابن خلكان. ونقل عنه الفهرى في «تحفة المجد الصريح» ص ٩٤، ٩٥.
٣ - «كتاب ما يلحن فيه العامة»، ذكره ابن خلكان، والقفطى.

<span data-type="title" id=toc-281>مجموعات من المسائل اللغوية والنحوية:</span>
١ - «مجالس (مجالسات) وأمال». ولعلهما أصلا مجموعتان مختلفتان: «ولأبي العباس مجالس وأمال أملاها على أصحابه» (إرشاد الأريب لياقوت ٢/ ١٥٣، عن الفهرست لابن النديم حيث العبارة مقتضبة هناك)، غير أنهما قد اختلطتا بعضهما','1','260',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (257,'ببعض منذ عصر متقدم. وترجع إحدى روايات المجالس إلى تلميذه ابن مقسم (المتوفي سنة ٣٥٤/ ٩٦٥، انظر ص ١٥٨ بعد). ويميّز من ناحية المحتوى بين:
(أ) نصوص عن بعض اللغويين والنحاة، وآرائهم، وتآليفهم، معروضة أحيانا في صورة مجالس. وينقل ابن النديم منها، دون أن يسمّيها، (ص ٣٩، ٤٢، ٥٣، ٥٥، ٦٦، ٦٩، ٧٤، ٩١) راجعا في ذلك إلى نسخة بخط ابن مقلة (المتوفي سنة ٣٢٨/ ٩٤٠)، وأورد في موضع آخر (ص ٥١) فقرة نقلها عن نسخة بخط ثعلب.
ونقلها ياقوت (إرشاد الأريب ٦/ ٨١) عن كتاب «الأمالى» لثعلب.
ومن ناحية أخرى يقتطف ياقوت من «المجالسات» (إرشاد الأريب ٥/ ١٩٤ - ١٩٥) أو دون التصريح باسم مصدره أيضا (١/ ٤٠٥ - ٤٠٦، ٦/ ٩٣، ١٠٢، ١١١، راجع (G.Bergstrasserin: ZS ٢ /١٩٢٤ /١٩٦ نصوصا تأريخية مناظرة في محتواها تتعلق ببعض اللغويين والنحويين المعروفين، وخاصة من الكوفيين.
وفي «مجالس الزجاجى» نحو ٢٠ نصا من نصوص المجالس ترجع إلى ثعلب باعتباره مؤلفها، ونصفها يشتمل على حوادث جرت بين ثعلب وبين أساتذته وزملائه. ومثل ابن النديم، الزجاجى، فقد اقتبس نصوصه من روايات مختلفة، ونقل أيضا في بعض المواضع عن نسخة بخط ثعلب (انظر على سبيل المثال ص ١٠٤). وتتطابق نقول الزجاجى مرارا مع نقول ابن النديم (فعلى سبيل المثال يتطابق الزجاجى ٤٨ - ٤٩ وابن النديم ٥٦) أو نقول ياقوت (فمثلا يتطابق الزجاجى ١٤٨ مع ياقوت، «إرشاد الأريب» ٦/ ١٠٢). ويبدو أنه لم تبق لنا مخطوطات لهذه النصوص.
(ب) تفاسير لآيات من القرآن وكثير من الشعر وأقوال مأثورة شتى مع ما يتعلق بها من الأخبار، فضلا عن شروح نحوية ولغوية في صورة أمال. وكثيرا ما ينقل عنها أيضا في الكتب على أنها أمال (انظر مثلا ابن مكى الصقلى في «تثقيف اللسان ١١١، ٣٣١ في مقابلة ٣٥٥؛ «تاج العروس» ١/ ٤٨)، على أن قدرا كبيرا من هذه النقول (لا سيما ما جاء في مؤلفات السيوطى) لم يرد في النص المنشور (انظر/ الزيادات الملحقة بالنصالمنشور ص ٨٠١ - ٨١١). والنشرة التى بين أيدينا (التى يجب معارضتها بالمخطوطات التى عرفت حديثا) تشتمل على خليط من أجزاء بها مجالس','1','261',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (258,'متكاملة، وسلاسل من الأمالى المفككة في ١٢ جزءا. وعنوان الكتاب في<span data-type="title" id=toc-282> المخطوطات </span>هو «كتاب الأمالى»، وتستثنى مخطوطة القاهرة التى تثبت من ناحيتها تسمية «كتاب الأمالى» في أواخر الأبواب.
المخطوطات:
دار الكتب بالقاهرة، لغة ٢٣ ش (١٣٤ ورقة)، ولغة ٢٧ ش (جزء، نسخة كتبت عن مخطوطة ياقوت، انظر الفهرس الثانى ٢/ ٣٤)، الجامعة الليبية ببنغازى (نسخت سنة ٥٨٣ هـ، انظر مجلة معهد المخطوطات ٢٠/ ١٩٧٤/ ١٨٧)، صائب بأنقره ٢٣٧٣ (١٣٢ ورقة، نسخة حسنة من القرن الخامس للهجرة، راجع، O.Rescher (in: MFO ٥ /١٩١٢ /٥٢٩ المكتبة اللورنسيانية بفلورنسا ٢٣٦) ٢٠٧/ ٤٥٧ Or، ورقة، من القرن الرابع للهجرة، انظر الفهرس، الذيل ص ٧٠)، المتحف الآسيوى بليننجراد ٣٢١ (؟ ، انظر، ٢٨١، Br.SI
و I. Krachovskijin: Dokl. Akad. Nauk SSSR ١٩٣٠, S. ٢١١ - ٢١٧
و، (Izbrannie Socinenija VI ,٥١٩ - ٥٢٧ ومنه قطع في برلين ٧٠٦٦/ ١ و ٢.
نشره عبد السلام محمد هارون على مخطوطتى القاهرة، القاهرة ١٩٤٨ - ١٩٤٩، ط ٢ ١٩٥٦، ط ٣ ١٩٦٠، انظر في هذه النشرة مصطفى جواد في: مجلة المجمع العلمى العراقي ٣/ ١٩٥٤/ ١٥٩ - ١٧٩ و «في التراث العربي» المجلد الأول، بغداد ١٩٧٥، ص ٣٧٣ - ٣٩٦. وانظر أيضا مصطفى الشكعة، «مناهج التأليف عند علماء العرب». قسم الأدب. بيروت ١٩٧٤، ص ٣٤٥ - ٣٥٢.
٢ - «معان وفوائد»، ويشتمل على مسائل في اللغة والنحو، ويوجد مخطوطا في «الأسكوريال ٧٧٨ (١٧ ورقة، نسخ سنة ٦٥٧، عن أصل لابن جنى، انظر فهرس المخطوطات ١/ ٣٧٣)، ولعله جزء من أحد الكتب الآتى ذكرها.
٣ - «كتاب المسائل»، ذكره ابن النديم، وياقوت، وابن خلكان.
٤ - «كتاب النوادر» (١٢٢)، وهو من مصادر ابن سيده في المخصص (انظر ١/ ١٢،
_________
(١٢٢) لا يجوز الخلط بينه وبين روايته لكتاب النوادر لأبي عمرو الشيبانى (انظر مراتب النحويين لأبي الطيب ٩١)، وانظر مثلا «إرشاد الأريب» لياقوت ٥/ ١٩٣.','1','262',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (259,'وراجع تاج العروس ١/ ٤٨) وأحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في تحفة المجد الصريح في شرح كتاب الفصيح (انظر مجلة المجمع العلمي العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٤)، ونقل عنه في «تاج العروس» ٢ ٥/ ٥٤٤ (دبج).

<span data-type="title" id=toc-283>وله في لغة القرآن والحديث:</span>
١ - «كتاب معانى القرآن» (١٢٣).
مذكور في كل قوائم مصنفاته، انظر أيضا پولس سباط في MIE ٤٩ /١٩٤٦ /٤٥ ,.No.٨١١: ونقل عنه الحريرى في «درة الغواص» ٤٣، ٤٧.
ولابن درستويه (المتوفي سنة ٣٤٧/ ٩٥٨) «كتاب التوسط بين الأخفش وثعلب في تفسير (أو معانى) القرآن»، ولم يصل إلينا، انظر ص ١٠٨ آنفا.
٢ - «كتاب غريب القرآن».
وهو كتاب «لطيف» (ابن النديم)، وذكره أيضا ياقوت، والسيوطى، وكان موجودا في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر يولس سباط، الموضع المذكور ص ٣٧، رقم ٦٦٢).
ولعله هو نفسه «كتاب مشكل القرآن» الذي كان يملكه الخطيب البغدادى (انظر «التسمية» للمالكى، المطبوع في كتاب «الخطيب البغدادى» ليوسف العش ٩٤، رقم ٤١).
٣ - «كتاب المجالس (المجالسات)» أو «الأمالى».
وهو أيضا في مسائل تتعلق بلغة القرآن، وقد وصل إلينا ونشر، انظر ص ١٤٦ آنفا.
وانظر في هذا أيضا المناقشات المتعلقة بمسائل في تفسير القرآن بينه وبين ابن كيسان، ومنافسه المبرد التى أوردها الزجاجي في «مجالس العلماء» ١٠٧ - ١٠٨، ١١٥ و ٢٧٦ - ٢٨١ في مواضع مختلفة.
٤ - «كتاب غريب الحديث».
ذكره ابن الأثير (النهاية، المقدمة ص ٥) في معرض كلامه على هذا النوع من الكتب، راجع كشف الظنون ١٢٠٥./
_________
(١٢٣) ذكر ابن النديم (ص ٣٣) أيضا تفسيرا للقرآن لمؤلف اسمه ابن ثعلب (؟ ).','1','263',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (260,'<span data-type="title" id=toc-284>القاسم الأنبارى</span>
هو أبو محمد القاسم بن محمد بن بشار، ولد بالأنبار على الفرات، ثم قدم إلى بغداد فيما بعد.
وكان من أصحاب أبي عكرمة الضبى، وسلمة بن عاصم، وثعلب، وامتدح بأنه عالم ثقة. واشتهر باشتغاله باللغة وعدّ أيضا من الأدباء. وكان تلميذه النابه هو ابنه محمد (انظر ص ١٥١ بعد). توفي سنة ٣٠٤/ ٩١٦ ببغداد.

<span data-type="title" id=toc-285>(أ) مصادر ترجمته:</span>
ابن النديم ٧٥؛ «طبقات النحويين» للزبيدى ٢٢٨؛ «مراتب النحويين» لأبي الطيب ١٥٨؛ «إرشاد الأريب» ٦/ ١٩٦ - ١٩٨؛ «إنباه الرواة» للقفطى ٣/ ٢٨؛ «غاية النهاية» لابن الجزرى ٢/ ٢٩؛ ابن قاضى شهبة، المخطوط ص ٤٨٥؛ «بغية الوعاة» للسيوطى ٣٨٠.- «الأعلام» للزركلى ٦/ ١٦؛ «معجم المؤلفين» لكحالة ٨/ ١١٥ - ١١٦.

<span data-type="title" id=toc-286>(ب) آثاره:</span>
ذكر له ابن النديم والمصادر التى تلته المصنفات الآتية:
١ - «كتاب الأمثال»؛ ٢ - «كتاب المذكر والمؤنث» (راجع مجلة معهد المخطوطات ١٧/ ١٩٧١/ ٣٠١)؛ ٣ - «كتاب غريب الحديث»؛ ٤ - «كتاب المقصور والممدود» (راجع مجلة كلية اللغة العربية، الرياض ٧/ ١٩٧٧/ ٨١)؛ ٥ - «خلق الإنسان»؛ ٦ - «كتاب خلق الفرس».

<span data-type="title" id=toc-287>أبو موسى الحامض</span>
هو سليمان بن محمد بن أحمد، وقيل له الحامض لفظاظته، عاش على ما يظهر في','1','264',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (261,'بغداد، وخالط ثعلبا وأصحابه. وسمع أيضا من البصريين. وبعد وفاة ثعلب أصبح زعيم مدرسة الكوفة، وإن كان في الواقع جامعا بين مذهبي الكوفيين والبصريين وبه شيء من الميل إلى مدرسة البصرة.
وقد أثنوا عليه ووصفوه بأنه من أكابر اللغويين؛ وكان إلى ذلك بارعا في البلاغة والشعر. وسمع منه أبو عمر الزاهد وبرزويه.
وقيل إنه أوصى بدفاتره لأبي فاتك المقتدرى ضنّا بها أن تصير إلى أحد. توفى سنة ٣٠٥/ ٩١٨.

<span data-type="title" id=toc-288>(أ) مصادر ترجمته:</span>
«طبقات النحويين» للزبيدى ١٧٠؛ ابن النديم ٧٩؛ «تاريخ بغداد» ٩/ ٦١؛ «نزهة الألباء» للانبارى ١ ٣٠٦ - ٣٠٧، ٢ ١٦٥ - ١٦٦، ابن خلكان ١/ ٢٦٩؛ «إرشاد الأريب» لياقوت ١ ٤/ ٢٥٤، ٢ ١١/ ٢٥٣ - ٢٥٥؛ «إنباه الرواة» للقفطى ٢/ ٢١ - ٢٢، ٣/ ١٤١ - ١٤٢ (حيث سمي: محمد بن سليمان)؛ مخطوطفي طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٣٢ ب- ٢٣٣ أ، ٢٦٥ أ- ٢٦٥ ب؛ «مسالك الأبصار» لابن فضل الله ٤/ ٢٩٢؛ ابن قاضى شهبه، المخطوط ص ٢٩٧ - ٢٩٨؛ «بغية الوعاة» للسيوطى ٢٦٢ - ٢٦٣
Flugel, Gramm. Schulen ١٩٥ - ١٩٦;-. Rescher, Abri BII, ٢٠٠
؛ «الأعلام» للزركلى ٣/ ١٩٥؛ «معجم المؤلفين» لكحالة ٤/ ٢٧٣./

<span data-type="title" id=toc-289>(ب) ومن آثاره:</span>
«كتاب (أو ذكر) ما يذكر وما يؤنّث من الإنسان ومن اللباس»، لم يرد ذكره بين مؤلفاته.

<span data-type="title" id=toc-290>المخطوطات:</span>
الاسكوريال ١٧٠٥/ ٧ (٦٩ أ- ٧٠ أ، نسخت سنة ٤٩٩ هـ، بخط الجواليقى، راجع فهرس المخطوطات ١/ ٣٦٨)، عاطف ٢٠٠٣/ ٧ (٩٦ أ- ٩٧ أ، نسخت سنة ١١٠٠ هـ، راجع، (O.Rescherin: MFO ٥ /١٩١٢ /٤٩١ ولي الدين ٣١٧٨/ ٢','1','265',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (262,'(٦ ب- ٧ أ، من القرن العاشر للهجرة، راجع (O.Rescherin: MO ٧ /١٩١٣ /١٠٨ , المتحف العراقي ببغداد ١٤٥٩ (٣ ورقات، نسخة بخط محمد السماوى، انظر إبراهيم السامرائي، مقدمة التحقيق ص ١٠٤)، و ١٦٧٧ (ورقتان، وهما كذلك من نسخ السماوى، سنة ١٣٣٢ هـ، انظر فهرس النقشبندى ٦٦، راجع كوركيس عواد في: سومر ١٣/ ١٩٥٧/ ٧٩ - ٨٠)، نسخة كانت ملكا للكرملى ولعلها الآن في المتحف العراقي أيضا (انظر، (Br.SI ,١٨٤ كلية الآداب بجامعة بغداد، مجموعة حسين على محفوظ (انظر مجلة معهد المخطوطات ٦/ ١٩٦٠/ ٥٢ - ٥٣)، الإمام المهدى بسامراء (من القرن الخامس للهجرة، مجموع مثل الأسكوريال ١٧٠٥، انظر مجلة معهد المخطوطات ٤/ ١٩٥٨/ ٢١٢)، دار الكتب بالقاهرة، مجموع ٨ ش (انظر الفهرس الثانى ٢/ ١٥)، التيمورية بدار الكتب، مجموع ٥٠/ ٢ (ص ٢٤ - ٢٧، من القرن ١٣ هـ)، راجع أيضا نهاد م. شتين في. Sarkiyatmecm.I /١٩٥٦ /٩٩ - ١٠٠:
نشره إبراهيم السامرائي في: «رسائل في اللغة»، بغداد ١٩٦٤، ص ١٠١ - ١٠٨.
ونشره ثانية رمضان عبد التواب في: «التذكير والتأنيث في اللغة»، القاهرة ١٩٦٧.

<span data-type="title" id=toc-291>وله تآليف مفردة في موضوعات وبعض مسائل لغوية:</span>
١ - «كتاب خلق الإنسان»، ذكرته جميع قوائم مصنفاته، راجع كشف الظنون ٧٢٣.
٢ - «كتاب الوحوش»، مذكور أيضا في كل القوائم، رآه ابن النديم بخط ابن أخى المؤلف، ووجدت منه نسخة في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر پولس سياط في. (٤٩/ ١٩٤٦ /٥٠ , No.٩٠٥:
٣ - «كتاب النبات»، ذكرته جميع قوائم مصنفاته.
٤ - «كتاب السبق والنضال»، ذكره ياقوت، وابن خلكان، والسيوطى. وكان في القرن ٧/ ١٣ باحدى مكتبات حلب (انظر بولس سباط، الموضع المذكور ٢٨، رقم ٤٩٧).
٥ - «كتاب الأمالى»، لم تذكره القوائم، انظر نقلا عنه في «تاريخ بغداد» ٢/ ٣٥٨، راجع «إرشاد الأريب» لياقوت ٧/ ٢٧، وكان أحد مصادر أحمد ابن يوسف','1','266',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (263,'الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٤).

<span data-type="title" id=toc-292>وله في لغة القرآن والحديث:</span>
١ - «غريب القرآن»، ذكر بين مصنفاته في «تاريخ بغداد» ٩/ ٦١، راجع «نزهة الألباء» للأنبارى ١٦٥.
٢ - «كتاب الأمالى» (انظر آنفا)، من الجائز أنه يحتوى على مسائل في لغة القرآن.
وفضلا عن هذا «صنّف كتبا حسانا في الأدب» (إنباه الرواة للقفطى ٢/ ٢١) وقيل إنه كان أيضا راسخ العلم بالشعر (انظر المرجع نفسه ٢٢).

<span data-type="title" id=toc-293>نفطويه</span>
هو أبو عبد الله إبراهيم بن محمد بن عرفه، لقّب بنفطويه لدمامته ولون وجهه./ وأصله من واسط حيث ولد سنة ٢٤٥/ ٨٥٩، ثم عاش في بغداد.
سمع من المبرد، وثعلب، ومحمد بن الجهم وغيرهم؛ وكان من تلاميذه المرزبانى، وأبو الفرج الأصفهاني.
وفضلا عن اشتغاله باللغة والنحو اشتغل بالتاريخ وسير العلماء والحديث والفقه والشعر (انظر فصل الأدب).
وقيل إنه كان في النحو (انظر فصل النحو) ضعيفا (راجع الزبيدى ١٧٢). وقد كان جامعا بين مذهبى الكوفيين والبصريين أكثر منه منتصرا للكوفيين. ولم يثنوا عليه لعلمه فقط، بل كذلك لورعه وثقته. وكان إلى هذا ظاهريا. توفي سنة ٣٢٣/ ٩٣٥ ببغداد.

<span data-type="title" id=toc-294>مصادر ترجمته:</span>
«المقتبس» للمرزبانى ٣٤٤ - ٣٤٥؛ ابن النديم ٨١ - ٨٢؛ «التهذيب» للأزهرى ١/ ٢٨؛ «نزهة الألباء» للأنبارى ١ ٣٢٦ - ٣٢٩، ٢ ١٧٨ - ١٨٠؛ «تاريخ بغداد» ٦/ ١٥٩ - ١٦٢؛ ابن خلكان ١/ ١٣؛ «إرشاد الأريب» لياقوت ١ ١/ ٣٠٧ - ٣١٥؛ ٢ ١/ ٢٥٤ - ٢٧٢؛ «إنباه الرواة» للقفطى ١/ ١٧٦ - ١٨٢؛ «لسان الميزان» لابن حجر','1','267',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (264,'١/ ١٠٩ - ١١٠؛ مخطوط في طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٦٦ ب؛ «مسالك الأبصار» لابن فضل الله ٤/ ٢٩٧ - ٢٩٨؛ «الوفيات» لابن قنفذ ٢٠٨؛ ابن قاضى شهبة، المخطوط ص ١٤٨ - ١٥١، «بغية الوعاة» للسيوطى ١٨٧ - ١٨٨
Flugel, Gramm. Schulen ٢١٣ - ٢١٥; Rescher, Abri BII, ٢٠١ - ٢٠٢;-. Br. SI, ١٨٤
؛ «أعيان الشيعة» للعاملى ٥/ ٣٨٥ - ٣٩٣؛ «الأعلام» للزركلى ١/ ٥٧ - ٥٨؛ «معجم المؤلفين» لكحالة ١/ ١٠٢؛ أكرم ضياء العمرى، «نفطويه النحوى ودوره في الكتابة والتأريخ» في: مجلة كلية الآداب، بغداد، ١٥/ ١٩٧٢/ ٧١ - ١٠٢.

<span data-type="title" id=toc-295>آثاره:</span>
من نحو ٣٢٠ مصنفا، قيل إنه صنفها (انظر لسان الميزان لابن حجر ١/ ١٠٩) لا يعرف عشرها، لا باسمائها، ولا بنقول عنها. والظاهر أن كثيرا من مصنفاته في اللغة كانت من مصادر الأزهرى (انظر ١/ ٢٨). وما نقل عنه في الكتب دون ذكر أسماء مصنفاته المأخوذ عنها قد جمعه أكرم ضياء العمرى في الموضع المذكور آنفا، ص ٩٣ - ٩٦.
١ - «كتاب الرد على المفضل (بن سلمة) في نقضه على الخليل»، وهو تفنيد لانتقاد المفضل بن سلمة (المتوفي نحو ٢٩٠/ ٩٠٣) كتاب «العين» للخليل ابن أحمد.
مذكور بين مصنفاته عند ابن النديم وفي ثلاث قوائم أخرى.
٢ - «كتاب اطرغشّ (١٢٤) في اللغة»، كان معروفا في الأندلس (انظر فهرست ابن خير ٣٧٢).
٣ - «قصيدة في غريب اللغة»، لم يرد ذكرها في قوائم مصنفاته، ذكرت مع أولها في «كشف الظنون» ١٣٤٣.
وقد شرحها تلميذه ابن خالويه (المتوفي سنة ٣٧٠/ ٩٨٠)، انظر المرجع نفسه.
٤ - «كتاب البارع»، ذكره ياقوت والصفدى، وكان موجودا في القرن ٧/ ١٣ في مكتبة بحلب (انظر بولس سباط في، (MIE ٤٩ /١٩٤٦ /١٠ ,No.١٧٨: ويحتمل أنه كان أيضا مصنفا في اللغة.
٥ - «كتاب النحل» أو «كتاب النمل»، انفرد بذكره ابن الأنبارى./
_________
(١٢٤) راجع «لسان العرب» ٨/ ٢٠١؛ «تاج العروس» ٤/ ٣١٩، ونجد العنوان نفسه عند ابن خالويه أيضا، انظر ص ١٧٩ بعد.','1','268',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (265,'<span data-type="title" id=toc-296>وله في الأمثال، والعبارات السائرة، والاشتقاق </span>..
١ - «كتاب الأمثال»، مذكور في خمس قوائم (انظر أيضا بولس سباط، في الموضع الآنف الذكر، ص ٩، رقم ١٦٠)، راجع. Sellheim ,Sprichworter ١٢٧
٢ - «أمثال القرآن» انظر في نقول عنه ما يأتى.
٣ - مجموعة من أقوال النبي (صلعم)، ولم تصل إلينا، انظر بعد.

<span data-type="title" id=toc-297>وله في لغة القرآن:</span>
١ - «مسألة سبحان (الله)»، ذكره ابن الأنبارى، وكان يملكه الخطيب البغدادى مع «مسألة الأمراء» (؟ ) (انظر التسمية للمالكى ضمن كتاب الخطيب البغدادى ليوسف العش ٩٩، رقم ١٥٧، ١٥٩).
وكان معروفا في الأندلس (انظر فهرست ابن خير ٣٧٦).
ويوجد مخطوطا في الظاهرية، مجموع ٧٩ (١١٩ أ- ١٢٦ أ، من القرن الرابع للهجرة).
٢ - «كتاب غريب القرآن»، «كتاب كبير» (تاريخ بغداد ٦/ ١٥٩)، ذكرته كل القوائم (واسمه عند السيوطى «إعراب القرآن»، وكان في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في. (MIE ٤٩ /١٩٤٦ /٣٧ ,No.٦٦٠:
٣ - «أمثال القرآن»، ذكره ياقوت والصفدى والسيوطى، ونقل عنه الزجاجى (المتوفي سنة ٣٣٧/ ٩٤٩) في مصنفه «كتاب اشتقاق اسماء الله» (انظر ع. المبارك في: المورد ٣، ١/ ١٩٧٤/ ٢٨٩).
٤ - مجموعة من أقوال النبي صلّى الله عليه وسلم، التى أصبحت من الأقوال السائرة، تقارن بكتاب «المجتنى» لابن دريد، ذكرها المسعودى في «مروج الذهب» ٤/ ١٧٤.

<span data-type="title" id=toc-298>وله مصنفات في العقائد:</span>
(استدراك على تاريخ التراث العربي، المجلد الأول)، وأسماء كتب غير مؤكدة.
١ - «كتاب الرد على من قال بخلق القرآن» ذكر في ست قوائم.
وذكره الأنبارى باسم «كتاب الرد على الجهمية» وبهذا الاسم ملكه الخطيب','1','269',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (266,'البغدادى (انظر التسمية للمالكى، ضمن كتاب «الخطيب البغدادى» ليوسف العش ١٠١، رقم ١٩٩).
وكان في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر بولس سباط في MIE ٤٩ /. (١٩٤٦ /٢٥ ,No.٤٣٨: ويحتمل ورود نقول عنه في «كتاب شرح أصول اعتقادات أهل السنة ... لابي القاسم هبة الله بن الحسن اللّالكائى (المتوفي سنة ٤١٨/ ١٠٢٧)، مخطوطة الظاهرية (انظر تاريخ التراث العربي ١/ ٥٠١)؛ ذكره أ. ض. العمرى، الموضع المذكور، ص ٩٤.
٢ - «كتاب التوبة»، ملكه الخطيب البغدادى (انظر التسمية للمالكى، الموضع المذكور، ص ١٠٠ رقم ١٨٠).
٣ - «كتاب الاقتصارات» (مذكور في ثلاث قوائم) أو «كتاب الاقتصامات» (ابن النديم، طهران) أو «كتاب الاقتضابات» (القفطى).

<span data-type="title" id=toc-299>ابن الأنبارى</span>
هو أبو بكر محمد بن القاسم بن محمد بن بشار الأنبارى (عاش من سنة ٢٧١/ ٨٨٥ إلى سنة ٣٢٨/ ٩٤٠).
وكان من علماء عصره في اللغة، وكذلك/ في النحو (انظر فصل النحو). وفضلا عن ذلك كانت له أيضا مشاركة في رواية الشعر (انظر فصل الأدب).

<span data-type="title" id=toc-300>مصادر ترجمته:</span>
انظر في قوائم مصنفاته ابن النديم ٧٥ (طهران، ص ٨٢؛ الترجمة الانجليزية ١٦٦)؛ «أنباه الرواة» للقفطى ٣/ ٢٠٤، ٢٠٨؛ «إرشاد الأريب» لياقوت ٧/ ٧٦ - ٧٧؛ «تاريخ بغداد» ٣/ ١٨٢، ١٨٤؛ ابن خلكان ١/ ٦٣٧ - ٦٣٨؛ «نزهة الألباء» للأنبارى ١٨١ - ١٨٢؛ «الوافي بالوفيات» للصفدى ٤/ ٣٤٤ - ٣٤٥؛ طبقات ابن قاضى شهبة ٢٣٥؛ «بغية الوعاة» للسيوطى ٩٢.','1','270',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (267,'<span data-type="title" id=toc-301>آثاره:</span>
١ - «كتاب الزاهر (في معانى كلمات الناس أو في معانى الكلام الذي يستعمله الناس)»، مذكور في الفهرست لابن النديم وفي خمس قوائم أخرى (انظر أيضا فهرست ابن خير ٣٤١).
وهو من مصادر ابن سيده في المخصص (انظر ١/ ١٣)؛ والصغانى في «التكملة» (انظر مقدمة التحقيق ١/ ٨) و «العباب» (انظر ١/ ٣٠)؛ وأحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في «تحفة المجد الصريح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣)، وانظر في محتويات الكتاب:
O. Rescherin: ZDMG ٦٤/ ١٩١٠/ ٥٢٠ Sellheim, Sprichworter ١٢٥ - ١٢٦

<span data-type="title" id=toc-302>المخطوطات:</span>
لا له لى ١٧٨٧ (٣٤٨ ورقة، كاملة، نسخت سنة ١٠٨٩ هـ، انظر؛ O.Rescher (in: ZDMG ٦٤ /١٩١٠ /٥١٩ ولى الدين ٢٥٩٧ (٣١٤ ورقة، من القرن ١٢ هـ، انظر الموضع نفسه)، فاتح ٣٩١٢ (٣٠٤ ورقة، من القرن السابع للهجرة، راجع رشر؛ (MFO ٥ /١٩١٢ /٤٩٨ أسعد ٣٢١٥ (المجلد الأول، ٢٠٩ ورقة، نسخ سنة ٣٧٨، انظر المرجع نفسه ص ٥٣٣)؛ و ٣٢١٦ (المجلد الثانى، ٣٥٢ ورقة، نسخ سنة ٦٢٢ هـ)، كوبريلى ١٢٨٠ (٣٠٨ ورقة، آخره ناقص، من القرن السابع للهجرة، راجع، (O.Rescherin: (MSOS ١٤ /١٩١١ /١٧٥ ومنها مصورة بدار الكتب بالقاهرة، لغة ٥٨٨ (انظر الفهرس الثانى ٢/ ١٦، فهرس المخطوطات ١/ ٣٥٦، وخلط هنا بينه وبين «مختصر» الزجاجى)، ونسخة منها بالدار أيضا، لغة ٨٠٦ (٣٨٤ ورقة، نسخت سنة ١٣٥١، انظر الفهرس الثانى ٧/ ٨)، فيض الله ١٦٠٨ (٢٠٠ ورقة، نسخت سنة ١٠٨٩، انظر، (ZDMG ٦٨ /١٩٤١ /٣٨١ راغب ١٤١٦ (٣١٤ ورقة، من القرن ١١ هـ، انظر، (ZDMG ٦٤ /١٩١٠ /٥٢٠ داماد إبراهيم ٣٨٩ (٢٢٧ ورقة، من القرن ١١ هـ)، برتو ٥٩٩ (١ ب- ٣١٧ ب، نسخت سنة ١٠٩٨ هـ)، حالت ٤٥٣ (٢٧٠ ورقة، نسخت سنة ١١٦٨ هـ)، تشستربيتى ٥٠٤٥ (١٠٥ ورقة، نسخت سنة ١١٦٣ هـ)، جاريت ١٠٤) B ١٧٦ ورقة، من القرن الخامس للهجرة، انظر الفهرس، رقم ٢٥٢)، قوله بالقاهرة ق ٢٣ (١٢٧ ورقة، انظر حاتم','1','271',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (268,'صالح الضامن، مقدمة التحقيق ص ٧٥)، ييل ١٩٢) ١٩٥ L ورقة، من القرن ١٢ هـ، انظر نيموى رقم ١٤٧).
نشره حاتم صالح الضامن، بغداد ١٩٧٩.

<span data-type="title" id=toc-303>ولكتاب (الزاهر) مختصران:</span>
(أ) «اختصار (أو مختصر) الزاهر»، لعبد الرحمن بن إسحاق الزجاجى (المتوفي سنة ٣٣٧/ ٩٤٩)، وقد وصل إلينا، انظر ص ١٠٥ آنفا.
(ب) «كتاب اختصار الزاهر» لأبي بكر خطّاب بن يوسف بن هلال الماردى القرطبي (المتوفي بعد سنة ٤٥٠/ ١٠٥٨، انظر «معجم المؤلفين» لكحالة ٤/ ١٠٣)، ذكره ابن خير في فهرسته ٣٤٢، والسيوطى في بغية الوعاة ٢٤٢.
٢ - «كتاب الأمثال»، ذكره الصفدى وابن قاضى شهبة فضلا عن المصادر الآنفة الذكر.
٣ - «قصيدة في مشكل اللغة»، أو «منظومة في الغريب»، أو «غريب اللغة»، وهى منظومة مع شرح، لم تذكرها قوائم مصنفاته.

<span data-type="title" id=toc-304>المخطوطات:</span>
نور عثمانية ٤٢٦٠/ ١٠ (الأوراق ٦٦ - ٧٤، من القرن العاشر للهجرة)، الظاهرية، عام ٥٤٣٣ (٩٠ أ- ٩٨ أ، نسخة قديمة، انظر فهرس اللغة ١٨١ - ١٨٢)، التيمورية بدار الكتب بالقاهرة، لغة ١٧٨ (٢٧ ورقة، نسخت سنة ١٢٩٠ هـ)، البلدية بالاسكندرية ٥٧٨٤ ج (١١ ورقة، نسخت سنة ١٢٩٦ هـ، انظر فهرس المخطوطات ١/ ٣٧٥)، ييل) ١/ ٣٢٧ - L الأوراق ١ - ٩، نسخت سنة ١١٥٧ هـ، انظر نيموى رقم ١٤٦).
٤ - «رسالة في شرح معانى الكذب»، وهى في المعانى الخمسة لهذه الكلمة، انظر نقولا عنها في «خزانة الأدب» ٣/ ٩، ١٣، ١٤.
٥ - «مسألة في التعجّب»، وتوجد مخطوطة في كوبريلى ١٣٩٣ (٩٧ أ- ٦٩ ب، نسخت سنة ٦٣٥ هـ)، برلين ١٥١، ١٢٢ Mq.) ٦٩٣٣ ب- ١٥٣ أ، نسخت نحو سنة ٦٠٠ هـ).','1','272',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (269,'نشرها محيى الدين توفيق ابراهيم في: آداب الرافدين، المجلد الخامس ١٠ - ١٢، انظر طارق ع. الجنابى، في مقدمته لكتاب «المذكر والمؤنث» ٢٠.
٦ - «كتاب الحاء» (هل اختلط بكتاب الهاءات؟ انظر فصل النحو)، ونقل عنه البكرى في «معجم ما استعجم» ٩٨./ ٧ - «كتاب الأضداد» قال صاحب «تاريخ بغداد»: «وما رأيت أكبر منه».
(٣/ ١٨٤).
ذكرته كل القوائم (راجع بولس سباط في. (MIE ٤٩ /١٩٤٦ /٧ ,No.١٢٣:
وكان من بين مصادر أحمد بن يوسف الفهرى (المتوفي سنة ٦٩١/ ١٢٩٢) في «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣)، ونقل عنه ابن السيد في الاقتضاب ١١٠؛ والبغدادى في الخزانة (انظر «إقليد الخزانة» للميمنى ٩)؛ والسيوطى في «المزهر» ١/ ٣٩٧.

<span data-type="title" id=toc-305>المخطوطات:</span>
ليدن ١٤٠) ٥٥٢ Or. ورقة، نسخت سنة ٦٥٢ هـ، انظر فورهوف ٢)، تشستربيتى ٤٥٣٣ (١٤٥ ورقة، من القرن السادس للهجرة، معارضة بالأصل الذي كتبه المؤلف)، طلعت بالقاهرة، لغة ٣٥٢ (٢٢٤ ورقة، من القرن ١١ هـ).
ونشر على مخطوطة ليدن بتحقيق هوتسما، M.Th.Houtsma ليدن ١٨٨١.
وأعيد طبعه بالقاهرة ١٩٠٧.
ونشره مرة أخرى محمد أبو الفضل إبراهيم، الكويت ١٩٦٠.
وانظر في نصوص منه مقارنة بفقر مناظرة من كتب قطرب وأبي حاتم، وابن السكيت وأبي الطيب: «نصوص في فقه اللغة» للسيد يعقوب بكر، المجلد الثانى، بيروت ١٩٧١، ص ٢١٧ - ٢٢٦؛ ولجبّور دراسة فيه:
S. Jabbour, Classificationet explicationdesad dadd''apresle Kitabal- addad d''al- Anbarlin: L''ambivalencedan slaculturearabe, Paris ١٩٦٧, S. ٦٥ - ٨٠.
(أ) ولتقى الدين عبد القادر التميمى (المتوفى سنة ١٠٠٥/ ١٥٩٧ كتاب سماه «مختصر كتاب الأضداد»، انظر «معجم المؤلفين» لكحاله ٥/ ٢٨٥)، وقد وصل إلينا ترتيبه على حروف المعجم الذي صنعه ابنه ملّا حسن (راجع «كشف الظنون»','1','273',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (270,'١١٦)، وهو مخطوط في تشستربيتى ٥٠٦٠ (٩٠ ورقة، من القرن ١١ هـ).
(ب) ورتبه محمد بن أحمد بن حمزة الإسكندرانى (المتوفى بعد سنة ١٣٠٤/ ١٨٨٧) في كتاب سماه «ترتيب كتاب الأضداد على حروف المعجم»، ويوجد مخطوطا بالتيمورية بالقاهرة، لغة ٢٤٦ - ٢٤٧ (نسخ سنة ١٣٠٤ هـ، بخط مؤلفه).
٨ - «كتاب الأمالى» أو «مجالس» أو «مجالسات»، مذكور في كل القوائم، ويحوى مسائل في اللغة والنحو والحديث والتفسير والأخبار والشعر. (انظر تاريخ بغداد ٣/ ١٨٢؛ ابن النديم، الموضع المذكور؛ الانبارى، الموضع المذكور)، ووردت نقول عنه في «إرشاد الأريب» لياقوت (انظر؛ ٤٢٩١/ ٢ SZ: niressartsgreB. G/١٩٧ ابن خلكان ١/ ٣٦ - ٣٧؛ «شرح نهج البلاغة» لابن أبي الحديد ١٢/ ٨٢ ومواضع أخرى كثيرة؛ «الدر المنثور» للسيوطى (ثبت مصادر مخطوطة برلين ٨٩٨، ٥٠ ب).
ووصل إلينا مجلس منه في مخطوطة الظاهرية، مجموعة ٨٨/ ١٠ (١٣٩ أ- ١٤٥ أ، من القرن السادس للهجرة، انظر فهرس الحديث ٢٦؛ م. ع. رمضان، مقدمته لكتاب «إيضاح الوقف والابتداء» لابن الأنبارى، القاهرة ١٩٧١، ص ١٧).
٩ - «كتاب النوادر»، نقل عنه أبو على القالى من نسخة بخطة في «سمط اللآلى» ١٥٩.

<span data-type="title" id=toc-306>ولابن الأنبارى تآليف مفردة في بعض الموضوعات:</span>
١ - «كتاب خلق الإنسان»، ذكره الصفدى وابن قاضى شهبه، راجع «كشف الظنون» ٧٢٢.
٢ - «كتاب خلق الفرس»، مذكور في المصادر نفسها.
ولعل هذين الكتابين هما كتابا أبيه (انظر ص ١٤٨ آنفا) نسبا إليه اختلاطا.

<span data-type="title" id=toc-307>وله في لغة القرآن والحديث:</span>
١ - «كتاب المشكل (في معانى القرآن)»، وذكر ابن النديم أنه لم يتمه.
وقيل إنه أملاه سنين كثيرة، وبلغ إلى «طه» (انظر «تاريخ بغداد» ٣/ ١٨٤؛ «إنباه الرواة» للقفطى ٣/ ٢٠٤).','1','274',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (271,'٢ - «رسالة في المشكل»، وهى ردّ على ابن قتيبة وأبي حاتم، ذكرت في أربع قوائم.
٣ - «كتاب غريب الحديث»، وذكر ابن النديم أنه لم يتمه، مع أنّ ما أملاه منه بلغ، فيما زعموا، ٤٥٠٠٠ ورقة (انظر «إنباه الرواة» للقفطى ٣/ ٢٠٤)./ ٤ - «شرح غريب حديث أم زرع»، وكان معروفا في الأندلس (انظر فهرست ابن خير ١٩٧).
ويوجد النص الأصلى دون شرح في «كتاب بلاغات النساء» لأحمد بن أبي طاهر طيفور، القاهرة ١٩٠٨، ص ٨٥ - ٢٦.
٥ - «شرح غريب خطبة عائشة أم المؤمنين في أبيها أبي بكر الصديق» وكان معروفا بالأندلس (انظر فهرست ابن خير ١٦٦ - ١٦٧، ١٩٧ - ١٩٨)، وملكه الخطيب البغدادى (انظر التسمية للمالكى في «الخطيب البغدادى» ليوسف العش ١٠٤، رقم ٢٧٥).

<span data-type="title" id=toc-308>المخطوطات:</span>
كوبريلى ١٦٠٠/ ٣ (٢٦ أ- ٣٠ ب، نسخت سنة ٦٥١ هـ، وتحتوى على مجمل لخطبة عائشة للخطيب البغدادى برواية أساتذته أبي بكر أحمد بن محمد البرقانى (انظر تاريخ التراث العربي ١/ ٢٢٩)، وأبي نعيم، وعلى بن المحسّن التنوخى. وقد روى الأخير نص ابن الأنبارى وكذلك ما شرحه من الألفاظ)، دار الكتب الوطنية بتونس، مجموعة حسن حسنى عبد الوهاب (ضمن مجموع، نسخت سنة ٧٤٠ هـ، انظر صلاح الدين المنجد في: مجلة المجمع العلمى العربي بدمشق ٣٧/ ١٩٦٢/ ٦٩٦ - ٦٩٧).
ونشره صلاح الدين المنجد على مخطوطة ببرنستون في: مجلة المجمع العلمى العربي بدمشق ٣٧/ ١٩٦٢/ ٤١٤ - ٤٢٧، وعلى حدة في دمشق ١٩٦٢.
ويرد أيضا نص الخطبة (دون شرح) في «كتاب بلاغات النساء» لأحمد ابن أبي طاهر طيفور، القاهرة ١٩٠٨، ص ٣ - ٦.
٦ - «شرح غريب كلام هند بنت أبي هالة التميمى في صفة رسول الله» (انظر في هند بنت أبي هالة: الكلبى/ كاسكل ٢/ ٢٨٣)، وكان معروفا في الأندلس (انظر فهرست ابن خير ١٩٧). وملكه الخطيب البغدادى باسم «تقريب خطبة هند بنت','1','275',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (272,'أبي هالة» (انظر التسمية للمالكى في «الخطيب البغدادى» ليوسف العش ١٠٤، رقم ٢٧٧).
٧ - «أمال»، أو مجالس، أو مجالسات» (انظر ص ١٥٣ آنفا)، اشتملت أيضا على مسائل في الحديث (انظر «نزهة الألباء» للأنبارى ١٨٢).

<span data-type="title" id=toc-309>أبو عمر الزاهد</span>
هو محمد بن عبد الواحد غلام ثعلب المطرّز (انظر تاريخ التراث العربي ٧/ ٣٥٤).
ولد سنة ٢٦١/ ٨٧٤. وتذكره الكتب أو تنقل عنه بألقابه الثلاثة.
وكان رأس أساتذته هو ثعلب. ونبغ في النحو واللغة؛ واشتغل فضلا عن ذلك بالشعر والحديث.
وكان من أولئك العلماء الذين يقال إنه كانت لهم حافظة عجيبة. وجاء في خبر مبالغ فيه (نشوار المحاضرة للتنوخى ٤، بيروت ١٩٧٢، ص ٢٢٦، وراجع «إرشاد الأريب» لياقوت ٧/ ٢٦) إنه أملي من حفظه ٣٠٠٠٠ ورقة لغة. على أنّ جودة حافظته قد دعت اللغويين إلى الارتياب في الروايات المقترنة باسمه. وقد أثنى عليه لورعه؛ ورى فضلا عن ذلك أنه كان مغاليا في ميله إلى الأمويين. توفى نحو سنة ٣٤٥ هـ/ ٩٥٧ م.

<span data-type="title" id=toc-310>مصادر ترجمته:</span>
«طبقات النحويين» للزبيدى ٢٢٩؛ ابن النديم ٧٦ - ٧٧؛ «تاريخ بغداد» ٢/ ٣٥٩ - ٣٦٠؛ «نزهة الألباء» للأنبارى ١ ٣٤٥ - ٣٥٤؛ ابن خلكان ١/ ٦٣٢ - ٦٣٤؛ «إرشاد الأريب» لياقوت ١ ٧/ ٢٦ - ٣٠؛ ٢ ١٨/ ٢٢٦ - ٢٣٤؛ «إنباه الرواة» للقفطى ٣/ ١٧١ - ١٧٧؛ «الوافي بالوفيات» للصفدى ٤/ ٧٢ - ٧٣؛ «مسالك الأبصار» لابن فضل الله ٤/ ٢٤٠ - ٢٤٣؛ ابن قاضى شهبة ١٧٥ - ١٧٨؛ «بغية الوعاة» للسيوطى ٦٩ - ٧٠؛ شذرات ابن العماد ٢/ ٣٧٠ - ٣٧١
Flugel, Gramm. Schulen ١٧٤ - ١٧٨;-. Br. GI, ١١٩, SI, ١٨٣ - ١٨٤; Rescher, Abri BII,/ ١٨٩ - ١٩١','1','276',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (273,'«الأعلام» للزركلى ٧/ ١٣٢ - ١٣٣؛ «معجم المؤلفين» لكحالة ١٠/ ٢٦٦ ج ٢٦٧؛ عبد العزيز الميمنى، «أبو عمر الزاهد» في: مجلة المجمع العلمى الهندى Ch.Pellatin: EI ,II ٢ ,١٠٩٣؛ ١٩ - ١ /١٩٧٦ /١

<span data-type="title" id=toc-311>آثاره:</span>
له مصنفات جامعة في اللغة، وأخرى غير مؤكدة، يحتمل أنها تدخل في هذا الباب:
(١) «كتاب المداخل (في غريب اللغة)» ويسمى أيضا «كتاب المداخلات»، ذكرته كل قوائم مصنفاته، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣٥٨ - ٣٥٩).
ويتألف الكتاب من ٣٠ بابا تحتوى على «سلاسل من الألفاظ» تفسر فيه لفظة بلفظة أخرى، ثم تفسر هذه بلفظة ثالثة، وهلم جرا.
ونقل عنه الدميرى (الحيوان ٢/ ١٨٣) على أنه لأحمد بن يحيى (ثعلب) - والواقع أن غالب النصوص التى فيه مأخوذة عن ثعلب عن ابن الأعرابي-.
وكان من مصادر حبيش بن إبراهيم التفليسى (المتوفى سنة ٦٢٩/ ١٢٣٢) في «قانون الأدب» (انظر مجلة المجمع العلمى العربي بدمشق ٥٠/ ١٩٧٥/ ٤٠٢، والصغانى في «التكملة» (انظر ١/ ٨) و «العباب» (انظر ١/ ٢٩).

<span data-type="title" id=toc-312>المخطوطات:</span>
كوبريلى ١٣٢٤/ ٤ (١٥٠ أ- ١٥٨ ب، نسخت سنة ٦٣٢ هـ، راجع، O.Rescher (in: MSOS ١٥ /١٩١٢ /٢ بورسه، حسين جلبى ١١٥٠٠/ ٢ (٩ أ- ١٩ ب، نسخت سنة ٦٣٦، انظر فهرس المخطوطات، ٦٥/ ٤١٩١ (O.Rescherin: ZDMG ٦٨ /؛ ٣٧١ /١ جوروم- ١٩١٣/ ١ (١ ب- ٣٠ ب، نسخت سنة ٣٧٨ هـ، انظر أحمد آتش في، (Isl.Ilim.Enst.Derg.١ /١٩٥٩ /٥٧ - ٥٨: الظاهرية، عام ٧٩٨٨ (١ أ- ٨ أ، ثم تليها إلى الورقة ١٧ زيادات لغوية لعبد العزيز الميمنى، انظر فهرس اللغة ١٤٨ - ١٤٩)، دار الكتب بالقاهرة في المجاميع: لغة ٢٢٩، ولغة ٢٦ ش (١٩ ب- ٢٣ أ، ناقصة، نسخت سنة ٥٤٢ هـ)، ومجموع ١٦٦ م (٣٤ أ- ٤٠. نسخت سنة','1','277',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (274,'١٢٠٥ هـ، راجع الفهرس الثانى ٢/ ٣٧)، التيمورية، لغة ٥٣٠ (ص ٣٠٠ - ٣٢٢)، ييل ١٠) ٣٣٨ - L ورقات، نسخت نحو سنة ١٣١٠ هـ، انظر نيموى رقم ١٦١).
نشره عبد العزيز الميمنى في: مجلة المجمع العلمى العربي بدمشق ٩/ ١٩٢٩/ ٤٤٩ - ٤٦٠، ٥٣٢ - ٥٤٤، راجع أيضا المجلة نفسها ص ٦٠١ - ٦١٦.
ونشره محققا على خمس مخطوطات محمد عبد الجواد، القاهرة ١٩٥٦، انظر في هذه النشرة جورج شحاته قنواتى في. OEDIM ٥ /١٩٥٨ /٣٨٩ - ٣٩٠:
وقد شرحه المؤلف نفسه في كتاب سماه «كتاب حلّ المداخل»، وعند ابن خلكان «علل المداخل»، ذكرته خمس قوائم.
٢ - «كتاب العشرات (في اللغة)»، ذكرته ست قوائم، كتبه تلميذه ابن خالويه (المتوفى سنة ٣٧٠/ ٩٨٠) ويحتوى أيضا على سؤالاته في بعض الفصول (راجع. (I.Krackovskijin: Islamica ٢ /١٩٢٦ /٣٣٣ ويشتمل في كثير من الفقر القصيرة على شروح وجيزة لطوائف من الألفاظ كل منها عشرة ألفاظ مشتركة في الوزن (فعال، فعيل وهلم جرا) وثالث أصولها. ونصوص أبي عمر (في مخطوطة بورسه) يرجع معظمها إلى ابن الأعرابي عن طريق ثعلب. انظر في نقول عنه: الحيوان للدميرى ٢/ ٢٠٨؛ معجم البلدان لياقوت ٢/ ٢٣٨ - ٢٣٩، ٩٥٩.

<span data-type="title" id=toc-313>المخطوطات:</span>
طهران، دانشكاه ١٣٥٥ (٦٠ ورقة، نسخت سنة ٧٦٠ هـ، انظر الفهرس ٨/ ٥٠ - ٥١، راجع مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ١٣)، برلين ٤٤، ٣/ ٤٧ We.) ٧٠١٤ ب- ٨٧ أ، حديثة)، مختصر ببورسة، حسين جلبى ١١٥٠/ ١ (١ أ- ٨ أ، نسخت سنة ٦٣٦ هـ، راجع، O.Rescherin: ZDMG ٦٨ /١٩١٤ /٥٦ فهرس المخطوطات ١/ ٣٦٠).
وقد أكمل الكتاب أبو عبد الله محمد بن جعفر التميمى القزّاز (المتوفى سنة ٤١٢/ ١٠٢١)، وقد وصل إلينا، انظر ص ٢٥٠ بعد.
٣ - «كتاب اليواقيت» أو «كتاب الياقوت (أو الياقوتة) (في اللغة)»، ذكرته جميع القوائم، انظر أيضا «تاريخ بغداد» ٢/ ٣٥٧ - ٣٥٨؛ فهرست ابن خير ٣٥٧؛ «كشف الظنون» ٢٠٥٣ - ٢٠٥٤. وقد ألف هذا الكتاب فيما بين سنتى ٣٢٦','1','278',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (275,'و ٣٣١ هـ، أملاه ارتجالا، ثم زاد فيه مرارا وبالغ في استكماله وأضاف إليه استدراكا سمّاه «المحرابيّة» (ابن النديم، القفطى).
انظر في نقول عنه: «طبقات الحنابلة» لابن أبي يعلى؛ «خزانة الأدب» (انظر إقليد الخزانة للميمنى ١٢٩)، وراجع «معجم البلدان» لياقوت ٢/ ٢٣٩.
وقد كان الكتاب/ مصدرا للأزهرى أطراه، وأفاد منه حق الإفادة (١/ ٢١) لما فيه من كثرة الغريب (نفسه ص ٢٣، وراجع ص ٣٠ وأورد منه أخبارا كما ورد في نقول الخزانة).
وهو من مصادر أحمد بن يوسف الفهرى (المتوفي سنة ٦٩١/ ١٢٩٢) في «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٢)؛ والصغانى في «التكملة» (انظر مقدمة التحقيق ١/ ٨)، و «العباب» ١/ ٢٩، ١٢٠.
ومنه نقل على غلاف إحدى المخطوطات برامبور طبعه عبد العزيز الميمنى في: مجلة المجمع العلمى العربي بدمشق ٩/ ١٩٢٩/ ٦١٥ - ٦١٦.
وأورد ياقوت خاتمة الكتاب في إرشاد الأريب ٧/ ٢٩ - ٣٠، راجع بغية الوعاة للسيوطى ٧٠.
ولعل فصلا منه كان عنوانه «ياقوتة الصراط في تفسير القرآن»، وامتلكه الخطيب البغدادى (انظر التسمية للمالكى في «الخطيب البغدادى» ليوسف العش ٩٥، رقم ٥٧)، وكان معروفا في الأندلس (انظر فهرست ابن خير ٦٠ - ٦١).
وقد وصل إلينا في مخطوطة لا له لى ٢٥٥/ ٤ (٥٧ أ- ٧٦ ب، نسخت سنة ٧٨٤ هـ)، ومخطوطة رشيد ٣٤٨/ ٣ (٤٦ أ- ٧٤ ب، من القرن التاسع للهجرة، انظر شيشين، نوادر ٢/ ٢٧٢).
وذكر ابن قاضى شهبه كتابا اسمه «الياقوتة في غريب القرآن»، ونقل الصغانى (العباب ١/ ٨٨) عن «ياقوتة الهادى».
٤ - «كتاب المستحسن (في اللغة)»، ذكره ابن النديم، وورد في خمس قوائم أخرى؛ وعليه زيادات للمؤلف نفسه في «كتاب فائت المستحسن»، ذكره ابن النديم وياقوت.','1','279',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (276,'٥ - «كتاب المرجان (في اللغة)»، مذكور عند ابن النديم، وفي خمس قوائم أخرى.
٦ - «كتاب الموشح»، ذكره ابن النديم وياقوت والسيوطى؛ وفي سائر القوائم «كتاب الموضح»، ربما كان هو «كتاب الموضح» لأستاذه ثعلب بروايته.
وكان «كتاب الموشح» مصدرا للصغانى في «التكملة» (انظر ١/ ٨)، و «العباب» (انظر ١/ ٢٩).
٧ - «كتاب التفاحة»، ذكرته كل القوائم.
٨ - «كتاب المكنون والمكتوم»، مذكور عند ابن النديم، وفي خمس قوائم أخرى.
ويوجد بين مصنفات أبي زيد الأنصارى (المتوفى سنة ٢١٥/ ٨٣٠) كتاب له اسم مناظر هو «كتاب المكتوم». والظاهر أن كلا الكتابين كانا مصنّفين في اللغة، تبعا لما كان يغلب على مؤلفيهما الاشتغال به من العلم.

<span data-type="title" id=toc-314>وله شروح وانتقادات لمصنفات المتقدمين اللغوية، وكتب في بعض مسائل اللغة وتفسير أسماء الأعلام؛ منها:</span>
١ - «فائت العين»، استدراكات على كتاب العين للخليل بن أحمد (المتوفى سنة ١٧٥/ ٧٩١)، ذكرته جميع القوائم، راجع «كشف الظنون» ١٤٤٣.
٢ - «كتاب ما أنكرته العرب على أبي عبيد فيما رواه أو صنّفه»، ذكرته كذلك كل القوائم، وورد خطأ في ثلاثة مصادر «أبو عبيدة» بدلا من «أبي عبيد».
٣ - «كتاب فائت الفصيح»، زيادات على كتاب «الفصيح» لأستاذه ثعلب (المتوفى سنة ٢٩١/ ٩٠٤)، «جزء لطيف» (ياقوت)، ذكرته كل القوائم، راجع كشف الظنون ١٢٧٣، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣٣٩) وموجودا في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر پولس سباط في MIE ٤٩ /١٩٤٦ /٣٨ ,. (No.٦٧٣: ويوجد مخطوطا في بورسة، حسين جلبى ١١٥٠/ ٢ (١٠٣ أ- ١١٣ أ، نسخت سنة ٥٤٢ هـ، راجع؛ H.Ritterin: Oriens ٢ /١٩٤٩ /٢٦٦ وفهرس المخطوطات ١/ ٣٦٢)، وهرجى أو غلو ١٤٢٢/ ٢ (٢١ ب- ٢٨ أ، نسخت سنة ٥٩٢ هـ، انظر','1','280',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (277,'فهرس المخطوطات ٢/ ٢٧٢)، وانظر أيضا عبد العزيز الميمنى في: مجلة المجمع العلمى العربي بدمشق ٩/ ١٩٢٩/ ٦١٣.
نشره عبد العزيز مطر، القاهرة ١٩٧٦.
٤ - «شرح كتاب الفصيح»، شرح على نفس الكتاب، ذكرته/ كل القوائم مستقلا عن الكتاب الآنف الذكر، ونقل عنه أبو حيان الأندلسى في «تذكرة النحاة»، الجزء الثانى، مخطوطة الأوقاف بالرباط ٢١٤، ص ٤٠١. وعن طريق كتاب «الواعى» لأبي القاسم عبد الدائم بن مرزوق القيروانى (المتوفى بعد سنة ٤٦٧/ ١٠٧٥، انظر «إنباه الرواة» للقفطى ٢/ ١٥٨) نقل الفهرى نصوصا من شرح الفصيح، فضلا عن كتاب «الياقوتة»، في «تحفة المجد الصريح» ص ١٤، ١٥، ٢٠، ٢٥، ٢٦، ٢٧، ٢٩، ٣١، ٣٣، ٣٤، ٣٥، ٣٦، ٣٨، ٣٩، ٤٠، ٤١، ٤٢، ٤٣، ٤٤، ٤٨، ٤٩، ٥٠، ٥٢، ٥٤، ٥٥، ٥٦، ٥٧، ٥٨، ٦١، ٦٢، ٦٣، ٦٤، ٦٥، ٦٧، ٦٨، ٧١، ٧٤، ٧٦، ٧٨، ٧٩، ٨٣، ٨٨، ٩١، ٩٢، ٩٣، ٩٤، ٩٥، ٩٦، ٩٨، ١٠٠، ١٠١، ١٠٣، ١٠٥، ١٠٧، ١١٤، ١٢٦، ١٢٩، ١٣٠، ١٣١، ١٣٣، ١٣٤، ١٣٦، ١٣٩، ١٤٠، ١٤٧، ١٤٨، ١٤٩، ١٥١، ١٥٢.
٥ - «فائت الجمهرة (والرد على ابن دريد)»، زيادات على الجمهرة لابن دريد (المتوفى سنة ٣٢١/ ٩٣٣)، ورد ذكره في خمس قوائم، وكان من مصادر الصغانى في «التكملة» (انظر ١/ ٨)، و «العباب» (انظر ١/ ٣١). انظر نقلا عنه في خزانة الأدب ٣/ ٤٢١.
٦ - «كتاب النوادر» (١٢٥)، جاء ذكره عند ابن النديم، وفي خمس قوائم أخرى.
٧ - «كتاب المجالسات»، انظر نقلا عنه في «الكنايات» للجرجانى ١٠٥، ولعله
_________
(١٢٥) روى كتابى النوادر لدهمج بن محرز النصرى (يحتمل أنه كان في النصف الثانى من القرن الثانى/ الثامن، انظر ص ٣١ آنفا) ولأبي شبل العقيلى (النصف الثانى من القرن الثانى/ الثامن، انظر ص ٣٤ آنفا) وأصلحهما، انظر تاريخ التراث العربي ٢/ ٨٦، ٨٧ (وليس اسمه أبا شنبل).','1','281',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (278,'من «مجالس ثعلب» بروايته. وربما كان الكتاب هو نفسه «الأمالى» التي ينقل عنها تاج العروس ٢ ١٠/ ٣٢٧ (ثفر).
٨ - «تفسير أسماء الشعراء»، ذكرته كل القوائم (وصحّف عند ياقوت إلى «أسماء القراء»)، وكان مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) - وسمّاه «غريب أسماء الشعراء» - في «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٢).
وله تآليف مفردة لبعض الموضوعات، وأسماء مصنفات غير بينة لعلها تدخل في هذا الباب:
١ - «كتاب العسل والنحل»، نسب في إحدى المخطوطات إلى أبي عمر، وأكبر الاحتمال أنه لأبي حنيفة الدينورى (المتوفى سنة ٢٨٢/ ٨٩٥)، انظر ص ١٦٩ بعد.
٢ - «كتاب اليوم والليلة والشهر والسنة والدهر»، وذكرته ست قوائم باسم «كتاب يوم وليلة»، وقد وصل إلينا، انظر تاريخ التراث العربي ٧/ ٣٥٤ - ٣٥٥؛ نشره م. ج. المعيبد في: مجلة معهد المخطوطات العربية ٢٤/ ١٩٧٨/ ٢٢٩ - ٣٣٨.
٣ - «كتاب الساعات»، ورد ذكره عند ابن النديم وفي خمس قوائم أخرى.
٤ - «كتاب البيوع»، هكذا ذكر في خمس قوائم، وعند ابن النديم «كتاب التنويع».
٥ - «كتاب السريع»، ذكره ياقوت فضلا عن الكتاب السالف، ويحتمل مع ذلك أنه تصحيف لكلمة «البيوع» أو «التنويع».
٦ - «كتاب الشورى»، مذكور عند ابن النديم، وفي خمس قوائم أخرى.

<span data-type="title" id=toc-315>وله في لغة القرآن والحديث:</span>
١ - «ياقوته الصراط في تفسير القرآن»، ولعله جزء من «كتاب اليواقيت»، وقد وصل إلينا، انظر ص ١٥٦ آنفا.
٢ - «كتاب الياقوتة في غريب القرآن»، جزء آخر من مصنفه «كتاب اليواقيت»، انظر ص ١٥٦ آنفا.
٣ - «كتاب غريب الحديث»، مع شروح على «مسند» ابن حنبل.','1','282',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (279,'ومن الجائز أنه كان على ترتيب حروف المعجم، «وكان يستحسنه جدا» (ابن خلكان). مذكور في كل القوائم، وكذلك في تاريخ بغداد ٢/ ٣٥٨ - ٣٥٩؛ وبولس سباط في؛ MIE ٤٦ /١٩٤٦ /٣٧ ,No.٦٦٤: وابن الأثير في مقدمة «النهاية». ويحتمل أنه من مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في كتابه «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣)./ ٤ - «جزء من رواية أبي عمر الزاهد غلام ثعلب في الحديث والأدب»، ويوجد مخطوطا في تشستربيتى ٣٤٩٥/ ١٠ (٩٥ أ- ١٠٩ أ، نسخت سنة ٧٣٨ هـ)؛ نشره أ. ج. آربرى في: مجلة المجمع العلمى العربي بدمشق ٢٤/ ١٩٤٩/ ٢٣٤ - ٢٤٣، ٣٧٢ - ٣٨٤، حديث، الجزء الثالث، الظاهرية، مجموع ١٦ (٨١ أ- ٨٨ أ، من القرن السابع للهجرة، راجع فهرس الحديث ٣٦٩)، راجع أيضا النقول المشابهة في موضوعها عند البكرى، «معجم ما استعجم» ١١٣٩، ١٢٢٧. «فأما رواية الحيث فالمحدّثون يوثقونه على ذلك» (إنباه الرواة للقفطى ٣/ ١٧٢).
ولرضى الدين على بن موسى بن طاوس الحسنى (المتوفى سنة ٦٦٤/ ١٢٦٦) «الاختيارات من كتاب أبي عمر الزاهد في الحديث» عن «كتاب الاجازات» له. لم يصل إلينا، انظر الذريعة ١/ ٣٦٦ - ٣٦٧.

<span data-type="title" id=toc-316>وله في الأخبار </span>(١٢٦):
«كتاب القبائل»، ذكره ابن النديم وغيره. وكانت نسخة منه في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في. (MIE ٤٦ /١٩٤٦ /٤٠ ,No.٧١٦:
_________
(١٢٦) جاء في الأعلام للزركلى ٧/ ١٣٣ أن له «كتاب أخبار العرب» وبقيت منه نسخة خطية؛ ولا علم لى به.
أما «كتاب فضائل معاوية» المنسوب له (انظر (Br.GI ,١١٩ فهو جزء من كتاب قديم لمؤلف مجهول رواه غلام ثعلب (انظر إنباه الرواة للقفطى ٣/ ١٧٢).','1','283',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (280,'<span data-type="title" id=toc-317>ابن مقسم</span>
هو أبو بكر محمد بن الحسن بن يعقوب العطار المقرئ (المتوفى سنة ٣٥٤/ ٩٦٥).
كان نحويا (انظر فصل النحو)، عالما بالقراءات، وصنف في اللغة، وتفسير القرآن، والحديث.

<span data-type="title" id=toc-318>ومن آثاره:</span>
١ - «كتاب النوادر»، ولعله اشتمل أيضا على مسائل ومشكلات في التفسير والحديث، وكان معروفا في الأندلس (انظر فهرست ابن خير ٣٨٠ - ٣٨١).
٢ - «كتاب مجالس (أو مجالسات) ثعلب» (انظر ص ١٤٦ آنفا)، وهو رواية لهذا الكتاب، ذكره ابن النديم وغيره.
٣ - «كتاب الأنوار (في علم «أو تفسير» القرآن)»، «له في التفسير ومعانى القرآن كتاب جليل» (تاريخ بغداد ٢/ ٢٠٦)، «وما رأيت مثله» (إرشاد الأريب لياقوت ٦/ ٤٩٨، راجع «إنباه الرواة» للقفطى ٣/ ١٠٠ - ١٠١، «البصائر والذخائر» للتوحيدي ٢/ ٣٧٨).
٤ - «كتاب في قوله تعالى: وَمَنْ يَقْتُلْ ... »، ذكره ياقوت والصفدى بين مصنفاته.
٥ - «الرد على المعتزلة»، لعله انتقاد لتأويلهم القرآن، ذكره ياقوت والصفدى والسيوطى.
٦ - «جزء فيه من حديث ... ابن مقسم»، ويوجد مخطوطا في دار الكتب بالقاهرة، حديث ١٥٥٨ (ص ١٨٩ - ١٩٩، من القرن الثامن للهجرة)، ومنه نسخة بالدار، ٢٥٦١٧ ب (٨ ورقات، نسخت سنة ١٣٥١ هـ، انظر الملحق ١/ ٢١٥).
وانظر في ابن قسم باعتبره محدّثا: تاريخ بغداد ٢/ ٢٠٦./','1','284',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (281,'<span data-type="title" id=toc-319>ج بغداد ومواضع أخرى</span>
<span data-type="title" id=toc-320>الليث</span>
هو الليث بن المظفّر (أو رافع) بن نصر بن سيّار، حفيد والى خراسان (نحو سنة ١٢٠/ ٧٣٨).
وكان كاتبا للبرامكة في بغداد. ويوصف بأنه كان عالما، برع في اللغة والنحو ورواية الشعر. وتوثقت الصداقة بينه وبين الخليل بن أحمد. وتذهب إحدى الروايات إلى أن الخليل سمع بشهرة الليث، فارتحل إليه وصنف له «كتاب العين» رمزا لصداقته وأهداه إليه (انظر إرشاد الأريب لياقوت ٦/ ٢٢٣).
وثمة روايات متفاوتة جدا فيما صار إليه الكتاب من بعد، ولا تقتصر على ذلك بل تتفاوت أيضا في مدى ما للخليل من نصيب في تأليف «كتاب العين» الذى أصبح متداولا نحو أواخر القرن الثانى/ الثامن، وفي مدى ما صنعه الليث فيه (انظر ص ٥٢ آنفا). ويكثر ورود نقل باسم الليث (١٢٧) في كتب اللغة، وقد تفادى كثير من العلماء هذا الإشكال، فنقلوا عن الكتاب بقولهم: «قال صاحب العين». ويبين لنا نقل أورده الأزهرى أن الليث كان على علم بتعريف حساب الكسور (•). ويحتمل أنه توفى نحو سنة ١٩٠/ ٨٠٥.

<span data-type="title" id=toc-321>مصادر ترجمته:</span>
«طبقات الشعراء» لابن المعتز ٣٨ - ٣٩؛ «مراتب النحويين» لأبي الطيب ٣١؛ «التهذيب للأزهرى ١/ ٢٨ - ٢٩، ٤٠ - ٤١؛ «إرشاد الأريب» لياقوت ٦/ ٢٢٢ - ٢٢٧؛ «إنباه الرواة» للقفطى ٣/ ٤٢ - ٤٣؛ «بغية الوعاة» للسيوطى ٣٨٣.-
St. Wild, Neueszuraltesten arabischen Lexikographiein: ZDMG ١١٢/ ١٩٦٢/ ٢٩٢
_________
(١٢٧) للأزهرى (٣/ ٤٣٦) تنبيه مهم، هذا نصه: «قلت: وهذا صحيح من قول الخليل، وهو كما حكاه عن الليث، وكلما قال: قلت للخليل فقال، أو قال: سمعت الخليل، فهو الخليل بن أحمد لا تدليس فيه؛ وإذا قال: قال الخليل، ففيه نظر».
(•) «قال الليث: والكسر من الحساب ما لم يكن سهما تامّا، والجميع: الكسور» (الأزهرى ١٠/ ٥٠).','1','285',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (282,'<span data-type="title" id=toc-322>العتّابى</span>
هو أبو عمرو كلثوم بن عمرو بن أيوب، شاعر عباسى (من المحتمل أنه توفى سنة ٢٠٨/ ٨٢٣، انظر تاريخ التراث العربي ٢/ ٥٤٠ - ٥٤١).
اشتغل كذلك باللغة والأدب (انظر فصل الأدب)؛ ويمتدح لبلاغته (انظر الفصل نفسه)./

<span data-type="title" id=toc-323><span data-type="title" id=toc-325>ومن آثاره:</span></span>
١ - «كتاب الألفاظ»، عرفه ابن النديم (١٢١) في رواية أبي عمر الزاهد عن المبرد.
٢ - «كتاب الخيل»، ذكره ابن النديم ١٢١.
٣ - «كتاب المنطق»، ذكره ابن النديم ١٢١.
<span data-type="title" id=toc-324>الدّورى</span>
هو أبو عمر حفص بن عمر بن عبد العزيز، قارئ جليل، يعدّ أيضا من النحويين. توفى سنة ٢٤٠/ ٨٥٤ أو سنة ٢٤٦/ ٨٦٠ (انظر تاريخ التراث العربي ١/ ١٣).
ومن آثاره:
١ - «كتاب ما اتفقت ألفاظه ومعانيه من القرآن! »، ذكره ياقوت في إرشاد الأريب ٤/ ١١٨ - ١١٩.
٢ - «كتاب أجزاء القرآن (١٢٨)»، المرجع نفسه.
_________
(١٢٨) استدراك على تاريخ التراث العربي ١/ ١٣ يتعلق بقراءته: «قراءة قالون والدورى» لعثمان ابن عمر بن أبي بكر الناشرى (المتوفي سنة ٨٤٨/ ١٤٤٤)، المخطوطات: المتوكلية بصنعاء، قراءة ٤٨ (٤٦ ورقة، ضمن مجموع، ومنه مصورة بمعهد المخطوطات بالقاهرة،','1','286',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (283,'<span data-type="title" id=toc-326>أبو الهيثم الرازى</span>
كان نحويا ولغويا، ولم يعرف إلا بكنيته. أصله من الرىّ، وقدم هراة قبل وفاة أستاذه شمر سنة ٢٥٥ هـ. وقيل إنه قرأ كتب شمر وتعلم منها ثم ردّ عليها، وهو ما شكا منه شمر بقوله: «تسلّح الرازىّ عليّ بكتبى». وكان تلميذه المشهور هو المنذرى، توفي أبو الهيثم سنة ٢٧٦/ ٨٨٩.

<span data-type="title" id=toc-327>مصادر ترجمته:</span>
التهذيب للأزهرى ١/ ٢٦؛ ابن النديم ٧٨؛ «إنباه الرواة» للقفطى ٤/ ١٨٢.

<span data-type="title" id=toc-328>ومن آثاره:</span>
١ - «كتاب مجرّد اللغة»، ذكره ابن النديم، وترجع إليه فيما يظهر نقول كثيرة أوردها الأزهرى في «التهذيب» (انظر ١/ ٢٦) عن مصنفات المنذرى ص ١٩٤ بعد). وثمة نقول أيضا في «التكملة» للصغانى ١/ ٤٧، ٤٨، ٥٧، ١٩٩، ٣٤٦، ٢/ ٢٤، ٩٩، ١٦١، ١٨٩، ٢٢٣، ٢٩٥، ٣٠٤، ٤٠٥، ٤٧٠، ٥٠٧، ٥١١، ٣/ ١٣٣، ١٥٤، ٢٧٢، ٢٨٧، ٣١٨، ٥١٢، ٥٢١، ٤/ ٥٨، ٨٠، ٩٠، ١٠٤، ١٧٨، ٢٣٦، ٢٦٦، ٢٦٧، ٤٩٢، ٥٠٧، ٥١٨، ٥٦٣، ٥٦٨. وانظر في النقول الكثيرة الواردة في «معجم الأمثال» للميدانى: ع. التكريتى في: المورد ٣، ٣/ ١٩٧٤/ ١٠٢ - ١٠٣.
وفضلا عن ذلك أفاد الأزهرى (٧/ ٦١١) من «نوادر» ابن بزرج بخط أبي الهيثم و (٢/ ٢٢٤) شرح أبي/ عمرو الشيبانى على شعر ابن مقبل، وكذلك (١٤/ ٣٩٢) «كتاب البقر» لأبي حاتم بخط أبي الهيثم.
_________
انظر مجلة معهد المخطوطات العربية ٢٢/ ١٩٧٦/ ٢٠، رقم ١١٨)، الرياض، مكتبة الجامعة ١٢١٢/ ١ (الأوراق ١ - ٢٤، نسخت سنة ٩٧٥ هـ، انظر فهرس القرآن ٦٦ - ٦٧).','1','287',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (284,'<span data-type="title" id=toc-329>ابن قتيبة</span>
هو أبو محمد عبد الله بن مسلم بن قتيبة الدينورى، ولد في الكوفة «على أرجح الأقوال» سنة ٢١٣/ ٨٢٨، وأكمل دراساته من بعد في بغداد.
ومن أساتذته أبو حاتم السجستانى والرياشى وإسحاق بن ابراهيم الموصلى الذين ظهرت آثارهم في ثقافته.
ولم يقتصر ابن قتيبة على علوم اللغة، بل امتد اشتغاله بالعلم إلى كل العلوم النظرية الإسلامية تقريبا وإلى بعض العلوم الطبيعية (انظر تاريخ التراث العربي ٣/ ٣٧٦ وما بعدها، ٤/ ٣٤٤، ٧/ ٣٥٠ وما بعدها).
وفي النحو يعد أول من خلط مذهبي الكوفيين والبصريين. ومما يميزه، مع ذلك، باعتباره عالما اهتماماته الأدبية المتعددة الجوانب، وعلمه الموسوعى الذي حاول بمعونته سدّ حاجة معاصريه إلى التعليم. وتولى ابن قتيبة قضاء الدّينور مدة، وتوفي سنة ٢٧٦/ ٨٨٩ (على الرغم من اختلاف الروايات) في بغداد.

<span data-type="title" id=toc-330>مصادر ترجمته:</span>
«مراتب النحويين» لأبي الطيب ٨٤ - ٨٥؛ «طبقات النحويين» للزبيدى ٢٠٠؛ ابن النديم ٧٧ - ٧٨؛ «التهذيب» للأزهرى ١/ ٣٠ - ٣١؛ «تاريخ العلماء» للتنوخى ٨٧ ب؛ «نزهة الألباء» للأنبارى ٢ ١٤٣ - ١٤٤؛ «تاريخ بغداد» ١٠/ ١٧٠ - ١٧١؛ «إنباه الرواة» للقفطى ٢/ ١٤٣ - ١٤٧؛ مخطوط في طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٢٢ أ- ٢٢٢ ب، ٢٦٧ أ؛ ابن قاضى شهبة، المخطوط ص ٣٤٥ - ٣٤٧؛ «مسالك الأبصار» لابن فضل الله ٤/ ٢٣٥ - ٢٣٦؛ «الوفيات» لابن قنفذ ١٨٨ - ١٨٩؛ «لسان الميزان» لابن حجر ٣/ ٣٥٧ - ٣٥٩؛ «بغية الوعاة» للسيوطى ٢٩١.
Flugel, Gramm. Schulen ١٨٧ - ١٩٠,; Br. GI, ١٢٠ - ١٢٣, SI, ١٨٤ - ١٨٧; Rescher, Abri BII, ١٩٥ - ١٩٨;
محمد كرد على، «كنوز الأجداد» ٨٨ - ٩٦
I. M. Al- Husaini, The Lifeand Worksof Ibn Qutayba. Beirut ١٩٥٠.','1','288',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (285,'محمد زغلول سلام، ابن قتيبة، القاهرة ١٩٥٧؛ «الأعلام» للزركلى ٤/ ٢٨٠، «معجم المؤلفين» لكحالة ٦/ ١٥٠ - ١٥١؛
G. Lecomte, Ladescendanced''I bn Qutaybaen Egyptein: Etudes Levi- Proven- Qal, Paris ١٩٦٢, I, ١٦٥ - ١٧٣; ders., Lesdisciplesdire ctsd''Ibn Qutaybain: Arabica ١٠/ ١٩٦٣/ ٢٨٢ - ٣٠٠; ders., Ibn Qutayba (morten ٢٧٦/ ٨٨٩) . L''Homme, T. Fahdin: Rev. d''hist. et
عرض له
(Bonoeuver, sesidees. Damaskus ١٩٦٥ dephilos. rel. ٢/ ١٩٦٨/ ١٨٩ - ١٩٠) ; P. Kunitzschin: Dict. Sc. Bio ١١/ ١٩٧٥/ ٢٤٦ - ٢٤٧.
عبد الله الجبورى، «دراسات في كتب ابن قتيبة» في: مجلة آداب المستنصرية ٢/ ١٩٧٧/ ١٠٥ - ١٣٢، ٣/ ١٩٧٨/ ٢٢٣ - ٢٥٠.
وفي مصادره المباشرة وغير المباشرة انظر رأى أبي حامد الخارزنجي (المتوفى سنة ٣٤٨/ ٩٥٩) وردّ الأزهرى (التهذيب ١/ ٣٣، ٣٤) وسنذكر أسماء مصنفاته التى لم تصل إلينا استنادا إلى قوائم كتبه الواردة عند ابن النديم ٧٧ - ٧٨ (طهران، ص ٨٥ - ٨٦، الترجمة الانجليزية ١٧١ - ١٧٢)؛ الأزهرى ١/ ٣٠ - ٣١، «إنباه الرواة» للقفطى ٢/ ١٤٤ - ١٤٦؛ «تاريخ بغداد» ١٠/ ١٧٠؛ ابن خلكان ١/ ٣١٤؛ «نزهة الألباء» للأنبارى ١٤٤؛ «بغية الوعاة» للسيوطى ٢٩١./

<span data-type="title" id=toc-331>في موضوعات عامة، وبعض المسائل اللغوية، والاشتقاق، والأمثال </span>(١٢٩):
١ - «كتاب التقفية» (خطأ «التفقيه»)، ذكر في ثلاث قوائم، وهو معجم مرتب على الحرف الأخير، مثل كتاب التقفية للبندنيجى (المتوفى سنة ٢٤٨/ ٨٩٧، انظر
_________
(١٢٩) «كتاب الألفاظ المغربة بالألقاب المعربة»، موضوع على غرار «كتاب الملاحن» لابن دريد.
ويوجد مخطوطا في القرويين بفاس ٨٠/ ٣٧٠ (٥٤ ورقة، نسخة قديمة، راجع فهرس بيل رقم ١٢٦٢؛ ومنها مصورة بمعهد المخطوطات بالقاهرة)، ليس من تأليف ابن قتيبة المترجم له، بل ألّفه عيسى بن ابراهيم بن عيسى بن قتيبة الذي يقال إنه عاش على عهد العبّاديين (٤١٤/ ١٠٢٣ - ٤٨٤/ ١٠٩١).','1','289',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (286,'ص ١٧١ بعد). ورأى منه ابن النديم ثلاثة أجزاء (من خمسة) في نحو ستمائة ورقة، ونبّه إلى أنه أكبر من كتاب البندنيجى وأحسن منه.
٢ - «المدخل في اللغة»، وهو من مصادر حبيش بن إبراهيم بن محمد التفليسى (المتوفي سنة ٦٢٩/ ١٢٣٢) في كتابه «قانون الأدب» (انظر حسين على محفوظ في:
مجلة المجمع العلمى العربي بدمشق ٥٠/ ١٩٧٥/ ٤٠٢).
٣ - «المسائل والأجوبة» يتناول مشكلات في لغة القرآن والحديث وبعض مسائل لغوية، وقد وصل إلينا، ونشر، انظر ص ١٦٣ بعد.
٤ - «كتاب الاشتقاق»، لم يذكر في القوائم ولا في مصدر آخر، وزعم الزركلى ٤/ ٢٨٠ أنه بقى مخطوطا، وهو أمر مشكوك فيه جدا.
٥ - «كتاب حكم الأمثال»، كذا ورد هذا العنوان غير المألوف عند ابن النديم، راجع Lecomte ,Ibn Qutayba ,S.١٦٢

<span data-type="title" id=toc-332>وله تآليف مفردة في بعض الموضوعات:</span>
١ - «كتاب خلق الإنسان» ذكره ابن النديم والسيوطى على أنه كتاب قائم بذاته (انظر أيضا «كشف الظنون» ٧٢٢)، ولعله كتاب لغوى يوافق باب «خلق الانسان» من كتابه «أدب الكاتب».
٢ - «كتاب الخيل» أو «كتاب الفرس»، ولعله تأليف في اللغة قائم بذاته، ولم يصل إلينا، انظر فصل الأدب.
٣ - «كتاب الوحش»، ولعله مصنف في اللغة مستقل بنفسه، ولم يصل إلينا، انظر فصل الأدب.
٤ - «كتاب النبات»، وذكر الزركلى ٤/ ٢٨٠ أنه يوجد مخطوطا. ولعل المقصود هو باب النبات من «كتاب الجراثيم» (انظر آنفا ص ٢٥٨ وما بعدها).
٥ - «كتاب الرياح»، ولعل كتاب قائم بذاته، ولم يصل إلينا انظر فصل الأدب؛ وتاريخ التراث العربي ٧/ ٣٥١.
٦ - «كتاب الأنواء»، وقد وصل إلينا ونشر، انظر تاريخ التراث العربي ٧/ ٣٥١.','1','290',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (287,'<span data-type="title" id=toc-333>وله في لغة القرآن والحديث:</span>
١ - «كتاب (تأويل) مشكل القرآن (١٣٠)» أو «كتاب المشكل» (ابن النديم).
وهو واحد من أشهر كتبه، ذكرته سبع قوائم (راجع فهرست ابن خير ٦٧)، ونقل عنه كثير.

<span data-type="title" id=toc-334>المخطوطات:</span>
كوبريلى ٢١١/ ١ (الأوراق ١ - ١١٦ أ، نسخت سنة ٥٤٣ هـ، راجع الحاشية، (H.Ritterin: Islam ١٨ /١٩٢٩ /٣٧، مراد ملا ٧٤ (١١٧ ورقة، نسخت سنة ٥٣٢ هـ، راجع فهرس المخطوطات ١/ ٤٥)، فيض الله ٢٣٢ (٤٤١ ورقة، نسخت سنة ٦١٩ هـ) سراى، مدينة ١٩٥ (٢٠٣ ورقة، من القرن السادس للهجرة، راجع الفهرس ١/ ٤٣٩ - ٤٤٠، وفي فهرس المخطوطات ١/ ٤٥ بيانات خطأ)، يوسف أغا بقونيه ٣٧٢٨ (ص ١٤٧٩ - ١٥٧٧، نسخت سنة ١١٩٢ هـ)، طهران، / دانشكاه ٦٨ (١١١ ورقة، من القرن ١٣ هـ، انظر الفهرس ١/ ٢٠٩)، و ١٤٤١ (١٥٠ ورقة، نسخت سنة ٩٥٠ هـ، انظر الفهرس ٨/ ١٠٧ - ١٠٨)، رضا برامبور، غريب القرآن ٤ (١٦٤ ورقة، انظر الفهرس ١/ ٥٨)، مكتبة السماوى (نسخة حديثة، انظر مجلة معهد المخطوطات ٤/ ١٩٥٨/ ٢١٥، رقم ١٧)، الظاهرية عام ٧٩٣٢ (١١٠ ورقة، نسخت سنة ٥٩٢ هـ، راجع فهرس عزة حسن ١/ ٣٩٥)، دار الكتب بالقاهرة، تفسير ٥١٨ (نسخت سنة ٥٥٨ هـ)، وتفسير ٦٦٣ (٨٦ ورقة، نسخت سنة ٣٧٩ هـ، انظر فهرس المخطوطات ١/ ٤٥)، عارف حكمت بالمدينة، تفسير ٩ (١٣٤ ورقة، نسخت سنة ٦٦٦ هـ)، ومنها مصورة بمعهد المخطوطات العربية بالقاهرة، القرويين بفاس ٨١٨ (٨ ورقات من الجزء الثانى، من القرن الخامس للهجرة، انظر فهرس الفاسى ٢/ ٤٩٠ - ٤٩١)، و ٩٤٤/ ١ (١ أ-
_________
(١٣٠) فيما يتعلق بالبيانات التى تزعم أن لابن قتيبة «تفسير سورة النور»، المطبوع بالقاهرة سنة ١٣٤٣ (انظر، Lecomte الموضع المذكور ص ١٦٠) فإنه يبدو على الأرجح أنه اشتبه بكتاب «تفسير سورة النور» لابن تيمية الذي طبع في القاهرة سنة ١٣٤٣ (انظر مشار، فهرست ٢٠٤).','1','291',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (288,'٦٠ ب، راجع فهرس بيل رقم؛ Li stedesmanuscrits ٩ ,No.٦٩ /٨٠؛ ٢٢١ مجلة معهد<span data-type="title" id=toc-335> المخطوطات </span>٥/ ١٩٥٩/ ١٤، رقم ٥٨ (، آيت عياش، الحمزاوية ٢٠٢، اليوم في الرباط؟ (الجزء الأول، نسخ سنة ٦٧٦ هـ، انظر محمد المنونى في: تطوان ٨/ ١٩٦٣/ ١١٢)، المكتبة العامة بالرباط، الكتانى ٢٩٤٦ (قطعتان، ٤٨ ورقة، من القرن الرابع للهجرة)، ومنها مصورة بمعهد المخطوطات بالقاهرة، الفاتيكان، بورجيا) ٢/ ١٣٢ ar.، الأوراق ١٦ - ٦٠، أولها ناقص، نسخت سنة ٨٣٩ هـ، انظر قيدا ١/ ٢٥٩ - ٢٦٠)، ليدن ١٨٦) ٧٠٤ Or.، ورقة، نسخت سنة ٤٠٤ هـ، انظر فورهوف ٢٤٢)، المتحف البريطانى، مجموعة بلشاه (انظر
. Edwardsin: . (Festschr. E. G. Browne, Cambridge ١٩٢٢, S- ١٣٧, No. ٣
نشره على مخطوطات القاهرة ومراد ملا السيد أحمد صقر، القاهرة ١٩٥٤، انظر في هذه النشرة جورج شحاته قنواتى في MIDEO ٢ /١٩٥٥ /٢٧٢ - ٢٧٥ وانظر أيضا G.Lecomte في الموضع المذكور، ص ١٤٠ - ١٤٣؛ وانظر في محتوى الكتاب ع. الشلقانى، مصادر اللغة، الرياض ١٩٨٠، ص ٣٧ - ٤٤.
وقد ألفت في التعليق على كتاب «تأويل مشكل القرآن» رسائل وكتب، منها:
(أ) «رسالة في المشكل» لابن الأنبارى (المتوفى سنة ٣٢٨/ ٩٤٠)، وهى رد على ابن قتيبة وأبي حاتم السجستانى، انظر ص ١٥٣ آنفا.
(ب) «كتاب القرطين»، صنعة ابن مطرّف (المتوفى سنة ٤٥٤/ ١٠٦٢)، وقد وصلت إلينا ونشرت، أنظره تحت «كتاب غريب القرآن».
(ج) «الانتصار لحمزة الزيات فيما نسبه إليه ابن قتيبة في مشكل القرآن» لأبي القاسم عبد الله بن محمد بن أحمد بن المعلم العكبرى (المتوفى سنة ٥١٦/ ١١٢٢، انظر «معجم المؤلفين» لكحالة ٦/ ١٠٩)، ذكر في طبقات السبكى ٤/ ٢٣٦)؛ و «كشف الظنون» ١٧٣.
٢ - «كتاب (تفسير) غريب القرآن»، ذكر في خمس قوائم (راجع فهرست ابن خير ٦٦).
المخطوطات:
يوسف أغا بقونيه ٦٧٤١ (٢٩٥ ورقة، نسخت سنة ٥٦٦ هـ، راجع Listedes','1','292',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (289,'manuscrits ١٥
؛ أحمد آتش في، (Belleten ١٦ /١٩٥٢ /٦١ الحيدرية بالنجف ٧٣٤ (نسخة قديمة، آخرها ناقص، انظر فهرس الحسينى ٧٠، رقم ١٤٢)، الظاهرية، عام ١٥٧١ (٣٠٠ ورقة، آخرها ناقص، من القرن السادس للهجرة، انظر ح.
الكسم في: مجلة المجمع العلمى العربي بدمشق ١٢/ ١٩٣٢/ ٧٠٣، وراجع محمد كرد على في: مجلة المجمع العلمى العربي بدمشق ١٨/ ١٩٤٣/ ١٨٣؛ وفهرس عزة حسن ١/ ٣٧٨؛ وفهرس اللغة ١١٠)، التيمورية بالقاهرة، لغة ٥٣٩، الطائف (انظر ع. قارى في: مجلة السلف، السنة الثانية، ص ٨ عن G.Lecomte في الموضع المذكور، ص ١٣٥)، الرياض، مكتبة الجامعة ٣٢١ (٢١٥ ورقة، نسخت سنة ١٣٥٢ هـ، انظر الفهرس، القرآن ١٠٤ - ١٠٥).
نشره السيد أحمد صقر، القاهرة ١٩٥٨، وأعيد طبعه في بيروت ١٩٧٨، انظر في هذه النشرة جورج شحاته قنواتى في، MIDEO ٥ /١٩٥٨ /٣٩٨ - ٣٩٩: وانظر أيضا G.Lecomte في الموضع المذكور، ص ١٣٤ - ١٣٧.
وقد ألّف محمد بن أحمد بن مطرّف كتابا في التعليق عليه سماه «كتاب القرطين»، وهو تهذيب لهذا الكتاب مع مشكل القرآن، انظر «الأعلام» للزركلى ٦/ ٢٠٦)، ويوجد مخطوطا في التيمورية بالقاهرة، لغة ٥٩ (راجع ع. أ. المعلوف في: مجلة المجمع العلمى العربي بدمشق ٣/ ١٩٢٣/ ٣٣٩ - ٣٤٠)؛ نشره عبد الحفيظ سعد عطية في جزءين، القاهرة ١٩٣٧.
٣ - «كتاب معانى القرآن، ذكره السيوطى وآخرون، ولعله هو نفسه «غريب القرآن» انظر G.Lecomte في الموضع المذكور، ص ١٣٦، ١٦٧.
٤ - «كتاب المسائل» (فهرست ابن خير ٣٧٨، «التسمية للمالكى» ضمن كتاب «الخطيب البغدادى» ليوسف العش ٩٩، رقم ١٦٢)، وأكمل من ذلك تسميته «كتاب المسائل والجوابات» (ابن النديم وثلاث قوائم أخرى)، ولعله هو نفسه/ «كتاب الجوابات الحاضرة» (السيوطى وكشف الظنون)، «في معانى غريب القرآن والحديث مما لم يقع في كتاب الغريب» (فهرست ابن خير ١٩٥). وقد وصل إلينا جزء منه يشتمل على مسائل في الحديث.','1','293',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (290,'<span data-type="title" id=toc-336><span data-type="title" id=toc-337>المخطوطات:</span></span>
جوتا ٦٣٦ (١٤ ورقة، نسخت سنة ٧٧٨ هـ)، رئيس الكتاب ٨٧٩/ ١ (١ أ- ١٣ أ، من القرن ١١ هـ، راجع؛ O.Rescherin: MFO ٥ /١٩١٢ /٥١٢: فهرس المخطوطات ١/ ٩٩ برقم خطأ)، ومنها نسخة بدار الكتب بالقاهرة، لغة ٦ ش/ ١ (الأوراق ١ - ٥، نسخها الشنقيطى، انظر الفهرس الثانى ٢/ ٣٠)، ومنها مصورة في المجمع العلمى العراقي ببغداد ٧٦ م.
وطبع باسم «كتاب المسائل والأجوبة في الحديث واللغة»، القاهرة ١٣٤٩، ونشره ش. العاشور في: المورد ٣، ٤/ ١٩٧٤/ ٢٣٣ - ٢٥٢. وفي «الاقتضاب» لابن السيد ٢٠٧ نقل لا نقع عليه في النص الذي وصل إلينا؛ ويقوم شاكر الفحام (دمشق) بتحقيق مخطوطة أوسع موجودة في حلب، انظر أيضا G.Lecomte في الموضع المذكور، ص ١٢٦ - ١٢٨.
٥ - «كتاب غريب الحديث»، ذكر في ست قوائم «وجاء اسمه «شرح غريب الحديث» في فهرست ابن خير ١٨٧، و «الاقتضاب» لابن السيد ١٢٩ وفي مواضع أخرى)، ونقل عنه ابن قتيبة نفسه كثيرا.
المخطوطات:
الظاهرية عام ١٥٧٢ (الجزء الأول، ١٣٩ ورقة، نسخت سنة ٥٧١ هـ)، وعام ١٥٧٣ (الجزء الثالث، ١٣٤ ورقة، نسخت سنة ٥٧٣ هـ)، وعام ٧٨٩٩ (بداية الجزء الأول باسم «كتاب غريب ألفاظ الأحكام»، ٥٨ ب- ٦٥ ب، انظر فهرس الألبانى ٩٣ - ٩٤، وراجع فهرس اللغة ١٠٢ - ١٠٦)، الغربية بصنعاء، بدون رقم (الجزء الثانى، ١٣١ ورقة، نسخت سنة ١٠٦٠ هـ)، ومنها مصورة بمعهد المخطوطات بالقاهرة، آيت عياش، الحمزاوية (اليوم في الرباط؟ ) ١٤ (الجزء الرابع، نسخ ٥١٧ هـ، انظر محمد المنونى في: تطوان ٨/ ١٩٦٣/ ١١٩)، مراكش، اليوسفية (اليوم في الرباط؟ ) ١٥٦ (من القرن السابع للهجرة).
نشره عبد الله الجبورى، بغداد ١٩٧٧، وكذلك ر. السويسى، المجلد الأول، تونس ١٩٧٩.
الدراسات:
G. Lecomte, Aproposdelaresur gencedesouvrages d''Ibn Qutaybasurlehadi t','1','294',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (291,'oux VIe/ XIIeet VIIe/ XIIIesiecles. Lecertificatsde Lectureduk. Garibal- hadit etk. Islahal- galat .. in: BEO ٢١/ ١٩٦٨/ ٣٤٧ - ٤٠٩;-, Ibn Qutayba ١٤٧ - ١٥١.
وألّف في التعليق على كتاب «غريب الحديث» كتب منها:
(أ) «الرد على ابن قتيبة في غريب الحديث» للغدة الاصفهانى (المتوفى سنة ٢٨٠/ ٨٩٣)، ولم يصل إلينا، انظر ص ١٦٧ بعد.
(ب) «الدلائل في شرح ما أغفله أبو عبيد وابن قتيبة في غريب الحديث» لقاسم بن ثابت السرقسطى (المتوفى سنة ٣٠٢/ ٩١٥)، مع التنبيه على أخطاء كتاب ابن قتيبة ونظائرها في كتاب أبي عبيد (المتوفى سنة ٢٢٤/ ٨٣٩)، وقد وصل إلينا، انظر ص ٢٥٢ بعد.
(ج) «تقريب الغريبين» لأبي الفتح سليم بن أيوب بن سليم الرازى (المتوفى سنة ٤٤٧/ ١٠٥٥) توسط فيه بين آراء أبي عبيد وابن قتيبة، وقد وصل إلينا، انظر ص ٨٦ آنفا.
٦ - «إصلاح الغلط (الواقع) في غريب الحديث»، وهو انتقاد لكتاب أبي عبيد، ذكرته خمس قوائم (انظر أيضا فهرست ابن خير ١٨٨)، ونقل عنه السيوطى في الأشباه والنظائر ٤/ ١٠٨.

<span data-type="title" id=toc-338>المخطوطات:</span>
آيا صوفية ٤٥٧/ ٢ (٢٢ أ- ٥٤ ب، ٦٠١ هـ، انظر؛ H.Ritterin: Islam ١٨ /١٩٢٩ /٣٧: فهرس المخطوطات ١/ ٥٧، ٣٤١)، الظاهرية، عام ٧٨٩٩ (٥١ أ- ٥٨ أ، انظر G.Lecomte ,Ibn Qutayba ١٥٢ - ١٥٣ راجع فهرس الألبانى رقم ٣٥٩).
نشره G.Lecomte في، MUSJ ٤٤ /١٩٦٨ /١٥٥ - ٢٣٦: وراجع له
Leprobleme d''Abu''Ubayd. Re Flexionssurles'''' erreurs''''queluia ttribue Ibn Qutaybain: Arabica ١٢/ ١٩٦٥/ ١٤٠ - ١٧٤
؛ وله أيضا) Aprop osdelaresurgence انظر آنفا)، وقد شرحه أبو المظفر محمد بن آدم بن كمال الهروي (المتوفى سنة ٤١٤/ ١٠٢٣ في كتاب سماه «شرح إصلاح الخطأ ... » انظر ص ٢٢٧ بعد)، ذكر في «كشف الظنون» ١٠٨.
٧ - «كتاب (تأويل) مختلف الحديث» أو «كتاب اختلاف تأويل الحديث» (ابن/','1','295',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (292,'النديم أو «كتاب مختلف الحديث المدّعى عليه التناقض (١٣١)» (فهرس ابن خير ١٩٩).

<span data-type="title" id=toc-339>المخطوطات:</span>
أسعد ١٦١ (١١٢ ورقة، نسخت سنة ١١٦١ هـ، انظر، H.Ritter: in Islam ١٧ / (١٩٢٨ /٢٥٦ ,١٨ /١٩٢٩ /٣٧ راغب ٢٦١ (١٣٠ ورقة، نسخت سنة ١١٧٣ هـ، انظر المرجع نفسه)، رئيس الكتاب ١٠٧ (١٣٢) (١٦٣ ورقة، من القرن الثامن للهجرة)، الظاهرية، حديث ٣٠٣ (١٥٤ ورقة، نسخت سنة ٥٨٢ هـ)، دار الكتب بالقاهرة، مجموع ٢٠٠ م/ ٢ (٢٠ أ- ١٢٣ أ، نسخت سنة ١٢٥٣ هـ، وعنوانها «كتاب الرد على من قال بتناقض الحديث»، راجع الفهرس ١ ٧، ٢/ ٦٨٠)، التيمورية، حديث ٢٢٢ (الجزء الأول من الكتاب الأول، انظر الفهرس ٢/ ٢٦٥)، الأوقاف ببغداد ٦٦٦٧ (٩٤ ورقة، نسخت سنة ٤٧٢ هـ، انظر كوركيس عواد في: سومر ٣/ ١٩٤٧/ ٢٣٩؛ طلس ٣٦، رقم ٢٧٨؛ فهرس الجبورى ١/ ١٩٦ - ١٩٧)، برلين ١٢٦٢ (١٠٤ ورقة، نسخت نحو سنة ٦٠٠ هـ)، ليدن ٢٠٨) ٨٨٢ Or.، ورقة، انظر فورهوف ٢٢٧)، المتحف البريطاني) ٢/ ٣١٠٧ Or.، الأوراق ١٠ - ٥٣، نسخت سنة ٨٢٨ هـ، انظر الملحق رقم ١٢٠٤)، تشستربيتى ٤٤٠٩ (١٣١ ورقة، نسخت سنة ٦٧٣ هـ)؛ نشره ف. ز. الكردى، ومحمود شكرى الآلوسى، ومحمود شابندرزاده، القاهرة ١٣٢٦، وطبع مرة أخرى في القاهرة ١٩٦٦، وترجمه لكونت إلى الفرنسية-
G. Lecomte, Letraitedesdiver gencesduhaditd''I bn Qutayba, Damaskus ١٩٦٢.
(انظر في هذه الترجمة)
T. Fahdin: Rev. d''hist. etdephilos. relig. ٢/ ١٩٦٨/ ١٩٠ - ١٩١
الدراسات:
G. Lecomte, Unexempled''evolu tiondelacontrove rseen Islam: de L''Ihtilaf al- haditd''al- Safi''iau Muhtalifal- habitd''Ibn Qutaybain: Stud. Isl. ٢٧/ ١٩٦٧/ ٥ - ٤٠;
_________
(١٣١) قارن عنوان مخطوطة برلين: «كتاب تأويل مختلف الحديث والردّ على من يريب في الأخبار المدّعى عليها التناقض».
(١٣٢) ليس عاشر أفندى ٧٠١ كما ذكر بروكلمن وريتر ولكونت.','1','296',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (293,'-, APropos D''unevariantecor aniquechez Ibn Qutaybain: Arabica ١٠/ ١٩٦٣/ ٣١٠ - ٣١١;, Lescitationsde L''Ancienetdu Nouveau Testamentdans L''oeuvred''Ibn G. Vajdain: Rev. deset. juives
وراجع، Qutaybain: Arabicn ٥ /١٩٥٨ /٣٤ - ٤٦ ;M.Talbi وانظر G.Lecomte ,Ibn Qutayba ١٣٧ - ١٤٠ وانظر أيضا
٩٩/ ١٩٣٥/ ٦٨ - ٨٠ Lesdivergencesdu Hadithin: Cah. de Tunisie ١٤/ ١٩٦٦/ ٢٦١.
وقد اختصره محمود بن طاهر بن المظفّر السنجارى في كتاب سماه «المغيث من مختلف الحديث»، ويوجد مخطوطا في المكتب الهندى بلندن ب ٨٦ (٦٥ ورقة، حوالى القرن العاشر للهجرة، انظر لوث رقم ١٩٦)، وآيا صوفية، حديث ١٣٥ (١٢٦٤ هـ، انظر الفهرس ١/ ٦٧٤).
٨ - «كتاب مشكل الحديث»، ذكرته أربع قوائم، وامتلكه الخطيب البغدادى مع «غريب الحديث» (انظر التسمية للمالكى في كتاب «الخطيب البغدادى» ليوسف العش ٩٧، رقم ١٢١).

<span data-type="title" id=toc-340>إسماعيل بن أبي محمد اليزيدى</span>
هو أبو على اسماعيل بن يحيى بن المبارك، أحد أبناء أبي محمد اليزيدى الخمسة (انظر فصل النحو).
وكان أديبا ولغويا نحويا. وراوية للشعر (انظر فصل الأدب)، وكان هو نفسه شاعرا (انظر تاريخ التراث العربي ٢/ ٦١٠). وكان من رواة أبي العتاهية ومحمد ابن سلام الجمحى. وقد نظم أيضا قصيدة في رثاء على بن يحيى المنجم (المتوفى بعد سنة ٢٧٥/ ٨٨٨)، وعلى هذا لا بد أنه، آخذين في الاعتبار تاريخ وفاة أبيه، قد عمّر مالا يقل عن ٧٣ سنة./

<span data-type="title" id=toc-341>مصادر ترجمته:</span>
ابن النديم ٥٠، ٥١؛ «المقتبس» للمرزبانى ٩٠ - ٩١؛ «تاريخ بغداد» ٦/ ٢٨٣ - ٢٨٤؛ «إرشاد الأريب» لياقوت ٢/ ٣٥٩ - ٣٦٠؛ «إنباه الرواة» للقفطى ١/ ٢١٣؛ «الوافي بالوفيات» للصفدى ٩/ ٢٤٠؛ «غاية النهاية» لابن الجزرى ١/ ١٧٠؛ ابن','1','297',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (294,'قاضى شهبة، المخطوط ص ٢٣١؛ بغية الوعاة للسيوطى» Br.SI ,١٦٩ ;M.Fleischhammerin: ZDMG ١١٢ /١٩٦٢ /٣٠٧ ;-.٢٠٠ معجم المؤلفين» لكحالة ٢/ ٣٠٠.

<span data-type="title" id=toc-342>ومن آثاره:</span>
«كتاب الوحوش»، ذكره ياقوت.

<span data-type="title" id=toc-343>النديم</span>
هو أبو جعفر محمد بن يحيى بن أبي عبّاد جابر العسكرى، لغوى من أهل بغداد، كان نديما للخليفة المعتضد (٢٧٩/ ٨٩٢ - ٢٨٩/ ٩٠٢)، وتوفي في خلافته.
«جامع المنطق»، وقد شرحه الزجاج من بعد بتكليف من الخليفة المعتضد. انظر ص ٩٩ آنفا.

<span data-type="title" id=toc-344>لغدة الأصفهانى</span>
هو أبو على الحسن بن عبد الله لغدة (لغذة ولجدة، انظر تاج العروس ٢/ ٤٩٤)، ولد في أصفهان، ثم عاش من بعد في العراق وكان لغويا ونحويا (انظر فصل النحو) وأديبا (انظر فصل الأدب).
عاصر أبا حنيفة الدينورى (المتوفى نحو سنة ٢٨٢/ ٨٩٥) وابن قتيبة (المتوفى سنة ٢٧٦/ ٨٨٩). وقد كتب ردودا عليهما وعلى آخرين من اللغويين ورواة الشعر والشعراء.
وقال حمزة الاصفهانى (إرشاد الأريب لياقوت ٣/ ٨٣) إنه جمع هذه الردود في كتاب (١٣٣).
والظاهر أن لغدة قد صنّف قبل كل شئ كتبا صغارا مفردة في اللغة (انظر ياقوت، الموضع نفسه). ووصف القفطى (إنباه الرواة ٣/ ٤٣) كتبه بأنها «موجودة ومفيدة».
_________
(١٣٣) وهذا الكتاب غير كتاب حمزة «التنبيه على حدوث التصحيف» الذى وصل إلينا.','1','298',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (295,'<span data-type="title" id=toc-345>مصادر ترجمته:</span>
ابن النديم ٨١ (طهران، ص ٨٩؛ الترجمة الانجليزية ١٧٨)؛ إرشاد الأريب لياقوت ١/ ١٢٧، ٣/ ٨١ - ٨٤، «إنباه الرواة» للقفطى ٣/ ٤٣؛ «بغية الوعاة» للسيوطى ٢٢٢؛ «كشف الظنون» ١١٦٠، ١٢٠٤، ١٦٣١، ١٩٨٠، ٢٠٤٣؛ «روضات الجنات» للخوانسارى»؛ Br.SI ,١٨٨ - .٢١٥ معجم المؤلفين» لكحالة ٣/ ٢٣٨.

<span data-type="title" id=toc-346>وله مصنّفات عامة في اللغة؛ منها:</span>
١ - «كتاب النوادر»، ذكرته أربع قوائم، وذكر حمزة الأصفهاني أنه كتاب كبير في «مسائل شكوكه من كتب اللغة».
٢ - «كتاب النطق»، ذكره ابن النديم والقفطى وياقوت.
٣ - «كتاب التسمية»، مذكور عند ابن النديم، وفي أربع قوائم أخرى.

<span data-type="title" id=toc-347>وله كتب في بعض الموضوعات؛ منها:</span>
١ - «كتاب الصفات»، ذكرته ثلاث قوائم، وذكر ياقوت أنه كتاب صغير، وكان كتابا في اللغة مرتبا على الموضوعات، تقدّم كتاب «الصفات» للديمرتى، وكتاب «التلخيص» لأبي هلال العسكرى. وقد أخذ أبو هلال على لغدة إسهابه في شرح الشواهد الشعرية، وأخطاءه في تفسير الألفاظ (انظر كتاب «التلخيص»، دمشق ١٩٦٩، ص ٢ - ٣، ٢٠١، ٢٣٢، ٢٤٧، ٣١٢، ٤٦٥، ٦٤٩).
٢ - «كتاب خلق الإنسان»، ذكره ياقوت عن حمرة الأصفهاني، وورد ذكره في قائمتين أخريين، راجع «كشف الظنون» ٧٢٢.
٣ - «كتاب خلق الفرس»، ذكرته تلك المصادر، راجع «كشف الظنون» ٧٢٣.
٤ - «كتاب بلاد العرب»، لم تذكره القوائم، وقد وصل إلينا ونشر، انظر فصل الجغرافية.
٥ - «كتاب الهشاشة والبشاشة»، ورد ذكره عند ابن النديم، وفي خمس قوائم أخرى، ولعله متعلق بكتاب «الهشاشة والبشاشة» لأبي زيد الأنصارى (انظر ص ٧٩','1','299',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (296,'آنفا) الذي روى لغدة كتبه (انظر «إرشاد الأريب» لياقوت ٣/ ٨٢).

<span data-type="title" id=toc-348>ومن ردوده على اللغويين:</span>
١ - «الرد على أبي عبيد (في غريب الحديث)، ذكرته أربع قوائم، وورد اسمه كاملا عند ياقوت (عن ابن النديم، ولم يرد في المخطوطات التى وصلت إلينا)، انتقد فيه غريب الحديث لأبي عبيد (المتوفى سنة ٢٢٤/ ٨٣٩).
٢ - «الرد على ابن قتيبة في غريب الحديث»، ذكر القوائم مع الكتاب السابق الذكر، انتقد فيه كتاب غريب الحديث لمعاصره ابن قتيبة (المتوفى سنة ٢٧٦/ ٨٨٩).

<span data-type="title" id=toc-349>بندار بن لرّة</span>
هو بندار بن عبد الحميد، كان أيضا أديبا ممتازا (انظر فصل الأدب)، يعد من اللغويين الذين خلطوا المذهبين.
توفى نحو سنة ٢٨٠/ ٨٩٣.

<span data-type="title" id=toc-350>آثاره:</span>
ذكر له ابن النديم كتابين ص ٨٣:
١ - «جامع اللغة»؛ ويحتمل أن ابن الحداد نقل عنه في «الأفعال» ١/ ٢٥٠، ٢/ ٢٥٩، ٤/ ١٣٥.
٢ - «كتاب الوحوش».

<span data-type="title" id=toc-351>أبو سعيد الضرير</span>
هو أحمد بن خالد البغدادى، كان أديبا (انظر فصل الأدب) ولغويا جليلا، سمع من أعيان اللغويين كأبي عمرو الشيبانى وابن الأعرابي. وانتقل فيما بعد مع/ لغويين آخرين هما الحسين بن الفضل البجلى وأبو إسحاق القرشى، ومع الطبيب أيوب الرهاوى إلى عبد الله بن طاهر وإلى المأمون على نيسابور. وقد ظهرت معارفه الخاصة','1','300',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (297,'هنالك في ذلك المحيط الذي أوجده ابن طاهر باستقدامه طائفة كثيرة من فصحاء الأعراب. وكان ذلك أكبر الأسباب في أن اللغويين بفارس خاصة ينقلون عنه.
وكان من تلامذته ابن قتيبة وشمر وأبو الهيثم الرازى. وقيل إنه توفي سنة ٢٨٢/ ٨٩٥ وعمره ١٠٤ سنة.

<span data-type="title" id=toc-352>مصادر ترجمته:</span>
«التهذيب» للأزهرى ١/ ٢٤؛ «إرشاد الأريب» لياقوت ١/ ١١٨ - ١٢٣؛ «إنباه الرواة» للقفطى ١/ ٤؛ «الوافي بالوفيات» للصفدى ٦/ ٣٦٩ - ٣٧١، و «نكت الهميان» للصفدى ٩٦ - ٩٧؛ «لسان الميزان» لابن حجر ١/ ١٦٦؛ «بغية الوعاة» للسيوطى ١٣١ - ١٣٢.- «معجم المؤلفين» لكحالة ١/ ٢١٤.

<span data-type="title" id=toc-353>آثاره:</span>
١ - كتاب في اللغة، لعله «كتاب النوادر»، نقل عنه الأزهرى ١/ ١٣٩، ١٧٦، ٤/ ٢٤٠، ٦/ ٦٦، ٧/ ٤٨٢، ٨/ ٦٩، ٤٢٤، ١٠/ ١٧٢، ٦٦٧، ١٢/ ٨٧، ٣٢٧، ١٤/ ٢١٢؛ والصغانى في «التكملة» ١/ ١٤، ٣٠، ٤٨، ١٧٣، ٢٠١، ٢١٤، ٤٤٧، ٢/ ١٦٦، ٢١٧، ٢٤٥، ٣٠٢، ٣١٧، ٣٤٩، ٣٨٣، ٥٢٤، ٣/ ٤٠، ٦٥، ١٩٩، ٢٢٦، ٣٧١، ٤٤٣، ٤٧٠، ٤/ ٤٨، ١٢١، ١٩٥، ١٩٩، ٢٢٢، ٢٣٣، ٢٦٥، ٢٩٧، ٣٠٣، ٣١٥، ٣٢٨، ٣٣٠، ٣٣١، ٣٣٨، ٣٤٦، ٣٦٧، ٣٧٧، ٣٧٨، ٣٩٠، ٤١٠، ٤٤٦، ٤٨٣، ٤٩٤، ٥٠٧، ٥١٧، ٥٤٧، ٥٧٤، ٥٨٢؛ والجوهرى (٢) ٨٠٥، (٣) ١٢٢٧؛ والميدانى في «مجمع الأمثال» (انظر ع. التكريتى في: المورد ٣، ٣/ ١٩٧٤/ ١١٦، ١١٧)؛ والمرزوقى في «الأزمنة والأمكنة» ١/ ٢٥٦، ٢٥٨، ٢٧٣، ٢٧٤، ٢٧٥، ٢٩٢.
٢ - «كتاب الرد على أبي عبيد في غريب الحديث»، ذكره ياقوت في «إرشاد الأريب» ١/ ١١٩.

<span data-type="title" id=toc-354>وله مجموعة من الأبيات، يحتمل أنها من الأبيات السائرة، يضمها:</span>
«كتاب الأبيات»، ذكره ياقوت وغيره، راجع تاريخ التراث العربي ٢/ ٩١ (تصحح ترجمته).','1','301',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (298,'<span data-type="title" id=toc-355>أبو حنيفة الدينورى</span>
هو أبو حنيفة أحمد بن داود بن ونند الدينورى، يرجّح أنه ولد في الربع الأول من القرن الثالث/ التاسع، إذ أنه كان تلميذا لابن السكيت (المتوفى سنة ٢٤٣/ ٨٥٧) وكذلك لأبيه إسحاق (انظر إنباه الرواة للقفطى ١/ ٤١). ويدين بمعارفه اللغوية للبصريين والكوفيين، لابن السكيت خاصة (إرشاد الأريب لياقوت ١ ١/ ١٢٣).
ومع هذا فلم يكن لغويا فقط، بل اشتغل أيضا بالعلوم الطبيعية (انظر تاريخ التراث العربي ١/ ١٥٨ وما بعدها). وبسبب تعدد جوانب علمه فإن أبا حيان التوحيدى في كتابه «تقريظ الجاحظ» قد جعله في طبقة الجاحظ وأبي زيد البلخى./ ووصفه بأنه «من نوادر الرجال، جمع بين حكمة الفلاسفة وبيان العرب، له في كل فن ساق وقدم» (إرشاد الأريب لياقوت ١ ١/ ١٢٤ - ١٢٥).
وقد اشتهر في تاريخ العلم الإسلامى على الخصوص بمصنفه الكبير «كتاب النبات». ولا يثبت أبو حنيفة به أنه عالم نابه في النبات فقط، بل ويجعلنا أيضا نتبين بدايات الجغرافية النباتية.
ولا شك أن مصنفه «كتاب الأنواء» هو خير تأليف مفرد في الآثار العلوية والفلك عند العرب (انظر تاريخ التراث العربي ٧/ ٣٤٩).
ومما يؤسف له أن كتبه اللغوية الخالصة مفقودة. وتشهد شذرات منها وكذلك مصنفه «كتاب النبات» بغزارة علمه بلغة العرب وأشعارها. وذهبالمسعودى (مروج الذهب ٣/ ٤٤٢) إلى أن ابن قتيبة نقل نقلا عن كثير من كتب أبي حنيفة وجعله عن نفسه. توفي سنة ٢٨٢/ ٨٩٥.

<span data-type="title" id=toc-356>مصادر ترجمته:</span>
ابن النديم ٧٨؛ «نزهة الألباء» للأنبارى ١ ٣٠٥ - ٣٠٦، ٢ ١٦٤ - ١٦٥؛ «إنباه الرواة» للقفطى ١ - ٤١ - ٤٤؛ «إرشاد الأريب» لياقوت ١ ١/ ١٢٣ - ١٢٧، ٢ ٣/ ٢٦ - ٣٢؛ «بغية الوعاة» للسيوطى ١٣٢؛ «البداية» لابن كثير ١١/ ٨٢؛ «خزانة الأدب» ١/ ٢٥ - ٢٦؛ «كشف الظنون» ١٠٨، ٢٨٠، ٤٤٧، ٦١٤، ٦٦٤، ١٣٩٩، ١٤٠٧، ١٤٤٦، ١٥٤٨.-
Flugel, Gramm. Schulen ١٩٠ - ١٩٢; Br. GI, ١٢٣, SI, ١٨٧; Rescher, Abri BII,','1','302',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (299,'١٩٨ - ٢٠٠; B. Lewinin: EI ٢, II, ٣٠٠
؛ «الأعلام» للزركلى ١/ ١١٩؛ «معجم المؤلفين» لكحالة ١/ ٢١٨ - ٢١٩.

<span data-type="title" id=toc-357>آثاره:</span>
١ - «كتاب الفصاحة»، ذكرته جميع القوائم، ومن المحتمل أنه كتاب جامع في اللغة.
٢ - «إصلاح المنطق»، ذكره ياقوت والصفدى والسيوطى والبغدادى في «خزانة الأدب»، ومن الجائز أنه كتاب أستاذه ابن السكيت، وبرواية أبي حنيفة، أو نسب إليه التباسا كتاب «إصلاح المنطق» لأبي على أحمد بن جعفر الدينورى (المتوفى سنة ٢٨٩/ ٩٠٢).
٣ - «كتاب ما يلحن فيه العامة» (ذكر في ست قوائم) أو «كتاب لحن العامة» (عند القفطى).
٤ - «كتاب الرد على لغدة الأصفهاني»، وقد ذكرته خمس قوائم، وفي بعضها مختصرا أو محرفا، وهو نقض لكتاب «الرد على الشعراء» للحسن بن عبد الله لغدة الأصفهانى (انظر ص ١٦٦ آنفا) الذي كان بينه وبين أبي حنيفة مناقضات في مسائل من اللغة (انظر «إرشاد الأريب» لياقوت ٣/ ٨٢).
٥ - «كتاب النبات»، وقد وصل إلينا بعضه ونشر، انظر تاريخ التراث العربي ٤/ ٣٣٨ - ٣٤٣، ٥/ ٤٢٨، وانظر أيضا «كتاب النبات .. القسم الثانى من القاموس النباتى: حروف سين- ياء»، جمعه مما نقل عنه م. حميد الله، القاهرة ١٩٧٣. وثمة نقول عنه أيضا في حواشى رواية أبي على القالى لكتاب أدب الكاتب لابن قتيبة، ويوجد مخطوطا في رئيس الكتاب ٧٤٢، انظر
S. A. Bonebakkerin: Actasprimercongr. est. arabeseislamicos (Cordoba ١٩٦٢) . Madrid ١٩٦٤,. S. ٤٥٩
٦ - «كتاب العسل والنحل»، وفي المخطوطة أنه من تصنيف أبي عمر الزاهد غلام ثعلب (المتوفي سنة ٣٤٥/ ٩٥٧)، وفي رأى/ ناشره أنه مطابق في محتواه للفقرات المناظرة من كتاب النبات، ويوجد مخطوطا في الاسكوريال ٢٣) ١٨٩٨ legajo، ب-','1','303',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (300,'٣٣ ب-، وآخرها ناقص، نسخت سنة ٥٢٣ هـ، انظر، J.Kraemerin: ZDMG ١١٠ /١٩٦٠ /٢٨٤ - ٢٩١ وراجع فهرس المخطوطات ١/ ٣٦٠)؛ نشره محمد جبار المعيبد في:
المورد ٣، ١/ ١٩٧٤/ ١١٣ - ١٤٢ (انظر في هذه النشرة و. دياب في: المورد ٣، ٤/ ١٩٧٤/ ٣٠٥ - ٣٠٦).

<span data-type="title" id=toc-358>وله في لغة القرآن:</span>
تفسير (القرآن)، وقال أبو حيان (المقابسات ٥٩) إنه كان في ١٣ مجلدا، وورد ذكره عند ياقوت وفي أربع قوائم أخرى.

<span data-type="title" id=toc-359>مصنفات أبي حنيفة في علوم أخرى:</span>
<span data-type="title" id=toc-360>في الرياضة:</span>
«كتاب الجبر والمقابلة»؛ «كتاب البحث (التخت) في حساب الهند»؛ «كتاب الجمع والتفريق»؛ «كتاب حساب الدور»؛ «كتاب الوصايا»؛ «كتاب نوادر الجبر» (أو الخبر؟ )، لم تصل إلينا، انظر تاريخ التراث العربي ٥/ ٢٦٢ - ٢٦٣.

<span data-type="title" id=toc-361>وله في الفلك:</span>
«كتاب القبلة والزوال»؛ «الزيج»؛ «كتاب الكسوف»، لم تصل إلينا، انظر تاريخ التراث العربي ٦/ ١٥٨ - ١٥٩.

<span data-type="title" id=toc-362>وفي الآثار العلوية:</span>
«كتاب الأنواء»، وبقيت قطع منه، انظر تاريخ التراث العربي ٧/ ٣٤٩، وانظر أيضا «التكملة» للصغانى ١/ ٦٣.

<span data-type="title" id=toc-363>وفي الطب:</span>
«كتاب الباه»، ذكرته خمس قوائم (وذكره ياقوت نقلا عن ابن النديم، ولم يرد فيه)، وكان موجودا في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في MIE (٤٩ /١٩٤٦ /١٠ ,No.١٨٠:','1','304',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (301,'<span data-type="title" id=toc-364>وفي الجغرافية:</span>
«كتاب البلدان» «كتاب كبير» (ابن النديم)، مذكور في كل القوائم، ولم يصل إلينا، راجع فصل الجغرافية.
كتاب مشكوك فيه لم يذكر إلا في «كشف الظنون» ٦١٤. واسمه «كتاب جواهر العلم».

<span data-type="title" id=toc-365>البندنيجى</span>
هو أبو بشر اليمان بن أبي اليمان الضرير، لغوى خلط المذهبين، ولد سنة ٢٠٠/ ٨١٥ في بندنيجين بالقرب من بغداد وبها نشأ. وفي مدينته تلك تلقى مرويات أبي الحسن الأثرم.
وقيل إنه كان موسرا وأنفق ثروته على العلم والعلماء. وإبّان إقامته في بغداد سمع من ابن الأعرابي، وابن السكيت، والرياشى، والزيادى. وعلى ما نعلم اليوم كان البندنيجى أوّل من رتب الموادّ اللغوية بحسب الحرف الأخير منها. وقد اتبعه على ما يحتمل ابن قتيبة في مصنفه «كتاب التقفية»، ثم الجوهرى في معجمه «الصحاح».
توفي سنة ٢٨٤/ ٨٩٧./

<span data-type="title" id=toc-366>مصادر ترجمته:</span>
ابن النديم ٨٢؛ «إرشاد الأريب» لياقوت ٧/ ٣٠٤ - ٣٠٥؛ «إنباه الرواة» للقفطى ٤/ ٧٣؛ «نكت الهميان» للصفدى ٣١٢ - ٣١٣؛ ابن قاضى شهبة، المخطوط ص ٥٤٥ - ٥٤٦؛ «بغية الوعاة» للسيوطى» Flugel ,Gramm.Schulen ١٩٥ ;-.٤٢٠ معجم المؤلفين» لكحالة ١٣/ ٢٥٦ - ٢٥٧.

<span data-type="title" id=toc-367>ومن آثاره:</span>
«كتاب التقفية في اللغة» (ذكره ابن النديم، وياقوت في معجم البلدان ١/ ٤٤٦)، آيا صوفية ٤٦٧٠ (٢٧٢ ورقة، نسخت سنة ٥٩١ هـ)، مكتبة حمد الجاسر الخاصة بالرياض.','1','305',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (302,'نشره خليل إبراهيم العطية، بغداد ١٩٧٦؛ وانظر مقاله «التقفية في اللغة للبندنيجى» في: المورد ٥، ٤/ ١٩٧٦/ ٣٠١ - ٣٠٥؛ وإبراهيم السامرائى، «في القوافى وكتاب «التقفية»، المورد ٧، ٣/ ١٩٧٨/ ٣٢٩ - ٣٣٣؛ وخليل إبراهيم العطية، «البندنيجى ومعجمه ... » في: اللسان العربي (أبحاث ودراسات) ١٧، ١/ ١٩٧٩/ ١٩٤ - ٢٠٢ (له بقية).

<span data-type="title" id=toc-368>إبراهيم الحربى</span>
هو أبو إسحاق إبراهيم بن إسحاق بن بشير (بشر)، أصله من مرو، وكان مولده سنة ١٩٨/ ٨١٣.
سمع من أحمد بن حنبل، وعثمان ابن أبي شيبة، والفضل بن دكين، وغيرهم.
ومن تلامذته أبو بكر بن الأنبارى، وأبو عمر الزاهد، وأبو بكر بن أبي داود، وآخرون.
كان لغويا وأديبا وفقيها ومحدثا. توفي سنة ٢٨٥/ ٨٩٨ في بغداد.

<span data-type="title" id=toc-369>مصادر ترجمته:</span>
ابن النديم ٢٣١ - ٢٣٢؛ «نزهة الألباء» للأنبارى ١ ٢٧٦ - ٢٧٨؛ «تاريخ بغداد» ٦/ ٢٧ - ٤٠؛ «إرشاد الأريب» لياقوت ١ ١/ ٣٧ - ٤٦، ٢ ١/ ١١٢ - ١٢٩؛ «إنباه الرواة» للقفطى ١/ ١٥٥ - ١٥٨؛ «فوات الوفيات» للكتبى ١/ ٥ - ٧؛ «الوافي بالوفيات» للصفدى ٥/ ٣٢٠ - ٣٢٤؛ ابن قاضى شهبة، المخطوط ص ١٣٨ - ١٣٩؛ «طبقات الشافعية» للسبكى ٢/ ٢٦ - ٢٧؛ «بغية الوعاة» للسيوطى»؛ Br.GI ,١٢٤ ,SI ,١٨٨ - .١٧٨ الأعلام» للزركلى ١/ ٢٤؛ «معجم المؤلفين» لكحالة J.C.Vadetin: EI ,III ٢ ,٩٩٤؛ ١٢ /١

<span data-type="title" id=toc-370>ومن آثاره:</span>
في لغة القرآن والحديث، والعقائد، والفقه، والزهد وغير ذلك (استدراك على تاريخ التراث العربي ١):
١ - «رسالة في أن القرآن غير مخلوق»، لم تذكره القوائم، ويوجد مخطوطا في','1','306',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (303,'حيدرآباد، مكتبة الجامعة العثمانية ٣٢) ٤٩٩ A، أ- ٣٤ أ، حديثة النسخ).
٢ - «تفسير (القرآن)»، نقل عنه ابن حجر في «التهذيب» ١٠/ ٢٨١ (عن حمد الجاسر، في مقدمته لكتاب «المناسك» للحربي، الرياض ١٩٦٩، ص ٢٢٢).
٣ - «كتاب الناسخ والمنسوخ»، ذكره ابن النديم ٣٧.
٤ - «كتاب (عدد) سجود القرآن»، ذكرته ثلاث قوائم (راجع «كشف الظنون» ١٤٢٤)، وكان بين كتب الخطيب البغدادى (انظر التسمية للمالكى، في كتاب «الخطيب البغدادى» ليوسف العش ٩٣، رقم ١٧) وفي القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر بولس سباط في. (MIE ٤٩ /١٩٤٦ /٢٨ ,No.٥٠٠
٥ - «كتاب غريب الحديث» (ذكر في فهرست ابن خير ١٩٤ باسم: شرح غريب الحديث). «وهو أجلّ كتاب، وأكبر ما صنّف في هذا النوع» (إنباه الرواة للقفطى ١/ ١٥٥) وهو أشهر كتب الحربي. وقد شرح فيه الغريب في مجاميع الحديث الآتية:
١ - مسند أبي بكر. ١٢ - مسند المسور بن مخرمة الزهرى.
٢ - مسند عمر. ١٣ - / مسند المطلب بن ربيعة.
٣ - مسند عثمان. ١٤ - مسند السائب (المخزومى).
٤ - مسند على. ١٥ - مسند خالد بن الوليد.
٥ - مسند الزبير. ١٦ - مسند أبي عبيدة الجرّاح.
٦ - مسند طلحة. ١٧ - مسند معاوية وغيره (١٣٤).
٧ - مسند سعد بن أبي وقاص. ١٨ - مسند عمرو بن العاص (١٣٥).
٨ - مسند عبد الرحمن بن عوف. ١٩ - مسند عبد الله بن العباس.
٩ - مسند العباس. ٢٠ - مسند عبد الله بن عمر بن الخطاب.
١٠ - مسند شيبة بن عثمان. ٢١ - مسند الموالى.
١١ - مسند عبد الله بن جعفر.
_________
(١٣٤) كذا عند ابن النديم؛ واستوفي ياقوت والصفدى ذكرهم: مسند ما روى عن عاصم بن عمر، مسند صفوان بن أمية، مسند جبلة بن هبيرة.
(١٣٥) وذكر ياقوت بعده: مسند عمران بن الحسين، مسند حكيم بن حازم، مسند عبد الله بن زمعة، مسند عبد الرحمن بن سمرة.','1','307',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (304,'ويقال إن الحربي ترك الكتاب دون تمام بهذا العنوان الأخير الذي كان خاتمة له.
وقد وصل إلينا المجلد الخامس من ابن العباس إلى آخره.
ويوجد مخطوطا في الظاهرية، عام ١٥٨٠ (٢١٤ ورقة، نقلت سنة ٧٤٧ هـ من نسخة ترجع إلى سنة ٣٧٢ هـ، انظر فهرس اللغة ١٠٦ - ١٠٧، راجع فهرس الألبانى ٢٥٤)، ووصف محتواها حمد الجاسر، في الموضع المذكور، ص ٢٢٨ - ٢٣٧. نقل عنه الصغانى في «التكملة» (انظر مقدمة التحقيق ١/ ٧)؛ و «العباب» (انظر ١/ ٢٦)؛ والأزهرى ١/ ٢٢٢؛ والدميرى في «الحيوان» (انظر؛ J.De Somogyiin: JA ٢١٣ / (١٩٢٨ /٣٩ وابن حجر في «الإصابة» ١/ ١٢٦، ٢/ ٥٩٤، ٤/ ١٣٤ وفي مواضع أخرى كثيرة.
٦ - «كتاب العلل»، نقل عنه ابن حجر في «التهذيب» ٢/ ٢٠، ٥/ ٣٥٢، ٧/ ٤٩٢، ١١/ ١٩٣، على ما ذكر حمد الجاسر، في الموضع المذكور، ص ٢٢٧ ٧ - «مسائل ابن حنبل»، روايته لها، وقد وصلت إلينا قطع منها في «طبقات الحنابلة» لابن أبي يعلى، انظر تاريخ التراث العربي ١/ ٥٠٧.
٨ - «كتاب القضاة والشهود»، مذكور في «كشف الظنون» ١٤٥٠.
٩ - «دلائل النبوة»، ذكره ابن أبي يعلى في «طبقات الحنابلة»، و «كشف الظنون» ٧٦٠، راجع حمد الجاسر، الموضع المذكور، ص ٢٢٢.
١٠ - «كتاب الأدب»، ذكره ابن النديم وياقوت.
١١ - «كتاب الحمّام وآدابه»، ذكرته ثلاث قوائم، وامتلكه الخطيب البغدادى (انظر التسمية للمالكى، في كتاب «الخطيب البغدادى» ليوسف العش ١٠٤، رقم ٢٧١)، راجع حمد الجاسر، في الموضع المذكور، ص ٢٢٤.
١٢ - «كتاب التيمم»، ذكره ابن النديم.
١٣ - «كتاب اتباع (؟ ) الأموات»، ورد ذكره في «كشف الظنون» ١٣٨٣.
١٤ - «كتاب إكرام الضيف»، لم تذكره قوائم مصنفاته، ويوجد مخطوطا في رئيس الكتاب ٢٣٧/ ٢ (٧٩ أ- ١٠٠ ب، نسخت سنة ٦٦٧ هـ)، دار الكتب بالقاهرة، حديث ١٦٨٨ (نسخ سنة ٦٦٧ هـ)، وبالدار نسخة عنها ٢٤٤٣٧ ب (٦٤ ورقة، من القرن ١٤ هـ، انظر الملحق ١/ ٧٠)؛ طبع في القاهرة ١٣٤٩.','1','308',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (305,'١٥ - «كتاب الهدايا والسنة فيها»، ذكرته ثلاث قوائم، راجع حمد الجاسر، في الموضع المذكور، ص ٢٣٨.
١٦ - «كتاب النهى عن الغيبة» أو «ذم الغيبة»، ذكره ابن أبي يعلى في «طبقات الحنابلة»، وحاجى خليفة في «كشف الظنون» ١٤١٩، راجع حمد الجاسر، في الموضع المذكور، ص ٢٢٤، وكان في خزانة كتب الخطيب البغدادى (انظر التسمية للمالكى، في كتاب «الخطيب البغدادى» ليوسف العش، ص ١٠٢، رقم ٢٣٥).
١٧ - «كتاب النهى عن الكذب»، وكان كذلك بين كتب الخطيب البغدادى (انظر المالكى، في المرجع المذكور، ص ١٠٢، رقم ٢٣٦)، راجع حمد الجاسر، في الموضع المذكور، ص ٢٣٨.
١٨ - «كتاب المناسك»، في طرق الحج إلى مكة، وقد وصل إلينا ونشر، انظر فصل الجغرافية.
١٩ - «كتاب المغازى»، ذكره له ابن النديم.
ونقل ابن الانبارى عنه في «الزاهر» من كتاب لم يسمّه، ١/ ٣٧٢، ٤٧٣، ٤٧٤، ٤٧٥، ٥٠١، ٥٠٢، ٥٠٣، ٢/ ٢٦١./

<span data-type="title" id=toc-371>أبو شعيب الحرّانى</span>
هو عبد الله بن الحسن بن أحمد، من أهل بغداد، ولد سنة ٢٠٦/ ٨٢١. وكان يعد محدثا ولغويا صدوقا، وكانت روايته في اللغة عن ابن السكيت خاصة. توفى سنة ٢٩٥/ ٩٠٨ أو سنة ٢٩٦/ ٩٠٩.

<span data-type="title" id=toc-372>مصادر ترجمته:</span>
«تاريخ بغداد» ٩/ ٤٣٥ - ٤٣٧؛ «إنباه الرواة» للقفطى ٢/ ١١٥.
ووردت نقول لغوية بروايته عن ابن السكيت في «التهذيب» للأزهرى ١/ ٧٥، ٦٠، ٨٢، ١٢٠، ١٣٣، ١٨٧، ١٩١، ٢٠٠، ٢٤٤، ٢٧٢، ٣٢٠، ٣٤٣، ٣٥٥، ٣٦٠، ٤٣٣، ٤٣٨، ٤٦٩، ٢/ ٤٢، ٤٥، ١٧٥، ١٨٦، ١٩٢، ٢٤٠،','1','309',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (306,'٢٦٣، ٣٠٠، ٣٣٢، ٣٤٢، ٣٥٩، ٣/ ٢٧ - ٢٨، ٤٦، ٥٤، ٥٩، ٧٤، ٨١، ٩٣، ١٠٣، ١٠٤، ١٦٢، ١٩٣، ٢٤٠، ٢٥٣، ٢٨٢، ٤٤٤، ٤٤٨، ٤/ ٢٩، ٤٥، ١٤٧، ١٧٤ - ١٧٥، ١٨٥، ٢٠٤، ٢١٤، ٢٣٣، ٢٤٤، ٣٢٩، ٣٧٧، ٣٨٤، ٥/ ٧، ٣٣، ٧٠، ٧١، ٨٢، ٩٠، ١٣٩، ١٥٣، ١٦٨، ٢١٣، ٢٣٩، ٦/ ٣٦، ١٥٥/ ٢٠٠، ٢٢١، ٢٨٤، ٨/ ١٨٢، ١٠/ ٢٧٢، ٥٣١، ٥٤٦، ٦٢١ - ٦٢٢، ١١/ ٢١، ٣٣، ٩٠، ١٣٢، ١٥١ - ١٥٢، ٢٧٩، ٣٣٨، ٤٥٥، ٤٨٦، ١٢/ ٥، ٦٢، ٦٧، ٧١، ٩٠، ١٩٥، ٢٠٣، ٤٤٥.

<span data-type="title" id=toc-373>أبو عبد الله اليزيدى</span>
هو محمد بن العباس بن محمد، ابن حفيد أبي محمد اليزيدى (انظر فصل النحو).
ولد سنة ٢٣٠/ ٨٤٥ وأخذ عن أبي الفضل الرياشى وثعلب، فكان نحويا ولغويا وأديبا جليلا (انظر فصل الأدب). ومن تلامذته أبو بكر الصولى. واستدعى في آخر عمره لتعليم أولاد الخليفة المقتدر بالله. توفى سنة ٣١٠/ ٩٢٢، وفي قول آخر سنة ٣١٣ هـ.

<span data-type="title" id=toc-374>مصادر ترجمته:</span>
ابن النديم ٥١؛ «طبقات النحويين» للزبيدى ٦٥؛ «تاريخ بغداد» ٣/ ١١٣؛ «نزهة الألباء» للأنبارى ١٦٦ - ١٦٧؛ «إنباه الرواة» للقفطى ٣/ ١٩٨ - ١٩٩؛ ابن خلكان ١/ ٦٣٦؛ «الوافى بالوفيات» للصفدى ٣/ ١٩٩؛ «مسالك الأبصار» لابن فضل الله ٤/ ٢٩٢ - ٢٩٣؛ «بغية الوعاة» للسيوطى ٥٠ - ٥١.
Flugel, Gramm. Schulen ٩١; Rescher, Abri BII, ١٣٦ - ١٣٧; Br. SI, ١٦٩ - ١٧٠; M. Fleischhammerin: ZDMG ١١٢/ ١٩٦٢/ ٣٠٦;
«الأعلام» للزركلى ٧/ ٥٢؛ «معجم المؤلفين» لكحالة ١٠/ ١٢١.

<span data-type="title" id=toc-375>ومن آثاره في اللغة:</span>
«كتاب النوادر في اللغة»، وكان عند ابن مكتوم نسخة مخطوطة منه في جزءين، انظر إنباه الرواة للقفطى ٣/ ١٩٩ الحاشية، وراجع ابن خلكان ١/ ٦٣٦.','1','310',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (307,'<span data-type="title" id=toc-376>وفي لغة القرآن:</span>
«غريب القرآن»، وليس بعمل جليل، وإنما هو في الأغلب مختصر من «مجاز القرآن» لأبي عبيدة. ويوجد مخطوطا في كوبريلى ٢٠٥ (٥٠ ورقة، نسخت سنة ٥٣٩ هـ، انظر فهرس المخطوطات ١/ ٣٥).

<span data-type="title" id=toc-377>وفي النحو:</span>
«مختصر في النحو»، ذكره ابن النديم وغيره بين مصنفاته./

<span data-type="title" id=toc-378>الأخفش الأصغر</span>
هو أبو الحسن على بن سليمان بن الفضل، ولد نحو سنة ٢٣٥/ ٨٤٩، أخذ عن المبرد وثعلب وغيرهما ببغداد، وقدم مصر سنة ٢٨٧/ ٩٠٠ وخرج منها إلى حلب سنة ٣٠٠/ ٩١٢، ثم رجع إلى بغداد وتوفى بها سنة ٣١٥/ ٩٢٧ وهو ابن ثمانين سنة.
روى عنه أبو جعفر النحاس، والمرزبانى، والمعافى بن زكرياء الجريرى.
كان أديبا (انظر فصل الأدب) ولغويا، إلا أن المرجح أنه لم يكن من أئمة اللغويين. وقال فيه تلميذه المرزبانى (المقتبس ٣٤١) إنه «لم يكن متسعا في الرواية للأخبار والعلم».

<span data-type="title" id=toc-379>مصادر ترجمته:</span>
ابن النديم ٨٣؛ «تاريخ العلماء» للتنوخى ٨٤ أ؛ «طبقات النحويين» للزبيدى ١٢٥ - ١٢٧؛ «تاريخ بغداد» ١١/ ٤٣٣؛ «إرشاد الأريب» لياقوت ١ ٥/ ٢٢٠ - ٢٢٥، ٢ ١٣/ ٢٤٦ - ٢٥٧؛ «نزهة الألباء» للأنبارى ١ ٣١٢ - ٣١٣، ٢ ١٦٩ - ١٧٠؛ «إنباه الرواة» للقفطى ٢/ ٢٧٦ - ٢٧٨؛ «مسالك الأبصار» لابن فضلالله ٤/ ٢٩٤ - ٢٩٥؛ ابن قاضى شهبة، المخطوط ص ٤٢٣ - ٤٢٤؛ «بغية الوعاة» للسيوطى ٣٣٨.-
Flugal, Gramm. Schulen ٦٣ - ٦٤, ٢٢٤ - ٢٢٥; Br. GI, ١٢٥, SI, ١٨٩; Rescher, Abri B II, ٢٠٢ - ٢٠٤;
«الأعلام» للزركلى ٥/ ١٠٣؛ «معجم المؤلفين» لكحالة ٧/ ١٠٤.','1','311',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (308,'<span data-type="title" id=toc-380>آثاره:</span>
١ - تهذيب لكتاب النوادر لأبي زيد الأنصارى، وقد وصل إلينا، انظر ص ٧٧ آنفا.
٢ - تهذيب لكتاب المسائية لأبي زيد الأنصارى، وقد وصل إلينا كذلك، انظر ص ٧٧ آنفا.
٣ - «تعليقات على كتاب النبات للأصمعى»، على هامش مخطوطة التيمورية بدار الكتب بالقاهرة، تاريخ ٨٩٠ (ص ١٣٧ - ١٦٩، انظر ع. ى. الغنيم، في مقدمته لكتاب «النبات»، القاهرة ١٩٧٢، ص ١٥).
٤ - «كتاب الجراد» (وليس العنوان بمحقق)، ذكره ابن النديم وياقوت.
٥ - «كتاب الأنواء»، ذكرته القوائم، ولم يصل إلينا، انظر تاريخ التراث العربي ٧/ ٣٥٣.
٦ - «دارات العرب»، وفيه ذكر ١٧ اسما من هذه الأسماء الجغرافية مع شواهد من الشعر، وقد وصل إلينا، انظر فصل الجغرافية.

<span data-type="title" id=toc-381>الجعد</span>
هو أبو بكر محمد بن عثمان بن المسبّح الشيبانى، كان نحويا (انظر فصل النحو)، لغويا، مفسرا، واشتغل أيضا بمسائل العروض.
وكان تلميذا للنحوى اللغوى البصرى أبي الحسن بن كيسان. توفى نحو سنة ٣٢٠/ ٩٣٢.

<span data-type="title" id=toc-382>مصادر ترجمته:</span>
ابن النديم ٨٢؛ «تاريخ بغداد» ٣/ ٤٧؛ «نزهة الألباء» للأنبارى ٢ ٢١٣؛ «إرشاد الأريب» لياقوت ٧/ ٣٩ - ٤٠؛ «إنباه الرواة» للقفطى ١/ ٢٦٩؛ «الوافى بالوفيات» للصفدى ٤/ ٨٢؛ «طبقات المفسرين» للداودى» Flugel ,Gramm.Schulen ٢١٩ - ٢٢٠ ;-.١٩٣ /٢ معجم المؤلفين» لكحالة ١٠/ ٢٨٧./','1','312',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (309,'<span data-type="title" id=toc-383>آثاره:</span>
<span data-type="title" id=toc-384>في اللغة:</span>
١ - «زيادات الفصيح (لثعلب)»، ويوجد مخطوطا في برنستون، جاريت (يهودا) ٦٤١ (٢٦ ب- ٢٩ ب، من القرن ١١ هـ، انظر ماخ ٣٢١، رقم ٣٧٥١).
٢ - «كتاب خلق الإنسان»، ذكره ابن النديم.
٣ - «كتاب الفرق»، ذكره كذلك ابن النديم.

<span data-type="title" id=toc-385>وله في لغة القرآن:</span>
١ - «كتاب معانى القرآن»، ذكره ابن النديم وغيره.
٢ - «كتاب ناسخ القرآن ومنسوخه»، ذكره في تاريخ بغداد وغيره كثيرا، رواه أبو عمران الفاسى (المتوفى سنة ٤٣٠/ ١٠٣٩، انظر-
J. Sourdel- Thomineund (D. Sourdelin: REI ٣٢/ ١٩٦٥/ ١٩

<span data-type="title" id=toc-386>الوشّاء</span>
هو أبو الطيب محمد بن أحمد بن إسحاق، من أهل بغداد، سمع من أبي بكر بن أبي الدنيا، ونفطويه، وأبي بكر الورّاق. وكان معلما لمكتب العامّة. وأثنوا عليه بأنه أديب فاضل (انظر فصل الأدب)، وبرع أيضا في النحو (انظر فصل النحو) واللغة وتاريخ الأدب.
ولا تذكر مصادرنا ممن أخذ عنه أو سمع منه إلا منية الكاتبة (انظر تاريخ بغداد ١٤/ ٤٤١؛ إنباه الرواة للقفطى ٣/ ٦١). توفى سنة ٣٢٥/ ٩٣٧ فيبغداد.

<span data-type="title" id=toc-387>مصادر ترجمته:</span>
ابن النديم ٨٥؛ تاريخ بغداد ١/ ٢٥٣ - ٢٥٤؛ «إرشاد الأريب» لياقوت ١ ٦/ ٢٧٧ - ٢٧٨، ٢ ١٧/ ١٣٢ - ١٣٤؛ «إنباه الرواة» للقفطى ٣/ ٦١ - ٦٢؛ «الوافى بالوفيات» للصفدى ٢/ ٣٢ - ٣٣؛ «بغية الوعاة» للسيوطى ٧ - ٨.-
Flugel, Gramm. Schulen ٢١٢ - ٢١٣; Br. GI. ١٢٤, SI, ١٨٩; Rescher, Abri BII, ٢٠٠ - ٢٠١;','1','313',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (310,'«الأعلام» للزركلى ٦/ ١٩٩؛ «معجم المؤلفين» لكحالة ٨/ ٢٣١ - ٢٣٢؛ رمضان عبد التواب، مقدمته لكتاب «الممدود والمقصور»، القاهرة ١٩٧٩.

<span data-type="title" id=toc-388>آثاره:</span>
١ - «كتاب المثلث»، ذكرته كل القوائم.
٢ - «كتاب خلق الإنسان»، ورد ذكره في جميع القوائم (راجع كشف الظنون ٧٢٣)، وكان في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في:
. (MIE ٤٩/ ١٩٤٦/ ١٩, No. ٣٣٩
٣ - «كتاب خلق الفرس»، ذكرته تلك القوائم و پولس سباط، الموضع المذكور، ص ١٩، رقم ٣٤٠.
٤ - «كتاب الفرق»، ذكرته كذلك كل القوائم.

<span data-type="title" id=toc-389>المفجّع</span>
هو أبو عبد الله محمد بن أحمد (أو محمد) بن عبد الله الكاتب، من أهل البصرة.
وكان شاعرا (انظر تاريخ التراث العربي ٢/ ٥٠٩)، لغويا، / أديبا (انظر فصل الأدب).
كان تلميذا لثعلب وهاجى ابن دريد. توفي سنة ٣٢٧/ ٩٣٩ أو بعد ذلك بقليل.
ومن آثاره كتاب «المنقذ في الأيمان»، وقيل إنه كان مثل كتاب الملاحن لابن دريد (انظر ص ١٠٤ آنفا)، أى أنه يتناول المترادفات التى يمكن التنصل من الأيمان باستعمالها، انظر تاريخ التراث العربي ٢/ ٥١٠ (حيث يجب إصلاح اسم الكتاب).

<span data-type="title" id=toc-390>أبو الهيذام العقيلى</span>
هو كلاب بن حمزة اللغوى، كان معلّما من أهل حرّان، قدم بغداد نحو سنة ٢٩٠/ ٩٠٣، ويبدو أنه أقام أيضا مدة في البصرة.
وكان نحويا، شاعرا (١٣٦)، خطّاطا، وفي النحو خلط مذهبى الكوفيين والبصريين.
_________
(١٣٦) انظر أبياتا له في «معجم الشعراء» للمرزبانى ٣٥٤، و «إرشاد الأريب» لياقوت ٥/ ٣٢٧ - ٣٢٨؛ ٦/ ٢٠٨ - ٢١١.','1','314',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (311,'وفي سنة ٣٠٠/ ٩١٣ نظم قصيدة في رثاء يحيى بن على المنجّم، ولا نعلم شيئا عن أحواله فيما تأخر من عمره. هجاه ابن لنكك البصرى (الشطر الأول من القرن الرابع/ العاشر، انظر تاريخ التراث العربي ٢/ ٥١٠ - ٥١١).

<span data-type="title" id=toc-391>مصادر ترجمته:</span>
ابن النديم ٨٢ (طهران، ص ٩١؛ الترجمة الانجليزية ١٨١)؛ «معجم الشعراء» للمرزبانى ٣٥٤؛ «إرشاد الأريب» لياقوت ٦/ ٢٠٨ - ٢١١؛ «إنباه الرواة» للقفطى ٤/ ١٨١؛ «بغية الوعاة» للسيوطى ٣٨٢.- «معجم المؤلفين» لكحالة ٨/ ١٤٥.

<span data-type="title" id=toc-392>آثاره:</span>
١ - «أرجوزة في الألغاز»، لم تذكرها القوائم، وتوجد مخطوطة في أماسية ٦٢١/ ٣ (الأوراق ٢٩٦ - ٣٠٢، نسخت سنة ٦٢٨ هـ).
٢ - «ما يلحن فيه العامة»، مذكور عند ابن النديم، وفي ثلاث قوائم أخرى (انظر أيضا «كشف الظنون» ١٥٧٧)، وكان في القرن ٧/ ١٣ في مكتبة من مكتبات حلب (انظر پولس سباط في. (MIE ٤٩ /١٩٤٦ /٤٣ ,No.٧٦١:
٣ - «كتاب الأراكة»، يحتمل أنه في ضرب من الشجر، ينمو في بلاد العرب (انظر الأزهرى ١٠/ ٣٥٤؛ لسان العرب ١٢/ ٢٦٨ - ٢٦٩؛ تاج العروس ٧/ ٩٩ - ١٠١؛ معجم البلدان لياقوت ١/ ١٨٢ - ١٨٣)، ذكره ابن النديم وآخرون.

<span data-type="title" id=toc-393>وله في النحو:</span>
«كتاب جامع النحو»، ذكره ابن النديم وغيره./

<span data-type="title" id=toc-394>الأشنانى</span>
هو أبو الحسين عمر بن الحسن بن على بن مالك الشيبانى، وكان محدّثا (انظر تاريخ التراث العربي ١/ ١٨٤)، مؤرخا، لغويا، توفى سنة ٣٣٩/ ٩٥٠.','1','315',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (312,'<span data-type="title" id=toc-395>ومن آثاره اللغوية:</span>
«كتاب الخيل»، ذكره ابن النديم ١١٥.

<span data-type="title" id=toc-396>زيادات على فصل «تدوين التاريخ»:</span>
١ - «كتاب مقتل زيد بن على».
٢ - «كتاب فضائل أمير المؤمنين على ابن أبي طالب».
٣ - «كتاب مقتل الحسين بن على».
ذكرها كلها ابن النديم.

<span data-type="title" id=toc-397>أبو الطيب اللغوى</span>
هو عبد الواحد بن على الحلبى، ولد في الشطر الثانى من القرن الثالث/ التاسع بعسكر مكرم. ثم توجه من بعد إلى بغداد حيث أخذ عن أبي عمر الزاهد وأبي بكر الصولى.
وقد نال شهرة واسعة أثناء مقامه ببغداد، حتى أن سيف الدولة قدمه، بعد وصوله إلى حلب، إلى منافسه ابن خالويه على أنه «لغوى» (رسالة الغفران لأبي العلاء المعرى ٥١٣ - ٥١٤). توفى سنة ٣٥١/ ٩٦٢ في غارة الدمستق على حلب.
وأسماء مصنفاته والقطع التى بلغتنا من بعضها تقضى بأنه كان يغلب عليه الاشتغال باللغة.
واشتغل أيضا بتاريخ الأدب (انظر مقدمة فصل النحو). ومما يدعو للأسف أن أكثر كتبه قد ضاعت بمقتله، وهو ما أسف عليه أبو العلاء المعرى (رسالة الغفران ٥١٣).

<span data-type="title" id=toc-398>مصادر ترجمته:</span>
رسالة الغفران للمعرى، ص ٥١٢ - ٥١٥؛ ابن القارح في: «رسائل البلغاء» لمحمد كرد على ٢٧٦؛ «إعلام النبلاء» للحلبى ٤/ ٣٥ - ٣٨؛ ابن قاضى شهبة، المخطوط ص ٣٩١ - ٣٩٢؛ «بغية الوعاة» للسيوطى ٣١٧؛ «هدية العارفين» لاسماعيل البغدادى Rescher ,Abri BII ,٢٠٦ ;Br.SI ,١٩٠ ;-.٦٣٣ /١','1','316',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (313,'عز الذين التنوخى، مقدمته لكتاب «الإبدال» (انظر رقم ٢ بعد)؛ عزة حسن، مقدمته لكتاب «الأضداد» (انظر رقم ١ بعد)؛ «الأعلام» للزركلى ٤/ ٣٢٥؛ «معجم المؤلفين» لكحالة ٦/ ٢١٠؛ ع. أ. زيدان، أبو «الطيب اللغوى» و<span data-type="title" id=toc-399>آثاره </span>في اللغة، بغداد ١٩٧٠.
آثاره:
١ - «كتاب الأضداد في كلام العرب»، لم يذكر بين مصنفاته، وكان من مصادر تاج العروس (انظر ١/ ٤) وخزانة الأدب (انظر إقليد الخزانة للميمنى ٩)، ويوجد مخطوطا في سليم أغا ٨٩٣/ ١ (الأوراق ١ - ١١٠ ب، نسخت سنة ١١١٤ هـ، انظر فهرس المخطوطات ١/ ٤٣١، راجع. (O.Rescherin: ZDMG ٦٨ /١٩١٤ /٥٦ - ٥٧
نشره عزة حسن، في مجلدين، / دمشق ١٩٦٣، وثمة فقر منه مقارنة بنصوص للغويين آخرين في: «نصوص في فقه اللغة العربية» للسيد يعقوب بكر، المجلد الثانى، بيروت ١٩٧١، ص ٢٢٧ - ٢٣٧، وكتب دراسة فيه:
O. Petit, Le Kitabal- Addadd''Abut- Taiyibal- Lu Gawiin: L''ambivalencedan s laculturearabe, Paris ١٩٦٧, S. ٨١ - ٨٤.
٢ - «كتاب الأبدال» (هكذا يقرأ تبعا للفهرى، انظر عزّ الدين التنوخى في: مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥١٥)، وهو على ما تذكر المصادر على غرار «كتاب القلب والإبدال» لابن السكيت.
وكان من مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في كتابه «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣).
ويوجد مخطوطا في مكتبة عابدين الخاصة بدمشق (١٠٧ ورقة، انظر عزّ الدين التنوخى في: مجلة المجمع العلمى العربي بدمشق ٣٢/ ١٩٥٧/ ٤٤٥ - ٤٥٢).
نظره عزّ الدين التنوخى، المجلد الأول، دمشق ١٩٦٠، وانظر في هذه النشرة عبد العزيز الميمنى في: مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٦٧٣ - ٦٨٠، وثمة فقرة منه مقارنة بنصوص للغويين آخرين، أوردها السيد يعقوب بكر، في الموضع المذكور، ص ٣١٧ - ٣٢٦.','1','317',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (314,'٣ - «كتاب الإتباع» أو «لطيف الإتباع»، ذكرته القوائم، ويوجد مخطوطا في الرباط (انظر مجلة المجمع العلمى العربي بدمشق ٤١/ ١٩٦٦/ ٣٧٤ - ٣٧٥)، دمشق، مكتبة عابدين الخاصة (أولها ناقص)، ونشره على مخطوطة دمشق عزّ الدين التنوخى في: مجلة المجمع العلمى العربي بدمشق ٣٦/ ١٩٦١/ ٦٨ - ١٠٢، ٢٤٨ - ٢٨٢، ٤٤٤ - ٤٦٨، ٦٢٩ - ٦٦٢، وفي دمشق ١٩٦١، واستدرك نقص أوله من مخطوطة الرباط في: مجلة المجمع العلمى العربي بدمشق ٤١/ ١٩٦٦/ ٣٧٥ - ٣٧٦، وثمة جزء منه مقارنا بنصوص مشابهة للغويين آخرين، أورده السيد يعقوب بكر، في الموضع المذكور، ص ٣٤١ - ٣٤٧.
٤ - «كتاب المثنى»، لم تذكره القوائم، ويوجد مخطوطا في مكتبة عابدين الخاصة بدمشق (راجع مجلة المجمع العلمى العربي بدمشق ١٥/ ١٩٣٧/ ٤١٢)؛ نشره عزّ الدين التنوخى في: مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٤٢١ - ٤٦٥، ٦٠٩ - ٦٤٦، ودمشق ١٩٦٠.
٥ - «كتاب شجر الدر»، يشتمل على ألفاظ مسلسلة ككتاب «المداخل» لأبي عمر الزاهد، وذكرته قوائم كتبه، ونقل عنه السيوطى في المزهر ١/ ٤٥٤، المخطوطات:
روضة خيرى بالقاهرة (الجزء الثانى من مجموع، الأوراق ١٥ - ٣٩، نسخت سنة ٨٦٧ هـ بخط السيوطى، انظر مجلة معهد المخطوطات العربية ٧، ٢/ ١٩٦١/ ١٠ - ١١)، الخزانة الزكية بدار الكتب بالقاهرة ٤٠٥، ومكتبة طلعت بها، لغة ٣٨٣ (٢٠ ورقة، نسخت سنة ١٣٠٨ هـ)، والتيمورية، لغة ٢١٠ (نسخت سنة ١٠٦٦ هـ)، ومنها مصورة بالدار، ٥٨٨٩ هـ (انظر الملحق ٢/ ٢)، الأزهر، مجموع ١١٨١ (من القرن ١٣ هـ)، ييل ٣٩) ٤٣ - L، ورقة، نسخت سنة ١٣٠٨ هـ، انظر نيموى رقم ١٤٣).
نشره محمد عبد الجواد، القاهرة ١٩٥٧، ط ٢ ١٩٦٨.
٦ - «كتاب الفرق» أو «كتاب الفروق»، ذكره المعرى والصفدى، ونقل عنه السيوطى في «المزهر» ١/ ٤٤٧.
٧ - «القصيدة الخالية»، في معنى كلمة «خال»، وهى في الوقت ذاته قافية','1','318',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (315,'القصيدة، وتوجد مخطوطة في الظاهرية، عام ٤٥٢٤/ ٣ (من القرن السادس أو السابع، انظر فهرس عزة حسن ٢/ ١).

<span data-type="title" id=toc-400>ابن خالويه</span>
هو أبو عبد الله الحسين بن أحمد بن خالويه (انظر تاريخ التراث العربي ٧/ ٣٥٦ وما بعدها).
ولد في همدان، وقصد بغداد سنة ٣١٤/ ٩٢٦ حيث أتيحت له فرصة الأخذ عن بعض أجلّة علماء الكوفيين/ والبصريين، وهم ابن دريد، والسيرافى، وأبو بكر بن الأنبارى، ونفطويه، وأبو عمر الزاهد.
وأخذ القراءات عن مجاهد، والحديث عن محمد بن مخلد (انظر تاريخ التراث العربي ١/ ١٨١).
وانتقل فيما بعد إلى حلب حيث نال بعمله مؤدّبا شهرة واسعة. وحظى عند سيف الدولة وكان يؤدب أولاده. وظل من بعد وفاة سيف الدولة (٣٥٦/ ٩٦٧) في البلاط، وعلى ما يحتمل إلى أن توفى سنة ٣٧٠/ ٩٨٠.
وقد برع ابن خالويه في النحو واللغة جميعا. وكان يعد أيضا أديبا جيدا.

<span data-type="title" id=toc-401>مصادر ترجمته:</span>
ابن النديم ٨٤؛ مخطوط في طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٤٣ أ؛ «الرجال» للنجاشى ٥٣؛ «اليتيمة» ١/ ١٢٣ - ١٢٤؛ «نزهة الألباء» للأنبارى ٢ ٢١٤ - ٢١٥؛ «تاريخ العلماء» للتنوخى ٨٨ ب؛ ابن خلكان ١/ ١٩٧ - ١٩٨؛ «إرشاد الأريب» لياقوت ١ ٤/ ٤ - ٦، ٢ ٩/ ٢٠٠ - ٢٠٥، «إنباه الرواة» للقفطى ١/ ٣٢٤ - ٣٢٧؛ «طبقات الشافعية» للسبكى ٢/ ٢١٢ - ٢١٣؛ «غاية النهاية» لابن الجزرى ١/ ٢٣٧؛ «مسالك الأبصار» لابن فضل الله ٤/ ٢٤٣ - ٢٤٤؛ «بغية الوعاة» للسيوطى ٢٣١ - ٢٣٢.
Flugel, Gramm. Schulen ٢٣٠ - ٢٣٢; Br. GI, ١٢٥, SI, ١٩٠; Rescher, Abri BII, ٢٠٤ - ٢٠٥; C. van Arendonkin: EIII ١, ٤١٨ - ٤١٩','1','319',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (316,'«أعيان الشيعة» للعاملى ٢٥/ ٤٨ - ٦٢؛ «الأعلام» للزركلى ٢/ ٢٤٨ - ٢٤٩؛ «معجم المؤلفين» لكحالة A.Spitalerin: EI ,III ٢ ,٨٢٤ - ٨٢٥؛ ٣١١ - ٣١٠ /٣

<span data-type="title" id=toc-402>آثاره:</span>
١ - «التذكرة»، وهو مجموع لغوى نحوى، ملكه القفطى بخط المؤلف (إنباه الرواة ١/ ٣٢٥). ويحتمل أن هذا الكتاب هو الذى نقل عنه الزجاجى باسم «مجموع» ابن خالويه (انظر الأشباه والنظائر للسيوطى ٣/ ٦٣).
٢ - «كتاب تقفية ما اتفق لفظه واختلف معناه لليزيدى»، على معجم الألفاظ المشتركة لإبراهيم بن يحيى بن اليزيدى (المتوفى سنة ٢٢٥/ ٨٤٠، انظر فصل النحو)، ذكره القفطى.
٣ - «شرح الفصيح»، شرح على كتاب ثعلب (المتوفى سنة ٢٩١/ ٩٠٤)، ونقل عنه السيوطى ١١ نقلا في المزهر (انظر الفهرس).
ويوجد مخطوطا في برنستون، جاريت (يهودا) ٤٠٢٥ (٩٣ ورقة، من القرن السابع للهجرة، انظر ماخ ٣٢١، رقم ٣٧٥٠).
٤ - «انتصار ... لأبي العباس ثعلب فيما تتبّعه عليه أبو إسحاق الزجاج»، وهو رد على انتقاد الزجاج لكتاب «الفصيح» لثعلب، ونقله السيوطى في «الأشباه والنظائر» ٤/ ١٢٧ - ١٣٠.
٥ - «كتاب أطرغشّ (١) (في اللغة)، أو «أطرغشّ وابرغشّ»، ذكره ابن النديم وغيره، وكان من مصادر الصغانى في «التكملة» (انظر ١/ ٨)؛ و «العباب» (انظر ١/ ٢٩)؛ وأحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في «تحفة المجد الصريح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٣).
٦ - «كتاب الأفق»، وهو كذلك من مصادر الصغانى والفهرى.
٧ - «الجامع للمشترك»، نقل عنه صاحب «تاج العروس» ٢ ٧/ ١٣٨ (ملح)، وهو على ما يظهر المصدر الرئيسى الذى لم ينصّ على اسمه ل «باب يشتمل على طرف من اللفظ المشترك الواقع في كلام العرب» لجلال الدين أبي محمد عبد الله بن سلمان بن','1','320',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (317,'حازم المرسى الشافعى، إمام الفاضلية (على الأقدم نحو سنة ٦٥٠ هـ) في مخطوطة راغب ١١٦٢/ ٢ (٣٤١ ب- ٣٥٣ أ، نسخت سنة ٩٩٥ هـ)

<span data-type="title" id=toc-403>وله في الاشتقاق وكتب الأسماء:</span>
١ - «كتاب الاشتقاق»، ذكره ابن النديم وورد في ثلاث قوائم أخرى (انظر أيضا «المزهر» للسيوطى ١/ ٣٥١). وكان في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر بولس سباط في./ (MIE ٤٩ /١٩٤٦ /٦ ,No.١٠٧: وجاء جوابه لسيف الدولة في اشتقاق «دمشق» في كتاب «مدينة دمشق» لابن عساكر ١/ ١٧ - ١٨.
٢ - «كتاب اشتقاق الشهور والأيام»، ذكره النجاشى، وقيل إن الجزء الأول منه قد بقى محفوظا، وطبع في ٣٩ صفحة (انظر الذريعة ٢/ ١٠١، رقم ٣٩٥ عن «اكتفاء القنوع»).
٣ - «كتاب اشتقاق خالويه»، وهو في اشتقاق اسمه، ذكره ياقوت والقفطى والسيوطى.
٤ - «كتاب الألقاب»، لم يرد ذكره إلا في «كشف الظنون» ١٣٩٧، ولعله مصحّف عن «كتاب الألفات» (انظر فصل النحو).
٥ - «كتاب أسماء الله الحسنى»، ونصّ عليه المؤلف نفسه في كتابه «إعراب ثلاثين سورة»، حيدرآباد ١٩٤١، ص ١٤ (عن عبد العال سالم مكرم في: مجلة المجمع العلمى العربي بدمشق ٤٥/ ١٩٧٠/ ٣٤٤).

<span data-type="title" id=toc-404>وله تآليف مفردة في بعض الموضوعات:</span>
١ - «كتاب في أسماء الأسد» أو «كتاب الأسد»، وفيه خمسمائة اسم للأسد، ذكرته أربع قوائم، وكان في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر بولس سباط في:
(MIE ٤٩/ ١٩٤٦/ ٦, No. ٩٦
، ونقل عنه أبو حيان الأندلسى في تذكرة النحاة، الجزء الثانى، مخطوطة الأوقاف بالرباط ٢١٤، ص ٤٧٦.
٢ - «كتاب الحشرات»، نقل عنه الدميرى في «الحيوان» ١/ ٢٦٩ (عن J.de (Somogyiin: JA ٢١٣ /١٩٢٨ /٢٠','1','321',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (318,'٣ - «رسالة في أسماء الريح» أو «كتاب الريح»، وقد وصل إلينا ونشر، انظر تاريخ التراث العربي ٧/ ٣٥٧.
٤ - «كتاب الشجر والنبات»، روايته لكتاب أبي زيد الأنصارى، وقد وصل إلينا وطبع، انظر تاريخ التراث العربي ٤/ ٣٣٣، وراجع ص ٧٨ آنفا.
٥ - «كتاب الكل والبعض»، لم تذكره القوائم، ويعرف من طريق كتاب «الرد على ابن خالويه في الكل والبعض» لابن درستويه (المتوفى سنة ٣٤٧/ ٩٥٨)، ولم يصل إلينا، انظر ص ١٠٧ آنفا.

<span data-type="title" id=toc-405>وله في لغة القرآن والحديث:</span>
١ - «ردّ كتاب الأغفال»، وهو نقد على «كتاب الأغفال فيما أغفله الزجاج من المعانى» لأبي على الفارسى (المتوفى سنة ٣٧٧/ ٩٨٧)، انظر في نقول عنه: «خزانة الأدب» ١/ ٣٥٢، ٣٥٤ (راجع «إقليد الخزانة» للميمنى ٦٢).
(أ) «نقض الهاذور»، وهو نقض أبي على الفارسى ما ردّه عليه ابن خالويه، انظر في نقول عنه، ص ١١٠ آنفا.
٢ - «(شرح) غريب الحديث»، وهو من مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) في كتابه «تحفة المجد الصريح» (انظر مجلة المجمع العلمى العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٤).

<span data-type="title" id=toc-406>ابن المراغى</span>
هو أبو الفتح محمد بن جعفر بن محمد الهمذانى، سكن بغداد وعلّم عزّ الدولة.
ووصفه ابن النديم وأبو حيان التوحيدى (انظر إرشاد الأريب لياقوت ٦/ ٤٦٦) بأنه كان نحويا وأديبا بارعا.
وروى عن أحمد بن عبد الله بن قتيبة وغيره؛ وعنه/ روى أبو الحسين المحاملى وسمع منه في سنة ٣٧١/ ٩٨١. ويبدو أنه توفى نحو سنة ٣٨٠/ ٩٩٠.

<span data-type="title" id=toc-407>مصادر ترجمته:</span>
ابن النديم ٨٥؛ «الإمتاع والمؤانسة» للتوحيدى ١/ ١٣٣ - ١٣٤؛ «تاريخ بغداد»','1','322',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (319,'٢/ ١٥٢ - ١٥٣؛ «تاريخ العلماء» للتنوخى ٧٣ أ؛ «إرشاد الأريب» لياقوت ٦/ ٤٦٦ - ٤٦٧؛ «إنباه الرواة» للقفطى ٣/ ٨٣؛ «بغية الوعاة» للسيوطى»؛ Flugel ,-.Gramm.Schulen ٢٣٤ - ٢٣٥ ٢٨ معجم المؤلفين» لكحالة ٩/ ١٥٧.

<span data-type="title" id=toc-408><span data-type="title" id=toc-411>آثاره:</span></span>
١ - «كتاب الاستدراك لما أغفله الخليل»، ذكره ابن النديم وغيره.
٢ - «كتاب البهجة (انظر فصل الأدب).

<span data-type="title" id=toc-409>أبو أحمد العسكرى</span>
هو الحسن بن عبد الله بن سعيد، ولد سنة ٢٩٣/ ٩٠٦ فى عسكر مكرم.
ارتحل فى حداثته إلى بغداد والبصرة حيث سمع من أبى بكر بن دريد ونفطويه.
ثم عاد فيما بعد إلى خوزستان حيث اشتغل فى موطنه عسكر، وفى تستر وغيرهما بالنحو واللغة والأدب (انظر فصل الأدب). توفى سنة ٣٨٢/ ٩٩٣. وكان الغالب عليه اللغة.

<span data-type="title" id=toc-410>مصادر ترجمته:</span>
«أخبار أصبهان» لأبى نعيم ١/ ٢٧٢، ٢/ ٢٩١؛ ابن خلكان ١/ ١٦٤ - ١٦٥؛ «إرشاد الأريب» لياقوت ١ ٣/ ١٢٦ - ١٣٥، ٢ ٨/ ٢٣٣ - ٢٥٨؛ «إنباه الرواة» للقفطى ١/ ٣١٠ - ٣١٢؛ مخطوط في طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٦٢ أ؛ ابن قاضى شهبة، المخطوط ٢٥٢ - ٢٥٣؛ «بغية الوعاة» للسيوطى ٢٢١؛ «خزانة الأدب» Br.SI ,١٩٣ ;J.W.Fuckin: EI ,I ٢ ,٧١٢ ;-٠٩٨ /١ «الأعلام» للزركلى ٢/ ٢١١؛ «معجم المؤلفين» لكحالة ٣/ ٢٣٩ - ٢٤١.
آثاره
١ - «الحكم والأمثال (المروية عن رسول الله وشرح ألفاظه التى لم يسبق إليها)»، (انظر فهرست ابن خير ٢٠٢)، ذكرته كل القوائم، وكان فى القرن ٧/ ١٣ فى مكتبة','1','323',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (320,'من مكتبات حلب (انظر بولس سباط فى، (MIE ٤٩ /١٩٤٦ /١٧ ,No.٣١٥ ويحتمل ورود نقول عنه في تاريخ أصبهان لأبى نعيم ١/ ٢٧٢، والحيوان للدميرى ١/ ٢٢٤، ٢/ ١١٩.
٢ - «جمهرة الأمثال»، وقد وصل إلينا بترتيب ابن أخته وتلميذه أبى هلال العسكرى، انظر ص ١٨٤ بعد.
٣ - «كتاب المختلف والمؤتلف (مما يدخل منه الوهم على المحدّثين)»، «وهو كتاب جليل» (القفطى)، ذكرته أربع قوائم، انظر بعد، رقم ٥ ب.
٤ - «كتاب ما لحن فيه الخواصّ من العلماء» «وهو كتاب معتبر» (القفطى).
٥ - «كتاب التصحيف (والتحريف)»، مذكور فى ثلاث قوائم، ونقل عنه ياقوت في «إرشاد الأريب» ٥/ ٣١٠؛ والدميرى فى «الحيوان» ١/ ٣٦٥؛ والسيوطى فى «المزهر» (انظر الفهرس ٢/ ٦٤١)؛ والبغدادى فى «الخزانة» (انظر إقليد الخزانة للميمنى ٣١ - ٣٢).
ويبدو أن الكتاب الكامل لم يصل إلينا. والظاهر أن المؤلف قد قسّمه فى زمان لاحق إلى كتب منفصلة أفرد كل واحد منها بموضوع يقتصر عليه، ثم عاد فأوجز بعضها، أو أعاد تهذيبه: وقد رأى ابن حجر (الإصابة ٣/ ٣٣٦) كتابين مستقلين فى التصحيف للعسكرى. وما وصل إليه هو: / أ) «شرح ما يقع فيه التصحيف والتحريف»، جزء من الكتاب الأصلى أو أحد الكتب المفردة المستقلة بذاتها، ويحتوى على ثلاثة أقسام:
١ - تصحيف فى أبيات من الشعر، مرتبة تبعا للغويين الذين رووا خطأها.
٢ - تصحيف فى أبيات، مرتبة على حسب الشعراء.
٣ - ما يصحف من أسماء أبطال وشعراء الجاهلية (انظر «إرشاد الأريب» لياقوت ٣/ ١٢٩ - ١٣١) الخ.

<span data-type="title" id=toc-412>المخطوطات:</span>
دار الكتب بالقاهرة، أدب ١٩٤ (المجلد الأول ويتضمن الجزء الأول والثانى، ١٩٢ ورقة؛ المجلد الثانى ويتضمن الجزء الثالث، ٤٩ ورقة، من القرن السادس أو','1','324',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (321,'السابع للهجرة، انظر الفهرس الثانى ٢/ ١٩، ٣/ ٢١٨؛ وفهرس<span data-type="title" id=toc-413> المخطوطات </span>١/ ٣٥٨)، التيمورية بدار الكتب، أدب ١٣٤٠، المتحف البريطانى ١٩٢) ٣٠٨٧ Or.، ورقة، نسخت سنة ١٢٩٤ هـ، نسخة منقولة عن مخطوطة القاهرة، انظر الملحق رقم ٨٤٢)، ييل ١٩٨) ٤٥ - L ورقة، ١٣٠٧ هـ، انظر نيموى رقم ١٤٨)؛ طبع الجزء الأول بالقاهرة ١٣٢٧.
ونشره كاملا عبد العزيز أحمد، القاهرة ١٩٦٣؛ ونشره من جديد م. يوسف وأحمد راتب النفاخ، الجزء الأول دمشق ١٩٨١.
ب) «تصحيفات المحدّثين»، مختصر من كتاب التصحيف «الكبير»، ويتألف من ثلاثة أقسام: ١ - تصحيفات فى القرآن، ٢ - تصحيفات فى الحديث، ٣ - الرواة الذين رووا المصحّف.
المخطوطات:
دار الكتب بالقاهرة، مصطلح ٢ ش (٢٠٤ ورقة، نسخت سنة ١٣١٠ هـ، انظر فهرس المخطوطات ١/ ٣٤٤، ٢/ رقم ١٧٨؛ الفهرس، مصطلح ١٧٨)، التيمورية، تفسير ٥٤٥ (نسخت سنة ١٣٤٠ هـ، انظر الفهرس ١/ ١٦٩)، المتحف البريطانى ٢٢٠) ٣٠٦٢ Or.، ورقة، نسخت سنة ١٢٩٧ هـ، انظر الملحق، رقم ١٦٣).
طبع على هامش النهاية لابن الأثير، القاهرة ١٣١١ (انظر سركيس ٣٥، ١٣٢٧).
ج) «أخبار المصحّفين»، وكان فى خزانة كتب الخطيب البغدادى (انظر التسمية للمالكى فى كتاب الخطيب البغدادى ليوسف العش ١٠٦، رقم ٣١٨). ولعل مختصرا منه فى الظاهرية، عام ٤٥٧٤ (٨ ورقات، من القرن الثامن للهجرة، انظر فهرس التاريخ ٢/ ٦٠٧) مع أخبار عن تصحيفات لبعض اللغويين، لا سيما في أسماء الأعلام، وكذلك فى الحديث، والشعر، والكتب الديوانية وغير ذلك.

<span data-type="title" id=toc-414>الحاتمى</span>
هو أبو محمد بن الحسن (أو الحسين) بن المظفر البغدادى النحوى اللغوى','1','325',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (322,'الكاتب، توفى سنة ٣٨٨/ ٩٩٨ (انظر فصلى النحو والأدب). ومع أنه كان أيضا لغويا معروفا، لم يرو لنا سوى اسم كتاب واحد من كتبه فى اللغة.

<span data-type="title" id=toc-415>مصادر ترجمته:</span>
«الإمتاع والمؤانسة» للتوحيدى ١/ ١٣٥؛ «الوافى بالوفيات» للصفدى ٢/ ٣٤٣ - ٣٤٤؛ «مسالك الأبصار» لابن فضل الله ٤/ ٢٤٨ - ٢٤٩.- «الأعلام» للزركلى ٦/ ٣١٢، «معجم المؤلفين» لكحالة ٩/ ٢٢٢ - ٢٢٣.

<span data-type="title" id=toc-416>آثاره:</span>
«كتاب فى اللغة»، لم يتم، انظر «إرشاد الأريب» لياقوت ٦/ ٥٠٣، «بغية الوعاة» للسيوطى ٣٥.

<span data-type="title" id=toc-417>الشّمشاطى</span>
هو أبو الحسن على بن محمد بن المطهّر العدوى (المتوفى بعد سنة ٣٩٤/ ١٠٠٤)، الأديب المعروف (انظر فصل الأدب)، ألّف أيضا في النحو (انظر فصل النحو) واللغة/ (انظر ابن النديم ١٥٤؛ «الرجال» للنجاشى ٢ ٢٠٠ - ٢٠٢، «إرشاد الأريب» لياقوت ٥/ ٣٧٦).
١ - «عمل» كتاب العين للخليل بن أحمد (انظر ص ٥٣ آنفا)، ترك فيه الألفاظ غير المستعملة وشواهد الشعر وما لا حاجة به من التكرار، وعوضا عن هذا أضاف إليه ألفاظا أخرى من الكتب.
٢ - «كتاب المثلث (الصحيح)»، مرتب على حروف المعجم.
٣ - «كتاب ما تشابهت مبانيه (بدلا من: «معانيه») وتخالفت معانيه».

<span data-type="title" id=toc-418>وله في لغة القرآن:</span>
«كتاب غريب القرآن».

<span data-type="title" id=toc-419>زيد بن رفاعة</span>
هو أبو الخير (القاسم) زيد بن عبد الله بن مسعود بن رفاعة الهاشمى، وقيل إنه','1','326',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (323,'كان من إخوان الصفاء؛ وعاصر الصاحب بن عباد. ويبدو انه فضلا عن اشتغاله بالنحو واللغة عالج الفلسفة والحديث. روى عن ابن دريد وأبى بكر بن الأنبارى.
ورمى بوضع الأحاديث. توفى نحو آخر القرن الرابع/ العاشر.

<span data-type="title" id=toc-420>مصادر ترجمته:</span>
تاريخ بغداد ٨/ ٤٥٠ - ٤٥١؛ «الامتاع والمؤانسة» للتوحيدى ٢/ ٣؛ «المنتظم» لابن الجوزى ٩/ ١٢٧؛ «ميزان الاعتدال» للذهبى ٢/ ١٠٣؛ «لسان الميزان» لابن حجر ٢/ ٥٠٦ - ٥٠٨.- مصطفى جواد في: مجلة المجمع العلمى العربى بدمشق ٢٢/ ٧ ١٩٤/ ١٨٢؛ «الأعلام» للزركلى ٣/ ٩٨؛ «معجم المؤلفين» لكحالة ٤/ ١٩٠.

<span data-type="title" id=toc-421>آثاره:</span>
١ - «جوامع كتاب إصلاح المنطق»، مختصر من كتاب ابن السكيت (المتوفى نحو سنة ٢٤٤/ ٨٥٨).

<span data-type="title" id=toc-422>المخطوطات:</span>
برلين ٦٩٢٩ (١٧٩ ورقة، نسخت سنة ١٠٩١ هـ)، آصفية، لغة ١٥٢ (٥٩٩ هـ، انظر الفهرس ٢/ ١٤٢٨).
ونشره على هذه المخطوطة الأخيرة ف. كرنكو، حيدرآباد ١٣٥٤، راجع» -٢ A .. Ahmedaliin: ZDMG ٩٠ /١٩٣٦ /٢٠١ - ٢٠٨ مجاز الفتيا (؟ )»، كان معروفا في الأندلس (انظر فهرست ابن خير ٣٧٤).
كتاب الأمثال، المجهول المؤلف، والمطبوع في حيدرآباد ١٣٥١ (انظر ص ٢٦١ بعد) فى فهارس مطبوعات دائرة المعارف العثمانية (على سبيل المثال ١٩٥٦، ص ٢٦، رقم ٧٩) (انظر، (Sellheim ,Sprichworter ١٤٣ - ١٤٤ ووقعت في هذا الخطأ أيضا مؤخرا عائشة عبد الرحمن في: المورد ٩، ٣/ ١٩٨٠/ ٢٥٠.

<span data-type="title" id=toc-423>أبو هلال العسكرى</span>
هو الحسن بن عبد الله بن سهل، كان تلميذا لأبى أحمد العسكرى، وكثيرا ما يقع','1','327',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (324,'الخلط بينهما، نظرا إلى توافقهما في الاسم واسم الأب والنسبة.
وذكر في بعض الأخبار (بغية الوعاة للسيوطى ٢٢١) أن أبا هلال كان ابن أخت أبي أحمد العسكرى. ولا يعرف عن أحوال حياته إلا القليل: كان يكسب معاشه من التجارة. وفي سنة ٣٩٥/ ١٠٠٥ فرغ من إملاء مصنفه «كتاب الأوائل». وذكر السيوطى أنه عاش إلى ما بعد سنة ٤٠٠/ ١٠٠٩ - ١٠١٠ (١٣٧).
وبرع أبو هلال في اللغة والنحو (انظر فصل النحو) والأدب (انظر فصل الأدب) وعرف أيضا بقول الشعر (انظر تاريخ التراث العربى ٢/ ٦١٤). وقد اشتهر في العصور التالية بمصنفاته: كتاب الصناعتين، وكتاب الأوائل، وجمهرة الأمثال، وديوان الأمثال.

<span data-type="title" id=toc-424>مصادر ترجمته:</span>
«إرشاد الأريب» لياقوت ١ ٣/ ١٣٥ - ١٣٩، ٢ ٨/ ٢٥٨ - ٢٦٧؛ «إنباه الرواة» للقفطى ٤/ ١٨٣؛ ابن قاضى شهبة، المخطوط ص ٢٥٣ - ٢٥٤.-
Flugel, Gramm. Schulen ٢٥٤ - ٢٥٥; Wustenfeld, Geschichtsschrei ber ١٥٧; Re- scher, Abri BII, ٢١٠ - ٢١٣; Br. SI, ١٩٣ - ١٩٤; J. Fuckin: EI, I ٢, ٧١٢;
«الأعلام» للزركلى ٢/ ٢١١ - ٢١٢؛ «معجم المؤلفين» لكحالة ٣/ ٢٤٠؛ بدوى طبانة، «أبو هلال العسكرى ومقاييسه البلاغية والنقدية»، القاهرة، ١٩٦٠.
G. Kanazi, The Worksof Abu Hilalal- Askarlin: Arabica ٢٢/ ١٩٧٥/ ٦١ - ٧٠.

<span data-type="title" id=toc-425>آثار أبى هلال:</span>
١ - «جمهرة الأمثال»، ويقال له أيضا «كتاب الأمثال»، ذكره ياقوت وغيره، المخطوطات: ولى الدين ٢٥٩٠ (١٥٨ ورقة، من القرن التاسع للهجرة، (انظر، O. (Rescherin: ZDMG ٦٤ /١٩١٠ /٥١٣ بشير أغا ٥٥١ (١٩٠ ورقة، نسخت سنة
_________
(١٣٧) المعروف عند أكثر المؤرخين أن أبا هلال توفى في السنة التى انتهى فيها من تأليف كتابه (الأوائل) وكتب أبو هلال في آخر كتاب الأوائل «وفرغنا من إملاء هذا الكتاب يوم الأربعاء لعشر خلت من شعبان سنة خمس وتسعين وثلاثمائة، وعلى هذا تكون وفاة أبى هلال سنة ٣٩٥ هـ.','1','328',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (325,'٦٨٠ هـ، راجع رشر في، (MFO ٥ /١٩١٢ /٥٣٥ كوبريلى ١٢٣٣ (٢٠٧ ورقة، نسخت سنة ٥٨٥ هـ، انظر رشر في؛ MSOS ١٤ /١٩١١ /١٩٨ فهرس المخطوطات ١/ ٤٤٢)، طهران، مجلس ٥٦٩ (١٦٤ ورقة، نسخت سنة ٥٠١ هـ)، ومنها ميكروفلم بمعهد المخطوطات العربية بالقاهرة، الظاهرية، عام ٤٠٩٠ (حوالى ٢٠٠ ورقة)، البلدية بالاسكندرية ١٣٧١ ب (نسخة قديمة، انظر الفهرس (١) الأدب ٨٥)، المتحف البريطانى ٢٤٤) ٣٨٦٧ Or.، ورقة، نسخت ١٠٨٤ هـ، انظر الملحق رقم ٩٩٦)؛ طبع في بومباى ١٣٠٧ وعلى هامش مجمع الأمثال للميدانى، القاهرة ١٣١٠.
وحققه محمد أبو الفضل إبراهيم وع. م. قطامش، فى مجلدين، القاهرة ١٩٦٤، انظر في محتواه ومصادره» -٢ Spellheim ,Sprichworter ١٣٨ - ١٤٢ ترتيب كتاب جمهرة الأمثال ... لأبى أحمد ... العسكرى (على حروف المعجم)»، وهو ترتيب كتاب أستاذه أبي أحمد العسكرى على حروف المعجم، ويوجد مخطوطا في نور عثمانية ٤٧٤٦ (٢٦٨ ورقة، نسخت سنة ٨٢٦ هـ، انظر. (Sesen ,Nawadir I ,٢٩٨ - ٢٩٩
٣ - «كتاب التلخيص (فى معرفة أسماء الأشياء ونعوتها وشرح أنواعها وفنونها التى تفتقر عامة أهل الأدب إلى علمها وتحتاج إلى إتقانها وحفظها)»، «كتاب مفيد» (إرشاد الأريب لياقوت ٣/ ١٣٦).
ويوجد مخطوطا في لاله لي ٣٥٥١ (١٤٩ ورقة، من القرن السادس للهجرة، آخره ناقص، انظر فهرس المخطوطات ١/ ٣٤٧، راجع O.Rescherin: MFO ٥ /١٩١٢ /. (٥٢٦ نشره عزة حسن، فى مجلدين، دمشق ١٩٦٩، وراجع ما كتبه في: مجلة المجمع العلمى العربى بدمشق ٤٣/ ١٩٦٨/ ٢١٢ - ٢١٨.
ويبدو أنه هو نفسه «كتاب الأسماء والصفات» الذى نقل عنه في تاج العروس ٢ ١١/ ١٣٤ (خبر).
٤ - «كتاب الفروق (اللغوية)» أو «كتاب الفرق بين المعانى»، «وهو كتاب حسن» (القفطى)، وهو من مصادر «خزانة الأدب» (انظر إقليد الخزانة للميمنى ٨٠)،','1','329',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (326,'<span data-type="title" id=toc-426>المخطوطات: </span>راغب ١٤٢٩ (١٤٧ ورقة، نسخت سنة ١١٣٦ هـ)، و ١٤٣٠ (١٠٠ ورقة، نسخت سنة ٨٥٠ هـ، انظر، (O.Rescherin: ZDMG ٦٤ /١٩١٠ /٥٢١ - ٥٢٤ آصفية، لغة ١٧٢ (انظر الفهرس ٢/ ١٤٤٠)، دار الكتب بالقاهرة، أدب ٧٣ ش (انظر الفهرس الثانى ٢/ ٢٢)، التيمورية، لغة ٢٥٨ (٨٨ ورقة، نسخت سنة ٩٤٩ هـ)، و ٢٥٩ (٢٠٣ ورقة، نسخة حديثة، راجع مجلة المجمع العلمى العربى بدمشق ٣/ ١٩٢٣/ ٣٤٠)، البلدية بالاسكندرية ١٢٧٩ ب (١٠٩ ورقة، نسخت سنة ٧٥٨ هـ، انظر الفهرس (١) اللغة ٦٥)، ومنها مصورة بدار الكتب بالقاهرة، لغة ٨٠٤ (انظر الفهرس الثانى ٧/ ١١)، تونس، دار الكتب الوطنية، الزيتونة ٢٣٦٣ (١٢١ ورقة).
طبع في القاهرة ١٣٥٣، وفي بيروت، الطبعة الأولى ١٩٧٣، الطبعة الرابعة ١٩٨٠.
وعلّق عليه بكتب منها «اللمعة (أو اللمع) من الفروق»، و «نبذة من كتاب الفروق»، و «مختصر الفروق اللغوية»، ولعلها مختصرات متشابهة إلى حد بعيد لمجهولين، وتحتاج إلى فحص مفصّل.
المخطوطات:
المتحف العراقى ببغداد ١٤٨١/ (١٤ ورقة، نسخت سنة ١٣١٩ هـ، انظر فهرس النقشبندى ١٠٥، رقم ٣٧٧)، جاكارتا، المتحف ٦٤٤/ ٢ (١٥ أ- ٢٤ ب، انظر الملحق، رقم ٦٦٧)، أمبروزيانا بميلانو) ٥/ ٧٥ A الأوراق ٢٧ - ٣٥ أ، نسخت سنة ١٢٢٢ هـ، انظر، (E.Griffiniin: RSO ٣ /١٩١٠ - ١١ /٥٨٥ تشستربيتى ٣٩٩٩/ ٣ (الأوراق ٤٠ - ٤٨، نسخت سنة ٦١٦ هـ)؛ وطبع في بولاق ١٣٢٣ (انظر سركيس ١٣٢٨)، والقاهرة ١٣٤٦.
٥ - «المعجم في بقية الأشياء» أو «أسماء بقايا الأشياء»، لم يذكر بين مصنفاته، المخطوطات: عاشر ٤٣٣/ ٦ (٧٢ أ- ٩٨ ب، من القرن السابع للهجرة، انظر، (O.Re -scherin: ZDMG ٦٨ /١٩١٤ /٣٨٩ حميدية ١٤٦٤ (الأوراق ٦٩ - ٩٩، من القرن السادس للهجرة)، الظاهرية، عام ٥٩٢٥، دار الكتب بالقاهرة، أدب ٢٢ ش','1','330',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (327,'(ضمن مجموع، نسخ سنة ١٣٠٦ هـ، انظر الفهرس الثانى ٣/ ٣٦٢)، وبها نسخة منها، لغة ٨٦٦ (٢٢ ورقة، انظر الفهرس الثانى ٧/ ١٧)، التيمورية، لغة ١٧٠ ومجموع ٨٠/ ٦ (٣١٥ ب- ٣٣٥ أ، من القرن العاشر للهجرة)، برلين، ٢/ ٩٨٢ Spr) ٧٠٥٢ الأوراق ١٥ - ٣٤)؛ ونشره رشر مختصرا ومع ترجمة لبعضه في MSOS.١٨ /١٩١٥ /١٠٣ - ١٣٠: وحققه إبراهيم الأبيارى وع. شلبى، القاهرة ١٩٣٤.
٦ - «شرح الفصيح»، شرح على كتاب «الفصيح» لثعلب (المتوفى سنة ٢٩١/ ٩٠٤)، ونقل عنه في كتابه جمهرة الأمثال ٢/ ٣٠٤، ويوجد مخطوطا في سراى، مدينة ٥٥٧ (٢٠٦ ورقة، من القرن السابع للهجرة، انظر فهرس المخطوطات ١/ ٣٥٨).
٧ - «كتاب ما تلحن فيه الخاصة» أو «كتاب لحن الخاصة»، ذكرته ثلاث قوائم، ولعله قائم على «كتاب ما لحن فيه الخواص» لأبى أحمد العسكرى (انظر ص ١٨١ آنفا).

<span data-type="title" id=toc-427>وله في لغة القرآن وغير ذلك:</span>
١ - «كتاب المحاسن (فى تفسير القرآن)» (١٣٨)، وهو، على ما يقال، تفسير للقرآن فى خمس مجلدات (ياقوت)، ذكرته أربع قوائم.
٢ - «كتاب في أخبار القضاة وما جرى لهم مع الأمراء والخلفاء» (القفطى) أو «كتاب من احتكم من الخلفاء إلى القضاة» (فى ثلاث قوائم أخرى)، وقد وصل إلينا باسم «كتاب ما احتكم به الخلفاء إلى القضاة»، عاشر ٤٣٣/ ٢ (١٢ أ- ٣٠ أ، من القرن السابع للهجرة، راجع G.Kanazi في المرجع المذكور ص ٦٨)، ومنه جزء في حميدية ١٤٦٤ (الأوراق ١٠٩ - ١١٨ ب، من القرن السادس للهجرة، انظر المرجع نفسه، ص ٧٠).
_________
(١٣٨) إن مخطوطة مشهد ومطبوعة طهران ١٢٦٨ المنسوبة لأبى هلال (Br.SI.١٩٤) يحتويان على تفسير للقرآن للإمام الحادى عشر الحسن بن على بن محمد العسكرى (المتوفى سنة ٢٦٠/ ٨٧٤)، انظر تاريخ التراث العربى ١/ ٤١، راجع، G.Kanazi المرجع المذكور، ص ٦٧). ولعل ما ذكره ياقوت مردّه أيضا إلى خلط قديم بين العسكريين.','1','331',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (328,'٣ - «كتاب الوجوه والنظائر»، وعند القفطى: «كتاب النظائر»، ونقل عنه العسكرى نفسه في «كتاب الفروق اللغوية»، القاهرة ١٣٥٣، ص ١١١، ولعله محفوظ في قيصرية، رشيد ١٠٦٦ (١٤٠ ورقة، من القرن التاسع للهجرة، انظر. (Sesen ,Nawadir I ,٢٩٩

<span data-type="title" id=toc-428>الشريف الرضى</span>
هو أبو الحسن محمد بن الحسين بن موسى العلوى (٣٥٩/ ٩٧٠ - ٤٠٦/ ١٠١٥)، العالم المتعدد الفنون، الأديب (انظر فصل تاريخ الأدب)، الشاعر (انظر تاريخ التراث العربى ٢/ ٥٩٥ - ٥٩٧).
تعلم النحو في صباه على أبى سعيد السيرافى (المتوفى سنة ٣٦٨/ ٩٧٩) أو على ولده ابن السيرافى (المتوفى سنة ٣٨٥/ ٩٩٥). وقرأ القرآن على أبى إسحاق إبراهيم بن أحمد بن محمد الطبرى المقرئ المالكى (المتوفى سنة ٣٩٣/ ١٠٠٣، انظر تاريخ بغداد ٦/ ١٩ - ٢٠) ودرس/ الفقه على أبى عبيد الله محمد بن محمد بن النعمان الشيخ المفيد (٣٣٣/ ٩٤٤ - ٤١٣/ ١٠٢٢، انظر تاريخ التراث العربى ١/ ٥٤٩ - ٥٥١)، كما أخبر ابن أبى الحديد في مقدمة «شرح نهج البلاغة» (١/ ٣٤، ٤١).

<span data-type="title" id=toc-429>آثاره:</span>
<span data-type="title" id=toc-430>فى لغة القرآن، ومعانيه، ولغة الحديث:</span>
١ - «معانى القرآن»، أثنى عليه ابن جنى (انظر «إنباه الرواة» للقفطى ٣/ ١١٥؛ ابن خلكان ٢/ ٣ وفي مواضع أخرى كثيرة)، ولعله هو نفسه الكتاب التالى ذكره.
٢ - «حقائق التأويل في متشابه التنزيل» (عنوان المخطوطة) أو «حقائق التنزيل» («الرجال» للنجاشى ٣١١)، وبقى منه الجزء الخامس في مشهد، رضا ١٣٢٠ (نسخ سنة ٥٣٣ هـ، انظر مجلة معهد المخطوطات العربية ٦/ ١٩٦٠/ ٣٣٠؛ الذريعة ٧/ ٣٢ - ٣٣)، وتوجد نسخ منقولة عنه في مكتبات خاصة بالعراق.
ونشره على إحداها ع. الحلى وم. ن آل كاشف الغطاء، النجف ١٩٣٦، راجع «أعيان الشيعة»، مجلد ٤٤، ص ١٧٨.','1','332',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (329,'٣ - «تلخيص البيان في مجازات القرآن» أو «مجاز القرآن» («الرجال» للنجاشى ٣١١) أو «مجازات القرآن» (إنباه الرواة للقفطى ٣/ ١١٥)، «جاء نادرا في نوعه» كما يقول ابن جنى «إنباه الرواة» للقفطى ٣/ ١١٥؛ ابن خلكان ٢/ ٣ وفي مواضع أخرى، وألف بعد «حقائق التأويل» بزمان، ولم يصل إلينا بتمامه، طهران، دانشكاه ١٠٣٤ (٢٣٤ ورقة، من القرن السادس للهجرة، انظر الفهرس ١/ ٦٧؛ الذريعة ١٩/ ٣٥١ - ٣٥٢)، ومنها مصورة بمعهد المخطوطات العربية بالقاهرة.
وطبع م. المشكاة صورة المخطوطة مع تقديم لحسين على محفوظ، طهران ١٩٥٣ (راجع محمد بهجة الأثرى في: مجلة المجمع العلمى العربى بدمشق ٣١/ ١٩٥٦/ ١٢٢ - ١٢٣).
ونشره عليها محمد عبد الغنى حسن، القاهرة ١٩٥٥ (انظر في هذه الطبعة شفيق جبرى في: مجلة المجمع العلمى العربى بدمشق ٣٠/ ١٩٥٥/ ٤٥٨ - ٤٦٠).
ونشره م. السيد جاسم في بغداد ١٩٥٥ - ١٩٥٦ على مخطوطة كاملة (من القرن ١٣ هـ) يملكها م. الجزائرى، وثمة مخطوطة أخرى في لندن أو كيمبردج، مجموعة بلشاه (انظر. (E.Edwardsin: Or.Stud.Browne ١٣٧
٤ - «مجازات الآثار النبوية» («الرجال» للنجاشى ٣١١)، ويوجد مخطوطا في كلية الآداب بجامعة بغداد، وكان سابقا ملكا لحسين على محفوظ (نسخت سنة ٩٨٢ هـ، انظر مجلة معهد المخطوطات العربية ٦/ ١٩٦٠/ ٢٦).
ونشره حسن صدر الدين باسم «المجازات النبوية» بغداد ١٣٢٨ (راجع: الذريعة ١٩/ ٣٥١)، ونشره أيضا م. مصطفى، القاهرة ١٩٣٧.
ولتقى الدين إبراهيم بن على بن الحسن الكفعمى (المتوفى سنة ٩٠٥/ ١٥٠٠) مختصر لهذا الكتاب سماه «اختصار المجازات النبوية»، انظر «معجم المؤلفين» لكحالة ١/ ٦٥، ويصحح، (Br.SII ,١٣٣ ويوجد مخطوطا ضمن مجموع به كتب بخط الكفعمى (انظر الذريعة ١/ ٣٥٨، وأيضا أعلى ص ٣٥٦).

<span data-type="title" id=toc-431>وله في الفقه:</span>
١ - «كتاب تعليق خلاف الفقهاء» (الرجال للنجاشى ٣١١)، ولعله على كتاب','1','333',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (330,'«مسائل الخلاف في الفقه» لأخيه الشريف المرتضى (انظر. (Br.SI ,٧٠٥ ,٧٠٦
٢ - «أخبار قضاة بغداد»، ذكره الصفدى في «الوافى بالوفيات» ٢/ ٣٧٥ (ربما نقلا عن ابن جنى).

<span data-type="title" id=toc-432>وله في النحو:</span>
«كتاب تعليقة في إيضاح أبى على الفارسى»، تعليقه على «الإيضاح العضدى» وهو أكبر كتب أبى على الفارسى (المتوفى سنة ٣٧٧/ ٩٨٧) فى النحو، ذكره النجاشى فى «الرجال» ص ٣١١ ومواضع أخرى./

<span data-type="title" id=toc-433>أبو منصور اللغوى</span>
هو أبو الحسن بن محمد بن عزيز (١٣٩)، كان لا يزال حيا في شهر شعبان من سنة ٤٣٧/ فبراير- مارس من سنة ١٠٤٦. ولا يعرف عن حياته أكثر من هذا.
انظر «الوافى بالوفيات» للصفدى ١٢/ ٢٤٤ (عن ياقوت)؛ و «بغية الوعاة» للسيوطى ٢٢٩.
وله: «ديوان العرب وميدان الأدب»، فى اللغة، عشرة مجلدات، ومرتب على حروف المعجم، رأى ياقوت نسخة منه عليها تعليق قراءة للمؤلف.
_________
(١٣٩) وقع في «كشف الظنون» ٨٠٠ و «معجم المؤلفين» لكحالة، خلط بينه وبين: أبى محمد (ال) حسن بن محمد بن على بن رجاء (أو: ابن الدهان) النحوى (المتوفى سنة ٤٤٧/ ١٠٥٥، انظر «إنباه الرواة» للقفطى ١/ ٣٠٤؛ «الوافى بالوفيات» للصفدى ١٢/ ٢٣٠ - ٢٣١ مع ذكر لمصادر أخرى).','1','334',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (331,'<span data-type="title" id=toc-434>٤ - اللغويون في فارس</span>
<span data-type="title" id=toc-435>ابن بزرج</span>
هو عبد الرحمن بن بزرج، لغوى لا يعرف عنه غير ذلك، توفى على ما يبدو في الربع الأول من القرن الثالث/ التاسع.
«التهذيب» للأزهرى ١/ ١٩؛ «إنباه الرواة» للقفطى ٢/ ١٦١ - ١٦٢.
له «كتاب النوادر» فى اللغة وأفاد منه الأزهرى بخط أبى الهيثم الرازى (انظر ص ١٦٠ آنفا)، وعن طريق كتاب شمر. ونقل عنه الأزهرى في «التهذيب» ١/ ٧٤، ٩٢، ١٦٣، ٢٠٦، ٢١٨، ٢٨٨، ٣٦٤، ٤٠١، ٤٠٣، ٢/ ١٤٧، ١٨٥، ١٩٩، ٢٠٢، ٢١٧، ٢٢٦، ٢٥٦، ٢٥٩، ٣/ ١١٨، ١٧٠، ١٩٣، ٣٨٤، ٤/ ١٢٤، ٢٥٥، ٢٨٢، ٣٢٩، ٤٧٧، ٥/ ٨٩، ١٠١، ١٦١، ٢٥٣، ٣٣٠، ٣٧١، ٣٨٢، ٣٩٩، ٦/ ٣٦، ٥٩، ٦٢، ٦٤، ٨٦، ٩٨، ١٥٣، ٢٢٩، ٢٣١، ٢٤٦، ٢٥٥، ٣٢٤، ٣٥٨، ٣٦١، ٣٧٢، ٣٨٦، ٤٠٧، ٤٣١، ٤٧٢، ٤٧٣، ٤٨٧، ٥٢٨، ٥٧٢، ٧/ ١٢٣، ١٨٣، ٢٣٣، ٢٤٢، ٤٠٢، ٥٦١، ٥٧٢، ١٠/ ١١، ١٧، ٢٨، ٦٩، ٢٠٧، ٢٨٤، ٢٨٩، ٣٠٧، ٣٠٩، ٣١٤، ٣٤٨، ٤٠٦، ٤٥٦، ٤٧٣، ٦٢٢، ١١/ ٥٢، ١٤١، ٢١٩، ٢٢١، ٢٢٥، ٢٣٠، ٢٥٥، ٢٨٤، ٢٨٧، ٤٢٣، ٤٢٧، ٤٥٨، ١٢/ ٥٥، ٢٢٣، ٢٢٦، ٢٦٠، ٢٨٩، ٣٢٧، ٣٥٢، ٣٦٤، ٤٥٢، ١٣/ ٤٦، ١٠٢، ١١٩، ١٢٨، ١٤٣، ١٥٧، ١٥٨، ٢١٥، ٢٤٦، ٢٨٢، ٣١٠، ٣٤٩، ١٤/ ٤٤، ١٧٦، ٢٣٦، ٢٣٩، ٢٧٥، ٢٨٣، ٤٠٤، ١٥/ ١٤، ٩٤، ٢٠٤، ٢٣٤، ٣٠٦، ٣٠٩، ٣٥٣، ٣٨٥، ٥٨٧، ٦٠٦، ٦٣٠؛ والصغانى في «التكملة» ١/ ٣٨، ٤٣، ٧٣، ٣٣٦، ٢/ ٢٨٣، ٢٩٢، ٢٩٤، ٤٤٤، ٥٠٦، ٥٢٥، ٥٢٦، ٣/ ٢٨، ٣٩، ١٨١، ٢٠٠، ٢٥٦، ٤٤٣، ٥٣٦، ٤/ ١٦، ٣٦، ٦٠، ٩٢، ١٢٣، ١٤٧، ١٥٧، ٢٧٣، ٢٧٨، ٢٨٨، ٣١٣، ٣٥٤، ٣٧٣، ٤٠٤؛ وياقوت في «معجم البلدان» ٢/ ٣٨٣، ٤/ ٣٥٣.','1','335',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (332,'<span data-type="title" id=toc-436>ابن هاني النيسابورى</span>
هو أبو عبد الرحمن عبد الله بن محمد بن هاني النحوى، صاحب الأخفش الأوسط، سمع النحويين واللغويين والمحدثين بالبصرة. وتوفى سنة ٢٣٦/ ٨٥٠.
انظر «التهذيب» للأزهرى ١/ ٢٤؛ و «تاريخ بغداد» ١٠/ ٧٢ - ٧٣؛ و «إنباه الرواة» للقفطى ٢/ ١٢٧ - ١٣١؛ و «بغية الوعاة» للسيوطى ٢٩٠.- و «معجمالمؤلفين» لكحالة ٦/ ١٤٣.
وله «كتاب نوادر العرب وغرائب ألفاظها»، وهو على ما ذكر الأزهرى كتاب كبير يوفى على ألفى ورقة سمع شمر من المؤلف بعضه وفرّقه فى كتبه. واستطاع الأزهرى أيضا أن يفيد إفادة مباشرة من بعض مجلدات الكتاب: ١/ ٩٣، ١٣١، ٣٠١، ٣٩٩، ٢/ ٢٨٩، ٣/ ٩٩، ١٢٤، ٢٢٣، ٢٧٣، ٣٤٨، ٤/ ٣٥، ٣٤٤، ٥/ ٢٢٧، ٢٩٩، ٣٣١، ٦/ ٣٤٧، ٤٠٩، ٧/ ٥١، ٤٣٤، ٤٥٧، ٨/ ٣٨، ٢٨٩، ٢٩٨، ٩/ ٩٢، ٢٧٧، ٣٥٣، ٣٧٩، ١٠/ ١٠٠، ١٤١، ٣٨٥، ٥٧٠، ١١/ ٧٩، ٤٠٣، ١٢/ ١٥٧، ٣٧٥، ٤٤٠، ١٣/ ٢٩، ١٣٢، ١٤٤، ١٤/ ٢٣٩، ٤١١، ١٥/ ٢٦٤./
<span data-type="title" id=toc-437>نصير الرّازى</span>

هو أبو المنذر نصير بن يوسف بن أبى نصير الرازى النحوى، وصفه الأزهرى بانه كان علامة نحويا. سمع من الكسائى، وأبى محمد اليزيدى وغيرهما. وقيل إن له مؤلفات حسانا. توفى نحو سنة ٢٤٠/ ٨٥٤.
<span data-type="title" id=toc-438>مصادر ترجمته:</span>
«التهذيب» للأزهرى ١/ ٢٢؛ «إنباه الرواة» للقفطى ٣/ ٣٤٧؛ «غاية النهاية لابن الجزرى ٢/ ٣٤٠ - ٣٤١؛ «بغية الوعاة» للسيوطى ٤٠٤.- «معجم المؤلفين» لكحالة ١٣/ ١٠٠.

<span data-type="title" id=toc-439>ومن آثاره:</span>
١ - كتاب في اللغة، كان من مصادر الأزهرى ٢/ ٢٣٤، ٥/ ٢٩٥،','1','336',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (333,'١١/ ٣٣٩؛ وابن فارس في «متخير الألفاظ»، بغداد ١٩٧٠، ٧٩؛ والميدانى في «مجمع الأمثال» (انظر المورد ٣، ٣/ ١٩٧٤/ ١٢١).
٢ - «كتاب في رسم المصحف» ذكره ابن الجزرى في «غاية النهاية» ص ٣٤١.

<span data-type="title" id=toc-440>أبو معاذ النحوى المروزى </span>(١٤٠)
هو الفضل بن خالد (عن «العباب» للصغانى ١/ ٢٤)، لغوى من أهل هراة، كان في النصف الأول من القرن الثالث/ التاسع. ولا يعلم عن حياته غير هذا. وكان ابن دريد ممن رووا عنه.
وانظر «التهذيب» للأزهرى ١/ ٢٥؛ و «إنباه الرواة» للقفطى ٤/ ١٧٤.

<span data-type="title" id=toc-441>ومن آثاره:</span>
١ - «كتاب في القرآن» نقل عنه الأزهرى: ٢/ ٢٧٠، ٣/ ١٣٤، ١٨٨، ٤٠٩، ٤/ ٢٧، ٧/ ٤١١، ٩/ ١٣، ١٠/ ١١٢، ١٢/ ٥١، ١٤/ ٢٠٠، ١٥/ ٢٢٥، ٢ - ٤؛ وابن دريد في «الجمهرة» ٢/ ٢٩٨؛ والصغانى في «التكملة» ٤/ ١١٠، ٦/ ٢٩٥، ٣٥٠، و «العباب» ١/ ٢٥ (طاء)؛ و «تاج العروس» ١٩/ ١٦٣ (بقط).
٢ - «غريب الحديث»، ذكره الخطابى (انظر بعد، ص ٢٠٨ الحاشية) ضمن كتب سابقيه.

<span data-type="title" id=toc-442>أبو العميثل</span>
هو عبد الله بن خليد (أو خالد) من أهل الرّى، مولى العباسيين، كان كاتبا وشاعرا لبيت عبد الله بن طاهر، ومؤدبا لأولاده. توفى سنة ٢٤٠/ ٨٥٤ (انظر أيضا فصل الأدب).

<span data-type="title" id=toc-443>مصادر ترجمته:</span>
ابن النديم ٤٨ - ٤٩ (طهران، ص ٥٤ - ٥٥؛ الترجمة الانجليزية ١٠٦ - ١٠٧)؛
_________
(١٤٠) وليس هو أبا معاذ (فى النص المطبوع «معاد») عبد الله بن عائذ، الذى روى كتاب «العين» للخليل عن الليث (انظر كتاب العين ص ٥٣).','1','337',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (334,'«الأغانى» ١٦/ ٣٩٥؛ «إنباه الرواة» للقفطى ٤/ ١٤٣ - ١٤٤؛ ابن خلكان ١/ ٣٢٩ - ٣٣٠؛ «الحيوان» للدميرى ٢/ ١٨٣؛ «مرآة الجنان» لليافعى ٢/ ١٣٠ - ١٣١؛ «الوافى بالوفيات» للصفدى (انظر
G. Gabrieli, Indicein: Rend. Accad. Lincei, ser. V, ٢٣/ ١٩١٥/ ٢٣٠
)؛ «البداية» لابن كثير ١/ ٣٢٢ - ٣٢٥؛ «هدية العارفين» للبغدادى ١/ ٤٤٠ - ٤٤١./
Flugel, Gramm. Schulen ٥٤ - ٥٥; Br. SI, ١٩٥; Rescher, Abri BII, ١٤٩;
«الأعلام» للزركلى ٤/ ٢١٥ - ٢١٦؛ «معجم المؤلفين» لكحالة ٦/ ٥٢.

<span data-type="title" id=toc-444>آثاره:</span>
١ - «كتاب ما اتفق لفظه واختلف معناه»، ذكره ابن النديم وآخرون بعده (انظر أيضا كشف الظنون ١٥٧٢)، واسمه في بعض<span data-type="title" id=toc-445> المخطوطات </span>«كتاب مأثور (من) اللغة».
المخطوطات:
ولى الدين ٣١٣٩ (٣٣ ورقة، نسخت سنة ٢٨٠ هـ، انظر، (O.Rescherin: MFO ٥ /١٩١٢ /٥٢٨ الظاهرية، عام ٧٩٣٦ (١٠١ أ- ١١٩ أ، انظر فهرس اللغة ١٤٥ - ١٤٦)، دار الكتب بالقاهرة، أدب ١٣ ش (نسخة من مخطوطة ولى الدين، ٣٨ أ- ٥٨ ب، نسخت سنة ١٣٢ هـ)، ومنها مصورة بالدار ١٢٢١٠ ز (انظر الملحق ٣/ ١)، برنستون، بريت، B ٢٠٠ وكانت سابقا في بيروت، مجموعة البارودى (٦٨ ورقة، من القرن الثامن للهجرة).
نشره ف. كرنكو، لندن ١٩٢٥ - ٢ - «كتاب التشابه»، من المحتمل أنه كتاب في اللغة، ذكره ابن النديم وغيره، انظر أيضا «كشف الظنون» ٤٠٨.
٣ - (كتاب المذكر والمؤنث)، ولم يذكر اسمه، وثمه نقل موافق للموضوع في «الحيوان» للدميرى ٢/ ٣٨١.','1','338',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (335,'<span data-type="title" id=toc-446>أبو الوازع الخراسانى</span>
هو محمد بن عبد الخالق، لغوى نحوى، صنّف «كتاب نوادر الأعراب» (ونقل عنه أيضا باسم «نوادر الأعاريب»)، أى انه روايات عن فصحاء الأعراب الذين اجتمعوا حول عبد الله بن طاهر (المتوفى سنة ٢٣٠/ ٨٤٤) فى نيسابور. وكان أبو تراب ممن رووا عن أبى الوازع-

<span data-type="title" id=toc-447>مصادر ترجمته:</span>
«التهذيب» للأزهرى ١/ ٣٣؛ «إنباه الرواة» للقفطى ٣/ ١٦٨.
وله «كتاب نوادر الأعراب» نقل عنه الأزهرى ١/ ٦٢، ٩٨، ١١٨، ١٥٦، ١٨٧، ٢٠٧، ٢١٣، ٢١٤، ٢٨٧، ٣٣٨، ٣٥٩، ٢/ ١١، ١٢، ٦٣، ١٦١، ١٧٥، ٣/ ١٥٤، ٤/ ٣٦، ١٥١، ٢٢٦، ٣٦٣، ٣٨١، ٣٩١، ٤٥١، ٥/ ٧، ١١٤، ١٣٣، ٣١٣، ٣٢٠، ٣٨٩، ٦/ ١٥٤، ١٩٦، ٢٦٢، ٣٤٤، ٥٢٥، ٥٦٣، ٧/ ١٠١، ٢٠٨، ٣٨٢، ٤٦٩، ٤٨٩، ٨/ ٢٦، ٧٨، ٨٦، ٢١٩، ٢٥٣، ٣١١، ٣٣٦، ٣٨٥، ٣٩٤، ٩/ ٦٩، ١٢٢، ١٦١، ١٩٣، ٣٥٥، ١٠/ ٩٩، ١٠٦، ١١٨، ١٣٦، ١٣٨، ٢٠٢، ٢٢٧، ٣٥٧، ٤١٦، ٤٣٤، ٦٣٣، ١١/ ١٠، ٣٩، ٦٣، ١٠٥، ١٩١، ٢٢٤، ٢٣٣، ٢٥٠، ٣٣١، ٣٤٧، ١٢/ ٣، ١٠٥، ٢٧٣، ١٣/ ٦٥، ١٠١، ١٥٠، ١٦٩، ١٧٩، ١٨٣، ٢٥٠، ٢٦٨، ٢٧٥، ١٤/ ٣٢٧، ١٥/ ٧٣، ٨٨، ١٢٢، ١٥٨، ٣٨٧، ٤٥٣، ٥١١، ٦٥١؛ والصغانى في «التكملة» ١/ ١٥٨، ١٧٤، ٢٨٢، ٤٩٩، ٢/ ٢٦، ٢٤٢، ٢٤٦.
ونقل أيضا عن الكتاب بذكر اسم مؤلفه أبى الوازع، انظر الأزهرى ١/ ٧٨، ٨٠، ٨١، ١٣٩، ١٤٤، ١١/ ٣٢٢؛ والصغانى في «التكملة» ٤/ ٢٧٨.

<span data-type="title" id=toc-448>شمر بن حمدويه</span>
هو أبو عمرو شمر بن حمدويه الهروى، من أهل هراة، ورحل ف ى شبيبته إلى العراق حيث أخذ عن ابن الأعرابى، والرياشى/، وأبى حاتم السجستانى، وأبى','1','339',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (336,'نصر وغيرهم. وبعد أن أقام بالعراق مدة رجع إلى خراسان. وكان لغويا بارعا بشهادة الأزهرى. توفى سنة ٢٥٥/ ٨٦٩.

<span data-type="title" id=toc-449>مصادر ترجمته:</span>
«التهذيب» للأزهرى ١/ ٢٥؛ «نزهة الألباء» للأنبارى ١ ٢٥٩ - ٢٦١، ٢ ١٣٥ - ١٣٦؛ «إرشاد الأريب» لياقوت ١ ٤/ ٢٦٢ - ٢٦٣، ١ ١١/ ٢٧٤ - ٢٧٥؛ «إنباه الرواة» للقفطى»؛ Br.SI ,١٧٩.٧٨ - ٧٧ /٢ الأعلام» للزركلى ٣/ ٢٥٣ «معجم المؤلفين» لكحالة ٤/ ٣٠٦ - ٣٠٧.

<span data-type="title" id=toc-450>آثاره:</span>
١ - «كتاب الجيم»، وهو معجم كبير، ابتدأه بحرف الجيم، وقيل إنه أودعه كثيرا جدا من الشواهد والشعر، وإنه ضنّ في حياته بإجازة روايته، وبعد أن ضاعت النسخة التى بخطه بعد وفاته بوقت قصير لم يعرف الكتاب إلا في نسخ قليلة، ولم ينقل عنه إلا نادرا.
وقد رأى الأزهرى بعض أجزاء من أول الكتاب (انظر التهذيب ١/ ٢٥، وانتقاده ص ٣٠، وراجع إنباه الرواة للقفطى ٢/ ٧٧ - ٧٨؛ «إرشاد الأريب» لياقوت ٤/ ٢٦٢ - ٢٦٣؛ «نزهة الألباء» للأنبارى ١٣٥ - ١٣٦).
٢ - «كتاب الأمثال»، نقل عنه الميدانى، انظر ع. التكريتى في: المورد، ٣، ٢/ ١٩٧٤/ ١٤، ٣، ٣/ ١٩٧٤/ ١١٧.
٣ - «كتاب السلاح»، ذكره ياقوت في «إرشاد الأريب» ٤/ ٢٦٣؛ والسيوطى في «بغية الوعاة» ٢٦٧. وانظر نقولا عنه في «التهذيب» للأزهرى ٢/ ٤٢٠، ٥/ ٣٦٩، ١٥/ ٧٥ (عن نسخة بخط المؤلف)؛ و «اللسان» ١٤/ ٢٣٠.
٤ - «كتاب الجبال والأودية»، ذكره ياقوت والسيوطى، وانظر في نقول عنه فصل الجغرافية.
٥ - «كتاب الحيّات»، نقل عنه الأزهرى ١/ ٣٣١، ٤/ ٢٢٠، ٧/ ٢١٩.','1','340',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (337,'<span data-type="title" id=toc-451>فى لغة القرآن والحديث:</span>
يؤخذ من المصادر الآنفة الذكر أن شمرا كان أيضا خبيرا بتفسير القرآن، وأن كتابه «الجيم» احتوى على مادة لغوية كثيرة من تفاسير القرآن المتقدمة.
وله «كتاب غريب الحديث»، وقيل إنه كان كتابا كبيرا جدا، احتوى على آراء أصيلة، انظر «التهذيب» للأزهرى ١/ ٢٥؛ «إنباه الرواة» للقفطى ٢/ ٧٧؛ «إرشاد الأريب» لياقوت ٤/ ٢٦٢ - ٢٦٣؛ «بغية الوعاة» للسيوطى ٢٦٧، وانظر في خلاف هذا رأى الخطابى، فى حاشية ص ٢٠٨ بعد.

<span data-type="title" id=toc-452>أبو داود السّنجىّ</span>
هو سليمان بن معبد المروزى، أصله من قرية بالقرب من مرو، وأخذ في العراق عن النضر بن شميل، والأصمعى، والهيثم بن عدى، وعبد الرزاق بن همام. واشتغل فضلا عن اللغة، بالحديث والتاريخ، توفى سنة ٢٥٧/ ٨٧١.

<span data-type="title" id=toc-453>مصادر ترجمته:</span>
التهذيب للأزهرى ١/ ٢٥؛ تاريخ بغداد ٩/ ٥١ - ٥٢؛ إرشاد الأريب لياقوت ٤/ ٢٥٥؛ إنباه الرواة للقفطى ٢/ ٢٠ - ٢١، التهذيب لابن حجر ٤/ ٢١٩ - ٢٢٠؛ بغية الوعاة للسيوطى ٢٦٣/- ٢٦٤.

<span data-type="title" id=toc-454>آثاره:</span>
١ - كتاب في اللغة كان من مصادر الأزهرى ٢/ ١٤٠، ٧/ ٣٠٣، ٨/ ٤٣، ٩/ ٨٦، ١٠/ ١٨٢.
٢ - «كتاب التأريخ»، ذكره ابن حجر في «التهذيب» ٤/ ٢٢٠.

<span data-type="title" id=toc-455>أبو تراب</span>
من أهل خراسان، رحل إلى هراة، وفيها أخذ عن شمر، وصنّف بعض أجزاء من كتابه «الاعتقاب»، ثم توجه إلى نيسابور حيث أتمّ كتابه. يرجح أنه توفى نحو سنة ٢٧٥/ ٨٨٨.','1','341',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (338,'ويبدو أنه هو نفسه اسحاق بن الفرج الذى لا يعرف من اسمه غير هذا. وتثبت فروق مخطوطات «التهذيب» للأزهرى (٤/ ٤٤٤، ٦/ ٢٢٤، ٧/ ٣٨٣، ٣٨٤، ٤٨٧، ٥٣٨، ١٢/ ٨٠، ٨٣) اسم ابن الفرج بدلا من أبى تراب.
وأثبت الاسم مرة في المتن (١/ ٣٧٥) بصورة: ابن الفرج أبو تراب. وعن معجم المترادفات الذى صنفه إسحاق بن الفرج، ولم يذكر لنا عنوانه، أورد الأزهرى نقولا تطابق في صيغتها ومحتواها ما أخذ عن كتاب «الاعتقاب» لأبى تراب (انظر بعد).
ويذكر هنا أيضا كما هو الحال في كتاب «الاعتقاب» عناوين الأبواب، نحو «باب الميم والباء» (١١/ ٢٤٤) أو «باب الصاد والفاء» (١٥/ ٥٧٣).
انظر ما نقله عنه الأزهرى في «التهذيب» ١/ ٦٩ (سمعت أبا الربيع البكرى)، ١/ ٧٨ (قال أبو الوازع)، ١/ ١٥٦، ٣٧٥، ٤٥٠، ٤/ ٣٦، ٥/ ٦٩ (سمعت خليفة الحسينى)، ١/ ٢٠٧، ٣/ ٣١٠، ٨/ ٣١١ (قال أبو عمرو)، ١/ ٢١٥، ٣٠٥، ٢/ ٢٦٤، ٥/ ٣٣٧، ٦/ ٢١٤، ٨/ ٨٨، ١٨٦ (روى لى الأصمعى، عن الأصمعى، قال الأصمعى)، ١/ ٣٣٠ (سمعت أبا السميدع)، ١/ ٣٣٤، ٨/ ٣٧٣ (قال الفراء)، ١/ ٣٦٨ (سمعت بعض بنى سليم)، ١/ ٣٩٥، ٤/ ٢١٢ (سمعت جماعة من قيس)، ١/ ٣٨٧، ٤/ ٢١٤، ٤٤١ (سمعت شجاعا السلمى)، ٣/ ٢٤٢، ٤/ ٢٣، ٨/ ٣٣٧، ١٢/ ٣٤، ١٤/ ٤٠، (سمعت مدركا الجعفرى أو الكلابى)، ٤/ ١١٨، ١٥/ ٥٧٣ (قال ابن الأعرابى)، ٤/ ٢٤٠) روى عن أبى سعيد الضرير)، ٤/ ٣٣٧، ٦/ ١٠٢ (سمعت أبا الجهم الجعفرى)، ٦/ ٢٣٧ (سمعت واقعا السلمى)، ٨/ ٣٨ (قال حصين السلمى)، ٨/ ١٦٩ (سمعت سليمان الكلابى)، ٨/ ٢٢٩ (سمعت زائدة البكرى)، ٨/ ٢٤٣ (سمعت الضبابى)، ٨/ ٣٠٧ (عن بعض العرب)، ٣٧٦ (عن أعرابى)، ٣٩٣ (سمعت أبا المقدام السلمى)، ١١/ ١٢٦ (سألت مبتكرا)، انظر أيضا ١/ ١١٦، ٣/ ٣٩٨، ٤٠٢، ٤/ ٤٤١، ٦/ ٩٩، ١٦٤، ٢٦١، ٥٣٧، ٧/ ٦٦١؛ والصغانى في «التكملة» ١/ ١٨٧، ٢/ ٨٣، ٣/ ١٥٠، ٣٥٤، ٣٥٨، ٤/ ١٠٢، ٢٥٨، ٣٤٨، ٤٠٣، ٥٣٢.','1','342',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (339,'<span data-type="title" id=toc-456>آثاره:</span>
١ - «كتاب الاعتقاب»، وهو معجم كبير للمترادفات، أثنى عليه الأزهرى كثيرا.
ونقل عن أبواب منه نحو «باب الجيم والحاء» أو «باب الجيم والخاء» (١١/ ٨)، و «باب الحاء والكاف» (٤/ ٣١١)، و «باب التاء والميم» (٩/ ٢٥٩) فى «تهذيب اللغة» ١/ ١٧٠، ١٨٣، ١٩٦، ٢١٣، ٢٦٨، ٢٩٤، ٣١٥، ٣١٧، ٣٢٠، ٣٧٥، ٣٨٢، ٢/ ١٢٢، ١٨٤، ٢٦٢، ٣٣٣، ٣/ ٢٠، ٢٦٢، ٢٦٣، ٣٢٦، ٣٧٢، ٤/ ٣٠، ٣١١، ٣٩٤، ٤٤٤، ٤٦١، ٥/ ٢٩٥، ٤/ ٦، ٩٨، ١١٥، ١٢٢، ١٥٩، ١٨١، ٢٢٤، ٤١٠، ٥٣٥، ٥٣٧، ٧/ ٧٤، ٨٠، ٨٤، ٨٦، ١٠٥، ١١٩، ١٥٣، ١٨٢، ٢١٣، ٣٨٣، ٣٨٤، ٣٩٣، ٤٦٠، ٤٨٧، ٥٣٨، ٦٧٩، ٨/ ٢٧، ٤٤، ٢١٧، ٢٣٣، ٩/ ٣٢، ٥٩، ١٠٢، ٢٠١، ٢٠٥، ٢٠٧، ٢١٥، ٢٥٩، ٣١١، ٣٤١، ٣٤٧، ٣٧٨، ٣٩٢، ٤٠٧، ٤١١، ٤١٣، ٤١٩، ٤٢٢، ١٠/ ٤٥، ٦١، ١٠٥، ١٣٧، ١٥٩، ١٧٨، ٢٧٩، ٢٨٠، ٣٠١، ٣٢٣، ٣٣٢، ٣٥٤، ٥٠٥، ٦٢٣، ٦٧٣، ١١/ ٨، ٢٩، ٤٢، ٥٤، ٨٢، ٩٨، ١٧٠، ٢٤٨، ٢٤٩، ٢٧١، ٢٩٧، ٢٩٨، ٣١٧، ٣٢٢، ٣٦٩، ٣٧٧، ٣٨٣، ٣٩١، ٤٠٣، ٤٥٦، ١٢/ ٤٣، ٧٠، ٨٠، ٨٢، ٨٣، ٩٦، ١١٧، ١٢١، ١٦٦، ١٩٤، ٢٠٠، ٢٠١، ٢٢٧، ٢٤٠، ٣٠٦، ٣٠٨، ٣٥٥، ٣٧١، ٤٢٢، ١٣/ ١١٣، ١٤٤، ١٤٨، ٢٢١، ٢٧١، ٢٧٨، ٣٤٥، ٣٥٠، ١٤/ ٥٤، ٥٩، ٦٤، ٧٠، ١١٩، ١٤١، ٢٩٦، ٣٢٢، ٣٥٥، ١٥/ ٩١، ٣٤٣، ٣٥٣، ٣٥٥، ٣٧٣، ٣٨٢، ٤٨٠، ٥٧٣؛ ونقل عنه الجوهرى في «الصحاح» ٤/ ١٣٤٣؛ وابن فارس في «مقاييس اللغة» ١/ ١٩٩؛ والصغانى في «التكملة» ١/ ٤٨، ٢١٤، ٣٤٢، ٢/ ١٣، ١٤٦، ٣/ ١٢٧، ٢١٩، ٢٢٧، ٣٨١، ٣٨٢، ٤٥٦، ٥٠٠، ٥١٧، ٤/ ٩٩، ١٩٢، ٢٢٧، ٢٤٤، ٣٢٨، ٣٣٠، ٣٤٢، ٣٦٧، ٤٢٥، ٤٣٨؛ ويحتمل في «العباب» ١/ ١٦٤، (طاء)، ١٠٤، ١٩٢؛ السيوطى في «المزهر» ١/ ١٦٩؛ والزبيدى في «تاج العروس» ٢ ٣/ ٢٩ (زهب)، ١٩٧ (صغب)، ٤/ ٢٧ (قرطب)، ١٩٨ (لتب)، ٥/ ٣٥٦ (مثث)، ٦/ ٤٢ (سلج)، ٢٢٦ (نبج)، ٧/ ٢٨٣ (شمخ)، ٨/ ٣١٠ (ضدد)، ٩/ ١٦٥ (مرد)، ١٤/ ٢٩٥ (نمر)، ٤٧٨','1','343',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (340,'(يهر)، ١٥/ ٤٩٦ (جرش)، ١٦/ ٩٠ (دمس)، ١٦٨ (شطس)؛ ٤١٥ (قهبلس)، ١٩/ ١٠٩ (وفض)، ١٧٤ (بهط)، ٤٢٦ (شمعط).
٢ - «كتاب الاستدراك على الخليل في المهمل والمستعمل»، ذكره ابن النديم والقفطى. وكان الكتاب في انتقاد الخليل بن أحمد، وقيل إن أبا تراب لم يكن محقّا في ذلك.

<span data-type="title" id=toc-457>أبو الأزهر البخارى</span>
لغوى من أهل بخارى، يبدو أنه كان في النصف الثانى من القرن الثالث/ التاسع. وصنف كتابا في عدة مجلدات تكميلا وإصلاحا لكتاب «العين» للخليل، وسماه «الحصائل». وإذا كان الأزهرى قد ذمّ الكتاب وعابه (١/ ٤٠)، فإنّ القفطى (إنباه الرواة ٤/ ٩٣) يجلّه ويرفع قدره ويرى أن الأزهرى قد وقع في هذا الرجل، وفي تصنيفه بغير حجّة.
نقل عنه الأزهرى في «التهذيب» ٢/ ٢٢./

<span data-type="title" id=toc-458>عبد الرحمن بن عيسى الهمذانى</span>
هو أبو الحسن عبد الرحمن بن عيسى بن حمّاد الكاتب، كان لغويا أديبا شاعرا من أهل همذان، توفى سنة ٣٢٠/ ٩٣٢.
ابن النديم ١٣٧ (طهران، ص ١٥٢؛ الترجمة الانجليزية ٣٠٠)؛ «إنباه الرواة» للقفطى» Br.SI.١٩٥ ;Rescher.Abri BII ,٢١٦ - ٢١٧ ;-.١٦٦ - ١٦٥ /٢ معجم المؤلفين» لكحالة ٥/ ١٦٣ - ١٦٤.
له «كتاب الألفاظ»، ذكره ابن النديم (انظر أيضا «دمية القصر» للباخرزى، المجلد الثانى، النجف ١٩٧١، ص ٤٩٧)، أثنى عليه كثيرا القفطى، وكان من مصادر حبيش بن إبراهيم بن محمد التفليسى (المتوفى سنة ٦٢٩/ ١٢٣٢) فى كتابه «قانون الأدب» (انظر مجلة المجمع العلمى العربى بدمشق ٥٠/ ١٩٧٥/ ٤٠١).','1','344',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (341,'<span data-type="title" id=toc-459>المخطوطات:</span>
أيا صوفية ٤٨٦٥/ ٢ (٨٣ أ- ١١٤ أ، نسخت سنة ٦٢٣ هـ)، ونفسها (١١٥ أ- ١٣٩ أ، نسخت سنة ٦٢٣ هـ)، شهيد على ٢٦٦١ (٥٥ ورقة، نسخت سنة ٥٤٩ هـ، انظر، (O.Rescherin: MFO ٥ /١٩١٢ /٥٢٢ فيض الله ١٦٨٤ (١٥٦ ورقة، نسخت سنة ٥٤٠ هـ)، ولى الدين ٢٦٣٨/ ٢ (١٦٦ ب ١٩٨ أ، من القرن ١١ هـ)، آصفية، إنشاء ٣١٨ (٦٨ ورقة، نسخت سنة ٧٦١ هـ، انظر الفهرس ٣/ ٥٢)، بورسه، حراتشى أوغلو ١٤٢٢/ ٤ (٥٦ أ- ١١١ أ، نسخت سنة ٥٩٢ هـ)، مانيسا ٨٠٠٩/ ٢ (٤١ أ- ٧٢ أ، نسخت سنة ٧٥٩ هـ، انظر شيشين، نوادر ٢/ ٣٣٤)، الأوقاف ببغداد ٦٠٢٦ (٥٩ ورقة، نسخت قبل سنة ٥٨٧ هـ، انظر طلس ١٧٢، رقم ٢٢٤١؛ فهرس الجبورى ٣/ ٢١٧، رقم ٥١٢١)، وباسم «تهذيب الكتاب وتلقيح الألباب»، كلية الآداب، جامعة بغداد، مجموعة محفوظ (نسخت سنة ٦٢٨ هـ، انظر مجلة معهد المخطوطات العربية ٦/ ١٩٦٠/ ٤٩)، الظاهرية، عام ٢٠٦/ ١ (١ أ- ٩٣ ب، نسخت سنة ١١٧٧ هـ، انظر فهرس اللغة ٦٢ - ٦٣)، عارف حكمت بالمدينة، أدب ٣ (٢٣٨ ورقة، مضبوطة بالشكل التام، انظر المنتخب لكحالة ٦١)، وباسم «نظائر الصناعات في صوفيا ٤٤) ٢٦٠٧ OP. ورقة، ناقصة، انظر فهرس درويش ٢/ ٢٥ - ٢٦)، ليننجراد، الجامعة (انظر، (Br.S I ,١٩٥ برلين ٨٤، ٣/ ١٩٩ Pet.) ٤٠٤٧ ب- ١١٠ أ) ليدن ١١١) ١٠٧٠ Or. ورقة، انظر فورهوف ١٠)، المتحف البريطانى) ٢٣٤٣٠ Add، آخرها ناقص، ٦٤ ورقة، من القرن ٨ أو ٩ هـ، انظر الفهرس ٦٤٠، رقم ١٣٨٤)، تشستربيتى ٣٣٧٧ (٧٣ ورقة، نسخت سنة ٥٤٩ هـ).
نشره لويس شيخو، بيروت ١٨٨٥، ٢ ١٨٩٨.
وطبع على مخطوطة شهيد على، فى استنبول ١٣٠٢ (باسم «ألفاظ الأشباه والنظائر») لعبد الرحمن بن محمد بن سعيد الأنبارى، وطبع أيضا في القاهرة ١٩٣١، وبيروت: دار الهدى ١٩٧٩، وتونس: الدار العربية للكتاب ١٩٨٠.

<span data-type="title" id=toc-460>ومما ألف على كتاب (الألفاظ):</span>
١ - شرح لأبى القاسم مهدى بن أحمد الخوافى النيسابورى الأديب (النصف الأول','1','345',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (342,'من القرن ٥/ ١١)، وقد وصل إلينا، انظر ص ٢٣٩ بعد.
٢ - شرح للعميدى (القرن ٥/ ١١، مصر)، ذكره القفطى (إنباه الرواة ٢/ ١٦٦)، لعل المراد به هو أبو سعيد محمد بن أحمد بن محمد العميدى (المتوفىسنة ٤٣٣/ ١٠٤٢، انظر «معجم المؤلفين» لكحالة ٩/ ١٣).
٣ - «نظم الجواهر في الألفاظ»، صنعة أبى العباس أحمد بن عبد الله، ويوجد مخطوطا في التيمورية بالقاهرة، لغة ٥٢٨ (نسخت سنة ٧٧٤ هـ).
وكان ديوان شعر الهمذانى، على ما ذكر ابن النديم (ص ١٦٧) ٥٠ ورقة. ونقل عنه في «الدر الفريد» ص ١٧٤.

<span data-type="title" id=toc-461>أبو حاتم الرازى</span>
هو أحمد بن حمدان بن أحمد، من أهل الرى (توفى سنة ٣٢٢/ ٩٣٤)، وهو واحد من أنبه علماء الإسماعيلية، برع في علوم الدين، وكذلك في علوم اللغة، وصنف- يحتمل قبل سنة ٣١٠/ ٩٢٢ - معجما للمصطلحات الدينية الشرعية./ وفي هذا المعجم الذى وصل إلينا ونشر باسم «كتاب الزينة» خصّ المؤلف المدخل إلى اللغة بعرض مفصل.
وفي رأيه أن اللغة العربية قد جازت أكمل الحروف وأتم الألفاظ.
ونقل الرازى عن الخليل بن أحمد نظامه الصوتى بمصوتاته (حركاته) الثلاثة وأحياز صوامته (حروفه) الثمانية.
ويعوّل في بعض اعتباراته للغة من نواح دينية على جعفر الصادق (المتوفى سنة ١٤٨/ ٧٦٥، انظر تاريخ التراث العربى ١/ ٥٢٨ - ٥٣١).
وفي تفسيره اللغوى لبعض آى القرآن يبدو أنه اعتمد اعتمادا كبيرا على «مجاز القرآن» لأبى عبيدة معمر بن المثنى (انظر فصل النحو).
وقد نبّه فجدا G.Vajda إلى وجوه شبه كبيرة بين سفر يصيره Sefer Yesirah (١٤١) المجهول المؤلف، وشرحه لدوناش ابن تميم (ظهر أمره حوالى منتصف القرن
_________
(١٤١) يحتمل أنه يرجع إلى القرن الثالث/ التاسع، وانظر في مناقشة تاريخه:
P.Kraus ,Jabiribn Hayyan II ,٢٦٦','1','346',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (343,'الرابع/ العاشر، انظر تاريخ التراث العربى ٦/ ١٩٦ - ١٩٧) وكتاب الزينة لأبى حاتم الرازى.

<span data-type="title" id=toc-462>ومن<span data-type="title" id=toc-465> آثاره:</span></span>
«كتاب الزينة (فى المصطلحات الإسلامية العربية)»، انظر في مخطوطاته: تاريخ التراث العربى ١/ ٥٧٣.
نشره حسين الهمدانى، القاهرة ١، ٢، ١٩٥٦ - ١٩٥٨، انظر
G. Vajda, Leslettresetless onsdelalangueara bed''apres Abu Hatimar- Razl in: Arabica ٨/ ١٩٦١/ ١١٣ - ١٣٠; R. Blacherein: Aranica ٨/ ١٩٦١/ ٢٠٩ - ٢١٠; G. C. Anawatiin: MIDEO ٤/ ١٩٥٧/ ٢٢٠ - ٢٢٣.

<span data-type="title" id=toc-463>المنذرى</span>
هو أبو الفضل محمد بن أبى جعفر، من أهل هراة، وغادر وطنه بإشارة من أستاذه أبى الهيثم الرازى ليكمل دروسه في بغداد. وسمع هنا لك من ثعلب والمبرد. وبرع على الخصوص في اللغة. وأنبه تلامذته هو الأزهرى الذى ينقل عنه كثيرا في تهذيبه.
توفى سنة ٣٢٩/ ٩٤١.

<span data-type="title" id=toc-464>مصادر ترجمته:</span>
«التهذيب» للأزهرى ١/ ٢٦؛ «إرشاد الأريب» ١ ٦/ ٤٦٤ - ٤٦٦، ٢ ١٨/ ٩٩ - ١٠١؛ «إنباه الرواة» ٣/ ٧٠ - ٧١؛ ابن قاضى شهبة ٨٠، المخطوط ص ٥٧٢؛ «بغية الوعاة» للسيوطى ٢٩؛ حاجى خليفة ١٠٢٥، ١٧٥٨، ١٨١٣، ١٩٦١.
؛
Flugel, Gramm. Schulen ٢١٦; Br. GI, ١٤٢, SI, ١٨٩; Rescher, ABri BII, ٢١٤
«معجم المؤلفين» لكحالة ٩/ ١٥٧ - ١٥٨.
آثاره
١ - «كتاب الفاخر والشامل»، أحد المصادر الرئيسية لتلميذه الأزهرى (انظر ١/ ٢٦)، الذى ينقل عنه باسم «المنذرى» ونادرا باسم/ «أبى الفضل» أيضا (على','1','347',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (344,'سبيل المثال ٦/ ٥٤٥، ١٠/ ٧٦، ١١/ ٢٥٨، ٤٠٨، ١٢/ ٢١٦). والأقرب تبعا لقوائم مصنفاته أننا بصدد كتابين مختلفين اسمهما «كتاب الفاخر (أو المفاخر)» و «كتاب الشامل» (١٤٢) ٢ - «كتاب الملتقط»، ذكرته قوائم كتبه، ويحتمل أنه أيضا في اللغة.
٣ - «كتاب زيادات (الغريب) المصنّف» أو «كتاب زيادات كتاب المؤلّف لأبى عبيد»، أدخله كذلك الأزهرى في كتابه (انظر ١/ ٢٦؛ ياقوت، الموضع المذكور).
٤ - «كتاب زيادات أمثال أبى عبيد»، أفاد منه أيضا الأزهرى (انظر ١/ ٢٦؛ ياقوت، الموضع المذكور)، ونقول الميدانى عن المنذرى يمكن أن تكون من هذا الكتاب (انظر المورد ٣، ٣/ ١٩٧٤/ ١٠٩ - ١١٠).
٥ - «نظم الجمان»، ذكرته القوائم، واشتمل (أيضا) على أخبار عن اللغويين والنحويين. وأفاد ياقوت من نسخة بخط الأزهرى، انظر «إرشاد الأريب» ١/ ١٢٣، ٢٩٢ - ٢٩٣، ٥/ ٥٦ - ٥٧، ٢٠٠، ٦/ ٢٢٥ - ٢٢٧ (عن» -٦ G.Bergstrasserin: ZS. (٢ /١٩٢٤ /١٩٤ كتاب الفروق»، نقل عنه الأزهرى ١٤/ ٣٩٢ - ٣٩٣.

<span data-type="title" id=toc-466>وللمنذرى في لغة القرآن والحديث:</span>
١ - «كتاب زيادات معانى القرآن للفراء»، أدخله تلميذه الأزهرى في «تهذيب اللغة» (انظر ١/ ٢٦؛ «إرشاد الأريب» لياقوت ٦/ ٤٦٥).
٢ - «كتاب زيادات غريب الحديث لأبى عبيد»، وهو كذلك من مصادر الأزهرى (أضفه في ١/ ٢٦ تبعا لياقوت، الموضع المذكور).

<span data-type="title" id=toc-467>الخارزنجى</span>
هو أبو حامد أحمد بن محمد البشتى الخارزنجى، «إمام أهل الأدب» فى عصره
_________
(١٤٢) و «كتاب مفاخر المقال في المصادر والأفعال» الذى روى مجهول الملفّ، ويوجد مخطوطا في كوبريلى ١٥٧٦ (١ أ- ٣٩١ ب، من القرن السابع هـ، انظر O.Rescherin: MSOS (١٥ /١٩١٢ /٢ ليس من مصنفات المنذرى.','1','348',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (345,'بخراسان، زار بغداد بعد سنة ٣٣٠/ ٩٤٢ في طريق الحج إلى مكة والتقى هناك بأبى عمر الزاهد (المتوفى سنة ٣٤٥/ ٩٥٧) وغيره. توفى سنة ٣٤٨/ ٩٥٠.

<span data-type="title" id=toc-468>مصادر ترجمته:</span>
«إنباه الرواة» للقفطى ١/ ١٠٧ - ١١٩؛ «إرشاد الأريب» لياقوت ٢/ ٦٤ - ٦٦؛ و «معجم البلدان» له ٢/ ٣٨٦؛ «الأنساب» للسمعانى ٥/ ٧ - ٨؛ «الوافى بالوفيات» للصفدى ٨/ ٧ - ٨؛ «اللباب» لابن الأثير ١/ ٣٣٥؛ ابن قاضى شهبة، المخطوط ص ٢٠٣ - ٢٠٤، ٥٦٣؛ «بغية الوعاة» للسيوطى ١٦٩ - ١٧٠؛ «روضات الجنات» للخوانسارى»؛ Br.SI ,٢٠٠ - ٠٦١ الأعلام» للزركلى ١/ ٢٠٠؛ «معجم المؤلفين» لكحالة ٢/ ٨٥.

<span data-type="title" id=toc-469>آثاره:</span>
١ - «تكملة كتاب العين»، استدراكات على معجم الخليل بن أحمد، وهو مؤلفه الرئيسى الذى وقع اختلاف شديد في قيمته، ذكره ياقوت وغيره (انظر أيضا كشف الظنون ١٤٤٣).
وممن نقل عنه أبو أحمد العسكرى في كتابه «التصحيف» ١٦١؛ والميدانى في «مجمع الأمثال» (انظر المورد ٣، ٢/ ١٩٧٤/ ١٤، ٣، ٣/ ١٩٧٤/ ١٢١).
وهو مصدر نقل عنه كثيرا الصاحب بن عبّاد (المتوفى سنة ٣٨٥/ ٩٩٥، انظر ص ٢٠٦ بعد) فى كتابه «المحيط» (انظر، (J.Kraemerin: Oriens ٦ /١٩٥٣ /٢٠٩ ووصل إلينا الجزء الخاص باللام- الياء، ويوجد مخطوطا في زحلة، مجموعة المعلوف ١٤٦٥ (٢٢ ورقة، نسخت سنة ٨١٥ هـ، انظر فهرس نصر الله ٤/ ١٧٨، رقم ٢٣٤)./ وفي حين يثنى السمعانى وغيره على الكتاب كثيرا، ينتقده الأزهرى (١/ ٣٢ - ٤٠؛ ترجمها (K.V.Zettersteenin: MO ١٤ /١٩٢٠ /٣٠ - ٣٨ انتقادا شديدا؛ لأن الخارزنجى كانت أكثر مصادره التى أفاد منها في مخطوطات لا سماع له فيها، وأنه صحّف وأخطأ كثيرا.
ومن هذه المصادر كما ذكرها الخارزنجى نفسه (ونقلها الأزهرى ١/ ٣٢ - ٣٣؛ والقفطى في الإنباه ١/ ١٠٨ - ١٠٩):','1','349',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (346,'للأصمعى: «كتاب الأجناس»، وكتاب «النوادر»، وكتاب «الصفات»، وكتاب «اشتقاق الأسماء»، وكتاب «السقى والأوراد» (أو: والموارد)، وكتاب «الأمثال»، وكتاب «ما اختلف لفظه واتفق معناه».
ولأبى عبيدة: كتاب «النوادر»، وكتاب «الخيل»، وكتاب «الديباج».
وللنضر بن شميل: كتاب معانى الشعر، وكتاب «غريب الحديث»، وكتاب «الصفات».
ولأبى عبيد: «الغريب المصنف»، وكتاب «الأمثال»، وغريب «الحديث». لابن السكيت: كتاب «الألفاظ»، وكتاب «الفروق»، وكتاب «الممدود والمقصور»، وكتاب «إصلاح المنطق»، وكتاب «المعانى»، وكتاب «النوادر».
ولأبى زيد: كتاب «النوادر». وكتاب «الصفات» لأبى خيرة.
ولقطرب: كتاب «الفروق»، وكتاب «الأزمنة»، وكتاب «اشتقاق الأسماء». وكتب النوادر لأبى عمرو الشيبانى، والفراء، وابن الأعرابى، والأخفش الأوسط (سعيد بن مسعدة)، وأبو محمد (يحيى بن المبارك) اليزيدى، واللحيانى. ومنها كتاب لغات هذيل لعزيز بن الفضل الهذلى (ابن الأشعث)، ونوادر الأعاريب لأبى الوازع محمد بن عبد الخالق.
٢ - «كتاب التفصلة»، ولعله كذلك كتاب في اللغة، ذكره ياقوت والصفدى والسيوطى.

<span data-type="title" id=toc-470>وله في شرح الأشعار:</span>
«شرح أبيات أدب الكاتب»، وهو شرح على أبيات الاستشهاد في كتاب ابن قتيبة (المتوفى سنة ٢٧٦/ ٨٨٩)، ذكره ياقوت وغيره، وكان في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في. (MIE ٤٩ /١٩٤٦ /١٤ ,No.٢٤٨

<span data-type="title" id=toc-471>ابن سمكة القمّى</span>
هو أحمد بن إبراهيم (أو إسماعيل) بن سمكة، وصفه القفطى بأنه كان نحويا لغويا، إماما فاضلا مذكورا في وقته، صاحب تصانيف حسان. انقطع إلى آل ابن العميد.','1','350',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (347,'وكان قريبه محمد بن سعيد بن سمكة القمى (انظر تاريخ التراث العربى ٦/ ٢٠٧) كذلك في خدمة ابن العميد. وكان فضلا عن ذلك أديبا مؤرخا. توفى نحو سنة ٣٥٠/ ٩٦١.

<span data-type="title" id=toc-472>مصادر ترجمته:</span>
«إنباه الرواة» للقفطى ١/ ٢٩؛ فهرست الطوسى ٥٥؛ «الرجال» للنجاشى ٠٧٦ - «معجم المؤلفين» لكحالة ١/ ١٣٩ - ١٦٥.

<span data-type="title" id=toc-473>آثاره:</span>
١ - «كتاب الأمثال»، ذكره القفطى، ووصفه بأنه كتاب على الأبواب ككتاب الأمثال لأبى عبيد القاسم بن سلام، إلا أنه أكبر وأكثر شرحا وبيانا/. ونقل عنه السيوطى باسم «جامع الأمثال» فى المزهر ١/ ٤٩٤ - ٤٩٥، ٥٠١.
٢ - «كتاب ذكر العسل وصفته»، وهو كتاب مستوفى فيه ما جاء في وصف العسل ومدحه، وما قيل في النحل، وما ورد في ذلك عن العرب؛ ذكرهالقفطى.
٣ - رسالة منظومة إلى ابن العميد، مقدارها ١٠٠ ورقة، ذكرها الطوسى والنجاشى.
استدراك على فصل «تدوين التاريخ» من تاريخ التراث العربى، المجلد الأول:
«كتاب العباسى»، فى أخبار الخلفاء العباسيين ودولتهم، ومقداره ١٠٠٠٠ ورقة (انظر الطوسى والنجاشى، ولكن قارنهما بابن النديم ص ١٣٩).

<span data-type="title" id=toc-474>الفارابى (اللغوى)</span>
هو أبو ابراهيم إسحاق بن إبراهيم الفارابى، وخال أبى نصر الجوهرى اللغوى.
كان أديبا نحويا لغويا (انظر أيضا فصل النحو) عاش في فارس، وقيل إنه أقام أيضا باليمن. توفى نحو سنة ٥٣٠/ ٩٦١ (ياقوت، عن مصدر مشكوك فيه) أو نحو سنة ٣٧٠/ ٩٨٠ (الصفدى، السيوطى).','1','351',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (348,'<span data-type="title" id=toc-475>مصادر ترجمته:</span>
«إرشاد الأريب» لياقوت ٢/ ٢٢٦ - ٢٢٩؛ «الأنساب» للسمعانى ١ ٤١٥ ب؛ «اللباب» لابن الأثير ٢/ ١٨٨؛ «الوافى» ٨/ ٣٩٥ - ٣٩٦؛ ابن قاضى شهبة، المخطوط ص ٢١١ - ٢١٢؛ «بغية الوعاة» للسيوطى ٠١٩١ -
Br. GI, ١٢٧ - ١٢٨, SI, ١٩٥; Rescher, Abri BII, ٢١٧ - ٢١٨;
الأعلام للزركلى ١/ ٢٨٤ - ٢٨٥؛ «معجم المؤلفين» لكحاله ٢/ ٢٢٧؛ أحمد مختار عمر في: اللسان العربى (أبحاث ودراسات) ١٧، ١/ ١٩٧٩/ ٩٢ - ١١٩ (له صلة).

<span data-type="title" id=toc-476>آثاره:</span>
١ - «ديوان الأدب (فى بيان لغة العرب، فى ميزان كلام العرب، أو نحو ذلك)» ذكره ياقوت (انظر أيضا پولس سباط في)؛ (MIE ٤٩ /١٩٤٦ /٢١ ,No.٣٧٠: كشف الظنون ٧٧٤ - ٧٧٥)، دخل في الصحاح للجوهرى، وكان مصدرا مهما لحبيش بن إبراهيم بن محمد التفليسى (المتوفى سنة ٦٢٩/ ١٢٣٢) فى كتابه «قانون الأدب» (انظر مجلة المجمع العلمى العربى بدمشق ٥٠/ ١٩٧٥/ ٤٠١).
ونقل عنه السيوطى كثيرا في المزهر (الفهرس ٢/ ٦٤٢). وقد أضحى الكتاب سابقا لعصره بترتيبه الألفاظ بحسب أواخرها. وفي الرواية المتقدمة للكتاب انظر ياقوت في إرشاد الأريب ٢/ ٢٢٧ - ٢٢٨، وانظر في محتوى الكتاب وأثره وأهميته المرجع نفسه؛ وع. مخلص في مجلة المجمع العلمى العربى بدمشق، O.Rescher؛ ٢٣٣ - ٢٢٨ /١٩٢٥ /٥ الموضع المذكور، ص. J.Kraemerin: Oriens ٦ /١٩٥٣ /٢١٢؛ ٢١٨

<span data-type="title" id=toc-477>المخطوطات:</span>
أيا صوفية ٤٦٧٧ (٤٤٢ ورقة، نسخت سنة ١١٤١ هـ)، و ٤٦٧٨ (٤٤٧ ورقة، نسخت سنة ١١٤٢ هـ، انظر. (O.Rescherin: WZKM ٢٦ /١٩١٢ /٨١ - ٨٢:
سراى، أحمد الثالث، ٢٦٥٢ (٢٦٤ ورقة نسخت سنة ٦٥٤ هـ، انظر رشر في:
RSO ٤/ ١٩١١ - ١٩/ ٧٣٠
؛ الفهرس ٤/ ٦).','1','352',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (349,'عاطف ٢٧١٧ (٤٥١ ورقة، نسخت سنة ٨٦٨ هـ، انظر رشر في. (MFO ٥ /١٩١٢ /٤٩٥
قليج على ٧٨٨ (٢٣٩ ورقة نسخت سنة ٥٤٠ هـ، انظر المرجع الآنف الذكر ص ٤٩٦؛ فهرس المخطوطات ١/ ٣٥٤)، جور ليلى على ٤٤٦ (٤٠١ ورقة، نسخت نحو سنة ٤٠٠ هـ، أولها ناقص، راجع. (O.Rescherin: MFO ٥ /١٩١٢ /٤٩٧
فاتح ٥١٩٢، وسابقا ٥١٩٣ (الجزء الثانى، ٢٢٧ ورقة، نسخت سنة ٥٨٢ هـ، انظر المرجع نفسه ص ٤٩٧).
رئيس الكتاب ١٠٨٤ (الجزء الأول، ٢٤٥ ورقة، نسخت سنة ٧٤٦ هـ)، و ١٠٨٥ (الجزء الثانى، انظر المرجع السابق ص ٥١٧)، و ١١٠١.
ولى الدين ٣١٠٥ (٢٤٨ ورقة، نسخت سنة ٥٢٤ هـ، راجع، (٥٢٧ MFO ٥ /١٩١٢ / مراد ملا ١٧٦٧ (٣٠٧ ورقة، نسخت سنة ٩٠٩ هـ)، و ١٧٦٨ (٢٣٨ ورقة، نسخت سنة ٧٨٣ هـ، انظر المرجع نفسه ص ٥٣٠)، حاجى بشير ٦٢٨ (مفقودة)، بشير أغا (أيوب) ١٢٨/ (٣٦٤ ورقة، نسخت سنة ٣٩١ هـ، انظر المرجع ذاته ص ٥٣٦؛ فهرس المخطوطات ١/ ٣٥٤).
مكتبة جامعة استنبول) ١٠٦٣ A.Y.، الجزء الأول، ١٩٢ ورقة، من القرن الخامس للهجرة)، و) ١٤٣٤ A.Y. الجزء الثانى، ١٧٨ ورقة، نسخت سنة ٣٧٢ هـ، بخط أبى نصر محمد بن أحمد الباقلانى، انظر فهرس المخطوطات ١/ ٣٥٤)، شهيد على ٢٦٧١/ ١ (٤٨١ ورقة).
فيض الله ١٦٧٧، جار الله ٢٠١٣، ينى جامع ١٠٨٤، مانيسا ٢٨٢٤ (٤١٦ ورقة، نسخت سنة ٦١١ هـ، انظر. (Listedes Manuscrits ٤
سپهسالار ١٠٥ (الجزء الأول، ٢٤٣ ورقة)، و ١٠٦ (الجزء الثانى، ٢٣٣ ورقة، آخره ناقص، من القرن الخامس للهجرة، انظر الفهرس ٢/ ١٨٥، رقم ٨٢٩، ٨٣٠.
أسعد طلس في: مجلة المجمع العلمى العربى بدمشق ٢٢/ ١٩٤٧/ ٥٠٦ - ٥٠٧، رقم ٢٨)، ملك ٣٤٥ (٣٠٩ ورقة، نسخت سنة ٤١٩ هـ، انظر','1','353',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (350,'الفهرس ١/ ٣٢٠ - ٣٢١، راجع مجلة معهد المخطوطات العربية ٦/ ١٩٦٠/ ٦٨، ٣٣١).
رضا برامبور، لغة ٢٩ (٢٥٤ ورقة، نسخت سنة ١٢٢٦ هـ)، ولغة ٣٠ (٢٢٩ ورقة، نسخت سنة ١٠٩٠ هـ، انظر الفهرس ١/ ٥٠٩، وراجع تذكرة النوادر ١١١)، الأوقاف ببغداد ١١٠٦ (٤٤٦ ورقة، نسخة قديمة، انظر طلس ١٧٣، رقم ٢٢٥٩؛ فهرس الجبورى ٣/ ٢٢٤، رقم ٥١٤٣)، المتحف العراقى ببغداد ١٢٩٧ (٥٢٨ صفحة، انظر فهرس النقشبندى ٨٩، رقم ٣١٤).
جامعة الحكمة ببغداد، مجموعة سركيس (٢١٨ ورقة، من القرن السابع للهجرة، انظر الفهرس ٦١، رقم ١٠٠).
العباسية بالبصرة، بدون رقم (٢٨٦ ورقة، انظر على الخاقانى في: مجلة المجمع العلمى العراقى ٨/ ١٩٦١/ ٢٧٤، رقم ١٩٧)، الخالدية بالقدس (٥٨٨ هـ، انظر «الأعلام» للزركلى ١/ ٢٨٥)، ومنها ميكرو فيلم بمعهد المخطوطات العربية بالقاهرة.
مجموعة الفتيانى بالقدس (نسخت سنة ٦٣٢ هـ)، ومنها كذلك ميكروفيلم بالقاهرة (انظر مجلة معهد المخطوطات العربية ٧، ٢/ ١٩٦١/ ١١٦).
المحمودية بالمدينة، أدب ٧ (الجزء الأول، ٢٨٥ ورقة، نقلت سنة ٩٢٦ هـ عن نسخة مكتوبة سنة ٦١٨ هـ)، ومنها كذلك ميكروفيلم بالقاهرة.
المتوكلية بصنعاء، لغة ٣٤ (الجزء الأول، ٣٢٦ ورقة)، ولغة ٣٥، ولغة ٣٦ (الجزء الأول، ٢٢٠ ورقة، من القرن السابع للهجرة)، ومن المخطوطتين ٣٤ و ٣٦ ميكروفيلمان بالقاهرة.
الغربية بصنعاء أدب ٤٠ (الجزء الأول، ١٠٩ ورقة، من القرن السابع للهجرة)، وبها مخطوطة أخرى بدون رقم (١٤١ ورقة، نسخت سنة ٦١١ هـ، وهو الجزء الأخير من نسخة أخرى)، ومنها ميكرو فيلم بالقاهرة.
مسجد المظفر بتعز بدون رقم (٢٤٢ ورقة، نسخت سنة ٦١٢ هـ)، ومنها ميكرو فيلم بمعهد المخطوطات بالقاهرة.
دار الكتب بالقاهرة، لغة ٢٥ (كاملة، ٢٢٢ ورقة، نسخت سنة ٥٧٤ هـ، راجع فهرس المخطوطات ١/ ٣٥٤)، ولغة ٢٣٤ (الجزء الأول، نسخ سنة ٦١١ هـ)، ولغة','1','354',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (351,'٢٦٤ (الجزء الأول)، ولغة ٣٤٤ (كاملة، نسخت سنة ٦١٧ هـ)، ولغة ٣٨٣ (كاملة، ٤٠٥ ورقة، نسخت سنة ٦٢٩ هـ، راجع الفهرس الثانى ٢/ ١٣ - ١٤).
التيمورية بدار الكتب، لغة ٤٩٨ (نسخت سنة ١١٤٦ هـ) القرويين بفاس ٨٠/ ٣٦٨ (ناقصة، انظر. Listedes Manuscrits ٤٨.No.٢٠٦ وفهرس بيل، رقم ١٢٥١)، و ١٢٤٣.
باريس ٦٦٦٣ (٢٤٨ ورقة، نسخت سنة ٥٤٧ هـ، راجع فجدا ٣٠٦؛ ونفسه فى. (Alb umdepaleographie ٦٦
ليدن ٢٣٧) ٤٧٣ Or. ورقة، نسخت سنة ٥٩٦ هـ، ناقصة)، و ١٨٥) ٥٤٧ Or. ورقة، نسخت ٦١٩ هـ، ناقصة)، و ١٦٨) ٢٣٩١ Or. ورقة، نسخت سنة ٦٥١ هـ، انظر فورهوف ٦٥ - ٦٦).
برلين، مجموعة دحداح ١٦١ (٤٠٣ ورقة، نسخت سنة ٦٢٩ هـ).
لندن، مدرسة الدراسات الشرقية والأفريقية ٤٧٩٧١ (الجزء الأول، ٢٦٦ ورقة، من القرن السابع للهجرة)، و ٤٧٩٧٢ (النصف الثانى، ١٨٩ ورقة، نسخت سنة ٦٢١ هـ، الفهرس العام ٢٢/ ٣ - ٤، وانظر. (Kat.Gacek No.٥٣ ,٥٤
المتحف البريطانى ٣٩١) ٥٠٣٢ Or.، ورقة، نسخت سنة ٨٧٩ هـ، انظر، (Ellis Descr.List ٥١ بودليانا بأكسفورد ٣٦٦) ٢٢٧ Pock، ورقة، نسخت سنة ٦٦٨ هـ، انظر فهرس، ٢٣٣ Uri رقم ١٠٨٧)، و) ٢٨١ Hunt. الجزء الأول، ٢٠٦ ورقة، نسخت سنة ٦٦٢ هـ، انظر الفهرس السابق ٢٣٧، رقم ١١١٨).
و) ٢٧٦ Hunt. الجزء الثانى، ٢٠٤ ورقة، انظر الفهرس السابق ٢٣٨، رقم ١١٢٣)، و) ٢٢٨ Hunt. الجزء الثانى، ٢٨٠ ورقة، نسخت سنة ٣٧٣ هـ، انظر الفهرس ذاته ٢٤٣، رقم ١١٥٦).
تشستربيتى ٣١٢٥ (٢٤٣ ورقة، من القرن السادس للهجرة).
برنستون، جاريت (يهودا) ٢٠٢ (٢٧٨ ورقة، نسخت سنة ١٢٠٥ هـ، انظر ماخ، رقم ٣٧٥٤)، موسكو، الكرملن (نسخت سنة ١٠٠٧ هـ، انظر كوركيس عواد في: سومر ٧/ ١٩٥١/ ٢٨١).
نشره أحمد مختار عمر وإبراهيم أنيس، فى أربعة مجلدات، القاهرة ١٩٧٤ - ١٩٧٨','1','355',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (352,'(انظر ما كتبه إبراهيم السامرائى عن هذه النشرة في: المورد ٩، ١/ ١٩٨٠/ ٤١٣ - ٤١٩).
وتقدم طبع مقدمته في: مجلة معهد المخطوطات العربية ٧، ٢/ ١٩٦١/ ١١١ - ١٥١.
ولإبراهيم السامرائي دراسة في الجذور المضاعفة: «مع ديوان الأدب ... » فى:
مجلة مجمع اللغة (الأردن) رقم ٥ - ٦/ ١٩٧٩/ ٩٠ - ١٠٥.

وقد عنى بعض العلماء بديوان الأدب وبتهذيبه، ويذكر التاريخ من<span data-type="title" id=toc-479> آثاره</span>م كتابين هما:
أ) «تهذيب ديوان الأدب» لأبى على الحسن بن المظفر النيسابورى الضرير (المتوفى سنة ٤٤٢/ ١٠٥٠، انظر معجم المؤلفين لكحالة ٣/ ٢٩٦)، انظر إرشاد الأريب لياقوت ٣/ ٢١٩، راجع بغية الوعاة للسيوطى ٢٣٠؛ كشف الظنون ٧٧٥./ ب) «تهذيب» وتصنيف جديد في عشرة مجلدات يحمل اسم الكتاب الأصلى لأبى سعيد محمد بن جعفر بن محمد الغورى، انظر «إرشاد الأريب» لياقوت ٦/ ٤٦٨، وراجع «إنباه الرواة» للقفطى ٢/ ٣٨٩؛ «بغية الوعاة» للسيوطى ٢٨ - ٢٩.

<span data-type="title" id=toc-478>الأهوازى</span>
هو أبو بكر محمد بن إسحاق، يحتمل أنه عاش قبل سنة ٣٥٠/ ٩٦١. ذكره ابن النديم ١٥٤ (طهران ١٧١).
آثاره:
١ - «كتاب النحل وأجناسه (وعروسه)»، ذكره ابن النديم.
٢ - «كتاب الفلاحة والعمارة»، ذكره ابن النديم أيضا.

<span data-type="title" id=toc-480>أبو الحسن الأهوازى</span>
لا يمكن القطع بأن هذا المؤلف هو نفسه أبو الحسين أحمد بن الحسين الكاتب الأهوازى (القرن الرابع/ العاشر، انظر تاريخ التراث العربى ١/ ٣٨٩،','1','356',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (353,'٥/ ٣١٢ - ٣١٣، ٦/ ٢٣٣، ٧/ ٤٠٧)؟ وتسوية صاحب الذريعة ٣/ ١٥٧ بينه وبين العالم الشيعى أبى الحسين بن سعيد الأهوازى (المكنى بأبى محمد، انظر تاريخ التراث ١/ ٥٣٩) ليست بمقنعة.

<span data-type="title" id=toc-481>آثاره:</span>
١ - «كتاب البهار في اللغة» (١٤٣)، كتاب في المترادفات يحتوى على عدة أبواب، ويبدأ بباب في صفة الإنسان وحليته (٦٥ ب)، وباب في صفة العين (٦٦ أ)، وباب فى صفة الأنف (٦٦ ب).
ويوجد مخطوطا في برنستون، جاريت (يهودا) ٣٥٨٦ (٦٥ ب- ٧٨ أ، من القرن ١١ هـ، انظر ماخ ٣٢١، رقم ٣٧٥٢).
٢ - «شرح المجاز (فى النحو) لابن السراج» (انظر فصل النحو)، ذكره القفطى في إنباه الرواة ٤/ ١٠٥.

<span data-type="title" id=toc-482>محمد بن بكر البسطامى</span>
كان لغويا نحويا يكاد ألا يكون معروفا، ولم يستند ياقوت فيما ذكره عن كتابه إلا على ما ذكره حمزة الأصفهاني.
ووصف حمزة كتاب البسطامى، ولكنه لم يذكر لنا عنوانه. وقال إنه صنفه على غرار كتاب الجمهرة/ لابن دريد وقصد به الفحص عن كتاب الياقوته (المجهول المؤلف)، الذى عزى فيه اعتسافا جزء كبير من علم العربية ابى ثعلب. ويبدو أن وفاة البسطامى كانت في نحو سنة ٣٥٠/ ٩٦١.

<span data-type="title" id=toc-483>وترجمته:</span>
«إرشاد الأريب» لياقوت ٦/ ٤١٨ - ٤١٩؛ «الوافى بالوافيات» للصفدى ٢/ ٢٦٢.
_________
(١٤٣) قارن بداية «كتاب التحلية» فى علامات الجسم وأسماء أعضائه (وليس في التزيين، ويجب تصحيح ذلك في تاريخ التراث ٣/ ٢٦٥) لأبى مروان عبدوس بن حكيم الكاتب: «تحلية بالسن ثم بالعلامات الظاهرة» (آيا صوفية ٤٨٢٧، ١٦١ ب)، «باب الأنوف» (١٦٢ ب)، «باب الحاجب» (١٦٢ ب)، «باب العين» (١٦٣ ب) ...','1','357',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (354,'<span data-type="title" id=toc-484>حمزة الأصفهانى</span>
هو أبو عبد الله حمزة بن الحسن، لغوى نحوى من أهل أصفهان، ولد نحو سنة ٢٨٠/ ٨٩٣. وكان أيضا أديبا ومؤرخا (انظر تاريخ التراث العربى ٦/ ٢١٠ - ٢١١، حيث يجب أن يصحح اسم أبيه هناك).
وكان من أساتذته ابن دريد، وأبو بكر بن الأنبارى، وأبو عمر الزاهد. توفى قبل سنة ٣٦٠/ ٩٧٠.

<span data-type="title" id=toc-485>مصادر ترجمته:</span>
ابن النديم ١٣٩؛ إنباه الرواة للقفطى ١/ ٣٣٥ - ٣٣٦.- حسين على محفوظ. حمزة بن الحسن الأصفهاني. سيرته و<span data-type="title" id=toc-486>آثاره </span>وآراؤه في اللغة والأدب والبلدان. بغداد ١٩٦٤ (لم أطلع عليه).
آثاره:
١ - «كتاب الخصائص والموازنة بين العربية والفارسية»، وانظر في محتواه المخطوطة الوحيدة الناقصة المعروفة إلى الآن، وهى محفوظة في القاهرة: تاريخ التراث العربى ١/ ٣٣٦، ٤/ ٢١١، ويبدو أن مصورة منها موجودة في المتحف العراقى ببغداد (انظر النقشبندى ص ٨٧، رقم ٣٠٨).
٢ - «التنبيه على حدوث التصحيف»، انظر في مخطوطاته: تاريخ التراث العربى ١/ ٣٣٧؛ نشره محمد أسعد طلس بالاشتراك مع أسماء الحمصى وعبد المعين الملوحى، دمشق ١٩٦٨.
وردّ عليه أبو نصر الصفّار، انظر فصل النحو.
٣ - «كتاب الأمثال على أفعل» أو «الدرّة» (الكلمات) الفاخرة في الأمثال السائرة الجارية على ألسنة الفصحاء»، وقد وصل إلينا الكتاب مخطوطا، انظر تاريخ التراث العربى ١/ ٣٣٦، ويوجد أيضا بدار الكتب بالقاهرة، أدب ٧٤٤٢ (٧٨ ورقة، انظر الملحق','1','358',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (355,'١/ ٧٨، حيث نسب الكتاب لأبى على القالى)، التيمورية بدار الكتب بالقاهرة، أدب ٨٠٦ (١٢٨ ورقة، نسخت سنة ١٠٧٥ هـ، انظر عبد المجيد قطامش في مقدمة التحقيق، ض ٤٩)، قوله بالقاهرة، أدب ٤٠ (١٣٥ ورقة، نسخت سنة ١١٦٣ هـ، انظر المرجع السابق ص ٥٠)؛ نشره عبد المجيد قطامش، فى مجلدين، القاهرة ١٩٧١، ١٩٧٢، والكتاب من كتب الأمثال الحافلة، انظر في مصادره؛ Sellheim Sprichworler ١٢٨ - ١٣٨، وانظر فيما نقله عنه الميدانى: ع. التكريتى في:
المورد ٣، ٢/ ١٩٧٤/ ٢٦ - ٢٨. وزاد عليه الثعالبى في «خاصّ الخاصّ»، انظر ص ٢٣٥ بعد.
٤ - «كتاب الأمثال الصادرة عن بيوت الشعر»، فى الأمثال والأقوال المأثورة الواردة في بعض الأشعار؛ وقد ذكره ابن النديم.
ويبدأ هذا الكتاب الكبير بمقدمة في طريقة النقل في الأشعار، وفي منقولات أبيات عند اليونان (مخطوطة برلين ٥ ب) وعند الفرس (٦ أ) وفي غزارة الحكم العربية. ويحتوى النص الرئيسى على نحو ٤٧٠٠ مثل من الشعر (أنصاف أبيات، وأبيات صحاح، وأزواج أبيات)، وهى مرتبة بحسب أوائل كلماتها فى سبعة أبواب، دون ذكر أسماء الشعراء أو المصادر: /.
١) باب (٧ أ- ٣٨ ب) فى ذكر أنصاف الأبيات (١٥ فصلا، ١٣٥٠ مثلا).- ٢) باب (٣٨ ب- ١٠٠ أ) فى الأبيات الصحاح (١٥ فصلا، ١٥٧٠ مثلا).- ٣) باب (١٠٠ أ- ١٢٤ أ) فى ذكر مثنى قصار الأبيات أو في الأبيات القصار السوائر بيتين بيتين (١٢ فصلا، ٦٠٠ مثل).- ٤) باب (١٢٤ أ- ١٤٥ أ) فى ذكر مثنى طوال الأبيات أو في الأبيات الطوال السوائر بيتين بيتين (١٠ فصول، ٢٧٠ مثلا).- ٥) باب (١٤٥ أ- ١٦٣ أ) فى ذكر منقولات أمثال للفرس إلى أبيات شعر، أو فى أبيات ذات أمثال منقولة من الفارسية إلى العربية (٤ فصول، ٤٠٠ مثل، وهنا يذكر لأول مرة مصدره: «من أمثال كليلة» أى كليلة ودمنة).-','1','359',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (356,'٦) باب (١٦٣ ب- ١٨٥ ب) فى جمل من الأمثال مختلفة الفنون من المنظوم والمنثور (٣ فصول، ما يربو على ٢٠٠ مثل من المنظوم، وسائرها من المنثور، وهى هنا أمثال بزرجمهر وحكم أردشير).- ٧) باب (١٨٥ ب- ١٩٠ ب) فى جمل من المقطعات تحوى سوائر أبيات فيها مضمّنات قد جرت في التضمين مجرى الأمثال (بقايا، دون تقسيم أو ذكر كميتها).
ويختم الكتاب فصل في التشبيهات الشعرية (١٩٠ ب- ٢٢٣ أ) فى ٦٠ فقرة صغيرة تخلو من الأمثلة الشعرية، وتتدرج موضوعيا من السماء والأرض والعناصر إلى النبات والحيوان والإنسان (تشبيهات متعلقة بجمال المرأة في الغزل) والحرب والسلاح وأمور شتى.

<span data-type="title" id=toc-487>المخطوطات:</span>
برلين ٢٢٣) ١٢١٥ Or.quart ورقة، من القرن الخامس للهجرة)، طهران، ملك ١١٥٠ (١٢٦ ورقة، من القرن العاشر للهجرة، انظر الفهرس ١/ ٦٠).

<span data-type="title" id=toc-488>الأزهرى</span>
هو أبو منصور محمد بن أحمد الأزهرى، ولد سنة ٢٨٢/ ٨٩٥ في هراة.
والظاهر أنه رحل في صباه إلى بغداد، حيث تمكن من الأخذ عن علماء مختلفين، من بينهم ابن السراج ونفطوية. والتقى هنالك أيضا بابن دريد وقد علت سنه، وبالزجاج وأبى بكر بن الأنبارى.
وعاد إلى بلده حيث واصل تحصيله فأخذ عن المنذرى واشتغل فوق ذلك بالفقه الشافعى.
وفي طريقه إلى الحج في خلافة المقتدر وقع مع آخرين في أسر القرامطة، وحكى هو نفسه أنه صنف القسم الأكبر من تهذيبه في الأسر (إرشاد الأريب لياقوت ٦/ ٢٩٩). وتوفى الأزهرى سنة ٣٧٠/ ٩٨٠.
وبتصنيفه «تهذيب اللغة» الذى جرى في وضعه «على نفس نظام كتاب العين»','1','360',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (357,'(كريمر) للخليل، اشتهر بأنه واحد من أهمّ اللغويين.

<span data-type="title" id=toc-489>مصادر ترجمته:</span>
مخطوط في طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٦٨ أ؛ إرشاد الأريب لياقوت ١ ٦/ ٢٩٧ - ٢٩٩، ٢ ١٧/ ١٦٤ - ١٦٧؛ ابن خلكان ١/ ٦٣٥ - ٦٣٦؛ الوافى بالوفيات للصفدى ٢/ ٤٥ - ٤٦؛ طبقات الشافعية للسبكى ٢/ ١٠٦؛ مسالك الأبصار لابن فضل الله ٤/ ٢٣٩ - ٢٤٠؛ ابن قاضى شهبة ٢٩ - ٣٠؛ بغية الوعاة للسيوطى ٨.-
Flugel, Gramm. Schulen ٢١٧ - ٢١٩; Br. G/ I, ١٢٩; SI, ١٩٧; Rescher, Abri B II, ٢١٤ - ٢١٦; J. Kraemer, Studienzuraltara bischen Lexikographiein: Oriens ٦/ ١٩٥٣/ ٢١٣; R. Blacherein: EI, I ٢, ٨٢٢;
الأعلام للزركلى ٦/ ٢٠٢؛ معجم المؤلفين لكحالة ٨/ ٢٣٠ - ٢٣١؛ عبد السلام هارون، مقدمة لكتاب تهذيب اللغة (انظر بعد).

<span data-type="title" id=toc-490>آثاره:</span>
وسنذكر مصنفاته التى لم تصل إلينا تبعا لقوائم كتبه التى وردت في إرشاد الأريب لياقوت ٦/ ٢٩٨ - ٢٩٩؛ الوافى بالوفيات للصفدى ٢/ ٤٦؛ طبقات الشافعية للسبكى ٢/ ١٠٦؛ ابن قاضى شهبة ٢٩؛ بغية الوعاة للسيوطى ٨.

<span data-type="title" id=toc-491>أمّا ما وصل الينا فهو:</span>
١ - «تهذيب اللغة»، «أكبر كتاب صنّف في اللغة وأحسنه» (نزهة الألباء للانبارى ٢٢٢)، اعتمد فيه على مؤلفات أستاذه المنذرىّ (المتوفى سنة ٣٢٩/ ٩٤١، انظر آنفا ص ١٩٤) ومصادر أخرى كثيره، أثبت بعضها في مقدمة تصنيفيّة- تاريخية. ويبدو أن الكتاب لم يشع شيوع «كتاب الصحاح» للجوهرى.

ويتراوح تقسيمه، تبعا لما تذكره قوائم مصنفاته و<span data-type="title" id=toc-492>المخطوطات </span>التى بلغتنا، بين خمسة أجزاء أو مجلدات وبين ثلاثين جزءا أو مجلدا.
المخطوطات:
كوبريلى ١٥٢٦ (٧٣٩ ورقة، نسخت سنة ١٠٨٣ هـ)، ١٥٢٧','1','361',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (358,'(الجزء الأول، ٢١٢ ورقة، من القرن السادس للهجرة)، ١٥٢٨ (جزء من مخطوطة أخرى، ١٩٥ ورقة، نسخت سنة ٥٩٧ هـ مع حواش لياقوت الحموى)، ١٥٢٩ (الجزء الثالث، ١٢٧ ورقة، من القرن السادس للهجرة)، ١٥٣٠ (الجزء الرابع، ٢٤٥ ورقة، نسخت قبل سنة ٥٩٣ هـ، وهى عدا هذا كرقم ١٥٢٨ ومعارضة بنسخة نقلت عن أخرى بخط المؤلف)، ١٥٣٢ (الجزء الخامس عشر، ٢٤٣ ورقة، من القرن السادس للهجرة)، ١٥٣٣ (الجزء الخامس، ٢٤٤ ورقة، من القرن السادس للهجرة، وهى عدا هذا كرقم ١٥٢٨، معارضة بنسخة المؤلف ومخطوطة أخرى متقدمة التاريخ)، ١٥٣٤ (الجزء السادس، ٢٠٩ ورقة، من القرن السادس للهجرة، كرقم ١٥٢٨)، ١٥٣٥ (الجزء الثامن، ٢٣٥ ورقة، من القرن السادس، معارض بمسودة المؤلف ونسخة شخص اسمه ابو زكرياء)، ١٥٣٦ (الجزء التاسع، ٢٠٤ ورقة، من القرن السادس للهجرة)، ١٥٣٧ (الجزء السادس، ٢٥٦ ورقة، نسخت سنة ٦٤٠ هـ، عن نسخة عليها حواش لابن الخشاب)، ١٥٣٨ (جزء، ٢٠٦ ورقة، من القرن السادس للهجرة، معارض بمسودة المؤلف، وعدا هذا كرقم ١٥٢٨)، ١٥٣٩ (الجزء الثانى عشر، ٢١٥ ورقة، من القرن السادس للهجرة، معارض بنسخة المؤلف، وعدا هذا كرقم ١٥٢٨، انظر فهرس المخطوطات ١/ ٣٥٠ - ٣٥١)، نور عثمانية ٤٦٨٦ (المجلد الأول، ٤٥٦ ورقة، نسخت سنة ١١٥٩ هـ)، ٤٦٨٧ (المجلد الثانى، ٤١٥ ورقة، من القرن ١٢ هـ)، ٤٧٤٣ (١٠٧٧ ورقة، نسخت سنة ١٢٤٩ هـ)، ٤٧٤٤ (٨٢٦ ورقة، نسخت سنة ١١٦١ هـ، مشحونة بالأخطاء)، آيا صوفية ٤٦٧١ (٩٨٧ ورقة، نسخت سنة ١١٣٩ هـ، انظر، O ,Rescherin (WZKM ٢٦ /١٩١٢ /٨٩ - ٩٠: حكيم أوغلو ٩٠٧','1','362',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (359,'(المجلد الأول، ٥١٩ ورقة، نسخت سنة ١١٣٩ هـ)، ٩٠٨ (المجلد الثانى، ٥٣٦ ورقة، الناسخ نفسه)، بشير أغا ٦٢٥ (١٠٧١ ورقة، نسخت سنة ١١٣٩ هـ، عن أصل لها يرجع تأريخها إلى سنة ٦١٤ هـ في مخطوطة ياقوت الحموى، راجع O.Rescherin: (MFO ٥ /١٩١٢ /٥٣٥ , مراد ملا ١٧٥٩ - ١٧٦٤ (المجلدات ١، ٢، ٣، ٥، ١٠، من القرن الثامن للهجرة، انظر المرجع السابق ص ٥٣٠)، عاطف ٢٧٠٨ (المجلد الأول، ٢٩٥ ورقة، نسخت سنة ١١٠٢ هـ)، ٢٧٠٩ (المجلد الثانى، ٣٣٨ ورقة، نسخة سنة ١١٠٢ هـ)، ٢٧١٠ (المجلد الثالث، ٢٩٦ ورقة، نسخت سنة ١١٠٢ هـ، انظر المرجع السابق ص ٤٩٥)، شهيد على ٢٦١٤ (الجزء الرابع عشر، ٢٨٧ ورقة، من القرن السابع للهجرة، انظر فهرس المخطوطات ١/ ٣٥١)، ولى الدين ٣٠٩٩ (٧٤٨ ورقة، نسخت سنة ١١٩٢ هـ، راجع O.Rescher (in: MFO ٥ /١٩١٢ /٥٢٧ فيض الله ٢٠٦٢ (٩٤٤ ورقة، نسخت سنة ١١٠٥ هـ) ٢٠٦٣ (٢٣٧ ورقة، نسخت سنة ٥٩٩ هـ)، راغب ١٤١٣ (٦٨٣ ورقة، نقلت سنة ١١٦٠ هـ عن نسخة ياقوت بحواشيها، المحفوظ بعضها في كوبريلى)، سراى أحمد الثالث، ٢٧٢٢ (المجلدات ٢، ٥، ٦، ٧، ٩، ١٠، ١١، ٢٠ أى المجلد الأخير، من القرن السادس للهجرة، راجع الفهرس ٤/ ٧ - ٩)، ٢٧٣٠ (الجزء الرابع، ٢٦٧ ورقة، من القرن السادس للهجرة، انظر الفهرس ٤/ ٧)، جوروم ١٨٤٣ (٢٢٩ ورقة، من القرن السابع للهجرة)، طهران، سپهسالار ٥٧ (المجلد الأول، ٤١٠ ورقة، من القرنالتاسع للهجرة)، ٥٨','1','363',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (360,'(المجلد الثانى، ٤٢٥ ورقة، لعله أيضا من القرن التاسع للهجرة). ٥٩ (المجلد الثالث، ٥٢٥ ورقة، لعله كذلك من القرن التاسع للهجرة)، ٦٠ (المجلد الرابع، ٣٣٥ ورقة، نقلت عن نسخة تاريخها سنة ٥٧٥ هـ)، ٦١ (المجلد الخامس، وهو آخر المجلدات هنا)، انظر أسعد طلس في: مجلة المجمع العلمى العربى بدمشق ٢٢/ ١٩٤٧/ ٥٠٢ - / ٥٠٣، مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٦٧؛ الفهرس ٢/ ١٦٦ - ١٧٣، رقم ٨٢٢ - ٨٢٦، ملى (نسخت سنة ١١٠٨ هـ، انظر مجلة معهد المخطوطات ٣/ ١٩٥٧/ ٢١، رقم ٣٢)، دانشگاه ٢٠١ (جزء من أوله، ٢٤٧ ورقة، من القرن الخامس للهجرة، مخطوطة مهمة)، ٢٠٢ (جزء آخر من المخطوطة نفسها، ٢٨٥ ورقة، انظر الفهرس ٢/ ٣٦٢ - ٣٦٣)، ومنها ميكرو أفلام بمعهد المخطوطات بالقاهرة، رضا بمشهد ٦٢٥٣ (الجزء الأول، نسخة قديمة)، ٦٢٥٤ (الجزء الثانى، نسخة قديمة، انظر فهرست ... دو كتابخانه ٦٧٤، وراجع فهرس دانشگاه، الموضع المذكور)، رضا برامبور (الجزء الحادى والعشرون حتى النهاية، نسخة قديمة، انظر الفهرس ١/ ٥٠٩، تذكرة النوادر ١١٢)، بنكيبور ١٩٦٤ (المجلد الرابع، ٢٣٤ ورقة، نسخت سنة ٦٣٩ هـ)، ١٩٦٥ (المجلد التاسع، ٢٠٢ ورقة، نقلت سنة ٦٣٩ هـ عن نسخة عليها تصويبات لابن العصّار وابن الخشاب، انظر الفهرس ٢٠/ ٤ - ٦)، بغداد، القادرية ٤٨٠ (الجزء الثالث، ٢٠٨ ورقة، من القرن السادس للهجرة، انظر إ. الدروبى فى: مجلة المجمع العلمى العراقى ٦/ ١٩٥٩/ ٢١١)، المتحف العراقى ببغداد','1','364',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (361,'٢٨٩ إلى ٢٩٤ (المجلد الثانى، ويشتمل على ستة أجزاء، ٧٦٨ ورقة، مصور عن إحدى مخطوطات القاهرة)، ١١١٩ (جزء من المجلد الأول، ١٠٠ ورقة، انظر فهرس النقشبندى ٨٦ - ٨٧)، العباسية بالبصرة، ح ٢٥ (الجزء الرابع، انظر على الخاقانى في مجلة المجمع العلمى العراقى ٨/ ١٩٦١/ ٢٧٣ - ٢٧٤، رقم ١٩٦)، الأحمدية بحلب (راجع أحمد عبد الغفور عطار في: مقدمة تهذيب اللغة- انظر بعد- ص ٢٠ - ٢١)، الظاهرية، عامّ ٩٧٧٧ (الجزء الثامن، ٤٣١ ورقة، انظر فهرس اللغة ٨٠ - ٨١)، عارف حكمت بالمدينة، لغة ٧ (الجزء الأول، ٣٣٢ ورقة، نقلت سنة ٦١٦ هـ عن نسخة ياقوت الحموى)، لغة ٨ (الجزء الثانى، ٤٠٦ ورقة، نسخت سنة ٦١٦ هـ، راجع المنتخب لكحالة ٤٦)، دار الكتب بالقاهرة، لغة ٩ (مجلدان يشتملان على ١٥ جزءا، ٧٠٣ و ٣٤٠ ورقة، من القرن ١١ هـ)، ومنهما مصورة بالدار. ٤٨٧ هـ (انظر الملحق ١/ ١٨٩)، لغة ١٠ (الأجزاء ٢ - ٥ و ٧ - ١٨) فى ١٦ مجلدا، نسخت سنة ٦٣٣ هـ، الجزء الثانى ٦٩ ورقة، الجزء الثالث ١٣٥ ورقة، الجزء الرابع ١١٥ ورقة، الجزء الخامس ٢٢٩ ورقة، الجزء السابع ٩٧ ورقة، الجزء الثامن ١٩٤ ورقة، الجزء التاسع ١٥١ ورقة، الجزء العاشر ١٣٤ ورقة،','1','365',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (362,'الجزء الحادى عشر ٢٢٦ ورقة، الجزء الثانى عشر ٢٠٥ ورقة، الجزء الثالث عشر ١٦١ ورقة، الجزء الرابع عشر ١٠٤ ورقة، الجزء الخامس عشر ٢٥١ ورقة، الجزء السادس عشر ١٦٧ ورقة، الجزء السابع عشر (لم يبين عدد أوراقه)، الجزء الثامن عشر ١٤٩ ورقة، (انظر الفهرس الثانى ٢/ ١٠؛ وفهرس المخطوطات ١/ ٣٤٨ - ٣٥٠)، ومنها مصورة بالدار ٦٦١١ هـ (انظر الملحق ١/ ١٨٩)، لغة ١١ (الجزءان ٩، ١٢ من مخطوطة أخرى في مجلدين)، المكتبة العامة بالرباط، أوقاف ١٦٠ (الجزء العاشر، ٢٣٥ ورقة، نسخت سنة ٦٥٣ هـ)، ومنها مصورة بمعهد المخطوطات بالقاهرة، باريس ٥٥٧٦ (الأوراق ١٥ - ٤١، قطعة، انظر فجدا ٦٥٧)، المتحف البريطانى) ٤١٨٠ Or.، الجزء الثانى، ١٨٩ ورقة، من القرن التاسع للهجرة، انظر الملحق ٥٧١، رقم) ٤١٩٣ Or.، (٨٣٩ جزء، ٩٦ ورقة، من القرن الثامن للهجرة، انظر الملحق ٥٧١ - ٥٧٢، رقم ٨٤٠)، ومنها مصورة بدار الكتب بالقاهرة ٥٣٧٤ هـ، ونسخة عنها ٥٥٣٤ (انظر الملحق ١/ ١٩٨ - ١٩٩)، ييل) ٣٦ A، المجلد الأول والثانى والثالث والسادس، نسخت سنة ١١٠٣ - ١١٠٦ هـ، انظر نيموى رقم ١٥١)، ونشر مقدمته التاريخية تسترشتين K.V.Zettersteenin: MO ١٤ /١٩٢٠ /١ - ١٠٦:','1','366',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (363,'ونشرها أيضا أحمد عبد الغفور عطار مصحوبة بدراسة، فى القاهرة ١٩٥٦، ونشر نص الكتاب كله اثنا عشر محققا، فى ١٥ مجلدا، القاهرة ١٩٦٤ - ١٩٦٧، انظر في هذه النشرة درية الخطيب في: مجلة المجمع العلمى العربى بدمشق ٤٨/ ١٩٧٣/ ٤٥٣ - ٤٦٣، ونشر رشيد عبد الرحمن العبيدى مجلدا للاستدراكات عنوانه: «تهذيب اللغة ... المستدرك على الأجزاء السابع والثامن والتاسع»، القاهرة ١٩٧٥، وأخرج فهارسه عبد السلام محمد هارون: «فهارس معجم تهذيب اللغة للأزهرى»، القاهرة ١٩٧٦، وص. الفرطوشى، «فهرس المواد اللغوية لكتاب تهذيب اللغة» فى: المورد ٨، ١/ ١٩٧٩/ ٢٤٥ - ٣٠٤، انظر أيضا عبد القادر المغربى في: مجلة المجمع العلمى العربى بدمشق ١/ ١٩٢١/ ٢٧٠ - ٢٧٦؛ محمد رضا الشبيبى في المجلة المذكورة ٧/ ١٩٢٧/ ٦٢ - ٦٥؛
F. Krenkowin; JRAS, Cent. Suppl. ١٩٢٤, S. ٢٦٨; J. Kraemerin: Oriens ٦/ ١٩٥٣/ ٢١٣; ٦/ ١٩٥٣/ ٢١٣;
المعجم العربى لحسين نصار ٣٣٢ - ٣٥٩.

<span data-type="title" id=toc-493>تهذيبه واختصاره الخ:</span>
أ- «قيد الأوابد من الفوائد (وهو ما اختلف فيه كتاب الصحاح ... وكتاب التهذيب ... )» لأبى الفضل أحمد بن محمد بن/ أحمد الميدانى (المتوفى سنة ٥١٨/ ١١٢٤)، وقد وصل إلينا، انظره تحت مؤلفات الجوهرى ص ٢١٧، رقم ٥.
ب- «ملتقط تهذيب اللغة» لجار الله محمود بن عمر بن محمد الزمخشرى (المتوفى سنة ٥٣٨/ ١١٤٤)، ويوجد مخطوطا في الخزانة الغروية بالنجف (نسخة منقولة من أصل بخط الزمخشرى، انظر فهرس الحسينى رقم ١١٩).
ج- «كتاب ضالّة الأديب من (أو: فى الجمع بين) الصحاح والتهذيب» لتاج الدين محمود بن أبى المعالى بن الحسن الحوارى (كان حيا سنة ٥٨٠/','1','367',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (364,'١١٨٤، انظر إرشاد الأريب لياقوت ٧/ ١٥٢)، انظر في نقول عنه ص ٢١٨، رقم ٨ تحت مؤلفات الجوهرى.
د- «البيان والتقريب في شرح التهذيب» لأمين الدين عبد الكريم بن عطايا بن عبد الكريم الإسكندرانى (المتوفى سنة ٦١٢/ ١٢١٥، انظر معجم المؤلفين لكحالة ٥/ ٣١٩)، ووصل فيه إلى نحو سبعة مجلدات ولم يتمه، أثنى عليه كثيرا ابن فرحون (الديباج ١٦٧).
هـ «مختصر التهذيب» للمصنف نفسه، «مختصر جيد» (ابن فرحون، الموضع نفسه)، وراجع كشف الظنون ٥١٥.
و- «تهذيب التهذيب» لسراج الدين محمود بن أبى بكر بن حامد الأرموى (المتوفى سنة ٦٨٢/ ١٢٨٣)، اعتمد فيه على «تهذيب اللغة» و «المحكم» لابن سيده، واتبع فيه تقسيم الأول، ويوجد بعضه مخطوطا في المتحف البريطانى) ٤١٨٩ Or.، المجلد الأخير من أصل خمسة، ٢٥٢ ورقة من القرن الثامن للهجرة، انظر الملحق ٥٨٦ - ٥٨٧، رقم ٨٦٦).
٢ - «كتاب تفسير إصلاح المنطق»، شرح على كتاب ابن السكيت (المتوفى نحو سنة ٢٤٤/ ٨٥٨) فى اللغة، ذكره ياقوت وغيره، راجع كشف الظنون ١٠٨.
٣ - «كتاب الأدوات» (؟ )، ذكره ياقوت وآخرون من المتأخرين.
٤ - «كتاب معرفة الصبح» (؟ )، ذكره ياقوت.

<span data-type="title" id=toc-494>وللأزهرى في لغة القرآن والحديث والفقه:</span>
١ - «كتاب التقريب (فى التفسير)»، ذكرته كافة القوائم، راجع كشف الظنون ٤٦٥؛ وجاء اسمه عند ابن خلكان (١/ ٦٣٥) «كتاب التفسير».
٢ - «كتاب معانى القراءات»، لم تذكره القوائم، ويوجد مخطوطا في رشيد ٢٢ (١٧٠ ورقة، نسخت سنة ٧٧٤ هـ، انظر فهرس المخطوطات ١/ ١٥)، ويجرى تحقيقه في دمشق.
٣ - «كتاب علل القراءات»، ورد ذكره في ثلاث قوائم.','1','368',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (365,'٤ - «ناسخ القرآن ومنسوخه»، عن البغدادى في الهداية ٢/ ٤٩.
٥ - «كتاب تفسير أسماء الله» أو «تفسير الأسماء الحسنى»، ذكرته أربع قوائم، وكان في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر بولس سباط في:
. (MIE ٤٩/ ١٩٤٦/ ١٤, No. ٢٥١
٦ - «كتاب الروح وما جاء فيه في القرآن والسنة»، مذكور عند ياقوت وفي ثلاث قوائم أخرى، ونقل عنه الأزهرى نفسه في التهذيب ٥/ ٢٢٣ ومابعدها.
٧ - «كتاب معانى شواهد غريب الحديث» و «كتاب تفسير شواهد غريب الحديث»، ذكرهما ياقوت على أنهما كتابان مختلفان، ويحتمل انهما موضوعان على كتاب أبى عبيد (انظر آنفا ص ٨٦).
٨ - «كتاب الزاهر في غريب ألفاظ الشافعى الموجودة في مختصر المزنى (المتوفى سنة ٢٦٤/ ٨٧٨، انظر تاريخ التراث العربى ١/ ٤٩٢) الذى يرويه عن الشافعى»، وتذكره القوائم باسم «تفسير ألفاظ المزنى» أو «شرح مختصر المزنى»، ونقل عنه الدميرى في الحيوان ١/ ١٩٦، ٢/ ٢١٨ (راجع. (J.De Somogyiin: JA ٢١٣ /١٩٢٨ /٢٠:
انظر في مخطوطاته: تاريخ التراث العربى ١/ ٤٩٣، رقم ١؛ نشره م. ج. الألفى، الكويت ١٩٧٩، /.
٩ - «كتاب غريب الفقه» أو «غريب الألفاظ التى استعملها الفقهاء» ونقل عنه الزبيدى في تاج العروس ٢ ٥/ ٢٧٥ (شثث) باسم «كتاب لغة الفقه»، وهو موجز في مجلد لاصطلاحات الفقه.
والظاهر أنه مستقل عن الكتاب الآنف الذكر، انظر ابن خلكان ١/ ٦٣٥؛ كشف الظنون ١٢٠٧ (وراجع المصدر نفسه ١٦٣٦)؛ «الهداية» لاسماعيل البغدادى ٢/ ٤٩.
١٠ - «الرد على الليث»، ولعله رد على الفقيه الليث بن سعد بن عبد الرحمن الفهمى (المتوفى سنة ١٧٥/ ٧٩١، انظر تاريخ التراث العربى ١/ ٥٢٠)، مذكور عند ياقوت وفي قائمتين أخريين.','1','369',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (366,'١١ - «كتاب الحيض»، عن «كشف الظنون» ١٤١٤؛ «هدية العارفين» لإسماعيل البغدادى ٢/ ٤٩.

<span data-type="title" id=toc-495>وله شروح على أشعار:</span>
١ - «تفسير السبع الطوال»، شرح على المعلقات، ذكرته ثلاث قوائم، راجع كشف الظنون ٤٤٨؛ تاريخ التراث العربى ٢/ ٥١، رقم ٦.
٢ - «كتاب تفسير شعر أبى تمام»، أوردته كل القوائم، راجع كشف الظنون ٧٧١، تاريخ التراث العربى ٢/ ٥٥٤.

<span data-type="title" id=toc-496>الجرباذقانى</span>
هو مهذّب الدين محمد بن الحسن بن محمد بن جعفر الجرباذقانى، صنف كتابه في اللغة بين رجل وشوال من سنة ٣٧٤/ ديسمبر ٩٨٤ ومارس ٩٨٥ (انظر ٩٤ أ، ١٥٨ أ، ١٩٥ أ، ٢٣٧ أ). ولا نعلم عن حياته شيئا آخر (انظر أيضا فصل النحو).

<span data-type="title" id=toc-497>ومن آثاره:</span>
«كتاب الروحة في الطاء والضاد»، معجم لألفاظ الغريب في هذين الحرفين، ويوجد مخطوطا في فاتح ٥١٩٥ (٣٠٨ ورقة، نسخت سنة ٣٧٤ هـ، بخط مؤلفه).
وليس به مقدمة نستطيع ان نستدل بها على مصادر المؤلف. وأكثر الرواة ذكرا هم الأصمعى، وأبو زيد الأنصارى، والفراء، واللحيانى، والأموى، و (أبو محمد) اليزيدى.
ولم يذكر المؤلف أسماء الكتب، إلا أنه من الراجح جدا أن نرى في كتب النوادر التى صنفها أولئك المؤلفون مصادر له.
ويبدو أن الجرباذقانى لم يفد من هذه المصادر إفادة مباشرة، بل عن طريق أحد المؤلفين من أساتذة جيله. ويذكر من هؤلاء الخارزنجى (المتوفى سنة ٣٤٨/ ٩٥٩) وآخر اسمه ابن يالا (؟ ) كان تلميذا لأبى عمر الزاهد (انظر ٢٣ أ).','1','370',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (367,'<span data-type="title" id=toc-498>الدّيمرتى</span>
هو أبو محمد القاسم بن محمد من أهل ديمرت، وهى قرية بالقرب من أصفهان، كان لغويا وأديبا (انظر فصل الأدب). ويبدو أن وفاته كانت نحوسنة ٣٧٥/ ٩٨٥.

<span data-type="title" id=toc-499>مصادر ترجمته:</span>
ابن النديم ٨٦، ١٣٧؛ أخبار أصفهان لأبى نعيم ٢/ ١٦٣؛ إرشاد الاريب لياقوت ٦/ ١٩٨ - ١٩٩؛ إنباه الرواة للقفطى ٣/ ٣٠؛ بغية الوعاة للسيوطى؛ Flugel ,Gramm.Schulen ٢٣٧ - .٣٨١ معجم المؤلفين لكحالة ٨/ ١١٩.

<span data-type="title" id=toc-500>آثاره:</span>
١ - «كتاب الصفات»، ذكره ياقوت ٦/ ١٩٩، وأبو هلال العسكرى في التلخيص ٣./.
٢ - «كتاب تقويم الألسنة»، ذكره ابن النديم وغيره.
٣ - «كتاب تفسير ضروب المنطق»، ذكره ياقوت ٦/ ١٩٩.
٤ - «كتاب تهذيب الطبع» وذكر ياقوت أنه يشتمل على قطعة كبيرة من نوادر اللغة.
٥ - «كتاب الإبانة»، ولسنا متحققين من موضوعه.

<span data-type="title" id=toc-501>وله في لغة الحديث:</span>
«غريب الحديث»، ذكره ياقوت في إرشاد الاريب ٦/ ١٩٩.

<span data-type="title" id=toc-502>أبو الحسن المزنى</span>
هو محمد بن أحمد، وزير نوح بن منصور السامانى (٣٦٦/ ٩٧٧ - ٣٨٧/ ٩٩٧)، كان أديبا شاعرا، كتب رسائل وغير ذلك.
وترجمته في اليتيمة ٤/ ٣٤٦؛ وإرشاد الأريب لياقوت Br.SII ,٩٢٠ ,No.٥٧.-.٢٧٩ /٦','1','371',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (368,'<span data-type="title" id=toc-503>ومن آثاره:</span>
«كتاب حروف الهجاء، وتعريفها، والإخبار عنها إثباتا ونفيا، ووصلا وفصلا»، ويوجد مخطوطا في التيمورية بدار الكتب بالقاهرة، لغة ٥٢٩ (نسخت سنة ١١٨٨ هـ)، ليدن ١٥) ٢٨٨١ Or.، ورقة، نسخت سنة ١٠٠٠ هـ، انظر فورهوف ١١٩).
<span data-type="title" id=toc-504>الصاحب بن عبّاد</span>
هو أبو القاسم إسماعيل بن عبّاد بن العباس الطّالقانىّ، ولد سنة ٣٢٤/ ٩٣٦، وقيل ٣٢٦ هـ باصطخر، وقيل بطالقان. وكان أبوه وزيرا لركن الدولة.
واشتغل بالأدب، وألف أيضا «كتاب أحكام القرآن» (انظر المنتظم لابن الجوزى ٧/ ١٨٤ - ١٨٥).
وتلقى إسماعيل أول دروسه على يد أبيه، ثم رحل إلى بغداد حيث واصل دروسه عند أحمد بن فارس وأبى الفضل بن العميد. وعمل كاتبا لهذا الأخير. إلا انه لم يلبث بعدها ان كتب لمؤيد الدولة، أخى عضد الدولة، وأخيرا خلف الوزير ابن العميد. وبقى في هذا المنصب حتى وفاته سنة ٣٨٥/ ٩٩٥.

وكان الصاحب بن عباد كأبيه معتزليا. وإلى جانب نجاحه في الاضطلاع بوظيفته فقد كان ايضا أديبا (انظر فصل الأدب) ولغويا نابها (انظر أيضا فصل النحو)، وروى الحديث ونظم الشعر (انظر تاريخ التراث العربى ٢/ ٦٣٦ - ٦٣٧).
<span data-type="title" id=toc-505>مصادر ترجمته:</span>
مثالب الوزيرين (وهما أبو الفضل بن العميد والصاحب بن عباد) لأبى حيان التوحيدى؛ تحقيق إبراهيم الكيلانى، دمشق ١٩٦١؛ ونشره محمد الطنجى باسم «أخلاق الوزيرين»، دمشق ١٩٦٥؛ رسالة الإرشاد/ فى أحوال الصاحب الكافى اسماعيل بن عبّاد، لأبى القاسم أحمد بن محمد القوبائى الحسينى، وهو مخطوط فى مكتبة جامعة استنبول ٥٠) ٤٦٨٨ A.Y. ورقة، نسخت سنة ١٢٩٣ هـ)، طهران، مجلس ٦٠٣ (٣٨ ورقة، ومنها ميكروفلم بمعهد المخطوطات بالقاهرة)،','1','372',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (369,'طهران، ملك ٦٢٨٨ (٥٦ ورقة، نسخت سنة ١٣١٢ هـ، انظر الفهرس ١/ ٣١).
وطبع في طهران ١٩٠٧، ١٩٣٣؛ نزهة الألباء للأنبارى ٢ ٢٢٢ - ٢٢٤؛ إنباه الرواة للقفطى ١/ ٢٠١ - ٢٠٣؛ إرشاد الأريب لياقوت ١ ٢/ ٢٧٣ - ٣٤٣، ٢ ٦/ ١٦٨ - ٣١٧؛ لسان الميزان لابن حجر ١/ ٤١٣ - ٤١٦.
Flugel, Gramm. Schulen ٢٤٠ - ٢٤٢; Br. GI, ١٣٠ - ١٣١, SI, ١٩٨ - ١٩٩; Rescher, Abri BII, ٢٢٢ - ٢٣٧;
الصاحب بن عبّاد لخليل مردم بك، دمشق ١٩٣٢؛ أعيان الشيعة للعاملى ١٢/ ٢٣١ - ٣٧٤؛
M. Khan, as- S. b. '' Ab. asa Writerand Poetin: Isl. Cult. ١٧/ ١٩٤٣/ ١٧٦ - ٢٠٥; M. Kabir, The S. Ism. B. '' Abb. in: Isl. Cult. ٣٠/ ١٩٥٦/ ١٩٠ - ١٩٨; Ritter, Geheimnisse ١٥٤ - ١٥٥;
الصاحب بن عباد، حياته وأدبه، لمحمد حسن آل ياسين، بغداد ١٩٥٧ (انظر ما علقه عليه شارل بلا؛ (Ch.Pellatin: Arabica ٥ /١٩٥٨ /٨٣ - ٨٤: الفن ومذاهبه في النثر العربى لشوقى ضيف، القاهرة ١٩٦٠، ص ٢١٧ - ٢٢٢؛ الأعلام للزركلى ١/ ٣١٢ - ٣١٣؛ معجم المؤلفين لكحالة ٢/ ٢٧٤ - ٢٧٥؛
Haywood, Arabic Lexicography ٦٢ - ٦٤, ٩٩; Cl. Cahen, Ch. Pellatin: EI, III ٢, ٦٧١ - ٦٧٣.
انظر قوائم مفصلة بمصنفاته في الفهرست لابن النديم ١٣٥ (طهران ١٥٠؛ الترجمة الانجليزية ٢٩٧)؛ ابن خلكان ١/ ٩٤؛ إرشاد الأريب لياقوت ٢/ ٣١٥ - ٣١٦؛ الوافى بالوفيات للصفدى ٩/ ١٣٨؛ ابن قاضى شهبة المخطوط ص ٢١٩؛ هدية العارفين لاسماعيل البغدادى ١/ ٢٠٩.

<span data-type="title" id=toc-506>وله في اللغة:</span>
١ - «كتاب المحيط (فى اللغة)»، وقد كتب أو روى على اختلاف في تقسيمه بين خمسة أو سبعة أو عشرة مجلدات، وذكر في خمس قوائم (راجع المزهر للسيوطى ١/ ٩٦ - ٩٧).','1','373',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (370,'وكان من مصادر الصغانى في التكملة (انظر مقدمة التحقيق ١/ ٨) والعباب (انظر ١/ ٣٠). (يتبع هذا المعجم اتباعا قريبا كتاب العين في محتواه وترتيبه، وينقل ابن عباد تفاسيره بنصها، على حين أنه يشير إلى الجذور المتروكة بقوله:
«أهمله الخليل؛ (J.Kraemerin: Oriens ٦ /١٩٥٣ /٢١٤ «وأكبر مصادره في الاستدراك هو كتاب «تكملة كتاب العين» لأبى حامد أحمد بن محمد البشتى الخارزنجى (المتوفى سنة ٣٤٨/ ٩٥٩، انظر ص ١٩٥ آنفا).
و«كثّر فيه الالفاظ، وقلّل الشواهد» (ابن خلكان، ١/ ٩٤).

<span data-type="title" id=toc-507>المخطوطات:</span>
سراى أحمد الثالث، ٢٧١٤ (حروف الخاء والغين والقاف، ٢٧٥ ورقة، من القرن السابع للهجرة، انظر؛ H.Ritterin: Oriens ٢ /١٩٤٩ /٢٣٧: وفهرس المخطوطات ١/ ٣٧٠)، آيا صوفية ٤٧٢٤ (حروف الجيم والشين والضاد والصاد والسين، ٣٦٠ ورقة، من القرن السابع للهجرة)، ومنها نسخة بمكتبة الإمام الحسن بالكاظمية (انظر محمد حسن آل ياسين، المرجع المذكور، ص ٢٣٥)، طهران، ملك ٣٣٩ (٢٨٩ ورقة، نسخت سنة ١٢٧٩ هـ، انظر الفهرس ١/ ٦٤٩ - ٦٥٠)، مكتبة حسين القزوينى الخاصة بكربلاء (كاملة، ٥٢٠ ورقة، نسخت سنة ١١١٧ هـ، انظر الطعمة ١/ ١٢٦ - ١٢٨)، ومنها نسخة بالمتحف العراقى ببغداد ٥٢٨ و ٥٢٩ (مجلدان، ٤٤٤ صفحة و ٤١٥ صفحة، نسخت سنة ١٣٥٤ هـ، انظر فهرس النقشبندى ١٠٣، رقم ٣٦٨، ٣٦٩)، دار الكتب بالقاهرة، لغة ٤٢ (الجزء الثالث، بعضه متصل، ويشتمل على ١٣ حرفا من الكاف إلى الفاء، انظر المورد ٢، ٤/ ١٩٧٣/ ٢٤٣)، المتحف البريطانى ١٢٨٩٨ (كاملة، ٣٥٥ ورقة، نسخت سنة ٧٦٠ هـ، انظر:
(M. Lingsin: Brit. Mus. Qly ٢٩/ ١٩٦٤ - ٦٥/ ١٥
؛ نشره محمد حسن آل ياسين، بغداد، ظهر إلى الآن المجلد الأول، ١٩٧٥','1','374',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (371,'(الأول من عشرة أجزاء)، انظر ما كتبه عنه و. دياب في: مجلة المجمع العلمى العربى بدمشق ٥٢/ ١٩٧٧/ ٨٨١ - ٨٩٠، وانظر أيضا محمد حسن آل ياسين فى: المورد ٢، ٤/ ١٩٧٣/ ٤٣٥ - ٢٤٤؛ وحسين نصار في «المعجم العربى» ٣٦٠ - ٣٧١، ٢ - «جوهرة الجمهرة»، مختصر من كتاب الجمهرة لابن دريد (المتوفى سنة ٣٢١/ ٩٣٣)، ذكرته ثلاث قوائم (انظر أيضا المزهر للسيوطى ١/ ٩٦)، ويقال إنه محفوظ في مكتبة الصدر الخاصة بالكاظمية (انظر الذريعة ٥/ ٢٩٢، ولكن راجع محمد حسن آل ياسين في: المورد ٢/ ٤/ ١٩٧٣/ ٢٣٧).
٣ - «رسالة في اللغة»، وينبغى أن يفحص عن اسمها ونسبتها إليه، / وتوجد مخطوطة في طهران، ملك ٤٦٠ (أربع ورقات، من القرن السابع للهجرة، انظر صلاح الدين المنجد فى: مجلة معهد المخطوطات ٦/ ١٩٦٠/ ٦٨؛ الفهرس ١/ ٦١٦ - ٦١٧).

<span data-type="title" id=toc-508>وللصاحب في الأمثال والأسماء:</span>
١ - «الأمثال السائرة من شعر المتنبى»، وقد وصل إلينا ونشر، انظر تاريخ التراث العربى ٢/ ٤٨٧، رقم ٣.
٢ - «كتاب أسماء الله وصفاته»، وأيضا «مختصر أسماء الله وصفاته»، ذكرته كلّ القوائم.

<span data-type="title" id=toc-509>وله تآليف مفردة في بعض الموضوعات، منها:</span>
١ - «كتاب الحجر» أو «كتاب الأحجار»، وبه من أسماء الأحجار ٢٠١ اسم، وقد وصل إلينا مع ما استدركه ابن القطاع (المتوفى سنة ٥١٥/ ١١٢١)، انظر فصل الجيولوجية وعلم المعادن، ونقل عنه الزبيدى في تاج العروس ٢ ٩/ ٣٩٠ (جلد).
٢ - «كتاب الأعياد»، ذكرته كل القوائم، ولم يصل إلينا، انظر تاريخ التراث العربى ٧/ ٣٥٨.','1','375',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (372,'<span data-type="title" id=toc-510>الخطّابى</span>
هو أبو سليمان حمد (أو أحمد) بن محمد بن إبراهيم، كان محدّثا (عاش من سنة ٣١٩/ ٩٣١ إلى سنة ٣٨٨/ ٩٩٨، انظر تاريخ التراث العربى ١/ ٢١٠ - ٢١١).
ويعد أيضا من اللغويين والأدباء. وكان من اساتذته فى اللغة والأدب اسماعيل الصفّار، وأبو عمر الزاهد، وأبو العباس الأصمّ؛ ومن تلامذته أبو عبيد الهروى والثعالبى.

<span data-type="title" id=toc-511>ومن مصنفاته اللغوية:</span>
١ - «إصلاح الألفاظ»، وكان من مصادر تاج العروس (انظر ١/ ٤)، ولعله هو نفسه الكتاب الآتى ذكره.
٢ - «إصلاح غلط المحدّثين»، انظر تاريخ التراث العربى ١/ ٢١١.
٣ - «غريب الحديث»، ويعتمد فيه اعتمادا كثيرا على أبى عبيد، انظر المرجع السابق ١/ ٢١١ (١٤٤)، ويقوم بتحقيقه ع. العزباوى، انظر: البحث العلمى (مكة) ١/ ١٩٧٨/ ٢٨٣.
٤ - «إعلام السنن في شرح المشكل من أحاديث البخارى»، انظر تاريخ التراث العربى ١/ ٢١١.
٥ - «معالم السنن» (شرح على سنن أبى داود)، انظر المرجع السابق.
٦ - «كتاب الأمالى»، نقل عنه ياقوت في معجم البلدان ٢/ ٢٢٢.
ولعل من آثاره الأدبية الكتابين التاليين اللذين لا تعرف الا اسميهما: ١ - «كتاب العروس»، ذكره ياقوت في إرشاد الأريب ٢/ ٨٣.
٢ - «كتاب الغنية عن الكلام»، ذكره ياقوت فى الموضع نفسه وانظر في شعره: يتيمة الدهر للثعالبى ٤/ ٣٣٤ - ٣٣٦./
_________
(١٤٤) يشطب منه: دار الكتب ٢٢٨٢١ ب. مخطوطة التيمورية، لغة ٧٩ غير كاملة. ويقول الخطابى في المقدمة إن كتب غريب الحديث التى صنفها المتقدمون عليه، وهم أبو عبيدة، والأصمعى، وقطرب، والنضر بن شميل، وإبراهيم الحربى، وأبو معاذ، وشمر','1','376',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (373,'<span data-type="title" id=toc-512>المتيم الإفريقى</span>
هو أبو الحسن محمد بن أحمد بن محمد المغربى، كان شاعرا، لغويا، توفى نحو نهاية القرن الرابع/ العاشر. انظر في قصائده ومصنفاته في الشعر والأدب:
تاريخ التراث العربى ٢/ ٦٣٧ - ٦٣٨.

<span data-type="title" id=toc-513>ومن آثاره:</span>
«المحكم في اشتقاق حروف المعجم»، صائب بانقرة؟ (نحو ٦٠ ورقة، نسخت سنة ٧١٩ هـ).

<span data-type="title" id=toc-514>ابن فارس</span>
هو أبو الحسن أحمد بن فارس بن زكرياء (انظر تاريخ التراث العربى ٧/ ٣٦٠)، ويحتمل أن أصله من قزوين.
تلقى دروسه في قزوين وهمذان وبغداد، ثم استدعاه فخر الدولة بن بويه إلى الرى ليعلم ابنه مجد الدولة. وهنالك أيضا حظى عند الصاحب بن عباد.
وغلب على علمه الاهتمام باللغة؛ وصنف مع ذلك تصانيف في تفسير القرآن، والنحو (انظر فصل النحو)، والتاريخ والفقه. توفى في الرى سنة ٣٩٥/ ١٠٠٥.

<span data-type="title" id=toc-515>مصادر ترجمته:</span>
مخطوط في طبقات اللغويين لمجهول بشهيد على ٢٥١٥/ ٢، ٢٦٨ أ- ٢٦٨ ب؛ يتيمة الدهر للثعالبى ٣/ ٤٠٠ - ٤٠٧؛ نزهة الألباء للانبارى ٢ ٢١٩ - ٢٢١؛ إرشاد الأريب ١ ٢/ ٦ - ١٥، ٢ ٤/ ٨٠ - ٩٨؛ إنباه الرواة للقفطى ١/ ٩٢ - ٢٥؛ الوافى بالوفيات للصفدى ٧/ ٢٧٨ - ٢٨٠؛ مسالك الأبصار لابن فضل الله
_________
بن حمدويه، والباجدّائى (الحسين بن عيّاش بن حازم، المتوفى سنة ٢٠٤/ ٨١٩، انظر تهذيب التهذيب لابن حجر ٢/ ٣٦٢ - ٣٧٣)، وأحمد بن الحسن الكنهى (؟ ، ولا يعرفه الخطابى نفسه)، كل هذه الكتب على كثرتها «كأنها كتاب واحد» لاتفاق أصحابها في المنهج، وهذا بخلاف كتاب غريب الحديث لابن قتيبة.','1','377',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (374,'٤/ ٢٤٤ - ٢٤٥؛ ابن قاضى شهبه، المخطوط ص ١٨٩ - ١٩٠؛ الوفيات لابن قنفد ٢١٨؛ بغية الوعاة للسيوطى ١٥٣؛ وطبقات المفسرين له ٤؛ هدية العارفين لاسماعيل البغدادى ١/ ٦٨ - ٦٩.
Flugel, Gramm. Schulen ٢٤٦ - ٢٤٨; Br. GI, ١٣٠, SI, ١٩٧ - ١٩٨; Rescher, Abri BII, ٢٢١ - ٢٢٢; M. Ben Chenebin: EI, II, ٤٠٠;
أعيان الشيعة للعاملى H.Fleischin: EI ,III ٢ ,٧٦٤ - ٧٦٥.؛ ٢٢٨ - ٢١٥ /٩ م. م. رضوان، «العلامة اللغوى ابن فارس الرازى»، القاهرة ١٩٧١.

<span data-type="title" id=toc-516>آثاره:</span>
١ - «المجمل (فى اللغة)»، وهو معجم موجز في اللغة الفصحى «مع استبعاد النادر والغريب ... ، وهو كتاب قيّم لشروحه الموجزة وشواهده الشعرية الوافرة (J.Kraemerin: Orirens ٦ /١٩٥٣ /٢١٥) . «وشأنه شأن كتاب «الجيم» فى ترتيبه الألفاظ على أوائلها، ثم ترتب المواد في داخل الحروف في أبواب بحسب عدد الأصول. «وعندى أن تصنيفه ذلك من أحسن ما صنّف في معناها» (دمية القصر للباخرزى ٢ ٢/ ٤٨٥). وينقل عنه كثيرا ويوصف بأنه أهم كتبه.

<span data-type="title" id=toc-517>المخطوطات:</span>
كوبريلى ١٥٧٢ (٤٤١ ورقة، نسخت سنة ٤٥٣ هـ)، نور عثمانية ٤٨٥٥ (كاملة، ٥٢٤ ورقة، من القرن السادس للهجرة، راجع، nirehcseR. O (: MSOS ١٥ /١٩١٢ /٢٣ سراى، خزينة ١١٧٣ (جزآن، ٤٧٧ ورقة، من القرنين العاشر والسادس للهجرة، انظر فهرس المخطوطات ١/ ٣٧٠)، / سراى، أحمد الثالث، ٢٧٣٤ (٥٤٩ ورقة، انظر الفهرس ٤/ ١١)، لا له لى ٣٦١٧ (٤٩٢ ورقة، نسخت سنة ٤٨٩ هـ)، و ٣٦١٨ (٣١٧ ورقة، نسخت سنة ٥٣٢ هـ، راجع، ٦٠١/ ٣١٩١/ ٧ OM: nirehcseR. O (أسعد ٣٢٦٩ (المجلد الأول)، ٣٢٧٠ (المجلد الثانى، نسخ سنة ٤٧٥ هـ،','1','378',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (375,'انظر، J.Kraemer فى الموضع المذكور آنفا، ص ٢١٦)، ينى جامع ١١٦٣ (٣٣٩ ورقة، نسخت سنة ٤٨٩ هـ)، فيض الله ٢٠٩٣ (٣٨٩ ورقة، نسخت سنة ٥٢٤ هـ، انظر فهرس المخطوطات ١/ ٣٧٠)، و ٢٠٩٤ (٣٠٢ ورقة، نسخة قديمة)، و ٢٠٩٥ (٢٩٤ ورقة، نسخة قديمة)، مسيح باشا ٦ (المجلد الثانى، ٢٨٧ ورقة، نسخت سنة ٥٦٣ هـ)، شهيد على ٢٦٧٢ (٥٧٣ ورقة، من القرن السادس للهجرة)، حميدية ١٤٢٨ (٢٤١ ورقة، من القرن السابع للهجرة)، راغب ١٤٤١ (٣٠٣ ورقة، نسخت سنة ٥١٦ هـ)، مراد ملا ١٨٠٩، و ١٨١٠، قاضى زاده محمد ٥٢٧/ ٢ (الأوراق ٢٦٢ - ٢٧٩، قطعة)، مانيسا ٦٥٣٥ (النصف الأول، ٢٠٠ ورقة، من القرن السادس، انظر شيشين، نوادر ١/ ١٤٠).
اينه كول ٥٦٤/ ١ (النصف الأول ٢٠٠ ورقة، نسخت سنة ٦٠٨ هـ)، و ٥٦٤/ ٢ (النصف الثانى، نحو ٢٠٠ ورقة، نسخت سنة ٦٠٨ هـ، انظر المرجع نفسه)، بورسه، حراتشى أوغلو ١٣٩٣ (المجلد الأول، ١٨٦ ورقة)، و ١٣٩٤ (المجلد الثانى، ١١٤ ورقة، نسخت قبل سنة ٧٦٤ هـ، راجع)، H.Ritterin (Oriens ٢ /١٩٤٩ /٢٣٨.: قونيه، يوسف أغا ٣٤٦٩ (جزآن، ٤٧٩ ورقة، نسخت سنة ٥٧٤ هـ و ٦٨٧ هـ، انظر، (Li stedesmanuscrits ... ٢٦ قونية، قيونوغلو ٢٠٣٥ (٤٤٤ ورقة، نسخت سنة ٥٧٥ هـ، انظر القائمة السابقة ص ٣٧)، قسطمونى ١٢٩٥، طهران، ملك ٣٤٦ (٢٥٣ ورقة، نسخت سنة ٤٥٧ هـ، انظر مجلة معهد المخطوطات العربية ٦/ ١٩٦٠/ ٦٨)، و ٤٢١ (٢٥٧ ورقة، من القرن ١١ هـ، انظر الفهرس ١/ ٦٤٢ - ٦٤٣)، مجلس، بدون رقم إلى الآن (٦٨) ورقة، مخطوطة حسنة جدا، أولها','1','379',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (376,'ناقص)، ومنها مصورة بمعهد المخطوطات بالقاهرة (انظر مجلة معهد المخطوطات العربية ٢١/ ١٩٧٥/ ١٨٦، رقم ٢٦٣، دانشگاه ٢٠٣ (٢٥٧ ورقة، راجع المجلة السابقة ٦/ ١٩٦٠/ ٣٣٢)، و ٢١٤ (٢٦٦ ورقة، نسخت سنة ٨٦٥ هـ، انظر الفهرس ٢/ ٤٤٧ - ٤٤٨) مشهد، رضا ٣٦٨٦ (نسخت سنة ١٠٣٦ هـ)، و ٧٨٩٧ (انظر فهرس ... دو كتابخانه ٩٨٢)، بغداد، المتحف العراقى ٥٤٢ (٣١٩ ورقة، نسخت سنة ٤٤٦ هـ، انظر فهرس النقشبندى ١٠١ - ١٠٢، رقم ٣٦٦)، بغداد، القادرية ٤٩٠ (ناقصة، من القرن السابع للهجرة)، وثمة نسخة أخرى من القرن الخامس للهجرة معروضة هناك (انظر النشرية ٧/ ١٩٧٤/ ٥٢٩)، الموصل، جامع الباشا (تامة، نسخة سنة ١١٠٩ هـ، انظر فهرس جلبى ٦٧، رقم ٢٧٧)، سامرا، الإمام المهدى (الجزء الثانى، من القرن الخامس للهجرة، انظر مجلة معهد المخطوطات العربية ٤/ ١٩٥٨/ ٢١٠)، الظاهرية، عام ٥٥٢٢ (١٣٦ ورقة، نسخت سنة ٩٠٨ هـ)، وعام ١٥٦١ (٤٤٦ ورقة)، وعام ١٠٦٣ (١٨٩ ورقة، انظر فهرس اللغة ٥١ - ٥٦)، المدينة، عارف حكمة، لغة ٤١ (الجزء الأول، ٣٣٠ ورقة، من القرن السابع للهجرة)، ولغة ٤٢ (الجزء الثانى من مخطوطة أخرى، ٣٠٠ ورقة، من القرن السابع للهجرة)، ومنها ميكرو فلم بمعهد المخطوطات بالقاهرة، دار الكتب بالقاهرة، لغة ٢٣٨ (قطعة قديمة)، ولغة ٣٨٢ (نسخت سنة ٥٦٥ هـ، ناقصة)، ولغة ١٨ ش (مجلدان، نسخا سنة ٦٤٨ هـ، انظر الفهرس الثانى ٢/ ٣٤)، الأزهر، لغة ٤٧ (الجزء الثانى، ١٩٩ ورقة، نسخت قبل سنة ٩٢٢ هـ، انظر الفهرس ٤/ ٣٠)، الاسكندرية، البلدية ٢١٧٩ ج (٥٠٤ ورقة، نسخت سنة ٦٠١ هـ، انظر فهرس اللغة (١) ٦٧؛ فهرس المخطوطات ١/ ٣٧٠)،','1','380',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (377,'مراكش، ابن يوسف ٦٣، ولعلها اليوم في المكتبة العامة بالرباط، فلورنسا، البلاتينية (السمعانى رقم ٣٥٦ و ٤٢١، عن بروكلمن)، ميلانو، أمبروزيانا ٩٩ (عن بروكلمن)، باريس ٤٢٤٧ (الجزء الأول، ٢٨٩ ورقة)، ٤٢٤٨ (الجزء الثانى منها، ٢٤٩ ورقة، نسخت سنة ٥٢٨ هـ)، ٤٢٤٩ (الجزء الأول، ٢٧٩ ورقة، من القرن السابع للهجرة)، ٤٢٥٠ (الجزء الثانى، ناقص، ٢٨٦ ورقة، من القرن الثامن للهجرة)، ٦٥٦٣ (٢١٦ ورقة، من القرن الخامس للهجرة، انظر دى سلان ٦٨٣؛ فجدا ٤٩٠)، ليدن ٢٧٥) ٣ Or.، ورقة، ليست بكاملها) ٤٨٥ Or.، (المجلد الأول، ٢٦٩ ورقة، نسخت قبل سنة ٦٠١ هـ) ٢٣٩٠ Or.، (المجلد الأول، ٣٠٩ ورقة، انظر فورهوف ٢٢٢، وم. أحمد، «نسخة تاريخية لمجمل اللغة لابن فارس» فى: مجلة المجمع العلمى الهندى ١/ ١٩٧٦/ ١٤٨ - ١٦٣)، برلين ٦٩٥٤ (النصف الأول، ٢١٥ ورقة، نسخت نحو سنة ٦٠٠ هـ)، ٦٩٥٥ (النصف الثانى، ٢٨٤ ورقة، نسخت نحو سنة ٨٠٠ هـ)، ٦٩٥٦ (النصف الثانى، ٢٦١ ورقة، نسخت سنة ٦٣٤ هـ)، ٦٩٥٧ (النصف الثانى، ١٤٢ ورقة، نسخت نحو سنة ٩٠٠ هـ)، و) ١١٢٩ Or.quart نسخت سنة ٩٤٢ أو ٧٤٢ هـ، انظر، J.Kraemerin (Oriens ٦ /١٩٥٣ /٢١٦: جوتا ٣٧٧ (الجزء الثانى، ٢٣٧ ورقة، نسخت سنة ٦٠٠ هـ)، المتحف البريطانى ٢٠٤) ٤١٤ Or.، ورقة، نسخت سنة ٧٤٥ هـ، انظر الفهرس ٧٥٤، رقم ٤٠٤) ٣٠٧٦ Or.، (١٦٨٣ ورقة، نسخت سنة ٥٥٠ هـ، انظر رى رقم ٢٧٠) ٧٤٩٨ Or.، (٨٤٣ ورقة، نسخت سنة ٥٨٩ هـ، انظر، (Ellis ,descr.list ٥٢ المتحف البريطانى أو كيمبردج، مجموعة بلشاه (أولها ناقص، من القرن الخامس للهجرة، انظر، (E.Edwardsin Or.Stud.Browne ١٤٨ ,No.٨٥:','1','381',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (378,'المكتب الهندى ٢٤٥٧ (٣٧٦ (ورقة، من القرن السادس للهجرة، انظر لوث رقم ٩٩١)، اكسفورد، بودليانا ٣٥٥) ٣٠١ Pocock، ورقة، نسخت سنة ٦٠٢ هـ و ٨٤٩ هـ، انظر أورى ٢٣٠، رقم ١٠٦٥)، تشستربيتى/ ٣٨٤٨ (٢٧١ ورقة، من القرن الخامس للهجرة، انظر أيضا الفهرس)، برنستون، مكتبة الجامعة) ١٢٨ Q، الجزءان الأول والثانى، من القرن السادس أو السابع للهجرة، انظر كوركيس عواد، مخطوطات ١٣)، جاريت (يهودا) خمس مخطوطات (أجزاء، غالبا من القرنين السادس والسابع للهجرة، ماخ ٣٢٢، رقم ٣٧٥٨)، نيويورك، المكتبة العامة (الجزء الأول، نسخ سنة ٥٦٧ هـ، انظر كوركيس عواد، مخطوطات ص ٥ - ٦)، ييل) ٣٠٦ - L، المجلد الأول، ٣٤٨ ورقة، من القرن السادس للهجرة، انظر نيموى رقم ١٦٧)، طبع المجلد الأول منه في القاهرة ١٣٣٢، الدراسات: رسالة ماجستير ل هـ. ح. حمودى، بغداد ١٩٧٣ (انظر. ٧٧٤ ٦٦٤ يبرعلا مجعملا، راصن نيسح؛ ) ٥٦٢/ ٨٧٩١/ ١، ٧ دروملات.
وقد علق عليه مجد الدين محمد بن يعقوب الفيروزابادى (المتوفى سنة ٨١٧/ ١٤١٤) بكتاب سمّاه (أوهام المجمل)، أصلح فيه أوهامه في ألف موضع (شذرات ابن العماد ٧/ ١٢٨) «مع تعظيمه [ابن فارس] له وثنائه عليه» (كشف الظنون ١٦٠٥).
٢ - «متخير الألفاظ»، مرتب على الموضوعات (انظر في الفصول المتعلقة بالفلك والآثار العلوية: تاريخ التراث العربى ٧/ ٣٦٠ - ٣٦١)، ذكرته ثلاث قوائم (انظر أيضا بولس سباط في، MIE ٤٩ /١٩٤٦ /٤٣ ,No.٧٦٢: (ونقل عنه الجرجانى (المنتخب من كنايات الأدباء ١٤٥) باسم «مختار الألفاظ، وكان من مصادر حبيش بن إبراهيم بن محمد التفليسى (المتوفى ٦٢٩/ ١٢٣٢) فى كتابه «قانون الأدب»','1','382',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (379,'(انظر مجلة المجمع العلمى العربى بدمشق ٥٠/ ١٩٧٥/ ٤٠٢).

<span data-type="title" id=toc-518><span data-type="title" id=toc-519>المخطوطات:</span></span>
بغداد، سابقا ملك السيد أحمد بن السيد عبد الوهاب، واليوم في المتحف العراقى (٧٥ ورقة، من القرن السادس للهجرة)؛ نشره هلال ناجى، بغداد ١٩٧٠، انظر في هذه النشرة شفيق جبرى في: مجلة المجمع العلمى العربى بدمشق ٤٦/ ١٩٧١/ ٦٢١ - ٦٢٥؛ ع. المهيرى في: حوليات (تونس) ٨/ ١٩٧١/ ٢٢٣ - ٢٢٥؛ رسالة ماجستير ل م. ع. جبارة، بغداد ١٩٧٠ (انظر المورد ٧، ١/ ١٩٧٨/ ٢٦٥).
٣ - «كتاب مقاييس اللغة»، «وهو كتاب جليل لم يصنف مثله» (ياقوت)، ذكرته خمس قوائم، وكان من مصادر الصغانى في التكملة والعباب، أفاد فيه من كتاب العين للخليل بن أحمد، وغريب الحديث والغريب المصنف لأبى عبيد، وإصلاح المنطق لابن السكيت، والجمهرة لابن دريد.
المخطوطات:
طهران، سپهسالار ٧٢ (٣١٩ ورقة، نسخت سنة ١٠٩٤ هـ، انظر أسعد طلس فى: مجلة المجمع العلمى العربى بدمشق ٢٢/ ١٩٤٧/ ٥٠١ - ٥٠٢؛ حسين على محفوظ في: مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٦٨؛ الفهرس ٢/ ٢٩١ - ٢٩٣)، بغداد، المتحف العراقى ١٤٥١ (٤٠٨ ورقة، نسخت سنة ١١١٧ هـ، انظر فهرس النقشبندى ١٠٨، رقم ٣٨٤)، المتحف البريطانى؛ ١١٣٥٢ Or.، نشره عبد السلام محمد هارون، فى ٦ مجلدات، القاهرة ١٩٤٧ - ١٩٥٢.
الدراسات: انظر مجلة المجمع العلمى العربى بدمشق ١١/ ١٩٣١/ ٦٥ - ٧١، ٣٥٢ - ٣٥٥؛ (خالد على)
Kh. I. Ali, Studienuberhomon yme Wurzelnim Arabischen. Mitbesonderer Berucksichtigung des Mu''gam Maqaylsal- lugavon Ahmad Ibn Faris. Diss.','1','383',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (380,'Heidelberg ١٩٦٤
؛ حسين نصار، المعجم العربى ٤٣٥ - ٤٦٥.
٤ - «فقه اللغة»، أهداه لتلميذه الصاحب بن عباد، ولهذا يسمى أيضا «كتاب الصاحبى (فى فقه اللغة وسنن العرب في كلامها)»، «مدخل إلى علم اللغة (المعجمية) العربية، ... وحدة متسقة نوعا ما) I.Goldziher ,Beitrage ... S.٢٠٤ «انظر أدناه)، ذكرته خمس قوائم (انظر أيضا بولس سباط في، MIE ٤٩ /١٩٤٦ / (٤٠ ,No.٧٠٩: نقل عنه كثيرا (انظر مثلا المزهر للسيوطى، الفهرس ٢/ ٦٤٧).

<span data-type="title" id=toc-520><span data-type="title" id=toc-522>المخطوطات:</span></span>
آيا صوفية ٤٧١٥ (١٢٢ ورقة، من القرن السادس للهجرة)، ولى الدين ٣١٢٩ (١٧٣ ورقة، نسخت سنة ٣٨٢ هـ، راجع؛ O.Rescherin: (MFO ٥ /١٩١٢ /٥٢٧ طبع في القاهرة ١٣٢٨؛ ونشره مصطفى الشويمى، بيروت ١٩٦٤، انظر في هذه النشرة ع. المهيرى في: حوليات (تونس) ١/ ١٩٦٤/ ١٥٣ - ١٥٥، ونشره أيضا السيد أحمد صقر، القاهرة ١٩٧٧.
وثمة نصوص منه مقارنة بنصوص مناظرة للغويين آخرين في: نصوص في فقه اللغة العربية للسيد يعقوب بكر، المجلد الأول، بيروت ١٩٧٠، ص ٥٩ - ٦٢، ٧٤ - ٧٥، المجلد الثانى، بيروت ١٩٧١، ص ٢١ - ٣٤.

<span data-type="title" id=toc-521>الدراسات:</span>
I. Goldziher, Beitragezur Geschichteder Sprachgelehrsamk eitbeiden Arabern. III: Abu- ١ - Husein Ibn Farisin: SBAWWien, Bd. ٧٣. ١٨٧٣. S. ٥١١ - ٥٥٢ undin: Ges. Schriften I, ١٨٧ - ٢٢٨;
زكى مبارك، النثر الفنى، المجلد الثانى، القاهرة ١٩٣٤، ص ٣٧ - ٤٧ ٥ - «كتاب تمام فصيح الكلام»، زيادات على كتاب الفصيح لأبى العباس ثعلب (المتوفى سنة ٢٩١/ ٩٠٤)، ذكرته قائمتان.
المخطوطات:
تشستربيتى (ولعلها كانت سابقا في النجف، انظر) ٢/ ٣٩٩٩ (Br.S ١٩٨ ,No.١٥ الأوراق ٢٩ - ٣٨، نقلها ياقوت الحموى سنة ٦١٦ هـ، عن نسخه','1','384',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (381,'بخط مؤلفه، تاريخها سنة ٣٩٠ هـ، راجع معجم البلدان لياقوت ٤/ ٤٣٠ - ٤٣١)، ومنها نسخة في التيمورية بالقاهرة، لغة ٥٢٣/ ١ (الأوراق ١ - ١٩) والمتحف العراقى ببغداد ٧٧٨ و ٢١٩٤ (انظر فهرس النقشبندى ٨٦)؛ نشر A.Arberry مخطوطة تشستربيتى بالتصوير الآلى، لند، ن ١٩٥١، ونشرها مصطفى جوادوى. مسكونى في: «رسائل في النحو واللغة»، بغداد ١٩٦٩، على مخطوطة حديثة (ليست كاملة)، انظر في هذه النشرة رمضان عبد التواب في:
المورد ١، ١ - ٢/ ١٩٧١/ ٢٠٧ - ٢١٢)، ونشرها كاملة إبراهيم السامرائى في:
مجلة المجمع العلمى العراقى ٢١/ ١٩٧١/ ١٦٠ - ١٩٥.
٦ - «الانتصار لثعلب»، لعله ردّ على منتقدى كتاب الفصيح، ذكرته قائمتان (انظر أيضا كشف الظنون ١٧٣).
٧ - «كتاب الإتباع والمزاوجة»، فى الكلمات المسجوعة التى توازن ما قبلها تأكيدا للمعنى، نحو ساغب لاغب، وسغوب لغوب، وحريب سليب، وما شابه ذلك. ذكرته قائمتان، وكان من مصادر الصغانى في التكملة والعباب.

<span data-type="title" id=toc-523>المخطوطات:</span>
أسعد ٣٧٣٥/ ١١ (٦٥ أ- ٧٢ أ، من القرن ١٢ هـ)، وهبى ١٩٨٧/ ١ (١ ب- ١٠ ب، من القرن ١٣ هـ)، كوبريلى ١٦٢٢ (١٣ أ- ٢٧ ب، من القرن السابع للهجرة، انظر شيشين، نوادر ١/ ١٣٩)، رئيس الكتاب ٥٩/ ٣ (١٥٩ ب- ١٦٧ أ، نسخت سنة ٩٩٧ هـ، انظر المرجع السابق)، فاتح ٥١٣٥ (١٤١ - ١٥٠ أ، من القرن ١١ هـ، انظر المرجع نفسه)، النجف، آية الله الحكيم ٥٥٢ م (٧ ورقات، نسخت سنة ١٣٤٨ هـ، انظر فهرس النجف ١/ ١٧)، دار الكتب بالقاهرة، لغة ٥٥ ش (نسخت سنة ٧١١ هـ، انظر الفهرس الثانى ٢/ ١)،','1','385',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (382,'طلعت بدار الكتب، مجموع ٣١٦، ومجموع ٤٨٠، تشستربيتى ٤٦٢٤ (٢٨ ورقة، نسخت سنة ٦٢٧ هـ)، برنستون، جاريت (يهودا) ٣٥٧٠ (٣٣ ب- ٤٧ ب، نسخها الصفدى، انظر ماخ ٣٢٤، رقم ٣٧٨٦)؛ نشره:
R. Brunnowin: Festschr. Th. Noldeke I, ١٩٠٦, S- ٢٢٥ - ٢٤٨
، ونشره أيضا كمال مصطفى، القاهرة ١٩٤٧، وثمة نصوص منه مقارنة بنصوص مناظرة للغويين غيره في: نصوص في فقه اللغة العربية للسيد يعقوب بكر، المجلد الثانى، بيروت ١٩٧١، ص ٣٤٩ - ٣٥٥. وقد نقّحه جلال الدين عبد الرحمن بن أبى بكر السيوطى (المتوفى سنة ٩١١/ ١٥٠٥) فى كتاب سماه «إلماع الإتباع» مع اختصار النص الأصلى وزيادات زادها، انظر المزهر ١/ ٤١٤.
٨ - «كتاب الثلاثة (فى اللغة)»، فى المعانى المترادفة للألفاظ الثلاثية التى يمكن تقليب أصولها ثلاثة تقاليب، لم يذكره إلا البغدادى في هدية العارفين، ويوجد مخطوطا في الإسكوريال ٣٦٣/ ٣ (الأوراق ١٢ - ١٥، نسخت سنة ٧٧١ هـ، انظر فهرس المخطوطات ١/ ٣٦٥)؛ نشره رمضان عبد التواب في: مجلة معهد المخطوطات العربية ١٠/ ١٩٦٤/ ٣٠٩ - ٣٧٨، وعلى حدة في القاهرة ١٩٧٠.
٩ - «ذخائر الكلمات»، يحتمل أنه مصنف في اللغة، ذكرته أربع قوائم وكان في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر بولس سباط في:
(MIE ٤٩/ ١٩٤٦/ ٢٤, No. ٤٢٥.
١٠ - «كتاب علل مصنف الغريب»، أى «علل الغريب المصنف»، وهو في نواحى الضغف في كتاب أبى عبيد (المتوفى سنة ٢٢٤/ ٨٣٩)، وكان من مصادر الصغانى في التكملة (انظر المقدمة ١/ ٨) والعباب (انظر ١/ ٣٠).
١١ - «آمال، فوائد»، ويشتمل أيضا على ملاحظات عن اللغويين والنحاة ومسائل في اللغة، انظر في نقول عنه: مقدمة فصل النحو./.

<span data-type="title" id=toc-524>ومن مصنفاته في الاشتقاق والاسماء:</span>
١ - «كتاب اشتقاق أسماء البلدان»، انظر فى نقول عنه: فصل الجغرافية.','1','386',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (383,'٢ - «دارات العرب» (وقيل أيضا خطأ «دار العرب»)، ولم يصل إلينا، انظر فصل الجغرافية.
٣ - «تفسير أسماء النبى»، كذا فى عدة قوائم وعند بولس سباط في:
MIE ٤٩/ ١٩٤٦/ ١٤, No. ٢٥٤
، وذكر باسم «المنبّئ في (تفسير) أسماء النبى» وفي «هدية العارفين» للبغدادى وفي «كشف الظنون» ٨٤٨، وقد وصل إلينا باسم «أسماء رسول الله»، أيا صوفية ٤٦٩/ ٣ (٩٦ ب- ٩٩ ب، نسخت قبل سنة ٨١٨ هـ، ناقصة).

<span data-type="title" id=toc-525>وله تآليف مفردة في موضوعات:</span>
١ - «كتاب خلق الإنسان»، جاء ذكره في خمس قوائم (راجع كشف الظنون ٧٢٢)، ووصل إلينا باسم «مقالة في أسماء أعضاء الإنسان»، الأحمدية بالموصل (ورقتان ضمن مجموع، انظر فهرس جلبى ٣٣، رقم ١٥٢/ ٥)؛ نشره ف. دبدوب في مجلة المجمع العلمى العربى بدمشق ٤٢/ ١٩٦٧/ ٢٣٥ - ٢٥٤.
٢ - «كتاب الفرق (بين الإنسان وغيره من الحيوان في أشياء من الخلق والخلوق)»، ذكرته أربع قوائم، وأشار إليه المؤلف في تمام فصيح الكلام (مجلة المجمع العلمى العراقى ٢١/ ١٩٧١/ ١٩٣)، المخطوطات: أسعد ٣١٤٤/ ٢ (٤٠ ب- ٥٦ أ، من القرن العاشر للهجرة)، لا له لى ٥٣٨٠/ ٢ (٣٣ ب- ٥١ ب، نسخت سنة ١١٥٥ هـ)، نشره رمضان عبد التواب، القاهرة والرياض، ١٩٨٢ على مخطوطة لا له لى.
٣ - «كتاب العم والخال»، مذكور في خمس قوائم، ٤ - «كتاب الشيات والحلى»، ذكرته كذلك خمسي قوائم، ٥ - «كتاب الحجر»، ألّفه للصاحب بن عبّاد الذى قيل إنه لم يتلق الكتاب بحسن القبول (انظر يتيمة الدهر ٣/ ٢٠٤؛ إنباه الرواة للقفطى ١/ ٩٣)، ذكرته أربع قوائم، ونقل عنه ابن فارسنفسه في كتاب الصاحبى ص ١٥ (عن رمضان عبد التواب في مقدمته لكتاب الثلاثة لابن فارس ص ٢٠).','1','387',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (384,'٦ - «كتاب النيروز»، ويعرض بعد شرح هذه الكلمة شرحا موجزا للألفاظ التى على زنة فيعول، لم تذكره قوائم مصنفاته، ويوجد مخطوطا في التيمورية بدار الكتب بالقاهرة، لغة ٤٠٢ (نسخت سنة ١٣٣٩ هـ)؛ نشره عبد السلام هارون في: نوادر المخطوطات ٢، القاهرة ١٩٥٤، ص ١٧ - ٢٥، ٧ - «كتاب الليل والنهار»، مذكور في ست قوائم (راجع كشف الظنون ١٤٥٤)، ولم يصل إلينا، انظر تاريخ التراث العربى ٧/ ٣٦٠، ٨ - «كتاب الأنواء على مذهب العرب»، لم تذكره القوائم، وقد وصل إلينا، انظر المرجع السابق.
٩ - «كتاب استعارة أعضاء الإنسان»، نشره أ. خان على مخطوطة أكسفورد، بودليانا (٩ ورقات، نسخت سنة ٦٥٠ هـ) في: المورد ١٢، ٢/ ١٩٨٣/ ٨١ - ١٠٨.
تآليف مختلفة من جهة محتواها ولم تثبت نسبتها اليه:
١ - «كتاب الموازنة»، وهو من مصادر الصغانى في التكملة (انظر المقدمة ١/ ٨) والعباب (انظر ١/ ٣٠)، ٢ - «كتاب الوجوه والنظائر»، ذكره البغدادى في «هدية العارفين» ١/ ٦٩.
٣ - «محنة (؟ ) الأريب»، مذكور في المصدر نفسه.

<span data-type="title" id=toc-526>وله في لغة القرآن والحديث، والفقه:</span>
١ - «جامع التأويل في تفسير القرآن»، كذا في أربع قوائم، وعند پولس سباط في، MIE ٤٩ /١٩٤٦ /١٦ ,No.٢٨١: وذكره البغدادى باسم «جامع التأويل في تفسير التنزيل»، وقيل إنه كان أربعة مجلدات. ونقل عنه ابن فارس نفسه في «فوائد الفاظ القرآن».
٢ - «فوائد ألفاظ القرآن»، تفسير المعانى المجازية لبعض ألفاظ القرن لم تذكره القوائم،','1','388',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (385,'ويوجد مخطوطا في عاشر ٤١٦/ ١٤ (٢٢٦ ب- ٢٢٩، على هامش المخطوطة، من القرن ١١ هـ).
٣ - «مقالة كلا وما جاء منها في كتاب الله»، لم يرد ذكرها في القوائم، ونقل عنها في كتابه «الصاحبى» ١٣٤ (عن ف. دبدوب في: مجلة المجمع العلمى العربى بدمشق ٤٢// ١٩٦٧/ ٢٤٣)،

<span data-type="title" id=toc-527>المخطوطات:</span>
التيمورية بالقاهرة، مجموع ٣٦٩ (انظر مجلة المجمع العلمى العربى بدمشق ٥٤/ ١٩٧٩/ ٣٦٧)، لكنا وبالهند، مكتبة سليمان الندوى الخاصة؛ نشرها على هذه المخطوطة عبد العزيز الميمنى في: «ثلاث رسائل»، القاهرة ١٣٤٤، ص ٥ - ١٨؛ ونشرها أيضا أ. ح. فرحات، الرياض ودمشق، ١٩٨٢.
٤ - «غريب إعراب القرآن» أو «غريب القرآن وإعرابه»، مذكور في خمس قوائم، وكان في القرن ٧/ ١٣ في إحدى مكتبات حلب (انظر بولس سباط في:
(MIE ٤٩/ ١٩٤٦/ ٣٧, No. ٦٥٩
٥ - «كتاب الأفراد»، لم تذكره القوائم، ونقل عنه السيوطى قطعة طويلة في «الإتقان، ٢/ ١٣٢ - ١٣٥ وكذلك الزركشى في «البرهان» ١/ ١٠٥ وما بعدها (عن رمضان عبد التواب في مقدمة لكتاب الثلاثة لابن فارس ص ١٨)، ٦ - «مأخذ (وربما مآخذ) العلم»، عن كشف الظنون ١٥٧٤ والهداية للبغدادى ٦٩، ويحتمل أنه يتعلق بعلوم القرآن والحديث مثل الباب الأول الذى يوافقه في التسمية من كتابه «حلية الفقهاء»، ٧ - «كتاب حلية الفقهاء»، يتناول في أبواب عديدة الأصول اللغوية لاصطلاحات الفقه، ذكرته كل القوائم (انظر أيضا فهرست ابن خير ٣٧٣؛ پولس سباط في؛ MIE ٤٩ /١٩٤٦ /١٨ ,No.٣١٩: كشف الظنون ٦٩٠)،','1','389',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (386,'ويوجد مخطوطا في انقره، صائب ١٧١٣ (أ- ٥٠ ب، نسخت سنة ٥٨٩ هـ، ويلى ذلك حتى ٥٤ ب مسائل فقهية- لغوية، وهى ليست مسائل «كتاب فتيا فقيه العرب»)، ٨ - «كتاب فتيا فقيه العرب» أو «مسائل في اللغة تعايا بها الفقهاء»، وهى طائفة من مسائل فقهية- لغوية على طريقة السؤال والجواب، قارنه بكتاب «الملاحن» لابن دريد محمد بن عبد الغفور الكلاعى (القرن ٦/ ١٢)، «إحكام صنعة الكلام»، بيروت ١٩٦٦، ص ١٨٩، وجاء ذكره في أربع قوائم (انظر أيضا فهرست ابن خير ٣٧٤)، وكان بين كتب الخطيب البغدادى (انظر التسمية للمالكى في كتاب الخطيب البغدادى ليوسف العش ١١٢، رقم ٤٦٧)،

<span data-type="title" id=toc-528>المخطوطات:</span>
مشهد، رضا ٤٣٧٩ (١٥ ورقة، نسخت سنة ١٠٠٢ هـ، انظر
O. Spiesin: Festsci. E. Littmann, Leiden ١٩٣٥, S. ٩٦
؛ أسعد طلس في: مجلة المجمع العلمى العربى بدمشق ٢٤/ ١٩٤٩/ ٢٧٣؛ فهرست، ... دو كتابخانه ٩٢٧)، بغداد، كلية الآداب، سابقا معهد الدراسات الإسلامية (انظر القائمة المطبوعة على الآلة الكاتبة ١٢٧، رقم ٨٨١)، نشره حسين على محفوظ في: مجلة المجمع العلمى العربى بدمشق ٣٣/ ١٩٥٨/ ٤٤٣ - ٤٦٦، ٦٣٣ - ٦٥٥، ويبدو أن الكتاب حفز الحريرى على إنشاء مقامته الثانية والثلاثين (انظر المزهر للسيوطى ١/ ٦٢٢ - ٦٣٧).

<span data-type="title" id=toc-529>وله في أصول الفقه ومناهجه</span>
(استدراك على المجلد الأول من تاريخ التراث العربى):
١ - «أصول الفقه»، ذكر في ثلاث قوائم، وكان في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في» -٢، (MIE ٤٩ /١٩٤٦ /٧ ,No.١٢١: رسالة في الفرائض الأربعة»، وذكر باسم «مقدمة الفرائض» فى ثلاث','1','390',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (387,'قوائم (انظر أيضا بولس سباط في، (MIE ٤٩ /١٩٤٦ /٤٦ ,No.٨٣٣: وتوجد مخطوطة في فاتح ٥٣٠٠/ ٨ (الأوراق ١٥٠ - ١٥٥، من القرن العاشر للهجرة)، وفي حلية الفقهاء ٣٦ أ- ٣٨ أباب مناظر، ٣ - «كتاب التاج»، روى مع «حلية الفقهاء» و «فتيا فقيه العرب» فى الأندلس (انظر فهرست ابن خير ٣٧٤)، ويجوز على هذا أنه كان أيضا في الفقه./

<span data-type="title" id=toc-530>الجوهرى</span>
هو أبو نصر اسماعيل بن حماد من أهل فرياب، ابن أخت إسحاق ابن إبراهيم الفارابى وتلميذه (انظر ص ١٩٧ آنفا) وعليه بدأ تحصيله، رحل في شبابه إلى بغداد حيث أخذ عن أبى على الفارسى والسيرافى. ثم سافر بعدها إلى الحجاز وطوّف في بلاد ربيعة ومضر ليأخذ عن أهلها لغتهم مشافهة ثم قفل راجعا إلى فارس، فأقام بالدّامغان ونيسابور مدة.
واختلف في تاريخ وفاته، فقيل سنة ٣٩٣/ ١٠٠٣ (١٤٥)، وقيل سنة ٣٩٨ هـ، وقيل سنة ٤٠٠ هـ.
ونال الجوهرى شهرة عريضة بمصنفه في اللغة الذى أخمل إلى حد بعيد معاجم من تقدمه وعاصره لسهولة مأخذه. واشتغل الجوهرى فضلا عن ذلك بالنحو والعروض.

<span data-type="title" id=toc-531>مصادر ترجمته:</span>
نزهة الألباء للأنبارى ٢ ٢٣٦ - ٢٣٨؛ إنباه الرواة للقفطى ١/ ١٩٤ - ١٩٨؛ إرشاد الأريب لياقوت ١ ٢/ ٢٦٦ - ٢٧٣، ٢ ٦/ ١٥١ - ١٦٥؛ لسان الميزان لابن حجر ١/ ٤٠٠ - ٤٠٢؛ مسالك الأبصار ٤/ ٢٤٥ - ٢٤٨؛ ابن قاضى شهبة، المخطوط ص ٢١٥ - ٢١٨.-
Flugel, Gramm. Schulen ٢٥٣ - ٢٥٤; Br. GI, ١٢٨ - ١٢٩, SI, ١٩٦ - ١٩٧; Rescher,
_________
(١٤٥) وهو محال إن كان ياقوت (إرشاد ٦/ ٤١٩) قد رأى حقيقة نسخة من الصحاح كتبها الجوهرى بخطه سنة ٣٩٦.','1','391',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (388,'Abri BII, ٢١٨ - ٢٢٠; M. Ben Chenebin: EI, I, ١٠٧٣ - ١٠٧٤; L. Kopfin: EI, II ٢, ٤٩٥ - ٤٩٧;
الأعلام للزركلى ١/ ٣٠٩ - ٣١٠؛ معجم المؤلفين لكحالة- ٢/ ٢٦٧ - ٢٦٨.

<span data-type="title" id=toc-532>آثاره:</span>
١ - «تاج اللغة وصحاح (صحاح) العربية»، وهو أوسع المعاجم العربية انتشارا قبل «القاموس المحيط» للفيروزآبادى، اعتمد فيه على «ديوان الأدب» لخاله إسحاق بن ابراهيم الفارابى (انظر ص ١٩٧ آنفا).
وزعموا أن الجوهرى نفسه بيّض الكتاب إلى حرف الضاد فقط، وأن تلميذه أبا إسحاق إبراهيم بن صالح الورّاق (١٤٦) بيّض بقيته. والأرجح أن الجوهرى أتم تبييض الكتاب وأن أبا إسحاق بن صالح أول من رواه. (انظر إرشاد الأريب لياقوت ٢/ ٢٧١ - ٢٧٢، كشف الظنون. (L.Kopf ,EI ,II ٢ ,٤٩٦ - ٤٩٧؛ ١٠٧٢ - ١٠٧١
ورأى ياقوت (إرشاد ٦/ ٤١٩) نسخة بخطّ الجوهرى، كتبها سنة ٣٩٦ هـ.
انظر ما قيل في تقدير الكتاب وملاحظات على تاريخ روايته: القفطى، فى الموضع المذكور؛ الأنبارى، فى الموضع المذكور؛ السيوطى في المزهر ١/ ٩٧ - ٩٩.
ومخطوطاته تكاد توجد في كل مكتبة، من بينها (مع اختصار البيانات):
كوبريلى ١٥٤٦ (نسخت سنة ٥٧٧ هـ)، ١٥٤٧ (نسخت سنة ٥٧٦ هـ)، ١٥٤٨ (نسخت سنة ٦٩٠ هـ)، ١٥٥٠ (نسخت سنة ٦٩٥ هـ)، شهيد على ٦٢٤٢ (نسخت سنة ٦٤٧ هـ)، بغدادلى وهبى ١٩٧٠ (نسخت سنة ٦٥٣ هـ)، ١٩٨٤ (نسخت سنة ٦١٠ هـ)، لا له لى ٣٥٦٥ و ٣٥٦٦ (نسخت سنة ٦٣٥ هـ)،
_________
(١٤٦) انظر فيه دمية القصر للباخرزى ٢، النجف ١٩٧١، ص ٥٠٢ - ٥٠٣؛ إنباه الرواة للقفطى ١/ ١٦٩ - ١٧٠، ٢/ ٩٠ (واسمه هنا صالح الوراق)؛ إرشاد الأريب لياقوت ١/ ٦٥؛ الوافى بالوفيات للصفدى ٦/ ٢٢.','1','392',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (389,'داماد ابراهيم ١١٢٣ (نسخت سنة ٦٧٥ هـ)، فيض الله ٢٠٧١ (نسخت سنة ٦٤٤ هـ)، ٢٠٧٣ (نسخت سنة ٥٧٩ هـ)، جار الله ٢٠١٨ (نسخت سنة ٦٤٨ هـ)، ٢٠١٩ (نسخت سنة ٥٤٤ هـ)، ٢٠٢٠ (نسخت سنة ٦٩٥ هـ)، / برتونيال ٧٢٠ (نسخت سنة ٦٥٤ هـ)، جورليلى على ٤٣٤ (نسخت سنة ٥٨٤ هـ)، اسميخان ٤١٥ (نسخت سنة ٦٧٧ هـ)، أيا صوفية ٤٦٨٧ (نسخت سنة ٦٥٢ هـ)، شاه زادة محمد ١٠٢ (نسخت سنة ٦٢٧ هـ)، مراد ملا ١٧٧١ (نسخت سنة ٥٢٦ هـ)، ١٧٧٢ (نسخت سنة ٥٢٥ هـ)، ١٧٧٣ (نسخت سنة ٥٢٥ هـ)، ١٧٧٤ (نسخت سنة ٥٢٦ هـ)، ١٧٧٥ (نسخت سنة ٥٢٦ هـ)، سراى أحمد الثالث، ٢٦٨٩ (نسخت ٦٢١ هـ)، ٢٦٩١ (نسخت سنة ٦٧٠ هـ)، ٢٧٣٠ (من القرن السادس للهجرة، انظر فهرس المخطوطات ١/ ٣٤٣)، سراى، خزينة ١١٨٠ (من القرن السابع للهجرة، انظر فهرس المخطوطات ١/ ٣٤٣)، انظر في هذه المخطوطة وسبع مخطوطات أخرى: الفهرس ٤/ ١١ - ١٤، أماسية ٩٧٢ (نسخت سنة ٥٨٧)، مانيسا ٢٧٩٠ (نسخت سنة ٦٨٨ هـ)، ٢٧٩٧ (نسخت سنة ٦٥٧ هـ، انظر، (Li stedesmanuscrits ... Manisa ١٩ نوشهر، إبراهيم باشا ٢١٥ (نسخت سنة ٦٣٢ هـ، نسخة كاملة وحسنة جدا، على هامشها حواس وتخريجات للشواهد، ولا غنى عنها في إخراج طبعة جديدة، راجع، (Li stedesmanuscrits ... Kayseri ١٥ نجده ٤٧١ (نسخت سنة ٦٧٢ هـ) ومخطوطتان أخريان (انظر المرجع السابق ص ١٩)، بورسه، أورهان ١٦٠١ (نسخت سنة ٦٤٦ هـ، انظر، Listedes (manuscrits ... Bursa ٧٤','1','393',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (390,'مخطوطتان في طهران، مكتبة السلطنة (نسخت سنة ٥٦٤ هـ و ٦٧٢ هـ، انظر مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٧١، رقم ١٣ و ١٤)، ملى (نسخت سنة ٦٨١ هـ، انظر المجلة السابقة ص ٢٢)، خمس مخطوطات في ملك (من القرن السابع للهجرة وأحدث عهدا، انظر الفهرس ١/ ٤٧٤ - ٤٧٦، وراجع مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٦٧)، مكتبة الخنجى الخاصة بطهران (الجزء الثانى، نسخ سنة ٦١٨ هـ، انظر مجلة معهدالمخطوطات ٣/ ١٩٥٧/ ٦٣، رقم ٥٨)، مكتبة ضياء الدين الددى الخاصة بطهران (الجزء الثالث، نسخ سنة ٦٥٠ هـ، انظر المجلة السابقة ص ١٣)، قم، مرعشى ٤١٩ (نسخة قديمة) و ٩٩٨ (انظر الفهرس ٢/ ٢٣ - ٢٤، ٣/ ١٩١)، مشهد، رضا، ٢٢ مخطوطة (انظر فهرست ... دو كتابخانه ٩٠٠ - ٩٠١)، مشهد، جوهر شاد (نسخت سنة ٩١٠ هـ، انظر فهرست ...
جهار كتابخانه ٣٤٣)، كابول، مكتبة المتحف (جزء، انظر دبور كوى ٨٤، رقم ٧٥)، عليكرة، الجامعة الإسلامية ٨٩٢٤٧٢٩/ ١٠ و ١١ (الجزءان الأول والثانى، نسخا سنة ٦٤٨ هـ، قوبلا على نسخة بخط الجواليقى، انظر الفهرس ١٣٥)، بنكيبور ١٩٦٦ (نسخت سنة ٦٣٣ هـ، قوبلت كذلك على نسخة بخط الجواليقى، انظر الفهرس ٢٠/ ٧ - ٨)، نسخة حسنة جدا و ١٢ غيرها في بغداد، الأوقاف (انظر فهرس الجبورى ٣/ ٢١٧ - ٢٢٠، رقم ٥١٢٢ - ٥١٣٤)، بغداد، المتحف العراقى ٢٢٩٥ (نسخت سنة ٥١٤ هـ) و ١٩ مخطوطة أخرى (انظر فهرس النقشبندى ٩٠ - ٩٤، رقم ٣١٧ - ٣٣٦)، بغداد، كلية الآداب، سابقا معهد الدراسات الإسلامية (مجلدان، من القرن الخامس للهجرة و ٥٧٧ هـ، انظر القائمة المطبوعة على الآلة الكاتبة ٥٠، رقم ٥٦٦، ٥٦٧)، الظاهرية، عام ٦٦٨٥ (الجزء الأول، نسخة قديمة وقيمة، منقولة عن نسخة','1','394',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (391,'تاريخها سنة ٤٢١ هـ، عليها حواش عديدة)، عام ٨٨٢٨ (اجزاء من مخطوطة تاريخها سنة ٥٩١ هـ، نقلت عن نسخة كتبها الجواليقى وعلى هامشها حواش للتبريزى)، انظر في هذه النسخة وتسع نسخ غيرها: فهرس اللغة ٣ - ١٧، المدينة، عارف حكمت، لغة ٢٨ (نسخت سنة ٦٨١ هـ، انظر المنتخب لكحالة ٧٤)، ومنها ميكرو فلم بمعهد المخطوطات بالقاهرة، الرياض، المكتبة العامة ٨٦/ ٨٦ (الجزء الأخير، نقل سنة ٨٩٠ هـ عن نسخة تاريخها سنة ٥٩٦ هـ، انظر مجلة معهد المخطوطات ٢٣، ١/ ١٩٧٧/ ٥، رقم ١٤)، ومنها ميكروفلم في معهد المخطوطات العربية بالقاهرة، الرياض، مكتبة الجامعة، لغة ٢٨١ (نسخت سنة ٨٥٨ هـ)، ومنها ميكرو فلم بمعهد المخطوطات بالقاهرة، تعز، مسجد الملك المظفر (جزء)، ومنه ميكروفلم بالقاهرة، الحزم (اليمن) (جزء، نسخ سنة ٩٧٧ هـ)، ومنه ميكرو فلم بالقاهرة، وثمة أجزاء مفردة في دار الكتب بالقاهرة، لغة ١٦ (نسخت سنة ٥٩٢ هـ)، لغة ٩٥ (نسخت سنة ٥٩٤ هـ)، لغة ٩٧ (نسخت سنة ٦٨٥ هـ)، لغة ١١٠ (كاملة، نسخت سنة ٥٩٥ هـ و ٥٩٦ هـ)، لغة ١١١ (نسخت سنة ٥٨١ هـ) وست مخطوطات أخرى (انظر الفهرس الثانى ٢/ ٦)، ٥٠٧٩ هـ (نسخت سنة ٦٠٢ هـ، انظر الملحق ١/ ١١٧)، وثمة ثلاث مخطوطات في الاسكندرية، البلدية (من القرن التاسع للهجرة وأحدث عهدا، انظر الفهرس (١) لغة ٦١ - ٦٢)، ٢٢ مجلدا فى تونس، دار الكتب الوطنية، الأحمدية (انظر فهرس منصور ١٤٠ - ١٤٥)، مجموعة حسن حسنى عبد الوهاب ١٨٢٠١ (الجزء الأول، نسخ سنة ٦٤٠ هـ، انظر فهرس منصور ١٤٧)، الرباط، المكتبة العامة، الأوقاف ٥٤٣ (الجزء الأخير، من القرن الثامن للهجرة)، ومنها ميكرو فلم بمعهد المخطوطات بالقاهرة، الرباط، مكتبة الملك (الزيدانية) ١٥٩٤ (الجزء الأخير، نسخ سنة ٧١٧ هـ)، ومنها ميكرو فلم بالقاهرة،','1','395',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (392,'الإسكوريال ٥٧٩ (كاملة، نسخت سنة ٨٤٤ هـ)، ومنها نسخة/ بميلانو، أمبروزيانا) A.F.١٩٨ sup. انظر فهرس لوفجرن/ ترينى ١/ ١٢٧، رقم ٢٣٣)، وفي باريس ١٤ مخطوطة (راجع فجدا ٦٤٣ - ٦٤٤)، برلين من ٦٩٣٦ إلى ٦٩٣٨، وأيضا) ٣٨٨١ Or.Oct. نسخت سنة ٦٩٤ هـ، راجع فاغنر رقم ٣٠٢)، ليدن، الأكاديمية ٧١ (٤ مجلدات، من القرن ٦ - ٧ للهجرة)، و ٥) ٢٥٦٢ Or. مجلدات، نسخ بعضها سنة ٥٨٩ هـ و ٦١٧ هـ) وثمة خمس مخطوطات أخرى (انظر فورهوف ٣٢٣)، صوفيا ٢٤٩) ٣٣٨٣ OP. ورقة، نسخت سنة ١٠٨٤ هـ) و) ٢١٧٤ OP. الجزء الثالث، ٢٧٧ ورقة، نسخة قديمة، انظر فهرس درويش ٢/ ٨ - ٩)، باكو، الأكاديمية (مجلدان، نسخا سنة ٥١٦ هـ، عليها حواش للجواليقى، انظر مجلة معهد المخطوطات ٢٣، ٢/ ١٩٧٧/ ١٣٨)، ليننجراد، المعهد ١٥٩ (نسخت سنة ٨٤٠ هـ، انظر روزن رقم ١٥١)، والمتحف الآسيوى، مجموعة بخارى ٥٩٥ (انظر بلياييف رقم ٥٤٤)، طشقند ١٤٨٧ (٧٨٧ ورقة، نسخت سنة ٩٠٤ هـ، انظر الفهرس، المجلد ٦، رقم ٤٤٠٠)، المتحف البريطانى) ٢٤٨٠ Or. نسخت سنة ٦٥٨ هـ، انظر الملحق، رقم ٨٤٥) وتسع مخطوطات أحدث عهدا (الفهرس ص ٢٢٧، رقم ٤٦٧، رقم ١٠٠٦، ص ٦٣٩، رقم ١٣٧٥ إلى ١٣٧٧ والملحق رقم ٨٤٦ إلى ٨٤٩)، تشستربيتى ٣٠١٩ (نسخت سنة ٥٩٤ هـ)، ٣٨٥٣ (نسخت سنة ٥٨٩ هـ)، ٤٢٤٩ (نسخت سنة ٥٩٨ هـ)، وثمة أربع مخطوطات في برنستون، جاريت (من القرن السادس وصدر السابع للهجرة، انظر الفهرس، رقم ٢٥٤ - ٢٦٠)، وتسع مخطوطات في جاريت (يهودا، أقدمها نحو سنة ٧٠٠ هـ، انظر ماخ ٣٢١، رقم ٣٧٥٥)،','1','396',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (393,'ست مخطوطات في ييل (من القرن السادس للهجرة وأحدث عهدا، انظر نيموى رقم ١٧٨ - ١٨٣).
نشر شيديوس الجزء الأول من ترجمة لاتينية:
E. Scheidius, Abu Nasri Ismaelisebn Hammadal- Gieuharii ... Liber Sehah, ... , Harderwijk ١٧٧٦,
وطبعه طبع حجر الشيخ موسى، تبريز ١٢٧٠، وطبعه مع مقدمة نصر الهورينى. فى مجلدين، بولاق ١٢٨٢. وطبعة ثانية ١٢٩٢، ثم طبع في ستة مجلدات، القاهرة ١٩٥٦ - ٥٧

<span data-type="title" id=toc-533>دراسات في الكتاب:</span>
I. Goldziher, Beitragezur Geschichteder Sprachgelehrsamk eit Beiden Arabern. II. Zur Gauharl- Literaturin: SBAWWien, PHil.- hist. Klasse ٧٢/ Ges. Schriften I
، ١٩ - ٥٣١؛ وفي: ٢٧٨١/ ٧٨٥ - ١٣٦،
C. Brockelmann, Gauharlunddie Anordnungdesarab ischen Alphabetsin: ZDMG ٦٩/ ١٩١٥/ ٣٨٣ - ٣٨٤; J. Kraemer, Studienzuraltara bischen Lexikograpiein: Oriens ٦/ ١٩٥٣/ ٢٠١ ff., hier S. ٢١٦ - ٢١٧;
حسين نصار، المعجم العربى ٤٤٨ - ٥٢٩؛ أحمد عبد الغفور عطار، الصحاح ومدارس المعجمات العربية، القاهرة ١٩٥٦؛ إبراهيم السامرائى، «كلمات في الصحاح» فى: مجلة مجمع اللغة (الأردن) رقم ٣ - ٤/ ١٩٧٩/ ٢٨ - ٤١، و «مع معجم الصحاح وحواشيه» فى المجلة المذكورة رقم ١٧ - ١٨/ ١٩٨٢/ ٥٥ - ١٠٢

<span data-type="title" id=toc-534>ومما صنّف في تهذيبه والاستدراك عليه واختصاره ونقده والانتصار له الخ</span>.:
١ - «كتاب المنتهى (فى اللغة)»، لأبى المعالى محمد بن تميم البرمكى اللغوى (صنّفه سنة ٣٩٧/ ١٠٠٧ في مصر)، وكان يشتمل، تبعا لياقوت (إرشاد الأريب ٦/ ٤١٩ - ٤٢٠) على كل مواد الصحاح مع زيادات قليلة. وقد وصلت إلينا قطعة منه من حرف الثاء، انظر ص ٢٤٤ بعد.','1','397',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (394,'٢ - (تصحيح الصحاح) لأبى الحسن الهروى النحوى (المتوفى نحو سنة ٤١٠/ ١٠١٩، انظر فصل النحو)، وهو من أوائل، وقيل «أول» من أدخل نسخة من كتاب الصحاح مصر. ويقال إنه أصلحه وأتمه (انظر إنباه الرواة للقفطى ٢/ ٣١١).
٣ - «حواش على الصحاح» لأبى القاسم الفضل بن محمد بن على البصرى القصبانى (المتوفى سنة ٤٤٤/ ١٠٥٢، انظر فصل النحو)، ذكره ياقوت في إرشاد الأريب ٦/ ١٤٣، راجع كشف الظنون ١٠٧٢.
٤ - «حواش على الصحاح» لابى القاسم على بن جعفر بن على بن القطّاع الصقلى (المتوفى سنة ٥١٥/ ١١٢١)، وعليه اعتمد تلميذه ابن برى في كتابه (انظر رقم ٩ بعد)، وانظر إرشاد الأريب لياقوت ٥/ ١٠٧، وراجع كشف الظنون ١٠٧٢.
٥ - «قيد الأوابد من الفوائد (وهو ما اختلف فيه كتاب الصحاح ... وكتاب التهذيب ... للأزهرى)» لابى الفضل أحمد بن محمد بن أحمد الميدانى (المتوفى سنة ٥١٨/ ١١٢٤)، ولم يذكر بين مصنفاته/ (انظر إنباه الرواة للقفطى ١/ ١٢٤)؛ وقد وصل إلينا، برلين ٦٩٤٢ (٣٨ ورقة، نسخت نحو سنة ٨٠٠ هـ).
٦ - «اختصار ... » لموهوب بن أحمد بن محمد الجواليقى (المتوفى سنة ٥٣٩/ ١١٤٤)، ويبدو أنه لم يكن معروفا لأصحاب المصادر، ويوجد مخطوطا في ليدن ٤٢٣) ٣١٧ Or.، ورقه، انظر فورهوف ٣٢٤).
٧ - «كتاب ينابيع اللغة» لأبى جعفر أحمد بن على بن أبى جعفر محمد جعفرك البيهقى (المتوفى سنة ٥٤٤/ ١١٥٠)، وهو مختصر لكتاب الصحاح قرأه على أستاذه الميدانى (انظر قم ٥ آنفا) مجردا من الشواهد مع زيادات كثيرة أفادها من معاجم أخرى، ولذلك غدا واسعا سعة الصحاح، ووصفه الحوارى في «كتاب ضالّة الأريب» (انظر رقم ٨ بعد)، انظر إرشاد الأريب لياقوت ١/ ٤١٥.','1','398',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (395,'٨ - «كتاب ضالّة الأديب من (أو: فى الجمع بين) الصحاح والتهذيب» لتاج الدين محمود بن أبى المعالى بن الحسن الحوارى (كان حيّا سنة ٥٨٠/ ١١٨٤، انظر إرشاد الأريب لياقوت ٧/ ١٥٢)، انتقد فيه الجوهرى، انظر نقولا عنه في إرشاد الأريب لياقوت ١/ ٤١٥، ٢/ ١٠٨ - ١٠٩، ٢٧١، راجع بغية الوعاة للسيوطى ٣٩٠؛ كشف الظنون ١٠٨٥.
٩ - «التنبيه والإفصاح (أو: الإيضاح، انظر إنباه الرواة للقفطى ٢/ ١١١) عما وقع (من الوهم) فى كتاب الصحاح» لأبى محمد عبد الله بن برّى بن عبد الجبار المصرى (المتوفى سنة ٥٨٢/ ١١٨٦)، ويعرف أيضا باسم «الأمالى» (وكذا ينقل عنه كثيرا في خزانة الأدب، انظر إقليد الخزانة للميمنى ٧١). أكمل ابن برى موادّ أستاذه ابن القطاع (انظر رقم ٤ آنفا) فى ستة مجلدات، إلى حرف الشين (ربع الصحاح) فوضع بذلك «أجود تآليفه» (كشف الظنون ١٠٧٢).

<span data-type="title" id=toc-535>المخطوطات:</span>
تضم مخطوطة شهيد على كل ما كتب: شهيد على ٢٦١٠/ ١ (المجلد الأول، ٢٠١ ورقة، نسخت سنة ٦٠٤ هـ، انظر فهرس المخطوطات ١/ ٣٤٧)، و ٢٦١٠/ ٢ (المجلد الثانى، ١٠٧ ورقة، من القرن العاشر للهجرة، نقلت عن نسخة بخط المؤلف، انظر المرجع نفسه)، فيض الله ٢٠٦٧ (نسخت سنة ٩٩٥ هـ)، قونيه، مكتبة قيونو غلو الخاصة ٢٠٢٠ (الجزء الثانى، ٢١١ ورقة، نسخت سنة ٥٩٤ هـ، راجع، Listedes (manuscrits ... Konya ٣٧ يوسف أغا ١٢٤ (الجزء الثالث من المخطوطة نفسها، ٢٤٩ ورقة، انظر أحمد آتش في، Belleten ١٦ /١٩٥٢ /٦٤: وراجع، (Li stedesmanuscrits ... Konya ٣٣ القاهرة، التيمورية بدار الكتب، لغة ٨ (انظر حسين نصار، المعجم ٢/ ٥٢٠، الحاشية)، الإسكوريال ١٥٨٥/ ١ (جزء، الأوراق ١ - ١٧١ أ، نسخت سنة ٩٩٧ هـ،','1','399',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (396,'راجع فهرس المخطوطات ١/ ٣٤٧)، برلين، ١٩٥٤ Or.quart انظر في محتوى الكتاب: حسين نصار، المعجم ٢/ ٥٢٠ - ٥٢٦. «مجرّد» للمؤلف نفسه، فاتح ٥٢٣٤ (١ أ- ١٥٢ ب، نسخ سنة ٧٠٩ هـ)، ونشره مصطفى حجازى وآخرون، على مخطوطتى شهيد على والاسكوريال، فى مجلدين، القاهرة ١٩٨٠، ١٩٨١، انظر في هذه النشرة أحمد مختار عمر في: مجلة معهد المخطوطات (الكويت) ١، ١/ ١٩٨٢/ ٣٩٥ - ٤٣٢، أ- وعليه «حواش» وزيادات لما بعد حرف الشين من الحروف، وهى لابن برى كذلك، فى آخر مخطوطة شهيد على ٢٦١٠/ ٢ (انظر أولها في فهرس المخطوطات ١/ ٣٤٧)، فاتح ٥٢٣٤ (١٥٣ ب- ٥٨ أ، نسخت سنة ٧٠٩ هـ)، الإسكوريال ٥٨٥/ ٢ (١٧١ ب- ١٧٦، نسخت سنة ٩٩٧ هـ).
ب- وعليه «تعليق لغوى من حواشى الصحاح للجوهرى من كلام ابن برّى»، مقتطفات لمجهول، كوبريلى ١٥٢١/ ١ (الأوراق ١ - ٢٣ ب، من القرن السادس للهجرة، انظر فهرس المخطوطات ١/ ٣٤٧).
ج- «تكلمة حاشية ابن برى» لعبد الله بن محمد بن عبد الرحمن السعدى البسطى (انظر تبصير المنتبه لابن حجر ١٥٤)، كتبت سنة ٦٢٢/ ١٢٢٥ (انظر حسين نصار، المعجم ٢/ ٥٢٠)، راجع خزانة الأدب ٢/ ٥٢٩؛ كشف الظنون ١٠٧٢.
١٠ - «حاشية الصحاح» لياقوت بن عبد الله الرومى الحموى (المتوفى سنة ٦٢٦/ ١٢٢٩)، ونقل عنه مرارا في خزانة الأدب (انظر إقليد الخزانة للميمنى ٧٢).
١١ - «نظم الصحاح» ليحيى بن معطى بن عبد النور النحوى/ (المتوفى','1','400',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (397,'سنة ٦٢٨/ ١٢٣١، انظر إنباه الرواة للقفطى ٤/ ٣٨ - ٣٩) ولم يكمله، انظر إرشاد الأريب لياقوت ٧/ ٢٩٢.
١٢ - «الجمع بين الصحاح ... والغريب المصنف» (انظر في ثانيهما ص ٨٢ آنفا) لأبى إسحاق إبراهيم بن قاسم البطليوسى (المتوفى نحو سنة ٦٤٢/ ١٢٤٤، انظر معجم المؤلفين لكحالة ١/ ٧٥)، وانظر بغية الوعاة للسيوطى ١٨٥، وراجع كشف الظنون ٦٠٠.
١٣ - «كتاب الإصلاح لما وقع من الخلل في كتاب الصحاح» لأبى الحسن على بن يوسف القفطى (المتوفى سنة ٦٤٦/ ١٢٤٨)، انظر إرشاد الأريب لياقوت ٥/ ٤٨٤، وراجع كشف الظنون ١٠٧٢.
١٤ - «نقود على الصحاح» لأبى العباس أحمد بن محمد بن أحمد بن الحاج الإشبيلى (المتوفى سنة ٦٤٧/ ١٢٤٩ أو ٦٥١/ ١٢٥٣)، انظر معجمالمؤلفين لكحالة ٢/ ٦٤، انظر بغية الوعاة للسيوطى ١٥٦، وراجع كشف الظنون ١٠٧٢.
١٥ - «التكملة والذيل والصلة» لرضى الدين أبى الفضائل الحسن بن محمد بن الحسن الصغانى (المتوفى سنة ٦٥٠/ ١٢٥٢)، وصفه حاجى خليفة بأنه «أوسع حجما منه (أى من الصحاح)» (كشف الظنون ١٠٧٢، راجع خزانة الأدب ١/ ١٦٠، ٤/ ٩١)،

<span data-type="title" id=toc-536>المخطوطات:</span>
سراى أحمد الثالث، ٢٧٠٥ (الجزء الأول، ١٧٣ ورقة، نسخت سنة ٧٥٤ هـ، انظر فهرس المخطوطات ١/ ٣٤٦)، كوغشلار ١١٨١ (الجزء الأول، ١٩١ ورقة، من القرن السابع للهجرة، انظر الفهرس نفسه)، أربع مخطوطات أخرى في سراى، انظر الفهرس ٤/ ١٦ - ١٧، كوبريلى ١٥٢٢ (الجزء الثانى من مخطوطة أحمد الثالث، ١٧٩ ورقة، نسخت سنة ٧٠٠ هـ)، و ١٥٢٤ (الجزء الثانى، ١٧٩ ورقة، من القرن الثامن للهجرة)، و','1','401',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (398,'١٥٢٥ (الأجزاء ٩ - ١١، ١٨٠ ورقة، من القرن السابع للهجرة، لعلها بخط المؤلف، انظر فهرس المخطوطات ١/ ٣٤٦)، مراد ملا ١٧٦٦ (٣١١ ورقة، نسخت سنة ٦٣٦ هـ، العنوان وإصلاحات على الهوامش بخط المؤلف، انظر فهرس المخطوطات ١/ ٣٥٥، راجع، O.Rescher (in: MFO ٥ /١٩١٢ /٥٣١ طرخان والدة السلطان ٣١٦ (٢٥٦ ورقة، نسخت سنة ٦٥٤ هـ)، بغداد، المتحف العراقى، مجموعة قاسم الرجب، وهى مصورة في ١٨ مجلد عن مخطوطة بغدادية تاريخها سنة ٦٤٢ هـ (انظر فهرس عوّاد ٢/ ١١، رقم ٢١٦)، القاهرة، دار الكتب، لغة ٣ (الجزء الأول، ٢٠٦ ورقة، نسخت سنة ٦٤١ هـ، الجزء الثانى، ٢٢١ ورقة، نسخت سنة ٦٤١ هـ، الجزء الثالث، ٥٣٤ ورقة، نسخت سنة ٦٤١ هـ، الجزء الرابع، ٢٤٢ ورقة، نسخت سنة ٦٤١ هـ، الجزء الخامس، ٢١٦ ورقة، نسخت سنة ٦٤١ هـ، الجزء السادس، ٢٢٧ ورقة، نسخت سنة ٦٤٢ هـ، انظر فهرس المخطوطات ١/ ٣٤٥ - ٣٤٦)، ولغة ٣٧ (جزء، غير كامل)، ولغة ٣٨٤ (الجزء الخامس، آخره ناقص، انظر الفهرس الثانى ٢/ ٨)، المدينة، عارف حكمت، لغة ٦ (٦٢١ ورقة، نسخت نحو سنة ٧٣٥ هـ، انظر المنتخب لكحالة ٤٦)، برلين ٦٩٣٩ (الجزء الأول، ٢٢٩ ورقة، نسخت نحو سنة ١٢٥٠ هـ)، و ٦٩٤٠ (الجزء الثانى، ٢/ ١٠٥ Glas.، الأوراق ٨ - ٣٠٦، نسخت نحو سنة ١٢٥٠ هـ)، و ٦٩٤١ (الجزء الثالث، ١٧٢ ورقة، نسخت سنة ١٢٥٨ هـ)، ودحداح ١٧٢ (الجزء الخامس، ١٥٨ ورقة، ناقصة)، المتحف البريطانى) ٣٤٤٦ Harl.، الجزء الأول، ٢٧٧ ورقة، من القرن التاسع للهجرة، انظر الفهرس ٢٢٧، رقم ٤٦٨)، طشقند ٢٢٥٦ (٣٠٠ ورقة، نسخت ١٠٥٥ هـ، انظر الفهرس، المجلد ٦، رقم ٤٤٠١).
ونشر المجلد الأول عبد العليم الطحاوى وع. حسن، القاهرة ١٩٧٠.','1','402',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (399,'ونشر المجلد الثانى إبراهيم الأبيارى ومحمد خلف الله أحمد، القاهرة ١٩٧١، ونشر المجلد الثالث محمد أبو الفضل إبراهيم ومحمد مهدى علام، القاهرة ١٩٧٣، ونشر المجلد الرابع عبد العليم الطحاوى وع. حسن، القاهرة ١٩٧٤، وانظر فى محتوى الكتاب: حسين نصار، المعجم العربى ٢/ ٥١٢ - ٥١٨.
١٦ - «كتاب ما أهمله الجوهرى من اللغة»، وهو كذلك للصغانى، ألفه بعد التكملة، على هامش مخطوطة القاهرة (انظر حسين نصار، المعجم ٢/ ٥١٨ - ٥١٩).
١٧ - «مجمع البحرين»، للصغانى أيضا، ألفه بعد الكتاب السابق الذكر، وهو خلاصة الصحاح والتكملة،

<span data-type="title" id=toc-537>المخطوطات:</span>
كوبريلى ١٥٧٠ (ستة مجلدات، ١٥٦٨ ورقة، نسخت سنة ٩٦٩ هـ) ومنها ميكروفلم بدار الكتب بالقاهرة، لغة ٦٤٥ (انظر الفهرس الثانى ٢/ ٢٤٩؛ فهرس المخطوطات ١/ ٣٧٠)، شهيد على ٢٦٧٣ (المجلد الأول، ١٤٩ ورقة، نسخت سنة ٨٧٣ هـ)، فيض الله ٢٠٩٢، باريس ٦٥١٥ (لعلها جزء، ٢٥١ ورقة، من القرن السابع/ للهجرة، انظر فجدا ٤٢)، ليننجراد، مكتبة الجامعة ٩٨ (عن بروكلمن، (Br.SI ,٦١٤ انظر في محتواه: حسين نصار، المعجم ٢/ ٥١٩ - ٥٢٠.
١٨ - «ترويح الأرواح في تهذيب الصحاح»، مختصر لأبى الثناء محمود بن أحمد بن محمود الزنجانى (المتوفى سنة ٦٥٦/ ١٢٥٨)، حجمه نحوالخمس من الصحاح، انظر كشف الظنون ١٠٧٣.
أ- «تنقيح الصحاح»، ويقال له أيضا «مختصر الصحاح»، وهو اختصار الكتاب السالف الذكر إلى قريب من النصف، وهو للمؤلف نفسه، راجع كشف الظنون ١٠٧٣.','1','403',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (400,'<span data-type="title" id=toc-538>المخطوطات:</span>
فاتح ٥٢٥٧ (١٦٧ ورقة، من القرن ١٢ هـ)، طهران، سبهسالار ٩٤ (١١٧ ورقة، نسخت سنة ١٢٨٧ هـ، انظر الفهرس ٢/ ١٦٦)، بغداد، متحف ١٢٧٤ (٢٣٦ ورقة، نسخت سنة ١٠٦٧ هـ، انظر فهرس النقشبندى ١٠٤ - ١٠٥، رقم ٣٧٦)، مكة، مكتبة الصبان الخاصة (وهى هنا بدون عنوان، من القرن التاسع للهجرة)، الفاتيكان ٥٦٥ (٢٢٥ ورقة، نسخت سنة ٨٩٤ هـ، انظر فيدا ١/ ٦٢)، باريس ٤٢٤٦ (١٤٣ ورقة، نسخت سنة ١٠١٠ هـ، راجع فجدا ٦٦٨)، برلين ٦٩٤٣ (١٦٧ ورقة، نسخت سنة ١٠٧٩ هـ)، برنستون، جاريت ١٣٠) L ٤٥ ورقة، نسخت سنة ١٠٥٧ هـ، انظر الفهرس، رقم ٢٦١)، جاريت (يهودا) ٣٣٠٤ (١٥٩ ورقة، من القرن ١٢ أو ١٣ هـ، انظر ماخ ٣٢١، رقم ٣٧٥٧)، نشره عبد السلام محمد هارون وأحمد عبد الغفور عطار، على مخطوطة مكة زاعمين انها مخطوطة فريدة، باسم «تهذيب الصحاح»، فى ثلاثة مجلدات، القاهرة ١٩٥٣، انظر في هذه النشرة عبد القادر المغربى في: مجلة المجمع العلمى العربى بدمشق ٢٨/ ١٩٥٣/ ٢٨٧ - ٢٨٩؛ جورج شحاته قنواتى في:
MIDEOI/ ١٩٥٤/ ١٠٧ - ١٠٨
، وانظر أيضا حسين نصار، المعجم ٢/ ٥٠٣ - ٥٠٤.
١٩ - «الصّراح من الصحاح»، مختصر مع ترجمته إلى الفارسية وتجريده من الشواهد لأبى الفضل محمد بن عمر بن خالد جمال (الدين) القرشىّ أو القرشىّ (ال) جمالى (فرع منه سنة ٦٨١/ ١٢٨٢، انظر كشف الظنون ١٠٧٧)، ويوجد مخطوطا في مكتبات كثيرة، من بينها: آيا صوفية ٤٦٩٩ (٢٩٩ ورقة، نسخت سنة ٩٢٢ هـ)، .','1','404',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (401,'سراى أحمد الثالث، ٢٦٩٢ (٤٤٩ ورقة، نسخت سنة ٩٥٧ هـ، انظر الفهرس ٤/ ١٤؛ فهرس المخطوطات ١/ ٣٥٩)، عاطف ٢٧٣١/ ١ (الأوراق ١ - ٢٨٠ ب، نسخت سنة ٩٢٥ هـ)، طهران، سبهسالار ١١١ (٣٤٢ ورقة، نسخت سنة ١٠٩٠ هـ)، و ١١٢ (٣٤٣ ورقة، من القرن ١٣ هـ)، و ١٢٨ (٤٣٥ ورقة، نسخت سنة ١٠٩٢ هـ)، و ١٣٠ (٥٠٣ ورقة، نسخت سنة ١٠٧٣ هـ، انظر الفهرس ٢/ ٢٠٠ - ٢٠٤)، مجلس ٦٧٧٣ (٢٦٦ ورقة، انظر الفهرس ٢/ ٢٧٥، رقم ٤٦٨، راجع مجلة معهد المخطوطات العربية ٦/ ١٩٦٠/ ٣٢٨)، ملك ٣٦١ (٣٢٢ ورقة، نسخت سنة ١٠٨٠ هـ)، و ٤٢٢ (٥٥٩ ورقة، من القرن ١١ هـ)، و ٤٢٤ (٣٧٦ ورقة، من القرن التاسع للهجرة، انظر الفهرس ١/ ٤٨٤ - ٤٨٥، مشهد، رضا ٣٦٦٥ و ٣٧٣٠ و ٦٨٨٤ و ٦٨٨٥ (فى مجلدين، انظر فهرست ... دو كتابخانه ٩٠٥)، مشهد، جوهرشاد ١٣٤، من القرن ١١ هـ، انظر فهرست ...
جهار كتابخانه ٣٤٤، عليكرة، الجامعة الإسلامية ٨٩٢٤٧٢٩/ ٨ و ٩ (٦٨٦ ورقة و ٤٢٦ ورقة، نقلت سنة ٧٠٠ هـ عن نسخة بخط مختصره ومترجمه، انظر الفهرس ص ١٣٥)، و ٨٩٢٤٧٢٩/ ١٣ (٤٥٤ ورقة، نسخة قديمة، انظر الفهرس ص ١٣٦)، آصفية: لغة ١٨ (٧٥٨ ورقة، نسخت سنة ٧٠٠ هـ، ويزعم انها بخط مختصر الكتاب ومترجمه)، ولغة ٢٣٣ (٤٨٥ ورقة، نسخت سنة ١٠٩٥ هـ)، ولغة ٢٦١ (٣٣٣ ورقة، نسخت سنة ١٠٨٣ هـ)، ولغة ٢٧٥ (٢٨٤ ورقة، انظر فهرست مشروح ١/ ٣٨٤)، الموصل، الأوقاف، مجموعة النبى شيت ١٠/ ٣ (٤٨٣ ورقة، انظر الفهرس ٢/ ١٧٠ - ١٧١)، والأحمدية (انظر فهرس جلبى ٣٨، رقم ١٢٢)، بيكنج، المسجد (انظر مجلة المجمع العلمى العربى بدمشق ١٥٣/ ١٩٧٨/ ٤٧٧ - ٤٧٨)،','1','405',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (402,'برلين ٦٩٤٧ (٢٨٤ ورقة، نسخت نحو سنة ١١٠٠ هـ)، ليدن) ١٠٠٧ Or.، مجلدان، ٢٨٨ و ٢٩٨ ورقة، نسخا سنة ٩٣١ هـ، انظر فورهوف ٣٤٧)، المتحف البريطانى، إضافات ٥٦٤٣ (٤٥٥ ورقة، نسخت سنة ١٧٧٩ للميلاد، انظر الفهرس ٤٦٧، رقم ١٠٠٧)، وإضافات ٧٤٣٥ (٤٣٨ ورقة، نسخت سنة ١١٢٢ هـ، انظر الفهرس ٤٦٧، رقم، Rieu ,Cat.Persian mss.٥٠٦ - ٥٠٧؛ ١٠٠٨ مدرسة الدراسات الشرقية والأفريقية ٤٤٥٧١ (٥١٤ ورقة، واحدة منأقدم مخطوطات الكتاب، نسخت سنة ٧٠٠ هـ، انظر فهرس Gacek رقم ٣٤٣)، ثمانى مخطوطات في المكتب الهندى (من القرن الحادى عشر وأحدث عهدا، انظر لوث رقم ١٠١٥ إلى ١٠٢٢)، أكسفورد ٣٥٦) ٤٧٤ Hunt.، ورقة، نسخت سنة ٩٥٦ هـ، انظر أودى ٢٣٧، رقم ١١١٥)، ليننجراد، المتحف الآسيوى، مجموعة بخارى ٩٦٤ (٦٣٦ ورقة، انظر بلياييف رقم ٥٤٦)، ييل ٣٠٧) ٢٩٠ - A، ورقة، من القرن ١٢ هـ، انظر نيموى رقم ١٨٦).
النشرات:
The Soorah, ed. by Durvesh Ulee, Jan Ulee, Meer Ghoolam Husunand Ubdoor- Ruheem, Calcutta, ٢ vols. ١٨١٢, edd., ٢ vols. ١٨١٥, edd ١٨٣٢, ebd (
) ,٢ Vols.١٨٩٨.؟ وطبع على الحجر في لكنو، فى مجلدين ١٢٨٩/ (مع مسرد للألفاظ بالفارسية، عنوانه «القراح بتكمّل الصحاح» لشرح الألفاظ الفارسية في «الصراح»)، وطبع مرة أخرى في لكنو ١٨٨٣، انظر أيضا سركيس ١٣٢٨، وطبع على الحجر في طهران (؟ ) ١٢٧٢، بولاق ١٢٨٧، ١٣٠١، ١٣٠٥ ومرارا أخرى.','1','406',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (403,'<span data-type="title" id=toc-539>ولمؤلف هذا الكتاب:</span>
«ملحقات الصراح» (انظر، . (Rieu ,Cat.Persianmss.٥٠٧ ولعل منه «رسالة المعربات من أعلام الملائكة والأجناس وأسماء المعارف والأجناس التى لم يذكرها صاحب الصحاح»، عاطف ٢٧٣١/ ٢ (٢٨١ أ- ٢٨٢ ب، نسخت سنة ٩٢٥ هـ)، انظر أيضا. Br.SI ,١٩٦
٢٠ - «حواش على الصحاح» لرضى الدين أبى عبد الله محمد بن على بن يوسف الشاطبى (المتوفى سنة ٦٨٤/ ١٢٨٥، انظر معجم المؤلفين لكحالة ١١/ ٧٢)، انظر بغية الوعاة للسيوطى ٨٣، راجع كشف الظنون ١٠٧٢، ٢١ - «مختار الصحاح» أو «مختصر الصحاح» لأبى عبد الله زين الدين محمد بن أبى بكر بن عبد القادر الحنفى الرازى (فرغ منه سنة ٦٦٠/ ١٢٦٢، انظر كشف الظنون ١٠٧٢ - ١٠٧٣)، وهو أكثر مختصراته شهرة وتداولا، مع إصلاحات وزيادات عن تهذيب اللغة للأزهرى ومصادر أخرى،

<span data-type="title" id=toc-540>المخطوطات:</span>
كوبريلى ١٥٩٢/ ٤، و ١١، ٣٢٥ (٢٦٠ ورقة)، عاطف ٢٧٥٥، ٢٧٥٦ (٢٧٦ ورقة، نسخت سنة ٩٤٥ هـ)، مراد ملا ١٧٨٨ (راجع، (J.Kraemerin: Oriens ٦ /١٩٥٣ /٢١٧ و ١٨١٤ (٢٨٣ ورقة)، و ١٨١٥، ١٨١٦ (نسخت سنة ٦٩٠ هـ)، و ١٨١٧ (نسخت سنة ٩٣٤ هـ)، مكتبة جامعة استنبول، ٩٠٦ A.Y.، و ٣١٣) ٦١٢٢ A.Y. ورقة، نسخت سنة ٩٨٩ هـ)، ولى الدين ٣١٥٤، وتوجد خمس مخطوطات في سراى (انظر الفهرس ٤/ ١٤ - ١٥)، شهيد على ٢٦٨٤، رئيس الكتاب ١١١٦ (نسخت سنة ٩٢٠ هـ)، ١١١٧، فيض الله ٢٠٩٦ (نسخت سنة ٩٣٢ هـ)، و ٢٠٩٧، جار الله ٢٠٤٢،','1','407',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (404,'حكيم أوغلو ٩٢٦ (نسخت سنة ٩٦٢ هـ)، أيا صوفية ٤٧٦١، ٤٧٦٣، ٤٧٦٤، نور عثمانية ٤٨٦٢ - ٤٨٦٦، فاتح ٥٢٥٢/ ١ - ٥٢٥٦، أسعد ٣٢٧١ (نسخت سنة ٨٩١ هـ)، و ٣٢٧٢، لا له إسماعيل ٦٧١/ ١، لاله لي ٣٦٢٠ - ٣٦٢٤، ينى جامع ١١٦٤، السليمانية ١٠١٧، ١٠١٨/ ١، حاجى محمود ٥٤١٢، ٥٤٢٢ (نسخت سنة ٧٤٧ هـ)، و ٥٤٥١، دار المثنوى ٥٨٥، داماد إبراهيم ١١٤١ (نسخت سنة ٩٤١ هـ)، الحميدية ١٤٣٠ (نسخت سنة ٩٧٢ هـ)، و ١٤٣١، جور ليلى على ٤٤١ (نسخت سنة ٩٢٠ هـ)، حالة ٥٦٤/ ١، قاضى زاده ٥٤٥ (نسخت سنة ٩٤٧ هـ)، بغداد المتحف العراقى ١٢٩٨ (٢٣٤ ورقة، نقلت سنة ١٠٠٨ هـ عن نسخة بخط مؤلفه، انظر فهرس النقشبندى ١٠٣ - ١٠٤، رقم ٣٧٠)، و ٨٦٠٢، سابقا مجموعة الآلوسى (٢٣٩ ورقة، انظر أسامة ناصر النقشبندى في: المورد ١٤/ ١٩٧٥/ ١٩٥، رقم ٢٦٢)، وفي المتحف العراقى خمس مخطوطات أخرى (انظر فهرس النقشبندى، ١٠٤، رقم ٣٧١ - ٣٧٥)، ثلاث مخطوطات في مكتبة الأوقاف ببغداد (انظر فهرس الجبورى ٣/ ٢٥٢، رقم ٥٢٤٩ - ٥٢٥١)، النجف، الخزانة الغروية ٧٨٢ (٣١٤ ورقة، نسخت سنة ٩٨٨ هـ، راجع مجلة معهد المخطوطات ٢٠/ ١٩٧٤/ ٤٥، رقم ٢٤٩)، الموصل، الأحمدية (٣١٠ ورقة، نسخت سنة ٩٦٧ هـ، انظر فهرس جلبى ٣٩، رقم ٢٢٤؛ فهرس الأوقاف ٥/ ٢٣٠)،','1','408',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (405,'الظاهرية، عام ١٥٤٧ (٢٦٢ ورقة، نسخت سنة ١٠٨٨ هـ)، وعام ١٥٤٨ (٤٥٥ ورقة، نسخت سنة ٩٩٩ هـ)، وعام ١٥٦٠ (جزء، ٥٨ ورقة، نسخت قبلسنة ١٠٥٥ هـ)، عام ٦٠٢٢ (٢٨٣ ورقة، نسخت سنة ٩٥٠ هـ)، عام ٦٢٦٩ (١٢٢ ورقة نسخت سنة ١٠٨٥ هـ)، عام ٦٩٨٩ (٢٦٦ ورقة، نسخت سنة ٩٥٤ هـ)، عام ٧١٢٤ (٢٢٤ ورقة)، عام ٧٦٤٥ (٢٧٨ ورقة، نسخت سنة ٩١٠ هـ)، عام ٧٨٥٩ (٢٤٧ ورقة، انظر فهرس اللغة ٣٦ - ٤٢)، المدينة، عارف حكمت، لغة ٨٥ (٢٢٤ ورقة، نسخت سنة ٩٦٦ هـ، انظر المنتخب لكحالة ٥٠)، القاهرة، دار الكتب، قوله ٦ (١٥٥ ورقة، نسخت سنة ٩٠٨ هـ)، و ١٠ (١٣١ ورقة، نسخت سنة ١٠٣٦ هـ)، و ١١ (٢٧١ ورقة، نسخت سنة ٩٨٣ هـ، انظر الفهرس ٢/ ٨)، الاسكندرية، البلدية ٢٢٧٣ ب (٩٧٣ هـ، انظر الفهرس (١) اللغة ٦٧)، تونس، دار الكتب الوطنية ٣٩١٣) ٢٩٢ ورقة، نسخت سنة ١٠٨٢ هـ)، و ٧٦٦٥ (٢٠٥ ورقة، انظر فهرس منصور ١٥٣)، فاس، القرويين (٨٠٨ هـ، انظر بيل رقم ١٢٦٥)، الفاتيكان ٥٣٥ (٣٤٠ ورقة، من القرن العاشر للهجرة، انظر فيدا ١/ ٥٩)، ميلانو، أمبروزيانا ٢) A.F.١٣٢ /sup./٢، ب- ٢٠٣، انظر فهرس لوفجرن/ ترينى ١/ ١١٩، رقم ٢٢٤)، باريس ٤٢٤١ (٢٩٦ ورقة، نسخت سنة ٩٢٠ هـ)، و ٤٢٤٢ (٢٨٤ ورقة، نسخت سنة ٩٥٨ هـ)، و ٤٢٤٣ (٢٩٨ ورقة، نسخت سنة ٩٥٨ هـ)، و ٤٢٤٤ (٢٢٣ ورقة، نسخت سنة ٩٧٣ هـ)، و ٤٢٤٥ (٢٢٨ ورقة، من القرن ١٢ هـ)، و ٦٦٩٦ (٣٩٠ ورقة، نسخت سنة ١١٥٧ هـ، راجع فجدا ٦٤٤)، ميونخ ٧٧٩ (٢٦١ ورقة، نسخت سنة ١١٢٦ هـ)، ليبتسج/ ٤٥٥ (١٢٦ ورقة، نسخت سنة ٩٨١ هـ)، برلين ٦٩٤٤ (١٣٣ ورقة، نسخت نحو سنة ١١٠٠ هـ) و ١٠٥، ٦٦٦ Lbg.) ١ /٦٩٤٥ ورقة، نسخت سنة ٩١١ هـ)، و، ٩٦٦ Lbg.) ٢ /٦٩٤٥ نسخت سنة ٩٤٣ هـ)،','1','409',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (406,'و ٢٤٤) ٣٩٧٣ or.Oct. ورقة، نسخت سنة ١٠٣١ هـ، انظر فاغنر رقم ٣٠٣)، المتحف البريطانى ٢٢٠) ٥٥٢٤ Har L.، ورقة، نسخت سنة ٩٤٩ هـ، انظر الفهرس ٢٢٧، رقم ٤٦٩)، وإضافات ٧٦٣٢ (٣٦٢ ورقة، من القرن ١٢ هـ، انظر الفهرس ٢٢٨، رقم ٤٧٠)، وإضافات ٢٣٤٢٤ (٣٣٣ ورقة، نسخت سنة ١٢٠١ هـ، انظر الفهرس ٦٣٩، رقم ١٣٧٨)، و ٤١٨٤ (٣٤٧ ورقة، نسخت سنة ١٠٣٨ هـ، انظر الملحق ٥٧٨، رقم ٨٥٠)، اكسفورد، بودليانا ٢٠٠) ٢٠٣ Marsh.، ورقة، ناقصة، نسخت سنة ١٠٩٠ هـ، انظر أورى ٢٣٢، رقم ١٠٨٠)، و) ١/ ٢٠٤ Hunt. الأوراق ١ - ٢١٦، نسخت سنة ٩٥٦ هـ، انظر أورى ٢٣٨، رقم ١١٢٥)، مانشستر ٧٥٤/ ١ (الأوراق ١ - ٢٢٠ أ، نسخت سنة ٩٠١ هـ)، جامعة جلاسجو) Hunt.، نسخت سنة ١٠٥١ هـ، انظر (JRAS ١٨٩٩ ,P.٧٥٢ , (No ,٤٩ أو بسالا ٢٤١) ٣١ St.، ورقة، نسخت سنة ٩٥٧ هـ، انظر تورنبرج رقم ٩)، برنستون، جاريت، ١٦٣ ب (٢١١ ورقة، من القرن التاسع للهجرة، انظر الفهرس رقم ٢٦٢)، ٣٩٧ ب (٢٠٨ ورقة، نسخت سنة ٩١٤ هـ، انظر الفهرس رقم ٢٦٣)، وثمة ست مخطوطات في جاريت (يهودا)، أقدمها من القرن الثامن للهجرة (انظر ماخ ٣٢١، رقم ٣٧٥٦)، وإحدى عشرة مخطوطة في صوفيا (انظر فهرس درويش ٢/ ١٨ - ٢١)، وطبع طبعات عديدة ببولاق أو القاهرة بين سنة ١٢٨٧ و ١٩٣٧ (انظر سركيس، (F.E.Karatay , ... Arapcabasmalar ... Katalogu ٣٧٢؛ ٩١٨ وبترتيب عصرى على حروف المعجم لمحمد خاطر، القاهرة ١٣٢٥ ومرارا عديدة (انظر حسين نصار، المعجم ٢/ ٥٠٦ - ٥٠٧)، وطبع أيضا في دمشق ١٣١٦، وبيروت ١٩٨٠.
وهناك كتاب أسمه «ملتقط صحاح الجوهرى وملحق بمختار الصحاح» ليير محمد','1','410',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (407,'ابن يوسف الأنقروىّ (المتوفى سنة ٨٦٦/ ١٤٦٢، انظر؛ Osm.muell.I ,٢٦٠ ورقم ٢٨)، مذكور في كشف الظنون ١٠٧٣،

<span data-type="title" id=toc-541>المخطوطات:</span>
نور عثمانية ٤٨٨٢ (٥١٢ ورقة، بخط مصنفة)، ينى جامع ١١٧٥ (٤٦٢ ورقة، نسخت سنة ٩٦٢ هـ)، شهيد على ٢٦٩٦ (٣٢٨ ورقة، نسخت سنة ٩٦٥ هـ)، عاطف ٢٧٦٩ (٤٥٥ ورقة، نسخت سنة ٩٤٤ هـ).
وكذلك «مختار الصحاح» لداود بن محمد القارمى الحنفى (كان لا يزال حيا سنة ١١٦٩/ ١٧٥٦، انظر معجم المؤلفين لكحالة ٤/ ١٤٢)، المتحف البريطانى ١٦٦) ٤١٨٥ Or.، ورقة، من القرن ١٢ هـ، انظر الملحق ٥٧٩، رقم ٨٥٢). وكذلك «صفو الرّاح من مختار الصحاح»، لعبد الرحمن بن عيسى الهمذانى (وينبغى عدم الخلط بينه وبين سميّه المذكور ص ١٩٣ آنفا)، دار الكتب بالقاهرة، لغة ٣١ (انظر الفهرس الثانى ٢/ ٢٠)، انظر في محتواه: حسين نصار، المعجم ٢/ ٥٠٧ - ٥٠٨.
٢٢ - «إعلام كلمة الصحاح وأعلام ألسنتها الفصاح» لشمس الدين أبي عبد الله محمد بن (ال) حسن بن سباع بن الصائغ الدمشقى الجذامى (المتوفى نحو سنة ٧٢٠/ ١٣٢٠)، ويوجد مخطوطا في ييل ٨٥) ٢٩١ - A، ورقة، نسخت قبل سنة ١١٢٢ هـ، انظر نيموى رقم ١٧٥)، ٢٣ - «الجامع في اختصارالصحاح»، مختصر «مجرد عن الشواهد» (كشف الظنون ١٠٧٢) للمصنف السابق، ويوجد مخطوطا في عارف حكمت بالمدينة، لغة ١٠ (٣٩٠ ورقة، نسخت سنة ٩٤٨ هـ، انظر المنتخب لكحالة ٤٦).
٢٤ - «غوامض الصحاح» لصلاح الدين خليل بن أيبك الصفدى (المتوفى سنة ٧٦٤/ ١٣٦٣) جوروم ١٩٠٥ (٧٣ ورقة، نسخت سنة ٧٥٧ هـ، بخط مؤلفه، انظر أحمد آتش في، (Isl.Ilim ,Enst.Derg.١ /١٩٥٩ /٦١ - ٦٢ الإسكوريال ١٩٢ (١١٩ ورقة، نسخت سنة ٧٥٧، وهى بخط المؤلف أيضا).','1','411',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (408,'٢٥ - «نفوذ السهم فيما وقع للجوهرى من الوهم» للمؤلف السالف الذكر، فرغ منه سنة ٧٥٧/ ١٥٥٦، ومنه ١٤ نقلا في خزانة الأدب (انظر إقليد الخزانة للميمنى ٧١ - ٧٢)، ونقل أوله في كشف الظنون ١٠٧٣، راجع حسين نصار، المعجم ٢/ ٥٢٦ - ٥٢٨، ويوجد مخطوطا في شهيد على ٢٧٠١ (٩٥ ورقة، من القرن ١٢ هـ، نقلت عن نسخة بخط المؤلف تاريخها سنة ٧٥٧ هـ، انظر» -٢٦ (Sesen ,Nawadir II ,١٦٥ - ١٦٦ حلى النواهد على ما في الصحاح من الشواهد»، وهو أيضا للصفدى، ألفه قبل الكتاب الآنف الذكر (كشف الظنون ١٠٧٣)، ولعله محفوظ باسم «مجموع شواهد الصحاح» لمجهول منه نسخة في برلين ٣، ٢/ ١٨٣٥ - We.) ٦٩٤٨ ب- ١٠ أ، نسخت نحو سنة ١٠٩٠ هـ)./ ٢٧ - «اعتراضات على مواضع من صحاح الجوهرى» لأبى الطاهر محمد بن يعقوب الفيروزابادى (المتوفى سنة ٨١٧/ ١٤١٤)، ويوجد مخطوطا في كوبريلى ١٥٧١ (الأوراق ١ - ٣٣)، وشهيد على ٣١٣/ ٥ (الأوراق ٤١ - ٥٣). وقد انتقد الفيروزابادى الجوهرىّ انتقادا شديدا في مقدمة قاموسه.
٢٨ - «ترجمان الصحاح» ترجمة إلى اللغة التركية لپير محمد بن يوسف الانقروى (المتوفى سنة ٨٦٦/ ١٤٦٢، انظر، (Osm.muell.I ,٢٦٠ ترجمه بعد فراغه من وضع «ملتقط الصحاح» (انظر رقم ٢١ أآنفا)، انظر كشف الظنون ١٠٧٣، لعله قد بقى في إحدى الترجمات التركية المنقولة غفلا من اسم مترجمها وتنسب عادة إلى وان قولى (انظر رقم ٣٣ بعد) ٢٩ - «جامع اللغة» ل (السيد) محمد بن السيد حسن بن السيد على (المتوفى نحو سنة ٨٦٦/ ١٤٦٢)، ويتضمن إصلاح ما وقع من الخلل في الصحاح وزيادات عليه،

<span data-type="title" id=toc-542>المخطوطات:</span>
سراى أحمد الثالث، ٢٧٣٣ (٤٤٣ ورقة، نسخت سنة ٨٧٢ هـ)، و ٢٧٦٢','1','412',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (409,'(٤٩٩ ورقة، نسخت سنة ٩٧٨ هـ، انظر الفهرس ٤/ ٤٠، وراجع فهرس<span data-type="title" id=toc-543> المخطوطات </span>١/ ٣٥١، وهنا يسمى أيضا «الراموز»، انظر بعد)، المتحف البريطانى ٤٢٢) ٤١٩٤ Or.، ورقة، نسخت سنة ٩٣٩ هـ، انظر الملحق ٥٧٩، رقم ٨٥١).
أ) «الراموز في اللغة»، وهو تنقيح جديد للكتاب الآنف الذكر صنعه المؤلف نفسه،
المخطوطات:
ينى جامع ١١٢٦ (٥٦٨ ورقة، نسخت سنة ٩٧٢ هـ)، و ١١٢٧ (٣٦٣ ورقة، نسخت سنة ٩٨٨ هـ)، ومنها مصورة بدار الكتب بالقاهرة، لغة ٦٠٠ (انظر الفهرس الثانى ٢/ ١٤)، عارف حكمت بالمدينة، لغة ٢٠ (٤٧٣ ورقة، نسخت سنة ٩٦١ هـ، انظر المنتخب لكحالة ٤٧)، دار الكتب الوطنية بتونس، الأحمدية ٣٩٣٧ (٥٥٩ ورقة، نسخت سنة ٩٥٩ هـ، انظر فهرس منصور ١٤٠).
٣٠ - «مختصر» لمحمد بن أحمد بن نجم الدين بن جمال الدين الحنفى، أكسفورد، بودليانا ٤١١) ٤٤، ٣١٧٧ Seld.، ورقة، نسخة سنة ٨٧٥ هـ، انظر أورى ٢٨٨، رقم ١٠٥٥).
٣١ - «فلق الصباح (أو الإصباح) فى تخريج أحاديث الصحاح»، مختصر لجلال الدين عبد الرحمن بن أبى بكر السيوطى (المتوفى سنة ٩١١/ ١٥٠٥)، انظر كشف الظنون ١٠٧٣، ١٢٩٠.
٣٢ - «الفصاح في علم اللغة على ترتيب الصحاح (بل مختصر منه)» لبدر الدين على بن محمد بن محمد العلى آبادى، فرغ منه سنة ٩٥٢/ ١٥٤٥، انظر هدية العارفين لإسماعيل البغدادى ١/ ٧٤٥)، الإسكوريال ٥٨٦ (٢٤٩ ورقة).
٣٣ - «ترجمان الصحاح» لمحمد بن مصطفى الوانى، المعروف بوان قولى (المتوفى سنة ١٠٠٠/ ١٥٩١، انظر، (Osm.muell.II ,٤٨ تهذيب تركى، وفي غالبه ترجمة «مختار الصحاح» لمحمد بن أبى بكر الرازى (انظر رقم ٢١ آنفا) مع زيادات عن يير','1','413',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (410,'محمد بن يوسف الأنقورى (انظر رقم ٢١ أآنفا). ونقلت بعض<span data-type="title" id=toc-544> المخطوطات </span>غفلا من اسم المترجم؛ ولعل إحداها تتضمن الترجمة المفقودة حتى الآن التى صنعها يير محمد الأنقروى، فيض الله ٢٠٥٧ (٢٥٦ ورقة، نسخت سنة ٩٦٧ هـ) ليدن) ٧١٦ Warn.، نسخت سنة ٩٦٩ هـ، انظر دى خويه ١/ ٧٣، رقم ١١٩)، هيدلبرج ٧٨٩) ١٦٤ A /T ورقة، نسخت سنة ١٠٠٤ هـ)، انظر J.Berenbach in: Zs ٦ /١٩٢٨ /٢٢٠ دريسدن ٣٦٤ (قطعة، ١٥ ورقة)، و ٣٦٥ (٢٨٩ ورقة، نسخت سنة ١٠٩٤ هـ).
المتحف البريطانى، إضافات ٧٤٣٦ (٣٠٨ ورقة، نسخت سنة ٩٧٣ هـ، انظر الفهرس ٤٦٧ - ٤٦٨، رقم ١٠٠٩)، وإضافات ٢٣٥٩٢ (٣٦٨ ورقة، نسخت سنة ٩٨٩ هـ، انظر الفهرس ٦٣٩ - ٦٤٠، رقم ١٣٧٩)، وإضافات ٢٣٥٩٣ (٢٦٨ ورقة، نسخت سنة ١١٢٨ هـ، انظر الفهرس ٦٤٠، رقم ١٣٨٠)، اكسفورد، بودليانا ٢٢٥) ٤٨ Pock.، ورقة، نسخت سنة ٩٣٦ هـ، انظر أورى ٢٤٣، رقم ١١٥٥)، وطبع باسم «كتاب لغات وان قولى» فى استنبول ١١٤١، ١١٧٠، سكوترى ١٨٠٣، راجع إ. جولد تسيهر، المرجع المذكور، ص» -٣٤ J.kraemerin: Oriens ٦ /١٩٥٣ /٢١٧؛ ١١٣ بهجة النفوس في المحاكمة بين الصحاح والقاموس» لبدر الدين محمد بن يحيى بن عمر القرافى المصرى (المتوفى/ سنة ١٠٠٨/ ١٦٠٠)،
المخطوطات:
كوبريلى ١١، ٣١٢ (١٩١ ورقة)، دار الكتب الوطنية بتونس، الأحمدية ٣٩٣٤ (١٨٩ ورقة، نسخت سنة ١١٩١ هـ، انظر فهرس منصور ١٥٠).','1','414',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (411,'٣٥ - «اختصار» للمولى محمد بن مصطفى التيروى العيشى (المتوفى سنة ١٠١٦/ ١٦٠٧، انظر معجم المؤلفين لكحالة ٩/ ١٤٣، ١٢/ ٢٥). وزعم المؤلف أن مختصره خير من مختار الصحاح لمحمد بن أبى بكر الرازى، «لكنه غير مشهور» (كشف الظنون ١٠٧٣).
٣٦ - «كتاب الوشاح وتثقيف الرماح في رد توهيم المجد الصحاح (أو للصحاح)» لأبى زيد عبد الرحمن بن عبد العزيز المغربى التّادلى، وهو في رد انتقاد الفيروزابادى للجوهرى.
نشره نصر الهورينى، بولاق ١٢٨١، وطبع على هامش الصحاح للجوهرى، بولاق ١٢٩٢، وطبع أيضا في القاهرة ١٣٠٥، انظر أيضا. Br.SI ,١٩٧
٣٧ - «مختصر» لأبى الكرم عبد الرحيم بن أبى المناقب عبد الله بن أبى المكارم شاكر بن أبى المطهّر حامد المعدانى.

<span data-type="title" id=toc-545>المخطوطات:</span>
باريس ١٩٢ (انظر G.Flugelin: Wiener Jahrb.٩٢ ,Anz.-Bl.S.٣٤ عن، (Br.GI ,١٢٨ المكتب الهندى بلندن ١٧٩٣/ ٤ (الأوراق ١٣٢ - ٤٣٧، نسخت قبل ١١٨٠ هـ، انظر لوث رقم ١٠٢٧).
٣٨ - «كتاب نجد الفلاح في مختصر الصحاح»، مختصر لمجهول مجرد من الشواهد، انظر كشف الظنون ١٠٧٣، ١٩٣٠.
٣٩ - شرح بدون عنوان ومجهول المؤلف، موجود في ييل ٤١٥) ٥ - S، ورقة، من القرن ١٢ هـ، انظر نيموى رقم ١٨٧).
٤٠ - مختصرات لمؤلفين مجهولين، لعل بعضا منها هو مختار الصحاح لمحمد بن أبى الرازي:
طهران، مكتبة الخنجى الخاصة (٩٦٣ هـ، انظر مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٦٣، رقم ٥٦)، جامعة الحكمة ببغداد، مجموعة سركيس (١٣٣ ورقة، نسخت سنة ٩٧٥ هـ، انظر الفهرس ٦٤، رقم ١٠٦)،','1','415',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (412,'دار الكتب بطنطا (نسخت سنة ٩٨٥ هـ، انظر مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٢٥٧، رقم ١٩٥)، اكسفورد، بودليانا ١٥٨) ٣٨٨ Marsh.، ورقة، انظر أورى ٢٣٨، رقم ١١٢٦)، مختصر مع نقول كثيرة عن «القاموس» و «المغرب» فى برلين ٦٩٤٦ (٢٣٩ ورقة، نسخت نحو ١١٠٠ هـ).

<span data-type="title" id=toc-546>وللجوهرى في النحو:</span>
«مقدمة فى النحو»، ذكرها ياقوت في إرشاد الأريب ٢/ ٢٦٨، راجع بغية الوعاة للسيوطى ١٩٥.
وكانت نسخة منه في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في:
. (MIE ٤٩/ ١٩٤٦/ ٤٦, No. ٨٣٢
وقد نظم الجوهرى الشعر أيضا إلا أنه كشعر العلماء، لا شعر كبار الشعراء؛ انظر فى أبيات له:
مخطوط برلين ٣٧٦، ٦٢٥ Pm.-) ٢ /٧٥٨٩ ب)؛ يتيمة الدهر للثعالبى ٤/ ٤٠٧؛ دمية القصر للباخرزى ٢، النجف ١٩٧١، ص ٤٩٠ - ٤٩١؛ [إرشاد الأريب لياقوت ٢/ ٢٧٠.

<span data-type="title" id=toc-547>الهروى</span>
هو أبو عبيد احمد بن محمد بن عبد الرحمن الهروى الباشانى، كان تلميذا للأزهرى، وأبى سليمان الخطابى وغيرهما. وممن رووا عنه أبو عمر عبد الواحد بن أحمد المليحى، وأبو عثمان اسماعيل بن عبد الرحمن الصابونى. توفى سنة ٤٠١/ ١٠١١.

<span data-type="title" id=toc-548>مصادر ترجمته:</span>
ابن خلكان ١/ ٣٤؛ إرشاد الأريب لياقوت ٢/ ٨٦ - ٨٧؛ إنباه الرواة للقفطى ٤/ ١٤٤ - ١٤٥؛ / طبقات الشافعية للسبكى ٣/ ٣٤؛ الوافى بالوفيات للصفدى','1','416',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (413,'٨/ ١١٤ - ١١٥؛ مسالك الأبصار لابن فضل الله ٤/ ٢٥٠ - ٢٥١؛ ابن قاضى شهبة، المخطوط ص ٢٠١؛ بغية الوعاة للسيوطى ١٦١.-
Br. GI, ١٣١, SI, ٢٠٠; J. Kraemerin: Oriens ٦/ ١٩٥٣/ ٢١٧ - ٢١٨;
الأعلام للزركلى ١/ ٢٠٣؛ معجم المؤلفين لكحالة ٢/ ١٥٠

<span data-type="title" id=toc-549>آثاره:</span>
«كتاب الغريبين في القرآن والحديث»، «كتاب نافع» (ابن خلكان ١/ ٣٤)، لم يلبث أن اشتهر حتى «سار مسير الشمس في الآفاق» (إنباه الرواة للقفطى ٤/ ١٤٤) وقيل إن الهروى كان أول من جمع بين غريب القرآن وغريب الحديث في كتابه هذا الذى بقى بترتيبه على حروف المعجم مثالا يحتذى حتى ظهور كتاب «النهاية» لابن الأثير. وقد كثر النقل هنه في الكتب (انظر أيضا فهرست ابن خير ٦٩؛ بولس سباط فى. (MIE ٤٩ /١٩٤٦ /٣٧ ,No.٦٧٠

<span data-type="title" id=toc-550>المخطوطات:</span>
سراى أحمد الثالث، ١٠٥ (جزء، ٢٦٠ ورقة، نسخت سنة ٥٤٩ هـ)، و ٢٧٧١ (الجزءان ٢ و ٣، من القرن السابع للهجرة)، و ٢٧٧٢ (الجزء الثانى، ٢٦٣ ورقة، من القرن السابع للهجرة)، سراى، مدينة ١٨٩ (من أوله إلى حرف الشين، ٢١٨ ورقة، نسخت سنة ٦٥٥ هـ، انظر فهرست المخطوطات ١/ ٨٩، راجع الفهرس ١/ ٥٩١، رقم ٢١٦٦)، أيا صوفية ٨٧٠ (الجزء الأول، ٢٩٨ ورقة، نسخت نحو سنة ٦٠٠ هـ، انظر، (M.Plessnerin: Islamica ٤ /١٩٣١ /٥٣٢ نور عثمانية ٥٩١ (٢١٠ ورقة، نسخت سنة ١١١٨ هـ)، و ١٩٦٨ (٢٦٨ ورقة، نسخت سنة ٦٩٧ هـ)، كوبريلى ٢٦٥ (٣٣٩ ورقة، نسخت سنة ٨٣٢ هـ)، ٣٧٥ (الجزء الرابع، ١٧٩ ورقة، نسخت سنة ٧٠٠ هـ)، ١٣٧٦ (الجزء الثانى، ٢٢٣ ورقة، نسخت سنة','1','417',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (414,'٦٩٩ هـ)، ٣٧٧ (الجزء الثانى، ٢٤٧ ورقة، نسخت سنة ٥٨٠ هـ)، ٣٧٩ (الجزء الثالث، ١٧٥ ورقة)، مكتبة جامعة استنبول) ٧١١ A.Y.، جزء، نسخ سنة ٥٤٧)، مراد ملا ٥٧٠ (٢٧٠ ورقة، نسخت سنة ٦٥٤ هـ)، عاطف ٢٧٣٤ (٣٢٧ ورقة، نسخت سنة ٥٨٠ هـ)، أسعد ١١٠ (نسخت سنة ٦٠٥ هـ)، شهيد على ١٣١ (نسخت سنة ٥٤٧ هـ)، ١٣٢ (نسخت سنة ١٠٣٩ هـ)، فاتح ٢٩٠ (الجزء الأول، ٢٦٤ ورقة، نسخت سنة ٦٠٣ هـ)، ٢٩١ (الجزء الأول، ٢٢٩ ورقة، من القرن السادس للهجرة)، ٢٩٢ (الجزء الثانى، ٣٤٩ ورقة، من القرن السادس للهجرة)، لاله لي ١٩١ (الجزء الثالث، ٢٣٨ ورقة)، راغب ١٤٢٢ (٤٥٧ ورقة)، قونية، يوسف أغا ٨٢٩١/ ٤٣٦٢ (كاملة في جزءين، ٦٧٣ ورقة، نسخت سنة ٥٥٤ هـ، انظر، H.Ritterin: Oriens ٢ /١٩٤٩ /٢٣٨ وراجع، (stirc- sunamsedetsiL ... ١٥ و ٤٦٩١/ ٣٩٧١ (الجزء الثانى، ١٤١ ورقة، نسخت سنة ٥٠٨ هـ، انظر، ٠٢ ... stircsunamsedets iL (و ٤٧٥٦ (الجزء الأول، ٢٣٥ ورقة، نسخت سنة. ٥٠٨ هـ)، و ٤٧٥٧ (الجزء الثانى، ٢٣٣ ورقة، نسخت سنة ٤٩٩ هـ)، و ٦٨٢ (الجزء الثانى، ١٤٥ ورقة، نسخت سنة ٦٧٤ هـ، انظر، (٠٢ ... stirc- sunamsedetsiL جوروم ١٨٣ (الجزء الثانى، ٢١٣ ورقة، نسخت سنة ٥٨٦ هـ)، بورسه، حراتشى أوغلو، لغة ٢١ و ٢٢ (الجزء الأول حتى الشين والجزء الثانى، ٣١١ ورقة و ٤٦٧ ورقة، من القرن ٦ أو ٧ هـ. انظر هـ. ريتر، فى الموضع المذكور)، ولغة ٣٥ (من الحاء إلى آخر الكتاب، ٢١٧ ورقة، نسخت سنة ٦٠٥ هـ، انظر هـ. ريتر، فى الموضع المذكور)، بورسة، حسين جلبى ٥٨ (الجزء الأول، ١٩٦ ورقة، نسخت سنة ٧٤٦ هـ)، اسبرطة، خليل حامد باشا ١٤٥١ (جزآن، نسخا سنة ٤٨١ هـ و ٥٦٣ هـ)،','1','418',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (415,'طهران، ملى، بدون رقم (نسخت سنة ١٢٧١ هـ، انظر مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٢١، رقم ٣٧)، طشقند ٣١٩٤ (انظر مجلة معهد المخطوطات ٦/ ١٩٦٠/ ٣٢٤)، موسكو، الكرملين ٤٩٢ (انظر كوركيس عواد في: سومر ٧/ ١٩٥١/ ٢٨٢)، بنكيبور ١٩٦٨ (٢٦٨ ورقة، نسخت سنة ٦٩٧ هـ، انظر الفهرس ٢٠/ ٨)، وله مخطوطات أخرى في الهند انظر بروكلمن، الموصل، الحسينية (مجلدان نقلا عن نسخة تاريخها سنة ٤٨٢ هـ أو ٥٦٣ هـ، ٣٠١ ورقة و ١٩٩ ورقة، نسخا سنة ١١٥٦ هـ، انظر فهرس الأوقاف ١/ ٥٣ - ٥٤، راجع جلبى ١٢٥، رقم ٦٥) وجامع الباشا (الجزء الثانى، نسخ سنة ٧١١ هـ، انظر جلبى ٥٤، رقم ١١٥)، دمشق، الظاهرية، عام ١٥٨٨ (الجزء الثانى، ١٦٧ ورقة)، وعام ٥٩٩ (الجزء الثالث، ١٨٩ ورقة، من القرن السادس للهجرة، انظر فهرس اللغة ١١٣ - ١١٧)، دار الكتب بالقاهرة، تفسير ١٦٧ (٢٤٧ ورقة، نسخت سنة ٥٢٦ هـ، انظر الفهرس الأول ١/ ١٨٤)، وتفسير ١٠١٦ (الجزء الثانى، ١٩٠ ورقة، من القرن الرابع للهجرة، ويحتمل انها بخط المؤلف، انظر فهرس المخطوطات ١/ ٨٩)، التيمورية، لغة ٥٤ (٢٣٣ ورقة، نسخت سنة ٥٧٦ هـ)، ولغة ٥٥ (٣ مجلدات، ٢٤٠ ورقة، و ٢٢٨ ورقة، و ١٩٥ ورقة، نسخت سنة ٦١٩ هـ، انظر فهرس المخطوطات ١/ ٨٩)، صنعاء، المتوكلية، تفسير ١٣٣ (الجزء الأول، ٢٠٨ ورقة)، ومنها مصورة بمعهد المخطوطات بالقاهرة، البلدية بالاسكندرية ٣٠١٠ ج (مجلدان، انظر فهرس اللغة ٦٥؛ فهرس التفسير ٢٢)، الرباط، مجموعة الكتانى ١٤٣ (٢١٠ ورقة، من القرن السابع للهجرة)، فاس، القرويين (فهرس بيل رقم ٢٢١)، مراكش، / ابن يوسف ١٠٣، آيت عياش، الحمزاوية ٣٧٤، لعلها اليوم في','1','419',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (416,'المكتبة العامة بالرباط (نسخة قديمة، انظر محمد المنونى في: تطوان ٨/ ١٩٦٣/ ١٣٨)، الإسكوريال ١٣٧٨ (من أوله إلى الخاء، ٥٤ ورقة، نسخت سنة ٧٣٥ هـ)، الفاتيكان ٣٩٣ (مجلدان، ٣٣٥ ورقة، نسخت سنة ٩٧٧ هـ، انظر فيدا ٤٢ - ٤٣)، باريس ٥٩٧٦ (الجزء الثالث، من الدال إلى السين، ١٨٣ ورقة، نسخت سنة ٥٨٩ هـ، راجع فجدا ٣٥٩)، ليبتسج ٤٥٧ (الجزء الثانى، من السين إلى الغين، ١٣٧ ورقة)، برلين ٦٩٦ (النصف الأول حتى الصاد، ٢٨٦ ورقة، نسخت سنة ٥٨١ هـ)، ٦٩٧ (من الفاء إلى آخره، ١٩٥ ورقة، نسخت سنة ٩٤٠ هـ)، ليدن ٢٢٧١ Or.، و ٢٢٧٢ (١٢٢ ورقة و ١٢٦ ورقة، نسخت سنة ٥٣٣ هـ، انظر فورهوف ٩٥)، المتحف البريطانى) ٧٤٩٢ Or.، من الباء إلى النون، ٣٠٥ ورقة، نسخت سنة ٥٠٦ هـ، انظر، (Ellis ,Descr.List ٥٢ و) ٧٧٣٤ Or. الجزء الثانى، ٣٠٢ ورقة، من القرن ١٢ هـ، انظر المرجع نفسه)، المكتب الهندى ٨٣٢ (٢٩٧ ورقة، نسخت سنة ٥١٠ هـ، انظر لوث رقم ٩٩٢)، منجانا ٢١٣ (الجزء الثانى، ١٨٠ ورقة، نسخت سنة ١٠٨٤ هـ، انظر الفهرس رقم ٤٠)، تشستربيتى ٣٠١١ (٢٢٧ ورقة، نسخت سنة ٥٨٦ هـ)، و ٣٣٦٤ (الجزء الثانى، ١٩٨ ورقة، سنة ٥٩١ هـ)، و ٤٨٥٩ (مجلدان، ٢٠٥ ورقة و ٢٩١ ورقة، نسخت سنة ٦٩٩ هـ)، برنستون، جاريت) G ٢ من أوله إلى الصاد، ٢١٣ ورقة، من القرن ٩ هـ، انظر الفهرس (رقم ١٤٤٥)، و ١١٧) B ٢٥٣ ورقة، من القرن السابع للهجرة، انظر الفهرس رقم ١٤٤٦)، ييل) ٤١٣ - L، من أوله إلى الحاء، ٨٢ ورقة، من القرن ١٣ هـ، انظر نيموى رقم ٧١٨)؛','1','420',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (417,'نشره (دون ذكر للمخطوطات التى اعتمد عليها) محمود محمد الطناحى، المجلد الأول، القاهرة ١٩٧٠.
فى الاستدراك عليه، وإصلاحه، واختصاره ... الخ ١ - «التنبيه على ألفاظ الغريبين» وأكثر تفصيلا «التنبيه على الألفاظ التى وقع خطأ في نقلها أو ضبطها أو تفسيرها في كتاب الغريبين» لأبى الفضل محمد بن ناصر بن محمد السلامى البغدادى (المتوفى سنة ٥٥٠/ ١١٥٥، انظر معجم المؤلفين لكحالة ١٢/ ٧٢)، وتذكره المراجع أيضا باسم «المآخذ على أبى عبيد الهروى في كتاب الغريبين» (انظر مثلا الوافى بالوفيات للصفدى ٥/ ١٠٥)،

<span data-type="title" id=toc-551><span data-type="title" id=toc-552>المخطوطات:</span></span>
الظاهرية بدمشق عام ١٥٨٩ (٨٠ ورقة، نسخة قديمة، انظر فهرس اللغة ٧٤)، التيمورية بدار الكتب بالقاهرة، لغة ٧٦ (٧٢ ورقة، نسخت سنة ١٣٢٧ هـ، راجع مجلة المجمع العلمى العربى بدمشق ٣/ ١٩٢٣/ ٣٣٩).
وثمة دراسة فيه لمحمود محمد الطناحى في: البحث العلمى (مكة) ٣/ ١٩٨٠/ ٤١٩ - ٤٢٧.
٢ - «مختصر» لأبى المكارم على بن محمد بن هبة الله النحوى الوزير (المتوفى سنة ٥٦١/ ١١٦٥، انظر كشف الظنون ١٢٠٩)، ويوجد مخطوطا في المتحف العراقى ببغداد ١٣٤٨ (٤٠٣ ورقة، انظر فهرس النقشبندى ٩٦، رقم ٣٤٠).
٣ - «المغيث في تكملة الغريبين في القرآن والحديث» لأبى موسى محمد بن عمر بن أحمد المدينى الأصفهاني (المتوفى سنة ٥٨١/ ١١٨٥)، ويشتمل على استدراكات وإصلاحات اتخذها ابن الأثير فيما بعد مع الكتاب الأصلى أساسا لكتابه «النهاية» (انظر كشف الصنون ١٢٠٦ - ١٢٠٧)،
المخطوطات:
كوبريلى ٤٤٠ (مجلدان، ١٧١ ورقة و ١٤٧ ورقة، نسخا سنة ٦٧٤ هـ، انظر فهرس المخطوطات ١/ ١٠٨)،','1','421',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (418,'فيض الله ٢٠١٦ (٣٥٠ ورقة، من القرن السابع للهجرة، انظر الفهرس نفسه)، شهيد على ٣٠٣ (٢٣٣ ورقة، نسخت سنة ٥٢٨ هـ، انظر شيشين، نوادر المخطوطات ٢/ ٤٠٩)، وعند إملائه كتاب المغيث نشأ متفرّعا عنه «كتاب تقذية ما يقذى العين من هفوات كتاب الغريبين».
ويوجد مخطوطا في أكسفورد، بودليانا) ٤/ ١٩٥ Marsh، الأوراق ١٠٥ - ١٩٥، نسخت سنة ٦٠٠ هـ، انظر الفهرس ٢/ ٣٨٠، رقم ٣٨١)، ولعله هو الكتاب الذى أشير إليه في كشف الظنون ١٢٠٩.
٤ - «المشرع الروى في الزيادة على غريب الهروى» لأبى عبد الله محمد بن على بن الخضر بن عساكر الغسّانى المالقى (المتوفى سنة ٦٣٦/ ١٢٣٩)، ذكره السيوطى في بغية الوعاة ٧٦، وراجع كشف الصنون ١٢٠٩؛ وكان من مصادر على بن سعود في كتابه «تخريج الدلالات السمعية» (انظر تاريخ التراث العربى ٩/ ٢٥٠)، انظر بيان المصادر الورقة ٢٢٩ أ.
[استدراك على المجلد الأول من تاريخ التراث العربى]:
«كتاب ولاة هراة»، ذكره ياقوت في إرشاد الأريب ٢/ ٨٧ وآخرون بعده./

<span data-type="title" id=toc-553>أبو النّدى</span>
هو محمد بن أحمد بن الغندجانى، لغوى أديب «واسع العلم» (ياقوت)، كان تلميذا لابن السيرافى (المتوفى سنة ٣٨٥/ ٩٩٥، انظر فصل النحو). ويبدو أنه عاش في فارس وتوفى نحو سنة ٤١٠/ ١٠٢٠. وكان أشهر تلامذته (١٤٧) الأسود الغندجانى (المتوفى سنة ٤٣٦/ ١٠٤٤، انظر ص ٢٣٧ بعد) الذى اعتمد عليه إلى حد بعيد فيما صنفه.
_________
(١٤٧) وله تلميذ آخر هو أبو الحسن على بن الحارث البيارى الخراسانى (انظر دمية القصر للباخرزى ٢ ٢/ ٤٩٣ - ٤٩٤؛ إنباه الرواة للقفطى ٢/ ٢٧٤ - ٢٧٥، ٤/ ١٨١)، ألّف شرحا على حماسة أبى تمام [استدراك على تاريخ التراث العربى] و «كتاب صناعة الشعر» [استدراك على ص ١٠٦ من المجلد نفسه].','1','422',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (419,'<span data-type="title" id=toc-554><span data-type="title" id=toc-557>مصادر ترجمته:</span></span>
إرشاد الأريب لياقوت ٣/ ٢٣، ٦/ ٢٩٤ - ٢٩٧، ومعجم البلدان له ٣/ ٨٢٠؛ إنباه الرواة للقفطى ٤/ ١٨١؛ تلخيص ابن مكتوم ٢٩٦، بغية الوعاة للسيوطى ٢١.

<span data-type="title" id=toc-555>مصنفاته:</span>
لم ترو لنا أسماء مصنفاته. وقد بقيت لنا شذرات منها في مصنفات الأسود الغندجانى (انظر أيضا معجم البلدان لياقوت ١/ ١٢٧ - ١٢٩، ٢/ ٧٤٨، ٣/ ٤١١، ٤/ ٦٣٦، ٨٨٢)، ونقول في مجمع الأمثال للميدانى (انظر المورد ٣، ٢/ ١٩٧٤/ ١٣، ٣، ٣/ ١٩٧٤/ ١٠٧) وفي ١٥ موضعا من معجم البلدان لياقوت.

<span data-type="title" id=toc-556>أبو المظفر الهروى</span>
هو محمد بن آدم بن كمال، لغوى جليل من أهل هراة، أخذ عن أبى بكر الخوارزمى وغيره. توفى سنة ٤١٤/ ١٠٢٣.
مصادر ترجمته:
دمية القصر للباخرزى ٢/ ٤٩٤ - ٤٩٥؛ إرشاد الأريب لياقوت ٦/ ٢٦٧؛ إنباه الرواة للقفطى ٣/ ١٢٦، بغية الوعاة للسيوطى ٤.- معجم المؤلفين لكحالة ٩/ ٣٥ - ٣٦.

<span data-type="title" id=toc-558>آثاره:</span>
١ - «شرح الإصلاح» (أى إصلاح المنطق لابن السكيت)، ذكره ياقوت وغيره.
٢ - «شرح أمثال أبى عبيد» (انظر ص ٨٤ آنفا)، ذكره ياقوت وغيره.
وله أيضا: ١ - «شرح الحماسة» ذكره ياقوت (انظر تاريخ التراث العربى ٢/ ٦٩).
٢ - «شرح ديوان أبى الطيب» (المتنبى، انظر تاريخ التراث العربى ٢/ ٤٩٣).','1','423',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (420,'<span data-type="title" id=toc-559>الزّجاجىّ</span>

هو أبو القاسم يوسف بن عبد الله الزجاجى الجرجانى، أديب لغوى نحوى من أسرة همدانية، ولد سنة ٣٥٢/ ٩٦٣، عاش أكثر عمره في جرجان وتوفى سنة ٤١٥/ ١٠٢٤ في استراباذ. وقيل إنه صنف شروحا كثيرة (لعل أكثرها على كتب في اللغة)./
<span data-type="title" id=toc-560>مصادر ترجمته:</span>
تاريخ جرجان للسهمى ٢ ٥٧٨؛ إرشاد الأريب لياقوت ٧/ ٣٠٨؛ تاج العروس ٢/ ٥٢؛ ابن قاض شهبة، المخطوط ص ٥٤٨ - ٥٤٩؛ بغية الوعاة للسيوطى؛ . Br.GI ,١٣١ - .٤٢٢ الأعلام للزركلى ٩/ ٣١٦؛ معجم المؤلفين لكحالة ١٣/ ٣١٢.

<span data-type="title" id=toc-561>آثاره:</span>
١ - «عمدة الكتّاب»، فى المترادفات، وهو تهذيب لكتاب «جواهر الألفاظ» لقدامة بن جعفر (المتوفى سنة ٣٣٧/ ٩٤٨، انظر فصل الأدب)، ذكر بين كتبه عند ياقوت وابن قاضى شهبة والسيوطى في المواضع المذكورة آنفا؛ كشف الظنون ١١٧١.

<span data-type="title" id=toc-562>المخطوطات:</span>
الأوقاف ببغداد ١٠١٣٤ (٢٨ ورقة، راجع فهرس طلس ١٧٣، رقم ٢٢٦٥)، و ١٢٢٠٨ (٥٨ ورقة، نسخت سنة ١٣٢٢ هـ، انظر فهرس الجبورى ٣/ ٢٤١، رقم ٥٢٠١)، المتحف العراقى ببغداد، مجموعة قاسم الرجب، الجزء الأول ضمن مجموع (انظر كوركيس عواد في: مجلة المجمع العلمى العراقى ١٢/ ١٩٦٥/ طبعة منفصلا ص ٢٧، رقم ١٦٢).
كلية الآداب ببغداد، سابقا معهد الدراسات الإسلامية (نسخة حسنة، انظر القائمة المطبوعة على الآلة الكاتبة ٩٦، رقم ١١٠١)، تشستربيتى ٣٢٤٢/ ٢، سابقا في','1','424',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (421,'مكتبة نور الدين مصطفى الخاصة بالقاهرة (الأوراق ٧١ - ١٤٠، نسخت سنة ٧٤٨ هـ)، ومنها مصورة في دار الكتب بالقاهرة، أدب ٤٩٣٣ (انظر الفهرس الثانى ٣/ ٢٥٨؛ فهرس المخطوطات ١/ ٣٦١)، ومنها نسخة منقولة عنها بالدار ١٧٥٤٩ ز (انظر الملحق ٢/ ١٤٢)، ومصورة بطلعت، مجموع ٤٩٠٠.
٢ - «كتاب اشتقاق الأسماء»، ذكره ياقوت والسيوطى.
٣ - «كتاب اشتقاق كلمات من أول الغريب المصنف» (لأبى عبيد، المتوفى سنة ٢٢٤/ ٨٣٩)، ذكره ابن قاضى شهبة.
٤ - «شرح الفصيح»، شرح على كتاب ثعلب (المتوفى سنة ٢٩١/ ٩٠٤)، انظر ياقوت وابن قاضى شهبة والسيوطى في المواضع المذكورة آنفا؛ كشف الظنون ١٢٧٢.
٥ - «أمال (فى دقائق علم اللغة وحقائقها)»، ذكره السهمى.
٦ - «كتاب خلق الإنسان» (١٤٨)، انظر ياقوت وابن قاضى شهبة والسيوطى في المواضع المذكورة آنفا، كشف الظنون ٧٢٢.
٨ - «كتاب الرياحين» ذكره ياقوت والسيوطى؛ وخلطه ابن قاضى شهبه وصاحب كشف الظنون باسم الكتاب المذكور في رقم ٢ وجعلا اسمه «كتاب اشتقاق أسماء الرياحين».

<span data-type="title" id=toc-563>وله في النحو والصرف:</span>
١ - «كتاب مسائل الخلاف» ذكره ابن قاضى شهبة.
٢ - «كتاب فعلت وأفعلت» مذكور في المصدر السابق.

<span data-type="title" id=toc-564>أبو منصور (بن) الجبّان</span>
هو محمد بن على بن عمر الرازى، ويعرف أيضا بابن الجبّان اللغوى، أصله
_________
(١٤٨) لعله ليس هو «كتاب البيان فيما اشتمل عليه خلق الإنسان» الذى بلغنا غفلا من اسم مؤلفه، برلين، ١/ ٧٤ Pm.-) ٧٠٣٧ الأوراق ١ - ٣٤ أ، نسخت سنة ٦٦٥ هـ)','1','425',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (422,'من الرىّ، عاش فى أصفهان وزار بغداد سنة ٣٩١/ ١٠٠١. كان لغويا أديبا شاعرا (وردت له أبيات عند أبى القفطى وغيره)، وهو تلميذ لأبى على الفارسى، وخالط ابن سينا، والصاحب بن عباد، ثم جفاه الصاحب من بعد. وكان لا يزال حيا سنة ٤١٦/ ١٠٢٥./

<span data-type="title" id=toc-565>مصادر ترجمته:</span>
إنباه الرواة للقفطى ٣/ ١٩٤، ٤/ ١٧٠ - ١٧١؛ إرشاد الأريب لياقوت ٧/ ٤٥ - ٤٧؛ الوافى بالوفيات للصفدى ٤/ ١٨٠؛ بغية الوعاة للسيوطى ٧٨ - ٧٩.- معجم المؤلفين لكحالة ١١/ ٣٠.

<span data-type="title" id=toc-566>آثاره:</span>
١ - «كتاب شرح الفصيح»، كتاب «حسن» (ياقوت)، وهو شرح على كتاب الفصيح لثعلب (المتوفى سنة ٢٩١/ ٩٠٤)، ذكرته كل المصادر، ويوجد مخطوطا فى سوهاج، لغة ٣٧ (٢٤١ ورقة، نسخت سنة ٣٩٨ هـ، انظر فهرس المخطوطات ١/ ٣٥٨، راجع مجلة معهد المخطوطات ١/ ١٩٥٥/ ١٩١، وكتب فيه ع. ج.
القزار رسالة ماجستير، بغداد ١٩٧٤ (انظر المورد ٧، ١/ ١٩٧٨/ ٢٦٤).
٢ - «كتاب الشامل»، وهو كتاب كبير فى اللغة مرتب على الحروف، قصد فيه جمع الألفاظ اللغوية كاملة قدر الامكان، مع قليل من الشواهد، وقد ملك القفطى شيئا من الكتاب، من أصل ثلاثة عشر مجلدا.
٣ - «لسان العرب»، وهو كتاب فى اللغة أوسع خطة و «أحسن ترتيبا وتبويبا» من كتابه «الشامل»، وبقى ناقصا (انظر القفطى).
٤ - «كتاب انتهاز الفرص فى تفسير (أو: تبيين) المقلوب من كلام العرب»، ذكرته ثلاثة مصادر.

<span data-type="title" id=toc-567>وله فى الأبنية:</span>
«كتاب أبنية الأفعال»، مذكور أيضا فى ثلاثة مصادر.','1','426',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (423,'<span data-type="title" id=toc-568>الخطيب الإسكافى</span>
هو أبو عبد الله محمد بن عبد الله خطيب القلعة الفخرية، لغوى من أهل أصفهان، وكان خطيبا بالرى. وهو أحد أصحاب ابن عباد الصاحب. توفى سنة ٤٢٠/ ١٠٢٩.

<span data-type="title" id=toc-569>مصادر ترجمته:</span>
إرشاد الأريب لياقوت ٧/ ٢٠؛ الوافى بالوفيات للصفدى ٣/ ٣٣٧، بغية الوعاة للسيوطى؛ Br.GI ,٢٧٩ ,SI ,٤٩١ - .٦٣ الأعلام للزركلى ٧/ ١٠٢؛ معجم المؤلفين لكحالة ١٠/ ٢١١.

<span data-type="title" id=toc-570>آثاره:</span>
١ - «مبادئ اللغة»، وهو معجم صغير مرتب على الموضوعات (١٤٩).

<span data-type="title" id=toc-571>المخطوطات:</span>
الظاهرية بدمشق ٥٩٧٤ (جزء، ١٠ ورقات، حديثة، انظر فهرس اللغة ١٤٦ - ١٤٧)، المتحف العراقى ببغداد ١٧٦٣ (٦٥ ورقة، نسخت ١٣٢٠ هـ، انظر فهرس النقشبندى ص ١٠٠)، كلية الآداب ببغداد (ضمن مجموع، رقم ١)، جامعة الحكمة ببغداد، مجموعة سركيس، مجموع ١٠٣/ ١ (نسخت سنة ١٢٩٣ هـ، انظر الفهرس ص ٦٢ - ٦٣).
وهو من مصادر حبيش بن إبراهيم بن محمد التفليسى (المتوفى سنة ٦٢٩/ ١٢٣٢) فى كتابه «قانون الأدب» (انظر مجلة المجمع العلمى العربى بدمشق ٥٠/ ١٩٧٥/ ٤٠٢).
_________
(١٤٩) إن ملاحظة ناشر الطبعة الأولى من الكتاب والتى اعتمد فيها على مخطوطة استخدمها لنشر الكتاب، وتزعم أن هذا الكتيب مستخلص من كتاب العين للخليل، ونوادر ابن الأعرابى الخ، هذه الملاحظة ذات قيمة ضئيلة من جهة تاريخ التراث، إذ أن الأمر يتعلق فيما يبدو برأى قارئ مجهول.','1','427',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (424,'نشر لويس شيخو نماذج من نصوصه فى: المشرق ٣/ ١٩٠٠/ ٤٩٨ - ٥٠١؛ وطبع فى القاهرة ١٣٢٥.
وللمؤلف نفسه شرح لشواهد مبادئ اللغة، ويوجد مخطوطا فى جامعة الحكمة ببغداد، مجموعة سركيس، مجموع ١٠٣/ ٢ (الفهرس ص ٦٣)./

<span data-type="title" id=toc-572>المخطوطات:</span>
٢ - «خلق الإنسان»، جامعة الحكمة ببغداد، مجموعة سركيس، مجموع ١٠٣/ ٣ (نسخت سنة ١٢٩٣ هـ، انظر الفهرس ص ٦٣)، المتحف العراقى ببغداد ٨٧٩٣ (١٩ ورقة، نسخت سنة ١٣١٨ هـ، انظر أسامة ناصر النقشبندى فى: المورد ٤، ١/ ١٩٧٥/ ١٩٤، الظاهرية بدمشق ٤٤٤١ (٢١ ورقة، نسخت سنة ١٣٢٦ هـ، وذكر فى فهرس اللغة ٨٤ - ٨٥ أنه باب من مبادئ اللغة»).
٣ - «كتاب المجالس»، وفيه ٣٥ مجلسا وأكبر موضوعاته بعضها فى القرآن وبعضها فى أخبار الرسول، ويشغل فيهما الكلام على النحو مكانا كبيرا.
ويوجد مخطوطا فى كوبريلى ٢٠٩ (١٢٤ ورقة، نسخت سنة ٥٧١ هـ) (١٥٠) ٤ - «لطف التدبير» (انظر فصل الفلسفة- السياسة).
٥ - «غلط كتاب العين»، ذكره ياقوت، فى الموضع المذكور.

<span data-type="title" id=toc-573>المرزوقى</span>
هو أبو على أحمد بن محمد بن الحسن الأصفهانى، كان لغويا نحويا بارعا.
ولد قبل سنة ٣٦٠/ ٩٧٠ وتوفى سنة ٤٢١/ ١٠٣٠. وكان من أساتذته أبو على
_________
(١٥٠) وقيل إن التصنيف اللغوى «كتاب درة التنزيل وغرة التأويل» الذى ينسب إليه إنما هو من تأليف الراغب الأصفهانى، انظر ع. ع. الساريتى في مجلة المجمع العلمى العربى بدمشق ٥١/ ١٩٧٦/ ١١٤ - ١١٧، وما كتبه أيضا في مجلة مجمع اللغة (الأردن) رقم ٣ - ٤/ ١٩٧٩/ ٩٧ - ١٠٦.','1','428',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (425,'الفارسى. وكان معلم أولاد بنى بويه بأصفهان مدة من الزمان. ويبدو أن قوّته في علوم العربية كانت في شروحه اللغوية على الشعر القديم.

<span data-type="title" id=toc-574>مصادر ترجمته:</span>
إرشاد الأريب لياقوت ٢/ ١٠٣ - ١٠٤؛ إنباه الرواة للقفطى ١/ ١٠٦؛ بغية الوعاة للسيوطى ١٥٩؛ ابن قاضى شهبة، المخطوط ص؛ Br.S -.I ,٥٠٢ ١٩٧ - ١٩٦ الأعلام للزركلى ١/ ٢٠٥؛ معجم المؤلفين لكحالة ٢/ ٩١ - ٩٢.

<span data-type="title" id=toc-575>آثاره:</span>
١ - «كتاب الأزمنة والأمكنة» وقد وصل إلينا، انظر تاريخ التراث العربى ٧/ ٣٦١ - ٣٦٣.
٢ - «الأمالى»، وتحتوى على أمور في اللغة والنحو، ويوجد مخطوطا في برمنجهام، منجانا ١٣٣٦/ ١ (١ أ- ٥٤ ب، من القرن ١٢ هـ، أولها. ناقص، انظر الفهرس رقم ١٤٢٦)، ومنها مصورة بدار الكتب بالقاهرة، لغة ٣٣٠٠ (انظر الفهرس الثانى ٣/ ٢٣).
٣ - «القول في ألفاظ الشمول والعموم»، ويوجد مخطوطا في برمنجهام، منجانا ١٣٣٦/ ٢ (٦٤ ورقة، من القرن ١٢ هـ، آخرها ناقص، انظر الفهرس رقم ١٤٢٦)، ومنها مصورة بدار الكتب بالقاهرة، لغة ٤١٤٠ (انظر الفهرس الثانى ٣/ ٢١)، المتحف العراقى ببغداد ١٣٩٥ (٢٢ ورقة، نسخت سنة ٦٤٩ هـ، انظر فهرس النقشبندى ص ٦١)؛ نشره إبراهيم السامرائى في: رسائل في اللغة، بغداد ١٩٦٤، ٦٩ - ٩٩.
٤ - «شرح فصيح ثعلب»، كوبريلى ١٣٢٣ (١٩٦ ورقة، نسخت سنة ٥٣٤ هـ، انظر فهرس المخطوطات، (O.Rescherin: MSOS ١٤ /١٩١١ /١٧٦ - ١٧٧؛ ٣٥٨ /١ ووردت نقول عنه في خزانة الأدب (انظر إقليد الخزانة للميمنى ٨٠) والمزهر للسيوطى (انظر الفهرس ٢/ ٦٤٤).

<span data-type="title" id=toc-576>وله في لغة القرآن:</span>
«غريب القرآن» المحمودية بالمدينة ٢٩. ل ٨٥ (انظر (O.Spiesin: ZDMG (٩٠ /١٩٣٦ /١٠٧','1','429',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (426,'<span data-type="title" id=toc-577>وله في شرح الأشعار:</span>
١ - «شرح أشعار هذيل»، ذكره ياقوت في إرشاد الأريب ٢/ ١٠٣، ولم يصل إلينا، انظر تاريخ التراث العربى ٢/ ٤٥./ ٢ - «شرح المفضليات»، وقد وصل إلينا، انظر تاريخ التراث العربى ٢/ ٥٤.
٣ - «شرح حماسة أبى تمام»، وصل إلينا، انظر المرجع نفسه ص ٦٩ - ٧٠.

<span data-type="title" id=toc-578>وله في النحو:</span>
١ - «شرح الموجز» (يحتمل أنه شرح كتاب ابن السرّاج، انظر فصل النحو)، ذكره ياقوت في الموضع المذكور.
٢ - «كتاب شرح النحو»، ذكره ياقوت أيضا.

<span data-type="title" id=toc-579>الثعالبى</span>
هو أبو منصور عبد الملك بن محمد بن إسماعيل النيسابورى، ولد نحو سنة ٣٥٠/ ٩٦٠. ولا يعرف عن حياته إلا شئ قليل جدا.
نشأ على ما يظهر في مسقط رأسه، وكان يكتسب رزقه مدة من الزمان، على أقل تقدير، بالاتجار في فراء الثعالب. ثم خالط فيما بعد بعض رجال السياسة بفارس، واستطاع من ثمة أن يحظى بمعونة بعض رجالات الحكم له. وعلى الخصوص انعقدت أواصر صداقة وطيدة بينه وبين الأمير أبى الفضل الميكالى (انظر فصل الأدب). وتوفى الثعالبى في سنة ٤٢٩/ ١٠٣٨.
ولمصنفاته الكثيرة التى وصلت إلينا أو بلغتنا أسماؤها اشتهر الثعالبى عند مؤرخى الأدب العربى المحدثين بكونه مؤلفا غزير التأليف في اللغة، والأدب الممتع، وتاريخ الأدب، والبلاغة. وقد أخذ عليه أنه «لم يرع حق من تقدمه من المؤلفين في تراثهم الفكرى إلا قليلا» و «انه كثيرا ما وقع في التكرار» (انظر بروكلمان فى. (EI ,IV ,٧٩١ وفي العقود الأخيرة من هذا القرن ازداد الاهتمام ببحث مؤلفات الثعالبى ونشرها. وليس في متناولى الآن الدراستان اللتان أفردتا لشخص الثعالبى ومصنفاته (١٥١).
_________
(١٥١) أتيح لى خلال إقامة قصيرة في طشقند ١٩٧٨ أن أطلع على بعض ملازم تحت الطبع','1','430',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (427,'وأقول في هذا الصدد إننى بعد قراءة عجلى لما بين يدى من مؤلفات الثعالبى قد تولّد لدىّ انطباع بأصالته، ومردّ هذه الأصالة قبل كل شئ إلى أنه انتخب مادته مما كان يهمه ويعنيه من كتب غيره من العلماء العرب في اللغة والأدب الممتع والبلاغة والنحو/ وتاريخ الأدب والأخلاق والسياسة تبع لاعتبارات أسلوبية، وإلى أنه جرى في عرضها على خطة غير التى جرى بها الاصطلاح.
ومن المؤكد أن هذه الطريقة في عرض المادة قد وجدت من عنى بها من قبله، ولكن يبدو أنه اعترف بها في صمت على أنها القاعدة الوحيدة في ترتيب المادة، والتزامها دونما تغيير مدة حياته.
والظاهر أنه كان يرى أن تعريفات المفاهيم أدنى تأثيرا في القارئ من تبويب المادة وتقسيمها تقسيما منهجيا.
إن إصدار حكم سديد في مدى أصالته في هذا الصدد يتوقف على ما سيثبت البحث أنه كان من نتاجه الخاص به. ويقدم لنا هو نفسه بعض الدلائل التى تومئ إلى أصالته؛ ومثال ذلك أنه يقرّر في كتابه «تحسين القبيح وتقبيح الحسن» - الذى جمع فيه مادة من الشعر والنثر مرتبة على قاعدة مدح القبيح وذم الحسن- أنه لم يسبقه أحد إلى ذلك. وطائفة من مؤلفاته مفردة لبعض موضوعات البلاغة. ولم يقصد الثعالبى فيها إلى عرض تمهيدى أو أسلوب تعليمى مصحوب بالتعريفات، بل رمى إلى أن يجمع نصوصا تمكّن القارئ من تقدير القيمة الجمالية.
ولا يبعد أبدا أنه مهد السبيل لكتاب «أسرار البلاغة» لعبد القاهر الجرجانى (المتوفى سنة ٤٦٣/ ١٠٧١)، كما سيتضح هذا أيضا بعض الاتضاح في مجال «نحو الأساليب» (انظره).
ولم يجر الثعالبى في مصنفاته اللغوية ومجموعات أمثاله التى وصلت إلينا- ولعل ذلك أيضا فيما ضاع منها- على الخطط المتبعة في الكتب المعتمدة، وإنما اختار
_________
- من إحداهما، وهى أطروحة للزميل الأوزبكى عصمة الله؛ أما الدراسة الأخرى فهى أطروحة ماجستير ل ع. الجادر: «الثعالبى ناقدا وأديبا»، بغداد ١٩٧٦ (انظر المورد ٧، ١/ ١٩٧٨/ ٢٦٩).','1','431',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (428,'وجوها أخرى متنوعة من وجوه اللغة، عامة وجامعة، ليقدّم بها نصوصه الممنتخبة للقارئ المتعلم الذى قد حصل من اللغة على المعارف الأساسية.
وكتابه «فقه اللغة» الذى يمكن أن نعده مصنّفه اللغوى الرئيسى انما هو مثال على ذلك بما اشتمل عليه من مدخل إلى اللغة. وكونه ليس الأول في هذا أيضا بل قد سبقه آخرون، ربما يعدّ أمرا بيّنا بذاته عند ما نراقب ما يجرى من عمليات تطور أخرى في تاريخ العلوم.
إن الصلات الوثيقة بين هذا الكتاب وكتاب الصاحبى لأحمد بن فارس أمر قد بينه إ. جولد تسيهر (١٥٢).
أما مسألة: إلى أى مدى اعتمد الثعالبى اعتمادا مباشرا على كتاب ابن فارس، فمسألة يجب أن يختلف حكمنا فيها عن حكم جولد تسهير؛ إذ أن علمنا اليوم/ بتاريخ العلوم العربية يؤدى بنا إلى الاعتقاد بوقوع تطور متصل في كل المجالات، ترجع بداياته إلى زمان يسبق ذلك الذى درج جولد تسيهر وأخلاقه المباشرون على الذهاب إليه.

<span data-type="title" id=toc-580>مصادر ترجمته:</span>
دمية القصر للباخرزى ٢/ ٢٢٦ - ٢٣٠؛ ابن خلكان ١/ ٣٦٥ - ٣٦٦؛ نزهة الألباء للأنبارى ١ ٤٣٦ - ٤٣٧؛ الحيوان للدميرى ١/ ٢٢٣ - ٢٢٤؛ الوافي بالوفيات للصفدى؛ قائمة بالكتب التى قرأها مع أسانيد، دبلن، تشستربيتى ٤٨٦٩/ ٧ (٣٨ أ- ٤٧ ب، من القرن ١١ هـ Br.GI ,٢٨٤ - ٢٨٦ ,SI ,٤٩٩ - ٥٠٢ - . (ونفسه في؛ EIIV ,٧٩١ - ٧٩٣ معجم المؤلفين لكحالة ٦/ ١٨٩ - ١٩٠؛ عبد الفتاح محمد الحلو، فى مقدمته للكتاب «التمثيل والمحاضرة»؛ وفي مقاله «شعر الثعالبى» فى: المورد ٦، ١/ ١٩٧٧/ ١٣٩ - ١٩٤ (انظر عليه م. الجادر في المورد ٨، ٣/ ١٩٧٩/ ٤٣٤ - ٤٤٣)؛ رجينا هينكه Regina Heinecke في مقدمة تحقيقها لكتاب «تحفة الوزراء»، بيروت ١٩٧٥.
_________
(١٥٢)
