INSERT INTO [b143165] VALUES (432,'فيض الله ١٥٨٦ (٣٠٢ ورقة)، لاله لي ١٧٠٥ م (١٨٠ ورقة، نسخت سنة ١٠٣٠ هـ)، جامعة استنبول ٢٥٨) ١٣٩٨ A.Y.، ورقة)، رئيس الكتاب ٧٧٧ (١٦٩ ورقة)، لاله اسماعيل ٤٠٥، سراى أحمذ الثالث ٢٤٧٧ (٢١١ ورقة، من القرن ٩ هـ)، راغب ١٠٩٧ (١٩٧ ورقة، نسخت سنة ٨٨٢ هـ)، ١٢٠٧ (١٩٣ ورقة، نسخت سنة ١١٦٧ هـ)، قونيه، يوسف أغا ٦٥٨٩ (١٧٣ ورقة، نسخت سنة ٩١٦ هـ)، برلين ٨٣٤٢ (٢٥٧ ورقة، من القرن ١٢ هـ)، فينا ٨٣ (تنقصها الأبواب الثلاثة عشر الأولى، ١٠٠ ورقة)، ييل ١١٩) ٤٥٣ - L، ورقة، انظر نيموى رقم ٢٠٦)، دبلن، تشستربيتى ٤٧٤٤ (٢٢٦ ورقة، من القرن ٨ هـ)، القاهرة، دار الكتب، أدب اللغة ٢٢٥ (أولها ناقص، نسخت سنة ١١١٩ هـ، انظر الفهرس الثانى ٣/ ٧٢)، و ١٣٦٢ (نسخت سنة ١٣١٣ هـ، انظر الفهرس الثانى ٣/ ٧٣)، و ٤٠٩٩ (آخرها ناقص، انظر الفهرس نفسه ٧٣)، و ٧٣ ش (ضمن مجموع، نسخت سنة ١٣٠٦ هـ، انظر الفهرس نفسه ٧٣)، الجزائر ١٤٤٢ (٢٢٧ ورقة، من القرن ١١ هـ)، بغداد، المتحف العراقى ٨٦٣٣ (الجزء الأول، ١٤٨ ورقة، انظر أسامة ناصر النقشبندى في: المورد ٤، ١٩٧٥/ ١٩٧)؛ طبع في القاهرة ١٣٢٦؛ ونشره محققا محمد أبو الفضل إبراهيم، القاهرة ١٩٦٥.
وزاد فيه ورتبه على الحروف محمد بن فضل الله بن محب الله المحبّى (المتوفى سنة ١١١١/ ١٦٩٩، انظر، (Br.SII ,٤٠٣ - ٤٠٤ وسماه «ما يعوّل عليه في المضاف والمضاف إليه» ويوجد مخطوطا في:','1','436',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (433,'سراى أحمد الثالث، ٢٤٥٥ (٣٤٦ ورقة، نسخت سنة ١١٠٩ هـ، بخط المؤلف، انظر الفهرس ٣/ ٥٧٨)، أيا صوفيه ٤٢٣٦، داماد إبراهيم ٩٥٦ (٤١٧ ورقة، نسخت سنة ١١٠٩ هـ)، انظر أيضا، ٤٠٤، Br.SII ولعل المؤلف التكملة المسماة «تذييل المرغوب من ثمار القلوب»، باريس ٦٠٢٩ (الأوراق ١٩٣ - ١٩٧، انظر فجدا ص ٦٥٠)؛ ولهذا الكتاب مختصران:
أ) «نفحة المجلوب من ثمار القلوب» لمحمود بن أبى المعالى بن الحسن الحوارى (المتوفى بعد سنة ٥٨٠/ ١١٨٤، انظر إرشاد الأريب لياقوت ٧/ ١٥١ - ١٥٢)، أنقره، صائب ١٨٢٥ (نسخت سنة ٥٨٥ هـ، لعلع بخط مختصره).
ولى الدين ٢٦٤٩، جامعة استنبول ٢٦) ٢/ ١٣٢٩ A.Y.، ب- ١٢٠ أ، انظر طوبوزوغلو، الموضع المذكور ص ٧٢ - ٧٣)، كيمبردج) ١/ ٩ Qq الأوراق ١ - ١٠٨، انظر الفهرس رقم ١١٩٢)، ليدن ١٧٨) ٩٠٣ Or.: ورقة، نسخت سنة ١٠٦٥ هـ، انظر فورهوف ص ٣٧٩).- ب) «عماد البلاغة في أمثلة أهل البراعة» لعبد الرءوف بن تاج العارفين ابن على المناوى (المتوفى سنة ١٠٣١/ ١٦٢١، انظر، (Br.II ,٣٠٦ كوبريلى ٢، ٢٥٨، بورسة، حسين جلبى، أدب ٥٦؛ طبع في القاهرة ١٣٢٦؛ راجع هينكه، الموضع المذكور ص ١٩ - ٢٠.
٣ - «كتاب المقصور والممدود»، ذكره الصفدى في الوافى بالوفيات (انظر مقدمة «لطائف المعارف»، القاهرة ١٩٦٠، ص ١٩، رقم ٧٠).
٤ - «مغرب اللغة»، أماسية ١٩٦٩ (١٨٤ ورقة، سنة ١٠٨٣ هـ)؟ / ٥ - «كتاب التوفيق للتلفيق»، وموضوعه تقسيم الأشياء والأشخاص وتصنيفها على أساس أوصافها في النثر والشعر،','1','437',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (434,'وقد حدّد المؤلف فيه قاعدته التى اتبعها في الترتيب بأنها «التلفيق بين الشئ وجنسه، والجمع بين الشئ وشكله»، ويحتوى على ٣٠ بابا، منها مثلا «التلفيق بين أوصاف خصائص الأشياء وردّ بعضها في التشبيهات إلى بعض».
ولا يسهل البت في نسبة هذا التصنيف إلى باب اللغة أو باب الأدب الممتع.
ويوجد مخطوطا في برلين ٨٣٣٨ (٨٠ ورقة، نسخت سنة ٦٤٤ هـ).
٦ - «نسيم السّحر». وهو تصنيف للأوصاف والأفعال تبعا لاتفاقها في الصفات أو الأحداث عند الانسان أو الحيوان أو الأشياء، ويوجد مخطوطا في بغداد، معهد الدراسات الإسلامية، مجموع ٤٠٤/ ١ (الأوراق ١ - ١٣، نسخت سنة ١٠٥٣ هـ)، أيا صوفية ٤٣٥٣/ ٢ (٣١ ب- ٣٥ ب، نسخت سنة ٨٨٤ هـ)، راغب ١٤٨٦/ ٢ (١٦٤ ب- ١٧١ أ، من القرن ١٢ هـ)، طهران، مجلس ٤٩٠٠ (١٥٨ ب- ١٦٥ أ، نسخت سنة ١٠٧١ هـ، انظر الفهرس ١٤/ ٨٠)، دبلن، تشستربيتى ٤٨٢٠/ ١ (الأوراق ١ - ٥، نسخت سنة ٨١٦ هـ)؛ نشرته ابتسام مرهون الصفار في: المورد ١، ١ - ٢/ ١٩٧٠/ ١٢٧ - ١٤٢.
وهذا التصنيف هو نفسه:
أ) «خصائص اللغة من كتاب نسيم السحر» (كذا في آخر المخطوطة)، كتبه ابراهيم بن أحمد الأسروشني (الاسم على صفحة العنوان فقط) لأبى الفتح الحسن بن إبراهيم الضّمرى، ويوجد مخطوطا في أيا صوفية ٢٠٥٢/ ١٢ (١٨٧ أ- ١٩٦ ب؛ نسخت سنة ٦٨٦ هـ)، ب) «خصائص اللغة»، وزعم أنه مختصر من كتاب الثعالبى «فقه اللغة» لأبى حفص عمر بن محمد بن أحمد النسفى (المتوفى سنة ٢٣٧/ ١١٤٢)، وقد أهدى إلى الضمرى المذكور آنفا. ويوجد مخطوطا في دمشق، الظاهرية، لغة ٢٠٦ (الأوراق ١٥١ - ١٦٦)، وعام ٩١٦٨ (ناقصة، ٦ ورقات، نسخت سنة ١٠٣٠ هـ، انظر فهرس اللغة ٨٣ - ٨٤؛ فهرس المخطوطات ١/ ٣٥٣).','1','438',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (435,'٧ - «الكناية والتعريض» أو «الكفاية في الكناية» أو «النهاية في التعريض والكناية»، وهو معجم للكنايات مرتب على الموضوعات، يشتمل على ٧ أبواب. وقد ألفه الثعالبى سنة ٤٠٠/ ١٠١٠ في نيسابور، ثم زاد فيه فيما بعد. ويحيل فيه على كتابيه «المبهج» و «الاقتباس من القرآن»، وكذلك على «كتاب المستنير» للمرزبانى، و «كتاب الأمثال المولّدة» لأبى بكر الطبرى، و «كتاب نتف الطرف» لأبى على السلامى، و «كتاب جراب الدولة» (لأحمد بن محمد السجزى، انظر فصل الأدب)، و «كتاب ملح النوادر» لمجهول، وعلى كتب أخرى.

<span data-type="title" id=toc-584>المخطوطات:</span>
كوبريلى ١١٩٧/ ٢ (٩٤ أ- ١٥٢ ب، من القرن ٩ هـ)، ١٣٥٣/ ١ (١ أ- ٤٠ ب، من القرن ١٣ هـ)، رئيس الكتاب ١١٥٧/ ٣ (٣٣ أورقة)، عاطف ٢٢٣٥ (٤٦ ورقة)، راغب ١٤٧٣/ ١ (الأوراق ١ - ٤٩، نسخت سنة ١١١١ هـ)، ولى الدين ٣٢٠٧/ ٤ (٧٥ أ- ١٠٣ أ، نسخت سنة ١٠٣٠ هـ، انظر، (O.Rescherin: MO ٧ /١٩١٣ /١٠٩ مراد ملا ١٥٨٢ (٤٤ ورقة)، بايزيد ٣٢٠٧/ ٤ (٧٥ أ- ١٠٣ أ، نسخت سنة ١٠٣٠ هـ)، أسعد ٣٥٤٢/ ٣١ (مختصر، الأوراق ٢٢٢ - ٢٢٦، من القرن ٦ هـ)، برلين ٧٣٣٦ (٣٠ ورقة، نسخت سنة ١٠٧٧ هـ) ليبتسج ٨٦٣ (١ أ- ١٠ ب، نسخت سنة ١١٤١ هـ)، الإسكوريال ٢٨١ (١٠٩ ورقة، نسخت سنة ١٠٠٩ هـ)، باريس ٥٩٣٤ (الأوراق ٧١ - ١١٠، انظر فجدا ٤٢٦)،','1','439',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (436,'لندن، المتحف البريطانى) ١/ ٣١٧٧ Or.، الأوراق ٢ - ٢٢، نسخت سنة ١٠٢٦، انظر الملحق رقم ١١١٠)، دبلن، تشستربيتى ٤٦٢٩/ ٦ (الأوراق ١٥٤ ب- ٢١٤، من القرن ٩ هـ)، قينا ٨٤ (الأوراق ٨٩ - ١١٨، نسخت سنة ١١٢٨ هـ)، القاهرة، دار الكتب، أدب ٤٩٢ (نسخت سنة ١٢٩٩ هـ، انظر الفهرس الثانى ٣/ ٤٢٢)، وأدب ٢٥ ش (٩٤ أ- ١٥٠ ب، نسخة حديثة، انظر الفهرس نفسه)، المدينة، عارف حكمت، مجموع ١١ (قديم) أو مجموع ١٧٧ (الأوراق ١٤٩ - ١٩٠، نسخت سنة ١٠٦٦ هـ، انظر المنتخب لكحالة ٩٦)؛ طبع في مكة ١٣٠١، والقاهرة ١٣٢٦، وأعيد طبعه بالتصوير في بيروت، راجع هينكه، الموضع المذكور ص ١٩.
٨ - «كتاب الأمثال»، وهو في قسمين رئيسيين، أورد فيهما أمثالا مرتبة على حسب طبقات الناس وحرفهم الخ، كوبريلى ١٣٥٣/ ٣ (الأوراق ٥٥ - ١١٨، نسخت سنة ٦٣٥ هـ). أوله: «الحمد لله ... الأمثال مصابيح الأقوال وقد ضربها الله ... وهذا كتاب في الأمثال قد أخرجناه على قسمين، قسم في سائر الفنون والغرر، والفصوص من أمثال طبقات الناس، وقسم في نكت الأفعال على «أفعل من كذا» مما يتضمنه كتاب حمزة الأصفهانى ... »، وتوجد مخطوطة أخرى بهذا الاسم في تونس، الأحمدية ٤٧٩٤ (١٥ ورقة، نسخت سنة ١٠٣٧ هـ، انظر فهرس منصور ٢٢ - ٢٣)، هل مطابقة لمخطوطة كوبريلى؟
٩ - «خاصّ الخاصّ»، أمثال مجموعة على القاعدة التى جرى عليها في كتاب الأمثال السابق، وهى على ثلاثة أقسام، أورد في القسم الثالث منها أمثالا لم تجئ فى «الدرة الفاخرة» لحمزة الأصفهانى/، وجعله في ٨ أبواب، وأهداه إلى أبى الحسن مسافر بن الحسن.
ويوجد مخطوطا في أيا صوفية ٤٨٢٤/ ٢ (٦٥ أ- ١٣٧ أ، نسخت سنة ٥٢٣ هـ، انظر فهرس المخطوطات ١/ ٤٤٧)، باريس ٥٩٣٤/ ٣ (الأوراق ١١١ - ١٢٠، انظر فجدا ص ٣٨٨)،','1','440',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (437,'القاهرة، دار الكتب، أدب ٥٧١٦ (نسخت سنة ١٢١٤ هـ، انظر الفهرس ٤/ ب ٢، ٤٦)؛ راجع هينكه، الموضع المذكور ص ١٨.

١٠ - «كتاب الأمثال بآيات من القرآن وأخبار عن النبى وكلام العرب وأدباء العجم» سراى، خزينة ١١٥٠/ ٥ (٤٥ ب- ٦٤ أ، من القرن ٨ هـ، راجع فهرس<span data-type="title" id=toc-585> المخطوطات </span>١/ ٤٢٧). أوله: «أما بعد حمد الله الذى خلق ورزق ووفق ... فإن أحسن الكلام الأمثال التى يجمع فيها الإيجاز والإصابة، وخير ما يوجد ... ».
ولا بد بعد من الفحص عما إذا كان بين هذا التصنيف و «كتاب أحاسن كلم النبى» صلة.
١١ - «التمثيل والمحاضرة في الحكم والمناظرة»، أهداه لشمس المعالى قابوس بن وشمكير، وهو مجموعة من الأمثال مرتبة على المعانى.
المخطوطات:
قره جلبى ٣١٢ (١٧١ ورقة، نسخت سنة ٥٨٠، انظر
T. R. Topuzoglu, Istan- bul Manuscriptsofwor ks (otherthan Yatlmatal- dahr) by Tha''alibiin: Isl. (Quart. ١٧/ ١٩٧٣/ ٦٤
، فيض الله ١٥٨٣ (١٧٦ ورقة، نسخت سنة ٥٩٧ هـ، انظر المقال السابق، ص ٦٥؛ فهرس المخطوطات ١/ ٤٣٩)، أيا صوفية ٤٢٤٩/ ١ (الأوراق ١ - ١٢٠، نسخت سنة ٨٥٩، انظر طوبوزوغلو، المقال السابق الذكر، ص ٦٥)، كوبريلى ١٢٢٩ (١٦٦ ورقة، نسخت سنة ١٠٠١ هـ، انظر المقال السالف، ص ٦٥)، جامعة استنبول ٤١٦) ١٣١٩ A.Y.، ورقة، نسخت سنة ١٠٣٨ هـ، انظر المقال السابق، ص ٦٥)، أسعد ٢٥٥٠/ ١ (الأوراق ١ - ١٠١، نسخت سنة ١٠٦٥ هـ، انظر المقال نفسه، ص ٦٥)، و ٣٥٤٢ (٢١٤ ب- ٢٢٢ أ، من القرن ٦ هـ)،','1','441',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (438,'فاتح ٣٧٢٤ (١٩٩ ورقة، انظر المقال المذكور آنفا، ص ٦٦؛ فهرس المخطوطات ١/ ٤٣٩)، نور عثمانية ٤١٤٥ (١٠٣ ورقة، انظر المقال الآنف الذكر، ص ٦٦)، رئيس الكتاب ١١٥٦/ ٢ (مختصر، ١١ ب- ٣٣ أ، من القرون ١٠ هـ)، بغدادلى وهبى ١٥٤١ (٨٢ ورقة، من القرن ١١ هـ)، أيا صوفية ٤٢٤٩/ ١، قونيه، يوسف أغا ٤٩٠٢ (١٣٦ ورقة، نسخت سنة ٥٨٧ هـ)، الإسكوريال ٥٤١ (جزء، ٥٤ ورقة)، باريس ٥٩١٤ (الأوراق ١ - ١٦٣، نسخت سنة ٥٨٣ هـ، انظر فجدا ص ٦٦٥)، و ٦٠١٩ (١٢٥ ورقة، نسخت سنة ٥٦٩ هـ، انظر فجدا ص ٦٦٥)، و ٦٠١٩ (١٢٥ ورقة، نسخت سنة ٥٦٩ هـ، انظر فجدا ص ٦٦٥)، ليدن ١١٥) ٤٣٥ Or.، ورقة، نسخت سنة ٦٢٧ هـ، انظر فورهوف ٣٦٣)، برنستون، جاريت ١٨٦ (١٣٨ ورقة، نسخت سنة ١٠١١ هـ)، طشقند ١٨٤٨/ ٣ (١١٩ ب- ١٩٣ ب، نسخت سنة ١١٤٠ هـ، انظر الفهرس ٢/ ٣٩٥ - ٣٩٦، رقم ١٧٧٨)، القاهرة، دار الكتب، أدب ٦٠٠ (١٢٢ ورقة، من القرن ٧ هـ، انظر الفهرس الثانى ٣/ ٦٨؛ فهرس المخطوطات ١/ ٤٣٩)، وأدب ٤٩٢ (ضمن مجموع، انظر الفهرس الثانى ٣/ ٦٨)، الاسكندرية، البلدية ١٢٢٨ ب (٢٠١ ورقة، من القرن ٧ هـ، انظر فهرس المخطوطات ١/ ٤٣٠)، الأزهر، أدب ٥٦٢ (١٢٠ ورقة، نسخت سنة ١٠٨٤ هـ، انظر الفهرس ٥/ ٥٨)، وأدب ٥٧٠ (١١٩ ورقة، نسخت سنة ١٣١١ هـ، انظر الفهرس ٥/ ٥٨)، المدينة، عارف حكمت، مجموع ١١ (قديم) أو مجموع ١٧٧ (الأوراق ٥١ - ١٤٨، نسخت سنة ١٠٦٦ هـ، انظر المنتخب لكحالة ٩٦).','1','442',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (439,'أ) مختصر منه لمجهول، باسم «جمع المنادرة في التمثيل والمحاضرة» لاله إسماعيل ٥٩٧ (الأوراق ١ - ٤١).
١٢ - «لباب الآداب»، ويتألف من ثلاثة أقسام:
١) أسرار اللغة وجوامعها وطرائف العربية وخصائصها، ٢) لطائف الألفاظ والمخاطبات والمكاتبات وبدائعها ومحاسنها وقلائدها، ٣) عيون الأشعار وأحاسنها وفصوصها وفرائدها.
فجعل القسم الأول للغة، والثانى للنثر الفنى، والثالث للأدب.

<span data-type="title" id=toc-586>المخطوطات:</span>
أسعد ٢٨٧٩ (١٤٧ ورقة، نسخت سنة ٦٥٣ هـ)، برلين ١٢٨) ١٩٨٥ Or.Oct.، ورقة، نسخت سنة ١٣١٩ هـ)، بغداد، كلية الآداب (سابقا معهد الدراسات الإسلامية، انظر القائمة المطبوعة على الآلة الكاتبة ص ١٠٨، رقم ١٢١٧).
المتحف العراقى ٨٧٥٦ (القسمان الثانى والثالث، ١٤٤ ورقة، نسخت سنة ١٣٣٠ هـ، انظر أسامة ناصر النقشبندى في: المورد ٤، ١/ ١٩٧٥/ ١٩٨)، /

<span data-type="title" id=toc-587>سهل بن المرزبان</span>
أبو نصر، كان لغويا أديبا شاعرا (انظر فصل الأدب) من أهل أصفهان، سكن نيسابور. ويبدو أن وفاته كانت نحو سنة ٤٣٠/ ١٠٣٩.
وأخباره في يتيمة الدهر للثعالبى ٤/ ٣٩١ - ٣٩٤.- الأعلام للزركلى ٣/ ٢١٠. له «كتاب الألفاظ» ويوجد مخطوطا في المدينة، عارف حكمت، لغة ٤ (٨١ ورقة، نسخت سنة ٧٦٦ هـ، انظر المنتخب لكحالة ٤٦). وفي مخطوطة أحمد الثالث، ٢٤٩٧/ ٢ (٢٦ أ- ٣٦ أ) كتاب في المترادفات اسمه «كتاب الألفاظ»، منسوب إلى منصور بن سهل بن محمد بن المرزبان الحاسب. ويجب البحث عن علاقة المخطوطتين بعضهما ببعض (وكان كذلك في كتاب «المنتهى فى الكمال» لمحمد بن سهل بن المرزبان الكرخى باب لم يصل إلينا، عنوانه «كتاب الألفاظ»، انظر تاريخ التراث العربى ٢/ ٧٦).','1','443',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (440,'<span data-type="title" id=toc-588>ابن دوست</span>
هو أبو سعيد عبد الرحمن بن محمد بن محمد بن عزيز، ولد سنة ٣٥٧/ ٩٦٨، كان أديبا ولغويا مرموقا، من أهل نيسابور. وكان معروفا أيضا بنظم الشعر. ولا نعرف إلى الآن من كتبه العديدة إلا اسم كتاب واحد. توفى سنة ٤٣١/ ١٠٤٠.
وأخباره في يتيمة الدهر للثعالبى ٤/ ٤٢٥ - ٤٢٨، وإنباه الرواة للقفطى ٢/ ١٦٧، وفوات الوفيات للكتبى ١/ ٥٤٩ - ٥٥٠؛ وبغية الوعاة للسيوطى ٣٠٢.- ومعجم المؤلفين لكحالة ٥/ ١٨٨.
وله من الكتب «الرد على الزجاجى فيما استدركه على ابن السكيت في إصلاح المنطق»، ذكره الكتبى، في الموضع المذكور.

<span data-type="title" id=toc-589>الأسود الغندجانى</span>
هو الحسن بن أحمد بن محمد، من أهل غندجان (ولم يضبطها غندجان إلا ياقوت في معجم البلدان) بالقرب من الأهواز، ويعرف أيضا بأبى محمد الأعرابى، وكان لغويا نحويا، أديبا، نسّابة، عالما بتاريخ العرب وشعرها في الجاهلية.
كان تلميذا لأبى الندى بن الغندجانى (المتوفى نحو سنة ٤١٠/ ١٠٢٠، انظر ص ٢٢٧ آنفا)، ولعله عاش أكثر عمره في شيراز حظيّا عند الوزير البويهى أبى منصور بهرام بن مافنه (المتوفى سنة ٤٣٣/ ١٠٤٢)، الذى أهدى إليه أيضا كتبه.
وذكر القفطى أنه توفى سنة ٤٣٦/ ١٠٤٤.

<span data-type="title" id=toc-590>مصادر ترجمته:</span>
إرشاد الأريب لياقوت ٣/ ٢٢ - ٢٤، ٦/ ٢٩٧؛ ومعجم البلدان له ٣/ ٨٢٠؛ إنباه الرواة للقفطى ٤/ ١٦٨ - ١٦٩، ١٨١؛ لسان الميزان لابن حجر ٢/ ١٩٤؛ ابن قاضى شهبة، المخطوط ص ٢٤٦؛ بغية الوعاة للسيوطى ٢١٧؛ خزانة الأدب للبغدادى؛ Br.GI ,١٠٢ ,SI ,٤٠ ,٥٠٣ - .٢١ /١ معجم المؤلفين لكحالة ٣/ ١٩٧.

<span data-type="title" id=toc-591>آثاره:</span>
١ - «كتاب الخيل» («مرتب على حروف المعجم»، كذا ذكره ياقوت وغيره)،','1','444',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (441,'ووصل إلينا باسم «كتاب أسماء خليل العرب وأنسابها وذكر فرسانها»، ويوجد مخطوطا/ فى القاهرة، دار الكتب، مجموع ٢ ش (ضمن مجموع، نسخت سنة ١٢٩٥ هـ، انظر الفهرس الثانى ٥/ ٢٥)، وفي بغداد، المتحف العراقى (مجموعة الآلوسى) ٨٥٩٥ (٣٨ ورقة، حديثة، انظر أسامة ناصر النقشبندى في: المورد ٤، ١/ ١٩٧٥/ ١٩٤، رقم ٢٤٩)، نقل عنه الصغانى في «ما بنته العرب على فعال» فى مجلة المجمع العلمى العربى بدمشق ٣٩/ ١٩٦٤/ ٣٠٣؛ راجع
G. Levi Della Vida, Les Livresdeschevaux, Leiden ١٩٢٨, S. ٤١
نشره م. ع. سلطانى، دمشق ١٩٨٢.
٢ - «نزهة الأديب في الرد على أبى على (الفارسى) فى التذكرة»، وهو نقد «التذكرة القصرية» لأبى على الحسن بن أحمد الفارسى (المتوفى سنة ٣٧٧/ ٩٨٧، انظر فصل النحو)؛ ذكره ياقوت وغيره (انظر أيضا أبيات المغنى للبغدادى ٤/ ١٢٣)، كان من مصادر الصغانى في التكملة (انظر ١/ ٨) والعباب (انظر ١/ ٣١).
وقيل إن أبا اليمن تاج الدين زيد بن الحسن الكندى (المتوفى سنة ٦١٣/ ١٢١٧، انظر معجم المؤلفين لكحالة ٤/ ١٨٩) حاول الرد عليه فأخفق (انظر إنباه الرواة للقفطى ٤/ ١٦٩).
٣ - «كتاب السّلّ والسرقة»، وبحسب ما نقل عنه في موضعين من الخزانة (٢/ ٨٣، ٤/ ١٥٣) يحتمل أنه كان كتاب منتخبات مع شرح لغوى لأبيات الشعر. ونقل عنه البغدادى أيضا في «حاشية على شرح بانت سعاد» ١/ ١٢٦ - ١٢٧. وكانت توجد نسخة من الكتاب في القرن ٧/ ١٣ بإحدى مكتبات حلب (انظر بولس سباط في:
(MIE ٤٩/ ١٩٤٦/ ٢٩, No. ٥٧٤
، ولعل هذا الكتاب هو الذى نقل عنه أبو حيان الاندلسى باسم «كتاب زلّات العلماء» فى تذكرة النحاة، الجزء الثانى، مخطوطة الرباط، المكتبة العامة، أوقاف ٢١٤، ص ٣٣٠ - ٣٣٢ (العنوان مذكور في الآخر).
٤ - «كتاب في مياه العرب» انظر فصل الجغرافية.
٥ - «كتاب في أسماء الأماكن» انظر فصل الجغرافية.','1','445',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (442,'<span data-type="title" id=toc-592>يحيى بن أحمد الفارابى</span>
أبو زكرياء، لغوى أديب من أهل فاراب (أو باراب)، روى أحاديث عن أبى عبد الرحمن عبد الله بن عبيد الله بن شريح البخارى؛ وروى عنه الحسن بن منصور المقرئ. يحتمل أنه ظهر أمره في الشطر الأول من القرن ٥/ ١١.
وأخباره في الأنساب للسمعانى ٢ ٢/ ٢٣؛ إرشاد الأريب لياقوت ٧/ ٢٦٨، ومعجم البلدان له ١/ ٤٦٢؛ بغية الوعاة للسيوطى ٤١٠؛ ابن قاضى شهبة، المخطوط ص ٥٢٥.
وله «كتاب المصادر في اللغة»، ذكرته المصادر السابقة.

<span data-type="title" id=toc-593>الخوافى</span>
هو أبو القاسم مهدى بن أحمد النيسابورى الأديب، عاصر الباخرزى وكان يكبره سنا، ووصفه الباخرزى بأنه أديب جليل. عاش في الشطر الأول من القرن ٥/ ١١ وتوفى قبل سنة ٤٥٠/ ١٠٥٨ أو نحوها./

<span data-type="title" id=toc-594>مصادر ترجمته:</span>
دمية القصر للبلخارزى ٢ ٢/ ٤٩٥ - ٤٩٦؛ إنباه الرواة للقفطى ٣/ ٣٣٢ - ٣٣٣؛ بغية الوعاة للسيوطى ٣٩٩ (خطأ: الجواليقى).- الأعلام للزركلى ٨/ ٢٥٦؛ معجم المؤلفين لكحالة ١٣/ ٢٦.
له «كتاب المختصر» (١٥٣)، وهو كتاب في المترادفات للترسّل، مقسم إلى أبواب صغيرة كثيرة. أوله: «أما بعد حمد الله ذى الجلال ... فإن أوّل ما أعهد إلى الناظر فى هذا الكتاب أن لا يتسارع إلى ملامى قبل الوقوف على مرامى».
وذكر ممن تقدّمه وممن اقتدى بهم أبا منصور عبيد الله بن جميل الأشلّ،
_________
(١٥٣) ذكر اسمه خطأ على صفحة العنوان من المخطوطة «التوسع في الترسّل»، وهو تنقيح «كتاب الترسّل» للحمادى الذى صنعه أبو يوسف يعقوب بن أحمد النيسابورى (لعله توفى سنة ٤٧٤/ ١٠٨٢، انظر دمية القصر للباخرزى. (Br.GI ,٢٨٧؛ ٢٤٢ - ٣٦ /٢، ١١٥ /١ ٢','1','446',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (443,'وعبد الرحمن بن عيسى الهمذانى (المتوفى سنة ٣٢٠/ ٩٣٢، انظر ص ١٩٣ آنفا).
ويبدو أن القفطى (الإنباه ٣/ ٣٣٣) رأى كتاب الخوافى، وقد سماه «شرح ألفاظ عبد الرحمن (بن عيسى) الهمذانى» وأثنى عليه.
ويوجد مخطوطا في أيا صوفيه ٤٧٦٠/ ١ (١ أ- ٧٢ ب، من القرن السادس للهجرة).','1','447',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (444,'<span data-type="title" id=toc-595>٥ - اللغويون فى جزيرة العرب ومصر</span>
<span data-type="title" id=toc-596>أبو على الهجرىّ</span>
هو هارون بن زكرياء، من أهل هجر البحرين، عاش مدة فى المدينة ثم فى مكة. كان لغويا نحويا أديبا.
ومن أساتذته ومن روى عنهم ابن الأعرابى وأبو ذكوان؛ وممن روى عنه ثابت بن حزم السرقسطى والحسن بن أحمد الهمدانى. والظاهر أنه توفى حوالى آخر القرن ٣/ ٩ وأخباره فى إرشاد الأريب لياقوت ٧/ ٢٣٤؛ وبغية الوعاة للسيوطى ٤٠٥.
و«أبو على الهجرى وأبحاثه فى تحديد المواضع» لحمد الجاسر، الرياض ١٩٦٨.

<span data-type="title" id=toc-597>آثاره:</span>
١ - «التعليقات والنوادر»، وصل إلينا بعضه، القاهرة، دار الكتب، لغة ٢٤٢ (مفقود، ومنه نسخة مصورة ٢٨١ ورقة، من القرن ٧ هـ، ونسخة خطية حديثة برقم: لغة ٣٥٤، انظر الفهرس الثانى ٢/ ٨)، كلكتا، جمعية البنغال الآسيوية (٢٨١ ورقة، انظر:
. (M. Z. Siddiqiin: ٢٢. Or. Congr. II, ٢٤٢ - ٢٤٥
وكان الكتاب من مصادر السمهودى فى «وفاء الوفا»؛ والهمدانى فى صفة جزيرة العرب؛ والبكرى فى معجم ما استعجم؛ وابن سيده فى المخصص، ونقل عنه ابن مكى الصقلى، انظر مقال حمد الجاسر في: العرب (الرياض) ١١، ٥ - ٦/ ١٩٧٦/ ٤٦٣ - ٤٦٧؛ تاج العروس ٢ ٢/ ٤٠١ (دجب)، ٣/ ١٥٢ (شقب)، ٥/ ٢٥٥ (ديث).
وموضوع الكتاب الرئيسى هو اللغة. ويحتوى على مادة مهمة جدا لجغرافية شبه جزيرة العرب، وللشعر.
ونشر حمد الجاسر مختارات منه في كتابه الآنف الذكر، ص ١١١ وما بعدها؛','2','463',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (445,'ونشره ح. ع. الحمّادى، الجزء الأول بغداد ١٩٨٠، الجزء الثانى بغداد ١٩٨١ (انظر ص. هامل في: المورد ١١، ١/ ١٩٨٢/ ١٨٥ - ١٨٦).
٢ - «منتخل (أو منتخب) الأراجيز»، نقل عنه المؤلف في كتابه السالف الذكر، انظر حمد الجاسر، الموضع المذكور، ص ١٠٠ - ١٠١.
٣ - «كتاب العقيق»، نقل عنه السمهودى في وفاء الوفا، انظر الجاسر، الموضع المذكور، ص ١٠١ - ١٠٣.

<span data-type="title" id=toc-598>ابن هشام</span>
هو أبو محمد عبد الملك بن هشام بن أيوب الذّهلىّ، من أهل البصرة وأقام بمصر. كان مؤرخا وعدّ أيضا من النحويين واللغويين. توفى سنة ٢١٨/ ٨٣٣، وقيل سنة ٢١٣/ ٨٢٨ (انظر تاريخ التراث العربى ١/ ٢٩٧ - ٢٩٩).
وأخباره في إنباه الرواة للقفطى ٢/ ٢١١ - ٢١٢؛ وبغية الوعاة للسيوطى ٣١٥؛ وحسن المحاضرة له ١/ ٢٢٨.- أحمد نصيف الجنابى، «الدراسات اللغوية والنحوية في مصر منذ نشأتها حتى نهاية القرن الرابع الهجرى»، القاهرة ١٩٧٧، ص ١٢٧ - ١٣٢.
ومن آثاره «كتاب ما وقع في أشعار السير من الغريب»، ذكره القفطى.

<span data-type="title" id=toc-599>عمارة بن زيد</span>
هو عمارة بن عبد الرحمن بن زيد بن أسلم العدوى المدنى (انظر لسان الميزان لابن حجر ٤/ ٢٧٨)، وكان أبوه قد صنف تفسيرا لغويا للقرآن (انظر تاريخ التراث العربى ١/ ٣٨). وحوالى آخر القرن الثانى/ الثامن استخرج عمارة أسماء الله التسعة والتسعين من القرآن.
وقد وصل إلينا مصنّفه هذا باسم «أسماء الله عزّ وجل التسعة والتسعون اسما مستخرجة من كتاب الله عزّ وجل»، ويوجد مخطوطا في شهيد على ١٣٤٥/ ٩ (٣٥ أ- ٣٦ ب، من القرن ٨ هـ)./','2','464',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (446,'<span data-type="title" id=toc-600>الاخفش</span>
هو أبو عبد الله أحمد بن عمران بن سلامة الألهانى، كان لغويا نحويا شاعرا، أصله من الشام، ورحل إلى العراق لطلب العلم، ثم استقر به المقام آخر الأمر في مصر. وكانت وفاته حوالى سنة ٢٦٠/ ٨٧٤.
وأخباره في طبقات النحويين للزبيدى ٢١٤؛ وتاريخ بغداد ٤/ ٣٣٣؛ وإرشاد الأريب ٢/ ٥ - ٦؛ والوافى بالوفيات للصفدى ٧/ ٢٧٠ - ٢٧١؛ وبغية الوعاة للسيوطى ١٥٢؛ وابن قاضى شهبة، المخطوط ص ١٨٩.- ومعجم المؤلفين لكحالة ٢/ ٣٥.
وله «تفسير غريب الموطّأ»، وقد وصل إلينا في مخطوطتين، انظر تاريخ التراث العربى J.Schachtin: Arabica ١٤ /١٩٦٧ /٢٤٤ - ٢٤٥؛ ٤٦٠ /١

<span data-type="title" id=toc-601>كراع النمل</span>
هو أبو الحسن على بن الحسن الهنائى، من أهل مصر، لغوى خلط المذهبين، ومال مع ذلك إلى البصريين، وكانت كتبه مرغوبا فيها خاصة في مصر والمغرب.
توفى سنة ٣١٠/ ٩٢٢.

<span data-type="title" id=toc-602>مصادر ترجمته:</span>
ابن النديم ٨٣؛ إرشاد الأريب لياقوت ٥/ ١١٢؛ إنباه الرواة للقفطى ٢/ ٢٤٠؛ بغية الوعاة للسيوطى ٣٣٣ - ٣٣٤؛ ابن قاضى شهبة، المخطوط ص ٤١٦.-
Flugel, Gramm. Schulen ١٩٩ - ٢٠٠; Rescher, Abri BII, ٢٠٤; Br. SI, ٢٠١
؛ معجم المؤلفين لكحالة ٧/ ١٧؛ أحمد نصيف الجنابى، المرجع المذكور، ص ٢٠٩ - ٢٤٦.

<span data-type="title" id=toc-603>آثاره:</span>
١ - «(المنتخب وال) - مجرد في فقه اللغة»، وذكره ابن النديم باسم «مجرد الغريب».','2','465',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (447,'ومصدراه الرئيسيان، دون أن ينص عليهما، هما: الغريب المصنف لأبى عبيد، وإصلاح المنطق لابن السكيت.
وقيل إن الكتاب كان مصنفا على غرار كتاب العين للخليل، إلا أن بعضه كان مرتبا على الحروف مثل كتاب الجيم لأبى عمرو الشيبانى.
وكان من مصادر لسان العرب لابن منظور، والتكملة والعباب للصغانى، والمخصص لابن سيده، وكتاب الحيوان للدميرى، وغير ذلك.
ويوجد مخطوطا في دار الكتب بالقاهرة، لغة ٨٥٨ (١٤٥ ورقة، من القرن الثامن للهجرة، انظر الفهرس الثانى ٨/ ١٨، وكتب دراسة في هذه المخطوطة أحمد مختار عمر في: البحث العلمى (مكة) ٣/ ١٩٨٠/ ٤٠٧ - ٤١٧. وانظر في باب «كتاب الإبل» حسين نصار في: مجلة المجمع العلمى العربى بدمشق ٣٦/ ١٩٦١/ ٥٩٧، وانظر أيضا أحمد نصيف الجنابى، المرجع المذكور، ص ٢٣٨ - ٢٤٦، ٥٣٤ - ٥٣٩.
٢ - «كتاب المنضّد في اللغة»، أفاد منه ياقوت (معجم البلدان ٢/ ٥٣٠، ٤/ ٦٠٨) فى نسخة بخط المؤلف.
٣ - «كتاب المنجّد»، ملكه القفطى، ويعتمد (دون أن يذكر مصدره) اعتمادا كبيرا على الغريب المصنف لأبى عبيد، وقد وصل إلينا.

<span data-type="title" id=toc-604>مخطوطاته:</span>
لندن. المتحف البريطانى ٨١) ٣٠٧٣ Or.، ورقة، نسخت سنة ١٢٩٧ هـ، انظر الملحق رقم ٨٣٥)، و) ٢/ ٤١٧٩ Or. الأوراق ١٦ - ٨٨، نسخت سنة ١٢٤٩ هـ، انظر الملحق رقم ٨٣٦، باسم «المنضّد»)، / القاهرة، دار الكتب، لغة ٢٦٥/ ٢ (٤٧ أ- ١٠٩ ب، نسخت سنة ٥٨٥ هـ)، و ٤٩٠ (١٦٧ ورقة، نسخت سنة ١٣٣٨ هـ)، ومجموع ٢٣٤ (١ أ- ١١٥ أ، نسخت سنة ٧٧٥ هـ، انظر الفهرس الثانى ٢/ ٤١).
نشره أحمد مختار عمر وضاحى عبد الباقى، القاهرة ١٩٧٦، انظر في هذه النشرة أحمد مطلوب في: البحث العلمى (مكة) ١/ ١٩٧٨/ ٢٧١ - ٢٨٠؛ ويحققه للنشر (باسم المنضّد) ص. م. العزاوى، بغداد (انظر المورد ٥،','2','466',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (448,'٢/ ١٩٧٦/ ٣٢٧)، انظر أيضا أحمد نصيف الجنابى، المرجع المذكور، ص ٢١٢ - ٢٣٨، ٥٣٣ - ٥٣٤.
٤ - «كتاب الأوزان (على وزن الأفعال) أو «كتاب أمثلة الغريب على وزن الأفعال»، وهو معجم لصيغ الجمع على وزن «الأفعال»، ذكره ياقوت والقفطى.
٥ - «كتاب الفريد»، ذكره ابن النديم.
٦ - «كتاب المصحّف»، ذكره ياقوت.
٧ - «كتاب المنظّم»، ذكره ياقوت، وكان إلى جانب «كتاب المجرّد» و «كتاب المنجّد» من مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) فى كتابه «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر مجلة المجمع العلمى العربى بدمشق ٣٥/ ١٩٦٠/ ٥٤٢)؛ وكان أيضا من مصادر على بن سعود في مصنفه «كتاب تخريج الدلالات السمعية» (انظر تاريخ التراث العربى ٩/ ٢٥٠)، انظر قائمة مصادره، ورقة ٢٣٠ ب.

<span data-type="title" id=toc-605>النحاس</span>
هو أبو جعفر أحمد بن محمد بن إسماعيل (المتوفى سنة ٣٣٨/ ٩٥٠)، وكان يغلب عليه النحو (انظر فصل النحو).

<span data-type="title" id=toc-606>مصنفاته اللغوية:</span>
١ - «خلق الإنسان»، ذكره المؤلف نفسه في «عمدة الكتاب» (انظره) فى الورقة ١٣ بعد الاوراق من آخر المخطوطة. وكان من مصادر «غاية الإحسان في خلق الإنسان» للسيوطى، برلين ٢ (٩٨٠ Spr.) ٧٠٣٨ أ، ٥ أ، ٦ أ، ١٠ ب، ١٤ ب، ١٥ أ، ١٧ أ.
٢ - «كتاب الخيل»، ذكره المؤلف نفسه في كتابه عمدة الكتّاب، الورقة ١٣ بعدّ الأوراق من آخر المخطوطة.
٣ - «كتاب اشتقاق أسماء الله جلّ وعز»، ذكره القفطى في إنباه الرواة ١/ ١٠١؛ ووجدت منه نسخة فى القرن ٧/ ١٣ بإحدى مكتبات حلب، انظر بولس سباط في:
MIE ٤٩/ ١٩٤٦/ ٦, No. ١٠٣','2','467',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (449,'٤ - «كتاب الاشتقاق»، ذكره القفطى في الإنباه ١/ ١٠٣؛ وياقوت في إرشاد الأريب ٢/ ٧٣. نقل عنه البكرى في: «فصل المقال» ٣٤، والفهرى في تحفة المجد الصريح، مخطوطة القاهرة (انظر ص ١٤٤ آنفا) ص ٣٢، ٩٠.

<span data-type="title" id=toc-607>وله في لغة القرآن:</span>
١ - «معانى القرآن»،

<span data-type="title" id=toc-608><span data-type="title" id=toc-609>مخطوطاته:</span></span>
بورسه) ١٢٩ Orhan Camii، المجلد ٣، من سورة الحج (٢٢) إلى سورة الفتح (٤٨)، ٢٠١ ورقة، نسخت سنة ٥٥٠ هـ)، القاهرة، دار الكتب، تفسير ٣٨٥ (المجلد ١، من أوله إلى سورة مريم (١٩)، ٢٣٣ ورقة، من القرن الخامس للهجرة، انظر الفهرس الثانى ١/ ٦٢؛ الملحق ٣/ ٧٤)، وثمة قطعة، من المحتمل انها من معانى القرآن، بدمشق، الظاهرية ١٨١ (٥ ورقات، قديمة، انظر عزة حسن ١/ ٢٩٩)، انظر أيضا أحمد نصيف الجنابى، المرجع المذكور، ص ٤٣٠ - ٤٥٠؛ ومقاله: «رؤية جديدة في معانى القرآن للنحاس» فى: المورد ٧، ٢/ ١٩٧٨/ ١١ - ٢٩.
٢ - «الناسخ والمنسوخ في القرآن الكريم»،
مخطوطاته:
سراى أحمد الثالث ١٤٢ (٢٧٥ ورقة، نسخت سنة ٧٣٥ هـ)، ينى جامع ١٥٣ (٢٨٥ ورقة)، كوتاهية، مكتبة وحيد باشا ٢٣٣٧/ ١ (اب- ١١٥ ب، نسخت سنة ٦١٧ هـ، انظر شيشين، نوادر ٣/ ٢٠)، برلين، ٣٠٩٥ Or.fol.، لندن، المتحف البريطانى ١٣٣) ٣٨٧٩ Or.، ورقة، من القرن السابع للهجرة، انظر الملحق رقم ١٢٨)، الإسكوريال ١٢٥٩ (٢٢٠ ورقة، نسخت سنة ٦٤٢ هـ)،','2','468',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (450,'دمشق، الظاهرية ٨٨٢٧ (٧٢ ورقة، نسخت سنة ٣٧٦ هـ، انظر عزة حسن ١/ ٤٠٥)، القاهرة، التيمورية، تفسير ١٢٩ (انظر الفهرس ١/ ٢٣٩)، اليمن، الغرفة بدون/ رقم (١١٦ ورقة، نسخت سنة ٧٣٧ هـ، ومنها مصورة بمعهد المخطوطات)؛ نشر في القاهرة ١٣٢٣ هـ، ١٩٢٣؛ أحد مصادر السيوطى في الدر المنثور.

<span data-type="title" id=toc-610>بريّة</span>
هو بريّة بن أبى اليسر الرياضى، كان شاعرا (انظر ابن النديم ١٦٤) ولغويا نحويا، ظهر أمره على عهد المعز لدين الله الفاطمى (٣٤١/ ٩٥٣ - ٣٦٥/ ٩٧٥) بمصر.
Br. GI, ١٣٢; Rescher, Abri BII, ٢٣٨.
وله كتاب «كتاب تلقيح العقول في الأمثال والحكم»، فى ١٥٧ بابا صغيرا (انظر حاجى خليفة، (Sellheim ,Sprichworter ١٢٧؛ ٤٨١ ومنه نسخة في مكتبة فيض الله ٢١٥٨/ ٤ (٢٦٠ أ- ٢٨٧ ب، نسخت سنة ١٠٤٤ هـ)، وفي ليدن) ٤٤٢ Or.، الأوراق ١ - ٦٦، نسخت سنة ١٠٣٣ هـ، انظر فورهوف ص ٣٦٢)، ورواية ذات ١٤٨ بابا في راغب ١٤٨٧ (١٠١ ب- من القرن ١٠ هـ).

<span data-type="title" id=toc-611>النّجيرمىّ</span>
هو أبو إسحاق إبراهيم بن عبد الله، من أهل تجيرم بفارس، وعاش في مصر.
كان لغويا نحويا ناثرا (انظر فصل الأدب) أخذ عن أبى إسحاق الزجّاج ببغداد. ومن تلامذته أبو (ال) - حسين المهلبى؛ وجنادة الهروى وغيرهما.
وكان بمصر في خدمة الإخشيديين محمد بن طغج (المتوفى سنة ٣٣٤/ ٩٤٦) وكافور (المتوفى سنة ٣٥٧/ ٩٦٨).','2','469',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (451,'<span data-type="title" id=toc-612>مصادر ترجمته:</span>
ابن خلكان ١/ ٥٤٧؛ إرشاد الأريب لياقوت ١/ ٢٧٧ - ٢٧٩؛ إنباه الرواة للقفطى ١/ ١٧٠ - ١٧١؛ الوافى بالوفيات للصفدى ٦/ ٣٤؛ بغية الوعاة للسيوطى ١٨١؛ النجوم الزاهرة لابن تغرى بردى ٤/ ٦؛ ابن قاضى شهبة، المخطوط؛ Br.SI ,٢٠١ - ٢٠٢؛ ١٤٥ - ١٤٤ الأعلام للزركلى ١/ ٤٢؛ معجم المؤلفين لكحالة ١/ ٥٥

<span data-type="title" id=toc-613>آثاره:</span>
١ - «كتاب أيمان العرب وطلاقها في الجاهلية»، فى القسم عند قدامى العرب،

<span data-type="title" id=toc-614>مخطوطاته:</span>
شهيد على ٢٣٩٤/ ٢ (الأوراق ٢٣٩ - ٢٤١، انظر فهرس المخطوطات ٢، رقم ٧٦)، قونبة، يوسف أغا ٤٨٧٩ (ص ٤٥٦ - ٤٦٠، نسخت سنة ١٠٨٣ هـ، انظر، (N.Hocain: Sarkiyat Mecm IV ,١٢١ .... القاهرة، دار الكتب، مجموع ٢٣٤ (الأوراق ١٦٤ - ١٧٠، انظر الفهرس ٧/ ٢٨٢)، التيمورية، لغة ٣٣١ (١٥٩ أورقة، نسخت سنة ٩٧٥ هـ)، طلعت بدار الكتب، لغة ٤٠٣ (٨ ورقات، نسخة حديثة)، المدينة، عارف حكمت (انظر، (O.Spiesin: ZDMG ٩٠ /١٩٣٦ /١٢٠ ييل ١٣) ٢٧٩ - L ورقة، انظر نيموى رقم ١٦٢٤)؛ نشره محب الدين الخطيب، القاهرة ١٩٢٤؛
Ch. D. Matthews, The Kitab Aimanal- arab ... in: JAOS ٥٨/ ١٩٣٨/ ٦١٥ - ٦٣٧
، وانظر أيضا
I. Goldziher, Notice Surlalitterature des Aimanal- '' Arab (Sermentsdesancie ns Arabes) in: Melanges Hartwig Derenbourg, Paris ١٩٠٩, ٢٢١ - ٢٣٠./
٢ - «الفوائد»، نقل عنه السيوطى من نسخة بخط المؤلف، فى المزهر ٢/ ١١٢، ٣٠٤، ٣١٩، ٣٣٧، ٣٤٩، ٣٦٠، ٤٤٥.','2','470',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (452,'٣ - «النوادر»، نقل عنه السيوطى في المزهر ٢/ ٢٩١ - ٢٩٢ (والأرجح انه هو الكتاب السابق نفسه).
٤ - «الأمالى»، نقل عنه ياقوت في معجم البلدان ١/ ٦٠٠.

<span data-type="title" id=toc-615>أبو العلاء</span>
هو عبد العزيز بن عبد الرحمن (بن حسين) بن (ال) - مهذّب، ويعرف أيضا بابن أبى الجليل اللغوى، نحوى ولغوى، أخذ عن أبى (ال) - حسين المهلبى. توفى على ما يظهر حوالى سنة ٣٠٩٣/ ١٠٠٣.

<span data-type="title" id=toc-616>مصادر ترجمته:</span>
إنباه الرواة للقفطى ٢/ ١٨٤ - ١٨٥؛ بغية الوعاة للسيوطى ٣٠٨؛ هدية العارفين لإسماعيل البغدادى ١/ ٥٧٧.- معجم المؤلفين لكحالة ٥/ ٢٤٨.
له كتاب «السبب لحصر كلام العرب»، وكان في ستين كراسا (على ما جاء في هداية العارفين)، ذكره أيضا القفطى، ورآه ابن حجر (تبصير المنتبه ٥٣٦ - ٥٣٧) بخط محمد بن الزكى عبد العظيم بن عبد الباقى المنذرى (المتوفى سنة ٦٤٤/ ١٢٤٦)، انظر الوافى بالوفيات للصفدى ٣/ ٢٦٤ - ٢٦٥.

<span data-type="title" id=toc-617>البرمكى</span>
هو أبو المعالى محمد بن تميم، من أهل مصر.
وكان لغويا، صنّف معجما كبيرا، لا يختلف كثيرا عن الصحاح للجوهرى.
وعند ياقوت أن البرمكى نقله من كتاب الصحاح للجوهرى.
أما القفطى، فعلى العكس من ذلك، قد أثنى على الكتاب كثيرا. ويدلّ فحص القطعة التى وصلت إلينا منه على أن البرمكى، كالجوهرى، قسّم مادته على أبواب تبعا للحرف الأخير. ولكنه، خلافا للجوهرى، لم يرتّبها في الفصول على الحروف الأوائل، بل تبعا للحرف الثانى من الآخر (انظر: أحمد نصيف الجنابى، المرجع المذكور، ص ٥١٤). وكان فراغه من تصنيف الكتاب سنة ٣٩٧/ ١٠٠٧.','2','471',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (453,'<span data-type="title" id=toc-618>مصادر الترجمة:</span>
إرشاد الاريب لياقوت ٦/ ٤١٩ - ٤٢٠؛ إنباه الرواة للقفطى ٤/ ١٧٨ - ١٧٩؛ الوافى بالوفيات للصفدى ٢/ ٢٨٠؛ بغية الوعاة للسيوطى ٢٨.- معجم المؤلفين لكحالة.
له كتاب «المنتهى في اللغة»، ذكره القفطى، الذى رأى منه ثلاثة حروف:
الألف والباء والتاء، كل حرف منها في مجلد. وقد وصلت إلينا قطعة من حرف التاء محفوظة في كوبريلى ١٥٢١/ ٢ (الأوراق ٢٤ - ٣٠، من القرن ٦ هـ، انظر فهرس المخطوطات ١/ ٣٧٥)، وراجع المعجم العربى لحسين نصار ٢/ ٥١١ - ٥١٢./
<span data-type="title" id=toc-619>أحمد بن مطرّف</span>

هو أبو الفتح أحمد بن مطرف بن إسحاق، ولد بعد سنة ٣٢٠/ ٩٣٢ في عسقلان. ولى القضاء بدمياط. كان لغويا أديبا شاعرا، وتوفى سنة ٤١٣/ ١٠٢٢.
<span data-type="title" id=toc-620>مصادر ترجمته:</span>
إرشاد الأريب لياقوت ٢/ ١١٥ - ١١٦؛ الوافى بالوفيات للصفدى ٨/ ١٨١؛ بغية الوعاة للسيوطى ١٧٠، ١٧١ (وباتباعه ياقوت أورد ذكر أحمد بن مطرف توهما فى موضعين، جاعلا منه شخصين مختلفين).- معجم المؤلفين لكحالة ٢/ ١٨٠.

<span data-type="title" id=toc-621>آثاره:</span>
١ - كتاب كبير في اللغة، ذكره ياقوت، فى المرجع المذكور، ولعله هو «ديوان الكلم» لأحمد بن مطرّف الطائى اللغوى، الذى رأى منه القفطى المجلد العشرين (الإنباه ١/ ١٣٥). وأرجح الاحتمال أن المجلد الأول منه (من الألف إلى الخاء) محفوظ في التيمورية بدار الكتب بالقاهرة، لغة ٧١ (١٠٩ ورقة، أولها ناقص، نسخت سنة ٥٠٢ هـ).
«رسالة في الضاد والظاء»، كتبها إلى الشريف أبى الحسن محمد بن القاسم','2','472',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (454,'الحسينى، حاكم تنّيس، انظر ياقوت، الموضع المذكور.
هذا ويذكرون له ديوانه، وكتابا في الأدب اسمه «كتاب النوائح»، وكانت منه نسخة موجودة في القرن ٧/ ١٣ باحدى مكتبات حلب، انظر بولس سباط في. MIE ٤٩ /١٩٤٦ /٥٠

<span data-type="title" id=toc-622>الوزير المغربى</span>
هو أبو القاسم الحسين بن على (بن الحسين بن على) بن محمد، لغوىّ أديب مؤرخ من أهل مصر، كان وزيرا للحاكم، إلا أنه اضطر إلى التخلى عن عمله ومغادرة مصر بعد أن قتل الخليفة أباه وعمه وأخويه.
وتوجه أول الأمر إلى الرملة ثم إلى مكة، ومن ثمة إلى العراق. وبعد أن أقام بواسط وبغداد شخص إلى الموصل، حيث جعله معتمد الدولة كاتبه ثم وزيره.
وقضى بضعة سنوات من عمره بميافارقين وزيرا للأمير أبى نصر أحمد بن مروان.
توفى سنة ٤١٨/ ١٠٢٧ (انظر تاريخ التراث العربى ٢/ ٦٢٩ - ٦٣٠).

<span data-type="title" id=toc-623>مصادر ترجمته:</span>
تتمة اليتيمية للثعالبى ١/ ٢٤ - ٢٥؛ الرجال للنجاشى ٥٥؛ ابن خلكان ١/ ١٩٥ - ١٩٧؛ إرشاد الأريب لياقوت ٤/ ٦٠ - ٦٤؛ لسان الميزان لابن حجر؛ Br.G ٢ I ,٤٣٤ ,SI ,٦٠٠ - ٦٠١.٣٠٢ - ٣٠١ /٢ الأعلام للزركلى ٢/ ٢٦٦ - ٢٦٧؛ معجم المؤلفين لكحالة ٤/ ٣٠.

<span data-type="title" id=toc-624>آثاره:</span>
١ - «كتاب المنخّل» (مختصر إصلاح المنطق لابن السكيت، انظر ص ١٣١ آنفا).

<span data-type="title" id=toc-625>المخطوطات:</span>
الاسكوريال ٦٠٥ (٨٧ ورقة، نسخت سنة ٤٨٦ هـ)، و ٣٧٨ (ورقة واحدة)، القاهرة، التيمورية، لغة ١٣٧ (١٠٧ ورقة، نسخت سنة ٥٥٢ هـ، انظر فهرس','2','473',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (455,'المخطوطات ١/ ٣٧١)، دار الكتب بالقاهرة، أدب ٧٦٢٧ (١٠٤ ورقة، نسخت سنة ٦٨١ هـ، انظر فهرس المخطوطات ١/ ٣٧١)، الأحمدية بتونس ٣٩٤٧ (نحو ٥٠ ورقة، نسخت سنة ١٠٠٣ هـ)، بغداد، المتحف العراقى ٥٢٧/ ١ (حديثة، انظر كوركيس عواد في: سومر ١٣/ ١٩٥٧/ ٧٩)، طهران، ملك ٩٧٠ (١١٤ ورقة، من القرن ٨ هـ، انظر الفهرس ١/ ٧٤٧)./ ولأبى القاسم محمد بن عبد الغفور الكلاعى (القرن ٧/ ١٣) كتاب سماه «خطبة كتاب المنخّل»، ذكره المؤلف في كتابه «إحكام صنعة الكلام»، بيروت ١٩٦٦، ص ٢٨.
٢ - «كتاب اختصار الغريب المصنف (لأبى عبيد)»، ذكره النجاشى.
٣ - «كتاب الإلحاق بالاشتقاق» (لابن دريد؟ )، ذكره النجاشى، ونقل عنه في تاج العروس ٢ ٧/ ٣٨٢ (أرد).
وله في لغة القرآن:
«كتاب خصائص علم القرآن»، ذكره النجاشى.

<span data-type="title" id=toc-626>ابن مغلّس</span>
هو أبو محمد عبد العزيز بن أحمد بن السّيد بن (ال) - مغلس القيسى الأندلسى البلنسى، وكان تلميذا لصاعد بن الحسن الرّبعى (منذ حوالى سنة ٣٨٠/ ٩٩٠ بالاندلس، وتوفى ٤١٧/ ١٠٢٦، انظر فصل الأدب)، ثم رحل من بعد إلى مصر وأخذ هنالك عن أبى يعقوب يوسف بن يعقوب ابن اسماعيل بن خرّزاذ النجيرمى السّعترى اللغوى النحوى (المتوفى في محرّم سنة ٤٢٣/ ديسمبر سنة ١٠٣١ - يناير ١٠٣٢، انظر الأنساب للسمعانى ٢٩٨؛ بغية الوعاة للسيوطى ٤٢٥). زار أيضا بغداد وتوفى سنة ٤٢٧/ ١٠٣٦ في أرض مقامه مصر.','2','474',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (456,'<span data-type="title" id=toc-627>مصادر ترجمته:</span>
جذوة المقتبس للحميدى ٢٦٩، الصلة لابن بشكوال ٣٥١؛ بغية الملتمس للضبى ٣٧١؛ ابن خلكان ١/ ٣٧٢؛ الوافى بالوفيات للصفدى (انظر
(G. Gabrieliin: Reand. Accad. Lincei, Ser. V, ٢٢/ ١٩١٣/ ٥٥٧
؛ نفح الطيب للمقرى، تحقيق: إحسان عباس ٢/ ١٣٢ - ١٣٣؛ ابن قاضى شهبة، المخطوط ص ٣٧١ - ٣٧٢. الأعلام للزركلى ٤/ ١٣٦؛ معجم المؤلفين لكحالة ٥/ ٢٤١ - ٢٤٢.
عرف أيضا باشتغاله باللغة، إلا أنه لم يبلغنا اسم لمصنّف له. وله أشعار، وقصائد هاجى بها أبا طاهر اسماعيل بن خلف (المتوفى سنة ٤٥٥/ ١٠٦٣، انظر (Br.GI ,٤٠٧ ,SI ,٧٢٠ - ٧٢١ وكانت مجموعة في ديوان.

<span data-type="title" id=toc-628>الحوفى</span>
هو أبو الحسن على بن إبراهيم بن سعيد، كان نحويا لغويا ومفسّرا، أخذ عن أبى بكر محمد بن على الأدفوى، صاحب أبى جعفر النحاس. توفى سنة ٤٣٠/ ١٠٣٩.
وأخباره في إرشاد الأريب لياقوت ٥/ ٨٠ - ٨١؛ وابن خلكان ١/ ٤١٨؛ وإنباه الرواة للقفطى ٢/ ٢١٩ - ٢٢٠؛ وبغية الوعاة للسيوطى ٣٢٥؛ وحسن المحاضرة له ١/ ٢٢٨؛ وابن قاضى شهبة، المخطوط ص ٤٠٤؛ وطبقات المفسرين للداودى؛ Br.GI ,٤١١ ,SI ,٧٢٩ - .٣٨٢ - ٣٨١ /١ ومعجم المؤلفين لكحالة ٧/ ٥ - ٦./

<span data-type="title" id=toc-629>آثاره:</span>
<span data-type="title" id=toc-630>فى لغة القرآن:</span>
١ - «كتاب البرهان في تفسير القرآن»، كان في ثلاثين مجلدا على ما ذكر ياقوت، ووصل إلينا منه:
فى دار الكتب بالقاهرة ٥٩ (٢، ٣، ٤، ٦ (مرتين)، ٨، ٩، ١٠، ١٢، ١٥، ١٨، ٢٤، ٢٥، ٢٦، ٢٧، ٢٨، ١٤٦ ورقة، ١٠٢ ورقة، ١٤٨ ورقة، ١٠٢','2','475',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (457,'ورقة، ١٤٢ ورقة، ١٢٨ ورقة، ١٣٤ ورقة، ٩٨ ورقة، ٩٥ ورقة، ٦٠ ورقة، ٤٧ ورقة، ١٦١ ورقة، ١٦٩ ورقة، ١٣٤ ورقة، ٢٤ ورقة، من القرن السادس للهجرة، انظر فهرس المخطوطات ١/ ٢٢ - ٢٤)، شهيد على ٢٨٨ (٩، ٢٣٨ ورقة، من القرن السابع للهجرة).
دار الكتب الوطنية بتونس (مجموعة عبد الوهاب) ١٨٤٨٧ (الجزء الثالث، ناقص، ١٨٩ ورقة، نسخت سنة ٦١٧ هـ، انظر فهرس منصور ١٥)، برلين ٧٤٤ (مجلد، يتناول سورتى يوسف والرعد، نسخ سنة ٥٧٠ هـ)، ليدن ٨٨، ٤) ٢٣٧١ Or. ورقة، نسخت قبل سنة ٧٩٥ هـ، انظر فورهوف ٥٤)، و ٢٥٥، ١٣) ٣٤٥ Or. ورقة، من القرن ١١ هـ، انظر فورهوف ٥٤).
وله مختصر لإسماعيل بن خلف الصقلى (المتوفى سنة ٤٥٥/ ١٠٦٣) فى برلين ٧٤٥ (المجلد ٤، ١٨٤ ورقة، نسخت نحو سنة ٨٠٠ هـ)، فى جامع الزيتونة بتونس، تفسير ٣٤ (١، ٣٠٠ ورقة، نسخت سنة ١٢٩٢ هـ)، و ٣٥ (٢، ٢٠٤ ورقة، نسخت سنة ١٢٩٢ هـ، انظر الفهرس ١/ ٢٧ - ٢٩).
٢ - «إعراب القرآن»، كان في عشرة مجلدات، على ما ذكر ابن خلكان والداودى.

<span data-type="title" id=toc-631>وله في النحو:</span>
١ - «كتاب الموضح في النحو»، ذكر ياقوت أنه كتاب كبير.
٢ - شرح على «كتاب مختصر النحو» لعلى بن حمزة الكسائى (المتوفى سنة ١٨٩/ ٨٠٥)، ولم يصل إلينا، انظر فصل النحو.
استدراك (انظر أيضا ص ٥٥، رقم ٢٠، الخوافى):
دل فحص مخطوطة المدينة، المحمودية، المرقمة بالرقم الجديد لغة ٢٠ (٣٣٠ ورقة) على أنها لا تحتوى كما تذكر صفحة العنوان على «مختصر العين» لأبى الحسن على بن القاسم السنجانى الخوافى الذى أورده الباخرزى، بل تشتمل على معجم لغريب القرآن والحديث، كان غير معروف إلى الآن، للخوافى نفسه (وليس الحوفى).','2','476',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (458,'واعتمد المؤلف، كما ذكر في المقدمة الموجزة، على أبى عبيدة، واتبع في تقسيمه الأبجدى كتاب العين للخليل بن أحمد.

<span data-type="title" id=toc-632>أبو سهل الهروى</span>
هو محمد بن على بن محمد، نحوى، ولد سنة ٣٧٢/ ٩٨٣. نزح أبوه، وكان كذلك نحويا، إلى مصر.
وأخذ أبو سهل عن أبى عبيد الهروى، وروى في مصر عن النجيرمى وأبى أسامة جنادة بن محمد الهروى النحوى. توفى سنة ٤٣٣/ ١٠٤١ بمصر.

<span data-type="title" id=toc-633>مصادر ترجمته:</span>
إرشاد الأريب لياقوت ٧/ ٤٧؛ إنباه الرواة للقفطى ٣/ ١٩٥؛ الوافى بالوفيات للصفدى ٤/ ١٢٠ - ١٢١؛ بغية الوعاة للسيوطى ٨٣.- الأعلام للزركلى ٧/ ١٦١؛ معجم المؤلفين لكحالة ١١/ ٦٠.

<span data-type="title" id=toc-634>آثاره:</span>
١ - «تهذيب كتاب الفصيح»، أول كتبه الثلاثة التى ألفها على كتاب الفصيح لثعلب (المتوفى سنة ٢٩١/ ٩٠٤)، انظر مقدمة «التلويح» فى: (الطرف الأدبية» ص ٢.
٢ - «إسفار كتاب الفصيح»، وضعه قبل «التلويح»، (انظر مقدمة التلويح، فى الموضع المذكور، راجع الوافى بالوفيات للصفدى ٤/ ١٢١).

<span data-type="title" id=toc-635>المخطوطات:</span>
مكة، مكتبة عبد القدوس الأنصارى الخاصة (بخط المؤلف، كتبت سنة ٤٢٧ هـ، انظر الأعلام للزركلى ٧/ القسم رقم ١١٥٣ ص ١٦١).
القاهرة، طلعت بدار الكتب، لغة ٣٨١ (٨٩ ورقة، نسخت سنة ٩٧٣ هـ، انظر فؤاد السيد في: مجلة معهد المخطوطات العربية ٣/ ١٩٥٧/ ٢٢٣).','2','477',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (459,'٣ - «التلويح في شرح الفصيح»، ذكره ياقوت والصفدى.

<span data-type="title" id=toc-636>المخطوطات:</span>
برنستون، جاريت ٥٠) H ٢٩٠، ورقة، نسخت سنة ١٢٦٥ هـ، انظر الفهرس، رقم ٢٤٩).
الرباط، المكتبة العامة، د ١٦٣٩/ ١ (١ ب- ١٥ ب، انظر فهرس علوش ١/ ٣٦١، رقم ١٧٢٧)؛ طبع في القاهرة ١٢٨٥، ١٢٨٩، ونشره محمد أمين الخانجى ومحمد بدر الدين أبو فراس النعسانى/ فى: الطرف الأدبية، القاهرة ١٩٠٧، ص ٢ - ٩٨.
ونشره أيضا محمد عبد المنعم خفاجى في: فصيح ثعلب والشروح التى عليه، القاهرة ١٩٤٩.
ومنه مختارات مقارنة بنصوص مناظرة للغويين آخرين في: نصوص في فقه اللغة العربية، المجلد الأول، بيروت ١٩٧٠، ص ٣٢٣ - ٣٦١.
ونقل عن أحد الشروح في خزانة الأدب (انظر إقليد الخزانة للميمنى ٨٠).
٤ - «كتاب المكنّى والمبنى» وهو من مصادر الصغانى في التكملة (انظر ١/ ٨) والعباب (انظر ١/ ٢٩)، وعلى ما يبدو كان أيضا أساس «كتاب المرصّع» للمبارك بن محمد بن الأثير (بغداد ١٩٧١)، انظر ص ٣٥ - ٣٦، ١٣٦، ١٤٦، ١٦٥، ٢٥٢.
٥ - «كتاب المثلثات». فى أربعة مجلدات، وهو كذلك من مصادر الصغانى في التكملة والعباب.
٦ - «كتاب المنمّق»، نقل عنه أيضا الصغانى.
٧ - «كتاب أسماء الأسد»، ذكره ياقوت، فى الموضع المذكور آنفا.
٨ - «كتاب أسماء السيف»، انظر ياقوت.','2','478',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (460,'<span data-type="title" id=toc-637>٦ - اللغويون في المغرب والأندلس</span>
<span data-type="title" id=toc-638>المهرى</span>
هو أبو الوليد عبد الملك بن قطن القيروانى، كان أحد متقدمى اللغويين بالمغرب؛ وكان إلى ذلك مؤرخا، شاعرا، عالما بالشعر القديم.
أخذ عن فصحاء الأعراب الذين نزلوا المغرب كابن الطرمّاح الأعرابى (أمان بن الصّمصامة) والمنيع الأعرابى. توفى عن سن عالية سنة ٢٥٦/ ٨٧٠.

<span data-type="title" id=toc-639>مصادر ترجمته:</span>
طبقات النحويين للزبيدى ٢٤٩ - ٢٥٣؛ إنباه الرواة للقفطى ٢/ ٢٠٩ - ٢١١؛ بغية الوعاة للسيوطى ٣١٤؛ ابن قاضى شهبة، المخطوط ص ٣٨٦ - ٣٨٧.- معجم المؤلفين لكحالة ٦/ ١٨٨.

<span data-type="title" id=toc-640>آثاره:</span>
١ - «كتاب الألفاظ»، ذكره الزبيدى وغيره.
٢ - كتاب في «اشتقاق الأسماء»، ذكره الزبيدى وغيره.
[استدراك على فصل التاريخ (فى المجلد الأول من تاريخ التراث العربى) كتاب فى «تفسير مغازى الواقدى»، ذكره الزبيدى].

<span data-type="title" id=toc-641>يحيى بن سلام</span>
هو يحيى بن سلام أو سلّام، المتوفى سنة ٢٠٠/ ٨١٥، (انظر تاريخ التراث العربى ١/ ٣٩).
له «كتاب التصاريف (تفسير القرآن مما اشتبهت أسماؤه وتصرّفت معانيه)»، فى تفسير المشترك في القرآن.
نشره هـ. شلبى، تونس ١٩٧٩.','2','479',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (461,'<span data-type="title" id=toc-642>ابن الحدّاد</span>
هو أبو عثمان سعيد بن محمد الغسانى، فقيه لغوى من أهل القيروان (ولد سنة ٢١٩/ ٨٣٤، وتوفى سنة ٣٠٢/ ٩١٥، انظر تاريخ التراث العربى ١/ ٦٠١).
صنف مصنفات فقهية ولغوية، لم تذكر مصادرنا إلا بعض أسمائها.

<span data-type="title" id=toc-643><span data-type="title" id=toc-646>مصادر ترجمته:</span></span>
طبقات النحويين للزبيدى ٢٦١ - ٢٦٢؛ إنباه الرواة للقفطى ٢/ ٥٣ - ٥٤؛ ابن قاضى شهبة، المخطوط ص ٢٩٤ - ٢٩٥؛ معالم الإيمان لابن ناجى ٢ ٢/ ٢٩٥ - ٣١٥.

<span data-type="title" id=toc-644>مصنفاته:</span>
مما ذكره الزبيدى من مؤلفاته:
١ - «كتاب الأمالى» و ٢ - «توضيح مشكل القرآن».
[استدراك على فصل العقائد (فى المجلد الأول من تاريخ التراث العربى):
الكتب التالية المتعلقة بالعقائد ذكرها الزبيدى وغيره:
١ - «كتاب العبادة الكبرى والصغرى»، ٢ - «كتاب المقالات»، وقيل إنه ردّ فيه على أهل المذاهب أجمعين، ٣ - «كتاب الاستيعاب»، ٤ - «كتاب عصمة المسلمين»، ٥ - «كتاب الاستواء»]./

<span data-type="title" id=toc-645>القزّاز</span>
هو أبو عبد الله محمد بن جعفر بن أحمد التميمى القيروانى، وكان لغويا نحويا بارعا، صاحب نظر في الأدب. صنّف سنة ٣٦١/ ٩٧٢ بأمر أبى تميم المعز، حاكم المغرب مصنّفه الكبير «كتاب الجامع» الذى أثنى عليه كثيرا لأصالته (انظر إنباه الرواة للقفطى ٣/ ٨٦ - ٨٧). توفى سنة ٤١٢/ ١٠٢١ وله من العمر نحو ٩٠ عاما.
مصادر ترجمته:
ابن خلكان ١/ ٦٥١ - ٦٥٢؛ إرشاد الأريب لياقوت ٦/ ٤٦٨ - ٤٧١؛ الوافى','2','480',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (462,'بالوفيات للصفدى ٢/ ٣٠٤ - ٣٠٥؛ بغية الوعاة للسيوطى؛ Br.SI ,٥٣٩ - .٢٩٠ الأعلام للزركلى ٦/ ٢٩٩؛ معجم المؤلفين لكحالة ٩/ ١٤٨ - ١٤٩؛ المنجى الكعبى، «القزاز القيروانى»، تونس. Ch.Bouyahiain: EI ,IV ٢ ,٨٦٧؛ ١٩٦٨

<span data-type="title" id=toc-647>آثاره:</span>
١ - «العشرات في اللغة»، وفيه أتمّ «كتاب العشرات» لأبى عمر الزاهد (انظر ص ١٥٥ آنفا)، ونقل عنه في خزانة الأدب ١/ ٦٥.
ويوجد مخطوطا في سليم أغا ٨٩٣/ ٢ (١١٢ أ- ١٥٧ أ، نسخت سنة ١١١٤ هـ، راجع فهرس المخطوطات، (O.Rescherin: ZDMG ٦٨ /١٩١٤ /٥٧؛ ٣٦٠ /١ رامبور (١٢ ورقة، نسخت سنة ٧٠٠ هـ، انظر إقليد الخزانة للميمنى ص ٧٦).
٢ - «كتاب فيه ذكر شئ من الحلى»، القاهرة، طلعت، لغة ٤٠١ (١٠ ورقات، نسخت سنة ١٢٢٣ هـ، انظر الكعبى، الموضع المذكور ص ٦٥).
ونشره باسم «كتاب الحلى» ط. النعسانى وأ. قدرى كيلانى، صيدا ١٣٤١/ ١٩٢٢، انظر فهرس دار الكتب المصرية ٢/ ٢٧.
٣ - «كتاب الجامع في اللغة». وصف بأنه كتاب كبير، وقيل إنه كان يشبه «تهذيب اللغة» للأزهرى (انظر إنباه الرواة للقفطى ٣/ ٨٦؛ الإرشاد لياقوت ٦/ ٤٦٨ - ٤٦٩؛ المزهر للسيوطى ١/ ٩٦).
وكان من مصادر أحمد بن يوسف الفهرى (المتوفى سنة ٦٩١/ ١٢٩٢) فى كتابه «تحفة المجد الصريح في شرح كتاب الفصيح» (انظر عبد العزيز الميمنى في: مجلة المجمع العلمي العربى بدمشق ٣٥/ ١٩٦٠/ ٥٤٢)، وجمع الكعبى هذه النقول في كتابه المذكور، ص ٨٨ - ٩٩؛ وكان الكتاب أيضا من مصادر على بن سعود في مصنفه «كتاب تخريج الدلالات السمعية» (انظر تاريخ التراث العربى ٩/ ٢٥٠)، راجع فهرس المصادر، الورقة ٢٣٠ ب؛ ونقل عنه في تاج العروس ٢ ١/ ٤٧٩ (وبأ)، ٢/ ٦٢ (ترب)، ٤/ ١٨٩ (لبب)،','2','481',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (463,'٥/ ٤٠٧ (بأج)، ٦/ ١٢٦، ١٢٧ (عوج)، ٧/ ١٣٧ (ملح)، ٨/ ٤٧٠ (غمد)، ٩/ ٢٧٩ (ودد)، ٤٨١ (نبذ).
٤ - «كتاب المثلث»، كان من مصادر أحمد بن يوسف الفهرى (انظر عبد العزيز الميمنى في: مجلة المجمع العلمى العربى بدمشق ٣٥/ ١٩٦٠/ ٥٤٣)، ولعله هو الشرح المذكور في كشف الظنون ١٥٨٧ على «كتاب المثلث» لقطرب (انظر ص ٦٢ آنفا).
٥ - «كتاب الضاد والظاء»، ذكره ياقوت في إرشاد الأريب ٦/ ٤٧١.
٦ - «كتاب إعراب (المقصورة) الدريدية»، ذكره ياقوت، الموضع نفسه، والصفدى في الوافى ٢/ ٣٠٥، واسمه في إنباه الرواة للقفطى ٣/ ٨٦: «شرح المقصورة» (١٥٤).
٧ - «غريب البخارى»، نقل عنه في تاج العروس ٢ ٥/ ٤٦٧ (حجج).
٨ - «كتاب المعالم»، وورد عنه نقل لغوى في تاج العروس ٥/ ٤٣٧ (ترج)./

<span data-type="title" id=toc-648>مكى بن أبي طالب</span>
هو أبو محمد مكى بن أبى طالب خاموش بن محمد القيسى (ولد سنة ٣٥٥/ ٩٦٦، وتوفى سنة ٤٣٧/ ١٠٤٥) من أهل القيروان، ألف رسائل في مختلف فروع العلم، ومنها ما يتناول مسائل في النحو (انظر فصل النحو) واللغة.

<span data-type="title" id=toc-649>آثاره:</span>
١ - «كتاب شرح حاجة وحوائج وأصلها» ذكره القفطى في إنباه الرواة ٣/ ٣١٨، وغيره.
٢ - «كتاب شرح العارية والعريّة»، المصدر السابق.
٣ - «كتاب معانى السنين القحطية والأيام»، المصدر نفسه.
_________
(١٥٤) أورد المنجى الكعبى بعض أسماء لمصنفات لغوية ونحوية، من بينها «كتاب الحروف»، وذهب إلى أنها مؤلفات أخرى للقزاز. ومن رأيى أن الحجج التي أوردها لا تنهض.','2','482',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (464,'٤ - رسالة في المعانى المختلفة للألفاظ، وتوجد مخطوطة في شهيد على ٣٠٥ (٨٩ ب ٩٠ ب).
٥ - شرح على كتاب الفصيح لثعلب، نقل عنه الفهرى في «تحفة المجد الصريح»، مخطوطة القاهرة (انظر ص ١٤٤ آنفا) ص ١٤، ٢٤، ٢٧، ٢٩، ٣٢، ٣٩، ٤٠، ٤٨، ٤٩، ٥٥، ٥٦، ٥٨، ٦٣، ٦٥، ٦٩، ٧١، ٧٥، ٧٩، ٨٠، ٨٤، ٨٥، ٨٩ - ٩٠، ٩٢، ٩٦، ٩٨، ٩٩، ١٠٤، ١١٤، ١٢٩، ١٣٣، ١٣٤، ١٤٧، ١٥١، ١٥٩، ١٦٠، راجع مجلة المجمع العلمي العربي بدمشق ٣٥/ ١٩٦٠/ ٥٤٤، سطر ٣.
[انظر في مصنفات أخرى (١٥٥) فى القراءات القرآنية والتفسير وغير ذلك. الفصول المتعلقة بذلك من «تاريخ التراث العربى» فى الفترة التالية لسنة ٤٣٠/ ١٠٤٠»].

<span data-type="title" id=toc-650>عبد الملك بن حبيب</span>
هو أبو مروان عبد الملك بن حبيب بن سليمان، عالم أندلسى متعدد الفنون (عاش من سنة ١٧٤/ ٧٩٠ الى سنة ٢٣٨/ ٨٥٢، انظر تاريخ التراث العربى ١/ ٣٦٢)، اشتغل بفنون مختلفة من العلوم والأدب (انظر فصل الأدب) وبمسائل الصرف والنحو واللغة.

<span data-type="title" id=toc-651>مصادر ترجمته:</span>
ابن الفرضى ٢ ١/ ٣١٢ - ٣١٥؛ طبقات النحويين للزبيدى ٢٨٢ - ٢٨٣؛ إنباه الرواة للقفطى ٢/ ٢٠٦ - ٢٠٧؛ الديباج المذهب لابن فرحون ٢ ٢/ ٨ - ١٥؛ الوفيات لابن قنفذ ١٧١.
_________
(١٥٥) توجد له رسالة «شرح الياءات المشددات» بمكتبة المصراتي الخاصة بطرابلس (٧ ورقات ضمن مجموع، نسخت سنة ٩٥٦ هـ. ونشرها باسم «الياءات المشدّدات في القرآن وكلام العرب» أ. ح. فرحات، الرياض- دمشق ١٩٨٢.','2','483',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (465,'<span data-type="title" id=toc-652>آثاره:</span>
فى لغة القرآن والحديث:
١ - «كتاب إعراب القرآن»، ذكره ابن فرحون في الديباج ٢/ ١٢.
٢ - «كتاب تفسير القرآن»، المصدر نفسه ص ١٣.
٣ - «كتاب القارئ»، نفسه ص ١٣.
٤ - «كتاب الناسخ والمنسوخ»، نفس الموضع.
٥ - «كتاب رغائب القرآن»، نفس الموضع.
٦ - «كتاب غريب الحديث»، ابن الفرضى ١/ ٣١٣؛ وبحسب ابن فرحون، الديباج ٢/ ١١، كان هذا الكتاب جزءا من كتاب كبير في عشرة مجلدات.
[استدراك على قائمة كتبه في الفقه:
١ - «كتاب الفرائض»، ذكره ابن فرحون ٢/ ١٢؛ ٢ - «كتاب الربا» / المصدر نفسه؛ ٣ - «كتاب الحكم والعمل بالجوارح»، المصدر نفسه؛ ٤ - «كتاب تفسير الموطّأ»، المصدر نفسه ص ١١].
[استدراك على فصل تدوين التاريخ:
١ - «كتاب النّسب»، ذكره ابن فرحون ٢/ ١٢؛ ٢ - «فضائل النبى»، المصدر نفسه؛ ٣ - «فضائل عمر بن عبد العزيز»، المصدر نفسه؛ ٤ - «فضائل مالك بن أنس»، المصدر نفسه؛ ٥ - «كتاب أخبار قريش»، المصدر نفسه؛ ٦ - «كتاب مغازى رسول الله»، المصدر نفسه ص ١٣].

<span data-type="title" id=toc-653>قاسم بن ثابت</span>
هو أبو محمد قاسم بن ثابت بن حزم السرقسطى، ولد سنة ٢٥٥/ ٨٦٩ وارتحل مع أبيه (وهو كذلك من اللغويين، وعاش من سنة ٢١٧/ ٨٣٢ إلى ٣١٣/ ٩٢٥، انظر الأعلام للزركلى ٢/ ٨٠) سنة ٢٨٨/ ٩٠١ إلى مكة. وعند عودته إلى الأندلس أحضر معه «كتاب العين». توفى سنة ٣٠٢/ ٩١٥.','2','484',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (466,'<span data-type="title" id=toc-654>مصادر ترجمته:</span>
طبقات النحويين للزبيدى ٣٠٩؛ جذوة المقتبس للحميدى ٣١٢؛ ابن الفرضى ٢ ١/ ٤٠٢ - ٤٠٣؛ بغية الملتمس للضبي ٤٣٤ - ٤٣٥؛ فهرست ابن خير ١٩١ - ١٩٤؛ إرشاد الأريب لياقوت ٦/ ١٥٤؛ إنباه الرواة للقفطى ١/ ٢٦٢؛ بغية الوعاة للسيوطى ٣٧٦؛ ابن قاضى شهبة، المخطوط ص. Flugel ,Gramm.٤٧٦ - ٤٧٥
Schulen ٢٠٠ - ٢٠١; Rescher, Abri BII, ٢٣٨ - ٢٣٩
معجم المؤلفين لكحالة ٨/ ٩٦ - ٩٧

<span data-type="title" id=toc-655>ومن آثاره:</span>
«كتاب الدلائل في غريب الحديث» أو «الدلائل في شرح ما أغفله أبو عبيد وابن قتيبة في غريب الحديث». وفي رأى الزبيدى (الموضع المذكور) أنه لم يؤلف بالأندلس كتاب أكمل منه في شرح الحديث. وقد بقى الكتاب غير كامل بسبب وفاة المؤلف الباكرة حتى أكمله أبوه من بعد.-

<span data-type="title" id=toc-656>المخطوطات:</span>
استنبول، المتحف التركى للآثار الإسلامية (متحف الأوقاف سابقا) ١٦٨٢ (الجزء الثانى، ١٢٧ ورقة، من القرن السادس للهجرة).
دمشق، الظاهرية، عام ١٥٧٩ (الجزء الثانى، ١٨٠ ورقة، نسخت سنة ٤٩٩ هـ، انظر فهرس اللغة ٨٨ - ٩٠، منقولة عن نسخة ابن المؤلف، راجع عزّ الدين التنوخى في: مجلة المجمع العلمى العربى بدمشق ٣٧/ ١٩٦٢/ ٣٦٢ - ٣٦٦)، الرباط ٢ مكتبة الأوقاف ١٩٧ (الجزءان الثانى والثالث، ١٥٣ ورقة و ١٥٤ ورقة، من القرن السابع للهجرة، انظر ش. الفحام في: مجلة المجمع العلمى العربى بدمشق ٥١/ ١٩٧٦/ ٤٨١ - ٥١٧). ونقل عنه ابن حجر في الإصابة ٢/ ١٠٠٧، ٤/ ٥٠، والفهرى في تحفة المجد الصريح، ص ٢٥، ٤٦، ٨٦، ١٥٢.

<span data-type="title" id=toc-657>قاسم بن سعدان</span>
هو أبو محمد قاسم بن سعدان بن عبد الوارث الريى، لغوى، فقيه، محدث جليل من أهل مالقة، عاش في قرطبة. توفى سنة ٣٤٧/ ٩٥٨.','2','485',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (467,'<span data-type="title" id=toc-658><span data-type="title" id=toc-660>مصادر ترجمته:</span></span>
ابن الفرضى ٢ ١/ ٤٠٨؛ طبقات النحويين للزبيدى ٣٢٧؛ بغية الوعاة للسيوطى ٣٧٧.
ويبدو أن له كتابا في الأمثال، نقل عنه أبو عبيد البكرى في «فصل المقال» ٦٩، ٤٤٠، ٤٦٦ (راجع/. (Sellheim ,Sprichworter ٩٩ - ١٠٠

<span data-type="title" id=toc-659>أبو على القالى</span>
هو اسماعيل بن القاسم بن عيذون، ولد بمنازجرد سنة ٢٨٠/ ٨٩٣. رحل سنة ٣٠٣ إلى الموصل، فسمع من أبى يعلى الموصلى، ثم قصد بغداد بعد عامين.
وكان من أساتذته هنالك ابن دريد، وأبو بكر بن الأنبارى، ونفطويه، وأبو بكر بن السراج، والزجاج، والأخفش الأصغر، وابن درستويه، وابن مجاهد المقرئ.
وفي سنة ٣٢٨/ ٩٤٠ غادر بغداد، ووافى قرطبة سنة ٣٣٠/ ٩٤٢ حيث أقام بها إلى وفاته سنة ٣٥٦/ ٩٦٧.
ويعد أبو على القالى من أشهر لغويى الأندلس. وفيها صنّف أهم كتبه. وكان اهتمامه باللغة يفوق اهتمامه بالنحو (انظر فصل النحو). هذا وقد نال شهرة كبيرة في الأدب (انظر فصل الأدب) بكتابه «الأمالى».
ولأبى على القالى دور خاص في تطور علوم اللغة والأدب، وذلك بإدخاله عددا كبيرا من مؤلفات علماء المشرق إلى الأندلس لأول مرة.
مصادر ترجمته:
طبقات النحويين للزبيدى ٢٠٢ - ٢٠٥؛ ابن الفرضى ٢ ١/ ٨٣ - ٨٤؛ جذوة المقتبس للحميدى ١٥٤ - ١٥٨؛ بغية الملتمس للضبى ٢١٦ - ٢١٩؛ ابن خلكان ١/ ٩٢ - ٩٣؛ إرشاد الأريب لياقوت ٢/ ٣٥١ - ٣٥٤؛ إنباه الرواة للقفطى ١/ ٢٠٤ ٢٠٩؛ مسالك الأبصار لابن فضل الله ٤/ ٢٣٨ - ٢٣٩؛ بغية الوعاة للسيوطى ١٩٨.-','2','486',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (468,'Flugel, Gramm. Schulen ١١٢ - ١١٤; Br. GI, ١٣٢, SI, ٢٠٢ - ٢٠٣; Rescher, Abri BII, ٢٣٩ - ٢٤١; M. Ben Chenebin: EI, II ٧٤٣;
الأعلام للزركلى ١/ ٣١٩ - ٣٢٠؛ معجم المؤلفين لكحالة ٢/ ٢٨٦ - ٢٨٧؛
S. A. Bonebakker, Two Manuscriptsof Al- Qali''s Redactionof Ibn Qutaiba''s Adabal- Katibin: Actas ١. Congr, Estud. Ar. Isl., Madrid ١٩٦٤, ٤٥٣ - ٤٦٦;
ع. الدقاق، «أبو على القالى وكتابه الأمالى» فى: مجلة المجمع العلمى العربى بدمشق ٤٤/ ١٩٦٩/ ٥١٥ - ٥٣٧؛ أ. ع. هريدى في: مجلة معهد المخطوطات العربية ٢٠، ٢/ ١٩٧٤/ ٤٩ وما بعدها؛ ع. الودغيرى، «حول تأثير القالى في الدراسات اللغوية والأدبية بالأندلس» فى: مجلة كلية الآداب (فاس R ,Sellheimin: EI ,٢ IV ,٥٠١ - ٥٠٢.؛ ١٤١ - ١٢١ /١٩٧٨ /١ (

<span data-type="title" id=toc-661>آثاره:</span>
١ - «كتاب البارع في اللغة»، تهذيب لكتاب العين للخليل بن أحمد. وقيل إن أصله كان يشتمل على ١٦٤ جزءا في نحو خمسة آلاف ورقة. ويبدو أن المؤلف استخرجه من كثير من التآليف المفردة في اللغة التى وضعها العلماء الذين سبقوه.
ولكننا لا نعرف سوى جزء صغير منه.

<span data-type="title" id=toc-662>مخطوطاته:</span>
لندن، المتحف البريطانى ٧٤) ٩٨١١ Or.، ورقة، من القرن الرابع للهجرة، ونشر A.S.Fulton صورة من المخطوطة، لندن ١٩٣٣، انظر، (R.Paretin: OLZ ٣٨ /١٩٣٥ /٣٧٤ - ٣٧٥ باريس ٤٢٣٥ (١٢ ورقة، من القرن الرابع للهجرة، انظر فجدا ٢٨٧)؛ نشره هاشم الطعان، بيروت ١٩٧٥؛ انظر أيضا المعجم العربى لحسين نصار ٣١٣ - ٣٣١.
٢ - «كتاب الأمثال» أو «كتاب أفعل من كذا»، أمثال تبدأ بعبارة: أفعل من ... ، له مخطوطة في تونس، الأحمدية ٣٩٤٥/ ٢ (٥ ورقات، نسخت سنة ٧٠١ هـ،','2','487',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (469,'لعلها ناقصة)، راجع محمد الفاضل بن عاشور في: مجموعات البحوث والمحاضرات (لمجمع اللغة، القاهرة)، ١٩٦٩، ص ٣٤١ - ٣٥٠ (انظر مجلة معهد المخطوطات العربية ٢٠، ٢/ ١٩٧٤/ ٧٧ - ٧٨)، نشره محمد الفاضل بن عاشور، تونس ١٩٧٢./ ٣ - «تبويب لحن العامة لأبى حاتم السجستانى» (انظر ص ٩٤ آنفا)، ذكره ابن خير، الفهرست ٣٤٨.

٤ - «كتاب حلى الإنسان والخيل وشياتها»، ذكره<span data-type="title" id=toc-663> الزبيدى</span>، والقفطى، إنباه الرواة ١/ ٢٠٦، وغيرهما.
٥ - «كتاب الإبل ونتاجها»، المصدران السابقان.
الزبيدى
هو أبو بكر محمد بن الحسن بن عبد الله، ولد سنة ٣١٦/ ٩٢٨ بأشبيلية. وأصل أسرته من حمص.
قصد الزبيدى قرطبة، وأخذ هناك عن أبي على القالى، وقاسم بن أصبغ القرطبى (المتوفى سنة ٣٤٠/ ٩٥١)، وسعيد بن فحلون (المتوفى سنة ٣٤١/ ٩٥٢) وغيرهم.
وكان من تلامذته ابنه أبو الوليد محمد (المتوفى سنة ٤٤٠/ ١٠٤٨)، وأبو القاسم ابراهيم بن محمد بن زكرياء بن الإفليلى (المتوفى سنة ٤٤١/ ١٠٤٩)، وإسماعيل بن سيده (المتوفى سنة ٤٠٠/ ١٠٠٩).
وعهد إليه الخليفة المستنصر بالله بتعليم ابنائه، وولّاه من بعد القضاء بقرطبة، ثم جعله صاحب شرطتها. وبعد وفاة المستنصر بالله سنة ٣٦٦/ ٩٧٦ عاد الزبيدى إلى اشبيلية، حيث تقلد القضاء فيها أيضا. توفى سنة ٣٧٩/ ٩٨٩ باشبيلية.
وقد برع الزبيدى في اللغة والنحو جميعا (انظر فصل النحو). ويدين بشهرته الكبيرة في مجال اللغة إلى مختصره لكتاب العين وإلى كتابه فى لحن العوام.

<span data-type="title" id=toc-664>مصادر ترجمته:</span>
جذوة المقتبس للحميدى ٤٣ - ٤٥؛ بغية الملتمس للضبى ٥٦ - ٥٧؛ ابن','2','488',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (470,'الفرضى ٢ ٢/ ٩٢؛ المغرب لابن سعيد ٢ ١/ ٢٥٥ - ٢٥٦؛ ابن خلكان ١/ ٦٥٠ - ٦٥١؛ إرشاد الأريب لياقوت ٦/ ٥١٨ - ٥٢٢؛ إنباه الرواة للقفطى ٣/ ١٠٨ - ١٠٩؛ الوافى بالوفيات للصفدى ٢/ ٣٥١.-
Br. GI, ١٣٢ - ١٣٣, SI, ٢٠٣, Rescher Abri BII, ٢٤١ - ٢٤٢;
الأعلام للزركلى ٦/ ٣١٢؛ معجم المؤلفين لكحالة ٩/ ١٩٨ - ١٩٩؛ رمضان عبد التواب، مقدمته لكتاب «لحن العوام».

<span data-type="title" id=toc-665>آثاره:</span>
١ - «مختصر كتاب العين للخليل بن أحمد»، وقد وصل إلينا، انظر ص ٥٣ آنفا.
٢ - «لحن العوام»، منه نسخة في مكتبة رئيس الكتاب ١١٢١ (٣٢ ورقة، من القرن العاشر، انظر فهرس المخطوطات ١/ ٣٦٦)؛ نشره رمضان عبد التواب، القاهرة ١٩٦٤؛ انظر في محتواه:
G. Krotkoffin: Bull. Collegeof Artsand Sience, Bagdad ٢/ ١٩٥٧/ ٣ - ١٦.
وقد انتقد هذا الكتاب في كتاب اسمه «الرد على الزبيدى»: وهو ردّ أبى عبد الله محمد بن أحمد بن هشام اللخمى (المتوفى سنة ٥٧٠/ ١١٧٤، راجع، (Br.GI ,٣٠٨ ومنه نسخة في مكتبه رئيس الكتاب ١١٢١/ ٢ (٤٩ ب- ٨٦ ب، من القرن العاشر للهجرة)، ومع رده على «تثقيف اللسان وتلقيح الجنان» لأبى حفص عمر بن خلف بن مكى الصقلى (المتوفى سنة ٥٠١/ ١١٠٧)، فى الاسكوريال ٤٦ (٧٢ ورقة، من القرن السابع للهجرة) و ٩٩ (٩٢ ورقة، نسخت سنة ٦٠٧ هـ، انظر فهرس المخطوطات ١/ ٣٥٥)، ونشر أولهما عبد العزيز مطر في: مجلة معهد المخطوطات العربية ١٢، ٢/ ١٩٦٦/ ٢١ - ١٠٩./ ٣ - «استدراك الغلط الواقع في كتاب العين».
وجد منه ع. الودغيرى ٤٣ صفحة، ونقل عنه الزبيدى نفسه في «كتاب المستدرك» (انظر بعد) ومنه ١٤ ورقة في مخطوطة القرويين بفاس ١١٨ (؟ )، انظر المناهل (الرباط) ٨/ ١٩٧٧/ ١٧٣ - ٢٠٤، ولا سيما ص ١٨٠، ١٨٦ - ١٨٧،','2','489',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (471,'ونقل عنه السيوطى في المزهر ١/ ١٣٥، ٢٧٨، ٢/ ٤٨، ٦٣، ٧٧، ١١٠، ٣٨١ - ٣٩٠.
٤ - «رسالة الانتصار للخليل فيما ردّ عليه في العين»، ذكره القفطى في إنباه الرواة ٣/ ١٠٩.
٥ - «المستدرك من الزيادة في كتاب البارع لأبى على البغدادى على كتاب العين للخليل بن أحمد» ذكره ابن خير، الفهرست ٣٥٠، ويوجد مخطوطا في القرويين بفاس ٦٤ (؟ )، انظر ع. الودغيرى، الموضع المذكور، ص ١٨٠ - ١٨٦.
٦ - «كتاب بسط البارع (لأبى على القالى)»، ونقل عنه في هامش «أدب الكاتب» لابن قتيبة، مخطوطة رئيس الكتاب ٧٤٢ (انظر. (S.A.Bonebakkerin: Oriens ١٣ - ١٤ /١٩٦١ /١٧٤
٧ - «هتك ستور الملحدين» أو «الرد على ابن مسرة (محمد بن عبد الله، المتوفى سنة ٣١٩/ ٩٣١، انظر تاريخ التراث العربى ١/ ٤٨١)»، ومحتواه فقهى، ذكره ابن بشكوال في الصلة ٢/ ٤٦٥؛ والسيوطى في بغية الوعاة ٣٤.

<span data-type="title" id=toc-666>ابن (آل) سيد الأندلسى</span>
هو أبو عبد الله محمد (كذا في المخطوطة، وعند ابن الفرضى، وتبعا له عند الصفدى والسيوطى) أو أبو القاسم أحمد (١٥٦) (فى سائر المصادر) ابن أبان بن (آل) سيد بن أبان اللخمى القرطبى، أحد تلاميذ أبى على القالى (المتوفى سنة ٣٥٦/ ٩٦٧، انظر ص ٢٥٣ آنفا)، كان نحويا (انظر فصل النحو)، نسّابة، لغويا، وولى أحكام الشرطة بقرطبة أيام الحكم الثانى المستنصر (٣٥٠/ ٩٦١ - ٣٦٦/ ٩٧٦). ونقل في مؤلفه الرئيسي في اللغة عن أبى على الفارسى (المتوفى سنة ٣٧٧/ ٩٨٧) وابن جنى (المتوفى سنة ٣٩٢/ ١٠٠١).
_________
(١٥٦) لعل في ذلك خلطا بين أخوين، أحدهما توفى سنة ٣٥٤/ ٩٦٥، والآخر مؤلف كتاب «السماء والعالم»، توفى سنة ٣٨٢/ ٩٩٢.','2','490',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (472,'وقيل إن ابن (آل) سيد توفى سنة ٣٨٢/ ٩٩٢ (٢) بقرطبة.

<span data-type="title" id=toc-667>مصادر ترجمته:</span>
جذوة المقتبس للحميدى ١١٠؛ بغية الملتمس للضبى ١٥٩؛ الصلة لابن بشكوال ١٤؛ ابن الفرضى (١٥٧) ٢/ ٦٩؛ إرشاد الأريب لياقوت ١/ ٣٦٤ (احمد)، ٦/ ٢٦٥ (محمد)؛ إنباه الرواة للقفطى ١/ ٣٠ - ٣١ (أحمد)، ٤/ ١٨٦ - ١٨٧ (ابن سيد)؛ الوافى بالوفيات للصفدى ١/ ٣٣٤ (محمد)، ٦/ ١٩٨ (أحمد)، بغية الوعاة للسيوطى ٤ (محمد)، ١٢٦ (أحمد).- معجم المؤلفين لكحالة ١/ ١٣٢ - ١٣٣، ٨/ ١٩٠ (محمد).
وله «كتاب السماء والعالم (فى اللغة)»، وتذكره المصادر باسم «كتاب العالم» (١٥٨)، وهو كتاب في اللغة مرتب على الموضوعات، قيل إنه كان في نحو مائة مجلد (أو في أربعين مجلدا على ما في إنباه الرواة للقفطى ٢/ ٣٣٤، ٤/ ١٨٧). «بدأ فيه بالفلك وختم بالذرّة».
وقد وصل إلينا منه السفر الثالث مشتملا على أجزاء/ فى خلق الإنسان وصفاته، ومنقسما إلى فصول كثيرة ويشابه كتاب «المخصص» لابن سيده، الذى يجوز أنه احتذى مثاله. وينقل عن كثير من أهل اللغة المعروفين، نحو الخليل بن أحمد أو صاحب العين (كثيرا)، وأبى عبيدة، والأصمعى، وأبى عبيد القاسم بن سلام (كتاب المصنّف ص ١٧٠)، وأبى زيد الأنصارى، وأبى عمرو الشيبانى، والفراء، والكسائى، وابن الأعرابى، وثعلب، وابن كيسان، وابن السكيت (كتاب الألفاظ ص ١٩٩)، وابن قتيبة، وابن دريد (كثيرا). ونقل عنه الفهرى باسم مؤلفه: «محمد بن أبان» فى تحفة المجلد الصريح ص ٤١، ٤٤، ٦٤، ٦٨، ٧٣، ٩٩، ١٠٨، ١٠٩، ١١١، ١١٥، ١٤٦، ١٤٨، ١٦٣.
_________
(١٥٧) التاريخ الذى ذكره ابن الفرضى والمصادر التى تابعته، وهو سنة ٣٥٤/ ٩٦٥، لا يصدق على مؤلف «كتاب السماء والعالم».
(١٥٨) نسب خطأ إلى ابن سيده في إرشاد الأريب لياقوت ٥/ ٨٥؛ نفح الطيب، تحقيق إحسان عباس، ٣/ ١٧٣، ١٩٢، انظر تاريخ التراث العربي ٧/ ٣٦٤.','2','491',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (473,'<span data-type="title" id=toc-668>المخطوطة:</span>
القرويين بفاس ٤٠/ ٢٦٤٦ (٣٢٣ صفحة، أصاب بعضها تلف شديد، وعليها وقف تاريخه سنة ٨٥٥ هـ، راجع مجلة المجمع العلمى العربى بدمشق. (Li stedesmanuscrits No.٢٠٧؛ ٦٨٤ /١٩٥٨ /٣٣
ولعل هذا الجزء هو الذى رآه المقّرى في فاس (انظر نفح الطيب، تحقيق إحسان عباس ٣/ ٣٨١).

<span data-type="title" id=toc-669>أبو عثمان القزاز</span>
هو سعيد بن عثمان بن سعيد البربرى، الملقب «لحية الزبل»، من أهل قرطبة، وكان نحويا لغويا أديبا. ويعد من أحسن تلامذة أبى على القالى. عاش من سنة ٣١٥/ ٩٢٧ إلى سنة ٤٠٠/ ١٠١٠.

<span data-type="title" id=toc-670><span data-type="title" id=toc-672>مصادر ترجمته:</span></span>
الصلة لابن بشكوال ١/ ٢٠٤ - ٢٠٦؛ بغية الملتمس للضبى ٢٩٨؛ إنباه الرواة للقفطى ٢/ ٤٤ - ٤٧؛ بغية الوعاة للسيوطى ٢٥٦.- معجم المؤلفين لكحالة ٤/ ٢٢٧.
وللقزاز «كتاب الرد على صاعد بن الحسن اللغوى البغدادى ... فى مناكير كتابه فى النوادر والغريب المسمّى بالفصوص» (انظر فصل الأدب)، ذكره القفطى وغيره.

<span data-type="title" id=toc-671>أبو غالب بن التّيّانى</span>
هو تمام بن غالب بن عمر المرسى، كان عفيفا ورعا، ووصف بأنه كان إماما في اللغة بالأندلس. توفى سنة ٤٣٦/ ١٠٤٤ بالمرية.
مصادر ترجمته:
جذوة المقتبس للحميدى ١٧٢؛ بغية الملتمس للضبى ٢٣٦؛ الصلة لابن بشكوال ١/ ١٢٢ - ١٢٣؛ إرشاد الأريب لياقوت ٢/ ٣٩٤ - ٣٩٥؛ إنباه الرواة','2','492',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (474,'للقفطى ١/ ٢٥٩ - ٢٦٠؛ ابن خلكان ١/ ١٢٠ - ١٢١؛ بغية الوعاة للسيوطى ٢٠٩.- معجم المؤلفين لكحالة ٣/ ٩٢.

<span data-type="title" id=toc-673>آثاره:</span>
١ - «تلقيح العين (فى اللغة)»، ذكره ياقوت وغيره، ولعله هو نفسه كتاب «فتح العين»، الذى وصفه وأثنى عليه السيوطى (المزهر ١/ ٨٨) والزبيدى (تاج العروس ١/ ١٢).
٢ - «موعب اللغة» نقل عنه الفهرى في «تحفة المجد الصريح» (انظر محمد المنونى فى: تطوان ٨/ ١٩٦٣/ ١٣٩، انظر ص ١٤٤ آنفا)، ونقل عنه أيضا صاحب تاج العروس ٢ ١/ ٤٧٩ (وبأ)، ٩/ ٣١١ (وعد)؛ راجع» -٣ J.Kraemerin: Oriens ٦ /١٩٥٣ /٢١٨ - ٢١٩. شرح الفصيح (لثعلب)»، انظر ص ١٤٣ آنفا.
٤ - «مختصر الجمهرة» لابن دريد، نقل عنه الفهرى إلى جانب «كتاب الموعب» فى تحفة المجد الصريح ص ١٢، ١٤، ١٥، ٢٠، ٢٢، ٢٣، ٢٤، ٢٥، ٢٦، ٢٧، ٢٨، ٣٠، ٣٢، ٣٨، ٣٩، ٤٢، ٤٤، ٤٨، ٥٥، ٥٧، ٥٨، ٦٠، ٦٢، ٦٣، ٦٥، ٦٨، ٧٠، ١١٦، ١٣٦، ١٤٦.','2','493',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (475,'<span data-type="title" id=toc-674>٧ - مؤلفون غير معروفين وكتب مجهولة المؤلف</span>
<span data-type="title" id=toc-675>«كتاب ضروب المنطق»</span>
يبدو هذا الكتاب المجهول المؤلف وكأنه سلف لكتاب «إصلاح المنطق» لابن السكيت. ولعل مؤلفه كان أحد فصحاء الأعراب.
وينقسم إلى أبواب كثيرة، دون أن يتبيّن فيها ترتيب موضوعى أو شكلى، ولا يبدو أن المؤلف قد سمّى مصادره. وينبغى الفحص عمّا بين هذا الكتاب وكتاب «إصلاح المنطق» من النسبة.
واسم هذا الكتاب كما ورد في المخطوطة «كتاب ضروب المنطق مما (أو: وما) لا يستغنى عنه الناس في محاوراتهم وألفاظهم، وهى ألفاظ وجيزة تأتى على الكثير من المعانى»، ويوجد مخطوطا في أسعد ٣٢٤٣/ ٢ (٢٢ أ- ٨٣ أ، من القرن السابع للهجرة).
أوله: «تقول هذا كلام واجز وموجز ووجيز، وهو المختصر في اللفظ».

<span data-type="title" id=toc-676>«كتاب السلاح»</span>
كتاب في السلاح غفل من اسم مؤلفه، وألفه أحد تلامذة الأصمعى بدليل قوله:
(سألت الأصمعى، ١٨ أ، سطر ٣) ولم يعرف إلى الآن، وقد وصلت إلينا قطع منه.
ومخطوطته في الأسكوريال ٢٨) ١٨٩٨ Legajoar.، ورقة ضمن مجموع، من القرن السادس للهجرة، انظر J.Kraemerin: ZDMG ١١٠ /١٩٦١ /٢٥٢ ff., وتتعلق به الصفحات ٢٦٨، ٢٩١ - ٢٩٥.

<span data-type="title" id=toc-677>الهذيلى</span>
لا يستدل على هذا المؤلف الذى كتب اسمه على صفحة العنوان من المخطوطة.
ويروى في كتابه عن أبى عبيدة، والأصمعى، وأبى زيد الأنصارى، وقد يستدل من','2','494',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (476,'هذا انه كان زميلا في الدرس ومعاصرا لأبى حاتم السجستانى وأبى نصر الباهلى، وأن وفاته كانت نحو سنة ٢٤٠ هـ.
واسم كتابه «كتاب الفرق»، ويوجد مخطوطا في أسعد ٣٢٤٣/ ١ (١ أ- ٢١ ب، من القرن السابع للهجرة). أوله: «هذا كتاب ما خالف فيه الإنسان ذوات الأربع من البهائم والسباع والطير؛ يقال: فم الانسان؛ وفيه ثلاث لغات، يقال: فم، وفم، وفم»./

<span data-type="title" id=toc-678>العصافى </span>(؟ )
هو أبو القاسم عمر بن محمد بن الهيثم، يبدو أنه توفى في أوائل القرن الرابع/ العاشر (كشف الظنون ٧٢٣).
واسم كتابه «كتاب خلق الإنسان»، وهو أهم المصادر التى اعتمد عليها السيوطى فى «غاية الإحسان في خلق الإنسان»، برلين ١، ٩٨٠ Spr.) ٧٠٣٨ ب- ٣٣ أ)، حيث نقل عنه في كل صفحة.

<span data-type="title" id=toc-679>أبو عبد الله محمد بن محمد</span>
الصّبحى، الصّنجى، الصّبيحى، أو ما شاكل ذلك، عاش في الشطر الأول من القرن ٤/ ١٠. ولم يرد ذكره في كتب التراجم. ولعل المقصود به هو أبو عبد الله محمد بن محمد المفجّع البصرى (انظر تاريخ التراث العربى ٢/ ٥٠٩ - ٥١٠؛ وآنفا ص ١٧٥).
واسم كتابه «كتاب الاشتقاق وشرح الصفات من كلام العلماء ولغة الفصحاء»، يوجد مخطوطا في برلين Or.quart.٣٩ ,٠٧ - ) ٧٠٤٢ ورقة، نسخت نحو سنة ١٢٠٠ هـ)، ويحتوى على الفصول الآتية:
١ - فى اشتقاق الأمر والقضاء والقدر.
٢ - فى اشتقاق الخلق والدنيا والآخرة واللوح والقلم والعرش والكرسى.
٣ - فى اشتقاق الملائكة وما لها من الأسماء والصفات ومعنى الجن والانس.
٤ - فى اشتقاق الجنة وما لها من الأسماء والنار ونعوتها.','2','495',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (477,'٥ - فى معنى الصراط والأعراف والبرزخ.
٦ - فى معنى الثواب والعقاب والوزر والإثم والقيامة.
٧ - فى معنى العالم والحيوان مثل الإنسان والبهيمة والنبات.
٨ - فى معنى السماء والأرض والفلك والبروج والنجوم والكواكب والأنواء والشمس والقمر.
٩ - فى معنى الإقليم والجزيرة والمصر والمدينة والبلدة والكورة والطرق والمنازل وأسماء مدن عربية.
١٠ - فى صفة الجبال والأودية والرمال والمياه والبحار والآبار.
١١ - فى الرياح والسحاب والرعد والبرق والمطر.
١٢ - فى معرفة الأيام والليالى والشهور والأزمنة.
١٣ - فى اشتقاق الروح والنفس والعقل والعلم والأدب والحكمة.
واستشهد بأبيات كثيرة لشعراء قدامى، ونقل عن لغويين من مختلف المدارس في أواخر القرن الثانى/ الثامن والقرن الثالث/ التاسع؛ ويبدو أن أحدثهم زمانا هو ابن دريد (المتوفى سنة ٣٢١/ ٩٣٣).

<span data-type="title" id=toc-680>«كتاب الجراثيم»</span>
معجم مرتب على الموضوعات، واسم مؤلفه، كما على صفحة العنوان، هو أبو محمد عبد الله بن مسلم (ولم يعد من الممكن قراءة بقية الاسم)، ومن ثمة نسب خطأ إلى ابن قتيبة (انظر ص ١٦١ آنفا) (راجع / (G.Lecomte ,Ibn Qutayba ١٧٣ - ١٧٤: وتبعا للإسناد الوحيد في الكتاب (ص ٢٦٩).
كان المؤلف تلميذا للحسن بن على الطوسى (المتوفى سنة ٣٠٨/ ٩٢٠ أو ٣١٢/ ٩٢٤، انظر معجم المؤلفين لكحالة ٣/ ٢٦٤). والكتاب مجموع إلى حد بعيد من «الغريب المصنف» لأبى عبيد (انظر
R. Abdel- Tawab, Das Kitabal- Garlb al- Musannaf ... , Heppenheim ١٩٦٢, S. ٩٨ - ١٠١, ١٠٢
)، وذلك لأن المؤلف كثيرا ما يذكر أبا عبيد إلى جانب الخليل، والأصمعى، وأبى زيد، وغيرهم، أما اسم هذا','2','496',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (478,'المصدر أو غيره من المصادر فلا يذكره. وباب «كتاب الكرم» أورده على أنه كلام أبى حاتم السجستانى. والأجزاء الأولى التى لم توضع عناوينإلا لبعض فصولها تدور موضوعاتها على «خلق الإنسان» و «الصفات». ويلى ذلك (مع اختصارنا للعناوين):
«كتاب النساء» (ص ٩٥ - ١١٩)، «أبواب الطعام» (ص ١٢٠ - ١٤٣)، «باب الأمر والنهى» (ص ١٤٣ - ١٤٧)، «باب الحاجة والكسب» (ص ١٤٧ - ١٥٢)، «باب الإقامة» (ص ١٥٢ - ١٥٨)، «باب النوادر ... » (ص ١٥٨ - ١٦٥)، «باب آخر من النوادر» (ص ١٦٥ - ١٧٢)، «باب الرحل» (ص ١٧٢ - ١٩٣)، «باب الحقد والضغينة» (ص ١٩٤ - ١٩٧)، «باب يجمع أبوابا» (ص ١٩٧ - ٢٠٩)، «باب الأزمنة والرياح» (ص ٢١٠ - ٢١٥)، «باب السحاب والمطر» (ص ٢١٦ - ٢٣٤)، «باب الجبال والأرضين» (ص ٢٣٤ - ٢٥٠)، «باب الشجر والنبات» (ص ٢٥٠ - ٢٦١)، «كتاب النخل والكرم» (ص ٢٦١ - ٢٦٩)، «كتاب الكرم عن أبى حاتم» (ص ٢٦٩ - ٢٩٢)، «كتاب الخيل» (ص ٢٩٢ - ٣٢٧)، «كتاب النّعم» (ص ٣٢٨ ٤٠٨)، «كتاب الطير» (ص ٤٠٩ - ٤١٥)، «باب نوادر الأسماء» (ص ٤١٦ - ٤٢٢)، «باب نوادر الفعل» (ص ٤٢٢ - ٤٢٩).
والباب الأخير «باب عيوب الشعر وأسماء القوافى» (ص ٤٣٠ - ٤٣٩) خارج عن نطاق سائر الكتاب.

<span data-type="title" id=toc-681>المخطوطة:</span>
«كتاب الجراثيم المستوعب لأسماء أصول العالم والبهائم والوحش والطير والسباع والهوام وكل نسمة تعرف ومتصرفاتهم وأفعالهم وأسماء أنواع الأرض والشجر والنبات وغير ذلك والعروض والقوافى»، الظاهرية، عام ١٥٩٦ (٤٣٩ صفحة، من القرن السادس للهجرة، راجع فهرس اللغة ٨١ - ٨٢).
ونشر من الكتاب إلى الآن الأبواب الآتى ذكرها، بعضها على أنه مجهول المؤلف، وبعضها الآخر منسوبا لمؤلفين آخرين (نذكرها على ترتيب الأبواب في المخطوطة):
أ) «باب الألسنة والكلام والأصوات والسكوت»، نشره لويس شيخو بمثابة «نخبة من كتاب الجراثيم لعبد الله بن مسلم» فى ذيل كتاب «فقه اللغة» للثعالبى، بيروت ١٨٨٥، ص ٣٤٨ - ٣٥٠.','2','497',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (479,'ب) «أبواب اللبن والشراب»، نشره لويس شيخو في: «البلغة في شذور اللغة»، بيروت ١٩٠٨، الطبعة الثانية ١٩١٤، ص ١٤٦ - ١٥٣.
ج) «باب الرحل وآلاته والأوانى في السفر والحضر والدور والبيوت والأخبية والأبنية»، نشره لويس شيخو باسم «كتاب الرحل والمنزل» فى: المشرق ١١/ ١٩٠٨/ ٤٤٠ - ٤٥٣ وفي: «البلغة في شذور اللغة» (انظر آنفا) ص ١٢١ - ١٤٠.
د) «باب الأزمنة والرياح وأسماء الدهر ونعوت الأيام والليالى»، نشره لويس شيخو بمثابة: «نخبة من كتاب الجراثيم» (انظر آنفا)، ص ٣٥١ - ٣٥٦./ هـ) «باب الشجر والنبات في السهل والجبل»، نشره لويس شيخو، الموضع المذكور، ص ٣٥٧ - ٣٦٥، راجع تاريخ التراث العربى ٤/ ٣٤٤.
و) «كتاب النخل والكرم»، نشره أوغست هفنر، انظر ص ٧٥ آنفا، على أنه للأصمعى.
ز) «كتاب الكرم عن أبى حاتم السجستانى» نشره كذلك أوغست هفنر على أنه للأصمعى، انظر ص ٩٥ آنفا؛ وتاريخ التراث العربى ٤/ ٣٣٦، ٣٤٤.
ح) «كتاب النعم والبهائم والوحش والسباع والطير والهوام وحشرات الأرض»، نشره M.Bouygesin: MFO ٣ /١٩٠٨ /١ - ١٤٤. وانظر ما كتبه عنه في، MFO ٧ /١٩١٤ - ٢١ /٣٠٥ - ٣١٠ راجع تاريخ التراث العربى ٣/ ٣٧٦ - ٣٧٧.

<span data-type="title" id=toc-682>«كتاب المبانى لنظم المعانى»</span>
مصنف واسع في لغة القرآن لمؤلف لم يستدل عليه إلى الآن. وكان هذا المؤلف في أرجح الاحتمالات فارسيا، أو أنه عاش في فارس. وبدأ تصنيف الكتاب في شهر شعبان من سنة ٤٢٥/ يوليو ١٠٣٤ وكان، على ما ذكر في المقدمة، قد ألف قبل ذلك عدة كتب في علوم القرآن. ولا يمكن التعرف على أسماء أساتذته المباشرين الذين يبدو أنهم غير معروفين في المراجع. ومن جملتهم أبو عبد الله محمد بن على، وأبو عبد الله محمد بن الهيصم، وأبو القاسم عبد الله بن محمشاذ، وأبو عبد الله محمد بن المنتصر، وأبو جعفر محمد بن أحمد بن جعفر.','2','498',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (480,'أما الأسماء المعروف فلا تظهر إلا في الجيل الثانى من الأساتذة، ومن بينهم اللغوى الأزهرى (المتوفى سنة ٣٧٠/ ٩٨٠، انظر ص ٢٠١ آنفا)، وعالمالحديث أبو أحمد الغطريفى (المتوفى سنة ٣٧٧/ ٩٨٧، انظر تاريخ التراث العربى ١/ ٢٠٣، وأضف إلى رقم ١ مخطوطة برلين ١٥٦٣). وأهم مصادره كما يظهر في الربع الأول وقريبا من آخر الكتاب هو «كتاب فيه ما فيه» لأبى سهل محمد بن محمد بن علي الطالقاني الأنمارى (المتوفى نحو سنة ٣٢٥/ ٩٣٧) برواية أبى عبد الله محمد بن على وابن محمشاذ المذكورين آنفا. ومن يرجع إليهم في القراءات القرآنية الكسائى والفراء وثعلب (يسميه أحمد بن يحيى) وابن الأنبارى من الكوفيين.

<span data-type="title" id=toc-683>المخطوطة:</span>
برلين ٢٧٣، ٣٠١ We.-) ٩١٠ ورقة، نسخت نحو سنة ٧٠٠ هـ)، ونشر آرثر جفرى مقدمة الكتاب في: «مقدمتان في علوم القرآن»، القاهرة ١٩٥٤، ص ٥ - ٢٥٠./

<span data-type="title" id=toc-684>«مسائل في اللغة» لمجهول</span>
فى الأجزاء المجهولة المؤلف من مجموع في اللغة بكوبريلى ١٣٩٣ (كتب سنة ٦٣٥ هـ)، بعض فقر لغوية موجزة، من الجائز انها ترجع إلى سنة ٤٣٠ هـ. وهى كالتالى:
١ - «مسألة: كل ما جاء من الأسماء على تفعال وهو مكسور ...
(٩٠ ب- ٩١ أ)»؛ ٢ - «مسألة المهراس» و «لغات متفرقة» (٩١ أ- ٩٣ ب)، مع نقول من جملتها نقل عن «كتاب النوادر» لأبى عمرو الشيبانى؛ ٣ - «تفسير ألفاظ، لا سيما من الحديث والقرآن (٩٤ ب- ٩٦ ب).

<span data-type="title" id=toc-685>«كتاب الأمثال»</span>
معجم الامثال هذا، المجهول المؤلف، مرتب على حروف المعجم، ويحتوى على شروح مقتضبة. والأزهرى هو أحدث رواته الذين نقل عنهم كما نقل عن أبى عمرو','2','499',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (481,'ابن العلاء، وأبى عبيدة، وأبى زيد الأنصارى، والأصمعى، وأبى عبيد، وابن الأعرابى، وابن السكيت، والمفضل ابن سلمة.
ومن الجائز أن الكتاب قد ألف في النصف الأول من القرن الخامس/ الحادى عشر. ويذكر هذا الكتاب في بعض الأحيان منسوبا لزيد بن رفاعة (انظر ص ١٨٣ آنفا) خطأ (انظر، (Sellheim ,Sprichworter ١٤٣ - ١٤٤ وانظر أيضا عائشة عبد الرحمن في: المورد ٩، ٣/ ١٩٨٠/ ٢٥٠.
وطبع الكتاب على مخطوطة بمكتبة الدولة برامبور (نسخت سنة ٥٧٤ هـ)، فى حيدرآباد سنة ١٣٥١، انظر زلهايم، الموضع المذكور.

<span data-type="title" id=toc-686>محمد بن قاسم بن عزرة الأزدى</span>
لغوى غير معروف، ذكر له الزبيدى (تاج العروس ١/ ٤) بين مصادره: «كتاب السرج واللجام والبيضة والدرع» و «كتاب الحمام والهدى».

<span data-type="title" id=toc-687>«مجموع غرائب أحاديث النبى»</span>
لعل هذا الكتاب قد وضع في الشطر الأول من القرن الخامس/ الحادى عشر، وصل إلينا ناقصا من أوله. وبآخره: «تم مجموع غرائب أحاديث النبى المستخرجة من غريب الحديث لأبى عبيد (القاسم بن سلام) والقتيبى (أى ابن قتيبة) وأبى سليمان الخطابى ومن أعلام الحديث». وكثيرا ما يورد المؤلف- ويسميه راوى الكتاب بالقاضى الإمام الأجل- تفسيرات من عنده./ ويحتوى القسم الأول على ألفاظ الغريب في أحاديث نبوية قصيرة، مرتبة على حروف المعجم (ينقص حرف الألف، ومن الباء- الجيم غير تام). وفي القسم الثانى (من ١٥٣ أ) تأتى أحاديث طويلة تتضمن أكثر من ثلاثة ألفاظ غريبة.

<span data-type="title" id=toc-688>المخطوطة:</span>
المتحف البريطانى ٢٠٥) ٤٥٨٩ Or، ورقة، نسخت سنة ٤٨٨ هـ، انظر الملحق، رقم ٨٤٤).','2','500',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (482,'<span data-type="title" id=toc-689>«تصنيف في اللغة» لمؤلف مجهول</span>
مخطوطة بشير أغا (أيوب) ١٩٣/ ٤ (٩٤ أ- ١٠٣ ب، نسخت سنة ٦٢٠ هـ، وكتب خطأ على صفحة العنوان «كتاب الأضداد»، انظر O.Rescherin: MFO. (٥ /١٩١٢ /٥٣٥ تحتوى هذه المخطوطة على الأبواب الأخيرة من كتاب مختصر في معانى بعض ألفاظ، يحتمل أنه وضع قبل سنة ٤٣٠ هـ. وعناوين هذه الأبواب هي: «باب الأضداد» (٩٤ أ)؛ «باب المراتب» (٩٦ أ)؛ «باب ما يقال للذكر ولا يقال للأنثى» (٩٨ ب)؛ «باب ما يقال للأنثى ولا يقال للذكر» (٩٩ أ)؛ «باب ما جاء على حروف المعجم في اللغة»، «٩٩ ب، فى أسماء الحروف باعتبارها كلمات ذوات معان»؛ «باب الأسماء» (١٠٠ أ، فى معانى أسماء الأعلام)؛ «باب ما يتعدى ولا يتعدى» (١٠٠ ب، فى الأفعال المتعدية واللازمة).','2','501',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (483,'ثبت المصادر والمراجع
مجموعات المخطوطات العربية في مكتبات العالم','2','503',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (484,'<span data-type="title" id=toc-690>ثبت المصادر والمراجع</span>
ثبت المصادر والمراجع هذا، المرتب على حروف المعجم، يحتوى على أسماء الكتب والمخطوطات والدوريات التى أفدنا منها وذكرناها مختصرة في هذا المجلد والمجلد التالى له، القريب منه في موضوعه (النحو). أما المصادر والمراجع التى أفدنا منها نادرا، وذكرنا أسماءها في نص الكتاب مستوفاة دون اختصار فلم نتكلف إدراجها هنا مرة أخرى. وتوجد اختصارات أسماء المكتبات وفهارس المخطوطات مجموعة في المجلد الذى صدر على حدة باسم «تاريخ التراث العربى: مجموعات المخطوطات العربية في مكتبات العالم»، الرياض ١٤٠٢ هـ/ ١٩٨٢ م.
وهذه المصادر والمراجع هي:

<span data-type="title" id=toc-691>(حرف الهمزة)</span>
كتاب الإبل، تأليف أبى سعيد عبد الملك بن قريب الأصمعى، فى: الكنز اللغوى في اللسن العربى، تحقيق أوغست هفنر. ليبسك ١٩٠٥، ص ٦٦ - ١٣٦ و ١٣٧ - ١٥٧ (اعتمادا على مخطوطات فينا والاسكوريال). الإتقان في علوم القرآن، لجلال الدين عبد الرحمن السيوطى. جزآن، بولاق ١٢٨٧.
أخبار أصفهان لأبى نعيم- ذكر أخبار أصفهان، تأليف ... أبى نعيم أحمد بن عبد الله الأصبهاني، تحقيق سون ديدرنج. جزآن، ليدن ١٩٣١ - ١٩٣٤.
كتاب أخبار النحويين البصريين، تأليف أبى سعيد الحسن بن عبد الله السيرافى، تحقيق ف. كرنكو. باريس- بيروت ١٩٣٦.
(٢) بتحقيق طه محمد الزينى ومحمد عبد المنعم خفاجى. القاهرة ١٩٥٥.
أخلاق الوزيرين للتوحيدى- أخلاق الوزيرين. مثالب الوزيرين الصاحب بن عباد وابن العميد، لأبى حيان على بن محمد التوحيدى، تحقيق محمد بن تاويت الطنجى. دمشق (١٩٦٥).
إرشاد الأريب لياقوت- إرشاد الأريب إلى معرفة الأديب، المعروف بمعجم الأدباء أو طبقات الأدباء، لياقوت الحموى، اعتنى بنسخه وتصحيحه د. س.','2','505',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (485,'مرجليوث. ٧ أجزاء، لندن ١٩٢٣ - ١٩٢٥ (سلسلة جب التذكارية ٦). وأعيد طبعه فى طهران، بدون تاريخ.
(٢) بتحقيق أحمد فريد رفاعى. ٢٠ جزءا، القاهرة ١٩٣٦ - ١٩٣٨. الأزمنة والأمكنة، للشيخ أبى على المرزوقى. جزآن، حيدرآباد: دائرة المعارف العثمانية ١٣٣٢ (١٩١٤).
الأشباه والنظائر فى النحو، للشيخ العلامة جلال الدين السيوطى. ٤ أجزاء.
حيدرآباد، ط ٢ ١٣٥٩ - ١٣٦١.
الإصابة فى تمييز الصحابة لابن حجر العسقلانى. ٤ مجلدات (فى ٨ أجزاء)، كلكتا ١٨٥٤ - ١٨٧٣.
إصلاح المنطق، لابن السكيت، تحقيق أحمد محمد شاكر وعبد السلام هارون.
القاهرة ١٩٤٩.
كتاب الأضداد، تأليف يعقوب بن إسحاق السكيت، فى: ثلاثة كتب في الأضداد للأصمعى والسجستانى ولابن السكيت، تحقيق أوغست هفنر. بيروت ١٩١٢، ص ١٦٣ - ٢٢٠.
كتاب الأضداد، لمحمد بن القاسم الأنبارى، تحقيق محمد أبى الفضل، إبراهيم. الكويت ١٩٦٠.
كتاب الأضداد في كلام العرب، لأبي الطيب عبد الواحد بن على اللغوى الحلبى، تحقيق عزة حسن. جزآن، دمشق ١٩٦٣.
الأعلام للزركلى- الأعلام. قاموس تراجم لأشهر الرجال والنساء من العرب والمستعربين والمستشرقين، لخير الدين الزركلى. ٩ أجزاء ومستدرك، دمشق ط ٢ بدون تاريخ، المستدرك الثانى (للطبعة الثانية والثالثة). بيروت ١٩٧٠ إعلام النبلاء للحلبى- إعلام النبلاء بتاريخ حلب الشهباء، لمحمد راغب الطباخ. ٧ أجزاء، حلب ١٩٢٢ - ١٩٢٦.
أعيان الشيعة (١)، تأليف السيد محسن الأمين العاملى. ٥٦ جزءا، دمشق ١٩٣٥ - ١٩٦٣.
(٢) ١٧ جزءا، بيروت ١٩٦٠ - ١٩٦١.','2','506',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (486,'الأغانى (٣)، لأبى الفرج الأصفهاني. ٢٤ مجلدا، القاهرة، دار الكتب المصرية، ١٩٢٧ - ١٩٧٤.
كتاب الأفعال، تأليف أبى عثمان سعيد بن محمد المعارفى السرقسطى، تحقيق حسين محمد شرف ومحمد مهدى علام. ٤ أجزاء (فى ٥ مجلدات)، القاهرة ١٩٧٥ - ١٩٨٠.
إقليد الخزانة، لعبد العزيز الميمنى. لاهور ١٩٢٧.
الأمالى، لأبى على اسماعيل بن قاسم القالى. ٣ أجزاء، القاهرة ١٣٢٤.
أمالى الزجاجى (أبى القاسم عبد الرحمن بن اسحاق الزجاجى)، تحقيق عبد السلام محمد هارون. القاهرة ١٣٨٢ (١٩٦٢).
أمالى اليزيدى- الأمالى، فيها مراث وأشعار ولغة وغيرها، لمحمد بن العباس اليزيدى، تحقيق ف. كرنكو. حيدرآباد، دائرة المعارف العثمانية ١٩٤٨.
كتاب الإمتاع والمؤانسة، تأليف أبى حيان التوحيدى، تحقيق أحمد أمين وأحمد الزين. ٣ أجزاء في مجلد، القاهرة ١٩٣٩ - ١٩٤٤. أعيد طبعه في بيروت، بدون تاريخ.
إنباه الرواة للقفطى- إنباه الرواة على أنباء النحاة، تأليف على بن يوسف القفطى، تحقيق محمد أبى الفضل إبراهيم. ٤ أجزاء، القاهرة ١٩٥٠ - ١٩٧٣.
الأنساب للسمعانى (١) - الأنساب، لأبى سعيد عبد الكريم بن أبى بكر السمعانى، طبعه مصورا وقدم له د. س. مرجليوث. ليدن- لندن ١٩١٢ (سلسلة جب التذكارية ٢٠).
(٢) بتحقيق عبد الرحمن بن يحيى المعلمى اليمانى. ٦ أجزاء، حيدرآباد ١٩٦٢ - ١٩٦٦.
كتاب الإنصاف في مسائل الخلاف بين النحويين البصريين والكوفيين، لأبى البركات عبد الرحمن بن محمد بن أبى سعيد الأنبارى، تحقيق غوتهولد فايل. ليدن ١٩١٣.
الأوائل، لأبى هلال الحسن بن عبد الله بن سهل العسكرى، تحقيق محمد المصرى ووليد قصاب. جزآن، دمشق ١٩٧٥.','2','507',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (487,'<span data-type="title" id=toc-692>(حرف الباء)</span>
البارع في اللغة، لأبى على اسماعيل بن القاسم القالى البغدادى، تحقيق هاشم الطعان. بغداد- بيروت ١٩٧٥.
البداية والنهاية في التاريخ لإسماعيل بن عمر بن كثير. ١٤ جزءا، القاهرة ١٣٥١ - ١٣٥٨.
البرهان في علوم القرآن، لبدر الدين محمد بن عبد الله الزركشى، تحقيق محمد أبى الفضل إبراهيم. ٤ أجزاء، القاهرة ١٩٥٧.
البصائر والذخائر، لأبى حيان التوحيدى، تحقيق إبراهيم الكيلانى. ٤ أجزاء (فى ٦ مجلدات)، دمشق ١٩٦٤ - ١٩٦٦ (؟ ).
بغية الملتمس في تاريخ رجال أهل الأندلس، لأحمد بن يحيى القرطبى الضبى، تحقيق ف. كوديرا وخ. ربيرا. مدريد ١٨٨٥.
كتاب بغية الوعاة في طبقات اللغويين والنحاة، تأليف العلامة الحافظ جلال الدين عبد الرحمن السيوطى الشافعى، تحقيق محمد أمين الخانجى وأحمد بن الأمين الشنقيطى. القاهرة ١٣٢٦.
(٢) تحقيق محمد أبى الفضل إبراهيم. جزآن، القاهرة ١٩٦٤، ١٩٦٥.
البيان والتبيين، تأليف أبى عثمان عمرو بن بحر الجاحظ، تحقيق عبد السلام محمد هارون. ٤ أجزاء، القاهرة ١٩٤٨ - ١٩٥٠.
كتاب البئر، لأبى عبد الله محمد بن زياد الأعرابى. تحقيق رمضان عبد التواب.
القاهرة ١٩٧٠ (المكتبة العربية).

<span data-type="title" id=toc-693>(حرف التاء)</span>
تاج العروس- شرح القاموس المسمى تاج العروس من جواهر القاموس، للإمام اللغوى محب الدين أبى الفيض السيد محمد مرتضى الحسينى الواسطى الزبيدى الحنفى. ١٠ أجزاء، القاهرة: المطبعة الخيرية ١٣٠٦ - ١٣٠٧. أعيد طبعه في بنغازى- بيروت ١٩٦٦.','2','508',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (488,'تاج العروس (٢) - تحقيق عبد الستار أحمد فراج وآخرين. الأجزاء ١ - ١٩ (لم يتم بعد)، الكويت ١٩٦٥ - ١٩٨٠.
تاريخ بغداد- تاريخ بغداد أو مدينة السلام، للحافظ أبى بكر أحمد بن على الخطيب البغدادى، وضعه في أزهى عصور الاسلام منذ تأسيسها إلى وفاته عام ٤٦٣ هـ. ١٤ مجلدا، القاهرة: الخانجى- بغداد: المكتبة العربية ١٩٣١.
تاريخ جرجان للشامى (٢) - تاريخ جرجان أو كتاب معرفة علماء أهل جرجان لأبى القاسم حمزة بن يوسف بن إبراهيم الشامى. حيدرآباد ط ٢ ١٩٦٧.
الحكماء للقفطى- تاريخ الحكماء، لأبى الحسن على بن يوسف القفطى، نشره ى. لبرت. ليبسك ١٩٠٣.
تاريخ خليفة بن خياط، تحقيق أكرم ضياء العمرى. جزآن، بغداد ١٩٦٧.
تاريخ الرسل والملوك، لأبى جعفر محمد بن جرير الطبرى، تحقيق دى خويه وآخرين. ٣ مجلدات، ليدن ١٨٧٩ - ١٩٠١.
تاريخ العلماء للتنوخى- كتاب تاريخ العلماء النحويين من البصريين والكوفيين وغيرهم، لأبى المحاسن المفضل بن محمد التنوخى، مخطوطة الأحقاف (اليمن)، فى خزانة حسين بن سهل الخاصة ١٩٢ (٧١ أ- ٨٨ ب، نسخت سنة ٧٣١ هـ)، تحقيق عبد الفتاح محمد الحلو. الرياض ١٤٠١/ ١٩٨١. [وقد رجعنا إلى المخطوطة قبل ظهور الكتاب محققا].
تاريخ علماء الأندلس (١)، تأليف أبى الوليد عبد الله بن الفرضى. تحقيق ف.
كوديرا. جزآن، مدريد ١٨٩١ - ١٨٩٢.
(٢) تحقيق عزت العطار. القاهرة ١٩٥٤.
تاريخ مدينة دمشق وذكر فضلها وتسمية من حلّها من الأماثل أو اجتاز بنواحيها من واردها وأهلها، لأبى القاسم على بن الحسن بن عساكر، تحقيق صلاح الدين المنجد. مجلدان، دمشق ١٩٥١ - ١٩٥٣؛ مجلد ١٠ تحقيق محمد أحمد دهمان، دمشق، بدون تاريخ.
تاريخ اليعقوبى (أحمد بن أبى يعقوب بن جعفر اليعقوبى)، تحقيق هوتسما.
مجلدان، ليدن ١٨٨٣.','2','509',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (489,'تبصير المنتبه بتحرير المشتبه، تأليف ابن حجر العسقلانى، تحقيق على محمد البجاوى ومحمد على النجار. ٤ أجزاء، القاهرة ١٩٦٤ - ١٩٦٧.
تتمة يتيمة الدهر، لأبى منصور عبد الملك الثعالبى، تحقيق عباس إقبال. جزآن، طهران ١٣٥٣ هـ.
تثقيف اللسان وتلقيح الجنان، لابن مكى الصقلى، تحقيق عبد العزيز مطر.
القاهرة ١٩٦٦.
تحفة الأبيه في من نسب إلى غير أبيه، لمحمد بن يعقوب الفيروزابادى، فى: نوادر المخطوطات ١.
تحفة المجد الصريح في شرح كتاب الفصيح للفهرى، انظر ص ١٤٤ آنفا.
تحقيق ما للهند- كتاب البيرونى في تحقيق ما للهند من مقولة مقبولة فى العقل أو مرذولة. حيدرآباد ١٣٧٧ (١٩٥٨).
تذكرة الحفاظ، لمحمد بن أحمد الذهبى. ٤ أجزاء (متصلة الترقيم). حيدرآباد، دائرة المعارف العثمانية، ١٩٥٥ - ١٩٥٨.
تسمية ما ورد به الخطيب البغدادى دمشق، لمحمد بن أحمد بن محمد المالكى، نشره يوسف العش في كتابه: الخطيب البغدادى، مؤرخ بغداد ومحدثها. دمشق ١٩٤٥، ص ٩٢ - ١١٠.
تقريب التهذيب، لابن حجر العسقلانى. القاهرة، المطبعة العلمية، بدون تاريخ (بعد سنة ١٩٦٠؟ ).
التكملة لابن الأبار (٢) (محمد بن عبد الله). تحقيق عزت العطار. القاهرة ١٩٥٥.
التكملة للصغانى- التكملة والذيل والصلة لكتاب تاج اللغة وصحيح العربية، تأليف الحسن بن محمد بن الحسن الصغانى، تحقيق عبد العليم الطحاوى وآخرين.
٤ أجزاء، القاهرة ١٩٧٠ - ١٩٧٤.
كتاب التلخيص في معرفة أسماء الأشياء لأبى هلال الحسن بن عبد الله بن سهل العسكرى، تحقيق عزة حسن. جزآن، دمشق ١٩٦٩.
تلخيص أخبار النحويين واللغويين المذكورين في كتاب الانباه، لابن مكتوب،','2','510',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (490,'مخطوطة دار الكتب بالقاهرة، التيمورية ٢٠٩٦ تاريخ (انظر مقدمة فصل النحو، ونقلت الأخبار عن انباه الرواة للقفطى).
كتاب التنبيه على حدوث التصحيف، تأليف حمزة بن الحسن الأصفهاني، تحقيق محمد أسعد طلس وأسماء الحمصى وعبد المعين الملوحى. دمشق ١٩٦٨.
تهذيب ابن عساكر- تهذيب تاريخ ابن عساكر (على ترتيب جديد مع اختصار الأسانيد، لم يتم) لعبد القادر بن أحمد بن مصطفى بن بدران. ٧ أجزاء، دمشق ١٣٢٩ - ١٣٥١.
تهذيب التهذيب، لابن حجر العسقلانى. ١٢ جزءا، حيدرآباد ١٣٢٥ - ١٣٢٧.
تهذيب اللغة، لأبى منصور محمد بن أحمد الأزهرى، تحقيق عبد السلام محمد هارون وآخرين. ١٥ جزءا، القاهرة ١٩٦٤ - ١٩٦٧.
مستدرك- تهذيب اللغة لأبى منصور محمد بن أحمد الأزهرى الهروى. المستدرك على الأجزاء السابع والثامن والتاسع، تحقيق رشيد عبد الرحمن العبيدى. القاهرة ١٩٧٥.

<span data-type="title" id=toc-694>(باب الثاء)</span>
ثمار القلوب في المضاف والمنسوب، لأبى منصور عبد الملك بن محمد بن اسماعيل الثعالبى النيسابورى، تحقيق محمد أبى الفضل ابراهيم. القاهرة ١٩٦٥.

<span data-type="title" id=toc-695>(باب الجيم)</span>
جذوة المقتبس في ذكر ولاة الأندلس وأسماء رواة الحديث وأهل الفقه والأدب وذوى النباهة والشعر، تأليف أبى عبد الله محمد بن فتوح بن على الحميدى، تحقيق محمد بن تاويت الطنجى. القاهرة ١٣٧١/ ١٩٥٣.
الجرح والتعديل، لعبد الرحمن بن أبى حاتم الرازى. ٤ أجزاء، حيدرآباد ١٩٥٢ ١٩٥٣.
جمهرة أنساب العرب، لابن حزم، تحقيق إ. ليفى بروفنسال. القاهرة ١٩٤٨.','2','511',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (491,'جمهرة اللغة، تأليف أبى بكر محمد بن الحسن بن دريد الأزدى البصرى (نشره ف. كرنكو). ٣ أجزاء وجزء رابع للفهارس، حيدرآباد ١٣٤٤ - ١٣٥١.
الجواهر المضيئة في طبقات الحنفية، لمحمد بن أبى الوفاء محمد القرشى. جزآن، حيدرآباد، دائرة المعارف العثمانية ١٣٣٢.
كتاب الجيم، لأبى عمرو الشيبانى، تحقيق إبراهيم الأبيارى وآخرين. ٣ أجزاء، القاهرة ١٩٧٤ - ١٩٧٥.

<span data-type="title" id=toc-696>(حرف الحاء)</span>
حاشية على شرح بانت سعاد لابن هشام، تأليف عبد القادر بن عمر البغدادى، تحقيق نظيف محرم خواجه. فيسبادن ١٩٨٠ (النشرات الاسلامية ٢٧ أ).
حسن المحاضرة في أخبار مصر والقاهرة، تأليف العلامة الشيخ جلال الدين السيوطى الشافعى. جزآن، القاهرة: المطبعة الشرقية ١٣٢٧.
كتاب حياة الحيوان الكبرى، للعلامة كمال الدين الدميرى. جزآن بولاق ١٢٨٤ (١٨٦٧).
فهرس مصادر كتاب حياة الحيوان للدميرى، وضعه يوسف دى شوموجى في:
J. A. ٢١٣/ ١٩٢٨/ ٥ - ١٢٨
كتاب الحيوان، تأليف أبى عثمان أبى عمرو بن بحر الجاحظ، تحقيق عبد السلام محمد هارون. ٧ أجزاء، القاهرة ١٩٣٨ - ١٩٤٧.

<span data-type="title" id=toc-697>(باب الخاء)</span>
خزانة الأدب ولب لباب لسان العرب، شرح عبد القادر بن عمر البغدادى على شواهد شرح الكافية للرضى. ٤ أجزاء، بولاق ١٢٩٩ (١٨٨٢).
الخصائص (١) لأبى الفتح عثمان بن جنى. الجزء الأول (لم يصدر غيره)، القاهرة ١٩١٣.
(٢)، تحقيق محمد على النجار. ٣ أجزاء، القاهرة ١٩٥٢، ١٩٥٥، ١٩٥٧، أعيد طبعه في بيروت، بدون تاريخ.','2','512',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (492,'<span data-type="title" id=toc-698>(باب الدال)</span>
درة الغواص، للحريرى، تحقيق هـ. توربكه. ليبسك ١٨٧١.
الدرة الفاخرة في الأمثال السائرة، للإمام حمزة بن الحسن الأصفهاني، تحقيق عبد المجيد قطامش. جزآن، القاهرة ١٩٧١، ١٩٧٢.
دمية القصر وعصرة أهل العصر، لأبى الحسن الباخرزى، تحقيق سامى مكى العانى. جزآن، بغداد ١٩٧٠ - ١٩٧١.
كتاب الديباج المذهب في معرفة أعيان علماء المذهب، تأليف ابراهيم بن على بن محمد بن فرحون اليعمرى. القاهرة ١٣٥١.
(٢) تحقيق محمد الأحمدى أبى النور. جزآن، القاهرة (١٩٧٢).

<span data-type="title" id=toc-699>(باب الذال)</span>
الذريعة الى تصانيف الشيعة، تأليف محمد محسن نزيل سامراء، الشهير بالشيخ أغا بزرك الطهرانى. ظهر منه ٢٣ مجلدا (إلى آخر حرف الميم)، طهران- النجف ١٩٣٦ - ١٩٧٥.

<span data-type="title" id=toc-700>(باب الراء)</span>
الرجال للنجاشى ٢ - كتاب الرجال الذى صنفه الشيخ الجليل أبو العباس أحمد ابن على بن أحمد بن العباس النجاشى. طهران، بدون تاريخ (١٣٣٧ ش).
رجال الصحيحين للقيسرانى- الجامع بين كتابى أبى نصر الكلاباذى وأبى بكر الأصبهاني في رجال البخارى ومسلم، للحافظ أبى الفضل محمد بن طاهر بن القيسرانى. جزآن حيدرآباد ١٣٢٣.
كتاب الرد على النحاة، لابن مضاء القرطبى، تحقيق شوقى ضيف. القاهرة ١٩٤٧.
رسالة الغفران، لأبى العلاء المعرى، تحقيق عائشة عبد الرحمن (بنت الشاطئ).
القاهرة ١٩٦٣ (ذخائر العرب ٤).
رسائل إخوان الصفاء. ٤ أجزاء، بيروت: صادر ١٣٧٦ - ٧٧/ ١٩٥٧.','2','513',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (493,'رسائل البلغاء، اختيار وتصنيف محمد كرد على. القاهرة ١٩٥٤.
روضات الجنات في أحوال العلماء والسادات، لمحمد باقر بن أمير الخوانسارى.
٤ أجزاء، طبع حجر، طهران ١٣٠٤ - ١٣٠٦.

<span data-type="title" id=toc-701>(باب الزاى)</span>
زهر الآداب وثمر الألباب، لأبى إسحاق إبراهيم بن على الحصرى القيروانى، تحقيق على محمد البجاوى، جزآن، القاهرة ١٩٥٣.
كتاب الزينة في الكلمات الإسلامية العربية، لأبى حاتم أحمد بن حمدان الرازى، تحقيق حسين بن فيض الله الهمدانى. جزآن، القاهرة ١٩٥٦ - ١٩٥٧.

<span data-type="title" id=toc-702>(باب السين)</span>
سر الفصاحة، للأمير أبى محمد عبد الله بن محمد بن سعيد بن سنان الخفاجى الحلبى، تحقيق على فوده. القاهرة ١٩٣٢.
سركيس- معجم المطبوعات العربية والمعربة، ليوسف أليان سركيس. القاهرة ١٩٢٨.
سمط اللآلى، للوزير أبى عبيد البكرى، تحقيق عبد العزيز الميمنى. ٣ أجزاء، القاهرة ١٩٣٦.

<span data-type="title" id=toc-703>(باب الشين)</span>
شذرات الذهب في أخبار من ذهب، لعبد الحى بن العماد. ٨ أجزاء، القاهره ١٣٥٠ - ١٣٥١.
شرح أبيات مغنى اللبيب، صنعة عبد القادر بن عمر البغدادى، تحقيق عبد العزيز رباح وأحمد يوسف الدقاق. ٧ أجزاء (لم يتم)، دمشق ١٩٧٣ - ١٩٨٠.
شرح ما يقع فيه التصحيف والتحريف، لأبى أحمد الحسن بن عبد الله بن سعيد العسكرى، تحقيق عبد العزيز أحمد. القاهرة ١٩٦٣.','2','514',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (494,'شرح نهج البلاغة، لابن أبى الحديد، بتحقيق محمد أبى الفضل إبراهيم. ٢٠ جزءا، القاهرة ١٩٥٩ - ١٩٦٤.
