INSERT INTO [b143165] VALUES (694,'Bouyahia, Ch. ٢٥٠.A Adda- Paris, M. (٥٤٨) . Ahmedali, A. ١٨٣. Akhidime, J. A. F. ٦. Ali, Kh. J. ٢١١. Altikulac, T. ٢٨٢. Arberry, A. J. ١٤٢, ١٥٨. ٢١٢. Arendok, C. Van. ١٧٩. Asin Palacios, M. ٥٣. B Bahura, G, N. (٥٥٣) . Barth, J. ١٤٢. Bausani, A. ٦٦. Beck, E. ١٢٥. Bedwell, W. ١. Bell, R. ١١١. ١٤٢. Berenbach, J. ٢٢٣. Bergstrasser, G. ١٤٦, ١٥٣, ١٩٥. Berlinghof, R. (٥٣٣) . Berque, J. ٦٦. Bertheau, E. ٨٤. Bevan, A. A. ١٢٦, (٥٢٥) . Bilge, K. R. (٥٢٦) . Bjorkmann, W. ٨٤. Blachere, R. ٤٤, ٥١, ١٩٤, ٢٠٢, (٥٢٧) . Blanc, H. ٦. Blau, J. ٢٦, ٣٦. Bonebakker, S. A. ٩٥, ١٦٩, ٢٥٣, ٢٥٥.','2','715',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (695,'<span data-type="title" id=toc-992>٤ - أرقام المخطوطات المجهولة المؤلف</span>
<span data-type="title" id=toc-993>استنبول</span>
<span data-type="title" id=toc-994>عاطف:</span>
٢٨١٥ (٤١ ب- ٤٥ ب) شرح على كتاب المثلث لقطرب ٦٦.

<span data-type="title" id=toc-995>بغدادلى وهبى:</span>
١٩١٦/ ٣ (٧ أ- ١٤ ب) نبذة على مثلثات قطرب ٢٦٧.
١٩١٦/ ٤ (١٥ أ- ب) نظم مثلث قطرب، وتعيّن أنه للبهنسى ٦٢.

<span data-type="title" id=toc-996>بايزيد:</span>
٧٩٠٩/ ٣ و ٤ و ١٠ (٣١ ب- ٣٤ أ)، ٤٠ أ- ٤٢ ب، ٥٢ أ- ٥٧ ب) شروح كتاب المثلث لقطرب ٦٦.

<span data-type="title" id=toc-997>أسعد:</span>
٢٧٦٠ (١٨٢ أ- ١٨٩ أ) شرح على كتاب المثلث لقطرب ٦٦.
٣٢٤٣/ ٢ (٢٢ أ- ٨٣ أ) كتاب ضروب المنطق ٢٥٧.

<span data-type="title" id=toc-998>حسن حسنى:</span>
٦١٨/ ٢ (١٠١ ب- ١٢٠ ب) ترجمة فارسية لكتاب المثلث لقطرب أو شرح عليه ٦٦.

<span data-type="title" id=toc-999>كوبريلى:</span>
١٣٩٣ (٩٠ ب- ٩١ أ) مسألة: كل ما جاء من الأسماء على تفعال وهو مكسور ... ٢٦١.
١٣٩٣ (٩١ أ- ٩٣ ب) مسألة المهراس، ولغات متفرقة ٢٦١، ١٢٢.
١٣٩٣ (٩٤ ب- ٩٦ ب) تفسير ألفاظ، لا سيما من الحديث والقرآن ٢٦١.','2','716',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (696,'١٥٢١/ ١ (الأوراق ١ - ٢٣ ب) تعليق لغة من حواشى الصحاح للجوهرى من كلام ابن برى، مختصرات لمجهول.
١٥٧٦ (١ أ- ٣٩١ ب) كتاب مفاخر المقال فى المصادر والأفعال ١٩٥.

<span data-type="title" id=toc-1000>الحاشية: لاله إسماعيل:</span>
٥٩٧ (الأوراق ١ - ٤١) جمع المنادرة في التمثيل والمحاضرة، مختصر من كتاب الثعالبى ٢٣٦.

<span data-type="title" id=toc-1001>شهيد على:</span>
٢٥١٥/ ٢ (٢٢٠ أ- ٢٦٨ ب) كتاب فى طبقات اللغويين والنحويين لمجهول، انظر ثبت المراجع.

<span data-type="title" id=toc-1002>الاسكوريال</span>
مجموع عربى ١٨٩٨ (٢٨ ورقة) كتاب السلاح ٢٥٧.

<span data-type="title" id=toc-1003>أكسفورد</span>
<span data-type="title" id=toc-1004>بودليانا:</span>
مارش ٣٨٨ (١٥٨ ورقة) مختار الصحاح (وهو غير كتاب الرازى) ٢٢٤.

<span data-type="title" id=toc-1005>باريس</span>
<span data-type="title" id=toc-1006>المكتبة الوطنية:</span>
٥٧٠٢ (الأوراق ٢٩٩ - ٣٠٠) شرح غفل من العنوان على كتاب المثلث لقطرب ٦٦.','2','717',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (697,'<span data-type="title" id=toc-1007>برلين</span>
<span data-type="title" id=toc-1008>مكتبة الدولة:</span>
٢٧٣، ١٠٣ We.-) ٩١٠ ورقة) كتاب المبانى لنظم المعانى ٢٦٠.
٢٣٩، ١٦٥ Pet.-) ٦٩٤٦ ورقة) مختصر غفل من العنوان لصحاح الجوهرى ٢٢٤.
٣، ٢/ ١٨٣٥ We.-) ٦٩٤٨ ب، ١٠ أ) مجموع شواهد الصحاح ٢٢٢.
، ١/ ٧٤ Pmm.-) ٧٠٣٧ الأوراق ١ - ٣٤ أ) كتاب البيان فيما اشتمل عليه خلق الإنسان ٢٢٨ الحشية.
٦، ٤/ ١٩٦٥ Spr.-) ٧٠٨٤ ب- ٧ أ) شرح غفل من العنوان على كتاب المثلث لقطرب ٦٦.

<span data-type="title" id=toc-1009>بغداد</span>
<span data-type="title" id=toc-1010>جامعة الحكمة:</span>
مجموعة سركيس (بدون رقم) منتخب الصحاح، مختصر من كتاب الجوهرى ٢٢٤.

<span data-type="title" id=toc-1011>المتحف العراقى:</span>
١٤٨١ (١٤ ورقة) مختصر الفروق اللغوية، مختصر من كتاب أبى هلال العسكرى ١٨٤ - ١٨٥.
١٨٨٣/ ٦ (٣ ورقات) الجامع المهذب فى شرح مشكل فصيح ثعلب، وهو منظوم ١٤٥.

<span data-type="title" id=toc-1012>تونس</span>
<span data-type="title" id=toc-1013>دار الكتب الوطنية:</span>
٤١٦٦/ ١٢ (ما قبل الورقة ١٦٥) شرح مثلث قطرب ٦٦.','2','718',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (698,'<span data-type="title" id=toc-1014>جاكارتا</span>
<span data-type="title" id=toc-1015>المتحف:</span>
٦٤٤/ ٢ (١٥ أ- ٢٤ ب) نبذة من كتاب الفروق (لأبى هلال العسكرى).

<span data-type="title" id=toc-1016>دبلن</span>
<span data-type="title" id=toc-1017>تشستربيتى:</span>
٣٤٨٦/ ١٣ (١٦١ أ- ١٦٤) شرح مثلث قطرب ٦٦.
٣٩٩٩/ ٣ (٤٠ أ- ٤٨ أ) مختصر من كتاب الفروق فى اللغة لأبى هلال العسكرى ١٨٥.
٤١٠٦ (٩٤ ب- ٩٦) شرح بالفارسية على كتاب المثلث لقطرب ٦٦.

<span data-type="title" id=toc-1018>دمشق</span>
<span data-type="title" id=toc-1019>الظاهرية:</span>
عام ٣٨٨٥ (١٤ ب- ٢٠ أ) موجز المثلثات، شرح على كتاب قطرب ٦٤.
عام ٤٣٦٧ (٤ أوراق) شرح مثلث قطرب ٦٦.
عام ٦٢١٧ (١٠ ورقات) شرح مثلث قطرب ٦٦.

<span data-type="title" id=toc-1020>رامبور</span>
<span data-type="title" id=toc-1021>مكتبة الدولة:</span>
لغة ٣١/ ٢ كتاب الأجناس من كلام العرب (نسب خطأ إلى أبى عبيد) ٨٦.
بدون رقم: كتاب الأمثال ٢٦١.','2','719',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (699,'<span data-type="title" id=toc-1022>الرباط</span>
<span data-type="title" id=toc-1023>المكتبة العامة:</span>
الحمزاوية ١٨٩/ ٢ التنبيهات على كامل المبرد، يحتمل أنه لعلى بن حمزة البصرى ١١٠ - ١١١.

<span data-type="title" id=toc-1024>طنطا</span>
<span data-type="title" id=toc-1025><span data-type="title" id=toc-1031>دار الكتب:</span></span>
بدون رقم، مختار الصحاح، لعله لمحمد بن أبى بكر الرازى ٢٢٤.

<span data-type="title" id=toc-1026>طهران</span>
<span data-type="title" id=toc-1027>مكتبة الخنجى الخاصة:</span>
مختار الصحاح، لعله لمحمد بن أبى بكر الرازى ٢٢٤.

<span data-type="title" id=toc-1028>فينا</span>
<span data-type="title" id=toc-1029>المكتبة الوطنية:</span>
٧٦/ ٣ (الأوراق ٩ - ١٠) شرح غفل من العنوان على كتاب المثلث لقطرب، قطعة ٦٦.

<span data-type="title" id=toc-1030>القاهرة</span>
دار الكتب:
مجموع ٢٣٤ (١٣٧ أ- ١٤٣ أ) كتاب الأجناس من كلام العرب (منسوب خطأ إلى أبي عبيد) ٨٦.
طلعت، مجموع ٧٨٩ (١١٠ أ- ١٢٥ أ) شرح لمجهول على كتاب المثلث لقطرب، وتعيّن انه نظم مثلثات قطرب للبهنسى ٦٢.','2','720',NULL,NULL,'0');
INSERT INTO [b143165] VALUES (700,'<span data-type="title" id=toc-1032>كابول</span>
<span data-type="title" id=toc-1033>المتحف:</span>
٢٣٣ (٢٣٣ ورقة) شرح شواهد غريب الحديث (لأبى عبيد) ٨٧.

<span data-type="title" id=toc-1034>لندن</span>
<span data-type="title" id=toc-1035>المتحف البريطانى:</span>
Or.
٤٥٨٩ (٢٠٥ ورقة) مجموع غرائب أحاديث النبى ٢٦١ - ٢٦٢ مجموعة بلشاه (بدون رقم) مختصر الجمهرة (لابن دريد) ١٠٣.

<span data-type="title" id=toc-1036>ليدن</span>
<span data-type="title" id=toc-1037>مكتبة الجامعة:</span>
Or.
١٤٩ (٢٢ ورقة) نظم فقه اللغة العربية (للثعالبى) ٢٣٤.

<span data-type="title" id=toc-1038>المدينة</span>
<span data-type="title" id=toc-1039>عارف حكمت:</span>
بدون رقم (١٤ ورقة) كتاب الأجناس من كلام العرب (منسوب خطأ إلى أبى عبيد) ٨٦.

<span data-type="title" id=toc-1040>ميلانو</span>
