INSERT INTO [b144114] VALUES (503,'<span data-type="title" id=toc-342>الباب الخامس والثمانون والسادس والثمانون فضل سورة البروج والطارق</span>
لم يصح شيء فيهما سوى كونهما من المفصل المذكر فضله في بداية فضل سورة ق','2','241',NULL,NULL,'0');
INSERT INTO [b144114] VALUES (504,'<span data-type="title" id=toc-343>الباب السابع والثمانون فضل سورة سبح اسم ربك الأعلى</span>
