INSERT INTO [b1458] VALUES (460,'<span data-type="title" id=toc-386>جُدَامَةُ بِنْتُ وَهْبٍ الْأَسَدِيَّةُ انْفَرَدَتْ عَنْهَا بِالرِّوَايَةِ عَائِشَةُ أُمُّ الْمُؤْمِنِينَ رَضِيَ اللَّهُ عَنْهَا</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (461,'<span data-type="title" id=toc-387>خَوْلَةُ بِنْتُ ثَامِرٍ الْأَنْصَارِيَّةُ تَفَرَّدَ عَنْهَا بِالرِّوَايَةِ النُّعْمَانُ بْنُ أَبِي عَيَّاشٍ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (462,'<span data-type="title" id=toc-388>خَوْلَةُ بِنْتُ ثَعْلَبَةَ انْفَرَدَ عَنْهَا بِالرِّوَايَةِ يُوسُفُ بْنُ عَبْدِ اللَّهِ بْنِ سَلَّامٍ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (463,'<span data-type="title" id=toc-389>صُمَيَّةُ تَفَرَّدَ عَنْهَا بِالرِّوَايَةِ عُبَيْدُ اللَّهِ بْنُ عَبْدِ اللَّهِ بْنِ عُمَرَ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (464,'<span data-type="title" id=toc-390>عَمْرَةُ الْأَنْصَارِيَّةُ تَفَرَّدَ عَنْهَا بِالرِّوَايَةِ عَبْدُ الرَّحْمَنِ الْأَعْرَجُ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (465,'<span data-type="title" id=toc-391>فُقَيْرَةُ، وَيُقَالُ مُلَيْلَةُ تَفَرَّدَ عَنْهَا بِالرِّوَايَةِ عَبْدُ الرَّحْمَنِ بْنُ هُرْمُزَ الْأَعْرَجُ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (466,'<span data-type="title" id=toc-392>الْكُنَى مِنَ النِّسْوَةِ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (467,'<span data-type="title" id=toc-393>أُمُّ جَمِيلٍ بِنْتُ الْمُجَلِّلِ انْفَرَدَ عَنْهَا بِالرِّوَايَةِ ابْنُهَا مُحَمَّدُ بْنُ حَاطِبٍ الْجُمَحِيُّ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (468,'<span data-type="title" id=toc-394>أُمُّ الْعَلَاءِ الْأَنْصَارِيَّةُ تَفَرَّدَ عَنْهَا بِالرِّوَايَةِ خَارِجَةُ بْنُ زَيْدِ بْنِ ثَابِتٍ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (469,'<span data-type="title" id=toc-395>أُمُّ مُبَشِّرٍ الْأَنْصَارِيَّةُ تَفَرَّدَ عَنْهَا بِالرِّوَايَةِ جَابِرُ بْنُ عَبْدِ اللَّهِ الْأَنْصَارِيُّ</span>',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b1458] VALUES (470,'<span data-type="title" id=toc-396>أُمُّ مَعْبَدٍ انْفَرَدَ عَنْهَا ابْنُهَا بِالرِّوَايَةِ مَعْبَدُ بْنُ كَعْبِ بْنِ مَالِكٍ</span>',NULL,NULL,NULL,NULL,'0');
