أمثلة على مجاوزة الدم الأربعين
مثال ١: ١٠ دم - ٢٠ طهر - ١١ دم
مثال ٢: ١ دم - ٣٠ طهر - ١ دم - ١٤ طهر - ١ دم
أمثلة على عدم مجاوزة الدم الأربعين
مثال ٣: ٥ دم - ٣٤ طهر - ١ دم
مثال ٤: ١٨ دم - ٢٢ طهر - ١ دم
مثال ٥: ١ دم - ٣٤ طهر - ١ دم - ١٥ طهر - ١ دم

أمثلة الحيض
أمثلة على عدم وقوع نصاب في زمان العادة
مثال ١: ٥ دم - ١٥ طهر - ١١ دم
مثال ٢: ٥ دم - ٤٦ طهر - ١١ دم
أمثلة على وقوع نصاب مساوٍ للعادة في زمانها
مثال ٣: ٥ دم - ٤٨ طهر - ١٢ دم
مثال ٤: ٥ دم - ٥٤ طهر - ١ دم - ١٤ طهر - ١ دم',NULL,'185',NULL,NULL,'0');
INSERT INTO [b151169] VALUES (172,'مثال على وقوع نصاب غير مساوٍ للعادة في زمانها
