INSERT INTO [b16636] VALUES (62,'٢٥ - أبو محمد بن القاسم الحرار. (١)
٢٦ - أبو موسى عيسى بن يعقوب الهسكوري. (٢)
٢٧ - أبو يعقوب بن يحيى بن الزيات. (٣)
