INSERT INTO [b1663] VALUES (39,'<span data-type="title" id=toc-39>٣٨ </span>- أبشروا بالمهدي رجل من قريش من عترتي يخرج في اختلاف من الناس وزلزال فيملأ الأرض قسطا وعدلا كما ملئت ظلما وجورا ويرضى عنه ساكن السماء وساكن الأرض ويقسم المال صحاحا بالسوية ويملأ قلوب أمة محمد غنى ويسعهم عدله حتى إنه يأمر مناديا فينادي: من له حاجة إلي فما يأتيه إلا رجل واحد يأتيه فيسأله فيقول: ائت السادن حتى يعطيك فيأتيه فيقول: أنا رسول المهدي إليك لتعطيني مالا فيقول: احث فيحثي ولا يستطيع أن يحمله فيلقي حتى يكون قدر ما يستطيع أن يحمله فيخرج به فيندم فيقول: أنا كنت أجشع أمة محمد ⦗٨⦘ صلى الله عليه وسلم نفسا كلهم دعي إلى هذا المال فتركه غيري فيرد عليه فيقول: إنا لا نقبل شيئا أعطيناه فيلبث في ذلك ستا أو سبعا أو ثمانيا أو تسع سنين ولا خير في الحياة بعده
(حم الباوردي) عن أبي سعيد.
_________
<span data-type="title">[حكم الألباني]</span> (ضعيف)',NULL,'7',NULL,NULL,'0');
