INSERT INTO [b1663] VALUES (1061,'<span data-type="title" id=toc-1061>١٠٦٠ </span>- اقتدوا باللذين من بعدي أبي بكر وعمر فإنهما حبل الله الممدود ومن تمسك بهما فقد تمسك بالعروة الوثقى التي لا انفصام لها
