INSERT INTO [b1663] VALUES (1145,'<span data-type="title" id=toc-1145>١١٤٤ </span>- أكمل المؤمنين إيمانا رجل يجاهد في سبيل الله بنفسه وماله ورجل يعبد الله في شعب من الشعاب قد كفى الناس شره
