INSERT INTO [b1663] VALUES (2264,'<span data-type="title" id=toc-2264>٢٢٦٦ </span>- الأبدال بالشام وهم أربعون رجلا كلما مات رجل أبدل الله مكانه رجلا يسقى بهم الغيث وينتصر بهم على الأعداء ويصرف عن أهل الشام بهم العذاب
