INSERT INTO [b1663] VALUES (2318,'<span data-type="title" id=toc-2319>٢٣٢٠ </span>- بحسب امرئ من الإيمان أن يقول: رضيت بالله ربا وبحمد رسولا وبالإسلام دينا
