INSERT INTO [b1663] VALUES (2348,'<span data-type="title" id=toc-2349>٢٣٥٠ </span>- بئس العبد عبد تخيل واختال ونسي الكبير المتعال بئس العبد عبد تجبر واعتدى ونسي الجبار الأعلى بئس العبد عبد سها ولها ونسي المقابر والبلا بئس العبد عبد عتا وطغى ونسي المبتدا والمنتهى بئس العبد عبد يختل الدنيا بالدين بئس العبد عبد يختل الدين بالشبهات بئس العبد عبد طمع يقوده بئس العبد عبد هوى يضله بئس العبد عبد رغب يذله
