INSERT INTO [b1663] VALUES (2461,'<span data-type="title" id=toc-2463>٢٤٦٤ </span>- تفتح أبواب السماء لخمس لقراءة القرآن وللقاء الزحفين ولنزول القطر ولدعوة المظلوم وللأذان
(طس) عن ابن عمر.
_________
<span data-type="title">[حكم الألباني]</span> (ضعيف)',NULL,'362',NULL,NULL,'0');
INSERT INTO [b1663] VALUES (2462,'<span data-type="title" id=toc-2464>٢٤٦٥ </span>- تفتح أبواب السماء ويستجاب الدعاء في أربعة مواطن ⦗٣٦٣⦘ عند التقاء الصفوف في سبيل الله وعند نزول الغيث وعند إقامة الصلاة وعند رؤية الكعبة
(طب) عن أبي أمامة.
_________
<span data-type="title">[حكم الألباني]</span> (ضعيف جدا)',NULL,'362',NULL,NULL,'0');
INSERT INTO [b1663] VALUES (2463,'<span data-type="title" id=toc-2465>٢٤٦٦ </span>- تفتح لكم أرض الأعاجم وستجدون فيها بيوتا يقال لها الحمامات فلا يدخلها الرجال إلا بإزار وامنعوا النساء أن يدخلنها إلا مريضة أو نفساء
(هـ) عن ابن عمر.
_________
<span data-type="title">[حكم الألباني]</span> (ضعيف)',NULL,'363',NULL,NULL,'0');
INSERT INTO [b1663] VALUES (2464,'<span data-type="title" id=toc-2466>٢٤٦٧ </span>- تفرغوا من هموم الدنيا ما استطعتم فإنه من كانت الدنيا أكبر همه أفشى الله ضيعته وجعل فقره بين عينيه ومن كانت الآخرة أكبر همه جمع الله تعالى له أمره وجعل غناه في قلبه وما أقبل عبد بقلبه إلى الله تعالى إلا جعل الله قلوب المؤمنين تفد إليه بالود والرحمة وكان الله تعالى بكل خير إليه أسرع
(طب) عن أبي الدرداء.
_________
<span data-type="title">[حكم الألباني]</span> (ضعيف)',NULL,'363',NULL,NULL,'0');
INSERT INTO [b1663] VALUES (2465,'<span data-type="title" id=toc-2467>٢٤٦٨ </span>- تفقدوا نعالكم عند أبواب المساجد
(حل) عن ابن عمر.
_________
<span data-type="title">[حكم الألباني]</span> (موضوع)',NULL,'363',NULL,NULL,'0');
INSERT INTO [b1663] VALUES (2466,'<span data-type="title" id=toc-2468>٢٤٦٩ </span>- تنقه وتوقه
(حب حل) عن ابن عمر.
_________
<span data-type="title">[حكم الألباني]</span> (ضعيف)',NULL,'363',NULL,NULL,'0');
INSERT INTO [b1663] VALUES (2467,'<span data-type="title" id=toc-2469>٢٤٧٠ </span>- تفكروا في الخلق ولا تفكروا في الخالق فإنكم لا تقدرون قدره
(أبو الشيخ) عن ابن عباس.
