INSERT INTO [b1663] VALUES (3015,'<span data-type="title" id=toc-3022>٣٠٢٢ </span>- الدواوين ثلاثة: فديوان لا يغفر الله منه شيئا وديوان لا يعبأ الله به شيئا وديوان لا يترك الله منه شيئا فأما الديوان الذي لا يغفر الله منه شيئا فالإشراك بالله وأما الديوان الذي لا يعبأ الله به شيئا فظلم العبد نفسه فيما بينه وبين ربه من صوم يوم تركه أو صلاة تركها فإن الله يغفر ذلك إن شاء ويتجاوز وأما الديوان الذي لا يترك الله منه شيئا فمظالم العباد بينهم القصاص لا محالة
