INSERT INTO [b1663] VALUES (3065,'<span data-type="title" id=toc-3074>٣٠٧٢ </span>- رأس العقل بعد الإيمان بالله التودد إلى الناس وأهل التودد في الدنيا لهم درجة في الجنة ومن كانت له في الجنة درجة فهو في الجنة ونصف العلم حسن المسألة والاقتصاد في المعشية نصف العيش تبقى نصف النفقة وركعتان من رجل ورع أفضل من ألف ركعة من مخلط وما تم دين إنسان قط حتى يتم عقله والدعاء يرد الأمر وصدقة السر تطفئ غضب الرب وصدقة العلانية تقي ميتة السوء وصنائع المعروف إلى الناس تقي صاحبها مصارع السوء الآفات والهلكات وأهل المعروف في الدنيا هم أهل المعروف في الآخرة والعرف ينقطع فيما بين الناس ولا ينقطع فيما بين الله وبين من افتعله
(الشيرازي في الألقاب هب) عن أنس.
_________
<span data-type="title">[حكم الألباني]</span> (ضعيف)',NULL,'452',NULL,NULL,'0');
