INSERT INTO [b1663] VALUES (3088,'<span data-type="title" id=toc-3097>٣٠٩٥ </span>- رحم الله أبا بكر زوجني ابنته وحملني إلى دار الهجرة وأعتق بلالا من ماله وما نفعني مال في الإسلام ما نفعني مال أبي بكر رحم الله عمر يقول الحق وإن كان مرا لقد تركه الحق وما له من صديق رحم الله عثمان تستحييه الملائكة وجهز جيش العسرة وزاد في مسجدنا حتى وسعنا رحم الله عليا اللهم أدر الحق معه حيث دار
