INSERT INTO [b1663] VALUES (3399,'<span data-type="title" id=toc-3411>٣٤٠٨ </span>- شوبوا شيبكم بالحناء فإنه أسرى لوجوهكم وأطيب لأفواهكم وأكثر لجماعكم الحناء سيد ريحان أهل الجنة الحناء يفصل ما بين الكفر والإيمان
