INSERT INTO [b1663] VALUES (3401,'<span data-type="title" id=toc-3413>٣٤١٠ </span>- شهادة المسلمين بعضهم على بعض جائزة ولا تجوز شهادة العلماء بعضهم على بعض لأنهم حسد
