INSERT INTO [b1663] VALUES (3403,'<span data-type="title" id=toc-3415>٣٤١٢ </span>- شهر رمضان شهر كتب الله عليكم صيامه وسننت لكم قيامه فمن صامه وقامه إيمانا واحتسابا خرج من ذنوبه كيوم ولدته أمه
