INSERT INTO [b1663] VALUES (3409,'<span data-type="title" id=toc-3421>٣٤١٨ </span>- شيبتني سورة هود وأخواتها الواقعة و {القارعة} و {الحاقة} و {إذا الشمس كورت} و {سأل سائل}
