INSERT INTO [b1663] VALUES (3432,'<span data-type="title" id=toc-3444>٣٤٤٢ </span>- الشمس تطلع ومعها قرن الشيطان فإذا ارتفعت فارقها فإذا استوت قاربها فإذا زالت فارقها فإذا دنت للغروب قارنها فإذا غربت فارقها
