INSERT INTO [b1663] VALUES (3433,'<span data-type="title" id=toc-3445>٣٤٤٣ </span>- الشمس والقمر ثوران عقيران في النار إن شاء أخرجهما وإن شاء تركهما
(ابن مردويه) عن أنس.
