INSERT INTO [b1663] VALUES (3774,'<span data-type="title" id=toc-3791>٣٧٨٨ </span>- عليكم بقلة الكلام ولا يستهوينكم الشيطان فإن تشقيق الكلام من شقائق الشيطان
